INSERT INTO [b1663] VALUES (4715,'<span data-type="title" id=toc-4737>٤٧٥١ </span>- للمسلم على المسلم ست بالمعروف: يسلم عليه إذا لقيه ويجيبه إذا دعاه ويشمته إذا عطس ويعوده إذا مرض ويتبع جنازته إذا مات ويحب له ما يحب لنفسه
