INSERT INTO [b16884] VALUES (2,'بِسْمِ اللهِ الرَّحْمَنِ الرَّحِيمِ
هذا الكتاب
يعد علم المقاصد الشرعية من أهم العلوم التي يتطرق اليها الباحث، وهذا الكتاب جاء لبيان أهمية البُعْد المقاصدي في الإجتهاد، وضرورة إنضاج المعالم الأساسية لنظرية المقاصد.
أثمرت جهود الباحث في بيان الكشف عن مقاصد الشارع، فكان هذا الكتاب ثمرة جهد كبير
وفي الأصل هذ الكتاب أطروحة، قدمت استكمالاً للحصول على درجة الدكتوراه، وقد أجازت اللجنة المشرفة هذه الرسالة.',NULL,'2',NULL,NULL,'0');
INSERT INTO [b16884] VALUES (3,'طُرقُ الكَشفِ عن مقاصد الشارع',NULL,'3',NULL,NULL,'0');
