INSERT INTO [b16965] VALUES (169,'<span data-type="title" id=toc-80>بيان إحصائي </span>يوضح عدد العاملات المنزليات والمربيات المسلمات بمدينة الرياض حتى ١٥/ ٥ / ١٤٢٩ هـ
المهنة ... العدد
