INSERT INTO [b17064] VALUES (891,'٣٤٢ - تفسير ابن عرفة - أبو عبدالله محمد بن محمد بن عرفة الورغمي - ت حسن المناعي - مركز البحوث بالكلية
٣٤٣ - تفسير ابن كثير = تفسير القرآن العظيم.
٣٤٤ - تفسير الشعراوي - محمد متولي الشعراوي - مطابع دار أخبار اليوم.
٣٤٥ - تفسير الطبري = جامع البيان
٣٤٦ - تفسير القرآن العزيز - عبد الرزاق بن همام الصنعاني - ت عبد المعطي قلعجي - دار المعرفة - بيروت - ط ١ - ١٤١١ هـ
٣٤٧ - تفسير القرآن العظيم - أبي الفداء الحافظ ابن كثير - ت عدة أساتذة - دار الشعب.
٣٤٨ - تفسير القرآن الكريم - محيي الدين ابن عربي - منشورات دار اليقظة العربية
٣٤٩ - تفسير المشكل من غريب القرآن - مكي بن أبي طالب - ت هدى الطويل المرعشلي - دار النور الإسلامي ط ١ - ١٤٠٨ هـ
٣٥٠ - تفسير النسائي - أبي عبد الرحمن أحمد بن شعيب بن علي النسائي - ت سيد الجليمي، صبري الشافعي - مكتبة السنة - الطبعة الأولى ١٩٩٠ م.
٣٥١ - تفسير سور المفصل - من القرآن الكريم - السيد عبد الله كنون - دار الثقافة - المغرب ١٤٠١ هـ
٣٥٢ - تفسير كتاب الله العزيز - هود بن محكم الهواري - ت بالحاج ابن سعيد شريفي - دار الغرب الإسلامية بيروت، لبنان.
٣٥٣ - تفسير مجاهد - مجاهد بن جبر - ت عبد الرحمن السورتي - المنشورات العلمية بيروت.
٣٥٤ - تفسير هود بن محكم = تفسير كتاب الله العزيز.
٣٥٥ - تقريب التهذيب - ابن حجر العسقلاني - ت محمد عوامة - دار الرشيد سوريا - ط ١ - ١٤٠٦ هـ
٣٥٦ - تكميل النفع بما لم يثبت به وقف ولا رفع - محمد عمرو عبد اللطيف - مكتبة التوعية الإسلامية - مصر ط ١ - ١٤١٠ هـ
٣٥٧ - تلخيص الحبير في تخريج أحاديث الرافعي الكبير الإمام أبي الفضل شهاب الدين أحمد العسقلاني - دار المعرفة بيروت لبنان.
٣٥٨ - تنظيم الحماية الفرنسية في المغرب - محمد خير فارس
٣٥٩ - تنوير الحوالك - عبد الرحمن السيوطي = انظر موطأ مالك
٣٦٠ - تنوير المقباس في تفسير ابن عباس - منسوب إلى الفيروز آبادي - بهامش الدر المنثور.','2','994',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (892,'٣٦١ - تهذيب الأسماء واللغات - النووي
٣٦٢ - تهذيب التهذيب - أحمد بن علي بن حجر العسقلاني - دائرة المعارف النظامية - الهند ١٣٢٥ هـ.
٣٦٣ - تهذيب تاريخ دمشق لابن عساكر (ت ٦٧٦ هـ)، عبد القادر بدران (ت ١٣٤٦ هـ)، دار المسيرة، بيروت ط ٢، ١٣٩٩ هـ.
٣٦٤ - توشيح الديباج وحلية الابتهاج لبدر الدين القرافي دار الغرب الإسلامي الطبعة الأولى ١٤٠٣ هـ
٣٦٥ - تونس في عهد الحماية - نقولا زيادة
٣٦٦ - تونس وجامع الزيتونة - محمد الخضر حسين - الدار التونسية.
٣٦٧ - تيسير التفسير - محمد المكي الناصري - دار الغرب الإسلامي - بيروت ١٤٠٥ هـ.
٣٦٨ - ثبت أبي جعفر أحمد بن علي البلوي الوادي آشي دار الغرب الإسلامي الطبعة الأولى ١٤٠٣ هـ
٣٦٩ - ثبت ابن عابدين - محمد بن عابدين - دمشق ١٣٠٢.
٣٧٠ - ثورة ابن غذاهم - وثائق تونسية الجزء الأول الدار التونسية للنشر ١٩٦٧ م ب سلامة
٣٧١ - ثورة علي بن غذاهم - جان غانياج - ترجمة لجنة من كتابة الدولة - الدار التونسية - المطبعة الرسمية للجمهورية التونسية ١٩٦٥ م.
٣٧٢ - جامع البيان في تفسير القرآن - أبي جعفر محمد بن جرير الطبري - الطبعة الثالثة ١٣٨٨ هـ - ١٩٦٨ م مطبعة مصطفى البابي الحلبي.
٣٧٣ - جامع التحصيل - الحافظ صلاح الدين ابن سعيد خليل بن كيكلدي العلائي - الطبعة الأولى ١٣٩٨ هـ - ١٧٩٨ م الدار العربية للطباعة.
٣٧٤ - جامع الزيتونة ومدارس العلم في العهد الحفصي والتركي للطاهر المعموري الدار العربية للكتاب ١٩٨٠
٣٧٥ - جامع بيان العلم وفضله وما ينبغي في روايته وحمله - ابن عبد البر - دار الكتب العلمية بيروت ١٣٩٨ هـ
٣٧٦ - جامع كرامات الأولياء - يوسف النبهاني - مصر ١٣٢٩ هـ.
٣٧٧ - جذوة الاقتباس في ذكر من حل من الأعلام مدينة فاس، أحمد بن القاضي المكناسي (ت ١٢٠٥ هـ)، دار المنصور، الرباط، ١٩٧٤ م.','2','995',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (893,'٣٧٨ - جذوة المقتبس في ذكر ولاة الأندلس، وأسماء رواة الحديث، والأدب، وذوي النباهة والشعر، لأبي عبد الله محمد بن فتوح الحميدي (ت ٤٨٨ هـ)، مكتب نشر الثقافة الإسلامية القاهرة (د. ت)، مطبعة السعادة، مصر، ١٩٥٣ م.
٣٧٩ - جواهر البداري الجواهر المنتقاق في إتمام ما أحل به كتاب الطبقات - أبو القاسم بن إبراهيم البرادي - القاهرة ١٣٠٢ هـ.
٣٨٠ - حاشية الأدنوي = انظر طبقات الأدنوي.
٣٨١ - حاضر العالم الإسلامي - لو ثروب ستودارد - نقله إلى العربية عجاج نويهض - مصر - ١٣٥٢ هـ.
٣٨٢ - حرز الأماني ووجه التهاني (الشاطبية) - القاسم بن فيره الشاطبي.
٣٨٣ - حسن البيان عما بلغته إفريقية في الإسلام من السطوة والعمران - محمد النيفر - تونس ١٣٥٣ هـ.
٣٨٤ - حسن المحاضرة في تاريخ مصر والقاهرة، للحافظ جلال الدين السيوطي (ت ٩١١ هـ)، تح محمد أبو الفضل إبراهيم، دار إحياء الكتب العربية، ط ١، ١٣٨٧ هـ.
٣٨٥ - حلية الأولياء وطبقات الأصفياء - للحافظ أبي نعيم أحمد بن عبد الله الأصبهاني - دار الكتاب العربي الطبعة الثالثة ١٤٠٠ هـ.
٣٨٦ - حوادث الأمير عبد الكريم - محمود كامل فريد - مطبعة التقدم
٣٨٧ - حوار مع الصوفية - أبو بكر العراقي - مكتبة دار العاصمة
٣٨٨ - خريدة القصر للعماد الأصفهاني - مصر - ١٩٥١ م.
٣٨٩ - خطط المقريزي = المواعظ والاعتبار
٣٩٠ - خلاصة الأثر في أعيان القرن الحادي عشر للمجي - مصر ١٢٨٤ هـ.
٣٩١ - خلاصة تاريخ تونس، ح. ح. عبد الوهاب، الشركة التونسية للتوزيع، ط ٥، ١٩٧٦ م.
٣٩٢ - خلاصة تهذيب الكمال في أسماء الرجال - أحمد بن عبد الله الخزرجي - مصر ١٣٢٢ هـ.
٣٩٣ - خلال جزولة - محمد المختار السوسي - تطوان
٣٩٤ - خير الدين التونسي - أبو القاسم محمد كرو - تونس
٣٩٥ - خير الدين باشا - المنجي الشملي - تونس ١٩٧٢ م
٣٩٦ - دائرة المعارف الإسلامية، جماعة من المستشرقين ١٣٥٢ هـ.
٣٩٧ - درء تعارض العقل والنقل - ابن تيمية','2','996',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (894,'٣٩٨ - درة الحجال في أسماء الرجال - أحمد بن محمد القاضي - للرباط.
٣٩٩ - دعائم الإسلام - النعمان بن محمد ابن حيون - ت آصف فيضي - طبع بالقاهرة.
٤٠٠ - دلائل النبوة - لأبي بكر أحمد بن الحسين البيهقي - الطبعة الأولى ١٤٠٥ هـ - دار الكتب العلمية.
٤٠١ - دليل مؤرخ المغرب - ابن سودة
٤٠٢ - دوحة الناشر لمحاسن من كان بالمغرب من مشاهير القرن العاشر - محمد بن علي الحسيني - فاس ١٣٠٩ هـ.
٤٠٣ - دور المرابطين في نشر الإسلام في غرب إفريقية - عصمت عبد اللطيف - دار الغرب الإسلامي بيروت ١٤٠٨ هـ
٤٠٤ - دور كتامة في تاريخ الخلافة الفاطمية - د موسى لقبال - الشركة الوطنية للنشر والتوزيع الجزائر ١٩٧٩
٤٠٥ - دولة الأدارسة ملوك تلمسان وفاس وقرطبة - إسماعيل العربي - دار الغرب الإسلامي ط ١٤٠٣ هـ
٤٠٦ - ذخيرة الحفاظ المخرج على الحروف والألفاظ - محمد بن طاهر بن علي بن القيسراني - ت د. عبد الرحمن بن عبد الجبار الفريوائي - دار السلف، دار الدعوة - الرياض، الهند - الطبعة الأولى ١٤١٦ هـ.
٤٠٧ - ذكرى الإمام المازري، عبد الله الزناد، دار بو سلامة، تونس، ١٣٨٧ هـ.
٤٠٨ - ذكريات مع الشعراوي = انظر جريدة المدينة
٤٠٩ - ذيل الأعلام - أحمد العلاونة - دار المنارة جدة ط ١، ١٤١٨ هـ
٤١٠ - ذيل البشائر - حسين خوجة - تونس ١٣٢٦ هـ.
٤١١ - ذيل العبر في خبر من غبر - محمد بن أحمد بن عثمان ابن قايماز الذهبي ت أبوا هاجر محمد السعيد ابن بسيومي زغلول - دار الكتب العلمية - بيروت، لبنان.
٤١٢ - ذيل تذكرة الحفاظ - أبي المحاسن الحسيني الدمشقي - دمشق ١٣٤٧ هـ.
٤١٣ - ذيل طبقات الحفاظ - أبو بكر عبد الرحمن السيوطي - دمشق ١٣٤٧ هـ.
٤١٤ - ذيل طبقات الحنابلة - ابن رجب - بيروت ١٣٧٠ هـ.
٤١٥ - ذيل مرآة الزمان - موسى بن محمد اليونيني - حيدر آباد ١٣٧٤ هـ.
٤١٦ - رحلة التجاني، أبو محمد عبد الله بن محمد التجاني، المطبعة الرسمية. تونس ١٣٧٧ هـ.','2','997',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (895,'٤١٧ - رحلة الحج إلى بيت الله الحرام - محمد الأمين الشنقيطي - دار الشروق ١٤٠٣ هـ الطبعة الأولى.
٤١٨ - رسالة افتتاح الدعوة، القاضي النعمان بن محمد الشيعي الإسماعيلي (ت ٣٦٣ هـ) تح وداد القاضي، دار الثقافة بيروت، ط ١، ١٩٧٠ م.
٤١٩ - رسالة لطيفة في أحاديث متفرقة ضعيفة - محمد بن قدامة المقدسي - ت محمد عيد العباسي - دار الثقافة للجميع - ط ١، ١٤٠٠ هـ
٤٢٠ - رواد النهضة الحديثة - مارون عبود - بيروت ١٩٥٢ م.
٤٢١ - روض القرطاس = انظر الأنيس المطرب.
٤٢٢ - روضات الجنات - الخوانساري
٤٢٣ - روضة التعريف بمفاخر مولاي إسماعيل بن الشريف لأبي عبد الله محمد الصغير اليفرني المطبعة الملكية بالرباط ١٣٨٢ هـ
٤٢٤ - رياض الجنة = معجم الشيوخ
٤٢٥ - رياض النفوس في طبقات علماء القيروان وإفريقية وزهادهم ونساكهم وسير من أخبارهم وفضائلهم وأوصافهم لأبي بكر عبد الله المالكي دار الغرب الإسلامي بيروت - لبنان ١٤٠٣ هـ
٤٢٦ - ريحانة الألبا وزهر الحياة الدنيا - الخفاجي - مصر ١٢٧٣ هـ.
٤٢٧ - زاد المسير في علم التفسير - ابن الجوزي - المكتب الإسلامي.
٤٢٨ - زعماء الإصلاح في العصر الحديث - أحمد أمين - ١٩٤٨ هـ.
٤٢٩ - زهرة الآس في بناء مدينة فاس - الحسن علي الجرثاني - الجزائر ١٣٤١ هـ.
٤٣٠ - سفينة البحار - عباس بن محمد رضا القمي - نجف ١٣٥٥ هـ.
٤٣١ - سل النصال = موسوعة أعلام المغرب.
٤٣٢ - سلافة العصر في محاسن الشعراء - ابن معصوم - مصر ١٣٢٤ هـ.
٤٣٣ - سلسلة الأحاديث الضعيفة والموضوعة - محمد ناصر الدين الألباني - مكتبة المعارف الرياض الطبعة الأولى ١٤٠٨ هـ - ١٩٨٧ م.
٤٣٤ - سلك الدرر في أعيان القرن الثاني عشر - مرادي - مصر ١٣٢٤ هـ.
٤٣٥ - سلم العامة والمبتدئين إلى معرفة أئمة الدين - عبد الله بن يحيى الباروني النفوسي - رساله في علماء الإباضة - مصر ١٣٢٤ هـ.
٤٣٦ - سلوة الأنفاس ومحادثة الأكياس لمحمد الكتاني - فاس ١٣١٦ هـ.
٤٣٧ - سنن القراء ومناهج المجودين - عبد العزيز بن عبد الفتاح القاري - مكتبة الدار - ط ١ - ١٤١٤ هـ','2','998',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (896,'٤٣٨ - سوس العالمة - المختار السوسي
٤٣٩ - سير أعلام النبلاء للإمام شمس الدين محمد بن أحمد الذهبي مؤسسة الرسالة الطبعة الثانية ١٤٠٢ هـ - ١٩٨٢ م
٤٤٠ - سير الأئمة وأخبارهم المعروف بتاريخ أبي زكرياء لأبي زكرياء يحيى بن أبي بكر دار الغرب الإسلامي بيروت - لبنان الطبعة الثانية ١٤٠٢ هـ ١٩٨٢ م
٤٤١ - سيرة ابن هشام - الطبعة الثالثة ١٣٩٨ هـ - ١٩٧٨ م - مطبعة الفجالة الجديدة.
٤٤٢ - سيرة القيروان، محمد العروسي المطوي، الدار العربية للكتاب، ١٩٨١ م.
٤٤٣ - شجرة النور الزكية في طبقات المالكية، محمد بن محمد مخلوف، دار الكتاب العربي، بيروت، لبنان. دار الفكر
٤٤٤ - شذرات الذهب في أخبار من ذهب أبي الفلاح عبد الحي الحنبلي منشورات ت دار الآفاق الجديدة.
٤٤٥ - شرح العقيدة الطحاوية - ابن أبي العز - مكتبة الدعوة الإسلامية.
٤٤٦ - شرح ديوان ابن رازكه - محمد سعيد بن دهاه
٤٤٧ - شرح مشكل غريب القرآن أبي محمد مكي بن أبي طالب القيسي - دار النور الإسلامي - الطبعة الأولى - بيروت لبنان ١٤٠٨ هـ.
٤٤٨ - شرف الطالب = انظر موسوعة أعلام المغرب
٤٤٩ - شهيرات التونسيات، ح. ح عبد الوهاب، مكتبة المنار، تونس، ط ٢، ١٩٦٦ هـ.
٤٥٠ - شيخ الجامع الأعظم محمد الطاهر ابن عاشور - بلقاسم الغالي - دار ابن حزم بيروت - ط ١ - ١٤١٧ هـ
٤٥١ - صبح الأعشى القلقشندي (أبو العباس أحمد بن علي) في صناعة الإنشا تحقيق الدكتور إحسان عباس بيروت
٤٥٢ - صحيح ابن ماجه - الألباني - مكتب التربية.
٤٥٣ - صحيح البخاري - انظر فتح الباري
٤٥٤ - صحيح الجامع - محمد ناصر الدين الألباني الطبعة الأولى ١٣٨٨ م - ١٩٦٩ هـ المكتب الإسلامي.
٤٥٥ - صحيح السيرة النبوية - ابن طرهوني - الطبعة الأولى ١٤١٤ هـ مكتبة العلم.
٤٥٦ - صحيح سنن أبي داود - الألباني - مكتب التربية.
٤٥٧ - صحيح مسلم للإمام أبي الحسين مسلم بن الحجاج القشيري - دار إحياء الكتب العربية.','2','999',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (897,'٤٥٨ - صراع مع الحماية - محمد المرزوقي ١٩٧٣ م
٤٥٩ - صفة الصفوة - ابن الجوزي - حيدر آباد ١٣٥٥ هـ.
٤٦٠ - صفحات من تاريخ مدن الجزائر - عبدالقادر نور الدين - قسنطينة
٤٦١ - صفوة من انتشر = انظر موسوعة أعلام المغرب.
٤٦٢ - صورة الأرض - أبو القاسم بن حوقل النصيبي، ليدن، ١٩٣٨ م.
٤٦٣ - صوفيات - عبد الرحمن الوكيل - مطبعة سفير ط ١ - ١٤١٣ هـ
٤٦٤ - ضعيف الترمذي - الألباني - مكتب التربية.
٤٦٥ - ضعيف الجامع الصغير وزيادته - محمد ناصر الدين الألباني - المكتب الإسلامي دمشق بيروت.
٤٦٦ - طبقات ابن قاضي شهبة النحاة واللغويين.
٤٦٧ - طبقات الحفاظ - عبد الرحمن السيوطي
٤٦٨ - طبقات الحنابلة - القاضي أبو يعلىمحمد بن عبد القادر النابلسي. - دمشق ١٣٥٠ هـ.
٤٦٩ - طبقات الشاذلية = جامع الكرامات العلمية.
٤٧٠ - طبقات الشافعية - الأسنوي
٤٧١ - طبقات الشافعية الكبرى الإمام أبي نصر عبد الوهاب ابن تقي الدين السبكي - دار المعرفة - بيروت لبنان
٤٧٢ - طبقات الفقهاء، إبراهيم بن علي الشيرازي، أبو إسحاق (ت ٤٧٦ هـ)
٤٧٣ - طبقات المشايخ بالمغرب - أبو العباس أحمد بن سعيد الدرجني (ت ٦٧٠ هـ) = إبراهيم طلاي مطبعة البحث قسنطينة ١٣٩٤ هـ.
٤٧٤ - طبقات المفسرين، جلال الدين السيوطي (ت ٩١١ هـ)، تح على محمد عمر، مكتبة وهبة القاهرة، ط ١، ١٣٩٦ هـ.
٤٧٥ - طبقات المفسرين لأحمد بن محمد الأدنوي الناشر مكتبة العلوم والحكم بالمدينة المنورة الطبعة الأولى ١٤١٧ هـ - ١٩٩٧ م
٤٧٦ - طبقات المفسرين للإمام جلال الدين عبد الرحمن السيوطي دار الكتب العلمية بيروت - لبنان الطبعة الأولى ١٤٠٣ هـ - ١٩٨٣ م
٤٧٧ - (*) طبقات المفسرين للحافظ شمس الدين محمد بن أحمد الداوودي دار الكتب العلمية بيروت - لبنان الطبعة الأولى ١٤٠٣ هـ ١٩٨٣ م
٤٧٧ - طبقات النحاة واللغويين لابن قاضي شهبة
٤٧٨ - طبقات النحويين واللغويين، أبو بكر، محمد بن الحسن الزبيدي الأندلسي، تح محمد أبو الفضل إبراهيم، دار المعارف، مصر.
_________
(*) قال مُعِدُّ الكتاب للشاملة: هذا ليس بالمطبوع','2','1000',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (898,'٤٧٩ - طبقات علماء إفريقية - أبي العرب محمد بن أحمد بن تميم - جمع محمد بن أبي شنب - الجزائر ١٣٣٢ هـ.
٤٨٠ - طبقات علماء إفريقية (وتونس) أبو العرب محمد بن أحمد بن تميم (ت ٣٣٣ هـ). تح محمد بن أبي شنب، الجزائر، ١٩١٥ م. تصوير دار الكتاب اللبناني، بيروت. تح علي الشابي ونعيم اليافي، الدار التونسية للنشر، ١٩٦٨ م.
٤٨١ - (*) طبقات علماء إفريقية، محمد بن حارث الخشني (ت ٣٦١ هـ)، تح محمد بن أبي شنب، كلية الأداب بالجزائر، ١٩١٥ م.
٤٨١ - طبقات علماء إفريقية وتونس لأبي العرب محمد بن أحمد بن تميم القيرواني الدار التونسية للنشر تونس المؤسسة الوطنية للكتاب الطبعة الثانية ١٩٨٥
٤٨٢ - عالم القرن العشرين وغواص في بحر القرآن = انظر مجلة الأربعاء
٤٨٣ - عبد الكريم أمير الريف - روبرت فورنو ترجمة فؤاد أيوب - دمشق
٤٨٤ - عبقرية اليوسي - عباس الجراري - الدار البيضاء ١٩٨١ م
٤٨٥ - عجائب الآثار - للجبرتي
٤٨٦ - عصر المرابطين والموحدين = الحضارة الإسلامية في المغرب والأندلس
٤٨٧ - عقود اللآلي في الأسانيد العوالي = ثبت العابدين.
٤٨٨ - علماء ومفكرون عرفتهم - محمد المجذوب - دار الشرق.
٤٨٩ - عنوان الأريب عمن نشأ بالمملكة التونسية من عالم وأديب، محمد النيفر، المطبعة التونسية ١٣٥١ هـ.
٤٩٠ - عنوان الدراية فيمن عرف من العلماء في المائة السابعة بيجاية - أحمد بن أحمد العنبري - الجزائري ١٣٢٨ هـ.
٤٩١ - عيون التواريخ - لابن شاكر الكتبي.
٤٩٢ - غاية النهاية في طبقات القراء لشمس الدين أبي الخير الجزري دار الكتب العلمية بيروت لبنان عنى بنشره ج. برجستراسر الطبعة الثالثة ١٤٠٢ هـ ١٩٨٢ م ط ٢، ١٤٠٠ هـ.
٤٩٣ - فتح الباري شرح صحيح البخاري - أحمد بن علي بن حجر العسقلاني - مكتبة الرياض.
٤٩٤ - فتح الشكور في معرفة أعيان علماء التكرور - أبي عبد الله الطالب بن محمد بن أبي بكر الصديق البرتلي الولاتي - ت محمد إبراهيم الكتاني، محمد حجي - دار الغرب الإسلامي بيروت - ط ١، ١٩٨١ م
٤٩٥ - فتح المغيث بشرح ألفية الحديث - السخاوي - ت علي حسين - المطبعة السلفية - الهند ١٤٠٧ هـ.
_________
(*) قال مُعِدُّ الكتاب للشاملة: هذا ليس بالمطبوع','2','1001',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (899,'٤٩٦ - فتوح إفريقية، عبد الله بن عمر الواقدي (ت ٢٠٧ هـ) مطبعة المنا، تونس ١٩٦٦ م.
٤٩٧ - فتوح البلدان، أحمد بن يحي البلاذري (ت ٢٧٩ هـ) دار الكتب العلمية، بيروت ١٤٠٣ هـ.
٤٩٨ - فتوح مصر وأخبارها، عبد الرحمن بن عبد الله بن الحكيم (ت ٢٥٧ هـ)، ليدن ١٩٣٠ هـ.
٤٩٩ - فصوص الحكم - محيي الدين ابن عربي
٥٠٠ - فضائل إفريقية في الآثار والأحاديث الموضوعة محمد العروسي المطوي، دار الغرب الإسلامي، بيروت، ١٤٠٣ هـ.
٥٠١ - فهارس الخزانة الحسنية بالقصر الملكي بالرباط المجلد السادس الفهرس الوصفي لعلوم القرآن محمد العربي الخطابي الرباط ٧٠٤١ هـ ط الأولى مطبعة النجاح
٥٠٢ - فهرس ابن عطية - ابن عطية - ت أبو الأجفان ومحمد الزاهي - دار الغرب الإسلامي بيروت ١٩٨٣ م.
٥٠٣ - فهرس الفهارس والأثبات ومعجم المعاجم والمشيخات والمسلسلات، عبد الحي بن عبد الكبير الكتاني، تح إحسان عباس، دار الغرب الإسلامي، ط ٢، ١٤٠٢ هـ.
٥٠٤ - فهرس مخطوطات خزانة القرويين، محمد العابد الفاسي، الدار البيضاء، ١٣٩٩ هـ.
٥٠٥ - فهرس مخطوطات مركز أحمد بابا للتوثيق والبحوث التاريخية بتنبكتو - مجموعة من المكتبيين بالمركز - مؤسسة الفرقان للتراث الإسلامي - لندن.
٥٠٦ - فهرسة من رواه عن شيوخه، أبو بكر محمد بن خير الأشبيلي (ت ٥٧٥ هـ)، المكتب التجاري، بيروت مكتبة المثنى، بغداد، مؤسسة الخانجي القاهر، ط ٢، ١٣٨٢ هـ.
٥٠٧ - فوات الوفيات - ابن شاكر الكتبي - مصر ١٢٩٩ هـ.
٥٠٨ - قادة فتح المغرب - محمود شيث خطاب - دار الفتح - بيروت الطبعة الأولى ١٣٨٦ هـ.
٥٠٩ - قضاة دمشق - لابن طولون المجمع العلمي العربي دمشق ١٩٥٦ م.
٥١٠ - قضاة قرطبة، محمد بن حارث الخشني القيرواني، (ت ٣٦١ هـ)، الدار المصرية للتأليف والترجمة ١٩٦٦ م.','2','1002',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (900,'٥١١ - قطوف دانية من آيات قرآنية - الأخضر بن قويدر الدهمة - المطبعة العربية غرداية - الجزائر ١٤١٨ هـ
٥١٢ - قلائد العقيان - للفتح بن خافان - صورة للطبعة باريس - تونس ١٩٦٦ م.
٥١٣ - قيام دولة المرابطين - حسن أحمد محمود - دار الفكر العربي - بدون تاريخ
٥١٤ - ضياء التأويل - لابن فودي - قواند - طبع بمطابع الاستقامة بالقاهرة
٥١٥ - كشف الأستار عن زوائد البزار - نور الدين علي بن أبي بكر الهيثمي - ت حبيب الرحمن الأعظمي - مؤسسة الرسالة - الطبعة الثانية ١٤٠٤ هـ.
٥١٦ - كشف الظنون عن أسامي الكتب والفنون للعلامة مصطفى بن عبد الله القسطنطيني الرومي الحنفي دار الكتب العلمية بيروت - لبنان ١٤١٣ هـ ١٩٩٢ م
٥١٧ - كنز الجوهر في تاريخ الأزهر سليمان الحنفي الزرياني - مصر - ١٣٢٠ هـ.
٥١٨ - لباب النقول في أسباب النزول - للإمام جلال الدين السيوطي - دار التراث.
٥١٩ - لحظ الألحاظ في الاستدراك والزيادة على ذخيرة الحفاظ - عبد الرحمن بن عبد الجبار الفريوائي - دار الدعوة، دار السلف - الهند، الرياض - الطبعة الأولى ١٤١٦ هـ.
٥٢٠ - لسان العرب - ابن منظور - دار المعارف
٥٢١ - لسان الميزان - للإمام شهاب الدين أبي الفضل أحمد بن علي بن حجر العسقلاني - مؤسسة الأعلمي للمطبوعات الطبعة الثالثة ١٣٩٠ هـ.
٥٢٢ - لقط الفرائد - أحمد بن محمد ابن القاضي المكناسي
٥٢٣ - متاعب المرأة في مرحلة الزواج - عز الدين محمد نجيب - مكتبة ابن سينا - مصر الجديدة القاهرة
٥٢٤ - مجمع الزوائد ومنبع الفوائد - للحافظ نور الدين علي بن أبي بكر الهيثمي - دار الكتاب العربي الطبعة الثالثة ١٤٠٢ هـ.
٥٢٥ - محمد العياشي وجهاده ضد الأسبان والبرتغال - شوقي الجمل - مجلة الجمعية المصرية للدراسات التاريخية عدد عام ٦٧٩١ م
٥٢٦ - مختصر العلو للعلي الغفار - الحافظ شمس الدين الذهبي، اختصره وحققه محمد ناصر الدين الألباني، الطبعة الأولى المكتب الإسلامي.
٥٢٧ - مدرسة الحديث في القيروان من الفتح الإسلامي إلى منتصف القرن الخامس الهجري للحسين بن محمد شواط الدار العالمية للكتاب الإسلامي الطبعة الأولى - ١٤١١ هـ','2','1003',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (901,'٥٢٨ - مرآة الجنان وعبر اليقظان لأبي محمد بن عبد الله اليافعي اليمني، بيروت ١٩٥٤ م.
٥٢٩ - مراصد الاطلاع على أسماء الأمكنة والبقاع، عبد المؤمن بن الحق البغدادي (ت ٧٣٩ هـ)، مطبعة الحلبي، القاهرة ط ١، ١٣٧٣ هـ.
٥٣٠ - مرويات الإمام أحمد في التفسير - مجموعة من الباحثين منهم حكمت بشير ومحمد طرهوني - الطبعة الأولى ١٤١٤ هـ - ١٩٩٤ م.
٥٣١ - مرويات الإمام مالك - ت حكمة بشير ياسين وابن طرهوني - دار المؤيد الرياض ١٤١٥ هـ.
٥٣٢ - مسائل الإمام أحمد - لعبد الله بن أحمد - المكتب الإسلامي.
٥٣٣ - مسائل الإمام أحمد - للنيسابوري - المكتب الإسلامي.
٥٣٤ - مسامرات الظريف بمحاسن التعريف - تاريخ فقهاد الدولة الحسينية بتونس - محمد السنوسي - تونس
٥٣٥ - مشاهير علماء الأمصار - أبوا حاتم ابن حبان البستي - القاهرة مطبعة الجنة التأليف الترجمة ١٣٧٩ هـ
٥٣٦ - مشاهير علماء نجد - عبد الرحمن بن عبد اللطيف آل الشيخ - دار اليمامة للبحث ١٣٩٤ هـ.
٥٣٧ - مطمح الأنفس - الفتح بن خاقان بيروت - مؤسست الرسالة ١٤٠٣ هـ.
٥٣٨ - معالم الإيمان في معرفة أهل القيروان، عبد الرحمن بن محمد الأنصاري الدباغ، (ت ٦٩٦ هـ)، أبو القاسم بن عيسى بن ناجي (٨٣٩ هـ) مكتبة الخانجي بمصر، المكتبة العتيقة تونس ط ٢، ١٣٨٨ هـ.
٥٣٩ - معالم التنزيل - أبي محمد الحسين بن مسعود الفراء البغوي بهامش تفسير الخازن - دار الفكر ١٣٩٩ هـ
٥٤٠ - معالم تاريخ المغرب والأندلس، د. حسين مؤنس، مؤسسة المعارف، بيروت، ط ١، ١٩٨٠ م.
٥٤١ - معاني القرآن - أبو جعفر النحاس - ت محمد علي الصابوني - شركة مكة للطباعة - الطبعة الأولى ١٤٠٨ هـ.
٥٤٢ - معجم أعلام الجزائر، عادل نويهض، مؤسسة نويهض الثقافية بيروت، ط ٢، ١٤٠٠ هـ.
٥٤٣ - معجم الأدباء شهاب الدين أبو عبد الله باقوة الحموي - القاهرة - نشرة مرجليوت - المطبعة الهندية سنة ١٩٢٣ م.','2','1004',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (902,'٥٤٤ - معجم الأطباء - أحمد عيسى - القاهرة - مطبعة فتح الله إلياس ١٣٦١ هـ.
٥٤٥ - معجم الأنساب والأسر الحاكمة - زامباور - ترجمة زكي حسن وآخرين - القاهرة - مطبعة جامعة فؤاد ١٩٥١ م.
٥٤٦ - معجم البلدان، ياقوت الحموي (ت ٦٢٦ هـ)، دار صادر، بيروت، ١٩٥٥ م.
٥٤٧ - معجم الجواهر = انظر الجواهر الحسان
٥٤٨ - معجم الشيوخ - محمد بن أحمد بن عثمان الذهبي - تحقيق محمد الهيلة - الطائف مكتبة الصديق ١٤٠٨ هـ.
٥٤٩ - معجم المؤلفين - عمر رضا كحالة مؤسسة الرسالة بيروت الطبعة الأولى ١٤١٤ هـ ١٩٩٣ م
٥٥٠ - معجم المحدثين والمفسرين - لعبد العزيز بن عبد الوحد بن علي بن عبد الله
٥٥١ - معجم المصنفين - للتونكي
٥٥٢ - معجم المطبوعات العربية - لعلي جواد طاهر
٥٥٣ - معجم المطبوعات - ليوسف سركيس
٥٥٤ - معجم المفسرين من صدر الإسلام حتى العصر الحاضر - لعادل نويهض - مؤسسة نويهض الثقافية الطبعة الثالثة ١٤٠٩ هـ - ١٩٨٨ م
٥٥٥ - معجم طبقات الحفاظ والمفسرين - عبد العزيز عز الدين السيروان - عالم الكتب الطبعة الأولى ١٤٠٤ هـ - ١٩٨٤ م
٥٥٦ - معجم مااستعجم في أسماء البلدان والمواضع - أبو عبيد عبد الله بن عبد العزيز البكري: تحقيق مصطفى السقا - عالم الكتب - بيروت ط ٣ سنة ١٤٠٣ هـ
٥٥٧ - معجم مقاييس اللغة - أحمد بن فارس - تحقيق عبد السلام هارون - القاهرة دار إحياء الكتب العربية ١٣٦٦ هـ.
٥٥٨ - معرفة القراء الكبار على الطبقات والأعصار - سيد جاد الحق - دار الكتب الحديثة - القاهرة سنة ١٩٦٩ م.
٥٥٩ - معرفة علوم الحديث - الحاكم النيسابوري - مؤسسة الرسالة.
٥٦٠ - مفتاح السعادة ومصباح السيادة - طاش كبري زادة - حيد آباد ١٣٢٩ هـ.
٥٦١ - مفكرون وأدباء من خلال آثارهم - أنور الجندي - دار الإرشاد للطباعة بيروت - ١٩٦٧ م.
٥٦٢ - مقدمة ابن خلدون - ولي الدين عبد الرحمن بن خلدون - القاهرة - دار الشعب ١٣٨٩ هـ.','2','1005',NULL,NULL,'0');
INSERT INTO [b17064] VALUES (903,'٥٦٣ - مقدمة الفتح = فتح الباري
٥٦٤ - مقدمة المحرر الوجيز = المحرر الوجيز
٥٦٥ - مقدمة تفسير ابن عرفة = تفسير ابن عرفة.
٥٦٦ - مقدمة تفسير الثعالبي = تفسير الثعالبي
٥٦٧ - مقدمة دولة الأدارسة = دولة الأدارسة
٥٦٨ - مقدمة سلسلة الأحاديث الضعيفة = سلسلة الأحاديث الصحيحة
٥٦٩ - مقدمة طبقات أبي العرب = طبقات أبي العرب
٥٧٠ - مقدمة فتح الباري = فتح الباري
٥٧١ - مقدمة في أصول التفسير - ابن تيمية - المكتبة السلفية
٥٧٢ - مقدمة مختصر تفسير الطبري = مختصر تفسير الطبري
