INSERT INTO [b17666] VALUES (535,'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ','3','560',NULL,NULL,'0');
INSERT INTO [b17666] VALUES (536,'{بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ (١) الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ (٢) الرَّحْمَنِ الرَّحِيمِ (٣) مَالِكِ يَوْمِ الدِّينِ (٤) إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ (٥) اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ (٦) صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلَا الضَّالِّينَ (٧)}','3','561',NULL,NULL,'0');
INSERT INTO [b17666] VALUES (537,'{قُلْ يَا أَيُّهَا النَّاسُ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ جَمِيعًا الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ لَا إِلَهَ إِلَّا هُوَ يُحْيِي وَيُمِيتُ فَآمِنُوا بِاللَّهِ وَرَسُولِهِ النَّبِيِّ الْأُمِّيِّ الَّذِي يُؤْمِنُ بِاللَّهِ وَكَلِمَاتِهِ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ (١٥٨)} (الأعراف)!
{وَمَا أَرْسَلْنَاكَ إِلَّا رَحْمَةً لِلْعَالَمِينَ (١٠٧)} (الأنبياء)!
{لَقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ وَذَكَرَ اللَّهَ كَثِيرًا (٢١)} (الأحزاب)!','3','562',NULL,NULL,'0');
INSERT INTO [b17666] VALUES (538,'(في علم المغازي خير الدنيا والآخرة)!
الزهري
(كنا نعلّم مغازي رسول الله - صلى الله عليه وسلم -، كما نعلم السورة من القرآن الكريم)!
زين العابدين علي بن الحسين
(كان أبي يعلمنا المغازي والسرايا، ويقول: يا بني هذه شرف آبائكم فلا تضيّعوا ذكرها)!
إسماعيل بن محمد بن سعد بن أبي وقاص','3','563',NULL,NULL,'0');
INSERT INTO [b17666] VALUES (539,'<span data-type="title" id=toc-229>مقدمة</span>
ونتابع السيرة النبوية في أطوارها التاريخية لنقف وقفة المتأمّل، عند (حديث بدء الوحي)، ونلقي الضوء على الملابسات المحيطة بهذا الأمر الإلهي العظيم، ونبصر أصداء هذا الحديث في سيرة خاتم النبيين محمد - صلى الله عليه وسلم -!
ومن ثمَّ نقف على أعتاب بداية الدعوة المباركة التي عمَّت الكون بخيريّتها ونقائها وصفائها!
ولقد جاء بدء هذا الوحي بـ (لا إله إلا الله) خالصة من كل رواسب الجاهليّة ليعلن على الملأ خيريّتها، ويشع نورها الحق إلى الناس جميعاً!
والله أسأل: التوفيق والسداد!
والعون والرشاد!
إنه سميع مجيب!
الكويت في: ١٨ من رجب ١٤٢٨ هـ
٢١ من يوليو ٢٠٠٨ م
راجي عفو ربه
سعد محمد محمد الشيخ (المرصفي)
أستاذ الحديث وعلومه
كلية الشريعة والدراسات الإِسلامية
جامعة الكويت - سابقاً','3','565',NULL,NULL,'0');
INSERT INTO [b17666] VALUES (540,'<span data-type="title" id=toc-230>حديث بدء الوحي في الميزان</span>','3','567',NULL,NULL,'0');
