INSERT INTO [b17851] VALUES (4,'المقدمة
بسم الله الرحمن الرحيم
الحمد لله ربّ العالمين، والصَّلاة والسَّلام على أشرف الأنبياء والمرسلين، نبيّنا محمَّد وعلى آله وصحبه أجمعين، وعلى من اتبعهم بإحسان إلى يوم الدِّين.
أمَّا بعد:
فهذه رسالة معنونة بـ "قطع الجدال في أحكام الاستقبال"، لمؤلِّفها الشيخ محمَّد بن حسن العجيمي المتوفَّى سنة (١١٥٦ هـ)، وهي تتعلَّق بحكم تقدم المأموم على الإِمام عند الكعبة إذا كانا في جهة واحدة، أو كان المأموم أمام الركنين وقد حاذى الإِمام.
وقد أحببت إخراجها لينتفع بها إخواني، وكان السبب في نشرها فضيلة الشيخ البحاثة المكرم الأستاذ محمد بن ناصر العجمي -حفظه الله- عندما زارني، فعرضتُ عليه ما في خزانتي من مخطوطات تتعلَّق بالبلد الحرام، فطلب منِّي العناية بهذه الرسالة والاشتراك في "لقاء العشر الأواخر"، الذي أتحف الباحثين بالرسائل النافعة، والمخطوطات القيِّمة.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b17851] VALUES (5,'فاستجبت لطلبه، وامتثلت لرغبته، فجزاه الله خيرًا وبارك الله فيه ونفع به، وجعل الله الخير أمامه، والسعادة وراءه، هو وإخوانه القائمين على هذا اللقاء، والعاملين على إخراج هذه الكنوز من مكنوناتها.
وأخيرًا أدعو الله الكريم أن يجعل ما عملته في ميزان حسناتي، وأن ينفع به إخواني، وصلَّى الله على سيِّدنا محمَّد وعلى آله وصحبه وسلَّم.
وكتبه
يُوسُف بن مُحمَّد الصّبيحي',NULL,'6',NULL,NULL,'0');
INSERT INTO [b17851] VALUES (6,'<span data-type="title" id=toc-2>ترجمة المؤلف </span>(١)
<span data-type="title" id=toc-3>اسمه وولادته:</span>
هو المؤرِّخ المحدِّث الشيخ محمد بن حسن العجيمي الحنفي المكي. وُلِد بمكة ونشأ بها.

<span data-type="title" id=toc-4>طلبه للعلم:</span>
أخذ العلوم عن عدد من العلماء حتى صار يشار إليه بالبنان، وحاز قصبات السبق في ميدان البيان، فأجازه عدد من العلماء بالتدريس.
وكان عالم زمانه وفريد أقرانه.

<span data-type="title" id=toc-5>مشايخه:</span>
أخذ عن والده العلَّامة حسن العجيمي وغيره من علماء عصره.
_________
(١) مصادر ترجمته: مختصر نشر النور والزهر ص (٤٦١)؛ ونظم الدرر ص (١٠١)؛ وأعلام المكيين (٢/ ٦٧١).',NULL,'7',NULL,NULL,'0');
INSERT INTO [b17851] VALUES (7,'<span data-type="title" id=toc-6>مؤلَّفاته:</span>
له عدد من المؤلفات، منها: "قطع الجدال"، وهي هذه الرسالة.

<span data-type="title" id=toc-7>وفاته:</span>
تُوُفِّي رحمه الله بمكة سنة ١١٥٦ هـ.
* * *',NULL,'8',NULL,NULL,'0');
INSERT INTO [b17851] VALUES (8,'<span data-type="title" id=toc-8>وصف المخطوطة</span>
اعتمدتُ في إخراج هذه النسخة على المخطوطة الموجودة في مكتبة مكة المكرمة برقم (٦) مجاميع، وهي بخط الرقعة، وتقع في ٧ صفحات في كل صفحة ٢٤ سطرًا.
ونسخها الشيخ جعفر بن أبي بكر اللبني وتملكها، وفي طرَّتها تقريظ لأحد علماء مكة للرسالة.
* * *',NULL,'9',NULL,NULL,'0');
