INSERT INTO [b1824] VALUES (758,'{فإن كنت فى شك مما أنزلنا إليك} ... ٩٤-٩٥) ... ١٣٧ ... {لا تمدن عينيك إلى ما متعنا} ... (٨٨-٨٩) ... ١٣٣
