محمد بن جرير الطبرى ... ٤ ... نسطورا الراهب ... ٢٩٨
محمد حسين هيكل ... ١٨٦ ... النووى = يحيى بن شرف الحورانى
محمد رشيد رضا ... ٢٢٧ ... نيازى عز الدين ... ٣٠٩
محمد بن السائب الكلبى ... ٣٢٠ ... (هـ)
محمد بن الشيخ الزرقانى ... ٣١٥ ... هرقل "ملك الروم" ... ٨
محمد بن عبد الوهاب الجبائى ... ١٥٠ ... هشام آل قطيط ... ٤٣٧
محمد عبده ... ٢٢٤ ... (و)
محمد بن على الشوكانى ... ١٥٤ ... الواقدى = محمد بن عمر بن واقد
محمد بن على بن عمر المازرى ... ٢٤٠ ... ورقة بن نوفل ... ١٩٧',NULL,'776',NULL,NULL,'0');
INSERT INTO [b1824] VALUES (778,'محمد بن عمر الواقدى ... ٢٠٤ ... (ى)
