-- Insert data for b18274
INSERT INTO [b18274] VALUES (1,'بسم الله الرحمن الرحيم
الحمد لله الذي فتح بتيسير الوصول إلى أصول','1','193',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2,'الشريعة الغراء، وشرح الصدور بنور الاهتداء إلى محجتها الزهراء، والصلاة والسلام على سيدنا ونبينا أفضل المرسلين حبيب الله أبي القاسم محمد خاتم النبيين وعلى آله الطيبين وصحابته هداة الدين.
وبعد:
فقد يسر الله الكريم الرءوف الرحيم، بإتمام «تيسير الوصول إلى','1','194',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (3,'منهاج الأصول» فجاء بحمد الله كنزًا مدفونًا ينفق من تبره، وبحرًا مشحونًا يستغنى (بفرائد دره.
لكنه مبسوط لكثرة تحقيقاته، وإيضاح منهاجه، وحل مشكلاته، فأردت اختصاره على طريقة متوسطة، حاوية لنفائسه المستنبطه','1','195',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (4,'ترتفع عن الإقلال المخل، وتنحط عن الإطناب الممل، وافية ببسط عبارة المتن
المشكلة، وتفصيل إشاراته المجملة، وذكر المحتاج إليه من القيود والأمثلة، وإظهار الضمائر والمتعلقات المعضلة، ومن الله الكريم أستمد الصواب والتوفيق إلى ما به يحصل جزيل الثواب وهو حسبي ونعم الوكيل.','1','196',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (5,'بسم الله الرحمن الرحيم
تقدس من تمجد بالعظمة والجلال، وتنزه من تفرد بالقدم والكمال، عن مناسبة الأشباه والأمثال، ومصادمة الحدوث والزوال».
افتتح المصنف (رحمه الله تعالى) كتابه بعد التيمن بالبسملة بالثناء على الله -تعالى- أداء لحق شيء مما يجب عليه من شكر نعمائه، إلى تأليف هذا المختصر، أثر من آثارها، ولم يأت بالمنهج المعروف، إما تفننًا في العبارة؛ لأن ما أتى به في هذه الخطبة مشتمل على «الحمد لله»، ولا ينافيه ما أخرجه أبو داود','1','197',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (6,'وغيره من حديث أبي هريرة -رضي الله عنه -من قوله صلى الله عليه وسلم: «كل أمر ذي بال لا يبدأ فيه بحمد الله أقطع».
لأن معناه: الافتتاح بما يدل على المقصود من حمد الله تعالى، لأن الخطبة يتعين فيها سياق واحد يمتنع العدول عنه، أو جعل المصنف (تقدس إلى آخره) بيانًا للبسملة، لأن من كان معبودًا بالحق لا محالة','1','198',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (7,'يكون متصفًا بصفات الكمال، منزهًا عن صفات النقص.
ولأن في العبارة الغريبة لذة ليست في المشهورة.
هذا وقد صرح المصنف فيما سيجيء بقوله: نحمده.
«وتقدس» من القدس، وهو الطهارة لغة.
والقدوس: فعول منه صفة من صفات الله تعالى، وهو المنزه عن','1','199',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (8,'كل وصف يدركه حس، أو يتصوره خيال أو يسبق إليه وهم، أو يختلج به ضمير، أو يفضي به تفكير، فهو منزه عن أوصاف كمال الخلق، كما هو منزه عن أوصاف نقصهم، بل كل صفة تتصور للخلق، فهو مقدس عنها وعما يشبهها ويماثلها.
قال الحلواني: (ومعنى: تقدس) أخذ القدس، أي: الطهارة التي تليق بجنابه لذاته، أي اختصت ذاته بالقدس، من غير عدم سابق (ولا صنع صانع).
وآثره على «قدس» و «انقدس»، لأن كثرة المباني تدل على كثرة','1','200',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (9,'المعاني، (فأفاد المبالغة: أي في اختصاصه بالطهارة).
ولأن فيه فخامة تملأ الفم، واختار صفة الماضي لدلالته على تحقيق مدلوله.
(قال بعضهم): وقدم الصفات السلبية على الإيجابية؛ لأن إثبات الإيجابية، التي هي الكمال تتوقف عند التطهير من النقائص.','1','201',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (10,'ولا يبعد أن يقصد من السلبية التمدح أيضًا، فتكون السلبية من صفات الكمال.
وقوله: «من تمجد»: يجوز أن تكون موصولة أو موصوفة أي الذي أو شيء، وتمجد صلته أو صفته، (ومن مع صلته أو صفته) في محل رفع لأنه فاعل تقدس (أو تمجد).
(قيل: وأتى بكلمة «من» إيماءً إلى أن صلتها المجد والشرف التام الحاصل له بحسب عظمة الصفات وكبرياء الذات، بلغت إلى مرتبة إذا لوحظت تعين إسنادها إلى الله -تعالى -بحيث لا مجال للوهم أن ينسبها لغيره.
فإذن لا فرق في التعبير عن خصوصية الذات بين الاسم العلمي وغيره، على أن كلمة من تفيد فخامة ليست في غيرها).
وتمجد، قال الحلواني: أي أخذ المجد لذاته كما تقدم، (أي','1','202',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (11,'اتصف بالعظمة والجلال).
والمجيد: الشريف لذاته، الجميل أفعاله، الجزيل عطاؤه ونواله، فكأن شرف الذات إذا قارنه حسن الأفعال سمي مجيدًا.
والمجد: الشرف التام الكامل، وهو لله -تعالى -في ذاته وصفاته وأفعاله.
والمجد: السعة وكثرة الخير.
والباء: في قوله بالعظمة للملابسة والمصاحبة لا للسببية، أي: مجده مصاحب للعظمة والجلال.
والعظيم من صفات الله -تعالى -: بمعنى أنه أعظم من كل','1','203',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (12,'عظيم (في نفاذ حكمه، وكمال قدرته، وقهره، وسلطانه، وأعظم من كل) عظيم في أن العقول لا تصل إلى كنه صمديته.
والجليل: هو الموصوف بنعوت الجلال.
ونعوت الجلال هو العز، والملك، والتقديس، والعلم، والقدرة، وغيرها من الصفات.
فالجامع لجميعها هو الجليل المطلق وهو الله -تعالى -.
قال الغزالي: وكأن الجليل يرجع إلى كمال الصفات، والعظيم إلى كمال الذات، والصفات جميعها منسوبًا إلى إدراك البصيرة (إذ كان','1','204',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (13,'بحيث يستغرق البصيرة) ولا تستغرقه البصيرة. انتهى
وصفات الله تعالى ثبوتية: كالحياة والعلم والقدرة والإرادة والسمع والبصر والكلام والبقاء.
وسلبية: مثل ليس بجسم ولا عرض ولا في مكان.','1','205',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (14,'قال الحلواني: فأشار المصنف (رحمه الله) بالعظمة إلى الصفات الثبوتية، وبالجلال إلى الصفات السلبية، (وأخر الصفات السلبية) رعاية للسجع.
أو يقال: صفاته -تعالى -صفات لطف، وصفات قهر، تسمى بصفات الجمال والجلال، فأشار بالعظمة إلى صفات الجمال أي اللطف، وأشار بالجلال إلى صفات القهر. وجمع بينهما ليكون العبد بين الخوف والرجاء.
قال العراقي:','1','206',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (15,'والمحفوظ «تقدس من تمجد بالعظمة والجلال»
وذكر الشيخ تقي الدين السبكي إنها: والجمال.
وقال: ومشاهدة صفة الجمال تثير المحبة ومشاهدة صفة الجلال تثير الهيبة، والعظمة تثير الهيبة أيضًا.
فلذلك قرن المصنف العظمة بالجمال، لتفيد معنى زائدًا على الجلال انتهى.','1','207',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (16,'وعطف المصنف قوله: «وتنزه» على قوله: «تقدس»؛ أنهما فعليتان وإعرابه كما مر.
قال الحلواني: ومعنى تنزه أي: أخذ في النزاهة أي الطهارة لذاته.
(كما تقدم).
وتفرد قال الحلواني: أي أخذ الفردانية لذاته كما مر، أي ذاته منفردة لا يشاركه شيء في القدم الذاتي فلا يكون وجوده عن الغير، وصفاته قديمة غير مسبوقة بالعدم.','1','208',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (17,'(وتحقيقه: أن التفعل في تفرد وفي سائر ما أورده من تقدس ونحوه، ليس لمطاوعة فعل آخر، كما يقال: علمته فتعلم، بل المراد التلبس بالشيء كما يقال في المحسوسات: تقمص فيقال: في المعاني: تفعل على معنى اتصافه به، أعني من غير تكلف، ويجوز أن يكون لمطاوعة فعل، وذلك أن معنى قدس: اعتقد التطهر فالمطاوعة في لفظ «تقدس» على معنى المعتقد تقديسه، أي أنه أقام له من الحجج','1','209',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (18,'والبراهين ما يلزم الخلق اعتقاد تنزيهه عن كل نقص فهذا معنى المطاوعة هنا لا التأثير، فمطاوعة كل شيء بحسبه، ومن هذا يعلم حسن عدول المصنف عن الإتيان بأصل الوصف إلى صيغة التفعل؛ لأنه أبلغ إذ فيه الإيماء إلى قيام الدليل على ذلك الوصف.
والكمال، قيل: هو فوق التمام.
والكمال المطلق: هو الذي يكون كلما يمكن حاصلًا له بالفعل وهو الله تعالى.
والمناسبة: اتحاد في النسبة.
والأشباه: جمع شبه -بكسر الشين وسكون الباء -وهو اتحاد في الصفات.
والأمثال: جمع مثل -بكسر الميم وسكون الثاء -وهو اتحاد في تمام','1','210',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (19,'الماهية (كذا قيل).
وقال في الصحاح: يقال هذا مِثْلُه ومَثَلُه كما يقال: شبهه وشبهه بمعنى والمصادمة الوصول من الطرفين.
والحدوث: كون الشيء بعد ما لم يكن.
والزوال: الفناء.','1','211',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (20,'ويجوز أن يتعلق قوله عن مناسبة بقوله: تقدس، أي تقدس أن تكون صفاته مناسبة لصفات غيره، وذاته أن تكون مماثلة لذات غيره.
وقوله: ومصادمة الحدوث والزوال متعلقًا بقوله: «تنزه» أي: تنزه أن يصل الحدوث إلى صفاته فلا تكون صفاته حادثة بل قديمة، وتنزه ذاته أن يكون له زوال وفناء، ففيه لف ونشر مرتب.
وصدر مجموع النشرين بكلمة «من» وعطف أحدهما على الآخر.
وأحال التعيين على ذهن السامع لأنه لا يخفى عليه أن الأول متعلق بالفقرة الأولى والثاني بالثانية.','1','212',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (21,'ويجوز أن يكون تقدس وتنزه متوجهًا إلى كل واحد منهما.
فيكون من باب تنازع الفعلين قاله الحلواني.
وفسر صاحب المحكم التقديس بالتنزيه.','1','213',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (22,'قال الزركشي: وحينئذ فقول المصنف بعده: وتنزه، من عطف أحد المترادفين على الآخر، وهو مطلوب في مقام الثناء نظرًا لتغاير الألفاظ.
والحق أن التنزيه أعم، فإن مادة القدس تدل على التطهير وهو نوع تنزيه.
وفي هذه الجمل إشارة إلى الوحدانية، ووجوب الوجود الذي يتصف بجميع الكمالات من العلم والقدرة والإرادة والحياة، ونفي الجسمية والجهة والتحيز ونفي التركيب وغيرها.
(والحاصل أنه تعالى لا يشبهه شيء لا شبهًا (بعيدًا ولا قريبًا .. ) لا في الصفات ولا في الذات).','1','214',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (23,'وقوله: «مقدر الأرزاق والآجال» ليس معطوفًا على ما قبله؛ لأنه خبر مبتدأ محذوف تقديره: هو.
والجملة الاسمية لا تعطف على الفعلية على قول اختاره الإمام الرازي.','1','215',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (24,'ونقل عن ابن جني: لاختلافهما بالنوع.
وقيل: بالجواز.
قال ابن هشام: وهو المفهوم من قول النحويين في باب الاشتغال في مثل قام زيد وعمرو أكرمته أن نصب عمرو أرجح لأن تناسب الجملتين المتعاطفتين أولى من تخالفهما.','1','216',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (25,'وقال أبو علي: يجوز في الواو فقط.
قال ابن هشام: وأضعف الثلاثة المنع مطلقًا.
وبالجملة فالأحسن هنا عدم العطف لما عرفت.
ومعناه، معين مقدار الرزق والأجل لكل واحد.
والرزق في الأصل: مصدر','1','217',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (26,'بمعنى الإخراج.
وشاع في اللغة أولًا على إخراج حظ من الله -تعالى -أو ممن غيره إلى آخر، حيوانًا كان أو غيره (لينتفع به) ثم شاع استعمالًا وشرعًا على إعطاء الله -تعالى -الحيوان ما ينتفع به حلالًا كان أو حرامًا.
والأجل: يطلق بالاشتراك اللفظي على جميع مدة العمر، وعلى آخر جزء منها والمعنيان صحيحان هنا، يعني أن الله -تعالى -كما قدر','1','218',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (27,'وضبط أحوال الإنسان من الأرزاق الروحانية التي هي العلوم والمعارف في هذه الدار وفي دار القرار، المتفاوتة حسب تفاوت أصناف المقربين والأبرار، كذلك قدر أحوال الحيوانات من سعة الرزق وضيقه وطول مدة العمر وقصره، فإذا كان الشخص واسع الرزق في العلم فلا يتكاسل في نشره وزيادته، أو ضيق الرزق فيه فيقتصر على الأهم من العلوم الدينية.
وعطف قوله: «ومدبر الكائنات في أزل الآزال» على «مقدر الأرزاق» لأنها جملة اسمية أيضًا مبتدؤها محذوف، وهذا من عطف العام على الخاص، هو من فنون البلاغة، وفيه دفع وهم، أن علمه وقدرته','1','219',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (28,'مختص بالأرزاق والآجال، فدفعه بالتعميم، فإن الكائنات أعم من الأرزاق والآجال وغيرها من الموجودات.
(وقال الحلواني): يعني أن الله -تعالى -دبر مآل كل شيء ومرجعه إلى أي شيء يكون من السعادة والشقاوة.
فعين سعادة أهل السعادة في الأزل، وكذا شقاوة أهل الشقاوة.
والتدبير: هو النظر في دبر الشيء، في آخره ومآله.
وإطلاقه على علام الغيوب محال.
فالمراد غايته، هو الإحكام والإتقان في الأفعال؛ لأن من تأمل في عاقبة أمر جاء ذلك الأمر على وجه أبلغ وأحسن.
والأزل: بفتح الزاي: القدم.','1','220',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (29,'قال في الصحاح: ذكر بعض أهل العلم أن أصل هذه الكلمة، قولهم: للقديم لم يزل، ثم نسب إلى هذا فلم يستقم إلا باختصار فقالوا: يزلي ثم أبدلت الياء ألفًا (لأنها أخف)
فقالوا أزلي كما قالوا في الرمح المنسوب إلى ذي يزن: أزني.
وجمع الأزل تأكيدًا لا تحقيقًا، قاله بعضهم.
وقيل: في إضافة الآزال مبالغة في نفي الأولية كأن للأزل أزلًا.
ويجوز أن يكون قوله في أزل الآزال من باب تنازع الفعلين فيكون مقدر ومدبر متوجهين إليه.','1','221',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (30,'أو يكون قوله: «مقدر الأرزاق والآجال» إشارة إلى القضاء.
يعني مقدر الأرزاق والآجال بحسب ما وقع.
وقوله: مدبر الكائنات إلى القدر أي دبر الكائنات التي ستوجد في علمه السابق، قاله الحلواني.
وقال بعضهم: الفرق بينهما: أن القدر عباة عن تعلق القدرة والإرادة بإيجاد جميع الأشياء، التعلق التنجيزي الواقع فيما لا يزال.
والقضاء: عبارة عن تعلقهما بها التعلق المعنوي الحاصل في الأزل.','1','222',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (31,'عالم الغيب والشهادة: خبر مبتدأ محذوف أي هو، وهذه الجملة بيان لجملة مقدر الأرزاق إلى آخره؛ لأن التقدير والتدبير دال على العلم الكامل الشامل بالضرورة، ولهذا قطعها ولم يعطف.
وفيه إشعار بعظم شأن العلم، وقدم الغيب قصدًا إلى رسوخه في علمه؛ لأن من كان عالمًا بالمغيبات كان راسخًا في العلم لا محالة، ويلزم من ذلك علمه بالمشاهدات من باب أولى.
وقوله: الكبير المتعال مبتدؤه محذوف أيضًا، وهذه الجملة مسبوقة لبيان ما سبق أيضًا؛ لأن من كان موصوفًا بهذه الصفات ضرورة يكون كبيرًا بحسب الذات، عاليًا بحسب الصفات.
والغيب بمعنى: الغائب عن الحس، والشهادة الحاضر له والألف','1','223',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (32,'واللام فيهما للاستغراق، أي يحيط علمًا بكل واحد واحد من الغيوب.
والشهادات ظاهرها وباطنها، دقيقها وجليلها، أولها وآخرها عاقبتها وفاتحتها بعلم قديم، وهذا من حيث كثرة المعلومات وهي لا نهاية لها، ثم يكون العلم في ذاته من حيث الوضوح والكشف على أتم ما يمكن فيه بحيث لا يتصور مشاهدة وكشف أظهر منه ثم لا يكون مستفادًا من المعلومات، بل تكون المعلومات مستفادة منه.
والكبير: هو ذو الكبرياء، والكبرياء: عبارة عن كمال الذات، وأعني بكمال الذات كمال الوجود، وكمال الوجود يرجع إلى شيئين:
أحدهما: دوامه أزلًا وأبدًا.
والثاني: أن وجوده هو الوجود الذي يصدر عنه كل موجود بالاختيار.','1','224',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (33,'والمتعال: هو الذي لا رتبة فوق رتبته، وجميع المراتب منحطة عنه.
والمراد بالفوقية: الفوقية بالمعنى لا بالمكان.','1','225',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (34,'وعبارة المصنف في تفسيره الكبير: العظيم الشأن الذي لا يخرج عن علمه شيء.
والمتعال: المستعلي عن كل شيء بقدرته، أو الذي كبر عن نعت المخلوقين وتعالى عنه.
ثم استأنف بقوله: «نحمده على فضله المترادف المتوال، ونشكره على ما عمنا من الإنعام والإفضال» جوابًا عن سؤال تقديره: كيف نحمد هذا المنعم العظيم الموصوف بهذه الصفات العظام؟
فقال: نحمده بصيغة الجمع ليشمله وغيره من المثنين، أدرج حمده في حمدهم، لعله يقبل، أو عظم نفسه امتثالًا لقوله تعالى: {(وأما','1','226',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (35,'بنعمة) ربك فحدث}.
وأتى بالفعل المضارع ليدل على التجدد والاستمرار، يعني فكما أن نعمه متجددة فحمدنا متجدد، كذا قيل.
والحمد: هو الثناء باللسان على الجمل الاختياري.
والشكر فعل ينبئ عن تعظيم المنعم بسبب الإنعام، سواء كان ذكرًا باللسان أو اعتقادًا، أو محبة بالجنان، أو عملًا وخدمة بالأركان.','1','227',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (36,'فمورد الحمد هو اللسان وحده، ومتعلقة يعم النعمة وغيرها.
ومورد الشكر يعم اللسان وغيره، ومتعلقه يكون النعمة وحدها.
فالحمد أعم باعتبار المتعلق وأخص باعتبار المورد والشكر بالعكس.
ومن هنا تحقق تصادقهما في الثناء باللسان في مقابلة الإحسان، وتفارقهما في صدق الحمد فقط على الوصف بالعلم والشجاعة، وصدق الشكر فقط على الثناء بالجنان في مقابلة الإحسان.','1','228',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (37,'وعن الأصفهاني: أن الحمد أعم من الشكر مطلقًا بحسب ماهيتها.
وتحقيق ماهيتها: أن الحمد ليس عبارة عن قول القائل: الحمد لله.
بل هو فعل يشعر بتعظيم المنعم بسبب كونه منعمًا، وذلك الفعل إما فعل القلب، أعني الاعتقاد باتصافه بصفات الكمال والجلال.
أو فعل اللسان أعني ذكر ما يدل عليه، أو فعل الجوارح وهو الإتيان بأفعال دالة على ذلك.
والشكر كذلك ليس قول القائل: الشكر لله، بل صرف العبد جميع ما أنعم الله تعالى به عليه من السمع والبصر وغيرهما إلى ما خلق له','1','229',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (38,'وأعطاه لأجله، كصرف النظر إلى مطالعة مصنوعاته، والسمع إلى تلقي ما ينبئ عن مرضاته، والاجتناب عن منهياته.
وعلى هذا يكون الحمد أعم من الشكر مطلقًا، لعموم النعم الواصلة إلى الحامد وغيره، واختصاص الشكر بما يصل إلى الشاكر.
والفضل: ضد النقص، قاله ابن دريد.
والجوهري قال: والإفضال: الإحسان.
(وقيل: المراد بالفضل: الفاضل عن الكفاية، وذلك أنه تعالى أعطى الكفاية وزيادة عليها).
والترادف: من الردف الذي يركب خلف الآخر، وكل من تبع شيئًا فهو ردفه (فمعنى المترادف: المتراكم بعضه على بعض).','1','230',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (39,'والتوالي: أن يكون واحدًا بعد واحد بحيث لا ينقطع، فالتوالي أبلغ من الترادف فلذا أردفه به دفعًا لوهم أنه ينقطع.
(والإنعام: مصدر أنعم إنعامًا، كأحسن يحسن إحسانًا.
وعدل عن التعبير بالنعم إلى الإنعام؛ لأن المصدر قد يكون أبلغ من الجمع.
والنعم جمع نعمة وهو كل ما يتنعم به من العيش.
وفي قوله: نحمده على فضله ونشكره على ما عمنا به تنبيه على أن الشكر لا يكون إلا في مقابلة نعمة بخلاف الحمد، ولهذا ينبغي للإنسان أن يحمد الله تعالى على ما أنعم به على غيره كما يحمده على ما أنعم به عليه.','1','231',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (40,'وفي الحديث أن النبي -صلى الله عليه وسلم -أتي ليلة الإسراء بقدح لبن وقدح خمر، (وخير بينهما)، فاختار اللبن فشربه، فقال جبريل: الحمد لله لو أخذت الخمر غوت أمتك.
وقيل): قوله: «فضله» إشارة إلى نعمه الباطنة من الإيمان والعرفان وغيرهما.
وقوله: «ونشكره على ما عمنا من الإنعام والإفضال» إشارة إلى نعمه الظاهرة من السمع والبصر وغيرهما، ولذا صرح بقوله: «عمنا» ليشمل الكل».
ووقف في بعض النسخ أحمده وأشكره وأصلي وهو واضح.','1','232',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (41,'«ونصلي على محمد الهادي إلى نور الإيمان، من ظلمات الكفر والضلال، وعلى آله وصحبه خير صحب وآل».
هذا دعاء للشارع المقنن للقوانين؛ لأنه سبب لحصول كمالاتنا وامتثالًا لقوله تعالى: {يا أيها الذين آمنوا صلوا عليه وسلموا تسليمًا}.
وأتى بالصلاة بعد حمد الله وشكره؛ لقوله تعالى: {ورفعنا لك ذكرك}.
معناه: لا أذكر إلا ذكرت معي: أشهد أن لا إله إلا الله وأشهد أن محمدًا رسول الله.','1','233',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (42,'روى هذا التفسير الرهاوي في أربعينه عن أبي سعيد عن النبي -صلى الله عليه وسلم -.
ورواه الشافعي عن مجاهد.','1','234',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (43,'قال النووي وغيره: ويكره إفراد الصلاة عن التسليم.
فإن قلت: قد جاءت الصلاة عليه غير مقرونة بالتسليم في آخر التشهد (في الصلاة).','1','235',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (44,'فالجواب: أن السلام تقدم فيه في قوله: «السلام عليك أيها النبي».
والصلاة: أصلها الدعاء، قال تعالى: {وصل عليهم} أي ادع لهم.
وقال الزجاجي: أصلها اللزوم.
قال الأزهري وغيره: الصلاة من الله تعالى رحمة، ومن الملائكة','1','236',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (45,'استغفار، ومن الآدمي تضرع ودعاء.
فصلاة الله تعالى على نبيه رحمة مقرونة بالتعظيم والثناء.
وفي البخاري عن ابن عباس -رضي الله عنهما -معنى يصلون: يبركون.
وسمى نبينا -صلى الله عليه وسلم -بمحمد لكثرة خصاله الحميدة.','1','237',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (46,'قال ابن فارس: ألهم الله أهله تسميته بذلك.
والمراد بكونه -صلى الله عليه وسلم -هاديًا، هو هدى الدلالة.
قال الله تعالى: {وإنك لتهدي إلى صراط مستقيم}.','1','238',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (47,'وأما هدى التوفيق والتأييد والعظمة فمما تفرد به الله تعالى.
(وقد تكون الهداية بمعنى الدوام عليها)، وإنما خصه بوصفه الهادي؛ لأنه المطلوب الأعظم، والغاية القصوى من اصطفاء الأنبياء، ولأنه أساس كل سعادة وفلاح.
(وقوله: من ظلمات الكفر والضلال، إشارة إلى أنه -صلى الله عليه وسلم -حصلت به الهدايتان جميعًا من ظلمات الكفر وظلمات الضلال).
والنور: الضياء، والجمع أنوار، قاله الجوهري.
وفي الكشاف:','1','239',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (48,'(ما حاصله أن) النور ضوء النار، وضوء كل نير وهو نقيض الضلمة واشتقاقه من النار، والنار من نار ينور إذا نفر؛ لأن فيها حركة واضطرابًا.
وإطلاق الضوء على النور شائع، وإن كان قد يقع بينهما فرق في بعض الاستعمالات.
والظلمات: جمع ظلمة، قال: والظلمة عدم النور.
وقيل: عرض ينافي النور واشتقاقها من قولهم:
«ما ظلمك أن تفعل كذا» أي «ما منعك وشغلك» لأنها تسد البصر (وتمنع الرؤية.','1','240',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (49,'وقال الإمام: الظلمة عدم النور عن ما من شأنه) أن يستنير.
فعلى هذا بين النور والظلمة تقابل العدم والملكة.
وعلى قول من قال: عدم النور يكون بينهما تقابل الإيجاب والسلب، وعلى قول من قال: عرض ينافي النور فبينهما تقابل التضاد.
وقوله: نور الإيمان: إما من باب إضافة العام إلى الخاص أو استعارة بالكناية.','1','241',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (50,'وقد اختلف في تفسيرها، والذي ذهب إليه المتقدمون أن الشيء إذا شبه بآخر ثم ترك التشبيه إلى الاستعارة فلابد أن يستعار للمشبه (لفظ موضوع للمشبه) به ليتحقق معنى الاستعارة الذي هو أخذ الشيء بالعارية.
ثم المستعار إما صريح: اسم المشبه به، كما في الاستعارة التصريحية، أو اسم المشبه به المسكوت عنه، أعني لفظ الكوكب مثلًا في مثالنا، وما أثبت للمشبه المذكور الذي هو الإيمان من لوازم المشبه به.
أعني النور كناية عن لفظ الكوكب، وكونه مستعارًا للإيمان، ومن هذا سميت استعارة بالكناية.
ومما ذكر في توجيه نور الإيمان، أوضح لك حال ظلمة الكفر','1','242',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (51,'والضلال.
ولما ضمن الهداية معنى الإخراج عداها بمن حيث قال: من ظلمات الكفر.
قيل: وذكر الضلال بعد الكفر تعميم بعد تخصيص.
ووحد النور، وجمع الظلمة، تنبيهًا على أن المهتدي بالإيمان (قد يتعين) له طريق خاص إلى الله تعالى بخلاف غير المهتدي، فإنه يميل إلى كل شيء ويفعل كل فعل ويذهب في كل طريق.
وقيل: توحيد النور للترغيب عليه، وتكثير الظلمات لتقبيح شأن الكفر، والتنفير عنه؛ لأن الكفر والضلالة متعدد متنوع. ولكل نوع ظلمة، بخلاف الإيمان فإنه متحد بالنوع غير مختلف.','1','243',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (52,'(قيل: وإنما قال: الهادي إلى نور الإيمان، ولم يقل إلى الإيمان، لفائدتين:
إحداهما: (أن الإيمان يكون) رافعًا للكفر، ونوره يدفع ظلمات الكفر، قال الله تعالى: {الله ولي الذين آمنوا يخرجهم من الظلمات إلى النور}.
فعبر المصنف بثمرة الإيمان، وهو النور التام يوم القيامة، وهو من باب التعبير بالعلة الغائية.
والفائدة الثانية: أن المراد بنور الإيمان بيان الأحكام الشرعية.
قال تعالى: {ويعلمهم الكتاب والحكمة}.','1','244',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (53,'والحكمة: السنة، فهو -صلى الله عليه وسلم -أخرج الناس بإذن الله تعالى من ظلمات الكفر ومن ظلمات الجهل).
والآل، أصله: أهل، بدليل أهيل، خص استعماله في الأشراف ومن له خطر.
وعن الكسائي: سمعت أعرابيًا فصيحًا يقول: أهل وأهيل وآل وأويل.
وآله -صلى الله عليه وسلم -قال الإمام الشافعي (رضى الله عنه) وجمهور أصحابه: هم بنو هاشم وبنو المطلب.','1','245',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (54,'وقيل: عترته أولاد فاطمة -رضي الله عنها -المنتسبون إليه. وقيل: أهل دينه كلهم وأتباعه إلى يوم القيامة.
قال الأزهري: هذا القول أقربها إلى الصواب، واختاره النووي في شرح مسلم، واختاره غيره.','1','246',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (55,'وحديث أنس المرفوع أن آله كل مؤمن تقي.','1','247',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (56,'وهاه البيهقي.
والصحب جمع صاحب، كركب جمع راكب.
والصحابي: نسبة إلى الصحبة ثم اختص في العرف بمن صحب النبي -صلى الله عليه وسلم -تشريفًا لهم.
وهو كل من لقي النبي -صلى الله عليه وسلم -مسلمًا ومات عليه ولو تخللت ردة.
والمراد من هذا التعريف معرفة الصحابة بعد انقراضهم.','1','248',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (57,'فقوله: لقي أعم من المجالسة، والمماشاة، ووصول أحدهما إلى الآخر، وإن لم يكالمه، ويدخل فيه رؤية أحدهما الآخر، فدخل ابن مكتوم ونحوه من العميان والذكر والأنثى.
وقوله: «مسلمًا» ليخرج من حصل له في حال كفره لقاء النبي -صلى الله عليه وسلم -فإنه ليس صحابيًا لعداوته له -صلى الله عليه وسلم -.
وقوله: مات عليه، أي على الإسلام، ليخرج من ارتد بعد أن لقيه مسلمًا، ومات على الردة كعبد الله بن جحش،','1','249',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (58,'وابن خطل.
وقوله: ولو تخللت ردة، أي بين لقياه مسلمًا وبين موته على الإسلام، فإن اسم الصحبة باق له سواء كان رجوعه إلى الإسلام في حياته، أم بعده -صلى الله عليه وسلم -.
سواء لقيه ثانيًا أم لا.
وقيل: الصحابي: من طالت صحبته ومجالسته، واختاره، جماعة من أهل الأصول.','1','250',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (59,'وأما قول الفقهاء: قال أصحاب الشافعي وأصحابنا فمجاز. مستفيض، للموافقة بينهم وشدة ارتباط بعضهم ببعض كالصاحب حقيقة.
فإن قلت: كيف جعل آل النبي -صلى الله عليه وسلم -خير آل مع أن آل إبراهيم -عليه الصلاة والسلام -أفضل منهم؛ لأن فيهم أنبياء.
أجيب: (بأن مراده) أنهم خير آل ليس لهم وصف أشرف من كونهم آل نبي، فأما من أنضم إلى ذلك كونه نبيًا فقد خرج عن وصوفه بالآل، لا أنه زال عنه الصفة، بل لأنه صارت له صفة أعظم منها فيذكر بالصفة العظمى.
وأما كون أصحابه خير الأصحاب فذلك واضح لقوله تعالى: {كنتم','1','251',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (60,'خير أمة أخرجت للناس} وأتبع صلاته -عليه الصلاة والسلام -بالصلاة على آله، وأصحابه، لأنهم كانوا مجتهدين وساعين في إظهار الدين وتبليغ الأحكام الدينية إلينا وذلك من النعم الجليلة.
(وإضافة الآل إلى المضمر لا يمتنع على الأصح لورود السماع به).
وبعد: أصلها أما بعد.
قال النحاس:','1','252',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (61,'وسئل أبو إسحاق عن معنى: أما بعد؟
فقال: قال سيبويه: معناها: مهما يكن من شيء، أي بعد ما ذكر، فوقعت كلمة أما موقع اسم هو المبتدأ وفعل هو الشرط، وتضمنت معناها.
(ولتضمنها معنى الشرط لزمتها الفاء اللازمة للشرط غالبًا).
ولتضمنها معنى الابتداء لزمها لصوق الاسم اللازم للمبتدأ قضاء لحق ما كان، وإبقاء له بقدر الإمكان.
وبعد ظرف لابد له من عامل، فعل أو شبهه يتعلق به.','1','253',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (62,'فقيل: إنه متعلق بأما، من حيث إنه قائم مقام فعل هو يكن المحذوف.
وقيل: إنه متعلق بقوله: يكن، وهي بضم الدال، وبالرفع والتنوين.
وبالنصب والتنوين، وبفتح الدال على تقدير لفظ المضاف إليه.
وكان النبي -صلى الله عليه وسلم -يأتي بها في كل خطبه وكتبه، وفي غرائب مالك للدارقطني بسند ضعيف: لما جاء ملك','1','254',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (63,'الموت إلى يعقوب عليه الصلاة والسلام قال يعقوب: -في جملة كلام - «أما بعد: فإنا أهل بيت موكلٌ بنا البلاء».
فإن صح فيكون أول من ابتدأ بها.
وقيل: داود، وأنها فصل الخطاب الذي أوتيه.
وقيل: قس بن ساعدة.
وقيل: كعب بن لؤي.','1','255',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (64,'وقيل: يعرب بن قحطان وقيل: سحبان (بن وائل)
وجواب الشرط قوله: «فإن أولى ما تهتم به الهمم العوالي، وتصرف فيه الأيام والليالي، تعلم المعالم الدينية، والكشف عن حقائق الملة الحنيفية، والغوص في تيار بحار مشكلاته، والفحص عن أستار أسرار معضلاته».
أولى: أحرى وأليق.
وتهم بفتح التاء وضم الهاء؛ لأن ماضيه ثلاثي، يقال: هم بالشيء إذا قصده بهمته أي: يهتم به ويقصد إلى فعله، ويقرؤه من لا خبرة','1','256',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (65,'له بضم التاء وكسر الهاء، على أن ماضيه رباعي، وهو خطأ، فذاك بمعنى الحزن من الهم.
قال الجوهري: وهممت بالشيء أهم هما، إذا أردته.
والهمم واحده همة بمعنى الإرادة.
والمراد النفوس العالية، فكنى بالهمم العوال عنها؛ لأنها من لوازم النفوس، وكأنها من فرط الهمة والإرادة صارت عينها.
(وصاحب الهمة العالية هو الذي يحرص على ما ينفعه ويبالغ في الاجتهاد في تحصيله).
والصرف: إخراج الشيء عن تصرفك.
والأيام والليالي: عبارة عن العمر، وهو من النفائس العظيمة.','1','257',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (66,'فينبغي أن يصرفه في أعز الأشياء.
وأشار إليه بقوله: تعلم المعالم الدينية.
والمعالم: جمع معلم موضع العلامة.
قال الحلواني: وأراد بها هنا الأحكام الشرعية الأصولية.
ولا يبعد أن يريد به (كل ما) له تعلق بالدين وهذا منه؛ لأن أدلة الشيء معالمه، إذ بها يهتدى إلى الأحكام المنسوبة إلى الدين.
(وقيل: المراد بالمعالم: أحكام الشرع بأدلتها، فإنها علامات تميز الحلال من الحرام).
والكشف: قال ابن دريد: كشفت الشيء أكشفه كشفًا إذا أظهرته','1','258',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (67,'وأبديته.
والملة: قال الراغب: هو القود إلى الطاعة.
والدين هو الانقياد له، وهما بالذات واحد، لكن الدين هو الطاعة، فيقال: اعتبار بفعل المدعو في انقياده إلى الطاعة.
والملة من أمللت الكتاب، فيقال: اعتبارًا بفعل الداعي إليها، والشارع لها ثم لكونهما بالذات.
قال تعالى: {دينًا قيمًا ملة إبراهيم حنيفًا}
فأبدل الملة من الدين.','1','259',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (68,'وقال ابن الأثير: الملة: الدين، كملة الإسلام.
وقيل: هي معظم الدين وجملة مايجيء به الرسل.
وقال الجوهري: والملة -بالكسر -: الدين والشريعة.
وقال ابن دريد: الملة النحلة التي ينتحلها الإنسان من الدين.
قال الحلواني: والحق أنهما -أي: الملة والدين -مختلفان متلازمان.
وفي الملة معنى الاجتماع، فلهذا لا يقال: ملة الله.
وفي الدين معنى الجزاء، ولهذا يضاف إلى الله تعالى فيقال: دين الله.','1','260',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (69,'والحنيفية: الإسلام.
والحنيف المائل عن الباطل إلى الحق.
(ولما كان طلب الآخرة متوقفًا على صحة الأعمال، وصحة الأعمال متوقفة على العلم، كان العلم أهم ما يطلب أولًا).
والغوص: هو النزول في عمق الماء.
والغواص: من ينزل في عمق البحر لاستخراج الدر وغيره، وغاص على الدر حصله، واستعلى عليه واطلع.
والتيار: الموج.
شبه المصنف العلم بالبحر الجاري؛ لأن العلم يجري ثوابه ولا ينقطع بالموت، كما لا ينقطع جريان البحر؛ ولأن الماء حياة الأنفس، وحياة الماء جريانه، كذلك العلم حياة القلوب.
وحياة العلم البحث فيه؛ ولأن الغوص في تيار البحر الجاري فيه','1','261',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (70,'مزيد مشقة على الغوص في الماء الراكد.
وفيه إشارة إلى أن العلم لا ينال براحة الجسد.
وأثبت له التيار ترشيحًا للاستعارة المصرحة؛ لأنه أطلق المشبه به وأراد المشبه، وأثبت ما يخص المشبه به.
وشبه التفكر والتأمل في حل تلك المشكلات بالغوص (لتخرج المسائل المشبهة بالجواهر.
وفيه إشارة إلى أن المسائل يستضاء بها من ظلمات الجهل، كما يستضاء بالجواهر في ظلمة الليل.
والفحص: البحث والتفتيش التام. وفيه إشارة إلى أن الإنسان ينبغي له أن يسعى في طلب العلم وفي السؤال عنه).
والأستار: جمع ستر.','1','262',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (71,'والأسرار: جمع سر، وهو الخفي عن الشيء.
قال الجوهري: السر الذي يكتم.
والمعضل: ما انغلق معناه ولا يفهم بسهولة.
وفي الصحاح: المعضلات: الشدائد.
(فمراد المصنف بالمعضلات: المسائل التي قوي إشكالها وبعدت عن الأفهام.
والمراد بالبحث عن أستار أسرارها: البحث عن علل المسائل المشكلة وعن معانيها، فإن العلم إذا عرف بعلله، والمسائل إذا عرفت بمعانيها، كان أدعى للنفس إلى قبولها والانقياد إليها؛ لأن إشكالها حينئذ يزول، كذا قيل).
وتشبيه المعاني الخفية تحت الألفاظ بالمخدرات خلف الأستار.','1','263',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (72,'استعارة مصرحة. وإثبات الستر ترشيح. والضمير في مشكلاته ومعضلاته، قيل: عائد على الكشف لا على الملة؛ لأنها بيضاء نقية؛ ولأن الضمير مذكر.
وقيل: عائد إلى الملة على تأويل الدين.
وعطف على قوله: إن أولى قوله: وإن كتابنا هذا "منهاج الوصول إلى علم الأصول"، الجامع بين المعقول والمشروع، والمتوسط بين الأصول والفروع، وهو أي المنهاج، وإن صغر حجمه كبر علمه','1','264',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (73,'وكثرت فوائده وجلت عوائده.
وأضاف الكتاب إلى ضمير المتكلم المعظم نفسه تعظيمًا للكتاب، وإظهارًا للنعمة قال تعالى: } وأما بنعمة ربك فحدث {وفيه إشارة إلى عظم شأن هذا الكتاب.
وتنشيط وترغيب للطالب على تحصيله.
وفي تسميته بالمنهاج، وإضافته إلى الوصول، إيماء إلى كونه طريقًا واضحًا موصلاً إلى علم أصول الفقه (على ما يتبين).
أن المنهاج والمنهج والنهج بنون مفتوحة وهاءٍ ساكنة: هو الطريق الواضح، كما قاله الجوهري.
قال: وتقول نهجت الطريق على وزن ضربت إذا أوضحته وبينته.
وحينئذ فتقول منه: أنا ناهج ومنهاج إذا أردت المبالغة.','1','265',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (74,'فيجوز أن يكون تسمية الكتاب بالمنهاج مأخوذًا من المعنى الأول.
قيل: فيكون شبه الكتاب بطريق واضح واسعٍ موصلٍ إلى المقصد بسهولة، فأطلق المشبه به وأراد المشبه فهو استعارة مصرحة.
ويجوز أن يكون من الثاني وكلاهما حسن.
وتقول أيضًا: نهجت بمعنى سلكت، وإرادة هذا المعنى بعيدة.
وتكلم الناس في "هذا"، الواقعة صدر الكلام.
فقيل: إنه وضع غير مشارٍ به ويشار به إذا وجد ما أريد من الإشارة، ورده الفارسي في التذكرة، واختار في الجواب: أن معناها التقريب، وتنزيله منزلة الحاضر.','1','266',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (75,'فلما تأكد عزم المصنف على تصنيف المنهاج عامله معاملة الموجود، فأشار إليه، وذلك لغة العرب.
قال الله تعالى: } هذا يوم الفصل {.
قال النووي: ومن المصنفين من يترك موضع الخطبة بياضًا فإذا فرغ ذكرها.
فأشار إلى حاضر لتكون عبارته في الخطبة موافقة لما ذكر فيحتمل الأمران في كلام المصنف.
وأصل الكتاب في اللغة الضم، فسمي كتابًا لضم حروفه ومسائله بعضها إلى بعض.
والكتاب اسم للمكتوب مجازًا، وهو من باب تسمية المفعول باسم المصدر، وهو كثير.
وهو في اصطلاح المصنفين: كالجنس المستقل الجامع لأبواب تلك الأبواب، أنواعه، وجمعه كتب بضم التاء وتسكن.','1','267',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (76,'والعلم: صفة توجب لمحلها تمييزًا لا يحتمل النقيض بوجه، وهذا يتناول التصور؛ إذ لا نقيض له، والتصديق اليقيني إذ له نقيض، ولا يحتمله، ويدخل فيه إدراك الحواس الخمس كالسمع والبصر.
وزاد بعضهم: في الحد قيدًا آخر، فقال: تمييزًا لها في الأمور المعنوية، فأخرج إدراك الحواس؛ لأن تمييزها في الأمور العينية الخارجية حيث رأى أنها ليست علمًا.','1','268',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (77,'وقيل: العلم إدراك المعلوم على ما هو به.
(فقوله: منهاج الوصول، يريد أن من قرأه أو فهمه وصل إلى علم الأصول).
ويجوز في قوله: "الجامع" الرفع صفة لمنهاج، أو خبر بعد خبر والجر صفة لعلم الأصول، أي جمع كتاب المنهاج أو علم أصول الفقه مسائل أصوليه، عقلية، ومسائل فروعية، أي نقلية.
وقوله: "المتوسط بين الأصول والفروع"، عطف على قوله: "الجامع".
يعني أنه متوسط بين الأصل المسمى بعلم الكلام وبين الفروع المسمى بعلم الفقه، فهو متوسط بين المعقول والمشروع؛','1','269',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (78,'لأن له حظا من الجانبين، تارة يأخذ من هذا وتارة يأخذ من ذاك.
والمعقول أصله: اسم للعقل كالميسور اسم لليسر وهو من جملة المصادر الواردة على مثال اسم المفعول.
وأصل الشرع: قال الرماني: العلم الظاهر، ومنه شرائع الإسلام وهو من الطريق: الشارع أي البارز الظاهر النير.
قال الجوهري: والشريعة ما شرع الله تعالى لعباده من الدين.
وسيجيء تفسير الأصول والفروع إن شاء الله تعالى.
(وقيل: والمراد بالمعقول، ما أدلته قطعية كالدالة على الوحدانية وثبوت الرسالة.','1','270',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (79,'والمراد بالمشروع ما أدلته ظنية، وهو الأحكام الشرعية.
وفي الأدلة الشرعية ما هو قطعي أيضًا.
لكن بانضمام الدليل العقلي إليه باعتبار النظر في مقدماته كالنظر في حال المعجزة).
وقوله: "إن صغر" بغير واو، شرط - جزاؤه "كبر" والجملة الشرطية خبر هو.
وفي أكثر النسخ بالواو فتكون الشرطية معترضة بين المبتدأ والخبر للتأكيد فلا تطلب الجزاء.
وقيل: الواو عطف على مقدر تقديره: إن لم يصغر حجمه وإن صغر حجمه كثر علمه، ففيه مبالغة في كثرة علمه، كذا قيل.
والظاهر قراءة "كبر" بالباء الموحدة لتقابل صغر، ولئلا يتكرر مع','1','271',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (80,'ما بعده.
قال في الصحاح: وكبر بالضم يكبر، أي عظم [يعظم].
قال: والصغر ضد الكبر.
فكأن المصنف - رحمه الله - تعالى - أشار إلى أن كبر معناه: متضمن لكثرة مسائله العظيمة.
ويوضحه قوله: وكثرت فوائده إلى آخره، والله أعلم.
والفوائد جمع فائدة، قال الجوهري: وهي ما استفدت من علم أو مال.
وقال الحلواني: الفائدة ما يستفاد من اللفظ، والفوائد بمعناه.
قال: والأحسن أن الفوائد ما يستفاد بالعقل؛ لأن الأصل عدم الترادف.','1','272',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (81,'ولهذا قال: كثرت فوائده؛ لأن الفوائد اللفظية يفهمها كل أحد.
وقال: جلت عوائده؛ لن ما يستفاد بالعقل قليل ولكنه عظيم شريف لا يفهمه كل أحد.
وقال الجوهري: العائدة: العطف والمنفعة، يقال: هذا الشيء أعود عليك من كذا أي أنفع.
وقال ابن دريد: العائدة: المعروف والصلة.
فعلى ما قالاه يكون معنى كلام المصنف في هذا الكتاب عطف للطالب ومنفعة ومعروف وصلة لإيضاحه وعذوبة لفظه وتحرير مسائله.
فقد عظمت عوائده على طالبيه.
وقال بعضهم: الفوائد هي القواعد: التي تترتب عليها الفوائد الكثيرة الفروعية.
والعوائد: المسائل الدقيقة والمباحث اللطيفة، فكأنها لغاية دقتها ولطفها تجب المعاودة إليها مرة أخرى.','1','273',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (82,'وقوله: "جمعته" استئناف، كأنه قيل: لم جمعت وضممت مسائل هذا الفن.
فقال: جمعت مسائل هذا الكتاب رجاء، أي لرجائي أن يكون سببًا لرشاد المستفيدين.
فرجاء: مفعول له علة لقوله: جمعته.
قال صاحب المحكم: الرشد والرشد والرشاد نقيض الغي.
وقال الهروي: إن معنى الثلاثة الهدى والاستقامة.
وأرشده إلى أمر هداه، أي جمعت مسائل هذا الكتاب رجاء كونه هداية إلى المقاصد والمطالب لمن هو بصدد استفادة أصول الفقه، وقد حقق الله - تعالى - رجاءه بمنه وكرمه ولم يرد بالسبب حقيقته.
وعطف قوله: "ونجاتي يوم الدين" على قوله: "لرشاد"، أي جمعته','1','274',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (83,'"لرجائي" أن يكون سببًا لنجاتي أي: خلاصي يوم القيامة، وهو يوم الجزاء، ووسيلة إلى الله تعالى ونفع الآخرة لا للدنيا لحقارتها.
والله تعالى حقيق بتحقيق رجاء الراجين استئناف أو حال.
ورجاء ممدود وأكثر ما يستعمل في الطمع أي: أنا رجوت من الله تعالى هذا المطلوب.
والحال أنه تعالى واسع جوده وعميم كرمه لائق به، وجدير له أن يحصل مرجوي ومرجو جميع المرتجين منه (ويعطيهم سؤالهم)، تقول هو حقيق بهذا الأمر أي جدير به، يعني أنه متصف بالقدرة عليه، فإنه الكريم الذي لا يخيب سائله، والجواد الذي لا يمنع المحتاجين نائله.
ففي الصحيحين من حديث أبي هريرة (رضي الله عنه): "أنا','1','275',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (84,'عند ظن عبدي بي".
زاد ابن حبان من حديث واثلة بن الأسقع: "فيظن بي ما شاء".
وظني فيه تعالى أن يعاملني وأحبائي بما يليق بكرمه ورحمته تعالى.
ووصله الله تعالى بحقيق لم ترد من جهة الشرع، فينبني على أن الأسماء توقيفية أم لا؟','1','276',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (85,'ومذهب الشيخ: الأول كذا قيل.
وروي أن بعض أهل البصرة رأى في المنام أنه سأل المصنف - رحمه الله تعالى - بعد وفاته كيف وجدت سكرات الموت؟
قال: ما وجدته، فإني كنت متأملاً في مسألة فوجدت نفسي في الجنة.
هذا رجائي يا أرحم الراحمين.','1','277',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (86,'أصول الفقه
مفرداته: الأصول، والفقه، (ولابد في معرفة المركب من معرفة مفرداته من حيث يصح تركيبها.
وأصول الفقه مركب إضافي دال على معنى، فلابد من معرفة مفرداته، أعنى هذين اللفظين) (الدالين على معنى) من حيث تصح الإضافة بينهما.
والأصل في اللغة: ما يبنى عليه الشيء.','1','278',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (87,'ويقال في الاصطلاح: للراجح وللمستصحب وللقاعدة الكلية وللدليل (وللصورة المقيس عليها)
والفقه سيجيء تعريفه.
فأصول الفقه: أدلة العلم من حيث هي أدلته، ونقل هذا المركب','1','279',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (88,'الإضافي الذي معناه أدلة الفقه من حيث هي أدلته، وجعل في العرف علمًا للعلم بالقواعد المذكورة، في قوله: معرفة دلائل الفقه إجمالاً، وكيفية الاستفادة منها وحال المستفيد.
فقوله: معرفة: جنس، والمراد بها الاعتقاد الجازم المطابق، أو الملكة التي هي مبدأ تفاصيل القواعد الثلاث.
والقواعد هي: القضايا الكلية التي تنطبق على جزئياتها عند تعرف أحكامها.','1','280',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (89,'وأدلة الفقه جمع مضاف، فيعم الأدلة المتفق عليها والمختلف فيها، فخرج معرفة غير الأدلة كالفقه، وأدلة غير الفقه كأدلة الكلام، ومعرفة بعض أدلة الفقه، فإنه جزء من أصول الفقه لا أصول الفقه.
والمراد من معرفة الأدلة أن يعلم أن الكتاب والسنة والإجماع والقياس أدلة يحتج بها وأن الأمر مثلاً للوجوب.','1','281',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (90,'ودلائل جمع دليل كما جمعوا وصيدًا على وصائد، وسليلاً على سلائل، حكاه أبو حيان في الارتشاف.
ويجوز أن يكون جمع دلالة، كرسائل ورسالة.
والمشهور أن جمع دليل هو أدلة، والدليل فعيل بمعنى فاعل من الدلالة وهي أعم من الإرشاد والهداية.
وهو في اصطلاح الأصوليين: ما يمكن التوصل بصحيح النظر فيه إلى مطلوب خبري.
وقوله إجمالاً: أي معرفة الأدلة من حيث الإجمال ككون','1','282',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (91,'الإجماع: حجة.
وقوله: وكيفية: عطف على دلائل أي ومعرفة كيفية استفادة الفقه من تلك الدلائل، أي استنباط الأحكام الشرعية منها، وذلك يرجع إلى معرفة شرائط الاستدلال كتقديم النص
على الظاهر، فلابد من معرفة تعارض الأدلة، ومعرفة الذي به الترجيح.','1','283',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (92,'وجعل من أصول الفقه، لأن المقصود من معرفة أدلة الفقه استنباط الأحكام، وهو متوقف على الترجيح بعد التعارض إذ هي ظنية، والمطلوب قابل للتعارض محتاج إلى الترجيح.
وقوله: وحال المستفيد: عطف على دلائل، أي ومعرفة حال المستفيد، وهو طالب حكم الله تعالى فيدخل المجتهد والمقلد إذ المجتهد يستفيد الأحكام من الأدلة، والمقلد منه.
وأشار بذلك إلى شرائط الاجتهاد وشرائط التقليد (كذا قيل).
وكان من أصول الفقه؛ لأن الأدلة الظنية ليس بينها وبين مدلولها ربط عقلي لجواز عدم دلالته عليه فاحتيج إلى رابط وهو','1','284',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (93,'الاجتهاد.
فائدة: كل علم له موضوع ومسائل.
فموضوعه هو ما يبحث في ذلك العلم عن الأحوال العارضة له:
ومسائله هي تلك الأحوال، فموضوع أصول الفقه هو الأدلة؛ لأنه يبحث فيه عن الأحوال العارضة لها من حيث دلالتها على الأحكام.
ومسائله: هي معرفة الأدلة باعتبار ما يعرض لها.
وهذا الأخير هو الواقع في الحد، لا الأول.','1','285',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (94,'قال العراقي ما حاصله: أن حمل المستفيد في كلام المصنف على المجتهد والمقلد ضعيف، فإن الفقه ليس موقوفًا على التقليد، ولا يسمى علم المقلد فقهًا، فليست معرفته من أصول الفقه، نعم، إذا علم المجتهد علم أن سواه مقلد، فمعرفته ليست مقصودة بل تحصل تبعًا.
(وأورد عليه: أن حده غير مانع لدخول التصور تحته.
وأورد أيضًا: أن تعبيره بالمعرفة يقتضي فقدان أصول الفقه عند فقدان العارف به، وليس كذلك.','1','286',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (95,'وأجيب عن الثاني بالتزامه، فقد جعل النبي - صلى الله عليه وسلم - ذهاب العلماء ذهابًا للعلم.
وأيضًا: الفقه كما يتفرع عن دليله، يتفرع عن العلم بدليله).
والفقه لغة: الفهم.
واصطلاحًا: العلم بالأحكام الشرعية العملية المكتسب من أدلتها','1','287',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (96,'التفصيلية.
فقوله: العلم جنس، وخرج بالأحكام العلم بالذوات والصفات والأفعال؛ لأن الأحكام هي القضايا، والنسب التامة.','1','288',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (97,'والمراد بالشرعية: المأخوذة من الشرع المبعوث به النبي - صلى الله عليه وسلم - وقوله العملية: أي المتعلقة بكيفية عمل قلبي أو غيره.
كالعلم بأن الوتر مندوب.
فخرج بالشرعية: العقلية، وبالعملية: العلمية أي الاعتقادية','1','289',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (98,'كالعلم بأن الله تعالى واحد، واللغوية، والحسابية.
والألف واللام في الأحكام يجوز أن تكون للجنس ولا يدخل المقلد لأن المراد بالعلم بالأحكام ما يقابل الظن.
وبالأدلة التفصيلية: الأمارات التي تفيد الظن','1','290',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (99,'وأن العمل بموجب الظن واجب قطعًا على المجتهد دون المقلد، لا بمعنى أن الفقه عبارة عن العلم (بموجب العلم) بل بمعنى أنه يجب عليه الجزم بوجوب ما دلت الأمارة على وجوبه، وحرمة ما دلت الأمارة على حرمته.
وهكذا فالمجتهد هو الذي يفضي به ظنه الحاصل من الأمارة إلى العلم بالأحكام بهذا المعنى، بخلاف المقلد فإن ظنه لا يصير وسيلة إلى العلم.
وفي هذا إشارة إلى جواب القاضي أبي بكر الآتي:
ويجوز أن تكون للاستغراق ولا يخرج بعض المجتهدين، إذا لم يحط','1','291',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (100,'بالكل؛ إذ المراد بالعلم بالجميع: التهيؤ له وهو أن يكون عنده ما يكفيه في استعلام الجميع من المآخذ والأسباب والشروط فيرجع إليه ويحكم، وعدم العلم في الحالة الراهنة لا ينافيه لجواز أن يكون ذلك لتعارض الأدلة، أو لعدم التمكن من الاجتهاد في الحال.
وإطلاق العلم على مثل هذا شائع في العرف، وهذا ما يقال: إن العلم عبارة عن ملكة يقتدر بها على إدراكات جزئية.
وقوله: المكتسب: صفة للعلم، وأخرج به علم الله تعالى والنبي وجبريل عليهما السلام.','1','292',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (101,'وقوله التفصيلية: خرج به العلم بذلك المكتسب للجدل من المقتضى والنافي المثبت بهما، ما يأخذه من الفقه ليحفظه عن إبطال خصمه، وكذا علم المقلد.
وتعبير المصنف في تعريف الأصول بالمعرفة، وفي الفقه بالعلم، كأنه حاول به التنبيه على أن المراد بالمعرفة والعلم واحد لا كما اصطلح عليه البعض من التفرقة بينهما.','1','293',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (102,'قال الجوهري: علمت الشيء أعلمه علمًا: عرفته.
قال العراقي: وقد وقع إطلاق المعرفة على الله تعالى في كلام النبي - صلى الله عليه وسلم - وأقوال الصحابة، وكلام أهل اللغة، ثم بسطه.
ثم أورد المصنف على حد الفقه إشكالاً فقال:
وقيل: الفقه من باب الظنون؛ لأنه مستفاد من الأدلة السمعية','1','294',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (103,'فيكون مظنونًا؛ لأن الأدلة السمعية كيفما كانت لا تفيد إلا الظن.
وما كان مقطوعًا منها فمن ضروريات الدين فليس بفقه، فالفقه إذًا مظنون فلا يصح أن يقال: الفقه العلم بالأحكام بل الظن.
قلنا جوابًا عن هذا الإشكال: لا نسلم كون الفقه ظنيًا بل هو قطعي؛ لأن المجتهد إذا ظن الحكم حصل له مقدمة قطعية، مثلاً:','1','295',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (104,'إذا غلب على ظنه انتقاض الوضوء بالمس حصل له مقدمة قطعية، وهي: انتقاض الوضوء مظنون، وأشار إليها بقوله: إذا ظن، ولنا مقدمة أخرى قطعية، وهي كل مظنون يجب عليه العمل به.
وأشار إليها بقوله: وجب عليه الفتوى والعمل به، فينتج انتقاض الوضوء يجب عليه العمل به، وهذه النتيجة قطعية؛ لأن المقدمتين قطعيتان.
الأولى وجدانية والثانية للدليل القاطع على وجوب إتباع الظن. فالحكم مقطوع به.','1','296',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (105,'وقد اختلف في الدليل القاطع.
فقيل: هو الإجماع على أن كل مجتهد يجب عليه العمل والإفتاء بما ظنه.
قيل: وفيه نظر: فإن الإجماع ظني.
وقيل: هو الدليل العقلي، لأن الظن هو الطرف الراجح من الاحتمالات فالطرف المقابل له مرجوح، والعمل بهما، يلزم منه اجتماع النقيضين، وتركهما بالعكس، والعمل بالمرجوح خلاف العقل، فتعين العمل بالراجح. وهو الظن.
وفيه نظر: فإنه إنما يجب العمل به، أو بنقيضه، إذا ثبت بدليل قاطع أن كل فعل يجب أن يتعلق به حكم شرعي.
وليس كذلك، فيجوز أن يكون عدم وجوبه بسبب عدم الحكم الشرعي، فيبقى الفعل على البراءة الأصلية كحاله قبل الاجتهاد، وكحاله','1','297',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (106,'عند الشك.
وأجيب عنه: بأنه لابد من دليل قاطع على إتباع الظن دفعًا للتسلسل أو إثبات الظن بنفسه.
وذلك القاطع إما إجماع وحده، وإما مع قرائن تحف به.
وقد مر (تحقيق جواب الشبهة).
وقوله: "والظن في طريقه" أشار إلى أن الظن، وإن كان واقعًا في المقدمتين فإنه قد وقع التصريح به في محمول الصغرى، وموضوع الكبرى، فلا يضر في قطعيتهما؛ لأن المعتبر في كون المقدمة قطعية أو ظنية إنما هو النسبة الحاصلة فيها سواء كان الطرفان قطعيين أو ظنيين، أو أحدهما وقد تقدم أن النسبتين قطعيتان.','1','298',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (107,'(وأورد على الحد أيضًا: أنه غير مانع لدخول التصور تحته؛ لأن العلم ينقسم إلى تصور وتصديق، ولم يأت بعد ذلك بما يخرجه والفقه تصديق لا تصور.
وأجيب: بأن التصور خرج بقوله: "بالأحكام" فإن الحكم هو النسبة الخبرية.
قيل: وإن أراد المصنف بالحكم هنا: إنشاء الأمر والنهي والتخيير كما هو الموافق لتعريفه له بالخطاب. وهذا قد يتعلق به العلم على وجه التصور، فيخرج التصور بقوله: "المكتسب من أدلتها التفصيلية" فإن التصور مكتسب من التعريفات لا من الأدلة.
وأورد أيضًا: أن الحكم يطلق على النسبة الخبرية، وعلى إنشاء الأمر','1','299',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (108,'ونحوه، ووقوع المشترك في التعريف ممتنع.
وأجيب بجوازه إذا دلت قرينة على المراد، كما صرح به الغزالي في مقدمة المستصفى.
والقرينة هنا موجودة، فإن العلم متعد إلى مفعولين ولا يجوز دخول الباء على مفعوله إلا إذا تضمن نسبة بنفي أو إثبات، فلما دخلت الباء مع لفظ "العلم" الذي ظاهره التعدي إلى مفعولين مع لفظ "الحكم" الذي هو ظاهر في النسبة كان ذلك قرينة في أن المراد بالأحكام ثبوتها لا تصورها.
وأورد أيضًا: أن لفظ الفقه بهذا التعريف لا يتناول إلا علم المجتهد. فمقتضاه:','1','300',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (109,'أنه إذا وقف على الفقهاء يختص به المجتهدون، وليس كذلك.
وأجيب: بأن هذا اصطلاح خاص فلا يلتفت إليه في الألفاظ فإن المرجع فيها إلى اللغة والعرف العام.
وإلى هذا أشار المتولى بقوله: إنه "يرجع فيه إلى العادة".','1','301',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (110,'ونظر في جواب المصنف عن شبهة القاضي فإن النزاع في أن الحكم الغالب على ظن المجتهد هل هو مقطوع به، ولم يقم الدليل عليه، وإنما أقامه على القطع بوجوب العمل بالحكم المظنون، ولا يلزم منه إذًا قطع بالحكم، فإن سلم فلا يدل على أنه معلوم؛ إذ العلم أخص من القطع، إذ المقلد قاطع وليس بعالم.
وقد يقال: إذا كان وجوب العمل بدليل قاطع، فإنه يلزم منه العلم، كما مر) ودليله أي دليل الفقه المتفق عليه بين الأئمة الأربعة الكتاب والسنة والإجماع والقياس.
والمختلف فيه الاستصحاب والاستحسان والأخذ بالأقل وغيره مما سيجيء إن شاء الله تعالى.
والمقصود من هذه الأدلة استنباط الأحكام الخمسة ليتمكن من','1','302',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (111,'إثباتها ونفيها.
والحكم على الشيء فرع عن تصوره فاحتاج الأصولي إلى تصور الأحكام، فلذا قال ولابد للأصولي من تصور الأحكام ليتمكن من إثباتها ونفيها.
لا جرم: أي لأجل ذلك رتبناه، أي المنهاج، على مقدمة - وهي بكسر الدال - مثل اسم مقدمة الجيش، أي للجماعة المتقدمة منه، من قدم اللازم بمعنى تقدم - وبفتحها - اسم لمقدمة الرجل في لغة: من قدم المتعدى.
ومقدمة الكتاب على ما يذكر فيه قبل الشروع في المقاصد، لارتباطها به.
وسبعة كتب في المقصود بالذات، خمسة في مباحث أدلة الفقه،','1','303',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (112,'وهي الكتاب والسنة والإجماع والقياس والأدلة المختلف فيها، والكتاب السادس في التعادل والتراجيح بين هذه الأدلة عند تعارضها،','1','304',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (113,'والسابع في الاجتهاد الرابط لها بمدلولها، وما يتبعه من التقليد وأحكام المقلدين.
تنبيه: قوله: لا جرم: إما متفرع على تعريف أصول الفقه، أي لأجل أن أصول الفقه لما كان عبارة عن المعارف الثلاث.
أو على قوله: ودليله المتفق عليه - إلى آخره (كذا قيل.
والأولى: أن يكون متفرعًا على تعريف أصول الفقه، وعلى قوله: ولابد للأصولي من تصور الأحكام).
قال الفراء: معنى لا جرم في الأصل، لابد ولا محالة، ثم استعمل بمعنى حقًا، فيجرى مجرى القسم.','1','305',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (114,'ويجاب باللام، فيقال: لا جرم لأفعلن لكنه لم يجرها هنا مجرى القسم، بل قصد به مجرد التأكيد.
كأنه قيل: رتبنا هذا الكتاب على كذا، ولابد من هذا الترتيب.
وقوله رتبناه: يصلح للفاعلية على قول الكوفيين في مجيء الفاعل جملة.
وعلى قول ابن مالك في مجيء الفاعل مؤولاً بالمصدر، وإن لم تكن معه أن.','1','306',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (115,'(أما المقدمة: ففي الأحكام ومتعلقاتها أي في بيان مفهوماتها الشرعية وأقسامها وأحكامها، ومعرفة الحاكم والمحكوم عليه وبه).
وفيها بابان:
* * *','1','307',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (116,'<span data-type="title" id=toc-2>الباب الأول في الحكم</span>
وفيه فصول

<span data-type="title" id=toc-3>الفصل الأول في تعريفه</span>
الحكم: خطاب الله - تعالى - المتعلق بأفعال المكلفين بالاقتضاء أو التخيير.
فالخطاب توجيه الكلام نحو الغير للإفهام.','1','311',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (117,'والمراد هنا بخطاب (الله تعالى) - هو ما أفاد، وهو الكلام النفسي الأزلي،','1','312',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (118,'المسمى في الأزل خطابًا على الراجح وبإضافته إلى الله - تعالى - أخرج خطاب من سواه إذ لا حكم إلا حكمه.','1','313',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (119,'والرسول والسيد إنما وجب طاعتهما، بإيجاب الله - تعالى - إياها.
وقوله: ((المتعلق بأفعال الكلفين)) أي البالغين العاقلين تعلقًا معنويًا قبل وجوده، وتنجيزيًا بعد وجوده بعد البعثة إذ لا حكم قبلها.
وخرج بفعل المكلفين: خطاب الله تعالى المتعلق بذاته وصفاته وذوات المكلفين والجمادات.
وقوله: ((المتعلق بأفعال المكلفين)) لا يمكن حمله على ظاهره قطعًا؛','1','314',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (120,'لأنه حينئذ لا يتناول شيئًا من الأحكام، إذ لا يصدق على حكم ما أنه خطاب متعلق بجميع أفعال المكلفين.
فالمراد تعلقه بفعل منها، وحينئذ يتناول ما لا يعم كخواص النبي - صلى الله عليه وسلم.
وقوله: ((بالاقتضاء)) أي طلب الفعل وطلب الترك، جازمًا أو غير','1','315',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (121,'جازم.
فطلب الفعل الجازم الايجاب، وغير الجازم الندب، وطلب الترك الجازم التحريم، وغير الجازم الكراهة، والتخيير الإباحة.
فدخلت الأحكام الخمسة وأخرج بذلك مثل قول تعالى: {والله خلقكم وما تعملون}.
إذ ليس فيه اقتضاء ولا تخيير، إنما هو أخبار بحال له.
ولا خطاب يتعلق بفعل غير البالغ العاقل.
وولي الصبي والمجنون مخاطب بأداء ما وجب في مالها منه كالزكاة وضمان المتلف، كما يخاطب صاحب البهيمة بضمان ما أتلفته','1','316',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (122,'حيث فرط في حفظها لتنزل فعلها في هذه الحالة منزلة فعله.
وصحة عبادة الصبي كصلاته وصومه المثاب عليها، ليس لأنه مأمور بها كما في البالغ، بل ليعتادها فلا يتركها بعد بلوغه.
واختار السبكي في حد الحكم: أنه خطاب الله تعالى المتعلق بأفعال المكلفين على وجه الإنشاء، ليندرج تحته خطاب الوضع، وهو كون الشيء سببًا أو شرطًا أو مانعًا، والحكم بالصحة والفساد.
فإن قلت: الفقه - على ما تقدم - هو العلم بالأحكام الشرعية المكتسب من الأدلة.
والدليل الشرعي: ليس إلا خطاب الله تعالى، وإذا كان الحكم أيضًا: خطاب الله - تعالى - فيصير حاصله الفقه: العلم بخطاب الله -','1','317',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (123,'تعالى - الحاصل عن خطاب الله -تعالى- أجيب: بأن المراد بالفقه: العلم بالخطاب النفساني الذي هو قديم، الحاصل عن الخطاب اللفظي الذي هو دليل.
أو كأنه قيل: علم بالوحي الحاصل عن إيجاب الله تعالى.
وقالت المعتزلة اعتراضًا على هذا الحد: خطاب الله - تعالى - قديم عندكم؛ لأنه كلامه، ومذهبكم - أهل السنة - أن الكلام قديم.
والحكم حادث؛ لأنه يوصف به، أي بالحدوث.','1','318',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (124,'كقولنا: حلت المرأة بعدما لم تكن حلالًا، فالحل من الأحكام الشرعية، وقد وصف بأنه لم يكن، وكان، (وكل ما) لم يكن وكان فهو حادث.
ويكون الحكم أيضًا صفة لفعل العبد، كقولنا: هذا وطء حلال، فجعل الحل صفة للوطء الذي هو فعل العبد، وفعل العبد حادث، وصفة الحادث أولى بالحدوث؛ لأنها إما مقارنة للموصوف، أو متأخرة عنه.
ويكون الحكم معللًا به أي بفعل العبد، كقولنا: حلت المرأة بالنكاح، وحرمت بالطلاق، فالنكاح علة للإباحة، والطلاق','1','319',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (125,'علة للتحريم، والنكاح والطلاق حادثان، إذ النكاح هو: الإيجاب والقبول، والطلاق قول الزوج: طلقت، فالمعلل بهما حادث بطريق الأولى؛ لأن المعلول إما مقارن لعلته، أو متأخر عنها.
فإذا كان الخطاب قديمًا، والحكم حادثًا، فتعريف الحكم الحادث بالخطاب القديم، بمقتضى أن يكون القديم حادثًا، أو الحادث قديمًا، وفساده واضحًا.
واعترضوا أيضًا:
بأن الحد غير جامع لأفراد المحدود؛ لأن أحكام الوضع ليست داخلة فيه، وأشار إليه بقوله:','1','320',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (126,'فموجبية الدلوك، وما نعيه النجاسة، وصحة البيع وفساده خارجة عنه أي عن الحد؛ لأن جعل الشيء سببًا كجعل دلوك الشمس، أي زوالها سببًا للصلاة، والزنا سببًا لوجوب الحد، أو شرطًا كطهارة المبيع لصحة البيع، ومانعية النجاسة لصحة الصلاة، لا اقتضاء فيها ولا تخيير، بل هي من وضع الشارع وتحصل بجعله.
وكذا الصحة والفساد حكمان شرعيان لأنا استفدناهما من الشارع، وليس فيهما طلب ولا تخيير. واعترضوا أيضًا:
بأن فيه أي في الحد الترديد؛ لأن فيه: أو، وهي موضوعة للترديد، أي للشك، وهو: أي الترديد ينافي التحديد.
لأن المقصود من الحد التعريف فتنافيا.
قلنا جوابًا عن الأول:','1','321',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (127,'الحادث التعلق، لا الحكم فإنه قديم إذ هو مثلًا:
قول الله - تعالى - في الأزل: أذنت لفلان أن يطأ فلانة إذا جرى بينهما نكاح.
فالحل قديم وتعلقه حصل بعد ما لم يكن، فالحادث التعلق ولا يلزم من حدوثه حدوث المنتسبين معًا، لجواز تعلق الخطاب القديم بالمعدوم، كما سيجيء.
والحكم متعلق بفعل العبد لا صفته، ويجوز أن يكون القديم متعلقًا بالحادث؛ إذ لا امتناع في تعلق المتقدم بالمتأخر كالقول المتعلق بالمعدومات، فيكون الحكم القديم متعلقًا بفعل العبد الحادث.
ولا يلزم من كون القول متعلقًا بشيء، أن يكون صفة لذلك الشيء، كالقول المتعلق بالمعدوم، والنكاح والطلاق اللذان هما فعل العبد ونحوهما من فعله معرفات له، أي للحكم لا علل له.','1','322',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (128,'ويجوز أن يكون الحادث معرفًا للقديم كالعالم - بفتح اللام - وهو جميع المخلوقات فإنه معرف للصانع القديم.
والموجبيةوالمانعية ليست أحكامًا حتى يجب دخولها في الحد، وإن سماها غيرنا به، بل هي أعلام للحكم لا هو، وإن سلم أنها أحكام فليست خارجة عن الحد؛ لأن خطاب الوضع يرجع إلى الاقتضاء والتخيير.
إذ معنى جعل الزنا سببًا لوجوب الحد، هو وجوب الحد عند الزنا.
وجعل الطهارة شرطًا لصحة البيع جواز الانتفاع بالمبيع.','1','323',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (129,'عندها وحرمته دونها. وعليه فقس.
والحاصل أن مرادنا من الاقتضاء والتخيير أعم من الصريح والضمني، وخطاب من قبيل الضمني.
وإليه أشار بقوله: ((فالمعني بهما اقتضاء الفعل والترك)).
(وأورد أن في دلوك الشمس ثلاثة أمور: وجوب الظهر، ولا نزاع في أنه حكم شرعي، ودلوك الشمس، ولا نزاع في أنه ليس بحكم شرعي، وكون الدلوك موجبًا للظهر، وهو محل النزاع فكيف','1','324',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (130,'يصح؟
وأجيب: أن المراد بأن هذه الأمور قطعية لا ظنية.
والترديد في أقسام المحدود لا في الحد، فلا ينافي التحديد، لأنه لا ترديد في الحد؛ (لأن الترديد إنما هو في أحدهما معينًا، وهو أخص من أحدهما مطلقًا، ولم يقع في أحدهما مطلقًا ترديد، وهو الواقع في الحد، إنما الترديد في الاقتضاء والتخيير اللذين هما من أقسام المحدود، الذي هو الحكم.','1','325',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (131,'<span data-type="title" id=toc-5>الفصل الثاني: في تقسيماته: </span>أي تقسيمات الحكم.
وهو ينقسم باعتبارات مختلفة إلى تقسيمات ستة:
وقد علمت أن الحكم نفس خطاب الله تعالى، فالإيجاب هو نفس قوله: افعل، وليس للفعل منه صفة حقيقية، فإن القول ليس لتعلقه منه صفة لتعلقه بالمعدوم كما مر.
فإذًا الخطاب صفة للحاكم ومتعلق بفعل المكلف باعتبار إضافته إلى الحاكم، يسمى إيجابًا، وإلى الفعل وجوبًا.
والحقيقة واحدة، والتغير اعتباري فلذلك تراهم يجعلون أقسام الحكم الوجوب والحرمة مرة، والإيجاب والتحريم أخرى، وتارة','1','326',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (132,'الوجوب والتحريم.

<span data-type="title" id=toc-6>الأول: من التقسيمات الستة:</span>
الخطاب إن اقتضى الوجود ومنع النقيض فوجوب.
لما قدم أن الحكم هو: الخطاب، صح التقسيم في الخطاب، وقرن الخطاب باللام لإفادة المعهود السابق، وهو خطاب الله تعالى.
فخطاب الله تعالى، إن اقتضى أي طلب وجود الفعل، ومنع من نقيضه هو الترك فوجوب.
وإن اقتضى الوجود ولم يمنع من الترك فندب.','1','327',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (133,'وإن اقتضى الترك أي ترك الفعل ومنع من النقيض وهو الإتيان به فحرمة، وإلا فكراهة أي: وإن اقتضى الترك ولم يمنع من الإتيان به فكراهة.
وزاد إمام الحرمين: خلاف الأولى فقال: الخطاب إن اقتضى الترك دون جزم فإن كان بنهى مخصوص (فالكراهة أو غير مخصوص) فخلاف الأولى.','1','328',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (134,'وإن كان الخطاب لا يقتضي شيئًا بل خيرنا بين الإتيان والترك فإباحة وإليه أشار بقوله: وإن خيرنا فإباحة.
فحد الوجوب مثلًا طلب الفعل مع المنع من الترك، وأمثلة الباقي','1','329',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (135,'لا تخفى.
ويرسم الواجب أي بأنه الذي يذم شرعًا تاركه قصدًا مطلقًا.
الرسم التعريف بالجنس والخاصة فالواجب الفعل المتعلق بالوجوب.','1','330',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (136,'وقوله: بأنه الذي، مراده به الفعل، فهو كالجنس دخل فيه الأحكام الخمسة.
والمراد بالذم شرعًا: نص الشارع به أو بدليله، وذلك أنه لا وجوب إلا بالشرع.
ومعنى الذم على تركه أنه على حالة لو تركه لكان مستنقصًا وملامًا، بحيث ينتهي الاستنقاصواللوم إلى حد يصلح لترتيب العقاب عليه.
واحتزر به عن المندوب والمكروه والمباح؛ إذ لا ذم فيهما.
وقوله: ((تاركه)) أخرج به الحرام فإنه يذم فاعله.','1','331',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (137,'وقوله: قصدًا، أي هو الذي بحيث لو ترك قصدًا لذم؛ إذ التارك لا على سبيل القصد لا يذم.
ويجوز أنه احترز به عما إذا مضى من الوقت مقدار يتمكن فيه من إيقاع الصلاة، ثم تركها بنوم أو نسيان (أو موت) فإن هذه الصلاة واجبة، ومع ذلك لم يذم شرعًا تاركها لأنه ما تركها؛ قصدًا فأتى بهذا القيد لإدخال هذا الواجب في الحد، فيصير به جامعًا.','1','332',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (138,'وقوله: ((مطلقًا)) ليدخل من الواجبات ما لا يذم تاركه كيفما تركه، بل بوجه دون وجه: وهو الموسع فإنه يذم تاركه إذا تركه في جميع وقته ولو تركه في بعض الوقت وفعله في بعض لا يذم.
وكذا فرض الكفاية فإنه يذم تاركه إذا لم يقم به غيره في ظنه.','1','333',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (139,'وكذا المخير إذا قلنا: كل واحد واجب، فإنه يذم تاركه إذا ترك معه الآخر. إذا عرفت ذلك فالواجب يرادفه الفرض عند الجمهور.
وقالت الحنفية: الفرض ما ثبت من التكليف بقطعي كالصلوات الخمس.','1','334',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (140,'والواجب: ما ثبت بظني كوجوب الوتر.
والنزاع لفظي، فنحن نجعل اللفظين اسمًا لمعنى واحد تتفاوت أفراده.
وهم يخصون كلًا منهما بقسم من ذلك المعنى ويجعلونه اسمًا له.','1','335',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (141,'وقد يتوهم من جعلهما مترادفين أن خبر الواحد ((الظني))، بل القياس المبني عليه في مرتبة الكتاب القطعي حيث جعل مدلولهما واحدًا وهو غلط ظاهر.
وأورد أنه قد فرق أصحابنا بين الفرض والواجب في الحج.','1','336',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (142,'حيث قالوا: الواجب ما يجبر بالدم والركن ما لا يجبر به.
والفرض يشملها، فيكون الفرض في هذا الموضع أعم من الواجب.
وأيضًا: إذا قال: الطلاق واجب علي طلقت زوجته بخلاف ما إذا قال: فرض علي على رأي.
وأجيب بأن الكلام هنا: في الأسماء الشرعية، فالمراد يترادف الفرض والواجب أنهما جميعًا يطلقان في الشرع على ما يمدح فاعله ويذم تاركه، والطلاق مبناه على وضع اللغة أو عرف الناس.','1','337',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (143,'والتحقيق: أن الفرض له إطلاقان: منها ما يأثم تاركه، وهو مرادف الواجب. ومنها ما لابد منه وهو الركن.
والواجب له إطلاقان: ما يمدح فاعله، ويذم تاركه، وهو مرادف للفرض بالمعنى الأول. وهو ما يقابل الركن).
والمندوب أي: الفعل الذي يتعلق به الندب.
وهو لغة: المدعو إليه فسمى الفعل بذلك لدعاء الشارع إليه وأصله المندوب إليه، ثم توسع بحذف حرف الجر فاستكن الضمير.
واصطلاحًا: ما يمدح فاعله ولا يذم تاركه.
فقوله يمدح: أخرج به المباح؛ إذ لا مدح فيه ولا ذم.','1','338',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (144,'وقوله: «فاعله» أخرج به الحرام والمكروه، إذ يمدح تاركهما.
والمراد بالفعل هنا هو الصادر من الشخص؛ ليعم الفعل المعروف والقول نفسانيًّا أو لسانيًّا فتدخل الأذكار القلبية واللسانية.
وقوله: ولا يذم تاركه، أخرج الواجب بجميع أقسامه فإن تاركه يذم في الجملة؛ لأنه نكرة في سياق النفي فيعم.
(والمحققون من النحاة على أن المراد بتنكير الجملة أن المفرد الذي يسبك منها نكرة في عموم الفعل المنفي ليس من جهة تنكيره، بل من جهة أن ما يتضمنه من المصدر نكرة، فمعنى ولا يذم تاركه، لا يكون ذم على تاركه).','1','339',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (145,'والمندوب يسمى: سنة ونافلة ومستحبًّا وتطوعًا ومرغوبًا فيه ألفاظ مترادفة.
وخالف في ذلك القاضي حسين والبغوي.','1','340',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (146,'والخوارزمي وقالوا: السنة ما واظب عليه النبي- صلى الله عليه وسلم-.
والمستحب: ما فعله مرة أو مرتين.
والتطوع: ما ينشئه الإنسان باختياره من الأغراض.
ولم يتعرضوا للمندوب؛ لشموله للأقسام الثلاثة كما هو الظاهر، والخلاف لفظي عائد إلى اللفظ والتسمية.','1','341',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (147,'والحرام ما أي فعل يذم (شرعًا فاعله)','1','342',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (148,'(فبقوله يذم: أخرج المكروه والمندوب والمباح.
وقوله: «شرعًا» إشارة إلى أن الذم لا يكون إلا بالشرع كما مر، خلافًا للمعتزلة.
وقوله: فاعله أخرج به الواجب.
والمراد بالفعل: الشيء الصادر من الفاعل ليعم الغيبة والنميمة من الأقوال والحسد والحقد ونحوهما من أعمال القلب.','1','343',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (149,'ويسمى الحرام معصية، وذنبًا، وقبيحًا، ومزجورًا عنه، ومتوعدًا عليه، أي من الشرع.
والمكروه ما أي فعل يمدح تاركه ولا يذم فاعله.
فـ «يمدح» أخرج المباح، و «تاركه» أخرج الواجب والمندوب.
«ولا يذم فاعله» أخرج الحرام.
والمباح لغة: الموسع فيه.','1','344',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (150,'واصطلاحًا: ما أي فعل لا يتعلق بفعله ولا تركه مدح ولا ذم، فخرج الأربعة وهو ظاهر، ويسمى المباح طلقًا وحلالًا.
الثاني: ما نهى عنه شرعًا فقبيح وإلا فحسن كالواجب والمندوب','1','345',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (151,'والمباح وفعل غير المكلف.','1','346',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (152,'هذا تقسيم ثان للفعل الذي تعلق به الحكم، وهو مستلزم لتقسيم الحكم.
فالفعل إن نهى الشارع عنه فهو القبيح، وتحته قسمان: المحرم والمكروه.
وإن لم ينه عنه فهو الحسن، فيدخل فيه أفعال المكلفين من الواجب والمندوب والمباح، وأفعال غيرهم كالساهي والصبي والنائم، وأفعال الله تعالى.
واعلم: أنه يجوز أن يكونه قسم الشيء أعم من مقسمه.','1','347',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (153,'كما يقال: العالم إما واجب أو ممكن، والممكن أعم من العالم (من وجه).
فكذا هنا المقسم فعل هو متعلق الحكم الشرعي، وقسمه الحسن.
وقال إمام الحرمين: المكروه ليس بحسن ولا قبيح.
فإن القبيح ما يذم عليه، والحسن ما يسوغ الثناء عليه، وهذا لا يسوغ الثناء عليه.
قال السبكي: ولم نر أحدًا نعتمده خالف إمام الحرمين فيما قال إلا ناسًا أدركناهم، قالوا: إنه قبيح؛ لأنه منهي عنه، والنهي أعم من نهي','1','348',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (154,'تحريم وتنزيه.
وكذا اختار إمام الحرمين: امتناع إطلاق الحسن على فعل غير المكلف.
(واعلم أن مراد المصنف بالمباح: هو ما استوى طرفاه في نظر الشارع، وهو ما حده أولًا بقوله: «ما لا يتعلق بفعله وتركه مدح ولا ذم».
ولا يرد عليه فعل غير المكلف؛ لأنه ما دخل في جنسه، إذ المراد بـ «ما» «فعل المكلف».','1','349',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (155,'ولو أراد «المباح» بالمعنى الأعم لم يمتنع عطف غير المكلف عليه؛ لأنه يكون من عطف الخاص على العام).
والمعتزلة قالوا: أي القبيح: ما ليس للقادر عليه العالم بحاله أي بصفته من المفسدة الداعية إلى تركه (أن يفعله).
والحسن: ماله أي: للقادر عليه العالم بصفته أن يفعله.
فالقبيح الحرام فقط، والحسن يشمل الواجب والمندوب والمكروه، والمباح وفعل الله تعالى.
ويؤخذ منه أن الفعل إذا لم يقدر عليه كالعاجز عن الشيء والملجأ إليه، فإنه لا يوصف عندهم بحسن ولا قبح وكذا ما لم يعلم حاله كفعل الساهي والنائم.','1','350',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (156,'وربما قالوا- أي: المعتزلة- في تعريف الحسن والقبيح: الواقع على صفة توجب الذم، فالقبيح، وهو الحرام فقط، أو على صفة توجب المدح فالحسن.
فدخل فيه الواجب والمندوب دون المباح، والمكروه.
إذ لا مدح في فعلهما، مع أنهما دخلا في حدهم الأول، للحسن؛ لأن القادر عليهما العالم بحالهما له أن يفعلهما.
فالحسن بتفسيرهم- أي المعتزلة- الأخير أخص منه بتفسيرهم أولًا.
الثالث: قيل:','1','351',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (157,'الحكم إما سبب أو مسبب كجعل الزنا سببًا لإيجاب الجلد على الزاني.
وهذا تقسيم ثالث للحكم باعتبار صفة عارضة، وهي كونه علة ومعلولًا.','1','352',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (158,'فإن أريد بالسببية أي بجعل الزنا سببًا لإيجاب الحد الإعلام بالحكم ومعرفًا له، فحق، إذ يجوز أن يقول الشارع متى رأيت إنسانًا يزني فاعلم أني أوجبت عليه الحد.
وتسميتها حكمًا بحث لفظي يرجع إلى الاصطلاح كما مر.
وإن أريد بها- أي: بالسببية- التأثير على معنى أن الله تعالى جعل الزنا مؤثرًا في إيجاب الحد فباطل، لأن الحادث وهو الزنا لا يؤثر في القديم وهو إيجاب الحد؛ إذ قد ثبت بالدليل قدم الحكم والحادث','1','353',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (159,'لا يؤثر في القديم؛ لأن تأثيره فيه يستدعي تأخر وجوده عنه أو مقارنته له.
ولأنه- أي: القول- بالتأثير مبني على أن للفعل جهات توجب الحسن والقبح وهو باطل.
وذلك لأن الزنا مثلًا لو كان مؤثرًا في إيجاب الحد، فإما أن يكون لكونه فعلًا، أو لكونه فعلًا مشتملًا على خصوصية ليست كسائر الأفعال لا سبيل إلى الأول، وإلا يلزم الترجيح بلا مرجح لكون الأفعال متساوية في كونها فعلًا ولا إلى الثاني، لأن كون الفعل مشتملًا على خصوصية ليست كسائر الأفعال لأجلها صار مؤثرًا ومبني على أن للفعل جهات توجب الحسن والقبح.
(قيل: قوله سبب أو مسبب فيه نظر؛ لأن السبب هو نفس','1','354',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (160,'الزنا، فليس حكمًا، وإنما الحكم جعله سببًا فهو سببي).
الرابع: الصحة: استتباع الغاية وبإزائها البطلان والفساد.
هذا تقسيم رابع للحكم باعتبار اجتماع الشروط المعتبرة في الفعل وعدم اجتماعها فيه سواء كان عبادة أو معاملة.
وغاية الشيء هو الأثر المقصود منه كحل الانتفاع بالمبيع مثلًا','1','355',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (161,'والاستمتاع في النكاح.
فإن ترتبت الغاية على الفعل وتبعته في الوجود كان صحيحًا فاستتباع الغاية طلب الفعل لتبعية غايته وترتب وجودها على وجوده؛ لأن السين للطلب.
وكأنه جعل الفعل الصحيح طالبًا ومقتضيًا لترتب أثره عليه مجازًا.
وأورد عليه أنه غير منعكس، لخروج المبيع قبل القبض في مدة الخيار إذا كان للبائع، فإنه صحيح، مع أنه لم يترتب عليه أثره وهو','1','356',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (162,'حل الانتفاع.
وغير مطرد لدخول الخلع والكتابة الفاسدين فإنه يترتب عليهما أثرهما من البينونة والعتق مع أنهما غير صحيحين.','1','357',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (163,'وأجيب عن الأول:
بأن المراد كونه متهيئًا لتبعية غايته له، ولا شك أن المبيع قبل القبض كذلك، وإن توقف على شرط.
(وقد يجاب عن الثاني بأن المراد ترتب أثره عليه من كل الوجوه.
ومقابل الصحة: البطلان والفساد، لفظان مترادفان، فإن معناهما كون الشيء لم يستتبع غايته) وفي أبواب في الفقه فرقوا بين الفاسد والباطل.
(والتحقيق أن الفاسد له إطلاقان:','1','358',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (164,'أحدهما: ما لا يترتب أثره عليه، وهذا هو المرادف للباطل وهما يقابلان الصحة.
الثاني: ما لا يترتب عليه أثره من كل الوجوه، بل من بعض الوجوه.
وهذا لا يرادف الباطل، وإن كان مقابلًا للصحيح).
وغاية المعاملات ترتب أثرها عليها كما أشار إليه بقوله: والمعني بالصحة إباحة الانتفاع (أي صحتها).
وغاية العبادة موافقة الأمر عند المتكلمين، وسقوط القضاء لدى الفقهاء.
- وفائدة الخلاف فيمن صلى بظن الطهارة ثم تبين له الحدث','1','359',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (165,'فتسمى هذه الصلاة صحيحة عند المتكلمين لموافقة الأمر.
غير صحيحة عند الفقهاء، لعدم سقوط القضاء؛ إذ لا خلاف في وجوب القضاء.
(وإليه أشار بقوله: وصلاة من ظن أنه متطهر صحيحة على الأول لا على الثاني).
وقال السبكي: تسمية الفقهاء هذه الصلاة باطلة ليس لاعتبارهم سقوط القضاء في حد الصحة كما ظنه الأصوليون، بل لأن شرط الصلاة الطهارة في نفس الأمر والصلاة بدون شرطها فاسد وغير مأمور بها.
ثم استدل على هذا: بأن الفقهاء يقولون كل من صحت صلاته صحة مغنيةً عن القضاء جاز الاقتداء به، فإنه يقتضي انقسام الصحة إلى ما يغني عن القضاء، وإلى ما لا يغني.','1','360',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (166,'ثم استدل بغير هذا ثم قال: الصواب أن يكون حد الصحة عند الفريقين موافقة الأمر، غير أن الفقهاء يقولون: ظان الطهارة مأمور مرفوع عنه الإثم بتركها، والمتكلمون يقولون: ليس مأمورًا، فلذلك تكون صلاته صحيحة عند المتكلمين لا الفقهاء.
وأبو حنيفة- رضي الله عنه- سمى ما لم يشرع بأصله ووصفه كبيع الملاقيح باطلًا، وما شرع بأصله دون وصفه كالربا فاسدًا.
ففي المعاملات الباطل عنده كبيع الملاقيح، وهو: ما في بطون الأمهات لانعدام ركن البيع، أي: المبيع؛ لأن من أركانه وجود مورد','1','361',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (167,'العقد يقينًا ومن أوصافه كون المبيع مقدور التسليم وهما منتفيان هنا.
وفي العبادات كالصلاة بدون بعض الشروط والأركان.
والفاسد: ما كان أصله مشروعًا، ولكن امتنع لوصف عارض.
ففي العبادات كصوم يوم النحر للإعراض بصومه عن ضيافة الله تعالى للناس بلحوم الأضاحي التي شرعها (الله تعالى) فيه.
وفي المعاملات كما في بيع الدينار بالدينارين لاشتماله على الزيادة فلو أسقطت في المجلس صح البيع.
وصرح ابن الحاجب بأن الصحة والفساد عقليان.','1','362',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (168,'وظاهر كلام المصنف تبعًا للأمدي إنهما شرعيان وهو رأي الجمهور.
والأجزاء معناه قريب من الصحة فلذا ذكره هنا.
وفرق بينهما: بأن الصحة أعم من الأجزاء؛ إذ تكون صفة للمعاملات والعبادات، والأجزاء لا يوصف به إلا العبادات وفيه نظر.','1','363',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (169,'فالأجزاء لغة: الاكتفاء بالشيء.
وشرعًا: هو: الأداء- (يعني الإتيان) - الكافي لسقوط التعبدية أي: طلبه، وذلك بأن تجتمع فيه الشرائط، وتنتفي عنه الموانع.
(فدخل فيه الأداء المصطلح عليه، والقضاء والإعادة فرضًا كان أو نقلاً.
والصواب أن الأجزاء هو الاكتفاء بالمأتي به، لا الإتيان بما يكفي.','1','364',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (170,'كذا قيل).
وقيل: القائلون هم الفقهاء.
سقوط القضاء أي الأجزاء: إسقاط القضاء.
ورد بأن القضاء حينئذٍ لم يجب لعدم الموجب له (فكيف سقط؟ )، وذلك أن القضاء إنما يجب بأمر جديد، فإذا أمر الشارع بالعبادة ولم يأمر بقضائها، فتأتي بها، فإنها توصف بالأجزاء، مع أن القضاء حينئذٍ لم يجب لعدم الموجب له وهو الأمر الجديد، وإذا (لم يجب) لم يقل سقط، إذ السقوط فرع الثبوت.','1','365',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (171,'أو يقال: الموجب للقضاء هو خروج الوقت من غير الإتيان بالفعل، فإذا أتى بالفعل في الوقت على وجهه فقد وجد الأجزاء ولم يوجد وجوب القضاء لعدم الموجب له، وهو خروج الوقت وإذا لم يصدق وجوب القضاء لا يقال: سقط، كما مر.
وقد يقال: المعنى بالسقوط رفع وجوب القضاء، وهي مناقشة لفظية.
- ورد تعريف الفقهاء ثانيًا بقوله: وبأنكم تعللون سقوط القضاء به أي: بالأجزاء، فتقولون: هذا سقط قضاؤه؛ لأنه أجزاء.
- والعلة غير المعلول فكيف تقولن: إنه هو؟
ولك أن تقول: المغايرة مسلمة، ولا يلزم من المغايرة عدم جواز التعريف به؛ لأنه تعريف رسمي،','1','366',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (172,'وهو يكون باللازم، وعدم لزوم القضاء من لوازم الأجزاء.
وإنما يوصف أي بالأجزاء وبعدمه ما يحتمل أن يقع على وجهين:
أحدهما: معتد به شرعًا؛ لكونه مستجمعًا للشرائط فيوصف بالأجزاء.
والثاني: غير معتد به لانتفاء شرط من شروطه، فيوصف بعدم الأجزاء.
كالصلاة والصوم والحج، وهذا مبني على أن الصلاة ونحوها تطلق على الصحيحة والفاسدة حقيقة.','1','367',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (173,'أما إذا قلنا: إطلاقها على الفاسدة مجازًا، فهي من القسم الثاني لا يقع إلا على وجه واحد، كذا قيل.
لا المعرفة ورد الوديعة، أما الذي لا يقع إلا على وجه واحد فلا يوصف بالأجزاء وعدمه كمعرفة الله تعالى، فإنه إن عرفه بطريق ما فواضح، وإن لم يعرفه فلا يقال عرفة غير مجزئة، إذ الفرض أنه ما عرف (فهو جاهل).
وأما جعل رد الوديعة كذلك، ففيه نظر؛ لأن المودع إذا حجر عليه لسفه ونحوه لا يجزئ الرد عليه بخلاف ما لم يحجر عليه','1','368',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (174,'فتكون ذات وجهين.
الخامس: العبادة إن وقعت في وقتها المعين ولم يسبق بأداء مختل، فأداء وإلا فإعادة.
هذا تقسيم خامس للحكم باعتبار متعلقة؛ إذ الأداء والقضاء والإعادة أقسام للفعل الذي تعلق به الحكم.
فالأداة: ما فعل في وقته المعين، أي: المضبوط بنفسه محدود','1','369',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (175,'الطرفين، أم لا ولم يسبق بإتيان مثله على نوع من الخلل.
وإن سبق بمثلها على نوع من الخلل فإعادة: كالصلاة المأمور بها بعد الإتيان بها على نوع من الخلل كترك النية.
وقيل: ما فعل في وقت الأداء ثانيًا لعذر.
(والأول جزم به الإمام الرازي ورجحه ابن الحاجب.
والثاني هو الأوفق لاستعمال الفقهاء).
فالمنفرد إذا صلى ثانية مع الجماعة كانت إعادة على الثاني لأن طلب الفضيلة عذر، دون الأول؛ إذ لم يكن فيها خلل.
فإن لم يكن وقت معين، فلا يوصف بالأداء ولا بالقضاء.','1','370',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (176,'وقد يوصف بالإعادة كمن أتى بذات السبب على نوع من الخلل فتداركها.
وإن وقعت العبادة قبل وقتها المعين حيث جوزه الشارع فتعجيل كإخراج زكاة الفطر.
(تنبيه: إذا أدرك من وقت المؤداة مقدار ركعة، وفعلها فيه كانت صلاته أداء على الأصح، وإن وقع بعضها خارجه).','1','371',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (177,'وإن وقعت العبادة بعده أي: بعد الوقت المعين، ووجد فيه أي: في الوقت سبب وجوبها، فدخل فيه ما إذا فحج عنه وليه لوقوعه بعد وقته الموسع، إذ الموسع قد يكون بالعمر أو بغيره فقضاء، وجب أداؤه كالظهر المتروكة قصدًا بلا عذر.
أو لم يجب أداؤه وأمكن، كصوم المسافر والمريض.
أو أمتنع أداؤه لاستحالته عقلاً كصلاة النائم.','1','372',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (178,'أو امتنع أداؤه شرعًا كصوم الحائض.
- وظاهر كلامه أن الأداء والإعادة والقضاء، أقسام متباينة.
- وقال الشيخ سعد الدين: إنه ظاهر كلام المتقدمين والمتأخرين، وكلام الإمام والغزالي يشعر بأن الإعادة قسم من الأداء.
- وقال المحقق: الإعادة قسم من الأداء في مصطلح القوم، وإن','1','373',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (179,'وقع في عبارات بعض المتأخرين خلافه.
ولعله يشير إلى المصنف وصاحب الحاصل والتحصيل.
وكذا قال ألسبكي: الأداء ما فعل في وقته، سواء فعله مرة أخرى قبل ذلك أم لا.
قال: هذا هو الذي نختاره، وهو مقتضى أطلاقات الفقهاء، ومقتضى كلام الأصوليين: القاضي أبي بكر في التقريب والإرشاد، والغزالي في المستصفي، والإمام في المحصول.','1','374',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (180,'ولكن الإمام لما أطلق ذلك، ثم قال: إنه إن فعل ثانيًا بعد خلل سمى إعادة ظن صاحب الحاصل والتحصيل أن هذا مخصص للإطلاق المتقدم فقيداه وتبعهما المصنف.
وليس لهم مساعد من أطلاقات الفقهاء، ولا من كلام الأصوليين. فالصواب أن الأداء اسم لما وقع في الوقت مطلقًا.
(وفي المرصاد للمصنف- كما قاله الأب هري- التصريح بأن الإعادة قسم من الأداء، حيث قال: "وهو أي الواجب أداء: إن فعل في وقته المعين، وقضاء إن فعل في غيره.','1','375',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (181,'والأداء: إن كان مسبوقًا بأداء مختل فإعادة.
فينبغي أن يؤول كلامه هنا عليه).
والنقل المؤقت يقضي في الأصح، وكذا صلاة الصبي بعد وقتها.
فرع: إذا ظن المكلف أنه لا يعيش إلى آخر الوقت الموسع،','1','376',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (182,'تضيق عليه الوقت فلو أخر الفعل عنه مع ظن الفوات عصى اتفاقًا.
وصورته أن يطالب أولياء الدم باستيفاء القصاص من الجاني فيحضره الإمام أو نائبه، ويحضر الجلاد، ويأمره بالقتل ونحوها.
فإن لم يفت كأن عفا أولياء الدم وعاش وفعل العبادة في آخره، أي آخر الوقت الأصلي بعد الوقت المضيق بحسب ظنه: فقضاء، عند القاضي أبي بكر (الباقلاني من المتكلمين، والقاضي حسين من الفقهاء) لأنه أوقعه بعد الوقت المضيق عليه شرعًا.
وأداء عند الحجة الإمام الغزالي، والجمهور؛ إذ لا عبرة بالظن البين خطؤه.
ولا خلاف مع القاضيين في المعنى، إلا أن يريدا وجوب نية','1','377',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (183,'القضاء وهو بعيد، إذ لم يقل به أحد.
إنما النزاع في التسمية وتسمية أداء أولى؛ لأنه فعل في وقته المعين بحسب الشرع، وإن عصى بالتأخير.
كما إذا اعتقد انقضاء الوقت قبل الوقت وأخر فإنه يعصى.
ثم إذا ظهر خطأ اعتقاده، وأوقعه في الوقت كان أداءً اتفاقًا.
فكذا هنا، هذا فيمن أخر مع ظن الفوات (وسلم.
وأما عكسه: وهو من أخر مع ظن السلامة) ومات، كأن مات فجأة.','1','378',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (184,'فالتحقيق أنه لا يعصي؛ لأن التأخير جائز له ولا يأثم بالجائز.
ولا يقال: شرط الجواز سلامة العاقبة؛ إذ لا يمكن العلم بها فيؤدي إلى تكليف المحال.
وهذا بخلاف ما وقته العمر، فإنه لو أخر ومات عصى، وإلا لم يتحقق الوجوب.','1','379',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (185,'السادس: هذا تقسيم للحكم باعتبار كونه على وفق الدليل أو خلافه.
وحاصله: أن الحكم ينقسم إلى رخصة وعزيمة.
فالرخصة لغة: التيسير والتسهيل، وهي بتسكين الخاء، وحكي ضمها.
واصطلاحًا- ما أشار إليه المصنف بقوله: الحكم إن ثبت على خلاف الدليل لعذر فرخصه.
فالحكم جنس، وأشار بالثابت: إلى أن الترخيص لا بد له من دليل.
واحترز (بقوله: على خلاف) الدليل عما أباحه الله تعالى من الأكل وغيره.','1','380',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (186,'وأطلق الدليل ليشمل: ما إذا كان الترخص بجواز الفعل.
على خلاف الدليل المقتضى للتحريم كأكل الميتة.
وما إذا كان بجواز الترك على خلاف الدليل المقتضي للوجوب كجواز الفطر في السفر.
أو على خلاف الدليل المقتضي للندب لترك الجماعة لعذر المطر والمرض ونحوهما فإنه رخصة.
ومعنى العذر: ما يطرأ في حق المكلف من مشقة وحاجة، فيمنع','1','381',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (187,'حرمة الفعل ونحوه.
واحترز به عن الحكم الثابت بدليل راجح على دليل آخر معارض له.
وعن التكاليف كلها- كما قيل- فليست برخصة؛ لأنها لم تثبت لأجل المشقة.
ثم أشار إلى أقسام الرخصة بقوله: كحل الميتة للمضطر والقصر','1','382',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (188,'والفطر للمسافر واجبًا، ومندوبًا، ومباحًا، فأكل الميتة للمضطر مثال للواجب والقصر للمسافر إذا كان سفره المباح ثلاث مراحل، مثال للمندوب.
والفطر للمسافر مثال للمباح، بالنظر إلى المسافر من حيث إنه مسافر، من غير نظر إلى التضرر وعدمه، ويجوز أن يكون الشيء مباحًا في نفسه، ويصير مندوبًا، بل واجبًا بسبب أمر طرأ.
(وأيضًا هو مباح عند بعض الفقهاء، ويكفي في التمسك ذلك.','1','383',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (189,'وبقي من أقسام الرخصة خلاف الأولى، مثل فطر المسافر الذي لا يجهل الصوم).
ومن الرخصة إباحة ترك الجماعة في الصلاة لمرض أو نحوه وحكمه الأصلي الكراهة (الصعبة بالنسبة إلى الإباحة، وسببها قائم حال الإباحة، وهو الانفراد فيما يطلب فيه الاجتماع من شعائر الإسلام).
وإلا أي: وإن لم يثبت الحكم على خلاف الدليل، كإباحة الشرب، أو ثبت على خلاف الدليل لكن لا لعذر كالتكاليف فعزيمة.
قال العراقي: فعلم من ذلك انقسام العزيمة عنده إلى الأحكام الخمسة.','1','384',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (190,'وعليه مشى السبكي ولم يحك خلافه.
وفي المحصول كذلك ما عدا الحرام.
وجعلها القرافي منقسمة إلى الواجب والمندوب فقط.
وخصها الآمدي وابن الحاجب في مختصره الكبير بالوجوب.
وفي كلام والدي أنها مختصة بالوجوب والتحريم لأن كلا منهما فيه عزم مؤكد.','1','385',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (191,'ونقل عن ابن دقيق العيد كلامًا آخر.
وقال الشيخ سعد الدين: إن الفعل لا يتصف بالعزيمة ما لم يقع في مقابلة الرخصة.
والعزيمة لغة: القصد المصمم (لأنه عزم أمره أي قطع وحتم، صعب على المكلف أو سهل.
قال الأب هري: للشارع في الرخصة حكمان:
أحدهما: كونها واجبًا أو ندبًا أو إباحة.','1','386',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (192,'وثانيهما: كونها مسببة عن عذر طارئ في حق المكلف، فناسب تخفيف الحكم عليه مع قيام المحرم، وهو من أحكام الوضع؛ لأنه حكم بالمسببة كما في غير الرخص).','1','387',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (193,'<span data-type="title" id=toc-14>الفصل الثالث: في أحكامه</span>
أي: أحكام الحكم الشرعي وفيه سبع مسائل:
الأولى:
الوجوب قد يتعلق بمعين كالصلاة والحج، ويسمى واجبًا','2','5',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (194,'معينًا.
وقد يتعلق بمبهم من أمور معينة، أي بأحدها، ويسمى واجبًا مخيرًا وتارة يجوز الجمع بين تلك الأمور، وتكون أفرادها محصورة كخصال الكفارة.','2','6',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (195,'وتارة لا يجوز الجمع ولا تكون أفراده محصورة، كما إذا مات الإمام الأعظم ووجد جماعة قد استعدوا للإمامة، أي: اجتمعت فيهم الشروط، فإنه يجب على الناس نصب واحد من غير زيادة عليه.
وإليه أشار بقوله: ونصب أحد المستعين للإمامة.
ونازع السبكي فيه أنه من المتواطئ الذي لم ينظر إلى خصوصياته وليس مقصودًا، ولا يتصور التكليف بواحد مبهم من أمور مبهمة؛ لأنه تكليف بما لا يعلمه المكلف.
وقالت المعتزلة: الكل واجب على معنى أنه لا يجوز الإخلال بالجميع.
ولا يجب الإتيان به.','2','7',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (196,'وهذا بعينه هو الأول فلا خلاف في المعنى.
لكنه ينافي ما ذهب إليه بعض المعتزلة من أنه يثاب ويعاقب على كل واحد، ولو أتى بواحد سقط عنه الباقي بناء على أن الواجب قد سقط بدون الأداء.
وجمهورهم على خلاف ذلك.
وقيل: الواجب واحد معين عند الله تعالى دون الناس.','2','8',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (197,'وهذا القول يرويه الأشاعرة عن المعتزلة، والمعتزلة عن الأشاعرة.
(كذا قيل).
وقد اتفق الفريقان على فساده.
ورد هذا القول: بأن التعيين يحيل ترك ذلك الواحد فلا يجوز العدول عن ذلك الواحد المعين.
والتخيير يجوزه أي: يجوز ترك ذلك الواحد المعين، والعدول عنه إلى غيره، والجمع بينهما غير ممكن فإذا ثبت أحدهما بطل الآخر.
وقد ثبت التخيير اتفاقًا في الكفارة، فانتفى الأول وهو','2','9',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (198,'التعيين.
قيل: لا نسلم أن مقتضى التخيير ترك ذلك الواحد المعين؛ لأنه يحتمل أن المكلف يختار المعين بأن يلهمه الله تعالى اختيار ما عينه تعالى له.
أو يعين الله تعالى ما يختاره المكلف.
أو يسقط ذلك الواجب المعين إذا لم يقع عليه بفعل غيره، كما سقطت الجلسة الفاصلة بين السجدتين بجلسة الاستراحة.
وأجيب عن الأول:','2','10',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (199,'وهو أن المكلف يختار المعين بأنه يوجب تفاوت المكلفين فيه؛ لأن من اختار شيئًا يكون هو الواجب عليه دون غيره من الخصال، فيكون الواجب على هذا غير الواجب على الآخر عند الاختلاف في الاختيار، و (هو أي) (التفاوت بين المكلفين في ذلك) خلاف النص، والإجماع".
لأن الآية دالة على أن كل خصلة من الخصال مجزئة عن كل مكلف، وأن الذي أخرج خصلة لو عدل إلى الأخرى لأجزأته ووقعت واجبة.
وأن المكلفين في ذلك سواء إلا باعتبار التخيير دون التكليف.
والإجماع كالآية.','2','11',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (200,'وأجيب عن الثاني: بأن الوجوب تحقق قبل اختياره، أي: قبل اختيار المكلف إجماعًا مع أن الواجب في تلك الحالة لا يستقيم أن يكون معينًا، لأن الفرض أن التعيين يتوقف على اختياره وقد فرض أن لا اختيار.
والجواب عن الثالث: أن الآتي بأيها أي: بأي خصلة شاء من الخصال هو آت بالواجب.
إجماعًا لا ببدله، فلو كان الواجب واحدًا معينًا والمأتي به قد يكون بدلاً عنه يسقطه لكان الآتي به ليس آتيًا بالواجب، بل ببدله وهو خلاف الإجماع.','2','12',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (201,'ولما انحصر السند فيما جعله المعترض سندًا صار السند ملزومًا مساويًا للمنع، فتكون الأجوبة عن السند جوابًا عن المنع، أو المنافاة بين التعيين والتخيير بديهية.
فالمعترض لا يستحق جوابًا ويترك، وأجاب عن السند دفعًا للشبهة.
قيل: دليلاً على أن الواجب واحد معين إن أتى المكلف بالكل معًا في وقت واحد، كأن وكل بالإعتاق والإطعام وأعطى الكسوة','2','13',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (202,'بنفسه أو بوكيل آخر، فهو ممتثل قطعًا.
فالامتثال إما أن يكون معللاً بالكل من حيث هو كل، أي المجموع علة، فالكل واجب، وهو ظاهر البطلان.
أو يعلل بكل واحد فتجتمع مؤثرات -وهي مثل الإعتاق والإطعام والكسوة -على أثر واحد، وهو الامتثال، وهو باطل؛ لأن استناده إلى هذا يستغني به عن استناده (إلى الآخر، واستناده إلى الآخر يستغنى به عن استناده) إلى هذا فيستغنى بكل منهما. عن الآخر، ويفتقر لكل منهما بدلاً عن الآخر، فيكون محتاجًا إليهما وغنيًا عنهما.
أو يعلل الامتثال بواحد غير معين ولم يوجد؛ إذ كل موجود فهو في نفسه معين، ولا إيهام ألبتة في الوجود الخارجي.','2','14',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (203,'أو يكون الامتثال حصل بمعين عند الله -تعالى -مبهم عند المكلف وهو المطلوب.
وأيضًا الفعل المأمور به يسقط الحكم المتعلق به بالشخص، والوجوب حكم معين فيستدعي فعلاً معينًا يسقط به لأن غير المعين، لا يناسب المعين ولا وجود له أيضًا في نفسه فيمتنع وصفه بالوجوب لاستحالة اتصاف المعدوم بالصفة الثبوتية، فبطل أن يكون غير معين، ووجوبه فتعين أن يكون واحدًا وهو المطلوب.','2','15',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (204,'وكذا الثواب على الفعل، والعقاب على الترك.
فإذا أتى بالكل فيثاب ثواب الواجب قطعًا، ولا جائز أن يكون على الكل ولا على (كل واحد ولا على) واحد لا بعينه لما تقدم.
وكذا إذا ترك الكل لا جائز أن يعاقب على الكل، ولا على كل واحد، ولا على واحد لا بعينه، فإذًا الواجب واحد معين لأنه لم يبق غيره.
وأجيب عن الأول:','2','16',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (205,'بأن الامتثال بكل واحد، ولا يلزم اجتماع مؤثرات على أثر واحد؛ لأن هذه الأمور وغيرها من الأسباب الشرعية علامات لا مؤثرات، وهو المعني بقوله: وتلك معرفات، واجتماع معرفات على معرف واحد جائز، كالعالم للصانع، وليس ما تقدم من الدليل على امتناع التأثير بكل واحد جار في المعرف؛ لأنه نصب علامة ثانية لتحصل المعرفة بهما بدلاً لامعًا وأنه غير محال.
ومقصود المصنف هدم دليل الخصم، ولا يقتضي ذلك أن يكون مختاره أن كل واحد واجب، ليناقض مختاره أولاً لأنه في مقام الجدل، هذا وقد قال أجيب.
قال العراقي: ويمكن أن يكون جوابًا تحقيقًا.','2','17',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (206,'وتقريره: أن قوله: إن الامتثال بكل واحد، لا يلزم منه أن يكون كل واحد هو المتمثل به، بل كل شيء حصل به الامتثال ولا منافاة بين هذا وبين قولنا: إن الواجب واحد لا بعينه لأن كل واحد متضمن للواجب وهو واحد لا بعينه ومع ذلك ففي الجواب شيء.
وأجيب عن الثاني: بأنه إنما يستدعي أحدها، أي: أحد','2','18',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (207,'الخصال (لا بعينه وإن كان لا يقع إلا في معين، وأحدها لا بعينه موجود وله تعين) من وجه، هو أنه أحد هذه الثلاثة، وذلك كالمعلول المعين المستدعي علة من غير تعيين مثل الحدث، فإنه يستدعي علة من غير تعيين وهو المس أو اللمس أو زوال العقل، أو خروج شيء من أحد السبيلين.
والجواب عن الأخيرين:
وهما الثواب والعقاب: أنه يستحق ثواب أمور وعقاب أمور، لا','2','19',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (208,'يجوز ترك كلها، ولا يجب (فعل كلها)، يعني إذا أتى بالكل أو ترك الكل فيستحق الثواب والعقاب على مجموع أمور لا يجوز ترك كلها ولا يجب فعلها.
واعلم أن تحقيق هذه المسألة: أن الذي وجب وهو المبهم لم يخير فيه، والمخير فيه وهو كل من المعينات لم يجب منه شيء؛ لأنه لم يوجب معينًا، وإن كان يتأذى به الواجب لتضمنه. مفهوم أحدها، وتعدد ما صدق عليه مفهوم أحد المعينات عند تعلق الوجوب، والتخيير ينفي اتحاد متعلق الوجوب والتخيير بحسب الذات.','2','20',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (209,'وإذا لم يتحد متعلق الوجوب والتخيير بالذات، وكان التخيير بين واجب هو أحد المعينات من حيث إنه أحدها مبهمًا، وبين غير واجب هو إحداها على) التعيين من حيث التعيين، لم يلزم منه ارتفاع حقيقة الوجوب؛ لأن هذا لا يوجب جواز ترك كل واحد من المعينات على الإطلاق، بل جواز ترك معين من حيث التعيين لطريق الإثبات لمعين آخر.','2','21',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (210,'تذنيب: هو فضلة للمسألة الأولى وبقية منها، وهو أن الحكم قد يتعلق على الترتيب،','2','24',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (211,'أي: يمتنع الإتيان بالثاني إلا بعد الأول، وقسمه ثلاثة أقسام:
الأول: ما يحرم فيه الجمع، وهو المعني بقوله: ويحرم الجمع كأكل المذكي والميتة.
وأشار إلى الثاني بقوله: أو يباح كالوضوء والتيمم فإن التيمم عند العجز واجب.
ولو استعمله مع الماء كان جائزًا في صورة، وهي ما لم تيمم لخوف بطء البرء من الوضوء، ثم يتوضأ متحملاً لمشقة بطء البرء، وإن بطل بوضوئه تيممه لانتفاء فائدته.
وأشار إلى الثالث بقوله: ويسن ككفارة الصوم حيث جامع في','2','25',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (212,'رمضان بشرطه فإنه يجب عليه إعتاق رقبة، فإن عجز صام شهرين، فإن عجز أطعم ستين مسكينًا، ويستحب له الإتيان بالكل فينوي بكل الكفارة، وإن سقطت بالأولى كما ينوي بالصلاة المعادة الفرض وإن سقط بالفعل أولاً.
وقال العراقي: استحباب الجمع (بين الكفارة) لم يذكره الفقهاء في كتبهم وليس بصحيح من جهة المعنى.','2','26',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (213,'ثم ذكر ما هو مندفع بما قررته.
الثانية:
هذا تقسيم آخر للوجوب باعتبار وقته، وبينه بقوله: الوجوب إن تعلق بوقت فإما أن يساوي الوقت ذلك الفعل كصوم شهر رمضان وهو المضيق، أي: يسمى بالواجب المضيق.
أو ينقص الوقت عنه أي عن الفعل فيمنعه من منع التكليف','2','27',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (214,'بالمحال إلا لغرض القضاء فلا يمنعه، كوجوب الظهر على الزائل عذره، في آخر الوقت كالجنون والمرض، وقد بقي من الوقت قدر (ما يسع) تكبيرة.
أو يزيد الوقت عليه أي: على الفعل كالظهر، ويسمى بالواجب الوسع.
فيقتضي إيقاع الفعل في أي جزء من أجزائه، فجميع الوقت','2','28',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (215,'وقت لأدائه، ففي أي جزء أوقعه فقد أوقعه في وقته؛ لأن الأمر بذلك يقتضي إيقاع الفعل في جزء من أجزاء الوقت، ولا تعرض فيه للتخيير بين الفعل والعزم، ولا لتخصيصه بأول الوقت أو آخره، بل الظاهر ينفيهما.
لأن قوله (عليه الصلاة والسلام): "الوقت بين هاتين" متناول لجميع أجزائه ولا يتعين بعض الأجزاء، للوجوب دون البعض لعدم أولوية البعض فيكون القول به تحكمًا باطلاً.
وأما حديث: "الصلاة في أول الوقت رضوان الله وآخره عفو الله".','2','29',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (216,'فدلالته على أن الجزء الأول أولى بالنظر إلى المبادرة إلى الطاعة، وبحثنا في أجزاء الزمان من حيث هي هي (هذا والحديث ضعيف).
وقال المتكلمون وهو الأصح في شرح المهذب الواجب في كل جزء من الوقت هو إيقاع الفعل فيه أو إيقاع العزم فيه على الفعل في ثاني','2','30',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (217,'الحال، إلا أن أخر الوقت) إذا بقي منه قدر ما يسع الفعل فيه فحينئذ يتعين الفعل.
وعبر المصنف عن هذا المذهب بقوله: يجوز تركه في الأول بشرط العزم (يعني في الجزء الأول على الفعل في الجزء الثاني) وإلا أي: لو جاز الترك في أول الوقت بلا عزم مع قولنا بوجوبه في أول الوقت، لجاز ترك الواجب بلا بدل.
ورد بأن العزم لا يصلح بدلاً عن الفعل؛ لأنه لو صلح بدلاً','2','31',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (218,'عن الفعل لتأدى الواجب به، إذ بدل الشيء يقوم مقامه، وإذا لم يصلح للبدلية فقد لزم ترك الواجب بلا بدل كما قلتم.
ورد أيضًا: بأنه لو وجب العزم في الجزء الثاني لتعدد البدل والمبدل واحد، يعني: إذا عزم في الجزء الأول من أجزاء الزمان على الفعل، فإما أن يجب العزم في الجزء الثاني أيضًا أولاً، فإن لم يجب فقد ترك الواجب من غير بدل، ويلزم التخصيص بلا مخصص -وترك هذا الشق لوضوحه.
وإن وجب فقد تعدد البدل وهي الإعزام مع أن المبدل واحد.','2','32',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (219,'ومنع السبكي: كون المبدل واحدًا قال: لأن العزم في الجزء الأول، بدل عن الفعل في الجزء الأول، والعزم في الجزء الثاني بدل عن الفعل في الجزء الثاني، فالمبدل متعدد.
ومنا من قال: يختص الوجوب بالأول أي بأول الوقت، وفي الآخر يكون فعله قضاء حتى يأثم بالتأخير عن أوله.
ولم يعرف عن الشافعية، لكن نقله الشافعي (رضي الله عنه في الأم) عن المتكلمين.','2','33',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (220,'وإن نقل القاضي أبو بكر الإجماع على نفي الإثم، ولنقله قال بعضهم: إنه قضاء يسد مسد الأداء.
وقالت جماعة من الحنفية: يختص الوجوب بالآخر.
وفعله في الأول تعجيل يسقط به الفرض، فيصير كمن أخرج الزكاة قبل وقتها','2','34',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (221,'وقال الكرخي: منهم الآتي بالفعل في الأول إن بقي على صفة الوجوب إلى آخر الوقت أي وهو على صفة التكليف يكون ما فعله واجبًا، وإن لم يكن على صفته كأن جن أو حاضت كان ما فعله نفلاً.
ونقل عن الكرخي غير ذلك.','2','35',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (222,'احتجوا أي الحنفية: بأنه لو وجب في أول الوقت لم يجز تركه؛ لأنه ترك الواجب وهو الفعل في الأول لكنه يجوز تركه اتفاقًا، فانتفى أن يكون واجبًا.
قلنا: المكلف مخير بين أدائه في أي جزء من أجزائه.
وحاصله: منع الملازمة، وإنما يلزم لو كان الفعل أولاً واجبًا على التعيين، وليس كذلك، بل هو في التحقيق راجع إلى الواجب المخير، فالتعجيل والتأخير فيه جائز، كخصال الكفارة.','2','36',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (223,'فكما أنا نصفها بالوجوب على معنى أنه لا يجوز الإخلال بجميعها، ولا يجب الإتيان به فكذا هذا.
فالمكلف مخير بين أفراد الفعل في المخير، وبين أجزاء الوقت في الموسع.
ونحن لم نوجب الفعل في أول الوقت بخصوصه حتى يرد علينا جواز إخراجه عنه، بل خيرناه بينه وبين ما بعده.
والمذهب الذي قبله لما علم دليله وجوابه عن دليل الحنفية لأنه عكسه تركه اختصارًا.
وتقريره: أنه لو كان واجبًا في آخر الوقت لعصى من تركه في آخر الوقت وقد أتى به في أوله.
والجواب: أن ذلك إنما يلزم لو تعين وجوبه آخر الوقت.','2','37',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (224,'فرع: (الواجب الموسع قد يسعه العمر كالحج وقضاء الفائت إذا فات بعذر','2','41',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (225,'فله التأخير) من غير تأقيت، ما لم يتوقع فواته إن أخر لمرض أو كبر أي: فوات ذلك الواجب، أي بأن يغلب على ظنه فواته.
فإن توقع بأن ظن الفوات، إما لكبر سن أو لمرض شديد، حرم التأخير، والثاني مسلم، والأول رأي.
والصحيح جواز التأخير للشيخ والشاب، فإن مات قبل الفعل عصى.
وقوله: (لمرض أو كبر) متعلق بقوله: يتوقع فواته.
ويؤخذ منه: أنه لا يحرم عليه التأخير إذا لم يظن الفوات أصلاً، أو ظنه، لكن لا لكبر أو مرض، بل لغيرهما -كالتنجيم والمنام.','2','42',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (226,'الثالثة:
الوجوب إما أن يتناول كل واحد، كالصلوات الخمس، أو واحدًا معينًا كالتهجد، ويسمى فرض عين، أو غير معين كالجهاد ويسمى فرضًا على الكفاية.','2','43',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (227,'هذا تقسم للوجوب باعتبار من يجب عليه، وحاصله:
أن الوجوب ينقسم إلى فرض عين وفرض كفاية:
ففرض العين قد يتناول كل واحد من المكلفين سواء كان فعل البعض شرطًا في فعل البعض كصلاة الجمعة، أو لا، كالصلوات الخمس.
وقد يختص بواحد كالتهجد حيث كان واجبًا عليه -صلى الله عليه وسلم -.
وفرض الكفاية: نحو الجهاد مما يحصل الغرض عنه بفعل البعض.','2','44',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (228,'وحكمه: وجوبه على البعض، كما جزم به المصنف تبعًا للإمام الرازي لقوله تعالى: {ولتكن منكم أمة يدعون إلى الخير} ... الآية.
وذهب الجمهور: إلى أنه يجب على الجميع لإثمهم بتركه، ويسقط بفعل البعض.
وأجاب القائل بالأول: بأن إثمهم بالترك لتفويتهم ما قصد','2','45',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (229,'حصوله من جهتهم في الجملة، لا للوجوب عليهم.
وسمي فرض كفاية؛ لأن فعل البعض كافٍ في تحصيل المقصود منه، والخروج عن عهدته.
بخلاف الأول فإنه لابد من فعل كل عين، أي ذات، فلذلك سمي فرض عين.
فإن ظن كل طائفة أن غيره فعله سقط الوجوب عن الكل، وإن ظن كل طائفة أنه، أي أن غيره لم يفعله، وجب عليهم الإتيان به ويأثمون بتركه، وإن ظنت طائفة قيام (غيرها به)، وظنت أخرى عكسه، سقط عن الأولى ووجب على الأخرى.','2','46',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (230,'(قال العراقي: اعتبر المصنف تبعًا للإمام الظن، وفيه نظر؛ لأن الوجوب على الكل معلوم فلا يسقط إلا بالعلم.
(وفيه بحث): وكان ينبغي أن يقول: فإن ظنت كل طائفة.
قاله السبكي).
والأمر فيه قريب؛ لأن تأنيث الطائفة غير حقيقي).','2','47',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (231,'الرابعة','2','48',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (232,'اتفقوا على أن الوجوب إذا كان مقيدًا بمقدمة ولم تكن تلك المقدمة واجبة: كأن تقول: إن ملكت النصاب فزك، فهذا لا يكون إيجابًا لتحصيل النصاب، إنما الكلام في الواجب المطلق.
كما أشار إليه بقوله: وجوب الشيء مطلقًا يوجب وجوب','2','49',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (233,'(مالا) يتم إلا به وكان مقدورًا.
وقد فسر الواجب المطلق: بما يجب في كل وقت وعلى كل حال فنوقض بالصلاة. فزيد في كل وقت الشارع فنوقض بصلاة الحائض فزيد: "إلا لمانع"، وهذا لا يشمل غير المؤقتات، ولا مثل الحج والزكاة، في إيجاب ما يتوقف عليه من الشروط والمقدمات.
فأشار المحقق إلى أن المراد بالتقييد والإطلاق بالنسبة إلى تلك المقدمة، حتى أن الزكاة بالنسبة إلى تحصيل النصاب مقيد فلا يجب','2','50',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (234,'وإلى تعينه وإفراده مطلق فيجب.
وحاصله: أن كل ما يتوقف عليه الوجوب لا يجب تحصيله، وإنما النزاع فيما يتوقف عليه إيقاع الواجب بعد تحقق الوجوب.
إذا علمت ذلك: فوجوب الشيء المطلق يوجب مقدمته، وهو الذي لا يتم الواجب إلا به سببًا شرعيًا.','2','51',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (235,'كالصيغة بالنسبة للعتق أو عقليًا كالنظر المحصل للعلم (أو عاديًا كحز الرقبة بالنسبة إلى القتل الواجب) أو شرطًا شرعيًا كالوضوء للصلاة، أو عقليًا كترك الأضداد للمأمور به، أو','2','52',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (236,'عاديًا كغسل جزء من الرأس لغسل الوجه إذا كانت المقدمة مقدورة للمكلف إذ لو لم تكن مقدورة له فلا يوجب وجوبه وجوب المقدمة كالداعية.
وقيل: وجوب الشيء مطلقًا يوجب السبب دون الشرط.','2','53',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (237,'وقيل: لا يوجبه فيهما أي لا في السبب والشرط.
واختار ابن الحاجب تبعًا لإمام الحرمين: فيما عدا السبب أنه إن كان شرطًا شرعيًا وجب أو غير شرعي فلا.
ومقتضى كلامه أن السبب لا خلاف فيه.
لنا على أنه يوجب الشرط والسبب.','2','54',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (238,'أن التكليف بالمشروط دون الشرط محال؛ لأنه إذا كان مكلفًا بالمشروط لا يجوز له تركه، وإذا لم يكن مكلفًا بالشرط جاز له تركه، ويلزم من جواز تركه جواز ترك المشروط فيلزم الحكم بعدم جواز ترك المشروط، وبجواز تركه، وهو جمع بين النقيضين.
وإذا وجب الشرط وهو أضعف وجب السبب من باب أولى.
قال العراقي: وصواب العبارة أن يقول: تكليف بمحال بزيادة الباء؛ لأن ابن التلمساني وغيره فرقوا بينهما فقالوا: تكليف','2','55',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (239,'المحال هو ما كان الخلل فيه راجعًا
إلى المأمور كتكليف الميت والجماد ومن لا عقل له من الأحياء.','2','56',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (240,'والتكليف بالمحال هو ما كان الخلل فيه راجعًا إلى المأمور به، كتكليف العاقل الذي يفهم الخطاب بما لا يطيقه وهو المراد هنا.
وتكليف ما لا يطاق فيه خلاف.
والصحيح عندنا جوازه فلا ينبغي القول بأنه محال.
وأجيب عنه: بأنه لما كان تكليف ما لا يطاق مثل المحال؛ لأنه غير واقع ساوى الأول فأطلق عليه أنه محال باعتبار الوقوع لا الجواز (فليتأمل السؤال والجواب).','2','57',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (241,'قيل: اعتراضًا على هذا الدليل: يختص الإيجاب بوقت وجود الشرط وحينئذ لا يلزم وجود المشروط دون الشرط.
قلنا: هذا خلاف الظاهر، إذ الكلام في الواجب المطلق الذي لا اختصاص له بوقت دون وقت.
فتقييده بوقت وجود الشرط خلاف الظاهر، وهو غير جائز.
قيل: اعتراضًا على هذا الجواب: إيجاب المقدمة كذلك،','2','58',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (242,'يعني قد جوزتم مخالفة الظاهر في إيجاب المقدمة (إذ أوجبتموها) بمجرد الأمر، مع أن الظاهر لا يقتضي وجوبها.
قلنا: لا فإن اللفظ لم يدفعه، يعني: أن خلاف الظاهر عبارة عن ما ينفيه اللفظ أو نفي ما يثبته، أما ما لا يتعرض له اللفظ لا بنفي ولا إثبات.
فإيجابها بدليل منفصل ليس خلاف الظاهر بخلاف تخصيص الوجوب بوقت وجود الشرط فإنه خلاف ما يقتضيه اللفظ من وجوب الفعل في كل وقت، وفيه نظر.
واعلم أن النزاع إنما هو في أن الأمر بالشيء هل يكون أمرًا بشرطه وإيجابًا له، وإلا فوجوب الشرط الشرعي الواجب معلوم قطعًا؛ إذ لا معنى لشرطيته سوى، حكم الشارع أنه يجب الإتيان به عند الإتيان','2','59',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (243,'بذلك الواجب، كالوضوء للصلاة، وهذا كما أن الشرط العقلي معلوم أنه لازم عقلاً.
تنبيه: وهو ما يكون السابق دالاً عليه في الجملة.
مقدمة الواجب إما أن يتوقف عليها وجوده شرعًا','2','60',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (244,'كالوضوء للصلاة.
أو يتوقف على المقدمة وجوده عقلاً كالمشي للحج؛ لأنه لو فرض انتفاء الشرع لقضى العقل بأنه لابد في وجوب الحج من قطع المسافة للحج.
أو يتوقف العلم به، أي بالواجب على المقدمة كالإتيان بالصلوات الخمس، فإنه يلزمه إذا ترك واحدة ونسي عينها؛ لأن','2','61',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (245,'العلم بأنه أتى بالمتروك المنسي، لا يحصل إلا بعد الإتيان بالخمس، فالصلوات الأربع مقدمة للواجب، لكن لا لوجوده؛ لجواز أن يكون الأول هو الواجب.
ومنه ستى شيء من الركبة لستر الفخذ، فإنه لا يتحقق ستر العورة إلا به، وبستر شيء من السرة.
والمثال الأول الواجب فيه متميز عن المقدمة، وطرأ عليه الإبهام.
والثاني لم يتميز أصلاً لأجل ما بينهما من التقارب خلقة.','2','62',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (246,'فروع متفرعة على ما تقدم:
الأول: لو اشتبهت منكوحته بأجنبية حرمتا عليه، على معنى أنه يجب عليه الكف عنهما جميعًا، إحداهما لكونها أجنبية، والأخرى لأنها مشتبهة بها، وليست حرامًا في الواقع، بل يجب الكف عنها مع حلها إلى رفع الاشتباه.
الثاني: لو قال لزوجتيه إحداكما طالق','2','63',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (247,'(ولم يعين)، حرمتا عليه تغليبًا للحرمة؛ لأن كل واحدة منهما يحتمل أن تكون هي المطلقة، فاحتمل الحل، والحرمة، في كل واحدة منهما، والتغليب للتحريم.
فالعلم باجتناب المطلقة إنما يحصل باجتنابهما.
وقوله: والله تعالى يعلم أنه سيعين أيتهما، لكن لما لم يعين لم تتعين.
أراد به جوابًا عن سؤال تقديره: إن قولكم: المحل غير معين','2','64',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (248,'ممنوع؛ لأن المحل معين عند الله - تعالى - أي المطلقة معينة عند الله - تعالى - ولكن التبست بغير المطلقة عندنا، فيكون مثل الفرع الأول، والمقصود تغاير الفرعين، وهو أن تكون إحداهما مبهمة في الفرع الثاني وإلا يتكرر.
فأجاب: بأن المحل ليس معينًا عند الله - تعالى - لأن الزوج لم يعين بعد فكيف يعلمه الله - تعالى - معينًا؛ لأن خلاف الواقع محال أن يعلمه.
إيضاحه: إن الله تعالى يعلم الأشياء كما هي واقعة، فإذا كانت المطلقة غير معينة علمها الله - تعالى - أيضًا غير معينة؛ لأنه الواقع،','2','65',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (249,'نعم هو - تعالى - يعلم أن الزوج سيعين فلانة لكن ما لم يعين لم يتعين عند الله - تعالى.
الثالث: الزائد على ما ينطلق عليه الاسم من المسح غير واجب وإلا أي لو كان واجبًا (لما جاز) تركه فيكون غير واجب.','2','66',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (250,'وقيل: الكل واجب.
* * *','2','67',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (251,'المسألة الخامسة
وجوب الشيء يستلزم حرمة نقيضه لأنها جزؤه، فالدال عليه يدل عليها بالتضمن.
قالت المعتزلة وأكثر أصحابنا: الموجب قد يغفل عن نقيضه.','2','68',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (252,'قلنا: لا، فإن الإيجاب بدون المنع من نقيضه محال وغن سلم فمنقوض بوجوب المقدمة.
قد اختلف في أن الأمر بالشيء هل هو نهي عن ضده؟
وليس الكلام في هذين المفهومين لتغايرهما لاختلاف الإضافة.
فإن الأمر مضاف إلى الشيء، والنهي إلى ضده، ولا في اللفظ لأن صيغة الأمر: "افعل" وصيغة النهي: "لا تفعل".','2','69',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (253,'وإنما الخلاف في الأوامر الجزئية المتعينة إذا أمر بها.
فهل ذلك الأمر نهي عن الشيء المعين المضاد له أولا؟
فإذا قال: تحرك، فهل هو في المعنى بمثابة قوله: لا تسكن.
وليس المراد بأن الأمر نفس النهي، بل المعنى أنهما حصلا بجعل','2','70',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (254,'واحد، كما في قولهم: الأمر بالشيء، أمر بمقدمته أي جعلاهما واحد لم يحصل كل منهما بأمر على حده.','2','71',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (255,'إذا علمت ذلك: فاختيار الإمام والغزالي وابن الحاجب أنه ليس نفس النهي عن ضده ولا يتضمنه، ونقله المصنف عن كثير من','2','72',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (256,'أصحابنا والمعتزلة: أي جمهورهم.
وقيل: نفس النهي عن ضده واتصافه يكون أمرًا ونهيًا باعتبارين كما يتصف الشيء الواحد بكونه قريبًا بعيدًا بالنسبة إلى شيئين ونقل: عن الأشعري.
وقيل: يتضمنه، ونقل عن القاضي أبي بكر (رحمه الله) والمصنف حكى مذهبين:
أحدهما: أنه يستلزمه واختاره.
والثاني: أنه لا يدل عليه ألبتة.
فعلى ما اختاره: يكون الأمر بالشيء: نهيًا عن جميع','2','73',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (257,'أضداده.
وقوله: وجوب الشيء: يعم الأمر وفعل الرسول - (صلى الله عليه وسلم) - والقياس والإجماع وغيرها من قرائن الأحوال مثل} كتب عليكم الصيام {.
واستدل على مختاره: وهو أن وجوب الشيء يستلزم حرمة نقيضه أي: استلزام الكل للجزء.
بأن ماهية الوجوب مركبة من أمرين: طلب الفعل والمنع من','2','74',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (258,'الترك.
فاللفظ الدال على الوجوب يدل على حرمة النقيض بالتضمن.
بيانه: أن السيد إذا قال لعبده: قم، فهذا الأمر يدل على طلب القيام والمنع من ترك القيام بالمطابقة، وعلى كل واحد منهما بالتضمن، وعلى الأضداد الوجودية للقيام كالقعود والاضطجاع وغيرها بالالتزام.
ونظر في كلام المصنف؛ لأنه إن أراد بالنقيض الترك فلا نزاع فيه لأحد وإن أراد به الأضداد العامة من القعود ونحوه، فاللفظ لا يدل','2','75',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (259,'عليها بالتضمن؛ لأنها ليست جزءًا من ماهية الواجب، فيلزمه. إما أنه غير المدعي أو نصب للدليل في غير محل النزاع.
وأجيب بأن اللفظ: إذا دل على المنع من الترك بالتضمن، والمنع من الترك دال على الأضداد الوجودية بالالتزام، يكون اللفظ دالاً عليها بالالتزام، فلذا قال في الأول: يستلزم وفي الثاني بالتضمن.
وقالت المعتزلة: ومن وافقهم: الموجب للشيء قد يغفل - بضم الفاء - عن نقيضه، أي لو كان الأمر بالشيء نهيًا عن ضده أو متضمنًا','2','76',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (260,'له لم يحصل بدون تعقل الضد، والكف عنه واللازم منتف.
أما الملازمة؛ فلأن الكف عن الضد هو مطلوب النهي ويمتنع أن يكون المتكلم طالبًا لأمر لا يشعر به، فيكون الكف عن الضد متعقلاً له، وما ذلك إلا بتعقل مفرديه وهما: الضد، والكف عنه.
وأما انتفاء اللازم: فلأنا نقطع بطلب حصول الفعل مع أن الآمر غافل أي: ذاهل عن الضد والكف عنه.
كما إذا قال السيد لعبده: قم، ويكون غافلاً عن القعود والاضطجاع.
وأجاب:
أولاً: بأن الموجب للشيء لابد وأن يتصور الوجوب؛ لأن المنع من الترك الذي هو النقيض جزء مفهوم الوجوب فلا يتصور إيجاب الشيء مع الغفلة عن) نقيضه؛ لأن من تصور الكل لابد وأن يتصور الجزء ضرورة.','2','77',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (261,'فإن قلت: هذا الجواب فيه نظر؛ لأنه لا نزاع في المنع من الترك وإنما النزاع في الأضداد الوجودية وظاهر أن السيد يأمر عبده بالقيام ويكون (غافلاً عن ضده كما بين).
أجيب: بأن الأضداد الوجودية إذا كان تركها لازمًا بالإيجاب يلزم من الشعور بالملزوم الشعور باللازم فيتم جوابه.
وأنت خبير بأن الأمر الصادر من الله تعالى لا يكون فيه ذلك.
(إذ الغفلة عليه تعالى محال فمحل الخلاف في غيره.
وأجاب ثانيًا: بأن) الغفلة لا تنافي الإيجاب ولا النهي.
فلا يلزم من كون الآمر غافلاً عن النقيض أن لا يكون منهيًا عنه كما أن مقدمة الواجب واجبة مع جواز الغفلة عنها.','2','78',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (262,'كما أن السيد إذا قال لعبده وهو على سطح: اسقني ماءً، مع كونه غافلاً عن وضع السلم والصعود إليه فكذلك حرمة النقيض.
وكلام المصنف في هذا المقام لا يخلو عن مناقشة.','2','79',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (263,'<span data-type="title" id=toc-23>المسألة السادسة: الوجوب إذا نسخ بقي الجواز</span>؛ الذي هو القدر المشترك بين الوجوب والندب والإباحة والكراهة فيجوز الإقدام عليه عملاً بالبراءة الأصلية خلافًا للغزالي.','2','81',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (264,'لأن الدال على الوجوب يتضمن هذا الجواز؛ لأن مفهوم الوجوب مركب من رفع الحرج عن الفعل مع إثبات الحرج على الترك.
فالدال على الوجوب مطابقة يدل على الجواز تضمنًا.
والناسخ لا ينافيه، أي: لا ينافي الجواز، فإنه أي الوجوب المركب يرتفع بارتفاع المنع من الترك.
فإن الركب يرتفع بارتفاع أحد أجزائه، وليس هذا تخصيصًا؛ لأنه إخراج جزء مفهوم اللفظ وليس إخراجًا لبعض ما صدق عليه مفهوم اللفظ.','2','82',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (265,'وصورة المسألة: أن يقول الشارع مثلاً نسخت الوجوب أو رفعت ذلك، أما إذا نسخ الوجوب بالتحريم، أو قال مثلاً: رفعت جميع ما دل عليه الأمر السابق من جواز الفعل والمنع من الترك، فيثبت التحريم قطعًا.
وقال العراقي: أراد يعني المصنف بالجواز: رفع الحرج عن الفعل، وبسطه ثم قال: والذي ادعى الغزالي عدم بقائه هو الجواز بمعنى التخيير بين الفعل والترك','2','83',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (266,'(وهو قوى) لأن الجواز بهذا التفسير لم يكن ثابتًا مع الوجوب فكيف يبقى بعده؟
قيل: يحتمل المعارضة للدليل الأول، أو دليل لعدم بقاء الجواز.
تقريره: أن الجنس يتقوم بالفصل؛ لأن الفصل علة للجنس فيرتفع جنس الواجب وهو الجواز (بارتفاعه أي) بارتفاع الفصل وهو المنع مع الترك، إذ ارتفاع العلة يوجب ارتفاع المعلول.
قلنا: لا نسلم كون الفصل علة للجنس.','2','84',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (267,'وإن سلم أنه علة له، فلا يلزم من ارتفاع العلة المعينة ارتفاع المعلول المعين؛ إذ المعلول المعين، لا يحتاج إلا إلى علة ما، لا إلى علة معينة.
فالجواز يحتاج إلى فصل من الفصول يتقوم به، أي: يوجد به لا إلى هذا الفصل المعين الذي هو المنع من الترك فيتقوم بفصل عدم الحرج في فعله فيبقى الجواز بحاله.
وفائدة الخلاف: أنه إذا بطل الخصوص هل يبقى العموم؟','2','85',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (268,'<span data-type="title" id=toc-24>المسألة السابعة: الواجب لا يجوز تركه</span>، لما تقدم من أن ماهية الوجوب مركبة من طلب الفعل مع المنع من الترك. فالمنع من الترك جزء مفهوم الوجوب، فيستحيل أن يكون الشيء واجبًا مع كونه جائز الترك.
وقال الكعبي: فعل المباح ترك الحرام؛ لأن فاعل المباح تارك للحرام ضرورة','2','86',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (269,'إذ السكوت ترك للقذف، والسكون ترك للقتل، وهو أي ترك الحرام واجب، ففعل المباح واجب مع جواز تركه فالواجب يجوز تركه.
قلنا: لا نسلم كون فعل المباح نفس ترك الحرام وإنما يكون كذلك','2','87',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (270,'لو لم يحصل إلا به، لكنه كما يحصل به يحصل بغيره من الواجب والمندوب والمكروه، فلا يكون فعل المباح نفس ترك الحرام بل به يحصل ترك الحرام والخاص غير العام، ففعل المباح غير ترك الحرام.
وفيه نظر، لأن فيه تسليم أن الواجب أحدها لا بعينه فما يعمل فهو واجب قطعًا فيكون المباح واجبًا في الجملة.
والكعبي لم يدع إلا أصل الوجوب، فأيما فعله المكلف فهو واجب (لأنه مقدمة الواجب) الذي هو ترك الحرام.','2','88',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (271,'ولهذا زيادة تحقيق ذكرته في الشرح.
وقال الفقهاء (أي أكثرهم) يجب الصوم على الحائض والمريض والمسافر؛ لأنهم شهدوا الشهر وهو أي: شهود الشهر موجب للصوم لقوله تعالى: } فمن شهد منكم الشهر فليصمه {.
وأيضًا يجب عليهم القضاء بقدره أي: بقدر ما فاتهم وذلك يدل','2','89',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (272,'على وجوب الأداء عليهم كغرامة المتلفات فثبت بأن الصوم يجب عليهم مع أنه يجوز لهم تركه. فالواجب يجوز تركه.
قلنا: العذر مانع من وجوب الأداء عليهم وشهود الشهر إنما يكون موجبًا عند عدم الأعذار المانعة من الوجوب، والعذر هنا قائم، وهو الحيض والسفر والمرض، والشيء قد لا يترتب على موجبه لمانع، فلا يلزم من شهود الشهر مع المانع الوجوب والقضاء يتوقف على السبب للوجوب، وهو دخول الوقت لا على نفس الوجوب، وإلا أي لو توقف على نفس الوجوب، لما وجب قضاء الظهر على من نام جميع','2','90',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (273,'الوقت؛ لأنه غير مكلف بالصلاة في حال نومه لامتناع تكليف الغافل.
وقد يقال: القضاء يجب بأمر جديد لا بالأمر الأول.
* * *','2','91',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (274,'<span data-type="title" id=toc-25>الباب الثاني: فيما لابد للحكم منه وهو الحاكم والمحكوم عليه والمحكوم به</span>
وفيه ثلاثة فصول:

<span data-type="title" id=toc-26>الفصل الأول: في الحاكم</span>
وهو الشرع دون العقل','2','93',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (275,'ولا نعني به أن العقل لا حكم له في شيء أصلاً، بل إنه لا يحكم بأن الفعل حسن أو قبيح في حكم الله تعالى، أي: كون الفعل متعلق المدح في العاجل والثواب في الآجل أو متعلق الذم عاجلاً والعقاب آجلاً.','2','94',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (276,'ولما كان القول بهذا مبنيًا على فساد الحسن والقبح.
قال لما بينا من فساد الحسن والقبح العقليين في كتاب المصباح في أصول الدين. وذكرت الدليل وما يرد عليه وجوابه في الشرح','2','95',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (277,'فراجعه منه إن شئت.
فرعان على التنزل: (من مذهب أهل السنة الذي هو الحق) إلى مذهب المعتزلة الباطل، وتسليم حكم العقل.','2','96',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (278,'وفيه نظر: للكيا الطبري حيث قال: كيف يصح أن يتنزل ويسلم للمعتزلة مسألة التقبيح والتحسين مع منع قولهم من مسألة شكر المنعم، مع أنهما شيء واحد؛ لأن المراد بشكر المنعم إتيان المستحسنات العقلية والانتهاء عن المستخبثات العقلية.

<span data-type="title" id=toc-27>الفرع الأول: شكر المنعم </span>ليس بواجب عقلاً فلا إثم في تركه على من لم تبلغه','2','97',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (279,'دعوة النبوة.
والمراد بالشكر الثناء على الله - تعالى - بسبب إنعامه بالخلق والرزق والصحة وغيرها، بالقلب بأن يعتقد أنه - تعالى - وليها، واللسان بأن يتحدث بها، والأعضاء بأن تخضع بها، فلا يجب عقلاً: إذ لا تعذيب قبل الشرع لقوله تعالى: } وما كنا معذبين حتى نبعث رسولاً {.
فنفى صحة التعذيب قبل البعثة فانتفى الوجوب قبلها؛ لأن الواجب هو الذي يصح أن يعاقب تاركه، فصحة التعذيب لازم','2','98',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (280,'للوجوب، ونفي اللازم دليل على نفي الملزوم، فلم يكن وجوب قبل الشرع، فليس الوجوب عقليًا، إذ لو كان عقليًا لثبت قبل الشرع لوجود العقل قبل البعثة.
وقولهم: الرسول العقل، خلاف الظاهر.
وتخصيصهم العذاب بالدنيوي تخصيص بلا مخصص.','2','99',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (281,'وأيضًا: لأنه لو وجب الشكر لوجب لفائدة واللازم باطل.
أما الأولى: فلأنه لولا الفائدة لكان عبثًا، وهو قبيح فلا يجب عقلاً.
وأما الثانية: فهي إما أن تكون لفائدة المشكور وهو منزه عنها لتعاليه عن الاحتياج إلى الفائدة، أو الفائدة للشاكر في الدنيا وأنه مشقة وتعب ناجز بلا حظ للنفس فيه، وما هو كذلك لا يكون له فائدة دنيوية.
أو لفائدة في الآخرة ولا استقلال للعقل بها؛ لأن أمور الآخرة من الغيب الذي لا مجال للعقل فيه.','2','100',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (282,'قيل من جهة المعتزلة: انفصالاً عن هذا الالتزام بأن له فائدة للعبد في الدنيا، لأنه يدفع عنه ظن ضرر الآجل، أي: الآتي في الدنيا.
وفي بعض الشروح أنه الأخروي، وفيه نظر لما تقدم من إبطال حصول فائدة في الآخرة؛ لأن أمور الآخرة من الغيب الذي لا مجال للعقل فيه فيأمن من احتمال العقاب لتركه. فإن العاقل يجوز أن خالقه طلب منه الشكر، فإن أتى به سلم من العقوبة، وإن تركه فلعله يعاقب، وهذا الاحتمال لازم الخطور على بال كل عاقل، فإنه إذا نشأ، ورأى ما هو عليه من النعم الجسام التي لا تحصى حينًا فحينا، علم أنه لا يمتنع كون المنعم بها قد ألزمه الشكر، فلو لم يشكره لعاقبه.','2','101',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (283,'قلنا: قد يتضمنه، أي: قد يتضمن الشكر الضرر فيخاف العقاب على الشكر:
لأنه تصرف في ملك الغير بدون إذنه، فإن ما يتصرف فيه العبد من نفسه وغيرها ملك لله تعالى، ولا نسلم أن الاحتمال الأول لازم الخطور على كل بال.
وكالاستهزاء لحقارة الدنيا بالقياس إلى كبريائه، ويعني أن هذا الشكر كالاستهزاء بالمنعم وما مثله إلا كمثل فقير حضر ملكا عظيمًا يملك البلاد شرقًا وغربًا، يعم البلاد بالهبة والعطاء، فتصدق عليه بلقمة خبز، فجعل يذكرها في المجامع ويشكره عليها، فإنه يعد استهزاء منه بالملك، فكذا هنا، بل أعظم؛ لأن ما أنعم الله - تعالى - به على عباده بالنسبة إلى كبريائه وخزائن ملكه أقل من نسبة اللقمة إلى خزائن','2','102',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (284,'الملك.
لأن نسبة المتناهي إلى غير المتناهي أقل من المتناهي إلى غير المتناهي والاستهزاء بالله تعالى يخاف منه العقاب، فلا يقطع بذلك.
ولأنه ربما لا يقع الشكر لائقًا بكبريائه وعظمته، لأن العبد قد لا يهتدي إلى الشكر اللائق قبل الشرع، فيأتي به على غير وجهٍ لائق وربما يكون خطأ فاحشًا وكفرًا عظيمًا، كما وقع لعبدة الأصنام.
قيل: ينتقض هذا بالوجوب بالشرعي، فإن دليلكم يوجب أن لا يجب الشكر شرعًا بعين ما قلتم وأنتم لا تقولون به.
قلنا: إيجاب الشرع لا يستدعي فائدة أي لا يجب أن يكون لفائدة','2','103',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (285,'كإيجاب العقل (بل له بحكم) المالكية أن يحكم بأحكام وإن لم تكن فيها فائدة ولا منفعة، وهذا بخلاف حكم العقل فإنه يقتضي فائدة ألبتة.
وهذا لا ينافي ما سيجيء للمصنف من أن الله تعالى شرع أحكامه لمصالح العباد تفضلاً وإحسانًا؛ لأن الكلام هنا في أن أفعال الله تعالى لا تكون معللة با لأغراض وهناك أن الله سبحانه - وتعالى - تفضل على عباده بما فيه مصلحتهم، ولا يلزم من وقوع شيء على وجه أن يكون ذلك علة له، فأين أحدهما من الآخر.
(وهاتان المسألتان) اشتبهتا على كثير من الناس، وقد ذكرت في الشرح جوابين آخرين: أحدهما تبعت فيه بعض مشايخي وفيه نظر.','2','104',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (286,'وأيضًا: فإن إيجاب الشرع، لفائدة في الدنيا والآخرة علمناها بإخبار الشارع وهذا لا يمكن في الوجوب؛ لأن العقل لا دخل له في الأمور الأخروية.
ولما كان هذا في غاية الوضوح تنزل المصنف وسلمه، ثم أجاب بأنه وإن لم تكن فائدة فلا يلزم محال في الشرعي بخلاف العقلي.

<span data-type="title" id=toc-28>الفرع الثاني: الأفعال الاختيارية</span>','2','105',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (287,'قبل البعثة مباحة عند المعتزلة البصرية وبعض الفقهاء.
محرمة عند المعتزلة البغدادية، وبعض الإمامية وابن أبي هريرة من','2','106',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (288,'الشافعية.
وتوقف الشيخ أبو الحسن الأشعري وأبو بكر الصيرفي الشافعي.
وفسره الإمام (فخر الدين) الرازي أي فسر التوقف بعدم الحكم.
والأولى أن يفسر بعدم العلم؛ لأن الحكم قديم عنده أي عند','2','107',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (289,'الأشعري.
ولا يتوقف تعلقه أي: الحكم على البعثة أي: عند الشيخ لتجويزه التكليف بالمحال.
واعلم أن المعتزلة قسمت الأفعال الاختيارية إلى ما لا يقضي العقل فيها بحسن ولا قبح، وإلى ما يقضي.
فالأول لهم فيها ثلاثة مذاهب:
الأول: الحرمة وثبوت الحرج في حكم الشرع.
وثانيها: الإباحة أي: الإذن وعدم الحرج.','2','108',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (290,'وثالثها: التوقف وفسر تارة بعد الحكم.
ورد بأنه قطع لا وقف، وتارة بعدم العلم بأن هناك حكمًا أم لا، أو أنه الحظر أو الإباحة.
فإن قلت: كيف يتصور القول بالحظر أو الإباحة بالمعنى المذكور.
مع انه لا شرع ولا حكم من العقل بحسن أو قبح.
أجيب: بأن معناه أن الفعل الذي لا يدرك العقل فيه بخصوصه جهة محسنة أو مقبحة كأكل الفواكه مثلًا، ولا يحكم فيه بخصوصه بحكم نفصيلي في فعلٍ فعل بحكم العقل فيها على الإجمال إنها محرمة عند الشارع وإن لم يظهر الشرع ولم يبعث النبي، أو مباحة.
وأيضًا يجوز أن يجزم بأحدهما ولا يكون عالمًا بالحسن ولا بالقبح لا لنفسه، بل لأمر آخر مثل أن يجزم بالحرمة للاحتياط.','2','109',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (291,'والثاني: ينقسم عندهم إلى الأقسام الخمسة المشهورة من: واجب، ومندوب، ومحرم، ومكروه، ومباح؛ لأنه لو اشتمل أحد طرفيه على مفسدة، فإما فعله فحرام، أو تركه فواجب، وإن لم يشتمل عليها، فإن اشتمل على مصلحة، فإما فعله فمندوب أو تركه فمكروه، وإن لم يشتمل عليها أيضًا فمباح.
وأنت مع هذا الخبير بما في كلام المصنف في هذه المسألة من مناقشة.
وقوله: «وفسره الإمام بعد الحكم» سهو وإن الذي فسره به الإمام هو ما اختاره بقوله: والأولى.','2','110',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (292,'وإن سلم أن الإمام فسره بهذا، فلا اعتراض على الإمام، لأن الشيخ فرع هذا الكلام على تقدير حكم العقل، لا على مذهبه، فلا يعد على تقدير كونه حاكمًا أن لا يحكم فيما يستقل بإدراك جهة حسنة وقبحة إلى أن يرد الشرع.
ولا يعترض أيضًا بأن هذا جزم لا توقف؛ لأن مراده بعدم الحكم المذكور من الحظر أو الإباحة، لا عدم الحكم مطلقًا، فالتوقف في غيرهما.','2','111',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (293,'وقوله: لا يتوقف إلى آخره جواب عن سؤال تقديره: إن تعلق الحكم بالأفعال الاختيارية حادث، فيجوز أن يكون المراد بعدم الحكم قبل البعثة عدم التعلق كما مر.
والجواب: أن التعلق لا يتوقف على البعثة أيضًا عند الأشعري لجواز التعلق قبل الشرع، وإن لم يعلم المكلف إذ غاية ما يلزم منه أنه تكليف بالمحال، وهو جائز على رأيه.
وقد نظر؛ لأنه لا يلزم من تجويز التكليف بالمحال أن يكون التعلق سابقًا على البعثة، وقد قام الدليل على أن هذه الصورة لم تقع، وهو ما سبق من قوله تعالى: {وما كنا معذبين حتى نبعث رسولًا} الآية
احتج الأولون: بأنها أي الأفعال: الاختيارية، كأكل','2','112',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (294,'الفواكه اللذيذة، والمراتب البية، والملابس السنية، والمناكح الشهية انتفاع خال عن أمارة المفسدة؛ إذ الغرض أنه كذلك، وعن مضرة المالك؛ لأن مالكها هو الله -تعالى- وهو لا يتضرر بشيء، فتباح كالاستظلال بجدار الغير، والاقتباس من ناره بغير إذنه، حيث تحقق الانتفاع الخالي عن أمارة المفسدة؛ إذ هو المفروض.
فاندفع قولهم: التعبير بالاستضاءة أولى، فالإباحة دائرة مع هذه الأوصاف وجودًا وعدمًا، فكانت علة، لأن الدوران يفيد العلية، وهي موجودة في مسألتنا فكانت مباحة.
وقوله: «عن أمارة المفسدة» ولم يقل: عن المفسدة لأن العبرة في','2','113',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (295,'القبح إنما هو بالمفسدة المستندة إلى الأمارة، فأما المفسدة الخالية عن الأمارة فلا اعتبار بها، ألا تراهم يلومون من يجلس تحت حائط مائل وإن سلم دون السليمة وإن وقعت عليه.
وأيضًا المآكل اللذيذة خلقت لغرضنا لامتناع العبث أي لو لم تخلق لغرض كان عبثًا خاليًا عن الحكمة وأنه نقص وهو على الله محال.
وذلك الغرض لا جائز أن يرجع إلى الله تعالى لتعاليه واستغنائه عنه، فثبت أنها مخلوقة لغرضنا.
وليس ذلك الغرض للإضرار اتفاقًا فهو للنفع.
وهو إما التلذذ، أو الاغتذاء، أو الاجتناب مع الميل إليها، أو الاستدلال بها، وتشهى طعومها على وجود الصانع','2','114',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (296,'وكمال قدرته تعالى.
ولا يحصل جميع ذلك إلا بالتناول، أما الاغتذاء والتلذذ فواضح.
وأما الاجتناب مع الميل فكذلك، وإلا لم يكن ميل.
وأما الاستدلال: فلأنه موقوف على معرفتها، ومعرفة المذوقات موقوف على التناول وتحصيل الغرض مطلوب، وهو وإن لم يكن واجبًا فلا أقل أن يكون مباحًا.
وأجيب عن الأول: بمنع حكم الأصل وهو إباحة الاستظلال دون إذن مالكه؛ لكونه من الأفعال الاختيارية فهو من صور النزاع وإباحته إنما ثبتت بالشرع.
وبأن علية الأوصاف أيضًا ممنوعة.','2','115',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (297,'يعني أنا وإن سلمنا إباحة الاستظلال، لكن لا نسلم أن العلة لإباحة هذه الأوصاف التي ذكرتم، وهو الانتفاع الخالي عن أمارة المفسدة إلى آخره والقياس إنما يصح عند اشتراكهما في العلة.
فإن قالوا وجدنا الإباحة دائرة مع هذه الأصاف وجودًا وعدمًا فكانت هي العلة.
قلنا: دلالة الدوران على كون الوصف علة للشيء الذي دار','2','116',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (298,'معه دلالة ضعيفة، وإليه أشار بقوله: «والدوران ضعيف» إذ لا يفيد القطع بل الظن -على رأي المصنف- والمطلب قطعي ولا يضرنا ادعاءهم أن الدوران يفيد القطع؛ لأنه لو كان ضروريًا لما اختلف فيه وأدلتهم النظرية تقبل الطعن.
وأجيب عن الثاني: أن أفعاله -تعالى- لا تعلل بالغرض، لأنه إن كانت العلة قديمة لزم قدم الفعل، وإن كانت حادثة لزم أن يكون لها علة ويتسلسل، فلا نسلم أنه خلقها لغرض ولا يلزم العبث؛ إذ له أن يفعل ما يشاء كما مر.
وإن سلم فالحصر في هذه الأشياء الأربعة (التي ذكرتموها)','2','117',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (299,'ممنوع ولما لا يجوز أن يكون الغرض يعود إلينا، ولا يتوقف على التناول كالتنزه بمشاهدة ألوانها، أو الاستنشاق بروائحها، أو الاستدلال على معرفة الصانع باختلاف ألوانها وأشكالها الغريبة، أو يشتهيها فيصير عنها فيثاب عليه، فلا يلزم من عدم الإباحة عبث.
وقال الآخرون: الأفعال الاختيارية تصرف في ملك الغير بغير إذن المالك وهو الله ٠ تعالى- فيحرم كما في الشاهد أي: المخلوق فإنه يحرم التصرف في ملكه بغير إذنه.','2','118',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (300,'ورد: بأن الشاهد يتضرر به، أي: بالتصرف (في ملكه) لضيق ملكه دون الغائب عن أبصارنا، وهو الله تعالى لتنزهه وتعاليه عن التضرر ولسعة ملكه.
تنبيه: فيه جواب وسؤال أورده القائلون بالإباحة والحرمة على القائلين بالتوقف فقالوا: هذه الأفعال إن كان ممنوعًا عنها فتكون محرمة وإلا فتكون مباحة، ولا واسطة بينهما.
فأجاب: بأن عدم الحرمة لا يوجب الإباحة؛ لأن عدم المنع أعم من الإذن فالمباح ما أذن في فعله وتركه، وعدم المنع أعم من الإذن،','2','119',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (301,'فإن فعل غير المكلف، غير ممنوع، ولا يسمى مباحًا، والأعم لا يستلزم الأخص، فعدم الحرمة لا يستلزم الإباحة الشرعية، وفيه نظر ذكرته في الشرح مع زيادة تحقيق.','2','120',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (302,'<span data-type="title" id=toc-29>الفصل الثاني: في المحكوم عليه</span>
وفيه مسائل:
الأولى: المعدوم يجوز الحكم عليه،','2','121',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (303,'والمراد به أن المعدوم الذي علم الله تعالى أنه يوجد بشرائط التكليف توجه عليه حكم في الأل، حكم بما يفهمه ويفعله فيما لا يزال.
فالتعلق عقلي لا تنجيزي وذلك جائز غير محال.','2','122',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (304,'كما أنا مأمورون بحكم الرسول -صلى الله عليه وسلم- مع أن ذلك الأمر كان حال عدمنا.
قيل اعتراضًا على هذا الدليل: الرسول -صلى الله عليه وسلم- أخبر أن من سيولد من الكلفين فالله تعالى سيأمره، فالرسول حينئذ مخبر لا آمر.
قلنا: أمر الله تعالى في الأزل، معناه أن فلانًا إذا وجد','2','123',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (305,'بشرط التكليف فهو مأمور بكذا.
قال العراقي: هذا يدل على أن المراد بالأمر هنا الإخبار، فإطلاق الأمر عليه مجاز.
وقد أبطل في المحصول كون الأمر معناه الإخبار في أوائل الأوامر والنواهي، واستشكله هنا.
وما نقله المصنف جزم به صاحب (الحاصل ونقله في) المحصول','2','124',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (306,'عن بعض الأصحاب
قيل اعتراضًا على هذا الجواب: الأمر في الأزل، سواء كان بمعنى الإخبار، أو بمعنى الإنشاء، ولا سامع ينقل، ولا مأمور يمتثل عبث وسفه، وهو غير جائز على الحكيم.
فإن من يجلس في دار ويأمر بأوامر مختلفة ويخبر بأخبار متعددة من غير حضور أحد عد عابثًا سفيهًا، بخلاف أمر الرسول -صلى الله عليه وسلم- فإنه وجد (هناك حينئذ) من سمع عنه وبلغ إلينا بالنقل.','2','125',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (307,'قلنا: هذا مبني على القبح العقلي وهو باطل، ومع هذا أي مع تسليمنا القول بالقبح العقلي فلا سفه في مسألتنا.
وذلك لأنه ليس المراد بالأمر أن يكون في الأزل لفظ هو أمر ونهي، بل المراد به معنى قديم قائم بذات الله تعالى وهو اقتضاء الطاعة من العباد، وأن العباد إذا وجدوا يصيرون مطالبين بذلك الطلب، وهذا لا سفه فيه، كما لا سفه في أن يكون في النفس طلب التعلم من ابن سيولد.
الثانية:','2','126',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (308,'لا يجوز تكليف الغافل، وهو من لا يفهم الخطاب، كالساهي والنائم والمجنون، من أحال تكليف المحال.
ومفهومه أن بعض المجوزين يجوزون تكليف الغافل.
(وهم الأقل) وأكثرهم على أنه (لا يجوز)
وإنما قلنا: أن هذا مفهومه لأنه الواقع، ولأنه (أي','2','127',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (309,'الاختلاف) صحيح من جهة المعنى.
وقوله: من أحال تكليف المحال -بغير باء- يحتمل أنه لا فرق عنده بينه وبين ما بالباء.
أو يرى الفرق -كما قيل به-
وهو أن تكليف المحال: الخلل فيه في المأمور.
والتكليف بالمحال الخلل فيه في المأمور به، وكلام المصنف مستقيم على الأول.','2','128',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (310,'واعلم أن فهم المكلف للتكليف شرط لصحة التكليف عند المحققين.
وذلك بأن يفهم (المخاطب الخطاب قدر ما يتوقف عليه الامتثال لا بأن يصدق) بأنه مكلف ويعلم أنه مكلف، وإلا لزم الدور وعدم تكليف الكفار، فعلى هذا لا حاجة إلى استثناء التكليف بالمعرفة على ما سيجئ، لأن التكليف بالمعرفة ليس من تكليف الغافل في شيء لأن معناه: أن لا يفهم التكليف، والخطاب.
وهذا قد فهمه، إن لم يصدق به، ولم يعلم أنه مكلف.','2','129',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (311,'فلا يكلف الغافل؛ لأن الفعل امتثالاً يعني أن الإتيان بالفعل على قصد الطاعة والامتثال لأمر الله تعالى، يعتمد العلم بالأمر وكذا الفعل المأتي به.
لأن الامتثال: هو أن يقصد إيقاع الفعل المأمور به على سبيل الطاعة.
ويلزم من ذلك علمه بتوجه الأمر نحوه، وبالفعل.
ولا يتصور هذا من الغافل إذ لا شعور له بالأمر ولا بالفعل، فيكون تكليفه محالاً.
فإن قيل: الفعل المجرد عن قصد الامتثال قد يصدر من الغافل اتفاقًا،','2','130',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (312,'وحينئذ إذا علم الله - تعالى - وقوع الفعل من شخص فلا استحالة في تكليفه به.
وقوله: ولا يكفي مجرد الفعل، جواب عن هذا السؤال المقدر في كلامه، أي: لا يكفي مجرد وقوع الفعل من غير قصد إلى إيقاعه في سقوط التكليف لقوله - صلى الله عليه وسلم -: "إنما الأعمال بالنيات" متفق عليه.
أي: إنما تصح الأعمال بالنيات، فكل عمل لم يقترن بالنية لا اعتبار له.','2','131',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (313,'وليس اعتبار طلاق السكران وقتله وإتلاقه من قبيل التكليف، بل من قبيل ربط الأحكام بأسبابها، كاعتبار قتل الطفل وإتلافه فإنه سبب لوجوب الضمان والدية من ماله على وليه وهو غير مكلف به قطعًا، بل كربط وجوب الصوم بشهود الشهر.
ونوقض بوجوب المعرفة فإن التكليف بها واقع مع أنه تكليف للغافل؛ إذ لو كان المكلف عارفًا بالآمر الذي هو الله تعالى لم يكلف مرة أخرى، وإلا لزم تحصيل الحاصل.','2','132',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (314,'وأجيب: بأنه مستثنى عن هذه القاعدة، وهو أنه لا يجوز تكليف الغافل إلا في هذه الصورة.
ولما استشعر المصنف ضعف هذه المناقضة وجوابها كما حققته لك في أول المقالة بناه وقال: ونوقض وأجيب.
الثالثة: الإكراه الملجئ: وهو الذي لا يبقى للشخص معه قدرة ولا اختيار، كالملقى من شاهق (يمنع التكليف بالملجأ إليه أو بنقيضه أو','2','133',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (315,'بهما) لزوال القدرة عنه، لأن الملجأ إليه واجب الوقوع، ونقيضه ممتنع الوقوع، (ولا قدرة على واحد من الواجب والممتنع.
وليس هذا كوجوب الفعل عند الداعية) والشروع فيه مع بقاء التكليف؛ لأنه وإن كان واجب الصدور ونقيضه ممتنع إلا أن أصل القدرة باق معهما، بخلاف الإكراه الملجئ، فإنه يرفع أصل القدرة فاقترقا.
قال القاضي أبو بكر في مختصر التقريب: هذا الإكراه أي الملجئ لا يسمى إكراهًا عند المحققين؛ لأن الإكراه لا يتحقق إلا مع تصور اقتدار','2','134',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (316,'وأفهم كلامه: أن الإكراه غير الملجئ، وهو الذي لا يصل للأول لا يمنع التكليف، كما لو قيل له: إن لم تقتل هذا وإلا قتلتك، وعلم أنه إن لم يفعل وإلا قتله.
فالمكره: لا مندوحة له عما أكره عليه إلا بالصبر على ما أكره به.
ونقل عن الأشاعرة: (لأن الفعل ممكن والفاعل متمكن وله اختيار).','2','135',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (317,'قال العراقي: وأما الفقهاء فقالوا: لا يباح بالإكراه القتل ولا الزنا في حق الرجل بالاتفاق، كما نقله الرافعي في الجنايات.','2','136',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (318,'والمرأة اختلف كلام الرافعي فيها: والظاهر فيها الإباحة.
قالوا: وتباح السرقة، وشرب الخمر، والإفطار، وإتلاف مال الغير والخروج من الصلاة والتلفظ بالكفر.','2','137',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (319,'الرابعة: التكليف يتوجه ويتعلق بالمكلف عند المباشرة للفعل والشروع فيه، والموجود قبل ذلك ليس أمرًا، بل هو إعلام له بأنه في الزمان الثاني سيصير مأمورًا، كما نقله في المحصول عن أصحابنا، أي: الأشاعرة.
واستشكل: لأنه يؤدي إلى سلب التكاليف، فإن المكلف يقول: لا أفعل حتى أكلف ولا أكلف حتى أفعل.','2','139',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (320,'وأجيب: بأنه قبل المباشرة متلبس بالترك، فتوجه إليه التكليف بترك الترك، وهو فعل؛ فإنه كف النفس عن الفعل، فقد باشر الترك، فتوجه إليه التكليف بترك الترك حالة مباشرته للترك وذلك الفعل، وصار اللوم والذم قبل المباشرة على التلبس بالكف عن الفعل المنهي ذلك الكف عنه.
لأن الأمر بالشيء يفيد النهي عن تركه.
ذكره بنحوه إمام الحرمين في تكليف ما لا يطاق.
وقال: الذهاب إلى أن التكليف عند الفعل مذهب لا يرتضيه لنفسه عاقل.
وقال هو وغيره: الأشعري لم ينص على جواز التكليف بما لا','2','140',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (321,'يطاق، وإنما أخذ من قاعدتين:
أحدهما: أن القدرة مع الفعل (والتكليف قبل ذلك).
والثانية: أن الأفعال مخلوقة لله تعالى.
فالمصنف اختار عكس مذهب الأشعري.
وقال المحقق شرحًا لكلام ابن الحاجب (والتابع فيه للآمدي):
التكليف بالفعل ثابت قبل حدوثه وينقطع بعد الفعل اتفاقًا، وهل هو باق حال حدوثه لا ينقطع؟
قال الأشعري به، ومنعه إمام الحرمين، والمعتزلة.','2','141',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (322,'ولا يتحقق مع الشيخ ما يصلح محلاً للنزاع فنقول:
إن أراد أن تعلقه لنفسه فلا ينقطع فحق، لكنه لا ينقطع بعد حدوثه كما لا ينقطع معه، لأن حقيقة التكليف أنه تكليف بالفعل، وطلب له، سواء اعتبر حال حدوث الفعل، أو قبله، أو بعده.
وقد قال بأنه ينقطع بعد الفعل.
وإن أراد أن تنجيز التكليف باق بعد، فهو باطل؛ لأنه تكليف (بغير الممكن، لأنه تكليف بإيجاد) الموجود، وهو محال.
ولأنه تنتفي فائدة التكليف وهو: الابتلاء لأنه إنما يتصور عند التردد في الفعل والترك وأما عند تحقق الفعل فلا.','2','142',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (323,'وحمل بعضهم كلام المصنف على كلام ابن الحاجب فقال:
قوله: التكليف يتوجه عند المباشرة، أي: متوجه عند المباشرة، فكأن الكاتب صحفه فعلى هذا لا إشكال في تقرير الدلائل، وموافقة المسائل.
قلت: ولا حاجة إلى قوله: متوجه - بالميم - بل يمكن تأويله على بابه، بمعنى أنه يتوجه عند المباشرة أيضًا، كما أنه متوجه قبلها، ولا تصحيف.
لكن قال الشيخ سعد الدين: الذي نقل عن الأشعري في الكتب المشهورة أن التكليف إنما يتعلق عند المباشرة لا قبلها.
وقال الأبهري: وكأن الشارح - يعني المحقق - كثيرًا ما يقول عند قراءة هذا الموضع عليه: ليس هذا التحرير - يعني الذي حرره ابن الحاجب - على ما ينبغي فلذلك بنى عليه ما بنى من أنه لا يتحقق مع الشيخ محل النزاع.','2','143',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (324,'فعلى هذا لا ينبغي أن يقرر كلام المصنف (على كلام ابن الحاجب).
وقال الكرماني: لا خلاف هنا في أن التكليف العقلي أزلي أو غير أزلي، ولا في التكليف التنجيزي، المعبر عنه بالتعلق.
إذ ذلك متوجه بدخول الوقت اتفاقًا، بل في أن التكليف إنما هو عند المباشرة، أي بنفس وقوع الفعل منه، أو بالإيقاع الذي الوقوع مترتب عليه، ثم بسطه.
وهنا أبحاث حسنة في الشرح ينبغي الإطلاع عليها في هذا المقام، تركتها خوف التطويل.
وقال المعتزلة: بل يتوجه التكليف قبلها، أي: قبل المباشرة.','2','144',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (325,'لنا: أن القدرة حينئذ أي: موجودة عند الفعل، فقبله ليس بقادر، والقدرة شرط التكليف عندكم.
قيل: التكليف الذي أثبتناه قبل المباشرة ليس تكليفًا بنفس','2','145',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (326,'الفعل، بل تكليف في الحال بالإيقاع للفعل في ثاني الحال، أي: في الزمن الثاني.
قلنا: الإيقاع المكلف به إن كان نفس الفعل المأمور به، فمحال التكليف به في الحال قبل الفعل؛ لأنه يلزم من امتناع التكليف بالفعل قبل التلبس به امتناع التكليف بالإيقاع؛ لأن الفرض أنه هو، وإن كان الإيقاع غيره، أي: غير الفعل، فيعود الكلام إليه ويتسلسل، يعني يعود الكلام إلى هذا الإيقاع، هل وقع التكليف به في حال وقوعه أو قبله؟
فإن كان الأول فيلزم أن يكون التكليف حال المباشرة وهو المدعى.
وإن كان قبله لزم أن يكون مكلفًا بما لا قدرة له عليه؛ لأن القدرة مع الفعل.
فإن قالوا: التكليف إنما هو بإيقاع هذا الإيقاع فينقل الكلام إليه ويؤدي إلى التسلسل، أو ينتهي إلى إيقاع يكون التكليف به حال المباشرة','2','146',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (327,'وهو المدعى.
ولا يقال: إيقاع الإيقاع نفس الإيقاع، لأن الإضافة تدل على المغايرة.
قالوا - أي المعتزلة: الفعل عند المباشرة، واجب الصدور، أي الوقوع، لوجود علته التامة، وهي القوة المستجمعة لشرائط التأثير، وكلما كان واجب الوقوع. لا يكون مقدورًا لامتناع تركه، إذ القادر هو: الذي إن شاء فعل وإن شاء ترك.
وإذا لم يكن مقدورًا لم يكن مكلفًا به، وإذا بطل التكليف عند المباشرة لزم التكليف قبلها.','2','147',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (328,'قلنا: الفعل حال القدرة، والداعية كذلك، أي: واجب الوقوع مع أنه مقدور عندكم.
والقدرة: القوة المستجمعة لشرائط التأثير.
وقالت المعتزلة: القوة التي من شأنها التأثير.
والداعية: هي الميل الحاصل عقيب النفع.
والمجموع من القدرة والداعية هي العلة التامة.','2','148',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (329,'<span data-type="title" id=toc-34>الفصل الثالث: في المحكوم به</span>
وفيه مسائل:
الأولى: التكليف بالمحال جائز عقلاً','2','149',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (330,'وهو ظاهر كلام الأشعري (وعليه جل أصحابه) (بل صرح به الشيخ في كتابه المسمى "بالإيجاز" حيث قال: إن تكليف العاجز الذي لا يقدر على شيء.
وتكليف المحال الذي لا يقدر عليه المكلف صحيح، وقد وجد تكليف الله تعالى لعباده بما هو المحال.
نقله الكرماني في النقود والردود) واختاره الإمام الرازي','2','150',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (331,'لأن حكمه تعالى لا يستدعي غرضًا، فجائز أن يكلف بالمحال.
فإن كان لذاته فالأمر به للإعلام، بأنه يعاقب المأمور به ألبتة، لأن لله - تعالى - أن يعاقب من شاء.
وإن كان محالاً لغيره فالأمر لفائدة الأخذ في المقدمات ليظهر طاعته بالبشر والأخذ، أو عصيانه بالكراهية والترك.
قيل: لا يتصور وجوده أي المحال، فلا يطلب؛ لأن التكليف به هو الطلب، وهو استدعاء الحصول، واستدعاء حصوله فرع تصور الوقوع، وموقوف عليه، فإذا (انتفى انتفى) وإنما','2','151',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (332,'قلنا: إنه لا يتصور وقوعه، لأنه لو تصور لتصور مثبتًا، ويلزم منه تصور الأمر على خلاف ماهيته، فإن ماهيته تنافي ثبوته، وإلا لم يكن ممتنعًا، فما يكون ثابتًا فهو غير ماهيته.
وحاصله: أن تصور ذاته غير ذاته، ويلزم قلب الحقائق
ويوضحه أنا لو تصورنا أربعة ليس بزوج، وكل ما ليس بزوج ليس بأربعة، فقد تصورنا أربعة ليس بأربعة، وهو ظاهر البطلان.
قلنا: إن لم يتصور امتنع الحكم باستحالته.','2','152',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (333,'وتحقيقه: أنا لا نسلم أن المحال لا يتصور وجوده، بل يتصور ثبوته في النفس، لأنا نحكم عليه بالحكم الثبوتي بأنه معدوم ومستحيل، وثبوت الشيء ليغره فرع ثبوته في نفسه فهو ثابت، وإذ ليس هو في الخارج فهو في النفس وذلك كاف في طلبه.
فلو امتنع تصوره لامتنع الحكم باستحالته (وفيه نظر).
والتكليف بالمحال: وإن كان جائزًا عقلاً فهو غير واقع شرعًا','2','153',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (334,'بالممتنع لذاته: كإعدام القديم، وقلب الحقائق، كأن ينقلب الحيوان حجرًا، أي مع بقاء حقيقة الحيوانية، وإلا لم يكن ممتنعًا لذاته.
وقيل: لم يقع مطلقًا، وحكاه الإمام في الشامل عن الجمهور وظاهر اختياره كالمصنف.
وقيل: وقع مطلقًا.
وقوله: للاستقراء دليل على عدم وقوع التكليف بالمحال لذاته،','2','154',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (335,'أي: تتبعنا الأحكام الشرعية فما وجدنا شيئًا يكون ممتنعًا لذاته.
فإن التكليفات الشرعية من الصلاة والزكاة والصوم والحج وغيرها معلوم، فالاستقراء التام معلوم.
وأيضًا الممتنع لذاته لا يكون في وسع المكلف، وما لا يكون في الوسع لا يكون مكلفًا به لقوله تعالى: } لا يكلف الله نفسًا إلى وسعها {.
وفيه نظر؛ لأن الآية تقتضي عدم وقوع التكليف بما ليس في الوسع، أعم من أن يكون لذاته أو لغيره.
فلا يصح الاستدلال بها على القول المفصل إلا مع بيان المخصص','2','155',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (336,'وجوابه: أنه سيأتي تخصيصه في كلام المصنف.
(وقال الكرماني: ما لا يطاق على مراتب:
أدناها: أن يمتنع العقل لعارض لعلم الله بعدم وقوعه أو اختياره.
والتكليف بهذا جائز، بل واقع إجماعًا، وإلا لم يكن العاصي مكلفًا.
وأقصاها: أن يمتنع لنفس مفهومه، كجمع الضدين.
وهذا أيضًا مما لا نزاع فيه للاتفاق على امتناعه.
والمرتبة الوسطى هي المتنازع فيها: فإنا معشر الأشاعرة نجوزه وإن لم يقع.
والمعتزلة تمنعه، وهو ما لا يتعلق به القدرة الحادثة عادة.
ولهذا يقال: تكليف ما لا يطاق بصيغة المجهول، لا ما لا يطيق بالمعرف، دفعًا لتوهم اختصاصه بفاعل معين).
قيل: التكليف بالمحال لذاته وقع، لأن الله تعالى أمر أبا لهب','2','156',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (337,'بالإيمان بما أنزل؛ لأنه أمر بتصديق رسوله - صلى الله عليه وسلم - في جميع ما جاء به، ومنه أنه لا يؤمن، فيكون مكلفًا بأنه يصدقه في أنه لا يصدقه في شيء مما أتى به من الله - تعالى - وهو محال.
وقوله: فهو جمع بين النقيضين يحتمل أنه تكليف بالنقيضين؛ لأن التصديق في الإخبار بأنه يصدقه في شيء، يستلزم عدم تصديقه في ذلك ضرورة أنه صدقه في شيء.
والتكليف بالشيء تكليف بلوازمه.
ويحتمل أنه تكليف بجمع النقيضين، أي: بالتصديق في حال عدم وجوب التصديق بناء على إخبار الله تعالى بأنه لا يصدقه.
قلنا: لا نسلم أنه أمر بعد ما أنزل أنه لا يؤمن، يعني لو أنزل','2','157',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (338,'الله - تعالى - أولاً أنه لا يؤمن، ثم أمره بالإيمان بجميع ما أنزل كان جمعًا بين النقيضين لكنه ليس كذلك، بل أمره أولاً بالإيمان، فلما لم يؤمن، أخبر الله - تعالى - نبيه (صلى الله عليه وسلم) أنه لا يؤمن.
على أن إيمان أبي لهب ممكن في نفسه، فلا يصير ممتنعًا لذاته بسبب إخبار الله تعالى.
غايته أنه ممتنع لتعلق علم الله - تعالى - به أو بإخباره، فليس من الممتنع لذاته في شيء، وإن كنا نقطع بعدم إيمانه، وهنا زيادة تحقيق مذكورة في الشرح.
(قال العراقي: والإجماع متحقق على أنه مكلف بالإيمان (قبل وبعد) فجواب المصنف باطل).','2','158',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (339,'الثانية:
الكافر مكلف بالفروع خلافًا للمعتزلة، وفرق قوم بين الأمر والنهي.
اعلم أن الشرط الشرعي للوجوب شرط في التكليف بوجوبه ووجوب أدائه بالاتفاق، والشرط لوجوب الأداء شرط في وجوب أدائه.','2','160',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (340,'ولا يشترط في التكليف بالفعل حصول الشرط الشرعي لصحة ذلك الفعل، كالإيمان للطاعات والطهارة للصلاة.
بل يجوز التكليف بالفعل وإن لم يحصل شرطه شرعًا.
خلافًا لأصحاب الرأي، وأبي حامد، أو أبي إسحاق الإسفراييني (في غير الإيمان) والمعتزلة كما نقله المصنف عنهم.
وفي المحصول: أن أكثر المعتزلة على الأول.','2','161',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (341,'وفي البرهان: أنه ظاهر مذهب الشافعي - (رحمه الله) -
وقيل: مكلف بترك النواهي دون الأوامر.
والمسألة مفروضة في بعض جزئيات محل النزاع، وهو تكليف الكافر بالفروع مع انتفاء شرطها، وهو الإيمان حتى يعذب بترك الفروع كما يعذب بترك الإيمان.
والعلماء يفرضون المسائل الكلية في بعض الصور الجزئية تقريبًا للفهم وتسهيلاً للمناظرة، لأنه إذا ثبت فيه ثبت في الجميع، لعدم القائل بالفصل لاتحاد المأخذ.
تنبيه:','2','162',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (342,'في المحصول في أثناء الاستدلال ما يقتضي أن الخلاف في غير المرتد، لكن القاضي عبد الوهاب في الملخص أجرى الخلاف فيه. كما حكاه القرافي.
لنا على وقوعه: أن الآية الآمرة بالعبادة كقوله تعالى:','2','163',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (343,'{يا أيها الناس اعبدوا ربكم}.
وقوله تعالى: } ولله على الناس حج البيت {.
وأمثالهما: تتناولهم لكونهم من الناس، والكفر غير مانع لإمكان إزالته؛ إذ امتثال الكافر حال كفره ممكن في نفسه بأن يسلم ويصلي.
وتحقيقه: أن الكفر الذي لأجله امتناع الإمتثال ليس بضروري، فكيف امتناع الامتثال التابع له.
وحاصله: أن الضرورة الوصفية لا تنافي الإمكان الذاتي.
(وكلما) يمكن إزالته لا يمنع التكليف، كالحدث المانع من الصلاة، فإنه لما أمكن إزالته لم يمنع التكليف بالصلاة، وإذا كان','2','164',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (344,'المقتضى موجودًا والمانع مفقودًا ثبت التكليف عملاً بالمقتضى السالم عن المعارض.
وأيضًا: (لو لم يكونوا مكلفين بالفروع لما أوعدهم الله تعالى على تركها، لكن) الآيات الموعدة على ترك الفروع كثيرة مثل (قوله تعالى): } وويل للمشركين الذين لا يؤتون الزكاة {.
وقوله تعالى: } ما سلككم في سقر قالوا لم نك من المصلين ولم نك نطعم المسكين {.
وقوله تعالى: } ومن يفعل ذلك يلق أثامًا {','2','165',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (345,'وهو عام للعقلاء، فصرح بتعذيبهم بترك الزكاة والصلاة.
وقوله: } ومن يفعل ذلك {الإشارة فيه إلى ما سبق من الشرك وقتل النفس والزنا.
وأيضًا أنهم كلفوا بالنواهي لوجوب حد الزنا عليهم، فيكونون مكلفين بالأمر، قياسًا عليها، والجامع بينهما الطلب.
قيل: الفرق بينهما أن الانتهاء عن المنهيات مع الكفر أبدًا ممكن، دون الامتثال بالواجبات مع الكفر؛ لأن النية في الواجبات لابد منها. والنية من الكافر غير صحيحة.','2','166',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (346,'وأجيب: بأن مجرد الفعل والترك من غير نية امتثال أمر الشارع لا يكفي في الامتثال فالإتيان بالأمر وترك النهي لغرض امتثال حكم الشرع يتوقف على الإيمان.
فاستويا، أي: الأمر والنهي، فصح القياس، وفيه نظر.
قال المصنف في - الغاية القصوى: لأن النية لا تعتبر في التروك كإزالة النجاسة، فالفرق واضح.
ونقل عنه أنه أجاب عن هذا بما حاصله:
ما تعني بقولك: الترك لا يحتاج إلى نية؟ إن أردت صورة الترك فمسلم، ولا فرق، فإن صورة الفعل أيضًا لا تحتاج إلى (نية.','2','167',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (347,'وإن أردت الترك الشرعي، فلا نسلم أنه لا يحتاج إلى) النية.
وأجاب عن هذا بأن المكلف إذا ترك المنهي عنه سقط عنه العقاب، وإن لم ينو بخلاف المأمور به، فإنه ما لو ينو لم يحصل الإجراء.
ولك أن ترجع نظر المصنف إلى اعتراض الإمام على أصل الدليل
وهو أنا لا نسلم تكليف الكفار بالنواهي، وإنما أقيم حد الزنا عليهم لالتزامهم أحكامنا أو يكون وجوب حد الزنا عليهم من قبيل الأسباب كقتل الطفل وإتلافه وفيه نظر.','2','168',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (348,'قيل: لو وجبت الفروع عليهم كالصلاة مثلاً، فإما أن تجب حال الكفر، أو بعده، وكلاهما باطل؛ إذ لا تصح مع الكفر. ولا قضاء بعده إجماعًا فانتفى فائدة التكليف.
قلنا: نختار الوجوب حال الكفر بأن يزيل الكفر ويأتي بالفروع كالمحدث، ويكون زمن الكفر زمنًا للتكليف فقط لا للإيقاع، المكلف به.
وسقوط القضاء، إما لأن القضاء بأمر جديد، وليس بينه وبين وقوع التكليف ولا صحته ربط عقلي، فلا يستلزم وجوب القضاء لا وقوع التكليف، ولا صحة وقوعه.','2','169',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (349,'وإما تخفيفًا عنه وترغيبًا في الإسلام.
قولك: فانتفى فائدة التكليف ممنوع أيضًا، فإنه وإن لم يصح الفعل مع الكفر، ولا يجب القضاء بعد الإسلام لكن له فوائد.
منها: تضعيف العذاب عليه في الآخرة.
واقتصر عليه المصنف، حيث قال: الفائدة تضعيف العذاب، لكون دليله دل عليه بخصوصه.
أو لأن غيره ليس من خطاب التكليف، بل من قبيل الأسباب، كما تقدم.
ومنها في الدنيا تنفيذ طلاقه وعتقه وظهاره، وإلزامه الكفارات وغير ذلك.
* * *','2','170',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (350,'الثالثة:
امتثال الأمر وهو الإتيان بالمأمور به على الوجه المطلوب شرعًا يوجب الإجزاء.
أي: سقوط الأمر، لأنه إن بقي الأمر متعلقًا به، أي: بعين ما أتى به.
فيكون أمرًا بتحصيل الحاصل، أو متعلقًا بغيره فيلزم أن لا يكون المأتي به أولاً كل المأمور به، فلم يمتثل بالكلية وقد فرضناه ممتثلاً.','2','171',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (351,'وقد يقال: المأتي به ثانيًا لا يكون نفس المأتي به أولاً بل مثله، فلا يكون تحصيلاً للحاصل، فلا يتم الدليل، على أنه قد لا يسلم أن القضاء عبارة عن استدراك ما قد فات من مصلحة الأداء، بل عن الإتيان بمثل ما وجب أولاً بطريق اللزوم.
وقال أبو هاشم: لا يوجبه، أي: امتثال الأمر لا يوجب الإجزاء كما لا يوجب النهي الفساد، بدليل صحة البيع وقت النداء.
والجواب: طلب الجامع بين الأمر والنهي، ثم الفرق بينهما.','2','172',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (352,'فإن قال: الجامع بأن كلا منهما طلب جازم، فالفرق أن النهي يدل على المنع من الفعل، ويجوز أن يكون الفعل ممنوعًا منه لتحقيق حكم آخر، كالنهي عن البيع وقت النداء، فإنه منع عنه لا لذاته، بل لتحقق الجمعة، وحينئذ إذا أتى به لم يفسد لكونه غير ممنوع لذاته، وأما الأمر فلا دلالة له إلا على اقتضاء الفعل فإذا أتى به فقد أتى بتمام المقتضى. وأدى ما عليه فينقطع عن التعلق.','2','173',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (353,'وإذا أردت تحقيق هذه المسألة على ما ينبغي فعليك بشرحي تيسير الوصول (إلى منهاج الأصول) من (المنقول والمعقول) الذي هذا ملخص منه فإنها مستوفاة فيه، ومقصودي من هذا المختصر مجرد حل','2','174',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (354,'كلام المصنف - رحمه الله تعالى - (ومن أراد الإشباع فعليه بذلك الشرح).
* * *','2','175',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (355,'الكتاب الأول
في الكتاب
أي القرآن، وهو الكلام المنزل على محمد - صلى الله عليه وسلم - للإعجاز بسورة منه.','2','179',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (356,'والاستدلال به يتوقف على معرفة اللغة؛ لأنه وارد بلغة العرب، ومعرفة أقسامها.
وهو أي الكتاب ينقسم إلى: أمر ونهي بحسب ذاته.
وبحسب مدلوله: إلى عام وخاص.
وبحسب كيفية دلالته إلى مجمل ومبين، وإلى ناسخ ومنسوخ.
لأن الدليل (قد يرد) لإثبات حكم أو لرفعه وهو الناسخ والمنسوخ.
والسنة مثل الكتاب في جميع الأقسام.','2','180',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (357,'قال العراقي: وهذه الأقسام في الإنشاء فقط.
فأما الأخبار فلا حظ فيها للأصولي.
فأطلق الكتاب وأراد به قسم الإنشاء منه.
وبيان ذلك في أبواب خمسة لكل قسم باب، وقدم بحث اللغات.
لكونها جنسًا لهذه الأحكام، ثم بحث الأمر والنهي؛ لأن النظر في ذات الشيء مقدم على أحواله، ثم قدم العام والخاص لأنهما من تعلقات الأمر والنهي، وبحث المجمل والمبين كبقية ذلك التعلق،','2','181',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (358,'وقدمهما على الناسخ والمنسوخ؛ لأن النسخ يطرأ على ثبات بأحد الوجوه المذكورة.

<span data-type="title" id=toc-39>الباب الأول: في اللغات</span>
وهي الألفاظ الموضوعة للمعاني، وهي تتوقف على أمور مذكورة في الفصل الأول.
وجمع اللغات مع أن الكلام في لغة العرب وهي واحدة، لاختلاف لغات القبائل.','2','182',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (359,'ولأن المباحث مشتركة بين جميع اللغات.
وفيه فصول:

<span data-type="title" id=toc-40>الفصل الأول: في الوضع</span>
أي في تحقيق وضع اللغات، وما يتعلق به، وهو ستة:
(الوضع، والموضوع، والموضوع له، وفائدته، والواضع، وطريق معرفته).
وذكرها في هذا الفصل على هذا الترتيب فقال: لما','2','183',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (360,'مست، أي اشتدت الحاجة إلى التعاون والتعارف لأن الله- تعالى- خلق الإنسان غير مستقل بمصالح معاشه، محتاجًا إلى مشاركة أبناء جنسه، لاحتياجه إلى غذاء ولباس ومسكن، والواحد لا يتمكن من تحصيل هذه الأشياء فضلًا عن صنعتها، فلابد من جمع يعاون بعضهم بعضًا، وذلك لا يتم إلا بأن يعرفه ما في نفسه، فاحتيج إلى شيء يحصل به التعريف.
والتعريف إما باللفظ، أو بالإشارة كحركة اليد والرأس والحاجب، أو بالمثال كالكتابة وشكل المطلوب، وكان اللفظ أفيد من الإشارة والمثال لعمومه.','2','184',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (361,'إذ يمكن التعبير به عن الذات والمعنى، والموجود والمعدوم، والحاضر والغائب، والقديم والحديث، وذي الشكل وغيره.
بخلاف الإشارة والمثال.
وأيسر لأن الحروف كيفيات تعرض للنفس الضروري للإنسان يحصل بطبيعته من غير كلفة ومشقة.
ولما كان الأمر ما ذكرنا وضعت الألفاظ بإزاء المعاني الذهنية دون الأمور الخارجية، لدورانها (أي الألفاظ) معها أي مع المعاني الذهنية.','2','185',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (362,'فإن من رأى شبحًا من بعيد وظنه حمارًا سماه حمارًا، وإن ظن فرسًا سماه فرسًا، وإذا حضر عنده وعلم أنه إنسان سماه إنسانًا.
فإذا دار اللفظ مع المعاني الذهنية، علم أن اللفظ موضوع بإزائها، ولو كانت موضوعة بإزاء الأمور الخارجية لتغير الخارجي بتغير الظنون، وهو باطل قطعًا.
واختار الشيخ أبو إسحاق الشيرازي أن الوضع بإزاء المعنى الخارجي.','2','186',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (363,'قال الإسنوي: ويظهر أن يقال: إن اللفظ موضوع بإزاء المعنى من حيث هو، أي مع قطع النظر عن كونه ذهنيًّا أو خارجيًّا.
وقوله: «ليفيد» أي وضعت لتفيد النسب أي الإضافة مثل غلام زيد، ودار هند.
والمركبات أي: المعاني المركبة، مثل: قام زيد، وزيد قائم،','2','187',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (364,'فوضع لفظ زيد لشخص معين، ولفظ غلام لآخر لنعلم عند الإضافة النسبية بينهما بالمالكية والمملوكية.
وكذا قام زيد ليعلم عند الإسناد صدور القيام من زيد.
وليس الغرض من الوضع إفادة الألفاظ للمعاني المفردة إذ لو كان كذلك لزم الدور.
ولذا أشار بقوله: «دون المعاني المفردة وإلا فيدور»، وذلك لأن الوضع موقوف على تصور المعنى، فلو توقف تصور المعنى على الوضع لدار.
ولا يجيء مثله في المركب لأن تصور معنى المركب موقوف على وضع الألفاظ لمعانيها المفردة من غير عكس.
هذا والدور مندفع؛ لأن فهم المعنى من اللفظ يتوقف على العلم بالوضع، وهو إنما يتوقف على فهمه في الجملة لا على فهم المعنى من اللفظ حتى يلزم الدور.','2','188',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (365,'ولم يثبت تعيين الواضع للغات هل هو الله تعالى أو البشر؟
والشيخ أبو الحسن الأشعري: زعم أن الله تعالى وضعه (أي','2','189',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (366,'اللفظ) ووقف عباده عليه، أي: أعلمهم بها، إما بالوحي أو بخلق أصوات تدل عليها وأسمعها
لواحد أو جماعة.
أو بخلق علم ضروري بها، لقوله تعالى: {وعلم آدم','2','190',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (367,'الأسماء كلها} دل على تعليمه- تعالى- الأسماء لآدم (صلى الله عليه وسلم).
وهو ظاهر في أنه الواضع دون البشر.
وكذلك الأفعال والحروف، إذ لا قائل بالفصل.
ولأن التعلم وهو الغرض يعسر بدونهما، ولأنهما أسماء في اللغة والتخصيص اصطلاح طرأ.
ولقوله تعالى: {ما أنزل الله بها من سلطان}.','2','191',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (368,'ولو لم تكن توقيفية لما ذمهم.
ولقوله تعالى: {ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم}.
والمراد اللغات بالاتفاق؛ إذ لا كثير اختلاف في «العضو»، وإذ بدائع الصنع في غيره أكثر.
فلولا أن توقيفي لما من.
ولأنها- أي: اللغات- لو كانت اصطلاحية لاحتاج الواضع في تعليمها لغيره إلى اصطلاح آخر، وذلك الاصطلاح الآخر لا يفيد','2','192',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (369,'لذاته، فلابد من اصطلاح آخر وهكذا ويتسلسل.
وهذا لا يثبت مذهب الشيخ، بل يبطل مذهب الاصطلاح فقط.
وأيضًا: لو كانت اصطلاحية لجاز التغيير في الاصطلاح.
كأن يصطلح المتأخرون على غير ما اصطلح عليه من قبلهم، إذ لا حجر في الاصطلاح.
فجاز أن يكون المراد بالصلاة والزكاة في زماننا غير ما اصطلح عليه في زمن الرسول، صلى الله عليه وسلم، فيرتفع الأمان عن الشرع.
وأجيب بأن الأسماء سمات الأشياء، أي: علاماتها','2','193',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (370,'وخصائصها، مثل أن علمه أن الخيل للكر والفر، والجمال للحمل، والثيران للزرع.
والضمير في {عرضهم} لا يصلح للأسماء إلا إذا أريد به المسميات، مع تغليب ذوي العقول على غيرهم.
سلمنا أن الأسماء هي اللغات، لكن يجوز أن تكون الأسماء قد وضعها طائفة أخرى غير بني آدم من الجن، أو غيرهم.
وإليه أشار بقوله: «أو ما سبق وضعها».
ولك أن تقول: التعليم للأسماء، والضمير للمسميات، وإن لم يتقدم لها ذكر في اللفظ للقرينة الدالة عليها.','2','194',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (371,'ويدل على أن التعليم للأسماء قوله تعالى: {أنبئوني بأسماء هؤلاء}. {فلما أنبأهم بأسمائهم}.
فأضاف الأسماء إلى المسميات، فدل على أنه ليس المراد بها المسميات أنفسها، بل الألفاظ الدالة عليها.
فلو كان التعليم للمسميات لما صح الإلزام بطلب الإنباء بالأسماء، ثم إنباؤه بنفسه بالأسماء.
وقوله: «أو ما سبق وضعها»، خلاف الظاهر؛ لأن الأصل عدم وضع سابق. لا يقال: يجوز أن يراد بالتعليم الإلهام بأن يضع نحو: {وعلمناه صنعة لبوس}؛ لأن المتبادر من تعليم الأسماء:','2','195',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (372,'تعريف وضعها لمعانيها.
فاحتمال الإلهام: احتمال مرجوح فلا ينافي الظهور، بل من لوازمه.
والتحقيق أن النزاع إن كان في الظهور، فالحق ما قال الشيخ.
وإن كان المطلوب هو اليقين فالحق ما قاله المصنف تبعًا للقاضي أبي بكر الباقلاني من التوقف وهو مبين في الشرح.
والذم في قوله تعالى: {إن هي إلا أسماء} للاعتقاد أي: لإطلاقهم لفظ الآلهة على الصنم مع اعتقادهم أنها آلهة.
إذ اللات والعزى ومناة أعلام على أصنام، فقرينة اختصاها بالذم دون سائر الأسماء دليل عليه.','2','196',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (373,'والتوقيف يعارضه الإقدار.
بيانه: أن الألسنة وإن كانت مجازًا عن اللغات لكن كون اختلافها من آيات الله تعالى لا يدل على أن جهة كونه آية، توقيف الله عليها، وتعليمها إيانا بعد الوضع لجواز أن يكون بتوفيق الله تعالى إيانا لوضعها، وإقدارنا عليه، فإن الجهتين سواء.
بل لا يبعد أن تكون الثانية أولى لكونها أدل على كمال القدرة وبديع الصنع.
ولا نسلم أنها لو كانت اصطلاحية لا تحتاج في تعليمها إلى اصطلاح آخر، بل يحصل التعليم بالترديد والقرائن، كما للأطفال، أي','2','197',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (374,'بترديد اللفظ، وهو تكراره مرة بعد مرة، مع القرائن كالإشارة إلى المسمى وبهذه الطريقة تعلمت الأطفال.
ولا نسلم ارتفاع الأمان عن الشرع، والتغيير لو وقع لاشتهر لكونه أمرًا مهمًّا تتوفر الدواعي على نقله إلينا لكنه لم يشتهر فلا يكون واقعًا.
وقال أبو هاشم: الكل مصطلح، أي مجموع الألفاظ إنما هي بوضع البشر، وإلا أي: لو كانت بوضع الله- تعالى- فالتوقيف منه للعباد على اللغات إما (أن يحصل) بالوحي ولا يتصور إلا بالإرسال فتتقدم البعثة على معرفة اللغات، وهي أي البعثة متأخرة لقوله تعالى: {وما أرسلنا من رسول إلا بلسان قومه}','2','198',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (375,'أي: بلغتهم، فدل على سبق اللغات الإرسال.
فلو كانت توقيفية بالوحي (لم تكن) سابقة على الإرسال، بل متأخرة، واللازم باطل، بدليل الآية.
أو التوقيف بخلق علم ضروري في عاقل، بأن الله تعالى وضعها لهذه المعاني، وهو باطل؛ إذ يلزمه معرفة الواضع فيعرفه تعالى ضرورة، فلا يكون مكلفًا بمعرفته تعالى لحصولها.
وحينئذ لم يكن مكلفًا بشيء، إذ لا قائل بالفرق مع أنه مكلف لما ثبت أن كل عاقل مكلف، أو في غيره أي غير عاقل وهو بعيد جدًا.
فلو لم يقطع بعدمه، فلا أقل من مخالفته للظاهر، فإذا انتفت طرق التوقيف، انتفى التوقيف، وثبت الاصطلاح.','2','199',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (376,'وأجيب: بأنه ألهم العاقل بأن واضعًا ما وضعها من غير تعيين، ولا يخلو فيه أن الواضع الله سبحانه وتعالى، فلا يلزم من كونه خلقها في عاقل أن يعرف الله تعالى ضرورة.
وإن سلم أن العلم بالوضع يتوقف على معرفة الواضع على التعيين، حتى يكون عارفًا بالله- تعالى- فلا يلزم من كونه غير مكلف بمعرفة الله- تعالى- أن لا يكون مكلفًا مطلقًا.','2','200',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (377,'كما أشار إليه بقوله: «لم يكن مكلفًا بالمعرفة فقط».
وفي هذا الجواب مناقشة: إذ لا يبعد أن يقال: الاتفاق على خلافه كما مر في الدليل.
والأحسن في الجواب: أنا لا نسلم أن التوقيف بالوحي لا يتصور إلا بالإرسال.
نعم توقيف قوم الرسول، وتعليمهم متوقف عليه.
وأما توقيف الرسول فيكفي فيه الوحي والإعلام من الله تعالى.
هذا ودلالة الآية على سبق اللغات إنما هو في حق الرسول الذي له قوم، فآدم مخصوص من ذلك، إذ لا قوم له عند البعثة.','2','201',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (378,'وقال الأستاذ أبو إسحاق الإسفراييني: ما وقع به التنبيه إلى الاصطلاح توقيفي، والباقي مصطلح؛ لأنه لو لم يكن القدر المحتاج إليه في الاصطلاح توقيفيًّا لكان اصطلاحيًّا، واحتاج في تعليمه إلا اصطلاح آخر وتسلسل.
والجواب: أنه يعرف بالترديد والقرائن كما سبق في جواب أدلة الشيخ فلذا تركه المصنف اكتفاء بما تقدم.','2','202',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (379,'ولما أبطل أدلة الفرق كما زعم، تعين عنده الوقف وقد علمت ما فيه.
ونقل عن الأستاذ غير ذلك.
وطريق معرفتها أي اللغات قسمان:
الأول: النقل المتواتر في الذي لا يقبل التشكيك، كالأرض والسماء. وفي غيره، الآحاد.
الثاني: استنباط العقل من النقل، كما إذا نقل أن الجمع','2','203',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (380,'المعرف باللام يدخله الاستثناء.
ومعلوم أنه- أي: الاستثناء-: إخراج ما يجب اندراجه في المستثنى منه فيحكم بعمومه، أي: يحكم بأن الجمع المعرف باللام يجب أن يكون متناولًا له ولغيره وهو معنى العموم.
فكون صيغ الاستثناء للإخراج، ثبت بالنقل لا العقل.
والضميمة العقلية: هي أن كل ما يدخله الاستثناء يجب أن يعم المستثنى منه.
فيحكم العقل بواسطة هاتين المقدمتين أن الجمع المعرف للعموم.
وأما العقل الصرف أي: الخالص، فلا يجدي، أي: لا ينفع في معرفة اللغات؛ لأن وضع لفظ لمعين من الممكنات، والعقل لا يستقل بها.','2','204',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (381,'<span data-type="title" id=toc-42>الفصل الثاني: في تقسيم الألفاظ</span>
وتقسيمه بحسب الاعتبار، دون الذات؛ لأن أكثر هذه الأقسام متداخلة، ومورد القسمة المفرد واحد كان أو أكثر.','2','205',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (382,'وقد تقسم الألفاظ باعتبار دلالتها، فتقسيم الدلالة اللفظية يلزمه تقسيم اللفظ الدال عليها.
فالتقسيم إما للدال، أو للمدلول، أو لهما.
فالأول: للدال: وهو ماله الدلالة، والدلالة كون الشيء بحيث يفهم منه شيء آخر والأول الدال والثاني المدلول.
فإن كان الدال لفظًا فالدلالة لفظية، وإلا فغير لفظية.
وكل منهما وضعية إن توقف الفهم على الوضع والاصطلاح،','2','206',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (383,'وإلا فغير وضعية
والوضع: تعيين الشيء ليدل على شيء آخر من غير قرينة.
والمقصود هنا الدلالة اللفظية الوضعية التي يكون للوضع فيه مدخل؛ إذ لا ينضبط غيرها.
وعرفوها بفهم المعنى من اللفظ بالنسبة إلى من هو عالم بوضعه، أي: فهمها يتوقف على العلم بالوضع.
إذا علمت ذلك (فتقول اعلم) أن دلالة اللفظ على تمام مسماه كدلالة لفظ الإنسان على الحيوان الناطق مطابقة، وسمي بذلك لأن اللفظ طابق معناه.','2','207',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (384,'ودلالة اللفظ على جزئه أي على جزء المسمى، كدلالة الإنسان على الحيوان فقط، أو الناطق فقط تضمن، وسمي بذلك لكون المعنى المدلول في ضمن المعنى الموضوع.
ودلالة اللفظ على لازمه الذهني كدلالة الإنسان على قائل العلم الذي هو خارج عن الحيوان الناطق التزام، وسمي دلالة التزام لكون المعنى المدلول لازمًا للمعنى الموضوع له.
وتقييده بالذهني إشارة إلى أنه يشترط لضبط المدلول الالتزامي أن يكون الخارج بحيث يلزم من تصور المعنى الموضوع له، تصوره بمعنى أنه كلما حصل المعنى الموضوع له في الذهني، حصل ذلك المعنى الخارج فيه؛ لأن فهم المعنى من اللفظ، إما بسبب أن اللفظ الموضوع له','2','208',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (385,'أو بسبب أنه يلزم من فهم المعنى الموضوع له فهمه.
وأما اللوازم البعدية التي قد تفهم من الألفاظ، فليس فهمها من مجرد الألفاظ، بل بمعونة القرائن فلا تكون مدلولات الألفاظ لأنا نعني بالدلالة: كون اللفظ بحيث يفهم منه المعنى عند إطلاقه بالنسبة إلى العالم بالوضع بشرط توجهه إليه، وتجرده عن الموانع والشواغل.
ولا يشترط في الالتزام اللزوم الخارجي، أي: كون المعنى التزامي بحيث متى حصل المسمى في الخارج حصل هو في الخارج.
تنبيه:
لا يقال: عبر باللفظ، وهو جنس بعيد لإطلاقه على المستعمل والمهمل، فكان التعبير بالقول أحسن؛ إذا لا يشتمل إلا المستعمل،','2','209',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (386,'كما نبه عليه ابن مالك؛ لأن القول يطلق على الرأي والاعتقاد إطلاقًا غلب على الحقيقة، بخلاف اللفظ هذا.
وقد قال بعضهم: وأورد على حصر الدلالة اللفظية في الثلاثة، دلالة صيغة العموم على أحد أفرادها فإنه ليس مطابقة ولا التزامًا -وذلك واضح -ولا تضمنًا (لأن دلالة صيغة العموم كلية: وهي الحكم على كل فرد بحيث لا يبقى فرد من الأفراد وإلا لزم عدم دلالتها على فرد من أفرادها في صورة الأمر والنهي.
وفي مقابلتها: الجزئية)
وهي: الحكم) على بعض أفراد الحقيقة من غير تعيين','2','210',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (387,'كقولنا: بعض الحيوان إنسان فالتعبير بالجزء يخرجها لأنه في مقابلة الكل: وهو الحكم على المجموع من حيث هو مجموع كأسماء العدد.
وأجاب الأصفهاني (في شرح المحصول) بأن هذا التقسيم إنما هو لفظ مفرد دال على معنى ليس ذلك المعنى نسبة بين مفردين وذلك لا يأتي هنا فلا ينبغي تطلبه.
قال: فيقول: اقتلوا المشركين، في قوة جملة من القضايا،','2','211',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (388,'فإن مدلوله اقتل (هذا المشرك) وهذه (الصيغ للعموم إذا اعتبرت بجملتها لا تدل على قتل) زيد المشرك، ولكنها تتضمن ما يدل على قتله، لا بخصوص كونه زيدًا، بل لعموم كونه زيدًا، ضرورة تضمنه اقتل زيدًا المشرك، فإنه من جملة هذه القضايا، وهي جزء من مجموع تلك القضايا فتكون دلالة هذه الصيغة على وجهين: قتل زيد المشرك فتضمنها ما يدل على ذلك الوجوب والذي هو في ضمن ذلك المجموع وهو دال على ذلك مطابقة.
قال: وليس ذلك من قبيل دلالة التضمن، بل هو من قبيل دلالة المطابقة.
واللفظ إن دل جزؤه أي: كل واحد من أجزائه على جزء المعنى','2','212',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (389,'المستفاد منه فمركب، كقام زيد وخمسة عشر وغلام عمرو وإلا أي واللفظ إن لم يدل جزؤه على جزء المعنى حال كونه جزءًا من ذلك فمفرد، وإن جاز أن يدل في حال آخر.
ولا خفاء أن المراد الدلالة الوضعية، وإلا فلحروف المفرد دلالة عقلية في الجملة فشمل ما لم يمكن للفظه جزء كهمزة الاستفهام.
أو يكون له جزء غير دال على معنى كزيد.
أو يكون له جزء دال على معنى لكن لا على جزء المعنى، كعبد الله','2','213',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (390,'علمًا، وتأبط شرًا، وبعلبك.
والمفرد إما أن لا يستقل بمعناه، وهو الحرف، يعني: أنه مشروط بحسب الوضع في دلالته على معناه الإفرادي ذكر متعلقه، فنحو من وإلى -مشروط في وضعهما دالين على معناهما الإفرادي، وهو الابتداء أو الانتهاء ذكر متعلقهما من دار أو سوق أو غيرهما مما يدخل عليه الحرف، وفيه إشكال وجوابه مذكور في الشرح.
أو يستقل بمعناه وهو الفعل:
إن دل بهيئته على الحاصلة باعتبار ترتب الحروف الأصلية','2','214',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (391,'والزائدة وحركاتها وسكناتها، على أحد الأزمنة الثلاثة الماضي كقام، والحال كيقوم، والمستقبل كقم.
وقوله: «بهيئته» احترز به عما يدل على الزمان بجوهره، كالأمس والغد فإنه اسم.
وهذا إنما هو في لغة العرب، وأما في لغة العجم فالدلالة على الزمان ليست بالهيئة، إذ قد تتحد الهيئة مع اختلاف الزمان.
وإلا أي: وإن لم يدل بهيئته على أحد الأزمنة الثلاثة فاسم،','2','215',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (392,'وهو كلي إن اشترك معناه.
أي: اشترك في مفهومه كثيرون بإمكان فرض صدقه على كثيرين كالعلم والوجود.
والكلي: متواطئ إن استوى حصول أفراده الذهنية والخارجية.
(وسمي بذلك) لتوافق الأفراد فيه كالإنسان الحاصل معناه في','2','216',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (393,'الأفراد الخارجية، والشمس الحاصل معناه في الأفراد الذهنية.
ومشكك: إن تفاوت في ذلك المعنى بأن كان حصوله في بعض الأفراد أو لا، أو أقدم أو أشد من حصوله في البعض الآخر.
وسمي مشككًا؛ لأنه يوقع الناظر في الشك من أنه من المتواطئ بناء على اشتراك الأفراد فيه معنى، أو من المشترك بناء على تفاوت ما بينهما كالوجود فإنه في الواجب أولى لكونه من ذاته، وأقدم لكونه موجد','2','217',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (394,'الممكنات، فوجوده لنفسه، وأشد لكون آثاره أكثر من آثار الممكنات.
وليس المراد بالأولوية أو الأقدمية أو الأشدية في الوجود، بل بالاتصاف بمفهوم اللفظ، بمعنى أن العقل إذا حاول مطابقة المفهوم لكثيرين وجد بعض الأفراد أولى بهذا المفهوم أو أقدم أو أشد.
والأمر الزائد في المشكك الذي به التفاوت مأخوذ في ماهية الفرد الذي يصدق عليه المشكك كمطلق البياض.
فالأفراد متفاوتة الماهيات في ذلك المفهوم، كالثلج والعاج.
والمفهوم مشترك بين الكل، والكلي، جنس إن دل على ذات غير معينة، كالفرس.
وأورد عليه: علم الجنس كأسامة فإنه يصدق عليه هذا التعريف مع أنه ليس باسم جنس.','2','218',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (395,'والفرق بينهما أن اسم الجنس هو الموضوع للحقيقة الذهنية من حيث هي هي.
وعلم الجنس: هو الموضوع للحقيقة بقيد تشخصها في الذهن.
ولعل المصنف يختار أنهما سواء، كما ذهب إليه جماعة.
ومشتق إن دل على ذي أي: صاحب صفة معينة كالفارس.
فإنه يدل على ذات مبهمة باعتبار صفة معينة من الصفات المتعينة التي تتضمنها المشتقات، لا على خصوصية الذات من كونه جسمًا أو غيره بدليل صحة قولنا الفارس جسم، فإنه يفيد فائدة جديدة.','2','219',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (396,'والاسم جزئي إن لم يشترك في مفهومه كثيرون كزيد.
واعلم أن الكلية والجزئية من صفات المعنى دون اللفظ، وإنما يقال للفظ: كلي وجزئي بالمجاز لكون معناه كذلك.
والجزئي: علم إن استقل بالدلالة على مدلوله من غير قرينة.
ومضمر إن لم يستقل، بل احتاج إلى قرينة غير الإشارة كأنا وأنت وهو.
واعلم أن عدم الاستقلال هنا غير عدم الاستقلال في الحرف.
فإن المضمر لما كان في إفادته المعنى الإفرادي غير مشروط بذكر متعلقه وضعًا فهو اسم مستقل، ولما كان مدلوله لا يتشخص إلا بإحدى القرائن فهو غير مستقل.','2','220',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (397,'قال العراقي: (وكأنه أراد أولًا بالاستقلال الاستقلال في المعنى، وثانيًا الاستقلال في الدلالة).
ويرد عليه: الأسماء الموصولة وأسماء الإشارة؛ فإنها لا تستقل فتأمله.
وما جزم به المصنف من أن المضمر جزئي، هو مذهب الجمهور.
وصحح القرافي أنه كلي، وقال الإسنوي: إنه الصواب.
تقسيم آخر:','2','221',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (398,'أي للدال والمدلول معًا.
وحاصله: أن اللفظ والمعنى، إما أن يتحدا، وهو المنفرد لانفراد لفظه بمعناه، كلفظ «الله» -تعالى -فإنه واحد ومدلوله واحد.','2','222',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (399,'أو يتكثرا أي: اللفظ والمعنى وهي المتباينة؛ (لأن كل واحد مباين للآخر، ومخالف له في معناه) تفاصلت معانيها كالسواد والبياض، أو تواصلت، أي: أمكن اجتماعهما.
إما بأن يكون أحدهما اسمًا للذات، والآخر صفة لها، كالسيف والصارم فإن السيف: يدل على الذات، سواء كان قاطعًا أم لا.
والصارم: مدلوله شديد القطع، فهما متباينان.
وقد يجتمعان في سيف قاطع.
وإما بأن يكون أحدهما صفة والآخر صفة صفةٍ كالناطق والفصيح، فإن الناطق يدل على الصفة والفصيح صفة له.','2','223',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (400,'أو يتكثر اللفظ ويتحد المعنى وهي المترادفة، مأخوذ من الرديف:
وهو ركوب اثنين دابة واحدة.
أو بالعكس وهو كون اللفظ واحدًا والمعنى كثيرًا، فإن وضع اللفظ لكل منها.
فمشترك، كالقرء الموضوع للطهر والحيض.
وإلا أي: وإن لم يوضع اللفظ لكل منها، بل وضع لمعنى ثم نقل إلى غيره، لا لعلاقة.','2','224',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (401,'قال الإمام: فهو المرتجل، ولم يذكره المصنف.
لأن المرتجل اصطلاحًا: اللفظ المخترع، أي: لم يتقدم له وضع كذا قيل.
ثم قال المصنف: فإن نقل لعلاقة واشتهر في الثاني، بحيث صار فيه أغلب من الأول كالصلاة، سمي بالنسبة إلى الأول منقولًا عنه، وإلى الثاني منقولًا إليه، شرعيًا أو عرفيًا عامًا أو خاصًا كما سيجيء، وإلا، أي: وإن لم يشتهر في الثاني فحقيقة ومجاز، أي:','2','225',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (402,'الأول حقيقة والثاني مجاز.
وعلم منه أن المجاز غير موضوع.
قال العراقي ما حاصله: اشترط في المنقول شيئين:
العلاقة والاشتهار، ثم ذكر أنها ليس كذلك.
فهو (ولا شك مجاز؛ لأن) نفي المجموع يصدق بنفي كل واحد منهما، وبنفي واحد فقط.
فإن حملناه على الأول، اقتضى أن المجاز ما نقل لغير علاقة ولم يشتهر، وهذا معلوم البطلان.','2','226',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (403,'وإن حملناه على نفي العلاقة لم يصح أيضًا.
وإن حملناه على نفي الاشتهار لم يصح أيضًا.
فالمجاز قد يكون أشهر من الحقيقة.
والأقرب في حل كلامه الثالث -أعني حمله على نفي الاشتهار.
ومقتضى كلامه أيضًا أنه يكفي في الحقيقة الوضع، وليس كذلك.
بل لابد من الاستعمال، وكذا المجاز).
وأما الثلاث الأول المتحدة المعنى، وهي متحد اللفظ والمعنى، متكثر اللفظ والمعنى، متكثر اللفظ متحد المعنى، فنصوص؛ لأن كلا','2','227',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (404,'منها لا يحتمل غيره.
وهذا معنى النص، إذ هو بلوغ الشيء غايته. وهذه كذلك لأنها في المرتبة العليا من وجوه الدلالة.
تنبيه: النص فيه اصطلاحات ثلاث:
أحدهما: ما لا يحتمل التأويل، وهو مراد المصنف هنا.
والثاني: ما يحتمله احتمالًا مرجوحًا.
والثالث: ما دل على معنى كيف كان، ذكرها القرافي في التنقيح.
وزاد ابن دقيق العيد في شرح العنوان.
دلالة الكتاب والسنة مطلقًا، وقال: إنه اصطلاح كثير من متأخري الخلافيين، وعليه جرى المصنف في القياس.','2','228',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (405,'ولم يذكر ابن دقيق العيد الثالث).
وأما الأقسام الباقية، الداخلة في كون اللفظ واحدًا والمعنى كثير.
وهو المشترك والمنقول عنه وإليه، والحقيقة والمجاز.
فمتساوي الدلالة منها كالمشترك بين المعنيين، مجمل بالنسبة إلى كل واحد من معنييه.
والراجح الدلالة ظاهر، والمرجوع الدلالة مؤول.
والقدر المشترك بين النص والظاهر، وهو رجحان الدلالة المحكم، فالمحكم جنس للنص والظاهر وهما نوعاه.','2','229',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (406,'والقدر المشترك بين المجمل والمؤول، وهو عدم الرجحان المتشابه، فهو جنس لنوعي المجمل والمؤول.
تقسيم آخر لمدلول اللفظ:
مدلول اللفظ: إما معنى، أو لفظا آخر.
واللفظ الذي هو المدلول: إما لفظ مفرد، أو لفظ مركب.
وكل من المفرد والمركب: إما مستعمل أو المهمل.
فقوله: نحو الفرس، مثال للمدلول الذي هو معنى ليس بلفظ.
والكلمة: مثال للفظ (المفرد المستعمل).','2','230',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (407,'فالكلمة: مدلولها لفظ وضع لمعنى مفرد، وهو الاسم والفعل والحرف.
وأسماء الحروف: مثال اللفظ المفرد المهمل.
فإن الباء اسم لبه: والضاد اسم لضه إلى آخرها.
ولم توضع لمعنى.
والخبر: مثال للفظ المركب المستعمل، (فإن الخبر موضوع لمثل زيد قائم، وقام زيد.','2','231',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (408,'والهذيان: مثال للفظ المركب المهمل) فإن الهذيان لفظ مدلوله مركب مهمل.
والمركب صيغ للإفهام، أي الناطق إنما صاغ المركب من المفردات وألفه منها لإفهام الغير ما في ضميره.
وقال: «صيغ» ولم يقل وضع، ليفيد أن المركب ليس موضوعًا.
وإنما ألف وركب للإفهام.
وممن رجع أن المركب المستعمل ليس موضوعًا ابن الحاجب وابن مالك.','2','232',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (409,'ورجح في جمع الجوامع تبعًا للقرافي أنه موضوع؛ لأن العرب رجحت في التراكيب كما رجحت في المفردات.
فإن أفاد المركب بالذات أي بالوضع (طلبا واحترز بالذات) عن مثل: {كتب عليكم الصيام} وأنا أطلب منك القيام، فإنه لم يوضع للطلب، بل للإخبار.
فالطلب للماهية أي: لذكرها استفهام، وللتحصيل أي: لتحصيل الماهية مع الاستعلاء أي: طلب الفعل الذي اشتق منه اللفظ','2','233',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (410,'كقم، أو كف النفس كلا تقم بغلظة، ومثل رفع صوت لا بتخضع وتذلل أمر، ويدخل فيه النهي، ولا ينافي هذا ما سيجيء له في باب الأمر أنه لا يعتبر الاستعلاء في الأمر؛ لأنه هنا ذكر تقسيم القوم.
وفي باب الأمر اختار ما رجح عنده.
وأجيب أيضًا بحمل كلامه هنا على الاصطلاحي وهناك على اللغوي.
والطلب مع التساوي في الرتبة التماس.
والالتماس في العرف: إنما يطلق على ما يكون مع التواضع لا مع التساوي.','2','234',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (411,'والطلب مع التسفل أي: والخضوع سؤال ودعاء.
وإلا وإن لم يفد المركب بالذات طلبًا، وذلك بأن لا يدل على طلب أصلًا كقام زيد، أو يدل عليه لكن لا بالذات، بل باللازم، نحو أنا طالب منك كذا، فمحتمل التصديق والتكذيب: خبر.
والعبارة الشائعة في تعريفه: ما يحتمل الصدق والكذب.
والمراد به احتمالهما بحسب المفهوم مع قطع النظر عن الخارج، بمعنى أن السامع إذا نظر إلى مجرد أنه إثبات شيء لشيء أو نفيه عنه، لم يمنع كونه مطابقًا للواقع كما لم يمنع كونه غير مطابق له، فدخل فيه ما','2','235',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (412,'يكون صدقًا محضًا: كقولنا: السماء فوقنا، أو كذبًا محضًا كقولنا: اجتماع النقيضين ممكن في الخارج.
والصدق عبارة عن مطابقة الحكم للواقع.
والكذب عدمها.
ومعرفة هذا المعنى لا يتوقف على معرفة الخبر، حتى يكون تعريفه بما يحتمل الصدق والكذب دورًا.
والمصنف عدل عن قولهم يحتمل الصدق والكذب إلى قوله: يحتمل التصديق والتكذيب، فإن كان عدوله حتى يدخل الخبر الصادق قطعًا والكاذب قطعًا فهذا غير محتاج إليه لما تقدم.
وإن كان عدوله هربًا من الدور فلا ينفعه، إذا يرد عليه أنهما الحكم بالصدق والكذب، فما فعل إلا أن وسع الدائرة؛ لأن الدور في الأول على تقدير وروده كان مرتبة فصار بمرتبتين: إن أريد بالتصديق الحكم بصدق الكلام وبثلاث مراتب: إن أريد الحكم بصدق المتكلم؛ لأنه','2','236',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (413,'يتوقف على صدق الكلام وهو على الخبر والخبر عليه.
والتحقيق أنه لا دور فلا فائدة للعدول.
وغيره أي: غير محتمل التصديق والتكذيب تنبيه، أي نبهت به على مقصودك ويندرج فيه التمني: وهو إظهار محبة الشيء ممكنًا كان أو محالًا.
والترجي: وهو إظهار إرادة الشيء الممكن أو كراهته.','2','237',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (414,'والقسم والنداء، وأدرج بعضهم في التنبيه الاستفهام والتعجب وهو اصطلاح.','2','238',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (415,'<span data-type="title" id=toc-45>الفصل الثالث: في الاشتقاق</span>
وهو لغة: الاقتطاع.
وفي الاصطلاح: تارة يحد باعتبار العلم، ومعناه كما قال','2','239',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (416,'الميداني: هو أن تجد بين اللفظين تناسبًا في المعنى والتركيب فترد أحدهما للآخر.
(فإن قلت هذا التعريف منطبق على تعريف الاشتقاق بحسب العمل أيضًا؛ لأن وجدان المناسبة بين اللفظين تركيبًا، ومعنى تعريفه بحسب العلم والعمل فلم خصصته بالعلم؟
فالجواب أن المراد بالرد: الحكم بأن أحدهما مأخوذ من الآخر، وهو من قبيل العلم لا العمل.
وإنما ذكر هذا القيد؛ لأن معرفة التناسب بدونه ليس من علم الاشتقاق لحصولهما لكل من ينظر في التراكيب وله أدنى تمييز مع أنه لا','2','240',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (417,'يوصف بكونه عالمًا بالاشتقاق) وتارة باعتباره العمل، أي: بأن يأخذ من اللفظ ما يوافقه في حروفه الأصول.
ومعناه كما حده المصنف بقوله: وهو رد اللفظ إلى لفظ آخر لموافقته له في حروفه الأصلية،','2','241',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (418,'ومناسبته له في المعنى.
فعلم منه أنه لابد في الاشتقاق من أمور:
أحدها: المشتق وأصله، فإن المشتق فرع ولو كان أصلًا في الوضع غير مأخوذ من غيره لم يكن مشتقًا.
ثانيها: أن يوافقه في الحروف إذ الأصالة والفرعية لا تتحققان بدونه والمعتبر الحروف الأصلية، فإن حروف الزيادة مثل الاستعجال والاستباق (لا عبرة بها)، فإن الاستباق يوافق الاستعجال في حروفه الزائدة والمعنى وليس بمشتق منه.','2','242',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (419,'ثالثها: الموافقة في المعنى بأن يكون فيه معنى الأصل إما مع زيادة كالضرب.
والضارب فإن الضارب ذات (ثبت) لها الضرب، وإما بدونها كالمقتل مصدر من القتل، وليس المراد بالموافقة في المعنى اتحاد المعنيين.
وقوله: رد لفظ، يشمل الاسم والفعل فانطبق على المذهبين.
وقوله: «ولابد من تغيير بزيادة أو نقصان حرف أو حركة أو كليهما» خارج عن الحد وقد تم الحد دونه.
وإنما ذكر تمهيدًا لتقسيم التغيير اللفظي إلى خمسة عشر، والتغيير اللفظي أعم من التحقيقي والتقديري ليدخل مثل هرب هربًا، وكذلك طلب وجلب من الطلب والجلب والفلك مفردًا وجمعًا.','2','243',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (420,'وهذه الأقسام منها أربعة: فيها تغيير واحد، ثم ستة فيها تغيران، ثم أربعة تلي هذه الستة فيها ثلاث تغييرات، والقسم الخامس عشر فيه أربع تغييرات.
وقوله: «بزيادة» مضاف إلى حرف أو حركة أو كليهما، وكذا «نقصان» مضاف إلى الثلاثة أيضًا: فيكون ستة أقسام.
الأول (بزيادة حرف، الثاني بزيادة حركة، الثالث) بزيادتهما معًا، وكذا النقصان.
وقوله: أو بزيادة أحدهما، ونقصانه أو نقصان الآخر.
تقديره أو بزيادة أحدهما، ونقصانه، أو بزيادة أحدهما، ونقصان','2','244',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (421,'الآخر، فيدخل فيه أربعة أقسام: فإن زيادة أحدهما (ونقصانه، يدخل فيه زيادة الحرف ونقصانه، وزيادة الحركة ونقصانها.
ويدخل في زيادة أحدهما، ونقصان الآخر، زيادة الحرف، ونقصان الحركة، وزيادة الحركة ونقصان الحرف.
وقوله: أو بزيادته أو نقصانه، بزيادة الآخر ونقصانه.
تقديره: أو بزيادة أحدهما، مع زيادة الآخر، ونقصانه، أو نقصان أحدهما مع زيادة الآخر، ونقصانه، فيدخل فيه أربعة أقسام.
فإن زيادة أحدهما)، مع زيادة الآخر ونقصانه، يدخل فيه زيادة الحرف مع زيادة الحركة ونقصانها، وزيادة الحركة مع زيادة الحرف ونقصانه فيدخل في نقصان أحدهما، مع زيادة الآخر، ونقصانه، نقصان الحرف مع زيادة الحركة ونقصانها، ونقصان','2','245',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (422,'الحركة مع زيادة الحرف ونقصانه.
وقوله: أو بزيادتهما ونقصانهما، أشار به إلى الخامس عشر، وهو زيادة الحرف والحركة معًا، ونقص الحرف والحركة معًا.
وقوله: نحو كاذب مثال لزيادة الحرف فقط فإن كاذبًا مشتق من الكذب، زيد فيه ألف، ونصر من النصر زيد فيه حركة وهي فتحة الصاد، وضارب من الضرب زيد فيه الألف وكسر الراء.
وخف من الخوف نقص منه الواو.','2','246',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (423,'وضرب ساكن الراء مصدرًا من ضرب الماضي، على مذهب الكوفيين في اشتقاقهم الاسم من الفعل نقصت منه حركة الراء، وغلى من الغليان، نقص منه الألف والنون في الآخر.
(وحركة الياء).
ومسلمات من مسلمة زيد فيه الألف والتاء، ونقص منه حرف وهو تاء مسلمة.','2','247',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (424,'وحذر بكسر الذال، اسم فاعل من الحذر، نقص حركة وهي فتحة الذال وزاد حركة وهي كسرها.
وعاد بالتشديد اسم فاعل من العدد، زاد حرفًا وهو الألف بعد العين، ونقص حركة الدال الأولى للإدغام.
ونبت ماضي من النبات نقص حرفًا وهو الألف، وزاد حركة وهي فتحة التاء.
واضرب من الضرب زاد فيها الألف للوصل، وحركة الراء، ونقص حركة الضاد، وخاف ماض من الخوف، زاد الألف وحركة','2','248',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (425,'الفاء، وحذف منه الواو.
وعد فعل أمر من الوعد نقص الواو وحركة الدال وزاد كسرة العين.
وكال بتشديد اللام اسم فاعل من الكلال نقص حركة اللام الأولى للإدغام، والألف التي بين اللامين، وزاد الألف قبل اللامين.
وارم من الرمي زاد همزة الوصل وحركة الميم ونقص الياء وحركة الراء.
وأنت خبير بأن التمثيل للتفهيم لا للتحقيق فلا يحسن المشاححة في مثل هذه الأمثلة.','2','249',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (426,'تنبيه:
المشتق قد يطرد كأسماء الفاعلين والصفات المشبهة.
وأفعل التفضيل والزمان والمكان والآلة.
وقد لا يطرد نحو القارورة والدبران.','2','250',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (427,'والعيوق والسماك وله تحقيق مذكور في الشرح.
وأحكامه:

أي<span data-type="title" id=toc-46> أحكام المشتق في مسائل:</span>
الأولى: شرط المشتق اسمًا كان أو فعلًا صدق أصله وهو المشتق','2','251',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (428,'منه، فلا يصدق ضارب على ذات إلا إذا صدق الضرب على تلك الذات، سواء صدق في الماضي (أو في الحال) أو في الاستقبال.
وقال: صدق أصله، دون وجود أصله، ليشمل الأزمنة الثلاثة.
خلافًا لأبي علي الجبائي وابنه أبي هاشم المعتزليين.
فإنهما قالا: بعالمية (الله تعالى) دون علمه بناء على مذهبهم','2','252',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (429,'في نفي الصفات، ومع ذلك يطلقون العالم على الله تعالى وسائر المشتقان وينكرون المشتق منهن ,عللاها -أي: العالمية -فينا -أي في المخلوقات -به أي: بالعلم.
قالوا: لأن ذاته تعالى اقتضت عالميته، وليست معللة بالعلم، بخلاف عالميتنا.
لنا على امتناع إطلاق المشتق بدون المشتق منه، أن الأصل وهو','2','253',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (430,'المشتق منه جزؤه، أي: جزء المشتق.
فإن العالم مثلًا ذات قام بها العلم، فلا يوجد المشتق دونه أي دون المشتق منه الذي هو جزؤه.
الثانية: شرط كونه أي كون المشتق حقيقة، دوام أصله وهو','2','254',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (431,'المشتق منه، كالضارب لمباشر الضرب.
خلافًا لابن سينا، وأبي هاشم، حيث لم يشترطا ذلك، فيطلق على من ضرب وانقضى ضربه: ضارب، حقيقة عندهما.
أما إطلاق ضارب على من سيضرب فإنه مجاز اتفاقًا.
ثم استدل المصنف على ..................................','2','255',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (432,'مختاره بقوله: لأنه يصدق نفيه، أي نفي المشتق عند زواله، أي زوال المشتق منه.
فيقال: زيد ليس بضارب، فيصدق أنه ليس بضارب في الحال، فيصدق ليس بضارب مطلقًا؛ لأن المقيد أخص من المطلق، وصدق الأخص مستلزم لصدق الأعم.
فإذا صدق نفيه فلا يصدق إيجابه وهو زيد ضارب وإلا لزم اجتماع النقيضين، فإذا صدق نفيه حقيقة كان إيجابه مجازًا، وهو المطلوب.
قيل: النفي مطلقًا لا ينافي الثبوت مطلقًا؛ لأنهما مطلقتان','2','256',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (433,'فلا تتناقضان.
قلنا: مؤقتتان بالحال أي: بحال التكلم؛ لأن أهل العرف ترفع أحدهما بالآخر، حيث يقال في الرد على من قال: زيد قائم -: ليس زيد بقائم.
فأغنى عن القيد فهم أهل العرف له، إذ لو لم يكن كذلك لما جاز استعمال كل أحد منهما في تكذيب الآخر ورفعه.
لكن أهل العرف يستعملون ذلك، فيكونان متناقضين.
لا يقال: فعل الماضي مشتق مع أنه حقيقة باعتبار ما مضى بلا نزاع؛ لأن المراد الصفات دون الأفعال، فإن الزمان داخل في مفهوم الفعل، بخلاف الصفات.
وأما مثل قوله تعالى: {والسارق والسارقة فاقطعوا}، و: {الزانية والزاني فاجلدوا} ونحوهما فإنها حقيقة مطلقًا بلا','2','257',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (434,'نزاع.
ومحل الخلاف: ما إذا كان المشتق محكومًا به كقولك: زيد مشرك دون ما إذا كان متعلق الحكم كالآيات وإلا سقط، الاستدلال بها في هذا الأعصار، فإنه يقال: لا يتناول حقيقة إلا من كان متصفًا بهذا الوصف حالة نزول الآية، وإطلاقه على غيره مجاز والأصل عدم المجاز (نبه عليه القرافي).
وعورض دليل المصنف بوجوه.
قال العراقي: لو قال بأوجهٍ كان أوجه؛ لأنه جمع قلة ووجوه جمع كثرة.','2','258',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (435,'الأول: أن الضارب مثلًا من له الضرب، وهو - أي: ثبوت الضرب - أعم من الماضي والحال، بدليل صحة انقسامه إليهما. وهو في الحال حقيقة اتفاقًا فكذا في الماضي.
ونوقض: بأنه أعم من المستقبل أيضًا؛ لأن من ثبت له الضرب، كما هو أعم من الماضي، فهو أعم من المستقبل أيضًا.
فيلزم أن يكون حقيقة فيه، وهو مجاز اتفاقًا.
والمشاحة في دلالة ((ثبت له الضرب)) على الماضي والحال والاستقبال لا تحسن، (مع ظهور المراد منه) وإن كان ظاهر لفظ ثبت مختصًا بالماضي.','2','259',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (436,'الثاني: أن النحاة منعوا عمل النعت الماضي، أي: المشتق كاسم الفاعل واسم المفعول إذا كان بمعنى الماضي، وليس معه أل فلا ينصب مفعوله، بل يتعين جره (بالإضافة إليه).
كقولك: مررت برجل ضارب زيدٍ أمس.
فأطلقوا اسم الفاعل على من صدر منه الضرب، والأصل في الإطلاق الحقيقة.
ونوقص: بأنهم أعملوا المستقبل أيضًا وهو مجاز اتفاقًا.
ورد بأنه تكثير المجاز وهو خلاف الأصل.
الثالث: أنه لو شرط بقاء المعنى لم يكن المتكلم ونحوه،','2','260',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (437,'أي: كالمخبر والمحدث حقيقة، واللازم باطل بالاتفاق.
بيان الملازمة: أنه لا يتصور حصوله إلا بحصول أجزائه وأنها حروف تنقضي أولًا فأولًا، ولا تجتمع في حين، وقبل حصولها لم تتحقق وبعده قد انقضت.
وأجيب: بأنه لما تعذر اجتماع أجزائه أي: الكلام وشبهه اكتفى في الإطلاق الحقيقي بمقارنته بآخر جزء.
وليس مبنى اللغة على المضايقة في أن ما ينقضي أجزاؤه شيئًا فشيئًا، هل هو باق أم لا؟ .
بل يعنون ببقاء المعنى عدم انقضائه بالكلية، حتى يقولوا لمن هو مباشر الأخبار والكلام: أنه مخبر ومتكلم حقيقة. وأن المعنى باق غير منقض.
وكذا المتحرك ما دام متوسطًا بين المبتدأ والمنتهى، وإلا تعذر أكثر','2','261',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (438,'أفعال الحال مثل: يضرب ويمشي، فإنها ليست آنية بل زمانية تنقضي أجزاؤه أولًا فأولًا.
والتحقيق أن المعتبر المباشرة العرفية كما يقال: يكتب القرآن ويمشي من مكة إلى المدينة ويراد به أجزاء من الماضي ومن المستقبل. متصلة، لا يتخللها فصل بعد عرفًا تركًا لذلك الأمر، وإعراضًا عنه، فالشرط وجود المعنى إن أمكن، وإلا فوجود آخر جزء منه، وذلك متحقق في الكلام ونحوه.
الرابع: أن المؤمن يطلق حال خلوه عن مفهومه، أي: مفهوم الإيمان والأصل في الإطلاق الحقيقة.','2','262',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (439,'ويوضحه أن المؤمن إذا نام أو غفل غير مباشر للإيمان على زعمهم.
لأن مفهوم الإيمان: إما تصديق القلب، أو إقرار اللسان أو عمل بالأركان.
أو الثلاثة، ويصدق عليه أنه مؤمن، للإجماع على أن المؤمن لا يخرج عن كونه مؤمنًا بنومه وغفلته، وتجري عليه أحكام المؤمنين وهو نائم أو غافل.
وأجيب: بأنه أي: بإن إطلاق المؤمن عليه مجاز باعتبار الإيمان السابق، بدليل عدم اطراده، وإلا لزم الاتصاف بالمتقابلين حقيقة، أي مؤمنًا كافرًا معًا، حقيقة فيما إذا صار الكافر مؤمنًا والنائم يقظانًا، والحلو حامضًا، والعبد حرًا، وذلك مما لا يحصر.
قال المصنف أيضًا: ((وإلا لأطلق الكافر على أكابر الصحابة حقيقة))','2','263',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (440,'باعتبار ما سبق، لكن الإطلاق غير جائز قطعًا بالإجماع.
قال الشيخ سعد الدين: كون المؤمن للنائم والغافل مجازًا بعيد جدًا، لا يبعد الإجماع على بطلانه، يعني بل يطلق عليه مؤمن حقيقة.
والتحقيق: أن النزاع (في حقيقته) اسم الفاعل، وهو الذي بمعنى الحدوث، لا في مثل المؤمن والكافر، والنائم واليقظان، والحلو والحامض (والعبد والحر) ونحو ذلك، مما يعتبر في بعضه الاتصاف به مع عدم طريان المنافي، وفي بعضه الاتصاف به بالفعل ألبته.','2','264',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (441,'الثالثة: اسم الفاعل لا يشتق لشيء، والفعل أي: المصدر المشتق منه لغيره، (أي: قائم بغير ذلك الشي)، بل يجب بمقتضى اللغة إطلاق ذلك المشتق على الذي قام به، للاستقراء، أي تتبعًا اللغة فوجدناها كذلك فلا يقال: زيد ضارب، والضرب قائم بعمرو.
وقالت المعتزلة:','2','267',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (442,'الله تعالى متكلم بكلام يخلقه في جسم، كما أنه الخالق، والخلق المخلوق، فجعلوا المتكلم لله، لا باعتبار كلام هو له بل كلام لجسم هو يخلقه فيه.
ويقولون: لا معنى لكونه متكلمًا، إلا أنه يخلق الكلام في الجسم.
لأنه قد ثبت إطلاق المتكلم عليه، وحصل لهم شبهة على امتناع قيام الكلام به، فلزمهم القول، بأن معنى المتكلم في حقه خالق الكلام في جسم.','2','268',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (443,'وقد أقيم البرهان في الكلام على صحة قيام الكلام النفسي بذاته، فيطلق عليه متكلم باعتبار الكلام القديم القائم بذاته، الذي ليس بحرف ولا صوت.
ثم قالوا: قد أطلق الخالق على الله تعالى باعتبار الخلق وهو المخلوق لقوله تعالى: {هذا خلق الله} والمخلوق ليس قائمًا بذاته.
قلنا: الخلق هو التأثير، وهو معنى إضافي قائم بالخالق، بمعنى تعلقه بالخالق واتصاف الخالق به، وليست صفة حقيقية.
وأما إطلاق الخلق في الآية على المخلوق، فهو مجاز من باب تسمية المتعلق باسم المتعلق.
قالوا: فيلزم قدم العالم وإلا لافتقر إلى تأثير آخر وتسلسل، إذ','2','269',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (444,'ليس يطلق الخالق إلا بمعنى المصدر أو المفعول، وقلتم: ليس هو المفعول، فتعين أن يكون هو المصدر، والمصدر هو: التأثير المستلزم للأثر ضرورة، فإن قدم التأثير قدم العالم، إذ لا يتصور تأثير ولا أثر، وإن كان حادثًا، احتاج إلى تأثير آخر وتسلسل.
قلنا: هو نسبة فلم يحتج إلى تأثير آخر.
تحقيقه: أن الذات قديم، وكذا القدرة، فلابد من أمر حادث (عنده تحدث) الحوادث، وهو تعلق القدرة، فهذا التعلق من حيث انتسابه إلى العالم صدور العالم، ومن حيث انتسابه إلى الذات الموصوفة بالقدرة القديمة هو خلق العالم، فمعنى الخلق كون الذات قد تعلقت قدرته القديمة بشيء.
وهذا معنى إضافي اعتباري، قائم بالخالق، بمعنى تعلقه بالخالق واتصاف الخالق به، وليست صفة حقيقية متقررة فيه، ليلزم كون','2','270',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (445,'القديم محلًا للحوادث، أو يحتاج إلى تأثير آخر.

<span data-type="title" id=toc-50>الفصل الرابع: في الترادف</span>
وهو في الاصطلاح: توالي الألفاظ المفردة الدالة على معنى واحد باعتبار واحد كالإنسان والبشر.
فقوله: ((توالي))، أي: تتابع؛ لأن اللفظ الثاني يتبع الأول في مدلوله.','2','271',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (446,'وقال: ((توالي)) ولم يقل: الألفاظ المتوالية؛ لأنه عرف معنى الترادف لا الألفاظ المترادفة، فعرف المصدر بالمصدر.
وقال: ((الألفاظ)) ليشتمل الفعل كقعد وجلس، والاسم كالإنسان والبشر والحرف كفي والباء.
وقوله: ((المفردة)) احتراز عن الاسم وحده، إذ الحد مركب نحو الإنسان والحيوان الناطق، فإنهما ليسا مترادفين.','2','272',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (447,'وقوله: ((الدالة)) احتراز عن الألفاظ المهملة.
وقوله: ((على معنى واحد، احتراز عن الألفاظ المتباينة تواصلت معانيها أو تفاصلت)).
وقوله: باعتبار واحد، احتراز عن الحقيقة والمجاز، كالأسد والشجاع، إذا أطلقا على شيء واحد، وعن مجازين أيضًا، كما يقال، للشجاع الحسن الوجه: أسد وبدر.','2','273',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (448,'ونوقض: بأنه لا حاجة إلى قوله: المفردة؛ لأن الحد والمحدود خرجا بقوله: ((باعتبار واحد))؛ لأن دلالتهما ليستا باعتبار واحد، فإن دلالة الحد تفصيلي ودلالة المحدود إجمالي.
وأنه غير مانع إذ هو صادق على تكرار اللفظ الواحد، فلابد من تقييده بالمتغايرة.
وأنه يخرج ترادف كلمتين فقط؛ لأن أقل الجمع عنده ثلاثة، فالصواب أن يقول توالي كلمتين فصاعدًا متغايرتين دالتين على معنى واحد باعتبار واحد، كذا قيل.
وجواب الأول: أنه لا يلزم أن يكون كل قيد للإخراج.
بل قد يأتي به لبيان أجزاء المحدود، وقد مر مثله.
وجواب الثاني: أنه استغنى عن قوله: المتغايرة، بالمثال، وهو قوله: كالإنسان والبشر.','2','274',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (449,'وجواب الثالث: أنه أراد بالألفاظ اللفظية فأكثر بقرينة قوله كالإنسان والبشر، والمجاز يجوز الإتيان به في الحد إذا وجدت قرينة دالة عليها كما هنا (والله أعلم).
(ولما كان) للتأكيد والتابع شبه بالترادف شرع في الفرق بينهما فقال: والتأكيد يقوي الأول، فلا يفيد المؤكد فائدة المؤكد، بينهما فقال: والتأكيد يقوي الأول، فلا يفيد المؤكد فائدة المؤكد،','2','275',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (450,'بل يفيد تقوية دلالته.
بخلاف اللفظ الثاني في الترادف، فإنه يفيد فائدة الأول، بعينها من غير تفاوت.
قال العراقي: ولو قال: التأكيد يقويه الأول، أو المؤكد يقوي الأول كان أولى.
والتابع: نحو ليطان ونطشان في نحو: عطشان نطشان وشيطان. ليطان لا يفيد وحده فلا يضر، ولو أفراد لم يدل على شيء، بخلاف عطشان وشيطان.
ويعلم من هذا الفرق بين التابع والتأكيد؛ لأن التأكيد مستقل بخلاف التابع. والفرق بين التأكيد والتابع إذا وقع بعد المتبوع: أن التأكيد يفيد مع تقوية الأول عدم إدارة المجاز بخلاف التابع.
وأن التابع يشترط أن يكون على زنة المتبوع بخلاف المؤكد.
وأن المؤكد: له مدلول في نفسه، بخلاف التابع فإنه في نفسه','2','276',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (451,'مهمل.

وأحكامه: أي<span data-type="title" id=toc-51> أحكام الترادف </span>في مسائل:
الأولى:
في سببه: أي في سبب الترادف.
المترادفان إما أن يقعا من واضعين، بأن يضع أحد القبيلتين أحد الاسمين لمسمى وتضع القبيلة الأخرى اسمًا لذلك المسمى ثم اشتهر الوضعان واشتبه أحدهما بالآخر، وهو معنى قوله ((والتبسا)).
وفيه فائدة وأي فائدة؛ لأن هذا هو السبب الأكثري، بناء على أن اللغات اصطلاحية، وما يقال: إن كلامه يقتضي أنه إذا علم الواضعين لم يكن اللفظ مترادفًا فيه ما فيه، وقد أوضحت جوابه','2','277',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (452,'في الشرح.
أو من واضع واحد لتكثير الوسائل إلى الإخبار عما في النفس فإنه ربما نسي أحد اللفظين، أو عسر عليه النطق به دون الآخر، كما عسر عليه النطق بالبر فيعبر بالحنطة، فيتوسع في التعبير لكثرة الذرائع، أو تعذرت القافية أو الوزن فيبقى الآخر وسيلة للمقصود.
والتوسع في مجال البديع، وهو: اسم لمحاسن الكلام كالتجنيس والقلب والمطابقة والمشاكلة.
فالتجنيس: بأن يوافق أحدهما غيره في الحروف دون صاحبه نحو','2','278',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (453,'رحبة رحبة، ولو قال: واسعة لعدم التجانس.
والمطابقة: وهو ذكر معنيين متقابلين، إذ قد يحصل بأحدهما دون الآخر، وإنما يتصور ذلك إذا كان أحدهما موضوعًا بالاشتراك لمعنى آخر يحصل باعتباره المطابقة دون صاحبه، كما قال: خسنا خير من خسكم، فقال: خسنا خير من خياركم، فوقع التقابل بين الخس والخيار بوجه، ووقع بينهما المشاكلة بوجه آخر، ولو قال: خير من قثائكم لم يحصل المطابقة، فلفظ الخس مشترك بين البقل والخسيس ولفظ الخيار بين الجياد والقثاء.
والمقابلة: وهي الجمع بين شيئين متوافقين أو أكثر وبين ضديهما، كقوله تعالى: {فأما من أعطى واتقى} الآية.
والمشاكلة: وهي ذكر الشيء بلفظ غيره لوقوعه في صحبته كقوله تعالى: {وجزاء سيئة سيئة مثلها}، {تعلم ما في نفسي ولا أعلم ما في نفسك}.','2','279',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (454,'قال السكاكي: والسجع يكون في النثر، كالقافية في الشعر كقولك: ((ما أبعد ما فات، وما أقرب ما هو آت)) فلو عبر بمضى ونحوه لما حصل هذا المعنى.
والقلب: كقوله: {وربك فكبر}.
الثانية: أنه (أي: الترادف)، خلاف الأصل، أي خلاف الراجح، حتى إذا تردد اللفظ بين كونه مترادفًا وغير مترادف، حمل','2','280',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (455,'على غير المترادف؛ لأنه تعريف بالمعرف أي: الذي عرف بالأول، ومحوج إلى حفظ الكل؛ لأن عند عدم حفظ الكل يختل الفهم، لاحتمال أن يكون المعلوم لأحد المتخاطبين، غير اللفظ المعلوم للآخر، فعند التخاطب لا يعلم كل منهما مقصود الآخر، ففيه مفسدة، أو يكون أكثر مقدمات، فيكون الأصل عدمه.
الثالثة:','2','281',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (456,'اللفظ يقوم بدل مرادفه من لغته، أي تجب صحة وقوع كل واحد من المترادفين، مكان الآخر إذا كانا من لغة واحدة، إذ التركيب يتعلق بالمعنى دون اللفظ، يعني أن المقصود من التركيب إنما هو المعني دون اللفظ.
فإذا صح المعنى مع أحد اللفظين وجب بالضرورة، أن يصح مع اللفظ الآخر، فلا مانع من ذلك؛ لأنه لو امتنع، لكان لمانع ضرورة، واللازم منتف؛ لأنه إما من جهة المعنى أو التركيب، وكلاهما منتف، أما من جهة المعنى فلأنه واحد فيهما، وأما من جهة التركيب؛ فلأنه لا حجر في التركيب إذا صح وأفاد المقصود، وذلك معلوم من اللغة قطعًا.','2','282',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (457,'(ولا يضر تخلف الصحة في بعض المواضع كما في نحو قولك: مررت بصاحب زيد، ولا يصح: مررت بذي زيد، وإن كانت مرادفة لصاحب زيد فإنه ربما كان لمانع بخصوص، فإن عدم المانع ليس جزءًا من المقتضى والتخلف لمانع عن المقتضى جائز والمانع هنا أن صيغة ذي لا تضاف إلا إلى اسم جنس ظاهر).
ويمتنع من لغتين.
والفرق: أن اختلاف اللغتين يستلزم ضم مهمل إلى مستعمل، فإن لفظة إحدى اللغتين بالنسبة إلى الأخرى مهملة.
وقال في المحصول: الحق أنه لا يجب مطلقًا.
وصحح ابن الحاجب: وجوبه مطلقًا.
وقوله: إذ التركيب، متعلق بالمعنى، إشارة إلى أن الخلاف إنما','2','283',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (458,'هو في حال التركيب، وأما حال الإفراد كما في تعديد الأشياء من غير عامل ملفوظ ولا مقدر فيجوز اتفاقًا كذا قيل.
وصرح بذلك في المنتهى، لكن المصنف لم يحك خلافًا، وإنما جزم بالحكم، وذكر علته.','2','284',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (459,'الرابعة: التوكيد: تقوية مدلول ما ذكر بلفظ ثان.
فقوله: بلفظ متعلق بقوله: تقوية، أي تقوية المذكور بلفظ ثان.
والمراد بقوله ثان: أعم من أن يكون مقدمًا أو مؤخرًا، لا ما وقع في الترتيب مؤخرًا، فإن المؤخر كما أنه ثان للأول، فكذا المقدم ثان للمؤخر، فلا يرد القسم.','2','285',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (460,'وإن اللام من المؤكدات، ولا يرد التابع لما تقدم.
فإما أن يؤكد اللفظ بنفسه كقوله - (صلى الله عليه وسلم):
((والله لأغزون قريشًا)) ثلاثًا، رواه أبو داود في رواية موافقة للمصنف، وفيه، ثم قال: ((إن شاء الله تعالى)).
ثم قال أبو داود: قد أسنده غير واحد عن شريك، وأسنده','2','286',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (461,'ابن حبان في صحيحه مرفوعًا باللفظ الموافق للمصنف أيضًا.
ورواه ابن القطان في علله كذلك، وقال: هذا حديث حسن غريب. ويسمى تأكيدًا لفظيًا.
أو بغيره، أي: بغير اللفظ الأول، وهو قسمان:
فالأول للمفرد كالنفس والعين للواحد مذكرًا كان أو مؤنثًا، وللمثنى كلا وكلتا مذكران ومؤنثان، وبالجمع لفظ كل وأجمعين وأخواته: أكتعين أبصعين أبتعين، فهذه أقسام للمفرد لأنه جعل مقابل الجملة.','2','287',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (462,'إذ جعله القسم الثاني، حيث قال: أو الجملة كإن وأخواتها، والقسم: فالتأكيد هنا بالجملة نحو قوله تعالى: {إن الله وملائكته يصلون على النبي}.
وجوازه، أي الترادف ضروري؛ لأن العقل لا يحيل تعدد الوسائل إلى تعيين المقصود، وفيه إشارة إلى الرد على من منع جواز الترادف.
وقوعه في اللغات، معلوم، لأن من استقرأ كلام العرب علم أنه واقع، نحو جلوس وقعود للهيئة المخصوصة، وسبع وأسد للحيوان المفترس، ويهتر ويحتر للقصير، وصهلب وشوذب للطويل، فلا يصح نفي وقوعه في اللغات.','2','288',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (463,'<span data-type="title" id=toc-56>الفصل الخامس: في الاشتراك</span>
والمشترك: لفظ موضوع لكل واحد من معنيين فأكثر، معا على البدل من غير ترجيح.
فقولنا: ((معًا)) احتراز عن المشترك معنى، كالمتواطئ والمشكك،
وقولنا: على سبيل البدل عن الموضوع للجميع من حيث هو.
وقولنا: من غير ترجيح عن الحقيقة والمجاز.
واكتفى المصنف بما تقدم من تقسيم الألفاظ عن حد المشترك هنا','2','289',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (464,'وإنما ذكر حد الترادف مع تقدمه في التقسيم توطئه للفرق، بينه وبين التأكيد، والتابع، وفيه مسائل:
الأولى: في إثباته.
أوجبه قوم عقلًا، لوجهين:
الأول: أن المعاني غير متناهية؛ إذ الأعداد أحد أنواع المعاني وهي غير متناهية، إذ ما من عدد، إلا وفوقه عدد.
والألفاظ متناهية، لتركبها من الحروف المتناهية، وهي ثمانية','2','290',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (465,'وعشرون حرفًا، بضم بعضها إلى بعض مرات متناهية.
فإذا وزع المعاني الغير متناهية على الألفاظ المتناهية، لزم الاشتراك للمعاني الكثيرة في اللفظ الواحد، وإلا يلزم خلو بعض المعاني من الألفاظ؛ لأنه إذا وضع كل لفظ من الألفاظ، وهي غير متناهية لمعنى واحد، كان الموضوع له متناهيًا، وتخلو المعاني الباقية وهي الأكثر، بل لا نسبة لها لعدم تناهيها، وهو باطل؛ لأنه يخل بغرض الوضع وهو تفهيم المعاني.
ورد: بعد تسليم المقدمتين بأن المقصود بالوضع متناهٍ، أي لا نسلم أن الألفاظ متناهية، قولك: لأنها مركبة من الحروف المتناهية، قلنا: نعم، لكن لا نسلم أن المركب من المتناهي متناهٍ، كأسماء العدد فإنها غير متناهية مع تركبها من اثنى عشر اسمًا، هي الواحد إلى العشرة والمائة والألف، والباقي يتركب منها مثل أحد عشر، أو تثنية','2','291',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (466,'مثل مائتان أو جمع مثل ألوف أو شبه جمع مثل ثلاثين.
ولا نسلم أن المعاني غير متناهية؛ لأن حصول ما لا نهاية له في الوجود محال، فما وجد فهو متناه.
سلمنا المقدمتين ومع ذلك لا يفيدكم؛ إذ المقصود بالوضع متناهٍ أي: أن المعاني التي يحتاج إلى التعبير عنها متناهية، لكونها مقصودة، وذلك ما نعلقه من المعاني، لامتناع تعقل ما لا يتناهى بالنسبة إلينا، وإن كان الواضع الله تعالى؛ لأن الوضع لفائدة مخاطبة الناس بها، وهو موقوف على تصورهم.
أو المعاني على قسمين:','2','292',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (467,'منها: ما تشتد الحاجة إلى وضعه.
ومنها: ما ليس كذلك، كأنواع الروائح، فإنه لم يوضع لكل رائحة منها اسم يخصه، فإذا تقرر خلو بعض المعاني عن الأسماء، لا يلزم محال، فإن الوضع إنما يكون لما تشتد الحاجة إليه.
ولا نسلم أن هذا المحتاج إليه غير متناهٍ.
وقد يقال: دليلكم من أصله لو سلم يلزم منه خلو بعض المعاني بالضرورة، ولو قلنا: بالاشتراك، إذ لا نسبة بين المتناهي وغير المتناهي، فإنه لو وضع لفظ الكثير من المعاني، فإنها لا تتناهى ضرورة، والله أعلم.
الثاني:
أن الوجود يطلق على الواجب، وعلى الممكن.','2','293',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (468,'ووجود الشيء عينه كما هو قول الأشعري (رحمه الله تعالى).
فالوجود الذي يطلق على الله تعالى، عين ذاته تعالى، والذي يطلق على الممكن عين الممكن، وليس هو أمرًا واحدًا فيهما فيكون مشتركًا في الاشتراك اللفظي.
ورد: بأن الوجود زائد مشترك، وليس الوجود عين الذات - كما زعمت - بل هو زائد عليها، فيكون مشتركًا اشتراكًا معنويًا فيكون مشككا.
وإن سلم أنه مشترك لفظًا، فوقوعه لا يقتضي وجوبه؛ لأن وقوع الشيء لا يستلزم وجوب وقوعه، فالدليل في غير محل النزاع.','2','294',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (469,'وأحاله أي المشترك آخرون: لأنه لا يفهم الغرض، فيكون مفسدة، يعني المشترك لا يفهم منه غرض المتكلم، الذي هو المقصود بالوضع؛ لأن الفهم لا يحصل مع الاشتراك، لخفاء القرينة، فيكون وضعه سببًا للمفسدة، والواضع حكيم فيستحيل أن يضعه.
قالوا: وما يظن من ذلك فإما مجاز أو متواطئ.
ونوقض: بأسماء الأجناس؛ لأنه لو كان عدم فهم مراد المتكلم مانعًا للوضع، لزم عدم وقوع أسماء الأجناس، كالحيوان والإنسان، إذ لو قال اشتر لي حيوانًا، لم يفهم المقصود وكذا الأسود','2','295',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (470,'ونحوه من المشتقات، فلا يدل على خصوصية تلك الذات، لكنه واقع غير ممتنع.
فإن قلت: اسم الجنس موضوع للقدر المشترك وهو مفهوم من اللفظ بخلاف المشترك، فإن المقصود منه فرد معين وهو غير معلوم.
أجيب: بأن اسم الجنس، وإن دل على القدر المشترك، والا أنه لا دلالة له على خصوصية الأفراد، فساوى المشترك في عدم الدلالة التفصيلية.
والتحقيق: أنا لا نسلم أن الفهم التفصيلي لا يحصل مع الاشتراك؛ لأن المقصود يعرف مفصلًا بالقرائن، كما ترى سلمناه، لكن ليس المقصود التفاهم التفصيلي في كل اللغة، بدليل أسماء الأجناس، بل قد يقصد التعريف الإجمالي كما يقصد التفصيلي.
والألفاظ المشتركة، وأسماء الأجناس، وإن لم تفد الفوائد','2','296',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (471,'التفصيلية، لكنها تفيد الفوائد الإجمالية.
والمختار إمكانه أي: إمكان المشترك، لجواز أن يقع من واضعين، ولم يعلم كلمنهما وضع الآخر، وهو السبب الأكثري، بناء على أن للاصطلاح في الوضع مدخلا فلا مفسدة؛ لأن اجتنابها موقوف على العلم بوقوع الاشتراك، والفرض أن لا علم.
أو من واضع واحد لغرض الإيهام على السامع حيث جعل التصريح سببًا للمفسدة.
أو لنسبته إلى الجهل، وهذا هو السبب الأقلي.
وقوله: ووقوعه، عطف على إمكانه أي: والمختار إمكانه ووقوعه،','2','297',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (472,'لغة للتردد في المراد من القرء ونحوه، من العين والجون، فلو كان متواطئًا أو مشككًا أو حقيقة ومجازًا لما وقع التردد.
وقد أطبق أهل اللغة على أن القرء للطهر والحيض معًا (على البدل) من غيرترجيح، وهو معنى الاشتراك.
ووقع في القرآن على الأصح مثل: ثلاثة قروء في قوله تعالى: {والمطلقات يتربصن بأنفسهن ثلاثة قروء}.
وقوله تعالى: {والليل إذا عسعس}.
وهو مشترك بين أقبل وأدبر، وهذا قول مرجوح.','2','298',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (473,'والذي نقله صاحب المحكم، عن الأكثرين، والنووي عن جمهور أهل اللغة، أن معناه: أدبر.
ونقل الفراء: إجماع المفسرين عليه، قال: وقال (آخرون: معناه أقبل وقال) آخرون: إنه موضوع لهما.
لكن المثال للتفهيم فلا يضر كونه مرجوحًا.
ووقوعه في القرآن له من الفوائد مع ما تقدم فائدة في الأحكام، وهي الاستعداد للامتثال إذا بين، وأنه يطيع بالعزم على الامتثال والاستعداد، له كما يعصى بخلافه.','2','299',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (474,'الثانية:
أنه أي: المشتر بخلاف الأصل، أي: إذا دار اللفظ بين الاشتراك، والانفراد، كان الغالب الانفراد، واحتمال الاشتراك مرجوح، وإلا لم يفهم ما لم يستفسر، أي: لو لم يكن مرجوحًا،','2','302',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (475,'لكان إما راجحًا على الانفراد، أو مساويًا له، وعليهما، فلا يحصل التفاهم عند التخاطب، إلا بالاستفسار، ثم يحتاج إلى البيان، والبيان محتاج إلى استفسار آخر.
ويلزم التسلسل، وهو باطل.
فإن الفهم يحصل بمجرد إطلاق اللفظ من غير احتياج إلى استفسار.
وأيضًا لامتنع الاستدلال بالنصوص، على إفادة الظنون.
فضلًا عن إفادة تحصيل العلوم، لجواز أن تكون ألفاظها موضوعة لمعان أخر وتكون تلك المعاني هي المرادة ونحن لا نعلمها، فلا يمكن التمسك بالنصوص.
ولأنه أقل بالاستقراء فإنه دل على أن الكلمات المشتركة أقل من المفردة والكثرة سبب الرجحان.','2','303',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (476,'ولأنه يتضمن مفسدة السامع؛ لأنه ربما لم يفهم، والغرض من الكلام حصول الفهم، وربما فقدت القرائن، فلم يفهم مراد المتكلم، وهاب السامع استفساره، أي: المتكلم لعلو منصب المتكلم، أو استنكف، فلم يستفسر لحقارة المتكلم، أو لكون الاستفسار مشعر بعدم الفهم.
أو فهم غير مراده أي: غير مراد المتكلم، وحكى لغيره فيؤدي إلى جهل عظيم، فإن الجميع يفهمون غير المراد.
وقوله: واللافظ مجرور عطف على السامع، أي: ويتضمن مفسدة السامع ومفسدة اللافظ؛ لأنه قد يحوجه إلى العبث؛ لأنه إذا تلفظ بالمشترك، ولم يفهم السامع مراده منه فيجب عليه التلفظ بما يدل','2','304',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (477,'عليه بطريق الإفراد فيقع المشرك عبثًا ضائعًا؛ ولأنه قد يؤدي إلى الإضرار أيضًا؛ لأنه قد يفهم غير مراده المؤدي إلى ضرورة فيفعله، أو يعتمد المتكلم فهمه، أي: فهم السامع (مع أن السامع لم يفهم مراده فيضيع غرضه أو غرض اللافظ.
وإنما كان الاشتراك متضمنًا لهذه المفاسد مع قلته فيكون مرجوحًا.
الثالثة:
مفهوما المشترك، إما أن يتباينا فلا يصدق أحدهما على الآخر فيكونا متضادين، أو نقيضين، كالقرء: للطهر والحيض، وضابطه -كما قال الآمدي: أن يمتنع الجمع بينهما كاستعمال صيغة','2','305',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (478,'"افعل" في الأمر بالشيء، والتهديد عليه، فإن الأمر يقتضي التحصيل، والنهي يقتضي الترك.
قيل: وفي مثال المصنف نظر؛ إذ لا يمتنع تكليف المرأة بالاعتداد بقرء، مراد به الحيض والطهر.
أو يتواصلا فيصح اجتماعهما فيكون أحدهما جزءًا لآخر كالممكن للعام والخاص، والعام جزء الخاص.
فالإمكان العام: هو سلب الضرورة عن الطرف المخالف للحكم.
والخاص: هو سلب الضرورة) عن طرفي الحكم.','2','306',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (479,'أو يكون أحدهما لازمًا له، أي: للآخر، كالشمس الموضوع للكوكب وضوئه اللازم له.
الرابعة:
جوز الشافعي والقاضيان: أبو بكر الباقلاني، وعبد الجبار،','2','307',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (480,'وأبو علي الجبائي: إعمال المشترك في مفهوماته الغير المتضادة (وهو المختار).
ومنعه أبو هاشم، والكرخي، وأبو الحسين البصري، والغزالي','2','308',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (481,'والإمام الرازي.
واعلم أن للمشترك أحوالاً.
الأول: إطلاقه على كل من (المعنيين على سبيل البدل بأن يطلق تارة، ويراد هذا، ويطلق تارة أخرى ويراد ذاك، ولا نزاع في صحته، وفي كونه حقيقة إذا قلنا بوقوعه في اللغة.
الثاني: إطلاقه على أحد المعنيين، لا على التعيين بأن يراد به في إطلاق واحد هذا أو ذاك مثل: تربصي قرء، أي: طهرًا أو حيضًا، وليكن ثوبك جونًا أي: أبيض أو أسود، وليس في كلام القوم ما يشعر بإثبات) ذلك أو نفيه إلا ما يشير إليه -كلام المفتاح -من أن ذلك حقيقة المشترك عند التجرد عن القرائن.
الثالث: إطلاقه على مجموع المعنيين، بأن يراد به في إطلاق واحد:
المجموع المركب من المعنيين، بحيث لا يصدق أن كلاً منهما مناط','2','309',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (482,'الحكم، ولا نزاع في امتناع ذلك حقيقة، وفي جوازه مجازًا إن وجدت علاقة مصححة.
الرابع: إطلاقه على كل واحد منهما، بأنه يراد به في إطلاق واحد هذا وذاك على أن يكون كلاً منهما مناط الحكم ومتعلق الإثبات والنفي، وهذا هو المتنازع فيه، وعلى هذا قياس الجمع بين الحقيقة والمجاز، أعني إرادة المعنى الحقيقي والمجازي، بل ربما يستغنى عنه بذكر المشترك كما فعل المصنف نظرًا إلى أن اللفظ موضوع للمعنى الحقيقي بالشخص والمجازي بالنوع.
وقوله: "الغير المتضادة" إشارة إلى أن المتضادة لا يمكن إعماله فيهما.
تنبيه:
استعمل المصنف "الغير" بالألف واللام وليس بخطأ، فهو رأي بعض النحاة كما أفاده النووي في التهذيب، واختاره.','2','310',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (483,'لنا: الوقوع في قوله تعالى: {إن الله وملائكته يصلون على النبي} والصلاة لفظ مشترك بين المغفرة والاستغفار، وهما معنيان مختلفان، والمراد في الآية كلا معنييه دفعة واحدة؛ لأن الصلاة من الله -تعالى -مغفرة، ومن الملائكة استغفار، والوقوع دليل الجواز.
وفسر الصلاة بالمغفرة، وإن فسرها غيره بالرحمة؛ لأن','2','311',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (484,'الرحمة من الله تعالى مجاز، لأنها حقيقة: رقة القلب، وهو على الله تعالى محال.
والكلام في الحقيقة دون المجاز.
قيل اعتراضًا على هذا الدليل: الضمير متعدد؛ لأن "يصلون" فيه واو الجمع، وهو متعدد، فضمير عائد إلى الله -تعالى -وضمير عائد إلى الملائكة، وإذا تعدد الضميران فيتعدد الفعل، فكأنه قيل: إن الله يصلي، والملائكة يصلون.
قلنا: يتعدد معنى لا لفظًا، وهو المدعى، يعني: أنك إن','2','312',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (485,'أردت تعدد الفعل لفظًا فممنوع، وإن أردت تقديرًا فمسلم، لكن الملفوظ يكون واحدًا مرادًا به المعاني المختلفة، وهو المدعى.
ولك أن تقول: يجوز أن تكون الصلاة مستعملة في القدر المشترك بين المغفرة والاستغفار، أي الاعتبار بإظهار شرفه.
وهو وإن كان مجازًا، والمجاز خلاف الأصل، لكنه مرجح على الاشتراك.
أو الخبر محذوفًا تقديره: إن الله يصلي، وملائكته يصلون، وإن كان فيه إضمار، وهو خلاف الأصل، لكنه مرجح على الاشتراك لمساواته للمجاز.','2','313',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (486,'ولنا أيضًا الوقوع في قوله تعالى: {ألم تر أن الله يسجد له} الآية.
إذ المراد بالسجود: الخضوع والانقياد؛ لأنه المتصور من الذوات.
ووضع الجبهة على الأرض، وإلا لكان تخصيص كثير من الناس غير مفيد لاستواء الكل من الناس والدواب في السجود بمعنى الانقياد، فثبت إرادة المعنيين المختلفين.
قيل اعتراضا على هذا الدليل: حرف العطف بمثابة العامل.
فكأنه قيل: يسجد له من في السموات، ويسجد له من في الأرض، إلى آخر الآية. فتكون الأفعال متعددة مستعملة في معان','2','314',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (487,'مختلفة، فليس من الاشتراك في شيء.
قلنا: لا نسلم أن حرف العطف بمثابة (العامل، فإن) العامل هنا رافع، والحرف لا يرفع.
وإن سلم أن حرف العطف بمثابة العامل فبمثابته في العمل أي يقوم مقامه في الإعراب لا في المعنى.
وليس في أكثر النسخ "في العمل" فعلى هذا، تقديره: إنا وإن سلمنا أن العاطف بمثابة العامل، لكنه على هذا التقدير يلزم أن يكون بمثابة العامل الأول بعينه، لكون حرف العطف قرينة له، وإذا كان بمثابة الأول بعينه يكون اللفظ واحدًا والمعنى كثيرًا، وهو المدعى.','2','315',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (488,'قيل في الآيتين: يحتمل وضعه للمجموع أيضًا، فالإعمال في البعض، أي يحتمل أن يكون لفظ الصلاة والسجود موضوعًا للمجموع من حيث هو مجموع، كما هو مجموع لكل واحد واحد، فيكون السجود مثلاً موضوعًا لثلاثة معان: لوضع الجبهة على انفراده، والانقياد على انفراده، ولهما معًا.
فعلى هذا يكون استعمال اللفظ في المجموع استعمالاً في بعض معانيه لا في كلها وهو خلاف المدعى.
قلنا: فيكون المجموع: مستندًا إلى كل واحد، وهو باطل، أي لو كان المجموع مرادًا لزم أن يكون المجموع من المغفرة والاستغفار، مستندًا إلى الله تعالى، وإلى الملائكة، وهو باطل.
ويكون المجموع من وضع الجبهة، والخضوع مستندًا إلى كل واحد من الشجر والدواب وغيرهما، مما ذكر في الآية، وهو باطل لأنه','2','316',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (489,'محال.
احتج المانع من تجويز استعمال المشترك في جميع معانيه: بأنه إن لم يضع الواضع للمجموع، لم يجز استعماله فيه؛ لأنه استعمال له في غير مدلوله، وإن وضع له أيضًا، كان استعمالاً في بعض معانيه، وهو غير المدعى، وترك هذا الأخير؛ لأنه يعلم مما تقدم.','2','317',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (490,'قلنا: لم لا يكفي الوضع: لكل واحد للاستعمال في الجميع، يعني يكون اللفظ موضوعًا لهذا ليدل عليه بالمطابقة، وللآخر كذلك فيكون اللفظ موضوعًا لكل واحد من المفهومين لا بشرط شيء من الانفراد والاجتماع.
وحينئذ إذا استعمل في الجميع يكون مستعملاً فيما وضع له فيكون حقيقة.
أو يقرر هكذا: لم لا يكون الوضع لكل واحد كافيًا للاستعمال في المجموع مجازًا من باب إطلاق اسم الجزء على الكل.
ومن المانعين: من جوز الاستعمال في الجميع، وكذا','2','318',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (491,'المثنى؛ لأنه في معناه، مثبتًأ كان أو منفيًا، نحو اعتدي بالأقراء أو لا تعتدي بالأقراء؛ لأن الجمع متعدد فجاز تعدد مدلولاته، بخلاف المفرد.
ومن المانعين: من جوز استعمال المشترك في جميع معانيه في السلب، وإن لم يكن جمعًا نحو: لا تعتدي بقرء، ومنعه في الإثبات؛ لأن السلب يفيد العموم، فيتعدد، بخلاف الإثبات.
ففي كلام المصنف حذف، وتقديره كما قررته؛ لأنهما فرقتان لا فرقة واحدة، والفرق ضعيف.
وأما في الجمع: فلأنه يفيد تعدد معنى المفرد، فإن قول القائل: اعتدى بأقراء، معناه: بقرء وقرء وقرء، فكما لا يصح في','2','319',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (492,'المفرد لا يصح في الجمع فإن آحاد الجمع يجب أن يكون من جنس واحد.
وأما في السلب فلأن النفي لا يفيد إلا رفع مقتضى الإثبات فإذا لم يفد في جانب الإثبات لا يفيد في جانب السلب أيضًا.
ونقل عن الشافعي -رضي الله تعالى عنه- والقاضي أبي بكر الوجوب.
أي: وجوب حمل لفظ المشترك على جميع معانيه حيث لا قرينة معه تدل على تعيين المراد فيه، احتياطًا، لتحصيل مراد المتكلم، إذ لو لم','2','320',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (493,'يحمل عليه، فإما أن لا يحمل على شيء من معانيه، وفي ذلك إهمال للفظ بالكلية.
أو يحمل على بعض معانيه دون بعض، وهو ترجيح بلا مرجح لاستواء اللفظ بالنسبة إليهما، وعدم القرينة المعينة للبعض فتعين الحمل على الكل.
فيكون المشترك عامًا، عند الشافعي (رضي الله عنه) فعلى هذا العام عنده قسمان: (قسم متفق الحقيقة، وقسم مختلف الحقيقة).
وظاهر كلام المصنف أنه للاحتياط لا للعموم، وكونه للعموم هو ظاهر كلام إمام الحرمين،','2','321',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (494,'والغزالي.
واختار بعض المتأخرين أن الاحتياط ومسمى العموم واحد.
تنبيه:
الوضع: جعل اللفظ (دليلاً على المعنى كتسمية الولد محمدًا وهذا أمر متعلق بالواضع.
والاستعمال: إطلاق اللفظ) وإرادة المعنى، وهو من صفات المتكلم.
والحمل: اعتقاد السامع مراد المتكلم، أو ما اشتمل على مراده.','2','322',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (495,'إذا علمت ذلك، فالمسألة السابقة بين فيها إطلاق المتكلم لفظ المشترك وإرادة جميع معانيه.
وفي هذه بين أن السامع إذا سمع لفظًا مشتركًا، ولم يكن قرينة حمله على جميع معانيه عند الإطلاق فيما يمكن الجمع والتحصيل.
(ولذا قال): في الاستعمال جوز، وفي الحمل يجب.
لكن قال الشيخ سعد الدين: إن ما نقل عن الشافعي -رضي الله عنه- وهو أنه يصح للمتكلم، استعماله فيهما، ويجب على السامع حمله عليهما، أخص من مذهب القاضي والمعتزلة، والمراد: الصحة اللغوية، فيتميز عن مذهب أبي الحسين، والغزالي، إذ الصحة عندهما، عقلية، بمعنى أنه لا دليل على امتناعه سوى منع أهل اللغة، فيكون مخالفًا لنقل المصنف.','2','323',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (496,'وقال العراقي: وافق الشافعي (رضي الله عنه) والقاضي أبا بكر، أبو عليالجبائي في وجوب الحمل حيث لا قرينة، كما نقله المصنف في باب العموم والقاضي عبد الجبار، كما نقله الإمام في مناقب الشافعي رضي الله تعالى عنه.
قال الرافعي: الأشبه أن المشترك لا يحمل على جميع معانيه.
الخامسة: المشترك: إن تجرد عن القرينة فمجمل، عند','2','324',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (497,'من لا يحمله على جميع معانيه.
وأما عند من يحمله عليها كالشافعي -رضي الله تعالى عنه- فواضح مما تقدم.
وإن قرن به ما يوجب اعتبار واحد أي معين، تعين الحمل عليه، كقولك: رأيت عينًا باصرة، وزال الإجمال.
وإن كان غير معين، فمجمل ولم يذكره لوضوحه.','2','325',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (498,'أو قرن به أكثر من واحد من مسمياته.
فكذا أي: يحمل على ذلك الأكثر عند من يجوز الإعمال في المعنيين إذا أمكن.
فإن لم يمكن، بأن يكونا متضادين، فمجمل.
وهذا معلوم مما سبق.
وإن أوجب اعتبار كل المسميات فكذا حكمها، ولم يورده المصنف؛ لأنه يعلم من قوله: أو أكثر.
وعند المانع من إعمال المشترك في معنيين مجمل.
أو قرن به ما يوجب إلغاء البعض أي: بعض معانيه.
كقوله: رأيت عينًا لا تقوم بها الأشياء.','2','326',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (499,'فينحصر المراد في الباقي من معانيه بعد إلغاء ذلك البعض الذي تقتضي القرينة إلغاؤه.
فإن كان الباقي بعد الإلغاء واحدًا تعين حمله عليه.
وإن كان الباقي أكثر فعند المجوز محمول عليه، وعند المانع مجمل.
وعلم جميع ذلك مما مر في كلام المصنف.
هذا كله إذا كان البعض الملغي معينًا، وإلا فهو مجمل.
أو قرن بالمشترك ما يوجب إلغاء الكل، فيحمل على المجاز لتعذر حمله على الحقيقة.
فإن كان بعضه ذا مجاز فقط حمل عليه، وإن كان لكل منها مجاز فقد تعارضت.
وإليه أشار بقوله: "وإن تعارضت حمل على المجاز".','2','327',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (500,'الراجح: هو أو أصله "أي حقيقته".
فإذا تساوت الحقائق، ولم تتساوى المجازات، بل بعضًا أقرب إلى حقيقته حمل اللفظ عليه لرجحانه بالأقربية.
وهو المراد بقوله: "حمل على الراجح هو".
وإن لم تتساوى الحقائق، بل رجح بعضها، والمجازات متساوية حمل اللفظ على المجاز، الذي حقيقته راجحة. وإليه أشار بقوله: "أو أصله".
وإن تساويا أي الحقائق والمجازات، قال العبري: فهل يجب حمله على جميعها أو يجوز أو يمتنع؟
فيه الخلاف المذكور في المشترك بالنسبة إلى حقائقها.
وعند المصنف مجمل.','2','328',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (501,'أو رجح أحدهما أي أحد المجازين على المجاز الآخر وترجح أصل المجاز الآخر على حقيقة المجاز الراجح فمجمل.
لأن لكل منهما رجحانًا على الآخر من وجه فاستويا.
ولك: أن تقول: عند الاستواء ينبغي أن يحمل على الجميع عند المجوز.
ونظر في جواز رجحان بعض حقائق المشترك؛ إذ رجحانه ينافي الاشتراك.','2','329',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (502,'<span data-type="title" id=toc-62>الفصل السادس: في الحقيقة والمجاز</span>
الحقيقة: فعيلة، مأخوذة من الحق بمعنى الثابت، أو المثبت؛ لأن فعيل قد يكون: بمعنى الفاعل كالعليم بمعنى العالم، وقد يكون بمعنى المفعول كالجريح بمعنى المجروح.
فالفعيل المشتق من الحق إن كان بمعنى الفاعل كان معناه الثابت.
وإن كان بمعنى المفعول كان معناه المثبت.','2','330',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (503,'نقل عن الأمر الذي له ثبات إلى العقد المطابق للواقع لأنه أولى بالوجود من العقد الغير مطابق.
ثم نقل من العقد المطابق إلى القول المطابق لهذه العلة بعينها.
ثم نقل إلى اللفظ المستعمل فيما وضع له في اصطلاح التخاطب فاللفظ، كالجنس لشموله الحقيقة وغيرها.
والباقي كالفصل لها يميزها عن غيرها.
وقوله: المستعمل: في صفة اللفظ يخرج اللفظ، المهمل، وما','2','331',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (504,'وضع أولًا ولم يستعمل وقوله: فيما وضع له، يخرج المجاز.
وقوله: في اصطلاح التخاطب، يدخل الحقيقة الشرعية واللغوية والعرفية العامة والخاصة حتى المشترك والمراد بالوضع إذا أطلق: الوضع الشخصي، وأحد قسمي الوضع النوعي: وهو ما كانت الهيئة فيه دالة على المعنى بنفسه من غير اشتراط قرينة خارجه عن اللفظ فلا يكون المجاز داخلًا أصلًا.
ويصير حد المصنف جامعًا مانعًا.
وقد يقال: الحق لغة الثبوت: وهو قدر مشترك بين هذه الأمور التي ذكرها المصنف.
ولو سلم المجاز فلا نسلم أن كل مجاز مأخوذ مما قبله بل الجميع','2','332',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (505,'مأخوذ من الحقيقة بعلاقةٍ معتبرة.
والتاء الداخلة على الفعيل المشتق من الحق لنقل اللفظ من الوصفية إلى الاسمية الصرفة. وهنا تحقيق راجعه من الشرح.
(والمجاز: مفعل) من الجواز','2','333',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (506,'بمعنى العبور وهو المصدر أو المكان أو الزمان (أي بناء مفعل مشترك بين المصدر والمكان لكونه حقيقة فيهما) نقل من المصدر أو المكان إلى الفاعل الذي هو الجائز الذي يجوز المكان وينتقل.
والعلاقة إن كان من المصدر فهي الجزئية لأن المشتق منه جزء من المشتق.
وإن كان المكان فالعلاقة إطلاق اسم المحل على الحال.
وإن كان من الزمان فلا علاقة بينه وبين الجائز، فلذا صرح بالمصدر والمكان دون الزمان.','2','334',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (507,'ثم نقل من الفاعل إلى اللفظ المستعمل في معنى غير موضوع له يناسب المصطلح بحسب التخاطب.
فاللفظ كالجنس.
وقوله: في صفته: المستعمل يخرج المهمل وما وضع له أولًا ولم يستعمل.
وقوله: في غير ما وضع له، يخرج الحقيقة، ولا ينافي ذلك كون المجاز موضوعًا بالنوع لما مر في تعريف الحقيقة فإنه لا بد هنا من اشتراط قرينة خارجة عن اللفظ.
وقوله: المناسب المصطلح، ليشمل المجاز اللغوي والشرعي والعرفي العام والخاص، ويشعر بأنه لا بد بين المعنى الحقيقي والمجازي من العلاقة، ويحترز عن العلم المنقول كبكر لأنه لم ينقل لعلاقة.','2','335',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (508,'وفيه مسائل:
الأولى:
الحقيقة اللغوية موجودة بالاتفاق كالحر والبرد، والسماء والأرض، لأنا نقطع باستعمالها في موضوعاتها وقدمها لأنها الأصل، وكذا العرفية موجودة بالاتفاق وهي التي نقلت من معناها اللغوي إلى غيره بحيث هجر الأول، وهي إما ألا يكون من قوم مخصوصين أو يكون.
فالأولى: تسمى العرفية العامة، وغلبت العرفية عند الإطلاق عليها، وذلك كالدابة لذوات الأربع، بعد أن كانت في اللغة لكل ما','2','336',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (509,'يدب على الأرض، لأنها مشتقة من الدبيب فخصها العرف ببعضها، ونحوها، كالملك فإنه مشتق من الألوكة وهي الرسالة، نقله أهل العرف العام إلى بعض الرسل.
والثانية: وهي التي من قوم مخصوصين وتسمى العرفية الخاصة كالقلب والنقص، والجمع، والفرق،','2','337',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (510,'للفقهاء وكالجوهر والعرض للمتكلمين، والرفع والنصب والجر للنحاة، فإن لكل واحد منهما معنى خاصًا في اللغة.
ونقل أهل العرف الخاص إلى معنى مصطلح عندهم كما سيجيء في القياس، بيان ما ذكره المصنف من اصطلاح الفقهاء واقتصر في التمثيل عليه لأنه يناسب المقام دون غيره.
واختلف في الحقيقة الشرعية: وهي الاسم (المستعمل فيما وضع له في الشرع،','2','338',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (511,'وخص بالاسم لأن الاستقراء دل على عدم الحرف الشرعي وعلى عدم الفعل الشرعي إلا بالتتبع وهو إما مجرى على الفعل) كالزكاة والصلاة. وإما مجرى على الفاعل كالمؤمن والفاسق.
ولا نزاع في أن الألفاظ المتداولة على لسان أهل الشرع المستعملة في غير معانيها اللغوية قد صارت حقائق فيها.
وإنما النزاع في أن ذلك لوضع الشرع وتعيينه، وأنها بحيث تدل على تلك المعاني بلا قرينة، لتكون حقائق شرعية وهو مذهب الجمهور، ونقله المصنف عن المعتزلة، حيث قال: فمنع القاضي أبو بكر، وأثبت المعتزلة، واختاره ابن الحاجب، أو لغلبتها في تلك المعاني في لسان أهل الشرع.
والشارع إنما استعملها فيه مجازًا لمعونة القرائن فتكون حقائق عرفية خاصة لا شرعية، وهو مذهب القاضي أبو بكر.','2','339',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (512,'فإذا وقعت مجردة عن القرائن في كلام أهل الكلام والفقه والأصول، ومن يخاطب باصطلاحهم يحمل على المعاني الشرعية وفاقًا.
وأما في كلام الشارع فالحق يحمل عليها، وعند القاضي على معانيها اللغوية.
وبعد تحرير محل النزاع ينبغي أن يعلم أن الآمدي في الإحكام والإمام في المحصول لم يذكرا سوى مذهبين.
أحدهما: إثبات كونها حقائق شرعية ونسبه كل منهما إلى المعتزلة، مع تصريح الآمدي بنسبته إلى الفقهاء أيضًا.
وثانيهما: نفي ذلك، ونسبه كل منهما إلى القاضي.
وكلام المختصر لابن الحاجب يوافق ذلك.','2','340',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (513,'كذا حرر المحقق محل النزاع ثم قال: والحق لا ثالث لهما.
قال الشيخ سعد الدين، ولما كان في كلام المنهاج ما يشعر بأن هنالك مذهبًا ثالثًا حيث قال بعد تقرير المذهبين: والحق أنها مجازات لغوية اشتهرت لا موضوعات مبتدأه.
نفاه، أي: المحقق، لأنه مذهب القاضي بعينه على ما تقرر في محل النزاع، قال: وهذا تحقيق جيد لو وافقه أدلة الفريقين.
واستشكل قول الشيخ: أنه مذهب القاضي بعينه بأن الظاهر أنه مذهب الجمهور لأنهم يقولون: إن الشارع نقلها، لكن لمناسبة فهي مجازات لغوية.','2','341',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (514,'فمعنى قول المصنف أنها مجازات اشتهرت، أي: في كلام الشارع، فصارت حقيقة له بحيث صار المعنى اللغوي مهجورًا.
وقوله: لا موضوعات مبتدأه أي: لا مناسبة فلا تكون هذه الألفاظ لذلك خارجة من لغة العرب.
وفي الشرح هنا نقل يخالف ما قاله المحقق: تركته اختصارًا فراجعه إن تيسر.
وقال العبري: فأشار، أي: المصنف، إلى بطلان مذهب القاضي بقوله: أنها مجازات لأنا نعلم بالضرورة أن هذه المعاني ليست حقائقها اللغوية، وأشار إلى ما هو الحق عنده، وهو صيرورتها حقائق شرعية، بقوله: اشتهرت لأنه لو لم تصر مشهورة في هذه المعاني،','2','342',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (515,'بحيث هجر في الشرع استعمالها في حقائقها اللغوية لما سبق إلى الفهم عند إطلاقها في الشرع هذه المعاني دونها.
وإذا سبقت علم أنها صارت حقائقها في الشرع.
وأشار إلى بطلان مذهب المعتزلة بقوله: لا موضوعات مبتدأه.
وإلا لم تكن لغة عربية، أي: الحجة المختارة أن هذه الألفاظ لو كانت موضوعات مبتدأه لم تكن عربية، إذ لم يضعها واضع لغة العرب بإزاء هذه المعاني، وإذا لم تكن عربية، فلا يكون القرآن كله عربيًا، لاشتماله على ما ليس بعربي حينئذٍ، وما بعضه- خاصة- عبي لا يكون عربيًا كله، وهو، أي: كون القرآن غير عربي، باطل لقوله تعالى: {وكذلك أنزلناه قرآنا عربيا}، ونحوه من الآيات الدالة على كونه عربيًا، كقوله تعالى: {إنا أنزلناه قرآنا عربيا} {وما أرسلنا من رسول إلا بلسان قومه}','2','343',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (516,'قيل: المراد بعضه، أي: بعض القرآن لا كله إذ الضمير في قوله: {إنا أنزلناه} للسورة.
ويجوز إطلاق اسم القرآن على بعضه، فإن الحالف على أن لا يقرأ القرآن يحنث بقراءة بعضه، وإذا كان بعض القرآن يسمى قرآنًا، جاز إن يشتمل القرآن على ما ليس بعربي مع أن السورة كلها عربية.
قلنا: استدلالكم بالحلف وإن دل على أن المراد بالقران البعض، لكنه معارض بما يقال إنه بعضه، فإنه لو أطلق عليه القرآن لم يكن لإدخال البعض معنى.
وأيضًا فأن بعض ذلك الشيء غير ذلك الشيء.','2','344',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (517,'فيصح أن يقال: إنه بعض القرآن، لا كله فعلم أن تسمية بعض القرآن قرانًا مجاز فيكون المراد من قوله: {إنا أنزلناه قرآنًا}، كل (القرآن، إذ) الأصل في الإطلاق الحقيقة.
هذا وقد نص الشافعي- رضي الله عنه- على أنه إذا قال لعبده: إن قرأت القرآن فأنت حر، لا يعتق إلا بقراءة الجميع فيمتنع الحنث.
وقد يقال: ؛ المعارضة غير تامة.
لأن ذلك إنما يكون فيما لم يشارك البعض الكل في مفهوم الاسم كالمائة، فإن المائة اسم لمجموع الآحاد المخصوصة.','2','345',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (518,'فلا يصدق على البعض بخلاف مثل الماء، فإنه اسم للجسم البسيط البارد الرطب بالطبع، فيصدق على الكل، وعلى أي بعض منه، فتصح أن هذا البحر ماء، ويراد بالماء مفهومه الكلي، وأنه بعض الماء، ويراد بالماء مجموع المياه الذي هو أحد أفراد هذا المفهوم، والقرآن من هذا القبيل.
فالسورة قرآن، وبعض من القرآن بالاعتبارين.
على أن هاهنا شيئًا آخر، وهو أن القرآن قد وضع بحساب الاشتراك للمجموع الشخصي وضعًا آخر، فيصح أن يقال: السورة بعض القرآن.
قيل: سلمنا أن المراد بقول تعالى: {إنا أنزلناه قرآنًا عربيًا} مجموع القرآن، وتلك- أي: الألفاظ غير العربية- كلمات قلائل، فلا تخرجه- أي: القرآن- عن كونه عربيًا لقلتها، كقصيدة فارسية فيها ألفاظ عربية، فإنه لا تخرج عن كونها فارسية فكذا هنا.
قلنا في الجواب عنه: إن اشتمال القرآن على تلك الكلمات وإن','2','346',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (519,'كانت قلائل تخرجه عن كونه عربيًا، وإلا أي: إذ لو لم تخرجه، لما صح الاستثناء منه، وقد صح، إذ يصح أن يقال: القرآن عربي إلا الألفاظ الشرعية، وكذا في القصيدة الفارسية المشتملة على قليل من الألفاظ العربية، يصح أن يقال: هذه القصيدة الفارسية إلا تلك الألفاظ العربية.
قيل: يكفي في عربيتها- أي: الألفاظ الشرعية- استعمالها- أي: العرب- لها في لغتهم، حتى يكون اللفظ ما يستعمله العرب، سواء كان في معناه الذي وضعه الواضع بإزائه، أو في غيره، وإذا كان كذلك كانت هذه الألفاظ عربية، وإن كانت موضوعاتها مبتدأة فلا يلزم اشتمال القرآن على غير العربي.
قلنا: استعمال العرب تلك الألفاظ في معانيها الشرعية، دون اللغوية لا يكفي كونها عربية، لأن تخصيص الألفاظ باللغات إنما هو','2','347',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (520,'بحسب الدلالة، أي: دلالتها على معانيها التي وضعت بإزائها.
وإن كان كذلك، فإذا دلت على معاني لم يضعها واضع العربية بإزائها لم تكن عربية.
وفيه إشكال، وجوابه في الشرح.
قيل: ما ذكرتم من الدليل على أن القرآن كله عربي غير مشتمل على ما ليس بعربي منقوض بالمشكاة:
قيل: حبشية، وقيل هندية، ومعناها الكوة.','2','348',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (521,'والقسطاس: فإنه رومي، ومعناه: الميزان.
والإستبرق والسجيل فارسيان، ومعنى الأول: الديباج الغليظ، مع أنها مذكورة في القرآن.
فيكون مشتملًا على ما ليس بعربي.
قلنا: كون هذه الألفاظ من تلك اللغات لا ينافي كونها عربية، لجواز أن يضعها واضع العربية بإزاء ما وضعها واضع تلك اللغات','2','349',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (522,'بإزائه، إذ توافق اللغات في الوضع غير ممتنع، كالصابون والتنور.
وإليه أشار بقوله: وضع العرب وافق فيها لغة أخرى.
واعلم أن جعل الأعلام من المعرب، أو مما فيه النزاع، محل مناقشة.
لأن النزاع في أسماء الأجناس المنسوبة إلى لغة أخرى المتصرف فيها عند العرب بدخول اللام، والإضافة، ونحو ذلك.','2','350',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (523,'والأعلام بحسب موضعها العلمي ليست مما ينسب إلى لغة دون لغة، ولا هي أيضًا مما تصرفت العرب فيها فاستعملتها في كلامهم.
والمصنف- رحمه الله تعالى- تابع للأكثرين، في أنه ليس في القرآن المعرب، ونص عليه الشافعي- رضي الله عنه- في أول الرسالة.
وجاء عن البراء بن عازب، وأبي موسى الأشعري.','2','351',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (524,'وأبي ميسرة، وسعد بن غياض، وسعيد بن جبير، ومجاهد، وابن عباس، وعكرمة- رضي الله عنهم- تفسير ألفاظ وقعت فيه أطلقوا عليها أنها بلسان غير العرب.
وهذا ليس صريحًا في مخالفة الأكثر لاحتمال التوافق كما قال المصنف.','2','352',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (525,'وعورض الدليل الدال على كون هذه الألفاظ مجازات مشهورة لا موضوعات مبتدأة بأن الشارع اخترع معان لم تكن معقولة للعرب، فإنها معان حديثة فلم تضع لهم ألفاظًا، لأن وضع اللفظ لمعنى مشروط بتعقله.
وكانت الحاجة ماسة إلى تعريفها للمكلف، فلابد لها من ألفاظ موضوعة من قبل الشارع بإزائها تدل عليها ويعبر بها ليمكن المكلف معرفتها.
قلنا: كفى التجوز: إيضاحه:
أنكم إن أردتم أنه لابد لها من إحداث ألفاظ مبتدأة','2','353',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (526,'فممنوع، لأن النزاع لم يقع إلا فيه.
لم لا يجوز أن يكتفي بالمجاز بأن يدل عليها، ويعبر عنها بألفاظ تدل عليها مجازًا، لما بينها وبين معانيها اللغوية من العلاقة المعتبر نوعها في العربية كالصلاة، فإنها في اللغة حقيقة للدعاء، ثم أطلقت على المعنى الشرعي مجازًا لما بينهما من العلاقة، وهي كون الدعاء جزء من المعنى الشرعي حتى يكون من باب إطلاق اسم الجزء على الكل.
وإن أردتم أنه لابد لها من ألفاظ كيف كانت يعبر بها عنها فمسلم، لكن لا يلزم من كونها دالة عليها حقيقة لجواز أن تدل عليها مجازًا.
وعورض أيضًا: بأن الإيمان في اللغة:
هو التصديق، بالإجماع. ومنه {وما أنت بمؤمن لنا}.','2','354',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (527,'وفي الشرع: فعل الواجب، ولا مناسبة مصححة للتجوز.
ولأنه أي لأن الإيمان عبارة عن الإسلام، وإلا يعني لو لم يكن إياه لم يقبل من مبتغيه- أي: من طالبه- لقوله تعالى: {ومن يبتغ غير الإسلام دينًا فلن يقبل منه} لكن الإيمان مقبول اتفاقًا.','2','355',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (528,'وأيضًا: لم يجز استثناء المسلم من المؤمن، لأنه حينئذ يكون مفهوم أحدهما مغايرًا لمفهوم الآخر، فلا يكون أحدهما من جنس الآخر، فلا يصح الاستثناء، لأنه إخراج بعض ما يتناوله اللفظ، فإذا كانا متغايرين امتنع إخراج أحدهما من الآخر، لكن الاستثناء صحيح، بل واقع، فلا يصح نفيه، وقد قال تعالى: {فأخرجنا من كان فيها من المؤمنين فما وجدنا فيها غير بيت من المسلمين}.
فلفظة "غير" من أدوات الاستثناء، ولولا الاتحاد لم يستقم الاستثناء لأنه مفرغ فيكون متصلًا مستلزمًا للدخول فيما قبله،','2','356',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (529,'أي: ما وجدنا فيها بيتًا من بيوت المؤمنين، إلا بيتًا من المسلمين.
وإنما يكون بيت المؤمن، إذا صدق المؤمن على المسلم.
إذ التحقيق: أنه ليس المراد بالبيت هو الجدران، بل أهل البيت.
فيكون التقدير: فما وجدنا أهل بيت من المؤمنين إلا أهل بيت من المسلمين، فثبت أن الإيمان الإسلام.
والإسلام هو الدين المعتبر، لقوله تعالى: {إن الدين عند الله الإسلام}.
والدين (عبارة عن) فعل الواجبات، لقوله تعالى: {وما أمروا إلا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة وذلك دين القيمة} أي: دين الملة المستقيمة.','2','357',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (530,'ولفظ "ذلك" إشارة إلى جميع ما تقدم، فيجب أن يكون كل ما تقدم دينًا معتبرًا، وكل ما تقدم فعل الواجبات، فيجب أن يكون المذكور دينًا، لأن الذي تقدم ذكره: إقام الصلاة، وإيتاء الزكاة، وهما من الأفعال الواجبة، فثبت أن الدين فعل الواجبات، (فإذا كان الإيمان في اللغة: التصديق وفي الشرع: فعل الواجبات)، فيكون مفهومه الشرعي مغايرًا لمفهومه اللغوي، فيكون من مخترعات الشرع ويلزم ما ذكرنا.
قلنا: الإيمان في الشرع: تصديق خاص، وهو تصديق النبي- صلى الله عليه وسلم- في كل أمر ديني، علم مجيئه ضرورة، فيكون حقيقة شرعة، ومجازًا لغويًا من باب إطلاق الكل','2','358',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (531,'على الجزء، كما مر في الصلاة.
وما ذكروا من الدليل فمقدماته ممنوعة، لأن مقدماته: إن الإيمان هو الإسلام، والإسلام هو الدين، وهما ممنوعان.
إذ لو صحتا، لكان مفهوم الإيمان مفهوم الإسلام والدين.
لكن مفهوم الإيمان يغاير مفهومهما لغة وشرعًا.
أما لغة؛ فلأن الإسلام لغة: الانقياد، والدين: الطاعة والعمل الظاهر، وهما مغايران مفهوم الإيمان لغة، أعني: التصديق.
وأما شرعًا: فلأن مفهوم الإيمان شرعًا، لو كان عين المفهوم الشرعي للإسلام، لما جاز في الشرع إثبات أحدهما ونفي الآخر، لكنه وقع في قوله تعالى: {قالت الأعراب آمنا قل لم تؤمنوا ولكن قولوا','2','359',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (532,'أسلمنا}. فنفى الإيمان وأثبت الإسلام، وفيه نظر مذكور في الشرح.
وإلى هذا كله أشار بقوله: وهو غير الإسلام والدين، فإنهما: الانقياد، والعمل الظاهر، ولهذا قال تعالى: {قل لم تؤمنوا وقولوا أسلمنا}.
وإنما جاز الاستثناء، أي: استثناء المسلم من المؤمن، لصدق','2','360',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (533,'المؤمن على المسلم، بسبب أن التصديق شرط صحة الإسلام شرعًا، وهو لا يستلزم اتحاد الإيمان والإسلام.
فالاستثناء لا يدل على أنه هو','2','361',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (534,'فروع على الحقيقة الشرعية المنقولة من اللغة لمناسبة.
الأول: النقل خلاف الأصل، يعني إذا دار اللفظ بين أن يكون منقولاً أو غير منقول، فالحمل على كونه غير منقول أولى.
إذ الأصل بقاء الأول على ما كان عليه، والنقل مناف لهذا الأصل فكان مرجوحًا.
ولأنه أي: النقل يتوقف على وضع اللفظ، بإزاء المعنى الأول، المنقول عنه، ونسخه، أي: نسخ المعنى الأول بأن يصير الاستعمال به مهجورًا.
ووضع ثان للفظ بإزاء المعنى المنقول إليه، وغير المنقول.','2','365',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (535,'يتوقف إلا على الوضع الأول فقط، وإذا كان كذلك فيكون النقل متوقفًا على مقدمات أكثر، مما يتوقف عليه غير المنقول، فيكون مرجوحًا، لأنه أندر وجودًا من الموقوف على مقدمات أقل.
الفرع الثاني: الأسماء الشرعية أي التي وضعها الشارع لمعنى، بحيث تدل عليه بلا قرينة، موجودة.
المتباينة: كالصلاة والزكاة والصوم.
والمترادفة: كالفرض والواجب والإنكاح والتزويج والمندوب والسنة وقد مر فلذا لم يذكره هنا.
والمتواطئة: كالحج، فإنه يطلق على: الإفراد، والتمتع، والقران.','2','366',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (536,'وهذه الثلاثة: مشتركة في مفهوم الحج وهو الإحرام، والوقوف، والطواف، والسعي، والحلق.
والمشككة: كالفاسق بالنسبة إلى من يرتكب الكبيرة الواحدة ويرتكب الكبائر المتعددة كذا قيل.
والمشتركة: على الأصح كالصلاة الصادقة على ذات الأركان الفعلية، والقولية، كالفرائض المكتوبة حالة الأمن، وعلى العادمة لجميع الأركان الفعلية، مثل صلاة المصلوب فإنها فقدت القيام والقعود والركوع والسجود، وعلى العادمة لبعضها (وذلك كصلاة) الجنازة فإنها عدم فيها الركوع والسجود دون القيام، فإن لفظ','2','367',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (537,'صلاة يطلق على الثلاثة وليس بينها قدر مشترك، فتعين الاشتراك لفظًا إذ الأصل في الإطلاق الحقيقة.
قال الصفي الهندي: وهو ضعيف، لأن كون الفعل واقعًا بالتحرم والتحلل، قدر مشترك بين تلك الصلوات، فلم لا يجوز أن يكون مدلولها واحدًا؟
قال: والأقرب: أنها متواطئة بالنسبة إلى الكل، لأن التواطؤ خير من الاشتراك.
ثم مثل للمشتركة بالطهور، فإنه يطلق على الماء والتراب، وما يدبغ به، وليس بينهما قدر مشترك يصح أن يكون مدلول','2','368',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (538,'اللفظ.
وأورد عليه أنه اكتفى بالتحرم والتحلل، وليكن بإزالة المانع قدرًا مشتركًا بين هذه الأمور.
والمعتزلة: سموا أسماء الذوات دينية، كالمؤمن والفاسق، أي قسموا الأسماء الشرعية إلى: أسماء الذوات، كالمشتقات مثل المؤمن، والكافر، والمصلي والمزكي، وسموها دينية.
وإلى أسماء الأفعال: كالصلاة والزكاة، وهي الشرعية، تفرقة بينهما.
وتبع المصنف في ذلك الإمام الرازي.','2','369',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (539,'وقال بهاء الدين السبكي: الخلاف بيننا وبين المعتزلة بالنسبة إلى أصول الشريعة، فإنهم يثبتون الوضع فيها ويسمونه بالديني.
ونحن لا نثبت ذلك إلا في الفروع.
ووافقه على هذا بعض المتأخرين.
وقال: إن كلام المصنف باطل، بل المنقول عن المعتزلة أن الأسماء المتعلقة بأصول الدين مشتقة كانت أو غيرها، دينية: كالإيمان، والمؤمن، والكفر، والكافر، وما تعلق بالفروع فهي: الشرعية كالصلاة والمصلي.
وقال الإسنوي نحوه قال: وممن نص عليه إمام الحرمين','2','370',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (540,'والغزالي
والحروف الشرعية: لم توجد، واستعملها الشرع في معناها اللغوي.
قال العراقي: وليس كذلك، بل هي كالأفعال توجد بالتبع، فإن نقل متعلق معاني الحروف من المعاني اللغوية، إلى المعاني الشرعية،','2','371',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (541,'مستلزم لنقلها كذا قال فتأمله.
والفعل الشرعي: وجد بالتبع، لنقل الاسم الشرعي، فيتبع الاسم مثل صلى الظهر، فإن الفعل عبارة عن المصدر والزمان.
وإذا كان المصدر شرعيًا، فلا بد أن كون الفعل شرعيًا ولم يوجد بطريق الأصالة بالاستقراء.
الفرع الثالث: صيغ العقود والفسوخ، كبعت وطلقت،','2','372',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (542,'وفسخت فهي في اللغة أخبار وفي الشرع قد تستعمل أخبارًا.
فإن استعملت لإحداث حكم فهل هي للإخبار عن ثبوت الأحكام كما كانت بحسب اللغة؟ وبه قالت الحنفية، وذلك بتقدير ثبوتها قبيل اللفظ، أو نقلت منها إلى إنشاء العقود والفسوخ، وهو المختار.
إذ لو كان أخبارًا، وكان ماضيًا، أو حالًا، لم يقبل التعليق على شرط؛ لأن التعليق عبارة عن توقيف دخول الشيء في الوجود على','2','373',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (543,'دخول غيره في الوجود، وهو والماضي والحال موجود فلا يقبله.
لكنها قابلة للتعليق إجماعًا، لاعتبار قول القائل: طلقتك إن دخلت الدار، وتطليقها عند دخول الدار.
وإلا أي وإن لم يكن ماضيًا ولا حالًا، بل لو كان خبرًا عن مستقبل، لم يقع معناه من الطلاق وغيره، فكان إذا قال: طلقتك لا يقع به طلاق كما لو قال: سأطلقك لكن الطلاق يقع به إجماعًا.
(وأيضًا: لو كانت هذه الصيغ أخبارًا، فإما أن تكون صادقة أو كاذبة، إذ الأخبار لا تخلو عنهما، وكلاهما باطل لأنها) إن كذبت لم يعتبر في الشرع، لكنها معتبرة فيه إجماعًا.
وإن صدقت فصدقها يتوقف على وقوع مدلولاتها؛ (لأن ما لا يقع مدلوله من الأخبار لا يكون صادقًا، فوقوع مدلولاتها) إما أن','2','374',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (544,'يتوقف على صدقها فيلزم الدور، لتوقف كل واحد من وقوع المدلول والصدق على الآخر.
وإليه أشار بقوله: "إما بها فيدور".
أو يتوقف على غير صدقها وإليه أشار بقوله: "أو بغيرها"، وهو باطل إجماعًا.
مثلًا صدق طلقتك إما أن يتوقف على وقوع الطلاق أو غيره.
فإن توقف على وقوع الطلاق، وهو يتوقف على صدق طلقتك فيدور.
وإن توقف صدق طلقتك على شيء آخر غير وقوع الطلاق فهو باطل إجماعًا، لأن وقوع الطلاق منتف عند انتفاء الصيغة.
ولا يقال: يجوز أن يكون صدقه باعتبار المستقبل، فلا يلزم الدور لأن البحث بتقدير قطع النظر عن الزمان، فلا تكون إخبارات، إذ بطلان اللازم يوجب بطلان الملزوم.
وأيضًا: لو كانت للإخبار، للزم أنه لو قال: للرجعية.','2','375',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (545,'طلقتك لم يقع الطلاق، كما لو نوى الإخبار عن الطلاق الماضي، لكنه يقع به الطلاق اتفاقًا.
فإذا بطل كونها للإخبار بهذه الأدلة تعين كونها للإنشاء.
وهنا نظر في الأدلة وزيادة تحقيق في الشرح.','2','376',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (546,'الثانية
في مباحث المجاز
المجاز: إما أن يقع في المفرد (من الألفاظ)، مثل الأسد للشجاع، فإنه يستعمل في غير موضوعه الأصلي لمناسبة.
أو يقع في المركب: وهو أن يستعمل كل واحد من الألفاظ المفردة','2','377',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (547,'في موضوعه الأصلي، ويسند الفعل إلى غير فاعله الحقيقي، مثل قول الشاعر: وهو الصلتان العبدي- وكان مسلمًا-.
أشاب الصغير وأفنى الكبير كر الغداة ومر العشي.
فأشاب، وأفنى، وكر، ومر: مستعملة في موضوعها اللغوي، والإسناد مجاز؛ لأن المشيب والمغني هو الله تعالى.
قال العراقي: والصواب التعبير بالتركيب، لأن التجوز في هذا القسم إنما هي النسبة بين المفردين.
لا يقال: هذا البيت فيه إطلاق الصغير على الشيخ، باعتبار ما كان، فهو مجاز في المفرد والمركب.','2','378',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (548,'لأن الصغير ليس ركنًا في الإسناد، لوقوعه فضلة، فإنه مفعول فلا اعتبار به.
وقال الشيخ سعد الدين: وقد اختلفوا في نحو أشاب الصغير، لعدم كون كر الغداة، ومر العشى، فاعليه حقيقة كما تقدم؛ لأن مدلول إسناد الفعل إلى الشيء، هو قيامه به وثبوته له، بحيث يتصف به، وهذا لا يصح ظاهرًا فيما أسند إلى غير ما هو له، من المصدر، والزمان، والمكان، وغيرها، فلابد من تأويل، إما في المعنى، أو في اللفظ.
واللفظ: إما المسند، أو المسند إليه، أو الهيئة التركيبية، الدالة على الإسناد وإلا لكان كذبًا.
الأول: أن لا مجاز فيه بحسب الوضع، بل بحسب العقل حيث أسند الفعل إلى غير ما يقتضى العقل إسناده إليه، وهو قول الشيخ عبد القاهر والإمام الرازي وجميع علماء البيان.','2','379',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (549,'الثاني: أن المسند مجاز عن المعنى الذي يصح إسناده إلى المسند إليه، المذكور، وهو قول ابن الحاجب.
الثالث: أن المسند إليه استعارة بالكناية عما يصح الإسناد إليه حقيقة، وهو قول السكاكى.
الرابع: أنه لا مجاز في شيء من المفردات، بل يشبه التلبس الغير الفاعلي بالتلبس الفاعلي، فاستعمل فيه اللفظ الموضوع لإفادة التلبس الفاعلي، فيكون استعارة تمثيلية كما في: أراك تقدم رِجلًا وتؤخر أخرى.
وهذا ليس قولًا لأحد من علماء البيان ولكنه ليس ببعيد.
أو المجاز فيهما أي: في الإفراد والتركيب.
كقوله: "أحياني اكتحالي بطلعتك" أي: سرني رؤيتك، فاستعمل','2','380',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (550,'الإحياء في السرور، والاكتحال في الرؤية. وهو مجاز. والإسناد أيضًا مجاز، لأن فاعل السرور هو الله تعالى لا الرؤية.
قال العبري: والحق أن إسناد الإحياء إلى الاكتحال مجاز، لا إسناد المسرة إلى الرؤية، لأن اللغة لم تبن على مذهب الأشعري.
فعلم من هذه الأمثلة أن المجاز قد وقع في اللغة، وهو الأصح، خلافًا للأستاذ أبي إسحاق الإسفراييني.
ووقع أيضًا: المجاز في القرآن، والحديث على الأصح.
ومنعه أبو بكر بن داود والظاهرية: في القرآن والحديث.','2','381',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (551,'(ونقل عن ابن القاص).
قال العراقي: والمشهور عن ابن داود، منعه في القرآن خاصة كما ذهب إليه بعض الحنابلة، وبعض المالكية.
لنا: قوله تعالى: {جدارًا يريد أن ينقض}.
فإن الظاهر من لفظ الإرادة، غير مراد لامتناع كون الجدار مريدًا، إذ لا شعور له، فوجب صرفها إلى غير الظاهر، وهو المجاز، وهو','2','382',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (552,'هنا استعارة، لأنه شبه إشرافه على السقوط، بالإرادة المختصة بذوات الأنفس.
ولا يقال: الإرادة هنا حقيقة بخلق الله تعالى فيه إرادة للقطع، بأنه ليس بمراد، وإن كان ممكنًا. فإنما يقع عند التحدي، وإظهار المعجزات. وإذا جاز في القرآن، جاز في الحديث.
وأما وقوعه في الحديث: فلقوله (عليه الصلاة والسلام) "حتى تذوقي عسيلته ويذوق عسيلتك".','2','383',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (553,'قال ابن داود فيه، أي: في المجاز إلباس، لكونه لا ينبني عن معناه بنفسه، فلا يناسب كلام الشرع؛ لأنه مبين للشرع.
قلنا: لا إلباس مع القرينة، وهو إنما يستعمل في الشرع مع القرينة.
قال مخصصًا حجته القرآن: ل وقع المجاز في القرآن، لكان الله تعالى متكلمًا بالمجاز.
ولو كان متكلمًا بالمجاز لجاز أن يقال: الله تعالى متجوز، إذ المتجوز هو المتكلم بالمجاز.','2','384',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (554,'لكن لا (يجوز أن) يقال لله تعالى، بأنه متجوز.
قلنا: لعدم الإذن، فإن أسماء الله- تعالى- توقيفية.
فلا يطلق عليه إلا بإذن، وقد انتفى، فلانتفاء إذن الشارع امتنع إطلاق اسم المتجوز على الله- تعالى- لأنه لا يصح لغة، واللازم صحته لغة.
أو عدم جواز الإطلاق، لإيهامه الاتساع فيما لا ينبغي، لأن قولنا فلان متجوز، يوهم أنه يتسمح، ويتوسع، فيما لا ينبغي من الأقوال والأفعال، لاشتقاقه من الجواز، وهو التعدي.','2','385',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (555,'تنبيه: المجاز يطلق بحسب الاشتراك على ما سبق، وعلى كلمةٍ تغير حكم إعرابها بسبب زيادة أو نقصان كما سيجئ، ووعلى نفس الإعراب المتغير.

انتهى بحمد الله تعالى الجزء الثاني من
كتاب
مختصر تيسير الوصول شرح منهاج الأصول
ويليه
إن شاء الله
الجزء الثالث
وأوله
الثالثة
شرط المجاز العلاقة','2','386',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (556,'الثالثة
شرط المجاز العلاقة، يعني أنه لا بد في المجاز من العلاقة بينه وبين الحقيقة وإلا فهو وضع جديد، أو غير مفيد، لأنه إن عين بإزاء المعنى المجازي فوضع جديد، وإلا فلا دلالة.
والعلاقة: تعلق ما للمعنى المجازي بالمعنى الحقيقي وأعم من أن يكون اتصالاً وانضمامًا بين الذاتين كما في المجاورة، أو غيره كما في البواقي.
وبعد الاتفاق على وجوب العلاقة في المجاز هل يشترط في آحاد المجازات أن ينقل بأعينها عن أهل اللغة أم لا؟ بل يكتفي بوجود','3','5',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (557,'العلاقة؟
اختلف فيه: وصحح الإمام وأتباعه الاشتراط، وجزم به المصنف. ونقل عن ابن الحاجب اختيار خلافه.
قال العراقي: وتقدم من المصنف في الحقيقة والمجاز ما يوافقه، فتأمله.
قال القرافي: والخلاف إنما هو في الأنواع، لا في جزيئات النوع الواحد. فالقائل بالاشتراط يقول لا بد أن تضع العرب نوع التجوز بالكل إلى الجزء وبالسبب إلى المسبب. وإلى هذا أشار المصنف بقوله: المعتبر نوعها.
(وقال الكرماني: إذا علمنا أنهم أطلقوا اسم اللازم على الملزوم، يكفينا هذا في إطلاق كل لازم على ملزومه، أو لا بد في كل صورة من جزئيات إطلاق اللوازم على الملزومات من السماع عنهم في','3','6',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (558,'ذلك اللازم والملزوم بعينه.
وهذا يقتضي أن الخلاف في جزيئات النوع.
وهو مقتضى كلام المحقق والشيخ سعد الدين، والله أعلم) والعلاقة المعتبر نوعها: خمسة وعشرون.
وقال بعضهم: أحد وثلاثون.','3','7',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (559,'والإمام في المحصول أورد منها اثني عشر وجهًا.
والمصنف أورد مما في المحصول أحد عشر نوعًا، وترك هنا واحدًا، وهو إطلاق المشتق بعد زوال المشتق منه. لأنه ذكره قبل، حيث قال: وشرط كون المشتق حقيقة إلى آخره.
والباقي مما قيل متداخل.
بل قال بعض المحققين: إن أنواع العلاقة المنحصرة في المجاورة نحو: السببية، وهو: إطلاق اسم السبب على المسبب، أي: العلة على المعلول.
والسببية على أربعة أقسام:
القابلية: ويعبر عنه بالمادي، مثل: سال الوادي.
فأطلق الوادي وأراد به الماء، لأن الوادي سبب قابل للماء،','3','8',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (560,'لكونه محلاً له.
ويصح أن يكون هذا من باب تسمية الحال باسم المحل.
أو المجاز بالنقصان، ولا يمتنع أن يجتمع في الشيء علاقتان فأكثر.
والصورية: كتسمية اليد قدرة، فإنك سميت الشيء باسم صورته، لأن القدرة صورة لليد، لكون القدرة حاله فيها.
قيل: وهو معكوس، والصواب تسمية القدرة يدًا.
والفاعلية: مثل نزل السحاب، أي: المطر، والسحاب سبب','3','9',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (561,'فاعلي للمطر، عرفًا لا حقيقة.
والغائية: كتسمية العنب خمرًا، فأطلق الخمر على العنب.
لأنها العلة الغائية عندهم.
والمسببية: هذا هو النوع الثاني: وهو إطلاق اسم المسبب على السبب.
كتسمية المرض المهلك بالموت، فإن المرض المهلك سبب الموت، والموت مسبب له.
وإطلاق اسم السبب على المسبب أولى من عكسه.
وإليه أشار بقوله: والأول أولى: لأن علاقة السببية أقوى من علاقة','3','10',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (562,'المسببية، لأن السبب المعين يستلزم المسبب المعين لذاته، والمسبب المعين لا يستلزم السبب المعين لذاته بل يقتضي لذاته سببًا ما، وهو المعنى بقوله: للاستلزام على التعيين وأولاها الغاية.
أي: إطلاق اسم السبب الغائي على المسبب أولى من الأسباب الثلاثة.
وفي بعض النسخ: ومنها الغائية، ومعناه: وأولى منها الغاية والأولى أوضح وأكثر.
(وإنما كانت الغائية أولى)، لأنها علة في الذهن، لأنها الباعثة للفاعل على الفعل، ومعلولة في الخارج، فوجدت فيها العلاقتان، فتكررت العلاقة بينها وبين المسبب فكانت أولى بإطلاق اسمها على المسبب','3','11',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (563,'من سائر العلل.
والمشابهة كالأسد للشجاع والمنقوش وتسمى استعارة وهي النوع الثالث.
وهي: تسمية الشيء بما يشبهه، إما في المعنى كالاشتراك في صفة، ويجب أن تكون الصفة ظاهرة في المعنى الموضوع له، لينتقل الذهن منه إليها، فيفهم المعنى الآخر، أعني غير الموضوع له باعتبار ثبوت تلك الصفة له.
ولا يخفى أن مجرد ثبوتها له لا يوجب الفهم لكونها مشتركة، بل لا بد من قرينة خصوص، مثلاً: إذا أطلقنا الأسد ينتقل منه إلى الشجاع، لكن لا يفهم من الإنسان الشجاع، إلا بقرينة، مثل: في الحمام، بخلاف إطلاق اسم الأسد على الأبخر.
أو في الصورة كإطلاق أسد، على الصورة المنقوشة في الحائط','3','12',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (564,'ويسمى هذا النوع من المجاز استعارة.
وهو قريب مما ذكره السكاكي.
لأن حاصله هو أن الاستعارة عبارة عن إطلاق لفظ على ما يشابه مدلوله في صفة ظاهرة مدعيًا أنه من جنس مدلوله الحقيقي بإفراده في الذكر مجردًا عن حرف التشبيه.
قال العراقي: وكلام المصنف يحتمل أن يكون راجعًا إليهما معًا، أو إلى الثاني فقط.
وعلى التقديرين: فهو مخالف للإمام والصفي الهندي.
فإنهما قالا: إن المسمى بالاستعارة، هو القسم الأول فقط، والإمام لم ينف الاستعارة عن القسم الثاني بل أخل بذكره.','3','13',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (565,'والمضادة: وهي النوع الرابع.
وهي: تسمية الشيء باسم ضده، مثل قوله تعالى: } وجزاء سيئة سيئة مثلها {.
فأطلق السيئة على الحسنة، التي هي ضدها، لكون جزاء السيئة حسنًا مشروعًا.
والكلية: وهي النوع الخامس.
وهو: إطلاق الكلي على الجزء، كإطلاق اسم القرآن لبعضه، إذا جعلناه اسمًا للكل المجموع.','3','14',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (566,'والجزئية وهي النوع السادس على الكلية: كالأسود (إذا جعل اسمًا) للزنجي، لأن الأسود اسم لجزء الزنجي لا لكله لأنه ليس أسود بتمامه، والأول وهو إطلاق اسم الكل على الجزء أقوى، فيكون أولى من عكسه عند التعارض للاستلزام، لأن الكل يستلزم الجزء دون عكسه.
والاستعداد: وهي النوع السابع.
وهو: أن يسمى الشيء المستعد لأمر باسم ذلك الأمر كتسمية الخمر','3','15',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (567,'الذي في الدن بالمسكر، فإنه مستعد للإسكار، وإليه أشار بقوله: "كالمسكر على الخمر في الدنط.
وتسمية الشيء باسم ما كان عليه، سواء كان جامدًا، كإطلاق العبد على العتيق، أو مشتقًا، كإطلاق الضارب على من وقع منه الضرب، وهذا ساقط في كثير من النسخ، وعليه اعتمدت في أول المسألة.
وعلاقة المجاورة المجوزة لإطلاق أحد المتجاورين على الآخر.','3','16',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (568,'وهي: النوع الثامن، كالراوية للقربة، التي هي ظرف الماء.
والراوية اسم للجمل الحامل لها لمجاورتها له.
والزيادة: وهي النوع التاسع، وقرينته: أن ينتظم الكلام بإسقاط كلمة، فيحكم بزيادتها، مثل قوله تعالى: } ليس كمثله شيء {فإن الكاف هنا زائدة، لأن المراد من الآية نفي المثل، وهو إنما يحصل بدونه، فاستعمل في غير ما وضع له، لأنه وضع للمثل، وهاهنا غير مستعمل فيه، فالعلاقة المجوزة لإطلاق الكاف على غير مدلوله اللغوي كونها زائدة.
والتحقيق: أن الكاف ليست زائدة، ولا يلزم محذور ويصير','3','17',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (569,'المعنى من كان على صفته المثل وشبهه فهو منفي فكيف المثل؟ .
وحينئذٍ يكون الكلام لنفي التشبيه والتشريك من غير تناقض، وله في الشرح زيادات وتحقيق فراجعه.
والنقصان: وهي النوع العاشر.
وقرينته أن ينتظم الكلام، إذا زيد عليه ما نقص منه.
مثل قوله تعالى: } واسأل القرية {، أي: أهل القرية، وإلا لم يستقم الكلام، فالعلاقة المجوزة لإطلاق المسئول على القرية إنما هو نقصان أهل.','3','18',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (570,'وفي الشرح أن المجاز بالزيادة، والنقصان، إنما كان هو في التركيب، لا في الأفراد، (ولم يحضرني قائله)، وما ذكرته هنا تبعت فيه العبري فتأمله.
النوع الحادي عشر: هو قوله: والتعلق الحاصل بين المصدر، واسم المفعول، واسم الفاعل ويدخل فيه ستة أقسام:','3','19',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (571,'(أحدهما): إطلاق اسم الفاعل على المفعول، كقوله تعالى: } من ماء دافق {أي مدفوق.
الثاني: عكسه نحو} حجابًا مستورًا {أي ساترًا.
الثالث: إطلاق المصدر وإرادة الفاعل، نحو رجل عدل، أي عادل.
الرابع: عكسه نحو: "قم قائمًا" أي: قيامًا.
الخامس: إطلاق اسم المفعول على المصدر، نحو: } بأيكم المفتون {أي: الفتنة.
السادس: عكسه كالخلق المخلوق في قوله تعالى: } هذا خلق','3','20',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (572,'الله {أي مخلوقه: فأطلق المصدر على اسم المفعول، ولا يضر كون المخلوق مشتقًا من الخلق، فيكون من باب إطلاق الجزء وإرادة الكل، لأنه اجتمع فيه مجازان فمثل به لأحدهما والعلاقة بالكسر ويصح الفتح بتأويل.
الرابعة
- المجاز بالذات، أي: بالأصالة، لا يكون في الحرف لعدم الإفادة للحرف وحده، بل لا يفيد إلا بذكر متعلقه، كما مر.','3','21',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (573,'وإذا لم يفد وحده فلا يدخله المجاز، لأن دخول المجاز فرع كون الكلام مفيدًا.
فإذا انضم إلى الحرف ما ينبغي أن ينضم إليه كان حقيقة، كقولنا: زيد في الدار، وإلا كان مجازًا كقوله تعالى: } ولأصلبنكم في جذوع النخل {.','3','22',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (574,'وله زيادة تحقيق في الشرح فراجعه.
ولا يكون المجاز بالذات، في الفعل والمشتق، لأنهم يتبعان الأصل، وهو المصدر.
فالمجاز يدخل في المصدر أولاً، ثم يسرى منه إليهما.
فلا يقال: الحال نطقت، أو ناطقة بكذا، إلا بعد تقدير المجاز في النطق المشتق منه، بأن يشبه النطق بالدلالة فيقال: الحال نطقت، بدل دلت، وناطقة بدل دالة.','3','23',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (575,'ولا يكون المجاز بالذات، في العلم لأنه لم ينقل لعلاقة إلا إذا كان العلم متضمنًا لنوع من وصفية نحو: زيد حاتم جودًا وخالد شجاعة.
وإذا لم ينقل لعلاقة لا يدخله المجاز، لأن شرط المجاز العلاقة. وفهم من كلام المصنف أن ما عدا هذه المذكورات يدخل فيه المجاز بالذات.
قال في المحصول: وهو اسم الجنس فقط نحو أسد.
الخامسة
المجاز خلاف الأصل، على معنى أن اللفظ إذا دار بين الحقيقة','3','24',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (576,'والمجاز، فحمله على المجاز مرجوح، وعلى الحقيقة راجح لاحتياجه إلى الوضع الأول.
والمناسبة بين المعنيين على أحد الوجوه المعتبرة المذكورة.
والنقل من المعنى الحقيقي إلى المعنى المجازي.
والحقيقة تحتاج إلى الوضع فقط، فيكون المجاز مرجوحًا لاحتياجه إلى مقدمات أكثر.
ولإخلاله بالفهم، لأنه يحتاج إلى القرينة الحالية أو المقالية.
وقد تخفي على السامع، فيحمل اللفظ على المعنى الحقيقي، مع','3','25',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (577,'أن المقصود المجازي.
هذا إذا لم يغلب المجاز.
فإن غلب على الحقيقة كالطلاق، فإن استعماله في معناه المجازي، وهو رفع قيد النكاح، غالب على معناه الحقيقي، وهو الإرسال تساويًا عند المصنف تبعًا للإمام، فلا ينصرف إلى أحدهما إلا بالنية.','3','26',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (578,'لأن كل واحد منهما راجح من وجه، مرجوح من وجه فهو مجمل.
وذكر الصفي الهندي أنه عزي إلى الشافعي - رضي الله عنه - وأورد أنه إنما يستقيم إذا لم يكن المجاز بعض الحقيقة، كالراوية.
فأما إذا كان منهما: فإن كان في صورة النفي انتفى المجاز، لأنه يلزم من نفي الأعم نفي الأخص.','3','27',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (579,'وإن كان في صورة الإثبات: تثبت الحقيقة مطلقًا، لأنه إن حمل عليها فواضح، وإن حمل على المجاز، فلأنه يلزم من ثبوت الأخص ثبوت الأعم.
قال العراقي: وإن صح النقل عن الشافعي - رضي الله عنه - فهو محمول على هذا التفصيل.
والأولى: الحقيقة المرجوحة، عند أبي حنيفة - رضي الله عنه - والمجاز الغالب، عند أبي يوسف - رضي الله عنهما - قال القرافي:','3','28',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (580,'قول أبي يوسف هو الحق، لأن الظهور هو المكلف به.
وهنا أشياء مهمة في الشرح يتعين الوقوف عليها.
* * *','3','29',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (581,'السادسة
يعدل من لفظ الحقيقة - الذي هو الأصل - إلى المجاز - الذي هو غير الأصل - لثقل لفظ الحقيقة على اللسان، كالخنفقيق وهي الداهية إلى لفظ آخر خفيف بينه وبين الاسم علاقة كالموت، فيقال: وقع الموت.
أو يعدل لحقارة معناه الحقيقي، واستقذاره فيترك الحقيقة لذلك، ويعدل إلى المجاز تنزيهًا منه كما يعدل من لفظ خرأة - بكسر الخاء','3','30',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (582,'المعجمة - إلى قضاء الحاجة، أو الغائط.
وقال العبري: كما يعبر عن قضاء الحاجة بالغائط، الذي هو اسم للمكان المطمئن من الأرض.
أو يعدل لبلاغة لفظ المجاز، ولفظه الحقيقي غير بليغ، كما يعبر عن السرور بالإحياء.
أو عظمة في معناه: أي: في معنى المجاز، كالمجلس العالي بدلاً عن فلان، فيقال: سلام على المجلس العالي، فيترك الحقيقة لأجل التعظيم.
أو زيادة بيان حال المذكور، أي: فيه تقوية لما يريده المتكلم كالأسد، حيث يعبر به عن الرجل الشجاع فإن قولك رأيت أسدًا يرمي،','3','31',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (583,'فيه من المبالغة ما ليس في قولك إنسانًا يشبه الأسد في الشجاعة.
السابعة
اللفظ قد لا يكون حقيقة ولا مجازًا كما في الوضع الأول، أي كاللفظ في الوضع الأول، قبل استعماله فيه وفي غيره، لأن الاستعمال جزء لمفهوم كل منهم أو لازم له فقبل الاستعمال لا يكون أحدهما.
والأعلام: كجعفر وأسد، فليست بحقيقة، لأنها ليست بوضع','3','32',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (584,'واضع اللغة، وفيه نظر.
ولا مجاز لأنها مستعملة لغير علاقة. وبيانه على ما ينبغي في الشرح.
وقد يكون اللفظ بالنسبة إلى معنى واحد حقيقة ومجازًا معًا باصطلاحين كالدابة بالنسبة إلى الحمار، فإنه حقيقة لغوية، ومجاز','3','33',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (585,'عرفي، لكون الدابة في العرف مخصوصة بالفرس والبغل.
وإنما قيل: بالنسبة إلى معنى واحد، لأن كون اللفظ الواحد حقيقة ومجازًا معًا بالنسبة إلى معنيين لا يحتاج إلى بيان لكثرة وقوعه.
وإنما قيد "باصطلاحين" لأنه يمتنع أن يكون اللفظ الواحد، حقيقة ومجازًا معًا باصطلاح واحد، وإلا يلزم أن يكون ذلك اللفظ موضوعًا لذلك المعنى، وغير موضوع له في اصطلاح واحد وأنه محال.
وعلم مما تقدم: أن اللفظ الواحد بالنسبة إلى المعنى الواحد قد يكون حقيقة فقط، أو مجازًا فقط، أو حقيقة ومجازًا معًا، أو لا حقيقة ولا مجازًا.
الثامنة
علامة الحقيقة:','3','34',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (586,'سبق الفهم، أي فهم السامع العارف باللغة عند سماع اللفظ من "غير قرينة" إلى ذلك المعنى؛ لأن اللفظ لو لم يكن موضوعًا لذلك المعنى لما سبق إلى الفهم دون غيره.
والتقييد "بغير قرينة" ليخرج مثل قولك: رأيت أسدًا يرمي بالنشاب، فإنه وإن سبق المعنى المجازي، لكن بسبب القرينة.
وفهم من جعله، هذه علامة للحقيقة أن علامة المجاز عكسها.
وعلامة الحقيقة أيضًا: العراء عن القرينة، عند استعماله مثلاً، إذا استعمل اللفظ في أحد المعنيين بلا قرينة، وفي الآخر بقرينة، دل على أن اللفظ حقيقة بالنسبة إلى المعنى الذي استعمل فيه بدون القرينة وإلا لما اقتصر على ذلك اللفظ.
وفهم منه أن علامة المجاز عدم العرو عن القرينة.','3','35',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (587,'وعلامة المجاز: الإطلاق على المستحيل، أي: إذا علق اللفظ بما يستحيل تعليق به، (علم أنه) غير موضوع له، فيكون مجازًا مثل قوله تعالى: } واسأل القرية {فإن سؤال القرية مستحيل.
وافهم أن عدم الاستحالة علامة للحقيقة.
وعلامة المجاز: الإعمال في المعنى المنسي سواء كان شرعيًا أو عرفيًا كالدابة، فإنها موضوعة في اللغة لكل ما يدب على الأرض.
ثم خصت في العرف العام بالفرس والبغل، وهجر استعماله فيه في الحمار، فإذا استعمل في العرف في الحمار، عرف كونه مجازًا عرفيًا للحمار.
وفهم من قوله: "المنسي" أن الوضع الأول صار مهجورًا غير ملاحظ أصلاً، حتى لو كان الوضع الأول ملاحظًا كان حقيقة.
وافهم أيضًا أن عكس هذه العلامة علامة للحقيقة وبقيت علامات','3','36',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (588,'أخرى تطلب من الشرح.

<span data-type="title" id=toc-75><span data-type="title" id=toc-76>الفصل السابع: في تعارض ما يخل بالفهم </span>مراد المتكلم</span>،
وذكر منها خمسة: وهو الاشتراك، والنقل، والمجاز، والإضمار، والتخصيص.
وخصها بالذكر لكثرة وقوعها، وتعارض بعضها مع بعض.
(وظاهر كلام المصنف أنه) إذا انتفت هذه الخمسة لم يبق خلل أصلاً، لأنه إذا انتفى احتمال الاشتراك، علم أن اللفظ موضوع','3','37',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (589,'لحقيقة واحدة، لكن لا يتعين المراد لاحتمال كونه منقولاً منها إلى معنى آخر.
فإذا انتفى النقل، علم أن اللفظ له حقيقة واحدة، لم ينقل منها إلى غيرها، لكنه لا يتعين المراد لاحتمال كونه مجازًا، إذ يجوز أن لا يريد المتكلم بهذا اللفظ معناه الحقيقي، بل معناه المجازي.
فإذا انتفى المجاز، علم أن المراد من اللفظ ما وضع له لا غير، لكنه لا يتعين المراد، لاحتمال أن يكون اللفظ مضمرًا بسببه ولا يمكن حمله على مدلوله الحقيقي فإذا انتفى الإضمار، تعين كون المراد من اللفظ ما وضع له لا غير، لكنه لا يتعين المراد من جميع الوجوه، لاحتمال التخصيص، إذ يجوز أن يكون المراد به بعض ما وضع له دون جميعه، لاحتمال كونه عامًا مخصصًا.
فإذا انتفى التخصيص أيضًا، انتفى الخلل بالكلية، وحصل الفهم التام.','3','38',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (590,'وإنما تخل هذه الأشياء باليقين، لا بالظن، فإن الظن حاصل مع وجود هذه الأمور.
تنبيه: الباقي من الاحتمالات المخلة باليقين خمسة وهي: النسخ، والتقديم والتأخير، والمعارض العقلي، وتغيير الإعراب، والتصريف.
وذلك التعارض بين الخمسة التي ذكرها المصنف يقع على عشرة أوجه، لأن الاشتراك مع الأربعة الباقية، يأتي منه أربع معارضات، ثم النقل مع الثلاثة الباقية يأتي ثلاثة، ثم المجاز مع الإضمار والتخصيص اثنان، ثم الإضمار مع التخصيص واحد.
وكل منها مرجوح بالنسبة إلى ما بعده، وراجح على ما قبله، إلا','3','39',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (591,'الإضمار والمجاز فإنهما سيان.
الأول: النقل أولى من الاشتراك، لإفراده في الحالتين، أي مدلول المنقول مفرد في الحالة التي قبل النقل، والحالة التي هي بعده لا إجمال فيه يتعين مدلوله، بخلاف المشترك، فإن فيه إجمالاً كالزكاة، يحتمل الاشتراك بين النماء، والقدر المخرج من النصاب، ويحتمل أن يكون موضوعًا للنماء ثم نقله الشرع إلى القدر المخرج.
الثاني: المجاز خير منه، أي: من الاشتراك، لكثرته.
أي: لكثرة وجود المجاز في استعمالات كلامهم.','3','40',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (592,'حتى قال ابن جني: أكثر اللغات مجاز، والكثرة تفيد الظن في محل الشك فتفيد الرجحان.
وأيضًا: يترجح المجاز على الاشتراك لأن فيه إعمال اللفظ مع القرينة في المعنى المجازي ودونها في المعنى الحقيقي، فلا إهمال فيه على التقديرين. بخلاف المشترك فإنه بدون القرينة لا يفيد المراد ففيه إهمال حينئذٍ.
وما يفيد المراد، ويكون معمولاً في جميع الأحوال خير مما يفيده في بعض الأحوال، ويكون مهملاً في البعض، لأن الأصل إعمال اللفظ لا إهماله، كالنكاح يحتمل أن يكون مشتركًا بين العقد والوطء، وأن يكون حقيقة في أحدهما وهو على الأصح العقد مجازًا في الآخر.
الثالث: الإضمار خير من الاشتراك لأن احتياجه إلى القرينة في صورة واحدة، وذلك حيث لا يتعين المعنى المراد، بأن لا يمكن إجراؤه على ظاهره.','3','41',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (593,'أو حيث تعدد الإضمار.
واحتياج الاشتراك إليها أي: إلى القرينة، في صورتين أو أكثر فيحتاج إلى القرينة في جميع أحواله، مثل قوله تعالى: } واسأل القرية {فإن القرية يحتمل أن تكون مشتركة بين الأهل والأبنية، أو حقيقة في الأبنية فقط والأهل مضمر.
الرابع: التخصيص خير من الاشتراك؛ لأنه خير من المجاز كما سيأتي، والمجاز خير من الاشتراك كما مر.
فالتخصيص خير من الاشتراك مثل قوله تعالى: } ولا تنكحوا ما نكح آباؤكم {فإنه يحتمل أنه مشترك بين العقد والوطء، فيكون','3','42',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (594,'معناه تحريم موطوءة الأب، ومنكوحته من غير الوطء.
أو مختص بالعقد، وخص عنه الفاسد من غير وطء.
الخامس: المجاز خير من النقل، لعدم استلزامه - أي: المجاز - نسخ المعنى الأول، وهجره أصلاً. بخلاف النقل كالصلاة، فإنها في اللغة الدعاء وفي استعمالها في المعنى الشرعي، يحتمل أن يكون بطريق النقل، ويحتمل أن يكون بطريق المجاز من باب إطلاق اسم الجزء على الكل لاشتمال الصلاة على الدعاء.
السادس: الإضمار خير من النقل، لأنه - أي: الإضمار - مثل','3','43',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (595,'المجاز، والمجاز خير من النقل لما عرفت، كقوله تعالى: } وحرم الربا {فإن الأخذ مضمر والربا نقل إلى العقد، فالربا في اللغة: الزيادة.
فيحتمل أن يحمل على حقيقته في اللغة، والأخذ مضمر أي: وحرم أخذ الربا.
أو نقل إلى العقد لاشتماله على الزيادة أي: وحرم العقد المشتمل على الزيادة.
السابع: التخصيص أولى من النقل لما تقدم من كونه خيرًا من','3','44',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (596,'المجاز، والمجاز خير من النقل، مثل قوله تعالى: } وأحل الله البيع {فإنه - أي: البيع - في اللغة للمبادلة مطلقًا، وخص الفاسد فيكون البيع في الآية عامًا مخصصًا، فإن الحلال في الشرع: إنما هو العقد المستجمع للشرائط، أو نقل من معناه اللغوي إلى العقد المستجمع لشرائط الصحة.
الثامن: الإضمار مثل المجاز لاستوائهما في القرينة مثل: هذا ابني، فإنه يحتمل أن يكون مجازًا، يريد به أنه معزوز محبوب، أو أمضر مثل، أي: مثل ابني، فيقتضي بقاء اللفظ مجملاً إلى أن يظهر','3','45',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (597,'دليل رجحان أحدهما.
وقيل: يقدم المجاز لكثرته، واختاره الصفي الهندي، وجزم به القرافي.
التاسع: التخصيص خير من المجاز، لأن الباقي من مفهوم اللفظ بعد التخصيص متعين، لأن في صورة التخصيص انعقد اللفظ دليلاً على كل الأفراد، فإذا خرج البعض بدليل بقي معتبرًا في الباقي، من غير احتياج إلى تأمل واجتهاد.
والمجاز ربما لا يتعين، بأن يكون للفظ مجازات متساوية، والمتعين للفهم دائمًا أولى مما لم يتعين وقتًا ما.
ولا يفهم حينئذٍ، مثل قوله تعالى: } ولا تأكلوا مما لم يذكر اسم الله عليه {.','3','46',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (598,'فإن المراد التلفظ، أي: ما ترك التلفظ باسم الله تعالى عليه عند ذبحه، وخص منه النسيان، أي: متروك التسمية بالنسيان، أو يكون المراد به المجاز، وهو الذبح، أي: لم يذبح على اسم الله تعالى، أو ذبح على اسم الأوثان، ويحرم غير المذبوح شرعًا وهنا بحث حسن متعلق بالآية في الشرح.
العاشر: التخصيص خير من الإضمار لما مر أن الإضمار والمجاز متساويان، وأن التخصيص خير من المجاز، فيكون التخصيص خيرًا من الإضمار، مثل قوله تعالى: } ولكم في القصاص حياة {يحتمل أن يكون فيه إضمار، أي: في مشروعية القصاص، لأن القاتل إذا','3','47',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (599,'علم أنه يقتل انزجر عن القتل وذلك حياة لنفسين.
أو لا إضمار فيه، ويكون في القصاص نفسه حياة، لإنزجار الناس عن القتل بقتل الفاعل، وخص عنه المقتص منه.
تنبيه: تتنبه به على أن التخصيص المتقدم الذي هو راجح على الكل، إنما هو التخصيص بحسب الأعيان دون الأزمان، لأن التخصيص بحسب الأزمان هو النسخ كما سيجيء، وهو غير راجح على الكل، بل مرجوح بالنسبة إلى الكل، فإن الاشتراك خير من النسخ، لأنه أي الاشتراك لا يبطل الخطاب، بل يورث التوقف إلى ظهور المراد منه، والنسخ يبطله بالكلية، فيكون الاشتراك راجحًا عليه، إذ الأصل عدم بطلان الخطاب.
ولا شك أن الاشتراك يورث إلباسًا، ولأجله كان خلاف الأصل، وكلما كان الالتباس أقل كان خيرًا، فلذا قال: والاشتراك بين علمين','3','48',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (600,'خير منه - أي: من الاشتراك بين علمٍ ومعنى.
والالتباس في الأعلام أقل مما هو في أسماء المعاني، لأن الأعلام تطلق على أشخاص معدودة، وأسماء المعاني تتناول المسمى في أي ذات كان من الأفراد، الغير متناهية.
ولا شك أن الالتباس في الأشخاص المتعددة أقل وقوعًا مما في الأفراد الغير المتناهية.
مثاله أن يقال: رأيت الأسودين، فحمله على شخصين كل اسمه أسود أولى، من حمله على شخص اسمه أسود وآخر لونه أسود.
ولا فرق بين علم الأعيان وعلم الجنس، مثل أسامة لحقيقة الأسد وشخص إنسان.
والاشتراك بين علمٍ ومعنى خير من الاشتراك بين معنيين لما ذكرنا.','3','49',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (601,'مثاله: الأسودان حمله على العلم والمعنى أولى من شخصين لونهما أسود.
وإليه أشار بقوله: وهو خير منه بين معنيين.

<span data-type="title" id=toc-77>الفصل الثامن: </span>في تفسير حروف يحتاج، أي يحتاج المجتهد إليها (في استنباط) الأحكام، لوقوعها في أدلة الفقه، وفيه مسائل:','3','50',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (602,'الأولى
الواو العاطفة للجمع المطلق، فهي في عطف الجملة التي لا محل لها من الإعراب لإفادة ثبوت مضمون الجملتين، لأن مثل قولنا: ضرب زيد أكرم عمرو بدون العاطف، يحتمل الإضراب، والرجوع عن الأول، فلا يفيد ثبوتهما بخلاف ما إذا عطفت.
وأما في عطف المفردات وما في حكمها من الجمل التي لها محل من الإعراب فهي لإفادة الجمع في حكم المعطوف عليه من الفاعلية، أو المعفولية، أو المسندية، أو غير ذلك.
ولا يجب الاجتماع في الزمان، وهو المعبر عنه بالمعية، ولا عدم الاجتماع، وكونهما في زمانين مع تأخر ما دخلت عليه، وهو المعبر عنه بالترتيب في زمان بل هي للجمع المشترك بينهما، المحتمل في','3','51',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (603,'الوجود لهما، من غير تعرض في الذكر لشيء منهما، ولا يلزم من عدم التعرض للمعية التعرض للترتيب. وهو معنى الجمع المطلق.
وفي تعبير المصنف "بالجمع المطلق" نظر لتقييد الجمع بقيد الإطلاق، وإنما هي للجمع لا بقيد. والأحسن أن يقال: لمطلق الجمع.
وقيل: للترتيب، وقيل: للمعية.
وقيدتها بالعاطفة تبعًا للإمام، للاحتراز عن الواو بمعنى','3','52',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (604,'مع وواو الحال.
ثم استدل المصنف على أنها لمطلق الجمع بإجماع النحاة (أنها كذلك).
ونقله أبو علي الفارسي، وذكره سيبويه في سبعة عشر موضعًا من كتابه.
وفيه نظر، فقد نقل عن جماعة من أئمة النحو: أنها للترتيب، منهم: قطرب،','3','53',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (605,'والربعي والفراء، وثعلب، وأبو عمرو الزاهد، وهشام، وأبو جعفر الدينوري.','3','54',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (606,'واشتهر عن أصحاب الشافعي، بل نقل عن الشافعي - رضي الله عنه.
واستدل المصنف أيضًا على أنها لمطلق الجمع بقوله: ولأنها أي الواو تستعمل حيث يمتنع الترتيب: إما لمفهوم الفعل، مثل: تقاتل زيد وعمرو.
فإن مفهوم التقاتل هو: الأخذ في فعل القتل معًا، ومع المعية يمتنع الترتيب.
وإما لقرينة تمنع منه، وذلك مثل: جاء زيد وعمرو قبله، فإنها لو كانت للترتيب فيه لتناقض الكلام، لكنه لم يتناقض اتفاقًا، وإذا صح استعمالها حيث يمتنع الترتيب كانت للجمع المطلق.
واعترض: بأن صحة إطلاق الواو حيث لا ترتيب لا يستلزم كونها','3','55',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (607,'حقيقة في جواز أن يكون مجازًا.
وأجيب: بأن الأصل في الإطلاق الحقيقة، وله زيادة تحقيق تطلب من الشرح.
ولأنها، أي الواو في الأسماء المختلفة، كالجمع والتثنية في الأسماء المتماثلة، لأنهم لما لم يتمكنوا من جمع الأسماء المختلفة، وتسميتها، استعملوا واو العطف فيها.
وهما - أي: الجمع والتثنية لا يوجبان الترتيب اتفاقًا، بل يفيدان الاشتراك في الحكم، فكذا واو العطف، وهذا الدليل ينفي المعية أيضًا.
قيل: لو كانت واو العطف بمنزلة التثنية، من غير إشعار بالترتيب، لما أنكر النبي صلى الله عليه وسلم على من خطب.
وأثنى، وثنى، ولم يعطف، لكنه أنكر - عليه السلام - على الخطيب، وهو ثابت بن قيس بن شماس في قوله: ومن عصاهما، حيث قال صلى','3','56',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (608,'الله عليه وسلم "بئس الخطيب أنت" ملقنًا للخطيب بقوله: "قل ومن عصى الله ورسوله".
ولفظ الحديث: أن رجلاً خطب عند النبي صلى الله عليه وسلم فقال: ومن يطع الله ورسوله فقد رشد ومن يعصهما فقد غوى، فقال صلى الله عليه وسلم: "بئس الخطيب أنت قل: ومن يعصي الله ورسوله". رواه مسلم وغيره.','3','57',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (609,'ولولا أن الواو للترتيب، لما كان بين العبارتين فرق.
ولم يكن للرد والتلقين معنى، وهو محال.
قلنا: ذلك الإنكار ليس لأن الواو للترتيب، بل لأن الإفراد أشد تعظيمًا، وليس في القرآن مثله، فرد عليه لترك التعظيم الذي كان يحصل بالإفراد لو أفرد.','3','58',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (610,'يدل عليه أن معصيتهما، لا ترتيب فيها، لأن كلا (من الله ورسوله) آمر بطاعة الأخر.
فمعصية أحدهما معصية لهما، كما أن رضاهما واحد.
وأما ما رواه أبو داود أن رسول الله صلى الله عليه وسلم خطب وقال "ومن يعصهما" فلا ينافي هذا، لأنه من رواية أبي عياض وهو مجهول.
هذا مع احتمال أن يكون في الأصل بالإفراد واختصر الراوي، وعلى تقدير الصحة والضبط فالمحذور من الجمع، وهو إبهام التسوية في حق المعصوم.
قيل: لو قال الزوج لغير الممسوسة أنت طالق وطالق، طلقت واحدة، بخلاف ما لو قال: أنت طالق طلقتين فإنها تطلق ثنتين.','3','59',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (611,'وما ذلك إلا بإفادة العبارة الأولى الترتيب فتبين منه بالطلقة الأولى، فلا يبقى المحل قابلاً للثانية.
ولا ترتيب في العبارة الثانية، فيلحقها الثنتان دفعة واحدة.
فلو كانت واو العطف بمنزلة الثانية لوقع ثنتان أيضًا.
قلنا: قوله: أنت طالق إنشاء، والإنشاءات مرتبة بترتيب اللفظ، فلما قال: (أنت طالق)، وقعت طلقة، فبانت فلم يبق المحل لخروج البائن عن كونها قابلة لوقوع الطلاق عليها، حتى تقع الثانية، فالترتيب فيها مستفاد من ترتيب الألفاظ لا من الواو.
وقوله: طلقتين إنما هو تفسير لطالق والكلام يتم بآخره فهو بخلاف الأول.
* * *','3','60',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (612,'الثانية:
الفاء العاطفة للتعقيب إجماعًا، أي: تدل على أن المعطوف بها وقع عقب المعطوف عليه بلا مهلة.
وفي نقل الإجماع نظر: فقد ذهب الجرمي إلى أنها إن دخلت على الأماكن والمطر لا تفيد الترتيب.','3','61',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (613,'وكذا الفراء: قال: إنها لا تفيد الترتيب مطلقًا مع قوله: إن الواو للترتيب كما سبق، وهو غريب.
"ولهذا" أي: لأجل كونها للتعقيب، قرن به الجزاء إذا لم يكن فعلاً، أي: يجب دخول الفاء على الجزاء إذا لم يكن بلفظ الفعل نحو: من دخل داري فله درهم.
والجزاء لا بد وأن يحصل عقب الشرط.
فلو لم تكن الفاء للتعقيب لما جاز دخولها على الجزاء الواجب وقوعه عقيب الشرط، فضلاً عن وجوب الدخول.
وقيد الجزاء بكونه غير فعل؛ لأنه إذا كان فعلاً فإن فيه تفصيلاً','3','62',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (614,'مذكورًا في الشرح.
فإن قيل: لو كانت الفاء للتعقيب لأفادته، حيث كانت، لكنها لا تفيده في بعض الصور، وهو مثل قوله تعالى: } لا تفتروا على الله كذبًا فيسحتكم بعذاب {.
فإن الإسحات لا يقع عقيب الفرية لكونها في الدنيا، والإسحات في الآخرة.
فجوابه: (أنه ثبت أن) الفاء للتعقيب حقيقة، فهي في الآية مجاز، إذ هو خبر من الاشتراك، وذلك أن الاستيصال لما كان يقطع','3','63',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (615,'بوقوعه جزاء للمفتري، جعل كالواقع عقب الافتراء مجازًا.
الثالثة
لفظة: "في" وضعت للظرفية، سواء كانت مكانية، أو زمانية ولو تقديرًا، أي: يدل على أن مدخلوها ظرف لما قبلها تحقيقًا، مثل: جلست في المسجد، أو تقديرًا مثل قوله تعالى: } ولأصلبنكم في جذوع النخل {.
شبه تمكن المصلوب على الجذع بتمكن المظروف في الظرف، فإن الجذع ليس مكانًا له حقيقة، لكنه مكان له تقديرًا، و "لو" في قول المصنف: ولو تقديرًا: للحال وقيل: للعطف أي: لو لم يكن تقديرًا','3','64',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (616,'ولو كان تقديرًا.
ولم يثبت مجيئها، أي في: للسببية، خلافًا لزاعمي ذلك من الفقهاء.
قال الإمام: لأن المرجع فيه إلى أهل اللغة، ولم يذكره أحد منهم.
وما استدلوا به يمكن حمله على الظرفية التقديرية مجازًا.
واختار ابن مالك أنها تجيء للسببية (كقوله تعالى)} لمسكم فيما أفضتم {أي: بسبب.','3','65',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (617,'وقوله عليه الصلاة والسلام: "إن امرأة دخلت النار في هرة".
واعلم أن ظاهر كلام المصنف تبعًا للإمام، أن "في" حقيقة في الظرفية الحقيقية والتقديرية، فتكون متواطئة أو مشككة.
ومقتضى كلام النحاة والأصوليين أن استعمالها (في الظرفية) التقديرية مجاز.
قال العراقي: ونحن ننازع في أن ظاهر كلامه مخالف للجمهور
الرابعة
(من) إذا لم تكن زائدة في الكلام، تستعمل لابتداء الغاية في','3','66',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (618,'المكان اتفاقًا: كقولك: خرجت من البيت إلى المسجد.
وفي الزمان، عند الكوفيين، والمبرد، وابن درستويه، وصححه ابن مالك، واختاره أبو حيان، لقوله تعالى: } من أول يوم {.
والتبيين، أي: لتبيين الجنس، كقوله تعالى: } فاجتنبوا الرجس من الأوثان {.','3','67',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (619,'والتبعيض، كقولك: أخذت من الدراهم، وتعرف بصلاحية إقامة البعض مقامها وهي حقيقة في التبيين لوجوده في جميع معانيها، فإنها بينت مبدأ اليوم، والمأخوذ، والرجس الذي يجب الاجتناب عنه، فتكون حقيقة فيه دفعًا للاشتراك؛ إذ لو كانت حقيقة في كل واحدٍ لزم الاشتراك، والأصل عدمه.
قال العراقي: لا بد أن يقول: والمجاز.
الخامسة
الباء: تعدي اللازم، وتجزئ المتعدي.','3','68',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (620,'اعلم: أن التعدية: عبارة عن أنه يضمن الفعل معنى التصيير، حتى يصير فاعل أصل الفعل مفعولاً للفعل المضمن، وأصل الفعل قائم به، كما كان مثل: ذهب زيد، وذهبت بزيد أي: جعلت زيدًا ذاهبًا.
والباء في اللغة: قد تجئ للتعدية كما في المثال المذكور، وإذا دخلت على الفعل المتعدي تفيد تجزئته.
وقال العراقي: معنى كلامه: أن الباء إذا دخلت على فعل لازم كانت للإلصاق، أو على فعل متعد كانت للتعبيض.
فعبر عن الإلصاق بالتعدي، وعن التبعيض بالتجزئة.
وفيه نظر: إذ الإلصاق لا يستلزم التعدية، نحو مررت بزيد، ووصلت هذا بهذا.
فإن الباء في المثالين للإلصاق، وليست للتعدية.
وأما باء التعدية: فهي التي تقوم مقام الهمزة في إيصال الفعل اللازم إلى المعفول.','3','69',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (621,'وما جزم به من أن الباء للتبعيض مخالف لما في المجمل حيث قال: الحق أنه حقيقة فيما ينطلق عليه الاسم دفعًا للاشتراك والمجاز.
ثم احتج للفرق بقوله: لما يعلم من الفرق الضروري بين قولهم: مسحت المنديل، ومسحت بالمنديل.
فإن الأول: يقتضي الشمول، والثاني التبعيض.
ونظر فيه من جهة أن الفرق بينهما أن اليد في المثال الأول ماسحة، والمنديل ممسوح، وفي الثاني بالعكس، لا من الجهة التي ذكرها.
(ومما يوضح الفرق الذي ذكره المصنف) أنه لا بد لدخول الباء من فائدة صونًا للكلام عن العبث، والأصل عدم الزيادة، وغير التجزي مفقود فتعين التجزي لأنه فائدة.','3','70',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (622,'وقالت الحنفية: إنما يثبت مجيء الباء للتجزئة إذا ثبت عن أهل اللغة مجيئه لها، لكن لم يثبت.
وقد نقل إنكاره عن ابن جني، وهو من أهل اللغة، ولولا فحصه لما نفي.
ورد: بأنه شهادة نفي، فلا تسمع مع الإثبات، فقد أثبته الكوفيون، ونص عليه الأصمعي، والفارسي، في كتابه التذكرة،','3','71',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (623,'وبه قال ابن مالك، ولا يلزم من عدم، وجود ابن جني عدم الوجدان.
السادسة
إنما: للحصر، وهو: قصر شيء على شيء بحيث لا يتجاوزه.
فمفهوم: إنما (هو نفي) غير المذكور في الكلام آخرًا، مثل','3','72',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (624,'إنما زيد قائم، وإنما العالم زيد، وإنما ضرب زيد عمرو يوم الجمعة أمام الأمير قائمًا.
لأن "إن" للإثبات، و"ما" للنفي، والأصل بقاؤهما على ما كانا، وليسا متوجهين إلى المذكور، ولا إلى غير المذكور للتناقض، وليس لإثبات ما عدا المذكور إجماعًا، فتعين عكسه، وهو أن "إن" لإثبات المذكور، و"ما" لنفي غير المذكور.
وهو معنى قوله: فيجب الجمع على ما أمكن.
واعترض: بأنها لو كانت نافية، لاقتضت التصدير، ولجاز نصب إنما زيد قائمًا، وغير ذلك.
وأجيب: أن المراد أن كلمة "إنما" هكذا للحصر كسائر الكلمات المركبة الموضوعة لمعنى، لا أن لفظة "إن" ولفظة "ما" ركبتا وبقيتا على','3','73',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (625,'أصلهما.
وما ذكر هو بيان وجه المناسبة، لئلا يلزم النقل، الذي هو خلاف الأصل.
وأيضًا: قد جاء "إنما" في كلام الفصحاء للحصر.
قال الأعشى مخاطبًا لعلقمة، مفضلاً عامرًا عليه:
ولست بالأكثر منهم حصًى، "أي عددًا" *** وإنما العزة للكاثر
أي: وما الغلبة إلا لمن هو أكثر عددًا، فالمراد حصر العزة في الكاثر.','3','74',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (626,'وقال الفرزدق:
"أنا الزائد" من الذود وهو الطرد "الحامي" (أي: الحافظ).
"الذمار" وهو العهد، وقيل الذمار: هو ما يجب على الرجل حفظه من أهله ومتعلقاته من أن يخلفهم عار وهو الأصح، وإنما يدافع عن أحسابهم أنا أو مثلي.
فمعناه: (عن أحسابهم أي) أن المدافع عن أحسابهم أنا لا غيري.','3','75',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (627,'وصرح باسم الشاعرين؛ لتعلم أنه من الأبيات التي يستشهد بها لإثبات القواعد، إذ ليس الغرض مجرد التمثيل.
واختار الآمدي أنها لا تفيد بل تفيد تأكيد الإثبات.
ونقل تصحيحه عن النحاة، ونقله أبو حيان عن البصريين، وعروض هذان الوجهان الدالان على "إنما" للحصر بقوله تعالى: } إنما المؤمنون الذين إذا ذكر الله وجلت قلوبهم {.
فلو كانت إنما للحصر لأفادته في هذه الآية، لكنها لا تفيد الحصر فيها للإجماع على أن من لا يوجل قلبه عن ذكر الله مؤمن أيضًا.
قلنا: المراد بالمؤمنين المذكورين في الآية: المؤمنون الكاملون، ويلزم منه أن من لا يخالف قلبه عند ذكر الله تعالى ليس بمؤمن كامل.','3','76',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (628,'<span data-type="title" id=toc-84>الفصل التاسع: في كيفية الاستدلال بالألفاظ</span>
أي: بألفاظ كتاب الله تعالى، وحديث (رسول الله) - صلى الله عليه وسلم - على الأحكام الشرعية، وفيه مسائل:
الأولى:
لا يخاطبنا الله تعالى بالمهمل: الذي لا موضوع له، فلا يفهم منه شيء، لأنه أي: المهمل هذيان، وهو نقص، لا يجوز على الله تعالى.','3','77',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (629,'واحتجت الحشوية: على أن الله - تعالى - خاطبنا بالمهمل بأوائل السور مثل: ألم، وطه، وحم.
قلنا: أسماؤها، أي: أسماء السور عند الأكثرين فليست بمهملة.','3','78',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (630,'واستدلوا أيضًا: بأن الوقف على قوله تعالى: } وما يعلم تأويله إلا الله {، واجب حتى يكون قوله: } والراسخون في العلم {كلاما مستأنفًا، وإلا، أي: لو لم يجب الوقف عليه حتى يكون قوله تعالى: } والراسخون في العلم {عطفًا على قوله: } إلا الله {فيكون المراد بقوله: } يقولون آمنا به {قائلين: } آمنا {فيكون حالاً، وهو باطل.
لأنه: إما أن يكون حالا عن الله تعالى، وعن الراسخين في العلم، فيلزم أن الله - تعالى - والراسخين في العلم} يقولون آمنا به كل من عند ربنا {، وذلك في حق الله تعالى محال.
ولم يذكره لوضوحه.
أو يكون حالا عن الراسخين في العلم فقط، وحينئذ يتخصص','3','79',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (631,'المعطوف بالحال دون المعطوف عليه وهو باطل، لأنه مناف للقاعدة وهي: أن المعطوف في حكم المعطوف عليه، فثبت أن الوقف على قوله تعالى: } إلا الله {واجب
وإذا كان الوقف عليه واجبًا، فقد خاطبنا الله تعالى بما لم نفهمه وهو المهمل.
قلنا: يجوز تخصيص المعطوف بالحال، حيث لا لبس مثل قوله تعالى: } ووهبنا له إسحاق ويعقوب نافلة {. فنافلة: حال عن','3','80',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (632,'المعطوف فقط، وهو يعقوب؛ لأن النافلة لغة: ولد الولد، وإنما هو يعقوب دون إسحاق.
أو استئنافًا، أي: جوابًا عن سؤال، كأن سائلاً سأل ما يقول الراسخون في المتشابهات؟
أجيب بقوله: } يقولون آمنا به كل من عند ربنا {.
سلمناه: لكن مخالفة الظاهر أهون من الخطاب بما لا يفيد أصلاً.
قال المحقق: والحق أن الخطاب بما لا يفهم بعيد، وإن كان لا يمتنع على الله تعالى.
قال في الكشاف: والأول هو الوجه، يعني العطف؛ لأنه لو','3','81',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (633,'أريد بيان حظ الراسخين مقابلاً لبيان حظ الزائغين، لكان المناسب أن يقال: وأما الراسخون فيقولون، ولأنه لا فائدة حينئذ في قيد الرسوخ، بل هذا حكم العالمين كلهم ولأنه حينئذ، لا ينحصر الكلام في المحكم والمتشابه.
على ما هو ظاهر العبارة حيث لم يقل: ومنه متشابهات؛ لأن ما لا يكون متضح المعنى ويهتدي العلماء إلى تأويله ورده إلى المحكم لا يكون محكمًا ولا متشابهًا بالمعنى الذي ذكرتم وهو كثير جدًا.
(وقد حكى الطبري والخطابي عن جمهور السلف الوقف على قوله','3','82',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (634,'} إلا الله {.
وروى عبد الرزاق (بسند صحيح) قال: كان ابن عباس - رضي الله عنه - يقرؤها: } وما يعلم تأويله إلا الله {.
ويقول الراسخون في العلم آمنا به.
وأخرج الطبري، وابن أبي حاتم بإسناد صحيح عن عائشة -','3','83',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (635,'رضي الله عنها - قالت في قوله تعالى: } والراسخون في العلم {: انتهى علمهم، إلى أن آمنوا بمتشابهه، ولم يعلموا تأويله.
وروى الطبري نحوه عن مالك - رضي الله عنه -).
قال الشيخ سعد الدين: والحق: أنه إن أريد بالمتشابه ما لا سبيل إليه للمخلوق، فالحق الوقوف على إلا الله، وإن أريد ما لا يتضح بحيث يتناول المجمل والمؤول فالحق العطف.
واستدلوا أيضًا على أن الله - تعالى - يخاطبنا بالمهمل','3','84',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (636,'بقوله - تعالى -: } كأنه رءوس الشياطين {.
ورءوس الشياطين لا معنى له، فيكون مهملاً.
قلنا: مثل في الاستقباح، يعني أنه موضوع لمتخيل يستقبحون ذلك المتخيل ويضربون المثل به في الاستقباح تنفيرًا عنها، فلا يكون مهملاً.
تنبيه: ذكر في المحصول: أن حكم الرسول (صلى الله عليه وسلم) في الامتناع كحكم الله - تعالى.','3','85',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (637,'قال الأصفهاني في شرحه: ولا أعلم أحدًا ذكر ذلك.
الثانية
لا يعني خلاف الظاهر من غير بيان، أي: لا يتكلم الشارع بلفظ له ظاهر، ويريد خلاف ظاهره، من غير قرينة؛ لأن الظاهر يتبادر إلى الفهم، وهو غير مراد فيوقع في الغلط، ويكون إغراء.
وأيضًا: لأن اللفظ الخالي عن البيان بالنسبة إليه، أي: إلى خلاف الظاهر، مهمل، بالنسبة إلينا، لعدم الشعور به، والمهمل لا يقع الخطاب به.
قال المرجئة:','3','86',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (638,'لا نسلم أن اللفظ المراد به غير ظاهره من غير باين مهمل، فإن المهمل: ما لا فائدة له.
وهذا اللفظ له فائدة؛ لأنه إذا تكلم بكلام ظاهره الوعيد، مع أنه لا يريد ذلك، حصل منه تخويف.
والتخويف يفيد إحجامًا عن المعاصي فهذه فائدة.
وقالوا: إن الله تعالى لا يعذب أحدًا من المؤمنين.
قلنا: حينئذ يرتفع الوثوق، عن قوله - تعالى - وعن قول رسوله - صلى الله عليه وسلم.','3','87',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (639,'واقتصر على الأول؛ لأن الثاني في معناه.
إذا ما من خطاب إلا ويحتمل أن يراد به غير ظاهره، وذلك معلوم الفساد.
وأيضًا: الإحجام إنما يتحقق إذا اعتقد العقاب، (وإذا لم يعتقد العقاب يكون إغراء على المعاصي) لا إحجامًا. فانتفت الفائدة، بل تضمن المفسدة.
تنبيه: محل الخلاف في غير الأوامر والنواهي، كما في شرح المحصول للأصفهاني، وإنما خالفت المرجئة في الآيات والأخبار الدالة على العقاب.
الثالثة
الخطاب إما أن يدل على الحكم بمنطوقه.','3','88',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (640,'والتحقيق: أن المنطوق: دلالة اللفظ على المدلول، في محل النطق.
والقيد الأخير: للتمييز بينه وبين المفهوم.
لأن ذلك المدلول، إن كان لما ذكر ونطق به، فالدلالة منطوق وإلا فمفهوم.
فالمنطوق: دلالة اللفظ على المدلول الحاصل بما تعلق به وذكر.
فالشرط في تحققه، مذكورية ماله المدلول لا مذكورية المدلول، فإنه قد يكون مذكورًا فيه وقد لا يكون.
والمفهوم بخلافه.
والمراد بكون المدلول للمذكور كونه حكمًا للمذكور، أو حالاً له. فالحكم واضح.','3','89',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (641,'والحال ما يكون شرطًا للمذكور عقلاً كما في المقدمة العقلية للواجب، نحو: ارم، فإنه مقتضٍ لتحصيل القوس.
أو شرعًا، كما في المقدمة الشرعية للواجب نحو: صل، فإنه مقتضٍ لإيجاب الوضوء.
أو سببًا له، أو مانعًا، فأقسام المنطوق بحسب ذكر الحكم، والحال أربعة:
- دلالة اللفظ على حكم مذكور لما نطق به.
- دلالته على حكم غير مذكور لما نطق به.
- دلالته على حال مذكور لما نطق به.
- دلالته على حال غير مذكور لما نطق به.
وهي مذكورة بأمثلتها في الشرح مع زيادة تحقيق وإيضاح فراجعه منه فإنه موضع مهم.
فإذا ورد خطاب من الشارع فيحمل على المنطوق الشرعي إن أمكن.','3','90',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (642,'لأن عرف الشارع، أن يعرف الأحكام الشرعية، ولذلك بعث، ولم يبعث لتعريف الموضوعات اللغوية، وغير الشرعي.
وإن لم يكن له حقيقة شرعية، أو كان ولم يمكن الحمل عليها، حمل على الحقيقة العرفية الموجودة في عهده - (عليه الصلاة والسلام) - وهو المعني بقوله: (ثم العرفي) لأنه المتبادر إلى الفهم.
ولأن الشارع يعتبر العرف في كثير من مسائل: الأيمان والطلاق، وإن لم يكن له منطوق عرفي.','3','91',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (643,'فإن لم يكن هناك قرينة صارفة عن المعني اللغوي، حمل على اللغوي، وهو معنى قوله: "ثم اللغوي" لأن الأصل في الإطلاق الحقيقة.
وهذا لا ينافي قول الفقهاء: أن ما ليس له ضابط في الشرع ولا في اللغة، فإنه يرجع فيه إلى العرف، فإنه: وإن اقتضى تأخر العرف عن اللغة لكن مرادهم العرف في غير زمنه - صلى الله عليه وسلم -.
ومراد الأصوليين العرف في زمنه - صلى الله عليه وسلم - كذا جمع به بعضهم.
وقال السبكي: مراد الأصوليين: إذا تعارض معناه في العرف ومعناه في اللغة قدمنا العرف.
ومراد الفقهاء: إذا لم يعرف حده في اللغة فإنه يرجع فيه إلى العرف.
ولهذا قالوا: كل ما ليس له حد في اللغة، ولم يقولوا:','3','92',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (644,'ليس له معنى.
قال العراقي: وعبارة الرافعي ربما تضاد هذا الجمع.
ويجمع بينهما: بأن كلام الفقهاء في الألفاظ الصادرة من غير الشارع، وكلام الأصوليين في ألفاظ الشارع.
وإن كانت قرينة حمل على المدلول المجازي.
وإليه أشار بقوله: "ثم المجازي" لكون اللفظ دالا، وتعذر حمله على الحقيقة، فلو لم يحمل على المجاز خرج اللفظ الدال عن أن يكون دالاً وهو غير جائز.
ويكون الترتيب في مجازات هذه الحقائق كالترتيب المذكور في','3','93',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (645,'الحقائق.
أو بمقهومه، أي: وإن دل الخطاب على الحكم بمفهومه، وهو المسمى بالدلالة المعنوية، والدلالة الالتزامية.
وهو أي: المفهوم (إما أن) يكون لازمًا عن لفظ مفرد، يوقف مدلول اللفظ عليه عقلاً، أو يوقف عليه شرعًا.','3','94',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (646,'فالذي يتوقف عليه عقلاً، مثل قولك: ارم، فإنه يستلزم الأمر بتحصيل القوس، والمرمى؛ لأن العقل يحيل الرمي بدونهما.
والذي يتوقف عليه شرعًا، مثل قولك: أعتق عبدك عني.
فإنه يستلزم سؤال تمليكه، حتى إذا اعتقه تبينا دخوله في ملكه، لأن العتق شرعًا، لا يكون إلا في مملوك، والمستلزم للملك هو: أعتق فقط، بدليل ما لو قال: أعتق غانمًا وهو ملك لغير المخاطب، يفهم منه إيجاب تحصيل تملكه.
وهذا القسم وهو اللازم عن مفرد: يسمى اقتضاء (أي: الخطاب','3','95',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (647,'يقتضيه.
وإن لم يتوقف مدلول اللفظ على المفهوم منه يسمى إيماء) وسيجيء.
أو يلزم المفهوم عن مركب: وهو إما موافق للمنطوق في الحكم، أي: في الجواز، والحرمة، والإيجاب، والسلب، وهو يسمي فحوى الخطاب، أي: معناه وتنبيه الخطاب.','3','96',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (648,'ومفهوم الموافقة: كدلالة تحريم التأفيف على تحريم الضرب.
فتحريم الضرب، استفدناه من التركيب؛ لأن مجرد التأفيف لا يدل على تحريم الضرب ولا على إباحته، بخلاف مجرد الرمي، فإنه يتوقف على القوس.
وكدلالة جواز المباشرة إلى الصبح في قوله تعالى: } أحل لكم ليلة الصيام الرفث {إلى قوله تعالى: } حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر {.','3','97',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (649,'على جواز الصوم جنبًا، إذ لو لم يجز، لوجب أن يحرم الوطء في آخر جزء من الليل بقدر ما يصح فيه الغسل، وهو مخالف لمنطوق الآية، أعني جواز المباشرة إلى طلوع الفجر، لكون حتى لانتهاء الغاية.
فالحكم المفهوم من اللفظ في محل السكوت في هاتين الآيتين، موافق لمدلوله في محل النطق، وهو التحريم في الآية الأولى، والجواز في الثانية.
ومثل بالمثالين إشارة إلى أن مفهوم الموافقة قد يكون أولى بالحكم من المنطوق، كالمثال الأول وقد يكون مساويًا، كالمثال الثاني، وهو المختار في جمع الجوامع، وظاهر كلام ابن الحاجب في','3','98',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (650,'موضع، وخالف في موضع آخر، فاشترط الأولوية، فلا يكون المساوي مفهوم موافقة، وهو مقتضى نقل إمام الحرمين عن الشافعي - رضي الله عنه -.
والخلاف في التسمية؛ فإنهم اتفقوا على الاحتجاج بالمساوي كالأولى.
وقال العلامة: المساوي لا يسمى موافقة ولا مخالفة.
وأشار المصنف أيضًا: بالمثالين إلى أن مفهوم الموافقة قد يكون من مكملات المعنى المنطوق كما في المثال الأول وقد لا يكون كالثاني.','3','99',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (651,'فإن قلت التنبيه بالأدنى على الأعلى والمساوي، لو كان من مفهوم الموافقة لكان جميع صور القياس بالطريق الأولى والمساوي من مفهوم الموافقة والثابت به ثابت بالنص.
أجيب: بأن الملازمة ممنوعة، لجواز أن يكون حكم الأصل ثابتًا بالإجماع لا بالنص، وعلى تقدير ثبوته بالنص يجوز أن يكون الفرع أدنى من الأصل فيما أثبت لأجله الحكم، فإنه لا تكون دلالته بالمفهوم اتفاقًا.
وقد جوز بعضهم القياس فيه على ما ذكره المصنف في تقسيم القياس، وإن لم يجوزه ابن الحاجب قاله الأبهري، وفيه نظر يأتيك تحقيقه في القياس.
القسم الثاني: أن يكون المفهوم مخالفًا للمنطوق، وإليه أشار بقوله: "أو مخالف كلزوم نفي الحكم عما عدا المذكور، ويسمى دليل الخطاب" (ولحن الخطاب)، ومفهوم المخالفة، كمفهوم الشرط، والصفة، ونحوهما مما سيأتي واعلم أن عد المصنف دلالة الاقتضاء مثل: ارم','3','100',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (652,'ودلالة قوله تعالى: } أحل لكم ليلة الصيام {الآية من أقسام المفهوم، ليس كما ينبغي لما تقدم لك تحقيقه في المنطوق والمفهوم؛ لأن الدلالة فيهما على حكم وحال لما نطق به وإيضاحه التام في الشرح.
الرابعة
فيها من مفاهيم المخالفة: اللقب والصفة، وقدم اللقب لكونه غير','3','101',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (653,'حجة.
ولأن الكلام فيه أقل، فقال: تعليق الحكم بالاسم: الجامد علمًا كان، أو اسم جنس.
ويقال له: مفهوم اللقب، لا يدل على نفيه، أي: نفي ذلك الحكم عن غيره، أي: عن غير ذلك الاسم، مثلاً إذا قيل: "زيد قائم" لا يدل على نفي القيام عن غيره، وإلا أي: لو دل تعليق الحكم بالاسم على نفي ذلك الحكم عن غير ذلك الاسم لما جاز القياس؛ لأن النص الدال على ثبوت الحكم في الأصل، إن كان متناولاً للفرع فلا','3','102',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (654,'قياس، لثبوت الحكم أيضًا فيه بالنص، وإن لم يكن متناولاً له فكذا؛ لأن التنصيص على حكم الأصل حينئذ يكون دالاً على نفي الحكم عن غيره، والفرع غيره، فيكون دالاً على نفي الحكم عن الفرع.
فلا يمكن إثبات الحكم في الفرع بالقياس؛ لأنه قياس في مقابلة النص فلا يعتبر.
خلافًا لأبي بكر الدقاق، وبعض الحنابلة في قولهم: إن','3','103',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (655,'التخصيص بالاسم يدل على نفي الحكم عما عدا ذلك الاسم.
الثاني: مفهوم الصفة: وهو تعليق الحكم بصفة من صفات الذات، وإليه أشار بقوله: وبإحدى صفتي الذات.
أي: وتعليق الحكم بإحدى صفتي الذات مثل: "في سائمة','3','104',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (656,'الغنم الزكاة".
رواه البخاري بلفظ أنه - صلى الله عليه وسلم - قال: "في سائمة غنم زكاة" فإن الغنم اسم ذات، ولها صفتان: السوم، والعلف.
وقد علق الوجوب على إحدى صفتيها، وهو السوم، فإنه يدل على عدم الوجوب في المعلوفة من جنس الغنم.','3','105',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (657,'وقيل: في المعلوفة مطلقًا.
والمراد بتخصيص الوصف: ما يفيد نقص الشيوع.
وقصر العام على البعض، لا مجرد ذكر صفة لموصوف، فلا يرد ما يكون لمدح أو ذم أو تأكيد أو نحوه.
وقال الأبهري: ليس المراد بالصفة هنا النعت، بل المتعرض لقيد في الذات سواء كان نصًا أو غيره من المشتق وظرف الزمان والمكان.
ومحل حجية مفهوم المخالفة: ما لم يظهر للتخصيص فائدة أخرى غير نفي الحكم، حتى لو كان له فائدة أخرى غيره لم يدل عليه، ككونه جوابًا لمن سأل، أو كان هو الغالب، أو لرده عادة','3','106',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (658,'مذمومة، أو كان المسكوت عنه أولى بالحكم أو مساويًا له.
فإذا ظهر للوصف إحدى هذه الفوائد ونحوها، بطل وجه دلالته على المخالفة.
وما اختاره المصنف، من كون مفهوم الصفة حجة بالشرط المتقدم نقل عن الشافعي، والإمام أحمد، والأشعري وكثير من العلماء.
خلافًا لأبي حنيفة، وابن سريج، والقاضي أبي بكر، وإمام الحرمين، والغزالي في قولهم: ليس بحجة مطلقًا.','3','107',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (659,'وفي النقل عن إمام الحرمين نظر، فقد صرح في البرهان بأنه حجة بشرط مبين في الشرح.
ولعل له قولان:
لنا على حجيته أنه المتبادر من نحو قوله صلى الله عليه وسلم: "مطل الغني ظلم" متفق عليه.','3','108',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (660,'فإنه يتبادر منه إلى الفهم أن مطل غير الغني ليس بظلم.
والمطل: (المد مع التأخير).
وإذا ثبت ذلك في العرف ثبت في اللغة؛ لأن الأصل عدم النقل، لاسيما وقد فهم ذلك أبو عبيد وهو عالم بلغة العرب فالظاهر فهمه ذلك لغة.','3','109',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (661,'وكذلك يتبادر إلى الفهم من قولهم: الميت اليهودي لا يبصر، أن الميت غير اليهودي يبصر. ولهذا يستهزأ بقائله ويضحك منه، ولولا أنه يدل لما تبادر إلى الفهم عرفًا.
ولأن ظاهر التخصيص يستدعي فائدة، وإلا لكان التخصيص عبثًا، وهو لا يليق بكلام البلغاء، فضلاً عن كلام الشارع.
وتخصيص الحكم المذكور فائدة، والفائدة التي استدعاها التخصيص، إما تخصيص الحكم بالمذكور، أو غيرها، وغيرها منتف بالأصل، فتعين أن الفائدة منحصرة فيه.
فإن الكلام فيما إذا لم يظهر للتخصيص فائدة.
وهنا في الشرح ما يتعين الوقوف عليه.','3','110',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (662,'ولنا أيضًا: أن الترتيب يشعر بالعلية كما ستعرفه في باب القياس من أن ترتب الحكم على وصف يشعر بعلية ذلك الوصف لذلك الحكم.
وحينئذ: فإما أن يكون لذلك الحكم علة أخرى غير ذلك الوصف، أو لا يكون.
والأصل ينفي علة أخرى، فبطل الأول، وتعين الثاني.
وحينئذ تنحصر العلية في ذلك الوصف، وإذا كان كذلك فينتفي','3','111',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (663,'الحكم بانتفائها، أي انتفاء ذلك الوصف لانتفاء المعلول عند انتفاء علته، وإذا انتفى الحكم بانتفاء الوصف، ثبت أن تعليق الحكم بإحدى صفتي الذات يدل على نفي الحكم عما ليس له تلك الصفة.
قيل: لو دل تعليق الحكم على صفة على نفي الحكم عما عدا المذكور، لدل عليه إما مطابقة، أو التزامًا، أو تضمنًا، لانحصار الدلالة فيها لكنه لا يدل.
أما المطابقة والتضمن؛ فلأن نفي الحكم عما عدا المذكور ليس هو عين إثبات الحكم في المذكور حتى يكون مطابقة ولا جزأه حتى يكون تضمنًا.
وأما الالتزام فالانتفاء شرطه وهو اللزوم الذهني؛ لأنه قد يتصور السامع المنطوق ويغفل عن المفهوم.','3','112',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (664,'واقتصر على المطابقة واللزوم؛ لأن التضمن داخل في الالتزام لغة لكون الجزء لازمًا للكل.
قلنا: اللفظ دل على نفي الحكم عما عدا المذكور التزامًا، لما ثبت أن الترتيب يدل على العلية.
أي: علية ذلك الوصف لذلك الحكم.
وثبت حصر العلية في ذلك الوصف لانتفاء غيره بالأصل، فثبت أن الوصف علة مساوية لذلك الحكم، وإذا ثبت ذلك لزم نفي الحكم عما عدا المذكور، وذلك؛ لأن انتفاء العلة يستلزم انتفاء معلولها المساوي لها.
والمراد المساوي: أن لا يكون له علة أخرى غير هذه العلة، ليخرج ماله علتان فلا يلزم من انتفاء أحدهما انتفاؤه، كما عرفت.','3','113',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (665,'قيل: لو دل تعليق الحكم بالصفة على نفي الحكم عما عدا المذكور لكان قوله تعالى: } ولا تقتلوا أولادكم خشية إملاق {، دالاً على جواز قتل الأولاد عند عدم الخشية عن الفقر لكنه ليس كذلك.
لأن تحريم قتل الأولاد ثابت إجماعًا فبطل دلالته.
قلنا: غير المدعي إذ قد شرطنا في حجية المخالفة عدم فائدة أخرى غير نفي الحكم، وفي هذه الآية فائدة أخرى، وهي رد عادتهم المذمومة.
وأيضًا: يدل على أن المسكوت عنه بطريق الأولى فيكون مفهوم موافقة، فيبطل مفهوم المخالفة.','3','114',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (666,'الخامسة
التخصيص بالشرط: يعني أن مفهوم الشرط: تعليق الحكم على شيء بكلمة الشرط مثل قوله تعالى: } وإن كن أولات حمل فأنفقوا عليهن {، علق وجوب الإنفاق على الحمل بكلمة: "إن"، فدل على انتفاء الإنفاق عنه عند انتفاء الحمل، لإجماع النحاة على أن "إن"','3','115',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (667,'للشرط، فينتفي المشروط بانتفائه.
أي: بانتفاء شرطه.
قيل: تسمية "إن" حرف شرط إنما هو اصطلاح للنحاة، وليس ذلك مدلولاً لغويا، فتكون منقولة عن موضوعها الأصلي، فلا يلزم من انتفائه انتفاء الحكم.
قلنا: استعمالها الآن للشرط، يدل على أنها في اللغة كذلك، إذ لو لم تكن، لكانت منقولة عن مدلولها، والأصل عدم النقل.
قيل: عليه: سلمنا أن "إن" للشرط، لكن لا نسلم أنه ينتفي المشروط بانتفاء الشرط، وإنما يلزم ذلك، لو لم يكن للشرط بدل يقوم مقامه.','3','116',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (668,'قلنا: لو كان للشرط بدل يقوم مقامه، فيلزم حينئذ أن يكون الشرط أحدهما، أي: البدن أو المبدل منه، فما فرض شرطًا لم يكن شرطًا، وحينئذ إذا لم ينتف المشروط بانتفائه لا يقدح في مدعانا.
وهو المراد بقوله: "وهو غير المدعى"؛ لأن المدعى أن الشرط واحد بعينه، فإذا وجد بدل يقوم مقام الشرط فلا يكون الشرط واحدًا بعينه، بل أحد الأمرين لا بعينه، وذلك خارج عن محل النزاع.
هذا إذا كان عبارة المتن "غير المدعى" بالراء.
فإن كانت بالنون، فتوجيهه: أن الشرط حينئذ يكون أحد الأمرين وانتفاء أحد الأمرين لا على التعيين يكون بانتفائهما، وينتفي','3','117',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (669,'المشروط بانتفائهما، وهو عين المدعى.
قيل: لو كان المعلق "بإن" ينتفي عند انتفاء ما دخلت عليه "إن" لكان قوله تعالى: } ولا تكرهوا فتياتكم على البغاء إن أردن تحصنًا {دليلاً على أن الإكراه لا يحرم إذا لم يرد التحصن، ولا ليس ذلك، بل هو حرام مطلقًا.
قلنا: لا نسلم أن الحرمة غير منتفية عنه، إذ لا يمكن الإكراه حينئذ، لأنهن إذا لم يردن التحصن لم يكن البغاء مكروهًا عندهن.','3','118',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (670,'والإكراه إنما هو: إلزام فعل مكروه، وإذا لم يمكن لم يتعلق به التحريم؛ لأن شرط التكليف الإمكان.
أو يقال: خرج مخرج الغالب، فلا مفهوم له.
وإن سلم أن المفهوم اقتضى التحريم، لكن قد انتفى لمعارض أقوى منه،
وهو الإجماع، فلم يجز العمل بالمفهوم.
السادسة
التخصيص بالعدد لا يدل على الحكم الزائد على ذلك العدد، ولا الناقص منه بمجرد العدد لا نفيًا ولا إثباتًا؛ لأن الأعداد إن لم تتخالف بالحقيقة جاز اشتراكها في حكم.','3','119',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (671,'وإن تخالفت فيها فكذلك؛ لأن المشتركين قد يختلفان في حكم والمصنف في هذا، تابع للإمام والآمدي.
وفي البرهان عن الشافعي، والجمهور: خلافه.
وقال في المحصول: قد يدل عليه دليل منفصل، وإيضاحه في الشرح.','3','120',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (672,'السابعة
النص: إما أن يستقل بإفادة الحكم أي: لا يحتاج إلى انضمام شيء، أو لا يستقل، أي: يحتاج إلى شيء آخر ينضم إليه، والمقارن له.
إما نص: آخر، مثل دلالة قوله تعالى: } أفعصيت أمري {مع قوله
تعالى: } ومن يعص الله ورسوله فإن له نار جهنم {، فإنهما يدلان على أن تارك الأمر يستحق العقاب.','3','121',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (673,'فإن قوله: } أفعصيت أمري {يدل على مقدمة وهي: أن تارك الأمر عاص، وقوله: } ومن يعص الله {يدل على مقدمة أخرى، وهي: أن كل عاص بصدد العذاب (فينتجان: أن تارك الأمر بصدد العذاب).
أو يكون أحد النصين دالاً على ثبوت الحكم لشيئين، والنص الآخر دالاً على ثبوت بعض ذلك الحكم لأحدهما فقط، فيجب القطع بأن باقي الحكم ثابت للثاني، مثل دلالة قوله تعالى: } وحمله وفصاله ثلاثون شهرًا {، مع قوله تعالى: } والوالدات يرضعن أولادهن {الآية.
على أن أقل مدة الحمل ستة أشهر، فإن قوله تعالى: } وحمله','3','122',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (674,'وفصاله ثلاثون شهرًا {يدل على أن مدة الشيئين وهما الحمل والرضاع ثلاثون شهرًا.
وقوله تعالى: } والوالدات يرضعن أولادهن حولين كاملين {على أن أكمل مدة الرضاع سنتان فوجب الحكم بأن باقي المدة، وهي ستة أشهر مدة الحمل.
أو يكون الضميمة إلى النص (إجماعًا مقارنًا).
مثل دلالة ما دل على إرث الخال من النص نحو قوله تعالى: } وأولوا الأرحام بعضهم أولى ببعض في كتاب الله {مع الإجماع الدال على أن الخالة بمثابة الخال في النسبة إلى الميت، لكون كل منهما فرعين لأصل أصل الميت على إرث الخالة وهذا على سبيل التقدير.','3','123',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (675,'وإليه أشار بقوله: "كالدال على أن الخالة بمثابة الخال في إرثها إذا دل نص عليه فنستفيد إرثها من ذلك النص بواسطة الإجماع فقد تكون الضميمة القياس مثاله: النص الدال على كون البر ربويًا وسكت عن التفاح فثبت حرمة الربا فيه قياسًا على البر.
وقد تكون الضميمة قرائن حال المتكلم مثل ما روى عنه - صلى الله عليه وسلم - أنه قال: "الاثنان فما فوقهما جماعة" رواه ابن ماجة.','3','124',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (676,'دل على أن الجمع اثنان؛ لأنه (عليه الصلاة والسلام) مبعوث لبيان أحكام الشرع لا اللغة.
* * *','3','125',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (677,'<span data-type="title" id=toc-92>الباب الثاني: في الأوامر </span>جمع أمر، والنواهي جمع نهي وتحقيقه في الشرح،','3','127',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (678,'وفيه فصول:
الأول: في لفظ الأمر
أي: فالأمر لا يعني به مسماه، كما هو المتعارف في الإخبار عن الألفاظ إن تلفظ بها.
والمراد مسمياتها، بل لفظة الأمر، وهو: ألف - ميم - راء، كما يقال: زيد مبتدأ، وضرب فعل ماض، وفي حرف جر.
فعلى هذا: مسمى الأمر لفظ، وهو صيغة "افعل"، ومسمى صيغة "افعل" الوجوب على الأصح، أو غير ذلك مما يأتي:
وفيه مسألتان:','3','129',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (679,'الأولى
أنه حقيقة في القول الطالب للفعل، لسبقه إلى الفهم عند إطلاقه، فكان حقيقة فيه، غير مشترك بينه وبين غيره، وإلا لبادر غيره، أو لم يبادر شيء.
وليس متواطئًا، وإلا لكان أعم من القول الطالب، فلم يفهم منه القول الطالب. لأن الأعم لا يدل على الأخص، كما لا يفهم من الحيوان الإنسان خاصة.
فقوله: "القول": أخرج الطلب بالإشارة، والقرائن المفهمة،','3','130',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (680,'فلا يكون أمرًا حقيقة.
وقوله: "الطالب" أخرج: الخبر وشبهه والأمر النفساني، فإنه طلب لا طالب، ولا شك أن الطالب حقيقة هو المتكلم، وإطلاقه على الصيغة مجاز من باب تسمية المسبب باسم سببه الفاعلي.
وقوله: "للفعل" أخرج: النهي فإنه طالب لترك الفعل.
وقد تقدم في تقسيم الألفاظ تقييد الطلب بالذات أي: بالوضع. ولا بد منها هنا، لئلا يرد: أنا طالب منك كذا.
ولم يقيده هنا به؛ لأنه حيث أطلق ينصرف إلى الطالب بالوضع، ولأنه قدمه.','3','131',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (681,'وما يقال: أنه لا بد من قيد آخر وهو: المانع من النقيض سهو.
ولا يعتبر العلو ولا الاستعلاء على ما اختاره المصنف.
قال المحقق: وهو الحق.
واعتبرت المعتزلة: العلو في حد الأمر.
وهو: أن يكون الآمر أعلى رتبة من المأمور في الواقع دون','3','132',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (682,'الاستعلاء:
وهو أن يطلب (على وجه) الغلظة، وإظهار تعاظم، وبه قال الشيخ أبو إسحاق وابن الصباغ والسمعاني.
ونقله القاضي عبد الوهاب عن أهل اللغة، وجمهور أهل العلم، واختاره.
وشرطه (أبو الحسين) البصري، من المعتزلة الاستعلاء دون','3','133',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (683,'العلو.
وبه قال الإمام الرازي، والآمدي، وابن الحاجب.
واشترطهما: ابن القشيري، ونقل عن القاضي عبد الوهاب.
ويفسدهما، أي: يفسد اشتراط العلو والاستغلاء، قوله تعالى: حكاية عن فرعون: } ماذا تأمرون {فأطلق الأمر على القول الطالب للفعل الصادر عن قوم فرعون بلا علو؛ لأن فرعون كان أعلى رتبة منهم، ولا استعلاء؛ لأنه كان يدعي إلهيتهم.','3','134',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (684,'وما يقال: لعله لم يكن العلو والاستعلاء شرطًا في لغة فرعون، ويكون شرطًا في لغة العرب بعيد؛ لأن الله تعالى حكى عنهم بالمعنى، فلا بد من الموافقة معنى.
وما اختاره المصنف هنا لا يناقض ما تقدم له لما تقدم.
وليس حقيقة في غيره، دفعًا للاشتراك، فإن الأصل عدمه، ولو كان مجازًا لأنه خير من الاشتراك.
وقال بعض الفقهاء: إنه مشترك بينه، أي: بين القول المخصوص وبين الفعل أيضًا؛ لأنه يطلق عليه أي: على الفعل مثل قوله','3','135',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (685,'تعالى: } وما أمرنا {أي: وما فعلنا إلا واحدة.
ومثل قوله تعالى: } وما أمر فرعون {، أي: وما فعل فرعون برشيد والأصل في الإطلاق الحقيقة.
قلنا: المراد في الآيتين: الشأن مجازًا لما مر من كونه حقيقة في القول الطالب.
فمعنى الآية الأولى: إن شأننا شيء واحد، وهو أنا إذا أردنا شيئًا أن نقول له: كن فيكون.
وحمل الأمر على الشأن في الثانية أشمل لمذمة فرعون من الفعل.','3','136',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (686,'وقال أبو الحسين البصري: إذا قيل: أمر فلان، ترددنا بين القول والفعل والشيء والشأن والصفة، وهو آية، أي: علامة الاشتراك.
لأنه إذا أطلق لفظ الأمر غير مقيد بما يخصصه بأحد الأمور الخمسة لم يدر الذهن أي هذه الأمور أريد.
فإذا قيد بشيء من المخصصات.
وقيل: أمر بالصلاة فهم القول، أو فعل أمره، فهم الفعل، أو تحرك لأمر فهم الشيء، (أو ساد) لأمر فهم الصفة، أو أمره مستقيم فهم الشأن.','3','137',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (687,'قلنا: لا نسلم تردد الذهن عند إطلاقه، بل يتبادر القول الطالب وهو علامة الحقيقة، وتوقف الباقي على القرينة، وهو علامة المجاز.
تنبيه:
صرح أبو الحسين في المعتمد وشرح العمدة، بأن الأمر ليس موضوعًا للفعل بخصوصه، وإنما يدخل في الشأن.
الثانية
الطلب بديهي التصور، أي: لا يحتاج في معرفته إلى تعريف بحد','3','138',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (688,'أو رسم، كالجوع وسائر الوجدانيات؛ لأن كل من لم يمارس الحدود والرسوم يعرف مفهوم الطلب ويميزه عن غيره، ويطلب في موضع الطلب.
ولولا أنهم عارفون بمفهومه لما تأتي منهم ذلك.
وهو أي: الطلب، غير العبارات المختلفة المعبر بها عنه لاختلافها، وعدم اختلاف الطلب.
والطلب غير الإرادة، خلافًا للمعتزلة في أن ماهية الطلب هي إرادة المأمور به.','3','139',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (689,'فالطلب معنى قائم بالنفس، ومعناه: ميل نفساني إلى ما فيه نفع أو ضر لمن يمكن ذلك في حقه ويؤول في حق الله - تعالى - بما يليق بجلاله.
والإرادة: هذا المعنى يفيد تحصيل المراد من القوة إلى الفعل.
فمعنى الإرادة أخص، ومعنى الطلب أعم، فكل مراد مطلوب، وليس كل مطلوب مراد.
لنا: أن الإيمان من الكافر مطلوب وليس بمراد، لما عرفت في مسألة التكليف بالمحال أن الإيمان من الكافر الذي علم الله - تعالى أنه لا يؤمن، كأبي لهب مطلوب بالاتفاق، مع أنه ليس بمراد لله تعالى لأن الإيمان - والحالة هذه - ممتنع، لأن خلاف علم الله - تعالى - لا يقطع قطعًا، وإذا كان إيمانه ممتنعًا فلا تصح إرادته بالاتفاق.','3','140',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (690,'وليس للمعتزلة أن يقولوا: فلا يكون الممتنع مطلوبًا؛ لأن المطلوب أعم فيجوز كونه ممتنعًا.
ولنا أيضًا: أن الممهد لعذره في ضرب عبده بأنه إنما يضربه لأنه لا يمتثل أمره، إذا أراد عصيان العبد يأمره ولا يريد منه ذلك حذرًا من اللوم.
مثلاً: لو أنكر السلطان ضرب سيدٍ لعبده، متوعدًا له بالإهلاك، إن ظهر أن العبد لا يخالف أمر سيده، والسيد يدعي مخالفة العبد له في أوامره ليدفع عن نفسه الهلاك، فإنه يأمر عبده بحضرة السلطان ليعصيه، ويشاهد السلطان عصيانه له، فيزول إنكاره ويخلص من الهلاك، فهنا قد أمره، وإلا لم يظهر عذره، وهو مخالفة الأمر، ولا يريد منه الفعل؛ لأنه لا يريد ما يفضي إلى هلاك نفسه، وإلا كان مريدًا لهلاك نفسه، وأنه محال.','3','141',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (691,'وعليه إشكالات وأجوبتها (تطلب من) الشرح.
وهذان الوجهان: كما دلا على تغاير الأمر للإرادة، دلا على عدم اشتراط الأمر بالإرادة.
اعترف أبو علي الجبائي، وابنه أبو هاشم بالتغاير بين الأمر والإرادة، وشرطا الإرادة في الدلالة.','3','142',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (692,'قالا: لأن صيغة الأمر كما ترد للطلب، ترد للتهديد أيضًا، فلا بد من تمييز ليتميز الطلب عن التهديد، ولا مميز إلا الإرادة، لكون المأمور مرادًا في الطلب غير مراد في التهديد.
قلنا: دلالة الألفاظ على معانيها إنما هي بحسب وضع اللفظ لها.
وحينئذ كونه مجازًا في التهديد حقيقة في الطلب كاف في الفرق بينهما، فيجب عند إطلاقه حمله على الحقيقة، وهو الطلب، وفي التهديد لا بد من قرينة.
تنبيه:
قال ابن برهان: لنا ثلاث إيرادات:
إرادة إيجاد الصيغة، وهي شرط اتفاقًا.','3','143',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (693,'وإرادة صرف اللفظ عن غير جهة الأمر إلى جهة الأمر، شرطها المتكلمون دون الفقهاء.
وإرادة الامتثال، وهي محل النزاع بيننا وبين أبي علي وابنه.
وذكر الثلاث أيضًا: إمام الحرمين، والغزالي، وغيرهما.
لكن حكى الخلاف في إرادة إيجاد الصيغة ابن المطهر المتأخر الرافضي في كتاب له في أصول الفقه.','3','144',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (694,'<span data-type="title" id=toc-96>الفصل الثاني: في صيغته</span>
أي: في صيغة القول الطالب، فإنه المراد بصيغة الأمر، وهو أمر المخاطب، وأمر الغائب، واسم الفعل بمعنى الأمر، وفيه مسائل:
الأولى أن صيغة: "افعل" ترد لستة عشر معنى:
الأول: الإيجاب، كقوله تعالى: } وأقيموا الصلاة {.
الثاني: الندب، مثل قوله تعالى: } فكاتبوهم {.','3','145',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (695,'ومنه، أي: ومن الندب، التأديب كقوله - صلى الله عليه وسلم - لعمر بن أبي سلمة: "كل مما يليك" متفق عليه.
فالأدب مندوب إليه، ونص على أنه منه؛ لأن بعضهم جعله قسما آخر.','3','146',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (696,'والفرق بينهما: ما بين العام والخاص؛ لأن الأدب متعلق بمحاسن الأخلاق، والمندوب أعم.
وأنت خبير بأن المثال للتفهيم، فلا يضر كون الشافعي (رضي الله عنه) نص على تحريم الأكل مما لا يلي الإنسان.
ويكفي في ذلك أن الندب قيل به في الجملة.
الثالث: الإرشاد، كقوله تعالى: } واستشهدوا {، والفرق بين الإرشاد والندب، أن الندب لثواب الآخرة، والإرشاد (لمنافع الدنيا.
والعلاقة بين الوجوب والندب والإرشاد) المشابهة المعنوية لاشتراكهما في الطلب.','3','147',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (697,'الرابع: الإباحة مثل قوله تعالى: } وكلوا واشربوا ولا تسرفوا {يفهم منه إرادة فوق سد الرمق إلى الشبع وليس بواجب.
ويجب أن تكون الإباحة معلومة من غير الأمر حتى تكون قرينة لجملة على الإباحة، والعلاقة هي الإذن، وهي مشابهة معنوية.
قال العراقي: أورد عليه أن الأمر في هذه الآية للوجوب.
ونحن نقول: لم يرد هذه الآية، بل قوله تعالى: } كلوا من الطيبات {.
الخامس: التهديد كقوله تعالى: } اعملوا ما شئتم {.','3','148',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (698,'فإنه فهم بالقرينة أنها صغية مذكورة فيه في معرض التهديد.
ومنه الإنذار كقوله تعالى: } قل تمتعوا فإن مصيركم إلى النار {. ونص عليه؛ لأن جماعة جعلوه قسمًا آخر.
والفرق بينهما أن التهديد هو نفس التخويف، والإنذار هو الإبلاغ، ولا يكون إلا في التخويف، قاله الجوهري.
فقوله تعالى: } قل تمتعوا {أمر بإبلاغ هذا الكلام المخوف الذي عبر عنه بالأمر، وهو تمتعوا فيكون أمرًا بالإنذار.
والعلاقة التي بينه وبين الإيجاب هي المضادة؛ لأن المهدد عليه هو الحرام.','3','149',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (699,'السادس: الامتنان كقوله تعالى: } كلوا مما رزقكم الله {.
والفرق بينه وبين الإباحة: أن الإباحة هي الإذن المجرد، والامتنان أن يقترن به ذكر احتياجنا إليه، أو عدم قدرتنا عليه، ونحوه كالتعرض في هذه الآية إلى أن الله تعالى هو الذي رزقه.
وفرق بعضهم بأن الإباحة تكون في الشيء الذي سيوجد بخلاف الامتنان.
والعلاقة فيه مشابهة الإيجاب في الإذن، لأن الامتنان إنما يكون في مأذون فيه.
السابع: الإكرام، نحو قوله تعالى: } ادخلوها بسلام آمنين {فإن ضم السلامة والأمن للأمر بدخول الجنة قرينة لكونها للإكرام.','3','150',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (700,'والعلاقة هي المشابهة في الإذن أيضًا.
الثامن: التسخير كقوله تعالى: } كونوا قردة خاسئين {.
والفرق بينه وبين التكوين، أن التكوين سرعة الوجود عن العدم، وليس فيه انتقال من حالة إلى حالة.
والتسخير هو الانتقال إلى حالة ممتهنة؛ إذ هو لغة الذلة: والامتهان في العمل، والباري تعالى خاطبهم بذلك في معرض التذليل لهم.
والعلاقة فيه وفي التكوين هي: المشابهة المعنوية، وهي التحتم في وقوع هذين، وفي فعل الواجب أو العلاقة الطلب.
ولا يقال الصواب السخرية، وهو الاستهزاء، فإنه ذهول عن','3','151',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (701,'المدلول السابق، وتغليط للأئمة، إذ سماه القفال، والغزالي، والإمام، وأتباعه، بالتسخير لا بالسخرية، وتكرير لما يأتي؛ إذ لا يخرج الاستهزاء عن الإهانة (أو التحقير).
التاسع: التعجيز كقوله تعالى: } فأتوا بسورة من مثله {.
أعجزهم بطلب المعارضة عن الإتيان بمثله.
والعلاقة المضادة؛ لأن التعجيز إنما هو في الممتنعات، والإيجاب','3','152',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (702,'في الممكنات.
العاشر: الإهانة مثل قوله تعالى: } ذق إنك أنت العزيز الكريم {إذ فهم بالقرينة أنها في معرض الإهانة.
والعلاقة فيه وفي الاحتقار هي المضادة؛ لأن الإيجاب على العباد تشريف لهم لما فيه من تأهيله لخدمته؛ إذ كل أحد لا يصلح لخدمة الملك، ولما فيه من رفع درجاتهم.
الحادي عشر: التسوية بين الشيئين، مثل قوله تعالى: } اصبروا أو لا تصبروا سواء عليكم {.
وعلاقته هي المضادة؛ لأن التسوية بين الفعل والترك مضادة','3','153',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (703,'لوجوب الفعل.
الثاني عشر: الدعاء، كقوله - صلى الله عليه وسلم -: "اللهم اغفر لي ما قدمت وما أخرت ... " إلى آخره رواه مسلم.
والعلاقة فيه وفيما بعده ما عدا الأخير هو الطلب.
وقد تقدم لبعضها علاقة أخرى.
الثالث عشر: التمني، كقول امرئ القيس:
"ألا أيها الليل الطويل ألا انجلي"','3','154',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (704,'وجعل الشاعر متمنيًا ولم يجعله مترجيًا؛ لأن الترجي في الممكنات، والتمني في المستحيلات، كما مر.
وليل المحب لطوله كأنه مستحيل الانجلاء، ولذا قال الشاعر:
................ *** وليل المحب بلا آخر
الرابع عشر: الاحتقار، نحو قوله تعالى حكاية عن موسى - (عليه الصلاة والسلام) يخاطب السحرة: } ألقوا ما أنتم ملقون {. يعني أن السحر في مقابلة المعجزة حقير.','3','155',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (705,'والفرق بينه وبين الإهانة أنها تكون بقول أو فعل أو ترك قول، أو ترك فعل كترك إجابته، ولا يكون بمجرد الاعتقاد، والاحتقار، قد يحصل بمجرد الاعتقاد.
الخامس عشر: التكوين، وهو الإيجاد، كقوله تعالى: } كن فيكون {.
السادس عشر: وروده بمعنى الخبر، كقوله - صلى الله عليه وسلم -: "إذا لم تستحي','3','156',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (706,'فاصنع ما شئت" رواه البخاري.
معناه: صنعت ما شئت.
وقيل: المعنى إذا لم تستحي من شيء لكونه جائزًا فاصنعه، إذ الحرام يستحى منه بخلاف الجائز.
وقد يرد عكسه، أي: الخبر بمعنى الأمر، كقوله تعالى: } والوالدات يرضعن أولادهن {أي: ليرضعن.
قال الإمام: والسبب في جواز هذا المجاز، أن الأمر والخبر يدلان على وجود الفعل.','3','157',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (707,'وقوله: "لا تنكح المرأة المرأة" يعني به: أن الخبر يقع موقع النهي، كما وقع موقع الأمر.
كما رواه الدارقطني في سننه أن النبي - صلى الله عليه وسلم - قال: "لا تزوج المرأة المرأة"، وفيه جميل، وثقه ابن حبان، فإن المراد منه النهي، وصيغته صيغة الخبر.
وأهمل عكس هذا القسم، وذكره الإمام وقال: "وجه المجاز أن النهي وهذا الخبر يدلان على عدم الفعل".','3','158',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (708,'ولا شك أن مجيء الخبر بمعنى الأمر أو النهي لا تعلق له بالمقصود الذي هو بيان مدلولات صيغة الأمر، وإنما ذكره استطرادًا.
وبقي من مدلولات صيغة الأمر:
التفويض، مثاله: } فاقض ما أنت قاض {.
والتعجب، كقوله تعالى: } انظر كيف ضربوا لك الأمثال {.
والتكذيب، كقوله تعالى: } قل فأتوا بالتوراة فاتلوها إن كنتم صادقين {.
والمشورة، كقوله تعالى: } فانظر ماذا ترى {.','3','159',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (709,'والاعتبار، كقوله تعالى: } انظروا إلى ثمره {.
الثانية
أنها حقيق في الوجوب، مجاز في البواقي، عند الجمهور، وهو الحق.
وهل ذلك بوضع اللغة، أو الشرع، أو العقل؟
[فيه] أقوال:','3','160',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (710,'صحح الشيخ أبو إسحاق الأول، وحكاه في البرهان عن الشافعي - رضي الله عنه - واختار هو الثاني.
وقال أبو هاشم: إنه للندب حقيقة، ونقل عن الشافعي - رضي الله عنه.
وقيل: للإباحة حقيقة.','3','161',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (711,'وقيل: مشترك بين الوجوب والندب اشتراكًا لفظيًا.
وقيل: للقدر المشترك بينهما، وهو الطلب.
وقيل: لأحدهما حقيقة، ولا نعرفه، وهو قول الحجة.
وفي نسبته للغزالي نظر.','3','162',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (712,'وقيل: مشترك بين الثلاثة، أي: موضوع للوجوب، والندب، والإباحة، بالاشتراك اللفظي.
وقيل: بالاشتراك المعنوي.
وقيل: مشترك بين الخمسة فقيل: أراد بالخمسة الوجوب والندب والإباحة والتحريم والكراهة.
وفيه نظر:
فإن الحرمة، والكراهة، لم يردا في المعاني الستة عشر.
إلا أن يقال: ورد التهديد، وهو محتمل التحريم فقط فتبقى الكراهة.
وقيل: بين الخمسة المذكورة في المتن بالترتيب، وهي الوجوب،','3','163',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (713,'والندب، والإرشاد، والإباحة، والتهديد.
ويؤيده أن في بعض النسخ بين الخمسة الأول.
والأول حكاه في المحصول.
ولا خلاف أنها ليست حقيقة في جميع هذه المعاني.
لنا: على أن صيغة الأمر للوجوب حقيقة وجوه خمسة:
الأول: أنه - تعالى - ذم إبليس على ترك السجدة في قوله تعالى: } ما منعك ألا تسجد إذ أمرتك {.
إذ الاستفهام على الله - تعالى - محال، لعلمه بالمانع، فالمراد الذم قطعًا، فيكون السؤال في معرض الإنكار والاعتراض.','3','164',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (714,'والمراد بالأمر: } اسجدوا {في قوله تعالى: } وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس {.
ولولا أن صيغة} سجدوا {للوجوب لما كان متوجهًا ولا ملامًا؛ لأنه حينئذ لم يلزم، لكنه (ذم على ترك المأمور فيكون واجبًا.
ولا قائل بالفرق بين هذا الأمر وغيره).','3','165',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (715,'الثاني: قوله تعالى: } وإذا قيل لهم اركعوا لا يركعون {.
ذمهم على مخالفة الأمر، وهو معنى الوجوب، وليس هو للإخبار؛ لأن ذلك معلوم.
قيل: إنما ذم على التكذيب، أي: تكذيب الرسل في التبليغ؛ لأنه رتب العذاب على التكذيب في قوله تعالى: } ويل يومئذ للمكذبين {ولم يقل: ويل يومئذ للتاركين للأمر.
قلنا: الظاهر أنه، أي: أن الذم للترك، أي: لترك الأمر؛ لترتب} لا يركعون {الدال على الذم على قوله: } اركعوا {، والترتيب يشعر بالعلية.','3','166',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (716,'وأن الويل إنما هو للتكذيب، وأيضًا فلتكثير الفائدة في كلام الله تعالى. وحينئذ، فإن صدر الترك والتكذيب من طائفتين عذبت كل منهما على ما فعلته، وإن صدرا من طائفة واحدة عذبت عليهما معا، كما تقدم.
قيل: لا يلزم من كون} اركعوا {للوجوب أن يكون مجرد الأمر للوجوب، لاحتمال أن تكون قرينة اقتضت الوجوب في هذه الصورة، فلذلك ذمهم على الترك وإليه أشار بقوله: "لعل قرينة أوجبت".
قلنا: رتب الأمر على ترك مجرد افعل - أي: على مجرد ترك الأمر لما قيل لهم: } اركعوا {ولم تظهر قرينة، والأصل عدمها.
فدل على أنه منشأ الذم لا القرينة.
الثالث:','3','167',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (717,'تارك الأمر مخالف للأمر، كما أن الآتي موافق له، فالمخالفة تقابل الموافقة، والمخالف للأمر على صدد العذاب، أي: يقرب أن ينزل عليه العذاب لقوله تعالى: } فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم {.
هدد مخالف الأمر، والتهديد دليل الوجوب.
بيانه: أن} الذين يخالفون {فاعل} فليحذر {} وأن تصيبهم {مفعوله، وهذا الأمر للإيجاب قطعًا.
إذ لا معنى لندب الحذر عن العذاب وإباحته.
ومعنى} يخالفون عن أمره {: يتركون امتثاله والإتيان بما أمروا به، من قولهم: خالفني فلان عن هذا إذا أعرض عنه، وأنت قاصد إيتاءه.
والمعني: يخالفون المؤمنين عن أمر الله - تعالى - أو أمر النبي - صلى الله عليه وسلم -.','3','168',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (718,'ويجوز أن يكون على تضمين المخالفة معنى الإعراض.
وإذا وجب على مخالف الأمر الحذر عن العذاب كان تهديدًا على مخالفة الأمر، وهو دليل على كون الأمر للوجوب؛ إذ لا تهديد على غير الواجب.
قيل: لا نسلم أن موافقة الأمر عبارة عن الإتيان بمقتضاه حتى ينتج ما قلتم، بل الموافقة اعتقاد حقية الأمر أي: كونه حقًا واجبًا قبوله.
فالمخالفة اعتقاد فساده وكذبه، لا ترك الأمر فلا يلزم ما ذكرتم.
قلنا: مخالفة الأمر عبارة عن: ترك المأمور به كما دللنا عليه، وأما اعتقاد حقية الأمر فذلك موافقة لدليل الأمر لا له.','3','169',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (719,'فإن موافقة الشيء عبارة عما يستلزم تقرير مقتضاه، فإن دل على كون الشيء صدقًا لدليل الأمر، فموافقته هي اعتقاد الحقية، وإن دل على إيقاع الفعل كالأمر، فموافقته هي الإتيان بذلك الفعل.
قيل: لا نسلم أن الآية، تدل على أنه - تعالى - أمر المخالفين بالحذر، بل على أنه - تعالى - أمر بالحذر عن المخالفين، لأن الفاعل} ليحذر {ضمير و} الذين يخالفون {مفعول} ليحذر {لا فاعل له.
قلنا: الإضمار خلاف الأصل، فإضمار الفاعل مع وجود ما يصلح أن يكون فاعلاً خلاف الأصل.
ومع هذا الإضمار فلا بد له، أي للضمير من مرجع، أي: من','3','170',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (720,'اسم ظاهر يرجع إليه وهو مفقود هنا.
قيل: مرجعه} الذين يتسللون {المقدم ذكرهم.
قلنا: هم المخالفون للأمر فكيف يؤمرون بالحذر عن أنفسهم؟
وإن سلم أن المراد من الآية بالحذر عن مخالفي الأمر، لكن يلزم منه أن يصير التقدير:
فليحذر الذين يتسللون منكم لواذًا عن الذين يخالفون.
وحينئذ فيضيع قوله تعالى: } أن تصيبهم فتنة {لكون} فليحذر {قد استوفى فاعله، ومفعوله، وليس هو مما يتعدى إلى','3','171',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (721,'مفعولين.
ولا يقال: إنه مفعول لأجله؛ لأنه يلزم أن يكون مجامعًا للحذر، إذ يجب أن يجامع الفعل علته فيه، واجتماعهما مستحيل.
وأيضًا: ضمير} فليحذر {مفرد} والذين يتسللون {جمع، فلا يعود إليه.
قيل: سلمنا أن المخالفين للأمر مأمورون بالحذر عن','3','172',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (722,'العذاب.
ولكن لم قلتم بأن المأمور بالحذر يجب عليه الحذر، وإنما يجب أن لو كان الأمر وهو قوله - تعالى: -} فليحذر {للوجوب، وهو ممنوع، إذ هو محل النزاع.
وإليه أشار بقوله: } فليحذر {لا يوجب.
قلنا: يحسن، وهو دليل قيام المقتضى، يعني نحن لا ندعى أن} ليحذر {يوجب الحذر، بل يدل على حسن الحذر، وحسن الحذر عن العذاب، يدل على قيام المقتضى للعذاب، إذ لو لم يوجد المقتضى له، لكان الأمر بالحذر عنه سفها وعبثًا، وهو على الله تعالى محال.
ولا مقتضى للعذاب إلا ترك المأمور به، فلزم كون الأمر للوجوب؛ لأن المقتضي للعذاب ترك الواجب لا ترك المندوب.
قيل: قوله تعالى: } عن أمره {.','3','173',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (723,'لا يعم لأنه مطلق.
قلنا: عام لجواز الاستثناء منه، إذ يصح أن يقال: فليحذر الذين يخالفون عن أمره إلا مخالفة الأمر الفلاني، والاستثناء معيار العموم، على أن الإطلاق كاف في المطلوب، وهو كون الأمر المطلق للوجوب خاصة، إذ لو كان حقيقة لغيره أيضًا لم يترتب الذم والتهديد على مخالفة مطلق الأمر.
الرابع:
تارك الأمر عاص لقوله تعالى: } أفعصيت أمري {أي: تركت مقتضاه إجماعًا.','3','174',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (724,'فحكم على تارك المأمور بأنه عاص.
وقوله تعالى في صفة الملائكة: } لا يعصون الله ما أمرهم ويفعلون ما يؤمرون {كذلك.
والعاصي يستحق النار، لقوله تعالى: } ومن يعص الله ورسوله فإن له نار جهنم خالدين فيها أبدًا {.
ويلزم من المقدمتين، أن تارك الأمر يستحق النار، فيكون للوجوب، إذ لا معنى لكون الأمر للوجوب إلا استحقاق تاركه النار.
واللام في قوله: تارك الأمر عاص للاستغراق.
قيل: لو كان العصيان ترك الأمر لتكرر قوله تعالى: } ويفعلون','3','175',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (725,'ما يؤمرون {؛ لأن قوله تعالى: } لا يعصون الله ما أمرهم {حينئذ معناه: يفعلون المأمور به.
قلنا: الأول وهو} لا يعصون الله ما أمرهم {ماض أو حال، أي: لا يعصون الله ما أمرهم به في الماضي أو الحال.
والثاني: مستقبل أي: ويفعلون ما يؤمرون في الاستقبال.
قيل: وقول المصنف: العصيان (ترك المأمور به.
فيه نظر: لأن النزاع إنما وقع في أن ترك المأمور به هل هو عصيان) لا في كون العصيان ترك المأمور به والعصيان أعم.','3','176',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (726,'ونظر فيه: من جهة أن النحاة نصوا على أن "لا" لنفي المستقبل، واستعمالها بمعنى "إن" قليل - مجاز، فيجتمع المجاز في الفعل المضارع، وفي لا أيضًا، لكن كثر استعمال الفعل المضارع في الحالة المستمرة.
قال العراقي: والأحسن في الجواب ما ذكره بعضهم أن قوله: } لا يعصون {إخبار عن الواقع منهم.
وقوله: } يفعلون {إخبار عن سجيتهم التي طبعوا عليها أعني الطاعة.
قيل: المراد، بقوله تعالى: } ومن يعص الله ورسوله {','3','177',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (727,'الكفار، لا تارك الأمر، لقرينة الخلود، فإن المؤمن العاصي لا يخلد في النار، لقوله تعالى: } (إن الله) لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء {.
قلنا: المراد من الخلود: المكث الطويل لا الدائم كما يقال: حبس فلان جبسًا مخلدًا ويراد به طول المكث.
الخامس: أنه (عليه الصلاة والسلام) احتج لذم أبي سعيد الخدري','3','178',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (728,'(رضي الله عنه) على ترك استجابته حال كونه مصليًا، (وهو يصلي) بقوله تعالى: } استجيبوا لله وللرسول إذا دعاكم {.
فهذا السؤال ليس طلبًا لفهم العذر؛ إذ لا حاجة إليه، فإن الصلاة عذر لترك الكلام، بل هو للذم والتوبيخ لترك المأمور به وهو الاستجابة.
بدليل أنه احتج عليه لذمه بقوله: } استجيبوا لله وللرسول إذا دعاكم {فإذا كان ترك الأمر موجبًا للذم - وخصوصًا عند حصول عذر تركه - كان للوجوب لا محالة؛ إذ لا معنى بكونه للوجوب سوى ذلك.
قال العبري: وفيه نظر؛ لأن الوجوب في هذه الصورة مستفاد من القرينة،
وهي قوله: } إذا دعاكم {لا من مجرد {استجيبوا}.','3','179',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (729,'تنبيه: وقع في نسخ المنهاج تبعًا للمستصفى والمحصول أبو سعيد الخدري وهو سهو، إنما هو أبو سعيد بن المعلى (كما رواه) البخاري عنه قال: كنت أصلي في المسجد فدعاني رسول الله - صلى الله عليه وسلم - فلم أجبه، ثم أتيته فقلت: يا رسول الله، إني كنت أصلي فقال: صلى الله عليه وسلم: "ألم يقل الله: } استجيبوا لله وللرسول إذا دعاكم {؟ " وقد رواه النسائي عن أبي بن كعب أيضًا.','3','180',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (730,'قال العراقي: ولا نعلم هذا روي عن أبي سعيد الخدري بوجه من الوجوه، وهو شيخ الإسلام في الحديث وغيره.
احتج أبو هاشم: بأن الفارق بين السؤال والأمر هو الرتبة، والسؤال للندب فكذا الأمر.
كذا وقع في كثير من النسخ، وفيه نظر. لأن الاحتجاج الثاني والثالث لا يوافق مذهب أبي هاشم.
وفي بعضها احتج المخالف، وفي بعضها احتجوا، وهما','3','181',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (731,'واضحان. ويمكن تصحيح الأول بتعسف.
وحاصل احتجاج أبي هاشم: أن أهل اللغة قالوا:
الفرق بين السؤال والأمر الرتبة، فإن رتبة الآمر المكي من رتبة السائل، والسؤال للندب، فكذا الأمر؛ لأن الأمر لو دل على الإيجاب، كان بينهما فرق آخر وهو خلاف ما قاله أهل اللغة.
قلنا: السؤال إيجاب؛ لأن السائل يستعمل الصيغة للإيجاب وإن لم يتحقق الوجوب على المسئول منه؛ إذ الوجوب من الشارع.','3','182',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (732,'واحتج القائل: بأن الصيغة حقيقة في القدر المشترك بين الوجوب والندب، وهو رجحان الفعل على الترك، بأن الصيغة لما استعملت فيهما، أي: في الوجوب والندب، فلو كانت حقيقة في كل واحد منهما لزم الاشتراك أو حقيقة في أحدهما، لزم المجاز، والاشتراك والمجاز خلاف الأصل، فتكون في القدر المشترك بينهما وهو طلب الفعل.
وعلم من هذا بطلان المذهب القائل: بالاشتراك إما بين الوجوب والندب فقط، أو بينهما وبين الإباحة فقط، أو بين الأحكام الخمسة.','3','183',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (733,'ولذا لم يتعرض المصنف لهذه المذاهب، وإن أوردها في أول المسألة.
قلنا: يجب المصير إلى المجاز، لما بينا من الدليل الدال على كون الصيغة حقيقة في الوجوب وإن كان المجاز خلاف الأصل.','3','184',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (734,'واحتج القائل بالوقف.
بأن تعرف مفهومها لا يمكن بالعقل، لأنه لا مدخل له في اللغات، وكذا النقل؛ لأنه لم يتواتر، وإلا لكان مفهوم الأمر معلومًا قطعًا، فكان لا يختلف فيه، فيكون من باب الآحاد، والآحاد لا تفيد القطع لما سيجيء.
فثبت أن تعرف مفهوم الأمر على سبيل القطع غير ممكن، وإذا لم يمكن، لم يمكن الحكم بكونه حقيقة في أحدهما على التعيين، لكون المسألة وهو الحكم على الصيغة بذلك علمية، فلا يكفي فيه غير القطع، وإذا لم يمكن الحكم على الصيغة بكونها حقيقة في أحدهما على التعيين، يلزم التوقف في ذلك؛ إذ هو عينه.
قلنا: المسألة وإن كانت أصولية لكنها وسيلة إلى العمل؛ لأن','3','185',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (735,'المقصود من كون الأمر للوجوب هو العمل به لا مجرد اعتقاده، والعمليات يكتفي فيها بالظن، وكذا ما كان وسيلة إلى العمل فيكفيها الظن.
وأيضًا: الحصر ممنوع، فإن هنا قسمًا آخر، وهو أنه يتعرف بتركيب عقلي من مقدمات نقلية، كما سبق من أن تارك الأمر عاص، وكل عاص يستحق النار، فتارك الأمر يستحق النار.
فإنه دل على أن الأمر للوجوب.
وكذا الجمع المحلى باللام، يدخله الاستثناء.
والاستثناء: إخراج ما لولاه لدخل فيه، فيدل على أن الجمع المعرف باللام للعموم.
فقوله: كما سبق، يحتمل كلاً من المثالين.
والأول: أولى للتصريح به فيما أخذ منه.
ولكونه دليلاً على المتنازع فيه ولأنه أقرب، واقتصر العبري على','3','186',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (736,'الثاني.
الثالثة:
الأمر بعد التحريم للوجوب، وبه قال القاضي أبو الطيب والشيخ أبو إسحاق، وأبو المظفر السمعاني، والإمام الرازي.','3','187',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (737,'وقيل: للإباحة، ورجحه ابن الحاجب، ونص عليه الشافعي - رضي الله عنه - كما نقله جماعة، ونقله ابن برهان عن أكثر الفقهاء والمتكلمين.
لنا: أن الأمر يفيده، أي: يفيد الوجوب، ووروده بعد الحرمة لا يدفعه؛ لأن وروده بعد الحرمة ليس معارضًا حتى يدفع ما ثبت له.
لأن الوجوب والإباحة منافيان للتحريم، ومع ذلك لا يمتنع الانتقال من التحريم إلى الإباحة فكذا إلى الوجوب.
فقد ثبت أنه غير مانع، وصيغة الأمر مقتضية للإيجاب، فوجب حمله على الوجوب عملاً بالمقتضى السالم عن المعارض، وفيه نظر.','3','188',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (738,'قيل: غلب في عرف الشرع فيها، فيقدم على الوجوب، وذلك؛ لأن الإباحة هي السابقة إلى الفهم.
في نحو قوله تعالى: } وإذا حللتم فاصطادوا {فيكون للإباحة.
قلنا: معارض بقوله تعالى: } فإذا انسلخ الأشهر الحرم فاقتلوا {.','3','189',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (739,'فإن القتال فرض كفاية، بعد أن كان حرامًا.
فإذا تعارضا تساقطا، وبقي دليلنا سالمًا فيفيد الوجوب.','3','190',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (740,'والأمر بعد الاستئذان، كالأمر بعد التحريم، قاله الإمام.
- واختلف القائلون بالإباحة: في النهي الوارد بعد الوجوب:','3','191',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (741,'- فمنهم من قال: للإباحة قياسًا على الأمر.
ومنهم من قال: إنه للتحريم، وحكي عن الجمهور.
ونقل الماضي أبو بكر والأستاذ الاتفاق عليه. كما لو ورد ابتداء.
بخلاف الأمر بعد التحريم؛ لأن مقتضى النهي، وهو الترك موافق للأصل، لأن الأصل عدم الفعل بخلاف مقتضى الأمر، وهو الفعل.
الرابعة:
الأمر المطلق: أي: العاري عن التقييد بالمرة أو بالتكرار أو','3','192',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (742,'بالصفة أو بالشرط، فإنه لا يفيد التكرار ولا يدفعه، بل إنما يفيد طلب فعل المأمور به من غير إشعار بالمرة والمرات، لكن المرة الواحدة لا بد منها في الامتثال، فهي من ضروريات الإتيان بالمأمور به.
وقيل: هو للمرة، فلا يحمل على التكرار إلا بدليل، وبه قال كثير.
قال الشيخ أبو حامد: وهو مقتضى قول الشافعي - رضي الله','3','193',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (743,'عنه.
وقيل: بالتوقف إما للاشتراك بينهما لفظًا، ولا قرينة معه فيجب التوقف، أو الجهل بالحقيقة أي: لا يدري حقيقته أهو التكرار أو المرة.
لنا: أنه صح تقييده بالمرة والمرات من غير تكرار ولا نقض، فلو كان للمرة لكان تقييده بها تكرارًا وبالمرات تناقضًا.
ولو كان للتكرار لكان تقييده به تكرارًا وبالمرة نقضًا.','3','194',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (744,'ولا يقال: هذا لا يثبت المدعى، إذ عدم التكرار والنقض قد لا يكون لكونه موضوعًا للماهية من حيث هي، بل لكونها مشتركًا أو لأحدهما، ولا نعرفه كما سبق فيكون التقييد لأحدهما؛ لأنه سيبطلها.
ولنا: أنه - أي: الأمر المطلق - ورد مع التكرار شرعًا كآية الصلاة، وعرفًا نحو: احفظ دابتي.
وورد مع عدمه شرعًا كآية الحج، وعرفًا كقوله: ادخل الدار.
فيكون حقيقة في القدر المشترك بين التكرار والمرة، وهو طلب الإتيان به - أي: بالفعل - مع عدم قطع النظر عن التكرار والمرة دفعًا للاشتراك والمجاز؛ لأنه لو كان حقيقة في كل منهما لزم الاشتراك.
أو في أحدهما لزم المجاز وهما خلاف الأصل.','3','195',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (745,'ولنا أيضًا: أنه لو كان للتكرار لعم الأوقات كلها لعدم أولوية وقت دون وقت فيكون تكليفًا بما لا يطاق، وأنه باطل.
ولنا أيضًا: أنه لو كان للتكرار لكان ينسخه كل تكليف بعده لا يجامعه في الوجود؛ لأن الاستغراق الثابت بالأول، يزول بالاستغراق الثابت بالثاني، واللازم باطل قطعًا.
وأنت خبير بأن النزاع إنما هو فيما يمكن، كما نقله الشيخ أبو إسحاق، وإمام الحرمين، وغيرهما.
وإلزامه بالنسخ فيه نظر؛ لأنه إنما يلزم إذا كان التكليف مطلقًا','3','196',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (746,'غير مخصص. وهذا غير واقع في الشرع.
أما إذا خصص الثاني بوقت فلا ينسخ الأول بل يخصصه.
قيل: إن أهل الردة لما منعوا الزكاة تمسك أبو بكر الصديق - رضي الله عنه - على التكرار بقوله تعالى: } وآتوا الزكاة {.','3','197',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (747,'متفق عليه من غير نكير من أحد من الصحابة، فكان إجماعًا منهم على أنها للتكرار.
قلنا: لعله، عليه (الصلاة والسلام) بين تكراره أي تكرار} وآتوا الزكاة {وقد رواه أبو داود من جهة عبد الله بن معاوية رفعه','3','198',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (748,'"ثلاث من فعلهن طعم طعم الإيمان: من عبد الله وحده، وهلم أنه لا إله إلا الله، وأعطى زكاة ماله طيبة بها نفسه في كل عام".
الحديث ... ولم يصل سنده.
ووصله الطبراني في معجمه.
قيل: النهي يقتضي التكرار، فكذا الأمر والجامع أن كلا منهما','3','199',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (749,'طلب
قلنا: الانتهاء أبدًا ممكن دون الامتثال؛ لأن مقتضى النهي عن الشيء الانتهاء، ومقتضى الأمر بالشيء الإتيان به.
وإذا كان كذلك، فالفرق بينهما في الاستغراق بمقتضاهما ظاهر؛ لأن استغراق الأوقات كلها بمقتضى النهي وهو الانتهاء عن الشيء أبدا ممكن، واستغراقها بمقتضى الأمر وهو امتثال المأمور والإتيان به دائمًا غير ممكن، فلا يصح القياس لظهور الفرق.
هذا إذا تنزلنا وسلمنا حكم الأصل، فإن منعناه فواضح، ولذا تنزل المصنف فلا يكون مناقضًا لما يأتي له من أن النهي كالأمر.
لكن فيه نظر: من جهة أن القائل به يشترط الإمكان كما مر.','3','200',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (750,'فالأحسن أن يجاب: بأن مقتضى الأمر الإتيان بالمأمور به، وذلك يصدق بمرة واحدة، بخلاف النهي، فإنه لما كان مقتضاه الكف عن المنهي عنه لم يتحقق ذلك إلا بالامتناع المستمر.
قيل: لو لم يتكرر لم يرد النسخ بعده؛ لأن ورود النسخ بعد الأمر، إنما هو لرفع الحكم الثابت بالأمر.
فإذا لم يكن للتكرار، كان منتهيًا بنفسه، فلا يحتاج إلى ناسخ، لكن النسخ ورد كثيرًا بعد الأمر فيكون للتكرار.
قلنا: الأمر وإن لم يقتض التكرار لكنه لا ينافيه لما مر أنه حقيقة في القدر المشترك، فيجوز حمله في بعض الصور على التكرار لقرينة تدل عليه. وحينئذ إذا ورد النسخ عليه كان وروده قرينة التكرار.','3','201',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (751,'قيل: حسن الاستفسار عن الأمر بأنه للمرة أو للمرات دليل الاشتراك اللفظي.
ولذلك قال سراقة للنبي - صلى الله عليه وسلم -: "أحجنا هذا لعامنا أم للا بد" مع أنه من أهل اللسان، وأقره عليه.
فلو كان الأمر موضوعًا في لسان العرب للتكرار أو للمرة لاستغنى عن الاستفسار، ولم يحسن.','3','202',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (752,'قلنا: حسن الاستفسار لا يدل على الاشتراك اللفظي بخصوصه لأنه يجري في الألفاظ المتواطئة، فإنه قد يستفسر عن أفراد المتواطئ، حتى إذا قال: أعتق رقبة، حسن أن يستفسر أمؤمنة أم كافرة؟ سليمة أم معيبة، إلى غير ذلك.','3','203',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (753,'الخامسة:
الأمر المعلق بشرط أو صفة، مثل قوله تعالى: } وإن كنتم جنبًا فاطهروا {، ومثل قوله تعالى: } والسارق والسارقة فاقطعوا {، لا يقتضي التكرار لفظًا ويقتضيه قياسًا.','3','204',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (754,'أما أنه لا يقتضيه لفظًا، وهو المعني بقوله: أما الأول فلأن ثبوت الحكم مع الصفة أو الشرط يحتمل التكرار وعدمه، فإن اللفظ إنما دل على تعليق شيء على شيء وهو أعم من تعليقه عليه في كل الصور، أو في صورة واحدة بدليل تقسيمه إليهما، والأعم لا يدل على الأخص. فالتعليق لا يدل على التكرار.
ولأنه لو قال لزوجته: إن دخلت الدار فأنت طالق، لم يتكرر الطلاق بتكرر الدخول، ولو كان يدل عليه (من جهة) اللفظ لتكرر. كما لو قال: كلما.
واعلم أن هذا من باب تعليق الإنشاء على الشرط والكلام في تعليق الأمر، فينبغي أن يقال: وإذا ثبت في هذا ثبت في ذاك','3','205',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (755,'بالقياس.
أو يمثل بقوله لموكله: طلق زوجتي إن دخلت الدار.
نعم إن كان تعليق الخبر والإنشاء، كتعليق الأمر في ثبوت الخلاف حصل المقصود.
وكلام الإحكام يقتضي أن الإنشاء لا يتكرر اتفاقًا وصرح به في الخبر.
وأما الثاني: وهو أن هذا الأمر يقتضي التكرار قياسًا؛ فلأن الترتيب أي: ترتيب الحكم على الصفة أو الشرط يفيد العلية كما سيجيء إن شاء الله تعالى.','3','206',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (756,'فيتكرر الحكم بتكررها؛ لأن المعلول يتكرر بتكرر علته.
واعترض عليه تاج الدين ابن السبكي بأنهم إنما ذكروا في القياس ترتيب الحكم على الوصف، لا على الشرط.
قال: ولم أر من صرح بمساواته له.
واختار ما ارتضاه القاضي أبو بكر، وهو أن المعلق بشرط لا يقتضي التكرار دون المعلق بصفة.
وإنما لم يتكرر الطلاق في مثل قوله: إن دخلت الدار فأنت طالق، بتكرر دخول الدار، لعدم اعتبار تعليله، أي: تعليل المكلف في أحكام الله تعالى؛ لأن من نصب علة لحكم، فإنما يتكرر حكمه','3','207',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (757,'بتكرر علته لا حكم غيره.
ولهذا لو قال: أعتقت غانمًا لسواده، لا يلزم عتق عبيده السود.
إلا أنه وقع به الطلاق مرة لئلا يكون كلام المكلف لغوًا.','3','208',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (758,'السادسة:
الأمر المجرد','3','209',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (759,'عن القرائن، لا يفيد الفور، خلافًا للحنفية ولا التراخي، خلافًا لقوم، بل يدل على مطلق الفعل، وأيهما حصل','3','210',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (760,'أجزأ.
وقيل: مشترك بين الفور والتراخي.
لنا: ما تقدم في الكلام على أن الأمر المطلق لا يقتضي التكرار.','3','211',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (761,'فيقال هنا: إن الأمر المطلق لو دل على الفور بعينه (أو على التراخي بعينه) لكان تقييده بأحدهما تكرارًا أو نقضًا.
فإذا قلت: افعل غدًا وهو يدل على الفور كان نقضًا، أو الآن كان تكرارًا، لكنه يصح من غير تكرار ولا نقض فيكون لمطلق الطلب.
وأيضًا: ورد الأمر تارة للفور، كالواجب المضيق، وتارة للتراخي كالحج، فلو كان حقيقة فيهما كان مشتركًا. أو في أحدهما كان مجازًا.
فلا بد أن يجعل للقدر المشترك دفعًا للاشتراك والمجاز. وتقدم دليل لا يأتي هنا.
قيل: إنه تعالى ذم إبليس على الترك، أي: ترك المبادرة بالسجدة لآدم - صلى الله وسلم عليه - ولو لم يقتض ذلك','3','212',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (762,'الفور، لما استحق الذم، لإمكان السجود وعدم تعين المبادرة.
قلنا: لعل هناك قرينة عينت الفورية (أي: يحتمل أن يكون ذلك مقرونًا) بما يدل على أنه للفور.
فإن قلت: الأصل عدم القرينة؟
أجيب: بأن الآية فيها قرينتان دالتان على الفور:
الفاء في قوله تعالى: } فقعوا له ساجدين {فإنها للتعقيب بلا مهلة، فالفورية مستفادة من الفاء لا من الأمر.
وفعل الأمر وهو قوله تعالى: } فقعوا {.
عامل في إذا، لأنها ظرف، والعامل فيها جوابًا على رأي البصريين.
فصار التقدير فقعوا له ساجدين وقت تسويتي إياه.','3','213',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (763,'فيكون زمان السجود بعينه زمان التسوية، فالفورية مستفادة منه، لا من الأمر.
وقال العراقي: نص النحاة على أن الفاء الواقعة في جواب الشرط لا تقتضي تعقيبًا. وهي لا تقتضي الفورية، إلا إذا كانت للتعقيب.
قيل: قوله تعالى: } وسارعوا {يوجب الفور، والمسارعة: التعجيل، وهي إلى نفس المغفرة غير ممكن؛ لأنها فعل الله تعالى، بل المراد سبب المغفرة على المجاز، من باب إطلاق اسم المسبب على السبب، فتكون المسارعة واجبة، ولا معنى للفور إلا ذلك.','3','214',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (764,'قلنا: فمنه أي: الفور مستفاد من قوله تعالى: } وسارعوا {لا من لفظ الأمر.
يعني أن حصول الفورية ليس من صيغة الأمر، بل من جوهر اللفظ؛ لأن لفظ المسارعة دال عليه كيفما تصرف.
أو ثبوت الفورية في المأمورات ليس مستفادًا من مجرد الأمر بهأ، بل من دليل منفصل وهو قوله تعالى: } وسارعوا {.
قيل: لو جاز التأخير.
وقلنا: إن الأمر ليس للفور - فإما أن يكون مع بدل،','3','215',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (765,'فإذا أتى بالبدل فيسقط عنه التكليف بالمأمور به، لأن البدل هو الذي يقوم مقام البدل مطلقًا، لكنه لا يسقط اتفاقًا.
ولا يقال: لم لا يجوز أن يقوم البدل مقام المبدل في ذلك الوقت لا مطلقًا؟ لأنه: إنما يتأتي على القول بأن الأمر يفيد التكرار، وقد أبطلناه. أو لا يكون التأخير معه، أي مع بدل.
فلا يكون واجبًا؛ لأنه لا معنى لغير الواجب إلا ما جاز تركه بلا بدل.
وأيضًا: لو جاز التأخير فحينئذ إما أن يكون للتأخير أمد، فلا بد أن يكون معينًا.
لأن القائلين به اتفقوا على أن ذلك الأمد المعين هو ظن الفوات على','3','216',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (766,'تقدير الترك.
وإليه أشار بقوله: وهو إذا ظن فواته "وهو غير شامل لأن كثيرًا من الشباب يموتون فجأة، ويقتلون غيلة فيقتضي ذلك عدم الوجوب عليهم في نفس الأمر؛ لأنه لو كان واجبًا لامتنع تركه"
والغرض أنا جوزنا له الترك في كل الأزمان المتقدمة على ذلك الظن، وهو باطل أو لا يكون للتأخير أمد، فلا يكون واجبًا، لأن تجويز التأخير أبدًا ينافي الوجوب.
قلنا: منقوض بما إذا صرح به أي: بالآمر بجواز التأخير، فقال: أوجبت عليك أن تفعل كذا في أي وقت شئت، فلو صح ما','3','217',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (767,'ذكرتم في الدليلين، لزم امتناع التأخير في هذه.
إذ دليلاكم جاريان فيه بعينه فما هو جوابكم هو جوابنا.
قيل: النهي يفيد الفور، فإنه يفيد الانتهاء عن المنهي عنه في الحال، فكذا الأمر يوجب الامتثال في الحال قياسًا عليه والجامع الطلب.
قلنا: لأنه أي النهي يفيد التكرار في جميع الأوقات ومن جملتها زمان الحال فلزم بالضرورة الفور بخلاف الأمر فافترقا.
وقد يقال: النهي يقتضي انتفاء الحقيقة، وهو بانتفائه في جميع الأوقات، والأمر يقتضي إثباتها وهو يحصل بمرة.
ويمكن حمل كلام المصنف عليه، فلا ينافي ما بعد هذا من أن النهي لا يقتضي التكرار.','3','218',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (768,'واعلم أن الخلاف بين القائلين بأن الأمر لا يقتضي التكرار.
أما من قال بأنه يقتضي التكرار، فمن ضرورته الفورية.','3','219',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (769,'<span data-type="title" id=toc-103>الفصل الثالث: في النواهي</span>
وفيه مسائل:
الأولى:
النهي يقتضي التحريم لقوله تعالى: } وما نهاكم عنه فانتهوا {.
اعلم أن لفظ النهي موضوع: لقول طالب الترك، أو الكف، على','3','221',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (770,'اختلاف فيه.
وترد صيغة النهي: وهي لا تفعل، لمعان: للتحريم، والكراهة، والإرشاد، والدعاء، وبيان العاقبة التقليل والاحتقار، واليأس، والخبر، والتهديد، وإباحة الترك.','3','222',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (771,'والالتماس، وأمثلتها في الشرح الذي هو أصل هذا المختصر.
وهل يشترط العلو والاستغلاء، وإرادة الترك أم لا؟ وله صيغة أم لا؟
وهل هو حقيقة في الطلب وحده، وأن ذلك الطلب الذي هو حقيقة فيه هل هو التحريم أو الكراهة أو كل منهما بالاشتراك أو الوقف.
كما اختلفوا في الأمر؟
فعلى هذا إذا ورد النهي مجردًا عن القرائن مقتضاه التحريم، كما جزم','3','223',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (772,'به المصنف، ونص عليه الشافعي - رضي الله تعالى عنه - للآية التي استدل بها المصنف.
فالانتهاء المأمور به واجب، ففعله يكون حرامًا ولا يعني بالتحريم إلا هذا.','3','224',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (773,'وهو كالأمر في التكرار والفور:
يعني أن حكمه حكم الأمر في أنه لا يدل على التكرار، ولا على الفور، واختاره الإمام.
وصحح الآمدي وابن الحاجب أنه للتكرار والفور.
وفي بعض نسخ المنهاج: إلا في التكرار والفور، فيكون موافقًا لابن الحاجب، وشاملاً لما تقدم.','3','226',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (774,'وبه يشعر قوله فيما تقدم.
قلنا: لأنه يفيد التكرار.
وقال ابن برهان: إنه مجمع عليه.
وفي المحصول: إنه المشهور، وجزم به الشيخ أبو إسحاق.','3','227',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (775,'قال العراقي: ولعل مراد المصنف، يعني بقوله: وهو الأمر، تشبيه النهي بالأمر في وجود الخلاف، لا في الترجيح.
الثانية:
النهي المطلق يدل شرعًا على الفساد في العبادات سوءا نهي عنها لعينها، أو لأمر قارنها، لأن النهي بعينه لا يكون مأمورًا به.
يعني أن الشيء الواحد يستحيل أن يكون مأمورًا به ومنهيًا عنه، وحينئذ لا يكون الآتي بالفعل المنهي عنه آتيًا بالمأمور به، فيبقى الأمر متعلقًا به.
ويكون الذي أتى به غير مجزئ، وهو المراد من دعوى الفساد.','3','228',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (776,'أو يقال: الآتي بالفعل المنهي عنه (لا يكون) آتيا بالمأمور به؛ لأن النهي: يطلب الترك، والأمر يطب الفعل وهو جمع بين النقيضين.
وكون العبادة لها جهتان: إن كانا متفارقين فهما شيئان مفترقان، فليس مما نحن فيه.
أو متلازمين فالمحذور باق.
والنهي يدل شرعًا على الفساد في المعاملات إذا رجع إلى نفس','3','229',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (777,'العقد.
أو رجع النهي إلى أمر داخل فيه أي: في العقد.
أو رجع إلى أمر لازم له أي: للعقد.
مثل للأول بقوله: كبيع الحصاة: فإن النبي - صلى الله عليه وسلم -: "نهى عنه" كما رواه مسلم.
وهو جعل الإصابة بالحصى بيعًا قائمًا مقام الصيغة، وهو أحد التأويلين في الحديث.','3','230',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (778,'ومثل للثاني بقوله: والملاقيح، فإن النبي - صلى الله عليه وسلم - نهى عن بيع الملاقيح، كما رواه البزار في مسنده.
وهو: بيع ما في بطون الأمات.
فالنهي راجع إلى نفس المبيع، والمبيع ركن من أركان العقد، والركن داخل في الماهية.
ومثل للثالث بقوله: "والربا، فإن النبي - صلى الله عليه وسلم -','3','231',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (779,'نهى عن الربا.
فأما ربا النسيئة، والتفرق قبل التقابض فواضح كون النهي فيه لمعنى خارج.
وأما ربا الفضل فلأن النهي عن بيع الدرهم بالدرهمين مثلاً إنما هو لأجل الزيادة، وذلك أمر خارج عن نفس العقد؛ لأن المعقود عليه من حيث هو قابل للبيع، وكونه زائدًا أو ناقصًا صفة من أوصافه لكنه لازم، واقتضى الفساد؛ لأن الأولين تمسكوا على فساد الربا بمجرد','3','232',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (780,'النهي من غير نكير وشاع وذاع فكان إجماعًا.
واقتصر على الثالث؛ لأنه بثبوته فيه يثبت في غيره من باب أولى.
وإن رجع النهي إلى أمر خارج مقارن، كالبيع في وقت النداء يوم الجمعة، فإن النهي فيه راجع إلى أمر خارج عن العقد، وهو تفويت صلاة الجمعة لا بخصوص البيع، إذ الأعمال كلها كذلك لتفويت أمر مقارن غير لازم لماهية البيع فلا يدل على الفساد.
وما جزم به المصنف من هذا التفصيل، نقل عن الأكثرين.
وحكاه ابن برهان عن نص الشافعي - رضي الله عنه.','3','233',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (781,'وقيل: لا يدل على الفساد مطلقًا، وحكاه الآمدي عن المحققين.
وقول المصنف: "شرعا" إشارة إلى أن النهي إنما يقتضي الفساد من جهة الشرع لا اللغة، وهو تابع في ذلك للآمدي، وابن الحاجب.
لأنه لم يخطر ببال واضع اللغة، فكيف يدل النهي عليه لغة.
وقيل: يدل على الفساد لغة.','3','234',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (782,'وقيل: من حيث المعنى، لا من حيث اللفظ.
وقوله: "النهي" ولم يقيده بشيء، إشارة إلى أن النهي إذا اقترن به ما يدل على الفساد أو الصحة، فلا شك في اعتباره، وليس من محل الخلاف.
وإطلاقه النهي يشمل التحريم والتنزيه ورجحه بعض المتأخرين.
وقال الصفي الهندي: محل الخلاف في نهي التحريم.
وأما التنزيه فلا خلاف فيه، أي: لا يدل على الفساد على ما يشعر به كلامهم، وصرح بذلك بعضهم.
وذكر ابن الصلاح، والنووي: أن الصلاة في الأوقات المكروهة','3','235',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (783,'لا تنعقد، وإن قلنا: إن الكراهة فيها للتنزيه.','3','236',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (784,'الثالثة:
مقتضى النهي فعل الضد، أي: كف النفس عن المنهي عنه، فإذا قال: لا تتحرك فمعناه اسكن؛ لأن متعلق النهي مكلف به، وكل مكلف به مقدور عليه والترك غير مقدور، فلا يكون الترك مقتضى النهي، فيكون مقتضى النهي فعل الضد وهو المطلوب.
وإليه أشار بقوله: لأن العدم غير مقدور.','3','239',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (785,'واعترض السبكي: على هذه العبارة بأن النهي قسيم الأمر، والأمر طلب الفعل، فلو كان النهي طلب فعل الضد، لكان أمرًا ولكان النهي من الأمر.
وقسيم الشيء لا يكون قسمًا منه.
قال: فالعبارة المحررة أن يقال: المطلوب بالنهي.
الانتهاء، ويلزم من الانتهاء فعل ضد المنهي عنه.
قال العراقي: والفرق بين هذه المسألة ومسألة النهي عن الشيء أمر بضده.','3','240',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (786,'أن البحث في تلك لفظي، وفي هذه معنوي.
وأن البحث في تلك في المتعلقات - بكسر اللام - وفي هذه - بفتح اللام.
وأن البحث في تلك في دلالة الالتزام، على ضد المنهي عنه، وفي هذه (في) دلالة المطابقة هل مدلولها العدم أو ضده، ذكرهما القرافي، وهما حسنان.','3','241',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (787,'وأن المراد هناك الضد الخاص، وهنا العام.
وأن النهي عن الزنا مثلاً فيه ثلاثة أمور:
انتفاء الزنا، والكف عنه، وفعل الضد.
والكلام هنا في الأمرين الأولين، وهناك في الأمر الثالث.
وقال أبو هاشم: المطلوب بالنهي ترك الفعل؛ لأن من دعي إلى زنا فلم يفعل مدح عقلا، على أنه لم يزن، من غير أن يخطر','3','242',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (788,'ببالهم فعل ضد الزنا.
قلنا: المدح على الكف، وهو فعل الضد، وليس المدح على الترك؛ لأنه عدم ولا مدح على العدم؛ لأنه ليس في وسعه.
الرابعة:
النهي إما أن يكون عن شيء واحد وهو ظاهر.
وإما أن يكون عن أشياء.
والنهي عن الأشياء.','3','243',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (789,'إما عن الجمع (بينهما، كنكاح الأختين فإن كل واحد مباح، والجمع بينهما منهي عنه حرام.
أو عن الجميع) أي: كل واحد منهي عنه، كالربا والسرقة.
* * *','3','244',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (790,'<span data-type="title" id=toc-108>الباب الثالث: في العموم والخصوص</span>
وفيه فصول:

<span data-type="title" id=toc-109>الفصل الأول: في العموم</span>
العموم من عوارض الألفاظ حقيقة.
فإذا قيل: هذا اللفظ عام صدق حقيقة.
وأما في المعنى: فإذا قيل: هذا المعنى عام فهل هو حقيقة:','3','245',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (791,'فيه مذاهب:
والمختار عند ابن الحاجب: أنه يصدق حقيقة كما في الألفاظ.
والعام: لفظ يستغرق جميع ما يصلح له بوضع واحد.
فقوله: لفظ، كالجنس.
مع الإشعار بأن العموم من عوارض الألفاظ.
لكن سيأتي له جواز تخصيص العلة والمفهوم وغيرهما.
والتخصيص فرع العموم.
وأجيب: بأنه يجوز أن يكون إطلاق العموم هناك على سبيل المجاز - كما رآه الجمهور - وكلامه هنا في المدلول الحقيقي.','3','246',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (792,'والعموم هناك بحسب اللغة، وهنا بحسب الاصطلاح.
وقوله: "يستغرق"، خرج به المطلق، فإنه لا يدل على شيء من الأفراد -
كما سيجيء - فضلاً عن استغراقها.
وعن النكرة في الإثبات مفردًا كان أو مثنى أو مجموعًا.
فإنه يتناول كل واحد بدلاً لا استغراقًا.
وعن الأعلام والضمائر وأسماء العدد نحو العشرة، فإنه لا يتناول جميع العشرات إلا بدلاً.
وقوله: جميع ما يصلح له احتراز عما لا يصلح.','3','247',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (793,'فإن عدم استغراق من، لمن لا يعقل، وأولاد زيد لأولاد عمرو، لا يمنع كونه عامًا لعدم صلاحيته له.
والمراد بالصلاحية أن يصدق عليه حقيقة.
وقوله: "بوضع واحد" متعلق بيصلح، والباء فيه للسببية؛ لأن صلاحية اللفظ لمعنى دون معنى سببها الوضع، لا المناسبة الطبيعية، أو حالاً من "ما"، أي: جميع المعاني الصالحة له في حال كونها حاصلة بوضع واحد.
واحترز به: عن خروج المشترك؛ إذ لولا هذا القيد، لما صدق الحد على لفظ العين المتناول لجميع أفراد الباصرة، مع أنه عام وللزم في عمومه استغراقه لجميع أفراد معانيه المتعددة.
وهذا معنى قول الإمام الرازي: إن قولنا: بوضع واحد، احتراز عن المشترك والذي له حقيقة ومجاز، فإن عمومه لا يقتضي أن يتناول','3','248',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (794,'مفهوميه معًا.
بيانه: أن العين قد وضعت مرتين: مرة للمبصرة ومرة للفوارة، مثلاً فهي صالحة لهما.
فإذا قال: رأيت العيون، وأراد بها العيون المبصرة دون الفوارة، أو بالعكس، فإنها لم تستغرق جميع ما يصلح لها مع أنها عامة.
لأن الشرط إنما هو استغراق الأفراد الحاصلة من وضع واحد وقد وجد، والذي لم يدخل فيها هو أفراد وضع آخر، فلا يضر.
(فلو لم) يذكر هذا القيد، لاقتضى أن لا تكون عامة، وما كان له حقيقة ومجاز يعمل فيه هذا العمل.
فيكون المقصود بهذا القيد، إدخال بعض الأفراد لا الإخراج.
ويجوز أنه احترز: بوضع واحد، عن استعمال اللفظ في حقيقته كالعين، وفي حقيقته ومجازه كالأسد، فإنه يصدق أنه لفظ مستغرق','3','249',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (795,'لجميع ما يصلح له، وليس بعام، فخرج بوضع واحد.
وهنا اعتراضات وأجوبتها في الأصل.','3','250',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (796,'وفيه مسائل:
الأولى:
أن لكل شيء حقيقة هو بها هو، مثلاً: الجسم الإنساني حقيقة ذلك الجسم، فتلك الحقيقة إنسان، فالدال، أي اللفظ الدال عليها، أي: على تلك الحقيقة، من حيث هي، أي من غير اعتبار شيء من المفهومات، كالوحدة والكثرة معه، وإن لم تخل عنه في الواقع، تسمى المطلق، كقولنا: الرجل خير من المرأة، أي: حقيقة الذكر من بني آدم، من غير التفات إلى شيء من الأفراد، خير من حقيقة الأنثى من بني آدم.
والفرق بين الحقيقة والماهية.','3','251',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (797,'أن الحقيقة ماهية بقيد الوجود في الخارج، والماهية أعم، والدال عليها بوحدة أي: بقيد الوحدة شخصًا أو نوعًا أو جنسًا.
فإن كانت معينة: فهي المعرفة، كزيد والإنسان والحيوان، وإن كانت غير معينة: فهي النكرة كرجل.
والدال على الحقيقة مع وحدات متعددة، أي: محصورة لا يتناول ما بعدها العدد كعشرة.
وإن كانت غير محصورة: بل مستوعبة لكل فرد من أفراد تلك الحقيقة (فهي العام).
وإليه أشار بقوله: ومع كل جزئياتها العام كالمشركين.
وهذا تقسيم اعتباري، كما سبق في تقسيم الألفاظ.
فإن العام والعدد قد يكونان معرفتين كالرجال والخمسة، وقد','3','252',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (798,'يكونان نكرتين، كرجل وخمسة في قولك: لا رجل.
الثانية
للعموم صيغة موضوعة حقيقة، كما ذهب إليه الشافعي - رضي الله عنه - والمحققون.
ولا يتصور نزاع في إمكان التعبير عن العموم بعبارة مثل: كل رجل.
ووقع النزاع في الصيغ المخصوصة التي يدعى عمومها:
هل هي للعموم أم لا؟','3','253',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (799,'فقال الأكثر: له صيغة هي حقيقة فيها.
ثم العموم إما لغة بنفسه، أي: إما مستفاد من اللغة، وهو الأكثر.
وهو على قسمين:
الأول: الدال على العموم بنفسه من غير انضمام قرينة.
وهو إما عام في ذوي العلم وغيرهم، فأي للكل، فإنها عامة فيما يضاف إليه من الأشخاص والأزمان والأمكنة والأحوال، ولا بد من تقييدها، بالاستفهامية والشرطية والموصولة؛ ليخرج الصفة: كمررت برجل أي رجل، بمعنى كامل، والحال: نحو مررت بزيد أي رجل - بفتح أي - بمعنى كامل أيضًا، أو مناديًا: نحو: يا أيها الرجل، فإنها لا تفيد العموم.','3','254',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (800,'ومثل أي العامة: كل وجميع، والذي والتي.
و"كل" أقوى صيغ العموم.
وإما في البعض وإليه أشار بقوله: "ومن" أي الشرطية أو الاستفهامية للعالمين - بكسر اللام - أي "من" عام في ذوي العلم، وهو متناول للباري تعالى كقوله تعالى: "ومن لستم له برازقين {.','3','255',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (801,'و"ما" عام لغيرهم، أي في غير ذوي العلم نحو: اشتر ما رأيت إلا أن تكون نكرة موصوفة نحو: مررت بما معجب لك، أي شيء، أو تعجبيه نحو: ما أحسن زيدًا، فلا يعم.
"وأين" عام للمكان خاصة نحو: أين تجلس أجلس.
"ومتى" عام للزمان نحو متى تجلس أجلس.
وقيد ابن الحاجب: ذلك بالزمان المبهم.
قال الإسنوي: ولم أر هذا الشرط في الكتب المعتمدة.
القسم الثاني:
الدال على العموم بقرينة، وإليه أشار بقوله: أو بقرينة، وتلك','3','256',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (802,'القرينة إما في الإثبات: كالجمع المحلى بالألف واللام، والمضاف بالألف واللام، والإضافة في الجمع، هما قرينة العموم فيهما، وسواء فيه جمع السلامة والتكسير، هذا قول الأكثرين.
وقيل: لا يفيد الجمع المحلى باللام العموم، بل الجنس مطلقًا.
ومحل الخلاف: إذا لم يكن هناك عهد.
فإن كان انصرف إلى المعهود، ولا يعم.
وقول سيبويه وغيره: جمع السلامة للقلة، وهي من الثلاثة إلى العشرة. لا ينافي قول المصنف: إن الجمع المحلى باللام للعموم.
لأن قول سيبويه محمول على النكرة.','3','257',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (803,'وكذا اسم الجنس، أي: وكذا تحصيل العموم بقرينة الإثبات في اسم الجنس كالجمع، وهي أل والإضافة كقوله تعالى: } ولا تقربوا الزنا {وقوله تعالى: } فليحذر الذين يخالفون عن أمره {.
وشرط تعميمه أن لا يتحقق عهد فإن كان هناك انصرف إليه قطعًا.
ولو عبر بالمفرد لكان أحسن، لشموله الاسم الذي ليس بجنس، وهو ما يتغير لفظه، عند تكثير مدلوله نحو دينار.
وكون اسم الجنس المفرد يفيد العموم إذا دخل عليه الألف واللام هو الذي نص عليه الشافعي - رضي الله عنه - ونقله الآمدي عنه وعن','3','258',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (804,'الأكثرين وهو قول أبي إسحاق والمبرد وصححه ابن الحاجب.
وصحح الإمام، وأتباعه أنه لا يعم.
قال العراقي: وعموم المفرد غير عموم الجمع فالأول يعم المفردات، والثاني يعم المجموع؛ لأن أل قد دخلت على جمع، وهي تعم أفراد ما دخلت عليه.
وفائدة هذا: تعذر الاستدلال بالجمع على مفرد في حالة النفي أو النهي لأنهما وردا على أفراد المجموع.
والواحد ليس بجمع كذا قال.
فإن قلت: إذا حلف بالطلاق وحنث لا يقع عليه غير واحدة وكان مقتضى العموم وقوع الثلاث.
أجاب الشيخ (عز الدين) بن عبد السلام بأن هذه يمين','3','259',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (805,'ويراعي فيها العرف لا موضوع اللغة.
وأجاب السبكي: بأن الطلاق حقيقة واحدة وهي قطع عصمة النكاح، وليس له أفراد حتى يقال: إنما يندرج في العموم، ولكن مراتبه مختلفة.
فإذا لم يذكر الثلاثة ولا نواها لم يحمل إلا على أقل المراتب، ثم بسطه.
وهنا أبحاث حسنة في الأصل.','3','260',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (806,'أو لقرينة في النفي، هو معطوف على قوله: في الإثبات، أي يفيد العموم بقرينة في النفي كالنكرة في سياقه، أي سياق النفي، والمراد النكرة المعنوية ليدخل المطلق.
وليس المراد النكرة الصناعية القابلة للمعرفة سواء باشرها النفي نحو: ما أحد قائم.
أو باشر عاملها نحو ما قام أحد.
وسواء كان النافي "ما" أو "لم" أو غيرهما.
ثم إن كانت النكرة صادقة على القليل والكثير "كشيء" أو ملازمة للنفي نحو "أحد" أو داخلاً عليها من نحو: ما من رجل، أو واقعة بعد "لا" العاملة عمل إن، وهي لا التي لنفي الجنس.','3','261',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (807,'فواضح كونها للعموم، وما عدا ذلك نحو: لا رجل قائمًا، وما في الدار رجل.
فالأصح: أنها ظاهرة في العموم لا نصًا.
واستثنى صاحب التلقيحات سلب الحكم عن العموم، كقولنا ما كل عدد زوجًا.
فإن هذا ليس من باب عموم السلب، أي: ليس حكمًا بالسلب على كل فرد، وإلا لم يكن فيه زوج، وذلك باطل.','3','262',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (808,'بل المقصود إبطال قول من قال: كل عدد زوج، وذلك سلب الحكم عن العموم.
وقوله: أو عرفًا، عطف على لغة، مثل قوله تعالى: } حرمت عليكم أمهاتكم {.
فإن أهل العرف حولوا هذا التحريم، وهو تحريم العين إلى تحريم أنواع الاستمتاعات؛ لأن الاستمتاع يفهم عرفًا إذا قيل: "حرمت عليك هذه المرأة" دون تحريم العين، أو الخدمة.
أو يفيد العموم عقلاً، وهو عطف على عرفًا، كترتيب الحكم','3','263',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (809,'على الوصف المناسب، نحو: حرمت الخمر للإسكار، فإن ترتيبه عليه يشعر بالعلية، والعقل يحكم؛ لأنه كلما وجدت العلة، وجد المعلول، وكلما انتفت انتفى.
وأما في الغلة فإنه لم يدل على هذا العموم.
أما في المفهوم فواضح، وأما في المنطوق فلما مر أن تعليق الشيء بالوصف لا يدل على التكرار من جهة اللفظ.
ومعيار العموم جواز الاستثناء.
أي: يعرف العموم به، فإنه أي الاستثناء يخرج ما يجب اندراجه','3','264',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (810,'لولاه، أي: لولا الاستثناء.
فلزم من ذلك دخول جميع الأفراد في المستثنى منه.
وإلا أي: لو لم يجب دخوله فيه لجاز أن يستثنى من الجمع المنكر، لكن الاستثناء منه لا يجوز باتفاق النحاة.
قالوا: إلا أن يكون المستثنى منه مختصًا، نحو: جاء رجال كانوا في دارك إلا زيدًا منهم.
فإن قلت: لو كان الاستثناء معيارًا للعموم، لكان أسماء العدد عامًا لجواز الاستثناء منها، وليس كذلك.
أجيب: بأن جواز الاستثناء معيار العموم إذا كان استثناء بعض ما يصلح اللفظ له عن بعض أفراده والعدد ليس كذلك، فإن بعض العشرة لا تصلح العشرة له.','3','265',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (811,'قيل: لو وجب أن تناول المستثنى منه المستثنى، لامتنع الاستثناء لكونه نقضًا؛ لأن المتكلم دل بأول كلامه على أن المستثنى داخل فيه، ودل بالاستثناء على عدم دخوله.
قلنا: منقوض بالاستثناء من العدد، فلو كان الاستثناء عن لفظ العام مع وجوب تناوله للمستثنى نقضًا، لكان الاستثناء عن العدد نقضًا، لوجوب التناول في العدد، لكونه نصًا في وجوب اندراج جميع الآحاد فيه، لكنه ليس بنقض، وإلا لم يوجد في كلام الله تعالى، وقد وجد فيه.
وتحقيقه كما قال الأكثرون: إن المراد بعشرة ونحوها في قوله: على عشرة إلا ثلاثة، إنما هو سبعة، وإلا ثلاثة، قرينة لإرادة السبعة من العشرة، إرادة الجزء باسم الكل كما في التخصيص بغيره حيث','3','266',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (812,'يقول: } اقتلوا المشركين {والمراد الحربيون بدليل يخرج الذمي.
وقال القاضي أبو بكر: المجموع، وهو عشرة إلا ثلاثة بإزاء سبعة كأنه وضع له اسمان مفرد وهو سبعة، ومركب وهو عشرة إلا ثلاثة".
وقيل: المراد بعشرة في هذا التركيب هو معنى عشرة باعتبار أفراده لم يعتبر فهو يتناول السبعة والثلاثة معًا، ثم أخرجت عنه الثلاثة بقوله: إلا ثلاثة، فدل "إلا" على الإخراج، وثلاثة على العدد المسمى بها حتى بقي سبعة.
ثم أسند إليه فلم يسند إلا إلى سبعة، فلا ثم
إلا إثبات ولا نفي أصلاً فلم يتناقض.','3','267',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (813,'قال ابن الحاجب: وهذا هو الصحيح. وله زيادة تحقيق في الأصل يتعين نظرها.
وأيضًا عطف على، ومعيار العموم، فيكون دليلاً ثانيًا على أن هذه الصيغ للعموم، فإن الأول: دليل على ذلك، وهو استدلال الصحابة (رضي الله عنهم) بعموم ذلك، أي: استدلوا بعموم الصيغ في الوقائع من غير إنكار عليهم، وشاع وذاع فيكون إجماعًا.
فمنها استدلالهم بعموم الجنس المحلى بأل مثل قوله تعالى: } الزانية والزاني فاجلدوا {.','3','268',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (814,'ومنها استدلال فاطمة على أبي بكر (رضي الله عنهما)
(في الإرث بقوله تعالى: } يوصيكم الله في أولادكم {ولم ينكر عليها أبو بكر) ولا أحد من الصحابة بل عدل أبو بكر إلى تخصيصه بالحديث الآتي، فيكون إجماعًا منهم على أن الجمع المضاف، وإن كان جمع فإنه عام.
وقيل: الاستدلال إنما هو بقرينة العقل وهو واضح في الأصل.
ومنها: استدلال عمر (رضي الله عنه) بقوله - صلى الله عليه','3','269',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (815,'وسلم: "أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله" متفق عليه.
وذلك لما هم أبو بكر (رضي الله عنه) بقتال مانعي الزكاة، فاحتج عليه بعموم اسم الجنس المحلى بالألف واللام، ولم ينكر عليه أحد من الصحابة، ولا أبو بكر (رضي الله عنهم)، لأنهم ما قالوا: إن اللفظ ما يفيده، بل استدل أبو بكر - (رضي الله عنه) - بالاستثناء المذكور في الحديث، وهو قوله عليه السلام: "إلا بحقه" أي: بحق هذا القول الذي هو كلمة الشهادة.','3','270',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (816,'وهنا تدقيق وفوائد تتعلق بالحديث في الأصل.
ومنها: احتجاج أبي بكر - رضي الله عنه - بما روي عنه - صلى الله عليه وسلم - أنه قال: "الأئمة من قريش" رواه النسائي مرفوعًا، وفيه بكير بن وهب.','3','271',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (817,'قال الذهبي: تبعًا ليحيي القطان: لا يعرف حاله، لكن وثقه ابن حبان: وله طرق يقوي بعضه بعضًا، وقرره الصحابة، ولولا أن الصيغة للعموم لما كان فيه حجة في الصورة الجزئية.
ومنها: استدلال أبي بكر - رضي الله عنه - على منع فاطمة - رضي الله عنها - من إرثه - صلى الله عليه وسلم - بقوله - صلى الله عليه وسلم -: "نحن معاشر الأنبياء لا نورث، ما تركناه صدقة"','3','272',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (818,'وهذا الحديث معزو إلى الترمذي في غير جامعه، وروى النسائي في سننه الكبرى: "إنا معاشر الأنبياء لا نورث" رواه الترمذي وقال حسن غريب.
والنبي اسم جنس يعم كل الأنبياء، وغير ذلك مما لا يحصى كثيره شائعًا من غير نكير.','3','273',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (819,'وتجويز كون العموم في هذه الأشياء يحتمل أن يكون لقرينة لا تنافي الظهور مع أن الأصل عدمها.','3','274',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (820,'الثالثة','3','275',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (821,'الجمع المنكر إذا لم يكن مضافًا نحو: رجال لا يقتضي العموم.
لأنه يحتمل كل أنواع العدد، بدليل صحة تقسيمه إليه، وتفسير الإقرار به وإطلاقه عليه ووصفه به.
كرجالٍ ثلاثة وعشرة.
ومورد التقسيم، وهو الجمع، أعم من أقسامه، فيكون الجمع أعم وكل فرد أخص.
ومن جملة أقسامه: الجمع المستغرق، والأعم لا يدل على الأخص ولا يستلزمه فلا يدل عليه، بخصوصه فلا يحمل عليه.
وقوله: في كل أنواع العدد، أي من الثلاثة فصاعدًا وإلا فيرد','3','276',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (822,'الاثنان.
وقال الجبائي: الجمع المنكر حقيقة في كل أنواع العدد بدليل صحة حمله لغة على كل عدد، بطريق الحقيقة، فيحمل على جميع حقائقه، لأنه أولى.
قلنا: لا، أي: ليس حقيقة في كل أنواع العدد، بل حقيقة في القدر المشترك.
بيانه: أنك إن أردت الاشتراك اللفظي فممنوع، ولا يلزم','3','277',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (823,'من إطلاقه على كل نوع أن يكون حقيقة فيه بخصوصه حتى يكون مشتركًا لفظيًا.
وإن أردت أنه مشترك اشتراكًا معنويًا فمسلم، لكن لا يدل على النوع المستغرق، لكونه أعم منه.
فلا دلالة له على خصوص أصلاً، كما، قدمته.
الرابعة
نفي المساواة في قوله تعالى: } لا يستوى أصحاب النار وأصحاب','3','278',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (824,'الجنة {يحتمل نفي الاستواء من كل وجه، ومن بعضه، فهو مشترك، بدليل صحة تقسيمه إليهما، فلا يبقى الاستواء من كل وجه؛ لأن الأعم لا يستلزم الأخص، (و) لأن الأعم لا إشعار له بالأخص بوجه من الوجوه، فلا يلزم من نفيه نفيه.
وهذا الدليل ضعيف؛ لأن الأعم لا يدل على الأخص في طرف الإثبات، لا في طرف النفي، فإن نفي الأعم يستلزم نفي الأخص ضرورة.','3','279',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (825,'فإن الإنسان ينتفي بانتفاء الحيوان قطعًا، ولولا ذلك لجاء مثله في كل نفي فلا يصح أبدًا؛ إذ يقال في: لا رجل، أعم من الرجل، بصفة العموم، فلا يشعر به.
وهو خلاف ما ثبت بالدليل.
وما اختاره المصنف من أن نفي المساواة لا يقتضي العموم، تابع فيه الإمام الرازي. ومذهب الشافعي - رضي الله عنه - أنه يقتضي العموم أي: يدل على عدم جميع وجوه المساواة.
وصححه ابن برهان، والآمدي وابن الحاجب.','3','280',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (826,'فلا يقتل مسلم بكافر ولو ذميًا، وهنا نفائس في الأصل.
وقوله: لا آكل عام في المأكول فيحمل على التخصيص، كما لو قيل: لا آكل أكلاً.
وفرق أبو حنيفة (رحمه الله) بأن أكلاً: يدل على التوحيد وهو ضعيف: فإنه للتوكيد فيستوى فيه الواحد والجمع.','3','281',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (827,'اعلم أنه إذا حلف على الأكل وتلفظ بشيء معين، كقوله مثلاً: والله لا آكل التمر، أو لم يتلفظ به لكن أتى بالمصدر ونوى به شيئًا معينًا، كقوله: والله لا آكل آكلاً، فلا خلاف بين الشافعي وأبي حنيفة - رضي الله عنهما - أنه لا يحنث بغيره.
فإن لم يتلفظ بالمأكول ولم يأت بالمصدر، ولكنه خصصه بنيته، كما إذا نوى التمر بقوله: والله لا أكلت، أو إن أكلت فعبدي حر.
ففي تخصيص الحنث به مذهبان منشؤهما أن هذا الكلام هل هو عام أم لا؟
فعلم أن صورة المسألة المختلف فيها: أن يكون فعلاً متعديًا لم يقيد بشيء، كما ذكره إمام الحرمين، والغزالي والآمدي وغيرهم.','3','282',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (828,'وأن يكون واقعًا بعد النفي أو الشرط، كما صوره ابن الحاجب وغيره.
وهذه الأمور مأخوذة من تمثيل المصنف.
فمذهب أبي حنيفة - رضي الله تعالى عنه - أنه ليس بعام، فلا يقبل التخصيص لأنه فرع العموم.
ومذهب الشافعي - رضي الله تعالى عنه - أنه عام لأنه نكرة في سياق النفي، أو الشرط فيعم.
ولأن لا آكل، يدل على نفي حقيقة الأكل الذي تضمنه الفعل، أعني المصدر، فلو لم ينتف بالنسبة إلى بعض المأكولات لم تكن حقيقة منتفية، ولا معنى للعموم إلا ذلك، وإذا ثبت أنه عام فيقبل التخصيص.','3','283',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (829,'واستدل المصنف على أن لا آكل عام، بالقياس على لا آكل أكلاً. فإن أبا حنيفة - رضي الله تعالى عنه - سلم أنه قابل للتخصيص بالنية كما مر.
فكذلك لا آكل إذ المصدر موجود فيه أيضًا.
وفرق أبو حنيفة رضي الله (عنه) بينهما بأن أكلاً مصدر يدل على التوحيد، فيكون كالنكرة في سياق النفي، فيفيد العموم.
ويقبل التخصيص ببعض المأكولات بخلاف لا آكل بدون أكلاً، فهو لنفي الحقيقة، وتخصيصه تفسير له بما لا يحتمله.
قال المصنف: وهو - أي: الفرق - ضعيف.
فإنا لا نسلم أن أكلاً للتوحيد، بل هو للتأكيد باتفاق النحاة.
والمصدر المؤكد يطلق على الواحد والجمع ولا يفيد فائدة زائدة على فائدة المؤكد، فلا فرق بين الأول والثاني.','3','284',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (830,'ولو سلمنا أن لا أكل ليس بعام، لكنه مطلق والمطلق يصح تقييده اتفاقًا وهنا فوائد نفيسة في الأصل.
* * *','3','285',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (831,'<span data-type="title" id=toc-114>الفصل الثاني: في الخصوص</span>
وهو الانفراد، وفيه مسائل:
الأولى
التخصيص إخراج بعض ما يتناوله اللفظ.
فقوله: إخراج كالجنس.','3','286',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (832,'وقوله: بعض، احتراز عن نسخ الكل.
وقوله: ما يتناوله اللفظ، دخل فيه العام وغيره كالاستثناء من العدد، فسيأتي أنه من المخصصات.
وكذا بدل البعض كما صرح به ابن الحاجب نحو: أكرم الناس قريشًا.
وأخرج بما يتناوله اللفظ: الاستثناء المنقطع، وعما ليس بلفظ كالمفهوم والعلة.
وأورد عليه أن ما أخرج باللفظ لم يتناوله.
وأجيب بأن المراد ما يتناوله الخطاب بتقدير عدم المخصص كقولهم: خصص العام، وهذا عام مخصوص.','3','287',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (833,'ولا شك أن المخصص ليس بعام، لكن المراد به كونه عامًا لولا تخصيصه.
ولما كان النسخ شبيهًا بالتخصيص، لكونه مخرجًا لبعض الأزمان، قال:
والفرق بينه وبين النسخ: أنه أي التخصيص يكون في البعض، أي: بعض الأفراد والنسخ عن الكل.
وفيه نظر: لأن إخراج البعض بعد العمل نسخ لا تخصيص، وفي بعض النسخ: والنسخ قد يكون عن الكل بزيادة "قد" وعلى هذا فلا إيراد.','3','288',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (834,'والأحسن في الفرق: أن النسخ بحسب الأزمان والتخصيص بحسب الأشخاص.
والمخصص - بفتح الصاد - العام المخرج عنه، والمخصص - بكسر الصاد - هو المخرج - بكسر الراء.
وهذا أي المخرج حقيقة، إرادة اللافظ لأنه لما جاز أن يريد الخطاب خاصًا وعامًا لم يترجح أحدهما على الآخر إلا بالإرادة.
ويقال: المخصص للدال عليها، أي على الإرادة مجازًا.
والدال يحتمل أن يكون صفة للشيء الدال على الإرادة، وهو دليل التخصيص لفظيًا كان أو عقليًا أو حسيًا، تسمية للدليل باسم المدلول.
ويحتمل أن يكون صفة للشخص، أي الشخص الدال على الإرادة، وهو المريد نفسه، أو المجتهد أو المقلد تسمية للمحل باسم الحال.','3','289',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (835,'وقال بعضهم: يقال بالمجاز على الدلالة على تلك الإرادة.
فائدة: العام المخصوص: هو الذي أريد عمومه وشموله لجميع الأفراد، من جهة تناول اللفظ لها، لا من جهة الحكم.
والعام الذي أريد به الخصوص لم يرد شموله لجميع الأفراد لا من جهة التناول ولا من جهة الحكم بل كلي استعمل في جزئي. ولهذا كان مجازًا قطعًا، لنقل اللفظ عن موضوعه الأصلي،','3','290',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (836,'بخلاف العام المخصوص، فإن فيه خلافًا سيجيء.
وفرق بينهما أيضًا: بأن قرينة العام المخصوص لفظية، وقرينة الذي أريد به الخصوص عقلية.
وأيضًا قرينة المخصوص: قد تنفك عنه، وقرينة الذي أريد به الخصوص لا تنفك عنه.
* * *','3','291',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (837,'الثانية
الشيء القابل للتخصيص حكم ثبت لمتعدد؛ لأن التخصيص إخراج البعض، والأمر الواحد لا يتصور فيه ذلك.
وأورد: أسماء الأعداد، والجمع المنكر.
وأجيب: بأن مدلول أسماء العدد واحد لا متعدد، فإن المتعدد في المعدود لا في اسم العدد، والجمع المنكر يقبل التخصيص إذا كان معه قرينة لفظية أو معنوية، ولا يلزم من قبوله التخصيص وقوع التخصيص حال تنكيره، وتجرده عن قرائن العموم.
وأورد القرافي على إطلاق المصنف: أن الواحد يندرج فيه الواحد بالشخص وهو يصح إخراج بعض أجزائه لصحة قولك: رأيت زيدًا، وتريد بعضه.','3','292',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (838,'ثم إن المتعدد قد يكون لفظًا، كقوله تعالى: } اقتلوا المشركين {.
أو يكون معنى، وهو ثلاثة.
الأول: العلة وجوز تخصيصها على ما سيجيء - إن شاء الله تعالى - في القياس كما في العرايا فإن الشارع نهى عن بيع الرطب','3','293',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (839,'بالتمر، وعلله بالنقصان إذا جف، وهذه العلة موجودة في العرايا، مع أن الشارع جوزه.
الثاني: مفهوم الموافقة، فيخصص بشرط بقاء الملفوظ، كقوله تعالى: } فلا تقل لها لهما أف {فإنه يدل بمنطوقه على تحريم التأفيف، وبمفهومه على تحريم الضرب، وسائر أنواع الأذى.
وخص عنه مثل (جواز حبس) الوالد في دين الولد؛ فإنه جائز','3','294',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (840,'على ما صححه الغزالي وجماعة منهم المصنف (في الغاية القصوى).
فأما إذا أخرج الملفوظ به، وهو التأفيف في مثالنا، فإنه لا يكون تخصيصًا بل نسخًا للمفهوم، وهو معنى قوله بعد ذلك: الأصل يستلزم نسخ الفحوى وبالعكس.
فإن قلت: حكمه هنا بأن إخراج الفحوى تخصيص لا نسخ للمنطوق معارض لما حكيناه عنه في النسخ.
أجيب: بأنه إن كان الإخراج لمعارض راجح كردة الأب المقتضية لقتله، ومطله المقتضي لحبسه، كان تخصيصًا لا ناسخًا للمنطوق.
لأنه لا ينافي ما دل عليه من الحرمة وهذا هو المراد هنا.
وإن لم يكن، بل ورد ابتداء كان ناسخًا لمنافاته إياه. وهذا هو المراد هناك.
قال العراقي: وأقول السؤال من أصله غير وارد، ولم يتكلم المصنف في إخراج جميع الفحوى، وإنما كلامه في إخراج بعضها، وهو حقيقة','3','295',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (841,'التخصيص، ولا يلزم من تخصيص الفحوى وهو إخراج بعض أفرادها نسخ الأصل، فإن حصل نسخ في الفحوى بأن أخرج جميع أفرادها عن الحكم كان نسخًا للأصل وهو الذي تكلم فيه المصنف هناك.
الثالث: مفهوم المخالفة، فيخصص بدليل راجح على المفهوم؛ لأنه إن كان مساويًا كان ترجيحًا من غير مرجح، وإن كان مرجوحًا كان العمل به ممتنعًا.
وهذا الشرط أهمله الإمام وهو الحق، لأن المخصص لا يشترط فيه الرجحان كما سيجيء؛ لأن فيه جمعًا بين الدليلين وذلك كتخصيص مفهوم قوله - صلى الله عليه وسلم -: "إذا بلغ الماء قلتين لم يحمل خبثًا". رواه أبو داود','3','296',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (842,'والترمذي وابن ماجة وغيرهم.
وصححه الحفاظ بالراكد.
ويعني أن مفهوم: "إذا بلغ الماء قلتين" يدل على أنه يحمل الخبث إذا لم يبلغهما.
وهذا المفهوم قد أخرج منه الجاري فلا ينجس إلا بالتغيير لقوله - صلى الله عليه وسلم -: "الماء طهور لا ينجسه شيء".
رواه أبو داود والترمذي، وقال: حديث حسن صحيح.','3','297',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (843,'فدل بمنطوقه على عدم التنجيس.
والمنطوق أرجح من المفهوم.
واختيار المصنف هذا تابع فيه الغزالي وغيره وهو قول قديم للشافعي - رضي الله عنه -.
والصحيح عدم الفرق بين الراكد والجاري؛ لأن عموم الحديث الثاني، مخصوص بمفهوم الأول.
قيل: التخصيص لا يجوز، لأنه في الأوامر يوهم البداء، بفتح الباء الموحدة والمد ودال مهملة - وهو ظهور المصلحة بعد خفائها.','3','298',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (844,'أو يوهم الكذب إن كان خبرًا، وهما على الله تعالى ممتنعان.
قلنا: يندفع بالمخصص، أي بالإرادة، أو بالدليل الدال على الإرادة، وذلك أنا إذا علمنا أن اللفظ في الأصل يحتمل التخصيص، فقيام الدليل على وقوعه مبين للمراد، وإنما يلزم البداء الكذب أن لو كان المخرج مرادًا.
واعلم أن كلام الإمام الرازي وأتباعه، وابن الحاجب يقتضي أن الخلاف في الأمر والخبر.
قال الإسنوي: وليس كذلك بل في الخبر خاصة.','3','299',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (845,'كما صرح به الآمدي، وهو مقتضى كلام "المعتمد" والشيخ أبي إسحاق وغيرهم.
الثالثة
يجوز التخصيص ما بقي من المخرج منه عدد غير محصور.
وما هنا مصدرية، تقديره يجوز التخصيص مدة بقاء عدد غير محصور من المخرج عنه.','3','300',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (846,'فإن كان محصورًا فلا.
ولا خفاء في امتناع ضبط الكثرة إلا فيما يعلم عدد أفراد العام.
وقال ابن الحاجب: لا بد أن يبقى من لعام جمع يقرب من مدلوله ومقتضاه: أن يكون أكثر من النصف، ويستثنى استعمال ذلك العام في الواحد تعظيمًا له وإعلامًا بأنه يجري مجرى الكثير، كقوله تعالى: } فقدرنا فنعم القادرون {.
ثم استدل المصنف على مختاره: بقوله: لسماجة أكلت كل رمان.
ولم يأكل غير واحدة.','3','301',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (847,'يعني لو خص إلى أن لا يبقى منه كثرة، بل إلى الواحد لم يكن على وفق اللغة، لأن القائل (إذا قال): أكلت كل رمان في البستان، وفيه ألف رمانة مثلاً، ولم يأكل منها غير واحدة عابه أهل اللغة، وكان سمجًا أي: قبيحًا لاغيًا وخطأ، وذلك دليل على الامتناع لغة، فوجب أن يكون في الباقي كثرة.
وفيه نظر: لأنه إن أراد بعدم حصر الكثرة عدم تناهيها، فباطل وهو ظاهر.
وإن أراد عدم العلم بقدرها.
فلو بقي كثرة معلومة القدر كأربعمائة مثلاً في الصورة المذكورة، وجب أن لا يصح التخصيص، وأنه يصح اتفاقًا.
وجوز القفال الشاشي: التخصيص إلى أقل المراتب التي ينطلق عليها ذلك اللفظ المخصوص مراعاة لمدلول الصيغة.','3','302',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (848,'فيجوز عنده في الجمع ما بقي ثلاثة، فإنه الأقل، أي: أقل الجمع عند الشافعي وأبي حنيفة - رضي الله عنهما - وهو المختار.
فإن أطلق على الاثنين أو الواحد كان مجازًا، بدليل تفاوت الضمائر، أي: اختلافها لغة، لأن ضمير المفرد غير بارز، وضمير المثنى ألف، وضمير الجمع واو نحو: افعل، وأفعلا، وافعلوا.
فاختلاف الضمير في التثنية والجمع يدل على اختلاف حقيقتهما لغة، كما يدل الاختلاف بين الواحد والجمع.
وأيضًا: فإنه لا يجوز وضع شيء منها مكان الآخر، فلو كان أقل','3','303',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (849,'الجمع اثنين لجاز التعبير عنه (بضمير الجمع) وليس كذلك.
وأيضًا: تفصيل أهل اللغة، فإنهم قالوا: الاسم قد يكون مفردًا، وقد يكون مثنى، وقد يكون مجموعًا، وبين صفتيهما أيضًا، فقالوا: رجلان عاقلان، ورجال عاقلون، فدل على المغايرة. وفيه نظر مبين في الأصل مع دليل صحيح على أن أقل الجمع ثلاثة.
وأقل الجمع حقيقة اثنان عند القاضي أبي بكر الباقلاني، والأستاذ، والغزالي.
ونقله أبو شامة عن مالك - رضي الله عنه - وقال: إنه','3','304',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (850,'المختار. بدليل قوله تعالى: } وكنا لحكمهم شاهدين {.
فأتى تعالى بضمير الجمع لاثنين، وهما داود وسليمان - عليهما الصلاة والسلام.
فقيل في جوابه: إنه أضاف إلى المعمولين.
يعني أن الحكم مصدر، والمصدر يضاف إلى الفاعل فقط، وإلى المفعول فقط وإليهما معًا.
فالمراد هنا: الحاكم، والمحكوم عليه، فيكون المراد داود وسليمان','3','305',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (851,'(عليهما الصلاة والسلام) والخصمين، كذا في الكشاف.
ونظر فيه لن المصدر إنما يضاف إليهما على البدل لا معًا.
ولما استشعر المصنف ضعفه وضعف ما بعده من الأجوبة، عزاها إلى غيره بصيغة التمريض على خلاف عادته.
وأجيب أيضًا: بأنه يجوز أن يراد سليمان وداود وأتباعهما، لأن الحاكم قد يكون له أتباع يوافقونه في حكمه.
وأجاب بعضهم: بأن الحكم يتضمن الأمر والقصة.
وبدليل قوله تعالى: } فقد صغت قلوبكما {.','3','306',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (852,'والخطاب مع عائشة وحفصة - رضي الله عنهما - فأطلق: قلوبكما، وأراد قلباكما، فأطلق الجمع على الثنين.
فقيل في جوابه: المراد بالقلوب الميول.
فإن القلب يطلق على اللحم الصنوبري في الجانب الأيسر حقيقة.
ويطلق على ما فيه من الميل مجازًا، فيصير المعنى ميولكما.
وفيه نظر؛ لأن الصغو بمعنى الميل، ولا معنى لميل الميول.
واعلم أن النزاع في نحو رجال، ومسلمين، وضربوا، واضربوا وهم من اللفظ المسمى بالجمع في اللغة.
وأما الجمع نفسه وهو: ضم شيء إلى شيء، فإن ذلك ثابت للاثنين فما زاد بلا خلاف.','3','307',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (853,'وكذا نحو: فعلنا، ونحو: } صغت قلوبكما {فإنه وفاق كذا في المنتهي.
ثم الخلاف في جمع القلة.
فإن أقل جمع الكثرة أحد عشر بإجماع النحاة، كذا قيل.
لكن ذكر الرافعي في فروع الطلاق: أنه لو قال: إن تزوجت النساء، أو اشتريت العبيد فامرأتي طالق، لم يحنث إلا إذا تزوج ثلاث نسوة، أو اشترى ثلاثة أعبد.','3','308',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (854,'وقد يستعمل في الأقل مجازًا.
فائدة:
جموع القلة خمسة أشياء، أربعة منها من جموع التكسير يجمعها قول الشاعر:
بأفعل وبأفعالٍ وأفعلةٍ *** وفعلةٍ يعرف (الأدني من العدد)
والخامس: جمع السلامة، إذا لم يكن فيه الألف واللام.
وبدليل ما روى من قوله - صلى الله عليه وسلم -: "الاثنان فما','3','309',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (855,'فوقهما جماعة" رواه ابن ماجة مرفوعًا، وفيه الربيع بن بدر، وهو متروك ووالده وجده وهما مجهولان، قاله الذهبي.
ورواه الدارقطني عن عمرو بن شعيب، وله طرق يقوي بعضها بعضًا.','3','310',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (856,'فهو صريح في إطلاق لفظ الجمع عليهما لكونه مستفادًا من الجماعة وبمعناها.
فقيل في جوابه: أراد جواز السفر، يعني كأن النبي - صلى الله عليه وسلم - نهى عن السفر وحده فبين أن الاثنين فما فوقهما جماعة في جواز السفر.
لأنه مبعوث لبيان أحكام الشرع لا أحكام اللغة.
وفيه نظر: لجواز السفر منفردًا، غايته الكراهة.
وهذا في غير محل النزاع كما مر.','3','311',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (857,'الرابعة
العام المخصص مجاز وهو المختار ورجحه الصفي الهندي، وابن الحاجب، وعزي لجمهور أصحابنا.
وإلا، أي: لو لم يكن مجازًا لزم الاشتراك، واللازم منتف.
أما الملازمة: فلأنه ثبت للعموم حقيقة من حيث هو عموم كما مر.
وهو لا يصدق على البعض، فلا يكون إطلاقه عليه حقيقة، باعتبار مفهوم واحد مشترك بين الكل والبعض؛ ليكون الاشتراك معنويًا، بل يتعين الاشتراك اللفظي.','3','312',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (858,'وأما بطلان اللازم، فلأن المجاز خير من الاشتراك.
أو نقول: هو خلاف ما فرض، وهو كونه ظاهر في العموم غير مشترك بينه وبين الخصوص.
وفيه نظر مبين في الأصل.
وقال بعض الفقهاء: إنه حقيقة.
قال الشيخ أبو حامد: وهو مذهب الشافعي - رضي الله عنه - وأصحابه.
وفرق الإمام الرازي تبعًا لأبي الحسين بين المخصص المتصل،','3','313',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (859,'أي بما لا يستقل، فيكون حقيقة، سواء كان صفة أو شرطًا أو استثناء أو علة نحو أكرم الرجال العلماء، أو أكرمهم إن دخلوا، أو أكرمهم إلا زيدًا، أو أكرمهم إلى المساء.
وبين المنفصل أي: ما يستقل من سمع أو عقل، فيكون مجازًا كالنهي عن قتل العبيد بعد الأمر بقتل المشركين.
قال الإمام: لأن العام المقيد بالصفة مثلاً لم يتناول غيرًا أي غير الموصوف، إذ لو تناوله لضاعت فائدة الصفة.
فإذا كان متناولاً له فقط، وقد استعمل فيه فيكون حقيقة. بخلاف العام المخصوص بدليل منفصل، فإن لفظه متناول للمخرج عنه بحسب اللغة مع أنه لم يستعمل فيه فيكون مجازًا وإلا لزم الاشتراك كما تقدم.
والتعبير بالصفة للتمثيل لا للتقييد.','3','314',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (860,'أو يقرر هكذا: لفظ العموم حال انضمام الصفة إليه ليس هو المفيد لذلك البعض المنطوق به.
لأن الرجال وحده من قولنا: الرجال العلماء.
لو أفاد العالمين، لما أفادت الصفة شيئًا.
وإذا لم يكن مفيدًا لذلك البعض استحال أن يقال: إنه مجاز فيه، بل المجموع الحاصل من لفظ العموم.
ولفظ الصفة هو المقيد له، وإفادته له حقيقة.
قلنا: المركب لم يوضع والمفرد متناول.
فالمركب من الموصوف مع الصفة غير موضوع للباقي، فلم يبق إلا المفردات.
والمفرد الذي هو العام متناول في اللغة لكل فرد، وقد استعمل في البعض فيكون مجازًا.
وقيل: هذا يعكر على ما ذكره في مجاز التركيب.','3','315',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (861,'فالأولى أن يجاب بأن كلامنا في العام المخصص (وهو الموصوف وحده لا في المجموع من المخصص والمخصص.
وأيضًا: لو) لم يكن الموصوف ونحوه متناولاً، لم يكن المتصل به مخصصًا؛ لأن التخصيص إخراج بعض ما يتناوله اللفظ.
ولا شك أن هذه الأشياء من المخصصات عنده.
والتحقيق: أن اللفظ متناول بحسب وضع اللغة.
ولكن الصفة قرينة في إخراج البعض فيكون مجازًا.','3','316',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (862,'الخامسة
العام إن خص بمبهم غير معين، كما إذا قال: هذا العام مخصوص، أو هذا العام لم يرد به كل ما يتناوله.
أو اقتلوا المشركين إلا بعضهم.
ومنه قوله تعالى: } أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم {.
فليس بحجة بالاتفاق كما قاله الآمدي.','3','317',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (863,'إنما الكلام في المخصص بمعين (مثل أن يقال: اقتلوا المشركين، ثم يظهر أن الذمي غير مراد. والصحيح) أنه حجة.
ومنعها - أي: منع حجيته - عيسى بن أبان وأبو ثور.
وفصل الكرخي فقال: إن خص بمتصل كان حجة، وإلا فلا.','3','318',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (864,'وهذا التفصيل يعرف بدليله، من المسألة السابقة، فتركه اختصارًا.
لنا: على أن العام المخصص بمعين حجة مطلقًا فيما بقى من الأفراد، أن دلالته على فرد لا تتوقف على دلالته على الآخر لاستحالة الدور.
فلا يلزم من زوالها زوالها.
يعني أن دلالة العام على الأفراد الباقية لا تتوقف على دلالته على البعض المخرج.
فإذا لم تتوقف يكون حجة.
أما أن دلالته على الباقي لا تتوقف على دلالته على الخارج، فلأنه لو توقف دلالته على الباقي على دلالته على الخارج، فلا يخلو إما أن تتوقف دلالته على الخارج على دلالته على الباقي أو لا.
فإن لم تتوقف دلالته على الخارج على دلالته على الباقي، كان ترجيحًا من غير مرجح، وهو تحكم باطل - لأن التقدير أن كلا منهما بعض العام.
ومدلول تضمني له من غير فرق.','3','319',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (865,'ولولا هذا التقدير لجاز أن يتوقف إفادة اللفظ لمعنى على إفادته الآخر من غير عكس، كما في المدلول التضمني والالتزامي بالنسبة على المطابقي.
على أنك إذا تحققت فالتوقف في مثل هذا من الجانبين، وإن توقف لزم الدور، فثبت أن دلالة اللفظ العام على الباقي لا تتوقف على دلالته على المخرج، فلا يلزم من زوال دلالته على الخارج، زوال دلالته على الباقي وفيه نظر وجوابه في الأصل.
والأحسن أن يستدل بعمل الصحابة - رضي الله تعالى عنهم - في','3','320',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (866,'العمومات المخصوصة من غير نكير، وتكرر وشاع فكان إجماعًا.','3','321',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (867,'السادسة
يستدل بالعام ويعمل به في جميع أفراده قبل البحث عنه، هل دخله مخصص أو لا؟
أما في حال حياته - صلى الله عليه وسلم - فيتمسك به بلا خلاف، قاله الأستاذ.','3','322',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (868,'وأما بعده، فكذلك عند المصنف، تبعًا لصاحب الحاصل ما لم يظهر المخصص.
وابن سريج: أوجب طلبه، أي طلب المخصص أولاً.
فيجب التوقف فيه حتى نبحث عن ذلك، فإن وجد له مخصصًا، وإلا عمل بالعموم، وحكاه الشيخ أبو حامد، وأبو إسحاق عن عامة أصحابنا.
وحكى ابن الحاجب وغيره الإجماع، على أن العمل بالعموم قبل البحث عن المخصص ممتنع، وهو غير مرضي، كما قاله الأبهري،','3','323',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (869,'لمخالفة أبي بكر الصيرفي.
قال: ولعل مراده بما نقل الإجماع عليه، أنه لا يجوز الهجوم على حكم العموم قبل النظر والتأمل فيما يعارضه من الخصوص.
فهذا ينبغي أن يكون مجمعًا عليه، وهكذا كل دليل مع ما يعارضه لا يجوز المبادرة إلى حكمه من غير نظر إلى ما يعارضه، وبهذا يشعر قول المستصفى انتهى.
وقال بعض المتأخرين: حكاية المصنف هذا الخلاف.
سبقه إليه أبو إسحاق الأستاذ، والشيخ.
وهنا زيادات مهمة في الأصل.
وإذا قلنا بوجوب البحث، فقال الجمهور: يكفي فيه أن يغلب على الظن عدمه.','3','324',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (870,'وقال القاضي أبو بكر وطائفة: لا بد من القطع بذلك.
وتمسك المصنف على مختاره بقوله: لنا لو وجب طلب المخصص في التمسك بالعام، لوجب طلب المجاز في التمسك بالحقيقة.
أما الملازمة: فلأن إيجاب طلب المخصص إنما كان للتخرز عن الخطأ، وهذا المعنى بعينه موجود في المجاز، وإليه أشار بقوله: "للتحرز عن الخطأ".
واللازم: وهو طلب المجاز منتف، فإنه لا يجب اتفاقًا.
فكذلك الملزوم وهو طلب المخصص.
وفي دعوى الاتفاق نظر، وبتقدير تسليمه فقد يفرق: بأن (احتمال وجود المخصص أقوى من احتمال وقوع المجاز، فإن أكثر) العمومات مخصوصة.','3','325',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (871,'وقال ابن سريج: عارض دلالته، أي: دلالة العام احتمال وجود المخصص إذ العام يحتمل وجود المخصص وعدمه على السواء، فحمله على العموم ترجيح بلا مرجح.
قلنا: الأصل يدفعه، أي يدفع ذلك الاحتمال؛ لأن الأصل عدم التخصيص.
والتعارض إنما يكون عند انتفاء الرجحان.
ولك أن تقول: الاستقراء دل على أن الغالب في العمومات الخصوص.
والعام المخصوص مجاز، فيدور الأمر بين الحقيقة المرجوحة، والمجاز الراجح، وتقدم للمصنف أنهما شيئان فيلزم التوقف كما قال ابن سريج.','3','326',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (872,'<span data-type="title" id=toc-121>الفصل الثالث: في المخصص</span>
والمراد به هنا الدال على التخصيص.
وهو متصل أي: لا يستقل بنفسه، بل يكون متعلقًا باللفظ الذي ذكر فيه العام.
ومنفصل وهو عكس المتصل.
فالمتصل أربعة أقسام:','3','328',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (873,'الاستثناء، والشرط، والصفة والغاية، وزاد ابن الحاجب بدل البعض وتركه المصنف.
الأول: الاستثناء: وهو الإخراج بإلا غير الصفة ونحوها.
فقوله: الإخراج، كالجنس شامل للمخصصات كلها.
وقوله: بإلا، مخرج لما عدا الاستثناء.
وقوله: غير الصفة، احتراز عن إلا إذا كانت للصفة، بمعنى غير، وهي التي تكون تابعة لجمع منكور غير محصور.
كقوله تعالى: } لو كان فيهما آلهة إلا الله لفسدتا {.
أي: غير الله فيكون صفة لا استثناء.','3','329',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (874,'فإن قلت: لا حاجة إلى تقييد "إلا" بغير الصفة لخروجها، بقوله: "الإخراج" فإن "إلا" التي هي صفة لا إخراج فيها لعدم تناول ما قبلها لما بعدها، ولهذا لم يذكره الإمام.
وأجيب: بأن المراد ما جاز دخوله، لا ما وجب دخوله.
وأيضًا للاحتراز عن نحو قولهم: قام القوم إلا زيد برفع زيد، فإنه جائز على جعل إلا صفة، كما ذكر ابن عصفور وغيره.
وقوله: ونحوها، أراد به الحروف المرادفة لإلا نحو: سوى، وحاشا، وخلا، وعدا، وهي حروف معلومة معينة.','3','330',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (875,'وهو تعريف لفظي، وأخذه "إلا" في التعريف لا يلزم منه تعريف الشيء بنفسه؛ لأن معنى إلا بديهي لا يتوقف على معرفة الاستثناء.
وأراد لفظ إلا لا معناه.
والواو في التعريف بمعنى "أو" الفاصلة، وهو واضح وأخوات "إلا" لشهرتها لم يذكرها ولم يبينها.
والاستثناء المنقطع الذي لا إخراج فيه مثل: قام القوم إلا حمارًا، مجاز على الأصح، فلا يرد على هذا التعريف فإنه للاستثناء الحقيقي وهو المتصل، لأنه المتبادر إلى الفهم، فلا يكون الاستثناء المنقطع أي:','3','331',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (876,'صيغته مشتركًا لفظيًا، ولا موضوعًا للقدر المشترك بين المتصل والمنقطع.
إذ ليس أحد معاني المشترك أو أفراد المتواطئ أولى بالظهور.
والتبادر عند قطع النظر عن عارض أو كثرة ملاحظة. أو نحو ذلك.','3','332',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (877,'واعلم أنا إذا قلنا: جاءني القوم إلا زيدًا، فالاستثناء يطلق على إخراج زيد، وعلى زيد المخرج، وعلى لفظ زيد المذكور بعد إلا، وعلى مجموع لفظ إلا زيدًا، وبهذه الاعتبارات اختلفت العبارات في تفسيره فيجب أن يحمل كل تفسير على ما يناسبه وزاد بعضهم في حد الاستثناء: أن يكون من متكلم واحد.
ورجحه الصفي الهندي.
وفيه أي: في الاستثناء مسائل:','3','333',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (878,'الأولى
شرطة الاتصال عادة، أي: يشترط في الاستثناء اتصاله بالمستثنى منه لفظًا، أو ما هو في حكم الاتصال، فلا يضر قطعه بتنفيس أو سعال أو طول الكلام المستثنى منه، ونحوها، مما لا يعد منفصلاً عادة وعرفًا بإجماع الأدباء أي: أهل اللغة، ولولا ذلك لما استقر عتق ولا طلاق، ولا حنث؛ لجواز الاستثناء بعده بأسهل طريق.
والإجماع بخلافه.
ونقل عن ابن عباس (رضي الله عنهما) خلافه، أي: جواز الاستثناء المنفصل.','3','334',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (879,'ثم اختلفوا فروى عنه الحافظ أبو موسى المديني أنه يجوز إلى أربعين يومًا، وقال: لا يثبت عن ابن عباس - (رضي الله عنهما).
وروى عنه الطبراني في معجمه الأوسط: أنه كان يرى الاستثناء ولو بعد سنة ورواه أبو موسى المديني ثم قال: هذا حديث غير متصل ولا ثابت.','3','335',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (880,'قال: ولو صح عن ابن عباس (رضي الله عنهما) لاحتمل رجوعه عنه، أو علم أن ذلك كان خاصًا برسول الله - صلى الله عليه وسلم - انتهى.
وروى عنه إلى شهر، وقيل: إلى سنة، وقيل: ابدًا.
بالجملة لم يثبت الأكثرون شيئًا من هذه الروايات عن ابن عباس (رضي الله عنهما) بل أولوه.','3','336',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (881,'وكذا عبر المصنف بقوله: ونقل.
ولما اختلفوا في كيفيته على المذاهب المتقدمة عبر بقوله: خلافه. وحكي عن سعيد بن جبير جواز تأخيره أربعة أشهر.
وعن عطاء والحسن: يمتد ما دام المجلس موجودًا.','3','337',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (882,'وعن مجاهد إلى سنتين.
قال بعض المتأخرين: وهي مذاهب شاذة.
واعلم أنه لا بد أن ينوى في الكلام اتفاقًا، إذا شرطنا اتصاله، فلو لم يعرض له نية الاستثناء إلا بعد فراغ المستثنى منه لم يعتد به وتكفي النية قبل فراغه من الكلام على الصحيح.','3','338',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (883,'القائل بجواز الاستثناء المنفصل قال: قياسًا على التخصيص بغيره، أي: بغير الاستثناء من المخصصات المنفصلة، والجامع أن كلا منهما مخصص.
والجواب: النقض بالصفة والغاية.
أي: لو صح القياس لزم جواز تأخير الوصف والغاية في التخصيص بهما بعين ما ذكر، لكنه لا يجوز تأخيرهما اتفاقًا.
وأيضًا: الفرق، فإن المخصص المنفصل مستقل ولذلك جاز انفصاله بخلاف الاستثناء.
وقوله: "وعدم الاستغراق" عطف على الاتصال.','3','339',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (884,'أي يشترط أيضًا أن لا يكون المستثنى مستغرقًا للمستثنى منه، سواء كان مثل المستثنى منه أو أكثر، فإن استغرق فهو باطل بالإجماع، كما حكاه الآمدي وابن الحاجب.
لكن في المدخل لابن طلحة في: أنت طالق ثلاثًا إلا ثلاثًا - قولان في اللزوم وعدمه.
وهو يقتضي صحة الاستثناء المستغرق، كذا قيل.
ومحل الإجماع - إن صح - إذا اقتصر عليه.','3','340',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (885,'فلو أعقبه باستثناء آخر، فالخلاف فيه مشهور. نحو: له علي عشرة إلا عشرة إلا ثلاثة.
فقيل: يلزمه عشرة، وقيل: ثلاثة.
ثم قال الجمهور: يصح استثناء المساوي كقوله: علي عشرة إلا خمسة، والأكثر. واختاره المصنف.
وشرط الحنابلة: على ما نقله: أن لا يزيد على النصف.
لكن نقل ابن الحاجب، تبعًا للآمدي عنهم امتناع المساوي أيضًا.','3','341',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (886,'وشرط القاضي أبو بكر الباقلاني: في آخر أقواله أن ينقص المستثنى منه أي من النصف.
لنا على الحنابلة والقاضي: لو قيل: علي عشرة إلا تسعة لزم واحد إجماعًا.
ولولا أن الاستثناء الأكثر ظاهر في وضع اللغة في بقاء الأقل لامتنع الاتفاق عليه عادة.
ولصار قوم ولو قليلاً إلى أنه يلزمه العشرة لكون الاستثناء لغوًا لأنه غير صحيح كما في المستغرق. وفيه نظر.','3','342',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (887,'ولنا على القاضي: استثناء المخلصين من الغاوين، في قوله تعالى حكاية عن إبليس: } قال فبعزتك لأغوينهم أجمعين إلا عبادك منهم المخلصين {.
وعكسه، أي: استثناء الغاوين من المخلصين، في قوله تعالى: } إن عبادي ليس لك عليهم سلطان إلا من اتبعك من الغاوين {.
إيضاحه: أنهما إن تساويا ثبت استثناء المساوي، وإن تفاوتا ثبت استثناء الأكثر.
وللقاضي أن يقول: الغاوين مستثنى من قوله: } إن عبادي ليس لك عليهم سلطان {.
والعباد الذين ليس لإبليس عليهم سلطان أعم من المخلصين؛ لأن عبادي جمع مضاف يفيد العموم، فإذا خرج عنه الغاوين الذين هم الكفار، بقي العباد غير الكفار، وهو أعم من المخلصين الذين هم','3','343',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (888,'المعصومون، وغيرهم أي: المؤمنون غير المعصومين.
فيكون المستثنى أقل من المستثنى منه.
والمخلصون في الآية الثانية أقل من الغاوين.
فالمستثنى أقل.
اللهم إلا أن يفسر العباد الذين ليس لإبليس عليهم سلطان بالمخلصين، فيستقيم ما ذكره المصنف.
لكن بالنظر إلى مفهومه أعم.
قال القاضي: الأقل ينسى فيستدرك.
يعني أن الاستثناء خلاف الأصل، إذ هو بمنزلة الإنكار بعد الإقرار.
خالفناه في الأقل؛ لأنه قد ينسى لقلة التفات النفس إليه فيستدرك ويبقى معمولاً به في غيره، وهو المساوي والأكثر.','3','344',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (889,'ونقض كلام القاضي بما ذكرنا فيما تقدم من أنه إذا قال: علي عشرة إلا تسعة، فإنه صحيح بإجماع الفقهاء، واستثناء الغاوين من المخلصين.
وأجيب أيضًا: بأنا لا نسلم أن الدليل منعه، وأنه إنكار بعد إقرار؛ لأنه كجملة واحدة؛ لأنه إسناد بعد إخراج، فليس فيه حكمان مختلفان.
الثانية
الاستثناء من الإثبات نفي، وبالعكس، خلافًا لأبي حنيفة','3','345',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (890,'(رضي الله عنه).
لنا: لو لم يكن كذلك، لم يكف "لا إله إلا الله" في التوحيد واللازم باطل بالإجماع.
بيان الملازمة: أنه إنما يتم التوحيد بإثبات الإلهية لله - تعالى - ونفيها عما سواه، والمفروض أنه لا يفيد الإثبات له، وإنما يفيد النفي فقط، فلو تكلم بها دهري منكر لوجود الصانع، وهو لا يفيد إلا نفي الغير لما نفى معتقده، ولم يعلم بها إسلامه.
احتج أبو حنيفة (رضي الله عنه) بقوله - صلى الله عليه وسلم -: "لا صلاة إلا بطهور"','3','346',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (891,'هو بهذا اللفظ لم يحفظ كما قاله الإسنوي والزركشي.
قال العراقي: وليس كذلك بل رواه الدارقطني من حديث عائشة - رضي الله عنها.
قال الزركشي: ويقرب منه: "لا يقبل الله صلاة إلا بطهور".
رواه ابن ماجة.','3','347',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (892,'والذي رواه مسلم: "لا يقبل الله صلاة بغير طهور".
تقديره: لا صحة للصلاة إلا بطهور.
فلو كان الاستثناء من النفي للإثبات، للزم ثبوت الصلاة بمجرد الطهور، وأنه باطل بالاتفاق.
قلنا: الحصر للمبالغة، لا للفني عن الغير، كقوله: "الحج عرفة" وهنا كذلك؛ لأن الطهارة لما كان أمرها متأكدًا صارت كأنه لا','3','348',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (893,'شرط للصحة غيرها.
حتى إذا وجدت توجد الصحة.
وأجيب أيضًا: بأن قولنا: الاستثناء من النفي إثبات، يصدق بإثبات صورة واحدة من كل استثناء؛ لأن دعوى الإثبات لا عموم فيها؛ إذ هي مطلقة، وحينئذ فيقتضي صحة الصلاة عند وجود الطهارة بصفة الإطلاق، لا بصفة العموم.
أي لا يقتضي صحة الصلاة في جميع صور الطهارة، بل يصدق ذلك بالمرة الواحدة.
تنبيه:
المشهور من كلام السادة الشافعية: أن الاستثناء من الإثبات نفي اتفاقًا، وصرح به الإمام الرازي والمحقق.','3','349',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (894,'وإنما الخلاف في كونه من النفي إثباتًا.
والمذكور في كتب الحنفية، ونقله عنهم الصفي الهندي: أنه ليس من الإثبات نفيًا، ولا من النفي إثباتًا، بل هو تكلم بالباقي بعد الثنيا.
ومعناه: إخراج المستثنى، وحكم على الباقي من غير حكم على المستثنى، ففي مثل علي عشرة إلا ثلاثة لا تثبت الثلاثة بالبراءة الأصلية وعدم الدلالة على الثبوت لا بسبب دلالة اللفظ على عدم الثبوت، وفي مثل: ليس علي إلا سبعة: لا يثبت شيء بحسب دلالة اللفظ لغة، وإنما يثبت بحسب العرف، وطريق الإشارة، كما في كلمة التوحيد، حيث يحصل بها الإيمان من المشرك، ومن القائل: بنفي الصانع، بحسب عرف الشرع، ويؤولون كلام أهل العربية، على أنه','3','350',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (895,'من الإثبات نفي، بأنه مجاز، تعبيرًا عن عدم الحكم بالحكم بالعدم لكونه لازمًا.
قال الشيخ سعد الدين: لكن إنكار دلالة ما قام إلا زيد، على ثبوت القيام لزيد، يكاد يلحق بإنكار الضروريات، وإجماع أهل العربية على أنه من النفي إثبات لا يحتمل التأويل.
الثالثة
الاستثناءات المتعددة إن تعاطفت، أي: عطف بعضها على بعض، نحو علي عشرة إلا ثلاثة، وإلا أربعة، وإلا اثنين.
أو استغرق الأخير الأول ولم تكن معطوفة، سواء كان مساويًا، نحو: له علي عشرة إلا اثنين، إلا اثنين، أو أزيد، نحو: عشرة','3','351',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (896,'إلا اثنين إلا ثلاثة.
فإنها في الثلاث صور تعدد إلى المتقدم عليها.
أما في الأول فلو وجب تساوي المعطوف والمعطوف عليه في الحكم، وأما في الثاني وهي ما إذا لم تكن متعاطفة، وكانت مستغرقة؛ لأنه لو عاد إلى الاستثناء الأول لما صح لكونه مستغرقًا.
ففي الصورة الأولى وهي المعطوفة، يلزمه درهم وفي الثانية (وهي المستغرق المساوي) ستة.
وفي الثالثة وهي المستغرق الزائد خمسة.
وللنحويين في المستغرق مذهبان:
أحدهما: موافقة المصنف.
والثاني: مذهب الفراء، أن الثاني يكون مقرًا به، فيلزمه في المثال الأول من المستغرق عشرة، والثاني: أحد عشر وإلا، أي: وإن لم يكن الثاني معطوفًا، ولا مستغرقًا، فإنه يعود الاستثناء الثاني إلى الاستثناء الأول أي: يكون مستثنى منه.','3','352',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (897,'وهكذا الثالث والرابع إلى غير ذلك؛ لأنه أقرب وهو دليل الرجحان، ولا بد من مراعاة ما تقدم وهو أن الاستثناء من الإثبات نفي وبالعكس، فإذا قال: عشرة، إلا ثمانية، إلا سبعة، فالسبعة مثبتة مستثناة من الثمانية، فتضم إلى ما بقي من العشرة بعد الثمانية وهو اثنان، فيكون مقرًا بتسعة وعليه فقس.
الرابعة
قال الشافعي - رضي الله عنه -: الاستثناء المتعقب للجمل المعطوف بعضها على بعض كقوله تعالى: } إلا الذين تابوا {، الظاهر أنه يعود','3','353',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (898,'إليها جميعًا عند عدم قرينة الاتصال والانفصال.
وخص أبو حنيفة - رضي الله عنه - الاستثناء بالجملة الأخيرة.
وتوقف القاضي أبو بكر، والشريف المرتضى، من الشيعة.','3','354',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (899,'إلا أن القاضي: توقف لعدم العلم بمدلوله لغة.
والمرتضى: لكونه مشتركًا بين عوده إلى الكل، وعوده إلى الأخيرة، لأنه ورد لهما، والأصل في الاستعمال الحقيقة، فيتوقف إلى ظهور القرينة.
وهو موافق للحنفية في الحكم، وإن خالف في المأخذ، لأنه يرجع إلى الأخيرة فيثبت حكمه فيها، ولا يثبت في غيرها، لكن هما لعدم ظهور تناولها، والحنفية لظهور عدم تناولها.
وقيل: إن كان بينهما، أي بين الجملتين تعلق فللجميع مثل:','3','355',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (900,'أكرم الفقهاء والزهاد، وأنفق عليهم إلا المبتدعة.
فتعلق أحدهما بالأخرى في هذا المثال كون أحدهما مضمرًا في الأخرى.
وكذا اشتراكهما في غرض نحو: أكرم بني تميم، واخلع عليهم، فإن الغرض هو التعظيم فيهما، وإلا أي: وإن لم يكن بينهما تعلق فللأخيرة يرجع الاستثناء فقط.
لنا: على أن الاستثناء يعود على جميع الجمل، أن الأصل اشتراك المعطوف والمعطوف عليه في المتعلقات كالحال، والشرط، وغيرهما، من الظرف والمجرور.
فكذلك الاستثناء، والجامع عدم الاستقلال.
وقد يمنع الخصم حكم الأصل.','3','356',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (901,'قيل من قبل الحنيفة: الاستثناء خلاف الدليل؛ لأنه إنكار بعد إقرار، والأصل أنه لا يعود لشيء من الجمل.
لكنه خولف في الجملة الأخيرة للضرورة، وهي صون الكلام عن اللغو، وخصت هي بالاستثناء لأنها أقرب فبقيت الجمل الأولى السابقة على الجملة الأخيرة على عمومها الذي هو الأصل.
قلنا: ما ذكرتم منقوض بالصفة والشرط، فإن دليلكم بعينه جار فيهما، لكونهما خلاف الأصل، مع أنهما عائدان للكل، وقد','3','357',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (902,'عرفت ما في هذا الجواب.
وقد يجاب: بأنا لا نسلم أنه يرجع للضرورة، بل عندنا أن وضعه للجميع، فلا يتقيد بالأخيرة، كما لو دل دليل على عوده إلى الجميع فإنه يعتبر إجماعًا.
ومع جواز وضعه للجميع لا يتم ما ذكرتم، وعليه إشكالات وأجوبتها في الأصل.
تنبيه:
مثل المصنف بالآية وهي قوله تعالى: } والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة أبدًا وأولئك هم الفاسقون إلا الذين تابوا {.
لأن هذا الاستثناء بعد ثلاث جمل: الأولى: آمرة بجلدهم، والثانية: ناهية عن قبول شهادتهم، والثالثة: مخبرة بفسقهم.','3','358',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (903,'ومذهب الشافعي - رضي الله عنه - جلد القاذف، وعدم قبول شهادته وأنه فاسق.
واستثنى من تاب فلا يبقى فاسقًا ولا مردود الشهادة، لكن الجلد لا يسقط؛ لأن حد الآدمي لا يسقط بالتوبة، فهو خارج عن محل النزاع.
وقول المصنف المتعقب للجمل، احترز به عن الاستثناء المتعقب للمفردات فإنه يعود لكها لعدم استقلالها.
واقتضى كلام جماعة أن هذا متفق عليه.
وقال الرافعي (رحمه الله): في الطلاق إذا قال: حفصة وعمرة','3','359',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (904,'طالقان، إن شاء الله تعالى، فهو من باب الاستثناء عقب الجمل.
ومحل عود الاستثناء إلى الجميع إذا لم يدل الدليل على إخراج البعض كما سبق. وشرط إمام الحرمين أيضًا شرطين:
أحدهما: أن لا يتخلل بين الجملتين كلام طويل، فإن تخلل فالاستثناء يختص بالأخيرة.
ثانيهما: أن تكون معطوفة بالواو خاصة.
وصرح أيضًا بهذا الشرط: الآمدي، وابن الحاجب، وهو الذي في منهاج النووي تبعًا لمحرر الرافعي.
وأطلق الإمام الرازي العطف. وخرج القاضي أبو بكر: بالتعميم في الواو وغيرها. وهو ظاهر إطلاق أصحاب الشافعي العطف.
وصرح المتولي: بعود الشرط إلى الجملتين، ولو كان العطف بثم، حكاه عنه في الروضة، وأصلها في تعدد الطلاق وإقراره.','3','360',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (905,'والشرط قسم من الاستثناء، صرح به الرافعي، وأن لا يدل دليل على خلافه، وإن دل اتبع.
الثاني من المخصصات المتصلة: الشرط: وهو لغة العلامة.
واصطلاحًا - على ما قال المصنف -: ما يتوقف عليه تأثير المؤثر لا وجوده، كالإحصان.','3','361',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (906,'فقوله: ما يتوقف عليه تأثير المؤثر - يدخل فيه جميع ما تقدم من الشرط وغيره.
وقوله: "لا وجوده" عطف على تأثير المؤثر، أي: لا يتوقف عليه وجود المؤثر، وخرج بهذا القيد: علة المؤثر، وجزؤه، وغير ذلك.
مما عدا الشرط، فإن التأثير متوقف على هذه الأشياء بالضرورة لكن ليس هو التأثير فقط، بل التأثير والوجود، بخلاف الشرط، فإن وجود المؤثر لا يتوقف عليه، بل إنما يتوقف عليه تأثيره كالإحصان، فإن تأثير الزنا في الرجم متوقف عليه، وأما نفس الزنا فلا؛ لأن البكر قد تزني.
فإن قلت: هذا التعريف: ينتقض بذات المؤثر، فإن التأثير متوقف عليها بالضرورة، ويصدق عليها أن المؤثر لا يتوقف وجوده عليها لاستحالة توقف الشيء على نفسه.
أجيب: بأن هذا بناء على أن الوجود عين الماهية.
والمصنف لا يراه، بل يختار أن الوجود من الأوصاف الزائدة','3','362',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (907,'العارضة للماهية.
فعلى هذا يصدق أن وجود المؤثر يتوقف على ذات المؤثر.
وللفرار من هذا السؤال عبر بقوله: لا وجوده ولم يقل: لا ذاته.
واعترض عليه أيضًا بأن هذا التعريف إنما يستقيم على رأي المعتزلة، والغزالي، فإنهم يقولون: إن العلل الشرعية مؤثرات، وإن اختلف مأخذها.
وأما المصنف وغيره من الأشاعرة، فإنهم يقولون: إنها أمارات على الحكم، وعلامات عليه فلا تأثير ولا مؤثر عندهم.','3','363',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (908,'واعترض أيضًا: بأنه غير منعكس؛ لأن الحياة شرط في العلم القديم، ولا يصدق عليها أن تأثير المؤثر في العلم يتوقف عليها؛ لأنا فرضناه قديمًا، ولا مؤثر في القديم؛ إذ المحوج إلى المؤثر هو الحدوث.
تنبيه:
الشرط ينقسم إلى عقلي، كالحياة للعلم، وشرعي كمثال المصنف، ولغوي: كقوله: إن دخلت الدار فأنت طالق وهنا فوائد حسنة في الأصل.
وفيه - أي في الشرط - مسألتان:','3','364',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (909,'الأولي:
الشرط إن وجد دفعة، كالتعليق على وقوع طلاق، ونحوه، مما يدخل في الوجود دفعة واحدة، فيوجد المشروط عند أول أزمنة الوجود، إن علق على الوجود، وعند أول أزمنة العدم، إن علق على العدم. وهو معنى قوله: "فذاك".
إلا، أي: وإن لم يوجد دفعة، بل وجد على التدريج، كقراءة الفاتحة مثلاً نظر.
فإن كان التعليق على وجوده، فيوجد المشروط عند تكامل أجزائه.
وإن كان على العدم، كقوله لزوجته، إن لم تقرئي الفاتحة فأنت','3','365',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (910,'طالق.
فيوجد المشروط وهو الطلاق، عند ارتفاع جزء من الفاتحة. لأن الكل ينتفي بانتفاء الجزء. وإليه أشار بقوله: أو ارتفاع جزء إن شرط عدمه.
الثانية:
في تعدد الشرط والمشروط، وهو تسعة أقسام:
لأن الشرط: إما أن يتحد أو يتعدد، وإذا تعدد فإما أن يكون كل واحد شرطًا على الجمع، حتى يتوقف المشروط على حصولهما جميعًا، أو على البدل. حتى يحصل بحصول أيهما كان. فهذه ثلاثة.
والجزاء أيضًا كذلك؛ لأنه إما أن يتحد، أو يتعدد، وإذا','3','366',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (911,'تعدد فإما على الجمع حتى يلزم حصول هذا وذاك، وإما على البدل حتى يلزم حصول أحدهما منهما، فهذه أيضًا ثلاثة.
وإذا اعتبرت التركيب كانت ثلاثة من الشرط، مع ثلاثة من الجزاء. يحصل من الضرب تسعة.
مثال المتحدين: إن قمت قمت، والمتعددين إن قمت وخرجت قمت وخرجت.
وتعدد الشرط على الجمع إن كان زانيًا محصنًا فارجم، (فلا بد من حصول المشروط من الجمع؛ إذ الشرط مجموعهما لا كل منهما.
وإليه أشار بقوله: "يحتاج إليهما")
وعلى البدل إن كان سارقًا أو نباشًا فاقطع، يكفي أحدهما كما تقدم.
وتعدد المشروط: على الجمع: إن شفيت فسالم وغانم حر، فشفي عتقًا.
وعلى البدل: إن شفيت فسالم أو غانم حر، فيعتق واحد منهما','3','367',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (912,'فيعينه السيد.
وإليه أشار بقوله: وإن قال أو فيعتق أحدهما.
ويعين، ولم يذكر المصنف اتحاد الشرط والمشروط اكتفاءًا بما تقدم، وذكر تعدد (هما) على الجمع والبدل كما علمت، ومجموع ذلك أربعة أقسام، لأنه الحاصل من ضرب اثنين في اثنين.
الثالث: من أقسام المخصصات المتصلة.
تخصيص الصفة: وهي قصر الصفة على بعض أفراد العام.
مثل قوله تعالى: } فتحرير رقبة مؤمنة {.','3','368',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (913,'واستشكل هذا المثال لأن رقبة ليس عامًا فهو من تقييد المطلق لا من تخصيص العموم.
وهي أي الصفة كالاستثناء، في اتصالها بما قبلها.
وإذا وردت بعد متعدد: نحو: أكرم بني تميم ومضر، وربيعة الطوال، في عودها للجميع أو للأخيرة.','3','369',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (914,'والمختار المختار.
ولو تقدمت نحو: وقفت على محتاجي أولادي وأولادهم، فيشترط الحاجة في أولاد الأولاد.
أما لو توسطت مثل أولادي المحتاجين وأولادهم.
فقال بعض المتأخرين: لا نعلم فيها نقلاً، ويظهر اختصاصها بما وليته.
ويدل له شيء حكاه الرافعي عن ابن كج مذكور في الأصل.','3','370',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (915,'وهل يجري الخلاف المذكور في الاستثناء في إخراج الأكثر والمساوي في الصفة؟
قال بعض المتأخرين: الظاهر أنه لا يجري، لكن كلام المصنف يشعر بجريانه.
الرابع: من المخصصات المتصلة الغاية: وهي طرفه، أي: غاية الشيء طرفه ومنتهاه، وأعاد الضمير على لفظ الشيء، وهو غير مذكور للعمل به.
قال العراقي: وأقول: بل أعاد الضمير على اسم المفعول المفهوم من الاسم، والتقدير: وهي طرف الشيء.
ونظيره قولهم في قوله تعالى: } اعدلوا هو أقرب للتقوى {.','3','371',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (916,'أي: العدل، فأعاد الضمير إلى المصدر المفهوم من الفعل، وللغاية لفظان: إلى وحتى
وحتى، وحكم ما بعدها خلاف ما قبلها، الضمير في بعدها عائد إلى حرف الغاية لا إلى الغاية. يعني حكم ما بعد حرف الغاية خلاف حكم ما قبله.
قوله: مثل قوله تعالى: } وأتموا الصيام إلى الليل {يدل عليه وكذا التمثيل بالمرافق، فيكون أراد بالغاية (ثانيًا، خلاف ما أراد','3','372',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (917,'بها أولاً، وهو غير ممتنع.
وأطلق على الحرف اسم الغاية) وهو المستعمل في عرف النحاة.
وحاصله: أن ما بعد الحرف ليس داخلاً في الحكم فيما قبله، بل محكومًا عليه بنقيض حكمه.
لأن ذلك الحكم لو كان ثابتًا فيه أيضًا، لم يكن الحكم منتهيًا ومنقطعًا، فلا تكون الغاية غاية، وهو محال.
وما اختاره المصنف طريقة الجمهور.
ومذهب سيبويه: أنه إن اقترن بمن فلا يدخل، وإلا فيحتمل الأمرين.
واختار الآمدي: أن التقييد بالغاية لا يدل على شيء.
وقيل: إن كان منفصلاً عما قبله بمفصل معلوم فإنه لا يدخل،','3','373',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (918,'وإلا فلا. (قال في المحصول: هو الأولى) وفي دخول غاية الابتداء أيضًا مذهبان.
(وذكر السبكي: أنه يستثنى من محل الخلاف شيئان:
أحدهما: الغاية التي لو سكت عليها لم يدل) عليها اللفظ (كقوله - عليه الصلاة والسلام -: "رفع القلم عن ثلاث ... " الحديث).','3','374',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (919,'والثاني: الغاية التي يكون اللفظ الأول قابلاً لها كقولنا: قرأت القرآن من فاتحته إلى خاتمته.
فالأول: لا يدخل الغاية قطعًا.
والثاني: يدخل قطعًا.
وقوله: "ووجوب غسل المرفق للاحتياط": جواب عن سؤال تقديره: لو كان ما بعد الغاية غير داخل فيما قبله، لكان غسل المرفق غير واجب وليس كذلك.
وحاصل الجواب: منع الملازمة، فإن - النبي صلى الله عليه وسلم - توضأ فأدار الماء على مرفقيه.
فاحتمل أن يكون غسله واجبًا، وتكون "إلى" بمعنى "مع"، واحتمل أن لا يكون فأوجب للاحتياط.
أو يقال: المرفق لما لم يكن متميزًا عن اليد امتيازًا حسيًا وجب غسله احتياطًا حتى يحصل العلم بغسل اليد.','3','375',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (920,'ولا يقال: على هذا التقدير يكون فيه إشعار باختيار التفصيل المتقدم؛ لأن هذا سند للمنع.
وذكر ابن الحاجب من المخصصات المتصلة: بدل البعض من الكل، وأنكره عليه الأصفهاني، وغيره.
والمخصص المنفصل: وهو الذي يستقل بنفسه أي: لا يحتاج في ثبوته إلى ذكر لفظ العام معه.
وقسمه إلى ثلاثة أقسام:
قال القرافي: وهي التخصيص بالعوائد، وقرائن الأحوال، والقياس إلا أن يدعي دخوله في السمعي.','3','376',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (921,'الأول: العقل: ضروريًا كان كقوله تعالى: } الله خالق كل شيء {فالعقل قاض ضرورة بخروج القديم الواجب عنه لاستحالة كونه مخلوقًا، ومقدرًا.
أو نظريًا: كقوله تعالى: } ولله على الناس حج البيت من استطاع إليه سبيلاً {فالعقل قاض بخروج من لا يفهم الخطاب، كالأطفال والمجانين، للدليل الدال على امتناع تكليف الغافل.','3','377',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (922,'والتمثيل الأول ينبني على أن المتكلم يدخل في عموم كلامه وأن الشيء يطلق على الله تعالى، وهو الصحيح فيهما.
وما جزم به المصنف من تخصيص العقل موافق للجمهور.
ومنعه طائفة وهو ظاهر كلام الشافعي - رضي الله عنه - (في','3','378',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (923,'الرسالة) على ما قيل.
فمنهم من جعله (خلافًا محققًا، ومنهم من جعله) لفظيًا، لأن خروج هذه الأمور من العموم لا نزاع فيه إلا أنه لا يسمى تخصيصًا إلا ما كان باللفظ.
الثاني من المخصصات المنفصلة: الحس.
والمراد به المشاهدة وإلا فالدليل السمعي من المحسوسات، وقد جعله قسيمًا له مثل قوله تعالى: عن بلقيس: } وأوتيت من كل شيء {','3','379',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (924,'لأنا نشاهد أشياء لم تؤت منها كالسماوات، وملك سليمان.
الثالث: من المخصصات المنفصلة: الدليل السمعي:
وفيه مسائل: وهي تسع على ما ذكره، الأولى في بيان ضابط كلي على سبيل الإجمال عند تعارض الدليلين السمعيين، والباقية في بيان التخصيص.
الأولي
الخاص إذا عارض العام، أي دل على خلاف ما دل عليه، فيؤخذ بالخاص، علم تقدم العام، أو تقدم الخاص، أو جهل التاريخ،','3','380',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (925,'وإليه أشار بقوله: "يخصصه علم تأخره أو لا".
وأبو حنيفة - رضي الله عنه - يجعل المتقدم منهما منسوخًا بالمتأخر.
وتوقف أبو حنيفة - رضي الله عنه - حيث جهل التاريخ فلم يعلم هل الخاص متقدم أو العام.
واعلم أن تحرير الأقسام فيما إذا عارض الخاص العام أن يقال له أحوال:
أحدها: أن يعلم تأخير الخاص عن العام، فإن تأخر عن وقت العمل به كان نسخًا "لقدر مدلوله من العام لا لجميع أفراد العام، فإنه لا خلاف في العمل بالعام في بقية الأفراد في المستقبل، ولم يكن تخصيصًا؛ لأن تأخير بيانه عن وقت العمل به ممتنع.','3','381',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (926,'وإن لم يتأخر عن وقت العمل به فالأكثرون على أنه تخصيص.
وقال أبو حنيفة (رضي الله عنه) والقاضي وإمام الحرمين: المتأخر ناسخ للمتقدم.
الثاني: تأخر العام عن الخاص سواء تأخر عن وقت العمل به أم لا، فيقدم الخاص فيما تعارضا فيه.
وأبو حنيفة (رحمه الله تعالى) ومن تبعه على أن المتأخر ناسخ.
الثالث: أن يتقاربا أي يوجدا في حالة واحدة سواء تقدم في اللفظ الخاص أو العام، كأن يقول: "فيما سقت السماء العشر".','3','382',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (927,'ثم يقول عقبه" "لا زكاة فيما دون خمسة أوسق".
أو بالعكس: فيقدم الخاص فيما تعارضا فيه.
وفي أصول الحنفية: أن حكم المقارنة والجهل بالتاريخ واحد، وهو ثبوت حكم التعارض فيما يتناولاه.
الرابع: أن لا يعلم تاريخها فيعمل بالخاص أيضًا. وتقدم فيما نقل عن أبي حنيفة (رضي الله عنه) فيه.
وإن كان عامًا من وجه خاصًا من وجه، فلا يقدم أحدهما على الآخر إلا بمرجح.','3','383',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (928,'قال ابن دقيق العيد: وكأن مرادهم الترجيح الذي لا يخص مدلول العموم كالترجيح بكثرة الرواة، وسائر الأمور الخارجية عن مدلول العموم من حيث هو.
وبهذا التحرير علم ما في كلام المصنف، ثم استدل على مختاره بقوله: لنا إعمال الدليلين أولى.
بيانه: أنا إذا عملنا بالخاص وقدمناه على العام، وخصصنا العام به، فقد أعملنا الدليلين، أما الخاص فواضح.
وأما العام ففي بعض ما دل العام عليه.
وإذا لم نجعله مخصصًا للعام، بل جعلناه منسوخًا بالعام المتأخر أغلينا الخاص بالكلية، ولا شك أن إعمال الدليلين - ولو من وجه - أولى.
انتهى بحمد الله تعالى الجزء الثالث، ويتلوه إن شاء الله تعالى الجزء الرابع، وأوله:
الثانية','3','384',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (929,'الثانية:
يجوز تخصيص الكتاب به أي بالكتاب، ويجوز تخصيص الكتاب بالسنة المتواترة ويجوز تخصيص الكتاب بالإجماع، لتضمن الإجماع نصًا مخصصًا، ويجوز تخصيص السنة المتواترة بالسنة المتواترة.
وقوله: كتخصيص قوله تعالى: {والمطلقات يتربصن بأنفسهن ثلاثة قروء}.
(بقوله: {وأولات الأحمال} .. الآية مثال لتخصيص الكتاب بالكتاب.
فتكون عدة الحامل بوضع الحمل، فيخص عموم: {والمطلقات','4','5',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (930,'يتربصن بأنفسهن ثلاثة قروء}.
والتخصيص إنما هو بالآية لا بدليل آخر؛ لأن الأصل عدم الغير.
وقد يقال: إنه إن أريد بالتخصيص أن وجوب العدة بالأقراء مقصور على غير الحامل، فمما لا نزاع فيه.
وكونه بغير طريق النسخ، بل بالتخصيص فلا دلالة عليه.
ومقتضى تفصيل الحنفية وبعض أدلة الشافعية أن المخصص يكون خاصًا ألبتة وليس بلازم. فإن أولات الأحمال عام، نعم يكون خاصًا بمعنى كونه متناولًا، لبعض ما يتناوله العام.
ومثال تخصيص الكتاب بالسنة المتواترة، قوله تعالى: {يوصيكم الله} الآية.','4','6',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (931,'فإنه مخصوص بقوله -عليه الصلاة والسلام -: «القاتل لا يرث».
رواه مالك والنسائي والترمذي (وابن ماجة).
وفيه نظر: فإنه غير متواتر اتفاقًا.
بل قال الترمذي: إنه لم يصح).','4','7',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (932,'لكن قال البيهقي: له شواهد تقويه.
وأجاب القرافي عما اعترض به من عدم التواتر بأن زمن التخصيص هو زمن الصحابة، وقد كان الحديث، إذ ذاك متواترًا.
قال: وكم من قضية كانت متواترة في الزمن الماضي، ثم صارت آحادًا بل ربما نسيت بالكلية.
ومثال تخصيص الكتاب بالسنة الفعلية، قوله تعالى: {(الزانية والزاني) فاجلدوا} فإنه مخصوص برجمه -صلى الله عليه وسلم -المحصن. متفق عليه من حديث أبي هريرة (رضي الله عليه) في','4','8',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (933,'قصة ماعز (رضي الله عنه).
وفيه نظر: لجواز أن يكون التخصيص إنما هو بالآية التي نسخت تلاوتها وبقي حكمها، وهي: «الشيخ والشيخة إذا زنيا فارجموهما».
ومثال تخصيص الكتاب بالإجماع: تنصيب حد القذف على العبد، فإنه ثابت بالإجماع، فكان مخصصًا لعموم قوله تعالى: {والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين','4','9',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (934,'جلدة}. وفي الإجماع نظر: لأن مالكًا -رضي الله عنه -حكى في الموطأ عن عمر بن عبد العزيز: أن العبد يجلد في القذف ثمانون كالحر.
الثالثة
يجوز تخصيص الكتاب والسنة المتواترة بخبر الواحد، وبه قال الجمهور','4','10',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (935,'ومنع ذلك قوم مطلقًا.
ومنع عيسى بن أبان من الحنفية فيما لم يخصص بمقطوع، فإن خص بدليل قطعي جاز؛ لأنه يصير مجازًا بالتخصيص فتضعف دلالته.
ومنع الكرخي فيما لم يخصص بمنفصل، سواء خص بمتصل أو لم يخص أصلًا فإن خص بمنفصل جاز.
وحكاية المصنف الخلاف المتقدم في تخصيص السنة المتواترة بالآحاد، تابع فيه القاضي أبا بكر فله فيه سلف.','4','11',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (936,'وقال ابن السمعاني: محل الخلاف في خبر الواحد الذي لم يجمعوا على العمل به.
فإن أجمعوا على العمل به خص الكتاب به بلا خلاف كالمتواتر.
وفيه نظر: فإن الدال على التخصيص حينئذ الإجماع.
وفي النقل عن الكرخي نظر مبين في الأصل.
لنا: على جواز تخصيص الكتاب والسنة المتواترة بخبر الواحد، أن فيه أعمالًا للدليلين.
أما الخاص فمن جميع وجوهه، أي في جميع ما دل عليه.
وأما العام فمن وجه دون وجه، أي في الأفراد التي سكت عنها الخاص.
وإعمال الدليلين ولو من وجه أولى من إهمال أحدهما؛ لأن الأصل في الدليل الإعمال، فإذا أعملنا العام مطلقًا بقي الخاص مهملًا.','4','12',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (937,'قيل: قال عليه السلام: «إذا روي عني حديث فاعرضوه على كتاب الله (تعالى) فإن وافق فاقبلوه وإن خالف فردوه».
وخبر الواحد المعارض لكتاب الله (تعالى) مخالف له، وذلك ظاهر فوجب رده، وإذا وجب رده، لم يجز تخصيص الكتاب به، وهو خاص بالكتاب دون السنة المتواترة والمدعى عام.
قلنا: منقوض بالمتواتر، أي يجوز تخصيص الكتاب بالسنة المتواترة باتفاق مع مخالفتها للكتاب.
هذا والحديث روي بمعناه، من طرق كلها ضعيفة، قاله الزركشي.
والعراقي قال: وقول شيخنا جمال الدين: غير معروف، عجيب مع كثرة هذه الطرق، وأشار إلى رواية الدارقطني له بمعناه من حديث','4','13',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (938,'علي والبيهقي في المدخل','4','14',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (939,'وابن عمر وثوبان رواهما الطبراني.','4','15',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (940,'وإلى رواية أبي يعلى الموصلي في مسنده، والهروي في ذم الكلام، والبيهقي في طرق أخرى كلها ضعيفة.
قال الخطابي: باطل لا أصل له.
وكذا قال البيهقي.
قال بعضهم: لو صح فمعناه «اعرضوه» أي: وتجدوه غير مخالف','4','16',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (941,'للكتاب.
قال الله تعالى: {وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا}.
قيل: العام وهو الكتاب أو السنة المتواترة قطعي، والخاص وهو خبر الواحد ظني، والظن لا يعارض القطع.
قلنا: العام مقطوع المتن، أي اللفظ؛ لأنه بلغ إلى الرسول قطعًا، مظنون الدلالة؛ لأنه يحتمل التخصيص وغيره.
والخاص بالعكس، أي: مظنون المتن لأنه خبر الواحد قطعي الدلالة، إذ لا تحتمل الأفراد الباقية، بل لا يحتمل إلا ما يفرض له، بخلاف العام فكان لكل قوة من وجه فتعادلا فجاز التخصيص، ولا يلزم التوقف.','4','17',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (942,'لأن إعمال الدليلين يرجح التخصيص.
وضعف الأصفهاني هذا الجواب بأن خبر الواحد يحتمل المجاز والنقل وغيرهما مما يمنع القطع.
غاية ما في الباب، أنه لا يحتمل التخصيص، وذلك لا ينفع.
نعم يمكن أن يدعى قوة دلالة الخاص على مدلوله، فإنها أقوى من دلالة العام.
قيل: لو خصص خبر الواحد، الكتاب أو السنة المتواترة لنسخ، أي الكتاب والسنة المتواترة، لكنه لا يجوز اتفاقًا.
والجامع أن النسخ رفع كما أن التخصيص رفع.
قلنا: لا نسلم أن النسخ رفع، بل بيان كما سيجيء.
سلمنا أنه رفع، لكن التخصيص أهون من النسخ؛ لأن النسخ أقوى لأنه رفع عن الكل، والتخصيص رفع عن البعض، ولا يلزم من','4','18',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (943,'تأثير الشيء في الأضعف تأثيره في لأقوى.
واقتصر على الجواب الثاني إرخاء للعنان.
فائدة: يجوز تخصيص السنة بالقرآن، مثاله: حديث «ما أبين من حي فهو ميت» رواه ابن ماجة.
فإنه عام خص بقوله تعالى: {ومن أصوافها} الآية.
وقوله: «وبالقياس» عطف على قوله: بخبر الواحد، أي يجوز','4','19',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (944,'تخصيص الكتاب والسنة المتواترة، بخبر الواحد، وبالقياس، ولا خلاف في أن القياس إذا كان قطعيًا، أنه يخص به.
كما قاله ابن الأنباري شارح البرهان وغيره.
وأما المظنون فاختلف فيه:
والمختار جواز تخصيص الكتاب والسنة المتواترة به.
وهو المنقول عن الأئمة الأربعة والأشعري.','4','20',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (945,'مثل أن يعم قوله تعالى: {خذ من أموالهم صدقة}.
المديون وغيره، فيخص المديون منه قياسًا على الفقير.
ومنع أبو على الجبائي: التخصيص وقال: يقدم العام مطلقًا جليًا كان القياس أو لا، ومخصوصًا كان العام أو لا.
واختاره في المعالم مع اختياره الأول في المحصول.
وشرك ابن أبان في جواز التخصيص بالقياس التخصيص للعام أولًا بنص، وإلا فلا يجوز.
وشرط الكرخي التخصيص بمنفصل حتى يجوز بعد ذلك التخصيص بالقياس.','4','21',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (946,'فإن لم يوجد دليل منفصل خص العام أولًا، فلا يجوز التخصيص بالقياس.
واعتبر الحجة، أي حجة الإسلام وهو الإمام الغزالي -رضي الله تعالى عنه -أرجح الظنين، يعني أن العام وإن كان مقطوع المتن، لكن دلالته ظنية كما مر، والقياس أيضًا دلالته ظنية، وحينئذ فإن تفاوتا في الظن فالعبرة بأرجح الظنين، وإن تساويا فالوقف.
قال العراقي: قال الإمام والأصفهاني، والهندي، إنه حق.
وأجاب الغزالي عما استدل به المصنف مما تقدم من إعمال الدليلين بمنع ذلك هنا، بل هو رفع العموم وتجريد العمل بالقياس، واستحسنه بعض المتأخرين.
وتوقف القاضي أبو بكر، وإمام الحرمين، في كتبه الأصولية.','4','22',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (947,'أي توقفا في القدر الذي تعارضا فيه، لكن في النهاية: يخص الظاهر بالقياس الجلي، إذا كان التأويل لا ينبو عن النص، بشرط أن يكون القياس صدر من غير الأصل، الذي ورد فيه الظاهر.
فإن لم يتجه قياس من غير مورد الظاهر لم يجز إزالة الظاهر، بمعنى مستنبط منه يتضمن تخصيصه، وقصره على (بعض المسميات.
واختار الآمدي أن على القياس إن كانت ثابتة بنص أو إجماع؛ جاز التخصيص وإلا فلا.
واختار ابن الحاجب: أنه ثبت علية العلة بنص أو إجماع.
أو كان الأصل مخصصًا للعام -أي مخرجًا عنه -خص به العام وإلا) فالمعتبر القرائن في آحاد الوقائع مما يظهر بها ترجيح أحدهما.
فإن ظهر ترجيح خاص بالقياس عمل به وإلا عمل بعموم الخبر.
لنا: على جواز التخصيص بالقياس مطلقًا، ما تقدم، أي في خبر الواحد.','4','23',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (948,'وهو أن إعمال الدليلين ولو من وجه أولى.
قيل: القياس فرع عن النص؛ لأن الحكم المقاس عليه لا بد وأن يكون ثابتًا بالنص؛ لأنه لو كان ثابتًا بالقياس لزم الدور أو التسلسل، فلو خص به لكان مقدمًا على أصله.
وإليه أشار بقوله: «فلا يتقدم».
قلنا: لا يقدم على أصله لكن إذا خص العموم به لم يقدم على أصله، وإنما قدم على أصل آخر.
قيل: مقدماته، أي القياس أكثر من مقدمات الخبر، فيكون أضعف، فلا يقدم على الأقوى، وإنما قلنا: إن مقدماته أكثر؛ لأنه','4','24',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (949,'يبحث فيه عن ستة أمور: حكم الأصل: وعلته، ووجودهما فيه، وخلوها عن المعارض فيه، والسند، والدلالة.
والخبر يبحث فيه عن شيئين:
السند، والدلالة.
قلنا: قد يكون بالعكس، أي تكون مقدمات العام أكثر من مقدمات القياس المخصص، إما بأن يكون النص الذي هو أصل القياس أعلى رتبة بكثير من النص العام المعارض للقياس، أو يكون النص المعارض له موقوفًا على حذف أو إضمار، مما يكثر','4','25',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (950,'المقدمات، دون النص المثبت لأصل القياس، فلا يلزم ما ذكرتم.
ومع هذا فإعمال الكل أحرى.
يعني لو سلم أن الظن الحاصل من العام أقوى، لكن مع ذلك العمل بالتخصيص أولى، إعمالًا للدليلين حتى لا يلزم إلغاء أحدهما ب الكلية.
هذا مع تجويزه تخصيص الكتاب بالسنة، وتخصيص المفهوم بمنطوق الكتاب والسنة، مع أنه أضعف، ودليله بعينه جار فيه.','4','26',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (951,'الرابعة','4','27',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (952,'يجوز تخصيص المنطوق بالمفهوم، سواء فيه مفهوم الموافقة، ومفهوم المخالفة.
واقتصر على مثال مفهوم المخالفة لأنه أضعف.
فإذا صلح مخصصًا فمفهوم الموافقة أولى؛ لأنه متفق عليه.
يسميه بعضهم: دلالة تضمن، وبعضهم القياس الجلي.','4','28',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (953,'واستدل على مختاره بقوله: لأنه دليل أي شرعي؛ لأنه عارض مثله، فكان العمل به جمعًا بين الأدلة، فوجب كسائر الأدلة.
ثم مثل له بقوله: كتخصيص ما روي عنه (عليه الصلاة والسلام) أنه قال: «خلق الله الماء طهورًا لا ينجسه شيء إلا ما غير طعمه أو لونه أو ريحه». بمفهوم قوله -صلى الله عليه وسلم -: «إذا بلغ','4','29',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (954,'الماء قلتين لم يحمل خبثًا».
فمنطوق الحديث الأول: هو أن عدم تنجيس الماء يعم القليل والكثير بدون التغير.
ومفهوم الثاني: خصصه بالكثير، لدلالة الشرط على أنه إذا لم يبلغ القلتين يحمل الخبث سواء تغير أم لا.
وإنما لم يعكس ويجعل الأول مخصصًا لهذا المفهوم.
بحال التغيير، لأنه حينئذ لا يبقى للشرط فائدة.
والحديث الأول شطره وهو قوله: «خلق الله الماء طهورًا لا ينجسه شيء» صحيح.
وقوله: «إلا ما غير طعمه أو لونه أو ريحه» ضعيف.
رواه ابن ماجة والبيهقي واتفقوا على ضعفه.','4','30',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (955,'والحديث الثاني تقدم تخريجه.
ومثال التخصيص بمفهوم الموافقة إذا قال: من دخل داري فاضربه، وقال: إن دخل زيد فلا تقل له: أف.
الخامسة
العادة الفعلية التي قررها رسول الله -صلى الله عليه وسلم -تخصيص للعموم.
مثاله: إذا كانت عادتهم أكل طعام مخصوص، وهو البر مثلًا','4','31',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (956,'فورد النهي عن بيع الطعام بجنسه متفاضلًا.
قال أبو حنيفة (رضي الله عنه) يختص النهي بالبر؛ لأنه المعتاد.
وخالفه الجمهور: فقالوا: بإجراء العموم على عمومه.
كذا نقل الآمدي، وابن الحاجب.
وفي المحصول: الحق أنها -أي العادة -إن كانت موجودة في عصره -صلى الله عليه وسلم -وعلم بها، وأقرها، فإنها تكون مخصصة، ولكن المخصص في الحقيقة هو التقرير.
وإن لم يكن بهذه الشروط، فإنها لا تخص؛ لأن أفعال الناس (لا تكون حجة) على الشرع.
نعم إن أجمعوا على التخصيص لدليل آخر فلا كلام.
وهذا التفصيل هو ظاهر كلام المصنف كما ترى. وهو في','4','32',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (957,'الحقيقة موافق لما نقله الآمدي عن الجمهور. فإنهم يقولون: إن العادة بمجردها لا تخصص، وأن التقرير يخصص.
وعلى هذا فالمراد من قول الجمهور: أن العادة لا تخصص، أن غير المعتاد يكون ملحقًا بالمعتاد في الدخول.
والمراد من قول الإمام الرازي أن العادة التي قررها الرسول -صلى الله عليه وسلم -تخصص، أن المعتاد يكون خارجًا عن غير المعتاد فهما مسألتان.
وكذا العادة القولية تخصص العموم، كما نص عليه الغزالي والآمدي ومن تبعه؛ لأن الحقيقة العرفية مقدمة على اللغوية.
وتقريره -صلى لله عليه وسلم -لفعل المكلف الذي علم','4','33',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (958,'به، على مخالفة العام تخصيص له، أي للمكلف. بمعنى أن الحكم العام لا يثبت في حقه؛ لأن سكوته -صلى الله عليه وسلم -دليل جواز الفعل إذا علم من عادته أنه لو لم يكن جائزًا لما سكت عن إنكاره. وإذا ثبت أنه دليل الجواز وجب التخصيص به جمعًا بين الدليلين كغيره.
فإن ثبت ما روي عنه -صلى الله عيله وسلم -أنه قال: «حكمي على الواحد حكمي على الجماعة». فحينئذ يرفع حكم العام عن الباقين أيضًا يكون ذلك نسخًا لا تخصيصًا.
قال ابن الحاجب: وكذا إن لم يثبت، ولكن ظهر معنى يقتضي جواز ذلك، فإنا نلحق بالمخالف من وافقه في ذلك المعنى.
وهذا إذا بين معنى هو العلة. وأما إذا لم يبين، فالمختار أنه لا يتعدى إلى غيره لتعذر دليله.','4','34',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (959,'وهذا الحديث قيل: سئل عنه الحافظ المزي والذهبي فقالا: إنه غير معروف. لكن نقل الإجماع على أن معناه ثابت.
وروى الترمذي: والنسائي عن النبي -صلى الله عليه وسلم -أنه قال: «ما قولي لامرأة واحدة إلا كقولي لمائة امرأة» لفظ النسائي.
وقال الترمذي: «إنما قولي لمائة امرأة كقولي لامرأة واحدة». وقال: حسن صحيح.','4','35',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (960,'وفي هذا إشارة إلى أن هذا الحديث قد يؤدي معنى الحديث الذي ذكره المصنف.
تنبيه: قال الآمدي: لا فرق في دلالة التقرير على الجواز، بين أن يكون الشخص عالمًا بسبق التحريم أم لا، وإلا كان فيه تأخير البيان عن وقت الحاجة.
ثم قال هو، وابن الحاجب: يشترط أن يكون -صلى الله عليه وسلم -قادرًا على الإنكار، (وأن لا) يعلم من الفاعل الإصرار على ذلك الفعل، واعتقاده الإباحة كتردد اليهود على كنائسهم.
السادسة
خصوص السبب لا يخصصه،','4','36',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (961,'فإذا ورد العام على سبب خاص لم يختص الحكم بذلك السبب، بل العبرة بعموم اللفظ لا بخصوص السبب، وبه قال الأكثرون. ونص عليه الشافعي (رضي الله عنه) في الأم.','4','37',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (962,'ونقل عن مالك -رضي الله تعالى عنه.
واختاره الإمام الرازي والآمدي وابن الحاجب أنه لا يعارضه يعني أن اللفظ عام، والعمل به واجب.
وخصوص السبب لا يصلح معارضًا، إذ لا منافاة قطعًا.
وإنما لم يخصص السبب عنه بالاجتهاد للقطع بدخوله في الإرادة.
ولا بعد أن يدل دليل على إرادة خاص، فيصير كالنص فيه والظاهر في غيره.','4','38',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (963,'قال العراقي: محل هذه المسألة إذا كان العام مستقلًا بنفسه دون السبب، وكان أعم من السبب.
وكان عمومه في المسئول عنه.','4','39',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (964,'ثم فصل ذلك وبسطه، وهنا أبحاث حسنة في الشرح.
وكذا مذهب الراوي لا يكون مخصصًا العموم.
وإن كان هو الراوي، وبه قال الجمهور.','4','40',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (965,'ونقله في المحصول عن الشافعي - (رضي الله عنه).
قال: بخلاف حمل الخبر على أحد محمليه.
فإن الشافعي (رحمه الله تعالى) يأخذ فيه بمذهب الراوي.
كحديث أبي هريرة رضي الله عنه -أن النبي -صلى الله عليه وسلم -قال: «إذا شرب الكلب في إناء أحدكم فليغسله سبعًا» متفق عليه.
وعمله في الولوغ بخلافه، إذا روى عنه الطحاوي،','4','41',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (966,'والدارقطني أنه قال: «إذا ولغ الكلب في الإناء فأهرقه، ثم اغسله ثلاث مرات».
وفي مسنده عبد الملك بن أبي سليمان، قال البيهقي: تفرد به من بين أصحاب عطاء.
وحكى في الخلافيات عن الدارقطني أن الصحيح عن أبي هريرة العمل بما روي فلا يؤخذ بمذهبه؛ لأنه ليس بدليل، إذ مذهب الصحابي (رضي الله عنه) ليس بحجة، فلا يجوز تخصيص العموم الذي هو','4','42',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (967,'حجة به، وإلا ترك الدليل لا لدليل وأنه غير جائز.
وفي هذا المثال نظر، فإنه لم يصح، وعلى تقدير صحته ليس من باب العموم، فإن العدد نص لا عموم فيه. والتخصيص فرع العموم.
وقرره الباجي بأن لفظ الكلب مفرد معرف بأل فهو عام','4','43',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (968,'يشمل كلب الزرع وغيره.
وأبو هريرة -رضي الله عنه -يرى الاقتصار في كلب الزرع على ثلاث.
وقال العراقي: لا تعرف هذه التفرقة عن أبي هريرة -رضي الله عنه.
فالأحسن أن يمثل بما روت عائشة -رضي الله عنها - (أن النبي صلى الله عليه وسلم) قال: «أيما امرأة نكحت بغير إذن وليها نكاحها باطل».','4','44',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (969,'وصح أنها زوجت حفصة بنت أخيها عبد الرحمن حين كان غائبًا، هكذا قيل، وهي مناقشة في المثال.
وقيل: إذا عمل الراوي بخلاف ما روى، فإنه يؤخذ بما روى؛ لأنه خالف الدليل، وإلا انقدحت روايته، إذ لو لم يخالف ما رواه لدليل لكانت روايته باطلة، وهو خلاف الإجماع، فيعتبر ذلك','4','45',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (970,'الدليل و (إن لم) يعرف بعينه ويخصص به جمعًا بين الدليلين.
قلنا: إنما يستدعي مخالفته دليلًا في ظنه، (وما ظنه) المجتهد دليلًا لا يكون دليلًا على غيره ما لم يعلم بعينه مع وجه دلالته، لأنه ربما ظنه دليلًا مخالفًا للعام، ولم يكن دليلًا في نفس الأمر فلا يجوز لغيره إتباعه في اعتباره والتخصيص به؛ لأنه تقليد من مجتهد، وأنه لا يجوز.
فإن دفع هذا الجواب بأن دليله يجب أن يكون قطعيًا، إذ لو كان ظنيًا لبينه دفعًا للتهمة فيعارض بمثله.
وأيضًا لو كان قطعيًا لم يخف على غيره عادة، ولم يجز مخالفة صحابي آخر له، وأنه جائز اتفاقًا.
تنبيه: قال إمام الحرمين: الخلاف في الصحابي وغيره. وهو مقتضى كلم ابن العربي في شرح الترمذي.','4','46',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (971,'وقال القرافي: الذي أعتقده أن الخلاف مخصوص بالصحابي.
السابعة
إفراد فرد من أفراد العام لا يخصص ذلك العام، إذا لم يكن للخاص مفهوم يعتد به خلافًا لأبي ثور.
أما إذا كان للخاص مفهوم يعتد به يقتضي نفي الحكم عن غيره من أفراد العام، فإنه يخصص ولم يتعرض له المصنف هنا اعتمادًا على ما سبق أن العام يختص بالمفهوم.','4','47',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (972,'فالأول مثل قوله -صلى الله عليه وسلم - «أيما إهاب دبغ فقد طهر» رواه النسائي والترمذي وابن ماجة عن ابن عباس (رضي الله عنهما) مرفوعًا. وقال الترمذي حسن صحيح.
ومن عزاه إلى رواية مسلم فقد وهم، كما قاله الزركشي معقوله -صلى الله عليه وسلم -في شاة ميمونة «دباغها','4','48',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (973,'طهورها».
قال الإسنوي: كذا ذكر المصنف.
وفي الصحيحين عن ابن عباس (رضي الله عنهما).
قال: وجد النبي -صلى الله عليه وسلم -شاة ميتة أعطتها مولاة لميمونة من الصدقة فقال النبي -صلى الله عليه وسلم - «هلا','4','49',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (974,'انتفعتم بجلدها» قالوا: إنها ميتة، قال (صلى الله عليه وسلم): «إنما حرم أكلها».
وحاصله أنه يعم طهارة كل إهاب ولا يختص بالشاة.
قال العراقي: لكن الحديث الذي أورده المصنف، رواه البزار في مسنده والطبراني في معجمه الكبير، والبيهقي في الخلافيات عن ابن عباس - (رضي الله عنهما) -ماتت شاة لميمونة فقال النبي -صلى الله عليه وسلم -: «أفلا استمتعتم بإهابها، فإن دباغ الأدم طهوره».','4','50',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (975,'لأنه -أي الخاص -غير مناف للعام وكان هو الموجب للتخصيص، لأنه إذا تعارضا تعذر العمل بهما من كل وجه، فيصار إلى العمل بهما من وجه، وإذا لم يتعارضا فيجب العمل بهما من كل وجه، من غير تخصيص عملًا بالمقتضى السالم عن المعارض.
قيل: من جهة أبي ثور: المفهوم مناف؛ لأن تخصيص الشاة بالذكر يدل بمفهومه على نفي الحكم عما عداه، وقد ذكرتم: «أن المفهوم يخصص العموم، ومفهوم الخاص نفي الحكم عن سائر صور العام، فوجب أن يخصصه.
قلنا: مفهوم اللقب مردود، كما قرر في موضعه.
وفي هذا الجواب ما يعرفك تخصيص كلام المصنف بالخاص الذي لا مفهوم له معتد به كما مر.','4','51',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (976,'الثامنة
عطف الخاص على العام لا يخصص ذلك العام مثل قوله -صلى الله عليه وسلم -: «ألا لا يقتل مسلم بكافر، ولا ذو عهد في عهده» رواه النسائي.
وهو في البخاري إلى قوله: «بكافر».','4','52',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (977,'وقال بعض الحنفية: بالتخصيص، تسوية بين المعطوفين، فقالوا: لا يقتل مسلم بكافر، ولا ذو عهد في عهده بكافر، تسوية بين المعطوف والمعطوف عليه في المعمولات.
والكافر الذي لا يقتل به الذمي هو الحربي، فيكون الكافر الذي لا يقتل به المسلم الحربي أيضًا. ولذا يقتلون المسلم بالذمي.
والحاصل: أنهم يدعون أن في مثل هذا العطف عموم المعطوف عليه.
وعلل الشافعية: لا حاجة إلى تقدير «بكافر» آخر؛ لأن قوله (صلى الله عليه وسلم): «ولا ذو عهد في عهده» كلام تام، أي: لا يقتل مسلم بكافر ذميًا كان أو حربيًا، ولا يقتل ذو عهد ما دام بعهده غير ناقضه.
فبقي قوله: بكافر على عمومه للحربي والذمي؛ لأنه نكرة في سياق النفي.','4','53',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (978,'قلنا: جوابًا عن قولهم: تجب التسوية بين المعطوف والمعطوف عليه في المعمولات [و] أن التسوية في جميع الأحكام غير واجبة.
والاعتراض على المصنف بأن هذا مناقض لما تقدم له.
مع أن الحنفية لا يقولون به.
وفيه نظر: وتقدم لك الكلام في جوابه مرات.
وفي هذا المثال نظر.
أما عند أصحابنا، فلأنه إذا لم يقدر «بكافر» آخرًا، لا يكون من عطف الخاص على العام، لأنه كلام برأسه كما مر.
وأما الحنفية فلأنه وإن قدر «بكافر» آخرًا، وإلا أنه يكون «بكافر» في الجملة الثانية أخص من كافر أولًا.
وهي ليس من عطف الخاص على العام؛ لأنه لا يلزم من عطف','4','54',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (979,'الجملة عطف المفرد.
فائدة: قال القفال الشاشي: عطف العام على الخاص لا يقتضي تخصيص العام.
التاسعة
إذا ذكر عام وبعده ضمير خاص يرجع إلى بعض ما يتناوله.
(فالأكثر أنه لا يكون تخصيصًا له، واختاره الغزالي والآمدي وابن الحاجب).
وإليه أشار المصنف بقوله: «عود ضمير خاص لا يخصص».','4','55',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (980,'وحكى القرافي عن الشافعي (رضي الله عنه) تخصيصه به وتقل عن أكثر الحنفية.
قال العراقي: (وفيه أي في) نقله عن الشافعي (رضي الله عنه) نظر، فإن فروع مذهبه تدل على موافقة الجمهور.
وقيل: بالوقف مثل قوله تعالى: {والمطلقات يتربصن بأنفسهن ثلاثة قروء}. مع قوله تعالى: {وبعولتهن أحق بردهن}.
والضمير في «بردهن» للرجعيات فقط، لأن البائن لا يملك','4','56',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (981,'الزوج ردها، فلا يوجب تخصيص التربص بالرجعيات، بل يعم الرجعيات والبائنات.
لأنه، أي الضمير الخاص، لا يزيد على إعادته، أي: على إعادة العام المتقدم.
ولو أعيد فقيل: وبعولة المطلقات أحق برد المطلقات، لم يكن تخصيصًا اتفاقًا.
ويحتمل أن يكون الضمير في قوله: «على إعادته عائد على البعض الخاص.
قال الإسنوي: وهو ما فهمه كثير من الشراح، ويعني بذلك أنه لو قيل: وبعولة الرجعيات أحق بردهن، لم يكن مخصصًا لما قبله، فبالأولى ما قام مقامه.
قال: والأول أصوب لتعبيره بالإعادة دون الإظهار، ولأنه','4','57',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (982,'أبلغ لكون الأول بعينه قد أعيد، ولم يلزم منه التخصيص، وجعل الضمير في «وبعولتهن».
قال: وللخصم أن يقول: الضمير يزيد على إعادة الظاهر؛ لأن الظاهر مستقل بنفسه، فينقطع معه الالتفات إلى الأول بخلاف المضمر.
فائدة: لو ورد بعد العام حكم لا يأتي إلا في بعض أفراده، كان حكمه كحكم الضمير صرح به في المحصول.
تذنيب: لما كان المطلق عامًا عمومًا بدليًا، والمقيد أخص منه','4','58',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (983,'كان تعارضهما من باب تعارض العام والخاص.
وتجري الأحكام المتقدمة هنا اتفاقًا واختلافًا، فلذا ذكره في بابه.
المطلق والمقيد إن اتحد سببهما، حمل (المطلق عليه، وظاهره سواء كانا مثبتين أو منفيين، اتحد حكمهما أو اختلف) عملًا بالدليلين، وإلا، أي وإن لم يتحد سببهما، فإن اقتضى القياس تقييده أي: تقييد المطلق به. وإلا، أي: وإن لم يقتض القياس تقييد المطلق فلا يقيد المطلق بالمقيد.
هذا ما يعطيه كلام المصنف.
والتحقيق أن يقال: المطلق والمقيد إن اتحد حكمهما وسببهما، وكان مثبتين، كتقييد الرقبة في كفارة القتل في موضع وإطلاقها فيه في موضع آخر.
فإن تأخر المقيد عن وقت العمل بالمطلق، فهو ناسخ، وإن تقدم','4','59',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (984,'عليه أو تأخر عنه لا عن وقت العمل، فالراجح حمل المطلق عليه جمعًا بين الدليلين ويكون المقيد بيانًا للمطلق، أي: بين أنه المراد منه، وحكى الآمدي وغيره الاتفاق على هذا،
وفيه نظر مبين في الشرح.
وإن اتحدا حكمًا وسببًا، وكانا منفيين نحو: لا تعتق مكاتبًا، ولا تعتق مكاتبًا كافرًا.
فيجوز إعتاق المكاتب المسلم، وبهذا صرح الإمام الرازي.
ومن لا يقول بالمفهوم يعمل بالإطلاق، ويمنع إعتاق المكاتب.
وبهذا قال ابن الحاجب تبعًا للآمدي.
وهو من باب الخاص والعام لكونه نكرة في سياق النهي لا من المطلق والمقيد كما توهم. وإن اتحد حكمهما وسببهما، وكان أحدهما أمرًا','4','60',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (985,'والآخر نهيًا، كأن يقال: أعتق رقبة، ويقول: لا تملك رقبة كافرة، فلا يعتق كافرة لتوقف الإعتاق على الملك. وتقييد المطلق بضد الصفة التي هي الكفر، وهي الإيمان وليس من حمل المطلق على المقيد.
وإن اختلف السبب واتحد الحكم كإطلاق رقبة في كفارة الظهار وتقييدها بالإيمان في القتل.
فقال أبو حنيفة: لا يحمل عليه أصلًا. وقيل: يحمل عليه من جهة اللفظ. وحكي عن جمهور أصحابنا.','4','61',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (986,'وقال الماوردي والروياني وسليم: إنه ظاهر مذهب الشافعي - (رضي الله عنه).
وقيل: يحمل عليه من جهة القياس إن اقتضى ذلك، بأن يشتركا في المعنى، وبه جزم المصنف تبعًا للإمام الرازي','4','62',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (987,'والآمدي. ونقله الآمدي وغيره عن الشافعي - (رضي الله عنه).
وإن اختلف الحكم واتحد السبب، كآية الوضوء فإنه قيد فيها غسل اليدين إلى المرفقين، وأطلق في التيمم الأيدي، وسببهما واحد، وهو الحدث.
ففيها الخلاف في التي قبلها، ذكره الباجي وابن العربي وحكى القرافي عن أكثر الشافعة: حمل المطلق هنا على المقيد، لكن قال ابن الحاجب: إن اختلف حكمهما فلا يحمل أحمدهما على الآخر توجه اتفاقًا أي سواء اتحد السبب أو اختلف، واعلم أن محل حمل المطلق على','4','63',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (988,'المقيد إذا لم يكن هناك قيدان متنافيان.
فإن كان كذلك استغنى عن القيدين وسقطا، وتمسكنا بالإطلاق.
(هذا إذا) قلنا بالحمل من جهة اللفظ.
فإن قلنا به من جهة القياس حمل على (ما حمله عليه) أولى.
فإن لم يكن قياس رجع إلى أصل الإطلاق.','4','64',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (989,'<span data-type="title" id=toc-145>الباب الرابع: في المجمل والمبين</span>
المجمل لغة: المجموع.
واصطلاحًا: هو ما لم تتضح دلالته، أعني ما له دلالة، وهي غير واضحة.
وهو يتناول القول والفعل والمشترك والمتواطئ.
والمبين: مشتق من التبيين، وهو التوضيح لغة.','4','65',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (990,'فالمبين -بكسر الباء -هو الموضح، وبالفتح -الموضح - بفتح الضاد -والمبين -بالكسر -في الاصطلاح: الكاشف عن المراد من الخطاب.
والمجمل لا يتصور إلا في معان متعددة.
وفيه أي في الباب فصول:

<span data-type="title" id=toc-146>الفصل الأول: في المجمل</span>
وفيه أي في هذا الفصل، مسائل: أي بالهمزة، ويجوز تخفيفها وجعلها بين بين -أي: بين الهمزتين وبين الباء.
وأما التصريح بالياء، كما هو الدائر على الألسنة فخطأ قاله الفارسي في الإيضاح.
الأولى من المسائل:','4','66',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (991,'اللفظ إما أن يكون مجملًا بين حقائقه، أي: بين معان وضع اللفظ لكل منها، كقوله تعالى: {(والمطلقات يتربصن بأنفسهن) ثلاثة قروء}.
فإن القرء موضوع بإزاء حقيقتين هما الحيض والطهر.
أو يكون مجملًا بين أفراد حقيقة واحدة، مثل قوله تعالى: {إن الله يأمركم أن تذبحوا بقرة}.
فإن لفظ بقرة موضوع لحقيقة واحدة معلومة ولها أفراد.','4','67',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (992,'والمراد واحد معين منها، كما سيجيء إن شاء الله تعالى.
أو يكون مجملًا بين مجازاته، وذلك إذا انتفت الحقيقة أي ثبت عدم إرادتها، وتكافأت المجازات.
أي لم يترجح بعضها على بعض.
فإن لم يدل دليل على عدم إرادة الحقيقة تعين الحمل عليها ولا إجمال، إلا إذا عارضها مجاز راجح.
فإن فيها الخلاف المعروف، وتقدم للمصنف واختار التساوي.
فعلى اختياره هو أيضًا مجمل.
ولم يذكره هنا اكتفاء بما مر، وهنا نفائس في الشرح ينبغي','4','68',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (993,'الوقوف عليها.
فإن ترجح واحد من المجازات؛ لأنه أقرب إلى الحقيقة من المجاز الآخر، كنفي الصحة من قوله -صلى الله عليه وسلم -: «لا صلاة إلا بفاتحة الكتاب» متفق عليه.
«ولا صيام لمن لم يبيت الصيام من الليل» رواه الأربعة بلفظ: «من','4','69',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (994,'لم يبيت الصيام قبل الفجر فلا صيام له».
قال النسائي: الصواب عندنا موقوف، وهو واضح في الشرح فحقيقة هذا إنما هو الإخبار عن نفي ذات الصلاة والصوم، عند انتفاء الفاتحة والتبييت.
وهذه الحقيقة غير مراد الشارع قطعًا؛ لأنا نشاهد الذات قد تقع بدون ذلك فتعين الحمل على المجاز، وهو إضمار الصحة أو الكمال.','4','70',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (995,'وإضمار الصحة أرجح، لكونه أقرب إلى الحقيقة، لأنها نفي الذات كما مر، ونفي الذات يستلزم انتفاء جميع الصفات، فنفي الصحة أقرب إليه بهذا المعنى من نفي الكمال؛ لأنه لا يبقى مع نفي الصحة وصف بخلاف نفي الكمال، فإن الصحة تبقى معه.
واعترض على المصنف باعتراض مبين في الأصل.','4','71',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (996,'قال العراقي: (في مثل هذا) إذا ورد من الشارع ممنوع، لإمكان انتفاء الحقيقة الشرعية بانتفاء جزئها أو شرطها، فالحق أنه لنفي الحقيقة وهو المحكي عن الأكثرين واختاره الآمدي، وابن الحاجب.
وقول المصنف وهم مردود.
قال: وما ذكره من تقدير الحرج دون الحكم في: «رفع عن أمتي» يقتضي إيجاب الكفارة في حنث الناس.','4','72',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (997,'والأصح من قولي الشافعي - (رضي الله عنه) -خلافه.
أو يرجح من المجازات لأنه أظهر عرفًا، أو أعظم مقصودًا، كرفع الحرج، وتحريم الأكل من: «رفع عن أمتي الخطأ» و {حرمت عليكم الميتة}.
وقوله: «حمل عليه» جواب الشرط الأول، وهو قوله: «فإن ترجح واحد» يعني إذا كان أحد المجازات أظهر عرفًا، مثاله ما روى عنه -صلى الله عليه وسلم -أنه قال: «رفع عن أمتي الخطأ والنسيان وما استكرهوا عليه».','4','73',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (998,'هو حديث غريب أخرجه ابن عدي في الكامل عن أبي بكرة مرفوعًا بلفظ: «رفع الله عن هذه الأمة الخطأ والنسيان والأمر','4','74',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (999,'يكرهون عليه».
وللحديث طرق أخرى أخرجها ابن أبي حاتم في التفسير فيها مقال.
وله طريق أخرى جيدة، عن عطاء عن ابن عباس (رضي الله عنهما) مرفوعًا: «وضع الله عن أمتي الخطأ والنسيان وما استكرهوا عليه» وهو حديث حسن، كما قاله النووي في الروضة وغيره أخرجه ابن ماجة.
وأخرجه أبو القاسم الفضل بن جعفر التميمي في فوائده بلفظ','4','75',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1000,'(رفع بدل) «وضع».
قال شيخ الإسلام: ورجاله ثقات لكن فيه تسوية الوليد، فقد رواه بشر بن بكر عن الأوزاعي، فأدخل بين عطاء، وابن عباس، عبيد بن عمير.','4','76',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1001,'وأخرجه الطبراني والدارقطني من طريقه، بلفظ: «تجاوز» بدل «وضع».
وبمجموع هذه الطرق يظهر أن للحديث أصلًا.
فحقيقة اللفظ ارتفاع نفس الخطأ والنسيان، وهو باطل لاستحالة رفع الشيء بعد صدوره، فتعين حمله على المجاز، وهو الحكم أو الإثم.
والثاني أولى لظهوره عرفًا، فإن السيد إذا قال لعبده: رفعت عنك','4','77',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1002,'الخطأ، فهم منه نفي المؤاخذة والعقاب.
لا يقال فيسقط عنه الضمان إذا أتلف مال الغير؛ لأنه داخل في عموم العقاب وقد رفع.
ولا يسقط بالاتفاق؛ لأنه إنما لم يسقط الضمان.
أما أنه ليس بعقاب؛ إذ يفهم من العقاب ما يقصد به الإيذاء والزجر، وهذا يقصد به جبر حال المتلف عليه، ولذلك وجب الضمان على الصبي وأنه لا يعاقب.
وإما لتخصيص الخبر بدليل يدل عليه، والتخصيص لا يوجب إجمالًا.
أو كان أعظم مقصودًا مثل قوله تعالى: {حرمت عليكم الميتة}.
فإن حقيقته تحريم العين وهو باطل قطعًا.','4','78',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1003,'فإن الأحكام الشرعية لا تتعلق إلا بالأفعال المقدورة للمكلفين، والعين ليست من أفعالهم، فتعين الصرف للمجاز، بإضمار الأكل والبيع واللمس أو غيرها.
والأولى الأكل لكونه أعظم مقصودًا عرفًا، فحمل اللفظ عليه.
والمثالان الأخيران ذكرهما المصنف بطريق اللف والنشر.
تنبيه:
قال العراقي: مقتضى كلام المصنف أن الإجمال لا يكون في الفعل، لأنه قال: اللفظ إما أن يكون مجملًا، إلى آخره.
قال: وليس كذلك، فقد ذكر ابن الحاجب وغيره، أن قيامه - (عليه الصلاة والسلام) -من الركعة الثانية يحتمل كونه عن عمد، فيدل على جواز ترك التشهد الأول وكونه سهوًا، فلا يدل عليه.
وفيه نظر؛ لأن عدم العود إليه، يدل على أنه غير واجب، سواء ترك عمدًا أو سهوًا.','4','79',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1004,'قلت: وفي أصل الاعتراض نظر، والله أعلم.
واعترض ثانيًا: بأنه مخالف لكلامه في تقسيم الألفاظ حيث حصر هناك المجمل في المشترك، وصرح بأنه متحد اللفظ والمعنى ومتكثرهما، ومتكثر اللفظ دون المعنى نصوص، مع أن منها المتواطئ.
وقد ذكر هنا قسمين آخرين للمجمل: أحدهما: المتردد بين مجازات. ثانيهما: قسم من المتواطئ فتأمله.
الثانية
قالت الحنفية، أي بعضهم، {وامسحوا برءوسكم}:','4','80',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1005,'مجمل؛ لأنه يحتمل مسح جميع الرأس، ومسح البعض على السواء، وقد بينه -صلى الله عليه وسلم -بمسح ناصيته، ومقدارها الربع، فكان الربع واجبًا.
وقال المالكية: يقتضي مسح الكل؛ لأن الرأس حقيقة في الكل، فلا إجمال لا تضاحه.
والحق: أنه أي مسح الرأس حقيقة، فيما ينطلق عليه الاسم، أي اسم المسح، وهو القدر المشترك بين الكل والبعض؛ لأن هذا التركيب يأتي تارة بمسح الكل، وتارة بمسح البعض، كما يقال: مسحت يدي برأس اليتيم، ولم يمسح منه إلا البعض».
وقلنا: ذلك دفعًا للاشتراك والمجاز، إذ لو لم نجعله للقدر.','4','81',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1006,'المشترك، لكان لهما حقيقة، وهو الاشتراك أو لأحدهما حقيقة وهو المجاز، فحملناه على القدر المشترك، دفعًا لهما؛ لأنه أولى منهما كما مر».
قال العراقي: وهو مخالف لما جزم به في معاني الحروف من أن الباء في الآية للتبعيض، كما هو رأي بعض أصحابنا، والمذكور هنا هو مذهب الشافعي -رضي الله عنه.
الثالثة
قيل: آية السرقة مجملة؛ لأن اليد تحتمل الكل أي من','4','82',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1007,'رءوس الأصابع إلى المنكب.
وتحتمل البعض أيضًا؛ لأنها تطلق على كل واحد منهما.
والقطع يحتمل الشق، أي: الجرح، ويحتمل الإبانة: وهو فصل العضو، لأنه يقال لمن جرح يده بالسكين: قطع يده، والأصل في الإطلاق الحقيقة، فجاء الإجمال.
والحق أن اليد للكل حقيقة، وتذكر للبعض مجازًا، بدليل قولنا في البعض: إنه ليس كل اليد، فكان ظاهرًا فيه، فلا إجمال.
والقطع للإبانة حقيقة، والشق إبانة أيضًا؛ إذ هو فيه إبانة بعض أجزاء اللحم عن بعض، فيكون متواطئًا وفيه نظر، فإن المطلوب في اليد هو المجاز.
وأحد أفراد المتواطئ من غير بيان من القرآن، فجاء الإجمال.','4','83',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1008,'<span data-type="title" id=toc-150>الفصل الثاني: في المبين</span>
بفتح الياء من قولك: بينت الشيء، تبيينًا، أي أوضحته.
والبيان يطلق على فعل المبين، وهو التبيين.
وعلى ما حصل بن التبيين وهو الدليل، وعلى متعلق التبيين، ومحله، وهو المدلول.
وبالنظر إلى المعاني الثلاثة اختلف في تفسيره وهو مبين في','4','84',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1009,'الشرح.
وهو أي المبين: إما الواضح بنفسه لعدم توقف إفادته على غير لغة.
أو الواضح بغيره لتوقف إفادة المراد على الغير.
فالأول مثل قوله تعالى: {والله بكل شيء عليم} فإن إفادته شمول علمه جميع الأشياء باللغة.
والثاني مثل قوله تعالى: {واسأل القرية}.
فإن المراد منه سؤال أهل القرية، وهو غير واضح بنفسه؛ إذ اللغة لا تكفي في إثباته، بل العقل يوضحه ويبينه.','4','85',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1010,'فإن حقيقة هذه اللفظة من جهة اللغة إنما هو طلب السؤال من الجدران، والعقل صرفنا عن ذلك لاستحالته، وبين أن المراد به الأهل.
فيكون في كلام المصنف لف ونشر، كما شرحه به الجاربردي والعبري، والإسفرايني وغيرهم.
قال العراقي: وهو وهم وليس كذلك، بل هو مثال آخر للواضح بنفسه.
وكذا قال غيره، وهو ظاهر كلام المحصول.','4','86',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1011,'قال: ويجب حمل كلام المصنف عليه؛ لأنه قسم ذلك الغير في المسألة الآتية إلى القول والفعل فقط.
فلو كان مثالًا له لكان انحصاره في القسمين باطلًا؛ لأن المببين فيه ليس واحدًا منهما بل العقل.
وعلى هذا فالواضح بغيره: هو ما يتوقف فهم المعنى منه على انضمام غيره إليه.
فلو أخر المصنف قوله: أو بغيره، لكان أولى.
وذلك الغير، وهو الدليل الذي حصل به الإيضاح، يسمى مبينًا بكسر الياء، وله أقسام تأتي.
وفيه أي في المبين -بكسر الياء -مسألتان، وفي بعض النسخ وفيه مسائل ولعله جعل «التنبيه» الثالثة.
أو أطلق الجمع على الاثنين مجازًا.','4','87',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1012,'الأولى
أنه أي المبين -بكسر الياء -قد يكون قولًا من الله تعالى.
وقد يكون قولًا من الرسول -صلى الله عليه وسلم -وقد يكون فعلًا منه -صلى الله عليه وسلم.
فالأول كقوله تعالى: {صفراء فاقع لونها} فإنه بيان لقوله تعالى: {إن الله يأمركم أن تذبحوا بقرة}.
ومثال الثاني: قول عليه الصلاة والسلام: «فيما سقت السماء العشر» رواه أبو داود والنسائي،','4','88',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1013,'وهو بعض حديث في البخاري فإنه مبين للحق المذكور، في قوله تعالى: {وآتوا حقه يوم حصاده}.
ومثال الثالث: صلاته -صلى الله عليه وسلم -فإنه بيان لقوله تعالى: {وأقيموا الصلاة}.
فلهذا قال: عليه الصلاة والسلام: «صلوا كما رأيتموني أصلي»، رواه البخاري.','4','89',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1014,'وحجه -صلى الله عليه وسلم -فإنه بيان لقوله تعالى: {ولله على الناس حج البيت} ولهذا قال -عليه الصلاة والسلام -: «خذوا عني مناسككم» رواه مسلم.
وغير ذلك من أفعاله، صلى الله عليه وسلم.
وقيل: الفعل لا يكون بيانًا.','4','90',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1015,'فإنه أدل، يعني أن الفعل أوضح دلالة على المقصود، ولهذا روي عنه -صلى الله عليه وسلم -أنه قال: «ليس الخبر كالمعاينة».
أخرجه الإمام أحمد في مسنده، وابن حبان في صحيحه والطبراني في الأوسط.
فالمشاهدة أوضح، فإذا جاز بيانه بالقول، فبالفعل أولى.
فإن قلت: إنما حصل البيان بقوله -صلى الله عليه وسلم -: «صلوا كما رأيتموني أصلي» و «خذوا عني مناسككم» لا بالفعل.','4','91',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1016,'فالجواب أن البيان بالفعل، وذلك بيان كون الفعل بيانًا، لا أنه هو المبين.
قال القاضي في التقريب: فلو قال: القصد بما كلفتم بهذه الآية ما أفعله، ثم فعل فعلًا، فلا خلاف أنه يكون بيانًا.
وفي المحصول: يعلم كون الفعل بيانًا للمجمل (بأن يعلم ذلك بالضرورة من قصده، أو يقول: هذا الفعل بيانًا للمجمل).
أو بالدليل العقلي، وهو أن يذكر المجمل وقت الحاجة إلى العمل به، ثم يفعل فعلًا يصلح أن يكون بيانًا له.
ولا يفعل شيئًا آخر، فيعلم أن ذلك الفعل بيان له، وإلا لزم تأخير البيان عن وقت الحاجة.
وما يقال: إن الترك من الرسول -صلى الله عليه وسلم -قد','4','92',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1017,'يكون بيانًا، كتركه التشهد الأول، فإنه بيان لعدم وجوبه.
وهذا القسم لم يذكره المصنف.
فجوابه أن الترك داخل في قسم الفعل -كما تقدم -.
تنبيه:
كما انقسم المجمل: إلى المفرد والمركب، فكذا مقابله المبين.
قد يكون في مفرد، وقد يكون في مركب، وقد يكون في فعل.
وقد يكون فيما سبق له إجمال، وهو ظاهر، وقد يكون ولم يسبق إجمال.
فإن اجتمعا، أي القول والفعل، وتوافقا في الدلالة على حكم واحد، فالسابق هو المبين قولًا كان أو فعلًا لحصول البيان به، والثاني تأكيد له.','4','93',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1018,'ولا فرق في ذلك بين العلم بالسابق، وجهله على الأصح.
وإن اختلفا كما روي عنه (عليه الصلاة والسلام): «من قرن الحج إلى العمرة فليطف لهما (طوافًا واحدًا».','4','94',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1019,'مع ما روي أنه -صلى الله عليه وسلم -قرن فطاف لهما) طوافين.
فالقول هو المأخوذ به على الأصح تقدم أو تأخر، أو لم يعلم؛ لأنه يدل بنفسه، والفعل لا يدل إلا بواسطة أحد الأمور الثلاثة','4','95',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1020,'المتقدمة.
فإن قلت: كيف الجمع بين قوله: فإنه أدل، يعني الفعل.
وقوله: فالقول أنه يدل بنفسه.
أجيب: بأن الفعل أقوى من جهة المعاينة والمشاهدة.
والقول أدل لعدم احتياجه إلى الفعل.
وعبارة العراقي: الفعل أدل على الكيفية والقول أدل على الحكم.
تنبيه:
إذا كان المجمل معلومًا، فحكى القاضي أبو بكر عن الجمهور أنه يجوز أن يكون المبين له مظنونًا، واختاره هو والإمام الرازي.
وقيل: إن البيان يجب أن يكون أقوى دلالة من المبين.
وقال المحقق شرحًا لكلام ابن الحاجب: قد اختلف في وجوب زيادة قوة البيان على قوة المبين.','4','96',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1021,'والأكثر على وجوب كونه أقوى.
وقال الكرخي: يلزم المساواة، أقل ما يكون.
وقال أبو الحسين: يجوز الأدنى.
ثم قال: هذا كله في الظاهر.
وأما في المجمل فيكفي في بيانه أدنى دلالة ولو مرجوحًا، إذ لا تعارض وهذا ما قاله الآمدي: إن المبين إن كان مجملًا كفى في تعيين أحد احتماليه أدنى ما يفيد التوضيح.
وإن كان عامًا أو مطلقًا لا بد أن يكون المخصص أو المقيد أقوى.
الثانية','4','97',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1022,'لا يجوز تأخيره، أي: البيان -عن وقت الحاجة وهو وقت تنجيز التكليف فالمراد بالحاجة: الإتيان بما كلف به لا إلا التكليف؛ لأنه تكليف بما لا يطاق.
لأن الإتيان بالشيء مع عدم العلم به محال.
ومن هنا تعلم أن المصنف يختار عدم جواز تكليف الغافل، ولا ينافيه مفهوم قوله فيما سبق: «لا يجوز تكليف الغافل من أحال تكليف المحال، لأن مفهومه صادق بأن من جوز تكليف المحال؛ بعضهم يجوز تكليف الغافل، وبعضهم يمنعه، وتقدم.
ويجوز تأخير البيان عن وقت الخطاب إلى وقت الحاجة.','4','98',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1023,'ومنعت المعتزلة ذلك.
قال في المحصول: إلا في النسخ، فإنهم وافقوا على جواز تأخيره.
وحكى الغزالي وغيره: الاتفاق عليه.
وفي كلام الإمام والآمدي ما يفهم جريان خلاف فيه قاله العراقي.
وجوز أبو الحسن البصري من المعتزلة، ومنا أي أهل السنة أو','4','99',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1024,'الشافعية القفال، والدقاق وأبو إسحاق المروزي تأخير البيان التفصيلي بالبيان الإجمالي أي: مع البيان الإجمالي وقت الخطاب، ليكون مانعًا من الوقوع في الخطأ.
مثل أن يقول: المراد بهذا العام هو المخصص وبهذا المطلق هو المقيد، وبالنكرة فرد معين، وبهذا اللفظ معنى مجازي أو شرعي، وهذا الحكم سينسخ.
وقوله: «فيما عدا المشترك» متعلق باشتراط البيان فيكون عامله محذوفًا أي معنى كائنًا فيما عدا المشترك.
يعني اشتراك البيان الإجمالي حال كونه واقعًا فيما عدا المشترك.','4','100',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1025,'أما المشترك وما لا ظاهر له يعمل به من المجمل، فإنه يجوز تأخير بيانه، لأن تأخيره لا: يوقع في محذور.
وقد صرح القفال في الإشارة بأنه يجوز تأخير البيان مطلقًا.
فنقل المصنف عنه التفصيل يحتمل أين يكون قولًا آخر له.
قال العراقي: وأما الدقاق فنقل عنه الأستاذ أبو إسحقاق موافقة المعتزلة، على المنع مطلقًا.
وأما أبو إسحاق، فإن أراد به المروزي -كما في المحصول فقد نقل عنه القاضي والشيخ أبو إسحاق والغزالي والآمدي موافقة المعتزلة.','4','101',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1026,'وإن أراد الأستاذ فقد صرح في كتابه بموافقة الأصحاب.
قلت: جوابه كما تقدم في صاحبهما والله أعلم.
لنا: مطلقًا في التخصيص وغيره ما له ظاهر، وما ليس له ظاهر.
قوله تعالى: {فإذا قرأناه فاتبع قرآنه ثم إن علينا بيانه}.
ذكر البيان بثم وهي للتراخي، فالبيان متأخر عن وقت الخطاب، والبيان عام؛ لأنه مضاف، واسم الجنس المضاف يفيد العموم (كما مر) وفيه نظر مبين في الشرح.','4','102',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1027,'قيل: المراد البيان التفصيلي، فلا يلزم مطلوبكم فإنه مطلق.
قلنا: تقييد بلا دليل.
ولنا خصوصًا، أي: مختصًا بالنكرة، أن المراد من قوله تعالى: {أن تذبحوا بقرة} بقرة معينة، لا أي بقرة كانت، كما هو الظاهر بدليل سؤالهم عن صافتها أولًا بقولهم: {ما هي}؟ وثانيًا {ما لونها}.
عينها تعالى بسؤالهم فقال تعالى: {إنها بقرة لا فارض ولا بكر عوان} {إنها بقرة صفراء فاقع لونها}.','4','103',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1028,'والضمير في السؤال ضمير المأمور بها، فكذا في الجواب.
فعلم أن المأمور بها معينة، والبيان تأخر عن وقت الخطاب حتى سألوه سؤالًا بعد سؤال فدل على الجواز.
قيل: ما ذكرتم يوجب التأخير عن وقت الحاجة فإنهم كانوا محتاجين إلى الذبح، فيكونون محتاجين إلى البيان في ذلك الوقت فيتأخر البيان عن وقت الحاجة.
فما تقتضيه الآية لا يقولون به وما يقولون به لا تقتضيه الآية.
قلنا: الأمر لا يوجب الفور.
وقد يقال: هذا الأمر يوجب الفور؛ لأنه إنما كان لفصل الخصومة بين المتنازعين في القتل، وهو المشهور في التفاسير، وفصل','4','104',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1029,'الخصومة لا بد وأن يكون في الحال.
هذا وقد روى البزار في مسنده مرفوعًا إلى النبي -صلى الله عليه وسلم -أنه قال: «إن بني إسرائيل لو أخذوا أدنى بقرة لأجزأتهم أو لأجزأت عنهم».
ثم قال: لا نعلمه يروى عن أبي هريرة إلا بهذا الإسناد لإسناد ذكره.
وعباد أحد رواته، ضعفه أبو حاتم والنسائي.
قال ابن القطان: قد أثبت عنه يحيى بن سعيد القدر، مع حسن رأيه','4','105',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1030,'فيه وتوثيقه له.
وله طريق آخر ذكره الحافظ الدارقطني في علله الكبير.
وروى الحافظ أبو محمد بن أبي حاتم في تفسيره بسنده إلى ابن عباس - (رضي الله عنهما) -أنه قال: «لو ذبحوا بقرة لأجزأتهم ولكن شددوا وتعنتوا بموسى فشدد الله عليهم».
رواه عنه أبو بكر الصيرفي في الأصول بأحسن من هذا','4','106',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1031,'الطريق فإن فيها السدي وهو ضعيف.
(وقد أخرجه: ابن أبي شيبة بسند صحيح موقوفًا على ابن عباس قيل):','4','107',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1032,'لا نسلم أنها معينة؛ لأنها لو كانت معينة لما عنفهم الله -تعالى -وذمهم على سؤالهم، لكنه ذمهم بقوله تعالى: {فذبحوها وما كادوا يفعلون}.
قلنا: الذم إنما كان للتواني، وهو تقصيرهم بعد البيان، ولم يكن الذم على سؤالهم.
وأيضًا فإيجاب المعينة بعد إيجاب خلافه، نسخ قبل الفعل، وهو ممتنع عند الخصم.
ولنا خصوصًا على جواز إطلاق العام وإرادة الخاص من غير بيان مقترن به لا تفصيلي ولا إجمالي.','4','108',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1033,'وأنه تعالى أنزل: {إنكم وما تعبدون من دون الله حصب جهنم}.
فنقض ابن الزبعري -بكسر الزاي وفتح الباء الموحدة وسكون العين مقصورًا -بالملائكة والمسيح.
وذلك أنه قال: «لأخصمن محمدًا أليس عبدت الملائكة وعبد المسيح، فيلزم أن يكون هؤلاء حصب جهنم» فنزل تخصيصه بقوله تعالى: {إن الذين سبقت لهم من الحسنى أولئك عنها مبعدون} فتأخر البيان عن وقت الخطاب وهو المطلوب.','4','109',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1034,'فإن «ما» في قوله تعالى: {إنكم وما تعبدون} يتناول ذوي العلم وغيرهم، وأراد غير ذوي العلم من غير بيان.
والقصة بمعناها رواها الحافظ أبو بكر بن مردويه في تفسيره عن ابن عباس (رضي الله عنهما).
رواها الحاكم في مستدركه بنحوها إلا أنه ذكر أن القائل لذلك','4','110',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1035,'المشركون لا ابن الزبعرى بخصوصه، وقال: صحيح الإسناد.
ورواها ابن أبي حاتم في تفسيره.
وفي رواية: فقال رسول الله -صلى الله عليه وسلم -: «إن كل من أحب أن يعبد من دون الله، فهو مع من عبده إنما يعبدون الشياطين .. » إلى آخره.
فنزلت: {إن الذين سبقت لهم منا الحسنى}.
قيل -اعتراضًا على هذا الدليل: «ما» لا تتناولهم؛ لأنها عامة لأفراد ما لا يعقل لغة، فلا تتناول الملائكة والمسيح.','4','111',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1036,'وحينئذ لا يكون نزول: {إن الذين سبقت لهم منا الحسنى} للبيان بل زيادة في البيان لجهل المعترض.
وإن سلم أن «ما» تتناولهم، لكنهم أي: المسيح والملائكة خصوا بالعقل، فإنه قاضٍ بأن أحدًا لا يعذب بذنب أحد، وهؤلاء الملائكة والمسيح ما أمروهم بعبادتهم، وما كانوا راضين بها، والعقل كان حاضرًا معهم في وقت نزول: {إنكم وما تعبدون} فلا يكون من قبيل تأخير البيان، بل نزلت الآية تأكيدًا للعقل.
وأجيب: بأن «ما» تستعمل في ذوي العلم أيضًا تمسكًا بقوله تعالى: {والسماء وما بناها} والمراد هو الله تعالى.
ولما استشعر ضعف الجواب (لأنه مخالف لما ذكر، من أن «ما» تعم','4','112',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1037,'غير ذوي العلم، واستعماله في ذوي العلم مجاز) بناه للمفعول.
وما وقع في بعض كتب الأصول من أنه -صلى الله عليه وسلم -قال لابن الزبعرى: «ما أجهلك بلغة قومك ما لما لا يعقل».
فقال الشيخ الحافظ أبو سعيد العلائي: غير صحيح.','4','113',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1038,'وهنا زيادة حسنة في الأصل.
وأشار المصنف إلى جواب الثاني: وهو قولهم: وإن سلم إلى آخره، بقوله: وإن عدم رضاهم إنما يعرف بالنقل.
يعني أن العقل إنما يحكم بعدم تعذيبهم بجريمة الغير، إذا علم','4','114',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1039,'عدم رضاهم بعبادتهم، وعدم رضاهم إنما يعرف بالنقل، وهو قوله تعالى: {إن الذين سبقت لهم منا الحسنى} الآية. فيتأخر البيان، وهو المطلوب.
لا يقال: هذا الجواب بناء على أن عصمة الأنبياء ثابتة بالسمع، والمعتزلة يقولون بالعقل فلا يستقيم الرد عليهم بذلك.
لأنا نقول: إثباته بالسمع بالدليل يصحح الرد عليهم.
قيل: من جهة أبي الحسين البصري: المشترط البيان الإجمالي فيما له ظاهر تأخير البيان فيما له ظاهر لم يرده الشارع يؤدى إلى مسارعة المكلف إلى الظاهر، وذلك إغواء للمكلف وإضلال له، ولا يقع ذلك من الشارع.
وفي نسخة إغراء -بالراء المهملة -أي حاملًا له عليه وهو إيقاع في الجهل.','4','115',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1040,'قلنا: كذلك ما يوجب الظنون الكاذبة من الخطاب مثل قوله تعالى: {الرحمن على العرش استوى} و {يد الله فوق أيديهم} فإنها لا يراد ظاهرها، فيلزم أن يكون إغواء على قولكم، وأنه ليس بإغواء إجماعًا، فكذا الأول.
وقد تقدم في منع الخطاب بالمهمل زيادة تحقيق تأتي هنا.
قيل: من جهة من منع تأخير البيان عن الخطاب الذي ليس له ظاهر كالمشترك، والذي له ظاهر.
ولكن امتنع الأخذ به لاقترانه بالدليل الإجمالي.
بأن الخطاب بذلك لا يحصل المقصود، فامتنع كالخطاب بلغة لا تفهم كمخاطبة العربي بالعجمي؛ لأن الخطاب يستلزم التفهيم؛ لأن حقيقته توجيه الكلام إلى المخاطب لأجل التفهيم.','4','116',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1041,'قلنا: الخطاب بما لا يفهمه السامع، لا يفيد غرضًا إجماليًا. ولا تفصيليًا بخلاف الأول، وهو الخطاب بالمجمل، فإنه يفيد غرضًا إجماليًا.
فإذا قال: ائتني بعين، أفاد الأمر بواحد من مسميات العيون، فيتهيأ للعمل بعد البيان، وتظهر طاعته بالبشر وعصيانه بالكراهة.
وكذا إذا قال: اقتلوا المشركين، أو قال: هذا العام مخصوص (وإلى هذا أشار المصنف بقوله: «يفيد غرضًا إجماليًا»).
تنبيه:
يجوز للنبي -صلى الله عليه وسلم -إذا أتاه وحي أو نزل عليه قرآن
-','4','117',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1042,'تأخير التبليغ عن أمته إلى وقت الحاجة.
لأنا نقطع بأنه لا استحالة فيه، ولأنه يجوز أن يكون في تأخيره مصلحة يعلمها الله تعالى. وقوله تعالى: {يا أيها الرسول بلغ ما أنزل إليك} لا يوجب الفور، لأنه أمر، والأمر لا يوجب الفور كما مر.
وقيل: لا يجوز تأخير التبليغ، لهذه الآية، وقد عرفت الجواب.
فائدة:
يجوز إسماع العام الداخل تحت العموم، مع عدم إسماع المخصص له','4','118',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1043,'إلى وقت الحاجة.','4','119',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1044,'<span data-type="title" id=toc-154>الفصل الثالث: في المبين له</span>
وهو المكلف.
إنما يجب البيان لمن أريد فهمه، أي: يتعين بيان المجمل لمن أراد الله فهمه؛ لأن تكليفه الفهم بدون البيان تكليف بالمحال، ولا يتعين بيانه لغيره، إذ لا تعلق له به، وأشار إليهما «بإنما» الدالة على الحصر.
ثم إرادة الفهم قد تكون للعمل بما تضمنه المجمل كالصلاة ليفهموا أي المجتهدون، ويعملوا به.','4','120',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1045,'أو يكون الفهم لأجل الفتوى به كأحكام الحيض للرجال لإفتاء النساء لا ليعلموا به.
وربما يوهم هذا الكلام أن النساء لا يجب عليهم تحصيل العلم ما كلفن به، مع أنه واجب، لاستواء الرجال والنساء في ذلك على من له استعداد العلم.
لكن الغالب أن الاستعداد في الرجال.','4','121',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1046,'<span data-type="title" id=toc-155>الباب الخامس: في الناسخ والمنسوخ</span>
ما مر كان يشترك فيه الكتاب والسنة والإجماع، وهذا يشترك فيه الكتاب والسنة، دون الإجماع، لما سيأتي أنه لا ينسخ ولا ينسخ به.
وفيه، أي في الباب فصلان:

<span data-type="title" id=toc-156>الفصل الأول: في النسخ</span>','4','123',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1047,'ويقال في اللغة لمعنيين:
للإزالة: يقال: نسخت الشمس الظل، ونسخت الريح آثار القدم، أي: أزالته.
وللنقل: نسخت (الكتاب، أي: نقلت ما فيه) إلى آخر.
وهو في الاصطلاح: بيان انتهاء حكم شرعي بطريق شرعي متراخ','4','124',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1048,'عنه.
فقوله: «بيان» كالجنس.
وقوله: «انتهاء»، أخرج به بيان المجمل.
وقوله: حكم شرعي، دخل فيه الأمر وغيره، وخرج به بيان','4','125',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1049,'انتهاء حكم عقلي، وهو البراءة الأصلية.
فإن انتهاءها بدليل شرعي ليس بنسخ.
وقوله: بطريق شرعي، ليخرج الانتهاء بطريق عقلي كالموت والغفلة والعجز، فعدم تعلق الحكم هنا لعدم قابلية المحل بطريان الموت والغفلة والعجز وليست دلائل شرعية.
وقال بطريق، ولم يقل بحكم؛ لأن النسخ قد يكون بالفعل والتقرير، كذا قيل.
والتحقيق: أنه دليل النسخ.','4','126',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1050,'وقوله: «متراخ»، احتراز عن البيان بالمتصل: كالاستثناء والصفة.
والمنفصل: كما لو قال: «لا تقتلوا أهل الذمة» عقب قوله: «اقتلوا المشركين».
واشترط في الناسخ: أن يكون متراخيًا، إذ لو لم يكن كذلك لكان الكلام متناقضًا.
وأخرج به أيضًا نحو: صل عند كل زوال إلى آخر الشهر.
ولا يرد على المصنف: المنسوخ إذا كان خبرًا؛ لأنه إن تضمن حكمًا شرعيًا فيفسخ، وإلا فلا ورود له.
وأما قول العدل: نسخ حكم كذا، فلم يدخل في حد المصنف؛ لأن قول العدل دال على ذلك الحكم الشرعي، فهو دليل النسخ الدال بالذات.','4','127',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1051,'ولا يرد أيضًا: ما إذا اختلفت الأمة على قولين، ثم أجمعوا على أحدهما، فإنه وإن تعين الأخذ به بعد التخيير بينهما، لأن توهم صدق الحد عليه، مع أن الإجماع لا ينسخ ولا ينسخ به، يرفعه ما احترز عنه؛ لأنه يذكره بعد استقلالًا.
أو في الحد حذف تقديره: بطريق شرعي غير الإجماع.
وأيضًا المراد بالتراخي في الحد: التراخي نزولًا.
والفرق بين النسخ والتخصيص مر.
وقال القاضي أبو بكر الباقلاني: رفع الحكم، أي: حده بأنه خطاب دال على ارتفاع الحكم الثابت بالخطاب المتقدم على وجه لولاه لكان ثابتًا مع تراخيه عنه وارتضاه الغزالي.
وقال القاضي: إنما آثرنا لفظ الخطاب على لفظ النص ليكون شاملًا للفظ، والفحوى، والمفهوم، وكل دليل، إذا يجوز النسخ بجميع ذلك.
وإنما قلنا: على ارتفاع الحكم، ليتناول الأمر والنهي والخبر.
وإنما قلنا: بالخطاب المتقدم، لأن ابتداء العبادات في الشرع يزيل','4','128',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1052,'حكم العقل من براءة الذمة ولا يسمى نسخًا.
وإنما قلنا: لولاه لكان ثابتًا، لأن حقيقة النسخ الرفع، وهو إنما يكون رافعًا لو كان المتقدم بحيث لولا طريانه لبقي.
وإنما قلنا: مع (تراخيه عنه، لأنه لو اتصل به لكان بيانًا لمدة العبادة لا نسخًا.
ورد تعريف القاضي بأن الحادث، الذي هو الرافع ضد السابق، وليس رفعه أي: رفع) الحادث السابق أولى من دفعه، أي: من دفع السابق الحادث.
والرفع والدفع مصدران مضافان إلى الفاعل.','4','129',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1053,'وجوابه: أن القاضي أراد بالحكم الأثر الثابت بالخطاب المتعلق تعلق التنجيز فإنه ليس قديمًا، فيجوز رفعه وتأخره عن غيره، وإيضاحه في الشرح مع تحقيقات حسنة.
وفيه -أي:
في الفصل الأول: مسائل:
الأولى
أنه جائز وواقع بإجماع أهل الشرائع، وأحاله اليهود أي بعضهم فقالوا: يمتنع عقلًا.','4','130',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1054,'وقال بعضهم: يجوز عقلًا لكنه لم يقع.
والعيسوية -منهم -قالوا بجوازه ووقوعه، وأن محمدًا -صلى الله عليه وسلم -عندهم لم ينسخ شريعة موسى -عليه الصلاة والسلام -بل بعث إلى بني إسماعيل، دون بني إسرائيل.
وكذبوا في ذلك -لعنهم الله -بل بعث إلى (كافة الخلق).','4','131',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1055,'لنا: على جواز النسخ، وأنه لو فرض لم يلزم منه محال لذاته، سواء اعتبرت المصالح، أم لا أن حكمه -تعالى -أن يتبع المصلحة فيتغير بتغيرها؛ لأنا نقطع بأن المصلحة (تختلف باختلاف الأوقات كشرب دواء في وقت دون وقت.
فلا بد أن تكون المصلحة) في وقت يقتضي شرع ذلك الحكم، وفي','4','132',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1056,'وقت رفعه فتتغير الأحكام بتغير المصالح وإلا وإن لم يتبع حكمه تعالى المصلحة فله تعالى أن يفعل كيف شاء.
ولنا أيضًا: أن نبوة محمد -صلى الله عليه وسلم -ثبتت بالدليل القاطع؛ لأنه ادعى النبوة قطعًا، وتواتر ذلك، وظهرت المعجزة منه، مثل القرآن، لأنه كتاب شريف، بالغ في فصاحة اللفظ وكثرة العلوم.
فإن المباحث الإلهية واردة فيه على أحسن الوجوه، وكذلك علوم الأخلاق، وعلوم السياسات، وعلم تصفية الباطن، وعلم أحوال القرون الماضية، والمغيبات، مع خلو بلده عن العلماء، والأفاضل، والكتب العلمية، والمباحث الحقيقية، ولم يواظب على القراءة والاستفادة ألبتة.
وانقضى من عمره أربعين سنة على هذه الصفة ثم ظهر مثل هذا الكتاب عليه، وذلك معجزة قاهرة.','4','133',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1057,'وأيضًا فقد تحداهم بأن يأتوا بمثله أو بسورة منه فعجزوا.
وأيضًا: فقد ظهرت على يده معجزات كثيرة مثل: انشقاق القمر، وتسبيح الحصا، وتكثير الطعام، ونبع الماء من بين أصابعه،','4','134',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1058,'وحنين الجذع، وكل منها، وإن كان (مرويًا بطريق) الآحاد لكن مجموعها يفيد التواتر.
وكل من ظهرت المعجزة على يده وادعى النبوة، فإنه يجب أن يكون نبيًا.
لأن الملك العظيم إذا حضر في محفل عظيم، وقام واحد فقال: يا أيها الناس إني رسول هذا الملك إليكم، ثم قال أيه الملك: إن كنت صادقًا في ذلك فخالف عادتك، وقم من سريرك، فإذا قام ذلك الملك عند سماع هذا الكلام عرف الحاضرون بالضرورة كون ذلك المدعي صادقًا في دعواه، فكذا هنا.
وله طريق آخر مذكورة في الكلام، فقد ثبتت نبوته ورسالته -صلى الله عليه وسلم -بالدليل القاطع.','4','135',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1059,'وقد نقل قوله تعالى: {ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها}.
فدل ذلك على النسخ، (لأن سبب نزولها: طعن الكفار حيث قولوا: إن محمدًا يأمر الشيء ثم ينهى عنه).','4','136',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1060,'ولنا أيضًا: أن آدم (عليه السلام) كان يزوج بناته من بنيه (رواه الطبري وغيره من ابن عباس (رضي الله عنهما).
ورواه الطبري عن مرة عن ابن مسعود وعن ناس من أصحاب رسول الله -صلى الله عليه وسلم -نحوه.','4','137',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1061,'قال شيخ الإسلام ما حاصله: وقد وقع لنا موصولًا إلى ابن عباس (رضي الله عنهما) وساق سنده إلى ابن عباس (رضي الله عنهما) قال: كان آدم (عليه السلام) نهى أن ينكح ابنته توأمها، وأن يزوج توأمة هذا الولد آخر، وأن يزوجه توأمه الآخر.
فذكر الخبر باختصار قال: وهذا أقوى ما وقفت عليه من أسانيد هذه القصة ورجاله رجال الصحيح، إلا عبد الله بن خثيم بمعجمة ثم مثلثة مصغر.
فإن مسلمًا أخرج له في المتابعات، وعلق له البخاري شيئًا، ووثقه الجمهور.
قال: وفي هذه الأخبار رد لما ذكره الثعلبي عن جعفر، كما','4','138',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1062,'سيجيء).
يعني ورد بلفظ الإطلاق بل العموم، لكن على التوزيع من غير تخصيص بالبنات والبنين في زمانه ولا يقيد بوقت دون وقت.
والاحتمالات التي لم تنشأ عن دليل بل ينفيها ظاهر الدليل تكون منفية، والآن تزويج البنات من البنين محرم اتفاقًا.
وهذا الرأي لليهود (لو صح ما رواه) الثعلبي عن جعفر الصادق، من إنكار تزويج أدم بناته من بنيه، (لكن فيه ما مر).
قيل: من جهة المانعين للنسخ مطلقًا: المأمور به حسن والمنهي عنه قبيح والفعل الواحد (في واحد).
لا يحسن ولا يقبح، لاستلزامه الضدين.','4','139',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1063,'قلنا: هذا مبني على الحسن والقبح العقليين، وذلك فاسد، والمبني على الفاسد فاسد.
ومع هذا، أي: مع القول به لا استحالة، لأنه يحتمل أن يحسن لواحد، أو يحسن في وقت ويقبح لآخر، أو يقبح لواحد لكن في وقت آخر كشرب دواء بالنسبة إلى شخصين، أو إلى شخص واحد في وقتين وقد مر، وهنا تحقيقات حسنة في الشرح.
الثانية
يجوز نسخ بعض القرآن، ومنع أبو مسلم الأصفهاني ذلك،','4','140',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1064,'كذا في المحصول.
ونقل عنه الآمدي وابن الحاجب وغيرهما، منع وقوع النسخ في القرآن وغيره.
أما نسخ جميع القرآن فلا يكون بالاتفاق.','4','141',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1065,'تنبيه: قال العراقي: في شرح المعالم لابن التلمساني: إن أبا مسلم هذا هو الجاحظ، وتبعوه عليه، وهو غلط ممن قاله وجهل بالتاريخ، وليست كنية الجاحظ أبا مسلم، بل كنيته أبو عثمان، وأبو مسلم هذا اسمه محمد بن علي بن محمد ذكره الذهبي (في العبر).
وقال: الأديب المفسر المعتزلي آخر أصحاب ابن المقرئ موتًا، له تفسير في عشرين مجلدًا، توفي سنة تسع وخمسين وأربعمائة في جمادى الآخرة وله ثلاث وتسعون سنة بتقديم التاء فيهما.
لنا: أن قوله تعالى: {متاعًا إلى الحلول} نسخت بقوله','4','142',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1066,'تعالى: {والذين يتوفون منكم ويذرون أزواجًا يتربصن بأنفسهن أربعة أشهر وعشرًا}.
(أخرجه البخاري عن عبد الله بن الزبير) (فنسخ الاعتداد بالحول، بأربعة أشهر وعشرًا).
قال أبو مسلم الأصفهاني قد تعتد الحامل به أي: بالحول لكونه قد يكون مدة الحمل، فذلك تخصيص لا نسخ.','4','143',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1067,'قلنا: لا تعتد الحامل بالحول، بل إنما تعتد بالحمل، أي بوضعه سواء حصل بسنة، أو أقل، أو أكثر، فخصوصية السنة لاغ لا اعتبار له؛ لأن الله تعالى أوجب الاعتداد بالحمل.
ولنا أيضًا: وقوع النسخ في القرآن، وأنه دليل الجواز، وذلك في تقديم الصدقة على نجوى الرسول (صلى الله عليه وسلم) فإنه وجب بقوله تعالى: {يا أيها الذين آمنوا إذا ناجيتم الرسول فقدموا .... } الآية.
ثم نسخ هذا الوجوب بقوله تعالى: {فإذ لم تفعلوا وتاب الله عليكم ... } الآية.','4','144',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1068,'رواه الطبراني والبزار والترمذي عن علي -رضي الله عنه -بمعناه، وقال: حسن.
قال أبو مسلم: إنما زال ذلك لزوال سببه، أي: سبب الوجوب، وهو التمييز بين المنافق وغيره من المسلمين، إذ المؤمن يمتثل، والمنافق','4','145',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1069,'يخالف، فلما حصل التمييز سقط الوجوب.
قلنا: زال كيف كان، يعني أن المدعى زوال الوجوب بعد ثبوته، سواء كان لزوال سببه أو لا، إذ هو معنى النسخ.
وفيه نظر: لأنه مناقض لما سيجيء، فإنه هناك استدل (على أن الإجماع) لا ينسخ القياس، بقوله: وأما القياس فلزواله بزوال شرطه، ومن جهة المعنى ما زال بزوال علة يمكن عودها.
لا يقال فيه: إنه منسوخ، بل مشروعيته باقية، حتى يعود عند عوده.
بل الجواب أنه ليس المراد من الآية التمييز، لأنه إن كان المراد التمييز للنبي -صلى الله عليه وسلم -فغير واضح، فإنه كان يعلم أعيانهم حتى سماهم لصاحب سره حذيفة -رضي الله عنه.','4','146',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1070,'وإن أراد التمييز للصحابة (رضي الله عنهم) فدعوى زواله عنهم ممنوع بل استمر إلى وفاته عليه الصلاة والسلام.
احتج أبو مسلم على منع نسخ القرآن بقوله تعالى: {لا يأتيه الباطل من بين يديه ولا من خلفه}.','4','147',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1071,'والنسخ باطل، فلو نسخ بعض القرآن لتطرق إليه الباطل، وذلك غير جائز اتفاقًا.
قلنا: الضمير في قوله تعالى: {لا يأتيه الباطل} راجع للمجموع، أي: لمجموع القرآن، ولا ينسخ مجموعه، وذلك اتفاق.
هذا والنسخ ليس باطلًا، لأن الباطل ضد الحق، والنسخ حق.
والمصنف إنما سلم ذلك في بعض القرآن متنزلًا.','4','148',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1072,'تنبيه:
قيل: إنما أراد أبو مسلم بنفي النسخ في القرآن خاصة.
وقيل: خلافه لفظي؛ لأنه يجعل المعنى في علم الله تعالى كالمعنى في اللفظ، ويسمي الكل تخصيصًا، والجمهور يسمون الأول تخصيصًا، والثاني نسخًا.','4','149',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1073,'الثالثة
يحوز نسخ الوجوب قبل العمل، سواء كان قبل دخول الوقت أو بعد دخوله وعدم انقضاء زمن يسع المأمور به، والأول: مثل أن يقول: يوم عرفة من قبل انقضاء زمان يسع الأسباب: لا تحجوا، فالنسخ أبدًا لا يتعلق بما مضى، بل بما يقدر وقوعه في','4','151',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1074,'الاستقبال من أفراد الفعل. خلافًا للمعتزلة، وبعض الفقهاء.
أما بعد خروج الوقت فليس محل خلاف، بل جائز بالاتفاق، كما اقتضاه كلام ابن الحاجب، وصرح به الآمدي، وهذا إنما يأتي إذا صرح بوجوب القضاء وقلنا: الأمر بالأداء يستلزمه.','4','152',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1075,'وأما إذا وقع النسخ في الوقت لكن بعد التمكن من فعله، فكلام المصنف يوهم جريان الخلاف فيه أيضًا.
ونقل الصفي الهندي المنع فيه عن الكرخي يوافقه، لكن صرح الآمدي بأنه جائز بلا خلاف، وإنما الخلاف قبل التمكن، وكذا ابن برهان وإمام الحرمين.
والمسألة ليست خاصة بالوجوب، بل غيره كذلك أيضًا أي كالندب.','4','153',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1076,'لنا على الجواز: إن إبراهيم (عليه الصلاة والسلام) أمر أي: أمره الله تعالى بذبح ولده.
قيل: إسماعيل، وبه قال الإمام الرازي.
وقيل: إسحاق، وصححه القرافي.
وإنما قلنا: إنه أمر بذبح ولده بدليل قوله تعالى: {إني أرى في المنام أني أذبحك} وقول: ولده له (عليهما الصلاة والسلام)','4','154',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1077,'{افعل ما تؤمر به}.
وقوله: {إن هذا لهو البلاء المبين} وقوله: {وفديناه بذبح عظيم} فنسخ قبله، ولو لم يكن مأمورًا به لما أقدم على الذبح وترويع الولد إذ هو ممتنع شرعًا وعادة، ولم يكن فيه بلاء ولم يحتج إلى الفداء؛ لأن الذبح يدل على المأمور به.
وأما كونه نسخ قبله، فلأنه لو لم ينسخ لذبح، لكنه لم يذبح.
قيل: لم يكن مأمورًا بالذبح وإنما كان مأمورًا بالمقدمات، فظن أنه مأمور به.
وتلك الأمور التي تمسك بها من قوله: {افعل ما تؤمر}','4','155',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1078,'وقوله: {إن هذا}، وحصول الفداء إنما هي بناء على ظنه - صلى الله عليه وسلم - أنه مأمور.
قلنا: ظن النبي - صلى الله عليه وسلم - مطابق يستحيل فيه الخطأ فحينئذ لا يخطئ ظنه - صلى الله عليه وسلم - لا سيما في ارتكاب هذا الأمر العظيم.
ولك أن تقرر كلام المصنف هكذا.
قيل: لا نسلم أنه كان مأمورًا بالذبح، وإنما ظن ذلك ظنًا بإراءة الرؤيا والجواب.
الجواب ومع هذا فما ذكروه توريط على أصلهم لإبراهيم - عليه الصلاة والسلام - في شيء يظهر أنه أمر وليس بأمر وذلك غير جائز عندهم.
قيل: وإن سلمنا أنه كان مأمورًا بالذبح لكن لا نسلم أنه','4','156',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1079,'نسخ قبل الذبح لما روي أنه امتثل فإنه قطع فوصل، يعني كلما قطع شيئًا التحم عقب القطع.
قلنا: لو كان كذلك، أي: لو أتى بالذبح لم يحتج إلى الفداء، لأن الفداء بدل، والبدل إنما يحتاج إليه إذا لم يوجد المبدل.
وأيضًا هذا خلاف العادة، والظاهر، ولم بنقل معتبرًا، (كذا قيل لكن روي بإسناد جيد، وأخرجه ابن أبي حاتم: أن الله ضرب على حلقه صفيحة من نحاس، قال: فقلبه على وجهه وحز القفا، فذلك قوله تعالى: {وتله للجبين} فنودي {أن يا إبراهيم قد صدقت الرؤيا} فالتفت فإذا الكبش فأخذه فذبحه ... الحديث.
قيل: لو كان الفعل واجبًا في الوقت الذي عدم الوجوب','4','157',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1080,'فيه، لكان الشخص الواحد، في حكم واحد، في وقت واحد، مأمورًا به ومنهيًا عنه، لكن الواحد بالواحد في الواحد لا يؤمر وينهى لأنه محال.
قلنا: يجوز، وإنما يكون محالًا لو كان المقصود حصول الفعل، أما إذا كان للابتلاء فلا.
كما أن السيد يقول لعبده: اذهب غدًا إلى موضع كذا راجلًا، وهو لا يريد الفعل، وإنما يريد امتحانه ورياضته، فلما تهيأ للذهاب يقول له: لا تذهب.
الرابعة
يجوز النسخ بلا بدل أي: يجوز نسخ الحكم الشرعي من غير حكم','4','158',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1081,'شرعي آخر.
وبه قال الجمهور، ومنعه قوم.
ونقل عن الشافعي -رضي الله عنه.
وقوله في الرسالة: ((وليس ينسخ فرض أبدًا إلا أثبت مكانه فرض)).
أوله أبو بكر الصيرفي على أن المراد بالفرض الحكم، أي: إذا','4','159',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1082,'نسخ لا بد أن يعقبه حكم آخر، فليس منافيًا لكلام أهل الأصول.
لأنه يرجع إلى ما كان عليه، وهو حكم.
فإن صدقة النجوى لما نسخ وجوبها، عاد الأمر إلى ما كان عليه من التخيير.','4','160',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1083,'قال العراقي: لكن يجب أن يؤول إطلاق قولهم بلا بدل، على أن المراد بلا بدل منصوص عليه.
ويجوز أيضًا: النسخ إلى بدل أثقل.
وإليه أشار بقوله: ((أو يبدل أثقل منه)).
ونقل ابن برهان عن الشافعي - رضي الله عنه - خلافه ولم يجزم به عنه، بل قال: نقله ناقلون عنه وليس بصحيح، يعني وليس بصحيح','4','161',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1084,'عنه.
وقوله: ((كنسخ وجوب تقديم صدقة النجوى)) يعني فإنه نسخ بلا بدل، فهو مثال للأول.
ومثال الثاني الكف عن الكفار فإن الكف عنهم كان واجبًا، أي: كان قتالهم حرامًا، لقوله تعالى: {ودع أذاهم} ثم نسخ بالقتال أي: بإيجاب القتال مع التشديد فيه، كثبات الواحد للعشرة، وذلك أثقل من الكف.','4','162',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1085,'(استدل- أي: بضم التاء مبني للمفعول - أي: على عدم جواز النسخ بلا بدل أو ببدل أثقل بقوله تعالى: {ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها}.
دلت الآية على أنه لا بد من الإتيان بحكم هو خير من المنسوخ أو مثله، والعدم والأثقل ليسا بخير ولا مثل.
قلنا: ربما يكون عدم الحكم أو الأثقل خيرًا.
أما خيرية عدم الحكم: فقد يكون في وقت لمصلحة المكلف.
وأما خيرية الحكم الأثقل: فقد يكون لزيادة الثواب.
وقد يقال: المراد {نأت} بلفظة {خير منها}، لا بحكم خير من حكمها، وليس الخلاف في اللفظ، إنما الخلاف في الحكم.
ولا دلالة عليه في الآية.','4','163',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1086,'سلمنا كون المراد الحكم، لكنه عام يقبل التخصيص.
سلمناه: لكن هذا دال على عدم الوقوع، وأما على عدم الجواز فلا، والنزاع في الجواز.
وقال الصفي الهندي: العدم الصرف لا يوصف بقوله: {نأت} لأن ما أتى فهو شيء.
قال العراقي: وهو متجه، إلا أنا نقول النسخ أعاد (الحكم الذي كان عليه أولًا، فقد أتي) بالحكم الذي كان من قبله.
ويبقى قول المصنف ((عدم الحكم)) معترضًا إلا أن يريد عدم حكم مبتدأ.

* * *','4','164',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1087,'الخامسة
ينسخ الحكم دون التلاوة، مثل قوله تعالى: {متاعًا ... } الآية فنسخ حكم الاعتداد بالحول واللفظ مستقر.
لما رواه البخاري عن عبد الله بن الزبير قال: قلت لعثمان: هذه الآية التي في البقرة: {والذين يتوفون منكم ويذرون أزواجًا} إلى قوله: {غير إخراج}.','4','165',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1088,'وقد نسختها الآية الأخرى، فلم تكتبها فقال: فدعها يا ابن أخي لا أغير شيئًا من مكانه.
وبالعكس، وهو نسخ التلاوة دون الحكم، مثل ما نقل ((الشيخ والشيخة إذا زنيا فارجموهما)) رواه الشافعي - رضي الله عنه - عن غمر - رضي الله عنه - بلفظ: ((إياكم أن تهلكوا عن آية الرجم أو يقول قائل: لا نجد حديثه في كتاب الله تعالى فلقد رجم رسول','4','166',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1089,'الله - صلى الله عليه وسلم - والذي نفسي بيده لولا أن يقول الناس زاد عمر في كتاب الله لكتبتها ((الشيخ والشيخة إذا زنيا فارجموهما ألبتة)) فإنا قد قرأناها)).
وفي الصحيحين نحوه، وقد تابعه جمع من الصحابة.
- وينسخان، أي: الحكم والتلاوة معًا، كما روي عن عائشة - رضي الله عنها - أنها قالت: ((كان فيما أنزل عشر رضعات معلومات','4','167',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1090,'محرمات فنسخن بخمس فتوفي رسول الله - صلى الله عليه وسلم - وهو فيما يقرأ من القرآن)).
رواه مسلم بلفظ ((يحرمن)) بدل ((محرمات)) وبزيادة معلومات بعد عشر رضعات.
قال: إليكا الطبري في تعليقه في الخلاف: وإنما أرادت: وكن مما يتلى وهذا واضح.
قال العراقي: وأجيب بأن المراد قارب الوفاة، وأن نسخ تلاوة','4','168',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1091,'الخمس لم يبلغ جميع الناس في حياة رسول الله - صلى الله عليه وسلم، فاستمر من لم يبلغه على قراءتها في القرآن حتى بلغه ذلك بعد وفاته - صلى الله عليه وسلم.
واعلم أن المصنف أورد ثلاث دعاوي مترتبة مع أدلتها مع أن الخلاف ثابت في الوليين لبعض المعتزلة، دون الثالثة، لأن إقامة الدليل ليس منحصرًا في رفع الخلاف وإلزام المخالف، بل قد يكون الغرض منه إثبات المدعى، وهو هنا معرفة حكم شرعي هو: جواز نسخ التلاوة والحكم معًا، كما يتمسك بالإجماع وبالنصوص والأقيسة الجلية.
وأما قول العلماء: لا يجوز نصب الدليل في غير محل النزاع فمعناه: إذا حاول المستدل إلزام الخصم ورفع الخلاف وأقام الدليل على ما لا خلاف فيه، فإين أحدهما من الآخر.

***','4','169',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1092,'السادسة
يجوز نسخ الخبر المستقبل، خلافًا لأبي هاشم اعلم أنه لا نزاع في جواز نسخ الخبر، ونسخ تكليفنا بالإخبار به.
لأن صورة الخبر وإن لوحظت في هذا، لكن النسخ لم يرد على الخبر، بل ورد على الطلب.
قال الأبهري: أما إذا نسخه بالتكليف بالإخبار بنقيضه، فإن كان هذا التكليف للرسول - صلى الله عليه وسلم - فلا يجوز، لأنه يرفع الوثوق بصدق الرسول.
وإن كان لغير الرسول (صلى الله عليه وسلم) فجائز عندنا، لأن','4','170',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1093,'الله تعالى كلف المؤمنين بالخبر الصادق كلما أخبروا عن شيء، وكلفهم بالكذب إذا كان فيه إنقاذ نفس معصومة.
وهذا من باب التخصيص ولا استحالة في وقوع مثله في باب النسخ.
والمعتزلة لا تجوزه، لأن الكذب عندهم قبيح، والتكليف عندهم بالقبيح قبيح.
وأما نسخ مدلول الخبر، أي: الأمر الخارجي الذي تطابقه الصورة الذهنية، كإيمان زيد مثلًا، إذا قيل: زيد مؤمن فهي مسألة الكتاب.','4','171',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1094,'وحاصله: إن كان مما لا يتغير فلا يجوز اتفاقًا، كما قاله الإمام والآمدي، ولم يستثنه المصنف.
وأما الذي يتغير، فقال الآمدي تبعًا للإمام: يجوز نسخه مطلقًا سواء كان ماضيًا، أو مستقبلًا، أو وعدًا، أو وعيدًا.
واختار ابن الحاجب عدم الجواز مطلقًا، ونقل عن أكثر المتقدمين وأبي هاشم.
واختار ابن الحاجب عدم الجواز مطلقًا، ونقل عن أكثر المتقدمين وأبي هاشم.
واختار المصنف: أنه إن كان مدلوله مستقبلًا جاز وإلا فلا.
قال الإسنوي: وهو الذي نقله الآمدي.','4','172',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1095,'قال ابن برهان: ومحل الخلاف إذا لم يكن الخبر معناه الأمر، فإن كان كقوله تعالى: {لا يمسه إلا المطهرون} جاز بلا خلاف.
وفي المحصول: أن الخلاف جارٍ فيه وإن تضمن حكمًا شرعيًا.
واستدل المصنف (على مختاره) بقوله: لنا أنه يحتمل أن يقال: لأعاقبن الزاني أبدًا، ثم يقال: أردت سنة، يعني يصح عقلًا أن يقول: لأعاقبن الزاني أبدًا، ثم يقول: أردت سنة، ولا معنى للنسخ إلا ذلك.
فإن النسخ إخراج بعض الزمان وهو موجود هنا.','4','173',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1096,'وقد يقال: هذا تخصيص لا نزاع في صحته، وليس بنسخ، لأنه لو حمل على النسخ لزم الكذب.
وإذا حمل على التخصيص لا يلزم ذلك، لأنه بيان لما أريد بلفظ الا بد لا رفع له، وإبطال كما في النسخ.
قيل من جهة أبي هاشم: نسخ الخبر يوهم الكذب؛ لأن المتبادر إلى الفهم من الخبر جميع المدة المخبر بها، والكذب قبيح، وإيهام القبيح قبيح.
قلنا: ونسخ الأمر أيضًا يوهم البداء، وهو: ظهور الشيء بعد خفائه.
فلو امتنع نسخ الخبر لإيهام الكذب، لامتنع نسخ الأمر لإيهام البداء، إذا إيهام البداء قبيح كما أن إيهام الكذب قبيح.','4','174',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1097,'<span data-type="title" id=toc-163>الفصل الثاني: في الناسخ</span>
- وهو ما ينسخ به -والمنسوخ- وهو ما نسخه غيره وفيه مسائل
الأولى
الأكثر على جواز نسخ الكتاب بالسنة المتواترة، وقيدته بالمتواترة إذ','4','175',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1098,'لا بد منه وسيجيء للمصنف ما يوضحه، وذلك كنسخ الجلد في حق المحصن، برجمه له - صلى الله عليه وسلم - مع أن حد الجلد كان ثابتًا بالقرآن لكل زان محصنًا وغيره، فقد نسخ القرآن بالنسخة المتواترة.
وفيه نظر، فإن هذا تخصيص للكتاب بالسنة كما ذكره المصنف مثالًا له، لا نسخ، لأنه لم يرفع الكل، وإن سلم فهو من نسخ القرآن','4','176',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1099,'بالقرآن.
(فإن ابن) بطال حكى عن بعضهم أن الرجم ثابت بالقرآن، وإيضاحه مع فوائد أخرى في الأصل.','4','177',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1100,'وبالعكس أي: الأكثر على جواز نسخ السنة بالكتاب.
وذلك كنسخ القبلة ((متفق عليه)).
فإن التوجه إلى بيت المقدس كان ثابتًا بالسنة، ثم إنه نسخ بالقرآن بقوله تعالى: {فول وجهك شطر المسجد الحرام}.
قال الحازمي: اتفق الناس على أنه - صلى الله عليه وسلم - قبل أن يؤمن بالتوجه نحو الكعبة كان يصلي إلى بيت المقدس ثم نزلت آية النسخ.','4','178',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1101,'واختلفوا في المنسوخ هل كان ثابتًا بنص القرآن أو بالسنة على قولين:
وللشافعي- رضي الله تعالى عنه - قول بخلافهما، يعني بمنع نسخ الكتاب بالسنة المتواترة وعكسه.','4','179',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1102,'ومقتضى عبارة المصنف أن له قولًا آخر يوافق الأكثر، لكن المشهور عن الشافعي - رضي الله عنه - الجزم بالمنع في الصورة الأولى، كما نقله إمام الحرمين والآمدي وابن الحاجب وغيرهم.
قال بعضهم: والظاهر أن الشافعي - رضي الله عنه - إنما نفى الوقوع فقط.
وأما الثانية: فحكى إمام الحرمين والآمدي وابن الحاجب عن الشافعي -رضي الله عنه - قولين كالمصنف.
والمشهور عن الشافعي - رضي الله تعالى عنه - منعه.
ونسبه الرافعي لاختيار أكثر أصحابه.
دليله - أي: دليل الشافعي رضي الله عنه- في الأول: وهو عدم','4','180',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1103,'جواز نسخ الكتاب بالسنة المتواترة قوله تعالى: {ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها} فإن الآية داله على أن ما ينسخ به القرآن يجب أن يكون خيرًا منها أو مثلها، والسنة لا تكون خيرًا من شيء من القرآن ولا مثله.
وأيضًا فالضمير في قوله تعالى: {نأت} راجع إليه تعالى، فيجب أن لا ينسخ إلا بما أتى به وهو القرآن.
ورد: بأن السنة وحي أيضًا لقوله تعالى: {وما ينطق عن الهوى إن هو إلا وحي يوحى}. فالآتي به هو الله تعالى.
قال الشافعي: - رضي الله تعالى عنه - في الرسالة: السنة منزلة، كما أن القرآن منزل، قال الله تعالى: {وأنزل الله عليك الكتاب والحكمة}.','4','181',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1104,'فالكتاب: القرآن، والحكمة هي: السنة.
ورواه البيهقي في المدخل عن الحسن.
ودليل الشافعي - رضي الله تعالى عنه - فيهما أي في منع نسخ الكتاب بالسنة، وعكسه قوله تعالى: {وأنزلنا إليك الذكر لتبين للناس ما نزل إليهم}.
فإن الآية دلت على أن السنة بيان لجمع القرآن؛ لأن {ما} عامة،','4','182',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1105,'فلو كانت ناسخة لم تكن مثبتة بل رافعة، إذ النسخ رفع.
وأما عكسه، فلأن السنة مبينة للكتاب كما تقرر.
فلو كان الكتاب ناسخًا لها لكان مبينًا لها، لأن النسخ بيان انتهاء الحكم وذلك دور.
وأجيب: عن الأول: بأن النسخ بيان انتهاء الحكم الشرعي لا رفع، فلا تنافي بين الكتاب والسنة، فيجوز كونها بيانًا له.
وعورض الثاني بقوله تعالى: {تبيانًا لكل شيء} والسنة شيء، فيكون تبيانًا لها والنسخ بيان.','4','183',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1106,'الثانية
لا ينسخ المتواتر بالآحاد، أي: لم يقع.
قال إمام الحرمين: بإجماع الأمة عليه، وكأنه لم يعتد بمخالفة بعض الظاهرية فيه.
ولكن ذهب القاضي أبو بكر، والغزالي إلى وقوعه في زمنه (عليه الصلاة والسلام) دون ما بعده.
أما جوازه عقلًا فبالاتفاق كما قاله الآمدي، لكن الخلاف','4','184',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1107,'ثابت كما نقله القاضي أبو بكر وغيره.
وإنما قلنا: لا ينسخ المتواتر بالآحاد، لأن القاطع الذي هو المتواتر لا يدفع بالظن الذي يفيده الآحاد وفيه نظر، وجوابه في الأصل.
قيل: نسخ المتواتر بالآحاد، قد وقع فإنه قوله تعالى: {قل لا أجد فيما أوحي إلي محرمًا على طاعم يطعمه إلا أن يكون ميتة أو دمًا مسفوحًا','4','185',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1108,'أو لحم خنزير}.
منسوخ، بما روي أنه علية الصلاة والسلام ((نهى عن أكل كل ذي ناب من السباع)). أحرجه الأئمة الستة.
وهو خبر آحاد.
وإذا جاز نسخ القرآن به فنسخ السنة المتواترة به أولى.','4','186',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1109,'قلنا: قوله {لا أجد} للحال، والتحريم في المستقبل لا ينافيه فلا ينسخ به.
غايته أن عدم التحريم ثبت بالآية ورفع بالخبر، لكن عدم التحريم معناه: بقاء الإباحة الأصلية، والخبر قد حرم حلال، الأصل ولم يرفع حكمًا شرعيًا، ومثله ليس نسخًا اتفاقًا.
لا يقال: حل أكل كل ذي ناب ثبت بقوله تعالى: {خلق لكم ما في الأرض جميعًا} فيكون حكمًا شرعيًا، ويكون رفعه نسخًا.
لأن نقول: هذه الآية عامة، فيكون الحديث مخصصًا لها.
تنبيه:
قول المصنف: ((لا ينسخ المتواتر بالآحاد)) مخصص لقوله: فيما تقدم.','4','187',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1110,'الأكثر على جواز نسخ الكتاب بالسنة - أي: المتواترة، وهذا ما وعدناك به، ومحله في الآحاد التي لا تفيد القطع، أما إذا أفادته لانضمام القرائن إليه فيجوز نسخ المتواتر به عند الأكثرين، كما يفهم من دليلهم، قاله الأبهري.
تنبيه:
مقتضى الكلام المصنف جواز نسخ الآحاد بالمتواتر وجواز نسخ المتواتر بالمتواتر، وجواز نسخ الآحاد بالآحاد وهو كذلك.
الثالثة
الإجماع لا ينسخ، أي: لا ينسخه غيره.
يعني: لا يرفع الحكم الثابت به، وبه قال الجمهور.','4','188',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1111,'لأن الناسخ له إما نصًا، أو إجماعًا أخر، أو قياسًا - لانحصار الأدلة الشرعية فيها - والكل باطل.
أما النص فلتقدمه على الإجماع، لأن الإجماع إنما ينعقد بعد زمنه - صلى الله عليه وسلم - ولا ينعقد في حياته - (صلى الله عليه وسلم - لأنه إن لم يكن قوله فيه فلا ينعقد.
وإن كان قوله فيه، فقوله أفاد الحكم، فلا فائدة لقول غيره، فالنص مقدم على الإجماع.
والناسخ يجب تأخره، وإليه أشار بقوله: ((لأن النص يتقدمه)).','4','189',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1112,'وأما الإجماع: فلأنه لا ينعقد على خلاف الإجماع، وإلا لكان أحد الإجماعين خطأ، وإليه أشار بقوله: ((ولا ينعقد الإجماع بخلافه)).
وأما القياس فلأن شرط العمل به أن لا يكون على خلاف الإجماع كما سيجيء- إن شاء الله تعالى - وإليه أشار بقوله: ((ولا القياس بخلاف الإجماع)).
والإجماع لا ينسخ به غيره وبه قال الجمهور، لأن المنسوخ به إما نص، أو إجماع، أو قياس.
أما النص والإجماع فظاهران - مما تقدم - من أنه لا ينعقد الإجماع بخلافهما.','4','190',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1113,'وأما القياس فلزواله، بزوال شرطه؛ لأن شرطه أن لا يكون على خلاف الإجماع، فلما اتعقد الإجماع على خلافه زال شرط العمل به، وزوال المشروط بسبب زوال شرطه لا يسمى نسخًا.
قيل: وهذا مناف لما تقدم من جوابه شبهة الأصفهاني حيث قال: زال كيف كان، وهنا تحقيق حسن في الأصل.','4','191',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1114,'والقياس إنما ينسخ بقياس أجلي، أي: أوضح وأظهر منه، فلا ينسخ بقياس مساوٍ، لأنه ترجيح بلا مرجح.
ولا بقياس أخفى لأنه عمل بالمرجوح وترك للراجح.','4','192',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1115,'ولا ينسخ القياس بنص ولا إجماع، - ولم يذكر دليله اكتفاء بما تقدم من أن القياس لا ينعقد على خلاف النص والإجماع، هذا ما تعطيه عبارة المصنف.
والأكثرون على منع كون القياس ناسخًا، كما حكاه القاضي أبو بكر واختاره وحكاه أبو إسحاق المروزي عن نص الشافعي (رضي الله تعالى عنه).
وقال القاضي حسين: إنه المذهب.
والجمهور على جواز كونه منسوخًا في زمنه - صلى الله عليه وسلم - واختار ابن الحاجب في القياس المظنون أنه لا ينسخ ولا ينسخ به،','4','193',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1116,'وفي القياس المقطوع أنه ينسخ بالمقطوع في حياته عليه الصلاة والسلام وهو إذا نسخ حكم الأصل بنص فقياس عليه، وأما بعده (صلى الله عليه وسلم) فلا ينسخ، إذ لا ولاية للنسخ للأمة.
نعم قد يظهر للأمة أنه كان منسوخًا بأن يظهر نسخ حكم أصله مثال نسخ القياس بالقياس الأجلى: ما لو فرض نص فيه (نهي عن) بيع البر بالبر متفاضلًا، وألحقنا السفرجل بالبر في منع بيعه بجنسه متفاضلًا، ثم جاء نص بجواز بيع التفاح بالتفاح متفاضلًا.
فقياس السفرجل على التفاح أجلى؛ لأن الجامع بينه وبين البر هو الطعم، وهو حاصل في التفاح مع زيادة اللون والطعم والشكل.
فإذا ألحقنا السفرجل بالتفاح في جوز البيع متفاضلًا كان القياس الثاني ناسخًا للأول لكونه أجلى.
الرابعة','4','194',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1117,'نسخ الأصل - وهو ماله المفهوم - يستلزم نسخ الفحوى - وهو مفهوم الموافقة.
وبالعكس، أي: نسخ الفحوى يستلزم نسخ الأصل، وما اختاره المصنف فيهما نقل عن الأكثرين، واختار ابن الحاجب: جواز نسخ الأصل دون الفحوى وامتناع نسخ الفحوى دون الأصل.
وجزم في المحصول: بأن نسخ الأصل يستلزم نسخ الفحوى.
(أما إن نسخ الأصل يستلزم الفحوى) فلم يذكر له المصنف دليلًا.','4','195',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1118,'واستدل عليه الإمام الرازي، بأن الفحوى تابع للأصل، فإذا ارتفع الأصل لم يكن بقاؤه لوجوب ارتفاع التابع بارتفاع متبوعه، وإلا لم يكن تابعًا له.
ورد بأن دلالة اللفظ على الفحوى تابعة لدلالته على الصل، وليس حكمها تابعًا لحكم الأصل.
فإن فهمنا لتحريم الضرب، حصل من فهمنا لتحريم التأفيف، لا أن الضرب إنما كان حرامًا لأن التأفيف حرام.
وأما استلزام نسخ الفحوى نسخ الأصل فذلك لأن نفي اللازم يستلزم نفي الملزوم، والفحوى لازم للأصل.
والفحوى نفسه يكون ناسخًا لغيره بالاتفاق كما نقله الآمدي تبعًا للإمام.','4','196',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1119,'لكن في شرح اللمع للشيخ أبي اسحاق خلافًا فيه بناء على أنه قياس، وأن القياس لا ينسخ به.
وحكى ابن السمعاني: المنع عن الشافعي - رضي الله عنه -.
واستدل للجواز: بأن دلالة الفحوى: إن كانت لفظية فلا كلام.
وإن كانت عقلية، فهي يقينية، فتقتضي النسخ، وفيه نظر.
قال الإسنوي: والراجح عند المصنف أن دلالة الفحوى من باب القياس.
وعنده أن القياس إنما يكون ناسخًا لقياس أخفى منه فيكون الفحوى كذلك.
فائدة:
يجوز نسخ المخافة مع أصلها وبدونها، ولا يجوز نسخ الأصل','4','197',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1120,'دونها في الأظهر كما قاله الصفي الهندي من احتمالين له، لأنها تابعة له فترتفع بارتفاعه، ولا يجوز النسخ بالمخالفة، كما قاله: ابن السمعاني لضعفها عن مقاومة النص.
وقال الشيخ أبو إسحاق: الصحيح الجواز، لأنها في معنى النطق.
الخامسة
زيادة صلاة على الصلوات الخمس ليس بنسخ، وبه قال','4','198',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1121,'الجمهور وقيل: إنه نسخ، لأنه يغير الوسط عن كونها وسطا، فتخرج الصلاة الوسطى عن كونها وسطى، فيبطل وجوب المحافظة عليها، الثابت بقوله تعالى: {حافظوا على الصلوات والصلاة والوسطى}، وأنه حكم شرعي.
قلنا: وكذلك زيادة عبادة مستقلة، أي: يلزمكم أنه نسخ بعين ما قلتم، وأنه ليس بنسخ اتفاقًا.','4','199',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1122,'وتحقيقه: أن كون الشيء وسطًا أو آخرًا، أمر حقيقي لا حكم شرعي، فلا يكون نسخًا، وإلا لزم أن يكون زيادة العبادة المستقلة نسخًا، لأنها تجعل العبادة الأخيرة غير أخيرة، وليس كذلك بالاتفاق.
وحاصله: أنه لا يبطل وجوب ما صدق عليها أنها وسطى أو أخيرة، وإنما يبطل كونها وسطى أو أخيرة وليس حكمًا شرعيًا.
أما زيادة شيء لا يستقل، مثل زيادة ركعة ونحوها أي كسجود أو شرط أو صفة.
فكذلك عند الشافعي - رضي الله عنه -ليست بنسخ مطلقًا، واختاره في المعالم.
ونسخ عند الحنفية مطلقًا.','4','200',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1123,'وفرق قوم بين ما نفاه المفهوم كما لو قيل: في الغنم المعلوفة الزكاة، بعد أن قيل: في الغنم السائمة الزكاة، فإنه يكون نسخًا. وبين ما لم بنفه المفهوم: فإنه لا يكون نسخًا كزيادة التغريب على الجلد، وعشرين سوطًا على حد القذف، ووصف الرقبة بالإيمان بعد إطلاقها.
وفرق القاضي عبد الجبار المعتزلي: بين ما ينفي اعتداد الأصل وبين ما لم ينفه، فجعل الأول نسخًا، والثاني ليس بنسخ.
مثال الأول: زيادة ركعة، فهذه الزيادة مخرجة للأصل عن الاعتداد به، إذ يقتضي استئنافه لو فعل وحده كما كان يفعل أولًا.
ومثال الثاني: زيادة التغريب على الجلد فإنه لا يقتضي استئناف الجلد.
وقال أبو الحسين البصري: إن نفي الزائد ما ثبت شرعًا كان نسخًا للحكم الأول الثابت شرعًا، سواء كان ثبوته بالمنطوق أو بالمفهوم وجعلناه حجة.','4','201',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1124,'وإلا أي: وإن لم ينف الزائد، ما ثبت شرعًا، بل رفع ما ثبت بدليل عقلي، أي البراءة الأصلية، فلا يكون نسخًا.
قال الإمام الرازي: هذا التفصيل أحسن، واختاره ابن الحاجب تبعًا للآمدي.
ثم إن المصنف مثل لهذا التفصيل بمثالين:
الأول للأول، والثاني للثاني، حيث قال: فزيادة ركعة على ركعتين نسخ، لاستعقابهما التشهد، فرفعت حكمًا شرعيًا، وهو وجوب التشهد عقب الركعتين.
وزيادة التغريب على الجلد ليس بنسخ؛ لأن عدم التغريب كان ثابتًا بمقتضى البراءة الأصلية.
ونقل في الإحكام عن أبي الحسين القائل بالتفصيل: إن هذين','4','202',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1125,'المثالين ليسا بنسخ.
ونقل الآمدي عن القاضي عبد الجبار أن التخيير في ثلاث خصال بعد التخيير في خصلتين يكون نسخًا أيضًا، وهو وارد على المصنف والإمام.
ونقل ابن الحاجب عنه أن زيادة الأسواط على القذف نسخ ((سهو)).
فائدة: نقصان جزء من العبادة أو شرط المختار: أنه ليس بنسخ لتلك العبادة وإن كان نسخًا لذلك الجزء والشرط اتفاقًا.','4','203',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1126,'خاتمة: يعرف الناسخ من المنسوخ.
وسماها خاتمة لتعلقها بسائر أنواع النسخ.
وضابطها: التأخر.
فمتى عرف المتأخر من الدليلين فهو الناسخ، وله طرق صحيحة، وطرق ضعيفة ذكر المصنف منها البعض فقال: النسخ يعرف بالتاريخ.
مثل: أن يعلم أن هذا نزلت في غزوة كذا وتلك في غزوة كذا، أو هذه في خامسة الهجرة وتلك في سادستها.
فلو قال الراوي: هذا سابق قبل كقول جابر - (رضي الله','4','204',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1127,'عنه) كان آخر الأمرين من رسول الله -صلى الله عليه وسلم- ترك الوضوء مما مست النار.
بخلاف ما لو قال الراوي هذا منسوخ، فلا يقبل، لجواز أن يقوله عن اجتهاد، ولا يراه ناسخًا.
وقال الكرخي: إن عينه فقال: هذا نسخ هذا، لم يرجع إليه.
وإن لم يعينه، بل قال: هذا منسوخ قبل.','4','205',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1128,'وما أحسن قول المصنف -رحمه الله تعالى).
"بخلاف ما لو قال منسوخ" فإنه نفى به مذهب الكرخي، وقيل: يثبت مطلقًا، ونقل عن ظاهر نص الشافعي -رضي الله عنه- وليس كذلك فإنه محتمل كما قاله العراقي.
هذا كله إذا لم يثبت كون الحكم منسوخًا.
أما إذا ثبت كون الحكم منسوخًا، ولم يعرف ناسخه.
فقال الراوي: هذا الناسخ فإنه يقبل.
ومما يعرف به النسخ، قول الرسول -صلى الله عليه وسلم- هذا منسوخ أو ما في معناه.
ومنه الإجماع على أن هذا ناسخ.
وبقي طرق أخرى في الشرح.
وإذا لم يعلم الناسخ معينًا بطريقة يجب التوقف حتى يظهر دليل، لا','4','206',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1129,'التخيير فيهما.

* * *','4','207',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1130,'الكتاب الثاني
في
السنة','4','209',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1131,'<span data-type="title" id=toc-170>الكتاب الثاني
في
السنة</span>
هي لغة: العادة والطريقة','4','211',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1132,'واصطلاحًا: تطلق على مقابل الفرض من العبادات.
وعلى ما صدر من النبي -صلى الله عليه وسلم- من الأقوال -التي ليست للإعجاز أو الأفعال أالتقرير أو الهم.','4','212',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1133,'وهذا هو المراد هنا وإليه أشار بقوله:
"وهو قول الرسول -صلى الله عليه وسلم- أو فعله".
لأن التقرير هو الكف عن الإنكار، والكف عن الإنكار فعل.
فاندرج في قوله أو فعله، وأتى بأو الدالة على التقسيم للإعلام بأن كلا من القول والفعل يصدق عليه اسم السنة.
وقد سبق مباحث القول بأنواعها من الأمر والنهي والعام والخاص والمجمل والمبين والناسخ والمنسوخ وغيرها.
والكلام الآن في الأفعال، وفي طرق ثبوتها، أي الذي ثبتت الأفعال بها، وهي الأخبار، وذلك في بابين:

* * *','4','213',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1134,'<span data-type="title" id=toc-171>الباب الأول: في أفعاله صلى الله عليه وسلم</span>
وفيه مسائل خمسة: والثاني في الأخبار.
وقدم الكلام في الأخبار على الكلام في الإجماع، لئلا يتخلل بين أفعاله (صلى الله عليه وسلم) وبين طرف ثبوتها مباحث أجنبية.
الأولى
من المسائل الخمس:','4','215',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1135,'"في عصمة الأنبياء عليهم الصلاة والسلام.
وهي مقدمة لما بعدها، لأن الاستدلال بأفعالهم، متوقف على عصمتهم، فقال: إن الأنبياء معصومون لا يصدر عنهم ذنب.
قال: إلا الصغار سهوًا، والتقرير مذكور في كتاب المصباح في علم الكلام من كلام المصنف، لأنها من مسائل الكلام.
والعصمة: ملكة نفسانية تمنع صاحبها من الفجور.','4','216',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1136,'والحق أن الأنبياء -صلوات الله وسلامه عليهم أجمعين- معصومون. لا يصدر عنهم ذنب، لا كبيرة ولا صغيرة، لا عمدًا ولا سهوًا بل طهر الله تعالى ذواتهم عن جميع النقائض. وحكى ابن برهان هذا عن اتفاق المحققين، وهو قول الأستاذ أبي إسحاق الإسفراييني وأبي الفتح الشهرستاني.','4','217',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1137,'والقاضي عياض والشيخ تقي الدين السبكي وغيرهم.
قال النووي: ومنعها المحققون، أي: الصغائر أيضًا على الأنبياء.
وقطعوا بالعصمة منها وأولوا (الظواهر الواردة فيها).
وقد ذكرت أجوبة كثيرة عن شبه كثيرة في الشرح يتعين الوقوف عليها.
الثانية','4','218',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1138,'فعله -صلى الله عليه وسلم- إن كان من الأفعال الجبلية كالقيام والقعود والأكل والشرب فهو دال على الإباحة.
وفي التنقيح للقرافي قول: إنه للندب، وجزم به بعضهم.
وحكى الأستاذ أبو إسحاق فيه وجهين:
أحدهما: هذا، وعزاه لأكثر المحدثين.
قال: والأصل فيه أنه يستدل به على إباحة ذلك.
والثاني: أنه لا يتبع فيه إلا بدلالة.
فقول المحقق، والإسنوي: "لا نزاع في كونها للإباحة" فيه ما','4','219',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1139,'فيه وما سوى ذلك ن ثبت كونه من خصائصه، فواضح (أنه لا تشاركه فيه الأمة).
وإن لم يثبت، وكان بيانًا لمجمل، فحكمه حكم الذي بينه من الإيجاب وغيره سيجيء ولذا (لم يذكره هنا).
وإن لم يكن، بيانًا وعلمنا صفته بالنسبة إلى النبي -صلى الله عليه وسلم- من الوجوب وغيره فحكم أمته كحكمه عند الجمهور.
وإن لم يعلم صفته نظر:
ن ظهر فيه قصد القربة، فإنه يدل على الندب عند الإمام وأتباعه ومنهم المصنف، وقد صرحوا به في المسألة الثالثة.','4','220',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1140,'وأما إذا لم يظهر فيه قصد القربة، وهو المعني بقوله: "فعله المجرد". واحترز عن جميع ما تقدم بذلك.
فإنه يدل على الإباحة عند مالك رضي الله عنه، واختاره إمام الحرمين في البرهان. وجزم به الإمام الرازي في موضع مع اختياره الوجوب في المعالم. ويدل على الندب عند الشافعي -رضي الله عنه. ويدل على الوجوب عند أبي العباس بن سريج وأبي سعيد الإصطخري.','4','221',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1141,'وابن خيران الشافعيين.
وتوقف أبو بكر الصيرفي، فقال لا يدل على شيء من الأحكام بالتعيين وهو المختار وصححه القاضي أبو الطيب.
وحكي عن جمهور المحققين كالغزالي.
واختاره الآمدي تبعًا للمحصول في موضع لاحتمالها أي لاحتمال هذه الأمور الثلاثة، واحتمال أن يكون من خصائصه -صلى الله عليه وسلم- فيتوقف إلى ظهور البيان.
وهذه المذاهب الأربعة حكاها الآمدي في الفعل الذي ظهر فيه قصد القربة.
وحكى القاضي أبو الطيب عن الصيرفي: أنه للندب.','4','222',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1142,'احتج القائل بالإباحة: بأنه فعله -صلى الله عليه وسلم- لا يكره لشرفه المانع عن ارتكابه المكروه، ولا يحرم لعصمته، والأصل عدم الوجوب والندب لاشتمالهما على زيادة والأصل عدمها فتبقى الإباحة.
وورد: بأن الغالب على فعله -صلى الله عليه وسلم- الوجوب، أو الندب، فيكون الحمل على الإباحة حملاً على المرجوح المغلوب، وهو ممتنع.
قال العراقي: يحتمل أن يقال: إنما يمتنع صدور المكروه منه (إذا','4','223',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1143,'لم يبين به الجواز، فإن فعله لبيان الجواز فعلا مانع من صدوره عنه) ويكون في حقه مستحبًا أو واجبًا.
ويحتمل أن يقال: لا يصدر عنه مطلقًا؛ لأن له مندوحة عن الفعل بالقول مطلقًا.
ولقائل أن يقول: الوجوب والندب وإن كانا غالبين على فعله -صلى الله عليه وسلم- لكنهما على خلاف الأصل، والأصل مقدم على الغالب.
واحتج القائل بالندب: بأن قوله تعالى: {لقد كان لكم في رسول الله أسوة حسنة} يدل على الرجحان لكون الأسوة موصوفة بالحسنة، والحسنة لها رجحان؛ لأن المراد بها في الآية الحسنة اللغوية لا الشرعية التي المباح منها. ويفهم من الحسن اللغوي','4','224',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1144,'الرجحان بالضرورة، والرجحان يحتمل الوجوب والندب، كذا قيل.
وقال العراقي في قوله: {أسوة} أفاد الشرعية، فلما قال: {حسنة} اقتضى زيادة على المشروعية، وبقي الرجحان.
ولقائل أن يقول: الآية تدل على وجوب المتابعة حيث قال: {لمن كان يرجو الله واليوم الآخر} فإنه جار مجرى التهديد، والأصل عدم الوجوب، فيبقى الندب.
وجواب هذا يأتي في جواب القائل بالوجوب فلذا تركه هنا.
واحتج القائل بالوجوب بقوله تعالى: {فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته واتبعوه}.','4','225',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1145,'أمر باتباعه، والأمر للوجوب.
وبقوله تعالى: {قل إن كنتم تحبون الله فاتبعوني}.
فإنه يدل على أن محبة الله -تعالى- مستلزمة للمتابعة.
ومحبته -تعالى- واجبة إجماعًا، ولازم الواجب واجب فتكون المتابعة واجبة.
وبقوله تعالى: {وما آتاكم الرسول فخذوه}.
وما فعله فقد أتاه، والأخذ هنا الامتثال، فيكون امتثال فعله واجبًا، لأن الأمر للوجوب.','4','226',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1146,'وأيضًا إجماع الصحابة -رضي الله عنهم- على وجوب الغسل بالتقاء الختانين، بقول عائشة -رضي الله عنها-: "فعلته أنا ورسول الله -صلى الله عليه وسلم- فاغتسلنا". أخرج قولها النسائي والترمذي وابن ماجة. قال الترمذي: صحيح. وقال في علله: قال البخاري: هو خطأ إنما يرويه الأوزاعي عن عبد الرحمن ابن القاسم مرسلاً، ولم يلتفت ابن حبان إلى ذلك وأخرجه في','4','227',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1147,'صحيحه.
وكذلك ابن القطان فأوجبوا الغسل بمجرد فعله.
وأجيب: بأن المتابعة هو الإتيان بمثل فعله على وجهه، أي: على الوجه الذي فعله من الوجوب وغيره، حتى لو فعله على وجه الندب، وفعلناه على وجه الوجوب لم يكن متابعة.
فحينئذ يلزم أن يكون الأمر بالمتابعة موقوفًا على جهة فعله، فما لم نعرفه لم نكن مأمورين بالمتابعة فيه.
وكذا التأسي شرطه العلم بصفة الفعل.
وفي المحصول والإحكام وغيرهما أن التأسي والمتابعة','4','228',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1148,'معناهما واحد، فلذا جعل المصنف جواب المتابعة جوابًا عن التأسي الذي استدل به القائل بالندب.
ولم يصرح بجوابه هناك تأدبًا مع الشافعي رضي الله عنه.
وقال العراقي: للمتابعة شرط ثالث ذكره الآمدي والإمام في الكلام على حجية الإجماع، وهو أن يكون إثباته به لكونه أتى به.
قال: وقوله: "أو بيانها" بالرفع.
{وما آتاكم الرسول} معناه: وما أمركم، وهو السابق إلى الفهم بدليل {وما نهاكم} حيث قابله به ليتجاوب طرفا النظم، وهو اللائق بالفصاحة الواجب رعايتها في القرآن.
واستدلال الصحابة -رضي الله عنهم.
لا نسلم أنه كان بمجرد فعله، بل بقوله -صلى الله عليه وسلم','4','229',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1149,'-: "خذوا عني مناسككم"- رواه مسلم.
لأنه فعل في باب المناسك، وورود هذا في الحج لا يمنع الاستدلال هنا؛ لأن العبرة بعموم اللفظ لا بخصوص السبب، واللفظ عام.
قال الجوهري: النسك العبادة، والناسك العابد.

* * *','4','230',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1150,'الثالثة
جهة فعله -صلى الله عليه وسلم- منحصرة في الوجوب والندب والإباحة لما تقدم.
والطرق التي يعلم بها الجهة إما تعلم الثلاثة، أو تخص بعضها.
الأول: العام.
وهو إما بتنصيصه -صلى الله عليه وسلم- على أحدها معينًا بأن يقول مثلاً: هذا الفعل واجب، أو مندوب، أو مباح.
أو بتسويته -صلى الله عليه وسلم- الفعل بما علم جهته، فيكون حكمه حكمه وله صورتان:','4','231',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1151,'الأولى: أن يفعل فعلاً، ثم يقول: هذا الفعل مثل الفعل الذي علمت جهته.
الثانية: أن يخير بينه وبين فعل بينت جهته، لأن التخيير لا يكون بين حكمين مختلفين، أي من واجب ومندوب، أو مندوب ومباح، ونحوها.
أو يعلم بطريق من الطرق أن ذلك الفعل امتثال آية دلت على أحد هذه الوجوه الثلاثة بالتعيين.
وإليه الإشارة بقوله: "بما علم أنه امتثال آية دلت على أحدها"، وهو عطف على "تنصيصه" وما مصدرية، تقديره: أو بعلمنا أنه .. إلى آخره.','4','232',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1152,'أو يعلم أن ذلك الفعل بيان لآية مجملة دلت على أحد الثلاثة وإليه أشار بقوله: "بيانها" أي بيان آية دلت على أحدها.
قال العراقي: لا نسلم كون فعله (صلى الله عليه وسلم) مندوبًا أو مباحًا، بل هو واجب مطلقًا، إذ البيان واجب عليه (صلى الله عليه وسلم) وإنما يكون ذلك في حقنا.
وقد صرح بذلك الإمام في باب المجمل والمبين.
قلت: وتقدم له قريبًا ما ينافي هذا، والله أعلم.
ويعلم خصوصًا أما الوجوب بأحد أمور ثلاثة:','4','233',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1153,'إما بأمارته الدالة على كونه واجبًا كالصلاة بأذان وإقامة؛ لأنهما شعار مختص بالفرائض.
وإما كونه موافقة نذر، كما إذا قال: إن شفى الله مريضي فلله على صوم يوم الاثنين فشفي.
أو يكون الفعل ممنوعًا، أي حرامًا.
لو لم يجب كالركوعين في الخسوف.','4','234',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1154,'هو في الصحيحين عن ابن عباس (رضي الله عنهما) وهو يدل على وجوب الركوع الثاني.
وبهذا يستدل على وجوب الختان: لأنه لو لم يكن واجبًا لما جاز قطع جزء من البدن.
ولا ينتقض هذا بسجود السهو،','4','235',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1155,'والتلاوة، وبرفع اليدين على التوالي في تكبيرات العيد.
لأنه تركه تارة والإتيان به أخرى دل على كونه مندوبًا، كذا قيل.
وفي شرح المهذب: أنه لو صلى صلاة الكسوف ركعتين كسائر الصلوات، صحت وكان تاركًا للأفضل.
وفي المحصول وغيره: أنه يعلم أيضًا وجوب الشيء بوجوب قضائه.','4','236',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1156,'ويعلم الندب خصوصًا بقصد القربة مجردًا عن أمارة تدل على خصوص الوجوب أو الندب.
فيدل على أنه مندوب؛ لأن الأصل عدم الوجوب.
وكونه قضاء لمندوب، لأن القضاء يماثل الأداء في الجملة ويعرف المندوب أيضًا بأن يداوم على الفعل ثم يتركه من غير نسخ.
الرابعة
الفعلان لا يتعارضان؛ لأن التعارض بين الأمرين هو تقابلهما على وجه يمنع كل واحد منهما مقتضى صاحبه.
فحينئذ لا يمكن التعارض (بين فعلين، بحيث يكون أحدهما ناسخًا','4','237',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1157,'للآخر، أو مخصصًا له.
لأنهما إن لم تتناقض أحكامهما فلا تعارض.
وإن تناقضت فكذلك، لأنه يجوز أن يكون الفعل في وقت واجبًا، وفي مثل ذلك الوقت بخلافه.
ولا يكون مبطلاً لحكم الأول، إذ لا عموم للأفعال، سواء كانا متماثلين، كصلاة العصر في وقتين، أو مختلفين، وجاز اجتماعهما، كصلاة وصوم، أو لم يجتمعا، كصوم وأكل في يومين.
فلا تعارض في الكل، بل التعارض إنما يقع بين القولين.
كما ذكره المصنف في الكتاب السادس.
أو بين الفعل والقول كما ذكره هنا، حيث قال: "فإن عارض فعله -صلى الله عليه وسلم- الواجب علينا اتباعه" فيه بأن دل دليل','4','238',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1158,'على ذلك قولاً متقدمًا على ذلك الفعل نسخه، أي نسخ ذلك القول المتقدم الفعل، المتأخر المخالف له، سواء كان ذلك القول عامًا، أو خاصًا به، أو خاصًا بنا.
وقوله: "الواجب اتباعه" احترز به عما إذا لم يدل دليل على أنه يجب علينا أن نتبعه في ذلك الفعل.
فإنه يستثنى منه صورة واحدة، لا يكون فيها ناسخًا، بل مخصصًا، وهو ما إذا كان خاصًا بنا فلا تعارض أصلاً.
ولم يذكر حكم الفعل الذي لم يقم الدليل على وجوب اتباعه فيه في','4','239',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1159,'شيء من الأقسام لعدم الفائدة بالنسبة إلينا.
وإن عارض فعله -صلى الله عليه وسلم- الواجب علينا اتباعه فيه، ودل الدليل على وجوب تكرره عليه وعلى أمته قولاً متأخرًا عامًا له -صلى الله عليه وسلم- ولأمته فبالعكس.
يعني فالقول العام المتأخر ينسخ هذا الفعل المتقدم.
قال العراقي: وهذا محله فيما إذا دل دليل على تكرر الفعل، وإلا فلا تعارض بينه وبين القول المتأخر.
فيؤخذ هذا من قول المصنف.
وإن اختص القول به نسخه في حقه، أي نسخ القول المختص به الفعل العام له ولأمته في حقه فقط، وبقي تكليفنا بالفعل دونه.
وإن اختص القول بنا خصنا في حقنا قبل الفعل، ونسخ القول الفعل','4','240',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1160,'عنا بعده؛ لأنه يستلزم تأخير البيان عن وقت الحاجة.
فيكون القول المتأخر ناسخًا لفعله المتقدم.
ومحل ما تقدم جميعه إذا كانت دلالة الدليل الدال على وجوب اتباع الفعل ظاهرة.
فإن كانت قطعية فلا يحمل القول المتأخر على التخصيص، بل يكون ناسخًا مطلقًا.
وهذا كله إذا كان الفعل المتقدم مما يجب اتباعه فيه، كما تكلم فيه المصنف.
فإن لم يكن كذلك فلا تعارض فيه بالنسبة إلى الأمة، لأن الفعل لم يتعلق بهم.
وأما بالنسبة إليه فإن كان الفعل مما دل الدليل على وجوب تكرره عليه وكان القول متأخرًا خاصًا به -صلى الله عليه وسلم- أو متناولاً له وللأمة بطريق النص، كقوله: لا يجب علي ولا عليكم، فيكون القول ناسخًا للفعل.
أو بطريق الظهور فيكون الفعل السابق مخصصصا لهذا العموم،','4','241',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1161,'لأنه لا يشترط تأخر المخصص عن العام كما عرفت.
ولم يذكره لوضوحه.
وإن جهل التاريخ ولم يعلم هذا القول متقدم أو متأخر -فالأخذ بالقول في حقنا، إن لم يمكن الجمع بينهما لاستبداده، أي لاستقلاله.
فإن دلالة القول على مدلوله أقوى من دلالة الفعل؛ لأن القول وضع لذلك فلا يختلف بخلاف الفعل فإن له محامل.
وإنما يفهم منه في بعض الأحوال ذلك بقرينة خارجية فيقع الخطأ فيه كثيرًا.
وقيل: يقدم القول مطلقًا، واختاره في الإحكام والمحصول.
وقيل: يقدم الفعل؛ لأنه أبين في الدلالة.','4','242',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1162,'وقيل: بالتوقف إلى الظهور لتساويهما في وجوب العمل.
واختار ابن الحاجب التوقف بالنسبة إلى النبي -صلى الله عليه وسلم- والأخذ بالقول بالنسبة إلينا.
وفرق بينهما بأنا متعبدون بالعمل فأخذنا بالقول لظهوره، ولا ضرورة بنا إلى الحكم بأحدهما بالنسبة إليه -صلى الله عليه وسلم.
ولعل سكوت المصنف عن الحكم بالنسبة إلى النبي -صلى الله عليه وسلم- لهذا والله أعلم.
الخامسة
أنه -عليه الصلاة والسلام- قبل النبوة تعبد -بضم التاء والعين- بشرع أي: كلف بشرع، لما ورد أنه كان يتعبد، كان يتحنث، ...','4','243',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1163,'كان يطوف، وتلك أعمال شرعية تعرف بالضرورة ممن يمارسها قصد الطاعة، وهو موافقة أمر الشارع، ولا يتصور من غير تعبد، فإن العقل بمجرده لا يحسنه، واختاره ابن الحاجب وغيره.
وعلى هذا فقيل: كلف بشرح نوح، وقيل: موسى، وقيل: عيسى وقيل: آدم، (عليهم الصلاة والسلام).
وقيل: جميع الشرائع شرع له.
وقيل: لا، أي لم يكن مكلفًا بشرع، ونقله القاضي أبو بكر عن جمهور المتكلمين.
وعلى هذا فانتفاؤه بالنقل أو العقل فيه خلاف.','4','244',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1164,'وقيل بالوقف، وبه قال إمام الحرمين والغزالي والآمدي.
ولا يخفى أن الخلاف في الفروع التي تختلف فيها الشرائع.
أما ما اتفقوا عليه كالتوحيد فلا شك في التعبد به.
وأما بعد النبوة: فالأكثر على المنع، من كونه متعبدًا بشريعة أصلاً، واختاره الإمام والآمدي وأشعر كلام المصنف باختياره.
وقيل: بل كان متعبدًا بذلك، أي مأمورًا بأخذ الأحكام من كتبهم.
صرح به الإمام، ولذا عبر عنه المصنف بقوله: "وقيل: أمر بالاقتباس".','4','245',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1165,'ويعبر عن هذا المذهب بأن: شرع من قبلنا شرع لنا.
واختاره ابن الحاجب.
وللشافعي -رضي الله عنه- قولان أصحهما الأول.
وأبطل المصنف الثاني بقوله: "ويكذبه" أي الثاني، انتظاره -صلى الله عليه وسلم- الوحي، مع وجود تلك الأحكام في شرع من تقدمه.
وعدم مراجعته -صلى الله عليه وسلم- كتبهم وأخبارهم في الوقائع.
وعدم مراجعتنا إذ لم يوجب علينا المراجعة.','4','246',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1166,'وفيه نظر مبين في الشرح.
قيل: راجع النبي -صلى الله عليه وسلم- التوراة في الرجم لما ترافع إليه اليهود في زنا المحصن.
(أخرجه الشيخان وأبو داود والترمذي والنسائي من حديث ابن عمر -رضي الله عنهما-).','4','247',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1167,'قلنا: مراجعته -صلى الله عليه وسلم- كانت للإلزام.
فإن اليهود أنكروا أن يكون في التوراة وجوب الرجم، ولم يكن لإنشاء شرع استدل من جهة القائلين بأنه كان بعد النبوة متعبدًا بشرع: بآيات أمر الرسول -صلى الله عليه وسلم- فيها اقتفاء أي اتباع الأنبياء السالفة -عليهم الصلاة والسلام أجمعين.
كقوله تعالى: {شرع لكم من الدين ما وصى به نوحًا}.
وقوله تعالى: {ثم أوحينا إليك أن اتبع ملة إبراهيم حنيفًا}.
وقوله تعالى: {أولئك الذين هدى الله فبهداهم اقتده}.
وشرعهم من جملة الهدى.','4','248',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1168,'وقوله تعالى: {إنا أنزلنا التوراة فيها هدى ونور يحكم بها النبيون} الآية.
قلنا: أمر باتباعهم (في أصول) الشريعة، وكلياتها وهي الأشياء التي لم تختلف باختلاف الشرائع، وهي أصول الديانات والكليات الخمس، أي حفظ النفوس، والعقول، والأعراض، والأموال، والأنساب، واتباعه عليه -الصلاة والسلام- لهم في ذلك حصل له بالدليل القاطع لا عن مجرد تقليد.

* * *','4','249',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1169,'<span data-type="title" id=toc-177>الباب الثاني: في الأخبار</span>
وهو جمع خبر، والخبر نوع مخصوص من القول.
وهو قسم من الكلام النفساني.
واختلف هل هو مشترك بينهما؟ أو حقيقة في أحدهما، مجاز في الآخر؟ كالخلاف في الكلام، وقدم المصنف تعريفه.
فلذا لم يذكره هنا.
وفيه -أي: في الباب فصول.','4','251',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1170,'لأن الخبر من حيث هو خبر، يحتمل الصدق والكذب، لكنه قد يقطع بصدقه أو كذبه لأمور خارجية، وقد لا يقطع بواحد منهما لعدم عروض موجب القطع به.
فصار الخبر ثلاثة أقسام: ذكر لكل قسم فصلاً.
وهذا إذا قلنا: إن الخبر منحصر في الصدق والكذب وهو الحق.','4','252',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1171,'قال الجمهور: معنى صدق الخبر مطابقة حكمه للواقع.
وهو الخارج الذي يكون لنسبه الكلام الخبري.
وكذبه: عدم مطابقته للواقع.
وقيد بحكمه؛ لأن رجوع الصدق والكذب إلى الحكم أولاً، وبالذات وإلى الخبر ثانيًا وبالواسطة.
والمشهور فيما بين القوم أن احتمال الصدق والكذب، من خواص الخبر لا يجري في غيره من المركبات.
مثل الغلام الذي لزيد، ويا زيد الفاضل.
وله زيادة تحقيق مع فوائد أخرى في الشرح.

* * *','4','253',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1172,'<span data-type="title" id=toc-178>الفصل الأول: فيما علم صدقه</span>
وهو سبعة أقسام:
الأول: ما علم وجود مخبره -بفتح الباء- أي: الخبر الذي علم وجود المخبر به.
والعلم به إما بالضرورة كقولنا: الواحد نصف الاثنين.
أو بالاستدلال: مثل: العالم حادث، والخبر الموافق لخبر المعصوم.
الثاني: خبر الله -تعالى- فإنه يعلم صدقه وإلا -أي: لو لم نقل بذلك -لكنا في بعض الأوقات أكمل منه، وذلك محال.
وهذا القسم وما بعده علمنا فيه أولاً صدق الخبر، ثم استدللنا بصدقه على وقوع المخبر عنه بخلاف الأول.','4','254',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1173,'فإنا علمنا أولا وقوع المخبر عنه، ثم استدللنا بوقوعه على صدق المخبر.
الثالث: خبر الرسول -صلى الله عليه وسلم -والمعتمد في حصول العلم دعواه الصدق في كل الأمور، وظهور المعجزة على وفقه عقيب هذه الدعوى.
الرابع: خبر كل الأمة؛ لأن الإجماع حجة.','4','255',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1174,'واستشكل؛ لأنه إن أريد بالحجة، ما هو مقطوع كما صرح به الآمدي.
فالإجماع ليس كذلك، عند المصنف والآمدي والإمام.
وإن أريد بالحجة ما يجب العمل به، فلا يلزم أن يكون مقطوعًا به.
وحكي في هذه المسألة قولان.
وإذا اجمعوا على وفق خبره، فالصحيح أنه لا يدل على صدقه في نفس الأمر مطلقًا، وليست هذه مسألة المصنف، بل غيرها.
الخامس: خبر جمع عظيم يستحيل تواطؤهم على الكذب عن شيء من أحوالهم، كالشهرة فيقطع بأن فيهم صادقًا وإن لم يتعين.','4','256',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1175,'السادس: الخبر المحفوف بالقرائن كخبر ملك يموت ولده، ولا مريض عنده سواه مع صراخ، وجنازة، وخروج المخدرات على حال منكرة غير معتادة، وخروج الملك وأكابر مملكته مع الجنازة.
وهذا ما اختاره إمام الحرمين والغزالي والإمام والآمدي، وابن الحاجب، والأكثرون على خلافه.
السابع: الخبر المتواتر.
والتواتر لغة: تتابع أمور واحدًا بعد واحد بفترة من الوتر.
ومنه {ثم أرسلنا رسلنا تترى}.','4','257',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1176,'والمتواتر في الاصطلاح: هو خبر بلغت رواته في الكثرة مبلغًا أحالت العادة تواطؤهم أي توافقهم على الكذب.
وفيه -أي في التواتر -مسائل:
الأولى
إنه يفيد العلم بصدقه إذا وجدت شرائطه بإتفاق العقلاء.
خلافًا للسمنية -بضم السين وفتح الميم -قوم من عبدة الأوثان.','4','258',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1177,'وكذا البراهمة.
وقيل: يفيد العلم بصدقه عن الموجود، لا عن الماضي لنا على أنه يفيد العلم بصدقه مطلقًا: أنا نعلم بالضرورة وجود البلاد النائية، -أي: البعيدة -كبغداد والصين، والأشخاص الماضية، كالأنبياء والصحابة والخلفاء، كما نعلم المحسوسات، ولا فرق بينهما فيما يعود إلى الجزم، وما ذلك إلا بالأخبار قطعًا.
قيل إيرادًا على إفادة العلم: إنا نجد التفاوت بينه -أي بين الخبر المتواتر -وبين قولنا: الواحد نصف الاثنين.','4','259',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1178,'فإنا إذا عرضنا على أنفسنا وجود البلاد البعيدة والأشخاص الماضية.
وقولنا: الواحد نصف الاثنين، وجدنا الثاني أقوى بالضرورة.
ولو حصل العلم بالمتواتر ضرورة، لما فرقنا بينه، وبين غيره من المحسوسات.
فإن حصول التفاوت دليل احتمال النقيض، واحتمال النقيض مناف للعلم.
قلنا: إنما حصلت التفرقة للاستئناس وألفة النفس فإنه سبب سرعة الجزم، وعدمه سبب بطء الجزم، لا أن في ذاتيهما تفاوتًا حتى يحتمل النقيض.
واعترض بأنه يقتضي تسليم تفاوت العلوم، والمشهور أنها لا تتفاوت.','4','260',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1179,'وأجيب: بأن مراد المصنف منع التفاوت، واستدل بالاسئناس.
قاله العراقي.
وهنا إشكالات بأجوبتها مع فوائد أخرى في الأصل.','4','261',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1180,'الثانية
إذا تواتر الخبر أفاد العلم عقب التواتر ضرورة.
لفظيًا كان: وهو ما اتفق رواته على قصة واحدة.
أو معنويًا: وهو ما نقل رواته قضايا بينها قدر مشترك -كما سيجيء إن شاء الله تعالى.
فلا حاجة إلى النظر والكسب، وبه قال الجمهور، واختاره الإمام الرازي وابن الحاجب.','4','262',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1181,'خلافًا لإمام الحرمين، والحجة الغزالي، والكعبي، وأبي الحسين البصري المعتزليان حيث قالوا: إنه نظري.
وفسر إمام الحرمين: كونه نظريًا، كما أفصح به الغزالي التابع له، أخذًا من كلام الكعبي بكونه يتوقف على مقدمات حاصلة عند السامع، وهي المحققة لكونه الخبر متواترًا من كونه خبر جمع، وكونهم بحيث يمتنع تواطؤهم على الكذب، وكونه عن محوس لا الاحتياج إلى النظر عقب سماع المتواتر.
فلا خلاف في المعنى في أنه ضروري، لأنه توقفه على تلك المقدمات لا ينافي كونه ضروريًا.','4','263',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1182,'ففي نقل المصنف نظر، إلا أن يؤول بما قالوه.','4','264',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1183,'وتوقف الشريف المرتضى والآمدي (عن القول بواحد من الضروري والنظري لتعارض دليليهما).
لنا: لو كان الخبر المتواتر نظريًا، لو يحصل لمن لا يأتي له النظر كالبله والصبيان، لكنه حاصل لهم فانتفى الأول.','4','266',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1184,'واعترضه النقشواني بمنع حصول العلم بالتواتر للصبيان حال طفولتهم، وعدم حصول النظر والتمييز لهم، حال كونهم مراهقين.
قال: وكذلك نقول في البله باعتبار الحالتين.
قيل: دليلاً للكعبي ومن تبعه: العلم بمقتضى الخبر المتواتر يتوقف على العلم بعدم تواطئهم، أي بعدم تواطؤ المخبرين على الكذب في العادة، وعلى أن لا داعي لهم إلى الكذب في حصول منفعة أو دفع مضرة.
وهذه المقدمات نظرية، والعلم متوقف على هذا النظر فيكون نظريًا من باب أولى.
قلنا: هذا الذي ذكرتموه من المقدمات حاصل بقوة قرينة من الفعل، فلا حاجة إلى النظر، أي: هذا النظر وإن كان بالقوة،','4','267',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1185,'لكنها قرينة من الفعل، فإنه من قبيل قضايا قياساتها معها، أي: إذا حصل طرفا المطلوب في الذهن حصلت هذه المقدمات من غير تعب وتأمل.
أو نقول: العلم بالصدق ضروري يحصل بالعادة لا بالمقدمات، فاستغنى عن الترتيب، ولا ينافيه صورة الترتيب.
فإن وجوده لا يوجب الاحتياج إليه، فإنها ممكنة في كل ضروري، لأنك إذا قلت: الأربعة زوج، فلك أن تقول: لأنه منقسم بمتساويين وكل منقسم بمتساويين زوج.
الثالثة
ضابطه، أي ضابط الخبر المتواتر: إفادة العلم بصدقه، وإذا علم ذلك عادة علم وجود الشرائط، وإذا لم يعلم تبينًا عدم التواتر، وفقد شرط.','4','268',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1186,'وليس الضابط في حصول العلم سبق العلم بها، كما يقوله من يرى أنه نظري.
والشرائط أربعة:
اثنان في السامعين، واثنان في المخبرين.
وأشار إليها بقوله: وشرطه: أن لا يعلمه السامع ضرورة لأنه مع علمه بمدلوله لا يفيده المتواتر علمًا؛ لامتناع تحصيل الحاصل.','4','269',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1187,'وشرطه أيضًا: أن لا يعتقد السامع خلافه، لشبهة دليل إن كان من العلماء، أو تقليد إن كان من العوام.
فإن ارتسام ذلك في ذهنه واستقراره فيه، واعتقاده له يمنعه من قبول غيره والإصغاء إليه.
وهذان الشرطان تبع فيهما المصنف الإمام الرازي والآمدي.
ولم يذكرهما الإمام الرازي في المعالم، ولا ابن الحاجب في مختصره.
قال العراقي: هذا شرط نقله الإمام عن الشريف المرتضى، ولم يصرح بموافقته ولا مخالفته، والحق خلافه.
وشرطه: أن يكون سند المخبرين في الإخبار إحساسًا به، أي إدراكه بإحدى الحواس الخمس.','4','270',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1188,'فإن أخبروا عما يستند إلى الدليل العقلي، فلا يفيد قطعًا؛ لأن التباس الدليل عليهم محتمل.
قال في البرهان: ولا معنى لتقييد المستند بالمحسوس، فإن المطلوب صدور الخبر عن العلم الضروري فالوجه التقييد به لتدخل قرائن الأحوال، وتبعه في المحصول.
وفيه نظر: فإن قرائن الأحوال لها استناد إلى الحس، وليست عقلية محضة.
فلذا عدل المصنف إلى العبارة المشهورة.
وشرطه مبلغهم عددًا يمتنع تواطؤهم على الكذب، أي بلوغ عدد المخبرين في جميع الطبقات، في الأول والآخر والوسط مبلغًا يمتنع بحسب العادة أن يتوافقوا على الكذب.','4','271',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1189,'ويختلف ذلك باختلاف المخبرين والوقائع والقرائن.
ولا حاجة إلى اشتراط كونهم عالمين بالمخبر عنه، كما بين في الشرح.
وعلم من اقتصار المصنف على اشتراط الأربعة: أنه لا يشترط في المخبرين الإسلام، ولا العدالة، ولا اختلاف الدين، والبلد','4','272',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1190,'والوطن، والنسب، (ولا وجود الإمام المعصوم، ولا وجود أهل الذلة ولا كثرتهم) بحيث لا يحصرهم عدد ولا يحويهم بلد، وهو','4','273',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1191,'الأصح لحصول العلم بدون ذلك.
وقال القاضي أبو بكر الباقلاني: لا يكفي خبر الأربعة الصادقين في إفادة العلم، وإلا، أي: لو أفاد خبر الأربعة في إفادة العلم لأفاد قول كل أربعة صادقين.
إذ حكم المتماثلات سواء، فلا يجب تزكية شهود الزنا، لحصول العلم بالصدق أو الكذب.
إذ عند الصدق لا يحتاج إلى التزكية، وعند الكذب لا يزكون أيضًا.
فثبت أنه لو أفادت الأربعة العلم لم يجب تزكية الشهود، وتزكيتهم واجبة قطعًا فبطل الأول.','4','274',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1192,'وتوقف القاضي في الخمسة، أي تردد، لاحتمال أنها لا تفيد العلم، إذ لو أفادته لأفاد قول كل خمسة.
ويلزم من ذلك أن لا يجب تزكيتها إذا شهدت بالزنا بعين ما تقدم.
ويحتمل أن يفيد العلم والتزكية، لاحتمال كذب واحد، فيبقى أصل الحجة بخلاف الأربعة، فإنه إذا كذب واحد منهم لم يبق نصاب شهادة الزنا.
ورد قول القاضي بعدم الاكتفاء بالأربعة: بأن حصول العلم بفعل الله -تعالى -عند القاضي والأشاعرة، فلا يجب الأطراد، لجواز أن يخلق الله -تعالى -العلم بقول أربعة دون أربعة.','4','275',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1193,'ورد أيضًا: بالفرق بين الرواية والشهادة.
فإن الأربعة في الرواية زائدة على القدر المشروط، بخلاف الأربعة في الشهادة، فلا يلزم من ترتب العلم على الأول ترتبه على الثاني.
وأيضًا الشهادة تقتضي شرعًا خاصًا، فلا يبعد فيها الاتفاق على المشهود عليه لعداوة بخلاف الرواية.
والحاصل: أن أمر الشهادة أضيق، وبالاحتياط أجدر.
وعزى بعض المتأخرين عدم الاكتفاء بأربعة للشافعية.','4','276',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1194,'وشرط -أي شرط قوم -أن أقل عدد الجمع الذي يفيد خبره العلم اثنا عشر، كنقباء موسى -عليه السلاة والسلام -المذكورين في قوله تعالى: {وبعثنا منهم اثني عشر نقيبًا}.
بعثوا كما قال أهل التفسير للكنعانيين، طليعة لبني إسرائيل المأمورين بجهادهم ليخبروهم بحالهم.
فكونهم على هذا العدد ليس إلا لأنه أقل ما يفيد العلم المطلوب في مثل ذلك.
(وقيل: الشرط عشرون، في أقل ما يكون) لقوله تعالى:','4','277',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1195,'{إن يكن منكم عشرون صابرون يغلبوا مائتين}.
فيتوقف بعث عشرين لمائتين على إخبارهم بصبرهم فكونهم على هذا العدد ليس إلا لأنه أقل مما يفيد العلم المطلوب في مثل ذلك.
وقيل: أقل ما يكون أربعون (لقوله تعالى): {يا أيها النبي حسبك الله ومن اتبعك من المؤمنين} وكانوا أربعين.
فإخبار الله تعالى عنهم بأنهم كافؤا (رسول الله) صلى الله عليه وسلم، يستدعى إخبارهم عن أنفسهم بذلك ليطمئن قلبه.
فكونهم على هذا العدد ليس إلا، لأنه أقل ما يفيد العلم المطلوب في مثل ذلك.','4','278',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1196,'وقيل: أقله سبعون، لقوله تعالى: {واختار موسى قومه سبعين رجلاً لميقاتنا} للاعتذار إلى الله تعالى من عبادة العجل، ولسماعهم كلامه تعالى ليخبروا قومهم.
وقيل: أقله ثلاثمائة وبضعة عشر، عدد أهل غزوة بدر، لأن الغزوة تواترت عنهم.
والبضع -بكسر الباء -وقد تفتح: ما بين الثلاث إلى التسع.
وعبارة إمام الحرمين وغيره: "ثلاثة عشر".
وزاد أهل السير: وأربعة عشر، وخمسة عشر، وستة عشر،','4','279',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1197,'وثمانية عشر، وتسعة عشر.
قال العراقي: ولا تنافي بين قول إمام الحرمين: إنهم ثلاثمائة وثلاثة عشر، وبين ما في كثير من كتب الحديث.
إنهم ثلاثمائة رجل وخمسة رجال؛ لأن النبي -صلى الله عليه وسلم -أدخل ثمانية من المؤمنين الذين لم يحضروها في عدادهم.
وأجرى عليهم حكمهم، فكملوا بذلك ثلاثمائة وثلاثة عشر، والحاضرون ثلاثمائة وخمسة رجال فقط.','4','280',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1198,'والكل ضعيف أي اشتراط الأقل على كل الأقوال؛ لأنها تقييدات بلا دليل.
وما ذكروه على تقدير صحتها يجوز أن تكون تلك من خواص المعدودين، لا من الأعداد.
فالحق أنه غير منحصر في عدد مخصوص، بل يختلف، وضابطه -كما تقدم -ما حصل العلم.
لأنا نقطع بحصول العلم بما ذكرنا من المتواترات من غير علم بعدد مخصوص، لا قبل حصول العلم كما يقتضيه رأي من يقول: إنه نظري، ولا بعده على رأينا، ولا سبيل إلى العلم به عادة؛ لأنه يتقوى الاعتقاد بتدريج، كما يحصل كمال العقل بتدريج خفي، والقوة البشرية قاصرة عن ضبط ذلك.
ثم الجمع الذي يستحيل تواطؤهم على الكذب إن أخبروا عن عيان أي عن مشاهدة.','4','281',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1199,'وأخرج به المدرك ببقية الحواس الخمس فذاك أي: تم به التواتر بشروطه، وإلا أي: وإن لم يخبروا عن عيان، بأن كانوا طبقات، فلم يخبروا عن عيان، بأن كانوا طبقات، فلم يخبروا عن عيان إلا الطبقة الأولى منهم، فيشترط ذلك أي: كونهم جميعًا يمتنع تواطؤهم على الكذب في كل الطبقات ليفيد خبرهم العلم، بخلاف ما إذا لم يكونوا كذلك في غير الطبقة الأولى، فلا يفيد خبرهم العلم.
ومن هذا تبين أن المتواتر في الطبقة الأولى، قد يكون آحادًا فيما قبلها.
وحمل بعضهم عليه: القراءات الشاذة.
وقوله: "عن عيان" غير واف -فإنه بكسر العين -هي الرؤية قاله','4','282',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1200,'الجوهري.
والخبر قد لا يكون مستندًا إليها، ولعله قصد بالعيان: اليقين المستند للحواس.
الرابعة
إذا كثرة الأخبار في الوقائع، واختلفت فيها، لكن كل واحد منها يشتمل على معنى مشترك بينها، بجهة التضمن أو الالتزام، حصل العلم بالقدر المشترك.
ويسمى المتواتر من جهة المعنى، كما مر.
مثلًا: لو أخبر واحد بأن حاتمًا أعطي دينارًا، وآخر أنه أعطي','4','283',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1201,'جملًا، وهلم جرًّا حتى يبلغ المخبرون عدد التواتر، فيقطع بأنه تواتر القدر المشترك لوجوده في الكل، أي في كل خبر من هذه الأخبار.
والقدر المشترك هنا هو الجود، لأنه لما كان إفادة ما ينبغي لا لغرض، فهو جزء من كل إعطاء مخصوص متواتر.
وهذا بالنظر إلى الظاهر، وإلا فالجود صفة في النفس هي مبدأ تلك الإفادة.
واعلم: أنه لا شيء من الوقائع بانفرادها يدل على السخاوة.
بمعنى حصول العلم بها منها، بل القدر المشترك من الجزئيات، هو السخاوة وهو متواتر.
لا بمعنى أن شيئًا من الوقائع الخبرية معلوم الصدق قطعًا.
كيف وهو آحاد.','4','284',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1202,'بل بمعنى أن العلم القطعي بالقدر المشترك يحصل من سماعها بطريق العادة.
تنبيه:
جرًّا- منون- وانتصب على المصدر، أي جروا جرًّا، أو على الحال أو على التمييز.
ومعنى هلم جرًّا: سيروا وتمهلوا في سيركم.
ثم استعمل فيما حصل عليه الدوام من الأعمال.
* * *','4','285',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1203,'<span data-type="title" id=toc-183>الفصل الثاني: فيما علم كذبه من الأخبار بالنظر إلى أمور خارجة عنه</span>
وهو قسمان: وزاد الإمام قسمًا ثالثًا:
وهو: ما نقل عنه، صلى الله عليه وسلم، بعد استقرار الأخبار.
ثم فتش عنه، فلم يوجد في بطون الصحف، ولا في صدور الرواة.
واستشكل: بأن عدم الوجدان يفيد الظن دون القطع.','4','286',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1204,'قلت: فلعل المصنف تركه لذا، والله أعلم.
الأول: ما علم خلافه: إما ضرورة، مثل قول القائل: النقيضان يجتمعان.
أو استدلالًا: كالخبر المخالف لما علم صدقة من خبر الله- تعالى- نحو قول الفلسفي: العالم قديم، فهذا مقطوع بكذبه.
الثاني: ما لو صح، أي الخبر الذي لو صح لتوفرت الدواعي على نقله، إما لغرابته: كسقوط الخطيب عن المنبر يوم الجمعة.
أو لتعلقه: بأصل من أصول الدين، كالنص على إمامة علي -','4','287',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1205,'رضي الله عنه- فعدم تواتره يدل على عدم صحته لمخالفته للعادة.
فإنا نجد من أنفسنا العلم بكذبه قطعًا.
ولولا أن هذا الأصل مذكور في العقول لما قطعنا بكذب من ادعى أن بين مكة والمدينة أكبر منهما.
ولما علمنا نفيها، لكنا نعلم النفي قطعًا، ولا مستند لهذا العلم إلا عدم النقل المتواتر.
وإليه أشار بقوله: «كما يعلم أن لا بلد بين مكة والمدينة أكبر منهما، إذ لو كان لنقل».','4','288',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1206,'وادعت الشيعة: «أنا لا نقطع بكذبه» لتجويز العقل صدقه.
وقالوا: إن النص دل على إمامة علي- كرم الله وجهه- نحو: «أنت الخليفة من بعدي». ولم يتواتر، كما لم يتواتر غيره من الأمور','4','289',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1207,'المهمة؛ لأن الحوامل المقدرة على كتمان الأخبار كثيرة لا يمكن ضبطها فكيف الجزم بعدمها.','4','290',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1208,'ومع جوازها لا يحصل الجزم.
فمن ذلك الإقامة فإنه تعم به البلوى، وتمس الحاجة إليها، ونقل آحادًا إفرادها وتثنيتها.','4','291',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1209,'وقراءة التسمية في الصلاة وتركها.
وكذلك معجزات الرسول- صلى الله عليه وسلم- كانشقاق القمر، وتسبيح الحصى في يده، ومشي الشجر ونحوها. فلم ينقل متواترًا بل آحادًا.
قلنا: الأولان: وهما الإقامة والتسمية من الفروع.','4','292',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1210,'ولا كفر ولا بدعة في مخالفتهما، وإن كان المخالف فيهما مخطئًا. فليست مما ذكرنا، لعدم توفر الدواعي على نقلها، بخلاف الإمامة، فإنها من أصول الدين، ومخالفتها فتنة وبدعة. وهنا تحقيق في الشرح.
وأما تلك المعجزات: فلقلة المشاهدين عدم التواتر، وهي غير محل النزاع، مع أنا لا نسلم أنها مما تتوفر الدواعي على نقلها، فإنها إنما تنقل لتستمر بين الناس، وقد استغنى عنها وعن استمرارها بالقرآن العظيم، الباقي على وجه كل زمان، الدائر على كل لسان في كل مكان.','4','293',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1211,'بخلاف ما يذكر في إمامة علي- رضي الله عنه- فإنه لا يعرف، ولو كان لما خفي على الصحابة الذين بايعوا أبا بكر.
وهنا تحقيقات حسنة مع فوائد في الأصل.
مسألة
بعض ما نسب إلى الرسول (صلى الله عليه وسلم) كذب لقوله- صلى الله عليه وسلم-: «سيكذب علي».
فإن كان قال ذلك فلا بد من وقوعه، وإلا ففيه كذب عليه. وفيه نظر مبين في الأصل.','4','294',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1212,'وأيضًا: لأن منها أي من الأخبار المنسوبة له- صلى الله عليه وسلم- ما لا يحتمل التأويل.
وهو معارض للدليل العقلي فيمتنع صدوره عنه- صلى الله عليه وسلم- لعصمته عن قول الباطل.
وحديث: «سيكذب علي».
قال الزركشي: لا يعرف بهذا اللفظ، ولعله مروي بالمعنى مما رواه مسلم مرفوعًا: «يكون في آخر الزمان دجالون كذابون، يأتونكم من الأحاديث بما لم تسمعوا أنتم ولا آباؤكم، فإياكم وإياهم لا','4','295',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1213,'يضلونكم».
وسببه، أي سبب وقوع الكذب، نسيان الراوي، بأن سمع خبرًا وطال عهده به، فنسي، فزاد أو نقص، أو عزاه للنبي- صلى الله عليه وسلم- وليس من كلامه.
أو غير معناه أو غلطه، بأن أراد أن ينطق بلفظ، فسبق لسانه إلى غيره ولم يشعر.
أو كان يرى جواز النقل بالمعنى، فأبدل مكان اللفظ المسموع، لفظًا آخر لا يطابقه، ظانًا المطابقة.
أو افتراء الملاحدة، أي الزنادقة، وغيرهم من الكفار، فإنهم وضعوا أحاديث تخالف المعقول لتنفير العقلاء عن شريعته المطهرة.
أو وضع الحديث للترغيب والترهيب، أو للارتزاق والاحتراف، أو','4','296',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1214,'والاحتساب وطلب الأجر أو الانتصار لآرائهم.
* * *','4','297',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1215,'<span data-type="title" id=toc-185>الفصل الثالث: فيما ظن صدقه من الأخبار وهو خبر العدل الواحد</span>.
واحترز «بالعدل» عن الفاسق والمجهول.
و«بالواحد» عن المتواتر، واحدًا كان راويه، أو أكثر، أفاد','4','298',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1216,'العلم بالقرائن المنفصلة أم لا.
والنظر فيه، أي في هذا الفصل منحصر في طرفين.
الأول: في وجوب العمل به.','4','299',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1217,'وقد دل عليه السمع وبه قال الأكثرون.
وقال ابن سريج والقفال وأبو الحسين البصري: دل عليه العقل أيضًا مع السمع.
وأنكر قوم: وجوب العمل بخبر الواحد.
ثم اختلفوا:
فقالت طائفة: لأنه لم يثبت على الوجوب دليل، ولو ثبت لأوجبناه وقالت أخرى: إنما لم يجب؛ لأن الدليل قام على عدم الوجوب.
ثم اختلف هؤلاء في الدليل المانع له، هل هو شرعي أو عقلي؟
وإليه أشار بقوله: «لعدم الدليل أو للدليل على عدمه شرعًا أو عقلًا».','4','300',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1218,'وأحاله آخرون، فقالوا: ورود العمل بخبر الواحد مستحيل عقلًا.
واتفقوا كلهم على الوجوب، أي: وجوب العمل بخبر الواحد في الفتوى والشهادة والأمور الدنيوية، كإخبار طبيب أو غيره بمضرة، شيء، ونحو ذلك من الآراء والحروب. وعبارة جمع الجوامع كالمصنف.
لكن عبارة المحصول: واتفقوا على الجواز.
وهنا مذاهب أخرى في الأصل.
لنا: على وجوب العمل بخبر الواحد وجوه ثلاثة:','4','301',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1219,'الأول: أنه تعالى أوجب الحذر أي الاحتراز. عن الشيء، بإنذار طائفة من الفرقة بقوله تعالى: {فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم إذا رجعوا إليهم لعلهم يحذرون}.
والإنذار: الخبر المخوف.
ويلزم منه وجوب العمل بخبر الواحد.
أما كونه تعالى أوجب الحذر، فلأن «لعل» للوجوب لتعذر','4','302',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1220,'حملها على حقيقة الترجي في حق الله- تعالى.
فحملت على أقرب المجازات، لأن الترجي وإن لم يكن طلبًا، بل توقعًا، لكن لا يفهم منه في حق الله تعالى إلا الطلب إطلاقًا للملزوم، وإرادة اللازم.
لأن المترجي طالب (لما يرجوه.
فالطلب من لوازم المترجي، فالله تعالى طالب) للحذر.
ويعرف هذا من جواب المصنف للشبهة الآتية؛ فلذا لم يذكره هنا.
وأما أن الأمر للوجوب فظاهر مما تقدم فلذا لم يذكره.
أو يفهم ذلك من إيجاب الحذر بإنذار طائفة، والطائفة من كل فرقة لا تكون أهل التواتر.
وذلك لأن الفرقة اسم ثلاثة فصاعدًا.
فالطائفة منها: واحد أو اثنان.','4','303',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1221,'وبالجملة: فلا يلزم أن يبلغ حد التواتر وهو المراد بالآحاد، فقد أوجب الحذر بقول الآحاد.
قيل: اعتراضًا على هذا الدليل: لعل للترجي لا للوجوب.
قلنا: تعذر الحمل على الترجي، فيحمل على الإيجاب لمشاركته في التوقع. وقد تقدم.
قيل أيضًا: الإنذار: هو الفتوى، لا الخبر المخوف، والفتوى يقبل فيها خبر الواحد، إذ هو اللائق بالفقه.
لأن الفقيه إنما يحتاج إليه في الفتوى.','4','304',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1222,'قلنا: يلزم تخصيص الإنذار بالفتوى مع أنها عامة فيها وفي الرواية، وتخصيص القوم بغير المجتهدين من المقلدين؛ لأن المجتهد لا يقلد مجتهدًا في فتواه، بخلاف ما إذا حمل الإنذار على الرواية، أو على ما هو أعم فإنه ينتفي التخصيصات.
أما الأول: وهو الفتوى فواضح، ولذا لم يذكره.
وأما الثاني: فلأن الرواية ينتفع بها المجتهد وغيره من المقلدين.
فالمجتهد في الأحكام، والمقلد في الانزجار، وحصول الثواب في نقلها لغيره وغير ذلك.
قيل أيضًا: لو كان المراد بالفرقة ثلاثة، فيلزم أن يخرج من كل ثلاثة واحد لأن «لولا» للتحضيض.','4','305',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1223,'تقديره هلا، وليس كذلك إجماعًا.
قلنا: خص النص فيه.
بيانه: أن النص عام في أن يخرج من كل ثلاثة واحد، وقبول خبر الواحد، ووجوب العمل به.
لكن خص بالنسبة إلى أن يخرج من كل ثلاثة واحد بالإجماع، لئلا تتعطل معايش الناس، وبقي على عمومه، في الإنذار ووجوب العمل به.
وهذا الدليل إنما يتم إذا رجعنا الضمير في «ليتفقهوا» و «لينذروا» للنافرين الذاهبين وهو أحد قولي المفسرين.
وأما إذا رجعناه للمقيمين الملازمين للنبي- صلى الله عليه وسلم- كما هو القول الآخر.
قال العراقي: وهو الصحيح، فلا يتم وهنا تحقيقات في','4','306',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1224,'الأصل.
الدليل الثاني على أنه يجب العمل بخبر الواحد: أنه لو لم يقبل خبر الواحد لما علل بالفسق، وذلك لأن خبر الواحد على هذا التقدير، يقتضي عدم القبول لذاته.
وهو كونه خبر واحد، فيمتنع تعليل عدم قبوله بغيره.
لأن ما بالذات لا يكون بالغير، إذ لو كان معللًا بالغير،','4','307',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1225,'لاقتضى حصوله به، مع كونه حاصلًا قبل ذلك أيضًا لكونه معللًا بالذات وهو محال. والثاني: وهو امتناع تعليله بالفسق باطل، لقوله تعالى: {إن جاءكم فاسق بنبأ فتبينوا} إذ علل رده بالفسق، فعلل عدم قبول قول الفاسقين بالتبيين؛ لأن ترتيب الحكم على الوصف المناسب بالفاء يدل على العلية.
فيكون التبيين معللًا بالفسق.
والتبيين: هو ظهور طلب صدق الخبر.
فيكون الخبر لذاته مقبولًا، وعدم قبوله موقوف على عدم ظهور صدقه، فإذا انتفى الفسق الذي هو علة التبيين، بقي كون الخبر الواحد مقبولًا لذاته وهو المطلوب.','4','308',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1226,'الدليل الثالث (على أنه يجب العمل بخبر الواحد).
القياس على الفتوى، والشهادة، والجامع ظهور المصلحة أو دفع المفسدة.
قيل: الفتوى والشهادة يقتضيان شرعًا، أي حكمًا خاصًّا في حق شخص واحد، وهو المستفتي والمشهود له.
والرواية بخلافه، فإنها تقتضي حكمًا عامًّا في حق الكل، ولا يلزم من تجويز قبول خبر الواحد في حق شخص معين يخطئ أو يصيب تجويزه في حق الكل، فإن خطره وضرره أعظم من الواحد.
ورد هذا الفرق بأصل الفتوى فإن اتباع الظن، لا يختص بمسألة ولا بشخص.','4','309',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1227,'وأيضًا: فإفتاء المفتي لا يختص بذلك المستفتي، بل يعم كل من وقع له مثل واقعته.
قيل: دليلًا لمنع العمل بخبر الواحد: أنه لو جاز العمل بخبر الواحد واتباع الظن، لجاز اتباع الأنبياء الذين يدعون النبوة من غير معجزة، بل بمجرد كونه عدلًا.
ولجاز الاعتقاد في معرفة الله- تعالى- وصفاته بالظن، قياسًا على الرواية، وأنه غير جائز بل لا بد من اليقين اتفاقًا.
قلنا: ما الجامع بين الإلهيات والنبوات، وبين خبر الواحد، فإن أبدوا جامعًا، مثل دفع ضرر المظنون.
فرقنا بأن الخطأ في الإلهيات والنبوات كفر وبدعة، بخلاف','4','310',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1228,'الفروع.
قيل أيضًا: لو كان خبر الواحد مقبولًا، لكان العمل بالظن واجبًا في ثبوت الحكم الشرعي، واللازم باطل، فالملزوم مثله.
أما الملازمة: فلأن خبر الواحد لا يفيد إلا الظن.
وأما بطلان اللازم، فلأن حكم الشرع يتبع المصلحة على معنى أنه يستلزمها.
فإن الاستقراء: دل على أن أحكامه تتبع لمصالح العباد تفضلًا وإحسانًا، والظن الحاصل من خبر الواحد لا يجعل ما ليس بمصلحة مصلحة؛ لأن الظن يخطئ ويصيب. فلا يعول عليه.
قلنا: ما ذكرتم من الدليل على عدم اعتبار الظن، منقوض بالفتوى، والأمور الدنيوية، فإن من قال: هذا والطعام مسموم وجب قبوله، والاحتراز عنه مع أنه يفيد الظن، والإجماع حاصل على اعتبار الظن فيها.','4','311',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1229,'الطرف الثاني في شرائط العمل به أي بخبر الواحد، وهو إما في','4','313',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1230,'المخبر: باسم الفاعل وهو الراوي.
أو المخبر عنه: وهو مدلول الخبر، أو الخبر نفسه وهو اللفظ.
أما الأول: فصفات تغلب على الظن أن المخبر صادق.
هذا ضابطها الإجمالي: وهي خمس عند التفصيل- على رأيه.
الأول: التكليف، فلا يقبل المجنون، لأنه لا يمكن الاحتراز عن الخلل سواء أطبق جنونه أو تقطع وأثر في زمن إفاقته.
وكذا الصبي غير المميز قطعًا، وكذا المميز عند الجمهور.
فإن غير المكلف لا يمنعه خشية الله- تعالى- أن يكذب لعلمه بأنه غير مكلف، فلا يحرم عليه الكذب، فلا إثم عليه فيه، فلا مانع من إقدامه عليه، فلا يحصل ظن صدقه، وهو الموجب للعمل.','4','314',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1231,'والصحيح قبول خبر الصبي في الإذن في دخول الدار، وحمل الهدية، إن لم يجرب عليه الكذب.
وكذا إخباره بطلب صاحب الوليمة لشخص، فإنه تجب به الإجابة، كما صرح به الماوردي والروياني إلا أنه شرط أن يقع في قلب المدعو صدقه.
وفي باب الأذان من شرح المهذب للنووي عن الجمهور قبول رواية الصبي في ما طريقه المشاهدة دون ما طريقه الاجتهاد.
قيل: يقبل الصبي المميز مطلقًا إن علم منه التحرز عن الكذب، وما ذاك إلا أنه يصح الاقتداء بالصبي المميز اعتمادًا على خبره بطهره.','4','315',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1232,'قلنا: صحة الاقتداء به ليست مستندة إلى قبول إخباره بطهره، لعدم توقف صحة صلاة المأموم على طهره، ؛ لأن المأموم متى لم يظن حدث الإمام صحت صلاته، وإن تبين حدث الإمام.
وأما الرواية فشرط صحتها السماع.
ومحل الخلاف: أن يكون المخبر به رواية محضة.
فلو أخبر برؤية الهلال، وجعلناه رواية، لم يقبل جزمًا.
هذا كله إذا سمع وروى قبل البلوغ.
فإن تحمل قبل البلوغ خبرًا ثم بلغ، وأدى ذلك الخبر بعد البلوغ قيل: قياسًا على الشهادة فإنها مقبولة اتفاقًا.','4','316',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1233,'فروايته أولى بالقبول، والمحذور السابق منتف هنا.
وأيضًا: فإن الإجماع منعقد على إحضار الصبيان مجالس الحديث، فلو لم يعتبر نقله لما أفاد ذلك.
وفيه نظر: لجواز أن يكون للتبرك (ولذلك يحضرون من لا يضبط) أو لاعتياد ملازمة الخبر أو سهولة الحفظ.
وجواب البعض عن هذا النظر بأنه: لا منافاة بينها وبين السماع سهو.
(قال العراقي: فكان ينبغي أن يزيد، يعني المصنف- فيه مع إجماعهم على قبول روايتهم بعد البلوغ ما تحملوه زمن الصبا.','4','317',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1234,'الشرط) الثاني: كونه، أي: كون الراوي من أهل القبلة، أي من أهل ملتنا، فلا تقبل رواية الكافر المخالف في القبلة، وهو المخالف لملة الإسلام إجماعًا.
وتقبل رواية الكافر الموافق الذي يصلي لقبلتنا.
كالمجسمة إن اعتقدوا حرمة الكذب، فإنه أي: هذا الاعتقاد يمنعه عنه، أي: عن الكذب، فيغلب على الظن صدقه؛ لأن المقتضى قد وجد، والأصل عدم المعارض.
والمصنف تابع في ذلك الإمام الرازي.','4','318',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1235,'وقاسه القاضيان: أبو بكر الباقلاني، وعبد الجبار بالفاسق المسلم، والكافر المخالف، في أنه لا تقبل روايته مطلقًا بجامع (الفسق والكفر).
واختاره الآمدي ونقله عن الأكثرين وجزم به ابن الحاجب.
قال المصنف: ورد بالفرق بين الكافر الموافق، وبين الفاسق.
لأن الموافق لا يعلم فسق نفسه، ويجتنب الكذب لما يعتقده من التدين والخشية والفاسق يتجرأ، وبالفرق بين الموافق والمخالف، وهو أن الكافر المخالف أغلظ كفرًا فيجب إذلاله، وقبول الرواية منصب شريف لا يليق به.','4','319',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1236,'واقتضى كلام المصنف أن المبتدع إذا لم يكفر ببدعته، ويحرم الكذب، تقبل روايته من باب أولى، لأنه فيه مع تأويله (في الابتداع) سواء دعى الناس إليه أم لا.
وقيل: لا يقبل مطلقًا لابتداعه المفسق له.
ونقل عن مالك وأحمد- رضي الله عنهما- قبول روايته.
إلا أن يدعوا الناس إلى بدعته، لأنه لا يؤمن فيه أن يضع الحديث على وفقها.
ونقل ابن حبان عن المحدثين: الاتفاق عليه.
وعزاه ابن الصلاح للأكثرين، وقال: إنه أعدل المذاهب وأولاها.','4','320',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1237,'أما من تجوز الكذب فلا يقبل، كفر ببدعته أم لا؟
قال الشافعي- رضي الله عنه-: أقبل شهادة أهل الأهواء إلا الخطابية من الرافضة؛ لأنهم يرون الشهادة بالزور لموافقيهم.
وأما ما يتوهم: أنه فسق لكونه خللًا في العمل، نحو من شرب النبيذ، ولعب بالشطرنج، من مجتهد يراهما حلالًا أو مقلد له فيه، فالقطع أنه ليس بفاسق ولا مبتدع.','4','321',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1238,'الثالث من الأوصاف المشروطة في المخبر وهي العدالة:
وهي لغة: التوسط في الأمر بين الإفراط إلى طرفي الزيادة والنقصان.
واصطلاحًا: هي ملكة في النفس، أي: هيئة نفسانية راسخة فيها، تمنعها من اقتراف الكبائر والرذائل المباحة أي الجائزة.
كالبول في الطريق، الذي هو مكروه، والأكل في السوق لغير','4','322',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1239,'سوقي.
واجتناب الكبائر عبارة عن التقوى، واجتناب الرذائل المباحة عبارة عن المروءة.
وضابطها: أن يسير بسيرة أمثاله في زمانه ومكانه.
واعلم أن المروءة ليست جزءًا من حقيقة العدالة.
وإن اشترط في قبول الشهادة والرواية.
فاسم العدالة صادق بدونه، وإن كان ظاهر كلام المصنف بخلافه.
وأجيب: بأن الماوردي قسم المروءة المشترطة في قبول الشهادة، إلى','4','323',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1240,'ثلاثة أقسام:
وجعل منهما قسمًا شرطًا في العدالة: وهو مجانبة ما يسخف من الكلام المؤدي إلى الضحك، وترك ما قبح من الفعل.
قال: فمجانبة ذلك من المروءة المشترطة، وارتكابها مفسق.
وفي الجواب نظر.
وزاد بعضهم في حد العدالة: واجتناب الصغيرة الدالة على الخسة كسرقة لقمة، وتطفيف حبة عمدًا.
وهذا يؤخذ من تعريف المصنف؛ لأن الألف واللام في «الكبائر والرذائل» للجنس لا للاستغراق؛ لأن تعاطي الكبيرة الواحدة، والرذيلة','4','324',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1241,'الواحدة قادحة.
فإذا كانت الرذيلة الواحدة المباحة قادحة، فبالأولى الصغيرة الخسيسة لأنها محرمة.
أما صغيرة غير الخسة، ككذبة لا يتعلق بها ضرر.
ونظره إلى أجنبية، فلا يشترط المنع عن اقتراف كل فرد منها، فإن اقتراف الفرد منها لا ينفي العدالة.
والإصرار على الصغيرة مسقط للعدالة، ولم يذكره المصنف؛ لأن','4','325',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1242,'الإصرار يصير الصغيرة كبيرة.
والمراد بالكبائر غير الكبائر الاعتقادية التي هي البدع فتلك تقدم ما فيها.
وزاد بعضهم في الحد: أن تمنعه الملكة، هوى النفس.','4','326',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1243,'وقال: لا بد (منه فإن المتقي للكبائر، وصغائر الخسة، مع الرذائل المباحة، قد يتبع هواه، عند وجوده لشيء منها، فيرتكبه، ولا عدالة لمن هو بهذه الصفة.
وهذا صحيح في نفسه غير محتاج إليه، مع ما ذكره المصنف؛ لأن من عنده ملكة تمنعه عن اقتراف ما ذكر، ينتفي عنه اتباع الهوى لشيء منه، وإلا لو وقع في المهوى فلا يكون عنده ملكة تمنع عنه.
والكبيرة:','4','327',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1244,'قيل: ما توعد عليه بخصوصه، من الكتاب أو السنة.
وقيل: هي ما فيه حد، واختاره في الحاوي الصغير.
قال الرافعي: وهم إلى ترجيح هذا أميل، والأول ما يوجد لأكثرهم وهو الأوفق لما ذكروه عند تفصيل الكبائر.
وقيل: ما نص الكتاب على تحريمه، أو وجب في جنسه حد، أو ترك فريضة تجب على الفور، أو كذب في الشهادة والروية واليمين، وحكاه: الرافعي عن أبي سعيد الهروي.
وقيل: إنها كل ذنب، (ولا صغيرة في الذنوب) واختاره جماعة منهم السبكي.
وقالوا ذلك: نظرًا إلى عظمة من عصى ربه عز وجل.','4','328',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1245,'وهنا أقوال أخر، وتمييز الكبائر من الصغائر وفوائد حسنة من الشرح.','4','329',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1246,'ثم فرع على اشتراط العدالة عدم قبول الشهادة في مسائل بقوله: «فلا تقبل رواية من أقدم على الفسق عالمًا» بكونه فاسقًا للإجماع.
ولقوله تعالى: {إن جاءكم فاسق بنبأ فتبينوا}.
وإن جهل أن ما أتى به فسق قبل، وبه قال الإمام الرازي وأتباعه ونص عليه الشافعي رضي الله عنه.','4','332',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1247,'وقال القاضي أبو بكر الباقلاني: لا يقبل؛ لأنه ضم جهل إلى فسق.
فإذا كان الفسق مع العلم مانعًا، فمع الجهل أولى، واختاره الآمدي.
قلنا: الفرق بينهما: عدم لجرأة في الفاسق الجاهل والجرأة، وقلة المبالاة في الفاسق العالم، فلا تزول غلبة ظن صدق الجاهل بخلاف العالم.
واكتفى المصنف عن الاستدلال هنا اكتفاء بما تقدم في الكافر الموافق، وهو رجحان الصدق.','4','333',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1248,'وأيضًا بأن في جواب شبهة القاضي إيماء إلى الدليل، فإن قلت: اشتراط العدالة مع قبول الفاسق متنافيان.
قلت: تقدم أن اجتناب الكبائر المشترطة في العدالة هو غير الاعتقادية ولذا يقبلون رواية المبتدع مع الاتفاق على اشتراط العدالة.
ومن لا تعرف عدالته، ولا فسقه- وحذفه لتقدم ذكره- لا تقبل روايته؛ لأن الفسق مانع من القبول إجماعًا، فلا بد من تحقق عدمه، أي تحقق ظن عدمه، كالصبا والكفر، والجامع احتمال المفسدة.','4','334',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1249,'تنبيه: شمل كلام المصنف من جهلت حالة باطنًا، لا ظاهرًا، وهو المستور والمشهور رد روايته، كما جزم به المصنف.
وقبله أبو حنيفة- رضي الله عنه- وابن فورك وسليم الرازي.
قال ابن الصلاح: يشبه أن يكون العمل على هذا الرأي في كثير من','4','335',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1250,'كتب الحديث المشهورة في غير واحد من الرواة الذين تقادم العهد بهم، وتعذرت الخبرة الباطنة لهم.
وتوقف فيه إمام الحرمين.
وشمل أيضًا مجهول الحال ظاهرًا وباطنًا وهو مردود أيضًا كما قال المصنف.','4','336',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1251,'وشمل أيضًا: مجهول العين وهو: من لم يؤو عنه إلا واحد وهو مردود، إذا لم ينضم إلى ذلك توثيق إمام له، فإن وثق مع رواية واحد عنه اكتفى بذلك.
قاله أبو الحسن القطان.
والعدالة تعرف بالتزكية، إذ العدالة أمر خفي في النفس، لا','4','337',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1252,'اطلاع عليها، إلا بالتزكية، أو الاختبار.
واقتصر على الأول، لأنه المقصود الآن.
وفيها أي التزكية مسائل:
الأولى
شرط العدد في الرواية والشهادة يعني لا يقبل في تزكية الشاهد والراوي إلا اثنان حكاه القاضي عن أكثر الفقهاء.
ومنع القاضي أبو بكر فيهما، واكتفى بواحد.','4','338',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1253,'والحق: الفرق بين الرواية والشهادة: فيكتفي في الرواية بواحد.
ويشترط في الشهادة اثنان كالأصل، فإنه اكتفي في أصل الرواية بواحد، وفي الشهادة باثنين.
فيكون تعديل كل واحد كأصلهن فإن فرع الشيء لا يزيد عليه وهنا فوائد حسنة يتعين الوقوف عليها من الأصل.
الثانية
في أنه هل يجب ذكر السبب في الجرح والتعديل أم لا؟','4','339',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1254,'قال الشافعي- رضي الله عنه- يذكر سبب الجرح، أي: يجب على الجارح أن يذكر سبب الجرح، إذ قد يجرح بما ليس جارحًا لاختلاف المذاهب فيه.
ولأن الجرح يحصل بخصلة واحدة فيسهل ذكرها، بخلاف التعديل.
فإنه لا يجب على المعدل ذكر سبب التعديل.
والتحقيق أن العدالة بمنزلة وجود مجموع يفتقر إلى اجتماع أجزاء، وشرائط يتعذر ضبطها أو يتعسر.
والجرح بمنزلة عدم له ويكفي فيه انتفاء شيء من الأجزاء والشروط فيسهل ذكرها كما تقدم.
وقيل: يذكر سبب التعديل، دون سبب الجرح، لكثرة التصنع في أسباب العدالة.','4','340',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1255,'ونقله إمام الحرمين والغزالي في المنخول، عن القاضي أبي بكر، والموجود في مختصر التقريب له القول الأول.
ورواه الخطيب البغدادي في الكفاية: بسنده إليه.
ونقله الغزالي في المستصفى عنه.
وقيل: يجب أن يذكر سببهما.
وقال القاضي أبو بكر: لا يجب ذكر السبب فيهما، بل يكفي','4','341',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1256,'الإطلاق.
واختاره الآمدي مع نقله له عن القاضي. وكذا نقله الإمام عن القاضي، وقد عرفت ما فيه. ويحتمل أن يكون له أقوال.
وقال الإمام الرازي تبعًا لإمام الحرمين: يكفي إطلاق الجرح والتعديل من العالم بأسبابهما دون غيره.
وقال في جمع الجوامع ما حاصله: أن هذا ليس مذهبًا آخر؛ لأن الجرح والتعديل إنما يعتبران من العالم بأسبابهما فالجاهل بذلك لا يعتد بقوله.
الثالثة
إذا تعارض الجرح والتعديل فإن الجرح مقدم على التعديل.','4','342',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1257,'سواء كان عدد المجرح أكثر، أو أقل، أو تساويا؛ لأن فيه أي في الجرح زيادة لم يطلع عليها المعدول ولا نفاها.
إيضاحه: أن في تقديم الجرح جمعًا للجرح والتعديل، فإن غاية قول المعدل: أنه لم يعلم فسقًا، ولم يظنه، فظن عدالته، إذ العلم بالعدم لا يتصور، والجارح يقول: أنا علمت فسقه.
فلو حكمنا بعدم فسقه كان الجارح كاذبًا.
وإذا حكمنا بفسقه كانا صادقين فيما أخبرا به، والجمع أولى، ما أمكن؛ لأن تكذيب العدل خلاف الظاهر.
واعلم أنا إنما نقدم الجرح إذا أطلقا.','4','343',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1258,'وأما إذا عين الجارح السبب ونفاه المعدل بطريق يقيني، مثل أن يقول الجارح: هو قتل فلانًا يوم كذا، وقال المعدل: أنا رأيته بعد ذاك اليوم، فيقع بينهما التعارض، لعدم إمكان الترجيح المذكور.
وحينئذ يصار إلى الترجيح.
وكذا إذا عين الجارح سببًا، فقال المعدل: تاب عنه وحسنت توبته، فيقدم التعديل، لأن معه هنا زيادة علم.
ويخرج هاتين المسألتين من تعليل المصنف.
الرابعة
في بيان الطرق التي يحصل بها التزكية
التزكية تحصل بأحد أمور:
منها أن: يحكم الحاكم- المشترط للعدالة في قبول الشهادة-','4','344',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1259,'بشهادته، وهذا القيد لم يذكره المصنف، وذكره الآمدي وغيره، ولا بد منه.
أو يثنى عليه عارف بأسباب العدالة، أي العدالة بأن تقول: هو عدل، أو مقبول الشهادة أو الرواية.
فلو أثنى عليه بغيرها، كالعلم والعقل ونحوهما، لم يكن تزكية أو يروى عنه من لا يروي عن غير العدل، فإن ذلك تعديل للمروي عنه، بخلاف من يروى عن كل من سمع، ولو كلف الثناء سكت، فإن تلك الرواية ليست بتعديل.','4','345',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1260,'ويعرف كونه لا يروى عن غير العدل: إما بتصريحه بذلك وإما بالاستقراء من حاله، كشعبة ومالك ويحيى القطان.
وأما روايته له في كتاب التزم فيه أنه لا يروي فيه إلا للعدل، وذلك كصحيح البخاري، ومسلم.
ويلحق بهما المستخرجات عليهما، وصحيح ابن خزيمة وابن حبان.','4','346',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1261,'أو يعمل عالم، يرى العدالة شرطًا في قبول الرواية بخبره فإن عمل المعدل بخبره، تعديل للمروي عنه.
إذ لو لم يكن عدلًا لم يكن العامل بخبره عدلًا بل فاسقًا.
والكلام في العامل العدل، وأسقط المصنف اشتراطه في التزكية لوضوحه.
فإن أمكن حمله على الاحتياط، أو على العمل بدليل آخر وافق الخبر فليس بتعديل.
وأفهم كلام المصنف: أن ترك العمل بشهادته أو بروايته ليس جرحًا له، وهو كذلك لجواز أن تدلا ويقبلا، ولا يرتب عليهما أثرهما','4','347',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1262,'لمعارض، كرواية أو شهادة أخرى، أو فقد شرط آخر غير العدالة، وهنا نفائس في الشرح.
الوصف الرابع المعتبر في الراوى: الضبط.
وهو أن يكون قوي الحفظ قليل السهو.
والمراد بقوة الحفظ: أن لا يزول ما سمعه عن خاطره بسرعة.
فمن اختل حفظه مطلقًا، أو بالنسبة إلى الأحاديث الطوال لا تقبل روايته، وإن كان عدلًا؛ لأنه لا يؤمن الزيادة والنقصان في حديثه.','4','348',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1263,'وعدم مساهلته في الحديث: بأن كان يروي وهو غير واثق بما يروي، فيزيد وينقص فيه، فلا نأمن الغلط في خبره.
وأما كونه محتاطًا في حديث النبي- صلى الله عليه وسلم- ومتساهلًا في حديث غيره، فلا يضره، وتقبل روايته.
وقيل: ترد روايته مطلقًا، ونص عليه الإمام أحمد- رضي الله عنه- والفرق بين عدم الحفظ والسهو: أن من لا يحفظ لا يحصل الحديث حال سماعه، ومن يعرض له السهو يحصل الحديث حال سماعه ويزول عن خاطره بسرعة أو بغير سرعة.
وشرط أبو علي الجبائي: العدد، فلم يقبل في خبر الزنا إلا أربعة، وفي خبر غيره إلا اثنين كما تقدم.','4','349',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1264,'ورد قول أبي علي: بقبول الصحابة خبر الواحد، بدليل ما نقل عنهم من الاستدلال بخبر الواحد، وعملهم به في الوقائع المختلفة التي لا تكاد تحصى، وتكون ذلك مرة بعد أخرى وشاع وذاع بينهم، ولم ينكر عليهم أحد إلا لنقل، وذلك يوجب العلم العادي باتفاقهم كالقول الصريح، وإن كان احتمال غيره قائمًا في واحد واحد.
قال أبو علي في بيان اشتراط العدد: الصحابة طلبوا العدد في وقائع كثيرة.
فمن ذلك أن أبا بكر لم يعمل بخبر المغيرة بن شعبة: في توريث الجدة إلى أن أخبره بذلك محمد بن مسلمة (رضي الله عنهم','4','350',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1265,'أجمعين) رواه الأربعة، ومالك في الموطأ وقال الترمذى: حسن صحيح.
ومنها: إنكار عمر خبر أبي موسى في الاستئذان حتى رواه','4','351',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1266,'أبو سعيد (رضي الله عنهم). رواه البخاري.
وطلب العدد منهم في الروايات الكثيرة دليل اشتراطه.
قلنا: إنما طلبوا العدد عند التهمة، وقصوره عن إفادة الظن لا مطلقًا، ونحن إنما ندعي أن خبر الواحد العدل مقبول حيث لا تهمة في روايته فلا يرد ما ذكر من الصور نقضًا؛ لأن ذلك مما لا نزاع فيه. وبهذا يجمع بين قبولهم تارة وردهم أخرى.
الخامسة
شرط أبو حنيفة- رضي الله عنه- فقه الراوي إن خالف خبره','4','352',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1267,'القياس، لأن العمل بخبر الواحد على خلاف الدليل الدال على عدم اعتبار رواية الواحد خالفناه فيما إذا كان الراوي فقيهًا لحصول الوثوق بقوله، فبقي ما عداه على أصله من أنه لا يعمل به.
ورد بأن العدالة في الراوي تغلب ظن الصدق، فإن العدل لا يروي إلا ما يعلم أنه كما يروى فتكفي العدالة ولا حاجة إلى الفقه.
ولم يحك الشيخ أبو إسحاق الشيرازي عن الحنفية اشتراط فقه الراوي إلا فيما خالف قياس الأصول، لا مطلق القياس.','4','353',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1268,'ولم يحكه صاحب البديع منهم إلا عن فخر الإسلام منهم خاصة لكن على تفصيل آخر مبين في الأصل مع فوائد حسنة.
وعلم من كلام المصنف: أنه لا يتوقف الأخذ بالحديث على انتفاء القرابة المقتضية لرد الشهادة،','4','354',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1269,'ولا على معرفة نسب الراوي، أو كونه عربيًا أو ذكرًا أو بصيرًا وهو كذلك.
أما إذا أكثر الروايات مع قلة مخالطته لأهل الحديث، فإن أمكن','4','355',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1270,'تحصيل ذلك القدر في ذلك الزمان قبلت وإلا فلا.
وأما الشرط الثاني: وهو المخبر عنه، أي مدلول الخبر فأن لا يخالفه قاطع لا يقبل التأويل، سواء كان القاطع عقليًا، أو نقليًا، كتابًا أو سنة متواترة، أو قياسًا قطعي المقدمات من ثبوت الحكم في الأصل.
وكون الوصف المعين علته ووجوده في الفرع.
فإن عارض الخبر دليل قطعي.
فإن قبل التأويل: بأن يكون نصًا يمكن تخصيصه بالخبر، أو قياسًا يمكن تخصيص الخبر به، أو عكسه جمعنا بينهما بحسب الإمكان، جمعًا بين الدليلين، وإلا رددناه؛ لأن المعارض قطعي، وخبر الواحد ظني،','4','356',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1271,'والقطعي راجح على الظني قطعًا.
وقوله: "لا يقبل التأويل" يحتمل أن يكون صفة لقاطع، وهو الظاهر بحسب اللفظ.
ويحتمل أن يكون متعلقًا بالخبر بأن يكون حالًا عن مفعول يخالفه.
ويحتمل: أنه يتعلق بهما جميعًا بحسب المعنى.
قال العبري: وهو الحق، فيكون حالًا عن الفاعل والمفعول، أ: حال كون القاطع والخبر لا يقبل التأويل لا يعمل بالخبر، وإن قبل أحدهما أول القابل له، ولا يضره، أي: ولا يضر الخبر مخالفة القياس له، ما لم يكون قطعي المقدمات سواء كان جميعها ظنية، أو بعضها ظنية والأخرى قطعية، فلا يضر الخبر مخالفة القياس','4','357',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1272,'ومعارضته، فلا يقدح في قبول الخبر الواحد، بل يقدم الخبر عليه على الأصح.
ونص عليه الشافعي- رضي الله تعالى عنه- لقلة مقدماته بالنسبة إلى مقدمات القياس؛ لأن الخبر مجتهد فيه (في أمرين: عدالة الراوي، ودلالة الخبر.
والقياس مجتهد فيه). في أمور ستة: حكم الأصل، وتعليله في الجملة، وتعيين الوصف الذي به التعليل، ووجود ذلك الوصف في الفرع، ونفي المعارض في الأصل، ونفيه في الفرع، هذا إذا لم يكن الأصل خبرًا، فإن كان خبرًا، وجب الاجتهاد في الستة مع الأمرين المذكورين، وهما العدالة والدلالة.
وظاهر أن ما يجتهد فيه في مواضع أكثر فاحتمال الخطأ فيه أكثر','4','358',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1273,'والظن الحاصل به أضعف، فما كان أقل كان أقل خطأ فهو أرجح فقدم.
أما إذا أمكن الجمع بينهما فيجمع كما مر.
ولا يضر الخبر مخالفة عمل الأكثر لأنهم ليسوا بحجة، لكونهم بعض الأمة، ولا يضر الخبر مخالفة الراوي بأن يكون عمله بخلاف مقتضى الخبر؛ لأن فعل الراوي ليس بحجة؛ لأنه ربما تمسك بما ظنه دليلاً ولا يراه.
وشمل كلام المصنف: ما إذا حمل الراوي مرويه على غير ظاهره.','4','359',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1274,'وما لو كان نصًا وعمل بخلافه (وهو كذلك) فيهما- أعني أنه لا يعمل إلا بما روى لا بما رأى.
أما إذا روى الصحابي حديثًا فيه لفظ مشترك، وحمله على أحد معنييه، فإن كانا متنافيين كالقرء، فالظاهر إتباعه فيه.
وإن لم يتنافا المحملان، فهو كسائر المشتركات يحمل على جميع معانيه عند من يراه، ولا يختص بما حمله عليه الصحابي، إلا أن يجمعوا على أن المراد أحدهما فيحمل عليه.','4','360',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1275,'وإن قلنا: لا يحمل على جميعها.
ففي البديع أن المعروف حمله على (ما عينه)؛ لأن الظاهر أنه لم يحمله عليه إلا بقرينة.
قال: ولا يبعد أن يقال: لا يكون تأويله حجة على غيره.
ونقل عن الصفي الهندي أن الخلاف فيما إذا قال ذلك بغير طريق التفسير للفظه، وإلا فتفسيره أولى بلا خلاف.
وقول المصنف: الراوي يشمل الصحابي والتابعي، وهو كذلك عند بعضهم.
ولا بد من التقييد بكونه من الأئمة.
وفرض الجمهور المسألة في الصحابي فقط.
وقد يفرق بأن ظهور القرينة للصحابي أقرب.','4','361',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1276,'فائدة:
الصحابة (رضي الله تعالى عنهم) كلهم عدول.
وحد الصحابي تقدم في شرح الخطبة.
والتابعي: من لقي الصحابي، كما لقي الصحابي النبي- صلى الله عليه وسلم- وفهم من كلام المصنف: أنه لا يجب عرض خبر الواحد','4','362',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1277,'على كتاب الله- تعالى- وبه قال الشافعي- رضي الله تعالى عنه- خلافًا لعيسى بن أبان.
وأما الشرط الثالث الذي في الخبر: وهو صيغة الرواية. ففيه، أي في هذا القسم مسائل خمس:

<span data-type="title" id=toc-194>المسألة الأولى: في كيفية ألفاظ الصحابي في نقل الأخبار عن النبي صلى الله عليه وسلم </span>فنقول:
لألفاظ الصحابي في نقل الأخبار سبع درجات:','4','363',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1278,'الأولى وهي أعلاها: حدثني رسول الله- صلى الله عليه وسلم- ونحوه.
كقوله: شافهني أو أخبرني، أو أنباني، أو سمعته يقول، أو رأيته يفعل كذا.
وإنما كانت أعلا الدرجات لكون هذه الصيغ نصوصًا في عدم الواسطة والاتصال.
الثانية: أن يقول الصحابي: قال الرسول- صلى الله عليه وسلم- وهي دون الأولى، لاحتمال التوسط بينه وبين النبي- صلى الله عليه وسلم.
والصحيح: الاحتاج بها، حملًا على سماعه منه؛ لأن الظاهر أنه لا يجزم بذلك إلا وقد سمعه منه.','4','364',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1279,'الثالثة: أن يقول: أمر رسول الله- صلى الله عليه وسلم- بكذا أو نهى عن كذا.
وهي دون الثانية، لاحتمال التوسط، كما في الثانية، واختصاصها باحتمال اعتقاد ما ليس بأمر أمرًا، لاختلاف الناس في صيغ الأمر.
واحتمال العموم والخصوص؛ لأنه مطلق يحتمل أن يكون أمرًا للكل وللبعض.
وعلى التقديرين محتمل الدوام واللادوام.
والجمهور على قبول "أَمَرَ" لأن الصحابي عدل عارف باللسان، فلا يطلق ذلك إلا بعد التحقق.','4','365',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1280,'وتوقف الإمام في القبول.
وضعف صاحب الحاصل: كونها حجة.
ولا شك أن قول الصحابي "سمعته" (عليه الصلاة والسلام" "أمر أو نهى" أقوى من قوله: "أمر" لاحتمال انتفاء التوسط.
وجعل في جمع الجوامع المرتبة الثالثة والرابعة: أن يبنى الصيغة للمفعول، فيقول أمرنا بكذا، أو نهينا عن كذا، أو أوجب، أو حرم، أو رخص.
وهو حجة عند الشافعي- رضي الله عنه- والأكثرين لأن من طاوع أميرًا والتزم طاعته إذا قاله، أي قال: أمرنا بكذا، فهم منه أمره- أي: أمر ذلك الأمير- فكذا هنا.
ولأن غرضه- أي غرض الصحابي- في قوله: أمرنا، بيان','4','366',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1281,'الشرع.
وتعليمنا إياه، فيحمل على من يصدر عنه الشرع، ويتعين أن يكون أمرًا لرسول الله- صلى الله عليه وسلم- دون الأئمة والولاة، لأن لأمره- تعالى- ظاهر، لا يتوقف على إخبار الصحابي، ولا يكون عن الإجماع؛ لأن الصحابي من الأمة، فلا يأمر نفسه.
وهذه دون ما قبلها لمساواتها لها في الاحتمالات مع اختصاصها باحتمال: أن لا يكون ذلك الأمر صادراً عن رسول الله- صلى الله عليه وسلم.
ويكون الآمر والناهي بعض الولاة، والإيجاب والحريم والترخيص استنباطًا من قائله.
ولأجل هذا الاحتمال المرجوح نقل عن الكرخي، والصيرفي','4','367',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1282,'أنها مترددة بين أمره خاصة، وأمر كل الأمة وأمر بعض الولاة.
الخامسة: أن يقول الصحابي: "من السنة كذا"
فالأكثر على أن ذلك مرفوع، وهو أصح قولي الشافعي رضي الله تعالى عنه؛ لأنه المتبادر عند الإطلاق.
وقد نقل سالم التابعي أحد الفقهاء السبعة من أهل المدينة عن الصحابة أنهم إذا أطلقوا السنة لا يريدون بذلك إلا سنة النبي صلى الله عليه وسلم.
وكانت هذه دون ما قبلها، إذ فيها الاحتمالات المذكورة مع احتمال آخر','4','368',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1283,'وهو: أن يكون المراد به سنة- لغير رسول الله- صلى الله عليه (وسلم)؛ لأن السنة لغة: الطريقة كما مر.
السادسة: أن يقول: عن النبي- صلى الله عليه وسلم- وهي محمولة على السماع، عند المصنف.
وصححه ابن الصلاح، والصفي الهندي وغيره لظهوره في السماع عنه أيضًا.
وإن كانت دون ما قبلها لكثرة استعمالها في التوسط.
وقيل: للتوسط، فلا يحمل على السماع لما تقدم. قلت: وإن كانت للتوسط فيقبل، والله أعلم.
السابعة: قول الصحابي: كنا نفعل في عهده- صلى الله عليه وسلم- فيقبل ذلك على الصحيح؛ لأن الظاهر إطلاعه- صلى الله','4','369',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1284,'عليه وسلم- على ذلك لتوفر دواعيهم على سؤاله عن أمور دينهم، ولأن ذلك الزمان زمان نزول الوحي، فلا يقع من الصحابة فعل شيء ويستمرون عليه إلا وهو غير ممنوع الفعل.
وقد استدل جابر وأبو سعيد- رضي الله عنهما- على جواز العزل بأنه كانوا يفعلونه والقرآن ينزل، ولو كان مما ينهى عنه لنهى عنه القرآن.','4','370',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1285,'وهذه دون ما قبلها للاحتمالات السابقة، وتختص بأنها ليست دالة على إضافة الحكم إلى رسول الله- صلى الله عليه وسلم- وكلام المصنف يقتضي أن الاحتجاج بها يتوقف عن تقييده بعهد الرسول- صلى الله عليه وسلم- وهو الذي جزم به ابن الصلاح.
وقال بعض المتأخرين: فإنه لا خلاف فيما إذا قال: "كنا نفعل" ولم يضفه لعهد النبي- صلى الله عليه وسلم- في أنه موقوف.','4','371',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1286,'وفيه نظر: فإن مقتضى كلام الإمام الرازي والآمدي الرفع في هذه أيضًا. وبه صرح أبو عبد الله الحاكم، وحكاه النووي عن كثير من الفقهاء، وقال: إنه قوي من حيث المعنى. وهنا فوائد نفيسة في الشرح.
الثانية: في كيفية رواية غير الصحابي عن الشيخ الراوي.','4','372',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1287,'يجوز لغير الصحابي، أن يروي الحديث عن الشيخ إذا سمع ذلك الحديث من لفظ الشيخ، وسواء كان ذلك إملاء منه والسامع يكتبه حالة الإملاء، أو تحديثًا مجردًا عن الإملاء، (وسواء كان ذلك) من حفظ الشيخ أو كتابه، وسواء سمع وحده أو في جمع.
ثم إن قصد الشيخ إسماعه، فله أن يقول: حدثني وأخبرني، وحدثنا وأخبرنا إن كان في جمع.
وإن لم يقصد الشيخ إسماعه، فلا يقول: حدثني وأخبرني، بل يقول: حدث أو أخبر أو سمعته يقول أو يحدث عن كذا؛ لأن الشيخ لم يخبره ولم يحدثه، وهذه الدرجة أعني سماع الشيخ أعلى الطرق.
الثانية: قراءته على الشيخ، وهو ساكت يسمع.
وإليه أشار بقوله: أو قرأ، أي: غير الصحابي عليه أي: على الشيخ، ويقول القارئ للشيخ بعد القراءة أو قبلها: هل سمعت هذا الحديث؟ فيقول الشيخ: نعم.
أو الأمر كما قرئ علي، وحينئذ للراوي أن يقول هنا أيضًا: حدثني، أو أخبرني، أو سمعته، كما قال في المحصول.','4','373',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1288,'وقد عرض ضمام بن ثعلبة (رضي الله عنه) على النبي صلى الله عليه وسلم- شعائر الإسلام التي سمعها من رسوله، وهو يصدقه على ذلك.
وكان الناس يذكرون للصحابة- رضي الله عنهم- الأحكام فيقرون الحق وينكرون الباطل.
وشرط إمام الحرمين: في صحة التحمل أن يكون بحيث لو فرض','4','374',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1289,'من القارئ لحن أو تصحيف لرده الشيخ ويسمى هذا "عرضًا"؛ لأن القارئ يعرض على الشيخ ما يقرؤه.
وتقديم المصنف السماع من لفظ الشيخ على القراءة عليه هو الصحيح.
الثالثة:
القراءة على الشيخ وهو ساكت، وإليه أشار بقوله: "أو سكت" وظن إجابته عند المحدثين.
فإذا قرأ على الشيخ وهو ساكت، ويقول القارئ للشيخ هل سمعت؟ فيشير برأسه، أو كأصبعه، فتكون الإشارة هنا بمنزلة التصريح في وجوب العمل به دون صيغة الرواية، فلا يجوز له أن يقول: حدثني أو أخبرني، أو سمعته.','4','375',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1290,'لأنه ما سمع شيئًا ولم يخبره، بل يقول: حدثني قراءة عليه، فإن لم يشر بشيء فههنا إن غلب على الظن في عرف المحدثين أنه ما سكت إلا لأن الأمر كما قرئ عليه وذلك لقرينة، وإلا لأنكره.
فللسامع مع العمل به جواز روايته عند عامة المحدثين والفقهاء، وصححه ابن الحاجب، إلا أنه يقال في الرواية: أخبرني قراءة عليه.
وصحح الغزالي: والآمدي تبعًا للمتكلمين المنع.
الرابعة:','4','376',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1291,'الكتابة، وإليه أشار بقوله: أو كتب الشيخ: حدثنا فلان، ويذكر الحديث، فللمكتوب إليه أن يعمل بكتابه إن علم أو ظن أنه خط شيخه.
لكن نقول عند الرواية: أخبرني، إذ الإخبار قد يكون بالكتابة، وهذه الطريقة لا بد معها أن يقول الشيخ ارْوِ عني، وهذه دون ما قبلها.
إذ القرينة الحالية، أقوى من الكتابة.
وجعل الإمام هذه في الطريقة الثالثة.
الخامسة:
طريق المناولة: وهي أن يشير الشيخ إلى كتاب صححه الشيخ فيقول: سمعت (ما في) هذا الكتاب من فلان، أو هذا مسموعي','4','377',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1292,'منه قرأته عليه.
وإليه أشار بقوله: "أو قال: سمعت ما في هذا الكتاب" فيجوز للسامع أن يرويه عنه، سواء ناوله الكتاب أم لا، وسواء قال له: ارْوِ عني أو لا، فالشيخ يصير بذلك محدثًا بما فيه، وللسامع أن يروي عنه ما في هذا الكتاب، سواء قال له ارْوِ عني، أو لم يقل، ويجوز أن يقول: حدثني مناولة، أو: ناولني.
وكذا إذا أطلق في الأصح.
فأما إذا قال له: حدث عني ما في هذا الكتاب، ولم يقل له: قد سمعته.
فإن الشيخ لا يصير بذلك محدثًا، وليس للسامع أن يحدث عنه، هكذا في المحصول.
قال العبري: وفيه نظر، فإنه إجازة مخصوصة، اللهم إلا أن يقول حدث ما في هذا الكتاب ولا يقول: عني.
فحينئذ يصح هذا الكلام، إذ الصادر من الشيخ جواز التحديث لا غير.','4','378',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1293,'وإذا سمع الشيخ نسخة من كتاب مشهور فليس له أن يشير إلى نسخة أخرى من ذلك الكتاب إلا إذا علم تطابقهما.
تنبيه:
ما جزم به المصنف في هذه المسألة هو الذي في الحصول.
وهو قول جماعة من المحدثين: كابن جريج وعبيد الله بن عمر، وقطع به ابن الصباغ.','4','379',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1294,'وعزاه القاضي عياض للأكثرين.
لكن قال الغزالي: ليس له رواية عنه بمجرد ذلك، وعليه كثير من المحدثين، وهو مقتضى كلام الآمدي، واختاره ابن الصلاح.
السادسة:
الإجازة من الشيخ، وإليه أشار بقوله: "أو يجيز له" أي: الشيخ.
والإجازة أقسام:
أحدها: أن يكون المجاز له معينًا، والمجاز به معينًا، كأجزت لك أو لفلان- ويرفع في نسبه بما يميزه عن غيره- أن يروي الكتاب الفلاني.
ويليه: أن يجيز لخاص في عام، كأجزت لك أن تروي عني جميع مسموعاتي.','4','380',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1295,'ويليه: الإجازة لعام في خاص، كأجزت لجميع المسلمين رواية صحيح البخاري عني.
ويليه الإجازة للمعدوم تبعًا للموجود نحو: أجزت لفلان ومن يوجد بعد ذلك من نسله.
وقد فعل ذلك أبو بكر بن أبي داود.
والجمهور على جواز الرواية بالإجازة والعمل بمقتضاها.','4','381',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1296,'والمنع رواه الربيع عن الشافعي، وحكاه القاضي عبد الوهاب عن مالك وحكي عن أبي حنيفة (رضي الله عنهم). وقال ابن حزم: إنها بدعة غير جائزة، وإيضاحه في الشرح مع زيادات حسنة.
انتهى بحمد الله تعالى الجزء الرابع، ويتلوه إن شاء الله تعالى الجزء الخامس، وأوله:
الثالثة','4','382',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1297,'الثالثة
لا تقبل المراسيل، وكذا رده الشافعي (رضي الله عنه) والقاضي أبو بكر الباقلاني، ونقله في جمع الجوامع عن الأكثرين.
خلافًا لأبى حنيفة ومالك وأحمد - (رضي الله عنهم) -وأشهر الروايتين عنه، والآمدي.','5','5',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1298,'وقال عيسى بن أبان: إن كان المرسل -بكسر السين -من أئمة النقل، كسعيد بن المسيب، والشعبي، فهو حجة، أو من غيرهم فلا.
واختاره ابن الحاجب، وصاحب البديع.
والمرسل في اصطلاح الأصوليين: قول غير الصحابي تابعيًا كان أو من بعده، قال النبي -صلى الله عليه وسلم -كذا، مسقطًا الواسطة','5','6',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1299,'بينه وبين النبي -صلى الله عليه وسلم.
وفي اصطلاح المحدثين: قول التابعي: قال رسول الله -صلى الله عليه وسلم -فإن كان القول من تابعي التابعين فمنقطع، أو ممن بعدهم فمعضل.
لنا على عدم قبول المرسل: أن عدالة الأصل الذي أسقط لم تعلم؛ لأنه غير معلوم، فلا تكون عدالته معلومة؛ لأن العلم بعدالة الشخص فرع عن العلم به فلا يقبل؛ لأن شرط قبول خبر الواحد: أن تكون عدالة المروي عنه معلومة، فلا يقبل المرسل لفقد شرط القبول فيه.','5','7',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1300,'قيل: علته الرواية عنه تعديل له، لكون الفرع عدلاً، والعدل لا يروي إلا عن عدل، وإلا كان ملبسًا غاشا.
قلنا في الجواب عنه: لا يلزم من الرواية عنه تعديله إذ راوي المرسل قد يروي عن غير العدل، بدليل أنه لو سئل عنه فلا يعرف اسمه، فكيف بعدالته؟ أو يجرحه أو يتوقف أو يعينه، فيعرفه بفسق لم يطلع هو عليه.
قيل أيضًا: إسناده -أي إسناد الراوي -الخبر إلى الرسول -صلى الله عليه وسلم -يقتضي الصدق فيما أسند.
وإلا لم يكن عدلاً، وإذا كان صادقًا فيه، كان الخبر في الظاهر مسندًا إلى رسول الله -صلى الله عليه وسلم -فكان مقبولاً.','5','8',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1301,'قلنا: إسناد العدل الخبر إلى رسول الله -صلى الله عليه وسلم -لا يلزم أن يقتضي صدق الخبر، بل يقتضي السماع عن رسول الله -صلى الله عليه وسلم -مع احتمال الواسطة، وتلك الواسطة لا تعلم حالها كما مر.
قيل أيضًا: الصحابة -رضي الله عنهم -أرسلوا، كما رواه الحاكم في كتاب العلم في مستدركه، قال: وهو صحيح على شرط الشيخين. وليس له علة، وقبلت مرسلاتهم بالإجماع.','5','9',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1302,'قلنا: إنما قبلت لظن السماع من النبي -صلى الله عليه وسلم -لكونه صحابيًا والعمل بالظن واجب.
وحاصل جواب المصنف: أن هذا ليس من المرسل، وأنه لا يقبل إذا تبينا أن الصحابي لم يسمعه، كما أن مرسل غير الصحابي لا يقبل، أيضًا وهو موافق لكلامه أولاً، فإنه أطلق عدم قبول المرسل ولم يفصل بين الصحابي وغيره.
وفي المحصول: إذا بين الصحابي بعد ذلك أنه كان مرسلاً، وسمى الأصل الذي رواه عنه، وجب قبوله أيضًا.
قال: وليس في الحالتين دليل على العمل بالمرسل.
ولك أن تقول: يقبل مرسل الصحابي مطلقًا؛ لأن مستنده إما','5','10',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1303,'سماعه من النبي -صلى الله عليه وسلم -وإما من صحابي آخر وكلهم عدول كما مر، وأما سماعه من التابعي فنادر.','5','11',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1304,'<span data-type="title" id=toc-197>فرعان على عدم قبول الخبر المرسل:</span>
الأول:
المرسل يقبل بأحد أمور تغلب على الظن صدقه: وذلك إذا تأكد بقول الصحابي أو فعله، فإن الظن يقوى عنده، وكأن فتوى الصحابي وفعله، يدل على أن له أصلاً في الشريعة.','5','12',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1305,'وقد احتج بعضهم بالمرسل، وبعضهم بقول الصحابي، فإذا اجتمعا تأكد أحدهما بالآخر.
وفتوى أكثر أهل العلم، أي: وتأكد بفتوى أكثر أهل العلم فيقبل لما سبق.
واقتصر المصنف على هذين.
وكذا إذا كان من مراسيل الصحابة كما مر.
وكذا إذا أسنده غير المرسل، وإن كان ذلك الإسناد ضعيفًا.
وكذا إذا عرف من حال الراوي الذي أرسله أنه لا يرسل إلا عن من يقبل قوله كمراسيل سعيد بن المسيب.','5','13',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1306,'وهذه الستة نص عليها الشافعي -رضي الله عنه -ونقلها عنه الإمام والآمدي ما عدا الأول.
وزاد غيرهما القياس.
وزاد بعضهم أن ينتشر من غير نكير، أو ينضم إليه عمل أهل العصر به.
وفائدة قبول المرسل: إذا تأكد بمسند آخر أو قياس الترجيح عند تعارض الأحاديث، فيرجح ما تقوى بالمرسل.
وربما يكون المسند ضعيفًا فلا يثبت الحكم به وحده ولا بالمرسل وحده.
فإذا تقوى أحدهما بالآخر ثبت الحكم، ولا يلزم من ذلك ضعف المجموع؛ لأنه قد يتحصل من اجتماع الضعيفين قوة مفيدة للظن.
قال العراقي: إذا تأكد المرسل بأحد هذه الأمور الأربعة، يعني','5','14',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1307,'قول الصحابي، أو فتوى أكثر أهل العلم، أو يسنده غير مرسله، أو يرسله من أخذ العلم عن غير شيوخ المرسل الأول، فإنما يقبل بثلاثة شروط:
أحدها: أن يكون مرسله من كبار التابعين، وأما صغار التابعين فلا يقبل مرسلهم وإن تأكد.
ثانيها: أن يعرف من حاله أنه لا يرسل إلا عن ثقة بأن يكون إذا سئل عمن اتهمه لا يسمي إلا ثقة.
ثالثها: أن يكون إذا شارك الحفاظ المتقين، إما أن يوافقهم، أو ينقص لفظه عن لفظهم، هذا هو مذهب الشافعي (رضي الله عنه)، الذي ذكره في الرسالة فاعتمده، فإني لم أر من الأصوليين من حرر هكذا. انتهى.
الفرع الثاني:
إن أرسل الراوي خبره مرة، ثم أسند ذلك الخبر أخرى. أو','5','15',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1308,'وقفه على الصحابي ثم رفعه قبل، وبه جزم الإمام وأتباعه؛ لأن تأكده بإسناده يغلب ظن صدقه.
وقيل: لا؛ لأن إهماله ذكر الرواة يدل على الضعف في روايته، إما لضعف رجاله أو لنوع من التدليس فيه.
وجوابه: أن تركه اسم الراوي قد يكون لنسيان
كذا شرحه الأصفهاني، والعبري
وقال الإسنوي: إن هذه الصورة لا إشكال في قبولها.
وبه جزم الإمام وأتباعه.
وأما إذا كان الراوي من شأنه إرسال الأحاديث، إذا رواها فاتفق إن روى حديثًا مسندًا، ففي قبوله مذهبان في المحصول والحاصل من غير ترجيح.
وهي مسألة الكتاب، فافهم ذلك.','5','16',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1309,'أرجحهما عند المصنف قبوله لوجود شرطه.
وعلى هذا قال الشافعي -رضي الله تعالى عنه -كما في المحصول: لا أقبل شيئًا من حديثه، إلا إذا قال فيه: حدثني، أو سمعت، دون غيرهما من الألفاظ الموهمة.
وفي المحصول: أن الراوي (إذا سمى) الأصل باسم لا يعرف به فهو كالمرسل، ونقل عن إمام الحرمين مثله.
الرابعة
يجوز نقل الخبر بالمعنى، للعارف بمدلولات الألفاظ، ومواقع الكلام بأن يأتي بلفظ (بدل آخر) مساوٍ له في المراد منه وفهمه،','5','17',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1310,'لأن المقصود المعنى، واللفظ آلة له.
وبه قال الجمهور، ومنهم الأئمة الأربعة.
وسواء في الجواز: نسي الراوي اللفظ أم لا.
خلافًا لابن سيرين محمد.','5','18',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1311,'(كما نقله عنه الترمذي والبيهقي).
حيث قال: بعدم جواز نقل الحديث بالمعنى مطلقًا.
واختاره ثعلب وغيره.','5','19',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1312,'ورواه ابن السمعاني عن ابن عمر -رضي الله عنهما، وعزاه صاحب التحصيل للشافعي (رضي الله تعالى) عنه.','5','20',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1313,'قال العراقي: وهو وهم قبيح نبهت عليه لئلا يغتر به.
أما غير العارف فلا يجوز له بغير اللفظ قطعًا.
لنا: على جواز نقل الخبر بالمعنى: أن الترجمة للأخبار بالفارسية جائزة إجماعًا، فبالعربية أولى؛ لأنه أقرب نظمًا وأوفى بمقصود تلك اللغة من لغة أخرى.
وفيه نظر.
فالأحسن أن يستدل بما رواه ابن مندة عن النبي -صلى الله عليه وسلم -أنه جوزه بشرط أن لا يحل حرامًا ولا يحرم حلالاً، وأصاب','5','21',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1314,'المعنى، وهو مبين في الشرح مع فوائد أخرى.
قيل: نقل الخبر بالمعنى يؤدي إلى طمس الحديث، أي: محو معناه واندراسه، فإننا نقطع باختلاف (العلماء، في معاني الألفاظ،','5','22',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1315,'وتفاوتهم، في تنبيه بعضهم على ما لا ينتبه له الآخر.
فإذا قدر) النقل بالمعنى مرتين، أو ثلاثًا، ووقع.
في كل مرة أدنى تغيير حصل بالتكرر تغيير كثير واختل المقصود بالكلية.
قلنا: لما تطابقا -أي: توافق المنقول إليه -والأصل في الجلاء والخفاء وتأدية المعنى من غير تفاوت بينهما لم يكن ذلك -أي: النقل طمسًا للحديث -ويكون ناقل الخبر بالمعنى مؤديًا له كما سمعه. وإن اختلفت الألفاظ.
أما عند تغيرٍ ما، فإنه لا يجوز بالاتفاق.
وليس من محل الخلاف: ما تعبدنا بألفاظه، كالأذان، والتشهد، والتكبير، والتسليم، وجعل الماوردي: محل الخلاف في الصحابي،','5','23',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1316,'وقطع في غيره بالمنع.
وفيه نظر.
ويجوز حذف بعض الخبر، ورواية الباقي، إذا كان مستقلاً في الأصح.','5','24',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1317,'(فائدة:
العمل بالقراءة الشاذة: وهي ما نقل آحادًا.
مثل ما أخرجه الحاكم وقال: صحيح الإسناد عن أبي العالية قال: في قراءة أبي بن كعب: "فصيام ثلاثة أيام متتابعات" جائز.
كما نص عليه الشافعي -رضي الله تعالى عنه -في مختصر البويطي.','5','25',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1318,'في باب الرضاع، وفي باب تحريم الجمع، حيث قال: إنه حجة.
وجزم به الشيخ أبو حامد، والمارودي، والقاضي أبو الطيب، والقاضي حسين، والمحاملي وابن يونس وكذا','5','26',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1319,'الرافعي في باب حد السرقة، وهو قول أبي حنيفة -رضي الله تعالى عنه.
واختار ابن الحاجب عدم الاحتجاج بها.
ونقله الآمدي عن الشافعي (رضي الله تعالى عنه).
وقال في البرهان: إنه ظاهر مذهب الشافعي (رحمه الله تعالى) وجزم النووي في شرح مسلم بما قاله.','5','27',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1320,'وقال الإسنوي: وما قالوه جميعه خلاف مذهب الشافعي (رضي الله تعالى عنه) (وخلاف قول جمهور أصحابه).
والذي وقع للإمام مستنده: عدم إيجابه للتابع في كفارة اليمين بالصوم مع قراءة ابن مسعود، السابقة.
وعدم الإيجاب يجوز أن يكون لعدم ثبوت ذلك عند الشافعي -','5','28',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1321,'رضي الله تعالى عنه -أو لقيام معارض).','5','29',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1322,'<span data-type="title" id=toc-201>المسألة الخامسة
في اختلاف رواة خبر واحد بالزيادة والنقصان</span>.
إذا روى راويان فصاعدًا حديثًا، واتفق عليه، وزاد أحد الرواة فيه زيادة انفرد بها، ولم يروها الآخر، وتعدد المجلس، أي: مجلس سماع ذلك الخبر من الرسول -عليه الصلاة والسلام.
قبلت تلك الزيادة؛ لأن عدالة الراوي تقتضي قبولها.
وسكوت الآخرين عن تلك الزيادة، لا يقدح في رواية تلك الزيادة، لجواز أن يكون الرسول -صلى الله عليه وسلم -ذكر تلك الزيادة في مجلس دون مجلس. بحسب ما يليق بالحال.
وكذا تقبل الزيادة إن اتحد المجلس، وجاز الذهول عن','5','30',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1323,'الآخرين، فيما ضبطه راوي تلك الزيادة، بأن كانوا قليلين، ومنعهم مانع من الضبط، ولم تغير الزيادة إعراب الباقي، كما إذا روى أحدهما: "في أربعين شاة شاة".
وروى آخر: "في أربعين شاة شاة سائمة".
لأن عدالة راوي الزيادة تقتضي قبول خبره، وإمساك الآخر عن روايتها لا تقدح فيه، لاحتمال أن يعرض له حال ذكر الرسول -صلى الله عليه وسلم -تلك الزيادة ما يمنع عن ضبطها، كعطاس، أو سهو، أو اشتغال قلب، أو دخل في أثناء الحديث أو غيره.
ولا شك أن ذهول الإنسان عما سمعه أكثر من توهم سماع ما لم','5','31',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1324,'يسمعه، وإن لم يجز على الآخرين من الرواة الذهول، لكونهم كثيرين، لا يجوز في العادة ذهولهم عما ضبطه الأقل لم تقبل تلك الزيادة.
وحينئذ يحمل أمر راويها، على أنه سمعها من غير النبي -صلى الله عليه وسلم -وظن أنه سمعها منه لعدالته.
وإن عير المزيد الإعراب مثل: "في أربعين شاةٍ شاة" أو نصف شاةٍ يعني يقول البعض: "شاة".
والبعض: "نصف شاةٍ".
فتتعارض الروايتان، وحينئذ طلب الترجيح.
أما إذا لم يعلم تعدد المجلس أو اتحاده، فهو أولى بالقبول.
من اتحاد المجلس لاحتمال التعدد.','5','32',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1325,'ولذا لم يذكره المصنف، هذا كله إذا تعدد الراوي.
وقال العراقي: ما ذكره -يعني المصنف -من هذا التفصيل الذي في المسألة الخامسة هو الذي اختاره الآمدي، وشرط الإمام في القبول مع ما هنا أن لا يكون الممسك عن الزيادة أضبط من الراوي لها، وأن لا يصرح بنفيها.
فإن صرح وقع التعارض.
وشرط ابن السمعاني في عدم قبول الزيادة إذا كان الذين لم يرووها عددًا لا يجوز ذهولهم عنها بأن يقولوا: إنهم لم يسمعوها لجواز اقتصارهم على بعض الحديث لغرض لهم في ذلك، وهذا ظاهر إن لم تكن الزيادة مما توفر الدواعي على نقلها.
وحكي إمام الحرمين عن الشافعي (رضي الله تعالى عنه) وسائر المحققين قبول زيادة الثقة مطلقًا.
وحكاه الخطيب عن جمهور الفقهاء والمحدثين.','5','33',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1326,'وادعى ابن طاهر اتفاق المحدثين عليه.
قال العراقي: وهو المختار المشهور المتصور المعمول به.
أما إذا اتحد، فإن زاد مرة، وحذف الزيادة مرة أخرى، والحال -كم تقدم -من اتحد المجلس، والإعراب كما صرح به في المحصول.
وحينئذ فالاعتبار بكثرة المرات، زيادة أو حذفًا، لأن الأقل أولى بالسهو، إلا أن يصرح الراوي فيقول: سهوت، فيعمل بمقتضاه.
وإن تساويا أخذنا بالزيادة؛ لأن السهو في نسيان ما سمع أكثر من إثبات ما لم يسمع كما مر.
أما إذا أسند الراوي الزيادة وعدمها إلى مجلسين، فإن الزيادة تقبل غيرت إعراب الباقي، أو لم تغيره، صرح به في المحصول.
قال العراقي: والأصح الحكم للزيادة مطلقًا كما تقدم.','5','34',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1327,'<span data-type="title" id=toc-202>الكتاب الثالث
في
الإجماع</span>','5','35',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1328,'الكتاب الثالث
في
الإجماع
وهو من الأدلة الشرعية، وقدمه على القياس لعصمته عن الخطأ.
والإجماع لغة: يقال: للعزم، وهو جزم الإرادة بعد التردد، وللاتفاق وهو في الاصطلاح: اتفاق أهل الحل والعقد من أمة محمد -','5','37',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1329,'صلى الله عليه وسلم -على أمر من الأمور.
والمراد بالاتفاق: التوافق إما في القول، أو في الفعل، أو في الاعتقاد أو ما في معناه من التقرير، والسكوت، عند القائل به.
والمراد بأهل الحل والعقد: المجتهدون في الأحكام الشرعية، الموجودون في عصر واحد.
فالاتفاق: كالجنس يشمل اتفاق هذه الأمة، واتفاق غيرهم.
وقوله: أهل الحل والعقد: أخرج به اتفاق العوام، واتفاق بعض','5','38',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1330,'المجتهدين، فليس بإجماع.
وعلم منه اختصاصه بالعدول، إن كانت العدالة ركنًا في الاجتهاد وعدم الاختصاص بهم، إن لم تكن ركنًا، وهو الأصح.
وأفهم أن مخالفة العوام لا تضر وهو الأصح.
وعلم منه أنه لا يشترط في المجمعين عدد التواتر لصدق أهل الحل','5','39',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1331,'والعقد بما دون ذلك، وهو الأصح.
وعلم منه أنه لو لم يكن إلا مجتهد واحد لم يحتج به، إذ أقل ما يصدق به اتفاق أهل الحل والعقد اثنان، وهو ما اختاره في جمع الجوامع.
وقال أبو إسحاق: إنه -أي -الواحد حجة.
وعزاه الصفي الهندي لأكثرين.
وقال بعضهم: لا خلاف في أنه ليس بإجماع.
وعلم منه أن التابعي المجتهد في وقت الصحابة معتبر معهم، وهو كذلك على الأصح.','5','40',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1332,'وقوله: من أمة محمد -صلى الله عليه وسلم -أخرج به اتفاق الأمم السالفة، فليس بإجماع.
وقوله: على أمر من الأمور يعم الإثبات والنفي، والأحكام الشرعية: كحل البيع، واللغوية: ككون الفاء للتعقيب، والعقلية: كحدث العالم، والدنيوية: كالآراء والحروب، وتدبير الرعية، وهو الأصح.
ولقصد التعميم، أردف المصنف الأمر بالأمور.','5','41',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1333,'فإن الأمر المجموع على الأوامر مختص بالقول، بخلاف المجموع على أمور.
وهذا، وإن كان مجازًا في الحد، لكنه جائز عند فهم المراد كما نص عليه الغزالي في مقدمة المستصفي.
والمراد باجتماع أمة محمد (صلى الله عليه وسلم) في عصر كما سبق وذلك سابق إلى فهم المتشرعة من الحديث مع المحافظة على لفظ الحديث.
والمراد بقولنا: "في عصر" في زمان، قل أو كثر.
ثم إنه قد اختلف في أنه: هل يشترط في الإجماع وانعقاده حجة، انقراض عصر المجمعين؟
فمن اشتراط ذلك: لا يكفي عنده الاتفاق في عصر، بل يجب استمراره ما بقى من المجمعين أحد.
فيزيد في الحد "إلى انقراض العصر".','5','42',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1334,'ومن يشترط عدم سبق الخلاف في الإجماع يزيد "ما لم يسبقه خلاف مجتهد". ولا شك أن هذا الحد الذي ذكره المصنف منطبق على اتفاق أهل الحل والعقد في حياة النبي -صلى الله عليه وسلم -بدونه، (مع أنه لا ينعقد في حياته -صلى الله عليه وسلم -كما نبه عليه المصنف في النسخ.
فيكون المراد هنا، ما لم يكن في حياته صلى الله عليه وسلم) واعلم أن البحث في الإجماع يقع في ثلاثة أمور:','5','43',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1335,'- في حجيته.
- وأنواعه.
- وشرائطه.
فلذا جعل الكلام فيه ثلاثة أبواب لبيان الأمور الثلاثة، وبدأ بالكلام، على كونه حجة؛ لأن الاحتجاج به، متوقف على بيان إمكانه، والإطلاع عليه: فلذا قدم الكلام فيهما، فقال: وفيه ثلاثة أبواب.
الأول في بيان كونه حجة
وفيه مسائل:','5','45',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1336,'الأولى
قيل: إنه محال لأن إجماع الجمع الغفير، والخلق الكثير، على حكم واحد، مع اختلاف قرائحهم، وآرائهم ممتنع عادة كاجتماع الناس في وقت واحد، على مأكول واحد.
وأجيب بالفرق بينهما: بأن الدواعي مختلفة، ثمة. يعني','5','46',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1337,'هناك، أي في المأكول الواحد، لاختلافهم في الشهوة والمزاج والطبع؛ فلذلك يمتنع اجتماعهم عليه.
بخلاف الحكم، فإنه تابع للدليل، فلا يمتنع اجتماعهم عليه؛ لوجود دليل قاطع أو ظاهر.
وقيل: الإجماع ممكن، ولكن يتعذر الوقوف عليه، لانتشارهم، أي المجمعين شرقًا وغربًا، وجواز خفاء واحد منهم، بأن يكون أسيرًا، أو محبوسًا، أو منقطعًا عن الناس.
وجواز خموله بحيث لا يعرف أحد أنه مجتهد فلا يذكر لنزول رتبته، وجواز كذبه، فيفتي على خلاف معتقده، خوفًا من سلطان جائر، أو مجتهد ذي منصب أفتى بخلاف رأيه.
أو جواز رجوعه عن فتواه قبل فتوى الآخر.','5','47',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1338,'ولأجل هذه الاحتمالات قال الإمام أحمد (رضي الله تعالى عنه): "من ادعى الإجماع فهو كاذب".
وأجيب بأنه لا تعذر للوقوف على الإجماع، في أيام الصحابة -رضي الله تعالى عنهم -لاندفاع هذه الاحتمالات إذ ذاك.
فإنهم أي: أهل الحل والعقد من الصحابة (رضي الله تعالى عنهم) كانوا محصورين قليلين، ومن خرج منهم إلى البلاد كان معروفًا في موضعه، فلم تتعذر معرفتهم، ومع ذلك كانوا مشهورين ورعين، وقوة دينهم تمنعهم عن الفتوى على خلاف معتقدهم، وكانوا محتاطين في الفتوى لا يرجعون عن فتواهم قبل فتوى الآخر، ولو وقع منهم رجوع لاشتهر.','5','48',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1339,'ولما كان هذا في غاية الوضوح لم يذكره المصنف واقتصر على ما يخفي.
وفي الشرح بيان عدد الصحابة.
واعلم أن هذا الجواب لا ينفي مذهب أهل الظاهر فإنهم قالوا: يختص الإجماع بالصحابة.
والصحيح أنه لا يختص لصدق مجتهدي الأمة في عصر بغيرهم.','5','49',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1340,'فالأحسن في الجواب: أنه تشكيك في مصادمة الضرورة فإنا نعلم قطعًا من الصحابة والتابعين الإجماع على تقديم الدليل القاطع على المظنون، وما ذلك إلا بثبوته عنهم، وبنقله إلينا ففسد ما استدلوا به.
الثانية
أنه أي: الإجماع حجة يجب العمل به، خلافًا للنظام المعتزلي.
والشيعة -بكسر الشين -وهم كل من قال بتفضيل علي -رضي الله عنه وكرم الله وجهه -بعده إلى يمونا هذا والخوارج، فإنه وإن نقل عنهم','5','50',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1341,'ما يقتضي الموافقة، فعند التحقيق هم مخالفون.
أما النظام: فلأنه لم يفسر الإجماع باتفاق المجتهدين، بل قال: هو كل قول يحتج به.
وأما الشيعة: فإنهم يقولون: الإجماع حجة، لكونه مشتملاً على قول الإمام المعصوم، فقوله هو الحجة فقط، لا الإجماع.
وأما الخوارج: فنقل عنهم: أن إجماع الصحابة حجة قبل حدوث الفرقة، وأما بعدها فالحجة في إجماع طائفتهم لا غير؛ لأن العبرة بقول المؤمنين، ولا مؤمن عندهم، إلا من كان على مذهبهم.
وكلام المصنف: يوهم أن النظام يسلم إمكان الإجماع، ويمنع','5','51',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1342,'حجيته.
ونقل ابن برهان، وابن الحاجب عنه أنه محال.
لنا: على كون الإجماع حجة وجوه:
الأول: أنه -تعالى -جمع بين مشاقة الرسول وإتباع غير سبيل المؤمنين في الوعيد، حيث قال: {ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم} الآية.
فتكون متابعة غير سبيل المؤمنين محرمة؛ إذ لا يجمع بين الحرام والمباح','5','52',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1343,'في الوعيد كالكفر وأكل الخبز.
وإذا حرم إتباع غير سبيلهم، فيجب إتباع سبيلهم، إذ لا مخرج عنهما؛ لأن حرمة إتباع غير سبيلهم، وإن كانت أعم من وجوب إتباع سبيلهم بحسب المفهوم، لكن لا مخرج بحسب الوجود من إتباع غير سبيلهم، وإتباع سبيلهم، إذ ترك إتباع سبيلهم، إتباع سبيل غيرهم.
لأن معنى السبيل هنا: ما يختاره الإنسان لنفسه من قول، أو فعل، أو اعتقاد.
فيجب إتباع المؤمنين الذين هم أهل الإجماع.
فإن هذا معنى إتباع سبيلهم الذي هو الإجماع.','5','53',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1344,'قيل: اعتراضًا على الدليل: أنه -تعالى -رتب الوعيد على الكل، يعني: على المجموع المركب من مشاقة الرسول - (صلى الله عليه وسلم).
ومخالفة سبيل المؤمنين؛ للعطف بالواو التي هي للجمع المطلق.
وحينئذ لا يلزم حرمة مخالفة سبيل المؤمنين، إذ لا يلزم من حرمة المجموع حرمة كل واحد من أجزائه، كجمع الأختين دون كل واحدة.
قلنا: بل الوعيد مرتب على كل واحد من المشاقة ومخالفة سبيلهم، وإلا لغي ذكر المخالفة، أي لو لم يكن الوعيد مرتبًا على كل واحد منهما، للزم أن يكون ذكر مخالفة سبيل المؤمنين لغوًا، لأن','5','54',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1345,'المشتاقة وحدها مستقلة.
في اقتضاء ترتيب الوعيد عليها، واللغو في كلام الله -تعالى- محال.
قيل عليه: سلمنا أن الوعيد مرتب على كل واحد منهما، لكن لا يلزم من ذلك أن تكون مخالفة سبيل المؤمنين حرامًا مطلقًا؛ لأنها معطوفة على المشاقة، والمشاقة مشروطة بتبيين الهدى، فتكون المخالفة أيضًا مشروطة بذلك، إذ الشرط في المعطوف عليه شرط في المعطوف، لأن الأصل: اشتراك المعطوف والمعطوف عليه في المتعلقات، والهدى عام لاقترانه باللام، فتكون متابعة غير سبيل المؤمنين حرامًا، إذا تبين جميع أنواع الهدى، ومن جملة الهدى الدليل الذي أجمعوا عليه، أي سند الإجماع وإذا تبين ذلك استغنى به عن الإجماع، فلا يبقى للتمسك بالإجماع فائدة لثبوت الحكم بذلك الدليل لا بالإجماع فلا يكون الإجماع حجة.','5','55',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1346,'قلنا: لا نسلم أن (كل ما) هو شرط في المعطوف عليه يكون شرطًا في المعطوف، بل العطف إنما يقتضي التشريك في مقتضى العامل، إعرابًا ومدلولًا لا في كل الوجوه.
وإن سلم أنه شرط لم يضر ذلك.
لأن المراد (من الهدى: دليل التوحيد والنبوة) وذلك كان مبينًا في زمنه، صلى الله عليه وسلم.
ولا يصح حمل الهدى على دلائل المسائل الفرعية؛ لأنه حينئذ لم تكن مشاقة الرسول - (صلى الله عليه وسلم) - حرامًا.
لأن جميع دلائل المسائل الفرعية (لم تكن) مبينًا في زمانه -صلى','5','56',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1347,'الله عليه وسلم- فيكون الشرط في حرمة المشاقة التوحيد دون دليل مسائل الفرع، (فتكون حرمة اتباع سبيل غير المؤمنين مشروطة بذلك) وقد تبين ذلك، فيكون الإجماع حجة.
قيل: سلمنا أن إتباع غير سبيل المؤمنين حرام، لكن لفظ «غير»، و «سبيل» مفردان، والمفرد لا عموم له.
فالآية لا توجب تحريم كل ما غاير سبيل المؤمنين، بل بعضه، فلا يلزم كون الإجماع حجة، لجواز أن يكون المراد بذلك البعض هو الكفر، وتكذيب الرسول -صلى الله عليه وسلم- ويدل له، أن الآية نزلت في رجل ارتد فعلم أن المراد منها المنع من الكفر.','5','57',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1348,'قلنا: بل يقتضي تحريم كل ما غاير سبيلهم؛ لأن اسم الجنس وإن كان مفردًا، لكنه إذا أضيف، أفاد العموم لجواز الاستثناء منه.
لأن القائل إذا قال: من دخل غير داري ضربته، فهم منه العموم، بدليل صحة الاستثناء لكل واحد من الدور المغايرة لداره، ويصح أن يقال: إلا السبيل الفلاني، وإذا صح الاستثناء منه يكون عامًا، لأن جواز الاستثناء معيار العموم كما مر.
وأما نزول الآية في رجل ارتد، فلا يمنع العموم، لما عملت أن العبرة بعموم اللفظ لا بخصوص السبب.
وهنا نظر مبين في الأصل.
قيل: سلمنا: أن الآية تقتضي حرمة كل ما يغاير سبيل المؤمنين.','5','58',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1349,'لكن المراد من السبيل: دليل أهل الإجماع، لا حكم الإجماع. لأن السبيل لغة: الطريق الذي يمشي فيه.
ولما تعذرت إرادته هنا تعين الحمل على المجاز.
والمجاز: إما قول أهل الإجماع، أو دليله الذي لأجله أجمعوا، وحمله على الثاني أولى لقوة العلاقة بينه وبين الطريق، إذ كل منهما موصل إلى المقصد.
فالآية دالة على حرمة مخالفة الاستدلال، بدليل الإجماع لا على حرمة مخالفة الإجماع، فلا يكون الإجماع حجة.
قلنا: حينئذ يكون المخالفة المشاقة؛ لأن الدليل الذي','5','59',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1350,'أجمعوا عليه، إما: الكتاب أو السنة أو القياس، فيكون داخلًا في مشاقة الرسول.
أما الكتاب والسنة فواضح، وأما القياس فراجح إلى دليله الذي هو الكتاب أو السنة، فيلزم التكرار والأصل عدمه.
وإذا أريد نفس الإجماع لا يلزم ذلك.
هذا إذا كانت عبارة المتن ما تقدم، وهو كذلك، في كثير من النسخ، وهو جواب الإمام الرازي.
لكن سيأتي هذا في كلام المصنف جوابًا عن سؤال آخر، لكن على تقدير آخر.
فإن كانت عبارة المتن كما هو في بعض النسخ.','5','60',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1351,'قلنا: حمله على الإجماع أولى لعمومه، كان معناه أن السبيل أيضًا: يطلق على الإجماع؛ لأن أهل اللغة يطلقون السبيل على كل ما يختاره الإنسان لنفسه من قول أو فعل.
ومنه قوله تعالى: {قل هذه سبيلي} وإذا كان كذلك فحمله على الإجماع أولى، لعموم فائدته.
فإن الإجماع يعمل به المجتهد والمقلد، ودليل الإجماع لا يعمل به سوى المجتهد.
وهذا جواب صاحب الحاصل، وهو أحسن من الأول.
قيل: لا نسلم أنه يجب إتباع سبيل المؤمنين في كل شيء، بل يجب إتباعهم فيما صاروا به مؤمنين، ويدل عليه أنها نزلت في','5','61',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1352,'مرتد.
ولأنه إذا قيل: لا تتبع غير سبيل الصالحين فهم منه المنع من ترك الأسباب التي صاروا بها صالحين، دون غيرها كالأكل والشرب.
قلنا: حينئذ تكون المخالفة المشاقة؛ لأنه لا معنى لمشاقة الرسول إلا ترك الإيمان.
وسمي بذلك لأنه في شق، أي: في جانب، والرسول في جانب آخر، فلو حمل على هذا لزم التكرار كما مر.
قيل: سلمنا تحريم إتباع غير سبيل المؤمنين، لكن لا نسلم وجوب إتباع سبيلهم، وإنما يلزم لو لم يكن بينهما واسطة (وهنا واسطة) وهي بترك الإتباع رأسًا أي بالكلية، فلا يتبع سبيلهم ولا غير سبيلهم.','5','62',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1353,'قلنا: الترك غير سبيلهم، ومر تحقيقه.
قيل: سلمنا وجوب الإتباع (لكنه لا يجب) في كل الأمور، لأنه لا يجب إتباعهم في فعل المباح إذا أجمعوا عليه وإلا لكان المباح واجبًا.
فالواجب حينئذ إتباع بعض سبيلهم، وإذا لم يجب إتباع سبيلهم في الكل لم يلزم إتباعهم فيما أجمعوا عليه، لجواز أن يكون المراد هو الإيمان أو غيره مما هو متفق عليه، فلا يكون الإجماع حجة.
قلنا: إتباع سبيل المؤمنين، كإتباع الرسول (عليه الصلاة','5','63',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1354,'والسلام) فيفعله كما يفعله، إن كان مباحًا فمباح، أو واجبًا فواجب، أو مندوبًا فمندوب ويجب علينا اعتقاده كما يعتقده.
أو يقرر هكذا: قيام الدليل على وجوب إتباعهم في كل الأمور، كقيام الدليل على وجوب إتباع النبي -صلى الله عليه وسلم- فيها.
فكما أن المباح قد أخرج من عموم التأسي لدليل، ولم يقدح في الدلالة على الباقي، فكذلك الإجماع، فيبقى واجب الإتباع في غيره، وفيه نظر.
قيل: المجمعون أثبتوا الحكم المجمع عليه بالدليل الذي أجمعوا لأجله.
وإثبات الحكم بذلك الدليل من جملة سبيلهم، فإن وجب علينا إثبات','5','64',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1355,'ذلك بإجماعهم لا بالدليل، كان ذلك إتباعًا لغير سبيلهم ولا يجوزونه.
وإن وجب إثباته (بالدليل، فيلزم أن لا يكون الإجماع نفسه دليلًا مستقلًا وهو خلاف المدعى.
وأيضًا: فأنتم لا تقولون بوجوب إثباته).
قلنا: خص النص وهو قوله تعالى: {ومن يشاقق الرسول} الآية.
فيه أي في وجوب الاستدلال بذلك الدليل.
وهذه الصورة قد خصت بالاتفاق، لأن الحكم ثبت بإجماعهم.
وإذا ثبت فلا يحتاج إلى دليل آخر.
قيل: سلمنا جميع ما قلتم، لكن الآية تدل على وجوب اتباع','5','65',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1356,'سبيل كل المؤمنين الموجودين إلى يوم القيامة.
لأنه جمع محلي باللام فيفيد العموم، لكن إتباع سبيل كل المؤمنين محال، فلا يكون واجبًا، وإلا يلزم وقوع التكليف بالمحال، ولا يكون إجماع أهل كل عصر حجة لأنهم بعض المؤمنين.
قلنا: ليس المراد من لفظ المؤمنين كل الموجودين إلى يوم القيامة، بل المراد كل المؤمنين الموجودين في كل عصر؛ لأن المقصود من حجية الإجماع العمل به، لأن الله -تعالى- علق العقاب على مخالفتهم زجرًا عنها وترغيبًا في الأخذ بقولهم.
فعملنا أن المقصود هو العمل، ولا عمل في القيامة.
(فإن قيل: غايتته الظهور والتمسك بالظاهر، إنما يثبت بالإجماع، ولولاه لوجب العمل بالدلائل المانعة من إتباع الظن، فيكون إثباتًا للإجماع بما لا يثبت حجيته إلا به فيصير دورًا.
والجواب: أن الإجماع الذي ثبتت حجيته بالظواهر، إنما هو بعض','5','66',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1357,'أنواع الإجماع، وهو ما لم يبلغ المجمعون فيه عدد التواتر.
وحجية الظواهر إنما ثبتت بنوع آخر منه يبلغ المجمعون فيه عدد التواتر، والنوع المتوقف حجيته على الظواهر غير النوع المتوقف عليه حجية الظواهر.
واعلم أن التمسك بالظواهر فيما يثبت به أصل كلي كمسألة المصنف هل يصح فيه خلاف؟
قال الأبهري: والحق أنه يصح، لأن السلف أثبتوا حجية الإجماع والقياس بالظواهر من غير نكير منهم. انتهى
وأيضًا: لأن المقصود منه العمل بالأحكام المستفادة منه، وفي مثله يكفي الظن.
وقيل: لا يصح لأنه من العلميات).','5','67',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1358,'الدليل الثاني على أن الإجماع حجة:
قول تعالى: {وكذلك جعلناكم أمة وسطًا}.
فقد عدلهم -تعالى- لأن معنى وسطًا: عدولًا، إذ وسط كل شيء أعدله.
وعلل ذلك أيضًا: بقوله تعالى: {لتكونوا شهداء على الناس} والشاهد لا بد وأن يكون عدلًا.
وهذا التعديل الحاصل لأمة محمد -صلى الله عليه وسلم- وإن لزم منه تعديل كل فرد منها، لكون نفي التعديل عن واحد مستلزم لنفيه عن المجموع، لكنه ليس المراد تعديلهم فيما ينفرد به كل واحد منهم، لأنا نعلم بالضرورة خلافه، فتعين أن يكون تعديلهم فيما يجمعون عليه، فتجب عصمتهم عن الخطأ قولًا وفعلًا، صغيرة وكبيرة لأن الله -تعالى- يعلم السر والعلانية، فلا يعدلهم مع ارتكاب المنهيات المؤثرة في','5','68',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1359,'العدالة، وإذا ثبتت عصمتهم، وجب أن يكون قولهم وفعلهم حجة وهو المطلوب.
وهذا بخلاف تعديلنا، فإنه لا يوجب العصمة عن الخطأ لعدم اطلاعنا على الخفايا.
قيل عليه: العدالة -لكونها فعل الواجبات واجتناب المحرمات- فعل العبد، والوسط -لكونه مجعولًا له -تعالى- بقوله تعالى: {جعلناكم أمة وسطا} - فعل الله تعالى، فلا تكون العدالة عبارة عن الوسط، فلا يكون جعلهم وسطًا عبارة (عن تعديلهم)، إذ المعدل لا يجعل العدل عدلًا بل يخبر عن حاله.
قلنا: الكل فعل الله -تعالى- على مذهبنا، يعني كون','5','69',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1360,'العدالة فعل العبد لا ينافي كونها وسطًا؛ إذ فعل العبد فعل الله -تعالى- مخلوق له -تعالى- كما هو مذهب أهل السنة، فقد برهن عليه في الكلام.
فكما أن الوسط مجعول له، فكذلك العدالة فعل له.
قيل: سلمنا أنهم عدول، لكنهم عدول وقت أداء الشهادة للآية والعدالة معتبرة في الشاهد وقت الشهادة لا قبله، فتكون الأمة عدولًا يوم القيامة لا في الدنيا.
فلا يكون قوله حجة.
قلنا: حينئذ لا مزية لهم، أي: لأمة محمد -صلى الله عليه وسلم- على غيرهم في هذه الفضيلة التي خصهم الله -تعالى- بها، فإن','5','70',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1361,'الكل، أي: كل الأمم تكون كذلك أي: عدول في الآخرة، لكن المزية حاصلة؛ لأن الله -تعالى- إنما وصفهم بالعدالة لتعظيمهم وتمييزهم عن سائر الأمم فتعين حمله على الدنيا، وفيه نظر.
فالأحسن أن يجاب: بأن العدالة لا تتحقق إلا مع التكليف، ولا تكليف في الآخرة.
الدليل الثالث على أن الإجماع حجة:
قال النبي -صلى الله عليه وسلم- «لا تجتمع أمتي على خطأ» رواه أبو داود، وسكت عليه لكن بلفظ: «وأن لا تجتمعوا على ضلالة»','5','71',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1362,'ونظائره من قوله (صلى الله عليه وسلم).
كما روي عنه (صلى الله عليه وسلم): «لا يجمع الله أمتي» أو قال: «هذه الأمة على الضلالة أبدًا» رواه الحاكم في المستدرك «عليكم بالسواد الأعظم».','5','72',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1363,'رواه أبو نعيم في تاريخ أصبهان. «ومن أثنيتم عليه خيرًا أوجبت له الجنة» رواه مسلم.','5','73',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1364,'فإنها أي: هذه الأحاديث: وإن لم يتواتر آحادها لكن المشترك بينهما، وهو عصمة الأمة عن الخطأ متواتر.
وهذا الدليل ساقط في كثير من النسخ.
وقال الإمام: دعوى التواتر المعنوي فيها بعيد.
وهو مبين في الأصل مع دليل قوي.
تنبيه:
حاصل الأدلة التي قالها المصنف إنما يحسن الاستدلال بها إذا','5','74',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1365,'قلنا: إن الإجماع ظني مطلقًا.
وهو الصحيح عند الإمام الرازي والآمدي.
والأكثر على أنه قطعي.
واختار في جمع الجوامع: أنه عن اتفق المعتبرون على أنه إجماع فهو قطعي، وإن كان مختلفًا فيه كالسكوتي، وما ندر مخالفه فظن، عند القائل به، وخرق الإجماع حرام.
والشيعة عولوا عليه، أي: على الإجماع، لاشتماله على قول الإمام المعصوم، إذ عندهم أن زمان التكليف لا يخلو عن إمام يأمر الناس بالطاعات، ويردعهم عن المعاصي، ولا بد، أن يكون معصومًا،','5','75',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1366,'وإلا لافتقر إلى إمام آخر، ويتسلسل، وإذا كان معصومًا كان الإجماع حجة، لاشتماله على قوله كما مر، لأنه رأس الأمة، ورئيسها لا لكونه إجماعًا.
وجوابه: أن ذلك مبني على وجوب مراعاة المصالح ولا يجب، وإن سلمناه، فالردع إنما يحصل بنصب إمام ظاهر قاهر.
وهو يجوزون أن يكون خفيًا خاملًا، ويجوزون عليه الكذب أيضًا خوفًا وتقية.
وذلك كله ينافي المطلوب.
ولما كان هذا البحث محله علم الكلام لم يذكروا جواله؛ لأنه ذكره في المطالع.
المسألة الثالثة
قال مالك -رضي الله عنه-: إجماع أهل المدينة حجة.','5','76',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1367,'ولم يقيده المصنف بالصحابة (رضي الله تعالى عنهم) والتابعين.
وقيده له ابن الحاجب.
فقيل: قول مالك -رضي الله تعالى عنه- محمول على أن روايتهم مقدمة على رواية غيرهم.
وقيل: محمول على حجية إجماعهم في المنقولات المستمرة كالأذان والإقامة، والصاع، والمد دون غيرهما، وبه قال القرافي.
وقال ابن دقيق العيد: إنه أقرب.
والصحيح عند ابن الحاجب هو التعميم، أي: القول بكونه حجة مطلقًا.','5','77',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1368,'وضعفه ابن دقيق العيد في شرح العمدة جدًا.
والأكثر على أنه ليس بحجة.
وقال مالك (رضي الله تعالى عنه) بحجيته، لقوله عليه الصلاة والسلام: «إن المدينة لتنفي خبثها».
والباطل خبث، فيكون منفيًا عن أهلها.
وهو ضعيف أي: الاستدلال بالحديث، لا الحديث نفسه، فإنه متفق عليه.
لكن بلفظ: «إن المدينة كالكير تنفي خبثها».','5','78',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1369,'ووجه الضعف أن الحديث يحمل على أنها في نفسها فاضلة مباركة لما علم من جود الباطل فيها كالمعاصي.
وأيضًا فلا دلالة على انتفاء الخطأ عما اتفق عليه أهلها بخصوصها.
ولا نسلم أن الخطأ خبث؛ لأن الخطأ معفو عنه والخبث منهي عنه.
وحمله ابن عبد البر والقاضي عياض: على حياته -صلى الله عليه وسلم- فلم يخرج عنه إلا من لا خير فيه.
قال النووي: وليس بظاهر، وبيانه في الأصل.','5','79',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1370,'الرابعة
قالت الشيعة: إجماع العترة حجة، لقوله تعالى: {إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرًا} فنفى الرجس عن أهل البيت، والخطأ رجس، فيكون منفيًا عنهم، وإذا كان الخطأ منفيًا عنهم، كان بإجماعهم حجة، وهم: علي وفاطمة، وابناهما:','5','80',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1371,'(الحسن، والحسين) -رضي الله عنهم- لأنها لمانزلت: لف النبي -صلى الله عليه وسلم- عليهم كساء وقال: «هؤلاء أهل بيتي وخاصتي، اللهم أذهب عنهم الرجس وطهرهم تطهيرًا».
قالت أم سلمة -رضي الله عنها-: وأنا معهم يا رسول الله؟
قال: «أنت على مكانك وأنت على خير» رواه الترمذي. وقال: حسن غريب، ورواه الحاكم، وقال: صحيح على شرط الشيخين،','5','81',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1372,'وروى مسلم قريبًا منه.
وأجيب: بمنع أن الخطأ رجس، لأنه مأجور به، والرجس: قيل: العذاب، وقيل: الإثم، وقيل: كل مستقذر ومستنكر.
وقالوا: أيضًا هو حجة، لقوله -صلى الله عليه وسلم- «إني تارك فيكم ما إن تمسكتم به لن تضلوا: كتاب الله وعترتي».','5','82',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1373,'رواه الترمذي بمعناه، وقال: حسن غريب.
فقد جعل -صلى الله عليه وسلم- العترة قرينة الكتاب، فتجب موافقتهما في الحجية.
وأجيب: بأن هذا من باب الآحاد، وهو غير مقبول عند الشيعة، هذا وليس فيه دلالة.
إذ المراد من قوله (عليه الصلاة والسلام): «لن تضلوا» الكفر والخروج عن الإسلام، فإن الضلال غالب فيه.
ولم يشتغل المصنف بجوابه لظهوره.','5','83',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1374,'وقد حكى الشيخ أبو أسحاق في شرح اللمع: عن الشيعة أيضًا، أن قول علي -رضي الله تعالى عنه- وحده حجة.
فإن قلت: كيف يجتمع النقل عنهم من كون إجماع العترة حجة، مع ما تقدم للمصنف عنهم إنكار الإجماع؟
فالجواب: أنهم أنكروا كونه حجة على تفسيره المعروف لا مطلقًا.
المسألة الخامسة
قال القاضي أبو خازم من الحنفية:','5','84',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1375,'إجماع الخلفاء الأربعة -رضي الله عنهم- حجة.
ونقل عن الإمام أحمد -رضي الله تعالى عنه- لقوله -صلى الله عليه وسلم-: «عليكم بسنتي وسنة الخلفاء الراشدين من بعدي تمسكوا بها، وعضوا عليها بالنواجذ».
رواه أبو داود وابن ماجة والترمذي، وقال: حسن صحيح وصححه غيره.','5','85',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1376,'والمراد بالخلفاء الراشدين هنا: الأئمة الأربعة، كما قال: البيهقي لما روى عنه -صلى الله عليه وسلم- أنه قال: «الخلافة من بعدي ثلاثون، سنة ثم تكون ملكًا» أي: تصير ملكًا.
وإسناده حسن.
وأخرجه الإمام أحمد في المناقب وألو حاتم.
وجه التمسك به: أنه عليه -الصلاة والسلام- أمر باتباع سنتهم،','5','86',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1377,'كما أمر باتباع سنته، فوجب إتباعهم كإتباعه.
وقيل: إجماع الشيخين أبي بكر وعمر- (رضي الله تعالى عنهما) - حجة، لقوله -صلى الله عليه وسلم-: «اقتدوا باللذين من بعدي أبي بكر وعمر» - رضي الله تعالى عنهما.
رواه الترمذي وحسنه، وأخرجه ابن ماجة، وابن حبان في صحيحه فلا يصح أن يقال: الحديث موضوع.
والجواب عن الحديثين واحد، وهو أن المراد منهما: بيان أهليتهم','5','87',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1378,'لاتباع المقلدين لهم (لا أن) إجماعهم حجة على المجتهدين.
فإن لفظ «عليكم» و «اقتدوا» مشعر بالتقليد ثم إنه معارض بما هو مبين في الشرح.
المسألة السادسة
يستدل بالإجماع في كل ما لا يتوقف عليه الإجماع كحدوث العالم -بفتح اللام- ووحدة الصانع.','5','88',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1379,'لأنه يمكننا معرفة الصانع، بإمكان العالم (وحدوث الأعراض).
ثم نعرف صحة النبوة، ثم نعرف الإجماع، ثم نعرف به حدوث العالم.
وكذلك وحدة الصانع، فإنه يجوز إثباتها بالإجماع، لا كإثباته، أي: لا كإثبات الصانع مما يتوقف حجية الإجماع عليه.
بيانه: الإجماع متوقف على وجود الصانع، وعلى كونه متكلمًا، وعلى صحة النبوة، فلو أثبتنا هذه الأشياء بالإجماع (لزم الدور) وفيه نظر مبين في الشرح.
على أن الشيخ أبا إسحاق في شرح اللمع قال: أنه لا يعتد بالإجماع','5','89',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1380,'في حدوث العالم أيضًا.
تنبيه: قال الشيخ تقي الدين واسم الصانع اشتهر على ألسنة المتكلمين ولم يرد في الأسماء.
وقريء قي الشواذ: «صنعة الله»، فمن اكتفى في إطلاق الأسماء بورود الفعل يكتفي بمثل ذلك.','5','90',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1381,'<span data-type="title" id=toc-210>الباب الثاني: في أنواع الإجماع التي اختلف في كونها إجماعًا</span>
وهي قسمان:
قسم أخرج من الإجماع وهو منه.
وقسم أدخل في الإجماع وليس منه.
وفيه مسائل:
الأولى
إذا تكلم المجتهدون جميعهم في مسألة، واختلفوا فيها على قولين فهل لمن يأتي بعدهم من المجتهدين إحداث قول ثالث في تلك المسألة؟','5','91',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1382,'اختلف فيه: منعه الأكثرون، وجزم به في المعالم. وجوزه أهل الظاهر مطلقًا.
والحق أنه، أي أن القول الثالث المحدث إن لم يرفع مجمعًا عليه، جاز إحداثه، لأنه لا محذور فيه.
مثاله: اختلافهم في جواز أكل المذبوح بلا تسمية.
قال بعضهم: يحل مطلقًا.','5','92',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1383,'وقال بعضهم: لا يحل مطلقًا.
فالتفصيل بين العمد والسهو ليس رافعًا لشيء أجمع عليه القائلان الأولان، بل هو موافق في كل قسم منه لقائل.
وإلا، أي: وإن رفع مجمعًا عليه، فلا يجوز، لامتناع مخالفة الإجماع مثاله:
ما قيل في الجد مع الأخ: الميراث للجد، وقيل: الميراث لهما.
فقد اتفق القولان على أن للجد شيئًا من الإرث، فلا سبيل إلى حرمانه.
فإن القول بحرمانه وإعطاء المال كله للأخ قول ثالث رافع لما أجمع عليه الأولان؛ فلا يجوز.
وأما القول الذي نقله ابن حزم في المحلي من أن الجد يحجب','5','93',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1384,'بالإخوة، فيحتمل أنهم أجمعوا بعده على خلافه أو متأخرًا عن الإجماع، فلا يعتد به (كذا قيل. لكن روى عن عبد الرحمن بن غنم أن عمر ذاكره في الجد قال: فقلت له: «إن دون الجد شجرة أخرى فما خرج منها فهو أحق بها». وهو موقوف حسن، كما قاله شيخ الإسلام. ونقل القول بحرمان الجد أيضًا عن زيد بن ثابت،','5','94',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1385,'وعن علي بن أبي طالب (رضي الله عنهما) وأنهما رجعا إلى المقاسمة).
واختار هذا التفصيل الإمام وأتباعه والآمدي وابن الحاجب.
قيل - من جهة المانعين مطلقًا. إن أهل العصر الأول اتفقوا على عدم القول الثالث، وعلى عدم الأخذ به، فإنهم لما اختلفوا على قولين، فقد أوجب كل من الفريقين الأخذ إما بقوله، أو بقول الآخر، وتجويز القول الثالث يرفع ذلك كله فيكون باطلًا.
قلنا: اتفاقهم على القولين كان مشروطًا بعدمه أي بعدم القول','5','95',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1386,'الثالث، فزال الإجماع بظهور ذلك القول الثالث بزواله أي: بزوال شرطه.
قيل اعتراضًا على هذا الجواب: ما ذكرتم في جواز إحداث القول الثالث وارد على الإجماع الوجداني، يعني الإجماع على القول الواحد، إذ يقال فيه كما قيل في الأول فيجوز خلافه.
قلنا: هذا وإن كان ممكنًا أيضًا في الإجماع الوجداني، لكنهم أجمعوا على عدم اعتباره فيه.
وإليه أشار بقوله: لم يعتبر فيه إجماعًا.
فليس لنا أن نحكم عليهم بوجوب التسوية بين الإجماع الوجداني، والإجماع على القولين، وفيه نظر.
قيل: إظهاره، أي: إظهر القول الثالث يستلزم تخطئة','5','96',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1387,'الأولين.
لأنه إنما يجوز إظهاره إذا كان حقًا؛ لأن الباطل لا يجوز القول به.
ولا يكون حقًا إلا عند كون القولين باطلين إذ الحق واحد.
وحينئذ يلزم إجماع الأمة على الباطل، وأنه غير جائز.
وأجيب عنه: بأن المحذور هو التخطئة للأمة في قول واحد إذا أجمعوا عليه، وأما إذا اختلفوا فيه فلا؛ لأن غاية ذلك تخطئة بعضهم في أمر، وتخطئة البعض الآخر في غير ذلك الأمر، فلم يجتمعوا على ضلالة.
وفيه نظر؛ لأن الأدلة المقتضية لعصمة الأمة من الخطأ شاملة للصورتين، فالتخصيص لا دليل عليه.
وأجيب عن هذا النظر بما فيه نظر، وهو مبين في الأصل.','5','97',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1388,'(قال الكرماني: ولا نظر، إذ باعتبار تواتر القدر المشترك من النصوص، صار ضروريًا من الدين محذورًا به تخطئة الأمة في الإجماع الوجداني، وأما في غيره فلم تضر هذه المرتبة، وعدم المساواة بينهما ظاهر).
تنبيه: صورة هذه المسألة: أن يتكلم كل المجتهدين في المسألة كما سبق.
وصرح به الغزالي في المستصفى.
وأما مجرد نقل القولين في عصر من الأعصار، فلا يكون مانعًا من إحداث الثالث، لأنا لا نعلم هل تكلم الجميع فيها أم لا؟
المسألة الثانية
في مجتهدي الأمة إذا لم يفضلوا بين مسألتين، أي: لم يفرقوا بينهما، بل قال بعضهم بالنفي مثلًا، وبعضهم بالإثبات، فهل يجوز','5','98',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1389,'لمن بعدهم الفصل بينهما أم لا؟
والحق أنهم إن نصوا بعد الفرق بين المسألتين -وعداه بالباء لتضمنه معنى صرحوا- أو لم ينصوا على ذلك.
لكن اتحد الجامع بين المسألتين، لم يجز التفصيل بينهما؛ لأنه رفع مجمعٍ عليه.
مثاله: ما لو قيل بتوريث العمة دون الخالة، أو العكس.
وقد اختلفوا في توريثهما: مع اتفاقهم على أن العلة فيه أو في عدمه، كونهما من ذوي الأرحام.','5','99',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1390,'فتوريث إحداهما دون الأخرى خارق للإجماع؛ لأنه بمثابة قولهم: لا تفصلوا.
وإلا، أي: وإن لم تكن المسألتان مما نصوا على اتحادهما في الحكم، ولا اتحدت العلة، لكن لم يكن من الأمة من فرق بينهما جاز التفصيل بينهما، إذ بذلك لا يصير مخالفًا لما أجمعوا عليه.
غايته أنه موافق لكل من الفريقين في مسألة، والموافقة في مسألة لا توجب عدم المخالفة في غيرها.
وإلا، أي: وإن لم يكن جائزًا لكان يجب على من ساعد، أي:','5','100',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1391,'وافق مجتهدًا في حكم بدليل مساعدته في جميع الأحكام، وذلك باطل.
مثاله: ما قيل: تجب الزكاة في مال الصبي دون الحلي المباح وعليه الشاغعي -رضي الله تعالى عنه.
وقد قيل: يجب فيهما، وقيل: لا يجب فيهما.
الفرق بين هذه المسألة والتي قبلها أن هذه فيما إذا كان محل الحكم متعددًا، وتلك فيما إذا كان متحدًا، قاله القرافي وغيره.
وجمع بينهما ابن الحاجب لقربهما في المعنى.
قيل -من جهة المانعين مطلقًا: الأمة أجمعوا على الاتحاد في','5','101',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1392,'الحكم في المسألتين، فالفصل بينهما مخالف للإجماع، فلا يجوز.
قلنا: ليس عدم التفصيل إجماع على اتحاد الحكم، إذ هو عين الدعوى ونتبرع ونقول: لا يدل عليه، لأن عدم القول بالتفصيل، غير القول بالتفصيل.
أو نقول: لا محذور في مخالفة هذا الإجماع، فإن الواقع منهم ليس هو التنصيص على الاتحاد، بل الاتحاد في فتواهم، وهو لا يمنع من الفصل، فإن ذلك عين الدعوى.
قيل: يجوز التفصيل بين المسألتين مطلقًا.
إذ لو لم يجز لم يقع، لكنه وقع، إذ قال الثوري: الجماع ناسيًا','5','102',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1393,'يفطر، والأكل ناسيًا لا يفطر، وكان بعضهم على أنه لا يفطر مطلقًا جماعًا وغير جماع. وبعضهم بفطر مطلقًا.
ففرق الثوري بين المسألتين مع اتحادهما في الجامع وهو الإفطار ناسيًا.
قلنا: ليس قول الثوري بدليل، ولا حجة على غيره، حتى يجوز التمسك به، ويجوز كونه من المخالفين في هذه المسألة، للمختار.','5','103',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1394,'الثالثة
يجوز الاتفاق من أهل العصر على الحكم بعد الاختلاف منهم فيه. واختاره الإمام الرازي، وابن الحاجب، خلافًا للصيرفي. ونقل عن القاضي.
وقيل: إن لم يستقر الخلاف جاز، وإلا فلا واختاره إمام الحرمين والآمدي.
لنا على الجواز: الإجماع من الصحابة (رضي الله تعالى عنه) على أن الخلافة لأبي بكر (رضي الله عنه) بعد الاختلاف منهم. رواه','5','104',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1395,'البيهقي (في سننه) وغيره.
وقد روى تقاعد بعضهم عن ذلك زمانًا، ثم وافق كما هو مبين في الشرح.
وذلك يقتضي أن الإجماع وقع بعد استقرار الخلاف، وهو يدل على الجواز عند عدم استقرار الخلاف من باب أولى، فدليل المصنف مطابق','5','105',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1396,'لدعواه فإنها (أعم، من أن يكون بعد استقرار الخلاف أو قبله).
والصيرفي له في الاحتجاج على مذهبه ما سبق في المسألة الأولى.
من أن الاختلاف الأول إجماع منهم على جواز الأخذ بأي واحد من القولين.','5','106',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1397,'والاتفاق بعده ينافي ذلك الإجماع، فلو جاز وقوعه لزم رفع الإجماع. وهو باطل، لما تقدم في النسخ.
وجوابه: ما سبق: وهو أن الإجماع على التخيير كان مشروطًا بعدم الاتفاق، فإذا اتفقوا فيزول بزوال شرطه.
ولما كان الجواب يعلم مما سبق اكتفي به.
المسألة الرابعة
إذ اختلف أهل العصر على قولين، ثم حدث بعدهم مجتهدون آخرون وحصل منهم الاتفاق على أحد قولي الأولين بعد ما استقر خلاف الأولين، وقال كل بمذهب.
وذلك كالاتفاق على حرمة بيع أم الولد مع أن عليًا وابن مسعود وجابرًا (رضي الله عنهم) وغيرهم كانوا يقولون بالجواز.','5','107',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1398,'وكاتفاقهم على حرمة نكاح المتعة، وهو نكاح المرأة إلى مدة.','5','108',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1399,'مع أن ابن عباس كان يفتي بالجواز، بعد استقراء خلاف الصحابة في المسألتين.
فهو إجماع وحجة، وبه قال الإمام الرازي، وأتباعه (وصححه النووي في شرح مسلم).
خلافًا لبعض الفقهاء والمتكلمين، حيث قالوا: إنه يمتنع حصوله،','5','109',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1400,'منهم الإمام أحمد (رضي الله عنه) وإمام الحرمين والأشعري والغزالي والآمدي.
وقال بعضهم: لا أثر لهذا الإجماع، وهو مذهب الشافعي، كما نقله الغزالي، وابن برهان.
قال ابن الحاجب: والحق أنه بعيد إلا في القليل من المسائل.
وصحح في جمع الجوامع: الامتناع إن طال الزمان، والجواز إذا قرب.
وحكي عن إمام الحرمين.','5','110',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1401,'قال الآمدي: ولا نسلم حصول الإجماع في المثال الأول، لأن الشيعة يقولون بالجواز.
قلت: وهو مذهب داود وأحد قولي الشافعي -رضي الله تعالى عنه. ونقل الماوردي عن ابن عباس (رضي الله عنهما) الرجوع عن جواز نكاح المرأة إلى مدة، وأفتى بالتحريم، والله أعلم.
لنا: على أن هذا الاتفاق إجماع وحجة، هو أنه أي هذا','5','111',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1402,'الاتفاق سبيل المؤمنين، وكل ما هو سبيل المؤمنين يجب اتباعه لما سبق.
قيل: إثبات هذا الحكم المختلف فيه بهذا الاتفاق رد للخلاف عند التنازع إلى الإجماع؛ لأن الرد إليه رد إلى غير الكتاب والسنة، وهو غير جائز، لقوله تعالى: {فإن تنازعتم في شيء فردوه إلى الله والرسول}.
فقد أوجب الرد إلى كتاب الله تعالى وسنة رسوله -صلى الله عليه وسلم -لا إلى الإجماع.
قلنا: زال شرط وجوب رد النزاع إلى الكتاب، وذلك لأن وجوب رد النزاع إلى الكتاب مشروط بوجود النزاع، فلما زال الشرط، وهو النزاع بين العصر الثاني زال المشروط وهو الرد إلى الكتاب، وفيه نظر.','5','112',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1403,'فالأحسن أن يجاب بأن الرد إلى الإجماع رد إلى الكتاب.
قيل: لو كان اتفاق أهل العصر الثاني على أحد قولي العصر الأول حجة، لكان اتفاق التابعين على أحد قولي الصحابة حجة، وكان الأخذ بقول الفريق الثاني من الصحابة غير جائز؛ لأنه مخالف لما وجب إتباعه، لكنه اهتداء، لما روي عنه -صلى الله عليه وسلم -أنه قال: «أصحابي كالنجوم بأيهم اقتديتم اهتديتم».
روى عن طرق كثيرة.','5','113',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1404,'(وقال ابن حزم: موضوع).
قال البيهقي: مشهور المتن وأسانيده ضعيفة.
قال الزركشي: لكن يتقوى طرقه بعضها ببعض، لاسيما وقد احتج به الإمام أحمد - (رضي الله عنه).
قال أبو يعلى: واحتجاجه يدل على صحته عنده، وله شواهد في الصحيح فدل الحديث على حصول الاهتداء بالاقتداء بقول كل واحد','5','114',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1405,'منهم سواء حصل بعد ذلك اتفاق أم لا، والتقييد خلاف الظاهر.
قلنا: هذا الخطاب كان مع العوام الذين كانوا في عصرهم، إذ الخطاب إنما يكون مع الحاضرين، فلا يتناول من بعدهم، فلا يكون الخطاب متناولًا لخواص أهل العصر الثاني، وإذا لم يكونوا مخاطبين به لم يبق فيه دلالة على مدعاكم.
ونبه على هذه الثلاثة بقوله: (في عصرهم) وليس الخطاب مع مجتهدي الصحابة (رضي الله عنهم) لأن المجتهد، لا يقلد المجتهد، ولأن قول الصحابي ليس بحجة، على ما سيأتي.
(على أن ابن عبد البر: فسر الحديث بالنقل؛ لأن جميعهم ثقات','5','115',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1406,'مأمونون عدول).
قيل: اختلافهم في العصر الأول إجماع منهم على التخيير بين قولين، بمعنى أنه يجوز الأخذ بكل واحد منهما، فلو كان اتفاق العصر الثاني على أحدهما إجماعًا مانعًا من الأخذ بخلافه؛ لزم تعارض الإجماعين وهو باطل، إذ يلزم بطلان أحدهما.
قلنا: قولكم: أن اختلاف العصر الأول، إجماع منهم على التخيير، ممنوع، لأن كل واحد من الفريقين، يعتقد خطأ الآخر، ويمنع عن الأخذ بقوله.','5','116',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1407,'وفي بعض النسخ: قلنا: زال لزوال شرطه، يعني الإجمال الأول على التخيير كان مشروطًا بعدم الإجماع الثاني، فلما حصل الإجماع الثاني زال الأول لزوال شرطه.
المسألة الخامسة
أهل العصر إذا اختلفوا في حكم على قولين، فماتت إحدى الطائفتين، أو ارتدت -والعياذ بالله تعالى -فإنه يصير في قول الباقين منهم حجة، لكونه قول كل الأمة الآن، وقول كل الأمة حجة كما مر، وبه جزم الإمام وأتباعه، وزادوا على المصنف التصريح بكونه إجماعًا أيضًا، وهو يؤخذ من تعليله.','5','117',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1408,'وحكى ابن الحاجب عن الأكثرين أنه لا يكون إجماعًا وذكر نحوه الآمدي.

<span data-type="title" id=toc-216>المسألة السادسة
فيما أدخل في الإجماع وليس منه</span>.
إذا قال البعض من أهل عصر واحد أو جماعة قولًا، وسكت الباقون عنه مع معرفتهم به، ولم ينكره أحد منهم، ولم يكن بعد استقرار المذاهب بل قبله وهو عند البحث عن المذاهب والنظر فيها؛ فليس بإجماع ولا حجة، واختاره القاضي ونقله عن الشافعي، وقال: إنه آخر أقواله.','5','118',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1409,'وقال إمام الحرمين: إنه ظاهر مذهبه.
وقال الغزالي: نص عليه في الجديد، واختاره الإمام الرازي وأتباعه.
وقيل: إجماع وحجة.
ونقل عن الإمام أحمد وأكثر الحنفية ويوافقه استدلال الشافعي -رضي الله تعالى عنه -بالإجماع السكوتي في مواضع.
وأجاب ابن التلمساني: بأنه إنما استدل به في وقائع تكررت كثيرًا، بحيث ينفي جميع الاحتمالات الآتية:','5','119',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1410,'وأجيب أيضًا: بأن تلك الوقائع ظهرت من الساكتين فيها قرينة الرضا فليست من محل النزاع.
كما ادعى الاتفاق على ذلك الروياني من الشافعية، والقاضي عبد الوهاب من المالكية.
وقال أبو على الجبائي المعتزلي: إنه إجماع، وحجة بعدهم، أي بعد انقراض العصر الأول.
وقه قال البندنيجي.
قال الشيخ أبو إسحاق في اللمع: إنه المذهب، قال: فأما قبل انقراضه، فهل نقول: إنه ليس إجماعًا قطعًا، أو على الخلاف؟ طريقان.','5','120',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1411,'وقال ابنه أبو هاشم هو حجة، وليس بإجماع، ونقل عن الصيرفي.
وقال الرافعي في كتاب القضاء كونه حجة هو المشهور.
قال: وهل هو إجماع؟ فيه وجهان.
أما إذا كان السكوت بعد استقرار المذاهب، فإنه لا يدل على الموافقة قطعًا، إذا لا عادة بإنكاره فلم يكن حجة.
لنا: على أنه ليس بإجماع ولا حجة قبل استقرار المذاهب، إنه ربما سكت لتوقف؛ لأنه لم يجتهد بعد، فلا رأي له في المسألة، أو اجتهد فتوقف لتعارض الأدلة، أو خوف من المفتي تعظيمًا له، أو هابه، أو الفتنة، فسكت لذلك.
أو رأى تصويب كل مجتهد فسكت؛ لأنه لا يرى الإنكار فرضًا.
ومع قيام هذه الاحتمالات لا يدل على الموافقة فلا يكون إجماعًا ولا','5','121',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1412,'حجة.
ورده ابن الحاجب بأنها، وإن كانت محتملة فهي خلاف الظاهر.
لما علم من عادتهم ترك السكوت في مثله، وفيه نظر.
قيل: من جهة أبي هاشم، تمسك في كل عصر بالقول المنتشر بين الصحابة ما لم يعرف له مخالف.
فدل ذلك على أن قول البعض وسكوت الباقين حجة.
وجوابه المنع، أي لا نسلم أن جميع الناس يتمسكون به من غير نكير، فإن وقع شيء فلعله وقع ممن يعتقد حجيته أو على الإلزام، أو على وجه الاستئناس به.
وأنه، أي هذا الدليل عند التحقيق، إثبات الشيء بنفسه، فإن القول المنتشر مع عدم الإنكار هو قول البعض وسكوت الباقين، فيكون إثباتًا','5','122',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1413,'للإجماع السكوتي بمثله. (وفيه نظر).
تنبيه: محل الخلاف ما إذا لم يقترن السكوت بأمارة الرضا.
فإن اقترن به فهو إجماع قطعًا، كما مر.
وإن اقترن بالسخط فليس بإجماع قطعًا، وأن يبلغ جميع المجتهدين، وأن يمضي زمن يمكنه النظر فيها عادة. وإلا فلا يكون من الإجماع السكوتي.
وأن تكون الواقعة في محل الاجتهاد، فإن لم تكن بأن كانت قطعية، أو لم تكن تكليفية، نحو عمار أفضل من','5','123',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1414,'حذيفة أو العكس.
فالسكوت على القول في الأولى بخلاف المعلوم فيها، وعلى ما قيل في الثانية، لا يدل على شيء.
وأن يكون قبل استقرار المذاهب كما مر.

<span data-type="title" id=toc-217>فرع: على القول بالإجماع السكوتي</span>، لأن حكمه تابع لحكمه، وهو قول البعض من المجتهدين قولًا فيها، أي في أمرٍ تعم به البلوى، أي تمس الحاجة إليه، كمس الذكر.
ولم يسمع من أحد خلافه.
فالمختار: أنه يكون كقول البعض وسكوت الباقين؛ لأن عموم','5','124',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1415,'البلوى يقتضي حصول العلم به، وإن لم يكن كذلك فلا، لاحتمال الذهول عنه.
وجزم المصنف به لقول الإمام الرازي: التفصيل هو الحق.
وقول البعض: وقال الإمام الرازي: الحق أنه إن كان مما تعم به البلوى فهو حجة، وإلا فلا.
وجزم البيضاوي به سهو، فإن الإمام الرازي والمصنف رجحا في الإجماع السكوتي أنه ليس بإجماع ولا حجة.
وهذه المسألة فرع تلك كما تقدم.
فإن قلنا: هناك ليس بحجة، فهنا أولى، وإن قلنا هناك: حجة، فلا يلزم أن يكون هنا حجة، إلا إذا كان مما تعم به البلوى.
وقلنا: الظاهر أن الكل اطلعوا عليه.
قال العراقي: واختار الآمدي أن قول البعض فيما تعم به البلوى، ولم يسمع خلافه، أنه ليس كقول البعض وسكوت الباقين، وهو أظهر لأنا لا نعلم هل بلغهم أم لا؟ .','5','125',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1416,'قلت: فيه نظر فليتأمل.
واعلم أن تمسك المصنف في باب القياس بالإجماع السكوتي لا ينافي اختياره هنا أنه ليس بإجماع ولا حجة؛ لأن تمسكه بما تكرر في وقائع كثيرة، ولم يظهر خلاف فتنتفي جميع الاحتمالات السابقة.
وتقدم هذا في الجمع بين كلامي الشافعي -رضي الله تعالى عنه -.','5','126',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1417,'<span data-type="title" id=toc-218>الباب الثالث: في شرائطه</span>
أي شرائط الإجماع، وما لا يكون شرطًا وإن ظن شرطيته.
وفيه -أي: في هذا الباب -مسائل خمسة:
الأولى
شرط الإجماع في كل فن من الفنون أن يكون فيه قول كل عالمي','5','127',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1418,'ذلك الفن في ذلك العصر.
فالعبرة في مسائل الكلام بالمتكلم، وفي مسائل الفقه بالتمكن من الاجتهاد فيها، فلا عبرة بالمتكلم في الفقه من حيث هو متكلم، ولا بالفقيه في الكلام من حيث هو فقيه، بل المتمكن من الاجتهاد، في باب من أبواب الفقه دون غيره يعتبر وفاقه وخلافه في ذلك الباب.
ولا عبرة بالفقيه الحافظ للأحكام الغير المتمكن من الاجتهاد في الأصح.
وأما الأصولي المتمكن من الاجتهاد فيه ولم يكن حافظًا للأحكام، ففيه أقوال: واختار القاضي أبو بكر: اعتباره مخالفةً','5','128',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1419,'وموافقةً.
قال الإمام الرازي: وهو الحق.
وقلنا: إن المعتبر في الإجماع قول كل عالمي ذلك الفن، فإن قول غيرهم يكون بلا دليل، لكونهم غير عالمين بأدلته، والقول بلا دليل يكون خطأ لا يعتد به، لأنهم مثل العوام في ذلك الفن.
فإذا قلنا: لا بد من قول جميع المجتهدين من ذلك الفن.
فلو خالف واحد منهم لم يكن قول الباقين سبيل الكل، فلا يكون إجماعًا ولا حجة، لأن الواجب (الذي قام الدليل عليه) إتباع سبيل كل المؤمنين لا إتباع سبيل بعضهم.','5','129',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1420,'وبه قال الجمهور، واختاره الإمام الرازي والآمدي.
وقال ابن الحاجب: إذا ندر المخالف لا يكون إجماعًا قطعيًا. لكن الظاهر أنه حجة.
وقال أبو الحسن الخياط من المعتزلة ومحمد بن جرير الطبري، وأبو بكر الرازي:','5','130',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1421,'{المؤمنون} الوارد في الآية، وغيرها يصدق على الأكثر.
كما يقال في البقرة: إنها سوداء باعتبار الغلبة، وإن كان فيها شعرات بيض.
فإذا صدق على الأكثر، كان قولهم حجة؛ لأنه سبيل المؤمنين.
وظاهره أنه لا تضر مخالفة الأقل، وأن العبرة بقول الذين هم أكثر من النصف، وإن كثر عدد المخالفين.
لكن سيأتي للمصنف في جواب شبهتهم الثانية ما يقتضي أنهم معترفون بأن مخالفة الثلث تضر.
ونقل الإمام الرازي عن الثلاثة أنهم قالوا: تضر مخالفة الثلاثة دون الاثنان والواحد.','5','131',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1422,'وقال القاضي أبو بكر: الذي يصح عن ابن جرير أن المخالفين إن بلغوا عدد التواتر قدحت مخالفتهم في الإجماع وإلا فلا.
قلنا: لفظ المؤمنين إنما يصدق على الأكثر مجازًا، فإن الجمع المعرف بأل حقيقة في الاستغراق ومجاز في غيره، ولذا يصح نفيه، إذ يقال: ليسوا كل المؤمنين، بل بعضهم، والأصل عدم المجاز.
وقالوا ثانيًا: حديث: «عليكم السواد الأعظم» رواه الحاكم في المستدرك. وهو بعض حديث،','5','132',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1423,'يدل على وجوب إتباع الأكثر، فيكون قولهم حجة.
وقال العراقي: لا يصح الاستدلال به- أي: بهذا الحديث -لضعفه، رواه ابن ماجة من حديث أنس بإسناد ضعيف.','5','133',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1424,'قلنا: المراد من السواد الأعظم: كل الأمة دون أكثرها؛ لأن إرادة أكثر الأمة من هذا الحديث، يوجب عدم الالتفاف إلى مخالفة الثلث -بضم الثاء -وأنتم لا تقولون به، كذا في بعض النسخ، وهو الموافق لنقل المصنف عن هؤلاء الثلاثة.
وفي بعضها: الثلاث -بفتح الثاء -، وهو الموافق لنقل الإمام عنهم، كما مر وعليه مشى الجاربردي والإسفرايني، قاله العراقي.
قال: والأول أولى ليوافق آخر كلام المصنف أوله.
واعلم أن أول هذا الحديث في المستدرك يدل على أن المراد بالسواد الأعظم: كل الأمة، كما هو مبين في الشرح.
فائدة: قال في الصحاح: الفن النوع.','5','134',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1425,'الثانية
لا بد له -أي: للإجماع -من سند -[أي]: من دليل -أو أمارة ينبني عليه ويستند إليه، وبه قال الجمهور، لأن الفتوى -أي: بدون المستند -قول (في الدين) بلا دليل ولا أمارة، وهو خطأ لكونه قولًا بالتشهي، والأمة معصومة عن الخطأ.
وفيه نظر:','5','135',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1426,'لجواز أن يوفقهم الله تعالى لاختيار الصواب.
قيل: أولًا: لو كان الإجماع عن مستند، فهو أي السند هو الحجة لا الإجماع، فلا فائدة للإجماع.
قلنا: لا نسلم ذلك بل يكونان أي الإجماع والسند دليلين على حكم واحد، واجتماع دليلين على حكم واحد جائز.
وإذًا: فائدة الإجماع كشفه عن وجود دليل في المسألة من غير حاجة إلى معرفته، والبحث عن دلالته على المدلول وحرمة مخالفته، إذ يجوز كون','5','136',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1427,'الظني سندًا للإجماع. والظني قبل أن يصير سندًا يجوز البحث عنه، وإذا صار سندًا سقط البحث والكشف عنه، وحرمت مخالفته حينئذ، وكانت مخالفته جائزة قبله.
قيل ثانيًا: الإجماع لا عن سند واقع، لأنهم صححوا بيع المراضاة إجماعًا بلا دليل، والوقوع دليل الجواز.
قلنا: لا نسلم أن الإجماع منعقد في هذه الصورة بلا دليل، بل','5','137',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1428,'له دليل هو سنده، وإنما ترك استكفاء بالإجماع فإنه أقوى منه، وعدم نقل الدليل لا يدل على عدمه.
واعلم أن ناقل الإجماع على صحة بيع المراضاة أبو الحسين (في المعتمد) وتبعوه.
فإن أرادوا به المعاطاه كما فسره به القرافي، كالشافعي -رضي الله تعالى عنه -وغيره مخالفين فيه.
وإن أرادوا غيره، فلا بد من بيانه، وبيان انعقاد الإجماع فيه من غير سند.
فإن قالوا: أنه إذا تحقق الرضا بين الجانبين صح البيع إجماعًا.','5','138',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1429,'وإنما اختلفوا فيما يتحقق به الرضا.
فالأدلة على هذا في غاية الشهرة والكثرة.

<span data-type="title" id=toc-221>فرعان: على أن الإجماع لا ينعقد إلا عن سند:</span>
الأول: يجوز الإجماع عن نص وظاهر، وكذا عن الأمارة، أي القياس، لأنها أي الأمارة مبدأ الحكم الشرعي، أي طريق إلى إثباته، فجاز أن يكون سندًا للإجماع كالنص، واختار (الإمام والآمدي) وابن الحاجب أنه واقع أيضًا بدليل أنهم أجمعوا','5','139',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1430,'على تحريم شحم الخنزير قياسًا على لحمه وعلى إراقة الشيرج إذا وقعت فيه فأرة قياسًا على السمن، وعلى إمامة أبي بكر -رضي الله تعالى عنه -قياسًا على تقديمه في الصلاة.','5','140',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1431,'وقيل: جائز ولم يقع.
قيل: عليه: إن الإجماع منعقد على جواز مخالفتها، أي مخالفة الأمارة للمجتهد، فلو جاز الإجماع عنها لزم جواز مخالفته؛ لأن مخالفته الأصل تستدعي مخالفة الفرع، لكن مخالفة الإجماع ممتنعة اتفاقًا.
قلنا: إنما تجوز مخالفة الأمارة قبل أن تصير سند الإجماع، أما بعد ما أجمعوا على حكم الأمارة فلا يجوز مخالفتها بالإجماع لاعتضادها (به.
قيل: اختلف فيها أي في الأمارة هل هي حجة أم لا؟
وذلك مانع عن انعقاد عنها)؛ لأن من لا يعتقد','5','141',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1432,'حجيتها لا يوافق القائل بالحجية، فلا ينعقد الإجماع لمخالفة البعض.
قلنا: ما ذكرتم منقوض بالعموم وخبر الواحد، فإن العلماء اختلفوا في حجية كل واحد منهما مع جواز انعقاد الإجماع عنهما اتفاقًا.
الفرع الثاني:
الإجماع الموافق لحديث، إذا لم نجد للإجماع دليلًا سواه لا يجب أن يكون انعقاد الإجماع ناشئًا عنه أي عن ذلك الحديث، وإن كان الظاهر أنه ناشئ عنه.
خلافًا لأبي (عبد الله) البصري المعتزلي حيث قال: يجب أن','5','142',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1433,'يكون مستنده.
ونقله ابن برهان عن الشافعي (رضي الله تعالى عنه) لأنه لا بد له من سند، وقد تيقنا صلاحيته.
والأصل عدم غيره.
وأوله بعضهم أن ذلك هو الظاهر لا أنه لازم.
وللقاضي عبد الوهاب فيه تفصيل مبين في الأصل.
وقال: إنه إذا كان الخبر متواترًا فلا خلاف في وجوب استناده إليه.','5','143',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1434,'وإنما كان اختيار المصنف أنه لا يجب أن يكون عنه لجواز اجتماع دليلين على مدلول واحد.
فيجوز أن يكون الإجماع عن غير ذلك الحديث.
المسألة الثالثة
لا يشترط انقراض المجمعين في انعقاد إجماعهم وكونه حجة، فإذا اتفقوا ولو حينا لم يجز لهم ولغيرهم مخالفته، وعليه المحققون.
ونقل عن الإمام أحمد (رضي الله عنه) وابن فورك وسليم الرازي اشتراطه.','5','144',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1435,'والمختار الأول، لأن الدليل السمعي قام على حجية الإجماع بدونه، يعني من غير اشتراط انقراض العصر، لأنه عام يتناول ما انقرض عصره، وما لم ينقرض، ولو في لحظة واحدة، مطلقًا غير مقيد بانقراض العصر.
قيل: لو لم يشترط لم يصح رجوع بعضهم لاستلزام الرجوع','5','145',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1436,'مخالفة الإجماع، لكن الرجوع ثابت.
فقد وافق الصحابة عليًا (رضي الله عنهم) في منع بيع أم الولد ثم رجع عنه.
ورد ذلك: بالمنع، أي بمنع ثبوت الإجماع قبل الرجوع.
ولا يصح منع ثبوت الرجوع، لأنه ثبت، فقد رواه حماد بن زيد.','5','146',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1437,'المسألة الرابعة
الإجماع لا يشترط التواتر في نقله، بل يجب العمل به، وإن نقل بخبر الواحد لأنه دليل، فجاز التمسك بمظنونه كما يجوز بمعلومه، كالسنة، حيث جاز نقلها بالآحاد.','5','147',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1438,'واختاره الإمام الرازي والآمدي وأتباعهما كابن الحاجب وقيل: ليس بحجة، ونقله الإمام الرازي عن الأكثرين.
المسألة الخامسة
الإجماع إذا عارضه نص أول القابل له منهما، وجمع بينهما به، كما إذا كان أحدهما عامًا، والآخر خاصًا، فيؤول العام بتخصيصه الخاص.
أو كانا خاصين فيحمل أحدهما على الحقيقة والآخر على المجاز جمعًا بين الدليلين.
وإلا، أي وإن لم يكن شيء منهما قابلًا للتأويل تساقطا، إذ رد أحدهما وقبول الآخر ترجيح بلا مرجح وأنه باطل.
هذا كله إذا كانا ظنيين.','5','148',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1439,'أما إذا كانا قطعيين فلا تعارض بينهما.
وكذا لو كان أحدهما قطعيًا، والآخر ظنيًا، بل يعمل بالقطعي لإلغاء المظنون في مقابلة القاطع.
وهنا فوائد في الأصل.','5','149',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1440,'الكتاب الرابع
في
القياس','5','151',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1441,'الكتاب الرابع
في
القياس
هو لغة: تقدير شيء بآخر، لتعلم المساواة والمفارقة بينهما.
فإطلاقه على المساواة مجاز، من باب إطلاق السبب على المسبب.','5','153',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1442,'قال صاحب الأساس: قاس به وعليه وإليه.
وقال العلامة: إنما عدي بعلى ليدل على البناء.
فإن انتقال الصلة للتضمن بعلى فإذا قلت: قست كذا على كذا أي بنيته عليه.
وهو في الاصطلاح: إثبات مثل حكم معلوم في معلوم آخر لاشتراكهما في علة الحكم عند المثبت.','5','154',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1443,'اعلم أن القياس من الأدلة الشرعية.
فلا بد من حكم مطلوب به وله محل ضرورة، والمقصود إثباته فيه، لثبوته في محل آخر يقاس هذا به، فكان هذا فرعًا وذاك أصلاً لحاجته إليه وابتنائه عليه، ولا يمكن ذلك في كل شيئين، بل إذا كان بينهما أمر مشترك ولا كل مشترك، بل مشترك يوجب الاشتراك في الحكم بأن يستلزم الحكم ونسميه علة الحكم.
فلا بد أن يعلم علة الحكم في الأصل، ويعلم ثبوت مثلها في الفرع، إذ ثبوت عينها مما لا يتصور؛ لأن المعنى الشخصي لا يقوم','5','155',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1444,'بعينه بمحلين، وبذلك يحصل ظن مثل الحكم في الفرع وهو المطلوب.
وقيل: بمثل لأن ثبوت عين حكم الأصل مما لا يتصور كما مر في العلة.
واقتصر المصنف على الثاني دون الأول لأنه يعلم منه.
فبهذه المقدمة تحقق اشتمال القياس على أركانه الأربعة، وهي الأصل والفرع، وحكم الأصل، وعلة حكم الأصل.
وقول المصنف: إثبات جنس والمراد به: هو القدر المشترك بين','5','156',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1445,'العلم والاعتقاد والظن.
وقوله: مثل احترز به عن إثبات خلاف حكم معلوم فإنه لا يكون قياسًا. وأشار به إلى أن الحكم الثابت في الفرع ليس هو عين الثابت في الأصل كما تقدم، والمثل تصوره بديهي.
وقوله: حكم غير منون.','5','157',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1446,'وأشار به إلى أحد الأركان الأربعة، وهو حكم الأصل.
والمراد به: نسبة أمر إلى آخر إيجابًا أو سلبًا، فشمل الحكم الشرعي والعقلي واللغوي.
وقوله: "معلوم"، أشار به إلى ركن آخر وهو الأصل.
وقوله: "في معلوم آخر"، أشار به إلى ركن آخر وهو الفرع.
والمراد بالمعلوم: المتصور، فدخل فيه اليقين والاعتقاد والظن، فإن العلم قد يستعمله الفقهاء بمعنى الأعم.
وقال معلوم: لأن القياس يجري في الموجود والمعدوم ممكنًا كان أو','5','158',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1447,'ممتنعًا.
وعبر عن الأصل والفرع بالمعلوم، لئلا يرد السؤال بأنه دور وإن كان جوابًا واضحًا.
وقوله: لاشتراكهما في علة الحكم، أشار به إلى الركن الرابع وهو العلة.
وأخذ العلة في تعريف القياس لا يلزم منه الدور؛ لأن العلة لا يتوقف فهمها على فهم القياس، لأنها تكون في القياس وغيره.
وقوله: "مثل"، مشعر بأنهما استويا في العلة.
وقوله: "عند المثبت"، أي القائس، ليشمل القياس الصحيح','5','159',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1448,'والفاسد.
ولم يعبر: بالمجتهد بل المثبت ليتناول المجتهد والمقلد، كما يقع في المناظرات.
قال الآمدي: وهذا الحد يرد عليه إشكال مشكل لا محيص عنه، وهو أن إثبات الحكم نتيجة القياس، فجعله ركنًا في الحد يقتضي توقف القياس على حكم الفرع المتوقف عليه، فيلزم الدور.
وأجيب بأنه: إنما يقتضي توقف معرفة القياس وتعقل ماهيته، على','5','160',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1449,'معرفة حكم الفرع، وتعقل ماهيته، وهو لا يتوقف على تعقل ماهية القياس لا تعقله ولا حصوله.
بل غاية الأمر أن حصوله يتوقف على حصول القياس، ومثله ليس من الدور في شيء.
وأورد أنه: يخرج عنه قياس الشبه: لأنه ليس فيه علة معينة، لا سيما الشبه الصوري عند من يعتبره، وقياس لا فارق إذ ليس فيه علة عند المجتهد.
وهذا الحد الذي ذكره المصنف أصله للقاضي أبي بكر الباقلاني.
واختاره الإمام الرازي وأتباعه.
واختار الآمدي وابن الحاجب: أنه مساواة فرع لأصل في علة حكمه.','5','161',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1450,'قال الشيخ سعد الدين: القياس وإن كان من أدلة الأحكام مثل الكتاب والسنة، لكن جميع تعاريفه واستعمالاته منبئ عن كونه فعل المجتهد.
فتعريفه بنفس المساواة محل نظر، ولهذا يعبر عنه المحقق بما حصلت فيه المساواة. فعلم من ذلك أن ما ذكره المصنف هو الشائع.
قيل: هذا الحد غير جامع لخروج قياس العكس والتلازم الاقتراني منه.
أما خروج قياس العكس)؛ فلأن المصنف اشترط في القياس تماثل','5','162',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1451,'الحكمين، والحكمان غير متماثلين فيه، كما في قولنا: لو لم يشترط الصوم في صحة الاعتكاف عند الإطلاق، لما وجب الصوم بالنذر كالصلاة، فإنها لما لم تكن شرطًا في صحة الاعتكاف حالة الإطلاق لم تصر شرطًا بالنذر.
فالمطلوب في الفرع، إثبات كون الصوم شرطًا في صحة الاعتكاف. والثابت في الأصل، نفي كون الصلاة شرطًا له.
فالأصل، الصلاة، والفرع: الصوم، والحكم في الأصل: عدم كونها شرطًا في صحة الاعتكاف، والعلة فيه: كونها غير واجبة بالنذر، والحكم الثابت في الفرع: كون الصوم شرطًا في صحة الاعتكاف، والعلة فيه: وجوبه بالنذر، فافترقا حكمًا وعلة. فهو قياس مع عدم صدق الحد عليه.
قلنا: ما سميتموه قياس العكس، هو قياس تلازم، والقياس','5','163',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1452,'لبيان الملازمة والتماثل حاصل على التقدير.
وحاصله: لو لم يشترط، لم يجب بالنذر، واللازم منتف، ودعوى الملازمة لا بد من بيانها بالدليل، فبينها المستدل بأن ما لا يكون شرطًا لشيء، لا يصير شرطًا له بالنذر، قياسًا على الصلاة، فإنها لما لم تكن شرطًا للاعتكاف لم تجب بالنذر.
ولا شك أن على تقدير عدم وجوبه بالنذر، فالمساواة حاصلة بينها وبين الصوم، وإن لم تكن حاصلة في نفس الأمر.
فالخصم: إن اعتمد في إيراد قياس العكس على القياس الذي لبيان الملازمة، فهو غير وارد، لأن الأصل والفرع فيه متماثلان لكن','5','164',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1453,'التماثل حاصل على التقدير كما تقدم.
فإن قوله، إثبات مثل حكم معلوم في معلوم آخر، أعم من أن يكون حقيقة أو تقديرًا، وإن اعتمد في الإيراد على التلازم، فنحن نسلم أنه خارج عن حد القياس، لكن لا يضيرنا ذلك، فإنه ليس بقياس عندنا، لأن أصول الفقه إنما يتكلم فيها على القياس المستعمل في الفقه.
والفقهاء إنما يستعملون قياس العلة.
وأما ما عداه مثل قياس التلازم أي الاستثنائي، والقياس الاقتراني، فلا يضرنا خروجه عن الحد؛ لأنا لا نسميهما قياسًا في هذا الاصطلاح.
وإنما يطلق عليهما لفظ القياس: المناطقة، إذ القياس في عرفهم: قول مؤل من أقوال متى سلمت لزم عنها لذاتها قول آخر.
وما يسميه الأصوليون قياسًا يسميه المناطقة تمثيلاً.','5','165',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1454,'ومثال التلازم: لو كان الوضوء عبادة لوجب فيه النية، لكنه عبادة فيجب فيه النية.
ومثال الاقتراني: الوضوء عبادة وكل عبادة لا بد فيهما من النية فالضوي لا بد فيه من النية.
وفيه أي في كتاب القياس بابان؛ لأن الكلام إما أن يكون في بيان حجيته، أو في بيان أركانه فأفرد لكل منهما بابًا.
وقدم بيان حجيته على أركانه، وإن كان الأركان مقدمًا بالذات لشدة الاهتمام بحجيته.','5','166',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1455,'<span data-type="title" id=toc-226>الباب الأول: في بيان أنه -أي القياس- حجة</span>
وفيه مسائل:
الأولى
في الدليل عليه
يجب العمل به، أي بالقياس المذكور في الأمور الشرعية شرعًا.','5','167',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1456,'وقال القفال الشاشي وأبو الحسين البصري: (يجب العمل به عقلاً وشرعًا وقال القاساني والنهرواني يجب العمل به، حيث','5','168',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1457,'كانت العلة منصوصة بصريح اللفظ أو إيمائه أو كان الفرع بالحكم أولى من الأصل كتحريم الضرب على تحريم التأفيف.
قلا: وليس للعقل هنا مدخل لا في الوجوب ولا في عدمه.
وداود الظاهري أنكر التعبد به، أي لم يرد في الشرع ما يدل على العمل بالقياس وإن جاز عقلاً.
ونقل ابن حزم والآمدي عن داود منع غير الجلي ولو كان مساويًا.','5','169',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1458,'أما الجلي. وهو. ما كان الملحق أولى بالحكم من الملحق به فغير ممتنع.','5','170',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1459,'ونقل الإمام الرازي عنه إحالته عقلاً.
وقال بعضهم: داود إن قال بالجلي لا يسميه قياسًا.
وأحاله الشيعة أي الإمامية منهم، والنظام.
أما الزيدية من الشيعة: فإنهم قائلون بأنه حجة.
وفي المحصول وغيره: أن النظام قال بإحالته في شرعنا فقط.','5','171',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1460,'وسيجئ للمصنف أن القياس الجلي لم ينكره أحد.
وأن النظام يقول: إن التنصيص على العلة أمر بالقياس فيكون كمذهب القاساني.
تنبيه: قال ابن عساكر: بلغني أن القفال كان قائلاً بالاعتزال ثم رجع إلى مذهب الأشعري.
فالنقل عنه بأنه يوجب القياس عقلاً ونحوه مما لا يأتي إلا على','5','172',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1461,'قواعد المعتزلة.
قال الزركشي: فحين رجع لا بد وأن يكون رجع عنه ويسقط ذكره عنه.
والقاساني -قيده الذهبي بإهمال السين، وغيره بها وبالمعجمة وهو من أصحاب داود.
وقال ابن باطيش: وانتقل إلى مذهب الشافعي (رضي الله تعالى عنه) والنهرواني قال الزركشي: الظاهر أنه محرف وأصله الياء لا الواو.
ونهرين من قرى بغداد.','5','173',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1462,'واستدل أصحابنا على أن العمل بالقياس واجب شرعًا بوجوه أربعة، لانحصار الأدلة هنا فيها:
الأول: أنه أي القياس مجاوزة بالحكم عن الأصل إلى الفرع.
والمجاوزة: عن شيء إلى غيره اعتبار؛ لأنه مشتق من العبور، والعبور حقيقة في المجاوزة بالنقل والاستعمال، فالقياس اعتبار، وهو أي الاعتبار مأمور، في قوله تعالى: {فاعتبروا يا أولى الأبصار}.
فالقياس مأمور به، والأمر للوجوب، فالاعتبار واجب (فالقياس واجب) والمعني بوجوبه وجوب العمل به وهو المطلوب.
قيل عليه: المراد بالاعتبار هنا: الاتعاظ، لا القياس، فإن','5','174',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1463,'القياس الشرعي لا يناسب صدر الآية، لأنه يصير معنى الآية: يخربون بيوتهم بأيديهم وأيدي المؤمنين فقيسوا الذرة على البر.
وهو في غاية الركاكة، فيصان كلام الله -تعالى- عنه.
قلنا: المراد من الاعتبار في الآية: هو القدر المشترك بين القياس والاتعاظ وهو نفس المجاوزةن والاتعاظ مجاوزة من حال الغير إلى حال نفسه.
وكون صدر الآية غير مناسب للقياس بخصوصه لا يسلتزم عدم مناسبته للقدر المشترك بينه وبين الاتعاظ.
قيل: لا يلزم من الأمر بالاعتبار الكلي، الذي هو القدر المشترك،','5','175',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1464,'الأمر بالقياس الذي هو جزئي منه؛ لأن الدال على الكلي، لا يدل على الجزئي بخصوصه، إذ الأعم لا يدل على الأخص.
قلنا: بلى، سلمنا: أن الدال على الكلي من حيث هو دال عليه، لا يدل على الجزئي.
ولكن لم لا يجوز أن يدل عليه بقرينة تلحقه وها هنا كذلك، فإن الاعتبار الدال على المجاوزة دال على جميع الجزئيات، بقرينة لحوق العموم به، وهو جواز الاستثناء فإنه دليل العموم، إذ لو قال: اعتبروا إلا الاعتبار الفلاني لا يخطأ لغة.
وفيه نظر: لأنه إنما يصح الاستثناء في مفعول الاعتبار، لا','5','176',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1465,'في مفهوم الاعتبار.
لأن الفعل في سياق الإثبات لا عموم له.
والنزاع إنما وقع في مفهوم الاعتبار لا في مفعوله.
وأيضًا: لو صح لاطرد في سائر الكليات، ولا يوجد كلي إلا وهو يدل على جميع الجزيئات وهذا باطل.
بل الجواب: أن الأمر بالماهية الكلية يقتضي التخيير بين الجزيئات عند عدم القرينة؛ لأن الكلية في ضمن جزئي من جزيئاتها، فإذا ثبت التخيير جاز العمل بالقياس، وجواز العمل به مستلزم','5','177',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1466,'لوجوبه، لأن كل من قال بالجواز قال بالوجوب كذا قيل.
قيل: وإن سلم أن الآية تدل على الأمر بالقياس لكن الدلالة ظنية، (ومسألة حجية القياس علمية)، فلا يجوز إثباتها بها؛ لأن التمسك بالعموم والاشتقاق إنما يفيد الظن.
والشارع إنما أجاز الظن في المسائل العلمية وهي الفروع بخلاف الأصول لفرط الاهتمام بها.
قلنا: المقصود من كون القياس حجة إنما هو العمل به لا مجرد الاعتقاد، كأصول الدين، فيكفي الظن وإن كانت علمية، لكونها وسيلة إلى العمل وهو المطلوب.
قال العراقي: هذا رأي أبي الحسين وطائفة، أن دلالة السمع على القياس ظنية.','5','178',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1467,'والأكثرون على أنها قطعية.
الوجه الثاني من الأدلة على وجوب العمل بالقياس:
وهو قصة معاذ وأبي موسى (رضي الله تعالى عنهما).','5','179',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1468,'أما قصة معاذ: فرواها أبو داود والترمذي.
وفيها أنه -عليه الصلاة والسلام- قال له: "فإن لم تجد في سنة رسول الله (صلى الله عليه وسلم) ولا في كتاب الله -تعالى-؟ قال: أجتهد برأيي ولا آلو، فضرب رسول الله -صلى الله عليه وسلم- صدره وقال: "الحمد لله الذي وفق رسولَ رسولِ الله لما يرضي رسول الله".
فدل ذلك على أن القياس حجة.','5','180',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1469,'قال الترمذي: وليس إسناده بمتصل).
وقال ابن حزم: لا يجوز الاحتجاج بحديث معاذ لسقوطه وضعف سنده.
وقال القاضي أبو الطيب: هو حديث صحيح.
وكذا أثبته غيره، وأجاب عن قدحه.
وفيه كلام متسع في الشرح.
وأما قصة أبي موسى: قال الزركشي: فإنما ورد عن عمر.','5','181',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1470,'أخرجه الدارقطني ثم البيهقي في سننهما.
وقد جمع في المحصول بين قصة معاذ وأبي موسى، وتبعه المصنف والشراح ورفعوها إلى النبي -صلى الله عليه وسلم- وقد علمت ما فيه.','5','182',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1471,'قيل: ما ذكرتم من العمل بالقياس إنما كان جائزًا في حياة النبي -صلى الله عليه وسلم- قبل استقراء الشرع، ونزول قوله تعالى: - {اليوم أكملت لكم دينكم} لأنه لم تكن النصوص (إذ ذاك وافية بجميع الأحكام، وأما بعد كمال الدين ونزول الآية، فالعمل) بالقياس متعذر لوجود النص على جميع الأحكام.
قلنا: الدليل دل على كون القياس حجة مطلقًا والأصل عدم التخصيص بوقت دون وقت.
والمراد من الإكمال في الآية إنما هو إكمال الأصول أي أصول الدين والكليات لا بيان جميع الفروع مفصلة لعدم النص على جميع','5','183',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1472,'الفروع؛ فيكون القياس حجة في زماننا لإثبات تلك الفروع.
الوجه الثالث من الأدلة:
أن أبا بكر -رضي الله تعالى عنه- قال في الكلالة: "أقول برأيي: الكلالة: ما عدا الوالد والولد".
رواه البيهقي في سننه.
والرأي: هو القياس إجماعًا.','5','184',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1473,'وعمر -رضي الله تعالى عنه أمر أبا موسى في عهده بالقياس.
رواه الدارقطني والبيهقي كما تقدم.
وقال عمر -رضي الله عنه- في الجد: "أقضي برأيي".
وقال له عثمان: "إن اتبعت رأيك فسديد وإن تتبع رأي الشيخ قبلك فنعم الرأي كان".
رواه بمعناه البيهقي وابن أبي خيثمة.
وقال علي -رضي الله تعالى عنه-: "اجتمع رأيي ورأي عمر-','5','185',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1474,'رضي الله تعالى عنه- في (أمهات الأولاد) أن أعتقهن" إلى آخره.
رواه سعيد بن منصور في سننه، عن عبيدة السلماني.
ورواه ابن أبي خيثمة من طرق.
وقاس ابن عباس -رضي الله تعالى عنهما- الجد على ابن الابن في الحجب للإخوة.','5','186',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1475,'رواه سعيد بن منصور في سننه.
وذكره البخاري تعليقًا مجزومًا به.
فثبت صدور القياس بما ذكر وبغيره من الوقائع الكثيرة، المشهورة الصادرة عن أكابر الصحابة وغيرهم، التي لا ينكرها إلا معاند، وهي وإن كانت آحادًا فبينها قدر مشترك، وهو العمل بالقياس، وذلك متواتر، ولم ينكر أحد ذلك عليهم، وإلا أي لو أنكر لاشتهر إنكاره أيضًا.
ونقل إلينا، لأنهم نقلوا الاختلاف في الفروع، فالاختلاف في الأصول العظيمة أولى بالنقل.
وحيث لم ينقل إلينا، علم أنه لا يوجد وسكوت الباقين من الصحابة الذين كانوا ينقادون للحق، ولا يمنعهم عن الإنكار رغبة ولا رهبة','5','187',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1476,'مع تكرره وإشاعته وعظمته دليل قطعي على أنهم راضون به، والعلم القطعي حاصل بأن العمل بها كان لظهورها (لا لخصوصيًا) تها كسائر الظواهر، فكان إجماعًا على العمل بالقياس فيكون حجة.
قيل: لا نسلم عدم الإنكار، (فقد نقل الإنكار) عمن ذكرتم وعن غيرهم، فإنهم ذموه أيضًا، فقد روى عن بن حميد أن أبا بكر (رضي الله عنه) قال: "أي أرضي تقلني وأي أسماء تظلني إن قلت في آية من كتاب الله تعالى بغير ما أراد".
وروى أبو داود عن علي -رضي الله عنه-: أنه قال: "لو كان الدين بالرأي لكان أسفل الخف ألى بالمسح من أعلاه".','5','188',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1477,'وروى الطبراني في معجمه الكبير عن ابن مسعود (رضي الله عنه): "لا تقيسوا أشياء".
وروى مجالد عن عمر -رضي الله عنه-: "إياكم وأصحاب الرأي فإنهم أعداء السنن" الحديث.
قلنا: إنما ذموا وأنكروا حيث فقد شرطه أي شرط القياس، فإن عملهم بالقياس في الصور الغير المحصورة قطعي، فيحمل على القياس الصحيح، والذم على الفاسد توفيقًا بين النقلين وجمعًا بين الأدلة.','5','189',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1478,'وهنا أسئلة مع أجوبتها في الشرح.
هذا: وقول أبي بكر -رضي الله تعالى عنه- في تفسير القرآن بالرأي. الذي هو منهي عنه، إذ لا مجال للعقل فيه بخلاف الفروع.
وم عمر -رضي الله تعالى عنه- حيث ترك الأحاديث وعمل بالقياس ولا شك أن العمل به مشروط بعدم النصوص.
الوجه الرابع من الأدلة على وجوب العمل بالقياس:
إن ظن تعليل الحكم في الأصل بعلة توجد في الفرع.','5','190',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1479,'فوجب ظن الحكم في الفرع ضرورة، لوجود ما ظن عليته فيه.
وحينئذ يلزم الجزم بثبوت الحكم في الفرع؛ لأن حكم الفرع إذا كان مظنونًا، كان نقيضه هو عدم الحكم موهومًا، والنقيضان لا يمكن العمل بهما، وإلا اجتمعا، ولا يمكن الترك لهما، وإلا ارتفعا، والعمل بالمرجوح وهو العدم الموهوم، مع وجود الراجح وهو الظن، بوجوده فيه ممتنع عقلاً وشرعًا، فتعين العمل بالراجح، وهو الظن بوجود الحكم فيه، ولا معنى لوجوب العمل بالقياس إلا هذا، وفيه نظر مبين في تعريف الفقه.
وإنما لم يعمل القاضي بقول شاهد واحد في غير الزنا وشاهدين','5','191',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1480,'فيه إذا غلب على ظنه الصدق؛ لأن العمل بالظن إنما يجب فيما لا يقتضي القاطع خلافه.
أما ما يقتضي القاطع خلافه فلا، بل لا يجوز.
والقائلون بعدم حجية القياس في الشرع: احتجوا بوجوه ستة:
الأل: قوله تعالى: {يا آيها الذين آمنوا لا تقدموا بين يدي الله ورسوله} فإنه يدل على أن العمل بالقياس منهي عنه، لكونه تقديمًا بين يدي الله ورسوله، لكونه قولاً بغير الكتاب والسنة.','5','192',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1481,'وكذا قوله تعالى: {وأن تقولوا على الله ما لا تعلمون}.
لأن العمل به قول بالظن الذي هو غير العلم لكونه متوقفًا على أمور لا يقطع بوجودها فيكون قولاً بما لا يعلم.
وكذا قوله تعالى: {ولا تقف ما ليس لك به علم}.
وقوله تعالى: {ولا رطب ولا يابس إلا في كتاب مبين}.
دل على اشتمال الكتاب على الأحكام كلها، فالحكم الثابت بالقياس، إن دل عليه الكتاب، لم يجز إثباته بالقياس، لأن شرطه فقد النص، وإن لم يدل عليه الكتاب لم يكن حكمًا شرعيًا فلا يكون القياس الدال عليه حجة.
وقوله تعالى: {إن الظن لا يغني من الحق شيئًا} دل على أن','5','193',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1482,'مقتضى القياس ليس بحق؛ لأن القياس ظن فلا يغني شيئًا.
قلنا: الجواب عن الآية الأولى: إنه لما أمر الله -تعالى- ورسوله بالقياس لم يكن القول به تقديمًا بين يدي الله ورسوله.
ولما كان هذا معلومًا من استدلال المصنف أولاً اكتفى به.
والجواب عن الآية الرابعة: أنا لا نسلم أن المراد به القرآن، بل اللوح المحفوظ.
وإن سلم، فإنه يستحيل أن يكون المراد منها اشتمال الكتاب على جميع الأحكام الشرعية من غير واسطة.
فإنه خلاف الواقع، بل المراد دلالتها عليها من حيث الجملة بوسط أو بغيره وحينئذ فلا يلزم من ذلك عدم الاحتياج إليه؛ لأن الكتاب','5','194',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1483,'على هذا التقدير لا يدل على بعضها إلا بواسطة إلينا، فيكون محتاجًا إليه.
وهذا الجواب يعلم من قول المصنف أولاً جوابًا عن شبهة.
قلنا: المراد الأصول لعدم النص على جميع الفروع، فلم يذكره هنا اكتفاء بما تقدم.
وأما الجواب عن بقية الآيات: فهو قوله: الحكم مقطوع به لما تقدم من الأدلة، والظن وقع في طريقه الموصل إليه.
وتقدم مثله في حد الفقه.
وإذا كان القياس مقطوعًا به، فلا يكون تقديمه بين يدي الله ورسوله.
الوجه الثاني لهم:','5','195',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1484,'قوله عليه الصلاة والسلام: "تعمل هذه الأمة برهة بالكتاب، وبرهة بالسنة، برهة بالقياس، فإذا فعلوا ذلك فقد ضلوا".
فإنه يدل على أن العمل بالقياس ضلال فلا يجوز.
والحديث رواه ابن حزم في رسالته الكبرى عن أبي هريرة -رضي الله عنه- ولا يقوم به حجة؛ لأن في بعض رواته من كذبه ابن معين.','5','196',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1485,'وترك أبو حاتم حديثه.
وقال أبو زرعة: ليس هو عندي ممن يكذب، وإنما كان يوضع له الحديث فيحدث به.
الوجه الثالث لهم:
ذم بعض الصحابة له أي القياس، كما مر من غير نكير من الباقين، فكان إجماعًا على منعه.
قلنا: الحديث والإجماع على تقدير صحتهما معارضان بمثلهما.','5','197',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1486,'أما الحديث فبحديث معاذ -رضي الله عنه.
وأما الإجماع فبالإجماع على جواز العمل به، فيجب التوفيق بينهما، بأن يحمل الأمر به والعمل، على القياس الصحيح، والنهي وذمه على القياس الفاسد، كما مر.
الوجه الرابع لهم:
نقل الإمامية عن الشيعة إنكاره، أي إنكار العمل بالقياس عن العترة، أي أهل البيت، وإجماعهم حجة.
قلنا: هذا النقل معارض بنقل الزيدية عن الشيعة عن العترة أنهم أجمعوا على العمل بالقياس، هذا وقد تقدم أن إجماع العترة ليس بحجة فالمصنف تنزل).','5','198',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1487,'الوجه الخامس لهم:
أنه أي العمل بالقياس يؤدي إلى الخلاف والمنازعة بين المجتهدين للاستقراء، ولأنه تابع للأمارات، وهي مختلفة، فكيف يجوز العمل به، وقد قال تعالى: {ولا تنازعوا فتفشلوا} فوجب أن يكون ممنوعًا.
قلنا: الآية إنما وردت في الآراء والحروب لقرينة قوله تعالى: {فتفشلوا وتذهب ريحكم}.
وأما التنازع في الأحكام فجائز لقوله -عليه الصلاة السلام- "اختلاف أمتي رحمة". قال الخطابي والبيهقي: روي عن النبي -صلى الله عليه وسلم- (وهو يدل على أن له أصلاً).','5','199',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1488,'قال الشيخ زين الدين العراقي: وأسنده في المدخل من حديث ابن عباس -رضي الله عنهما- بلفظ: "اختلاف أصحابي لكم رحمة".
وإسناده ضعيف.
وأجاب في المحصول: بأن هذا الدليل بعينه قائم في الأدلة العقلية، فجوابهم جوابنا.
الوجه السادس وعليه اعتمد النظام:
أن الشارع فضل بين الأزمنة والأمكنة في الشرف، وبين الصلاة في القصر.','5','200',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1489,'ففضل ليلة القدر، والأشهر الحرم على غيرها.
وفضل مكة والمدينة (وبيت المقدس).
وقصره في الرباعية من الصلوات دون غيرها مع استواء الزمان والمكان والصلوات متماثلات في الحقيقة، فلم يجمع بين المتماثلات بل فرق بينها.
وجمع بين الماء والتراب في التطهير وهما مختلفان؛ إذ الماء منظف والتراب مشوه فلم يفرق بينهما.
وأوجب التعفف، أي غض البصر على الحرة الشوهاء.','5','201',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1490,'شعرها وبشرتها مع أن الطبع لا يميل إليها، دون الأمة الحسناء التي يميل الطبع إليها، مع أن علة التعفف موجودة في صورة الأمة الحسناء.
تنبيه: في أكثر النسخ: وأوجب التعفف على الحرة الشوهاء، (وشرحه العبري هكذ ا: وأوجب التعفف على الحرة الشوهاء) ولم يوجبه على الأمة الحسناء.
وهو مطابق لما في أكثر النسخ، وفيه نظر من جهة المعنى.
وفي بعض النسخ: "عن" بدل "على".
وشرحه الأصفهاني: على ما شرحته أولاًا بناء على ما في بعض النسخ التي فيها "عن"، وعلى النسختين ينبغي أن يشرح كما شرحته أولاً.
أما إذا كانت النسخة "عن" فواضح وأما على جعلها "على" فكذلك.','5','202',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1491,'فإن "على" تأتي بمعنى "عن" وكذلك قطع سارق القليل، دون غاصب الكثير، مع أن تلك العلة موجودة في الغصب، فلم يعتبر استلزام العلة الحكم، وجلد بقذف الزنا، وشرط فيه أي: في إثباته شهادة أربعة، دون الكفر الذي هو أغلظ منه؛ إذ لا يجلد بالقذف به ولا يشترط في إثباته شهادة أربع، فلم يعتبر أولوية القياس.','5','203',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1492,'وذلك أي: جميع ما تقدم من هذه المعاني ينافي القياس.
إذ مداره على إبداء المعنى، وعلى إلحاق صورة بأخرى تماثلها في ذلك المعنى، وعلى التفريق بين المتماثلات، والجمع بين المختلفات لا يأتي ذلك.
قلنا: القياس إنما يسوغ الشرع حيث عرف المعنى، أي: العلة الجامعة مع انتفاء المعارض، وغالب الأحكام من هذا القبيل.
وما ذكر من الصور، نادر لا يقدح في حصول الظن الغالب.','5','204',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1493,'هذا، وشيء من ذلك غير معلوم فيما ذكر من الصور (لجواز عدم صلاحية) ما توهتمتوه في المتماثلات جامعًا لكونه جامعًا، أو وجود المعارض له، إما في الأصل أو في الفرع، وإما في الجمع بين المختلفات، فلجواز اشتراك المختلفات، في معنى جامع، هو العلة للحكم في الكل، فإن المختلفات لا يمتنع اشتراكهما في صفات ثبوتية وأحكام.
وأيضًا: فيجوز اختصاص كل بعلة تقتضي حكم المخالف الآخر، فإن العلل المختلفة لا يمتنع أن توجب في المحال المختلفة حكمًا واحدًا.
المسألة الثانية
قال النظام، وأبو الحسين البصري، وبعض الفقهاء، كالإمام أحمد -رضي الله عنه- والشيخ أبو إسحاق، وأبو بكر الرازي: إن التنصيص من الشارع على العلة في الحكم أمر بالقياس مطلقًا، سواء كانت تلك العلة علة للفعل، كأكرم زيدًا لعلمه، أو علة للترك كحرمة الخمر لإسكارها،','5','205',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1494,'فيتعدى الحكم إلى غير محل النص، وإن لم يرد ما يوجب التعبد بالقياس؛ لأنه لا فائدة لذكر العلة إلا ذلك.
وقال الجمهور: ومنهم الإمام الرازي، والآمدي لا يكون أمرًا مطلقًا بل لا بد من دليل يدل عليه.
ونقله الآمدي عن أكثر الشافعية وهو المختار.
واكتفى المصنف بالدليل عليه؛ لأنه مشعر باختياره.
وفي المستصفى عن النظام: أن التنصيص على العلة يقتضي تعميم الحكم في جميع مواردها بطريق عموم اللفظ لا بالقياس.
وفرق أبو عبد الله البصري المعتزلي، بين الفعل والترك، فجعله أمرًا','5','206',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1495,'به في جانب الترك دون الفعل.
لنا: على أن التنصيص على العلة ليس أمرًا بالقياس.
أن الشارع إذا قال مثلا: حرمت الخمر لكونها مسكرة.
يحتمل أن تكون عليه الحرمة هو الإسكار مطلقًا، حيث يثبت التحريم في كل ما يسكر، ويكون أمرًا بالقياس كما ذهبتم إليه.
ويحتمل أن تكون علية التحريم هو إسكارها أي: إسكار الخمر بحيث يكون قيد الإضافة إلى الخمر معتبرًا في العلة، فلا يثبت به التحريم في (كل ما) يسكر، لجواز اختصاص إسكارها بترتب مفسدة عليه، دون إسكار النبيذ.','5','207',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1496,'وإذا احتمل الأمران فلا يتعدى التحريم إلى غيرها إلا عند ورود الأمر بالقياس، وإذا ثبت ذلك في جانب الترك ثبت في جانب الفعل قطعًا.
فإن قيل: الاحتمال الثاني ساقط بحسب العرف، إذ الأغلب على الظن عرفًا عدم التقييد بالمجمل الذي نص على عليته، وإذا ثبت سقوط التقييد في العرف ثبت سقوطه في الشرع.
لما روي عنه -عليه الصلاة والسلام-: "ما رأه المسلمون حسنًا فهو عند الله حسن".
وهذا الحديث لا يعرف إلا موقوفًا.
وإذا كان ساقطًا شرعًا كانت العلة هي الإسكار مطلقًا، فكان أمرًا','5','208',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1497,'بالقياس، ويحتمل أن يريد أن الأغلب في العلل تعديتها دون تقييدها بمحل الحكم بالاستقراء.
قلنا: النزاع في أن التنصيص على العلة هل يستقل بإفادة وجوب القياس وما ذكرتم يقتضي أنه لا بد أن يضم إليه أن الغالب عدم تقييد العلة بالمحل فالتنصيص وحده لا يفيد.
أو يقول: مجرد التنصيص على العلة لا يلزم منه الأمر بالقياس، ما','5','209',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1498,'لم يدل دليل على وجوب إلحاق الفرع بالأصل.
فإن قيل: الاحتمال الذي ذكرتموه، وهو كون العلة إسكار الخمر، مخصوص بالمثال المذكور، ولا يأتي دليلكم في غيره، مثلاً، لو قال الشارع: علة الحرمة في الخمر الإسكار، لا يدفع الاحتمال الذي فيه التقييد وتثبت الحرمة في كل صورة.
قلنا: فيثبت الحكم هنا في كل الصور بالنص لا بالقياس، إذ القياس يقتضي ثبوت الحكم في الفرع بغير النص المثبت لحكم الأصل.
وإذا امتنع القياس امتنع الأمر به.','5','210',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1499,'قال أبو عبد الله البصري: من ترك أكل شيء لأذاه، دل على تركه كل مؤذ بخلاف من تصدق على فقير لفقره، أو لمثوبة.
فإنه لا يدل على تصدقه على كل فقير وتحصيل كل مثوبة.
وجوابه: أن فهم التعميم في الأول لقرينة التأذي، وكون ترك المؤذي مطلقًا مركوزًا في الطباع، وخصوصية ذلك المؤذي ملغاة عقلاً، بخلاف الأحكام، فإنها قد تختص بمحالها بأمور لا تدرك.
المسألة الثالثة
القياس إما قطعي، أو ظني، فيكون الفرع بالحكم أولى، كتحريم','5','211',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1500,'الضرب على تحريم التأفيف، أو مساويًا كقياس الأمة على العبد في السراية، أو أدون كقياس البطيخ على البر في الربا.
اعلم أن الكلام هنا في مقامين:
أحدهما: القياس، والثاني: الحكم الذي في الأصل.
فالقياس الذي هو الإلحاق قسمان: قطعي، وظني.
والقطعي: يتوقف على العلم بعلة الحكم، وحصول مثل تلك العلة في الفرع.
فإذا علمها المجتهد، علم ثبوت الحكم في الفرع، سواء كان ذلك الحكم مقطوعًا به، أو مظنونًا.
ومثل له الإمام الرازي بقياس تحريم الضرب على تحريم التأفيف.','5','212',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1501,'فإنا نعلم أن العلة هي الأذى، ويعلم وجودها في الضرب، ولكن الحكم هنا ظني؛ إذ دلالة اللفظ -عند الإمام- لا تفيد إلا الظن.
فعلى هذا القياس قطعي والحكم ظني.
وحاصله: أنا قطعنا بإلحاق هذا الفرع لذلك الأصل في حكمه المظنون.
والقياس الظني: هو أن يكون إحدى مقدمتيه أو كلتيهما مظنونة، كقياس السفرجل على البر في الربا.
فإن الحكم بأن العلة هي الطعم ليس مقطوعًا به لجواز كونها الكيل أو القوت وإلى هذا كله أشار المصنف بقوله: "القياس: إما قطعي أو','5','213',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1502,'ظني".
أما الحكم الذي في الأصل وهو الثاني، فإن كان قطعيًا، فيستحيل أن يكون الحكم في الفرع أولى منه، كما في المحصول.
وإن لم يكن قطعيًا سواء كان القياس قطعيًا أم لم يكن، فثبوت الحكم قد يكون أولى من ثبوته في الأصل، أو مساويًا، أو دونه.
فالأول: كقياس تحريم الضرب، على تحريم التأفيف؛ لأن الأذى فيه أكثر.
والثاني: كقياس الأمة على العبد في سراية العتق من','5','214',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1503,'البعض إلى الكل، فهما متساويان في هذا الحكم لتساويهما في علته، وهو تشوف الشارع إلى العتق.
وهذان القياسان يسميان بالقياس في معنى الأصل، والقياس الجلي وهو: ما يقطع فيه بنفي تأثير الفارق بين الأصل والفرع.
الثالث: وهو الأدون -فهي الأقيسة التي يستعملها الفقهاء في مباحثهم، كقياس البطيخ على البر في باب الربا، بجامع الطعم، فإنه العلة -عندنا- في الأصل، ويحتمل ما قيل: إنها القوت أو الكيل، وليس في البطيخ إلا الطعم فثبوت الحكم فيه أدون من','5','215',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1504,'ثبوته في البر المشتمل على الأوصاف الثلاثة.
فأدونيه القياس من حيث الحم لا من حيث العلة.
إذ لا بد من تمامها فظهر بذلك أنه ليس المراد بأدون أن لا توجد فيه العلة بتمامها، بل أن تكون العلة في الأصل ظنية كما مر.
وحينئذ فلا اعتراض على المصنف في تقسيمه الفرع إلى أولى ومساو وأدون.','5','216',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1505,'ولا منافاة بينه وبين قول المحصول: يشترط أن لا يكون بين العلتين تفاوت.
تنبيه: جعل المصنف تحريم الضرب، وغيره من أمثلة فحوى الخطاب من القياس يوهم منافاته لما تقدم له أن اللفظ دل بالالتزام وقد مر في المفاهيم تحقيقه وجعله قياسًا.
نقله في البرهان عن معظم الأصوليين، ونص عليه الشافعي -رضي الله تعالى عنه- في الرسالة.
وقال الصفي الهندي: لا منافاة بينهما، فقد يكون الحكم ثابتًا بالمفهوم وبالقياس معًا.
ووجهه بعضهم: بأن المفهوم مسكوت عنه، والقياس إلحاق مسكوت عنه بمنطوق ولكن الأول هوا لمعتمد قاله العراقي فليتأمل.','5','217',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1506,'قيل: إن تحريم التأفيف يدل على تحريم أنواع الأذى التي منها الضرب عرفًا لأن المنع من التأفيف منقول بالعرف عن موضوعه اللغوي إلى المنع من أنواع الأذى، وإذا كان كذلك، كان تحريم الضرب مستفادًا من النص عرفًا لا من القياس.
ويكذبه، أي: يدفعه قول الملك للجلاد، إذا استولى على عدوه: خذ هذا الرجل اقتله ولا تستخف به، فإنه نفى الاستخفاف وأمر بالقتل.
ولو ثبت نقل تحريم التأفيف بالعرف إلى تحريم الضرب، لما حسن من الملك ذلك، لكنه حسن، فبطل النقل.
فعلم أن تحريم الضرب مستفاد من القياس لا من النص.
وفيه نظر فإن الكلام في التأفيف لا في الاستخفاف.
ولأن النهي عن الاستخفاف والتأفيف دلالته على تحريم القتل ظاهرة.','5','218',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1507,'فغاية ذلك أنه صرح بمخالفة الظاهر، والتصريح بخلاف الظاهر جائز، فالأولى أن يجاب بمنع النقل.
قيل: تحريم الضرب لو ثبت قياسًا على حرمة التأفيف.
لما قال به، أي: بحرمة الضرب منكرة أي: منكر القياس.
واللازم منتف، لأن حرمة الضرب متفق عليها.
قلنا: القياس القطعي لم ينكر، لأنه لجلائه لا يمكن إنكاره.
فلذلك لم يقع الاختلاف فيه، فحرمة الضرب مستفادة من القياس الجلي الذي لم ينكره أحد، وإنما أنكروا القياس الخفي فقط.','5','219',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1508,'قيل: نفي الأدنى يدل على نفي الأعلى عرفًا، كقولهم: فلان لا يملك الحبة، فإنه يدل عرفًا على أنه لا يملك درهمًا ولا دينارًا ولا غيرهما.
وكقولهم: فلان لا يملك النقير ولا القطمير، فإنه يدل عرفًا على أنه لا يملك شيئًا من غير نظر إلى القياس.
(وإذا كان نفي الأدنى يدل على نفي الأعلى، كان تحريم التأفيف دالاً على تحريم الضرب) عرفًا.
فكان تحريم الضرب مستفادًا من الآية لا من القياس.
قلنا: أما الأول وهو قولهم: فلان لا يملك الحبة، فلأن نفي الجزء وهو الحبة يستلزم نفي الكل، فدل فيه نفي الأدنى على نفي الأعلى بخلاف التأفيف، فإنه ليس جزء من الضرب، فظهر الفرق بينهما.','5','220',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1509,'وأما الثاني: وهو قولهم: فلان لا يملك النقير ولا القطمير، فلأ، النقل فيه ضرورة، يعني أنه إنما يدل بحسب العرف على أنه لا يملك شيئًا لضرورة، وهي أن النقير هو النقرة التي في ظهر النواة، والقطمير شق النواة، كما في المحصول. أو القشرة الرقيقة التي عليها، كما في الصحاح.
ولا يمكن حملها على الحقيقة، فنقل إلى العرف (وهو عدم تملك شيء) لضرورة امتناع الحمل على الحقيقة، ولا ضرورة هنا، أي: لا ضرورة في دعوى النقل في التأفيف لجواز الحمل على حقيقته اللغوية، فظهر الفرق.
المسألة الرابعة','5','221',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1510,'القياس يجري في جميع الشرعيات، ويجوز التمسك به فيها.
حتى في الحدود، كإيجاب قطع النباش، قياسًا على السارق بجامع أخذ مال الغير خفية من حرز.
وحتى في الكفارات، كإيجابها على القاتل عمدًا، قياسًا على قتل الخطأ (بجامع القتل) بغير حق.','5','222',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1511,'وحتى في الرخص، كقياس غير الحجر، على الحجر في جواز الاستنجاء به، بجامع الجامد الطاهر القالع.
وحتى في التقديرات، كقياس نفقة الزوجة على الكفارة في تقديرها على الموسر بمدين،
كما في فدية الحج، والمعسر بمد كما في كفارة الوقاع، بجامع أن كلا منهما مال يجب في الشرع ويستقر في الذمة.
وأصل التفاوت من قوله تعالى: {لينفق ذو سعة من سعته} الآية.
وهذا كله إذا وجدت شرائط القياس فيها، وهو مذهب الشافعي -','5','223',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1512,'رضي الله تعالى عنه- كما قال الإمام الرازي.
وإنما قلنا: يجري القياس في الحدود والكفارات والرخص والتقديرات وسائر الشرعيات.
إذا وجدت شرائط القياس فيها، لعموم الدلائل الدالة على العمل بالقياس، نحو قوله تعالى: {فاعتبروا} وغيره مما تقدم، فإنها عامة، غير مختصة بنوع.
وصحح الآمدي وابن الحاجب: أن القياس لا يجري في جميع الأحكام؛ لأنه ثبت فيها ما لا يعقل معناه، كضرب الدية على','5','224',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1513,'العاقلة.
وقال المحقق: قال في المحصول: النزاع في أنه هل في الشرع جمل من الأحكام لا يجري فيها القياس، أو ينظر في كل مسألة، مسألة هل يجري فيها القياس أم لا؟
ولو كان المراد ذلك لم ينفه هذا الدليل، والظاهر أنه المراد، (فإن ما) نفاه مما ينبغي أن لا يختلف فيه اثان.
ومنع أبو حنيفة -رضي الله تعالى عنه- في الحدود والكفارات والرخص والتقديرات.
قال: لأنها لا يدرك المعنى فيها.
وأجيب: بأنه يدرك في بعضها فيجري فيه القياس.','5','225',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1514,'ويجري القياس حتى في العقليات عند أكثر المتكلمين.
إذا تحقق فيها جامع عقلي، بالعلة، أو الحد، أو الشرط، أو الدليل، عندهم.
فالجمع بالعلة أقواها.
مثاله: العالمية في المخلوق معللة بالعلم، فكذا في الخالق تعالى.
ومثال الجمع بالحد: حد العالم غائبًا بمن له العلم قياسًا على الشاهد.
ومثال الشرط: العالم في الغائب مشروط بالحياة قياسًا على الشاهد.
ومثال الدليل: إتقان الفعل وأحكامه، يدلان على إرادة الفاعل','5','226',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1515,'وعلمه في الغائب قياسًا على الشاهد.
ويجري القياس حتى في اللغات عند أكثر الأدباء، كما نقله ابن جني في الخصائص عنهم واختاره هو والمازني، والفارسي من أهل العربية، وهو اختيار القاضي أبي بكر وابن سريج وغيرهم.
قال الإمام الرازي هنا: هو الحق.
قال: وذهب أكثر أصحابنا، وأكثر الحنفية إلى المنع.
واختاره الآمدي وابن الحاجب وجزم به في المحصول في','5','227',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1516,'موضع: "وليس الخلاف فيما ثبت تعميمه بالنقل كالرجل والضارب، أو بالاستقراء كرفع الفاعل ونصب المفعول، إنما الخلاف في تسمية مسكوت عنه، أي: معنى لم يعلم بالنقل ولا بالاستقراء.
إنه من أفراد مسمى ذلك الاسم باسم إلحاقًا له، بمعنى سمي بذلك الاسم، لمعنى تدور التسمية معه وجودًا وعدمًا، فيرى أنه ملزوم التسمية، فأينما وجد (ثبتت التسمية به كتسمية النبيذ خمرًا، إلحاقًا له بالعقار لمعنى هو التخمير للعقل المشترك) بينهما الذي دار معه التسمية.
فما لم يوجد في ماء العنب لا يسمى خمرًا بل عصيرًا، وإذا وجد فيه يسمى به، وإذا زال عنه لم يسم به بل خلا إلا أن يثبت في هذا المثال','5','228',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1517,'نقل أو استقراء. فيخرج عن محل النزاع، فلا يكون المثال مطابقًا ولا يضر، فإن المثال يراد للتفهيم لا للتحقيق.
وليكن هذا على ذكرٍ منك ينفعك في مواضع.
ودليل المسألة يطلب من الشرح.
دون الأسباب، فإن القياس لا يجري فيها، وهو المشهور كما في المحصل وصححه الآمدي وابن الحاجب.','5','229',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1518,'وذهب أكثر الشافعية كما قاله الآمدي إلى الجواز.
قال: والخلاف يجري في الشروط.
قال ابن برهان: وفي المحال أيضًا، فقال: يجوز القياس في الأسباب والشروط والمحال عندنا خلافًا لأبي حنيفة.
مثاله في السبب: قياس اللواط على الزنا بجامع إيلاج فرج في فرج محرم شرعًا، مشتهى طبعًا.
ومنع المصنف هنا لا ينافي قياس اللائط على الزاني في وجوبه.
الحد؛ لأن ذلك في الحكم، وهذا في كون اللواط سببًا.
والسبب غير الحكم.
ودون العادات، يعني فلا يجري القياس في الأمور التي ترجع إلى','5','230',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1519,'العادة والخلقة كأقل الحيض أو النفاس أو الحمل وأكثره.
لأنها تختلف باختلاف الأشخاص والأزمنة والأحوال والأزمان وعدم انضباطها وعدم العلم بأسبابها، فيرجع فيها إلى قول الصادق.
وهذا الحم منقول في المحصول عن الشيخ أبي إسحاق الشيرازي، لكنه فصل في شرح اللمع بين ما لا يكون عليه أمارة كأقل الحيض وأكثره، فلا يجري فيه القياس، لأن أسبابها غير معلومة لا قطعًا ولا ظنًا.
وبين ما عليه أمارة فيجوز إثباته بالقياس، كالخلاف في الشعر هل له علة الروح أم لا؟
وذكر الماوردي والروياني: أن الصحيح جواز القياس في المقادير','5','231',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1520,'كأقل الحيض وأكثره، لأنه قد يدرك.
وجمع بعضهم بينهما: بحمل الأول على الحيض من حيث الجملة، الثاني في الأشخاص المعينة.
قال في جمع الجوامع: والصحيح أن القياس حجة إلا في الأمور العادية والخلقية وإلا في كل الأحكام، وإلا القياس على منسوخ أي: فلا يجوز فيها.','5','232',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1521,'<span data-type="title" id=toc-231>الباب الثاني: في أركانه</span>
أي: أركان القياس.
وأركان الشيء أجزاؤه في الوجود التي لا يحصل إلا بحصولها، داخلة في حقيقته بالنظر إلى الوجود العقلي محققة لهويته، بالنظر إلى الوجود الذهني.
وأركان القياس أربعة كما مر؛ لأنها المأخوذة في حقيقته حيث يفسر بأنه: إثبات مثل حكم معلوم في معلوم آخر، لاشتراكهما في العلة.
وأما في حكم الفرع فثمرة القياس، فيتأخر عنه، فلا يكون ركنًا له، فلذا لم يذكره المصنف.','5','233',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1522,'قال بعضهم: في هذا الجواب نظر؛ لأن ثمرة القياس هو إثبات حكم الأصل في الفرع، لا الحكم نفسه.
فالأولى في الجواب أن حكم الفرع هو حكم الأصل في الحقيقة وإن كان غيره باعتبار المحل.
وأشار المصنف إلى الأركان الأربعة بقوله: إذا ثبت الحكم في صورة لمشترك بينهما وبين غيرها تسمى الأول أصلاً، والثانية فرعًا والمشترك علة وجامعًا.
مثاله: النبيذ مسكر، فيحرم قياسًا على الخمر، بدليل حرمت الخمر مثلاً.
فالأصل هو الخمر؛ لأنه المحل المشبه به، الذي ثبت فيه الحكم، وهو الصورة الأولى في كلام المصنف.
ودليل الأصل قوله: حرمت الخمر مثلاً.
والنبيذ هو الفرع؛ لأنه محل الحكم المشبه، وهو الصورة الثانية.
والمشترك وهو الإسكار، ويسمى علة وجامعًا.','5','234',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1523,'ولما كان حكم الأصل معروف التسمية من قوله: إذا ثبت الحكم في صورة.
ثم قوله: يسمى الأول أصلاً، تركه لوضوحه؛ لأنا عرفنا من قوله: إذا ثبت الحكم في صورة أنه يسمى حكمًا وأنه ركن؛ لأنه أخذه من تعريفه.
وعرفنا من قوله: يسمى الأول أصلاً أن ذلك الحكم حكم الأصل.
وهذا الاصطلاح الذي ذكره المصنف هو الاصطلاح المتعارف بين الفقهاء، ونحن عليه نستمر.
وجعل المتكلمون دليل الحكم في الأصل "أصلاً" لقوله: حرمت الخمر مثلاً في هذا المثال.
وأما الفرع فعلى الاصطلاحين حكم المشبه، ولم يقل أحد: أنه دليله، وكيف يقال ودليله القياس؟
وتحقيقه: أن الأصل ما ينبني عليه غيره، فلا بعد في','5','235',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1524,'الاصطلاحين؛ لأن الحكم في الفرع ينبني على الحكم في الأصل، وهو على مأخذه ومحله، فهما مما ينبني عليه الحكم في الفرع ابتداء وبواسطة فلا بعد في التسمية.
وجعل الإمام الرازي القياس مشتملاً على أصلين وفرعين فجعل الحكم في الصورة الأولى، كتحريم الخمر أصلاً للعلة التي فيها، العلة فرعًا عنه.
وأما في الصورة الثانية: وهو النبيذ، فالأمر بالعكس.
فتكون العلة التي فيها أصلاً للحكم والحكم فرعًا عنها.
فائدة:
حيث وقع الإمام في هذا الكتاب فالمراد به الإمام الرازي، وفي مختصر ابن الحاجب: إمام الحرمين.
وبيان ذلك، أي: أركان القياس المجملة في فصلين مفصلة؛ لأن','5','236',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1525,'أركانه أربعة:
أحدها: العلة والباقي ملعول.
فرجع البحث إلى ما يتعلق بالعلة، وإلى ما يتعلق بالمعلول، فأفرد لكل منهما فصلاً.

<span data-type="title" id=toc-232>الفصل الأول: في العلة وبيان أقسامها</span>
وقدر الكلام عليها، لأنها الركن الأعظم، وهي المعرف للحكم، أي: تدل على وجوده ولا تؤثر فيه؛ لأن المؤثر هو الله تعالى.
فالعلة نصبها الشارع أمارة يستدل بها المجتهد على وجود الحكم، إذا لم يكن عارفًا به، ويجوز تخلفه في حق العارف، كالغيم الرطب أمارة المطر، وتخلف التعريف بالنسبة للعارف لا تخرجها عن كونها أمارة.
وما جزم به المصنف هو الحق، ومذهب جمهور أهل السنة.','5','237',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1526,'وقيل: هي المؤثر بذاته، وهو باطل.
وقيل: هي المؤثرة لا بذاتها، ولا بصفة فيها، ولكن بجعل الله تعالى لها مؤثرة.
ونقل عن الغزالي، والإمام الرازي.
والمعروف عن الإمام: الأول.
وقيل: إنها الباعث، ومعناه: أن تكون مشتملة على حكمة مقصودة للشارع من شرع الحكم من تحصيل مصلحة أو تكميلها، أو دفع مفسدة أو تقليلها، وهو اختيار الآمدي وابن الحاجب.
قال بعضهم: وهو مبني على جواز تعليل أفعال الباري تعالى بالغرض.','5','238',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1527,'وهو محكي عن الفقهاء.
والمتصور عند الأشاعرة خلافه، فإنه تبارك وتعالى لا يبعثه شيء على شيء، وقال في "المقترح": إن أريد بأنها الباعث للشرع على الحكم إثبات غرض حادث له فهو محال.
وإن أريد أن يعقبها حصول الصلاح في العادة فسميت باعثًا تجوزًا، فلا يجوز إطلاقه على الباري -تعالى- لما فيه من إيهام المحال، إلا أن يتحقق إذن من الشارع في إطلاقه، ولا سبيل إليه.
وجمع السبكي: بين كلام المتكلمين والفقهاء: بأن العلة باعثة للمكلف على امتثال الحكم، لا أنها باعثة للشارع على شرع الحكم كما','5','239',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1528,'توهمه بعضهم، فإن المعلل فعل الملكف لا حكم الله -تعالى-.
فإن قيل: العلة المستنبطة عرفت به أي بالحكم؛ لأن معرفة علية الوصف متأخرة عن طلب عليته المتأخر عن معرفة الحكم، فلو عرف الحكم بها لكان العلم بها سابقًا على العلم بالحكم فيدور فلا يصح جعل العلة مجرد أمارة.
قلنا: تعريفه أي: تعريف الحكم للعلة المستنبطة، إنما هو في الأصل؛ لأنا نعلم الحكم ثم نطلب علته.
وتعريفها -أي: تعريف العلة -للحكم إنما هو في الفرع، لأنا نعلم العلة في الفرع، ثم نثبت الحكم بها فيه.','5','240',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1529,'ويوضحه أن للحكم فردين:
فرده في الأصل يعرف للعلة.
وفرد في الفرع معرف بالعلة، فلا دور (لاختلاف الجهة) حكم الفرع، وإن كان مثل حكم الأصل لا يلزم منه أن ما يكون معرفًا لأحدهما، أن يكون معرفًا للآخر؛ لأن الامتثال قد يختلف بالجلاء والخفاء، وإن اشتركت في الماهية ولوازمها، فيجوز أن يكون الأجلى معرفًا، والأخفى معرفًا.
والنظر المتعلق بالعلة منحصر في ثلاثة أطراف:
لأن الكلام إما في الطرق الدالة على العلية؛ لأن كون الوصف الجامع علة، حكم خبري ضروري فلا بد في إثباته من الدليل،','5','241',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1530,'أو في الطرق الدالة على إبطالها، أو في أقسامها.

<span data-type="title" id=toc-233>الطرف الأول
في
الطرق الدالة على العلية
وهي تسعة:</span>
الأول: النص.
والمراد به هنا: ما يدل بالوضع من الكتاب والسنة على علية وصف الحكم كما مر.','5','242',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1531,'وهو قسمان: قاطع، وظاهر:
الأول: القاطع، وهو الذي لا يحتمل غير العلية، كقوله تعالى في الفيء: {كيلا يكون دولة}.
أي: إنما وجب تخميسه كيلا يتداوله الأغنياء بينهم، فلا يحصل للفقراء منه شيء.
والدولة -بالفتح والضم-.','5','243',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1532,'وقوله عليه الصلاة والسلام: "إنما الاستئذان لأجل البصر".
متفق عليه بلفظ: "من أجل".
أي: إنما جعل الاستئذان واجبًا في الشرع لأجل حفظ البصر، حتى لا يقع على من حرم النظر إليه.
وقوله -صلى الله عليه وسلم-: "إنما نهيتكم عن لحم الأضاحي لأجل الدافة" رواه مسلم بلفظ: "من أجل".
أي: لأجل التوسعة على الطائفة التي قدمت المدينة في أيام التشريق.
والدافة: -بالدال المهملة وتشديد الفاء- القوافل السيارة، لأجل','5','244',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1533,'كثرة الناس، مأخوذ من الدفيف، وهو السير اللين.
وفي معنى "لأجل"، "ومن أجل"، "لعله كذا" أو "بسبب كذا" أو "المؤثر" أو "الموجب".
ومنه "إذن" نحو قوله تعالى: {إذا لأذقناك ضعف الحياة} الآية.
وتركها المصنف؛ لأنها بمعنى لأجل.
القسم الثاني: النص الظاهر اللام، إلى آخره.
وقول المصنف: "والظاهر" معطوف على القاطع.
وقوله: "اللام" إما بدل منه، أو مبتدأ خبره محذوف، تقديره:','5','245',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1534,'فمنه اللام".
كقوله تعالى: {أقم الصلاة لدلوك الشمس}.
فإن أئمة اللغة، قالوا: اللام للتعليل، وقولهم في الألفاظ حجة، ولم يكن قاطعًا لاحتماله الملك والاختصاص وغيرهما.
وأما قوله تعالى: {ولقد ذرأنا لجهنم كثيرًا من الجن الإنس}.
وقول الشاعر:','5','246',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1535,'له ملك ينادي كل يومٍ *** لدوا للموت وابنوا للخراب
فإن اللام فيهما لما تعذر حملها على التعليل؛ إذ جهنم لا تكون علة للخلق، والموت ليس علة للولادة ولا الخراب علة للبناء، كانت للعاقبة مجازًا؛ لأن عاقبة كثير من المخلوقين جهنم، وعاقبة الولادة الموت، وعاقبة البناء الخراب.
وإنما قلنا ذلك: لئلا يلزم الاشتراك، والمجاز خير منه.
ووجه العلاقة أن عاقبة الشيء مترتبة عليه في الحصول كترتب العلة العامة على معلولها.
تنبيه:
أخرج البيهقي في الشعب من حديث أبي هريرة (رضي الله عنه) عن النبي -صلى الله عليه وسلم- قال: "إن ملكًا بباب من أبواب الجنة ينادي كل يوم: من يقرض الله اليوم يجزى غدًا".','5','247',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1536,'الحديث من رواية مؤمل بن إسماعيل وفيه: "وإن ملكًا بباب آخر يقول: يا بني آدم، لدوا للموت وابنوا للخراب".
وفي كتاب "الزهد الكبير" لأحمد -رضي الله عنه- عن طريق عبد الواحد بن زياد قال: قال عيسى -عليه الصلاة والسلام-: "يا بني آدم لدوا للموت وابنوا للخراب".
ومن الظاهر "إن" مثل قوله -صلى الله عليه وسلم- في المحرم الذي وقصته ناقته: "لا تقربوا طيبًا؛ فإنه يحشر يوم القيامة','5','248',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1537,'ملبيًا".
متفق عليه بلفظ: "فإنه يبعث".
وجعل المصنف هذا مثالاً للتعليل الصريح، ليس مخالفًا لما سيأتي له من جعله مثالاً للإيماء لأن فيه جهتان: جهة تدل على التعليل','5','249',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1538,'بالصريح وهي "إن" وجهة تدل بالإيماء: هي ترتب الحكم على الوصف بالفاء، فصح التمثيل به للنص تارة وللإيماء أخرى.
قال التبريزي في التنقيح: (والحق أن): "إن" التأكيد مضمون الجملة، ولا إشعار لها بالتعليل.
ولهذا يحسن استعمالها ابتداء من غير سبق حكم.
وكذا قوله -عليه الصلاة والسلام-: (إنها ليست بنجسة" -يعني الهرة) إنها من الطوافين عليكم والطوافات".','5','250',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1539,'رواه الأربعة عن أبي قتادة وصححه الترمذي.
قال الزركشي: وتكلم فيه ابن مندة بما بان فيه عدم تأثيره.
ومن الظاهر "الباء" مثل قوله تعالى: {فبما رحمة من الله لنت لهم} أي بسبب الرحمة لنت لهم.
قال في المحصول: وأصلها الإلصاق، ولكن العلة لما اقتضت وجوب المعلول حصل فيها معنى الإلصاق، فحسن استعمالها فيه مجازًا.
قال الإسنوي: وهو صريح في أنها لا تحمل عند الإطلاق','5','251',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1540,'على التعليل فلا تكون ظاهرة فيه وهو الصواب.
وقال العبري: قريبًا منه.
واقتصر سيبويه على أنها للإلصاق.
ولها معان أخر ذكرت بعضها في شرح الوردية.
وزاد ابن الحاجب: إن كان كذا، وهي "إن" الشرطية، وكذلك بيد، وحتى، وعلى، وفي، ومن.
وزاد ابن مالك: "وإذا" لقوله تعالى: {وإذ اعتزلتموهم وما يعبدون إلا الله}.
تنبيه:
قدم ابن الحاجب، وصاحب جمع الجوامع: الإجماع على النص؛ لأنه مقدم عليه في العمل، وأخره المصنف (عن','5','252',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1541,'النص) لأنه أصل الإجماع، ولكنه أخره أيضًا عن الإيماء.
وجعل في جمع الجوامع أعلى مراتب الصريح: مثل "لعله كذا"، ويليه "لسبب كذا" ويليه "من أجل كذا"، ويليه "كي"، ومثله "إذاً".
وجعل ابن السمعاني: "لأجل" و"كي" دون ما قبلهما في الصراحة.
الطريق الثاني من الطرق الدالة على علية الوصف: الإيماء.
والإيماء لغة: الإشارة الخفية.
واصطلاحًا: اقتران وصف بحكم، لو لم يكن هو أو نظيره للتعليل لكان بعيدًا، قاله ابن الحاجب.','5','253',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1542,'وقال غيره: هو ما يدل على علية وصف، بواسطة قرينة من القرائن.
ويسمى التنبيه أيضًا.
وهو خمسة أنواع:

<span data-type="title" id=toc-236>النوع الأول: ترتيب الحكم على الوصف بالفاء</span>.
وهو أن يذكر حكمًا أو وصفًا، وتدخل الفاء على الثاني منها، سواء كان: هو الوصف أو الحكم، وسواء كان ذلك في: لفظ الرسول (صلى الله عليه وسلم) أو الراوي الفقيه، أو غيره.
وإليه أشار بقوله: ويكون أي: الترتيب بإلقاء في الوصف أو الحكم، وفي لفظ الشارع، أو الراوي.
مثاله: في دخول الفاء على الحكم في كلام الشارع {والسارق','5','254',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1543,'والسارقة فاقطعوا أيديهما}.
ومثال دخول الفاء على الوصف في كلام الرسول -صلى الله عليه وسلم-: "لا تقربوه طيبًا فإنه يبعث يوم القيامة ملبيًا". وتقدم تخريجه.
ولم يوجد في كلام الرواي مثال دخول الفاء على الوصف، كما قيل.
ومثال دخول الفاء على الحكم في كلام الراوي: "زنى ماعز فرجم". وهو مروي بالمعنى، وحديثه في الصحيحين. لكن مقصود المصنف هذا اللفظ.
قال الزركشي: ولم يرد.
وقال العراقي: لا يعرف أصلاً بهذا اللفظ.
فالأحسن أن يمثل بما رواه أبو داود والترمذي','5','255',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1544,'والنسائي.
عن عمران بن حصين أن النبي -صلى الله عليه وسلم- "سهى فسجد" وقال الترمذي: حسن غريب، وصححه الحاكم على شرطهما.
قال الآمدي: والوارد في كلام الشارع أقوى في العلية من الوارد في كلام الراوي بلا شك، لاحتمال الغلط، إلا أنه لا ينفي الظهور.','5','256',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1545,'قال: ويشبه أن يكون تقديم العلة أقوى من عكسه.
وجعل المصنف هذا من باب الإيماء، تبع فيه الآمدي.
وجعله ابن الحاجب من باب الصريح.
ولما كان الترتيب السابق، وهو ما فيه الفاء يقتضي العلية.
فرع عليه نفس الترتيب المجرد عن الفاء فقال:
فرع: ترتيب الحكم على الوصف يدل على العلية، وإن لم يكن مناسبًا.
وقيل: يدل ترتيب الحكم على الوصف إذا كان مناسبًا، ونقل عن','5','257',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1546,'الآمدي وابن الحاجب.
لنا: أنه إذا قيل: أكرم الجاهل وأهن العالم، قبح ذلك عرفًا، وليس قبحه لمجرد الأمر بإكرام الجاهل وإهانة العالم، فإنه أي: الأمر بالإكرام للجاهل، قد يحسن لدينه أو شجاعته أو سوابق نعمه.
والأمر بالأهانة قد يحسن لفسق العالم أو بدعته، أو سوء خلقه، ونحو ذلك.
وإذا لم يكن القبح لمجرد الأمر، فهو لسبق التعليل إلى الفهم من جعل الجهل علة للإكرام، والعلة علة للإهانة؛ لأن الأصل عدم علة أخرى.
وإذا سبق إلى الأفهام التعليل مع عدم المناسبة؛ والفاء، لزم أن يكون حقيقة فيه فإن قيل: الدلالة أي: دلالة الترتيب على الوصف الغير المناسب للعلية في هذه الصورة لا تستلزم دلالته في الكل، أي: في كل الصور، لأن المثال الجزئي لا يصحح القضية للكلية لجواز','5','258',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1547,'اختلاف الجزئيات في الأحكام.
قلنا: الترتيب المذكور إذا دل على العلية في هذه الصورة، يجب أن يدل عليها في جميع الصور دفعًا للاشتراك.
إذ لو كان دالاً على غير العلة في بعض الصور لكان مشتركًا بينهما، والأصل عدمه.
ونظر فيه من جهة: أن الترتيب من جملة المركبات، وهي غير موضوعة عنده تبعًا للإمام الرازي، فلا توصف حينئذ باشتراك ولا مجاز؛ لأنهما فرع الوضع.
تنبيه: إذا ذكر الوصف والحكم كلاهما، كان إيماء بالاتفاق.','5','259',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1548,'وهو الذي ذكره المصنف، فإن ذكر أحدهما فقط مثل أن يذكر الوصف صريحًا والحكم مستنبطًا نحو: {وأحل الله البيع}.
فإن حل البيع وصف له قد ذكر، فعلم منه حكمه وهو الصحة، أو ذكر الحكم، والوصف مستنبط، وذلك كثير منه أكثر العلل المستنبة، نحو حرمة الخمر، فالمختار في جمع الجوامع تبعًا للصفي الهندي: أن الأول: وهو ذكر الوصف إيماء دون الثاني: وهو ذكر الحكم، وله زيادة تحقيق في الشرح.
النوع الثاني من الإيماء:
أن يحكم الشارع على شخص بحكم عقب علمه بصفة المحكوم عليه','5','260',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1549,'كقول الأعرابي: واقعت أهلي في رمضان يا رسول الله؟ فقال: "أعتق رقبة". رواه ابن ماجه.
وأصل الحديث بغير هذه الصيغة في الكتب الستة.
فدل الحديث على أن الوقاع علة للإعتاق، وذلك لأن صلاحية جوابه -صلى الله عليه وسلم- للأعرابي بقوله: "أعتق رقبة". يغلب الظن على كونه جوابًا للأعرابي عن سؤاله، ليحصل غرضه حيث عرض واقعته','5','261',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1550,'عليه، ولئلا يلزم إخلاء السؤال عن الجواب (وتأخير البيان) عن وقت الحاجة. وحينئذٍ فيكون السؤال معادًا فيه -أي: في الجواب- تقديرًا فكأنه اقعت فكفر.
فالتحق هذا النوع م الإيماء بالأول منه في إفادة العلية؛ لأنه في التقدير ترتيب حكم على وصف بلفظ الفاء الذي هو النوع الأول، لكنه دونه في الظهور؛ لأن الفاء هاهنا مقدرة، وهناك محققة، ولاحتمال عدم قصد الجواب، كما يقول العبد: طلعت الشمس، فيقول السيد: اسقني ماء.
كل ذلك وإن بعد فليس بممتنع.
واعلم أن مثل ذلك إذا حذف عنه بعض الأوصاف، وعلل بالباقي سمي "تنقيح المناط".','5','262',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1551,'مثاله: (في قصة الأعرابي، أن يقال): كونه أعرابيًا لا مدخل له في العلة، إذ الهندي والأعرابي حكمهما في الشرع واحد، وكذا كون المحل أهلاً، فإن الزنا أجدر به.
أو يقال: كونه وقاعًا لا مدخل له، فيبقى كونه إفسادًا للصوم.
تنبيه: في أكثر النسخ: "أفطرت يا رسول الله".
قال العراقي: وهو سبق قلم، أو أطلق الأعم وأراد به الأخص.
فإن الوقاع نوع من المفطرات، وقد أصلح في بعض النسخ على الصواب.','5','263',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1552,'النوع الثالث من الإيماء:
أن يذكر الشارع وصفًا لو لم يؤثر في الحكم، يعني لو لم يكن علة له، أي: علامة عليه، لم يفد ذكره.
وحينئذٍ يتعين أن يكون ذلك الوصف علة لذلك الحكم، وإلا وقع في كلام الشارع ما لا فائدة له، وهو باطل.
ومثل له المصنف بأربعة أمثلة؛ لأنه أربعة أقسام:
الأول: إن ذكره واقعًا لسؤال أورده من توهم الاشتراك بين صورتين. مثل ما روي عنه (عليه الصلاة والسلام) أنه امتنع من الدخول على قوم عندهم كلب، فقيل: له إنك تدخل على قوم وعندهم هرة، زعمًا منه أن الهرة كالكلب في النجاسة.
فقيل: إنه -عليه الصلاة والسلام- قال دفعًا لزعمه: "إنها ليست بنجسة، إنها من الطوافين عليكم والطوافات".','5','264',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1553,'فلو لم يكن طوفها علة لعدم نجاستها، ودافعًا لسؤال السائل لم يكن لذكره فائدة وكان ذكره عبثًا، لا سيما وهو من الواضحات.
وجمع الهرة -بالياء والنون- مع أنها لا تعقل؛ لأن المراد بها من جنس الطوافين.
واعلم أن هذا الحديث ذكره في "المحصول"، وهو غير معروف هكذا، فإن قصة دخوله على قوم دون قوم رواها أحمد في مسنده، والذي فيه أنه -عليه الصلاة والسلام- أجاب بقوله: "إن الهرة سبع".
وأما قوله "إنها ليست بنجسة" (إلى آخره).
فإنما ورد عند إصغاء الإناء لتشرب منه، قاله العراقي.
وتقدم تخريجه.
قال: ويجاب عن المصنف بأنه لم يذكر هذه القصة، بل اقتصر','5','265',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1554,'على قوله: "إنها من الطوفين" فاحتمل أن يريد به قوله -صلى الله عليه وسلم- هذا الكلام عقب إصغاء الإناء لهما، كما هو المعروف.
والتمثيل به على هذا التقدير أيضًا صحيح، وإن قات منه جعله أحد الأقسام الأربعة التي ذكرها في "المحصول" فإنه ليس فيه دفع سؤال.
أورده من توهم الاشتراك بين صورتين.
ثانيهما: أن يذكر الشارع وصفًا في محل الحكم لا حاجة إلى ذكره ابتداء، ولا معنى له لو لم يكن علة، مثل ما روي عن ابن مسعود -رضي الله تعالى عنه- أن النبي -صلى الله عليه وسلم- قال له ليلة الجن: "ما في إداواتك"؟ قال: نبيذ.
قال: "ثمرة طيبة وماء طهور". رواه أبو داود والترمذي وابن ماجة، زاد الترمذي: فتوضأ به.','5','266',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1555,'قال: ولم يروه غير أبي زيد وهو مجهول، ولا يعرف عنه غير هذا الحديث.
وقال أبو زرعة، وابن عدي: هو حديث ليس بصحيح.
وفي صحيح مسلم عن ابن مسعود (رضي الله عنه) صريحًا ما ينافي هذه الرواية.','5','267',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1556,'وقال العراقي: ضعيف باتفاق المحدثين.
فوصف المحل، وهو النبيذ بطيب ثمره وطهورية مائه دليل على بقاء طهورية الماء.
قال القرافي: هو غير مطابق؛ لأن ذكره -عليه الصلاة والسلام- طيب الثمرة ليس إشارة إلى العلة في بقاء الطهورية، بل إلى عدم المانع.
ثالثها: أن يسأل الشارع عن وصف، فإذا أجاب عنه المسئول عنه، أقره عليه، ثم يذكر بعده حكمًا.
وذلك مثل قوله -صلى الله عليه وسلم- لما سئل عن جواز بيع الرطب بالتمر متساويًا فقال: "أينقص الرطب إذا جف"، قيل: نعم، فقال: "فلا إذن" رواه الأربعة وصححه الترمذي ابن خزيمة والحاكم.','5','268',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1557,'فنبه على أن النقصان علة منع البيع، وكونه مفهومًا من الفاء، وإذا لا ينافي ذلك، إذ لو قدرنا انتفاءهما لبقي فهم التعليل.
رابعها: أن يقر الرسول -صلى الله عليه وسلم- السائل على حكم ما يشبه المسئول عنه، ثم ينبه على وجه الشبه، فيعلم أن وجه الشبه هو العلة، وذلك مثل ما روي من قوله عليه الصلاة والسلام -لعمر- رضي الله تعالى عنه- وقد سأل عن قبلة الصائم هل يفسد الصوم من غير إنزال؟ : "أرأيت لو تمضت بماء، ثم مججته"- يعني: لفظته "أكنت شاربه؟ " فقال: لا.
فنبه الرسول على ما روي بهذا على أن حكم القبلة في عدم إفسادها للصوم كحكم ما يشبهها، وهو المضمضة.','5','269',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1558,'ووجه الشبه أن كلا منهما مقدمة لم يترتب عليه المقصود، وهو الشرب والإنزال.
تنبيه:
قال الزركشي: المحفوظ من هذا الحديث: "أرأيت لو تمضمضت من الماء وأنت صائم" قال عمر: قلت: لا بأس بذلك. قال: "فمه".
رواه أبو داود والنسائي، وقال: حديث منكر.
وأعله ابن الجوزي "بليث" توهمًا أنه "ابن أبي سليم".','5','270',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1559,'وإنما هو الليث بن سعد الإمام الجليل (رضي الله عنه).
ورواه البزار في مسنده، وقال الحاكم: صحيح على شرط الشيخين وصححه ابن حبان، وقال الإمام أحمد: حديث ضعيف.
قال العراقي: وقوله "أكنت شاربه"؟ ، لا أعرف له أصلاً.
وقال شيخ الإسلام: وقد وقع التصريح بأنه الليث بن سعد في رواية أبي داود.','5','271',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1560,'وكذا في الروايتين المتقدمتين عن الدارمي. وعبد، وكذا صرح به الهيثم بن كليب في مسنده، ووقع في روايته في آخره، فقلت: لا بأس بذلك، فقال: "ففيم".
وكذا وقع بهذا اللفظ.
وفي رواية عن عيسى بن حماد، ونبه على ذلك أبو داود.
(وقال ابن حزم: صحيح).','5','272',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1561,'النوع الرابع: من الإيماء:
أن يفرق الشارع في الحكم بين شيئين بذكر وصف لأحدهما، فيعلم أن ذلك الوصف علة لذلك الحكم، وإلا لم يكن لتخصيصه بالذكر فائدة.
ومثل له المصنف بمثالين؛ لأنه نوعان:
الأول: أن لا يكون حكم الشيء الآخر، وهو قسيم الموصوف (مذكورًا معه) مثل ما رواه الترمذي والنسائي وابن ماجة عن أبي هريرة مرفوعًا: "القاتل لا يرث".
وفيه إسحاق بن أبي فروة.','5','273',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1562,'قيل: متروك.
وله طرق مبينة في الأصل.
ففي الحديث الفرق بين القاتل وبين سائر الورثة، بذكر القتل الذي يجوز أن يكون مؤثرًا في منع الإرث.
فعلم كون القتل مانعًا من الإرث.
وليس في هذا الخطاب ذكر حكم سائر الورثة.
وثانيهما: أن يكون حكم الشيئين مذكورًا في الخطاب، إما بلفظ الشرط، واقتصر عليه المصنف، لأن بقية الأقسام في معناه مثل قوله -صلى الله عليه وسلم-: "إذا اختلف الجنسان','5','274',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1563,'فبيعوا كيف شئتم يدًا بيد" رواه مسلم فيعلم منه أن اختلاف الجنسين علة لجواز البيع متفاضلاً.
أو تكون التفرقة بلفظ الغاية، كقوله تعالى: {ولا تقربوهن حتى يطهرن}.
أو بالاستثناء كقوله تعالى: {فنصف ما فرضتم إلا أن يعفون} أو بالاستدراك كقوله تعالى {لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما عقدتم الأيمان}.','5','275',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1564,'أو باستئناف أحد الشيئين بذكر صفة من صفاته بعد ذكر الآخر، وتكون تلك الصفة صالحة للعلية.
مثاله ما روي عنه -صلى الله عليه وسلم- أنه قال "للراجل سهم وللفارس سهمان".
قال الزركشي: ولم يرد من لفظ النبي -صلى الله عليه وسلم- هكذا، وأقرب ما فيه، مبين في الأصل.

(ولكن روى الدارقطني والبيهقي عن أبي كبشة الأنماري حديثًا.
وفيه أن النبي -صلى الله عليه وسلم- قال: "ألا إني جعلت للفرس سهمين وللراجل سهمًا".','5','276',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1565,'قال شيخ الإسلام: وهو حيدث غريب، رجاله ثقات، إلا عبد الله ابن بشر تابعي صغير فيه مقال.
وذكر هذه الأقسام الخمسة في المحصول).
النوع الخامس من الإيماء:
النهي عن مفوت الواجب أي: عن فعل يكون مانعًا لما تقدم وجوبه مثل قوله تعالى: {فاسعوا إلى ذكر الله وذروا البيع}.
فإنه تعالى لما أوجب علينا السعي وقت نداء الجمعة، وكان البيع في ذلك الوقت مفوتًا لهذا الواجب، وقد نهانا عن البيع فيه، علم أن علة النهي مانعًا من السعي الواجب في ذلك الوقت.','5','277',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1566,'الطريق الثالث من الطرق الدالة على كون الوصف الجامع علة للحكم.
وهو الإجماع في عصر من الأعصار على كونه علة، والظن كاف لما تقدم.
وإنما يتصور الاختلاف في مثله، بأن يكون الإجماع ظنيًا، كالثابت بالآحاد والسكوتي، أو يكون ثبوت الوصف في الأصل في الفرع ظنيًا.
أو يدعي الخصم معارضًا في الفرع.
وذلك كتعليل تقديم الأخ من الأبوين، على الأخ من الأب في الإرث، بامتزاج النسبين.
فأجمعوا على التقديم: بكونه من الأبوين، على كونه من الأب في','5','278',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1567,'الإرث، فيقاس عليه تقدمه في ولاية النكاح، والصلاة عليه وتحمل العاقلة بجامع امتزاج النسبين.
الطريق الرابع من الطرق الدالة على علية الوصف:
وهي المناسبة، وتسمى: الإخالة -بكسر الهمزة، وبالخاء المعجمة- من خال: إذا ظن، لأنه بالنظر إليه يظن أنه علة.
ويسمى تخريج المناط.
لأنه أبدى مناط الحكم، أي علق علته.
وحاصله: تعيين العلة في الأصل بمجرد إبداء المناسبة بينها، وبين الحكم من ذات الأصل لا بنص ولا بغيره كالإسكار للتحريم، فإن النظر في المسكر وحكمه، ووصفه، يعلم منه كون الإسكار مناسبًا لشرع التحريم.','5','279',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1568,'وعرف في جمع الجوامع المناسبة: بتعيين العلة: بإبداء مناسبة
مع الاقتران والسلامة عن القوادح.
وأخرج: بإبداء المناسبة، تعيين العلة بالطرد أو الشبه.
وأراد بالاقتران: مقارنة الحكم للوصف.
وهو من زيادته على ابن الحاجب لبيان اعتماد المناسبة لا لتحقيق ماهيتها وأورد عليه أن معرفة إبداء المناسبة متوقف على معرفتها فكيف يعرف بها.
وأجيب بأن المناسبة في التعريف لغوية، بمعنى الملائمة، فلا دور.','5','280',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1569,'واعلم أنه يتحقق الاستدلال على أن الوصف الذي أبداه هو العلة بعدم ما سواه بطريق السبر.
ولا يكفي أن يقول بحثت فلم أجد غيره، وإلا لزم الاكتفاء به ابتداء ولا قائل به.
وأما المناسب فهو في اللغة: الملائم.','5','281',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1570,'وفي الشرع- قال المصنف- ما يجلب للإنسان نفعًا أو يدفع عنه ضررًا.
أي: الوصف الذي يقتضي إلى ما يجلب للإنسان نفعًا أو يدفع عنه ضررًا.
والنفع: اللذة، أو ما يكون طريقًا إليها.
والضرر: الألم، أو ما يكون طريقًا إليه.
وقيد بالإنسان لتعالى الرب- تعالى- وتقدس عن الضرر أو الانتفاع ونظر فيه من جهة أن فيه تفسير العلة بالحكم.
لأن الوصف المناسب من أقسام العلة، كالقتل يناسب القصاص، والجالب للنفع الدافع للضرر، هو الحكم.
كإيجاب القصاص: جالب لمنفعة بقاء الحياة (ودافع لضرر التعدي) وحكى في المحصول التعريف الذي اختاره المصنف عمن','5','282',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1571,'يعلل أحكام الله- تعالى
وقيل المناسب: هو الوصف الملائم لأفعال العقلاء في العادة، أي: يقصده العقلاء لتحصيل مقصود مخصوص. كما يقال: هذه اللؤلؤة تناسب هذه اللؤلؤة. واختاره في جمع الجوامع، وحكاه في المحصول عمن لا يعلل أحكام الله تعالى، وهو مذهب الأشعري، وهو المعتمد.
وقيل: هو وصف ظاهر منضبط يحصل عقلًا من ترتيب الحكم عليه ما يصلح أن يكون مقصودًا للعقلاء، واختاره الآمدي وابن الحاجب، والمقصود: إما حصول مصلحة أو دفع مفسدة.
وهو- أيك المناسب- حقيقي: إن كانت مناسبته بحيث لا','5','283',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1572,'يزول بالتأمل.
والحقيقي الدنيوي: إن كانت لمصلحة تتعلق بالدنيا.
والدنيوي ضروري: إن انتهت مصلحته إلى حد الضرورة.
وهو قسمان:
- ضروري في نفسه
- ومكمل للضروري
واقتصر المصنف على الأول، وهو أعلى المراتب في إفادة ظن الاعتبار، وذلك كحفظ النفس بالقصاص، وحفظ الدين بالقتال','5','284',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1573,'للكفار، وحفظ العقل بالزجر عن المكسرات، وحفظ المال: الذي به المعاش بالضمان على متلفه، وعقوبة السارق والمحارب، وحفظ النسب بالحد على الزنا؛ لأن المزاحمة في الأبضاع مجلبة للفساد والتقاتل، ومفضية إلى اختلاط الأنساب المؤدي إلى انقطاع تعهد الأولاد المنافي لبقاء النوع وهذه هي الكليات الخمس الضرورية التي روعيت في كل ملة.
وزاد في جمع الجوامع تبعًا للطوافي.','5','285',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1574,'سادسًا: وهو العرض.
فتحريمه معلوم من الدين بالضرورة وحفظه بحد القذف.
ومقتضى كلامه أنه في مرتبة المال.
ويحتمل أن يجعل دون الكليات فيكون من الملحق به.
القسم الثاني: وهو المكمل للضروري: وذلك كحد قليل المسكر وهو لا يزيل العقل، وحفظ العقل حاصل بتحريم المسكر، وإنما حرم القليل للتتميم والتكميل؛ لأن قليله يدعو إلى كثيره، بما','5','286',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1575,'يورث النفس من الطرب المطلوب زيادته بزيادة سببه إلى أن يسكر، ومن حام حول الحمى يوشك أن يقع فيه.
والمناسب مصلحي: إن كانت مصلحته في محل الحاجة. ولم تنته إلى حد الضرورة، ويسمى الحاجي، وهو أيضًا ينقسم إلى قسمين:
- حاجي في نفسه.
- ومكمل للحاجي.
مثال الحاجي في نفسه: البيع والإجارة والقراض والمساقاة.','5','287',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1576,'فإن المعاوضة: وإن ظنت أنها ضرورية فكل واحد من هذه العقود، ليس بحيث لو لم يشرع لأدى إلى فوات شيء من الضروريات الخمس.
قال المحقق: وهذه ليست في مرتبة واحدة، فإن الحاجة تشتد وتضعف، وبعضها آكد من بعض، وقد يكون بعضها ضروريًا في بعض الصور، كالإجارة في تربية الطفل الذي لا أم له ترضعه، وكشراء المطعوم، والملبوس، فإنه ضروري من قبيل حفظ النفس، ولذلك لم تخل عنه شريعة، وإنما أطلقنا الحاجي عليها باعتبار الأغلب.
مثال المكمل للحاجي: وجوب رعاية الكفاءة ومهر المثل في الولي','5','288',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1577,'إذا زوج الصغيرة.
فإن أصل المقصود من شرع النكاح وإن كان حاصلًا بدونهما لكنه أشد إفضاء إلى دوام النكاح، وهو من مكملات مقصود النكاح.
والمصنف- رحمه الله- مثل للمصلحي: بقوله: كنصب الولي للصغير لئلا تضيع حقوقه.
والظاهر أنه من القسم الأول من الحاجي.
والمناسب تحسيني: إذا لم يكن في محل الضرورة ولا الحاجة، ولكن مستحسن في العادات، وفيه تحسين وتزيين، وسلوك منهج أحسن من منهج، وذلك كتحريم القاذورات.
فإن نفرة الطباع عنها لخساستها، مناسب لحرمة تناولها حثًا للناس على مكارم الأخلاق، ومحاسن العادات، والشيم.
ومنه سلب العبد أهلية الشهادة، وإن كان ذا دين وعدالة يغلبان','5','289',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1578,'على ظن صدقه لنقصه عن المناصب الشريفة.
وجعل في جمع الجوامع وغيره: التحسيني قسمان:
أحدهما: أن لا يعارضه شيء من القواعد، كسلب العبد أهلية الشهادة.
ثانيهما: أن يعارض قاعدة معتبرة، كالكتابة.
فإنها جوزت لاستحسانها في العادة، مع مخالفتها للقاعدة في امتناع بيع الإنسان ماله بماله.
والمناسب الأخروي: كتزكية النفس، من تهذيب الأخلاق ورياضة','5','290',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1579,'النفوس المقتضية لشرعية العبادات.
فإن الصلاة مثلًا وضعت للخضوع والتذلل.
والصوم لانكسار النفس بحسب القوى الشهوانية والغضبية، فإذا كانت النفس زكية تؤدي المأمورات وتجتنب المنهيات حصلت لها على السعادة الأخروية. والمناسب الإقناعي: هو الذي يظن في بادئ النظر أنه مناسب، وإذا بحث عنه حق البحث فيزول ذلك الظن، وتظهر عدم مناسبته بالتأمل فيه.
مثاله: تعليل تحريم الخمر والميتة بالنجاسة، ثم يقيس عليه الكلب والخنزير.
فكونه نجسًا يناسب إذلاله، ومقابلته بالمال في البيع إعزاز، والجمع بينهما متناقض.
فهذا وإن كان يظن أنه مناسب، لكنه في الحقيقة ليس كذلك؛','5','291',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1580,'لأن كونه نجسًا معناه أنه لا تجوز الصلاة معه.
وليس بينه وبين امتناع البيع مناسبة.
والمناسبة تفيد العلة إذا اعتبرها، أي: اعتبر نفس العلة الشارع فيه، أي: في نفس الحكم.
وليس المراد باعتباره أن ينص على العلة، أو يومئ إليها، وإلا لم تكن العلة مستفادة من المناسبة، وذلك كالسكر في الحرمة، فإنه مناسب للتحريم حفظًا للعقل.
وعلم أن الشارع لم يعتبر عينة في جنس التحريم، ولا جنسه في عين التحريم، ولا جنسه في جنس التحريم.
ولكن اعتبر النوع في النوع، فإن السكر نوع من الوصف، والتحريم نوع من الحكم.','5','292',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1581,'واعتبر الشارع نوع الوصف (في جنسه، أي: في جنسه الحكم، وذلك، كامتزاج النسبين، فإنه نوع من الوصف، اعتبر في مطلق التقديم على الأخ من الأب وهو جنس الحكم، فإن مطلق التقديم علة جنس لنوعين:
وهما: التقديم في الإرث، والتقديم في ولاية النكاح، كذا شرحه العبري.
وفي الأصل أوضح من ذلك.
أو بالعكس: يعني اعتبر جنس المناسبة في نوع الحكم،','5','293',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1582,'كالمشقة المشتركة بين الحائض والمسافر في سقوط قضاء الصلاة.
فإن المشقة المشتركة بينهما، جنس للمشقتين، وقد اعتبرها الشارع في نوع الحكم، وهو سقوط الصلاة عنهما، أيك القضاء عن الحائض والركعتين عن المسافر.
أو اعتبر الشارع جنسه أي: جنس الوصف في جنسه أي: في جنس الحكم، كإيجاب حد القذف على الشارب، لكون الشارب مظنة القذف، لما رواه النسائي أن عليًّا- رضي الله تعالى عنه- قال في شارب الخمر: «أرى أنه إذا شرب هذى وإذا هذى افترى فيكون عليه حد المفتري ثمانون جلدة» يعني القاذف. «فأمر عمر فجلدة ثمانين».
فقد أوجبوا حد القذف على شارب الخمر، لا لكونه شارب خمر،','5','294',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1583,'بل إقامة لمظنة القذف، وهو الشرب، مقام القذف قياسًا على إقامة الخلوة بالأجنبية، مقام الوطء في التحريم، لكون الخلوة مظنة الوطء المحرم.
فاعتبرت المظنة التي هي جنس لمظنة الوطء والقذف في الحكم الذي هو جنس لإيجاب حد القذف، وحرمة الوطء.
وإليه أشار بقوله: «والمظنة قد أقيمت مقام المظنون».
والمراد بالجنس هنا: القريب، لأن اعتبار الجنس البعيد في الجنس البعيد هو المناسب كما سيجيء إن شاء الله تعالى، وأعم أوصاف الحكم- على ما في المحصول- كونه حكمًا.
ثم الحكم ينقسم إلى وجوب، وغيره. والوجوب إلى عبادة، وغيرها.','5','295',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1584,'والعبادة إلى صلاة، وغيرها.
والصلاة إلى نافلة وغيرها.
فما ظهر تأثير في الفرض أخص مما ظهر في الصلاة.
قال: وكذا في جانب الوصف، فأعم الأوصاف كونه يناط به الحكم، ثم المناسب ثم الضروري.
وإنما قلنا: إن المناسبة في هذه الأقسام الأربعة تفيد ظن العلية، لأن الاستقراء في الأحكام الشرعية دل على أن الله- سبحانه وتعالى شرع أحكامه لمصالح العباد، أي: يعقبها حصول الصلاح في العادة.
فإن العلماء في كل زمان تتبعوا الأحكام الشرعية.
فوجدوها مقارنة للحكم، والمصالح غير منفكة عنها.','5','296',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1585,'وهذه الرعاية ليست واجبة على الله- تعالى- لتعاليه، بل تفضلًا وإحسانًا منه- تعالى- لا على سبيل التحتم والوجوب، ولا على أن فعله- تعالى- معللًا بها وقد مر مثله في موضعين من هذا الكتاب.
وإذا كانت مشروعة للمصالح، فحيث ثبت حكم شرعي، وهناك وصف مناسب له يتضمن مصلحة للعبد ولم يوجد غيره من الأوصاف الصالحة للعلية، ظن كونه علة لذلك الحكم، والأصل عدم غيره، فلو لم تكن علة لخلا الحكم عن العلة، والاستقراء ينفيه.
فثبت أن المناسبة تفيد ظن العلية، وظن العلية معمول به قطعًا.
وهنا في الشرح فوائد حسنة.
وعلم من كلام المصنف أن الوصف المناسب إذا ألغاه الشارع أي: بورود الفرع على عكسه، فإنه لا يجوز التعليل به، وهو كذلك بلا','5','297',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1586,'شك.
وقوله: «وإن لم تعتبر» قسيم لقوله: «والمناسب يفيد العلية إذا اعتبرها الشارع فيه، وأشار به إلى المناسب الذي لا يعلم هل اعتبره الشارع أو ألغاه، وهو «المناسب المرسل» كذا فسره المصنف في الغاية','5','298',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1587,'القصوى تبعًا للآمدي، وفيه خلاف سيجيء، وقد اعتبره مالك رضي الله عنه.
وقال ابن الحاجب: المرسل هو الذي لم يعتبره الشارع سواء علم إلغاؤه أو لم الاعتبار والإلغاء.
وحملت كلام المصنف على أحد القسمين، كما عرفت، لأنه الذي اعتبره مالك (وهو الذي) فسره به المصنف في الغاية كما مر.
وقال الإمام الرازي: وذلك إنما يكون بحسب أوصاف هي أخص من كونه وصفًا مصلحيًّا مشهودًا له بالاعتبار.
ولذا عبر عن المناسب المرسل بأنه (الذي اعتبر جنسه في','5','299',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1588,'جنسه، ولم يوجد له أصل يدل على) اعتبار نوعه في نوعه.
وقول المصنف: «والغريب» تقسيم للقسم الأول: وهو المناسب الذي علم اعتباره.
وحاصله: أنه باعتبار تأثير نوع الوصف، أو جنسه في نوع الحكم، أو جنسه، ينقسم إلى: (الغريب والملائم والمؤثر. فالمناسب الغريب: ما أثر هو، أي) نوع الوصف فيه، أي: في نوع الحكم، ولم يؤثر جنسه، أي: جنس الوصف في جنسه، أي: في جنس الحكم.
وسمي به؛ لأنه لم يشهد غير أصله المعين باعتباره.','5','300',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1589,'وذلك كالطعم في الربا.
فإن نوع الطعم، وهو الاقتيات، مؤثر في ربوية البر، ولم يؤثر جنس الطعم في ربوية جنس المطعومات، كمأكول البهائم.
والوصف الملائم: ما أثر نوعه في نوعه وأثر جنسه في جنسه أيضًا.
مثاله: أن يقال: يجب القصاص في القتل بالمثقل، قياسًا على القتل بالمحدد بجامع كونهما جناسة عمد عدوان.
فالحكم مطلق القصاص، وهو جنس يجمع القصاص في النفس وفي الأطراف وغيرهما من القوى.
والوصف جناية العمد العدوان، وأنه جنس يجمع الجناية في النفس وفي الأطراف وفي المال.
وقد اعتبر جنس الجناية في جنس القصاص.','5','301',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1590,'واعتبر نوع الجناية، وهو القتل العمد العدوان في وجوب القتل قصاصًا.
وهذا القسم متفق على قوله.
والوصف المؤثر: ما أثر جنسه أي: جنس الوصف، فيه أي في نوع الحكم كالمشقة في سقوط الصلاة.
هذا ظاهر كلام المصنف، وهو مناف لما في المحصول.
حيث قال: المؤثر: هو أن يكون الوصف مؤثرًا في جنس الحكم. ولما في مختصر المنتهى أيضًا.','5','302',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1591,'وسمي مؤثرًا: لظهور تأثيره في الحكم بالنص أو الإجماع، ولهذا لا يحتاج إلى المناسبة.
قال العبري: ولعل المتن، يعني متن المنهاج: «والمؤثر ما أثر في جنسه» والغلط إنما وقع من الناسخ.
وهنا في الشرح تقسيم الآمدي وابن الحاجب وما توافقا فيه مع الصنف، وما اختلفوا فيه تركته هنا خوف الإطالة.','5','303',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1592,'مسألة
المناسبة لا تبطل بالمعارضة، يعني أن الوصف إذا كان مشتملًا على مصلحة مناسبة لمشروعية الحكم، على وجه يلزم منه وجود مفسدة، فلا تنخرم المناسبة، وهو في ذلك تابع للإمام الرازي.
لأن الفعل المناسب إن تضمن ضررًا أقل من نفعه، أو مساويًا له، فظاهر أنه لا تبطل مناسبته، وإلا يلزم ترجيح المرجوح، أو','5','305',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1593,'الترجيح بلا مرجح، وإن تضمن ضررًا أزيد من نفعه فكذلك لا تبطل مناسبته، إذ لا يصير نفعه غير نفع، لاستحالة انقلاب الحقائق.
وإذا بقي نفع الوصف، بقيت مناسبته وهو المطلوب لكن يندفع مقتضاه. فلا يترتب عليه لكونه مرجوحًا، ولا يلزم منه بطلانه.
واقتصر على الشق الأخير من الترديد؛ لأن عدم بطلان المناسبة على هذا التقدير يستلزم عدم بطلانه على التقديرين الأولين.
وقيل: تنخرم المناسبة إن كان وجود المفسدة مساوية لمصلحته، أو راجحة عليها، واختاره ابن الحاجب والصفي الهندي. وصاحب جمع الجوامع.
واتفقوا على عدم ترتب مقتضاها عليها.','5','306',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1594,'ولذا قال بعضهم: الخلاف لفظي.
وليست الصلاة في الدار المغصوبة مما نحن فيه؛ لأن الكلام في مصلحة ومفسدة لشيء واحد، ومفسدة الغصب لم تنشأ من الصلاة. فإنه لو شغل المكان من غير أن يصلي لأثم.
وكذلك مصلحة الصلاة لم تنشأ من الغصب، فإنه لو أدى في غير المغصوب لصحت.
وإذ قد عرفت أنه لا بد من رجحان المصلحة على المفسدة عند تعارضهما فللترجيح طرق:
فمنها: تفصيلية تختلف باختلاف المسائل وتنشأ من خصوصياتها.
ومنها: طريق إجمالي شامل لجميع المسائل، وهو أنه لو لم يقدم رجحان المصلحة على المفسدة في محل النزاع لزم أن يكون الحكم قد ثبت فيه لا لمصلحة، وهو التعبد، وتقدم.','5','307',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1595,'الطريق الخامس من الطرق الدالة على علية الوصف:
الشبه:
وقد اختلفت عباراتهم في تفسيره: حتى قال إمام الحرمين: لا يتحرر في الشبه عبارة مستمرة في صناعة الحدود.
وقال القاضي أبو بكر الباقلاني: الوصف المقارن للحكم إن ناسبه بالذات، بأن تكون جلبة النفع، أو دفعة الضرر، لكونه ذلك الوصف، لا لأمر آخر، كالسكر (المناسب بالذت) للحرمة؛ لأن زوال العقل يناسب بالذات الحرمة، والمنع من شربه، فهو الوصف المناسب المتفق على قبوله.','5','308',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1596,'أو ناسبه بالتبع أي: بالاستلزام لا بالذات، كالطهارة المناسبة بالتبع لاشتراط النية في قياس الوضوء على التيمم، فإن الطهارة من حيث هي لا تناسب اشتراط النية، وإلا اشترطت في الطهارة عن النجس، لكن تناسبه من حيث إنها عبادة، والعبادة مناسبة بالذات، لاشتراط النية فهو الشبه.
وسمي شبهًا؛ لأن عدم مناسبته للحكم بالذات يقتضي ظن عدم عليته.
ومناسبته بالتبع يقتضي ظن عليته فاشتبه الأمر فيه، وإن لم يناسب الوصف المقارن للحكم، الحكم لا بالذات.
ولا بالتبع: فهو الطرد المردود اتفاقًا، كبناء القنطرة للتطهير مثلًا.
إذا قيل: يصح التطهير بالماء المستعمل؛ لأنه مائع تبنى القنطرة','5','309',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1597,'على جنسه، فأشبه الماء في النهر، فإن بناء القنطرة على الماء، ليس مناسبًا لكونه طهورًا ولا مستلزمًا له.
كذا نقل المصنف عن القاضي، وكذا نقله غيره.
لكن في مختصر التقريب له أن الشبه: إلحاق فرع بأصل لكثرة إشباهه للأصل في الأوصاف من غير أن يعتقد أن الأوصاف التي شابه الفرع فيها الأصل علة لحكم الأصل.
وقيل: الوصف ما لم يناسب الحكم إن علم في الشرع اعتبار جنسه القريب في الجنس القريب لذلك الحكم فهو الشبه.
كما يقال تنحصر الطهارة عن الجنس في الماء كالحدث، لكون كل منهما طهارة مرادة للصلاة.
فإن الوصف، وهو كون الطهارة مرادة للصلاة، لا يناسب تعيين الماء.
ولكن الشرع اعتبر جنسه القريب، وهو الطهارة بالماء المشتركة بين كونها مرادة للصلاة، أو مرادة لمس المصحف، أو للطواف في الجنس القريب، للحكم. وهو العبادة المشروطة بالطهارة.','5','310',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1598,'وإلا أي: والوصف إن لم يعلم في الشرع اعتبار جنسه القريب في الجنس القريب لذلك الحكم فالطرد.
فعلم من التقسيم الأول: أن الشبه هو الوصف المقارن للحكم المناسب له بالتبع، وقد فسروه بأنه الجمع بين الأصل والفرع بما لا يناسب الحكم، ولكن يستلزم المناسب.
ولا يصار إلى الأخذ مع إمكان قياس العلة، كما حكى القاضي أبو بكر إجماع الناس عليه.
وعلم من التقسيم الثاني أنه الوصف الذي ليس بمناسب.
وعلم اعتبار جنسه القريب في جنس الحكم القريب. وقيل: الشبه ما يوهم المناسبة وليس بمناسب.
وهو يشبه الطردي من حيث إنه غير مناسب، ويشبه المناسب من حيث التفات الشرع إليه.','5','311',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1599,'ويتميز عن الطردي بأن الطردي وجوده كالعدم.
ويتميز عن المناسب الذاتي بأن المناسب مناسبته عقلية، وإن لم يرد الشرع. ومثاله في الشرح. وهذا القول نقله الآمدي عن أكثر المحققين. وقد اختلف في قياس الشبه. فحكي عن الشافعي (رحمه الله): أنه حجة.
قال: ابن السمعاني: أشار إلى الاحتجاج به في مواضع من كتبه.','5','312',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1600,'ورده القاضي أبو بكر، وأبو إسحاق المروزي والشيرازي.
ونازع في صحة القول به عن الشافعي (رضي الله تعالى عنه).
واعلم أن تعبيره عما ليس بمناسب ولا مستلزم للمناسب بالطرد موافق لتعبير إمام الحرمين والغزالي والإمام وغيرهم.','5','313',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1601,'وعبر الآمدي: بالطردي، وهو المشهور.
فإن الطرد بغير ياء، سيجيء- إن شاء الله تعالى- أنه من الطرق الدالة على العلية على رأي.
واعتبر الشافعي- رضي الله عنه- فيما إذا تردد فرع بين أًلين قد أشبه أحدهما في الحكم، والآخر في الصورة المشابهة في الحكم.
ولذا ألحق العبد المقتول بسائر المملوكات، في لزوم قيمته على القاتل وإن زادت على الدية.
والجامع أن كلًّا منهما يباع ويشترى.
واعتبر ابن علية- (هو إبراهيم بن إسماعيل','5','314',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1602,'الجهمي) - المشابهة في الصورة، حتى لا تزاد على الدية.
ونقل عن أبي حنيفة وأحمد- (رضي الله تعالى عنهما).
واعتبر الإمام الرازي: ما يظن استلزمه للحكم، بأن يكون علة للحكم، (أو مستلزمًا لما هو علة له)، سواء كانت العلة المشابهة في الحكم، أو في الصورة لوجوب العمل بالظن.','5','315',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1603,'ولم يعتبر القاضي أبو بكر: قياس الشبه مطلقًا.
قال الإسنوي: أدخل المصنف قياس الاشتباه، في مسألة قياس الشبه؛ لأن فيه مناسبة له.
قال: «ومقتضى كلام المصنف أن القاضي خالف في الشبه، وقياس الاشتباه، وقد أخذ الشارحون بظاهره فصرحوا به».
وليس كذلك: فقد صرح الغزالي قبل باب أركان القياس من المستصفى، بأن قياس الاشتباه ليس فيه خلاف؛ لأنه متردد بين قياسين متناسبين، ولكن وقع التردد في تعيين أحدهما.
وذكر في البرهان قريبًا منه، انتهى.
وجعل في جمع الجوامع قياس الشبه مراتب.
أعلاها: قياس علة الاشتباه في الحكم والصفة.
وظاهر كلامه، أن المراتب للقائلين بحجيته، فهو موافق للمصنف','5','316',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1604,'في جعله قياس الاشتباه قسم من الشبه.
هذا وقد قال المحقق: إن الشبه يقال لمعنى آخر: وهو الوصف الجامع لآخر، إذا تردد به الفرع بين أصلين.
فالأشبه منهما هو الشبه، كالنفسية والمالية في العبد المقتول بين الحر والفرس، وهو بالحر أشبه؛ إذ مشاركته له في الأوصاف والأحكام أكثر.
وحاصله: تعارض مناسبتين، رجح أحدهما، وليس من الشبه المقصود في شيء.
وأوردناه لنأمن الغلط الناشئ من الاشتراك.
فنبه- رحمه الله تعالى- على أنه قسم من الشبه، وأن لفظ الشبه مشترك بينه وبين ما تقدم، وأنه ليس مما يعد (من مسالك العلة).
وأن الذي يعد غير ذلك.','5','317',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1605,'فالمصنف- رحمه الله تعالى- جمع أنواع الشبه التي تعد من مسالك العلة وغيرها استطرادًا.
وحكايته خلاف القاضي في الجميع قد وافقه فيه صاحب جمع الجوامع كما مر.
وقال العراقي: هذا الخلاف المذكور هو في قياس علية الاشتباه، وهل هو قياس الشبه بعينه أو قسم منه فيه نظر.
والأقرب الثاني.
وكلام المصنف يدل على الأول.
وأما ما أفهمه كلام شيخنا جمال الدين أنه قسم برأسه فمردود.
قال: ومقتضى كلام المصنف أن القاضي لم يعتبر قياس الشبه، ولا قياس الأشباه، إن لم يكن هو إياه، وبه صرح غير واحد.
وقيل: إنه كذلك في مختصر التقريب والإرشاد له، ثم حكى كلام الغزالي وإمام الحرمين المتقدمين.','5','318',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1606,'لنا: على أن الشبه حجة ودليل على علية الوصف: أنه أي الشبه يفيد ظن وجود العلة [في] الوصف، أما على التفسير الأول فلأنه مستلزم للعلة.
وأما على الثاني فلأنه لما ثبت أن الحكم لا بد له من علة.
ورأينا تأثير جنس الوصف في جنس الحكم دون غيره من الأوصاف، كأن ظن إسناده الحكم إليه أقوى من ظن إسناده إلى غيره، وإذا ثبت إفادته للظن فيثبت الحكم لوجوب العمل بالظن.
قال القاضي: الوصف المسمى شبهًا ليس بمناسب للحكم، وإلا لم يكن شبهًا، بل كان مناسبًا، وما ليس بمناسب فهو مردود بالإجماع (فالشبه مردود بالإجماع).
قلنا: دعوى رد ما ليس بمناسب بالإجماع، ممنوع، لأنك ما','5','319',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1607,'تعني بقولك: غير مناسب؟
إن أردت: أنه غير مناسب ولا مستلزم للمناسب فمسلم، وهذا ليس كذلك، وإن أردت أعم، حتى يشمل المستلزم للمناسب فممنوع؛ لأن الإجماع إنما انعقد فيما لم يستلزم المناسب، أما ما استلزمه فليس بمردود بالإجماع، بل هو محل النزاع.
الطريق السادس من الطرق الدالة على علية الوصف: الدوران وسماه ابن الحاجب تبعًا للآمدي، بالطرد، والعكس.
وهو: أن يحدث تعلق الحكم بحدوث وصف، وينعدم بعدمه.
فالوصف هو المدار، والحكم، هو الدائر وقد يوجد الدوران','5','320',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1608,'في محل واحد، كعصير العنب، فإنه كان مباحًا قبل إسكاره، فلما أسكر حرم، فلما زال الإسكار بصيرورته خلًّا عاد الحل.
فدار تعلق التحريم مع الإسكار وجودًا وعدمًا، وقد يكون في محلين كالقمح لما كان مطعومًا جرى فيه الربا، والكتاب لما لم يكن مطعومًا لم يكن فيه ربًا.
وهنا تحقيقات في الأصل.','5','321',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1609,'قال العبري: وأحسن التفاسير، يعني للدوران، تفسير الإمام العلامة النسفي- رحمه الله تعالى- وهو أن الدوران عبارة عن ترتيب الأثر على الشيء الذي له صلوح العلة مرة بعد أخرى.
قال العراقي: والمختار أن التعلق قديم كما تقدم.
وبه قال في المحصول في موضع.
قال السبكي: وهو المختار.
وعند المصنف أنه حادث تبعًا للمحصول في موضع.
قال السبكي: ويجب أن يحمل قولهم بحدوث التعلق على ظهور أثره','5','322',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1610,'لا على وجوده فلا يكون بين الكلامين مخالفة في المعنى.
ومثل ذلك بقول الشخص: أذنت لك أن تبيع عبدي هذا يوم الخميس، فالإذن قبل يوم الخميس موجود متعلق به، وأثره يظهر يوم الخميس.
وهو يفيد علية المدار ظنًّا.
وبه قال الأكثرون: منهم الإمام الرازي.
قال إمام الحرمين: وذهب القاضي أبو الطيب إلى أنه أعلى المسالك المظنونة، وكان يدعي إفضاءه إلى القطع.
ولا يلزم المستدل به بيان انتفاء ما هو أولى منه بإفادة العلية، بل','5','323',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1611,'يصح الاستدلال به مع إمكان الاستدلال بما هو أولى منه، بخلاف ما تقدم في الشبه.
وقيل: يفيد علية المدار قطعًا، وبه قال بعض المعتزلة.
وقيل: لا يفيد علية المدار لا ظنًّا ولا قطعًا.
واختاره الآمدي وابن الحاجب.
لنا على أن الدوران يفيد ظن علية الوصف: أن التعلق الحادث لا بد له من علية إما لحدوثه، أو لكونه الأحكام تابعة للمصالح.
(فعليته إما الوصف المدار، أو غيره) وغير المدار ليس بعلة، لأنه وجد قبله، أي: قبل الحكم، فليس بعلة له للتخلف، أي: لتخلف','5','324',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1612,'معلوله، وهو الحكم عنه.
وإلا أي: وإن لم توجد قبله، فالأصل عدمه، فلا يوجد عند الحكم؛ لأن الأصل بقاء ما كان (على ما كان، وإذا كان معدومًا عند حصول الحكم لا يكون علة له لامتناع اجتماع المعلول مع عدم العلة).
قال العبري: وفيه نظر.
وإذا كان للحكم علة وغير المدار لا يكون علة، ظن كون المدار علة فيكون الدوران مفيدًا لظن العلية، وهو المطلوب.
ولنا أيضًا أن علية بعض المدارات للحكم الدائر مع التخلف، أي: تخلف ذلك الدائر عن ذلك المدار في شيء من الصور لا تجتمع مع عدم علية بعضها الآخر؛ لأن ماهية الدوران من حيث هي:','5','325',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1613,'إما أن تدل على علية المدار للدائر، فيلزم علية هذه المدارات التي فرضنا عدم عليتها، لأنه حيث وجد الدوران، وجد علية المدار للدائر، فلا تجتمع علية بعض المدارات مع عدم علية بعضها.
أو لا تدل ماهية الدوران على علية المدار للدائر، فيلزم عدم علية تلك المدارات التي فرضنا عليتها للتخلف السالم عن المعارض الذي هو مقتضٍ لعدم العلية، وهو تخلف الدائر عن المدار مع سلامته عن المعارض، وهو دلالة ماهية الدوران على العلية.
فإن دلالة ماهية الدوران على العلية يقتضي علية المدار، والتخلف يقتضي عدم عليته فبينهما تنافٍ.
والأول: وهو علية بعض المدارات مع التخلف ثابت بالاتفاق، لأن شرب السقمونيا علة الإسهال مع تخلف الإسهال عنه، فانتفى الثاني، وهو عدم علية بعض المدارات للدائر.
ويلزم من انتفائه علية جميع المدارات، وهو المطلوب.','5','326',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1614,'وقيد عليه بعض المدارات بالتخلف المذكور، ليستدل به على عدم علية تلك، على تقدير عدم دلالة ماهية الدوران على العلية.
وعورض هذا الدليل بمثله، فقيل:
عدم علية بعض المدارات، مع التخلف في شيء من الصور لا تجتمع مع علية البعض.
لأن ماهية الدوران إن دلت على علية المدار كانت المدارات المتخلف عنها الدائرة علة للدليل الدال على عليتها.
وإن لم تدل عليها يلزم عدم علية هذه المدارات بالأصل السالم عن معارضة دلالة الدوران على العلية.
والأول ثابت ككثير من الاتفاقيات، فانتفى الثاني، وهو علية بعض المدارات، فلا يكون شيء منها علة، وهو المطلوب.
وأجيب: بأنا نختار أن الدوران دال على العلية.
قولكم: فيلزم علية تلك المدارات التي تخلف عنها الدائر لوجود','5','327',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1615,'الدليل عليه فاسد، لأنه لا يلزم من وجود الدليل وجود المدلول، لأن المدلول قد لا يثبت لمعارض من مانع أو فقد شرط.
وعلى قولكم: يلزم وجود المدلول بدون الدليل، وهو غير معقول فالترجيح معنا.
قيل: الدوران لا يفيد العلية مطلقًا، لأنه مركب من الاطراد.
وحاصله أنه لا يوجد في صورة بدون حكم، ووجوده بدون الحكم هو النقض، فيكون الاطراد هو السلامة عن النقض.
والنقض أحد مفسدات العلة، والسلامة عن مفسد واحد لا توجب انتفاء كل مفسد، ولا ينتفي الفساد إلا به.
سلمنا، لكن انتفاء كل مفسد لا يكفي في الصحة، فلا بد من مقتض للصحة من علة، وعدم المانع وحده لا يصلح علة،','5','328',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1616,'فعلم أن الطرد لا يؤثر.
والعكس لم يعتبر في العلل الشرعية على الصحيح، لأن عدم العلة مع وجود المعلول لعلة أخرى لا يقدح في علية العلية المعدومة، لجواز أن يكون للمعلول علتان على التعاقب، كالبول واللمس بالنسبة إلى الحدوث فلا يكون شيء منهما دالًا على العلية، فلا يكون الدوران دليلًا على العلة.
قلنا: كون كل واحد من الطرد والعكس، لا يدل على العلية عند انفراده، لا يلزم منه عدم دلالتهما عليها عند الاجتماع.
أعني الدوران، فإنه قد يكون للمجموع ما ليس لأجزائه، لأن المجموع يغاير كل واحد من أجزائه، فجاز أن يثبت لأحد المتغايرين ما لا يثبت للآخر كأجزاء العلة، فإن كلًّا منهما مفردًا غير مؤثر','5','329',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1617,'ومجموعهما مؤثر.
وعليه إشكال، وجوابه في الشرح.
الطريق السابع من الطرق الدالة على علية الوصف: التقسيم الحاصر، والتقسيم الغير الحاصر.
ويعبر عنهما بالسبر والتقسيم، ويعبر عنهما بالسبر فقط.
وهو حصر الأوصاف الموجودة في الأصل الصالحة للعلية.
أي: في بادئ الرأي في عدد، ثم إبطال بعضها.
وهو ما سوى الذي يدعى أنه العلة واحدًا كان أو أكثر.','5','330',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1618,'وعند التحقيق: الحصر راجع إلى التقسيم، والسبر إلى الإبطال.
والسبر لغة: الاختبار.
والتقسيم متقدم عليه في الوجود، لأنه: تعداد الأوصاف التي يتوهم صلاحيتها للتعليل، ثم يسبرها أي: يختبرها ليميز الصالح للتعليل من غيره.
لكن السبر وإن تأخر عن التقسيم فهو متقدم عليه أيضًا، لأنه أولًا يسبر المحل، هل فيه أوصاف أم لا؟
ثم يقسم ثم يسبر ثانيًا.
فقد تقدم السبر في اللفظ في كلامهم باعتبار السبر الأول.
ثم إن كان دائرًا بين النفي والإثبات فهو الحاصر.','5','331',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1619,'كقولنا: ولاية الإجبار في النكاح إما أن لا تعلل بشيء، أو تعلل بالبكارة أو الصغر أو غيرهما.
والكل باطل سوى الثاني، وهو أن تكون الولاية معللة بالبكارة.
فالأول والرابع للإجماع على أن الأحكام الشرعية معللة بعلل، وعلى أن غيرهما ليس بعلة للولاية، والثالث: وهو التعليل بالصغر، (فلأنها لو كانت معللة بالصغر)، لثبتت الولاية في البنت الصغيرة، لثبوت علتها، لكنها لا تثبت لقوله عليه الصلاة والسلام: «الأيم أحق بنفسها» رواه مسلم.','5','332',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1620,'فإنه يدل على نفي ولاية الإجبار عن الثيب صغيرة كانت أو كبيرة.
وإذا بطلت الأقسام- سوى الثاني- كان حقًّا.
فكانت الولاية معللة بالبكارة، وهو المطلوب.
والسبر غير الحاصر: هو الذي لا يكون دائرًا بين النفي والإثبات، ويسمى بالتقسيم المنتشر.
وخالف المصنف في التعبيرين، تنبيهًا على جواز إطلاق كل واحد من السبر والتقسيم، على كل واحد من القسمين.
والأول: قد يفيد القطع بالمطلوب، إذا كان الحصر في الأقسام، وإبطال غير المطلوب قطعيًّا، فيكون حجة في العقليات والشرعيات.','5','333',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1621,'وأما الثاني: فلا يفيد إلا الظن فلا يكون حجة في العقليات، بل في الشرعيات على الأصح.
وذلك مثل أن تقول علة حرمة الربا في البر مثلًا، إما الطعم، أو الكيل، أو القوت أو المال، والكل باطل- سوى الطعم- لكذا فتعين الطعم.
هذا إن لم يتعرض للإجماع على تعليل حكمه، وعلى حصر العلة في الأقسام، فإن تعرض لذلك كان قطعيًّا، قاله في المحصول.
وهنا بحثان:
الأول: أنه يكفي في بيان الحصر إذا منع أن يقول: بحثت، فلم أجد سوى هذه الأوصاف، ويصدق فيه لعدالته وتدينه، وذلك مما يغلب ظن عدم غيره.','5','334',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1622,'الثاني: أن المعترض له أن يبين وصفًا آخر، إذا بينه لزم المستدل إبطاله ولا يلزم انقطاعه.
فإن قيل: السبر غير الحاصر لا يفيد العلية، لأن حرمة الربا مثلًا يجوز أن تكون معللة بعلة، فإن الأحكام منها ما لا يعلل، بدليل أن علية العلة غير معللة، وإلا لزم التسلسل، فتكون هذه كتلك لا علة لها، ولئن سلم أن لها علة، ، فلم لا يجوز أن تكون العلة غير المذكورات؟ .
وما الدليل على الحصر في الأربعة؟
وإليه أشار بقوله: «أو العلة غيرها».
قلنا جوابًا عن الأول: إنا قد بينا أن الغالب على الأحكام','5','335',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1623,'الشرعية تعليلها بالمصالح تفضلًا وإحسانًا كما مر. فإذًا ظن (التعليل أغلب من ظن) عدم التعليل.
وعن الثاني: أن الأصل عدم غيرها، أي: عدم علة أخرى غير المذكورات، وذلك كاف في حصول الظن بغلبة أحدها.
الطريق الثامن من الطرق الدالة على علية الوصف: الطرد وهو مصدر بمعنى الاطراد، وهو أن يثبت معه، أي: مع الوصف الذي لم يعلم كونه مناسبًا ولا مستلزمًا للمناسب الحكم، فيما عدا المتنازع فيه، أي: في جميع الصور المغايرة لمحل النزاع، .
فيثبت ذلك الحكم مع ذلك الوصف فيه، أي: في محل النزاع.
أيضًا، إلحاقًا للمفرد بالأعم الأغلب.','5','336',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1624,'فإن استقراء الشرع دل على أن النادر في كل باب ملحق بالغالب.
فإذا رأينا الوصف في جميع الصور المغايرة لصورة النزاع مقارنًا للحكم، ثم رأينا الوصف حاصلًا في الفرع، وجب أن نستدل به على ثبوت الحكم فيه إلحاقًا لهذه الصورة الواحدة بسائر الصور.
وقال في جمع الجوامع: هو مقارنة الحكم للوصف، أي: من غير مناسبة، وهو تعريف القاضي أبي بكر، وهو أعم من تعريف المصنف، وقد سبق للمصنف نقله عنه كذلك، ولعله هنا عرف الطرد الذي يختار حجيته دون مطلق الطرد.','5','337',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1625,'وبهذا يشعر قوله: «وقيل تكفي مقارنته في صورة وهو ضعيف».
لأن الظن لا يحصل إلا بالتكرار.
وما اختاره المصنف من كون الطرد: يثبت الحكم بالشرط المتقدم ذهب إليه الغزالي في شفاء الغليل، والإمام الرازي.
وعزاه لكثير من فقهائنا.
ورده الآمدي وابن الحاجب تبعًا للمستصفى والأكثرين، كما حكاه عنهم إمام الحرمين وغيره.','5','338',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1626,'وبالغ القاضي أبو بكر في الإنكار على القائل به.
وقال ابن السمعاني في القواطع: (قياس المعنى تحقيق، والشبه تقريب، والطرد تحكم.
الطريق التاسع من الطرق) الدالة على علية الوصف: تنقيح المناط: أي تلخيص ما ناط الحكم به، أي ربط الشارع الحكم به وعلقه عليه وهو العلة.
والمناط: اسم مكان الإناطة، من ناط ينوط، إذا علق الشيء بالشيء وألصقه به.
فلما ربط الحكم بالعلة وعلق عليها، سميت مناطًا.
وتنقيح مناط العلة: بأن يبين المستدل إلغاء الفارق بين الأصل','5','339',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1627,'والفرع، وعدم تأثيره.
وحينئذ فيلزم اشتراكهما في الحكم.
مثاله: أن يقال لا فارق بين القتل بالمثقل والمحدد إلا كونه محددًا، وكونه محددًا لا مدخل له في العلية.
لأن المقصود من القصاص: حفظ النفس، فيكون القتل هو العلة، وقد وجد في المثقل فيجب فيه القصاص.
وقد يقال تعريفًا آخر لتنقيح المناط:
العلة إما المشترك بين الأصل والفرع وهو القتل العمد في مثالنا.
أو الميز للأصل عن الفرع، أي: الذي اختص به الأصل،','5','340',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1628,'وهو كونه قتلًا بالمحدد.
والثاني باطل لأن الفارق ملغي، فثبت الأول، وهو أن العلة هو المشترك بينهما فثبت الحكم في الفرع لحصوله فيه.
قال في المحصول: وهذا طريق جيد إلا أنه بعينه طريق السبر والتقسيم من غير تفاوت.
وأجيب: بالفرق بينهما: فإن هنا يتعرض لاختصاصه بالأصل، بخلاف طريق السبر، فإنه لا يتعرض فيه.
ولا يكفي في تنقيح المناط أن يقال: الحكم لا بد له من محل، ومحل الحكم إما المشترك بين الأصل والفرع، أو مميز الأصل عن الفرع لكذا فثبت الأول.','5','341',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1629,'لأنه لا يلزم من ثبوت المحل الذي هو للحكم، ثبوت الحكم الذي هو الحال، إذ يصدق هذا الرجل طويل، ولا يلزم ثبوت الطول لكل رجل، مع الاشتراك في محل الطول الذي هو الرجولية.
وجعل في جمع الجوامع: تنقيح المناط عبارة عن: الاجتهاد في الحذف والتعيين، وتحته قسمان:
أحدهما: أن يدل نص ظاهر على التعليل بوصف، فيحذف خصوص ذلك الوصف عن الاعتبار، ويناط الحكم بالأعم.
الثاني: أن يدل لفظ ظاهر على التعليل بمجموع أوصاف، فيحذف بعضها عن درجة الاعتبار. إما لأنه طردي، لو لثبوت الحكم مع بقية الأوصاف بدونه، ويناط بالباقي.
وجعل إلغاء الفارق، مسلكًا عاشرًا من مسالك العلة.
وقدم تنقيح المناط، لاعتضاده بظاهر في التعليل بمجموع','5','342',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1630,'أوصاف، لكن قد يكون دليل نفي الفارق قطعيًا فيكون أقوى من تنقيح المناط على رأي من غاير بينهما.
ثم ذكر أن إلغاء الفارق والدوران والطرد على القول به ترجع إلى نوع من الشبه، فإنها اشتركت في حصول الظن فيها في الجملة (لا مطلقًا) من غير تعيين جهة المصلحة المقصودة من شرع الحكم، لأنها لا تدرك بواحد منها بخلاف المناسبة وهذا حقيقة الشبه.
واعلم أن الفرق بين تنقيح المناط وتحقيق المناط وتخريج المناط: أن تنقيح المناط قد عرفته.
وأما تحقيق المناط فهو: تحقيق العلة المتفق عليها في الفرع، أي: إقامة الدليل على وجودها فيه، كما إذا اتفقنا على أن العلة في الربا هي القوت، ثم يختلفان في أن التبن هل هو مقتات حتى يجري فيه الربا.
وأما تخريج المناط: فهو استخراج علة معينة للحكم ببعض الطرق','5','343',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1631,'المتقدمة كالمناسبة وغيرها.
مثاله: استخراج الطعم أو القوت أو الكيل بالنسبة إلى تحريم الربا.
وقد قال بتنقيح المناط أكثر منكري القياس.
تنبيهك نبه به المصنف على طريقين آخرين ظن بعض الأصوليين أنهما مفيدان للعلة.
والمختار عدم إفادتهما.
أحدهما: ما قيل: (من أن) هذا الوصف علة، لأنه لا دليل على عدم عليته (وإذا انتفى الدليل على عدم عليته، انتفى عدم عليته وإذا) انتفى عدم عليته، فهو علة لامتناع ارتفاع النقيضين.','5','344',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1632,'قلنا: معارض بمثله، فنقول هذا الوصف ليس بعلة، إذ لو كان علة لوجد دليل على عليته، وإذا لا دليل لعليته فليس بعلة.
وحاصله: أن جعل العجز عن إثبات عدم عليته دليلًا على (عليته ليس بأولى من جعل العجز عن إثبات عليته دليلًا على) عدمها.
الثاني: ما قيل هذا الوصف علة، لأنه لو كان علة لسوينا بين الأصل والفرع، ولو سوينا بينهما لتأتى القياس المأمور به، إذ القياس المأمور به لا يتأتى بدون التسوية بينهما فيكون حمل الوصف على العلة مقدمة لتحقق المأمور به، (وتحقق المأمور به) واجب، فحمل الوصوف على العلية واجب، لما ثبت أن مقدمة الواجب واجب.','5','345',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1633,'قلنا: هو أي هذا القول دور، لأن تأتي القياس المأمور به متوقف على علية هذا الوصف، فلو توقف عليته على تأتي القياس المأمور به لزوم الدور.
الطرف الثاني
فيما يبطل العلية','5','346',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1634,'وهو ستة:
النقض، وعدم التأثير، والكسر، والقلب، والقول بالموجب، والفرق.

<span data-type="title" id=toc-252>الأول النقض</span>
ويسمى تخصيص العلة، وتخصيص الوصف: وهو إبداء الوصف','5','347',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1635,'الذي يُدَّعى أنه علة في محل ما يدون الحكم فيه، مثل أن تقول: لمن لم يبيت الصيام من الليل، تعرى أول صومه عن النية فلا يصح، كعراء أول صلاته منها، فيجعل عراء أول الصوم من النية علة لبطلانه، فينتقض بالتطوع بالصوم، فإنه يصح بدون التبييت فقد وجدت العلة، وهي العري بدون الحكم وهو عدم الصحة في النفل.
وقد اختلف في كون النقض قادحًا في العلية:
قيل: يقدح في علية الوصف مطلقًا، سواء كانت العلة منصوصة، أو مستنبطة، وسواء كان التخلف لمانع أو لا لمانع.
واختاره في جمع الجوامع وعزاه للشافعي- رضي الله تعالى','5','348',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1636,'عنه- ونقل عن اختيار الإمام الرازي.
وقال ابن السمعاني في القواطع: هو مذهب الشافعي (رضي الله تعالى عنه) وجميع أصحابه إلا القليل منهم.
وقال الغزالي في شفاء الغليل: إنه لا يعرف له (فيه نص).
وقيل: لا يقدح مطلقًا، ونسب لأكثر الحنفية والمالكية والحنابلة.
وقيل: لا يقدح في المنصوصة ويقدح في المستنبطة.
وقيل: لا يقدح حيث وجد مانع للتخلف أو فقد شرط، سواء','5','349',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1637,'كانت العلة مستنبطة أو منصوصة، وإلا فتقدح، وهو المختار، واختاره الصفي الهندي، وعزاه في جمع الجوامع لأكثر فقهائنا.
وهنا نفائس تراجع من الأصل.
واستدل لمختاره على عدم القدح لمانع، بقوله: «قياسًا على','5','350',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1638,'التخصيص» فكما أن التخصيص لا يقدح في كون الوصف علة، والجامع بينهما هو جمع الدليلين المتعارضين، فإن مقتضى العلة ثبوت الحكم في جميع محالها، ومقتضى المانع عدم ثبوته في بعض تلك الصور، فيجمع بينهما بأن يترتب الحكم على العلة فيما عدا صورة وجود المانع.
كما أن مقتضى العام، ثبوت حكمه في جميع أفراده، ومقتضى التخصيص عدم ثبوته في بعضها، وقد جمعنا بينهما.
فالنقض للمانع المعارض للعلة، كالتخصيص المعارض للعام.
فنسبة العلة إلى محالها كنسبة العام إلى أفراده، ونسبة النقل لمانع إلى العلة كنسبة المخصص إلى العام، كما أن العام بعد التخصيص حجة في الباقي، كذلك العلة مقتضية للحكم فيما عدا النقض لمانع.
وأيضًا: لأن الظن بعلية الوصف باق مع التخلف لمانع، لأن التخلف هنا، يسنده العقل إلى المانع، لا إلى عدم المقتضى، بخلاف ما لم يكن مانع، أي: بخلاف التخلف الذي لم يكن لمانع، فإنه يغلب على','5','351',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1639,'الظن أن التخلف لعدم المقتضى، وإذا بقي ظن علية الوصف مع المانع لم يكن قادحًا في عليته، إذ المراد بالعلية، : بقاء الظن بها وهو المطلوب.
قيل: من جهة القائلين بأن النقض يقدح مطلقًا.
العلة: ما يستلزم الحكم، والوصف المنقوض لمانع قبل انتفاء المانع لم يستلزم الحكم، وإذا ثبتت المقدمتان: فيقال: الوصف المنقوض لمانع لا يسلتزم الحكم والعلة تستلزم الحكم، لينتج: فالوصف المنقوض لمانع ليس بعلة.
وإذا كان التخلف لمانع قادحًا، فلغير مانع أولى، فكان التخلف مطلقًا قادحًا.','5','352',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1640,'قلنا: ليست العلة ما تستلزم الحكم، بل ما يغلب على ظنه وجود الحكم بمجرد النظر إليه، وإن لم يخطر المانع بالبال وجودًا أو عدمًا، أو لم يخطر بباله شيء منهما فيكون علة.
وتحقيقه: أن الذي يستلزم إنما هو العلل العقلية، لأنها علل بالذات، ويستلزم معلولها استلزامًا ذاتيًّا، وما بالذات لا ينفك، لا جرم: دل الانفكاك على عدم العلية وهذه علل بالوضع، فقد لا يستلزم معلولها، ولا يضر الانفكاك.
هذا كله إذا كان النقض غير وارد بطريق الاستثناء.
وأما الوارد استثناء عن القاعدة الكلية، أورد على جميع المذاهب فإنه لا يقدح كمسألة العرايا، فإنها وردت على جميع ما علل به،','5','353',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1641,'كالكيل، والطعم، والقوت، والمال، مع أنه غير قادح في علية أحدها؛ لأن الإجماع منعقد على أن حرمة الربا معللة بأحدها.
فلا يقدح الاستثناء في عليتها، لأن الإجماع أدل على العلية من النقض على عدم العلية، وإلا يلزم الإجماع على الخطأ، وإنه باطل.
ومقتضى كلام المصنف: أن الوارد على سبيل الاستثناء لا يقدح بلا خلاف، وبه صرح الخنجي والشيخ سعد الدين.
وكلام الإمام الرازي، وصاحب الحاصل، يقتضي أن فيه','5','354',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1642,'خلافًا.
قال العبري: والمعتمد قول الإمام لا غيره.
وهنا فوائد مبسوطة في الأصل.
وجوابه- أي: جواب النقض الذي يدفع به- بأحد أمور ثلاثة على ما ذكر المصنف.
الأول: منع المستدل وجود العلة في صورة النقض، لا عنادًا ومكابرة، بل لعدم قيد من القيود المعتبرة في علة الوصف.','5','355',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1643,'مثاله: أن يقال: من لم يبيت النية في رمضان يعرى أول صومه عنها فلا يصح، فينقض بالتطوع.
فيجاب بأن العلة في البطلان: هو عراء أول الصوم بقيد كونه واجبًا لا مطلق الصوم، وهذا القيد مفقود في التطوع فلم توجد العلة فيه.
وليس للمعترض إذا منع العلل وجود العلة في صورة النقض (لعدم قيد كما ذكرنا الدليل على وجوده، أي: على وجود الوصف بتمامه في صورة النقض).
وبه جزم الإمام الرازي تبعًا للأكثرين لأنه نقل من الاعتراض إلى الاستدلال.','5','356',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1644,'أو لأنه نقل من مسألة إلى مسألة، وهو غصب غير ملتفت إليه في اصطلاح أهل النظر.
ولو قال المعترض للمستدل ما دللت به على وجوده هنا، أي على وجود الوصف مع قيوده المعتبرة في الفرع دل عليه ثمة، أي: دل عليه في محل النقض، فينتقض دليل لوجوده في محل النقض، بدون مدلوله، وهو وجود العلة.
فهو نقل من نقض العلة إلى نقض الدليل الدال عليها وهل يسمع أم لا؟','5','357',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1645,'فيه خلاف:
قال الجدليون: لا يسمع هذا من المعترض، لأنه انتقل من نقض العلة إلى نقض دليلها.
ونقل عن اختيار الآمدي والصفي الهندي وصوبه في جمع الجوامع.
قال الإسنوي: وغيره: وهو الأقرب إلى الفهم من كلام المصنف، لأنه علل منع المعترض من الدليل على وجوده بأنه نقل فدل على أن النقل غير مقبول مطلقًا.
وقيل: يسمع.
وهو ظاهر كلام المحصول لأن القدح في (دليل العلة، قدح في) العلة، وهو مطلوب فلا انتقال.','5','358',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1646,'ولذا قال ابن الحاجب إن في عدم سماعه نظرًا.
هذا إذا ادعى انتقاض دليل العلية معينًا.
ولو ادعى أحد الأمرين فقال: يلزم إما انتقاض العلة أو انتقاض دليلها، وكيف كان فلا تثبت العلية كان مسموعًا بالاتفاق، فإن عدم الانتقال فيه ظاهر، قاله المحقق.
الثاني: من أجوبة النقض.
أن يدعى المعلل ثبوت الحكم في تلك الصورة التي نقض بها المعترض وإليه أشار بقوله: «أو دعوى الحكم» تحقيقًا: مثل أن يقول المستدل: السلم عقد معاوضة، فلا يشترط فيه التأجيل كالبيع','5','359',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1647,'فينتقض بالإجارة فإنها عقد معارضة، فلا يشترط فيها التأجيل.
فيقول: المستدل: ليس الأجل شرطًا لصحة عقد الإجارة أيضًا، بل التأجيل الذي فيها، إنما هو لاستقرار المعقود عليه، وهو الانتفاع بالعين، إذ لا يتصور استقرار المعقود عليه وهو الانتفاع بالعين.
إذ لا يتصور استقرار المنفعة المعدومة في الحال.
وإليه أشار بقوله: «قلنا: هناك أي: في الإجارة» لاستقرار المعقود عليه لا لصحة العقد، ولا يلزم من كون الشيء شرطًا في الاستقرار أن يكون شرطًا في الصحة.
ومحل صحة الجواب بذلك أن لا يكون انتفاؤه في صورة النقض، مذهب المستدل.
فأما إذا كان مذهب المستدل فليس له الجواب به. سواء كان مذهب المعترض أم لا.
واعلم أن دعوى الحكم في صورة النقض يدفعه، ولو كان ثبوت','5','360',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1648,'ذلك الحكم في النقض تقديرًا، كقولنا: رق الأم علة لرق الولد، فينقضه المعترض بولد المغرور بحرية الأم فإن رق الأم حاصل مع عدم رق الولد اتفاقًا.
ويقول المعلل: ثبت الرق في ولد المغرور تقديرًا، وإن لم يثبت ظاهرًا وإلا أي: لو لم يثبت رقه تقديرًا لم يجب عليه قيمته؛ لأن القيمة للرقيق لا للحر.
وتوقف الإمام الرازي في كون التقديري يدفع النقض.
الثالث: من أجوبة النقض: إظهار المانع.
وإليه أشار بقوله: «أو إظهار المانع»، يعني: بيان وجود معارض في','5','361',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1649,'محل النقض اقتضى نقيض الحكم: كنفي الوجوب للوجوب، أو ضده: كالحرمة للوجوب.
واقتضاء المانع لنقيض الحكم أو ضده إما لتحصيل مصلحة، وهي اللذة، أو ما هو وسيلة إليها، أو لدفع مفسدة، وهي الألم، أو ما هو وسيلة إليها.
أما تحصيل المصلحة، فكما في العرايا، فإن وصف الثمن والمثمن وهو كونهما مطعومين مع عدم العلم بمساواتهما علة للتحريم.
وقد تخلف التحريم عنه في العرايا وهي: بيع الرطب على رأس النخل بالتمر على الأرض بمساواته تخمينًا لوصف لا يقينًا لمانع، هو حاجة الفقير الذي لا يملك النخل، وله تمر إلى الرطب، ومالك النخل والرطب إلى التمر، وهو يقتضي عدم التحريم لتحصيل مصلحة هي التلذذ بالغذاء المحتاج إليه، وعدم التحريم نقيض التحريم.
وضربه الدية على العاقلة في الخطأ.
فإن القتل العمد العدوان سبب للزجر، وهو علة لضرب الدية على القاتل، ووجوب أدائهما عليه لينزجر به ويحصل به حفظ النفس، وقد','5','362',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1650,'تخلف الحكم عنه (في الخطأ) لمانع وهو الخطأ به وعدم القصد، لقوله عليه الصلاة والسلام كما روى عنه «رفع عن أمتي الخطأ».
بل هو يقتضي عدم وجوب أداء الدية عليه تحصيلًا لمصلحة حفظ ماله، وبقية الأمثلة في الأصل.
قال ابن الحاجب: هذا كله إذا لم تكن العلة منصوصة بظاهر عام، فإن كان فلا يجب إظهار المانع بعينه بل يحكم بتخصيصه بغير محل النقض، ويقدر المانع لجلب مصلحة أو دفع مفسدة، فيكون تخصيصًا للعموم لا للعلة.
وقال بعضهم: إنما بناه على مختاره في ذلك.
تنبيه: في بيان ما يكون نقضًا ويستحق الجواب، وما لا يكون كذلك، فالذي يكون نقضًا قسمان، لأن دعوى الحكم قد تكون في بعض الصور، وقد تكون في كلها.','5','363',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1651,'القسم الأول: وهو ما يكون في بعض الصور، وهو قد يكون في صورة معينة، أو صورة مبهمة.
وعلى التقديرين: فقد تكون الدعوة ثبوت الحكم أو نفيه.
فهذه أربع صور:
الأولى: دعوى ثبوته في صورة معينة، ينقضه النفي العام في جميع الصور، لأن الموجبة الجزئية تنتقض بالسالبة الكلية، ولا تنتقض بنفيه عن بعض الصور، لعدم تناقض الجزئيتين.
الثانية: دعوة ثبوت الحكم في صورة مبهمة، ينتقض بالنفي العام كالتي قبلها.
الثالثة: دعوى نفي الحكم عن صورة معينة، ينتقض بإثباته في','5','364',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1652,'جميع الصور لا في بعضها لما مر من تناقض الجزئية والكلية دون الجزئيتين.
مثاله: النبيذ غير نجس قياسًا على خل الزبيب، فينقضه: بأن كل نبيذ مسكر، وكل مسكر نجس.
الرابعة: دعوى نفي الحكم عن صورة مبهمة ينتقض بإثباته في كل صورة.
وإلى هذه الأقسام أشار بقوله: «دعوى ثبوت الحكم في صورة معينة أو مبهمة ينتقض بالإثبات أو النفي العامين».
وتقديره ثبوت الحكم في صورة معينة أو مبهمة ودعوى نفي الحكم عن صورة معينة أو مبهمة ينتقض بالإثبات العام، وهو من باب اللف والنشر على جعل الأول للثاني، والثاني للأول.','5','365',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1653,'القسم الثاني: وهو ما يكون الحكم في جميع الصور.
وهو إما النفي وإما الإثبات.
والأول: ينتقض بالإثبات في صورة معينة أو مبهمة.
والثاني: ينتقض بالنفي في صورة معينة أو مبهمة.
فهذه أربع صور أيضًا أشار إليها بقوله: «وبالعكس».
وتقدير كلامه: أن دعوة ثبوت الحكم العام تنتقض (بالنفي عن) صورة معينة أو مبهمة.
(لأن نقيض الموجبة الكلية سالبة جزئية.
ودعوى نفي حكم عام ينتقض بإثباته في صورة معينة أو مبهمة).
لأن نقيض السالبة الكلية موجبة جزئية.
ولا ينتقض الثبوت بالثبوت، ولا النفي بالنفي، ولا الكلي بالكلي، ولا الجزئي بالجزئي.
أي: السالبة الكلية لا تنتقض بالموجبة الكلية.
نعم يكون خلافًا ولا يكون نقضًا.','5','366',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1654,'وكلام المصنف إنما هو فيما إذا كان المقصود إثبات الحكم أو نفيه فقط.
أما إذا كان المقصود الأمرين معًا وجب كون الحكم مطردًا منعكسًا مع علته، كالحد مع المحدود، فينتقض بثبوت الحكم عند الوصف وبعدمه عند ثبوته.
قال العراقي: وقد يفهم من كلامه تخصيص الانتقاض بالإثبات والنفي العامين، وليس كذلك، فإن دعوى الثبوت في صورة معينة ينتقض بالنفي عن تلك الصورة وكذا العكس.
المبطل الثاني للعلية
عدم التأثير وعدم العكس.
وجمع بينهما لتقارب معناهما.
فعدم التأثير هو: بأن يبقى الحكم بعده، أي: بعد الوصف الذي','5','367',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1655,'فرض له علة.
وعدم العكس: هو: بأن ثبت الحكم في صورة أخرى مخالف للعلة الأولى.
وجعل في جمع الجوامع: عدم التأثير كون الوصف لا مناسبة فيه أي: لإثبات الحكم (ولا نفيه).
فتعريف المصنف التابع فيه للإمام الرازي أخص، ثم قال: ومن ثم أي: ومن أجل تفسيره بذلك اختص قدح عدم التأثير بقياس المعنى- دون الشبه والطردي- وبالمستنبطة المختلف فيها.','5','368',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1656,'وجعل عدم التأثير أربعة أقسام:
أحدهما: أن يكون ذلك في الوصف بكونه طرديًا.
ثانيها: أن يكون ذلك في الأصل بأن يستغني عنه بوصف آخر.
ثالثها: أن لا يكون ذلك في الحكم، وهو ثلاثة أنواع:
لأنه إما أن لا يكون لذكره فائدة أصلًا أو له فائدة','5','369',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1657,'ضرورية (أو غير ضرورية) ومع كونها ضرورية لا تأثير له في الحكم.
رابعها: أن يكون عدم التأثير في الفرع، لكون الوصف لا يطرد في جميع صور النزاع.
وأمثلة هذه الأقسام كلها مع فوائد حسنة في الأصل.
ثم قال المصنف: فالأول، أي: عدم التأثير، كما لو قيل: في عدم جواز بيع الغائب: مبيع لم يره المشتري، فلا يصح بيعه كالطير في','5','370',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1658,'الهواء بجامع عدم الرؤية.
فيقال: لا أثر لكونه غير مرئي، لأن عدم الصحة ثابت مع الرؤية لانتفاء القدرة على التسليم.
وحاصله: معارضته (في الأصل) أي: بإبداء علة أخرى وهي العجز عن التسليم.
ولذا بناه المصنف على جواز التعليل بعلتين.
والثاني: وهو عدم العكس.
مثل: الصبح لا يقصر، فلا يقدم أذانه على قوته كالمغرب.
فيقال: عدم القصر الذي جعل علة لمنع تقديم الأذان في الوقت ليس بعلة.
لأنه منتف فيما قصر من الصلاة كالظهر والعصر وذلك مع','5','371',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1659,'أن منع التقديم ثابت فيما قصر، فيكون منع التقديم معللًا بعلة أخرى غير عدم القصر.
وحاصل هذا المطالبة بالدليل على كون الوصف علة.
والأول: وهو عدم التأثير: يقدح في علية الوصف أي: يبطلها إن منعنا تعليل الواحد بالشخص بعلتين مختلفين، لأن الحكم لما بقي بعد عدم الوصف علمنا استغناؤه عنه، فلا يكون معللًا به، أما إذا لم يمنع ذلك فلا يقدح بقاء الحكم بعد الوصف في عليته لجواز حصوله بالعلة الأخرى.
قال العبري: هذا إذا فسرت العلة بالتامة، أما إذا فسرت بالمعرف أو بالعلة الناقصة، فإن ذلك لا يقدح في العلية.
وأما على التفسير بالمعرف: فلأنه جاز أن يكون معرف الشيء أمرًا يحدث بعد الشيء، وينعدم قلبه كالعالم للصانع.
وأما على التفسير بالناقصة: فلأن عدم المعلول معلول لعدم كل واحد من العلل الناقصة، وهو باق بعد زوال آية واحدة فرضت منها، مع أنه','5','372',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1660,'لا يلزم عدم عليته عدم تلك العلل لعدم المعلول.
والثاني: وهو عدم العكس: إنما يقدح في علية الوصف، حيث يمتنع تعليل الواحد بالنوع بعلتين مختلفتين؛ لأنه حينئذ يمتنع ثبوت مثل ذلك الحكم في صورة أخرى بعلة تخالف العلة الأولى.
أما إذا لم يمتنع فلا استحالة.
وقد علمت من هذا: أن الحكم الواحد إن بقي بعينه بعد زوال الوصف فهو الواحد بالشخص، وعدم التأثير.
وإن بقي مثله فعلة أخرى فهو الواحد بالنوع.
وعدم العكس، والعكس: انتفاء الحكم لانتفاء الوصف.
وذلك: أي تعليل الحكم الواحد بالشخص أو النوع، بعلتين','5','373',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1661,'مختلفتين جائز في العلل المنصوصة كالإيلاء واللعان.
فإنهما علتان مستقلتان في تحريم وطء الزوجة التي هي حكم واحد.
وفيه نظر: فإن الزوجة لا تحرم بالإيلاء، فلو مثل بالظهار مع الطلاق الرجعي لكان حسنًا.
فإنه يصح اجتماعهما في محل واحد، وهما علتان لتحريم الوطء وكذلك كل واحد من القتل والردة، علة لجواز القتل الذي هو','5','374',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1662,'حكم واحد، وإذا ثبت ذلك في الواحد بالشخص، ثبت ذلك في الواحد بالنوع، بطريق أولى، لأن كل من قال بالأول قال بالثاني بخلاف العكس، وهو من محاسن كلامه.
(وقال العراقي: ظاهر كلامه جريان الخلاف في الواحد بالنوع، والواحد بالشخص.
وقد صرح الآمدي والصفي الهندي وغيرهما: بأن محل الخلاف (في الواحد) بالشخص.
أما الواحد بالنوع: فيجوز تعليله بعلتين وأكثر بلا خلاف انتهى.','5','375',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1663,'وذلك كتعليل قتل زيد بردته، وعمرو بالقصاص، وخالد بالزنا بعد الإحصان).
ولا يجوز تعليل الحكم الواحد بعلتين مختلفتين (في العلل المستنبطة فإن الحكم فيها إنما يسند إلى ما ظن ثبوته لأجله، وحينئذ يمتنع التعليل بعلتين مختلفتين)، لأن ظن ثبوت الحكم لأحدهما أي: لأحد الوصفين يصرفه عن ثبوته للآخر.
وعن المجموع أي: وعن ثبوته لمجموع الوصفين فلا تكون العلة إلا واحدًا مثاله: إذا أعطى شيئًا لفقيه فقير، فالإعطاء يحتمل أن يكون (للفقه أو للفقر) أو لمجموعهما، فلا يجوز الاستناد إليهما لما قلنا.
وفيه نظر: فإن العلة ليست ما يفيد وجود أمر بل ما يفيد العلم بوجود أمر وليس بصارف، لأنها بمعنى الدليل، ويجوز اجتماع أدلة على مدلول واحد.','5','376',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1664,'وما اختاره المصنف (قال به ابن فورك) واختاره الغزالي والإمام الرازي.
وقال الجمهور: يجوز تعليل الحكم الواحد بعلتين فصاعدًا مطلقًا، وهو واقع، واختاره ابن الحاجب.
وقيل: ممتنع مطلقًا عقلًا وشرعًا، واختاره الآمدي، وحكاه عن القاضي أبي بكر وإمام الحرمين، وصححه في جمع الجوامع.
وقيل: يجوز عقلًا ويمتنع شرعًا.
قال الصفي الهندي: وهو المشهور عن إمام الحرمين.
ومحل الخلاف في الواحد بالشخص كما مر.','5','377',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1665,'قال بعضهم: ومحل الخلاف أيضًا في العلل الشرعية، أما العلل القطيعة فممتنع قطعًا.
والقائلون بوقوع تعدد العلل المستقلة: اتفقوا على أنها إذا ترتبت حصل الحكم بالأولى.
وأما إذا اجتمعت دفعة، كمن مس ولمس وبال معًا ففيه خلاف.
والمختار عند ابن الحاجب أن كل واحد علة مستقلة، والصحيح جواز تعليل حكمين بعلة واحد، وهنا تحقيقات في الشرح.
المبطل الثالث للعلة: الكسر','5','378',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1666,'وهو عدم تأثير أحد الجزأين للوصف الذي ظن كونه علة للحكم ونقض الجزء الآخر، فلا يرد إلا على الوصف المركب من جزأين، يكون أحدهما ملغي والآخر منقوضًا.
فهو في الحقيقة قدح في تمام العلة بعدم التأثير، وفي جزأيها بالنقض، قاله في المحصول.
وذلك كقولهم: صلاة الخوف صلاة يجب قضاؤها إجماعًا، فيجب أداؤها كصلاة الأمن.
فالعلة كونها صلاة يجب قضاؤها، فإذا قال ذلك.
قيل له: خصوصية الصلاة، وهو القيد الأول ملغى لا أثر','5','379',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1667,'له؛ لأن الحج كذلك، فإنه يجب أداؤه لوجوب قضائه مع أنه ليس بصلاة فبقي كونه عبادة يحب قضاؤها، وهو منقوض بصوم الحائض، فإنه عبادة يجب قضاؤها ولا يجوز أداؤها، فضلًا عن الوجوب.
لا يقال: حج التطوع يجب قضاؤه، ولا يجب أداؤه، لأنه إذا أحرم بالحج لا يجوز له الخروج من الحج، فوجب أداؤه في الجملة.
وتعريف المصنف للكسر تابع فيه الإمام الرازي.
وممن رجح القدح به الآمدي وابن الحاجب إلا أنهما سمياه: النقض المكسور.','5','380',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1668,'وعرفا الكسر: بوجود حكمة العلة، بدون العلة والحكم.
ويعبر عنه بنقض المعنى، أي: الحكمة.
والراجح أنه لا يقدح، لأنه لم يرد على العلة.
انتهى بحمد الله تعالى الجزء الخامس،
من كتاب
مختصر تيسير الوصول
شرح منهاج الأصول
ويتلوه إن شاء الله تعالى الجزء السادس،
وأوله:
المبطل الرابع للعلية
* * *','5','381',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1669,'المبطل الرابع للعلية
القلب: وهو في الاصطلاح: أن يربط المعترض خلاف قول المستدل على علته الذي استدل بها المستدل إلحاقًا بأصله، أي: بأصل المستدل الذي جعله (مقيسًا عليه) وهذا مختص بالقياس.','6','5',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1670,'وهو إما نفي مذهبه أي: مذهب المستدل صريحًا كقولهم: المسح ركن من أركان الوضوء فلا يكفي فيه أقل ما ينطلق عليه الاسم كالوجه.
فيقول القالب: المسح ركن منه- أي: من الوضوء- فلا يقدر بالربع كالوجه، فتقدير المسح بالربع مذهب المستدل، وقد نفاه القالب صريحًا.
وهذان الحكمان لا يتنافيان لذاتهما، لأنهما حصلا في الأصل الذي هو الوجه، ويتنافيان في الفرع الذي هو المسح لعارض.
وهو اتفاق الإمامين على أن الواجب: إما الربع، أو الأقل دون الاستيعاب أو يدل بالقلب على نفي مذهب المستدل ضمنًا. بأن يدل','6','6',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1671,'على نفي لازم من لوازم مذهبه، فيلزم نفي مذهبه، -وإن لم (يصرح به) كقولهم: بيع الغائب عقد كعاوضة، فيصح مع الجهل بالمعاوضة كالنكاح فيقول القالب: عقد معاوضة فلا يثبت فيه خيار الرؤية كالنكاح، فثبوت خيار الرؤية في بيع الغائب لازم لصحته، فإذا نفاه يلزم نفي صحته، وإن لم يصرح به.
ومنه أي: من هذا النوع- خلافًا للقاضي أبي بكر- قلب المساوة وهو أن يكون في أصل القياس حكمان، أحدهما: منتف من الفرع بالاتفاق بينهما والآخر مختلف فيه، فإذا أراد المستدل إثبات المختلف فيه بالقياس على الأصل فيقول المعترض: تجب التسوية بين','6','7',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1672,'الحكمين في الفرع قياسًا على الأصل، ويلزم من وجوب التسوية بينهما في لفرع انتفاء مذهبه.
وذلك كقولهم: المكره مالك للطلاق مكلف، فيقع طلاقه بإيقاعه إياه كالمختار.
فيقول القالب: المكره مكلف مالك للطلاق فنسوي بين إقراره بالطلاق وبين إيقاعه كالمختار، وإذا ثبتت التسوية بينهما.
فإما أن يكون في الوقوع وهو باطل إجماعًا، أو في عدمه، وهو المطلوب.
وسمي بهذا الإسم؛ لأن المساوة التي بين الحكمين في الأصل (إنما هي) في وقوعها، والتي أثبتها القالب في الفرع إنما هو في عدم الوقوع، فكأنه بدل تلك المساوة بهذه.
وقال المصنف: (ومنه) ولم يصرح بأنه من أفراد النوع الضمني، لأن','6','8',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1673,'الضمني ينفي لازم مذهب المستدل كما عرفت، وهذا لا ينفيه فلا يكون من أفراده، قاله العبري.
أو يدل بالقلب على إثبات مذهب المعترض.
مثاله: أن يقول في اشتراط الصوم في الاعتكاف: الاعتكاف لبث مخصوص. فلا يكون بمجرده قربة، كالوقوف بعرفه.
فإنه إنما صار قربه بانضمام عبادة أخرى إليه، وهى الإحرام.
فيقول القالب: الاعتكاف لبث مخصوص فلا يشترط فيه الصوم كالوقوف بعرفة، فعدم اشتراط الصوم الذي أثبته مذهب المعترض.
والفرق بين هذا والقسم الأول: أن القسم الأول هو الذي ينفي مذهب المستدل صريحًا، ولا يكون ذلك المنفي بعينه نفي مذهب القالب.
فإن قيل: القلب يمتنع تحقيقه، لأنه لما اشترط فيه اتحاد الأصل-','6','9',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1674,'المقيس عليه- مع الاختلاف في الحكم، لزم منه اجتماع الحكمين المتنافيين (في أصل واحد، والمتنافيان لا يجتمعان ضرورة.
قلنا: إن التنافي بين الحكمين ليس بحسب الذات، حتى يمتنع اجتماعهما في أصل واحد) بل حصل التنافي بينهما في الفرع بغرض الإجماع أي: بسبب عارض، وهو الإجماع الدال على أن الفرع لا يكون فيه إلا احدهما كما مر في التقرير، فلا يلزم ما ذكرت.
واعلم أن القلب يشبه فساد الوضع من حيث إنه إثبات نقيض الحكم بعلة المستدل، لأن حاصله إبطال وضع القياس المخصوص في إثبات الحكم المخصوص، وذلك لأن الجامع الذي يثبت به الحكم قد ثبت اعتباره بنص أو إجماع في نقيض الحكم.','6','10',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1675,'والوصف الواحد لا يثبت به النقيضان.
لكن الفرق بينهما أن في القلب ثبت نقيض الحكم بأصل المستدل.
وفساد الوضع ثبت بأصل آخر، فلو ذكره بأصله كان هو القلب.
تنبيه: القلب في الحقيقة معارضه، فإنها تسليم دليل الخصم، وإقامة دليل آخر على خلاف مقتضاه، وهذا بعينه صادق على القلب.
إلا أن علة المعارضة وأصلها، قد يكون مغايرًا لعلة المستدل وأصله.','6','11',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1676,'والقلب: يشترط فيه اتحاد الأصل والعلة.
وحاصله: أنه نوع من المعارضة مخصوص، فإنه الأصل والجامع فيه يشترك بين قياسي المستدل والمعارض.
وفائدة ذلك: أنه يجئ الخلاف في قبوله، ويكون المختار قبوله إلا أنه أولى بالقبول من المعارضة المحضة، لأن أبعد من الانتقال.
وقال الصفي الهندي: يمتاز القلب عن مطلق المعارضة بأمرين:
أحدهما: أنه لا يمكن فيه الزيادة في العلة، وفى سائر المعارضات يمكن.
الثاني: أنه لا يمكن منع وجود العلة في الأصل والفرع، لأن أصل','6','12',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1677,'القالب، وفروعه هما أصل المستدل (وفروعه، ويمكن ذلك في سائر المعارضات.
وظاهر كلام المصنف أن علة المعارضة وأصلها تكون مغايره لعلة المستدل) وأصله حتمًا فلا يكون القلب نوعًا منه.
وبه صرح العبري ونسبه لاصطلاح الأصوليين.
وما شرحته أولًا عليه. قال ابن الحاجب: إنه الحق.
المبطل الخامس للعلية
القول بالموجب- بفتح الجيم- أي: بما أوجبه الدليل.
وهو لا يختص بالقياس، بل يأتي في كل دليل.','6','13',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1678,'وهو عبارة عن: تسليم مقتضي قول المستدل مع بقاء الخلاف.
أي: تسليم مدلول الدليل مع بقاء النزاع.
وذلك دعوى نصيب الدليل في غير محل النزاع.
وعدهم له من مبطلات الدليل لا ينافي تسليمه، لأنه ليس المراد تسليم دلالة ذلك الدليل على ما ادعاه المستدل.
بل تسليم صحة ذلك الدليل لكن لا على قول المستدل، بل على خلافه فهو مبطل للعلة.
وسببه تخيل المستدل أن ما ذكره من نص أو قياس مستلزم لحكم المسألة المتنازع فيها مع أنه غير مستلزم لها.
فلا ينقطع النزاع بتسليمه إياه.','6','14',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1679,'والقول بالموجب على ما ذكره المنصف قسمان:
الأول: أن يقع في المنفي، وذلك إذا كان مطلوب المستدل نفي الحكم، واللازم من دليله شيء معين غير موجب لذلك، فيتمسك به لتوهمه أنه مأخذ الخصم.
الثاني: أن يقع في إثبات الحكم في النوع، واللازم من دليله.
ثبوته في صورٍ ما من الجنس.
مثاله في النفي: أن يقول المستدل يجب القصاص في القتل بالمثقل؛ لأن التفاوت في الوسيلة لا يمنع القصاص كالتفاوت','6','15',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1680,'في المتوسل إليه، فإنه لا يمنع وجوب القصاص اتفاقًا.
يعني أن المحدد والمثقل وسيلتان إلى القتل، فالتفاوت الذي بينهما، لا يمنع وجوب القصاص، كما لا يمنع في المتوسل إليه.
وهو أنواع الجراحات القاتلة.
فيقول المعترض: كون التفاوت في الوسيلة غير مانع من وجوب القصاص مسلم، ولكن لم لا يمنعه؟ أي لم لا يمنع القصاص غيره من أمر موجود في المثقل غير التفاوت؟
ثم لو بينا أن الموجب لوجوب القصاص، وهو إزهاق الروح بغير حق، قائم في صور القتل بالمثقل؟ ولا مانع فيه غيره، أي: غير التفاوت في الوسيلة، لكنا مقرين بالانقطاع في الاستدلال الأول، لأنه ظهر من بيان هذا أنه لم يكن ما ذكرنا أولًا تمام الدليل، بل أحد','6','16',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1681,'أجزائه.
وقد اختلف في أن المعترض إذا قال: ليس هذا مأخذي هل يصدق؟
والصحيح أنه يصدق، لأنه أعرف بمذهبه ومذهب إمامه.
واعلم أن أكثر القول بالموجب من هذا القبيل وهو ما يقع لاشتباه المأخذ لخفاء مأخذ الأحكام (وقل ما) يقع لاشتباه محل الخلاف لشهرته (ولتقدم التحرير غالبًا.
ومثال القول بالموجب): في جانب الثبوت: قولهم: في وجوب زكاة الخيل: حيوان يسابق عليها فتجب الزكاة فيها كالإبل.','6','17',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1682,'فيقول المعترض: وجوب الزكاة في الخيل مسلم، ولكن في زكاة التجارة، والخلاف إنما وقع في نوع من الزكاة وهي زكاة العين.
ومقتضي دليلكم: وجوب مطلق الزكاة، ولا يلزم من إثبات المطلق إثبات جميع أنواعه حتى يلزم هذا النوع الذي وقع فيه الخلاف.
واعلم أن المستدل قد يسكت عن مقدمة خشية أن يمنعها الخصم فيحتاج إلى الاستدلال عليها فيقع في أمر آخر وهو أن يقول المعترض بالموجب.
مثاله في الوضوء: ما ثبت قربة فشرطه النية كالصلاة، ويسكت عن الصغرى، فلا يقول: الوضوء يثبت قربة.
فيقول المعترض، مسلم، ومن أين يلزم أن يكون الوضوء يثبت قربة.
قال الجدليون: القول بالموجب فيه انقطاع أحد المتناظرين، إذا لو بين أن المثبت مدعاة، أو ملزومة.','6','18',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1683,'أو المبطل، مأخذ الخصم، أو الصغرى حق انقطع المعترض إذا لم يبق بعده إلا تسليم المطلوب، وإلا انقطع المستدل، إذ قد ظهر عدم إفضاء دليله إلى مطلوبه.
ونظر فيه ابن الحاجب، بما هو مبين في الأصل.
المبطل السادس للعلية
الفرق: بين الأصل والفرع: وهو جعل المعترض تعين الأصل الذي للقياس، أي: الخصوصية التي فيه علة لحكمه.
أو جعل تعين الفرع، أي: خصوصيته مانعًا من ثبوت حكم الأصل','6','19',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1684,'فيه.
مثال الأول: إذا قيل: الخارج من غير السبيلين ناقض للوضوء كالخارج منهما، بجامع خروج النجاسة.
فيقال: الفرق بينهما أن الخصوصية التي في الأصل، وهي خروج النجاسة من السبيلين، هي العلة في انتقاض الوضوء لا مطلق خروجها.
ومثال الثاني: قول الحنفي: يجب القصاص على المسلم بقتل الذمي قياسًا على غير المسلم بجامع القتل العمد العدوان فيهما.
فيقول المعترض: الفرق بينهما أن تعين الفرع وهو كونه مسلمًا، مانع لوجوب القصاص عليه لشرفه بالإسلام.
والأول وهو الفرق بخصوص الأصل يؤثر في منع علية الوصف المشترك بينه وبين الفرع، حيث لم يجز التعليل للحكم الواحد بعلتين','6','20',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1685,'مختلفتين، وذلك لأن حكم الأصل إذا كان معللًا بما يختص به لا يكون معللًا بالمشترك بينه وبين الفرع، ولا يلزم تعليل الحكم الواحد بعلتين مختلفتين، (وهو غير جائز في هذه العلل، ولا يؤثر في منع عليته، حيث جاز التعليل بمختلفتين) لأن تعليل حكم الأصل بالمختص به لا ينافي تعليله بالمشترك بينه وبين الفرع، إذ لا يلزم منه إلا التعليل بمختلفين وذلك جائز في هذه العلل.
والثني وهو الفرق يجعل الفرق بجعل الفرع مانعًا من ثبوت الحكم فيه يؤثر عند من جعل النقض مع المانع قادحًا في العلة، لأن العلة عنده ما يستلزم الحكم، والوصف المشترك مع هذا المانع لا يستلزم الحكم فلا يكون علة عنده.
وعند من لم يجعل النقض مع المانع قادحًا في العلة لا يؤثر في منع عليته إذ لا يلزم منه إلا تخلف الحكم المانع وهو غير قادح عنده كذا','6','21',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1686,'شرحه العبري وغيره.
وعلم من هذا البيان أن مختار المصنف قدح النوع الأول في المستنبطة دون المنصوصة، وعدم قدح النوع الثاني مطلقًا.
وقال الإسنوي: بناء الأول على التعليل بعلتين صحيح، وأما الثاني فلا، بل يؤثر مطلقًا في دفع كلام المستدل.
أما على القول بأن النقض مع المانع قادحًا فواضح.
وأما على القول بأنه غير قادح، فلأن العلة وإن كانت صحيحة، لكن قام بالفرع، وهو المسلم في مثالنا، مانع يمنع من ترتب مقتضاها عليه، لأن الغرض أن ذلك من باب التخلف لمانع، ويستحيل وجود الشيء مع مقارنة المانع معه.','6','22',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1687,'وحينئذ فيحصل للمعترض مقصوده، وهو عدم إيجاب القصاص.
قال: ولم يتعرض الإمام وأتباعه، ولا ابن الحاجب لهذا البناء أصلًا.
نعم إطلاق الإمام أن قبول الفرق مبني على تعليل الحكم الواحد بعلتين.
وإذا حملنا كلامه على الفرق بتعيين الأصل لم يرد عليه شيء انتهى.
قال العراقي: كذا أورده شيخنا جمال الدين رحمه الله تعالى وغيره.
وفيه نظر، لأن المعترض لم يحصل له مقصوده من جهة إفساد علة المستدل، بل من جهة أخرى، والكلام إنما هو في إفساد العلة بالفرق ولم يحصل ذلك، فتأمل.','6','23',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1688,'وجعل في جمع الجوامع القدح بالفرق راجع إلى المعارضة في الأصل أو الفرع، فحكمه في الرد والقبول حكمه.
وذهب كثير من المتقدمين إلى أنه معارضة في الصل والفرع معًا، حتى لو اقتصر على أحدهما لا يكون فرقًا.
وحكى بعضهم في قبوله قولان أصحهما قبوله.
وحكاه إمام الحرمين عن جماهير الفقهاء.
وقال الشيخ أبو إسحاق في الملخص: إنه أفقه شيء يجري في النظر.
والثاني: أنه مردود لا يقدح، وعزاه ابن السمعاني للمحققين، وحاصل كلام ابن الحاجب مبين في الأصل مع فوائد جمة.','6','24',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1689,'قال الآمدي: وأول ما يجب الابتداء به من الأسئلة: الاستفسار، ثم فساد الاعتبار، وهو: منع المعترض من تمكنه من القياس مطلقًا، كأنه يدعي أن القياس لا يعتبر في تلك المسألة ثم فساد الوضع، وهو: منع تمكنه من القياس المخصوص، ثم منع الحكم في الأصل، ثم منع وجود العلة فيه، ثم الأسئلة المتعلقة بالعلة كالمطالبة، وعدم التأثير، والقدح في المناسبة، والتقسيم.
وكون الوصف غير ظاهر ولا منضبط، وكونه غير مفض إلى','6','25',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1690,'المقصود، ثم النقض، ثم الكسر، ثم المعارضة في الأصل، ثم ما يتعلق بالفرع كمنع وجود العلة فيه، ومخالفة حكمه لحكم الأصل، واختلاف الضابط، والحكمة، والمعارضة في الفرع والقلب، ثم القول بالموجب.

<span data-type="title" id=toc-260>الطرف الثالث
في أقسام العلة</span>
وما يصح به التعليل منها، وما لا يصح.
إذا ثبت حكم في محل فنقول:','6','26',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1691,'علة ذلك الحكم: إما محله كتعليل حرمة الربا في النقدين بجوهرهما، أي: بكونهما ذهبًا وفضة، وهو نفس المحل.
أو العلة جزؤه، أي: جزء ذلك المحل: كتعليل خيار الرؤية في بيع الغائب بكونه عقد معاوضة.
أو العلة أمر خارج عنه.
وذلك الخارج إما عقلي، وهو الوصف الذي لا يتوقف على وضع.
وهو ثلاثة أقسام:
حقيقي: وهو ما يمكن تعقله باعتبار نفسه، ولا يتوقف على وضع، كتعليل الربا في البر بالطعم، فالطعم مدرك بالحس، وهو أمر','6','27',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1692,'حقيقي لا يتوقف معقوليته على معقولية غيره.
ويعتبر فيه، أن يكون ظاهرًا لا خفيًا، ومنضبطًا يتميز عن غيره.
أو إضافي: كتعليل ولاية الإجبار بالأبوة.
أو سلبي: كتعليل عدم وقوع طلاق المكره بعدم الرضا.
أو الخارج: شرعي.
إن كان الواضع الشرع، كتعليل جواز رهن المشاع بجواز بيعه.
والخارج لغوي: إن كان الواضع الرعب على رأي، كتعليل حرمة النبيذ بكونه مسمى بالخمر كالمعتصر من العنب.
ونفي الخارج العرفي، ذكره في المحصول.','6','28',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1693,'وكل واحدة من هذه العلل، إما متعدية: وهي التي تتجاوز عن محل الحكم المنصوص، كتعليل حكمه بما يشاركه فيه غيره.
أو قاصرة: وهي التي تتجاوز عنه كتعليل حكمه بمحله.
وعلى كل واحد من التقديرات المذكورة، إما أن تكون العلة بسيطة، كالأمثال المذكورة، أو مركبه بمن الصفة الحقيقية.
والإضافية كتعليل عدم قتل الأب قتل صدر من الأب فلا يجب به القصاص.','6','29',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1694,'فالقتل حقيقي، والأبوة إضافية.
أو من الحقيقية والسلبية، كتعليل وجوب القصاص على قاتل الذمي، بكونه قتلًا بغير حق. أو من الإضافة والسلب، كتعليل القصاص بالقتل بالمثقل، بكونه بغير حق. أو من الثلاثة كتعليل وجوب القصاص بالقتل بالمثقل بكونه قتلا عمدًا بغير حق، والمختار جواز التعليل بكل واحد من هذه الأقسام.
وقيل: لا يعلل بالمحل وكذا جزؤه الخاص، ونقله الآمدي عن الأكثرين.
لأن محل الحكم قابل له، فلا يكون علة له وإلا يلزم','6','30',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1695,'كون الشيء الواحد قابًلا للشيء وفاعًلا له.
وأنه باطل؛ لأن نسبة القابل للمقبول بالإمكان، ونسبة الفاعل إلي المفعول بالوجوب، وبين الوجوب والإمكان تناف.
وإليه أشار بقوله: "القابل لا يفعل".
قلنا: لا نسلم أن القابل للشيء لا يكون فاعلًا له، ولا أن الوجوب والإمكان متنافيان، وإنما يلزم أن لو كان المراد من الإمكان هو الإمكان الخاص، وليس كذلك، بل المراد به الإمكان العام.
ومع هذا كله فالعلة الشرعية هي: المعرف للحكم لا المؤثر فيه.
فلا يلزم من تعليل الحكم به كون الشيء قابلًا وفاعلًا لشيء واحد.','6','31',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1696,'وما اختاره المنصف نقل عن الأكثرين.
وقيل: يجوز التعليل بالمحل في العلة القاصرة، إذ لا استبعاد في أن يقول الشارع: حرمت الخمر لكونها خمرًا، فإن كونها خمرًا يناسب حرمته.
ولا يجوز في المتعدية، إذا يستحيل حصول مورد النص بخصوصه في غيره، واختاره الإمام والآمدي وابن الحاجب والصفي الهندي.
قال العبري: وهو الحق.
(واعلم أن التعليل قد يكون بالوصف الضابط المشتمل على الحكمة','6','32',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1697,'المناسبة للحكم، كجواز تعليل القصر بالسفر المشتمل على المشقة والمشقة حكمة مناسبة للحكم الذي هو القصر، وجعل السفر علة وإن كان المقصود المشقة، لكونها يعسر ضبطها لاختلاف مراتبها بحسب الأشخاص والأحوال، وليس كل قدر منها يوجب الترخيص وإلا سقطت العبادات.
وتعيين القدر منها الذي توجبه متعذر، فضبطت بوصف ظاهر منضبط هو السفر، فجعل أمارة لها، فلذا كان التعليل بالوصف الضابط متفقًا عليه.
وأما التعليل بمجرد الحكمة فقد اختلف فيه:
فجوزه المنصف مطلقًا تبعًا للإمام الرازي).','6','33',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1698,'وقيل: لا يعلل الحكم بالحكم الغير المضبوطة كالمصالح والمفاسد، وبه قال الأكثرون، كما حكاه الآمدي، لأنه لا يعلم وجود القدر الحاصل من المصلحة الذي ترتب الحكم عليه في الأصل في الفرع، لكون المصلحة غير مضبوطة، ولكون المصالح والمفاسد من الأمور الباطنة التي لا يمكن الوقوف على مقاديرها، ولا امتياز لكل واحدة من مراتبها التي لا نهاية لها عن المرتبة الأخرى.
وحينئذ لا يجوز للمستدل إثبات حكم الفرع بها، فلا يجوز التعليل بها.','6','34',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1699,'قلنا: لو لم يجز التعليل بتلك الحكم، لامتناع الاطلاع على القدر الذي نيط به الحكم، لما جاز التعليل بالوصف المناسب المشتمل عليها، (لعدم العلم بالقدر الحاصل فيه من المصلحة حينئذ). لكنهم يستدلون علي علية الوصف باشتماله علي المصلحة.
فالدال على عليته: إما اشتماله على مطلق المصلحة وهو باطل، وإلا لكان كل وصف مشتمل على أي مصلحة كانت علة لذلك الحكم. أو اشتماله على مصلحة معينة. فإن لم يمكن الاطلاع عليها، لم يمكن الاستدلال علي علية الوصف بكونه مشتملًا عليها، لأن العلم باشتمال الوصف عليها من غير العلم بها ممتنع.
وإذا أمكن الاستدلال أمكن الاطلاع على تلك الحكم فيصح التعليق بها، لأنه إذا ظن أن الحكم في الأصل إنما ثبت لمصلحة، وتلك','6','35',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1700,'المصلحة وجدت في الفرع، يحصل من هذين الظنين ظن الحكم) في الفرع لا محالة.
وإليه أشار بقوله: فإذا حصل ظن أن الحكم، أي: في الأصل لمصلحة، وأنها وجدت في الفرع لزم بالضرورة أن يحصل ظن الحكم فيه، أي: في الفرع.
ولك أن تمنع الملازمة لجواز التعليل بالمظنة، وإن انتفت المئنة كما في الملك المرفه في السفر، فإنه يجوز له القصر مع انتفاء','6','36',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1701,'المشقة.
واعلم أنه يجوز التعليل بما لا يطلع على حكمته، لأنه لا يخلو عنها في نفس الأمر، ويسمى أمارة.
واختلف فيما قطع بانتفائها في بعض الصور، كاستبراء الصغيرة.
فإن الاستبراء لتيقن براءة الرحم، وهو منتف فيها.
فقال الغزالى، وتلميذه محمد بن يحيى: (ثبتت الحكمة) للمظنة.','6','37',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1702,'وقال الجدليون: لا تثبت لانتفاء الحكمة، لإنها روح العلة، وهذا يرشدك إلي تحقيق ماتقدم.
واعلم أنه يجوز تعليل الحكم الثبوتي، كالتحرير بالإسكار.
والعدمي بالعدمي، كعدم نفاذ التصرف (بعدم العقل. والعدمي بالوجودي، كعدم نفاذ التصرف) بالإسراف.
وأما عكسه: وهو تعليل الحكم الوجودي بالوصف العدمي، فقد اختلف فيه.
والأكثر: على جوازه، منهم المنصف، واختاره الإمام الرازي هنا؛','6','38',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1703,'لأن دوران الحكم قد يحصل مع بعض العدميات.
وقيل: العدم لا يعلل به، واختاره الآمدي، ابن الحاجب، وصاحب جمع الجوامع، والإمام الرازي في كلامه على الدوران، لأن الأعدام لا تتميز عن غيرها، (وكل ما) لا يتميز عن غيره لا يكون علة، فلا تكون الأعدام علة.
أما كون الأعدام لا تتميز عن غيرها، فلأن المتميز عن غيره لا بد أن يكون موصوفًا بصفة التمييز، والموصوف بصفة التميز ثابت، والعدم نفي محض.
وأما ن (كل ما) لا يتميز عن غيره لا يكون عليه فلأن','6','39',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1704,'الشيء الذي يكون علة لا بد أن يكون متميزًا عما لا يكون علة وإلا لم يعرف كونه علة.
وأيضًا: الأوصاف منها ما يصلح للعلية، ومنها ما لا يصلح لها، فيجب على المجتهد سبرها ليتميز له الصالح من غيره، وليس المجتهد سبرها- أي: الأعدام -بالإجماع، ويجب عليه سبر الأوصاف الصالحة للعلية كما مر.
فلا تكون الأعدام من الأوصاف الصالحة للعلية، وإذا ثبتت هاتان المتقدمتان ثبت أن الوصف السلبي لا يعلل به وهو المطلوب.
[قلنا: الجواب عن الأول: أنا لا نسلم أن الأعدام لا تتميز بل تقبل التمييز إذا كانت من الأعدام المضافة، فإن عدم اللازم متميز عن عدم الملزوم، فإنا نحكم بأن عدم اللازم يستلزم عدم الملزوم دون العكس.','6','40',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1705,'وما استدلوا به فجوابه: أن الموصوف بالتمييز إنما يستدعى الثبوت في النفس فقط، والعدم له ثبوت فيه.
نعم، الأعدام المطلقة ليس لها تمييز، ونحن نسلم امتناع التعليل بها.
والجواب عن الثاني: أنا لا نسلم أن المجتهد يجب عليه سبر الأوصاف مطلقًا، سواء كانت متناهية أو غير متناهية، بل يجب سبر المتناهية دون غيرها لامتناع سبرها. وحينئذ، فنقول: ليس على المجتهد سبرها-أي: الأعدام- فسقطت عنه لعدم تناهيها لا لعدم صلوحها للعلة، فلا يتم ما ذكرتم. واعلم أن الخلاف في تعليل الثبوتى بالعدمي يجرى في كون العدم جزء علة أيضًا.
وقد ذكره ابن الحاجب، وأهمله المنصف لوضوحه، بل قد يدعي دخوله في كلامه؛ لأنه متى كان جزء العلة عدمًا، فقد صدق','6','41',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1706,'التعليل بالعدم
والوصف الإضافي: وهو ما يعقل باعتبار غيره، كالأبوة والبنوة والمعية والقبلية والبعدية، عدمي.
فيجري فيه الخلاف في تعليل الثبوتى بالعدمي.
تنبيه: ظاهر كلام المنصف أن القائلين بمنع التعليل بالعلة العدمية، قائلون به سواء كان المعلل به عدميًا، أم لا. وتقدم ما فيه.','6','42',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1707,'ويجوز تعليل الحكم الشرعي بالحكم الشرعي، عند المنصف تبعًا للإمام الرازي، أما بمعنى المارة المجردة فظاهر.
وأما بمعني الباعث: فلأن الحكم قد يدور مع حكم آخر، والدوران يفيد ظن العلية.
واختار ابن الحاجب: أنه إن كان باعثًا على حكم الأصل لتحصيل مصلحة يقتضيها حكم الأصل جاز، وإن كان لدفع مفسدة يقتضيها حكم الأصل فلا. وإيضاحه في الأصل.
وقيل: لا يجوز تعليل الحكم الشرعي بالحكم الشرعي مطلقًا، لأن الحكم الشرعي الذي هو علة يتحمل','6','43',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1708,'الحكم الذي جعل معلولاً، ويتجمل أن يكون متأخرًا عنه، ويتحمل أن يكون مقارنًا له.
والتقدير الأول ينافي التعليل، لأن تقدم العلة على المعلول يقتضي تخلفه عنها.
وكذا الثاني: لأن تقدم المعلول على العلة ينافي عليتها لامتناع تأخر العلة عن المعلول.
والثالث: يتحمل التعليل وعدمه، إذ على تقدير المقارنة، يجوز أن تكون العلة هو أو غيره.
وعلى هذا إنما يجوز التعليل بالحكم المقارن، وهو أحد التقديرات الثلاث التي لا يكون علة عليها، فيكون مرجوحًا بالنسبة إليها. والعبرة في الشرع بالراجح دون المرجوح، فوجب الحكم في الشرع بأنه ليس بعلة، فيمتنع التعليل به.','6','44',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1709,'قلنا: لا نسلم اختصاصه بالمقارنة، بل يجوز به، ويجوز بالمتأخر، ولا ينافي العلية، لأنه- أي: الحكم الشرعي المتأخر الذي فرضناه علة- معرف، والمعرف للشيء يجوز أن يتأخر عنه كالعالم للصانع. ويجوز التعليق بالعلل القاصرة، كما تقدم.','6','45',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1710,'وبه، قال الأكثرون، منهم الإمام مالك والشافعي وأحمد- رضي الله تعالى عنهم.
وقالت الحنفية: لا يعلل بالقاصرة، أي: إن كانت مستنبطه كما قيد به القاضي، والآمدي، وابن الحاجب وغيرهم.
لكن حكى القاضي عبد الوهاب في الملخص قولاً بالمنع مطلقًا، وعزاه لأكثر فقهاء العراق.','6','46',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1711,'قال العراقي: وهو غريب.
وقالوا: لا يعلل بالقاصرة لعدم الفائدة في التعليل بها، إذ الفائدة منحصرة في إثبات الحكم بها وهو منتف.
وإلا فإما في الأصل أو الفرع.
أما الأصل فالحكم فيه ثبت بغيرها من نص أو إجماع.
وأما الفرع فالمفروض أنه لا فرع، وما لا فائدة فيه لا يجوز التعليل به شرعًا، ولا عقلا لكونه عبثًا
قلنا: لا نسلم انحصار الفائدة في معرفة الحكم الأصل والفرع.','6','47',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1712,'فإن معرفة كونه -أي: الحكم الشرعي- واقعًا على وجه المصلحة، وفق الحكمة، لتكون النفوس إلى قبوله أميل.
[وهذه] فائدة معتبرة، وله فوائد أخرى:
منها: أنه إذا عرف قصورها عرف امتناع أن تلحق بتلك المنصوص عليه غيره.
ومنها: أنها تقوي النص وتعضده، قاله القاضي أبو بكر.
قال: وكذا كل دليلين اجتمعا في مسألة، فيكون الحكم ثابتًا بالنص والعلة معًا.
ويحمل هذا على ما إذا كان النص ظاهرًا.
فأما إذا كان قاطعًا فلا يقويه، صرح به في البرهان','6','48',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1713,'ومنها: أن المكلف يفعل ذلك لأجل تلك العلة، فيحصل له أجر ذلك الفعل للامتثان وأجر قصد الفعل لأجلها، فيفعل المأمور به لكونه أمرًا وللعلة، قال السبكي.
قال العراقي: ومقتضى جواب المصنف تسليم أن حكم الأصل لا يمكن ثبوته بالعلة، وهو قول الحنفية.
لكن نقل الإمام والآمدي وابن الحاجب عن أصحابنا ثبوته بها وهو دافع للدليل من أصله.
ولنا: على جواز التعليل بالعلة القاصرة: أن صحة العلة ليست موقوفة على تعديتها إلى الفرع، وذلك أن التعدية- أي: تعدية','6','49',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1714,'العلة إلى الفرع- توقفت على صحة العلية في نفسها، فلو توقفت هي -أي: صحة العلية في نفسها -عليها- أي: على تعديتها إلى الفرع- لزم الدور.
فإذا لم تتوقف صحتها في نفسها على تعديتها إلى الفرع صحت العلية في نفسها، سواء كانت متعدية أو قاصرة.
وقد يقال: التعدية شرط العلية، بمعنى وجود الوصف فيغيره، ومشروط بها، يعني وجود الحكم به في غيره، فتوهم لزوم الدور غلط نشأ من اشتراك لفظ التعدية بين ثبوت الوصف في محل آخر، وبين ثبوت الحكم في محل آخر، والموقوف على العلية هو الثاني؛ والموقوف عليه هو الأول.
واعلم أن الفرق بين العلة القاصرة، والتعليل بالمحل: أن القاصرة أعم من المحل، لأن المحل ما وضع له اللفظ كالبر والخبر.','6','50',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1715,'والقاصرة: وصف اشتمل عليه المحل لم يوضع له اللفظ كالنقدية.
ويجوز التعليل بمجرد الاسم: اللقب الجامد.
على المختار في جمع الجوامع تبعًا للشيخ أبي إسحاق الشيرازي كتعليل طهورية الماء بأنه ماء.
وقيل: بالمنع، وبه قال الإمام الرازي، ونقل الاتفاق عليه.
ويجوز التعليل بالاسم المشتق،','6','51',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1716,'وأما التعليل بالمشتق من الصفة، فقال ابن السمعاني: هو من علل الأشبه الصورية.
فمن احتج بالشبه الصوري احتج به.
والفرق بين المحل والاسم.
قيل: إن المراد التعليل بالتسمية، نحو: حرمت الخمر لتسميتها خمراً، إذ التسمية لا تأثر لها بخلاف المعنى المستفاد من المحل بإشارة أو تنبيه.
ويجوز أن تكون العلة مركبة، وقد وقع ذلك كالقتل العمد العدوان في القصاص كما مر.','6','52',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1717,'وقيل: لا يجوز، لأنه لو علل بالوصف المركب، لكان عدم كل واحد من أجزائه علة تامة لعدم عليته؛ لأن عدم كل واحد منها علة لارتفاع عليته، فإذا انتفى جزء واحد، تنتفي العلية الضرورية.
ثم إذا انتفى جزء آخر، فإن لم تنتف عليته يلزم التخلف للمعلول عن علته التامة.
أو يلزم تحصيل الحاصل إن انتفت، فثبت أن التعليل بالمركب يستلزم أحد الأمرين المحالين، وكل ما يستلزم المحال فهو محال.
قلنا: العلة عدمية، فلا يلزم ذلك أي: العلية صفة عدمية، فإنها من النسب والإضافات التي هي أمور اعتبارية يعتبرها العقل، ولا وجود لها في الخارج، فلا تحتاج انتفاء العلية إلى علة حقيقة، كذا قرره البعض.','6','53',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1718,'وقرره بعضهم هكذا إذا كانت العلة عدمية كان نقيضها، أي: انتفاء العلية (وجوديًا لوجوب) كون أحد النقيضين وجوديًا، فلا يجوز أن يكون عدم كل جزء علة له، لأن الأمور العدمية لا تكون علة للأمر الوجودي، وحينئذ لا يلزم ما ذكرتم.
قال العبري: وفيه نظر، يعني كعجل انتفاء العلية وجوديًا.
وهو ظاهر في العدم، ولأن الدليل ينقلب، ولأن فيه مخالفة لمختار المنصف في تعليل الوجودي بالعدمي.','6','54',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1719,'والأحسن في الجواب: أنه لا يلزم من انتفاء صفة العلية بعدم الوصف، أن يكون عدم الوصف علة للانتفاء، مقتضية له بالاستقلال، بل يجوز أن يكون وجوده شرطًا للوجود.
فإن الشيء كما يعدم لعدم العدم، فقد يعدم لعدم شرط الوجود، ولو سلم فهو كالبول بعد اللمس، واللمس بعد البول، وكما لا يلزم هناك تختلف، فكذا هنا،
والوجه في تقريره أن الانتفاءات ليست علًلا عقلية ليلزم ما ذكرتم إنما هي أمارات وضيعة، ولا بعد في اجتماع عدة من المارات مرتبة تارة ضربة أخرى.
ولا بد في تحقيق المقابل من رفع جميع الانتفاءات، وهو بتحقيق جميع الوصاف، فيجب تركب الأمارة في الطرف الآخر من أوصاف متعددة.','6','55',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1720,'وها هنا مسائل تتعلق بالعلة:
الأولى
يستدل بوجود العلة على وجود الحكم، كما يقال: وجد القتل العمد العدوان فيجب القصاص، لأن وجود العلة يستلزم وجود المعلول.
ولا يستدل بعليتها- أي: بعلة العلة على وجود الحكم، كم يقال: علية القتل العمد العدوان لوجوب القصاص ثابت في القتل بالمثقل، فيجب فيه القصاص.
لأنها، -أي: على الحكم- نسبة بين العلة والحكم، فهي تتوقف في وجودها عليه -أي: على الحكم- لأن النسبة متوقفة على','6','56',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1721,'المنتسبين، فلو أثبت الحكم بها لزوم الدور.
ورد: بأن النسبة المتوقفة على المنتسبين في الذهن لا في الخارج.
وأيضًا: العلة هي المعروف- أي: علامة- فلا دور.
المسألة الثانية
التعليل لعدم الحكم المانع عنه، أو بانتفاء شرط لا يتوقف على المقتضى.
كما يقال: عدم شرط صحة البيع وهو الرؤية، أو وجد الجهل بالمبيع فلا يصح.','6','57',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1722,'فلا يجب وجود المقتضى مثل بيع من أهله في محله، عند المصنف تبعًا للإمام الرازي وابن الحاجب.
لأنه -أي: المانع- إذا أثر معه، أي: إذا أثر في عدم الحكم مع وجوب المقتضى له الذي هو ضده فبدونه أولى، أي: فبدون المقتضى أولى بالتأثير فيه، لأن الشيء حال ضعفه إذا أثر في غيره فتأثيره فيه حال القوة أولى.
وقيل: يجب
ونقل عن الجمهور ومنهم الآمدي، لأنه لا يسند العدم، أي: عدم الحكم إلى المانع.
لأن المسند إليه: إن كان العدم المستمر فباطل، إذ المانع حادث،','6','58',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1723,'والعدم المستمر أزلي. فلا يسند إلية لامتناع إسناد الأزلي إلى الحادث.
وإن كان العدم المتجدد، والتجدد في العدم إنما يتصور عند قيام المقتضى، فيكون موقوفًا على المقتضى وهو المدعى.
قلنا: المسند إلى المانع الحادث، هو العدم المستمر، ولا استحالة في ذلك؛ لأن العلل الشرعية معرفات، والحادث قد يعرف بالقديم كالعالم المعرف للصانع الأزلي.
قال الإسنوي: وهذه مسألة من تفاريع تخصيص العلة، فإنه يمتنع الجمع بين المقتضى والمانع عند من يمنع التخصيص، ولا يمتنع ذلك عند من يجوزه.
المسألة الثالثة
لا يشترط الاتفاق من المعلل والسائل، على أن العلة في','6','59',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1724,'الأصل "المقيس عليه" هي هذا الوصف على الأصح.
بل يكفي انتهاض الدليل القطعي، أو الظني عليه، أي: على وجود العلة في الأصل لحصول المقصود به، وقياسًا على سائر المقدمات.
المسألة الرابعة
الشيء الذي جعلناه علة: قد يدفع الحكم ولا يرفعه، أي','6','60',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1725,'إذا قارن ابتداء رفع الحكم، وإن وجد في الأثناء لم يقدح، وذلك كالعدة، فإنها تمنع ابتداء النكاح لا دوامه.
فإن المرأة لو اعتدت عن وطء شبيهة لا ينفسخ نكاحها.
أو يرفعه -أي: يرفع الحكم- ولا يدفعه، كالطلاق، فإنه يرفع النكاح السابق، ولا يدفع النكاح اللاحق، فلا يمنع وقوع نكاح جديد.
واعترض: بأن الدفع أسهل من الرفع، فإذا صلح الوصف لا يكون رافعًا، (فصلاحيته لأن يكون دافعًا أولى)، والطلاق كما يرفع حل الاستمتاع دفعه، ولكن هذا الدفع والرفع ليسا مؤبدين.
بل يزولان بنكاح جديد.
أو يدفع ويرفع أيضًا، كالرضاع، فإنه يدفع ابتداء النكاح، وإن وقع في دوامه قطعه.','6','61',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1726,'المسألة الخامسة
العلة الواحدة: قد يعلل بها معلول واحد، كالسكر للحرمة.
وقد يعلل بها (معلولان متماثلان) في ذاتين كالقتال الصادر من زيد وعمرو، فإنه يوجب القصاص عليهما.
ولا يأتي ذلك في الذات الواحدة لاستحالة اجتماع المثلين.
وقد يعلل بها معلولان مختلفان يمكن اجتماعهما.
وقد يعلل بها ضدان لا يمكن اجتماعهما، ولكن بشرطين متضادين؛ لأنه لو جاز اجتماع الشرطين لجاز اجتماع الضدين عند وجود علتهما وأنه محال، كالجسم يكون علة للسكون، بشرط البقاء في الحيز، وللحركة بشرط الانتقال منه.','6','62',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1727,'(والمصنف اقتصر) على الأخير، لأن ما قبله ظاهر أخذه منه، ولا بعد في مناسبة وصف واحد لحكمين، وكالسرقة للقطع زجرًا لغيرة، وله للعود لمثله، وللتغريم جبرًا لصاحب المال.
تنبيه:
من شروط العلة: أن لا يكون ثبوتها متأخرًا عن ثبوت حكم الأصل بل بمقارنه خلافًا بقوم.
قال الصفي الهندي: والحق الجواز إن أريد بالعلة المعرف.','6','63',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1728,'ومنها: أن لا يعود على الأصل بالإبطال.
وهل يشترط في العلة على أن لا تعود على أصلها بالتخصيص؟
قولان:
أما عودها على أصلها بالتعميم فإن جائز اتفاقًا.','6','64',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1729,'ومنها: إذا كانت مستنبطة، أن لا يعارضها وجود وصف مناف لها في الأصل صالح للعلية مفقود في الفرع، فيطلب الترجيح.
ومنها: أن لا تخالف نصًا أو إجماعًا.','6','65',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1730,'وأن لا تتضمن زيادة على النص، بأن يدل النص على علية وصف فيزيد علية بالاستنباط قيدًا. ثم منهم من أطلق ذلك. وقيده الآمدي بما إذا نافت الزيادة مقتضى النص.
وقال الصقي الهندي: إنما يتجه الأول لو كانت الزيادة على النص نسجًا، وليس كذلك
وأن تكون وصفًا معينًا لا مبهمًا، وقد ذكر المصنف بعد.','6','66',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1731,'وأن لا تكون وصفًا مقدرًا، أي: مفروضًا لا حقيقة له.
قاله في المحصول.
ونقل عن الفقهاء العصرين خلافة.
وأن لا يتناول دليلها حكم الفرع بعمومه وخصوصه.','6','67',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1732,'وهي مبينة بأمثلتها في الشرح.

<span data-type="title" id=toc-265><span data-type="title" id=toc-266>الفصل الثاني: في الأصل والفرع</span></span>
أما الأصل فشرطه: ثبوت الحكم فيه، لأن القياس لا يتأتى إلا (بعد ثبوت) الحكم في الأصل.
فعلى هذا لا يجوز أن يكون منسوجًا، لأنه إنما تعدى باعتبار الشارع الوصف الجامع في الأصل، حيث أثبت الحكم به، ولما زال','6','68',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1733,'الحكم مع ثبوت الوصف، علم أنه لم يبق معتبرًا في نظره، فلا يتعدى الحكم به.
إذ لم (يبق الاستلزام) الذي كان دليلاً للثبوت.
ولا بد أن يكون ثبوت ذلك الحكم فيه بدليل غير القياس.
فيجوز أن يكون الدليل كتابًا، أو سنة، أو اتفاق الأمة.
أو اتفاق الخصمين فقط، لكن بشرط أن لا يكون لعلتين مختلفين. ولا يجوز أن يكون دليل حكم الأصل القياس عند الجمهور.','6','69',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1734,'وذلك لأنهما، أي: القياسين إن اتحدا في العلة التي بين أصل الأصل، والأصل، وبينه وبين الفرع، وذلك بأن تكون العلة الجامعة بين الأصل وأصله هي التي بينه وبين فرعه.
فالقياس على الأصل الأول.
مثاله: أن يقال: السفرجل مطعوم، فيكون ربويا كالتفاح، فيمنع كون التفاح ربويا، فيقول: لأنه مطعوم كالبر.
فذكر الوسط: أعنى ما هو أصل في القياس الثاني، وفرع في القياس الأول، ضائع لإمكان طرحه من الوسط.
وقياس الفرع الثاني على الأصل الأول، لأنه هنا كان يمكنه أن يقول: في السفرجل؛ لأنه مطعوم كالبر من غير التعرض للتفاح، فكان ذكر التفاح عديم الفائدة.
وإن لم يتحد القياسين في العلة، بل اختلفنا، بأن كانت العلة بين','6','70',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1735,'الأصل وأصله، غير العلة بينه وبين فرعه، لم ينعقد القياس الثاني لعد الجامع بينهما فلا يمكن إثبات الحكم فيه.
مثاله: الجذام عيب يفسخ به البيع، فيفسخ به النكاح قياسًا على الرتق، فإنه يفسخ به النكاح لفوات الاستمتاع كالجب.
فالجامع بين الجذام والرتق: كون كل منهما عيبًا يفسخ به البيع، والجامع بين الرتق والجب: كون كل منهما مفوتًا للاستمتاع المستحق بالعقد وهو الوطء، فلا ينعقد قياس الجذام على الرتق، لعدم الجامع بينهما وهو كون كل منهما، متفوتًا للاستمتاع، فإنه غير موجود في الجذام، فلا يمكن إثبات الحكم فيه وهو انفساخ النكاح.
تنبيه: أطلق المنصف عدم جواز كون حكم الأصل ثابتًا بالقياس.
واختار في جمع الجوامع: أن محله ما إذا لو يظهر للوسط فائدة كما مر.
فإن ظهر للوسط فائدة جاز، كما يقال: التفاح ربوي، قياسًا','6','71',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1736,'على الزبيب بجامع الطعم، والزبيب ربوي قياسًا على التمر، بجامع الطعم مع الكيل، والتمر ربوي قياسًا على الأرز بجامع الطعم والكيل والقوت، ثم يسقط الكيل والقوت عن الاعتبار بطريقه، فيثبت أن العلة الطعم وحده، وأن التفاح ربوي كالبر، ولو قيس ابتداء عليه بجامع الطعم، لم يسلم ممن يمنع عليته، فقد ظهر للتوسيط بالتدريج فائدة، وهي السلامة من منع علية الطعم فيما ذكر فتكون تلك القياسات صحيحة بخلاف ما تقدم.
وشرطه أن لا يتناول دليل حكم الأصل الفرع، وإلا -أي: لو تناوله لضاع القياس، لأنه حينئذ يكون إثبات الحكم في الفرع بذلك الدليل لا بالقياس، ولم يكن جعل أحدهما أصلاً والآخر فرعًا أولى من العكس.
وقد أختلف في اشتراط عدم النص على حكم الفرع؟','6','72',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1737,'فنقل الإمام عن الأكثرين: عدم الاشتراط.
وفي جمع الجوامع تبعًا للصي الهندي: أن النص قد يكون موافقًا للقياس فهو موضع الخلاف.
فمن أطلق المنع تمسك بقصة معاذ (رضي الله تعالى عنه)، فإنها تفهم امتناع القياس عند وجود النص.
ومن قال بالجواز: فلجواز ترادف الأدلة على مدلول واحد.
ومحل الخلاف: ما إذا لم يكن النص الدال على ثبوت حكم الفرع، وهو عينه الذي دل على حكم الأصل.
وهي مسألة الكتاب، فإن كان ذلك، فقال الصفي الهندي: ينبغي أن يكون القياس باطلاً لما تقدم، وإن كان مدلول النص مخالف لمدلول القياس بطل القياس، أيضًا لئلا يلزم تقدمه على النص ولا فائدة للقياس في هذه الحالة إلا تجربة النظر، وهو التمرن ورياضة الذهن، ولا سبيل إلى قبوله والعمل به.','6','73',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1738,'وشرطه أن يكون حكم الأصل معللاً بوصف معين، لأن إلحاق الفرع بالأصل لأجل وجود العلة يستدعى العلم بحصول العلة، والعلم بحصول العلة متوقف على تعليل حكم الأصل وعلى تعين علته، وتقدم ذلك في شروط العلة.
وشرطه أن يكون حكم الأصل غير متأخر عن حكم الفرع، إذا لم يكن لحكم الفرع دليل سواه، أي: سوى القياس، لأنه إذا كان متأخرا لزم إن يثبت حكم الفرع قبل ثبوت العلة لتأخر الأصل، وثبوته مقارنًا لعلته، والمتقدم على المقارن للشيء متقدم على ذلك الشيء، فلا يصح أن يكون معرفة ثبوت الفرع مأخوذة من حكم الأصل.
نعم يصح ذلك إلزاما للخصم، بان يقول: أنت تقول بحكم الأصل لهذه العلة، فيجب إن تقول بحكم الفرع لوجود العلة.
وعليه حمل بعضهم قول الشافعي -رضي الله تعالى عنه- في بيان اشتراط النية مع الوضوء كالتيمم طهارتان. فكيف يفترقان؟','6','74',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1739,'وبعضهم جعله قياسًا صحيحًا لما سيجيء:
فإن كان لحكم الفرع دليل سوى القياس، فلا يشترط تقدم حكم الأصل عليه، لأن حكم الفرع يكون ثابتًا بذلك الدليل قبل القياس، وعند القياس يكون بإثباته وبالقياس.
وغاية ما يلزمه أن تكون أدله على مدلول واحد، وانه جائز.
مثاله: ما تقدم، فان التيمم مشروعيته متأخرة عن الوضوء، لأنها بعد الهجرة، والوضوء لها دليل لآخر وهو قوله (عليه الصلاة والسلام): "إنما الأعمال بالنيات".
وهذا إنما يتم إذا ورد الحديث قبل مشروعية الوضوء،','6','75',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1740,'والمصنف في هذا التفصيل تابع للإمام الرازي، وأبي الحسين البصري، وفي العدة لابن الصباغ مثله.
وأشار إليه في المستصفى.
وأطلق الآمدي وابن (الحاجب المنع)
قال العراقي: وتقييده بما إذا لم يكن لحكم الفرع دليل سواه لم يذكره أكثر المصنفين.
وشرط الكرخي في الأصل شرطا آخر وهو: عدم مخالفته الأصول، بأن يكون حكمه موافقًا للأصول في الكتاب والسنة وغيرهما.','6','76',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1741,'أو تحقق أحد أمور ثلاثة، إن خالف أي: إن كان حكم الأصل مخالفًا للأصول كالعرايا في الرطب، إذا جعل أصلا في جواز قياس العنب عليه.
فشرطه أمر من أمور ثلاثة:
الأول: التنصيص من الشارع على العلة، أي: على علة حكم الأصل، لأن التنصيص من الشارع على العلة كالتصريح منه بوجوب القياس عليه كما مر.
والثاني: الإجماع على التعليل، أي: على تعليل حكم الأصل فلا يكون من الأحكام التعبدية، ولا من الأحكام التي اختلف في تعليلها كالتطهير بالماء، ثم إذا اختلف على العلة فلا فرق بين أن يتفقوا على تعيين العلة، أم يختلفوا فيها','6','77',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1742,'وإليه أشار بقوله: "مطلقًا"
والثالث: موافقة هذا القياس على هذا الأصل، سائر القياسات على أصول آخر.
والحق عند الإمام الرازي، وأتباعه، ومنهم المنصف أنه يجب على المجتهد أن يطلب الترجيح- في هذه المسألة- بينه، أي: وبين القياس (على هذا الأصل الذي خالف باقي الأصول، وبين غيره، أي: وبين القياس) أصول أخر، بما يمكن الترجيح به الطرق المذكورة في باب ترجيح الأقيسة.
فما يترجح من القياسين في نظره تعين العمل به، سواء كان حكم','6','78',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1743,'الأصل المخالف مقطوعًا به أو مظنونًا.
ولكن الشارع نص على علته، فيطلب الترجيح في هذين القسمين.
أما إذا لم يكن حكمه مقطوعًا به، ولا نص على عليته، بل يكون حكمه مظنونًا، وعلته مستنبطة، فالأولى القياس على سائر الأصول الموافقة.
فهذا القسم وارد على المصنف، فإنه لا يطلب فيه ترجيح.
وجزم الآمدي: بأنه لا يجوز القياس على ما يكون حكمه مخالفًا للأصول والقواعد الواردة من جهة الشرع مطلقًا.
وهو مقتضي كلام ابن الحاجب.
وقال جماعة من الشافعية والحنفية: بالجواز مطلقًا.','6','79',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1744,'وزعم عثمان ابن مسلم البتي التابعي: أنه لا بد من قيام ما يدل على جواز القياس عليه، فلا يقاس على الأصل حتى يقوم الدليل على جواز القياس على ذلك الأصل.
والمراد من ورود الدليل هو على الباب من حيث هو لا على المسألة بخصوصها.
فإذا كانت المسألة في النكاح فورود الدليل في باب النكاح مثلًا.
وشرط: بشر بن غياث المريسي -بفتح الميم- الجهمي أحد','6','80',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1745,'أمرين:
إما انعقاد الإجماع عليه، أي: على كون حكمه معللًا، أو التنصيص على عين تلك العلة.
وكلام المصنف في نقله عن بشر مخالف للمحصول من وجهين:
فإنه نقل عن إمامنا أشياء فيه عنه اشتراط الإجماع على كون حكمه معللًا، وثبوت النص على عين تلك العلة.
وتبع المصنف في الوهم الأول: صاحب الحاصل، والثاني صاحب التحصيل.
وضعفهما، أي: ضعف مذهب البتي والمريسي ظاهر.','6','81',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1746,'لأن عموم قوله تعالى: {فاعتبروا} ينفي هذين الشرطين، فإنه ورد مطلقًا، والأصل عدم القيد، وكذلك عمل الصحابة.
وقيل: المحصور بالعدد لا يجوز القياس عليه، حتى قالوا: في قوله عليه الصلاة والسلام: "خمس يقتلن في الحل والحرم" أنه لا يقاس عليه.','6','82',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1747,'قال في المحصول: والحق جوازه لما قلناه.
وأما الفرع فشرطه: وجود العلة فيه بلا تفاوت بينه وبين الأصل، فلا بد أن تكون علته مماثلة لعله الأصل، إما في عينها: كقياس النبيذ على الخمر بجامع السكر.
أو في جنسها: كقياس وجوب القصاص في الأطراف على القياس في النفس بجامع الجناية.
وشرط المصنف: أن لا تتفاوت العلتان في الماهية، ولا في الزيادة والنقصان ليس مخالفًا لما تقدم له من أن القياس قديكون أولى، وقد','6','83',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1748,'يكون مساويًا، وقد يكون أخفى، كما توهمه جماعة لما مر من تحقيقه هناك، وإنما شرط المماثلة؛ لأن القياس إثبات مثل حكم معلوم في معلوم آخر.
فإذا وجب تماثل الحكمين، وجب تماثل الوصفين بلا تفاوت.
وشرط شرطان فاسدان:
أحدهما: حصول العلم به، أي: بوجود الحكم في الفرع ولا يكفي الظن به.
والثاني: الدليل على ثبوت حكمه، أي: حكم الفرع إجمالًا، فعلم جملة، والقياس يفصله ويعينه، كالجد مع الأخوة.
فإن الشرع دل على إرثه في الجملة، والقياس يعينه. فإنه قاسه بعهم على ابن الابن في الحجب.
وبعضهم على الأخ في المقاسمة.','6','84',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1749,'ورد كل من المذهبين: بأن الظن، أي: ظن وجود الحكم في الفرع، بعد حصول الظن بعلية الوصف الموجود في الأصل، يحصل عند ظن وجود العلة فيه دونهما، أي: بدون هذين الشرطين المذكورين، والعمل بالظن واجب، فلا يشترط حصولهما في القياس.
تنبيه: قد يستعمل القياس في عرف الفقهاء على وجه التلازم، وهو الذي يسمى عند المنطقيين بالقياس الاستثنائي، فيثبتون الحكم به، تارة وينفونه أخرى.
ففي الثبوت: يجعل حكم الأصل ملزومًا لحكم الفرع، والعلة','6','85',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1750,'المشتركة بين الأصل والفرع دليلًا على الملازمة فيلزم من ثبوت (حكم الأصل ثبوت حكم الفرع، لأنه يلزم وجود الملزوم وجود) اللازم.
وفي النفي: نقيضه، أي يجعل نقيض حكم الأصل لازمًا لنقيض حكم الفرع، بعد إثبات الملازمة بين حكم الأصل، والفرع بالقياس، حتى يلزم بحكم عكس النقيض من انتفاء حكم الفرع انتفاء حكم الأصل.
فالأول مثل: أن يعدل عن قولنا: تجب الزكاة في مال الصبي قياسًا على مال البالغ، الملك النصاب، أو دفع حاجة الفقير إلى قوله: "لما وجبت الزكاة في مال البالغ للمشترك بينه وبين مال الصبي، وهي ملك النصاب، أو دفع حاجة الفقير، وجب في ماله، أي: في مال الصبي.','6','86',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1751,'فالذي كان أصلًا، وهو مال البالغ، جعل ملزومًا لما كان فرعًا، وهو مال الصبي، وجعل العلة دليلًا على التلازم.
والثاني مثل: أن يعدل عن قوله: لا زكاة في الحلي قياسًا على اللآلئ بجامع الزينة إلى قوله: لو وجبت الزكاة في الحلي للمشترك بينه وبين اللآلئ لو جبت في اللآلئ قياسًا) عليه، ولكن اللازم وهو وجوب الزكاة في اللآلئ منتف، لأنها لا تجب في اللآلئ، فالملزوم في الحلي منتف مثله وهو المطلوب.
ووجه الملازمة: اشتراكهما في الزينة.
ولما كانت المقدمة المنتجة في المثال الأول هو إثبات الملزوم ذكر المصنف "لما" لإفادتها ذلك.
ولما كانت المقدمة المنتجة في المثال الثاني نفي اللازم أتى "بلو" الدالة على امتناع الشيء لامتناع غيره.','6','87',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1752,'وما ذكره هنا ليس مخالفًا لما مر في أول القياس من قوله: والتلازم الاقتراني لا يسميها قياسًا، لأنه ذكر هناك اختياره.
ولهذا قال: لا نسميهما.
وذكر هنا استعمال بعضهم ولذلك بناه للمفعول كذا قيل.
واعلم أنه يرد على التلازم الاعتراضات الواردة على القياس ما عدا الأسئلة المتعلقة بنفس الوصف الجامع؛ لأنه لم يذكر فيه وصف جامع.
ويختص بسؤال لا يرد على القياس.
وإيضاحه في الشرح.
فائدة: القياس من الدين، وهو من أصول الفقه، خلافًا لإمام الحرمين.
قال ابن السمعاني: يجوز أن يقال: إنه دين الله تعالى.
ودين رسول الله (صلى الله عليه وسلم)، (ولا يجوز أن يقال: إنه قول الله -تعالى- ولا قول رسول الله -صلى الله عليه وسلم).','6','88',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1753,'والقياس فرض كفاية، إذا احتيج إليه وتعدد المجتهدون، وفرض عين إذا احتاج إليه بأن لم يجد غيره في واقعة، أي: يصير فرض عين عليه.','6','89',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1754,'الكتاب الخامس
في
دلائل اختلاف فيها وفيه بابان','6','91',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1755,'<span data-type="title" id=toc-268>الكتاب الخامس: في دلائل اختلف فيها</span>
فقال بعض المجتهدين: إنها أدلة شرعية.
وقال بعضهم: ليست أدلة شرعية.
وتقدم ما في دلائل.
وفيه، أي: في هذا الكتاب بابان؛ لأن الأدلة المختلف فيها قسمان: مقبولة عنده، ومردودة، فأفرد لكل منهما بابًا.

<span data-type="title" id=toc-269>الباب الأول: في المقبولة منها</span>
وهي ستة:','6','93',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1756,'الأول
الأصل في المنافع، أي: في الأشياء النافعة -بعد ورود الشرع- الإباحة لقوله تعالى: {خلق لكم ما في الأرض جميعًا}.
فأخبر -تعالى- بأن جميع المخلوقات الأرضية للعباد؛ لأن "ما" للعموم وقد أكد بقوله: "جميعًا"، واللام في "لكم" تفيد الاختصاص على جهة الانتفاع للمخاطبين، إذ اللام في اللغة للاختصاص النافع، كما تقول: الثوب لزيد والجل للفرس.
فلزم أن يكون جميع الأشياء النافعة مباحة مأذونًا فيها شرعًا، وهو المطلوب.
وكذلك قوله تعالى: {قل من حرم زينة الله التي أخرج لعباده','6','94',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1757,'والطيبات من الرزق} فالاستفهام ليس على حقيقته، بل هو للإنكار، فأنكر -تعالى- تحريم الزينة التي يختص بنا الانتفاع بها بمقتضى اللام.
وإنكار التحريم يقتضي انتفاء التحريم، وإذا انتفت الحرمة تعينت الإباحة وهذه الآية في معرض الامتنان، فيقتضي الإذن في الانتفاع، وإلا لم يكن منه.
وكذلك قوله تعالى {أحل لكم الطيبات} فإن اللام في "لكم" يدل على أن "الطيبات" مخصوصة بنا على جهة الانتفاع- كما مر.
والحاصل من الآيات الثلاث: أنها تدل على الإباحة بواسطة اللام.','6','95',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1758,'وليس المراد من الطيبات الحلال، لئلا يلزم التكرار، بل ما تستطيبه النفوس والطباع.
لأن الأصل عدم معنى ثالث والأصل في المضار أي: الأشياء الضارة التحريم، لقوله -عليه الصلاة والسلام-: "لا ضرر ولا ضرار في الإسلام" رواه أبو داود في المراسيل.
عن واسع بن حبان عن أبي لبابة عن النبي -صلى الله عليه وسلم- بلفظ: "لا ضرر في الإسلام ولا إضرار".','6','96',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1759,'وروي عن طرق مبينة في الشرح.
فدل على نفي الضرر مطلقًا؛ لأن النكرة المنفية تعم، وهذا النفي ليس واردًا على الإمكان، ولا على الوقوع قطعًا، لوقوع الضرر والإضرار (بل على الجواز، فإذا انتفى الجواز ثبت التحريم وهو المطلوب).
قال ابن عبد البر: قيل: الضرر والإضرار بمعنى، فجمع','6','97',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1760,'بينهما توكيدًا، وقيل: بمعنى الفعل والمفاعلة أي: لا تضر أحدًا ابتداء ولا تضاره إن ضر.
وقيل: الضرر الاسم، والإضرار الفعل.
قيل: على الأول: وهو أن الأصل في المنافع الإباحة، لا نسلم أن "اللام" تفيد الاختصاص النافع في جميع الصور لغة، فإن "اللام" تجيء لغير النفع أيضًا، كقوله تعالى: {وإن أسأتم فلها}.
فإنه لاختصاص المضار.
وكذلك قوله تعالى: {ولله ما في السماوات وما في الأرض}','6','98',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1761,'ليست للاختصاص النافع، لتنزهه تعالى عن الانتفاع به.
قلنا: استعمال "اللام" في غير الانتفاع مجاز، لاتفاق أئمة اللغة على أنها موضوعة للملك، ومعناه: الاختصاص النافع، لا التمليك الشرعي وإلا لم يصح قولهم: الجل للفرس، فتكون "اللام" حقيقة في الاختصاص النافع، فاستعماله في غيره مجاز، لأنه خير من الاشتراك.
وفيه نظر لأن المصنف قدم أن اللام موضوعة التعليل.
فالأحسن أن يقال: إن "اللام" موضوعة للاختصاص المطلق،','6','99',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1762,'وهو القدر المشترك، وليس فيه اشتراك ولا مجاز.
قيل: عليه (أيضًا سلمنا) أن اللام للاختصاص النافع، لكن لا يلزم من ذلك إباحة جميع المنتفعات، لأنه مطلق، ولم لا يجوز أن يكون المراد بالانتفاع المخصوص بنا والاستدلال بما في الأرض على وجود الصانع، فإن هذا نفع عظيم.
قلنا: هو يعني الاستدلال حاصل لكل مكلف في نفسه، فإنه يمكن أن يستدل من نفسه على صانعه، فيحمل على غيره، أي: على غير الاستدلال تكثيرًا للفائدة، فيكون أولى، فيكون راجحًا.
وما قيل: إنه يحمل على غيره، وإلا يلزم تحصيل الحاصل، فيه نظر.','6','100',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1763,'واستثنى السبكي من الأصل في الأشياء الإباحة "الأموال" فقال: الظاهر أن الأصل فيها التحريم، وإيضاحه في الشرح وغيره لم يذكر هذا الاستثناء.
الثاني من الأدلة
الاستصحاب
يعني استصحاب الحال فإنه حجة، خلافًا للحنفية والمتكلمين.','6','101',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1764,'كذا أطلق المصنف الخلاف تبعًا لجماعة.
والتحقيق: أن له صورًا:
الأولى: استصحاب العدم الأصلي.
وهو نفي ما نفاه العقل، ولم يثبته الشرع، كوجوب صوم رجب فهو حجة جزمًا.
الثانية: استصحاب مقتضى العموم أو النص، إلى أن يرد المغير من مخصص أو ناسخ، فهو حجة جزمًا، فيعمل بهما إلى وروده.
وتقدم أن ابن سريج: خالف في العمل بالعام قبل البحث عن المخصص.','6','102',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1765,'وقال ابن السمعاني: لا يسمى هذا استصحابًا؛ لأن ثبوت الحكم فيه باللفظ.
الثالثة: استصحاب حكم دل الشرع على ثبوته لوجود سببه كثبوت الملك بالشراء، أو شغل الذمة عن فرض، أو إتلاف، إذا لم يعرف وفاؤه، فهو حجة مطلقًا.
ولم يختلف أصحابنا في العمل بالاستصحاب في هذه أيضًا، وفيها خلاف لغيرهم: فقيل: ليس بحجة مطلقًا، حكي عن بعض المتكلمين وعزاه الإمام الرازي للحنفية.
وقيل: إنه حجة لإبقاء ما كان على ما كان، وليس بحجة لإثبات أمر لم يكن، كاستصحاب حياة المفقود قبل الحكم بموته، فإنه دافع للإرث منه وليس برافع، لعدم إرثه من غيره، للشك في حياته، فلا يثبت استصحابها، ويشهد له مسائل في مذهبنا.','6','103',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1766,'وقيل: حجة، بشرط أن لا يعارضه ظاهر، فإن عارضه ظاهر عمل بالظاهر، سواء كان الظاهر مستندًا إلى علته أم لا.
وهو المرجوح من قولي الشافعي (رضي الله تعالى عنه).
وقيل: إنه حجة إن لم يعارضه ظاهر مستندًا إلى علته.
فإن استند الظاهر إلى علته قدم على الأصل.
وقيل: إنما يقدم الظاهر الغالب على الأصل إذا كان له سبب، كما لو رأى ظبية تبول في ماء كثير ثم قرب إليه فوجده متغيرًا، فإنا نحكم بنجاسته إحالة على السبب الظاهر.
نص عليه الشافعي -رضي الله تعالى عنه- وتابعه الأصحاب.
وقيل: يفرق في هذه الصورة بين أن يعهده عن قرب غير','6','104',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1767,'متغير، فيعمل بالسبب المذكور، وبين أن لا يكون له به عهد، أو يعهده من زمن بعيد فيعمل باستصحاب الأصل.
قاله القفال، والجرجاني.
وقال في جمع الجوامع: إنه الحق.
الرابعة: استصحاب حال الإجماع في موضع الخلاف، بأن يجمعوا على حكم في حال أخرى.
والأكثرون: على أنه لا يحتج باستصحاب تلك الحالة في هذه خلافًا للمزني وأبي بكر الصيرفي: وابن سريج والآمدي في قولهم يحتج بذلك.
مثاله: الخارج النجس من غير السبيلين لا ينقض الوضوء عندنا','6','105',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1768,'استصحابًا لما قبل الخروج من بقائه المجمع عليه.
إذا علمت ذلك فالاستصحاب الذي نقول به -دون الحنفية- وينصرف إليه الاسم: ثبوت أمر في الزمان الثاني لثبوته في الزمن الأول، لانتفاء ما يصلح أن يتغير به الحكم بعد البحث التام.
والسين فيه للطلب.
فأما عكسه: وهو ثبوت (الأمر في) الأول لثبوته في الثاني، فاستصحاب مقلوب.
كأن يقال: في المكيال الموجود الآن كان على عهد (رسول الله) -صلى الله عليه وسلم- باستصحاب الحال في الماضي.','6','106',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1769,'قال السبكي: ولم يقل الأصحاب به إلا في مسألة واحدة، بينتها في الشرح مع فوائد حسنة.
لنا على حجية الاستصحاب: أن ما ثبت في الزمان الأول من وجود أمر أو عدمه، ولم يظهر زواله، لا قطعًا ولا ظنًا، ظن بقاؤه.
كما كان ضرورة، والعمل بالظن واجب.
وإنما قلنا ذلك لوجهين:
الأول: لولا ذلك، أي: لولا بقاء الظن بثبوت الثابت الذي لم يظهر زواله بوجه من الوجوه.
لما تقررت المعجزة لتوقفها، أي: لتوقف المعجزة على استمرار العادة، لأن المعجزة: أمر خارق للعادة مقرون بالتحدي مع عدم','6','107',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1770,'المعارضة.
إذ لو جاز التغير فيها لم تكن معجزة خارقة لها لجواز تغيرها فلم تكن معجزة.
ولولا أن ما ثبت في الزمان الأول يظن بقاؤه كما كان لم تثبت الأحكام الثابتة في عهده -عليه الصلاة والسلام- (بالنسبة إلينا) لجواز النسخ، فإنه إن لم يحصل الظن ببقاء تلك الأحكام لم يكن الحكم بثبوتها لانتفاء الجزم بثبوتها، لأن احتمال بقائها مساو لجواز نسخها.
ولكان الشك في الطلاق مانعًا من الحل، كالشك في النكاح حيث كان مانعًا من الحل اتفاقًا، والثلاثة التالية باطلة فالمقدم كذلك.','6','108',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1771,'ولنا أيضًا: على أن ثبوت الشيء في الزمان الأول من غير ظهور مزيلة، يقتضي ظن بقائه في الزمان الثاني، وذلك؛ لأن ظن البقاء راجح على حدوث الفناء، وذلك لأن الباقي مستغن عن سبب جديد، أو شرط جديد، لأن الاحتياج إليهما، إنما هو لأجل الوجود، والوجود حاصل.
فلا يحتاج إليهما، وإلا يلزم تحصيل الحاصل، بل يكفيه دوامهما، دون الحادث، الذي هو عدم الباقي، فإنه لا بد له من سبب وشرط جديدين، فيفتقر إلى مقدمات أكثر فيكون مرجوحًا بالنسبة إلى الباقي.
وأيضًا: فإنه يقل عدمه أي يقل عدم الباقي، وذلك','6','109',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1772,'لأنه أقل من عدم الحادث؛ لأن الباقي داخل في الوجود (وكل ما) دخل في الوجود فهو متناه، لاستحالة دخول ما لا يتناهى في الوجود، فكذا عدمه، بخلاف عدم الحادث، فإنه لا نهاية له لصدق عدم الحادث على ما لا نهاية له، وإذا كان عدم الباقي أقل كانوجوده أكثر، فيكون راجحًا على وجو الحادث، وهو المطلوب.
وهنا نفائس في الشرح.','6','110',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1773,'الثالث
من الأدلة المقبولة
الاستقراء
وهو تام وناقص.
فالتام -قال بعضهم-: هو إثبات الحكم في جزئي لثبوته في الكل، والناقص إثباته في فرد لثبوته في أكثر الجزئيات.','6','111',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1774,'وقال بعضهم: التام إثبات حكم كلي لثبوته في جميع جزئياته.
(والناقص: إثبات حكم كلي لثبوته في بعض جزئياته).
والأول فيه تسامح؛ لأن الاستقراء حجة موصلة إلى التصديق الذي هو الحكم الكلى، فكأنه أراد أن إثبات المطلوب بالاستقراء هو: ثبات حكم كلي لوجوده في أكثر الجزئيات.
والصحيح في تفسيره انه عبارة عن: تصفح أمور جزئية ليحكم بحكمها على أمر يشمل تلك الجزئيات.
فتصفحنا جزئيات ذلك الكلي، لنطلب الحكم في واحد واحد هو الاستقراء. وإيجاب الحكم لذلك الأمر الكلي، أو سلبه عنه هو نتيجة الاستقراء.','6','112',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1775,'سمي بذلك لأن المستقري يتبع جزئيًا فجزئيًا ليتحصل المطلوب.
يقول: استقرت البلاد إذا تتبعها قرية قرية.
يخرج من أرض إلي أرض.
والناقص هو مقصود المصنف.
ويسمي عند الفقهاء إلحاق الفرد بالأعم الأغلب.
مثاله: الوتر يؤدى على الراحلة في السفر، فلا يكون واجبًا، لاستقراء أن الواجبات لا تفعل كذلك على الراحلة.','6','113',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1776,'أما كون الوتر يؤدي على الراحلة في السفر، فبالإجماع.
وأما الثانية: فباستقراء وظائف اليوم والليلة، فلم نجد واجبًا يؤدى على الراحلة.
وهذا لا يفيد القطع، لجواز أن يكون حكم ما لم يستقرأ من الجزئيات، على خلاف ما استقرئ منها. بل يفيد الظن عند المنصف، حيث قال "وهو يفيد الظن والعمل به لازم، لقوله -صلى الله عليه وسلم-: "نحن نحكم بالظاهر".
قال الزركشي: أفادني شيخنا مغلطاي أن الحافظ أبا طاهر','6','114',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1777,'إسماعيل (بن علي) بن إبراهيم بن أبى القاسم الجنزوري رواه في كتابه:
إدارة الحكام في قضية الكندي والحضرمي اللذين اختصمنا إلى النبي -صلى الله عليه وسلم.
وأصل حديثهما: في الصحيحين، فقال المقتضي عليه: قضيت علي والحق لي، فقال رسول الله صلى الله عليه وسلم- "إنما أقضي بالظاهر والله يتولى السرائر".
وله شواهد.','6','115',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1778,'قال: والحديث مشتهر في كتب الفقه وأصوله، وقد استنكره جماعة من الحفاظ منهم المزي والذهبي وقالوا: لا أصل له.
وقال العراقي: لم أقف على هذا الحديث بإسناد.
وقال والدي: لا (أصل له.
وفي الصحيحين): فأقضى له على نحو ما أسمع.
وفي البخاري: عن عمر (رضي الله عنه) (عنه): "إنما نأخذكم الآن بما ظهر لنا من أعمالكم"
وفي الأم للشافعي (رضي الله عنه) بعد أن أخرج حديث أم','6','116',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1779,'سلمة (رضي الله عنها): "إنما أنا بشر" الحديث
قال: رضي الله عنه فأخبر -صلي الله عليه وسلم- أنه إنما يحكم بالظاهر، وإن أمر السرائر إلى الله- تعالى.
فربما ظن أن هذا حديث. وهو كلام الشافعي (رضي الله عنه) كذا قيل.
والمصنف تابع في جزمه لصاحب الحاصل.
وقال الإمام الرازي: الأظهر أنه: لا يفيد الظن، إلا بدليل منفصل.
أما ثبوته في جزئي (لثبوته في جزئي) آخر بجامع فهو القياس','6','117',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1780,'الشرعي وأما هذا فإنه إلحاق للفرد بالأكثر بغير جامع.
وأما التام (فلا خلاف) كما قال الصفي الهندي إنه حجة.
والأكثرون: على أنه مفيد للقطع.
واعلم أن الوتر كان واجبًا عليه -صلى الله عليه وسلم- فيحتمل أن فعله على الراحلة كان بعد النسخ. والله أعلم.
الرابع
من الأدلة المقبولة','6','118',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1781,'أخذ الشافعي -رضي الله عنه- بأقل ما قيل، وهو مذهب الجمهور.
إذا كان الأقل جزءًا من الأكثر [و] إذا لم يجد دليلاً يدل على الأكثر أو الأقل.','6','119',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1782,'كما قيل: في دية الكتابي: الثلث، وقيل: النصف، وقيل: الكل.
فأوجب الشافعي رضي الله تعالى عنه- الثلث: بناء على الإجماع والبراءة الأصلية.
فوجوب الأقل مجمع عليه؛ لأن من أوجب الثلث فقد أوجبه، ومن أوجب ومن أوجب الزائد استلزم بالضرورة لإيجاب الأقل وهو الثلث، والأصل براءة الذمة عن الزائد، فليس إجماعًا، محضًا، بل مركب من أمرين.','6','120',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1783,'وقال القاضي أبو بكر وغيره: إن الناقل عن الشافعي أنه من الإجماع لعله زل في كلامه.
هذا إذا انحصرت الأقوال في ثلاثة، أما إذا لم تنحصر. كما إذا كان مع هذه الأقوال فول رابع، وهو أنه لا يوجب شيئًا فلم يكن الأخذ بأقل ما قيل واجبًا.
وكذا إذا دل دليل على الأقل، فإن الحكم يكون لأجل الدليل فقط.
أو دل دليل على الزائد فيرجع إليه، ولا يجوز الأخذ بالأقل.
واعترض على أصل الدليل، بأن قيل: إذا كان مركبًا من هذين الدليلين فكيف جعل دليلاً مستقلاً؟
وكيف تتجه المخالفة فيه ممن يوافقه عليهما.','6','121',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1784,'فإن قيل: يجب الأكثر لأنه؛ ثبت في الذمة شيء ليتقن الخلاص والخروج عن عهدة ما وجب عليه.
قلنا: إنما يجب ذلك حيث تيقن الشغل [أي] شغل الذمة به، والزائد على الأقل لم يتيقن فيه ذلك؛ لأنه لم يثبت عليه دليل فلا يجب.
الخامس
قد عرفت أن الوصف المناسب للحكم قد يعتبره الشارع، وقد يلغيه، وقد لا يعلم حاله في الاعتبار والإلغاء، وقد عرفت القسمين الأولين.','6','122',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1785,'وأما الثالث وهو المناسب المرسل: ويسمى بالمصالح المرسلة فقد اختلفت فيه.
والأكثرون: على أنه غير معتبر مطلقًا، واختاره ابن الحاجب،
وقال الآمدي: إنه الحق الذي اتفق عليه الفقهاء.
واختار المنصف تبعًا للغزالي أنه إن كانت المصلحة ضرورية، أي لا','6','123',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1786,'يمكن تحصيلها بطريق آخر، وهي إحدى الضروريات الخمس.
قطعية: وهى التي يجزم بحصول المصلحة فيها بالقطع لا بالظن.
كلية: لرجوعها إلي كافة الأمة، كتترس الكفار الصائلين بأسارى المسلمين.
وقطعنا بأنا لو امتنعا عن قتل الترس، لصدمونا واستولوا على ديارنا، وقتلوا المسلمين كافة حتى الترس، ولو رمينا الترس لقتلنا مسلمًا من غير ذنب صدر منه,','6','124',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1787,'فقتل الترس -والحالة هذه- مصلحة مرسلة، لكونه لم يعهد في الشرع جواز قتل مسلم بلا ذنب.
ولم يقم -أيضًا- دليل على عدم جواز قتله عند اشتماله على مصلحة عامة للمسلمين، ضرورية قطعية.
اعتبر المناسب حينئذ، ويكون من الأدلة المقبولة.
فلذلك يجوز أن يؤدي اجتهاد مجتهد إلى أن يقول هذا الأسير مقتول بكل حال فحفظ كل المسلمين أقرب إلى مقصود الشارع من حفظ مسلم.
وإلا، أي: إن لم تكن المصلحة ضرورية، قطعية كلية، فلا يعتبر المناسب، ولا يكون دليلاً مقبولاً، فعلم منه أنها لو لم تكن','6','125',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1788,'ضرورية ولكن كانت حاجية فإنها لا تعتبر. وكذا إن لم تكن قطعية ولكنها ظنية. وكذا إن لم تكن كلية ولكنها جزئية.
هذا وقد قال صاحب جمع الجوامع: ليس منه، أي من المناسب المرسل مصلحة ضرورية كلية قطعية، رداً على الإمام والآمدي وغيرهما حيث قالوا: لم يقل الشافعي -رضي الله تعالي عنه- بالمرسل إلا في هذه المسألة.
ورداً على المصنف وغيره، في جعلها من المناسب المرسل،','6','126',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1789,'وحكاية الخلاف فيها: لأن هذه الصورة قد قام الدليل على اعتبارها.
فإنه إذا قيل: فيه سفك دم امرئ معصوم.
عورض بأن في الكف عن إهلاك دماء معصومة لا حصر لها.
وقد علم من الشرع تقديم حفظ الكلي على الجزئي، وأن حفظ أصل الإسلام عن استئصال الكفار أهم في نظر الشرع من حفظ طائفة مخصوصة، ولم يسم هذا قياسًا.
إذ ليس له أصل معين، بل أدلته متعددة من الكتاب والسنة وقرائن الأحوال، فلذلك أطلق عليه اسم المصلحة المرسلة، وكأن هذه التسمية هي الموقعة للإمام وغيره في جعل الشافعي قائلًا بالمصلحة في هذه المسألة
واعلم أنه تنازع في اشتراط القطع: حكاية الأصحاب وجهين في مسألة التترس من غير اشتراط القطع.
وعللوا المنع بأن غاية الأمر أن نخاف على أنفسنا، ودم المسلم لا','6','127',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1790,'يباح بالخوف.
وفيه تصريح بجريان الخلاف في حالة الخوف بدون القطع.
قال العراقي: وقد قال: حالة القطع محل جزم، والخلاف في صورة الخوف، وبه صرح العزالي في المستصفي.
قلت: وعبارته: نحن إنما نجوزه عند القطع أو الظن قريب من القطع. والله أعلم.
وأما مالك -رضي الله تعالي عنه- فقد اعتبره أي: اعتبر المناسب المرسل مطلقًا، سواء اشتملت هذه المناسبة على هذه القيود، أو لم تشتمل.','6','128',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1791,'لأن اعتبار جنس المصالح في الشرع، حيث اعتبر الحكم المشتمل على المصلحة (الخالصة، والحكم المشتمل على المصلحة) الراجحة، واعتبار جنس المصالح يوجب ظن اعتباره، أي اعتبار المناسب المرسل؛ لأنه إذا غلب على ظننا أن هذا الحكم مصلحة غالبة على المفسدة (وقطعنا بأن المصلحة الغالبة على المفسدة) معتبرة في الشرع، لزم ظن أن هذه المصلحة معتبرة شرعًا، والعمل بالظن واجب.','6','129',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1792,'ولأن الصحابة رضي الله تعالى عنهم- قنعوا بمعرفة المصالح، ولم يلتفتوا إلى الشرائط التي يعتبرها فقهاء الزمان في القياس والأصل والفرع.
إذ المقصود من الشرائع رعاية المصالح، وإذا كان كذلك فالمناسب المرسل معتبر في الشرع.
وإن فقدت المناسبة هذه المناسبة هذه الشرائط التي ذكرتم، وذلك يدل على اعتباره مطلقًا ولم يجب المنصف عن هذه الدليلين.
قال بعضهم: لقوتهما.
وأجاب البعض عن الأول: بأنه لو وجب اعتبار المصالح المرسلة لاشتراكها مع المصالح المعتبرة في كونها مصالح، لوجب إلغاؤها أيضًا، لاشتراكهما مع المصالح الملغاة في ذلك، فيلزم اعتبارها وإلغاؤها وهو محال.
وعن الثاني: بأنا لا نسلم إجماع الصحبة علية، بل إنما اعتبروا من','6','130',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1793,'المصالح ما اطلعوا على اعتبار الشارع لنوعه أو جنسه القريب.
وما نقله المصنف عن مالك -رضي الله تعالى عنه- من اعتبار المناسب المطلق، نقل عن إمامنا أشياء أيضًا عن الشافعي -رضي الله تعالى عنه- قال: إمام الحرمين: إلا أنه شرط فيه أن تكون تلك المصالح مشتبهة بالمصالح المعتبرة.
واعتبر إمام الحرمين: المصلحة في الجملة، لكن لم يعتبر جنس المصلحة مطلقًا، بل بالغ في البرهان في إنكاره.
فلا يقال: اختاره إمام الحرمين.','6','131',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1794,'وقيل: ترد في العبادات وتقبل في المعاملات.
قال ابن الأنباري: وهو الذي يقتضيه مذهب مالك- رضي الله تعالى عنه.
السادس
من الأدلة المقبولة عند المصنف ونقله في المحصول عن بعض الفقهاء ولم يصرح بموافقته: فقد الدليل بعد التفحص البليغ، فإنه يدل على عدم الحكم بعد ما يدل عليه.','6','132',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1795,'بيانه: أن المجتهد إذا طلب في الواقعة النص والإجماع والقياس، واجتهد في الطلب ولم يجد شيئًا يغلب على الظن عدمه، أي عدم الدليل، وعدمه- أي: عدم الدليل- يستلزم عدم الحكم؛ لأنه لو ثبت حكم شرعي من غير أن ينصب عليه الشارع دليلًا، لزم تكليف الغافل، وذلك باطل، لامتناع تكليف الغافل.
والمراد بعدم الحكم: عدم تعليقه؛ لأن الحكم قديم.','6','133',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1796,'<span data-type="title" id=toc-276>الباب الثاني: في الأدلة المردودة</span>
وهما دليلان:

<span data-type="title" id=toc-277>الأول: الاستحسان</span>','6','135',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1797,'قال به أبو حنيفة والحنابلة، وأنكره غيرهم، لظنهم أنهم يريدون به: الحكم بغير دليل.
قال في الحصول: وليس الخلاف في جواز استعمال لفظ الاستحسان لوروده في الكتاب والسنة، ويرد في ألفاظ','6','136',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1798,'المجتهدين.
كقول الشافعي -رضي الله تعالى عنه- في المتعة: "أستحسن أن تكون ثلاثين درهمًا"
فسماه استحسانًا: لأنه عنده حسنًا لما قام عنده مما يقتضي ذلك، ولا ينكر التعبير به عن حكم ثبت بدليل.
لكن يشكل على هذا ما في سنن الشافعي- (رضي الله تعالى عنه) وقد ذكر خيار الشفعة ثلاثًا.
قال الشافعي- رضي الله تعالى عنه: "هذا استحسان مني ليس بأصل، ولا بد من تأويله، فثبت أن الخلاف إنما هو في المعنى','6','137',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1799,'فلا بد من تفسيره ليمكن قبوله، أو رده. وهو استفعال من الحسن. يطلق على ما يميل إليه الإنسان ويهواه من الصور والمعاني، وإن كان مستقبحًا عند غيره، وليس هذا محل الخلاف، لاتفاق الأمة قبل ظهور المخالفين على امتناع القول في الدين بالتشهي، فيكون الخلاف فيما عدا ذلك. وقد اختلف في التعبير عنة اختلاقًا كثيرًا. ذكر المنصف منه ثلاثة أشياء فقال: وفسر بأنه: دليل في نفس المجتهد وتقصر عنه عبارته، فلا يقدر على إظهاره.
ورد الاستحسان بهذا التفسير بأنه لا بد من ظهوره، أي: من','6','138',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1800,'ظهور ذلك الدليل ليتميز صحيحة من فاسدة، لجواز أن يكون فاسدًا.
ولما كان فيه نظر من جهة انه إن أريد بوجوب إظهاره أنه لا يكون قبل ذلك حجة على المناظر، فواضح.
لكنه ليس محل الخلاف.
وإن أريد به أن المجتهد لا يثبت به الأحكام فممنوع.
ويجب عليه العمل به اتفاقًا، ولا أثر لعجزه عن التعبير فإنه يختلف بالنسبة إالى الغير، إلا أن يشك المجتهد فلا يجوز له العمل به فبناه.
لذلك قال ابن الحاجب: والحق أنه لا يتحقق استحسان مختلف فيه، لأنهم ذكروا في تفسيره أمورًا لا تصلح محلا للخلاف؛ لأن بعضها مقبول اتفاقًا، وبعضها مردد بين ما هو مقبول اتفاقًا، وبين ما هو مردود اتفاقًا.
وجعل هذا التفسير من المتردد بين القبول والرد كما مر.
وفسره- أي: الاستحسان- فسره الكرخى: بأنه قطع المسألة','6','139',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1801,'عن نظائرها قي الحكم لما هو أقوى، فيقضي العدول عن الأول.
وذلك حيث دل دليل خاص على إخراج صورة مما دل عليه العام، كتخصيص أبي حنيفة- رضي الله تعالى عنه- قول القائل:
"مالي صدقة بالزكوي من المال"؛ لأن الدليل على وجوب الوفاء بالنذر يقتضي وجوب الصدقة بجميع أمواله عملاً بلفظه، لكن هنا دليل، وهو قوله تعالى: {خذ من أمولهم صدقة} فإن المراد بالمال في الآية "الزكوي" إجماعًا، فكذا في قول الناذر والجامع: قرينة إضافية الصدقة إلى المال في الصورتين.
وهذا الدليل أعني "النص" أقوى من النذر، لكونه نصا، فثبت مقتضاه، وهو قطع بعض الأفراد عن هذا الحكم، وتخصيصه بالبعض الآخر.
وإليه أشار بقوله: "لقوله تعالى: {خذ من أموالهم صدقة}.','6','140',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1802,'وعلى هذا التفسير: فالاستحسان تخصيص.
إذ ليس معنى التخصيص إلا إخراج بعض الأفراد.
وهذا كذلك.
وفي أكثر النسخ: "فالتخصيص استحسان".
والأولى أولى؛ لأن النزاع في الاستحسان، كذا قيل.
وفسر: (أبو الحسن البصري: الاستحسان) بأنه ترك وجه من وجوه الاجتهاد غير شامل شمول الألفاظ لأقوى، أي: لوجه أقوى','6','141',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1803,'من الأول، يكون كالطارئ فخرج التخصيص، أي: يكزن في حكم الطارئ عليه.
فأشار بقوله: "ترك وجه من وجوه الاجتهاد" إلى أن الواقعة التي اجتهد فيها المجتهدون لها وجوه كثيرة واحتمالات متعددة، فيأخذ المجتهد بواحد منها، ثم إنه يترك ذلك الوجه لما هو أقوى.
واحترز بقوله: "غير شامل شمول الألفاظ" عن تخصيص العموم.
واحترز بقوله: "يكون في حكم الطارئ (عليه" عن ترك أضعف القياسين لأجل الأقوى، فإن أقواهما ليس في حكم الطارئ).
مثاله: العنب، ثبت تحريم بيعه بالزبيب، سواء كان على رأس الشجر، أم لا، قياسًا على الرطب.
ثم إن الشارع أرخص في جواز بيع الرطب على رءوس النخل','6','142',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1804,'بالتمر، فقسنا عليه العنب، وتركنا القياس الأول لكونه أقوى، فلما اجتمع في الثاني القوة والطريان كان استحسانًا.
ويكون حاصله تخصيص العلة، وهو المعبر عنه بالنقض.
وليس مما انفرد به الحنفية، وقد مر ما فيه.
قيل: وفيه نظر، بل حاصله- كما قال الآمدي- الرجوع عن حكم دليل لطريان دليل أخر أقوى منه، وهذا أعم من تخصيص العلة.
قال ابن السمعاني في القواطع: قال أبو زيد في الحنفية:
الإلهام، ما حرك القلب بعلم يدعوك إلى العمل به من غير استدلال','6','143',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1805,'بآية، ولا نظر في حجة.
قال: والذي عليه جمهور العلماء أنه لا يجوز العمل به، إلا عند فقد الحجج كلها في باب ما أبيح له علمه بغير علم.
وقال في جمع الجوامع: ليس بحجة، هذا في غير المعصوم، أما المعصوم كالنبي فهو حجة في حقه، وحق غيره إذا تعلق بهم، كالوحي.
الثاني من الأدلة المردودة
-قيل-','6','144',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1806,'قول الصحابي
إذا كان عالمًا، حجة على غيره إذا لم يكن صحابيًا، وهو قول قديم للشافعي -رضي الله تعالى عنه-.
ونقل عن مالك وأكثر الحنفية.','6','145',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1807,'وقيل: قوله حجة، إن خالف القياس، وإلا فلا.
قال ابن برهان في الوجيز: إنه الحق البين وأن الشافعي- رضي الله تعالى [عنه] تدل عليه.
وقال الشافعي في القديم: قول الصحابي حجة، إن انتشر، ولم يخالف وإلا فلا، وحكاه ابن الصباغ في "العدة" عن الجديد.','6','146',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1808,'والصحيح الجديد: أنه ليس بحجة مطلقًا.
قال السبكي: تبعًا للإمام الرازي في باب الأخبار من المحصول.
ولابن الصباغ في الكامل: يستثنى من قوله في الجديد: ليس بحجة الحكم التعبدي فقوله في حجة، بظهور أن مستنده فيه التوقيف عن النبي- صلى الله عليه وسلم- قول الشافعي- رضي الله عنه- روي عن علي- رضي الله عنه- أنه صلى في ليلة ست ركعات، في كل ركعة ست سجدات ولو ثبت ذلك عن علي- رضي الله عنه- قلت به، لأنه لا مجال للقياس فيه، فالظاهر أنه فعله توقيفًا.
قال العراقي: ليس هذا عملاً بقول الصحابي، وإنما هو تحسين للظن به، في أنه لا يفعل مثل ذلك إلا توقيفًا، فهو مرفوع حكمًا، وهو نظير ما اشتهر من أن قول الصحابي فيما لا مجال للاجتهاد فيه','6','147',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1809,'مرفوع حكمًا، لحمله على أنه سمعه من النبي- صلى الله عليه وسلم.
فذاك في القول وهذا في الفعل، والله أعلم.
وموافقة الشافعي- رضي الله تعالى عنه- لزيد بن ثابت في الفرائض ليس تقليدًا له، بل لدليل قام عنده، فوافق اجتهاده اجتهاده، واستأنس به.
وإذا قلنا: إن قول الصحابي ليس بحجة، فهل يجوز لغير المجتهد تقليده؟ فيه خلاف، حكاه إمام الحرمين.
قال: والمحققون على المنع، لارتفاع الثقة بمذهبه، إذ لم يدون بخلاف مذهب كل الأئمة الأربعة (رضي الله تعالى عنه).','6','148',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1810,'وبهذا جزم ابن الصلاح، وعداه إلى كل من لم يدون مذهب.
وقال: إنه يتعين تقليد الأئمة الأربعة.
قال الإسنوي: حكاية الأقوال على الوجه الذي ذكره المصنف غلط لم يتنبه له أحد الشارحين.
وسببه: اشتباه مسألة بمسألة، وذلك أن الكلام هنا في أمرين:
أحدهما: أن قول الصحابي هل هو حجة أم لا؟
وفيه ثلاثة مذاهب: ثالثها: إن خالف القياس كان حجة وإلا فلا.
الأمر الثاني: إذا قلنا: إن قول الصحابي ليس بحجة، فهل يجوز للمجتهد تقليده؟
فيه ثلاثة أقوال للشافعي -رضي الله تعالى عنه- الجديد أنه لا يجوز مطلقًا.
الثلث -وهو قول قديم-: أنه إن انشر جاز وإلا فلا.
هكذا صرح به الغزالي في المستصفى والآمدي في الإحكام وغيرهما، وأفراد لكل حكم مسألة.','6','149',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1811,'وذكر الإمام في المحصول نحو ذلك، فتوهم صاحب الحاصل أن المسألة الثانية أيضًا في كونه حجة، فصرخ بما توهمه.
فرأى المصنف حال اختصاره أن تفريق أقوال الحكم الواحد لا معنى له فأخذ حاصل المسألتين من الأقوال وجمعة في هذا الموضع، فلزم منه أن القول المنفصل بين الانتشار وعدمه تفصيل في الاجتماع به فافهمه.
واعلم أن في جمع الجوامع: حكاية هذا القول في كونه حجة كما صنع المنصف ولم ينكره شراحه، لكن العراقي في نكته على المناهج أنكره، فليحرر.','6','150',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1812,'لنا على كون قول الصحابي ليس بحجة مطلقًا قوله تعالى:
{فاعتبروا} فإنه أمر بالاعتبار، وهو الاجتهاد، وذلك يمنع من التقليد.
لأن الاجتهاد هو: البحث عن الدليل، والتقليد: هو الأخذ بقول غيره من غير دليل.
وفيه نظر؛ لأن القائلين بكونه حجة، يمنعون كونه تقليدًا ويجعلونه كسائر الأدلة.
ولك تقرير الاستدلال بالآية بأن الأمر بالاعتبار يقتضي وجوب','6','151',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1813,'الاجتهاد مطلقًا، خالفناه مع وجود النص أو الإجماع، فيبقى ما عدا ذلك على الأصل.
ولنا أيضًا: إجماع الصحابة على جواز مخالفة بعضهم لبعض.
ولو كان قول بعضهم حجة، لوقع الإنكار على من خالفه منهم، وإذا جاز مخالفة كل واحد منهم لهم، فيجوز لغيرهم أيضًا مخالفة كل واحد منهم عملاً بالاستصحاب وهو المطلوب.
ولنا أيضًا: قياس الفروع على الأصول: فإن قول الصحابي ليس','6','152',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1814,'بحجة على غيره من المجتهدين في أصول الدين، فلا يكون أيضًا حجة قي فروعها، والجامع بينهما: تمكن المجتهدين في الموضعين من الوقوف على الحكم بطريقه.
وقد يفرق بينهما: بأن المطلوب في الأصول العلم، وقول الصحابي لا يفيده بخلاف الفروع، فإن المطلوب هو الظن، وقد يحصل بقول الصحابي.
فإن قلت: قد استدل المنصف هنا بالقياس مع وجود النص والإجماع على زعمه، وكذا يفعله الفقهاء، مع أن القياس ليس بدليل مع واحد من النص، والإجماع كما مر فكيف الجواب.
أجيب: بأن ذلك يذكر على سبيل التنبيه لا على سبيل الإثبات،','6','153',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1815,'أي: لو لم يكن نص أو إجماع؛ كان القياس دليلاً حتى لو طعن فيهما، كفى في إثبات المطلوب القياس.
وأيضًا فإن فيه تنبيهًا على أن الحكم ليس بخارج عن سنن القياس.
قيل: من جهة القائل بأن قول الصحابي حجة مطلقًا:
أنه روي عنه- صلى الله عليه وسلم- أنه قال: "أصحابي كالنجوم بأنهم اقتديتم اهتديتم"
رواه عبد الله بن روح المدائني (بلفظ: " مثل الصحابي في أمتي مثل النجوم بأنهم اقتديتم اهتديتم" وفيه مقال.','6','154',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1816,'ورواه بمعناه الدرامي وفيه ضعف، وقد روي من طرق كثيرة.
قال ابن حزم: هو خبر موضوع
قال البراز: لا يصح عن النبي-صلى الله عليه وسلم.
وقال البيهقي: هذا الحديث مشهور المتن وأسانيده ضعيفة، لم يثبت','6','155',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1817,'في هذا إسناد.
قال الزركشي: لكن تتقوى طرقه بعضها البعض، لا سيما وقد احتج به الإمام أحمد (رضي الله تعالى عنه)، واعتمد عليه في فضائل الصحابة (رضي الله تعالى عنه) ومن شواهد حديث في مسلم.
فدل الحديث على أن الاهتداء لازم للاقتداء بأي واحد منهم.
وذلك يقتضي أن يكون قوله حجة، وإلا لم يكن المقتدى به مهتديًا.','6','156',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1818,'قلنا: المراد عوام الصحابة؛ لأن الخطاب إنما هو مع الصحابة لكونه خطاب مشافهة: فانتقى دخول غيرهم.
ثم إن الصحابة المخاطبين بذلك لا يجوز أن يكونوا مجتهدين لكونه محل الخلاف كما تقدم.
فتعين أن المراد منه أن غير المجتهد منهم إذا اقتدى بأي مجتهد كان منهم اهتدى، وهو صحيح مسلم.
قيل: من جهة القائل بأن قول الصحابي عند مخالفة القياس حجة؛ أن الصحابي بعد ما عرف القياس إذا خالف مقتضى القياس, فقد اتبع الخبر، إذ هو الحامل له على مخالفة القياس، وإلا فيكون قد ترك القياس المأمور به، وانقدحت عدالته، وهو باطل، فمخالفه كاشفة عن الخبر الذي هو حجة، وليس هو بحجة لذاته.','6','157',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1819,'قلنا: ربما خالف القياس إذا اطلع لما أي لشيء ظنه دليلاً، ولم يكن في الواقع دليلاً، فلم يكن كاشفًا عن الخبر، فلم يكن حجة.
ولم يتعرض المنصف لحجة القول المنفصل بين المنتشر وغيره، لتقدم الكلام فيه في الإجماع.','6','158',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1820,'مسألة
منعت المعتزلة -أي: جمهورهم- تفويض الحكم إلى رأي النبي- صلى الله عليه وسلم- أو العالم.
وذلك بأن يقال من قبل الله -تعالى- للنبي- صلى الله عليه وسلم-أو','6','159',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1821,'العلم على لسان نبي: احكم بما تشاء في الوقائع من غير دليل، فما حكمت به فهو حكم الله -تعالى- فإنك لا تحكم إلا بالصواب؛ لأن الحكم يتبع المصلحة.
فلو فوض ذلك إلى اختيار العبد؛ لأدى إلى تخلف الحكم عن المصلحة، لجواز أن يصادف اختيار هـ ما ليس بمصلحة في نفس الأمر، وما ليس بمصلحة في نفس الأمر لا يصير بجعله إليه، أي: إلى المجتهد مصلحة لأن الحقيقة لا تقلب بالاختيار.
وإذا حكم بما ليس بمصلحة، فلم يكن حكمًا شرعيًا لما علمت.
قلنا: هذا مبني على أصل. وهو وجوب رعاية المصالح','6','160',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1822,'وذلك الأصل ممنوع لما تقدم.
وإن سلم (فلم لا) يجوز أن يكون اختياره، أي: اختيار المفوض إليه، أمارة على وجود المصلحة، بأن يلهمه الله تعالى اختيار ما فيه المصلحة، وإن لم يعلم بها؛ لأنه لما أخبره -تعالى- بأنه لا يحكم إلا بالصواب، وتوقف الحكم بالصواب على المصلحة لزم أن لا يحكم إلا بالمصلحة وإن جهلها.
وقيل: يجوز التفويض، وبه قال بعض المعتزلة، واختاره الآمدي، وابن الحاجب، وصاحب جمع الجوامع تبعًا لابن','6','161',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1823,'السمعاني.
ويكون هذا القول مدركًا شرعيًا، ويسمي: التفويض لدلالته عليه.
ولكنه لم يقع وجزم بوقوعه- أي: بوقوع التفويض -موسى بن عمران من المعتزلة لقوله -عليه الصلاة والسلام- بعد ما أنشدت قتيلة- بضم القاف- ابنة النضر بن الحارث.
ما كان ضرك لو مننت وربما من الفتى وهو المغيظ المحنق
فرق لها رسول الله -صلى الله عليه وسلم- حتى دمعت عيناه،','6','162',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1824,'وقال لأبي بكر (رضي الله تعالى عنه): "لو سمعت شعرها ما قتلت أباها".
رواه الزبير بن بكار، وذكره ابن هشام في السير، عن محمد ابن إسحاق وساق الأبيات بطولها إلا أنه ذكر أنه أخوها.','6','163',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1825,'قال السهيلي: والصحيح أنها بنته لا أخته. وممن حكي القولين الحصري في زهر الآداب. وحكي عن الزبير قال: سمعت بعض أهل العلم يغمز في أبيات قتيلة هذه ويقول: هي مصنوعة.
فدل الحديث على أن القتل وعدمه مفوض إليه، إذا لو كان بأمر الله- تعالى- قتله سمع الشعر أم لم يسمع.
وأيضًا سؤال الأقرع في الحج:','6','164',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1826,'أكل عام يا رسول الله؟
قال-صلى الله عليه وسلم-: "لو قلت ذلك لوجب".
رواه النسائي بلفظ: "لو قلت: نعم لو جبت".
وهو في مسلم فقال: رجل ولم يسمه.
فهو صريح في أن قوله المجرد من غير وحي يوجبه.
وهو معنى التفويض إلى اختياره.','6','165',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1827,'ونحوه مثل قوله -صلى الله عليه وسلم-: "لولا أن أشق على أمتي لأمرتهم بالسواك عند كل صلاة". متفق عليه.
وقول العباس -رضي الله تعالى عنه- لما قال -صلى الله عليه وسلم-: "ولا يختلي خلاؤه" إلا الإذخر، فقال -صلى الله عليه','6','166',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1828,'وسلم- "إلا الإذخر" متفق عليه.
قلنا: هذه الصور كلها لعلها ثبتت بنصوص محتملة الاستثناء على وفق إرادة بعض الناس، كأن أوحى الله تعالى إليه قبل قتل النضر أن اقتله إلا إن أنشدت ابنته، وأن اكتب لحج على الناس مرة إلا أن يسأل الأقرع، وتقول: نعم، فإنه يجب كل سنة حينئذ.
وكذلك البواقي.
ويحتمل أيضًا التخيير، ففي النضر يكون مخيرًا فيه وفي غيره، من','6','167',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1829,'الأسارى، والتخيير ليس بممتنع اتفاقًا، بل هو ثابت في حق كل إمام، فلا يدل على أن الحكم كان مفوضًا إلى رأيه وهنا فوائد في الشرح.
وتوقف الشافعي -رض- في التفويض.
واختلف في محل تردده:
فقال الإمام الرازي: في الجواز، ونقل عن الجمهور أن تردده في الوقوع مع جزمه بالجواز.','6','168',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1830,'الكتاب السادس
في
التعادل والتراجيح','6','169',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1831,'الكتاب السادس
في
التعادل والتراجيح
بين الأدلة عند تعارضها
فإذا تعارضت الأدلة، فإن لم يكن لبعضها على بعض مزية، فهو التعادل وهو التساوي، وإن كان فهو الترجيح.
وفيه أبواب أربعة.','6','171',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1832,'<span data-type="title" id=toc-281>الباب الأول: في تعادل الأمارتين</span>
والباقية في الترجيح: (لأن الكلام في التراجيح): إن لم يختص بدليل معين فهو البحث عن الأحكام الكلية كما سيجيء، وإن اختص: فالدليل الذي يرجح على معارضة إما كتاب أو سنة أو قياس.
فالكتاب والإجماع لا يأتي فيها الترجيح.
أما الكتاب: فلأنه لا ترجيح لأحد الآيتين على الأخرى عند تعارضهما، إلا بأن تكون إحداهما مخصصة للأخرى أو ناسخة لها.','6','173',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1833,'وسبق الكلام فيها، فلم يحتج لإعادته، مع أنه قد أشار إليه في الحكم الرابع من الأحكام الكلية للترجيح.
وأما الإجماع، فلأنه لا تعارض فيه كما مر.
فالترجيح إنما يكون لأحد الخبرين على الآخر، (أو لأحد القياسين على الآخر)، فلذلك انحصرت مباحث الترجيح في الأبواب الثلاثة.
ولا شك أنه يمتنع تعادل القاطعين، أي: تقابلهما: بأن يدل كل منهما على منافي ما يدل عليه الآخر، إذ لو جاز ذلك لثبت مدلولهما','6','174',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1834,'فيجتمع المتنافيان فلا وجود لقاطعين متنافيين.
ولا فرق في ذلك بين العقلين والنقليين، والعقلي والنقلي. والكلام في النقليين حيث لا نسخ بينهما.
وأما التعادل بين الأمارتين: فإن كان في نظر المجتهد، فهو متفق','6','175',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1835,'على جوازه.
وإن كان في نفس الأمر -فقال المصنف- منعه الكرخي وصححه في جمع الجوامع.
ونقل عن الإمام أحمد -رضي الله تعالى عنه.
وجوزه قوم، واختاره ابن الحاجب مع نقله هو وغيره له عن الأكثرين.','6','176',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1836,'وحينئذ فالتخيير للمجتهد بينهما للعما والحكم، ووللمستفتي في الفتوى عند القاضي أبي بكر، وأبي علي الجبائي، وابنه أبي هاشم.
وحزم به الإمام الرازي والمصنف في الكلام على تعارض النصين والتساقط عند بعض الفقهاء، ويرجع إلى غيرهما، وهي البراءة','6','177',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1837,'الأصلية.
فلو حكم القاضي بأحدهما مرة، لم يحكم بالأخرى، أخرى على القول بالتخيير، لقوله -عليه الصلاة والسلام- لأبي بكر (رضي الله تعالى عنه): "لا نقض في شيء واحد بحكمين مختلفين".
ونقل عن الحافظ الذهبي وغيره أن هذا الحديث لا يعرف.','6','178',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1838,'وقال الزركشي وغيره، رواه النسائي عن أبي بكرة -بالتاء لا بحذفها- قال: سمعت رسول الله -صلى الله عليه وسلم- يقول: "لا يقضين أحد في قضاء بقضاءين.
وترجم عليه: باب النهي: باب النهي عن أن يقضي في قضاء بقضاءين.
وقال ابن حزم في الإحكام وقد ذكر قوله تعالى: {يحلونه عامًا ويحرمونه عامًا}.
ومن هذا نهى رسول الله -صلى الله عليه وسلم- عن أن يقضي','6','179',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1839,'أحد في أمر واحد بقضاءين. انتهى.
ففي رواية النسائي أن الراوي أبو بكرة، لا أبو بكر، وأنه شرع عام لا خطاب لواحد وعليه اعتراض وجوابه في الشرح.
مسألة
تعارض قولي المجتهد في مقلديه، كتعارض الأمارتين في حق المجتهدين، فلذا عقبه به، فنقول:','6','180',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1840,'إذا نقل عن إمامنا أشياء [عن] مجتهد قولان، في موضع واحد، في مسألة واحدة فإن ذكر مع ذلك ما يشعر بترجيح أحدهما، ولو بالتفريع عليه فهو مذهبه.
وفائدة ذكر المرجوح معه، بيان مرجوحيته لئلا يتوهم رجحانه.
وإن لم يذكر معه ما يشعر بترجيح أحدهما، فإنه يدل على توقفه في تلك المسألة لعدم ظهور رجحان أحدهما.
ويحتمل أن يكونا احتمالين له تردد بينهما لتعارض الأدلة عنده.','6','181',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1841,'أو يحتمل أن يكونا مذهبين للمجتهدين غيره، ونص عليهما لئلا يظن الذاهب (إلى أحدهما) لقوله في نظره، أنه خارق للإجماع.
وإن نقل عن المجتهد قولان في مجلسين وعلم المتأخر منهما، فهو مذهبه، ويكون الأول مرجوعًا عنه.
وإلا أي: وإن لم يعلم المتأخر منهما، حكى عنه القولان،','6','182',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1842,'ولا يحكم على أحدهما بعينه بالرجوع عنه، وإن كنا نعلم أن أحدهما مرجوع عنه.
وأقوال الشافعي -رضي الله تعالى عنه- كذلك، أي: وقع منه التنصيص عليهما في موضع واحد، وفي موضعين، وعلم المتأخر وجهل.
ولم يقع للشافعي -رض- ذكر القولين، في وقت واحد، من غير ترجيح لأحدهما، إلا في بضع عشرة مسألة.
كما نقله الشيخ أبو إسحاق الشيرازي في شرح اللمع، عن القاضي أبي حامد المروروزي، ووهم من جعله الشيخ أبي حامد','6','183',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1843,'الإسفرايني.
وتتمة كلام القاضي أبي حامد: أنها ستة عشر أو سبعة عشر.
وقال االقاضي أبو بكر: قال المحققون لا تكاد تبلغ عشرًا.
ووقوع أقوال الشافعي -رضي الله تعالى عنه- على الوجهين المتقدمين فيه دليل على علو شأنه في العلم والدين.
فعلى الوجه الأول، أما في العلم: فلأن كل من كان أغوص نظرًا وأتم وقوفًا على شرائط الأدلة كانت الإشكالات الموجبة للتوقف عنده أكثر.
وأما في الدين: فلأنه لما لم يظهر له وجه الرجحان صرح بعجزه','6','184',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1844,'(عن ما) هو عاجز فيه، ولم يستنكف بعدم العلم به.
وعلى الوجه الثاني: فوجد دلالته على علو شأنه في العلم، أنه كان طول عمره - (رضي الله تعالى عنه) مشتغلًا بالطلب والبحث دائم الاجتهاد والعمل.
وفي الدين: فلأنه يدل على أنه متى لاح له في الدين شيء أظهره، وأنه لم يكن متهمًا بترويج قوله، بل كان مطمح نظره وإرشاد الخلق إلى الحق، ولم يتعصب لترويج مذهبه وهنا فوائد في الشرح.','6','185',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1845,'<span data-type="title" id=toc-283>الباب الثاني: في الأحكام الكلية للترجيح</span>
وهي الأمور العامة لأنواعها، بحيث لا يخص فردًا من أفراد الأدلة.
وهو مشتمل على مقدمة مبينة لحقيقة الترجيح، ولمشروعيته، وعلى أربع مسائل.
أما الترجيح فهو لغة، جعل الشيء راجحًا، ويقال: مجازًا لاعتقاد الرجحان.
وفي الاصطلاح: تقوية إحدى الأمارتين على الأخرى ليعمل بها.','6','187',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1846,'وخص الترجيح "بالأمارتين" أي: الدليلين الظنيين لما سيأتي بيانه وقوله: "ليعمل بها"، احتراز عن تقوية إحدى الإمارتين على الأخرى لا ليعمل بها، بل لبيان أن أحدهما أفصح من ألأخرى فإنه ليس من الترجيح المصلح عليه.
وزاد صاحب "البديع" في التعريف "وصفًا" ليخرج الترجيح بدليل مستقل فلا يجوز؛ لأنه يؤدي إلى الانتقال لدليل آخر.','6','188',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1847,'فإنه لا تعلق للثاني بألول، فالعدول إليه انتقال.
ونازع الصفي الهندي: في تعريف الترجيح بالتقوية التي هي مستندة إلى الشارع أو المجتهد حقيقة، وإلى ما به الترجيح مجازًا.
وقال: هذا في الاصطلاح نفس ما به الترجيح.
واعلم أن للفقهاء (ترجيحًا خاصًا) يحتاج إليه استنباط الأحكام.
وذلك لا يتصور فيما يس فيه دلالة على الحكم أصلًا، ولا فيما دلالته عليه قطعية لما سيجيء أنه لا تعارض بين قطعيين ولا بين قطعي وظني، فتعين أن يكون لأمارة على أخرى ولا يحصل حكمًا محضًا، بل لا بد من اقتران أمر يقوى على معارضها، فهذا الاقتران الذي هو سبب الترجيح: هو المسمى بالترجيح في مصطلح القوم.
لا جرم عرفة ابن الحاجب: بأنه اقتران الأمارة بما تقوى به على معارضها.
وإذا حصل الترجيح وجب العمل به، وهو تقديم إحدى','6','189',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1848,'الأمارتين.
كما رجحت الصحابة خبر عائشة -رضي الله عنها- في التقاء الختانين، وهو قولها: "إذا التقى الختانان فقد وجب الغسل" على خبر أبي هريرة (رضي الله عنه) وهو قوله -صلى الله عليه وسلم-: (إنما الماء من الماء) لأن أزواج النبي صلى الله عليه وسلم وخصوصًا','6','190',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1849,'عائشة -رضي الله تعالى عنها- أعلم بفعله في هذه الأمور من الرجال الأجانب.','6','191',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1850,'مسألة
لا ترجيح في القطعيات سواء كانت عقلية أو نقلية كما مر، إذ لا تعارض بينهما، والترجيح لا يكون إلا بعد التعارض، وإلا أي: لو كان بين القطعيات تعارض ارتفع النقيضان، حيث لم يعمل بهما أو اجتمعا حيث عمل بهما.
والعمل بأحدهما دون الآخر يلزمه الترجيح من غير مرجح وكلها ظاهر البطلان.
قال الإسنوي: وإطلاق عدم الترجيح في القطعيات فيه نظر، لما ستعرفه في تعارض النصين.
وجوابه: أن المراد بالنصين: الدليلان، لا بقيد كونهما قطعيين،','6','192',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1851,'وهو جار على أحد الاصطلاحات في النص كما مر.
وسكت المصنف هنا عن التعارض بين القطعي والظني، وهو ممتنع كما مر.
واعترض أيضًا: بأنه إن أريد منع التعارض بين قاطعين في نفس الأمر، فمسلم، لكن ليس محل الكلام.
وإن أريد منعه في الذهن فممنوع.
مسألة
إذا تعارض نصان، فإنما يحتاج إلى الترجيح بينهما، إن لم يمكن العمل بكل واحد منهما.','6','193',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1852,'فإن أمكن ولو من وجه دون وجه، فلا يصار إلى الترجيح، وحينئذ فالعمل بهما من وجه أولى من إهمال أحدهما بالكلية؛ لأن الأصل في الدليلين الإعمال.
وذلك بأن يتبعض الحكم، أي: حكم كل واحد من الدليلين المتعارضين.
أي: يكون قابلًا للتبعيض، فيثبت البعض بأحدهما.
والبعض الآخر بالآخر.
ويعبر عنه أيضًا "بالاشتراك والتوزيع".
مثاله: الملك في اثنين، يدعي كل منهما أنه ملكه ولا بينة لأحد أو لكل واحد منهما بينة.
فإن الملك بينهما، لأن يد كل واحد دليل ملكه، وعمل بكل','6','194',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1853,'من وجه، ولا حاجة إلى الترجيح، بخلاف ما لم يتبعض، كالقتل، وحد القذف، فإنه لا بد في ذلك من الترجيح.
أو يتعدد حكم كل واحد (من الدليلين)، أي: يحتمل أحكامًا، فيثبت بكل واحد بعضها أي: بعض تلك الأحكام.
مثاله: ما روى عنه -صلى الله عليه وسلم- أنه قال: "لا صلاة لجار المسجد إلا في المسجد".
فإنه معارض لتقريره -صلى الله عليه وسلم- في غير المسجد، ومقتضى كل واحد منهما متعدد.
فإن الخبر يحتمل نفي الصحة، ونفي الكمال، ونفي الفضيلة،','6','195',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1854,'وكذا التقرير يحتمل في الإثبات ذلك أيضًا، فيحمل الخبر على نفي الكمال، والتقرير على ثبوت الصحة.
أو يعم كل واحد من الدليلين، بأن يكون كل واحد منهما مثبتًا لحكم في الموارد المتعددة فيوزع الدليلان عليهما، ويحمل كل منهما على بعض تلك الموارد المتعددة فيوزع الدليلان عليهما، ويحمل كل منهما على بعض تلك الموارد، كقوله -صلى الله عليه وسلم-: "ألا أخبركم بخير الشهود" فقيل: نعم؛ فقال: "أن يشهد الرجل قبل أن يستشهد" (رواه مسلم بلفظ: "ألا أخبركم بخير الشهداء الذي يأتي بشهادته قبل أن يسألها".','6','196',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1855,'وقوله عليه الصلاة والسلام: "ثم يفشو الكذب حتى يشهد الرجل قبل أن يستشهد").
في الصحيحين قريب منه، وهو مبين في الشرح.
فيحمل الأول على حق الله تعالى، والثاني على حقنا جمعًا بين الدليلين.','6','197',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1856,'وهو يلزم ترجيح كل منهما على الآخر من وجه، وذلك ظاهر.
وما جمع به المصنف بين الحديثين ذكره غيره.
لكن حكى الترمذي أن المراد بالذي يشهد ولا يستشهد: شاهد الزور.
مسألة
إذا تعارض نصان: وتساويا في القوة: بأن يكونا معًا معلومين أو مظنونين بحيث لا يغلب أحدهما الآخر، وتساويا في العموم: بأن يصدق كل منهما على ما صدق عليه الآخر.','6','198',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1857,'والمراد من المعارض أعم من الناسخ، ولهذا قسمه إليه وإلى غيره.
فإذا تساويا فيما ذكر، وعلم المتأخر منهما بعينه، فهو ناسخ للمتقدم، سواء كانا معلومين أو مظنونين، وسواء كان من الكتاب أو السنة، أو أحدهما من الكتاب، والآخر من السنة، هذا إذا كان قابلًا للنسخ.
فإن لم يكن قابلًا له، كصفات الله -تعالى- على ما مثل به، فيتساقطان، ويجب الرجوع إلى دليل آخر.','6','199',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1858,'وإن كان الدليلان خاصين فكالمتساويين في القوة والعموم.
ولم يذكره المصنف: لأنه يعلم حكمه منه لمساواته له.
وقال العراقي: إنما اقتصر على ذكر العموم (استغناء بذكر أحد الضدين عن الآخر، كما في قوله تعالى: {سرابيل تقيكم الحر} أي: والبرد).
وإن جهل المتأخر فلم يعلم عينه، فالتساقط إن كانا معلومين، ويجب الرجوع إلى غيرهما؛ لأن كلا منهما يحتمل أنه المنسوخ احتمالًا على السواء، أو الترجيح إن كانا مظنونين فيعمل بالأقوى إن وجد، وإن تساويا تخير المجتهد وإن علم تقاربهما.
قال في المحصول: إن كانا معلومين وأمكن التخيير فيهما، تعين القول به، فإنه إن تعذر الجمع لم يبق إلا التخيير.','6','200',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1859,'ولا يجوز أن يرجح أحدهما على الآخر بقوة الإسناد لما عرف أن المعلوم لا يقبل الترجيح، ولا أن يرجح بما يرجع بما يرجع إلى الحكم، لكون أحدهما للحظر، لأنه يقتضي طرح المعلوم بالكلية.
وإن كانا مظنونين وجب الرجوع إلى الترجيح، فيعمل بالأقوى.
وإن تساويا فالتخيير.
وإن لم يتساويا في القوة والعموم.
فالأول بأن كان أحدهما قطعيًا والآخر ظنيًا.
والثاني: بأن يكون أحدهما أخص من الآخر مطلقًا، أو من وجه.','6','201',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1860,'وإليه أشار بقوله: "أو أخص مطلقًا عمل به، وإن تخصص من وجه طلب الترجيح يعني فيرجح القطعي إذا كان أحدهما، ويعمل به سواء كانا عامين أو خاصين، أو المقطوع به خاصًا والمظنون عامًا.
فإن كانا بالعكس، فإنه يقدم الظني.
وكذلك يرجح أحدهما إذا كان أخص مطلقًا، فيرجح الخاص على العام، ويعمل به جمعًا بين الدليلين، وسواء علم المتأخر منهما أم لا، وسواء كان الخاص مظنونًا، والعام مقطوعًا به أم لا، كما في المحصول.
قال الإسنوي: وهذه الصورة- وهي ما إذا كان العام مقطوعًا','6','202',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1861,'والخاص مظنونًا- لا تؤخذ من كلام المصنف في هذه المسألة، لأن كلامه هذا، وإن اقتضى إدخالها، فكلامه في القسم الذي قبله يقتضي إخراجها لكنها تؤخذ من كلامه في التخصيص.
ولعل المصنف إنما أهملها لذلك.
نعم (إن عملنا) بالعام المقطوع به، ثم ورد الخاص بعد ذلك.
فلا يؤخذ به إذا كان مظنونًا؛ لأن الأخذ به في هذه الحالة نسخ لا تخصيص كما سبق، ونسخ المقطوع بالمظنون لا يجوز انتهى.
أما إذا كان بينهما عموم وخصوص من وجه دون وجه، فيطلب الترجيح بينهما من جهة أخرى ليعمل بالراجح؛ لأن الخصوص يقتضي الرجحان.
وقد ثبت هنا لكل واحد منهما خصوص من وجه بالنسبة إلى الآخر، فيكون لكل منهما رجحان على الآخر، فيصار إلى الترجيح.','6','203',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1862,'ولا فرق بين أن يكونا قطعيين أو ظنيين، لكن في الظنيين يمكن الترجيح لقوة الإسناد، وبالحكم ككون أحدهما للحظر مثلًا.
(وأما في القطعيين: فلا يمكن الترجيح لقوة الإسناد كما في المحصول.
بل يرجح بالحكم، كالتحريم مثلًا) لأن التقديم بهذا الوجه طريقة الاجتهاد، وليس في ترجيح أحدهما على الآخر بالاجتهاد اطراح الآخر.
قال: بخلاف ما إذا تعارضا من كل وجه، أي: إذا علمنا تفاوتهما فإنه لا يجوز أن يرجح أحدهما على الآخر أصلًا.
وحيث قلنا بالترجيح فلم يترجح أحدهما على الآخر فالحكم','6','204',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1863,'التخيير كما في المحصول.
وقد جزم المصنف بذلك في الأقسام السابقة، واستفدنا من كلامه هنا أن الصحيح عنده في تعادل الأمارتين إنما هو التخيير فإنه لم يصحح هناك شيئًا.
مسألة
قد يرجح بكثرة الأدلة، فيرجح مقتضى دليل بكثرة الأدلة عليه دون مايقابله».
وبه قال الجمهور، ومنهم مالك والشافعي (رضي الله تعالى','6','205',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1864,'عنهما) و «قد» في كلام المصنف للتحقيق.
وقلنا: بالترجيح بكثرة الأدلة: لأن الظنين أقوى من ظنٍ واحد لكونه أقرب إلى القطع، والعمل بالأقوى أولى.
ولا شك أن الدليلين يفيدان ظنين بخلاف الواحد.
وقيل: لا ونسب للحنفية؛ لأنه لو كان كثرة الأدلة توجب الترجيح، لم يجب تقدم الخبر على الأقسية الكثيرة، لكنه يقدم الخبر على الأقيسة الكثيرة اتفاقًا.
فدل على أن الأدلة الكثيرة لا توجب الرجحان.','6','206',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1865,'قلنا: تلك الأقسية إن اتحد أصلها، بأن علل الحكم بعلة واحدة فمتحدة، يعني أنها في الحقيقة قياس واحد.
وحينئذ تقديم الخبر عليها ترجيحًا لدليل على دليل، وذلك جائز اتفاقًا.
وإلا، أي: وإن لم تتحد أصول تلك الأقسية، بل علل الحكم فيها بعلل متعددة فممنوع قولكم: «أنه يقدم الخبر عليها إجماعًا ممنوع» بل تقدم الأقسية على الخبر.
قال العراقي: تبع فيه الإمام، وفيه نظر.
(قال: فالظاهر) تقديم خبر الواحد على الأقيسة، وإن تعددت أصولها ما لم تصل القطع، إذ لا يحصل منها يزيد على الظن الحاصل من خبر الواحد.','6','207',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1866,'<span data-type="title" id=toc-288>الباب الثالث: في ترجيح الأخبار</span>
بعضها على بعض.
وهي على وجوه سبعة:

<span data-type="title" id=toc-289>الأول: ما يتعلق بحال الراوي</span>.
فيرجح بكثرة الرواة على الأصح، بأن يكون رواة أحدهما أكثر','6','209',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1867,'عددًا من رواة الآخر؛ لأن العدد الأكثر أبعد خطأ من العدد الأقل، ولأن كل واحد يفيد طنًا وقلة الوسائط بين الراوي: وبين النبي -صلى الله عليه وسلم- وهو علو الإسناد؛ لأن احتمال الخطأ فيه أقل، كذا في المحصول
ثم قال: ولكنه مرجوح من وج÷ آخر، وهو كونه نادرًا.
وفقه الراوي، سواء كانت الرواية باللفظ أو بالمعنى؛ لأن الفقيه إذا سمع ما يمتنع حمله على ظاهره بحث عنه حتى يطلع على ما يزول به الإشكال بخلاف غيره.','6','210',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1868,'وعلمه بالعربية؛ لأن العالم بها يمكنه التحفظ على مواقع الزلل فكان الوثوق بروايته أكثر، كذا في المحصول.
ثم قال: ويمكن أم يقال: هو مرجوح لأنه يعتمد على معرفته فلا يبالغ في الحفظ، والجاهل به يخاف فيبالغ في الحفظ.
وأفضليته قال الإسنوي -في العربية أو الفقه، كما قاله الإمام فالخبر الذي يكون راويه أفقه، أو أنحى، مقدم على الآخر.
لأن الوثوق بقول الأعلم أتم.','6','211',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1869,'ثم لا فرق (في الراجح بأحد هذه الأمور، بين أن يكون روى الحديث باللفظ لأو المعنى، والمرجوح بها) رواه باللفظ.
وحسن الاعتقاد، أي: حسن اعتقاد الراوي، فيرجح على رواية المبتدعي إن لم تسقط بدعته عدالته.
وكونه، أي: كون الراوي صاحب الواقعة المروية، فإنه أعرف بالحال من غيره.
مثاله: حديث أبي داود عن ميمونة (رضي الله تعالى عنها): «تزوجني رسول الله صلى الله عليه وسلم ونحن حلالان بسرف».','6','212',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1870,'مع أن خبر ابن عباس (رضي الله تعالى عنهما) المتفق عليه أنه تزوجها وهو محرم.
ومثله: كونه مباشرًا للواقعة، فإنه أعرف بها من غيره.
وكون الراوي جليس المحدثين؛ لأنه أعرف بطريق الرواية وشرائطها.
وكذا جليس غير المحدثين من العلماء، قاله الإمام وغيره.','6','213',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1871,'بل لو اشترك الراويان في أصل المجالسة، ولكن كان أحدهما أكثر، فإنه يقدم كما في المحصول أيضًا، ولم يفرض المسألة إلا في ذلك. والاقتصار على مجالسة المحدثين، قاله صاحب التحصيل.
وكونه مختبرًا أي: مزكى بالممارسة والاختبار، فإنه يقدم على من عرفت عدالته بالتزكية أو العمل؛ لأن المعاينة أقوى من الخبر.
ثم يقدم إذا كان معدلًا بالعمل على روايته، يعني تثبت عدالته بعمل من روى عنه بما رواه عنه، بالخبر الذي يكون رواية معدلًا (بهذا الطريق، راجح على الذي يكون راويه معدلًا) بغيره.
وعبر بقوله: «ثم» لتعلم أن التعديل بالاختيار مقدم على هذا الطريق ولم يبين ذلك الغير الذي تقدم عليه التعديل بالعمل.','6','214',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1872,'فإن أراد به التلفظ بالتزكية، ففيه نظر، فإن الآمدي وابن الحاجب وغيرهما جزموا بعكسه.
فإن أراد به الرواية عنه، وهو الذي صرح به صاحب الحاصل ففيه نظر؛ لأن الرواية لا تكون تعديلًا إلا إذا شرط أن لا يروى إلا عن العدل، ومع التصريح بهذا الشرط لا تتقاعد الرواية عن التعديل باللفظ، فيأتي فيه ما تقدم بل أولى منه.
وقال الإمام الرازي: إذا زكي الراوي، فإن عمل بخبره كانت روايته راجحة على ما إذا زكاه وروى خبره.
ولك أن تجعل الباء في كلام المصنف، بمعنى المصاحبة، أي: معه لا مع العمل، فلا يخالف كلام أحد ممن تقدم.','6','215',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1873,'وأما التعديل بالحكم مع التعديل بالعمل، فقال الآمدي: الحكم أولى لأن الاحتياط فيه أبلغ.
ويرجع بكثرة المزكين للراوي، ولذا قدم حديث بسرة في الانتفاض بمس الذكر، على حديث: طلق في نفيه.','6','216',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1874,'وبحثهم أي: يرجح بكثرة بحث المزكين عن أحوال الناس.
وكذا زيادة عدالتهم والوثوق بهم، قاله ابن الحاجب.
وعلمهم، أي: يرجح بكثرة علم المزكين.
قال الإسنوي: يعني بالعلوم الشرعية، كما اقتضاه كلام المحصول، لكون الثقة بقولهم أكثر، لا بأحوال الراوي، كما قاله الشارحون فإنه تقدم ما يدل عليه.
وقال العبري: قال الخنجي: المراد بالعلم هنا: الاطلاع على أحوال الراوي، والحق أنه يحمل على الأعم من ذلك.
وحفظه، أي: ويرجح بحفظ الراوي، وتحته صورتان:','6','217',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1875,'أحدهما: أن يكون اعتماد الراوي على حفظه للفظ الحديث، فيقدم على من اعتماده على كتابة للاختلاف في جواز الاعتماد على الكتاب من غير حفظ.
الثانية: أن أحدهما أكثر حفظًا، أي: أقل نسيانًا فروايته راجحة على نسيانه أكثر وزيادة ضبطه.
والضبط: هو شدة الاعتناء بالحديث والاهتمام بأمره.
فيقدم خبر الائد ضبطًا، ولو كان زيادة الضبط -عليه الصلاة والسلام- بأن يكون أكثر حرصًا على مراعاة كلماته وحروفه.','6','218',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1876,'فلو كان أحدهما أكثر ضبطًا، لكنه أكثر نسيانًا، والآخر بالعكس، ولكن لا يمنع خبره، فالأقرب التعارض، كما في المحصول.
قال الإسنوي: وهو يدل على تفسير الضبط بما تقدم، لا بعدم النسيان كما قاله الشارحون.
ودوام عقله، فإنه يرجح على من اختلط عقل راويه في بعض الأوقات، بشرط أن لا يعلم، هل رواه في حال سلامة عقله أم لا؟ كما في المحصول.
والمصنف أطلق تبعًا للحاصل والتحصيل.
وصوبه العراقي (لأنه نظر في كلام المحصول.
قال): إذا اشتبه ما رواه في حال السلامة بما رواه في حال','6','219',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1877,'الاختلاط رد حديثه، كما صرح به ابن الصلاح، وغيره من أئمة الحديث فلا يصح ترجيح غيره عليه.
وشهرته، فإنه ترجح شهرة الراوي بالصفات الحسنة كالورع والفطنة والعلم؛ لأن الشهرة بالمنصب وغيره مانعة من الكذب ومن التدليس عليه، وكذا شهرة عدالته.
وشهرة نسبه، فإنه يرجح بها أيضًا، وبه قال الآمدي وابن الحاجب.
لأن من ليس مشهور النسب قد يشاركه ضعيف في الاسم، واحترازه عما يوجب مقتضى منزلته المشهورة أكثر.
ويقدم معروف النسب على مجهوله، قاله في المحصول.
وعدم التباس اسمه باسم غيره من الضعفاء، وصعب التمييز -كما في المحصول فإن روايته راجحة على من التبس اسمه باسم غيره من','6','220',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1878,'الضعفاء.
قال: وكذلك صاحب الاسمين مرجوح، بالنسبة إلى الاسم الواحد، ويمكن دخوله في كلام المصنف.
وتأخر إسلامه؛ لأن تأخر إسلام الراوي دليل على تأخر روايته، والمصنف تابع في ذلك الشيخ أبا إسحاق في شرح اللمع وصاحب الحاصل.
وعكسه ابن الحاجب تبعًا للآمدي لزيادة أصالته في الإسلام.','6','221',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1879,'وحكى ابن السمعاني عن الحنفية أنه لا يرجح بالتأخير.
قال ابن السمعاني: وما قلناه أولى؛ لأن سماع المتأخر تحقق تأخره، وسماع المتقدم يحتمل التقدم والتأخر، فمحق التأخر أولى، ومتأخر الصحبة كمتأخر الإسلام.
وفي الشرح هنا التقديم بأمور: منها: إذا كان سماعه شفاهًا ليس من وراء حجاب.
وكونه من رءوس الصحابة، أي: رؤسائهم لقربه من مجلس النبي -صلى الله عليه وسلم- وشدة ديانتهم.
ورجح في جمع الجوامع التقديم بالذكورة.','6','222',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1880,'وقال الأستاذ أبو إسحاق الإسفرايني: إنه لا يرجح بالذكورة.
وقال ابن السمعاني في القواطع: إنه ظاهر المذهب، ولم يذكر الأول إلا احتمالًا.
وقال الكيا الطبري: لم يقل أحد إن رواية الرجال مرجحة على رواية النساء.
وقيل: تقدم المرأة إذا كان المروي في أحكام النساء.
وقدم في جمع الجوامع: بالحرية.
وقال ابن السمعاني: والحرية لا تأثير لها في قوة الظن.
وكونه غير مدلس تقدم روايته على المدلس المقبول.
وكونه في الصحيحين مقدم على ما هو في أحدهما، وما كان','6','223',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1881,'على شرطهما.
الوجه الثاني من الوجوه السبعة: الترجيح بوقت الرواية.
فيرجح الخبر الذي يكون الراوي له، لا يروي الأحاديث إلا في وقت البلوغ على خبر الراوي في الصبا وفي البلوغ، بأن روى ذلك الحديث مرتين، مرة في بلوغه ومرة في صباه؛ لأن الراوي في هاتين الحالتين، يكون متحملًا في وقت الصبا بالضرورة، والاعتماد على ضبط البالغ أكثر.
ويرجح الخبر الذي يكون المتحمل له وقت البلوغ فقط، على المتحمل له في حال الصبا، والبلوغ، لجواز أن تكون روايته بواسطة تحمله','6','224',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1882,'الواقع في حال الصبا، دون الواقع في حال البلوغ.
وإلى الوقتين أشار بقوله: «أو فيه أيضًا» أي: في البلوغ منضمًا إلى ما ذكرنا وهو الصبا.
هكذا شرحه بعضهم.
قال الإسنوي: وهو بعيد في المعنى، ولا يكاد يوجد التصريح به لأحد ويكون فيه تكرار.
وشرحه بعضهم هكذا:
فيرجح الخبر الذي يكون راوية، لا يروى الأحاديث إلا في وقت البلوغ، على خبر الذي لم يروها إلا في صباه.
أو روى بعضها في صباه وبعضها في بلوغه، لاحتمال أن يكون هذا الخبر من مروياته في حال الصغر، ولم يعلم سماعه بذلك، وكذا يرجح خبر الذي لم يتحمل رواية الأحاديث إلا في زمان بلوغه، على','6','225',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1883,'من لم يتحمل إلا في زمان صباه، أو تحمل بعضها في صباه، وبعضها في بلوغه لاحتمال كون هذا الخبر من الذي تحمله في حال الصغر.
قال الأسنوي: وهو الأقرب إلى كلام الإمام، وهو صحيح.
وقال العراقي: قول الإسنوي وغيره، أن رواية من لم يرو إلا بعد البلوغ، مقدمة على رواية من لم يرو إلا في زمن الصبا غير مستقيم؛ لأن الرواية زمن الصبا غير مقبولة، فكيف يرجح غيرها عليها، والترجيح فرع القبول.
نعم هذا في التحمل صحيح؛ لأن التحمل زمن الصبا مقبول، ولهذا اقتصر المصنف في الترجيح (بوقت الرواية على أمر واحد)
وذكر في الترجيح بوقت التحمل أمرين:
أحدهما: حيث قال: «التحمل في الصبا أو فيه أيضًا» أي: في زمن البلوغ مع زمن الصبا.','6','226',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1884,'قال: ومحل الترجيح المذكور مع الاحتمال، أما من يتعين أن الراوي أو المتحمل قبل البلوغ أو بعده، إنما روى هذا أو تحمله بعد البلوغ، فلا ترجيح لمن لم (يتحمل ولم) يرو إلا بعد البلوغ عليه، لانتفاء العلة التي ذكرها لمرجوحيته، وهي احتمال أن هذا فيما رواه أو تحمله قبل البلوغ، هذا مفهوم من تعليلهم.
قال: وهو واضح ولم أر من صرح به.
قال: وقوله: «أوفيه» صوابه «أو وفيه» بزيادة «الواو»؛ لأن المقصود ترجيح المتحمل بوقت البلوغ فقط، على المتحمل وقت الصبا فقط.
وعلى المتحمل وقت الصبا والبلوغ، وعلى حذف، «الواو» يبقى ظاهره.
ترجيح المتحمل وقت البلوغ على المتحمل وقت البلوغ، وهو فاسد.
وإنما ذكر الترجيح بوقت التحمل مع كون الترجمة ليس فيها','6','227',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1885,'الترجيح بوقت الرواية، استطرادًا من الترجيح بوقت الرواية، إلى الترجيح بوقت التحمل؛ لأن الرواية ناشئة عن التحمل.
وعبارة جمع الجوامع: ومتحملًا بعد التكليف، أي: يرجح على المتحمل قبل التكليف، وعلى المتحمل في الحالتين.
الوجه الثالث من الوجوه السبعة: الترجيح بكيفية الرواية فيرجح الخبر المتفق على رفعه إلى النبي -صلى الله عليه وسلم- على الخبر الذي اختلف في رفعه إليه، أو هو موقوف على الصحابي.
ويرجح الخبر المحكي بسبب نزوله، على الذي لم يذكر معه ذلك.
لأن ذكر الراوي لسبب النزول يدل على اهتمامه بمعرفة ذلك الحكم، وهذا إذا كانا خاصين.','6','228',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1886,'فإن كانا عامين -قال الإسنوي-: فالأمر بالعكس، كما نقله الإمام هنا ونص عليه الشافعي (رضي الله تعالى عنه).
قال ابن الحاجب: إلا إذا تعارضا في صاحب السبب فإنه أولى؛ لأن ترك الجواب مع الحاجة مما يقتضي تأخير البيان عن وقت الحاجة.
ولو أبدل المصنف النزول بالورود، لكان صريحًا في تناول الأخبار.
ويرجح الخبر المحكي بلفظه -صلى الله عليه وسلم- على الخبر المروي بالمعنى.
وكذا على خبر المحتمل الرواية بالمعنى لبعده عن الخطأ والتبديل، ولكونه مجمعًا على قبوله.','6','229',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1887,'ويرجح ما (أي الذي) لم ينكره راوي الأصل، وهو من إضافة الأعم إلى الأخص كمسجد الجامع.
وهي زيادة لا يتبادر الذهن إليها، ولو زاد (ألـ) «في» «راوي» أو حذفه كان أصوب.
والمعنى: أن الخبر الذي لم ينكره الراوي الأصل (لرواية الفرع) وهو شيخه، مقدم على ما أنكره شيخ راويه، بأن قال: ما رويته لأن الظن الحاصل من الأول أقوى، كذا شرحه بعضهم.
وقال العراقي: المراد ترجيح ما لم ينكره الراوي على ما تردد فيه، أما ما أنكره فإنه غير مقبول وإنكاره مسموع منه، فلا ترجيح بينه وبين غيره.','6','230',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1888,'الوجه الرابع من الوجوه السبعة: الترجيح بوقت وروده.
أي: بوقت ورود الخبر.
وهي ستة أقسام ذكرها الإمام الرازي وضعفها.
فترجح الأخبار المدنيات على المكيات.
قال الإسنوي: والمصطلح عليه بين أهل العلم أن المكي: ما ورد قبل الهجرة سواء كان في مكة أم في غيرها.
والمدني: هو ما ورد بعدها، سواء كان في المدينة أم في مكة أم في غيرهما.
قال: وهذا الاصطلاح ليس هو المراد هنا؛ لأنه لو كان كذلك لكان المدني ناسخًا للمكي بلا نزاع.
وأيضًا فلأن تقديم المنسوخ على الناسخ، ليس من باب الترجيح،','6','231',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1889,'كما نص عليه الإمام، بل المراد أن الخبر الوارد في المدينة مقدم على الوارد في مكة، سواء علمنا أنه ورد في مكة قبل الهجرة أو لم نعلم الحال.
قال الإمام: لأن الغالب في المكيات، ورودها قبل الهجرة والوارد منها بعد الهجرة قليل، والقليل ملحق بالكثير، فيحصل الظن بأن هذا الحديث الوارد في مكة إنما ورد قبل الهجرة، فيجب تقديم المدني عليه لكونه متأخرًا.
ويرجح الخبر المشعر بعلو شأن الرسول -صلى الله عليه وسلم- لأن ظهور أمره كان في آخر عمره (صلى الله عليه وسلم) فدل على التأخير.','6','232',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1890,'كذا أطلقه المصنف تبعًا للحاصل.
وفي المحصول الأولى أن يفصل، فيقال: إن دل أحدهما على العلو والآخر على الضعف، قدم الدال على العلو.
وأما إذا لم يدل الآخر لا على القوة ولا على الضعف، فمن أين يقدم الأول عليه؟
وأجيب: بأنه إن كان التأخير سببًا للرجحان فالدال على العلو معلوم التأخير، أو مظنونة، بخلاف ما لم يدل على شيء.
ويرجح الخبر المتضمن للتخفيف على المتضمن للتغليظ؛ لأنه أظهر تأخرًا فإن النبي -صلى الله عليه وسلم- كان يغلظ في ابتداء أمره زجرًا لهم عن العادات الجاهلية، ثم مال إلى التخفيف، كذا في الحاصل وتبعه المصنف.','6','233',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1891,'قال الإسنوي: وإطلاق هذه الدعوى مع ما سيأتي من كون المحرم مقدمًا على المبيح لا يستقيم.
وقد جزم الآمدي بتقديم الدال على التشديد، قال: لأن احتمال تأخره؛ أظهر لا لأن الغالب منه -عليه الصلاة والسلام- أنه ما كان يشدد إلا بحسب علو شأنه، ولهذا أوجب العبادات شيئًا فشيئًا، (وحرم المحرمات شيئًا فشيئًا)، وتبعه ابن الحاجب.
والإمام الرازي ذكر هذا الحكم في حادثة كان -عليه الصلاة والسلام- يغلظ فيها زجرًا للعرب عن عاداتها، ثم خفف فيها نوع تخفيف.
ولا يلزم منه العدول إلى التخفيف مطلقًا، كما ظنه','6','234',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1892,'صاحب الحاصل والمصنف.
وحينئذ فليس بين الإمام والآمدي، اختلاف.
قال العراقي: والحق أن المتضمن للتغليظ يقدم على المتضمن للتخفيف.
ويرجح الخبر المطلق من غير تاريخ على خبر متقدم التاريخ؛ لأن المطلق أشبه بالمتأخر.
وقيل بالعكس.
ويرجح الخبر المؤرخ بتاريخ مضيق، أي: واردًا في آخر عمره -عليه الصلاة والسلام- على الخبر المطلق؛ لأنه أظهر تأخرًا.','6','235',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1893,'ويرجح خبر المتحمل في الإسلام، على الخبر الذي لا يعلم هل تحمل في حال الإسلام أو في حال الكفر.
الوجه الخامس من الوجوه السبعة: الترجيح باللفظ فيرجح الخبر الفصيح اللفظ على الركيك، للاختلاف في قبول الركيك وإن كان الحق قبوله، لاحتمال رواية راويه له بالمعنى.
ولا يرجح الأفصح على الفصيح في الأصح؛ لأنه -صلى الله عليه وسلم- كان يتكلم (بالأمرين ويقدم) الوارد بلغة قريش.','6','236',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1894,'ويرجح الخبر الخاص على الخبر العام. ويرجح غير المخصص الباقي (على عمومه على العام المخصص) للاختلاف في حجيته.
واختار في جمع الجوامع تبعًا للصفي الهندي (عكسه، لأنه الغالب ويقدم ما) قل تخصيصه أيضًا.
ويرجح اللفظ المستعمل بلفظ الحقيقة على المستعمل بطريق (المجاز، لأن دلالة الحقيقة) أظهر، وهذا إذا لم يغلب المجاز كما تقدم.
ويرجح المجاز الأشبه بها، أي: بالحقيقة على (مجاز غيره، وإذا','6','237',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1895,'تعذر) الحمل على الحقيقة، فالشرعية من الحقائق مرجحة على غيرها، لأنه -عليه الصلاة والسلام- بعث لبيان الشرعيات.
فالظاهر من حاله -صلى الله عليه وسلم- أنه يخاطب بها، ثم بعد الحقيقة الشرعية ترجح الحقيقة العرفية لاشتهارها، وبيان معناها.
ويرجح الخبر المستغني عن الإضمار على الخبر المفتقر إليه، لأن الإضمار على خلاف الأصل.
والإضمار: وإن كان داخلًا في تعريف المجاز لكنه نوع برأسه.','6','238',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1896,'ويرجح الخبر الدال على المراد من وجهين، على الخبر الدال من وجه واحد، لأن الظن الحاصل من الأول أقوى، لتعدد جهة الدلالة.
ويرجح الخبر الدال على المراد بغير وسط، على الدال عليه بواسطة، لأن قلة الوسائط تدل على قلة الاحتمال، وقلة الاحتمال سبب الرجحان.
ويرجح الخبر المؤمى إلى علة الحكم، على الخبر الذي لا يكون كذلك.
لأن انقياد الطباع إلى الحكم المعلل أسرع، ولأن الأول أقوى في الاهتمام بالحكم من الثاني.
وعلم منه: أن الحكم المذكور فيه الحكم مع العلة صريحًا، أولى بالتقدم على ما فيه الحكم فقط.','6','239',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1897,'ويقدم ما ذكرت فيه العلة قبل الحكم على عكسه، قاله في جمع الجوامع. وعكس النقشواني ذلك رادًا به على الإمام.
ويرجح الخبر المذكور معه معارضه لقوله -عليه الصلاة والسلام: - «كنت نهيتكم عن زيارة القبور فزوروها» على ما لا يذكر معه معارضه.
ويرجح الخبر المقرون بالتهديد على الخالي منه، لأنه يدل على تأكد','6','240',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1898,'الحكم الذي تضمنه.
وكذا لو كان التهديد في أحدهما أكثر، قاله في المحصول، وكذا ما فيه تأكيد على الخالي منه.
ويقدم العام الشرطي على النكرة المنفية، خلافًا للصفي الهندي، وهي تقدم على الباقي من صيغ العموم، كالمعرف باللام والإضافة، وهنا فوائد حسنة في الشرح.','6','241',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1899,'الوجه السادس من السبعة: الترجيح بالحكم:
فيرجح الخبر المبقي لحكم الأصل، أي: المقرر لمقتضى البراءة الأصلية على الخبر الناقل لذلك الحكم أي: الرافع له.
وهو في هذا تابع للإمام الرازي، لأنه أي: المبقي لو لم يتأخر عن الناقل لم يفد، لأن البقاء حينئذ يكون مستفادًا من العقل، فيلزم إهماله، وهو منتف بالأصل.
وإذا كان متأخرًا عن الناقل أفاد تأسيسًا.
والجمهور: على تقديم الناقل، لأن الناقل يستفاد منه ما لا يعلم من غيره بخلاف المبقي.','6','242',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1900,'واختار في المستصفى تساويهما.
مثاله: حديث: «من مس ذكره فليتوضأ». صححه الترمذي وغيره، مع حديث الترمذي وغيره أنه -صلى الله عليه وسلم- سأله رجل (مس ذكره، عليه) وضوء؟ قال: «لا إنما هو بضعة منك».
ويرجح الخبر (المحرم','6','243',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1901,'على) المبيح، أي: على الدال على الجواز، ليدخل المكروه والمندوب والمباح، وبه قال ابن الحاجب والآمدي ونقله عن أصحابنا والأكثرين لقوله عليه الصلاة والسلام: «ما اجتمع الحلال والحرام إلا وغلب الحرام الحلال»
رواه عبد الرزاق في مصنفه موقوفًا على عبد الله هو ابن مسعود.','6','244',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1902,'قال البيهقي: فيه جابر الجعفي، وهو ضعيف، وفيه انقطاع أيضًا.
قال الزركشي: ولا يعرف مرفوعًا، يعني إلى النبي -صلى الله عليه وسلم- قال العراقي: قال والدي هذا الحديث لا أصل له.
وأيضًا للاحتياط في تقديم الحرام، لأن (ذلك الفعل) (إن كان) حرامًا ففي ارتكابه ضرر، وإن كان مباحًا فلا ضرر في','6','245',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1903,'تركه.
ويعادل الخبر المحرم، الخبر الموجب، أي: يساويه، فلا يعمل بأحدهما. إلا بمرجح، لأن المحرك يتضمن الذم على الفعل، والموجب يتضمن الذم على الترك فتساويا.
كذا جزم به المصنف تبعًا للإمام الرازي.
وجزم الآمدي بترجيح المحرم، لأن اعتناء الشارع بدرء المفاسد','6','246',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1904,'أكد من اعتنائه بجلب المصالح.
ويرجح مثبت الطلاق والعتاق من الأخبار، على الخبر النافي لهما، لأن الأصل عدم القيد، فالدال على أحدهما دال على زوال قيد النكاح أو ملك اليمين، فيكون موافقًا للأصل فيكون أرجح.
والمصنف تبع في ذلك الآمدي، ثم قال الآمدي: ويمكن أن يقال: بل النافي أولى.','6','247',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1905,'ورجح في جمع الجوامع تبعًا لابن الحاجب أن المثبت مقدم على النافي مطلقًا، لاشتماله على زيادة علم.
ويرجح الخبر الذي هو نافي الحد على الموجب له، لأنه -أي: الحد- ضرر، والضرر منفي عن الإسلام.
ولقوله -صلى الله عليه وسلم-: «ادرءوا الحدود بالشبهات».','6','248',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1906,'قال العراقي: وأنكره بعضهم بهذا اللفظ وقال: المعروف «ادرءا الحدود ما استطعتم» لكن لا وجه لإنكاره، فقد رواه الن عدي في جمعه لأحاديث أهل مصر والجزيرة من حديث ابن عباس (رضي الله عنهما) وفيه ابن لهيعة. انتهى.
(وذكر البيهقي في المعرفة أنهبهذا اللفظ جاء من حديث علي مرفوعًا، ثم لما ساقه في السنن الكبير أورده من وجهين وليس في واحد منهما با','6','249',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1907,'لشبهات. كذا هو عند الدارقطني.
وفي شرح مختصر ابن الحاجب لابن السبكي أن أبا محمد الحارثي ذكره في مسند أبي حنيفة (رضي الله تعالى عنه) من حديث ابن عباس (رضي الله عنهما).','6','250',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1908,'قال شيخ الإسلام: ووهم بعض من أخذ كلامه فنسيه إلى أبي محمد الدرامي، فكأنه محرف عليه، ورويناه في مسند مسدد عن ابن مسعود (رضي الله عنه) قال: «ادرءوا الحدود بالشبهة». وهو موقوف حسن الإسناد.
وحديث «ادرءوا الحدود عن المسلمين ما استطعتم». الحديث حديث غريب أخرجه الترمذي، فقال: لا نعرفه إلا من رواية محمد بن ربيعة عن يزيد بن زياد.','6','251',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1909,'ويزيد يضعف في الحديث، ولم ينفرد به محمد بن ربيعة، فقد أخرجه الحاكم من رواية الفضل بن موسى عن يزيد بن زياد مرفوعًا أيضًا، وقال صحيح الإسناد.
وأخرجه الترمذي والبيهقي من رواية وكيع عن يزيد موقوفًا، ورجحاها على الرواية المرفوعة.','6','252',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1910,'(وروي من طرق).
فورود الخبر في نفي الحد إن لم يوجب الجزم بذلك، فلا أقل من حصول الشبهة، والشبهة تدفع الحد بهذا الحديث. وخالف في ذلك المتكلمون. ويرجح النهي على الأمر، والأمر على الإباحة.
والأصح تقديم المثبت للحكم الوضعي على المثبت للحكم التكليفي.','6','253',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1911,'الوجه السابع من الوجوه السبعة: الترجيح بالأمر الخارجي.
فيرجح أحد الخبرين على الآخر بعمل أكثر السلف، على الأصح.
ويرجح أيضًا ما كان موافقًا لدليل آخر على ما لم يوافقه.','6','254',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1912,'وكذا الموافق مرسلًا أو صحابيًا أو أهل المدينة.
(وفي الكل) خلاف مبين في الشرح مع فوائد كثيرة تركتها خوف السآمة.
قال العراقي: تبع الإمام في التعبير «بأكثر السلف» وهو يقتضي (أن ما) دون ذلك لا يحصل به الترجيح.
وهو مخالف لما جزم به الأمدي، واقتضاه كلام ابن الحاجب.','6','255',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1913,'<span data-type="title" id=toc-296>الباب الرابع: في ترجيح الأقيسة بعضها على بعض</span>
وهو يقع بوجوه خمسة:
الأول: ما يكون بحسب العلة
فيرجح القياس المعلل بالوصف الذي هو المظنة كالسفر، على القياس المعلل بنفس الحكمة كالمشقة، لأن التعليل بالمظنة متفق عليه، بخلاف الحكمة.
ثم يرجح القياس المشتمل على الحكمة الوجودية على القياس المعلل بالوصف العدمي، لأن العلم بالعدم لا يدعو إلى شرع الحكم إلا إذا','6','257',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1914,'حصل العلم باشتمال ذلك العدم على نوع مصلحة، فالداعي إلى شرع الحكم هو المصلحة لا العدم، فالتعليل لا العدم، فالتعليل بالمصلحة أولى، وهذا المعنى وإن كان يقتضي ترجيح الحكمة على الوصف الحقيقي، لكنه عارضه كون الحقيقي أضبط، فلذا قدم عليها.
وعلم مما تقدم رجحان التعليل بالحكمة الحقيقية على التعليل بالأوصاف الإضافية والأوصاف التقديرية لكونه عدمية.
ثم يرجح القياس المشتمل على الوصف العدمي على القياس المعلل بالحكم الشرعي، لأنه أشبه بالصفات الحقيقية، من حيث إن اتصاف الشيء به لا يحتاج شرع، بخلاف الشرعي.','6','258',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1915,'ثم يرجح القياس الذي علته الحكم الشرعي، على القياس الذي علتته وصف مقدر، كرقبة ولد المغرور الموجبة للغرامة، لأن التعليل بالحكم الشرعي تعليل بأمر محقق، فهو واقع على وفق الأصول، بخلاف الوصف المقدر فإنه عدمي، كذا في المحصول.
قال الإسنوي: ومقتضى إطلاق المصنف أن التعليل بالوصف التقديري أولى من الحكم الشرعي، لكون التقديري من العدميات كما مر.
ويرجح التعليل بالوصف البسيط على المركب على الأصح؛ لأن البسيط متفق عليه، والاجتهاد فيه أقل.','6','259',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1916,'وعلم منه: أن كونها أقل أوصافًا أولى من الكثيرة الأوصاف لسلامة القليلة، ويرجح التعليل بالوصف الوجودي للوجودي، أي: للحكم الوجودي، على تعليل الحكم العدمي بالوصف الوجودي، وعلى تعليل الحكم الوجودي بالوصف العدمي، ويليله تعليل العدمي بالعدمي.
وجزم صاحب الحاصل بأن تعليل العدمي بالوجودي أولى من عكسه.

<span data-type="title" id=toc-298>الوجه الثاني: الترجيح بحسب دليل العلية:</span>
وهي الطرق الدالة على علة الوصف في الأصل.','6','260',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1917,'وعلى ما أورده إما عقلية، أو نقلية.
والنقلية: إما نص أو إيماء.
والنص: إما قاطع أو ظاهر.
والعقلية: الخمسة الباقية وهي المناسبة، والدوران، والسبر، والشبه، والطرد، ولم يذكر الإجماع من النقلية، ولا تنقيح المناط من العقلية.
إذا علمت ذلك فنقول:
يرجح القياس الثابت بالإ (جماع القاطع ثم) بالنص القاطع، ثم بالإجماع الظني، ثم النص الظني.','6','261',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1918,'وقيل: يقدم النص على الإجماع لأنه فرعه.
ونسب لصاحب الحاصل والمصنف.
ثم يرجح الظاهر وله ألفاظ: أقواها اللام، ثم إن والباء وهما: سواء، كما أفهمه كلامه.
واختار الصفي الهندي تقديم الباء، لكونها أظهر في التعليل بالاستقراء.','6','262',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1919,'ثم يرجح القياس الثابت عليه وصفه بالمناسبة على الدوران وغيره مما بقي، لأن المناسبة لا تنفك عن العلية.
وفي جمع الجوامع بعد النص: الإيماء، ثم السبر، ثم المناسبة.
ونقل عن اختيار الآمدي وابن الحاجب.
ثم إن المناسب قد يكون من الضروريات الخمس، وقد يكون من الحاجيات، وقد يكون من التحسينات.','6','263',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1920,'فيرجح بالمناسبة الضرورية الدينية، على الضرورية الدنيوية، ثم الدنيوية، ثم التي في حيز الحاجة الأقرب اعتبارًا، فالأقرب، يعني فيرجح بعد المصلحة الدينية المصلحة المتعلقة بالنفس، ثم النسب، ثم العقل، ثم المال، ثم الحاجيات، ثم التحسينيات، والمكمل لكل قسم يلحق به.
ويرجح الأقرب اعتبارًا منها، على الأبعد اعتبارًا.','6','264',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1921,'والقرب إلى الاعتبار إما بحسب ذات الوصف وهو إما أن يناسب نوعه نوع الحكم، أو نوعه جنس الحكم (أو بالعكس)، أو يناسب جنسه جنس الحكم، فالأول مقدم على بقية الأقسام، والثاني والثالث كالمتعارضين وهما مقدمان على الرابع.
وإما بحسب صفته، كالوصف الذي يناسب الحكم مناسبة جلية، بأن يلتفت الذهن إليه عند سماعه، كقوله (عليه الصلاة والسلام) «لا يقضي القاضي وهو غضبان».
فإن الذهن يلتفت عند سماعه إلى أنه مناسب للحكم لعدم استبقاء الفكر، فإنه أقرب إلى اعتبار الشرع من الوصف المناسب الخفي','6','265',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1922,'الذي لا يلتفت الذهن إليه عند سماعه.
وإما أن يكون باعتبار أمور خارجية كالوصف الذي تأيدت مناسبته للحكم، إما بسائر الطرق كالإيماء، والدوران، والسبر، وغيرها؛ فإنه اقرب إلى اعتبار الشرع مما لا يكون كذلك.
ويرجح حاصله إلى الترجيح بكثرة الأدلة، او بالخلو عن المعارض.
وإلى هذا كله أشار بقوله: «الأقرب اعتبارًا فالأقرب».
ثم يرجح القياس الذي عليته الدوران، على الذي ثبتت عليته بالسبر أو غيره من الطرق الباقية، لأن العلية المستفادة من الدوران مطردة منعكسة، بخلاف غيره من الطرق، وقد مر تقدم السبر على المناسبة المقدمة على الدوران.','6','266',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1923,'وقدم في جمع الجوامع: الشبه على الدوران.
ثم الدوران قد يكون في محل واحد، كالسكر في ماء العنب مع الحرمة وجودًا في الخمر وعدمًا في الخل والعصير.
وقد يكون في محلين كدوران وجوب الزكاة وجودًا في الذهب والفضة، وعدمًا في الثياب ونحوها.
فالدوران في محل أرجح، لأن احتمال الخطأ فيه أقل، ثم في محلين.
ثم يرجح القياس الذي علته السبر.
قال في المحصول: وهذا إذا كان السبر مظنونًا، فإن كان مقطوعًا','6','267',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1924,'به فالعمل به متعين، وليس من قبيل الترجيح.
واختار الآمدي وابن الحاجب تقديم السبر على المناسبة.
ثم يرجح القياس الذي وصفه الشبه على ما ثبت بالإيماء، لأن الشبه له مناسبة مع الحكم ولو بالتبع بخلاف الإيماء.
فإن ترتب الحكم على الوصف لا يوجب كونه مناسبًا.
وهذا الذي جزم به المصنف: من كون الإيماء مؤخرًا عما قبله.
ذكره الإمام بحثًا بعد أن نقل عن الجمهور أنهم اتفقوا على تقديم الإيماء على جميع الطرق العقلية حتى المناسبة.
ثم يرجح الإيماء (على الطرد، لكون الثابت بالإيماء بيانًا','6','268',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1925,'في جميع الموارد بخلاف الطرد)
(ثم الطرد) آخرها لما تقدم.
قال العراقي: مقتضاه: ترجيح الطرد على تنقيح المناط، وفيه نظر ظاهر (والصواب تقديم تنقيح المناط).

<span data-type="title" id=toc-299>الوجه الثالث: الترجيح بحسب دليل الحكم في الأصل</span>.
فيرجح من القياسين المتعارضين ما يترجح دليل حكم أصله على دليل حكم أصل الآخر بأحد المرجحات المذكورة في الباب قبله،','6','269',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1926,'أو بغيره من المرجحات، وهذا إنما يمكن في الأدلة الظنية لما سبق مرارًا.
فإن كانت من الآحاد، ترجح بعضها على بعض بالمتن والسند، وإن كانت متواترة لم يمكن الترجيح إلا بالمتن.
ويرجح القياس الذي دليل حكم أصله النص، على الذي ثبت حكم أصله بالإجماع لأنه فرعه، أي: لأن الإجماع فرع عن النص، لأن حجيته إنما ثبتت بالأدلة اللفظية.
كذا جزم به تبعًا للحاصل.
ونقل الإمام الرازي عن الأصوليين: تقديم الإجماع على النص، لأن','6','270',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1927,'الأدلة اللفظية قابلة للتخصيص والتأويلات بخلاف الإجماع. ثم قال: وهذا مشكل لكونه فرعًا له.

<span data-type="title" id=toc-300>الوجه الرابع: الترجيح بحسب كيفية الحكم</span>. وقد سبق في ترجيح الأخبار.
فيرجح القياس المبقي للبراءة الأصلية على الناقل، والمحرم على المبيح، والمثبت على النافي على ما فيه، ودارئ الحد على المثبت، والموجب والمحرم سواء.

<span data-type="title" id=toc-301>الوجه الخامس: الترجيح بحسب محل العلة</span>.','6','271',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1928,'وهو: إما الأصل أو الفرع:
أما الأصل: فيرجح القياس إذا كانت علته موافقة الأصول الكثيرة في العلة على ما لا يوافقها، لأن موافقة كل أصل دليل مستقل على صحة التعليل بها.
ولا شك أن الظن يقوى بكثرة الأدلة.
وكذا يرجح القياس الذي يوافق حكم أصله الأصول الشرعية، على ما لا يوافقها، لأن الأول متفق عليه، دون الثاني.
وإليه أشار بقوله: "والحكم".
والمراد بالموافقة أن يكون جنس ذلك الحكم ثابتًا في الأصول أو دلالة على ذلك الحكم.','6','272',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1929,'وأما الفرع: فيرجح القياس الذي تكون العلة في فرعه مطردة، أي: مثبتة للحكم في كل الفروع، على القياس الذي لا تكون العلة فيه مطردة، بل مثبتة للحكم في بعض الفروع (دون بعض)؛ لأن المطردة مجمع عليها بخلاف المنقوضة.
وهنا فوائد في الشرح.
واعلم أن المرجحات لا تنحصر لكثرتها جدًا، ومثارها قوة الظن وغلبته.
وقد سبق كثير في كلام المصنف فلم يحتج لإعادته، فسبق في بحث المفاهيم تقديم بعضها على بعض، وسبق في تعارض ما يخل بالفهم ترجيح المجاز على المشترك وغيرهما، وتعارض القول والفعل سبق في','6','273',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1930,'الكلام على السنة، وتقديم بعض أنواع المناسبة على بعض سبق في القياس.','6','274',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1931,'الكتاب السابع
في
الاجتهاد والإفتاء','6','275',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1932,'<span data-type="title" id=toc-302>الكتاب السابع
في
الاجتهاد والإفتاء وأحكامهما</span>
وفيه بابان:
يختص الأول بالأول والثاني بالثاني.

<span data-type="title" id=toc-303>الباب الأول: في الاجتهاد</span>
والمراد منه عند الإطلاق: الاجتهاد في الفروع.','6','277',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1933,'والاجتهاد لغة: تحمل الجهد، وهو المشقة في أمر.
وهو في الاصطلاح: استفراغ الجهد في درك الأحكام الشرعية.
فقوله: "استفراغ الجهد" معناه: بذل تمام الطاقة بحيث يحس في نفسه العجز عن المزيد عليه، وهو كالجنس.','6','278',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1934,'وقوله: "في درك الأحكام" خرج به استفراغ الجهد في فعل من الأفعال، وخرج به الاجتهاد في الكلام، والأصول.
إذ المراد بالحكم هو الذي عرفه في المقدمة: وهو خطاب الله -تعالى- المتعلق بأفعال المكلفين بالاقتضاء أو التخيير.
فعلى هذا قوله: "الشرعية" للتأكيد اهتمامًا به.
وقيل: المراد "بالأحكام" أعم من ذلك، فيكون التقييد "بالشرعية" ليخرج غيرها.','6','279',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1935,'وقيد ابن الحاجب: "استفراغ الجهد" بالفقيه، ليحترز عن استفراغ غير الفقيه، وعليه كلام مبين في الشرح.
وعلم من تعريف "الاجتهاد" تعريف المجتهِد والمجتهَد فيه.
فالمجتهد: هو المستفرغ وسعه في درك الأحكام الشرعية.
والمجتهد فيه: كل حكم شرعي.
قال الإمام والآمدي: وليس فيه دليل قطعي.
وفيه أي: في هذا الباب فصلان:','6','280',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1936,'<span data-type="title" id=toc-304>الفصل الأول: في المجتهدين وأحكامهم وشرائطهم</span>
وفيه (أي: في هذا الفصل) مسائل:
المسألة الأولى
يجوز له -عليه الصلاة والسلام- أن يجتهد.
وبه قال الأكثرون، وهو مذهب الشافعي وأحمد -رضي الله','6','281',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1937,'عنهما- لعموم قوله تعالى: {فاعتبروا يا أولي الأبصار}.
فإنه عام شامل له -صلى الله عليه وسلم- لأنه كان أعظم الناس بصيرة وأكثرهم خبرة بشرائط القياس، فيكون مأمورًا بالاجتهاد، فاعلاً له، صيانة لعصمته عن ترك المأمور به.
وأيضًا: وجوب العمل بالراجح يقتضي ذلك، فإذا غلب على ظنه -صلى الله عليه وسلم- أن الحكم معلل بكذا، ثم عَلِم أو ظن وجود ذلك الوصف في صورة أخرى، فيلزم أن يحصل له ظن ثبوت الحكم في تلك الصورة، فيجب العمل بالظن لما هو مقرر في بداية','6','282',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1938,'العقول من وجوب العمل بالراجح.
ولأنه -أي: ولأن العمل بالاجتهاد- أشق من العمل بالنص، لأنه يحتاج إلى إتعاب النفس في بذل واسع، فيكون أكثر ثوابًا، فلو لم يعمل به -صلى الله عليه وسلم- مع أن بعض أمته يعمل به، لزم اختصاص بعض أمته بفضيلة لم توجد فيه (وذلك باطل قطعًا).
ولأن الاجتهاد أدل على الفطانة) وجودة القريحة من العمل بالنص، فيكون العمل بالاجتهاد نوعًا من الفضل.
فلا يجوز خلو الرسول -صلى الله عليه وسلم- منه، لكونه جامعًا لأنواع الفضائل.
ونظر في الدليلين الأخيرين من جهة: أنا لا نسلم أن علو درجته تقتضي تركه للاجتهاد، بل قد يقتضي تركه، إذ الشيء قد يسقط','6','283',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1939,'لدرجة أعلى، ولا يكون فيه نقص لأجره، ولا كون غيره مختصًا بفضيلة ليست له، وذلك كمن يحرم ثواب الشهادة لكونه حاكمًا، وثواب التقليد لكونه مجتهدًا، وثواب القضاء لكونه إمامًا.
وما اختاره المصنف من جواز الاجتهاد له -صلى الله عليه وسلم- هو مذهب الجمهور ونُقِل عن الشافعي وأحمد -رضي الله عنهما.
ومنع ذلك أب علي الجبائي وابنه أو هاشم: لقوله تعالى: {وما ينطق عن الهوى إن هو إلا وحي يوحى} وهو ظاهر في العموم، وأن كل ما ينطق به هو عن وحي، وهو ينفي الاجتهاد، ولأنه القول بالقياس كما تقدم، والقياس قول بالرأي، والرأي هو قول بهوى النفس ومشيئتها.','6','284',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1940,'قلنا: النبي -صلى الله عليه وسلم- مأمور به -أي: بالاجتهاد- لما مر.
فليس بهوى، فإن أمره بالاجتهاد إذا كان بالوحي كان نطقه بالحكم المجتهد فيه نطقًا عن الوحي لا عن الهوى.
قال العراقي: ما حاصله تبع في هذا الجواب صاحب الحاصل، ولم يستدل الخصم بقوله تعالى: {وما ينطق عن الهوى} إذ لا يمكن القول بأن الاجتهاد هوى، وإنما استدلاله بقوله: {إن هو إلا وحي يوحى}.
وجوابه: ما ذكره الغزالي والإمام: أنه لما أُمر بالاجتهاد لم يكن العمل بمقتضاه نطقًا بغير الوحي.','6','285',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1941,'وقالا ثانيًا: لا يجوز له الاجتهاد لأنه -صلى الله عليه وسلم- كان ينتظر الوحي في جواب كثير من المسائل كالظهار واللعان.
ولو كان مأمورًا بالقياس لما انتظر الوحي بل اجتهد وأجاب.
قلنا: لا نسلم الملازمة، فإنه ربما تأخر لجواز الوحي الذي','6','286',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1942,'عدمه شرط في الاجتهاد.
فإن شرط العمل بالقياس فقد النص، فانتظاره ليحصل الناس على النص، بأن يصبر مقدارًا يعرف به أن الله -تعالى- لا ينزل فيه وحيًا.
أو لأنه لم يجد أصلاً يقيس عليه في الحال فانتظر نصًا يقيس عليه.
وأيضًا: فربما تأخر الاجتهاد، فإن استفراغ الوسع يستدعي زمانًا.
وقيل: يجوز له -صلى الله عليه وسلم- الاجتهاد في الآراء والحروب دون غيرها، وقيل بالتوقف في هذه الثلاثة، ونقله في المحصول عن أكثر المحققين.
وإذا قلنا: بالجواز فقيل: وقع، واختاره الآمدي وابن الحاجب.','6','287',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1943,'وقيل: لم يقع.
وقيل بالوقف.
ومحل الخلاف في الفتوى، أما في الأقضية فيجوز الاجتهاد فيها قطعًا.
قاله القرافي، وتبعه غير واحد.
ويشهد له ما في سنن أبي داود عن أم سلمة -رضي الله تعالى عنها- قالت: أتى رسول الله (-صلى الله عليه وسلم- رجلان) يختصمان في مواريث وأشياء قد درست فقال: "إني إنما أقضي بينكم برأيي فيما لم ينزل علي فيه".','6','288',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1944,'قال الغزالي: فإذا اجتهد -صلى الله عليه وسلم- فقاس فرعًا على أصل، فيجوز القياس على هذا الفرع، لأنه صار أصلاً بالنص.
قال: وكذا لو أجمعت الأمة عليه.

<span data-type="title" id=toc-306>فرع: مبني على جواز الاجتهاد للرسول (صلى الله عليه وسلم)</span>.
وهو أنه لا يخطئ اجتهاده -صلى الله عليه وسلم- على المختار، خلافًا لجماعة، وإلا وجب اتباعه في ذلك الخطأ، لكوننا مأمورين','6','289',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1945,'باتباعه فيلزم الأمر باتباع الخطأ وهو باطل. وفي هذا الاستدلال نظر. فالأحسن أن يقال: إن اجتهاده -صلى الله عليه وسلم- لا يخطئ تنزيهًا لمنصب النبوة عن الخطأ في الاجتهاد.
المسألة الثانية
يجوز للغائبين عن الرسول -صلى الله عليه وسلم- من المجتهدين','6','290',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1946,'الاجتهاد وفاقًا، ويجوز للحاضرين أيضًا على الأصح، إذ لا يمتنع أمرهم به أي: بالاجتهاد بأن يقول الرسول -صلى الله عليه وسلم- لهم قد أوحي إلي أنكم مأمورون بالاجتهاد، أو بالعمل على وفق ظنكم.
وحكاية المصنف الاتفاق في الغائبين تبع فيه الأستاذ أبا منصور،','6','291',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1947,'وفي المحصول أنه جائز بلا شك لكن المشهور إجراء الخلاف فيه أيضًا.
وممن حكاه فيه الآمدي وغيره.
وهل المراد بالغيبة، الغَيبة عن مجلسه -صلى الله عليه وسلم- أو عن البلد التي هو فيها، أو إلى مسافة القصر فما فوقها أو إلى مسافة يشق معها الارتحال للسؤال عن النص عند كل نازلة.
قال الغزالي: لم أر فيه نقلاً، وهو محتمل.
وإذا قلنا بجوزاه: فقيل: وقع أيضًا، واختاره الغزالي والآمدي وابن الحاجب.
وقيل: لم يقع.
وقيل بالوقف، واختاره المصنف، حيث قال: "ولم يثبت وقوعه" يعني في الحاضرين، ولا ينبغي إعادته إلى الغائب أيضًا، فإنه مع كونه مخالفًا للظاهر مخالف لقول الأكثرين، وميل الإمام الرازي.','6','292',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1948,'ونظر في توقفه من جهة ما في الصحيحين في قول أبي بكر في قصة أبي قتادة في السلب، وإنما قال ذلك اجتهادًا وإلا أسنده إلى النص.','6','293',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1949,'وفي تحكيم النبي -صلى الله عليه وسلم- لسعد بن معاذ في بني قريظة فحكم بقتلهم فقال النبي -صلى الله عليه وسلم- "قضيت بحكم الله" في الصحيحين أيضًا.
وأجيب عن الأول: بأن الصديق إنما قاله تنفيذًا لقوله - (عليه الصلاة والسلام): "من قتل قتيلاً فله سلبه" لا باجتهاده.','6','294',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1950,'وعن الثاني: بأن محل النزاع حيث لم يوجد من الشارع إذن.
وفي طبقات الشيخ أبي إسحاق الشيرازي لم يكن أحد يفتي بحضرة النبي صلى الله عليه وسلم- غير أبي بكر الصديق (رضي الله تعالى عنه) وفيه ما تقدم من الاحتمال، وهنا فوائد في الشرح.
قيل من جهة مانعي الاجتهاد في حضرته - (صلى الله عليه سلم).
إن الاجتهاد عرضة للخطأ، أي: في معرض الخطأ، وهم قادرون على تحصيل النص الآمن منه، وسلوك السبيل المخوف مع','6','295',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1951,'القدرة على سلوك السبيل الآمن قبيح عقلاً.
قلنا لا نسلم كون الاجتهاد عرضة للخطأ بعد الإذن فيه، لأن الشرع لما قال: أنت مأمور بالاجتهاد أو بالعمل على وفق الظن كان آمنًا من الغلط، لأنه يكون بعد الاجتهاد آتيًا بما أمر به كذا في المحصول.
ونظر فيه من جهة أن كون الاجتهاد مأمورًا به لا ينافي كونه خطأ؛ لأن كون الاجتهاد خطأ عبارة عن عدم وجدان الأمارة الدالة على الحكم المعين في الواقعة كما سيأتي.
وإنما يمنع من التأثيم، فيجوز أن يكون مأمورًا بالعمل بالظن مع أنه لا يجد تلك الأمارة.
فالأحسن أن يجاب: بأنا لا نسلم أنه قادر على تحصيل النص فإنه قد يُسأل عن الواقعة، فلا يجد فيها شيئًا. بل يؤمر فيها بالاجتهاد.
سلمناه لكن لا نسلم أن ترك العمل بمقتضى الاحتياط قبيح.','6','296',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1952,'سلمناه لكنه مبني على التحسين والتقبيح العقليين.
الثالثة
المجتهد لا بد له أن يعرف من الكتاب والسنة ما يتعلق بالأحكام، وذلك لأن شرط الاجتهاد أن يكون المكلف متمكنًا من استنباط الأحكام الشرعية، ولا يحصل هذا التمكن إلا بمعرفة هذين الأمرين وما بعدهما، والتي تتعلق بالأحكام من الكتاب والسنة خمسمائة آية، كما قال الإمام الرازي.
وقيل: مائة، واستشكل؛ لأن تمييز آيات الأحكام من غيرها متوقف على معرفة الجميع، ولا يمكن المجتهد تقليد غيره في تمييزها','6','297',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1953,'والقرائح تتفاوت في استنباط الأحكام.
وفهم من التعبير "بالمعرفة" أنه لا يشترط حفظها، وبه صرح الإمام الرازي وغيره، بل يكون عارفًا بمواقعه حتى يرجع إليه في وقت الحاجة.
لكن نقل القيرواني عن الشافعي -رضي الله تعالى عنه- أنه يشترط حفظ جميع القرآن.
ولا يشترط أيضًا حفظ السنة المتعلقة بالأحكام ولا معرفة الجميع كما في الكتاب.','6','298',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1954,'قال الغزالي: ويكفيه أن يكون عنده أصل مصحح يجمع أحاديث الأحكام كسنن أبي داود، ومعرفة السنن للبيهقي، أو أصل وقعت العناية فيه يجمع أحاديث الأحكام يكتفي فيه بمعرفة مواقع كل باب فيراجعه وقت الحاجة.
قال النووي: والتمثيل بأبي داود لا يصح لأنه لم يستوعب الصحيح من أحاديث الأحكام ولا معظمه، وكم في صحيح البخاري وصحيح مسلم من حديث حكم ليس في سنن أبي داود.
ولا بد للمجتهد أن يعرف مواقع الإجماع حتى لا يخرقه ويفتي بخلافه، ولا يشترط حفظها، بل يكفي معرفته بأن ما أفتى به ليس مخالفًا للإجماع إما بأن يعلم موافقته لعالم، أو يظن أن تلك الواقعة حادثة لم يسبق لأهل الأعصار المتقدمة فيها كلام.','6','299',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1955,'ولا بد للمجتهد أن يعرف شرائط القياس المعتبرة، وعلم منه أنه لا بد من معرفته لأنه قاعدة الاجتهاد، والموصل إلى تفاصيل أحكام الوقائع التي لا حصر لها.
وقد اختلف في أن إنكار القياس هل يقدح في الاجتهاد أم لا؟
فقيل: يقدح، وبه قال إمام الحرمين والقاضي أبو بكر.
وقيل لا يقدح، ورجحه في جمع الجوامع، وهو متقضى كلام أصحابنا حيث ذكروا خلاف الظاهرية في تعاليقهم.
وقيل: لا يقدح إن أنكر القياس الخفي فقط.
فإن أنكر الجلي أيضًا قدح، وهو ظاهر كلام ابن الصلاح وغيره.
ولا بد أن يعرف المجتهد كيفية النظر في استفادة المجهول من المعلومات لأن المجتهد ناظر في استنباط الأحكام، فلا بد له من العلم','6','300',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1956,'بكيفية النظر.
فيشترط أن يعرف شرائط الحدود والبراهين، وكيفية تركيب مقدماتها، واستنتاج المطلوب منها.
ولا بد أن يعرف علم العربية، وهو العلم بلغة العرب مفرداتها ومركباتها وأحوالها من النحو والصرف، لأن شرعنا عربي، ولا تتم معرفته إلا بمعرفة كلام العرب ومن هذه الجهة يعرف العموم والخصوص، والحقيقة والمجاز، والإطلاق والتقييد، وغيره مما سبق.
ولا بد من معرفة الناسخ والمنسوخ من الكتاب والسنة لئلا يحكم','6','301',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1957,'بالمنسوخ المتروك.
ولا بد من معرفة حال الرواة في القوة والضعف، ومعرفة طرق الجرح التعديل لأن الأدلة لا اطلاع عليها إلا النقل، فلا بد من معرفة النقلة وأحوالهم ليعرف المنقول الصحيح من الفاسد.
ويكفي في الخبر بحال الرواة في زماننا الرجوع إلى أئمة ذلك من المحدثين، كالإمام أحمد والبخاري ومسلم والدارقطني وأبي داود ونحوهم، لأنهم أهل المعرفة بذلك، فجاز الأخذ بقولهم كما نأخذ بقول المقومين في القيم.
وأيضًا فلتعذرها في زماننا إلا بواسطة، وهم أولى من غيرهم.','6','302',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1958,'تنبيه: شرط المجتهد البلوغ، والعقل، وأن يكون ذا ملكة، أي: هيئة راسخة في النفس يدرك بها ما من شأنه أن يعلم.
ويعلم هذا من قول المصنف: "وكيفية النظر".
وأن يكون: فقيه النفس، أي: شديد الفهم لمقاصد الكلام.
وأن يكون: عارفًا بالدليل العقلي، أي: البراءة الأصلية، والتكليف به (في الحجية، كما تقدم أن استصحاب العدم الأصلي حجة) فيتمسك به، إلى أن يعرف عنه دليل شرعي.','6','303',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1959,'ومعرفة أسباب النزول في آيات الأحكام، فإن الخبرة بها ترشد إلى فهم المراد.
ومعرفة شرط التواتر والآحاد المحقق لهما المذكور فيما مر، لتقدم الأول على الثاني، فإنه إذا لم يكن خبيرًا به قد يعكس.
ولا حاجة في المجتهد إلى علم الكلام لإمكان الاستنباط لمن يجزم بعقيدة الإسلام.
والمصنف تابع للأصوليين في ذلك.
وقال الرافعي: عد الأصحاب من شروط الاجتهاد معرفة أصول العقائد.
قال الغزالي: وعندي أنه يكفي اعتقاد جازم، ولا يشترط معرفتها.','6','304',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1960,'على طريق المتكلمين.
ولا حاجة إلى الفقه لأنه نتيجته، -أي: نتيجة الاجتهاد- فكيف يحتاج إليه؟ وصحح ابن الصلاح اشتراط ذلك في المفتي الذي يتأتى به فرض الكفاية. ليسهل عليه إدراك أحكام الوقائع على القربة من غير تعب كثير، وإن لم يشترط ذلك في المجتهد المستقل.
وهو معنى قول الغزالي: إنما يحصل الاجتهاد في زماننا بممارسة الفقه، فهو طريق تحصيل الدراية في الزمان، ولم يكن الطريق في زمن الصحابة -رضي الله تعالى عنهم- ذلك.
ولا يشترط في المجتهد الذكورة والحرية، وكذا العدالة في','6','305',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1961,'الأصح وإنما يشترط الأمور المتقدمة في المجتهد المطلق.
وأما الذي دونه في الرتبة فهو مجتهد المذهب أي -المقلد لإمام من الأئمة- فلا يشترط فيه إلا معرفة قواعد إمامه، فإذا وقعت حادثة لم يعرف لإمامه فيها نصًا اجتهد فيها على مذهبه، وخرجها على أصوله.
ودونه في الرتبة مجتهد الفتيا، وهو: المتبحر في مذهب إمامه المتمكن من ترجيح قوله على آخر إذا أطلقهما.','6','306',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1962,'والصحيح جواز تجزئ الاجتهاد: بأن يحصل لبعض الناس قوة الاجتهاد في بعض الأبواب بأن يعلم أدلته باستقراء منه، أو من مجتهد كامل وينظر فيها

<span data-type="title" id=toc-308>الفصل الثاني: في أحكام الاجتهاد</span>','6','307',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1963,'فالمصيب من المختلفين في العقليات واحد، وهو: من صادف الحق فيها لتعينه في الواقع، وذلك كحدوث العالم، ووجود الباري (تبارك وتعالى) وصفاته، وبعثته للرسل.
وأما نافي الإسلام كله أو بعضه.','6','308',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1964,'(المجمع عليه، المعلوم من الدين بالضرورة، فإنه مخطئ آثم كافر، سواء اجتهد، أو لم يجتهد، لأنه) لم يصادف الحق فيهت.
ولا عبرة بمخالفة الجاحظ والعنبري، فإن الإجماع منعقد على خلاف قولهما قبل ظهورهما.
وأما غير العقلية وهي التي ليست أصلًا من أصول الشرع المجمع عليه.','6','309',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1965,'فإن كان فيها قاطع: فالمصيب فيها واحد وفاقًا.
وقيل: على الخلاف فيما لا قاطع فيها، فإن قصر في طلبه كان آئمًا، وإن لم يقصر فغير آثم على الأصح.
وأما التي لا قاطع فيها، فهي التي تكلم فيها المصنف فقال:
اختلف في تصويب المجتهدين في المسائل الفروعية التي لا قاطع فيها. هل كل مجتهد فيها مصيب أو المصيب فيها واحد؟
وقال بالأول الشيخ أبو الحسن الأشعري والقاضي أبو بكر وأبو يوسف ومحمد بن الحسن وابن سريج.','6','310',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1966,'ونقل عن جمهور المتكلمين من الأشاعرة والمعتزلة.
وقال الجمهور- وهو الصحيح-: أن المصيب واحد.
ونقل عن الأئمة الأربعة.
وقال ابن السمعاني في القواطع: إنه ظاهر مذهب الشافعي، ومن حكى عنه غيره فقد أخطأ.
والمصنف تابع للإمام في بناء هذا الخلاف على الخلاف في مسألة أخرى: وهي أن لكل صورة من صور الوقائع قبل الاجتهاد حكمًا معينًا لله تعالى فيها أم ليس فيها ذلك، بل الحكم فيها تابع لظن المجتهد، فما','6','311',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1967,'ظنه فهو حكم الله تعالى في حقه وحق مقلده.
فإن قلنا: لله- تعالى- في كل صورة حكمًا معينًا لا يكون كل مجتهد مصيبًا بل المصيب منه من حكم بذلك المعين.
وإن قلنا: إن حكم كل حادثة ما أدى إليه اجتهاد المجتهد كان كل مجتهد مصيبًا.
وقال الشيخ القاضي: حكم الله تعالى تابع لظن المجتهد.
وقال أبو يوسف ومحمد وابن سريج: إن في كل حادثة شيئًا لو حكم الله- تعالى- فيها لكان بذلك الشيء، وتسمى هذه المقالة "الأشبه" قاله في المنخول.','6','312',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1968,'وهذا حكم على الغيب.
ثم إن القائلين بأن لله في كل واقعة حكمًا معينًا سابقًا على اجتهاد المجتهد.
منهم من قال: لا دليل عليه وإنما هو كدفين يصيبه من شاء الله تعالى، ويخطئه من شاء الله تعالى.
ومنهم من قال: عليه دللي قطعي، وظني.','6','313',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1969,'فالقائل: بأنه قطعي بشر المريسي وأبو بكر الأصم، وعليه المخطئ آثم.
والقائل بأنه ظني الأستاذ، ونقل عن الأئمة الأربعة وأكثر الفقهاء وكثير من المتكلمين.
والمختار عند المصنف تبعًا للإمام الرازي ما صح عن الشافعي- رضي الله عنه- أن في الحادثة حكمًا معينًا عليه أمارة من وجدها أصاب،','6','314',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1970,'ومن فقدها أخطأ ولا يأثم.
فإن قلت كيف يصح القول بأن حكم الله تعالى تابع لظن المجتهد، مع القول بقدم الحكم لأن ظن المجتهد حادث، ويمتنع إيقاع القديم بالحادث، فكيف يصح إسناد هذا القول إلى الأشعري والقاضي والأئمة، ومذهبهم أن الحكم قديم.
أجيب: بأنه ليس المراد بالحكم هنا خطاب الله تعالى ليرد هذا، بل المراد به: ما يتأدى إليه الاجتهاد ويستلزمه ويجب عليه.
وعلى من يقلده العمل به.
فقيل: ذلك حكم واحد أي: نسبة إيقاعية بين المكلف والفعل بالاقتضاء أو التخيير معين عند الله تعالى وفي نفس الأمر يتبعه الاجتهاد، فإن تأدى إليه كان صحيحًا، وإن تخطاه كان فاسدًا ومع هذا تجب متابعة ظنه الفاسد ولا يأثم به.
وقيل غير ذلك.','6','315',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1971,'واستدل المصنف لمختاره بقوله: لأن الاجتهاد مسبوق بالدلالة، إذ الاجتهاد: هو طلب دلالة الدليل على الحكم، وطلب الدلالة متأخر عن الدلالة، لأن الوقوف على الشيء، يستدعي تقدم ذلك الشيء في الوجود. فثبت أن الاجتهاد مسبوق.
بالدلالة لأنه، أي: الاجتهاد طلبها، أي: طلب دلالة الدليل على الحكم، وطلب الدلالة متأخر عن الدلالة؛ لأن الوقوف على الشيء يستدعي تقدم ذلك الشيء في الوجود، فثبت أن الاجتهاد مسبوق بالدلالة).
والدلالة متأخرة عن الحكم؛ لأنها نسبة بين الدليل والمدلول الذي هو الحكم، والنسبة بين الأمرين متأخرة عنهما؛ فإذا ثبت أن الدلالة متأخرة من الحكم لزم أن يكون الاجتهاد متأخرًا عن الحكم بمرتبتين، لأنه متأخر عن الدلالة المتأخرة عن الحكم.','6','316',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1972,'فلو تحقق الاجتهادان بأن يكون مدلول كل واحد حقًا صوابًا لاجتمع النقيضان. لاستلزامه حكمين متناقضين في نفس الأمر بالنسبة إلى مسألة واحدة.
ولأنه قال- عليه الصلاة والسلام-: "من أصاب فله أجران ومن أخطأ فله أجر واحد".
متفق عليه بلفظ: "إذا حكم الحاكم فاجتهد ثم أصاب فله أجران، فإذا حكم فاجتهد ثم أخطأ فله أجر".
وفي رواية الحاكم: "إذا اجتهد الحاكم فأخطأ فله أجر، وإن أصاب فله عشرة أجور" ثم قال: صحيح الإسناد.','6','317',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1973,'فدل على أن المجتهد قد يكون مخطئًا وقد يكون مصيبًا، وهو يدل: على أن في الواقعة حكمًا معينًا، وإلا لكان الحكم بكون أحدهما مخطئًا والآخر مصيبًا ترجيحًا بلا مرجح.
ودل أيضًا: على أن المخطئ لا يأثم، لأنه حكم- عليه الصلاة والسلام- بأنه مأجور.
وفي دليلي المصنف نظر.
أما الأول: فلا نسلم أن طلب الشيء يتوقف على ثبوته في الخارج بل في النفس.
سلمناه: لكن لا يثبت به المدعى بتمامه فإنه لا يدل على سقوط','6','318',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1974,'الإثم عن المخطيء وحصول الأجر.
وأيضًا فقوله: الاجتهاد: طلب الدلالة ممنوع، بل طلب الحكم نفسه لكن بسبب الدلالة.
وأما الثاني: فهو خبر آحاد، والمسألة أصولية. سلمناه لكن لا دلالة فيه؛ لأن القضية الشرطية لا تدل على وقوع شرطها بل ولا على جواز وقوعه. وأيضًا فالخطأ متصور عند القائلين بأن كل مجتهد مصيب. فيما إذا كان نص أو إجماع أو قياس جلي، وظن المجتهد غيره بعد الجهد.
قيل: لو تعين الحكم لكان ما أنزل اله- تعالى في تلك الواقعة ذلك الحكم، فالمخالفة له وهو الحكم بغير ذلك الحكم لم يحكم بما','6','319',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1975,'أنزل الله؛ فيفسق ويكفر لقوله تعالى: {ومن لم يحكم بما أنزل الله فأولئك هم الفاسقون}.
وفي موضع آخر: {فأولئك هم الكافرين}.
لكن المخالف ليس بفاسق ولا كافر إجماعًا، فلا يكون في الواقعة حكم معين وهو المطلوب.
قلنا: لما أمر المجتهد بالحكم بما ظنه- وإن أخطأ فيه- فقد حكم بما أنزل الله تعالى، إذ من جملة ما أنزل الله تعالى أن كل مجتهد مأمور بأن يحكم في الواقعة بمقتضى ظنه وإن كان خطأ، وحينئذ لا يكفر المخالف ولا يفسق.
قيل أيضًا: لو لم يصوب الجميع من المجتهدين، لما جاز للمجتهد','6','320',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1976,'نصب المخالف له في الحكم، لكونه حاكمًا بزعمه بغير ذلك الحكم المعين وغير ذلك الحكم المعين باطل، فيكون الحاكم بزعمه مبطلًا، إذ لا نعني بالمبطل سوى الحاكم بالباطل، والمبطل لا يجوز نصبه بالإجماع فثبت أنه لو لم يقم كل مجتهد مصيبًا لما أجاز نصب المخالف، ولكن نصب المخالف جائز.
إذ قد نصب أبو بكر زيدًا بن ثابت- رضي الله عنهما- وزيد كان يخالفه في كثير من المسائل كالجد.
وشاع ذلك في الصحابة- رضي الله عنهم- ولم ينكروه فيكون كل مجتهد مصيبًا، فلا يكون حكم الواقعة قبل الاجتهاد معينًا وهو المطلوب.
قلنا: نعم لم يجز تولية المبطل، والمخطئ في الاجتهاد ليس بمبطل.
إذ المبطل هو الذي يخالف الحق عمدًا، والمخطئ آت بالمأمور به.','6','321',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1977,'فليس بمبطل قطعًا، والممنوع إنما هو تولية المبطل لا المخطئ فلا يتم ما ذكرتم.
قال الزركشي: والمعروف أن الذي استخلف زيد بن ثابت إنما هو عمر وعثمان وأما أبو بكر- رضي الله عنهم أجمعين فنصبه لجمع القرآن.

<span data-type="title" id=toc-309>فرعان- على القول بالاجتهاد سواء كان في الواقعة حكم معين قبل الاجتهاد أم لا</span>؟','6','322',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1978,'الفرع الأول: في بيان كيفية رفع المنازعة عن واقعة تعلقت بالمجتهدين ولا يمكن توافقهما فيها.
مثاله: لو رأى الزوج لفظة كناية ورأته المرأة صريحًا في الطلاق فله، أي: فللزوج الطلب للوطء منها لبقاء الزوجية بينهما في ظنه، ولها الامتناع منه لوقوع الطلاق في ظنها.
فهذه منازعة وفي تصالحهما إشكال يرد على المذهبين، لوجوب العمل بالظن.','6','323',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1979,'وإن كان خطأ فيراجعان غيرهما من المجتهدين أو غيرهم ليفصل بينهما.
وإن كان صاحب الواقعة حاكمًا إذ الحاكم لا يحكم لنفسه، بل ينصب من يقضي بينهما.
وليس هذا تقليدًا للمجتهد، بل هو ترجيح أحد الاجتهادين من الزوجين على الآخر بمرجح، لما عرفت أن ظنين أقوى وأرجح من ظن واحد كذا قيل.','6','324',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1980,'الفرع الثاني:
إذا تغير الاجتهاد، كما لو ظن أن الخلع فسخ لا ينقص عدد الطلاق به، فخالع امرأته ثلاث مرات، ثم تغير اجتهاده وظن أنه طلاق، فلا ينقض الاجتهاد الأول بعد اقتران الحكم به، بل يبقى النكاح صحيحًا، وليس للزوج تسريح المرأة نقضًا للأول، لأن قضاء القاضي لما اتصل به تأكد، فلا يؤثر تغير الاجتهاد، وينقض الاجتهاد الأول قبله، أي: قبل قضاء القاضي؛ لأنه ظن خطؤه، وصواب الاجتهاد الثاني، والعمل بالظن واجب فيسرح المرأة.
والمراد بعدم النقض: أن يقر العمل الأول على ما مضى ويترك العمل به بعد.','6','325',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1981,'والمراد ينقضي الاجتهاد الأول: ترك العمل به، وإلا فالاتفاق على أن الاجتهاد لا ينقض بالاجتهاد.
وإنما يعمل بالاجتهاد فيما عدا الأحكام المبنية على الاجتهاد الأول.
نعم إن خالف في حكمه الأول نص كتاب أو سنة، أو إجماعًا.
أو ظاهرًا، ولو قياسًا، نقض حكمه ومحل ذلك أن يكون النص المخالف موجودًا.
فإن حدث بعده، وهذا إنما يتصور في عصره- صلى الله عليه','6','326',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1982,'وسلم- لم ينقض ما مضى، صرح به الماوردي.
وذكر في جمع الجوامع: أنه ينقض الحكم في المسائل الاجتهادية في مسألتين: حكم المجتهد علي خلاف اجتهاد نفسه. وهو باطل لمخالفته ما وجب عليه العمل به من الظن.
وحكم المقلد بخلاف اجتهاد إمامه، إلا أن يفعل ذلك لتقليده غيره وبقيت فروع وفوائد مبينة في الشرح.','6','327',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1983,'<span data-type="title" id=toc-310>الباب الثاني: في الإفتاء</span>
وفيه مسائل:
وهو منحصر في المفتي، والمستفتي، وما فيه الاستفتاء وجعل لكل واحد مسألة.
الأولى: في المفتي
يجوز الإفتاء للمجتهد إذا اتصف بالشروط المعتبرة في الراوي، وكذا نحو إفناء مقلد الحي.
واعلم أن من لم يبلغ رتبة الاجتهاد المطلق تحته صورتان
إحداهما: أن يصل إلى رتبة الاجتهاد المقيد، بأن استقل بتقرير مذهب إمام معين كما هي صفات أصحاب الوجوه، وهذا يجوز له الإفتاء','6','329',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1984,'قطعًا.
الثاني: : أن لا يصل إلى هذه الرتبة وإمامه حي.
فقيل: يجوز له الإفتاء مطلقًا بما صح عنده من مذهب إمامه سواء كان سماعًا منه، أو رواية عنه، أو مسطورًا في كتاب معتمد عليه، وهو مقتضى اختيار الإمام وجزم المصنف.
وقيل: عكسه.
وقيل: إن كان مطلعًا على المأخذ أهلًا للنظر، جاز، لوقوع ذلك على [مر] الإعصار من غير إنكار، وإلا لم يجز؛ لأنه يفتي بغير علم.','6','330',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1985,'واختاره الآمدي وابن الحاجب. وحكى عن الأكثرين.
قال العراقي: ويمكن رد كلام المصنف إليه.
وإن كان إمامه ميتًا ففي الإفتاء بقوله خلاف ينبني على جواز تقليده.
والعلماء قد اختلفوا في تقليد الميت: فقيل: لا يجوز تقليده مطلقًا، وعزاه في المنخول لإجماع الأصوليين؛ لأنه أي: لأن الميت لا قول له لانعقاد الإجماع عل خلافه.
ولو كان له قول لم ينعقد، كما لا ينعقد على خلاف قول الحي.
وإذا لم يكن له قول لا يجوز تقليده، ولا الإفتاء بما كان ينسب','6','331',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1986,'إليه.
قالوا: وإنما صنفت كتب الفقه لاستفادة طرق الاجتهاد من تفرقهم في الحوادث، وكيفية بناء بعضها على بعض، ومعرفة المتفق عليه من المختلف فيه ..
ورد بالمنع لما سبق فيه من الخلاف، وإن سلم فهو معارض بحجة الإجماع بعد موت المجمعين.
والمختار جوازه، وبه قال الجمهور.
وعبر عنه الشافعي- رضي الله تعالى عنه- بقوله: المذاهب لا تموت بموت أربابها للإجماع عليه في زماننا.','6','332',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1987,'فإنهم أجمعوا على جواز العمل بهذا النوع من الفتوى؛ إذ ليس في الزمان مجتهد، ونظر فيه: من جهة أن عند عدم المجتهدين كيف ينعقد الإجماع، فالأولى أن يقال: غنه لو لم يجز لأدى إلى فساد أحوال الناس وتضررهم.
وأيضًا: فالعدل الموثوق به إذا حكى عن مجتهد عدل حكمًا للعامي، غلب على ظنه صدق الحاكي، وصدق المجتهد، وجب عليه قبوله والعمل به؛ لأن العمل بالظن واجب.
فائدة: التقليد: العمل بقول الغير من غير حجة.','6','333',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1988,'والمراد: بالقول ما يعم الفعل والتقرير تغليبًا.
تنبيه:
إنما انتقل المصنف من الإفتاء إلى التقليد؛ لأن إفتاء المقلد فرع على جواز التقليد ومبني عليه.

<span data-type="title" id=toc-312>المسألة الثانية: في المستفتي:</span>
وهو: من يجوز له الاستفتاء، ومن لا يجوز له.
ومن يريد الاستفتاء مجتهد وغيره.
فغيره: وهو من لم يبلغ رتبة الاجتهاد إذا كان عالمًا، يجوز له','6','334',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1989,'الاستفتاء في الفروع.
والصحيح جواز التقليد له، بل يجب عليه تقليد مجتهد.
وكان يجوز الاستفتاء للعامي، لعدم تكليفهم، أي: لعدم تكليف العوام في شيء من الإعصار بالاشتغال بأسبابه، أي: بأسباب الاجتهاد. فلو كانوا مأمورين بذلك لكلفوا به، وأنكروا عليهم العمل بفتاويهم مع أنه لم يقع شيء من ذلك.
وفيه أيضًا: تفويت لمعايشهم واستضرارهم بالاشتغال بتحصيل أسبابه.
وذلك سبب لفساد الأحوال، فيكون القول به باطلًا، بل','6','335',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1990,'يجب عليهم التقليد لمن له الأهلية دون المجتهد، فإنه لا يجوز له الاستفتاء بعد الاجتهاد وظن الحكم، ولا التقليد اتفاقًا، ويجب عليه العمل بما ظنه.
وكذا لا يجوز له الاستفتاء ولا التقليد إذا لم يجتهد على المختار.
لأنه مأمور بالاعتبار- أي: بالاجتهاد- (في قوله) تعالى {فاعتبروا} فإنه شامل له وللعامي أيضًا، لكن ترك العمل به بالنسبة إلى العامي لعجزه عن الاجتهاد فبقي معمولًا به في حق المجتهد.
وحينئذ لو جاز له الاستفتاء لكان تاركًا للاعتبار المأمور به، وتركه معصية فلا يجوز.','6','336',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1991,'ونبه الإمام الرازي على أن الخلاف في الجواز لا في الوجوب.
وقيل: لا يجوز التقليد لأحد لا العامي ولا غيره، بل يجب على كل واحد أن يقف على الحكم بطريقه.
وكاد ابن حزم يدعي الإجماع على النهي عن التقليد، وحكي ذلك عن مالك والشافعي (رضي الله عنهما) وغيرهما.
وقال الصيدلاني: إنما نهى الشافعي- رضي الله عنه- عن التقليد','6','337',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1992,'لمن بلغ رتبة الاجتهاد، فمن قصر عنها فليس له إلا التقليد.
وقيل: يجوز للمجتهد أن يستفي ويقلد، وحكي عن سفيان الثوري وأحمد وإسحاق- رضي الله عنهم.
قيل: كون المجتهد قبل الاجتهاد لا يجوز له الاستفتاء لأنه مأمور بالاعتبار، كما تقدم.
معارض بعموم قوله تعالى: {فاسألوا لأهل الذكر إن كنتم لا تعلمون}.
فإنه يدل على جواز السؤال لمن لا يعلم، مجتهدًا كان أو غيره، والمجتهد قبل اجتهاده غير عالم فوجب أن يجوز له ذلك.','6','338',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1993,'وبقوله تعالى: {يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم}.
فإنه يدل على وجوب طاعة أولي الأمر على كل واحد من المؤمنين سواء كان مجتهدًا أو غيره، والعلماء من أولي الأمر؛ لأن أمرهم ينفذ حتى على الولاة والأمراء، فيكون قولهم معمولًا به في حق المجتهد والمقلد، فيجوز للمجتهد قبل اجتهاده الأخذ بقول العلماء، وهو المطلوب.
ومعارض أيضًا بالإجماع، وهو قول عبد الرحمن بن عوف لعثمان- رضي الله عنهما- أبايعك على كتاب الله- تعالى- وسنة رسوله-','6','339',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1994,'صلى الله عليه وسلم- وسيرة الشيخين.
فالتزمه عثمان (وكان ذلك) بمحضر من الصحابة، فلم ينكر عليهما أحد، فكان ذلك إجماعًا على جواز أخذ المجتهد بقول المجتهد الميت، وإذا جاز ذلك جاز الأخذ بقول الحي بطريق الأولى.
وقول عبد الرحمن (رضي الله عنه) رواه البخاري قريبًا مما ذكره المصنف.','6','340',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1995,'ورواه عبد الله بن احمد في زياداته على المسند بلفظ: "أبايعك على كتاب الله وسنة رسوله وسيره أبي كبر وعمر".
قلنا: الجواب عن الأول، وهو قوله تعالى: {فاسألوا} مخصوص بالعوام، وإلا أي: لو يخص بالعوام بل شمل المجتهدين الغير العالمين، لوجب على المجتهد بعد الاجتهاد، ولا قائل به.
ويجب أن يكون قوله: "لوجب" أي: لوجب أن يجوز للمجتهد التقليد بعد الاجتهاد، وهو باطل أيضًا.
وأيضًا: {فاسألوا} أمر بالسؤال من غير تعيين المسئول، وهو','6','341',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1996,'مطلق صادق تصوره، ويقول به في السؤال عن الأدلة والكشف عنها.
وأيضًا: فالمجتهد لا يخرجه عن كونه عالمًا غبية المسألة عن ذهنه مع تمكنه من معرفتها من غير احتياج على غيره.
والجواب عن الثاني وهو قوله: {أطيعوا الله} الآية.
إنه إنما ورد في الأقضية جون المسائل الاجتهادية؛ لأنه مطلق لا عموم له فيصدق بذلك، أو هي طاعتهم في قبول أدلة الأحكام.
والمراد من السيرة: في قول عبد الرحمن: العدل، والإنصاف بين الناس، والبعد عن حب الدنيا؛ لا الأخذ بالاجتهاد.
وإلا لجاز للمجتهد تقليد الميت، وهو باطل بالإجماع. كما قاله العبري.','6','342',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1997,'وعلم من كلام المصنف جواز تقليد المفضول من المجتهدين مع التمكن من تقليد الفاضل، وهو المشهور.
وفيه خلاف مذكور في الشرح مع فوائد أخرى.

<span data-type="title" id=toc-313>المسألة الثالثة: فيما يجوز فيه الاستفتاء وما لا يجوز</span>.
إنما يجوز الاستفتاء في الفروع على ما سبق، فإذا وقعت لعامي','6','343',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1998,'حادثة فاستفتى فيها مجتهدًا، وعمل فيها بفتوى ذلك المجتهد، فليس له الرجوع عنه إلى فتوى غيره في تلك الحادثة بعينها بالإجماع، كما نقله ابن الحاجب وغيره.
(وفي جمع الجوامع: الخلاف فيه).
وإن كان قبل العمل، فقال النووي: المختار ما نقله الخطيب وغيره أنه إن لم يكن هناك مفت آخر لزمه بمجرد فتواه وإن لم تسكن نفسه.','6','344',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (1999,'وإن كان هناك آخر لم يلزمه بمجرد إفتائه إذ ليس له أن يسأل غيره.
وحينئذ فقد يخالفه فيجيء فيه الخلاف في اختلاف المفتين.
أما إذا وقعت له حادثة غير تلك، فالأصح أنه يجوز له أن يستفتي فيها غير من استفتاه في الحادثة السابقة.
وقطع الكيا الهراسي بأنه يجب على العامي أن يلتزم مذهبًا معينًا.','6','345',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2000,'واختار في جمع الجوامع: أنه يجب ذلك ولا يفعله بمجرد التشهي، بل يختار مذهبًا يقلده في كل شيء يعتقده أرجح أو مساويًا لغيره لا مرجوحًا.
وقال النووي: الذي يقتضيه الدليل أنه لا يلزمه التمذهب بمذهب، بل يستفتي من شاء، لكن من غير تلفظ للرخص ولعل من منعه لم يثق بعدم تلفظه، وإذا التزم مذهبًا معينًا، فيجوز له الخروج عنه على الأصح في الرافعي.
ثم إذا قلنا بالجواز فشرطه أن لا يتتبع الرخص بأن يختار من كل مذهب ما هو أهون عليه بل يفسق بذلك كما نقله الرافعي عن أبي','6','346',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2001,'إسحاق.
وعن أبن أبي هريرة: لا يفسق.
وذكر القرافي: أن تقليد مذهب الغير حيث جوزناه؛ فشرطه أن لا يكون موقعًا في أمر يجتمع على إبطاله الإمام الذي كان على مذهبه والإمام الذي انتقل إليه.
واختلف في الاستفتاء والتقليد في الأصول، وهو علم يبحث فيه عن ذات الله تعالى وما يجب له، وما يمتنع عليه من الصفات وأحوال الممكنات والمبدأ والمعاد على قانون الإسلام؛ كوجود الباري ووحدته وإثبات الصفات وصحة النبوات.
فقال الجمهور: لا يجوز للمجتهد ولا للعامي التقليد فيه؛ لأن','6','347',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2002,'تحصيل العلم في الأصول واجب على الرسول- صلى الله عليه وسلم بقوله تعالى: {فاعلم أنه لا إله إلا الله}.
وإذا وجب على الرسول وجب علينا لوجوب متابعته بقوله تعالى: {واتبعوه}.
والأصل عدم اختصاص النبي- صلى الله عليه وسلم- بهذا.
(وأدلة أخرى مذكورة في الشرح) وقيل: يجوز.','6','348',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2003,'وقيل: يجب التقليد ويحرم النظر والبحث فيه.
وظاهر كلام الشافعي- رضي الله عنه- على ما قيل يوافق هذا المذهب، حيث ذمه وحمل ذمه له حيث نظر الناظر فيه من غير كلام الله تعالى ورسوله- صلى الله عليه وسلم- بالنظر في علم الأوائل الذي هو جهل وضلال.
وقال البيهقي: إن نهي الشافعي- رضي الله تعالى عنه- وغيره إنما هو لإشفاقهم على الضعفة أن لا يبلغوا ما يريدون منه فيضلوا، وقد زلت بسببه أقدام جماعة، مع أن أصحابنا عدوه من فروض الكفايات أن نظر فيه بالشرعيات.
وإذا منعنا من التقليد فيه فحكي عن الأشعري أن إيمان المقلد لا يصح، وأنه يقول بتكفير العوام.','6','349',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2004,'وأنكره الأستاذ أبو القاسم القشيري، وقال: هذا كذب وزور من تلبيس الكرامية على العوام، فإنهم يقولون: الإيمان: الإقرار المجرد.
وعند الأشعري الإيمان هو التصديق، والظن بجميع عوام المسلمين أنهم يصدقون الله تعالى في أخباره.','6','350',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2005,'فأما ما ينطوي على العقائد فالله تعالى أعلم به انتهى.
وحمل بعضهم كلام الأشعري بتقدير صحته عنه على أنه أراد به أن من اختلج في قلبه شيء من السمعيات القطعية كالإسراء أو النبوة، وجب أن يجتهد في إزالته بالدليل العقلي؛ فإن استمر على ذلك لم يصح إيمانه.
وقال الأستاذ أبو منصور: أجمع أصحابنا على أن العوام مؤمنون عارفون بالله- تعالى- وأنهم حشو الجنة للإخبار والإجماع فيه، لكن منهم من قال: لا بد من نظر عقلي في العقائد وقد حصل لهم منه القدر الكافي، فإن فطرتهم جبلت على توحيد الصانع وقدمه وحدوث الموجودات سوى الله تعالى، وإن عجزوا عن التعبير عن اصطلاح المتكلمين.
والعلم بالعبارة علم زائد لا يلزمهم.
وتنقيح مناط الخلاف المتقدم ما ذكره صاحب جمع الجوامع، وهو انه: إن أريد بالتقليد الأخذ بقول الغير بغير حجة مع احتمال شك أو وهم كما في تقليد إمام في الفروع مع تجويز أن يكون الحق في','6','351',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2006,'خلافه، فهذا لا يكفي في الإيمان (عند أحد لا الأشعري ولا غيره.
وإن أريد به الاعتقاد الجازم لا لموجب، فهذا كاف في الإيمان).
ولم يخالف في ذلك إلا أبو هاشم من المعتزلة، كذا حكاه عن والده، وسبقه إليه الآمدي في الإنكار. قاله العراقي.
ولعل قول المصنف: ولنا فيه نظر إشارة إلى تحرير محل النزاع.
وما المراد بالتقليد على ما سبق تحقيقه.
ومقتضى كلام المصنف- (رحمه الله تعالى) - في مواضع كثيرة من تفسيره ترجيح المنع من التقليد.
وكلام الأصفهاني في شرحه للمنهاج يرمز إلى ما شرحته عليه.
وهو أقرب ما بين به النظر ويحتمل غير ذلك.','6','352',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2007,'والله تعالى أعلم.
(وليكن هذا آخر كلامنا والله الموفق والهادي).
والحمد لله رب العالمين حمدًا يوافي نعمه ويكافئ مزيده بجميع محامده كلها، ما علمت منها وما لم اعلم، على جميع نعمه كلها ما علمت منها وما لم أعلم، لدى خلقه كلهم ما علمت منهم وما لم أعلم، كما هو أهله، وصلى الله وسلم، على سيدنا محمد، سيد المرسلين، وإمام المتقين، ورسول رب العالمين، حبيب اله تعالى، أبي القاسم، خاتم النبيين، وأفضل خلق الله أجمعين، كما صليت على إبراهيم وآل إبراهيم في العالمين إنك حميد مجيد، كلما ذكره الذاكرون والذاكرات، وكلما غفل عن ذكره الغافلون والغافلات، وعلى سائر الأنبياء والمرسلين وآل كل وسائر الصالحين.
اللهم يا أرحم الراحمين اجمع لنا ولمشايخنا ولأولادنا ولمن أحبنا وأحببناه فيك خيري الدنيا والآخرة، وادفع عنا شرهما بكرمك وإحسانك يا','6','353',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2008,'دائم المعروف، يا كثير الخير، وارحم والدنيا وأدخلنا الجنة ومتعنا بالنظر إلى وجهك الكريم وأنت راض عنا، من غير عذاب يسبق، واجعلنا من أحبابك ومحبيك، ولا تخيب رجاءنا فيك، ولا في نبيك سيد المرسلين- صلى الله عليه وسلم-.
اللهم شفعه فينا، ورضه عنا، وارض عنا أبدًا دائمًا، ووفقنا لإتباع شريعته- صلى الله عليه وسلم-، ولا تنزع منا ما وهبته لنا من الخير، وزدنا خيرًا يليق بكرمك وإحسانك، وانفع بهذا الكتاب.
اعتصمت بالله، توكلت على الله، أسلم أمري على الله، ما شاء الله، لا قوة إلا بالله العلي العظيم؟
قال مؤلفه خلده الله تعالى في السعادة الدائمة العظمى التي تليق بكرم الله تعالى، وجوده وإحسانه، وفضله المطلق العميم هو ومن أحب في الله ومن أحبه وأولاده ومن يلوذ به (وكذلك يفعل بنا وجميع المسلمين بمنه وكرمه وجوده وإحسانه القديم).
كتبه مؤلفه الفقير إلى رحمة ربه محمد بن محمد الإمام بالمدرسة الكاملية بين القصرين عامله الله تعالى بخفي لطفه العظيم، ورضي الله تعالى عن سيدي عبد القادر الكيلاني سلطان العارفين في زمنه، وجعلني في بركته وأولادي وأهلي وأحبائي أبدًا دائما آمين.','6','354',NULL,NULL,'0');
INSERT INTO [b18274] VALUES (2009,'وكان الفراغ منه في ليلة يسفر صباحها عن ثاني جمادي الآخر سنة خمس وأربعين وثمانمائة.
ثم ألحقت فيه زيادات من كتاب التحرير لما في منهاج الأصول من المنقول والمعقول لشيخ الإسلام ولي الدين العراقي تغمده الله برحمته.
وأصول هذا الكتاب الذي جمعته وأصله منها كتب الأئمة الأعلام الشيخ جمال الدين الإسنوي، والعبري، والحلواني، والقاضي عضد الدين في شرحه مختصر ابن الحاجب، والشيخ سعد الدين، والشيخ سيف الدين الأبهري، والشيخ ولي الدين العراقي في شرح جمع الجوامع، والشيخ بدر الدين الزركشي في تخريج أحاديث المنهاج والمختصر وغير ذلك- رضي الله تعالى عنهم أجمعين- والحمد لله رب العالمين. (وصلى الله على سيدنا محمد وآله وصحبه وسلم تسليمًا كثيرًا إلى يوم الدين وحسبنا الله ونعم الوكيل ولا حول ولا قوة إلا بالله العلي العظيم).','6','355',NULL,NULL,'0');
