INSERT INTO [b18562] VALUES (357,'الشيخ جمال الدين أبي الفرج عبد الرحمن بن علي بن محمد المعروف بابن الجوزي، المتوفى سنة ٦٥٦ هـ، الطبعة الثانية بومباي الهند ١٣٧٩ هـ / ١٩٥٩ م.
