INSERT INTO [b19235] VALUES (778,'المحبر: للعلامة الإخباري النسابة محمد بن حبيب بن أمية الهاشمي البغدادي المتوفى سنة ٢٤٥ هـ، برواية أبي سعيد الحسن بن الحسين السكري طبع دائرة المعارف النعمانية حيدر أباد الدكن سنة ١٣٦١ هـ.
