INSERT INTO [b1994] VALUES (417,'<span data-type="title" id=toc-274>٢٥٤ - القاضي أبو يوسف * </span>(ع)
الإمام العلّامة، فقيهُ العِراقَين، يعقوبُ بن إبراهيم الأنصاريُّ الكوفي، صاحبُ أبي حنيفة.

روى عن: هشام بن عُروة، وأبي إسحاق الشَّيباني، وعطاء بن السَّائب، وطبقتهم.
وعنه: محمد بن الحسن الفقيه، وأحمد بنُ حنبل، وابنُ مَعين، وبشر بن الوليد، وعلي بنُ الجَعْد، وعليُّ بنُ مسلم الطوسي، وخلق.
نشأ في طلب العِلم، وكان أبوه فقيرًا، فكان أبو حنيفة يتعاهدُ أبا يوسف بمئةٍ بعدَ مئة.
وقال المزني: أبو يوسف أتبعُ القوم للحديث (١).
_________
* طبقات ابن سعد: ٧/ ٣٣٠، تاريخ البخاري الكبير: ٨/ ٣٩٧، التاريخ الصغير: ٢/ ٢٢٨، المعارف: ص ٤٩٩، المعرفة والتاريخ: ١/ ١٣٣ وغيرها، أخبار القضاة: ٣/ ٢٥٤، الجرح والتعديل: ٩/ ٢٠١، مشاهير علماء الأمصار: ت ١٣٥٦، فهرست النديم: ص ٢٥٦، تاريخ جرجان: ص ٤٨٧، الاستيعاب: ص ٥٨٤ ضمن ترجمة سعد بن حبتة، الانتفاء: ١٧٢، تاريخ بغداد: ١٤/ ٢٤٢، طبقات الشيرازي: ص ١٣٤، أنساب السمعاني: ٢/ ٨٦، وفيات الأعيان: ٦/ ٣٧٨، سير أعلام النبلاء: ٨/ ٥٣٥ - ٥٣٩، ميزان الاعتدال: ٤/ ٤٧٤، العبر: ١/ ٢٨٤، تذكرة الحفاظ: ١/ ٢٩٢، مرآة الجنان: ١/ ٣٨٢، الجواهر المضية: ٢/ ٢٢٠ (طبعة الهند)، النجوم الزاهرة: ٢/ ١٠٧، تاج التراجم: ٦٠، طبقات الحفاظ: ص ١٢١، مفتاح السعادة: ٢/ ١٠٠، شذرات الذهب: ١/ ٢٩٨، الفوائد البهية: ص ٢٢٥، مناقب الإمام أبي حنيفة: ٢/ ١٤٣، هدية العارفين: ٢/ ٥٣٦، الرسالة المستطرفة: ص ٥٢، طبقات الأصوليين: ١/ ١٠٨، تاريخ التراث العربي: ٢/ ٤٩.
(١) ميزان الاعتدال: ٤/ ٤٤٧.','1','421',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (418,'وقال يحيى بنُ يحيى التَّميمي: سمعتُ أبا يوسفَ يقول عند وفاته: كلُّ ما أَفتيتُ به فقد رجعتُ عنه إلَّا ما وافق الكتاب والسُّنة (١).
وفي لفظ: إلَّا ما في القرآن، واجتمع عليه المسلمون.
وروى إبراهيم بنُ أبي داود البُرُلُّسي، عن ابن مَعين قال: ليس في أصحاب الرَّأي أحدٌ أكثرَ حديثًا ولا أثبتَ من أبي يوسف (٢).
وروى عبّاس، عن ابن مَعين قال: أبو يوسف صاحب حديث، وصاحب سنَّة (٣).
وقال أبو سَماعة: كان أبو يوسف يصلِّي بعدما وليَ القضاء في كل يوم مئتي ركعة (٤).
وقال أحمد: كان منصفًا في الحديث (٥).
وقال الفلّاس: صدوق (٦).
مات في ربيع الآخر سنة اثنتين وثمانين ومئة، عن سبعين سنة إلَّا سنَة.
وأكبرُ شيخ له حُصين بنُ عبد الرحمن، ولم يلقَ عبدَ اللهِ بنَ دينار، بل بينهما رجل.
_________
(١) تاريخ بغداد: ١٤/ ٢٥٤.
(٢) تاريخ بغداد: ١٤/ ٢٥٩.
(٣) سير أعلام النبلاء: ٨/ ٥٣٧.
(٤) تاريخ بغداد: ١٤/ ٢٥٥.
(٥) تاريخ بغداد: ١٤/ ٢٦٠.
(٦) المصدر السابق.','1','422',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (419,'<span data-type="title" id=toc-275>٢٥٥ - أبو معاوية * </span>(ع)
الحافظ الثبت، محدِّثُ الكوفة، محمد بن خازم الكوفي الضرير.

حدَّث عن هشام بن عُروة، والأعمش، وليث بن أبي سُليم، وأبي إسحاق الشَّيباني، وإسماعيل بن أبي خالد، وطبقتهم.
وعنه: أحمد بنُ حنبل، وابنُ مَعين، وأبو خَيثمْة، والحسنُ بنُ عرفة، وهنّاد، وسعدانُ بنُ نصر، والحسنُ بنُ محمد الزَّعفراني، وأحمد بنُ عبد الجبّار، وخلق.
ولد سنةَ ثلاث عشرة ومئة.
قال أبو نعيم: سمعتُ الأعمش يقول لأبي معاوية: أمّا أنتَ فقد ربطتَ رأسَ كيسِك (١).
وقيل: إنَّ شعبةَ كان إذا حدَّث بحضرة أبي معاوية يراجعه في حديث الأعمش ويقول: أليس كذا أليس كذا؟ (٢).
_________
* طبقات ابن سعد: ٦/ ٣٩٢، طبقات خليفة: ت ١٣٠٤، تاريخ البخاري الكبير: ١/ ٧٤، ثقات العجلي: ص ٤٠٣، المعارف: ص ٥١٠، الجرح والعديل: ٧/ ٢٤٦، مشاهير علماء الأمصار: ت ١٣٠٤، تاريخ بغداد: ٥/ ٢٤٢، أنساب السمعاني: ٧/ ٨٢، تهذيب الكمال: ورقة ١١٩١، سير أعلام النبلاء: ٩/ ٧٣ - ٧٨، تذهيب التهذيب: ٣/ ٢٠٠، ميزان الاعتدال: ٤/ ٤٧٠، العبر: ١/ ٣١٨، تذكرة الحفاظ: ١/ ٢٩٤، الكاشف: ٣/ ٣٣، دول الإسلام: ١/ ١٢٣، نكت الهميان: ص ٢٤٧، شرح العلل لابن رجب: ٢/ ٦٦٩، تهذيب التهذيب: ٩/ ١٣٧، النجوم الزاهرة: ٢/ ١٤٨، طبقات الحفاظ: ص ١٢٢، خلاصة تذهيب الكمال: ص ٣٣٤، شذرات الذهب: ١/ ٣٤٣.
(١) تاريخ بغداد: ٥/ ٢٤٤.
(٢) المصدر السابق.','1','423',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (420,'قال أبو نعيم. لزم أبو معاويةَ الأعمشَ عشرين سنة (١).
وقال أحمد بن حنبل: كان أبو معاوية إذا سئل عن حديث الأعمش يقول: قد صار في فمي علقمًا. قال أحمد: وكان - واللَّه - حافظًا للقرآن، ويضطرب في غير حديث الأعمش (٢).
قال ابن المديني: كتبت عن أبي معاوية، عن الأعمش ألفًا وخمسمئة حديث (٣).
وقيل: كان الرَّشيد يبجِّل أبا معاوية، ويحترمُه.
مات -في قول الجماعة- سنةَ خمس وتسعين ومئة، وقيل: سنة أربع. رحمهُ اللَّه.

<span data-type="title" id=toc-276>٢٥٦ - مروان بن مُعَاوية * </span>(ع)
ابن الحارث بن أسماء بن خارجة بن عُيينة بن حِصن، الحافظ الثقة، أبو عبد اللَّه الفَزَاريُّ الكوفي، نزيلُ مكَّة ثم دمشق.
_________
(١) تاريخ أبي زرعة الدمشقي: ١/ ٣٠٣.
(٢) تهذيب الكمال: ورقة ١١٩١.
(٣) تاريخ بغداد: ٥/ ٢٤٦.
* تاريخ البخاري الكبير: ٧/ ٣٧٢، التاريخ الصغير: ٢/ ٢٧٤، ثقات العجلي: ص ٤٢٤، تاريخ أبي زرعة الدمشقي: ١/ ٦٣٧ وغيرها، الجرح والتعديل: ٨/ ٢٧٢، مشاهير علماء الأمصار: ت ١٣٦٧، تاريخ بغداد: ١٣/ ١٤٩، أنساب السمعاني: ٩/ ٢٩٧، تهذيب الكمال: ورقة ١٣١٨، سير أعلام النبلاء: ٩/ ٥١ - ٥٣، العبر: ١/ ٣١١، ميزان الاعتدال: ٤/ ٩٣، الكاشف: ٣/ ١١٧، تذهيب التهذيب: ٤/ ٣١ / ب، تذكرة الحفاظ: ١/ ٢٩٥، تهذيب التهذيب: ١/ ٩٦٠، =','1','424',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (421,'حدَّث عن عاصم الاحول، وحُميد الطَّويل، وأبي مالك سعد بن طارق، إسماعيل بن أبي خالد، وموسى الجُهني، ومحمد بن سُوقة، وعدَّة.
وعنه: أحمد، وإسحاق، وأبو خَيثمة، والحسين بن حُريث، ودُحيم، وأبو كُريب، وابنُ عرفة، ومحمد بن هشام بن مَلَّاس النّميري، وخلق.
قال أحمد بن حنبل: ثبت، حافظ، كان يحفظ حديثَه كلَّه (١).
وقال ابن المديني: ثقة فيما روى عن المَعْروفين (٢).
وقال ابن مَعين: كان يلتقطُ شيوخًا من السِّكك (٣).
قيل: مات فجأةً بمكَّة في عشر ذي الحجّة سنةَ ثلاثٍ وتسعين ومئة.
وقيل: كان فقيرًا، فكان الناس يبرُّونَه. رحمهُ اللَّهُ تعالى.
_________
= طبقات الحفاظ: ص ١٢٣، خلاصة تذهيب الكمال: ص ٣٧٣، شذرات الذهب: ١/ ٣٣٣.
(١) تاريخ بغداد: ١٣/ ١٥١.
(٢) المصدر السابق.
(٣) الخبر في "الجرح والتعديل" ٨/ ٢٧٣ و"أنساب السمعاني" ٩/ ٢٩٧ و"تهذيب الكمال" ورقة ١٣١٨ من قول ابن نمير.','1','425',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (422,'<span data-type="title" id=toc-277>٢٥٧ - مروان بنُ شجاع * </span>(خ، د، ت، ق)
الحافظ، أبو عَمرو الجَزَري، مولى بني أميَّة، حرّانيُّ سكن بغداد، وكان عالمًا بخُصَيف (١).

حدَّث: عنه (٢)، وعن إبراهيم بنِ أبي عَبْلَة، وسالم الْأَفْطَس.
وعنه: أحمد بن حنبل، وسُريج بنُ يونس، وأحمد بن مَنيع، وأبو عبيد، ويعقوب الدَّوْرقي، والحسن بنُ عَرفة، وعدّة.
وثقه ابنُ مَعين، وغيره.
وقال ابن سعد: كان راويةً لخُصَيف (٣).
وقال خليفة: مات سنة أربع وثمانين ومئة.
_________
* طبقات ابن سعد: ٧/ ٤٨٥، طبقات خليفة: ت ٣٠٩١، تاريخ البخاري الكبير: ٧/ ٣٧٢، التاريخ الصغير: ٢/ ٢٣٤، الجرح والتعديل ٨/ ٢٧٣، المجروحين والضعفاء: ٣/ ١٣، تاريخ بغداد: ١٣/ ١٤٧، أنساب السمعاني: ٥/ ١٣٨، اللباب: ١/ ٤٥٠، تهذيب الكمال: ورقة ١٣١٥، سير أعلام النبلاء: ٩/ ٣٤، العبر: ١/ ٢٨٩، ميزان الاعتدال: ٤/ ٩١، تهذيب التهذيب: ٤/ ٣١، تذكرة الحفاظ: ١/ ٢٩٦، الكاشف: ٣/ ١١٧، تهذيب التهذيب: ١٠/ ٩٤، طبقات الحفاظ: ص ١٢٣، خلاصة تهذيب الكمال: ص ٣٧٣، شذرات الذهب: ٦/ ٣٠١.
(١) خصيف: هو الإمام الفقيه، خصيف بن عبد الرحمن الخِضرمي الجزري الحراني، مولى بني أمية. وثقه ابن معين. وقال النسائي: صالح. وقال أحمد بن حنبل: ليس بحجة. وقال أبو حاتم: سيء الحفظ. انظر "سير أعلام النبلاء": ٦/ ١٤٥.
(٢) يعني: عن خصيف. قال السمعاني: "الخصيفي: نسبة لأبي عمرو مروان بن شجاع الخصيفي الجزري القرشي الأموي مولاهم. نسب إلى خصيف بن عبد الرحمن الجزري لكثرة روايته عنه" انظر "الأنساب": ٥/ ١٣٨.
(٣) طبقات ابن سعد: ٧/ ٤٨٥.','1','426',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (423,'<span data-type="title" id=toc-278>٢٥٨ - عبد الْأعلى بنُ عبد الْأعلى * </span>(ع)
المحدِّث العالم، أبو محمد القرشي السَّاميُّ (١) البصري.

روى عن: حُميد الطَّويل، والجُريري، ويونس بن عُبيد، وداود بن أبي هند، وغيرهم.
وعنه: إسحاقُ بنُ راهويه، والفلّاس، وأبو بكر بنُ أبي شَيبة، ونصرُ بن علي، وبُنْدار، وخلق.
وثقه غيرُ واحد.
أما ابنُ سعدٍ فقال (٢): لم يكن بالقوي.
مات في شعبان سنةَ تسعٍ وثمانين ومئة. رحمة اللَّهُ تعالى.
_________
* تاريخ ابن معين: ٢/ ٣٣٩، طبقات ابن سعد: ٧/ ٢٩٠، طبقات خليفة: ت ١٩٠٦، تاريخ خليفة: ٤٥٨، تاريخ البخاري الكبير: ٦/ ٧٣، التاريخ الصغير: ٢/ ٢٤٦، ثقات العجلي: ص ٢٨٤، المعرفة والتاريخ: ١/ ١٨٠، ضعفاء العقيلي: ٣/ ٥٨، الجرح والتعديل: ٦/ ٢٨، مشاهير علماء الأمصار: ت ١٢٦٨، الإكمال لابن ماكولا: ٤/ ٥٥٧، تهذيب الكمال: ورقة ٧٦٠، سير أعلام النبلاء: ٩/ ٢٤٢ - ٢٤٣، العبر: ١/ ٣٠٣، ميزان الاعتدال: ٢/ ٥٣١، تذهيب التهذيب: ٢/ ١٩٧ / ب، تذكرة الحفاظ: ١/ ٢٩٦، الكاشف: ٢/ ١٣٠، تهذيب التهذيب: ٦/ ٩٦، طبقات الحفاظ: ص ١٢٣، خلاصة تذهيب الكمال: ص ٢٢٠، شذرات الذهب: ١/ ٣٢٤.
(١) في الأصل "الشامي" خطأ، والتصويب من مصادر الترجمة.
(٢) في "الطبقات": ٧/ ٢٩٠.','1','427',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (424,'<span data-type="title" id=toc-279>٢٥٩ - الفضل بنُ موسى * </span>(ع)
الإمام الحافظ، أبو عبد الله المَرْوَزي السِّيناني، أحد أئمَّة خراسان. وسِينان: من قرى مرو.

رحل، وسمع من: هشام بن عُروة، وخُثيم بن عِراك، وإسماعيل بن أبي خالد، ومَعْمر، وحسين المعلّم، وطبقتهم.
وعنه: إسحاق بن راهويه، وعليُّ بنُ حُجر، ويحيى بنُ أكثم، والحسين بن حُريث، وعليُّ بنُ خَشْرم، ومحمود بن غَيلان، ومحمود بنُ آدم، وعدة.
قال أبو نعيم: هو أثبتُ من ابن المبارك (١).
وقال وكيع: أعرفُه، ثقة، صاحب سنَّة (٢).
وقال ابن حِبّان: من جلَّة أهل مرو، ومتقني المحدِّثين [بها] (٣).
_________
* تاريخ ابن معين: ٢/ ٤٧٥، طبقات ابن سعد: ٧/ ٣٧٢، طبقات خليفة: ت ٣١٣٨، تاريخ البخاري الكبير: ٧/ ١١٧، التاريخ الصغير: ٢/ ٢٦٨، المعارف: ص ٤٢٢، الجرح والتعديل: ٧/ ٦٨، مشاهير علماء الأمصار: ت ١٥٨٦، أنساب السمعاني: ٧/ ٢٣٠، معجم البلدان: ٣/ ٣٠٠، اللباب: ٢/ ١٦٩، تهذيب الكمال: ورقة ١١٠٤، سير أعلام النبلاء: ٥/ ١٠٤ - ١٠٥، تذكرة الحفاظ: ١/ ٢٩٦، العبر: ٧/ ٣٠١، ميزان الاعتدال: ٣/ ٣٦٠، الكاشف: ٢/ ٣٣٠، تذهيب التهذيب: ٣/ ١٤٠ / ب، دول الإسلام: ١/ ١٢١، تهذيب التهذيب: ٨/ ٢٨٦، طبقات الحفاظ: ص ١٢٤، خلاصة تذهيب الكمال: ص ٣٠٩، شذرات الذهب: ١/ ٣٢٩.
(١) الجرح والتعديل: ٧/ ٦٩.
