INSERT INTO [b1994] VALUES (442,'<span data-type="title" id=toc-287>٢٦٧ - بِشْرُ بنُ المُفَضَّل * </span>(ع)
ابن لاحق، الإِمامُ الحافظ، أبو إسماعيل الرَّقَاشيُّ مولاهم البصري.

حدَّث عن: سُهيل بن أبي صالح، وحُميد الطَّويل، والجُرَيري، وخالد الحذَّاء، والطبقة.
وعنه: ابنُ المديني، وابنُ راهويه، وأحمد، ونصرُ بنُ علي، والفلّاس، وأحمد بن المِقْدام، وخلق.
قال أحمد: إليه المُنْتهى في التثبُّتِ بالبصرة (١).
وقال ابنُ المديني: كاد يصلِّي كلَّ يوم أربعمئة ركعة، ويصومُ يومًا، ويفطرُ يومًا (٢).
ورُوي أنّه ذُكرَ عنده جَهْميٌّ، فقال: لا تذكروا ذاك الكافر (٣).
توفي سنةَ ستٍّ -أو سبع- وثمانين ومئة. رحمة اللَّهِ عليه.
_________
* تاريخ ابن معين: ٢/ ٥٩، طبقات ابن سعد: ٧/ ٢٩٠، تاريخ خليفة: ٤٥٨، تاريخ البخاري الكبير: ٢/ ٨٤، التاريخ الصغير: ٢/ ٢٤٤، المعارف: ص ٥١٣، الجرح والتعديل. ٢/ ٣٦٦، مشاهير علماء الأمصار: ت ١٢٧٦، الجمع لابن القيسراني: ١/ ٥٢، أنساب السمعاني: ٦/ ١٤٦ - ١٤٧، تهذيب الكمال: ٤/ ١٤٧ - ١٥١ (طبعة محققة وفيها استقصاء لمصادر ترجمته)، سير أعلام النبلاء: ٩/ ٣٦ - ٣٩، العبر: ١/ ٢٩٦، الكاشف: ١/ ١٠٤، تذهيب التهذيب: ١/ ٨٥ / ب، تذكرة الحفاظ: ١/ ٣٠٩، تهذيب التهذيب ١/ ٤٥٨، طبقات الحفاظ: ص ١٢٨، خلاصة تذهيب الكمال: ص ١٢٨، شذرات الذهب: ١/ ٣١٠.
(١) الجرح والتعديل: ٢/ ٣٦٦.
(٢) تهذيب الكمال: ٤/ ١٥٠.
(٣) المصدر السابق، وقد تقدم تعريف الجهمية في ترجمة إبراهيم بن طهمان.','1','446',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (443,'<span data-type="title" id=toc-288>٢٦٨ - محمد بن حرب * </span>(ع)
الفقيه الثَّبت، أبو عبد الله الخَوْلانيُّ الحِمْصيُّ الأبرش، كاتب الزُّبيري.

روى عن: الزُّبيدي، وبَحِير بن سعد، ومحمد بن زياد الأَلهاني، وعمر بن رُوْبة، والأوزاعي، وعدّة.
وعنه: أبو مُسْهر، وإسحاقُ بنُ راهويه، ومحمد بنُ وهب بن عَطيَّة، وكَثير بنُ عُبيد، وأبو التَّقِيِّ اليَزَني، ومحمد بن مصفَّى، وأبو عتبة الحِجازي، وخلق.
وذكر ابن سعد أنَّه وليَ قضاءَ دمشق (١).
وقال ابنُ مَعين وغيرُه: ثقة (٢).
قال يزيد بنُ عبد ربِّه: مات سنةَ أربعٍ وتسعين ومئة. رحمهُ اللَّه تعالى.
_________
* طبقات ابن سعد: ٧/ ٤٧٠، طبقات خليفة: ت ٣٠٥١، تاريخ البخاري الكبير: ١/ ٦٩، التاريخ الصغير: ٢/ ٢٧٥، ثقات العجلي: ص ٤٠٢، المعرفة والتاريخ: ١/ ٣٨٥ وغيرها، تاريخ أبي زرعة الدمشقي: ١/ ٢٤٠ وغيرها، الجرح والتعديل: ٧/ ٢٣٧، تهذيب الكمال: ورقة ١١٨٥، سير أعلام النبلاء: ٩/ ٥٧ - ٥٩، العبر: ١/ ٣١٥، تذهيب التهذيب: ٣/ ١٩٦ / ب، تذكرة الحفاظ: ١/ ٣١٠، الكاشف: ٣/ ٢٨، تهذيب التهذيب: ٩/ ١٠٩، النجوم الزاهرة: ٢/ ١٤٦، طبقات الحفاظ: ص ١٢٨، خلاصة تذهيب الكمال: ص ٣٣٢، قضاة دمشق لابن طولون: ص ١٧، شذرات الذهب: ١/ ٣٤١.
(١) طبقات ابن سعد: ٧/ ٤٠٧، وانظر "قضاة دمشق" ص ١٧.
(٢) انظر "الجرح والتعديل": ٧/ ٢٣٧.','1','447',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (444,'<span data-type="title" id=toc-289>٢٦٩ - عَبِيدة بنُ حُميد * </span>(خ، ٤)
الكوفي الحذّاء، الحافظُ الثَّبت.

حدَّث عن: الأسود بن قَيس، وعبد العزيز بن رُفيع، وعبد الملك بن عُمير، ومنصور، والأعمش، وعدّة.
وعنه: الثوريُّ مع تقدُّمِه، وأحمد بنُ حنبل، وأحمد بن مَنيع، والحسنُ بنُ الصبّاح البزّار، والحسن بنُ محمد الزَّعفراني، وعمرو النّاقد، ومحمد بنُ سعيد بن غالب العطَّار، وغيرهم.
وكان عالمًا نبيلًا، صاحب حديث، ونحو، وقرآن. وكان مؤدِّب الأمين (١).
قال أحمد، وابنُ مَعين: ثقة (٢). وقال أحمد: أتيناهُ فأَملى علينا، ثم كثرَ عليه النّاسُ حتَّى غُلبنا عنه، وكثرَ الزِّحام.
وعاش نيفًا وثمانين سنة، وتوفي سنةَ تسعين ومئة. رحمهُ الله تعالى.
_________
* تاريخ ابن معين: ٢/ ٣٨٧، طبقات ابن سعد: ٧/ ٣٢٩، تاريخ البخاري الكبير: ٣/ ٢٥، التاريخ الصغير: ٢/ ٢٥٢، ثقات العجلي: ص ٣٢٤، المعرفة والتاريخ: ٢/ ١٧١، الجرح والتعديل: ٦/ ٩٢، مشاهير علماء الأمصار: ت ١٣٦٠، تاريخ بغداد: ١١/ ١٢٠، أنساب السمعاني: ٤/ ٨٧، تهذيب الكمال: ورقة ٩٠٢، سير أعلام النبلاء: ٨/ ٥٠٨ - ٥١٠، تذكرة الحفاظ: ١/ ٣١١، ميزان الاعتدال: ٣/ ٢٥، تذهيب التهذيب: ٣/ ٢٥، العبر: ٣٠٦، مشتبه النسبة: ٢/ ٤٣٧، تهذيب التهذيب: ٧/ ٨١، النجوم الزاهرة: ٢/ ١٣٤، طبقات الحفاظ: ص ١٢٩، خلاصة تذهيب الكمال: ص ٢٥٦، شذرات الذهب: ١/ ٣٢٦.
(١) انظر "طبقات ابن سعد": ٧/ ٣٢٩.
(٢) انظر "تاريخ بغداد": ١١/ ١٢١.','1','448',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (445,'<span data-type="title" id=toc-290>٢٧٠ - عُبيد اللَّهِ بن عُبيد الرحمن * </span>(خ، م، ت، س، ق)
الأشجعي، أبو عبد الرحمن الكوفي، الإمامُ الحافظ.

سمع: إسماعيل بن أبي خالد: ، وهشام بنَ عُروة، وغيرهما. ثم لزم الثَّوري مدَّةً، فكان يقول: سمعتُ من سفيان ثلاثين ألف حديث (١).
وقال ابنُ مَعين: ما بالكوفة أعلم بسفيانَ من الأشجعي (٢).
حدّث عنه: يحيى بنُ آدم، وأبو النّضر، وابن مَعين، وأبو خَيثمة، وأبو كُرَيب، وعثمان بنُ أبي شَيبة، ويعقوب الدَّورقي، وغيرهم.
قال ابن مَعين: صالحٌ ثقة. وقال: كان أعلم بسفيان من عبد الرحمن، ومن يحيى بن سعيد، ومن أبي أحمد الزّبيري؛ وقَبيصة، وأبي حُذيفة (٣).
وقال قَبيصة: لمّا مات الثَّوريُّ جلسَ الأشجعيُّ موضعَه (٤). وقد تحوَّل الأشجعيُّ بعد ذلك إلى بغداد.
ومات في أول سنةِ اثنتين وثمانين ومئة. رحمه اللهُ تعالى.
_________
* طبقات ابن سعد: ٧/ ٣٢٨، ثقات العجلي: ص ٣١٨، الجرح والتعديل: ٥/ ٣٢٣، تاريخ بغداد: ١٠/ ٣١١، أنساب السمعاني: ١/ ٢٧١، تهذيب الكمال: ورقة ٨٨٨، تذكرة الحفاظ: ١/ ٣١١، العبر: ١/ ٢٨٢، تهذيب التهذيب: ٧/ ٣٤، طبقات الحفاظ: ص ١٢٩، خلاصة تذهيب الكمال: ص ٢٥٢، شذرات الذهب: ١/ ٢٩٧.
(١) تاريخ بعداد: ١٠/ ٣١١.
(٢) تاريخ بغداد ١٠/ ٣١٢.
(٣) المصدر السابق.
(٤) المصدر السابق.','1','449',NULL,NULL,'0');
INSERT INTO [b1994] VALUES (446,'<span data-type="title" id=toc-291>٢٧١ - عَبدة بن سليمان * </span>(ع)
الإمام الحافظ، أبو محمد الكِلَابيُّ الكوفي.

حدَّث عن: عاصم الأحول، وهشام بنِ عُروة، وإسماعيل بن أبي خالد، وغيرهم.
وعنه: ابنُ راهويه، وأبو خَيثمة، وأبو كُرَيب، وأبو سعيد الأشجّ، وغيرهم.
قال أحمد بن حنبل: ثقة ثقة وزيادة، مع صلاحٍ وشدَّة فقر، عليه فروة خَلقة لا تساوي كبير شيء (١).
مات في رجب سنةَ ثمانٍ وثمانين ومئة. رحمه اللهُ تعالى.

<span data-type="title" id=toc-292>٢٧٢ - عبد الرحمن بن محمد * * </span>(ع)
ابن زياد، أبو محمد المُحَاربيُّ، الكوفيُّ، الحافظ.
_________
* تاريخ ابن معين: ٢/ ٣٧٩، طبقات ابن سعد: ٦/ ٣٩٠، تاريخ البخاري الكبير: ٦/ ١١٥، التاريخ الصغير: ٢/ ٢٤٣، ثقات العجلي: ص ٣١٥، المعرفة والتاريخ: ٢/ ١٦٧، الجرح والتعديل: ٦/ ٨٩، تهديب الكمال: ورقة ٨٧٤، سير أعلام النبلاء: ٨/ ٥١١، تهذيب التهذيب: ٢/ ٢٦١ / ب، تذكرة الحفاظ: ١/ ٣١٢، العبر: ١/ ٢٩٩، تهذيب التهذيب: ٦/ ٤٥٨، النجوم الزاهرة: ٢/ ١٢١٧، طبقات الحفاظ: ص ١٢٩، خلاصة: تذهيب الكمال: ص ٢٤٩، شذرات الذهب: ١/ ٣٢٠.
