INSERT INTO [b203] VALUES (2,'ذَيْلُ لِسَانِ المِيْزَانِ
«رُوَاةٌ ضُعَفَاءُ أوْ تُكُلِّمَ فيهِم، لَمْ يُذكَرُوا فِي كُتُبِ الضُّعَفَاءِ وَالمتَكَلَّمِ فيهِم»',NULL,'3',NULL,NULL,'0');
