معاذ بن المثنى العنبري (ت ٢٨٨ هـ)، علي بن عبد الصمد الطيالسي (ت ٢٨٩ هـ)، محمد بن موسى النهرتيري (ت ٢٨٩ هـ)، محمد بن موسى بن مُشَيش البغدادي (ت ٢٨٩ هـ)،
صدقة بن موسى (ت بعد ٢٨٩ هـ)، أحمد بن علي بن مسلم الأبار (ت ٢٩٠ هـ)، عبد اللَّه بن الإمام أحمد (ت ٢٩٠ هـ)، محمد بن العباس المؤدب (ت ٢٩٠ هـ)، الحسين بن إسحاق بن إبراهيم التستري (ت ٢٩٠ هـ)، إبراهيم بن محمد بن الحارث الأصبهاني (ت ٢٩١ هـ)، أحمد بن يحيى ثعلب (ت ٢٩١ هـ)،
محمد بن إبراهيم بن سعيد البوشنجي (ت ٢٩١ هـ)، محمد بن حبيب، أبو عبد اللَّه البزار (ت ٢٩١ هـ)، أحمد بن علي بن سعيد أبو بكر المروزي (ت ٢٩٢ هـ)، إدريس بن عبد الكريم المقرئ (ت ٢٩٢ هـ)، يحيى بن أبي نصر أبو سعد الهروي (ت ٢٩٢ هـ)،','1','81',NULL,NULL,'0');
INSERT INTO [b20875] VALUES (74,'أحمد بن حفص السعدي (ت ٢٩٣ هـ)، أحمد بن العباس بن الأشرس أبو العباس (ت ٢٩٣ هـ)، أحمد بن محمد بن صدقة (ت ٢٩٣ هـ)، عمر بن حفص أبو بكر السدوسي (ت ٢٩٣ هـ)، محمد بن عبدوس السراج (ت ٢٩٣ هـ)، محمد بن إسحاق بن إبراهيم ابن راهويه (ت ٢٩٤ هـ)،
موسى بن هارون الحمال (ت ٢٩٤ هـ)، أحمد بن بشر الطيالسي (ت ٢٩٥ هـ)، علي بن أحمد أبو غالب الأزدي (ت ٢٩٥ هـ)، هارون بن عيسى أبو حامد الخياط (ت ٢٩٦ هـ)،
إبراهيم بن هاشم البغوي (ت ٢٩٧ هـ)، أحمد بن عبد الرحمن بن أبي عوف البزوري (ت ٢٩٧ هـ)، الجنيد بن محمد الخراز (ت ٢٩٨ هـ)، عبد الرحمن بن مهدي (ت ٢٩٨ هـ)،
