-- Insert data for b21546
INSERT INTO [b21546] VALUES (1,'ذم الدنيا
<span data-type="title" id=toc-1>الجزء الأول</span>',NULL,'11',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (2,'أخبرنا الشيخ الفقيه الإمام أبو عبد الله محمد بن محمد بن عبد الرحمن بن محارب القيسي بقراءتي عليه يوم الأحد الخامس من شهر ربيع الأول سنة ثمان وثلاثين وستمائة، قال: أخبرتنا الشيخة نور العين لامعة بنت المبارك بن كامل بن أبي غالب الخفاف، قراءة عليها وأنا أسمع يوم الثلاثاء رابع عشر من ربيع الأول سنة اثنتي عشرة وستمائة قال لها الإمام الحافظ أبو سعد أحمد بن محمد بن أحمد البغدادي، قال: أخبرنا أبو العباس أحمد بن محمد الطهراني قال: أخبرنا أبو محمد الحسن بن أحمد بن حيوة، أخبرنا أبو الحسن أحمد بن محمد العبدي اللبناني، قال: أخبرنا أبو بكر عبد الله بن محمد بن عبيد الله القرشي قال:',NULL,'11',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (3,'١ - حدثنا سعيد بن سليمان الواسطي، عن زكريا بن منظور بن ثعلبة بن أبي مالك، نا أبو حازم، عن سهل بن سعد قال: مر رسول الله صلى الله عليه وسلم بذي الحليفة، فرأى شاة شائلة برجلها، فقال: أترون هذه الشاة هينة على صاحبها، قالوا: نعم، قال: والذي نفسي بيده، ⦗١٢⦘ للدنيا أهون على الله عز وجل من هذه على صاحبها، ولوكانت الدنيا تعدل عند الله جناح بعوضة ما سقى كافراً منها شربة.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (4,'٢ - حدثنا خالد بن خداش، نا حماد بن زيد، عن مجالد عن قيس بن أبي حازم، عن المستورد بن شداد، قال: إني لفي ركب مع رسول الله صلى الله عليه وسلم إذ مر بسخلة منبوذة، فقال: أترون هذه هانت على أهلها حين ألقوها فقالوا: من هوانها ألقوها، قال: والذي نفسي بيده للدنيا أهون على الله عز وجل من هذه على أهلها.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (5,'٣ - وحدثنا أبو خثيمة، ومحمد بن علي بن أبي حاتم الأزدي، قالا: ⦗١٣⦘ أخبرنا محمد بن مصعب، حدثنا الأوزاعي، عن الأزهري، عن عبيد الله بن عبد الله بن عتبة، عن ابن عباس، قال: مر رسول الله صلى الله عليه وسلم بشاة ميتة، فقال: والذي نفسي بيده للدنيا على الله عز وجل أهون من هذه الشاة على أهلها.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (6,'٤ - حدثني الحسن بن الصباح، أخبرنا سعيد بن محمد، عن موسى الجهني، عن زيد بن وهب، عن سلمان، قال: قال رسول الله صلى الله عليه وسلم: الدنيا سجن المؤمن وجنة الكافر.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (7,'٥ - وحدثني الوليد بن سفيان العطار، أخبرنا ابن أبي عدي، عن شعبة، عن العلاء بن عبد الرحمن، عن أبيه، عن أبي هريرة، قال: قال رسول الله صلى الله عليه وسلم: الدنيا سجن المؤمن وجنة الكافر',NULL,'14',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (8,'٦ - وحدثنا العباس بن يزيد البصري، أخبرنا معاوية، أخبرنا ⦗١٥⦘ الأعمش، عن شمر بن عطية، عن شهر بن حوشب، عن عبادة - أراه رفعه -، قال: يجاء بالدنيا يوم القيامة، فيقال: ميزوا ما كان لله عز وجل وألقوا سائرها في النار.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (9,'٧ - وحدثنا محمد بن حميد، أخبرنا مهران بن أبي عمرو، أخبرنا سفيان الثوري، عن محمد بن المنكدر، عن أبيه، قال: قال رسول الله صلى الله عليه وسلم: الدنيا ملعونة، ملعون ما فيها إلا ما كان لله منها.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (10,'٨ - حدثنا خالد بن خداش، أخبرنا عبد العزيز بن محمد، حدثني ⦗١٦⦘ عمرو بن أبي عمرو، عن المطلب بن حنطب، عن أبي موسى الأشعري، أن رسول الله صلى الله عليه وسلم، قال: من أحب دنياه أضر بآخرته، ومن أحب آخرته أضر بدنياه، فآثروا ما يبقى على ما يفنى.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (11,'٩ - حدثني سريج بن يونس، أخبرنا عباد بن العوام، عن هشام، أو عوف، عن الحسن، قال: قال رسول الله صلى الله عليه وسلم: حب الدنيا رأس كل خطيئة.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (12,'١٠ - وحدثنا سريج بن يونس، حدثني مروان بن معاوية، عن محمد بن أبي قيس، عن سليمان بن حبيب، عن أبي أمامة الباهلي، قال: لما بعث محمد صلى الله عليه وسلم أتت إبليس جنوده، وقالوا: قد بعث نبي وأخرجت أمته، قال: يحبون الدنيا؟ قالوا: نعم، قال: لئن كانوا يحبونها ما أبالي أن لا يعبدوا الأوثان، وأنا أغدو عليهم وأروح بثلاث: أخذ المال من غير حقه، وإنفاقه في غير حقه، وإمساكه عن حقه، والشر كله لهذا تبع.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (13,'١١ - وحدثني أبو علي عبد الرحمن بن زبان الطائي، حدثنا عبد الصمد بن عبد الوارث، أخبرنا عبد الواحد بن زيد حدثني أسلم الكوفي، عن مرة، عن زيد بن أرقم، قال: ⦗١٨⦘ كنا مع أبي بكر فدعا بشراب فأتي بماء وعسل، فلما أدناه من فيه بكى وبكى حتى أبكى أصحابه، فسكتوا وما سكت، ثم عاد فبكى حتى ظنوا أنهم لن يقدروا على مسألته، ثم مسح عينيه، فقالوا: يا خليفة رسول الله، ما أبكاك؟ قال: كنت مع رسول الله صلى الله عليه وسلم فرأيته يدفع عن نفسه شيئاً ولم أر معه أحداً، فقلت: يا رسول الله ما الذي تدفع عن نفسك؟ قال: هذه الدنيا مثلت لي فقلت لها: إليك عني، ثم رجعت، فقالت: إنك إن أفلت مني لن ينفلت مني من بعدك.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (14,'١٢ - وحدثنا إسحاق بن إسماعيل، أخبرنا سفيان، عن إسماعيل، عن قيس سمعه يقول: أخبرنا المستورد الفهري أنه سمع رسول الله صلى الله عليه وسلم يقول: والله ما الدنيا في الآخرة إلا مثل ما يجعل أحدكم إصبعه في اليم، فلينظر ما يرجع إليه.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (15,'١٣ - حدثني محمد بن عثمان العجلي، أخبرنا أبو أسامة، عن ⦗١٩⦘ مجالد، عن الشعبي، قال: قال عمر بن الخطاب رضي الله عنه: والله ما الدنيا في الآخرة إلا كنفجة أرنب.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (16,'١٤ - حدثنا حمدون بن سعد المؤدب، أخبرنا النضر بن إسماعيل، عن موسى الصغير، عن عمرو بن مرة، عن أبي جعفر، قال: قال رسول الله صلى الله عليه وسلم: يا عجباً كل العجب للمصدق بدار الخلود، وهو يسعى لدار الغرور.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (17,'١٥ - حدثني سريج بن يونس، أخبرنا الوليد بن مسلم، قال: قال الضحاك بن عثمان: سمعت بلال بن سعد يقول: قال أبو الدرداء: ⦗٢٠⦘ لو كانت الدنيا تزن عند الله جناح بعوضة ما سقى فرعون منها شربة ماء.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (18,'١٦ - حدثني سريج أخبرنا عنبسة بن عبد الواحد، عن مالك بن مغول قال: قال ابن مسعود: الدنيا دار من لا دار له، ومال من لا مال له، ولها يجمع من لا عقل له.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (19,'١٧ - حدثنا هارون بن عبد الله، وعلي بن مسلم، قالا: أخبرنا سيار، حدثنا جعفر، أخبرنا مالك بن دينار، قال: قالوا: لعلي بن أبي طالب رضي الله عنه: يا أبا الحسن، صف لنا الدنيا؟ قال: أطيل أم أقصر؟ قالوا: بل أقصر، قال: حلالها حساب، وحرامها النار.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (20,'١٨ - وحدثني الحسين بن عبد الرحمن، أخبرنا عبيد الله بن محمد التيمي، عن شيخ من بني عدي، قال: قال رجل لعلي بن أبي طالب: يا أمير المؤمنين، صف لنا الدنيا؟ قال: وما أصف لك من دار من صح فيها أمن، ومن سقم فيها ندم، ومن افتقر فيها حزن، ومن استغنى فيها فتن، من حلالها حساب ومن حرامها النار.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (21,'١٩ - حدثني القاسم بن هاشم، أخبرنا عبد الوهاب بن نجدة الحوطي، أخبرنا بقية بن الوليد، عن أبي الحجاج المهري، عن ابن الميمون اللخمي، أن رسول الله صلى الله عليه وسلم وقف على مزبلة، فقال: هلموا إلى الدنيا، وأخذ خرقاً وقد بليت على تلك المزبلة وعظاماً قد نخرت، فقال: هذه الدنيا.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (22,'٢٠ - حدثنا داود بن عمرو، أخبرنا منصور بن أبي الأسود، عن ⦗٢٢⦘ الأعمش، عن أبي سفيان، عن الحسن البصري، قال: قال رسول الله صلى الله عليه وسلم: إن الدنيا حلوة خضرة، وإن الله مستخلفكم فيها فناظر كيف تعملون، إن بني إسرائيل لما بسطت لهم الدنيا ومهدت تباهوا في الحلية والطيب والنساء والثياب.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (23,'٢١ - وحدثنا محمد بن عمرو بن العباس الباهلي، أخبرنا سعيد بن عامر، عن معاذ بن الأعلم، عن يونس بن عبيد، قال: ما شبهت الدنيا إلا كرجل نائم فرأى في منامه ما يكره وما يحب، فبينما هو كذلك إذ انتبه.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (24,'٢٢ - وحدثنا إسحاق بن إسماعيل، أخبرنا إبراهيم بن عيينة، قال: قيل لبعض الحكماء: أي شيء أشبه بالدنيا؟ قال: أحلام النائم.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (25,'٢٣ - وحدثني محمد بن الحسين، قال: سمعت أبا زكريا المنتوف يحدث ⦗٢٣⦘ القواريري، قال: ذكرت الدنيا عند الحسن البصري، فقال: أحلام نوم أو كظل زائل ... إن اللبيب بمثلها لا يخدع',NULL,'22',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (26,'٢٤ - وحدثني محمد بن الحسين، أخبرنا يوسف بن الحكم الرقي، قال كان الحسن بن علي يتمثل، ويروى أنه من قوله: يا أهل لذات دنيا لا بقاء لها ... إن اغتراراً بظل زائل حمق',NULL,'23',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (27,'٢٥ - حدثني موسى بن عبد الله المقرئ، قال: نزل أعرابي بقوم فقدموا له طعاماً فأكل، ثم قام إلى ظل خيمة لهم فنام هناك، فاقتلعوا الخيمة فأصابته الشمس، فانتبه وقام وهو يقول:
ألا إنها الدنيا كظل بنيته ... ولا بد يوماً أن ظلك زائل',NULL,'23',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (28,'٢٦ - حدثني الحسين بن عبد الرحمن، حدثني محمد بن أنس، قال: مر قوم بواد فسمعوا هاتفاً يقول: وإن امرءاً دنياه أكبر همه ... لمستمسك منها بحبل غرور',NULL,'24',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (29,'٢٧ - حدثني أبو علي الطائي، أخبرنا عبد الرحمن المحاربي، عن ليث: أن عيسى ابن مريم رأى الدنيا في صورة عجوز هتماء عليها من كل زينة، فقال لها: كم تزوجت؟ قالت: لا أحصيهم، قال: كلهم مات عنك أو كلهم طلقك؟ قالت: بل كلهم قتلت قال: فقال عيسى عليه السلام: بؤساً لأزواجك الباقين، ألا يعتبرون بأزواجك الماضين، كيف تهلكينهم واحداً واحداً ولا يكونون منك على حذر.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (30,'٢٨ - حدثني إسحاق بن إسماعيل، أخبرنا روح بن عبادة، أخبرنا عوف، عن أوفى، عن أبي العلاء، قال: رأيت في النوم عجوزاً كبيرة متغضنة الجلد ينظرون إليها، فجئت فنظرت فعجبت من نظرهم إليها وإقبالهم عليها، فقلت لها: ويلك من أنت؟ قالت: أو ما ⦗٢٥⦘ تعرفني؟ قلت: لا ما أدري من أنت؟ قالت: فإني أنا الدنيا.
قال: قلت: أعوذ بالله من شرك، قالت: فإن أحببت أن تعاذ من شري فابغض الدرهم.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (31,'٢٩ - حدثني إبراهيم بن سعيد الجوهري، أخبرنا سفيان بن عيينة، قال: قال لي أبو بكر بن عياش: رأيت الدنيا - يعني في النوم - عجوزاً مشوهة حدباء.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (32,'٣٠ - وحدثني غير إبراهيم بن سعيد، أن أبا بكر بن عياش، قال: رأيت في النوم عجوزاً شمطاء مشوهة تصفق بيديها وخلفها خلق يتبعونها ويصفقون ويرقصون، فلما كانت بحذائي أقبلت علي، فقالت: لو ظفرت بك صنعت بك ما صنعت بهؤلاء.
قال: ثم بكى أبو بكر وقال: رأيت هذا قبل أن أقدم إلى بغداد.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (33,'٣١ - حدثنا إسحاق بن إسماعيل، حدثنا جرير، عن شهر بن حوشب، قال: قال عيسى بن مريم: ⦗٢٦⦘ لا تتخذوا الدنيا رباً فتتخذكم الدنيا عبيداً، أكنزوا كنزكم عند من لا يضيعه، فإن صاحب كنز الدنيا يخاف عليه الآفة، وإن صاحب كنز الله لا يخاف عليه الآفة.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (34,'٣٢ - وحدثنا إسحاق بن إسماعيل، حدثني يحيى بن أبي بكير العبدي، أخبرنا بعض العلماء، قال: قال عيسى بن مريم عليه السلام: يا معشر الحواريين إني قد كببت لكم الدنيا على وجهها فلا تنعشوها بعدي، فإن من خبث الدنيا أن الله عصي فيها، وإن من خبث الدنيا أن الآخرة لا تدرك إلا بتركها، ألا فاعبروا الدنيا ولا تعمروها.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (35,'٣٣ - حدثنا محمد بن علي بن شقيق، أخبرنا محمد بن العباس، أخبرني الحسن بن رشيد، عن وهيب المكي، قال: بلغني أن عيسى عليه السلام قال قبل أن يرفع: يا معشر الحواريين: إني قد كببت لكم الدنيا فلا تنعشوها بعدي، فإنه لا خير في دار قد عصي الله عز وجل فيها، ولا تعمروها، واعلموا أن أصل كل خطيئة حب الدنيا، ورب شهوة أورثت أهلها حزناً طويلاً.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (36,'٣٤ - وحدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: ⦗٢٧⦘ سمعت الفضيل بن عياض، وابن عيينة يقولان: قال عيسى بن مريم عليه السلام بطحت لكم الدنيا وجلستم على ظهرها فلا ينازعكم فيها إلا الملوك والنساء، فأما الملوك فلا تنازعوهم الدنيا فإنهم لن يعرضوا لكم ما تركتموهم ودنياهم، وأما النساء فاتقوهن بالصوم والصلاة.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (37,'٣٥ - حدثنا أزهر بن مروان الرقاشي، أخبرنا جليس للمعتمر بن سليمان، أخبرنا شعيب بن صالح، قال: قال عيسى بن مريم عليه السلام.
: ما سكنت الدنيا في قلب عبد إلا وأليط قلبه منها بثلاث: شغل لا ينفك عناؤه، وفقر لا يدرك غناه، وأمل لا يدرك منتهاه.
الدنيا طالبة ومطلوبة، فطالب الآخرة تطلبه الدنيا حتى يستكمل فيها رزقه، وطالب الدنيا تطلبه الآخرة حتى يجيء الموت فيأخذه بعنقه.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (38,'٣٦ - حدثني أبو إسحاق الرياحي، أخبرنا جعفر بن سليمان، قال: سمعت مالك بن دينار يحدث، عن الحسن قال: ⦗٢٨⦘ أربع من أعلام الشقاء: قسوة القلب، وجمود العين، وطول الأمل، والحرص على الدنيا.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (39,'٣٧ - حدثني أحمد بن عاصم العبداني، أخبرنا سعيد بن عامر، عن شعبة، عن عمرو بن مرة، عن عبد الله بن سلمة، قال: قال معاذ بن جبل: يا معشر القراء، كيف بدنيا تقطع رقابكم؟!! ‍‍فمن جعل الله غناه في قلبه فقد أفلح، ومن لا، فليست بنافعته دنياه.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (40,'٣٨ - حدثنا العباس العنبري، أخبرنا محمد بن جهضم، أخبرنا إسماعيل بن جعفر، عن عمارة بن غزية، عن عاصم بن عمر بن قتادة، عن ⦗٢٩⦘ محمود بن لبيد، عن قتادة بن النعمان، قال: قال رسول الله صلى الله عليه وسلم: إذا أحب الله عبداً حماه الدنيا كما يحمي أحدكم مريضه الماء.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (41,'٣٩ - حدثنا علي بن مسلم، أخبرنا سيار بن حاتم، أخبرنا جعفر بن سليمان، قال: سمعت مالك بن دينار يقول: اتقوا السحارة، فإنها تسحر قلوب العلماء.
يعني: الدنيا.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (42,'٤٠ - حدثنا سريج بن يونس، أخبرنا عبد الوهاب بن عطاء، عن موسى بن يسار، أنه بلغه أن النبي صلى الله عليه وسلم، قال: إن الله عز وجل لم يخلق خلقاً هو أبغض إليه من الدنيا، وإنه منذ خلقها لم ينظر إليها.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (43,'٤١ - حدثنا علي بن الحسن بن أبي مريم عن شاذان، عن حماد بن سلمة، عن أبي جعفر الخطمي، قال: كان لجدي مولى يقال له زياد، يعلم بنيه، فنعس الشيخ فجعل زياد يذكر لهم الدنيا والشيخ يسمع، فقال الشيخ: يا زياد ضربت على بني قبة الشيطان، اكشطوها بذكر الله عز وجل.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (44,'٤٢ - حدثني سريج بن يونس، أخبرنا يزيد بن هارون، أخبرنا هشام، قال: سمعت الحسن يقول: والله ما أحد من الناس بسط له الدنيا فلم يخف أن يكون قد مكر به فيها إلا كان قد نقص عقله وعجز رأيه، وما أمسك الله عن عبد الدنيا فلم يظن أنه قد خير له فيها إلا كان قد نقص عقله وعجز رأيه.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (45,'٤٣ - وحدثني سريج بن يونس، أخبرنا مروان بن معاوية، عن شيخ من بني بكر بن وائل، عن الحسن مثله.
ثم قرأ هاتين الآيتين: {فلما نسوا ما ذكروا ⦗٣١⦘ به} .
إلى قوله: {والحمد لله رب العالمين} [الأنعام ٤٤، ٤٥]
وقال الحسن: مكر بالقوم ورب الكعبة وأعطوا حاجاتهم ثم أخذوا.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (46,'٤٤ - حدثنا سريج، عن الوليد بن المسلم، عن الأوزاعي، قال سمعت بلال بن سعد يقول: والله لكفى به ذنباً أن الله عز وجل يزهدنا في الدنيا ونحن نرغب فيها، فزاهدكم راغب ومجتهدكم مقصر وعالمكم جاهل.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (47,'٤٥ - حدثني محمد بن الحارث المقري، حدثنا سيار، أخبرنا جعفر، أخبرنا أبو عمران الجوني، قال: مر سليمان بن داود في موكبه والطير تظله، والجن والإنس عن يمينه وعن يساره.
قال: فمر بعابد من عباد بني إسرائيل، فقال: يا ابن داود لقد آتاك الله ملكاً عظيماً، قال: فسمع سليمان كلمته، فقال: لتسبيحة في صحيفة مؤمن خير مما أعطي ابن داود، فما أعطي لابن داود يذهب والتسبيحة تبقى.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (48,'٤٦ - حدثنا عصمة بن الفضل، أخبرنا الحارث بن مسلم الرازي - وكانوا ⦗٣٢⦘ يرونه من الأبدال عن يحيى بن أبي كثير، أن أبا بكر الصديق رضي الله عنه كان يقول في خطبته: أين الوضاء الحسنة وجوههم، المعجبون بشبابهم؟ أين الملوك الذين بنوا المدائن وحصنوها بالحيطان؟ أين الذين كانوا يعطون الغلبة في مواطن الحرب؟ قد تضعضع بهم الدهر فأصبحوا في ظلمات القبور، الوحا الوحا، النجاة النجاة.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (49,'٤٧ - حدثنا محمد بن الحسين، حدثني خالد بن يزيد القرني، أخبرنا أبو شهاب، عن رجل من عبد القيس، أن حذيفة كان يقول: ما من صباح ولا مساء إلا ومناد ينادي: يا أيها الناس، الرحيل الرحيل، وإن تصديق ذلك في كتاب الله عز وجل: {إنها لإحدى الكبر* نذيرا للبشر*لمن شاء منكم أن يتقدم} قال: في الموت {أو يتأخر} [المدثر: ٣٥، ٣٧] قال: في الموت.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (50,'٤٨ - حدثني محمد بن الحسين، أخبرنا يحيى بن راشد، أخبرنا أبو عاصم، حدثني بزيع الهلالي، عن سحيم مولى بني تميم، قال: جلست إلى عامر بن عبد الله وهو يصلي، فجوز في صلاته ثم أقبل علي، فقال: أرحني بحاجتك، قال: قمت عنه وقام إلى صلاته.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (51,'٤٩ - وحدثني محمد أخبرنا عبد الله بن محمد، حدثني سلمة بن سعيد، قال: مرض داود الطائي فسأله رجل عن حديث، قال: دعني فإني إنما أبادر بخروج نفسي.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (52,'٥٠ - حدثني أبو بكر الصوفي، قال: سمعت أبا معاوية الأسود يقول: إن كنت أبا معاوية تريد لنفسك الجزيل فلا تنم من الليل ولا تغفل، قدم صالح ⦗٣٤⦘ الأعمال ودع عنك كثرة الأشغال، بادر قبل نزول ما تحاذر، ولا تهتم بأرزاق من تخلف، فلست أرزاقهم تكلف.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (53,'٥١ - حدثني أبو علي الطائي، أخبرنا المحاربي، عن إسماعيل بن مسلم، عن أبي معشر، عن إبراهيم، قال: قال عمر بن الخطاب رضي الله عنه: ⦗٣٥⦘ التؤدة في كل شيء خير إلا في أمر الآخرة',NULL,'34',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (54,'٥٢ - حدثني محمد بن الحسين، أخبرنا داود بن المحبر، عن صالح الناري، عن الحسن، قال: يتوسد المؤمن بما قدم من عمله في قبره إن خيراً فخير، وإن شراً فشر، فاجتنبوا المبادرة رحمكم الله في المهلة.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (55,'٥٣ - حدثني محمد بن الحسين، حدثنا بشر بن عمر الزهراني، أخبرنا عبد الواحد بن صفوان، قال: كنا مع الحسن في جنازة، فقال: رحم الله امرءاً عمل لمثل هذا اليوم، إنكم اليوم تقدرون على ما لا يقدر عليه إخوانكم هؤلاء من أهل القبور، فاغتنموا الصحة والفراغ قبل يوم الفزع والحساب.
معناه لا تقعدوا على الدنيا.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (56,'٥٤ - حدثني محمد، أخبرنا عبد الله بن أبي بكر، حدثنا جعفر بن سليمان، قال: سمعت حبيباً أبا محمد يقول: لا تقعدوا فراغاً فإن الموت يطلبكم.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (57,'٥٥ - حدثني محمد بن بشر بن عبد الله النهشلي، قال: دخلنا على أبي بكر النهشلي وهو في الموت، وهو يومئ برأسه يرفعه ويضعه وكأنه يصلي، فقال له بعض أصحابه: في مثل هذه الحالة رحمك الله، قال: إنني أبادر طي الصحيفة.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (58,'٥٦ - حدثني محمد الراسبي يذكر، عن يزيد الأعرج الشني، أنه كان يقول لأصحابه كثيراً: بحسبكم بقاء الآخرة من فناء الدنيا.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (59,'٥٧ - حدثني عثمان بن أبي شيبة، أخبرنا معاوية بن هشام، قال: سمعت سفيان الثوري يقول: كان يقال: إنما سميت الدنيا لأنها دنية، وإنما سمي المال لأنه يميل بأهله.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (60,'٥٨ - حدثنا علي بن الجعد، أخبرنا علي بن علي يعني الرفاعي، عن الحسن قال: بينما رجلان من صدر هذه الأمة يتراجعان بينهما أمر الناس، فقال أحدهما لصاحبه: لا أبا لك أما ترى الناس وقد أتى ما أهلكهم عن هذا الأمر بعدما زعموا أن قد آمنوا؟
قال: جعل يقول: ضعف الناس الذنوب والشيطان.
قال: وجعل يعرض بأمور لا توافق الرجل في نفسه، فلما رأى ذلك، قال: بل خرجوا عن هذا الأمر بعدما زعموا أن قد آمنوا، إن الله عز وجل أشهر الدنيا وغيب الآخرة، فاخذ الناس بالشاهد وتركوا الغائب، والذي نفس عبد الله بن قيس بيده لو أن الله قرن إحداهما إلى جانب الأخرى حتى يعاينها الناس ما عدلوا ولا امتثلوا.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (61,'٥٩ - حدثنا علي بن الجعد، أخبرني علي بن علي، عن الحسن في قوله: {لقد خلقنا الإنسان في كبد} [البلد: ٤] ، قال الحسن: لا أعلم خليقة يكابد من هذا الأمر ما يكابد هذا الإنسان.
قال: وقال سعيد أخوه: يكابد مضايق الدنيا وشدائد الآخرة.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (62,'٦٠ - حدثنا خالد بن خداش، أخبرنا حماد بن زيد، عن علي بن زيد بن جدعان، ⦗٣٩⦘ عن أبي نضرة، عن أبي سعيد، قال: صلى بنا رسول الله صلى لله عليه وسلم العصر، ثم قام فخطبنا، فقال في خطبته: ألا إن الدنيا حلوة خضرة، وإن الله مستخلفكم فيها فناظر كيف تعملون، أر فاتقوا الدنيا واتقوا النساء.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (63,'٦١ - حدثنا خالد بن خداش، أخبرنا حماد بن زيد، عن علي بن زيد، والمعلى، عن الحسن، أن النبي صلى الله عليه وسلم مر على دور من دور الجاهلية، فرأى سخلة منبوذة خداج ما عليها شعر فقال: أترون هذه هانت على أهلها؟ قالوا: من هوانها ألقوها، قال: فو الذي نفسي بيده للدنيا أهون على الله عز وجل من هذه على أهلها.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (64,'٦٢ - وحدثنا خالد بن خداش، أخبرنا حماد بن زيد، عن علي بن زيد قال: كان بشير بن كعب كثيراً ما يقول: انطلقوا حتى أريكم الدنيا.
قال: فجيئ بهم إلى الشرق وهي يومئذ مزبلة ⦗٤٠⦘، فيقول: انظروا إلى دجاجهم وبطهم وثمارهم.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (65,'٦٣ - حدثنا خالد بن خداش، أخبرنا حماد بن زيد، عن مجالد، عن قيس بن أبي حازم، عن المستورد بن شداد، قال: قال رسول الله صلى الله عليه وسلم: والذي نفسي بيده ما الدنيا في الآخرة إلا كرجل وضع إصبعه في اليم فلينظر بم رجعت إليه.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (66,'٦٤ - حدثنا العباس بن أبي عبد الله، عن شيخ من الأنصار، عن وهب بن منبه، قال: بينما ركب يسيرون إذ هتف بهم هاتف: ألا إنما الدنيا مقيل للرائح ... قضى وطراً من حاجة ثم هجرا
ألا لا ولا يدري علام قدومه ... ألا كلما قدمت تلفى مؤخراً',NULL,'40',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (67,'٦٥ - حدثني عون بن إبراهيم، عن علي بن معبد قال: قال وهب بن منبه: قرأت في بعض الكتب: الدنيا غنيمة الأكياس وغفلة الجهال، لم يعرفوها حتى أخرجوا منها، فسألوا الرجعة فلم يرجعوا.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (68,'٦٦ - حدثني عون بن إبراهيم، حدثني أحمد بن أبي الحواري، عن ⦗٤١⦘ عمر بن عبد الواحد، عن عثمان بن عطاء، عن أبيه: {إنا أخلصناهم بخالصة ذكرى الدار} [ص: ٤٦] قال: أخلصناهم بذكر الآخرة.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (69,'٦٧ - حدثني محمد بن إسماعيل بن إبراهيم العنبري الكوفي، عن جابر بن عون الأسدي، قال: أول كلام تكلم به سليمان بن عبد الملك، أن قال: الحمد لله الذي ما شاء صنع، وما شاء رفع، وما شاء وضع، ومن شاء أعطى، ومن شاء منع، إن الدنيا دار غرور، ومنزل باطل، وزينة تتقلب، تضحك باكياً وتبكي ضاحكاً، وتخيف آمناً وتؤمن خائفاً، وتفقر مثريها وتثري فقيرها، ميالة لاعبة بأهلها.
يا عباد الله اتخذوا كتاب الله إماماً وارضوا به واجعلوه لكم قائداً، فإنه ناسخ لما قبله ولن ينسخه كتاب بعده.
اعلموا عباد الله، إن هذا القرآن يجلو كيد الشيطان وضغائنه كما يجلو ضوء الصبح إذا تنفس إدبار الليل إذا عسعس.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (70,'٦٨ - حدثنا عبد الرحمن بن صالح، أخبرنا معاوية، عن الأعمش، عن عمارة بن عمير، عن عبد الرحمن بن يزيد، قال: قال عبد الله: أنتم أكثر صلاة، وأكثر صياماً، وأكثر جهاداً من أصحاب محمد صلى الله عليه وسلم، وهم كانوا خيراً منكم.
قالوا: فيم ذاك يا أبا عبد الرحمن؟ قال: كانوا أزهد منكم في الدنيا وأرغب في الآخرة.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (71,'٦٩ - وأنشدني أبو الحسن أحمد بن يحيى قوله: ألا أيها الطالب ... أمراً ليس يلحقه
ويا من طال بالدنيا ... وزهرتها تعلقه
أما ينفك ذا أمل ... صروف الدهر تسبقه
وأعقل ما يكون المر ... ء فالحدثان تطرقه
أرى الدنيا تمني المر ... ء أمراً لا يحققه
ويكذب نفسه فيها ... وريب الدهر يصدقه
ولم أر جامعاً إلا ... يد الدنيا تفرقه',NULL,'42',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (72,'٧٠ - ووأنشدني الحسين بن عبد الرحمن الشاعر ذكر الدنيا، فقال: ألم ترها تلهي بنيها عشية ... وتترك في الصبح المجالس نوحا
وتنمي عديد الحي حتى إذا بها ... غدت فأدارت بالمنون له الرحا',NULL,'42',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (73,'٧١ - حدثني محمد بن الحسين، حدثني أبو عمر الضرير، حدثني رجل من المسعوديين، قال: قال عون بن عبد الله: زهرة الدنيا غرور ولو تحلت بكل زينة، والخير الأكبر غداً في الآخرة فنحن بين مسارع ومقصر.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (74,'٧٢ - حدثني محمد بن الحسين، حدثني المنهال بن يحيى، حدثني إياس بن حمزة رجل من أهل البحرين، قال:
: قالت امرأة من قريش كانت تسكن البحرين: لو رأت أعين الزاهدين ثواب ما أعد الله لأهل الإعراض عن الدنيا لذابت أنفسهم شوقاً واشتياقاً إلى الموت لينالوا من ذلك ما أملوا من فضله تبارك وتعالى.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (75,'٧٣ - وحدثنا أبو عبد الرحمن القرشي عبد الله بن عمر بن محمد، أخبرنا محمد بن يعلى، أخبرنا موسى بن عبيدة الربذي، أن لقمان قال لابنه: يا بني إنك إن استدبرت الدنيا منذ يوم نزلتها، واستقبلت الآخرة فأنت إلى دار تقرب منها أقرب منك إلى دار تباعد عنها.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (76,'٧٤ - حدثني محمد بن إسماعيل بن إبراهيم العنبري، أخبرنا أبو شجاع، قال: كتب علي بن أبي طالب إلى سلمان الفارسي:
أما بعد، فإنما الدنيا مثل الحية لمن لمسها يقتل بسمها، فأعرض عما يعجبك فيها لقلة ما يصحبك منها.
وضع همومها لما أيقنت به من فراقها، وكن أسر ما تكون فيها أحذر ما تكون لها، فإن صاحبها كلما اطمأن منها إلى سرور أشخصه عنه مكروه، والسلام.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (77,'٧٥ - حدثني محمد بن عبد المجيد التميمي، أخبرنا جعفر بن سليمان، عن مالك بن دينار، قال: قال لي عبد الله الرازي: إن سرك أن تجد حلاوة العبادة وتبلغ ذروة سنامها فاجعل بينك وبين شهوات الدنيا حائطاً من حديد.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (78,'٧٦ - حدثني إبراهيم بن سعيد، أخبرنا عبد العزيز القرشي قال: قال سفيان، قال عيسى بن مريم: ⦗٤٥⦘ كما لا يستقيم النار والماء في إناء كذلك لا يستقيم حب الآخرة والدنيا في قلب المؤمن.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (79,'٧٧ - حدثني عبيد الله بن محمد، أخبرنا أبو أسامة، أخبرنا مالك بن مغول، عن سهل أبي الأسد، قال: كان يقال: مثل الذي يريد أن يجمع له الآخرة والدنيا مثل عبد له ربان لا يدري أيهما رضي.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (80,'٧٨ - حدثنا خالد بن خداش، أخبرنا حماد بن زيد، عن ثابت، قال: كتب إلي سعيد بن أبي بدرة، قال أبو موسى: إنه لم يبق من الدنيا إلا فتنة منتظرة وكل محزن.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (81,'٧٩ - حدثني هارون بن سفيان، أخبرنا ابن أبي ليلى، عن مسلمة بن ⦗٤٦⦘ جعفر، عن عبد الله بن دينار، عن الحسن، أنه كان يقول: من أحب الدنيا وسرته ذهب خوف الآخرة من قلبه، وما من عبد يزداد علماً ويزداد على الدنيا حرصاً إلا ازداد إلى الله عز وجل بغضاً وازداد من الله بعداً.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (82,'٨٠ - حدثني هارون بن سفيان، أخبرنا الوليد بن صالح، أخبرنا أبو المليح، عن ميمون - يعني ابن مهران -، قال: الدنيا كلها قليل وقد ذهب أكثر القليل وبقي قليل من القليل.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (83,'٨١ - أنشدني رجل من بني يشكر: إنما الدنيا وإن سر ... ت قليل من قليل
ليس يحلو أن تبدى ... لك في زي جميل
ثم ترميك من المأ ... من بالخطب الجليل
إنما العيش جوار الل ... هـ في ظل ظليل
حيث لا تسمع ما يؤ ... ذيك من قال وقيل',NULL,'47',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (84,'٨٢ - حدثني حمزة بن العباس، أخبرنا عبدان بن عثمان، أخبرنا ابن المبارك، أخبرنا حنظلة ابن أبي سفيان، عن عطاء، قال: قال ابن مسعود: ما أكثر أشباه الدنيا منها.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (85,'٨٣ - حدثني حمزة بن العباس، أخبرنا عبدان بن عثمان، أخبرنا ابن ⦗٤٨⦘ المبارك، أخبرنا ابن لهيعة، أخبرنا شعيب بن أبي سعيد: أن رجلاً قال: يا رسول الله، كيف لي أن أعلم كيف أنا؟ قال: إذا رأيت كلما طلبت شيئاً من أمر الآخرة وابتغيته يسر لك، وإذا أردت شيئاً من أمر الدنيا وابتغيته عسر عليك فأنت على حال حسنة، وإذا كنت على خلاف ذلك فإنك على حال قبيحة.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (86,'٨٤ - حدثني محمد بن الحسين، حدثني أبو أيوب الدمشقي، قال: قال أنس بن ينعم - وكان من عباد أهل الشام -:
بؤساً لمحب الدنيا، أتحب ما أبغض الله عز وجل؟ ! ! .',NULL,'49',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (87,'٨٥ - حدثنا محمد بن الحسين، أخبرنا محمد بن يزيد بن خنيس، أخبرنا سفيان الثوري: قال عمر بن الخطاب: لا يغرنك أن يجعل لك كثيراً ما تحب من أمر دنياك إذا كنت ذا رغبة في أمر آخرتك.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (88,'٨٦ - أنشدني أحمد بن موسى الثقفي: جهول ليس تنهاه النواهي ... ولا تلقاه إلا وهو ساهي
يسر بيومه لعباً ولهواً ... ولا يدري وفي غده الدواهي
مررت بقصره فرأيت فيه ... عجيباً فيه من زجر وناهي
بدا فوق السرير فقلت من ذا ... فقالوا ذلك الملك المباهي
رأيت الباب سود والجواري ... ينحن وهن يكسرن الملاهي
تبين أي دار أنت فيها ... ولا تسكن إليها وآذر ما هي',NULL,'49',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (89,'٨٧ - حدثنا إسحاق بن إسماعيل، أخبرنا جرير، عن ليث، قال: صحب رجل عيسى بن مريم، فقال: أكون معك وأصحبك.
قال: فانطلقا فانتهيا إلى شط نهر فجلسا يتغديان ومعهما ثلاثة أرغفة، فأكلا رغيفين وبقي رغيف فقام ⦗٥٠⦘ عيسى إلى النهر فشرب ثم رجع فلم يجد الرغيف، فقال للرجل: من أخذ الرغيف؟ قال: لا أدري.
قال: فانظلق معه صاحبه فرأى ظبية معها خشفان.
قال: فدعا أحدهما فأتاه فذبحه، فاشتوى منه فأكل هو وذاك، ثم قال للخشف: قم بإذن الله، فقام فذهب، فقال للرجل: أسألك بالذي أراك هذه الآية من أخذ الرغيف؟ قال: ما أدري.
قال: ثم انتهيا إلى وادي فأخذ عيسى بيد الرجل فمشيا على الماء، فلما جاوزا، قال: أسالك بالذي أراك هذه الآية من أخذ الرغيف؟ قال: لا أدري.
قال: فانتهيا إلى مفازة فجلسا فأخذ عيسى فجمع تراباً أو كثيباً، ثم قال: كن ذهباً بإذن الله، فصار ذهباً، فقسمه ثلاث أثلاث، فقال: ثلث لي، وثلث لك، وثلث لمن أخذ الرغيف، فقال: أنا أخذت الرغيف، قال: فكله لك.
قال: وفارقه عيسى فانتهى إليه رجلان في المفازة ومعه المال، فأراردا أن يأخذا منه ويقتلاه، فقال: هو بيننا أثلاثاً.
قال: فابعثوا أحدكم إلى القرية حتى يشتري طعاماً.
قال: فبعثوا أحدهم.
قال: فقال الذي بعث: لأي شيء أقاسمهما هذا المال، ولكني أضع في هذا الطعام سماً فأقتلهما.
قال: ففعل.
وقال ذانك: لأي شيء نجعل لهذا ثلث المال ولكن: إذا رجع إلينا قتلناه واقتسمناه بيننا.
قال: فلما رجع إليهما قتلاه وأكلا الطعام فماتا.
قال: فبقي ذلك المال في المفازة وأولئك الثلاثة قتلى عنده.
وفي غير حديث إسحاق بن إسماعيل: قال: فمر بهم عيسى على تلك الحال، فقال: هذه الدنيا فاحذروها.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (90,'٨٨ - حدثنا إسحاق بن إسماعيل، حدثنا روح بن عبادة، أخبرنا ⦗٥١⦘ هشام بن حسان، عن الحسن، قال: بلغني أن رسول الله صلى الله عليه وسلم قال لأصحابه: إنما مثلي ومثلكم ومثل الدنيا كمثل قوم سلكوا مفازة غبراء حتى إذا لم يدروا ما سلكوا منها أكثر أو ما بقي، أنفذوا الزاد وحسروا الظهر وبقوا بين ظهراني المفازة لا زاد ولا حمولة فأيقنوا بالهلكة.
فبينما هم كذلك إذ خرج عليهم رجل في حلة يقطر رأسه، فقالوا: إن هذا قريب عهد بريف، وما جاءهم هذا إلا من قريب.
قال: فلما انتهى إليهم قال: يا هؤلاء، قالوا: يا هذا، قال: علام أنتم؟ قالوا: على ما ترى.
قال: أرأيتم إن هديتكم إلى ماء روي ورياض خضر، ما تعملون؟ قالوا: لا نعصيك شيئاً، قال: عهودكم ومواثيقكم بالله، قال: فأعطوه عهودهم ومواثيقهم بالله لا يعصونه شيئاً.
قال: فأوردهم ماء ورياضاً خضراً.
قال: فمكث فيهم ما شاء الله ثم قال: يا هؤلاء، قالوا: يا هذا، قال: الرحيل، قالوا: إلى أين؟ قال: إلى ما ليس كمائكم وإلى رياض ليس كرياضكم.
قال: فقال جل القوم وهم أكثرهم: والله ما وجدنا هذا حتى ظننا أن لن نجده، وما نصنع بعيش خير من هذا.
قال: وقالت طائفة وهم أقلهم: ألم تعطوا هذا الرجل عهودكم ومواثيقكم بالله لا تعصونه شيئاً وقد صدقكم في أول حديثه، فوالله ليصدقكم في آخره؟ .
قال: فراح فيمن اتبعه وتخلف بقيتهم، فنزل بهم عدو فأصبحوا ما بين أسير وقتيل.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (91,'٨٩ - حدثنا إسحاق بن إسماعيل، أخبرنا روح بن عبادة، عن عوف، عن الحسن قال: يلغني أن رسول الله صلى الله عليه وسلم قال: ⦗٥٢⦘ إنما مثل الدنيا كمثل الماشي في الماء، هل يستطيع الذي يمشي في الماء أن لا تبتل قدماه.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (92,'٩٠ - حدثني علي بن أبي مريم عن شيخ له عن أبيه عن وهب بن منبه قال: قال عيسى: بحق أقول لكم كما ينظر المريض إلى طيب الطعام فلا يلتذ به، من شدة الوجع كذلك صاحب الدنيا لا يلتذ العبادة، ولا يجد حلاوتها مع ما يجد من حب الدنيا، وبحق أقول لكم إن الدابة إذا لم تركب وتمتهن تصعبت وتغير خلقها، كذلك القلوب إذا لم ترقق بذكر الموت وتنصبها دأب العبادة، تقسو، وتغلظ.
بحق أقول لكم إن الزق ما لم بنخرق أو يقحل فسوف يكون وعاء للعسل، وكذلك القلوب ما لم تحرقها الشهوات، أو يدنسها الطمع أو يقسيها النعيم، فسوف تكون أوعية للحكمة.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (93,'٩١ - حدثني عبد الرحمن بن أبي صالح، أخبرنا المحاربي، عن سفيان، وقال: بلغنا أن لقمان قال لابنه: يا بني إن الدنيا بحر عميق يغرق فيه ناس كثير، فلتكن سفينتك فيها تقوى الله، وحشوها الإيمان بالله، وشراعها التوكل على الله، لعلك تنجو، وما أراك بناج.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (94,'٩٢ - حدثني سريج بن يونس، حدثني من سمع عبيد الله بن مسلم قال: بلغني أن عيسى بن مريم عليه السلام، قال: ⦗٥٣⦘ ويل لصاحب الدنيا كيف يموت ويتركها، ويأمنها وتغره، ويثق بها وتخذله، ويل للمغترين كيف أرتهم ما يكرهون، وفارقهم ما يحبون، وجاءهم ما يوعدون، وويل لمن الدنيا همه، والخطايا عمله، كيف يفتضح غداً بذنبه.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (95,'٩٣ - حدثني عون بن إبراهيم، حدثني أحمد بن أبي الحواري، حدثني عبادة أبو مروان، قال: أوحى الله إلى موسى: يا موسى ما لك ولدار الظالمين، إنها ليست لك بدار، أخرج منها همك، وفارقها بعقلك، فبئست الدار هي، إلا لعامل يعمل فيها، فنعمت الدار هي، يا موسى إني مرصد للظالم حتى آخذ منه للمظلوم.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (96,'٩٤ - حدثني محمد بن الحسين، حدثني عون بن عمارة، قال: قال أبو محرز الطفاوي: كلف الناس بالدنيا ولن ينالوا منها فوق قسمتهم، وأعرضوا عن الآخرة وببغيتها يرجو العباد نجاة أنفسهم.
قال: قال أبو محرز: لما بان للأكياس أعلى الدارين منزلة طلبوا العلو بالعلو من الأعمال، وعلموا أن الشيء لا يدرك بأكثر منه، فبذلوا أكثر ما عندهم، بذلوا والله المهج رجاء الراحة لديه، والفرج في يوم لا يخيب فيه له طالب.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (97,'٩٥ - حدثنا هارون بن عبد الله، حدثنا محمد بن بشر، أخبرنا مسعر، ⦗٥٤⦘ عن إبراهيم بن محمد بن المنتشر، قال: كان مسروق يركب بغلته كل جمعة، ويحملني خلفه فأتى كناسة بالحيرة قديمة فحمل عليها بغلته، ويقول الدنيا تحتنا.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (98,'٩٦ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أخبرنا عبد الله بن المبارك، أنبأنا إبراهيم بن نشيط، أخبرنا كعب بن علقمة، قال: قال سعد بن مسعود التجيبي: إذا رأيت العبد دنياه تزداد، وآخرته تنقص مقيماً على ذلك راضياً به، فذلك المغبون الذي يلعب بوجهه وهو لا يشعر.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (99,'٩٧ - حدثني حمزة، أنبأنا عبدان، أنبأنا عبد الله، أنبأنا وهيب، قال: قال عيسى عليه السلام: أربع لا يجتمعن في أحد من الناس إلا تعجب: الصمت وهو أول العبادة، والتواضع لله عز وجل، والزهادة في الدنيا، وقلة الشيء.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (100,'٩٨ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا ⦗٥٥⦘ عبد الله، أنبأنا حريث بن السائب، أخبرنا الحسن، قال: مر رسول الله صلى الله عليه وسلم، على مزبلة في طريق المدينة فقال: من سره أن ينظر إلى الدنيا بحذافيرها فلينظر إلى هذه المزبلة ثم قال: ولو أن الدنيا تعدل عند الله جناح ذبابة ما أعطى كافراً منها شيئاً.
وقال بعض الحكماء من الشعراء:
أما مررت بساحات معطلة ... فيها المزابل كانت قبل مغشية
أما نظرت إلى الدنيا وزينتها ... بزخرف من غرور اللهو موشية
أعظم بحمقة نفس لا تكون بما ... تغنى به من صروف الدهر مغنية
لله در أذى عين تقر بها ... وإنها لعلى التنغيص مبينة
قال أبو بكر: أملى علي عبد الرحمن بن صالح هذه الرسالة:
أما بعد: عافانا الله وإياك من شر دار قد أدبرت، والنفوس عليها قد ولهت، ورزقت وإياك خير دار قد أقبلت، والقلوب عنها قد غلفت، وكأن المعمور من هذه الدار قد يرحل عن أهله، وكأن المغفول عنه من تلك الدار قد أباح بأهله فغنم غانم، وندم نادم، واستقبل الخلق خلد لا يزول، وحكم عليهم جبار لا يجور، فهنالك فضع الهموم، وصغر ما دونه من متاع هذا الغرور، والسلام.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (101,'٩٩ - حدثني عبد الرحمن بن صالح، أخبرنا أبو معاوية، عن الأعمش ⦗٥٦⦘ عن عمارة، عن يزيد بن معاوية النخعي، قال: إن الدينا جعلت قليلاً فما بقي منها إلا قليل من قليل.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (102,'١٠٠ - أنشدني أحمد بن موسى الثقفي: فتى مالت به الدنيا ... وغرته ببارقها
فلاذ بها وعانقها ... وبئست عرس عاشقها
غدا يوماً لضيعته ... ليصلح من مرافقها
فلما جاءها والشم ... س تزهر من مشارقها
تلقته جداولها ... تفجر في حدائقها
وأطرف من طرائفها ... جنياً من بواسقها
وجيء بخيرها ثمرا ... وأطيبها لذائقها
وأطمعه مؤلفه ... بباين في مذائقها
فأمعن في ثرايدها ... وأكثر من شرائقها
وجيء بقهوة حرف ... تساق بكف سائقها
بكفي طفلة خود ... تثنى في مخانقها
فحدث نفسه كذباً ... وزوراً غير صادقها
ومناها الخلود لها ... غبياً عن بوائقها
فأصبح هالكاً فيها ... على أدنى نمارقها
ولاذ بنعشه عصب ... تسير على عوائقها
إلى دار البلى فرداً ... وحيداً في مضايقها
إلا إن الأمور غداً ... تصير إلى حدائقها',NULL,'56',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (103,'١٠١ - أنشدني أبي رحمه الله: دع الدنيا لناكحها ... يستقبح من روائحها
ولا يغررك رائحة ... تصيبك من روائحها ⦗٥٧⦘
أرى الدنيا وإن عشقت ... تدل على فضائحها
مصدقة لغائبها ... مكذبة لمادحها',NULL,'56',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (104,'١٠٢ - أنشدني عامر بن عامر الهمذاني: إنما الدنيا إلى الجنة والنار طريق
والليالي متجر الإنسان والأيام سوق',NULL,'57',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (105,'١٠٣ - أنشدني الحسن بن عبد الله: إذا لم يعظني واعظ من جوارحي ... فما شيء سواه بنافعي
أؤمل دنيا أرتجي من رحابها ... غلالة سم مورد الموت ناقع
ومن يأمن الدنيا يكن مثل آخذ ... على الماء خانته فروج الأصابع
وكالحالم المسرور عند منامه ... بلذة أضغاث من أحلام هاجع
فلما تولى الليل ولى سروره ... وعادت عليه عاطفات الفجائع',NULL,'57',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (106,'١٠٤ - أنشدني الحسن بن السكن بن السكن بن سليمان: حياتك بالهم مقرونة ... فما تقطع العيش إلا بهم
لذا ذات دنياك مسمومة ... فما تأكل الشهد إلا بسم',NULL,'57',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (107,'١٠٥ - حدثنا علي بن الجعد الجوهري أنبأنا المبارك بن فضالة عن الحسن قال: خطب عتبة بن غزوان الناس بالبصرة فحمد الله وأثنى عليه، ثم قال: يا أيها الناس إن هذه الدنيا قد آذنت بصرم، وولت حذاء، ولم يبق منها إلا صبابة كصبابة الإناء، وإنكم مفارقوها لا محالة فانتقلوا منها بخير ما بحضرتكم، ⦗٥٨⦘ والذي نفسي بيده ما كانت قبلك نبوة إلا تناسخت حتى يكون آخرها ملكاً وستبلون الأمراء بعدنا.
الحسن قال لنا بعد عبرة: وإني أعوذ بالله أن أكون في نفسي عظيماً، وفي أنفس الناس صغيراً، فقد رأيتني سابع سبعة مع رسول الله، صلى الله عليه وسلم، فرأينا من شهر ما لنا طعام إلا ما نصيب من ورق الشجر حتى قرحت أشداقنا من أكل الشجر، ولقد رأيتني ألتقط بردة فشققتها بيني وبين سعد بن مالك فما علمت من السبعة حتى اليوم إلا قد أصبح أميراً على مصر، أعجبتم، فما بعدكم أعجب، والذي نفسي بيده لو أن حجراً قذف في شفير جهنم ما بلغ قعرها سبعين سنة ً، والذي نفسي بيده لتملأن والذي نفسي بيده إن ما بين مصراعي الجنة مسيرة أربعين سنة، والذي نفسي بيده ليأتين عليها ساعة وهو كظيظ.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (108,'١٠٦ - حدثنا عثمان بن معبد، أخبرنا عبد الله بن صالح، حدثني الليث بن سعد، حدثني يزيد بن أبي حبيب: أن علي بن رباح، أخبره أنه سمع عمرو بن العاص، يقول على المنبر: والله ما رأيت قوماً قط أرغب فيما كان رسول الله صلى الله عليه وسلم، يزهد فيه منكم، ⦗٥٩⦘ يرغبون في الدنيا، وكان رسول الله صلى الله عليه وسلم يزهد فيها، والله ما مر رسول الله صلى الله عليه وسلم، ثلاث من الدهر إلا والذي عليه أكثر من الذي له.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (109,'١٠٧ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله بن المبارك، أخبرني يحيى بن أيوب، حدثني عبد الله بن جنادة المعافري: أن أبا عبد الرحمن الحبلي، حدثه عن عبد الله بن عمرو، عن النبي صلى الله عليه وسلم، قال: الدنيا سجن المؤمن، وسنته، فإذا فارق الدنيا فارق السجن والسنة.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (110,'١٠٨ - وحدثنا حمزة بن العباس، أنبأنا عبدان، أنبأنا عبد الله، أنبأنا شريك بن عبد الله، عن يعلى بن عطاء، عن أبيه، عن عبد الله بن عمور، قال: ⦗٦٠⦘ الدنيا جنة الكافر، وسجن المؤمن، وإنما مثل المؤمن حين تخرج نفسه كمثل رجل كان في سجن فأخرج، فجعل يتقلب في الأرض، ويتفسح فيها.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (111,'١٠٩ - وحدثني حمزة بن العباس، أنبانا عبدان، أنبأنا عبد الله، أنبأنا عبد الرحمن بن يزيد بن جابر، حدثني أبو عبد ربه، قال: سمعت معاوية، يقول على هذا المنبر: سمعت رسول الله صلى الله عليه وسلم يقول: إنما بقي من الدنيا بلاء وفتنة، وإنما مثل عمل أحدكم كمثل الوعاء إذا طاب أعلاه طاب أسفله، وإذا خبث أعلاه خبث أسفله.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (112,'١١٠ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله، أنبأنا المبارك بن فضالة، عن الحسن: أنه كان إذا تلى هذه الآية:
{فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور} [لقمان: ٣٣] . ⦗٦١⦘
قال: من قال ذا؟ من خلقها؟ ومن هو أعلم بها.
قال: وقال الحسن: إياكم وما شغل من الدنيا، فإن الدنيا كثيرة الأشغال، لا يفتح رجل على نفسه باب شغل، إلا أوشك ذلك الباب أن يفتح عليه عشرة أبواب.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (113,'١١١ - وحدثني حمزة، أنبأنا عبدان، أنبأنا عبد الله، أنبأنا طلحة بن صبيح، عن الحسن قال: المؤمن من يعلم أن ما قال الله عز وجل كما قال، والمؤمن أحسن الناس عملاً، وأشد الناس خوفاً، لو أنفق جبلاً من مال ما أمن دون أن يعاين لا يزداد صلاحاً، وبراً وعبادة، إلا ازداد فرقاً، يقول: لا أنجو، والمنافق يقول: سواد الناس كثير، وسيغفر لي، ولا بأس علي، يسيء في العمل، ويتمنى على الله عز وجل.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (114,'١١٢ - حدثني أبو سعيد المديني عبد الله بن المسيب، حدثني محمد بن عمر بن سعيد العطار، حدثني زكريا بن منظور، عن عمه، عن عمر بن عبد العزيز، كتب إلى أخ له: يا أخي إنك قد قطعت عظيم السفر، وبقي أقله فاذكر يا أخي المصادر والموارد، فقد أوحي إلى نبيك محمد صلى الله عليه وسلم، في القرآن أنك من أهل الورود، ولم يخبرك أنك من أهل الصدر والخروج، وإياك أن تغرك الدنيا، فإن الدنيا دار من لا دار له، ومال من لا مال له، أي أخي إن أجلك قد دنا، فكن وصي نفسك، ولا تجعل الرجال أوصياءك.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (115,'١١٣ - حدثني عبد الرحمن بن صالح، حدثنا عمرو بن هاشم الجنبي، عن جويبر، عن الضحاك، عن ابن عباس، قال: إن الله عز وجل ناجى موسى فقال: يا موسى إنه لم يتصنع لي المتصنعون بمثل الزهد في الدنيا، ولم يتقرب لي المتقربون بمثل الورع عما حرمت عليهم.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (116,'١١٤ - حدثني عبد الرحمن بن صالح، أنبأنا عبد الرحمن المحاربي، عن مالك بن مغول، قال: أخبرت عن الحسن قال: قالوا: يا رسول الله، من خيرنا؟ قال: وأزهدكم في الدنيا وأرغبكم في الآخرة.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (117,'١١٥ - حدثني علي بن أبي مريم، عن زهير بن عباد، أخبرنا داود بن هلال النصيبي، قال: مكتوب في في صحف إبراهيم عليه السلام: يا دنيا ما أهونك على الأبرار الذين تصنعت لهم، وتزينت لهم، إني قد قذفت في قلوبهم بغضك، والصدود عنك، ما خلقت خلقاً أهون علي منك، كل شأنك صغير، وإلى الفناء تصيرين، قضيت عليك يوم خلقت الخلائق أن لا تدومي لأحد، ولا يدوم لك أحد، وإن بخل بك صاحبك، وشح عليك، طوبى للأبرار الذين أطلعوني من قلوبهم على الرضا، وأطلعوني من ضميرهم على الصدق، والاستقامة، طوبى لهم، ما لهم عندي من الجزاء إذا وفدوا إلي من قبورهم، النور يسعى أمامهم، والملائكة حافون بهم حتى أبلغ بهم ما يرجون من رحمتي.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (118,'١١٦ - وحدثني ابن أبي مريم، حدثني زكريا بن يحيى، حدثني أبو العباس الكندي، قال: أهديت إلى صديق لي سكراً، فكتب إلي: لا تعد ودع الإخاء على حاله حتى نلتقي، وليس في القلوب شيء ثم كتب في أسفل كتابه: ما طالب الدنيا من حلالها وجميلها وحسنها عند الله بالمحمود، ولا بالمغبوط، فكيف يطلبها من أيدي المخلوقين من قذرها ونكدها، بالعسار والمنقصة.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (119,'١١٧ - حدثني سليمان بن أبي شيخ، أخبرنا أبو سفيان الحميري أحسبه، عن حصين: لما بعث الله موسى وهارون عليهما السلام إلى فرعون، قال: ⦗٦٤⦘ لا يرعكما لباسه الذي لبس من الدنيا فإن ناصيته بيدي، ليس ينطق ولا يطرق ولا يتنفس إلا بإذني، ولا يعجبكما ما متع به منها، فإنما هي زهرة الحياة الدنيا وزينة المترفين ولو شئت أن أزينكما بزينة من الدنيا ليعرف فرعون حين يراها أن مقدرته تعجز عما أوتيتما لفعلت، ولكنني أرغب بكما عن ذلك، وأزوي ذلك عنكما، وكذلك أفعل بأوليائي، إني لأذودهم عن نعيمها كما يذود الراعي الشفيق غنمه عن مراتع الهلكة، وإني لأجنبهم سلوتها كما يجنب الراعي الشفيق إبله، عن مبارك العرة، وما ذاك لهوانهم علي، ولكن ليستكملوا نصيبهم من كرامتي سالماً موفراً، لم يكلمه الطمع، ولم تنقصه الدنيا بغرورها، إنما يتزين لي أوليائي بالذل والخشوع، والخوف والتقوى، تثبت في قلوبهم، فتظهر على أجسادهم، فهي ثيابهم التي يلبسون، ودثارهم الذي يظهرون، وضميرهم الذي يستشعرون، ونجاتهم التي بها يفوزون، ورجاؤهم الذي إياه يأملوان، ومجدهم الذي به يفخرون، وسيماهم التي بها يعرفون، فإذا لقيتهم فاخفض لهم جناحك، وذلل لهم قلبك، ولسانك، واعلم أنه من أخاف لي ولياً فقد بازرني بالمحاربة، ثم أنا الثائر له يوم القيامة.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (120,'١١٨ - حدثنا الحكم بن موسى، أخبرنا الخليل بن أبي الخليل، عن صالح بن أبي شعيب، قال: أوحى الله عز وجل إلى عيسى بن مريم:
أنزلني من نفسك كهمك، واجعلني ذخراً لك في معادك، وتقرب إلي بالنوافل أدنك، وتوكل علي أكفك، ولا تول غيري فأخذلك، اصبر على البلاء، وارض بالقضاء، وكن كمسرتي فيك، فإن مسرتي أن أطاع فلا أعصى، وكن مني قريباً، وأحيي ذكري بلسانك، وليكن ودي في قلبك، تيقظ لي في ساعات الغفلة، وكن لي راهباً راغباً إلي أمت قلبك بالخشية، راع الليل لتجزى مسرتي، وأظمئ لي نهارك ليومك الذي عندي، نافس في الخيرات جهدك، وكن في الخليقة بعدلي، واحكم فيها بنصيحتي، فقد أنزلت عليك شفاء وساوس الصدر من مرض ⦗٦٥⦘ الشيطان، وجلاء الأبصار، وعشاء الكلال، ولا تكن حلساً، كأنك مقبور، وأنت حي تتنفس، بحق أقول لك ما آمنت بي الخليقة إلا خشعت لي، ولا أخشعت لي إلا رجت ثوابي، أشهدك أنها آمنة من عقابي ما لم تغير أو تبدل سنتي، أكحل عينك بملول الحزن، إذا ضحك البطالون، احذر ما هو آت من أمر الميعاد من الزلازل، والأهوال، والشدائد، حيث لا ينفع مال، ولا أهل، ولا ولد، ابك على نفسك أيام الحياة، بكاء من قد ودع الأهل، وقلى الدنيا، واترك اللذات لأهلها، وارفع رغبتك عند إلهك، وكن على ذلك محتسباً صابراً، طوبى لك إن نالك ما وعدت الصابرين، ترج من الدنيا يوماً بيوم، وارض منها بالبلغة وليكفك منها الخشن، ذق مذاقة ما قد ذهب منك أين طعمه، وما لم يأتك أين لذاته، لو رأت عيناك ما أعددت لأوليائي الصالحين لذاب قلبك وزهقت نفسك اشتياقاً إليه.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (121,'١١٩ - حدثنا فهد بن حيان، وداود بن عمرو الضبي، أخبرنا عبد الله بن المبارك، عن رباح بن زيد، عن عبد العزيز بن جوران، عن وهب بن منبه، قال: مثل الدنيا والآخرة كمثل رجل له ضرتان إن أرضى إحداهما أسخط الأخرى.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (122,'١٢٠ - وحدثني سريج، أخبرنا خلف بن خليفة، عن سيار أبي الحكم، قال: ⦗٦٦⦘ الدنيا والآخرة يجتمعان في قلب العبد فأيهما غلب كان الآخر تبعاً له.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (123,'١٢١ - حدثني عون بن إبراهيم، حدثني أحمد بن أبي الحواري، قال: سمعت أبا سليمان، قال:
إذا كانت الآخرة في القلب، جاءت الدنيا تزحمها، وإذا كانت الدنيا في القلب، لم تزحمها الآخرة، لأن الآخرة كريمة والدنيا لئيمة.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (124,'١٢٢ - حدثنا هارون بن عبد الله، أخبرنا جعفر، قال: سمعت مالك بن دينار، يقول: بقدر ما تحزن للدينا فكذلك يخرج هم الآخرة من قلبك، وبقدر ما تحزن للآخرة فكذلك يخرج هم الدنيا من قلبك.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (125,'١٢٣ - حدثنا محمد بن علي بن شقيق، أخبرنا أبو إسحاق إبراهيم بن الأشعث، قال: سمعت الفضيل بن عياض، قال: قال ابن عباس:
يؤتى بالدنيا يوم القيامة في صورة عجوز شمطاء زرقاء أنيابها بادية مشوهة خلقها، فتشرف على الخلائق، فيقال: أتعرفون هذه؟ فيقولون: نعوذ بالله من معرفة هذه.
فقال: هذه الدنيا التي تناحرتم عليها، بها تقاطعتم الأرحام، وبها ⦗٦٧⦘ تحاسدتم وتباغضتم، واغتررتم، ثم تقذف في جهنم فتنادي أي رب أين أتباعي وأشياعي؟ فيقول الله عز وجل: ألحقوا بها أتباعها وأشياعها.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (126,'١٢٤ - وحدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: سمعت الفضيل قال: بلغني أن رجلاً عرج بروحه، قال: فإذا بامرأة على قارعة الطريق عليها من كل زينة الحلى، والثياب، وإذا هي لا يمر بها أحد إلا جرحته، وإذا هي أدبرت كانت أحسن شيء رآه الناس، وإذا أقبلت كانت أقبح شيء رآه الناس، عجوز شمطاء، زرقاء عمشاء، قال: فقلت: أعوذ بالله منك.
قالت: لا والله لا يعيذك الله حتى تبغض الدرهم.
قلت: من أنت؟ قالت: أما تعرفني؟ قلت: لا.
قالت: أنا الدنيا.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (127,'١٢٥ - وحدثنا محمد بن علي، أخبرنا أبو إسحاق، قال: سمعت الفضيل يقول: يجاء بالدنيا يوم القيامة تتبختر في زينتها، ونضرتها فتقول: يا رب اجعلني لأحسن عبادك داراً، فيقول: لا أرضاك له، أنت لا شيء، فكوني هباء منثوراً، فتكون هباء منثوراً.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (128,'١٢٦ - حدثني محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: قال ابن عيينة: حدثت عن عبد الواحد، أنه كان يقول: ⦗٦٨⦘ ما الدنيا إن كنت لبائعها في بعض الحالات كلها بشربة على الظمأ.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (129,'١٢٧ - حدثني محمد بن علي، أخبرنا إبراهيم، قال: سمعت الفضيل، يقول: قيل: يا ابن آدم إجعل الدنيا داراً تبلغك لأثقالك، واجعل نزولك فيها استراحتك لا يحسبك كالهارب من عدوك المستريح إلى أهله في طريق مخوفة لا يجد مسالماً يقدم فيه من الراحة فتبدلت في سفره يستبقي صالح متاعه لإقامته فإن عجزت أن تكون كذلك في العمل فليكن ذلك هو الأصل، وإياك أن تكون لصاً من لصوص تلك الطريق ممن ينهون عنه، وينأون عنه، وإن آية العمى إذا أردت أن تعرف بذلك نفسك، أو غيرك فإنها لا تقف عن الهلكة، ولا تمض في الغربة فذلك أعمى القلب، وإن كان بصيراً.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (130,'١٢٨ - حدثنا الحسن بن إبراهيم، أخبرنا يحيى بن يمان، عن أشعث بن إسحاق القمي، قال: قال عيسى بن مريم: لا تطلبوا الدنيا بهلكة أنفسكم، أطلبوا الدني بترك ما فيها، عراة دخلتموها، وعراة تخرجون منها، هي اليوم همه، وغداً راحل يشغله.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (131,'١٢٩ - أخبرنا إسحاق، أخبرنا يحيى بن يمان، عن أشعث بن إسحاق، قال: قيل لعيسى بن مريم عليه السلام: لو اتخذت بيتاً؟ قال: يكفيني خلقان من كان قبلنا.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (132,'١٣٠ - حدثنا إسحاق، أخبرنا أبو أسامة، حدثني سليمان بن المغيرة، عن ثابت البناني، قال: قيل لعيسى عليه السلام: لو اتخذت حماراً تركبه لحاجتك.
قال: أنا أكرم على الله من أن يجعل لي شيئاً يشغلني به.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (133,'١٣١ - حدثنا الهيثم بن خالد البصري، أخبرنا الهيثم بن جميل، أخبرنا محمد بن مسلم، عن إبراهيم بن ميسرة، عن طاووس، قال: قال النبي صلى الله عليه وسلم: الزهد في الدنيا يريح القلب والبدن، والرغبة في الدنيا تطيل الهم والحزن.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (134,'١٣٢ - وحدثنا أبو حاتم الرازي، أخبرنا هشام بن عمار، حدثني ⦗٧٠⦘ صدقة عن عتبة بن أبي حكيم، أخبرنا أبو الدرداء الرهاوي، قال: قال رسول الله صلى الله عليه وسلم: إحذروا الدنيا فإنها أسحر من هاروت وماروت.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (135,'١٣٣ - حدثنا أبو خيثمة زهير بن حرب، أخبرنا وكيع بن الجراح، أخبرنا المسعودي، عن عمرو بن مرة، عن إبراهيم، ⦗٧١⦘ عن علقمة عن عبد الله، عن النبي صلى الله عليه وسلم قال: ما لي وللدينا إنما مثلي ومثل الدنيا كمثل راكب قال في ظل شجرة في يوم صائف راح وتركها.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (136,'١٣٤ - حدثنا عبد الله بن معاوية الجمحي، أخبرنا ثابت بن يزيد، أخبرنا هلال بن خباب، عن عكرمة، عن ابن عباس، قال: دخل عمر بن الخطاب على النبي صلى الله عليه وسلم وهو على حصير قد أثر في جنبه فقال، يا رسول الله لو اتخذت فراشاً أوثر من هذا؟ فقال: ما لي وللدنيا، وما للدنيا وما لي، والذي نفسي بيده ما مثلي ومثل الدنيا إلا كراكب سار في يوم صائف فاستظل تحت شجرة ساعةً من نهار، ثم راح وتركها.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (137,'١٣٥ - حدثني عبيد الله بن جرير العتكي، أخبرنا محمد بن أبي بكر، ⦗٧٢⦘ أخبرنا أبو عوانة، عن عبد الملك بن عمير، قال: سمعت الحسن يحدث، قال: خرج رسول الله صلى الله عليه وسلم على دابته فمر على خوص نخلة فقلب إصبعاً من أصابع يديه، فانطلق إلى أهله فوضع له سرير مرمول بخوص ووضعت تحته قطعة عباءة، ووضع تحت رأسه وسادة من أدم، محشوة ليفاً فأخبر بذلك، فمر عمر فجاء سريعاً، وفي جانب البيت أهباب، فقال: يا رسول الله أما تؤذيك هذه الريح؟ لو نحيتها، أنا أشهد أنك أكرم.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (138,'<span data-type="title" id=toc-2>الجزء الثاني</span>',NULL,'73',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (139,'أخبرتنا الشيخة نور العين لامعة ضوء الصباح بنت المبارك بن كامل الخفاف قالت: أخبرنا الشيخ الحافظ أبي سعد أحمد بن محمد بن أحمد البغدادي أنبأنا أبو العباس أحمد بن محمد الطهراني أنبأنا أبو محمد بن أحمد بن يوه أنبأنا أبو الحسن أحمد بن محمد بن اللبناني أنبأنا أبو بكر عبد الله.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (140,'١٣٦ - عن عبد الله بن صالح بن مسلم العجلي، أخبرني رجل من بني شيبان،: أن علي بن أبي طالب خطب، فقال:
الحمد لله أحمده، وأستعينه، وأومن به وأتوكل عليه وأشهد أن لا إله إلا الله وحده لا شريك له، وأن محمداً عبده ورسوله، أرسله بالهدى، ودين الحق، ليزيح به علتكم، وليوقظ به غفلتكم، واعلموا أنكم ميتون ومبعوثون من بعد الموت، وموقوفون على أعمالكم، ومجزون بها، فلا تغرنكم الحياة الدنيا، فإنها دار بالبلاء محفوفة، وبالفناء معروفة، وبالغدر موصوفة، وكل ما فيها إلى زوال، وهي بين أهلها دول وسجال لا تدوم أحوالها، ولن يسلم من شرها نزالها بينا أهلها منها في رخاء وسرور إذا هم منها في بلاء وغرور، أحوال مختلفة، وتارات متفرقة، العيش فيها مذموم، والرخاء فيها لا يدوم، وإنما أهلها فيها أغراض مستهدفة ترميهم بسهامها، وتقصمهم بحمامها، وكل حتفه فيها، وحظه منها موفور، واعلموا عباد الله أنكم وما أنتم فيه من هذه الدنيا على سبيل من قد مضى، ممن كان أطول منكم أعماراً، وأشد منكم بطشاً، وأعمر دياراً وأبعد آثاراً، فأصبحت أصواتهم هامدة، خامدة من بعد طول تقلبها، وأجسادهم منها بالية، وديارهم خالية، وآثارهم عافية، ⦗٧٤⦘ واستبدلوا بالقصور المشيدة، والسرر والنمارق الممهدة، الصخور والأحجار المسندة في القبور اللاطئة الملحدة التي قد بني بالخراب فناؤها، وشيد بالتراب بناؤها، فمحلها مقترب، وساكنها مغترب بين أهل عمار موحشين، وأهل محلة متشاغلين لا يستأنسون بالعمران، ولا يتواصلون بتواصل الجيران، والإخوان، على ما بينهم من قرب الجوار، ودنو الدار، وكيف يكون بينهم تواصل، وقد طحنهم بكلكله البلى، وأكلتهم الجنادل والثرى، فأصبحوا بعد الحياة أمواتاً، وبعد غضارة العيش رفاتاً، فجع بهم الأحباب، وسكنوا التراب، وظعنوا فليس لهم إياب، هيهات هيهات {كلا إنها كلمة هو قائلها ومن ورائهم برزخ إلى يوم يبعثون} [المؤمنون: ١٠٠] وكأن قد صرتم إلى ما صاروا إليه من البلى والوحدة في دار الموت، وإن تمننتم في ذلك المضجع وضمكم ذلك المستودع، فكيف بكم لو قد تناهت الأمور، وتبعثرت القبور، وحصل ما في الصدور، ووقفتم للتحصل بين يدي الملك الجليل، فطارت القلوب لإشفاقها من سالف الذنوب، وهتكت عنكم الحجب والأستار، وظهرت منكم العيوب والأسرار، هناك تجزى كل نفس ما كسبت، يقول الله: {ليجزي الذين أساؤوا بما عملوا ويجزي الذين أحسنوا بالحسنى} .
[النجم: ٣١] .
{ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها ووجدوا ما عملوا حاضرا ولا يظلم ربك أحداً} .
[الكهف: ٤٩] جعلنا الله وإياكم عاملين بكتابه، متبعين لأوليائه، حتى يحلنا وإياكم دار المقامة من فضله، إنه حميد مجيد.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (141,'١٣٧ - حدثني أزهر بن مروان الرقاشي، أخبرنا جعفر بن سليمان قال: سمعت مالك بن دينار، يقول: بقدر ما تفرح للدنيا كذلك تخرج حلاوة الآخرة من قلبك.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (142,'١٣٨ - وحدثني أزهر بن مروان، أخبرنا جعفر بن سليمان، قال: سمعت مالك بن دينار، يقول: قال عيسى عليه السلام للحواريين: يا معشر الحواريين كلوا خبز الشعير والماء القراح، ونبات الأرض، فإنكم لا تقومون بشكره، واعلموا أن حلاوة الدنيا مرارة الآخرة.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (143,'١٣٩ - حدثنا محمد بن معمر العجيفي، حدثني من سمع سفيان بن عيينة، يقول: والله ما أعطى الله عز وجل الدنيا من أعطاها إياه إلا اختباراً، ولا زواها من زواها عنه إلا اختباراً، وآية ذلك أن رسول الله صلى الله عليه وسلم جاع وشبعتم، ابن آدم تهيأ للجدل وتيسر لحسابك وانظر من موقفك، على من يسألك عن النقير، والقطمير والفتيل، وما هو أصغر من ذلك، وأكبر وما تغني حياة بعدها الموت.
قال: فقيل له: يا أبا محمد من يقول هذا؟ قال: ومن يحسن يقول هذا إلا الحسن.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (144,'١٤٠ - أنشدني أبو جعفر القرشي: يا عاشق الدنيا وللدنيا سمادير وسكر
اسمع لموعظة الزما ... ن فما بسمعك وقر
كم قد مضى ملك له ... نظر إلى الجلساء شزر
وله مباهاة بما ... لم يبق فيه له فخر
وتمر أزمنة بنا ... يمضي بها شهر وشهر
وتمر فينا الحادثا ... ت لها نباطي ونشر
⦗٧٦⦘
ويكون من يبني القصو ... ر يضمه من بعد قبر
والدهر فيه عجائب ... من صرفه شفع ووتر
والموت فيه على الذها ... ب بأنفس الثقلين قدر
وعوابر الدنيا تمر عليك وأنت لهن جسر
ولرب حال بين صا ... حبها وبين الموت فقر
ومن يفك لعاشق الدنيا من الشهوات أسر؟ !',NULL,'75',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (145,'١٤١ - وقال بعض الحكماء: أما يكفي أهل الدنيا ما يعانون من كثرة الفجائع، وتتابع المصائب في المال والأخوان، والنقص في القوى والأبدان.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (146,'١٤٢ - وقال بعض حكماء الشعراء: خطبت يا خاطب الدنيا مشمرة ... في ذبح أزواجها كصيد الغرانيق
كم من ذبيح لها من تحت ليلتها ... زفت إليه بمعزاف وتصفيق.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (147,'١٤٣ - وأنشدني أبو الحسن الباهلي أو غيره: يا خاطب الدنيا إلى نفسها ... تناه عن خطبتها تسلم
إن التي تخطب قتالة ... قريبة العرس من المأتم',NULL,'76',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (148,'١٤٤ - وأنشدني أبو جعفر مولى بني هاشم: وكم نائم نام في غبطة ... أتته المنية في نومته
وكم من مقيم على لذة ... دهته الحوادث في لذته
وكل جديد على ظهرها ... سيأتي الزمان على جدته',NULL,'76',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (149,'١٤٥ - حدثنا أبو بكر الصوفي، حدثني الحسين بن الربيع، قال: سمعت أبا إسحاق الفزاري، يقول: سمعت حبيبي فضيل بن عياض، يقول: ⦗٧٧⦘ خمسة من علامات الشقاء: قسوة القلب، وجمود العين، وقلة الخيار، والرغبة في الدنيا، وطول الأمل وخمسة من السعادة: اليقين في القلب والورع في الدين، والزهد في الدنيا، والحياء والعلم.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (150,'١٤٦ - كتب إلي عبد الله التيمي، قال: أخبرنا شعيب بن إبراهيم التيمي، حدثني سيف بن عمر الأسدي، عن بدر بن عثمان، عن عمه، قال: آخر خطبة خطبها عثمان في جماعة:
إن الله إنما أعطاكم الدنيا لتطلبوا بها الآخرة، ولم يعطكموها لتركنوا إليها، إن الدنيا تفنى، والآخرة تبقى، لا تبطرنكم الفانية، ولا تشغلنكم عن الباقية، آثروا ما يبقى على ما يفنى، فإن الدنيا منقطعة، وإن المصير إلى الله عز وجل واتقوا الله، فإن تقواه جنة من بأسه، ووسيلة عنده واحذروا أمر الله، والزموا جماعتكم، ولا تصيروا أحزابا {واذكروا نعمة الله عليكم إذ كنتم أعداء} إلى آخر الآيتين.
[آل عمران: ١٠٣] .',NULL,'77',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (151,'١٤٧ - حدثني علي بن الحسن بن أبي مريم، عن عبد الله بن صالح العجلي، عن معاذ الحذاء، قال: سمع الإمام علي بن أبي طالب رجلاً يسب فقال له: إنها لدار صدق لمن صدقها، ودار عافية لمن فهم عنها، ودار غنى لمن تزود منها، ومسجد أحباء الله، وهبوط وحيه، ومصلى ملائكته، ومتجر أوليائه، واكتسبوا فيها الرحمة، وربحوا فيها الجنة، فمن ذا يذم الدنيا، وقد آذنت بفراقها، ونادت بينها ونعت نفسها وأهلها، فمثلت لهم ببلائها البلاء، وشوقت بسرورها إلى السرور، فذمها قوم عند الندامة، وحمدها آخرون حدثتهم فصدقوا، وذكرتهم فذكروا، فأيها المعتل بالدنيا، المغتر بغرورها متى استهوتك الدنيا، بل متى غرتك؟ أبمضاجع آبائك من الثرى؟ أم بمصارع أمهاتك من البلى؟ كم قد قلبت ⦗٧٨⦘ بكفك، ومرضت بيدك، تطلب له الشفاء، وتسأل له الأطباء؟ لم تظفر بحاجتك ولم تسعف بطلبتك، قد مثلت لك الدنيا بمصرعه مصرعك غداً، يوم لا يغني عنك بكاؤك، ولا ينفعك أحباؤك.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (152,'١٤٨ - حدثني علي بن أبي مريم، عن بعض أشياخه، قال: قال عبد الواحد بن زيد: يا ويح من يطلبون الدنيا! أما يستحيون من طلب الدنيا، وقد ضمن لهم الرزق، فكفى الراغب منهم الطلب، وأمروا بالطاعة فهم يطلبون منها ما إن فاتهم سلموا، وإن وجدوه ندموا، وهل الخير إلا خير الآخرة، الخير في الدنيا معدوم، والخفض فيها مذموم، والمقصر فيها عن حظه ملوم.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (153,'١٤٩ - وحدثني علي بن أبي مريم عن محمد بن الحسين، حدثني عمار بن عثمان، حدثني حصين بن القاسم، قال: سمعت عبد الواحد بن زيد، يقول: بالله لحرص المرء على الدنيا أخوف عليه عندي من أعدى أعدائه له.
قال: وسمعته يقول:
يا إخواتاه لا تغبطوا حريصاً على ثروة، ولا سعة في مكسب، ولا مال، وانظروا إليه بعين المقت له في فعله، وبعين الرحمة له في اشتغاله اليوم بما يرد به غداً في المعاد، قال: ثم يبكي ويقول:
الحرص حرصان: فحرص فاجع، وحرص نافع، فأما النافع فحرص المرء على طاعة الله.
وأما الفاجع فحرص المرء على الدنيا، متعذب مشغول لا يسر، ولا يلذ بجمعه لشغله، ولا يفرغ من محبته الدنيا لآخرته كداً كداً لما يفنى وغفلته عما يدوم ويبقى.
قال: ثم يبكي.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (154,'١٥٠ - وأنشدني ابن أبي مريم.
: لا تغبطن أخا حرص على سعة ... وانظر إليه بعين الماقت القالي
⦗٧٩⦘
إن الحريص لمشغول لشقوته عن ... السرور بما يحوي من المال',NULL,'78',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (155,'١٥١ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله، أخبرنا الأسود بن شيبان السدوسي، قال: قال الفضل بن ثور بن شقيق بن ثور وكان تهمه نفسه قلت للحسن: يا أبا سعيد رجلان طلبا أحدهما الدنيا فأصابها، فوصل فيها رحمه، وقدم فيها لنفسه، وجانب الآخر الدنيا؟ فقال: أحبهما إلي الذي جانب الدنيا، فأعاد عليه، فأعدا عليه مثله.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (156,'١٥٢ - وحدثني أنبأنا عبدان، أنبأنا عبد الله، أخبرنا حيوة بن شريح، أخبرني أبو هاني الخولاني أنه سمع عمرو بن حريث وغيره يقولون: إنما نزلت هذه الآية في أصحاب الصفة {ولو بسط الله الرزق لعباده لبغوا في الأرض} [الشورى: ٢٧] وذلك أنهم قالوا: لو أن لنا فتمنوا الدنيا.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (157,'١٥٣ - حدثنا إسحاق بن إسماعيل، أخبرنا أبو معاوية، عن ⦗٨٠⦘ الأعمش عن شمر بن عطية، عن المغيرة بن سعد بن الأخرم، عن أبيه، عن عبد الله، قال: قال رسول الله صلى الله عليه وسلم: لا تتخذوا الضيعة وترغبوا في الدنيا.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (158,'١٥٤ - حدثني عبد الرحمن بن صالح، أخبرنا الحسين بن علي الجعفي، عن شيخ من أهل البصرة، عن يزيد بن ميسرة الحمصي، وكان قد قرأ الكتب قال: أجد فيما أنزل أيحزن عبدي أن أقبض عنه الدنيا، وذلك أقرب له مني، أو يفرح عبدي أن أبسط له الدنيا، وذلك أبعد له مني ثم قرأ:
{أيحسبون أنما نمدهم به من مال وبنين * نسارع لهم في الخيرات بل لا يشعرون} [المؤمنون: ٥٥، ٥٦] .',NULL,'80',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (159,'١٥٥ - حدثني محمد بن ناصح، أخبرنا بقية بن الوليد، عن محمد بن مرة التستري، قال: قال عمر بن الخطاب:
⦗٨١⦘
الزهد في الدنيا راحة القلب والبدن.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (160,'١٥٦ - حدثني الحسن بن محبوب، أخبرنا أبو توبة الربيع بن نافع، حدثنا أبو ربيعة، عن أبيه، عن جده، قال: كتب عمر بن عبد العزيز إلى بعض عماله:
أما بعد: فكأن العباد قد عادوا إلى الله فينبئهم بما عملوا، ليجزي الذين أساءوا بما عملوا، ويجزي الذين أحسنوا بالحسنى، فإنه لا معقب لحكمه، ولا ينازع في أمره، ولا يقاطع في حقه الذي استحفظه عباده، وأوصاهم به وإني أوصيك بتقوى الله، وأحثك على الشكر فيما عندك من نعمة، وآتاك من كرامته، فإن نعمه يمدها شكره، ويقطعها كفره، وأكثر ذكر الموت الذي لا تدري متى يغشاك، ولا مناص، ولا فوت وأكثر ذكر يوم القيامة، وشدته فإن ذلك يدعوك إلى الزهادة فيما زهدت فيه، والرغبة فيما رغبت فيه، ثم كن مما أوتيت من الدنيا على وجل، فإن من لا يحذر ذلك، ولا يتخوفه توشك الصرعة أن تدركه في الغفلة، وأكثر النظر في عملك في دنياك بالذي أمرت به، ثم اقتصر عليه، فإن فيه لعمري شغلاً عن دنياك، ولن تدرك العلم حتى تؤثره على الجهل، ولا الحق حتى تذر الباطل، فنسأل الله لنا ولك حسن معونته، وأن يدفع عنا وعنك بأحسن دفاعه برحمته.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (161,'١٥٧ - وحدثني الحسن بن محبوب، أخبرنا الفياض بن إسحاق أبو يزيد، أخبرنا فضيل بن عياض، عن عطاء بن السائب، قال: قال أبو عبد الرحمن السلمي: ⦗٨٢⦘ نزلنا وبيننا وبين المدائن فرسخ فأخذ أبي بيدي، فذهب بي إلى الجمعة، فإذا حذيفة يخطب فقال:
ألا إن الساعة قد اقتربت، وإن القمر قد انشق وإن الدنيا قد آذنت بفراق، وإن المضمار اليوم، وغداً السباق.
فقلت: يا أبتاه غداً يستبق الناس؟ فقال: يا بني ما أجهلك، إنما يعني العمل، فلما كانت الجمعة الثانية، قال: مثلها، وإن الغاية النار، والسابق من سبق إلى الجنة.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (162,'١٥٨ - حدثني محمد بن يحيى بن أبي حاتم الأزدي، أخبرنا سعد بن يونس، عن أبي عمرو الشيباني، عن عمران بن عبد الحميد، عن هشام، عن الحسن، قال: يحشر الناس كلهم عراة ما خلا أهل الزهد.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (163,'١٥٩ - وحدثني محمد بن يحيى، حدثني جعفر بن أبي جعفر، قال: كتب إبراهيم بن أدهم إلى أخ له، فجاء في كتابه: أرفض ياأخي حب الدنيا فإن حب الدنيا يعمي ويصم.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (164,'١٦٠ - حدثني أبو الخطاب زياد بن يحيى الحساني، حدثني عبد الله بن ⦗٨٣⦘ بكر السهمي، حدثني ابن لمحمد بن حصين: أن الحسن بن أبي الحسن مر على مجلس لثقيف فقالوا له: يا أبا سعيد لو وعظتنا بكلمات لعل الله أن ينفعنا بهن، فتكلم وهو قائم فقال: إن ربنا لا شريك له، جعل الدنيا دار مرحلة، وجعل الخير والشر فيها فتنة لأهلها ليبلوهم أيهم أحسن عملاً، فهم يتقلبون فيها لسعي مختلف في مدة من آجال منقطعة، تجري عليهم فيها أرزاق، يأكلون منها ما صحبوها، ويتركونها عما قليل لمن بعدهم، كما ورثوها عمن كان قبلهم، كذلك حتى تلفظ الدنيا أهلها، وتبلغ مداها، وتفنوا كما فنوا، وجعل الآخرة دار الحيوان في جنة ونار، نزل بكم الخير والشر من قضائه، الخير من الشر بعيد، والشر من الخير بعيد، فنسأل الذي خلقنا لما شاء أن يجعل متقلبنا ومتقلبكم إلى داره السلام.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (165,'١٦١ - حدثني هارون بن سفيان، حدثني عباد بن موسى أبو عقبة البصري، أخبرنا محمد بن مسلم الطائفي، عن عمرو بن دينار، عن عبيد بن عمير، قال: الدنيا أمد، والآخرة أبد.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (166,'١٦٢ - حدثني عبد الرحمن بن صالح، حدثني الحكم بن يعلى، قال: قال الحسن البصري: ليس من حبك الدنيا طلبك ما يصلحك فيها، ومن زهدك فيها ترك الحاجة يسدها عنك تركها، من أحب الدنيا وسرته ذهب خوف الآخرة من قلبه.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (167,'١٦٣ - وحدثني عبد الرحمن بن صالح، أخبرنا المحاربي، عن سفيان، قال: بلغنا أن لقمان قال لابنه: يا بني إن الدنيا بحر عميق يغرق فيه ناس كثير، فلتكن سفينتك فيها تقوى الله، وحشوها الإيمان، وشراعها التوكل على الله، لعلك تنجو، وما أراك بناج.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (168,'١٦٤ - قال سليمان بن يزيد العدوي: ومازالت الدنيا يحور نعيمها ... وتصبح بالأمر العظيم تمخض
محلة أضياف ومنزل غربة ... تهافت من حافاتها وتنفض',NULL,'84',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (169,'١٦٥ - وقال سليمان بن يزيد العدوي: أرى الناس أضيافاً أناخوا ... بغربة تقلبهم أيامهم وتقلبوا
بدار غرور حلوة يرغثونها ... وقد عاينوا منها الزوال وجربوا
تسرهم طوراً وطوراً تذيقهم ... مضيق مكاوي حرها يتقلب
يذمون دينا لا يريحون درها ... ولم أر كالدنيا تذم وتحلب
لها درة تفيء الحليم وتحتها ... من الموت سم مجهز حين يشرب
وقد خثر ذات الجميل لا در درها ... فأصبح في جد وأصبح يلعب
وكلهم حيران يكذب قوله بفعل ... وخير القيل ما لا يكذب',NULL,'84',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (170,'١٦٦ - وقال بعض الحكماء: يا معشر أبناء الدنيا لكم في الظاهر اسم الغنى، ولأهل التقلل نفس هذا المعنى، حرمتم التفكه بما حوته أيديكم لفادح التعب، وعوضتم منه خوف نزول الفجائع به، وارتقاب وصول الآفات إليه، خدعتم ومالك المقادير عن حظكم، دأبت الدنيا أن تسوغكم حلاوتها، وما استدر لكم من ضرعها حتى وكلتم بطلب سواه لتمتعكم مما حصل منها لكم، وتصدكم عن التمتع به بأشغالكم، بمستأنف تحمدون فيه أنفسكم مما يعز مطلبه عليكم، وتبذلون فيه راحتكم، فإن وصلتم إليه لحق بالأول من المدخر، وأنشأت لكم وطراً في غرة كذلك أنتم وهي ما صحبتموها بالرغبة منكم فيها.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (171,'١٦٧ - حدثنا إسحاق بن إسماعيل، أخبرنا يحيى بن أبي بكير، أخبرنا عبد الله بن الفضل التميمي، قال: آخر خطبة خطبها عمر بن عبد العزيز أن صعد المنبر فحمد الله وأثنى عليه ثم قال: أما بعد فإن ما في أيديكم أسلاب الهالكين، وسيتركها الباقون كما تركها الماضون، ألا ترون أنكم في كل يوم وليلة تشيعون غادياً أو رائحاً إلى الله وتضعونه في صدع من الأرض، ثم في بطن صدع غير ممهد، ولا موسد، قد خلع الأسلاب، وفارق الأحباب وأسكن التراب، وواجه الحساب، فقيراً إلى ما قدم أمامه، غنياً عما ترك بعده، أما والله إني لأقول لكم هذا، وما أعرف من أحد من الناس مثل ما أعرف من نفسي.
قال: ثم قال بطرف ثوبه على عينه، فبكى ثم نزل فما خرج حتى أخرج إلى حفرته.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (172,'١٦٨ - حدثني عمر بن أبي الحارث الهمداني، أخبرنا محبوب بن عبد الله ⦗٨٦⦘ النميري، أخبرنا عبيد الله بن أبي المغيرة القرشي، قال: كتب إلي الفضل بن عيسى: أما بعد فإن الدار التي أصبحنا فيها دار بالبلاء محفوفة، وبالفناء موصوفة، كل ما فيها إلى زوال ونفاد، بينما أهلها في رخاء وسرور، إذا صيرتم في وعثاء ووعور، أحوالها مختلفة، وطبقاتها منصرفة، يضربون ببلائها، ويمتحنون برخائها، العيش فيها مذموم، والسرور فيها لا يدوم، وكيف يدوم عيش تغيره الآفات، وتنويه الفجيعات، وتفجع فيه الرزايا، وتسوق أهله المنايا، إنما هم بها أغراض مستهدفة، والحتوف لها مستشرفة، ترميهم بسهامها، وتغشاهم بحمامها، لا بد من الورود بمشارعه، والمعاينة لفظائعه، أمر قد سبق من الله في قضائه، وعزم عليه في إمضائه، فليس منه مذهب، ولا عنه مهرب، ألا فأخبث بدار يقلص ظلها ويفنى أهلها، إنما هم بها سفر نازلون، وأهل ظعن شاخصون، كأن قد انقلبت بهم الحال، وتنادوا بالارتحال، فأصبحت منهم قفاراً، قد انهارت دعائمها، وتنكرت معالمها، واستبدلوا بها القبور الموحشة، التي استبطنت بالخارب، وأسست بالتراب، فمحلها مقترب، وساكنها مغترب، بين أهلها موحشين، وذوي محلة متشاسعين، لا يستأنسون بالعمران، ولا يتواصلون بتواصل الإخوان، ولا يتزاورون تزاور الجيران، قد اقتربوا في المنازل، وتشاغلوا عن التواصل، فلم أر مثلهم جيران محلة، لا يتزاورون على ما بينهم من الجوار، وتقارب الديار، وأنى ذلك منهم، وقد طحنهم بكلكله البلى، وأكلتهم الجنادل والثرى، وصاروا بعد الحياة رفاتاً، قد فجع بهم الأحباب، وارتهنوا فليس لهم إياب، وكأن قد صرنا إلى ما إليه صاروا، فمرتهن في ذلك المضجع، ويضمنا ذلك المستودع، نؤخذ بالقهر والاعتسار، وليس ينفع منه شفق الحذار، والسلام.
قال: قلت له: فأي شيء كتبت إليه؟ قال: لم أقدر له على الجواب.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (173,'١٦٩ - حدثني أبو عبد الله التميمي، حدثني شريح العابد، ومحمد بن عبد الله ⦗٨٧⦘ الشيباني، قالا: سمعنا خيثم بن جحشة العجلي أبا بكر العابد يقول:
يا خاطب الدنيا على نفسها ... إن لها في كل يوم خليل
ما أقتل الدنيا لخطابها ... تقتلهم قدماً قتيلاً قتيل
تستنكح البعل وقد وطنت ... في موضع آخر منه بديل
إني لمغتر وإن البلى يعمل ... في جسمي قليلاً قليل
تزودوا للموت زاداً فقد ... نادى مناديه الرحيل الرحيل',NULL,'86',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (174,'١٧٠ - حدثنا سعيد بن سليمان، عن سليمان بن المغيرة، عن ثابت، قال: لما بعث النبي صلى الله عليه وسلم قال إبليس لشياطينه: لقد حدث أمر فانظروا ما هو فانطلقوا، ثم جاءوه فقالوا: ما ندري.
قال إبليس: أنا آتيكم بالخبر فذهب، فقال: بعث محمد عليه السلام، قال: فجعل يرسل شياطينه إلى أصحاب النبي، صلى الله عليه وسلم، فيجيئون بصحفهم ليس فيها شيء، فقال: ما لكم؟ أما تصيبون منهم شيئاً؟ قالوا: ما صحبنا قوماً قط مثل هؤلاء، نصيب منهم، ثم يقومون إلى صلاتهم فيمحى ذلك، قال إبليس: رويداً لهم، عسى أن تفتح لهم الدنيا، هنالك تصيبهم حاجتكم منهم.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (175,'١٧١ - حدثنا أبو عبد الرحمن القرشي، وعبد الرحمن بن صالح الأزدي، قالا: أخبرنا المحاربي، عن موسى الجهني، قال: سمعت عون بن عبد الله بن عتبة، يقول: ويحي كيف تشتد حاجتي إلى الدنيا، وليست بداري؟ أم كيف أجمع لها ⦗٨٨⦘ وفي غيرها قراري وخلدي؟ أم كيف تعظم رغبتي فيها، والقليل منها يكفيني؟ أم كيف آمن فيها، ولا يدوم فيها حالي؟ أم كيف يشتد حرصي عليها ولا ينفعني ما تركت منها بعدي؟ أم كيف أوثرها، وقد ضرت من آثرها قبلي، أم كيف لا أبادر بعملي من قبل أن تنصرم مدتي؟ أم كيف لا أعرض نفسي لما لا يقوى هوائي؟ ، أم كيف يشتد عجبي بها، وهي مزايلتي ومنقطعة عني؟',NULL,'87',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (176,'١٧٢ - حدثني الحسن بن حماد الضبي، أخبرنا حسين الجعفي، عن فضيل بن عياض، عن سفيان الثوري، قال: كان من دعائهم: اللهم زهدنا في الدنيا ووسع علينا منها ولا تزوها عنا فترغبنا فيها.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (177,'١٧٣ - حدثني محمد بن قدامة الجوهري، قال: قال إبراهيم بن أدهم: الأجر كريم، يغضب على الدنيا.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (178,'١٧٤ - حدثنا عبد الرحمن بن صالح، أخبرنا المحاربي، عن ⦗٨٩⦘ مبارك، عن الحسن قال: إن أصحاب رسول الله، صلى الله عليه وسلم، كانوا أكياساً، عملوا صالحاً، وأكلوا طيباً، وقدموا فضلاً، ولم ينافسوا أهل الدنيا في دنياهم، ولم ينافسوهم في عزها، ولم يجزعوا لذلها، أخذوا صفوها، وتركوا كدرها، والله ما تعاظم في أنفسهم حسنة عملوا، ولا تصغر في أنفسهم سيئة.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (179,'١٧٥ - حدثنا عبد الرحمن بن صالح، حدثنا عبد الله بن إدريس، عن حصين بن عبد الرحمن، قال: قال: إبراهيم التيمي: إن من كان قبلكم كانت الدنيا مقبلة عليهم، وهم يفرون منها، ولهم من العزم ما لهم، وإنكم تطلبون الدنيا، وهي مدبرة عنكم، ولكم من الأحداث ما لكم، فقيسوا أمركم وأمرهم.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (180,'١٧٦ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله، أنبأنا سفيان، عن سليمان عن مالك بن الحارث، عن عبد الرحمن بن ⦗٩٠⦘ يزيد، عن ابن مسعود قال: أنتم أطول جهاداً، وأكثر صلاة من أصحاب رسول الله صلى الله عليه وسلم، وكانوا خيراً منكم، قالوا: ولم؟ قال: كانوا أزهد منكم في الدنيا، وأرغب منكم في الآخرة.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (181,'١٧٧ - حدثنا أبو كريب، أخبرنا ابن أبي زائدة، عن مجالد بن سعيد، عن الشعبي قال: قال شريح: تهون على الدنيا الملامة، كن حريصاً على استخلاصها من تلوثها.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (182,'١٧٨ - أنشدني أبو إسحاق القرشي التيمي: ننافس في الدنيا ونحن نعيبها ... لقد حذرتناها لعمري خطوبها
وما تحسب الأيام تنقص مدة ... على أنها فينا سريع دبيبها
كأني برهط يحملون جنازتي ... إلى حفرتي يحثى علي كثيبها
فكم ثم من مسترجع متوجع ... ونائحة يعلو علي نحيبها
وباكية تبكي علي وإنني ... لفي غفلة من صوتها ما أجيبها
أيا هاذم اللذات ما منك مهرب ... تحاذر نفس منك ما سيصيبها
وإني لممن يكره الموت والبلى ... ويعجبه روح الحياة وطيبها ⦗٩١⦘
فحتى متى حتى متى وإلى متى ... يدوم طلوع الشمس بي وغروبها؟!
رأيت المنايا قسمت بين أنفس ... ونفسي سيأتي بعدهن نصيبها',NULL,'90',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (183,'١٧٩ - حدثنا أبو كريب، أخبرنا المحاربي، عن بكر بن خنيس، عن شعيب بن سليمان، أو غيره، قال: إن ذا القرنين لقي ملكاً من الملائكة، فقال: علمني علماً أزداد به إيماناً ويقيناً، قال: إنك لا تطيق ذلك.
قال: لعل الله يطوقني.
قال: لا تغتم لغد، واعمل في اليوم لغد، وإن أتاك الله من الدنيا سلطاناً أو مالاً فلا تفرح به، وإن صرف عنك فلا تأس عليه، وكن حسن الظن بالله عز وجل، وضع يدك على قلبك فما أحببت أن تصنع بنفسك فاصنعه بأخيك، ولا تغضب فإن الشيطان أقدر ما يكون على ابن آدم حين يغضب، ورد الغضب بالكظم، وسكنه بالتؤدة، وإياك والعجلة، فإنك إذا عجلت أخطأت حظك، وكن سهلاً ليناً للقريب والبعيد، ولا تكن جباراً عنيداً.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (184,'١٨٠ - وحدثنا أبو كريب، حدثني المحاربي، عن عاصم الأحول، عن الشعبي، عن مسروق في قول السائل: أين الزاهدون في الدنيا، والراغبون في الآخرة؟ .
قال مسروق: ما كنت أفضل عليهما شيئاً',NULL,'91',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (185,'١٨١ - وحدثنا أبو كريب، أخبرنا المحاربي، عن عاصم الأحول، قال: بلغني أن ابن عمر سمع رجلاً يقول: أين الزاهدون في الدنيا، والراغبون في الآخرة؟ فأراه قبر النبي صلى الله عليه وسلم، وأبي بكر وعمر، وقال: عن هؤلاء تسأل.
.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (186,'١٨٢ - حدثني محمد بن العباس بن محمد، أخبرنا الحسين بن محمد، أخبرنا أبو سليمان النصيبي، عن أبي إسحاق السبيعي، عن زرعة، عن عائشة قالت: قال رسول الله صلى الله عليه وسلم: الدنيا دار من لا دار له، ومال من لا مال له، ولها يجمع من لا عقل له.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (187,'١٨٣ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله، أنبأنا عبد الرحمن المسعودي، عن القاسم بن عبد الرحمن، قال: قال عبد الله بن مسعود: ⦗٩٣⦘ لوددت أني من الدنيا فرد كالراكب الغادي الرائح',NULL,'92',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (188,'١٨٤ - وحدثني حمزة، أنبأنا عبدان، أنبأنا عبد الله، أنبأنا محمد بن سليم، قال: قال الحسن: ما من مسلم يرزق رزق يوم بيوم، ولا يعلم أنه قد خير له إلا عاجز، أو قال: غبي الرأي.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (189,'١٨٥ - وحدثني حمزة، أنبأنا عبدان، أنبأنا عبد الله، أنبأنا ثور بن يزيد، عن خالد بن معدان، قال: قال أبو الدرداء: الدنيا ملعونة، ملعون ما فيها إلا ذكر الله، وما أوى إليه.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (190,'١٨٦ - وحدثني حمزة، أنبأنا عبدان، أنبأنا عبد الله، قال: أنبأنا ⦗٩٤⦘ بعض أهل البصرة أن مطرف بن الشخير ماتت امرأته، أو بعض أهله، فقال أناس من إخوانه: انطلقوا بنا إلى أخيكم مطرف، حتى لا يخلو به الشيطان فيدرك بعض حاجته منه، فأتوه فخرج عليهم دهيناً في هيئة حسنة، فقالوا: خشينا شيئاً، فنرجو أن يكون الله قد عصمك منه، وأخبروه بالذي قالوا: فقال مطرف:
لو كانت لي الدنيا كلها، فسئلتها بشربة يوم القيامة لافتديت بها.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (191,'١٨٧ - أنشدني أحمد بن موسى الثقفي: دع الدنيا لمفتتن ... وإن أبدت محاسنها
وخذ منها بأيسرها ... وإن بسطت خزائنها
فإن الدار دار بلى ... حيال الموت آمنها
وقد قلبت لك الأيا ... م ظاهرها وباطنها
وحسبك من صفات الوا ... صفين بأن تعاينها
أليس جديدها يبلى ... ويفني الموت ساكنها',NULL,'94',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (192,'١٨٨ - أنشدني أبو نصر المدني: هذه الدار ملكها قبلنا ... عصبة بادوا وخلوها لنا
فملكناها كما قد ملكوا ... وسيملكها أناس بعدنا
ثم تفنيهم وتفنى بعدهم ... ليست الدنيا لحي وطنا
عجباً للدار كم تخدعنا ... حسرة يا حسرة يا حزنا',NULL,'94',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (193,'١٨٩ - حدثني أبو سليمان القرشي، حدثني داود بن بلال - وكان ينزل في بني زهران - قال: سمعت ميموناً المزني، قال: سمعت الحسن يتمثل: الدنيا تعذب من هونها ... وتورث قلبه حزناً وداء
فإن أبغضتها نجيت منها ... وإن أحببتها تلقى البلاء',NULL,'94',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (194,'١٩٠ - حدثنا خلف بن هشام البزار، قال: بلغنا أن سفيان الثوري كان يتمثل: أرى أشقياء الناس لا يسأمونها ... على أنهم فيها عراة وجوع
أراها وإن كانت قليلاً كأنها ... سحابة صيف عن قليل تقشع
كركب قضوا حاجاتهم وترحلوا ... طريقهم بادي العلامة مهيع',NULL,'95',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (195,'١٩١ - قال بعض الحكماء: وذكر الدنيا فقال: كم من يوم لي قد صحت سماؤه، وامتد علي ظله، تمدني ساعاته بالمنى، وتضحك لي عن كل ما أهوى في رفاهة ناضرة، وحال تدفق بالغبطة، أرتع في ظل قريب، مجناه، ينبسق إلي فيه الموافقة، وتلاحظني تباشير الأحبة، تجوز معاني الوصف، وينحسر عنه الطرف، حتى إذا اتصلت أسباب سروره بي، نفست الدنيا به علي فسعت بالتشتيت إلى الفتنة، وبالنقص إلى مدته، فكسفت بمحبته كسوفاً، وأرهقت نضرتنا الفراق، وقطعتنا فرقاً في الآفاق بعد إذ كنا كالأعضاء المؤتلفة، والأغصان الندية المنقطعة، فأصبح ربعنا المألوف، قد محا أعلامه الزمان، وأبلت أسباب العهد به الأيام، فلقلبي وجوب عند ذكرهم، يكاد ينفطر جزعاً مما يعاين من فقدهم، ويقاسي من بعدهم، ونظراتي تطرد في الجفون من حرارات الكمد، وأوجاع كلوم لا تندمل، فما لي للمقام في مراتع الأشجان، ومرابض المنايا، وأوعية الرزايا.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (196,'١٩٢ - حدثني أبو الحسن الخزاعي، حدثني رجل من ولد شبيب بن شيبة، رحل عن البصرة عشرين سنة، ثم قدمها فأتى مجلسه فلم ير أحداً من جلسائه فقال: يا مجلس القوم الذين بهم تفرقت المنازل، ⦗٩٦⦘
أصبحت بعد عمارة قفراً، يخرقك الشمائل
فلئن رأيتك موحشاً فمتى أراك وأنت آهل؟',NULL,'95',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (197,'١٩٣ - حدثني أبو محمد التميمي البصري، قال: قال سفيان بن عيينة: كان ابن شبرمة غاب عن الكوفة، ثم قدمها، وقد كان يخرج مع أصحاب له إلى ظل جبل فيتمتعون بظله، ويتحدثون في فيئه فلما قدمها رأى الظل باقياً، وفقد من كان يؤنسه، فقال متمثلاً:
وأجهشت للتوباد حين رأيته ... ونادى بأعلى صوته فدعاني
فقلت له أين الذين عهدتهم ... بجذعك في عيش وحسن زماني
فقال مضوا واستودعوني بلادهم ... ومن الذي يبقى على الحدثان',NULL,'96',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (198,'١٩٤ - أنشدني سعيد بن محمد العامري قوله: لقد نغص الدنيا على حب أهلها ... لها أنها محفوفة بالمصائب
ولو لم تكن فيها المصائب ما ارتضى ... محبتها في حالة ذو تجارب
ألم تره تغذو بنيها بدرها ... وتصرعهم آفاتها بالعجائب
وما الخير فيها حين تسعف أهلها ... ولا الشر إلا كالبروق الكواذب
يزولان عمن كان فيها بنعمة ... وبؤس كما زالت صدور الكواكب',NULL,'96',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (199,'١٩٥ - حدثني محمد بن إسحاق الثقفي، قال: قال بعض الحكماء: كيف يفرح بالدنيا من يومه يهدم شهره، وشهره سنته، وسنته عمره، وكيف يفرح من يقوده عمره إلى أجله، وتقوده حياته إلى موته؟ ! .',NULL,'96',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (200,'١٩٦ - وحدثني محمد بن إيحاق، قال: قال بعض الحكماء: ⦗٩٧⦘ الأيام سهام، والناس أغراض، والدهر يرميك كل يوم بسهامه، ويستخدمك بلياليه، وأيامه، حتى تستغرق جميع أجزائك، فكم بقاء سلامتك مع وقوع الأيام بك، وسرعة الليالي في بدنك، لو كشف لك عما أحدثت الأيام فيك من النقص، وما هي عليه من هدم ما بقي إلا استوحشت من كل يوم يأتي عليك واستثقلت ممر الساعات بك، ولكن تدبير الله فوق الاعتبار، وبالسلو عن غوائل الدنيا، وجد طعم لذاها، وإنها لأمر من العلقم، إذا عجمها الحكيم، وأقل من كل شيء يسمى القليل، وقد أعيت الواصف لعيوبها بظاهر أفعالها، وما تأتي به من العجائب أكثر مما يحيط به الواعظ، نستوهب الله رشداً إلى الصواب.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (201,'١٩٧ - وحدثني محمد بن إسحاق، قال: قيل لبعض الحكماء: صف لنا قدر الدنيا، ومدة البقاء؟ فقال: الدنيا وقتك الذي يرجع إليك فيه طرفك، لأن ما مضى عنك فقد فاتك إدراكه، وما لم يأت فلا علم لك به، الدهر يوم مقبل تنعاه ليلته، وتطويه ساعته، وأحداثه تتصل في الإنسان بالتغيير والنقصان، والدهر موكل بتشتيت الجماعات وانخرام الشمل، وتنقل الدول، والأمل طويل، والعمر قصير، وإلى الله تصير الأمور.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (202,'١٩٨ - أنشدني محمود الوراق قوله: المرء دنيا نفسه ... فإذا انقضى فقد انقضت
تفنى له بفنائه ... وتعود فيمن حصلت
ما خير مرضعة بكأ ... س الموت تفطم من غدت
بينما قرب صلاحه ... أفسدت ما أصلحت',NULL,'97',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (203,'١٩٩ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد ⦗٩٨⦘ الله بن المبارك، أخبرني معمر، ويونس، عن الزهري، عن عروة بن الزبير، أنه أخبره، أن المسور بن مخرمة، أخبره أن عمرو بن عوف وهو حليف بني عامر بن لؤي، وكان شهد بدراً مع رسول الله صلى الله عليه وسلم، أخبره: أن رسول الله صلى الله عليه وسلم، بعث أبا عبيدة بن الجراح فجاءه بمال من البحرين، فسمعت الأنصار بقدوم أبي عبيدة بن الجراح فوفوا صلاة الفجر مع رسول الله صلى الله عليه وسلم، فلما صلى رسول الله صلى الله عليه وسلم، وانصرف فتعرضوا له فتبسم رسول لله صلى الله عليه وسلم، حين رآهم ثم قال:
أظنكم سمعتم أن أبا عبيدة قدم بشيء؟ قالوا: أجل يا رسول الله.
قال: فأبشروا وأملوا ما يسركم، فوالله ما الفقر أخشى عليكم، ولكني أخشى عليكم بأن تبسط الدنيا عليكم كما بسطت على من كان قبلكم، فتنافسوها كما تنافسوها، وتهلككم كما أهلكتهم؟ .',NULL,'97',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (204,'٢٠٠ - وحدثني حمزة، أنبأنا عبدان، أنبأنا عبد الله، أنبأنا ابن لهيعة، حدثني يزيد بن أبي حبيب: أن أبا الخير حدثهم، أن عقبة بن عامر الجهني، حدثهم أن رسول الله صلى الله عليه وسلم، صلى على قتلى أحد بعد ثماني سنين كالمودع للأحياء، والأموات، ثم طلع المنبر فقال:
إني بين أيديكم فرط، وأنا عليكم شهيد، وإن موعدكم الحوض، وإني لأنظر إليه وأنا في مقامي هذا، وإني لست أخشى عليكم أن تشركوا بعدي، ولكني أخاف عليكم الدنيا أن تنافسوها.
قال عقبة: فكانت آخر نظرة نظرتها إلى رسول الله صلى الله عليه وسلم.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (205,'٢٠١ - وحدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أخبرنا عبد الله، أنبأنا يونس بن يزيد، عن الزهري، أخبرني إبراهيم بن عبد الرحمن بن عوف: أنه قدم وافداً على معاوية في خلافته، قال: ⦗١٠٠⦘ فدخلت المقصورة فسلمت على مجلس من أهل الشام، وجلست بين أظهرهم فقال لي رجل منهم: من أنت يا فتى؟ قلت: أنا إبراهيم بن عبد الرحمن بن عوف قال: يرحم الله أباك، أخبرني فلان لرجل قد سماه أنه قال:
والله لألحقن بأصحاب رسول الله صلى الله عليه وسلم، فلأحدثن بهم عهداً ولأكلمنهم، فقدمت المدينة في خلافة عثمان فلقيتهم إلا عبد الرحمن بن عوف، أخبرت أنه بأرض له بالجرف، فركبت إليه حتى جئته فإذا هو واضع رداءه يحول الماء بمسحاة في يده فلما رآني استحيى مني، فألقى المسحاة وأخذ رداءه، فسلمت عليه، وقلت له: قد جئت لأمر، وقد رأيت أعجب منه، هل جاءهم إلا ما جاءنا، وهل علمتم إلا ما قد علمنا، قال عبد الرحمن: لم يأتنا إلا ما جاءكم، ولم نعلم إلا ما قد علمتم.
قال: قلت: فما لنا نزهد في الدنيا، وترغبون، ونخف في الجهاد وتتثاقلون، وأنتم سلفنا، وخيارنا وأصحاب نبينا صلى الله عليه وسلم، فقال عبد الرحمن: لم يأتنا إلا ما قد جاءكم، ولم نعلم إلا ما قد علمتم، ولكنا بلينا بالضراء فصبرنا، وبلينا بالسراء فلم نصبر؟ .',NULL,'99',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (206,'٢٠٢ - وحدثني حمزة أنبأنا عبدان أنبأنا عبد الله أنبأنا يونس بن يزيد عن الزهري قال: بلغنا أن عبد الله بن السعدي كان يحدث، وهو رجل من بني عامر بن لؤي، وكان من أصحاب رسول الله صلى الله عليه وسلم، قال:
بينا أنا نائم أوفيت على جبل، فبينا أنا عليه طلعت لي ثلة من هذه الأمة قد سدت الأفق، حتى إذا دنوا مني دفعت عليهم الشعاب، بكل زهرة من الدنيا فمروا، ولم يلتفت إليها منهم راكب، فلما جاوزوها قلصت الشعاب بما فيها، فلبثت ما شاء الله، ثم طلعت علي ثلة مثلها، حتى إذا بلغوا مبلغ الثلة الأولى دفعت عنهم الشعاب ⦗١٠١⦘ بكل زهرة من الدنيا، فالآخذ، والتارك، وهم على ظهر الشعاب، حتى إذا جاوزوها قلصت الشعاب بما فيها، فلبثت ما شاء الله، ثم طلعت الثالثة حتى بلغوا مبلغ الثلتين دفعت إليهم الشعاب بكل زهرة من الدنيا، فأناخ أول راكب منهم فلم بجاوزه راكب فنزلوا يهتالون من الدنيا، فعهدي بالقوم، وهم يهتالون وقد ذهبت الركاب.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (207,'٢٠٣ - حدثني القاسم بن هاشم، أخبرنا عبد العزيز القرشي، أخبرنا علي بن الحزور، عن أبي مريم، قال: سمعت عمار بن ياسر، قال: قال رسول الله صلى الله عليه وسلم:
ما عبد الله بشيء أفضل من الزهد في الدنيا.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (208,'٢٠٤ - أخبرنا إسحاق بن إسماعيل، أخبرنا سليمان بن الحكم بن عوانة، أخبرنا عتبة بن حميد، عمن حدثه، عن قبيصة بن جابر، قال: قال علي بن أبي طالب رضي الله عنه:
من زهد في الدنيا تهاون بالمصيبات، ومن ارتقب الموت سارع إلى الخيرات.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (209,'٢٠٥ - وحدثنا محمد بن إسحاق الثقفي، قال: قال رجل من عبد القيس: أين تذهبون؟ بل أين يراد بكم، وحادي الموت في أثر الأنفاس حثيث ⦗١٠٢⦘ موضع، وعلى اجتياح الأرواح من منزل الفناء إلى دار البقاء مجمع، وفي خراب الأجساد المتفكهة بالنعيم مسرع.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (210,'٢٠٦ - حدثني محمد بن الحسين، أخبرنا عمار بن عثمان الحلبي، أخبرنا زياد بن الربيع اليحمدي، حدثني عبد العزيز أبو مرحوم، قال: دخلنا مع الحسن على مريض نعوده، فلما جلس عنده قال: كيف تجدك؟ قال: أجدني أشتهي الطعام، فلا أقدر أن أسيغه، وأشتهي الشراب فلا أقدر على أن أتجرعه.
قال: فبكى الحسن، وقال: على الأسقام والأمراض أسست هذه الدار، فهبك تصح من الأسقام وتبرأ من الأمراض، هل تقدر على أن تنجو من الموت؟ قال: فارتج البيت بالبكاء.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (211,'٢٠٧ - وحدثني محمد بن حسين، حدثني أحمد بن سهيل، حدثني ضمرة بن ربيعة، قال: رأيت شيخاً بعسقلان، وقد اجتمع عليه الناس وهو يقول: عجبت من الناس أنهم ينظرون إلى الموتى في كل يوم ينقلون، وهم في الدنيا في غلفة يلعبون، ثم غشي عليه.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (212,'٢٠٨ - حدثنا الحسن بن محبوب، وغيره قالوا: أخبرنا إسحاق بن سليمان الرازي عن أبي جعفر الرازي، ⦗١٠٣⦘ عن الربيع بن أنس، قال: قال رسول الله صلى الله عليه وسلم:
كفى بذكر الموت مزهداً من الدنيا، ومرغباً من الآخرة.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (213,'٢٠٩ - قال بعض حكماء الشعراء: يا ساكن الدنيا أتعمر مسكنا ... لم يبق فيه مع المنية ساكن
الموت شيء أنت تعلم أنه ... حق وأنت بذكره متهاون
إن المنية لا تؤامن من أنت ... نفسه يوماً ولا تستأذن
واعلم بأنك لا أبا لك في الذي ... أصبحت تجمعه لغيرك خازن',NULL,'103',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (214,'٢١٠ - حدثني محمد بن عثمان العجلي، أخبرنا حسين الجعفي، قال: ذكر زائدة عن شيخ من أهل البصرة، عن أمية بن قسيم، عن حذيفة، عن النبي صلى الله عليه وسلم: إن الله يحمي عبده الدنيا كما يحمي الراعي الشفيق غنمه عن مراتع الهلكة.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (215,'٢١١ - حدثنا إبراهيم بن عبد الملك، حدثني هاشم بن المتوكل الإسكندراني، أخبرنا أبو عباد الزاهد، عن الحسن البصري، قال: ⦗١٠٤⦘ مسكين ابن آدم رضي بدار حلالها حساب، وحرامها عذاب، إن أخذه من حله حوسب بنعيمه، وإن أخذه من حرام عذب به، ابن آدم يستقل ماله، ولا يستقل عمله، يفرح بمصيبته في دينه، ويجزع من مصيبته في دنياه.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (216,'٢١٢ - حدثني ابن أبي مريم، عن محمد بن الحسين، عن حكيم بن جعفر، حدثني عبد الله بن أبي نوح، قال: سمعت رجلاً من العباد يقول: ما تكاملت المروءة في امرئ قط إلا لذي المعروف، وهانت عليه الدنيا.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (217,'٢١٣ - حدثني محمد بن يحيى بن أبي حاتم الأزدي، وغيره عن سعيد بن عامر عن عون بن معمر، قال: كتب الحسن إلى عمر بن عبد العزيز: سلام عليك أما بعد فكأنك بآخر من كتب عليه الموت، وقد مات.
فأجابه عمر: سلام عليك أما بعد فكأنك بالدنيا لم تكن وبالآخرة لم تزل.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (218,'٢١٤ - حدثنا محمد بن علي بن الحسن المروزي، أخبرنا أبو إسحاق إبراهيم بن الأشعث، عن فضيل بن عياض، قال: سمعته، يقول: قال عيسى بن مريم:
إنكم لن تدركوا ما تريدون إلا بترككم ما تشتهون، ولا تنالون ما تأملون إلا بصبركم على ما تلهون، ويل لصاحب الدنيا كيف يموت ويتركها، ويأمنها وتخونه، ويثق بها وتخدعه، ويل للمغترين بالدنيا كيف أزفهم ما يكرهون، وفارقهم ما يشتهون، وجاءهم ما يوعدون ويل لمن الدنيا همه، والخطايا عمله كيف يفتضح غداً.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (219,'٢١٥ - وحدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، أخبرنا الفضيل عن منصور، عن سالم بن أبي الجعد: قال: عيسى بن مريم:
اتقوا فضول الدنيا فإنها رجس عند الله.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (220,'٢١٦ - وحدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: سمعت ابن عيينة، يقول: قال عيسى بن مريم: كانت الدنيا ولم أكن فيها، وتكون ولا أكون فيها، وإنما لي فيها أيامي التي أنا فيها، فإن شقيت فيها فأنا شقي.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (221,'٢١٧ - وحدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: سمعت فضيل بن عياض، يقول: إن رجلاً من الحواريين قام إلى عيسى فقال: يا روح الله حدثني عن النفر الزهاد الذين لقيهم يونس بن متى لعل ذلك ينبه أبناء الدنيا من رقدة الغفلة، ويخرجهم من ظلمة الجهل، فرب كلمة قد أحيت سامعها بعد الموت، ورفعته بعد الضعة، ونعشته بعد الصرعة، وأعنته بعد الفقر، وجبرته بعد الكسر، ويقظته بعد ⦗١٠٦⦘ الوسنة، فنقبت عن قلبه، ففجرت فيه ينابيع الحياة، فسالت فيه أودية الحكمة، وأنبتت فيه غراس الرحمة، إذا وافق ذلك القضاء من الله.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (222,'٢١٨ - أنشدني محمود الوراق قوله: ما أفضح الموت للدنيا وزينتها ... وما أفضح الدنيا لأهليها!!
لا ترجعن على الدنيا بلائمة ... فعذرها لك باد في مساويها
لم يبق من عيبها شيء لصاحبها ... إلا وقد بينته في معانيها
تفني البنين وتفني الأهل دائبة ... وتستليم إلى من لا يعاديها
فما يزيدهم قتل الذي قتلت ... ولا العدداوة إلا رغبة فيها',NULL,'106',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (223,'٢١٩ - حدثنا يعقوب بن عبيد، أخبرنا يعقوب بن إسحاق الحضرمي، أخبرنا شعبة عن الحكم، عن ابن أبي ليلى، عن عبد الله - يعني: ابن ربيعة -،: أن رسول الله صلى الله عليه وسلم، كان في مسير له، فإذا شاة ميتة فقال رسول الله صلى الله عليه وسلم:
أترون هذه هينة على أهلها؟ قالوا: نعم.
قال: الدنيا على الله أهون من هذه على أهلها.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (224,'٢٢٠ - حدثنا خالد بن خداش المهلبي، أخبرنا حماد بن زيد، عن ⦗١٠٧⦘ علي بن زيد، عن أبي نضرة، عن أبي سعيد الخدري، قال: صلى بنا رسول الله صلى الله عليه وسلم، العصر بنهار، ثم قام فخطبنا فلم يترك شيئاً قبل قيام الساعة إلا خبر به، حفظه من حفظه ونسيه من نسيه، وقال: وجعل الناس يتلفتون إلى الشمس هل بقي منها شيء.
فقال:
ألا إنه لم يبق من الدنيا فيما مضى منها إلا كما بقي من يومكم هذا فيما مضى منه.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (225,'٢٢١ - حدثني الفضل بن جعفر بن عبد الله، أخبرنا وهب بن بيان، أخبرنا يحيى بن سعيد العطار، أخبرنا أبو سعيد خلف بن حبيب، عن أنس بن مالك، قال: قال رسول الله صلى الله عليه وسلم:
مثل هذه الدنيا مثل ثوب شق من أوله إلى آخره، فبقي متعلقاً بخيط في آخره، فيوشك ذلك الخيط أن ينقطع.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (226,'٢٢٢ - حدثنا حميد النسائي، أخبرنا إسماعيل بن أبي أويس، حدثني ⦗١٠٨⦘ مالك بن أنس، عن زيد بن أسلم، عن عطاء بن يسار، عن أبي سعيد الخدري، قال: قال رسول الله صلى الله عليه وسلم:
إن أكثر ما أخاف عليكم ما يخرج الله لكم من بركات الأرض، فقيل: ما بركات الأرض؟ قال: زهرة الدنيا.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (227,'٢٢٣ - حدثني محمد بن قدامة الجوهري، حدثني رجل من أهل البصرة، عن أبيه، أخبرنا مبارك بن فضالة، عن علي بن عبد الله بن عباس، قال: دخلت على عبد الملك بن مروان في يوم شديد البرد وإذا هو في جبة باطنها قوهي معصفر، وظاهرها خز أغبر، وحوله أربعة كولين.
قال: فرأى البرد في تقفقفي.
فقال: ما أظن يومنا هذا إلا بارداً.
قلت: أصح الله أمير المؤمنين ما يظن ⦗١٠٩⦘ أهل الشام أنه أتى عليهم يوم أبرد منه.
قال: فذكر الدنيا فذمها، ونال منها، وقال: هذا معاوية عاش أربعين سنة.
عشرين أميراً، وعشرين خليفة، هذه جثوته عليها ثمامة نابتة، لله درختمه - يعني عمر بن الخطاب - ما كان أعلمه بالدنيا.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (228,'٢٢٤ - وحدثني محمد بن قدامة، عن شيخ له: أن عبد الملك بن مروان وقف على قبر معاوية، وعليه توتة تهتز وتزهر فقال: الحمد لله عشرين سنة أميراً، وعشرين سنة خليفة، ثم صرت إلى هذا، هل الدهر والأيام إلا كما أرى رزية مال، أو فراق حبيب.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (229,'٢٢٥ - سمعت عبد الله بن عقيل، يحدث محمد بن قدامة، قال: قال عيسى بن مريم:
من علامة الزاهدين في الدنيا تركهم كل خليط لا يريد ما يريدون.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (230,'٢٢٦ - وسمعت يمان الحذاء، يحدث ابن قدامة قال: قال فضيل بن عياض لأبي تراب:
الدخول في الدنيا هين، لكن التخلص منها شديد.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (231,'٢٢٧ - حدثنا محمد بن عبد الله المديني، أخبرنا المعتمر بن سليمان، عن أبيه، عن مسعر بن كدام، قال: ⦗١١٠⦘ قدم ملك من الملوك على رجل يقضي فقتله، فقال: ما أراه كان يقضي إلا وعنده كتب، فبعث إلى امرأته أو إلى أخته، هل كانت له كتب؟ قالت: لا، إلا أنه كان معه كتاب صغير لا يفارقه، فالتمسوه في مقتله، فوجدوا كتاباً فيه أربع كلمات: عجبت لمن يعرف أن الموت حق كيف يفرح، وعجبت لمن يعلم أن النار حق فكيف يضحك، وعجبت لمن يرى تغيير الدنيا وتقلبها بأهلها كيف يطمئن إليها، وعجبت لمن يعلم أن القدر حق كيف ينصب؟ ؟ .',NULL,'109',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (232,'٢٢٨ - حدثني الحسن بن الصباح، عن الوليد بن شجاع، عن هشام بن إسماعيل، قال: كان ملك من الملوك لا يأخذ أحداً من أهل الإيمان إلا بصلبه، فأتى رجل من أهل الإيمان بالله، فأمر بصلبه، فقيل له أوص.
قال: بأي شيء أوصي أدخلت في الدنيا، ولم أستأمنها، وعشت فيها جاهلاً، وأخرجت وأنا كاره.
قال: وكانوا في ذلك الزمان لا يخرج أحد إلا معه كيس مدور مما تتخذه الفرس فيه ذهب أو فضة، فلما قتل ابتدروا ذلك الكيس، وهم يرون أن فيه ذهباً أو فضة فأصابوا كتاباً فيه ثلاث كلمات: إذا كان القدر حقاً فالحرص باطل، وإذا كان الغدر في الناس طباعاً فالثقة بكل أحد عجز، وإذا كان الموت بكل أحد راصداً فالطمأنينة إلى الدنيا حمق.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (233,'٢٢٩ - حدثنا محمد بن عاصم، أخبرني نافع أبو هرمز، عن أنس بن مالك، قال: ⦗١١١⦘ جاء ملك الموت إلى نوح، فقال: يا أطول النبيين عمراً، كيف وجدت الدنيا ولذتها؟ قال: كرجل دخل بيتاً له بابان، فقام وسط البيت هنية، ثم خرج من الباب الآخر.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (234,'٢٣٠ - حدثنا إسحاق بن إسماعيل، أخبرنا جرير، عن عطاء بن السائب، عن أبي البختري: أن عمر كتب إلى أبي موسى:
أن لا تؤخر عمل اليوم لغد فتتوالى عليكم الأعمال، فتضيع وإن للناس نفرة عن سلطانهم، أعوذ بالله أن تدركني ضغائن محمولة، ودنيا مؤثرة، وأهواء متبعة.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (235,'٢٣١ - وحدثنا إسحاق بن إسماعيل، أخبرنا جرير، عن عطاء بن السائب، عن أبي البختري، وميسرة قالا: إن علياً قسم ما في بيت المال حتى لم يبق فيه إلا أربعة آلاف، فأمر بها فقسمت فقيل له في ذلك.
فقال: لا والله حتى تبعر فيه الغنم.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (236,'٢٣٢ - حدثنا أحمد بن حاتم الطويل، أخبرنا محمد بن الحجاج، عن مجالد، عن الشعبي، عن قبيصة بن جابر، قال: ⦗١١٢⦘ ما رأيت أزهد في الدنيا من علي بن أبي طالب.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (237,'٢٣٣ - حدثني هارون بن الحسن، حدثني حمزة، حدثني عبد الله بن شوذب، قال: كان يقال: إن الله وسم الدنيا بالوحشة، وجعل أنس المطيعين به.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (238,'٢٣٤ - حدثنا أحمد بن محمد البصري، أخبرنا أبي، عن الحسن بن محمد الحضرمي، قال: خطب عمر بن عبد العزيز، فقال:
أيها الناس إنكم خلقتم لأمر إن كنتم تصدقون به إنكم لحمقى، وإن كم تكذبون به إنكم لهلكى، إنما خلقتم للأبد، ولكنكم من دار إلى دار تنقلون، عباد الله إنكم في دار لكم فيها من طعامكم غصص، ومن شرابكم شرق، لا تصفو لكم نعمة، تسرون بها إلا بفراق أخرى تكرهون فراقها، فاعلموا لما أنتم صائرون إليه، وخالدون فيه، ثم غلبة البكاء فنزل.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (239,'٢٣٥ - حدثني محمد بن الحسين، أخبرنا داود بن المحبر، حدثني صالح المري، حدثني رجل من الأزد،: أنه سمع عمر بن عبد العزيز، يقول في خطبته:
لا تغرنكم الدنيا، والمهلة فيها، فعن قليل عنها تنقلون، وإلى غيرها ترتحلون، فالله عباد الله في أنفسكم فبادروا بها الفوت، قبل حلول الموت، فلا يطولن بكم الأمد، فتقسوا قلوبكم، فتكونوا كقوم دعوا إلى حظهم فقصروا عنه بعد المهلة، فندموا على ما قصروا عند الآخرة.
قال: ثم نحب وهو على المنبر.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (240,'٢٣٦ - قال أبو منصور الأنصاري، عن ابن عيينة، قال: قال الحجاج بن يوسف على المنبر: لسحق ردائي هذا أحب إلي مما مضى من الدنيا، وما بقي منها أشبه بما مضى.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (241,'٢٣٧ - حدثنا عبد الله بن شبيب بن خالد القيسي، حدثني أحمد بن محمد المهري، حدثني رجل من عبد القيس، قال: دخلت حرفة ابنة النعمان بن المنذر على معاوية بن أبي سفيان فقال لها: أخبريني عن حالكم كيف كانت؟ قالت: أطيل أم أقصر؟ قال: لا بل أقصري.
قالت: أمسينا مساء، وليس في العرب أحد إلا وهو يرغب إلينا، وهو لا يرهب منا، فأصبحنا صباحاً، وليس في العرب إلا ونحن نرغب إليه، ونرهب منه.
ثم قالت:
بينا نسوس الناس في كل بلدة ... إذا نحن فيهم سوقة نتنصف
فأف لدنيا لا يدوم نعيمها ... تقلب تارات بنا وتصرف',NULL,'113',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (242,'٢٣٨ - أنشدني أبو عجاجة أعرابي من بني أسد: ألا إنما الدنيا كنبت قرارة ... تعالت قليلاً ثم هبت سمومها
وكيف على الدنيا تبكي وقد ترى ... بعينيك أن لم يبق إلا ذميمها',NULL,'113',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (243,'٢٣٩ - حدثني الحسين بن علي بن عبد الله البزار، عن علي بن عياش الحمصي، أخبرنا إسماعيل بن عياش، عن عبد الرحمن البجلي وغيره قالوا: ⦗١١٤⦘ قدم على معاوية رجل من نجران يقولون: كان له يوم قدم عليه مائة سنة، فسأله عن الدنيا فقال: سنوات بلاء، وسنوات رخاء، يوم ويوم، وليلة وليلة، يولد مولود، ويهلك هالك، فلولا المولود باد الخلق، ولولا الهالك ضاقت الدنيا بمن فيها.
فقال له: سل؟ قال: عمر مضى فترده؟! أو أجل حضر فتدفعه؟! قال: لا أملك ذلك.
قال: لا حاجة لي إليك، ثم قال:
استرزق الله خيراً وارضين به ... فبينما العسر إذ دارت مياسير
وبينما المرء في الأحياء مغتبط ... إذ صار رمساً تعفيه الأعاصير',NULL,'113',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (244,'٢٤٠ - وحدثني الحسين بن علي، عن أبي مسهر، عن مزاحم بن زفر، قال: سمعت سفيان الثوري ينشد من قول ابن حطان:
أرى أشقياء الناس لا يسأمونها ... على أنهم فيها عراة وجوع
أراها وإن كانت قليلاً كأنها ... سحابة صيف عن قليل تقشع.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (245,'٢٤١ - حدثني محمد بن إسحاق الثقفي، قال: قال بعض الحكماء: عجبت ممن يحزن على نقصان ماله، ولا يحزن على فناء عمره، وعجبت ممن الدنيا مولية عنه، والآخرة مقبلة إليه يشتغل بالمدبرة، ويعرض عن المقبلة.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (246,'٢٤٢ - حدثني يعقوب بن إسماعيل، حدثنا يعقوب بن إبراهيم، حدثني عمر بن محمد المكي، قال: خطب عمر بن عبد العزيز فقال: ⦗١١٥⦘
إن الدنيا ليست بدار قراركم، دار كتب الله عليها الفناء، وكتب على أهلها منها الظعن، فكم عامر مونق عما قليل يخرب، وكم مقيم مغتبط عما قليل يظعن، فأحسنوا رحمكم الله منها الرحلة بأحسن ما يحضركم من النقلة، وتزودوا فإن خير الزاد التقوى، إنما كفيء ظلال قلص فذهب، بينما ابن آدم في الدنيا ينافس، وبها قرير عين قانع، إذ دعاه الله بقدره، ورماه بيوم حتفه، فسلبه آثاره ودنياه، وصير لقوم آخرين مصانعه ومغناه، إن الدنيا لا تسر بقدر ما تضر، إنما تسر قليلاً، وتحزن حزناً طويلاً.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (247,'٢٤٣ - حدثني محمد بن إسحاق الثقفي، عن عبد الله بن صالح، قال: قال داود الطائي: يا بن آدم فرحت ببلوغ أملك، وإنما بلغته بانقضاء مدة أجلك، ثم سوفت بعملك كأن منفعته لغيرك.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (248,'٢٤٤ - وأنشدني محمد بن إسحاق: من كان راكب يوم ليس يأمنه ... وليلة علها في عقب دنياه
فكيف يلتذ عيشاً أو يطيب له ... وكيف تعرف طعم الغمض عيناه',NULL,'115',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (249,'٢٤٥ - حدثني هارون بن سفيان، أخبرنا زكريا بن عدي، أخبرنا إسماعيل بن عبد الأعلى، عن أبيه عن العلاء بن المنذر، قال: الدنيا سبعة آلاف سنة، فقد مضى منها ستة آلاف وستمائة أو خمسمائة ونيف منذ بعث النبي صلى الله عليه وسلم.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (250,'٢٤٦ - حدثني محمد بن علي بن الحسن بن شفيق، أخبرنا إبراهيم بن ⦗١١٦⦘ الأشعث، عن فضيل بن عياض، قال: بلغني أن رجلاً من العباد قال: الدنيا سبعة آلاف سنة، لأعبدن فيها لعلي أن أنجو من يوم كان مقداره ألف سنة ولعله لم يعش بعد مقالته هذه يوماً واحداً فأعطاه الله على نيته.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (251,'٢٤٧ - حدثني سلمة بن شبيب، أخبرنا سهل بن عاصم، عن سلم بن ميمون الخواص، قال: سمعت عثمان بن زائدة، يقول: كان كرز العباداني يجتهد في العبادة، فقيل له في ذلك.
فقال: كم بلغكم عمر الدنيا؟ قالوا: سبعة آلاف سنة.
فقال: فكم بلغكم مقدار يوم القيامة؟ قالوا: خمسون ألف سنة.
قال: أفيعجز أحدكم أن يعمل سبع يوم، حتى تأمن ذلك اليوم.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (252,'٢٤٨ - حدثني إبراهيم بن عبد الملك، أخبرنا عبد الله بن محمد بن أسماء أبو عبيد، أخبرنا عون بن معمر، قال: كتب رجل عالم إلى عمر بن عبد العزيز: ⦗١١٧⦘ أما بعد: فإن الدنيا ليست بدار مقامة، وإنما أهبط آدم من الجنة إليها عقوبة، بحسب من لا يدري ما ثواب الله أنها ثواب، وبحسب من لا يدري ما عقاب الله أنها عقاب، وليست كذلك ولكنها دار تسلم أهلها إلى النقمة، مثلها مثل الحية مسها لين، وفيها الموت، فكن فيها كالمريض الذي يكره نفسه على الدواء، رجاء العافية، وتدع ما تشتهي من الطعام رجاء العافية.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (253,'٢٤٩ - حدثنا أحمد بن إبراهيم، حدثني أخي أخبرنا عبد الرحمن بن مهدي، عن حماد بن زيد، عن هشام، عن الحسن، قال: ما الدنيا كلها من أولها إلى آخرها إلا كرجل نام نومة، فرأى في منامه ما يجب ثم انتبه.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (254,'٢٥٠ - أنشدني إبراهيم بن عبد الملك لسليمان بن يزيد العدوي: عجباً لأمنك والحياة قصيرة ... وبفقد إلف لا تزال تروع
أفقد رضيت بأن تعلل بالمنى ... وإلى المنية كل يوم تدفع
لا تخدعنك بعد طول تجارب ... دنيا تكشف للبلاء وتصرع
أحلام نوم أو كظل زائل ... إن اللبيب بمثلها لا يخدع ⦗١١٨⦘
وتزودن ليوم فقرك زادا ... ألغير نفسك لا أبا لك تجمع',NULL,'117',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (255,'٢٥١ - حدثني علي بن سعيد أخبرنا ضمرة عن هشام قال: قال سعيد بن جبير:
إنما الدنيا جمعة من جمع الآخرة.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (256,'٢٥٢ - حدثنا أبو بلال الأشعري، أخبرنا جابر بن سليمان، عن أبي عمير المكي، عن الحسن، قال: كان رسول الله صلى الله عليه وسلم، يقول في دعائه:
اللهم إني أعوذ بك من دنيا تمنع خير الآخرة.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (257,'٢٥٣ - حدثنا أبو سعيد المدني، عن إبراهيم بن حمزة، حدثني محمد بن ⦗١١٩⦘ فضالة النحوري، حدثني الزبير بن عباد بن حمزة بن عبد الله بن الزبير، قال: رأى عامر بن عبد الله بن الزبير امرأة ثائرة الشعر، بين أضعاف المقابر وهي تقول:
آذنت زينة الحياة بببن ... وانقضاء من أهلها وفناء
قال: فأول الناس ذلك من رؤى عامر الدنيا.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (258,'٢٥٤ - حدثني محمد بن علي بن شفيق، أخبرنا إبراهيم بن الأشعث، قال: قال سفيان بن عيينة: من أخذ شيئاً من الدنيا لمعصية الله، فقد أخذ ثمناً قليلاً.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (259,'٢٥٥ - حدثني أبو بكر بن أحمد بن قريش، قال: قال فضيل بن عياض: خطب الناس هارون فاستند إلى البيت فقال:
أيها الناس، إن الدينا غرارة، أهلكت من كان قبلكم من الأمم السالفة، ألا وهي مهلكة من بقي، ألا فلا تغرنكم الدنيا.
قال: أبكاني قوله، وأعجبت من فعله.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (260,'٢٥٦ - أنشدتي أبو الحسن الباهلي: إحذر الموت فإن الموت يغتال النفوسا ... وارفض الدنيا وقابل وجهها وجهاً عبوساً.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (261,'٢٥٧ - حدثني الحسين بن عبد الرحمن، عن رجل من قريش قال: كتب بعض الحكماء إلى أخ له:
أما بعد: فإن الدنيا حلم، والآخرة يقظة، والمتوسط بينهما الموت، ونحن في أضغاث، والسلام.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (262,'٢٥٨ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله، أنبأنا ابن لهيعة، عن عمارة بن غزية، عن عبد الله بن عروة بن الزبير، قال: أشكو إلى الله عيبي ما لا أترك، ونعتي ما لا آتي، وإنما ينكي بالدين الدنيا.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (263,'٢٥٩ - أنشدنا أبو سعيد المديني لعبد الله بن عروة: يبكون بالدين للدنيا وبهجتها ... أرباب دنيا عليها كلها صادي
لا ينظرون لشيء من معادهم ... تعجلوا حظهم في العاجل البادي
لا يهتدي ولا يهدون تابعهم ... ضل المقود وضل القائد الهادي',NULL,'120',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (264,'٢٦٠ - حدثني حمزة بن العباس، أنبأنا عبدان بن عثمان، أنبأنا عبد الله، أنبأنا ابن لهيعة، عن عطاء بن دينار، عن سعيد بن جبير قال: ⦗١٢١⦘ الغرور بالله أن يصر العبد في معصية الله، ويتمنى على الله في ذلك المغفرة، والغرة في الحياة الدنيا أن يغترها وتشغله عن الآخرة، فيمهد لها ويعمل لها، كقول العبد إذا أفضى إلى الآخرة: يا ليتني قدمت لحياتي، وأما متاع الغرور فهو ما يلهيك عن طلب الآخرة فهو متاع الغرور، وما لم يلهك فليس بمتاع الغرور، ولكنه متاع وبلاغ إلى ما هو خير منه.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (265,'٢٦١ - حدثني إبراهيم بن يعقوب، قال: قال بشر بن الحارث: من سأل الله الدنيا فإنما يسأله طول الوقوف.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (266,'٢٦٢ - حدثني سلمة بن شبيب، أخبرنا سهل بن عاصم، عن عثمان بن زفر التيمي، عن أبي الصهباء التيمي، قال: قال إبراهيم التيمي: الدنيا مشغلة، اللهم لا تشغلني بها، ولا تعطني منها شيئاً.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (267,'٢٦٣ - وحدثني سلمة بن شبيب، عن داود بن مهران، أخبرنا شهاب بن خراش، ⦗١٢٢⦘ عن محمد بن مطرف، قال: قال أبو حازم ما في الدنيا شيء يسرك إلا وقد التزق به شيء يسوءك.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (268,'٢٦٤ - وحدثني سلمة بن شبيب، أنه حدث عن عبد الله بن المبارك، قال: أخبرنا محمد بن النضر الحارثي، قال: قال رسول الله صلى الله عليه وسلم: لا تشغلوا قلوبكم بذكر الدنيا.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (269,'٢٦٥ - وحدثني سلمة بن شبيب، أخبرنا سهل بن عاصم، عن سلم بن ميمون، أخبرنا أبو طيبة الجرجاني، قال: قلت لكرز بن وبرة: من ذا الذي يبغضه البر والفاجر؟ قال: العبد يكون من أهل الآخرة، ثم يرجع إلى الدنيا.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (270,'٢٦٦ - وحدثني سلمة بن شبيب، أنه حدث عن عبد الله بن وهب، عن ⦗١٢٣⦘ بكر بن مضر، عن عمارة بن غزية، قال: سمعت رجلاً سأل ربيعة فقال:
يا أبا عثمان، ما رأس الزهادة؟ قال: جمع الأشياء بحقها، ووضعها في حقها.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (271,'٢٦٧ - وحدثني سلمة، عن سهل بن عاصم، قال: قال داود الطائي:
من علامة المريدين للزهد في الدنيا، ترك كل خليط لا يريد ما يريدون.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (272,'٢٦٨ - حدثني حاتم بن يحيى، قال: كتب إلينا عبد الله خبيق، قال: حذيفة يعني المرعشي كتب إلي يوسف بن أسباط:
أما بعد، فإني أوصيك بتقوى الله، والعمل بما علمك الله، والمراقبة حيث لا يراك أحد إلا الله، والاستعداد لما ليس لأحد فيه حيلة، ولا ينتفع بالندم عند نزوله، فاحسر عن رأسك قناع الغافلين، وانتبه من رقدة الموتى، وشمر للسباق غداً، فإن الدنيا ميدان المتسابقين، ولا تغتر بمن قد أظهر النسك، وتشاغل بالوصف، وترك العمل بالموصوف، واعلم ياأخي أن لا بد لي ولك من المقام بين يدي الله، يسألنا عن الدقيق الخفي، وعن الجليل الجافي، ولست آمن أن يسألني وإياك عن وساوس الصدور، ولحظات العيون، وإصغاء الأسماع، وما عسى يعجز مثلي عن صفة مثله، واعلم يا أخي أنه مما وصف به منافقو هذه الأمة: أنهم خالطوا أهل الدين بأبدانهم، وطابقوهم عليها بأهوائهم، وخضعوا لما طمعوا من نائلها، فسكتوا عما سمعوا من باطلها، وفرحوا بما رأوا من زينتها، وداهن بعضهم بعضاً في القول والفعل، وتركوا باطن العمل بالتصحيح، فحرمهم الله بذلك الثمن الربيح، واعلم يا أخي أنه لا يجزي من العمل القول، ومن البذل العدة، ولا من التوقي ⦗١٢٤⦘ التلاؤم، فقد صرنا في زمان هذه صفة أهلها، فمن كان كذلك فقد تعرض للمهالك، وصد عن سواء السبيل، وفقنا الله وإياك لما يحب، والسلام.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (273,'٢٦٩ - حدثني الوليد بن شجاع السكوني، حدثني ضمرة بن ربيعة، عن ابن شوذب، قال: قيل لكثير بن زياد أوصنا، قال:
بيعوا دنياكم بآخرتكم تربحونها والله جميعاً، ولا تبيعوا آخرتكم بدنياكم فتخسرونهما والله جميعاً.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (274,'٢٧٠ - حدثني أبو عبد الله أحمد بن بكير، قال: قال محمد بن علي: كان لي أخ كان في عيني عظيماً، وكان الذي عظمه في عيني صغر الدنيا في عينه.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (275,'٢٧١ - حدثني محمد بن العباس، أخبرنا عبيد الله بن عمر، أخبرنا حماد بن زيد، أخبرنا يزيد بن حازم، قال: كان سليمان بن عبد الملك يخطبنا كل جمعة، ويقول في خطبته: ⦗١٢٥⦘
ألا إن أهل الدنيا فيها على وجل، لم تمض بهم نية، ولم تطمئن بهم دار حتى يأتي أمر الله وهم على ذلك، وكذلك لا يدوم نعيمها، ولا يؤمن فجائفها، يبقى شرار أهلها ثم يقرأ: {أفرأيت إن متعناهم سنين* ثم جاءهم ما كانوا يوعدون * ما أغنى عنهم ما كانوا يمتعون} [الشعراء: ٢٠٥، ٢٠٧] .',NULL,'124',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (276,'٢٧٢ - حدثني محمد بن العباس، عن صالح بن عبد الله، قال: كتب عمر بن عبد العزيز إلى عامله عدي بن أرطأة:
أما بعد: فإن الدنيا عدوة أولياء الله، وعدوة أعداء الله، أما أولياء الله فختمهم، وأما أعداء الله فغمتهم.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (277,'٢٧٣ - حدثني محمد بن العباس، أخبرنا محمد بن عمر بن الكميت الكلاعي، أخبرنا أبو إسحاق المقري، قال: كان ابن الحنفية يقول:
إني واصف لك أخاً لي كان أعظم الناس في عيني، وكان الذي عظمه في عيني صغر الدنيا في عينه، كان خارجا من سلطان بطنه فلا يضع فيها ما لا يجد، ولا يكثر إذا وجد وكان خارجا من سلطان الجهالة فلا يقدم على الأمر إلا بنية.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (278,'٢٧٤ - حدثني محمد بن العباس، أخبرنا محمد بن عمر بن الكميت، قال: سمعت داود بن يحيى بن يمان، عن أبيه، قال: مر موسى برجل قد مات تحت رأسه لبنة، ورأسه ولحيته في التراب فقال:
رب عبدك هذا ضاع، فقال: يا موسى، إني إذا أقبلت على عبد بوجهي زويت عنه الدنيا بحذافيرها.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (279,'٢٧٥ - حدثني عمر بن عبد الله، أنه حدث عن مخلد بن حسين، عن هشام، عن الحسن، قال: لا تخرج نفس ابن آدم من الدنيا إلا بحسرات ثلاث: أنه لم يشبع مما جمع، ولم يدرك ما أمل، ولم يحسن الزاد لما قدم عليه.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (280,'٢٧٦ - حدثني صاحب لنا قال: قيل لبعض العباد: قد نلت الغناء؟ قال: إنما نال الغناء من أعتق من رق الدنيا',NULL,'126',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (281,'٢٧٧ - حدثني الحسين بن عبد الرحمن، عن علي بن محمد القرشي عن مسلمة بن محارب قال: قال عامر بن عبد قيس:
الدنيا والدة الموت، وناقضة للمبرم، ومن تجعله للعطية، وكل من فيها يجري على ما لا يريد، وكل مستقر فيها غير راض بها، وذلك شهيد على أنها ليست بدار قرار.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (282,'٢٧٨ - وحدثني الحسين بن عبد الرحمن، قال: كان ابن السماك يقول: من أذاقته الدنيا حلاوتها لميله إليها جرعته الآخرة مرارتها بتجافيه عنها.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (283,'٢٧٩ - أنشدني الحسين بن عبد الرحمن: دنيا يا دنيا يا غادرة ... إليك عني اليوم يا ساحرة
لا لذة أحسن من لذة ... منبوذة من ذي يد قادرة
يا عيني كم عانيت من عبرة ... فاعتبري إن كنت لي ناظرة
ما لذة إلا وقد نلتها ... لم يبق إلا لذة الآخرة
طوبى لمن كانت له عزمة ... مخلصة باطنة ظاهرة
يا نفس للمكروه غب غد ... مر فهل أنت له صابرة
ما لذة الدنيا وعين ترى ... فيها إلى ما لذتي صائرة',NULL,'127',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (284,'٢٨٠ - حدثني العباس بن هشام بن محمد، عن أبيه قال: قال روح بن حاتم: بينما أنا واقف على باب بعض ولاة البصرة إذ أقبل خالد بن صفوان يسير على بغلة له فقال لي:
يا ابن أخي ماهجرت ولا أظهرت على باب أحد من الولاة إلا وأنا أراك عليه، أكل هذا حباً في الدنيا وحرصاً عليها؟ قال: فأجللت أن أجيبه، ثم قلت: إنما هذا مثل العمر، ولعله أراد الجواب مني فقلت: يا عم بحسبك برؤيتك إياي عليها طلباً منك لها، فضحك ثم قال: لئن قلت ذاك يا ابن أخي لقد ذهب رونق الوجه، ودماء القلب، وحسام الصلب، وسنا البصر، ومد الصوت، وماء الشباب، واقترب عهد العلل، والله ما أتت علينا ساعة من أعمارنا إلا ونحن نؤثر الدنيا على ما سواها، ثم ما تزداد لنا إلا تخليا، وعنا إلا تولياً، ثم ضرب بغلته فذهب.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (285,'٢٨١ - حدثني الحسين بن عبد الرحمن، حدثني صالح بن مالك، قال: كتبت أم إبراهيم الصائغ إلى إبراهيم، وقد كان يومئذ مجاوراً بمكة، تسأله القدوم عليها، فكتب إليها بكتاب فيه:
إن مرو التي تعجبك ملاقاتي تعجبك ملاقاتي إياك فيها، ليست بدار دوام، ولكن مرو منزل أسفار، وإنما سبيل المقام فيها بين الأمهات والأولاد يسير حتى تصيروا منها إلى دارين: إحداهما فرقة لا تواصل فيها، والأخرى صلة لا فرقة فيها، فإن كنت في شك من ذلك فأين الملوك الذين نزلوها، وأين الجموع الذين كانوا فيها، وأين الأمم الذين تشاحت عليها، وأين البناءون الذين ضربوا في تحصينها، إن تدعوهم لا يسمعون، بدلوا بالحياة موتاً، كأن لم يعمروها ولم يسكنوها، فهل ينفع مع هذا الهم حبيب حبيباً وخليل خليلاً، إنه ليس أحد لأحد إلا ما كان له في الآخرة، فأما أهل الدنيا فمتحولون منها عن قريب.
والسلام.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (286,'٢٨٢ - حدثنا الحسين بن عبد الرحمن أخبرنا محمد بن عمر المزني عن عمار بن سعيد قال: مر المسيح عليه السلام بقرية فإذا أهلها موتى في الأفنية والطرق فقال للحواريين:
يا معشر الحواريين إن هؤلاء ماتوا عن سخط، ولو ماتوا من غير ذلك لتدافنوا، قالوا: يا روح الله وددنا أنا علمنا خبرهم؟ فسأل ربه، فأوحى الله إليه: إذا كان الليل فنادهم يجيبوك، فلما كان الليل أشرف على نشر ثم نادى يا أهل القرية، فأجابه مجيب لبيك يا روح الله، فقال: ما حالكم، وما قصتكم، قالوا: بتنا في عافية، وأصبحنا في الهاوية.
قال: وكيف ذلك؟ قال: بحبنا الدنيا وطاعتنا أهل المعاصي.
قال: وكيف كان حبكم للدنيا؟ قال: حب الصبي لأمه إذا أقبلت فرحنا، وإذا أدبرت حزنا وبكينا عليها.
قال: فما بال أصحابك لم يجيبوني؟ قال: لأنهم ملجمون بلجم من نار بأيدي ملائكة غلاظ شداد.
قال: فكيف أجبتني أنت من بينهم؟ قال: لأني كنت فيهم، ولم أكن منهم، فلما نزل بهم العذاب أصابني معهم، فأنا معلق على شفير جهنم لا أدري أنجو منها، أم أكبكب فيها؟ ! فقال ⦗١٢٩⦘ المسيح للحواريين: لأكل خبز الشعير، بالملح الجريش، ولبس المسوح، والنوم على المزابل كثير مع عافية الدنيا.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (287,'٢٨٣ - أنشدني صاحب لنا: منع الهوى من كاعب ومدام ... نور المشيب وواعظ الإسلام
ولقد أراني والحوادث جمة ... لا تستفيق جهالتي وغرامي
فاليوم أقصر باطلي وأرحت من ... سعي الوشاة وألسن الوام
وعرفت أني لا محالة شارب ... عجلت أو أخرت كأس حمامي
أين الملوك الناعمون وأين من ... مثل الرجال له على الإقدام
أين الألى اقتادوا الجياد على الوجا ... لحق البطون كأنهم دوام
منشورة خرق الدرفس تظلهم ... في كل مشتجر الوسج لهام
وتميل في يوم اللقاء عليهم ... كأس المدام مناصف الخدام
فأديلت الأيام من شرواتهم ... من ذا يقوم لدولة الأيام
دول تولج في الوكور سهامها ... وعلى أين ما اللجة للعوام',NULL,'129',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (288,'٢٨٤ - بلغني عن أبي سليمان الداراني، قال: لا يصبر عن شهوات الدنيت إلا من كان في قلبه ما يشغله من الآخرة.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (289,'٢٨٥ - وبلغني عن بعض الحكماء، قال: من زهد في الدنيا ملكها، ومن رغب في الدنيا حرمها.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (290,'٢٨٦ - حدثني سلمة بن شبيب، عن عبد الوهاب بن نجدة، عن بقية بن الوليد، عن ضبارة بن عبد الله الألهاني، عن دويد بن نافع قال: قال عيسى بن مريم: ⦗١٣٠⦘
تعملون لدنيا صغيرة، وتتركون الآخرة الكبيرة، وعلى كلكم يمر الموت.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (291,'٢٨٧ - وحدثني سلمة بن شبيب، أخبرنا سهل بن عاصم، قال: سمعت فرج بن سعيد، قال: سمعت يوسف بن أسباط قال: قال لي زرعة: من كان صغير الدنيا أعظم في عينه من كبير الآخرة، وكيف يرجو أن نصنع له في دنياه وآخرته.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (292,'٢٨٨ - حدثني محمد بن عثمان بن علي العجلي، أخبرنا حسين الجعفي، عن زائدة، عن هشام، عن الحسن، قال: خرج عمر في يوم حار، واضعاً رداءه على رأسه، قال: فمر به غلام على حمار فقال:
يا غلام احملني معك.
قال: فوثب الغلام عن الحمار، فقال: اركب يا أمير المؤمنين.
فقال: لا أركب، وأركب خلفك، تريد أن تحملني على المكان الخشن، وتركب على المكان الوطئ، ولكن اركب أنت وأكون أنا خلفك.
قال: فدخل المدينة وهو خلفه، والناس ينظرون إليه.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (293,'٢٨٩ - حدثني محمد بن علي بن الحسن، أخبرنا إبراهيم بن الأشعث، قال: سمعت الفضيل بن عياض يذكر: عن النبي صلى الله عليه وسلم، قال:
الزهد في الدنيا يريح القلب والبدن، والرغبة في الدنيا تكثر الهم والحزن.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (294,'٢٩٠ - وحدثنا محمد بن علي، أخبرنا إبراهيم، قال: سمعت يعني الفضيل بن عياض، يقول: جعل الشر كله في بيت وجعل مفتاحه حب الدنيا، وجعل الخير كله في بيت وجعل مفتاحه الزهد في الدنيا.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (295,'٢٩١ - حدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: سألت فضيل بن عياض ما الزهد في الدنيا؟ قال:
القنوع هو الزهد وهو الغنى.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (296,'٢٩٢ - وحدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: سمعت الفضيل، يقول: حدثني رجل، قال: سمعت عون بن عبد الله، يقول:
إن الدنيا والآخرة في قلب ابن آدم ككفتي الميزان، بقدر ما ترجح إحداهما تخف الأخرى.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (297,'٢٩٣ - وحدثنا محمد بن علي، أخبرنا إبراهيم، قال: سمعت الفضيل، يقول: كتب الحسن بن أبي الحسن إلى عمر بن عبد العزيز: ⦗١٣٢⦘
أما بعد، يا أمير المؤمين، فاعلم أن الدنيا ليست بدار إقامة، وإنما أهبط آدم إليها عقوبة، فبحسب من لا يدري ثواب الله أنه ثواب، وبحسب من لا يدري عقاب الله أنه عقاب، ليست صرعتها كالصرعة تهين من أكرمها، وتذل من أعزها، وتفقر من جمعها، ولها في كل حين قتيل، فالزاد منها تركها، والغنى فيها فقرها، هي والله يا أمير المؤمنين كالسم يأكله من لا يعرفه ليشفيه، وهو حتفه، فكن فيها يا أمير المؤمنين كالمداوي جرحه، يحتمي قليلاً مخافة ما يكره طويلاً، ويصبر على شدة الدواء مخافة البلاء، فأهل البصائر الفضائل فيها يا أمير المؤمنين، مشيهم بالتواضع، وملبسهم بالاقتصاد، ومنطقهم بالصواب، ومطعمهم الطيب من الرزق، قد نفذت أبصارهم في الأجل، كما نفذت أبصارهم في العاجل، فخوفهم في البر كخوفهم في البحر، ودعاؤهم في السراء كدعائهم في الضراء، ولولا الأجل الذي كتب عليهم لم تقر أرواحهم في أبدانهم إلا قليلاً، خوفاً من العقاب، وشوقاً إلى الثواب، عظم الخالق في أعينهم، وصغر المخلوق عندهم، فارض منها بالكفاف وليكفك ما بلغك المحل.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (298,'٢٩٤ - حدثنا أبو بكر الصوفي، قال: سمعت أبا معاوية الأسود، يقول: من كانت الدنيا أكبر همه طال غداً في القيامة غمه.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (299,'٢٩٥ - حدثني سلمة بن شبيب، أخبرنا الحميدي، عن سفيان بن عيينة، عن أبيه، قال: سمعت مسلمة بن عبد الملك، يقول:
إن أقل الناس هماً في الآخرة أقلهم هماً في الدنيا.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (300,'٢٩٦ - أنشدني سليمان بن أبي شيخ: ما زالت الدنيا منغصة ... لم ينج صاحبها من البلوى
دار الفجائع والهمود ودا ... ر البث والأحزان والشكوى
بينا الفتى فيها يسير بها ... إذ صار تحت ترابها ملقى
تقفو مساوئها محاسنها ... لا شيء بين النعي والبشرى',NULL,'133',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (301,'٢٩٧ - حدثنا أبو بكر بن أبي النضر، أخبرنا سعيد بن عامر، قال: حدثنا جعفر بن سليمان، قال: قال مالك بن دينار.
: اصطلحنا على حب الدنيا، فلا يأمر بعضنا بعضاً، ولا ينهي بعضنا بعضاً، ولا يدعنا الله على هذا، فليت شعري أي عذاب الله ينزل؟',NULL,'133',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (302,'٢٩٨ - وقال بعض حكماء الشعراء: ركنا إلى الدار دار الغرور ... وقد سحرتنا بلذاتها
فما نرعوي لأعاجيبها ... ولا لتصرف حالاتها
تنافس فيها وأيامها ... تردد فينا بآفاتها',NULL,'133',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (303,'٢٩٩ - وقال رجل من قريش: كل حي وإن تملى بعيش ... سوف يحدوه بالفناء حاديان
أين أهل الحجا بنو عبد شمس ... والبهاليل من بني مروان
والغيوث الليوث في الحرب والجد ... ب إذا ما تقارب الزحفان
ورجال إذا استهلوا فيهم على الخيل ... فجن تردى على عقبان
وضع الدهر فيهم شفرتيه ... وتوالى عليهم العصران
فتولوا كأنهم لم يكونوا ... والليالي يلعبن بالإنسان
هون الوجد إن كل الورى يو ... ماً عليه سيعصف الملوان',NULL,'133',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (304,'٣٠٠ - حدثني عبد الرحيم بن يحيى، أخبرنا عثمان بن عمارة، قال: قال بعض العلماء: الزهد في الدنيا ألا يقيم الرجل على راحة تستريح إليها نفسه.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (305,'٣٠١ - وحدثني عبد الرحيم بن يحيى، أخبرنا عثمان بن عمارة، قال: كان يقال: الورع يبلغ بالعبد إلى الزهد في الدنيا، والزهد يبلغ به حب الله عز وجل.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (306,'٣٠٢ - حدثني أبو يزيد النميري، حدثني أبو يحيى الزهري، قال: قال عبد الله بن عبد العزيز العمري عند موته:
بنعمة ربي أحدث أني لم أصبح أملك على الناس إلا سبعة دراهم، من كل شعر فنلته بيدي، وبنعمة ربي أحدث لو أن الدنيا أصبحت تحت قدمي لا يمنعني من أخذها إلا أن أزيل قدمي عنها ما أزلتها.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (307,'٣٠٣ - حدثني القاسم بن هاشم، عن محمد بن عبد الله الحذاء، قال: سمعت العمري يقول:
إنما الدنيا والآخرة إناءان أيهما أكفأت كان الغسل فيه.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (308,'٣٠٤ - وحدثني أحمد بن بكير، قال: سمعت صالح بن عبد الكريم، قال: مثل القلب مثل الإناء إذا أملأته، ثم زدت فيه شيئاً فاض، وكذلك القلب إذا امتلأ من حب الدنيا لم تدخله المواعظ.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (309,'٣٠٥ - حدثني أبو حفص البخاري، أخبرنا سعيد بن منصور، أخبرنا ⦗١٣٥⦘ يعقوب بن عبد الرحمن، قال: سمعت أبا الحازم، يقول: يسير الدنيا يشغل عن كثير الآخرة.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (310,'٣٠٦ - حدثني الحسين بن علي، أنه حدث عن عباءة بن كليب، عن محمد بن النضر الحارثي، قال: كان محمد بن كعب، يقول:
الدنيا دار فناء ومنزل بلغة، رغبت عنها السعداء، وانتزعت من أيدي الأشقياء، فأشقى الناس بها أرغب الناس فيها، وأزهد الناس فيها أسعد الناس بها، هي المعذبة لمن أطاعها، المهلكة لمن اتبعها، الخائنة لمن انقاد، علمها جهل، وغناها فقر، وزيادتها نقصان، وأيامها دول.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (311,'٣٠٧ - وحدثني الحسين بن علي، أنه حدث، عن زيد بن الحباب، حدثني معاوية بن عبد الكريم، قال: ذكروا عند الحسن الزهد، فقال الحسن:
لستم في شيء، الزاهد الذي إذا رأى أحداً قال: هو أفضل مني.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (312,'٣٠٨ - وحدثني هارون بن عبد الله، أخبرنا محمد بن يزيد خنيس قال: قال وهيب:
لو أن علماءنا عفا الله عنا وعنهم نصحوا الله في عباده، فقالوا: يا عباد الله اسمعوا ما نخبركم عن نبيكم صلى الله عليه وسلم، وصالح سلفكم من الزهد في الدنيا، فاعلموا به، ولا تنظروا إلى أعمالنا هذه الفاسدة كانوا قد نصحوا لله في عباده، ولكنهم يأبون إلا أن يجروا عباد الله إلى فتنهم وما هم فيه.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (313,'<span data-type="title" id=toc-3>الجزء الثالث</span>',NULL,'137',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (314,'أخبرتنا الشيخة نور العين لامعة بنت المبارك بن كامل الخفاف قالت: أنبأنا الشيخ الحافظ أبو سعد أحمد بن محمد بن أحمد البغدادي، قال: أنبانا أبو العباس أحمد بن محمد الطهراني قال: أنبأنا أبو محمد الحسن بن أحمد بن يوه قال: أنبأنا أبو الحسن أحمد بن محمد اللبناني قال: أنبانا أبو بكر عبد الله بن محمد بن عبيد القرشي المعروف بابن أبي الدنيا.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (315,'٣٠٩ - أخبرنا محمد بن علي بن شقيق، قال: سمعت إبراهيم بن الأشعث، قال: سمعت الفضيل بن عياض، يقول:
لا يعطى أحد من الدنيا شيئاً إلا انتقص من آخرته مثله.
ويقال: باء بمثليه من الهم، ولا يعطي أحد من الدنيا شيئاً إلا قلبها بمثليه من الشغل، فإن شئت فاستكثر منها، وإن شئت فأقلل، والله ما تأخذ إلا من كيسك.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (316,'٣١٠ - وحدثنا محمد بن علي، قال: سمعت إبراهيم بن الأشعث قال: سمعت فضيل بن عياض، يقول:
قيل يا موسى: أيحزن عبدي المؤمن أن أزوي عنه الدنيا، وهو أقرب له مني، ويفرح أن أبسط له الدنيا وهو أبعد له مني.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (317,'٣١١ - حدثنا محمد بن عبد الله المدائني، أخبرنا أبو معاوية، عن الأعمش، عن مجاهد، عن ابن عمر، قال: لا يصيب عبد من الدنيا شيئاً إلا نقص من درجاته عند الله عز وجل، وإن كان عليه كريماً.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (318,'٣١٢ - حدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، قال: سمعت الفضيل يقول:
ما رأيت أحداً عظم الدنيا فقرت عينه فيها، ولا انتفع بها، وما حقرها أحد إلا تمتع بها.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (319,'٣١٣ - قال: قال: وسمعته يقول، يعني الفضيل:
عامة الزهد في الناس يعني إذا لم تحب ثناء الناس، ولم تبال بمذمتهم.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (320,'٣١٤ - حدثنا محمد بن علي، أخبرنا إبراهيم بن الأشعث، أخبرنا الفضل بن عثمان أخبرنا سلام بن مسكين، قال: سمعت الحسن، يقول: ⦗١٣٩⦘
أهينوا الدنيا فوالله ما هي لأحد بأهنأ منها لمن هانها.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (321,'٣١٥ - حدثني الخليل بن عمرو، أخبرنا ابن السماك، عن عبد الواحد بن زيد، عن الحسن، قال: إذا أراد الله بعبد خيراً أعطاه من الدنيا عطية، ثم يمسك، فإذا أنفد عاد عليه، وإذا هان عليه عبد بسطها له بسطاً.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (322,'٣١٦ - حدثني محمد بن العباس، أخبرنا محمد بن عمر بن الكميت الكلابي، قال: سمعت داود بن يحيى بن يمان، عن أبيه، قال: قال بهيم:
إنما أخاف أن تدفق الدنيا دفقة فتغرقني.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (323,'٣١٧ - وحدثني محمد بن العباس، أخبرنا محمد بن عمر الكلابي، قال: كان بعض العلماء يدعو: أيا ممسك السماء أن تقع على الأرض إلا بإذنه، أمسك عني الدنيا.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (324,'٣١٨ - حدثني محمد بن العباس، أخبرنا محمد بن عمر بن الكميت، عن زافر بن سليمان، عن عثمان بن زائدة، قال: قيل لمحمد بن الحنفية:
من أعظم الدنيا قدراً؟ ⦗١٤٠⦘ قال: من لم ير الدنيا كلها لنفسه خطراً، إنه ليس لأبدانكم ثمن إلا الجنة فلا تبيعوها إلا بها.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (325,'٣١٩ - وحدثني محمد بن العباس، أخبرنا محمد بن عمر بن الكميت، قال: مكتوب في حكمة عيسى عليه السلام: من علامة المريدين للزهد في الدنيا تركهم كل خليط لا يريد ما يريدون.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (326,'٣٢٠ - حدثنا محمد بن علي بن الحسن بن شقيق، أخبرنا أبو إسحاق إبراهيم بن الأشعث، أخبرنا الفضيل بن عياض، عن محمد بن سوقة، قال: أمران لو لم نعذب إلا بهما كنا مستحقين بهما لعذاب الله: أحدنا يزاد الشيء من الدنيا فيفرح فرحاً ما علم الله أنه فرحه بشيء زاده قط في دينه، وينقص الشيء من الدنيا فيحزن عليه حزناً ما علم الله أنه حزنه على شيء نقصه قط في دينه.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (327,'٣٢١ - حدثنا محمد بن علي، أخبرنا أبو إسحاق إبراهيم بن الأشعث، أنبأنا يحيى بن سليم، قال: قال لي عمر بن محمد بن المنكدر:
أرأيت لو أن رجلاً صام الدهر لا يفطر، وقام الليل لا يفتر، وتصدق بماله وجاهد في سبيل الله عز وجل، واجتنب محارم الله عز وجل، غير أنه يؤتى يوم القيامة ⦗١٤١⦘ على رؤوس الخلائق في ذاك الجمع الأعظم بين يدي رب العالمين فيقال: ها إن هذاعظم في عينه ما صغر الله، وصغر في عينه ما عظم الله، كيف ترى يكون حاله؟ فمن منا ليس هذا، هكذا الدنيا عظيمة عنده مع ما اقترفنا من الذنوب والخطايا.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (328,'٣٢٢ - وحدثنا محمد بن علي، أخبرنا أبو إسحاق، قال: سمعت الفضيل: يقول: ذكر عن نبي الله صلى الله عليه وسلم، أنه قال:
إذا عظمت أمتي الدنيا نزع منها هيبة الإسلام، وإذا تركت الأمر بالمعروف، والنهي عن المنكر حرمت بركة الوحي.
ذكر سفيان نحوه.
وقال سفيان ذلك في كتاب الله عز وجل: {سأصرف عن آياتي الذين يتكبرون في الأرض بغير الحق} [الأعراف: ١٤٦] .
قال: سأنزع عن قلوبهم فهم القرآن.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (329,'٣٢٣ - حدثنا محمد بن علي، أخبرنا أبو إسحاق، قال: سمعت الفضيل: يقول:
رهبة العبد من الله على قدر علمه بالله، وزهادته في الدنيا على قدر رغبته في الآخرة.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (330,'٣٢٤ - حدثنا محمد بن علي، قال: قال أبو إسحاق، وسمعت ⦗١٤٢⦘ الفضيل يقول: قال أبو الدرداء:
لا تزال نفس ابن آدم شابة في حب الدنيا والدرهم ولو التقت ترقوتاه من الكبر، إلا الذين امتحن الله قلوبهم للآخرة وقليل ما هم.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (331,'٣٢٥ - وحدثنا محمد بن علي، أخبرنا أبو إسحاق، قال: سمعت الفضيل يقول: قال أبو حازم:
اشتدت مؤونة الدنيا، ومؤونة الآخرة، فأما مؤونة الآخرة فإنك لا تجد لها أعواناً، وأما مؤونة الدنيا فإنك لا تضرب بيدك إلى شيء منها إلا وجدت فاجراً قد سبقك إليه.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (332,'٣٢٦ - حدثني الحسن بن الصباح حدثني عبد الله بن محمد، وكان من خير الرجال، قال: أخبرنا أبو المغيرة المخزومي، حدثني سعيد بن مسلمة، أخبرني ابن حميد الطويل رجل ممن كان انقطع إلى مكة من أهل الفضل، وليس بابن حميد البصري،: أن علي بن أبي طالب رضي الله عنه كان يقول في دعائه: اللهم إنك جعلت الدنيا فتنة، ونكالاً، فاجعل حظي من جميعها ونصيبي من قسمها، وشوقي من سلطانها، سلواً عنها، وعملاً بما ترضى به عني.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (333,'٣٢٧ - وقال بعض حكماء الشعراء: أرى علل الدنيا تروح وتغتدي ... علينا كأطراف الأسنة في القنا
أخوض من الدنيا غروراً كأنه ... سراب من الآمال واللهو والمنى ⦗١٤٣⦘
ولي كل يوم بالمنايا معرض ... من الحادثات ليس غيري بها عنى
كفى عجباً أني أموت وأنني ... مكب على الدنيا أبني بها البنا
تعلقت بالدنيا غروراً بلهوها ... إذا استحيت الدنيا هنا قتلت هنا
وما أنا إلا كالغريق تشبثت ... يداه التماساً للحياة بما رنا
وما أنا إن لم يلبس الله ستره ... وما أنا إن لم يرحم الله من أنا',NULL,'142',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (334,'٣٢٨ - وقال: عجبت من الدنيا ومن حبنا لها ... ولم تزل الدنيا تعرض للبغض
لهوت وساعات النهار أحثثته ... بلطف للإبرام مني وللنقض',NULL,'143',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (335,'٣٢٩ - وقال: وللدينا منى فاحذر مناها ... منى الدنيا مراتعها وخيمة
دع الدنيا لراضي الرتع فيها ... يعيش برتعه عيش البهيمة
وما زالت صروف الدهر تجري ... فمقلقة ومقعدة مقيمة
وغب الصبر عافية وروح ... وليس الصبر إلا بالعزيمة',NULL,'143',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (336,'٣٣٠ - حدثني أبو عمر الأزدي، قال: نظر رجل من العرب إلى أخيه وحرصه على الدنيا، فقال: أي أخي أنت طالب ومطلوب، يطلبك ما لا تفوته وتطلب ما قد كفتيه، فكان ما غاب عنك قد كشف لك، وما أنت فيه قد نقلت عنه، أي أخي كأنك لم تر حريصاً محروماً، ولا زاهداً مرزوقاً.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (337,'٣٣١ - حدثني أبو عمر الأزدي، قال: وعظ رجل من العرب ابناً له فقال:
يا بني إن الدنيا تسعى على من يسعى لها، ويسعى معها، فالهرب منها قبل العطب فيها، فقد والله آذنتك ببين، وانطوت لك على خنن.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (338,'٣٣٢ - أنشدني عمر بن علي بن هارون: إنما الدنيا حدور ... فعزيز وذليل
وأخو الفقر حقير ... وأخوا المال نبيل
وإذا ما الجد ولى ... عذب الرأي الأصيل
كل بؤس ونعيم فهو ... في الدنيا يزول ⦗١٤٤⦘
ثم يبقى الله والأعما ... ل والفعل الجميل',NULL,'143',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (339,'٣٣٣ - قال أبو بكر: قرأت في كتاب لداود بن رشيد بخطه دخل ابن السماك على هارون فقال: عظني وأوجز فقال:
ما أعجب يا أمير المؤمنين ما نحن فيه، كيف غلب علينا، وأعجب ما نصير إليه كيف غفلنا عنه، عجب لصغير حقير إلى الفناء يصير غلب على كثير طويل دائم غير زائل.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (340,'٣٣٤ - حدثني علي بن أبي مريم، عن أبي مسعود القتات، قال: قال ابن السماك:
إن الدنيا من أولها إلى آخرها قليل، وإن الذي بقي منها في جنب الذي مضى قليل، وإنما لك منها قليل، ولم يبق من قليلك إلا قليل، وقد أصبحت في دار الشراء، ودار الفداء، وغداً تصير إلى دار الجزاء ودار البقاء، فاشتر اليوم نفسك، وفادها بكل جهدك لعلك أن تخلص من عذاب ربك.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (341,'٣٣٥ - حدثني علي بن أبي مريم، عن أبي مسعود القتات، قال: قال ابن السماك:
إن الذي يخاف من شر الدنيا أعظم من الشر الذي نحن فيه منها، وإنما يوضح شر الدنيا عند الفراق لها إن صرنا إلى الهلاك بها.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (342,'٣٣٦ - حدثنا الفضل بن سهل، أخبرنا أبو النضر هاشم ⦗١٤٥⦘ بن القاسم، أخبرنا محمد بن طلحة، عن أبي غرارة، قال: مر على عبد الله بن عمر براذين عبد الله بن الزبير بمنى، وهي تروث الشعير فقال:
أما إن المعاد لو دان واحداً ما غلبونا على الدنيا كأنه يعزي نفسه.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (343,'٣٣٧ - حدثني أبو جعفر الضبي، حدثني حسين بن عبد الله، عن سفيان، قال: إن لم تدعوا الدنيا رغبة في الآخرة، فاتركوها اتقاء أن تكون مبارة، ومبارك أكثرها فيها منكم.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (344,'٣٣٨ - حدثني ابن أبي مريم، قال: قال سلمة بن غفار: قال سفيان:
إذا أردت أن تعرف قدر الدنيا فانظر عند من هي.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (345,'٣٣٩ - وحدثني ابن أبي مريم، عن خالد بن يزيد القرني، قال: أخبرنا فروة الخياط، عن رجل من البصرة يقال له: صالح، قال: سمعت فرقد السبخي يقول: ⦗١٤٦⦘ خدعتكم الدنيا وأبطرتكم، أما والله لتدعنها غير محمودين، ولا معروف لكم ذلك.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (346,'٣٤٠ - قال أبو بكر: قرأت في كتاب داود بن رشيد بخطه: حدثني أبو عبد الله الصوفي، قال: قال إبراهيم بن أدهم:
إنما زهد الزاهدون في الدنيا اتقاء أن يشاركوا الحمقى، والجهال في جهلهم.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (347,'٣٤١ - وقرأت في كتاب داود أيضاً: وحدثني أبو عبد الله قال: كتب عمر بن عبد العزيز إلى الحسن أن عظني وأوجز فكتب إليه الحسن:
أما بعد: فإن رأس ما هو مصلحك ومصلح به على يديك: الزهد في الدنيا، وإنما باليقين، واليقين بالتفكر، والتفكر بالاعتبار، فإذا أنت فكرت في الدنيا لم تجدها أهلاً أن تبيع بها نفسك، ووجدت نفسك أهلاً أن تكرمها بهوان الدنيا، فإنما الدنيا دار بلاء ومنزل غفلة.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (348,'٣٤٢ - وقرأت في كتاب داود بن رشيد، حدثني أبو عبد الله، قال: قال عيسى بن مريم:
طالب الدنيا مثل شارب ماء البحر كلما ازداد شرباً ازداد عطشاً حتى يقتله.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (349,'٣٤٣ - قال: وحدثني أبو عبد الله، قال: قال أبو المغيرة البصري:
لو أن عبداً شغل نفثة من نفثاته فأصاب بتلك النفثة الدنيا بما فيها كان هو المغبون في حاضرة القيامة.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (350,'٣٤٤ - قال: وقال عيسى بن مريم:
يا معشر الحواريين ازهدوا في الدنيا تمشوا فيها بلا هم.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (351,'٣٤٥ - قال: وقال عبد الله: قال أبو هاشم:
كانوا وإن كانت الدنيا في أيديهم كانوا فيها لله خزاناً، لم ينفقوها في ⦗١٤٧⦘ شهواتهم، ولا لذاتهم، كانوا إذا ورد عليهم حق من حقوق الله أمضوها فيه',NULL,'146',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (352,'٣٤٦ - وقرأت في كتاب داود بن رشيد قال بعض الحكماء: كل شيء فاتك من الدنيا غنيمة.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (353,'٣٤٧ - حدثنا محمد بن عبد الله المديني، أخبرنا إسماعيل بن عياش الحمصي، حدثني أبو راشد التنوخي، عن يزيد بن ميسرة، قال: كان أشياخنا يسمون الدنيا خنزيرة، ولو وجدوا لها اسماً شراً منه سموها به، وكانوا إذا أقبلت إلى أحدهم دنيا قالوا: إليك إليك يا خنزيرة، لا حاجة لنا بك، إنا نعرف إلهنا.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (354,'٣٤٨ - حدثنا الحسن بن عيسى، أنبانا عبد الله بن المبارك، أنبأنا معمر، ويونس، عن الزهري، أن عروة بن الزبير، أخبره أن المسور بن مخرمة، أخبره أن عمرو بن عوف، وهو حليف بني عامر بن لؤي، وكان شهد بدراً مع رسول الله، صلى الله عليه وسلم، أخبره: أن رسول الله صلى الله عليه وسلم، بعث أبا عبيدة بن الجراح إلى البحرين ليأتي بجزيتها، وكان رسول الله صلى الله عليه وسلم صالح أهل البحرين، وأمر عليهم العلاء بن الحضرمي، فقدم أبو عبيدة بمال من البحرين فسمعت الأنصار بقدوم أبي عبيدة، فوافوا صلاة الفجر مع رسول الله صلى الله عليه وسلم، فلما صلى رسول الله صلى الله عليه وسلم تعرضوا له فتبسم رسول الله صلى الله عليه وسلم، حين رآهم، ثم قال: ⦗١٤٨⦘ أظنكم علمتم أن أبا عبيدة قدم بشيء؟ قالوا: أجل يا رسول الله، قال صلى الله عليه وسلم: أبشروا، وأملوا ما يسركم، فوالله ما الفقر أخشى عليكم، ولكني أخشى أن تبسط الدنيا عليكم، كما بسطت على من كان قبلكم، فتتنافسوها كما تنافسوها، فتهلككم كما أهلكتهم',NULL,'147',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (355,'٣٤٩ - حدثني أبو جعفر القرشي، عن شيخ من قريش، قال: قال خالد بن صفوان:
بت أفكر فكسبت البحر الأخضر بالذهب الأحمر، ثم نظرت فإذا الذي يكفيني من ذلك رغيفان وطمران.
وزاد غيره:
فلما تدبرت أمري إذا أمنيتي أمنية أحمق.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (356,'٣٥٠ - وأنشدني الحسين بن عبد الرحمن إبراهيم بن داود في مثل ذلك: حاسبت نفسي فوجدت الذي ... من كل ما في الأرض يكفيها
قوتاً يقيم الصلب منها وإن ... قل وأطماراً تواريها
فإن هي استغنت بهذا الذي ... يكفي فإن الله مغنيها',NULL,'148',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (357,'٣٥١ - حدثنا خالد بن خداش، حدثني عبد العزيز بن أبي حازم، حدثني أبي عن عبد الله بولى، عن أبيه من أصحاب النبي صلى الله عليه وسلم،: أن رسول الله صلى الله عليه وسلم، أتى جبل الأحمر فرأى شاة ميتة، فأخذها بأنفنا، فقال: ⦗١٤٩⦘ أترون هذه كريمة على أهلها؟ قالوا: وما كرامتها؟ قال: فوالله للدنيا أهون على الله من هذه على أهلها.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (358,'٣٥٢ - حدثنا أبو خيثمة، أخبرنا إسماعيل بن إبراهيم، عن شعبة عن عمر بن سليمان عن عبد الرحمن بن أبان، عن أبيه، عن زيد بن ثابت: عن النبي صلى الله عليه وسلم قال: من كانت الآخرة نيته جمع الله له شمله، وجعل غناه في قلبه، وأتته الدنيا وهي راغمة، ومن كانت نيته الدنيا فرق الله عليه أمره، وجعل فقره بين عينيه، ولم يأته من الدنيا إلا ما كتب له.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (359,'٣٥٣ - قال أبو الحسن: أخبرنا الحارث بن أبي أسامة، أخبرنا أبو عبد الرحمن المقرئ، أخبرنا الربيع بن صبيح، ⦗١٥٠⦘ عن زيد الرقاشي، عن أنس بن مالك: أن رسول الله صلى الله عليه وسلم، قال:
من كانت نيته طلب الآخرة جعل الله غناه في قلبه، وجمع له شمله، وأتته الدنيا وهي راغمة، ومن كانت نيته طلب الدنيا جعل الله الفقر بين عينيه، وشتت عليه أمره، ولا يأتيه منها إلا ما كتب له.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (360,'٣٥٤ - حدثنا عبد الله، حدثني محمد بن يحيى بن أبي حاتم الأزدي، أخبرنا داود بن المحبر، أخبرنا همام بن يحيى، عن قتادة، عن أنس بن مالك، قال: قال: رسول الله صلى الله عليه وسلم:
من كانت الدنيا همه وسدمه، لها يشخص، ولها ينصب، وإياها ينوي، جعل الله عز وجل الفقر بين عينيه، وشتت عليه ضيعته، ولم يأته إلا ما كتب له، ومن كانت الآخرة همه وسدمه، لها يشخص، ولها ينصب، وإياها ينوي جعل الله عز وجل الغنى في قلبه، وجمع عليه ضيعته، وأتته الدنيا وهي صاغرة راغمة.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (361,'٣٥٥ - حدثنا عبد الله، حدثني محمد بن إدريس الحنظلي، أخبرنا ⦗١٥١⦘ المعلى بن أسد العمي، أخبرنا عبد العزيز بن المختار، عن موسى بن عقبة، حدثني بلال بن سعد التيمي، عن أبيه أن أبا الدرداء ذكر الدنيا، فقال: إنها ملعونة، ملعون ما فيها، إلا ما كان لله عز وجل، وما ابتغي به وجهه.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (362,'٣٥٦ - حدثنا عبد الله، حدثني يعقوب بن عبيد، أخبرنا أبو عاصم النبيل، عن محمد بن عمارة، عن عبد الله بن عبد الرحمن: أن النبي صلى اله عليه وسلم أتى بهدية، فالتمس في البيت شيئاً يضعه فيه، فقال:
ضعه بالحضيض، فلو كانت الدنيا تعدل عند الله عز وجل شيئاً، ما أعطى كافراً منها قدر جناح بعوضة.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (363,'٣٥٧ - حدثنا عبد الله، أخبرنا أحمد بن عيسى المصري، أخبرنا ⦗١٥٢⦘ عبد الله بن وهب، أخبرني يحيى بن أيوب، عن عيسى بن موسى، عن عبد الله بن محمد، عن أبي مرة مولى عقيل، عن أبي هريرة: عن النبي صلى الله عليه وسلم قال:
ما ذئبان جائعان ضاريان في غنم تفرق أحدهما في أولها، والآخر في آخرها، بأسرع منها فساداً من امرئ في دينه يبتغي شرف الدنيا ومالها.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (364,'٣٥٨ - حدثنا عبد الله، حدثني زيد بن إسماعيل بن سيار، أخبرنا معاوية بن عمرو، أخبرنا محمد بن بشر العبدي، أخبرنا مسلم الأعور، أخبرنا عبد الحميد بن أبي جعفر الفراء، قال: قال الحسن:
من أحب الدنيا حرصاً وسرته، خرج خوف الآخرة من قلبه، ومن ازداد علماً ثم ازداد على الدنيا حرصاً، لم يزدد من الله عز وجل إلا بعداً، ولم يزدد من الله إلا بغضاً.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (365,'٣٥٩ - حدثنا عبد الله، حدثني شجاع بن الأشرس، أخبرنا إسماعيل بن عياش، عن مطعم بن المقدام الصنعاني، وغيره، عن محمد بن واسع، ⦗١٥٣⦘ قال: كتب سلمان إلى أبي الدرداء: أي أخي: إياك أن تجمع من الدنيا ما لا تؤدي شكره، فإني سمعت رسول الله صلى الله عليه وسلم يقول:
يجاء بصاحب الدنيا الذي أطاع الله عز وجل فيها، وماله بين يديه، كلما تكفأ به الصراط، قال له ماله: امض فقد أديت حق الله عز وجل في، ثم يجاء بصاحب الدنيا الذي لم يطع الله فيها، وماله بين كفيه، كلما تكفأ به الصراط، قال له ماله: ويلك ألا أديت حق الله عز وجل في، فما يزال كذلك، حتى يدعو بالويل والثبور.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (366,'٣٦٠ - حدثنا عبد الله، أخبرنا هارون بن عبد الله، أخبرنا سيار، أخبرنا جعفر، أخبرنا مالك بن دينار، قال: قال: أبو هريرة: الدنيا موقوفة ما بين السماء والأرض كالشن البالي، تنادي بهذا منذ يوم خلقها إلى يوم فنائها: يا رب لم تبغضني؟ يا رب لم تبغضني؟ فيقول لها: اسكتي يا لا شيء، اسكتي يا لا شيء.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (367,'٣٦١ - حدثنا عبد الله، أخبرنا شجاع بن الأشرس، أخبرنا إسماعيل بن عياش، حدثني عبد الله بن دينار البهراني، وغيره: أن المسيح عليه السلام كان يقول لأصحابه:
بحق أقول لكم، إن شركم عملاً عالم يحب الدنيا، ود لو أن الناس كلهم ⦗١٥٤⦘ كانوا في عمله مثله، ما أحب إلى عبيد الدنيا لو يجدون معذرة، وما أبعدهم منها لو كانوا يعلمون.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (368,'٣٦٢ - حدثنا عبد الله، أخبرنا صالح بن مالك، أخبرنا عبيد الله أبو مسلم الجعفي قائد الأعمش عن الأعمش، عن إبراهيم، قال: قال عبد الله بن مسعود:
دخلت على رسول الله صلى الله عليه وسلم، وهو نائم في غرفة له، وكأنها بيت حمام، فإذا هو نائم على حصير قد أثر بجلده، فجعلت أبكي وأمسح عنه وأبكي، فقال: يا عبد الله ما يبكيك؟ .
قلت: يا رسول الله ذكرت كسرى وقيصر يفترشان الحرير والديباج.
فقال: أما ترضى أن تكون لهم الدنيا، ولنا الآخرة، ما أنا والدنيا إلا كمثل رجل مر في يوم صائف، فاستظل تحت شجرة، فلما أبرد ارتحل، وذهب.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (369,'٣٦٣ - حدثنا عبد الله، أخبرنا يحيى بن سليمان المحاربي، أخبرنا مسعر بن كدام، حدثني عون بن عبد الله بن عتبة قال: كانوا يتواصون فيما بينهم بثلاثة أحرف، يكتب بها بعضهم إلى بعض: من عمل لله كفاه الله الناس ومن عمل لآخرته كفاه الله دنياه، ومن أصلح سريرته أصلح الله له علانيته.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (370,'٣٦٤ - حدثنا عبد الله، حدثني إبراهيم بن يعقوب، قال: قال العمري عبد الله بن عبد العزيز:
الزهد: الرضا.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (371,'٣٦٥ - حدثني عبد الله قال: حدثني من سمع أحمد بن أبي الحواري، قال: سمعت أبا سليمان الداراني، قال:
الورع أول الزهد، والقناعة أول الرضا.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (372,'٣٦٦ - قال أحمد: وقلت لأبي هشام عبد الملك المغازلي: أي شيء الزهد؟
قال: قطع الآمال، وإعطاء المحمود، وخلع الراحة.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (373,'٣٦٧ - قال أبو بكر: وزعم إسحاق بن إبراهيم أن أيوب بن شبيب، قال: حدثني محمد بن ثور، عن أبي حنيفة، وليس بصاحب الرأي، عن أبي السحماء، قال: بينا أنا أسير بين الإسكندرية والفسطاط، إذا برجل على فرس، فقال: يا أبا السحماء ما تعدون الزهد فيكم؟ قال: قلت: ترك هذا الحطام.
قال: لا، ولكن هو ان يتواجد الرجل في المكان الذي يرجو أن يراه الله فيه فيرحمه.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (374,'٣٦٨ - حدثني الحسن بن عبد العزيز الجروي، قال: كان أبو السحماء الكلبي قد بلغ من الدنيا والسلطان مبلغاً، ثم عزم على الزهد فيها، فترك ذلك أجمع، وأقبل على العبادة والنسك.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (375,'٣٦٩ - قال: وأخبرني الحارث بن مسكين: أنه خرج مرة إلى الإسكندرية ⦗١٥٦⦘ فنزل منزلاً، فقال: الحمد لله استرحنا من صحبة الملوك، نمد أرجلنا إذا شئنا ونبكي إذا شئنا، ونعمل ما أردنا.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (376,'٣٧٠ - حدثنا الحسن بن عبد العزيز، حدثنا عمرو بن أبي سلمة، عن سعيد بن عبد العزيز: أن عيسى بن مريم عليه السلام قال:
من ذا الذي يبني على موج البحر داراً، تلكم الدنيا فلا تتخذوها قراراً.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (377,'٣٧١ - حدثني علي بن الحسن بن أبي مريم، عن داود بن عبيد الله بن مسلم الحنفي قال: كان بعض الحكماء يقول في كلامه:
في كل حال تلقى الدنيا مختمرة متنكرة، حتى إذا هبطت دياراً لها، كشفت قناعها، وتحسرت، فانتصبها العاملون مثالاً لأنفسهم، فنظروا فيها بالعبر، وقطعوا قلوبهم كمداً خرج إليها بالفكر في الغير، أولئك الذين أنزلوا الدنيا حق منزلتها، فهم فيها أهل كلال ووصب، قد ذوبوا الأجساد، وأظمأوا الأكباد خوفاً، أن يحل بهم ما يحل بالهالكين قبلهم، الذين أناخت الدنيا في ديارهم، فأشعرتهم من طوارق مثلها، ما صاروا بذلك عبراً وحديثاً للباقين من بعدهم، فالقوم في مناجاة العزيز بالاستكانة له، والتذلل والتضرع إليه، والاستعاذة به من شر ما تهجم به الدنيا على أوليائها، والرغبة إليه في الخلاص من ذلك، لا يستكثرون له من أنفسهم طاعة، ولو ماتوا قياماً على الأعقاب متعبدين، ولا يستصغرون من أنفسهم إلى الدنيا من المعاصي لحظة، ولو كانوا أيام حياتهم عنها معرضين، ملأت الآخرة قلوبهم، فليس لأنفسهم عندهم في الدنيا راحة، أولئك الذين اتصلت قلوبهم بمحبة وصف سيدهم دار القرار، فعلقوا من الوصف بأوهام العقول، ما استطارت لذلك قلوبهم، وغشيت عن غيره أبصارهم، فعيشهم في الدنيا منغوص، وحظها منها عند أنفسهم ⦗١٥٧⦘ منقوص، ينظرون إليها بعين الرهبة منها، فإذا ذكرت عندهم الآخرة جاءت الرغبة فطاشت عندها العقول.
قال: وكان يقول:
إن الدنيا كأس سكرات، أماتت شاربيها وهم أحياء، فعموا وهم يبصرون وصموا وهم يسمعون، وخرسوا وهم ينطقون.
قال: وكان يقول:
ليت الدنيا لم تخلق، وليتها إذا خلقت لم أخلق:
قال: وكان يقول:
تصرعنا ونثق بها، وترينا عبرها فنواريها عن أنفسنا، فيا عجباً كل العجب من زاهد فيك، وأنت ترغبين فيه، ويا عجباً كل العجب من ماقت لك، وأنت له محبة.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (378,'٣٧٢ - أنشدني أبو جعفر القرشي: أيهاالنائم الذي عينه الدهر نائمة ... أيقظ العين إنها بالأماني حالمة
لا تغرنك الحياة بدنيا مسالمة ... إنها بعد سلمها ذات يوم مراغمة',NULL,'157',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (379,'٣٧٣ - وأنشدني أبو جعفر: احذر من الدنيا تعبثها فكم ... من صالح عبثت به ففسد
ما بين فرحتها وترحتها ... إلا كما قام امرؤ وقعد
يا ذا المزوق دار ملك بل ... مضروبة مثلاً لدار أبد
كم من أخ لك مات مستلب ... كشهاب ضوء لاح ثم خمد',NULL,'157',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (380,'٣٧٤ - حدثني محمد بن إدريس، أخبرنا أحمد بن عبد الله بن عياض القرشي، أخبرنا عبد الوهاب بن همام، أخبرنا عبد الصمد بن معقل عن ⦗١٥٨⦘ وهب قال: قرأت في كتاب شعيا أنه قيل ليونس بن متى:
يا يونس إذا أحب العالم الدنيا نزعت لذة مناجاتي من قلبه.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (381,'٣٧٥ - حدثني محمد بن إدريس، أخبرنا علي بن ميسرة الرازي، أخبرنا عبد العزيز بن أبي عثمان، حدثني عثمان بن زائدة عن عمران القصير أنه قال: ألا صابر كريم لأيام قلائل، حرام على قلوبكم أن تجد طعم الإيمان، حتى تزهدوا في الدنيا.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (382,'٣٧٦ - حدثني محمد بن إدريس سمعت العباس بن الجلال يقول: قال سابق البربري:
أصبحتم جزراً للموت يأخذكم ... كما أن البهائم في الدنيا لكم جزر
وليس يزجركم ما توعدون به ... والبهم يزجرها الراعي فتنزجر
ما يشعرون بها في دينهم نقصوا ... جهلاً وإن نقصت دنياهم شعروا
أبعد آدم يرجون الخلود وهل ... يبقى فروع لأصل حين ينقعر
لا ينفع الذكر قلباً قاسياً أبداً ... والحبل في الحجر القاسي له أثر',NULL,'158',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (383,'٣٧٧ - حدثني سلمة بن شبيب، عن زهير بن عباد الرواسي، عن داود بن هلال النصيبي، قال: قال عيسى بن مريم عليه السلام:
ويلكم علماء السوء من أجل دنيا دنية، وشهوة رزية، تفرطون في ملك الجنة، وتنسون هول يوم القيامة.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (384,'٣٧٨ - حدثني سلمة بن شبيب، عن عبد الوهاب بن نجدة، عن ⦗١٥٩⦘ بقية بن الوليد، عن ضبارة بن عبد الله الألهاني، عن دويد بن نافع، قال: قال عيسى بن مريم عليه السلام:
تعملون لدنيا صغيرة وتتركون الآخرة الكبيرة، وعلى كلكم يمر الموت.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (385,'٣٧٩ - وحدثني سلمة، عن آدم بن أبي إياس، عن المبارك بن فضالة، عن الحسن قال: والله ما أصبح في الدنيا ما يغر ذا قلب، وكلكم ذو قلب، ولكن ما يغر ذا قلب حي.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (386,'٣٨٠ - وحدثني سلمة، أنه حدث عن عبد الله بن وهب، عن عبد الرحمن بن زيد بن أسلم، قال: الخاسر من عمر دنياه بخراب آخرته، والخاسر من استصلح معاشه بفساد دينه، والمغبون حظا من رضي بالدنيا من الآخرة، فإنه قال لقوم: {إن الذين لا يرجون لقاءنا ورضوا بالحياة الدنيا واطمأنوا بها} .
[يونس: ٧] .',NULL,'159',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (387,'٣٨١ - حدثني سلمة، أخبرنا سهل بن ⦗١٦٠⦘ عاصم، قال: قال الأصمعي:
كان يقال خبر الدنيا أشد من مختبرها، ومختبر الآخرة أشد من خبرها.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (388,'٣٨٢ - حدثني سلمة، أخبرنا سهل بن عاصم، أخبرنا عبدة بن سليمان، قال: قال خالد بن يزيد بن معاوية:
ابن آدم لا يلهك أهل، إنما أنت فيهم ضعيف غير أهل، لا تزايلهم، ولا تلهينك مساكن، إنما أنت فيهم عمرى عن مساكن أنت مخلد فيها أبداً.
ابن آدم إنك إنما تسكن يوم القيامة في بيت اليوم، وتنزل يومئذ على ما نقلت في حياتك من متاعك.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (389,'٣٨٣ - حدثني سلمة بن شبيب، عن أحمد بن أبي الحواري، قال: قال لي أبو عبد الله الناجي: تدري أي شيء قلت البارحة يا أحمد؟ قلت:
إنه قبيح بعبد ضعيف مثلي يعلم عظيماً مثلك ما لا يعلم، إنك تعلم إني لو جعلت لي الدنيا كلها من أولها إلى آخرها حلالاً لقذرتها، ولم أردها.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (390,'٣٨٤ - حدثني سلمة بن شبيب، عن زهير بن عباد، عن داود بن هلال، قال: أوحى الله إلى داود:
ما لقلوب أحبائي وما للغم بالدنيا، إن الغم بها يمص حلاوة مناجاتي من ⦗١٦١⦘ قلوبهم مصاً، داود لا تجعل بيني وبينك عالماً قد أسكرته الدنيا، فيحجبك بسكره عن محبتي، أولئك قطاع طريق عبادي المريدين.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (391,'٣٨٥ - حدثني سلمة بن شبيب، عن عبد الله بن عمر الواسطي، عن أبي الربيع الأعرج، عن شريك، عن جابر، قال: قال لي محمد بن علي:
يا جابر إني لمحزون، وإني لمشتغل القلب، قلت: وما حزنك وشغل قلبك؟ قال: يا جابر إنه من دخل قلبه صافي خالص دين الله شغله عما سواه، يا جابر ما الدنيا؟ وما عسى أن تكون؟ هل هو إلا مركب ركبته، أو ثوب لبسته، أو امرأة أصبتها، يا جابر إن المؤمنين لم يطمئنوا إلى الدنيا ببقاء فيها، ولم يأمنوا قدوم الآخرة، لم يصمهم عن ذكر الله ما سمعوا بآذانهم من الفتنة، ولم يعمهم عن نور الله ما رأوا بأعينهم من الزينة، ففازوا بثوب الأبرار، إن أهل التقوى أيسر أهل الدنيا مؤنة، وأكثر لله معونة، إن نسيت ذكروك، وإن ذكرت أعانوك، قوالين بحق الله، قوامين بأمر الله، قطعوا محبتهم لمحبة ربهم، ونظروا إلى الله ومحبته بقلوبهم، وتوحشوا من الدنيا بطاعة مليكهم، وعلموا أن ذلك منظور إليه من شأنه، وأنزل الدنيا بمنزل نزلت به وارتحلت منه، أو كمال أصبته في منامك فاستيقظت، وليس معك منه شيء، واحفظ الله عز وجل ما استرعاك من دينه وحكمته.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (392,'٣٨٦ - حدثني علي بن الحسن بن أبي مريم، عن الحسين بن زياد المروزي، قال: قال معدان:
اعمل للدنيا على قدر مكثك فيها، واعمل للآخرة على قدر مكثك فيها.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (393,'٣٨٧ - حدثني علي بن أبي مريم، عن شيخ له، عن يوسف بن أسباط، قال: قال لي زرعة: ⦗١٦٢⦘
من كان صغير الدنيا في عينيه أعظم من كبير الآخرة، كيف يرجو أن يصنع له في دنياه وآخرته.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (394,'٣٨٨ - حدثنا روح بن عبد الرحمن، أخبرنا صالح بن عبد الكريم، قال: قال بعض الحكماء:
إنما نسلم من الدنيا فيها، فمن أخذ منها لها خرج منه، وحوسب عليه، ومن أخذ منها لغيرها قدم عليه، وأقام فيه.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (395,'٣٨٩ - حدثني الحسين بن عبد الرحمن، عن بعض أشياخه، قال: قال الحسن:
إنما الدنيا غموم وهموم، فإذا رأى أحدكم منها سروراً فهو ربح.',NULL,'162',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (396,'٣٩٠ - أنشدني أحمد بن موسى البصري، قوله: أشكو إلى الله نفساً ما تلائمني ... تبغي هلاكي ولا آلو أناجيها
ما إن تزال تناجيني بمعصية ... فيها الهلاك وإني لا أواتيها
أعيت وأعييتها تأبى وأقذعها ... وربما غلبتني ثم أثنيها
أخيفها بوعيد الله مجتهداً ... وليس تنفك يلهيها ترجيها
بل قل لموطن دار لا يقربها ... كأنه خالد فيها يعانيها
أهل رأيت سليماً من بوائقها ... أم هل سمعت بحي خالد فيها
أما تخاف ذنوباً جمة سلفت ... أنسيت عدتها والله يحصيها
يا رب سيئة باشرت منكرها ... فبت تظهرها والله يخفيها
وأنت في كل يوم مبصر عبراً ... مناً من الله تحذيراً وتنبيها
أما ترى الموت ما ينفك مختطفاً ... من كل ناحية نفساً فيحويها
قد نغصت أملاً كانت تؤمله ... وقام في الحي ناعيها وباكيها
وأسكنوا الترب تبلى فيه أعظمهم ... بعد الغضارة ثم الله يحييها
وصار ما جمعوا منها وما ادخروا ... بين الأقارب تحويه أدانيها
فامهد لنفسك في أيام مدتها ... واستغفر الله مما أسلفت فيها',NULL,'162',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (397,'٣٩١ - حدثني عبد الرحمن بن صالح، حدثني شعيب بن راشد، عن أبي روح الأنصاري، قال: كان من دعاء الحسين:
اللهم ارزقني الرغبة في الآخرة حتى اعرف صدق ذلك في قلبي بالزهد مني في دنياي، اللهم ارزقني بصراً في أمر الآخرة حتى أطلب الحسنات شوقاً، وأفر من السيئات خوفاً من ربي.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (398,'٣٩٢ - حدثني أبو العباس الأزدي عبيد الله بن جرير، أخبرنا محمد بن أبي بكر، قال: قال ابن السماك: كان يقال كل شيء فاتك من الدنيا غنيمة.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (399,'٣٩٣ - قال: وذكر سعيد بن أبي الحسن الدنيا، فقال الحسن: يا سعيد سهوت حتى ذكرت الدنيا.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (400,'٣٩٤ - قال: وقال الحسن:
لو لم تكن لنا ذنوب إلا حبنا الدنيا خشينا أن يعذبنا الله.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (401,'٣٩٥ - قال: وقال رجل لإخوانه:
تعالوا حتى نستغفر الله من شيء لا يستغفر الناس منه، حبنا للدنيا.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (402,'٣٩٦ - قال: وكان يقال: إنما ساء العمل من طول الأمل.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (403,'٣٩٧ - وحدثني عبيد الله الأزدي، أخبرنا محمد بن أبي بكر، أخبرنا ⦗١٦٤⦘ بشر بن عباد، عن الأسود بن شيبان، حدثني خالد بن شمير، قال: مر ابن عمر بمكة، فإذا نجدة وابن الزبير متصافين بالبطحاء، فقال: ما هذا؟ قالوا: هذه نجدة وابن الزبير، قال: لقد أعظم هؤلاء الدنيا.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (404,'٣٩٨ - حدثني أبو إسحاق الأزدي، أخبرنا زيد بن عوف، أخبرنا شيخ يقال له: الفضل بن داود، عن أبي عمران، أخبرنا شيخ كان ينزل مصر، قال: أوحى الله عز وجل إلى داود:
لا تجعل بيني وبينك عالماً قد سكن قلبه حب الدنيا، إن أهون ما أعاقبهم به أن أنزع حب مناجاتي من قلوبهم.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (405,'٣٩٩ - حدثني أبو الفضل العباس الدوري مولى بني هاشم، أخبرنا الحسن بن الربيع، أخبرنا جعفر بن سليمان الضبعي، عن الربيع بن صبيح، عن يزيد الرقاشي، عن أنس بن مالك، قال: قال رسول الله، صلى الله عليه وسلم:
من كانت الدنيا همه جعل الله فقره في قلبه، وشتت عليه أمره، ولم يأته منها إلا ما كتب له، ومن كانت الآخرة أكبر همه جعل الله غناه في قلبه، وجمع له شمله، وأتته الدنيا وهي راغمة.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (406,'٤٠٠ - وحدثني الفضل، أخبرنا محمد بن الطفيل، قال: سمعت الفضيل بن عياض، يقول: ⦗١٦٥⦘
حزن الدنيا للدنيا يذهب بهم الآخرة، وفرح الدنيا للدنيا يذهب بحلاوة العبادة.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (407,'٤٠١ - حدثنا الحارث بن محمد العمي، أخبرنا سعيد بن عامر، أخبرنا هشام صاحب الدستوائي، قال: قرأت في كتاب بلغني أنه من كلام عيسى عليه السلام:
تعملون للدنيا وأنتم ترزقون فيها بغير العمل، ولا تعملون للآخرة وأنتم لا ترزقون فيها إلا بالعمل، ويلكم علماء السوء! الأجر تأخذون، والعمل تضيعون، يوشك رب العمل أن يطلب عمله، وتوشكون أن تخرجوا من الدنيا العريضة إلى ظلمة القبر وضيقه، الله نهاكم عن الخطايا كما أمركم بالصيام والصلاة، كيف يكون من أهل العلم من سخط رزقه، واحتقر منزلته، وقد علم أن ذلك من علم الله وقدرته؟ كيف يكون من أهل العلم من اتهم الله فيما قضى له، فليس يرضى شيئاً أصابه؟ كيف يكون من أهل العلم من دنياه آثر عنده من آخرته، وهو مقبل في دنياه أفضل رغبة؟ كيف يكون من أهل العلم من مسيره إلى آخرته، وهو مقبل على دنياه؟ وما يضره أحب إليه مما ينفعه؟ كيف يكون من أهل العلم من يطلب الكلام ليخبر به الناس، ولا يطلب الكلام ليعمل به.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (408,'٤٠٢ - أنشدني شيخ لنا: سل الأجداث عن صور بلينا ... وعن خلق نعمن فصرن طينا
وعن ملك تعذر بالأماني ... وكان يظن أن سيعيش حينا
فجاد بنفسه للموت لما أتاه ... وكان بجودها أبداً ضنينا
فصار على اليمين إلى التنادي ... بلا حراك المقلب لليمينا
لقد أبت القبور على شفيق ... أتاها أن تفك له رهينا
هي الدنيا تفرق كل جمع ... وإن ألف القرين بها القرينا',NULL,'165',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (409,'٤٠٣ - حدثني محمد بن حاتم قال: سمعت ⦗١٦٦⦘ قبيصة قال: سمعت الثوري يقول:
خير الدنيا لكم ما لم تبتلو به منها، فإذا ابتليتم بها فخيرها لكم ما خرج من أيديكم منها.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (410,'٤٠٤ - حدثني صالح بن مالك أخبرنا أبو عبيدة الناجي عن الحسن قال: إنكم أصبحتم في دار مذمومة لأهلها، خلقت فتنة وضرب لها أجل، إذا انتهت إليه تنفد، فهي دار قلعة ومنزل بلغة، أخرج نباتها وبث فيها من كل دابة، ثم أخبرهم خبر الذي هم إليه صائرون، وأمر فيه عباده بما أخرج لهم من ذلك بطاعته، وبين لهم سبيلها، ووعدهم الخير عليه، فهم في قبضته فليس منهم معجز له من أعمالهم شيء يخفى عليه، فهم يعملون أعمالاً مختلفة، شعبهم فيها شتى، بين عاص ومطيع، ولكل جزاء من الله بما عمل، ونصيب غير منقوص، ولم أسمع الله عز وجل فيما عهد إلى عباده، وأنزل عليهم من كتابه، رغب في الدنيا أحداً من خلقه، ولا رضي لهم بالطمأنينة فيها، ولا الركون إليها، بل صرف الله فيها الآيات، وضرب الأمثال لها في العيب لها، والنهي عنها، والرغبة في غيرها، وقد تبين للصالحين من عباد الله أن الأمر الذي خلقت له الدنيا وأهلها عظيم الشأن هائل المطلع، غير والله شبيه بما هم فيه، ولا يشبه ثوابهم ولا عقابهم، ولكنها دار الخلود يدين الله العباد بأعمالهم، وينزلهم منازلهم، ثم لا يتغير بؤس عن أهله، ولا نعيم، وأن الدنيا دار عمل من صحبها بالبغض لها، والزهادة فيها، والهضم لها، سعد بحظه من الله، ومن صحبها بالحب لها والرغبة فيها، خسر حظه عند الله، ثم أسلمته إلى ما لا صبر له عليه، ولا طاقة له به من عذاب الله وسخطه، فأمرها صغير، ومتاعها قليل، والفناء عليها مكتوب، والله ولي ميراثها، وأهلها متحولون عنها إلى منازل لا تبلى، ولا يغيرها طول العمر فيها بفناء فيموتون، ولا وإن طال الثواء فيها يخرجون، فاحذروا ذلك الموطن، وأكثروا ذكر المنقلب، ولذلك ⦗١٦٧⦘ فاعدد، ومن شره فاهرب، ولا يلهينك المتاع الفاني، واقطع ابن آدم من الدنيا أكبر همك، وبادر أجلك، ولا تقل غداً غداً فإنك لا تدري متى إلى الله تصير، ولا تكن يا بن آدم مغتراً، ولا تأمن ما لم يأتك الأمان منه، فإن الهول الأعظم، ومفظعات الأمور أمامك لم تخلص منهن حتى الآن، ولا بد من ذلك المسلك، وحضور تلك الأمور كلها فإنما بعافية من شرها، ونجاة من هولها، وإما بهلكة فليس بعدها خير ولا انتعاش.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (411,'٤٠٥ - حدثني صالح بن مالك أخبرنا أبو عبيدة الناجي عن الحسن قال: ابن آدم لا تعلق قلبك بالدنيا فتعلقه بشر معلق، قطع حبالها، وغلق أبوابها، حسبك أيها المرء ما بلغك المحل، حمقاً تباهي بمالك، وحمقاً تباهي بولدك، وأنت في غم الساعة، هيهات هيهات، ذهبت الدنيا لحال، وبقيت الأعمال قلائد في أعناق بني آدم.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (412,'٤٠٦ - قال بعض حكماء الشعراء: أبا لمنزل الفاني تؤمل أن تبقى ... كفاك بما ترجو وتأمله خرقا
رأيت قوى الدنيا يزيد انتقاصها ... ويدعو إليه صفو لذاتها الرنقا
وفي كل يوم محدث لك فرقة ترى ... خطبها خطباً جليلاً وإن دقا
لعمرك ما الدنيا بباقية ولا بها ... أحد يبقى فتطمع أن تبقى',NULL,'167',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (413,'٤٠٧ - وقال حكيم من الشعراء: بان منه الشباب فهو كثيب ... وعلا العارضين منه مشيب
ليت شعري ماذا أرجي من الدنيا ... ولم يبق لي عليها حبيب
أفردتني الخطوب من أهل ودي ... حسرتي ما تريد مني الخطوب
كل يوم لي من خليل فراق ... أي عيش مع الفراق يطيب',NULL,'167',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (414,'٤٠٨ - حدثني أبو محمد التميمي، قال: قال ابن السماك:
كأن المعمور من هذه الدنيا قد ارتحل عنه، وكأن المغفول من الآخرة قد أناخ بأهله فثم، فضع الهموم.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (415,'٤٠٩ - حدثني الحسن بن عبد العزيز، أخبرنا أبو مسهر، أخبرنا سعيد بن عبد العزيز: أن عيسى عليه السلام نظر إلى إبليس، فقال:
هذا أركون الدنيا إليها خرج، وإياها سأل، لا أشركه في شيء منها، ولا حجراً أضعه تحت راسي، ولا أكثر فيها ضاحكاً حتى أخرج منها.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (416,'٤١٠ - حدثني هارون بن إبراهيم الإمام، أخبرنا أبو سعيد البجلي، عن إسماعيل بن أبي خالد، قال: مر إبليس بعيسى بن مريم وهو متوسد حجراً، فقال له: يا عيسى قد رضيت من الدنيا بهذا الحجر؟ قال: فأخذ من تحت رأسه فقذف به إليه، فقال: هذا لك مع الدنيا لا حاجة لي فيه.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (417,'٤١١ - حدثنا الهيثم بن خارجة، أخبرنا عبد الله بن عبد الرحمن، قال: سمعت عمير بن هانئ العنسي، قال:
قلت لابن عمر: كيف تقول فينا، وفي هؤلاء؟ قال: ما أنا لكم بحامد، ولا لهم بغادر، أنتم أصحاب دنيا تنافستموها بينكم، تهافتون في النار تهافت الذباب في المرق.
قال: قلت: أرأيت؟ قال: إن شئت، قلت: أرأيت، أرأيت، ألك رحل؟ انطلق إلى رحلك.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (418,'٤١٢ - حدثني محمد بن إدريس، أخبرنا عبدة بن سليمان، أخبرنا عبد ⦗١٦٩⦘ الله بن المبارك، قال: قال سلام بن أبي مطيع:
الزهد على ثلاثة وجوه: واحد أن يخلص العمل لله عز وجل، والقول، ولا يراد بشيء منه الدنيا، والثاني: ترك ما لا يصلح، والعمل بما يصلح، والثالث: الحلال أن تزهد فيه، وهو تطوع، وهو أدناها.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (419,'٤١٣ - حدثني محمد بن إدريس، أخبرني عبد الحميد بن صالح، أخبرنا قطري الخشاب، عن عبد الوارث، عن أنس بن مالك، قال: قال رسول الله صلى الله عليه وسلم:
إذا كان يوم القيامة صارت أمتي ثلاث فرق: فرقة يعبدون الله عز وجل للدنيا، وفرقة يعبدونه رياء وسمعة، وفرقة يعبدونه لوجهه ولداره، فيقول للذين كانوا يعبدونه للدنيا: بعزتي وجلالي ومكاني، ما أردتم بعبادتي؟ فيقولون: بعزتك وجلالك ومكانك، الدنيا.
فيقول: إني لم أقبل من ذلك شيئاً، اذهبوا بهم إلى النار.
ويقول للذين كانوا يعبدونه رياء وسمعة: بعزتي وجلالي ومكاني، ما أردتم بعبادتي؟ فيقولون: بعزتك وجلالك ومكانك، رياء وسمعة.
قال: فإني لم أقبل من ذلك شيئاً، اذهبوا بهم إلى النار.
قال: ويقول للذين كانوا يعبدونه لوجهه ولداره: بعزتي وجلالي ومكاني، ما أردتم بعبادتي؟ فيقولون بعزتك وجلالك ومكانك، لوجهك ولدارك.
فيقول: صدقتم، اذهبوا بهم إلى الجنة.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (420,'٤١٤ - حدثني محمد بن إدريس، أخبرنا ابن أبي ليلى، أخبرنا موسى أبو محمد المدني، مولى عثمان بن عفان، عن خالد بن يزيد بن عبد الرحمن، عن ⦗١٧٠⦘ أبيه، عن جده: أن علي بن أبي طالب قال في خطبته:
أوصيكم بتقوى الله، والترك للدنيا التاركة لكم، وإن كنتم لا تحبون تركها المبلية أجسامكم، وإن كنتم تريدون تجديدها، فإنما مثلكم ومثلها كمثل سفر سلكوا طريقاً، فكأنهم قد قطعوه، أو أفضوا إلى علم فكأنهم قد بلغوه، وكم عسى أن يجري المجرى حتى ينتهي إلى الغاية، وكم عسى أن يبقى من له يوم من الدنيا، وطالب حثيث يطلبه حتى يفارقها، فلا تجزعوا لبؤسها، وضرائها، فإنه إلى انقطاع، ولا تفرحوا بنعيمها فإنه إلى زوال، عجبت لطالب الدنيا، الموت يطلبه، وغافل ليس بمغفول عنه.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (421,'٤١٥ - حدثني محمد بن إدريس، أخبرنا عبدة بن سليمان، أخبرنا آدم أخبرنا أبو عاصم إمامنا بعابدان، عن سلم بن بشير قال: إن الحواريين قالوا لعيسى عليه السلام: يا روح الله علمنا عملاً واحداً يحببنا إلى الله عز وجل؟ قال: ابغضوا الدنيا يحببكم الله.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (422,'٤١٦ - حدثني محمد بن إدريس، أخبرنا هريم بن عثمان، عن سلام بن مسكين، عن مالك بن دينار، قال: حب الدنيا رأس كل خطيئة والنساء حبالة الشيطان والخمر داعية كل شر.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (423,'٤١٧ - حدثني علي بن أبي مريم، عن أبي يزيد الرقي، عن يوسف بن أسباط، قال: من صبر على الأذى، وترك الشهوات، وأكل الخبز من حلاله، فقد أخذ بأصل الزهد.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (424,'٤١٨ - وحدثني علي، قال: سئل بعض الحكماء عن الزهد فقال:
إن من أدنى الزهد أن يقعد أحدكم في منزله، فإن كان قعوده لله وإلا خرج، ⦗١٧١⦘ ويخرج فإن كان خروجه لله رضي، وإلا رجع، فإن كان رجوعه لله رضي وإلا ساح، ويخرج درهمه فإن كان إخراجه لله رضي وإلا حبسه، ويحبسه فإن كان حبسه لله رضي وإلا رمى به، ويتكلم فإن كان كلامه لله رضي وإلا سكت، ويسكت فإن كان سكوته لله رضي وإلا تكلم.
فقيل له: هذا صعب؟ فقال: هذا الطريق إلى الله عز وحل، وإلا فلا تتعبوا.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (425,'٤١٩ - حدثنا محمد بن عبيد الله، أخبرنا يونس بن محمد، أخبرنا المعتمر بن سليمان، قال: كتب ليث: من ليث بن أبي سليم إلى سليمان بن طرخان:
سلام عليك، فإني أحمد إليك الله الذي لا إله إلا هو العلي العظيم، وأشهد أن محمداً عبده ورسوله أما بعد: فإني أوصيك بتقوى الله، فإن المتقي ينفعه من عمله ما قل منه أو كثر، جعلنا الله وإياك برحمته من المتقين، كتبت إليك ونحن ومن قبلنا أهلنا وإخواننا على ما كان من شيء بنعمة الله وعافيته، فله الحمد.
أتاني كتابك تذكر فيه ما ليس يخفى على ذي عقل، ولا قوة إلا بالله، قد أعلم أن الرسل إنما بعثت بهدم الدنيا، وبناء الآخرة، والناس فيها حدثني من أدرك أصحاب الرسول صلى الله عليه وسلم، أنهم قالوا: كنا إذا أسلمنا أقبلنا إلى الآخرة، وتركنا الدنيا لأهل الشرك، وإن الناس اليوم أقبلوا على أمر دنياهم، وتركوا أمر آخرتهم.',NULL,'171',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (426,'٤٢٠ - حدثني الحسين بن عبد الرحمن، حدثني إبراهيم بن رجاء، قال: سمعت ابن السماك يقول:
الناس ثلاثة: زاهد، وصابر، وراغب، فأما الزاهد فأصبح قد خرجت الأفراح ⦗١٧٢⦘ والأحزان من صدره عن اتباع هذا الغرور، فهو لا يفرح بشيء من الدنيا آتاه، ولا يحزن على شيء من الدنيا فاته، ولا يبالي على عسر أصبح، أم على يسر، فهذا المبرز في زهده، وأما الصابر: فرجل يشتهي الدنيا بقلبه، ويتمناها بنفسه، فإذا ظفر بشيء منها ألجم نفسه عنها كراهة شتاتها، وسوء عاقبتها، فلو تطلع على ما في نفسه عجبت من نزاهته وعفته.
أما الراغب: فلا يبالي من أين أتته الدنيا، ولا يبالي دنس فيها عرضه، او وضع فيه حسبه، أو جرح دينه، فهؤلاء في غمرة يضطربون، وهؤلاء أنتن من أن يذكروا.',NULL,'171',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (427,'٤٢١ - أنشدني الحسين بن عبد الرحمن: وطالبا حاجة الدنيا قد اختلفا ... وطالما اختلفت بالناس حالاتها
فطالب ليربح النفس أوبقها ... وطالب ليربح النفس عناها',NULL,'172',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (428,'٤٢٢ - حدثنا محمد بن عمارة الأسدي، أخبرنا محمد بن طفيل، أخبرنا حماد بن زيد، عن هشام، عن الحسن، قال: دخولك على أهل السعة مسخطة.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (429,'٤٢٣ - وحدثنا محمد بن عمارة، أخبرنا قبيصة، أخبرنا سفيان، عن الصلت بن بهرام، عن الحسن، قال: ما بسطت الدنيا لأحد إلا اغتراراً.',NULL,'172',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (430,'٤٢٤ - أنشدني الحسن بن عبد الرحمن: كفلت يا طالب الدنيا بهم ... طويل لا يؤول إلى انقطاع
وذل في الحياة بغير عز ... وفقر لا يؤول إلى اتساع
وشغل ليس يعقبه فراغ ... وسعي دائم من كل اتساع
وحرص لا يزال عليه عبداً ... وعبد الحرص ليس بذي ارتفاع',NULL,'172',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (431,'٤٢٥ - حدثني الحسين بن عبد الرحمن، قال: قيل: لرجل من قريش: ما الزهد؟ قال:
والله ما هو بالتقشف ولا بخشونة للمطعم، ولكنه طلق النفس عن محبوب الشهوة.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (432,'٤٢٦ - وحدثنا الحسن بن عبد العزيز، أخبرني موسى بن أبي عمران وكان أحد العلماء: قال: قدم أعرابي المدينة فصلى الجمعة، فسمع الخطبة فأعجبه ما سمع، فلما صلى انصرف إلى منزله، ودخل الأعرابي مع من دخل فأتي بطعام، فرأى من ألوان الطعام ما لم يشبه ما تكلم به، فأنشأ يقول:
لقد رابني من اهل يثرب أنهم ... يهمهم تقويمنا وهم عصل
يذمون الدنيا وهم يرضونها ... أفاويق حتى ما يدر لها ثعل
إذا ركبوا الأعواد قالوا فأحسنوا ... ولكن حسن القول يفسده الفعل',NULL,'173',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (433,'٤٢٧ - حدثنا محمد بن جعفر الوركاني، أخبرنا معمر بن سليمان، عن سعيد بن عوسجة،: أن أبا الدرداء قال: قال رسول الله صلى الله عليه وسلم:
لو تعلمون ما أعلم لبكيتم كثيراً ولضحكتم قليلاً، ولهانت عليكم الدنيا ولآثرتم الآخرة.
ثم قال أبو الدرداء من قبل نفسه:
لو تعلمون ما أعلم لخرجتم إلى الصعدات تبكون على أنفسكم، ولتركتم أموالكم لا حارس لها، ولا راجع إليها، إلا ما لا بد لكم منه، ولكن يغيب عن ⦗١٧٤⦘ قلوبكم ذكر الآخرة، وحضرها الأمل فصارت الدنيا أملك بأعمالكم، وصرتم كالذين لا يعلمون فبعضكم شر من البهائم التي لا تدع هواها مخافة مما فيه عاقبته لكم، لاتحابون، ولا تناصحون وأنتم إخوان على دين، ما فرق بين أهوائكم إلا خبث سرائركم، ولو اجتمعتم على البر لتحاببتم، ما لكم تناصحون في أمر الدنيا، لا يملك أحدكم النصيحة لمن يحبه ويعينه على أمر آخرته، ما هذا إلا من قلة الإيمان في قلوبكم، لو كنتم توقنون بخير الآخرة وشرها، كما توقنون بالدنيا لآثرتم طلب الآخرة، لأنها أملك بأموركم، فإن قلتم حب العاجلة غالب؟ فإنا نراكم تدعون العاجل من الدنيا للآجل منها، تكدون أنفسكم بالمشقة، والاحتراق في أمر لعلكم لا تدركونه، فبئس القوم أنتم، ما حققتم إيمانكم بما يعرف به الإيمان البالغ فيكم، فإن كنتم في شك مما جاء به محمد صلى الله عليه وسلم فائتونا فلنبين لكم، ولنريكم من النور ما تطمئن إليه قلوبكم، والله ما أنتم بالمنقوصة عقولكم، فنعذركم، إنكم لتبينون صواب الرأي في دنياكم، وتأخذون بالحزم في أمركم، ما لكم تفرحون باليسير من الدنيا تصيبونه، وتحزنون على اليسير منها يفوتكم، حتى يتبين ذلك في وجوهكم، ويظهر على ألسنتكم، وتسمونها المصائب، وتقيمون فيها المآتم، وعامتكم قد تركوا كثيراً من دينهم، بما لا يتبين ذلك في وجوهكم، ولا يتغير حال بكم، إني لأرى الله قد تبرأ منكم بلقاء بعضكم بعضاً بالسرور، فكلكم يكره أن يستقبل صاحب بما يكره مخافة أن يستقبل صاحبه بمثله، فأصبحتم على الغل، ونبتت مراعيكم على الدمن، وتصافيتم على رفض الأجل، لوددت أن الله أراحني منكم، وألحقني بما أحب رؤيته لو كان حياً لم يصابركم، فإن كان فيكم خير فقد أسمعتكم، وإن تطلبوا ما عند الله تجدوه يسيراً، والله أستعين على نفسي وعليكم.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (434,'٤٢٨ - حدثني هارون بن إبراهيم الإمام، أخبرنا زيد بن الحباب أخبرنا موسى بن عبيدة، أخبرني أخي عبد الله بن عبيدة، عن عروة بن الزبير: أن مصعب بن عمير وعليه نمرة ما تكاد تواريه، والنبي صلى الله عليه وسلم، جالس ومعه نفر من ⦗١٧٥⦘ أصحابه، فلما رأوه نكسوا، ليس عندهم ما يعطونه.
قال: فأثنى عليه النبي صلى الله عليه وسلم خيراً.
قال: فسلم، فقال رسول الله صلى الله عليه وسلم:
لقد رأيته عند أبويه وما فتى من فتيان قريش مثله، يكرمانه وينعمانه، فخرج من ذلك ابتغاء مرضاة الله ونصرة رسوله، أما إنكم لو تعلمون من الدنيا ما أعلم لاستراحت أنفسكم فيها، أما إنه لا يأتي عليكم إلا كذا حتى تفتحوا فارس والروم فيغدوا أحدكم في حلة، ويغدو عليكم بقصعة، ويراح عليكم بأخرى.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (435,'٤٢٩ - حدثني أحمد بن محمد بن سليمان، أنه حدث عن حليسي الضبعي، عن سعيد بن أبي عروة، عن قتادة، قال: قال لي عمران بن حطان: إني لعالم بخلافك، ولكن على ذلك أحفظ، ثم أخذ بيدي فقال:
حتى متى تسقي النفوس بكأسها ... ريب المنون وأنت لاه ترتع
أحلام نوم أو ظل زائل ... إن اللبيب بمثلها لا يخدع
فتزودن من قبل يومك زادا ... أم هل لغير لا أبا لك تجمع',NULL,'175',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (436,'٤٣٠ - حدثني صالح بن مالك، أخبرنا أبو عبيدة الناجي، قال: قال الحسن:
طالبان يطلبان: فطالب الآخرة مدرك بما طلب، لا فوت به عليه، وطالب الدنيا عسى أن يصيب منها قليلاً، وما يفوته منها أكثر، إن الدنيا لما فتحت على أهلها كلبوا والله أشد الكلب، حتى عدى بعضهم على بعض بالسيف، وحتى استحل بعضهم حرمة بعض، فيا لهذا فساداً ما أكثره.',NULL,'175',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (437,'٤٣١ - حدثنا محمد بن الحسين، حدثنا عيسى بن ميمون أبو عمرو النجدي، قال: سمعت صالحاً المري يقول في كلامه:
وكيف تقر بالدنيا عين من عرفها؟ قال: ثم يبكي، ويقول: خلف الماضين، وبقية المتقدمين، رحلوا أنفسكم عنها قبل الرحيل، فكأن الأمر عن قريب قد نزل.
قال: ثم بكى.',NULL,'175',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (438,'٤٣٢ - وأنشدني أبو جعفر القرشي: إنا على قلعة من هذه الدار ... نساق عنها بإمساء وإبكاء
نبكي ونندب آثار الذين مضوا ... وسوف تلحق آثار بآثار
طالت عمارتنا الدنيا على غرر ... ونحن نعلم أنا غير عمار
يا من تحث بترحال على عجل ... ليس المحلة غير الفوز والنار
فاختر لنفسك قبل الموت في مهل ... غداً نفوز ويشقى كل مختار
واترك مفاخرة الدنيا وزينتها ... يوم القيامة يوم الفخر والعار',NULL,'176',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (439,'٤٣٣ - وأنشدني أبو جعفر القرشي أيضاً: هل غاية الدنيا وإن نلتها ... إلا ثرى قبر وملحود
فاعمل لما ترجو وما يبقى ... والحبل بالمهلة ممدود',NULL,'176',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (440,'٤٣٤ - حدثني أبو عبد الله النخعي حدثني ابن الكلبي أخبرنا شرقي بن قسطامي حدثني مشايخنا أنهم سمعوا حريقة بنت النعمان تنشد: بينا نسوس الناس والأمر أمرنا ... إذا نحن فيهم سوقة نتنصف
فأف لدنيا لا يدوم نعيمها ... تلعب تارات بنا وتصرف',NULL,'176',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (441,'٤٣٥ - قاال أبو بكر: ودفع إلي رجل من أهل مرو كتاباً فيه: سئل عبد الله بن المبارك
ما ينبغي للعالم أن يتكرم عنه؟ .
قال: ينبغي للعالم أن يتكرم عما حرم الله عليه، ويرفع نفسه عن الدنيا، فلا تكون منه على بال.',NULL,'176',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (442,'٤٣٦ - وسئل عبد الله قيل: ما ينبغي أن نجعل عظيم شكرنا له.
قال: زيادة آخرتكم، ونقصان دنياكم، وذلك أن زيادة آخرتكم لا تكون إلا بنقصان دنياكم، وزيادة دنياكم لا تكون إلا بنقصان آخرتكم.',NULL,'176',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (443,'٤٣٧ - وحدثني أبو عبد الله محمد بن أحمد المروزي، عن عبدان عن عثمان، عن سفيان بن عبد الملك، عن عبد الله بن المبارك، قال: حب الدنيا في القلب والذنوب قد احتوشته فمتى يصل الخير إليه.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (444,'٤٣٨ - حدثني الحسن بن سعيد القواريري، قال: كان رجل يلتقط النوى، ويتمثل بهذه الأبيات:
أرى الدنيا لمن هي في يديه ... عذاباً كلما كثرت لديه
تهين المكرمين لها بصغر ... وتكرم كل من هانت عليه
إذا استغنيت عن شيء فدعه ... وخذ ما كنت محتاجاً إليه',NULL,'177',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (445,'٤٣٩ - حدثني محمد بن الحسين، حدثني أبو عبد الله محمد بن معاوية، عن بعض رجاله، قال: بلغنا أنه أوحى إلى الدنيا: من خدمك فاتعبيه، ومن خدمني فاخدميه.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (446,'٤٤٠ - حدثني أبو عبد الله الأصبهاني، قال: سمعت محمد بن النعمان بن عبد السلام ينشد:
لو كنت باليوم العظيم تعنى ... لكانت الدنيا عليك سجنا
ولم تكن بالعيش مطمئنا ... أما علمت يا ضعيف أنا
يوماً مجازون بما قدمنا ... لو قد بعثنا ثم قد سئلنا
عن سالف الأعمال ما أقلنا ... ما أعظم القول إذا وقفنا',NULL,'177',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (447,'٤٤١ - وأنشدني الحسين بن عبد الله: إذا لم يعظني واعظ من جوارحي ... بنفع فما شيء سواه بنافعي
أؤمل دنيا أرتجي من حلابها ... غلالة سم مورد الموت ناقع
ومن قابض من الدنيا يكن مثل آخذ ... على الماء خانته فروج الأصابع ⦗١٧٨⦘
وكالحالم المسرور عند منامه ... بلذة أضغاث من أحلام هاجع
فلما تولى الليل ولى سروره ... وعادت عليه عاطفات الفجائع',NULL,'177',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (448,'٤٤٢ - حدثني من سمع ابن أبي الحواري، قال: قلت لأبي صفوان الرعيني بمكة، وكان سفيان بن عيينة يجيء فيسلم عليه، ويقف عليه، ما الدنيا التي ذمها الله عز وجل في القرآن التي ينبغي للعاقل أن يجتنبها؟ قال:
كل ما أصبت من الدنيا تريد به الدنيا فهو مذموم، وكل ما أصبت فيها تريد به الآخرة فليس منها.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (449,'٤٤٣ - وحدثني من سمع ابن أبي الحواري، حدثني أبو عبد الرحمن الموصلي، حدثني أبو موسى خادم الأعمش، عن الأعمش، عن إبراهيم، قال: كانوا يطلبون الدنيا فإذا بلغوا الأربعين طلبوا الآخرة.
فحدثت به المعافى بن عمران فأعجبه.
قلت له: يا أبا عبد الرحمن بأي شيء طلب الآخرة بعد الأربعين؟ قال: قوت يوم بيوم.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (450,'٤٤٤ - حدثني عون بن إبراهيم، حدثني أحمد بن أبي الحواري، قال: سمعت مؤدباً لأهل البصرة يقال له أبو غسان، وجاءه شاب فقال: يا أبا غسان قال: إليك يا حبيبي.
قال: متى ترتحل الدنيا من القلب؟ قال:
إذا وقعت العزيمة رحلت الدنيا من القلب، وردج القلب في ملكوت السماء، وإذا لم تقع اضطرب القلب ورجع إلى الدنيا.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (451,'٤٤٥ - حدثنا محمد بن عبد العزيز بن أبي رزمة، قال: سمعت علي بن ⦗١٧٩⦘ الحسن قال: قلت لعبد الله أوصني، قال:
تجاف عن الدنيا ما استطعت.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (452,'٤٤٦ - وحدثني الحسين بن عبد الرحمن، عن شيخ من فزارة، قال: كان يقال:
الدنيا دار بلاء، فإذا رأى أحدكم فيها رخاء فلينكره.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (453,'٤٤٧ - حدثني الحسين بن عبد الرحمن، قال: قيل لبعض العلماء: أي شيء أجده أدفع للفاقة؟ قال: الزهد.
قيل: وما الزهد؟ قال: العلم، ثم يفرق ما بين الدنيا والآخرة، ثم طلب الرفيع بالخسيس.
قيل: فأيهما أجدى؟ قال: ترك إعمال الفكر في شيء من الدنيا.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (454,'٤٤٨ - أنشدني الحسين بن عبد الرحمن، قال: أنشدني إبراهيم بن داود: لا يكون المغتاب للناس ذو الوجهين ... عند المليك يوماً وجيها
لا ولا طالب الفضول من الدنيا ... ولذاتها يكون فقيها
أدرك الزاهدون كل نعيم إذا ... أباحوا النفوس ما يكفيها
واسترق الحريص فيها فما يغنيه ... منها كل الذي ظل فيها
هي دار تزيد من صدغها معة ... والذليل من يصفيها',NULL,'179',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (455,'٤٤٩ - وحدثني الحسن بن عبد الرحمن، عن زكريا بن عدي، قال: قال عيسى بن مريم:
يا معشر الحواريين ارضوا بدنيء الدنيا مع سلامة الدين كما رضي أهل الدنيا بدنيء الدين مع سلامة الدنيا.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (456,'٤٥٠ - قال زكريا: وفي ذلك يقول الشاعر: أرى رجالاً بأدنى الدين قد قنعوا ولا أراهم رضوا في العيش بالدون
فاستغن بالدين عن دنيا الملوك كما استغنى الملوك بدنياهم عن الدين',NULL,'180',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (457,'٤٥١ - حدثني الحسين بن عبد الرحمن، قال: قال بعض الحكماء: أما بلوتم الدنيا فهل زالت تؤنبكم عسفاً، وتسوسكم خسفاً، في كل يوم لكم فيها شغل جديد، وحزن عتيد، إنما صدقتم الأمل فكذبكم، وأطعتم الهوى فأوبقكم، فكيف تفرون رحمكم الله من هذا الموت، الذي لا تدرون أن ما فيه أحق أن يكون عندكم، فهؤلاء لكم مفظعاً، أما قبله من تخوف بغتاتة التي لا تدرون في أي حالاتكم توافيكم، أما الذي ترون من أسبابه فما يعروكم من الانتقاص ضعفاً بعد قوة، وإخلاقاً بعد جدة، وهرماً بعد شباب، وسقماً بعد صحة في كل يوم يموت من أجسادكم ميت ينعي لكم أنفسكم، ويخبركم عن فنائكم، حتى يهجم عليكم بمرارة كأسه، وفظاعة مذاقه، فتصيروا رهائن الموت، وودائع الحفر إلى يوم الوقت المعلوم.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (458,'٤٥٢ - حدثنا محمد بن عمارة الأسدي، أخبرنا حسن بن حسين العرني، أخبرنا علي بن بكر، عن إبراهيم بن إسحاق، عن وهب بن منبه، قال: من فرح من قلبه بشيء من الدنيا فقد أخطأ الحكمة، ومن جعل شهوته تحت قدميه يفرق شيطانه من ظله، ومن غلب عليه هواه فهو الغالب.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (459,'٤٥٣ - حدثني محمد بن إدريس الحنظلي، أخبرنا عبد الله بن إسماعيل بن ⦗١٨١⦘ يزيد بن حجر ابن بنت الأوزاعي، حدثني أبي، قال: وجدت في كتب جدك الأوزاعي بخط يده:
ابن آدم اعمل لنفسك، وبادر فقد أوتيت من كل جانب، وأعول كعويل الأسير المكبل، ولا تجعل بقية عمرك للدنيا وطلبها في أطراف الأرض، حسبك ما بلغك منها، ستسلم طائعاً، وتغر بيوم فقرك وفاقتك، واسع في طلب الأمان فإنك في سفر إلى الموت يطرد بك نائماً ويقظاناً، واذكر سهر أهل النار في خلد أبداً، وتخوف أن ينصرف بك من عند الله عز وجل إلى النار، فيكون ذلك آخر العهد بالله، ومنقطع الرجاء، واذكر أنك قد راهقت الغاية، وإنما بقي الرمق فسدد تصبراً وتكرماً، وارغب ببقية عمرك أن تفنيه للدنيا، وخذ منها ما يوصلك لآخرتك، ودع منها ما يشغلك.',NULL,'180',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (460,'٤٥٤ - حدثني محمد بن إدريس، أنه حدث عن عبد الله بن عبد الغفار، قال: كتب زهير بن نعيم إلى أبي سعيد عبد الله بن عبد الغفار:
سلام عليك: فإني أحمد إليك الله الذي لا إله إلا هو، وأوصي نفسي وإياك بتقوى الله وطاعته، والانتهاء إلى أمره في الحالات كلها، فإنما العاقبة للمتقين، وإنما يجزى كل قوم بما كانوا يعملون.
أما بعد: فإني أكتب إليك يا بن أخ، وأنا في عافية، ومسير إلى الموت على أي الحالات، كذا محفوظ علينا ما قدمت أيدينا، فالله الله في نفسك، يا ابن أخ أكثر الفكرة في مصرع أبيك، وأمك، وأبعد عن فضول الدنيا، وارض منها باليسير، فإن عامة الغفلة والنسيان في طلب فضول الدنيا، رضانا الله وإياك منها بالأقل، ورزقنا وإياك فيها العمل الأكثر لدار الآخرة حتى يخرجنا وإياك منها، وهو علينا غير ساخط بمنه ورحمته، فإنه لا يمن بذلك غيره، وإن استطعت يا بن أخ فلا تنس قول الله عز وجل: ⦗١٨٢⦘ {أم يحسبون أنا لا نسمع سرهم ونجواهم بلى ورسلنا لديهم يكتبون} [الزخرف: ٨٠] .',NULL,'181',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (461,'٤٥٥ - وحدثني محمد بن إدريس الحنظلي، أخبرنا إسحاق بن عبد المؤمن الدمشقي، قال: كتب إلي أحمد بن عاصم الأنطاكي فكان في كتابه:
إنا أصبحنا في دهر حيرة تضطرب علينا أمواجه بغلبة الهوى، العالم منا والجاهل، فالعالم منا مفتون بالدنيا مع ما يدعيه من العلم، والجاهل منا عاشق لها، مستملأ من فتنة عالمه، فالمقل لا يقنع، والمكثر لا يشبع، فكل قد شغل الشيطان قلبه بخوف الفقر، فأعاذنا الله وإياك من قبولنا عدة إبليس، وتركنا عدة رب العالمين، يا أخي لا تصحب إلا مؤمناً، يعظك بفعله، ومصاديق قوله، أو مؤمناً تقياً فمتى صحبت غير هؤلاء، ورثوك النقص في دينك وقبح السيرة في أمورك، وإياك والحرص والرغبة، فإنهما يسلبانك القناعة والرضا، وإياك والميل إلى هواك فإنه يصدك عن الحق، وإياك أن تظهر أنك تخشى الله وقلبك فاجر، وإياك أن تضمر ما إن أظهرته أخزاك، وإن أضمرته أرداك.
والسلام.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (462,'٤٥٦ - حدثنا علي بن الحسن العامري، أخبرنا علي بن حفص المدائني، أخبرنا شيخ من البصريين يقال له أبو الورقاء، قال: سمعت أنس بن مالك وسمع رجلاً يقول: أين الزاهدون في الدنيا، والراغبون في الآخرة؟ قال: أولئك أهل بدر.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (463,'٤٥٧ - حدثني أبو علي المدائني، أخبرنا قطر بن حماد بن واقد، أخبرنا أبي، قال: سمعت مالك بن دينار يقول: يقولون: مالك زاهد، مالك زاهد، أي زهد عند مالك، ولمالك جبة وكساء، وإنما الزاهدون عمر بن عبد العزيز أتته الدنيا فاغرة فاها فتركها.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (464,'٤٥٨ - حدثني أبو عبد الله الرازي قال: قال بعض الحكماء:
الزهد فيما يشغلك عن الله عز وجل، وقال بعضهم: الزهد ترك الشهوات.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (465,'٤٥٩ - حدثني محمد بن يوسف، قال: سمعت بشر بن الحارث، وقيل له: مات فلان، قال جمع الدنيا، وذهب إلى الآخرة، ضيع نفسه.
قيل له: إنه كان يفعل ويفعل، وذكروا أبواب البر، فقال: وما ينفع هذا، وهو يجمع الدنيا.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (466,'٤٦٠ - قال أبو بكر: قال بعض الحكماء: المرء في الدنيا على أكبر خطر، إما نعمة زائلة، وإما بلية نازلة، وإما مصيبة جارية، وإما منية قاضية، فلقد كدرت عليه المعيشة إن غفل، هو من النعماء على خطر، ومن البلايا على حذر، ومن المنايا على يقين.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (467,'٤٦١ - حدثني محمد بن عمارة الأسدي، أخبرنا مالك بن إسماعيل، أخبرنا مسلمة بن جعفر، عن عمرو بن عامر البجلي، عن وهب بن منبه، قال: ⦗١٨٤⦘ ثلاث من مناقب الكفر: الغفلة عن الله عز وجل، وحب الدنيا والطيرة',NULL,'183',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (468,'٤٦٢ - حدثنا إسحاق بن إسماعيل، أخبرنا سفيان بن عيينة، عن أسلم بن عبد الملك: أنه سمع سعيد بن أبي الحسن يذكر عن أنس بن مالك أن النبي صلى الله عليه وسلم قال:
أنتم اليوم على بينة من ربكم تأمرون بالمعروف، وتنهون عن المنكر، وتجاهدون في سبيل الله، أنتم اليوم على بينة من ربكم، لم تظهر فيكم السكرتان: سكرة الجهل، وسكرة العيش، العاملون يومئذ بالكتاب سراً وعلانية، فالتابعون الأولون من المهاجرين والأنصار لهم أجر المحسنين قالوا: يا رسول الله منا، أو منهم؟ قال: بل منكم',NULL,'184',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (469,'٤٦٣ - قال أبو بكر: قيل لبعض الحكماء: من أبعد الناس همة وأصدقهم نية؟ قال:
من استغرق الدنيا طرفه، وعطف إلى طلب الجنة شغله.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (470,'٤٦٤ - حدثنا العباس بن الفضل البجلي، قال: أكثر قوم ذم الدنيا عند رابعة، فقالت: أقلوا من ذم الدنيا، فإنه من أحب شيئاً أكثر ذكره.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (471,'٤٦٥ - حدثنا إسحاق بن إبراهيم، أخبرنا حماد بن زيد، عن أيوب عن الحسن قال: إذا رأيت الرجل ينافسك في الدنيا فنافسه في الآخرة.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (472,'٤٦٦ - حدثنا خالد بن خداش، أخبرنا حماد بن زيد، قال: قال أيوب:
إن زهد رجل فلا يجعلن زهده عذاباً على الناس.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (473,'٤٦٧ - حدثني محمد بن إدريس، أخبرنا عبدة بن سليمان، عن ابن المبارك، عن جعفر بن سليمان، قال: هم الدنيا ظلمة في القلب، وهم الآخرة نور في القلب.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (474,'٤٦٨ - حدثني أحمد بن أبي نصر، قال بعض الحكماء: للدنيا أمثال تضربها، الأيام للأنام، وعلم الزمان لا يحتاج إلى ترجمان، ويحب الدنيا من صمت أسماع القلوب عن المواعظ، وما أحث السباق لو شعر الخلائق.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (475,'٤٦٩ - أنشدني أحمد بن أبي نصر: يلتمس العز بها أهلها ... والله قد عرفهم ذلها
يا عاقد العقدة يرجو بها ... العيش كأن الموت قد حلها
كم تعمر الدنيا ورب السماء ... يريد أن يخربها كلها!!',NULL,'185',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (476,'٤٧٠ - حدثني رجل من بني تميم، قال: قال بعض الحكماء: الدنيا تبغض إلينا نفسها، ونحن نحبها! ! فكيف لو تحببت إلينا.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (477,'٤٧١ - حدثني أبو عبد الله الإمام، قال: سمعت ابن أبي الحواري، قال: سمعت أبا سليمان، يقول:
لو أن رجلاً دخل عل ملك من ملوك الدنيا، فقال: سلني.
فقال: أسألك ⦗١٨٦⦘ جزرة بقل، أكان حازماً؟!! فوالله للدنيا أهون على الله عز وجل من جزرة البقل على الملك.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (478,'٤٧٢ - أخبرني ربيعة الحنفي عن شيخ من أهل البصرة قال: قال وهب بن منبه: رأينا ورقة تهفو بها الريح، فأخذناها فإذا فيها مكتوب:
بسم الله الرحمن الرحيم:
دار لا يسلم منها من فيها، ما أخذ أهلها منها لها خرجوا منه، ثم حوسبوا به، وما أخذ أهلها منها لغيرها خرجوا منه، ثم أقاموا به، وكأن قوماً من أهل الدنيا ليسوا من أهلها كانوا فيها كمن ليس فيها، عملوا فيها بما يبصرون، وبادروا فيها ما يحذرون، تنقلب أجسادهم بين ظهراني أهل الدنيا، وتنقلب قلوبهم بين ظهراني أهل الآخرة، يرون أهل الدنيا يعظمون، وهم أشد تعظيماً لموت قلوبهم.
قال: فسألت عن هذا الكلام فلم أجد أحداً يعرفه!!',NULL,'186',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (479,'٤٧٣ - حدثني محمد بن جعفر بن مهران البصري، عن رجل، عن أبيه: أن غلاماً لعبد الملك بن مروان كتب إليه: إن صخرة قبلنا يقال إن تحتها كنزاً يحتاج إلى نفقة، فكتب إليه عبد الملك: أن واصل بين النفقة حتى تستخرج هذا الكنز، فعولجت حتى قلبت فلم أجد تحتها كنزاً ووجد عليها كتاباً فيه:
ومن يحمد الدنيا بعيش يسره ... فسوف لعمري عن قليل يلومها
إذا أقبلت كانت المر حسرة ... وإن أدبرت كانت كثيراً غمومها',NULL,'186',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (480,'٤٧٤ - قال أبو بكر: قيل لبعض الحكماء: ما الدنيا؟ قال: تريدون المذمومة على ألسن الأنبياء الحكماء؟ قالوا: نعم.
قال: المعصية.
قيل: فأي الزهاد أفضل؟ قال: أقلهم حظاً من الدنيا.
قيل: متى يصفو توكل الزاهد؟ قال: إذا لم يلزمه منه مخلوق.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (481,'٤٧٥ - قال أبو بكر: وقال بعض الحكماء:
ما فرحت يا ابن آدم بما يفنى إلا بعد نسيانك ما يبقى، ولا ركنت إلى زينة ⦗١٨٧⦘ الدنيا إلا بتركك نصيبك من جنة المأوى، ولا متعت نفسك بمواعيد المنى إلا بعد ما عانقت هذه الدنيا، ولا تتوقت في تسمين بدنك حتى نسيت دراجك في كفنك.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (482,'٤٧٦ - قال أبو بكر: قيل لبعض الحكماء: من أعرف الناس بعيوب الدنيا؟
قال: أكثرهم للموت ذكراً.
قيل: فلم نكره الموت؟ قال: لإيثاركم الدنيا.
قيل: متى يحكم على العبد بالغفلة؟ قال: إذا ركن إلى الدنيا.
قيل: متى يذهب منا الحكمة والعلم؟ قال: إذا طلب بهما الدنيا.
قيل: ما الذي يمنع من طلب الآخرة؟ قال: حب الدنيا.
قيل: ما علامة ترك الدنيا؟ قال: طلب الآخرة.
قيل: الدنيا لمن هي؟ قال: لمن تركها.
قيل: الآخرة لمن هي؟ قال: لمن طلبها.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (483,'٤٧٧ - قال أبو بكر: قال بعض الحكماء:
الدنيا دار خراب، وأخرب منها قلب من يعمرها، والجنة دار عمران، وأعمر منها قلب من يطلبها.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (484,'٤٧٨ - حدثني الحارث بن محمد العمي، عن أبي الحسن القرشي، قال: قال رجل من الأنصار: صغر فلان في عيني لعظم الدنيا في عينه، كان يرد السائل ويبخل بالنائل.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (485,'٤٧٩ - حدثني الحارث بن محمد، عن أبي الحسن القرشي، قال: قال أبو حازم:
من عرف الدنيا لم يفرح فيها برخاء، ولم يحزن على بلوى.
أنشدني أبو عبد الله الكناني:
فتى قالت له الدنيا نل فلم ينل ... قذى العين منها عفة وتكرماً ⦗١٨٨⦘
رجل جعل القرآن موقع طرفه ... فنفذ منها ما أحل وحرما',NULL,'187',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (486,'٤٨٠ - حدثني القاسم بن هاشم، حدثني إسحاق بن عباد، قال: قال لي بعض الحكماء: أضرب لك مثل هذا الخلق: مثل قوم اتخذوا الدنيا دار إقامة، واتخذوا الآخرة لهواً وغروراً.
ثم قال: اضرب بيدك ما شئت من هذا الخلق إذا نصحته في أمر دينه اتخذك عدواً.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (487,'٤٨١ - حدثني إسحاق بن عبد الله، قال: ترك الفدى، أرى الناس قد اتخذوا الدنيا رأس مال، وعدوا ما جاءهم من الآخرة ربحاً، وقد عزمت على أن أجعل الآخرة رأس مالي، وأعد ما جاءني منها ربحاً.
قال: ففعل ذلك.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (488,'٤٨٢ - وحدثنا إسحاق بن حاتم المدائني، قال: سمعت الحسين بن أبي عبد الله المعلم قال: قال سليمان التيمي: اللهم إنك تعلم أني لا أريد من الدنيا شيئاً، فلا ترزقني منها شيئاً.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (489,'٤٨٣ - حدثني إسحاق بن حاتم، قال: سمعت حسين بن أبي عبد الله قال: كنا عند حجاج الخراساني بمكة ندعو، وكان معنا رجل مكثر، فقال أبو الحجاج:
اللهم لا ترزقنا ديناراً ولا درهماً، فأمنا كلنا ما خلا الرجل المكثر.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (490,'٤٨٤ - حدثنا موسى أبو عمران الجصاص، قال: سمعت أبا سليمان الداراني، يقول: ينبغي للعبد المعنى بنفسه أن يميت العاجلة الفانية الزائلة، المنغصة بالآفات من قلبه، ويذكر الموت وما بعده من الأهوال، والخسران والندامة، والوقوف بين يدي الله عز وجل وسؤاله إياه، والممر على الصراط، ⦗١٨٩⦘ والنار، فإنه يخف عليه التجافي عن دار الغرور:',NULL,'188',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (491,'٤٨٥ - حدثني موسى أبو عمران، قال: سمعت أبا سليمان، يقول: الدنيا تطلب الهارب منها، وتهرب من الطالب لها، فإن أدركت الهارب منها جرحته، وإن أدركت الطالب لها قتلته.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (492,'٤٨٦ - حدثني محمد بن إدريس، أخبرنا عبدة بن سليمان، أخبرنا ابن المبارك، قال: قال الحسن:
خباث كل عيدانك، قد مصصناه فوجدناه مراً.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (493,'٤٨٧ - حدثني الحسين بن عبد الرحمن، قال: قال بشر بن الحارث:
من هوان الدنيا على الله عز وجل أن جعل بيته وعراً.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (494,'٤٨٨ - حدثني الحسين بن عبد الرحمن قال: قال أبو معاوية الأسود: الخلق كلهم يسعى في أقل من جناح ذبابة، فقال له رجل، وما أقل من جناح ذبابة؟ قال: الدنيا.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (495,'٤٨٩ - حدثني الحسين بن عبد الرحمن، عن شيخ مولى لبني هاشم، قال: قال الحسن: إن قوماً أكرموا الدنيا فصلبتهم على الخشب، فأهينوها، فأهنأ ما تكونون إذا أهنتموها.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (496,'٤٩٠ - حدثني الحسين بن عبد الرحمن، عن شيخ من فزارة، قال: سمعت أبا خالد الصوري وكان من أطول الناس صمتاً، يقول: اللهم أخرجني من جوار إبليس إلى جوارك.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (497,'٤٩١ - وأنشدني الحسين بن عبد الرحمن: لعمرك ما الدنيا بدار لأهلها ... ولو عقلوا كانوا جميعاً على وجل
فما تبحث الساعات إلا على البلى ... ولا تنقضي الأيام إلا على ثكل',NULL,'190',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (498,'٤٩٢ - حدثني محمد بن إدريس، أخبرنا زهير بن عباد، أخبرنا عبد الله بن حكيم بن أبي داهري، عن مجاعة بن الزبير، عن الحسن قال: لا يكون الرجل زاهداً في الدنيا حتى لا يجزع من ذلها، ولا ينافس أهلها فيها.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (499,'٤٩٣ - وحدثني محمد بن إدريس، أخبرني أحمد بن عبد الله بن عياض، أخبرنا عبد الوهاب بن همام، أخبرنا عبد الصمد بن معقل عن وهب بن منبه قال: قرأت في كتاب شعيا أنه قيل ليونس بن متى:
يا يونس إذا أحب العالم الدنيا نزعت مناجاتي من قلبه.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (500,'٤٩٤ - أنشدني أبو عبد الله قوله: رويداً بني الدنيا ألم تر أنهم ... إلى أجل تسعى إليه مقادره
أراها إذا ربت لها ابناً ولم تدع ... له أرباً دست له ما يحاذره
فكن عند صفو الدهر للدهر حاذراً ... فلا صفو إلا سوف يكدر آخره
قال أبو بكر: أنشدني علي بن عبد الله:
لما توعد الدنيا به من شرورها ... يكون بكاء الطفل ساعة يوضع
وإلا فما يبكيه منها وإنها ... لأفسح مما كان فيه وأوسع',NULL,'190',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (501,'٤٩٥ - حدثني محمد بن إدريس الحنظلي، أخبرني الحسن بن عبد الله ⦗١٩١⦘ الرازي عن بكار الربذي عن عمه موسى بن عبيدة الربذي، عن أبي سعيد مولى ابن عامر قال: قال داود عليه السلام:
الدنيا غرارة ترفل بالمطمئن، وتفجع الآمن.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (502,'٤٩٦ - حدثني عون بن إبراهيم، حدثني أحمد بن أبي الحواري، حدثني عبادة أبو مروان، قال: أوحى الله عز وجل إلى موسى عليه السلام:
يا موسى ما لك ولدار الظالمين، إنها ليست لك بدار، أخرج منها همك، وفارقها بعقلك، فبئست الدار هي، إلا لعامل يعمل فيها فنعمت الدار له، يا موسى إني مرصد للظالم حتى أديل منه المظلوم.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b21546] VALUES (503,'٤٩٧ - قال أبو بكر: قال محمد بن علي بن شقيق: عن أبيه قال: أخبرنا عبد الله، أخبرنا عبد الرحمن بن يزيد بن جابر عن هارون بن زيد قال: سئل الحسن عن قوله عز وجل {ثمنا قليلا} [البقرة: ٤١] ما الثمن القليل؟ قال: الدنيا بحذافيرها.
آخر كتاب ذم الدنيا والحمد لله رب العالمين وصلواته على نبينا محمد خاتم المرسلين.',NULL,'191',NULL,NULL,'0');
