INSERT INTO [b2171] VALUES (580,'*- عبد الله بن كنانة عن بن عباس وعنه ابنه هشام حديثه مضطرب س','1','589',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (581,'٢٩٢٩- عبد الله بن كيسان أبو عمر عن مولاته أسماء وابن عمر وعنه عطاء وابن جريج قال أبو داود ثبت ع
٢٩٣٠- عبد الله بن كيسان المروزي أبو مجاهد عن سعيد بن جبير وعكرمة وعنه السيناني وعلي بن الحسن بن شقيق ضعفه أبو حاتم د
٢٩٣١- عبد الله بن كيسان مولى طلحة عن عبد الله بن شداد والمقبري وعنه موسى بن يعقوب وثق ت
٢٩٣٢- عبد الله بن أبي لبيد أبو المغيرة المدني عن أبي سلمة والمطلب بن عبد الله وعنه السفيانان ثقة خ م د س ق
٢٩٣٣- عبد الله بن لحي أبو عامر الهوزني الحمصي عن عمر ومعاذ وبلال وعنه راشد بن سعد وأزهر الحرازي ثقة د س ق
٢٩٣٤- عبد الله بن لهيعة أبو عبد الرحمن الحضرمي الفقيه قاضي مصر عن عطاء والاعرج وابن أبي مليكة وعمرو بن شعيب وعنه يحيى بن بكير وقتيبة والمقرئ ضعف وقال أبو داود سمعت أحمد يقول من كان مثل بن لهيعة بمصر في كثرة حديثه وإتقانه وضبطه قلت العمل على تضعيف حديثه توفي ١٧٤ د ت ق','1','590',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (582,'٢٩٣٥- عبد الله بن مالك أبو تميم الجيشاني هاجر من اليمن زمن عمر وسمع منه ومن علي وتلا على معاذ وعنه بكر بن سوادة وكعب بن علقمة وعدة وكان من العابدين مات ٧٧ م ت س ق
٢٩٣٦- عبد الله بن مالك بن بحينة وبحينة أمه مطلبية من السابقين عنه حفص بن عاصم والاعرج ومحمد بن يحيى بن حبان توفي مع عائشة تقريبا ع
٢٩٣٧- عبد الله بن مالك عن علي وابن عمر وعنه أبو إسحاق وأبو روق الهمداني شيخ د ت
٢٩٣٨- عبد الله بن مالك بن حذافة عن أمه وعنه كثير بن فرقد د س
٢٩٣٩- عبد الله بن مالك له صحبة عنه شبل بن خليد س
٢٩٤٠- عبد الله بن مالك اليحصبي عن عقبة بن عامر وعنه أبو سعيد جعثل الرعيني قال بن يونس هو أبو تميم الجيشاني ٤
٢٩٤١- عبد الله بن المبارك بن واضح أبو عبد الرحمن الحنظلي مولاهم المروزي شيخ خراسان عن سليمان التيمي وعاصم الاحول والربيع بن أنس وعنه بن مهدي وابن معين وابن عرفة فأبوه تركي مولى تاجر وأمه خوارزمية ولد سنة ١١٨ وتوفي بهيت ١٨١ في رمضان ع','1','591',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (583,'٢٩٤٢- عبد الله بن المثنى بن عبد الله بن أنس بن مالك أبو المثنى عن عمومته والحسن وعنه ابنه
محمد ومسدد وعبد الواحد بن غياث قال أبو حاتم صالح وقال أبو داود لا أخرج حديثه خ ت ق
٢٩٤٣- عبد الله بن أبي المجالد عن بن أبي أوفى مولاه وعبد الرحمن بن أبزى وعنه أبو إسحاق الشيباني وشعبة ثقة وسماه شعبة محمدا فوهم خ د س ق
٢٩٤٤- عبد الله بن محرر العامري قاضي الجزيرة عن نافع والزهري وعنه عبد الرزاق وأبو نعيم قال البخاري منكر الحديث ق
٢٩٤٥- عبد الله بن محصن الانصاري اختلف في صحبته عنه ابنه سلمة ت ق
*- عبد الله بن محصن عن عمته وعنه بشير بن يسار صوابه حصين سعبد الله بن محصن عن عمته وعنه بشير بن يسار صوابه حصين س ١١٣١
٢٩٤٦- عبد الله بن محمد بن أبي شيبة الحافظ أبو بكر العبسي مولاهم الكوفي صاحب التصانيف عن شريك وابن المبارك وهشيم وعنه البخاري ومسلم وأبو داود وابن ماجة وأبو يعلى والباغندي قال الفلاس ما رأيت أحفظ منه وقال صالح جزرة هو أحفظ من أدركنا عند المناظرة توفي ٢٣٥ سوى ت
٢٩٤٧- عبد الله بن محمد بن إسحاق أبو عبد الرحمن الاذرمي عن هشيم وجرير وعنه أبو داود','1','592',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (584,'والنسائي وابن صاعد قال أبو حاتم ثقة د س
٢٩٤٨- عبد الله بن محمد بن أسماء الضبعي عن عمه جويرية وجعفر بن سليمان وعنه البخاري ومسلم وأبو داود وأبو يعلى قال أحمد الدورقي لم أر بالبصرة أفضل منه توفي ٢٣١ خ م د س
٢٩٤٩- عبد الله بن محمد بن أبي الاسود حميد أبو بكر البصري الحافظ عن مالك وديلم بن غزوان وخاله عبد الرحمن بن مهدي وعنه البخاري وأبو داود وإبراهيم الحربي مات ٢٢٣ خ د ت
٢٩٥٠- عبد الله بن محمد التيمي عن عمته عائشة وعنه سالم ونافع قتل بالحرة شابا خ م س
٢٩٥١- عبد الله بن محمد بن تميم المصيصي عن حجاج بن محمد ووهب بن جرير وعنه النسائي
وأبو عوانة وطائفة ثقة س
٢٩٥٢- عبد الله بن محمد بن حجاج بن أبي عثمان الصواف عن عبد الوهاب الثقفي ومعاذ بن هشام وعنه الترمذي وابن خزيمة وابن صاعد توفي ٢٥٥ ت
٢٩٥٣- عبد الله بن محمد بن الربيع الكرماني بالمصيصة عن بن المبارك وإسماعيل بن مجالد وعنه الدارمي وأبو حاتم ثقة س
٢٩٥٤- عبد الله بن محمد بن رمح المصري عن بن وهب وعنه بن ماجة ومحمد بن محمد بن الاشعث توفي ٢٥ ق','1','593',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (585,'٢٩٥٥- عبد الله بن محمد بن صيفي عن حكيم بن حزام وعنه صفوان بن موهب وثق حب س
٢٩٥٦- عبد الله بن محمد أبو جعفر الجعفي البخاري الحافظ المسندي عن فضيل ومعتمر وعنه البخاري ومحمد بن نصر المروزي توفي ٢٢٩ خ ت
٢٩٥٧- عبد الله بن محمد بن عبد الله بن زيد بن عبد ربه الانصاري عن جده وعنه بن سيرين وجماعة وثق د
٢٩٥٨- عبد الله بن محمد بن عبد الله بن أبي فروة أبو علقمة الفروي عن المقبري ونافع وعنه سعيد بن منصور وابن راهويه ثقة توفي ١٩ م د س
٢٩٥٩- عبد الله بن أبي عتيق محمد بن عبد الرحمن بن أبي بكر عن عائشة وابن عمر وعنه ابناه محمد وعبد الرحمن وابن إسحاق ثقة خ م س ق
٢٩٦٠- عبد الله بن محمد بن عبد الرحمن بن المسور بن مخرمة بصري سمع بن عيينة وغندرا وعنه مسلم والاربعة وابن خزيمة وابن أبي داود مات ٢٥٦ م ٤
٢٩٦١- عبد الله بن محمد بن عقيل الطالبي عن بن عمر وجابر وعنه معمر وزائدة وبشر بن المفضل قال أبو حاتم وعدة لين الحديث وقال بن خزيمة لا أحتج به د ت ق','1','594',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (586,'٢٩٦٢- عبد الله بن محمد بن الحنفية عن أبيه وعنه الزهري وعمرو بن دينار توفي بالحميمة ٩٨ ع
٢٩٦٣- عبد الله بن محمد بن علي بن نفيل الحافظ أبو جعفر النفيلي الحراني عن مالك وزهير وعنه أبو داود وهلال بن العلاء والفريابي قال أبو داود ما رأيت أحفظ منه وكان أحمد يعظمه وقال بن واره هو من أركان الدين توفي ٢٣٤ خ ٤
٢٩٦٤- عبد الله بن محمد بن عمر بن علي بن أبي طالب عن أبيه وخاله الباقر وعنه أبو أسامة وابن أبي فديك ثقة د س
٢٩٦٥- عبد الله بن محمد بن عمرو الغزي أبو العباس عن عمرو بن أبي سلمة والفريابي وعنه أبو داود وابن خزيمة وابن أبي حاتم د
٢٩٦٦- عبد الله بن محمد بن معن عن أم هشام وعنه خبيب بن عبد الرحمن وثق م د
٢٩٦٧- عبد الله بن محمد بن يحيى الطرسوسي الضعيف من العبادة عن بن عيينة وأبي معاوية وعنه أبو داود والنسائي وابن أبي داود وثقه النسائي د س
٢٩٦٨- عبد الله بن محمد بن أبي يحيى الاسلمي عن أبيه وعمه أنيس وأبي صالح السمان وعنه','1','595',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (587,'بن أبي فديك وقتيبة وثقه بن معين مات ١٧٤ لقبه سحبل د
٢٩٦٩- عبد الله بن محمد الليثي عن تابعي صغير عنه يونس المؤدب لا يعرف ق
٢٩٧٠- عبد الله بن محمد التميمي عن عمر بن عبد العزيز وابن جدعان وعنه الوليد بن بكير واه ق
٢٩٧١- عبد الله بن محمد بن الرومي ببغداد عن بن عيينة وعبدة بن سليمان وعنه مسلم وأبو يعلى والسراج ثقة توفي ٢٣٦ م
٢٩٧٢- عبد الله بن محيريز الجمحي المكي ببيت المقدس رباه أبو محذورة له عنه وعن عبادة بن الصامت وعنه مكحول والزهري قال رجاء بن حيوة إن فخر علينا أهل المدينة بابن عمر فإنا نفخر بعابدنا بن محيريز إن كنت لاعد بقاءه أمانا لاهل الارض مات قبل المائة ع
٢٩٧٣- عبد الله بن المختار البصري عن الحسن ومعاوية بن قرة وعنه شعبة والحمادان قال شعبة
كان أصغر مني وقال بن معين ثقة م د س ق
٢٩٧٤- عبد الله بن مخلد التميمي النيسابوري عن أبي نعيم ومكي بن إبراهيم وأبي عبيد فأكثر وعنه أبو داود وابن خزيمة وابن الشرقي توفي ٢٦ د
٢٩٧٥- عبد الله بن مرة الخارفي عن بن عمر ومسروق وعنه منصور والاعمش ثقة مات سنة مائة ع
٢٩٧٦- عبد الله بن مرة الزرقي عن أبي سعيد وعنه أبو الفيض س
٢٩٧٧- عبد الله بن مرة أو بن أبي مرة الزوفي شهد فتح مصر ونزلها سمع خارجة في الوتر وعنه عبد الله بن راشد ورزين الزوفيان سنده منقطع د ت ق','1','596',NULL,NULL,'0');
INSERT INTO [b2171] VALUES (588,'٢٩٧٨- عبد الله بن مسافع الحجبي عن عمته صفية وعنه ابنها منصور وابن جريج توفي ٩٨ د س
٢٩٧٩- عبد الله بن مسعود أبو عبد الرحمن الهذلي حليف بني زهرة من السابقين الاوليين عنه علقمة والاسود وزر روى الحارث عن علي مرفوعا لو كنت مؤمرا أحدا من غير مشورة لامرت عليهم بن أم عبد أخرجه الترمذي روى أنه خلف تسعين ألف دينار سوى الرقيق والمواشي مات بالمدينة لما وفد سنة ٣٢ ع
