INSERT INTO [b2228] VALUES (1,'تاريخ القرآن الكريم

محمد طاهر الكردي',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b2228] VALUES (2,'كتاب تاريخ القرآن الكريم

وهو اول كتاب من نوعه
ملتزم طبعه ونشره
مصطفى محمد يغمور
بمكة

طبع للمرة الأولى
بمطبعة الفتح بجدة - الحجاز عام ١٣٦٥ هـ و ١٩٤٦ م',NULL,'1',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (3,'كل نسخة لم تكن مختومة بختم الناشر تعد مسروقة
الختم
مؤلفات محمد طاهر الكردى الخطاط صاحب هذا الكتاب غفر الله له
(١) تاريخ القرآن وغرائب رسمه وحكمه طبع ولله الحمد
(٢) تاريخ الخط العربي وآدابه.."
(٣) تحفة العباد في حقوق الزوجين والوالدين والاولاد "
(٤) حسن الدعابة فيما ورد في الخط وادوات الكتابة "
(٥) كراسة الحرمين في تعليم خط الرقعة. "
(٦) مجموعة الحرمين في تعليم خط النسخ. "
(٧) رسالة في الدفاع عن الكتابة العربية في الحروف والحركات "
(٨) ارشاد الزمرة لمناسك الحج والعمرة (على المذهب الشافعي) سيطبع ان شاء الله
(٩) تحفة الحرمين في بدائع الخطوط العربية. " "
(١٠) نفحة الحرمين في تعليم خطى النسخ والثلث "
(١١) مختصر المصباح والمختار (في اللغة العربية) . "
(١٢) بدائع الشعر ولطائفه.."
(١٣) المحفوظات الادبية الممتازة.."
(١٤) أدبيات الشاى والقهوة. "
_________
وقد كتب المذكور مصحفا كريما سماه مصحف مكة المكرمة وهو اول مصحف كتبه بيده كما هو اول مصحف سيطبع ان شاء الله تعالى بمكة المشرفة ادام الله عليه توفيقه وفضله ورضاه وختم حياته بخير على الايمان الكامل آمين (*)',NULL,'2',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (4,'تاريخ القرآن وغرائب رسمه وحكمه
يبحث عن تعريف القرآن وما يتضمنه، وعن جمعه وكتابته وترتيب آياته وسوره وضبطه وتصحيحه، وعن غرائب رسم كلماته وهل رسمه توقيفي ام لا، وعن حكم اتباعه وسبب نقطه وتشكيله، وعن معرفة الصحابة للاملاء والكتابة، وعن مقارنة كتاباتنا برسمه وغير ذلك من المباحث القيمة
تأليف محمد طاهر بن عبد القادر الكردى المكى
الخطاط بالمعارف العامة * بمكة المشرفة
لطف الله به وعامله برحمته واحسانه وستره في الدنيا والآخرة آمين
حقوق الطبع محفوظة للمؤلف
طبع سنة ١٣٦٥ هجرية بجدة',NULL,'1',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (5,'بسم الله الرحمن الرحيم
الحمد لله رب العالمين حمدا يليق بعظمته وجلاله، والصلاة والسلام على نبينا محمد وصحبه وآله (وبعد)
فلقد وفقنا الله تعالى لكتابة القرآن العظيم (١) الذى لا يأتيه الباطل من بين يديه ولا من خلفه، كتبناه على وفاق رسم المصحف العثماني ناقلين عن المصحف الذى طبعته الحكومة المصرية سنة ألف واثنين وأربعين هجرية تحت إشراف مشيخة الازهر المعمور ومشيخة المقارئ العمومية، لان اتباع رسمه واجب بالاجماع وإن كتب بعض كلماته على غير طريقتنا المتبعة " ولدا يقال "
_________
(١) انتهينا من كتابة هذا المصحف الكريم في ختام عام ألف وثلثمائة واثنين وستين هجري وقد تألفت لجنة من قبل الحكومة للعناية بتصحيحه، وهو أول مصحف كتبناه كما هو أول مصحف سيطبع بمكة المشرفة ان شاء الله تعالى في عام ١٣٦٦ هـ (*)',NULL,'2',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (6,'خطان لا يقاس عليهما خط المصحف وخط العروض (١) والمراد بالمصحف العثماني مصحف عثمان بن عفان رضي الله عنه الذى أمر بكتابته وجمعه وكانوا يسمونه " المصحف الامام " (٢)
_________
(١) أي لا تقاس كتاباتنا العامة على خط المصحف العثماني لمخالفته القواعد الاملائية في بعض الكلمات كما بينا ذلك مفصلا في هذا الكتاب، وكذلك لا تقاس كتاباتنا على خط العروض لانه يكتب على حسب الملفوظ به فمثلا هذا البيت تلق الامور بصبر جميل * وصدر رحيب وخل الحرج
فان العروضيين يكتبونه هكذا تلقق ل امور بصبرن جميلن وصدرن رحيبن وخلل ل حرج ومثله هذا البيت لا تسأل المرء عن خلائقه * في وجهه شاهد من الخبر فانهم يكتبونه هكذا لا تسأل ل مرء عن خلائقهى * في وجههى شاهدن من ل خبرى (٢) الاصل في هذه التسمية ما جاء في بعض الروايات ان عثمان بن عفان رضى الله عنه لما بلغه اختلاف المعلمين في القرآن قال عندي تكذبون به وتلحنون فيه فمن نآي عنى كان اشد تكذيبا واكثر لحنا يا اصحاب محمد اجتمعوا فاكتبوا للناس اماما - واما سبب تسميته بالمصحف فانه لما جمع أبو بكر رضي الله عنه القرآن قال سموه فقال بعضهم سموه انجيلا فكرهوه وقال بعضهم سموه السفر فكرهوه فقال ابن مسعود رأيت بالحبشة كتابا يدعونه المصحف فسموه به (*)',NULL,'3',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (7,'من حيث اتباعه رسما وكتابة (١) وهو يشمل جميع المصاحف التى كتبت بأمره رضي الله عنه وارسلت إلى الامصار، وقال بعضهم انه خاص بمصحفه الذي كان يقرأ فيه.
هذا ولما شرعنا في كتابة مصحفنا المذكور ووصلنا إلى نحو خمسة اجزاء منه، وجدنا في الرسم العثماني العجب العجاب، ورأيناه جديرا بدراسته وتحقيق النظر فيه، وحريا بأن تؤلف فيه رسالة خاصة تطبع وتنشر في الاقطار الاسلامية - فألفنا هذا الكتاب واستقصينا جميع انواع الكلمات المخالفة لقواعد كتاباتنا، اللهم الا ماشرد عن
النظر وغاب عن الفكر والحق يقال - ان في رسم المصحف العثماني يقف الفكر حائرا، والذهن تائها، إذ أنه في نفسه لا قاعدة له - فمثلا نجد كلمة " كتاب "
_________
(١) فان قيل ان المصحف العثماني الامام لم يكن فيه نقط ولا شكل ولم تكن فيه ارقام للآيات ولا علامات للاجزاء والاحزاب فكان الواجب حذف هذه الاشياء من المصاحف اتباعا للمصحف العثماني - نقول - ان هذه الامور حدثت فيما بعد حيث اختلفت الالسن باختلاط العرب بالعجم لانتشار الاسلام فخوفا من التصحيف والالتباس في كلمات القرآن اخترعوا هذه الاشياء التى هي ليست داخلة في جوهر الحروف وانما هي من العلامات الدالة على القراءة الصحيحة فصار وضعها من اللازم وسنتكلم عنها مفصلا في الخاتمة ان شاء الله تعالى وكان تقسيم القرآن إلى اجزاء واحزاب في زمن الحجاج (*)',NULL,'4',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (8,'مرسومة في جميع القرآن بغير ألف ما عدا اربعة مواضع (١) فانها مرسومة بالالف نحو " لكل أجل كتاب " وكلمة " قال " مرسومة في جميع القرآن بالالف ما عدا خمسة مواضع (٢) فانها بحذف الالف نحو " قل رب احكم بالحق " وكلمة " أيها " مرسومة في جميع القرآن بالف بعد الهاء ما عدا ثلاثة مواضع (٣) فانها بحذف الالف نحو " أيه الثقلان " وكلمة " ابراهيم " مرسومة في سورة البقرة هكذا " ابرهم " وفي بقية القرآن هكذا " ابرهيم " وكلمة " يا ابن أم " مرسومة في سورة طه هكذا " قال يبنؤم " وفي الاعراف هكذا " قال ابن أم " وكلمة " ما نشاء " مرسومة في سورة هود هكذا " ما نشؤا " وفي سورة الحج هكذا " ما نشاء " وكلمة " الامثال " مرسومة بالالف بعد الثاء ومرسومة بحذف الالف.
وحذفت الواو والياء من آخر هذين الفعلين " ويدع الانسان - فهو يشفين " من غير علة إلى غير ذلك من الكلمات التى قد تكتب في بعض المواضع بشكل وفى بعضها بشكل آخر مع ان الكلمة هي هي بعينها لم تتغير (٤) فمن يرشدنا إلى سبب هذا التغاير في رسم المصحف العثماني الا الصحابة
_________
(١ و ٢ و ٣) ذكرنا هذه المواضع كلها في آخر الفصل الثاني من الباب الخامس (٤) انظر في الجدول الثاني في الفصل الثاني من الباب الرابع، وانظر ايضا في الفصل الرابع من الباب الخامس في بعض غرائب الرسم العثماني (*)',NULL,'5',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (9,'الذين كتبوه بأمرنا عثمان وهذا إذا قاموا من قبورهم ولقد صدق من قال " كما ان للقرآن معجز في ذاته فخطه معجز ايضا " وإلى هذا المعنى اشار العلامة الشيخ محمد العاقب بن ما يأبى الشنقيطى دفين فاس رحمه الله تعالى بقوله.
والخط فيه معجز للناس * وحائد عن مقتضى القياس
لا تهتدى لسره الفحول * ولا تحوم حوله العقول
قد خصه الله بتلك المنزله * دون جميع الكتب المنزله
ليظهر الاعجاز في المرسوم * منه كما في لفظه المنظوم
والحقيقة ان تأليف كتابنا هذا هو من بركة كتابتنا للمصحف المذكور حيث كنا نتبع فيه الرسم العثماني كلمة كلمة، ولولاه لما كنا ندرك معنى الرسم العثماني ووجهة مخالفته لقواعد املائنا - وغاية ما كنا نعرف ان نحو * كتاب، وابراهيم، واسماعيل، واسحاق وهارون، وسليمان،،، مكتوب في المصحف بغير ألف، اما غيرها فلا تقع اعيننا عليه لتعود ألسنتنا على القراءة الصحيحة - والسبب في عدم ملاحظتنا هيئة رسم الكلمات في المصحف هو عدم الاعتناء بتعليم القراءات وفن الرسم وعلم التجويد حتى اندثرت من غالب البلاد الاسلامية وربما كانت مصر هي الوحيدة في المحافظة على هذه العلوم.',NULL,'6',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (10,'وهذا الكتاب هو أول كتاب من نوعه فانه لم يؤلف في هذا الموضوع على نمطه كتاب من قبل - نعم لقد ألف علماء القراءات المتقدمون في رسم المصحف العثماني مؤلفات جليلة وحصروا مرسوم القرآن كلمة كلمة على هيئة ما كتبه الصحابة رضى الله عنهم بحيث لم يفهم شئ منه، الا انهم لم يبحثوا عنه كما بحثنا، ولم يقارنوا بين مرسومه كما قارنا - على اننا لا ندعى المعرفة اكثر منهم بل نمشي على ضوئهم مع ما يفتح الله به علينا من فضله الواسع فهو الفتاح العليم لا راد لفضله.
ولقد بسطنا القول في هذا الكتاب عن القرآن العظيم من جميع نواحيه بسطا وافيا ولم نتعرض للناسخ والمنسوخ ولا لوجوه القراءات وتراجم القراء لان كلا من ذلك فن مستقل بذاته يحتاج إلى مؤلف خاص، وجعلنا في ذيله هامشا لزيادة الايضاح وتمام الفائدة، وحصرناه في ستة ابواب وخاتمة تحت كل باب جملة فصول، وسميناه " تاريخ القرآن وغرائب رسمه وحكمه " نسأل الله الحى القيوم أن يجعله خالصا لوجهه الكريم، وأن ينفع به من طالعه بقلب سليم، وأن يوفقنا لما يحبه ويرضاه، وأن يختم لنا بخاتمة السعادة ويسترنا في الدارين، ويجعلنا من الذين لا خوف عليهم
ولا هم يحزنون، وأن يحشرنا مع الذين أنعم الله عليهم من النبيين',NULL,'7',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (11,'والصديقين والشهداء والصالحين وحسن أولئك رفيقا آمين، وصلى الله على نبينا محمد أبى القاسم الامين، وعلى آله وصحبه أجمعين.
" وما توفيقي الا بالله عليه توكلت واليه أنيب "
غرة جمادى الثانية سنة ١٣٦٥ هجرية
المؤلف
محمد طاهر الكردى
الخطاط بالمعارف العامة بمكة المكرمة',NULL,'8',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (12,'<span data-type="title" id=toc-3>الجدول الاول وفيه بعض الكلمات بحسب رسم المصحف العثماني</span>
الله يبدؤا الخلق هي عصاي أتوكؤا عليها أو من وراى حجاب وما دعؤا الكفرين إلى فرعون وملايه وجائ يومئذ بجهنم وجاءو على قميصه قال يبنؤم لا تأخذ بأييكم المفتون وألو استقموا والسماء بنينها بأييد علمؤا بنى اسرءيل الذين أسئوا السوآى ولا تقولن لشائ أصحاب لئيكة امرأت فرعون قرت قل هو نبؤا عظيم ثلث مائة سنين أفإين مات أو قتل ألئن حصحص الحق ومن يعظم شعئر الله سنت الاولين فقال الملؤا الذين لايلاف قريش إى لفهم لكم أيه الثقلان لتخذت عليه أجرا أفتوني في رءيى رحمت الله وبركته ومعصيت الرسول ولا مستئنسين
_________
وقد وضعنا جدولا آخر في الفصل الثاني من الباب الرابع يشتمل على بعض كلمات بالرسم العثماني فراجعه في محله (*)',NULL,'9',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (13,'<span data-type="title" id=toc-4>الباب الاول</span>
(وفيه ثلاثة فصول)
<span data-type="title" id=toc-5>(الفصل الاول * في تعريف القرآن وما يتضمنه)</span>
ذهبوا في معنى القرآن إلى جملة أقوال ذكرها السيوطي في كتابه الاتقان والمختار منها ما نص عليه إمامنا الشافعي رحمه الله تعالى وهو: ان لفظ القرآن المعرف بأل ليس مهموزا ولا مشتقا بل وضع علما على الكلام المنزل على النبي صلى الله عليه وسلم واما القرآن فقد قال اهل السنة القرآن كلام الله تعالى منزل غير مخلوق منه بدأ واليه يعود وهو مكتوب في المصاحف محفوظ في الصدور مقروء بالالسنة مسموع بالآذان والاشتغال بالقرآن من أفضل العبادات سواء كان بتلاوته أو بتدبر فيه - قال الله تعالى - إن الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور ليوفيهم اجورهم ويزيدهم من فضله انه غفور شكور - وقال - كتاب انزلناه اليك مبارك ليدبروا آياته وليتذكر أولوا الالباب - وقال - الله نزل أحسن الحديث كتابا متشابها مثانى تقشعر منه جلود الذين يخشون ربهم وتلين جلودهم وقلوبهم إلى ذكر الله ذلك هدى الله يهدى به من يشاء ومن يضلل الله فماله من هاد',NULL,'10',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (14,'ولبعضهم: وإذا أردت من العلوم أجلها * فعليك بالقرآن والاعراب هذا لدينك ان أردت ديانة * وهدى وذاك لمنطق وخطاب ولبعضهم ايضا: نعم السمير كتاب الله ان له * حلاوة هي أحلى من جنى الضرب (١) به فنون المعاني قد جمعن فما * تفتر من عجب الا إلى عجب أمر ونهى وأمثال وموعظة * وحكمة أو دعت في أفصح الكتب لطائف يجتليها كل ذى بصر * وروضة يجتنيها كل ذى أدب فالقرآن يتضمن الاحكام، والشرائع، والامثال، والحكم، والمواعظ والتاريخ، ونظام الكون، وغير ذلك.
قال بعضهم: ألا انما القرآن تسعة أحرف * سأنبيكها في بيت شعر بلا خلل حلال حرام محكم متشابه * بشير نذير قصة عظة مثل فالقران ما ترك شيئا من أمور الدين الا وبينه، ولا من نظام الكون الا واوضحه، وفيه يقول الله تعالى " ونزلنا عليك الكتاب تبيانا لكل شئ وهدى ورحمة وبشرى للمسلمين " ويقول " ولقد ضربنا للناس في هذا القرآن من كل مثل لعلهم يتذكرون "
_________
(١) الضرب بفتحتين العسل الابيض قاله في المصباح (*)',NULL,'11',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (15,'وقال عليه الصلاة والسلام " كتاب الله فيه نبأ ما قبلكم وخبر ما بعدكم وحكم ما بينكم هو الفصل ليس بالهزل من تركه من جبار قصمه الله تعالى ومن ابتغى الهدى في غيره اضله الله تعالى وهو حبل الله المتين
وهو الذكر الحكيم وهو الصراط المستقيم وهو الذى لا تزيع به الاهواء ولا تلتبس به الالسنة ولا تشبع منه العلماء ولا يخلق على كثرة الرد ولا تنقضي عجائبه " أخرجه الترمذي.
ومعنى لا يخلق لا يبلى (وقال ايضا) " أعربو القرآن والتمسوا غرائبه " رواه البيهقى والحاكم عن ابى هريرة والمراد باعرابه معرفة معاني ألفاظه وما أحسن ما رواه الامام السيوطي رحمه الله تعالى في كتابه الاتقان عن بعضهم حيث يقول: اعتنى قوم بضبط لغات القرآن وتحرير كلماته ومعرفة مخارج حروفه وعددها، وعدد كلماته وآياته وسوره واحزابه وأنصافه وأرباعه، وعدد سجداته والتعليم عند كل عشر آيات إلى غير ذلك من حصر الكلمات المتشابهة والآيات المتماثلة، من غير تعرض لمعانيه ولا تدبر لما اودع فيه فسمو القراء - واعتني النحاة بالمعرب منه والمبنى من الاسماء والافعال والحروف العاملة وغيرها، وأوسعوا الكلام في الاسماء وتوابعها وضروب الافعال واللازم والمعتدى ورسوم خط الكلمات وجميع ما يتعلق به حتى ان بعضهم اعرب',NULL,'12',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (16,'مشكله وبعضهم اعربه كلمة كلمة - واعتني المفسرون بألفاظه فوجدوا منه لفظا يدل على معنى واحد ولفظا يدل على معنيين ولفظا يدل على اكثر فأجروا الاول على حكمه وأوضحوا معنى الخفى منه وخاضوا في ترجيح احد المحتملات ذى المعنيين أو المعاني وأعمل كل منهم فكره وقال بما اقتضاه نظره - واعتني الاصوليون بما فيه من الادلة العقلية والشواهد الاصلية والنظرية فاستنبطوا منه وسموا هذا العلم بأصول الدين - وتأملت طائفة منهم معاني خطابه فرأت منها ما
يقتضى العموم ومنها ما يقتضى الخصوص إلى غير ذلك فاستنبطوا منه أحكام اللغة من الحقيقة والمجاز - وتكلموا في التخصيص والاخبار والنص والظاهر والمجمل والمحكم والمتشابه والامر والنهى والنسخ، إلى غير ذلك من الاقيسة واستصحاب الحال والاستقراء وسموا هذا الفن أصول الفقه - وأحكمت طائفة صحيح النظر وصادق الفكر فيما فيه من الحلال والحرام وسائر الاحكام فأسسوا أصوله وفرعوا فروعه وبسطوا القول في ذلك بسطا حسنا وسموه بعلم الفروع وبالفقه ايضا - وتلمحت طائفة ما فيه من قصص القرون السالفة والامم الخالية ونقلوا أخبارهم ودونوا آثارهم ووقائعهم حتى ذكروا بدء الدنيا وأول الاشياء وسموا ذلك بالتاريخ والقصص -',NULL,'13',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (17,'وتنبه آخرون بما فيه من الحكم والامثال والمواعظ التى تقلقل قلوب الرجال فاستنبطوا مما فيه من الوعد والوعيد، والتحذير والتبشير، وذكر الموت والميعاد، والحشر والحساب، والعقاب والثواب، والجنة والنار فصولا من المواعظ واصولا من الزواجر فسموا بذلك الخطباء والوعاظ - وأخذ قوم بما في آية المواريث من ذكر السهام وأربابها وغير ذلك من علم الفرائض واستنبطوا منها من ذكر النصف والربع والسدس والثمن حساب الفرائض - ونظر قوم الي ما فيه من الآيات الدالة على الحكم الباهرة في الليل والنهار والشمس والقمر والنجوم والبروج وغير ذلك فاستخرجوا منه علم المواقيت - ونظر الكتاب والشعراء إلى ما فيه من جزالة اللفظ وبديع النظم وحسن السياق والمبادئ والمقاطع والمخالص والتلوين في الخطاب والاطناب والايجاز وغير ذلك
فاستنبطوا منه المعاني والبيان والبديع - انتهى جميع العلم في القرآن لكن * تقاصر عنه أفهام الرجال ويعجبنا وصف الاستاذ مصطفى صادق الرافعى رحمه الله تعالى (١) للقران الكريم حيث يقول في كتابه اعجاز القران ما نصه: القرآن الفاظ إذا اشتدت فأمواج البحار الزاخرة، وإذا هي لانت فأنفاس الحياة الآخرة، تذكر الدنيا فمنها عمادها ونظامها، وتصف الآخرة
_________
(١) توفى الرافعى المذكور في ١٠ مايو ١٩٣٧ م الموافق ٢٩ صفر ١٣٥٦ هجرية (*)',NULL,'14',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (18,'فمنها جنها وضرامها، ومتى وعدت من كرم الله جعلت الثغور تضحك في وجوه الغيوب، وان أوعدت جعلت الالسنة ترعد من حمى القلوب، ومعان بينا هي عذوبة ترويك من ماء البيان، ورقة تستروح منها نسيم الجنان، ونور تبصر به في مرآة الايمان وجه الامان، وبينا هي ترف بندى الحياة على زهرة الضمير، وتخلق في اوراقها من معاني العبرة معنى العبير، وتهب عليها بأنفاس الرحمة فتنم بسر هذا العالم الصغير، ثم بيناهى تتساقط من الافواه تسافط الدموع من الاجفان، وتدع القلب من الخشوع كأنه جنازة ينوح عليها اللسان، وتمثل للمذنب حقيقة الانسانية حتى يظن انه صنف آخر من الانسان، إذا هي بعد ذلك اطباق السحاب وقد انهارت قواعده، والتمعت ناره وقصفت في الجو رواعده، وإذا هي السماء وقد اخذت على الارض ذنبها واستأذنت في صدمة الفزع ربها، فكادت ترجف الراجفة تتبعها الرادفة، وانما هي عند ذلك زجرة واحدة، فإذا الخلق طعام الفناء وإذا الارض مائدة.
انتهى كلام الرافعى رحمه الله تعالى
هذا وان ومن عظمة القرآن في ذاته اقرار علماء الافرنج بسمو مكانته واعترافهم برفيع منزلته، وخشوعهم لدي سماع ترتيل آياته، واعجابهم بما حواه من نظام الكون ودستور المدنية والعمران.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (19,'وهناك بعض من نوابغ مستشرقي الافرنج من يتخصص لحفظ القرآن وفهم تفاسيره، ومن يعتنى بعلم القراءات وفن التجويد، ومن ينقطع إلى دراسته وبيان مزايا دين الاسلام ولهم في ذلك مؤلفات.
وان بقوا على ديانتهم.
وان أول طبع للمصحف بالخط العربي كان في مدينة همبرج بألمانيا وذلك في سنة ١١١٣ هجرية (١) ويوجد من هذه الطبعة مصحف بدار الكتب العربية المصرية بالقاهرة قال المستشرق الالماني الدكتور شومبس في القرآن الكريم في احدى الجمعيات " يقول بعض الناس ان القرآن كلام محمد - وهو خطأ محض - فالقرآن كلام الله تعالى الموحى على لسان رسوله محمد، فليس في استطاعة محمد ذلك الرجل الامي في تلك العصور الغابرة أن يأتينا بكلام تحار فيه عقول الحكماء ويهدى الناس من الظلمات إلى النور، وربما تعجبون من اعتراف رجل أو ربى بهذه الحقيقة، انى درست القرآن فوجدت فيه تلك المعاني العالية والنظامات المحكمة وتلك البلاغة التى لم اجد مثلها قط في حياتي، جملة واحدة منه تغنى
_________
(١) وسببه على ما يظهر لنا ان اختراع المطبعة كان في ألمانيا سنة ١٤٣١ ميلادية ثم عم انتشارها بقية الممالك، واول دخولها إلى تركيا كان في زمن السلطان احمد الثالث وكان طبع المصاحف في عهده ممنوعا وسنتكلم في آخر الكتاب عن ظهور
المطابع وانتشارها انشاء الله تعالى (*)',NULL,'16',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (20,'عن مؤلفات هذا ولا شك اكبر معجزة اتى بها محمد عن ربه " اه كلامه وقال المستشرق ما كس مننى: ان مرشد المسلمين هو القرآن وحده، والقرآن ليس بكتاب دينى فقط بل هو ايضا كتاب الآداب وتجد به الحياة السياسية والاجتماعية، بل هو يرشد الانسان إلى وظائفه اليومية، والاحكام الاسلامية التى لا توجد بالقرآن توجد في السنة والتى لا تكون واضحة لا في القرآن ولا في السنة توجد في الفقه الواسع الذى هو علم الحقوق الاسلامي اه كلامه وما قيمة ما يقوله الانسان في القرآن الكريم بعد قوله تعالى فيه " كتاب احكمت آياته ثم فصلت من لدن حكيم خبير " وقوله " أفلا يتدبرون القرآن ولو كان من عند غير الله لوجدوا فيه اختلافا كثيرا " وقوله " لو أنزلنا هذا القرآن على جبل لرأيته خاشعا متصدعا من خشية الله " وقوله " قل لئن اجتمعت الانس والجن على أن يأتوا بمثل هذا القرآن لا يأتون يمثله ولو كان بعضهم لبعض ظهيرا " (قال المقرى في اضاءة الدجنة) وأخبر الله بعجز الانس * والجن عن إتيانهم بالجنس من مثله وطولبوا بسوره * فما استطاعوا مثلها ضروره ومن لجلباب الحيا أزاحا * معارضا له حوى افتضاحا كمثل ما جاء به مسيلمه * من ترهات باختلال معلمه ركيكة في لفظها والمعنى * كقوله والطاحنات طحنا..الخ',NULL,'17',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (21,'<span data-type="title" id=toc-6>الفصل الثاني (القرآن في اللوح المحفوظ)</span>
جاء في تفسير ابن كثير في سورة القدر ما نصه: قال ابن عباس (١) وغيره أنزل الله القرآن جملة واحدة من اللوح المحفوظ إلى بيت العزة من السماء الدنيا ثم نزل مفصلا بحسب الوقائع في ثلاث وعشرين سنة على رسول الله صلى الله عليه وسلم - وجاء فيه ايضا عند قوله تعالى " بل هو قرآن مجيد في لوح محفوظ " ما نصه: وقال الحسن البصري ان هذا القرآن المجيد عند الله في لوح محفوظ ينزل منه ما يشاء على من يشاء من خلقه وقال الطبراني عن ابن عباس ان رسول الله صلى الله عليه وسلم قال ان الله تعالى خلق لوحا محفوظا من درة بيضاء صفحاتها من ياقوتة حمراء قلمه نور وكتابه نور لله في كل يوم ستون وثلاثمائة لحظة يخلق ويرزق ويميت ويحيى ويعز ويذل ويفعل ما يشاء. اه
_________
(١) هو عبد الله بن عباس بن عبد المطلب ولد بمكة قبل الهجرة بثلاث سنين وصح انه صلى الله عليه وسلم دعاله بقوله اللهم فقهه في الدين وعلمه التأويل اللهم علمه الحكمة وتاويل القرآن اللهم بارك فيه وانشر منه واجعله من عبادك الصالحين قال الحسن كان ابن عباس يقوم على منبرنا هذا فيقرأ البقرة وآل عمران فيفسرهما آية آية توفى رضى الله عنه بالطائف سنة ثمان وستين وقيل سنة تسع وقيل سنة سبعين وعمره احدى وسبعون سنة وله مناقب عظيمة (*)',NULL,'18',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (22,'<span data-type="title" id=toc-7>الفصل الثالث (في انزال القرآن)</span>
جاء في تفسير ابن كثير عند قوله تعالى " شهر رمضان الذى انزل فيه القرآن الآية " ما ملخصه روى الامام أحمد بن حنبل ان رسول الله صلى الله عليه وسلم قال انزلت صحف ابراهيم في أول ليلة من رمضان وأنزلت التوراة لست مضين من رمضان والانجيل لثلاث عشرة خلت من رمضان وانزل القرآن لاربع وعشرين خلت من رمضان، وفي حديث جابر بن عبد الله ان الزبور انزل لثنتى عشرة خلت من رمضان والانجيل لثماني عشرة والباقى كما تقدم.
وقال اسرائيل عن السدى عن محمد بن ابى المجالد عن مقسم عن ابن عباس انه سأل عطية بن الاسود فقال وقع في قلبى الشك قول الله تعالى " شهر رمضان الذى انزل فيه القرآن " وقوله " انا انزلناه في ليلة مباركة " وقوله " انا أنزلناه في ليلة القدر " وقد انزل في شوال وفي ذى القعدة وفي ذى الحجة وفي المحرم وصفر وشهر ربيع فقال ابن عباس انه انزل في رمضان في ليلة القدر وفي ليلة مباركة جملة واحدة ثم انزل على مواقع النجوم ترتيلا في الشهور والايام * وفي رواية سعيد بن جبير عن ابن عباس قال انزل القرآن',NULL,'19',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (23,'في النصف الثاني من شهر رمضان إلى سماء الدنيا فجعل في بيت العزة (١) ثم انزل على رسول الله صلى الله عليه وسلم في عشرين سنة (٢) لجواب كلام الناس وفي رواية عكرمة عن ابن عباس قال نزل القرآن في شهر رمضان في ليلة القدر إلى هذه السماء الدنيا جملة واحدة وكان الله يحدث لنبيه ما يشاء ولا يجئ المشركون بمثل يخاصمون به الا جاءهم الله بجوابه وذلك قوله " وقال الذين كفروا لولا انزل عليه القرآن جملة واحدة كذلك لنثبت به فؤادك ورتلناه ترتيلا ولا يأتونك بمثل الا جئناك بالحق وأحسن تفسيرا " اه من تفسير ابن كثير

<span data-type="title" id=toc-8>الباب الثاني</span>
(وفيه خمسة فصول)
<span data-type="title" id=toc-9>(الفصل الاول في جمع القرآن الكريم)</span>
يطلق جمع القرآن تارة على حفظه في الصدور وتارة على كتابته فعلى المعنى الثاني نقول: ان القرآن جمع ثلاث مرات (الجمع الاول) كتب كله في عهد النبي صلى الله عليه وسلم لكن غير مجموع في موضوع واحد
_________
(١) وهو البيت المعمور وهو مسامت للكعبة بحيث لو نزل لنزل عليها (٢) وفي رواية ابن عباس السابقة في ثلاث وعشرين سنة لان بعضهم يقول كان صلى الله عليه وسلم اول ما نزل عليه القرآن في الاربعين من عمره على أرجح الاقوال، وبعضهم يقول كان في الثالثة والاربعين وبعضهم يقول كان في الثانية والاربعين وهجرته صلى الله عليه وسلم إلى المدينة كانت بعد مضى ثلاث وخمسين سنة من مولده صلى الله عليه وعلى آله وصحبه وسلم (*)',NULL,'20',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (24,'ولا مرتب السور بل كان مفرقا في العسب واللخاف والرقاع والاقتاب (١) ونحوها مع كونه محفوظا في الصدور - فقد روي الحاكم في المستدرك عن زيد بن ثابت قال كنا عند رسول الله صلى الله عليه وسلم نؤلف القرآن من الرقاع الحديث - وعنه ايضا قال كنت أكتب الوحى عند رسول الله صلى الله عليه وسلم وهو يملى على فإذا فرغت قال اقرأه فأقرؤه فان كان فيه سقط أقامه وروى البخاري عن البراء قال لما نزلت " لا يستوى القاعدون من المؤمنين والمجاهدون في سبيل الله " قال النبي صلى الله عليه وسلم ادع لى زيدا وليجئ باللوح والدواة والكتف أو الكتف والدواة ثم قال أكتب لا يستوى القاعدون الحديث، وأخرج مسلم من حديث ابى سعيد قال قال رسول الله صلى الله عليه وسلم لا تكتبوا عنى شيأ غير القرآن (٢) قال
_________
(١) العسب بضم فسكون وبضمتين ايضا جمع عسيب وهو جريد النخل كانوا يكشطون الخوص ويكتبون في الطرف العريض، واللخاف بكسر اللام جمع لخفة بفتح فسكون وتجمع ايضا على لخف بضمتين وهى صفائح الحجارة الرقاق، والرقاع بالكسر جمع رقعة بالضم وهى القطعة من النسيج أو الجلد والاقتاب جمع قتب بفتحتين وهو رحل البعير (٢) قال النووي في شرحه على صحيح مسلم عند هذا الحديث ما ملخصه: قيل انما نهي عن كتابة الحديث مع القرآن في صحيفة واحدة لئلا يختلط فيشتبه على القارئ وقيل ان حديث النهي منسوخ بجملة احاديث " ذكرها النووي في شرحه " ثم قال قال القاضي كان بين السلف من الصحابة والتابعين اختلاف كثير في كتابة العلم فكرهها كثيرون منهم واجازها اكثرهم ثم اجمع المسلمون على جوازها وزال ذلك الخلاف اه (*)',NULL,'21',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (25,'الحارث المحاسبى في كتاب فهم السنن كتابة القرآن ليست بمحدثة فانه صلى الله عليه وسلم كان يأمر بكتابته ولكنه كان مفرقا في الرقاع والاكتاف والعسب اه وعدم جمعه في مجلد في حياته عليه الصلاة والسلام كان لامرين (الاول) الامن فيه من وقوع خلاف بين الصحابة لوجوده صلى الله عليه وسلم بين أظهرهم (الثاني) خوف نسخ شئ منه بوحى قرآن بدله، ففى الاتقان قال الخطابى انما لم يجمع صلى الله عليه وسلم القرآن في مصحف لما كان يترقبه من ورود ناسخ لبعض أحكامه أو تلاوته فلما انقضى نزوله بوفاته (١) ألهم الله الخلفاء الراشدين ذلك وفاء بوعده الصادق بضمان حفظه على هذه الامة وإلى ما تقدم اشار العلامة الشيخ محمد العاقب الشنقيطى رحمه الله بقوله لم يجمع القرآن في مجلد * على الصحيح في حياة أحمد
للامن فيه من خلاف ينشأ * وخيفة النسخ بوحى يطرأ وكان يكتب على الاكتاف * وقطع الادم واللخاف وبعد إغماض النبي فالاحق * أن أبا بكر بجمعه سبق جمعه غير مرتب السور * بعد إشارة إليه من عمر ثم تولى الجمع ذو النوزين * فضمه ما بين دفتين مرتب السور والآيات * مخرجا بأفصح اللغات
_________
(١) توفي رسول الله صلى الله عليه وسلم يوم الاثنين ١٣ ربيع الاول سنة احدى عشرة للهجرة (*)',NULL,'22',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (26,'(الجمع الثاني) جمع ابى بكر الصديق رضى الله عنه روى البخاري في صحيحه عن عبيد بن السباق (١) ان زيد ابن ثابت رضى الله عنه قال أرسل إلى أبو بكر مقتل اهل اليمامة فإذا عمر بن الخطاب عنده قال أبو بكر رضى الله عنه ان عمر اتانى فقال ان القتل قد استحر يوم اليمامة (٢) بقراء القرآن وانى أخشى أن يستحر القتل بالقراء بالمواطن فيذهب كثير من القرآن وانى ارى أن تأمر بجمع القرآن فقلت لعمر كيف تفعل شيئا لم يفعله رسول الله صلى الله عليه وسلم قال عمر هذا والله خير فلم يزل عمر يراجعني حتى شرح الله صدري لذلك ورأيت في ذلك الذى رأى عمر قال زيد قال أبو بكر انك رجل شاب عاقل لانتهمك وقد كنت تكتب الوحى لرسول الله صلى الله عليه وسلم فتتبع القرآن فاجمعه فو الله لو كلفوني نقل
_________
(١) قال في فتح الباري عبيد بن السباق بفتح المهملة وتشديد الموحدة مدني يكنى أبا سعد ذكره مسلم في الطبقة الاولى من التابعين
(٢) استحر القتل أي اشتد واليمامة واقعة جهة نجد وكانت مع مسيلمة الكذاب الذى ادعى النبوة وقد قتل في هذه الوقعة وابتدأت غزوتها في اواخر عام الحادي عشر وانتهت في ربيع الاول عام الثاني عشر للهجرة وفيها قتل من القراء سبعون قارئا من الصحابة وقيل سبعمائة وقد قتل منهم مثل هذا العدد في بئر معونة قرب المدينة في عهد النبي صلي الله عليه وسلم، ولا يخفي ان قتل مثل هذا العدد من القراء ليس بقليل خصوصا والكتابة ما كانت منتشرة عندهم حتى يرجعوا إلى ما كتبوه بل كان اعتمادهم على ما في صدورهم (*)',NULL,'23',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (27,'جبل من الجبال ما كان أثقل على مما أمرنى من جمع القرآن (١) قلت كيف تفعلون شيئا لم يفعله رسول الله صلى الله عليه وسلم قال هو والله خير فلم يزل ابو بكر براجعنى حتى شرح الله صدري للذي شرح الله له صدر ابى بكر وعمر رضى الله عنهما فتتبعت القرآن أجمعه من العسب واللخاف وصدور الرجال حتى وجدت آخر سورة التوبة مع ابى خزيمة الانصاري لم أجدها مع احد غيره (٢) " لقد جائكم رسول من انفسكم عزيز عليه ما عنتم " حتى خاتمة براءة فكانت الصحف عند ابى بكر حتى توفاه الله
_________
(١) استثقاله لهذا الامر لم يكن من جهة ما يحصل له من المشقة والتعب وانما كان خوفا من اقدامه على امر لم يفعله رسول الله صلى الله عليه وسلم ولم يأمر به فلما ظهرت له المصلحة في ذلك اقدم عليه بهمة ونشاط (٢) أي لم يجد صحيفتها والا فهي محفوظة في الصدور، أولم يجد في آخر سورة التوبة قراءة من الاحرف السبعة الا عند ابى خزيمة الانصاري فتأمل - ولم يذكره احد انه من حفاظ القرآن ولكن قد يحفظ منه بعض السور والايات وقد جاء من طريق ابى العالية انهم لما جمعوا القرآن في خلافة ابى بكر كان الذى
يملى عليهم ابى بن كعب فلما انتهوا من براءة إلى قوله يفقهون ظنوا ان هذا آخر ما نزل منها فقال ابى بن كعب اقرأني رسول الله صلى الله عليه وسلم آيتين بعدهن " لقد جاءكم رسول من انفسكم إلى آخر السورة " قال في فتح الباري شرح صحيح البخاري - الارجح ان الذى وجد معه آخر سورة التوبة أبو خزيمة بالكنية والذى وجد معه الآية من الاحزاب خزيمة، وابو خزيمة قيل هو بن اوس بن زيد بن أصرم مشهور بكنيته دون اسمه وقيل هو الحارث بن خزيمة واما خزيمة فهو ابن ثابت ذو الشهادتين اه من الفتح (*)',NULL,'24',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (28,'ثم عند عمر حياته ثم عند حفصة بنت عمر رضى الله عنه (١) رواه البخاري في كتاب التفسير في باب جمع القرآن ورواه ايضا في الكتاب المذكور في سورة براءة وجاء في رواية ابن ابى داود أن عمر ابن الخطاب سأل عن آية من كتاب الله فقيل كانت مع فلان قتل يوم اليمامة فقال انا لله فأمر بجمع القرآن - اي راجع ابا بكر حتى أمر بجمعه (ويحتار بعضهم) في فهم هذه الرواية كيف ان الآية التى سأل عنها عمر لا توجد الا مع فلان الذى قتل يوم اليمامة فنقول ان منطوق الرواية لا يدل على حصر الآية عند فلان فهناك غيره ممن يحفظها ايضا فعمر لما سمع بقتل فلان يوم اليمامة خاف من قتل حفاظ كلام الله تعالى ان يضيع
_________
(١) تقول دائرة المعارف الاسلامية لماذا أو دعت الصحف عند حفصة ولم تودع عند الخليفة الجديد الذى ولى امر المسلمين وهو عثمان بن عفان (فنقول) أو دعت الصحف عند حفصة بوصية من ابيها عمر بن الخطاب لانها كانت تحفظ القرآن كله في صدرها وكانت تقرأ وتكتب وهى زوجة رسول الله صلى الله عليه وسلم ام المؤمنين وابنة عمر بن الخطاب خليفة المسلمين، ثم انه لم يتعين خليفة
حين وفاة عمر حتي تسلم إليه لان عمر لم يوص بالخلافة إلى أحد وانما جعلها شورى في بضعة اشخاص فلهذه الاعتبارات كانت حفصة رضى الله عنها أولى من غيرها بحفظ المصحف، ونظر عمر أصوب وأحكم.
وفي كتاب الاصابة قال أبو عمر أوصى عمر إلى حفصة وأوصت حفصة إلى اخيها عبد الله بما اوصى به إليها عمر وبصدقة تصدقت بها بالغابة.
توفى عمر رضى الله عنه في ذى الحجة سنة ثلاث وعشرين.
(*)',NULL,'25',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (29,'القرآن فراجع ابا بكر في ذلك حتى جمعه في الصحف.
وجاء في رواية اخرى: ان عمر بن الخطاب دخل على ابى بكر فقال ان اصحاب رسول الله صلى الله عليه وسلم باليمامة يتهافتون تهافت الفراش في النار وانى اخشى ان لا يشهدوا موطنا الا فعلوا ذلك حتى يقتلوا وهم حملة القرآن فيضيع القرآن وينسى ولو جمعته وكتبته فنفر منها أبو بكر وقال أفعل ما لم يفعل رسول الله صلى الله عليه وسلم فتراجعا في ذلك ثم ارسل أبو بكر إلى زيد ابن ثابت (١) قال زيد فدخلت عليه وعمر مسربل (٢) فقال لى أبو بكر
_________
(١) هو زيد بن ثابت بن الضحاك الانصاري الخزرجي يقال انه شهد أحدا واستصغر يوم بدر ويقال اول مشاهده الخندق وكتب الوحى وغيره للنبى صلى الله عليه وسلم وكان من علماء الصحابة واعلمهم بالفرايض وفيه جاء الحديث افرض امتي زيد بن ثابت، وعن خارجة بن زيد عن ابيه قال اتي بى النبي صلى الله عليه وسلم مقدمه المدينة فقيل هذا من بني النجار وقد قرأ سبع عشرة سورة فقرأت عليه فأعجبه ذلك فقال تعلم كتاب يهود فاني ما آمنهم على كتابي ففعلت فما مضى لى نصف شهر حتي حذقته فكنت اكتب له إليهم وإذا كتبوا إليه قرأت له، استخلفه عمر بن الخطاب على المدينة ثلاث مرات وكان عثمان إذا حج يستخلفه
على المدينة ايضا ورمى زيد يوم اليمامة بسهم فلم يضره - مات وهو ابن ست وخمسين وقيل اربع وخمسين واختلف في وقت وفاته فقيل سنة خمس واربعين وقيل غير ذلك قال أبو هريرة حين مات زيد اليوم مات حبر هذه الامة وعسى الله ان يجعل في ابن عباس منه خلفا اه ملخصا من الاصابة والاستيعاب، والمراد بكتاب يهود السريانية كما في الرواية الاخرى الآتية في الفصل الثاني، وقيل العبرانية والله تعالى اعلم (٢) السربال ما يلبس من قميص أو درع - قاله في المصباح (*)',NULL,'26',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (30,'ان هذا قد دعاني إلى أمر فأبيت عليه وانت كاتب الوحى فان تكن معه اتبعتكما وان توافقني لا أفعل فاقتص أبو بكر قول عمر وعمر ساكت فنفرت من ذلك وقلت يفعل ما لم يفعل رسول الله صلى الله عليه وسلم إلى أن قال عمر كلمة وما عليكما لو فعلتما ذلك فذهبنا ننظر فقلنا لا شئ والله ما علينا في ذلك شئ قال زيد فأمرني أبو بكر فكتبته في قطع الادم وكسر الاكتاف والعسب (١) اه وهذه الرواية أوردها الطبري في تفسيره وهل اللفظ واحد ام لا يحتاج إلى المراجعة.
وكان زيد لا يقبل من احد شيئا حتى يشهد شهيدان فان ابا بكر قال لعمر ولزيد اقعدا على باب المسجد فمن جاء كما بشاهدين على شئ من كتاب الله فاكتباه - اخرجه ابن ابى داود من طريق هشام بن عروة عن ابيه (٢) جاء في كتاب نهاية القول المفيد (فان قيل) كان زيد حافظا للقرآن وجامعا له فما وجه تتبعه المذكورات (والجواب) انه كان يستكمل وجوه قراءاته ممن عنده ما ليس عنده وكذا نظره في
المكتوبات التى قد عرف كتابتها وتيقن امرها فلا بد من النظر فيها
_________
(١) الادم بضمتين وبفتحتين ايضا جمع اديم وهو الجلد المدبوغ، والاكتاف جمع كتف وهو عظم عريض يكون في اصل كتف الحيوان، والعسب بضم فسكون وبضمتين ايضا جمع عسيب وهو جريد النخل إذا نزع منه خوصه (٢) انظر الفصل الثاني في احتياط الصحابة في كتابة القرآن (*)',NULL,'27',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (31,'وان كان حافظا ليستظهر بذلك وليعلم هل فيها قراءة غير قراءته ام لا وإذا استند الحافظ عند الكتابة إلى اصل يعتمد علية كان آكد واثبت في ضبط المحفوظ وجاء في ارشاد القراء والكاتبين: ان زيدا كتب القرآن كله بجميع اجزائه وأوجهه المعبر عنها بالاحرف السبعة الواردة في حديث ان هذا القرآن انزل على سبعة احرف فاقرؤا ما تيسر منه وكان اولا اتاه جبريل فقال له إن الله يأمرك أن تقرئ أمتك القرآن على حرف واحد ثم راجعه إلى السابعة فقال ان الله يأمرك أن تقرئ أمتك القرآن على سبعة أحرف فأيما حرف قرؤا عليه أصابوا (١) اه من عنوان البيان في علوم التبيان.
فأبوبكر رضى الله عنه هو اول من جمع القرآن الكريم بالاحرف السبعة التى نزل بها واليه تنسب الصحف البكرية وكان ذلك بعد وقعة اليمامة التى كان انتهاؤها سنة اثنتى عشرة للهجرة - فجمعه للقرآن كان في سنة واحدة تقريبا (٢) لانه وقع ببن غزوة اليمامة وبين وفاته
_________
(١) سيأتي شرح هذا الحديث في الفصل الخامس من الباب الثاني (٢) لولا همة الصحابة الذين بذلوا أنفسهم لله لما تم في مدة سنة واحدة كتابة
المصحف بالاحرف السبعة كلها وجمعه من الاحجار والعظام والجلود ونحوها فانظر إلى توفيق الله لهم وعنايته بهم وتأمل كيف خدموا الدين ونشروا الاسلام رضى الله عنهم (*)',NULL,'28',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (32,'رضى الله عنه التى كانت في جمادى الثانيه سنة ثلاثة عشر - قال على بن ابى طالب أعظم الناس في المصاحف اجرا أبو بكر رحمة الله على أبى بكر هو أول من جمع كتاب الله.
(ويسأل بعضهم) لماذا لم يأمر أبو بكر أو عمر أن ينسخ الناس مصاحف مما كتبه زيد بن ثابت ولماذا لم يحرص كبار الصحابة على ان يكون لدي كل واحد منهم أو لدى بعضهم على الاقل نسخ من هذه الصحف التى تتضمن كتاب الله.
(فنقول) ان ابا بكر رضى الله عنه لم يجمع القرآن لحدوث خلل في قراءته وانما جمعه خوفا من ذهاب حملته بقتلهم في الغزوات وكان جمعه له بالاحرف السبعة والناس يقرؤن بها إلى زمن عثمان فلا يختلف مصحف ابى بكر عما يقرؤه الناس ويحفظونه فلا داعى إذا لحمل الناس على مصحفه.
اما عثمان رضى الله عنه فانه لم يجمع القرآن الا بعد أن رآى اختلاف الناس في قراءته حتى ان بعضهم كان يقول ان قراءتى خير من قراءتك وكان جمعه له بحرف واحد وهو لغة قريش وترك الاحرف الستة الباقية فكان من الواجب حمل الناس على اتباع مصحفه وعلى قراءته بحرف واحد فقط قبل ان يختلفوا فيه',NULL,'29',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (33,'اختلاف اليهود والنصارى كما ترى تفصيل ذلك في الجمع الثالث اما عدم نسخ كبار الصحابة مصاحف على نمط ما جمعه أبو بكر فلم يكن هناك ما يدعو لذلك لعدم اختلاف ما جمعه أبو بكر بما عند الناس، وان بعضهم كتبوا مصاحفهم على عهد النبي صلى الله عليه وسلم وتلقوه منه سماعا - فكان جمع ابى بكر بمثابة سجل للقرآن يرجع إليه إذا حدث أمر كما وقع لعثمان حين جمعه القرآن فانه رجع إلى الصحف البكرية وكانت عند حفصة بنت عمر (ويسأل بعضهم ايضا) لم لم يجتمع أبو بكر وعمر وعثمان وعلى على نسخ المصحف وهم يحفظونه كله في صدورهم (فنقول) ان ابا بكر هو خليفة المسلمين وهؤلاء هم كبار الصحابة وهم اصحاب الرأى والشورى ومنهمكون في الغزوات ونشر الاسلام والنظر في مصالح الامة فاشتغالهم بأنفسهم بجمع القرآن يمنعهم عن النظر في شؤن المسلمين لان التفرغ لجمعه يحتاج إلى مدة طويلة وعناء عظيم - وإذا عرفت انهم كانوا يجمعونه مما كتب على نحو العظام والالواح والحجارة وانهم ما كانوا يقبلون من احد شيئا من القرآن الا بشاهدين علمت انهم يحتاجون في البحث والترتيب والمراجعة',NULL,'30',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (34,'والتصحيح إلى مدة غير قصيرة، وظهر لك ما تحملوه من المشقة العظمى والتعب الكبير - خصوصا وانهم في هذه المرة جمعوه بالاحرف السبعة كلها وهذا يستلزم أن يكون حجم مصحف ابى بكر أضعاف حجم مصحف عثمان لان هذا جمعه على حرف واحد من الاحرف السبعة.
لذلك اسند الخلفاء الاربعة جمع القرآن إلى زيد بن ثابت كاتب الوحى بين يدى رسول الله صلى الله عليه وسلم وهو الذى شهد العرضة الاخيرة وكان من حفظة القرآن وأعلم الصحابة فقام بهذه المهمة خير قيام في مصحف أبى بكر وفي مصحف عثمان رضى الله عنهم (١) وجزاهم عن الاسلام والمسلمين خير الجزاء والحقيقة لو لم يلهم الله تعالى هؤلاء الصحابة الكرام بجمع القرآن العظيم بكتابته في الصحف لذهب بموت حفاظه وانقراض الصحابة وهذا مصداق عزوجل " انا نحن نزلنا الذكر وانا له لحافظون " ولقد كان سعى عمر رضى الله عنه لجمع القرآن من فضائله التى
_________
(١) كان عمر زيد حين كتب مصحف ابي بكر نحو اثنتين وعشرين سنة وكان عمره حين كتب مصحف عثمان نحو خمس وثلاثين سنة (*)',NULL,'31',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (35,'لا تحصى ومناقبه التى لا تستقصى كيف وقد قال فيه صلى الله عليه وسلم ان الله جعل الحق على لسان عمر وقلبه، وقال * (لقد كان فيما قبلكم من الامم محدثون فان يكن في امتى أحد فانه عمر) * رواه البخاري ومسلم قوله محدثون هو بفتح الدال المهملة وتشديدها أي ملهمون وكما نزل القرآن بموافقته في أسرى بدر وفي الحجاب وفي تحريم الخمر وافق عمر الحق والصواب وفى اشارته على ابى بكر بجمع القرآن ولقد جمع بعضهم موافقات عمر رضى الله عنه في منظومة أولها الحمدلله وصلى الله * على نبيه الذى اجتباه يا سائلي والحادثات تكثر * عن الذى وافق فيه عمر وما يرى انزل في الكتاب * موافقا لرأيه الصواب
(الجمع الثالث) جمع عثمان بن عفان رضى الله عنه (١) ولم ينقل انه كتب بيده مصحفا وانما امر بجمعه وكتابته على حرف واحد من الاحرف السبعة التى نزل بها القرآن فلذلك ينسب إليه ويقال " المصحف العثماني "
_________
(١) تولى عثمان لآخر يوم من ذى الحجة سنة ثلاث وعشرين للهجرة فاستقبل بخلافته المحرم عام اربع وعشرين وقتل في ذي الحجة سنة خمس وثلاثين (*)',NULL,'32',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (36,'وسببه كما في البخاري عن أنس ان حذيفة بن اليمان (١) قدم على عثمان وكان يغازى أهل الشام في فتح أرمينية وأذريجان مع اهل العراق
_________
(١) هو حذيفة بن اليمان العبسى واسم اليمان حسيل بن جابر واليمان لقب كان ابوه قد اصاب دما فهرب إلى المدينة فحالف بنى عبد الاشهل فسماه قومه اليمان لكونه حالف اليمانية وتزوج والدة حذيفة وهى من الانصار اسمها الرباب بنت كعب بن عدى فولد له بالمدينة واسلم حذيفة وابوه وأرادا شهود بدر فصدهما المشركون وشهدا أحدا فاستشهد اليمان بها وشهد حذيفة الخندق وله بها ذكر حسن وما بعدها * وروي حذيفة عن النبي صلى الله عليه وسلم الكثير وهو معروف في الصحابة بصاحب سر رسول الله صلى الله عليه وسلم روى مسلم عن عبد الله بن يزيد الخطمى عن حذيفة قال لقد حدثني رسول الله صلى الله عليه وسلم ما كان وما يكون حتى تقوم الساعة وكان عمر ينظر إليه عند موت من مات منهم فان لم يشهد جنازته حذيفة لم يشهدها عمر وكان فتح همدان والرى والدينور على يد حذيفة، سئل حذيفة اي الفتن اشد قال ان يعرض عليك الخير والشر فلا تدرى أيهما تركب قال العجلى استعمله عمر على المدائن فلم يزل بها حتى مات بعد قتل عثمان وبعد بيعة على بأربعين
يوما وذلك سنة ست وثلاثين اه ملخصا من الاصابة والاستيعاب وحذيفة هذا هو الذي يقول " كان الناس يسألون رسول الله صلى الله عليه وسلم عن الخير وكنت أسأله عن الشر مخافة أن يدركنى فقلت يارسول الله انا كنا الخ " وتمام الحديث في البخاري في كتاب الفتن وفي علامات النبوة أيضا وتمامه في صحيح مسلم في كتاب الامارة في باب الامر بلزوم الجماعة ولولا التطويل لسقنا الحديث بتمامه.
فانه حديث مهم (ونقول) بما ان النبي صلى الله عليه وسلم حدث حذيفة بما كان وما يكون لى يوم القيامة لا يبعد أن يسر عليه الصلاة والسلام إليه ان يحرض عثمان لجمع القرآن على حرف واحد إذا رآى اختلاف الناس في قراءته فكتم حذيفة هذا الامر حتى جاء وقته (*)',NULL,'33',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (37,'فأفزع حذيفة اختلافهم في القراءة فقال حذيفة لعثمان يا امير المؤمنين أدرك هذه الامة قبل أن يختلفوا في الكتاب اختلاف اليهود والنصارى (١) فأرسل عثمان إلى حفصة أن أرسلي الينا بالصحف ننسخها في المصاحف ثم نردها اليك فأرسلت بها حفصة إلى عثمان فأمر زيد ابن ثابت (٢) وعبد الله بن الزبير (٣) وسعيد بن العاص (٤) وعبد الرحمن
_________
(١) وفي رواية قدم حذيفة من أرمينيا فلم يدخل بيته حتى أتى عثمان فقال يا امير المؤمنين أدرك الناس.
الخ.
وقد ذكر ابن حجر في فتح الباري عند هذا الحديث روايات كثيرة فيما اختلفوا فيه من القراءات لم ننقلها هنا خوف التطويل فراجعها ان شئت (٢) تقدمت ترجمة زيد عند جمع أبى بكر للمصحف في صحيفه ٢٦ (٣) هو عبد الله بن الزبير بن العوام وامه أسماء بنت ابى بكر ولدته سنة
ثنتين من الهجرة وقيل ولد في السنة الاولى وهو اول مولود ولد في الاسلام من المهاجرين بالمدينة وكانت به لسانة وفصاحة وكان كثير الصلاة والصيام بويع له بالخلافة سنة اربع وستين وقيل خمس وستين وقتل في ايام عبد الملك سنة ثلاث وسبعين.
اه ملخصا من الاستيعاب (٤) هو سعيد بن العاص بن سعيد بن العاص بن امية ولد عام الهجرة وقيل بل سنة احدي وكان احد اشراف قريش ممن جمع السخاء والفصاحة وهو احد الذين كتبوا المصحف لعثمان استعمله عثمان على الكوفة وغزا بالناس طبرستان فافتتحها توفى في خلافة معاوية سنة تسع وخمسين.
اه ملخصا من الاستيعاب " وقد ورد ابن العاص بالياء وبغير ياء (*)',NULL,'34',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (38,'ابن الحارث بن هشام (١) فنسخوها في المصاحف (٢) وقال عثمان للرهط القرشيين الثلاثة إذا اختلفتم انتم وزيد بن ثابت في شئ من القرآن فاكتبوه بلسان قريش فانه انما نزل بلسانهم ففعلوا (٣) حتى إذا نسخوا
_________
(١) هو عبد الرحمن بن الحارث بن هشام بن المغيرة المخزومى تزوج عمر أمه فنشأ في حجر عمر وتزوج بنت عثمان ثم كان ممن ندبه عثمان لكتابة المصاحف من شباب قريش قال ابن سعد كان من اشراف قريش قال ابن حبان مات سنة ثلاث واربعين.
اه ملخصا من الاصابة (٢) واخرج ابن أبى داود انه جمع اثنى عشر رجلا من قريش والانصار وقال لهم إذا اختلفتم في لغة فاكتبوه بلغة قريش فلم يختلفوا الا في التابوت في البقرة فقال زيد بالهاء وقال عيره بالتاء فكتبوه بالتاء (٣) وفي البخاري في كتاب التفسير في باب نزل القرآن بلسان قريش والعرب " وقال لهم (أي عثمان لزيد ومن معه من المذكورين) إذا اختلفتم انتم
وزيد بن ثابت في عربية من عربية القرآن فاكتبوها بلسان قريش فان القرآن انزل بلسانهم ففعلوا ".
جاء في فتح الباري شرح صحيح البخاري قال القاضى أبو بكر الباقلانى معنى قول عثمان نزل القرآن بلغة قريش أي معظمه وان لم تقم دلالة قاطعة على ان جميعه بلسان قريش فان ظاهر قوله تعالى انا جعلناه قرآنا عربيا انه نزل بجميع ألسنة العرب..الخ كلامه " وقال أبو شامة يحتمل ان يكون قوله نزل بلسان قريش اي ابتداء نزوله ثم أبيح ان يقرأ بلغة غيرهم كما سيأتي تقريره في باب انزل القرآن على سبعة احرف اه وتكميله ان يقول انه نزل أولا بلسان قريش احد الاحرف السبعة ثم نزل بالاحرف السبعة المأذون (*)',NULL,'35',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (39,'الصحف في المصاحف رد عثمان الصحف إلى حفصة وارسل إلى كل أفق بمصحف مما نسخوا وأمر بما سواه من القرآن في كل صحيفة أو مصحف أن يحرق (١) قال ابن شهاب واخبرني خارجة بن زيد ابن ثابت سمع زيد بن ثابت قال فقدت آية من الاحزاب حين نسخنا المصحف قد كنت اسمع رسول الله صلى الله عليه وسلم يقرأ بها فالتمسناها فوجدناها
_________
في قراءتها تسهيلا وتيسيرا كما سيأتي بيانه فلما جمع عثمان الناس على حرف واحد راى ان الحرف الذى نزل القرآن اولا بلسانه أولى الاحرف فحمل الناس عليه لكونه لسان النبي صلى الله عليه وسلم ولما له من الاولية المذكورة وعليه يحمل كلام عمر لابن مسعود ايضا اه من فتح الباري، وكلام عمر لابن مسعود سيأتي قريبا في الهامش (١) والسبب في احراقها هو قطع جذور اختلاف الناس في القراءة فقد يكون بعضهم كتب شيئا من القرآن على غير وجه صحيح لما نشأ فيهم من الخلاف الذى كان سببا في قيام عثمان بجمع القرآن وحمل الناس عليه - فباحراق تلك
الصحف تتوحد قراءتهم على حرف واحد حسب ما في مصحف عثمان * روي ابو بكر بن ابى داود باسناد صحيح عن مصعب بن سعد بن ابى وقاص قال ادركت الناس متوافرين حين حرق عثمان المصاحف فأعجبهم ذلك أو قال لم ينكر ذلك منهم احد اه " وانما لم يأمر عثمان بحرق صحف حفصة لانها كتبت بأمر ابى بكر بالاحرف السبعة لا يتطرقها الشك وعنها نقل مصحفه ولانه وعدها بردها إليها فبقيت عندها إلى وفاتها فلما توفيت استردها مروان حين كان اميرا بالمدينة من جهة معاوية وأمر بتشقيقها وغسلها وقال انما فعلت هذا لانى خشيت ان طال بالناس زمان أن يرتاب في شأن هذه الصحف مرتاب " وقيل لما ماتت حفصة سلم عبد الله بن عمر هذه الصحف لجمع من الصحابة فغسلت غسلا (*)',NULL,'36',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (40,'مع خزيمة بن ثابت الانصاري (١) " من المؤمنين رجال صدقوا ما عاهداو الله عليه " فالحقناها في سورتها في المصحف - رواه البخاري في كتاب التفسير في باب جمع القرآن ورواه الطبري في تفسيره بلفظ اخر وفي رواية ابى قلابة فلما فرغ عثمان من المصحف كتب إلى اهل الامصار انى صنعت كذا وكذا ومحوت ما عندي فامحوا ما عندكم اه وفي رواية شعيب عند ابن أبى داود (٢) والطبراني وغيرهما وأمرهم ان يحرقوا كل مصحف يخالف المصحف الذى ارسل به اه.
(نقول) اكثر الروايات على الاحراق وبعضها على المحو فيمكن الجمع بينها بأن نقول كان الاحراق فيما كتب على نحو الجلود والعظام وكان المحو فيما كتب على نحو الالواح والحجارة والمحو قد يكون بالغسل وقد يكون بالطمس.
وفي رواية أن حذيفة قال يا أمير المؤمنين أدرك الناس فقال عثمان وما ذاك قال غزوت مرج أرمينيا فحضرها أهل العراق وأهل الشام فإذا أهل الشام يقرؤن بقراءة أبى بن كعب فيأتون
_________
" ١ " ستأتي ترجمة خزيمة في الفصل الثالث في ضبط وتصحيح المصحف العثماني.
(٢) هو ابن داود الظاهرى وهو من جملة اصحاب الحديث (*)',NULL,'37',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (41,'بما لم يسمع أهل العراق فيكفرهم اهل العراق وإذا أهل العراق يقرؤن بقراءة ابن مسعود فيأتون بما لم يسمع أهل الشام فيكفرهم أهل الشام قال زيد فأمرني عثمان إلى آخر القصة (١) وفى رواية اختلفوا في القرآن على عهد عثمان حتى اقتتل الغلمان والمعلمون (٢) فبلغ ذلك عثمان بن عفان فقال عندي تكذبون به وتلحنون فيه فمن نآى عنى من الامصار كان اشد تكذيبا واكثر لحنا يا اصحاب محمد اجتمعوا فاكتبوا للناس إماما.
واخرج ابن ابى داود بسند صحيح عن سويد بن غفلة قال قال على لا تقولوا في عثمان الا خيرا فو الله ما فعل الذى فعل في المصاحف الا عن ملا منا قال ما تقولون في هذه القراءة فقد بلغني ان بعضهم يقول قراءتى خير من قراءتك وهذا يكاد يكون كفرا قلنا فما ترى قال أرى ان يجمع الناس على مصحف واحد فلا تكون فرقة ولا اختلاف
_________
(١) اعلم ان اهل دمشق وحمص اخذوا عن المقداد بن الاسود واهل الكوفة عن ابن مسعود واهل البصرة عن ابى موسى الاشعري وكانوا يسمون مصحفه لباب القلوب وقرأ كثير من اهل الشام بقراءة ابى بن كعب.
انظر في الفصل الخامس من الباب الثاني في حديث انزل القرآن على سبعة احرف (٢) فان قيل - كيف يتصور ذلك مع ان الطالب هو الذى يتلقى القرآن والعلم من معلمه - نقول - يمكن ذلك بان يسمع من اهله وجيرانه قراءة غير قراءة معلمه وتأكيدهم له بصحتها.
(*)',NULL,'38',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (42,'قلنا فنعم ما رأيت (١) وقال على ايضا لو وليت لعملت بالمصاحف التى عمل بها عثمان.
وفي عنوان البيان قال الالوسى في تفسيره وهذا الذى ذكرناه من فعل عثمان هو ما ذكره غير واحد من المحققين حتي صرحوا بان عثمان لم يصنع شيئا فيما جمعه أبو بكر من زيادة أو نقص أو تغيير ترتيب سوى انه جمع الناس على القراءة بلغة واحدة وهى لغة قريش محتجا بأن القرآن نزل بلغتهم اه (٢) وهو ظاهر في ان ترتيب السور كترتيب الآيات كان في عهد ابى بكر رضى الله عنه خلافا لما ذكره الحاكم في مستدركه.
انتهى من عنوان البيان.
_________
(١) الظاهر من هذه الرواية والتى قبلها ان عثمان رضى الله عنه كان في نيته جمع الناس على مصحف واحد وعلى قراءة واحدة حين رآى اختلاف الناس في قراءة القرآن غير أنه لم يعزم علي تنفيذ ما كان يضمره الا بعد ان انذره حذيفة صاحب سر رسول الله صلى الله عليه وسلم من عاقبة هذا الاختلاف * بل ان عمر شعر بهذا في ايام خلافته فكتب إلى ابن مسعود يأمره ان يقرئ الناس القرآن بلغة قريش كما يأنى بعد هذا الكلام (٢) واخرج أبو داود من طريق كعب الانصاري ان عمر كتاب إلى ابن مسعود ان القرآن نزل بلسان قريش فاقرئ الناس بلغة قريش لا بلغة هذيل
قال ابن عبد البر يحتمل ان يكون هذا من عمر على سبيل الاختيار لان الذى قرأ به ابن مسعود لا يجوز قال وإذا ابيحت قراءته على سبعة اوجه انزلت جاز الاختيار فيما أنزل اه من فتح الباري على صحيح البخاري - وابن مسعود كان من هذيل وستأنى ترجمته (*)',NULL,'39',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (43,'قال ابن حجر وكان ذلك (أي جمع عثمان للمصحف) في سنة خمس وعشرين قال وغفل بعض من أدركناه فزعم انه في حدود سنة ثلاثين ولم يذكر مستندا (١) قال ابن التين وغيره الفرق بين جمع ابى بكر وجمع عثمان أن جمع ابى بكر كان لخشية ان يذهب من القرآن شئ بذهاب حملته لانه لم يكن مجموعا في موضع واحد فجمعه في صحائف مرتبا لايات سوره على ما وقفهم عليه النبي صلى الله عليه وسلم وجمع عثمان كان لما كثر الاختلاف في وجوه القراءة حتى قرؤه بلغاتهم على اتساع اللغات فأدى ذلك بعضهم إلى تخطئة بعض فخشى من تفاقم الامر في ذلك فنسخ تلك الصحف في مصحف واحد مرتبا لسوره واقتصر من سائر اللغات على لغة قريش محتجا بأنه نزل بلغتهم وان كان قد وسع في قراءته بلغة غيرهم رفعا للحرج والمشقة في ابتداء الامر فرآى ان الحاجة إلى ذلك قد انتهت فاقتصر على لغة واحدة اه فلو تأملت ما كان يحصل لبعضهم في عهد النبي صلى الله عليه وسلم من الفزع وتغير الحال عند سماعه قراءة لا يعرفها كما سيأتي بيانه عند
_________
(١) إذا تحققنا متى كانت غزوة ارمينيا واذربيجان وقدرنا المدة التى تستغرق كتابة المصحف ظهر لنا ذلك.
وقد غزا العرب أرمينيا مرتين الاولى في عهد عمر بن الخطاب سنة ثمانية عشر هجرية والثانية في عهد عثمان بن عفان سنة ست وعشرين كما ذكره الاستاذ عبد الوهاب النجار في كتابة تاريخ الاسلام قال وجعل
الطبري ذلك سنة احدى وثلاثين (*)',NULL,'40',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (44,'حديث انزل القرآن على سبعة احرف، لم تستغرب حدوث الاختلاف في قراءة القرآن بعد وفاته عليه الصلاة والسلام بنحو خمسة عشر عاما وان جمع عثمان القرآن بحرف واحد وحمل الناس عليه لهو عين الحكمة وعين الصواب، وهو سر قوله تعالى " انا نحن نزلنا الذكر وانا له لحافظون " ولو ترك الناس على ما كانوا عليه ولم تتوحد قراءتهم للقرآن لوقع التحريف والتبديل فيه إلى يوم القيامة..فرضى الله تعالى عن صحابة رسول الله اجمعين (فان قيل) لم امتثل زيد بن ثابت امر عثمان بجمع القرآن ولم يمتثل أمر أبى بكر الا بعد نظر ومراجعة (نقول) كان ذلك مع ابى بكر لان هذا الامر لم يفعله رسول الله صلى الله عليه وسلم ولم يأمر به فخوفا من وقوعهم في محظور توقف هو وابو بكر ايضا عن موافقة عمر ثم بعد روية وتفكر ظهر لهم أن ذلك من المصلحة الدينية وأن تركهم له قد يؤدى إلى ضياع ما أنزله الله على رسوله، فبعد أن جمع زيد المصحف لابي بكر لا مبرر له في عدم موافقته وامتثاله امر عثمان خصوصا وقد رآى اختلاف الناس في قراءة القرآن (وان قيل) لم اسند أبو بكر حمع المصحف لزيد وحده واسنده إليه عثمان واشرك معه رجالا من قريش (نقول) اختص أبو بكر زيدا',NULL,'41',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (45,'وحده لما يعهده فيه من النشاط وقوة الشباب ولانه كان يكتب الوحى لرسول الله صلى الله عليه وسلم فهو ادرى بأوجه القراءات كلها وعثمان انما اشرك
معه نفرا من قريش لانه يريد جمع القرآن على حرف واحد وهو لغة قريش ويد من الانصار، ولانه يريد سرعة انجاز جمعه خوفا من تفاقم امر اختلاف الناس في القراءة ولننقل هنا شيئا مناسبا مما ذكره الامام محمد بن جرير الطبري المولود سنه اربع وعشرين ومائتين في اول تفسيره بعد أن بين وجهة حمل عثمان الناس على مصحفه وهو (فان قال) بعض من ضعفت معرفته وكيف جاز لهم ترك قراءة أقرأهموها رسول الله صلى الله عليه وسلم وامرهم بقراءتها (قيل) ان امرهم بذلك لم يكن أمر ايجاب وفرض وانما كان أمر اباحة ورخصة الخ (ويسأل بعضهم) لم لم تكن الاحرف الستة الموجودة وقد انزلت من عند الله تعالى على نبيه صلى الله عليه وسلم وهو أقرأها اصحابه فان نسخت فرفعت فما الدليل عليه، وان نسيتها الامة وتركتها فذلك تضييع ما قد امروا بحفظه (فأجاب الامام ابن جرير الطبري) على هذه الاسئلة بقوله: لم تنسخ الاحرف الستة فترفع ولا ضيعتها الامة وهى مأمورة بحفظها ولكن الامة أمرت بحفظ القرآن',NULL,'42',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (46,'وخيرت في قراءته وحفظه بأى تلك الاحرف السبعة شاعت وضرب لها مثلا في الفقه وهو إذا حنث موسر في يمين فله ان يختار كفارة من ثلاث كفارات اما بعتق أو اطعام أو كسوة فكذلك الامة امرت بحفظ القرآن وقراءته وخيرت في قراءته بأى الاحرف السبعة شاءت فرأت لعلة من العلل اوجبت عليها الثبات على حرف واحد (١) قراءته بحرف واحد ورفض القراءة بالاحرف الستة الباقية ولم تحظر قراءته
بجميع حروفه على قارئه بما أذن له في قراءته به ثم اورد الطبري انباء ما قد حدث في ايام ابى بكر وعثمان من جمع المصحف اه كلام ابن جرير رحمه الله تعالى ولا يخفى ان جوابه سديد ومعتمد، وقد اطال البحث في هذا الموضوع فراجع تفسيره ان شئت وجاء في فتح الباري على صحيح البخاري قال أبو شامة وقد اختلف السلف في الاحرف السبعة التى نزل بها القرآن هل هي مجموعة في المصحف الذى بأيدى الناس اليوم أو ليس فيه الا حرف واحد منها مال الباقلانى إلى الاول وصرح الطبري وجماعته بالثاني وهو المعتمد اه منه
_________
(١) فان قال قائل ما العلة التي اوجبت على الامة الثبات على حرف واحد من الاحرف السبعة قيل الخوف من ضياع القرآن باختلافهم في قراءته كما في تفسير الطبري (*)',NULL,'43',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (47,'وجاء في فتح الباري ايضا ما نصه: وسبب اختلاف القراءات السبع وغيرها كما قال ابن ابى هشام ان الجهات التى وجهت إليها المصاحف كان بها من الصحابة من حمل عنه اهل تلك الجهة وكانت المصاحف خالية من النقط والشكل قال فثبت اهل كل ناحية على على ما كانوا تلقوه سماعا عن الصحابة بشرط موافقة الخط وتركوا ما يخالف الخط امتثالا لامر عثمان الذى وافقه عليه الصحابة لما رأوا في ذلك من الاحتياط للقرآن فمن ثم نشأ الاختلاف بين قراءة الامصار مع كونهم متمسكين بحرف واحد من السبعة اه من فتح الباري لابن حجر (فخلاصة ما تقدم) أن أبا بكر أول من جمع القرآن باشارة عمر
رضى الله عنهما وكان جمعه بالاحرف السبعة كلها التى نزل بها القرآن وسببه الخوف من ضياعه بقتل القراء في الغزوات - ثم في خلافة عثمان كثر اختلاف الناس في قراءة القرآن فخشى رضى الله عنه عاقبة هذا الامر الخطير وقام بجعع القرآن على حرف واحد من الا حرف السبعة وهو حرف قريش وترك الا حرف الستة الباقية حرصا منه على جمع المسلمين على مصحف واحد وقراءة واحدة وعزم على كل من كان عنده مصحف مخالف لمصحفه الذى جمعه أن يحرقه فأطاعوه واستصوبوا رأيه - فالمصحف العثماني لم يجمع الا بحرف واحد من',NULL,'44',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (48,'الاحرف السبعة وان القراءات المعروفة الآن جميعها في حدود ذلك الحرف الواحد فقط واما الاحرف الستة فقد اندرست بتاتا من الامة كما صرح بهذا الامام ابن جرير الطبري في تفسيره حيث قال " فتركت الامة القراءة بالاحرف الستة التى عزم عليها امامها العادل (يعنى عثمان) في تركها طاعة منها له ونظرا منها لانفسها ولمن بعدها من سائر اهل ملتها حتى درست من الامة معرفتها وتعفت آثارها فلا سبيل لاحد اليوم إلى القراءة بها لدثورها وعفو آثارها وتتابع المسلمون على رفض القراءة بها من غير جحود منها بصحتها وصحة شئ منها ولكن نظرا منها لانفسها ولسائر اهل دينها فلا قراءة اليوم للمسلمين الا بالحرف الواحد الذى اختاره لهم امامهم الشفيق الناصح دون ما عداه من الا حرف الستة الباقية فان قال بعض من ضعفت معرفته وكيف جاز لهم ترك قراءة اقرأهموها رسول الله صلى الله عليه وسلم وأمرهم بقراءتها قيل ان امرهم بذلك لم يكن امر ايجاب وفرض وانما كان اباحة ورخصة..الخ اه
فتنبه لهذا الموضوع المهم ولا تفوتك معرفته فانه مبحث نفيس.
ولقد اتينا بكلام ابن جرير الطبري رحمه الله تعالى لانه من كبار الائمة ولان عصره كان قريبا من عصر الصحابة والتابعين فانه ولد سنة مائتين واربع وعشرين وطاف الاقاليم في طلب العلم وسمع',NULL,'45',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (49,'عن الثقات الاجلة وجمع من العلوم ما لم يشاركه احد في عصره وله تصانيف عديدة حكى انه مكث اربعين سنة فكتب في كل يوم منها اربعين ورقة توفى في شوال عام ثلاثمائة وعشره وصلى على قبره عدة شهور ليلا ونهارا اه باختصار من طبقات الشافعية الكبرى ولنختم هذا الفصل بابيات في موضوع جمع القرآن من نظم الامام الشاطبي رحمه الله تعالى في عقيلة اتراب القصائد وهى: ان اليمامة أغواها مسيلمة الك * - ذاب في زمن الصديق إذ خسرا وبعد بأس شديد حان مصرعه * وكان بأسا على القراء مستعرا نادى أبا بكر الفاروق خفت على الق * - راء فادارك القرآن مستطرا فأجمعوا جمعه في الصحف واعتمدوا * زيد بن ثابت العدل الرضى نظرا فقام فيه بعون الله يجمعه * بالنصح والجد والحزم الذي بهرا من كل أوجهه حتى استتم له * بالاحرف السبعة العليا كما اشتهرا فأمسك الصحف الصديق ثم إلى الف * - اروق أسلمها لما قضى العمرا وعند حفصة كانت بعد فاختلف الق * - راء فاعتزلوا في أحرف زمرا وكان في بعض مغزاهم مشاهدهم * حذيفة فرآى في خلفهم عبرا فجاء عثمان مذعورا فقال له * أخاف أن يخلطوا فادارك البشرا فاستحضر الصحف الاولى التي جمعت * وخص زيدا ومن قريشهم نفرا
على لسان قريش فاكتبوه كما * على الرسول به انزاله انتشرا فجردوه كما يهوى كتابته * ما فيه شكل ولا نقط فيحتجرا',NULL,'46',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (50,'<span data-type="title" id=toc-10>الفصل الثاني (في احتياط الصحابة في كتابة القرآن)</span>
جمع القرآن العظيم لاول مرة في التاريخ وهو مفرق في الالواح والعظام وصدور الرجال ليس بالامر الهين، بل هو عمل خطير يحتاج إلى عناية كبرى ونثبت تام * لذلك ما كانت اللجنة القائمة بجمعه يعتمدون على ما في صدورهم منه وفيهم من يحفظه كله كما انهم ما كانوا يكتفون بمجرد نظر إلى ما هو مكتوب في الرقاع ونحوها بل يأخذونه عمن تلقاه سماعا من رسول الله صلى الله عليه وسلم فذلك أدعى للاطمئنان والاحتياط وأبعد للشك والارتياب.
فقد اخرج ابن أبى داود من طريق يحيى بن عبد الرحمن بن حاطب قال قدم عمر فقال من تلقى من رسول الله صلى الله عليه وسلم شيئا من القرآن فليأت به وكانوا يكتبون ذلك في الصحف والالواح والعسب وكان زيد لا يقبل من احد شيئا حتى يشهد شهيدان.
واخرج ابن أبى داود ايضا من طريق هشام بن عروة عن أبيه أن أبا بكر قال لعمر ولزيد اقعدا على باب المسجد فمن جاءكما بشاهدين على شئ من كتاب الله فاكتباه.
فهاتان الروايتان تدلان صريحا انهم ما كانوا يكتفون بمجرد وجدان شئ من كتاب الله مكتوبا حتى يشهد به من تلقاه سماعا',NULL,'47',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (51,'زيادة في الاحتياط، وهذه الطريقة محكمة جدا وحيث يطمئن إليها كل مسلم ولا تدع مجالا لطعن المنافقين.
قال ابن حجر - وكأن المراد بالشاهدين شاهد الحفظ والكتابة قال السخاوي المراد انهما يشهدان على أن ذلك المكتوب كتب بين يدى رسول الله صلى الله عليه وسلم (١) والمراد انهما يشهدان على أن ذلك من الوجوه التى نزل بها القرآن.
يقول بعض المعاصرين لنا - أن رواية الجلوس على باب المسجد واستعراض مالدى الناس من قرآن هي إلى الوهم اقرب منه إلى الحقيقة - فنقول - ان جمع القرآن بالاحرف السبعة واستقصاؤها لا يكون الا باستعراض ما لدى الناس من قرآن لما عسى ان توجد عند بعضهم آية أو قراءة من الاحرف السبعة تلقاها من النبي صلى الله عليه وسلم لا توجد عند آخر - ثم ان المسجد في ذلك العهد هو خير مكان يليق باستقبال الناس لمثل هذا الامر الجليل، فالحضارة المدنية المستلزمة لانتظام دواوين الحكومات لم تكن تعرف عند العرب وقنئذ بل كانوا في حالة من البداوة وبساطة العيش حتى ان نفس المسجد النبوى كان سقفه
_________
(١) يؤيد هذا المعنى رواية ابن عساكر الآتية قريبا وهى ان عثمان خطب في الناس..الخ (*)',NULL,'48',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (52,'من الجريد وجدرانه من اللبن، فإذا علم ما ذكر زال الاستغراب من هذه الرواية التى هي عين الحقيقة.
واخرج ابن أشتة في المصاحف عن الليث بن سعد قال أول من جمع القرآن أبو بكر وكتبه زيد وكان الناس يأتون زيد بن ثابت فكان لا يكتب آية الا بشاهدي عدل وان آخر سورة براءة لم توجد الا مع خزيمة بن ثابت (١) فقال اكتبوها فان رسول الله صلى الله عليه وسلم جعل شهادته بشهادة رجلين (٢) فكتب وان عمر اتى بآية الرجم
_________
(١) ترجمة خزيمة بن ثابت ستأتي في الفصل الثالث في ضبط وتصحيح المصحف الكريم - لكن ورد في بعض الروايات " مع أبى خزيمة الانصاري " فتأمل وقد تقدم الكلام عليه في جمع ابى بكر للقرآن (٢) سبب جعل شهادة خزيمة بن ثابت بشهادة رجلين هو ان النبي صلى الله عليه وسلم اشترى فرسا من سواد بن الحارث فاستتبعه ليقضيه ثمن الفرس فأسرع النبي صلى الله عليه وسلم المشى وابطأ البائع المذكور فجعل رجال يعترضونه يساومونه في الفرس حتى زادوه على ثمنه وهم لا يعلمون ان النبي صلى الله عليه وسلم اشتراه منه فانكر الاعرابي بيعه للنبي صلى الله عليه وسلم فشهد له خزيمة بن ثابت فقال له رسول الله صلى الله عليه وسلم بم تشهد ولم تكن حاضرا قال بتصديقك وانك لا تقول الا حقا فقال عليه الصلاة والسلام من شهد له خزيمة أو عليه فحسبه وفي رواية فجعل شهادته بشهادة رجلين - هذه خلاصة القصة وهى مشهورة في كتب الاحاديث والسير قال الامام السندي في حاشيته على سنن النسائي والمشهور انه صلى الله عليه وسلم رد الفرس بعد ذلك على الاعرابي فمات من ليلته عنده، رواه النسائي في اواخر كتاب البيوع (*)',NULL,'49',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (53,'فلم يكتبها لانه كان وحده (١) وروى ابن عساكران عثمان خطب في الناس يومئذ وعزم على كل رجل عنده شئ من كتاب الله لما جاء به فكان الرجل يجئ بالورقة والاديم فيه القرآن حتى جمع من ذلك كثرة ثم دعاهم رجلا رجلا
فناشدهم اسمعت رسول الله صلى الله عليه وسلم وهو املاه عليك فيقول نعم فلما فرغ من ذلك عثمان قال من اكتب الناس (٢) قالوا كاتب رسول الله صلى الله عليه وسلم
_________
(١) آية الرجم هي " الشيخ والشيخة إذا زنيا فارجموهما البتة " وقد كانت مكتوبة فنسخت تلاوتها وبقى حكمها معمولا بها - عن ابن عباس حدثنى عبد الرحمن بن عوف ان عمر بن الخطاب خطب الناس فسمعته يقول ألا وان ناسا يقولون ما الرجم في كتاب الله وانما فيه الجلد وقد رجم رسول الله صلى الله عليه وسلم ورجمنا بعده ولولا ان يقول قائل أو يتكلم متكلم ان عمر زاد في كتاب الله ما ليس مثه لاثبتها كما نزلت - رواه الامام احمد والنسائي - وقد ذكر الشوكاني في كتابه نيل الاوطار في اوائل كتاب الحدود شيئا كثيرا عن آية الرجم وحكمه فراجعه (٢) أي في معرفة فواعد الكتابة وحسن الخط - وترجمة زيد تفدمت وكان يكتب السريانية ايضا فقد قال أمرنى رسول الله صلى الله عليه وسلم ان اتعلم السريانية قال انى لا آمن يهود على كتابي فما مر بى نصف شهر حتى تعلمت وحذقت فيه فكنت أكتب له صلى الله عليه وسلم واقرأ له كتبهم، وفي رواية تعلمتها في سبعة عشر يوما - وذكروا انه تعلم العبرانية ايضا في خمسة عشر يوما ولا يخفى ان الانسان يحتاج لبضعة اعوام لتعلم اي لغة قراءة وكتابة وكون زيد يتعلم السريانية في نصف شهر لا شك ان ذلك من معجزاته صلى الله عليه وسلم فانه لما احتاج إلى من يكتب له السريانية وامر زيدا بتعلمها طوى الله له مرحلة التعليم التى تحتاج لبضع سنين إلى نصف شهر (*)',NULL,'50',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (54,'زيد بن ثابت قال فأى الناس أعرب (١) قالوا سعيد بن العاص قال فليمل سعيد وليكتب زيد اه - وفي الرواية السابقة ان عثمان احضر معهما
عبد الله بن الزبير وعبد الرحمن بن الحارث بن هشام وقد تقدمت ترجمتهم فرواية ابن عساكر هذه تقتضي ان عثمان استأنف في جمعه أخذ القرآن من الناس وبعد أن استوثق بصحة ما اتوه به من الآيات القرآنية أمر زيدا ومن معه بكتابته ونسخه، ورواية البخاري المتقدمة في الفصل الاول تدل على ان عثمان انما نسخ مصحفه عن صحف أبى بكر التى اخذها من حفصة وقد علمت ان جمعه وجمع أبى بكر متفقان غير أن جمع عثمان كان بحرف واحد وهو لغة قريش وجمع أبى بكر كان بجميع الاحرف السبعة فعلى رواية ابن عساكر يمكن ان نقول ان عثمان فعل ذلك للوقوف على ما عند الناس من القراءات، أو لانه عزم في نفسه على احراق ما كتبه الناس من القرآن إذا تم نسخ مصحفه - لا انه فعل لشكه في صحة جمع ابى بكر وهو الذى اعتمد في نسخ مصحفه على صحف أبى بكر
_________
(١) أي افصح وقد تقدم في ترجمة سعيد بن العاص انه ممن جمع السخاء والفصاحة (*)',NULL,'51',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (55,'ففى هذه الروايات كلها دلالة واضحة على شدة احتياطهم في جمع القرآن الكريم وتثبتهم في كتابته لذلك اجمعت الصحابة كلهم على هذا العمل المبرور وتلقوه بالقبول التام (١) وكان عددهم حينئذ اثنى عشر ألفا تقريبا (٢) رضى الله عنهم اجمعين
<span data-type="title" id=toc-11>الفصل الثالث (في ضبط وتصحيح المصحف الكريم)</span>
قد يتوهم بعض قاصري العقول ان القرآن ربما سقط منه شئ حين نسخهم وجمعهم له أو حصل فيه تغيير أو تحريف كما زعم ذلك
_________
(١) ذكروا أن ابن مسعود رضى الله عنه لما حضر مصحف عثمان إلى الكوفة لم يوافق على الرجوع عن قراءته ولا على اعدام مصحفه من غير ان ينكر على عثمان عمله وقال أفأترك ما أخذت من في رسول الله صلى الله عليه وسلم بضعا وسبعين سورة..الخ وابن مسعود هذا هو احد الاربعة المذكورين في حديث خذوا القرآن من أربعة من عبد الله بن مسعود وسالم ومعاذ وأبى بن كعب كما في صحيح البخاري.
وترجمة ابن مسعود ستأتي في الفصل الخامس في نزول القرآن على سبعة احرف (٢) الظاهر انهم كانوا يحصون المسلمين فقد اخرج البخاري في كتاب الوصايا في باب كتابة الامام الناس عن حذيفة رضى الله عنه قال قال النبي صلى الله عليه وسلم اكتبوا لى من تلفظ بالاسلام من الناس فكتبنا له ألفا وخمسمائة رجل..الخ (*)',NULL,'52',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (56,'بعض المستشرقين من الافرنج وكما زعمت الشيعه ان الصحابة حرفوا القرآن وأسقطوا كثيرا من اياته وسوره وكتموا ما نزل في امامة على رضى الله عنه واستخلافه (١) فنقول.
ان الله تعالى قد تكفل بحفظ القرآن الكريم وضمن صيانته من عبث العابثين بصريح قوله " انا نحن نزلنا الذكر وانا له لحافظون " وقوله " وانه لكتاب عزيز لا يأتيه الباطل من بين يديه ولا من خلفه " واى دليل اعظم على ذلك من مرور أربعة عشر قرنا والقرآن هو هو ما مسته ايدى الخلائق بالتحريف ولا بالتزوير وهكذا
يكون محفوطا إلى ان يرفعه الله من الصدور والمصاحف فلا تبقى في الارض منه آية ويكون هذا في اخر الزمان قبل يوم القيمة كما جاء في كثير من الاخبار (٢) فالصحابة رضوان الله تعالى عليهم ما كانوا ليتهاونوا في امر المصحف وهم الذين ايد الله بهم الاسلام، فقد ورد عن زيد بن ثابت انه قال كنت
_________
(١) راجع تفسير الالوسى في مقدمة الجزء الاول فانه روى كثيرا من اقوال الشيعة قاتلهم الله تعالى، وراجع ايضا تفسير القرطبى فانه ذكر شيئا مما طعن بعضهم في القرآن بالزيادة والنقصان والرد على قائل ذلك (٢) قال القرطبى ان رفع القرآن على هذه الكيفية الواردة في الاحاديث انما يكون بعد موت عيسى عليه السلام وهدم الحبشة للكعبة اه (*)',NULL,'53',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (57,'اكتب الوحى عند رسول الله صلى الله عليه وسلم وهو يملى على فإذا فرغت قال اقرأه فأقرؤه فان كان فيه سقط أقامه.
وفي بعض الروايات عن زيد بن ثابت ايضا المتخصص في كتابة القرآن أنه قال - فلما فرغت (أي من نسخ مصحف عثمان) عرضته عرضة فلم اجد فيه هذه الاية من المؤمنين رجال صدقوا ما عاهدوا الله عليه فمنهم من قضى نحبه ومنهم من ينتظر وما بدلوا تبديلا (١) قال فاستعرضت المهاجرين أسألهم عنها فلم اجدها عند احد منهم ثم استعرضت الا نصار أسألهم عنها فلم أجدها عند أحد منهم حتى وجدتها عند خزيمة (يعنى ابن ثابت) (٢) فكتبتها ثم عرضته عرضة اخرى فلم اجد فيه هاتين الآيتين لقد جاءكم رسول من انفسكم عزيز عليه
_________
(١) في سورة الاحزاب
(٢) وترجمة خزيمة كما نلخصها من الاصابة هي.
خزيمة بن ثابت بن الفاكة الانصاري الاوسي من السابقين الاولين شهد بدرا وما بعدها وقيل أول مشاهده أحد وكان بكسر أصنام بني خطمة (بفتح المعجمة وسكون المهملة) وكانت راية خطمة بيده يوم الفتح وفيه قال النبي صلى الله عليه وسلم من شهد له خزيمة أو عليه فحسبه وجعل شهادته بشهادة رجلين (وقد تقدم سبب ذلك) قتل خزيمة يوم صفين فانه قال انا لا اقتل ابدا حتى يقتل عمار فلما قتل عمار جرد سيفه فقاتل حتى قتل اه وكانت وقعة صفين سنة سبع وثلاثين (*)',NULL,'54',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (58,'ما عنتم حريص عليكم.
إلى اخر السورة فاستعرضت المهاجرين فلم اجدها عند أحد منهم ثم استعرضت الانصار أسألهم عنها فلم اجدها عند احد منهم حتى وجدتها مع رجل آخر يدعى خزيمة ايضا (١) فأثبتها في آخر براءة ولو تمت ثلاث ايات لجعلتها سورة على حدة ثم عرضته عرضة اخرى فلم أجد فيه شيئا ثم ارسل عثمان إلى حفصة يسألها ان تعطيه الصحيفة وحلف لها ليردنها إليها فأعطته فعرض المصحف عليها فلم يختلف في شئ (٢) فردها إليها وطابت نفسه وأمر الناس أن يكتبوا مصاحف (٣) فأنت ترى في كلام زيد بن ثابت أنه بعد فراغه من كتابة
_________
(١) جاء في بعض الروايات ان آخر سوره التوبة لقد جاءكم رسول من انفسكم الخ وجد مع خزيمة الانصاري وجاء في بعضها انه وجد مع ابى خزيمة الانصاري وقد تقدم الكلام على هذا في جمع ابى بكر للقرآن في الفصل الاول (٢) أي لم يختلف مصحفه مع مصحف ابى بكر في الحرف الذى أخذه منه وهو حرف قريش هذا هو المقصود من كلامه لا أن مصحفه مطابق لمصحف
ابى بكر كلمة كلمة فان مصحف ابى بكر مكتوب بجميع الاحرف السبعه كما سبق بيانه ومصحف عثمان كتب على حرف واحد منها (٣) هذه الرواية تدل على أن عثمان طلب صحف ابي بكر من حفصة بعد ان تم نسخ مصحفه ليستعرضه عليها، والرواية التى سبقت عند جمع عثمان المصحف تدل على انه طلب الصحف منها عند الشروع في جمع مصحفه لينسخه منها فنأمل (*)',NULL,'55',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (59,'المصحف راجعه ثلاث مرات ثم راجعه امير المؤمنين عثمان بنفسه فلما اطمأن قلبه حمل الناس على ان يكتبوا المصاحف على؟ فط هذا المصحف الامام، فهل بعد هذه المراجعات الاربعة واجماع الصحابة كلهم على قبوله يتطرق الشك إلى قلب احد من المسلمين في كلام رب العالمين القائل " انا نحن نزلنا الذكر وانا له لحافظون " ولو جوزنا في نسخ القرآن وكتابته وجمعه السهو والنسيان عليهم أو عدم معرفتهم لاصول الكتابة وقواعد الاملاء لادى ذلك فيه إلى التغيير والتبديل والنقص والزيادة وهذا محال فالقرآن سليم من اللحن والغلط ليس فيه حرف زايد ولا حرف ناقص ولا تبديل في كلمة ولا تحريف في اخرى - وكيف لا يكون كذلك والذين جمعوه هم كبار الصحابة وأشراف العرب الذين عنهم اخذت الفصاحة وفيهم ظهر البيان وقد تلقوه غضا طريا من رسول الله صلى الله عليه وسلم وأما ما ورد أن عثمان رضى الله عنه قال (ان في القرآن لحنا ستقيمه العرب بألسنتها) فغير صحيح ولا يعقل ان عثمان يقول ذلك لا قبل جمعه القرآن ولا بعده - نعم انه قال قبل جمعه لما بلغه اختلاف الناس في القرآن حتى اقتتل الغلمان والمعلمون (عندي تكذبون به
وتلحنون فيه فمن نآى عنى من الامصار كان اشد تكذيبا واكثر لحنا يا اصحاب محمد اجتمعوا فاكتبوا للناس اماما) ولا يخفى الفرق بين القولين',NULL,'56',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (60,'وقد رد القول الاول العلامة الالوسى في اول تفسيره روح المعاني بقوله.
فالحق ان ذلك لا يصح عن عثمان والخبر ضعيف مضطرب منقطع إذ كيف يظن بالصحابة اولا اللحن في الكلام فضلا عن القرآن وهم هم ثم كيف يظن بهم ثانيا اجتماعهم على الخطأ وكتابته ثم كيف يظن بهم ثالثا عدم التنبه والرجوع ثم كيف يظن بعثمان عدم تغييره وكيف يتركه لتقيمه العرب وإذا كان الذين تولوا جمعه لم يقيموه وهم الخيار فكيف يقيمه غيرهم فلعمري ان هذا مما يستحيل عقلا وشرعا وعادة اه منه ومن المشاهد انه لو أمر احد الملوك أو الامراء بنسخ مصحف أو كتاب لا يقدمه الكاتب إليه الا بعد العناية بتصحيحه والتثبت من عدم وجود أي غلط فيه فكيف بهؤلاء الصحابة الذين بذلوا أنفسهم لله لا يتحرون في كتابة وضبط المصحف الكريم الذى هو اساس الدين الاسلامي الحنيف هذا ولقد وصلت عدة مصاحف من جمع عثمان إلى البلدان الاسلامية فلو وجدوا فيها خطأ أو غلطا لما سكت احد من المسلمين عليه ولكنهم أجمعوا على صحتها وقبولها وقد قال عليه الصلاة والسلام " ان امتى لن تجتمع على ضلالة فإذا رأيتم اختلافا فعليكم بالسواد الاعظم " رواه ابن ماجة عن انس بن مالك وهو حديث صحيح',NULL,'57',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (61,'وقال ايضا في حديث العرباض بن سارية " فانه من يعش منكم فسيرى
اختلافا كثيرا فعليكم بسنتى وسنة الخلفاء الراشدين من بعدى عضوا عليها بالنواجذ " رواه أبو داود والترمذي ولهذا كان اجماعهم حجة.
على انك لن تجد من المسلمين عناية بشئ كعنايتهم بكتاب الله تعالى الذى لا يأتيه الباطل من بين يديه ولا من خلفه - سواء في نسخه أو تصحيحه أو حفظه أو حرمته وهذا لا يحتاج إلى دليل.
وانظركم من المصاحف التى لا تعد ولا تحصى قد كتبت منذ بدء الاسلام إلى يومنا هذا " أي اربعة عشر قرنا " فهل رأيت فيه تبديلا أو تغييرا مع كثرة اعداء الدين من مختلف الاجناس والعقول (ولنختم هذا الفصل) بما رواه البيهقى عن يحى بن اكثم قال دخل يهودى على المأمون فأحسن الكلام فدعاه إلى الاسلام فابى ثم بعد سنة جاء مسلما فتكلم في الفقه فأحسن الكلام فسأله المأمون ما سبب اسلامه قال انصرفت من عندك فامتحنت هذه الاديان فعمدت إلى التوراة فكتبت ثلاث نسخ فزدت فيها ونقصت وأدخلتها البيعة (١) فاشتريت منى وعمدت إلى الانجيل فكتبت ثلاث نسخ فزدت فيها ونقصت وأدخلتها البيعة فاشتريت مني وعمدت إلى القرآن فكتبت
_________
(١) قال في المنجد البيعة بكسر الباء المعبد للنصارى واليهود (*)',NULL,'58',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (62,'ثلاث نسخ فزدت فيها ونقصت وأدخلتها إلى الوراقين (١) فتصفحوها فوجدوا فيها الزيادة والنقصان فرموا بها فلم يشتروها فعلمت ان هذا الكتاب محفوظ فكان هذا سبب اسلامي - ذكره الزرقاني على المواهب في الجزء الخامس (حفظة القرآن) (في عهد النبي صلى الله عليه وسلم)
حفظ كثير من الصحابة القرآن كله على عهد رسول الله صلى الله عليه وسلم فممن حفظه من المهاجرين أبو بكر، وعمر، وعثمان، وعلى، وطلحة، وسعد وابن مسعود، وحذيفة (٢) وسالم مولى ابى حذيفة (٣) وابو هريرة وابن عمر، وابن عباس، وعمرو بن العاص، وابنه عبد الله، ومعاوية
_________
(١) هم الذين يبيعون الكتب والورق (٢) تقدمت ترجمته عند جمع عثمان القرآن في صحيفة ٣٣ (٣) هو سالم مولى ابى حذيفة بن عتبة احد السابقين الاولين، روى ان عائشة احتبست على النبي صلى الله عليه وسلم فقال ما حبسك قالت سمعت قارئا يقرأ فذكرت من حسن قراءته فأخذ رداءه وخرج فإذا هو سالم مولي ابى حذيفة فقال الحمد لله الذى جعل في امتى مثلك، وروى البخاري من حديث ابن عمر كان سالم مولى ابى حذيفة يؤم المهاجرين الاولين في مسجد قباء وفيهم أبو بكر وعمر اه ملخصا من الاصابة (*)',NULL,'59',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (63,'وابن الزبير، وعبد الله بن السائب، وعائشة، وحفصة، وام سلمة (١) وأم ورقة (٢) وممن حفظه من الانصار * زيد بن ثابت، ومعاذ بن جبل، وأبى ابن كعب، وابو الدرداء، ومجمع بن حارثه، وانس بن مالك، وابو زيد الانصاري أحد عمومة أنس بن مالك (٣) رضى الله تعالى عنهم اجمعين (ومما يناسب المقام) ما يروى.
أن خزرجا كانت تفاخر أوسا بأربعة ممن حفظوا القرآن كله على عهد النبي صلى الله عليه وسلم، وأن أوسا كانت تفاخر خزرجا
_________
(١) عائشة وحفصة وام سلمة هن امهات المؤمنين ازواج النبي صلى الله عليه وسلم وام سلمة اسمها هند على الاصح وهى آخر أمهات المؤمنين موتا ودفنت
بالبقيع بالمدينة رضي الله عن امهات المؤمنين اجمعين (٢) ام ورقة هي بنت عبد الله بن الحارث كانت قد جمعت القرآن وكان رسول الله صلى الله عليه وسلم يزورها ويسميها الشهيدة وقد كان أمرها أن تؤم اهل دارها وكان لها مؤذن فغمها غلام لها وجارية كانت قد دبرتهما فقتلاها في امارة عمر فقال عمر صدق رسول الله صلى الله عليه وسلم كان يقول انطلقوا بنا نزور الشهيدة اه ملخصا من الاصابة (٣) قال في الاصابة في تمييز الصحابة.
أبو زيد الذى جمع القرآن وقع في حديث أنس في صحيح البخاري غير مسمى وقال أنس هو احد عمومتي واختلفوا في اسمه فقيل أوس وقيل ثابت بن زيد وقيل معاذ وقيل سعد بن عبيد وقيل قيس بن السكن وهذا هو الراجح كما بينته في حرف القاف اه منه (*)',NULL,'60',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (64,'بأربعة ممن لهم مناقب اخرى وإلى مفاخرتهما اشار صاحب نظم عمود النسب رحمه الله تعالى بقوله.
فاخرت الخزرج أوسا بنفر * مع النبي حفظوا كل السور زيد بن ثابت معاذ بن جبل * ثم أبى وابو زيد البطل والاوس خزرجا بذى الشهادة * كانت شهادتين في الافاده والمراد بذى الشهادتين خزيمة بن ثابت وبما ان المقصود ذكر حفاظ القرآن لم نأت ببقية المفاخرة * وإذا تأملت حالة العرب اول ظهور الاسلام وعدم انتشار الكتابة بينهم علمت ان عدد الذين ذكرناهم ممن يحفظ القرآن كله ليس بقليل - ولا شك ان جميع الصحابة رضى الله عنهم يحفظون منه بعض السور والآيات كل منهم بحسب فراغه واستعداده وذلك لصلواتهم وعباداتهم.

<span data-type="title" id=toc-12>الفصل الرابع (في ترتيب آيات القرآن وسوره)</span>
جاء في كتاب الاتقان للسيوطي ان الاجماع والنصوص المترادفة على أن ترتيب الآيات توقيفي لا شبهة في ذلك (اما الاجماع) فنقله غير واحد منهم الزركشي في البرهان وابو جعفر بن الزبير في مناسباته وعبارته ترتيب الآيات في سورها واقع بتوقيفه صلى الله عليه وسلم وأمره',NULL,'61',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (65,'من غير خلاف في هذا بين المسلمين (ومنها النصوص) فمنها حديث زيد السابق كنا عند النبي صلى الله عليه وسلم نؤلف القرآن من الرقاع، ومنها ما اخرجه احمد بأسناد حسن عن عثمان بن أبى العاص قال كنت جالسا عند رسول الله صلى الله عليه وسلم إذ شخص (١) ببصره ثم صوبه ثم قال أتانى جبريل فأمرني أن أضع هذه الاية هذا الموضع من هذه السورة (ان الله يأمر بالعدل والاحسان وإيتاء ذى القربى إلى اخرها) ومنها ما اخرجه احمد وابو داود والترمذي والنسائي وابن حبان والحاكم عن ابن عباس قال قلت لعثمان ما حملكم على أن عمدتم إلى الانفال وهى من المثانى وإلى براءة وهى من المئين فقرنتم بينهما ولم تكتبوا بينهما سطر بسم الله الرحمن الرحيم ووضعتموها في السبع الطوال (٢) فقال عثمان كان رسول الله صلى الله عليه وسلم
_________
(١) قال في المصباح شخص بصره من باب خضع إذا فتح عينيه وجعل لا يطرف اه منه (٢) قال في الاتقان.
السبع الطوال بكسر الطاء وضمها أولها البقرة وآخرها براءة " هذا بجعل الانفال وبراءة سورة واحدة " وقيل السابعة يونس وقيل الكهف، والمئون ما وليها سميت بذلك لان كل سورة منها تزيد على مائة
آية أو تقاربها، والمثاني ما ولى المئين لانها ثنتها أي كانت بعدها فهى لها ثوان والمئون لها أوائل، وقال الفراء هي السورة التي آيها اقل من مائة آية وقد تطلق على الفاتحة وعلى القرآن كله ايضا والمفصل ما ولى المثانى من قصار السور سمى بذلك لكثرة الفصول التى بين السور بالبسملة ويسمى المفصل بالمحكم ايضا وآخره سورة الناس بلا نزاع - واختلف في أوله على اثنى عشر قولا أحدها ق) * i',NULL,'62',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (66,'تنزل عليه السورة ذات العدد فكان إذا نزل عليه الشئ دعا بعض من كان يكتب فيقول ضعوا هذه الآيات في السورة التى يذكر فيها كذا وكذا وكانت الانفال من اوائل ما نزل بالمدينة وكانت براءة من اخر القرآن نزولا وكانت قصتها شبيهة بقصتها فظننت انها منها فقبض رسول الله صلى الله عليه وسلم ولم يبين لنا انها منها فمن اجل ذلك قرنت بينهما ولم اكتب بينهما سطر بسم الله الرحمن الرحيم ووضعتها في السبع الطوال واخرج القشيرى الصحيح ان التسمية لم تكن فيها (أي في براءة) لان جبريل عليه السلام لم ينزل فيها
_________
والثانى الحجرات وصححه النووي..الخ انظر الاتقان - وللمفصل طوال وأوساط وقصار (قيل) طواله إلى عم واوساطه منها إلى والضحى ومنها إلى آخر القرآن قصاره.
وقد ذكر صاحب الاتقان جملة اقوال فراجعه وجاء في كتاب الفقه على المذاهب الاربعة في الجزء الاول من قسم العبادات ما ملخصه: الشافعية قالوا ان طوال المفصل من الحجرات إلى سورة عم يتساءلون واواسطه من سورة عم إلى سورة والضحى وقصاره منها إلى آخر القرآن * والحنفية قالوا ان طوال المفصل من الحجرات إلى سورة البروج واواسطه من سورة البروج إلى سورة لم يكن وقصاره من سورة لم يكن إلى سورة الناس *
والمالكية قالوا ان طوال المفصل من سورة الحجرات إلى آخر والنازعات واواسطه من بعد ذلك إلى والضحى وقصاره منها إلى آخر القرآن * والحنابلة قالوا ان طوال المفصل من سورة ق إلى عم واواسطه إلى سورة والضحى وقصاره إلى آخر القرآن انتهى من كتاب الفقه المذكور (*)',NULL,'63',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (67,'وقال البغوي في شرح السنة: الصحابة رضى الله عنهم جمعوا بين الدفتين القرآن الذى انزله الله على رسوله من غير أن زادوا أو نقصوا منه شيئا خوف ذهاب بعضه بذهاب حفظته فكتبوه كما سمعوا من رسول الله صلى الله عليه وسلم من غير أن قدموا شيئا أو أخروا أو وضعوا له ترتيبا لم يأخذوه من رسول الله صلى الله عليه وسلم وكان رسول الله صلى الله عليه وسلم يلقن اصحابه ويعلمهم ما نزل عليه من القرآن على الترتيب الذى هو عليه الآن في مصاحفنا بتوقيف جبريل اياه على ذلك واعلامه عند نزول كل آية ان هذه الآية تكتب عقب آية كذا في سورة كذا فثبت ان سعى الصحابة كان في جمعه في موضع واحد لا في ترتيبه فان القرآن مكتوب في اللوح المحفوظ على هذا الترتيب الذى انزله الله جملة إلى السماء الدنيا ثم كان ينزله مفرقا عند الحاجة وترتيب النزول غير ترتيب التلاوة - اه من الاتقان (واما ترتيب السور) ففى كونه اجتهاديا أو توقيفيا خلاف والجمهور على الاول قال أبو بكر الانباري انزل الله تعالى القرآن كله إلى سماء الدنيا ثم فرقه في بضع وعشرين فكانت السورة تنزل لامر يحدث والآية جوابا لمستخبر فيوقف جبريل النبي صلى الله عليه وسلم على موضع الآية والسورة فمن قدم أو أخر فقد افسد نظم القرآن *',NULL,'64',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (68,'وقال ايضا اتساق السور كاتساق الآيات والحروف كله عن النبي صلى الله عليه وسلم فمن قدم سورة أو اخرها فقد افسد نظم القرآن.
وفي ايقاظ الاعلام قال أبو جعفر النحاس والمختار كون ترتيب السور توقيفا كالآيات وقال الزركشي والخلاف بين الفريقين في ترتيب السور لفظي لان القائل بعدم صدوره من النبي صلى الله عليه وسلم يقول انه رمز لهم بذلك والثانى يقول انه صرح لهم به ولذلك قال مالك انما ألفوا القرآن على ما كانوا يسمعونه من النبي صلى الله عليه وسلم اه وذكر الامام النووي في شرحه على صحيح مسلم في باب صلاة النبي صلى الله عليه وسلم ودعائه في الليل عند حديث حذيفة قال صليت مع النبي صلى الله عليه وسلم ذات ليلة فافتتح البقرة فقلت يركع عند المائة ثم مضى فقلت يصلى بها في ركعة فمضى فقلت يركع ذبها ثم افتتح النساء فقرأها ثم افتتح ال عمران فقرأها يقرأ مترسلا ... الخ الحديث - مانصه - قال القاضى عياض فيه دليل لمن يقول ان ترتيب السور اجتهاد من المسلمين حين كتبوا المصحف وانه لم يكن ذلك من ترتيب النبي صلى الله عليه وسلم بل وكله إلى امته بعده قال وهذا قول مالك وجمهور العلماء واختاره القاضى أبو بكر الباقلانى قال ابن الباقلانى هو اصح القولين مع احتمالهما قال والذى نقوله ان ترتيب السور ليس بواجب في الكتابة ولا في الصلاة ولا في',NULL,'65',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (69,'الدرس ولا في التلقين والتعليم وانه لم يكن من النبي صلى الله عليه وسلم في ذلك نص ولا حد تحرم مخالفته ولذلك اختلف ترتيب المصاحف قبل مصحف عثمان قال واستجاز النبي صلى الله عليه وسلم والامة بعده في جميع الاعصار ترك
ترتيب السور في الصلاة والدرس والتلقين - قال واما على قول من يقول من اهل العلم ان ذلك بتوقيف من النبي صلى الله عليه وسلم حدده لهم كما استقر في مصحف عثمان انما اختلف المصاحف قبل ان يبلغهم التوقيف والعرض الاخير فيتأول قراءته صلى الله عليه وسلم النساء أولا ثم آل عمران هنا على انه كان قبل التوقيف والترتيب وكانت هاتان السورتان هكذا في مصحف أبى قال ولا خلاف انه يجوز للمصلى ان يقرأ في الركعة الثانية سورة قبل التى قرأها في الاولى وانما يكره ذلك في ركعة ولمن يتلو في غير صلاة قال وقد اباحه بعضهم وتأول نهى السلف عن قراءة القرآن منكوسا على من يقرأ من اخر السورة إلى اولها قال ولا خلاف ان ترتيب آيات كل سورة بتوقيف من الله تعالى على ما هي عليه الآن في المصحف وهكذا نقلته الامة عن نبيها صلى الله عليه وسلم هذا آخر كلام القاضي عياض والله تعالي اعلم انتهى ما ذكره النووي.
قال السيوطي في الاتقان والذى ينشرح له الصدر ما ذهب إليه البيهقى وهو أن جميع السور ترتيبها توقيفي الا براءة والانفال ولا ينبغى ان يستدل بقراءته صلى الله عليه وسلم',NULL,'66',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (70,'سورا ولاء على ان ترتيبها كذلك وحينئذ فلا يرد حديث قراءته النساء قبل آل عمران لان ترتيب السور في القراءة ليس بواجب ولعله فعل ذلك لبيان الجواز اه وقال الكرماني: ترتيب السور هكذا هو عند الله تعالى في اللوح المحفوظ وعليه كان رسول الله صلى الله عليه وسلم يعرض على جبريل كل سنة ما كان يجتمع عنده منه وعرض عليه في السنة التى توفى فيها مرتين، وقال ابن الحصار ترتيب السور ووضع الآيات مواضعها انما كان بالوحى كان
رسول الله صلى الله عليه وسلم يقول ضعوا آية كذا في موضع كذا وقد حصل اليقين من النقل المتواتر بهذا الترتيب من تلاوة رسول الله صلى الله عليه وسلم ومما اجمع الصحابة على وضعه هكذا في المصحف اه وقال البيهقى في المدخل كان القرآن على عهد النبي صلى الله عليه وسلم مرتبا سوره وآياته على هذا الترتيب الا الانفال وبراءة لحديث عثمان السابق اه وقد ذكر السيوطي رحمه الله تعالى في كتابه الاتقان روايات عديدة فراجعه ان شئت وإلى ما سبق أشار الشيخ محمد العاقب الشنقيطى رحمه الله في نظمه كشف العمى بقوله: قد أنزل القرآن دون ثنيا (١) * ليلته إلى سماء الدنيا ثم على قلب النبي هجما * به الامين أنجما منجما
_________
(١) الثنيا بضم الثاء مع الياء والثنوى بالفتح مع الواو اسم من الاستثناء قاله في المصباح أي انزل القرآن إلى السماء الدنيا جملة واحدة دون استثناء شئ منه (*)',NULL,'67',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (71,'وليس ترتيب النزول كالادا * وفي الاد الترتيب بالوحى اقتدى (١) فهو كما هو عليه مستطر * في لوحه المحفوظ نعم المستطر وذاك في السور في القول الاحق * والحق في الآى عليه متفق ويحرم التنكيس فيه والخبر * جاء بتنكيس قراءة السور (٢) (واما اسماء السور) فبتوقيف من النبي صلى الله عليه وسلم كما ثبت ذلك من الآحاديث والآثار فمن ذلك ما اخرجه احمد باسناد حسن عن عثمان بن ابى العاص قال كنت جالسا عند رسول الله صلى الله عليه وسلم إذ شخص ببصره ثم صوبه ثم قال أتانى جبريل فأمرني أن أضع هذه الآية هذا الموضع من هذه السورة إن الله يأمر بالعدل والاحسان وإيتاء ذى القربى
_________
(١) أي ليس ترتيب النزول كترتيب التلاوة فان اول ما نزل اقرأ باسم ربك
الذى خلق واول القرآن الفاتحة (٢) أي يحرم التنكيس في الآيات مطلقا خطا وقراءة، واما في السور فيحرم تنكيسها في الخط عن حالتها في المصحف، اما في قراءتها فقد ورد في الحديث ان النبي صلى الله عليه وسلم فعله اه من كتاب ايقاظ الاعلام لوجوب اتباع رسم المصحف الامام للشيخ محمد حبيب الله الشنقيطى رحمه الله قال في فتح الباري واما ما جاء عن السلف من النهى عن قراءة القرآن منكوسا فالمراد به أن يقرأ من آخر السورة إلى أولها اه (*)',NULL,'68',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (72,'إلى آخرها (ومنه) ما أخرجه مسلم من حديث أبى هريرة ان البيت الذى تقرأ فيه البقرة لا يدخله شيطان (ومنه) ما اخرجه مسلم ايضا عن ابى الدرداء مرفوعا من حفظ عشر آيات من أول سورة الكهف عصم من الدجال وفي لفظ من قرا العشر الاواخر من سورة الكهف * ومن تتبع ما ورد في خصائص بعض السور ظهر له ذلك واضحا جليا فلا داعى لاطالة البحث.
فعلم من جميع ما تقدم ان ترتيب آيات القرآن توقيفي باتفاق العلماء، وكذلك تسمية السور باسماء خاصة، وان ترتيب سوره مختلف فيه فقال بعضهم انه توقيفي وقال بعضهم انه من اجتهاد الصحابة رضى الله تعالى عنهم.
ولقد أنعمنا النظر في ترتيب السور فلم يظهر لنا ترجيح أحد القولين على الآخر فلكل منهما وجهة ولا يسعنا الا أن نفوضه إلى علام الغيوب، ولا بأس أن نذكر هنا ما يؤيد كلا القولين فنقول (الدليل على انه توقيفي) أن الصحابة رضى الله تعالى عنهم هم
أشد الناس اقتداء برسول الله صلى الله عليه وسلم وأبعدهم عن الابتداع والعمل بالظن والهوى، ومما لا شك فيه انه حين جمعهم للقرآن الكريم تحروا فيه كل شئ فما قدموا سورة على اخرى الا باستناد إلى أمره صلى الله عليه وسلم أو فعله',NULL,'69',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (73,'أو تقريره، ولا يخفى أن النبي صلى الله عليه وسلم عرض القرآن على جبريل مرتين (١) في السنة التى توفى فيها، ولا ريب أن القرآن حينئذ كان قد انزل كله على رسول الله صلى الله عليه وسلم فعرضه على جبريل هذه المرة كان من أوله الي آخره، وبالضرورة يكون ترتيبه على ما هو في اللوح المحفوظ الموافق على ما هو عليه الآن بهذه الصفة إذ لا يعرضه صلى الله عليه وسلم العرض الاخير على جبريل الا مرتب الايات والسور، وان زيد بن ثابت كان حاضرا هذه العرضة الاخيرة وهو كاتب الوحى فعلى هذه العرضة كتب مصحف ابى بكر ومصحف عثمان.
ثم لا يعقل أن يضعوا سور القرآن كيفما اتفق لهم، فلو كان ترتيبها باجتهادهم لرتبوها اما بحسب تاريخ نزولها أو مواقعها، واما بحسب طولها وقصرها، واما بحسب ترتيب مصحف احد كبار الصحابة
_________
(١) قال في فتح الباري شرح صحيح البخاري واختلف في العرضة الاخيرة هل كانت بجميع الاحرف المأذون في قراءتها أو بحرف واحد منها وعلى الثاني فهل هو الحرف الذى جمع عليه عثمان جميع الناس أو غيره وقد روى احمد وابن ابى داود والطبري من طريق عبيدة بن عمر السلمانى ان الذي جمع عليه عثمان الناس يوافق العرضة الاخيرة اه من الفتح وتؤخذ من هذه العرضة جملة امور - منها - اكمال نزول القرآن - ومنها ترتيب الآيات والسور - ومنها الاشارة إلى قرب أجله صلى الله عليه وسلم فقد
روى البخاري انه اسر إلى ابنته فاطمة أن جبريل يعارضني بالقرآن كل سنة وانه عارضنى العام مرتين ولا أراه الا حضر اجلى.
(*)',NULL,'70',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (74,'كعلى بن ابى طالب وابن عباس وابن مسعود وأبى بن كعب - وكل ذلك لم يكن فما هناك سوى التوقيف (والدليل على انه اجتهادى) ما جاء في صحيح مسلم عن حذيفة قال صليت مع النبي صلى الله عليه وسلم ذات ليلة فافتتح البقرة فقلت يركع عند المائة ثم مضى فقلت يصلى بها في ركعة فمضى فقلت يركع بها ثم افتتح النساء فقرأها ثم افتتح آل عمران فقرأها يقرأ مترسلا..الخ الحديث، فكونه صلى الله عليه وسلم قرأ النساء اولا ثم آل عمران فيه دليل على ان ترتيب سور المصحف من اجتهاد الصحابة كما تقدم ذلك من قول القاضى عياض وان ترتيبها في الصلاة ليس بواجب وايضا ما جاء في صحيح البخاري عن يوسف بن ماهك قال انى عند عائشة ام المؤمنين رضى الله عنها إذ جاءها عراقى فقال أي الكفن خير قالت ريحك وما يضرك قال يا ام المؤمنين ارينى مصحفك قالت لم قال لعلى أولف القرآن عليه فانه يقرأ غير مؤلف قالت وما يضرك أيه قرأت قبل انما نزل اول ما نزل منه سورة من المفصل..الخ الحديث، ففى قول عائشة للعراقي وما يضرك أيه قرأت قبل دليل على أن ترتيب السور في التلاوة ليس بواجب، وهو كذلك في جميع المذاهب فانه يجوز ترك ترتيبها في الصلاة والتلاوة والدرس، لان كل سورة',NULL,'71',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (75,'مستقلة بذاتها مستوفية لآياتها - ويفهم من هذا الحديث أن الناس
كانوا يقرؤن القرآن ويكتبونه من غير ترتيب لسوره حتى جمع عثمان مصحفه وحمل الناس عليه.
فلو كان ترتيب المصحف توقيفيا لم يختلف ترتيب السور في مصاحف كبار الصحابة كعلى بن ابى طالب وأبى بن كعب، وعبد الله بن عباس، وعبد الله بن مسعود، ومعاذ بن جبل، وعائشة ام المؤمنين وزيد ابن ثابت فكل واحد من هؤلاء كتب مصحفه على عهد رسول الله صلى الله عليه وسلم فمصحف على كان أوله اقرأ ثم المدثر ثم ن وهكذا إلى آخر المكى والمدنى ومصحف ابن مسعود كان أوله البقرة ثم النساء ثم آل عمران على اختلاف شديد، وقد ذكر ابن النديم في كتابه الفهرست (١) ترتيب سور مصاحف بعض الصحابة كما ذكره ايضا السيوطي في كتابه الاتقان فراجعهما ان شئت.
فلو كان هناك أمر صريح أو اشارة خفية من النبي صلى الله عليه وسلم في ترتيب سور المصحف لما عزب ذلك على هؤلاء وهم من اجلاء الصحابة واكثرهم اتصالا به عليه الصلاة والسلام
_________
(١) الف ابن النديم كتابه الفهرست عام ٣٧٧ هجرية وهو يعد من اقدم الكتب واهمها وقد ظهر الآن في عالم المطبوعات (*)',NULL,'72',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (76,'(وختام المقام) أن ترتيب سور المصحف سواء كان توقيفيا أو اجتهاديا فانه يجب علينا اتباع المصحف العثماني في ترتيب سوره ورسم كلماته، لاننا مأمورون باتباع الصحابة آثمون بمخالفتهم قال عليه الصلاوة والسلام كما في حديث العرباض بن سارية "..فعليكم بسنتى وسنة الخلفاء الراشدين المهديين عضوا عليها بالنواجذ (١) " ولهذا كان
_________
(١) والحديث المروي عن عرباض بن سارية رضي الله عنه هو " قال وعظنا رسول الله صلى الله عليه وسلم موعظة وجلت منها القلوب وذرفت منها العيون قلنا يارسول الله كأنها موعظة مودع فأوصنا قال اوصيكم بتقوى الله والسمع والطاعة وان تأمر عليكم عبد فانه من يعش منكم فسيرى اختلافا كثيرا فعليكم بسنتي وسنة الخلفاء الراشدين المهديين عضوا عليها بالنواجذ واياكم ومحدثات الامور فان كل بدعة ضلالة رواه أبو داود والترمذي - وفي رواية احمد وابن ماجه عن عرباض ايضا " قد تركتكم على البيضاء ليلها كنهارها لا يزيغ عنها بعدي الا هالك ومن يعش منكم فسيرى اختلافا كثيرا فعليكم..الخ الحديث والعرباض بن سارية رضى الله عنه هو بكسر العين وسكون الراء كان من اهل الصفة وهو ممن نزل فيه قوله تعالى " ولا على الذين إذا ما أتوك لتحملهم " قال محمد بن عوف كان قديم الاسلام جدا، نزل الشام ثم سكن حمص ومات في فتنة ابن الزبير سنة خمس وسبعين في خلافة عبد الملك بن مروان " فقوله صلى الله عليه وسلم ومن يعش منكم فسيرى اختلافا كثيرا " هذا من ضمن معجزاته عليه الصلاة والسلام التي لا تحصى ملقد وقع ويقع كثير من الامور والفتن التى اخبر بها فكم من المغيبات ذكرا * فبعضها مضى وبعض سيرى ومعجزات المصطفى ليست تعد * وفي الشفا منها كثير قد ورد (*)',NULL,'73',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (77,'اجماعهم حجة، وقد أجمعوا على اعتماد مصحف عثمان ونسخوا مصاحفهم على نمطه كما سبق بيانه - فلا يوجد مسلم على وجه الارض يرى مخالفته ولله الحمد وهذا مصداق قوله تعالى " انا نحن نزلنا الذكر وانا له لحافظون " عدد المصاحف
(التى فرقها عثمان رضى الله عنه في الامصار) تقدم أن عثمان بن عفان لما فرغ من جمع مصحفه أرسل إلى كل افق بمصحف مما نسخوا وأمرهم أن يحرقوا كل مصحف يخالف المصحف الذى أرسل به - وقد أختلفوا في عدة المصاحف التى فرقها في الامصار فقيل انه أربعة وهو الذى اتفق عليه اكثر العلماء، وقيل انها خمسة وقيل انها ستة وقيل سبعة وقيل ثمانية أما كونها اربعة فقيل انه ابقى مصحفا بالمدينة وارسل مصحفا إلى الشام ومصحفا إلى الكوفة ومصحفا إلى البصرة، وأما كونها خمسة فالاربعة المتقدم ذكرها والخامس ارسله إلى مكة، وأما كونها ستة فالخمسة المتقدم ذكرها والسادس اختلف فيه فقيل جعله خاصا لنفسه وقيل ارسله إلى البحرين، وأما كونها سبعة فالستة المتقدم ذكرها والسابع ارسله اليمن، واما كونها ثمانية فالسبعة المتقدم ذكرها والثامن',NULL,'74',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (78,'كان لعثمان يقرأ فيه وهو الذى قتل وهو بين يديه. اه من نهاية القول المفيد.
وبعث رضى الله عنه مع كل مصحف من يرشد الناس إلى قراءته بما يحتمله رسمه من القراءات مما صح وتواتر (١) فكان عبد الله بن السائب مع المصحف المكى، والمغيرة بن شهاب مع المصحف الشامي وابو عبد الرحمن السلمى مع المصحف الكوفى، وعامر بن قيس مع المصحف البصري - وأمر زيد بن ثابت أن يقرئ الناس بالمدني.
ولا ندرى لم لم يرسل عثمان رضى الله عنه لكل بلدة من البلاد الاسلامية مصحفا أو بضعة مصاحف، والظاهر والله تعالى اعلم ان ذلك
كان لقلة النساخ في عهدهم ولعدم وجود الورق عندهم فقد كانوا يكتبونها على الجلود والعسب واللخاف والاكتاف ونحوها فربما يلزم لكتابة مصحف واحد قنطار من هذه الاشياء ولقد وصف الزنجانى مصحف على رضى الله عنه بأنه كان في سبعة اجزاء وقد أتى به يحمله على جمل وهو يقول هذا القرآن جمعته، وروى أن الصاحب بن عباد المتوفى سنة ٣٨٥ هجرية كان يحمل معه في اسفاره
_________
(١) وهذا اختلاف قراءات في لغة واحدة لا اختلاف لغات، انظر في الفصل الثاني من الباب الثالث لتقف على سبب اختلاف رسوم هذه المصاحف.
(*)',NULL,'75',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (79,'كتاب الاغانى على اربعين جملا، وذكروا أن الامام الشافعي رحمه الله تعالى كان كثيرا ما يكتب المسائل على العظام حتى ملاء منها خبايا (١) كل ذلك كان لعدم انتشار الورق عندهم في ذلك الزمن (٢) ، ولا ندرى كيف كانوا يعثرون على مسألة من المسائل وهى مكتوبة على نحو العظام واللخاف والاكتاف التى يعسر ترتيبها لا شك ان مراجعتها والوقوف عليها ليس بسهل ومع ذلك كانوا أئمة الدين وانجم الهدى والذى نراه ان المصاحف العثمانية التى ارسلت إلى الامصار كتبت على الجلود وكتبت بالخط الكوفى الذى ما كانوا يعرفون من الخط سواه وكتبت بغير نقط ولا شكل ولم يكن فيها علامات للاجزاء والاحزاب ونحوها
_________
(١) نستنتج مما ذكر: أن المصاحف التى رفعت على رؤس الرماح في الحرب بين على ومعاوية رضى الله عنهما سنة ٣٧ البالغ عددها نحو ثلاثمائة مصحف طلبا للهدنة وحقنا للدماء، لم تكن بمصاحف كاملة وانما هي اجزاء من القرآن مكتوبة
على نحو العسب والالواح والاكتاف وبذلك يمكن للرجل رفع ما كتب من القرآن على شئ مما ذكر، فاطلاق المؤرخين رفع المصاحف في هذه الحرب انما هو من اطلاق الكل وارادة الجزء والله اعلم (٢) إذا أردت الوقوف على ظهور الورق فعليك بمراجعة كتابنا " تاريخ الخط العربي وآدابه " وهو مطبوع بمصر (*)',NULL,'76',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (80,'<span data-type="title" id=toc-13>الفصل الخامس (في نزول القرآن على سبعة أحرف)</span>
روى البخاري في كتاب التفسير عن ابن عباس رضى الله عنهما أن رسول الله صلى الله عليه وسلم قال أقرأنى جبريل على حرف فراجعته فلم أزل أستزيدة ويزيدني حتى انتهى إلى سبعة أحرف.
وأخرج أبو يعلى في مسنده ان عثمان قال على المنبر اذكر الله رجلا سمع النبي صلى الله عليه وسلم قال ان القرآن انزل على سبعة احرف كلها شاف كاف لما قام فقاموا حتى لم يحصوا فشهدوا بذلك فقال وانا اشهد معهم رواه جمع من الصحابة يبلغ عددهم واحدا وعشرين صحابيا وقد نص ابو عبيدة على تواتره.
وروي مسلم والبخاري واللفظ له عن عمر بن الخطاب رضى الله عنه قال سمعت هشام بن حكيم (١) يقرأ سورة الفرقان في حياة رسول الله صلى الله عليه وسلم فاستمعت لقراءته فإذا هو يقرأ على حروف كثيرة لم يقرئنيها رسول الله صلى الله عليه وسلم فكدت أساوره (٢) في الصلاة فتصبرت
_________
(١) هو هشام بن حكيم بن حزام القرشى الاسدي أسلم يوم الفتح ومات قبل ابيه كان من فضلاء الصحابة وخيارهم ممن يأمرون بالمعروف وينهون عن المنكر قال
ابو نعيم استشهد بأجنادين اه من الاستيعاب (واجنادين موضع بالشام من نواحى فلسطين بعضهم يقول انه بلغظ التثنية وبعضهم بلفظ الجمع قاله صاحب معجم البلدان) (٢) اساوره أي أثب عليه (*)',NULL,'77',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (81,'حتى سلم فلببته (١) بردائه فقلت من أقرأك هذه السورة التى سمعتك تقرأ فقال أقرأنيها رسول الله صلى الله عليه وسلم فقلت كذبت فان رسول الله صلى الله عليه وسلم قد أقرأنيها على غير ما قرأت (٢) فانطلقت به أقوده إلى رسول الله صلى الله عليه وسلم فقلت انى سمعت هذا يقرأ بسورة الفرقان على حروف لم تقرئنيها فقال رسول الله صلى الله عليه وسلم أرسله اقرأ يا هشام فقرأ عليه القراءة التى سمعته يقرأ فقال رسول الله صلى الله عليه وسلم كذلك انزلت ثم قال اقرأ يا عمر فقرأت القراءة التى أقرأنى فقال رسول الله صلى الله عليه وسلم كذلك انزلت ان هذا القرآن انزل على سبعة احرف فاقرؤا ما تيسر منه (٣) .
_________
(١) أي اخذته بردائه وهو بفتح اللام وتشديد الباء الاولى (٢) وسبب اختلاف قراءتهما كما ذكره ابن حجر في فتح الباري ان عمر حفظ هذه السورة من رسول الله صلى الله عليه وسلم قديما ثم لم يسمع ما نزل فيها بخلاف ما حفظه وشاهده، ولان هشاما من مسلمة الفتح فكان النبي صلى الله عليه وسلم اقرأه على ما نزل اخيرا فنشأ اختلافهما من ذلك، ومبادرة عمر للانكار محمولة على انه لم يكن سمع حديث انزل القرآن على سبعة احرف الا في هذه الواقعة اه (٣) روى البخاري هذا الحديث في كتاب التفسير في باب انزل القرآن على سبعة احرف ورواه ايضا في باب من لم ير بأسا ان يقول سورة البقرة وسورة كذا وكذا في كتاب التفسير، ورواه مسلم في آخر كتاب صلاة المسافرين
وقصرها في فضل من يقوم بالقرآن ويعلمه عند بيان ان القرآن انزل على سبعة حرف.
ورواه الطبري ايضا في تفسير.
(*)',NULL,'78',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (82,'قال ابن حجر في فتح الباري على صحيح البخاري عند هذا الحديث ما نصه.
" فصل " لم اقف في شئ من طرق حديث عمر على تعيين الاحرف التى اختلف فيها عمر وهشام من سورة الفرقان وقد زعم بعضهم فيما حكاه ابن التين انه ليس في هذه السورة عند القراء خلافء فيما ينقص من خط المصحف سوى قوله وجعل فيها سراجا وقرى سرجا جمع سراج قال وباقى ما فيها من الخلاف لا يخالف خط المصحف قال ابن حجر قلت وقد تتبع أبو عمر بن عبد البر ما اختلف فيه القراء من ذلك من لدن الصحابة ومن بعدهم من هذه السورة فأوردته ملخصا وزدت عليه قدر ما ذكره وزيادة على ذلك وفيه تعقب على ما حكاه ابن التين في سبعة مواضع أو اكثر اه منه ثم ذكر ابن حجر ما ورد من القراءات في سورة الفرقان فراجعه ان شئت فلو نقلناه هنا لطال بنا الكلام اه وفي رواية لابي بن كعب (١) انه قال دخلت المسجد اصلى فدخل
_________
(١) هو ابى بن كعب بن قيس الانصاري النجاري سيد القراء وهو احد فقهاء الصحابة واقرؤهم لكتاب الله تعالى وهو اول من كتب لرسول الله صلى الله عليه وسلم مقدمه المدينة كتب الوحى قبل زيد ومعه ايضا وروانس ان النبي صلى الله عليه وسلم دعا ابيا فقال ان الله امرني ان اقرا عليك قال آلله سمانى لك قال نعم فجعل ابى (*)',NULL,'79',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (83,'رجل فافتتح النحل فقرأ فخالفنى في القراءة فلما انفتل (١) قلت من أقرأك قال رسول الله صلى الله عليه وسلم ثم جاء رجل فقام وصلى فقرأ فافتتح النحل فخالفنى وخالف صاحبي فلما انفتل قلت من أقرأك قال رسول الله صلى الله عليه وسلم قال فدخل قلبى من الشك والتكذيب اشد مما كان في الجاهلية فأخذت بأيديهما وانطلقت بهما إلى رسول الله صلى الله عليه وسلم فقلت استقرئ هذين فاستقرأ احدهما فقال أحسنت فدخل قلبى من الشك والتكذيب أشد مما كان في الجاهلية ثم استقرأ الآخر فقال احسنت فدخل صدري من الشك والتكذيب اشد مما كان في الجاهلية فضرب رسول الله صلى الله عليه وسلم صدري بيده فقال اعيذك بالله يا أبى من الشك ثم قال ان جبريل عليه السلام اتانى فقال ان ربك عزوجل يأمرك ان تقرأ القرآن على حرف واحد فقلت اللهم خفف عن امتى ثم عاد فقال ان ربك عزوجل يأمرك ان تقرأ القرآن على حرفين فقلت اللهم خفف عن امتى ثم عاد فقال ان ربك عزوجل يأمرك ان تقرا القرآن على سبعة احرف وأعطاك بكل ردة مسألة الحديث - وفي صحيح مسلم عن ابى بن كعب
_________
يبكى قال انس ونبئت انه قرا عليه لم يكن الذين كفروا، مات ابى سنة اثنين وعشرين فقال عمر اليوم مات سيد المسلمين وقيل مات في خلافة عثمان سنة ثلاثين وقيل غير ذلك اه ملخصا من الاصابة والاستيعاب.
(١) انفتل أي انصرف من صلاته.
(*)',NULL,'80',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (84,'رواية بهذا المعنى ايضا في آخر كتاب صلاة المسافرين وقصرها في فضل من يقوم بالقرآن ويعلمه وللطبرى رواية بهذا المعنى عن أبى بن كعب ايضا وروى البخاري عن ابن مسعود (١) رضى الله عنه قال سمعت
رجلا قرأ وسمعت النبي صلى الله عليه وسلم يقرأ خلافها فجئت به النبي صلى الله عليه وسلم فأخبرته فعرفت في وجهه الكراهية وقال كلا كما محسن ولا تختلفوا فان
_________
(١) هو عبد الله بن مسعود بن غافل الهذلى اسلم قديما وهاجر الهجرتين وشهد بدرا والمشاهد بعدها ولازم النبي صلى الله عليه وسلم وكان يحمل نعليه قال أبو نعيم كان سادس من أسلم وكان يقول أخذت من في رسول الله صلى الله عليه وسلم سبعين سورة واختلف في وفاته فقيل توفى سنة اثنين وثلاثين وقيل غير ذلك اه ملخصا من الاصابة وينسب إلى امه احيانا فيقال ابن أم عبد وكان يلج على رسول الله صلى الله عليه وسلم ويلبسه نعليه ويمشى امامه ومعه ويستره إذا اغتسل ويوقظه إذا نام وقال له رسول الله صلى الله عليه وسلم اذنك على أن يرفع الحجاب وأن تسمع سوادي حتى أنهاك رواه مسلم في كتاب السلام في باب استحباب السلام على الصبيان وكان يعرف في الصحابة بصاحب السواد والسواك وزاد بعضهم والفراش والوساد وشهد له رسول الله صلى الله عليه وسلم بالجنة وإلى ما ذكر اشار صاحب نظم عمود النسب بقوله ومن هذيل صاحب السواد * والنعل والفراش والوساد قال الامام النووي في شرحه على صحيح مسلم عند الحديث المذكور السواد بكسر السين المهملة اتفق العلماء على ان المراد به السرار بكسر السين وبالراء المكررة وهو السر والمسارر يقال ساودت الرجل مساودة إذا ساررته اه (*)',NULL,'81',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (85,'من كان قبلكم اختلفوا فهلكوا.
رواه البخاري في كتاب بدء الخلق في حديث الغار في اول باب منه وللطبرى وللطبراني (١) عن زيد بن أرقم قال جاء رجل إلى
رسول الله صلى الله عليه وسلم فقال اقرأني ابن مسعود سورة اقرأنيها زيد واقرأنيها أبى بن كعب فاختلف قراءتهم فبقراءة أيهم آخذ فسكت رسول الله صلى الله عليه وسلم وعلى إلى جنبه فقال على ليقرأ كل انسان منكم كما علم فانه حسن جميل.
وعن هشام بن على عن زيد بن علقمة النخعي قال: لما خرج عبد الله بن مسعود من الكوفة اجتمع إليه اصحابه فودعهم ثم قال لا تنازعوا في القرآن فانه لا يختلف ولا يتلاشى ولا ينفد بكثرة الرد وان شريعته الاسلام وحدوده وفرائضه فيه واحدة ولو كان شئ من الحرفين ينهى عن شئ يأمر به الآخر كان ذلك الاختلاف ولكنه جامع ذلك كله لا تختلف فيه الحدود والفرائض ولا شئ من شرائع الاسلام ولقد رأيتنا نتنازع فيه عند رسول الله صلى الله عليه وسلم فيأمرنا نقرأ عليه فيخبرنا ان كلنا محسن ولو أعلم أحدا أعلم بما انزل الله على رسوله منى
_________
(١) الطبري هو الامام محمد بن جرير الطبري المولود سنة ٢٢٤ هجرية والطبراني من اصحاب الحديث (*)',NULL,'82',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (86,'لطلبته حتى ازداد علمه إلى علمي ولقد قرأت من لسان رسول الله صلى الله عليه وسلم سبعين سورة قد كنت علمت انه يعرض عليه القرآن في كل رمضان حتى كان عام قبض فعرض عليه مرتين فكان إذا فرغ اقرأ عليه فيخبرني انى محسن فمن قرأ على قراءتى فلا يدعنها رغبة عنها ومن قرأ على شئ من هذه الحروف فلا يدعنه رغبة عنه (١) فان من جحد بآية جحد به كله اه رواه الطبري في تفسيره وهل اللفظ واحد ام لا يحتاج إلى المراجعة.
وهناك روايات كثيرة في نزول القرآن على سبعة احرف اكتفينا بما ذكر لان سرد جميعها موجب للتطويل (٢) فاختلاف هذه الاحرف انما هو اختلاف ألفاظ وتلاوة لا اختلاف معان موجبة لاختلاف احكامه (مثال ذلك) ما رواه ابن فارس بسنده عن هانئ قال كنت عند عثمان رضى الله عنه وهم يعرضون المصاحف فأرسلني بكتف شاة إلى أبى بن كعب فيها " لم يتسن " و " فأمهل الكافرين " و " لا تبديل
_________
(١) وفى هذا المعنى روى الطبراني عن ابن مسعود ان النبي صلى الله عليه وسلم قال انزل القرآن على سبعة احرف فمن قرأ على حرف منها فلا يتحول إلى غيره رغبة عنه (٢) ذكر الامام ابن جرير الطبري كثيرا من الروايات الواردة في نزول القرآن على سبعة احرف في اول تفسيره واطال الكلام فيه (*)',NULL,'83',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (87,'للخلق " قال فدعا بالدواة فمحا احدى اللامين وكتب " لخلق الله " ومحا فأمهل وكتب " فمهل " وكتب " لم يتسنه " ألحق فيها هاء والقراءة في المصاحف على هذا الاصلاح.
ولقد ذهب العلماء في المراد بهذه الاحرف السبعة إلى نحو أربعين قولا ذكرها الامام السيوطي في كتابه الاتقان في علوم القرآن نذكر ملخص ذلك وهو.
المختار منها أن المراد سبع لغات كما صححه البيهقى في الشعب واختلفوا في تعيينها فقال أبو عبيدة قريش وهذيل وثقيف وهوازن وكنانة وتميم واليمن وقيل غير ذلك، وجاء عن ابى صالح عن ابن عباس قال نزل القرآن على سبع لغات (١) منها خمس بلغة العجز من هوازن ويقال لهم علياء هوازن.
قال أبو عبيدة ليس المراد ان كل كلمة تقرأ على سبع لغات بل اللغات السبع مفرقة فيه فبعضه بلغة قريش وبعضه بلغة هذيل وبعضه بلغة هوازن وبعضه بلغة اليمن ومعناه ان جبريل عليه السلام كان يأتي في كل عرضة بحرف إلى أن تمت الاحرف السبعة وذلك تخفيف
_________
(١) قال بعضهم الحكمة في نزول القرآن على سبع لغات من أعيان العرب تأليف قلوبهم لما كان فيهم من الحمية العربية ولطلب فهم المراد فافتخر كل بلغته حين شاهدوا نزول القرآن فاستأنس كثير من فصحائهم فكان سبب ايمانه اه (*)',NULL,'84',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (88,'وتيسير على الامة في التكلم بكتابهم كما خفف عنهم في شريعتهم هذا هو المعول عليه اه (١) وقال أبو شامة ظن قوم ان القراءت السبع الموجودة الآن هي التى اريدت في الحديث وهو خلاف اجماع اهل العلم قاطبة وانما يظن ذلك بعض اهل الجهل * وقال مكى بن ابى طالب واما من ظن ان قراءة هؤلاء القراء كعاصم ونافع هي الاحرف السبعة التى في الحديث فقد غلط غلطا عظيما قال ويلزم من هذا ان ما خرج عن قراءة هؤلاء السبعة مما ثبت عن الائمة وغيرهم ووافق خط المصحف لا يكون قرآنا وهو غلط عظيم قال وهذه القراءات التى يقرأ بها اليوم وصحت رواياتها عن الائمة جزء من الاحرف السبعة التى نزل بها القرآن اه من فتح الباري على صحيح البخاري.
وقال ابن قتيبة لم ينزل القرآن الا بلغة قريش (٢) واحتج بقوله " وما ارسلنا من رسول الا بلسان قومه " فعلى هذا تكون اللغات السبع
_________
(١) ويدل على هذا ما اخرجه أبو داود من طريق كعب الانصاري ان عمر
كتب إلى ابن مسعود ان القرآن نزل بلسان قريش فأقرئ الناس بلغة قريش لا بلغة هذيل اه وابن مسعود كان من هذيل (٢) ومعنى ان القرآن نزل بلغة قريش سبق بيانه في الجمع الثالث عند رواية البخاري فارجع إليه وهو في هامش صحيفة ٣٥ (*)',NULL,'85',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (89,'في بطون قريش وبذلك جزم أبو على الاهوازي، وقال ايضا في كتاب المشكل ان الله امر نبيه صلى الله عليه وسلم بأن يقرئ كل امة بلغتهم وما جرت به عادتهم فالهذلي يقرأ عتى حين وغيره حتى حين والاسدي يعلمون وتعلمون وتسود وجوه وألم إعهد اليكم بكسر حرف المضارعة والتميمى يهمز والقرشي لا يهمز والآخر يقرأ قيل لهم وغيض الماء باشمام الضم مع الكسر وهذا يقرأ عليهم وفيهم بضم الهاء وهكذا وكل ذلك ثابت بالوحى المنزل على نبيه صلى الله عليه وسلم قال ابن قتيبة ولو اراد كل فريق من هؤلاء ان ينزل عن لغته وما جرى عليه اعتياده طفلا ويافعا وكهلا لاشتد ذلك عليه وعظمت المحنة فيه ولا يمكنه الا بعد رياضة للنفس طويلة وتذليل للسان وقطع للعادة فأراد الله برحمته ولطفه ان يجعل له متسعا في اللغات ومتصرفا في الحركات كتيسيره عليهم في الدين اه وهذه اللغات والقراءة بها كانت موجودة ومعمولا بها إلى عهد عثمان رضى الله عنه فلما اختلطت قبائل العرب وعرف كل لغة الآخر وسهل على كل قبيلة النطق بلغة القبيلة الاخرى وحدث في عهده رضى الله عنه ما يدعو إلى حمل الناس على القراءة بلغة واحدة أمر رضى الله عنه بجمع القرآن وكتابته وقراءته بلغة واحدة.
اه كل ذلك من كتاب الاتقان للسيوطي رحمه الله ومن غيره',NULL,'86',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (90,'وفي نهاية القول المفيد.
قال المحقق ابن الجزرى ولازلت أستشكل هذا الحديث (أي حديث ان هذا القرآن انزل على سبعة أحرف الخ) وافكر فيه وأمعن النظر من نحو نيف وثلاثين سنة حتى فتح الله على بما يمكن أن يكون صوابا ان شاء الله تعالى وذلك انى تتبعت القراءات صحيحها وضعيفها وشاذها فإذا هي يرجع اختلافها إلى سبعة أوجه لا يخرج عنها وذلك - إما في الحركات بلا تغيير في المعنى والصورة نحو البخل باثنين ويحسب بوجهين - أو بتغيير في المعنى فقط نحو فتلقى آدم من ربه كلمات - واما في الحروف بتغيير في المعنى لا في الصورة نحو تبلوا وتتلوا وعكس ذلك نحو بسطة وبصطة - أو بتغييرهما نحو اشد منكم ومنهم - واما في التقديم والتأخير نحو فيقتلون ويقتلون - أو في الزيادة والنقصان نحو ووصى وأوصى - فهذه سبعة أوجه لا يخرج الاختلاف عنها اه كلامه (١)
_________
(١) فمعنى قوله نحو البخل باثنين أي بقراءتين كآية " ويأمرون الناس بالبخل " في سورة النساء فقد قرئ بالبخل بضم الباء وسكون الخاء وقرئ بفتحهما، ومعنى قوله ويحسب بوجهين اي قرئ بفتح السين وكسرها كآية " يحسب أن ماله أخلده " ومعنى قوله أو بتغيير في المعنى فقط نحو " فتلقى آدم من ربه كلمات " أي قرئ برفع آدم على انه فاعل ونصب كلمات على انه مفعول به وقرئ بالعكس أي بنصب آدم على انه مفعول وبرفع كلمات على انه فاعل، ومعني قوله نحو تبلوا وتتلوا أي قرئ تعالى " هنالك تبلوا كل نفس ما اسلفت " بيونس بالتاء ثم بالباء قبل اللام وقرئ (*)',NULL,'87',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (91,'وقد حمل ابن قتيية وغيره العدد المذكور في حديث انزل القرآن
على سبعة احرف على الوجوه التى يقع بها التغاير في سبعة اشياء ذكرها مفصلا ابن حجر في فتح الباري على صحيح البخاري عند الكلام على هذا الحديث فراجعه ان شئت فانا لم ننقلها منه خوف التطويل وقد استوفى ابن حجر رحمه الله شرح هذا الحديث في فتح الباري ينبغى مطالعته فانه مبحث مهم ولا يبعد ان يكون هذا الحديث متشابها يفوض معناه إلى الله تعالى كما ذهب إليه بعض العلماء وذلك لامرين - الاول - كثرة اختلاف العلماء في معناه حتى بلغ نحو اربعين قولا - الثاني - ورود احاديث كثيرة في هذا المعنى بعبارات مختلفة (منها) قوله صلى الله عليه وسلم أقرأنى جبريل على حرف فراجعته فلم أزل أستزيده ويزيدني حتى انتهى
_________
تتلوا بتاءين، ومعنى قوله نحو اشد منكم ومنهم اي قرئ قوله تعالى " كانوا هم اشد منهم قوة " بغافر اشد منهم واشد منكم، ومعنى قوله واما في التقديم والتأخير نحو فيقتلون ويقتلون اي قرئ قوله تعالى " يقاتلون في سبيل الله فيقتلون ويقتلون " بالتوبة الاول بالبناء للمعلوم والثانى للمجهول وقرئ بالعكس اه اخذنا هذا البيان مشافهة عن الصالح المبارك الشيخ احمد التيجى عمدة قراء الحجاز بمكة المشرفة اطال الله حياته ونفع به الامة آمين قال السيوطي في الاتقان قلت ومن امثلة التقديم والتأخير قراءة الجمهور " كذلك يطبع الله على كل قلب متكبر جبار " وقرأ ابن مسعود على قلب كل متكبر جبار اه (*)',NULL,'88',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (92,'إلى سبعة احرف، رواه الشيخان ولمسلم برواية اخري، وللترمذي من وجه آخر انه صلى الله عليه وسلم قال يا جبريل انى بعثت إلى أمة أميين منهم العجوز
والشيخ الكبير والغلام والجارية والرجل الذى لم يقرأ كتابا قط الحديث (ومنها) انزل القرآن من سبعة ابواب على سبعة احرف كلها شاف كاف رواه الطبراني (ومنها) انزل القرآن على سبعة احرف فمن قرأ على حرف منها فلا يتحول إلى غيره رغبة عنه رواه الطبراني (ومنها) انزل القرآن على عشرة احرف بشير ونذير وناسخ ومنسوخ وعظة ومتشابه ومثل ومحكم وحلال وحرام رواه السجزى في الابانة (ومنها) انزل القرآن على ثلاثة أحرف رواه احمده وغيره (ومنها) ان رسول الله صلى الله عليه وسلم كان عند أضاءة بنى غفار (١) فأتاه جبريل عليه السلام فقال ان الله عزوجل يأمرك ان تقرئ أمتك القرآن على حرف قال اسأل الله معافاته ومغفرته وان امتى لا تطيق ذلك ثم اتاه الثانية فقال ان الله عز وجل يأمرك ان تقرئ امتك القرآن على حرفين قال اسأل الله معافاته ومغفرته وان امتى لا تطيق ذلك ثم جاءه الثالثة فقال ان الله عزوجل يأمرك ان تقرئ امتك القرآن على ثلاثة احرف فقال اسأل الله معافاته ومغفرته وان امتى لا تطيق ذلك ثم جاءه الرابعة فقال ان الله عزوجل
_________
(١) أضاءة هي بفتح الهمزة وبضاد معجمة ماء مستنقع كالغدير بالمدينة (*)',NULL,'89',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (93,'يأمرك ان تقرئ امتك القرآن على سبعة أحرف فأيما حرف قرؤا عليه فقد أصابوا رواه النسائي في سننه * فإذا أمعنت النظر في هذه الاحاديث ظهرت لك منها جملة معان فتأمل جيدا.
والحقيقه التى لا تنكر انه لولا عثمان رضى الله عنه جمع الامة على مصحف واحد وعلى حرف واحد لذهب المسلمون اليوم في القرآن الكريم كل مذهب، ولاختلفت القرءآت لديهم كل الاختلاف، ولوجد اعداء
الدين مسلكا سهلا لايقاع الشك والدسيسة في قلوب ضعاف المسلمين وجهالهم، وإذا وقع الاختلاف والتكذيب في عهده رضى الله عنه فكيف بنا اليوم وقد بدأ الاسلام يعود غريبا فجزى الله صحابة رسول الله صلى الله عليه وسلم عن الاسلام والمسلمين خير الجزاء ووفقنا لاتباع مسلكهم القويم ومنهجهم المستقيم آمين ولننقل هنا نص ما ذكره الامام محمد بن جرير الطبري المولود سنة أربع أو خمس وعشرين ومائتين في تفسيره بعد أن بين وجهة حمل عثمان الناس على مصحفه وهو (فان قال) بعض من ضعفت معرفته وكيف جار لهم ترك قراءة أقرأهموها رسول الله صلى الله عليه وسلم وأمرهم بقرائتها (قيل) ان أمرهم بذلك لم يكن أمر ايجاب وفرض وانما كان أمر اباحة ورخصة.
الخ (ويسأل بعضهم) لم لم تكن الاحرف الستة موجودة وقد',NULL,'90',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (94,'انزلت من عند الله تعالى على نبيه صلى الله عليه وسلم وهو اقرأها اصحابه فأن نسخت فرفعت فما الدليل عليه وان نسيتها الامة وتركتها فذلك تضييع ما قد أمروا بحفظه (فأجاب الامام ابن جرير الطبري) على هذه الاسئلة بقوله: لم تنسخ الاحرف الستة فترفع ولا ضيعتها الامة وهى مأمورة بحفظها ولكن الامة أمرت بحفظ القرآن وخيرت في قراءته وحفظه بأى تلك الاحرف السبعة شاءت وضرب لها مثلا في الفقه وهو إذا حنث موسر في يمين فله أن يختار كفارة من ثلاث كفارات إما بعتق أو إطعام أو كسوة فكذلك الامة أمرت بحفظ القرآن وقراءته وخيرت في قراءته بأى الاحرف السبعة شاءت فرأت لعلة من العلل أوجبت عليها الثبات على حرف واحد قراءته بحرف واحد ورفض
القراءة بالاحرف الستة الباقية ولم تحظر قراءته بجميع حروفه على قارئه بما اذن له في قراءته به ثم أورد الطبري أنباء ما قد حدث في أيام أبى بكر وعثمان عن جمع المصحف اه.
ولا يخفى أن جواب ابن جرير سديد * قال أبو شامة وقد اختلف السلف في الاحرف السبعة التى نزل بها القرآن هل هي مجموعة في المصحف الذى بأيدى الناس اليوم أو ليس فيه الاحرف واحد منها مال الباقلانى إلى الاول وصرح الطبري وجماعة بالثاني وهو المعتمد اه من فتح الباري',NULL,'91',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (95,'وسبب اختلاف القراءات السبع وغيرها كما قال ابن هشام ان الجهات التى وجهت إليها المصاحف كان بها من حمل عنه اهل تلك الجهة وكانت المصاحف خالية من النقط والشكل قال فثبت أهل كل ناحية على ما كانوا تلقوه سماعا عن الصحابة بشرط موافقة الخط وتركوا ما يخالف الخط امتثالا لامر عثمان الذى وافقه عليه الصحابة لما رأوا في ذلك من الاحتياط للقرآن فمن ثم نشأ الاختلاف بين قراءة الامصار مع كونهم متمسكين بحرف واحد من السبعة اه من فتح الباري على صحيح البخاري فوائد اختلاف القراءات نزول القرآن بالاحرف السبعة لا يؤدى إلى التناقض في الاحكام الشرعية واصول الدين وفي الحلال والحرام والامر والنهى، فالاختلاف الواقع بين هذه الاحرف انما هو اختلاف ألفاظ وتلاوة فقط وتؤخذ منه جملة فوائد (منها) بيان حكم مجمع عليه كقراءة سعد بن ابى وقاص وغيره " وله اخ أو اخت من أم " فان هذه القراءة تبين ان المراد بالاخوة
هنا الاخوة للام " ومنها " ترجيح حكم اختلف فيه كقراءة " أو تحرير رقبة مؤمنة في كفارة اليمين " ففيها ترجيح لاشتراط الايمان كما ذهب إليه الشافعي وغيره ولم يشترطه أبو حنيفة " ومنها " الجمع بين حكمين',NULL,'92',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (96,'مختلفين كقراءة يطهرن ويطهرن بالتخفيف والتشديد فينبغي الجمع بينهما وهو ان الحائض لا يقربها زوجها حتى تطهر بانقطاع حيضها وتطهر بالاغتسال " ومنها " ايضاح حكم يقتضى الظاهر خلافه كقراءة " فامضوا إلى ذكر الله " فان قراءة فاسعوا يقتضى ظاهرها المشى السريع وليس كذلك فكانت القراءة الاخرى موضحة لذلك " ومنها " تفسير ما لعله لا يعرف كقراءة " كالصوف المنفوش " " ومنها " ما هو حجة لترجيح قول بعض العلماء كقراءة " أو لمستم النساء " إذ اللمس يطلق على الجس والمس (ومنها) ما هو حجة لاهل الحق ودفع لاهل الزيغ كقراءة " وملكا كبيرا " بكسر اللام (١) وردت عن ابن كثير وغيره وهى من اعظم الدليل على رؤية الله تعالى في الدار الاخرة وقد قيل وخير ما فسرته بالوارد إلى غير ذلك - اه من اجابة شيخ المقارئ المصرية لاسئلتنا التى كنا بعثناها إليه من مكة المشرفة وسنذكر منها في هذا الكتاب ما يناسب كل مقام وفصل ان شاء الله تعالى.
_________
(١) من آية " وإذا رأيت ثم رأيت نعيما وملكا كبيرا " بسورة الانسان (*)',NULL,'93',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (97,'<span data-type="title" id=toc-14>الباب الثالث</span>
(وفيه خمسة فصول)
<span data-type="title" id=toc-15>(الفصل الاول * في رسم المصحف العثماني وقواعده)</span>
المراد برسم المصحف ما كتبه الصحابة من الكلمات القرآنية في المصحف العثماني على هيئة مخصوصة لا تتفق مع قواعد الكتابة وينحصر امر هذا الرسم في ست قواعد (١) وهى: الحذف، والزيادة، والهمز، والبدل والوصل، والفصل، وما فيه قراءتان فكتب على احداهما وقد جمع هذه القواعد العلامة لمرحوم الشيخ محمد العاقب الشنقيطى بقوله: الرسم في ست قواعد استقل * حذف زيادة وهمز وبدل وما أتى بالوصل أو بالفصل * موافقا للفظ أو للاصل وذو قراءتين مما قد شهر * فيه على احداهما قد اقتصر وشرح هذه القواعد يطول وأنما نأتى بجملة أمثلة اقتطفناها من كتاب إيقاظ الاعلام لوجوب اتباع رسم المصحف الامام للعلامة المحدث الشهير الشيخ محمد حبيب الله الشنقيطى رحمه الله تعالى (فمثال الحذف) تعلمن مما علمت، ربى اكرمن، فأرسلون يوسف ايها الصديق
_________
(١) أي في ستة انواع فان رسمه لا قاعدة له ولا يتمشى مع القواعد الاملائية (*)',NULL,'94',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (98,'وحذف واو داود، واحدى نون ننجي بالانبياء، وحذف احدى اللامين من نحو اليل والذى، وحذف الالف من بسم الله، ومن لتخذت عليه اجرا، وحذف الواو من نحو يمح الله الباطل، ويدع الانسان وقد أشار الشيخ محمد العاقب إلى مواضع حذف الواو من آخر الفعل بقوله وحذف الواو بغير داع * في يدع الانسان ويدع الداع سندع صالح ويمح الله * إن سبق الباطل لا سواه (١) " ومثال الزيادة " لكنا هو الله ربى، سأوريكم آياتى، وأولئك،
والسماء بنينها بأييد، بلقاءى ربهم، ولا تقولن لشائ، أو لاذبحنه (ومثال البدل) يتوفيكم، ومن عصاني، والاقصا والصلوة، والربو، والزكوة، وليكونا من الصاغرين، وان رحمت الله (ومثال الوصل) ألن نجعل لكم، وألن نجمع عظامه، فأينما تولوا فثم وجه الله، ويكان الله (ومثال الفصل) أن لا اله الا انت سبحانك، وأن لا اله الا هو بهود، ولكى لا يكون على المؤمنين حرج ومال هذا الكتاب (ومثال ما فيهما قراءتان فكتب على احداهما)
_________
(١) يعني تحذف الواو من قوله تعالى " ويمح الله الباطل " بالشوري بخلاف قوله " يمحوا الله ما يشاء ويثبت " بالرعد فانه باثبات الواو (*)',NULL,'95',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (99,'كالصراط كتبت بالصاد مع ان قراءة المكى من رواية قنبل بالسين الخالصة وقراءة خلف باشمام الصاد زايا، ومثله بصطة وبمصيطر فيكتب الجميع بالصاد لا غير، وكالالف المرسوم في لاهب لك غلاما زكيا مع انه قرئ بياء المضارعة إلى غير ذلك من الامثلة (أما مثال الهمز) فالهمز له أحوال متنوعة وأمثلة كثيرة تعرف من كتب الاملاء وقد فصل علماء الرسم احوال الهمز في القرآن لا داعى لذكرها هنا خوف التطويل ومن اراد بسط القول فليرجع إلى كتب القراءات وسنذكر ان شاء الله تعالي طرفا من احوال الهمز في آخر الباب الرابع

<span data-type="title" id=toc-16>الفصل الثاني (في اختلاف رسم المصاحف العثمانية)</span>
سبق الكلام على بيان عدد المصاحف التى أرسلها عثمان بن عفان رضى الله عنه إلى المدن والامصار وهذه المصاحف كلها تسمى المصاحف العثمانية وهى التى يجب اتباع رسمها وان اختلف رسم كل مصحف عن الاخر بالحذف والاثبات، فمن قال بالحذف مثلا في بعضها يدعى انه هو الموجود في المصحف العثماني ومن قال بالاثبات يدعى عكس ذلك مع اتفاق الطرفين على ان الموجود في المصحف العثماني هو الحق الثابت في نفس الامر باجماع الامة وذلك كالخلاف في كلمة " لدا " هل كتبت بالالف ام بالياء كما اشار إليه الخراز في مورد الظمآن بقوله:',NULL,'96',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (100,'وفي لدا في غافر يختلف * وفي لدا الباب اتفاقا ألف (١) وقال في كلمة الربا وبعضهم في الروم ايضا كتبا * واوا بقوله تعالى من ربا وقال في كلمة تعسا وابن نجاح قال عن بعض أثر * تعسا بياء وهو غير مشتهر وكالخلاف الواقع في هذه الكلمات: لاوضعوا، ولانتم، ولاتوها ولالى - هل تزاد فيها ألف بعد الالف الاصلية كما زيدت في كلمة " لااذبحنه " ام لا.
(واعلم) أن الخلاف الواقع في رسم بعض كلمات المصحف ليس خلافا حقيقيا بل هو خلاف صوري، اما الخلاف الواقع في وجوه القراءات السبع فهو خلاف حقيقي واقع بينهم لكن مع تجويز كل واحد من السبعة قراءة غيره واعترافه بأنها متواترة وانها من عند الله تعالى وهذا الخلاف في وجوه القراءات ليس على حد الخلاف في الاحكام الشرعية لان كلا من وجوه القراءات حق في نفس الامر كما صرح
_________
(١) أي كتبت " لدا " بالياء في آية لدى الحناجر بغافر، وفي بعض المصاحف
كتبت بالالف بخلافها في آية لدا الباب بيوسف فانها بالالف اتفاقا (*)',NULL,'97',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (101,'به عليه الصلاة والسلام وكلا من الاحكام الشرعية حق باعتبار الاجتهاد وفي نفس الامر الحق واحد ليس الا لحرمة العمل بالمقابل اه من ايقاظ الاعلام.
ذكر جملة من الامثلة التى اختلفت كتابتها ورسومها في المصاحف قوله تعالى " لئن انجانا " في سورة الانعام مكتوب في المصحف الكوفى بالالف وفي غيره بالتاء بعد الياء أي انجيتنا.
وقوله تعالى " كانوا أشد منهم قوة " مكتوب منكم بالكاف في المصحف الشامي وبالهاء في غيره.
وقوله تعالى " واذ نجياكم من آل فرعون " هو هكذا في امام اهل العراق وفى امام اهل الشام واهل الحجاز واذ نجاكم.
وقوله تعالى " وما عملت ايديهم " هكذا في بعضها وفي بعضها وما عملته ايديهم وقوله تعالى " وجعل اليل سكنا " هكذا في بعضها وفي بعضها وجاعل اليل بالالف.
وقوله تعالى " سارعوا إلى مغفرة من ربكم " بغير واو قبل السين وفي بعضها وسارعوا بالواو.
وقوله تعالى) قل انما أدعوا ربى " هكذا في بعضها وفى بعضها قال انما بالالف.
وقوله تعالى " والشمس والقمر حسبانا " في بعض المصاحف بحذف الالف من باء حسبانا هكذا حسبنا.
وقوله تعالى " هررت ومروت " في بعض',NULL,'98',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (102,'المصاحف باثبات الالف في الهاء والميم وفي بعهضا بحذفها منهما.
وقوله تعالى " لومة لائم " في بعض المصاحف هكذا - لئم - بحذف ألف
المد.
وقوله تعالى " فأحيكم ثم يميتكم " في بعضها فاحياكم بالالف وكلمة " ابراهيم " مرسومة في سورة البقرة بحذف الياء في المصحف الشامي رالعراقى ومرسومة باثباتها في المصحف المكى والمدنى.
وألف التثنية فد تحذف في بعض المصاحف وفي بعضها لا تحذف نحو قوله تعالى " إذ همت طائفتان " وقوله " كانا يأكلان الطعام " إلى غير ذلك وهذا حسبما ذكره أئمة القراءات المتقدمون ونقلوه بالسند المتصل عن الثقاة العدول الذين شاهدوا تلك المصاحف العثمانية.
(سبب اختلاف رسوم المصاحف العثمانية) لا ندرى لم اختلفت رسوم تلك المصاحف التى كتبت بأمر عثمان رضى الله عنه وارسلت إلى المدن والامصار وقد اجاب على هذا العلامة الشيخ محمد حسنين مخلوف العدوى وكيل الجامع الازهر والمعاهد الدينية بمصر المتوفى عام ١٣٥١ تقريبا رحمه الله تعالى في كتابه " عنوان البيان في علوم التبيان " بقوله.
ان هذا الاختلاف بين تلك المصاحف انما هو اختلاف قراءات في لغة واحدة (١) لا اختلاف لغات قصد
_________
(١) وهى لغة قريش كما سبق الكلام عند جمع عثمان المصحف (*)',NULL,'99',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (103,'باثباته انفاذ ما وقع الاجماع عليه إلى اقطار بلاد المسلمين واشتهاره بينهم وانما كتبت هذه في البعص بصورة وفي آخر بأخرى لانها لو كررت في كل مصحف لتوهم نزولها كذلك ولو كتبت بصورة في الاصل وباخرى في الحاشية لكان تحكما مع ايهام التصحيح ومثل هذا بعد امر عثمان رضى الله عنه وبعثه إلى كل جهة ما اجمع الصحابة على الاخذ به لا يؤدى إلى تنازع أو فتنة لان أن اهل كل جهة قد استندوا إلى اصل مجمع عليه
وامام يرشدهم إلى كيفية قراءته والحاصل ان المصاحف العثمانية كتبت بحرف واحد وهو حرف قريش وان ذلك الحرف يسع من القراءات ما يرسم بصور مختلفة اثباتا وحذفا وابدالا فكتب في بعضها برواية وفي بعضها برواية اخرى تقليلا للاختلافا في الجهة الواحدة بقدر الامكان فكما اقتصر على لغة واحدة في جميع المصاحف اقتصر على رسم رواية واحدة في كل مصحف والمدار في القراءة على عدم الخروج عن رسم تلك المصاحف ولذلك لا يحظر على اهل أي جهة أن يقرؤا بما يقتضيه رسم الجهة الاخرى اه كلامه رحمه الله تعال وهو كلام حسن وجواب سديد.
ولم نقف على شئ من كلام المتقدمين والمتأخرين من العلماء في هذا الموضوع سواه فمن لم يقتنع بجواب الشيخ العدوي المذكور نقول',NULL,'100',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (104,'له ان رسم المصاحف العثمانية سر من الاسرار التى لم تهتد إلى حله فحول العلماء ونوابغ العقلاء كما سنتكلم عنه فما علينا غير الاتباع والتسليم.

<span data-type="title" id=toc-17>الفصل الثالث (في رسم القرآن الكريم هل هو توقيفي ام لا)</span>
اختلف العلماء في رسم المصحف العثماني فبعضهم يقول انه من اصطلاح الصحابة وبعضهم يقول انه توقيفي ويستدلون عليه بأن النبي صلى الله عليه وسلم كان هو الذى يملى زيد بن ثابت القران من تلقين جبريل عليه السلام كما يشهد بذلك اطباق القراء على قوله تعالى واخشوني في البقرة باثبات الياء وفي المائدة بحذفها في الموضعين ونظائر ذلك كثيرة مما يدل على ان هجاء القرآن وكتابته بالتوقيف وانه ليس من الرسم الموضوع وقد كتب القرآن في عهد رسول الله صلى الله عليه وسلم لكن غير مجموع في موضع واحد ولا مرتب السور.
والذى يظهر لنا والله تعالى اعلم ان رسم المصحف العثماني غير توقيفي ونستدل على قولنا هذا بخمسة امور.
(الامر الاول) ان من معجزات النبي صلى الله عليه وسلم كونه أميا لا يكتب ولا يقرأ كتابا كما قال تعالى " وما كنت تتلوا من قبله',NULL,'101',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (105,'من كتاب ولا تخطه بيمينك إذا لارتاب المبطلون (١) " فكيف يملى عليه الصلاة والسلام زيد بن ثابت على حسب قواعد الكتابة والاملاء من نحو الزيادة والنقص والوصل والفصل.
فهل كان يقول صلى الله عليه وسلم لكاتب الوحى اكتب كلمة " ابراهيم " في سورة البقرة كلها بغير ياء واكتبها في بقية القرآن بالياء واكتب كلمة " بأييد (٢) " بياءين.
واكتب كلمة " وجائ يومئذ بجهنم " بزيادة ألف بعد الجيم.
واكتب كلمة " لشائ (٣) " بزيادة ألف بعد الشين واكتب كلمة " أفإين مات (٤) " بزيادة ياء قبل النون.
واكتب كلمة " الله يبدؤا الخلق " بهمزة فوق الواو وألف بعدها.
واكتب هذه الكلمات " جاءو.
فاءو.
باءو.
تبوءو " بغير ألف فيها بعد واو الجماعة وفيما عدا هذه الكلمات أثبت الالف بعدها.
واكتب كلمة " مائة " بالالف واكتب كلمة " فئة " بغير ألف.
واكتب كلمة
_________
(١) فالامية في حقه عليه الصلاة والسلام كمال وفي حق غيره نقص وذلك لو كان متعلما الكتابة والقراءة لقالوا ان هذا القرآن ليس من عند الله وانما وضعه من نفسه بقوة علمه ومعرفته (٢) من آية والسماء بنيناها بأييد
(٣) من آية ولا تقولن لشئ انى فاعل ذلك غدا الا ان يشاء الله (٤) من آية افان مات أو قتل (*)',NULL,'102',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (106,'" سعوا " التى بالحج بالالف بعد الواو.
واحذفها من " سعو " التى بسبأ.
واكتب كلمة " واخشوني " بالياء في البقرة واحذفها منها في التى بالمائدة واحذف اللام الثانية من كلمة " اليل " وأثبتها في كلمة " اللؤلؤ " واكتب الكلمات " الصلوة.
الزكوة.
الربوا " بالواو واكتب " قرت عين لى " بالتاء واكتب " قرة اعين " بالهاء وافصل كى عن لا في " كى لا يكون دولة " وأوصلها في " لكيلا تأسوا " وهكذا في جميع القرآن.
فان كان املاء النبي صلى الله عليه وسلم القرآن لكاتب الوحى بهذه الصفة فالرسم توقيفي بلا جدال لكن لم نر منقولا ان النبي صلى الله عليه وسلم كان يملى كاتب الوحى بهذه الصفة والكيفية، فلو كان كذلك لتواتر عنه صلى الله عليه وسلم وما كان ذلك خافيا على احد، ولو كان كذلك ايضا لكان عليه الصلاة والسلام عارفا باصول الكتابة وقواعد الاملاء وكيف وهو النبي الامي
(الامر الثاني) لما اختلف زيد بن ثابت ومن معه في كلمة " التابوت " أيكتبونه بالتاء ام بالهاء رفعوا الامر إلى عثمان رضى الله عنه فأمرهم ان يكتبوها بالتاء.
فلو كان الرسم توقيفيا باملاء النبي صلى الله عليه وسلم بالكيفية التى ذكرناها لقال لهم زيد إن النبي صلى الله عليه وسلم امرني بكتابتها بالتاء ولقال عثمان لزيد كاتب الوحى اكتبها بالكيفية التى املاك بها رسول الله صلى الله عليه وسلم',NULL,'103',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (107,'(الامر الثالث) لو كان الرسم توقيفيا لما اختلف الرسم
في المصاحف التى ارسلها عثمان رضى الله عنه إلى المدن والامصار كما سبق بيانه قبل هذا الفصل.
(الامر الرابع) لو كان الرسم توقيفيا لصرح بذلك الامام مالك ولما جوز كتابة الصحف والالواح للصغار المتعلمين بغير الرسم العثماني ولصرح بذلك ايضا جميع الائمة.
(الامر الخامس) لو كان الرسم توقيفيا لنعتوه (بالرسم التوقيفى) أو (بالرسم النبوى) وما كانوا نعتوه (بالرسم العثماني) نسبة لعثمان بن عفان فاستدلالهم بأن زيد بن ثابت كتب كلمة (واخشوني) بالبقرة باثبات الياء وكتبها في المائدة بحذفها في غير محله، لان ثبوت الياء أو حذفها يعلم من وقوف القارئ على الكلمة، فان وقف بالسكون على نون واخشوني كتبت بالنون فقط وان وقف على الياء كتبت بالياء قال بعضهم ان مدار الرسم والكتابة * معتبر بالوقف والبداءه فزيد بن ثابت عرف ذلك من وقف النبي صلى الله عليه وسلم على الكلمة، فعلم مما ذكرناه ان رسم المصحف ليس توقيفيا وانما هو من وضع الصحابة واصطلاحهم لحكمة لم ندركها',NULL,'104',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (108,'بقى علينا أن نعرف لماذا لم يكتبوا المصحف على قواعد الكتابة ولماذا لم يمشوا في كتابته على وتيرة واحدة هذا سؤال يجب ان يوجه إلى الصحابة الذين كتبوه بأمر عثمان رضى الله عنه، وانى يكون ذلك وقد ذهبوا إلى جوار ربهم الكريم - ومن هنا يقول العلماء إن رسم المصحف سر من الاسرار لم يطلع عليه أحد وان خطه معجز كلفظه
المقروء.
هذا ولا تتوهمن عليهم السهو أو الخطأ في كتابة كلام الله تعالى وقد مر عليك بطلان ذلك في الفصل الثالث من الباب الثاني في ضبط وتصحيح المصحف الكريم، ولا يخطرن ايضا ببالك أنهم ما كانوا يعرفون اصول الكتابة فلذلك اضطربوا في رسم المصحف فان هذا وهم باطل كما سنقيم الدليل عليه في الفصل الخامس.

<span data-type="title" id=toc-18>الفصل الرابع (في حكم اتباع رسم المصحف العثماني)</span>
حكم اتباع رسم المصحف العثماني الوجوب باتفاق الائمة قاطبة وان لم ندرك حكمة كتابته على هذه الصورة من الرسم المخالف لقواعد الكتابة واليك تفصيل ذلك.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (109,'(قال بعضهم) لقد اجمع على كتابة المصاحف العثمانية اثنا عشر ألفا من الصحابة رضى الله عنهم فيجب على كل مسلم ان يقتدى بهم وبفعلهم لقوله صلى الله عليه وسلم عليكم بسنتى وسنة الخلفاء الراشدين المهديين من بعدى عضوا عليها بالنواجذ الحديث وقوله، اقتدوا باللذين من بعدى ابى بكر وعمر فانهما حبل الله المدود من تمسك بهما فقد تمسك بالعروة الوثقى وقال البيهقى في شعب الايمان من يكتب مصحفا فينبغي ان يحافظ على الهجاء الذى كتبوا به تلك المصاحف ولا يخالفهم فيه ولا يغيير مما كتبوه شيئا فانهم كانوا اكثر علما واصدق قلبا ولسانا واعظم امانة فلا ينبغى ان نظن بانفسنا استدراكا عليهم.
وسئل مالك رحمه الله تعالى (١) هل يكتب المصحف على ما احدثه
الناس من الهجاء فقال لا الا على الكتبة الاولى رواه الدانى في المقنع ثم قال ولا مخالف له من علماء الامة، وقال في موضع آخر سئل مالك عن الحروف في القرآن مثل الواو والالف أترى ان تغير في المصحف ان وجد فيه كذلك قال لا قال أبو عمر ويعنى الواو والالف المزيدتين في الرسم المعدومتين في اللفظ نحو أولو، وفى رواية قال أشهب سئل
_________
(١) ولد الامام مالك سنة ٩٥ هجريه وتوفى سنة ١٧٩ (*)',NULL,'106',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (110,'مالك فقيل له ارأيت من استكتب مصحفا اترى ان يكتب على ما احدثه الناس من الهجاء اليوم قال لا ارى ذلك ولكن يكتب على الكتبة الاولى (١) قال الدانى في المحكم ولا مخالف لمالك في ذلك من علماء الامة لان ما روى عنه هو مذهب باقى الائمة ومستند الائمة الاربع هو مستند الخلفاء الاربع وقال الامام احمد رحمه الله تعالى (٢) تحرم مخالفة خط مصحف عثمان في واو أو ياء أو الف أو غير ذلك، ونقل الجعبرى وغيره اجماع الائمة الاربعة على وجوب اتباع هذا المرسوم قال القرطبى في اوائل تفسيره وقال اشهب سمعت مالكا وسئل عن العشور التى تكون في المصحف بالحمرة وغيرها من الالوان فكره ذلك وقال تعشير المصحف بالحبر لا بأس به وسئل عن المصاحف يكتب فيها خواتم السور في كل سورة ما فيها من آية قال انى اكره ذلك في امهات المصاحف ان يكتب فيها شئ أو يشكل فأما ما يتعلم به من الغلمان من المصاحف فلا أرى بذلك بأسا قال أشهب ثم اخرج الينا مصحفا
_________
(١) يفهم من هذا ان الامة في القرنين الاولين ادركت مخالفة الرسم العثماني لقواعد كتاباتهم ورغبوا في كتابة المصاحف على القواعد الكتابية
فاستفتوا الامام مالكا فلم يفتهم بجواز ذلك فامتثلوا واطاعوا وما علينا الا اتباعهم والاقتداء بهم (٢) ولد الامام احمد سنة ١٦٤ هجريه وتوفي سنة ٢٤١ (*)',NULL,'107',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (111,'لجده كتبه إذ كتب عثمان المصاحف فرأينا خواتمه من حبر على عمل السلسلة في طول السطور ورأيته معجوم الآى بالحبر اه قوله معجوم الآى بالحبر أي موضوع في آخر كل آية نقطة من الحبر للفصل بين الآيات.
قال الخراز في مورد الظمآن مشيرا إلى اجابة مالك: ومالك حض على الاتباع * لفعلهم وترك الابتداع إذ منع السائل من ان يحدثا * في الامهات نقط ما قد احدثا وانما رآه للصبيان * في الصحف والالواح للبيان ووضع الناس عليه كتبا * كل يبين عنه كيف كتبا اجلها فاعلم كتاب المقنع * وقد اتى فيه بنص مقنع قوله وانما رآه للصبيان الخ أي ان مالكا رحمه الله جوز كتابة الالواح والصحف بغير الرسم العثماني للصغار الذين يتعلمون القرآن حتى لا يصعب عليهم التعليم وهذا القول عن مالك ذكره ايضا العلامة الشيخ محمد مكى نصر في كتابه القول المفيد في علم التجويد وقال الشيخ محمد العاقب الشنقيطى رحمه الله تعالى رسم الكتاب سنة متبعة * كما نحا اهل المناحى الاربعة لانه اما بأمر المصطفى * أو باجتماع الراشدين الخلفا',NULL,'108',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (112,'وكل من بدل منه حرفا * باء بكفر أو عليه أشفا (١) وقال القاضى عياض في آخر كتاب الشفا أجمع المسلمون ان من نقص حرفا قاصدا لذلك أو بدله بحرف آخر مكانه أو زاد فيه حرفا مما لم يشمل عليه المصحف الذى وقع عليه الاجماع واجمع على انه ليس من القرآن عامدا لكل هذا انه كافر اه كلامه وايده شراحه.
وقال الشيخ عبد الرحمن بن القاضى المغربي ولا يجوز مخالفة مرسوم المصحف العثماني ولا يلتفت إلى اعتلال من خالف بقوله ان العامة لا تعرف مرسوم المصحف ويدخل عليهم الخلل في قراءتهم في المصحف إذا كتب على المرسوم العثماني إلى آخر ما عللوا به فهذا ليس بشئ لان من لا يعرف المرسوم من الامة يجب عليه ان لا يقرأ في المصحف حتى يتعلم القراءة على وجهها ويتعلم مرسوم المصحف فان فعل غير ذلك فقد خالف ما اجمعت عليه الامة وحكمه معلوم في الشرع الشريف ومن علل بشئ فهو مردود عليه لمخالفته للاجماع المتقدم وقد تعدت هذه المفسدة إلى خلق كثير من الناس في هذا الزمان فليحتفظ من ذلك في حق نفسه وحق غيره اه من ايقاظ الاعلام.
وجاء في كتاب نهاية القول المفيد في علم التجويد ما نصه: أجمع
_________
(١) قال في المصباح أشفيت على الشئ بالالف أشرقت (*)',NULL,'109',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (113,'اهل الاداء وأئمة القراء على لزوم تعلم مرسوم المصحف العثماني فيما تدعو إليه الحاجة وقال الامام الخراز في كتابة عمدة البيان في الزجر عن محالفة رسم المصاحف ما نصه: فواجب على ذوى الاذهان * أن يتبعوا المرسوم في القرآن
ويقتدوا بمن رآه نظرا * إذ جعلوه للامام وزرا وكيف لا يصح الاقتداء * بما اتى نصا به الشفاء روى عياض انه من غيرا * حرفا من القرآن عمدا كفرا زيادة أو نقصا أو إن بدلا * شيئا من الرسم الذى تأصلا فعلم مما سبق اجماع الائمة على عدم جواز كتابة القرآن بغير الرسم العثماني، اما ما ذكره الدمياطي في كتابه اتحاف فضلاء البشر في القراءات الاربعة عشر بأن شيخ الاسلام العز بن عبد السلام قال لا يجوز كتابة المصحف الآن على المرسوم الاول باصطلاح الائمة لئلا يوقع في تغيير من الجهال فقد رد عليه بعضهم بقوله وهذا لا ينبغى اجراؤه على اطلاقه لئلا يؤدى إلى درس العلم ولا يترك شئ قد احكمه السلف مراعاة لجهل الجاهلين لاسيما وهو احد الاركان التى عليها مدار القراءات (١) اه
_________
(١) انظر الفصل الاول من الباب الرابع لتقف على ما يترتب عن مخالفة الرسم العثماني (*)',NULL,'110',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (114,'(فان قيل) لم لم يقولوا باتباع رسم الصحف البكرية وهى كتبت قبل مصحف عثمان (فنقول) ان مصحف ابى بكر كان مكتوبا بجميع الاحرف السبعة ولابد أن تكون كتابة كل حرف منها برسم صريح لا يحتمل قراءة حرف آخر، وان أبا بكر لم يحمل الناس على اتباع مصحفه لعدم الضرورة إلى ذلك كما سبق بيانه (١) فان الناس كانوا يقرؤن في زمنه بالاحرف السبعة فكان مصحفه الذى جمعه محفوظا عنده ثم كان عند عمر ثم كان عند حفصة بنت عمر فلما ماتت غسل غسلا فلم يبق له اثر (٢)
اما مصحف عثمان فقد استنسخه من الصحف البكرية على حرف واحد فقط من الاحرف السبعة وهو حرف قريش وترك الاحرف الستة الباقية خشية اختلاف الناس في القراءة وامر بحرق جميع الالواح والمصاحف غير مصحفه الذى جمعه حتى لا تكون فرقة ولا اختلاف وحمل الناس على مصحفه ووافقه الصحابة على هذا العمل المبرور فصار اتباعه واجبا في ترتيبه ورسمه، وان كل مصحف من المصاحف التى ارسلها عثمان إلى المدن والامصار كتب برسم غير رسم الآخر ليحتمل
_________
(١) انظر الفصل الاول من الباب الثاني عند جمع ابى بكر للقرآن (٢) تقدم في الجمع الثالث سبب غسل الصحف البكرية التى كانت عند حفصة رضى الله عنها بعد وفاتها (*)',NULL,'111',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (115,'الرسم وجها من القراءات (١) فلما صار العمل على هذه المصاحف العثمانية قالوا بوجوب اتباع رسم أي مصحف منها ولابد ان يكون رسم مصحف عثمان موافقا لرسم الصحف البكرية في حدود الحرف الواحد الذى جمع مصحفه عليه وهو حرف قريش خصوصا في حذف الالف من نحو الكتب والانسن واسحق واسمعيل وزيادة الواو في نحو اؤلئك وأولو وغير ذلك والله تعالى اعلم بغيبه.
ومما يناسب هذا المقام ذكر اربعة أسئلة من الاسئلة التى كنا بعثناها لمشيخة المقارئ المصرية (٢) مع الاجابة عليها.
(فالسؤال الاول) هل من ضمن القراءات المتواترة (٣) قراءة روعى فيها رسم المصحف العثماني ام لا.
_________
(١) انظر في آخر الفصل الثاني من الباب الثالث لتقف على علة اختلاف
الرسم في المصاحف العثمانية (٢) لما عزمتا على تأليف هذا الكتاب خطرت في بالنا جملة اسئلة عددها تسعة عشر سؤالا مما يتعلق بالقرآن الكريم فاستفتينا فيها مشيخة المقارئ المصرية فاجابتا عليها في اليوم العاشر من شهر شعبان عام ألف وثلاثمائة وثلاث وستين هجرية، ونحن نذكر في هذا الكتاب من تلك الاسئلة والاجابة عليها ما يناسب كل مقام، ولولا خوف التطويل لوضعنا جميع الاسئلة مع اجوبتها في ذيل هذا الكتاب.
(٣) سيأتي بيان القراءات المتواترة في السؤال الثالث قريبا (*)',NULL,'112',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (116,'(فأجابنا عليه) شيخ القراء هناك فضيلة الاستاذ المحقق الشيخ محمد على الضباع (١) بقوله: رسم المصحف ركن من اركان القراءة فكل قراءة مراعى فيها هذا الرسم وقد وردت نصوص أئمة الاداء بأن ائمة القراءة بالكوفة وأبا عمر والمازني ونافعا بن أبى نعيم المدنى اعتنوا بمتابعة خط المصحف في الوقوف الاختبارية (٢) لقصد توقيف القارئ على حقيقة رسمها واستحسن ذلك المحققون لسائر القراء.
اه (والسؤال الثاني) هل يطلق على من كتب مصحفا بقراءة من القراءات المتواترة انه خالف رسم المصحف العثماني وانه ارتكب محظورا أم لا.
(فأجابنا عليه) شيخ القراء المذكور بقوله: كاتب المصحف إذا رسم هجاء كلماته بصورها الرسمية على وجه مما اثر عن اصحاب رسول الله صلى الله عليه وسلم والتزم فيما ورد فيه منها رسمان كل منهما لقراءة رسما يطابق قراءة معينة من القراءات المتواترة ثم ضبطه بأى طريق من طرق الضبط على وجه
_________
(١) الضباع بالضاد المعجمة والباء الموحدة المشددة (٢) الوقف الاختباري بالباء الموحدة هو اختبار القارئ ليعلم كيف يقف على رسم المصحف العثماني من مقطوع وموصول وثابت ومحذوف وتاء تأنيث لم تكتب بهاء (*)',NULL,'113',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (117,'معتبر عند اهل الاداء فلا يقال انه خالف الرسم العثماني ولا انه ارتكب محظورا وان كانت الصورة التى أتى بها لا تحكى صورة بعينها لمصحف من المصاحف الستة (١) لان المعتبر في متابعة الرسم العثماني تصوير الكلمة القرآنية على وجه أثر عن تلك المصاحف أو بعضها وأما الضبط فقد جرى عمل المسلمين على الترخيص به دفعا للالتباس ومنعا للتحريف والخطأ في كلام رب العالمين.
اه (والسؤال الثالث) ما هي القراءات المتواترة وكم عددها وما أسماؤها وما معنى القراءة الشاذة وهل تصح الصلاة بها في احد المذاهب ام لا وما مثالها وهل من يقرأ بها في غير الصلاة للتعبد يثاب عليها ام لا فان لم تصح الصلاة بها ولم يؤجر قارئها فما معنى كونها قراءة شاذة وهل يترتب عليها حكم شرعى ام لا.
(فأجابنا عليه) شيخ القراء المذكور بقوله: القراءات المتواترة هي كل قراءة صح سندها بنقل جماعة لا يمكن تواطؤهم على الكذب عن مثلهم من البداءة إلى المنتهى ووافقت العربية مطلقا ووافقت احد المصاحف العثمانية ولو تقديرا، والذى جمع في زماننا هذه الاركان الثلاثة
_________
(١) هذا على القول بان المصاحف التى ارسلها عثمان بن عفان إلى الامصار ستة وقد تقدم ذكر الاختلاف في عددها فراجعه في صحيفة ٧٤ (*)',NULL,'114',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (118,'هو قراءة الائمة العشرة (١) - نافع، وابن كثير، وابو عمرو، وابن عامر وعاصم، وحمزة، والكسائي، وابو جعفر، ويعقوب، وخلف - أخذها الخلف عن السلف إلى أن وصلت الينا فقراءة احدهم كقراءة باقيهم في كونها مقطوعا بها..الخ ما أجابنا به شيخ القراء حفظه الله تعالى. اه
وقد اكتفينا بهذه النبذة من اجابته على سؤالنا المذكور المتشعب بيانا للقراءات المتواترة ولم نذكر بقية الاجابة خوفا من التطويل مع انها نافعة قيمة كيف لا وهى صادرة من علامة محقق اكثر الله من امثاله ولما كان في الاجابة بعض جمل تحتاج لزيادة الايضاح رأينا أن نعقب عليها بشرح مختصر نقلناه من كتاب عنوان البيان في علوم التبيان وهو منقول عن الامام ابن الجزرى رحمه الله تعالى فنقول: " قوله ووافقت العربية مطلقا " أي ولو بوجه من وجوه النحو سواء كان أفصح ام فصيحا مجمعا عليه أم مختلفا فيه اختلافا لا يضر مثله إذا كانت القراءة مما شاع وذاع وتلقاه الائمة بالاسناد الصحيح إذ هو الاصل الاعظم والركن الاقوم وكم من قراءة انكرها بعض اهل النحو أو كثير منهم ولم يعتبر انكارهم كإسكان بارئكم ويأمركم وخفض الارحام والفصل بين المضافين في مثل " قتل أولادهم شركائهم "
_________
(١) سيأتي الكلام على ذكرهم وتاريخ وفاتهم في الفصل الاول من الباب الرابع (*)',NULL,'115',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (119,'فإذا ثبتت الرواية لم يردها قياس عربية ولا فشو لغة لان القراءة سنة متبعة يلزم قبولها والمصير إليها.
" وقوله ووافقت احد المصاحف العثمانية " يعنى ما كان ثابتا في
بعضها دون بعض كقراءة ابن عامر قالوا اتخذ الله ولدا في البقرة من غير واو وبالزبر وبالكتاب المنير بزيادة الباء في الاسمين فأن ذلك ثابت في المصحف الشامي فان لم يكن في شئ من المصاحف العثمانية فشاذ لمخالفته الرسم المجمع عليه.
" وقوله ولو تقديرا " كملك يوم الدين فأنه كتب في الجميع بلا ألف فقراءته بالالف توافقه تقديرا لحذفها في الخط اختصارا وقد يوافق اختلاف القراءات الرسم تحقيقا نحو تعلمون بالتاء والياء ويغفر لكم بالياء والنون ونحو ذلك مما يدل تجرده عن النقط والشكل في حذفه واثباته على فضل عظم الصحابة رضى الله عنهم في علم الهجاء خاصة وفهم ثاقب في تحقيق كل علم اه من كتاب عنوان البيان للعلامة الشيخ محمد حسنين مخلوف العدوى رحمه الله تعالى.
ولقد طلبنا من الاستاذ الجليل مرجع القراء وعمدتهم عندنا بمكة المشرفة الشيخ احمد بن محمد التيجى حفظه الله وأطال عمره ايضاح ما ذكر من اسكان بارئكم ويأمركم وخفض الارحام والفصل بين المضافين',NULL,'116',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (120,'في مثل قتل أولادهم شركائهم (فأجابنا بما يأتي) : إن أبا عمرو بن العلاء احد أئمة القراء يقرأ كلمة " بارئكم " من قوله تعالى " فتوبوا إلى بارئكم فاقتلوا أنفسكم ذلكم خير لكم عند بارئكم فتاب عليكم " باسكان الهمزة تخفيفا - ويقرأ كلمتي " يأمركم ويأمرهم " من قوله تعالى " ان الله يأمركم أن تؤدوا الامانات إلى أهلها " ومن قوله تعالى " يأمرهم بالمعروف وينهاهم عن المنكر ويحل لهم الطيبات " باسكان الراء في الكلمتين للتخفيف - وإن حمزة أحد
أئمة القراء يقرأ كلمة " والارحام " من قوله تعالى " واتقوا الله الذى تساءلون به والارحام " بكسر الميم عطفا على الضمير المجرور.
وأما قوله تعالى " وكذلك زين لكثير من المشركين قتل أولادهم شركاؤهم " بسوره الانعام فيقرأ ابن عامر أحد أئمة القراء زين بضم الزاى فعل مجهول، وقتل بضم اللام نائب فاعل، وأولادهم بفتح الدال مفعول للمصدر، وشركائهم بكسر الهمزة مضاف إليه وقتل هو المضاف وقد فصل بينهما بأولادهم - والفصل بين المضاف والمضاف إليه لا يجوز عند اكثر النحويين الا في الشعر لان المضاف إليه بمنزلة جزء المضاف كما لا يجوز عندهم اسكان الهمزة من قوله تعالى فتوبوا إلى بارئكم وهو اسم مجرور واسكان الراء من قوله تعالى ان الله',NULL,'117',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (121,'يأمركم وهو فعل مضارع مرفوع.
ولكن ثبوت القراءة بما ذكر عن هؤلاء الائمة مقدم على القواعد النحوية فالقراءة هي الاصل المعتبر اه قول الشيخ التيجى ادام الله النفع به.
(والسؤال الرابع) هل يجوز اتلاف المصاحف المطبوعة على غير رسم المصحف العثماني ام لا وهل لها حرمة ام لا.
(فأجابنا عليه) شيخ القراء المذكور بقوله: إذا كان في المصحف المطبوع كلمات رسمت على خلاف الرسم العثماني المشهور وكانت هذه الكلمات مما يترتب على رسمها كذلك اخلال بحكم من احكام تلاوة القرآن كوصل ما اثر عن الرسم العثماني قطعه وعكسه أو كرسم هاء التأنيث التى يقتضى الرسم العثماني رسمها بالتاء هاء فخشية ان يتسرب التحريف إلى اللفظ الشريف يتعين اتلاف ذلك المصحف إذا تعذر
اصلاحه، اما إذا كانت تلك الكلمات مما لا يترتب على رسمها كذلك اخلال بحكم من احكام اللفظ كاثبات بعض الالفات أو الياءات أو الواوات المحذوفات في الرسم العثماني لقصد الا ختصار فلا بأس ببقائه واحترامه تبعا لما جرى عليه بعض متأخرى المشارقة من الترخيص باثباتها تيسيرا على العامة وتنزيلا لها منزلة الضبط لانها تؤدي ما يؤديه ولم أر في ذلك نصا يعتد به، وهل تعد هذه الاحرف من القرآن',NULL,'118',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (122,'أولا، الظاهر من عمل العادين أن منهم من عدها مراعاة للفظ ومنهم من أسقطها مراعاة للخط العثماني وهذا أولى وأحوط محافظة على المرسوم وخشية ان يزاد في القرآن ما ليس منه.
انتهت الاسئلة الاربعة والاجابة عليها.
فخلاصة ما تقدم: ان الواجب علينا اتباع رسم المصحف العثماني وتقليد أئمة القراءات خصوصا علماء الرسم منهم والرجوع إلى دواوينهم العظام كالمقنع لابي عمر والدانى والعقيلة للشاطبي فان أئمة القراءات المتقدمين قد حصروا مرسوم القرآن الكريم كلمة كلمة على هيئة ما كتبه الصحابة في المصاحف العثمانية ونقولوا ذلك بالسند المتصل عن الثقاة العدول الذين شاهدوا تلك المصاحف.
هذا وقد بحثنا كثيرا في دور الكتب " الكتبخانات " بالحجاز ومصر عن نفس المصاحف العثمانية فلم نقف على خبر موثوق نطمئن إليه بوجودها.
ولقد جاء في خلاصة الوفا بأخبار دار المصطفى للسمهودي انه في الحريق الاول الذى حصل للمسجد النبوى سنة ستمائة واربع وخمسين
للهجرة كان من جملة ما احترق الكتب والمصاحف ولم يسلم من الحريق سوى بعض اشياء منها المصحف الشريف العثماني..الخ - فعلى هذا',NULL,'119',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (123,'كان المصحف العثماني موجود ابالحرم النبوى بالمدينة المنورة إلى التاريخ المذكور ثم لا يعلم احد أين ذهب، ويقول بعض من نعاصرهم انه كان موجود بالمدينة المنوره إلى ان خرج الاتراك من الحجاز عام ألف وثلاثمائة واربع وثلاثين وانه ربما نقل إلى الاستانة.
ولقد رأينا في " مجلة الدنيا وكل شئ " التى تصدر بمصر في كل اسبوع مرة واحدة بتاريخ ٢٨ جمادى الثانية عام ١٣٥٧ هـ الموافق ٢٤ أغسطس عام ١٩٣٨ - ان حكومة المانيا ستعيد في ستة اشهر من تنفيذ المعاهدة الحالية إلى حكومة ملك الحجاز النسخة الاصلية لمصحف الخليفة عثمان بن عفان رضى الله عنه والتى اخذت من المدينة المنورة بواسطة القوات الاتراك وثبت انها سلمت للامبراطور السابق غليوم الثاني هذا ما وقفنا عليه في هذا الشأن.
فوائد اتباع الرسم العثماني اعلم ان في اتباع الرسم العثماني جملة فوائد (منها) وقوف الناس على كيفية كتابة المصاحف في ابتداء الامر (ومنها) النص على بعض اللغات الفصيحة ككتابة هاء التأنيث تاء على لغة طئ وكحذف ياء يوم يأت لا تكلم نفس على لغة هذيل (ومنها) افادة المعاني بالقطع والوصل في بعض الكلمات نحو " أم من يكون عليهم عليهم وكيلا "',NULL,'120',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (124,'فان قطع أم عن من يفيد معنى بل دون وصلها بها (ومنها) أخذ القراءات
المختلفة من اللفظ المرسوم برسم واحد نحو " وما يخدعون الا أنفسهم " فلو كتبت وما يخادعون لفاتت قراءة وما يخدعون ومنها عدم الاهتداء إلى تلاوته على حقه الا بالتلقى شأن كل علم نفيس يتحفظ عليه.
اه من اجابة مشيخة المقارئ المصرية لاسئلتنا الرد على الافرنج (القائلين باستنباط القراءات من الرسم) يقول بعض المستشرقين من الافرنج أمثال جولد زيهر اليهودي ونولدكة الالماني المولود عام ١٨٣٦ م (١) ان رسم المصحف هو الاصل وان القراءات تابعة له نشأت عن عدم وجود الشكل والنقط أي " الحركات والاعجام " في الحروف والكلمات ايام الصحابة فنحن نرد هنا على قولهم هذا بالبرهان قاطع حتى لا يتوهم ذلك أحد من المسلمين
_________
(١) كان بدء اهتمام الافرنج باللغة العربية من القرن العاشر للميلاد ثم زاد اهتمامهم باللغات الشرقية كالعربية والتركية والفارسية وتخصص اناس منهم في دراستها فترجموا كثيرا من العلوم إلى لغاتهم، ومن القرن الثامن عشر للميلاد إلى الآن نبغ كثيرون منهم - وقد ذكر جورجى زيدان في كتابه تاريخ آداب اللغة العربية اسماء طائفة من المستشرقين وأعمالهم فراجعه ان شئت.
(*)',NULL,'121',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (125,'وأنى لهؤلاء الافرنج أن يفهموا كلام رب العالمين وشريعة خاتم النبين محمد صلى الله عليه وسلم وهم قد كفروا به.
ولئن استمعنا إلى فلسفتهم وآرائهم في بعض المواضيع، لا نسمح لهم أن يتناولوا الابحاث الدينيه الاسلاميه ويخوضوا في المسائل الدقيقة المهمة - على اننا لا ننكر للغربين نظرياتهم الصائبة في بعض النواحى التاريخية، واستكشافاتهم
العظيمة للاثار العمرانية، ومخترعاتهم الهائلة في المصالح الحيوية - وانما ننكر عليهم الخوض في الابحاث الدينية الاسلامية لانها غير مبنية على التصورات العقلية والتخيلات الفكرية بل انها مبنية على قول الله تبارك وتعالى وعلى سنة نبينا العربي الكريم محمد صلى الله عليه وسلم وهم لا يؤمنون بكتاب الله، ولا يقرون برسالة نبينا، ولا يعرفون من اللغة العربية ودقائقها ما يعرفه اهلها - فمن الانصاف والعدل أن يرجعوا إلى كبار علمائنا الاعلام فيما يشكل عليهم من الامور إذا ما أرادوا الوصول إلى الحقيقة.
واليك فساد رأيهم في بحث القراءات: اعلم اننا لو اخذنا بقولهم هذا للزم ان الصحابة والتابعين هم الذين استنبطوا هذه القراءات من رسم المصحف العثماني، فعليه يكون قد تطرق التحريف والتبديل في القرآن العظيم وهذا مستحيل بصريح قوله تعالى " انا نحن نزلنا الذكر واناله لحافظون " وقوله جل جلاله " وانه لكتاب عزيز لا يأتيه',NULL,'122',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (126,'الباطل من بين يديه ولا من خلفه تنزيل من حكيم حميد " وحاشا لله ان يتهاون الصحابة أو يعلموا برأيهم في أمر من أمور الدين فضلا عن القرآن الكريم الذي هو اساس الدين الاسلامي الحنيف، وانما هم تلقوه عن رسول الله صلى الله عليه وسلم مشافهة وسماعا كلمة كلمة وآية آية وسورة سورة بالقراءات التى تدخل في معنى حديث " ان هذا القرآن انزل على سبعة احرف فاقرؤا ما تيسر منه ".
ولقد وصل الينا القرآن المجيد من رسول الله صلى الله عليه وسلم بالتواتر القطعي والاسناد الصحيح عن الثقاة العدول والعلماء الفحول طبقة بعد طبقة فالقراءات مأخوذة من النبي صلى الله عليه وسلم مشافهة وسماعا وليست مستخرجة
من رسم المصحف بل الرسم تابع لها مبنى عليها واى دليل اعظم على هذا مما وقع لعمر بن الخطاب مع هشام ابن حكيم حينما سمعه يقرأ سورة الفرقان على حروف كثيرة لا يعرفها عمر، ومما وقع لابي بن كعب في المسجد مع الرجليين اللذين قرأ كل منهما سورة النحل في الصلاة بقراءة تخالف قراءة أبى.
ومما وقع لعبد الله بن مسعود مع رجل سمعه بقرأ قراءة تخالف قراءته ومما وقع كذلك مع غير هؤلاء، فيحتكمون إلى رسول الله صلى الله عليه وسلم فيقر كلا منهم على قراءته ويقول " إن هذا القرآن انزل على سبعة احرف',NULL,'123',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (127,'فاقرؤا ما تيسر منه " وتفصيل ما وقع لهؤلاء الصحابة الاجلاء مذكور في الفصل الخامس في نزول القرآن على سبعة احرف فراجعه.
ولقد أنعمنا النظر فوجدنا انه لا يمكن اخذ القراءات من رسم المصحف العثماني إذ الرسم لم يوضع للدلالة على شئ منها، وما جاء من قراءة بعض الكلمات بالغيبة والخطاب أو بالرفع والنصب انما هو بالتلقى والاخذ من رسول الله صلى الله عليه وسلم لا لاحتمال ذلك من صورة الرسم الخالية من النقط والتشكيل في ذلك الزمن واليك بيان ذلك ليتضح لك ما ذكرناه فمثلا قول الله تعالى " أم تقولون ان ابراهيم واسماعيل واسحاق ويعقوب والاسباط.." (١) قرئ ام يقولون بالغيبة وأم تقولون بالخطاب، وقوله تعالى " وما الله بغافل عما يعملون ولئن أتيت.." (٢) قرئ يعملون بالغيبة وبالخطاب وقوله تعالى " والله بصير بما يعملون قل من كان.." (٣) قرئ بالغيبة وبالخطاب، وقوله " ومن تطوع خيرا.." (٤) قرئ بالغيبة وبالخطاب.
كل ذلك كان بالتلقى من النبي صلى الله عليه وسلم لا من رسم المصحف الذى يحتمل القراءة بالياء والتاء لعدم وجود النقط فيه فلو كان كذلك لقرئ
_________
(١ و ٢ و ٣ و ٤) بسورة البقرة (*)',NULL,'124',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (128,'قوله تعالى " ولا تنفعها شفاعة ولاهم.." (١) بالياء والتاء مع انه ما قرئ الا بتاء التأنيث فقط، بخلاف قوله تعالى " ولا يقبل منها شفاعة.." (٢) فقد قرئ بالياء والتاء.
وقول الله تعالى " فبشرناها باسحاق ومن وراء اسحاق يعقوب " (٣) قرئ يعقوب بالنصب والرفع، وقوله تعالى " ولا تسأل عن اصحاب الجحيم ولن ترضى عنك.." (٤) قرئ ولا تسأل بالرفع والجزم، وقوله تعالى " واتخذوا من مقام ابراهيم مصلى " (٥) قرئ بكسر الخاء وفتحها، وقوله تعالى " فامتعه قليلا.." (٦) قرئ بالتشديد والتخفيف، وقوله تعالى " ولكن البر من آمن بالله.." (٧) قرئ بتشديد ولكن ونصب البر وقرئ بتخفيف ولكن ورفع البر.
كل ذلك كان بالتلقى من النبي صلى الله عليه وسلم لامن رسم المصحف الذى يحتمل القراءة بالرفع والنصب أو بالكسر والجزم لعدم وجود الحركات في المصحف في ذلك الزمن، فلو كان كذلك لقرئ قوله تعالى " إذا قضى
_________
(١) هذه الآية قبل وإذا ابتلى ابراهيم ربه.
بسورة البقرة (٢) هذه الآية بعد أتأمرون الناس بالبر بأربع آيات.
بسورة البقرة (٣) بسورة هود (٤ و ٥ و ٦ و ٧) بسورة البقرة (*)',NULL,'125',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (129,'أمرا فانما يقول له كن فيكون " (١) بنصب فيكون مع انه ما قرئ الا بالرفع فقط، بخلاف قوله تعالى " انما امره إذا اراد شيئا ان يقول له كن فيكون " (٢) فقد قرئ فيكون بالرفع وبالنصب.
ثم انه ما كل كلمة رسمت في المصحف العثماني لتدل على القراءات لكن احيانا توافق القراءات الرسم نحو: تعلمون بالتاء والياء ويغفر لكم بالياء والنون، وفاكهين وفكهين، وأسرى وأسارى، وتفادوهم وتفدوهم.
وأحيانا تقرأ الكلمة بجملة وجوه بينما الرسم لا يدل على كل ذلك نحو كلمة " جبريل " فقد قرئت بكسر الجيم وفتحها، وقرئت جبرءيل بفتح الجيم والراء وبعدها همزة مكسورة ممدودة، وقرئت جبرءل بفتح الجيم ولراء وبعدها همزة مكسورة غير ممدوده، وكلمة " ميكال " قرئت بلا همز وقرئت ميكاءيل بهمزة مكسورة ممدودة وقرئت ميكاءل بهمزة مكسورة غير ممدودة.
واحيانا لا يرمز الرسم إلى شئ من القراءات وان خالف قواعد الاملاء نحو: لااذبحنه، ولا تقولن لشائ وجائ يومئذ بجهنم
_________
(١) بآل عمران (٢) بيس (*)',NULL,'126',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (130,'بزيادة ألف في الكلمات الثلاث - ونحو: والسماء بنيناها بأييد، وبأييكم المفتون، بزيادة ياء فيهما - ونحو: سبحن الله، وسليمن واسحق، وجاءو، وفاءو بحذف الالف المد منها.
فهذه الكلمات ونحوها ليس فيها غير قراءة واحدة وهى التى نقرؤها اليوم وان جاء رسمها
على خلاف القاعدة (١) فعلم مما ذكرناه ان القراءات هي الاصل وان الرسم تبع لها لا كما يقول المستشرقون من الافرنج انها ناشئة من الرسم وتابعة له ولا نعتقد انه يوجد مسلم على وجه الارض يأخذ بآرائهم المبنية على التخيلات ويترك اقوال ائمة المسلمين وعلمائهم المستندة إلى الكتاب والسنة.

<span data-type="title" id=toc-19>الفصل الخامس (في معرفة الصحابة لقواعد الاملاء والكتابة)</span>
يعتقد كثير من الناس ان الصحابة رضوان الله عليهم ما كانوا يعرفون قواعد الاملاء واصول الكتابة ويستدلون على هذا برسم
_________
(١) اخذنا ما دكرناه من اوجه القراءات من الاستاذ الجليل الشيخ احمد التيجى المتقدم ذكره في الفصل الخامس من الباب الثاني اطال الله حياته وادام النفع به آمين.
(*)',NULL,'127',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (131,'المصحف العثماني حتى ابن خلدون يقول بهذا في مقدمته، على انهم لو قالوا ان الكتابة لم تكن منتشرة فيهم لكان أولى من نسبتهم إلى جهل اصولها وقواعدها مع انها ما وصلت الينا الا منهم.
ونحن نعتقد اعتقادا جازما بأن الصحابة كانوا يعرفون قواعد الاملاء والكتابة حق المعرفة (١) ونستدل على قولنا هذا استدلالا فنيا بثلاثة امور (الامر الاول) قال الالوسى في تفسيره روح المعاني ما نصه: والظاهر ان الصحابة كانوا متقنين رسم الخط عارفين ما يقتصى ان يكتب وما يقتضى ان لا يكتب وما يقتضى ان يوصل وما يقتضى ان لا يوصل إلى غير ذلك لكن خالفوا القواعد في بعض المواضع لحكمة اه
قوله في بعض المواضع أي من القرآن الكريم ورسم كلماته فالآلوسي وهو العالم المتبحر وصاحب التفسير الكبير لا يقول هذا الا بعد النظر والتحقيق وان لم يذكر الشواهد التى تؤيد قوله.
(الامر الثاني) مما لا يخفى على أحد ان الصحابة كانوا يراسلون الملوك والامراء في مهمات الامور وكانوا يكتبون فيما بينهم العقود
_________
(١) لا ننكر ان الامية كانت متغلبة عليهم والتعليم لم يكن منتشرا بينهم لكن نقول ان المتعلمين منهم كانوا متقنين القراءة والكتابة على الوجه الصحيح والقواعد المرعية كما سيظهر لك في هذا الفصل (*)',NULL,'128',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (132,'والمستندات من بيع وشراء وضمان وعطاء، فلو كتبوا هذه الامور على غير قواعد الاملاء والكتابة لادى ذلك إلى الالتباس والخطأ في فهم مرادهم مع ان الحروف والكلمات ما وضعت الا لتدل على الكلام الملفوظ (١) فان اختفلت كتابته اختلف اللفظ فاختلف المعنى فاختلط الامر عليهم.
واى دليل اعظم على نباهة العرب قبل اختراع الحركات " التشكيل " من تفرقتهم في الكتابة بين عمر وبين عمرو بزيادة الواو في الاسم الا خير لئلا يحصل لبس واشتباه، فلو تأملت لم اختاروا الواو علامة للتفرقة بين الاسمين دون غيرها من الاحرف الهجائية لظهر لك ذكاؤهم المفرط وقوة تفكيرهم في ذلك.
على أن بعض كتاباتهم وخطوطهم لا زالت محفوظة لدينا ففى دار الكتب العربية بمصر يوجد كثير من كتابة القرن الاول والقرون التى تليه على الاحجار والجلود والاوراق البردية (٢) ، وقد شاهدناها
_________
(١) ولذلك عرفوا الخط بأنه تصوير اللفظ بحروف هجائية
(٢) كان الورق البردى يصنع قديما من لب السيقان الطويلة للنبات المعروف باسم (سيبرس بايبرس) بعد جعله شرائح رقيقة تصف بجانب بعض ليتكون منها طبقة ثم تصقل بعد ذلك فتصير صحيفة رقيقة وقد بينا ذلك في كتابنا تاريخ الخط العربي وآدابه المطبوع بمصر (*)',NULL,'129',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (133,'بأنفسنا حين اقامتنا بها (١) وقرأناها فلم نجد فيها خطأ املائيا ولا غلطة كتابية وكنا نرغب أن نضع هنا صورة صحيفة من القرآن الكريم المكتوب في عهد الصحابة ورسم شئ من خطوطهم غير ان ظروف الحالة لم تساعدنا على ذلك لكن وضعناها في كتابنا تاريخ الخط العربي وآدابه فراجعه ان شئت ولا نذهب بك بعيدا فهذه جبال الحجاز كم توجد في صخورها واحجارها من كتابات الصحابة وخطوطهم خصوصا في المدينة المنورة ومكة المشرفة والطائف المأنوس، ولقد وقفنا عليها في هذه الاماكن فعجبنا من حسن خطها وصحة كتابتها وتحقيق حروفها قد كتبت بأنواع متعددة من الخط الكوفى نرجو الله ان يحفظها من التلف فان كثيرا من الكتابات على الصخور لم يبق لها أثر لان الناس يكسرونها إلى قطع لبناء البيوت (٢) كما شاهدنا في صخور بعض الشعاب والجبال من الكتابات التى يرجع عهدها إلى ما قبل الاسلام وغالبا هي
_________
(١) ذهبنا إلى مصر لطلب العلم مرتين الاولى في عام ألف وثلاثمائة واربعين للهجرة ومكثنا بها سبع سنين، والثانية في عام ألف وثلاثمائة وثلاث وخمسين ومكثنا بها سنتين وسنذهب إليها للمرة الثالثة ان شاء الله تعالى في شبان من عامنا هذا وهو عام ألف وثلاثمائة وخمس وستين
(٢) حبذا لو أمرت الحكومة بمنع العمال من اتلاف الصخور والاحجار لمكتوبة فان في حفظها فوائد جمة كما افادتنا رؤيتنا لها في هذا الموضوع المهم (*)',NULL,'130',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (134,'مكتوبة بالحروف الحميرية أو المسند فاننا لم نتحقق من ذلك لانه يحتاج إلى التخصص والفراغ التام وقد استنتجنا من رؤيتنا لها ان هذه الاماكن التى هي بين الجبال كانت في يوم من الايام مساكن لاقوام نزلوا بها ولا يبعد أن يعثر الباحث بين هذه الجبال على كهوف وغيران تحتفظ في زواياها على آثارهم وكنوزهم كما رأى بعضهم ذلك (١)
_________
(١) روى ابن كثير في الجزء الثاني من تاريخه عند ذكر أخبار عبد الله بن جدعان وبعد أن ساق نسبه قال.
وهو ابن عم والد ابى بكر الصديق رضى الله عنه وكان من الكرماء الاجواد في الجاهلية المطعمين للمسنتين وكان في بدء امره فقيرا مملقا وكان شريرا يكثر من الجنايات حتى أبغضه قومه وعشيرته واهله وقبيلته وابغضوه حتى ابوه فخرج ذات يوم في شعاب مكة حائرا بائرا فرآى شقا في جبل فظن ان يكون به شيئا يؤذى فقصدة لعله يموت فيستريح مما هو فيه فلما اقترب منه إذا ثعبان يخرج إليه ويثب عليه فجعل يحيد عنه ويثب فلا يغنى شيئا فلما دنا منه إذا هو من ذهب وله عينان هما ياقوتتان فكسره واخذه ودخل الغار فإذا فيه قبور لرجال من ملوك جرهم ومنهم الحارث بن مضاض الذى طالت غيبته فلا يدرى اين ذهب (ومضاض هو ابن عمرو الجرهمى وهو اول من صار إليه أمر البيت بعد نابت بن اسماعيل عليه السلام وقد تزوج اسماعيل بنت مضاض فجاءته باثني عشر بنينا) ووجد عند رؤسهم لوحا من ذهب فيه تاريخ وفاتهم ومدد ولايتهم وإذا عندهم من الجواهر واللآلى والذهب والفضة شئ
كثير فاخذ منه حاجته ثم خرج وعلم باب الغار ثم انصرف إلى قومه فاعطاهم (*)',NULL,'131',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (135,'(الامر الثالث) ان الخط الكوفى وصل إلى الحجاز من اهل الحيرة والانبار (وهما من مدن العراق) ووصل اليهما من طارئ طرأ عليهم من اليمن، فالصحابة رضى الله عنهم كانوا يكتبون بالخط الكوفى الذى هو فرع من الخط الحميرى العربي القديم الذى كان منتشرا باليمن وليس من المعقول ان الخط الحميرى الذى هو اساس الخط العربي لا يكون له اصول وقواعد معروفة، بل ان للخطوط التى هي اقدم من الخط الحميرى بآلاف السنين قواعد تامة لا تخفى على من تخصص بفك طلاسمها وترجمتها في وقتنا الحاضر وذلك كالخط الهير وغليفى بانواعه الثلاثة والفينيقى والآشوري والسرياني.
ولقد اجمع المؤرخون على ان اول من ادخل الكتابة إلى مكة المشرفة حرب بن أمية (١) بن عبد شمس بن مناف القرشى وهو تعلمها
_________
حتى احبوه وسادهم وجعل يطعم الناس وكلما قل ما في يده ذهب إلى ذلك الغار فأخذ حاجته ثم رجع وكانت له جفنة ياكل منها الراكب على بعيره ووقع فيها صغير فغرق..الخ اه من تاريخ ابن كثير (١) وترجمته هي حرب بن امية بن عبد شمس جد معاوية بن ابى سفيان تعلم الخط من بشر بن عبد الملك حيث كان له صحبة بحرب بن امية لتجارته عندهم في بلاد العراق وقد سافر بشر معه إلى مكة فتزوج الصهباء بنت حرب المذكور اخت ابى سفيان وقد تعلم الخط من حرب المذكور جماعة منهم عمر بن الخطاب (*)',NULL,'132',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (136,'في أسفاره من عدة اشخاص منهم بشر بن عبد الملك (١) ثم تعلم منهما
جماعة من قريش بمكة.
_________
وعثمان بن عفان وعلى بن ابى طالب وطلحة بن عبيد الله وغيرهم رضى الله عنهم وحرب هذا كان قائد قريش كلها يوم الفجار وهو الذى تحمل الديات من ماله حينما دعا الناس إلى الصلح في ذلك اليوم ورهن لسدادها ولده ابا سفيان وكان حرب يسمر مع عبد المطلب بن هاشم وقد دامت الالفة بينها طويلا.
اه من الجزء الثاني من محاضرات الخضرى بزيادة وتصرف، وفي كتاب الاعلام تزعم العرب ان الجن قتلته بثأر حية وفيه قال الشاعر وقبر حرب بمكان قفر * وليس قرب قبر حرب قبر وقد فاتنا ان نذكر ترجمته هذه عند ذكر اسمه في كتابنا تاريخ الخط العربي وآدابه.
(١) وترجمته هي بشر بن عبد الملك أخوا كيدر بن عبد الملك بن عبد الجن الكندى ثم السكوني صاحب دومة الجندل كان بشر المذكور يأتي الحيرة فيقيم بها الحين وكان نصرانيا فتعلم الخط العربي من اهل الحيرة ثم أتى مكة في بعض شأنه فرآه سفيان بن أمية بن عبد شمس وابو قيس بن عبد مناف بن زهرة بن كلاب يكتب فسألاه أن يعلمهما الهجاء ثم اراهما الخط فكتبا ثم ان بشرا وسفيان وأبا قيس أتوا الطائف في تجارة فصحبهم غيلان بن سلمة الثقفى فتعلم الخط منهم وفارقهم بشر ومضى إلى ديار مضر فتعلم الخط منه عمرو بن زرارة بن عدس فسمى عمرو الكاتب ثم اتى بشر الشام فتعلم الخط منه ناس هناك اه بتصرف من كتاب فتوح البلدان للبلاذرى.
وفاتنا أن نذكر ترجمته هذه عند ذكر اسمه في كتابنا تاريخ الخط العربي وآدابه (*)',NULL,'133',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (137,'اما المدينة فقد ذكروا ان رسول الله صلى الله عليه وسلم دخلها وكان فيها يهودى
من يهود ما سكة يعلم الصبيان الكتابة وكان فيها بضعة عشر رجلا يعرفونها منهم زيد بن ثابت وكان يكتب العربية والسريانية ثم انتشرت الكتابة بالمدينة اكثر من انتشارها بمكة بتحريض النبي صلى الله عليه وسلم فقد روى انه امر عبد الله بن سعيد بن العاص ان يعلم الناس الكتابة، وجاء عن عبادة بن الصامت قال علمت ناسا من اهل الصفة الكتابة والقرآن ولقد جعل المسلمون فدية الكاتب من اسارى غزوة بدر الكبرى تعليم عشرة من صبيان المدينة وبذلك كثر التعلمون حتى بلغ عدد كتابه صلى الله عليه وسلم نحو اربعين رجلا.
ومن بدء الهجره إلى أمر عثمان رضى الله عنه بجمع القرآن يكون قد مر ربع قرن أفلا يكون التعليم منتشرا في هذه المده - فهل بعد هذا نقول ان الصحابة رضوان الله تعالى عليهم ما كانوا يعرفون قواعد الكتابة والاملاء، ومن اراد زياده الايضاح عن دخول الخط في الحجاز فعليه بمراجعة كتابنا تاريخ الخط العربي وآدابه وهو مطبوع بمصر.
(فان قيل) حيث ثبت انهم كانوا يعرفون قواعد الكتابة فلم اضطربوا في كتابة بعض الكلمات في المصحف العثماني (نقول) ان هذا الامر هو اللغز الذى جعل الافكار حائرة، لم تهد إلى حله فحول',NULL,'134',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (138,'العلماء وكبار العقلاء ومن هنا نسبوا إلى الصحابة الجهل بقواعد الكتابة فلو نظروا إلى كتاباتهم العامة المتداولة بينهم لما نسبوا ذلك إليهم (وان قيل) ان قواعد الاملاء والنحو والصرف وضعها علماء الكوفة وعلماء البصرة (١) (نقول) نحن لا ننكر ذلك ولكن ليس المعنى انهم اخترعوا تلك القواعد من عند انفسهم كلا، وانما وضعوا نصب اعينهم
لغة العرب وكتاباتهم فبنوا عليها قواعدهم واستنتجوها منها حتى يكون النطق مطابقا لنطقهم والكتابة موافقة لكتاباتهم، فالقواعد دائرة على لغة العرب وكتابتهم لا العكس.
والحقيقة ان قواعد كتاباتنا وشكل خطوطنا مأخودة عن العرب الاقدمين، ومهما تعددت انواعها وتطورت صورها فالاصل واحد لم يتغير، ولو أردنا بسط هذا الكلام بحسب فن الخطوط لخرجنا عن الموضوع الذي نحن بصدده، فتأمل ما ذكرناه لك جيدا فانه مبحث
_________
(١) امام البصريبن هو سيبويه عمرو بن عثمان بن قنبر واختلف في تاريخ وفاته فقيل سنة ١٨٨ وقيل غير ذلك، وامام الكوفيين هو الكسائي على بن حمزة احد القراء العشرة واختلف في تاريخ وفاته ايضا فقيل سنة ١٩٢ وقيل غير ذلك - ولقد جرى بينهما جدال طويل في بعض قضايا النحو فتشيع لسيبويه اهل البصرة وللكسائي اهل الكوفة وبسبب ذلك نشأ الخلاف بين النحويين وايجاد المذهبين مذهب البصريين ومذهب الكوفيين ثم جاء بعدهما أئمة زادوا في تلك القواعد.
(*)',NULL,'135',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (139,'نفيس لا تجده في غير كتابنا هذا والله الموفق للصواب.
فهل بعد هذه الادلة ننسب إلى الصحابة الجهل بقواعد الكتابة والاملاء حاشاهم من ذلك وهم أنجم الهدى وأئمة الدين واللغة والكتابة (ومن اللطائف) المناسبة لهذا المقام: ما يروى عن عمر بن الخطاب رضى الله عنه أنه لقى اعرابيا فسأله هل تحسن القراءة قال نعم فقال اقرأ بأم القرآن فقال الاعرابي والله ما احسن البنات فكيف الام فضربه عمر بالدرة (بكسر الدال وتشديد الراء هي السوط) وأسلمه إلى الكتاب ليتعلم فمكث فيه حينا ثم هرب فلما رجع لاهله أنشدهم
أتيت مهاجرين فعلموني * ثلاثة اسطر متتابعات كتاب الله في رق صحيح * وآيات القران مفصلات وخطوا لى أبا جاد وقالوا * تعلم سعفصا وقريشات وما أنا والكتابة والتهجى * وما خط البنين مع البنات وفي عنوان البيان: ان أول من جمع الاولاد في المكتب عمر بن الخطاب رضى الله عنه وأمر عامر بن عبد الله الخزاعى ان يلازمهم للتعليم وجعل رزقه من بيت المال وأمره ان يكتب للبليد في اللوح ويلقن الفهيم من غير كتب وسألوه تخفيف التعليم فأمر المعلم بالجلوس بعد صلاة الصبح إلى الضحى العالي ومن صلاة الظهر إلى صلاة العصر ويستريحون',NULL,'136',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (140,'بقية النهار، ولما خرج رضى الله عنه إلى الشام عام فتحها ومكث شهرا ثم رجع إلى المدينة وقد استوحش الناس منه فخرجوا للقائه تلقاه الصغار على مسيرة يوم وكان ذلك يوم الخميس فباتوا معه ورجع بهم يوم الجمعة فتعبوا في خروجهم ورجوعهم فشرع لهم الاستراحة في اليومين المذكورين فصار ذلك سنة متبعة ودعا بالخير لمن احيا هذه السنة - انظر الفواكه الدوانى على رسالة ابى زيد القيرواني - اه من عنوان البيان

<span data-type="title" id=toc-20>الباب الرابع</span>
(وفيه فصلان)
<span data-type="title" id=toc-21>(الفصل الاول * فيما لو كتبنا القرآن الكريم بقواعد كتاباتنا)</span>
يقول بعض المتعلمين لو كان نسخ القرآن وطبعه بقواعد كتابتنا لكان أولى واحسن من الرسم العثماني - وكما سبق انه لا يجوز ذلك باجماع الائمة والعلماء فاننا نبين في هذا الفصل ما يترتب على قولهم هذا فنقول
اننا لو كتبنا القرآن على طريقتنا المألوفة لادى ذلك إلى ذهاب شئ من وجوه القراءات، إذ من القواعد المقررة عند الائمة أن الوقف الاختباري على كلمات القرآن يتبع الرسم العثماني، والوقف الاختباري بالباء الموحدة كما تقدم: هو اختبار القارئ ليعلم كيف يقف على رسم المصحف العثماني من مقطوع وموصول وثابت ومحذوف وتاء تأنيث لم تكتب بهاء.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (141,'فمثلا ان كلمة " الضعفاء " مرسومة في المصحف العثماني بصورتين (الصورة الاولى) كما تراها بألف بعد الفاء ثم همزة على السطر، فهذه وما رسم مثلها لا اختلاف بين أئمة القراءات (١) في الوقف عليها بالهمز تبعا للرسم ما عدا حمزة وهشام فيقفان عليها بالالف ولا ينظران إلى الهمز (٢) (الصورة الثانية) هكذا " الضعفؤا " بحذف ألف المد من الفاء ووضع الهمزة على واو وألف بعدها فهذه وما رسم مثلها بالواو أو ما رسم بالياء نحو " أو من وراءى حجاب " يقفون عليها بالهمز
_________
(١) ائمة القراءات عشرة وهم: أبو عمرو بن العلاء المتوفى سنة ١٥٤ هجرية، وعبد الله بن كثير المتوفى سنة ١٢٠ هـ، ونافع بن نعيم المتوفى سنة ١٦٩ هـ وعبد الله بن عامر المتوفى سنة ١١٨ هـ وعاصم بن بهدلة الاسدي المتوفى سنة ١٢٨ هـ، وحمزة بن حبيب الزيات العجلى المتوفى سنة ١٥٦ هـ وعلى بن حمزة الكسائي امام النحاة المتوفي سنة ١٨٩ هـ وابو جعفر بن يزيد القعقاع المدنى المتوفي سنة ١٣٢ هـ، ويعقوب بن اسحاق الحضرمي المتوفى سنة ١٨٥ هـ، وقيل سنة ٢٠٥ هـ وخلف ابن هشام بن طالب (ولم نقف على تاريخ وفاته) (٢) ان قيل لم لم يقفا على الهمزة اتباعا للرسم - قالوا وقفا عليها بحسب ما
تلقياه عن مشايخهما وهم عن مشايخهم إلى رسول الله صلى الله عليه، وحمزة هو ابن حبيب الزيات احد ائمة القراءات، وهشام هو ابن عمار وهو روى عن ابن عامر احد الائمة (*)',NULL,'138',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (142,'ولا ينظرون إلى زيادة الواو أو الياء في الرسم ما عدا حمزة وهشام فانهما يقفان على ما رسم بالواو باثنى عشر وجها خمسة منها على القياس وسبعة على الرسم ويقفان على ما رسم بالياء بابدال الهمز ألفا بخمسة أوجه على الوجه القياسي وبأربعة أوجه على الرسمي ويعلم كل ذلك من علماء القراءات.
وان كلمة " الملا " مرسومة بصورتين (الصورة الاولى) كما تراها بوضع الهمزة على الالف فهذه وما رسم مثلها لا اختلاف بين القراء في الوقف عليها بالهمز تبعا للرسم ما عدا حمزة وهشام فيقفان بابدال الهمز ألفا ولهما روم حركة الهمزة فقط (والصورة الثانية) هكذا " الملؤا " بهمزة على واو وألف بعدها فيقفون عليها بالهمز ولا ينظرون إلى زيادة الواو بخلاف حمزة وهشام فانهما يقفان عليها بالوجهين المتقدمين ولهما الوقف بالواو فيقولون " الملوا " ولهما الروم والاشمام (١) وان كلمة " رحمت " المرسومة احيانا بالتاء واحيانا بالهاء فبعضهم يقف على التاء بحسب الرسم وبعضهم على الهاء بحسب الاصل ومثلها كل كلمة تشابهها نحو " نعمت، وسنت، وامرأت "
_________
(١) الروم بفتح الراء هو الاتيان بثلث الحركة، والاشمام هو ضم الشفتين بعد اسكان الحرف (*)',NULL,'139',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (143,'وان كلمة " ابراهيم " التى رسمت في البقرة هكذا " ابرهم " فقد قرأها ابن عامر ابراهام وقد ورد في غير البقرة قراءة ابراهيم ابراهام أيضا وذلك في بعض المواضع التى بينها الامام الشاطبي رحمه الله تعالى.
وان كلمة " ننج " في آية " كذلك حقا علينا ننج المؤمنين " بيونس مرسومة بحذف الياء من الجيم بالاتفاق ولذا وقف كل القراء عليها بحذف الياء تبعا للرسم ما عدا يعقوب فانه يقف باثباتها للدلالة على الاصل.
وان كلمة " لكنا " في آية " لكنا هو الله ربى " بالكهف مرسومة بالالف وهى تحذف وصلا عند جميع القراء بخلاف ابن عامر فانه يمدها، أما في الوقف فانهم يقفون عليها بالالف بالاتفاق حسب الرسم وان كلمة " يخدعون " من آية " يخدعون الله والذين آمنوا " لو رسمت بالالف هكذا " يخادعون " لفاتت قراءة يخدعون.
وان لفظ " كلمة " المرسوم بالتاء من آية " وتمت كلمة ربك صدقا وعدلا " لو رسمت بالالف على قراءة الجمع هكذا " كلمات " لفاتت قراءة الافراد ولذلك رسموها بالتاء بدل الهاء.
أما الكلمات التى ترسم أحيانا متصلة وأحيانا منفصلة نحو: أن لا وأن لو، وبئسما، وعما، وكيلا - فالوقف على الحرف الاخير بالاتفاق',NULL,'140',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (144,'إن كانت متصلة، وعلى الحرف الاول أو الثاني إن كانت منفصلة أي يكون القارئ مخيرا في ذلك اه ذكرنا هذه الاوجه من القراءات نقلا عن الشيخ احمد التيجى بمدرسة الفلاح بمكه حفظه الله والامثلة المذكورة تكفى اللبيب وتغنى عن التطويل والحافظ
للقرآن الكريم بجميع القراءات يعرف مالا يعرفه غيره في هذا الموضوع، فإذا فهمت ما شرحناه هنا ظهر لك خطورة نسخ القرآن العظيم بقواعد كتابتنا المألوفة نعم إذا كتبنا نحو هذه الكلمات المرسومة في المصحف العثماني هكذا: " رب العلمين، الرءيا، اسمعيل، اليل مال هذا الرسول، هذا غلم، لاذبحنه، ولا تقولن لشائ أرءيت الذى، وألو استقموا " بحسب قواعد كتاباتنا هكذا " رب العالمين، الرؤيا، اسماعيل الليل، ما لهذا الرسول، هذا غلام لاذبحنه، ولا تقولن لشئ، أرأيت الذى، وأن لو استقاموا " لا بأس به إذ لا يضر هذا التعديل البسيط ولا يحصل به اخلال بحكم من احكام التلاوة - لكن لم يجوز أحد من الائمة والعلماء مخالفة الرسم العثماني في نسخ المصحف وطبعه مطلقا ضر أو لم يضر ولم يستثنوا من هذا الحكم شيئا من الكلمات.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (145,'<span data-type="title" id=toc-22>الفصل الثاني (فيما لو اتبعنا رسم المصحف العثماني في كتاباتنا)</span>
لا يمكن لنا أن نتبع رسم المصحف العثماني في كتاباتنا العامة لان مرسومه لا قاعدة له، فالصحابة رضى الله عنهم ما مشوا في كتابته على وتيرة واحدة، فأحيانا يكتبون الكلمة في موضع بشكل واحيانا يكتبونها في موضع بشكل آخر، ولهذا قالوا " خط المصحف لا يقاس عليه ".
نعم رسموا بعض كلمات في جميع القرآن على صورة واحدة وذلك نحو " الملئكة، الانسن، الشيطن، سلطن، الصرط، العلمين أسطير، هذا ذلك، هؤلاء أولوا أولئك " فانهم حذفوا ألف المد من هذه الكلمات وزادوا واوا في أولوا وأولئك.
ونحن نتبعهم في كتابة الكلمات الخمسة الاخيرة على ما رسموا غير أننا نحذف الالف من آخر كلمة اولو، واما في المصحف فبالالف هكذا أولوا.
ولنذكر لك طائفة من الكلمات المتماثلة لفظا المتباينة رسما في هذا الجدول الثاني ليتضح لك ما ذكرناه من ان مرسومه لا قاعدة له.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (146,'الجدول الثاني وفيه بعض الكلمات المرسومة في المصحف العثماني في موضع بشكل وفي موضع بشكل آخر الم ذلك الكتب واذ قال ربك للملئكة وكذبوا بآياتنا كذابا إنا لما طغا (١) الماء حملناكم الا من بعد ما جاءتهم البينة (٣) إن الصفا والمروة من شعائر (٥) الله القرون الاولى بصائر (٧) للناس لكل اجل كتاب قل رب احكم بالحق لا يسمعون فيها لغوا ولا كذابا فأما من طغى (٢) وءاثر الحيوة
فهم على بينت (٤) منه والبدن جعلناها لكم من شعئر (٦) الله هذا بصئر (٨) للناس
_________
(١) بالالف بالحاقة * (٢) بالياء بالنازعات ومثلها ليطغى بالعلق ومثلها انه طغي بطه ومثلها أو أن يطغى بطه ايضا * (٣) بالهاء في البينة (٤) بالتاء في فاطر * (٥) بالالف بالبقرة * (٦) بدون ألف بالحج (٧) بالالف بالقصص * (٨) بدون ألف بالجاثية (*)',NULL,'143',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (147,'وإن تعدوا نعمة (١) الله لا تحصوها سنة (٣) من قد أرسلنا قبلك وألفيا سيدها لدا (٥) الباب وامتازوا اليوم أيها (٧) المجرمون قال ابن ام (٩) إن القوم ونقر في الارحام ما نشاء (١١) انظر كيف ضربوا لك الامثال (١٣) تبارك (١٥) الذى إن شاء جعل قال له صاحبه (١٧) وهو يحاوره وأصحب الايكة (١٩) وقوم تبع كى (٢١) لا يكون دولة بين الاغنياء وإن تعدوا نعمت (٢) الله لا تحصوها فلن تجد لسنت (٤) الله تبديلا إذ القلوب لدى (٦) الحناجر سنفرغ لكم أيه (٨) الثقلان
قال يبنؤم (١٠) لا تأخذ بلحيتي أو أن نفعل في أموالنا ما نشؤا (١٢) انظر كيف ضربوا لك الامثل (١٤) تبرك (١٦) الذي بيده الملك إذ يقول لصحبه (١٨) لا تحزن كذب أصحب لئيكة (٢٠) المرسلين لكيلا (٢٢) تأسوا على ما فاتكم
_________
(١) بالهاء بالنحل * (٢) بالتاء بابراهيم * (٣) بالهاء بالاسراء (٤) بالتاء بفاطر * (٥) بالالف بيوسف * (٦) بالياء بغافر (٧) بالالف بيسن * (٨) بدون ألف بالرحمن * (٩) بالالف وبفصل ابن عن أم بالاعراف * (١٠) بدون ألف مع اتصال ابن بأم بطه (١١) الهمزة على السطر بالحج * (١٢) الهمزة على واو بهود * (١٣) بالالف بالاسراء * (١٤) بدون ألف بالفرقان * (١٥) بالالف بالفرقان * (١٦) بدون الف بالملك * (١٧) بالالف بالكهف * (١٨) بدون ألف بالتوبة * (١٩) بالف بق (٢٠) بدون ألف بالشعراء * (٢١) منفصلة بالحشر * (٢٢) متصلة بالحديد (*)',NULL,'144',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (148,'أئذا (١) متنا وكنا ترابا وأنا ظننا أن لن (٣) تقول الانس وأن الله ليس بظلام (٥) للعبيد وإذا لاتخذوك (٧) خليلا وما دعاء (٩) الكفرين الا في ضلل قل أؤنبئكم (١١) بخير من ذلكم
يمحوا الله (١٣) ما يشاء ويثبت لا يستأخرون (١٥) ساعة ولا يستقدمون ولا تايئسوا (١٧) من روح الله وهو الذى أحياكم (١٩) ثم يميتكم أءذا (٢) متنا وكنا ترابا أيحسب الانسان ألن (٤) نجمع عظامة وأن الله ليس بظلم (٦) للعبيد لو شئت لتخذت (٨) عليه اجرا وما دعؤا (١٠) الكفرين الا في ضلل أءلقى (١٢) الذكر عليه من بيننا ويمح (١٤) الله البطل ويحق الحق فلا يستئخرون (١٦) ساعة ولا يستقدمون حتى إذا استيئس (١٨) الرسل وكنتم اموتا فأحيكم (٢٠) ثم يميتكم
_________
(١) الهمزة الثانية على نبرة بالواقعة * (٢) الهمزة الثانية على السطر بالقمر (٣) باثبات النون بعد الالف ومثلها كما ظننتم أن لن يبعث الله احدا ومثلها وانا ظننا أن لن نعجز الله - كل هذه الآيات في الجن * (٤) بحذف النون بعد الالف بالقيمة ومثلها وألو استقاموا على الطريقة بالجن * (٥) بالالف بآل عمران * (٦) بدون الف بالحج * (٧) بالالف بالاسراء * (٨) بدون الف بالكهف * (٩) الهمزة على السطر بالرعد * (١٠) الهمزة على واو بغافر (١١) الهمزة الثانية على واو بآل عمران * (١٢) الهمزة الثانية على السطر بالقمر * (١٣) بالواو بآخر الرعد * (١٤) بحذف الواو بالشورى * (١٥) الهمزة
على الالف بالاعراف * (١٦) بحذف الف الهمزة بيونس * (١٧) بزيادة الف بين التاء والياء بيوسف * (١٨) بحذف الالف بيوسف * (١٩) بالالف في آخر الحج * (٢٠) بحذف الالف في اول البقرة (*)',NULL,'145',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (149,'فعلم مما ذكرناه في هذا الجدول ان الصحابة رضى الله تعالى عنهم لم يتخذوا طريقة واحدة يسيرون عليها في كتابة المصحف العثماني فكيف يسهل علينا اتباع رسمه في كتاباتنا العامة - وسنذكر أن شاء الله تعالى في الفصول الاتية كثيرا من الامثلة وغرائب الرسم (بيان ما يسوغ لنا اتباعه) (من المرسوم وما لا يسوغ) إذا امعنا النظر وقارنا بين مرسوم المصحف العثماني وبين طريقة كتابتنا نجد في بعض الحالات لا يسوغ لنا اتباع رسمه لحصول الالتباس وصعوبة القراءة، وفي بعض الحالات يستحسن لنا اتباعه.
(فبيان ما لا يسوغ لنا اتباع رسمه) نحو ما ذكرناه في هذا الجدول الثاني وما ذكرناه في الجدول الاول الواقع في اول الكتاب وكذلك حذف ألف المد من الكلمات نحو: رب العلمين، ذلك الكتب لا ريب فيه، أضغث أحلم، هل أتى على الانسن يجعلون أصبعهم، والمرسلت عرفا، فالعصفت عصفا والنشرت نشرا، فالفرقت فرقا، فلملقيت ذكرا.
ونحو: امرأت، وقرت، ورحمت، ونعمت، وسنت واليل - وحذف نبرة الهمزة أي سنتها من نحو السيئات وخطئين، ومتكئين.
إلى غير ذلك من الكلمات التى لا يمكن',NULL,'146',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (150,'استقصاؤها ولا تخفى مخالفتها ولنضرب مثالا لذلك بهذه الجمل الآتية وهى: (ايها الطلاب النجباء من حفظ العلوم والادب، وسبق اقرانه في الطلب، تعطى له جائزة من الديوان العالي، ويوظف حسب درجة معلوماته، وقوة ملكته، فاجتهدوا رعاكم الله تعالى في طلب العلم وصابروا عليه، تنالوا سعادة الدارين، فالعلماء نور البلاد، والجهلاء شرار العباد، وأقرنوا العلم بالعمل تكونوا من الفائزين) فإذا كتبنا هذه الجمل بحسب رسم المصحف العثماني تكون صورتها هكذا: (أيه الطلب النجبؤا من حفظ العلوم والادب، وسبق أقرنه في الطلب، تعطى له جئزة من الديون العلى، ويوظف حسب درجت معلومته، وقوت ملكته، فاجتهدوا رعكم الله تعلى في طلب العلم وصبروا عليه تنلوا سعادت الدرين، فالعلمؤا نور البلد والجهلاء شرار العبد وأقرنوا العلم بالعمل تكونوا من الفئزين) .
فالفرق في كتابة الجمل المذكورة بالرسم العثماني وبقواعد كتابتنا عظيم جدا كما هو ظاهر، فالرسم العثماني خاص بكتابة المصاحف فقط',NULL,'147',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (151,'الا يستسيغه الانسان في غيرها.
(أما بيان ما يستحسن لنا اتباعه) ففى نحو هذه الكلمات: اشتريه، ومثويه، وإحديهما، ويغشها، وتقويها، وهل أتيك
وترضيه ونجيكم.
فكتابة الرسم العثماني لها اصح من كتابتنا لان أصلها يأتي ونحن نكتبها بالالف هكذا: اشتراه، مثواه، احداهما، يغشاها تقواها، وهل اتاك، ترضاه، نجاكم.
(ويستحسن لنا اتباعه ايضا) في الهمزات المرسومة في نحو هذه الكلمات: " واسئل، والمنشئات، والظمئان، وقرءان، ومئاب ووطئا، وخطئا، وليطفئوا، ومسئولا، وشركاءى، والمسئ ويسئمون، ويجئرون، وشطئه، وشيئا " وقد نتفق مع مرسوم المصحف في كتابة بعض الكلمات ولكثرة استعمالنا لها لا نشعر بذلك (مثاله) بسم الله الرحمن الرحيم، لا اله الا الله، هذا، ذلك، هأنتم، هؤلاء، ههنا، أولئك، أولاء، أولو (١)
_________
(١) أي في زيادة الواو التى قبل اللام من أولو، اما الالف التى بعد الواو الثانية فهى تثبت في الرسم العثماني وتحذف على قاعدتنا - وقد اشار بعضهم إلى زيادة الواو في أولو، اولاء أولئك بقوله: تزاد واو وسط الثلاثة * أولى أولات وأولا الاشارة وآخرا تزاد واو عمرو * في حالة الرفع كذا في الجر (*)',NULL,'148',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (152,'لكن، إذا، طه، الذين، ووضع ألف بعد واو الجماعة، والهمزات بأنواعها (عدا ما نستثنيه هنا) إلى غير ذلك مما لا يخفى.
وكان من حق هذه الكلمات أن تكتب هكذا: (باسم اللاه الرحمان الرحيم، لا إلاه إلا اللاه، هاذا، ذالك ها أنتم، هاألاء، هاهنا، ألائك، ألو، ألاء، لاكن، إذن طاها، اللذين (١))
لان الكتابة تكون على حسب النطق فان كان الحرف ممدودا تزاد فيه أحد حروف المد التى هي الالف والواو والياء، بحسب حركة ذلك الحرف من فتح أو ضم أو كسر.
احوال الهمزة في القرآن اعلم أن جميع حالات الهمز في المصحف العثماني مضبوط على القواعد المحررة ما عدا شئ يسير وهو في هذه الكلمات الاتية.
(ءأمنتم، أءنزل، ءألد، أءذا، أءنا، أءنك، رءا، الرءيا
_________
(١) القاعدة كما ذكره الخضري على ابن عقيل في اول اسم الموصول أن يكتب الذى وجمعه والتى بلام واحدة لكثرة استعمالها واللذين واللتين مثنى بلامين على الاصل في كل ما أوله لام حلى بأل وللفرق بينه وبين الجمع نصبا وجرا وحمل الرفع عليهما اه من الخضرى - أما في رسم القرآن فقد كتب جميع ما ذكر بلام واحدة.
(*)',NULL,'149',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (153,'ورءيا، أرءيت (١) ، ألئن، الملؤا، نبؤا، لا تظمؤا العلمؤا، الضعفؤا، نشؤا، شركؤا، شفعؤا يتفيؤا، تفتؤا، ينشؤا، أتوكؤا، أنبؤا، يبدؤا البلؤا، دعؤا (٢) مستئنسين، فليستئذنوا، فلا يستئخرون (٣) ولا تايئسوا (٤) يبنؤم، متكئا، من ورائ - حجاب، إلى فرعون وملائه، يسئلك، وسئل (٥) يجئرون، ومنءاناءى اليل، لتنوأ) ثم ان الهمزة قد توضع على نبرة " أي سنة " وقد لا توضع عليها (فالحالة الاولى) في نحو هذه الكلمات " فئة، خاسئا، بالخاطئة
السيئة ".
_________
(١) كتبت الهمزة في أرءيت الذى يكذب على السطر بخلافها في ورأيت الناس يدخلون فانها كتبت على الالف.
(٢) يلاحظ أن الكلمات الستة عشر التى أولها الملؤا وآخرها دعؤا زيدت في أواخرها ألف (٣) هذا في سورة يونس أما التى بالاعراف فانها هكذا فلا يستأخرون (٤) أي وبزيادة الف وكذلك انه لا يايئس بخلاف فلما استيئسوا ومثلها حتى إذا استيئس كلاما بدون الف وهذه الكلمات كلها في سورة يوسف واما التى بسورة الرعد وهى أفلم يايئس فأنها بالالف (٥) الشاهد في الهمزة لا في حذف الالف التى قبل السين (*)',NULL,'150',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (154,'(والحالة الثانية) في نحو هذه الكلمات " السيئات، متكئا خطئين، متكئين، الافئدة، شيئا " - على أننا تأملنا الكلمتين الاخيرتين " الافئدة وشيئا " ونحوهما من كل همزة وقعت بعد ساكن لوجدنا حذف النبرة منهما أولى وأصح.
(ومما يناسب هذا المقام) ذكر سؤال من الاسئلة التى كنا بعثناها لمشيخة المقارئ المصرية مع الاجابة عليه (١) وهو " المعروف ان الهمزة اخترعها الخليل بن احمد الفراهيدي المتوفى عام مائة وسبعين فلم تكن إذا موجودة في مصحف عثمان بن عفان رضى الله عنه فلم لم تكتب الهمزات في المصحف الاميري الذى طبعته الحكومة المصرية عام ألف وثلاثمائة واثنين واربعين هجرية على القواعد الاملائية فمثلا كلمة أفرءيتم كتبت الهمزة الثانية على السطر وكذلك أرءيت الذى
مع أنهما مفتوحتان وما قبلهما مفتوح ايضا بخلاف ورأيت الناس وكلمة ءأنتم كتبت الهمزة الاولى على السطر فلم لم تكتب على الالف مثل أيطمع ولم لم تكتب الهمزة الثانية من أءنا على الياء مثل أئذا وكلتاهما مكسورتان وما قبلهما مفتوح "
_________
(١) سبق أن ذكر في صحيفة ١١٢ تاريخ ورود اجابة مشيخة المقارئ المصرية على اسئلتنا وأن جميع الاسئلة والاجابة عليها محفوظ لدينا (*)',NULL,'151',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (155,'(فأجابنا عليه) شيخ القراء هناك فضيلة الاستاذ الشيخ محمد على الضباع اجابة طويلة نختصرها فيما يأتي وهو: ان صورة الهمزة ليست في مصحف عثمان لانها من علامات الضبط الذى أحدث بعد عهده والهمز في القرآن إما أن يكون همز قطع أو وصل، فهمزة الوصل ترسم ألفا ونص اهل الرسم على حذفها في خمسة أحوال (الاولى) مثل وأتوا وفأتوا (الثانية) مثل وسئل فسئل (الثالثة) مثل للدار للذين (الرابعة) مثل أتخذتم أفترى (الخامسة) مثل بسم الله.
واما همزة القطع فالاصل في رسمها أن تكتب ألفا إذا وقعت أولا وإلا كتبت بصورة الحرف الذى تؤول إليه في التخفيف أو تقرب منه فان كانت تخفف ألفا أو كالالف فقياسها ان تكتب ألفا، وان كانت تخفف ياء أو كالياء فقياسها أن تكتب ياء، وان كانت تخفف واوا أو كالواو فقياسها أن تكتب واوا، وان كانت تخفف بالحذف بنقل أو غيره فقياسها الحذف.
وتفصيل هذا الاصل.
ان همزة القطع على قسمين ساكنة ومتحركة، والساكنة تقع وسطا وطرفا، والمتحركة تقع ابتداء ووسطا
وطرفا هذا هو القياس في العربية وخط المصاحف العثمانية.
وجاءت أحرف في خط المصاحف خارجة عن هذا القياس لمعنى',NULL,'152',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (156,'مقصود ووجه مستقيم يعرفه من قدر للسلف قدرهم.
ومن هذه الاحرف كلمة " رأيت " كيف جاءت بعد همزة الاستفهام نحو: أرءيت، أفرءيت، أرءيتم، أفرءيتم، فانه رسم في بعض المصاحف بحذف الالف التى بعد الراء وقد ضبطت في المصحف الاميري بوضع القطعة " الهمزة " مكانها لتدل عليها ولعلها حذفت لاحتمال القراءتين فانها قرئت بالهمزة وتركها.
ومنها " ءأنذرتهم " وبابه فانه رسم في المصاحف بألف واحدة وهل هي الاولى أو الثانية، الذى جرى عليه العمل في المصحف الاميري أنها الثانية ولذا وضعت قطعة الضبط وهى " الهمزة " مكان همزة الاستفهام لتدل عليها.
ومنها " أءنا " ونحوه من كل ما اجتمع فيه همزتان مفتوحة ومكسورة والاولى للاستفهام فانه رسم في بعض المواضع بحذف صورة الهمزة الثانية وهى الياء وقد وضع علماء الضبط القطعة مكانها لتدل عليها.
وقد استوفى علماء القراءة والرسم ما خرج عن الاصل من باب الهمز في كتبهم فارجع إليها ان شئت اه.
هذا مختصر ما أجابنا به فضيلة شيخ المقارئ المصرية حفظه الله تعالى',NULL,'153',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (157,'ولقد فصل جميع ما جاء مجملا عن احوال الهمز في اجابته ونحن خوفا
من التطويل لخصناها فيما ذكر بما لا يخل بالمقصود.

<span data-type="title" id=toc-23>الباب الخامس (في ذكر شئ من مرسوم القرآن الكريم)</span>
(وفيه اربع فصول)
<span data-type="title" id=toc-24>(الفصل الاول * في رسم الكلمات الآتية)</span>
ابراهيم، ايها سعوا، عتوا، لام الجر المقطوع عن مجرورها، ابدال هاء التأنيث تاء - وغيرها نذكر في كل فصل من فصول هذا الباب شيئا من الكلمات القرآنية على رسم المصحف العثماني معتمدين في النقل على المصحف الاميري الذى طبعته الحكومة المصرية سنة ألف وثلاثمائة واثنين وأربعين هجرية.
فمثلا - كلمة " ابرهيم " مرسومة في سورة البقرة هكذا " ابرهم " وفي بقية القرآن هكذا " ابرهيم " اما الالف التى بعد الراء فمحذوفة من الصورتين وكلمة " أيها " مرسومة في جميع القرآن بالالف بعد الهاء ما عدا ثلاثة مواضع فمرسومة فيها بحذف الالف هكذا " أيه " نحو " سنفرغ لكم أيه الثقلان "',NULL,'154',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (158,'وكلمة " عتوا " التى بالاعراف وضعوا فيها ألفا بعد الواو بخلاف " عتو " التى بالفرقان فانهم حذفوها منها وكلمة " سعوا " التى بالحج وضعوا فيها ألفا بعد الواو بخلاف " سعو " التى بسبأ فانهم حذفوها منها وكلمة " مائة " كتبوها بالالف (١) ولم يكتبوا كلمة " فئة "
مثلها مع انها تشابهها في الحركات، وأثبتوا الالف بعد واو فعل جمع في القرآن كله (٢) ما عدا اربع كلمات وهى: " جاءو.
فاءو.
باءو تبوءو " فانهم حذفوا الالف منها بعد الواو وأبدلوا هاء التأنيث تاء (٣) في ما يأتي من الكلمات وذلك في بعض المواضع التى تعرف من علم التجويد وفن الرسم وهى: " رحمة، نعمة، سنة، امرأة، كلمة شجرة، جنة، قرة، فطرة، بينة، بقية، ابنة، لعنة، معصية "
_________
(١) والى زيادة الالف في مائة اشار بعضهم بقوله: ووسطا تزاد في لفظ مائه * ولو مع الآحاد كالخمسمائة (٢) القاعدة الاملائية: أن تزاد الالف بعد واو فعل جمع نحو ضربوا واضربوا ولم يضربوا، وإلى هذا أشار بعضهم بقوله: تزاد في واو الضمير كاشعروا * بأنهم لم يظلموا إذ قدروا الا جمع اسم كألو الفضل وضاربو زيد، وفعل مفرد كيدعو - لكن رسم المصحف لا يتمشى مع القواعد فلم تحذف الالف من نحن أولوا قوة، ومن لمن كان يرجوا الله وحذفت من الكلمات الاربع (جاءو.
فاءو.
باءو.
تبوءو.
) (٣) والقاعدة الاملائية أن كل تاء بعد الفتحة تربط.
(*)',NULL,'155',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (159,'مثال ذلك: رحمت الله، وبنعمت الله، سنت الاولين، امرأت العزيز، كلمت ربك، ان شجرت الزقوم، وجنت نعيم، قرت عين لى ولك، فطرت الله التى فطر الناس عليها، فهم على بينت منه بسورة فاطر، بقيت الله خير لكم، ومريم ابنت عمران، أن لعنت الله، ومعصيت الرسول.
فهذه الكلمات فيما عدا المواضع التى ذكرت في علم التجويد ترسم
بالهاء (١) ولولا خوف التطويل لذكرنا تلك المواضع كلها.
وإلى ما ذكر اشار العلامة المرحوم الشيخ محمد العاقب بقوله، فما أتى من صور مزيده * فيه وحذف أحرف عديده كالياء إذ زيدت لدى بأييد * وحذفت من قوله ذا الايد والالف المزيد في لفظ مائه * وفي أقاموا دون جاءو وفئه والالف المرسوم في فعل سعوا * في الحج دون غيره وفي عتوا ونعمت إذ رسمت بالتاء * طورا وطورا صورت بالهاء
_________
(١) وبما أنه ورد في رسم المصحف العثماني ابدال هاء التأنيث تاء أحيانا كما هو مذكور اعلاه لا نخطئ الاتراك في كتابتهم لبعض الاسماء التى في آخرها هاء بالتاء نحو.
نعمت، عصمت، شوكت، جودت، طلعت، رأفت، حكمت دولت، حريت، عدالت، سماحت، نزاهت.
والظاهر انهم يكتبونها كذلك بحسب نطقهم.
(*)',NULL,'156',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (160,'والاحرف التى يهجى القارى (١) * بها هجاء الالدة الصغار (٢) فكل ذا لعلة مقدره * وحكمة عن الحجا مخدره أنفاسه للنفس لا تنسم * وسره عن الورى مطلسم وقد تكلف شيوخ الكتبه * فسارعوا فيه لنحت الاجوبة فذكروا من ذاك ما لا يقنع * قلبا ولا غل غليل ينقع (٣) ومثلا - قطعوا لام الجر عن مجرورها في اربعة مواضع نحو " مال هذا الرسول ".
وقطعوا في عن ما بأحد عشر موضعا نحو " لمسكم في ما أفضتم " وقطعوا كل عن ما في خمسة مواضع نحو " كل ما دخلت امة "
وقطعوا من عن ما في ثلاثة مواضع نحو " فمن ما ملكت " وقطعوا حيث عن ما في موضعين نحو " وحيث ما كنتم فولوا وجوهكم شطره " وقطعوا أن المخففة المفتوحة عن لا في عشرة مواضع نحو " أن لا تشرك بى شيئا " ووصلوا بئس بما في ثلاثة مواضع نحو " بئسما اشتروا به أنفسهم "
_________
(١) المراد بالاحرف هنا فواتح بعض السور نحو حم وطسم وكهيعص (٢) الالدة بكسر الهمزة وسكون اللام الصبية (٣) أي لايل يز عطش عطشان (*)',NULL,'157',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (161,'ووصلوا أين بما في خمسة مواضع نحو " أينما ثقفوا " ووصلوا كى بلا في اربعة مواضع نحو " كيلا تحزنوا على ما فاتكم " إلى غير ذلك مما لو أحصيناه لطال بنا الشرح، ولهذا لم نذكر بيان كل المواضع في جميع ما تقدم ويعلم تفصيلها من كتب التجويد.
ولقد ذكر نوعي الفصل والوصل العلامة الشيخ محمد العاقب الشنقيطى رحمه الله تعالى في نظمه كشف العمى بقوله فصل وفى ما الفصل احدى عشره * من بعد لا جناح اخرى البقره والشعرا والروم فيهما استقر * واثنان مع يبلوكم مثل الزمر وبعدهم في الانبياء ونقلا * قبل أفضتم وأوحى ولا وباتصال الخط بيسما خلا * ما فاء أو لام عليه دخلا وقطع مما قد أتى يقينا * من ما رزقنا في المنافقينا وقبلها حرفان باستواء * مع ملكت في الروم والنساء
وكلما بالاتصال يدرى * الا سألتم وردوا تترا وقطعت أم من يكون في النسا * وقبل يأتي وخلقنا أسسا وأينما بالوصل عنهم يؤخذ * مع ثم يدرككم يوجه اخذوا وسورة الاحزاب كى لا الاول * فيها وفي نحل وحشر يفصل وحكم لام الجر أن ينفصلا * في مال هذا والذين هؤلا واخرجت مخرج مال الله * مع انعدام الشبه والتضاهى فصل وحيث ما بفصل قد فشا * عن ما نهوا عن من تولى ويشا ولات حين ثم هم ويوم هم * في غافر والذاريات وابن ام فصل ووصل أيما قد التزم * كويكأن فيم ممن عم مم مهما والا ربما وأما * كانما هلم مع نعما هذا وغير ذا من البديهى * وذكره يقدح في النبيه فاقطع إذا صح وصل ان لم يصح * والفرق بين ذا وذاك يتضح',NULL,'158',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (162,'<span data-type="title" id=toc-25>الفصل الثاني (في رسم البسملة، وهمزة نحو ائذا، وتبارك، وألف المد) (وألف التثنية، وما كتب بلام أو لامين وغيرها)</span>
حذفوا من " بسم الله الرحمن الرحيم " ثلاث ألفات، الاولى من بسم، والثانية من الله، والثالثة من الرحمن - والعلماء لم يبحثوا عن حذف الالف من الاسمين الشريفين بل ذكروا تعليلات متنوعة عن حذفها من بسم فقط، على انه مهما ذكروا من التعليلات لحذفها منه فما هو الا من قبيل الاستئناس والتمليح لا غير، لان الحقيقة التى لا تنكر ان كتابة البسملة بهيئتها المعروفة لدينا هي من رسم المصحف العثماني
من ابتداء الامر قبل النظر في العلل والاسباب (١) ثم لا ندرى لم حذفت ألف بسم من البسملة فقط ولم تحذف من
_________
(١) جاء في اول تفسير القرطبى روى الشعبى والاعمش ان رسول الله صلى الله عليه وسلم كان يكتب باسمك اللهم حتى امر ان يكتب باسم الله فكتبها فلما نزلت قل ادعوا الله أو ادعوا الرحمن كتب بسم الله الرحمن فلما نزلت انه من سليمان وانه بسم الله الرحمن الرحيم كتبها وفي مصنف ابى داود قال الشعبي وابو مالك وقتادة وثابت بن عمارة ان النبي صلى الله عليه وسلم لم يكتب بسم الله الرحمن الرحيم حتى نزلت سورة النمل اه من التفسير المذكور ومعنى كتابة رسول الله صلى الله عليه وسلم البسملة انه امر بكتابتها، وهذا من قبيل بنى الامير المدينة فالنبى عليه الصلاة والسلام امى لا يقرا ولا يكتب.
(*)',NULL,'159',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (163,'" اقرأ باسم ربك " ومن " سبح اسم ربك " ومن " فسبح باسم ربك العظيم " مع العلم بأنه كان من حق البسملة ان تكتب على حسب النطق هكذا " باسم اللاه الرحمان الرحيم ".
ويرى بعضهم بمناسبة حذف الالف من بسم تطويل الباء منها بمقدار نصف الالف ليدل عليها ولانه اول حرف يكتب من القرآن فتطويله من قبل التعظيم وإلى هذا أشار الشيخ محمد العاقب رحمه الله تعالى بقوله: يطول الباء ويحذف الالف * من لفظ بسم الله كيفما ألف وحد طوله بلا ازدياد * مقدار نصف ألف المعتاد وهل للاشعار بما قد سلبا * أو ليرى أول حرف كتبا مقابلا بالرفع والتحسين * قولان في نفسير فخر الدين (ونحن نقول) ان تطويل الباء وكتابة الحروف راجع لقواعد تحسين
الخط التى وضعها الخطاطون لاظهار جمال الحروف، ففى بعض انواع الخطوط يستحسن تطويل الباء الواقعة اول الكلمة كما في الخط الكوفى وخط الثلث والنسخ وفي بعضها يستحسن تقصيرها كما في الخط الفارسى وخط الرقعة.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (164,'ومن اللطائف المناسبة قول ابى سعيد الرستمى: من الناس من يعطى المزيد على الغنى * ويحرم ما دون الرضا شاعر مثلى كما ألحقت واو بعمرو زيادة * وضويق بسم الله في ألف الوصل وحذفوا الالف من " واسئل " اينما وقعت في القرآن فيكتبونها هكذا " وسئل، فسئل " ولم يحذفوها من نحو " واستغفره، واستغنى، واستغشوا ".
وحذفوا احدى اللامين من هذه الكلمات " اليل، والذى والذان، والذين، والتى، والئى " وكان من حقها كتابتها بلامين بحسب النطق * ولم يحذفوفها من هذه الكلمات " اللطيف، اللؤلؤ اللهو، اللعبين، اللغو، اللوامة، اللت، اللعنة، اللهب " وحذفوا ألف المد الواقعة بعد الواو من نحو هذه الكلمات " والقوعد، وحدة، إخونكم، أخولكم، أزوجه، لوقع، ألونكم ابوبا، امولكم، الصوعق، روسى، بأكوب بأفوههم، السموت " ولم يحذفوها بعد الواو من نحو هذه الكلمات " الواقعة، توارت الجوار، الكوافر، لهى الحيوان، الكواكب، واكواب، الحواريون اواب، لواحة، خوار، الثواب " وأثبتوا ألف المد في كلمة " لاهية قلوبهم " بأول الانبياء وحذفوها
من هذه الكلمات " لبثين، لغية، لقيه، لعبين "',NULL,'161',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (165,'وحذفوا ألف المد ايضا من نحو " قالوا جزؤه (١) ، وذلك جزؤا (٢) ، وجزؤا سيئة (٣) ولم يحذفوها من نحو " جزاؤهم عند ربهم " (٤) ، جزاؤكم جزاء موفورا (٥) ، لهم جزاء الضعف " (٦) وأثبتوا ألف المد غالبا إذا وقعت بعدها همزة نحو " حدائق، الارائك، الخائضين، خائفا، طائفة، وابتغاؤكم، هذا بصائر " (٧) ولم يثبتوها في نحو " سئحت، كبئر الاثم * الخبئث، اسرءيل " وكتبوا الهمزة الثانية من " أئذا وأئنا " على السطر احيانا وعلى النبرة أحيانا اخرى نحو " أئذا متنا، أءذا كنا ترابا، أئنا لمخرجون، أءنا لمبعوثون ".
وكتبوا " وإذا رءا الذين، ورءا المجرمون " بألف بعد الهمزة التى على السطر بخلاف " ما كذب الفؤاد ما رأى، لقد رأى منءايت ربه " فانه بياء بعد الهمزة التى رسمت فوق الالف.
وكتبوا بالواو هذا الثمانية الكلمات " الصلوة، الزكوة
_________
(١) بسورة يوسف * (٢) بالحشر * (٣) بالشورى * (٤) بالبينة (٥) بالاسراء * (٦) بسباء (٧) كتبت بصائر بالالف في سورة القصص والاسراء والاعراف، واما التى بالجاثية فقد كتبت بغير الف هكذا " بصئر " (*)',NULL,'162',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (166,'الحيوة (١) الربوا، النجوة، الغدوة، مشكوة، منوة "
وقد جمعها المرحوم الشيخ محمد العاقب في قوله.
وفى الصلاة والحياة فاكتبا * واوا بغير مضمر مثل الربا مشكاة الزكاة والنجاة مع * مناة والغداة كيفما وقع (٢) وكتبوا قوله تعالى " وليكونا من الصاغرين " وقوله " لنسفعا بالناصية " بالالف وكان الاولى كتابتهما بالنون لانهما فعلان اتصلت بهما نون التوكيد الخفيفة وكتبوا " إذا " بالالف نحو " تلك إذا كرة خاسرة " اما كتابتها في غير المصحف فقد اختلفوا فمنهم من يكتبها بالنون ومنهم من يكتبها بالالف وانظر تفصيل ذلك في حاشية الخضرى على ابن عقيل عند قول الناظم (ونصبوا باذا المستقبلا) .
وإلى ما ذكر أشار بعضهم بقوله: نون إذا ونون توكيد تخف * نحو إذا لنسفعا فبالالف كذلك التنوين في اسم نصبا * مثاله اهلا وسهلا مرحبا ما لم يكن..الخ
_________
(١) تكتب الثلاث الكلمات الاولى بالواو إذا لم تضف إلى ضمير فان اضيفت كتبت بالالف نحو " الا حياتنا الدنيا، في صلاتهم، وكذلك وماءاتيتم من ربا " ما عدا أصلوتك تأمرك، وان صلوتك سكن لهم (٢) كان الاولى كتابة الكلمات في البيتين بالواو لكن عدلنا عن ذلك لتسهل قراءتها.
(*)',NULL,'163',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (167,'ووضعوا ألفا بعد دال ثمود إذا وقعت في محل نصب فقط نحو " إن ثمودا كفروا ربهم، وثمودا فما ابقى، وعادا وثمودا " وحذفوا الالف من " تبرك اسمك ربك " ومن " تبرك الذى
بيده الملك " ومن " ماء مبركا " ومن " شجرة مبركة " واثبتوها في " فتبارك الله رب العلمين " وفي " فتبارك الله احسن الخلقين " وفي " وهذا ذكر مبارك " وفي " منزلا مباركا " وحذفوا الالف من " أو أثرة من علم " في الاحقاف وفي " علئ آثرهم " في الصافات وفى الزخرف وفي الحديد.
واثبتوها في " علئ آثارهما " بالكهف وفي " وءاثارا في الارض " في آيتين بسورة غافر.
وحذفوا الالف من نحو " ويضرب الله الامثل للناس " ومن " أنظر كيف ضربوا لك الامثل " بالفرقان ومن " وتلك الامثل نضربها للناس " وأثبتوها في " فلا تضربوا لله الامثال " وفي " كذلك يضرب الله الامثال " وفي " انظر كيف ضربوا لك الامثال " بالاسراء وحذفوا الالف من لفظة (كتاب) في جميع القرآن ما عدا هذه الآيات فانهم كتبوها بالالف وهى " من كتاب ربك، لكل أجل كتاب، إلا ولها كتاب معلوم، طس تلك آيات القرآن وكتاب مبين ".',NULL,'164',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (168,'واثبتوا الالف في لفظة (قال) في جميع القرآن ما عدا هذه الآيات فانهم كتبوها بغير ألف وهى " قل رب احكم بالحق، قل كم لبثتم في الارض، قل إن لبثتم الا قليلا، قل ربى يعلم القول، قل أولو جئتكم ".

<span data-type="title" id=toc-26>الفصل الثالث (في ألف التثنية * ورسم صيغ المبالغة، وصيغ المفرد والجمع) (ورسم صاحبكم، واسماء بعض الانبياء، وألف المد)</span>
لا يحذفون ألف التثنية غالبا نحو " هل يستويان، هذان خصمان، تستفتيان، جنتان، يخصفان " وقد يحذفونها نادرا نحو " إن هذن لسحرن " ولا يحذفون غالبا ألف المد في صيغ المبالغة نحو " توابا، أواب وهاجا، ثجاجا، ديارا، كبارا، الوهاب، الكفار، حمالة، شاقوا الله، وأن الله ليس بظلام للعبيد " التى بآل عمران فقط.
وقد يحذفونها منها نادرا نحو " وما أنا بظلم للعبيد " التى بالحج والتى بق، علم الغيوب، تشقون فيهم، ولا تحضون على طعام، وهو الخلق العليم، إلى العزيز الغفر " ولا يحذفون ألف المد في صيغ المفرد غالبا نحو " عالم الغيب،',NULL,'165',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (169,'لقادر، ناصر، ساجدا، كاذبة، فاعل، فاسق، شاكر، قانتا، كاشفة صابرا، وكان الكافر، وهو ظالم لنفسه، كانت ظالمة، وعمارة، سقاية " وقد يحذفونها منها نادرا نحو " وهى ظلمة، وانى لاظنه كذبا إنى عمل فسوف، عقبة، ميقت، فما متع " أما حدف الف المد في صيغ الجمع فمطرد نحو " رب العلمين الظلمين، شكرون، في السجدين، خمدون، الكفرون، ان المنفقين، الرسخون، من الغبرين الحكمين " وأثبتوا الالف في " وما صاحبكم بمجنون، قال له صاحبه وهو يحاوره، ثم تتفكروا ما بصاحبكم من جنة، وصاحبهما في الدنيا معروفا، فنادوا صاحبهم فتعاطى، ولا تكن كصاحب الحوت " وحذفوها من " فقال لصحبه، إذ يقول لصحبه، ما اتخذ
صحبة، وصحبته وبنيه " وحذفوها من بعض الاسماء نحو " ابرهيم، اسمعيل، اسحق سليمن، هرون، صلح.
" اما حذف الف المد واثباتها في غير ما ذكرناه فهو كثير في القرآن الكريم فمثال الحذف: هذا غلم، بهتن، بضعة، خشعة، إذ يتنزعون، والمحصنت، حفظون، الانسن، سلطن، شيطن',NULL,'166',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (170,'ومثال الاثبات: أنصار الله، وإذا خاطبهم، علمه البيان، عجاف من أساور، واما الجدار * إلى نعاجه، المحراب، من عاصم، بحسبان (ويقول) الحسن بن احمد الهمداني صاحب كتاب الاكليل (١) : ان قاعدة الكتابة الحميرية إذا وقعت الالف في وسط الكلمة حذفت نحو " همدان وريام " فانهم يكتبونهما هكذا " همدن وريم " وكانوا يثبتون واو عليهموكما يثبتون ضمة آخر الحرف اه
غير ان رسم المصحف لا يتبع قاعدة خاصة ولا يتمشى على طريقة واحدة كما رأيت.

<span data-type="title" id=toc-27>الفصل الرابع (في بعض غرائب رسم المصحف العثماني)</span>
لقد ذكرنا في الفصول المتقدمة كثيرا من الكلمات المتنوعة، ونذكر في هذا الفصل جملة من الكلمات التى تعد غريبا في بابها وطريفا في كتابتها فمن ذلك: انهم رسموا كلمة " لااذبحنه " بزيادة ألف بعد ألفها الاصلية
_________
(١) هو أوسع كتاب في الحروف الحميرية ويقع في عشرة اجزاء ولا يوجد منها سوى جزءين في المكتبة الملوكية ببرلين ويقال انه موجود بكامل اجزائه في
مكتبة جلالة الامام يحيى ملك اليمن الحالى والحسن بن احمد الهمداني المذكور هو من اهل القرن الرابع للهجرة فانه توفى سنة ٣٦٤ هجرية تقريبا وهو الذى ألف ايضا كتاب صفة جزيرة العرب.
(*)',NULL,'167',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (171,'ورسموا كلمة " أفإين مات " وكلمة " أفإين مت " بزيادة ياء قبل النون في الكلمتين.
ورسموا كلمة " والسماء بنينها بأييد " وكلمة " بأييكم المفتون " بياءين في الكلمتين.
ورسموا كلمة " سأوريكم دار الفسقين " بزيادة واو بعد الالف بخلاف ما يماثلها نحو " ما اريكم " بسورة غافر.
ورسموا كلمة " يبنؤم " متصلة ببعضها بسورة طه بخلاف ما يماثلها في الاعراف فانها هكذا " قال ابن أم ".
ورسموا كلمة " وجائ يومئذ بجهنم " بالفجر وكلمة " وجائ بالنبيين " بالزمر بزيادة ألف بعد الجيم فيهما بخلاف ما يماثلهما نحو " سئ بهم وضاق بهم ذرعا " في سورة هود وفي سورة العنكبوت ايضا ورسموا كلمة " ولا تقولن لشائ " بالكهف بزيادة الف بعد الشين.
ورسموا كلمة " أصحب لئيكة " بالشعراء بحذف الالف بخلاف ما يماثلها بسورة ق فانها هكذا " أصحب الايكة ".
ورسموا كلمة " من عباده العلمؤا " بفاطر بوضع الهمزة على الواو وألف بعدها.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (172,'ورسموا مثلها كلمة " وما دعؤا الكفرين " التى بغافر، واما التى بالرعد فانها هكذا " وما دعاء الكفرين " ورسموا كلمة " في اموالنا ما نشؤا " بسورة هود بوضع الهمزة على واو فألف بعدها بخلاف ما يماثلها بالحج فانها هكذا " ما نشاء " ورسموا كلمة " الضعفؤا " بوضع الهمزة على واو فألف بعدها وذلك بسورة غافر فقط بخلافها في غيرها فانها كتبت هكذا " الضعفاء " ورسموا كلمة " يأيها الملؤا " وكلمة " قل هو نبؤا عظيم " بوضع الهمزة على الواو فألف بعدها في الكلمتين.
ورسموا كلمة " إلى فرعون وملايه " بزيادة ياء قبل الهاء ورسموا كلمة " لتخذت عليه أجرا " بالكهف بحذف الالف بخلاف ما يماثلها نحو " وإذا لاتخذوك خليلا " بالاسراء فانه باثبات الالف ورسموا كلمة " مائة " بالالف بخلاف " فئة " فانها بحذفها.
ورسموا كلمة " أياك نعبد واياك نستعين " باثبات الالف بخلاف ما يماثلها نحو " وايى فاتقون " فانه بحذف الالف.
ورسموا هذه الكلمات فقط " جاءو.
فاءو.
باءو.
تبوءو " في القرآن كله بحذف الالف من واو فعل جمع.
ورسموا كلمة " ومن يعظم شعئر الله " وكلمة " والبدن جعلناها',NULL,'169',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (173,'لكم من شعئر الله " بدون ألف بعد العين فيهما بخلاف " ان الصفا والمروة من شعائر الله " بالبقرة فانها بالالف.
ورسموا كلمة " كبئر الاثم " بحذف الالف بعد الباء.
ورسموا كلمة " هذا بصئر للناس " التى بالجاثية فقط بدون ألف
بعد الصاد بخلاف التى بالاعراف وبالاسراء وبالقصص فانها بالالف ورسموا كلمة " رضية " التى بالقارعة فقط بدون ألف بخلاف التى بالحاقة وبالغاشية وبالفجر فانها بالالف ورسموا كلمة " قل سبحان ربى " بالالف في هذه الآية فقط وفي جميع القرآن بدون ألف هكذا " سبحن " ورسموا كلمة " عبادنا " التى بصيغة الجمع بغير ألف في سورة ص فقط في آية " واذكر عبدنا ابرهيم واسحق ويعقوب " وفيما عدا هذه الآية رسموها بالالف في جميع القرآن.
ورسموا كلمة " ءاياتنا التى بيونس فقط بألف بعد الياء وفي جميع القرآن بغير ألف هكذا " ءايتنا " ورسموا كلمة " قرءنا عربيا " التى في اول الزخرف فقط بدون ألف بعد الهمزة وفي جميع القرآن بالالف هكذا " قرءان " ورسموا كلمة " من نخيل وأعناب " التى بأواخر البقرة فقط',NULL,'170',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (174,'بالالف وفي جميع القرآن بغير ألف هكذا " وأعنب " ورسموا هاء التأنيث تاء احيانا في نحو " نعمة.
رحمة.
سنة شجرة " فيكتبونها احيانا هكذا " نعمت.
رحمت.
سنت.
شجرت " وقد سبق بيان ما يماثلها في أول صحيفة ١٥٦ ورسموا كلمة " في روضات الجنات " التى بالشورى فقط بالالف بعد النون وفي جميع القرآن بدون ألف هكذا " جنت ".
ورسموا كلمة " السموات " التى بأول فصلت فقط بالالف بعد الواو وفي جميع القرآن بدون ألف هكذا " السموت " أما حذف
الالف بعد ميمها فمطرد في القرآن كله.
ورسموا كلمة " سعوا " التى بالحج بالالف واما التى بسبأ فانها بحذف الالف التى بعد الواو ورسموا كلمة " عتوا " التى بالفرقان بدون ألف بعد الواو اما التى بالاعراف فانها بالالف.
ورسموا كلمة " سراجا " بالالف بعد الراء في جميع القرآن ما عدا التى بالفرقان فانها بدون ألف.
ورسموا كلمة " فأحيكم " التى بأول البقرة بدون ألف بعد الياء واما التى بآخر الحج فانها بالالف هكذا " فأحياكم ".',NULL,'171',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (175,'ورسموا كلمة " من نبائ المرسلين " التى بالانعام الهمزة تحت الياء بخلاف غيرها نحو " من نبإ موسى " بدون ياء ورسموا كلمة " أو من ورائ حجاب " التى بالشورى الهمزة على ياء بخلاف غيرها نحو " من وراء جدر " بدون ياء ورسموا كلمة " وايتاءي ذى القربى " وكلمة " من تلقاءى نفسي " وكلمة " ومنءاناءى اليل " الهمزة بالياء في هذه الكلمات الثلاث ورسموا كلمة " وكذلك ننجي المؤمنين " بالانبياء بنون واحدة وبياء بعد الجيم بخلاف التى بيونس فانها بنونين وبحذف الياء التى بعد الجيم هكذا " وكان حقا علينا ننج المؤمنين " ورسموا كلمة " أن لن تقول الانس والجن " باثبات النون بعد الهمزة بسورة الجن بخلاف كلمة " ألن نجمع عظامه " بالقيامة فانها بحذفها وانظر صحيفة ١٤٥ في السطر الثاني مع الهامش
ورسموا كلمتي " الظهر والباطن " بحذف ألف المد من الظاهر واثباتها في الباطن مع ان وزنهما واحدوهما من اسماء الله تعالى.
ورسموا كلمة " لدا الباب " بيوسف بالالف وكلمة " لدى الحناجر " بغافر بالياء.
ورسموا كلمة " إنا لما طغا الماء " بالحاقة بالالف وكلمة',NULL,'172',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (176,'" فأما من طغى " بالنازعات وبغيرها بالياء.
ورسموا كلمة " أيها " بغير ألف بعد الهاء في ثلاثة مواضع فقط في جميع القرآن وهى " أيه الثقلان، أيه المؤمنون، يأيه الساحر " ورسموا كلمة " بأيتنا كذابا " بألف بعد الذال، واما كلمة " لغوا ولا كذابا " فانها بحذف الالف وكلتاهما بسورة النبأ.
ورسموا كلمة " ابراهيم " في سورة البقرة هكذا " ابرهم " وفي بقية القرآن هكذا " ابرهيم " ورسموا كلمة " ايلافهم " هكذا " لايلاف قريش إلفهم " ورسموا كلمة " أنت ولى في الدنيا والآخرة " وكلمة " ان ولى الله " كما تراهما هنا ورسموا كلمة " هو يحى ويميت " وكلمة " فيستحى منكم " كما تراهما هنا ورسموا هذه الكلمات " النبين، الحوارين، الامين " كما تراها هنا ورسموا هذه الكلمات الثمانية بالواو وهى " الصلاة، الزكاة، الربا، النجاة، الغداة، مشكاة، مناة، الحياة "
أي تكون كتابتها في المصحف هكذا " الصلوة، الزكوة، الحيوة، النجوة، الربوا الغدوة، مشكوة، منوة "',NULL,'173',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (177,'ما لم تضف بعضها إلى ضمير وقد سبق قريبا بيان ذلك في الفصل الثاني من هذا الباب في صحيفة ١٦٣ هامش رقم ١ ورسموا هذه الكلمات " قواريرا، سلسلا، الظنونا، السبيلا، الرسولا " كما تراها هنا أي بزياة ألف في أواخرها.
إلى غير ذلك مما لا يمكن حصره فلو تكلمنا على مرسوم القرآن كلمة كلمة لقصر بنا الحال وطال بنا المجال وفيما ذكرناه هنا وفى الفصول السابقة كفاية لاولى الالباب.
وإلى بعض ما تقدم اشار الشيخ محمد العاقب الشنقيطى رحمه الله بقوله: للزيد بعد الهمز واو أدخلا * في سأورى أولو أولات وأولا والياء في بأييد المنون * وأفإين ايتإى ذى القربى عنى من نبإى الانعام مع ورإى * شورى وءاناءى ومن تلقإى وأدخل الالف قبل همزة * ملايه بالخفض ثم مائة وقبل يا لشائ إنى أدخلا * ولفظ يايئس بعد لفظ لم ولا وفي لااذبحن عن الهمز يجى (١) * وقيل في لا اوضعوا جاءو جاى (٢)
_________
(١) عن الهمز أي بعدها (٢) أي روى ان الالف قد جاء مزيدا في قوله تعالى لاء اوضعوا عن بعض علماء الرسم كما في لااذبحنه وقد جاء عن بعضهم ايضا في لا انتم ولا اتوها ولاء إلى لكن الراجح عدم الزيادة في هذه الكلمات اه من ايقاظ الاعلام لوجوب اتباع رسم المصحف الامام (*)',NULL,'174',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (178,'(وخلاصة القول) اننا لم ندرك السر في رسم المصحف العثماني كما لم يدركه من قبلنا من كبار الائمة وفحول العلماء - وسواء فهمنا ذلك أو لم نفهم فالواجب علينا اتباعه حرفا حرفا وكلمة وكلمة وما وسع القرون الاولى وهم خير القرون يسعنا ونحن على ابواب الفتن وفي آخر الزمن نسأل الله لطفه ورحمته وفضله واحسانه انه لطيف خبير.
فكل خير في اتباع من سلف * وكل شر في ابتداع من خلف

<span data-type="title" id=toc-28>الباب السادس</span>
(وفيه خمس فصول)
<span data-type="title" id=toc-29>(الفصل الاول * فيما ذكره العلماء من التعليلات لبعض مرسوم المصحف العثماني)</span>
ذكر العلماء تعليلات متنوعة لبعض كلمات الرسم العثماني غير ان هذه التعليلات ما هي الا من قبيل الاستئناس والتمليح لانها لم توضع الا بعد انقراض الصحابة رضى الله عنهم وهم قد كتبوا المصحف بهذا الرسم لحكمة لم نفهمها واشارة لم ندركها من غير ان ينظروا إلى العلل النحوية أو الصرفية التى استنبطت بعدهم، ونحن نأتى هنا بشئ من ذلك للعلم به.
(فمنها) انهم قالوا حذفت الالف من بسم الله طلبا للخفة',NULL,'175',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (179,'لكثرة استعمالها، قيل لما أسقطوا الالف ردوا طول الالف على الباء ليكون دالا على سقوط الالف ولا تحذف الالف إذا أضيف الاسم إلى غير الله ولا مع غير الباء.
(فنحن نقول) ما هي الخفة في بسم الله بحذف الالف وما هو الثقل في " اقرأ باسم ربك " وفي " سبح اسم ربك " باثباتها ثم ان تطويل الباء أو تقصيرها من بسم الله راجع إلى قواعد تحسين الخط ففى بعض انواع الخطوط تقصر وفي بعضها تطول حتى في الخط الكوفى القديم.
(ومنها) انهم قالوا حذفت الواو من " ويمح الله الباطل " للاشارة إلى سرعة ذهابه واضمحلاله - وزيدت الياء في " والسماء بنيناها بأييد " للفرق بين الايدى التى بمعنى القوة والايدى التى ليست بمعنى القوة.
(فنحن نقول) إذا سلمنا بعلة حذف الواو من " ويمح الله الباطل " فهل يمكن ان نشير إلى أن اثبات الواو في " يمحوا الله ما يشاء ويثبت " يدل على التراخي في المحو والاثبات - وان جرينا على رأيهم أن زيادة الياء في بأييد للفرق بين التى للقوة والتى ليست للقوة فما نقول في زيادة الياء في " بأييكم المفتون " دون زيادتها في " أيكم احسن عملا " (ومنها) انهم قالوا ان زيادة الالف في " لااذبحنه " اشارة إلى أن الذبح لم يقع فكأنما لا نافية، وقيل ان زيادة الالف فيها اشارة',NULL,'176',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (180,'إلى الفتحة لان الفتحة عندهم ألف وكذلك الياء في " ايتاءى ذى القربى " اشارة إلى الكسرة لانها ياء والواو في " سأوريكم آياتى " اشارة إلى الضمة لانها واو أيضا.
(فنحن نقول) : ان كان الامر كذلك فلم لم تكن الالف موجودة في نحو " لاستغفرن لك " والياء موجودة في نحو " من وراء جدر " والواو موجودة في نحو " ما أريكم ".
ثم هل ان زيادة الالف والواو والياء في الكلمات المذكورة هي بمثابة الحركات والتشكيل فان كان كذلك فلم لم يضعوها في جميع كلمات القرآن لتنوب عن الحركات.
(ومنها) انهم قالوا ان كلمة " أحيا " من نحو آية " وانه هو أمات وأحيا " رسمت بالالف كراهة اجتماع متماثلين - فلم لم تكتب كلمة " يحيى " من آية ثم لا يموت فيها ولا يحى كذلك لنفس العلة.
(ومنها) انهم قالوا ان الالف التى بعد الراء من كلمة " ابراهيم " حذفت للاختصار - ونحن نقول ان هذه العلة ليست مطردة في جميع القرآن فقد تحذف الالف من بعض الكلمات نحو: في عيشة رضية بالقارعة، وجعل فيها سرجا بالفرقان، وحرم على قرية بالانبياء ان عذاب ربك لوقع بالطور.
وقد لا تحذف من بعضها نحو',NULL,'177',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (181,'لسعيها راضية بالغاشية، وجعلنا سراجا وهاجا بالنبأ، ليلا من المسجد الحرام بالاسراء، إذا وقعت الواقعة.
(ومنها) انهم قالوا حذفت ياء المضارع لغير جازم في " يوم يأت لا تكلم نفس " على لغة هذيل - ونحن نقول ان هذه الكلمات يا عباد فاتقون، ويا عباد الذين آمنوا " كلاهما في الزمر " ويدع الانسان، ويوم يدع الداع، حذفت منها الياء والواو فهل هذا الحذف على لغة بعض القبائل ايضا ام لا، ولم لم تحذف الياء من آية " يا عبادي الذين آمنوا " في العنكبوت، ومن " قل يا عبادي الذين اسرفوا " في الزمر ولم تحذف الواو من نحو " يمحوا الله ما يشاء ويثبت ".
(ومنها) انهم قالوا رسمت هاء التأنيث تاء نحو " رحمت ونعمت
وامرأت " على لغة طئ - (ونحن نقول) لم لم يكن ذلك مطردا في جميع القرآن فان هذه الكلمات نفسها ومعها بضع كلمات اخرى رسمت احيانا بالهاء واحيانا بالتاء اما غيرها فانها مكتوبة بالهاء على وتيرة واحدة نحو: قيمة، ذرة، القارعة، مسغبة، رقبة، زجرة، خافية.
(ومنها) انهم قالوا في قوله تعالى " لايلاف قريش إلفهم " حذفت الياء من ايلافهم للاقتصار.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (182,'وقالوا ان كلمة " ولييى " من آية " انت ولى في الدنيا والآخرة " ومن آية " ان ولى الله " اتفق شيوخ الرسم على كتابتها بياء واحدة ورجح الدانى وابو داود في حرف الاعراف انها الثانية وفي حرف يوسف انها الاولى ولهذا الترجيح كتب حرف الاعراف وضبط هكذا " ولى " وكتب حرف يوسف وضبط هكذا " ولى " ولا مانع من رسمهما وضبطهما معا بصورة منهما.
(ونحن نقول) هل هذه الكلمات الآتية مثلها ام لا وهى: النبين، الحوارين، الامين، وهو يحى، ويميت.
فالخلاصة ان كل هذه التعليلات التى ذكرها العلماء من الزيادة والحذف في بعض كلمات القرآن لا تغنى شيئا، والحقيقة هكذا وصلت الينا عن الصحابة الذين كتبوا القرآن الكريم ولم ينكشف سر ذلك لاحد والله سبحانه علام الغيوب

<span data-type="title" id=toc-30>الفصل الثاني (في اختراع النقط والشكل)</span>
لم يكن النقط والشكل " أي الاعجام والحركات " معروفا قبل الاسلام فكانوا يقرؤن على الوجه الصحيح حسب الفطرة والغريزة فلما انتشر الاسلام واختلط العرب بالعجم طرأ عليهم الخطأ والتصحيف فاحتاجوا إلى وضع علامات تقيهم من ذلك فاخترعوا النقط والشكل',NULL,'179',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (183,'(وسبب تشكيل المصحف) ان زياد بن سمية وكان واليا على البصرة لما رآى ظهور الخطأ عند العرب طلب من ابى الاسود الدؤلى ان يضع طريقة لاصلاح الالسنة عند القراءة فلم يجبه إلى طلبه فدبر زياد حيلة فقال لرجل من أتباعه اقعد على طريق ابى الاسود واقرأ شيئا من القرآن وتعمد اللحن ففعل الرجل ذلك وقرأ " ان الله برئ من المشركين ورسوله " وكسر اللام فلما سمعه أبو الاسود اعظم ذلك وقال عز وجه الله تعالى من أن يبرأ من رسوله فذهب من فوره إلى زياد وقال له قد أجبتك إلى ما سألت، ورأيت أن أبدأ باعراب القرآن فابغنى كاتبا فبعث إليه ثلاثين كاتبا فاختار واحدا منهم وقال له خذ المصحف وصبغا يخالف لون المداد فإذا رأيتنى فتحت شفتي بالحرف فانقط واحدة فوقه وإذا كسرتهما فانقط واحدة أسفله وإذا ضممتهما فاجعل النقطة بين يدى الحرف فان تبعت شيئا من هذه الحركات غنة فانقط نقطتين وأخذ يقرأ بالتأني والكاتب يضع النقط وكلما اتم صحيفة اعاد أبو الاسود نظره عليها واستمر على ذلك حتى أعرب المصحف كله وترك السكون بلا علامة فأخذ الناس هذه الطريقة عنه وكانوا يسمون هذه النقط شكلا ثم تفننوا في هيئة النقط فمنهم من جعلها مربعة ومنهم من جعلها مدورة ثم زادوا علامات في الشكل إلى ان وصلت الينا بهذه الصورة التى نستعملها اليوم',NULL,'180',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (184,'(وسبب نقط المصحف) أن الناس مكثوا يقرؤن في مصاحف عثمان رضى الله عنه نيفا واربعين سنة ثم كثر التصحيف بالعراق ففزع الحجاج (١) إلى كتابه في زمن عبد الملك وسألهم ان يضعوا علامات لهذه الحروف المشتبهة ودعا نصر بن عاصم الليثى ويحى بن يعمر العدواني (وهما ممن اخذ عن ابى الاسود) لهذا الامر وكانت عامة المسلمين تكره ان يزيد احد شيئا على ما في مصحف عثمان ولو للاصلاح وتوقف كثير منهم في قبول الاصلاح الاول الذى ادخله أبو الاسود فبعد البحث والتروى قرر نصر ويحى ادخال الاصلاح الثاني وهو أن توضع النقط افرادا وازواجا لتمييز الاحرف المتشابهة كالدال والذال فالاولى تهمل والثانية تعجم من فوق بنقطة واحدة وهكذا في بقية الحروف وجرى الناس عليه إلى الآن غير ان هناك اختلافا بين الفاء والقاف بين المشارقة والمغاربة فالمشارقة ينقطون الفاء بواحدة من فوق والقاف بنقطتين من فوق ايضا والمغاربة ينقطون الفاء بنقطة واحدة من أسفل والقاف بنقطة واحدة من فوق ولا ضرر في اصطلاحهم حيث أمن اللبس والاشتباه عندهم.
_________
(١) توفى الحجاج بن يوسف الثقفى في شوال سنة خمس وتسعين للهجرة وكان من حفاظ القرآن المعدودين.
(*)',NULL,'181',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (185,'ومن اراد زيادة البحث في هذا الموضوع فعليه بمطالعة كتابنا تاريخ الخط العربي وآدابه فقد بسطنا القول فيه هناك.
والذى يغلب على ظننا والله اعلم بغيبه انه كما ادخل النقط والشكل
في المصاحف سيأتي على الناس زمان يدخلون فيها علامات الترقيم كعلامة الاستفهام والتنصيص والتأثر وقد ذكرناها مفصلا في كتابنا تاريخ الخط العربي وآدابه فراجعه.
والحقيقة لا نرى بأسا في ادخالها في المصاحف لانها من دواعى سرعة الفهم ومن محسنات الكتابة لا دخل لها في جوهر الحروف والكلمات ولا تغير اللفظ ولا المعنى فيكون ادخالها في المصاحف كادخال النقط والشكل ووضع علامات التجويد فوق الكلمات وعلامات الضبط فيها.

<span data-type="title" id=toc-31>الفصل الثالث (في كتابة المصاحف قديما وحديثا)</span>
روى عن على زين العابدين بن الحسين بن على بن ابى طالب رضى الله عنه انه يقول كانت المصاحف لاتباع انما يأتي الرجل بورقة عند المنبر فيقوم الرجل المحتسب فيكتب له من اول البقرة ثم يجئ غيره حتى يتم المصحف.',NULL,'182',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (186,'هكذا كان في ابتداء الاسلام ثم صار كثير من الناس يتفرغون لكتابة المصاحف لعدم وجود المطابع في ذلك الزمن فكان يكتب بعضهم مائة مصحف وبعضهم مائتين وبعضهم أقل أو اكثر، ولئن كانت المطابع غير موجودة في زمنهم فقد كانت قلوبهم عامرة بالتقوى ممتلئة ايمانا ويقينا وكانوا اكثر تلاوة للقرآن واشد تمسكا بأحكامه واكثر رغبة وتنافسا في نسخه وكتابته واهدائه لبعضهم وجعله في المساجد ودور العلم والتدريس رجاء الاجر والثواب.
ذكر ابن خلكان عند ترجمة اسحاق بن مرامر الشيباني النحوي اللغوى قال ولده عمرو لما رجع (١) أبى اشعار العرب ودونها كانت نيفا وثمانين قبيلة وكان كلما عمل منها قبيلة واخرجها إلى الناس كتب مصحفا وجعله بمسجد الكوفة حتى كتب نيفا وثمانين مصحفا بخطه اه فكم مثل اسحق من كتب عدة مصاحف يقصد بها وجه الله تعالى ونفع المسلمين ولقد ذكرنا في كتابنا تاريخ الخط العربي وآدابه طائفة ممن كتبوا القرآن الكريم عدة مرات فراجعه هناك ولم نذكرهم هنا حتى لا نخرج عن الموضوع.
فقارن رحمك الله بين ايامنا وايامهم ورجالنا ورجالهم فلا حول
_________
(١) قال في المصباح المنير ورجعت الكلام وغيره أي رددته وبها جاء القرآن قال تعالى (فان رجعك الله) ..اه المراد منه (*)',NULL,'183',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (187,'ولا قوة الا بالله اللهم تداركنا برحمتك ولطفك وبرك واحسانك انك على كل شئ قدير.
هذا ولما ظهرت المطابع في زماننا قل اشتغال الناس بكتابة المصاحف ونسخها لكن لا يزال الملوك والامراء والاثرياء المثقفون إلى يومنا هذا يفتخرون باقتناء المصاحف الخطية القيمة ويسندون نسخها وكتابتها إلى من اشتهر بحسن الخط ويصرفون على ذلك المبالغ الطائلة بسخاء وكرم فيكون المصحف المعتنى بنسخه وكتابته قيما جميلا جديرا بالمحافظة عليه ليبقى اثرا خالدا.
والمصاحف في العهد الاول كانت تكتب بأنواع متعددة بالخط الكوفى إلى القرن الخامس تقريبا، ثم لما تنوعت الخطوط صاروا
يكتبونها بخط الثلث إلى القرن الثامن أو التاسع، ولما ظهر خط النسخ الذي هو من اجمل الخطوط صاروا يكتبونها به إلى عصرنا الحاضر (١) والحق يقال ان جمال المصاحف لا يظهر الا إذا كتبت بخط النسخ فقط اما بقية انواع الخطوط فلا يستحسن كتاباتها بها كخط الرقعة والديوانى والفارسي وسياقت وشكسته لان قاعدة هذه الخطوط
_________
(١) يوجد في دار الكتب العربية بمصر كثير من المصاحف القيمة الاثربة المكتوبة بخطوط متنوعة من القرن الاول للهجرة إلى عصرنا الحاضر وقد ذكرنا شيئا منها في كتابنا تاريخ الخط العربي فراجعه (*)',NULL,'184',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (188,'عدم تشكيلها والمصاحف يجب تشكيلها صيانة للقارئ من اللحن.
بل قد يحرم كتابتها ببعض هذه الخطوط كخط سياقت وشكسته فان هذين الخطين لا يعرفهما احد في جميع البلدان العربية ويندر جدا من يعرفهما في بلاد الترك والعجم وقد وضعنا صورتهما وتكلمنا عنهما في كتابنا تاريخ الخط العربي وآدابه فراجعه.
فكتابة المصاحف بالخطين المذكورين يؤدى إلى الخلل والتحريف وهذا لا يجوز، فان عم انتشارهما في البلاد الاسلامية ارتفع المحظور ولم يبق للتحريم وجه وقد بسطنا القول في هذا الموضوع في أول كتابنا تاريخ الخط المذكور عند شرح الاحاديث الواردة في الخط والكتابة فراجعه ان شئت.',NULL,'185',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (189,'ظهور المطابع كان أول اختراع المطابع في ألمانيا سنة ١٤٣١ ميلادية وبالضرورة
مضت مدة طويلة حتى أتقنت صناعتها وظهرت صلاحيتها، فدخلت اولا في بلاد ايطاليا ثم فرنسا ثم في انجلترا ثم انتشرت في جميع البلدان.
وفي عصرنا الحاضر تقدمت صناعة المطابع وادخل فيها من التحسينات الفنية ما لم يكن في الحسبان - وان أول مصحف طبع بالخط العربي كان في همبرج بألمانيا سنة ١١١٣ هجرية ويوجد من هده الطبعة مصحف بدار الكتب العربية بمصر القاهرة، كما يوجد بها مزامير داود عليه السلام بأربع لغات مع تفسير لاتيني طبعت في جنوة بايطاليا سنة ٩٣٥ هجرية، وبعد سنة ١٥١٦ ميلادية طبع المصحف ايضا في البندقية بايطاليا وسبب طبع المصحف الكريم في همبرج والبندقية وجود المطابع فيهما دون البلاد الاسلامية كما هو ظاهر.
ومن العجيب انه عند اول ظهور المطبعة في ايطاليا طعن علماؤهم فيها طعنا جارحا حتى قالوا انها بدعة همجية ألمانية ونادى كهنتهم لنهدم كيان الطباعة أو تهدم هي كياننا * وفى ابتداء ظهور الكتب المطبوعة لم يقبل الناس على شرائها.',NULL,'186',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (190,'ولما دخلت المطبعة إلى تركيا في زمن السلطان احمد الثالث أفتت مشيخة الاسلام بجواز استعمالها الا انه بقى طبع المصحف ممنوعا ثم عادت الدولة العثمانية فمنعت المطبعة ثم جاء السلطان عبد الحميد الاول فأعادها وجاء السلطان محمود الاول فاهتم بها اكثر * واول كتاب طبع بالاستانة " صحاح الجوهرى " قيل انه في سنة ١١٢٩ هجرية أفتى شيخ الاسلام بالاستانة عبد الله افندي بجواز طبع الكتب غير الدينية (١) وفيما بعد سنة ١١٤١ هـ طبعت كتب هامة في اللغة والادب والتاريخ بالعربية
والتركية والفارسية ثم استصدروا الفتوى بطبع كتب الدين وتجليد القرآن الكريم * ومن اشهر مطابع الاستانة القديمة مطبعة الجوائب واول من ادخلها إلى الديار التونسية محمد باشا باى الذى تولى امارة تونس عام ١٢٧١ هجرية * واول مطبعة ظهرت في حلب كان سنة ١٦٩٨ ميلادية * ومن اقدم المطابع في لبنان مطبعه قزحيا وكانت احرفها سريانية ثم صارت عربية ومطبعة الشوير وطبع فيها المزامير سنة ١٧٣٣ ميلادية * واقدم المطابع في بيروت مطبعة القديس جاورجيوس
_________
(١) وقيل كانت فتوى شيخ الاسلام المذكور بجواز الطباعة في سنة ١١٠٣ هجرية وبالرجوع إلى المصادر التركية تظهر الحقيقة.
(*)',NULL,'187',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (191,'فانها انشئت سنة ١٧٥٣ ميلادية * وبعدها المطبعة الامريكية انشئت في مالطة سنة ١٨٢٢ ميلادية ثم نقلت إلى بيروت سنة ١٨٣٤ ميلادية وبعدها المطبعة الكاثوليكية للآباء اليسوعيين تأسست عام ١٨٤٨ ميلادي وكانت تطبع على الحجر ثم صارت تطبع على الحروف سنة ١٨٥٤ ميلادية وهى أكبر المطابع وفيها حروف عربية وافرنجية ويونانية وسريانية وعبرانية وارمنية.
وأول مطبعة ظهرت بمصر مطبعة الحملة الفرنساوية جاء بها بونابرت معه سنة ١٧٩٨ ميلادية لطبع المنشورات والاوامر باللغة العربية وقد سميت بالمطبعة الاهلية وكانت بالقاهرة إلى يونيو سنة ١٨٠١ ميلادية حين انسحاب الفرنساويين من مصر وبعد ذلك ظلت مصر نحو عشرين عاما بغير مطبعة حتى استقر الامر لمحمد على باشا فأنشأ المطبعة الاهلية سنة ١٨٢١ ميلادية وتعرف بمطبعة بولاق لانها وضعت اخيرا في بولاق واول ما طبع فيها قاموس ايطالى عربي سنة ١٨٢٢ ميلادية وقد
اشتغلت هذه المطبعة اكثر من تسعين عاما وكانت اكبر مطبعة عربية في العالم وهى التى تسمى بالمطبعة الاميرية * ثم كثرت المطابع الآن بمصر على مختلف انواعها كما ادخلت عليها تحسينات عظيمة حسب التطور الحديث',NULL,'188',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (192,'ومن اراد التوسع في البحث عن تاريخ الطباعة العربية فليراجع الجزء الرابع من كتاب تاريخ آداب اللغة العربية لجورجي زيدان ومجلة الهلال لسنتي ٩ و ٢٢، ومجلة المشرق لسنتي ٣ و ٤، والمقتطف لسنة ٧، وتاريخ جودت الجزء الاول.
واول مطبعة ظهرت بمكة المشرفة هي المطبعة الاميرية التى سميت فيما بعد (بمطبعة ام القرى) وهى التى اطلق عليها الآن (مطبعة الحكومة) والذي استحضرها عثمان باشا نوري الذى كان واليا على الحجاز في عهد الاتراك أتى بها في سنة ١٣٠٣ هجرية تقريبا وجعلها في المكان الذى هي فيه الآن بأجياد وكانت بادئ امرها صغيرة الحجم لكن في وقتنا الحاضر اهتمت الحكومة السعودية بها وجلبت لها كثيرا من ادوات الطباعة وآلاتها الحديثة.
وفى عام ١٣٢٧ هـ تقريبا استحضر الشيخ محمد ماجد الكردى رحمه الله تعالى مطبعة على حسابه الخاص سماها (المطبعة الماجدية) وجعلها في داره الكائنة بحارة القرراة وقد طبع بها كثير من الكتب ثم استحضر الشيخ محمد صالح نصيف مطبعة في عام ١٣٤٥ هـ تقريبا سماها (المطبعة السلفيه) وهى معطلة الآن لا تستلمل وللشركة العربية للطبع والنشر مطبعة تسمى (المطبعة العربية) ومحلها بالشامية.',NULL,'189',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (193,'واما في المدينة المنورة ففيها مطبعة السيد عثمان حافظ استوردها في سنة ١٣٥٥ تقريبا على حسابه الخاص، واما في جدة ففيها مطبعتان الاولى (مطبعة رمزي) جلبت في سنة ١٣٢٩ هـ تقريبا، والثانية (مطبعة الفتح الوطنية) لصاحبها: المحترم الشيخ عبد الرحيم عبد الفتاح وفيها طبع كتابنا تاريخ القرآن الكريم وقد تأسست في سنة ١٣٥٠ هـ
<span data-type="title" id=toc-32>الفصل الرابع (في عدم جواز قراءة القرآن وكتابته بغير العربية)</span>
اتفقت الائمة (١) على عدم جواز ترجمة القرآن وكتابته وقراءته بغير العربية لان ذلك يؤدى إلى التحريف والتبديل بلا شك اذلا يعقل ترجمته ترجمة حرفية بالمثل - اما الترجمة التفسيرية فلا بأس بها
_________
* (١) قيل ان الامام أبا حنيفة جوز قراءة القرآن بالفارسية في خصوص الصلاة للعاجز عن العربية ولقد قال الالوسى في تفسيره عند قوله تعالى " وانه لفى زبر الاولين " ان ابا حنيفة رجع عن قوله هذا كما صححه جمع من الثقاة المحققين.
(*)',NULL,'190',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (194,'لانها تشرح معانيه وتبين غوامضه وفى هذا الموضوع مؤلفات خاصة تكفى الاشارة هنا إلى حكم ذلك.
وكيف يمكن كتابته أو ترجمته حرفيا باللغات الاجنبية ومخارج حروفها ليست كمخارج الحروف العربية وعدد حروف هجائها قد يزيد عنها وقد ينقص ومن هنا يعلم استحالة ترجمته حرفيا بغير اللغة العربية
(فالمصحف له مكانة خاصة) وحرمة كبيرة لدى كافة المسلمين في مشارق الارض ومغاربها، فكما اتفقت الائمة على عدم جواز كتابته بغير الرسم العثماني محافظة على هيئة كتابته الاولى، اتفقت ايضا على عدم جواز كتابته وترجمته حرفيا بغير اللغة العربية خوفا من التغيير والتبديل الذى لابد من حصوله بالترجمة، واتفقت ايضا على عدم جواز مسه أو حمله للمحدث ولو حدثا أصغر كما هو مبسوط في كتب الفقه وكيف لا يكون جديرا بالاحترام والتعظيم وهو كلام الخبير اللطيف، واساس الدين الحنيف، وقد قال فيه سبحانه وتعالى " لو انزلنا هذا القرآن على جبل لرأيته خاشعا متصدعا من خشية الله ".
وقال فيه عليه الصلاة والسلام من ضمن الحديث الذى اخرجه الترمذي " هو حبل الله المتين وهو الذكر الحكيم وهو الصراط المستقيم وهو الذى لا تزيغ به الاهواء ولا تلتبس به الالسنة ولا تشبع منه العلماء ولا يخلق على كثرة الرد ولا تنقضي عجائبه ".',NULL,'191',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (195,'<span data-type="title" id=toc-33>الفصل الخامس (في عدد أجزاء القرآن وانصافه وسوره وآياته وحروفه)</span>
عدد سور القران مائة واربع عشرة سورة ومن عدها مائة وثلاث عشرة سورة يجعل الانفال وبراءة سورة واحدة وعدد اجزائه ثلاثون جزءا.
وأما أنصافه فقد قال بعض القراء: القرآن العظيم له أنصاف باعتبارات فنصفه بالحروف النون من " نكرا " في الكهف والكاف من النصف الثاني * ونصفه بالكلمات الدال من قوله " والجلود " في الحج
وقوله ولهم مقامع من النصف الثاني * ونصفه بالآيات ياء " يأفكون " من سورة الشعراء وقوله فألقى السحرة من النصف الثاني * ونصفه على عدد السور آخر الحديد والمجادلة من النصف الثاني * وهو عشره بالاحزاب، وقيل ان النصف بالحروف الكاف من " نكرا " وقيل الفاء من قوله " وليتطف ".
(وأما آياته) فعددها ستة الاف ومائتان وست وثلاثون آية وهذا على حسب المصحف الاميري الذى طبعته الحكومة المصرية عام ١٣٤٢ قال الامام الدانى اجمعوا على ان عدد آيات القران ستة الاف',NULL,'192',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (196,'اية ثم اختلفوا فيما زاد على ذلك فمنهم من لم يزد ومنهم من قال ومائتا آية وأربع آيات وقيل واربع عشرة وقيل وتسع عشرة وقيل وخمس وعشرون وقيل وست وثلاثون اه.
وتختلف الاعداد التى يعدون بها في سائر الآفاق إلى ستة أو سبعة كالعدد الكوفى والعدد البصري والعدد المكى والعدد المدنى.
الخ ويعلم كل ذلك من كتب القراءات، وقد بين ذلك شيخ المقارئ المصرية سابقا الشيخ محمد بن على بن خلف الحداد رحمه الله تعالى في كتابه سعادة الدارين في بيان وعد آى معجز الثقلين فارجع إليه ان شئت فان معرفة الآيات ضرورية ولها جملة فوائد لم نتعرض لذكرها خوف التطويل.
قال السخاوى في جمال القراء (فان قيل) فما الموجب لاختلافهم في عدد آى القرآن (قلت) النقل والتوقيف (فان قيل) فلو كان ذلك توقيفيا لم يقع اختلاف (قلت) الامر في ذلك على نحو من اختلاف القراءات وكلها مع الاختلاف راجع للنقل..الخ
(واما عدد كلماته) فقيل سبع وسبعون ألف كلمة وتسعمائة وأربع وثلاثون كلمة وقيل وأربعمائة وسبع وثلاثون، قال للسيوطي وسبب هذا الاختلاف في عد الكلمات ان الكلمة لها حقيقة ومجاز ولفظ ورسم واعتبار * كل منها جائز وكل من العلماء اعتبر احد الجوائز.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (197,'(واما عدد حروفه) فقيل ثلاثمائة ألف حرف وثلاثة وعشرون ألف حرف وستمائة حرف وسبعون حرفا وقيل غير ذلك، قال السيوطي والاشتغال باستيعاب ذلك مما لا طائل تحته وقد استوعبه ابن الجوزي في فنون الافنان وعد الانصاف والاثلاث إلى الاعشار واوسع القول في ذلك فراجعه منه اه.
وذكر بعضهم ان في القرآن كذا وكذا من الالف وكذا وكذا من الباء وكذا وكذا من التاء وهلم جرا إلى آخر الحروف الهجائية وفي أول حاشية الجمل على تفسير الجلالين بيان عدد كل ذلك وقال فيه ان عدد جلالات القرآن ألفان وستمائة وأربعة وستون، وقد ذكر ايضا ابن كثير رحمه الله تعالى في أول تفسيره عدد آيات القرآن وكلماته وحروفه وقد ذكر بعضهم أن عدة النقط على حروفه ألف ألف وخمس وعشرن ألفا وثلاثون نقطة كما جاء ذلك في حاشية اسنى المطالب، والحقيقة ان عد كلمات القرآن وحروفه أمر لا يستهان به إذ يحتاج إلى صبر وجلد عظيمين وإلى انتباه تام فقل من يتصدى لذلك.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (198,'الخاتمة
(وفيها خمس فوائد) (الفائدة الاولى - في فضائل القرآن) اعلم ان حفظ القرآن في الصدور فرض كفاية على الامة وكذلك تعليمه، اما نسيانه فكبيرة كما صرح به النووي في الروضة وغيرها لحديث ابى داود وغيره عرضت على ذنوب امتى فلم أر ذنبا أعظم من سورة من القرآن أو آية أوتيها رجل ثم نسيها، وفى الصحيحين تعاهدوا القرآن فو الذى نفس محمد بيده لهو أشد تفلتا من الابل في عقلها وفيهما ايضا مثل القرآن مثل الابل المعقلة إن عقلها صاحبها أمسكها وإن تركها ذهبت.
(اخرج) الشيخان لا حسد الا في اثنتين رجل علمه الله القرآن فهو يتلوه آناء الليل والنهار فسمعه جار له فقال ليتنى أوتيت ما أوتى فلان فعملت مثل ما يعمل ورجل آتاه الله مالا فهو يهلكه في الحق فقال رجل ليتنى أوتيت مثل ما أوتى فلان فعملت مثل ما يعمل.
(واخرج) الشيخان ايضا وغيرهما الماهر بالقرآن مع السفرة الكرام البررة والذى يقرأ القرآن ويتعتع فيه وهو عليه شاق له اجران.
(واخرج) احمد والترمذي ما من مسلم يأخذ مضجعه فيقرأ سورة',NULL,'195',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (199,'من كتاب الله تعالى الا وكل الله به ملكا يحفظه فلا يقربه شئ يؤذيه حتى يهب متى هب.
(واخرج) احمد من حديث معاذ بن انس من قرأ القرآن في سبيل الله كتب مع الصديقين والشهداء والصالحين وحسن أولئك رفيقا (وأخرج) مسلم من حديث جابر بن عبد الله خير الحديث كتاب
الله الخ (وأخرج) البخاري عن عثمان بن عفان قال قال النبي صلى الله عليه وسلم ان افضلكم من تعلم القرآن وعلمه (واخرج) النسائي وغيره من حديث أنس قال اهل القرآن هم اهل الله وخاصته (واخرج) الطبراني من حديث أنس جملة القرآن عرفاء اهل الجنة (واخرج) الطبراني من حديث انس ايضا من قرأ القرآن يقوم به آناء الليل والنهار يحل حلاله ويحرم حرامه حرم الله لحمه ودمه على النار وجعله مع السفرة الكرام البررة حتى إذا كان يوم القيمة كان القرآن حجة له (واخرج) مسلم اقرؤا القرآن فانه يأتي يوم القيمة شفيعا لاصحابه (واخرج) الحاكم من حديث عبد الله بن عمر الصيام والقرآن يشفعان للعبد (واخرج) الديلى من حديث على حملة القرآن في ظل الله يوم لا ظل الا ظله (واخرج) الحاكم من حديث ابى هريرة يجئ صاحب القرآن يوم القيمة فيقول القرآن يا رب حله فيلبس تاج الكرامة ثم يقول يا رب',NULL,'196',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (200,'زده يا رب ارض عنه فيرضى عنه ويقال له اقرأ وارق وبزاد له بكل آية حسنة (واخرج) البيهقى من حديث عائشة البيت الذى يقرأ فيه القرآن يتراءى لاهل السماء كما تتراءى النجوم لاهل الارض (واخرج) ابن ماجه وغيره من حديث ابن مسعود عليكم بالشفاءين العسل والقرآن (واخرج) ايضا من حديث على خير الدواء القرآن (واخرج) البيهقى في الشعب عن وائلة بن الاسقع ان رجلا شكى إلى النبي صلى الله عليه وسلم وجع حلقه قال عليك بقراءة القرآن (واخرج) الطبراني من حديث ابن عمر ثلاثة لا يهو لهم الفزع الاكبر ولا ينالهم الحساب هم على كثيب من مسك حتى يفرغ من حساب الخلائق، رجل قرأ القرآن ابتغاء وجه الله
وأم به قوما وهم به راضون الحديث (واخرج) الشيخان مثل الذى يقرأ القرآن كمثل الاترجة طعمها طيب وريحها طيب والذى لا يقرأ القرآن كالتمرة طعمها طيب ولا ريح لها ومثل الفاجر الذى يقرأ القرآن كمثل الريحانة ريحها طيب وطعمها طيب مر ومثل الفاجر الذى لا يقرأ القرآن كمثل الحنظلة طعمها مر ولا ريح لها (واخرج) مسلم ما اجتمع قوم في بيت من بيوت الله يتلون كتاب الله ويتدارسونه بينهم الا تنزلت عليهم السكينة وغشيتهم الرحمة وحفتهم الملائكة وذكرهم الله فيمن عنده (وقال) رسول الله صلى الله عليه وسلم يقال لصاحب القرآن اقرأ وارق',NULL,'197',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (201,'ورتل كما كنت ترتل في الدنيا فان منزلك عند آخر آية نقرؤها (وقال) عليه الصلاة والسلام يقول الله تعالى من شغله قراءة القرآن عن دعائي اعطيته افضل ثواب الشاكرين (وقال) صلى الله عليه وسلم ان الله يرفع بهذا الكتاب أقواما ويضع به آخرين رواه مسلم (وعن أنس مرفوعا) سبع يجرى للعبد أجرهن بعد موته وهو في قبره من علم علما أو أجرى نهرا أو حفر بئرا أو غرس نخلا أو بنى مسجدا أو ترك ولدا يستغفر له من بعد موته أو ورث مصحفا رواه ابن ماجه وغيره.
(هذه نبذة) مما ورد في فضائل القرآن جملة وقد ورد كثير من الاحاديث في فضل سور بعينها لم نذكرها خوف التطويل.
(الفائدة الثانية في الاكثار من تلاوة القرآن الكريم) ذكر الآله للقلوب قوت * إذا انتفى فانها تموت يستحب الاكثار من تلاوة القرآن قال الله تعالى مثنيا على تاليه " يتلون آيات الله آناء الليل " وقال " ان قرآن الفجر كان مشهودا "
أي تشهده الملائكة وقال " ان الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور ليوفيهم أجورهم ويزيدهم من فضله انه غفور شكور " وقال " كتاب انزلناه اليك مبارك ليدبروا آياته وليتذكر أولوا الالباب " وقال " الله نزل',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (202,'أحسن الحديث كتابا متشابها مثانى تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم إلى ذكر الله ذلك هدى الله يهدى به من يشاء ومن يضلل الله فما له من هاد " وقال رسول الله صلى الله عليه وسلم اقرؤا القرآن فانه يأتي يوم القيمة شفيعا لاصحابه رواه مسلم في صحيحه (واخرج) البيهقى من حديث النعمان ابن بشير أفضل عبادة امتى قراءة القرآن (وفى الحديث) يقول الله تعالى من شغله قراءة القرآن عن دعائي اعطيته افضل ثواب الشاكرين (وفي البخاري) اقرؤا القرآن ما ائتلفت عليه قلوبكم فإذا اختلفتم فقوموا عنه.
وروى الامام احمد عن ابى سعيد الخدرى ان رسول الله صلى الله عليه وسلم قال اوصيك بتقوى الله تعالى فانه رأس كل شئ وعليك بالجهاد فانه رهبانية الاسلام وعليك بذكر الله تعالى وتلاوة القرآن فانه روحك (١) في السماء وذكرك في الارض.
قال الليث في البستان: ينبغى للقارئ ان يختم في السنة مرتين ان لم يقدر على الزيادة، وروى عن ابى حنيفة انه قال من قرأ القرآن في كل سنة مرتين فقد أدى حقه لان النبي صلى الله عليه وسلم عرض على جبريل
_________
(١) قال العزيرى على الجامع الصغير روحك بفتح الراء اي راحتك (*)',NULL,'199',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (203,'في السنة التى قبض فيها مرتين * وقال غيره يكره تأخير ختمه اكثر من اربعين يوما بلا عذر نص عليه احمد لان عبد الله بن عمر سأل النبي صلى الله عليه وسلم في كم نختم القرآن قال في اربعين يوما رواه أبو داود واخرج الشيخان عن عبد الله بن عمرو قال قال لى رسول الله صلى الله عليه وسلم اقرأ القرآن في شهر قلت انى اجد قوة قال اقرأه في عشر قلت انى اجد قوة قال اقرأه في سبع ولا تزد على ذلك.
قال النووي في الاذكار المختار ان ذلك يختلف باختلاف الاشخاص فمن كان يظهر له بدقيق الفكر لطائف ومعارف فليقتصر على قدر يحصل له معه كمال فهم ما يقرأ وكذلك من كان مشغولا بنشر العلم أو فصل الحكومات أو غير ذلك من مهمات الدين والمصالح العامة فليقتصر على قدر لا يحصل بسببه اخلال بما هو مرصد له ولا فوات كماله وان لم يكن من هؤلاء المذكورين فليكثر ما امكنه من غير خروج الا حد الملل أو الهذرمة في القراءة اه وقد كره غير واحد من السلف قراءة القرآن في اقل من ثلاث لقوله صلى الله عليه وسلم من قرأ القرآن في اقل من ثلاث لم يفقه ونهى عليه الصلاة والسلام عن الهذرمة بالقرآن (١)
_________
(١) هذر في منطقة خلط والهذر بفتحتين الهذيان.
(*)',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (204,'وعن ابى حمزة قال قلت لابن عباس انى سريع القراءة وانى اقرأ القرآن في ثلاث فقال لان أقرأ البقرة في ليلة فادبرها وأرتلها احب إلى من أن أقرأكما تقول (وفي تفسير ابن كثير) قال الاعمش عن أبى
وائل عن ابن مسعود قال كان الرجل منا إذا تعلم عشر آيات لم يجاوزهن حتى يعرف معانيهن والعمل بهن وقال أبو عبد الرحمن السلمى حدثنا الذين كانوا يقرئوننا انهم كانوا يستقرئون من النبي صلى الله عليه وسلم وكانوا إذا تعلموا عشر آيات لم يخلفوها حتى يعملوا بما فيها من العمل فتعلمنا القرآن والعمل جميعا اه.
الفائدة الثالثة (في تجويد القرآن العظيم) قال في عنوان البيان فرض الله تعالى على الامة ضبط القرآن وتعلمه وروايته على الوجه الذى نزل به بمعنى انه يجب ان يكون في كل عصر طائفة من الامة تبلغ حد التواتر يقومون بتحمله وروايته باللغة التى نزل بها ويحفظونه من التحريف والتغيير والتبديل وان يكون فيهم من يعرف اوجه القراءات والطرق والكيفيات المتلقاة من افواه المشايخ طبقة عن طبقة إلى رسول الله صلى الله عليه وسلم، ثم قال وقد اجمعوا على ان النقص في كيفية القرآن وهيئته كالنقص في ذاته ومادته فترك المد',NULL,'201',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (205,'والغنة والتفخيم والترقيق كترك حروفه وكلماته ومن هنا وجب تجويد القرآن كما قال ابن الجزرى.
والاخذ بالتجويد حتم لازم * من لم يجود القرآن آثم لانه به الاله انزلا * وهكذا منه الينا وصلا والتجويد هو اعطاء الحروف حقها وترتيلها ورد كل حرف إلى مخرجه واصله وتلطيف النطق به على كمال هيئته من غير اسراف ولا تعسف ولا افراط ولا تكلف، قال ابن الجزرى ولا اعلم لبلوغ النهاية في التجويد مثل رياضة الالسنة والتكرار على اللفظ المتلقى من فم
المحسن، ثم قال واهل الصدر الاول ما كانوا يقرءون القرآن ولا يعلمونه الاطفال الا مرتلا مجودا حتى لا يخرج الصبى من المكتب الا على رياضة تامة ومعرفة بتلاوة القرآن وترتيله لا ينقصه الا معرفة الاحكام والاصطلاحات الفنية التى يسمونها الآن علم التجويد.
الفائدة الرابعة (في آداب تلاوة القرآن) (يستحب) الترتيل في القراءة قال الله تعالى " ورتل القرآن ترتيلا " لانه أقرب إلى الاجلال والتوقير وأشد تأثيرا في القلب',NULL,'202',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (206,'قال ابن مسعود لا تنثروه نثر الدقل (١) ولا تهذوه هذ الشعر (٢) قفوا عند عجائبه وحركوا به القلوب ولا يكون هم احدكم آخر السورة قال الشيخ الاخضري رحمه الله تعالى وانما يتلى بالارعواء * والحزن والخشوع والبكاء فواجب تقديس ذكر الله * عن فعل كل عابث ولاه اخرج البيهقى من حديث ابن عمر مرفوعا من قرأ القرآن فأعربه كان له بكل حرف عشرون حسنة ومن قرأه بغير اعراب كان له بكل حرف عشر حسنات، والمراد باعرابه معرفة معاني ألفاظه.
(ويستحب) تحسين الصوت بالقراءة لقوله صلى الله عليه وسلم زينوا القرآن بأصواتكم رواه ابن ماجه، وروى مسلم عن ابى موسى الاشعري ان رسول الله صلى الله عليه وسلم قال له يا ابا موسى لو رأيتنى وانا استمع لقراءتك البارحة فقال اما والله لو اعلم انك تستمع قراءتى لحبرتها لك تحبيرا وقال الزهري عن أبى سلمة كان عمر إذا رآى أبا موسى قال ذكرنا ربنا يا أبا موسى
فيقرأ عنده، وقال النبي صلى الله عليه وسلم فيه لقد أوتى مزمارا من مزامير آل داود
_________
(١) الدقل بفتحتين أردأ التمر (٢) هذ قراءته هذا أسرع فيها وهو بالذال المعجمة (*)',NULL,'203',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (207,'وقال عثمان النهدي كان ابو موسى يصلى بنا فلو قلت انى لم اسمع صوت صنج قط ولا بربط قط (١) ولا شيئا قط أحسن من صوته.
والحقيقة ان قراءة القرآن بالصوت الحسن تهيج الارواح وتحرك القلوب وتوقظ النفس عن غفلتها وتطرد الملل والسآمة عن الفؤاد، هذا إذا كان في حدود التوقير والتعظيم، اما ما كان في قالب الطرب والغناء فهو المنهى عنه، قال ابن كثير في كتاب فضائل القرآن فأما الاصوات بالنغمات المحدثة المركبة على الاوزان والاوضاع الملهية والقانون الموسيقائى فالقرآن ينزه عن هذا ويجل ويعظم أن يسلك في أدائه هذا المذهب وقد جاءت السنة بالزجر عن ذلك اه قال بعضهم: واحذر من التطريب كالغناء * واحذر من التحزين للرياء واحذر من الترعيد والتحريف * فان ذا من سائر التحريف قال الرافعى في كتابه اعجاز القرآن: التطريب هو أن يترنم بالقرآن ويتنغم به فيمد في غير مواضع المد ويزيد في المد إن اصاب موضعه - والتحزين هو أن يأتي بالقراءة على وجه حزين يكاد يبكى مع خشوع وخضوع (٢) - والترعيد هو أن يرعد القارئ صوته
_________
(١) الصنج والبربط آلتان من آلات اللهو (٢) القراءة بالحزن والخشوع بنية صادقة لا تكره وانما تكره للرياء كما هو صريح في البيتين (*)',NULL,'204',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (208,'كأنه يرعد من البرد أو الالم اه وقال ايضا رحمه الله فيه: أول ما ظهرت القراءة بألحان الغناء كان في المائة الثانية وكان ممن يقرأ بهذه الالحان الهيثم وأبان وابن أعين ومحمد بن سعيد وهذا من أهل المائة الثالثة اه (ويسن) الاستماع لقراءة القرآن وترك اللغط والحديث لقوله تعالى " فإذا قرئ القرآن فاستمعوا له وأنصتوا لعلكم ترحمون " (ويسن) أن يستاك عند القراءة فقد ورد " أن افواهكم طرق للقرآن فطيبوها بالسواك " وأن يجلس مستقبل القبلة بسكينة ووقار، وأن تكون القراءة في مكان لائق والمسجد افضل لانه مكان العبادة (ويسن) التعوذ قبل القراءة لقوله تعالى " فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم " وصفة التعوذ اعوذ بالله من الشيطان الرجيم، وكان جماعة من السلف يزيدون السميع العليم أي يقولون اعوذ بالله السميع العليم من الشيطان الرجيم - وبعد التعوذ يأتي بالبسملة اول كل سورة غير براءة (ويسن) التكبير من خاتمة والضحى إلى خاتمة قل اعوذ برب الناس وصفة التكببر أن يقف القارئ بعد كل سورة وقفة لطيفة ويقول الله اكبر وقيل لا اله الا الله والله اكبر.
(ويسن) إذا فرغ من الختمة أن يشرع في أخرى عقب الختم',NULL,'205',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (209,'لحديث الترمذي وغيره " أحب الاعمال إلى الله الحال المرتحل الذى يضرب من أول القرآن إلى آخره كلما حل ارتحل " واخرج الدارمي بسند حسن عن ابن عباس عن أبى بن كعب أن النبي صلى الله عليه وسلم كان إذا قرأ
قل أعوذ برب الناس افتتح من الحمد ثم قرأ من البقرة إلى وأولئك هم المفلحون ثم دعا بدعاء الختمة ثم قام (والدعاء) عند الختم مستجاب وعنده تنزل الرحمة، قال الامام النووي يستحب الدعاء بعد قراءة القرآن استحبابا ويتأكد تأكيدا شديدا وقد نص الامام أحمد على استحبابه ايضا، أخرج الطبراني عن العرباض بن سارية مرفوعا من ختم القرآن فله دعوة مستجابة، وأخرج أيضا عن أنس أنه كان إذا ختم القرآن جمع أهله ودعا - لذلك كانوا يجتمعون عند ختمه (والافضل) ختم القرآن أول النهار أو أول الليل لما رواه الدارمي بسند حسن عن سعد بن أبى وقاص قال إذا وافق ختم القرآن أول الليل صلت عليه الملائكة حتى يصبح وان وافق ختمه أول النهار صلت عليه الملائكة حتى يمسى',NULL,'206',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (210,'دعاء ختم القرآن (نأتى هنا بدعاء جامع اقتبسناه من جملة أدعية مأثورة وهو:) اللهم ارحمنى بالقرآن واجعله لى إماما ونورا وهدى ورحمة * اللهم ذكرني منه ما نسيت وعلمني منه ما جهلت وارزقني تلاوته آناء الليل وأطراف النهار واجعله لى حجة يا رب العالمين * اللهم أصلح لى دينى الذى هو عصمة أمرى وأصلح لى دنياى التى فيها معاشى وأصلح لى آخرتي التى فيها معادى واجعل الحياة زيادة لى في كل خير واجعل الموت راحة لى من كل شر * اللهم اجعل خير عمرى آخره وخير عملي خواتمه وخير أيامى يوم ألقاك فيه * اللهم إنى أسألك عيشة هنية وميتة
سوية ومردا غير مخزي ولا فاضح * اللهم انى أسألك خير المسألة وخير الدعاء وخير النجاح وخير العمل وخير الثواب وخير الحياة وخير الممات وثبتنى وثقل موازيني وحقق إيمانى وارفع درجتي وتقبل صلاتي واغفر خطيئاتي وأسألك العلا من الجنة * اللهم انى أسألك أن ترفع ذكرى وتضع وزرى وتصلح أمرى وتطهر قلبى وتحصن فرجى وتنور قلبى وتغفر ذنبي * اللهم أحسن عاقبتنا في الامور كلها وأجرنا من خزى الدنيا وعذاب الاخرة * اللهم اقسم لنا من خشيتك ما تحول به بيننا',NULL,'207',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (211,'وبين معصيتك ومن طاعتك ما تبلغنا بها جنتك ومن اليقين ما تهون به علينا مصائب الدنيا ومتعنا بأسماعنا وأبصارنا وقوتنا ما أحييتنا واجعله الوارث منا واجعل ثأرنا على من ظلمنا وانصرنا على من عادانا ولا تجعل مصيبتنا في ديننا ولا تجعل الدنيا أكبر همنا ولا مبلغ علمنا ولا تسلط علينا من لا يرحمنا * اللهم انى أسألك موجبات رحمتك وعزائم مغفرتك والسلامة من كل إثم والغنيمة من كل بر والفوز بالجنة والنجاة من النار * اللهم لا تدع لنا ذنبا الا غفرته ولا هما الا فرجته دينا الا قضيته ولا حاجة من حوائج الدنيا والآخرة الا قضيتها يا ارحم الرحمين * اللهم ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار وصلى الله على نبينا محمد وعلى آله وأصحابه وسلم تسليما كثيرا (آمين) الفائدة الخامسة (في آداب كتابة القرآن) لكتابة القرآن الكريم اداب كثيرة - منها - أن يكون الكاتب على وضوء بل هذا واجب على البالغ العاقل - ومنها - أن يكون على
نظافة في الثوب والبدن والمكان - ومنها - أن يحسن خطه فقد ورد " من كتب بسم الله الرحمن الرحيم مجودة غفر له " وورد أنه صلى الله عليه وسلم قال لكاتبه " ألق الدواة وحرف القلم وأنصب الباء وفرق السين',NULL,'208',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (212,'ولا تعور الميم وحسن الله ومد الرحمن وجود الرحيم " وقد شرحنا هذين الحديثين وغيرهما في أول كتابنا تاريخ الخط العربي وآدابه المطبوع بمصر شرحا دقيقا فنيا لم يطرقه قبلنا احد فانظره فيه فانه مبحث نفيس ومنها - أن يكتبه بحسب الرسم العثماني فاتباع رسمه واجب كما سبق بيان ذلك في هذا الكتاب - ومنها - أن يكتبه بحروف بحيث يقرآ بالبصر الصحيح من غير مشقة في تلاوته.
وقد نظم آداب كتابة القرآن الشيخ محمد العاقب الشنقيطى رحمه الله تعالى في قوله: مما به يهتم كل مسلم * ضبط كتابة الكتاب المحكم فاستقر ما لها من الآداب * واعمل به تسلم من العتاب قبل الشروع ألق الدواة * بصوفة وحرف الاداة وإن أردت كتبه في رق * أو غيره فاكتبه دون مشق وحسن الخط ولا تحرفا * نقط الحروف والحروف جوفا كى لا تجى أسطره مخلطة * ولا تري حروفه مقرمطه وكتبه في الصحف الصغار * يكره كالكتب على الجدار وكتبه على محل يوطأ * أو محوه فيه فذاك خطأ ومن يعظم حرمات الله * فان ذاك من تقى الاله قوله فاكتبه دون مشق أي لا تسرع في الكتابة.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (213,'(تنبيه) كثير من الخطاطين إذا أرادوا أن يكتبوا شيئا من القرآن على ورق أو لوح أو نحوهما يركبون الكلمات بعضها فوق بعض مباعدين حروفها بحيث تصعب قراءتها على من لم يحفظ القرآن وهذا كما لا يخفى لا يجوز لحصول اللبس والاشتباه في القراءة، والسبب في كتابتهم بهذه الصورة انهم ينظرون إلى جمال التركيب الخطى فقط غير ناظرين إلى تفرقة أجزاء الكلمات القرآنية وهذا خطأ فاحش نلفت نظرهم إليه على أن قليلا من العناية والتأمل يهديهم إلى جمال التركيب مع عدم تفرقة الحروف والله الموفق للصواب.
(وانى) استغفر الله العظيم من هفوة القلم، وزلة القدم فانه غفور رحيم، وليكن ختام الكتاب بأربع أبيات من نظمى إقرارا بوحدانية الله راجيا منه تعالى أن يثبتني بالقول الثابت في الحياة الدنيا وفي الآخرة بفضله ورحمته وهى: (الامر لله ليس الامر للفلك * ولا لزيد ولا عمرو ولا ملك) (ما شاء كان وما لم لم يكن أبدا * فما هنالك مخلوق بمشترك) (تنزه الله عن أهل وعن ولد * وعن شريك فما في الامر من شكك) (إليه وجهت وجهى دائما أبدا * له صلاتي وصومي مخلصا نسكى)',NULL,'210',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (214,'(نسأل الله الحى القيوم لذى لا يموت * أن يعاملنا بما هو أهله) (وأن يسترنا في الدارين ويجعلنا من الذين لا خوف عليهم ولا هم يحزنون) (ربنا آتنا في الدنيا حسنة وفى الآخرة حسنة وقنا عذاب النار وأدخلنا) (الجنة مع الابرار امين وصلى الله على نبينا محمد وعلى اله وصحبه أجمعين)
(سبحان ربك رب العزة عما يصفون وسلام على المرسلين) (والحمد لله رب العالمين) (ولقد كان طبعه على نفقة الفاضل الشيخ مصطفى محمد يغمور بمكة) (ومن عجيب الاتفاق ان تمام طبع هذا الكتاب كان في يوم سفر مؤلفه) (من جدة إلى مصر وكردستان للزيارة وهو يوم الجمعة الموافق) (عشرين شعبان عام ألف وثلاثمائة وخمس وستين من) (الهجرة النبوية * وان طبعه بهذا الرونق) (الجميل وظهوره بهذا الشكل البديع) (كان في مطبعة الفتح الوطنية) (لصاحبها المحترم الشيخ) (عبد الرحيم عبد الفتاح) (بجدة بالحجاز)',NULL,'211',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (215,'(قال محمد طاهر الكردى المكى الخطاط) (مؤلف هذا الكتاب في الحكم والامثال) حركات المرء تدل على عقله * حفظ المعروف من المروءة * لا تهد شيئا لمن لا يقدره * تقدير الاعمال يزيد نشاط العمال * مراعاة احساس الاصدقاء تقوى حبل الصداقة * العزيز إذا افتقر هان * دوام العزلة يميت النشاط والهمة * الاعتراف بالاحسان من كمال الانسان * إذا افتقر العاقل تعرض للزلل * الاستبداد والقسوة يورثان البلادة والجفو * ة هضم الحقوق موجب للعقوق * لا ينهض المرء بفقره * الكريم إذا ضاقت به الاحوال لم يختلط بالناس * المال اساس النجاح * الكريم
بلا مال كالشجاع بلا سلاح * المال يستر العيوب * الاحمق واللئيم يضيع فيهما المعروف * كثرة الخضوع نفاق * لا يشقى من حالفه الحظ * لا تحتقر ضعيف اليوم فقد يصبح غدا عظيما * لا تتودد إلى من لا يعتبرك * الفوضى عاقبتها الفشل * الصبور إذا انتقم بطش * الانهماك في العمل يؤدى إلى الملل * من احترم غيره فقد احترم نفسه (ومن نظمه غفر الله له) كم عاقل فاضل تلقاه مضطربا * وجاهل خامل تلقى به طربا هذا له الحظ في الدنيا وذاك له * عز من الله في أخراه قد وجبا',NULL,'212',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (216,'(ومن نظمه ايضا) لقد استراح من الحياة وكدها * ومن الهموم ورؤية الاهوال من مات أو من جن أو متبتل * لزم القناعة صادق الاحوال (ومن نظمه ايضا) دع الامر تحت القضا والقدر * فما ينفع العقل لا والحذر فمن رام سخطا على ما جرى * فذاك الكفور وشر البشر ومن سلم الامر نال المنى * وما يبتغيه ونال الظفر فصبرا جميلا على ما قضا * هـ الاله عساه يزيل الضرر ولا تتركن الدعا والطلب * فان اللطيف به قد أمر ولا تركبن بحار الهوى * فان المعاصي قرين الخطر (ومن نظمه ايضا متضرعا إلى الله تعالى) زدنى بفرط الابتلاء تصبرا * والطف بما قدرته فيما جرى يا من له عنت الوجوه جميعها * رحماك فالعبد الذليل تحيرا
كم عاقل فاضل تلقاه مضطربا * وجاهل خامل تلقى به طربا هذا له الحظ في الدنيا وذاك له * عز من الله في أخراه قد وجبا',NULL,'213',NULL,NULL,'0');
INSERT INTO [b2228] VALUES (217,'(ومن نظمه ايضا) لقد استراح من الحياة وكدها * ومن الهموم ورؤية الاهوال من مات أو من جن أو متبتل * لزم القناعة صادق الاحوال (ومن نظمه ايضا) دع الامر تحت القضا والقدر * فما ينفع العقل لا والحذر فمن رام سخطا على ما جرى * فذاك الكفور وشر البشر ومن سلم الامر نال المنى * وما يبتغيه ونال الظفر فصبرا جميلا على ما قضا * هـ الاله عساه يزيل الضرر ولا تتركن الدعا والطلب * فان اللطيف به قد أمر ولا تركبن بحار الهوى * فان المعاصي قرين الخطر (ومن نظمه ايضا متضرعا إلى الله تعالى) زدنى بفرط الابتلاء تصبرا * والطف بما قدرته فيما جرى يا من له عنت الوجوه جميعها * رحماك فالعبد الذليل تحيرا إن لم يكن لى منك لطف شامل * أو فضل احسان على مكررا فمن الذى أرجو لكشف بليتي * أو من إليه أميل من بين الورى والكل مفتقر اليك وسائل * من فيض جودك نقطه أن تقطرا لا أرتجى أحدا سواك وأنت لى * نعم الملاذ ومن رجاك استبشرا إنى سألتك والهموم تراكمت * والدهر عاند والزمان تنكرا حاشا تخيب من رجاك مؤملا * مهما جنى أو كان فيك مقصرا',NULL,'213',NULL,NULL,'0');
