INSERT INTO [b22676] VALUES (258,'٢-<span data-type="title" id=toc-20> فهرس الأحاديث:</span>
أ
أحسن الصوت بالقرآن أخشاهم لله تعالى١٩٩
أدع لي زيدًا وليجئ باللوح والدواة والكتف ٣٤
ارموهم بالبعر ٢٠١
أسأل الله معافاته ومغفرته وإن أمتي لا تطيق ذلك ٨١
أعاذك الله من الشك وخسأ عنك الشيطان ... أتاني جبريل ٨٠
أقرأني جبريل عليه السلام على حرف واحد فراجعته فلم أزل ٧٧
أقرأه على سبعة أحرف من سبعة أبواب من الجنة ٨١
اقرأه في شهر ٣٩
اقرءوا القرآن بحزن فإنه نزل بحزن ٢٠٤
اقرءوا القرآن فيوشك أن يأتي قوم يقرءونه ٢٠٧
اقرءوا القرآن ما نهاك فإذا لم ينهك فلست تقرؤه ١٩٤
اقرأ يا هشام ... كذلك أنزلت أن هذا القرآن ٧٨
ألا هلك المتنطعون ٢٠٣
اللهم اخسأ الشيطان عنه، يا أبي أتاني آت من ربي ٨٠
التمسوها في العشر الأواخر من رمضان ٩
الذي إذا سمعته رأيته يخشى الله تعالى ١٩٩
إن أحسن الناس صوتا بالقرآن من إذا سمعته ٢٠٤
إن جبريل كان يعارضني بالقرآن في كل سنة وإنه ٣٥
إن جبريل وميكائيل أتياني فقعد جبريل عن يميني ٨٢','1','258',NULL,NULL,'0');
INSERT INTO [b22676] VALUES (259,'أنزلت صحف إبراهيم ... وأنزل القرآن لأربع وعشرين ١٢
أنزل القرآن على ثلاث أحرف ٨٨
أنزل القرآن على سبعة أحرف ٦، ٧٧، ٩٣، ١٠٢، ١٠٧، ١٣١، ١٣٣، ١٤٦/ ١٤٧/ ١٥٠.
أنزل القرآن ... فاقرءوا ما تيسر منه ٨٥
أنزل القرآن ... فالمراء في القرآن كفر فما عرفتم منه ٨٥
أنا سيد ولد آدم ولا فخر ٥
إن القرآن أنزل على سبعة أحرف فلا تماروا في القرآن ١٠٤
إن القرآن نزل على سبعة أحرف ١٧١
إن القرآن نزل على سبعة أحرف كل حرف منها شاف كاف ١٣٥
إن الكتاب الأول نزل من باب ونزل القرآن ١٣٧
إنما أهلك من كان قبلكم الاختلاف ٨٧
إن ملائكة الرحمة باسطة أجنحتها عليهم ٤٤
إن من إجلال الله عز وجل إكرام ذي السلطان المقسط ٢٠٣
إن هذا القرآن أنزل على سبعة أحرف ١٣٤
إن هذا القرآن أنزل على سبعة أحرف من سبعة أبواب ١٦٠
إن هذا القرآن نزل بحزن فإذا قرأتموه ٢٠٤
إن هذا القرآن نزل على سبعة أحرف فأي ذلك قرأتم ٨٤
إن هذا القرآن على سبعة أحرف فلا تماروا فيه ٨٤
إني بعثت إلى أمة أمية فيهم الشيخ الفاني ١٢٣٦
إني بعثت إلى أمة أميين فيهم الغلام والجارية ١٣٠
خ
خذوا القرآن من أربعة من عبد الله بن مسعود ٣٦
ز
زينوا القرآن بأصواتكم ٢٠٠','1','259',NULL,NULL,'0');
INSERT INTO [b22676] VALUES (260,'ض
ضعوا هذه الآيات في السورة التي يذكر فيها كذا ٣٣
ط
طوبى للشام ٤٤
ف
فاقرءوا ولا حرج ولكن لا تختموا ذكر رحمة بعذاب ٨٥، ١٠٣
ك
كان الكتاب الأول نزل على حرف واحد ونزل القرآن ١٣٧
كان الكتاب الأول نزل من باب واحد على حرف ... القرآن ١٠٧
كنت نبيا وآدم بين الماء والطين ٢٥
ل
لا تماروا في القرآن فإن المراء فيه كفر ١٣٥
لقيت جبريل عند أحجار المراء فقلت يا جبريل ٨٣
لما كان جبريل يستفتح له السماوات سماء سماء ٢٥
المراء في القرآن كفر ١٤١
م
من قرأ القرآن فقام به آناء الليل والنهار يحل حلاله ٢٠٦
من قرأ منها حرفا فهو كما قرأ ٨١
ن
نحن أمة أمية لا نكتب ولا نحسب وإن الشهر ١٣٢','1','260',NULL,NULL,'0');
INSERT INTO [b22676] VALUES (261,'نزل القرآن على سبعة أحرف أيها قرأت أصبت ٨٤
نزل القرآن على سبعة أحرف حكيما غفورًا رحيما ٨٥، ١٢٩
هـ
هذا القرآن أنزل على سبعة أحرف فاقرءوا ولا حرج ١٠٣
هلك المتنطعون ٢٠٣
هون على أمتي ٩٦
ي
يا أبي إن ربي أرسل إلي أن أقرأ القرآن على حرف ٧٩
يا أبي إني أقرئت القرآن فقال لي على حرف فقال الملك ٨٢، ٨٧، ١٠٣
يا جبريل إني بعثت إلى أمة أميين منهم العجوز ٨٢
يخرج في آخر الزمان قوم أحداث الأسنان سفهاء الأحلام ٢٠٣','1','261',NULL,NULL,'0');
