INSERT INTO [b23144] VALUES (201,'<span data-type="title" id=toc-12>الفهرس:</span>
الصفحة الموضوع
٧ -تمهيد
٢٣ -الفصل الأول: ما حول الأرض من نجوم وكواكب:
٢٣ - قصور الحواس البشرية.
٢٥ - المجرة.
٢٩ - الشمس.
٣٠ - انفصال جسم الأرض.
٣٠ - زينة الأرض والسماء.
٣١ - سجود الكائنات وتسبيحها.
٣٣ - الفصل الثاني: الغلاف الجوي حول الأرض:
٣٦ - تطور الغلاف الجوي.
٣٨ - الأوزون.
٤٠ - ارتفاع حرارة الجو.
٤١ - الرياح.
٤٤ - بعض الظواهر الجوية:
٤٤ - الرعد والبرق.
٤٥ - الصاعقة.
٤٦ - قوس قزح.
٤٧ - السراب.
٤٨ - حجارة من السماء:
٤٨ - الشهب والنيازك',NULL,'219',NULL,NULL,'0');
INSERT INTO [b23144] VALUES (202,'٥٠ - الحاصب والسجيل.
٥٢ - الحجر الأسود.
٥٣ - الفصل الثالث: اليابسة:
٥٣ - الأرض:
٥٤ - التركيب الداخلي للكرة الأرضية.
٥٦ - تناقص أطراف الأرض.
٥٨ - المجال المغناطيسي للأرض.
٥٨ - تضاريس اليابسة.
٦٢ - الجبال وصخورها.
٦٥ - الجدد والغرابيب.
٦٨ - صلابة الخر والحجارة.
٦٩ - البلورات.
٧١ - الحركات الأرضية:
٧١ - الحركات البطيئة للقشرة الأرضية.
٧٤ - الحركات السريعة للقشرة الأرضية.
٨٣ - الفصل الرابع: الغلاف المائي للقشرة الأرضية:
٨٤ - الماء عند تكوين الأرض.
٨٥ - منشأ وتطور المحيطات.
٨٦ - تضاريس قيعان المحيطات.
٨٦ - كيمياء الماء.',NULL,'220',NULL,NULL,'0');
INSERT INTO [b23144] VALUES (203,'٨٧ - ملوحة مياه المحيطات والبحار.
٩٢ - السحاب والبرد.
٩٣ - الثلج والجليد.
٩٤ - الماء يسكن اليابسة.
٩٩ - الفصل الخامس: الزمان والمكان:
٩٩ - الزمان:
٩٩ - البداية.
١٠٠ - تركيب الذرة.
١٠١ - العمر المطلق للأرض.
١٠٢ - أسس التأريخ الجيولوجي لقشرة الأرض.
١٠٤ - تقسيم الزمن الجيولوجي.
١١٠ - وحدات قياس الزمن.
١١٥ - الواقعة "النهاية".
١٢٠ - المكان:
١٢١ - الشرق والغرب.
١٢٢ - البوصلة.
١٢٣ - مواقع النجوم.
١٢٥ - الفصل السادس: الضياء والنار والطاقة:
١٢٥ - الضياء:
١٢٥ - الضياء الوافد من السماء.',NULL,'221',NULL,NULL,'0');
INSERT INTO [b23144] VALUES (204,'١٢٦ -الضياء النابع من مصدر أرضي.
١٢٧ - الله نور السموات والأرض.
١٢٨ - النار:
١٣١ - نار جهنم.
١٣٢ - النار والجان.
١٣٣ - الطاقة:
١٣٣ - طاقة مصدرها تركيب كوكب الأرض نفسه.
١٣٥ - طاقة مصدرها أشعة الشمس.
١٣٧ - الفصل السابع: ما ينفع الناس:
١٣٧ "أ" الحياة على الأرض قبل الإنسان.
١٤٢ "ب" سكنى الإنسان للأرض.
١٤٣ "جـ" خلق الإنسان.
١٤٨ "د" الإنسان والنمو الحضاري.
١٥٢ "هـ" الماء في خدمة البشر.
١٥٦ "و" النبات في خدمة البشر.
١٥٩ "ز" الأحجار ومواد البناء في خدمة البشر.
١٦٤ "ح" خامات صناعية غير فلزية في خدمة البشر.
١٦٤ - ملح الطعام.
١٦٥ - الطلق.
١٦٦ - الكبريت.',NULL,'222',NULL,NULL,'0');
INSERT INTO [b23144] VALUES (205,'١٦٦ - القطران.
١٦٦ - الزجاج.
١٦٩ "ط" خامات الفلزات في خدمة البشر:
١٦٩ - الفلزات في القرآن الكريم:
١٦٩ - الذهب.
١٧١ - الفضة.
١٧١ - النحاس.
١٧٢ - الحديد.
١٧٣ - استخراج الفلزات من مناجمها وتصنيعها.
١٧٩ - الفلزات في الفتاوي الشرعية.
١٨٠ - العملة المعدنية.
١٨١ - زكاة الركاز والمعدن.
١٨٤ - كيفية تحصيل زكاة المعدن.
١٨٤ - الآراء في ملكية الخامات التعدينية والبترول.
١٨٥ "ي" خامات الطاقة في خدمة البشر.
١٩١ "ك" الأحجار الكريمة في خدمة البشر.
١٩٥ - الخاتمة.
٢٠١ - مراجع.
٢٠٥ - أشكال.
٢٠٧ ١- نموذج لقطاع في باطن الأرض.',NULL,'223',NULL,NULL,'0');
INSERT INTO [b23144] VALUES (206,'٢٠٩ ٢- نموذج صفات باطن الأرض.
٢١١ ٣- التحلل الإشعاعي لذرة يورانيوم ٢٣٨ إلى رصاص مع الزمن.
٢١٣ ٤- نموذج انقسام اليابسة.
٢١٥ ٥- أحزمة المناطق الأكثر قابلية للزازل.
٢١٧ ٦- أطراف أقراص القشرة الأرضية.
٢١٩ - الفهرس.',NULL,'224',NULL,NULL,'0');
