INSERT INTO [b23585] VALUES (587,'وفي يس: (وَإِنْ نَشَأْ نُغْرِقْهُمْ) «١» .
وفي حم عسق: (إِنْ يَشَأْ يُسْكِنِ الرِّيحَ) «٢» .
وفي النجم: (أَمْ لَمْ يُنَبَّأْ) «٣» .
وفي القمر: (وَنَبِّئْهُمْ) «٤» .
وفي المعارج: (تُؤْوِيهِ) «٥» .
وفي البلد: (مُؤْصَدَةٌ) «٦» .
وفي العلق: (اقْرَأْ بِاسْمِ رَبِّكَ) «٧» و (اقْرَأْ وَرَبُّكَ) «٨» .
وفي الهمزة: (مُؤْصَدَةٌ) «٩» .
وأما السبعة الباقية- فهي ستة أسماء وفعل:
فالأسماء: (البأس) «١٠» (والكأس) ، و (الرّأس) ، «١١» و (الضّأن) ، «١٢» و (الذّئب) «١٣» . و (وبئر) «١٤» والفعل: (يَلِتْكُمْ) «١٥» .
_________
(١) يس: ٤٣.
