INSERT INTO [b23598] VALUES (502,'٨٤- سورة الانشقاق: ٣٤٠ ٨٥- سورة البروج: ٣٤٠ ٨٦- سورة الطارق: ٣٤١ ٨٧- سورة الأعلى: ٣٤٢ ٨٨- سورة الغاشية: ٣٤٢ ٨٩- سورة الفجر: ٣٤٣ ٩٠- سورة البلد: ٣٤٤ ٩١- سورة والشمس وضحاها: ٣٤٥ ٩٢- سورة والليل إذا يغشى: ٣٤٦ ٩٣- سورة والضحى: ٣٤٦ ٩٤- سورة الانشراح: ٣٤٧ ٩٥- سورة التين: ٣٤٧ ٩٦- سورة العلق: ٣٤٧ ٩٧- سورة القدر: ٣٤٨ ٩٨- سورة البرية (البينة) : ٣٤٨ ٩٩- سورة الزلزلة: ٣٤٨ ١٠٠- سورة العاديات: ٣٤٩ ١٠١- سورة القارعة: ٣٥٠ ١٠٢- سورة التكاثر: ٣٥٠ ١٠٣- سورة والعصر: ٣٥٠ ١٠٤- سورة الهمزة: ٣٥١ ١٠٥- سورة الفيل: ٣٥١ ١٠٦- سورة قريش: ٣٥٢ ١٠٧- سورة الماعون: ٣٥٢ ١٠٨- سورة الكوثر: ٣٥٣ ١٠٩- سورة الكافرون: ٣٥٣ ١١٠- سورة النصر: ٣٥٣ ١١١- سورة أبي لهب (المسد) : ٣٥٤ ١١٢- سورة الإخلاص: ٣٥٤',NULL,'515',NULL,NULL,'0');
INSERT INTO [b23598] VALUES (503,'١١٣- سورة الفلق: ٣٥٥ ١١٤- سورة الناس: ٣٥٦ الخاتمة: ٣٥٧ الفهارس الفنية: ٣٦١ (١) الآيات القرآنية الواردة في غير موضعها: ٣٦٣ (٢) الأحاديث النبوية: ٣٦٧ (٣) الشعر والرجز: ٣٦٨ (٤) الألفاظ الغريبة المفسرة: ٣٧٢ (٥) اللغات والألسنة: ٤٨٥ (٦) الأعلام: ٤٩٠ (٧) أسماء الكتب: ٤٩٨ (٨) المراجع: ٤٩٩ (٩) الفهرس العام: ٥١١',NULL,'516',NULL,NULL,'0');
