INSERT INTO [b23600] VALUES (1,'<span data-type="title" id=toc-1>مقدمة المحقق</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ

<span data-type="title" id=toc-2>حياته </span>«١»
: أ- اسمه ونسبه:
أبو محمد سهل بن عبد الله بن يونس بن عيسى بن عبد الله بن رفيع التّستري «٢». ولد بمدينة تستر في سنة مائتين، وقيل: إحدى ومائتين «٣». وإلى هذه المدينة ترجع نسبته (التستري) وهذه المدينة من أعظم مدن خوزستان، (وتفرد بعض الناس بجعل تستر مع الأهواز، وبعضهم يجعلها مع البصرة ... وجعلها عمر بن الخطاب من أرض البصرة لقربها منها) «٤».
_________
(١) انظر ترجمته وأخباره في المصادر الآتية:
الأعلام ٣/ ١٤٣ والأنساب للسمعاني ١/ ٤٦٥ والبداية والنهاية ١/ ١٨٢ (حوادث سنة ٢٨٣ هـ) وتاريخ الأدب لبروكلمان ٤/ ١٣ وتاريخ التراث العربي ١: ٤/ ٢٩ - ٣٠ والتصوف في الإسلام ٦٦ - ٦٧ وحركة التصوف الإسلامي ١٠٩ - ١٢٠ وحلية الأولياء ١٠/ ١٩٠ - ٢١٢ وحياة الحيوان ١/ ٥٤٥ - ٥٤٧ (مادة السبع) والرسالة القشيرية ١٥ وسير أعلام النبلاء ١٣/ ٣٣٠ - ٣٣٣ وشذرات الذهب ١/ ١٨٢ - ١٨٣ وصفوة الصفوة ٤/ ٦٤ - ٦٦ وطبقات الصوفية ١/ ١٦٦ - ١٧١ وطبقات الشعراني ١/ ١٣ والعبر ٢/ ٧٦ (حوادث سنة ٢٨٣ هـ) والعصر العباسي الثاني ١٦٣ والكامل في التاريخ ٦/ ٣٨٩ (حوادث سنة ٢٨٣ هـ) واللباب في معرفة الأنساب ١/ ٢١٦ واللمع للسراج ٣٩٤ ومرآة الجنان ٢/ ٢٤٩ والمعارضة والرد ١ - ٧٥ ومعجم المفسرين ١/ ٢١٨ ومعجم المؤلفين ٤/ ٢٨٤ ومن التراث الصوفي ١ - ١٢٥ والمنتظم ٥/ ٢٦٣ والنجوم الزاهرة ٣/ ٩٥ ونفحات الأنس لجامي ٧٣ والوافي بالوفيات ١٦/ ١٧ ووفيات الأعيان ٢/ ٤٢٩ - ٤٣٠.
(٢) حلية الأولياء ١٠/ ١٩٠ وطبقات الصوفية ١/ ١٦٦ والفهرست ص ٢٦٣ ومعجم البلدان ٢/ ٣١ (تستر) ووفيات الأعيان ٢/ ٤٢٩.
(٣) الكامل في التاريخ ٦/ ٣٨٩ (حوادث سنة ٢٨٣) والوافي بالوفيات ١٦/ ١٧.
(٤) معجم البلدان ٢/ ٣٠.',NULL,'3',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (2,'أما أسرته فلم تفدنا المصادر بشيء عنها، سوى ما ذكره ابن بطوطة الذي قال إنه رأى حفدة للتستري في تستر «١». أما وفاته فكانت بالبصرة سنة (٢٨٣ هـ) «٢» وقيل سنة (٢٧٣ هـ) «٣»، وقيل (٢٩٣ هـ) «٤».
ب- نشأته وتصوفه:
نشأ سهل التستري في تستر، وكانت بدايات اتجاهه إلى التصوف في سن مبكرة جدا، واحتفظ لنا اليافعي بنص مروي عن سهل التستري تحدث فيه عن نشأته واتخاذه التصوف منهجا وسبيلا لحياته، فقال: (كنت ابن ثلاث سنين، وكنت أقوم بالليل أنظر إلى صلاة خالي محمد بن سوّار، وكان يقوم بالليل، وكان يقول: يا سهل، اذهب ونم، فقد شغلت قلبي. وقال لي يوما خالي: ألا تذكر الله الذي خلقك؟ فقلت: كيف أذكره؟ فقال: قل بقلبك عند تقلبك في ثيابك ثلاث مرات من غير أن تحرك به لسانك: الله معي، الله ناظر إلي، الله شاهدي. فقلت ذلك ليالي ثم أعلمته، فقال: قلها في كل ليلة سبع مرات، فقلت ذلك، فوقع في قلبي حلاوة. فلما كان بعد سنة قال لي خالي: احفظ ما علمتك ودم عليه إلى أن تدخل القبر، فإنه ينفعك في الدنيا والآخرة، فلم أزل على ذلك سنين، فوجدت لها حلاوة في سري. ثم قال لي خالي يوما:
يا سهل، من كان الله معه وهو ناظر إليه وشاهده لا يعصيه، إياك والمعصية.
حفظت القرآن وأنا ابن ست أو سبع، وكنت أصوم الدهر، وقوتي خبز الشعير اثنتي عشرة سنة، فوقعت لي مسألة وأنا ابن ثلاث عشرة سنة، فسألت أن يبعثوا بي إلى البصرة أسأل عنها، فجئت البصرة، وسألت علماءها، فلم يشفني ما سمعت. فخرجت إلى عبادان إلى رجل يعرف بأبي حبيب حمزة بن عبد الله العبادي، فسألته عنها فأجابني. وأقمت عنده مدة أنتفع بكلامه وأتأدب بأدبه. ثم رجعت إلى تستر، فجعلت قوتي اقتصارا على أن يشترى لي بدرهم فرقان الشعير، فيطحن ويختبز، فأفطر عند السحر كل ليلة على أوقية واحدة بغير ملح ولا إدام،
_________
(١) رحلة ابن بطوطة ١/ ٢٠٩، وتاريخ الأدب العربي لبروكلمان ٦/ ١٣.
(٢) البداية والنهاية ١١/ ٧٤ وتذكرة الحفاظ ٢/ ٦٨٥ وسير أعلام النبلاء ١٣/ ٣٣٣، وشذرات الذهب ١/ ١٨٢ وصفوة الصفوة ٤/ ٦٦ وطبقات الصوفية ١/ ١٦٧ والعبر ٢/ ٧٦ والكامل ٦/ ٣٨٩ ومعجم البلدان ٢/ ٣١ والمنتظم ٥/ ١٦٣ والنجوم الزاهرة ٣/ ٩٥ ووفيات الأعيان ٢/ ٤٢٩ والوافي بالوفيات ١٦/ ١٧.
(٣) البداية والنهاية ١١/ ٧٤ وسير أعلام النبلاء ١٣/ ٣٣٣ وصفوة الصفوة ٤/ ٦٦ ومعجم البلدان ٢/ ٣١ والمنتظم ٥/ ١٦٣ والوافي بالوفيات ١٦/ ١٧ ووفيات الأعيان ٢/ ٤٢٩.
(٤) طبقات الصوفية ١/ ١٦٧.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (3,'وكان يكفيني ذلك الدرهم سنة. ثم عزمت على أن أطوي ثلاث ليال، ثم جعلتها خمسا، ثم سبعا، حتى بلغت خمسا وعشرين ليلة، وكنت على ذلك عشرين سنة. ثم خرجت أسيح في الأرض سنين، ثم عدت إلى تستر، وكنت أقوم الليل كله) «١».
يتضح من هذا النص أن بدايات التستري الصوفية كانت على يد خاله الذي لا تفيدنا المصادر بشيء عنه، سوى أنه لقّن التستري مبادئ التصوف، ثم تلقى التصوف على يد شيخه حمزة العبادي في عبادان.
وتفيد مصادر أخرى أنه صحب ذا النون المصري الذي كان له دور- لا نعلم مداه- في رعاية بذرة التصوف لديه، فقد ذكرت بعض المصادر أن التستري لقيه في الحج وصحبه «٢».
وثمت متصوف آخر هو إدريس بن أبي خولة الأنطاكي، أفادت المصادر أن التستري حكى عنه، ولم تضف إلى ذلك شيئا آخر «٣».
ولا ندري كم من الزمن أقام في تستر، فإنه هجرها ورحل إلى البصرة وأقام فيها حتى وفاته «٤».
وعن سبب انتقاله إلى البصرة قال ابن الجوزي: (حكى رجل عن سهل أنه يقول: إن الملائكة والجن والشياطين يحضرونه، وإنه يتكلم إليهم، فأنكر ذلك عليه العوام، حتى نسبوه إلى القبائح، فخرج إلى البصرة، فمات بها) «٥».
ويبدو أن ابن الجوزي كان يتحامل على التستري، فقد أنكر عليه تفسير قوله تعالى:
وَالْجارِ ذِي الْقُرْبى وَالْجارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّبِيلِ [النساء: ٣٦] فقد فسرها التستري على أنها القلب والنفس والجوارح «٦»، (وابن الجوزي اكتفى بإنكار هذا التفسير، دون أن يذكر مبررا لإنكاره) «٧».
_________
(١) مرآة الجنان ٢/ ٢٤٩ (حوادث سنة ٢٨٣) وذكر ابن خلكان في وفيات الأعيان ٢/ ٤٢٩ من هذا الخبر قصته مع خاله فقط وانظر: التصوف في الإسلام ٦٦ - ٦٧ وحركة التصوف الإسلامي ص ١١٠.
(٢) البداية والنهاية ١١/ ١٧٤ وسير أعلام النبلاء ١٣/ ٣٣٠ ووفيات الأعيان ٢/ ٤٢٩ ومعجم البلدان ٢/ ٣١ (تستر).
(٣) بغية الطلب ٣/ ١١٣٤.
(٤) معجم البلدان ٢/ ٣١ (تستر) ووفيات الأعيان ٢/ ٤٢٩.
(٥) تلبيس إبليس ص ٢٠٧.
(٦) تفسير التستري ص ٤٥. [ ..... ]
(٧) من التراث الصوفي ص ١٠٢.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (4,'ج- تلاميذه وأصحابه: (رواة أخباره) ١ - ابن درستويه: ورد في سير أعلام النبلاء ١٣/ ٣٣٠: (ابن درستويه صاحب سهل قال:
قال سهل ... ).
٢ - أبو جعفر المصيصي المغازلي: ورد في بغية الطلب ١٠/ ٤٣٧٩: (من العباد المذكورين.
حكى عن سهل التستري).
٣ - أبو الحسن البشري: ورد في تكلمة الإكمال ١/ ٤١١: (من أصحاب سهل التستري، روى عنه كثيرا).
٤ - أبو الحسن البغدادي المزين.
٥ - أبو الحسن النخاس: ورد في تاريخ بغداد ١٤/ ٤٢٨: (سمع سهل بن عبد الله التستري).
٦ - أبو علي البصري: ورد في تاريخ بغداد ١٤/ ٤٢٦: (سكن بغداد، وكان من عباد الله الصالحين، وممن صحب سهل بن عبد الله التستري، حكى عنه أبو محمد الجريري).
٧ - أبو محمد الجريري: ورد في طبقات الصوفية ١/ ٢٠٣ وصفوة الصفوة ٢/ ٤٤٧ - ٤٤٨:
(أبو محمد الجريري: يقال: إن اسمه أحمد بن محمد بن الحسين، وكنية والده أبو الحسين. كان من كبار أصحاب الجنيد، وصحب أيضا سهل بن عبد الله التستري، وهو من علماء مشايخ القوم.
أقعد بعد الجنيد في مجلسه لتمام حاله وصحة علمه. توفي سنة ثلاثمائة وإحدى عشرة).
٨ - أبو يعقوب السوسي: ورد في كتاب من التراث الصوفي ٧١: (ومن أصدقاء سهل أيضا أبو يعقوب السوسي الصوفي والأستاذ العظيم الذي أشرف على أبي يعقوب إسحاق بن محمد النهرجوري المتوفى سنة ٣٣٣/ ٩٤٤. ومن هنا نشأت علاقة الود بين النهرجوري والتستري الذي كان يقدره حق التقدير). وثمت خبر ورد في تفسير التستري يفيد أنهما كانا معا بأرجان، وهذا الخبر ورد أيضا في اللمع للسراج ١٩٣.
٩ - أحمد بن سالم: ورد في العبر ٢/ ٣٢٦ وشذرات الذهب ٢/ ٣٦: (أبو الحسن بن سالم الزاهد أحمد بن محمد بن سالم البصري: شيخ السالمية. وكان له أحوال ومجاهدات، وعنه أخذ الأستاذ أبو طالب صاحب القوت، وهو آخر أصحاب سهل التستري وفاة). وانظر:
الحلية ١٠/ ٣٧٨ وسير أعلام النبلاء ١٦/ ٢٧٢.
١٠ - أيوب الحمال: ورد في صفوة الصفوة ٢/ ٢٩٣: (أيوب الحمال أبو سليمان: من ذوي الكرامات، صحب سهل بن عبد الله التستري).
١١ - البربهاري: ورد في سير أعلام النبلاء ١٥/ ٩٠: (البربهاري: شيخ الحنابلة، القدوة،',NULL,'6',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (5,'الإمام، أبو محمد الحسن بن علي بن خلف البربهاري الفقيه. كان قوالا بالحق، لا يخاف في الله لومة لائم. صحب سهل بن عبد الله التستري، وصنف التصانيف. توفي سنة ثلاثمائة وثمان وعشرين وعمره سبع وسبعون سنة). وانظر: شذرات الذهب ١/ ٣١٩ وطبقات الحنابلة ٢٩٩ والعبر ٢/ ٢٢٢.
١٢ - بكر بن محمد ابن العلاء أبو الفضل القشيري: ورد في سير أعلام النبلاء ١٥/ ٥٣٧: (حكى عن سهل، وصنف التصانيف، وسكن مصر. توفي بمصر سنة ثلاثمائة وأربع وأربعين، وجاوز الثمانين سنة). وجاء في الديباج المذهب ١/ ١٠٠: (من كبار فقهاء المالكيين رواية للحديث).
١٣ - الحلاج: الحسين بن منصور الحلاج أبو مغيث، توفي سنة ٣٠٩ هـ. جاء في سير أعلام النبلاء ١٤/ ٣١٤ أنه (نشأ بتستر، وصحب سهل التستري، وصحب ببغداد الجنيد). وانظر ديوان الحلاج، المقدمة ص ٧.
١٤ - عبد الجبار بن شيراز بن يزيد العبدي النهربطي: ورد في تكملة الإكمال ٣/ ٤٦٥ ومعجم البلدان ٥/ ٣١٩ أنه (روى عن سهل التستري) وجاء في اعتقاد أهل السنة ١٨٢ - ١٨٣ أنه (صاحب سهل التستري) وروى عن التستري عدة أقوال وردت في الحلية أثناء ترجمة سهل التستري ١٠/ ١٩٨ - ٢١١.
١٥ - علي بن عبد العزيز الضرير الصوفي البغدادي أبو الحسن: جاء في تاريخ بغداد ١٢/ ٣٠: (من قدماء مشايخهم، صحب سهل بن عبد الله التستري).
١٦ - عمر بن واصل العنبري، أبو الحسن: جاء في تاريخ بغداد ١١/ ٢٢١: (أظنه بصريا، سكن بغداد، وروى بها عن التستري). قلت: ورد اسمه في تفسير التستري حوالي عشر مرات، انظر فهرس الأعلام بذيل التفسير.
١٧ - محمد بن الحسن بن أحمد الجوري: جاء في الإكمال ٣/ ١٠ أنه (حدث عن سهل التستري) وفي معجم البلدان ٢/ ١٨٢ جور: (سمع سهل التستري قراءة).
١٨ - محمد بن الحسن: ورد في تاريخ بغداد ٥/ ٢٥٢ أنه كان (صاحب سهل بن عبد الله)، ثم ذكر قولا للتستري وروى قولا للتستري في الحلية ١٠/ ٢١١.
١٩ - محمد بن أحمد بن سالم أبو عبد الله: ورد في طبقات الصوفية ١/ ٣١٢: (صاحب التستري وراوي كلامه. لا ينتمي إلى غيره من المشايخ، وهو من أهل الاجتهاد، وطريقته طريقة أستاذه سهل التستري، وله بالبصرة أصحاب ينتمون إليه، وإلى ابنه أبي الحسن). وجاء في',NULL,'7',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (6,'الكامل لابن الأثير ٦/ ٤٦٦، حوادث سنة ٢٩٧ هـ: (فيها توفي أبو عبد الله محمد بن سالم صاحب التستري) .
٢٠- المزين: أبو الحسن البغدادي علي بن محمد المزين، توفي سنة ٣٢٨ هـ، ورد في سير أعلام النبلاء ١٥/ ٢٣٢: (الأستاذ، العارف، من أورع القوم وأكملهم حالا. صحب التستري والجنيد، وجاور بمكة) .
- ورد في سير أعلام النبلاء ١٣/ ٣٣٠ عند ترجمة سهل التستري: (روى عنه الحكايات:
عمر بن واصل وأبو محمد الجريري وعباس بن عصام ومحمد بن المنذر الهجيمي) .
- ورد في حلية الأولياء ١٠/ ١٩٨- ٢١١ أسماء خمسة وعشرين رجلا سمعوا سهل التستري ونقلوا أقواله. وكذلك وردت بعض الأسماء في طبقات الصوفية ١/ ١٦٦- ١٧١ وصفوة الصفوة ٤/ ٦٤- ٦٦.

<span data-type="title" id=toc-3>مؤلفاته:</span>
يرى بعض الدارسين أن التستري لم يقم بكتابة مؤلفاته بنفسه «١» ، ولعل مرد ذلك (حرصه على أن لا يضع بين أيدي خصومه وثائق خطية تحمل فكره، وتكون سببا في عواقب قد تسوء) «٢» . وقال كمال جعفر: (إن المؤرخين قد نسبوا إليه عددا من المؤلفات التي تختلف كما وكيفا. وأغلب الظن أن هذه الكتب والرسائل إنما هي خلاصة انتقاها ونقلها تلامذته من بعده، وبخاصة ابن سالم) «٣» .
وقام العلامة فؤاد سزكين بإحصاء مؤلفات التستري المخطوطة، مع بيان مكان وجودها، وانتهى إلى ذكر ثمانية كتب «٤» كما قام كمال جعفر بإحصاء مماثل، انتهى فيه إلى ذكر اثني عشر كتابا «٥» ، منها ستة كتب ذكرها سزكين، وثلاثة ذكرها النديم في الفهرست «٦» وكتاب ذكره حاجي خليفة في كشف الظنون «٧» . وانفرد سزكين بذكر كتابين للتستري، لم يذكرهما جعفر،
_________
(١) حركة التصوف الإسلامي ص ١٠٩ ومن التراث الصوفي ص ٧٩.
(٢) حركة التصوف الإسلامي ص ١٠٩.
(٣) من التراث الصوفي ص ٧٩.
(٤) تاريخ التراث العربي: المجلد الأول، الجزء الرابع ص ١٢٩.
(٥) من التراث الصوفي ص ٧٩- ٨٣.
(٦) الفهرست ص ٢٦٣.
(٧) كشف الظنون ٢/ ١١٩٣.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (7,'وبذلك يكون مجموع عدد مؤلفات التستري هو أربع عشرة كتابا، وهي حسب ترتيبها الهجائي:
١- تفسير القرآن العظيم: ذكر سزكين أن لهذا التفسير ست نسخ خطية موزعة كما يلي:
- جوتا: ٥٢٩ (١٥٣ ق، ٨٢٥ هـ) .
- القاهرة ثان: ١/ ٣٨، تفسير ٦٨.
- الظاهرية: ٥١٥ (١٤٦ ق) ، نسخة حديثة، انظر عزة حسن ١/ ١٧٦.
- فاتح: ٦٣٨ (٧٢ ق، ٨٧٢ هـ) ، ٣٤٨٨/ ٢ (من ١٨١ أ- ٢٧٩ أ، ٩٦٥ هـ) .
- صنعاء: ٦٢.
- طبع بالقاهرة ١٣٢٦ هـ/ ١٩٠٨ م، ثم أعيد طبعه ١٣٢٩ هـ/ ١٩١١.
٢- جوابات أهل اليقين: ذكره النديم في الفهرست ص ٢٦٣ وعنه ذكره كمال جعفر في كتابه من التراث الصوفي ص ٨١. وهو من الكتب المفقودة.
٣- دقائق المحبين: ذكره النديم في الفهرست ص ٢٦٣، وعنه ذكره كمال جعفر في كتابه من التراث الصوفي ص ٨١، وذكر أن اسمه رقائق المحبين في الكواكب الدرية للمناوي ١/ ٢٤٣ وفي هدية العارفين ١/ ٤١٢ وفي روضات الجنان للخوانساري ص ٣٢٤.
٤- رسالة في الحروف: ذكر سزكين ص ٣٠ أن لها نسخة خطية في تشنستربيتي ٣١٦٨/ ٣ (٨٣- ٨٤، ٦٨٦ هـ) . وذكر كمال جعفر في كتابه من التراث الصوفي ص ٨٠: (يبدو أنها الرسالة التي أشار إليها إسماعيل البغدادي في كتابه هدية العارفين ١/ ٤١٢ بعنوان «زايرجة» . وربما أطلق البغدادي عليها هذا الاسم نظرا لما رأى من أرقام بهوامش المخطوط ظن أنها القيم العددية للحروف التي تمتّ بصلة وثيقة بالزايرجة، على حين أنها خاصة بأسرار الحروف التي تشرح فكرة الخلق والتأليف، بناء على التأمل في حقيقة الحروف من الجانب الميتافيزيقي) .
٥- رسالة في الحكم والتصوف: ذكر سزكين وجعفر أن لهذه الرسالة نسخة خطية في مكتبة أيا صوفيا ٤١٢٨/ ٤ (١٤٨- ١٦٨، القرن السابع هـ) . ومنها نسخة مصورة في معهد المخطوطات العربية برقم ١٩٥. وقال جعفر: (اطلعنا على هذه الرسالة فوجدناها غير كاملة وليست ذات أهمية كبيرة، بل يبدو أنها ربما لم تكن سوى مستخلصات محرفة وغير تامة، من بعض مؤلفات سهل التستري) .
- زايرجة: من الكتب المفقودة. ورد ذكرها في كشف الظنون ٢/ ٩٤٨، وأبجد العلوم ٢/ ٣١٣، وهدية العارفين ١/ ٤١٢. وورد في كشف الظنون أن علم الزايرجة هو من القوانين الصناعية لاستخراج الغيوب. وانظر ما تقدم برقم ٤ رسالة في الحروف.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (8,'٦- سلسبيل سهلية: ذكرها جعفر قائلا: (نسب الشيخ السنوسي هذا المؤلف الصغير، الذي هو عبارة عن صيغة يظن أنها مأثورة لسهل، ولكن ماسينيون يشكك في نسبتها، ويرى أنها ذات أصل أحدث بكثير من عصر التستري. ويشير عبد الرؤوف المناوي في الكواكب الدرية ١/ ٢٤٣ إلى هذه الصيغة باعتبارها الصيغة التي تعوّد سهل أداءها. ويبدو أنها نفس الصيغة التي تلقاها من خاله محمد بن سوّار) .
٧- الغاية لأهل النهاية: من الكتب المفقودة. ورد ذكرها في كشف الظنون ٢/ ٩٤٨.
وقال جعفر: (ورد ذكر هذا المؤلف في كشف الظنون ٤/ ٣٠٣، كما ورد في هدية العارفين ١/ ٤١٢. ويذكر الفريابي ت ٣٠٠/ ٩١٢ أن عنوان هذا المؤلف هو «خلاصات غايات أهل النهاية» . [القرشي: طبقات الحنفية ١/ ٥٣] ) .
٨- لطائف القصص في قصص الأنبياء: ذكر سزكين أن له نسخة خطية في مكتبة طلعت، مجموع ٢٨٣. وقال جعفر: (أورد ذكر هذا المؤلف حاجي خليفة في كشف الظنون ٢/ ١٠٧.
ويبدو أن حاجي خليفة قد رأى هذا المؤلف فعلا ... ولم نعثر على هذا المؤلف للآن، وإن كنا نعتقد أن أجزاء قليلة منه قد احتفظ بها في كل من التفسير وكلام سهل، في تلك المواضع التي تخدم غرضا روحيا كما كان يرى التستري) .
٩- كتاب المعارضة والرد على أهل الفرق وأهل الدعاوى في الأحوال: ذكر سزكين أنه لها نسخة خطية في كوبرلي برقم ٧٢٧/ ٣ (٢٠٥ أ- ٢٤٢ أ، القرن السابع الهجري) . وهذا الكتاب حققه محمد كمال جعفر سنة ١٩٨٠ ونشره في القاهرة، دار الإنسان. ويضم هذا الكتاب بعض أقوال سهل التستري، محكية على لسان ابن سالم أحيانا، وعلى لسان غيره أحيانا أخرى.
وإذا كان عنوانه يوحي بأنه كتاب كلامي مخصص للجدل حول المسائل الكلامية الخالصة، فإن الواقع غير ذلك، فهو كتاب يضم كثيرا من أوجه النقد الموجهة ضد بعض آراء الفرق الكلامية وبخاصة القدرية والمرجئة، ونقض بعض الاتجاهات أو العادات أو الفرق الصوفية.
١٠- كتاب الميثاق: ذكره جعفر قائلا: (نصّ عليه ناسخ رسائل الجنيد وهو تلميذ ابن عربي، إسماعيل ابن سودكين الذي توفي عام ٦٤٦/ ١٢٨٤. ومن المهم أن نلاحظ أن هذا الناسخ يتخذ العنوان الذي وجده لسهل نموذجا ومثالا لعنوان رسالة مماثلة للجنيد في نفس الموضوع. ويضاف إلى ذلك أن الناسخ يؤكد أنه وجد وقرأ رسالة في «الميثاق» لسهل بن عبد الله التستري وهذا المؤلف- وإن كان قد فقد- يمكن التقاط بعض نقاطه الهامة المتصلة بموضوعه فيما عثر عليه من أقوال سهل، وبخاصة في التفسير وفي كلامه المجموع، فيما يتصل بتعليقة على',NULL,'10',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (9,'الآية الكريمة الخاصة بالميثاق في سورة الأعراف رقم ١٧١) .
١١- كلام سهل: ذكر سزكين أن له ثلاث نسخ خطية:
- كوبرلي ٧٢٧ (١- ١٥٢ ب) وهي بعنوان: كلمات الإمام الرباني سهل بن عبد الله التستري. ومن المرجح أنها جمعت في القرن السابع الهجري.
- مكتبة جامعة إستانبول ٤٠٨٩ (٣٤ ق، القرن ١١ هـ) .
- مكتبة أسعد ٣٥٢٧ (٢١٧ ب- ٢٤٨ ب، حوالي ١١٠٠ هـ) .
واكتفى جعفر بذكر نسخة كوبرلي قائلا عنها: (كتب هذا المخطوط عطاء الله المعروف بنوعي زاده القاضي في مدينة أسكوب عام ١٠٤٤ هـ/ ١٦٣٤ م. وقد نقله هذا الناسخ من أصل قديم لا يعرف تاريخه) . وعلق جعفر على هذا الكتاب قائلا إنه: (يضم أقوال سهل الموجزة المركزة حول قضايا الزهد والتصوف وعلم الكلام، مقدمة على سبيل الحكاية، مما يشهد بأن التستري نفسه لم يخطه، وإنما سجل أقواله حاضر ومجلسه، وعلى رأسهم ابن سالم الأب، ثم تلقى ذلك وتناوله بالترتيب ابن سالم الابن أو أبو القاسم الصقلي نفسه، الذي كرس جهوده فيما بعد للشرح والتعليق على أقوال سهل، والدفاع عنه دفاعا حارا) .
١٢- مقالة في المنهيات: ذكر سزكين أن لها نسخة خطية في: (طهران، كلية الحقوق ٢٥١ ج (١٢ أ- ١٩ أ، ١٢٧٩ هـ، انظر الفهرس ص ٤٨٧) .
١٣- مناقب أهل الحق ومناقب أهل الله عزّ وجلّ: ذكرها سزكين وحدد موضعها قائلا:
(شرحها محمد الرايثني، القرن السابع الهجري: طلعت، تصوف ١٥٨١ (٣٦٧ ق، ٦٧٥ هـ) .
١٤- مواعظ العارفين: ذكره النديم في الفهرست ص ٢٦٣. ولأن كلام سهل التستري كان يشوبه الغموض ويكتنفه الإبهام، فقد قام أحد مريديه، وهو أبو القاسم عبد الرحمن بن محمد بن عبد الله الصّقليّ المتوفى نحو سنة ٣٨٠ هـ/ نحو ٩٩٠ م «١» ، بتأليف كتاب: «الشرح والبيان لما أشكل من كلام سهل» ، وذكر جعفر أن الصقلي في كتابه هذا (عرض بعض الأقوال السهلية التي أثارت التساؤل، ثم تبع كل قول بالشرح والتفسير الذي يراه) «٢» . ولهذا الكتاب نسختان خطيتان:
- كوبرلي: ٧٢٧ (١٥٣ أ- ٢٠٤ ب) ، القرن السابع الهجري.
- أسعد أفندي: ١٦٢٢ (٤٢ ق) ، القرن التاسع الهجري. وتوجد منه نسخة مصورة في
_________
(١) الأعلام ٣/ ٣٢٥.
(٢) من التراث الصوفي ص ٨٣.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (10,'معهد المخطوطات العربية برقم ٢٨٧ (١/ ١٧١) . وهذا الكتاب ضمن مجموع فيه أيضا كتابان لسهل، وقد تقدم ذكرهما، وهما كلام سهل، وكتاب المعارضة والرد على أهل الفرق وأهل الدعاوي في الأحوال. ويضم هذا المجموع (٢٤٢ ورقة) .
(إن أقوال سهل وما سجّل له من آراء قد شمل فعلا حقل التصوف برمته، وقد لمس تقريبا كل المشاكل الصوفية والكلامية التي أثارت اهتمام عصره) «١» .
ويرى جعفر أن النتاج الفكري للتستري (يشهد بإلمام كامل بعلم الكلام والفلسفة كما فهمتها بعض الفرق الإسلامية ... وأنه لم يكن يجهل الفروع الأخرى من أبواب المعرفة كالطب والكيمياء) «٢» .

<span data-type="title" id=toc-4>كلمة حول تفسير التستري</span>
أول ما يلفت النظر إلى هذا التفسير هو حجمه اللطيف، مما يعني بالتالي أنه لا يضم تفسيرا تاما لجميع الآيات القرآنية، وإنما هو تفسير لبعض آيات القرآن، وتعليقات كانت استجابة لأسئلة بعض مريديه. وليس عجيبا أن يكون تفسيره مختصرا، فتلك طريقة أهل التصوف، ومن هؤلاء السلمي صاحب طبقات الصوفية الذي وضع تفسيره المختصر «حقائق التفسير» .
وأثناء قيامي بتحقيق هذا التفسير، استوقفتني فيه أخبار وحوادث تتصل بسيرة التستري وحياته الروحية «٣» ، حتى كدت أشك في نسبة التفسير إليه، لا سيما وأن فيه أيضا خبر احتضاره «٤» ، وأحداثا وقعت بعد وفاته، كقصة عمرو بن الليث الذي توفي بعده بست سنوات، أي سنة ٢٨٩ هـ «٥» ، وعلاوة على ذلك فيه شرح لبعض أقواله «٦» . وما أكثر ما يرد فيه: (وسئل سهل) ، و (قيل له) ، و (قلت لسهل) ، و (سمعته يقول) ...
إن وجود مثل هذه الأخبار والأقوال في هذا التفسير يدل بلا شك على أن التستري لم يضع بنفسه هذا التفسير، ومع ذلك فإن ما فيه من أقوال وآراء يمثل بصدق أقواله وآراءه خير تمثيل، وتتجلى مصداقية ذلك في أن هذه الأقوال والآراء يمكن توثيقها من مصادر صوفية أخرى، وهذا ما قمت به، ويتضح ذلك في الحواشي التي ذيلت بها متن التفسير.
_________
(١) من التراث الصوفي ص ٨٢.
(٢) المصدر نفسه ص ٧٤.
(٣) انظر في هذا التفسير على سبيل المثال الصفحات: ٨٨، ١٦٢، ١٧٠، ٢١٢.
(٤) تفسير التستري ص ٧٦. [.....]
(٥) المصدر نفسه ص ٥٩.
(٦) المصدر نفسه ص ١٦.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (11,'إن أقوال التستري المتعلقة بالتفسير كانت موضع اهتمام الصوفيين الذين كانوا يجلون التستري، فأخذوا بتدوينها في مؤلفاتهم، وأشهر الكتب التي حفظت لنا بعض أقواله وآرائه كتاب قوت القلوب لابن طالب المكي، وكتاب حلية الأولياء للأصفهاني «١» ، ويعد أبو بكر البلدي في أهم الرجال الذين اهتموا اهتماما فائقا بجمع أقوال التستري، ونجد اسمه يتكرر بكثرة في تفسير التستري ويمكن ملاحظة ذلك من خلال فهرس الأعلام الذي ذيلت به هذا التفسير.
ويرى د. كمال جعفر (أن دور أبي بكر بالنسبة للتفسير إنما هو الرواية عن طريق والده أبي النصر. ولما كان التفسير مجرد تعليقات مقتضبة على بعض آيات القرآن في مناسبات مختلفة، فلا يستبعد أن يكون أبو بكر قد بوّبه ورتبه حسب السور، مطعما إياه ببعض الروايات التاريخية أو السيرة الشخصية لسهل) «٢» .
ومع ترجيح القول بأن أبا بكر البلدي هو من قام بجمع أقوال التستري المتعلقة بتفسير آيات القرآن، فإنه فاته ضمّ تفسير آيات أخرى، ومن يقرأ تفسير القرطبي يجد فيه أقوالا للتستري في تفسير آيات لم ترد في تفسيره «٣» .
_________
(١) هذا عدا عن كتابي التستري اللذين حققهما د. كمال جعفر. حيث نجد أقوالا كثيرة للتستري، جمعها بعض مريديه بدقة وأمانة، والكتابان هما: كتاب كلام سهل الذي نشره المحقق تحت عنوان من التراث الصوفي، وكتاب المعارضة والرد.
(٢) من التراث الصوفي ص ٩٣.
(٣) انظر تفسير القرطبي: ١/ ٢٦٩، ٢/ ١٧٤، ٣١١، ٥/ ١٨١- ١٨٢، ٢٥٩، ٧/ ٣٤٦، ٩/ ٢٦٩، ١٢/ ٧٣، ١٦/ ١٦٨ وهذه الإشارة إلى الإحالات على سبيل المثال لا الحصر.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (12,'خطبة الكتاب
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ اللهم صلّ على سيدنا محمد وعلى آله وصحبه وسلم أخبرنا الشيخ الواعظ أبو نصر أحمد بن عبد الجبار بن محمد بن أحمد بن محمد بن أبي النصر البلدي إجازة عليه، شافهني بها في دارة يوسف أن جدّه الإمام أبا بكر محمد بن أحمد البلدي أخبره قال: حدثنا الفقيه أبو نصر أحمد بن علي بن إبراهيم الطائفي الصفّار قال: حدثنا أبو القاسم علي بن أحمد بن محمد بن الحسن الوضّاحي، حدثنا أبو العباس عبد الرحمن بن الحسن بن عمر البلخي ببلخ في سكة ساسان، وقال أبو يوسف أحمد بن محمد بن قيس السجزي:
سمعت أبا محمد بن عبد الله التستري رحمه الله تعالى في سنة خمس وسبعين ومائتين يقول:
حدثنا محمد بن سوار عن أبي عاصم النبيل عن بشر عن عكرمة عن ابن عباس رضي الله عنهما قال: سألت رسول الله صلّى الله عليه وسلّم فيم النجاة غدا؟ فقال: «عليك بكتاب الله عزَّ وجلَّ، فإن فيه نبأ من كان قبلكم وخبر من بعدكم، وحكم ما بينكم من دينكم الذي تعبدكم به الله عزَّ وجلَّ، به تصلون إلى المعرفة، ومن يرد الهدى في غيره يضلّه الله، هو أمر الله الحكيم، وهو الصراط المستقيم، وهو الشفاء النافع، وهو الذي لم تنته الجن إذ سمعته أن قالوا: إِنَّا سَمِعْنا قُرْآناً عَجَباً (١) يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ وَلَنْ نُشْرِكَ بِرَبِّنا أَحَداً «١» [الجن: ١- ٢] .
هو الذي ظاهره أنيق وباطنه عميق، وهو الذي يعجز عنه كل فهم لقول الله تعالى:
وَإِذْ صَرَفْنا إِلَيْكَ نَفَراً مِنَ الْجِنِّ يَسْتَمِعُونَ الْقُرْآنَ فَلَمَّا حَضَرُوهُ قالُوا أَنْصِتُوا فَلَمَّا قُضِيَ وَلَّوْا إِلى قَوْمِهِمْ مُنْذِرِينَ (٢٩) قالُوا يا قَوْمَنا إِنَّا سَمِعْنا كِتاباً أُنْزِلَ مِنْ بَعْدِ مُوسى مُصَدِّقاً لِما بَيْنَ يَدَيْهِ يَهْدِي إِلَى الْحَقِّ وَإِلى طَرِيقٍ مُسْتَقِيمٍ [الأحقاف: ٢٩- ٣٠] فسأله رجل عن علم الله تعالى في عباده: هل هو شيء بدا له من بعد ما خلقهم، أو كان من قبل أن يخلقوا؟ فقال: بَلْ هُوَ قُرْآنٌ مَجِيدٌ [البروج: ٢١] أي كتاب محكم فِي لَوْحٍ مَحْفُوظٍ [البروج: ٢٢] قبل أن يخلقوا
_________
(١) سنن الترمذي: باب ما جاء في فضل القرآن، حديث رقم ٢٩٠٦، وسنن الدارمي: حديث رقم ١٣٣١، وشعب الإيمان ٢/ ٣٢٦ (رقم ٨٣٦) ، ٣/ ٧١ (رقم ١٩٣٥) ، ومصنف ابن أبي شيبة، حديث ٣٠٠٠٧.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (13,'وأن الله عز وجل فرغ من علم عباده وما يعملون قبل أن خلقهم، ولم يجبرهم على المعصية، ولا أكرههم على الطاعة، ولا أهملهم من تدبيره، بل نبه على ما تواعد به من كذب بقدره فقال:
فَمَنْ شاءَ فَلْيُؤْمِنْ وَمَنْ شاءَ فَلْيَكْفُرْ [الكهف: ٢٩] على وجه التهدد، إذ لا حول لهم ولا قوة إلا بما سبق علمه فيهم أنه سيكون منه بهم، ولهم قال الله تعالى: وَإِذا أَرادَ اللَّهُ بِقَوْمٍ سُوْءاً فَلا مَرَدَّ لَهُ [الرعد: ١١] فالخير من الله تعالى أمر، وإليه الولاية فيه، والشر من الله نهي، وإليه العصمة فيه.
قال سهل رضي الله عنه: وما من آية في القرآن إلّا ولها أربعة معان، ظاهر وباطن وحدّ ومطلع، فالظاهر التلاوة، والباطن الفهم، والحد حلالها وحرامها، والمطلع إشراف القلب على المراد بها فقها من الله عزَّ وجلَّ. فالعلم الظاهر علم عام، والفهم لباطنه، والمراد به خاص، قال تعالى: فَمالِ هؤُلاءِ الْقَوْمِ لا يَكادُونَ يَفْقَهُونَ حَدِيثاً [النساء: ٧٨] أي لا يفقهون خطابا.
قال سهل: فلا بد للعبد من مولاه، ولا بد له من كتابه، ولا بد له من نبيه صلى الله عليه وسلّم، إذ قلبه معدن توحيده، وصدره نور من جوهره أخذ قواه من معدنه إلى هيكله، فمن لم يكن عنده شيء يتبع به أو أضرب عنه كذلك لم تكن الجنة منزلا له، وإذا لم يكن الله معه وناصره فمن معه، وإذا لم يكن القرآن إمامه، ولم يكن النبي صلى الله عليه وسلّم له شافعا من يشفع له، وإذا لم يكن في الجنة فهو في النار.
وقوله: «صدره نور» أي موضع النور. «من جوهره» : وهو أصل محل النور في الصدر الذي منه ينتشر النور في جميع الصدر. وإضافة الجوهر إلى الله تعالى ليس المراد ذاته، وإنما هي على طريق الملك. «أخذ قواه» : يعني قوى النور من معدنه، وهو الصدر وما حل مصدق. «إلى هيكله» : يعني إلى جوارحه، وإنما عنى بها نور الطاعات التي في الجوارح، فمن لم يكن عنده شيء من الهداية سمع به، أي فهم به.
وقال النبي صلى الله عليه وسلّم: «القرآن شافع مشفّع وما حل مصدق، فمن شفع له القرآن نجا، ومن محل به هلك» «١» . وقال سهل: إن الله تعالى أنزل القرآن على نبيه صلّى الله عليه وسلّم، وجعل قلبه معدنا لتوحيده والقرآن، فقال: نَزَلَ بِهِ الرُّوحُ الْأَمِينُ (١٩٣) عَلى قَلْبِكَ [الشعراء: ١٩٣- ١٩٤] وكلفه تبليغه والبيان عنه ليعلم المؤمنون به ما أنزل إليهم، فمن آمن به وعلم تبيانه وعمل بحكمه كان كامل الإيمان لله تعالى، ومن آمن به وقرأه ولم يعمل بعلم ما فيه لم يكمل أجره. والناس في قراءة القرآن على ثلاثة مقامات فقوم أعطوا الفهم بقيامهم بأداء الأمر واجتناب النهي من
_________
(١) نوادر الأصول ٣/ ٢٦٠.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (14,'الظاهر والباطن، وصدقهم فيه بنور بصيرة اليقين، وهو سكون القلب إلى الله تعالى في كل حال وعلى كل حال، فليس لهؤلاء همة في الألحان ولا في التطريب بطيبة الصوت تكلفا، إنما همهم التفهّم وطلب المزيد من الله تعالى فهما لأمره ونهيه. والمراد من أحكام فرضه وسنة نبيه صلّى الله عليه وسلّم، فهم بعلمه عاملون، وبالله مستعينون، وعلى آدابه صابرون كما أمرهم بقوله: اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا [الأعراف: ١٢٨] أي استعينوا بالله على أمر الله بالسنة فرضا، أي سنة الله، واصبروا على آدابه باطنا وظاهرا، كي يكسبكم فهما وفطنة. والمراد منه تفضلا لا يبالون بطيب حنجرة الأصوات، فهم الذين أعطاهم الله تعالى فهم القرآن، هم خاصة الله وأولياؤه، لا هم للدنيا، ولا الدنيا منهم في شيء، ولا فيما في الجنة رغبوا، أخذ منهم الدنيا فلم يبالوا، وهبها لهم فردوها كما ردها نبيهم صلّى الله عليه وسلّم لمّا عرضت عليه، طرحوا أنفسهم بين يديه رضى وسكونا إليه وقالوا: لا بد لنا منك أنت أنت، لا نريد سواك، فهم المتفردون بالله كما قال النبي صلى الله عليه وسلّم: «سيروا، سبق المفردون إلى رحمة الله، قالوا: ومن المفردون يا رسول الله؟ قال: الذين اهتروا بالذكر لله تعالى، يأتون يوم القيامة خفافا قد حط الذكر عنهم أثقالهم» «١» . قال سهل: هم المشايخ المهترون «٢» في الذكر بالذكر لله تعالى، مجالسون كما قال النبي صلى الله عليه وسلّم: يقول الله تعالى: «أنا جليس من ذكرني حيثما التمسني عبدي وجدني» «٣» ، وقال تعالى: فَأَيْنَما تُوَلُّوا فَثَمَّ وَجْهُ اللَّهِ [البقرة: ١١٥] .

<span data-type="title" id=toc-6>باب صفات طلاب فهم القرآن</span>
قال الله عزّ وجلّ: وَإِنَّهُ لَتَنْزِيلُ رَبِّ الْعالَمِينَ (١٩٢) نَزَلَ بِهِ الرُّوحُ الْأَمِينُ (١٩٣) عَلى قَلْبِكَ لِتَكُونَ مِنَ الْمُنْذِرِينَ [الشعراء: ١٩٢- ١٩٤] قال سهل: فعلى مقدار النور الذي قسمه الله تعالى له يجد هداية قلبه وبصيرته، فظهر على صفاته أنوار نوره، قال الله تعالى: وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُوراً فَما لَهُ مِنْ نُورٍ [النور: ٤٠] فالقرآن حبل الله بين الله وبين عباده، من تمسك به نجا، لأن الله تعالى جعل القرآن نورا وقال: وَلكِنْ جَعَلْناهُ نُوراً نَهْدِي بِهِ مَنْ نَشاءُ مِنْ عِبادِنا [الشورى: ٥٢] ومعنى جعلنا: بيّنّا ما فيه من محكم ومتشابه، وحلال وحرام، وأمر ونهي، كما قال الله عزّ وجلّ:
_________
(١) نوادر الأصول ٣/ ٦٤.
(٢) جاء في المصدر السابق: (المهتر إذا نطق يشبه كلامه كلام من لم يستعمله عقله، لأن العقل يخرج الكلام على اللسان بتدبير وتؤدة وتأن، وهذا المهتر إنّما ينطق به، فكأنه الماء على لسانه يجري، حتى يشبه الهذيان في بعض أحواله، وهو في الباطن مع الله تعالى من أصفى الناطقين وأصدقهم. والمهتر في اللغة: الشيخ الكبير الذي قد أفند عقله ... ) .
(٣) شعب الإيمان ١/ ٤٥١.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (15,'إِنَّا جَعَلْناهُ قُرْآناً عَرَبِيًّا [الزخرف: ٣] أي بينّاه بلسان عربي مبين، يعني بحروف المعجم التي بينها الله لكم، بها تعرفون ظاهرا وباطنا. وقال الله تعالى: وَاتَّبَعُوا النُّورَ الَّذِي أُنْزِلَ مَعَهُ [الأعراف: ١٥٧] يعني القرآن الذي قلب النبي صلى الله عليه وسلّم معدنه. قيل له: ما معنى قوله: «القرآن حبل الله بين الله وبين عباده» ؟ قال: لا طريق لهم إليه إلّا به، وبفهم ما خاطبهم فيه للمراد منهم به، والعمل بالعلم لله مخلصين فيه، والاقتداء بسنة محمد صلّى الله عليه وسلّم المبعوث إليهم، كما قال تعالى:
مَنْ يُطِعِ الرَّسُولَ فَقَدْ أَطاعَ اللَّهَ [النساء: ٨٠] يعني من يطع الرسول صلّى الله عليه وسلّم في سنته فقد أطاع الله في فرائضه.
وقال ابن عباس «١» رضي الله عنهما: أنزل الله تعالى القرآن جملة واحدة إلى سماء الدنيا، ثم نجّمه الله تعالى على النبي صلى الله عليه وسلّم خمس آيات وأقل وأكثر «٢» . قوله سبحانه وتعالى:
فَلا أُقْسِمُ بِمَواقِعِ النُّجُومِ (٧٥) وَإِنَّهُ لَقَسَمٌ لَوْ تَعْلَمُونَ عَظِيمٌ (٧٦) إِنَّهُ لَقُرْآنٌ كَرِيمٌ [الواقعة: ٧٥- ٧٧] وقال ابن عباس رضي الله عنهما: لم ينزل القرآن في شهر ولا في شهرين، ولا في سنة ولا في سنتين، بل كان بين نزول أوله ونزول آخره عشرون سنة أو ما شاء الله من ذلك، وذلك لأن لإسرافيل مكانا في العرش خافض بصره وحوله الملائكة السّفرة الكرام البررة ولوح من زمرّد، فإذا أراد الله أمرا كان في ذلك اللوح، فقرع ذلك جبينه ينظر ما فيه، فبعث الرسل، فذلك قوله: فِي لَوْحٍ مَحْفُوظٍ [البروج: ٢٢] لأن القرآن أنزل جملة واحدة على السّفرة الكرام الكاتبين، فنجّمته السّفرة الكرام الكاتبون على جبريل عليه السلام عشرين سنة، فنجّمه جبريل عليه السلام على النبي صلى الله عليه وسلّم كذلك «٣» ، فقال المشركون: لولا نزل الله عليه القرآن جملة واحدة، فقال الله تعالى: كَذلِكَ لِنُثَبِّتَ بِهِ فُؤادَكَ [الفرقان: ٣٢] أي ليكون ذلك جوابا لما يسألونك عنه. إذ لو أنزلناه جملة واحدة لم يكن عندك جواب سؤالهم. وقال سهل: أنزل الله القرآن على خمسة أخماس خمس محكم وخمس متشابه وخمس حلال وخمس حرام وخمس أمثال. فالمؤمن العارف بالله تعالى يعمل بمحكمه، ويؤمن بمتشابهه، ويحلّ حلاله،
_________
(١) عبد الله بن عباس بن عبد المطلب القرشي الهاشمي (٣ ق هـ- ٦٨ هـ) : حبر الأمة، الصحابي الجليل، نشأ في بدء عصر النبوة. روى الأحاديث الصحيحة عن رسول الله صلّى الله عليه وسلّم، وشهد مع علي الجمل وصفين.
(الإصابة: ت ٤٧٧٢ والحلية ١/ ٣١٤) .
(٢) البرهان في علوم القرآن ١/ ١٢٨ والإتقان ١/ ١٤٦، ١٥٦ وفي شعب الإيمان ٢/ ٣٣١ أن عمر بن الخطاب رضي الله عنه قال: (تعلموا القرآن خمسا خمسا، فإن جبريل عليه السلام نزل القرآن على النبي صلى الله عليه وسلّم خمسا خمسا) .
(٣) الإتقان ١/ ١٤٧.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (16,'ويحرّم حرامه، ويعقل أمثاله «١» ، كما قال: وَما يَعْقِلُها إِلَّا الْعالِمُونَ [العنكبوت: ٤٣] أي أهل العلم بالله تعالى والمعرفة به خاصة.
قال سهل: في القرآن آيتان ما أشدّهما على من يجادل في القرآن، وهما قوله تعالى: مَا يُجادِلُ فِي آياتِ اللَّهِ إِلَّا الَّذِينَ كَفَرُوا [غافر: ٤] أي يماري في آيات الله ويخاصم بهوى نفسه وطبع جبلّة عقله، قال تعالى: وَلا جِدالَ فِي الْحَجِّ [البقرة: ١٩٧] أي لا مراء في الحج. والثانية قوله: وَإِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتابِ لَفِي شِقاقٍ بَعِيدٍ [البقرة: ١٧٦] .
قال النبي صلى الله عليه وسلّم: «معاشر الناس، لا تجادلوا في القرآن فإن جادل به المؤمن المهتدي أصاب، وإن جادل به المنافق المفتري أقام حجّة بالقياس والهوى بغير صواب» .
وقال النبي صلى الله عليه وسلّم: «شرار عباد الله يتبعون شرار المسائل ليمتحنوا بها عباد الله أعناتا» «٢» .
والله تعالى خصمهم يوم القيامة، لأن كل سائل مسؤول يوم القيامة ما أردت به.
وقال سهل: العجب كل العجب لمن قرأ القرآن ولم يعمل به، ولم يجتنب ما نهاه الله عنه، أما استحيا من الله ومحاربته ومخالفته وأمره ونهيه بعد علمه به؟ فأي شيء أعظم من هذه المحاربة؟ ألم يسمع وعده ووعيده؟ ألم يسمع ما وعد الله به من النكال فيرحم نفسه ويتوب؟
ألم يسمع قوله تعالى: إِنَّ رَحْمَتَ اللَّهِ قَرِيبٌ مِنَ الْمُحْسِنِينَ [الأعراف: ٥٦] فيجهد في الإحسان؟
ألم يسمع قوله: «ورحمتي سبقت عذابي» فيرغب في رحمته.
وقال سهل: اللهم أنت أكرمتهم بالموهبة الجميلة، وخصصتهم بهذه الفضيلة، اللهم فاعف عنا وعنهم، ثم قال: إن الله تعالى ما استولى وليا من أمة محمد صلّى الله عليه وسلّم إلّا علّمه القرآن إما ظاهرا وإما باطنا، قيل له: إن الظاهر نعرفه، فالباطن ما هو؟ قال: فهمه، وإن فهمه هو المراد. قال أبو بكر السّجزي: سمع مني هذه الحكاية الجنيد «٣» فقال: صدق سهل، كان عندنا ببغداد عبد أسود عجميّ اللسان، نسأله عن القرآن آية آية، فيجيبنا عن ذلك بأحسن جواب، وهو لا يحفظ القرآن، وتلك دلالة ولايته.
_________
(١) في المستدرك على الصحيحين ٢/ ٣١٧: (.... ونزل القرآن من سبعة أبواب على سبعة أحرف: زاجر وآمر، وحلال وحرام، ومحكم ومتشابه، وأمثال. فأحلوا حلاله ... ) . [.....]
(٢) المدخل إلى السنن الكبرى ص ٢٣٠، رقم ٣٠٧ وجامع العلوم والحكم ص ٩٣.
(٣) الجنيد بن محمد بن الجنيد البغدادي الخزاز (ت ٢٩٧ هـ ٩١٠ م) : صوفي، من علماء الدين، مولده ونشأته ووفاته ببغداد. أول من تكلم في علم التوحيد ببغداد. من آثاره: «رسائل» ، و «دواء الأرواح» .
(الحلية ١٠/ ٢٥٥ وتاريخ بغداد ٧/ ٢٤١) .',NULL,'19',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (17,'قال سهل: روي عن ابن مسعود «١» رضي الله تعالى عنه أنه قال: ينبغي لحامل القرآن أن يعرف بليله إذا الناس نائمون، وبصيامه إذا الناس يفطرون، وبحزنه إذا الناس يفرحون، وببكائه إذا الناس يضحكون، وبصمته إذا الناس يتكلمون، فينبغي أن يكون حامل القرآن باكيا حزينا حكيما عالما، لا جافيا ولا غائلا «٢» ، يعني أن لا يكون كذابا. قال سهل: أخبرني محمد بن سوار «٣» أنه حج سنة من السنين فرأى أيوب السّختياني «٤» قد ابتدأ بأول القرآن مصليا، وإذا بناحية منه رجل من أهل البصرة مستقبل الكعبة قد ابتدأ بسورة: وَيْلٌ لِلْمُطَفِّفِينَ [المطففين: ١] وهو يردد قوله تعالى: أَلا يَظُنُّ أُولئِكَ أَنَّهُمْ مَبْعُوثُونَ [المطففين: ٤] قال: فبلغ أيوب السختياني إلى ثلثي القرآن وذلك الرجل يردد هذه الآية، فلما كان عند السحر بلغ أيوب «الفيل» ، وانتهى الرجل إلى قوله: يَوْمَ يَقُومُ النَّاسُ لِرَبِّ الْعالَمِينَ [المطففين: ٦] وغشي عليه، فتقدمنا إلى الرجل فوجدناه ميتا.
وقد اختلف الناس في طلب فهم القرآن، فقوم طلبوا فهم القرآن بتكرار درسه ليستخرجوا فهم ظاهر أحكامه، فمنهم مقلّ ومنهم مكثر عالم عامل لله تعالى بمنازل الجنة، وعامل لله تعالى إيجابا، وعالم به لا عامل له، وقوم طلبوه لحفظ التلاوة والتعليم لغيره، منهم سليم في فعله، ومنهم مغتر بربه، ورجل كثير الدرس له ومراده تعلم طلب الألحان، ويريد أن يشار إليه، ويكسب من حطام الدنيا، فهو من أخسر الثلاثة عند الله تعالى. قال سهل: وأخبرني محمد بن سوار عن عمرو بن مرداس «٥» عن أبي هريرة «٦» رضي الله تعالى عنه قال: قال رسول الله صلّى الله عليه وسلّم:
_________
(١) عبد الله بن مسعود بن غافل بن حبيب الهذلي ( ... - ٣٢ هـ) : صحابي، من أكابرهم فضلا وعقلا، من السابقين إلى الإسلام. كان خادم رسول الله الأمين وصاحب سره. ولي بيت مال الكوفة. (الإصابة ت ٤٩٥٥ والحلية ١/ ١٢٤) .
(٢) شعب الإيمان ٢/ ٢٩٠ (رقم ١٨٠٧) وكتاب الزهد لابن أبي عاصم ص ١٦٢ ونسب هذا القول إلى سفيان الثوري في المدخل إلى السنن الكبرى ص ٣٣٩ (رقم ٥٥٧) .
(٣) محمد بن سوار: يقال إنه كان خال سهل بن عبد الله التستري. روي عن ابن عيينة، وعنه سهل. (تهذيب التهذيب ٩/ ١٨٦) .
(٤) أيوب السختياني: أيوب بن أبي تميمة كيسان السختياني البصري، أبو بكر (٦٦- ١٣١ هـ) : سيد فقهاء عصره. تابعي، من النساك الزهاد، من حفاظ الحديث. كان ثبتا ثقة. (الحلية ٣/ ٣) .
(٥) عمرو بن مرداس: من رواة الحديث عن بلال وأبي هريرة. (الإصابة ت ٦٥١٥) .
(٦) أبو هريرة: عبد الرحمن بن صخر الدوسي (٢٠ ق هـ- ٥٩ هـ) : صحابي، كان أكثر الصحابة حفظا للحديث ورواية له. لزم صحبة النبي صلى الله عليه وسلّم. ولي إمرة المدينة مدة، ثم البحرين. (الإصابة، الكنى ت ١١٧٩ والحلية ١/ ٣٧٦) .',NULL,'20',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (18,'«اقرؤوا القرآن بلحون العرب من غير تكلف لغيرها، ولا تقرءوه بلحون أهل الكنائس والبيع وأهل الأهواء والبدع، فإني وأمتي الأتقياء براء من التكلف، وإنه سيأتي أقوام من بعدي يرجّعون فيه أصواتهم ترجع القينات بالأغاني، مفتونة قلوبهم فتانة لقلب السامع، أولئك هم الغافلون» «١» . قال سهل: وإني أخاف بعد ثلاثمائة إلى ما فوقها أن يندرس القرآن بالتشاغل بالألحان والقصائد والأغاني، قيل له: وكيف ذلك يا أبا محمد؟ فقال: لأنهم ما أحدثوا هذه الألحان والقصائد والأغاني إلّا للتكسّب بها، حتى ملك إبليس قلوبهم، كما ملك قلوب شعراء الجاهلية، وحرموا فهم القرآن والعمل لله به. وقد حكى محمد بن سوار عن ابن أبي ذئب «٢» عن محمد بن عبد الرحمن «٣» عن ثوبان «٤» أنه سمع النبي صلى الله عليه وسلّم يقول: «سماع الأغاني ينسي القرآن ويشغل عن الذكر» . قال أبو بكر: كان أبو سعيد الخراز «٥» مقيما بمكة، وكان من أشد الناس محبة للسماع من قصائد الجذل وأشعار الغزل، فأخبرني غلامه أبو الأذنين أنه رآه بعد موته في المنام، وقال له: ما فعل الله بك يا أبا سعيد؟ فقال: غفر لي بعد توبيخ وددت أنه أمر بي إلى النار ولم يوبخني. فقلت له: ولم ذلك؟ قال: أوقفني الحق بين يديه من وراء حجاب الخوف، وقال لي: حملت أمري على ليلى وسعدى، ولولا أنك وقفت لي وقفة أردتني بها لأمرت بك إلى النار، فلما أن زال حجاب الخوف إلى حجاب الرضا قلت: يا إلهي لم أجد من يحمل عني ما حملتني غيرك فأشرت إليك، قال: صدقت، وأمر بي إلى الجنة، والله أعلم.
_________
(١) نوادر الأصول ٣/ ٢٥٥ وشعب الإيمان ٢/ ٥٤٠ (رقم ٢٦٤٩) ومجمع الزوائد ٧/ ١٦٩ والمعجم الأوسط ٧/ ١٨٣.
(٢) ابن أبي ذئب: محمد بن عبد الرحمن بن المغيرة، من بني عامر (٨٠- ١٥٨ هـ) : تابعي، من رواة الحديث من أهل المدينة، كان يفتي بها. (الأعلام ٦/ ١٨٩) .
(٣) محمد بن عبد الرحمن بن محيصن السهمي بالولاء ( ... - ١٢٣ هـ) : مقرىء أهل مكة بعد ابن كثير، وأعلم قرائها بالعربية، كان لا بأس به في الحديث. (الأعلام ٦/ ١٨٩) .
(٤) ثوبان بن يجدد، أبو عبد الله ( ... - ٥٤ هـ) : مولى رسول الله صلّى الله عليه وسلّم، اشتراه النبي ثم أعتقه، فلم يزل يخدمه إلى أن مات، فخرج ثوبان إلى الشام، واستقر بحمص، وتوفي بها. (الأعلام ٢/ ١٠٢) .
(٥) أبو سعيد الخراز: عبد الرحيم بن عبد الله بن عبد الرحيم بن البرقي ( ... - ٢٨٦ هـ) : شيخ الصوفية، وراوي السيرة. قال السلمي: هو إمام القوم في كل فن من علومهم. (سير أعلام النبلاء ١٣/ ٤٢٠ وتذكرة الحفاظ ٢/ ٦٣٧) . وثمت رجل آخر اسمه أبو سعيد الخراز، وهو أحمد بن عيسى البغدادي الصوفي ( ... - ٢٧٧ أو ٢٨٦ هـ) من كبار شيوخ الصوفية، وأحد المذكورين بالورع والمراقبة، وحسن الرعاية والمجاهدة. (تاريخ بغداد ٤/ ٢٧٦) .',NULL,'21',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (19,'<span data-type="title" id=toc-7>فصل في قوله بسم الله الرحمن الرَّحِيمِ</span>
قال أبو بكر: سئل سهل عن معنى: «بسم الله الرحمن الرَّحِيمِ» فقال:
الباء بَهاء الله عزَّ وجلَّ. والسين سناء الله عزَّ وجلَّ. والميم مجد الله عزّ وجلّ «١» .
والله: هو الاسم الأعظم الذي حوى الأسماء كلها، وبين الألف واللام منه حرف مكنى غيب من غيب إلى غيب، وسر من سر إلى سر، وحقيقة من حقيقة إلى حقيقة. لا ينال فهمه إلاَّ الطاهر من الأدناس، الآخذ من الحلال قواماً ضرورة الإيمان.
والرحمن: اسم فيه خاصية من الحرف المكنى بين الألف واللام.
والرحيم: هو العاطف على عباده بالرزق في الفرع والابتداء في الأصل رحمة لسابق علمه القديم.
قال أبو بكر: أي بنسيم روح الله اخترع من ملكه ما شاء رحمة لأنه رحيم. وقال علي بن أبي طالب رضي الله عنه: «الرحمن الرحيم» اسمان رقيقان أحدهما أرقُّ من الآخر «٢» ، فنفى الله تعالى بهما القنوط عن المؤمنين من عباده.
_________
(١) نسب هذا القول إلى النبي عيسى عليه السلام في الفردوس، بمأثور الخطاب ١/ ٢٢٩ وانظر مثل هذا القول في تأويل مشكل القرآن ص ٣٠٩. [.....]
(٢) نسب هذا القول إلى ابن عباس رضي الله عنه في عمدة الحفاظ ٢/ ٨٠ (رحم) .',NULL,'22',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (20,'<span data-type="title" id=toc-8>سورة فاتحة الكتاب</span>

<span data-type="title" id=toc-9>[سورة الفاتحة (١) : آية ٢]</span>
الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ (٢)
قال سهل: معنى: الْحَمْدُ لِلَّهِ [٢] الشكر لله، فالشكر لله هو الطاعة لله، والطاعة لله هي الولاية من الله تعالى كما قال الله تعالى: إِنَّما وَلِيُّكُمُ اللَّهُ وَرَسُولُهُ، وَالَّذِينَ آمَنُوا [المائدة: ٥٥] ولا تتم الولاية من الله تعالى إلا بالتبري ممن سواه. ومعنى: رَبِّ الْعالَمِينَ [٢] سيد الخلق المربّي لهم، والقائم بأمرهم، المصلح المدبر لهم قبل كونهم، وكون فعلهم المتصرف بهم لسابق علمه فيهم، كيف شاء لما شاء، وأراد وحكم وقدر من أمر ونهي، لا رب لهم غيره.

<span data-type="title" id=toc-10>[سورة الفاتحة (١) : الآيات ٤ الى ٥]</span>
مالِكِ يَوْمِ الدِّينِ (٤) إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ (٥)
مالِكِ يَوْمِ الدِّينِ [٤] أي يوم الحساب، إِيَّاكَ نَعْبُدُ [٥] أي نخضع ونذلّ ونعترف بربوبيتك ونوحّدك ونخدمك، ومنه اشتق اسم العبد. وَإِيَّاكَ نَسْتَعِينُ [٥] أي على ما كلفتنا بما هو لك، وإليك المشيئة والإرادة فيه، والعلم والإخلاص لك، ولن نقدر على ذلك إلاَّ بالمعونة والتسديد لنا منك، إذ لا حول لنا ولا قوة إلاَّ من عندك. فقيل له: أليس قد هدانا الله إلى الصراط المستقيم؟ قال: بلى، ولكن طلب الزيادة منه كما قال: وَلَدَيْنا مَزِيدٌ [ق: ٣٥] فكان معنى قوله: «اهدنا» : أمددنا منك بالمعونة والتمكين. وقال مرة أخرى: «اهدنا» معناه أرشدنا إلى دين الإسلام الذي هو الطريق إليك بمعونة منك، وهي البصيرة، فإنا لا نهتدي إلاَّ بك، كما قال: عَسى رَبِّي أَنْ يَهْدِيَنِي سَواءَ السَّبِيلِ [القصص: ٢٢] أي يرشدني قصد الطريق إليه. قال:
وسمعت سهلاً يحكي عن محمد بن سوار عن سفيان عن سالم عن أبي الجعد عن ثوبان قال:
قال رسول الله صلّى الله عليه وسلّم: «يقول الله عزَّ وجلَّ: قسمت الصلاة بيني وبين عبدي نصفين، فنصفها لي ونصفها لعبدي، ولعبدي ما سأل. قال: فإذا قال العبد: «الحمد لِلَّهِ رَبِّ العالمين» قال تعالى:
حمدني عبدي، فإذا قال: «الرحمن الرحيم» قال الله تعالى: أثنى عَليّ عبدي، وإذا قال: «مالك يَوْمِ الدين» يقول الله: فهذه الآيات لي ولعبدي بعدها ما سأل، وإذ قال: «إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِين اهدنا الصراط المستقيم» إلى آخره يقول الله عزَّ وجلَّ: هذا لعبدي ولعبدي ما سأل» «١» .
_________
(١) سنن ابن ماجة: الأدب، باب ثواب القرآن، حديث رقم ٣٧٨٤ وسنن أبي داود: الصلاة، باب القراءة في الفجر، حديث رقم ٨٢١ والترمذي: تفسير القرآن، باب: ومن سورة فاتحة الكتاب، حديث رقم ٢٩٥٣',NULL,'23',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (21,'قال سهل: معنى قوله: «مجدني عبدي» أي وصفني بكثرة الإحسان والإنعام، وقال سهل: وروي عن مجاهد «١» أنه قال: آمين اسم من أسماء الله تعالى «٢» ، وقال ابن عباس رضي الله تعالى عنهما: ما حسدتكم النصارى على شيء كما حسدتكم على قولكم آمين «٣» . وحكى محمد بن سوار عن ابن عيينة «٤» عن عمرو بن دينار «٥» عن جابر بن عبد الله «٦» رضي الله عنه قال: قال رسول الله صلّى الله عليه وسلّم: «استقيموا ولن تحصوا، واعلموا أن خير أعمالكم الصلاة، ولا يحافظ على الوضوء إلاَّ مؤمن، فإذا قال الإمام: ولا الضالين، فقولوا: آمين، فإن الله يرضى على قائلها، ويقبل صلاته، ويجيب دعاءه» «٧» . وحكى الزهري «٨» عن ابن المسيب «٩» عن أبي هريرة رضي الله تعالى عنه قال: قال رسول الله صلّى الله عليه وسلّم: «إذ قال الإمام: «ولا الضالين» قولوا:
آمين، فإن الملائكة يقولون آمين، فمن وافق تأمينه تأمين الملائكة غُفِرَ له ما تقدم من ذنبه» «١٠» .
_________
(١) مجاهد بن جبر، أبو الحجاج المكي (٢١- ١٠٤ هـ) : تابعي، مفسر، من أهل مكة، شيخ القراء والمفسرين أخذ التفسير عن ابن عباس. تنقل في الأسفار، واستقر في الكوفة. (الحلية ٣/ ٢٧٩) .
(٢) خطّأ أبو علي الفارسي من قال في (آمين) : إنه اسم من أسماء الله عزّ وجلّ. (سفر السعادة ص ١٣٤) ، وقال السخاوي في سفر السعادة: (وأما ما روي ... عن مجاهد أنه اسم من أسماء الله عزّ وجلّ فإن تأويله أن (آمين) لما تضمن الضمير الذي هو مصروف إلى الله عزَّ وجلَّ قيل: إنه اسم الله عزَّ وجلَّ على هذا التقدير، لا أن الكلمة اسم من أسمائه عزّ وجلّ دون الضمير) . وانظر عمدة الحفاظ ١/ ١٢٦ (أمن) .
(٣) في فيض القدير ٥/ ٤٤١: (ما حسدتكم اليهود على شيء ما حسدتكم على آمين) ، وفيه أيضا: (وأخرجه ابن ماجة مختصرا عن عائشة بلفظ: (ما حسدتكم اليهود على شيء ما حسدتكم على السلام والتأمين) .
(٤) سفيان بن عيينة بن ميمون الهلالي الكوفي، أبو محمد (١٠٧- ١٩٨ هـ) : محدث الحرم المكي، من الموالي ولد بالكوفة، وسكن مكة، وتوفي بها. كان حافظا ثقة، واسع العلم. حج سبعين سنة. (الحلية ٧/ ٢٧٠) .
(٥) عمرو بن دينار الجمحي بالولاء، أبو محمد الأشرم (٤٦- ١٢٦ هـ) : فقيه، كان مفتي أهل مكة، فارسي الأصل، مولده بصنعاء، ووفاته بمكة. (الأعلام ٥/ ٧٧) .
(٦) جابر بن عبد الله بن عمرو بن حرام الخزرجي الأنصاري السلمي (١٦- ٧٨ هـ) : صحابي، من المكثرين في الرواية عن النبي صلّى الله عليه وسلّم. وروى عنه جماعة من الصحابة. غزا تسع عشرة غزوة. (الأعلام ٢/ ١٠٤) .
(٧) القراءة خلف الإمام ص ١١- ٣٠.
(٨) الزهري: محمد بن مسلم بن عبد الله بن شهاب الزهري القرشي (٥٨- ١٢٤ هـ) : أول من دون الحديث وأحد أكابر الحفاظ والفقهاء. تابعي، من أهل المدينة. كان يحفظ ٢٢٠٠ حديث. (الأعلام ٧/ ٩٧) .
(٩) سعيد بن المسيب بن حزن المخزومي القرشي (١٣- ٩٤ هـ) : سيد التابعين، وأحد الفقهاء السبعة بالمدينة.
جمع بين الحديث والفقه والزهد والورع. (الحلية ٢/ ١٦١) .
(١٠) صحيح البخاري: كتاب التفسير، حديث رقم ٤٢٠٥.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (22,'<span data-type="title" id=toc-11>السورة التي يذكر فيها البقرة</span>

<span data-type="title" id=toc-12>[سورة البقرة (٢) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الم (١) ذلِكَ الْكِتابُ لا رَيْبَ فِيهِ هُدىً لِلْمُتَّقِينَ (٢) الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلاةَ وَمِمَّا رَزَقْناهُمْ يُنْفِقُونَ (٣)
قال سهل: الم [١] اسم الله عزَّ وجلَّ فيه معان وصفات يعرفها أهل الفهم به، غير أن لأهل الظاهر فيه معاني كثيرة «١» ، فأما هذه الحروف إذا انفردت، فالألف تأليف الله عزَّ وجلَّ ألف الأشياء كما شاء، واللام لطفه القديم، والميم مجده العظيم «٢» . قال سهل: لكل كتاب أنزله الله تعالى سر، وسر القرآن فواتح السور «٣» ، لأنها أسماء وصفات، مثل قوله: «المص، الر، المر، كهيعص، طسم، حمسق» فإذا جمعت هذه الحروف بعضها إلى بعض كانت اسم الله الأعظم، أي إذا أخذ من كل سورة حرف على الولاء، أي على ما أنزلت السورة وما بعدها على النسق: «الر» و «حم» و «نون» معناه الرحمن «٤» . وقال ابن عباس والضحاك: «الم» معناه:
أنا الله أعلم «٥» . وقال علي رضي الله عنه: هذه أسماء مقطعة، إذا أخذ من كل حرف حرف لا يشبه صاحبه فجمعن كان اسماً من أسماء الرحمن إذا عرفوه ودعوا به كان الاسم الأعظم الذي إذا دُعي به أجاب. وقال سهل: الم (١) ذلِكَ الْكِتابُ [١- ٢] الألف الله، واللام العبد، والميم محمد صلّى الله عليه وسلّم كي يتصل العبد بمولاه من مكان توحيده واقتدائه بنبيه. وقال سهل: بلغني عن ابن عباس أنه قال: أقسم الله تعالى أن هذا الكتاب الذي أنزل على محمد صلّى الله عليه وسلّم هو الكتاب الذي هو من عند الله تعالى فقال: الم (١) ذلِكَ الْكِتابُ الألف الله، واللام جبريل عليه السلام، والميم محمد صلّى الله عليه وسلّم، فأقسم الله تعالى بنفسه وجبريل ومحمد عليهما السلام «٦» . وقال: إن الله تعالى اشتق من اسمه الأعظم الألف واللام والهاء «٧» ، فقال: إِنِّي أَنَا اللَّهُ رَبُّ الْعالَمِينَ [القصص: ٣٠] واشتق لهم اسماً من أسمائه فجعله اسم نبيه صلّى الله عليه وسلّم، وآخر من اسم نبيه آدم عليه السلام فقال:
_________
(١) انظر تفسير أسرار الفواتح بحروف التهجي في البرهان ١/ ١٦٥- ١٧٨ والإتقان ٣/ ٢٤- ٣٤.
(٢) البرهان ١/ ١٧٣. [.....]
(٣) هذا قول الشعبي في الإتقان ٣/ ٢٤ وللصديق في البرهان ١/ ١٧٣، ١٧٤.
(٤) الإتقان ٣/ ٢٤ ومعاني القرآن وإعرابه للزجاج ١/ ٥٦.
(٥) الإتقان ٣/ ٢٤ والبرهان ١/ ١٧٤ ومعاني القرآن وإعرابه ١/ ٥٦، ٦٢.
(٦) معاني القرآن وإعرابه ١/ ٥٦.
(٧) تفسير القرطبي ١/ ١٠٢ وسفر السعادة ص ١٣٤.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (23,'ذلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آمَنُوا وَأَنَّ الْكافِرِينَ لاَ مَوْلى لَهُمْ [محمد: ١١] إلاَّ الطاغوت أي الشيطان.
ومعنى: لاَ رَيْبَ فِيهِ [٢] أي لا شك فيه. هُدىً لِلْمُتَّقِينَ [٢] أي بياناً للمتقين، والمتقون هم الذين تبرؤوا من دعوى الحول والقوة دون الله تعالى، رجعوا إلى اللجا والافتقار إلى حول الله وقوته في جميع أحوالهم، فأعانهم الله ورزقهم من حيث لا يحتسبون، وجعل لهم فرجاً ومخرجاً مما ابتلاهم الله به. قال سهل: حول الله وقوته فعله، وفعله بعلمه، وعلمه من صفات ذاته. وحول العبد وقوته دعواه الساعة وإلى الساعة، والساعة لا يملكها إلاَّ الله تعالى، فالمتقون الذين يؤمنون بالغيب فالله هو الغيب ودينه الغيب، فأمرهم الله عزَّ وجلَّ أن يؤمنوا بالغيب وأن يتبرؤوا عن الحول والقوة فيما أمروا به ونهوا عنه اعتقاداً وقولاً وفعلاً ويقولوا لا حول لنا عن معصيتك إلا بعصمتك، ولا قوة لنا على طاعتك إلاَّ بمعونتك، إشفاقاً منه عليهم، ونظراً لهم من أن يدعوا الحول والقوة والاستطاعة كما ادعاها من سبقت له الشقاوة، فلما عاينوا العذاب تبرؤوا من ذلك، فلم ينفعهم تبرؤهم حين عاينوا العذاب، وقد أخبر الله عمن هذا وصفهم في قوله: فَلَمْ يَكُ يَنْفَعُهُمْ إِيمانُهُمْ [غافر: ٨٥] أي دعواهم، لَمَّا رَأَوْا بَأْسَنا [غافر: ٨٥] فَما كانَ دَعْواهُمْ إِذْ جاءَهُمْ بَأْسُنا إِلَّا أَنْ قالُوا إِنَّا كُنَّا ظالِمِينَ [الأعراف: ٥] وكما ادعى الحول والقوة والاستطاعة فرعون وقال: متى شئت إني أؤمن، فلما آمن لم يقبل منه، قال الله تعالى: آلْآنَ وَقَدْ عَصَيْتَ [يونس: ٩١] .
قوله: وَمِمَّا رَزَقْناهُمْ يُنْفِقُونَ [٣] قال سهل: إن الله تعالى وصف بذلك من جبله بجبلّةٍ متعلقاً بسبب من سببه غير منفك عن مراقبته، وهم الذين لم يختاروا قط اختياراً، ولا أرادوا شيئاً دونه، ولا اختياراً دون اختياره لهم كما اختاره لهم، ولا أرادوا شيئاً منسوباً يغنيهم عنه، ومن غيره هم مبرؤون. قال أبو بكر: قيل لسهل: لقد آتاك الله الحكمة، فقال: قد أوتيت، إن شاء الله، الحكمة، وغيباً علمت من غيب سره، فأغناني عن علم ما سواه. وَأَنَّ إِلى رَبِّكَ الْمُنْتَهى [النجم: ٤٢] وبإتمام ما بدأني به من فضله وإحسانه.

<span data-type="title" id=toc-13>[سورة البقرة (٢) : آية ٥]</span>
أُولئِكَ عَلى هُدىً مِنْ رَبِّهِمْ وَأُولئِكَ هُمُ الْمُفْلِحُونَ (٥)
قوله عزَّ وجلَّ: أُولئِكَ عَلى هُدىً مِنْ رَبِّهِمْ [٥] أي بيان من ربهم بنور هدايته القلوب مشاهدة له، وسكوناً إليه من نوره الذي أفردهم به في سابق علمه، فلا ينطقون إلاَّ بالهدى، ولا يبصرون إلاَّ إلى الهدى، فالذين به اهتدوا غير مفارق لهم، فكانوا بذلك مشاهدين لأنهم غير غائبين عنه، ولو سئلوا عنه أخبروا، ولو أرادوا لسبقت الأشياء إرادتهم، فهم المفلحون، وهم المرشدون إلى الهدى والفلاح بهدايته لهم، والباقون في الجنة مع بقاء الحق عزَّ وجلَّ. قال سهل: ولقد بلغني أن الله تعالى أوحى إلى',NULL,'26',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (24,'داود عليه السلام: يا داود، انظر لا أفوتك أنا، فيفوتك كل شيء، فإني خلقت محمدا صلّى الله عليه وسلّم لأجلي، وخلقت آدم عليه السلام لأجله، وخلقت عبادي المؤمنين لعبادتي، وخلقت الأشياء لأجل ابن آدم، فإذا اشتغل بما خلقته من أجله حجبته عما خلقته من أجلي «١» .

<span data-type="title" id=toc-14>[سورة البقرة (٢) : آية ٢٢]</span>
الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِراشاً وَالسَّماءَ بِناءً وَأَنْزَلَ مِنَ السَّماءِ ماءً فَأَخْرَجَ بِهِ مِنَ الثَّمَراتِ رِزْقاً لَكُمْ فَلا تَجْعَلُوا لِلَّهِ أَنْداداً وَأَنْتُمْ تَعْلَمُونَ (٢٢)
قوله تعالى:
فَلا تَجْعَلُوا لِلَّهِ أَنْداداً [٢٢] قال سهل: أي أضداداً. فأكبر الأضداد النفس الأمّارة بالسوء المتطلعة إلى حظوظها ومناها بغير هدى من الله.

<span data-type="title" id=toc-15>[سورة البقرة (٢) : آية ٢٥]</span>
وَبَشِّرِ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ كُلَّما رُزِقُوا مِنْها مِنْ ثَمَرَةٍ رِزْقاً قالُوا هذَا الَّذِي رُزِقْنا مِنْ قَبْلُ وَأُتُوا بِهِ مُتَشابِهاً وَلَهُمْ فِيها أَزْواجٌ مُطَهَّرَةٌ وَهُمْ فِيها خالِدُونَ (٢٥)
وسئل عن قوله: وَأُتُوا بِهِ مُتَشابِهاً وَلَهُمْ فِيها أَزْواجٌ مُطَهَّرَةٌ [٢٥] فقال: ليس في الجنة شيء من فرش ولا آنية ولا لباس ولا طيب ولا طير، ولا شيء من النبات، ولا شيء من الفواكه كلها، فما في الدنيا يشبه ذلك إلاَّ اتفاق الأسماء فقط، وذلك أن رمان الجنة لا يشبه رمان الدنيا قط إلاَّ باتفاق الأسماء فقط، وكذلك التمر والعناب وأشباه ذلك، وإنما أراد بقوله: «متشابهاً» أي في اللون، مختلفاً في الطعم، وذلك أن الملائكة تأتي الأولياء في الجنة بالتفاح في الغداء، ثم يأتون به في العشاء، فيقول الأولياء: هذا ذلك. فيقال لهم: ذوقوه. فإذا ذاقوه أصابوا له غير طعم الأول، فلا يجوز أن تدفع قدرة الله تعالى أن يؤدي طعم التفاح طعم الرمان واللوز والسفرجل «٢» قال سهل: وإني لأعرف رجلاً من الأولياء رأى في الدنيا رمانة كبيرة كأكبر ما كان بين يدي رجل على شاطئ البحر، فقال له الولي: ما هذا بين يديك؟ فقال: رمانة رأيتها في الجنة فاشتهيتها، فأتاني الله بها، فلما وضعتها بين يدي ندمت على استعجالي ذلك في الدنيا. قال له ذلك الرجل:
أفآكل منها؟ قال له الرجل: إن قدرت أن تأكل منها فكل، فضرب بيده إليها فأكل أكثرها، فلما رآه يأكل منها أعظمه ذلك، فقال: أبشر بالجنة، فإني لم أعرف منزلتك قبل أكلك منها، وذلك أنه لا يأكل من طعام الجنة في الدنيا إلاَّ من هو من أهل الجنة. قال أبو بكر: فقلت لسهل: هل أخبرك الآكل من تلك الرمانة ما كان طعمها؟ قال: نعم، فيها طعم يجمع طعوم الفواكه، ويزيد على ذلك في طعمه لين وبرد ليس هو في شيء من طعوم الدنيا. قال أبو بكر: فلم أشك ولا مَنْ سمع هذه الحكاية من سهل إلاَّ أنه هو صاحب الرمانة والآكل منها.

<span data-type="title" id=toc-16>[سورة البقرة (٢) : آية ٣٠]</span>
وَإِذْ قالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي جاعِلٌ فِي الْأَرْضِ خَلِيفَةً قالُوا أَتَجْعَلُ فِيها مَنْ يُفْسِدُ فِيها وَيَسْفِكُ الدِّماءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ قالَ إِنِّي أَعْلَمُ ما لا تَعْلَمُونَ (٣٠)
وسئل عن قوله: إِنِّي جاعِلٌ فِي الْأَرْضِ خَلِيفَةً [٣٠] قال: إن الله تعالى قبل أن يخلق آدم عليه السلام قال للملائكة: إِنِّي جاعِلٌ فِي الْأَرْضِ خَلِيفَةً وخلق آدم عليه السلام من طين العزة من نور محمد صلّى الله عليه وسلّم، وأعلمه أن نفسه الأمّارة بالسوء أعدى عدو له «٣» ، وأنه خلقها
_________
(١) قوت القلوب ١/ ٤٣٠، ٢/ ١٣.
(٢) معاني القرآن وإعرابه ١/ ١٠٢.
(٣) يقصد قوله صلّى الله عليه وسلّم: (أعدى عدوك نفسك التي بين جنبيك) ، انظر كشف الخفاء ١/ ١٤٨، ١٦٠، ٢/ ٢٢٢، وسيذكر التستري هذا الحديث في تفسير سورة الأحقاف.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (25,'ليسارها عليه بمعلومه فيها خواطر وهمماً، يأمرها بإدامة الافتقار واللجأ إليه، إن أبدى عليها طاعة قالت: أعني، وإن حركت إلى معصية قالت: اعصمني، وإن حركت إلى نعمة قالت:
أوزعني، وإن قال لها: اصبري على البلاء، قالت: صبرني، ولا يساكن قلبه أدنى وسوسة لها دون الرجوع عنها إلى ربه، وجعل طبعها في الأمر ساكناً، وفي النهي متحركاً، وأمره بأن يسكن عن المتحرك، ويتحرك عن الساكن بلا حول ولا قوة إلا بالله، أي لا حول له عن معصيته إلاَّ بعصمته، ولا قوة له على طاعته إلاَّ بمعونته، ثم أمره بدخول الجنة والأكل منها رغداً حيث شاء. ونص عليه النهي عن الأكل من الشجرة، فلما دخل الجنة ورأى ما رأى قال: لو خلدنا، وإنما لنا أجل مضروب إلى غاية معلومة. فأتاه إبليس من قبل مساكنة قلبه بوسوسة نفسه في ذلك، فقال: هل أدلك على شجرة الخلد التي تتمناها في هذه الدار، وهي سبب البقاء والخلود:
وَقالَ مَا نَهاكُما رَبُّكُما عَنْ هذِهِ الشَّجَرَةِ إِلَّا أَنْ تَكُونا مَلَكَيْنِ [الأعراف: ٢٠] فكانت دلالته هذه غروراً. وألحق الله عزَّ وجلَّ به وسوسة العدو لسابق علمه فيه، وبلوغ تقديره وحكمه العادل عليه.
وأول نسيان وقع في الجنة نسيان آدم عليه السلام، وهو نسيان عمد لا نسيان خطأ، يعني تركُ العهد. قال سهل: بلغني عن بعض التابعين أنه قال: النسيان في كتاب الله عزَّ وجلَّ على وجهين:
الترك، كما قال في سورة البقرة: أَوْ نُنْسِها [١٠٦] أي نتركها فلا ننسخها، ومثله قوله:
وَلا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ [٢٣٧] أي لا تتركوا الفضل بينكم، كذلك في طه: فَنَسِيَ [طه: ٨٨] يعني ترك العهد، ومثله في تنزيل السجدة: فَذُوقُوا بِما نَسِيتُمْ لِقاءَ يَوْمِكُمْ هذا إِنَّا نَسِيناكُمْ [السجدة: ١٤] أي تركناكم في العذاب كما تركناكم من العصمة عند الإقامة على الإصر.
قال: والوجه الآخر النسيان هو الذي لا يحفظ فيذهب من ذكره، كما قال في الكهف:
فَإِنِّي نَسِيتُ الْحُوتَ [الكهف: ٦٣] أي لم أحفظ ذكره، وذلك أن الله تعالى جعل للشيطان شركة مع نفس الجبلة فيما هو من حظوظها الذي هو شيء غير الله تعالى، وقول موسى للخضر:
لاَ تُؤاخِذْنِي بِما نَسِيتُ [الكهف: ٧٣] أي ذهب مني ذكره، وقال في سبح: سَنُقْرِئُكَ فَلا تَنْسى [الأعلى: ٦] أي سنحفظك فلا تنسى، وهذا لإطراقه إلى تدبير نفسه.
ولم تكن فكرته اعتباراً، فكانت تكون عبادة، وإنما كانت فكرة بطبع نفس الجبلة، وهذا حكم الله تعالى به قبل خلق السماوات والأرض أنه لا يرى بقلبه عنده شيئاً، وهو غيره مساكناً',NULL,'28',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (26,'إياه، إلاَّ سلط عليه إبليس يوسوس في صدره إلى نفسه بالهوى في معنى دعته إليه، أو يرجع باللجأ إلى ربه والاعتصام به، فستر الله بذكره في أوطانه عند الإقامة على النهي، حتى تم سابق علم الله إليه فيما نهاه عنه أن سيكون ذلك منه، وصار فعله علم سنّة في ذريته إلى يوم القيامة.
ولم يرد الله معاني الأكل في الحقيقة، وإنما أراد معاني مساكنة الهمة مع شيء هو غيره، أي لا يهتم بشيء هو غيري. فآدم صلوات الله عليه لم يعتصم من الهمة والفعل في الجنة، فلحقه ما لحقه من أجل ذلك. وكذلك من ادعى ما ليس له، وساكنه قلبه ناظراً إلى هوى نفسه فيه، لحقه الترك من الله عزَّ وجلَّ مع ما حل عليه نفسه إلاَّ إن رحمه، فيعصمه من تدبيره وينصره على عدوه وعليها، يعني إبليس، فأهل الجنة معصومون فيها من التدبير الذي كانوا به في دار الدنيا، فآدم صلوات الله عليه لم يعصم من مساكنة قلبه تدبير نفسه بالخلود لما أدخل الجنة، ألا ترى أن البلاء دخل عليه من أجل سكون القلب إلى ما وسوست به نفسه، فغلب الهوى والشهوة على العلم والعقل والبيان ونور القلب لسابق القدر من الله تعالى، حتى انتهى كما قال النبي صلى الله عليه وسلّم:
«إن الهوى والشهوة يغلبان العلم والعقل» «١» .

<span data-type="title" id=toc-17>[سورة البقرة (٢) : آية ٣٧]</span>
فَتَلَقَّى آدَمُ مِنْ رَبِّهِ كَلِماتٍ فَتابَ عَلَيْهِ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ (٣٧)
وسئل عن قوله: فَتَلَقَّى آدَمُ مِنْ رَبِّهِ كَلِماتٍ فَتابَ عَلَيْهِ [٣٧] ما هذه الكلمات التي تلقاها من ربه؟ قال سهل: أخبرني محمد بن سوار عن أبيه عن الثوري «٢» عن عبد العزيز ابن رفيع «٣» عن عبد الله بن عمر «٤» رضي الله عنهما أنه قال: لما ذكر آدم صلوات الله عليه خطيئته قال: يا رب، أرأيتَ معصيتَك التي عصيتُك، أشيء كتبته عليّ قبل أن تخلقني، أم شيء ابتدعته؟
قال: بل شيء كتبته عليك إنك ستفعله بترك العصمة مني قبل أن أخلقك بخمسين ألف عام. قال آدم صلوات الله عليه: فكما كتبته عليّ فاغفر لي، فإنا قد ظَلَمْنا أَنْفُسَنا [الأعراف: ٢٣] أي
_________
(١) هذا ليس حديثا، بل قول للحارث بن أسد في حلية الأولياء ١٠/ ٨٨، وسيعاد ذكره في تفسير سورة ص: ٣٨، والشمس: ٩١.
(٢) الثوري: سفيان بن سعيد بن مسروق الثوري، أبو عبد الله (٩٧- ١٦١ هـ ٧١٦- ٧٧٨ م) : أمير المؤمنين في الحديث. كان سيد أهل زمانه في علوم الدين والتقوى. (الحلية ٦/ ٣٥٦، ٧/ ٣ وتاريخ بغداد ٩/ ١٥١)
(٣) عبد العزيز بن رفيع الأسدي، أبو عبد الله المكي الطائفي ( ... - ١٣٠ هـ) : تابعي، ثقة، يقوم حديثه مقام الحجة. (تهذيب التهذيب ٦/ ٣٠١) .
(٤) عبد الله بن عمر بن الخطاب العدوي (١٠ ق هـ- ٧٣ هـ) : صحابي، من أعز بيوتات قريش في الجاهلية.
كان جريئا جهيرا. شهد فتح مكة. وهو آخر من توفي فيها من الصحابة. أفتى الناس في الإسلام ستين سنة. (الحلية ١/ ٢٩٢ والإصابة ت ٤٨٢٥) .',NULL,'29',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (27,'بالإقامة على همة النفس والسكون إلى تدبيرها، وتبنا عن الرجوع إليه، وَإِنْ لَمْ تَغْفِرْ لَنا [الأعراف: ٢٣] أي في الدنيا وَتَرْحَمْنا [الأعراف: ٢٣] في ما بقي من أعمارنا لَنَكُونَنَّ مِنَ الْخاسِرِينَ [الأعراف: ٢٣] أي من الأشقياء المعذبين في الآخرة، فكانت هذه الكلمات التي قال الله تعالى: فَتَلَقَّى آدَمُ مِنْ رَبِّهِ كَلِماتٍ فَتابَ عَلَيْهِ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ [٣٧] . وروي عن النبي صلى الله عليه وسلّم أنه قال: «قال آدم لموسى عليهما السلام: بكم تجد الخطيئة كتبت عليّ من قبل أن أخلق؟ قال: بأربعين ألف عام. قال النبي صلى الله عليه وسلّم: فحج آدم وموسى عليهما السلام» «١» .
وسئل عن قوله: وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ [٣٠] فقال: أي نطهر أنفسنا بقولنا ما ألهمتنا تفضلاً منك علينا، تباركت ربنا.

<span data-type="title" id=toc-18>[سورة البقرة (٢) : الآيات ٤٠ الى ٤٢]</span>
يا بَنِي إِسْرائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَإِيَّايَ فَارْهَبُونِ (٤٠) وَآمِنُوا بِما أَنْزَلْتُ مُصَدِّقاً لِما مَعَكُمْ وَلا تَكُونُوا أَوَّلَ كافِرٍ بِهِ وَلا تَشْتَرُوا بِآياتِي ثَمَناً قَلِيلاً وَإِيَّايَ فَاتَّقُونِ (٤١) وَلا تَلْبِسُوا الْحَقَّ بِالْباطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنْتُمْ تَعْلَمُونَ (٤٢)
وسئل عن قوله: وَإِيَّايَ فَارْهَبُونِ [٤٠] ما هذه الرهبة التي أمرهم بها؟ فقال: أراد موضع نور النفس من بصر القلب والمعرفة من كلية القلب، لأن المكابدة والمجاهدة في الإيمان، فإذا سكن القلب من التقوى إلى الغير انكشف نور اليقين، ووصل العبد ساكناً بالإيمان لله توحيداً على تمكين. أعني سكون قلبه إلى مولاه، فصار نور اليقين يكشف عن علم اليقين، وهو الوصول إلى الله تعالى، فلا ذلك اليقين بنور اليقين إلى عين اليقين ولا مخلوق، لأنه نور من نور ذات الحق، لا بمعنى الحلول، ولا بمعنى الجمع، ولا بمعنى الاتصال، ولكن معنى اتصال العبد بمولاه من موضع توحيده وطاعته بالله ورسوله، فعلى قدر قوته من البصر بالله يدرك التقوى لله والرهبة إياه. وأصل التقوى: مباينة النفس، فيباينها في ذلك، ولا يساكنها شيئاً من ملاذ هواها، ولا ما تدعوه إليه من حظوظها التي لم تتعذر فيها. اعلم أن الناس يتفاضلون في القيامة على قدر نور يقينهم، فمن كان أوزن يقيناً كان أثقل ميزاناً، وكان من دونه في ميزانه. قيل: بِمَ تعرف صحة يقين العبد؟ قال: بقوة ثقته بالله تعالى، وحسن ظنه به، فالثقة بالله مشاهدة باليقين، وعين اليقين وكليته وكماله ونهايته الوصول إلى الله عزَّ وجلَّ.
فقيل له: ما معنى قوله: وَإِيَّايَ فَاتَّقُونِ [٤١] قال «٢» : أراد بذلك موضع علمه السابق فيهم، أي لا تأمنوا المكر والاستدراج، فتسكن قلوبكم إلى ملاحظة سلامتكم في الدنيا مع الإقامة على التقصير، وإلى حلمي عنكم في المعاجلة لكم في نفس أمنكم واغتراركم وغفلتكم فتهلكوا. وقال النبي صلى الله عليه وسلّم «لو زاد في اليقين عيسى بن مريم لمشى على الهواء كما مشى على الماء» «٣» ، وقد مشى نبينا محمد صلّى الله عليه وسلّم ليلة الإسراء على الهواء لقوة نور يقينه التي أعطاه الله تعالى
_________
(١) صحيح البخاري: كتاب الأنبياء، باب وفاة موسى، حديث رقم ٣٢٢٨ والسنن الكبرى ٦/ ٣٩٤.
(٢) حلية الأولياء ١٠/ ١٩٩. [.....]
(٣) كتاب الزهد الكبير ٢/ ٣٥٧.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (28,'من نوره زيادة نور إلى نور كان من الله تعالى. وقوله صلّى الله عليه وسلّم: «لو ثبتت المعرفة على قلب داود صلوات الله عليه ولم يغفل ما عصى» فلعمري أن المعرفة أدرجت في أوطانها لتجري عليه ما كان من علم الله سابقاً فيه، فلا بد من إظهاره على أوصافه إذا كان على حتم لا يتغير العلم إلى غير ما علم العالم جل وعز، فإنما ستر الله عزَّ وجلَّ في أوطان داود صلوات الله عليه نور اليقين الذي به يبصر عين اليقين وكليته، ليتم حكم الله تعالى فيه، ألا ترى أن العبد إنما ينظر إلى الحق بسبب لطيفة من الحق بوصولها إلى قلبه هي من أوصاف ذات ربه ليست بمكونة ولا مخلوقة ولا موصولة ولا مقطوعة، وهي سر من سر إلى سر وغيب من غيب إلى غيب، فبالله اليقين، والعبد موقن بسبب منه إليه على قدر ما قسم الله له من الموهبة وجملة سويداء قلبه. وللإيمان وطنان، وهو ما سكن فلم يخرج، ونور اليقين خطرات، فإذا سكن واستقر صار إيماناً، واليقين خطرات بعده، فهو في المريد هكذا حاله أبداً.
وسئل عن قوله: وَلا تَلْبِسُوا الْحَقَّ بِالْباطِلِ [٤٢] الآية، فقال: أي لا تلبسوا بأمر الدنيا أمر الآخرة. وأراد لا يحل لأهل الحق كتمان الحق عن أهله خاصة، عمن يرجون هدايته إلى الله عزَّ وجلَّ، فأما أهله فإنهم يزدادون بصيرة به، وأما من كان من غير خاصة أهله فإن قول الحق لهم هداية وإرشاد إلى الله تعالى.

<span data-type="title" id=toc-19>[سورة البقرة (٢) : آية ٤٥]</span>
وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلاةِ وَإِنَّها لَكَبِيرَةٌ إِلاَّ عَلَى الْخاشِعِينَ (٤٥)
وسئل عن قوله: وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلاةِ [٤٥] الآية، فقال: الصبر هاهنا الصوم والصلاة وصلة المعرفة، فمن صحت له الصلاة، وهي الوصلة، لم يبقَ له على الله تهمة، إِذ السؤال تهمة، ولا يبقى السؤال مع الوصلة، ألا ترى إلى قوله: وَإِنَّها لَكَبِيرَةٌ إِلَّا عَلَى الْخاشِعِينَ [٤٥] .

<span data-type="title" id=toc-20>[سورة البقرة (٢) : آية ٤٨]</span>
وَاتَّقُوا يَوْماً لا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئاً وَلا يُقْبَلُ مِنْها شَفاعَةٌ وَلا يُؤْخَذُ مِنْها عَدْلٌ وَلا هُمْ يُنْصَرُونَ (٤٨)
وسئل عن قوله: وَلا يُقْبَلُ مِنْها شَفاعَةٌ وَلا يُؤْخَذُ مِنْها عَدْلٌ وَلا هُمْ يُنْصَرُونَ [٤٨] أي لو جاءت بكل شيء من الأعمال من كبير أو صغير أو كثير أو قليل لم يتقبل ذلك منها، ولا شيء منه عند حصولهم في القيامة، والعدل: المثل، ألا ترى إلى قوله:
أَوْ عَدْلُ ذلِكَ صِياماً [المائدة: ٩٥] أي مثله وجزاءه.

<span data-type="title" id=toc-21>[سورة البقرة (٢) : آية ٥٥]</span>
وَإِذْ قُلْتُمْ يا مُوسى لَنْ نُؤْمِنَ لَكَ حَتَّى نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنْتُمْ تَنْظُرُونَ (٥٥)
وسئل عن قوله: فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنْتُمْ تَنْظُرُونَ [٥٥] قال: الصاعقة: الموت، والصاعقة: كل عذاب مهلك ينزله الله تعالى بمن يشاء من عباده، فينظرون إلى ذلك عياناً، ويريه غيرهم فيهم اعتبارا وتحذيرا.

<span data-type="title" id=toc-22>[سورة البقرة (٢) : الآيات ٧١ الى ٧٢]</span>
قالَ إِنَّهُ يَقُولُ إِنَّها بَقَرَةٌ لا ذَلُولٌ تُثِيرُ الْأَرْضَ وَلا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لا شِيَةَ فِيها قالُوا الْآنَ جِئْتَ بِالْحَقِّ فَذَبَحُوها وَما كادُوا يَفْعَلُونَ (٧١) وَإِذْ قَتَلْتُمْ نَفْساً فَادَّارَأْتُمْ فِيها وَاللَّهُ مُخْرِجٌ ما كُنْتُمْ تَكْتُمُونَ (٧٢)
وسئل عن قوله: لا شِيَةَ فِيها [٧١] فقال: أي لا علامة فيها تشينها، ولا لون يخالف لون سائر جسدها. وتلك حكمة من صانعها، وعبرة لمن اعتبر بها، وزاد لإيمانه وتوحيده يقيناً.
قوله: وَإِذْ قَتَلْتُمْ نَفْساً فَادَّارَأْتُمْ فِيها [٧٢] أي تنازعتم فيها. قوله: فَلِمَ قَتَلْتُمُوهُمْ إِنْ كُنْتُمْ صادِقِينَ [آل عمران: ١٨٣] قال سهل: هذا توبيخ من الله عزَّ وجلَّ لهم بما كان من آبائهم من قتلهم الأنبياء. ألا ترى أنه لم يقتل المخاطبون بهذه الآية نبياً في وقت محمد صلّى الله عليه وسلّم، ولا كان في',NULL,'31',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (29,'وقتهم نبي غيره، فواجههم بفعل من كانوا من نسلهم ومن فوقهم، كما واجه النبي صلى الله عليه وسلّم بما خاطب به أمته، وذلك قوله: يا أَيُّهَا النَّبِيُّ إِذا طَلَّقْتُمُ النِّساءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ [الطلاق: ١] وكذلك معنى قوله: عَمَّ يَتَساءَلُونَ (١) عَنِ النَّبَإِ الْعَظِيمِ [النبأ: ١- ٢] لأي علة تسألون محمداً صلّى الله عليه وسلّم وهو أعلم بذلك.
وسئل عن قوله: فَما أَصْبَرَهُمْ عَلَى النَّارِ [١٧٥] فقال: أي على الفتوى من غير علم من السنة والشرع، والعبودية بعمل أهل النار.

<span data-type="title" id=toc-23>[سورة البقرة (٢) : آية ١٠٢]</span>
وَاتَّبَعُوا ما تَتْلُوا الشَّياطِينُ عَلى مُلْكِ سُلَيْمانَ وَما كَفَرَ سُلَيْمانُ وَلكِنَّ الشَّياطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَما أُنْزِلَ عَلَى الْمَلَكَيْنِ بِبابِلَ هارُوتَ وَمارُوتَ وَما يُعَلِّمانِ مِنْ أَحَدٍ حَتَّى يَقُولا إِنَّما نَحْنُ فِتْنَةٌ فَلا تَكْفُرْ فَيَتَعَلَّمُونَ مِنْهُما ما يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْجِهِ وَما هُمْ بِضارِّينَ بِهِ مِنْ أَحَدٍ إِلاَّ بِإِذْنِ اللَّهِ وَيَتَعَلَّمُونَ ما يَضُرُّهُمْ وَلا يَنْفَعُهُمْ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَراهُ ما لَهُ فِي الْآخِرَةِ مِنْ خَلاقٍ وَلَبِئْسَ ما شَرَوْا بِهِ أَنْفُسَهُمْ لَوْ كانُوا يَعْلَمُونَ (١٠٢)
قوله تعالى: وَما هُمْ بِضارِّينَ بِهِ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اللَّهِ [١٠٢] أي بعلم الله السابق فيه قبل وقوع ذلك الفعل من الفاعل. قوله تعالى:
اتَّقُوا اللَّهَ حَقَّ تُقاتِهِ [آل عمران: ١٠٢] أراد فيما تعبدكم به لا فيما يستحقه الحق في ذاته عزّ وجلّ. قوله: فَأَنْزَلْنا عَلَى الَّذِينَ ظَلَمُوا رِجْزاً [٥٩] قال: الرجز هو العذاب.

<span data-type="title" id=toc-24>[سورة البقرة (٢) : آية ١١٢]</span>
بَلى مَنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ فَلَهُ أَجْرُهُ عِنْدَ رَبِّهِ وَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ (١١٢)
قوله تعالى:
بَلى مَنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ [١١٢] قال سهل: أي دينه، كما قال في سورة النساء:
وَمَنْ أَحْسَنُ دِيناً مِمَّنْ أَسْلَمَ وَجْهَهُ [النساء: ١٢٥] أي ممن أخلص دينه لله، وهو الإسلام وشرائعه، وقال، أي في لقمان: وَمَنْ يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَهُوَ مُحْسِنٌ [لقمان: ٢٢] يعني يخلص دينه لله.
وسئل عن قوله: لا يَعْلَمُونَ الْكِتابَ إِلَّا أَمانِيَّ [٧٨] يعني أنهم يتمنون على الله الباطل ميلاً إلى هوى نفوسهم بغير هدى من الله، يعني اليهود. قوله: وَأَيَّدْناهُ بِرُوحِ الْقُدُسِ [٨٧] قال: القدس هو الحق، يعني الذي طهّر من الأولاد والشركاء والصاحبة.

<span data-type="title" id=toc-25>[سورة البقرة (٢) : آية ١٢٨]</span>
رَبَّنا وَاجْعَلْنا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنا أُمَّةً مُسْلِمَةً لَكَ وَأَرِنا مَناسِكَنا وَتُبْ عَلَيْنا إِنَّكَ أَنْتَ التَّوَّابُ الرَّحِيمُ (١٢٨)
قوله: وَمِنْ ذُرِّيَّتِنا أُمَّةً مُسْلِمَةً لَكَ [١٢٨] قال: «الأمة» : الجماعة، و «مسلمة لك» ، أي: مسلمة لأمرك ونهيك، بالرضا والقبول منك.

<span data-type="title" id=toc-26>[سورة البقرة (٢) : آية ١٣٤]</span>
تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَها ما كَسَبَتْ وَلَكُمْ ما كَسَبْتُمْ وَلا تُسْئَلُونَ عَمَّا كانُوا يَعْمَلُونَ (١٣٤)
قيل له: ما معنى: تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَها مَا كَسَبَتْ [١٣٤، ١٤١] قال: أي تلك جماعة مضت لسابق علم الله فيهم.

<span data-type="title" id=toc-27>[سورة البقرة (٢) : آية ١٤٣]</span>
وَكَذلِكَ جَعَلْناكُمْ أُمَّةً وَسَطاً لِتَكُونُوا شُهَداءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيداً وَما جَعَلْنَا الْقِبْلَةَ الَّتِي كُنْتَ عَلَيْها إِلاَّ لِنَعْلَمَ مَنْ يَتَّبِعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلى عَقِبَيْهِ وَإِنْ كانَتْ لَكَبِيرَةً إِلاَّ عَلَى الَّذِينَ هَدَى اللَّهُ وَما كانَ اللَّهُ لِيُضِيعَ إِيمانَكُمْ إِنَّ اللَّهَ بِالنَّاسِ لَرَؤُفٌ رَحِيمٌ (١٤٣)
قوله: وَسَطاً [١٤٣] أي عدلاً. فالمؤمن مصدّق لعباده، كما قال: يُؤْمِنُ بِاللَّهِ وَيُؤْمِنُ لِلْمُؤْمِنِينَ [التوبة: ٦١] ، أي: يصدق الله ويصدق المؤمنين. قوله تعالى: إِنَّ اللَّهَ بِالنَّاسِ لَرَؤُفٌ رَحِيمٌ [١٤٣] أي شديد الرحمة والرأفة بهم، يعني الرفق والحلم عنهم لعلمه بضعفهم، وأن لا حال لهم إليه لا به ولا منه.

<span data-type="title" id=toc-28>[سورة البقرة (٢) : آية ١٤٨]</span>
وَلِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيها فَاسْتَبِقُوا الْخَيْراتِ أَيْنَ ما تَكُونُوا يَأْتِ بِكُمُ اللَّهُ جَمِيعاً إِنَّ اللَّهَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (١٤٨)
لِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيها
[١٤٨] أراد أن الله تعالى يولي أهل كل ملة إلى الجهة التي يشاء.

<span data-type="title" id=toc-29>[سورة البقرة (٢) : آية ١٥٥]</span>
وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِنَ الْخَوْفِ وَالْجُوعِ وَنَقْصٍ مِنَ الْأَمْوالِ وَالْأَنْفُسِ وَالثَّمَراتِ وَبَشِّرِ الصَّابِرِينَ (١٥٥)
قوله تعالى: وَبَشِّرِ الصَّابِرِينَ [١٥٥] قال: هم الذين صار الصبر لهم عيشاً وراحةً ووطناً، يتلذذون بالصبر لله تعالى على كل حال.

<span data-type="title" id=toc-30>[سورة البقرة (٢) : آية ١٥٧]</span>
أُولئِكَ عَلَيْهِمْ صَلَواتٌ مِنْ رَبِّهِمْ وَرَحْمَةٌ وَأُولئِكَ هُمُ الْمُهْتَدُونَ (١٥٧)
قوله: أُولئِكَ عَلَيْهِمْ صَلَواتٌ مِنْ رَبِّهِمْ وَرَحْمَةٌ وَأُولئِكَ هُمُ الْمُهْتَدُونَ [١٥٧] قال سهل: أراد بالصلاة عليهم الترحم عليهم، أي ترحم من ربهم. وقال النبي صلى الله عليه وسلّم: «اللهم صلِّ',NULL,'32',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (30,'على آل أبي أوفى» «١» حين أتوه بالصدقات، أي ترحم عليهم. وقال سهل: حدثنا محمد بن سوار عن أبي عمرو بن العلاء «٢» أنه قال: الصلاة على ثلاثة أوجه، أحدها: الصلاة المفروضة بالركوع والسجود كما قال: فَصَلِّ لِرَبِّكَ وَانْحَرْ [الكوثر: ٢] أي خذ شمالك بيمينك في الصلاة متذلّلاً متخشعاً بين يدي الله تعالى، كذا روي عن علي رضي الله عنه. والوجه الثاني: الترحم. والوجه الثالث: الدعاء مثل الصلاة على الميت، وقد قال النبي صلى الله عليه وسلّم: «إذا دُعِيَ أحدُكم إلى الطعام فليجب فإن كان صائماً فليصلِّ» «٣» أي فليَدعُ لهم بالبركة. وقال عليه الصلاة والسلام في حديثه: «وصلَّت عليكم الملائكة» «٤» أي ترحَّمت عليكم. وقال عليه الصلاة والسلام في حديثه: «وإذا أكل عنده الطعامُ صلَّت عليه الملائكة حتى يمسي» أي دعت له الملائكة. قال سهل: الصلاة على وجهين أحدهما الاستغفار، والآخر المغفرة، فأما الاستغفار فقوله: وَصَلِّ عَلَيْهِمْ [التوبة: ١٠٣] أي استغفر لهم وَصَلَواتِ الرَّسُولِ [التوبة: ٩٩] أي استغفار الرسول. وما المغفرة فقوله تعالى:
هُوَ الَّذِي يُصَلِّي عَلَيْكُمْ [الأحزاب: ٤٣] أي يغفر لكم وملائكته، أي يستغفرون لكم، ومثله:
إِنَّ اللَّهَ وَمَلائِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّ [الأحزاب: ٥٦] أي أن الله يغفر للنبي، وتستغفر له الملائكة ثم قال: يَا أَيُّهَا الَّذِينَ آمَنُوا صَلُّوا عَلَيْهِ [الأحزاب: ٥٦] أي استغفروا له. وفي البقرة: صَلَواتٌ مِنْ رَبِّهِمْ [١٥٧] أي مغفرة من ربهم.

<span data-type="title" id=toc-31>[سورة البقرة (٢) : آية ١٦١]</span>
إِنَّ الَّذِينَ كَفَرُوا وَماتُوا وَهُمْ كُفَّارٌ أُولئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَالْمَلائِكَةِ وَالنَّاسِ أَجْمَعِينَ (١٦١)
قوله: عَلَيْهِمْ لَعْنَةُ اللَّهِ [١٦١] أي الطرد لهم من رحمة الله والإبعاد، وكذلك كلّ ملعون مطرود.

<span data-type="title" id=toc-32>[سورة البقرة (٢) : آية ١٦٦]</span>
إِذْ تَبَرَّأَ الَّذِينَ اتُّبِعُوا مِنَ الَّذِينَ اتَّبَعُوا وَرَأَوُا الْعَذابَ وَتَقَطَّعَتْ بِهِمُ الْأَسْبابُ (١٦٦)
قوله: وَتَقَطَّعَتْ بِهِمُ الْأَسْبابُ [١٦٦] أي الوصلات التي كانوا يتواصلون بها في الدنيا، وتنعقد المودات بينهم من أجلها من غير طاعة الله ورسوله وغير مرضاته.

<span data-type="title" id=toc-33>[سورة البقرة (٢) : آية ١٨٦]</span>
وَإِذا سَأَلَكَ عِبادِي عَنِّي فَإِنِّي قَرِيبٌ أُجِيبُ دَعْوَةَ الدَّاعِ إِذا دَعانِ فَلْيَسْتَجِيبُوا لِي وَلْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُدُونَ (١٨٦)
قوله:
فَلْيَسْتَجِيبُوا لِي [١٨٦] قال: بالدعاء، وَلْيُؤْمِنُوا بِي [١٨٦] أي يصدقوني، فأنا حيث ما دعاني مخلصاً لا آيساً ولا قنطا.

<span data-type="title" id=toc-34>[سورة البقرة (٢) : آية ١٩٧]</span>
الْحَجُّ أَشْهُرٌ مَعْلُوماتٌ فَمَنْ فَرَضَ فِيهِنَّ الْحَجَّ فَلا رَفَثَ وَلا فُسُوقَ وَلا جِدالَ فِي الْحَجِّ وَما تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللَّهُ وَتَزَوَّدُوا فَإِنَّ خَيْرَ الزَّادِ التَّقْوى وَاتَّقُونِ يا أُولِي الْأَلْبابِ (١٩٧)
قوله: وَتَزَوَّدُوا فَإِنَّ خَيْرَ الزَّادِ التَّقْوى [١٩٧] قال:
هو الرفيق إلى ذكر الله تعالى خوفاً، إذ لا زاد للمحب سوى محبوبه، وللعارف سوى معروفه.
وقال في قوله: مَنِ اسْتَطاعَ إِلَيْهِ سَبِيلًا [آل عمران: ٩٧] قال: الزاد والراحلة، ثم قال: أتدرون
_________
(١) صحيح البخاري: كتاب الزكاة، باب صلاة الإمام، حديث رقم ١٤٢٦.
(٢) أبو عمرو بن العلاء: زبان بن عمار التميمي المازني البصري، أبو عمرو، ويلقب أبوه بالعلاء (٧٠- ١٥٤ هـ) من أئمة اللغة والأدب، وأحد القراء السبعة. ولد بمكة، ونشأ بالبصرة، وتوفي بالكوفة. (الأعلام ٣/ ٤١) .
(٣) صحيح مسلم: باب الأمر بإجابة الداعي ١٤٣١ وسنن الترمذي: باب ما جاء في إجابة الصائم الدعوة ٧٨٠ وسنن أبي داود: باب في الصائم ٢٤٦٠.
(٤) مسند أحمد ٣/ ١٣٨ وصحيح ابن حبان ١٢/ ١٠٧ رقم ٥٢٩٦ والسنن الكبرى ١٠١٢٩.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (31,'ما الزاد والراحلة؟ فقالوا: لا. فقال: الزاد الذكر، والراحلة الصبر. قال «١» : وقد صحبه رجل في طريق مكة فلم يجد يومين شيئاً فقال: يا أستاذ أحتاج إلى قوت. فقال: القوت هو الله. فقال: لا بد من قوت يقوم به الجسد. فقال: الأجساد كلها بالله عزَّ وجلَّ وأنشد: [من البسيط]
يا حِبُّ زِدْنِي سَقَاكَ الشَّوْقُ من دِيَمٍ ... يَزِيدُنِي صَوْبُها الأحزانَ والكربا
ودامَ لي لوعةٌ في القَلْبِ تَحْرِقُنِي ... إِنِّي متى أَزدادُ حُبّاً زادني طربا
ثم قال: الدنيا هي التي قطعت المنقطعين إلى الله عن الله عزَّ وجلَّ. وقال «٢» : عيش الملائكة في الطاعة، وعيش الأنبياء بالعلم وانتظار الفرج «٣» ، وعيش الصديقين بالاقتداء، وعيش سائر الناس [عالماً كان أو جاهلاً، زاهداً كان أو عابداً] «٤» في الأكل والشرب.
قوله: وَاتَّقُونِ يا أُولِي الْأَلْبابِ [١٩٧] أي يا أهل الفهم عني بالعقول السليمة. وقال:
إن الله تعالى أمرهم أن يتقوه على مقدار طاقات عقولهم بما خصهم به من نور الهداية بذاته، والقبول منه، وإفرادهم بالمعنى الذي ركبه فيهم، وعلمه بهم قبل خلقهم، فذكرهم تلك النعمة عليهم، ودعاهم بتلك النعمة التي سبقت لهم إلى الاعتراف بنعمة ثانية بعد الموهبة الأزلية، وهي حقيقة المعرفة، وقبول العلم بالعمل خالصاً له. قيل: فما معنى التقوى وحقيقته؟ قال:
الحقيقة لله عزَّ وجلَّ أن تعاجل لدى العمل القليل بالموت، وكذا الخطايا بالعقوبة، فيعرف ذلك فيتقيه، فلا يتكل على شيء سواه. قيل له: قد اختلفت أسباب تقوى الخلق؟ قال: نعم، كما اختلف أفعالهم. قال أبو بكر: فقلت: لقد ثبت في القرآن أن تقوى كل امرئ على حسب طاقته.
فقال: نعم، قد قال الله تعالى: فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَأَطِيعُوا [التغابن: ١٦] فردهم إلى ما في طاقتهم. فقلت له: لقد قال الله تعالى: اتَّقُوا اللَّهَ حَقَّ تُقاتِهِ [آل عمران: ١٠٢] قال سهل: أما أصحابنا فيقولون إن هذا الخطاب لقوم مخصوصين بأعيانهم، لأنهم طولبوا بما لم يطالب به الأنبياء عليهم السلام، وكما قال إبراهيم ويعقوب لأولادهما: يا بَنِيَّ إِنَّ اللَّهَ اصْطَفى لَكُمُ الدِّينَ فَلا تَمُوتُنَّ إِلَّا وَأَنْتُمْ مُسْلِمُونَ [١٣٢] . وإنما تعبد الله الخلق على حسب طاقاتهن، والذين قيل لهم: اتَّقُوا اللَّهَ حَقَّ تُقاتِهِ [آل عمران: ١٠٢] طولبوا بالتقوى على حسب معرفتهم بالله، فكان معنى ذلك، أي اتقوا الله حق تقاته ما قدرتم عليه، لا أنه رخص في ترك التقوى بتلك
_________
(١) حلية الأولياء ١٠/ ١٩٨.
(٢) حلية الأولياء ١٠/ ١٩٨.
(٣) في الحلية: (وانتظار الوحي) .
(٤) ما بين القوسين إضافة من الحلية.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (32,'الآية: وَلا تَمُوتُنَّ إِلَّا وَأَنْتُمْ مُسْلِمُونَ [آل عمران: ١٠٢] أي مسلمون لأمر الله بكل حال مفوضون إليه، والآخرون ردوا إلى الاجتهاد، فافهم الفرق بين الاثنين في الخطاب، إذا كان اللفظ متفقاً والمعنى مختلفاً خاص وعام. قال أبو بكر: ثم قال سهل: لو دعا المتقون على المسرفين لهلك الأولون والآخرون منهم، ولكن الله جعل المتقين رحمة للظالمين ليستنقذهم بهم، فإنّ أكرم الخلق على الله عزَّ وجلَّ المتقون كما قال الله: إِنَّ أَكْرَمَكُمْ عِنْدَ اللَّهِ أَتْقاكُمْ [الحجرات: ١٣] فمن أراد كرامة الله عزَّ وجلَّ فليتَّقِه، فإنه ينال بالتقوى كرامته، والدخول إلى جنته، ويسكن في جواره، ويفوز فوزاً عظيماً، وقد قال النبي صلى الله عليه وسلّم: «من أصلح سريرته أصلح الله علانيته ومن اتقى الله في سره قربه وأدناه» «١» .

<span data-type="title" id=toc-35>[سورة البقرة (٢) : آية ٢٠١]</span>
وَمِنْهُمْ مَنْ يَقُولُ رَبَّنا آتِنا فِي الدُّنْيا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنا عَذابَ النَّارِ (٢٠١)
قوله: رَبَّنا آتِنا فِي الدُّنْيا حَسَنَةً [٢٠١] أي العلم والعبادة خالصاً وَفِي الْآخِرَةِ حَسَنَةً [٢٠١] أي الرضا، كما قال: رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ [المائدة: ١١٩] .

<span data-type="title" id=toc-36>[سورة البقرة (٢) : آية ٢٢٤]</span>
وَلا تَجْعَلُوا اللَّهَ عُرْضَةً لِأَيْمانِكُمْ أَنْ تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِ وَاللَّهُ سَمِيعٌ عَلِيمٌ (٢٢٤)
وسئل عن قوله: وَلا تَجْعَلُوا اللَّهَ عُرْضَةً لِأَيْمانِكُمْ أَنْ تَبَرُّوا [٢٢٤] ما هذا البر؟ فقال: يعني أن لا تصلوا القرابة لعلة اليمين. فقيل له: لقد قال: لَيْسَ الْبِرَّ أَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ [١٧٧] فقال: يعني ليس من التقوى أن لا تفعلوا غير ذلك وَلكِنَّ الْبِرَّ مَنْ آمَنَ بِاللَّهِ [١٧٧] الآية. ألا تراه كيف قال: أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ أَنْفُسَكُمْ [٤٤] يعني اليهود كانوا يأمرون إخوانهم من الرضاعة بطاعة الله تعالى واتباع النبي صلى الله عليه وسلّم، وكانوا لا يفعلون ذلك.

<span data-type="title" id=toc-37>[سورة البقرة (٢) : آية ٢٣٥]</span>
وَلا جُناحَ عَلَيْكُمْ فِيما عَرَّضْتُمْ بِهِ مِنْ خِطْبَةِ النِّساءِ أَوْ أَكْنَنْتُمْ فِي أَنْفُسِكُمْ عَلِمَ اللَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلكِنْ لاَّ تُواعِدُوهُنَّ سِرًّا إِلاَّ أَنْ تَقُولُوا قَوْلاً مَعْرُوفاً وَلا تَعْزِمُوا عُقْدَةَ النِّكاحِ حَتَّى يَبْلُغَ الْكِتابُ أَجَلَهُ وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنْفُسِكُمْ فَاحْذَرُوهُ وَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ حَلِيمٌ (٢٣٥)
قوله: وَلكِنْ لاَّ تُواعِدُوهُنَّ سِرًّا [٢٣٥] أي مناكحة. قوله: وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنْفُسِكُمْ فَاحْذَرُوهُ [٢٣٥] أي علم ما في غيب أنفسكم قبل خلقه لكم من فعل حركة أو سكون بخير أمر به وأعان على فعله، وفعل ما نهى عنه، ولم يعصم من نزل به، وخلى من شاء مع الهوى لإظهار فعل ما نهى عنه، ولم يعصم عدلاً منه وحكماً، فكان معنى قوله: مَا فِي أَنْفُسِكُمْ [٢٣٥] أي ما لم تفعلوه، وفِي أَنْفُسِكُمْ [٢٣٥] أي ما ستفعلونه «٢» ، فَاحْذَرُوهُ [٢٣٥] أي اضرعوا إليه فيه حتى يكون هو الذي يتولى الأمر بالمعونة والتوفيق على الطاعة، ويعصم عن النهي بالنصر والتأييد.
ألا ترون إلى قول عبد الله بن مسعود رضي الله عنهما: اللهم إن كنا عندك في أم الكتاب أشقياء محرومين فامْحُ ذلك عنا وأثبتنا سعداء مرحومين، فإنك تمحو ما تشاء، وتثبت وعندك أم الكتاب.
_________
(١) مصنف ابن أبي شيبة ٧/ ١٦٢ (رقم ٣٤٩٨٨) ، ٧/ ٢١٧ (رقم ٣٥٤٧٢) وصفوة الصفوة ٣/ ١٠٤.
(٢) حلية الأولياء ١٠/ ٢٠٠، ٢١٠.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (33,'قوله: وَهُوَ أَلَدُّ الْخِصامِ [٢٠٤] أي شديد الخصومة بالباطل. وقد روت عائشة رضي الله عنها عن النبي صلى الله عليه وسلّم أنه قال: «أبغض الرجال إلى الله تعالى الألدُّ الخصم» «١» . قوله:
وَزُلْزِلُوا [٢١٤] أي أرادوا به وخوفوا به وحذروا مكر الله عزَّ وجلَّ. وسئل عن قوله: حَتَّى يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ مَتى نَصْرُ اللَّهِ أَلا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ [٢١٤] أكان قولهم استبطاء للنصر؟ قال سهل: لا، ولكن لما أيسوا من تدبيرهم قالوا: مَتى نَصْرُ اللَّهِ [٢١٤] فلما علم الله تعالى من تبريهم من حولهم وقوتهم وتدبيرهم لأنفسهم وإظهارهم الافتقار إليه، وأن لا حيلة لهم دونه أجابهم بقوله: أَلا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ [٢١٤] قال سهل: البلاء والعافية من الله عزَّ وجلَّ، والأمر والنهي منه، والعصمة والتوفيق منه، والثواب والعقاب منه، والأعمال منسوبة إلى بني آدم، فمن عمل خيراً وجب عليه الشكر ليستوجب به المزيد، ومن عمل شراً وجب عليه الاستغفار ليستوجب به الغفران. والبلوى من الله على وجهين «٢» : بلوى رحمة، وبلوى عقوبة، فبلوى الرحمة: يبعث صاحبه على إظهار فقره [وفاقته] «٣» إلى الله عزَّ وجلَّ وترك التدبير، وبلوى العقوبة: يبعث «٤» صاحبه على اختيار منه وتدبيره. فسئل سهل: الصبر على العافية أشد أم على البلاء؟ فقال: طلب السلامة في الأمن أشد من طلب السلامة في الخوف.
وقال في قوله: وَمَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ [التغابن: ١١] قال: يؤمن بالله أن بلواه من الله يهد قلبه لانتظار الفرج منه. قوله: وَتَعاوَنُوا عَلَى الْبِرِّ وَالتَّقْوى [المائدة: ٢] أي على أداء الفرائض، لأن البر الإيمان، وأداء الفرائض فرعه، والتقوى السنة، فلا يتم فرض إلاَّ بالسنة، ونهى عن التعاون على الإثم وهو الكفر والنفاق، والعدوان وهو البدعة والخصام، وهما لعبان فنهوا عن اللعب، كما أمروا بالبر وهو الفرض والسنة، وأخذ النفس بالصبر على ذلك كله خالصاً لله فيه.

<span data-type="title" id=toc-38>[سورة البقرة (٢) : آية ٢٤٦]</span>
أَلَمْ تَرَ إِلَى الْمَلَإِ مِنْ بَنِي إِسْرائِيلَ مِنْ بَعْدِ مُوسى إِذْ قالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنا مَلِكاً نُقاتِلْ فِي سَبِيلِ اللَّهِ قالَ هَلْ عَسَيْتُمْ إِنْ كُتِبَ عَلَيْكُمُ الْقِتالُ أَلاَّ تُقاتِلُوا قالُوا وَما لَنا أَلاَّ نُقاتِلَ فِي سَبِيلِ اللَّهِ وَقَدْ أُخْرِجْنا مِنْ دِيارِنا وَأَبْنائِنا فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتالُ تَوَلَّوْا إِلاَّ قَلِيلاً مِنْهُمْ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ (٢٤٦)
قوله: أَلَمْ تَرَ إِلَى الْمَلَإِ مِنْ بَنِي إِسْرائِيلَ [٢٤٦] من هؤلاء الملأ؟ قال سهل:
أراد بذلك الرؤساء، ألا ترون في قول رسول الله صلّى الله عليه وسلّم وقد سمع رجلاً بعد وقعة بدر وهو يقول: إنما قتلنا يوم بدر عجائز صلعاً، فقال رسول الله صلّى الله عليه وسلّم: «أولئك الملأ من قريش» «٥» يعني الأشراف والسادات.

<span data-type="title" id=toc-39>[سورة البقرة (٢) : آية ٢٥٥]</span>
اللَّهُ لا إِلهَ إِلاَّ هُوَ الْحَيُّ الْقَيُّومُ لاَ تَأْخُذُهُ سِنَةٌ وَلا نَوْمٌ لَهُ مَّا فِي السَّماواتِ وَما فِي الْأَرْضِ مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلاَّ بِإِذْنِهِ يَعْلَمُ ما بَيْنَ أَيْدِيهِمْ وَما خَلْفَهُمْ وَلا يُحِيطُونَ بِشَيْءٍ مِنْ عِلْمِهِ إِلاَّ بِما شاءَ وَسِعَ كُرْسِيُّهُ السَّماواتِ وَالْأَرْضَ وَلا يَؤُدُهُ حِفْظُهُما وَهُوَ الْعَلِيُّ الْعَظِيمُ (٢٥٥)
وسئل عن قوله: اللَّهُ لا إِلهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ [٢٥٥] فقال: هذه
_________
(١) صحيح البخاري: باب تفسير سورة البقرة، ٤٢٥١.
(٢) حلية الأولياء ١٠/ ١٩٦، ٢١١.
(٣) ما بين القوسين إضافة من الحلية ١٠/ ١٩٦. [.....]
(٤) في الحلية (يترك) مكان (يبعث) .
(٥) نوادر الأصول ١/ ٣٣٣.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (34,'أعظم آية في كتاب الله تعالى، وفيها اسم الله الأعظم، وهو مكتوب بالنور الأخضر في السماء سطراً واحداً من المشرق إلى المغرب، كنت رأيته كذلك في ليلة القدر مكتوباً، وأنا بعبادان لا إله إلا هو الحي القيوم، فمعنى: الْحَيُّ الْقَيُّومُ القائم على خلقه كل شيء بآجالهم وأعمالهم وأرزاقهم المجازي بالإحسان إحساناً وبالسيئات غفراناً وبالنفاق والكفر والبدعة عذاباً، فمن قال:
لا إله إلاَّ الله، فقد بايع الله، فحرام عليه إذا بايعه أن يعصيه في شيء من أمره ونهيه، في سره وعلانيته، أو يوالي عدوه، أو يعادي وليه. قوله: لاَ تَأْخُذُهُ سِنَةٌ وَلا نَوْمٌ [٢٥٥] فالسنة:
النعاس، وقال: السنة ما خالط القلب من النوم.

<span data-type="title" id=toc-40>[سورة البقرة (٢) : آية ٢٥٧]</span>
اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِجُهُمْ مِنَ الظُّلُماتِ إِلَى النُّورِ وَالَّذِينَ كَفَرُوا أَوْلِياؤُهُمُ الطَّاغُوتُ يُخْرِجُونَهُمْ مِنَ النُّورِ إِلَى الظُّلُماتِ أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ (٢٥٧)
قال سهل في قول الله تعالى: اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا [٢٥٧] أي ولاية الرضا فهو المتولي لهم بما سبق لهم من هدايته ومعرفته إياهم على توحيده وذلك لعلمه بتبرئهم من كل سبب إلا من خالفهم فأخرجوا من الظلمات إلى النور ومن الكفر والضلالة والمعاصي والبدع إلى الإيمان وهو النور الذي أثبته الحق عزَّ وجلَّ في قلوبهم وهو نور بصيرة اليقين الذي به يستبصرون التوحيد والطاعة له فيما أمر ونهى وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُوراً فَما لَهُ مِنْ نُورٍ [النور: ٤٠] .
قوله عزَّ وجلَّ: وَالَّذِينَ كَفَرُوا أَوْلِياؤُهُمُ الطَّاغُوتُ [٢٥٧] أي الشيطان. قال سهل: ورأس الطواغيت كلها النفس الأمارة بالسوء، لأن الشيطان لا يقدر على الإنسان إلاَّ من طريق هوى النفس، فإن أحس منها بما تهم به ألقى إليها الوسوسة.

<span data-type="title" id=toc-41>[سورة البقرة (٢) : آية ٢٦٠]</span>
وَإِذْ قالَ إِبْراهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِ الْمَوْتى قالَ أَوَلَمْ تُؤْمِنْ قالَ بَلى وَلكِنْ لِيَطْمَئِنَّ قَلْبِي قالَ فَخُذْ أَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلى كُلِّ جَبَلٍ مِنْهُنَّ جُزْءاً ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْياً وَاعْلَمْ أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ (٢٦٠)
وسئل عن قوله: وَإِذْ قالَ إِبْراهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِ الْمَوْتى [٢٦٠] أفكان شاكاً في إيمانه حتى سأل ربه أن يريه آية ومعجزة ليصح معها إيمانه؟ فقال سهل: لم يكن سؤاله ذلك عن شك، وإنما كان طالباً زيادة يقين إلى إيمان كان معه، فسأل كشف غطاء العيان بعيني رأسه ليزداد بنور اليقين يقيناً في قدرة الله، وتمكيناً في خلقه، ألا تراه كيف قال: أَوَلَمْ تُؤْمِنْ قالَ بَلى [٢٦٠] فلو كان شاكاً لم يجب ب «بلى» ، ولو علم الله منه الشك وهو أخبر ب «بلى» وستر شكه لكشف الله تعالى ذلك، إذ كان مثله مما لا يخفى عليه، فصح أن طلب طمأنينته كان على معنى طلب الزيادة في يقينه. فقيل: إن أصحاب المائدة طلبوا الطمأنينة بإنزال المائدة، وكان ذلك شكاً، فكيف الوجه فيه؟ فقال: إن إبراهيم عليه السلام أخبر أنه مؤمن، وإنما سأل الطمأنينة بعد الإيمان زيادة، وأصحاب المائدة أخبروا أنهم يؤمنون بعد أن تطمئن قلوبهم، كما قال: وَتَطْمَئِنَّ قُلُوبُنا وَنَعْلَمَ أَنْ قَدْ صَدَقْتَنا [المائدة: ١١٣] فأخبروه أن علمهم بصدقة بعد طمأنينتهم إلى معاينتهم المائدة يكون ابتداء إيمان لهم. وقال أبو بكر: وسمعته مرة أخرى يقول:
وَلكِنْ لِيَطْمَئِنَّ قَلْبِي [٢٦٠] أي لست آمنُ أن يعارضني عدو لك إذا قلت: رَبِّيَ الَّذِي',NULL,'37',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (35,'يُحْيِي وَيُمِيتُ
[٢٥٨] فيقول: أنت رأيته يحيي ويميت، فيطمئن قلبي إلى الإجابة بنعم إذا شاهدت ذلك، ولذلك قال النبي صلى الله عليه وسلّم: «ليس الخبر كالمعاينة» «١» ، وقال سهل: وفيها وجه آخر أنه سأله أن يريه إحياء الموتى طمأنينة له في أنه اتخذ خليلاً. قال سهل: وفيه وجه آخر معناه:
أن سؤالي إياك لا أستحق به عليك إلاَّ ما تحققه لي، وذلك موقف الخواص من خلقه، فسؤالي إياك أن تريني إحياء الموتى ليطمئن قلبي مني، وقد كان في الجاهلية يسمى الخليل. قلنا فقوله:
لِيَطْمَئِنَّ قَلْبِي [٢٦٠] أي خلتي، هذا لما أعلمه أنك تحيي وتميت.
وسئل سهل: إذا بلغ العبد إلى كفاح العيان ما علامته في البيان؟ فقال: يغلب بطرد الشيطان، وهو أن النفس في معاينة الهوان، ولا سبيل إليه للنفس والشيطان بعزلهما عن الشيطان إلاَّ بحفظ الرحمن. وقال: [من الوافر]
كفايات الكفاح بحسن ظني ... كنسج العنكبوت بباب غار
وحسنُ الظَّنِّ جاوزَ كُلَّ حجبٍ ... وحُسْنُ الظَّنِّ جاوز نُورَ نار
علاماتُ الْمُقَرَّبِ وواضحات ... بعيدٌ أمْ قريبٌ ليلُ سارِ
فمنْ كانَ الإِله لهُ عِياناً ... فلا نومَ القرارِ إلى النهارِ
تقاضاه الإله لَهم ثلاثاً ... فهل من سائلٍ من لطف بارِ
متى نَجس الولوغ ببحر ود ... فدع شقي النباح بباب داري
ألا يا نفس والشيطان أخسوا ... كبطلان الوساوس والغمارِ
قوله: «كفايات الكفاح بحسن ظني» كأنه أشار إلى قوله: أَوَلَمْ يَكْفِ بِرَبِّكَ [فصلت: ٥٣] فقال رسول الله صلّى الله عليه وسلّم: «بلى يا رب» وكذلك لما أنزلت أَلَيْسَ اللَّهُ بِأَحْكَمِ الْحاكِمِينَ [التين: ٨] قال رسول الله صلّى الله عليه وسلّم: «بلى يا رب» ومن طريق فهمهم القرآن: أوَ لم يَكْفِ بِرَبِّكَ يا محمد بنصرتك في الدنيا على أعدائك بالقتل والهزيمة، وفي العقبى بالمقام المحمود والشفاعة، وفي الجنة باللقاء والزيارة. وقوله: «كنسج العنكبوت بباب غار» وذلك أنَّ غار العارفين هو السر، واطلاع رب العالمين إذا بلغوا إلى مقام الكفاح، وهو عيان العيان بعد البيان، فليس بينهم وبين الله تعالى إلاَّ حجاب العبودية بنظره إلى صفات الربوبية والهوية والإلهية والصمدية إلى السرمدية بلا منع ولا حجاب، مثل من طريق الأمثال كنسيج العنكبوت حول قلبه، وسره فؤاده بلطف الربوبية وكمال الشفقة بلا حجاب بينه وبين الله تعالى كنسج العنكبوت بباب غار رسول الله صلّى الله عليه وسلّم صرف الله به جميع أعدائه من صناديد قريش بدلالة إبليس إياهم عليه، كذلك أهل المعرفة إذا بلغوا
_________
(١) نوادر الأصول ١/ ٢٩٣ ومجمع الزوائد ١/ ١٥٣ والمستدرك على الصحيحين رقم ٣٢٥٠.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (36,'إلى مقام العيان بعد البيان انقطع وصرف وساوس الشيطان وسلطان النفس، وصار كيدهم ضعيفاً، بيانه قوله: إِنَّ كَيْدَ الشَّيْطانِ كانَ ضَعِيفاً [النساء: ٧٦] يعني صار عليهم ضعيفاً كما قال: إِنَّ عِبادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطانٌ [الحجر: ٤٢] لأن العبد إذا جاوز بحسن ظنه جميع الحجب، حتى لا يكون بينه وبين الله حجاب، فليس للنفس والشيطان والدنيا دخول على قلبه وفؤاده بالوساوس، ولذلك قال النبي صلى الله عليه وسلّم: «رأيت البارحة عجباً عبد بينه وبين الله حجاب فجاء حسن ظنه بالله فأدخله الحجاب» «١» .
وقوله: «وحسن الظن جاوز نور نار» كأنه أشار إلى متابعة الرسول شرفاً بتفضيله على الخليل والكليم، لأن الأنبياء والأولياء في مقام رؤية النار والنور على مقامات شتى، فالخليل رأى النار وصارت عليه برداً وسلاماً، والكليم رأى النار نوراً بيانه قوله: إِنِّي آنَسْتُ نَاراً [طه: ١٠] وكان في الأصل نوراً مع قوله: أَنْ بُورِكَ مَنْ فِي النَّارِ [النمل: ٨] يعني موسى في وسط النور فاشتغل بالنور فعاتبه فقال: لا تشتغل بالنور فإني منور النور، بيانه: إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ [طه: ١٢] وأما الحبيب صلّى الله عليه وسلّم فأراه النار والنور، وجاوز حجاب النار والنور، ثم أدناه بلا نار ولا نور، حتى رأى في دنوه الأدنى منوّر الأنوار، بيانه قوله: مَا كَذَبَ الْفُؤادُ مَا رَأى [النجم: ١١] فرفع الحبيب عن مقام الخليل والكليم ومقامات جميع الأنبياء المقربين، حتى صار مكلماً بالله بلا وحي ولا ترجمان أحد، بيانه قوله: فَأَوْحى إِلى عَبْدِهِ مَآ أَوْحى
[النجم: ١٠] يعني قال الحبيب للحبيب سراً وعلمه وأكرمه بفاتحة الكتاب وخواتيم سورة البقرة.
وقوله: «علامات المقرب واضحات» أراد أن جميع الأنبياء والملائكة لهم قربة، ومحمد صلّى الله عليه وسلّم أقربهم قربة، على وزن أفعل، يقول قريب وأقرب، فالقريب يدخل في الفهم والوهم والتفسير، وأما الأقرب خارج عن الفهم والوهم والتفسير، وما بعده لا يدخل في العبارة ولا في الإشارة، وذلك أن موسى عليه السلام لما سمع ليلة النار نداء الوحدانية من الحق فقال: إلهي أقريب أنت فأناجيك أم بعيد فأناديك «٢» ؟ فنادى الكليم من مكان القريب والبعيد أنه قريب.
ولم يكن هذا في وصف الرسول حينئذ صيره مقرباً، حتى سلم الله عليه فقال: السلام عليكم، وإن الله تعالى مدح أمته فقال: وَالسَّابِقُونَ السَّابِقُونَ (١٠) أُولئِكَ الْمُقَرَّبُونَ [الواقعة: ١٠- ١١] ولم يقل القريبون، وعلامات المقرب واضحات من هذه الأمة، فالقريب وجد من الله المنة والكرامة، والبعيد وجد من الله العذاب والعقوبة، والمعبد وجد من الله الحجاب والقطيعة، والمقرب وجد من الله اللقاء والزيارة.
_________
(١) نوادر الأصول ٣/ ٢٣١ والعلل المتناهية ٢/ ٦٩٩ (رقم ١١٦٥) .
(٢) كشف الخفاء ١/ ٢٣٢ وحلية الأولياء ٦/ ٣٧، ٤٢ وشعب الإيمان ١/ ٤٥١.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (37,'قوله: «فمن كان الإله له عياناً» علامات المشتاقين، فليس لهم نوم ولا قرار لا بالليل ولا بالنهار، والمخصوص بهذه الصفة صهيب «١» وبلال «٢» ، لأن بلالاً كان من المشتاقين، وكذلك صهيب، لم يكن لهما نوم ولا قرار. وقد حكي أن امرأة كانت اشترت صهيباً فرأته كذلك فقالت:
لا أرضى حتى تنام بالليل لأنك تضعف فلا يتهيأ لك الاشتغال بأعمالي، فبكى صهيب وقال:
إن صهيباً إذا ذكر النار طار نومه، وإذا ذكر الجنة جاء شوقه، وإذا ذكر الله تعالى طال شوقه «٣» .
وقوله: «تقاضاه الإله لهم ثلاثاً» لأن «هل» من حروف الاستفهام، وأن الله عز وجل يرفع الحجاب كل ليلة فيقول: «هل من سائل فأعطيه سؤله؟ هل من مستغفر فأغفر له؟ هل من داعٍ فأجيب دعوته؟» . فإذا كانت ليلة القدر رفع الله الشرط فقال: «غفرت لكم وإن لم تستغفروني، وأعطيتكم وإن لم تسألوني، وأجبت لكم قبل أن تدعوني» «٤» ، وهذا غاية الكرم.
قوله: «متى نجس الولوغ ببحر ود» أشار إلى ولوغ الكلب، إذا ولغ في الإناء يغسل سبع مرات أو ثلاثاً «٥» ، باختلاف الألفاظ الواردة عن رسول الله صلّى الله عليه وسلّم، فكيف ولو أن ألف ألف كلب ولغوا في بحر؟ فلا اختلاف بين الأمة أن البحر لا ينجس بوساوس الشيطان، وولوغه في قلوب العارفين والمحبين في بحر الوداد متى يوجب التنجس، لأنه كلما ولغ فيه جاءه موج فطهره.
وقوله: «فدع شقي النباح بباب داري» يعني دع يشقى إبليس يصيح على باب الدنيا بألوان الوساوس، فإنه لا يضرني، كقوله: إِذا مَسَّهُمْ طائِفٌ مِنَ الشَّيْطانِ تَذَكَّرُوا [الأعراف: ٢٠١] بالوحدانية مع قوله: وَإِذا ذَكَرْتَ رَبَّكَ فِي الْقُرْآنِ وَحْدَهُ وَلَّوْا عَلى أَدْبارِهِمْ نُفُوراً [الإسراء: ٤٦] .
_________
(١) صهيب بن سنان بن مالك، من بني النمر بن قاسط (٣٢ ق هـ- ٣٨ هـ) : صحابي، من أرمى العرب سهما وهو أحد السابقين إلى الإسلام. شهد بدرا وأحدا والمشاهد كلها. (الإصابة ت ٤٠٩٩ والحلية ١/ ١٥١)
(٢) بلال بن رباح الحبشي ( ... - ٢٠ هـ) : مؤذن رسول الله صلّى الله عليه وسلّم، وخازنه على بيت ماله، وأحد السابقين إلى الإسلام، وشهد المشاهد كلها مع رسول الله. (الحلية ١/ ١٤٧) .
(٣) التخويف من النار ص ٢٨ وروي مثل هذا الخبر في عامر بن عبد قيس. انظر صفوة الصفوة ٣/ ٢٠٧ وكتاب الزهد لابن أبي عاصم ص ٢٨، حيث قال عامر: (إن ذكر جهنم لا يدعني أنام) .
(٤) صحيح مسلم: صلاة المسافرين وقصرها وصحيح البخاري: كتاب التهجد ١٠٩٤، وكتاب الدعوات ٥٩٦٢، وكتاب التوحيد ٧٠٥٦ ومسند أحمد ٢/ ٤٣٣، ٤/ ٨١، ٢١٧، ٢١٨.
(٥) يقصد الحديث: «إذا ولغ الكلب في إناء أحدكم فاغسلوه سبع مرات» ، انظر: صحيح ابن حبان ٤/ ١٠٩- ١١٤ (رقم ١٢٩٤- ١٢٩٨) وشرح النووي على صحيح مسلم ٣/ ١٨٥- ١٨٦.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (38,'قوله: «اخسؤوا» تباعدوا عني، يقال للكلب اخسأ على كمال البعد والطرد، وبهذا عاقبهم في آخر عقوباته إياهم، كقوله: اخْسَؤُا فِيها وَلا تُكَلِّمُونِ [المؤمنون: ١٠٨] . قوله تعالى: حافِظُوا عَلَى الصَّلَواتِ [٢٣٨] أي داوموا على إقامتها. وأما قوله: وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكاةَ [النور: ٥٦] فعلى وجهين أحدهما الإقرار بها من غير تصديق، كما قال في براءة:
فَإِنْ تابُوا [التوبة: ٥] أي من الشرك، وَأَقامُوا الصَّلاةَ [التوبة: ٥] يعني وأقروا بإقام الصلاة وإيتاء الزكاة فَخَلُّوا سَبِيلَهُمْ [التوبة: ٥] ، وكقوله: فَإِنْ تابُوا وَأَقامُوا الصَّلاةَ وَآتَوُا الزَّكاةَ فَإِخْوانُكُمْ فِي الدِّينِ [التوبة: ١١] ومواليكم، ونظيرها في السجدة «١» . والوجه الثاني: الإقامة، كما قال في المجادلة: فَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكاةَ [المجادلة: ١٣] ، ونظيرها في المزمل «٢» .
وقال في المائدة «٣» : الَّذِينَ يُقِيمُونَ الصَّلاةَ [المائدة: ٥٥] أي يتمونها. وسئل عن قوله:
وَالصَّلاةِ الْوُسْطى [٢٣٨] ما معنى ذكرها مفردة؟ قال: إنما أفردها لاختصاص من الصلوات وإن كانت داخلة في جملتها، كما انفرد جبريل وغيره بالذكر لاختصاصهم من جملة الملائكة.
قال: وفيها وجه آخر، وهو أن أوقات سائر الصلوات مشهورة عند العالم والجاهل، فعلامتها واضحة، ووقت العصر أخفى، فحثَّ على مراعاتها في وقتها بما خصها من الذكر. قوله:
وَقُومُوا لِلَّهِ قانِتِينَ [٢٣٨] أي قاموا لله في الصلاة مطيعين. فكم من مصلّ غير مطيع كالمنافق ونحوه. وسئل النبي صلّى الله عليه وسلّم أي الصلاة أفضل؟ فقال: «طول القنوت أي طول القيام» «٤» ، وقال زيد بن أرقم «٥» رضي الله عنه: القنوت السكوت، لأنا كنا نتكلم في الصلاة، فأنزل الله تعالى:
وَقُومُوا لِلَّهِ قانِتِينَ [٢٣٨] فأمسكنا عن الكلام «٦» . وكان محمد بن سوار يقول: القنوت الوتر، سمي قنوتاً لقيام الرجل فيه بالدعاء من غير قراءته القرآن، بل هو التعظيم بالدعاء.
_________
(١) ليس في سورة السجدة نظير ما ورد في سورة التوبة.
(٢) المزمل، الآية ٢٠، وأيضا في سورة الحج، الآية ٧٨.
(٣) في الأصل: (وقال في البقرة) والصواب ما أثبته وقد تكررت الآية في الأنفال: ٣ والنمل: ٣ ولقمان: ٤، وأما ما ورد في سورة البقرة: ٣ فهو قوله تعالى: الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلاةَ.
(٤) صحيح مسلم: كتاب صلاة المسافرين وقصرها برقم ٧٥٦ وابن ماجة: كتاب إقامة الصلاة ١/ ٤٥٦ والترمذي: كتاب الصلاة ١/ ٨٧ وأحمد ٣/ ٣٠٢، ٣٩١، ٤١٢ والنسائي: كتاب الزكاة ١/ ٣٤٩. [.....]
(٥) زيد بن أرقم الخزرجي الأنصاري ( ... - ٦٨ هـ) : صحابي. غزا مع النبي صلى الله عليه وسلّم سبع عشرة غزوة، وشهد صفين مع علي. ومات بالكوفة. (الأعلام ٣/ ٥٦) .
(٦) صحيح البخاري: كتاب العمل بالصلاة ١١٤٢ وصحيح مسلم: المساجد ٥٣٩، وانظر تأويل مشكل القرآن ٤٥٢ وعمدة الحفاظ ٣/ ٣٤٠ (قنت) .',NULL,'41',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (39,'<span data-type="title" id=toc-42>[سورة البقرة (٢) : الآيات ٢٦٨ الى ٢٦٩]</span>
الشَّيْطانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشاءِ وَاللَّهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ وَفَضْلاً وَاللَّهُ واسِعٌ عَلِيمٌ (٢٦٨) يُؤْتِي الْحِكْمَةَ مَنْ يَشاءُ وَمَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْراً كَثِيراً وَما يَذَّكَّرُ إِلاَّ أُولُوا الْأَلْبابِ (٢٦٩)
وسئل عن قوله: الشَّيْطانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشاءِ [٢٦٨] قال: هو أن يأخذوا الشيء من غير حله، ويضعوه في غير محله.
وسئل عن قوله: وَمَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْراً كَثِيراً [٢٦٩] قال: روى أبو سعيد الخدري «١» رضي الله عنه عن النبي صلى الله عليه وسلّم قال: «القرآن حكمة الله عزَّ وجلَّ بين عباده فمن تعلم القرآن وعمل به فكأنما أدرجت النبوة بين جنبيه إلاَّ أنه لا يوحى إليه يحاسب حساب الأنبياء عليهم السلام إلاَّ في تبليغ الرسالة» «٢» . وأخبرني محمد بن سوار عن عُقَيْل «٣» عن الزهري عن ابن المسيب عن أبي هريرة رضي الله عنه قال: قال رسول الله صلّى الله عليه وسلّم: «القرآن حكمة فمن تعلم القرآن في شبيبته خلط بلحمه ودمه «٤» . ألا وإن النار لا تمس قلباً وعى القرآن «٥» ، ولا جسداً اجتنب محارمه وأحل حلاله وآمن بمحكمه ووقف عند متشابهه ولم يبتدع فيه» .
وقال مجاهد وطاووس «٦» : الحكمة القرآن، كما قال في النحل: ادْعُ إِلى سَبِيلِ رَبِّكَ بِالْحِكْمَةِ [النحل: ١٢٥] يعني القرآن. وقال الحسن «٧» : الحكمة: الفهم في القرآن، والحكمة النبوة، كما قال في ص: وَآتَيْناهُ الْحِكْمَةَ [ص: ٢٠] يعني النبوة، وقال لداود عليه السلام:
وَآتاهُ اللَّهُ الْمُلْكَ وَالْحِكْمَةَ [٢٥١] يعني النبوة من الكتاب.
وقال قتادة «٨» : الحكمة: هي الفقه في دين الله عزَّ وجلَّ، واتباع رسول الله صلّى الله عليه وسلّم.
_________
(١) أبو سعيد الخدري: سعد بن مالك بن سنان الخدري الأنصاري (١٠ ق هـ- ٧٤ هـ) : صحابي. كان من ملازمي النبي صلى الله عليه وسلّم. غزا اثنتي عشرة غزوة. (الحلية ١/ ٣٦٩) .
(٢) علل ابن أبي حاتم ٢/ ٦٥ رقم ١٦٨٢.
(٣) عقيل بن خالد بن عقيل الأيلي الأموي بالولاء ( ... - ١٤١ هـ) : من حفاظ الحديث، ثقة. كان شرطيا بالمدينة. توفي بمصر. (تهذيب التهذيب ٧/ ٢٢٨) .
(٤) السنن الصغرى رقم ٩٨٩ وشعب الإيمان رقم ١٩٥٢، ٢٦٩٦ ونوادر الأصول ٢/ ٩٦.
(٥) في نوادر الأصول ٣/ ٢٥٣: (لا تغرنكم هذه الصحف المعلقة، إن الله تعالى لا يعذب قلبا وعى القرآن) وانظر فتح الباري ٩/ ٧٩.
(٦) طاووس بن كيسان الخولاني الهمداني بالولاء (٣٣- ١٠٦ هـ) : من أكابر التابعين تفقها في الدين ورواية للحديث، وجرأة على وعظ الخلفاء. توفي حاجا بالمزدلفة. (الحلية ٤/ ٣) .
(٧) الحسن بن يسار البصري (٢١- ١١٠ هـ) : أحد العلماء الفقهاء الفصحاء الشجعان النساك. كان إمام أهل البصرة، وحبر الأمة في زمنه. (الحلية ٢/ ١٣١) .
(٨) قتادة بن دعامة بن قتادة بن عزيز، أبو الخطاب السدوسي (٦١- ١١٨ هـ) : مفسر حافظ ضرير أكمه. كان أحفظ أهل البصرة. (الأعلام ٥/ ١٨٩) .',NULL,'42',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (40,'وقال السُّدي «١» : الحكمة النبوة. وقال: زيد بن أسلم «٢» : الحكمة العقل. وقال الربيع ابن أنس «٣» : الحكمة خشية الله تعالى. وقال ابن عمر: الحكمة ثلاث: آية محكمة، وسنة ماضية، ولسان ناطق بالقرآن. وقال أبو بكر: قال سهل: الحكمة إجماع العلوم، وأصلها السنة، قال الله تعالى: وَاذْكُرْنَ مَا يُتْلى فِي بُيُوتِكُنَّ مِنْ آياتِ اللَّهِ وَالْحِكْمَةِ [الأحزاب: ٣٤] فالآيات الفرض، والحكمة السنة. وأراد سهل من ذلك أن العرب تقول: حكمت الرجل إذا منعته من الضرر والخروج عن الحق مثل قوله: حِكْمَةٌ بالِغَةٌ [القمر: ٥] قال: أي تامة، كما قال:
آتَيْناهُ حُكْماً وَعِلْماً [الأنبياء: ٧٤] فهي حينئذ بلغت إلى أهلها دون غيرهم، فهم في كل حال فيها ينطقون، وإلى أحكامها يفزعون، وعن معانيها يكشفون، كما قيل: زاحم الحكماء، فإن الله يحيي القلوب الميتة بالحكم، كما يحيي الأرض الميتة بوابل المطر «٤» . ثم قال: رأس مال الحكمة ثلاث: الأول رياض النفس في المكروهات، والثاني فراغ القلب عن حب الشهوات، والثالث القيام على القلب بحفظ الخطرات، ومن راقب الله عند خطرات قلبه عصمه عند حركات جوارحه. وقال عمر بن واصل «٥» : يُؤْتِي الْحِكْمَةَ مَنْ يَشاءُ [٢٦٩] أي يؤتي الإصابة في كتابه من يشاء، كما قال الله تعالى لأزواج النبي صلى الله عليه وسلّم عند تعداد النعم عليهن:
وَاذْكُرْنَ مَا يُتْلى فِي بُيُوتِكُنَّ مِنْ آياتِ اللَّهِ وَالْحِكْمَةِ [الأحزاب: ٣٤] فالآيات القرآن، والحكمة ما جاء به الرسول صلّى الله عليه وسلّم من المستنبط منها، كما قال علي رضي الله عنه: الآيات رجل آتاه الله فهما في كتابه.

<span data-type="title" id=toc-43>[سورة البقرة (٢) : آية ٢٧٣]</span>
لِلْفُقَراءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللَّهِ لا يَسْتَطِيعُونَ ضَرْباً فِي الْأَرْضِ يَحْسَبُهُمُ الْجاهِلُ أَغْنِياءَ مِنَ التَّعَفُّفِ تَعْرِفُهُمْ بِسِيماهُمْ لا يَسْئَلُونَ النَّاسَ إِلْحافاً وَما تُنْفِقُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ (٢٧٣)
وسئل عن قوله: لِلْفُقَراءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللَّهِ [٢٧٣] وعن الفرق بينهم وبين المساكين. فقال: الله تعالى وصف الفقير بصفة العدم من حال سؤال الافتقار واللجأ إليه،
_________
(١) السدي: إسماعيل بن عبد الرحمن السدي ( ... - ١٢٨ هـ) : تابعي، حجازي الأصل. كان إماما عارفا بالوقائع وأيام الناس. (الأعلام ١/ ٣١٧) .
(٢) زيد بن أسلم العدوي العمري ( ... - ١٣٦ هـ) : فقيه مفسر، من أهل المدينة. كان ثقة كثير الحديث، وله كتاب في التفسير. (الأعلام ٣/ ٥٧) .
(٣) الربيع بن أنس البكري البصري ثم الخراساني ( ... - ١٤٠ هـ) : صدوق، ذكره ابن حبان في الثقات. كان يتشيع فيفرط. (تهذيب التهذيب ٣/ ٢٠٧) .
(٤) هذا القول من وصية لقمان الحكيم لابنه، موطأ مالك ٢/ ١٠٠٢ وكتاب الزهد لابن أبي عاصم ١/ ١٠٧ وشرح الزرقاني ٤/ ٥٥٢. [.....]
(٥) قال الخطيب البغدادي في تاريخ بغداد ١١/ ٢٢١: «عمر بن واصل: أظنه بصريّا، سكن بغداد، وروى بها عن سهل التستري، وحدّث عن عبيد الله بن لؤلؤ السلمي» .',NULL,'43',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (41,'ووصفهم بالرضا والقنوع، فقال تعالى: لا يَسْئَلُونَ النَّاسَ إِلْحافاً [٢٧٣] وهم أصحاب صفة رسول الله صلّى الله عليه وسلّم وهم نحو من أربعين رجلاً، ليست لهم في المدينة مساكن ولا عشائر، فهذه أحوال أقوام مدحهم الله تعالى لشدة الافتقار إليه، لا استطاعة لهم ولا قوة إلا به ومنه، هو حولهم وقوتهم، نزع عنهم قوة سكون قلوبهم إلى غيره، وهو وسوسة النفس إلى شيء دون الله تعالى، فهم بهذا الوصف أعلى حالاً، فمن ردّه الله تعالى إلى مساكنة نفسه فقال: لِمَساكِينَ يَعْمَلُونَ فِي الْبَحْرِ [الكهف: ٧٩] فردهم إلى حالتهم التي قد سكنوا إليها.
وأما الفقير الذي سلمه الفقر إلى الله تعالى إن حركته في موت نفسه فهو أحسن حالاً من الذي سكن إلى حال له لمتابعة نفسه. قال عمر بن واصل: وإذا كان الفقير إلى الله عزَّ وجلَّ الراضي لا يسكن إلا بالرضا والتسليم، فقد كمل له الاسمان جميعاً الفقر والمسكنة. قال أبو بكر سمعت سهلاً يقول الفقير الفقير العاجز، وهو الفقر بلبلبة القلب إلى الله عزَّ وجلَّ، والسكون إِليه بالطاعة والمسكنة ذل، وهي المعصية لله. قال: وحكى الحسن عن أنس «١» رضي الله عنه عن النبي صلى الله عليه وسلّم أنه قال لما أنزلت هذه الآية: «صانعوا الفقراء ليوم ملكهم. فقيل: يا رسول الله ومتى ملكهم؟ قال: يوم القيامة» «٢» .

<span data-type="title" id=toc-44>[سورة البقرة (٢) : آية ٢٨١]</span>
وَاتَّقُوا يَوْماً تُرْجَعُونَ فِيهِ إِلَى اللَّهِ ثُمَّ تُوَفَّى كُلُّ نَفْسٍ ما كَسَبَتْ وَهُمْ لا يُظْلَمُونَ (٢٨١)
وسئل عن قوله: وَاتَّقُوا يَوْماً تُرْجَعُونَ فِيهِ إِلَى اللَّهِ [٢٨١] فقال: هي آخر آية ختم الله تعالى بها القرآن، وتوفي رسول الله صلّى الله عليه وسلّم بعد نزولها بثمانين يوماً.
ثم قال: إذا دخلت مظالم ليلة أهل الدنيا لأهل الدنيا ذهب النوم والقرار عن أهل السجن، ما يدرون ما يصنع بهم بدعتي عليهم، فيقتلون أو يعذبون، أم يعفى عنهم فيطلقون، فهذه مظالم أهل الدنيا لأهل الدنيا، فكيف مظالم الحق لأهل العقبى؟.

<span data-type="title" id=toc-45>[سورة البقرة (٢) : آية ٢٨٦]</span>
لاَ يُكَلِّفُ اللَّهُ نَفْساً إِلاَّ وُسْعَها لَها ما كَسَبَتْ وَعَلَيْها مَا اكْتَسَبَتْ رَبَّنا لا تُؤاخِذْنا إِنْ نَسِينا أَوْ أَخْطَأْنا رَبَّنا وَلا تَحْمِلْ عَلَيْنا إِصْراً كَما حَمَلْتَهُ عَلَى الَّذِينَ مِنْ قَبْلِنا رَبَّنا وَلا تُحَمِّلْنا ما لا طاقَةَ لَنا بِهِ وَاعْفُ عَنَّا وَاغْفِرْ لَنا وَارْحَمْنا أَنْتَ مَوْلانا فَانْصُرْنا عَلَى الْقَوْمِ الْكافِرِينَ (٢٨٦)
قوله: لاَ يُكَلِّفُ اللَّهُ نَفْساً إِلَّا وُسْعَها [٢٨٦] أي طاقتها، لَها مَا كَسَبَتْ [٢٨٦] أي ثواب العمل الصالح، وَعَلَيْها مَا اكْتَسَبَتْ [٢٨٦] يعني أوزار الذنوب. ثم قال: من لم تهمه الذنوب السالفة لم يعصم في أيامه الغابرة، ومن لم يعصمه الله تعالى في بقية أيامه فهو من الهالكين في معاده. قيل له: متى يعرف الرجل ذنوبه؟ قال: إذا حفظ أنوار قلبه فلم يترك شيئا
_________
(١) أنس بن مالك بن النصر الأنصاري (١٠ ق هـ- ٩٣ هـ) : صاحب رسول الله صلّى الله عليه وسلّم وخادمه. روى عنه ٢٢٨٦ حديثا. توفي بالبصرة. (الأعلام ٢/ ٢٥) .
(٢) في المعجم الصغير ٢/ ١٣ والفردوس بمأثور الخطاب ٤/ ٣٩٢ رقم ٧١٣٧: «ويل للأغنياء من الفقراء يوم القيامة، يقولون: ربنا ظلمونا حقوقنا التي فرضت عليهم، فيقول: وعزتي وجلالي لأدنينكم ولأباعدنهم» وفي كشف الخفاء ١/ ٣٧، ٢/ ١١٤، والفردوس بمأثور الخطاب ١/ ٨٣ رقم ٢٦١: (قال الحسين بن علي:
اتخذوا عند الفقراء الأيادي، فإن لهم دولة إذا كان يوم القيامة ... ) .',NULL,'44',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (42,'يدخل عليه ولا يخرج منه، إلاَّ بوزن، حينئذ يعرف ذنوبه، فمن فتح على نفسه باب حسنة فتح الله عليه سبعين باباً من التوفيق، ومن فتح على نفسه باب سيئة فتح الله عليه سبعين باباً من الشر من حيث لا يعلمه العبد، وما من قلب يهم بما لا يعنيه إلاَّ عوقب في الحال بتضييع ما يعنيه، ولا يعرف ذلك إلاَّ العلماء بالله. وسئل عن قوله: إِنْ تَرَكَ خَيْراً [١٨٠] ما هذا الخير عندك؟
قال: المال الحلال، كما قال الله تعالى: مَآ أَنْفَقْتُمْ مِنْ خَيْرٍ [٢١٥] أي من مال حلال في وجوهه وابتغاء مرضاته، فقال: وَما تُنْفِقُوا مِنْ خَيْرٍ [٢٧٢] أي من مال حلال، يُوَفَّ إِلَيْكُمْ [٢٧٢] أي توفون الجزاء من الله تعالى على فعلكم وما قصدتم به. وسئل عن قوله:
وَالصَّابِرِينَ فِي الْبَأْساءِ وَالضَّرَّاءِ [١٧٧] أي في بداية الأمر بالسنة، وَالضَّرَّاءِ [١٧٧] أي في اجتناب المنهي ظاهراً وباطناً في أكل الحلال، والبأساء في الظاهر الفقر، والضراء الشدة، وَحِينَ الْبَأْسِ [١٧٧] أي عند القتال.
وسئل عن قوله: أَخَذَتْهُ الْعِزَّةُ بِالْإِثْمِ [٢٠٦] قال: يعني الحمية، كما قال في ص:
فِي عِزَّةٍ وَشِقاقٍ [ص: ٢] أي في حمية واختلاف. وقوله: يُحِبُّونَهُمْ كَحُبِّ اللَّهِ وَالَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِلَّهِ [١٦٥] أي يحبون الأنداد كحبهم الله عزَّ وجلَّ، فقد وصف الله تعالى شدة كفرهم وصدقهم في حال الكفر جهلاً، ووصف محبة المؤمنين وصدقهم في الإيمان بالله تعالى حقاً، ثم فضل المؤمنين بالمعرفة فقال: وَالَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِلَّهِ [١٦٥] بمعرفتهم وسائر أسباب العبد المؤمن إلى الإقبال عليه وإقامة الذكر له، وتلك منزلة العارفين المحبين، إذ المحبة عطف من الله تعالى بخالصة الحق. فقيل له: ما علامة المحبة؟ قال: معانقة الطاعة ومباينة الفاقة.
وقد حكي أن الله تعالى أوحى إلى موسى عليه السلام: أتدري لم ألقيت عليك محبتي؟ فقال:
لا يا رب. فقال: لأنك ابتغيت مسرتي. يا موسى: أنزلني منك على بال، ولا تنس ذكري على حال، وليكن همتك ذكري، فإن طريقك عليّ «١» ، والله سبحانه وتعالى أعلم.
_________
(١) في المعجم الوسيط ١/ ١٤١: (قال موسى: أنا أكون على حال من الحال، أجلّك أن أذكرك، الغائط والجنابة، فقال: اذكرني على كل حال) . وانظر مثل ذلك في الحلية ٦/ ٣٧.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (43,'<span data-type="title" id=toc-46>السورة التي يذكر فيها آل عمران</span>

<span data-type="title" id=toc-47>[سورة آل عمران (٣) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الم (١) اللَّهُ لا إِلهَ إِلاَّ هُوَ الْحَيُّ الْقَيُّومُ (٢)
الم (١) اللَّهُ لا إِلهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ [١- ٢] قال: هو اسم الله الأعظم مكتوب على السماء بالنور الأخضر من المشرق إلى المغرب «١» . قوله:

<span data-type="title" id=toc-48>[سورة آل عمران (٣) : آية ٤]</span>
مِنْ قَبْلُ هُدىً لِلنَّاسِ وَأَنْزَلَ الْفُرْقانَ إِنَّ الَّذِينَ كَفَرُوا بِآياتِ اللَّهِ لَهُمْ عَذابٌ شَدِيدٌ وَاللَّهُ عَزِيزٌ ذُو انْتِقامٍ (٤)
وَأَنْزَلَ الْفُرْقانَ [٤] يعني القرآن فيه المخرج من الشبهة والضلالة.

<span data-type="title" id=toc-49>[سورة آل عمران (٣) : الآيات ٧ الى ٨]</span>
هُوَ الَّذِي أَنْزَلَ عَلَيْكَ الْكِتابَ مِنْهُ آياتٌ مُحْكَماتٌ هُنَّ أُمُّ الْكِتابِ وَأُخَرُ مُتَشابِهاتٌ فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا تَشابَهَ مِنْهُ ابْتِغاءَ الْفِتْنَةِ وَابْتِغاءَ تَأْوِيلِهِ وَما يَعْلَمُ تَأْوِيلَهُ إِلاَّ اللَّهُ وَالرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ آمَنَّا بِهِ كُلٌّ مِنْ عِنْدِ رَبِّنا وَما يَذَّكَّرُ إِلاَّ أُولُوا الْأَلْبابِ (٧) رَبَّنا لاَ تُزِغْ قُلُوبَنا بَعْدَ إِذْ هَدَيْتَنا وَهَبْ لَنا مِنْ لَدُنْكَ رَحْمَةً إِنَّكَ أَنْتَ الْوَهَّابُ (٨)
قوله: فَيَتَّبِعُونَ مَا تَشابَهَ مِنْهُ ابْتِغاءَ الْفِتْنَةِ [٧] يعني الكفر. وَابْتِغاءَ تَأْوِيلِهِ [٧] يعني تفسيره على ما يوافق هوى نفوسهم. وَما يَعْلَمُ تَأْوِيلَهُ إِلَّا اللَّهُ [٧] قال ابن عباس رضي الله عنهما: أنزل القرآن على أربعة أحرف، حلال وحرام لا يعذر أحد بجهالته، وتفسير تفسره العرب، وتفسير تفسيره العلماء، ومتشابه لا يعلمه إلاَّ الله تعالى، فمن ادعى علمه سوى الله عزَّ وجلَّ فهو كاذب.
قوله: وَالرَّاسِخُونَ فِي الْعِلْمِ [٧] قال: حكي عن علي رضي الله عنه: هم الذين حجبهم العلم عن الاقتحام بالهوى والحجج المضروبة، دون الغيوب لما هداهم الله وأشرفهم على أسراره المغيبة في خزائن العلوم فقالوا: آمَنَّا بِهِ كُلٌّ مِنْ عِنْدِ رَبِّنا [٧] فشكر الله تعالى لهم وجعلهم أهل الرسوخ والمبالغة في العلم زيادة منه لهم، كما قال الله تعالى: وَقُلْ رَبِّ زِدْنِي عِلْماً [طه: ١١٤] قال سهل: استثنى الله تبارك وتعالى الراسخين في العلم بقولهم: كُلٌّ مِنْ عِنْدِ رَبِّنا [٧] يعني الناسخ والمنسوخ والمحكم والمتشابه، وهم الكاشفون عن العلوم الثلاثة إذ العلماء ثلاثة: الربانيون والنورانيون والذاتيون، وبعد العلوم الأربعة: الوحي والتجلي والعندي واللدني، كما قال تعالى: آتَيْناهُ رَحْمَةً مِنْ عِنْدِنا وَعَلَّمْناهُ مِنْ لَدُنَّا عِلْماً [الكهف: ٦٥] ، وَما يَذَّكَّرُ إِلَّا أُولُوا الْأَلْبابِ [البقرة: ٢٦٩] أي وما يتذكر إلاَّ أولو الفهم والعقول الذين يقولون: رَبَّنا لاَ تُزِغْ قُلُوبَنا بَعْدَ إِذْ هَدَيْتَنا [٨] أي لا تُمل قلوبنا عن الإيمان بعد إذ هديتنا بهداية منك، وَهَبْ لَنا مِنْ لَدُنْكَ رَحْمَةً إِنَّكَ أَنْتَ الْوَهَّابُ [٨] لمن رجع إليك بالافتقار والتضرع والمسكنة. ثم قال سهل: ليس للعبد حيلة سوى أن يواظب في جميع عمره على قول: «رب سلم سلم، الأمان الأمان، الغوث الغوث» .
_________
(١) تقدم هذا القول في تفسير الآية ٢٥٥ من سورة البقرة.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (44,'قال الله تعالى: كَما بَدَأَكُمْ تَعُودُونَ [الأعراف: ٢٩] يعني ينبغي للموحد أن يعلم يقيناً أنه ليس كل من أحب الحق أحبه، لأن إبليس قابله بعلاء الحب فقال: أَأَسْجُدُ لِمَنْ خَلَقْتَ طِيناً [الإسراء: ٦١] وأنت الله لا يجوز أن يعبد غيرك، حتى لعنه. فليس كل من تقرب إليه قبله وليس كل من أطاعه قبل طاعته، إنه بصير بما في الضمير، فلا يأمن أحد أن يفعل به كما فعل بإبليس لعنه بأنوار عصمته، وهو عنده في حقائق لعنته، ستر عليه ما سبق منه إليه حتى عاقبه بإظهاره عليه، فليس للعبد إلاَّ استدامة الغوث بين يديه. وقد كان الرسول صلّى الله عليه وسلّم يقول: «يا ثابت المثبتين ثبتني بثباتك، يا ثابت الوحدانية لا إله إلاَّ أنت، سبحانك إني كنت من الظالمين» . وكان يقول: «يا ولي الإسلام وأهله ثبتني بالإسلام حتى ألقاك به» «١» ، قال: وموضع الإيمان بالله تعالى القلب، وموضع الإسلام الصدر، وفيه تقع الزيادة والنقصان.

<span data-type="title" id=toc-50>[سورة آل عمران (٣) : آية ١٥]</span>
قُلْ أَأُنَبِّئُكُمْ بِخَيْرٍ مِنْ ذلِكُمْ لِلَّذِينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَأَزْواجٌ مُطَهَّرَةٌ وَرِضْوانٌ مِنَ اللَّهِ وَاللَّهُ بَصِيرٌ بِالْعِبادِ (١٥)
وقوله: وَأَزْواجٌ مُطَهَّرَةٌ [١٥] يعني من الأحداث التي كانت تنالهن في الدنيا من الحيض وغيره، ألا ترى إلى قوله: وَسَقاهُمْ رَبُّهُمْ شَراباً طَهُوراً [الإنسان: ٢١] أي طهرهم به من بقاء أدناس الدنيا.

<span data-type="title" id=toc-51>[سورة آل عمران (٣) : آية ١٨]</span>
شَهِدَ اللَّهُ أَنَّهُ لا إِلهَ إِلاَّ هُوَ وَالْمَلائِكَةُ وَأُولُوا الْعِلْمِ قائِماً بِالْقِسْطِ لا إِلهَ إِلاَّ هُوَ الْعَزِيزُ الْحَكِيمُ (١٨)
قوله: شَهِدَ اللَّهُ [١٨] قال: أي علم الله وبيّن أَنَّهُ لا إِلهَ إِلَّا هُوَ [١٨] شهد لنفسه بنفسه، وهو خاص لذاته واستشهد من استشهد من خلقه قبل خلقهم بعلمه، فنبه به أهل معرفته أنه عالم بما يكون قبل كونه، وأن حقيقة التوحيد ما كان بدون الأكوان، كما شهد به الحق لنفسه بنفسه قبل الأكوان. وقال عبد الواحد: كنت مع أيوب السختياني فرأى حمالاً يحمل الحطب، فقلت: هل لك برب؟ فقال: أمثلي يُسأل عن ربه. فقلت له: إن كان لك خالق كما تزعم، فلم اشتغلت بالحطب؟ فأشار الرجل إلى السماء، فصار الحطب ذهباً، فتعجبنا منه لذلك، ثم قال: اللهم لا حاجة لي إلى هذا، فتحول الذهب حطباً كما كان، فقلنا له: ما حملك على هذا؟ فقال: لأني عبد، فأحمل هذا كي لا أنسى نفسي «٢» .

<span data-type="title" id=toc-52>[سورة آل عمران (٣) : آية ٢٦]</span>
قُلِ اللَّهُمَّ مالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشاءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشاءُ وَتُعِزُّ مَنْ تَشاءُ وَتُذِلُّ مَنْ تَشاءُ بِيَدِكَ الْخَيْرُ إِنَّكَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (٢٦)
قوله: قُلِ اللَّهُمَّ مالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشاءُ [٢٦] يعني المعرفة والتوحيد وشرائع دينك الإسلام والعاقبة المحمودة، وهو أن يتولى الله العبد ولا يكله إلى نفسه. قوله:
وَاعْتَصِمُوا بِحَبْلِ اللَّهِ جَمِيعاً وَلا تَفَرَّقُوا [١٠٣] أي تمسكوا بعهده وهو التوحيد، كما قال تعالى: أَمِ اتَّخَذَ عِنْدَ الرَّحْمنِ عَهْداً [مريم: ٧٨] أي توحيداً وتمسكوا بما ملككم من تأدية فرضه وسنة نبيه، وكذلك قوله: إِلَّا بِحَبْلٍ مِنَ اللَّهِ [١١٢] معناه إلاَّ بعهد من الله ودينه، وإنما سماه
_________
(١) مجمع الزوائد ١٠/ ١٣٦ والمعجم الأوسط ١/ ٢٠٦.
(٢) نسب مثل هذا الخبر إلى حيوة بن شريح في كرامات الأولياء ص ١٩٣ وتهذيب الحفاظ ص ٨٧ وأيضا نسب إلى التستري في الحلية ١٠/ ٢١٠.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (45,'حبلاً لأنه من تمسك به توصل إلى الأمر الذي يؤمنه.

<span data-type="title" id=toc-53>[سورة آل عمران (٣) : آية ٢٨]</span>
لا يَتَّخِذِ الْمُؤْمِنُونَ الْكافِرِينَ أَوْلِياءَ مِنْ دُونِ الْمُؤْمِنِينَ وَمَنْ يَفْعَلْ ذلِكَ فَلَيْسَ مِنَ اللَّهِ فِي شَيْءٍ إِلاَّ أَنْ تَتَّقُوا مِنْهُمْ تُقاةً وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ وَإِلَى اللَّهِ الْمَصِيرُ (٢٨)
قوله: وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ [٢٨] قال: أي عدله، لأن النار عدله لمن خالفه، والجنة فضله لمن أطاعه، ألا ترون إلى قوله عليه الصلاة والسلام: «يا من لا يرجى إلاَّ فضله ولا يخشى إلّا عدله» .

<span data-type="title" id=toc-54>[سورة آل عمران (٣) : آية ٣٥]</span>
إِذْ قالَتِ امْرَأَتُ عِمْرانَ رَبِّ إِنِّي نَذَرْتُ لَكَ مَا فِي بَطْنِي مُحَرَّراً فَتَقَبَّلْ مِنِّي إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ (٣٥)
قوله: قالَتِ امْرَأَتُ عِمْرانَ رَبِّ إِنِّي نَذَرْتُ لَكَ مَا فِي بَطْنِي مُحَرَّراً [٣٥] أي حررته وأعتقته من رق الدنيا من متابعة هواه ومرادات نفسه، وجعلته خادماً لعباد بيت المقدس خالصا لله تعالى،

<span data-type="title" id=toc-55>[سورة آل عمران (٣) : آية ٣٧]</span>
فَتَقَبَّلَها رَبُّها بِقَبُولٍ حَسَنٍ وَأَنْبَتَها نَباتاً حَسَناً وَكَفَّلَها زَكَرِيَّا كُلَّما دَخَلَ عَلَيْها زَكَرِيَّا الْمِحْرابَ وَجَدَ عِنْدَها رِزْقاً قالَ يا مَرْيَمُ أَنَّى لَكِ هذا قالَتْ هُوَ مِنْ عِنْدِ اللَّهِ إِنَّ اللَّهَ يَرْزُقُ مَنْ يَشاءُ بِغَيْرِ حِسابٍ (٣٧)
فَتَقَبَّلَها رَبُّها بِقَبُولٍ حَسَنٍ [٣٧] أي وقال: الملك الأعلى أولى بالمحرر عن رقّ النفس ورقّ الدنيا. وَأَنْبَتَها نَباتاً حَسَناً [٣٧] قال: بالعمل الصالح في ذكر الله تعالى وجوارحها في خدمة الله وقلبها في معرفة الله عزّ وجلّ،

<span data-type="title" id=toc-56>[سورة آل عمران (٣) : آية ٤٣]</span>
يا مَرْيَمُ اقْنُتِي لِرَبِّكِ وَاسْجُدِي وَارْكَعِي مَعَ الرَّاكِعِينَ (٤٣)
يا مَرْيَمُ اقْنُتِي لِرَبِّكِ [٤٣] أي لله فصلي، وإياه بالإخلاص فاعبدي، وإليه بالدعاء فاقنتي وتضرعي

<span data-type="title" id=toc-57>[سورة آل عمران (٣) : آية ٤٧]</span>
قالَتْ رَبِّ أَنَّى يَكُونُ لِي وَلَدٌ وَلَمْ يَمْسَسْنِي بَشَرٌ قالَ كَذلِكِ اللَّهُ يَخْلُقُ مَا يَشاءُ إِذا قَضى أَمْراً فَإِنَّما يَقُولُ لَهُ كُنْ فَيَكُونُ (٤٧)
. قوله: كَذلِكِ اللَّهُ يَخْلُقُ مَا يَشاءُ إِذا قَضى أَمْراً فَإِنَّما يَقُولُ لَهُ كُنْ فَيَكُونُ [٤٧] قال: إذا كان في علمه السابق الأزلي أمر فأراد إظهاره قال له كن فيكون، قال القائل شعر: [من الطويل] .
قضى قبلَ خلقِ الخلق ما هو خالقٌ ... خلائقَ لا يَخفى عليه أمورُها
هواها ونجواها ومضمر قلبها ... وقبل الهوى ماذا يكون ضميرها

<span data-type="title" id=toc-58>[سورة آل عمران (٣) : آية ٦١]</span>
فَمَنْ حَاجَّكَ فِيهِ مِنْ بَعْدِ ما جاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعالَوْا نَدْعُ أَبْناءَنا وَأَبْناءَكُمْ وَنِساءَنا وَنِساءَكُمْ وَأَنْفُسَنا وَأَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللَّهِ عَلَى الْكاذِبِينَ (٦١)
قوله: ثُمَّ نَبْتَهِلْ [٦١] أي يدعو بعضنا على بعض باللعنة والمبتهل الداعي، والابتهال الدعاء، والمسبح الذاكر، وهو الذي لا تكتبه إلاَّ الحفظة لأنه مشاهدة المذكور في الذكر بالمذكور وهو معنى قوله: «أنا جليس من ذكرني وحيثما التمسني عبدي وجدني» «١» .

<span data-type="title" id=toc-59>[سورة آل عمران (٣) : آية ٦٤]</span>
قُلْ يا أَهْلَ الْكِتابِ تَعالَوْا إِلى كَلِمَةٍ سَواءٍ بَيْنَنا وَبَيْنَكُمْ أَلاَّ نَعْبُدَ إِلاَّ اللَّهَ وَلا نُشْرِكَ بِهِ شَيْئاً وَلا يَتَّخِذَ بَعْضُنا بَعْضاً أَرْباباً مِنْ دُونِ اللَّهِ فَإِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِأَنَّا مُسْلِمُونَ (٦٤)
قوله: قُلْ يا أَهْلَ الْكِتابِ تَعالَوْا إِلى كَلِمَةٍ سَواءٍ بَيْنَنا وَبَيْنَكُمْ أَلَّا نَعْبُدَ إِلَّا اللَّهَ [٦٤] يعني إلى طمع عدل بيننا وبينكم، لأنهم كانوا مقرين بأن خالقهم وخالق السماوات والأرض هو الله تعالى، فنوحده ولا نعبد إلاَّ إياه. وأصل العبادة: التوحيد مع أكل الحلال وكف الأذى، ولا يحصل الأكل الحلال إلاَّ بكف الأذى، ولا كف الأذى إلاَّ بأكل الحلال، وأن تعلموا أكل الحلال وترك أذى الخلق والنية في الأعمال كما تعلموا فاتحة الكتاب، ليصفوا إيمانكم وقلوبكم وجوارحكم، فإنما هي الأصول. قال: حكى محمد بن سوار عن الثوري أنه قال: منزلة لا إله إلاَّ الله في العبد بمنزلة الماء في الدنيا، قال الله تعالى: وَجَعَلْنا مِنَ الْماءِ كُلَّ شَيْءٍ حَيٍّ [الأنبياء: ٣٠] فمن لم ينفعه اعتقاد لا إله إلاَّ الله والاقتداء بسنة رسول الله صلّى الله عليه وسلّم فهو ميت. قال سهل: وإني لأعرف رجلاً من أولياء الله تعالى اجتاز برجل مصلوب وجهه إلى غير القبلة، فقال: أين ذلك اللسان الذي كنت تقول به صادقاً: «لا إله إلاَّ الله» ، ثم قال: اللهم هب لي ذنبه. قال سهل:
_________
(١) تقدم الحديث في نهاية خطبة هذا الكتاب.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (46,'فاستدار نحو القبلة بقدرة الله.

<span data-type="title" id=toc-60>[سورة آل عمران (٣) : الآيات ٧٢ الى ٧٣]</span>
وَقالَتْ طائِفَةٌ مِنْ أَهْلِ الْكِتابِ آمِنُوا بِالَّذِي أُنْزِلَ عَلَى الَّذِينَ آمَنُوا وَجْهَ النَّهارِ وَاكْفُرُوا آخِرَهُ لَعَلَّهُمْ يَرْجِعُونَ (٧٢) وَلا تُؤْمِنُوا إِلاَّ لِمَنْ تَبِعَ دِينَكُمْ قُلْ إِنَّ الْهُدى هُدَى اللَّهِ أَنْ يُؤْتى أَحَدٌ مِثْلَ ما أُوتِيتُمْ أَوْ يُحاجُّوكُمْ عِنْدَ رَبِّكُمْ قُلْ إِنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَاللَّهُ واسِعٌ عَلِيمٌ (٧٣)
قوله: وَجْهَ النَّهارِ [٧٢] أي أول النهار. قوله: وَاللَّهُ واسِعٌ عَلِيمٌ [٧٣] أي كثير العطاء يقدر بقدرته الأزلية أن يعطى جميع ما يُسأل، وهو المحيط بكل شيء، كما قال: وَسِعَ كُلَّ شَيْءٍ عِلْماً [طه: ٩٨] .

<span data-type="title" id=toc-61>[سورة آل عمران (٣) : آية ٧٩]</span>
ما كانَ لِبَشَرٍ أَنْ يُؤْتِيَهُ اللَّهُ الْكِتابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِباداً لِي مِنْ دُونِ اللَّهِ وَلكِنْ كُونُوا رَبَّانِيِّينَ بِما كُنْتُمْ تُعَلِّمُونَ الْكِتابَ وَبِما كُنْتُمْ تَدْرُسُونَ (٧٩)
وسئل عن قوله: وَلكِنْ كُونُوا رَبَّانِيِّينَ بِما كُنْتُمْ تُعَلِّمُونَ الْكِتابَ وَبِما كُنْتُمْ تَدْرُسُونَ [٧٩] قال محمد بن سوار: الربّاني الذي لا يختار على ربه أحداً سواه، وهو اسم مشتق من الربوبية. وقال سهل: الربانيون هم العالمون في الدرجة من العلم بالعلم. كما قال محمد بن الحنفية «١» ، لما مات عبد الله بن عباس رضي الله عنهما: لقد مات هذا اليوم رباني هذه الأمة «٢» .
وإنما نسب إلى الرب لأنه عالم من علمه. كما قال: مَنْ أَنْبَأَكَ هذا قالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ [التحريم: ٣] ، فنسبه إلى النبوة بما علمه الله عزَّ وجلَّ. وكل من أنبأك بخبر موافق للكتاب والسنة فهو منبئ. والعلماء ثلاث: رباني ونوراني وذاتي بلا واسطة بينه وبين الله تعالى فيه بقية من الله عزَّ وجلَّ. وقال عمر بن واصل: الربانيون هم المجموعون من العلماء، كما قال علي رضي الله عنه: الناس ثلاثة: عالم رباني، ومتعلم على سبيل نجاة، وهمج رعاع أتباع كل ناعق.

<span data-type="title" id=toc-62>[سورة آل عمران (٣) : آية ٨٥]</span>
وَمَنْ يَبْتَغِ غَيْرَ الْإِسْلامِ دِيناً فَلَنْ يُقْبَلَ مِنْهُ وَهُوَ فِي الْآخِرَةِ مِنَ الْخاسِرِينَ (٨٥)
قوله:
وَمَنْ يَبْتَغِ غَيْرَ الْإِسْلامِ دِيناً [٨٥] قال: الإسلام هو التفويض كقوله: وَلا تَمُوتُنَّ إِلَّا وَأَنْتُمْ مُسْلِمُونَ [١٠٢] أي مفوضون وكذلك قوله: إِنَّ الدِّينَ عِنْدَ اللَّهِ الْإِسْلامُ [١٩] .

<span data-type="title" id=toc-63>[سورة آل عمران (٣) : آية ٩٢]</span>
لَنْ تَنالُوا الْبِرَّ حَتَّى تُنْفِقُوا مِمَّا تُحِبُّونَ وَما تُنْفِقُوا مِنْ شَيْءٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ (٩٢)
وسئل عن قوله: لَنْ تَنالُوا الْبِرَّ حَتَّى تُنْفِقُوا مِمَّا تُحِبُّونَ [٩٢] أي لن تبلغوا التقوى كلها حتى تحاربوا أنفسكم، فتنفقوا بعض ما تحبون، ولا إنفاق كإنفاق النفس في مخالفتها وطلب مرضاة الله عزَّ وجلَّ. وحكي عن عيسى عليه السلام مر بثلاثة نفر نحلت أبدانهم وتغيرت ألوانهم، فقال: ما الذي بلغ بكم ما أرى؟ فقالوا: الخوف من خالقنا، والحذر من عقوبة عصياننا فقال: حق على الله أن يؤمن الخائف. قال: فجاوزهم إلى ثلاثة هم أشد نحولاً، فقال: ما الذي بلغ بكم ما أرى؟ فقالوا: الشوق إلى ربنا. فقال: حق على الله أن يعطيكم ما رجوتم. فجاوزهم إلى ثلاثة نفر هم أشد نحولاً، كأن وجوههم البدور، قال: ما الذي بلغ بكم ما أرى؟ فقالوا: الحب قال: أنتم المقربون ثلاثاً، فمن أحب الله تعالى فهو المقرب، لأن من أحب شيئاً تسارع إليه، فالمرتبة الأولى مرتبة التوابين، والمرتبة الثانية مرتبة المشتاقين، ثم يبلغ العبد المرتبة الثالثة، وهي المحبة، ألا ترون أنهم كيف اتفقوا كلهم فيمن الكل له، وأعرضوا عن الكل إلى من له الكل؟.
_________
(١) ابن الحنفية: محمد بن علي بن أبي طالب (٢١- ٨١ هـ) : أحد الأبطال الأشداء في الإسلام، وهو أخو الحسن والحسين. كان واسع العلم، ورعا، أسود اللون. (الحلية ٣/ ١٧٤) .
(٢) مصنف ابن أبي شيبة ٦/ ٣٨٣، وفضائل الصحابة لابن حنبل ٢/ ٩٥٥.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (47,'<span data-type="title" id=toc-64>[سورة آل عمران (٣) : آية ٩٦]</span>
إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبارَكاً وَهُدىً لِلْعالَمِينَ (٩٦)
وقوله: إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبارَكاً [٩٦] أي أول بيت وضع للناس بيت الله عزَّ وجلَّ بمكة هذا هو الظاهر، وباطنها الرسول يؤمن به من أثبت الله في قلبه التوحيد من الناس.

<span data-type="title" id=toc-65>[سورة آل عمران (٣) : آية ١٠٦]</span>
يَوْمَ تَبْيَضُّ وُجُوهٌ وَتَسْوَدُّ وُجُوهٌ فَأَمَّا الَّذِينَ اسْوَدَّتْ وُجُوهُهُمْ أَكَفَرْتُمْ بَعْدَ إِيمانِكُمْ فَذُوقُوا الْعَذابَ بِما كُنْتُمْ تَكْفُرُونَ (١٠٦)
قوله: يَوْمَ تَبْيَضُّ وُجُوهٌ [١٠٦] يعني تبيض وجوه المؤمنين بنور إيمانهم، وَتَسْوَدُّ وُجُوهٌ [١٠٦] الكافرين بظلم كفرهم. وسئل عن قوله: وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ [البقرة: ٦١] فقال: هذه الأجسام الغرض منها ما أودع الله فيها من الودائع، ابتلى الله الخليقة بها، فمنها ما هو اعتبار للطائعين وهو الكفر، ومنها ما هو حجة على الغافلين، وهو المعرفة والتصديق في الأقوال والأفعال، كما قال: وَجَعَلَ الظُّلُماتِ وَالنُّورَ [الأنعام: ١] فباطن هذه الآية: النور العلم، والظلمات الجهل، لقوله: وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُوراً فَما لَهُ مِنْ نُورٍ [النور: ٤٠] أي ما يستبصر به القلب الإيمان بالله، فنور الإيمان من أعظم منن الله عزَّ وجلَّ وكراماته. والثاني الطيب من القول، وهو قوله تعالى: تَعالَوْا إِلى كَلِمَةٍ سَواءٍ بَيْنَنا وَبَيْنَكُمْ [٦٤] الآية.
والثالث إطاعة بالجوارح خالصاً لله، من إقام الصلاة وإيتاء الزكاة والقنوع والرضا، فدعاهم بذلك إلى أطيب القول وأحسن الفعال، ولو لم يكن الإيمان بالله والقرآن الذي هو علم الله فيه الدعوة إلى الإقرار بالربوبية والتعبد إياه في الفزع، لم تعرف الأنبياء عليهم الصلاة والسلام من أجابهم من الخلق.

<span data-type="title" id=toc-66>[سورة آل عمران (٣) : آية ١٤١]</span>
وَلِيُمَحِّصَ اللَّهُ الَّذِينَ آمَنُوا وَيَمْحَقَ الْكافِرِينَ (١٤١)
قوله: وَلِيُمَحِّصَ اللَّهُ الَّذِينَ آمَنُوا [١٤١] يعني تخليصهم من عيوب الذنوب، كما أخلصوا له بالعمل، وهو الجهاد في سبيل الله، وَيَمْحَقَ الْكافِرِينَ [١٤١] أي وليهلك الكافرين بالذنوب عن الابتلاء.

<span data-type="title" id=toc-67>[سورة آل عمران (٣) : آية ١٥٢]</span>
وَلَقَدْ صَدَقَكُمُ اللَّهُ وَعْدَهُ إِذْ تَحُسُّونَهُمْ بِإِذْنِهِ حَتَّى إِذا فَشِلْتُمْ وَتَنازَعْتُمْ فِي الْأَمْرِ وَعَصَيْتُمْ مِنْ بَعْدِ ما أَراكُمْ ما تُحِبُّونَ مِنْكُمْ مَنْ يُرِيدُ الدُّنْيا وَمِنْكُمْ مَنْ يُرِيدُ الْآخِرَةَ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ وَلَقَدْ عَفا عَنْكُمْ وَاللَّهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ (١٥٢)
قوله: وَلَقَدْ عَفا عَنْكُمْ [١٤٩] يعني الفئة المنهزمة يوم أحد حين لم يستأصلهم جميعاً. وَاللَّهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ [١٥٢] بالعفو عنهم وقبول التوبة منهم.

<span data-type="title" id=toc-68>[سورة آل عمران (٣) : آية ١٥٥]</span>
إِنَّ الَّذِينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطانُ بِبَعْضِ مَا كَسَبُوا وَلَقَدْ عَفَا اللَّهُ عَنْهُمْ إِنَّ اللَّهَ غَفُورٌ حَلِيمٌ (١٥٥)
قوله: إِنَّ الَّذِينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطانُ بِبَعْضِ مَا كَسَبُوا [١٥٥] فسئل ما هذا الكسب؟ فقال: هو الإعجاب الذي كان منهم بكثرة عددهم يوم حنين، وأخذهم العزة يوم بدر، وكان لشرك الشيطان إياهم بعد مساكنة قلوبهم ورؤيتهم نفوسهم بما سولت لهم أنفسهم من الإعجاب، فترك الله عصمتهم جزاءً لهم. وقد قال النبي صلى الله عليه وسلّم حين سمع من أصحابه يوم حنين يقولون لن نؤتى من قلة: «لا تتمنوا لقاء العدو واسألوا الله العافية» «١» من تدبيركم إلى نفوسكم بحال، دون الافتقار إلى الله عزَّ وجلَّ، ألا ترى أن داود عليه السلام لما
_________
(١) صحيح البخاري: كتاب الجهاد، ٢٨٠٤، ٢٨٦١، ٢٨٦٣ وصحيح مسلم: كتاب الجهاد والسير، ١٧٤١- ١٧٤٢.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (48,'سأل ربه اللحوق بإبراهيم وإسماعيل وإسحاق فقال له: لست هناك يا داود. فقال: ولم يا رب؟
فقال: لأن أولئك ابتليتهم فصبروا ولم يعرفوا الدنيا ولا عرفتهم وإنك عرفت الدنيا وعرفتك واتخذتها أهلاً. فقال داود عليه السلام: فأرني من عبادك من لو ابتليته صبر. فقال الله عزَّ وجلَّ:
فإني مبتليك. فكان هو المبتدي في طلب البلاء للامتحان من الله تعالى، يعني وذلك لعلم الله السابق في غيب مستور تفرد بمعرفته، فأتاه إبليس في صورة حمامة، وكان من قصته وقصة أوريا ابن حنان ما كان، والله تعالى لم يعصمه من الهم والقصد والفعل، وعصم يوسف من الفعل ولم يعصمه من الهم والقصد.

<span data-type="title" id=toc-69>[سورة آل عمران (٣) : الآيات ١٥٩ الى ١٦٠]</span>
فَبِما رَحْمَةٍ مِنَ اللَّهِ لِنْتَ لَهُمْ وَلَوْ كُنْتَ فَظًّا غَلِيظَ الْقَلْبِ لانْفَضُّوا مِنْ حَوْلِكَ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشاوِرْهُمْ فِي الْأَمْرِ فَإِذا عَزَمْتَ فَتَوَكَّلْ عَلَى اللَّهِ إِنَّ اللَّهَ يُحِبُّ الْمُتَوَكِّلِينَ (١٥٩) إِنْ يَنْصُرْكُمُ اللَّهُ فَلا غالِبَ لَكُمْ وَإِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذِي يَنْصُرُكُمْ مِنْ بَعْدِهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ (١٦٠)
قوله: فَبِما رَحْمَةٍ مِنَ اللَّهِ لِنْتَ لَهُمْ [١٥٩] يعني بتعطف من الله لنت لهم وَلَوْ كُنْتَ فَظًّا [١٥٩] باللسان غَلِيظَ الْقَلْبِ لَانْفَضُّوا مِنْ حَوْلِكَ [١٥٩] أي لتفرقوا من عندك فَاعْفُ عَنْهُمْ [١٥٩] أي تجاوز عن زللهم وَاسْتَغْفِرْ لَهُمْ [١٥٩] هزيمتهم يوم أحد، وَشاوِرْهُمْ فِي الْأَمْرِ [١٥٩] أي لا تبعدهم بالعصيان عنك واشملهم بفضلك فإنك بنا تعفو وبنا تستغفر وإيانا تطالع، فَإِذا عَزَمْتَ فَتَوَكَّلْ عَلَى اللَّهِ [١٥٩] أي إذا أردت إمضاءه بعد المشورة فَتَوَكَّلْ عَلَى اللَّهِ [١٥٩] أي ثق بالله مع ذلك، وفوض إليه جميع أمورك، وافتقر إليه دون غيره فلم يخرج من الدنيا حتى كشف الله تعالى في قلبه العلوم التي كانت بينه وبين الله تعالى بلا واسطة فيها، لما كان يجب من النظر والتفكر اعتباراً بقدرة ربه، كي ينال المزيد من الله تعالى كما أمره بقوله تعالى: وَقُلْ رَبِّ زِدْنِي عِلْماً [طه: ١١٤] وقد حث على ذلك أمته بما روي عنه صلّى الله عليه وسلّم أنه قال: «شاوروا المتقين الذين يؤثرون الآخرة على الدنيا ويؤثرون على أنفسهم في أموركم» . وقال: «شاوروا العلماء الصالحين فإذا عزمتم إلى إمضاء ذلك فتوكلوا على الله» . وقال: آخِ من الإخوان أهل التقى، واجعل مشورتك من يخاف الله تعالى، ولا يكن كلامك بدلاً، ولا تعادين أحداً أبداً حتى تعلم كيف صنعه بينه وبين الله تعالى، فإن كان حسن الصنيع فلا تعادينه، فإن الله تعالى لا يكله إليك، وإن كان سيئ الصنيع فلا تعادينه، فإن الصنيع السوء يكفيه. وقال: من استشير فأشار بغير رأيه سلبه الله تعالى رأيه يعني غشه فيما أشار به عليه، وقال: من شاور واتكل في إمضاء ما عزم ثم ندم فقد اتهم الله تعالى.
قوله: إِنْ يَنْصُرْكُمُ اللَّهُ فَلا غالِبَ لَكُمْ وَإِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذِي يَنْصُرُكُمْ مِنْ بَعْدِهِ [١٦٠] قال: الخذلان هو غاية الترك، وأما الترك فإن صاحبه يذنب وهو مقر بذنبه، فإذا أذنب على أنه ديانة فهو الخذلان، وهو عقوبة الله تعالى صاحب الخذلان لأنه أقامه على ذنبه مع علمه به وتسويفه بالتوبة، ألا ترى أن إبليس لما أبى وأصر عليه بعد الإباء خذله الله بعلمه السابق',NULL,'51',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (49,'فيه، لأنه أراد منه ما علم ولم يرد منه ما أمره به، وآدم عليه السلام لما لم يكن بالترك مخذولاً أقر بالذنب بعد إتيانه ورجع إلى ربه جل وعز، فقبل توبته،

<span data-type="title" id=toc-70>[سورة آل عمران (٣) : آية ١٧٣]</span>
الَّذِينَ قالَ لَهُمُ النَّاسُ إِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزادَهُمْ إِيماناً وَقالُوا حَسْبُنَا اللَّهُ وَنِعْمَ الْوَكِيلُ (١٧٣)
فقوله تعالى: وَقالُوا حَسْبُنَا اللَّهُ وَنِعْمَ الْوَكِيلُ [١٧٣] أي نعم الكفيل بأرزاقنا ونعم الرب. كقوله تعالى: أَلَّا تَتَّخِذُوا مِنْ دُونِي وَكِيلًا [الإسراء: ٢] أي: ربا.

<span data-type="title" id=toc-71>[سورة آل عمران (٣) : آية ١٨٧]</span>
وَإِذْ أَخَذَ اللَّهُ مِيثاقَ الَّذِينَ أُوتُوا الْكِتابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَلا تَكْتُمُونَهُ فَنَبَذُوهُ وَراءَ ظُهُورِهِمْ وَاشْتَرَوْا بِهِ ثَمَناً قَلِيلاً فَبِئْسَ ما يَشْتَرُونَ (١٨٧)
قوله: فَنَبَذُوهُ وَراءَ ظُهُورِهِمْ [١٨٧] أي: لم يعملوا بالكتاب وَاشْتَرَوْا بِهِ ثَمَناً قَلِيلًا [١٨٧] يعني اشتروا بالآخرة الباقية عرض الدنيا الفانية.

<span data-type="title" id=toc-72>[سورة آل عمران (٣) : آية ١٩١]</span>
الَّذِينَ يَذْكُرُونَ اللَّهَ قِياماً وَقُعُوداً وَعَلى جُنُوبِهِمْ وَيَتَفَكَّرُونَ فِي خَلْقِ السَّماواتِ وَالْأَرْضِ رَبَّنا ما خَلَقْتَ هذا باطِلاً سُبْحانَكَ فَقِنا عَذابَ النَّارِ (١٩١)
قوله تعالى: الَّذِينَ يَذْكُرُونَ اللَّهَ قِياماً وَقُعُوداً وَعَلى جُنُوبِهِمْ [١٩١] قال: من أراد حفظ القرآن فليختم بثلاث ختمات على شرط الآية، ختمة قائماً يصلي، وختمة قاعداً يدرس، وختمة مضجعاً على جنبيه، فإنه لا ينسى إن شاء الله عزَّ وجلَّ. ومن اشتغل بطلب العلم بالتقوى وقراءة القرآن وذكر الله عزَّ وجلَّ واتباع السنة واجتناب اللهو لم تصبه الأمراض والأسقام.
ومن أطاع الله بالعلم وصدق النية لم يفقد عقله. وقال النبي صلّى الله عليه وسلّم: «من أطاع الله عزَّ وجلَّ فقد ذكره ومن عصاه فقد نسيه» «١» .

<span data-type="title" id=toc-73>[سورة آل عمران (٣) : آية ٢٠٠]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَصابِرُوا وَرابِطُوا وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ (٢٠٠)
قوله: يَا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَصابِرُوا وَرابِطُوا وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ [٢٠٠] قال: الإيمان أربعة أركان: الأول التوكل على الله، والثاني الاستسلام لأمره، والثالث الرضا بقضائه، والرابع الشكر لنعمائه والتقوى.

<span data-type="title" id=toc-74>باب الإيمان</span>
اليقين قلب الإيمان، والصبر عماد الإيمان، والإخلاص كمال الإيمان، لأن العبد بالإخلاص ينال التصديق، وبالتصديق ينال التحقيق، وبالتحقيق يصل إلى الحق. والإخلاص ثمرة اليقين، لأن اليقين مشاهدة السر، فمن لم تكن له مشاهدة السر مع مولاه لم يخلص عمله لله، والله سبحانه وتعالى أعلم.
_________
(١) في مجمع الزوائد ٢/ ٢٥٨ والمعجم الكبير ٢٢/ ١٥٤: (من أطاع الله فقد ذكره) وفي الزهد لابن المبارك ١/ ١٧: (من أطاع الله فقد ذكر الله، وإن قلت صلاته وصيامه وتلاوته القرآن، ومن عصى الله فقد نسي الله وإن كثرت صلاته وصيامه وتلاوته القرآن) .',NULL,'52',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (50,'<span data-type="title" id=toc-75>السورة التي يذكر فيها النساء</span>

<span data-type="title" id=toc-76>[سورة النساء (٤) : آية ٤]</span>
وَآتُوا النِّساءَ صَدُقاتِهِنَّ نِحْلَةً فَإِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِنْهُ نَفْساً فَكُلُوهُ هَنِيئاً مَرِيئاً (٤)
سئل عن قوله: وَآتُوا النِّساءَ صَدُقاتِهِنَّ نِحْلَةً [٤] قال: أعطوهن الصداق هبة من الله عزَّ وجلَّ لهن. وقد قال: إن النحلة الديانة، وقال: قال النبي صلّى الله عليه وسلّم: «أقذر المعاصي عند الله تعالى منع الأجير أجرته، ومنع المرأة مهرها» .

<span data-type="title" id=toc-77>[سورة النساء (٤) : آية ١٧]</span>
إِنَّمَا التَّوْبَةُ عَلَى اللَّهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَرِيبٍ فَأُولئِكَ يَتُوبُ اللَّهُ عَلَيْهِمْ وَكانَ اللَّهُ عَلِيماً حَكِيماً (١٧)
قوله تعالى: إِنَّمَا التَّوْبَةُ عَلَى اللَّهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَرِيبٍ [١٧] قال: التائب يتقي المعصية ويلزم الطاعة، والمطيع يتقي الرياء ويلزم الذكر، والذاكر يتقي العجب ويلزم نفسه التقصير. وحكي أن الله تعالى أوحى إلى داود عليه السلام أن أنين المذنبين أحب إلي من صراخ الصديقين «١» .

<span data-type="title" id=toc-78>[سورة النساء (٤) : آية ٢٩]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا لا تَأْكُلُوا أَمْوالَكُمْ بَيْنَكُمْ بِالْباطِلِ إِلاَّ أَنْ تَكُونَ تِجارَةً عَنْ تَراضٍ مِنْكُمْ وَلا تَقْتُلُوا أَنْفُسَكُمْ إِنَّ اللَّهَ كانَ بِكُمْ رَحِيماً (٢٩)
قوله:
وَلا تَقْتُلُوا أَنْفُسَكُمْ [٢٩] يعني لا تهلكوا أنفسكم بالمعاصي والإصرار، وترك التوبة عند الرجوع إلى الاستقامة، إِنَّ اللَّهَ كانَ بِكُمْ رَحِيماً [٢٩] حيث حرم عليكم المعصية، كي لا تهلكوا

<span data-type="title" id=toc-79>[سورة النساء (٤) : آية ٣١]</span>
إِنْ تَجْتَنِبُوا كَبائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّئاتِكُمْ وَنُدْخِلْكُمْ مُدْخَلاً كَرِيماً (٣١)
وهو قوله تعالى: إِنْ تَجْتَنِبُوا كَبائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّئاتِكُمْ [٣١] وقال: روي عن ابن مسعود أنه قال: الكبائر من أول النساء إلى هذه الآية. قال سهل: الكبائر ما أوعد الله تعالى النار عليه في كتابه.

<span data-type="title" id=toc-80>[سورة النساء (٤) : آية ٣٦]</span>
وَاعْبُدُوا اللَّهَ وَلا تُشْرِكُوا بِهِ شَيْئاً وَبِالْوالِدَيْنِ إِحْساناً وَبِذِي الْقُرْبى وَالْيَتامى وَالْمَساكِينِ وَالْجارِ ذِي الْقُرْبى وَالْجارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّبِيلِ وَما مَلَكَتْ أَيْمانُكُمْ إِنَّ اللَّهَ لاَ يُحِبُّ مَنْ كانَ مُخْتالاً فَخُوراً (٣٦)
قوله: وَالْجارِ ذِي الْقُرْبى وَالْجارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْبِ وَابْنِ السَّبِيلِ [٣٦] قال: أما ظاهرها فالجار الجنب: البعيد الأجنبي، والصاحب بالجنب:
هو الرفيق في السفر، وقد قيل الزوجة، وابن السبيل: الضيف، أما باطنها فالجار ذو القربى هو القلب، والجار الجنب هو الطبيعة، والصاحب بالجنب هو العقل المقتدي بالشريعة، وابن السبيل هو الجوارح المطيعة لله، هذا باطن الآية.

<span data-type="title" id=toc-81>[سورة النساء (٤) : آية ٤١]</span>
فَكَيْفَ إِذا جِئْنا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَجِئْنا بِكَ عَلى هؤُلاءِ شَهِيداً (٤١)
قوله تعالى: فَكَيْفَ إِذا جِئْنا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَجِئْنا بِكَ عَلى هؤُلاءِ شَهِيداً [٤١] قال سهل: إن الله تعالى وكل بكل عبد مسلم ثلاثمائة وستين ملكاً بعدد عروقه، إن أراد خيراً أعانوه، وإن أراد شراً عاتبوه عليه، فإن عمل شيئاً من ذلك حفظوه عليه، حتى إذا كان يوم القيامة عرضوه عليه ووافقوه على ذلك، حتى إذا صاروا إلى الله تعالى شهدوا عليه بوفاء الطاعة واقتراف الخطيئة، قال الله تعالى: وَجاءَتْ كُلُّ نَفْسٍ مَعَها سائِقٌ وَشَهِيدٌ [ق: ٢١] .
_________
(١) شعب الإيمان ٥/ ٤٥٢ (رقم ٧٢٥١) .',NULL,'53',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (51,'<span data-type="title" id=toc-82>[سورة النساء (٤) : الآيات ٤٧ الى ٤٨]</span>
يا أَيُّهَا الَّذِينَ أُوتُوا الْكِتابَ آمِنُوا بِما نَزَّلْنا مُصَدِّقاً لِما مَعَكُمْ مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهاً فَنَرُدَّها عَلى أَدْبارِها أَوْ نَلْعَنَهُمْ كَما لَعَنَّا أَصْحابَ السَّبْتِ وَكانَ أَمْرُ اللَّهِ مَفْعُولاً (٤٧) إِنَّ اللَّهَ لاَ يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَيَغْفِرُ مَا دُونَ ذلِكَ لِمَنْ يَشاءُ وَمَنْ يُشْرِكْ بِاللَّهِ فَقَدِ افْتَرى إِثْماً عَظِيماً (٤٨)
قوله تعالى: مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهاً [٤٧] أي يحول الله عن الهدى والبصيرة إلى طبع الجهالة. قوله تعالى: إِنَّ اللَّهَ لاَ يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَيَغْفِرُ مَا دُونَ ذلِكَ لِمَنْ يَشاءُ [٤٨] قال: إذا لم يكن بينه وبين أحد مظلمة، وإنما كانت ذنوبه فيما بينه وبين الله تعالى، فإنه يغفرها وهو الجواد الكريم، وقد روي عن النبي صلّى الله عليه وسلّم قال: «يؤتى بعبد يوم القيامة فيؤمر به إلى النار، فيقول: ما كذا كان ظني. فيقول الله عزَّ وجلَّ: ما كان ظنك بي؟ فيقول: أن تغفر لي. فيقول الله عزَّ وجلَّ: قد غفرت لك، فيأمر به إلى الجنة» .

<span data-type="title" id=toc-83>[سورة النساء (٤) : آية ٦٣]</span>
أُولئِكَ الَّذِينَ يَعْلَمُ اللَّهُ ما فِي قُلُوبِهِمْ فَأَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَهُمْ فِي أَنْفُسِهِمْ قَوْلاً بَلِيغاً (٦٣)
قوله تعالى: وَقُلْ لَهُمْ فِي أَنْفُسِهِمْ قَوْلًا بَلِيغاً [٦٣] أي مبلغاً بلسانك كنه ما في قلبك بأحسن العبارة عني.

<span data-type="title" id=toc-84>[سورة النساء (٤) : الآيات ٧٦ الى ٧٧]</span>
الَّذِينَ آمَنُوا يُقاتِلُونَ فِي سَبِيلِ اللَّهِ وَالَّذِينَ كَفَرُوا يُقاتِلُونَ فِي سَبِيلِ الطَّاغُوتِ فَقاتِلُوا أَوْلِياءَ الشَّيْطانِ إِنَّ كَيْدَ الشَّيْطانِ كانَ ضَعِيفاً (٧٦) أَلَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أَيْدِيَكُمْ وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكاةَ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتالُ إِذا فَرِيقٌ مِنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللَّهِ أَوْ أَشَدَّ خَشْيَةً وَقالُوا رَبَّنا لِمَ كَتَبْتَ عَلَيْنَا الْقِتالَ لَوْلا أَخَّرْتَنا إِلى أَجَلٍ قَرِيبٍ قُلْ مَتاعُ الدُّنْيا قَلِيلٌ وَالْآخِرَةُ خَيْرٌ لِمَنِ اتَّقى وَلا تُظْلَمُونَ فَتِيلاً (٧٧)
قوله: الَّذِينَ آمَنُوا يُقاتِلُونَ فِي سَبِيلِ اللَّهِ وَالَّذِينَ كَفَرُوا يُقاتِلُونَ فِي سَبِيلِ الطَّاغُوتِ [٧٦] قال: المؤمنون خصماء الله على أنفسهم، والمنافقون خصماء النفوس على الله عزَّ وجلَّ، يبتدرون إلى السؤال ولا يرضون بما يختار الله لهم وهو سبيل الطاغوت، إذ النفس أكبر الطواغيت، إذا خلا العبد معها، قيل له عن المعصية. قوله تعالى: قُلْ مَتاعُ الدُّنْيا قَلِيلٌ [٧٧] فسئل: ما الدنيا؟ فقال: الدنيا كلها جهل إلاَّ موضع العلم، العلم كله حجة إلاَّ موضع العمل به، والعمل كله هباء إلاَّ موضع الإخلاص، والإخلاص لا يتم إلاَّ بالسنة. ثم قال: دنياك نفسك، فإذا أفنيتها فلا دنيا لك.

<span data-type="title" id=toc-85>[سورة النساء (٤) : آية ٨١]</span>
وَيَقُولُونَ طاعَةٌ فَإِذا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طائِفَةٌ مِنْهُمْ غَيْرَ الَّذِي تَقُولُ وَاللَّهُ يَكْتُبُ ما يُبَيِّتُونَ فَأَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللَّهِ وَكَفى بِاللَّهِ وَكِيلاً (٨١)
قوله تعالى: فَأَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللَّهِ [٨١] فسئل: ما التوكل؟ فقال: التوكل طرح البدن في العبودية، وتعلق القلب بالربوبية، والتبري من الحول والقوة. قيل له: ما حقيقة التوكل في الأصل؟ فقال «١» : حقيقة التوكل في الأصل الإقرار بالتوحيد، وفي الفرع علم الساعة، وفي السكون المعاينة. ثم قال: لا تجزعوا من التوكل، فإنه عيش لأهله. قيل: من أهله؟ قال:
الذين خصوا بالخصوصية. فقيل له: لو زدت لنا وضوحاً. فقال سهل: إن العلوم كلها أدنى باب من التعبد، وجملة التعبد أدنى باب من الورع، وجملة الزهد أدنى باب من ظهور القدرة، ولا تظهر القدرة إلاَّ للمتوكل، وليس للتوكل غاية وصف يوصف به، ولا حد يضرب له بالأمثال، ولا غاية ينتهي إليها. فقيل له: صف لنا بعضه. فقال: إن المتوكل له ألف منزل، أول منزل منه المشي في الهواء. قيل له: بماذا يصل العبد إليه؟ فقال: إن أول الأشياء المعرفة، ثم الإقرار، ثم التوحيد، ثم الإسلام، ثم الإحسان، ثم التفويض، ثم التوكل، ثم السكون إلى الحقّ جلَّ وعزَّ في جميع الحالات، وقال: لا يصح التوكل إلاَّ للمتقي. قيل: ما التقوى؟ قال: كف الأذى «٢» .
_________
(١) في الحلية ١٠/ ٢٩٨: (سئل عن حقيقة التوكل، فقال: نسيان التوكل) . [.....]
(٢) في الحلية ١٠/ ٢٩٨، (قال: لا يصح التوكل إلاَّ للمتقي، ولا تتم التقوى إلا لمتوكل) .',NULL,'54',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (52,'<span data-type="title" id=toc-86>[سورة النساء (٤) : الآيات ٨٥ الى ٨٦]</span>
مَنْ يَشْفَعْ شَفاعَةً حَسَنَةً يَكُنْ لَهُ نَصِيبٌ مِنْها وَمَنْ يَشْفَعْ شَفاعَةً سَيِّئَةً يَكُنْ لَهُ كِفْلٌ مِنْها وَكانَ اللَّهُ عَلى كُلِّ شَيْءٍ مُقِيتاً (٨٥) وَإِذا حُيِّيتُمْ بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْها أَوْ رُدُّوها إِنَّ اللَّهَ كانَ عَلى كُلِّ شَيْءٍ حَسِيباً (٨٦)
قوله تعالى: وَمَنْ يَشْفَعْ شَفاعَةً سَيِّئَةً يَكُنْ لَهُ، كِفْلٌ مِنْها [٨٥] يعني الحظ منها، لأنها تمنع رضا الله تعالى. قوله: وَإِذا حُيِّيتُمْ بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْها أَوْ رُدُّوها [٨٦] يعني زيادة على سلامه الصادر بالنصح لله تعالى. وقال النبي صلى الله عليه وسلّم: «السلام اسم من أسماء الله تعالى أظهره في أرضه، فأفشوه بينكم» «١» .

<span data-type="title" id=toc-87>[سورة النساء (٤) : آية ٨٨]</span>
فَما لَكُمْ فِي الْمُنافِقِينَ فِئَتَيْنِ وَاللَّهُ أَرْكَسَهُمْ بِما كَسَبُوا أَتُرِيدُونَ أَنْ تَهْدُوا مَنْ أَضَلَّ اللَّهُ وَمَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلاً (٨٨)
قوله تعالى: وَاللَّهُ أَرْكَسَهُمْ بِما كَسَبُوا [٨٨] يعني أعادهم إلى ما جبلت عليه أنفسهم من الجهل به. وقال عليه الصلاة والسلام: «لا تستنجوا بعظم ولا روث فإنه ركس» «٢» ، يعني رجع من حاله الأول إلى أن صار طعام الجن. أَتُرِيدُونَ [٨٨] معشر المخلصين أَنْ تَهْدُوا مَنْ أَضَلَّ اللَّهُ [٨٨] قال سهل: الإضلال من الله ترك العصمة عما نهى عنه، وترك المعونة على ما أمر به.

<span data-type="title" id=toc-88>[سورة النساء (٤) : آية ٩٠]</span>
إِلاَّ الَّذِينَ يَصِلُونَ إِلى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثاقٌ أَوْ جاؤُكُمْ حَصِرَتْ صُدُورُهُمْ أَنْ يُقاتِلُوكُمْ أَوْ يُقاتِلُوا قَوْمَهُمْ وَلَوْ شاءَ اللَّهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقاتَلُوكُمْ فَإِنِ اعْتَزَلُوكُمْ فَلَمْ يُقاتِلُوكُمْ وَأَلْقَوْا إِلَيْكُمُ السَّلَمَ فَما جَعَلَ اللَّهُ لَكُمْ عَلَيْهِمْ سَبِيلاً (٩٠)
قوله تعالى: أَوْ جاؤُكُمْ حَصِرَتْ صُدُورُهُمْ [٩٠] أي ضاقت قلوبهم عن قتالكم وقتال قومهم، لحبهم السلامة وركونهم إلى العافية، وهم بنو مدرج.

<span data-type="title" id=toc-89>[سورة النساء (٤) : آية ١٠٥]</span>
إِنَّا أَنْزَلْنا إِلَيْكَ الْكِتابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِما أَراكَ اللَّهُ وَلا تَكُنْ لِلْخائِنِينَ خَصِيماً (١٠٥)
قوله: لِتَحْكُمَ بَيْنَ النَّاسِ بِما أَراكَ اللَّهُ [١٠٥] يعني بما علمك الله تعالى من الحكمة في القرآن وشرائع الإسلام.

<span data-type="title" id=toc-90>[سورة النساء (٤) : آية ١١٧]</span>
إِنْ يَدْعُونَ مِنْ دُونِهِ إِلاَّ إِناثاً وَإِنْ يَدْعُونَ إِلاَّ شَيْطاناً مَرِيداً (١١٧)
قوله تعالى: إِنْ يَدْعُونَ مِنْ دُونِهِ إِلَّا إِناثاً [١١٧] يعني أصواتاً وهو الحجارة والحديد.

<span data-type="title" id=toc-91>[سورة النساء (٤) : آية ١٢١]</span>
أُولئِكَ مَأْواهُمْ جَهَنَّمُ وَلا يَجِدُونَ عَنْها مَحِيصاً (١٢١)
قوله عزَّ وجلَّ: وَلا يَجِدُونَ عَنْها مَحِيصاً [١٢١] يعني معدلا.

<span data-type="title" id=toc-92>[سورة النساء (٤) : آية ١٣٩]</span>
الَّذِينَ يَتَّخِذُونَ الْكافِرِينَ أَوْلِياءَ مِنْ دُونِ الْمُؤْمِنِينَ أَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَإِنَّ الْعِزَّةَ لِلَّهِ جَمِيعاً (١٣٩)
قوله:
أَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ [١٣٩] يعني المنافقين يبتغون عند اليهود المنعة والقوة، ألا ترى إلى قوله عليه الصلاة والسلام: «ما نزل من السماء شيء أعز من اليقين» «٣» أي أمنع وأعظم.

<span data-type="title" id=toc-93>[سورة النساء (٤) : الآيات ١٤١ الى ١٤٢]</span>
الَّذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِنْ كانَ لَكُمْ فَتْحٌ مِنَ اللَّهِ قالُوا أَلَمْ نَكُنْ مَعَكُمْ وَإِنْ كانَ لِلْكافِرِينَ نَصِيبٌ قالُوا أَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِنَ الْمُؤْمِنِينَ فَاللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيامَةِ وَلَنْ يَجْعَلَ اللَّهُ لِلْكافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلاً (١٤١) إِنَّ الْمُنافِقِينَ يُخادِعُونَ اللَّهَ وَهُوَ خادِعُهُمْ وَإِذا قامُوا إِلَى الصَّلاةِ قامُوا كُسالى يُراؤُنَ النَّاسَ وَلا يَذْكُرُونَ اللَّهَ إِلاَّ قَلِيلاً (١٤٢)
قوله: أَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ [١٤١] يعني نغلب ونستولي عليكم. قوله: إِنَّ الْمُنافِقِينَ يُخادِعُونَ اللَّهَ وَهُوَ خادِعُهُمْ
[١٤٢] أي يسرع لهم الجزاء على إظهار الإيمان وإضمار الكفر بترك العصمة والتوفيق، وتمديد الأموال والبنين، والإطراق على عاجل الدنيا، وخاتمتهم النار، فهذا هو المراد من قوله: يُخادِعُونَ اللَّهَ وَهُوَ خادِعُهُمْ
[١٤٢] . قال سهل في قوله تعالى: بَلْ عَجِبْتَ وَيَسْخَرُونَ [الصافات: ١٢] أراد به سرعة مجازاتهم على الإقامة والنفي، فسمى قوله باسم فعلهم. وقد أخبر عنهم بالعجب في مواضع، قال في قوله في قل أوحي: إِنَّا سَمِعْنا قُرْآناً عَجَباً [الجن: ١] وفي ق: بَلْ عَجِبُوا [ق: ٢] وفي ص: إِنَّ هذا لَشَيْءٌ عُجابٌ [ص: ٥] وقد ذكر في الصافات: بَلْ عَجِبْتَ وَيَسْخَرُونَ [الصافات: ١٢] أي رأيت جزاءهم عظيماً، فسمى تعظيم الثواب عجباً، لأن المتعجب إنما يتعجب من أمر بلغ نهايته، فهذا هو المراد من قوله: بَلْ عَجِبْتَ [الصافات: ١٢] .
_________
(١) في صحيح البخاري: كتاب صفة الصلاة، رقم ٧٩٧ والمعجم الكبير ١٠/ ٨٢ (١٠٣٩١) والمعجم الصغير ١/ ١٥٣ (٢٠٣) والمعجم الأوسط ٣/ ٢٣١ (٣٠٠٨) : (إن الله هو السلام) .
(٢) صحيح البخاري: كتاب الوضوء، رقم ١٥٤- ١٥٥.
(٣) نوادر الأصول ٣/ ١٦٩.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (53,'وقد حكي أن شقيقاً قرأ على شريح: «بَلْ عَجِبْتُ» «١» فقال له شريح: «بَلْ عَجِبْتَ» إن الله لا يعجب من شيء، إنما يعجب مَنْ لا يعلم «٢» . قال شقيق: فأخبرت به إبراهيم فقال: إن شريحاً يعجبه علمه، وإن ابن مسعود أعلم منه، وكان يقرأ: «بَلْ عَجِبْتُ» بالضم.
وَإِذا قامُوا إِلَى الصَّلاةِ قامُوا كُسالى
[١٤٢] فهذه من علامات المنافقين، حيث خانوا في هذه الأمانة التي تحمّلوها في الظاهر، واعلم أن لله تعالى أمانة في سمعك وبصرك ولسانك وفرجك، وظاهرك وباطنك، عرضها عليك، فإن لم تحفظها خنت الله، واللَّهَ لاَ يُحِبُّ الْخائِنِينَ [الأنفال: ٥٨] . وقد حكي عن أبي حبان أنه قال: ارتحلت إلى مكة وجئت سعيد بن جبير «٣» فقلت له: جئتك من خراسان في تأويل قوله عليه الصلاة والسلام: «علامة المنافق ثلاث إذا حدث كذب وإذا وعد أخلف وإذا ائتمن خان» «٤» . ولا أرى أنها في نفسي، فتبسم سعيد وقال: وقع في سري ما وقع في سرك، فأتيت علي بن أبي طالب «٥» وعبد الله بن عباس رضي الله عنهما وقت القيلولة، فوجدتهما عند البيت، فسألتهما عن تأويل هذا الحديث فتبسما، وقالا: لقد أشكل علينا ما أشكل عليك، فذهبنا إلى النبي صلى الله عليه وسلّم وقت القيلولة، فأذن لنا فذكرنا له صلّى الله عليه وسلّم هذا، فتبسم رسول الله صلّى الله عليه وسلّم وقال: «ألستما على شهادة أن لا إله إلا الله؟
قلنا: بلى. فقال: هل رجعتما عن ذلك؟ فقلنا: لا. قال: لقد قلتما وصدقتما. ثم قال: ألستما على ما قررتكما عليه من الإيمان بالله وملائكته وكتبه ورسله والجنة والنار والبعث؟ قلنا: نعم، كأنها رأي العين. فقال صلّى الله عليه وسلّم: هذا من الإنجاز. ثم قال صلّى الله عليه وسلّم: ألستما تصليان وتسجدان في الصلاة في الخلوة؟ فقلنا: نعم. فقال: هي الأمانة لا خيانة فيها» .
_________
(١) معجم القراءات القرآنية ٤/ ١٩٧، القراءة رقم ٧٣٧٦، وذكر من مصادر القراءة: النشر ٢/ ٣٥٦ والبحر المحيط ٧/ ٣٥٤ والكشاف ٣/ ٣٣٧.
(٢) في معاني القرآن وإعرابه ٤/ ٣٠٠: (ومن قرأ «عجبت» فهو إخبار عن الله، وقد أنكر قوم هذه القراءة، وقالوا: الله عزّ وجلّ لا يعجب. وإنكارهم هذا غلط ... والعجب من الله، خلافه من الآدميين، كما قال: «ويمكر الله» [الأنفال: ٣٠] و «سخر الله» [التوبة: ٧٩] ، و «هو خادعهم» [النساء: ١٤٢] . والمكر من الله والخداع خلافه من الآدميين ... ) .
(٣) سعيد بن جبير الأسدي، بالولاء، الكوفي (٤٥- ٩٥ هـ) : تابعي، كان أعلمهم على الإطلاق. أخذ العلم عن ابن عباس وابن عمر. قتله الحجاج لخروجه على عبد الملك بن مروان. (الحلية ٤/ ٢٧٢) .
(٤) صحيح البخاري: كتاب الإيمان، حديث رقم ٣٣ وصحيح مسلم: كتاب الإيمان، حديث رقم ١٢٤.
(٥) ثمت شك في هذه الرواية، لأن علي بن أبي طالب قتل سنة ٤٠ هـ، أي قبل ولادة سعيد بن جبير.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (54,'وقال سهل: إن اليقين أوتاد قلوب العارفين وأرواح المشتاقين، كما أن جبال الدنيا مع جبل ق أوتاد الأرضين قوام للعالمين، ثم زاد قوة قلبك حيث قال تعالى: لَوْ أَنْزَلْنا هذَا الْقُرْآنَ عَلى جَبَلٍ لَرَأَيْتَهُ خاشِعاً مُتَصَدِّعاً مِنْ خَشْيَةِ اللَّهِ [الحشر: ٢١] وقد أنزلته على قلوبهم حفظاً وعليكم أمراً، فلم يؤثر حمله فيكم لحفظي إياكم ولطفي ونظري إليكم.
ثم قال: انتهت عقول المؤمنين سائرة إلى العرش فسلمت وحفت بظرائف حكمه وفنون بره، وسارت عقول المنافقين، فلما بلغت رامت الغيوب، فردت منكسة، قال الله تعالى: وَمَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلًا [٨٨، ١٤٣] .

<span data-type="title" id=toc-94>[سورة النساء (٤) : آية ١٧١]</span>
يا أَهْلَ الْكِتابِ لاَ تَغْلُوا فِي دِينِكُمْ وَلا تَقُولُوا عَلَى اللَّهِ إِلاَّ الْحَقَّ إِنَّمَا الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ رَسُولُ اللَّهِ وَكَلِمَتُهُ أَلْقاها إِلى مَرْيَمَ وَرُوحٌ مِنْهُ فَآمِنُوا بِاللَّهِ وَرُسُلِهِ وَلا تَقُولُوا ثَلاثَةٌ انْتَهُوا خَيْراً لَكُمْ إِنَّمَا اللَّهُ إِلهٌ واحِدٌ سُبْحانَهُ أَنْ يَكُونَ لَهُ وَلَدٌ لَهُ مَّا فِي السَّماواتِ وَما فِي الْأَرْضِ وَكَفى بِاللَّهِ وَكِيلاً (١٧١)
قوله تعالى: يا أَهْلَ الْكِتابِ لاَ تَغْلُوا فِي دِينِكُمْ [١٧١] قال: أي لا تجاوزوا دينكم بالبدع، وتعدلوا عن الحق، وهو الكتاب والسنة والإجماع، ميلاً إلى هوى نفوسكم.
وقال: قوام الدين والدنيا في ثلاث: العلم والأدب والمبادرة، وهلاك الدين والدنيا في ثلاث: الجهل والخرق والكسل.
وسمعته مرة أخرى يقول «١» : أربع من دعائم الدين: القيام بالحق على نفسك وغيرها، والقعود عن باطل نفسك وغيرها، والمودة لأهل طاعة الله، والبغض لأهل معصيته.
_________
(١) الحلية ١٠/ ١٩١.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (55,'<span data-type="title" id=toc-95>السورة التي يذكر فيها المائدة</span>

<span data-type="title" id=toc-96>[سورة المائدة (٥) : الآيات ٢ الى ٣]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا لا تُحِلُّوا شَعائِرَ اللَّهِ وَلا الشَّهْرَ الْحَرامَ وَلا الْهَدْيَ وَلا الْقَلائِدَ وَلا آمِّينَ الْبَيْتَ الْحَرامَ يَبْتَغُونَ فَضْلاً مِنْ رَبِّهِمْ وَرِضْواناً وَإِذا حَلَلْتُمْ فَاصْطادُوا وَلا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ أَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرامِ أَنْ تَعْتَدُوا وَتَعاوَنُوا عَلَى الْبِرِّ وَالتَّقْوى وَلا تَعاوَنُوا عَلَى الْإِثْمِ وَالْعُدْوانِ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقابِ (٢) حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْزِيرِ وَما أُهِلَّ لِغَيْرِ اللَّهِ بِهِ وَالْمُنْخَنِقَةُ وَالْمَوْقُوذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيحَةُ وَما أَكَلَ السَّبُعُ إِلاَّ ما ذَكَّيْتُمْ وَما ذُبِحَ عَلَى النُّصُبِ وَأَنْ تَسْتَقْسِمُوا بِالْأَزْلامِ ذلِكُمْ فِسْقٌ الْيَوْمَ يَئِسَ الَّذِينَ كَفَرُوا مِنْ دِينِكُمْ فَلا تَخْشَوْهُمْ وَاخْشَوْنِ الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَأَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَرَضِيتُ لَكُمُ الْإِسْلامَ دِيناً فَمَنِ اضْطُرَّ فِي مَخْمَصَةٍ غَيْرَ مُتَجانِفٍ لِإِثْمٍ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ (٣)
سئل عن قوله: وَتَعاوَنُوا عَلَى الْبِرِّ وَالتَّقْوى [٢] فقال: البر الطاعة لله واتقاء المعصية.
قوله: فَلا تَخْشَوْهُمْ وَاخْشَوْنِ [٣] يعني: فلا تخشوا الكفار في عبادتي واخشوني في اتباعهم، فقال: أعجز الناس من خشي من لا ينفعه ولا يضره، والذي بيده النفع والضر يخاطبه في قوله: فَلا تَخْشَوْهُمْ وَاخْشَوْنِ [٣] .

<span data-type="title" id=toc-97>[سورة المائدة (٥) : الآيات ٥ الى ٦]</span>
الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّباتُ وَطَعامُ الَّذِينَ أُوتُوا الْكِتابَ حِلٌّ لَكُمْ وَطَعامُكُمْ حِلٌّ لَهُمْ وَالْمُحْصَناتُ مِنَ الْمُؤْمِناتِ وَالْمُحْصَناتُ مِنَ الَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلِكُمْ إِذا آتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسافِحِينَ وَلا مُتَّخِذِي أَخْدانٍ وَمَنْ يَكْفُرْ بِالْإِيمانِ فَقَدْ حَبِطَ عَمَلُهُ وَهُوَ فِي الْآخِرَةِ مِنَ الْخاسِرِينَ (٥) يَا أَيُّهَا الَّذِينَ آمَنُوا إِذا قُمْتُمْ إِلَى الصَّلاةِ فَاغْسِلُوا وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى الْمَرافِقِ وَامْسَحُوا بِرُؤُسِكُمْ وَأَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ وَإِنْ كُنْتُمْ جُنُباً فَاطَّهَّرُوا وَإِنْ كُنْتُمْ مَرْضى أَوْ عَلى سَفَرٍ أَوْ جاءَ أَحَدٌ مِنْكُمْ مِنَ الْغائِطِ أَوْ لامَسْتُمُ النِّساءَ فَلَمْ تَجِدُوا ماءً فَتَيَمَّمُوا صَعِيداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَأَيْدِيكُمْ مِنْهُ ما يُرِيدُ اللَّهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَلكِنْ يُرِيدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ (٦)
قوله تعالى: الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّباتُ [٥] قال:
الطيبات الحلال من الرزق. قوله: إِذا قُمْتُمْ إِلَى الصَّلاةِ فَاغْسِلُوا وُجُوهَكُمْ [٦] قال: الطهارة أربعة أشياء: صفاء المطعم وصدق اللسان ومباينة الآثام وخشوع السر، وكل واحد من هذه الأربعة يقابل بكل واحد من تطهير الأعضاء الظاهرة. قوله تعالى: وَلكِنْ يُرِيدُ لِيُطَهِّرَكُمْ [٦] يعني يطهركم من أحوالكم وأخلاقكم وأفعالكم، لترجعوا إليه بحقيقة الفقر من غير تعلق بسبب من الأسباب. والطهارة على سبعة أوجه «١» : طهارة العلم من الجهل، وطهارة الذكر من النسيان، وطهارة الطاعة من المعصية، وطهارة اليقين من الشك، وطهارة العقل من الحمق، وطهارة الظن من النميمة، وطهارة الإيمان مما دونه، ولكل عقوبة طهارة، إلاَّ عقوبة القلب فإنها قسوة «٢» .

<span data-type="title" id=toc-98>[سورة المائدة (٥) : آية ٢٣]</span>
قالَ رَجُلانِ مِنَ الَّذِينَ يَخافُونَ أَنْعَمَ اللَّهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبابَ فَإِذا دَخَلْتُمُوهُ فَإِنَّكُمْ غالِبُونَ وَعَلَى اللَّهِ فَتَوَكَّلُوا إِنْ كُنْتُمْ مُؤْمِنِينَ (٢٣)
قوله تعالى: قالَ رَجُلانِ مِنَ الَّذِينَ يَخافُونَ أَنْعَمَ اللَّهُ عَلَيْهِمَا [٢٣] فسئل: ما هذه النعمة؟ فقال: أنعم الله عليهما بالخوف والمراقبة، إذ الخوف والهم والحزن يزيد في الحسنات، والأشر والبطر يزيد في السيئات.

<span data-type="title" id=toc-99>[سورة المائدة (٥) : الآيات ٥٤ الى ٥٦]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ دِينِهِ فَسَوْفَ يَأْتِي اللَّهُ بِقَوْمٍ يُحِبُّهُمْ وَيُحِبُّونَهُ أَذِلَّةٍ عَلَى الْمُؤْمِنِينَ أَعِزَّةٍ عَلَى الْكافِرِينَ يُجاهِدُونَ فِي سَبِيلِ اللَّهِ وَلا يَخافُونَ لَوْمَةَ لائِمٍ ذلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَاللَّهُ واسِعٌ عَلِيمٌ (٥٤) إِنَّما وَلِيُّكُمُ اللَّهُ وَرَسُولُهُ وَالَّذِينَ آمَنُوا الَّذِينَ يُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكاةَ وَهُمْ راكِعُونَ (٥٥) وَمَنْ يَتَوَلَّ اللَّهَ وَرَسُولَهُ وَالَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللَّهِ هُمُ الْغالِبُونَ (٥٦)
قوله: أَعِزَّةٍ عَلَى الْكافِرِينَ [٥٤] يعني غليظة عليهم. قوله:
إِنَّما وَلِيُّكُمُ اللَّهُ وَرَسُولُهُ وَالَّذِينَ آمَنُوا [٥٥] قال: ولاية الله تعالى الاختيار لمن استولاه، ثم أعلم الرسول أنه ولي المؤمنين، فيجب عليه أن يوالي من والى الله تعالى والذين آمنوا، ثم قال:
وَمَنْ يَتَوَلَّ اللَّهَ وَرَسُولَهُ وَالَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللَّهِ هُمُ الْغالِبُونَ [٥٦] يعني غالبون هوى نفوسهم.

<span data-type="title" id=toc-100>[سورة المائدة (٥) : آية ٦٤]</span>
وَقالَتِ الْيَهُودُ يَدُ اللَّهِ مَغْلُولَةٌ غُلَّتْ أَيْدِيهِمْ وَلُعِنُوا بِما قالُوا بَلْ يَداهُ مَبْسُوطَتانِ يُنْفِقُ كَيْفَ يَشاءُ وَلَيَزِيدَنَّ كَثِيراً مِنْهُمْ ما أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْياناً وَكُفْراً وَأَلْقَيْنا بَيْنَهُمُ الْعَداوَةَ وَالْبَغْضاءَ إِلى يَوْمِ الْقِيامَةِ كُلَّما أَوْقَدُوا ناراً لِلْحَرْبِ أَطْفَأَهَا اللَّهُ وَيَسْعَوْنَ فِي الْأَرْضِ فَساداً وَاللَّهُ لا يُحِبُّ الْمُفْسِدِينَ (٦٤)
قوله: بَلْ يَداهُ مَبْسُوطَتانِ يُنْفِقُ كَيْفَ يَشاءُ [٦٤] وقال: يعني حكمه وأمره ونهيه نافذ في ملكه.
_________
(١) ذكر في الحلية ١٠/ ٢٠٨ ثلاثة أوجه فقط.
(٢) الحلية ١٠/ ٢٠٨.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (56,'<span data-type="title" id=toc-101>[سورة المائدة (٥) : الآيات ٦٦ الى ٦٧]</span>
وَلَوْ أَنَّهُمْ أَقامُوا التَّوْراةَ وَالْإِنْجِيلَ وَما أُنْزِلَ إِلَيْهِمْ مِنْ رَبِّهِمْ لَأَكَلُوا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ أَرْجُلِهِمْ مِنْهُمْ أُمَّةٌ مُقْتَصِدَةٌ وَكَثِيرٌ مِنْهُمْ ساءَ ما يَعْمَلُونَ (٦٦) يا أَيُّهَا الرَّسُولُ بَلِّغْ ما أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ وَإِنْ لَمْ تَفْعَلْ فَما بَلَّغْتَ رِسالَتَهُ وَاللَّهُ يَعْصِمُكَ مِنَ النَّاسِ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الْكافِرِينَ (٦٧)
قوله: وَلَوْ أَنَّهُمْ أَقامُوا التَّوْراةَ وَالْإِنْجِيلَ وَما أُنْزِلَ إِلَيْهِمْ مِنْ رَبِّهِمْ لَأَكَلُوا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ أَرْجُلِهِمْ [٦٦] يعني لو علموا بما أنزل الله على محمد صلّى الله عليه وسلّم، فلو عملت به لبلغت هذه المنزلة كما بلغها من عمل بها، ولو أقبلت على الرازق لكفيت مؤنة الرزق. ثم قال: ولست أكبر من عمرو بن الليث «١» كان يمر وبين يديه ألف راكب وألف غلام، في يد كل غلام عمود من ذهب وفضة، فآل أمره إلى أن حبس في بيت حين حمل إلى الخليفة، ومنع عنه الطعام والشراب، وفتح الباب فوجدوه ميتاً، وفمه مملوء من الجص والآجر من شدة جوعه. ثم قال: إني نصحت لكم، وإني لكم من الناصحين. وقد حكى مالك بن دينار «٢» عن حماد بن سلمة «٣» وحماد بن يزيد «٤» أنهما دخلا على رابعة «٥» فذكرا شيئاً من أمر الدنيا فقالت رابعة: لقد أكثرتما ذكر الدنيا، ما أظنكما إلاَّ جياعاً، فإن كنتما جياعاً فاعمدا إلى القدر وذلك الدقيق، فاصنعا لأنفسكما ما وسوس، قال بعض من كان معها: لو كان لنا ثوم. فقال حماد: فرأيت رابعة حركت شفتيها، فما سكتت حتى جاء طير في منقاره رأس ثوم، فرمى به ومضى «٦» .
قوله تعالى: وَاللَّهُ يَعْصِمُكَ مِنَ النَّاسِ [٦٧] قيل: ما هذه العصمة؟ فقال: إن الله تعالى وعده أن لا يبتليه كما ابتلى سائر الأنبياء عليهم الصلاة والسلام، إبراهيم بالنار، وإسماعيل بالذبح، وغيرهما، إذ كان لا يشعر بما يفعل به، كما قال: وَما أَدْرِي مَا يُفْعَلُ بِي وَلا بِكُمْ [الأحقاف: ٩] فأعلمه الله تعالى أنه يعصمه من الناس.
_________
(١) عمرو بن الليث، الصفار ( ... - ٢٨٩ هـ) : ثاني أمراء الدولة الصفارية، وأحد الشجعان الدهاة. ولي بعد وفاة أخيه مؤسس الدولة يعقوب سنة ٢٦٥ هـ، وأقره المعتمد العباسي على أعمال أخيه كلها، وهي:
خراسان وأصبهان وسجستان والسند وكرمان، وعزله المعتمد سنة ٢٧١، وقامت حروب بينهما، ثم ولاه المعتمد سنة ٢٧٦ شرطة بغداد. (الأعلام ٥/ ٨٤) .
(٢) مالك بن دينار البصري ( ... - ١٣١ هـ) : من رواة الحديث. كان ورعا، يأكل من كسبه، ويكتب المصاحف بالأجرة. توفي في البصرة. (الحلية ٢/ ٣٥٧) . [.....]
(٣) حماد بن سلمة بن دينار البصري الرّبعي بالولاء ( ... - ١٦٧ هـ) : مفتي البصرة، وأحد رجال الحديث، ومن النحاة. كان حافظا ثقة مأمونا. (الحلية ٦/ ٢٤٩) .
(٤) حماد بن يزيد بن درهم الأزدي الجهضمي (٩٨- ١٧٩ هـ) : شيخ العراق في عصره، ومن حفاظ الحديث المجودين، حفظ أربعة آلاف حديث. (الحلية ٦/ ٢٥٧) .
(٥) رابعة بنت إسماعيل العدوية ( ... - ١٣٥ هـ) : صالحة، مشهورة، من أهل البصرة، ومولدها بها. لها أخبار في العبادة والنسك، ولها شعر. (الأعلام ٣/ ١٠) .
(٦) في كرامات الأولياء ص ٢٢٦ (أن رابعة كانت تطبخ قدرا، فاشتهت بصلا، فجاء طير في منقاره بصلة، فألقاها إليها) .',NULL,'59',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (57,'<span data-type="title" id=toc-102>[سورة المائدة (٥) : آية ٨٣]</span>
وَإِذا سَمِعُوا مَآ أُنْزِلَ إِلَى الرَّسُولِ تَرى أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ يَقُولُونَ رَبَّنا آمَنَّا فَاكْتُبْنا مَعَ الشَّاهِدِينَ (٨٣)
قوله: وَإِذا سَمِعُوا مَآ أُنْزِلَ إِلَى الرَّسُولِ تَرى أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ [٨٣] قال: هم القسيسون والرهبان، كان الناس يتمسحون بهم لعلمهم في الدين، قدموا على النبي صلى الله عليه وسلّم فقرأ عليهم القرآن، فرقُّوا له، ففاضت أعينهم ولم يستكبروا، بعصمة الله إياهم عن الاستكبار، فدخلوا في دينه لما وضع الله تعالى من علمه فيهم، ثم قال: فساد الدين بثلاث:
الملوك إذا أخذوا في السرف والشهوات، والعلماء إذا أفتوا بالرخص، والقراء إذا تعبدوا بغير علم «١» وإن العلماء يحتاج إليهم الخلق في الدنيا والآخرة، وقد حكي عن جابر بن عبد الله رضي الله عنه عن النبي صلى الله عليه وسلّم قال: «إن أهل الجنة يحتاجون إلى العلماء في الجنة كما يحتاجون إليهم في الدنيا، يزورون ربهم في كل جمعة فيقال لهم: تمنوا ما شئتم. فينطلقون إلى العلماء، فيقول لهم العلماء: تمنوا كذا تمنوا كذا، فيتمنون» «٢» .

<span data-type="title" id=toc-103>[سورة المائدة (٥) : آية ١٠٩]</span>
يَوْمَ يَجْمَعُ اللَّهُ الرُّسُلَ فَيَقُولُ مَاذَآ أُجِبْتُمْ قالُوا لا عِلْمَ لَنا إِنَّكَ أَنْتَ عَلاَّمُ الْغُيُوبِ (١٠٩)
وقوله تعالى: يَوْمَ يَجْمَعُ اللَّهُ الرُّسُلَ فَيَقُولُ مَاذَآ أُجِبْتُمْ قالُوا لاَ عِلْمَ لَنا [١٠٩] يعني لا علم لنا بما كان في قلوبهم من الإيمان بك وغيره، إنما علمنا بما أظهروه من الإقرار باللسان إِنَّكَ أَنْتَ عَلَّامُ الْغُيُوبِ [١٠٩] فقيل له: يطالبهم بحقيقة ما في قلوب الأمة؟ فقال: لا، وإنما وقع السؤال بنفسه إياهم عن حقيقة الظاهر الذي لا يظهر إلاَّ بحقيقة الباطن، فأجابوا بالإشارة إلى رد العلم إليه. ويحتمل أن يكون معناه: لا علم لنا بمعنى سؤالك، مع علمك بما أجبنا:
إِنَّكَ أَنْتَ عَلَّامُ الْغُيُوبِ [١٠٩] .

<span data-type="title" id=toc-104>[سورة المائدة (٥) : آية ١١٦]</span>
وَإِذْ قالَ اللَّهُ يا عِيسَى ابْنَ مَرْيَمَ أَأَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَأُمِّي إِلهَيْنِ مِنْ دُونِ اللَّهِ قالَ سُبْحانَكَ ما يَكُونُ لِي أَنْ أَقُولَ ما لَيْسَ لِي بِحَقٍّ إِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُ تَعْلَمُ مَا فِي نَفْسِي وَلا أَعْلَمُ ما فِي نَفْسِكَ إِنَّكَ أَنْتَ عَلاَّمُ الْغُيُوبِ (١١٦)
قوله تعالى: تَعْلَمُ مَا فِي نَفْسِي وَلا أَعْلَمُ مَا فِي نَفْسِكَ [١١٦] أي لا أعلم غيبك في سؤالك، مع علمك به. ويحتمل أن يريد: تعلم ما في سري ولا أعلم ما في نفسك المستودع في سري، لأن سرك بينك وبينها لا يطلع عليه أحد دونك، وهي العين التي ترى بها الحق، وأذن تسمع بها الحق، ولسان ينادي بالحق. والدليل عليه قوله تعالى للمنافقين: صُمٌّ بُكْمٌ عُمْيٌ [البقرة: ١٨، ١٧١] لأنه لم يكن لهم هذه المستودعات، والله سبحانه وتعالى أعلم.
_________
(١) انظر مثل هذا القول للتستري في الحلية ١٠/ ٢٠٦.
(٢) كشف الخفاء ١/ ٢٦٣، ولسان الميزان ٥/ ١٥، وفيهما أن الحديث موضوع.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (58,'<span data-type="title" id=toc-105>السورة التي يذكر فيها الأنعام</span>

<span data-type="title" id=toc-106>[سورة الأنعام (٦) : آية ٥٢]</span>
وَلا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَداةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ ما عَلَيْكَ مِنْ حِسابِهِمْ مِنْ شَيْءٍ وَما مِنْ حِسابِكَ عَلَيْهِمْ مِنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِمِينَ (٥٢)
سئل عن قوله: وَلا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَداةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ [٥٢] قال: أي يريدون وجه الله ورضاه، ولا يغيبون عنه ساعة. ثم قال: أزهد الناس أصفاهم مطعماً، وأعبد الناس أشدهم اجتهاداً في القيام بالأمر والنهي، وأحبهم إلى الله أنصحهم لخلقه. وسئل عن العمر قال: الذي يضيع العمر.

<span data-type="title" id=toc-107>[سورة الأنعام (٦) : آية ٥٤]</span>
وَإِذا جاءَكَ الَّذِينَ يُؤْمِنُونَ بِآياتِنا فَقُلْ سَلامٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلى نَفْسِهِ الرَّحْمَةَ أَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُوءاً بِجَهالَةٍ ثُمَّ تابَ مِنْ بَعْدِهِ وَأَصْلَحَ فَأَنَّهُ غَفُورٌ رَحِيمٌ (٥٤)
قوله تعالى: كَتَبَ رَبُّكُمْ عَلى نَفْسِهِ الرَّحْمَةَ أَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُوءاً بِجَهالَةٍ ثُمَّ تابَ مِنْ بَعْدِهِ وَأَصْلَحَ فَأَنَّهُ غَفُورٌ رَحِيمٌ [٥٤] . وقد حكي أن الله تعالى أوحى إلى داود عليه السلام: يا داود من عرفني أرادني، ومن أرادني أحبني، ومن أحبني طلبني ومن طلبني وجدني، ومن وجدني حفظني «١» . فقال داود صلوات الله عليه: إلهي، أين أجدك إذا طلبتك؟ فقال: عند المنكسرة قلوبهم من مخافتي «٢» . فقال: إلهي، أتيت أطباء عبادك للتداوي فكلهم دلوني عليك، فبؤساً للقانطين من رحمتك، فهل لي وجه أن تداويني؟ فقال الله عزَّ وجلَّ:
الذين أتيتهم كلهم دلوك علي؟ فقال: نعم. قال: فاذهب فبشر المذنبين، وأنذر الصديقين. فتحير داود فقال: يا رب، غلطت أنا أم لا؟ قال: ما غلطت يا داود. قال: وكيف ذلك؟ قال: بشر المذنبين بأني غفور، وأنذر الصديقين بأني غيور. فسئل: من الصديقون؟ فقال: الذين عدوا أنفاسهم بالتسبيح والتقديس، وحفظوا الجوارح والحواس، فصار قولهم وفعلهم صدقاً، وصار ظاهرهم وباطنهم صدقاً، وصار دخولهم في الأشياء وخروجهم عنها بالصدق، ومرجعهم إلى مقعد صدق عند مليك مقتدر.

<span data-type="title" id=toc-108>[سورة الأنعام (٦) : آية ٦٩]</span>
وَما عَلَى الَّذِينَ يَتَّقُونَ مِنْ حِسابِهِمْ مِنْ شَيْءٍ وَلكِنْ ذِكْرى لَعَلَّهُمْ يَتَّقُونَ (٦٩)
وقوله سبحانه وتعالى: وَلكِنْ ذِكْرى لَعَلَّهُمْ يَتَّقُونَ [٦٩] قال: إن الله تعالى أخذ على أوليائه التذكرة لعباده، كما أخذ التبليغ على أنبيائه صلوات الله عليهم أجمعين. فعلى أولياء الله أن يدلوا عليه، فمتى قعدوا عن ذلك كانوا مقصرين. قيل له: فقد رأينا كثيرا منهم
_________
(١) ورد هذا القول منسوبا إلى عتبة الغلام في الحلية ١٠/ ٨١.
(٢) الحلية ٤/ ٣٢ وصفوة الصفوة ٢/ ٢٩٣ وفي الحلية ٢/ ٣٦٤ أنه حديث بين الله عزّ وجلّ ونبيه موسى عليه السلام.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (59,'قعدوا عن ذلك. فقال: إنهم لم يقعدوا عنه إلاَّ عند عدم الاحتياج إليه، كالأمر بالمعروف والنهي عن المنكر. وقد كان عندنا رجل بالبصرة له منزلة رفيعة، لزمه فرض من ذلك في وقت من الأوقات، فبادر نحوه، فلقيه رجل آخر وقال له: إن الله تعالى أمرني بما عزمت عليه، وكفاك إياه، فرجع إلى منزله، وحمد الله تعالى على حسن الكفاية، والله أعلم.

<span data-type="title" id=toc-109>[سورة الأنعام (٦) : الآيات ٧٦ الى ٧٧]</span>
فَلَمَّا جَنَّ عَلَيْهِ اللَّيْلُ رَأى كَوْكَباً قالَ هذا رَبِّي فَلَمَّا أَفَلَ قالَ لا أُحِبُّ الْآفِلِينَ (٧٦) فَلَمَّا رَأَى الْقَمَرَ بازِغاً قالَ هذا رَبِّي فَلَمَّا أَفَلَ قالَ لَئِنْ لَمْ يَهْدِنِي رَبِّي لَأَكُونَنَّ مِنَ الْقَوْمِ الضَّالِّينَ (٧٧)
قوله: فَلَمَّا جَنَّ عَلَيْهِ اللَّيْلُ رَأى كَوْكَباً قالَ هذا رَبِّي [٧٦] فقال: كان هذا القول منه تعريضاً لقومه عند حيرة قلوبهم، لأنه كان أوتي رشده من قبل، كما قال: وَكَذلِكَ نُرِي إِبْراهِيمَ مَلَكُوتَ السَّماواتِ وَالْأَرْضِ [٧٥] . قيل: ما معنى قوله: لَئِنْ لَمْ يَهْدِنِي رَبِّي [٧٧] قال: يعني لئن لم يدم لي الهداية، لَأَكُونَنَّ مِنَ الْقَوْمِ الضَّالِّينَ [٧٧] ثم قال: كانت ملة إبراهيم عليه السلام السخاوة، وحالة التبري من كل شيء سوى الله تعالى، ألا تراه حين قال جبريل عليه السلام:
هل لك حاجة؟ قال: أما إليك فلا. لم يعتمد على أحد سواه في كل حال.

<span data-type="title" id=toc-110>[سورة الأنعام (٦) : آية ٩٨]</span>
وَهُوَ الَّذِي أَنْشَأَكُمْ مِنْ نَفْسٍ واحِدَةٍ فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌ قَدْ فَصَّلْنَا الْآياتِ لِقَوْمٍ يَفْقَهُونَ (٩٨)
وقوله تعالى: فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌ [٩٨] أي مستقر في أرحام النساء و «مستودع» يعني النطفة في صلب آدم عليه السلام.

<span data-type="title" id=toc-111>[سورة الأنعام (٦) : آية ١٢٠]</span>
وَذَرُوا ظاهِرَ الْإِثْمِ وَباطِنَهُ إِنَّ الَّذِينَ يَكْسِبُونَ الْإِثْمَ سَيُجْزَوْنَ بِما كانُوا يَقْتَرِفُونَ (١٢٠)
وقوله: وَذَرُوا ظاهِرَ الْإِثْمِ وَباطِنَهُ [١٢٠] يعني اتركوا المعاصي بالجوارح، ومحبتها بالقلب، وبالإصرار عليها.

<span data-type="title" id=toc-112>[سورة الأنعام (٦) : آية ١٢٥]</span>
فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلامِ وَمَنْ يُرِدْ أَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقاً حَرَجاً كَأَنَّما يَصَّعَّدُ فِي السَّماءِ كَذلِكَ يَجْعَلُ اللَّهُ الرِّجْسَ عَلَى الَّذِينَ لا يُؤْمِنُونَ (١٢٥)
وقوله: فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلامِ [١٢٥] . قال سهل: إن الله ميز بين المريد والمراد في هذه الآية، وإن كان الجميع من عنده، وإنما أراد أن يبين موضع الخصوص من العموم، فخص المراد في هذه السورة وغيرها، وذكر المريد وهو موضع العموم في هذه السورة أيضاً، وهو قوله تعالى: وَلا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَداةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ [٥٢] فهو قصد العبد في حركاته وسكونه إليه، كما قال:
وَالَّذِينَ اسْتَجابُوا لِرَبِّهِمْ وَأَقامُوا الصَّلاةَ [الشورى: ٣٨] فكل من وجد حال المريد والمراد فهو من فضل الله عليه، ألا ترى أنه جمع بينهما في قوله تعالى: وَما بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللَّهِ [النحل: ٥٣] قيل له: فما الفصل بينهما؟ فقال: المريد الذي يتكلف القصد إليه والعبادة لله تعالى ويطلب الطريق إليه، فهو في الطلب بعد، والمراد قيام الله تعالى له بها، والرجل يجد في نفسه ما يدل على المريد، والمراد يدخل في الطاعات وقتاً يجد ما يحمله على الأعمال من غير تكلف وجهد نظراً من الله تعالى له، ثم يخرج بعد ذلك إلى علو المقامات ورفيع الدرجات.
قيل له: ما معنى المقامات؟ فقال: هي موجودة في كتاب الله تعالى في قصة الملائكة: وَما مِنَّا إِلَّا لَهُ مَقامٌ مَعْلُومٌ [الصافات: ١٦٤] وقال: وَلِكُلٍّ دَرَجاتٌ مِمَّا عَمِلُوا [١٣٢] ، وقال في صفة المريد: شغل المريد إقامة الفرض والاستغفار من الذنب وطلب السلامة من الخلق. وقال سهل: إن الله عزَّ وجلَّ ينظر في القلوب والقلوب عنده، فما كان أشدها تواضعاً له خصه بما شاء',NULL,'62',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (60,'ثم بعد ذلك ما كان أسرعها رجوعاً، وهما هاتان الخصلتان. وقال: ما اطلع الله على قلب فرأى فيه همّ الدنيا إلاَّ مقته، والمقت أن يتركه ونفسه. والقلب لا يملكه أحد إلاَّ الله تعالى، ولا يطيع أحداً إلاَّ الله، فإذا ذكرت به فضع سرك مع الله، فإنه ليس من أحد وضعت سرك عنده إلاَّ هتكه، إلاَّ الله عزّ وجلّ.

<span data-type="title" id=toc-113>[سورة الأنعام (٦) : آية ١٢٧]</span>
لَهُمْ دارُ السَّلامِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِما كانُوا يَعْمَلُونَ (١٢٧)
قوله: لَهُمْ دارُ السَّلامِ عِنْدَ رَبِّهِمْ [١٢٧] قال: يعني سلم فيه من هواجس نفسه ووساوس عدوه.

<span data-type="title" id=toc-114>[سورة الأنعام (٦) : آية ١٢٩]</span>
وَكَذلِكَ نُوَلِّي بَعْضَ الظَّالِمِينَ بَعْضاً بِما كانُوا يَكْسِبُونَ (١٢٩)
قوله تعالى: وَكَذلِكَ نُوَلِّي بَعْضَ الظَّالِمِينَ بَعْضاً بِما كانُوا يَكْسِبُونَ [١٢٩] أي ينتقم الله تعالى من الظالم بالظالم، ثم ينتقم من الجميع بنفسه.

<span data-type="title" id=toc-115>[سورة الأنعام (٦) : آية ١٤٧]</span>
فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ واسِعَةٍ وَلا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِمِينَ (١٤٧)
قوله تعالى: فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ واسِعَةٍ [١٤٧] قال سهل: قيل لرسول الله صلّى الله عليه وسلّم: من أعرض عنك فرغبه فيَّ، فإن من رغب فينا ففيك رغب لا غير، فأطمعهم في الرحمة، ولا تقطع قلبك عنهم فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ واسِعَةٍ [١٤٧] .

<span data-type="title" id=toc-116>[سورة الأنعام (٦) : الآيات ١٥١ الى ١٥٣]</span>
قُلْ تَعالَوْا أَتْلُ ما حَرَّمَ رَبُّكُمْ عَلَيْكُمْ أَلاَّ تُشْرِكُوا بِهِ شَيْئاً وَبِالْوالِدَيْنِ إِحْساناً وَلا تَقْتُلُوا أَوْلادَكُمْ مِنْ إِمْلاقٍ نَحْنُ نَرْزُقُكُمْ وَإِيَّاهُمْ وَلا تَقْرَبُوا الْفَواحِشَ مَا ظَهَرَ مِنْها وَما بَطَنَ وَلا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلاَّ بِالْحَقِّ ذلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَعْقِلُونَ (١٥١) وَلا تَقْرَبُوا مالَ الْيَتِيمِ إِلاَّ بِالَّتِي هِيَ أَحْسَنُ حَتَّى يَبْلُغَ أَشُدَّهُ وَأَوْفُوا الْكَيْلَ وَالْمِيزانَ بِالْقِسْطِ لا نُكَلِّفُ نَفْساً إِلاَّ وُسْعَها وَإِذا قُلْتُمْ فَاعْدِلُوا وَلَوْ كانَ ذا قُرْبى وَبِعَهْدِ اللَّهِ أَوْفُوا ذلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَذَكَّرُونَ (١٥٢) وَأَنَّ هذا صِراطِي مُسْتَقِيماً فَاتَّبِعُوهُ وَلا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيلِهِ ذلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَتَّقُونَ (١٥٣)
قوله تعالى: وَلا تَقْرَبُوا الْفَواحِشَ مَا ظَهَرَ مِنْها وَما بَطَنَ [١٥١] ما ظهر منها ما نهى عن إتيانه بالجوارح الظاهرة، وما بطن يعني الإصرار عليه هو على ضربين: فواحد يأتي بمعصية ويبقى مصراً عليها مقيماً على إتيانها، وآخر مصرّ على المعصية لمحبتها في القلب، ولا يقدر أن يفعلها متى وجدها لضعف جوارحه، وهو على أن يفعلها، وهذا من أعظم الإصرار.
وقال سهل: من أكل الحلال بالشهوة فهو مصرّ، ومن جاوز حاله إلى الغد ما لم يأت الغد فهو مصر. فسئل عن الأنبياء صلوات الله عليهم أجمعين في التفكر فيما لا يعنيهم، فقال: يجوز عليهم الفعل بالجوارح حتى تابوا إلى الله تعالى عن ذلك، فكيف الفكرة. قيل له: هل للقلب من تعبد استعبده الله به دون الجوارح؟ فقال: نعم سكون القلب. قيل له: السكون هو الغرض أم العلم الذي به السكون؟ فقال: هو علم اسميه السكون، يجر ذلك السكون إلى اليقين، فالسكون مع اليقين فريضة.
قوله تعالى: وَإِذا قُلْتُمْ فَاعْدِلُوا
[١٥٢] قال: تكلم أهل الصدق على أربعة أوجه، قوم تكلموا في الله وبالله ولله ومع الله، وقوم تكلموا في أنفسهم لأنفسهم فسلموا من آفة الكلام وقوم تكلموا في الخلق ونسوا أنفسهم وابتدعوا وضلوا، وبئس ما صنعوا إلى أنفسهم. فاتركوا الكلام للعلم، ثم تكلموا على الضرورة تسلموا من آفات الكلام. يعني أن لا تتكلم حتى تخاف من الإثم. ثم قال: من ظن [ظن السوء] «١» حرم اليقين، ومن تكلم بما لا يعنيه حرم الصدق، ومن شغل جوارحه في غير الله «٢» حرم الورع، فإذا حرم العبد هذه الثلاث هلك، وهو مثبت في ديوان
_________
(١) ما بين القوسين إضافة من الحلية ١٠/ ١٩٦.
(٢) في الحلية ١٠/ ١٩٦: (اشتغل بالفضول) مكان (شغل جوارحه في غير الله) .',NULL,'63',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (61,'الأعداء. وقد حكي عن الربيع بن خيثم «١» رحمه الله أنه قال: ما أنا عن نفسي براض فأتفرغ من ذم نفسي إلى ذم الناس، خافوا الله في ذنوب العباد، وتواثبوا في ذنوب أنفسهم «٢» .
قوله عزَّ وجلَّ: وَأَنَّ هذا صِراطِي مُسْتَقِيماً [١٥٣] قال: الطريق المستقيم هو الذي لا يكون لأصحاب الأهواء والبدع في الدين، هم ليست لهم توبة، كما روي عن النبي صلى الله عليه وسلّم أنه قال: «لكل ذنب توبة، إلاَّ لأصحاب البدع والأهواء، وإني منهم بريء وهم مني براء، وإن الله عزَّ وجلَّ حجز عنهم التوبة» »
، أي ضيق عليهم التوبة.

<span data-type="title" id=toc-117>[سورة الأنعام (٦) : آية ١٦٥]</span>
وَهُوَ الَّذِي جَعَلَكُمْ خَلائِفَ الْأَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجاتٍ لِيَبْلُوَكُمْ فِي ما آتاكُمْ إِنَّ رَبَّكَ سَرِيعُ الْعِقابِ وَإِنَّهُ لَغَفُورٌ رَحِيمٌ (١٦٥)
قوله تعالى: إِنَّ رَبَّكَ سَرِيعُ الْعِقابِ وَإِنَّهُ لَغَفُورٌ رَحِيمٌ [١٦٥] قال: يعني عقوبة القلب وهو الستر والحجاب، حتى يميل إلى من سواه، وما من عبد يطلع الله على قلبه فيرى في قلبه غيره إلاَّ سلط عليه عدوه، وإنه لغفور لمن تاب منه. قال: ولا يقال لشيء من المضار عقوبة، فإنها طهارة وكفّارة، إلاَّ قسوة القلب فإنها عقوبة «٤» ، وعقوبات العلانية العذاب، وعقوبات القلب درجات، فالقلب للنفس فيه حظ ومراد.
قوله تعالى: إِنَّ الَّذِينَ فَرَّقُوا دِينَهُمْ وَكانُوا شِيَعاً [١٥٩] قال: المحجوب الذي يسلط الله عليه عدوه، لا يجول قلبه في الملكوت، ولا تظهر له القدرة، ولا يشاهد الله، والقلب القاسي أن يكله الله إلى تدبيره وأسبابه، وإنما مثل ميل القلب اللسان إذا تكلم اللسان بشيء لم يتكلم بغيره كذلك القلب إذا هم بشيء لم يكن معه غيره، والله سبحانه وتعالى أعلم.
_________
(١) الربيع بن خيثم بن عائذ، أبو يزيد الثوري ( ... - ٦٥ هـ) : كان يعد من عقلاء الرجال، أدرك زمان النبي صلى الله عليه وسلّم وأرسل عنه. شهد صفين مع علي. (الحلية ٩/ ٤٨) .
(٢) الحلية ٩/ ٥٢ وشعب الإيمان ٥/ ٣١٢ (رقم ٦٧٦٤) ، ٦/ ٨٧ (رقم ٧٥٦٣) وصفوة الصفوة ٣/ ٦٠.
(٣) نوادر الأصول ٢/ ٢٤٥، والحلية ٤/ ١٣٨، وفيه: (هذا حديث غريب من حديث شعبة تفرد به بقية) .
(٤) في الحلية ١٠/ ٢٠٨: (كل عقوبة طهارة، إلاَّ عقوبة القلب فإنها قسوة) . [.....]',NULL,'64',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (62,'<span data-type="title" id=toc-118>السورة التي يذكر فيها الأعراف</span>

<span data-type="title" id=toc-119>[سورة الأعراف (٧) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
المص (١)
قوله عزَّ وجلَّ: المص [١] يعني أنا الله أقضي بين الخلق بالحق «١» ، ومن هذه الحروف اسم الله تعالى وهو الصمد «٢» .

<span data-type="title" id=toc-120>[سورة الأعراف (٧) : آية ١٦]</span>
قالَ فَبِما أَغْوَيْتَنِي لَأَقْعُدَنَّ لَهُمْ صِراطَكَ الْمُسْتَقِيمَ (١٦)
قوله تعالى: لَأَقْعُدَنَّ لَهُمْ صِراطَكَ الْمُسْتَقِيمَ [١٦] أي شرائع الإسلام بعد أن بينها الله تعالى لهم بقوله: أَوَلَمْ يَهْدِ لَهُمْ [السجدة: ٢٦] أي: أو لم نبين لهم طريق الخير وهو الأمر وطريق الشر وهو النهي، فمالوا إلى حظ نفوسهم كما قالُوا طائِرُكُمْ مَعَكُمْ [يس: ١٩] .

<span data-type="title" id=toc-121>[سورة الأعراف (٧) : آية ٢٠]</span>
فَوَسْوَسَ لَهُمَا الشَّيْطانُ لِيُبْدِيَ لَهُما ما وُورِيَ عَنْهُما مِنْ سَوْآتِهِما وَقالَ مَا نَهاكُما رَبُّكُما عَنْ هذِهِ الشَّجَرَةِ إِلاَّ أَنْ تَكُونا مَلَكَيْنِ أَوْ تَكُونا مِنَ الْخالِدِينَ (٢٠)
قوله: فَوَسْوَسَ لَهُمَا الشَّيْطانُ [٢٠] قال: الوسوسة ذكر الطبع، ثم النفس، ثم الهم والتدبير، ووسواس العدو على ثلاث مقامات: فالأول يدعوه ويوسوس له، والثاني يأمن إذا علم أنه يقبل، والثالث ليس له إلاَّ الانتظار والطمع، وهو للصديقين.

<span data-type="title" id=toc-122>[سورة الأعراف (٧) : آية ٢٩]</span>
قُلْ أَمَرَ رَبِّي بِالْقِسْطِ وَأَقِيمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ كَما بَدَأَكُمْ تَعُودُونَ (٢٩)
قوله تعالى: وَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ [٢٩] فقال: اطلبوا من السر بالنية الإخلاص فإن الرياء لا يعرفه إلاَّ المخلصون، واطلبوا من العلانية الفعل بالاقتداء، فإن من لم يكن اقتداؤه في جميع أموره بالنبي صلّى الله عليه وسلّم فهو ضال، وغير هذين مغاليط.

<span data-type="title" id=toc-123>[سورة الأعراف (٧) : آية ٣١]</span>
يا بَنِي آدَمَ خُذُوا زِينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَكُلُوا وَاشْرَبُوا وَلا تُسْرِفُوا إِنَّهُ لاَ يُحِبُّ الْمُسْرِفِينَ (٣١)
قوله تعالى: وَكُلُوا وَاشْرَبُوا وَلا تُسْرِفُوا إِنَّهُ لاَ يُحِبُّ الْمُسْرِفِينَ [٣١] قال «٣» :
الأكل على خمسة: الضرورة والقوام والقوت والمعلوم والفقد، والسادس لا خير فيه وهو التخليط، فإن الله تعالى خلق الدنيا فجعل العلم والحكمة في الجوع، وجعل الجهل والمعصية في الشبع، فإذا جعتم فاطلبوا الشبع ممن ابتلاكم بالجوع، وإذا شبعتم فاطلبوا الجوع ممن ابتلاكم بالشبع، وإلا تماديتم وطغيتم، ثم قرأ: إِنَّ الْإِنْسانَ لَيَطْغى (٦) أَنْ رَآهُ اسْتَغْنى [العلق: ٦- ٧] وقال: إن الجوع سر من أسرار الله تعالى في الأرض لا يودعه عند من يذيعه.
_________
(١) في البرهان ١/ ١٧٤ والإتقان ١/ ٢٤ أن ابن عباس فسر هذه الحروف بأنها: (أنا الله أفصل) .
(٢) في الإتقان ١/ ٢٥ أن ابن عباس قال: (المص: الألف من الله، والميم من الرحمن، والصاد من الصمد) .
(٣) الحلية ١٠/ ٢٠٣.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (63,'<span data-type="title" id=toc-124>[سورة الأعراف (٧) : آية ٣٣]</span>
قُلْ إِنَّما حَرَّمَ رَبِّيَ الْفَواحِشَ مَا ظَهَرَ مِنْها وَما بَطَنَ وَالْإِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَأَنْ تُشْرِكُوا بِاللَّهِ ما لَمْ يُنَزِّلْ بِهِ سُلْطاناً وَأَنْ تَقُولُوا عَلَى اللَّهِ ما لا تَعْلَمُونَ (٣٣)
وقوله تعالى: قُلْ إِنَّما حَرَّمَ رَبِّيَ الْفَواحِشَ مَا ظَهَرَ مِنْها وَما بَطَنَ [٣٣] قال: يعني الحسد بقلبه والفعل بجوارحه، ولو أن يترك التدبير فيهما كان من أوتاد الأرض، ولكن العبد بين حالين، إما أن يدبر بقلبه ما لا يعنيه، أو يعمل بجوارحه ما لا يعنيه، ليس ينجو من أحدهما إلاَّ بعصمة الله تعالى، فعيش القلوب اليقين وظلمتها التدبير. قال: وكنا مع سهل عند غروب الشمس فقال لأحمد بن سالم «١» : اترك الحيل حتى نصلي العشاء بمكة.
وقوله تعالى: وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لاَ تَعْلَمُونَ [٣٣] قال: من تكلم عن الله من غير إذن، وعلى سبيل الحرمة وحفظ الأدب، فقد هتك الستر، وقد منع الله تعالى أن يقول عليه أحد ما لم يعلم.

<span data-type="title" id=toc-125>[سورة الأعراف (٧) : آية ٤٣]</span>
وَنَزَعْنا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهارُ وَقالُوا الْحَمْدُ لِلَّهِ الَّذِي هَدانا لِهذا وَما كُنَّا لِنَهْتَدِيَ لَوْلا أَنْ هَدانَا اللَّهُ لَقَدْ جاءَتْ رُسُلُ رَبِّنا بِالْحَقِّ وَنُودُوا أَنْ تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوها بِما كُنْتُمْ تَعْمَلُونَ (٤٣)
وقوله تعالى: وَنَزَعْنا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ [٤٣] قال: هو الأهواء والبدع.

<span data-type="title" id=toc-126>[سورة الأعراف (٧) : آية ٤٦]</span>
وَبَيْنَهُما حِجابٌ وَعَلَى الْأَعْرافِ رِجالٌ يَعْرِفُونَ كُلاًّ بِسِيماهُمْ وَنادَوْا أَصْحابَ الْجَنَّةِ أَنْ سَلامٌ عَلَيْكُمْ لَمْ يَدْخُلُوها وَهُمْ يَطْمَعُونَ (٤٦)
وقوله تعالى: وَعَلَى الْأَعْرافِ رِجالٌ [٤٦] قال: أصحاب الأعراف هم أهل المعرفة.

<span data-type="title" id=toc-127>[سورة الأعراف (٧) : آية ٤٨]</span>
وَنادى أَصْحابُ الْأَعْرافِ رِجالاً يَعْرِفُونَهُمْ بِسِيماهُمْ قالُوا ما أَغْنى عَنْكُمْ جَمْعُكُمْ وَما كُنْتُمْ تَسْتَكْبِرُونَ (٤٨)
قال الله تعالى: يَعْرِفُونَهُمْ بِسِيماهُمْ [٤٨] إقامتهم لشرفهم في الدارين وأهلهما، يعرفهم الملكان كما أشرفهم على أسرار العباد في الدنيا وأحوالهم.

<span data-type="title" id=toc-128>[سورة الأعراف (٧) : آية ٥٦]</span>
وَلا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلاحِها وَادْعُوهُ خَوْفاً وَطَمَعاً إِنَّ رَحْمَتَ اللَّهِ قَرِيبٌ مِنَ الْمُحْسِنِينَ (٥٦)
وقوله تعالى: وَلا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلاحِها [٥٦، ٨٥] قال: أن لا تفسدوا الطاعة بالمعصية، وذلك أن من كان مقيماً على المعصية على أدنى منهيّ فجميع حسناته ممزوجة بتلك المعصية، ولا تخلص له حسناته البتة وهو مقيم على سيئة واحدة حتى يتوب وينخلع عن ذلك المنهي، ويصفيها عن كدورات المعاصي في السر والعلانية.

<span data-type="title" id=toc-129>[سورة الأعراف (٧) : آية ٦٨]</span>
أُبَلِّغُكُمْ رِسالاتِ رَبِّي وَأَنَا لَكُمْ ناصِحٌ أَمِينٌ (٦٨)
وقوله تعالى: وَأَنَا لَكُمْ ناصِحٌ أَمِينٌ [٦٨] ومن لم ينصح الله في نفسه ولم ينصحه في خلقه هلك، ونصيحة الخلق أشد من النفس، وأدنى نصيحة النفس الشكر، وهو أن لا يعصى الله تعالى بنعمه. وسمعته مرة أخرى يقول: النصيحة أن لا تدخل في شيء لا تملك صلاحه.

<span data-type="title" id=toc-130>[سورة الأعراف (٧) : الآيات ٩٤ الى ٩٥]</span>
وَما أَرْسَلْنا فِي قَرْيَةٍ مِنْ نَبِيٍّ إِلاَّ أَخَذْنا أَهْلَها بِالْبَأْساءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَضَّرَّعُونَ (٩٤) ثُمَّ بَدَّلْنا مَكانَ السَّيِّئَةِ الْحَسَنَةَ حَتَّى عَفَوْا وَقالُوا قَدْ مَسَّ آباءَنَا الضَّرَّاءُ وَالسَّرَّاءُ فَأَخَذْناهُمْ بَغْتَةً وَهُمْ لا يَشْعُرُونَ (٩٥)
وقوله تعالى: وَما أَرْسَلْنا فِي قَرْيَةٍ مِنْ نَبِيٍّ إِلَّا أَخَذْنا أَهْلَها بِالْبَأْساءِ وَالضَّرَّاءِ [٩٤] قال: يعني فقد قلوبهم بالجهل عن العلم والشدة في دنياهم حتى اشتغلوا بها عن آخرتهم ثُمَّ بَدَّلْنا مَكانَ السَّيِّئَةِ الْحَسَنَةَ حَتَّى عَفَوْا [٩٥] أي كثروا ليس هو العفو بعينه، قال تعالى:
خُذِ الْعَفْوَ [١٩٩] أي الفضل في أموالهم التي هي وديعة الله عندهم، لأن الله تعالى قد ابتاعها منهم، فليس له نفس ولا مال. قيل له: فأين نفسه؟ قال: دخلت تحت مبايعة الله تعالى.
قال: إِنَّ اللَّهَ اشْتَرى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ [التوبة: ١١١]

<span data-type="title" id=toc-131>[سورة الأعراف (٧) : آية ٩٩]</span>
أَفَأَمِنُوا مَكْرَ اللَّهِ فَلا يَأْمَنُ مَكْرَ اللَّهِ إِلاَّ الْقَوْمُ الْخاسِرُونَ (٩٩)
وقوله تعالى: أَفَأَمِنُوا مَكْرَ اللَّهِ [٩٩] قال: المكر المضاف إلى تدبيره في سابق علمه من قدرته
_________
(١) أحمد بن سالم، أبو الحسن: شيخ الصوفية في عصره، كان من أصحاب سهل التستري. (سير أعلام النبلاء ١٦/ ٢٧٢) .',NULL,'66',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (64,'فلا ينبغي لأحد أن يأمن مكره، لأن أمن المكر لا يدفع القدر، ولا يخرج أحداً عن قدرة الله تعالى، ولا يخلو أحد من خوف وإن بلغ كل خوف، وإذا عرف منزلته عند الله تعالى ازداد علمه وتكاملت رغبته، فأما من لم يعرف منزلته فذلك عار عليه. قال عمر بن واصل: فقلت له: كيف يزداد مع علمه منزلة؟ فقال: هما رجلان، فرجل ازداد وطلب الزيادة وحرص لذلك، ورجل أضعف منه، كان ذلك منه شكراً لئلا يسلب ما أعطاه.

<span data-type="title" id=toc-132>[سورة الأعراف (٧) : آية ١٢٨]</span>
قالَ مُوسى لِقَوْمِهِ اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا إِنَّ الْأَرْضَ لِلَّهِ يُورِثُها مَنْ يَشاءُ مِنْ عِبادِهِ وَالْعاقِبَةُ لِلْمُتَّقِينَ (١٢٨)
وقوله تعالى: قالَ مُوسى لِقَوْمِهِ اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا [١٢٨] قال: أمرهم أن يستعينوا بالله على أمر الله، فيقهروا ما فيها ويستولوا عليها وعلى مخالفتها، وأن يصبروا على ذلك تأدباً.

<span data-type="title" id=toc-133>[سورة الأعراف (٧) : آية ١٤٦]</span>
سَأَصْرِفُ عَنْ آياتِيَ الَّذِينَ يَتَكَبَّرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَإِنْ يَرَوْا كُلَّ آيَةٍ لا يُؤْمِنُوا بِها وَإِنْ يَرَوْا سَبِيلَ الرُّشْدِ لا يَتَّخِذُوهُ سَبِيلاً وَإِنْ يَرَوْا سَبِيلَ الغَيِّ يَتَّخِذُوهُ سَبِيلاً ذلِكَ بِأَنَّهُمْ كَذَّبُوا بِآياتِنا وَكانُوا عَنْها غافِلِينَ (١٤٦)
قوله: سَأَصْرِفُ عَنْ آياتِيَ الَّذِينَ يَتَكَبَّرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ [١٤٦] قال: هو أن يحرمهم فهم القرآن «١» ، والاقتداء بالرسول عليه الصلاة والسلام. وروي عن النبي صلى الله عليه وسلّم أنه قال: «من أعطي فهم القرآن فقد أعطي الخير الكثير، ومن فاته فهم القرآن فقد فاته علم عظيم» . وقال النبي صلّى الله عليه وسلّم: «من تعظيم الله إكرام ذي الشيبة في الإسلام، وإكرام الإمام العادل، وإكرام حامل القرآن غير الغالي فيه» «٢» .
قوله: وَإِنْ يَرَوْا سَبِيلَ الغَيِّ يَتَّخِذُوهُ سَبِيلًا [١٤٦] قال: ردهم إلى سابق علمه فيهم أنهم سيفعلون ذلك لخذلانه إياهم بمادلتهم عليه أنفسهم الطبيعية من الحركة في النهي، والسكون في الأمر، وادعاء الحول والقوة على ما جبلت عليه أنفسهم، والاغترار به.

<span data-type="title" id=toc-134>[سورة الأعراف (٧) : الآيات ١٤٨ الى ١٤٩]</span>
وَاتَّخَذَ قَوْمُ مُوسى مِنْ بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلاً جَسَداً لَهُ خُوارٌ أَلَمْ يَرَوْا أَنَّهُ لا يُكَلِّمُهُمْ وَلا يَهْدِيهِمْ سَبِيلاً اتَّخَذُوهُ وَكانُوا ظالِمِينَ (١٤٨) وَلَمَّا سُقِطَ فِي أَيْدِيهِمْ وَرَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قالُوا لَئِنْ لَمْ يَرْحَمْنا رَبُّنا وَيَغْفِرْ لَنا لَنَكُونَنَّ مِنَ الْخاسِرِينَ (١٤٩)
قوله تعالى: وَاتَّخَذَ قَوْمُ مُوسى مِنْ بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلًا جَسَداً لَهُ خُوارٌ [١٤٨] قال: عجل كل إنسان ما أقبل عليه، فأعرض به عن الله من أهل وولد، ولا يتخلص من ذلك إلاَّ بعد إفناء جميع حظوظه من أسبابه، كما لم يتخلص عبدة العجل من عبادته إلاَّ بعد قتل النفوس.
قوله تعالى: وَلَمَّا سُقِطَ فِي أَيْدِيهِمْ [١٤٩] قال: يعني ندموا، يقال: سُقِطَ الرجل في يديه إذا ندم على أمر.

<span data-type="title" id=toc-135>[سورة الأعراف (٧) : آية ١٥٦]</span>
وَاكْتُبْ لَنا فِي هذِهِ الدُّنْيا حَسَنَةً وَفِي الْآخِرَةِ إِنَّا هُدْنا إِلَيْكَ قالَ عَذابِي أُصِيبُ بِهِ مَنْ أَشاءُ وَرَحْمَتِي وَسِعَتْ كُلَّ شَيْءٍ فَسَأَكْتُبُها لِلَّذِينَ يَتَّقُونَ وَيُؤْتُونَ الزَّكاةَ وَالَّذِينَ هُمْ بِآياتِنا يُؤْمِنُونَ (١٥٦)
قوله تعالى: إِنَّا هُدْنا إِلَيْكَ [١٥٦] أي تبنا إليك.

<span data-type="title" id=toc-136>[سورة الأعراف (٧) : آية ١٦٣]</span>
وَسْئَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كانَتْ حاضِرَةَ الْبَحْرِ إِذْ يَعْدُونَ فِي السَّبْتِ إِذْ تَأْتِيهِمْ حِيتانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعاً وَيَوْمَ لا يَسْبِتُونَ لا تَأْتِيهِمْ كَذلِكَ نَبْلُوهُمْ بِما كانُوا يَفْسُقُونَ (١٦٣)
قوله تعالى: إِذْ يَعْدُونَ فِي السَّبْتِ [١٦٣] قال: يعدون في اتباع الهوى في السبت.

<span data-type="title" id=toc-137>[سورة الأعراف (٧) : آية ١٦٩]</span>
فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتابَ يَأْخُذُونَ عَرَضَ هذَا الْأَدْنى وَيَقُولُونَ سَيُغْفَرُ لَنا وَإِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُ أَلَمْ يُؤْخَذْ عَلَيْهِمْ مِيثاقُ الْكِتابِ أَنْ لا يَقُولُوا عَلَى اللَّهِ إِلاَّ الْحَقَّ وَدَرَسُوا ما فِيهِ وَالدَّارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ أَفَلا تَعْقِلُونَ (١٦٩)
قوله تعالى: وَدَرَسُوا مَا فِيهِ [١٦٩] أي تركوا العمل به.

<span data-type="title" id=toc-138>[سورة الأعراف (٧) : الآيات ١٧١ الى ١٧٢]</span>
وَإِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَأَنَّهُ ظُلَّةٌ وَظَنُّوا أَنَّهُ واقِعٌ بِهِمْ خُذُوا ما آتَيْناكُمْ بِقُوَّةٍ وَاذْكُرُوا ما فِيهِ لَعَلَّكُمْ تَتَّقُونَ (١٧١) وَإِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَأَشْهَدَهُمْ عَلى أَنْفُسِهِمْ أَلَسْتُ بِرَبِّكُمْ قالُوا بَلى شَهِدْنا أَنْ تَقُولُوا يَوْمَ الْقِيامَةِ إِنَّا كُنَّا عَنْ هذا غافِلِينَ (١٧٢)
وقوله: وَإِذْ نَتَقْنَا الْجَبَلَ [١٧١] قال: يعني فتقنا وقد زعزعنا. كما قال العجاج «٣» :
_________
(١) هذا قول سفيان بن عيينة في الإتقان ٤/ ٢١٦.
(٢) كشف الخفاء ١/ ٢٨٤.
(٣) العجاج: عبد الله بن رؤبة بن لبيد بن صخر السعدي التميمي ( ... - نحو ٩٠ هـ) : راجز مجيد، من الشعراء. ولد في الجاهلية، وقال الشعر فيها، ثم أسلم. (الأعلام ٤/ ٨٦) .',NULL,'67',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (65,'[من الرجز]
قد ربَّبوا أحلامَنا الجلائلا ... وفتقوا أحلامنا الأثاقلا «١»
وقوله تعالى: وَإِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَأَشْهَدَهُمْ عَلى أَنْفُسِهِمْ [١٧٢] قال: إن الله تعالى أخذ الأنبياء من ظهر آدم عليه الصلاة والسلام، ثم أخذ من ظهر كل نبي ذريته كهيئة الذر، لهم عقول، فأخذ من الأنبياء ميثاقهم، كما قال: وَإِذْ أَخَذْنا مِنَ النَّبِيِّينَ مِيثاقَهُمْ وَمِنْكَ وَمِنْ نُوحٍ [الأحزاب: ٧] وكان الميثاق عليهم أن يبلغوا عن الله تعالى أمره ونهيه، ثم دعاهم جميعاً إلى الإقرار بربوبيته لقوله تعالى: أَلَسْتُ بِرَبِّكُمْ [١٧٢] وأظهر قدرته حتى قالُوا بَلى [١٧٢] ، فجمع الله مراده من خلقه، وما هم عليه من الابتداء والانتهاء في قولهم:
«بلى» ، إذ هو على جهة الابتلاء، وقد قال الله تعالى: وَكانَ عَرْشُهُ عَلَى الْماءِ لِيَبْلُوَكُمْ [هود: ٧] وأشهد الأنبياء عليهم حجة كما قال: وَأَشْهَدَهُمْ عَلى أَنْفُسِهِمْ [١٧٢] ثم أعادهم في صلب آدم عليه السلام، ثم بعث الأنبياء ليذكرهم عهده وميثاقه، وكان في علمه يوم أقروا بما أقروا به من يكذب به ومن يصدق به، فلا تقوم الساعة حتى تخرج كل نسمة قد أخذ الميثاق عليها، ثم تقوم الساعة. فقيل: ما علامة السعادة والشقاوة؟ قال: إن من علامات الشقاوة إنكار القدرة، وإن من علامة السعادة أن تكون واسع القلب بالإيمان، وأن ترزق الغنى في القلب والعصمة في الطاعة والتوفيق في الزهد، ومن ألهم الأدب فيما بينه وبين الله تعالى طهر قلبه ويرزق السعادة، وليس شيء أضيق من حفظ الأدب. فقيل له ما الأدب؟ قال: اجعلوا طعامكم الشعير، وحلواكم التمر، وإدامكم الملح، ودسمكم اللَبن، ولباسكم الصوف، وبيوتكم المساجد، وضياءكم الشمس، وسراجكم القمر، وطيبكم الماء، وبهاكم النظافة، وزينتكم الحذر، وعملكم الارتضاء، أو قال: الرضا، وزادكم التقوى، وأكلكم بالليل، ونومكم بالنهار، وكلامكم الذكر، وصمتكم وهمتكم التفكر، ونظركم العبرة، وملجأكم وناصركم مولاكم، واصبروا عليه إلى الممات «٢» .
وقال: ثلاث من علامات الشقاوة: أن تفوته الجماعة وهو بقرب من المسجد، وأن تفوته الجماعة وهو في المدينة، وأن يفوته الحج وهو بمكة. قال سهل: والذرية ثلاث: أول وثاني وثالث:
فالأول: محمد صلّى الله عليه وسلّم، لأن الله تعالى لما أراد أن يخلق محمداً صلّى الله عليه وسلّم أظهر من نوره نوراً، فلما بلغ حجاب العظمة سجد لله سجدة، فخلق سبحانه من سجدته عموداً عظيما كالزجاج من
_________
(١) الرجز ليس للعجاج، وهو بلا نسبة في لسان العرب (نتق) .
(٢) كتاب الزهد الكبير ٢/ ٣٥٦.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (66,'النور، أي باطنه وظاهره فيه عين محمد صلّى الله عليه وسلّم، فوقف بين يدي رب العالمين بالخدمة ألف ألف عام بطبائع الإيمان، وهو معاينة الإيمان ومكاشفة اليقين ومشاهدة الرب، فأكرمه الله تعالى بالمشاهدة قبل بدء الخلق بألف ألف عام. وما من أحد في الدنيا إلاَّ غلبه إبليس لعنه الله فأسره، إلاَّ الأنبياء صلوات الله عليهم، والصديقون الذين شاهدت قلوبهم إيمانهم في مقاماتهم، وعرفوا اطلاع الله عليهم في جميع أحوالهم، فعلى قدر مشاهدتهم يعرفون الابتلاء، وعلى قدر معرفتهم الابتلاء يطلبون العصمة، وعلى قدر فقرهم وفاقتهم إليه يعرفون الضر والنفع، ويزدادون علماً وفهماً ونظراً. ثم قال: ما حمل الله على أحد من الأنبياء ما حمل على نبينا محمد صلّى الله عليه وسلّم من الخدمة، وما من مقام خدمة خدم الله تعالى بها من ولد آدم عليه السلام إلى أن بعث نبينا صلّى الله عليه وسلّم، إلاَّ وقد خدم الله بها نبينا صلّى الله عليه وسلّم. وقد سئل عن معنى قوله صلّى الله عليه وسلّم: «إني لست كأحدكم إن ربي يطعمني ويسقيني» «١» فقال: ما كان معه طعام ولا شراب، ولكنه كان يذكر خصوصيته عند الله تعالى، فيكون كمن أكل الطعام وشرب الشراب، ولو كان معه شراب أو طعام لآثر أهله وأهل الصفة على نفسه.
الثاني: آدم صلوات الله عليه، خلقه من نور، قال عليه السلام: «وخلق محمداً صلّى الله عليه وسلّم، يعني جسده، من طين آدم عليه السلام» .
والثالث: ذرية آدم. وإن الله عزَّ وجلَّ خلق المريدين من نور آدم، وخلق المرادين من نور محمد صلّى الله عليه وسلّم، فالعامة من الخلق يعيشون في رحمة أهل القرب، وأهل القرب يعيشون في رحمة المقرب، يَسْعى نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمانِهِمْ [الحديد: ١٢] .

<span data-type="title" id=toc-139>[سورة الأعراف (٧) : آية ١٧٦]</span>
وَلَوْ شِئْنا لَرَفَعْناهُ بِها وَلكِنَّهُ أَخْلَدَ إِلَى الْأَرْضِ وَاتَّبَعَ هَواهُ فَمَثَلُهُ كَمَثَلِ الْكَلْبِ إِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَثْ ذلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآياتِنا فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ (١٧٦)
وقوله تعالى: وَلَوْ شِئْنا لَرَفَعْناهُ بِها [١٧٦] يعني بلعام بن باعوراء، وَلكِنَّهُ أَخْلَدَ إِلَى الْأَرْضِ وَاتَّبَعَ هَواهُ [١٧٦] وأعرض لمتابعة هواه، وأن الله تعالى قسم الأعضاء في الهوى لكل عضو حظاً منه، فإذا مال عضو من أعضائه إلى الهوى يرجع ضره إلى القلب. واعلموا أن للنفس سراً ما ظهر ذلك السر على أحد من خلقه إلاَّ على فرعون فَقالَ أَنَا رَبُّكُمُ الْأَعْلى [النازعات: ٢٤] . فقال: كيف نسلم من الهوى؟ فقال: من ألزم نفسه الأدب سلم منه، فإنه من قهر نفسه بالأدب عبد الله عزَّ وجلَّ بالإخلاص. قال «٢» : وللنفس سبع حجب سماوية، وسبع حجب أرضية، فكلما يدفن العبد نفسه أرضاً سما قلبه سماء، فإذا دفن النفس تحت الثرى وصل القلب
_________
(١) صحيح البخاري: كتاب الصوم، رقم ١٨٦٠- ١٨٦٦ وكتاب الاعتصام بالكتاب والسنة، رقم ٦٨٦٩ ومسند أحمد ٢/ ٤١٨.
(٢) الحلية ١٠/ ٢٠٨.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (67,'إلى العرش. وقد حكي عن كهمس «١» أنه كان يصلي كل يوم وليلة ألف ركعة، وكان يسلم بين كل ركعتين، ثم يقول لنفسه: قومي يا مأوى كل شر ما رضيت عنك «٢» .

<span data-type="title" id=toc-140>[سورة الأعراف (٧) : آية ١٨٠]</span>
وَلِلَّهِ الْأَسْماءُ الْحُسْنى فَادْعُوهُ بِها وَذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمائِهِ سَيُجْزَوْنَ ما كانُوا يَعْمَلُونَ (١٨٠)
قوله تعالى: وَلِلَّهِ الْأَسْماءُ الْحُسْنى فَادْعُوهُ بِها [١٨٠] قال: إن وراء الأسامي والصفات صفات لا تخرقها الأفهام، لأن الحق نار يتضرم لا سبيل إليه، ولا بد من الاقتحام فيه.
وقوله: وَذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمائِهِ [١٨٠] يعني يجورون في أسمائه يكذبون.

<span data-type="title" id=toc-141>[سورة الأعراف (٧) : آية ١٨٢]</span>
وَالَّذِينَ كَذَّبُوا بِآياتِنا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لا يَعْلَمُونَ (١٨٢)
وقوله: وَالَّذِينَ كَذَّبُوا بِآياتِنا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لاَ يَعْلَمُونَ [١٨٢] قال: يعني نمدهم بالنعم وننسيهم الشكر عليها، فإذا سكنوا وحجبوا عن المنعم أخذوا.

<span data-type="title" id=toc-142>[سورة الأعراف (٧) : آية ١٨٥]</span>
أَوَلَمْ يَنْظُرُوا فِي مَلَكُوتِ السَّماواتِ وَالْأَرْضِ وَما خَلَقَ اللَّهُ مِنْ شَيْءٍ وَأَنْ عَسى أَنْ يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ (١٨٥)
وقوله: أَوَلَمْ يَنْظُرُوا فِي مَلَكُوتِ السَّماواتِ وَالْأَرْضِ [١٨٥] قال: ذكر الله تعالى قدرته في خلقه ووصف حاجتهم إليه، وما خلق من شيء سمعوه ولم يروه، فاغتروا به، ولو شاهدوا ذلك بقلوبهم لآمنوا بالغيب، فأداهم الإيمان إلى مشاهدة الغيب الذي غاب عنهم، وورثوا درجات الأبرار فصاروا أعلاما للهدى.

<span data-type="title" id=toc-143>[سورة الأعراف (٧) : الآيات ١٨٧ الى ١٨٨]</span>
يَسْئَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْساها قُلْ إِنَّما عِلْمُها عِنْدَ رَبِّي لاَ يُجَلِّيها لِوَقْتِها إِلاَّ هُوَ ثَقُلَتْ فِي السَّماواتِ وَالْأَرْضِ لا تَأْتِيكُمْ إِلاَّ بَغْتَةً يَسْئَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْها قُلْ إِنَّما عِلْمُها عِنْدَ اللَّهِ وَلكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ (١٨٧) قُلْ لاَّ أَمْلِكُ لِنَفْسِي نَفْعاً وَلا ضَرًّا إِلاَّ ما شاءَ اللَّهُ وَلَوْ كُنْتُ أَعْلَمُ الْغَيْبَ لاسْتَكْثَرْتُ مِنَ الْخَيْرِ وَما مَسَّنِيَ السُّوءُ إِنْ أَنَا إِلاَّ نَذِيرٌ وَبَشِيرٌ لِقَوْمٍ يُؤْمِنُونَ (١٨٨)
وقوله: لاَ يُجَلِّيها لِوَقْتِها إِلَّا هُوَ [١٨٧] يعني لا يجلي نفس الطبع من الهوى إلى طاعته، إلاَّ هو. هذا باطن الآية. قوله: يَسْئَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْها [١٨٧] أي عالم بوقتها. قوله: قُلْ لاَّ أَمْلِكُ لِنَفْسِي نَفْعاً وَلا ضَرًّا إِلَّا مَا شاءَ اللَّهُ [١٨٨] فكيف ينفع غيره من لم يملك نفعه، وإنما ذلك إلى الله تعالى.

<span data-type="title" id=toc-144>[سورة الأعراف (٧) : آية ١٩٨]</span>
وَإِنْ تَدْعُوهُمْ إِلَى الْهُدى لا يَسْمَعُوا وَتَراهُمْ يَنْظُرُونَ إِلَيْكَ وَهُمْ لا يُبْصِرُونَ (١٩٨)
وقوله: وَتَراهُمْ يَنْظُرُونَ إِلَيْكَ وَهُمْ لاَ يُبْصِرُونَ [١٩٨] قال: هي القلوب التي لم يزينها الله بأنواره والقربة، فهو أعمى عن درك الحقائق رؤية الأكابر.

<span data-type="title" id=toc-145>[سورة الأعراف (٧) : آية ٢٠٥]</span>
وَاذْكُرْ رَبَّكَ فِي نَفْسِكَ تَضَرُّعاً وَخِيفَةً وَدُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْآصالِ وَلا تَكُنْ مِنَ الْغافِلِينَ (٢٠٥)
وقوله: وَاذْكُرْ رَبَّكَ فِي نَفْسِكَ تَضَرُّعاً وَخِيفَةً [٢٠٥] ما حقيقة الذكر؟ قال: تحقيق العلم بأن الله تعالى مشاهدك، وتراه بقلبك قريباً منك، وتستحي منه ثم تؤثره على نفسك في أحوالك كلها، ثم قال: ليس من ادعى الذكر فهو ذاكر. فقيل له ما معنى قوله صلّى الله عليه وسلّم: «الدنيا ملعون ما فيها إلاَّ ذكر الله تعالى» «٣» قوله: «ذكر الله» هاهنا الزهد عن الحرام، وهو أن يستقبله حرام، فيذكر الله تعالى، ويعلم أنه مطلع عليه، فيجتنب ذلك الحرام. وقوله: وَلا تَكُنْ مِنَ الْغافِلِينَ [٢٠٥] قال سهل: حقاً أقول لكم ولا باطل، يقيناً ولا شك: ما من أحد ذهب منه نفس واحد في غير ذكر الله إلاَّ وهو غافل عن الله عزَّ وجلَّ. وقال: غفلة الخاص السكون إلى الشيء، وغفلة العام الافتخار بالشيء، يعني السكون، والله سبحانه وتعالى أعلم.
_________
(١) كهمس بن الحسن التميمي ( ... - ١٤٩ هـ) : من عباد أهل البصرة. كان أبر شيء بأمه، وبعد وفاتها أقام بمكة حتى مات. (الحلية ٦/ ٢١١) .
(٢) نسب هذا الخبر إلى عامر بن عبد قيس في الحلية ٢/ ٨٩ وإلى سعيد بن المسيب في فيض القدير ٤/ ٢٤٨.
(٣) نوادر الأصول ١/ ٢٥٥ وسنن الترمذي ٢٣٢٢ وسنن ابن ماجة ٤١١٢. [.....]',NULL,'70',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (68,'<span data-type="title" id=toc-146>السورة التي يذكر فيها الأنفال</span>

<span data-type="title" id=toc-147>[سورة الأنفال (٨) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
يَسْئَلُونَكَ عَنِ الْأَنْفالِ قُلِ الْأَنْفالُ لِلَّهِ وَالرَّسُولِ فَاتَّقُوا اللَّهَ وَأَصْلِحُوا ذاتَ بَيْنِكُمْ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ إِنْ كُنْتُمْ مُؤْمِنِينَ (١) إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَإِذا تُلِيَتْ عَلَيْهِمْ آياتُهُ زادَتْهُمْ إِيماناً وَعَلى رَبِّهِمْ يَتَوَكَّلُونَ (٢)
قوله تعالى: فَاتَّقُوا اللَّهَ وَأَصْلِحُوا ذاتَ بَيْنِكُمْ [١] قال: التقوى ترك كل شيء تقع عليه فهو في الآداب مكارم الأخلاق وفي الترغيب أن لا يظهر ما في سره، وفي الترهيب أن لا يقف مع الجهل. ولا تصح التقوى إلاَّ بالمقتدي بالنبي صلّى الله عليه وسلّم وبالصحابة.
قوله: إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ [٢] قال: هاجت من خشية الفراق، فخشعت الجوارح لله بالخدمة.

<span data-type="title" id=toc-148>[سورة الأنفال (٨) : آية ١١]</span>
إِذْ يُغَشِّيكُمُ النُّعاسَ أَمَنَةً مِنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِنَ السَّماءِ ماءً لِيُطَهِّرَكُمْ بِهِ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطانِ وَلِيَرْبِطَ عَلى قُلُوبِكُمْ وَيُثَبِّتَ بِهِ الْأَقْدامَ (١١)
وقوله تعالى: إِذْ يُغَشِّيكُمُ النُّعاسَ أَمَنَةً مِنْهُ [١١] قال: النعاس ينزل من الدماغ والقلب حي، والنوم على القلب من الظاهر وهو حكم النوم، وحكم النعاس حكم الروح.

<span data-type="title" id=toc-149>[سورة الأنفال (٨) : آية ١٩]</span>
إِنْ تَسْتَفْتِحُوا فَقَدْ جاءَكُمُ الْفَتْحُ وَإِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْ وَإِنْ تَعُودُوا نَعُدْ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْئاً وَلَوْ كَثُرَتْ وَأَنَّ اللَّهَ مَعَ الْمُؤْمِنِينَ (١٩)
وقوله: إِنْ تَسْتَفْتِحُوا فَقَدْ جاءَكُمُ الْفَتْحُ [١٩] وذلك أن أبا جهل قال يوم بدر:
اللهم انصر أفضل الدينين عندك، وأرضاهما لديك، فنزل: إِنْ تَسْتَفْتِحُوا [١٩] يعني تستنصرون «١» . وقد روي عن النبي صلى الله عليه وسلّم أنه كان يستفتح بصعاليك المهاجرين أي يستنصر بفقرائهم «٢» .

<span data-type="title" id=toc-150>[سورة الأنفال (٨) : آية ٢٣]</span>
وَلَوْ عَلِمَ اللَّهُ فِيهِمْ خَيْراً لَأَسْمَعَهُمْ وَلَوْ أَسْمَعَهُمْ لَتَوَلَّوْا وَهُمْ مُعْرِضُونَ (٢٣)
وقوله: وَلَوْ عَلِمَ اللَّهُ فِيهِمْ خَيْراً لَأَسْمَعَهُمْ [٢٣] أي لفتح أقفال قلوبهم بالإيمان.

<span data-type="title" id=toc-151>[سورة الأنفال (٨) : آية ٢٩]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَتَّقُوا اللَّهَ يَجْعَلْ لَكُمْ فُرْقاناً وَيُكَفِّرْ عَنْكُمْ سَيِّئاتِكُمْ وَيَغْفِرْ لَكُمْ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ (٢٩)
وقوله: إِنْ تَتَّقُوا اللَّهَ يَجْعَلْ لَكُمْ فُرْقاناً [٢٩] أي نوراً في الدين من الشبهة بين الحق والباطل.

<span data-type="title" id=toc-152>[سورة الأنفال (٨) : آية ٣٧]</span>
لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلى بَعْضٍ فَيَرْكُمَهُ جَمِيعاً فَيَجْعَلَهُ فِي جَهَنَّمَ أُولئِكَ هُمُ الْخاسِرُونَ (٣٧)
وقوله: لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ [٣٧] قال: الخبيث على ضروب: الكفر والنفاق والكبائر، والطيب على ضروب: وهو الإيمان، فيه درجة الأنبياء والصديقين والشهداء والصالحين، فأخبر الله تعالى أنه يميز بينهما، ثم يجعل الخبيث بعضه على بعض على مقدار ذنوبهم طبقة طبقة، كما قال: إِنَّ الْمُنافِقِينَ فِي الدَّرْكِ الْأَسْفَلِ مِنَ النَّارِ [النساء: ١٤٥] .
_________
(١) في السيرة النبوية ٣/ ١٧٦: (قال أبو جهل بن هشام: اللهم أقطعنا للرحم وآتانا بما لا يعرف، فأحنه الغداة.
فكان هو المستفتح) .
(٢) مسند أحمد ٣/ ٩٦ والمعجم الأوسط ٣/ ٣٤٨ وشعب الإيمان ٧/ ٣٣٦.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (69,'<span data-type="title" id=toc-153>[سورة الأنفال (٨) : آية ٤٦]</span>
وَأَطِيعُوا اللَّهَ وَرَسُولَهُ وَلا تَنازَعُوا فَتَفْشَلُوا وَتَذْهَبَ رِيحُكُمْ وَاصْبِرُوا إِنَّ اللَّهَ مَعَ الصَّابِرِينَ (٤٦)
وقوله: وَتَذْهَبَ رِيحُكُمْ [٤٦] أي دولتكم.

<span data-type="title" id=toc-154>[سورة الأنفال (٨) : آية ٤٨]</span>
وَإِذْ زَيَّنَ لَهُمُ الشَّيْطانُ أَعْمالَهُمْ وَقالَ لا غالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَإِنِّي جارٌ لَكُمْ فَلَمَّا تَراءَتِ الْفِئَتانِ نَكَصَ عَلى عَقِبَيْهِ وَقالَ إِنِّي بَرِيءٌ مِنْكُمْ إِنِّي أَرى ما لا تَرَوْنَ إِنِّي أَخافُ اللَّهَ وَاللَّهُ شَدِيدُ الْعِقابِ (٤٨)
وقوله: نَكَصَ عَلى عَقِبَيْهِ [٤٨] من حيث جاء.

<span data-type="title" id=toc-155>[سورة الأنفال (٨) : آية ٥٣]</span>
ذلِكَ بِأَنَّ اللَّهَ لَمْ يَكُ مُغَيِّراً نِعْمَةً أَنْعَمَها عَلى قَوْمٍ حَتَّى يُغَيِّرُوا ما بِأَنْفُسِهِمْ وَأَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ (٥٣)
وقوله: ذلِكَ بِأَنَّ اللَّهَ لَمْ يَكُ مُغَيِّراً نِعْمَةً أَنْعَمَها عَلى قَوْمٍ حَتَّى يُغَيِّرُوا مَا بِأَنْفُسِهِمْ [٥٣] قال: إن الله تعالى خص الأنبياء عليهم الصلاة والسلام وبعض الصديقين بمعرفة أنعم الله عليهم قبل زوالها، وحلم الله عنهم «١» .

<span data-type="title" id=toc-156>[سورة الأنفال (٨) : آية ٦٩]</span>
فَكُلُوا مِمَّا غَنِمْتُمْ حَلالاً طَيِّباً وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ (٦٩)
وقوله: فَكُلُوا مِمَّا غَنِمْتُمْ حَلالًا طَيِّباً [٦٩] قال: الحلال ما لا يعصى الله فيه، والطيب ما لا ينسى الله فيه.

<span data-type="title" id=toc-157>[سورة الأنفال (٨) : آية ٧٢]</span>
إِنَّ الَّذِينَ آمَنُوا وَهاجَرُوا وَجاهَدُوا بِأَمْوالِهِمْ وَأَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوْا وَنَصَرُوا أُولئِكَ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ وَالَّذِينَ آمَنُوا وَلَمْ يُهاجِرُوا ما لَكُمْ مِنْ وَلايَتِهِمْ مِنْ شَيْءٍ حَتَّى يُهاجِرُوا وَإِنِ اسْتَنْصَرُوكُمْ فِي الدِّينِ فَعَلَيْكُمُ النَّصْرُ إِلاَّ عَلى قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثاقٌ وَاللَّهُ بِما تَعْمَلُونَ بَصِيرٌ (٧٢)
وقوله: إِنَّ الَّذِينَ آمَنُوا وَهاجَرُوا وَجاهَدُوا بِأَمْوالِهِمْ وَأَنْفُسِهِمْ [٧٢] قال: جميع الطاعات لله جهاد النفس، وليس جهاد أسهل من جهاد السيف، ولا جهاد أشد من مخالفة النفس.
_________
(١) سيعاد هذا القول في تفسير الآية ١٣ من سورة الزخرف.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (70,'<span data-type="title" id=toc-158>السورة التي يذكر فيها التوبة</span>
قال سهل: أخبرني محمد بن سوار عن مالك بن دينار ومعروف بن علي عن الحسن عن محارب بن دثار «١» عن جابر بن عبد الله رضي الله عنهم قال: قال رسول الله صلّى الله عليه وسلّم لما أنزلت سورة براءة: «بعثت بمداراة الناس» «٢» .

<span data-type="title" id=toc-159>[سورة التوبة (٩) : آية ٢]</span>
فَسِيحُوا فِي الْأَرْضِ أَرْبَعَةَ أَشْهُرٍ وَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ وَأَنَّ اللَّهَ مُخْزِي الْكافِرِينَ (٢)
قوله تعالى: فَسِيحُوا فِي الْأَرْضِ [٢] يعني سيروا فيها اعتبارا، وبالله إقرارا.

<span data-type="title" id=toc-160>[سورة التوبة (٩) : آية ٨]</span>
كَيْفَ وَإِنْ يَظْهَرُوا عَلَيْكُمْ لا يَرْقُبُوا فِيكُمْ إِلاًّ وَلا ذِمَّةً يُرْضُونَكُمْ بِأَفْواهِهِمْ وَتَأْبى قُلُوبُهُمْ وَأَكْثَرُهُمْ فاسِقُونَ (٨)
وقوله تعالى: إِلًّا وَلا ذِمَّةً [١٠] قال: الإلّ هو القرابة، والذمة العهد.

<span data-type="title" id=toc-161>[سورة التوبة (٩) : آية ١٦]</span>
أَمْ حَسِبْتُمْ أَنْ تُتْرَكُوا وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جاهَدُوا مِنْكُمْ وَلَمْ يَتَّخِذُوا مِنْ دُونِ اللَّهِ وَلا رَسُولِهِ وَلا الْمُؤْمِنِينَ وَلِيجَةً وَاللَّهُ خَبِيرٌ بِما تَعْمَلُونَ (١٦)
قوله: وَلِيجَةً [١٦] قال عمر بن واصل العنبري: كل شيء أدخلته شيئاً وليس منه فهو وليجة. وقال سهل: يعني لم يغفلوا عنه بميل القلوب إلى أنفسهم.

<span data-type="title" id=toc-162>[سورة التوبة (٩) : آية ٢٩]</span>
قاتِلُوا الَّذِينَ لا يُؤْمِنُونَ بِاللَّهِ وَلا بِالْيَوْمِ الْآخِرِ وَلا يُحَرِّمُونَ ما حَرَّمَ اللَّهُ وَرَسُولُهُ وَلا يَدِينُونَ دِينَ الْحَقِّ مِنَ الَّذِينَ أُوتُوا الْكِتابَ حَتَّى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَهُمْ صاغِرُونَ (٢٩)
قوله: وَلا يَدِينُونَ دِينَ الْحَقِّ [٢٩] أي لا يطيعون، ومن كان في سلطان رجل فهو في دينه، كما قال الله تعالى: مَا كانَ لِيَأْخُذَ أَخاهُ فِي دِينِ الْمَلِكِ [يوسف: ٧٦] أي في سلطانه، كذلك إذا دخلت النفس في الإخلاص لله تعالى، كانت داخلةً في سلطان القلب والعقل ونفس الروح وطاعة البدن بالذكر لله تعالى.

<span data-type="title" id=toc-163>[سورة التوبة (٩) : آية ٣٢]</span>
يُرِيدُونَ أَنْ يُطْفِؤُا نُورَ اللَّهِ بِأَفْواهِهِمْ وَيَأْبَى اللَّهُ إِلاَّ أَنْ يُتِمَّ نُورَهُ وَلَوْ كَرِهَ الْكافِرُونَ (٣٢)
قوله تعالى: يُرِيدُونَ أَنْ يُطْفِؤُا نُورَ اللَّهِ بِأَفْواهِهِمْ [٣٢] يعني يريدون أن يردوا القرآن بتكذيبهم بألسنتهم، وَيَأْبَى اللَّهُ إِلَّا أَنْ يُتِمَّ نُورَهُ [٣٢] أي يظهر دينه الإسلام.

<span data-type="title" id=toc-164>[سورة التوبة (٩) : آية ٦٧]</span>
الْمُنافِقُونَ وَالْمُنافِقاتُ بَعْضُهُمْ مِنْ بَعْضٍ يَأْمُرُونَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوفِ وَيَقْبِضُونَ أَيْدِيَهُمْ نَسُوا اللَّهَ فَنَسِيَهُمْ إِنَّ الْمُنافِقِينَ هُمُ الْفاسِقُونَ (٦٧)
قوله عزَّ وجل: نَسُوا اللَّهَ فَنَسِيَهُمْ [٦٧] قال: يعني نسوا نعم الله عندهم، فأنساهم شكر النعم.

<span data-type="title" id=toc-165>[سورة التوبة (٩) : آية ٧١]</span>
وَالْمُؤْمِنُونَ وَالْمُؤْمِناتُ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكاةَ وَيُطِيعُونَ اللَّهَ وَرَسُولَهُ أُولئِكَ سَيَرْحَمُهُمُ اللَّهُ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ (٧١)
قوله: وَالْمُؤْمِنُونَ وَالْمُؤْمِناتُ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ [٧١] قال: موالاته مع المؤمنين كف الأذى عنهم. قال: واعلموا أن العبد لا يبلغ حقيقة الإيمان حتى يكون لعباد الله كالأرض، إذ هم عليها ومنافعهم منها. وقال «٣» : الأصول عندنا سبع: التمسك بكتاب الله، والاقتداء برسول الله صلّى الله عليه وسلّم، وأكل الحلال، وكف الأذى، واجتناب الآثام، والتوبة، وأداء الحقوق.
_________
(١) محارب بن دثار بن كردوس السدوسي الشيباني الكوفي ( ... - ١١٦ هـ) : قاضي الكوفة. كان فقيها فاضلا، حسن السيرة، زاهدا شجاعا، من أفرس الناس. وكان من المرجئة في علي وعثمان. (تهذيب التهذيب ١٠/ ٤٥- ٤٦) .
(٢) كشف الخفاء ١/ ٣٤١ وشعب الإيمان ٦/ ٣٥١.
(٣) الحلية ١٠/ ١٩٠ ومفتاح الجنة ٧٣.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (71,'<span data-type="title" id=toc-166>[سورة التوبة (٩) : آية ٧٣]</span>
يا أَيُّهَا النَّبِيُّ جاهِدِ الْكُفَّارَ وَالْمُنافِقِينَ وَاغْلُظْ عَلَيْهِمْ وَمَأْواهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيرُ (٧٣)
قوله: يا أَيُّهَا النَّبِيُّ جاهِدِ الْكُفَّارَ وَالْمُنافِقِينَ [٧٣] قال: جاهد نفسك بسيف المخالفة وحملها حمولات الندم، وسيرها في مفاوز الخوف، لعلك تردها إلى طريق التوبة والإنابة، ولا تصح التوبة إلا من متحير في أمره، مبهوت في شأنه، واله القلب مما جرى عليه، قال تعالى: حَتَّى إِذا ضاقَتْ عَلَيْهِمُ الْأَرْضُ بِما رَحُبَتْ [١١٨] الآية.

<span data-type="title" id=toc-167>[سورة التوبة (٩) : آية ١٠٨]</span>
لا تَقُمْ فِيهِ أَبَداً لَمَسْجِدٌ أُسِّسَ عَلَى التَّقْوى مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَنْ تَقُومَ فِيهِ فِيهِ رِجالٌ يُحِبُّونَ أَنْ يَتَطَهَّرُوا وَاللَّهُ يُحِبُّ الْمُطَّهِّرِينَ (١٠٨)
قوله: فِيهِ رِجالٌ يُحِبُّونَ أَنْ يَتَطَهَّرُوا [١٠٨] قال: هذه الطهارة أراد بها الذكر لله تعالى سراً وعلانية والطاعة له.

<span data-type="title" id=toc-168>[سورة التوبة (٩) : الآيات ١١١ الى ١١٢]</span>
إِنَّ اللَّهَ اشْتَرى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ يُقاتِلُونَ فِي سَبِيلِ اللَّهِ فَيَقْتُلُونَ وَيُقْتَلُونَ وَعْداً عَلَيْهِ حَقًّا فِي التَّوْراةِ وَالْإِنْجِيلِ وَالْقُرْآنِ وَمَنْ أَوْفى بِعَهْدِهِ مِنَ اللَّهِ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذِي بايَعْتُمْ بِهِ وَذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ (١١١) التَّائِبُونَ الْعابِدُونَ الْحامِدُونَ السَّائِحُونَ الرَّاكِعُونَ السَّاجِدُونَ الْآمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ الْمُنْكَرِ وَالْحافِظُونَ لِحُدُودِ اللَّهِ وَبَشِّرِ الْمُؤْمِنِينَ (١١٢)
قوله تعالى: إِنَّ اللَّهَ اشْتَرى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ [١١١] يعني اشتراها من شهوات الدنيا وما يوجب الاشتغال عن ذكره، حتى تكون نفسه وماله خالصة له، فمن لم يبع من الله حياته الفانية وشهواته الزائلة، كيف يعيش مع الله تعالى؟ وكيف يحيا حياة طيبة؟ ثم قال: إِنَّ اللَّهَ اشْتَرى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ [١١١] بما لا خير فيه، وعوضهم ما فيه الخير كله، مع أن ما في الكونين فهو ملكه، وهذا من غاية لطفه وكرمه بعباده المؤمنين. وقد حكي عن مالك بن دينار أنه مر بقصر يعمر، فسأل الأجراء عن أجرتهم، فأجابه كل واحد منهم بما كانت أجرته، ولم يجبه واحد، فقال: ما أجرتك؟ فقال: لا أجر لي. فقال:
ولم ذلك؟ قال: لأني عبد صاحب القصر. فقال مالك: إلهي ما أسخاك، الخلق كلهم عبيدك، كلفتهم العمل ووعدتهم الأجر.
قوله: التَّائِبُونَ الْعابِدُونَ [١١٢] قال سهل: ليس شيء في الدنيا من الحقوق أوجب على الخلق من التوبة، فهي واجبة في كل لمحة ولحظة، ولا عقوبة عليهم أشد من فقد علم التوبة. فقيل: ما التوبة؟ فقال: أن لا تنسى ذنبك «١» . وقال: أول ما يؤمر به المبتدئ التحويل من الحركات المذمومة إلى الحركات المحمودة، وهي التوبة ولا تصح له التوبة حتى يلزم نفسه الصمت، ولا يصح له الصمت حتى يلزم نفسه الخلوة، ولا تصح له الخلوة إلاَّ بأكل الحلال، ولا يصح له أكل الحلال إلاَّ بأداء حق الله تعالى، ولا يصح له أداء الحق إلاَّ بحفظ الجوارح والقلب، ولا يصح له ما وصفنا حتى يستعين بالله عزَّ وجلَّ على جميعه. فقيل: ما علامة صدق التوبة؟ قال علامتها أن يدع ما له سوى ما ليس له. وسئل سهل عن الرجل يتوب ويقلع عن ذلك الذنب، ثم يخطر ذلك بقلبه أو يراه أو يسمع به فيجد حلاوة ذلك الذنب السيئ كيف الحيلة فيه؟ فقال: وجدان الحلاوة من الطبع لا يتحول، فيصير المحبوب مكروهاً ولكن يقهر عزم القلب فيرجع في ذلك إلى الله عزَّ وجلَّ، ويرفع إليه شكواه، ويلزم نفسه وقلبه الإنكار ولا يفارقه، فإنه إن غفل عن الإنكار طرفة عين تخوفت عليه أن لا يسلم منه «٢» . قال: دعوا القال والقيل كله في هذا الزمان، عليكم بثلاث: توبوا إلى الله عزَّ وجلَّ مما تعرفوه بينكم وبينه، وأدوا مظالم العباد
_________
(١) قوت القلوب ١/ ٣١٨.
(٢) قوت القلوب ١/ ٣٢٤.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (72,'التي قبلكم، فإذا أصبحتم فلا تحدثوا أنفسكم بالمساء، وإذا أمسيتم فلا تحدثوا أنفسكم بالصباح، لأن الأحداث قد كثرت، والخطر عظيم، فاتقوا الله، وألزموا أنفسكم التوبة.

<span data-type="title" id=toc-169>[سورة التوبة (٩) : آية ١٢٢]</span>
وَما كانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَافَّةً فَلَوْلا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طائِفَةٌ لِيَتَفَقَّهُوا فِي الدِّينِ وَلِيُنْذِرُوا قَوْمَهُمْ إِذا رَجَعُوا إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ (١٢٢)
قوله تعالى: فَلَوْلا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طائِفَةٌ لِيَتَفَقَّهُوا فِي الدِّينِ [١٢٢] قال:
ليتعلموا ما يحتاج إليه في أمر الدين. وقد حكي عن الحسن البصري أنه قال: الفقيه الزاهد في الدنيا، الراغب في الآخرة، البصير في أمر دينه. وسئل سهل عن معنى قوله صلّى الله عليه وسلّم: «طلب العلم فريضة على كل مسلم» «١» ، فقال: يعني علم الحال «٢» . قيل: وما علم الحال؟ قال: من الباطن الإخلاص، ومن الظاهر الاقتداء، فمن لم يكن ظاهره إمام باطنه، وباطنه كمال ظاهره فهو في تعب من البدن. قيل: وما تفسير ذلك؟ قال: إن الله قائم عليك في سرك وعلانيتك وحركاتك وسكونك لا تغيب عنه طرفة عين، كما قال: أَفَمَنْ هُوَ قائِمٌ عَلى كُلِّ نَفْسٍ بِما كَسَبَتْ [الرعد: ٣٣] . وقال: مَا يَكُونُ مِنْ نَجْوى ثَلاثَةٍ إِلَّا هُوَ رابِعُهُمْ [المجادلة: ٧] الآية، وقال:
وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ الْوَرِيدِ [ق: ١٦] وهو العرق الذي في جوف القلب، فأخبر أنه أقرب إلى القلب من ذلك العرق. فإذا علمت ذلك، ينبغي أن تستحي منه، وما هاج في القلب شيء مما تهوى النفس. فذكر العبد قيام الله عزَّ وجلَّ عليه، فتركه إلاَّ دخل قلبه من علم حاله ما لو قسم ما أعطى ذلك العبد على أهل المدينة لسعدوا جميعاً وفازوا به، وقد أشار إليه مالك بن أنس «٣» رضي الله عنهما حيث قال: ليس العلم بكثرة الرواية، إنما العلم نور يجعله الله في القلب «٤» .
قيل له: كيف يعرف الرجل الحال والعلم به؟ فقال: إذا كنت تتكلم فحالك الكلام، وإذا سكت فحالك السكوت، وإذا قمت فحالك القيام، وإذا قعدت فحالك القعود، والعلم به أن تنظر أن هذا الحال لله أو لغيره، فإن كانت لله استقررت عليها، وإن كانت لغيره تركتها، وهو المحاسبة التي أمر بها عمر رضي الله عنه حيث قال: حاسبوا أنفسكم قبل أن تحاسبوا وزنوا أنفسكم قبل أن توزنوا «٥» . وقد كان عمر رضي الله عنه يضرب نفسه بالدرة في المحاسبة.
_________
(١) كشف الخفاء ١/ ٧١، ٢/ ٥٦، والمعجم الأوسط ٢/ ٢٨٩، ٦/ ٩٦، والمعجم الصغير ١/ ٣٦، ٥٨، وشرح سنن ابن ماجة ١/ ٢٠، وبعد الحديث قال: (قال النووي: إنه ضعيف، وإن كان صحيحا) .
(٢) قوت القلوب ١/ ٣٢٤.
(٣) مالك بن أنس بن مالك الأصبحي الحميري (٩٣- ١٧٩ هـ) : إمام دار الهجرة، وأحد الأئمة الأربعة، وإليه تنسب المالكية. من أشهر كتبه الموطأ. (الأعلام ٥/ ٢٥٧) .
(٤) صفوة الصفوة ٢/ ١٧٩، وسير أعلام النبلاء ١٣/ ٣٢٣ والشطر الأول من القول نسب إلى إبراهيم الخواص في شعب الإيمان ٢/ ٢٤٩، وبعده: (إنما العالم من اتبع العلم واستعمله) ، كما نسب الشطر الأول من القول إلى عبد الله في الحلية ١/ ١٣١، وبعده: (ولكن العلم الخشية) .
(٥) سنن الترمذي ٤/ ٦٣٨ (رقم ٢٤٥٩) وكتاب الزهد لابن مبارك ص ١٠ وصفوة الصفوة ١/ ٢٨٦ ومصنف ابن أبي شيبة ٧/ ٩٦، رقم ٣٤٤٥٩. وسيعاد قوله في تفسير الآية ١٤ من سورة الإسراء.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (73,'<span data-type="title" id=toc-170>السورة التي يذكر فيها يونس عليه السلام</span>

<span data-type="title" id=toc-171>[سورة يونس (١٠) : الآيات ٢ الى ٣]</span>
أَكانَ لِلنَّاسِ عَجَباً أَنْ أَوْحَيْنا إِلى رَجُلٍ مِنْهُمْ أَنْ أَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ قالَ الْكافِرُونَ إِنَّ هذا لَساحِرٌ مُبِينٌ (٢) إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّماواتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ يُدَبِّرُ الْأَمْرَ ما مِنْ شَفِيعٍ إِلاَّ مِنْ بَعْدِ إِذْنِهِ ذلِكُمُ اللَّهُ رَبُّكُمْ فَاعْبُدُوهُ أَفَلا تَذَكَّرُونَ (٣)
قوله تعالى: وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ [٢] قال: يعني سابقة رحمة أودعها في محمد صلّى الله عليه وسلّم.
قوله: يُدَبِّرُ الْأَمْرَ [٣] قال: يقضي القضاء وحده، فيختار للعبد ما هو خير له، فخيرة الله خير له من خيرته لنفسه. وقيل لسهل حين احتُضر: فيما تكفن، وأين تقبر، ومن يصلي عليك بعد موتك؟ فقال: أدبر أمري حياً وميتاً، وقد كفيت عنه بسابق تدبير الله تعالى لعبده.

<span data-type="title" id=toc-172>[سورة يونس (١٠) : آية ١٢]</span>
وَإِذا مَسَّ الْإِنْسانَ الضُّرُّ دَعانا لِجَنْبِهِ أَوْ قاعِداً أَوْ قائِماً فَلَمَّا كَشَفْنا عَنْهُ ضُرَّهُ مَرَّ كَأَنْ لَمْ يَدْعُنا إِلى ضُرٍّ مَسَّهُ كَذلِكَ زُيِّنَ لِلْمُسْرِفِينَ ما كانُوا يَعْمَلُونَ (١٢)
قوله تعالى: وَإِذا مَسَّ الْإِنْسانَ الضُّرُّ دَعانا لِجَنْبِهِ [١٢] قال: الدعاء هو التبري مما سوى الله تعالى.

<span data-type="title" id=toc-173>[سورة يونس (١٠) : آية ٢٢]</span>
هُوَ الَّذِي يُسَيِّرُكُمْ فِي الْبَرِّ وَالْبَحْرِ حَتَّى إِذا كُنْتُمْ فِي الْفُلْكِ وَجَرَيْنَ بِهِمْ بِرِيحٍ طَيِّبَةٍ وَفَرِحُوا بِها جاءَتْها رِيحٌ عاصِفٌ وَجاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكانٍ وَظَنُّوا أَنَّهُمْ أُحِيطَ بِهِمْ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ لَئِنْ أَنْجَيْتَنا مِنْ هذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ (٢٢)
قوله: دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ [٢٢] قال: الإخلاص هو المشاهدة، وحياة القلب في شيئين، الإيمان في الأصل والإخلاص في الفرع، وإن الإخلاص خطر عظيم، وصاحبه منه على حذر حتى يصل إخلاصه بالموت، لأن الأعمال بالخواتيم، وَاعْبُدْ رَبَّكَ حَتَّى يَأْتِيَكَ الْيَقِينُ [الحجر: ٩٩] .

<span data-type="title" id=toc-174>[سورة يونس (١٠) : آية ٢٥]</span>
وَاللَّهُ يَدْعُوا إِلى دارِ السَّلامِ وَيَهْدِي مَنْ يَشاءُ إِلى صِراطٍ مُسْتَقِيمٍ (٢٥)
قوله تعالى: وَاللَّهُ يَدْعُوا إِلى دارِ السَّلامِ وَيَهْدِي مَنْ يَشاءُ إِلى صِراطٍ مُسْتَقِيمٍ [٢٥] قال: الدعوة عامة والهداية خاصة، فإنه رد الهداية إلى المشيئة وهي سابقة القدر من الله تعالى.

<span data-type="title" id=toc-175>[سورة يونس (١٠) : آية ٥١]</span>
أَثُمَّ إِذا مَا وَقَعَ آمَنْتُمْ بِهِ آلْآنَ وَقَدْ كُنْتُمْ بِهِ تَسْتَعْجِلُونَ (٥١)
قوله تعالى: أَثُمَّ إِذا مَا وَقَعَ آمَنْتُمْ بِهِ آلْآنَ وَقَدْ كُنْتُمْ بِهِ تَسْتَعْجِلُونَ [٥١] يعني كنتم تستعجلون بالجحود بنا، وتذكرون غيرنا، فإذا صرتم إلينا وعاينتم ما وعدناكم من عذابنا آمنتم حين لا ينفع، فلا بد للخلق كلهم من الإقرار بالتوحيد في الآخرة عند تجلي حكم الذات، ونزول الأضداد والأنداد، والدعاوي بها، لزوال الشك وخوف العذاب.

<span data-type="title" id=toc-176>[سورة يونس (١٠) : آية ٥٨]</span>
قُلْ بِفَضْلِ اللَّهِ وَبِرَحْمَتِهِ فَبِذلِكَ فَلْيَفْرَحُوا هُوَ خَيْرٌ مِمَّا يَجْمَعُونَ (٥٨)
قوله تعالى: قُلْ بِفَضْلِ اللَّهِ وَبِرَحْمَتِهِ فَبِذلِكَ فَلْيَفْرَحُوا [٥٨] أي بتوحيده ونبيه محمد صلّى الله عليه وسلّم كما قال: وَما أَرْسَلْناكَ إِلَّا رَحْمَةً لِلْعالَمِينَ [الأنبياء: ١٠٧] .',NULL,'76',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (74,'<span data-type="title" id=toc-177>[سورة يونس (١٠) : آية ٦٢]</span>
أَلا إِنَّ أَوْلِياءَ اللَّهِ لاَ خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ (٦٢)
قوله تعالى: أَلا إِنَّ أَوْلِياءَ اللَّهِ لاَ خَوْفٌ عَلَيْهِمْ وَلا هُمْ يَحْزَنُونَ [٦٢] قال سهل:
هم الذين وصفهم رسول الله صلّى الله عليه وسلّم: «إذا رؤوا ذكر الله» «١» وهم المجاهدون في الله السابقون إليه الذين توالت أفعالهم على الموافقة أُولئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا [الأنفال: ٤] وقال: اجتمع الخير كله في هذه الأربعة وبها صاروا أبدالاً: إخماص البطون، والاعتزال عن الخلق، وسهر الليل، والصمت. قيل له: لِمَ سمي الأبدال أبدالاً؟ فقال: لأنهم يبدلون الأحوال، أخرجوا أبدانهم عن الحيل في سرهم، ثم لا يزالون ينقلبون من حال إلى حال، ومن علم إلى علم، فهم أبداً في المزيد من العلم فيما بينهم وبين ربهم.
قيل: الأوتاد أفضل أم الأبدال؟ قال: الأوتاد. قيل: وكيف ذلك؟ قال: لأن الأوتاد قد بلغوا وثبتت أركانهم، والأبدال ينقلبون من حال إلى حال. وقال سهل: لقيت ألفاً وخمسمائة صدّيق، فمنهم أربعون بديلاً وسبعة أوتاد، وطريقهم ومذهبهم ما أنا عليه. وكان يقول: أنا حجة الله عليكم خاصة، وعلى الناس عامة. وكان من طريقه وسيرته أنه كان كثير الشكر والذكر، دائم الصمت والفكر، قليل الخلاف، سخي النفس، قد ساد الناس بحسن الخلق والرحمة والشفقة عليهم والنصيحة لهم، متمسكاً بالأصل، عاملاً بالفرع، قد حشى الله قلبه نوراً، وأنطق لسانه بالحكمة، وكان من خير الأبدال، وإن قلنا من الأوتاد فقد كان القطب الذي يدور عليه الرحى، ولولا أن الصحابة لا يقاس بهم أحد لصحبتهم ورؤيتهم لكان كأحدهم، عاش حميداً ومات غريباً بالبصرة رحمة الله عليه. وقد كان رجل يصحب سهلاً يقال له عبد الرحمن بن أحمد، فقال يوماً لسهل: يا أبا محمد، إني ربما أتوضأ للصلاة فيسيل الماء من بين يدي فيصير قضبان ذهب وفضة. فقال سهل: يا حبيبي، أما علمت أن الصبي إذا بكى يناول خشخاشة حتى يشتغل بها، فانظر أي شيء هو هذا يعمل «٢» . وقال: كان في منزله بيت يقال له بيت السباع، وكانت السباع تجيء سهلاً، فكان يدخلها ذلك البيت، ويضيفها فيطعمها اللحم، ثم يخليها.

<span data-type="title" id=toc-178>[سورة يونس (١٠) : آية ١٠٩]</span>
وَاتَّبِعْ مَا يُوحى إِلَيْكَ وَاصْبِرْ حَتَّى يَحْكُمَ اللَّهُ وَهُوَ خَيْرُ الْحاكِمِينَ (١٠٩)
قوله: وَاتَّبِعْ مَا يُوحى إِلَيْكَ وَاصْبِرْ [١٠٩] قال: أجرى الله في الخلق أحكامه، وأيدهم على اتباعها بفضله وقدرته، ودلَّهم على رشدهم بقوله: وَاتَّبِعْ مَا يُوحى إِلَيْكَ وَاصْبِرْ [١٠٩] فالصبر على الاتباع ترك تدبير النفس، ففيه النجاة عاجلاً من رعونات النفس، وآجلاً من حياء المخالفة.
_________
(١) مسند أحمد ٦/ ٤٥٩ ونوادر الأصول ٤/ ٨٠، ٨٦. [.....]
(٢) سير أعلام النبلاء ١٣/ ٣٣٣.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (75,'<span data-type="title" id=toc-179>السورة التي يذكر فيها هود عليه السلام</span>

<span data-type="title" id=toc-180>[سورة هود (١١) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الر كِتابٌ أُحْكِمَتْ آياتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَكِيمٍ خَبِيرٍ (١)
قوله تعالى: فُصِّلَتْ مِنْ لَدُنْ حَكِيمٍ خَبِيرٍ [١] أي بيّن فيها الوعد على الطاعة، والوعيد بالعقاب على المعصية والإصرار عليها.

<span data-type="title" id=toc-181>[سورة هود (١١) : آية ٣]</span>
وَأَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُمَتِّعْكُمْ مَتاعاً حَسَناً إِلى أَجَلٍ مُسَمًّى وَيُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ وَإِنْ تَوَلَّوْا فَإِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ كَبِيرٍ (٣)
قوله: وَأَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ [٣] قال: الاستغفار هو الإجابة، ثم الإنابة ثم التوبة، ثم الاستغفار فالإجابة بالظاهر، والإنابة بالقلب، والتوبة مداومة الاستغفار من تقصيره فيها «١» .
قوله: يُمَتِّعْكُمْ مَتاعاً حَسَناً [٣] قال: ترك الخلق والإقبال على الحق.

<span data-type="title" id=toc-182>[سورة هود (١١) : آية ١٥]</span>
مَنْ كانَ يُرِيدُ الْحَياةَ الدُّنْيا وَزِينَتَها نُوَفِّ إِلَيْهِمْ أَعْمالَهُمْ فِيها وَهُمْ فِيها لا يُبْخَسُونَ (١٥)
قوله: مَنْ كانَ يُرِيدُ الْحَياةَ الدُّنْيا وَزِينَتَها نُوَفِّ إِلَيْهِمْ أَعْمالَهُمْ [١٥] قال: يعني من أراد بعلمه غير الله آتاه الله أجر عمله في الدنيا، فلا يبقى له في الآخرة شيء، لأنه لم يخلص بعمله لله لما أحب له من المنزلة في الدنيا، ولو علم أن الله سخر الدنيا وأهلها لطلاب الآخرة لم يراء بعلمه. وقد قيل لسهل: أي شيء أشد على النفس؟ فقال: الإخلاص. قيل: ولِمَ ذلك؟
فقال: لأنه ليس للنفس فيه نصيب. وسئل: هل يدخل الفرائض رياء؟ فقال: نعم، قد دخل الإيمان الذي هو أصل الفرائض حتى أبطله وصار نفاقاً، فكيف العمل، فكل من لم يعب أحد عليه في ظاهره، ويعلم الله خلافه من سره في أي حال كان، فهو المرائي الذي لا شك فيه.

<span data-type="title" id=toc-183>[سورة هود (١١) : آية ٢٣]</span>
إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ وَأَخْبَتُوا إِلى رَبِّهِمْ أُولئِكَ أَصْحابُ الْجَنَّةِ هُمْ فِيها خالِدُونَ (٢٣)
قوله تعالى: وَأَخْبَتُوا إِلى رَبِّهِمْ [٢٣] أي خشعت قلوبهم إلى ربهم، وهو الخشية، فالخشوع ظاهر والخشية سر، كما قال الرسول صلّى الله عليه وسلّم: «لو خشع قلبه لخشعت جوارحه» «٢» . فقد حكي أن موسى صلوات الله عليه قص في بني إسرائيل، فمزق واحد منهم قميصه، فأوحى الله تعالى إلى موسى أن قل له: مزق لي قلبك ولا تمزق لي ثيابك «٣» .
_________
(١) قوت القلوب ١/ ٣٣٥.
(٢) نوادر الأصول ٣/ ٢١٠، ٤/ ٢٤.
(٣) صفوة الصفوة ٣/ ٢٦٥ وكتاب الزهد لابن أبي عاصم ص ٨٧ وفيض القدير ١/ ٧٩.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (76,'<span data-type="title" id=toc-184>[سورة هود (١١) : آية ٤٠]</span>
حَتَّى إِذا جاءَ أَمْرُنا وَفارَ التَّنُّورُ قُلْنَا احْمِلْ فِيها مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلاَّ مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ آمَنَ وَما آمَنَ مَعَهُ إِلاَّ قَلِيلٌ (٤٠)
قوله تعالى: وَفارَ التَّنُّورُ [٤٠] قال: كان تنوراً من حجارة، وهو تنور آدم صار لنوح قد جعل الله فوران الماء منه علامة عذابه، وجعل ينبوع عيون قلب محمد صلّى الله عليه وسلّم بأنوار العلوم رحمة لأمته، إذ أكرمه الله تعالى بهذه الكرامة، فنور الأنبياء عليهم الصلاة والسلام من نوره، ونور الملكوت من نوره، ونور الدنيا والآخرة من نوره، فمن أراد المحبة حقيقة فليتبعه، قال الله تعالى لنبيه صلّى الله عليه وسلّم: قُلْ إِنْ كُنْتُمْ تُحِبُّونَ اللَّهَ فَاتَّبِعُونِي يُحْبِبْكُمُ اللَّهُ [آل عمران: ٣١] فجعل المحبة في اتباعه، وجعل جزاء اتباعه محبته لعباده، وهي أعلى الكرامة.
وقد حكي عن أبي موسى الأشعري «١» قال: بينما نحن عند رسول الله صلّى الله عليه وسلّم إذ أقبل بنا حتى نصبنا وجهه كأنه يريد أن يخبرنا، ثم سجد وسجدنا معه في أول النهار، حتى كان نحو نصف من النهار، حتى وجد بعضنا طعم التراب في أنفه، حتى قال بعضنا لبعض: قد مات رسول الله صلّى الله عليه وسلّم، ثم رفع رأسه فقال: الله أكبر. فقلنا: الله أكبر. فقال له قائل: يا رسول الله، لقد ظننا أنك مت، ولو كان ذلك ما بالينا أن تقع السماء على الأرض. فقال: أتاني حبيبي جبريل صلوات الله عليه، فقال لي: يا محمد، إن ربك يقرئك السلام، ويخيرك بين أن يدخل ثلث أمتك الجنة، وبين الشفاعة، فلما طمعت في الثلث اخترت الشفاعة، فارتفع ونصبتكم وجهي أريد أن أخبركم، فأتاني فقال: يا محمد، إن ربك يقرئك السلام، ويخيرك بين أن يدخل ثلثي أمتك الجنة، وبين الشفاعة، فلما طمعت في الثلثين اخترت الشفاعة، فارتفع ونصبتكم وجهي أريد أن أخبركم، ثم أتاني فقال لي: يا محمد، إن ربك قد شفعك في الثلثين، ولم يجبك في الثلث، فسجدت شكراً لله تعالى فيما أعطاني «٢» .
وقال سهل: انتهت همم العارفين إلى الحجب، فوقفت مطرقة، فأذن لها بالدخول، فدخلت فسلمت، فخلع عليها خلع التأييد، وكتب لها من الرقع براءات، وأن همم الأنبياء صلوات الله عليهم جالت حول العرش، فألبست الأنوار، ورفع منها الأقدار، واتصلت بالجبار فأفنى حظوظها، وأسقط مرادها، وجعلها متصرفة به له. وقال: آخر درجات الصديقين أول
_________
(١) أبو موسى الأشعري: عبد الله بن قيس بن سليم بن حضار، من بني الأشعر (٢١ ق هـ- ٤٤ هـ) :
صحابي، من الشجعان الولاة الفاتحين. وأحد الحكيمين اللذين رضي بهما علي ومعاوية بعد حرب صفين. (الأعلام ٤/ ١١٤) .
(٢) المستدرك على الصحيحين ١/ ٦٠، ١٣٧ (رقم ٣٦، ٢٢٤) ومسند أحمد ٤/ ٤٠٤، ٤١٥، ٦/ ٢٣، ٢٨، ٢٩.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (77,'أحوال للأنبياء صلوات الله عليهم، وإن صلّى الله عليه وسلّم عبد الله تعالى بجميع أحوال الأنبياء، وليس في الجنة ورقة من أوراق الأشجار إلا ومكتوب عليها محمد صلّى الله عليه وسلّم «١» ، به ابتدأ الأشياء وبه ختمها، فسماه خاتم النبيين.

<span data-type="title" id=toc-185>[سورة هود (١١) : آية ٧٥]</span>
إِنَّ إِبْراهِيمَ لَحَلِيمٌ أَوَّاهٌ مُنِيبٌ (٧٥)
قوله تعالى: إِنَّ إِبْراهِيمَ لَحَلِيمٌ أَوَّاهٌ مُنِيبٌ [٧٥] قال: إن الله تعالى أشرفه على حركة النفس الطبيعية وسكونها، ولم يشرفه على علمه، لأنه ممحو عنه أو مثبت عليه، لئلا يسقط الخوف والرجاء عن نفسه، فكان إذا ذكره تأوه منه وسكت عن مسألة علم الخاتمة إذ لم يكن له مع الله عزَّ وجلَّ اختيار. ثم قال سهل: إن الخوف رجل وإن الرجاء أنثى «٢» ، ولو قسم ذرة من خوف الخائفين على أهل الأرض لسعدوا بذلك. فقيل له: فكم يكون مع الخائفين هكذا؟ فقال:
مثل الجبل الجبل.

<span data-type="title" id=toc-186>[سورة هود (١١) : آية ٧٨]</span>
وَجاءَهُ قَوْمُهُ يُهْرَعُونَ إِلَيْهِ وَمِنْ قَبْلُ كانُوا يَعْمَلُونَ السَّيِّئاتِ قالَ يا قَوْمِ هؤُلاءِ بَناتِي هُنَّ أَطْهَرُ لَكُمْ فَاتَّقُوا اللَّهَ وَلا تُخْزُونِ فِي ضَيْفِي أَلَيْسَ مِنْكُمْ رَجُلٌ رَشِيدٌ (٧٨)
قوله تعالى: هؤُلاءِ بَناتِي هُنَّ أَطْهَرُ لَكُمْ [٧٨] أي هن أحل لكم تزويجاً من إتيان الفاحشة.

<span data-type="title" id=toc-187>[سورة هود (١١) : آية ٨٨]</span>
قالَ يا قَوْمِ أَرَأَيْتُمْ إِنْ كُنْتُ عَلى بَيِّنَةٍ مِنْ رَبِّي وَرَزَقَنِي مِنْهُ رِزْقاً حَسَناً وَما أُرِيدُ أَنْ أُخالِفَكُمْ إِلى ما أَنْهاكُمْ عَنْهُ إِنْ أُرِيدُ إِلاَّ الْإِصْلاحَ مَا اسْتَطَعْتُ وَما تَوْفِيقِي إِلاَّ بِاللَّهِ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ (٨٨)
قوله تعالى: وَما أُرِيدُ أَنْ أُخالِفَكُمْ إِلى مَآ أَنْهاكُمْ [٨٨] قال: كل عالم أعطي علم الشر، وليس هو مجانباً للشر، فليس بعالم، ومن أعطي علم الطاعات وهو غير عامل بها، فليس بعالم. وقد سأل رجل سهلاً فقال: يا أبا محمد، إلى من تأمرني أن أجلس إليه؟ فقال:
إلى من تحملك جوارحه لا لسانه.

<span data-type="title" id=toc-188>[سورة هود (١١) : آية ٩١]</span>
قالُوا يا شُعَيْبُ ما نَفْقَهُ كَثِيراً مِمَّا تَقُولُ وَإِنَّا لَنَراكَ فِينا ضَعِيفاً وَلَوْلا رَهْطُكَ لَرَجَمْناكَ وَما أَنْتَ عَلَيْنا بِعَزِيزٍ (٩١)
قوله تعالى: وَلَوْلا رَهْطُكَ لَرَجَمْناكَ [٩١] قال: حكى محمد بن سوار عن أبي عمرو ابن العلاء أنه قال: الرهط الملأ، والنفر الرجال من غير أن تكون فيهم امرأة.

<span data-type="title" id=toc-189>[سورة هود (١١) : آية ١١٣]</span>
وَلا تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُ وَما لَكُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِياءَ ثُمَّ لا تُنْصَرُونَ (١١٣)
قوله تعالى: وَلا تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا [١١٣] قال: أي لا تعتمدوا في دينكم إلا على سنتي.
_________
(١) ورد مثل هذا القول عن ابن عباس في الحلية ٣/ ٣٠٤ ومجمع الزوائد ٩/ ٥٨ والمعجم الكبير ١١/ ٧٦.
(٢) قوت القلوب ١/ ٤١٦.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (78,'<span data-type="title" id=toc-190>السورة التي يذكر فيها يوسف عليه السلام</span>

<span data-type="title" id=toc-191>[سورة يوسف (١٢) : آية ٦]</span>
وَكَذلِكَ يَجْتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيلِ الْأَحادِيثِ وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ وَعَلى آلِ يَعْقُوبَ كَما أَتَمَّها عَلى أَبَوَيْكَ مِنْ قَبْلُ إِبْراهِيمَ وَإِسْحاقَ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٌ (٦)
قوله تعالى: وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ [٦] يعني بتصديق الرؤيا التي رأيتها لنفسك.

<span data-type="title" id=toc-192>[سورة يوسف (١٢) : آية ١٨]</span>
وَجاؤُ عَلى قَمِيصِهِ بِدَمٍ كَذِبٍ قالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْراً فَصَبْرٌ جَمِيلٌ وَاللَّهُ الْمُسْتَعانُ عَلى ما تَصِفُونَ (١٨)
قوله تعالى: فَصَبْرٌ جَمِيلٌ [١٨] قال: الصبر مع الرضا. قيل: ما علامته؟ قال: أن لا يجزع فيه. فسئل: بأي شيء يحصل التجمل بالصبر؟ قال: بالمعرفة بأن الله تعالى معك، وبراحة العافية، فإنما مثل الصبر مثل قدح أعلاه الصبر وأسفله العسل. ثم قال: عجبت ممن لم يصبروا كيف لم يصبروا للحال، ورب العزة يقول: إِنَّ اللَّهَ مَعَ الصَّابِرِينَ [البقرة: ١٥٣] .

<span data-type="title" id=toc-193>[سورة يوسف (١٢) : آية ٢١]</span>
وَقالَ الَّذِي اشْتَراهُ مِنْ مِصْرَ لامْرَأَتِهِ أَكْرِمِي مَثْواهُ عَسى أَنْ يَنْفَعَنا أَوْ نَتَّخِذَهُ وَلَداً وَكَذلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ وَلِنُعَلِّمَهُ مِنْ تَأْوِيلِ الْأَحادِيثِ وَاللَّهُ غالِبٌ عَلى أَمْرِهِ وَلكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ (٢١)
قوله تعالى: أَكْرِمِي مَثْواهُ عَسى أَنْ يَنْفَعَنا [٢١] يعني عسى أن يكون شفيعنا في الآخرة.

<span data-type="title" id=toc-194>[سورة يوسف (١٢) : آية ٢٤]</span>
وَلَقَدْ هَمَّتْ بِهِ وَهَمَّ بِها لَوْلا أَنْ رَأى بُرْهانَ رَبِّهِ كَذلِكَ لِنَصْرِفَ عَنْهُ السُّوءَ وَالْفَحْشاءَ إِنَّهُ مِنْ عِبادِنَا الْمُخْلَصِينَ (٢٤)
قوله تعالى: وَلَقَدْ هَمَّتْ بِهِ وَهَمَّ بِها لَوْلا أَنْ رَأى بُرْهانَ رَبِّهِ [٢٤] يعني همَّ بنفسه الطبيعية إلى الميل إليها، وهمَّ بنفس التوفيق والعصمة الفرار منها ومخالفتها. ومعناه أنه عصمه ربه، ولولا عصمة ربه لهمَّ بها ميلاً إلى ما دعته نفسه إليه، وعصمه ما عاين من برهان ربه عزَّ وجلَّ، هو أنه جاءه جبريل صلوات الله عليه في سورة يعقوب عليه السلام عاضاً إصبعه، فولى عند ذلك نحو الباب مستغفرا.

<span data-type="title" id=toc-195>[سورة يوسف (١٢) : آية ٤٢]</span>
وَقالَ لِلَّذِي ظَنَّ أَنَّهُ ناجٍ مِنْهُمَا اذْكُرْنِي عِنْدَ رَبِّكَ فَأَنْساهُ الشَّيْطانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ (٤٢)
قوله تعالى: اذْكُرْنِي عِنْدَ رَبِّكَ [٤٢] قال: حكي أن جبريل صلوات الله عليه دخل على يوسف في السجن، فقال له جبريل: يا طاهر ابن طاهر، إن الله تعالى أكرمني بك وبآبائك، وهو يقول لك: يا يوسف، أما استحييت مني حيث استشفعت إلى غيري، فوعزتي لألبثنك بضع سنين. قال: يا جبريل، هو عني راض؟ قال: نعم. قال: إذن لا أبالي. وكان علي ابن أبي طالب رضي الله عنه يقول: ما أنا ونفسي إلا كراعي غنم، كلما ضمها من جانب انتشرت من جانب.

<span data-type="title" id=toc-196>[سورة يوسف (١٢) : آية ٥٣]</span>
وَما أُبَرِّئُ نَفْسِي إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ إِلاَّ مَا رَحِمَ رَبِّي إِنَّ رَبِّي غَفُورٌ رَحِيمٌ (٥٣)
قوله تعالى: وَما أُبَرِّئُ نَفْسِي إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ [٥٣] قال: إن النفس الأمارة هي الشهوة، وهي موضع الطبع، إِلَّا مَا رَحِمَ رَبِّي [٥٣] موضع العصمة، والنفس',NULL,'81',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (79,'المطمئنة هي نفس المعرفة، وأن الله تعالى خلق النفس وجعل طبعها الجهل، وجعل الهوى أقرب الأشياء إليها، وجعل الهوى الباب الذي منه تدخل هلاك الخلق. فسئل سهل عن معنى الطبع، وعما يوجب العصمة عنه. فقال: طبع الخلق على أربع طبائع: أولها طبع البهائم البطن والفرج والثاني طبع الشياطين اللعب واللهو، والثالث طبع السحرة المكر والخداع، والرابع طبع الأبالسة الإباء والاستكبار. فالعصمة من طبع البهائم الإيمان، والسلامة من طبع الشياطين التسبيح والتقديس وهو طبع الملائكة، والسلامة من طبع السحرة الصدق والنصيحة والإنصاف والتفضل، والسلامة من طبع الأبالسة الالتجاء إلى الله تعالى بالتضرع والصراخ، وطبع العقل العلم، وطبع النفس الجهل، وطبع الطبع الدعوى «١» .
قوله تعالى: وَدَخَلَ مَعَهُ السِّجْنَ فَتَيانِ [٣٦] قال: إنما قال الله تعالى: «فتيان» لأنهما لم يتجاوز واحدهما في الدعوى، ورجعا في كل ما كان لهما إلى صاحبهما، فسماهما فتيان.
قوله تعالى: ذلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ [٥٢] قال: لم أنقض له عهداً، ولم أكشف له سترا.

<span data-type="title" id=toc-197>[سورة يوسف (١٢) : آية ٦٧]</span>
وَقالَ يا بَنِيَّ لا تَدْخُلُوا مِنْ بابٍ واحِدٍ وَادْخُلُوا مِنْ أَبْوابٍ مُتَفَرِّقَةٍ وَما أُغْنِي عَنْكُمْ مِنَ اللَّهِ مِنْ شَيْءٍ إِنِ الْحُكْمُ إِلاَّ لِلَّهِ عَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ (٦٧)
قوله تعالى: عَلَيْهِ تَوَكَّلْتُ [٦٧] فسئل ما حقيقة التوكل؟ قال: الاسترسال مع الله تعالى على ما يريد. فقيل: ما حق التوكل؟ فقال: أوله العلم وحقيقته العمل، ثم قال: إن المتوكل إذا كان على الحقيقة لا يأكل طعاماً، وهو يعلم أن غيره أحق منه.
قوله تعالى: قالُوا يا أَيُّهَا الْعَزِيزُ مَسَّنا وَأَهْلَنَا الضُّرُّ [٨٨] يعني يا أيها الملك العظيم، وباطنها يا أيها المغلوب في نفسه، كما قال الله تعالى: وَعَزَّنِي فِي الْخِطابِ [ص: ٢٣] أي غلبني فيه.

<span data-type="title" id=toc-198>[سورة يوسف (١٢) : الآيات ٨٥ الى ٨٧]</span>
قالُوا تَاللَّهِ تَفْتَؤُا تَذْكُرُ يُوسُفَ حَتَّى تَكُونَ حَرَضاً أَوْ تَكُونَ مِنَ الْهالِكِينَ (٨٥) قالَ إِنَّما أَشْكُوا بَثِّي وَحُزْنِي إِلَى اللَّهِ وَأَعْلَمُ مِنَ اللَّهِ مَا لاَ تَعْلَمُونَ (٨٦) يا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَأَخِيهِ وَلا تَيْأَسُوا مِنْ رَوْحِ اللَّهِ إِنَّهُ لا يَيْأَسُ مِنْ رَوْحِ اللَّهِ إِلاَّ الْقَوْمُ الْكافِرُونَ (٨٧)
قوله تعالى: حَتَّى تَكُونَ حَرَضاً [٨٥] قال: حكي عن علي رضي الله عنه أنه قال:
الحرض هو البلاء لتألم القلب. وقال ابن عباس رضي الله عنه: الحرض دون الموت. وقال سهل: أي فاسد الجسم والعمل من الحزن. وإنما كان حزنه على دين يوسف، لا على نفسه، لأنه علم أنه لو مات على دينه اجتمع معه في الآخرة الباقية، وإذا تغير دينه لم يجتمعا أبداً. وقد حكي عن سفيان أنه قال: إن يعقوب عليه السلام لما جاءه البشير قال له يعقوب: على أي دين تركت يوسف؟ فقال: على دين الإسلام. قال: الآن تمت النعمة.
قوله تعالى: نَّما أَشْكُوا بَثِّي وَحُزْنِي إِلَى اللَّهِ
[٨٦] يعني همي وحزني. قال سهل:
لم يكن حزن يعقوب على يوسف، إنما كان مكاشفاً لما وجد من قلبه الوجد على مفارقة يوسف
_________
(١) الحلية ١٠/ ٢٠٦.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (80,'فقال: كيف يكون وجد فراق الحق عزَّ وجلَّ. وقد عمل بمفارقة مخلوق كل هذا، فشكى بثه وحزنه إلى الله تعالى لا إلى غيره.
قوله تعالى: قالَ كَبِيرُهُمْ [٨٠] أي في العقل لا في السن.
قوله تعالى: لاَ تَيْأَسُوا مِنْ رَوْحِ اللَّهِ [٨٧] قال سهل: أفضل الخدمة وأعلاها انتظار الفرج من الله تعالى، كما حكي عن ابن عمر رضي الله عنه عن النبي صلى الله عليه وسلّم أنه قال: «انتظار الفرج بالصبر عبادة» «١» وانتظار الفرج على وجهين: أحدهما قريب، والآخر بعيد فالقريب في السر فيما بين العبد وربه، والبعيد في الخلق فينظر إلى البعيد فيحجب عن القريب.

<span data-type="title" id=toc-199>[سورة يوسف (١٢) : آية ١٠١]</span>
رَبِّ قَدْ آتَيْتَنِي مِنَ الْمُلْكِ وَعَلَّمْتَنِي مِنْ تَأْوِيلِ الْأَحادِيثِ فاطِرَ السَّماواتِ وَالْأَرْضِ أَنْتَ وَلِيِّي فِي الدُّنْيا وَالْآخِرَةِ تَوَفَّنِي مُسْلِماً وَأَلْحِقْنِي بِالصَّالِحِينَ (١٠١)
قوله تعالى: تَوَفَّنِي مُسْلِماً وَأَلْحِقْنِي بِالصَّالِحِينَ [١٠١] قال سهل: فيه ثلاثة أشياء، سؤال ضرورة وإظهار فقر واختيار فرض، ومعناه: أمتني وأنا مسلم إليك أمري، مفوض إليك شأني، لا يكون لي إلى نفسي رجوع بحال ولا تدبير بسبب من الأسباب.

<span data-type="title" id=toc-200>[سورة يوسف (١٢) : آية ١٠٦]</span>
وَما يُؤْمِنُ أَكْثَرُهُمْ بِاللَّهِ إِلاَّ وَهُمْ مُشْرِكُونَ (١٠٦)
قوله تعالى: وَما يُؤْمِنُ أَكْثَرُهُمْ بِاللَّهِ إِلَّا وَهُمْ مُشْرِكُونَ [١٠٦] قال: يعني شرك النفس الأمارة بالسوء كما قال النبي صلى الله عليه وسلّم: «الشرك في أمتي أخفى من دبيب النمل على الصفا» «٢» ، هذا باطن الآية، وأما ظاهرها مشركو العرب يؤمنون بالله، كما قال: وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَهُمْ لَيَقُولُنَّ اللَّهُ [الزخرف: ٨٧] وهم مع ذلك مشركون يؤمنون ببعض الرسل ولا يؤمنون ببعضهم.

<span data-type="title" id=toc-201>[سورة يوسف (١٢) : آية ١٠٨]</span>
قُلْ هذِهِ سَبِيلِي أَدْعُوا إِلَى اللَّهِ عَلى بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي وَسُبْحانَ اللَّهِ وَما أَنَا مِنَ الْمُشْرِكِينَ (١٠٨)
قوله تعالى: أَدْعُوا إِلَى اللَّهِ عَلى بَصِيرَةٍ [١٠٨] أي أبلغ الرسالة ولا أملك الهداية، وإنما الهداية إليك. وقد سئل سهل عن قوله عليه السلام: «ولا ينفع ذا الجد منك الجد» «٣» ، فقال: أي من جد في الطلب، وكان منك المنع، لم ينفعه جده في الطلب. وقال: إن الخلق لم يكشف لهم سر، ولو كشف لهم لأبصروا، ولم يشاهدوا وإن شاهدوا تم الأمر، وهذا شيء عظيم. ثم قال: أهل لا إله إلا الله كثير، والمخلصون منهم قليل، والله سبحانه وتعالى أعلم.
_________
(١) شعب الإيمان ٧/ ٢٠٤ ومسند الشهاب ١/ ٦٢ وفيض القدير ٣/ ٥٢، وفيه الشرح الآتي: (أي إذا حل بعبد بلاء، فترك الجزع والهلع، وصبر على مر القضاء، فذلك منه عبادة يثاب عليها، لما فيه من الانقياد للقضاء) .
(٢) المستدرك على الصحيحين ٢/ ٣١٩ ونوادر الأصول ٤/ ١٤٧ والترغيب والترهيب ٤/ ١٦، وسيعاد في تفسير سورة: المنافقون.
(٣) صحيح البخاري: صفة الصلاة رقم ٨٠٨ وصحيح مسلم: المساجد ومواضع الصلاة رقم ٥٩٣.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (81,'<span data-type="title" id=toc-202>السورة التي يذكر فيها الرعد</span>

<span data-type="title" id=toc-203>[سورة الرعد (١٣) : الآيات ١١ الى ١٣]</span>
لَهُ مُعَقِّباتٌ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ إِنَّ اللَّهَ لا يُغَيِّرُ ما بِقَوْمٍ حَتَّى يُغَيِّرُوا مَا بِأَنْفُسِهِمْ وَإِذا أَرادَ اللَّهُ بِقَوْمٍ سُوْءاً فَلا مَرَدَّ لَهُ وَما لَهُمْ مِنْ دُونِهِ مِنْ والٍ (١١) هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفاً وَطَمَعاً وَيُنْشِئُ السَّحابَ الثِّقالَ (١٢) وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلائِكَةُ مِنْ خِيفَتِهِ وَيُرْسِلُ الصَّواعِقَ فَيُصِيبُ بِها مَنْ يَشاءُ وَهُمْ يُجادِلُونَ فِي اللَّهِ وَهُوَ شَدِيدُ الْمِحالِ (١٣)
قوله تعالى: لَهُ مُعَقِّباتٌ مِنْ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ [١١] يعني ملائكة الليل والنهار يعقب بعضهم بعضاً يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ [١١] مقاديره على عبده من خير وشر، ويشهدون له بالوفاء، وعليه بالجفاء يوم القيامة.
قوله تعالى: هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفاً وَطَمَعاً [١٢] قال: روي عن ابن عباس رضي الله عنه أنه قال: الرعد ملك، وهو الذي تسمعون صوته، والبرق سوط من نور يزجر به الملك السحاب «١» ، وكذا قال مجاهد «٢» . وعن علي بن أبي طالب رضي الله عنه أنه قال: البرق مخاريق الملائكة، والرعد صوت ملك «٣» . وقال قتادة: الرعد صوت السحاب.
قوله تعالى: وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلائِكَةُ مِنْ خِيفَتِهِ [١٣] فأفرد الملائكة ذكراً.
وقال عكرمة «٤» : الرعد ملك موكل بسحاب يسوقه، كما يسوق راعي الإبل إبله. وحكى كعب «٥» عن عمر رضي الله عنه قال: سمعت رسول الله صلّى الله عليه وسلّم يقول: «إن الله ينشئ السحاب فينطق بأحسن النطق ويضحك بأحسن الضحك فمنطقه الرعد ومضحكه البرق» «٦» قاله أبو بكر
_________
(١) سنن البيهقي الكبرى: باب ما جاء في الرعد، ٣/ ٣٦٣ (رقم ٧٢٦٧) .
(٢) في المصدر السابق (أن مجاهدا كان يقول: الرعد ملك، والبرق أجنحة الملك، يسقن السحاب) وفي التاريخ الكبير ٢/ ١٠٤ أنه قال: (البرق مخاريق من نار) . [.....]
(٣) في المصدر السابق أنه قال: (الرعد ملك، والبرق مخراق من حديد) .
(٤) عكرمة بن عبد الله البربري المدني، مولى ابن عباس (٢٥- ١٠٥ هـ) : تابعي، كان من أعلم الناس بالتفسير والمغازي. روى عنه زهاء ثلاثمائة رجل. (الحلية ٣/ ٣٢٦) .
(٥) كعب بن ماتع بن ذي هجن الحميري ( ... - ٣٢ هـ) : تابعي. كان في الجاهلية من كبار علماء اليهود في اليمن، وأسلم في زمن أبي بكر. أخذ عنه الصحابة وغيرهم كثيرا من أخبار الأمم الغابرة، وأخذ هو من الكتاب والسنة عن الصحابة. (الحلية ٥/ ٣٦٤، ٦/ ٣) .
(٦) مسند أحمد ٥/ ٤٣٥ والفردوس بمأثور الخطاب ٥/ ٢٦٥ (رقم ٨١٣٩) .',NULL,'84',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (82,'فقلت له: ما تقول أنت؟ وكان في يوم وابل وصوت رعد شديد، فقال: هذا خبر رضا الله عزَّ وجلَّ، فكيف خبر غضبه، نعوذ بالله من غضبه.

<span data-type="title" id=toc-204>[سورة الرعد (١٣) : آية ٢٨]</span>
الَّذِينَ آمَنُوا وَتَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللَّهِ أَلا بِذِكْرِ اللَّهِ تَطْمَئِنُّ الْقُلُوبُ (٢٨)
قوله تعالى: أَلا بِذِكْرِ اللَّهِ تَطْمَئِنُّ الْقُلُوبُ [٢٨] قال: الذكر من العلم السكون، والذكر من العقل الطمأنينة. قيل: وكيف ذاك؟ قال: إذا كان العبد في طاعة الله فهو الذاكر، فإذا خطر بباله شيء فهو القاطع، وإذا كان في فعل نفسه فحضر بقلبه ما يدله على الذكر والطاعة فهو موضع العقل. ثم قال: كل من ادعى الذكر فهو على وجهين: قوم لم يفارقهم خوف الله عزَّ وجلَّ، مع ما وجدوا في قلوبهم من الحب والنشاط، فهم على حقيقة من الذكر، وهم لله والآخرة والعلم والسنة. وقوم ادعوا النشاط والفرح والسرور في جميع الأحوال، فهم للعدو والدنيا والجهل والبدعة، وهم شر الخلق.

<span data-type="title" id=toc-205>[سورة الرعد (١٣) : آية ٣٦]</span>
وَالَّذِينَ آتَيْناهُمُ الْكِتابَ يَفْرَحُونَ بِما أُنْزِلَ إِلَيْكَ وَمِنَ الْأَحْزابِ مَنْ يُنْكِرُ بَعْضَهُ قُلْ إِنَّما أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ وَلا أُشْرِكَ بِهِ إِلَيْهِ أَدْعُوا وَإِلَيْهِ مَآبِ (٣٦)
قوله تعالى: قُلْ إِنَّما أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ وَلا أُشْرِكَ بِهِ [٣٦] سئل سهل: متى يصح للعبد مقام العبودية؟ قال: إذا ترك تدبيره ورضي بتدبير الله تعالى فيه.

<span data-type="title" id=toc-206>[سورة الرعد (١٣) : آية ٣٩]</span>
يَمْحُوا اللَّهُ مَا يَشاءُ وَيُثْبِتُ وَعِنْدَهُ أُمُّ الْكِتابِ (٣٩)
قوله تعالى: يَمْحُوا اللَّهُ مَا يَشاءُ وَيُثْبِتُ وَعِنْدَهُ أُمُّ الْكِتابِ [٣٩] قال: يمحو الله ما يشاء من الأسباب، ويثبت الأقدار، وعنده أم الكتاب. قال: القضاء المبرم الذي لا زيادة فيه ولا نقصان.

<span data-type="title" id=toc-207>[سورة الرعد (١٣) : آية ٤٣]</span>
وَيَقُولُ الَّذِينَ كَفَرُوا لَسْتَ مُرْسَلاً قُلْ كَفى بِاللَّهِ شَهِيداً بَيْنِي وَبَيْنَكُمْ وَمَنْ عِنْدَهُ عِلْمُ الْكِتابِ (٤٣)
قوله: وَمَنْ عِنْدَهُ عِلْمُ الْكِتابِ [٤٣] قال سهل: الكتاب عزيز، وعلم الكتاب أعز، والعمل به أعز، والعمل عزيز، والإخلاص في العمل أعز، والإخلاص عزيز، والمشاهدة في الإخلاص أعز، والمرافقة عزيزة، والأنس في المرافقة أعز، والأنس عزيز، وآداب محل الأنس أعز، والله سبحانه وتعالى أعلم.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (83,'<span data-type="title" id=toc-208>السورة التي يذكر فيها إبراهيم عليه السلام</span>

<span data-type="title" id=toc-209>[سورة إبراهيم (١٤) : آية ٧]</span>
وَإِذْ تَأَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لَأَزِيدَنَّكُمْ وَلَئِنْ كَفَرْتُمْ إِنَّ عَذابِي لَشَدِيدٌ (٧)
قوله تعالى: لَئِنْ شَكَرْتُمْ لَأَزِيدَنَّكُمْ [٧] قال: شكر العلم العمل، وشكر العمل زيادة العلم، فهو أبداً في هذا، وهذه حاله. وقال: الشكر أن تريد المزيد، وإلا شكر مطعون.
قال: وحقيقة العجز الاعتراف به. وقد حكي أن داود عليه السلام قال: يا رب كيف أشكرك، وشكري إياك تجديد مِنّة منك عليّ؟ قال الله تعالى: الآن شكرتني «١» .

<span data-type="title" id=toc-210>[سورة إبراهيم (١٤) : آية ١١]</span>
قالَتْ لَهُمْ رُسُلُهُمْ إِنْ نَحْنُ إِلاَّ بَشَرٌ مِثْلُكُمْ وَلكِنَّ اللَّهَ يَمُنُّ عَلى مَنْ يَشاءُ مِنْ عِبادِهِ وَما كانَ لَنا أَنْ نَأْتِيَكُمْ بِسُلْطانٍ إِلاَّ بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ (١١)
قوله تعالى: وَلكِنَّ اللَّهَ يَمُنُّ عَلى مَنْ يَشاءُ [١١] يعني بتلاوة كتابه والفهم فيه.

<span data-type="title" id=toc-211>[سورة إبراهيم (١٤) : آية ١٩]</span>
أَلَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّماواتِ وَالْأَرْضَ بِالْحَقِّ إِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ (١٩)
قوله تعالى: أَلَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّماواتِ وَالْأَرْضَ بِالْحَقِّ [١٩] قال: خلق الأشياء كلها بقدرته، وزينها بعلمه، وحكمها بحكمته فالناظر من الخلق إلى الخالق تبين له عجائب الخلقة، والناظر من الخالق إلى الخلق يكشف له عن آثار قدرته وأنوار حكمته وبليغ صنعته.

<span data-type="title" id=toc-212>[سورة إبراهيم (١٤) : الآيات ٢٥ الى ٢٦]</span>
تُؤْتِي أُكُلَها كُلَّ حِينٍ بِإِذْنِ رَبِّها وَيَضْرِبُ اللَّهُ الْأَمْثالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ (٢٥) وَمَثَلُ كَلِمَةٍ خَبِيثَةٍ كَشَجَرَةٍ خَبِيثَةٍ اجْتُثَّتْ مِنْ فَوْقِ الْأَرْضِ ما لَها مِنْ قَرارٍ (٢٦)
قوله تعالى: تُؤْتِي أُكُلَها كُلَّ حِينٍ بِإِذْنِ رَبِّها [٢٥] قال: كان ابن المسيب يقول:
الحين ستة أشهر، وقد سأله رجل فقال: إني حلفت أن لا تدخل امرأتي على أهلها حيناً، فما الحين؟ قال سعيد: الحين من حين أن تطلع النخلة إلى أن ترطب، ومن أن ترطب إلى أن تطلع.
وقال ابن عباس رضي الله عنهما: كل حين أراد به غدوة وعشية «٢» ، وهو على طريق سهل بن عبد الله، فإنه قال: هذا مثل ضربه الله لأهل المعرفة في الله عليهم من إقامة فروضه بالليل والنهار.
وسئل سهل عن معنى قوله: أَلَمْ تَرَ كَيْفَ ضَرَبَ اللَّهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ أَصْلُها ثابِتٌ وَفَرْعُها فِي السَّماءِ [٢٤] قال: حكي عن ابن عباس رضي الله عنهما أن النبي صلى الله عليه وسلّم خرج على أصحابه وهم يذكرون الشجرة الطيبة فقال: «ذلك المؤمن أصله في الأرض وفرعه في السماء» ، يعني عمله مرفوع إلى السماء مقبول. فهذا مثل ضربه الله للمؤمن والكافر فقال:
_________
(١) الحلية ٦/ ٥٦ وشعب الإيمان ٤/ ١٠١ (رقم ٤/ ٤٤- ٤٤١٥) وجامع العلوم والحكم ١/ ٢٤٤.
(٢) في تفسير ابن كثير ٢/ ٥٥٠: (تؤتي أُكُلَهَا كُلَّ حِينٍ، قيل: غدوة وعشيا، وقيل: كل شهر، وقيل: كل شهرين، وقيل: كل ستة أشهر، وقيل: كل سبعة أشهر، وقيل: كل سنة) . وفي عمدة الحفاظ ١/ ٤٧٦:
(قيل: معناه ساعة، وقيل: أربعون سنة) .',NULL,'86',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (84,'كَلِمَةً طَيِّبَةً [٢٤] يعني كلمة الإخلاص كَشَجَرَةٍ طَيِّبَةٍ [٢٤] يعني النخلة أَصْلُها ثابِتٌ وَفَرْعُها فِي السَّماءِ [٢٤] يعني أغصانها مرفوعة إلى السماء، فكذلك أصل عمل المؤمن كلمة التوحيد، وهو أصل ثابت، وفرعه وهو عمله مرفوع إلى السماء مقبول، إلا أن فيه خللاً وإحداثاً، ولكن لا يزول أصل عمله، وهو كلمة التوحيد، كما أن الرياح تزعزع أغصان النخلة، ولا يزول أصلها، وشبه عمل الكافر كشجرة خبيثة فقال: وَمَثَلُ كَلِمَةٍ خَبِيثَةٍ [٢٦] يعني شجرة الحنظل أخبث ما فوق الأرض ليس لها أصل في الأرض، كذلك الكفر والنفاق ليس له في الآخرة من ثبات، وليس في خزائن الله أكبر من التوحيد.
وسئل عن تفسير: لا إِلهَ إِلَّا اللَّهُ [محمد: ١٩] فقال: لا نافع ولا دافع إلا الله تعالى.
وسئل عن الإسلام والإيمان والإحسان، فقال: الإسلام حكم، والإيمان وصل، والإحسان ثواب ولهذا الثواب ثواب. فالإسلام الإقرار وهو الظاهر، والإيمان هو الغيب، والإحسان هو التعبد. وربما قال: الإيمان يقين. وسئل عن شرائع الإسلام فقال: قال العلماء فيه فأكثروا، ولكن هي كلمتان: ما آتاكُمُ الرَّسُولُ فَخُذُوهُ وَما نَهاكُمْ عَنْهُ فَانْتَهُوا [الحشر: ٧] ثم قال: هي كلمة واحدة: مَنْ يُطِعِ الرَّسُولَ فَقَدْ أَطاعَ اللَّهَ [النساء: ٨٠] .

<span data-type="title" id=toc-213>[سورة إبراهيم (١٤) : آية ٣٤]</span>
وَآتاكُمْ مِنْ كُلِّ ما سَأَلْتُمُوهُ وَإِنْ تَعُدُّوا نِعْمَتَ اللَّهِ لا تُحْصُوها إِنَّ الْإِنْسانَ لَظَلُومٌ كَفَّارٌ (٣٤)
قوله تعالى: وَإِنْ تَعُدُّوا نِعْمَتَ اللَّهِ لاَ تُحْصُوها [٣٤] بأن جعل السفير فيما بينكم وبينه الأعلى والواسطة الكبرى.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (85,'<span data-type="title" id=toc-214>السورة التي يذكر فيها الحجر</span>

<span data-type="title" id=toc-215>[سورة الحجر (١٥) : آية ٣]</span>
ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الْأَمَلُ فَسَوْفَ يَعْلَمُونَ (٣)
قوله تعالى: ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الْأَمَلُ فَسَوْفَ يَعْلَمُونَ [٣] قال: إذا اجتمعت أربعة في عبد قيل له: إنك لن تنال شيئاً من هذا الأمر، إذا أحب أن يأكل شيئاً طيباً، ويلبس ثوباً ليناً، وينفذ أمره، ويكثر شيئه يقال: هيهات هذا الذي قطع الخلق عن الله تعالى.
وقد حكي أن الله أوحى إلى داود عليه السلام: حذر وأنذر أصحابك أكل الشهوات، فإن القلوب المعلقة بشهوات الدنيا عقولها عني محجوبة «١» . وقال سهل: الأمل أرض كل معصية، والحرص بذر كل معصية، والتسويف ماء كل معصية، والقدرة أرض كل طاعة، واليقين بذر كل طاعة، والعمل ماء كل طاعة. قال: وكان سهل يقوى على الوجد سبعين يوماً لا يأكل فيها طعاماً، وكان يأمر أصحابه أن يأكلوا اللحم في كل جمعة مرة، كيلا يضعفوا عن العبادة، وكان إذا أكل ضعف، وإذا جاع قوي، وكان يعرق في البرد الشديد في الشتاء وعليه قميص واحد، وكان إذا سألوه عن شيء من العلم يقول: لا تسألوني فإنكم لا تنتفعون في هذا الوقت بكلامي. وفد عباس بن عصام يوماً وهو يقول: أنا منذ ثلاثين سنة أكلم الله، والناس يتوهمون أني أكلمهم «٢» .

<span data-type="title" id=toc-216>[سورة الحجر (١٥) : آية ٤٠]</span>
إِلاَّ عِبادَكَ مِنْهُمُ الْمُخْلَصِينَ (٤٠)
قوله تعالى: إِلَّا عِبادَكَ مِنْهُمُ الْمُخْلَصِينَ [٤٠] قال: الناس كلهم أموات إلا العلماء، والعلماء كلهم نيام إلا العاملين، والعاملون كلهم مغترون إلا المخلصين، والمخلصون على خطر عظيم.

<span data-type="title" id=toc-217>[سورة الحجر (١٥) : آية ٧٢]</span>
لَعَمْرُكَ إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ (٧٢)
قوله تعالى: إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ [٧٢] أي في جهلهم وضلالتهم يعصون، واعلم أن المعاصي كلها منسوبة إلى الجهل، والجهل كله منسوب إلى السكر، ويقال هو نفس المسكر.

<span data-type="title" id=toc-218>[سورة الحجر (١٥) : آية ٧٥]</span>
إِنَّ فِي ذلِكَ لَآياتٍ لِلْمُتَوَسِّمِينَ (٧٥)
قوله تعالى: إِنَّ فِي ذلِكَ لَآياتٍ لِلْمُتَوَسِّمِينَ [٧٥] قال: يعني المتفرسين، وقد روى أبو سعيد الخدري رضي الله عنه عن النبي صلى الله عليه وسلّم أنه قال: «اتقوا فراسة المؤمن فإنه ينظر بنور الله ثم قرأ: إِنَّ فِي ذلِكَ لَآياتٍ لِلْمُتَوَسِّمِينَ [٧٥] » «٣» ، ومعناه المتفسرون في السرائر وهو كما قال
_________
(١) الحلية ٥/ ٣٨٢.
(٢) انظر مثل هذا القول في صفوة الصفوة ٣/ ١٨٣.
(٣) نوادر الأصول ٣/ ٨٦ وكشف الخفاء ١/ ٤٢- ٤٣ والمعجم الكبير ٨/ ١٠٢ والمعجم الأوسط ٣/ ٣١٢، ٨/ ٢٣.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (86,'عمر رضي الله عنه لسارية «١» : «الجبل الجبل» «٢» .

<span data-type="title" id=toc-219>[سورة الحجر (١٥) : آية ٨٥]</span>
وَما خَلَقْنَا السَّماواتِ وَالْأَرْضَ وَما بَيْنَهُما إِلاَّ بِالْحَقِّ وَإِنَّ السَّاعَةَ لَآتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيلَ (٨٥)
قوله: فَاصْفَحِ الصَّفْحَ الْجَمِيلَ [٨٥] قال:
حكى محمد بن الحنفية عن علي رضي الله عنه في قوله تعالى: فَاصْفَحِ الصَّفْحَ الْجَمِيلَ [٨٥] قال: هو الرضا بلا عتاب «٣» . وقال سهل: بلا حقد ولا توبيخ بعد الصفح، وهو الإعراض الجميل.

<span data-type="title" id=toc-220>[سورة الحجر (١٥) : الآيات ٩١ الى ٩٢]</span>
الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ (٩١) فَوَ رَبِّكَ لَنَسْئَلَنَّهُمْ أَجْمَعِينَ (٩٢)
قوله تعالى: الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ [٩١] قال: ظاهر الآية ما عليه أهل التفسير وباطنها ما أنزل الله تعالى من أحكامه في السمع والبصر والفؤاد وهو قوله تعالى: إِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤادَ كُلُّ أُولئِكَ كانَ عَنْهُ مَسْؤُلًا [الإسراء: ٣٦] فأعرضوا عن العمل به ميلاً إلى دواعي نفس الطبع. قوله تعالى: فَوَ رَبِّكَ لَنَسْئَلَنَّهُمْ أَجْمَعِينَ [٩٢] قال: هذه الآية فيها خصوص فإن من هذه الأمة من يحشر من القبر إلى الجنة لا يحضر الحساب ولا يشعر بالأهوال وهم الذين قال الله تعالى: أُولئِكَ عَنْها مُبْعَدُونَ [الأنبياء: ١٠١] وقد قال النبي صلى الله عليه وسلّم: «إن أولياء الله يخرجون من قبورهم إلى الجنة لا يقفون للحساب ولا يخافون طول ذلك اليوم، أولئك هم السابقون إلى الجنة رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ذلِكَ الْفَوْزُ الْعَظِيمُ [المائدة: ١١٩] » .

<span data-type="title" id=toc-221>[سورة الحجر (١٥) : آية ٩٤]</span>
فَاصْدَعْ بِما تُؤْمَرُ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ (٩٤)
قوله تعالى: فَاصْدَعْ بِما تُؤْمَرُ [٩٤] أي أظهر القرآن في الصلاة بما أوحينا إليك. قيل:
ما الوحي؟ قال: المستور من القول، قال الله تعالى: يُوحِي بَعْضُهُمْ إِلى بَعْضٍ [الأنعام: ١١٢] أي يسر بعضهم إلى بعض وقد يكون بمعنى الإلهام كما قال تعالى: وَأَوْحى رَبُّكَ إِلَى النَّحْلِ [النحل: ٦٨] يعني ألهم النحل.
قوله: وَلَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِما يَقُولُونَ (٩٧) فَسَبِّحْ بِحَمْدِ رَبِّكَ [٩٧، ٩٨] أي صلِّ لله تعالى واذكره، فكأن الله تعالى قال له: إن ضاق صدرك بقرب الكفار بكذبهم، بما وصفوا لك من الضد والند والشريك بجهلهم وحسدهم، فارجع إلى مشاهدتنا وقربنا بذكرنا، فإن قربك فينا، وسرورك بذكرنا ومشاهدتنا، واصبر على ذلك، فإن رضاي فيك. وقد حكي أن موسى عليه السلام قال: إلهي دلني على عمل إن أنا عملته نلت به رضاك. قال: فأوحى إليه: يا ابن عمران، إن رضاي في كرهك ولن تطيق ذلك. قال: فخر موسى عليه السلام ساجداً باكياً، وقال: إلهي خصصتني منك بالكلام، فلم تكلم بشراً قبلي، ولم تدلني على عمل أنال به رضاك.
فأوحى الله تعالى إليه: إن رضاي في رضاك بقضائي.
_________
(١) سارية بن زنيم الدؤلي ( ... - نحو ٣٠ هـ) : صحابي، من الشعراء، القادة، الفاتحين. كان من العدائين، جعله عمر أميرا على جيش، وسيره إلى بلاد فارس، ففتح بلادا، منها: أصبهان. (الأعلام ٣/ ٦٩- ٧٠) .
(٢) كشف الخفاء ٢/ ٥١٤- ٥١٥ وفضائل الصحابة ١/ ٢٦٩ والإصابة ٣/ ٥، ٦.
(٣) قوت القلوب ١/ ٣٩٣.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (87,'<span data-type="title" id=toc-222>السورة التي يذكر فيها النحل</span>

<span data-type="title" id=toc-223>[سورة النحل (١٦) : آية ٨]</span>
وَالْخَيْلَ وَالْبِغالَ وَالْحَمِيرَ لِتَرْكَبُوها وَزِينَةً وَيَخْلُقُ مَا لاَ تَعْلَمُونَ (٨)
سئل عن قوله تعالى: وَيَخْلُقُ مَا لاَ تَعْلَمُونَ [٨] قال: أما ظاهر الآية ما حكاه ابن عباس رضي الله عنهما عن النبي صلى الله عليه وسلّم أنه قال: «إن مما خلق الله تعالى أرضاً من لؤلؤة بيضاء مسيرة ألف عام في ألف عام، عليها جبل من ياقوتة حمراء، تحيط بها سماء تلك الأرض، فيها ملك قد ملأ شرقها وغربها، له ستمائة وستون ألف رأس، في كل رأس ستمائة وستون فم، في كل فم ستمائة ألف لسان، يثني على الله بكل لسان ستمائة وستين ألف مرة في كل يوم، فإذا كان يوم القيامة نظر إلى عظمة الله تعالى فقال: وعزتك وجلالك ما عبدتك حق عبادتك» ، قال الله تعالى: وَيَخْلُقُ مَا لاَ تَعْلَمُونَ [٨] وباطنها علمك الحق جل جلاله الوقوف عند ما لا يدركه عقلك من آثار الصنع وفنون العلم أن يقابله بالإنكار، فإنه خلق ما لا تعلمه أنت، ولا أحد من خلقه إلا من علمه الحق عزَّ وجلَّ.

<span data-type="title" id=toc-224>[سورة النحل (١٦) : الآيات ١١ الى ١٢]</span>
يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخِيلَ وَالْأَعْنابَ وَمِنْ كُلِّ الثَّمَراتِ إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ (١١) وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهارَ وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومُ مُسَخَّراتٌ بِأَمْرِهِ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَعْقِلُونَ (١٢)
وسئل عن قوله: مِنْ كُلِّ الثَّمَراتِ إِنَّ فِي ذلِكَ لَآيَةً [١١] وقال بعدها: إِنَّ فِي ذلِكَ لَآياتٍ [١٢] فقال: لأن الثمرات من نوع واحد، والليل والنهار نوعان، وكذلك الشمس والقمر، فقال: لَآياتٍ لِقَوْمٍ يَعْقِلُونَ [١٢] . واعلم أن الله تعالى لما أراد إظهار علمه أودع علمه العقل، وحكم أنه لا يصل أحد إلى شيء منه إلا بالعقل، فمن فاته العقل فقد فاته العلم.

<span data-type="title" id=toc-225>[سورة النحل (١٦) : آية ٢١]</span>
أَمْواتٌ غَيْرُ أَحْياءٍ وَما يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ (٢١)
قوله: أَمْواتٌ غَيْرُ أَحْياءٍ وَما يَشْعُرُونَ [٢١] قال سهل: خلق الله تعالى الخلق، ثم أحياهم باسم الحياة، ثم أماتهم بجهلهم بأنفسهم، فمن كانت حياته بالعلم فهو الحي، وإلا فهم الأموات بجهلهم.

<span data-type="title" id=toc-226>[سورة النحل (١٦) : آية ٣٦]</span>
وَلَقَدْ بَعَثْنا فِي كُلِّ أُمَّةٍ رَسُولاً أَنِ اعْبُدُوا اللَّهَ وَاجْتَنِبُوا الطَّاغُوتَ فَمِنْهُمْ مَنْ هَدَى اللَّهُ وَمِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلالَةُ فَسِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كانَ عاقِبَةُ الْمُكَذِّبِينَ (٣٦)
قوله تعالى: وَلَقَدْ بَعَثْنا فِي كُلِّ أُمَّةٍ رَسُولًا أَنِ اعْبُدُوا اللَّهَ [٣٦] قال: العبادة زينة العارفين، وأحسن ما يكون العارف إذا كان في ميادين العبودية والخدمة، يترك ما له لما عليه.

<span data-type="title" id=toc-227>[سورة النحل (١٦) : آية ٥٣]</span>
وَما بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللَّهِ ثُمَّ إِذا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْئَرُونَ (٥٣)
قوله: وَما بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللَّهِ ثُمَّ إِذا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْئَرُونَ [٥٣] قال سهل:
لو أن الله تعالى طالب حملة العرش، فمن دونهم من الملائكة ومن النبيين والمرسلين، بما جهلوا من',NULL,'90',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (88,'نعمة الله عليهم لعذبهم عليها، وهو غير ظالم. قيل لسهل: أي شيء يفعل الله بعبده إذا أحبه؟
قال: يلهمه الاستغفار عند التقصير، والشكر له عند النعمة، وإنما أرادوا بالنية أن يتعرفوا بها نعم الله تعالى عليهم، فيدوم لهم الشكر ويدوم لهم المزيد. ثُمَّ إِذا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْئَرُونَ [٥٣] يعني إياه تدعون عند الفقر والبلاء، وربما يكون ذلك نعمة من الله عليكم، إذ لو شاء لابتلاكم بأشد منه، فيصير ذلك عند أشد البلاء نعمة، فيجزعون منه، ولا يصبرون ولا يشكرون. وبلغنا أن الله تعالى أوحى إلى داود عليه السلام فقال: اصبر على المئونة تأتك مني المعونة «١» .

<span data-type="title" id=toc-228>[سورة النحل (١٦) : آية ٥٥]</span>
لِيَكْفُرُوا بِما آتَيْناهُمْ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ (٥٥)
قوله تعالى: فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ [٥٥] قال: هذا وعد من الله تعالى لكفار مكة على تكذيبهم، مع ما أنعم الله عليهم في الدنيا، أنهم سيعلمون جزاء ذلك في الآخرة، وهذه الآية أيضاً وعيد شديد للغافلين على ما قال الرسول صلّى الله عليه وسلّم: «من أخذ من الدنيا نهمته حيل بينه وبين نهمته في الآخرة حلالها حساب وحرامها عقاب» «٢» ، وإنما يحاسب المؤمنون بما أخذوا من الحلال فضلاً على ما يكفيهم، فأما من أخذ البلغة من الحلال فهو داخل تحت قوله صلّى الله عليه وسلّم: «ليس من الدنيا كسرة يسد بها المؤمن جوعته، وثوب يواري به عورته ويؤدي فيه فرضه، وبيت يكنه من حر الشمس وبرد الشتاء» «٣» .

<span data-type="title" id=toc-229>[سورة النحل (١٦) : آية ٦٧]</span>
وَمِنْ ثَمَراتِ النَّخِيلِ وَالْأَعْنابِ تَتَّخِذُونَ مِنْهُ سَكَراً وَرِزْقاً حَسَناً إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَعْقِلُونَ (٦٧)
قوله: تَتَّخِذُونَ مِنْهُ سَكَراً وَرِزْقاً حَسَناً [٦٧] قال: هذه الآية نسخت بآية الخمر، كذا قال إبراهيم «٤» والشعبي «٥» . قال سهل: السكر عندي ما يسكر النفس في الدنيا، ولا تؤمن عاقبته في الآخرة. وقد دخل على سهل أبو حمزة الصوفي «٦» فقال: أين كنت يا أبا حمزة؟ قال:
_________
(١) فيض القدير ٢/ ٣١٨، وأعاده في ٢/ ٣١٩ وبعده: (وإذا رأيت لي طالبا فكن له خادما) .
(٢) الترغيب والترهيب ٤/ ٧٧ (رقم ٤٨٦٨) وشعب الإيمان ٧/ ١٢٥ (رقم ٩٧٢٢) . [.....]
(٣) في الترغيب والترهيب ٤/ ٧٧ (رقم ٤٨٧٠) : (روي عن ثوبان رضي الله عنه قال: قلت: يا رسول الله، ما يكفيني من الدنيا؟ قال: ما سدّ جوعتك، ووارى عورتك، وإن كان لك بيت يظلك فذاك، وإن كانت لك دابة فبخ. رواه الطبراني في الأوسط) .
(٤) إبراهيم بن أدهم بن منصور، التميمي، البلخي ( ... - ١٦١ هـ) : زاهد مشهور، تفقه ورحل إلى بغداد، وجال في العراق والشام والحجاز، وأخذ عن كثير من العلماء فيها. (الحلية ٧/ ٣٦٧) .
(٥) الشعبي: عامر بن شراحيل بن عبد بن ذي كبار الشعبي الحميري (١٩- ١٠٣ هـ) : راوية، من التابعين، يضرب المثل بحفظه. كان فقيها، شاعرا. استقضاه عمر بن عبد العزيز. (الحلية ٣/ ٤١٠، وتاريخ بغداد ١٢/ ٢٢٧) .
(٦) أبو حمزة الصوفي: محمد بن إبراهيم الصوفي ( ... - ٢٧٠ هـ) : أستاذ البغداديين في التصوف، وكان عالما بالقراءات. (تاريخ بغداد ١/ ٣٩٠) .',NULL,'91',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (89,'كنا عند فلان أخبرنا أن السكر أربعة. فقال: اعرضها علي. فقال: سكر الشراب وسكر الشباب وسكر المال وسكر السلطنة. فقال: وسكرتان لم يخبرك بهما. فقال: ما هما؟ فقال: سكر العالم إذا أحب الدنيا وسكر العابد إذا أحب أن يشار إليه.

<span data-type="title" id=toc-230>[سورة النحل (١٦) : آية ٧٢]</span>
وَاللَّهُ جَعَلَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْواجاً وَجَعَلَ لَكُمْ مِنْ أَزْواجِكُمْ بَنِينَ وَحَفَدَةً وَرَزَقَكُمْ مِنَ الطَّيِّباتِ أَفَبِالْباطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللَّهِ هُمْ يَكْفُرُونَ (٧٢)
قوله تعالى: وَجَعَلَ لَكُمْ مِنْ أَزْواجِكُمْ بَنِينَ وَحَفَدَةً [٧٢] قال: روي عن ابن مسعود رضي الله عنه أنه قال: الحفدة الأختان. وعن ابن عباس رضي الله عنهما أنه قال: البنون الصغار الصغار، والحفدة الذين يعينون الوالد على عمله. وعن الضحاك «١» قال: الحفدة الخدمة لله إيجاباً بغير سؤال منهم غيره.

<span data-type="title" id=toc-231>[سورة النحل (١٦) : آية ٨٨]</span>
الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ زِدْناهُمْ عَذاباً فَوْقَ الْعَذابِ بِما كانُوا يُفْسِدُونَ (٨٨)
قوله: زِدْناهُمْ عَذاباً فَوْقَ الْعَذابِ [٨٨] قال: حكى جابر بن عبد الله رضي الله عنه أنه سأل النبي صلى الله عليه وسلّم عن هذه الزيادة ما هي، فقال له رسول الله صلّى الله عليه وسلّم: «الزيادة خمسة أنهار تخرج من تحت العرش على رؤوس أهل النار الجاحدين بالله ورسوله، ثلاثة أنهار على مقدار الليل ونهران على مقدار النهار تجري ناراً أبداً ما داموا فيها» «٢» .

<span data-type="title" id=toc-232>[سورة النحل (١٦) : آية ٩٠]</span>
إِنَّ اللَّهَ يَأْمُرُ بِالْعَدْلِ وَالْإِحْسانِ وَإِيتاءِ ذِي الْقُرْبى وَيَنْهى عَنِ الْفَحْشاءِ وَالْمُنْكَرِ وَالْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ (٩٠)
قوله تعالى: إِنَّ اللَّهَ يَأْمُرُ بِالْعَدْلِ وَالْإِحْسانِ [٩٠] قال: العدل قول لا إله إلا الله وأن محمداً رسول الله، والاقتداء بسنة نبيه صلّى الله عليه وسلّم، وَالْإِحْسانِ [٩٠] أن يحسن بعضكم إلى بعض، وَإِيتاءِ ذِي الْقُرْبى [٩٠] أي من رزقه الله فضلاً فليُعطِ من استرعاه الله أمره من أقاربه، والْفَحْشاءِ [٩٠] الكذب والغيبة والبهتان، وما كان من الأقوال، وَالْمُنْكَرِ [٩٠] ارتكاب المعاصي، وما كان من الأفعال، يَعِظُكُمْ [٩٠] يؤدبكم بألطف أدب، وينبهكم بأحسن الانتباه، لَعَلَّكُمْ تَذَكَّرُونَ [٩٠] أي تتعظون وتنتهون. قال سهل: الناس نيام فإذا ماتوا انتبهوا «٣» .

<span data-type="title" id=toc-233>[سورة النحل (١٦) : آية ٩٧]</span>
مَنْ عَمِلَ صالِحاً مِنْ ذَكَرٍ أَوْ أُنْثى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَياةً طَيِّبَةً وَلَنَجْزِيَنَّهُمْ أَجْرَهُمْ بِأَحْسَنِ ما كانُوا يَعْمَلُونَ (٩٧)
قوله تعالى: مَنْ عَمِلَ صالِحاً مِنْ ذَكَرٍ أَوْ أُنْثى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَياةً طَيِّبَةً [٩٧] قال: الحياة هي أن ينزع من العبد تدبيره، ويرد إلى تدبير الحق فيه «٤» .
_________
(١) الضحاك بن مزاحم البلخي الخراساني ( ... - ١٠٥ هـ) : مفسر، كان يؤدب الأطفال، ويقال: كان في مدرسته ثلاثة آلاف صبي. (الأعلام ٣/ ٢١٥) .
(٢) لم أجد الحديث في مصادر الحديث، وفي تفسير ابن كثير ٢/ ٦٠٣ عن ابن عباس قال: (هي خمسة أنهار تحت العرش يعذبون ببعضها في الليل، وببعضها في النهار) .
(٣) في كتاب الزهد الكبير ٢/ ٢٠٧ أنه قال: (الناس نيام فإذا انتبهوا ندموا، وإذا ندموا لم تنفعهم ندامتهم) ، وفي كشف الخفاء ٢/ ٤١٤، ٥٢٥ أنه من قول علي بن أبي طالب، ثم قال في ٢/ ٤١٤: (عزاه الشعراني في الطبقات إلى سهل التستري وفي فيض القدير ٥/ ٥٦ أنه حديث نبوي، وفي الحلية ٧/ ٥٢ أنه لسفيان الثوري.
(٤) تفسير القرطبي ١٠/ ١٧٤.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (90,'<span data-type="title" id=toc-234>[سورة النحل (١٦) : آية ١١٠]</span>
ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هاجَرُوا مِنْ بَعْدِ مَا فُتِنُوا ثُمَّ جاهَدُوا وَصَبَرُوا إِنَّ رَبَّكَ مِنْ بَعْدِها لَغَفُورٌ رَحِيمٌ (١١٠)
قوله: ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هاجَرُوا مِنْ بَعْدِ مَا فُتِنُوا ثُمَّ جاهَدُوا وَصَبَرُوا [١١٠] قال سهل: هاجروا يعني هجروا قرناء السوء بعد أن ظهرت الفتنة منهم في صحبتهم، ثم جاهدوا أنفسهم على ملازمة أهل الخير، ثم صبروا على ذلك، ولم يرجعوا إلى ما كانوا عليه في بدء الأحوال. وقد سأل رجل سهلاً فقال: إن معي مالاً، ولي قوة وأريد الجهاد، فما تأمرني؟
فقال له سهل: المال العلم، والقوة النية، والجهاد مجاهدة النفس، لا يقبل العافية فيما حرم الله تعالى إلا نبي أو صديق، فقيل لأبي عثمان: ما معنى قوله: «إلا نبي أو صديق» ؟ فقال: لا يدخل في شيء لا تقوم له.

<span data-type="title" id=toc-235>[سورة النحل (١٦) : آية ١١٩]</span>
ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهالَةٍ ثُمَّ تابُوا مِنْ بَعْدِ ذلِكَ وَأَصْلَحُوا إِنَّ رَبَّكَ مِنْ بَعْدِها لَغَفُورٌ رَحِيمٌ (١١٩)
قوله: ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهالَةٍ ثُمَّ تابُوا مِنْ بَعْدِ ذلِكَ وَأَصْلَحُوا [١١٩] قال سهل: ما عصى الله تعالى أحد إلا بجهل، ورُبَّ جهلٍ أورث علماً، والعلم مفتاح التوبة، والإصلاح صحة التوبة، فمن لم يصلح توبته فعن قريب تفسد توبته، لأن الله تعالى يقول:
ثُمَّ تابُوا مِنْ بَعْدِ ذلِكَ وَأَصْلَحُوا [١١٩] ، وسئل سهل عن الجاهل، فقال: الذي يكون إمام نفسه، ولا يكون له إمام صالح يقتدي به.

<span data-type="title" id=toc-236>[سورة النحل (١٦) : آية ١٢٧]</span>
وَاصْبِرْ وَما صَبْرُكَ إِلاَّ بِاللَّهِ وَلا تَحْزَنْ عَلَيْهِمْ وَلا تَكُ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ (١٢٧)
قوله: وَاصْبِرْ وَما صَبْرُكَ إِلَّا بِاللَّهِ [١٢٧] قال سهل: واصبر واعلم أنه لا معين على الأمور إلا الله تعالى، والله سبحانه وتعالى أعلم.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (91,'<span data-type="title" id=toc-237>السورة التي يذكر فيها الإسراء</span>

<span data-type="title" id=toc-238>[سورة الإسراء (١٧) : آية ٨]</span>
عَسى رَبُّكُمْ أَنْ يَرْحَمَكُمْ وَإِنْ عُدْتُمْ عُدْنا وَجَعَلْنا جَهَنَّمَ لِلْكافِرِينَ حَصِيراً (٨)
قوله تعالى: وَإِنْ عُدْتُمْ عُدْنا [٨] قال سهل: يعني إن عدتم إلى المعصية عدنا إلى المغفرة، وإن عدتم إلى الإعراض عنا عدنا إلى الإقبال عليكم، وإن عدتم إلى الفرار منا عدنا إلى أخذ الطرق عليكم، ارجعوا إلينا فإن الطريق علينا.

<span data-type="title" id=toc-239>[سورة الإسراء (١٧) : آية ١١]</span>
وَيَدْعُ الْإِنْسانُ بِالشَّرِّ دُعاءَهُ بِالْخَيْرِ وَكانَ الْإِنْسانُ عَجُولاً (١١)
قوله: وَيَدْعُ الْإِنْسانُ بِالشَّرِّ دُعاءَهُ بِالْخَيْرِ [١١] قال سهل: أسلم الدعوات الذكر وترك الاختيار بالسؤال والدعاء، لأن في الذكر الكفاية، وربما يدعو الإنسان ويسأل ما فيه هلاكه وهو لا يشعر، ألا ترى أن الله تعالى يقول: وَيَدْعُ الْإِنْسانُ بِالشَّرِّ دُعاءَهُ بِالْخَيْرِ [١١] والذاكر على الدوام التارك للاختيار والدعاء والسؤال مبذول له أفضل الرغائب، وساقط عنه آفات السؤال والاختيار، ولذلك قال رسول الله صلّى الله عليه وسلّم: «يقول الله: من شغله ذكري عن مسألتي أعطيته أفضل ما أعطي السائلين» «١» .

<span data-type="title" id=toc-240>[سورة الإسراء (١٧) : الآيات ١٣ الى ١٤]</span>
وَكُلَّ إِنسانٍ أَلْزَمْناهُ طائِرَهُ فِي عُنُقِهِ وَنُخْرِجُ لَهُ يَوْمَ الْقِيامَةِ كِتاباً يَلْقاهُ مَنْشُوراً (١٣) اقْرَأْ كِتابَكَ كَفى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيباً (١٤)
قوله تعالى: وَكُلَّ إِنسانٍ أَلْزَمْناهُ طائِرَهُ فِي عُنُقِهِ [١٣] قال: علمه أي ما كان من خير وشر.
قوله: اقْرَأْ كِتابَكَ كَفى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيباً [١٤] قال: حكي عن الحسن البصري أنه قال: أعد للسؤال جواباً وللجواب صواباً، وإلا فأعد للنار جلباباً. وقال عمر رضي الله عنه: حاسبوا أنفسكم قبل أن تحاسبوا وزنوا أنفسكم قبل أن توزنوا وتأهبوا للعرض الأكبر قبل أن تعرضوا «٢» . فسئل سهل عن المحاسبة والموازنة، فقال: المحاسبة على وجهين: محاسبة فيما بين العبد وربه، وهو سر، ومحاسبة فيما بينه وبين الخلق وهي علانية، والموازنة إذا استقبلك فرضان أو سنتان أو نافلتان نظرت أيهما أقرب إلى الله وأوزن عنده، فابتدأت به.
_________
(١) نوادر الأصول ٣/ ٦٤، ٢٥٩ ومسند ابن أبي شيبة ٦/ ٣٤ وفتح الباري ١١/ ١٣٤ ومسند الشهاب ١/ ٣٤، ٢/ ٣٢٦.
(٢) تقدم قول عمر بن الخطاب في نهاية تفسير سورة التوبة.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (92,'<span data-type="title" id=toc-241>[سورة الإسراء (١٧) : آية ٢٥]</span>
رَبُّكُمْ أَعْلَمُ بِما فِي نُفُوسِكُمْ إِنْ تَكُونُوا صالِحِينَ فَإِنَّهُ كانَ لِلْأَوَّابِينَ غَفُوراً (٢٥)
قوله: رَبُّكُمْ أَعْلَمُ بِما فِي نُفُوسِكُمْ [٢٥] أي بما في قلوبكم، لأن القلب يجمع العقل والنفس والهوى.
قوله: إِنْ تَكُونُوا صالِحِينَ فَإِنَّهُ كانَ لِلْأَوَّابِينَ غَفُوراً [٢٥] قال ابن المسيب:
الأواب الذي يذنب ثم يتوب، ثم يذنب ثم يتوب، ويموت على توبته «١» . وقال الحسن: الأواب التائب الذي لا يكون معه وقتان، إنما هو مهيئ للتوبة كل لمحة ولحظة. وحكي عن ضمرة بن حبيب «٢» عن النبي صلى الله عليه وسلّم أنه قال: «من فتح له باب خير فلينتهزه فإنه لا يدري متى يغلق عنه» «٣» يعني فليعتبر وقته ولا يؤخر.

<span data-type="title" id=toc-242>[سورة الإسراء (١٧) : آية ٣٦]</span>
وَلا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ إِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤادَ كُلُّ أُولئِكَ كانَ عَنْهُ مَسْؤُلاً (٣٦)
قوله تعالى: وَلا تَقْفُ ما لَيْسَ لَكَ بِهِ عِلْمٌ [٣٦] أي لا تبغ ما ليس لك به علم كما قال رسول الله صلّى الله عليه وسلّم: «نحن بنو النضر بن كنانة، لا نقفو أمنا ولا ننتفي من أبينا» «٤» ، يعني آباء العرب.

<span data-type="title" id=toc-243>[سورة الإسراء (١٧) : آية ٥٧]</span>
أُولئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلى رَبِّهِمُ الْوَسِيلَةَ أَيُّهُمْ أَقْرَبُ وَيَرْجُونَ رَحْمَتَهُ وَيَخافُونَ عَذابَهُ إِنَّ عَذابَ رَبِّكَ كانَ مَحْذُوراً (٥٧)
قوله تعالى: وَيَرْجُونَ رَحْمَتَهُ وَيَخافُونَ عَذابَهُ [٥٧] قال: رحمته جنته في الظاهر وفي الباطن حقيقة المعروف. ثم قال: إن الخوف والرجاء زمان للإنسان فإذا استوى قامت له أحواله، وإذا رجح أحدهما بطل الآخر «٥» ، ألا ترى أن النبي صلّى الله عليه وسلّم يقول: «لو وزن رجاء المؤمن وخوفه لاعتدلا» «٦» .

<span data-type="title" id=toc-244>[سورة الإسراء (١٧) : الآيات ٦٧ الى ٦٩]</span>
وَإِذا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ إِلاَّ إِيَّاهُ فَلَمَّا نَجَّاكُمْ إِلَى الْبَرِّ أَعْرَضْتُمْ وَكانَ الْإِنْسانُ كَفُوراً (٦٧) أَفَأَمِنْتُمْ أَنْ يَخْسِفَ بِكُمْ جانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حاصِباً ثُمَّ لا تَجِدُوا لَكُمْ وَكِيلاً (٦٨) أَمْ أَمِنْتُمْ أَنْ يُعِيدَكُمْ فِيهِ تارَةً أُخْرى فَيُرْسِلَ عَلَيْكُمْ قاصِفاً مِنَ الرِّيحِ فَيُغْرِقَكُمْ بِما كَفَرْتُمْ ثُمَّ لا تَجِدُوا لَكُمْ عَلَيْنا بِهِ تَبِيعاً (٦٩)
قوله تعالى: وَإِذا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ إِلَّا إِيَّاهُ [٦٧] أي ما تسألون كشفه إلا منه، وتتبرؤون من حولكم وقوتكم، وتعترفون بحوله وقوته، وهذه الآية رد على أهل القدر الذين يدّعون الاستطاعة لأنفسهم دون الله تعالى. أَفَأَمِنْتُمْ أَنْ يَخْسِفَ بِكُمْ جانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حاصِباً [٦٨] ، وقال: فَيُرْسِلَ عَلَيْكُمْ قاصِفاً مِنَ الرِّيحِ فَيُغْرِقَكُمْ [٦٩] فإن كانت لهم استطاعة فليدفعوا عن أنفسهم العذاب.
_________
(١) لسان العرب (أوب) .
(٢) ضمرة بن حبيب بن صهيب الزبيدي، أبو عتبة الحمصي ( ... - ١٣٠ هـ) : تابعي، شامي، كان ثقة، وكان مؤذن المسجد الجامع بدمشق. (تهذيب التهذيب ٤/ ٤٠٢) .
(٣) مسند الشهاب ١/ ٢٦٨.
(٤) مسند أحمد ٥/ ٢١١- ٢١٢ والمعجم الكبير ١/ ٢٣٥، ٢/ ٢٨٦ وسنن ابن ماجة ٢/ ٨٧١ رقم ٢٦١٢. [.....]
(٥) تفسير القرطبي ١٠/ ٢٨٠.
(٦) مصنف ابن أبي شيبة ٧/ ١٧٨ وشعب الإيمان ٢/ ١٢.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (93,'<span data-type="title" id=toc-245>[سورة الإسراء (١٧) : آية ٧٢]</span>
وَمَنْ كانَ فِي هذِهِ أَعْمى فَهُوَ فِي الْآخِرَةِ أَعْمى وَأَضَلُّ سَبِيلاً (٧٢)
قوله تعالى: وَمَنْ كانَ فِي هذِهِ أَعْمى فَهُوَ فِي الْآخِرَةِ أَعْمى [٧٢] أي من كان في الدنيا أعمى القلب عن أداء شكر نعم الله تعالى عليه ظاهرة وباطنة فهو في الآخرة أعمى عن رؤية المنعم.

<span data-type="title" id=toc-246>[سورة الإسراء (١٧) : آية ٨٠]</span>
وَقُلْ رَبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ وَأَخْرِجْنِي مُخْرَجَ صِدْقٍ وَاجْعَلْ لِي مِنْ لَدُنْكَ سُلْطاناً نَصِيراً (٨٠)
قوله: وَقُلْ رَبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ [٨٠] يعني أدخلني في تبليغ الرسالة مدخل صدق، وهو أن لا يكون لي إلى أحد ميل، وإني لا أقصر في حدود التبليغ وشروطه، وأخرجني من ذلك على السلامة وطلب رضاك منه على الموافقة، وَاجْعَلْ لِي مِنْ لَدُنْكَ سُلْطاناً نَصِيراً [٨٠] أي زيّنّي بزينة جبروتك ليكون الغالب عليهم سلطان الحق لا سلطان الهوى. وسمعت سهلاً مرة أخرى يقول: وَاجْعَلْ لِي مِنْ لَدُنْكَ سُلْطاناً نَصِيراً [٨٠] لساناً ينطق عنك، ولا ينطق عن غيرك «١» .

<span data-type="title" id=toc-247>[سورة الإسراء (١٧) : آية ١٠٧]</span>
قُلْ آمِنُوا بِهِ أَوْ لا تُؤْمِنُوا إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ إِذا يُتْلى عَلَيْهِمْ يَخِرُّونَ لِلْأَذْقانِ سُجَّداً (١٠٧)
قوله تعالى: إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ إِذا يُتْلى عَلَيْهِمْ يَخِرُّونَ لِلْأَذْقانِ سُجَّداً [١٠٧] قال سهل: لا يؤثر شيء على السر مثل ما يؤثر عليه سماع القرآن، فإن العبد إذا سمع خشع سره، وأنار ذلك قلبه بالبراهين الصادقة، وزين جوارحه بالتذلل والانقياد، والله سبحانه وتعالى أعلم.
_________
(١) الحلية ١٠/ ١٩٥ وقوت القلوب ١/ ٢٨١.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (94,'<span data-type="title" id=toc-248>السورة التي يذكر فيها الكهف</span>

<span data-type="title" id=toc-249>[سورة الكهف (١٨) : آية ٧]</span>
إِنَّا جَعَلْنا ما عَلَى الْأَرْضِ زِينَةً لَها لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلاً (٧)
قوله تعالى: لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلًا [٧] قال: أيهم أحسن إعراضاً عن الدنيا، وما يوجب الاشتغال عن الله تعالى، وإخباتاً وسكوناً إلينا، وعلينا توكلاً وإقبالاً.

<span data-type="title" id=toc-250>[سورة الكهف (١٨) : الآيات ٩ الى ١٠]</span>
أَمْ حَسِبْتَ أَنَّ أَصْحابَ الْكَهْفِ وَالرَّقِيمِ كانُوا مِنْ آياتِنا عَجَباً (٩) إِذْ أَوَى الْفِتْيَةُ إِلَى الْكَهْفِ فَقالُوا رَبَّنا آتِنا مِنْ لَدُنْكَ رَحْمَةً وَهَيِّئْ لَنا مِنْ أَمْرِنا رَشَداً (١٠)
وسئل عن قوله: الرَّقِيمِ [٩] فقال: الرقيم هو رئيسهم، وهو المسمى بالكلب، وليس بكلب لهم، قال الله تعالى: وَكَلْبُهُمْ باسِطٌ ذِراعَيْهِ بِالْوَصِيدِ [١٨] أي باسط ذراعيه بالأمر والنهي. وقال عكرمة: الرقيم الدواة بلسان الروم. وقال الحسن: الرقيم الوادي الذي فيه الكهف.
وقال كعب: الرقيم لوح من رصاص فيه أسماؤهم وأنسابهم ودينهم وممن هربوا، وأما الوصيد فهو فناء الباب.
قوله تعالى: آتِنا مِنْ لَدُنْكَ رَحْمَةً [١٠] أي احفظنا على ذكرك.

<span data-type="title" id=toc-251>[سورة الكهف (١٨) : آية ١٣]</span>
نَحْنُ نَقُصُّ عَلَيْكَ نَبَأَهُمْ بِالْحَقِّ إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ وَزِدْناهُمْ هُدىً (١٣)
قوله تعالى: إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ [١٣] قال سهل: إنما سماهم فتية لأنهم آمنوا به بلا واسطة، وقاموا إليه بإسقاط العلائق عن أنفسهم.
قوله تعالى: وَزِدْناهُمْ هُدىً [١٣] أي بصيرة في الإيمان.

<span data-type="title" id=toc-252>[سورة الكهف (١٨) : الآيات ١٧ الى ١٨]</span>
وَتَرَى الشَّمْسَ إِذا طَلَعَتْ تَتَزاوَرُ عَنْ كَهْفِهِمْ ذاتَ الْيَمِينِ وَإِذا غَرَبَتْ تَقْرِضُهُمْ ذاتَ الشِّمالِ وَهُمْ فِي فَجْوَةٍ مِنْهُ ذلِكَ مِنْ آياتِ اللَّهِ مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِيًّا مُرْشِداً (١٧) وَتَحْسَبُهُمْ أَيْقاظاً وَهُمْ رُقُودٌ وَنُقَلِّبُهُمْ ذاتَ الْيَمِينِ وَذاتَ الشِّمالِ وَكَلْبُهُمْ باسِطٌ ذِراعَيْهِ بِالْوَصِيدِ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِراراً وَلَمُلِئْتَ مِنْهُمْ رُعْباً (١٨)
قوله تعالى: وَمَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِيًّا مُرْشِداً [١٧] قال: من يرد الله منه إظهار ما علم منه من الشقاوة بترك العصمة إياه، فلن تجد له عاصماً منه.
قوله تعالى: لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِراراً [١٨] يعني لو اطلعت عليهم بنفسك لوليت منهم فراراً، ولو اطلعت عليهم بالحق لوقفت على حقائق الوحدانية فيهم منه.

<span data-type="title" id=toc-253>[سورة الكهف (١٨) : آية ٢١]</span>
وَكَذلِكَ أَعْثَرْنا عَلَيْهِمْ لِيَعْلَمُوا أَنَّ وَعْدَ اللَّهِ حَقٌّ وَأَنَّ السَّاعَةَ لا رَيْبَ فِيها إِذْ يَتَنازَعُونَ بَيْنَهُمْ أَمْرَهُمْ فَقالُوا ابْنُوا عَلَيْهِمْ بُنْياناً رَبُّهُمْ أَعْلَمُ بِهِمْ قالَ الَّذِينَ غَلَبُوا عَلى أَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِداً (٢١)
قوله تعالى: قالَ الَّذِينَ غَلَبُوا عَلى أَمْرِهِمْ [٢١] قال: ظاهرها الولاية، وباطنها نفس الروح وفهم العقل وفطنة القلب بالذكر لله عزَّ وجلّ.

<span data-type="title" id=toc-254>[سورة الكهف (١٨) : آية ٢٨]</span>
وَاصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَداةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ وَلا تَعْدُ عَيْناكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَياةِ الدُّنْيا وَلا تُطِعْ مَنْ أَغْفَلْنا قَلْبَهُ عَنْ ذِكْرِنا وَاتَّبَعَ هَواهُ وَكانَ أَمْرُهُ فُرُطاً (٢٨)
قوله تعالى: وَلا تُطِعْ مَنْ أَغْفَلْنا قَلْبَهُ عَنْ ذِكْرِنا [٢٨] قال: الغفلة إبطال الوقت بالبطالة. وقال: إن للقلب ألف موت، آخرها القطيعة عن الله عزَّ وجلَّ، وإن للقلب ألف حياة، آخرها لقاء الحق عزَّ وجلَّ، وإن في كل معصية للقلب موتاً، وفي كل طاعة للقلب حياة.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (95,'<span data-type="title" id=toc-255>[سورة الكهف (١٨) : آية ٣٠]</span>
إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ إِنَّا لاَ نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلاً (٣٠)
قوله تعالى: إِنَّا لاَ نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلًا [٣٠] قال: حسن العمل الاستقامة عليه بالسنة، وإنما مثل السنة في الدنيا مثل الجنة في الآخرة، ومن دخل الجنة سلم، كذلك من لزم السنة في الدنيا سلم من الآفات. وقال مالك بن أنس رضي الله عنه: لو أن رجلاً ارتكب جميع الكبائر ثم لم يكن فيه شيء من هذه الأهواء والبدع لرجوت له. ثم قال: من مات على السنة فليبشر ثلاث مرات. وقال سهل: لا يرفع الحجاب عن العبد حتى يدفن نفسه في الثرى. قيل له: كيف يدفن نفسه في الثرى؟ قال: يميتها على السنة، ويدفنها في اتباع السنة، لأن لكل شيء من مقامات العابدين مثل الخوف والرجاء والحب والشوق والزهد والرضا والتوكل غايةً، إلا السنة فإنه ليست لها غاية ونهاية. وسئل عن معنى قوله: «ليست للسنة غاية» متى بن أحمد فقال: لا يكون لأحد مثل خوف النبي صلّى الله عليه وسلّم أو حبه أو شوقه، أو زهده أو رضاه أو توكله أو أخلاقه، وقد قال الله تعالى: وَإِنَّكَ لَعَلى خُلُقٍ عَظِيمٍ [القلم: ٤] . وسئل عن معنى قوله صلّى الله عليه وسلّم: «أجيعوا أنفسكم وأعروها» «١» ، فقال: أجيعوا أنفسكم إلى العلم وأعروها عن الجهل.

<span data-type="title" id=toc-256>[سورة الكهف (١٨) : آية ٣٩]</span>
وَلَوْلا إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شاءَ اللَّهُ لاَ قُوَّةَ إِلاَّ بِاللَّهِ إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مالاً وَوَلَداً (٣٩)
قوله: قُلْتَ مَا شاءَ اللَّهُ لاَ قُوَّةَ إِلَّا بِاللَّهِ [٣٩] أي ما شاء الله في سابق علمه، لا يقف عليه أحد إلا الله تعالى، لاَ قُوَّةَ إِلَّا بِاللَّهِ [٣٩] أي لا قوة لنا على أداء ما أمرتنا به في الأصل، والسلامة منه في الفرع، والخاتمة المحمودة إلا بمعونتك، وكذا تفسير قوله صلّى الله عليه وسلّم: «لا حول ولا قوة إلا بالله» «٢» أي لا حول عن السلامة من الجهل في الأصل، ومن الإصرار في الفرع إلا بعصمتك ولا قوة لنا على أداء ما أمرتنا به في الأصل والسلامة منه في الفرع والخاتمة المحمودة إلا بمعونتك.
وسئل سهل: ما أفضل ما أعطي العبد؟ قال: علم يستزيد به افتقاراً إلى الله عزّ وجلّ.

<span data-type="title" id=toc-257>[سورة الكهف (١٨) : آية ٥٥]</span>
وَما مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جاءَهُمُ الْهُدى وَيَسْتَغْفِرُوا رَبَّهُمْ إِلاَّ أَنْ تَأْتِيَهُمْ سُنَّةُ الْأَوَّلِينَ أَوْ يَأْتِيَهُمُ الْعَذابُ قُبُلاً (٥٥)
قوله: وَما مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جاءَهُمُ الْهُدى [٥٥] قال: جاءهم الهدى وطرق الهداية كانت مسدودة عليهم، فمنعهم الهدى والإيمان الحكم الذي جرى عليهم في الأزل.

<span data-type="title" id=toc-258>[سورة الكهف (١٨) : آية ١٠٩]</span>
قُلْ لَوْ كانَ الْبَحْرُ مِداداً لِكَلِماتِ رَبِّي لَنَفِدَ الْبَحْرُ قَبْلَ أَنْ تَنْفَدَ كَلِماتُ رَبِّي وَلَوْ جِئْنا بِمِثْلِهِ مَدَداً (١٠٩)
قوله: قُلْ لَوْ كانَ الْبَحْرُ مِداداً لِكَلِماتِ رَبِّي لَنَفِدَ الْبَحْرُ [١٠٩] قال: أي بعلم ربي وعجائبه، ثم قال: إن من علمه كتابه، لو أن عبداً أعطي لكل حرف من القرآن ألف فهم لما بلغ نهايته علم الله فيه، لأنه كلامه القديم، وكلامه صفته، ولا نهاية لصفاته، كما لا نهاية له، وإنما يفهم على قدر ما يفتح الله على قلوب أوليائه من فهم كلامه.
قوله: فَمَنْ كانَ يَرْجُوا لِقاءَ رَبِّهِ فَلْيَعْمَلْ عَمَلًا صالِحاً وَلا يُشْرِكْ بِعِبادَةِ رَبِّهِ أَحَداً [١١٠] قال: العمل الصالح ما كان خالياً عن الرياء مقيداً بالسنة، والله سبحانه وتعالى أعلم.
_________
(١) هذا القول للنبي عيسى عليه السلام في الحلية ٢/ ٣٧٠.
(٢) صحيح البخاري: التهجد، رقم ١٠٦٩.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (96,'<span data-type="title" id=toc-259>السورة التي يذكر فيها مريم عليها السلام</span>

<span data-type="title" id=toc-260>[سورة مريم (١٩) : آية ١٣]</span>
وَحَناناً مِنْ لَدُنَّا وَزَكاةً وَكانَ تَقِيًّا (١٣)
قوله تعالى: وَحَناناً مِنْ لَدُنَّا [١٣] أي فعلنا ذلك رحمة من لدنا بأبويه، وَزَكاةً [١٣] أي طهرناه من ظنون الخلق إليه فيه، وَكانَ تَقِيًّا [١٣] أي مقبلاً علينا، معرضاً عما سوانا. وقال: إن أحوال الأنبياء كلها محضة.

<span data-type="title" id=toc-261>[سورة مريم (١٩) : الآيات ٣١ الى ٣٢]</span>
وَجَعَلَنِي مُبارَكاً أَيْنَ مَا كُنْتُ وَأَوْصانِي بِالصَّلاةِ وَالزَّكاةِ ما دُمْتُ حَيًّا (٣١) وَبَرًّا بِوالِدَتِي وَلَمْ يَجْعَلْنِي جَبَّاراً شَقِيًّا (٣٢)
وقوله: وَجَعَلَنِي مُبارَكاً أَيْنَ مَا كُنْتُ [٣١] يعني آمر بالمعروف، وأنهى عن المنكر، وأرشد الضال، وأنصر المظلوم، وأغيث الملهوف.
قوله عزَّ وجلَّ: وَلَمْ يَجْعَلْنِي جَبَّاراً شَقِيًّا [٣٢] أي جاهلاً بأحكامه متكبراً على عبادته، وقال النبي صلى الله عليه وسلّم: «الكبرياء رداء الله من نازع الله فيه أكبه على منخره في النار» «١» .
وسئل عن قوله عزَّ وجلَّ: إِنِّي نَذَرْتُ لِلرَّحْمنِ صَوْماً [٢٦] فقال: صمتاً عن الكل، إلا عن ذكرك، إذا سأل الصائم أن تقر عينه بك، ويسكن قلبه إليك لا إلى غيرك، فَلَنْ أُكَلِّمَ الْيَوْمَ إِنْسِيًّا [٢٦] .

<span data-type="title" id=toc-262>[سورة مريم (١٩) : آية ٥٢]</span>
وَنادَيْناهُ مِنْ جانِبِ الطُّورِ الْأَيْمَنِ وَقَرَّبْناهُ نَجِيًّا (٥٢)
قوله: وَقَرَّبْناهُ نَجِيًّا [٥٢] أي مناجياً للمكاشفة التي لا تخفى من الحق على القلوب محادثة وودا، كما قال تعالى: سَيَجْعَلُ لَهُمُ الرَّحْمنُ وُدًّا [٩٦] أي مكاشفة تتخذ الأسرار من غير واسطة. وهذا مقام من الله للذين صدقوا الله في السر والعلانية.

<span data-type="title" id=toc-263>[سورة مريم (١٩) : آية ٦١]</span>
جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمنُ عِبادَهُ بِالْغَيْبِ إِنَّهُ كانَ وَعْدُهُ مَأْتِيًّا (٦١)
قوله تعالى: جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمنُ عِبادَهُ بِالْغَيْبِ [٦١] يعني معاينة الحق بمعنى القرب الذي جعله بينه وبينهم، فيرى العبد قلبه في قرب الحق مشهوداً في غيب الغيب، وغيب الغيب هو نفس الروح وفهم العقل وفطنة المراد بالقلب، فإن نفس الروح موضع العقل، وهو موضع القدس، والقدس متصل بالعرش، وهو اسم من أسماء العرش، وجعل الله تعالى للنفس جزءاً من ألف جزء من الروح، بل أقل من ذلك، فإذا صارت إرادة الروح إرادة النفس أعطيا فيما بينهما الفطنة والذهن، والفطنة إمام الذهن، والفهم إمام الذهن، والفطنة حياة،
_________
(١) المستدرك على الصحيحين ١/ ١٢٩ وسنن أبي داود: باب ما جاء في الكبر، رقم ٤٠٩٠ وسنن ابن ماجة:
باب البراءة من الكبر، رقم ٤١٧٤ ومسند أحمد ٢/ ٢٤٨، ٣٧٦، ٤١٤، ٤٢٧، ٤٤٢.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (97,'والفهم عيش، وإنما يفهم الكلام رجلان: واحد يحب أن يفهم لكي يتكلم به في موضع، فليس له حظ منه إذ ذاك، وآخر يسمعه فيشغله العمل به عن غيره، وهذا أعز من الكبريت الأحمر، وأعز من كل عزيز، وهو في المتحابين في الله. والتفهم بكلف والفطنة لا تنال بالتكلف، وهو العمل بالإخلاص له، فإن لله تعالى عباداً في الجنة لو حجبوا عن اللقاء طرفة عين لاستغاثوا فيها كما يستغيث أهل النار في النار، لأنهم عرفوه، أفلا ترون إلى الكليم عليه السلام حيث لم يصبر عن رؤيته لما وجد حلاوة مناجاته حتى قال: «إلهي، ما هذا الصوت العيراني الذي غلب على قلبي منك؟ قد سمعت صوت الوالدة الشفيقة، وصوت الطير في الهواء، فما سمعت صوتاً أجلب لقلبي من هذا الصوت» .
وكان موسى عليه السلام بعد ذلك كلما رأى جبلاً أسرع إليه، وصعد عليه شوقاً إلى كلامه جل جلاله.
وقد كان رجل من بني إسرائيل لا يذهب موسى إلى مكان إلا مشى بحذائه، ولا يجلس مجلساً إلا جلس بحذائه، حتى تأذى موسى عليه السلام منه، قيل له: إنك أذيت نبي الله. قال:
إنما أريد أن أنظر إلى الفم الذي كلّم الله به. فقال: رَبِّ أَرِنِي أَنْظُرْ إِلَيْكَ [الأعراف: ١٤٣] فقال:
يا موسى، إنه لن يراني خليقة في الأرض إلا مات. فقال: «رب أرني أنظر إليك وأموت، أحب إلي من أن لا أنظر إليك وأحيى» . فمن أخلص لله قلبه له فاشتاق إليه وصل إليه.
وقد كان أبو عبيد الله الخواص يصيح ببغداد فيقول: أنا من ذكرك جائع لم أشبع، أنا من ذكرك عطشان لم أرو، ووا شوقاه إلى من يراني ولا أراه، ثم يأتي دجلة وعليه ثياب فيرمي نفسه فيها، فيغوص في موضع ويخرج من موضع آخر وهو يقول: أنا من ذكرك جائع لم أشبع، أنا من ذكرك عطشان لم أرو، ووا شوقاه إلى من يراني ولا أراه، والناس على الشط يبكون.
وجاء رجل إلى سهل يوماً والناس مجتمعون عليه فقال: يا أبا محمد انظر إيش عمل بك وإيش يوقع لك، فلم يؤثر ذلك على سهل، وقال: هو المقصود هو المقصود.

<span data-type="title" id=toc-264>[سورة مريم (١٩) : آية ٧٦]</span>
وَيَزِيدُ اللَّهُ الَّذِينَ اهْتَدَوْا هُدىً وَالْباقِياتُ الصَّالِحاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَواباً وَخَيْرٌ مَرَدًّا (٧٦)
قوله تعالى: وَيَزِيدُ اللَّهُ الَّذِينَ اهْتَدَوْا هُدىً [٧٦] قال: أي يزيد الله الذين اهتدوا بصيرة في إيمانهم بالله، وفي اقتدائهم بمحمد صلّى الله عليه وسلّم، وهو زيادة الهدى والنور المبين.

<span data-type="title" id=toc-265>[سورة مريم (١٩) : آية ٨٥]</span>
يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمنِ وَفْداً (٨٥)
قوله تعالى: يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمنِ وَفْداً [٨٥] أي ركباناً. والمتقون هم الذين يتقون ما سوى الله عزَّ وجلَّ. وقال: لا يكمل للعبد شيء حتى يحصن عمله بالخشية، وفعله بالورع، وورعه بالإخلاص، وإخلاصه بالمشاهدة، والمشاهدة بالتقوى عما سوى الله.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (98,'وقال: كانت قلوبهم أعز عليهم من أن يروا فيها شيئاً غير الله عزَّ وجلَّ، فإن الله لما خلق القلب قال: «خلقتك لي خاصة» ، فهذه القلوب جوالة، إما تجول حول العرش، وإما تجول في الحش.
قوله تعالى: أَلَمْ تَرَ أَنَّا أَرْسَلْنَا الشَّياطِينَ عَلَى الْكافِرِينَ تَؤُزُّهُمْ أَزًّا [٨٣] قال:
تزعجهم بالمعاصي إزعاجاً، وتدعوهم إليها بما تهوى أنفسهم بترك عصمة الله، كما قال تعالى في قصة اللعين: وَما كانَ لِي عَلَيْكُمْ مِنْ سُلْطانٍ إِلَّا أَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي [إبراهيم: ٢٢] ودعاؤه على مقامات فقد يكون إلى الشر، وقد يكون إلى الخير، كما قال النبي صلى الله عليه وسلّم: «إن الشيطان ليورد أحدكم سبعين باباً من الخير ليوقعه في باب من الشر فيهلكه» قال: وإن اللعين يوسوس إلى جميع أهل العبادات وأصحاب الجهد، ولا يبالي منهم إلا من لا يدخل في شيء، حتى يعلم أنه له أو عليه، وإنما وقع المغاليط للعباد والزهاد في العلم لا في الاجتهاد، فلم يكن لهم حال يعرفونها فيما بينهم وبين ربهم، فإن الله تعالى إذا حاسب العبد يوم القيامة فكل فعل عرف صاحبه حاله فيه من طاعة أو معصية ثبت عقله له، وما جهل فيه حاله تحير ودهش لذلك لأنه إذا عرف حاله صحت الطاعة والتوبة بحجة الله، وإذا لم يعرف يتحير ويدهش لأنه عمل بغير حجة.
وسئل سهل عن رجل يذكر الله فيخطر بقلبه: إن الله معك. قال: هو مكلف ثالث، إما أن يكون عدواً فيريد أن يقطعه، وإما أن يكون ذلك نفسه تريد أن تخونه وتخدعه، فلا يلتفتن إلى الخواطر في هذه الحال، والله سبحانه وتعالى أعلم.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (99,'<span data-type="title" id=toc-266>السورة التي يذكر فيها طه عليه السلام</span>

<span data-type="title" id=toc-267>[سورة طه (٢٠) : آية ٧]</span>
وَإِنْ تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَأَخْفى (٧)
قوله تعالى: فَإِنَّهُ يَعْلَمُ السِّرَّ وَأَخْفى [٧] قال: أخفى من السر ما لم يفكره العبد فيه وهو مفكره نوما.

<span data-type="title" id=toc-268>[سورة طه (٢٠) : آية ١٨]</span>
قالَ هِيَ عَصايَ أَتَوَكَّؤُا عَلَيْها وَأَهُشُّ بِها عَلى غَنَمِي وَلِيَ فِيها مَآرِبُ أُخْرى (١٨)
قوله تعالى: وَلِيَ فِيها مَآرِبُ أُخْرى [١٨] قال: أول من ملك العصا آدم، وهي من آس الجنة، ثم انتقلت من نبي إلى نبي حتى صارت إلى شعيب، فلما زوجه بنته أعطاها إياه، فكان موسى عليه السلام يتوكأ عليها، ويهش بها على غنمه، وينثر الورق إلى غنمه، ثم يأخذ بها من الشجر ما يريد، ويرسلها على السباع والوحوش وهوام الأرض فيضربها. وإذا اشتد الحر نصبها في الأرض فتكون كالظلة، وإذا نام حرسته حتى يستيقظ، وإذا كانت له ليلة مظلمة أضاءت له كالسراج، وإذا كان يوم غيم وغم عليه وقت الصلاة بينت له بشعاع طرفها، وإذا جاع غرزها في الأرض فأثمرت من ساعتها، فهذه مآرب عصاه «١» . فقد ذكر موسى عليه السلام من العصا منافع ومآرب ظهرت له، فأراد الله تعالى مآرب ومنافع كانت خافية عليه، كانقلابها ثعباناً، وضربها بالحجر لتنجاش عيون الماء، وضربها بالبحر وغير ذلك، فأراه أن علوم الخلق، وإن كانوا مؤيدين بالنبوة، قاصرة عن علم الحق بالأكوان.

<span data-type="title" id=toc-269>[سورة طه (٢٠) : الآيات ٣٩ الى ٤٢]</span>
أَنِ اقْذِفِيهِ فِي التَّابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِي وَعَدُوٌّ لَهُ وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي وَلِتُصْنَعَ عَلى عَيْنِي (٣٩) إِذْ تَمْشِي أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلى مَنْ يَكْفُلُهُ فَرَجَعْناكَ إِلى أُمِّكَ كَيْ تَقَرَّ عَيْنُها وَلا تَحْزَنَ وَقَتَلْتَ نَفْساً فَنَجَّيْناكَ مِنَ الْغَمِّ وَفَتَنَّاكَ فُتُوناً فَلَبِثْتَ سِنِينَ فِي أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلى قَدَرٍ يا مُوسى (٤٠) وَاصْطَنَعْتُكَ لِنَفْسِي (٤١) اذْهَبْ أَنْتَ وَأَخُوكَ بِآياتِي وَلا تَنِيا فِي ذِكْرِي (٤٢)
قوله تعالى: وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي [٣٩] قال: أظهر الله عليه ميراث علمه قبل العمل، فأورثه محبة في قلوب عباده، لأن من القلوب قلوباً تثاب قبل الفعل، وتعاقب قبل الرأي، كما يجد الإنسان في نفسه فرحاً لا يعرف سببه، وغماً لا يعرف سببه.
قوله تعالى: وَفَتَنَّاكَ فُتُوناً [٤٠] أي فتناً لنفسك الطبيعية وبيناها حتى لا تأمن مكر الله.
قوله تعالى: وَاصْطَنَعْتُكَ لِنَفْسِي [٤١] أي تفرد إلي بالتجريد لا يشغلك عني شيء.
قوله: وَلا تَنِيا فِي ذِكْرِي [٤٢] أي لا تكثر الذكر باللسان، وتغفل عن مراقبة القلب.
_________
(١) تفسير القرطبي ١١/ ١٨٧ وكتاب التاريخ لابن حبيب ٥٦، وخرج محققه الخبر من تاريخ الطبري ١/ ٤٦٤ والكامل لابن الأثير ١/ ١٧٩ والبداية والنهاية ١/ ٢٤٧.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (100,'<span data-type="title" id=toc-270>[سورة طه (٢٠) : آية ٤٤]</span>
فَقُولا لَهُ قَوْلاً لَيِّناً لَعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشى (٤٤)
قوله: فَقُولا لَهُ قَوْلًا لَيِّناً [٤٤] قال: حكي عن ابن عباس رضي الله عنه أنه قال:
كان موسى عليه السلام إذا دخل على فرعون قال له: يا أبا مصعب قل لا إله إلا الله وإني رسول الله. قال سهل: إن الله تعالى ألبس موسى عليه السلام لبسة المتأوبين، ونفى عنه عجلة المتهجمين لما رآه من الفضل والتمكين، ولم يرد به إيماناً، إذ لو أراد لقال: لعله يؤمن، وإنما أراد الحق عزَّ وجلَّ بذلك ملاطفة موسى عليه السلام بأجمل الخطاب وألين الكلام، لأن ذلك محرك لقلوب الخلائق أجمعين، كما قال النبي صلّى الله عليه وسلّم: «جبلت القلوب على حب من أحسن إليها وبغض من أساء إليها» «١» ، ليقطع به حجته، ويرغب من علم الله هدايته من السحرة وغيرهم.

<span data-type="title" id=toc-271>[سورة طه (٢٠) : آية ٤٦]</span>
قالَ لاَ تَخافا إِنَّنِي مَعَكُما أَسْمَعُ وَأَرى (٤٦)
قوله تعالى: قالَ لاَ تَخافا إِنَّنِي مَعَكُما أَسْمَعُ وَأَرى [٤٦] قال: أخبر الله أنه معهما بالنظر، مشاهد لكل حال هما عليه بالقوة والمعونة والتأييد، لا تخافا إبلاغ الرسالة بحال.

<span data-type="title" id=toc-272>[سورة طه (٢٠) : آية ٥٨]</span>
فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِهِ فَاجْعَلْ بَيْنَنا وَبَيْنَكَ مَوْعِداً لا نُخْلِفُهُ نَحْنُ وَلا أَنْتَ مَكاناً سُوىً (٥٨)
قوله تعالى: فَكُلُوا مِنْها [البقرة: ٥٨] قواما ولا تشبعوا منه فتسكروا عن الذكر، فإن السكر حرام. وقال: من جوّع نفسه انتقص بقدر ذلك دمه، وبقدر ما انتقص من دمه بالجوع انقطعت الوسوسة من القلب، ولو أن مجنوناً جوّع نفسه لصار صحيحاً. وقال النبي صلّى الله عليه وسلّم:
«ما من وعاء أبغض إلى الله من بطن ملىء طعاما» «٢» .

<span data-type="title" id=toc-273>[سورة طه (٢٠) : آية ١١١]</span>
وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ وَقَدْ خابَ مَنْ حَمَلَ ظُلْماً (١١١)
قوله تعالى: وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ [١١١] قال: أي خضعت له بقدر مقامها من المعرفة بالله، وتمكين التوفيق منه.

<span data-type="title" id=toc-274>[سورة طه (٢٠) : آية ١٢٣]</span>
قالَ اهْبِطا مِنْها جَمِيعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدىً فَمَنِ اتَّبَعَ هُدايَ فَلا يَضِلُّ وَلا يَشْقى (١٢٣)
قوله: فَمَنِ اتَّبَعَ هُدايَ فَلا يَضِلُّ وَلا يَشْقى [١٢٣] قال: هو الاقتداء وملازمة الكتاب والسنة، فلا يضل عن طريق الهدى، ولا يشقى في الآخرة والأولى.

<span data-type="title" id=toc-275>[سورة طه (٢٠) : آية ١٣١]</span>
وَلا تَمُدَّنَّ عَيْنَيْكَ إِلى مَا مَتَّعْنا بِهِ أَزْواجاً مِنْهُمْ زَهْرَةَ الْحَياةِ الدُّنْيا لِنَفْتِنَهُمْ فِيهِ وَرِزْقُ رَبِّكَ خَيْرٌ وَأَبْقى (١٣١)
قوله تعالى: وَلا تَمُدَّنَّ عَيْنَيْكَ إِلى مَا مَتَّعْنا بِهِ أَزْواجاً مِنْهُمْ زَهْرَةَ الْحَياةِ الدُّنْيا [١٣١] قال: أي لا تنظر إلى ما يورثك وسوسة الشيطان، ومخالفة الرحمن، وأماني النفس، والسكون إلى مألوفات الطبع، فإن كل واحد منها مما يقطع عن ذكر الله عزَّ وجلَّ.
والله سبحانه وتعالى أعلم.
_________
(١) نوادر الأصول ١/ ١٤٩ وكشف الخفاء ١/ ٣٩٥ ومسند الشهاب ١/ ٣٥٠.
(٢) في المستدرك على الصحيحين ٤/ ٣٧٦، رقم ٧٩٤٥ والسنن الكبرى ٤/ ١٧٧، رقم ٦٧٦٨ وكشف الخفاء ٢/ ٢٦٠: (ما ملأ آدمي وعاء شرا من بطنه ... ) .',NULL,'103',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (101,'<span data-type="title" id=toc-276>السورة التي يذكر فيها الأنبياء عليهم الصلاة والسلام</span>

<span data-type="title" id=toc-277>[سورة الأنبياء (٢١) : آية ٧]</span>
وَما أَرْسَلْنا قَبْلَكَ إِلاَّ رِجالاً نُوحِي إِلَيْهِمْ فَسْئَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لا تَعْلَمُونَ (٧)
قوله تعالى: فَسْئَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لاَ تَعْلَمُونَ [٧] قال: يعني أهل الفهم عن الله، والعلماء بالله وبأوامره وبأيامه. قيل: صفهم لنا. قال: العلماء ثلاثة: عالم بالله لا بأمر الله ولا بأيام الله، وهو عامة المؤمنين وعالم بالله وبأمر الله لا بأيام الله، وهم العلماء وعالم بالله وبأمر الله وبأيام الله، وهم النبيون والصديقون «١» .

<span data-type="title" id=toc-278>[سورة الأنبياء (٢١) : آية ١٠]</span>
لَقَدْ أَنْزَلْنا إِلَيْكُمْ كِتاباً فِيهِ ذِكْرُكُمْ أَفَلا تَعْقِلُونَ (١٠)
قوله تعالى: لَقَدْ أَنْزَلْنا إِلَيْكُمْ كِتاباً فِيهِ ذِكْرُكُمْ [١٠] قال: يعني العمل بما فيه حياتكم «٢» .

<span data-type="title" id=toc-279>[سورة الأنبياء (٢١) : آية ٢٧]</span>
لاَ يَسْبِقُونَهُ بِالْقَوْلِ وَهُمْ بِأَمْرِهِ يَعْمَلُونَ (٢٧)
قوله: لاَ يَسْبِقُونَهُ بِالْقَوْلِ وَهُمْ بِأَمْرِهِ يَعْمَلُونَ [٢٧] قال: أن الله تعالى جعل الكرامات كلها للمتقين من عباده، ثم للمبتدئين، ووصفهم فقال: لاَ يَسْبِقُونَهُ بِالْقَوْلِ [٢٧] أي لا اختيار لهم مع اختياره، وَهُمْ بِأَمْرِهِ يَعْمَلُونَ [٢٧] وهو اتباع السنة في الظاهر، ومراقبة الله في الباطن.

<span data-type="title" id=toc-280>[سورة الأنبياء (٢١) : آية ٣٥]</span>
كُلُّ نَفْسٍ ذائِقَةُ الْمَوْتِ وَنَبْلُوكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً وَإِلَيْنا تُرْجَعُونَ (٣٥)
قوله: وَنَبْلُوكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً [٣٥] قال: الشر متابعة النفس والهوى بغير هدى، والخير العصمة من المعصية والمعونة على الطاعة.

<span data-type="title" id=toc-281>[سورة الأنبياء (٢١) : آية ٨٣]</span>
وَأَيُّوبَ إِذْ نادى رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ وَأَنْتَ أَرْحَمُ الرَّاحِمِينَ (٨٣)
قوله تعالى: وَأَيُّوبَ إِذْ نادى رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ [٨٣] قال: الضر على وجهين:
ضر ظاهر وضر باطن فالباطن حركة النفس عند الوارد واضطرابها، والظاهر إظهار ما في السر من ذلك، فمتى احتل الضر الباطن سكن الظاهر عن إظهاره وصبر على الآلام، وإذا تحرك الباطن تحت الوارد انزعج الظاهر بالصياح والبكاء، فكان شكواه إلى الله عزَّ وجلَّ كي يعطي المعونة على رضا قلبه بالوارد، وذلك أن القلب إذا كان راضياً بأمر الله لم يضر العبد ما فعلت جوارحه، ألا ترى إلى بكاء النبي صلى الله عليه وسلّم حين مات ابنه إبراهيم كيف بكى عليه رحمة له بطبع البشرية، فلم يضره ما فعلت جوارحه، لأن قلبه كان راضياً به.
_________
(١) نسب القول للمسيح عليه السلام في نوادر الأصول ٤/ ١٠١.
(٢) تفسير القرطبي ١١/ ٢٧٣.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (102,'وكان سهل يقول لأصحابه: قولوا في دعائكم: إلهي إن طبختني فأنا قدر، وإن شويتني فأنا محنوذ، ولا بد أن تُعرف، فَمُنَّ عليَّ بمعرفتك.
وسئل سهل عن الدار، دار إسلام أم دار كفر؟ فقال: الدار دار بلوى واختبار. وقال عبد الرحمن المروزي لسهل: يا أبا محمد، ما تقول في رجل من منذ خمسة وعشرين يوماً تطالبه نفسه أن تشبع ورق السدر من منذ ثمانية عشر يوماً؟ فقال له سهل: ما تقول في رجل تطالبه نفسه أن يشم ورق السدر. قال: فوثب عبد الرحمن وانتفخت أوداجه «١» .
قوله تعالى: قُلْنا يا نارُ كُونِي بَرْداً وَسَلاماً عَلى إِبْراهِيمَ [٦٩] قال: النار مسلطة على الإحراق فمن لم تسلط عليه لم تحرقه. قال عمر بن واصل العنبري: كنت عند سهل ذات ليلة فأخرجت فتيلة السراج، فنالت من إصبعي شيئا يسيرا أو لمت منه، فنظر إلي سهل ووضع إصبعه نحو ساعتين، لا يجد لذلك ألماً ولا أثراً بإصبعه أثر، وهو يقول: أعوذ بالله من النار «٢» .

<span data-type="title" id=toc-282>[سورة الأنبياء (٢١) : الآيات ١٠٥ الى ١٠٦]</span>
وَلَقَدْ كَتَبْنا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ أَنَّ الْأَرْضَ يَرِثُها عِبادِيَ الصَّالِحُونَ (١٠٥) إِنَّ فِي هذا لَبَلاغاً لِقَوْمٍ عابِدِينَ (١٠٦)
قوله: أَنَّ الْأَرْضَ يَرِثُها عِبادِيَ الصَّالِحُونَ [١٠٥] قال: أضافهم إلى نفسه وحلاهم بحلية الصلاح، معناه: لا يصلح إلا ما كان خالصاً لي، لا يكون لغيري فيه أثر، وهم الذين أصلحوا سريرتهم مع الله، وانقطعوا بالكلية عن جميع ما دونه.
قوله: إِنَّ فِي هذا لَبَلاغاً لِقَوْمٍ عابِدِينَ [١٠٦] قال: لم يجعله بلاغاً لجميع عباده، بل خصه لقوم عابدين، وهم الذين عبدوا الله تعالى، وبذلوا له مهجهم، لا من أجل عوض، ولا من أجل الجنة، ولا من أجل النار، بل حباً له وافتخاراً بما أهّلهم لعبادتهم إياه، والله سبحانه وتعالى أعلم.
_________
(١) انظر مثل هذا الخبر في قوت القلوب ٢/ ٢٩٢- ٢٩٣، الفصل ٣٩.
(٢) هذا القول لمالك بن دينار في الحلية ٢/ ٣٥٧- ٣٥٨.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (103,'<span data-type="title" id=toc-283>السورة التي يذكر فيها الحج</span>

<span data-type="title" id=toc-284>[سورة الحج (٢٢) : آية ٣]</span>
وَمِنَ النَّاسِ مَنْ يُجادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّبِعُ كُلَّ شَيْطانٍ مَرِيدٍ (٣)
قوله تعالى: وَمِنَ النَّاسِ مَنْ يُجادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ [٣، ٧] أي يخاصم في الدين بالهوى والقياس دون الاقتداء، فعند ذلك يضل الناس ويبتدع.

<span data-type="title" id=toc-285><span data-type="title" id=toc-286>[سورة الحج (٢٢) : آية ١١]</span></span>
وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلى حَرْفٍ فَإِنْ أَصابَهُ خَيْرٌ اطْمَأَنَّ بِهِ وَإِنْ أَصابَتْهُ فِتْنَةٌ انْقَلَبَ عَلى وَجْهِهِ خَسِرَ الدُّنْيا وَالْآخِرَةَ ذلِكَ هُوَ الْخُسْرانُ الْمُبِينُ (١١)
قوله تعالى: وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلى حَرْفٍ [١١] قال: المؤمن وجه بلا قفا، كرّار غير فرّار، تراه يجاهد في دين الله وطاعته، من إقامة توحيده واقتدائه بنبيه، وإدامة التضرع، واللجأ إلى الله رجاء الاتصال به من موضع الاقتداء، كما روى زيد بن أسلم عن النبي صلى الله عليه وسلّم: «ما من أمتي إلا دخل الجنة إلا من أبى، قلنا: ومن الذي يأبى ذلك؟ قال: من أطاعني دخل الجنة ومن عصاني فقد أبى أن يدخل الجنة» «١» .
[سورة الحج (٢٢) : آية ١١]
وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلى حَرْفٍ فَإِنْ أَصابَهُ خَيْرٌ اطْمَأَنَّ بِهِ وَإِنْ أَصابَتْهُ فِتْنَةٌ انْقَلَبَ عَلى وَجْهِهِ خَسِرَ الدُّنْيا وَالْآخِرَةَ ذلِكَ هُوَ الْخُسْرانُ الْمُبِينُ (١١)
قوله: فَإِنْ أَصابَهُ خَيْرٌ اطْمَأَنَّ بِهِ [١١] يعني الذي يتبع الهوى إن رضي قلبه وفرحت نفسه بعاجل حظها اطمأن به، وإلا رجع إلى ما يدعوه الهوى من الكفر.

<span data-type="title" id=toc-287>[سورة الحج (٢٢) : آية ١٤]</span>
إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ إِنَّ اللَّهَ يَفْعَلُ ما يُرِيدُ (١٤)
قوله: إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ جَنَّاتٍ [١٤] قال: هم الذين صدقوا الله في السر والعلانية، واتبعوا سنة نبيهم صلّى الله عليه وسلّم، ولم يبتدعوا بحال.

<span data-type="title" id=toc-288>[سورة الحج (٢٢) : آية ١٨]</span>
أَلَمْ تَرَ أَنَّ اللَّهَ يَسْجُدُ لَهُ مَنْ فِي السَّماواتِ وَمَنْ فِي الْأَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُومُ وَالْجِبالُ وَالشَّجَرُ وَالدَّوَابُّ وَكَثِيرٌ مِنَ النَّاسِ وَكَثِيرٌ حَقَّ عَلَيْهِ الْعَذابُ وَمَنْ يُهِنِ اللَّهُ فَما لَهُ مِنْ مُكْرِمٍ إِنَّ اللَّهَ يَفْعَلُ ما يَشاءُ (١٨)
قوله تعالى: وَالشَّمْسُ وَالْقَمَرُ [١٨] قال: سجود هذه الأشياء معرفتها بالحق بالتذلل والانقياد له.

<span data-type="title" id=toc-289>[سورة الحج (٢٢) : الآيات ٢٦ الى ٢٩]</span>
وَإِذْ بَوَّأْنا لِإِبْراهِيمَ مَكانَ الْبَيْتِ أَنْ لا تُشْرِكْ بِي شَيْئاً وَطَهِّرْ بَيْتِيَ لِلطَّائِفِينَ وَالْقائِمِينَ وَالرُّكَّعِ السُّجُودِ (٢٦) وَأَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجالاً وَعَلى كُلِّ ضامِرٍ يَأْتِينَ مِنْ كُلِّ فَجٍّ عَمِيقٍ (٢٧) لِيَشْهَدُوا مَنافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَعْلُوماتٍ عَلى مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعامِ فَكُلُوا مِنْها وَأَطْعِمُوا الْبائِسَ الْفَقِيرَ (٢٨) ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَلْيُوفُوا نُذُورَهُمْ وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ (٢٩)
قوله: وَطَهِّرْ بَيْتِيَ لِلطَّائِفِينَ [٢٦] يعني طهّر بيتي من الأوثان لعبادي الطاهرة قلوبهم من الشك والريب والقسوة، فكما أمر الله بتطهير بيته من الأصنام، فكذلك أمر بتطهير بيته الذي أودعه سر الإيمان ونور المعرفة، وهو قلب المؤمن، أمر الله تعالى المؤمن بتطهيره عن الغل والغش والميل إلى الشهوات والغفلة للطائفين فيه زوائد التوفيق والقائمين بأنوار الإيمان، وَالرُّكَّعِ السُّجُودِ [٢٦] الخوف والرجاء، فإن القلب إذا لم يسكن خرب، وإذا سكنه غير مالكه خرب، فإذا أردتم أن تعمروا قلوبكم فلا تدعوا فيها غير الله، وإذا أردتم أن تعمروا ألسنتكم فلا تدعوا فيها غير الصدق، وإذا أردتم أن تعمروا جوارحكم فلا تدعوا فيها شيئاً إلا بالسنة.
_________
(١) صحيح البخاري: الاعتصام بالكتاب والسنة، رقم ٦٨٥١. [.....]',NULL,'106',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (104,'قوله: وَأَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجالًا [٢٧] قال: إن لله تعالى عباداً يذهبون إلى المساجد بعضهم على السرير، وبعضهم على المراكب من ذهب عليها سندس، وتجرها الملائكة.
قال أحمد بن سالم: كنت في أرض أصلحها، فرأيت سهلاً على فرش فوق ماء الفرات. وقال:
دخلت يوماً دار سهل وكان بابه صغيراً، فرأيت فرساً قائماً، فخرجت فزعاً، وتعجبت كيف دخل من هذا الباب الصغير، فرآني سهل وقال: ارجع، فرجعت فلم أر شيئاً. وحكي أن عمر ابن الخطاب رضي الله عنه أشرف على أهل عرفات فقال: لو يعلم الجمع هنا بفناء من نزلوا لاستبشروا بالفضل بعد المغفرة.
قوله: وَيَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَعْلُوماتٍ عَلى مَا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعامِ [٢٨] يعني الهدايا والضحايا. وحكي عن فتح الموصلي «١» أنه أشرف في يوم العيد على الموصل، فرأى الدخان في بيوت الناس، فقال: إلهي كم من متقرب إليك في هذه الليلة بقربان، وقد تقربت إليك بقربان، يعني الصلوات، فما أنت صانع فيه يا محبوب «٢» . وحكي عن عدي بن ثابت الأنصاري «٣» أنه قال: قربان المتقين الصلاة «٤» ، والله أعلم.
قوله: وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ [٢٩] قال: اختلف الناس فيه. قال الحسن: إنما سماه عتيقاً تكرمه له، كما تقول العرب: جسد عتيق، وفرس عتيق إذا كان كريماً. وحكى خاله محمد ابن سوار عن الثوري أنه قال: إنما قيل ذلك لأنه أقدم مساجد الله وأعتقها، كما قال: إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبارَكاً [آل عمران: ٩٦] ، وقال بعضهم: سماه عتيقاً لأنه لم يقصده جبار من الجبابرة بمكيدة إلا قصمه الله تعالى، فأعتق البيت منه. وقال بعضهم: لأنه أعتق من الغرق في زمن الطوفان، حيث رفع إلى السماء، وكما أعتق الله بيته كذلك أعتق قلب المؤمن
_________
(١) فتح بن محمد بن وشاح الأزدي الموصلي ( ... - ١٦٥، أو ١٧٠ هـ) : أحد الأولياء. له أحوال ومقامات وقدم راسخ في التقوى. (تاريخ بغداد ١٢/ ٣٨١ وصفوة الصفوة ٤/ ١٨١) . وثمت رجل آخر يعرف بفتح الموصلي، وهو فتح بن سعيد، أبو نصر الموصلي ( ... - ٢٢٠ هـ) : من كبار مشايخ الموصل، كان من الزهاد المتصوفة. ومعظم المصادر تخلط بين أخبارهما. (تاريخ بغداد ١٢/ ٣٨١ وصفوة الصفوة ٤/ ١٨٣- ١٨٩) .
(٢) صفوة الصفوة ٤/ ١٨٨، والورع لابن حنبل ص ٩٢.
(٣) عدي بن ثابت الأنصاري ( ... - ١١٦ هـ) : عالم الشيعة الإمامية وصالحهم في عصره. قال الذهبي: لو كانت الشيعة مثله لقلّ شرهم. مولده ووفاته في الكوفة (الأعلام ٤/ ٢١٩) .
(٤) مصنف ابن أبي شيبة ٢/ ١٥٩ وتفسير القرطبي ٦/ ١٣٥.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (105,'من الغير، وهو أقدم مما نصبه الله تعالى علماً في أرضه وجعله في المسجد الحرام، كذلك القلب له قلب آخر، وهو موضع وقوف العبد بين يدي مولاه، لا يتحرك في شيء إنما هو ساكن إليه.

<span data-type="title" id=toc-290>[سورة الحج (٢٢) : آية ٤٦]</span>
أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِها أَوْ آذانٌ يَسْمَعُونَ بِها فَإِنَّها لاَ تَعْمَى الْأَبْصارُ وَلكِنْ تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ (٤٦)
قوله تعالى: فَإِنَّها لاَ تَعْمَى الْأَبْصارُ وَلكِنْ تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ [٤٦] قال: أليس من نور بصر القلب يغلب الهوى والشهوة، فإذا عمي بصر القلب عما فيه غلبت الشهوة وتواترت الغفلة، فعند ذلك يصير البدن متخطياً في المعاصي غير منقاد للحق بحال.

<span data-type="title" id=toc-291>[سورة الحج (٢٢) : آية ٥٢]</span>
وَما أَرْسَلْنا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَلا نَبِيٍّ إِلاَّ إِذا تَمَنَّى أَلْقَى الشَّيْطانُ فِي أُمْنِيَّتِهِ فَيَنْسَخُ اللَّهُ ما يُلْقِي الشَّيْطانُ ثُمَّ يُحْكِمُ اللَّهُ آياتِهِ وَاللَّهُ عَلِيمٌ حَكِيمٌ (٥٢)
قوله تعالى: وَما أَرْسَلْنا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَلا نَبِيٍّ إِلَّا إِذا تَمَنَّى أَلْقَى الشَّيْطانُ فِي أُمْنِيَّتِهِ [٥٢] قال: يعني إذا تلا ونفسه ملاحظة للتلاوة ألقى الشيطان في أذنه، إذ له على النفس فيه شركة، إذ الملاحظة فيها من هوى النفس وشهوتها، فإذا شاهد المذكور لا الذكر لها القلب عما سواه ولم يشاهد شيئاً غير مولاه، وصار الشيطان أسيراً من أسرائه، ألا ترى أن العبد إذا سها في قراءته، وذكر ربه عزَّ وجلَّ، فهو يسكن قلبه إلى أدنى حظ من حظوظ النفس، حتى يجد العدو عليه سبيلاً.
وقد قال الحسن: الوسواس وسواسان، أحدهما من النفس والآخر من الشيطان، فما كان من ذلك إلحاحاً فهو من النفس يستعان عليها بالصيام والصلاة والأدب، وما كان من ذلك نبذاً فهو من الشيطان يستعان عليها بالقرآن والذكر.

<span data-type="title" id=toc-292>[سورة الحج (٢٢) : آية ٥٤]</span>
وَلِيَعْلَمَ الَّذِينَ أُوتُوا الْعِلْمَ أَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ وَإِنَّ اللَّهَ لَهادِ الَّذِينَ آمَنُوا إِلى صِراطٍ مُسْتَقِيمٍ (٥٤)
قوله: فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ [٥٤] قال: صدق الإيمان وحقيقته يورث الإخبات في القلب، وهو الرقة والخشية والخشوع في القلب وطول التفكر وطول الصمت، وهذا من نتائج الإيمان، لأن الله تعالى يقول: فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ [٥٤] ، والله سبحانه وتعالى أعلم.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (106,'<span data-type="title" id=toc-293>السورة التي يذكر فيها المؤمنون</span>

<span data-type="title" id=toc-294>[سورة المؤمنون (٢٣) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
قَدْ أَفْلَحَ الْمُؤْمِنُونَ (١) الَّذِينَ هُمْ فِي صَلاتِهِمْ خاشِعُونَ (٢)
قوله تعالى: قَدْ أَفْلَحَ الْمُؤْمِنُونَ (١) الَّذِينَ هُمْ فِي صَلاتِهِمْ خاشِعُونَ [١، ٢] قيل: ما الخشوع؟ قال: الخشوع علانية، وهو الوقوف بين يدي الله تعالى على الإقامة على شروط آداب الآمر، وهو تخليص الحركات والسكون عما سواه، وأصل ذلك الخشية في السر، فإذا أعطي الخشية ظهر الخشوع على ظاهره، وهي من شروط الإيمان.
وقد حكي عن الحسن بن علي «١» رضي الله عنه أنه إذا فرغ من وضوئه تغير لونه، فقيل له في ذلك، فقال: يحق على من أراد أن يدخل على ذي العرش أن يتغير لونه. ويروى عن النبي صلى الله عليه وسلّم أنه قال لمعاذ «٢» : «يا معاذ: إن المؤمن قد قيده القرآن عن كثير من هوى نفسه، وحال بينه وبين أن يهلك فيما هوي بإذن الله، إن المؤمن لذي الحق أسير. يا معاذ: إن المؤمن يسعى في فكاك رقبته. يا معاذ: إن المؤمن لا تسكن روعته، ولا يؤمن اضطرابه حتى يخلف جسر جهنم.
يا معاذ: إن المؤمن يعلم أن عليه رقباء على سمعه وبصره ولسانه ويده ورجليه وبطنه وفرجه، حتى اللمحة ببصره، وفتات الطينة بإصبعه، وكحل عينه، وجميع سعيه، التقوى رفيقه، والقرآن دليله، والخوف محجته، والشوق مطيته، والوجل شعاره، والصلاة كهفه، والصيام جنته، والصدقة فكاكه، والصدق وزيره، والحياء أميره، وربه من وراء ذلك بالمرصاد. يا معاذ: إني أحب لك ما أحب لنفسي، وأنهيت إليك ما أنهى إلي جبريل صلوات الله عليه، فلا أعرفن أحداً يوافيني يوم القيامة أسعد بما آتاك الله تعالى منك» «٣» .
_________
(١) الحسن بن علي بن أبي طالب الهاشمي القرشي ( ... - ٥٠ هـ) : ثاني الأئمة الاثني عشر عند الإمامية، أمه فاطمة الزهراء بنت رسول الله صلّى الله عليه وسلّم، وهو أكبر أولادها وأولهم. كان عاقلا حليما محبا للخير، فصيحا. (الأعلام ٢/ ١٩٩) .
(٢) معاذ بن جبل بن عمرو بن أوس الأنصاري الخزرجي (٢٠ ق هـ- ١٨ هـ) : صحابي جليل، كان أعلم الأمة بالحلال والحرام، شهد المشاهد كلها مع رسول الله صلّى الله عليه وسلّم. بعثه النبي قاضيا ومرشدا لأهل اليمن.
(الحلية ١/ ٢٢٨) .
(٣) الفردوس بمأثور الخطاب ٥/ ٣٧١، ٣٧٤ ومجمع الزوائد ١/ ١٧٠ والمعجم الأوسط ٨/ ١٧٦ والحلية ١/ ٢٦- ٢٧، ١٠/ ٣١.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (107,'<span data-type="title" id=toc-295>[سورة المؤمنون (٢٣) : آية ١٧]</span>
وَلَقَدْ خَلَقْنا فَوْقَكُمْ سَبْعَ طَرائِقَ وَما كُنَّا عَنِ الْخَلْقِ غافِلِينَ (١٧)
قوله: وَلَقَدْ خَلَقْنا فَوْقَكُمْ سَبْعَ طَرائِقَ [١٧] يعني الحجب السبعة التي تحجبه عن ربه عزَّ وجلَّ. فالحجاب الأول عقله، والثاني علمه، والثالث قلبه، والرابع خشيته، والخامس نفسه، والسادس إرادته، والسابع مشيئته.
فالعقل باشتغاله بتدبير الدنيا، والعلم بمباهاته مع الأقران، والقلب بالغفلة، والخشية بإغفالها عن موارد الأمور عليها، والنفس لأنها مأوى كل بلية، والإرادة إرادة الدنيا والإعراض عن الآخرة، والمشيئة بملازمة الذنوب.

<span data-type="title" id=toc-296>[سورة المؤمنون (٢٣) : آية ٥١]</span>
يا أَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّباتِ وَاعْمَلُوا صالِحاً إِنِّي بِما تَعْمَلُونَ عَلِيمٌ (٥١)
قوله: كُلُوا مِنَ الطَّيِّباتِ وَاعْمَلُوا صالِحاً [٥١] يعني كلوا من الحلال قواماً مع حفظ الأدب. والقوام ما يمسك به النفس، ويحفظ فيه القلب والأدب فيه شكر المنعم، وأدنى الشكر أن لا تعصيه بنعمة.

<span data-type="title" id=toc-297>[سورة المؤمنون (٢٣) : آية ٥٧]</span>
إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ (٥٧)
قوله: إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ [٥٧] قال: الخشية انكسار القلب من دوام الانتصاب بين يديه، ومن بعد هذه المرتبة الإشفاق، وهو أرق من الخشية، واللطف والخشية أرق من الخوف، والخوف أرق من الرهبة، فلكل منها صفة ومكان.

<span data-type="title" id=toc-298>[سورة المؤمنون (٢٣) : آية ٧٦]</span>
وَلَقَدْ أَخَذْناهُمْ بِالْعَذابِ فَمَا اسْتَكانُوا لِرَبِّهِمْ وَما يَتَضَرَّعُونَ (٧٦)
قوله: فَمَا اسْتَكانُوا لِرَبِّهِمْ وَما يَتَضَرَّعُونَ [٧٦] قال: ما أخلصوا لربهم في العبودية، ولا ذلوا له بالوحدانية.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (108,'<span data-type="title" id=toc-299>السورة التي يذكر فيها النور</span>

<span data-type="title" id=toc-300>[سورة النور (٢٤) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
سُورَةٌ أَنْزَلْناها وَفَرَضْناها وَأَنْزَلْنا فِيها آياتٍ بَيِّناتٍ لَعَلَّكُمْ تَذَكَّرُونَ (١)
قوله تعالى: سُورَةٌ أَنْزَلْناها وَفَرَضْناها [١] أي جمعناها وبينا حلالها وحرامها.

<span data-type="title" id=toc-301>[سورة النور (٢٤) : آية ٢٢]</span>
وَلا يَأْتَلِ أُولُوا الْفَضْلِ مِنْكُمْ وَالسَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبى وَالْمَساكِينَ وَالْمُهاجِرِينَ فِي سَبِيلِ اللَّهِ وَلْيَعْفُوا وَلْيَصْفَحُوا أَلا تُحِبُّونَ أَنْ يَغْفِرَ اللَّهُ لَكُمْ وَاللَّهُ غَفُورٌ رَحِيمٌ (٢٢)
قوله تعالى: وَلْيَعْفُوا وَلْيَصْفَحُوا [٢٢] يعني وليعفوا عن ظلم الناس لهم. وحكي عن سفيان الثوري أنه قال: أوحى الله تعالى إلى عزير أنك إن لم تطب نفساً أن تكون مضغة في أفواه الآدميين، لم أكتبك عندي من المتواضعين. قال: فقال عزير: إلهي، فما علامة من صافيته في مودتك. فقال: أقنعه بالرزق اليسير، وأحركه للخطر العظيم، قليل المطعم، كثير البكاء، يستغفرني بالأسحار، ويبغض فيّ الفجار.

<span data-type="title" id=toc-302>[سورة النور (٢٤) : آية ٢٦]</span>
الْخَبِيثاتُ لِلْخَبِيثِينَ وَالْخَبِيثُونَ لِلْخَبِيثاتِ وَالطَّيِّباتُ لِلطَّيِّبِينَ وَالطَّيِّبُونَ لِلطَّيِّباتِ أُولئِكَ مُبَرَّؤُنَ مِمَّا يَقُولُونَ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ (٢٦)
قوله: الْخَبِيثاتُ لِلْخَبِيثِينَ [٢٦] قال: الخبيثات القلوب من النساء للخبيثي القلوب من الرجال، والخبيثو القلوب من الرجال للخبيثات القلوب من النساء.

<span data-type="title" id=toc-303>[سورة النور (٢٤) : الآيات ٣٠ الى ٣١]</span>
قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْ ذلِكَ أَزْكى لَهُمْ إِنَّ اللَّهَ خَبِيرٌ بِما يَصْنَعُونَ (٣٠) وَقُلْ لِلْمُؤْمِناتِ يَغْضُضْنَ مِنْ أَبْصارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلا يُبْدِينَ زِينَتَهُنَّ إِلاَّ ما ظَهَرَ مِنْها وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلى جُيُوبِهِنَّ وَلا يُبْدِينَ زِينَتَهُنَّ إِلاَّ لِبُعُولَتِهِنَّ أَوْ آبائِهِنَّ أَوْ آباءِ بُعُولَتِهِنَّ أَوْ أَبْنائِهِنَّ أَوْ أَبْناءِ بُعُولَتِهِنَّ أَوْ إِخْوانِهِنَّ أَوْ بَنِي إِخْوانِهِنَّ أَوْ بَنِي أَخَواتِهِنَّ أَوْ نِسائِهِنَّ أَوْ ما مَلَكَتْ أَيْمانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أُولِي الْإِرْبَةِ مِنَ الرِّجالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلى عَوْراتِ النِّساءِ وَلا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ ما يُخْفِينَ مِنْ زِينَتِهِنَّ وَتُوبُوا إِلَى اللَّهِ جَمِيعاً أَيُّهَا الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ (٣١)
قوله: قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصارِهِمْ [٣٠] أي غضوا أبصاركم عن محارم الله تعالى، هو عن النظر من غير غيرة. وروي عن عبادة بن الصامت «١» عن النبي صلى الله عليه وسلّم أنه قال:
«اضمنوا لي ستة أضمن لكم الجنة، أصدقوا إذا حدثتم، وأوفوا إذا وعدتم، وأدّوا إذا ائتمنتم، واحفظوا فروجكم، وغضوا أبصاركم، وكفوا أيديكم» «٢» . وحكي عن ابن عمر رضي الله عنه أنه سئل: أكان رسول الله صلّى الله عليه وسلّم يلتفت في الصلاة؟ قال: ولا في غير الصلاة.
قوله: وَتُوبُوا إِلَى اللَّهِ جَمِيعاً أَيُّهَا الْمُؤْمِنُونَ [٣١] قيل: ما التوبة؟ قال: أن تبدل بدل الجهل العلم وبدل النسيان الذكر وبدل المعصية الطاعة.

<span data-type="title" id=toc-304>[سورة النور (٢٤) : آية ٣٥]</span>
اللَّهُ نُورُ السَّماواتِ وَالْأَرْضِ مَثَلُ نُورِهِ كَمِشْكاةٍ فِيها مِصْباحٌ الْمِصْباحُ فِي زُجاجَةٍ الزُّجاجَةُ كَأَنَّها كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبارَكَةٍ زَيْتُونَةٍ لا شَرْقِيَّةٍ وَلا غَرْبِيَّةٍ يَكادُ زَيْتُها يُضِيءُ وَلَوْ لَمْ تَمْسَسْهُ نارٌ نُورٌ عَلى نُورٍ يَهْدِي اللَّهُ لِنُورِهِ مَنْ يَشاءُ وَيَضْرِبُ اللَّهُ الْأَمْثالَ لِلنَّاسِ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ (٣٥)
قوله تعالى: اللَّهُ نُورُ السَّماواتِ وَالْأَرْضِ [٣٥] يعني مزين السماوات والأرض بالأنوار، مَثَلُ نُورِهِ [٣٥] يعني مثل نور محمد صلّى الله عليه وسلّم. قال الحسن البصري: عنى بذلك قلب المؤمن وضياء التوحيد، لأن قلوب الأنبياء صلوات الله عليهم أنور من أن توصف بمثل هذه
_________
(١) عبادة بن الصامت بن قيس الأنصاري (٣٨ ق هـ- ٣٤ هـ) : صحابي، من الموصوفين بالورع. شهد العقبة وبدرا وسائر المشاهد، وحضر فتح مصر. (الأعلام ٣/ ٢٥٨) .
(٢) شعب الإيمان ٤/ ٢٠٦، ٢٣٠.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (109,'الأنوار، وقال: النور مثل نور القرآن مصباح، المصباح سراجه المعرفة وفتيلته الفرائض ودهنه الإخلاص ونوره نور الاتصال. فكلما ازداد الإخلاص صفاء، ازداد المصباح ضياء، وكلما ازداد الفرائض حقيقة ازداد المصباح نورا.

<span data-type="title" id=toc-305>[سورة النور (٢٤) : آية ٣٧]</span>
رِجالٌ لا تُلْهِيهِمْ تِجارَةٌ وَلا بَيْعٌ عَنْ ذِكْرِ اللَّهِ وَإِقامِ الصَّلاةِ وَإِيتاءِ الزَّكاةِ يَخافُونَ يَوْماً تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَالْأَبْصارُ (٣٧)
قوله: يَخافُونَ يَوْماً تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَالْأَبْصارُ [٣٧] ، يعني يوم البعث تتقلب فيه القلوب والأبصار حالاً بعد حال لا يدومون على حال، فالمؤمن الذي يخاف هذا اليوم.
وقد حكي عن الحسن أنه قال: ذكر عنده أن رجلاً يخرج من النار بعد ألف عام، فقال الحسن: يا ليتني أنا هو «١» .
وحكي عن عون بن عبد الله «٢» أنه قال: أوصى لقمان ابنه قال: يا بني ارجُ الله رجاء لا تأمن فيه مكره، وخَف الله تعالى خوفاً لا تيأس فيه من رحمته. فقال: كيف أستطيع ذلك ولي قلب واحد؟ فقال: يا بني إن المؤمن لذو قلبين: قلب يرجو الله به، وقلب يخافه به «٣» ، والله سبحانه وتعالى أعلم.
_________
(١) قوت القلوب ١/ ٤٠١.
(٢) عون بن عبد الله بن عتبة بن مسعود الهذلي ( ... - نحو ١١٥ هـ) ، خطيب، راوية، ناسب، شاعر، كان من آداب أهل المدينة، اشتهر بالعبادة والقراءة. (الحلية ٤/ ٢٤٠) .
(٣) قوت القلوب ١/ ٣٨١، ٤٢٣ وشعب الإيمان ٢/ ١٨.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (110,'<span data-type="title" id=toc-306>السورة التي يذكر فيها الفرقان</span>

<span data-type="title" id=toc-307>[سورة الفرقان (٢٥) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
تَبارَكَ الَّذِي نَزَّلَ الْفُرْقانَ عَلى عَبْدِهِ لِيَكُونَ لِلْعالَمِينَ نَذِيراً (١)
قوله تعالى: تَبارَكَ الَّذِي نَزَّلَ الْفُرْقانَ [١] قال سهل: يعني جلّ وعلا من خصّ محمدا صلّى الله عليه وسلّم بإنزال الفرقان عليه ليفرق بين الحق والباطل، والولي والعدو، والقريب والبعيد، عَلى عَبْدِهِ [١] أي على عبده الأخلص ونبيه الأخصّ وحبيبه الأدنى وصفيه الأولى، لِيَكُونَ لِلْعالَمِينَ نَذِيراً [١] أي يكون للخلق سراجاً ونوراً نهدي به إلى أحكام القرآن، ويستدلون به على طريق الحق ومنهاج الصدق.

<span data-type="title" id=toc-308>[سورة الفرقان (٢٥) : آية ٢٠]</span>
وَما أَرْسَلْنا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلاَّ إِنَّهُمْ لَيَأْكُلُونَ الطَّعامَ وَيَمْشُونَ فِي الْأَسْواقِ وَجَعَلْنا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَتَصْبِرُونَ وَكانَ رَبُّكَ بَصِيراً (٢٠)
قوله: وَجَعَلْنا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَتَصْبِرُونَ وَكانَ رَبُّكَ بَصِيراً [٢٠] قال:
إن الله تعالى أمر بالصبر على ما جعل للإنسان فيه فتنة، ومن ذلك قلة الإطراق إلى ما في أيدي الناس.
وقد روى أبو أيوب «١» عن النبي صلّى الله عليه وسلّم أنه أتاه رجل فقال: «إذا قمت إلى صلاتك فصلِّ صلاة مودع ولا تكلمن بكلام تعتذر منه غداً، واجمع اليأس مما في أيدي الناس» «٢» ، وقد كان السلف يغتنمون ذلك حتى حكي عن حذيفة «٣» أنه قال: إن أقرّ أيامي لعيني ليوم أرجع إلى أهلي، فيشكون إلي الحاجة، وذلك أني سمعت رسول الله صلّى الله عليه وسلّم يقول: «إن الله ليحمي عبده المؤمن من الدنيا كما يحمي المريض أهله الطعام والشراب، وإن الله ليتعاهد المؤمن بالبلاء كما يتعاهد الوالد ولده بالخير» «٤» .
_________
(١) أبو أيوب: خالد بن زيد بن كليب، أبو أيوب الأنصاري ( ... - ٥٢ هـ) : صحابي، شهد بدرا وأحدا وسائر المشاهد. كان شجاعا صابرا تقيا محبا للغزو والجهاد. (الحلية ١/ ٣٦١) .
(٢) سنن ابن ماجة: باب الحكمة، ٤١٧١ ومسند أحمد ٥/ ٤١٢ والمعجم الكبير ٩/ ١٥٤. [.....]
(٣) حذيفة بن اليمان (حسل) بن جابر العبسي ( ... - ٣٦ هـ) : صحابي، من الولاة الشجعان الفاتحين. كان صاحب سر النبي صلى الله عليه وسلّم في المنافقين. (الحلية ١/ ٢٧٠) .
(٤) شعب الإيمان ٧/ ٣٢١ وفيض القدير ٢/ ٢٦٠.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (111,'<span data-type="title" id=toc-309>[سورة الفرقان (٢٥) : آية ٢٨]</span>
يا وَيْلَتى لَيْتَنِي لَمْ أَتَّخِذْ فُلاناً خَلِيلاً (٢٨)
قوله تعالى: لَيْتَنِي لَمْ أَتَّخِذْ فُلاناً خَلِيلًا [٢٨] قال: أصح الخلة ما لا يورث الندامة، وليس ذلك إلا الأنس بالله تعالى، والعزلة عن الخلق. وكان رسول الله صلّى الله عليه وسلّم يلازم الخلوة لما فتح الله في قلبه من العلم، فكان يحب التفكر فيه. وما من رجل حسنت صلاته إلا واستأنس به كل شيء. والرجل يكون نائماً، فيحركه من نومه أوقات الصلاة فينتبه، وهذا من إخوانه من الجن قد استأنس به، وربما يسافرون معه إذا سافر، ويؤثرونه على أنفسهم، وربما استأنس به الملائكة.
وقد سأل رجل سهلاً فقال: إني أريد أن أصحبك. فقال: إذا مات أحدنا فمن يصحب الباقي فليصحبه الآن. وكان الربيع بن خيثم جالساً على باب داره يوماً، فجاء حجر فصكّ جبهته فشجه، وقال: لقد وعظت يا ابن خيثم، فدخل منزله وأغلق الباب على نفسه، فما رؤي جالساً مجلسه ذلك حتى مات «١» .

<span data-type="title" id=toc-310>[سورة الفرقان (٢٥) : آية ٥٨]</span>
وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِي لا يَمُوتُ وَسَبِّحْ بِحَمْدِهِ وَكَفى بِهِ بِذُنُوبِ عِبادِهِ خَبِيراً (٥٨)
قوله: وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِي لاَ يَمُوتُ [٥٨] سئل ابن سالم عن التوكل والكسب بأيهما تعبد الخلق؟ قال: التوكل حال الرسول صلّى الله عليه وسلّم، والكسب سنته. وإنما استنّ الكسب لهم لضعفهم حين أسقطوا عن درجة التوكل الذي هو حاله، فلم يسقطهم عن درجة طلب المعاش بالكسب الذي هو سنته، ولولا ذلك لهلكوا «٢» . قال سهل: من طعن في الكسب فقد طعن في السنة، ومن طعن في التوكل فقد طعن في الإيمان «٣» .

<span data-type="title" id=toc-311>[سورة الفرقان (٢٥) : الآيات ٦٣ الى ٦٤]</span>
وَعِبادُ الرَّحْمنِ الَّذِينَ يَمْشُونَ عَلَى الْأَرْضِ هَوْناً وَإِذا خاطَبَهُمُ الْجاهِلُونَ قالُوا سَلاماً (٦٣) وَالَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّداً وَقِياماً (٦٤)
قوله: وَإِذا خاطَبَهُمُ الْجاهِلُونَ قالُوا سَلاماً [٦٣] أي صواباً من القول وسداداً.
وقال الحسن البصري رحمه الله: هذا دأبهم في النهار، فإذا دخل الليل كانوا كما وصف الله في آخر الآية: وَالَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّداً وَقِياماً [٦٤] .

<span data-type="title" id=toc-312>[سورة الفرقان (٢٥) : آية ٧٠]</span>
إِلاَّ مَنْ تابَ وَآمَنَ وَعَمِلَ عَمَلاً صالِحاً فَأُوْلئِكَ يُبَدِّلُ اللَّهُ سَيِّئاتِهِمْ حَسَناتٍ وَكانَ اللَّهُ غَفُوراً رَحِيماً (٧٠)
قوله: إِلَّا مَنْ تابَ [٧٠] قال: لا تصح التوبة لأحدكم حتى يدع الكثير من المباح، مخافة أن يخرجه إلى غيره، كما قالت عائشة رضي الله عنها: اجعلوا بينكم وبين الحرام ستراً من الحلال، كان رسول الله صلّى الله عليه وسلّم يدعنا بعد الطهر ثلاثاً حتى تذهب فورة الدم.

<span data-type="title" id=toc-313>[سورة الفرقان (٢٥) : آية ٧٢]</span>
وَالَّذِينَ لاَ يَشْهَدُونَ الزُّورَ وَإِذا مَرُّوا بِاللَّغْوِ مَرُّوا كِراماً (٧٢)
قوله: وَالَّذِينَ لاَ يَشْهَدُونَ الزُّورَ [٧٢] قال: الزور مجالس المبتدعين.
والله سبحانه وتعالى أعلم.
_________
(١) صفوة الصفوة ٣/ ٦٧ وشعب الإيمان ٦/ ٢٦٤.
(٢) الحلية ١٠/ ٣٧٨- ٣٧٩ وطبقات الصوفية ١/ ٣١٢ وتلبيس إبليس ١/ ٣٤٤.
(٣) الحلية ١٠/ ١٩٥ وقوت القلوب ٢/ ٩ وتلبيس إبليس ١/ ٣٤٤.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (112,'<span data-type="title" id=toc-314>السورة التي يذكر فيها الشعراء</span>

<span data-type="title" id=toc-315>[سورة الشعراء (٢٦) : آية ٣]</span>
لَعَلَّكَ باخِعٌ نَفْسَكَ أَلاَّ يَكُونُوا مُؤْمِنِينَ (٣)
قوله تعالى: لَعَلَّكَ باخِعٌ نَفْسَكَ أَلَّا يَكُونُوا مُؤْمِنِينَ [٣] قال: أي مهلك نفسك باتباع المراد في هدايتهم، وقد سبق الحكم منا بما يكون من إيمان المؤمن وكفر الكافر، فلا تغيير ولا تبديل، وباطن ذلك أنك شغلت نفسك عنا بالاشتغال بهم حرصاً على إيمانهم، ما عليك إلا البلاغ، فلا يشغلك الحزن في أمرهم عنا.

<span data-type="title" id=toc-316>[سورة الشعراء (٢٦) : آية ٥]</span>
وَما يَأْتِيهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمنِ مُحْدَثٍ إِلاَّ كانُوا عَنْهُ مُعْرِضِينَ (٥)
قوله: وَما يَأْتِيهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمنِ مُحْدَثٍ [٥] قال: أي ما أحدث لهم من علم القرآن الذي لم يكونوا يعلمونه من قبل، وهو النزول، إلا أعرضوا عنه، ليس أن يكون الذكر في نفسه محدثاً، لأنه من صفات ذات الحق، ليس بمكون ولا مخلوق.

<span data-type="title" id=toc-317>[سورة الشعراء (٢٦) : الآيات ٧٨ الى ٨٢]</span>
الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ (٧٨) وَالَّذِي هُوَ يُطْعِمُنِي وَيَسْقِينِ (٧٩) وَإِذا مَرِضْتُ فَهُوَ يَشْفِينِ (٨٠) وَالَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ (٨١) وَالَّذِي أَطْمَعُ أَنْ يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ (٨٢)
قوله: الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ [٧٨] قال: الذي خلقني لعبوديته يهديني إلى قربه.
قوله: وَالَّذِي هُوَ يُطْعِمُنِي وَيَسْقِينِ [٧٩] قال: يطعمني لذة الإيمان، ويسقيني شراب التوكل والكفاية.
قوله: وَإِذا مَرِضْتُ فَهُوَ يَشْفِينِ [٨٠] قال: يعني إذا تحركت بغيره لغيره عصمني، وإذا ملت إلى شهوة من الدنيا منعها عني.
قوله: وَالَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ [٨١] قال: الذي يميتني بالغفلة ثم يحييني بالذكر.
قوله: وَالَّذِي أَطْمَعُ أَنْ يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ [٨٢] أخرج كلامه على شروط الأدب بين الخوف والرجاء، ولم يحكم عليه بالمغفرة.

<span data-type="title" id=toc-318>[سورة الشعراء (٢٦) : آية ٨٤]</span>
وَاجْعَلْ لِي لِسانَ صِدْقٍ فِي الْآخِرِينَ (٨٤)
قوله تعالى: وَاجْعَلْ لِي لِسانَ صِدْقٍ فِي الْآخِرِينَ [٨٤] قال: ارزقني الثناء في جميع الأمم والملأ.

<span data-type="title" id=toc-319>[سورة الشعراء (٢٦) : آية ٨٩]</span>
إِلاَّ مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ (٨٩)
قوله عزّ وجلّ: إِلَّا مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ [٨٩] قال: الذي سلم من البدع مفوض إلى الله أمره راضٍ بقدر الله.

<span data-type="title" id=toc-320>[سورة الشعراء (٢٦) : آية ٢١٢]</span>
إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ (٢١٢)
قوله تعالى: إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ [٢١٢] قال: يعني عن استماع القرآن والفهم في محل الأوامر والنواهي.

<span data-type="title" id=toc-321>[سورة الشعراء (٢٦) : آية ٢١٤]</span>
وَأَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ (٢١٤)
قوله: وَأَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ [٢١٤] قال: خوّف الأقرب منك واخفض جناحك للأبعدين، دلَّهم علينا بألطف الدلالات، وأخبرهم بأني جواد كريم.

<span data-type="title" id=toc-322>[سورة الشعراء (٢٦) : آية ٢٢٧]</span>
إِلاَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ وَذَكَرُوا اللَّهَ كَثِيراً وَانْتَصَرُوا مِنْ بَعْدِ ما ظُلِمُوا وَسَيَعْلَمُ الَّذِينَ ظَلَمُوا أَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ (٢٢٧)
قوله تعالى: إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ وَذَكَرُوا اللَّهَ كَثِيراً [٢٢٧] قال: خلق الله تعالى السر وجعل حياته في ذكره، وخلق الظاهر وجعل حياته في حمده وشكره، وجعل عليهما الحقوق، وهي الطاعة، والله سبحانه وتعالى أعلم.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (113,'<span data-type="title" id=toc-323>السورة التي يذكر فيها النمل</span>

<span data-type="title" id=toc-324>[سورة النمل (٢٧) : الآيات ١٠ الى ١١]</span>
وَأَلْقِ عَصاكَ فَلَمَّا رَآها تَهْتَزُّ كَأَنَّها جَانٌّ وَلَّى مُدْبِراً وَلَمْ يُعَقِّبْ يا مُوسى لا تَخَفْ إِنِّي لاَ يَخافُ لَدَيَّ الْمُرْسَلُونَ (١٠) إِلاَّ مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْناً بَعْدَ سُوءٍ فَإِنِّي غَفُورٌ رَحِيمٌ (١١)
قوله تعالى: إِنِّي لاَ يَخافُ لَدَيَّ الْمُرْسَلُونَ (١٠) إِلَّا مَنْ ظَلَمَ [١٠، ١١] قال: لم يكن في الأنبياء والرسل ظالم، وإنما هذه مخاطبة لهم كناية عن قومهم، كما قال للنبي صلّى الله عليه وسلّم: لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ [الزمر: ٦٥] والمقصود من ذلك أمته، فإنهم إذا سمعوا ما خوطب به النبي صلّى الله عليه وسلّم من التحذير كانوا أشد حذرا.

<span data-type="title" id=toc-325>[سورة النمل (٢٧) : آية ١٩]</span>
فَتَبَسَّمَ ضاحِكاً مِنْ قَوْلِها وَقالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلى والِدَيَّ وَأَنْ أَعْمَلَ صالِحاً تَرْضاهُ وَأَدْخِلْنِي بِرَحْمَتِكَ فِي عِبادِكَ الصَّالِحِينَ (١٩)
قوله تعالى: رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ [١٩] قال: ليس للعبد أن يتكلم إلا بأمر سيده، وأن يبطش إلا بأمره، وأن يمشي إلا بأمره، وأن يأكل وينام ويتفكر إلا بأمره، وذلك أفضل الشكر الذي هو شكر العباد لسيدهم.
قوله تعالى: وَأَدْخِلْنِي بِرَحْمَتِكَ فِي عِبادِكَ الصَّالِحِينَ [١٩] قال: يعني ارزقني قربة أوليائك، لأكون من جملتهم، وإن لم أصل إلى مقامهم.

<span data-type="title" id=toc-326>[سورة النمل (٢٧) : آية ٥٢]</span>
فَتِلْكَ بُيُوتُهُمْ خاوِيَةً بِما ظَلَمُوا إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَعْلَمُونَ (٥٢)
قوله تعالى: فَتِلْكَ بُيُوتُهُمْ خاوِيَةً بِما ظَلَمُوا [٥٢] قال: الإشارة في البيوت إلى القلب، فمنها ما هو عامر بالذكر، ومنها ما هو خرب بالغفلة، ومن ألهمه الله عزّ وجلّ بالذكر فقد خلصه من الظلم.

<span data-type="title" id=toc-327>[سورة النمل (٢٧) : آية ٥٩]</span>
قُلِ الْحَمْدُ لِلَّهِ وَسَلامٌ عَلى عِبادِهِ الَّذِينَ اصْطَفى آللَّهُ خَيْرٌ أَمَّا يُشْرِكُونَ (٥٩)
قوله تعالى: الْحَمْدُ لِلَّهِ وَسَلامٌ عَلى عِبادِهِ الَّذِينَ اصْطَفى [٥٩] قال: أهل القرآن يلحقهم من الله السلام في العاجل بقوله: وَسَلامٌ عَلى عِبادِهِ [٥٩] وسلام في الآجل، وهو قوله: سَلامٌ قَوْلًا مِنْ رَبٍّ رَحِيمٍ [يس: ٥٨] .

<span data-type="title" id=toc-328>[سورة النمل (٢٧) : آية ٦٢]</span>
أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذا دَعاهُ وَيَكْشِفُ السُّوءَ وَيَجْعَلُكُمْ خُلَفاءَ الْأَرْضِ أَإِلهٌ مَعَ اللَّهِ قَلِيلاً ما تَذَكَّرُونَ (٦٢)

<span data-type="title" id=toc-329>[سورة النمل (٢٧) : الآيات ٦٤ الى ٦٥]</span>
أَمَّنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ وَمَنْ يَرْزُقُكُمْ مِنَ السَّماءِ وَالْأَرْضِ أَإِلهٌ مَعَ اللَّهِ قُلْ هاتُوا بُرْهانَكُمْ إِنْ كُنْتُمْ صادِقِينَ (٦٤) قُلْ لاَّ يَعْلَمُ مَنْ فِي السَّماواتِ وَالْأَرْضِ الْغَيْبَ إِلاَّ اللَّهُ وَما يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ (٦٥)
قوله تعالى: أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذا دَعاهُ [٦٢] قيل: من المضطر؟ قال: الذي إذا رفع يديه لا يرى لنفسه حسنة غير التوحيد، ويكون منه على خطر «١» . وقال مرة أخرى: المضطر هو المتبرئ من الحول والقوة والأسباب المذمومة «٢» .
_________
(١) قوت القلوب ٢/ ٤، ٩.
(٢) قوت القلوب ٢/ ٩.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (114,'والدعوة صنفان: دعاء المضطر، ودعاء المظلوم وهي مستجابة من الناس لا محالة، مؤمناً كان أو كافراً، لأن الله تعالى يقول: أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذا دَعاهُ [٦٢] ، كقوله: وَمَنْ يَرْزُقُكُمْ مِنَ السَّماءِ وَالْأَرْضِ [٦٤] ودعاء المظلوم يرفع فوق الحجاب، ويقول الله تعالى:
«وعزتي وجلالي لأنصرنك ولو بعد حين» .
قوله: قُلْ لاَّ يَعْلَمُ مَنْ فِي السَّماواتِ وَالْأَرْضِ الْغَيْبَ إِلَّا اللَّهُ [٦٥] قال: أخفى غيبه عن المخلوقين بجبروته، ولم يطلع عليه أحداً، لئلا يأمن أحد من عبيده مكره، فلا يعلم أحد ما سبق له منه، فيكون همهم في إبهام العواقب ومجاري السوابق، لئلا يدعو ما لا يليق بهم من أنواع الدعاوى في المحبة والمعرفة وغير ذلك. قال: كان مائة ألف صديق ظاهرين للخلق، حتى كان لا يسمع أصوات الميازيب ببيت المقدس من المجتهدين بالميل، فلما ظهر شيئان، سألوا الله تعالى فأماتهم دعوى الحب ودعوى التوكل. فقيل له في القول قول الحارث «١» حيث قال: سهرت ليلي وأظمأت نهاري «٢» . فقال: يعني لا حاجة لي إلى الكشف، لأنه حظ الكفار في الدنيا، فأنا لا أشاركهم في حظهم، فلذلك قلت: أنا مؤمن. قيل له: قوم يقولون مثل ما قال الحارث، فقال:
دعواهم باطلة، وكيف تصح لهم الدعوى، ولم يدع ذلك أبو بكر وعمر رضي الله عنهما، وكانت شعرة في صدرهما أفضل من الحارث، وإنما قال ذلك الحارث رضي الله عنه لا بنفسه، وإنما أظهر الله ذلك فتنة لمن بعده من المدعين، فكيف يصح لهؤلاء أن يدعوا ذلك لأنفسهم. قال تعالى:

<span data-type="title" id=toc-330>[سورة النمل (٢٧) : آية ٧٣]</span>
وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَكِنَّ أَكْثَرَهُمْ لا يَشْكُرُونَ (٧٣)
وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ [٧٣] قال: منعه فضل، كما أن عطاءه فضل، ولكن لا يعرف مواضع فضله في المنع إلا خواص الأولياء.

<span data-type="title" id=toc-331>[سورة النمل (٢٧) : آية ٨٨]</span>
وَتَرَى الْجِبالَ تَحْسَبُها جامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحابِ صُنْعَ اللَّهِ الَّذِي أَتْقَنَ كُلَّ شَيْءٍ إِنَّهُ خَبِيرٌ بِما تَفْعَلُونَ (٨٨)
قوله تعالى: وَتَرَى الْجِبالَ تَحْسَبُها جامِدَةً [٨٨] قال: إن الله تعالى نبه عباده على تقضي الأوقات وغفلتهم فيها، فجعل الجبال مثلاً للدنيا، يظن الناظر أنها واقفة معه وهي آخذة بحظها منه، ولا يبقى بعد الانقضاء إلا الحسرة على الفائت الناظر أنها واقفة معه، وهي آخذة.
_________
(١) الحارث بن مالك بن قيس الليثي، المعروف بابن البرصاء: صحابي. روى عن النبي صلى الله عليه وسلّم، وعنه الشعبي وعبيد بن جريج. (تهذيب التهذيب ٢/ ١٣٥) .
(٢) في مصنف ابن أبي شيبة ٦/ ١٧٠، رقم ٣٠٤٢٥: (عن زبيد قال: قال رسول الله صلّى الله عليه وسلّم: كيف أصبحت يا حارث بن مالك؟ قال: أصبحت مؤمنا حقا. قال: إن لكل قول حقيقة، فما حقيقة ذلك؟ قال: أصبحت عزفت نفسي عن الدنيا، وأسهرت ليلي، وأظمأت نهاري، وكأني أنظر إلى عرش ربي قد أبرز للحساب ... ) .',NULL,'117',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (115,'<span data-type="title" id=toc-332>السورة التي يذكر فيها القصص</span>
قوله تعالى: فَبَصُرَتْ بِهِ عَنْ جُنُبٍ وَهُمْ لاَ يَشْعُرُونَ [١١] أي عن بعد عن مشاهدة عيننا فيه.

<span data-type="title" id=toc-333>[سورة القصص (٢٨) : آية ٨]</span>
فَالْتَقَطَهُ آلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَناً إِنَّ فِرْعَوْنَ وَهامانَ وَجُنُودَهُما كانُوا خاطِئِينَ (٨)
قوله تعالى: لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَناً [٨] أي رفعوه ليكون لهم فرحاً وسروراً ولم يعلموا، إنما أضمرت القدرة فيه من تصييره لهم عدوا وحزنا.

<span data-type="title" id=toc-334>[سورة القصص (٢٨) : آية ١٠]</span>
وَأَصْبَحَ فُؤادُ أُمِّ مُوسى فارِغاً إِنْ كادَتْ لَتُبْدِي بِهِ لَوْلا أَنْ رَبَطْنا عَلى قَلْبِها لِتَكُونَ مِنَ الْمُؤْمِنِينَ (١٠)
قوله تعالى: وَأَصْبَحَ فُؤادُ أُمِّ مُوسى فارِغاً [١٠] أي فارغاً من ذكر غير الله، اعتماداً على وعد الله، إِنَّا رَادُّوهُ إِلَيْكِ [٧] .

<span data-type="title" id=toc-335>[سورة القصص (٢٨) : آية ٢٤]</span>
فَسَقى لَهُما ثُمَّ تَوَلَّى إِلَى الظِّلِّ فَقالَ رَبِّ إِنِّي لِما أَنْزَلْتَ إِلَيَّ مِنْ خَيْرٍ فَقِيرٌ (٢٤)
قوله: فَقالَ رَبِّ إِنِّي لِما أَنْزَلْتَ إِلَيَّ مِنْ خَيْرٍ فَقِيرٌ [٢٤] رجع إلى الله بالافتقار والتضرع، فقال: إني لما عودتني من جميل إحسانك على الدوام، فقير إلى شفقتك، ونظرك إلي بعين الرعاية والكلاءة، فردني من وحشة المخالفين إلى أنس الموافقين، فرزقه الله صحبة شعيب صلوات الله عليهما وأولاده.

<span data-type="title" id=toc-336>[سورة القصص (٢٨) : آية ٦٠]</span>
وَما أُوتِيتُمْ مِنْ شَيْءٍ فَمَتاعُ الْحَياةِ الدُّنْيا وَزِينَتُها وَما عِنْدَ اللَّهِ خَيْرٌ وَأَبْقى أَفَلا تَعْقِلُونَ (٦٠)
قوله تعالى: وَما أُوتِيتُمْ مِنْ شَيْءٍ فَمَتاعُ الْحَياةِ الدُّنْيا [٦٠] قال: من أخذ من الدنيا بشهوة منه حرمه الله في الدنيا والآخرة ما هو خير منها، ومن أخذ منها لضرورة دخلت بنفسه أو لحق لزمه لم يحرم ما هو خير في الدنيا، لذة العبادة ومحبة الحق عز وجل، وفي الآخرة الدرجات العلى «١» . وقيل لعامر بن عبد قيس «٢» : لقد رضيت من الدنيا باليسير. قال: أفلا أخبركم بمن رضي بدون ما رضيت؟ قالوا: بلى. قال: من رضي الدنيا حظا من الآخرة «٣» .
_________
(١) ورد مثل هذا القول في نوادر الأصول ٤/ ١٨٦.
(٢) عامر بن عبد الله، المعروف بابن عبد قيس العنبري ( ... - نحو ٥٥ هـ) : تابعي. أول من عرف بالنسك من عباد التابعين بالبصرة. تلقن القرآن من أبي موسى الأشعري. (الحلية ٢/ ٨٧) .
(٣) كتاب الزهد لابن أبي عاصم ص ٢٢٨ ونسب هذا القول إلى داود الطائي في الحلية ٧/ ٣٥٣ وصفوة الصفوة ٣/ ١٤١.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (116,'<span data-type="title" id=toc-337>[سورة القصص (٢٨) : آية ٧٦]</span>
إِنَّ قارُونَ كانَ مِنْ قَوْمِ مُوسى فَبَغى عَلَيْهِمْ وَآتَيْناهُ مِنَ الْكُنُوزِ ما إِنَّ مَفاتِحَهُ لَتَنُوأُ بِالْعُصْبَةِ أُولِي الْقُوَّةِ إِذْ قالَ لَهُ قَوْمُهُ لاَ تَفْرَحْ إِنَّ اللَّهَ لا يُحِبُّ الْفَرِحِينَ (٧٦)
قوله: لاَ تَفْرَحْ إِنَّ اللَّهَ لاَ يُحِبُّ الْفَرِحِينَ [٧٦] قال: من فرح بغير مفروح استجلب حزناً لا انقطاع له، وليس للمؤمن راحة دون لقاء الحق جل وعز. وحكي عن الأعمش «١» قال:
كنا نشهد جنازة فلا ندري من نعزي من حزن القوم «٢» .

<span data-type="title" id=toc-338>[سورة القصص (٢٨) : آية ٧٨]</span>
قالَ إِنَّما أُوتِيتُهُ عَلى عِلْمٍ عِنْدِي أَوَلَمْ يَعْلَمْ أَنَّ اللَّهَ قَدْ أَهْلَكَ مِنْ قَبْلِهِ مِنَ الْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةً وَأَكْثَرُ جَمْعاً وَلا يُسْئَلُ عَنْ ذُنُوبِهِمُ الْمُجْرِمُونَ (٧٨)
قوله تعالى: إِنَّما أُوتِيتُهُ عَلى عِلْمٍ عِنْدِي [٧٨] قال: ما نظر إلى نفسه أحد فأفلح، ولا ادعى لنفسه حالاً فتم له.
والسعيد من الخلق من صرف بصره عن أحواله، وأفعاله سبيل الفضل والإفضال، ورؤية منة الله في جميع الأفعال والشقي من زين نفسه وأحواله وأفعاله حتى افتخر بها، وادعى ذلك لنفسه، فشؤمه يهلكه يوماً ما وإن لم يهلكه في الوقت، ألا ترى الله كيف حكى عن قارون بقوله: إِنَّما أُوتِيتُهُ عَلى عِلْمٍ عِنْدِي [٧٨] يعني الفضل، وهو أنه كان أقرأهم للتوراة «٣» ، فادعى لنفسه فضلاً، فخسف الله به الأرض ظاهراً، وكم قد خسف بالأشرار وصاحبها لا يشعر بذلك، وخسف الأشرار هو منع العصمة، والرد إلى الحول والقوة بإطلاق اللسان في الدعاوي العريضة، والعمى عن رؤية الفضل، والقعود عن القيام بالشكر على ما أعطي، فحينئذ يكون وقت الزوال.
_________
(١) الأعمش: سليمان بن مهران الأسدي (٦١- ١٤٨ هـ) : تابعي. نشأ في الكوفة، وتوفي فيها. كان عالما بالقرآن والحديث والفرائض. (تاريخ بغداد ٩/ ٣) .
(٢) الحلية ٥/ ٥٠ وكتاب الزهد لابن أبي عاصم ٣٦٥. [.....]
(٣) تفسير القرطبي ١٣/ ٣١٥، وقيل أيضا إن معنى الآية: (إنّ الله تعالى إنما أعطاني هذا المال لعلمه بأني أستحقه ولمحبته لي) ، انظر تفسير القرطبي وتفسير ابن كثير ٣/ ٤١٠.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (117,'<span data-type="title" id=toc-339>السورة التي يذكر فيها العنكبوت</span>

<span data-type="title" id=toc-340>[سورة العنكبوت (٢٩) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الم (١) أَحَسِبَ النَّاسُ أَنْ يُتْرَكُوا أَنْ يَقُولُوا آمَنَّا وَهُمْ لا يُفْتَنُونَ (٢)
قوله تعالى: الم (١) أَحَسِبَ النَّاسُ أَنْ يُتْرَكُوا أَنْ يَقُولُوا آمَنَّا وَهُمْ لاَ يُفْتَنُونَ [١، ٢] قال: أي لا يصيبهم البلاء، وإنما البلاء باب بين أهل المعرفة وبين الحق عزَّ وجلَّ. وحكي أن الملائكة تقول: يا رب، عبدك الكافر بسطت له الدنيا وزويت عنه البلاء، فيقول للملائكة:
اكشفوا لهم عن عقابه، فإذا رأوه قالوا: لا ينعمه ما أصاب من الدنيا. وتقول: يا رب، عبدك المؤمن تزوي عنه الدنيا وتعرضه للبلاء. فيقول للملائكة: اكشفوا لهم عن ثوابه، فإذا رأوا ثوابه قالوا: لا يضره ما أصابه في الدنيا «١» . وقال: اجعلوا صلاتكم الصبر على البأساء، وصومكم الصمت، وصدقتكم كف الأذى، والصبر على العافية أشد منه على البلاء. ومنه قيل: طلب السلامة أن لا تتعرض للبلاء.

<span data-type="title" id=toc-341>[سورة العنكبوت (٢٩) : آية ١٧]</span>
إِنَّما تَعْبُدُونَ مِنْ دُونِ اللَّهِ أَوْثاناً وَتَخْلُقُونَ إِفْكاً إِنَّ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ لا يَمْلِكُونَ لَكُمْ رِزْقاً فَابْتَغُوا عِنْدَ اللَّهِ الرِّزْقَ وَاعْبُدُوهُ وَاشْكُرُوا لَهُ إِلَيْهِ تُرْجَعُونَ (١٧)
قوله: فَابْتَغُوا عِنْدَ اللَّهِ الرِّزْقَ [١٧] قال: اطلبوا الرزق بالتوكل لا بالكسب، فإن طلب الرزق بالكسب طريق العوام. وحكي عن عيسى بن مريم عليه السلام أنه قال: بحق أقول لكم:
لا الدنيا تريدون ولا الآخرة. قالوا: بيّن لنا ذلك يا نبي الله، وقد كنا نرى أنا نريد أحدهما. فقال:
لو أطعتم رب الدنيا الذي بيده مفاتيح خزائنها لأعطاكموها، ولو أطعتم رب الآخرة لأعطاكموها ولكن لا هذه تريدون ولا تلك «٢» .

<span data-type="title" id=toc-342>[سورة العنكبوت (٢٩) : آية ٢١]</span>
يُعَذِّبُ مَنْ يَشاءُ وَيَرْحَمُ مَنْ يَشاءُ وَإِلَيْهِ تُقْلَبُونَ (٢١)
قوله تعالى: يُعَذِّبُ مَنْ يَشاءُ [٢١] بمتابعة البدعة، وَيَرْحَمُ مَنْ يَشاءُ [٢١] بملازمة السنة.

<span data-type="title" id=toc-343>[سورة العنكبوت (٢٩) : آية ٤٣]</span>
وَتِلْكَ الْأَمْثالُ نَضْرِبُها لِلنَّاسِ وَما يَعْقِلُها إِلاَّ الْعالِمُونَ (٤٣)

<span data-type="title" id=toc-344>[سورة العنكبوت (٢٩) : آية ٤٥]</span>
اتْلُ ما أُوحِيَ إِلَيْكَ مِنَ الْكِتابِ وَأَقِمِ الصَّلاةَ إِنَّ الصَّلاةَ تَنْهى عَنِ الْفَحْشاءِ وَالْمُنْكَرِ وَلَذِكْرُ اللَّهِ أَكْبَرُ وَاللَّهُ يَعْلَمُ ما تَصْنَعُونَ (٤٥)
قوله تعالى: وَتِلْكَ الْأَمْثالُ نَضْرِبُها لِلنَّاسِ وَما يَعْقِلُها إِلَّا الْعالِمُونَ [٤٣] قال: ضرب الله الأمثال للناس عامة، إذ شواهد القدرة تدل على القادر، ولا يعقلها إلا خاصته، فالعلم أعز، والفقه عن الله أخص، فمن عرف علم نفسه الطبيعية وحده وهم، ومن عرفه بعلم الله فالله عرف مراده منه لنفسه، وليس مع الخلق من معرفة الحق وراء ذلك، وإنما وقعت الإشارة إليه لبعد قلوبهم عن المعرفة في الحقيقة، ألا ترى إلى قوله: إِنَّ الصَّلاةَ تَنْهى عَنِ الْفَحْشاءِ وَالْمُنْكَرِ [٤٥] قال: في هذه الآية تزيين الانصراف عن الفحشاء والمنكر بواحدة وهو الإخلاص في الصلاة، وكل صلاة لا تنهى عن الفحشاء والمنكر، ولا يوجد فيها تزيين الانصراف عن ذلك فهي معلولة، والواجب تصفيتها.

<span data-type="title" id=toc-345>[سورة العنكبوت (٢٩) : آية ٥٦]</span>
يا عِبادِيَ الَّذِينَ آمَنُوا إِنَّ أَرْضِي واسِعَةٌ فَإِيَّايَ فَاعْبُدُونِ (٥٦)
قوله تعالى: إِنَّ أَرْضِي واسِعَةٌ فَإِيَّايَ فَاعْبُدُونِ
[٥٦] قال: يعني إذا عمل بالمعاصي والبدع في أرض فاخرجوا منها إلى أرض المطيعين. وقد قال النبي صلى الله عليه وسلّم: «الفارّ بدينه عند فساد الأمة له أجر سبعين شهيداً في سبيل الله عزَّ وجلَّ» ، والله سبحانه وتعالى أعلم.
_________
(١) مصنف ابن أبي شيبة ٧/ ١٦٦ والحلية ٤/ ١١٨، ١٢٣ وصفوة الصفوة ٣/ ٩٤.
(٢) الحلية ٦/ ٥٧- ٥٨.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (118,'<span data-type="title" id=toc-346>السورة التي يذكر فيها الروم</span>

<span data-type="title" id=toc-347>[سورة الروم (٣٠) : آية ٤]</span>
فِي بِضْعِ سِنِينَ لِلَّهِ الْأَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ (٤)
قوله تعالى: لِلَّهِ الْأَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُ [٤] يعني من قبل كل شيء، ومن بعد كل شيء، لأنه هو المبدئ والمعيد، سبق تدبيره في الخلق، لأنه عالم بهم في الأصل والفرع.

<span data-type="title" id=toc-348>[سورة الروم (٣٠) : الآيات ٤٠ الى ٤١]</span>
اللَّهُ الَّذِي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ هَلْ مِنْ شُرَكائِكُمْ مَنْ يَفْعَلُ مِنْ ذلِكُمْ مِنْ شَيْءٍ سُبْحانَهُ وَتَعالى عَمَّا يُشْرِكُونَ (٤٠) ظَهَرَ الْفَسادُ فِي الْبَرِّ وَالْبَحْرِ بِما كَسَبَتْ أَيْدِي النَّاسِ لِيُذِيقَهُمْ بَعْضَ الَّذِي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ (٤١)
قوله: اللَّهُ الَّذِي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ [٤٠] قال: أفضل الرزق السكون إلى الرازق.
قوله ثُمَّ يُمِيتُكُمْ [٤٠] يعني يهلككم. قال: إن الله تعالى خلق الخير والشر، ووضع الأمر والنهي، فاستعبدنا بالخير وقرنه بالتوفيق، ونهانا عن الشر وقد قرن ارتكابه بترك العصمة والخذلان، فالجميع خلقه، فمن وفق للخير وجب عليه الشكر، ومن ترك مع الشر وجب عليه الاستغاثة بالله عزَّ وجلَّ.
قوله تعالى: ظَهَرَ الْفَسادُ فِي الْبَرِّ وَالْبَحْرِ [٤١] قال: مثل الله تعالى الجوارح بالبر، ومثل القلب بالبحر، وهم أعم نفعاً وأكثر خطراً، هذا باطن الآية، ألا ترى أن القلب إنما سمي قلباً لتقلبه وبعد غوره، ولهذا قال النبي صلى الله عليه وسلّم لأبي الدرداء «١» رضي الله عنه: «جدد السفينة فإن البحر عميق» «٢» ، يعني جدد النية لله تعالى من قلبك، فإن البحر عميق، فحينئذ إذا صارت المعاملة في القلوب التي هي بحور ليس له منها مخرج، وخرجت النفس من الوسط، استراحت الجوارح، فصار صاحبها في كل يوم أقرب إلى غورها، وأبعد من نفسه حتى يصل.
وسئل عن معنى قوله صلّى الله عليه وسلّم: «من تواضع لغني ذهب ثلثا دينه» «٣» فقال: للقلب ثلاث مقامات: جمهور القلب، ومقام اللسان من القلب، ومقام الجوارح من القلب. وقوله: «ذهب ثلثا دينه» يعني اشتغل من الثلاثة اثنان: اللسان وسائر الجوارح، وبقي الجمهور الذي لا يصل إليه أحد، وهو موضع إيمانه من القلب.
_________
(١) أبو الدرداء: عويمر بن مالك بن قيس الأنصاري ( ... - ٣٢ هـ) : صحابي، من الحكماء الفرسان القضاة، وهو أول قاض عين بدمشق. (الحلية ١/ ٢٠٨) .
(٢) في الفردوس بمأثور الخطاب ٥/ ٣٣٩ رقم ٨٣٦٨ أنه حديث خاطب به رسول الله صلّى الله عليه وسلّم أبا ذر الغفاري.
(٣) كشف الخفاء ٢/ ٣١٦- ٣١٧ وشعب الإيمان ٦/ ٢٩٨ وفي قوت القلوب ٢/ ٢٠ أنه خبر منقول من التوراة.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (119,'ثم قال: إن القلب رقيق يؤثر فيه كل شيء، فاحذروا عليه واتقوا الله به. فسئل: متى يتخلص القلب من الفساد؟ قال: لا يتخلص إلا بمفارقة الظن والحيل، وكأن الحيل عند ربك كالكبائر عندنا، وقد قال النبي صلّى الله عليه وسلّم: «الكبيرة ما يشرح في صدرك والإثم ما حاك في صدرك وإن أفتاك المفتون وأفتوك. ثم قال: إن اضطرب القلب فهو حجة عليك» «١» .

<span data-type="title" id=toc-349>[سورة الروم (٣٠) : آية ٥٠]</span>
فَانْظُرْ إِلى آثارِ رَحْمَتِ اللَّهِ كَيْفَ يُحْيِ الْأَرْضَ بَعْدَ مَوْتِها إِنَّ ذلِكَ لَمُحْيِ الْمَوْتى وَهُوَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (٥٠)
قوله: فَانْظُرْ إِلى آثارِ رَحْمَتِ اللَّهِ [٥٠] قال: ظاهرها المطر، وباطنها حياة القلوب بالذكر، والله سبحانه وتعالى أعلم.
_________
(١) في مسند أحمد ٤/ ١٩٤ ونوادر الأصول ١/ ٢٣٩ والترغيب والترهيب ٢/ ٣٥١ وجامع العلوم والحكم ص ٢٥١: (البر: ما سكنت إليه النفس واطمأن إليه القلب، والإثم: ما لم تسكن إليه النفس ولم يطمئن إليه القلب، وإن أفتاك المفتون) وفي جامع العلوم رواية أخرى: (البر: ما انشرح له الصدر، والإثم ما حاك في صدرك، وإن أفتاك عنه الناس) .',NULL,'122',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (120,'<span data-type="title" id=toc-350>السورة التي يذكر فيها لقمان</span>

<span data-type="title" id=toc-351>[سورة لقمان (٣١) : آية ٦]</span>
وَمِنَ النَّاسِ مَنْ يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّخِذَها هُزُواً أُولئِكَ لَهُمْ عَذابٌ مُهِينٌ (٦)
قوله تعالى: وَمِنَ النَّاسِ مَنْ يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ [٦] قال: هو الجدال في الدين والخوض في الباطل.

<span data-type="title" id=toc-352>[سورة لقمان (٣١) : آية ١٥]</span>
وَإِنْ جاهَداكَ عَلى أَنْ تُشْرِكَ بِي ما لَيْسَ لَكَ بِهِ عِلْمٌ فَلا تُطِعْهُما وَصاحِبْهُما فِي الدُّنْيا مَعْرُوفاً وَاتَّبِعْ سَبِيلَ مَنْ أَنابَ إِلَيَّ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ (١٥)
قوله: وَاتَّبِعْ سَبِيلَ مَنْ أَنابَ [١٥] يعني من لم يهتد الطريق إلى الحق عزَّ وجلَّ فليتبع آثار الصالحين لتوصله بركة متابعتهم إلى طريق الحق، ألا ترى كيف نفع اتباع الصالحين كلب أصحاب الكهف، حتى ذكره الله تعالى بالخير مراراً، وقد قال النبي صلّى الله عليه وسلّم في ذلك الحديث: «هم الذين لا يشقى بهم جليسهم» «١» .

<span data-type="title" id=toc-353>[سورة لقمان (٣١) : الآيات ١٩ الى ٢٠]</span>
وَاقْصِدْ فِي مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَ إِنَّ أَنْكَرَ الْأَصْواتِ لَصَوْتُ الْحَمِيرِ (١٩) أَلَمْ تَرَوْا أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَّا فِي السَّماواتِ وَما فِي الْأَرْضِ وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظاهِرَةً وَباطِنَةً وَمِنَ النَّاسِ مَنْ يُجادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَلا هُدىً وَلا كِتابٍ مُنِيرٍ (٢٠)
قوله: إِنَّ أَنْكَرَ الْأَصْواتِ لَصَوْتُ الْحَمِيرِ [١٩] فإنه يصيح لرؤية الشيطان، فلذلك سماه الله تعالى منكراً. وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظاهِرَةً وَباطِنَةً [٢٠] الظاهرة محبة الصالحين، والباطنة سكون القلب إلى الله تعالى.

<span data-type="title" id=toc-354>[سورة لقمان (٣١) : آية ٢٢]</span>
وَمَنْ يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقى وَإِلَى اللَّهِ عاقِبَةُ الْأُمُورِ (٢٢)
قوله: وَمَنْ يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَهُوَ مُحْسِنٌ [٢٢] قال: من يخلص دينه لله عزَّ وجلَّ ويحسن أدب الإخلاص، بِالْعُرْوَةِ الْوُثْقى [٢٢] وهي السنة.
قوله: وَلا تُصَعِّرْ خَدَّكَ لِلنَّاسِ [١٨] أي لا تعرض وجهك عمن استرشدك الطريق إلينا، وعرفهم نعمتي وإحساني لديهم.

<span data-type="title" id=toc-355>[سورة لقمان (٣١) : آية ٣٤]</span>
إِنَّ اللَّهَ عِنْدَهُ عِلْمُ السَّاعَةِ وَيُنَزِّلُ الْغَيْثَ وَيَعْلَمُ ما فِي الْأَرْحامِ وَما تَدْرِي نَفْسٌ مَّاذَا تَكْسِبُ غَداً وَما تَدْرِي نَفْسٌ بِأَيِّ أَرْضٍ تَمُوتُ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ (٣٤)
قوله: وَما تَدْرِي نَفْسٌ مَّاذَا تَكْسِبُ غَداً [٣٤] أي ما له وعليه في الغيب من المقدور فاحذروه بإقامة ذكره والصراخ إليه، حتى يكون هو المتولي لشأنهم، كما قال: يَمْحُوا اللَّهُ مَا يَشاءُ وَيُثْبِتُ [الرعد: ٣٩] .
قوله تعالى: وَما تَدْرِي نَفْسٌ بِأَيِّ أَرْضٍ تَمُوتُ [٣٤] قال: على أي حكم تموت من السعادة والشقاوة، ولذلك قال الرسول صلّى الله عليه وسلّم: «لا تغرنكم كثرة الأعمال فإن الأعمال بالخواتيم» «٢» .
_________
(١) صحيح البخاري: كتاب الدعوات رقم ٦٠٤٥ وصحيح مسلم: كتاب الذكر رقم ٢٦٨٩.
(٢) مسند أحمد ٥/ ٣٣٥ والترغيب والترهيب ٤/ ٤٨.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (121,'وكان يقول: «يا ولي الإسلام وأهله ثبتني بالإسلام حتى ألقاك به» «١» ، وقال: «يا مقلب القلوب والأبصار ثبت قلبي على دينك» «٢» ، مع ما أمنه الله من عاقبته، وإنما قال ذلك تأديباً ليقتدوا به، ويظهروا فقرهم وفاقتهم إلى الله عزَّ وجلَّ، ويتركوا السكون إلى الأمن من مكره، ولذلك قال إبراهيم عليه الصلاة والسلام: وَاجْنُبْنِي وَبَنِيَّ أَنْ نَعْبُدَ الْأَصْنامَ
[إبراهيم: ٣٥] وقال يوسف عليه السلام: تَوَفَّنِي مُسْلِماً وَأَلْحِقْنِي بِالصَّالِحِينَ [يوسف: ١٠١] فهذا كله تبرٍّ من الحول والقوة بالافتقار إليه، كما قال: لَوْلا دُعاؤُكُمْ [الفرقان: ٧٧] أي تبريكم من كل شيء سواي قولاً، وقال: أنتم الفقراء إلى الله عزّ وجلّ.
_________
(١) تقدم الحديث في تفسير سورة آل عمران.
(٢) المستدرك على الصحيحين ١/ ٧٠٦- ٧٠٧، ٢/ ٣١٧، ٤/ ٥٣٧ وسنن الترمذي ٤/ ٤٤٨ (رقم ٢١٤٠) ٥/ ٥٣٨ (رقم ٣٥٢٢) ، ٥/ ٥٧٣ (رقم ٣٥٨٧) ومجمع الزوائد ٦/ ٣٢٥، ٧/ ٢١٠.',NULL,'124',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (122,'<span data-type="title" id=toc-356>السورة التي يذكر فيها السجدة</span>

<span data-type="title" id=toc-357>[سورة السجده (٣٢) : آية ٥]</span>
يُدَبِّرُ الْأَمْرَ مِنَ السَّماءِ إِلَى الْأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِي يَوْمٍ كانَ مِقْدارُهُ أَلْفَ سَنَةٍ مِمَّا تَعُدُّونَ (٥)
قوله تعالى: يُدَبِّرُ الْأَمْرَ مِنَ السَّماءِ إِلَى الْأَرْضِ [٥] قال: يوحي من أمره إلى عبيده ما لهم فيه هدى ونجاة، يطوي لمن رضي رزق القضاة بتدبير الله له، وأسقط عنه سوء تدبيره، ورده إلى حال الرضا بالقضاء والاستقامة في جريان المقدور عليه أولئك من المقربين، وأن الله تعالى خلق الخلق من غير حجاب، ثم جعل حجابهم تدبيرهم.

<span data-type="title" id=toc-358>[سورة السجده (٣٢) : آية ١٣]</span>
وَلَوْ شِئْنا لَآتَيْنا كُلَّ نَفْسٍ هُداها وَلكِنْ حَقَّ الْقَوْلُ مِنِّي لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ (١٣)
قوله تعالى: وَلَوْ شِئْنا لَآتَيْنا كُلَّ نَفْسٍ هُداها [١٣] قال: لو شئنا لحققنا دعاوي المحقين، وأدحضنا براهين المبطلين.

<span data-type="title" id=toc-359>[سورة السجده (٣٢) : الآيات ١٥ الى ١٧]</span>
إِنَّما يُؤْمِنُ بِآياتِنَا الَّذِينَ إِذا ذُكِّرُوا بِها خَرُّوا سُجَّداً وَسَبَّحُوا بِحَمْدِ رَبِّهِمْ وَهُمْ لا يَسْتَكْبِرُونَ (١٥) تَتَجافى جُنُوبُهُمْ عَنِ الْمَضاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفاً وَطَمَعاً وَمِمَّا رَزَقْناهُمْ يُنْفِقُونَ (١٦) فَلا تَعْلَمُ نَفْسٌ مَّآ أُخْفِيَ لَهُمْ مِنْ قُرَّةِ أَعْيُنٍ جَزاءً بِما كانُوا يَعْمَلُونَ (١٧)
قوله تعالى: إِنَّما يُؤْمِنُ بِآياتِنَا الَّذِينَ إِذا ذُكِّرُوا بِها خَرُّوا سُجَّداً [١٥] قال: لا يجد العبد لذة الإيمان حتى يغلب علمه جهله، ويكون الغالب على قلبه الرحمة.
قوله تعالى: تَتَجافى جُنُوبُهُمْ عَنِ الْمَضاجِعِ [١٦] قال: إن الله تعالى وهب لقوم هبة، وهو أن أدناهم من مناجاته، وجعلهم من أهل وسيلته وصلته، ثم مدحهم إلى إظهار الكرم بأنه وفقهم على ما وفقهم له، فقال: تَتَجافى جُنُوبُهُمْ عَنِ الْمَضاجِعِ [١٦] .
قوله تعالى: يَدْعُونَ رَبَّهُمْ خَوْفاً وَطَمَعاً [١٦] قال: أي خوفاً من هجرانه وطمعاً في لقائه.
قوله عزَّ وجلَّ: فَلا تَعْلَمُ نَفْسٌ مَّآ أُخْفِيَ لَهُمْ مِنْ قُرَّةِ أَعْيُنٍ [١٧] قال: أعينهم بما شاهدوا من ظاهر الحقائق، وباطنها التي كشفت لهم من مكاشفات، فرأوها وتمسكوا بها، فقرّت أعينهم، وسكنت إليها قلوبهم، وغيرهم لا يعلمون ما أخفي لهم.
والله سبحانه وتعالى أعلم.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (123,'<span data-type="title" id=toc-360>السورة التي يذكر فيها الأحزاب</span>

<span data-type="title" id=toc-361>[سورة الأحزاب (٣٣) : آية ٤]</span>
مَّا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ وَما جَعَلَ أَزْواجَكُمُ اللاَّئِي تُظاهِرُونَ مِنْهُنَّ أُمَّهاتِكُمْ وَما جَعَلَ أَدْعِياءَكُمْ أَبْناءَكُمْ ذلِكُمْ قَوْلُكُمْ بِأَفْواهِكُمْ وَاللَّهُ يَقُولُ الْحَقَّ وَهُوَ يَهْدِي السَّبِيلَ (٤)
قوله تعالى: مَّا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ [٤] قال: المتوجه إلى الله عزَّ وجلَّ قصداً من غير التفات، فمن نظر إلى شيء سوى الله فما هو بقاصد إلى ربه، وإن الله تعالى يقول:
مَّا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ [٤] قيل: قلب يقبل به على ربه، وقلب يدبر به أمور الدنيا. وللعقل طبعان: طبع للدنيا وطبع للآخرة مؤتلف بطبع نفس الروح، فطبع الآخرة منطبع بطبع نفس الروح، وطبع الدنيا مؤتلف بالنفس الشهوانية. ولهذا قال الرسول صلّى الله عليه وسلّم: «لا تكلني إلى نفسي طرفة عين» «١» ، فإن العبد ما دام مشتغلاً بنفسه فهو محجوب عن الله عزّ وجلّ.

<span data-type="title" id=toc-362>[سورة الأحزاب (٣٣) : آية ٦]</span>
النَّبِيُّ أَوْلى بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ وَأَزْواجُهُ أُمَّهاتُهُمْ وَأُولُوا الْأَرْحامِ بَعْضُهُمْ أَوْلى بِبَعْضٍ فِي كِتابِ اللَّهِ مِنَ الْمُؤْمِنِينَ وَالْمُهاجِرِينَ إِلاَّ أَنْ تَفْعَلُوا إِلى أَوْلِيائِكُمْ مَعْرُوفاً كانَ ذلِكَ فِي الْكِتابِ مَسْطُوراً (٦)
قوله تعالى: النَّبِيُّ أَوْلى بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ [٦] قال: من لم ير نفسه في ملك الرسول صلّى الله عليه وسلّم، ولم ير ولاية الرسول صلّى الله عليه وسلّم في جميع الأحوال لم يذق حلاوة سنته بحال، لأن النبي صلى الله عليه وسلّم هو أولى بالمؤمنين، والنبي صلّى الله عليه وسلّم يقول: «لا يؤمن أحدكم حتى أكون أحب إليه من نفسه وماله وولده والناس أجمعين» «٢» .

<span data-type="title" id=toc-363>[سورة الأحزاب (٣٣) : آية ٨]</span>
لِيَسْئَلَ الصَّادِقِينَ عَنْ صِدْقِهِمْ وَأَعَدَّ لِلْكافِرِينَ عَذاباً أَلِيماً (٨)
قوله تعالى: لِيَسْئَلَ الصَّادِقِينَ عَنْ صِدْقِهِمْ وَأَعَدَّ لِلْكافِرِينَ عَذاباً أَلِيماً [٨] قال عبد الواحد بن زيد «٣» : الصدق الوفاء لله بالعمل. وسئل سهل عن الصدق فقال: الصدق خوف الخاتمة، والصبر شاهد الصدق، وإنما صعب الصدق على الصديقين، والإخلاص على المخلصين، والتوبة على التائبين، لأن هذه التلبية لها حكم بدل الروح. قيل لأحمد بن متى: ما معناه؟ قال: أن لا يبقى للنفس نصيب «٤» . وقال سهل: لا يشم أحد رائحة الصدق ما دام يداهن نفسه أو غيره. بل الصدق أن يكون في سره أنه ليس على وجه الأرض أحد طالبه الله بالعبودية غيره، ويكون رجاؤه خوفه، وخوفه انتقاله، فإذا رآهم الله تعالى على هذه الحالة تولى أمورهم وكفاهم، فصارت كل شعرة من شعورهم تنطق مع الله بالمعرفة، فيقول الله تعالى لهم يوم القيامة: «لمن عملتم، ماذا أردتم؟ فيقولون: لك عملنا، وإياك أردنا. فيقول: صدقتم» . فوعزته فقوله لهم في المشاهدة: «صدقتم» ألذ عندهم من نعيم الجنة. فقيل لأحمد بن متى: ما معنى
_________
(١) المستدرك على الصحيحين ١/ ٧٣٠ والسنن الكبرى ٦/ ١٤٧ (١٠٤٠٥) ، ٦/ ١٦٧ (١٠٤٨٧) ومجمع الزوائد ١٠/ ١١٧، ١٣٧، ١٨١ ومسند أحمد ٥/ ٤٢.
(٢) صحيح البخاري: الإيمان، رقم ١٤- ١٥ وصحيح مسلم: الإيمان، رقم ٤٤.
(٣) عبد الواحد بن زيد ( ... - ١٧٧ هـ) : واعظ من متصوفة البصرة. تلقى أصول علومه عن الحسن البصري.
(الحلية ٦/ ١٥٦) . [.....]
(٤) فيض القدير ٤/ ٣٤٣ وكتاب الزهد الكبير ٢/ ٣٤٣.',NULL,'126',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (124,'قوله: رجاء الصدق خوفه، وخوفه انتقاله؟ فقال: لأن الصدق رجاؤهم وطلبهم، ويخافون في طلبهم أن لا يكونوا صادقين، فلا يقبل الله منهم، كما قال: وَالَّذِينَ يُؤْتُونَ مَآ آتَوْا وَقُلُوبُهُمْ وَجِلَةٌ [المؤمنون: ٦٠] أي وجلة في الطاعة خوف الرد عليهم.

<span data-type="title" id=toc-364>[سورة الأحزاب (٣٣) : آية ٣٥]</span>
إِنَّ الْمُسْلِمِينَ وَالْمُسْلِماتِ وَالْمُؤْمِنِينَ وَالْمُؤْمِناتِ وَالْقانِتِينَ وَالْقانِتاتِ وَالصَّادِقِينَ وَالصَّادِقاتِ وَالصَّابِرِينَ وَالصَّابِراتِ وَالْخاشِعِينَ وَالْخاشِعاتِ وَالْمُتَصَدِّقِينَ وَالْمُتَصَدِّقاتِ وَالصَّائِمِينَ وَالصَّائِماتِ وَالْحافِظِينَ فُرُوجَهُمْ وَالْحافِظاتِ وَالذَّاكِرِينَ اللَّهَ كَثِيراً وَالذَّاكِراتِ أَعَدَّ اللَّهُ لَهُمْ مَغْفِرَةً وَأَجْراً عَظِيماً (٣٥)
قوله: إِنَّ الْمُسْلِمِينَ وَالْمُسْلِماتِ [٣٥] قال: الإيمان أفضل من الإسلام، والتقوى في الإيمان أفضل من الإيمان، واليقين في التقوى أفضل من التقوى، والصدق في اليقين أفضل من اليقين، وإنما تمسكتم بالأنا فإياكم أن تنفلت من أيديكم. وقال: الإيمان بالله في القلب ثابت، واليقين بالصدق راسخ، فصدق العين ترك النظر إلى المحظورات، وصدق اللسان في ترك ما لا يعني، وصدق اليد ترك البطش للحرام، وصدق الرجلين ترك المشي إلى الفواحش، وحقيقة الصدق من دوام النظر فيما مضى، وترك النظر فيما بقي، وإن الله تعالى أعطى الصديقين من العلم ما لو نطقوا به لنفذ البحر من نطقهم، وهم مختفون لا يظهرون للناس إلا فيما لا بد لهم منه، حتى يخرج العبد الصالح، فعند ذلك يظهرون، ويعلمون العلماء من علومهم.
قوله تعالى: وَالذَّاكِرِينَ اللَّهَ كَثِيراً وَالذَّاكِراتِ [٣٥] قال: الذاكر على الحقيقة من يعلم أن الله مشاهده فيراه بقلبه قريباً منه، فيستحي منه، ثم يؤثره على نفسه وعلى كل شيء من جميع أحواله. وسئل سهل مرة أخرى: ما الذكر؟ فقال: الطاعة. قيل: ما الطاعة؟ قال: الإخلاص قيل: ما الإخلاص؟ قال: المشاهدة. قيل: ما المشاهدة؟ قال: العبودية. قيل: ما العبودية؟ قال:
الرضا. قيل: ما الرضا؟ قال: الافتقار. قيل: ما الافتقار؟ قال: التضرع والالتجاء سلم سلم إلى الممات. وقال ابن سالم: الذكر ثلاث: ذكر باللسان فذاك الحسنة بعشر، وذكر بالقلب فذاك الحسنة بسبعمائة، وذكر لا يوزن ثوابه وهو الامتلاء من المحبة.

<span data-type="title" id=toc-365>[سورة الأحزاب (٣٣) : آية ٣٨]</span>
ما كانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيما فَرَضَ اللَّهُ لَهُ سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ وَكانَ أَمْرُ اللَّهِ قَدَراً مَقْدُوراً (٣٨)
قوله: وَكانَ أَمْرُ اللَّهِ قَدَراً مَقْدُوراً [٣٨] قال: أي معلوماً قبل وقوعه عندكم، وهل يقدر أحد أن يتقي المقدور؟ وقد قال عمر رضي الله عنه لما طعن: «وَكانَ أَمْرُ اللَّهِ قَدَراً مَقْدُوراً [٣٨] ، ولقد أخبرني رسول الله صلّى الله عليه وسلّم أنهم سيفعلون هذا» . وحكي عن الضحاك أنه ينزل ملكان من السماء ومع أحدهما صحيفة فيها كتاب، ومع الآخر صحيفة ليس فيها كتاب، فيكتب عمل العبد وأثره، فإذا أراد أن يصعد قَال لصاحب الصحيفة المكتوبة: عارضني فيعارضه، فلا يخطىء حرفا.

<span data-type="title" id=toc-366>[سورة الأحزاب (٣٣) : آية ٧١]</span>
يُصْلِحْ لَكُمْ أَعْمالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ فَقَدْ فازَ فَوْزاً عَظِيماً (٧١)
قوله: يُصْلِحْ لَكُمْ أَعْمالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ [٧١] قال: من وفقه الله لصالح الأعمال فذاك دليل على أنه مغفور له، لأن الله تعالى قال: يُصْلِحْ لَكُمْ أَعْمالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ [٧١] .
والله سبحانه وتعالى أعلم.',NULL,'127',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (125,'<span data-type="title" id=toc-367>السورة التي يذكر فيها سبأ</span>
قوله تعالى: قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ مِنْ عِبادِهِ وَيَقْدِرُ لَهُ [٣٩] قال:
الرزق على وجهين:
رزق وهو ذكر لنفس الروح والعقل والقلب، مثل عيش الملائكة، وحياتهم بالذكر، متى أمسك عنهم ماتوا.
والرزق الآخر هو المأكول والمشروب ونحو ذلك لنفع الطبع، وفيه يقع الحلال والحرام فالحلال ما رزقه الله تعالى وأمر بالأخذ منه، والحرام ما رزقه الله تعالى ونهى عنه، وهو قسمة النار ولا أعلم شيئاً أشد من كف الأذى وأكل الحلال.

<span data-type="title" id=toc-368>[سورة سبإ (٣٤) : آية ٣٧]</span>
وَما أَمْوالُكُمْ وَلا أَوْلادُكُمْ بِالَّتِي تُقَرِّبُكُمْ عِنْدَنا زُلْفى إِلاَّ مَنْ آمَنَ وَعَمِلَ صالِحاً فَأُولئِكَ لَهُمْ جَزاءُ الضِّعْفِ بِما عَمِلُوا وَهُمْ فِي الْغُرُفاتِ آمِنُونَ (٣٧)
قوله تعالى: وَما أَمْوالُكُمْ وَلا أَوْلادُكُمْ بِالَّتِي تُقَرِّبُكُمْ عِنْدَنا زُلْفى [٣٧] قال: الزلفى هو القرب من الله تعالى.

<span data-type="title" id=toc-369>[سورة سبإ (٣٤) : آية ٣٩]</span>
قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ مِنْ عِبادِهِ وَيَقْدِرُ لَهُ وَما أَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُ وَهُوَ خَيْرُ الرَّازِقِينَ (٣٩)
قوله: وَما أَنْفَقْتُمْ مِنْ شَيْءٍ فَهُوَ يُخْلِفُهُ [٣٩] قال: الخلف على الإنفاق، والأنس بالعيش مع الله تعالى، والسرور به.

<span data-type="title" id=toc-370>[سورة سبإ (٣٤) : آية ٤٦]</span>
قُلْ إِنَّما أَعِظُكُمْ بِواحِدَةٍ أَنْ تَقُومُوا لِلَّهِ مَثْنى وَفُرادى ثُمَّ تَتَفَكَّرُوا ما بِصاحِبِكُمْ مِنْ جِنَّةٍ إِنْ هُوَ إِلاَّ نَذِيرٌ لَكُمْ بَيْنَ يَدَيْ عَذابٍ شَدِيدٍ (٤٦)
قوله: إِنَّما أَعِظُكُمْ بِواحِدَةٍ أَنْ تَقُومُوا لِلَّهِ مَثْنى وَفُرادى [٤٦] قال: يرجع الحساب يوم القيامة إلى أربعة:
الصدق في الأقوال، والإخلاص في الأعمال، والاستقامة مع الله في جميع الأحوال، ومراقبة الله على كل حال.
والله سبحانه وتعالى أعلم.',NULL,'128',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (126,'<span data-type="title" id=toc-371>السورة التي يذكر فيها فاطر</span>

<span data-type="title" id=toc-372>[سورة فاطر (٣٥) : آية ٦]</span>
إِنَّ الشَّيْطانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا إِنَّما يَدْعُوا حِزْبَهُ لِيَكُونُوا مِنْ أَصْحابِ السَّعِيرِ (٦)
قوله تعالى: إِنَّما يَدْعُوا حِزْبَهُ [٦] يعني الشيطان يدعو أهل طاعته من أهل الأهواء والبدع والضلالات والسامعين ذلك من قائلها.

<span data-type="title" id=toc-373>[سورة فاطر (٣٥) : آية ١٠]</span>
مَنْ كانَ يُرِيدُ الْعِزَّةَ فَلِلَّهِ الْعِزَّةُ جَمِيعاً إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ وَالَّذِينَ يَمْكُرُونَ السَّيِّئاتِ لَهُمْ عَذابٌ شَدِيدٌ وَمَكْرُ أُولئِكَ هُوَ يَبُورُ (١٠)
قوله: إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ [١٠] قال: ظاهرها الدعاء والصدقة، وباطنها الذكر، عملاً بالعلم، وإقبالاً بالسنة، يرفعه أي يوصله بالإخلاص فيه لله تعالى.

<span data-type="title" id=toc-374>[سورة فاطر (٣٥) : آية ١٥]</span>
يا أَيُّهَا النَّاسُ أَنْتُمُ الْفُقَراءُ إِلَى اللَّهِ وَاللَّهُ هُوَ الْغَنِيُّ الْحَمِيدُ (١٥)
قوله: يا أَيُّهَا النَّاسُ أَنْتُمُ الْفُقَراءُ إِلَى اللَّهِ [١٥] قال: يعني أنتم إليه في أنفسكم، فإن الله تعالى لما خلق الخلق حكم لعباده بالفقر إليه، وهو الغني، فمن ادعى الغنى حجب عن الله عزَّ وجلَّ، ومن أظهر فقره إليه أوصل الله فقره بغناه، فينبغي للعبد أن يكون مفتقراً إليه في السر، منقطعاً عن غيره، حتى تكون عبوديته محضة، إذ العبودية المحضة هي الذل والخضوع.
فقيل له: وكيف يفتقر إليه؟ قال: إظهار الفقر في ثلاث: فقرهم القديم، وفقرهم في حالهم، وفقرهم في موت أنفسهم من تدبيرهم ومن لم يكن كذلك فهو مدّعٍ في فقره. وقال: الفقير الصادق الذي لا يسأل ولا يرد ولا يحبس. وقال عمر بن عبد العزيز «١» رضي الله عنه: صفة أولياء الله عزَّ وجلَّ ثلاثة أشياء: الثقة بالله تعالى في كل شيء، والفقر إليه في كل شيء، والرجوع إليه من كل شيء.

<span data-type="title" id=toc-375>[سورة فاطر (٣٥) : آية ٣٢]</span>
ثُمَّ أَوْرَثْنَا الْكِتابَ الَّذِينَ اصْطَفَيْنا مِنْ عِبادِنا فَمِنْهُمْ ظالِمٌ لِنَفْسِهِ وَمِنْهُمْ مُقْتَصِدٌ وَمِنْهُمْ سابِقٌ بِالْخَيْراتِ بِإِذْنِ اللَّهِ ذلِكَ هُوَ الْفَضْلُ الْكَبِيرُ (٣٢)
قوله: ثُمَّ أَوْرَثْنَا الْكِتابَ الَّذِينَ اصْطَفَيْنا مِنْ عِبادِنا [٣٢] قال عمر بن واصل: سمعت سهلاً يقول: السابق العالم، والمقتصد المتعلم، والظالم الجاهل «٢» . وقال أيضاً: السابق الذي اشتغل بمعاده، والمقتصد الذي اشتغل بمعاده ومعاشه، والظالم الذي اشتغل بمعاشه دون معاده.
وقال الحسن البصري رحمه الله: السابق الذي رجحت حسناته على سيئاته، والمقتصد الذي استوت حسناته وسيئاته، والظالم الذي رجحت سيئاته على حسناته.

<span data-type="title" id=toc-376>[سورة فاطر (٣٥) : آية ٣٤]</span>
وَقالُوا الْحَمْدُ لِلَّهِ الَّذِي أَذْهَبَ عَنَّا الْحَزَنَ إِنَّ رَبَّنا لَغَفُورٌ شَكُورٌ (٣٤)
قوله: الْحَمْدُ لِلَّهِ الَّذِي أَذْهَبَ عَنَّا الْحَزَنَ [٣٤] أي حزن القطيعة، إِنَّ رَبَّنا لَغَفُورٌ شَكُورٌ [٣٤] يعني غفور لذنوب كثيرة، شكور لأعمال يسيرة.
_________
(١) عمر بن عبد العزيز بن مروان بن الحكم الأموي القرشي (٦١- ١٠١ هـ) : الخليفة الصالح والملك العادل، وخامس الخلفاء الراشدين. توفي مسموما بعد سنتين من خلافته. (الأعلام ٥/ ٥٠) .
(٢) تفسير القرطبي ١٤/ ٣٤٨، وفيه أقوال أخرى.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (127,'<span data-type="title" id=toc-377>السورة التي يذكر فيها يس صلّى الله عليه وسلّم</span>

<span data-type="title" id=toc-378>[سورة يس (٣٦) : آية ١١]</span>
إِنَّما تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمنَ بِالْغَيْبِ فَبَشِّرْهُ بِمَغْفِرَةٍ وَأَجْرٍ كَرِيمٍ (١١)
قوله تعالى: إِنَّما تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمنَ بِالْغَيْبِ [١١] قال: من عبد الله في سره أورثه اليقين، ومن عبد الله بصدق اللسان لم يستقر قلبه دون العرش، ومن عبد الله بالإنصاف كانت السماوات والأرض في ميزانه. قيل: وما الإنصاف؟ قال: الإنصاف أن لا تتحرك جميع أعضائك إلا لله، ومتى طالبته برزق الغد فقد ذهب إنصافك، لأن القلب لا يحمل همين، والإنصاف بينك وبين الخلق أن تأخذ بالفضل، فإذا طلبت الإنصاف فلست بمنصف.
وحكي عن يحيى وعيسى عليهما السلام أنهما خرجا يمشيان، فصدم يحيى امرأة، فقال له عيسى: يا ابن خالتي، لقد أصبت اليوم خطيئة، ما أرى الله يغفرها لك. قال: وما هي؟ قال:
صدمت امرأة. قال: والله ما شعرت بها. قال عيسى: سبحان الله، بدنك معي، فأين قلبك؟
قال: معلق بالعرش، ولو أن قلبي اطمأن إلى جبريل صلوات الله عليه طرفة عين، لظننت أني ما عرفت الله عزّ وجلّ.

<span data-type="title" id=toc-379>[سورة يس (٣٦) : آية ٢٢]</span>
وَما لِيَ لاَ أَعْبُدُ الَّذِي فَطَرَنِي وَإِلَيْهِ تُرْجَعُونَ (٢٢)
قوله: وَما لِيَ لاَ أَعْبُدُ الَّذِي فَطَرَنِي [٢٢] وسئل عن خير العبادات فقال: الإخلاص لقوله: وَما أُمِرُوا إِلَّا لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ [البينة: ٥] ولا يخلص العمل لأحد، ولا تتم عبادته وهو يفر من أربع: الجوع والعري والفقر والذلة، وإن الله تعالى استعبد الخلق بهذه الثلاث: العقل والروح والقوة، وإذا خاف على اثنين منها، ذهاب عقله وذهاب روحه، تكلف لها بشيء، وأما القوة فلا يتكلف لها ولا يفطن لها، وإن صلى جالسا.

<span data-type="title" id=toc-380>[سورة يس (٣٦) : آية ٦٦]</span>
وَلَوْ نَشاءُ لَطَمَسْنا عَلى أَعْيُنِهِمْ فَاسْتَبَقُوا الصِّراطَ فَأَنَّى يُبْصِرُونَ (٦٦)
قوله: وَلَوْ نَشاءُ لَطَمَسْنا عَلى أَعْيُنِهِمْ [٦٦] قال: يعني ولو نشاء لفقأنا أعين قلوبهم التي يبصرون الكفر وطريقه، فيبصرون طريق الإسلام، ولا يبصرون غيره، فَأَنَّى يُبْصِرُونَ [٦٦] طريق الإسلام، ولم يفعل ذلك.

<span data-type="title" id=toc-381>[سورة يس (٣٦) : آية ٦٩]</span>
وَما عَلَّمْناهُ الشِّعْرَ وَما يَنْبَغِي لَهُ إِنْ هُوَ إِلاَّ ذِكْرٌ وَقُرْآنٌ مُبِينٌ (٦٩)
قوله: إِنْ هُوَ إِلَّا ذِكْرٌ وَقُرْآنٌ مُبِينٌ [٦٩] قال: هو الذكر والتفكر.
والله سبحانه وتعالى أعلم.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (128,'<span data-type="title" id=toc-382>السورة التي يذكر فيها الصافات</span>

<span data-type="title" id=toc-383>[سورة الصافات (٣٧) : آية ٨٤]</span>
إِذْ جاءَ رَبَّهُ بِقَلْبٍ سَلِيمٍ (٨٤)
قوله تعالى: إِذْ جاءَ رَبَّهُ بِقَلْبٍ سَلِيمٍ [٨٤] أي مستسلم مفوض إلى ربه بكل حال راجع لسره.

<span data-type="title" id=toc-384>[سورة الصافات (٣٧) : الآيات ٨٨ الى ٨٩]</span>
فَنَظَرَ نَظْرَةً فِي النُّجُومِ (٨٨) فَقالَ إِنِّي سَقِيمٌ (٨٩)
قوله تعالى: فَنَظَرَ نَظْرَةً فِي النُّجُومِ (٨٨) فَقالَ إِنِّي سَقِيمٌ [٨٨- ٨٩] قال: وحكي عن محمد بن سوار عن أبي عمرو بن العلاء قال: معناه نظر إلى النبات، كقوله: وَالنَّجْمُ وَالشَّجَرُ يَسْجُدانِ [الرحمن: ٦] وأراد بالنجم ما لا ساق له من النبات، وبالشجر ما له ساق.

<span data-type="title" id=toc-385>[سورة الصافات (٣٧) : آية ١٠٧]</span>
وَفَدَيْناهُ بِذِبْحٍ عَظِيمٍ (١٠٧)
قوله: وَفَدَيْناهُ بِذِبْحٍ عَظِيمٍ [١٠٧] قال: إبراهيم عليه الصلاة والسلام لما أحب ولده بطبع البشرية تداركه من الله فضله وعصمته حتى أمره بذبحه، إذ لم يكن المراد منه تحصيل الذبح، وإنما كان المقصود تخليص السر من حب غيره بأبلغ الأسباب، فلما خلص السر له ورجع عن عادة الطبع فداه بذبح عظيم.
قوله: إِنَّ هذا لَهُوَ الْبَلاءُ الْمُبِينُ [١٠٦] قال: يعني بلاء رحمة، ألا ترون كيف بعثه على الرضى. قال: وبلغنا أنه مكتوب في الزبور: «ما قضيت على مؤمن قضاء أحَبَّه أو كرهه إلا وهو خير له» . وحكي أن الله تعالى أوحى إلى إبراهيم صلوات الله عليه: ما من أحد وسّعتُ إليه إلا أنقصت بقدره من آخرته، ولو كنت أنت يا خليلي. وقال أبو يعقوب السوسي: جاءنا فقير ونحن بأرجَّان وسهل بن عبد الله يومئذ بها، فقال: إنكم أهل العناية فقد نزلت بي محنة، فقال له سهل: في ديوان المحنة وقعت منذ تعرضت لهذا الأمر، فما هي؟ قال: فتح لي شيء من الدنيا فاستأثرت به في غير ذوي محرم ففقدت إيماني وحالي. فقال سهل: ما تقول في هذا يا أبا يعقوب؟ فقلت: محنته بحاله أعظم من محنته بإيمانه. فقال لي سهل: مثلك يقول هذا يا أبا يعقوب «١» ؟ وسئل سهل عن الحال فقال: حال الذكر من العلم السكون، وحال الذكر من العقل الطمأنينة، وحال التقوى من الإسلام الحدود، ومن الإيمان الطمأنينة. وقال: إذا كان للعبد حال فدخل عليه البلوى، فإن طلب الفرج بحال دون تلك الحال فهو منه حدث. قيل: وكيف ذلك؟
قال: مثل أن يكون جائعاً فيطلب الشبع، لأن درجة الجائع أعلى.

<span data-type="title" id=toc-386>[سورة الصافات (٣٧) : آية ١٤٣]</span>
فَلَوْلا أَنَّهُ كانَ مِنَ الْمُسَبِّحِينَ (١٤٣)
قوله: فَلَوْلا أَنَّهُ كانَ مِنَ الْمُسَبِّحِينَ [١٤٣] قال: يعني من القائمين بحقوق الله تعالى قبل البلاء، والله سبحانه وتعالى أعلم.
_________
(١) اللمع لابن السراج ص ١٩٣.',NULL,'131',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (129,'<span data-type="title" id=toc-387>السورة التي يذكر فيها ص</span>

<span data-type="title" id=toc-388>[سورة ص (٣٨) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
ص وَالْقُرْآنِ ذِي الذِّكْرِ (١)
قوله تعالى: ص وَالْقُرْآنِ ذِي الذِّكْرِ [١] قال: ذي الشأن الشافي والوعظ الكافي.

<span data-type="title" id=toc-389>[سورة ص (٣٨) : آية ٦]</span>
وَانْطَلَقَ الْمَلَأُ مِنْهُمْ أَنِ امْشُوا وَاصْبِرُوا عَلى آلِهَتِكُمْ إِنَّ هذا لَشَيْءٌ يُرادُ (٦)
قوله: أَنِ امْشُوا وَاصْبِرُوا عَلى آلِهَتِكُمْ [٦] قال: هو الصبر المذموم الذي وبخ الله به الكفار. وقد سمعته يقول: الصبر على أربع مقامات: صبر على الطاعة، وصبر على الألم، وصبر على التألم، وصبر مذموم وهو الإقامة على المخالفة.

<span data-type="title" id=toc-390>[سورة ص (٣٨) : آية ٢٠]</span>
وَشَدَدْنا مُلْكَهُ وَآتَيْناهُ الْحِكْمَةَ وَفَصْلَ الْخِطابِ (٢٠)
قوله: وَآتَيْناهُ الْحِكْمَةَ وَفَصْلَ الْخِطابِ [٢٠] قال: إنما أعطاه الله ذلك حين سأله أن يرفع منزلته على منزلة إسماعيل وإسحاق، فقال: لست هناك يا داود، ولكني أجعل لك مقاماً من الحكمة، وفاصلة، وهي: «أما بعد» . وهو أول من قال ذلك، وبعده قس بن ساعدة «١» .
وقد قيل: فصل الإيمان لخطاب البيان.
قوله: وَشَدَدْنا مُلْكَهُ [٢٠] قال: أي بالعدل وبالوزراء الصالحين يدلونه على الخير، كما قال الرسول صلّى الله عليه وسلّم: «إن الله تعالى إذا أراد بوالٍ خيراً جعل له وزيراً صدوقاً إن نسي ذكره وإن ذكر أعانه» .

<span data-type="title" id=toc-391>[سورة ص (٣٨) : آية ٢٤]</span>
قالَ لَقَدْ ظَلَمَكَ بِسُؤالِ نَعْجَتِكَ إِلى نِعاجِهِ وَإِنَّ كَثِيراً مِنَ الْخُلَطاءِ لَيَبْغِي بَعْضُهُمْ عَلى بَعْضٍ إِلاَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ وَقَلِيلٌ ما هُمْ وَظَنَّ داوُدُ أَنَّما فَتَنَّاهُ فَاسْتَغْفَرَ رَبَّهُ وَخَرَّ راكِعاً وَأَنابَ (٢٤)
قوله: وَخَرَّ راكِعاً وَأَنابَ [٢٤] قال: الإِنابة هي الرجوع من الغفلة إلى الذكر، مع انكسار القلب وانتظار المقت.

<span data-type="title" id=toc-392>[سورة ص (٣٨) : آية ٢٦]</span>
يا داوُدُ إِنَّا جَعَلْناكَ خَلِيفَةً فِي الْأَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَلا تَتَّبِعِ الْهَوى فَيُضِلَّكَ عَنْ سَبِيلِ اللَّهِ إِنَّ الَّذِينَ يَضِلُّونَ عَنْ سَبِيلِ اللَّهِ لَهُمْ عَذابٌ شَدِيدٌ بِما نَسُوا يَوْمَ الْحِسابِ (٢٦)
قوله: وَلا تَتَّبِعِ الْهَوى فَيُضِلَّكَ عَنْ سَبِيلِ اللَّهِ [٢٦] قال: أي ظلمة الهوى تستر أنوار ذهن النفس والروح وفهم العقل وفطنة القلب، كما قال النبي صلّى الله عليه وسلّم: «إن الهوى والشهوة يغلبان العقل والعلم» «٢» والبيان لسابق القدرة من الله تعالى.

<span data-type="title" id=toc-393>[سورة ص (٣٨) : آية ٣٢]</span>
فَقالَ إِنِّي أَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّي حَتَّى تَوارَتْ بِالْحِجابِ (٣٢)
قوله: إِنِّي أَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّي [٣٢] قال: عن صلاة العصر وحدها.

<span data-type="title" id=toc-394>[سورة ص (٣٨) : آية ٣٥]</span>
قالَ رَبِّ اغْفِرْ لِي وَهَبْ لِي مُلْكاً لاَّ يَنْبَغِي لِأَحَدٍ مِنْ بَعْدِي إِنَّكَ أَنْتَ الْوَهَّابُ (٣٥)
قوله: قالَ رَبِّ اغْفِرْ لِي وَهَبْ لِي مُلْكاً لا يَنْبَغِي لِأَحَدٍ مِنْ بَعْدِي [٣٥] قال: ألهم الله تعالى سليمان أن يسأله ملكا لا ينبغي لأحد من بعده، ليقصم به الجبابرة والكفرة، والذين يخالفون ربهم ويدعون لأنفسهم قدرة من الجن والإنس، فوقع السؤال من سليمان عليه السلام على اختيار الله له، لا على اختياره لنفسه.

<span data-type="title" id=toc-395>[سورة ص (٣٨) : آية ٤٦]</span>
إِنَّا أَخْلَصْناهُمْ بِخالِصَةٍ ذِكْرَى الدَّارِ (٤٦)
قوله: إِنَّا أَخْلَصْناهُمْ بِخالِصَةٍ ذِكْرَى الدَّارِ [٤٦] قال: أخلص إبراهيم وإسماعيل وإسحاق عن ذكر الدنيا بذكره خالصة، لا لمال جزاء، ولا شاهدوا فيه أنفسهم، بل ذكروه به له، وليس من ذكر الله بالله كمن ذكر الله بذكر الله، والله سبحانه وتعالى أعلم.
_________
(١) قس بن ساعدة بن عمرو بن عدي الإيادي ( ... - نحو ٢٣ ق. هـ) : أحد حكماء العرب، ومن كبار خطبائهم في الجاهلية. كان أسقف نجران. أدركه النبي صلى الله عليه وسلّم قبل النبوة. (الأعلام ٥/ ١٩٦) .
(٢) تقدم الحديث في تفسير سورة البقرة، وهو من قول الحارث بن أسد في الحلية ١٠/ ٨٨، وسيعاد في تفسير الآية الرابعة من سورة الشمس.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (130,'<span data-type="title" id=toc-396>السورة التي يذكر فيها الزمر</span>

<span data-type="title" id=toc-397>[سورة الزمر (٣٩) : آية ٧]</span>
إِنْ تَكْفُرُوا فَإِنَّ اللَّهَ غَنِيٌّ عَنْكُمْ وَلا يَرْضى لِعِبادِهِ الْكُفْرَ وَإِنْ تَشْكُرُوا يَرْضَهُ لَكُمْ وَلا تَزِرُ وازِرَةٌ وِزْرَ أُخْرى ثُمَّ إِلى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ (٧)
قوله تعالى: وَإِنْ تَشْكُرُوا يَرْضَهُ لَكُمْ [٧] قال: أول الشكر الطاعة، وآخره رؤية الجنة.

<span data-type="title" id=toc-398>[سورة الزمر (٣٩) : آية ٩]</span>
أَمَّنْ هُوَ قانِتٌ آناءَ اللَّيْلِ ساجِداً وَقائِماً يَحْذَرُ الْآخِرَةَ وَيَرْجُوا رَحْمَةَ رَبِّهِ قُلْ هَلْ يَسْتَوِي الَّذِينَ يَعْلَمُونَ وَالَّذِينَ لاَ يَعْلَمُونَ إِنَّما يَتَذَكَّرُ أُولُوا الْأَلْبابِ (٩)
قوله: قُلْ هَلْ يَسْتَوِي الَّذِينَ يَعْلَمُونَ وَالَّذِينَ لاَ يَعْلَمُونَ [٩] قال: العلم الكتاب والاقتداء، لا الخواطر المذمومة، وكل علم لا يطلبه العبد من موضع الاقتداء صار وبالاً عليه لأنه يدّعي به.

<span data-type="title" id=toc-399>[سورة الزمر (٣٩) : آية ١١]</span>
قُلْ إِنِّي أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ مُخْلِصاً لَهُ الدِّينَ (١١)
قوله: قُلْ إِنِّي أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ مُخْلِصاً لَهُ الدِّينَ [١١] قال: الإخلاص الإجابة، فمن لم يكن له الإجابة فلا إخلاص له. وقال: نظر الأكياس في الإخلاص فلم يجدوا شيئاً غير هذا، وهو أن تكون حركاته وسكناته في سره وعلانيته لله عزَّ وجلَّ وحده، لا يمازجه هوى ولا نفس.

<span data-type="title" id=toc-400>[سورة الزمر (٣٩) : آية ١٧]</span>
وَالَّذِينَ اجْتَنَبُوا الطَّاغُوتَ أَنْ يَعْبُدُوها وَأَنابُوا إِلَى اللَّهِ لَهُمُ الْبُشْرى فَبَشِّرْ عِبادِ (١٧)
قوله: وَالَّذِينَ اجْتَنَبُوا الطَّاغُوتَ [١٧] قال: الطاغوت الدنيا، وأصلها الجهل، وفرعها المآكل والمشارب، وزينتها التفاخر، وثمرتها المعاصي، وميزانها القسوة والعقوبة.

<span data-type="title" id=toc-401>[سورة الزمر (٣٩) : آية ٣٨]</span>
وَلَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّماواتِ وَالْأَرْضَ لَيَقُولُنَّ اللَّهُ قُلْ أَفَرَأَيْتُمْ ما تَدْعُونَ مِنْ دُونِ اللَّهِ إِنْ أَرادَنِيَ اللَّهُ بِضُرٍّ هَلْ هُنَّ كاشِفاتُ ضُرِّهِ أَوْ أَرادَنِي بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكاتُ رَحْمَتِهِ قُلْ حَسْبِيَ اللَّهُ عَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُونَ (٣٨)
قوله: إِنْ أَرادَنِيَ اللَّهُ بِضُرٍّ هَلْ هُنَّ كاشِفاتُ ضُرِّهِ [٣٨] قال: يعني إن نزع الله عني العصمة عن المخالفات أو المعرفة على الموفقات، هل يقدر أحد أن يوصلها إلي، أَوْ أَرادَنِي بِرَحْمَةٍ [٣٨] أي بالصبر على ما نهى عنه، والمعونة على ما أمر به، والاتكال عليه في الخاتمة.
وقال: الرحمة العافية في الدين والدنيا والآخرة، وهو التولي من البداية إلى النهاية.

<span data-type="title" id=toc-402>[سورة الزمر (٣٩) : الآيات ٤١ الى ٤٣]</span>
إِنَّا أَنْزَلْنا عَلَيْكَ الْكِتابَ لِلنَّاسِ بِالْحَقِّ فَمَنِ اهْتَدى فَلِنَفْسِهِ وَمَنْ ضَلَّ فَإِنَّما يَضِلُّ عَلَيْها وَما أَنْتَ عَلَيْهِمْ بِوَكِيلٍ (٤١) اللَّهُ يَتَوَفَّى الْأَنْفُسَ حِينَ مَوْتِها وَالَّتِي لَمْ تَمُتْ فِي مَنامِها فَيُمْسِكُ الَّتِي قَضى عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْأُخْرى إِلى أَجَلٍ مُسَمًّى إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَتَفَكَّرُونَ (٤٢) أَمِ اتَّخَذُوا مِنْ دُونِ اللَّهِ شُفَعاءَ قُلْ أَوَلَوْ كانُوا لا يَمْلِكُونَ شَيْئاً وَلا يَعْقِلُونَ (٤٣)
قوله: إِنَّا أَنْزَلْنا عَلَيْكَ الْكِتابَ لِلنَّاسِ بِالْحَقِّ [٤١] يعني أنزله لهم ليهتدوا بالحق إلى الحق، ويستضيؤوا بأنواره.
قوله: اللَّهُ يَتَوَفَّى الْأَنْفُسَ حِينَ مَوْتِها وَالَّتِي لَمْ تَمُتْ فِي مَنامِها [٤٢] قال: إذا توفى الله الأنفس أخرج الروح النوري من لطيف نفس الطبع الكثيف. والتوفي في كتاب الله على ثلاثة أوجه: أحدها الموت، والآخر النوم، والثالث الرفع. فالموت ما ذكرنا، والنوم قوله: وَالَّتِي لَمْ تَمُتْ فِي مَنامِها [٤٢] يعني يتوفى التي لم تمت في منامها، وقال: وَهُوَ الَّذِي يَتَوَفَّاكُمْ بِاللَّيْلِ [الأنعام: ٦٠] يعني النوم، والرفع بعيسى عليه السلام: إِنِّي مُتَوَفِّيكَ وَرافِعُكَ إِلَيَّ [آل عمران: ٥٥]',NULL,'133',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (131,'فإنه إذا مات فينزع عنه لطيف نفس الروح النوري من لطيف نفس الطبع الكثيف الذي به يعقل الأشياء ويرى الرؤيا في الملكوت، وإذا نام نزع عنه لطيف نفس الطبع الكثيف لا لطيف نفس الروح النوري، فيستفيق النائم نفساً لطيفاً، وهو من لطيف نفس الروح الذي إذا زايله لم تكن له حركة، وكان ميتاً. ولنفس طبع الكثيف لطيفة، ولنفس الروح لطيفة، فحياة لطيف نفس الطبع بنور لطيف نفس الروح، وحياة روح لطيف نفس الروح بالذكر، كما قال: أَحْياءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَ [آل عمران: ١٦٩] أي يرزقون الذكر بما نالوا من لطيف نفس النوري، وحياة الطبع الكثيف بالأكل والشرب والتمتع، فمن لم يحسن الإصلاح بين هذين الضدين، أعني نفس الطبع ونفس الروح حتى يكون عيشهما جميعاً بالذكر والسعي بالذكر، فليس بعارف في الحقيقة. وقال عمر بن واصل: وكان المبرد «١» النحوي يقول: الروح والنفس شيئان متصلان لا يقوم أحدهما بدون الآخر. قال: فذكرت ذلك لسهل، فقال: أخطأ، إن الروح يقوم بلطفه في ذاته بغير نفس الطبع الكثيف، ألا ترى أن الله تعالى خاطب الكل من الذر بنفس روح وفهم عقل وفطنة قلب وعلم لطيف بلا حضور طبع كثيف.
قوله: أَمِ اتَّخَذُوا مِنْ دُونِ اللَّهِ شُفَعاءَ [٤٣] قال: أم اتخذوا طريق البدعة في الدين قربة في الدين إلى الله، على أن ينفعهم ذلك.

<span data-type="title" id=toc-403>[سورة الزمر (٣٩) : آية ٤٥]</span>
وَإِذا ذُكِرَ اللَّهُ وَحْدَهُ اشْمَأَزَّتْ قُلُوبُ الَّذِينَ لاَ يُؤْمِنُونَ بِالْآخِرَةِ وَإِذا ذُكِرَ الَّذِينَ مِنْ دُونِهِ إِذا هُمْ يَسْتَبْشِرُونَ (٤٥)
قوله: وَإِذا ذُكِرَ اللَّهُ وَحْدَهُ اشْمَأَزَّتْ قُلُوبُ الَّذِينَ لاَ يُؤْمِنُونَ بِالْآخِرَةِ [٤٥] جحدت قلوبهم مواهب الله عندها.

<span data-type="title" id=toc-404>[سورة الزمر (٣٩) : الآيات ٥٣ الى ٥٤]</span>
قُلْ يا عِبادِيَ الَّذِينَ أَسْرَفُوا عَلى أَنْفُسِهِمْ لاَ تَقْنَطُوا مِنْ رَحْمَةِ اللَّهِ إِنَّ اللَّهَ يَغْفِرُ الذُّنُوبَ جَمِيعاً إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ (٥٣) وَأَنِيبُوا إِلى رَبِّكُمْ وَأَسْلِمُوا لَهُ مِنْ قَبْلِ أَنْ يَأْتِيَكُمُ الْعَذابُ ثُمَّ لا تُنْصَرُونَ (٥٤)
قوله: قُلْ يا عِبادِيَ الَّذِينَ أَسْرَفُوا عَلى أَنْفُسِهِمْ لاَ تَقْنَطُوا مِنْ رَحْمَةِ اللَّهِ [٥٣] قال:
أمهل الله تعالى عباده تفضلاً منه إلى آخر نفس، فقال لهم: «لا تقنطوا من رحمتي فلو رجعتم إلي في آخر نفسٍ قبلتكم» . قال: وهذه أبلغ آية في الإشفاق من الله تعالى إلى عباده، لعلمه بأنه ما حرمهم ما تفضل به على غيرهم، فرحمهم حتى أدخلهم في عين الكرم بالذكر القديم لهم.
وقد حكي عن جبريل عليه الصلاة والسلام أنه سمع إبراهيم عليه الصلاة والسلام يقول: يا كريم العفو. فقال له جبريل عليه السلام: يا إبراهيم، أتدري ما كرم عفوه؟ قال: لا يا جبريل.
قال: إذا عفا عن سيئة جعلها حسنة «٢» . ثم قال سهل: اشهدوا علي أني من ديني أن لا أتبرأ من فساق أمة محمد صلّى الله عليه وسلّم وفجارهم وقاتلهم وزانيهم وسارقهم، فإن الله تعالى لا يُدْرَك غاية كرمه وفضله وإحسانه بأمة محمد صلّى الله عليه وسلّم خاصة.
_________
(١) المبرد: محمد بن يزيد بن عبد الأكبر الأزدي (٢١٠- ٢٨٦ هـ) : إمام العربية ببغداد في زمانه، وأحد أئمة الأدب والأخبار. (الأعلام ٧/ ١٤٤) .
(٢) شعب الإيمان ٥/ ٣٨٩ (رقم ٧٠٤٣) وقوت القلوب ١/ ٣٣٤، ٣٧٦.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (132,'قوله: وَأَنِيبُوا إِلى رَبِّكُمْ وَأَسْلِمُوا لَهُ [٥٤] يعني: ارجعوا له بالدعاء والتضرع والمسألة وَأَسْلِمُوا لَهُ [٥٤] يعني: فوضوا الأمور كلها إليه.

<span data-type="title" id=toc-405>[سورة الزمر (٣٩) : آية ٥٦]</span>
أَنْ تَقُولَ نَفْسٌ يا حَسْرَتى عَلى مَا فَرَّطْتُ فِي جَنْبِ اللَّهِ وَإِنْ كُنْتُ لَمِنَ السَّاخِرِينَ (٥٦)
قوله: أَنْ تَقُولَ نَفْسٌ يا حَسْرَتى عَلى مَا فَرَّطْتُ فِي جَنْبِ اللَّهِ [٥٦] قال: يعني اشتغلت بعاجل الدنيا ولذة الهوى ومتابعة النفس، وضيعت في جنب الله، يعني في ذات الله القصد إليه، والاعتماد عليه بترك مراعاة حقوقه وملازمة خدمته.

<span data-type="title" id=toc-406>[سورة الزمر (٣٩) : آية ٦٣]</span>
لَهُ مَقالِيدُ السَّماواتِ وَالْأَرْضِ وَالَّذِينَ كَفَرُوا بِآياتِ اللَّهِ أُولئِكَ هُمُ الْخاسِرُونَ (٦٣)
قوله تعالى: لَهُ مَقالِيدُ السَّماواتِ وَالْأَرْضِ [٦٣] بيده مفاتيح القلوب، يوفق من يشاء لطاعته وخدمته بالإخلاص، ويصرف من يشاء عن بابه.

<span data-type="title" id=toc-407>[سورة الزمر (٣٩) : الآيات ٦٧ الى ٦٩]</span>
وَما قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيامَةِ وَالسَّماواتُ مَطْوِيَّاتٌ بِيَمِينِهِ سُبْحانَهُ وَتَعالى عَمَّا يُشْرِكُونَ (٦٧) وَنُفِخَ فِي الصُّورِ فَصَعِقَ مَنْ فِي السَّماواتِ وَمَنْ فِي الْأَرْضِ إِلاَّ مَنْ شاءَ اللَّهُ ثُمَّ نُفِخَ فِيهِ أُخْرى فَإِذا هُمْ قِيامٌ يَنْظُرُونَ (٦٨) وَأَشْرَقَتِ الْأَرْضُ بِنُورِ رَبِّها وَوُضِعَ الْكِتابُ وَجِيءَ بِالنَّبِيِّينَ وَالشُّهَداءِ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَهُمْ لا يُظْلَمُونَ (٦٩)
قوله: وَما قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ [٦٧] أي ما عرفوه حق معرفته في الأصل والفرع.
قوله: فَصَعِقَ مَنْ فِي السَّماواتِ وَمَنْ فِي الْأَرْضِ [٦٨] قال: باطن الآية أن الملائكة إنَّما يؤمرون بالإمساك عن الذكر لا بالنفخة ولا بنزع عزرائيل، لأن الله أحياهم بذكره، كما أحيا بني آدم بأنفاسهم، قال الله تعالى: يُسَبِّحُونَ اللَّيْلَ وَالنَّهارَ لاَ يَفْتُرُونَ [الأنبياء: ٢٠] فإذا أمسك الذكر عنهم ماتوا.
قوله: وَأَشْرَقَتِ الْأَرْضُ بِنُورِ رَبِّها [٦٩] قال: قلوب المؤمنين يوم القيامة تشرق بتوحيد سيدهم، والاقتداء بسنة نبيهم صلّى الله عليه وسلّم.

<span data-type="title" id=toc-408>[سورة الزمر (٣٩) : آية ٧٤]</span>
وَقالُوا الْحَمْدُ لِلَّهِ الَّذِي صَدَقَنا وَعْدَهُ وَأَوْرَثَنَا الْأَرْضَ نَتَبَوَّأُ مِنَ الْجَنَّةِ حَيْثُ نَشاءُ فَنِعْمَ أَجْرُ الْعامِلِينَ (٧٤)
قوله: الْحَمْدُ لِلَّهِ الَّذِي صَدَقَنا وَعْدَهُ [٧٤] قال: إن الحمد منهم في الجنة ليس على جهة التعبد، إذ التعبد قد رفع عنهم، كما رفع خوف الكسب والقطع، وبقي خوف الإجلال والتعظيم لله عزَّ وجلَّ، وإنما الحمد منهم لذة لنفس الطبع ونفس الروح والعقل، والله سبحانه وتعالى أعلم.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (133,'<span data-type="title" id=toc-409>السورة التي يذكر فيها غافر</span>

<span data-type="title" id=toc-410>[سورة غافر (٤٠) : الآيات ١ الى ٥]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
حم (١) تَنْزِيلُ الْكِتابِ مِنَ اللَّهِ الْعَزِيزِ الْعَلِيمِ (٢) غافِرِ الذَّنْبِ وَقابِلِ التَّوْبِ شَدِيدِ الْعِقابِ ذِي الطَّوْلِ لا إِلهَ إِلاَّ هُوَ إِلَيْهِ الْمَصِيرُ (٣) مَا يُجادِلُ فِي آياتِ اللَّهِ إِلاَّ الَّذِينَ كَفَرُوا فَلا يَغْرُرْكَ تَقَلُّبُهُمْ فِي الْبِلادِ (٤)
كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَالْأَحْزابُ مِنْ بَعْدِهِمْ وَهَمَّتْ كُلُّ أُمَّةٍ بِرَسُولِهِمْ لِيَأْخُذُوهُ وَجادَلُوا بِالْباطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَأَخَذْتُهُمْ فَكَيْفَ كانَ عِقابِ (٥)
قوله تعالى: حم (١) تَنْزِيلُ الْكِتابِ مِنَ اللَّهِ الْعَزِيزِ الْعَلِيمِ [١- ٢] قال: يعني الحي الملك هو الذي أنزل عليك الكتاب، وهو الذي قلبت به قلوب العارفين العزيز عن درك الخلق العليم بما أنشأ وقدر. غافِرِ الذَّنْبِ [٣] أي ساتر الذنب على من يشاء، وَقابِلِ التَّوْبِ [٣] عمن تاب وأخلص العمل له بالعلم، ذِي الطَّوْلِ [٣] ذي الغنى عن الكل، مَا يُجادِلُ فِي آياتِ اللَّهِ [٤] يعني في الذات والقدرة والقرآن والسنة بهوى النفس، كما قال: وَجادَلُوا بِالْباطِلِ [٥] أي بالهوى من غير هدى من الله، كما قال: فَلِمَ تُحَاجُّونَ فِيما لَيْسَ لَكُمْ بِهِ عِلْمٌ [آل عمران: ٦٦] إلا الذين كفروا وابتدعوا غير الحق.

<span data-type="title" id=toc-411>[سورة غافر (٤٠) : آية ٧]</span>
الَّذِينَ يَحْمِلُونَ الْعَرْشَ وَمَنْ حَوْلَهُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِهِ وَيَسْتَغْفِرُونَ لِلَّذِينَ آمَنُوا رَبَّنا وَسِعْتَ كُلَّ شَيْءٍ رَحْمَةً وَعِلْماً فَاغْفِرْ لِلَّذِينَ تابُوا وَاتَّبَعُوا سَبِيلَكَ وَقِهِمْ عَذابَ الْجَحِيمِ (٧)
قوله: فَاغْفِرْ لِلَّذِينَ تابُوا [٧] قال: هم الذين تابوا من الغفلة، وأنسوا بالذكر، واتبعوا سنة المصطفى صلّى الله عليه وسلّم.

<span data-type="title" id=toc-412>[سورة غافر (٤٠) : آية ١٠]</span>
إِنَّ الَّذِينَ كَفَرُوا يُنادَوْنَ لَمَقْتُ اللَّهِ أَكْبَرُ مِنْ مَقْتِكُمْ أَنْفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى الْإِيمانِ فَتَكْفُرُونَ (١٠)
قوله: إِنَّ الَّذِينَ كَفَرُوا يُنادَوْنَ لَمَقْتُ اللَّهِ أَكْبَرُ مِنْ مَقْتِكُمْ أَنْفُسَكُمْ [١٠] قال: المقت غاية الإبعاد من الله عزَّ وجلَّ، والكفار إذا دخلوا النار مقتوا أنفسهم، ومقت الله عملهم أشد من دخول النار.

<span data-type="title" id=toc-413>[سورة غافر (٤٠) : آية ١٥]</span>
رَفِيعُ الدَّرَجاتِ ذُو الْعَرْشِ يُلْقِي الرُّوحَ مِنْ أَمْرِهِ عَلى مَنْ يَشاءُ مِنْ عِبادِهِ لِيُنْذِرَ يَوْمَ التَّلاقِ (١٥)
قوله تعالى: رَفِيعُ الدَّرَجاتِ ذُو الْعَرْشِ يُلْقِي الرُّوحَ مِنْ أَمْرِهِ [١٥] أي رافع الدرجات يرفع درجات من يشاء بالمعرفة به، يُلْقِي الرُّوحَ مِنْ أَمْرِهِ [١٥] أي ينزل الوحي من السماء إلى الأرض بأمره.

<span data-type="title" id=toc-414>[سورة غافر (٤٠) : آية ٦٠]</span>
وَقالَ رَبُّكُمُ ادْعُونِي أَسْتَجِبْ لَكُمْ إِنَّ الَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبادَتِي سَيَدْخُلُونَ جَهَنَّمَ داخِرِينَ (٦٠)
قوله: وَقالَ رَبُّكُمُ ادْعُونِي أَسْتَجِبْ لَكُمْ [٦٠] قال: الدعاء بالمروة مستجاب لا محالة، وهو الجمع «١» من سهم الرامي، وما من مؤمن دعا الله تعالى إلا استجاب له فيما دعاه بعينه، من غير أن يعلم ذلك العبد، أو صرف عنه بذلك سوءاً، أو كتب له بذلك حسنة. فقيل له:
ما معنى قولهم: «الدعاء أفضل العمل» «٢» ؟ فقال: لأنه تضرع والتجاء وإظهار الفقر والفاقة.

<span data-type="title" id=toc-415>[سورة غافر (٤٠) : آية ٨١]</span>
وَيُرِيكُمْ آياتِهِ فَأَيَّ آياتِ اللَّهِ تُنْكِرُونَ (٨١)
قوله: يُرِيكُمْ آياتِهِ فَأَيَّ آياتِ اللَّهِ تُنْكِرُونَ
[٨١] قال: أظهر الله تعالى آياته لأوليائه وجعل السعيد من عباده من صدقهم على كراماتهم، وأعمى أعين الأشقياء عن ذلك وصرف قلوبهم عنه، ومن أنكر آيات الأولياء فإنما ينكر قدرة الله تعالى، فإن القدرة تظهر على الأولياء الآيات، لا هم أنفسهم يقدرون على إظهارها، كما قال: يُرِيكُمْ آياتِهِ فَأَيَّ آياتِ اللَّهِ تُنْكِرُونَ
[٨١] .

<span data-type="title" id=toc-416>[سورة غافر (٤٠) : آية ٨٥]</span>
فَلَمْ يَكُ يَنْفَعُهُمْ إِيمانُهُمْ لَمَّا رَأَوْا بَأْسَنا سُنَّتَ اللَّهِ الَّتِي قَدْ خَلَتْ فِي عِبادِهِ وَخَسِرَ هُنالِكَ الْكافِرُونَ (٨٥)
قوله: سُنَّتَ اللَّهِ الَّتِي قَدْ خَلَتْ فِي عِبادِهِ [٨٥] قال: السنة مشتقة من أسماء الله تعالى السين سناؤه والنون نوره والهاء هدايته منه إياهم، فهم على سنن الطريق الواضح إليه.
والله سبحانه وتعالى أعلم.
_________
(١) كذا في الأصل.
(٢) فيض القدير ٢/ ٤٤.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (134,'<span data-type="title" id=toc-417>السورة التي يذكر فيها السجدة (فصلت)</span>

<span data-type="title" id=toc-418>[سورة فصلت (٤١) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
حم (١)
قوله تعالى: حم [١] يعني قضى في اللوح المحفوظ، وكتب فيه ما هو كائن.

<span data-type="title" id=toc-419>[سورة فصلت (٤١) : الآيات ٤ الى ٥]</span>
بَشِيراً وَنَذِيراً فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لا يَسْمَعُونَ (٤) وَقالُوا قُلُوبُنا فِي أَكِنَّةٍ مِمَّا تَدْعُونا إِلَيْهِ وَفِي آذانِنا وَقْرٌ وَمِنْ بَيْنِنا وَبَيْنِكَ حِجابٌ فَاعْمَلْ إِنَّنا عامِلُونَ (٥)
قوله: بَشِيراً وَنَذِيراً [٤] قال: بشيراً بالجنة لمن أطاعه واتبع ما فيه، ونذيراً بالنار لمن عصاه وأعرض عن مراد الله فيه وخالفه.
قوله تعالى: وَقالُوا قُلُوبُنا فِي أَكِنَّةٍ مِمَّا تَدْعُونا إِلَيْهِ [٥] قال: أي في أغطية الإهمال فمالت إلى الشهوة والهوى، فلا تعقل دعوة الحق، وَفِي آذانِنا [٥] التي في القلوب وَقْرٌ [٥] أي ثقل من الصمم عن الخير، فلا تسمع هواتف الحق، وَمِنْ بَيْنِنا وَبَيْنِكَ حِجابٌ [٥] أي ستر من الهوى وجبلة الطبع، لا نراك كما يراك غيرنا.

<span data-type="title" id=toc-420>[سورة فصلت (٤١) : آية ٢٤]</span>
فَإِنْ يَصْبِرُوا فَالنَّارُ مَثْوىً لَهُمْ وَإِنْ يَسْتَعْتِبُوا فَما هُمْ مِنَ الْمُعْتَبِينَ (٢٤)
قوله: وَإِنْ يَسْتَعْتِبُوا فَما هُمْ مِنَ الْمُعْتَبِينَ [٢٤] يعني إن يستقيلوا لا يقالوا، وإن اعتذروا لا يعذروا.

<span data-type="title" id=toc-421>[سورة فصلت (٤١) : آية ٣٠]</span>
إِنَّ الَّذِينَ قالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقامُوا تَتَنَزَّلُ عَلَيْهِمُ الْمَلائِكَةُ أَلاَّ تَخافُوا وَلا تَحْزَنُوا وَأَبْشِرُوا بِالْجَنَّةِ الَّتِي كُنْتُمْ تُوعَدُونَ (٣٠)
قوله: إِنَّ الَّذِينَ قالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقامُوا [٣٠] قال: أي لم يشركوا بعده، كذا روي عن النبي صلى الله عليه وسلّم أنه قال: «هم أمتي ورب الكعبة استقاموا ولم يشركوا كما فعلت اليهود والنصارى» «١» ، قال عمر رضي الله عنه: لم يروغوا روغان الثعالب «٢» .
قوله: تَتَنَزَّلُ عَلَيْهِمُ الْمَلائِكَةُ أَلَّا تَخافُوا وَلا تَحْزَنُوا [٣٠] يعني عند الموت. وقد قال النبي صلى الله عليه وسلّم: «يقول الله تعالى: ما ترددت في شيء كترددي في قبض روح المؤمن» «٣» ، أي ما رددت الملائكة إلى شيء كردهم إلي عبدي المؤمن في قبض روحه بالبشارة وبالكرامة، أن لا تخافوا على أنفسكم ولا تحزنوا يوم الجمع، كما قال: لاَ يَحْزُنُهُمُ الْفَزَعُ الْأَكْبَرُ [الأنبياء: ١٠٣] قال: المتولي لجملتكم بالرضا، الحافظ قلوبكم، المقر أعينكم بالتجلي، جزاء لتوحيدكم، وتفضلاً من ربكم.

<span data-type="title" id=toc-422>[سورة فصلت (٤١) : آية ٣٣]</span>
وَمَنْ أَحْسَنُ قَوْلاً مِمَّنْ دَعا إِلَى اللَّهِ وَعَمِلَ صالِحاً وَقالَ إِنَّنِي مِنَ الْمُسْلِمِينَ (٣٣)
وقوله: وَمَنْ أَحْسَنُ قَوْلًا مِمَّنْ دَعا إِلَى اللَّهِ [٣٣] أي ممن دل على الله وعلى عبادته وسنة رسوله صلّى الله عليه وسلّم، واجتناب المناهي، وإدامة الاستقامة مع الله، والاستقامة به خوفاً من الخاتمة، وفي الطريقة الوسطى، والجادة المستقيمة التي من سلكها سلم، ومن تعداها ندم.

<span data-type="title" id=toc-423>[سورة فصلت (٤١) : آية ٤٩]</span>
لاَّ يَسْأَمُ الْإِنْسانُ مِنْ دُعاءِ الْخَيْرِ وَإِنْ مَسَّهُ الشَّرُّ فَيَؤُسٌ قَنُوطٌ (٤٩)
قوله: لاَّ يَسْأَمُ الْإِنْسانُ مِنْ دُعاءِ الْخَيْرِ [٤٩] قال: لا يمل من ذكر ربه وشكره وحمده والثناء عليه.

<span data-type="title" id=toc-424>[سورة فصلت (٤١) : آية ٥١]</span>
وَإِذا أَنْعَمْنا عَلَى الْإِنْسانِ أَعْرَضَ وَنَأى بِجانِبِهِ وَإِذا مَسَّهُ الشَّرُّ فَذُو دُعاءٍ عَرِيضٍ (٥١)
قوله: وَإِذا أَنْعَمْنا عَلَى الْإِنْسانِ أَعْرَضَ وَنَأى بِجانِبِهِ [٥١] قال: يعني عن الدعاء والشكر على ما أنعم به عليه، واشتغل بالنعمة، وافتخر بغير مفتخر به.

<span data-type="title" id=toc-425>[سورة فصلت (٤١) : آية ٥٣]</span>
سَنُرِيهِمْ آياتِنا فِي الْآفاقِ وَفِي أَنْفُسِهِمْ حَتَّى يَتَبَيَّنَ لَهُمْ أَنَّهُ الْحَقُّ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُ عَلى كُلِّ شَيْءٍ شَهِيدٌ (٥٣)
قوله: سَنُرِيهِمْ آياتِنا فِي الْآفاقِ [٥٣] يعني الموت، قال: والموت خاص وعام، فالعام موت الخلقة والجبلة، والخاص موت شهوات النفس، والله سبحانه وتعالى أعلم.
_________
(١) تفسير القرطبي ١٥/ ٣٥٨.
(٢) الزهد لابن مبارك ص ١١٠، وفيه: (أخرجه أحمد في الزهد) .
(٣) صحيح البخاري: الرقاق، رقم ٦١٣٧ ونوادر الأصول ٢/ ٢٣٢ وفيض القدير ٥/ ٥٠١.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (135,'<span data-type="title" id=toc-426>السورة التي يذكر فيها الشورى</span>

<span data-type="title" id=toc-427>[سورة الشورى (٤٢) : الآيات ٧ الى ٩]</span>
وَكَذلِكَ أَوْحَيْنا إِلَيْكَ قُرْآناً عَرَبِيًّا لِتُنْذِرَ أُمَّ الْقُرى وَمَنْ حَوْلَها وَتُنْذِرَ يَوْمَ الْجَمْعِ لا رَيْبَ فِيهِ فَرِيقٌ فِي الْجَنَّةِ وَفَرِيقٌ فِي السَّعِيرِ (٧) وَلَوْ شاءَ اللَّهُ لَجَعَلَهُمْ أُمَّةً واحِدَةً وَلكِنْ يُدْخِلُ مَنْ يَشاءُ فِي رَحْمَتِهِ وَالظَّالِمُونَ مَا لَهُمْ مِنْ وَلِيٍّ وَلا نَصِيرٍ (٨) أَمِ اتَّخَذُوا مِنْ دُونِهِ أَوْلِياءَ فَاللَّهُ هُوَ الْوَلِيُّ وَهُوَ يُحْيِ الْمَوْتى وَهُوَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (٩)
قوله: لِتُنْذِرَ أُمَّ الْقُرى وَمَنْ حَوْلَها [٧] قال: ظاهرها مكة، وباطنها القلب، ومِنْ حوله الجوارح. فأنذرهم لكي يحفظوا قلوبهم وجوارحهم عن لذة المعاصي واتباع الشهوات.
قوله: وَتُنْذِرَ يَوْمَ الْجَمْعِ [٧] ، قال: أي يوم جمع أهل الأرض على ذكره، كجمع أهل السماوات.
قوله: فَرِيقٌ فِي الْجَنَّةِ وَفَرِيقٌ فِي السَّعِيرِ [٧] قال: من غرس الشوك لا يجتني عنباً، فاصنعوا ما شئتم، فإن الطريق اثنان، فأي طريق منهما سلكتموه وردتم على أهله.
قوله: وَلَوْ شاءَ اللَّهُ لَجَعَلَهُمْ أُمَّةً واحِدَةً [٨] قال: ظاهرها الكفر وباطنها حركات العبد وسكونه ولو شاء الله لجعلها كلها في طاعته وَلكِنْ يُدْخِلُ مَنْ يَشاءُ فِي رَحْمَتِهِ [٨] أي في طاعته وَالظَّالِمُونَ [٨] الذين يدعون الحول والقوة مَا لَهُمْ مِنْ وَلِيٍّ وَلا نَصِيرٍ [٨] على خلاف، وهو السكون في الأمر، والحركة في النهي.
قوله: وَهُوَ يُحْيِ الْمَوْتى [٩] باطنها قلوب كل أهل الحق يحييها بذكره ومشاهدته، قال: ولا تحيا النفوس حتى تموت.

<span data-type="title" id=toc-428>[سورة الشورى (٤٢) : آية ١٣]</span>
شَرَعَ لَكُمْ مِنَ الدِّينِ مَا وَصَّى بِهِ نُوحاً وَالَّذِي أَوْحَيْنا إِلَيْكَ وَما وَصَّيْنا بِهِ إِبْراهِيمَ وَمُوسى وَعِيسى أَنْ أَقِيمُوا الدِّينَ وَلا تَتَفَرَّقُوا فِيهِ كَبُرَ عَلَى الْمُشْرِكِينَ ما تَدْعُوهُمْ إِلَيْهِ اللَّهُ يَجْتَبِي إِلَيْهِ مَنْ يَشاءُ وَيَهْدِي إِلَيْهِ مَنْ يُنِيبُ (١٣)
قوله: شَرَعَ لَكُمْ مِنَ الدِّينِ مَا وَصَّى بِهِ نُوحاً [١٣] فأول من حرم البنات والأمهات والأخوات نوح عليه السلام، فشرع الله لنا محاسن شرائع الأنبياء.
قوله: وَالَّذِي أَوْحَيْنا إِلَيْكَ وَما وَصَّيْنا بِهِ إِبْراهِيمَ وَمُوسى وَعِيسى [١٣] من إقامة الطاعة لله وإقامة الإخلاص فيها، وإظهار الأخلاق والأحوال.
قوله: مَنْ كانَ يُرِيدُ حَرْثَ الْآخِرَةِ نَزِدْ لَهُ فِي حَرْثِهِ [٢٠] قال: حرث الآخرة القناعة في الدنيا، والرضا في الآخرة، وحرث الدنيا ما أريد به غيره. قال: ووجه آخر، يعني من عمل لله تعالى إيجاباً لا طلباً للجزاء صغر عنده كل مطلوب دون الحق عزَّ وجلَّ، فلا يطلب الدنيا ولا الجنة، وإنما يطلب النظر إليه، وهو حظ ذهن نفس الروح، وفهم العقل، وفطنة القلب كما خاطبهم، والاقتداء من غير أن كانت النفس الطبيعية حاضرة هناك، غير أن للنفس منها',NULL,'138',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (136,'حظاً لامتزاجها بتلك الأنوار، مثل النسيم الطيب. ومن عمل لأجل الدنيا نُؤْتِهِ مِنْها وَما لَهُ فِي الْآخِرَةِ مِنْ نَصِيبٍ [٢٠] ، فتشتغل نفسه بتنعم الدنيا التي هي حظها من أجل النصيب في الآخرة، وهو رؤية الحق على الأبد.

<span data-type="title" id=toc-429>[سورة الشورى (٤٢) : آية ٢٣]</span>
ذلِكَ الَّذِي يُبَشِّرُ اللَّهُ عِبادَهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ قُلْ لا أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِلاَّ الْمَوَدَّةَ فِي الْقُرْبى وَمَنْ يَقْتَرِفْ حَسَنَةً نَزِدْ لَهُ فِيها حُسْناً إِنَّ اللَّهَ غَفُورٌ شَكُورٌ (٢٣)
قوله تعالى: قُلْ لاَّ أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِلَّا الْمَوَدَّةَ فِي الْقُرْبى [٢٣] قال: باطنها صلة السنة بالفرض. وحكي عن الحسن في هذه الآية قال: من تقرب إلى الله بطاعته وجبت له محبته.
قوله: وَمَنْ يَقْتَرِفْ حَسَنَةً نَزِدْ لَهُ فِيها حُسْناً [٢٣] قال: يعني معرفة حاله في عمله، وقبل دخوله فيه، وبعد فراغه منه أنه سقيم أو صحيح.

<span data-type="title" id=toc-430>[سورة محمد (٤٧) : آية ٢٤]</span>
أَفَلا يَتَدَبَّرُونَ الْقُرْآنَ أَمْ عَلى قُلُوبٍ أَقْفالُها (٢٤)
قوله تعالى: فَإِنْ يَشَإِ اللَّهُ يَخْتِمْ عَلى قَلْبِكَ [٢٤] قال: يختم على قلبك الشوق والمحبة، فلا تلتفت إلى الخلق، ولا تشتغل في حبهم وإتيانهم.

<span data-type="title" id=toc-431>[سورة الشورى (٤٢) : آية ٥٢]</span>
وَكَذلِكَ أَوْحَيْنا إِلَيْكَ رُوحاً مِنْ أَمْرِنا ما كُنْتَ تَدْرِي مَا الْكِتابُ وَلا الْإِيمانُ وَلكِنْ جَعَلْناهُ نُوراً نَهْدِي بِهِ مَنْ نَشاءُ مِنْ عِبادِنا وَإِنَّكَ لَتَهْدِي إِلى صِراطٍ مُسْتَقِيمٍ (٥٢)
قوله: وَإِنَّكَ لَتَهْدِي إِلى صِراطٍ مُسْتَقِيمٍ [٥٢] أي تدعو إلى ربك بنور هدايته.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (137,'<span data-type="title" id=toc-432>السورة التي يذكر فيها الزخرف</span>

<span data-type="title" id=toc-433>[سورة الزخرف (٤٣) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
حم (١) وَالْكِتابِ الْمُبِينِ (٢)
قوله تعالى: حم (١) وَالْكِتابِ الْمُبِينِ [١- ٢] أي بين فيه الهدى من الضلالة والخير من الشر وبين فيه سعادة السعداء وشقاوة الأشقياء

<span data-type="title" id=toc-434>[سورة الزخرف (٤٣) : آية ٤]</span>
وَإِنَّهُ فِي أُمِّ الْكِتابِ لَدَيْنا لَعَلِيٌّ حَكِيمٌ (٤)
وَإِنَّهُ فِي أُمِّ الْكِتابِ [٤] قال: هو اللوح المحفوظ. لَدَيْنا لَعَلِيٌّ حَكِيمٌ [٤] قال: أي رفيع مستول على سائر الكتب.

<span data-type="title" id=toc-435>[سورة الزخرف (٤٣) : آية ١٣]</span>
لِتَسْتَوُوا عَلى ظُهُورِهِ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ إِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا سُبْحانَ الَّذِي سَخَّرَ لَنا هذا وَما كُنَّا لَهُ مُقْرِنِينَ (١٣)
قوله تعالى: لِتَسْتَوُوا عَلى ظُهُورِهِ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ [١٣] قال «١» : إن الله خص الأنبياء عليهم الصلاة والسلام وبعض الصديقين بمعرفة نعم الله تعالى عليهم قبل زوالها وحلم الله عنهم، ومن لم يعرف نعم الله عليه إلا في مطعمه ومشربه ومركبه فقد صغرت عنده نعم الله.

<span data-type="title" id=toc-436>[سورة الزخرف (٤٣) : آية ١٥]</span>
وَجَعَلُوا لَهُ مِنْ عِبادِهِ جُزْءاً إِنَّ الْإِنْسانَ لَكَفُورٌ مُبِينٌ (١٥)
قوله تعالى: وَجَعَلُوا لَهُ مِنْ عِبادِهِ جُزْءاً [١٥] أي في عبادته جزءاً ألا ترى أن النبي صلى الله عليه وسلّم يقول: «إن أحدكم يصلي وليس له من صلاته إلا ثلثها أو ربعها» «٢» .

<span data-type="title" id=toc-437>[سورة الزخرف (٤٣) : آية ٣٢]</span>
أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ نَحْنُ قَسَمْنا بَيْنَهُمْ مَعِيشَتَهُمْ فِي الْحَياةِ الدُّنْيا وَرَفَعْنا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجاتٍ لِيَتَّخِذَ بَعْضُهُمْ بَعْضاً سُخْرِيًّا وَرَحْمَتُ رَبِّكَ خَيْرٌ مِمَّا يَجْمَعُونَ (٣٢)
قوله: وَرَفَعْنا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجاتٍ [٣٢] قال: رفعنا بعضهم على بعض في المعرفة والطاعة عيشاً لهم في الدنيا والآخرة.
قوله: وَرَحْمَتُ رَبِّكَ خَيْرٌ [٣٢] أي من كثرة الأعمال لطلب الجزاء.

<span data-type="title" id=toc-438>[سورة الزخرف (٤٣) : آية ٣٦]</span>
وَمَنْ يَعْشُ عَنْ ذِكْرِ الرَّحْمنِ نُقَيِّضْ لَهُ شَيْطاناً فَهُوَ لَهُ قَرِينٌ (٣٦)
قوله تعالى: وَمَنْ يَعْشُ عَنْ ذِكْرِ الرَّحْمنِ نُقَيِّضْ لَهُ شَيْطاناً [٣٦] قال: قد حكم الله أنه لا يعرض عبد عن ذكره، وهو أن يرى بقلبه شيئاً سواه ساكناً إياه، إلا سلط الله عليه شيطاناً ليضله عن طريق الحق ويغريه.

<span data-type="title" id=toc-439>[سورة الزخرف (٤٣) : آية ٥٥]</span>
فَلَمَّا آسَفُونا انْتَقَمْنا مِنْهُمْ فَأَغْرَقْناهُمْ أَجْمَعِينَ (٥٥)
قوله تعالى: فَلَمَّا آسَفُونا انْتَقَمْنا مِنْهُمْ [٥٥] قال: أي فلما غايظونا بالإقامة على المخالفة في الأوامر، وإظهار البدع في الدين، وترك السنن اتباعاً لوجود الأهواء، نزعنا نور المعرفة من قلوبهم، وسراج التوحيد من أسرارهم، ووكلناهم إلى أنفسهم وما اختاروه، فضلُّوا وأضلّوا. ثم قال: الاتباع الاتباع، الاقتداء الاقتداء، فإنه سبيل السلف، وما ضل من اتبع، وما نجا من ابتدع.

<span data-type="title" id=toc-440>[سورة الزخرف (٤٣) : الآيات ٦٩ الى ٧٢]</span>
الَّذِينَ آمَنُوا بِآياتِنا وَكانُوا مُسْلِمِينَ (٦٩) ادْخُلُوا الْجَنَّةَ أَنْتُمْ وَأَزْواجُكُمْ تُحْبَرُونَ (٧٠) يُطافُ عَلَيْهِمْ بِصِحافٍ مِنْ ذَهَبٍ وَأَكْوابٍ وَفِيها مَا تَشْتَهِيهِ الْأَنْفُسُ وَتَلَذُّ الْأَعْيُنُ وَأَنْتُمْ فِيها خالِدُونَ (٧١) وَتِلْكَ الْجَنَّةُ الَّتِي أُورِثْتُمُوها بِما كُنْتُمْ تَعْمَلُونَ (٧٢)
قوله تعالى: الَّذِينَ آمَنُوا بِآياتِنا وَكانُوا مُسْلِمِينَ (٦٩) ادْخُلُوا الْجَنَّةَ أَنْتُمْ وَأَزْواجُكُمْ تُحْبَرُونَ [٦٩، ٧٠] بلذة النظر جزاء لما منَّ عليهم من التوحيد عند تجلي المكاشفة لأوليائه، وهو البقاء مع الباقي. ألا ترى كيف خصهم في الإيمان بشرط التسليم لأمره والسكون بين يديه.
قوله تعالى: وَفِيها مَا تَشْتَهِيهِ الْأَنْفُسُ وَتَلَذُّ الْأَعْيُنُ [٧١] قال: أي ما تشتهي الأنفس من ثواب الأعمال، وتلذ الأعين بما فضل الله به من التمكين في وقت اللقاء جزاء لتوحيدهم.
قال: الجنة جزاء أعمال الجوارح، واللقاء جزاء التوحيد، ألا ترى أن الله تعالى قال: وَتِلْكَ الْجَنَّةُ الَّتِي أُورِثْتُمُوها بِما كُنْتُمْ تَعْمَلُونَ [٧٢] .
_________
(١) تقدم هذا القول في تفسير الآية (٥٣) من سورة الأنفال. [.....]
(٢) عون المعبود: باب ما جاء في نقصان الصلاة ٣/ ٣ وفيض القدير ٢/ ٣٣٣.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (138,'<span data-type="title" id=toc-441>السورة التي يذكر فيها الدخان</span>

<span data-type="title" id=toc-442>[سورة الدخان (٤٤) : آية ٣]</span>
إِنَّا أَنْزَلْناهُ فِي لَيْلَةٍ مُبارَكَةٍ إِنَّا كُنَّا مُنْذِرِينَ (٣)
قوله تعالى: إِنَّا أَنْزَلْناهُ فِي لَيْلَةٍ مُبارَكَةٍ [٣] قال: أنزل الله ليلة القدر القرآن جملة إلى بيت العزة في سماء الدنيا من اللوح المحفوظ على أيدي الملائكة السفرة، وأنزل على روح محمد صلّى الله عليه وسلّم وهو الروح المبارك، فسماها ليلة القدر مباركة لاتصال البركات بعضها ببعض.

<span data-type="title" id=toc-443>[سورة الدخان (٤٤) : آية ١٠]</span>
فَارْتَقِبْ يَوْمَ تَأْتِي السَّماءُ بِدُخانٍ مُبِينٍ (١٠)
قوله تعالى: يَوْمَ تَأْتِي السَّماءُ بِدُخانٍ مُبِينٍ [١٠] قال: الدخان في الدنيا قسوة القلب والغفلة عن الذكر، ولا عقوبة أعظم في الدنيا من فساد القلب. وقد حكي عن أويس القرني «١» وهرم بن حيان «٢» أنهما التقيا يوماً، فقال هرم لأويس: ادعُ الله. فقال: يصلح لك نيتك وقلبك «٣» فلن تعالج شيئاً أشد منهما، بينما قلبك مقبل إذ هو مدبر، وبينما هو مدبر إذ هو مقبل، ولا تنظر إلى صغير الخطيئة، وانظر إلى عظمة من عصيت، فإنك إن عظمتها فقد عظمت الله تعالى، وإن صغرتها فقد صغرت الله تعالى.
قوله تعالى: لاَ إِلهَ إِلَّا هُوَ يُحْيِي وَيُمِيتُ [٨] قال: لا إله على الحقيقة إلا من يقدر على الإيجاد من العدم، وعلى العدم من الإيجاد.

<span data-type="title" id=toc-444>[سورة الدخان (٤٤) : آية ٢٤]</span>
وَاتْرُكِ الْبَحْرَ رَهْواً إِنَّهُمْ جُنْدٌ مُغْرَقُونَ (٢٤)
قوله: وَاتْرُكِ الْبَحْرَ رَهْواً [٢٤] طريقاً ساكناً، وباطنها: اجعل القلب ساكناً إلى تدبيري إِنَّهُمْ جُنْدٌ مُغْرَقُونَ [٢٤] يعني المخالفين عن توالي تدبير أنفسهم.

<span data-type="title" id=toc-445>[سورة الدخان (٤٤) : آية ٤٢]</span>
إِلاَّ مَنْ رَحِمَ اللَّهُ إِنَّهُ هُوَ الْعَزِيزُ الرَّحِيمُ (٤٢)
قوله: إِلَّا مَنْ رَحِمَ اللَّهُ [٤٢] أي من علم الله في سابق علمه أنه مرحوم، أدركته في العاقبة بركة تلك الرحمة، حيث جعل المؤمنين بعضهم شفعاء بعض.
_________
(١) أويس القرني: أويس بن عامر بن جزء بن مالك ( ... - ٣٧ هـ) : أحد النساك العباد المقدمين، من سادات التابعين. أدرك حياة النبي ولم يره. شهد صفين مع علي. (الحلية ٢/ ٧٩) .
(٢) هرم بن حيان العبدي الأزدي ( ... - بعد ٢٦ هـ) : قائد فاتح، من كبار النساك. من التابعين. (الأعلام ٨/ ٨٢) .
(٣) في صفوة الصفوة ٣/ ٥٥: (قال هرم لأويس: أوصني. قال: توسد الموت إذا تمت، واجعله نصب عينيك، وإذا قمت فادع الله أن يصلح لك قلبك ونيتك ... ) .',NULL,'141',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (139,'<span data-type="title" id=toc-446>السورة التي يذكر فيها الجاثية</span>

<span data-type="title" id=toc-447>[سورة الجاثية (٤٥) : آية ٣]</span>
إِنَّ فِي السَّماواتِ وَالْأَرْضِ لَآياتٍ لِلْمُؤْمِنِينَ (٣)
قوله تعالى: إِنَّ فِي السَّماواتِ وَالْأَرْضِ لَآياتٍ لِلْمُؤْمِنِينَ [٣] قال: العلامات لمن أيقن بقلبه واستدل بكونها على مكونها.

<span data-type="title" id=toc-448>[سورة الجاثية (٤٥) : آية ١٣]</span>
وَسَخَّرَ لَكُمْ مَّا فِي السَّماواتِ وَما فِي الْأَرْضِ جَمِيعاً مِنْهُ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَتَفَكَّرُونَ (١٣)
قوله تعالى: وَسَخَّرَ لَكُمْ مَّا فِي السَّماواتِ وَما فِي الْأَرْضِ جَمِيعاً مِنْهُ [١٣] قال: إذا سكن قلب العبد إلى مولاه قويت حال العبد، فسخر له كل شيء، بل أنس به كل شيء، حتى الطيور والوحوش. وحكي عن الثوري قال خرجت مع شيبان «١» الراعي إلى مكة فعرض لنا الأسد.
فقلت: يا شيبان أما ترى هذا الكلب. فقال: لا تخف. فما هو إلا أن سمع الأسد كلام شيبان الراعي، حتى جعل يبصبص بذنبه، فأتاه شيبان فأخذ بأذنه وعركها. فقلت له: ما هذه الشهرة يا شيبان؟ فقال: وأي شهرة ترى يا ثوري، والله لولا مخافة الشهرة ما حملت زادي إلى مكة إلا على ظهره «٢» . وكان شيبان يحضر صلاة الجمعة، فبصر بذئب عند الغنم، فقال له: اقعد عند الغنم حتى إذا رجعت أعطيتك حملاً، فرجع من صلاة الجمعة، فإذا هو بالذئب قاعد يحفظ له الغنم، فأعطاه حملاً له. وكان سهل يقول لشاب يصحبه: إن كنت تخاف السباع فلا تصحبني.
وسئل سهل: كيف يدرك الرجل منزلة الكرامات؟ فقال: من زهد في الدنيا أربعين يوماً صادقاً مخلصاً فقد ظهرت الكرامات من الله عزَّ وجلَّ له، ومن لم تظهر له فهو لما فقد من زهده من الصدق والإخلاص، أو كلاماً نحو هذا.
_________
(١) شيبان أبو محمد الراعي: كان في العبادة فائقا، وبالتوكل على ربه واثقا. كان في عصر هارون الرشيد.
(الحلية ٨/ ٣١٧ وصفوة الصفوة ٤/ ٣٧٧) .
(٢) صفوة الصفوة ٤/ ٣٧٧، والحلية ٧/ ٦٨- ٦٩ وسير أعلام النبلاء ٧/ ٢٦٨. وروي أن مثل هذا الخبر جرى مع ابن عمر، (لسان الميزان ٢/ ٤٩) ومع إبراهيم بن الأدهم، (التدوين في أخبار قزوين ٢/ ٢٤٦) ومع أم هارون (صفوة الصفوة ٤/ ٣٠٤) .',NULL,'142',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (140,'<span data-type="title" id=toc-449>[سورة الجاثية (٤٥) : الآيات ١٧ الى ١٩]</span>
وَآتَيْناهُمْ بَيِّناتٍ مِنَ الْأَمْرِ فَمَا اخْتَلَفُوا إِلاَّ مِنْ بَعْدِ ما جاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيامَةِ فِيما كانُوا فِيهِ يَخْتَلِفُونَ (١٧) ثُمَّ جَعَلْناكَ عَلى شَرِيعَةٍ مِنَ الْأَمْرِ فَاتَّبِعْها وَلا تَتَّبِعْ أَهْواءَ الَّذِينَ لا يَعْلَمُونَ (١٨) إِنَّهُمْ لَنْ يُغْنُوا عَنْكَ مِنَ اللَّهِ شَيْئاً وَإِنَّ الظَّالِمِينَ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ وَاللَّهُ وَلِيُّ الْمُتَّقِينَ (١٩)
قوله تعالى: وَآتَيْناهُمْ بَيِّناتٍ مِنَ الْأَمْرِ [١٧] قال: فتحنا أسماعهم لفهم خطابنا، وجعلنا أفئدتهم وعاء لكلامنا، وأعطيناهم فراسة صادقة يحكمون بها في عبادنا حكم يقين وأخبار صدق، فهذه هي البينات من الأمر في طريق الباطن.
قوله تعالى: ثُمَّ جَعَلْناكَ عَلى شَرِيعَةٍ مِنَ الْأَمْرِ فَاتَّبِعْها [١٨] قال: يعني منهاج سنن من كان من قبلكم من الأنبياء، فإنهم على منهاج الهدى والشريعة الشارع الممتد الواضح إلى طريق النجاة وسبيل الرشد.
قوله تعالى: إِنَّهُمْ لَنْ يُغْنُوا عَنْكَ مِنَ اللَّهِ شَيْئاً [١٩] من استغنى بغير الله فبغناه افتقر ومن اعتز بغيره فبعزه ذل، ألا ترى أن الله يقول: إِنَّهُمْ لَنْ يُغْنُوا عَنْكَ مِنَ اللَّهِ شَيْئاً [١٩] .

<span data-type="title" id=toc-450>[سورة الجاثية (٤٥) : آية ٢١]</span>
أَمْ حَسِبَ الَّذِينَ اجْتَرَحُوا السَّيِّئاتِ أَنْ نَجْعَلَهُمْ كَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ سَواءً مَحْياهُمْ وَمَماتُهُمْ ساءَ ما يَحْكُمُونَ (٢١)
قوله تعالى: أَمْ حَسِبَ الَّذِينَ اجْتَرَحُوا السَّيِّئاتِ
[٢١] الآية، قال: ليس من أقعد على بساط الموافقة كمن أقيم في مقام المخالفة، فإن بساط الموافقة يجر بصاحبه إلى مقاعد الصدق، ومقام المخالفة يهوي بصاحبه في لظى.

<span data-type="title" id=toc-451>[سورة الجاثية (٤٥) : آية ٢٣]</span>
أَفَرَأَيْتَ مَنِ اتَّخَذَ إِلهَهُ هَواهُ وَأَضَلَّهُ اللَّهُ عَلى عِلْمٍ وَخَتَمَ عَلى سَمْعِهِ وَقَلْبِهِ وَجَعَلَ عَلى بَصَرِهِ غِشاوَةً فَمَنْ يَهْدِيهِ مِنْ بَعْدِ اللَّهِ أَفَلا تَذَكَّرُونَ (٢٣)
قوله تعالى: أَفَرَأَيْتَ مَنِ اتَّخَذَ إِلهَهُ هَواهُ [٢٣] قال: يعني أفرأيت من كان مغموراً في لذة نفسه من الدنيا، غير ورع ولا تقي، فاتبع مراده ولم يسلك مسالك الاقتداء، وآثر شهوات الدنيا على نعيم العقبى، أنى تكون له في الآخرة من الدرجات الرفيعة والمنازل السنية «١» وَأَضَلَّهُ اللَّهُ عَلى عِلْمٍ [٢٣] قال: أي على علم الله السابق فيه بترك عصمته ومعونته.

<span data-type="title" id=toc-452>[سورة الجاثية (٤٥) : آية ٢٦]</span>
قُلِ اللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ إِلى يَوْمِ الْقِيامَةِ لا رَيْبَ فِيهِ وَلكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ (٢٦)
قوله: قُلِ اللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ [٢٦] قال: يحييكم في بطون أمهاتكم، ثم يميتكم بجهالة، ويجمعكم إلى يوم القيامة أولكم وآخركم لا ريب فيه.

<span data-type="title" id=toc-453>[سورة الجاثية (٤٥) : آية ٢٨]</span>
وَتَرى كُلَّ أُمَّةٍ جاثِيَةً كُلُّ أُمَّةٍ تُدْعى إِلى كِتابِهَا الْيَوْمَ تُجْزَوْنَ ما كُنْتُمْ تَعْمَلُونَ (٢٨)
قوله تعالى: وَتَرى كُلَّ أُمَّةٍ جاثِيَةً [٢٨] قال: على ركبها تجادل عن نفسها عند المرافقة الصادق يجتهد في تحقيق صدقه، والجاحد يجتهد في الدفع عن نفسه، وكلٌّ محكوم عليه في الذي أملاه، مدده ريقه، وقلمه لسانه، وقرطاسه جوارحه.

<span data-type="title" id=toc-454>[سورة الجاثية (٤٥) : آية ٣٧]</span>
وَلَهُ الْكِبْرِياءُ فِي السَّماواتِ وَالْأَرْضِ وَهُوَ الْعَزِيزُ الْحَكِيمُ (٣٧)
قوله: وَلَهُ الْكِبْرِياءُ فِي السَّماواتِ وَالْأَرْضِ [٣٧] قال: العلو والقدرة والعظمة والحول والقوة له في جميع الملك، فمن اعتصم به أيّده بحوله وقوته، ومن اعتمد على نفسه وكّله الله إليها.
والله سبحانه وتعالى أعلم.
_________
(١) في تفسير القرطبي ١٦/ ١٦٨ بعد تفسير الآية المذكورة، أن سهل التستري قال: هواك داؤك، فإن خالفته فدواؤك) .',NULL,'143',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (141,'<span data-type="title" id=toc-455>السورة التي يذكر فيها الأحقاف</span>

<span data-type="title" id=toc-456>[سورة الأحقاف (٤٦) : آية ٦]</span>
وَإِذا حُشِرَ النَّاسُ كانُوا لَهُمْ أَعْداءً وَكانُوا بِعِبادَتِهِمْ كافِرِينَ (٦)
قوله تعالى: وَإِذا حُشِرَ النَّاسُ كانُوا لَهُمْ أَعْداءً [٦] قال: في نفوسهم التي أقادتهم إلى متابعتها في الجزاء على أحكام هواها، لأنها تشهد عليهم. وقد قال رسول الله صلّى الله عليه وسلّم: «إن أعدى عدو المرء نفسه التي بين جنبيه» «١» .

<span data-type="title" id=toc-457>[سورة الأحقاف (٤٦) : آية ٩]</span>
قُلْ مَا كُنْتُ بِدْعاً مِنَ الرُّسُلِ وَما أَدْرِي مَا يُفْعَلُ بِي وَلا بِكُمْ إِنْ أَتَّبِعُ إِلاَّ ما يُوحى إِلَيَّ وَما أَنَا إِلاَّ نَذِيرٌ مُبِينٌ (٩)
قوله تعالى: قُلْ مَا كُنْتُ بِدْعاً مِنَ الرُّسُلِ [٩] قال: أي كانت قبلي رسل يأمرون بما آمر به، وينهون عما أنهى عنه، وما كنت عجباً من الرسل، فإني لم أدعكم إلا إلى التوحيد، ولم أدلكم إلا على مكارم الأخلاق، وبهذا بعثت الأنبياء قبلي.
قوله تعالى: وَقالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ [النمل: ١٩] قال:
أي ألهمني التوبة والعمل بالطاعة.

<span data-type="title" id=toc-458>[سورة الأحقاف (٤٦) : آية ١٥]</span>
وَوَصَّيْنَا الْإِنْسانَ بِوالِدَيْهِ إِحْساناً حَمَلَتْهُ أُمُّهُ كُرْهاً وَوَضَعَتْهُ كُرْهاً وَحَمْلُهُ وَفِصالُهُ ثَلاثُونَ شَهْراً حَتَّى إِذا بَلَغَ أَشُدَّهُ وَبَلَغَ أَرْبَعِينَ سَنَةً قالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلى والِدَيَّ وَأَنْ أَعْمَلَ صالِحاً تَرْضاهُ وَأَصْلِحْ لِي فِي ذُرِّيَّتِي إِنِّي تُبْتُ إِلَيْكَ وَإِنِّي مِنَ الْمُسْلِمِينَ (١٥)
قوله: وَأَصْلِحْ لِي فِي ذُرِّيَّتِي [١٥] قال: اجعلهم لك عبيد حق، ولي خَلَفَ صدق.

<span data-type="title" id=toc-459>[سورة الأحقاف (٤٦) : الآيات ٣٠ الى ٣١]</span>
قالُوا يا قَوْمَنا إِنَّا سَمِعْنا كِتاباً أُنْزِلَ مِنْ بَعْدِ مُوسى مُصَدِّقاً لِما بَيْنَ يَدَيْهِ يَهْدِي إِلَى الْحَقِّ وَإِلى طَرِيقٍ مُسْتَقِيمٍ (٣٠) يا قَوْمَنا أَجِيبُوا داعِيَ اللَّهِ وَآمِنُوا بِهِ يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُجِرْكُمْ مِنْ عَذابٍ أَلِيمٍ (٣١)
قوله: يَهْدِي إِلَى الْحَقِّ وَإِلى طَرِيقٍ مُسْتَقِيمٍ [٣٠] قال: أي يدل على طريق الحق بالخروج عن المعاملات والرسومات والتحقيق بالحق، وهو الصراط المستقيم.
قوله: يا قَوْمَنا أَجِيبُوا داعِيَ اللَّهِ [٣١] قال: لا يجيب الداعي إلا من سمع النداء، فوفق للخيرات وأيقن، وإلا فمن يحسن إجابة الدعوة. وقال: إن في قلب كل مؤمن داعياً يدعوه إلى رشده، فالسعيد من سمع دعاء الداعي فاتبعه.

<span data-type="title" id=toc-460>[سورة الأحقاف (٤٦) : آية ٣٥]</span>
فَاصْبِرْ كَما صَبَرَ أُولُوا الْعَزْمِ مِنَ الرُّسُلِ وَلا تَسْتَعْجِلْ لَهُمْ كَأَنَّهُمْ يَوْمَ يَرَوْنَ ما يُوعَدُونَ لَمْ يَلْبَثُوا إِلاَّ ساعَةً مِنْ نَهارٍ بَلاغٌ فَهَلْ يُهْلَكُ إِلاَّ الْقَوْمُ الْفاسِقُونَ (٣٥)
قوله تعالى: فَاصْبِرْ كَما صَبَرَ أُولُوا الْعَزْمِ مِنَ الرُّسُلِ [٣٥] قال: يعني اصبر صبر أهل المعرفة، كما صبر أولو العزم من الرسل الذين كانوا قبلك رضى وتسليماً من غير شكوى ولا جزع. وقال: أولو العزم من الرسل إبراهيم صلوات الله عليه، ابتلي بالنار وذبح الولد فرضي وسلم وأيوب عليه السلام بالبلاء وإسماعيل بالذبح فرضي ونوح بالتكذيب فصبر ويونس ببطن الحوت فدعا والتجأ ويوسف صلوات الله عليه بالسجن والجب فلم يتغير ويعقوب بذهاب البصر وفقدان الولد، فشكا بثه إلى الله، ولم يشك إلى غيره، وهم اثنا عشر نبياً صلوات الله عليهم، صبروا على ما أصابهم، فهم أولو العزم من الرسل، والله سبحانه وتعالى أعلم.
_________
(١) رواية الحديث: (أعدى عدوك نفسك التي بين جنبيك) انظره في: تحفة الأحوذي ٢/ ٤٨١، ٩/ ٣١٩ وشرح سنن ابن ماجة ١/ ٢٠ وعون المعبود ٤/ ٢٨٥ وكشف الخفاء ١/ ١٤٨، ١٦٠، ٢/ ٢٢٢.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (142,'<span data-type="title" id=toc-461>السورة التي يذكر فيها محمد صلى الله عليه وسلّم</span>

<span data-type="title" id=toc-462>[سورة محمد (٤٧) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ أَضَلَّ أَعْمالَهُمْ (١)
قوله تعالى: الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ أَضَلَّ أَعْمالَهُمْ [١] قال: أضلها في إطلاق القول بلا حقيقة معه.

<span data-type="title" id=toc-463>[سورة محمد (٤٧) : آية ٥]</span>
سَيَهْدِيهِمْ وَيُصْلِحُ بالَهُمْ (٥)
قوله: سَيَهْدِيهِمْ وَيُصْلِحُ بالَهُمْ [٥] قال: يعني سيهديهم في قبورهم لجواب منكر ونكير ويصلح بالهم. قال: أي صلح يسرع لهم في القلب بمباشرة الجزاء، وفي الآخرة بلذة اللقاء عند تجلي المكاشفة كفاحاً، والتولي لهم عند ذلك،

<span data-type="title" id=toc-464>[سورة محمد (٤٧) : آية ١١]</span>
ذلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آمَنُوا وَأَنَّ الْكافِرِينَ لا مَوْلى لَهُمْ (١١)
كما قال: ذلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آمَنُوا [١١] أي بالرضى والمحبة والحفظ على مقام القرب.

<span data-type="title" id=toc-465>[سورة محمد (٤٧) : آية ١٥]</span>
مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ فِيها أَنْهارٌ مِنْ ماءٍ غَيْرِ آسِنٍ وَأَنْهارٌ مِنْ لَبَنٍ لَمْ يَتَغَيَّرْ طَعْمُهُ وَأَنْهارٌ مِنْ خَمْرٍ لَذَّةٍ لِلشَّارِبِينَ وَأَنْهارٌ مِنْ عَسَلٍ مُصَفًّى وَلَهُمْ فِيها مِنْ كُلِّ الثَّمَراتِ وَمَغْفِرَةٌ مِنْ رَبِّهِمْ كَمَنْ هُوَ خالِدٌ فِي النَّارِ وَسُقُوا ماءً حَمِيماً فَقَطَّعَ أَمْعاءَهُمْ (١٥)
قوله: وَمَغْفِرَةٌ مِنْ رَبِّهِمْ [١٥] قال: المغفرة من ربهم في الجنة ما يغشاهم عند النظر إلى الحق من أنواره.

<span data-type="title" id=toc-466>[سورة محمد (٤٧) : آية ١٩]</span>
فَاعْلَمْ أَنَّهُ لاَ إِلهَ إِلاَّ اللَّهُ وَاسْتَغْفِرْ لِذَنْبِكَ وَلِلْمُؤْمِنِينَ وَالْمُؤْمِناتِ وَاللَّهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْواكُمْ (١٩)
قوله: وَاسْتَغْفِرْ لِذَنْبِكَ وَلِلْمُؤْمِنِينَ وَالْمُؤْمِناتِ [١٩] قال: يعني استغفر من همة نفس الطبع. قال النبي صلى الله عليه وسلّم: «ما منا إلا من همّ فعصى» ، يعني همت نفسه عليه على قلبه بحظها من عاجل شهوتها بشيء دونه، ثم أعرض عن ذلك واستغفر الله، كما قال النبي صلّى الله عليه وسلّم: «إنه ليغان على قلبي وإني لأستغفر الله تعالى في كل يوم سبعين مرة» «١» .

<span data-type="title" id=toc-467>[سورة محمد (٤٧) : آية ٢٤]</span>
أَفَلا يَتَدَبَّرُونَ الْقُرْآنَ أَمْ عَلى قُلُوبٍ أَقْفالُها (٢٤)
قوله: أَمْ عَلى قُلُوبٍ أَقْفالُها [٢٤] قال: إن الله تعالى خلق القلوب وأقفل عليها بأقفال، وجعل مفاتيحها حقائق الإيمان، فلم يفتح بتلك المفاتيح على التحقيق إلا قلوب أوليائه والمرسلين صلوات الله عليهم أجمعين والصديقين وسائر الناس يخرجون من الدنيا، ولم تفتح أقفال قلوبهم، والزهاد والعباد والعلماء خرجوا منها وقلوبهم مقفلة، لأنهم طلبوا مفاتيحها في
_________
(١) صحيح البخاري: كتاب الدعوات، رقم ٥٩٤٨ وشرح سنن ابن ماجة ١/ ٢٧٠ (رقم ٣٨١٥) ومسند أحمد ٤/ ٢١١.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (143,'العقل، فضلوا الطريق، ولو طلبوه من جهة التوفيق والفضل لأدركوه، والمفتاح أن تعلم أن الله قائم عليك، رقيب على جوارحك، وتعلم أن العمل لا يكمل إلا بالإخلاص مع المراقبة.
قوله: وَكَأَيِّنْ مِنْ قَرْيَةٍ هِيَ أَشَدُّ قُوَّةً مِنْ قَرْيَتِكَ الَّتِي أَخْرَجَتْكَ أَهْلَكْناهُمْ فَلا ناصِرَ لَهُمْ [١٣] في الآية دليل على تفضيله على الكليم، لأنه لم يخرج خوفاً منهم، كما خرج موسى عليه السلام، ولكنه خرج كما قال الله تعالى: أَخْرَجَتْكَ [١٣] ولم يقل خرجت ولا جزعت، لأنه لله وبالله في جميع أوقاته، فلم يجز منه التفات إلى الغير بحال ما.
قوله: أَفَمَنْ كانَ عَلى بَيِّنَةٍ مِنْ رَبِّهِ [١٤] قال: المؤمن على بيان من ربه، ومن كان على بينة من ربه لزم الاقتداء بالسنن.
قوله تعالى: فَاعْلَمْ أَنَّهُ لا إِلهَ إِلَّا اللَّهُ [١٩] قال: الخلق كلهم موتى إلا العلماء، ولذلك دعا نبيه صلى الله عليه وسلّم إلى محل الحياة بالعلم بقوله: فَاعْلَمْ [١٩] .

<span data-type="title" id=toc-468>[سورة محمد (٤٧) : آية ٣٣]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَلا تُبْطِلُوا أَعْمالَكُمْ (٣٣)
قوله: أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ [٣٣] أي في تعظيم الله، وَلا تُبْطِلُوا أَعْمالَكُمْ [٣٣] أي برؤيتها من أنفسكم ومطالبة الأعواض من ربكم، فإن العمل الخالص الذي لم يطلب به العوض.

<span data-type="title" id=toc-469>[سورة محمد (٤٧) : آية ٣٨]</span>
ها أَنْتُمْ هؤُلاءِ تُدْعَوْنَ لِتُنْفِقُوا فِي سَبِيلِ اللَّهِ فَمِنْكُمْ مَنْ يَبْخَلُ وَمَنْ يَبْخَلْ فَإِنَّما يَبْخَلُ عَنْ نَفْسِهِ وَاللَّهُ الْغَنِيُّ وَأَنْتُمُ الْفُقَراءُ وَإِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْماً غَيْرَكُمْ ثُمَّ لا يَكُونُوا أَمْثالَكُمْ (٣٨)
قوله تعالى: وَاللَّهُ الْغَنِيُّ وَأَنْتُمُ الْفُقَراءُ [٣٨] قال: معرفة السر كله في الفقر، وهو سر الله، وعلم الفقر إلى الله تعالى تصحيح علم الغنى بالله عزَّ وجلَّ.
والله سبحانه وتعالى أعلم.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (144,'<span data-type="title" id=toc-470>السورة التي يذكر فيها الفتح</span>

<span data-type="title" id=toc-471>[سورة الفتح (٤٨) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
إِنَّا فَتَحْنا لَكَ فَتْحاً مُبِيناً (١) لِيَغْفِرَ لَكَ اللَّهُ مَا تَقَدَّمَ مِنْ ذَنْبِكَ وَما تَأَخَّرَ وَيُتِمَّ نِعْمَتَهُ عَلَيْكَ وَيَهْدِيَكَ صِراطاً مُسْتَقِيماً (٢)
قوله تعالى: إِنَّا فَتَحْنا لَكَ فَتْحاً مُبِيناً [١] قال: يعني أسرار العلوم في قلبك حتى ظهر عليك آثارها، وهي من أعلام المحبة وتمام النعمة. لِيَغْفِرَ لَكَ اللَّهُ مَا تَقَدَّمَ مِنْ ذَنْبِكَ وَما تَأَخَّرَ [٢] قال: أي ما تقدم من ذنب أبيك آدم صلوات الله عليه وأنت في صلبه، وما تأخر من ذنوب أمتك، إذ كنت قائدهم ودليلهم.

<span data-type="title" id=toc-472>[سورة الفتح (٤٨) : آية ٤]</span>
هُوَ الَّذِي أَنْزَلَ السَّكِينَةَ فِي قُلُوبِ الْمُؤْمِنِينَ لِيَزْدادُوا إِيماناً مَعَ إِيمانِهِمْ وَلِلَّهِ جُنُودُ السَّماواتِ وَالْأَرْضِ وَكانَ اللَّهُ عَلِيماً حَكِيماً (٤)
قوله تعالى: هُوَ الَّذِي أَنْزَلَ السَّكِينَةَ فِي قُلُوبِ الْمُؤْمِنِينَ [٤] يعني الطمأنينة. فأول ما كاشف الله به عباده المعارف، ثم الوسائل، ثم السكينة، ثم البصائر. فمن كاشفه الحق بالبصائر عرف الأشياء بما فيها من الجواهر، كأبي بكر الصديق رضي الله عنه ما أخطأ في نطق.
قوله: وَلِلَّهِ جُنُودُ السَّماواتِ وَالْأَرْضِ [٤] قال: جنوده مختلفة، فجنوده في السماء الأنبياء، وفي الأرض الأولياء وجنوده في السماء القلوب، وفي الأرض النفوس ما سلط الله عليك فهو من جنوده وإن سلط الله عليك نفسك أهلك نفسك بنفسك، وإن سلط عليك جوارحك أهلك جوارحك بجوارحك، وإن سلط نفسك على قلبك قادتك إلى متابعة الهوى، وإن سلط قلبك على نفسك وجوارحك زمها بالأدب، وألزمها العبادة، وزينها بالإخلاص في العبودية، فهذا كله جنود الله.

<span data-type="title" id=toc-473>[سورة الفتح (٤٨) : الآيات ٨ الى ١١]</span>
إِنَّا أَرْسَلْناكَ شاهِداً وَمُبَشِّراً وَنَذِيراً (٨) لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُ وَتُسَبِّحُوهُ بُكْرَةً وَأَصِيلاً (٩) إِنَّ الَّذِينَ يُبايِعُونَكَ إِنَّما يُبايِعُونَ اللَّهَ يَدُ اللَّهِ فَوْقَ أَيْدِيهِمْ فَمَنْ نَكَثَ فَإِنَّما يَنْكُثُ عَلى نَفْسِهِ وَمَنْ أَوْفى بِما عاهَدَ عَلَيْهُ اللَّهَ فَسَيُؤْتِيهِ أَجْراً عَظِيماً (١٠) سَيَقُولُ لَكَ الْمُخَلَّفُونَ مِنَ الْأَعْرابِ شَغَلَتْنا أَمْوالُنا وَأَهْلُونا فَاسْتَغْفِرْ لَنا يَقُولُونَ بِأَلْسِنَتِهِمْ مَّا لَيْسَ فِي قُلُوبِهِمْ قُلْ فَمَنْ يَمْلِكُ لَكُمْ مِنَ اللَّهِ شَيْئاً إِنْ أَرادَ بِكُمْ ضَرًّا أَوْ أَرادَ بِكُمْ نَفْعاً بَلْ كانَ اللَّهُ بِما تَعْمَلُونَ خَبِيراً (١١)
قوله: إِنَّا أَرْسَلْناكَ شاهِداً وَمُبَشِّراً وَنَذِيراً [٨] قال: شاهداً عليهم بالتوحيد، ومبشراً لهم بالمعونة والتأييد، ومحذراً عن البدع والضلالات.
قوله: وَتُعَزِّرُوهُ وَتُوَقِّرُوهُ [٩] قال: أي تعظموه غاية التعظيم في قلوبكم، وتطيعوه بأبدانكم ولهذا سمى التعزير تعزيراً لأنه أكبر التأديب.
قوله: يَدُ اللَّهِ فَوْقَ أَيْدِيهِمْ [١٠] قال: أي حول الله وقوته فوق قوتهم وحركتهم، وهو قولهم للرسول صلّى الله عليه وسلّم عند البيعة: «بايعناك على أن لا نفر ونقاتل لك» . وفيها وجه آخر:
يَدُ اللَّهِ فَوْقَ أَيْدِيهِمْ [١٠] أي منة الله عليهم في الهداية لبيعتهم وثوابه لهم فوق بيعتهم وطاعتهم لك.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (145,'قوله: شَغَلَتْنا أَمْوالُنا وَأَهْلُونا [١١] اعتذروا به، فحكاه الله لك لتعلم أن الإقبال على الله عزَّ وجلَّ بترك الدنيا وما فيها، فإنها تشغل عن الله ألا ترى المنافقين كيف اعتذروا بقولهم:
شَغَلَتْنا أَمْوالُنا وَأَهْلُونا [١١] .

<span data-type="title" id=toc-474>[سورة الفتح (٤٨) : الآيات ٢٥ الى ٢٧]</span>
هُمُ الَّذِينَ كَفَرُوا وَصَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرامِ وَالْهَدْيَ مَعْكُوفاً أَنْ يَبْلُغَ مَحِلَّهُ وَلَوْلا رِجالٌ مُؤْمِنُونَ وَنِساءٌ مُؤْمِناتٌ لَمْ تَعْلَمُوهُمْ أَنْ تَطَؤُهُمْ فَتُصِيبَكُمْ مِنْهُمْ مَعَرَّةٌ بِغَيْرِ عِلْمٍ لِيُدْخِلَ اللَّهُ فِي رَحْمَتِهِ مَنْ يَشاءُ لَوْ تَزَيَّلُوا لَعَذَّبْنَا الَّذِينَ كَفَرُوا مِنْهُمْ عَذاباً أَلِيماً (٢٥) إِذْ جَعَلَ الَّذِينَ كَفَرُوا فِي قُلُوبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجاهِلِيَّةِ فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلى رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوى وَكانُوا أَحَقَّ بِها وَأَهْلَها وَكانَ اللَّهُ بِكُلِّ شَيْءٍ عَلِيماً (٢٦) لَقَدْ صَدَقَ اللَّهُ رَسُولَهُ الرُّؤْيا بِالْحَقِّ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرامَ إِنْ شاءَ اللَّهُ آمِنِينَ مُحَلِّقِينَ رُؤُسَكُمْ وَمُقَصِّرِينَ لا تَخافُونَ فَعَلِمَ ما لَمْ تَعْلَمُوا فَجَعَلَ مِنْ دُونِ ذلِكَ فَتْحاً قَرِيباً (٢٧)
قوله: وَلَوْلا رِجالٌ مُؤْمِنُونَ وَنِساءٌ مُؤْمِناتٌ لَمْ تَعْلَمُوهُمْ أَنْ تَطَؤُهُمْ [٢٥] قال:
المؤمن على الحقيقة من لا يغفل عن نفسه، وقلبه يفتش أحواله ويراقب أوقاته، فيرى زيادته من نقصانه، فيشكر عند رؤية الزيادة، ويتفرغ ويدعو عند النقصان، هؤلاء الذين بهم يدفع الله البلاء عن أهل الأرض، ولا يكون المؤمن متهاوناً بأدنى التقصير، فإن التهاون بالقليل يستوجب الكثير.
قال: فإن العبد لا يجد طعم الإيمان حتى يدع ست خصال: يدع الحرام والسحت والشبهة والجهل والمسكر والرياء، ويتمسك بالعلم وتصحيح العمل والنصح بالقلب والصدق باللسان والصلاح مع الخلق في معاشرتهم والإخلاص لربه في معاملته. قال: وكتاب الله مبني على خمس: الصدق والاستخارة والاستشارة والصبر والشكر.
قوله: وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوى وَكانُوا أَحَقَّ بِها وَأَهْلَها [٢٦] قال: هي كلمة لا إله إلا الله فإنها رأس التقوى. ثم قال: خير الناس المسلمون، وخير المسلمين المؤمنين، وخير المؤمنين العلماء العاملون، وخير العاملين الخائفون، وخير الخائفين المخلصون المتقون الذين وصلوا إخلاصهم وتقواهم بالموت، فإن مثله كمثل راكب السفينة بالبحر، لا يدري أينجو منه أن يغرق فيه، والذين تم لهم ذلك أصحاب رسول الله صلّى الله عليه وسلّم بقوله: وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوى [٢٦] .
قوله: لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرامَ إِنْ شاءَ اللَّهُ آمِنِينَ [٢٧] قيل: ما هذا الاستثناء؟
قال: هذا تعليم للعباد وتأديب لهم بشدة الافتقار إليه في كل وقت وحال وتأكيد، فإن الحق إذا استثني مع كمال علمه لم يكن لأحد من عباده مع قصور علمهم أن يحكم في شيء من غير استثناء.

<span data-type="title" id=toc-475>[سورة الفتح (٤٨) : آية ٢٩]</span>
مُحَمَّدٌ رَسُولُ اللَّهِ وَالَّذِينَ مَعَهُ أَشِدَّاءُ عَلَى الْكُفَّارِ رُحَماءُ بَيْنَهُمْ تَراهُمْ رُكَّعاً سُجَّداً يَبْتَغُونَ فَضْلاً مِنَ اللَّهِ وَرِضْواناً سِيماهُمْ فِي وُجُوهِهِمْ مِنْ أَثَرِ السُّجُودِ ذلِكَ مَثَلُهُمْ فِي التَّوْراةِ وَمَثَلُهُمْ فِي الْإِنْجِيلِ كَزَرْعٍ أَخْرَجَ شَطْأَهُ فَآزَرَهُ فَاسْتَغْلَظَ فَاسْتَوى عَلى سُوقِهِ يُعْجِبُ الزُّرَّاعَ لِيَغِيظَ بِهِمُ الْكُفَّارَ وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ مِنْهُمْ مَغْفِرَةً وَأَجْراً عَظِيماً (٢٩)
قوله تعالى: سِيماهُمْ فِي وُجُوهِهِمْ مِنْ أَثَرِ السُّجُودِ [٢٩] قال: المؤمن بالله وجه بلا قفا، مقبل عليه غير معرض عنه، ذلك سيما المؤمن. وقال عامر ابن عبد قيس: كاد المؤمن يخبر عن مكنون علمه، وكذلك وجه الكافر، وذلك قوله: سِيماهُمْ فِي وُجُوهِهِمْ [٢٩] وقال ابن مسعود رضي الله عنه: سر المؤمن يكون رداء عليه.
والله سبحانه وتعالى أعلم.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (146,'<span data-type="title" id=toc-476>السورة التي يذكر فيها الحجرات</span>

<span data-type="title" id=toc-477>[سورة الحجرات (٤٩) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَرَسُولِهِ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ (١) يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تَرْفَعُوا أَصْواتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلا تَجْهَرُوا لَهُ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ أَنْ تَحْبَطَ أَعْمالُكُمْ وَأَنْتُمْ لا تَشْعُرُونَ (٢) إِنَّ الَّذِينَ يَغُضُّونَ أَصْواتَهُمْ عِنْدَ رَسُولِ اللَّهِ أُولئِكَ الَّذِينَ امْتَحَنَ اللَّهُ قُلُوبَهُمْ لِلتَّقْوى لَهُمْ مَغْفِرَةٌ وَأَجْرٌ عَظِيمٌ (٣)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَرَسُولِهِ [١] قال: إن الله تعالى أدب عباده المؤمنين، أي لا تقولوا قبل أن يقول، فإذا قال فأقبلوا عليه ناصتين له، مستمعين إليه، واتقوا الله في إهمال حقه، وتضييع حرمته إِنَّ اللَّهَ سَمِيعٌ [١] ما تقولون عَلِيمٌ [١] بما تعملون.
قوله تعالى: لاَ تَرْفَعُوا أَصْواتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ [٢] أي لا تخاطبوه إلا متفهمين، ثم بين كرامة من عظّمه فقال: أُولئِكَ الَّذِينَ امْتَحَنَ اللَّهُ قُلُوبَهُمْ لِلتَّقْوى [٣] أي أخلص نياتهم له.

<span data-type="title" id=toc-478>[سورة الحجرات (٤٩) : الآيات ٦ الى ٧]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا إِنْ جاءَكُمْ فاسِقٌ بِنَبَإٍ فَتَبَيَّنُوا أَنْ تُصِيبُوا قَوْماً بِجَهالَةٍ فَتُصْبِحُوا عَلى ما فَعَلْتُمْ نادِمِينَ (٦) وَاعْلَمُوا أَنَّ فِيكُمْ رَسُولَ اللَّهِ لَوْ يُطِيعُكُمْ فِي كَثِيرٍ مِنَ الْأَمْرِ لَعَنِتُّمْ وَلكِنَّ اللَّهَ حَبَّبَ إِلَيْكُمُ الْإِيمانَ وَزَيَّنَهُ فِي قُلُوبِكُمْ وَكَرَّهَ إِلَيْكُمُ الْكُفْرَ وَالْفُسُوقَ وَالْعِصْيانَ أُولئِكَ هُمُ الرَّاشِدُونَ (٧)
قوله: إِنْ جاءَكُمْ فاسِقٌ بِنَبَإٍ [٦] قال: الفاسق الكذاب. وباطنها تأديب من بلغه ذمّه من أحد بأن لا يعجل بعقوبته ما لم يتعرف ذلك من نفسه.
قوله: فَضْلًا مِنَ اللَّهِ وَنِعْمَةً [٨] قال: تفضل الله عليهم فيما ابتدأهم به، وهداهم إليه بأنواع القرب والزلف.
قوله: وَلكِنَّ اللَّهَ حَبَّبَ إِلَيْكُمُ الْإِيمانَ وَزَيَّنَهُ فِي قُلُوبِكُمْ [٧] قال: أي استخلص قلوبكم عطفاً منه في عبادته بالإخلاص فيها، إذ الاستخلاص من عطفه، والإخلاص من حقه، ولن يقدر العبد على تأدية حقه إلا بعطفه بالمعونة عليه بأسباب الإيمان، وهي الحجج القاطعة والآيات المعجزة.
قوله: وَكَرَّهَ إِلَيْكُمُ الْكُفْرَ وَالْفُسُوقَ وَالْعِصْيانَ [٧] خوفاً من عاقبته المذمومة.

<span data-type="title" id=toc-479>[سورة الحجرات (٤٩) : آية ٩]</span>
وَإِنْ طائِفَتانِ مِنَ الْمُؤْمِنِينَ اقْتَتَلُوا فَأَصْلِحُوا بَيْنَهُما فَإِنْ بَغَتْ إِحْداهُما عَلَى الْأُخْرى فَقاتِلُوا الَّتِي تَبْغِي حَتَّى تَفِيءَ إِلى أَمْرِ اللَّهِ فَإِنْ فاءَتْ فَأَصْلِحُوا بَيْنَهُما بِالْعَدْلِ وَأَقْسِطُوا إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ (٩)
قوله: وَإِنْ طائِفَتانِ مِنَ الْمُؤْمِنِينَ اقْتَتَلُوا فَأَصْلِحُوا بَيْنَهُما [٩] قال: ظاهرها ما عليه أهل التفسير، وباطنها هو الروح والعقل والقلب والطبع والهوى والشهوة، فإن بغى الطبع والهوى والشهوة على القلب والعقل والروح فليقاتله العبد بسيوف المراقبة وسهام المطالعة وأنوار الموافقة، ليكون الروح والعقل غالباً والهوى والشهوة مغلوبا.

<span data-type="title" id=toc-480>[سورة الحجرات (٤٩) : آية ١٢]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا اجْتَنِبُوا كَثِيراً مِنَ الظَّنِّ إِنَّ بَعْضَ الظَّنِّ إِثْمٌ وَلا تَجَسَّسُوا وَلا يَغْتَبْ بَعْضُكُمْ بَعْضاً أَيُحِبُّ أَحَدُكُمْ أَنْ يَأْكُلَ لَحْمَ أَخِيهِ مَيْتاً فَكَرِهْتُمُوهُ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ تَوَّابٌ رَحِيمٌ (١٢)
قوله: اجْتَنِبُوا كَثِيراً مِنَ الظَّنِّ [١٢] قال: أي لا تطعنوا على أحد بسوء الظن من غير حقيقة. وقد قال النبي صلى الله عليه وسلّم: «أكذب الحديث الظن» «١» . ثم قال سهل: الظن السيئ من الجهل
_________
(١) صحيح البخاري: كتاب النكاح، رقم ٤٨٤٩ وكتاب الأدب، رقم ٥٧١٧، ٥٧١٩ وصحيح مسلم، كتاب الأدب، رقم ٢٥٦٣.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (147,'من نفس الطبع، وأجهل الناس من قطع على قلبه من غير علم، فقد قال الله تعالى: وَذلِكُمْ ظَنُّكُمُ الَّذِي ظَنَنْتُمْ بِرَبِّكُمْ أَرْداكُمْ فَأَصْبَحْتُمْ مِنَ الْخاسِرِينَ [فصلت: ٢٣] وإن العبد ليحرم الرزق الهني وصلاته بالليل بسوء الظن. وقد كان رجل من العباد نام ليلة عن ورده، فجزع عليه، فقيل: أتجزع على ما تدركه؟ قال: لست أجزع عليه، وإنما أجزع على الذنب الذي به صرت محروماً عن ذلك الخير. فقيل لسهل: ما معنى قوله صلّى الله عليه وسلّم: «احترسوا من الناس بسوء الظن» «١» ، فقال: معنى هذا بسوء الظن بنفسك لا بالناس، أي اتهم نفسك بأنك لا تنصفهم من نفسك في معاملاتهم.
قوله تعالى: وَلا تَجَسَّسُوا [١٢] قال: أي لا تبحث عن المعائب التي سترها الله على عباده، فإنك ربما تبتلى بذلك. وقد حكي عن عيسى عليه السلام أنه كان يقول: لا تكثروا الكلام في غير ذكر الله عزَّ وجلَّ، فتقسوا قلوبكم، فإن القلب القاسي بعيد من الله ولا تنظروا في ذنوب الناس كأنكم أرباب، وانظروا إلى أعمالكم كالعبيد، واعلموا أن الناس مبتلى ومعافى، فارحموا أهل البلاء وسلوا الله العافية «٢» .
قوله: وَلا يَغْتَبْ بَعْضُكُمْ بَعْضاً [١٢] قال: من أراد أن يسلم من الغيبة فليسد على نفسه باب الظنون، فإن من سلم من الظن سلم من الغيبة، ومن سلم من الغيبة سلم من الزور، ومن سلم من الزور سلم من البهتان. قال: وقال ابن عباس رضي الله عنهما: للمنافق غيبة، وليس للفاسق غيبة، لأن المنافق كتم نفاقه، والفاسق افتخر بفسقه. قال: وهذا إنَّما أراد به فيما أظهره من المعاصي، فأما ما كتمه من المعاصي ففيه غيبة.

<span data-type="title" id=toc-481>[سورة الحجرات (٤٩) : الآيات ١٤ الى ١٥]</span>
قالَتِ الْأَعْرابُ آمَنَّا قُلْ لَمْ تُؤْمِنُوا وَلكِنْ قُولُوا أَسْلَمْنا وَلَمَّا يَدْخُلِ الْإِيمانُ فِي قُلُوبِكُمْ وَإِنْ تُطِيعُوا اللَّهَ وَرَسُولَهُ لا يَلِتْكُمْ مِنْ أَعْمالِكُمْ شَيْئاً إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ (١٤) إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ ثُمَّ لَمْ يَرْتابُوا وَجاهَدُوا بِأَمْوالِهِمْ وَأَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ أُولئِكَ هُمُ الصَّادِقُونَ (١٥)
قوله: قُلْ لَمْ تُؤْمِنُوا وَلكِنْ قُولُوا أَسْلَمْنا [١٤] قال: يعني أقررنا مخافة السبي والقتل لأن الإيمان إقرار باللسان صدقاً، وإيقان في القلب عقداً، وتحقيقها بالجوارح إخلاصاً، وليس في الإيمان أنساب، وإنما الأنساب في الإسلام، والمسلم محبوب إلى الخلق، والمؤمن غني عن الخلق.
قوله: يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا [١٧] أي صدقوك فيما دعوتهم إليه.

<span data-type="title" id=toc-482>[سورة الحجرات (٤٩) : آية ١٧]</span>
يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا قُلْ لا تَمُنُّوا عَلَيَّ إِسْلامَكُمْ بَلِ اللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَداكُمْ لِلْإِيمانِ إِنْ كُنْتُمْ صادِقِينَ (١٧)
بَلِ اللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَداكُمْ لِلْإِيمانِ إِنْ كُنْتُمْ صادِقِينَ [١٧] أي عالمين بأن الله هو الذي من عليكم بالهداية في البداية. قال سهل: استعملت الورع أربعين سنة، ثم وقع مني التفات فأدركني قوله: يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا [١٧] .
والله سبحانه وتعالى أعلم.
_________
(١) المعجم الأوسط ١/ ١٨٩ وفتح الباري ١٠/ ٥٣١.
(٢) الموطأ ٢/ ٩٨٦ (رقم ١٧٨٤) ومصنف ابن أبي شيبة ٦/ ٣٤٠ (رقم ٣١٨٧٩) ، ٧/ ٦٥ (رقم ٣٤٢٣٠) وشعب الإيمان ٤/ ٢٦٣.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (148,'<span data-type="title" id=toc-483>السورة التي يذكر فيها ق</span>

<span data-type="title" id=toc-484>[سورة ق (٥٠) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
ق وَالْقُرْآنِ الْمَجِيدِ (١)
قوله تعالى: ق [١] أقسم الله تعالى بقوته وقدرته، وظاهرها الجبل المحيط بالدنيا، وهو أول جبل خلقه الله تعالى، ثم بعده جبل أبي قبيس وهو الجبل الذي فوق الصفا، ودونه بمسيرة سنة جبل تغرب الشمس وراءه، كما قال: حَتَّى تَوارَتْ بِالْحِجابِ [ص: ٣٢] وله وجه كوجه الإنسان، وقلب كقلوب الملائكة في المعرفة.
قوله: وَالْقُرْآنِ الْمَجِيدِ [١] قال: يعني المشرف على سائر الكلام.

<span data-type="title" id=toc-485>[سورة ق (٥٠) : آية ٨]</span>
تَبْصِرَةً وَذِكْرى لِكُلِّ عَبْدٍ مُنِيبٍ (٨)
قوله: تَبْصِرَةً وَذِكْرى لِكُلِّ عَبْدٍ مُنِيبٍ [٨] أي مخلص القلب لله بالتوحيد إليه، وإدامة ذكره بواجباته.

<span data-type="title" id=toc-486>[سورة ق (٥٠) : آية ١٢]</span>
كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَأَصْحابُ الرَّسِّ وَثَمُودُ (١٢)
قوله تعالى: وَأَصْحابُ الرَّسِّ [١٢] أي البئر.

<span data-type="title" id=toc-487>[سورة ق (٥٠) : آية ١٤]</span>
وَأَصْحابُ الْأَيْكَةِ وَقَوْمُ تُبَّعٍ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيدِ (١٤)
والْأَيْكَةِ [١٤] الغيضة، وباطنها أصحاب الرس أصحاب الجهل. وَأَصْحابُ الْأَيْكَةِ [١٤] متبعو الشهوات.

<span data-type="title" id=toc-488>[سورة ق (٥٠) : آية ١٨]</span>
مَّا يَلْفِظُ مِنْ قَوْلٍ إِلاَّ لَدَيْهِ رَقِيبٌ عَتِيدٌ (١٨)
قوله تعالى: مَّا يَلْفِظُ مِنْ قَوْلٍ إِلَّا لَدَيْهِ رَقِيبٌ عَتِيدٌ [١٨] قال: أي حافظ حاضر لا يغيب عنه، ولا يعلم الملك ما في الضمير من الخير والشر إلا عند مساكنة القلوب إياه، فيظهر أثر ذلك على الصدر من الصدر إلى الجوارح نور ورائحة طيبة عند العزم على الخير، وظلمة ورائحة منتنة عند العزم على الشر، والله يعلم ذلك منه على كل حال، فليتقه بقوله: إِنَّ اللَّهَ كانَ عَلَيْكُمْ رَقِيباً [النساء: ١] .

<span data-type="title" id=toc-489>[سورة ق (٥٠) : الآيات ٢١ الى ٢٢]</span>
وَجاءَتْ كُلُّ نَفْسٍ مَعَها سائِقٌ وَشَهِيدٌ (٢١) لَقَدْ كُنْتَ فِي غَفْلَةٍ مِنْ هذا فَكَشَفْنا عَنْكَ غِطاءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيدٌ (٢٢)
قوله تعالى: وَجاءَتْ كُلُّ نَفْسٍ مَعَها سائِقٌ وَشَهِيدٌ [٢١] يعني كتبة في الدنيا تسوقه إلى المحشر، ويشهدون له وعليه، فيقول العبد: أليس قولك الحق وقد قلت: وَإِنْ تَعُدُّوا نِعْمَتَ اللَّهِ لاَ تُحْصُوها [إبراهيم: ٣٤] ، وقال نبيك صلّى الله عليه وسلّم: «ما منكم أحد يدخل الجنة بعمله إلا برحمة الله» «١» ، فيقول الله تعالى: «قولي الحق، وصدق نبيي صلّى الله عليه وسلّم، انطلق إلى الجنة برحمتي» . قال:
وهو معنى قوله تعالى: لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ [هود: ١١] .
_________
(١) صحيح مسلم، رقم ٢٨١٥، ٢٨١٦ ومجمع الزوائد ١٠/ ٣٥٧.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (149,'قوله تعالى: فَبَصَرُكَ الْيَوْمَ حَدِيدٌ [٢٢] يعني بصر قلبك نافذ في مشاهدة الأحوال كلها.

<span data-type="title" id=toc-490>[سورة ق (٥٠) : آية ٢٩]</span>
مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَما أَنَا بِظَلاَّمٍ لِلْعَبِيدِ (٢٩)
قوله تعالى: مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ [٢٩] أي ما يتغير عندي ما سبق في علمي، فيكون بخلاف ما سبق العلم فيه.

<span data-type="title" id=toc-491>[سورة ق (٥٠) : آية ٣٢]</span>
هذا ما تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٍ (٣٢)
قوله تعالى: لِكُلِّ أَوَّابٍ حَفِيظٍ [٣٢] قال: هو الراجع بقلبه من الوسوسة إلى السكون إلى الله تعالى. والحفيظ المحافظ على الأوقات والأحوال بالأوامر والطاعات. وقال ابن عيينة:
الأواب الحفيظ الذي لا يقوم من مجلس حتى يستغفر الله منه، خيراً كان أو شراً، لما يرى فيه من الخلل والتقصير.

<span data-type="title" id=toc-492>[سورة ق (٥٠) : آية ٣٧]</span>
إِنَّ فِي ذلِكَ لَذِكْرى لِمَنْ كانَ لَهُ قَلْبٌ أَوْ أَلْقَى السَّمْعَ وَهُوَ شَهِيدٌ (٣٧)
قوله تعالى: إِنَّ فِي ذلِكَ لَذِكْرى لِمَنْ كانَ لَهُ قَلْبٌ [٣٧] يعني لمن كان له عقل يكسب به علم الشرع.
قوله تعالى: أَوْ أَلْقَى السَّمْعَ وَهُوَ شَهِيدٌ [٣٧] يعني استمع إلى ذكرنا وهو حاضر مشاهد ربه غير غائب عنه. وسئل سهل عن العقل، قال: العقل حسن النظر لنفسك في عاقبة أمرك.
والله سبحانه وتعالى أعلم بالصواب.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (150,'<span data-type="title" id=toc-493>السورة التي يذكر فيها الذاريات</span>

<span data-type="title" id=toc-494>[سورة الذاريات (٥١) : آية ١٥]</span>
إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَعُيُونٍ (١٥)
قوله تعالى: إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَعُيُونٍ [١٥] قال: المتقي في الدنيا في جنات الرضى يتقلب، وفي عيون الأنس يسبح، هذا باطن الآية.

<span data-type="title" id=toc-495>[سورة الذاريات (٥١) : آية ١٧]</span>
كانُوا قَلِيلاً مِنَ اللَّيْلِ ما يَهْجَعُونَ (١٧)
قوله تعالى: كانُوا قَلِيلًا مِنَ اللَّيْلِ مَا يَهْجَعُونَ [١٧] قال: لا يغفلون ولا ينامون عن الذكر بحال.

<span data-type="title" id=toc-496>[سورة الذاريات (٥١) : الآيات ١٩ الى ٢٢]</span>
وَفِي أَمْوالِهِمْ حَقٌّ لِلسَّائِلِ وَالْمَحْرُومِ (١٩) وَفِي الْأَرْضِ آياتٌ لِلْمُوقِنِينَ (٢٠) وَفِي أَنْفُسِكُمْ أَفَلا تُبْصِرُونَ (٢١) وَفِي السَّماءِ رِزْقُكُمْ وَما تُوعَدُونَ (٢٢)
قوله تعالى: وَفِي أَمْوالِهِمْ حَقٌّ لِلسَّائِلِ وَالْمَحْرُومِ [١٩] قال: يعني الصدقة على من طلبها منهم ومن لم يطلبها. وقال الحسن البصري: أدركت أقواماً إن كان الرجل ليعزم على أهله أن لا يردوا سائلاً، ولقد أدركت أقواماً إن كان الرجل ليخلف أخاه في أهله أربعين عاماً، وإن أهل البيت يبتلون بالسائل، ما هو من الجن ولا من الإنس، وإن الذين كانوا من قبلكم كانوا يأخذون من الدنيا بلاغاً، ويبتاعون بالفضل أنفسهم. رحم الله امرأ جعل العيش عيشاً واحداً، فأكل كسرة وليس خلقاً، ولزق بالأرض، واجتهد في العبادة، وبكى على الخطيئة، وهرب من العقوبة، وابتغى الرحمة، حتى يأتي عليه أجله وهو كذلك «١» . وحكي (أن رجلاً أتى النبي صلى الله عليه وسلّم فقال: يا رسول الله مالي لا أحب الموت، جعلني الله فداك؟ فقال: هل لك مال؟ قال: نعم. قال: قدم مالك.
قال: لا أطيق ذلك يا رسول الله. قال: فإن قلب المرء مع ماله، إن قدمه أحب أن يلحقه، وإن أخره أحب أن يتخلف معه) «٢» .
قوله تعالى: وَفِي الْأَرْضِ آياتٌ لِلْمُوقِنِينَ [٢٠] قال: يعني للعارفين بالله يستدلون بها على معرفتهم.
قوله تعالى: وَفِي أَنْفُسِكُمْ أَفَلا تُبْصِرُونَ [٢١] قال: أي في صورها وتقديرها بأحسن التقادير، وعروقها السائرة فيها كالأنهار الجارية، وشقوقها من غير ألم وصل إليكم بعد ما كنتم نطفاً، ثم ركبكم من طبق إلى طبق، أفلا تبصرون هذه القدرة البليغة فتؤمنوا بوحدانيته وقدرته، وأن الله تعالى خلق في نفس ابن آدم ألفاً وثمانين عبرة، فثلاثمائة وستون منها ظاهرة، وثلاثمائة
_________
(١) الحلية ٢/ ١٤٩ وكتاب الزهد الكبير ٢/ ٦٥. [.....]
(٢) الفردوس بمأثور الخطاب ٣/ ٢٠٥ (رقم ٤٥٨٠) .',NULL,'153',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (151,'وستون منها باطنة، لو كشف عنها لأبصرتم، وثلاثمائة وستون منها غامضة لا يعرفها إلا نبي أو صديق، لو بدت منها عبرة لأهل العقول لوصلوا إلى الإخلاص، فإن الله تعالى حجب قلوب الغافلين عن ذكره باتباعهم الشهوات عن هذه العبر، فكشف قلوب العارفين به عنها فأوصلهم إليه.
قوله تعالى: وَفِي السَّماءِ رِزْقُكُمْ وَما تُوعَدُونَ [٢٢] أي تفرغوا لعبادتي ولا يشغلكم طلب الرزق عنا، فإنا نرزقكم، ثم قال: إن الله رضي عنكم بعبادة يوم فارضوا عنه برزق يوم بيوم. قال: وفيها وجه آخر: وَفِي السَّماءِ رِزْقُكُمْ [٢٢] أي من الذكر وثوابه.

<span data-type="title" id=toc-497>[سورة الذاريات (٥١) : آية ٢٤]</span>
هَلْ أَتاكَ حَدِيثُ ضَيْفِ إِبْراهِيمَ الْمُكْرَمِينَ (٢٤)
قوله تعالى: هَلْ أَتاكَ حَدِيثُ ضَيْفِ إِبْراهِيمَ الْمُكْرَمِينَ [٢٤] قال: سماهم مكرمين لأنه خدمهم بنفسه، وكان منذ سبعة أيام لم يطعم شيئاً، ينتظر ضيفاً، فلما أرسل الله تعالى ملائكته إليه استبشر بهم وخدمهم بنفسه ولم يطعم معهم، وهي علامة الخلة المؤكدة أن يطعم ولا يطعم، ويشفي الغير من ألم ويسقم.

<span data-type="title" id=toc-498>[سورة الذاريات (٥١) : آية ٥٠]</span>
فَفِرُّوا إِلَى اللَّهِ إِنِّي لَكُمْ مِنْهُ نَذِيرٌ مُبِينٌ (٥٠)
قوله تعالى: فَفِرُّوا إِلَى اللَّهِ إِنِّي لَكُمْ مِنْهُ نَذِيرٌ مُبِينٌ [٥٠] قال: يعني ففروا مما سوى الله إلى الله، وفروا من المعصية إلى الطاعة، ومن الجهل إلى العلم، ومن عذابه إلى رحمته، ومن سخطه إلى رضوانه. وقد قال النبي صلى الله عليه وسلّم: «أعوذ بك منك» «١» ، فهذا أيضاً باب منه عظيم.

<span data-type="title" id=toc-499>[سورة الذاريات (٥١) : آية ٥٤]</span>
فَتَوَلَّ عَنْهُمْ فَما أَنْتَ بِمَلُومٍ (٥٤)
قوله تعالى: فَتَوَلَّ عَنْهُمْ فَما أَنْتَ بِمَلُومٍ [٥٤] قال: أعرض عنهم فقد جهدت في الإِبلاغ جهدك.
والله سبحانه وتعالى أعلم.
_________
(١) المستدرك على الصحيحين ٣/ ٩٣ (رقم ٤٥٠٢) وسنن الترمذي ٥/ ٥٢٤ (رقم ٣٤٩٣) ، ٥/ ٥٦١ (رقم ٣٥٦٥) .',NULL,'154',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (152,'<span data-type="title" id=toc-500>السورة التي يذكر فيها الطور</span>

<span data-type="title" id=toc-501>[سورة الطور (٥٢) : الآيات ٤ الى ٥]</span>
وَالْبَيْتِ الْمَعْمُورِ (٤) وَالسَّقْفِ الْمَرْفُوعِ (٥)
قوله تعالى: وَالْبَيْتِ الْمَعْمُورِ [٤] قال: ظاهرها ما حكى محمد بن سوار بإسناده عن ابن مسعود رضي الله عنه قال: قال النبي صلى الله عليه وسلّم: «ليلة أسري بي إلى السماء رأيت البيت المعمور في السماء الرابعة- ويروى السابعة- يحجه كل يوم سبعون ألف ملك لا يرجعون إليه بعده أبداً» «١» الحديث بطوله ... وباطنها القلب قلوب العارفين معمورة بمعرفته ومحبته، والأنس به، وهو الذي تحجه الملائكة لأنه بيت التوحيد.
قوله تعالى: وَالسَّقْفِ الْمَرْفُوعِ [٥] هو العمل المرضي الذي لا يراد به جزاء إلا الله تعالى.

<span data-type="title" id=toc-502>[سورة الطور (٥٢) : آية ٢٦]</span>
قالُوا إِنَّا كُنَّا قَبْلُ فِي أَهْلِنا مُشْفِقِينَ (٢٦)
قوله تعالى: إِنَّا كُنَّا قَبْلُ فِي أَهْلِنا مُشْفِقِينَ [٢٦] قال: أي خائفين وجلين من سوء القضاء وشماتة الأعداء.

<span data-type="title" id=toc-503>[سورة الطور (٥٢) : الآيات ٤٨ الى ٤٩]</span>
وَاصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنا وَسَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ (٤٨) وَمِنَ اللَّيْلِ فَسَبِّحْهُ وَإِدْبارَ النُّجُومِ (٤٩)
قوله تعالى: وَاصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنا [٤٨] يعني ما ظهر على صفاتك من فعل وقدرة يتولى جملتك بالرعاية والكلاية والرضى والمحبة والحراسة من الأعداء.
قوله تعالى: وَسَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ [٤٨] قال: يعني صل المكتوبة بالإخلاص لربك حين تقوم إليها.
قوله تعالى: وَمِنَ اللَّيْلِ فَسَبِّحْهُ وَإِدْبارَ النُّجُومِ [٤٩] قال: يعني لا تغفل عن ذكر من لا يغفل عن برك وحفظك في كل الأوقات صباحاً ومساءً.
والله سبحانه وتعالى أعلم.
_________
(١) صحيح مسلم رقم ١٦٣.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (153,'<span data-type="title" id=toc-504>السورة التي يذكر فيها النجم</span>

<span data-type="title" id=toc-505>[سورة النجم (٥٣) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَالنَّجْمِ إِذا هَوى (١) مَا ضَلَّ صاحِبُكُمْ وَما غَوى (٢) وَما يَنْطِقُ عَنِ الْهَوى (٣)
قوله تعالى: وَالنَّجْمِ إِذا هَوى [١] يعني ومحمد صلّى الله عليه وسلّم إذا رجع من السماء.
قوله تعالى: مَا ضَلَّ صاحِبُكُمْ وَما غَوى [٢] قال: أي ما ضل عن حقيقة التوحيد قط، ولا اتبع الشيطان بحال.
قوله تعالى: وَما يَنْطِقُ عَنِ الْهَوى [٣] يعني لا ينطق بالباطل قط. قال كان نطقه حجة من حجج الله تعالى، فكيف يكون للهوى والشيطان عليه اعتراض؟

<span data-type="title" id=toc-506>[سورة النجم (٥٣) : آية ٨]</span>
ثُمَّ دَنا فَتَدَلَّى (٨)
قوله تعالى: ثُمَّ دَنا فَتَدَلَّى [٨] قال: يعني قرباً بعد قرب.

<span data-type="title" id=toc-507>[سورة النجم (٥٣) : الآيات ١١ الى ١٤]</span>
مَا كَذَبَ الْفُؤادُ مَا رَأى (١١) أَفَتُمارُونَهُ عَلى مَا يَرى (١٢) وَلَقَدْ رَآهُ نَزْلَةً أُخْرى (١٣) عِنْدَ سِدْرَةِ الْمُنْتَهى (١٤)
قوله تعالى: مَا كَذَبَ الْفُؤادُ مَا رَأى [١١] من مشاهدة ربه ببصر قلبه كفاحاً.
قوله تعالى: أَفَتُمارُونَهُ عَلى مَا يَرى [١٢] منا وبنا وما يرى منا بنا أفضل مما يراه به.
قوله تعالى: وَلَقَدْ رَآهُ نَزْلَةً أُخْرى [١٣] قال: يعني في الابتداء حين خلقه الله سبحانه وتعالى. ويقال نوراً في عامود النور قبل بدء الخلق بألف ألف عام بطبائع الإيمان مكاشفة الغيب بالغيب قام بالعبودية بين يديه: عِنْدَ سِدْرَةِ الْمُنْتَهى [١٤] وهي شجرة ينتهي إليها علم كل أحد.

<span data-type="title" id=toc-508>[سورة النجم (٥٣) : الآيات ١٦ الى ١٨]</span>
إِذْ يَغْشَى السِّدْرَةَ مَا يَغْشى (١٦) مَا زاغَ الْبَصَرُ وَما طَغى (١٧) لَقَدْ رَأى مِنْ آياتِ رَبِّهِ الْكُبْرى (١٨)
إِذْ يَغْشَى السِّدْرَةَ مَا يَغْشى [١٦] السدرة من نور محمد صلّى الله عليه وسلّم في عبادته، كأمثال فراش من ذهب، ويجريها الحق إليه من بدائع أسراره، كل ذلك ليزيده ثباتاً لما يرد عليه من الموارد.
مَا زاغَ الْبَصَرُ وَما طَغى [١٧] قال: ما مال إلى شواهد نفسه ولا إلى مشاهدتها، وإنما كان مشاهداً بكليته ربه تعالى، شاهداً ما يظهر عليه من الصفات التي أوجبت الثبات في ذلك المحل.
لَقَدْ رَأى مِنْ آياتِ رَبِّهِ الْكُبْرى [١٨] يعني ما يبدي من صفاته من آياته رآها، ولم يذهب بذلك عن مشهوده، ولم يفارق مجاورة معبوده، وما زاده إلا محبة وشوقاً وقوة، أعطاه الله قوة احتمال التجلي والأنوار العظيمة، وكان ذلك تفضيلاً له على غيره من الأنبياء.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (154,'ألا ترى أن موسى صعق عند التجلي، ففي الضعف جابه النبي صلى الله عليه وسلّم في مشاهدته كفاحاً ببصر قلبه، فثبت لقوة حاله وعلو مقامه ودرجته.

<span data-type="title" id=toc-509>[سورة النجم (٥٣) : آية ٤٠]</span>
وَأَنَّ سَعْيَهُ سَوْفَ يُرى (٤٠)
قوله تعالى: وَأَنَّ سَعْيَهُ سَوْفَ يُرى [٤٠] قال: أي سوف يرى سعيه ويعلم أنه لا يصلح للحق ويعلم الذي يستحقه سعيه، وأنه لو لم يلحقه فضل الله لهلك سعيه.

<span data-type="title" id=toc-510>[سورة النجم (٥٣) : الآيات ٤٣ الى ٤٤]</span>
وَأَنَّهُ هُوَ أَضْحَكَ وَأَبْكى (٤٣) وَأَنَّهُ هُوَ أَماتَ وَأَحْيا (٤٤)
قوله تعالى: وَأَنَّهُ هُوَ أَضْحَكَ وَأَبْكى [٤٣] قال: يعني أضحك المطيع بالرحمة، وأهلك العاصي بالسخط، وأضحك قلوب العارفين بنور معرفته، وأبكى قلوب أعدائه بظلمات سخطه «١» .
وَأَنَّهُ هُوَ أَماتَ وَأَحْيا [٤٤] قال: أمات قلوب الأعداء بالكفر والظلمة، وأحيا قلوب الأولياء بالإيمان وأنوار المعرفة.

<span data-type="title" id=toc-511>[سورة النجم (٥٣) : آية ٤٨]</span>
وَأَنَّهُ هُوَ أَغْنى وَأَقْنى (٤٨)
وَأَنَّهُ هُوَ أَغْنى وَأَقْنى [٤٨] قال: ظاهرها متاع الدنيا، وباطنها أغنى بالطاعة وأفقر بالمعصية. وقال ابن عيينة: أغنى وأقنى أي أقنع وأرضى.
والله سبحانه وتعالى أعلم بالصواب.
_________
(١) في تفسير القرطبي ١٧/ ١١٧ ورد قول التستري في تفسير الآية المذكورة: (أضحك الله المطيعين بالرحمة، وأبكى العاصين بالسخط) .',NULL,'157',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (155,'<span data-type="title" id=toc-512>السورة التي يذكر فيها القمر</span>

<span data-type="title" id=toc-513>[سورة القمر (٥٤) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
اقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ (١)
قوله تعالى: اقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ [١] على عهد رسول الله صلّى الله عليه وسلّم فلقتين، حتى ذهبت فلقة وراء جبل حراء، وهي أول علامة من علامات الساعة. وحكي عن أبي عبد الرحمن السلمي «١» قال: كنت مع أبي بالمدائن، وكانت الجمعة، فذهب بي إلى الجمعة وهو آخذ بيدي، فقام حذيفة بن اليمان على المنبر، فحمد الله وأثنى عليه، ثم قال: اقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ [١] ، ألا وإن الساعة قد اقتربت، وإن القمر قد انشق، ألا وإن الدنيا قد أدبرت، ألا وإن المضمار اليوم، والسباق غداً. فلما خرجنا قلت: يا أبت، غداً يستبق الناس. قال: يا بني السباق غداً. [فقلت لأبي: أيستبق الناس غداً؟ قال: يا بني] «٢» إنك لجاهل، إنَّما يقول من عمل اليوم سبق في الآخرة «٣» .

<span data-type="title" id=toc-514>[سورة القمر (٥٤) : آية ١٧]</span>
وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ (١٧)
قوله تعالى: وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ [١٧، ٣٢، ٣٩] أي هوّنا القرآن للذكر، ولولا ذلك لما أطاقت الألسنة أن تتكلم به، فهل من مدكر لهذه النعمة.

<span data-type="title" id=toc-515>[سورة القمر (٥٤) : الآيات ٥٢ الى ٥٣]</span>
وَكُلُّ شَيْءٍ فَعَلُوهُ فِي الزُّبُرِ (٥٢) وَكُلُّ صَغِيرٍ وَكَبِيرٍ مُسْتَطَرٌ (٥٣)
قوله تعالى: وَكُلُّ شَيْءٍ فَعَلُوهُ فِي الزُّبُرِ [٥٢] قال: يعني في الكتب التي تكتبها الحفظة.
وَكُلُّ صَغِيرٍ وَكَبِيرٍ مُسْتَطَرٌ [٥٣] أي مكتوب في الكتاب، فيعرض عليهم يوم القيامة بين يدي الله تعالى. وقد حكي عن أبي حازم «٤» أنه قال: ويحك يا أعرج، ينادي يوم القيامة: يا أهل خطيئة كذا فتقوم معهم، ثم ينادى: يا أهل خطيئة كذا فتقوم معهم، وأراك يا أعرج تقوم مع أهل كل خطيئة «٥» ، والله سبحانه وتعالى أعلم.
_________
(١) أبو عبد الرحمن السلمي: محمد بن الحسين بن محمد بن موسى الأزدي السلمي (٣٢٥- ٤١٢ هـ) :
شيخ الصوفية وصاحب تاريخهم وطبقاتهم وتفسيرهم. بلغت تصانيفه مائة أو أكثر. (الأعلام ٦/ ٩٩) .
(٢) ما بين القوسين إضافة من المستدرك على الصحيحين ٤/ ٦٥١ (رقم ٨٨٠٠) .
(٣) المستدرك على الصحيحين ٤/ ٦٥١ (رقم ٨٨٠٠) ومصنف ابن أبي شيبة ٧/ ١٣٩ والحلية ١/ ٢٨١ وتاريخ بغداد ١/ ٢٠٢.
(٤) أبو حازم: سلمة بن دينار المخزومي، ويقال له الأعرج ( ... - ١٤٠ هـ) : عالم المدينة وقاضيها وشيخها.
فارسي الأصل. كان زاهدا عابدا. (الحلية ٣/ ٢٢٩) .
(٥) الحلية ٣/ ٢٣٠- ٢٣١ وصفوة الصفوة ٢/ ١٦٤.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (156,'<span data-type="title" id=toc-516>السورة التي يذكر فيها الرحمن</span>

<span data-type="title" id=toc-517>[سورة الرحمن (٥٥) : آية ٤]</span>
عَلَّمَهُ الْبَيانَ (٤)
قوله تعالى: عَلَّمَهُ الْبَيانَ [٤] قال: يعني علّمه الكلام الذي هو من نفس الروح وفهم العقل وفطنة القلب وذهن الخلق وعلم نفس الطبع، ألهم الله ذلك آدم عليه السلام وبين ذلك.

<span data-type="title" id=toc-518>[سورة الرحمن (٥٥) : آية ٧]</span>
وَالسَّماءَ رَفَعَها وَوَضَعَ الْمِيزانَ (٧)
قوله تعالى: وَوَضَعَ الْمِيزانَ [٧] قال: باطنها الأمر والنهي على الجوارح.

<span data-type="title" id=toc-519>[سورة الرحمن (٥٥) : آية ١٧]</span>
رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِ (١٧)
قوله تعالى: رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِ [١٧] قال: باطنها مشرق القلب ومغربه ومشرق اللسان ومغربه، ومشرق توحيده ومغربه مشاهدته. وقال تعالى: بِرَبِّ الْمَشارِقِ وَالْمَغارِبِ [المعارج: ٤٠] أي مشارق الجوارح بالإخلاص، ومغاربها بالطاعة للناس ظاهراً وباطنا.

<span data-type="title" id=toc-520>[سورة الرحمن (٥٥) : الآيات ١٩ الى ٢٠]</span>
مَرَجَ الْبَحْرَيْنِ يَلْتَقِيانِ (١٩) بَيْنَهُما بَرْزَخٌ لاَّ يَبْغِيانِ (٢٠)
قوله تعالى: مَرَجَ الْبَحْرَيْنِ يَلْتَقِيانِ [١٩] قال: أحد البحرين القلب، فيه أنواع الجواهر:
جوهر الإيمان وجوهر المعرفة وجوهر التوحيد وجوهر الرضى وجوهر المحبة وجوهر الشوق وجوهر الحزن وجوهر الفقر وغيرها والبحر الآخر النفس.
قوله تعالى: بَيْنَهُما بَرْزَخٌ لاَّ يَبْغِيانِ [٢٠] وهو العصمة والتوفيق.

<span data-type="title" id=toc-521>[سورة الرحمن (٥٥) : آية ٤٦]</span>
وَلِمَنْ خافَ مَقامَ رَبِّهِ جَنَّتانِ (٤٦)
قوله تعالى: وَلِمَنْ خافَ مَقامَ رَبِّهِ جَنَّتانِ [٤٦] قال لبيد: هَمَّ بمعصية، ثم ذكر مقامه بين يدي الله تعالى يوم الحساب، فانتهى عنها «١» . ولقد بلغني أن شاباً في خلافة عمر رضي الله عنه كان له جمال ومنظر، وكان عمر رضي الله عنه يعجبه الشاب ويتفرس فيه الخير، فاجتاز الفتى بامرأة فأعجب بها، فلما أراد أن يهمّ بالفاحشة نزلت عليه العصمة، فخرّ لوجهه مغشياً، فحملته المرأة إلى منزله، وكان له أب شيخ كبير، إذا أمسى جلس على الباب ينتظره، فلما رآه الشيخ غشي عليه، فلما أفاق سأله عن حاله، فقصّ عليه، ثم صاح صيحة فخرّ ميتاً، فلما دفن وقف وقرأ عمر رضي الله عنه على قبره: وَلِمَنْ خافَ مَقامَ رَبِّهِ جَنَّتانِ [٤٦] فناداه من القبر: إن الله أعطانيهما وزادني معهما ثالثة «٢» .

<span data-type="title" id=toc-522>[سورة الرحمن (٥٥) : آية ٥٦]</span>
فِيهِنَّ قاصِراتُ الطَّرْفِ لَمْ يَطْمِثْهُنَّ إِنْسٌ قَبْلَهُمْ وَلا جَانٌّ (٥٦)
قوله تعالى: فِيهِنَّ قاصِراتُ الطَّرْفِ [٥٦] قال: أي غاضات الأبصار عن غير أزواجهن فمن قصر طرفه في الدنيا عن الحرام والشبهات، وعن اللذات وزينتها، أعطاه الله في الجنة قاصرات الطرف، كما وعد.

<span data-type="title" id=toc-523>[سورة الرحمن (٥٥) : آية ٧٢]</span>
حُورٌ مَقْصُوراتٌ فِي الْخِيامِ (٧٢)
قوله تعالى: حُورٌ مَقْصُوراتٌ فِي الْخِيامِ [٧٢] قال: أي محبوسات في الخيام.
وقد حكى محمد بن سوار بإسناده عن أبي موسى الأشعري رضي الله عنه عن النبي صلى الله عليه وسلّم أنه قال:
«إن للمؤمن في الجنة خيمة من لؤلؤة بيضاء طولها ثلاثون ميلاً فيها أهلون لا يرى بعضهم بعضاً» «٣» ، والله سبحانه وتعالى أعلم.
_________
(١) نسب هذا القول إلى مجاهد في كتاب الورع ص ١١٥.
(٢) ورد مثل هذا الخبر في شعب الإيمان ١/ ٤٦٨ (رقم ٧٣٦) .
(٣) صحيح مسلم رقم ٢٨٣٨ باب في صفة خيام الجنة، وصحيح البخاري برقم ٤٥٩٨.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (157,'<span data-type="title" id=toc-524>السورة التي يذكر فيها الواقعة</span>

<span data-type="title" id=toc-525>[سورة الواقعة (٥٦) : آية ٣]</span>
خافِضَةٌ رافِعَةٌ (٣)
قوله تعالى: خافِضَةٌ رافِعَةٌ [٣] قال: يعني القيامة تخفض أقواماً بالدعاوى، وترفع أقواماً بالحقائق.

<span data-type="title" id=toc-526>[سورة الواقعة (٥٦) : الآيات ٧ الى ١٠]</span>
وَكُنْتُمْ أَزْواجاً ثَلاثَةً (٧) فَأَصْحابُ الْمَيْمَنَةِ مَآ أَصْحابُ الْمَيْمَنَةِ (٨) وَأَصْحابُ الْمَشْئَمَةِ ما أَصْحابُ الْمَشْئَمَةِ (٩) وَالسَّابِقُونَ السَّابِقُونَ (١٠)
قوله تعالى: وَكُنْتُمْ أَزْواجاً ثَلاثَةً [٧] قال: يعني فرقاً ثلاثة. فَأَصْحابُ الْمَيْمَنَةِ مَآ أَصْحابُ الْمَيْمَنَةِ [٨] يعني الذين يعطون الكتاب بأيمانهم. وَأَصْحابُ الْمَشْئَمَةِ ما أَصْحابُ الْمَشْئَمَةِ [٩] يعني الذين يعطون الكتاب بشمائلهم. وَالسَّابِقُونَ السَّابِقُونَ [١٠] قال هم الذين سبق لهم من الله الاختيار والولاية قبل كونهم. الْمُقَرَّبُونَ [١١] في منازل القرب وروح الأنس، وهم الذين سبقوا في الدنيا، فسبق الأنبياء إلى الإيمان بالله، وسبق الصديقون والشهداء من الصحابة وغيرهم إلى الإيمان بالأنبياء.

<span data-type="title" id=toc-527>[سورة الواقعة (٥٦) : الآيات ٣٩ الى ٤٠]</span>
ثُلَّةٌ مِنَ الْأَوَّلِينَ (٣٩) وَثُلَّةٌ مِنَ الْآخِرِينَ (٤٠)
قوله تعالى: ثُلَّةٌ مِنَ الْأَوَّلِينَ [١٣، ٣٩] قال: يعني فرقة من الأولين وهم أهل المعرفة.
وَثُلَّةٌ مِنَ الْآخِرِينَ [٤٠] وهم الذين آمنوا بمحمد صلّى الله عليه وسلّم وبجميع الرسل والكتب.
قوله تعالى: لاَ يَسْمَعُونَ فِيها لَغْواً وَلا تَأْثِيماً [٢٥] قال: ما ذاك بمشهد لغو ولا مكان إثم، لأنه محلٌ قُدِّس بالأنوار للمقدسين من العباد، وقد ظهر منهم وعليهم ما يصلح لذلك المقام.

<span data-type="title" id=toc-528>[سورة الواقعة (٥٦) : آية ٨٣]</span>
فَلَوْلا إِذا بَلَغَتِ الْحُلْقُومَ (٨٣)
قوله تعالى: فَلَوْلا إِذا بَلَغَتِ الْحُلْقُومَ [٨٣] يعني نفسه بلغت الحلقوم، وهو متحير لا يدري ما يصير أمره، كما حكي عن مسروق بن الأجدع «١» أنه بكى حين حضرته الوفاة، فاشتد بكاؤه، فقيل له: ما يبكيك؟ قال: وكيف لا أبكي، وإنما هي ساعة، ثم لا أدري إلى أين يسلك بي «٢» .

<span data-type="title" id=toc-529>[سورة الواقعة (٥٦) : الآيات ٨٨ الى ٩٠]</span>
فَأَمَّا إِنْ كانَ مِنَ الْمُقَرَّبِينَ (٨٨) فَرَوْحٌ وَرَيْحانٌ وَجَنَّةُ نَعِيمٍ (٨٩) وَأَمَّا إِنْ كانَ مِنْ أَصْحابِ الْيَمِينِ (٩٠)
فَأَمَّا إِنْ كانَ مِنَ الْمُقَرَّبِينَ [٨٨] يعني الأنبياء والشهداء والصالحين بعضهم أفضل درجة من بعض، منازلهم في القرب على مقدار قرب قلوبهم من المعرفة بالله تعالى. فَرَوْحٌ وَرَيْحانٌ [٨٩] في الجنة. وقال أبو العالية «٣» في هذه الآية: لم يكن الرجل منهم يفارق الدنيا حتى يؤتى بغصن من ريحان الجنة فيشمه ثم تفيض روحه فيها «٤» . وَأَمَّا إِنْ كانَ مِنْ أَصْحابِ الْيَمِينِ [٩٠] قال: يعني الموحدين العاقبة لهم لأنهم أمناء الله قد أدوا الأمانة، يعني أمره ونهيه والتابعين بإحسان لم يحدثوا شيئاً من المعاصي والزلات، فأمنوا الخوف والهول الذي ينال.
والله سبحانه وتعالى أعلم.
_________
(١) مسروق بن الأجدع بن مالك بن أمية الهمداني الوادعي الكوفي (١- ٦٣ هـ) : تابعي، ثقة، من عباد أهل الكوفة. شلت يده يوم القادسية. (تهذيب التهذيب ١٠/ ١٠٠) .
(٢) صفوة الصفوة ٣/ ٢٦. [.....]
(٣) أبو العالية: رفيع بن مهران الرياحي البصري ( ... - ٩٠ هـ) : أدرك الجاهلية، وأسلم بعد وفاة النبي صلى الله عليه وسلّم بسنتين.
تابعي ثقة، من كبار التابعين. لم يكن أحد بعد الصحابة أعلم بالقراءة منه. (تهذيب التهذيب ٣/ ٢٤٦) .
(٤) تفسير ابن كثير ٤/ ٣٢٢.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (158,'<span data-type="title" id=toc-530>السورة التي يذكر فيها الحديد</span>

<span data-type="title" id=toc-531>[سورة الحديد (٥٧) : الآيات ٣ الى ٤]</span>
هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْباطِنُ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ (٣) هُوَ الَّذِي خَلَقَ السَّماواتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَما يَخْرُجُ مِنْها وَما يَنْزِلُ مِنَ السَّماءِ وَما يَعْرُجُ فِيها وَهُوَ مَعَكُمْ أَيْنَ ما كُنْتُمْ وَاللَّهُ بِما تَعْمَلُونَ بَصِيرٌ (٤)
قوله تعالى: هُوَ الْأَوَّلُ وَالْآخِرُ [٣] قال: اسم الله الأعظم مكنى عنه في ست آيات من أول سورة الحديد من قوله: هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْباطِنُ [٣] وليس المعنى في الأسماء إلا المعرفة بالمسمى، والمعنى في العبادة إلا المعرفة في العبودية. ومعنى الظاهر ظاهر العلو والقدرة والقهر، والباطن الذي عرف ما في باطن القلوب من الضمائر والحركات.
قوله تعالى: يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ [٤] قال: باطن الآية الأرض نفس الطبع، فيعلم ما يدخل القلب الذي فيها له من الصلاح والفساد.
وَما يَخْرُجُ مِنْها [٤] من فنون الطاعات، فتبين آثارها وأنوارها على الجوارح.
وَما يَنْزِلُ مِنَ السَّماءِ [٤] عليها من آداب الله تعالى إياه. وَما يَعْرُجُ فِيها [٤] إلى الله من الروائح الطيبة والذكر.

<span data-type="title" id=toc-532>[سورة الحديد (٥٧) : الآيات ٦ الى ٧]</span>
يُولِجُ اللَّيْلَ فِي النَّهارِ وَيُولِجُ النَّهارَ فِي اللَّيْلِ وَهُوَ عَلِيمٌ بِذاتِ الصُّدُورِ (٦) آمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ فَالَّذِينَ آمَنُوا مِنْكُمْ وَأَنْفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ (٧)
قوله تعالى: وَيُولِجُ النَّهارَ فِي اللَّيْلِ [٦] قال: باطنها الليل نفس الطبع والنهار نفس الروح، فإذا أراد الله تعالى بعبده خيراً ألّف بين طبعه ونفس روحه على إدامة الذكر، فأظهر ذلك على مقابلة أنوار الخشوع.
قوله تعالى: آمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ [٧] قال: يعني ورثكم من آبائكم وملككم، فأنفقوا عيش أنفسكم الطبيعية من الدنيا في طاعته وطاعة رسوله.
فَالَّذِينَ آمَنُوا مِنْكُمْ وَأَنْفَقُوا [٧] أعمارهم في الوجوه التي أمرهم الله بالإنفاق فيها.
لَهُمْ أَجْرٌ كَبِيرٌ [٧] وهو البقاء مع الباقي في جنته ورضاه.

<span data-type="title" id=toc-533>[سورة الحديد (٥٧) : الآيات ١١ الى ١٣]</span>
مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضاً حَسَناً فَيُضاعِفَهُ لَهُ وَلَهُ أَجْرٌ كَرِيمٌ (١١) يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِناتِ يَسْعى نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمانِهِمْ بُشْراكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ (١٢) يَوْمَ يَقُولُ الْمُنافِقُونَ وَالْمُنافِقاتُ لِلَّذِينَ آمَنُوا انْظُرُونا نَقْتَبِسْ مِنْ نُورِكُمْ قِيلَ ارْجِعُوا وَراءَكُمْ فَالْتَمِسُوا نُوراً فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بابٌ باطِنُهُ فِيهِ الرَّحْمَةُ وَظاهِرُهُ مِنْ قِبَلِهِ الْعَذابُ (١٣)
قوله تعالى: مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضاً حَسَناً [١١] قال: أعطى الله عباده فضلاً، ثم سألهم قرضاً حسناً، والقرض الحسن المشاهدة فيه، كما قال النبي صلى الله عليه وسلّم: «اعبد الله كأنك تراه» «١» .
وحكي عن أبي حازم أنه قال: إن بضاعة الآخرة كاسدة، فاستكثروا من أوان كسادها، فإذا جاء يوم نفاقها لم تقدروا منها على قليل ولا على كثير «٢» .
_________
(١) صحيح البخاري: الإيمان، ٥٠ وصحيح مسلم: الإيمان، ٨ وسنن أبي داود رقم ٤٦٩٥.
(٢) الحلية ٣/ ٢٤٢ وصفوة الصفوة ٢/ ١٦٣.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (159,'قوله تعالى: يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِناتِ يَسْعى نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمانِهِمْ [١٢] قال: نور المؤمن يسعى بين يديه، له هيبة في قلوب الموافقين والمخالفين، يعظمه الموافق ويعظم شأنه، ويهابه المخالف ويخافه، وهو النور الذي جعله الله تعالى لأوليائه، ولا يظهر ذلك النور لأحد إلا إن انقاد له وخضع، وهو من نور الإيمان، ثم وصف المنافقين أنهم يقولون لهم: انْظُرُونا نَقْتَبِسْ مِنْ نُورِكُمْ [١٣] فنمضي معكم على الصراط فإنا في الظلمة، فتقول لهم الملائكة: ارْجِعُوا وَراءَكُمْ فَالْتَمِسُوا نُوراً [١٣] بعقولكم التي كنتم تدبرون بها أموركم في الدنيا، فيرجعون إلى ورائهم، فيضرب الله بين أنفسهم وبين عقولهم سوراً وقد ستر الخيرة، فلا يصلون إلى طريق هدى، حتى إذا انتهوا في السير على الصراط سقطوا في جهنم خالدين فيها.

<span data-type="title" id=toc-534>[سورة الحديد (٥٧) : الآيات ١٥ الى ١٦]</span>
فَالْيَوْمَ لاَ يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَلا مِنَ الَّذِينَ كَفَرُوا مَأْواكُمُ النَّارُ هِيَ مَوْلاكُمْ وَبِئْسَ الْمَصِيرُ (١٥) أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَما نَزَلَ مِنَ الْحَقِّ وَلا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلُ فَطالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ وَكَثِيرٌ مِنْهُمْ فاسِقُونَ (١٦)
قوله تعالى: فَالْيَوْمَ لاَ يُؤْخَذُ مِنْكُمْ فِدْيَةٌ [١٥] يعني لا يؤخذ منكم فداء عن أنفسكم.
قال ابن سالم: خدمت سهل بن عبد الله ستين سنة، فما تغير في شيء من الذكر أو غيره، فلما كان آخر يوم من عمره قرأ رجل بين يديه هذه الآية: فَالْيَوْمَ لاَ يُؤْخَذُ مِنْكُمْ فِدْيَةٌ [١٥] فرأيته ارتعد واضطرب حتى كاد يسقط، فلما رجع إلى حال صحوه سألته عن ذلك وقلت: لم يكن عهدي بك هذا. فقال: نعم يا حبيبي قد ضعفت. فقلت: ما الذي يوجب قوة الحال؟ فقال: لا يرد عليه وارد إلا هو يبتلعه بقوته، فمن كان كذلك لا تغيره الواردات، وإن كانت قوية. وكان يقول: حالي في الصلاة وقبل الدخول فيها سواء. وذلك أنه كان يراعي قلبه، ويراقب الله تعالى بسره قبل دخوله، فيقوم إلى الصلاة بحضور قلبه وجمع همته.
قوله تعالى: أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ [١٦] قال: ألم يحن لهم أوان الخشوع عند سماع الذكر، فيشاهدوا الوعد والوعيد مشاهدة الغيب.
قوله تعالى: فَقَسَتْ قُلُوبُهُمْ [١٦] قال يعني باتباع الشهوة.

<span data-type="title" id=toc-535>[سورة الحديد (٥٧) : آية ٢٠]</span>
اعْلَمُوا أَنَّمَا الْحَياةُ الدُّنْيا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفاخُرٌ بَيْنَكُمْ وَتَكاثُرٌ فِي الْأَمْوالِ وَالْأَوْلادِ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَباتُهُ ثُمَّ يَهِيجُ فَتَراهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطاماً وَفِي الْآخِرَةِ عَذابٌ شَدِيدٌ وَمَغْفِرَةٌ مِنَ اللَّهِ وَرِضْوانٌ وَمَا الْحَياةُ الدُّنْيا إِلاَّ مَتاعُ الْغُرُورِ (٢٠)
قوله تعالى: أَنَّمَا الْحَياةُ الدُّنْيا لَعِبٌ وَلَهْوٌ [٢٠] قال: الدنيا نفس نائمة، والآخرة نفس يقظانة. قيل: فما النجاة منها؟ قال: أصل ذلك العلم، ثم ثمرته مخالفة الهوى في اجتناب المناهي، ثم مكابدة النفس على أداء الأوامر على الطهارة من الأدناس، فيورث السهولة في التعبد والحلول بعده في مقامات العابدين، ثم يذيقه الله ما أذاق أولياءه وأصفياءه وهي درجة المذاق.
قال: وذكر لنا أن إبراهيم خليل الرحمن عليه الصلاة والسلام أصابه يوماً عطش شديد في مفازة يوم شديد الحر، فنظر إلى حبشي يرعى الإبل فقال: هل عندك ماء؟ فقال: يا إبراهيم أيما أحب إليك الماء أو اللبن؟ فقال: الماء. قال: فضرب بقدمه على صخرة فنبع الماء، فتعجب',NULL,'162',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (160,'إبراهيم عليه الصلاة والسلام، فأوحى الله إلى إبراهيم: لو سألني هذا الحبشي أن أزيل السماوات والأرض لأزلتهما. فقال: ولم ذلك يا رب؟ قال: لأنه ليس يريد من الدنيا والآخرة غيري.
وقال عامر بن عبد القيس: وجدت الدنيا أربع خصال فأما خصلتان فقد طابت نفسي عنهما: النساء وجمع الماء، وأما الخصلتان فلا بد منهما وأنا مصرفهما ما استطعت: النوم والطعام «١» .

<span data-type="title" id=toc-536>[سورة الحديد (٥٧) : آية ٢٣]</span>
لِكَيْلا تَأْسَوْا عَلى مَا فاتَكُمْ وَلا تَفْرَحُوا بِما آتاكُمْ وَاللَّهُ لا يُحِبُّ كُلَّ مُخْتالٍ فَخُورٍ (٢٣)
قوله تعالى: لِكَيْلا تَأْسَوْا عَلى مَا فاتَكُمْ [٢٣] قال: في هذه الآية دليل على الرضا في الشدة والرخاء.

<span data-type="title" id=toc-537>[سورة الحديد (٥٧) : الآيات ٢٧ الى ٢٨]</span>
ثُمَّ قَفَّيْنا عَلى آثارِهِمْ بِرُسُلِنا وَقَفَّيْنا بِعِيسَى ابْنِ مَرْيَمَ وَآتَيْناهُ الْإِنْجِيلَ وَجَعَلْنا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبانِيَّةً ابْتَدَعُوها ما كَتَبْناها عَلَيْهِمْ إِلاَّ ابْتِغاءَ رِضْوانِ اللَّهِ فَما رَعَوْها حَقَّ رِعايَتِها فَآتَيْنَا الَّذِينَ آمَنُوا مِنْهُمْ أَجْرَهُمْ وَكَثِيرٌ مِنْهُمْ فاسِقُونَ (٢٧) يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَآمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ وَيَجْعَلْ لَكُمْ نُوراً تَمْشُونَ بِهِ وَيَغْفِرْ لَكُمْ وَاللَّهُ غَفُورٌ رَحِيمٌ (٢٨)
قوله عزَّ وجلَّ: وَرَهْبانِيَّةً ابْتَدَعُوها [٢٧] قال: الرهبانية مأخوذة من الرهبة، وهو الخوف، ومعناه ملازمة الخوف من غير طمع. مَا كَتَبْناها عَلَيْهِمْ [٢٧] أي ما تعبدناهم بذلك.
قوله عزَّ وجلَّ: يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ [٢٨] قال: يعني الرحمة وعين الرحمة، فالسر سر المعرفة، والعين عين الطاعة لله ولرسوله.
والله سبحانه وتعالى أعلم.
_________
(١) الحلية ٢/ ٩٠- ٩١ وكتاب الزهد الكبير ٢/ ٦٣- ٦٤ وكتاب الزهد لابن أبي عاصم ص ٢٢٣- ٢٢٤ وشعب الإيمان ٥/ ٣٩ والطبقات الكبرى ٧/ ١١١.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (161,'<span data-type="title" id=toc-538>السورة التي يذكر فيها المجادلة</span>

<span data-type="title" id=toc-539>[سورة المجادلة (٥٨) : آية ١٠]</span>
إِنَّمَا النَّجْوى مِنَ الشَّيْطانِ لِيَحْزُنَ الَّذِينَ آمَنُوا وَلَيْسَ بِضارِّهِمْ شَيْئاً إِلاَّ بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ (١٠)
قوله تعالى: إِنَّمَا النَّجْوى مِنَ الشَّيْطانِ [١٠] قال: النجوى إلقاء من العدو إلى نفس الطبع كما قال النبي صلى الله عليه وسلّم: «للملك لمة وللشيطان لمة» «١» .
قوله عزَّ وجلَّ: وَتَناجَوْا بِالْبِرِّ وَالتَّقْوى [٩] قال: بذكر الله وقراءة القرآن والأمر بالمعروف والنهي عن المنكر.

<span data-type="title" id=toc-540>[سورة المجادلة (٥٨) : آية ٢٢]</span>
لاَّ تَجِدُ قَوْماً يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ يُوادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ وَلَوْ كانُوا آباءَهُمْ أَوْ أَبْناءَهُمْ أَوْ إِخْوانَهُمْ أَوْ عَشِيرَتَهُمْ أُولئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمانَ وَأَيَّدَهُمْ بِرُوحٍ مِنْهُ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ أُولئِكَ حِزْبُ اللَّهِ أَلا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ (٢٢)
قوله تعالى: لاَّ تَجِدُ قَوْماً يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ يُوادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ [٢٢] قال: كل من صح إيمانه فإنه لا يأنس بمبتدع ويجابهه، ولا يؤاكله ولا يشاربه ولا يصاحبه، ويظهر له من نفسه العداوة والبغضاء، ومن داهن مبتدعاً سلبه الله حلاوة السنن، ومن تحبب إلى مبتدع يطلب عزة في الدنيا وعرضاً، أذله الله بذلك العز، وأفقره الله بذلك الغنى، ومن ضحك إلى مبتدع نزع الله نور الإيمان من قلبه، ومن لم يصدق فليجرب.
قوله تعالى: أُولئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمانَ وَأَيَّدَهُمْ بِرُوحٍ مِنْهُ [٢٢] قال: كتب الله الإيمان في قلوب أوليائه سطوراً، فالسطر الأول التوحيد، والثاني المعرفة، والثالث الصدق، والرابع الاستقامة، والخامس الصدق، والسادس الاعتماد، والسابع التوكل. وهذه الكتابة هي فعل الله لا فعل العبد، وفعل العبد في الإيمان ظاهر الإسلام، وما يبدو منه ظاهراً وما كان منه باطناً فهو فعل الله تعالى. وقال أيضاً: الكتابة في القلب موهبة الإيمان التي وهبها الله منهم قبل أن خلقهم من الأصلاب والأرحام، ثم أبدى بصراً من النور في القلب، ثم كشف الغطاء عنه حتى أبصروا ببركة الكتابة ونور الإيمان المغيبات. وقال: حياة الروح بالذكر، وحياة الذكر بالذاكر وحياة الذاكر بالمذكور، رضي الله عنهم بإخلاصهم له في أعمالهم، ورضوا عنه بجزيل ثوابه لهم على أعمالهم. أُولئِكَ حِزْبُ اللَّهِ [٢٢] الحزب الشيعة، وهم الأبدال، وأرفع منهم الصديقون. أَلا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ [٢٢] يعني هم الوارثون أسرار علومهم المشرقون على معاني ابتدائهم وانتهائهم.
والله سبحانه وتعالى أعلم.
_________
(١) المعجم الكبير ٩/ ١٠١ ومسند البزار ٥/ ٣٩٤ وصفوة الصفوة ١/ ٤١٣.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (162,'<span data-type="title" id=toc-541>السورة التي يذكر فيها الحشر</span>

<span data-type="title" id=toc-542>[سورة الحشر (٥٩) : آية ٢]</span>
هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتابِ مِنْ دِيارِهِمْ لِأَوَّلِ الْحَشْرِ ما ظَنَنْتُمْ أَنْ يَخْرُجُوا وَظَنُّوا أَنَّهُمْ مانِعَتُهُمْ حُصُونُهُمْ مِنَ اللَّهِ فَأَتاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يا أُولِي الْأَبْصارِ (٢)
قوله تعالى: يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ [٢] قال: أي يخربون قلوبهم ويبطلون أعمالهم باتباعهم البدع وهجرانهم طريقة الاقتداء بالنبيين.
يُخْرِبُونَ بُيُوتَهُمْ [٢] أي بمجانبة المؤمنين ومشاهدتهم ومجالستهم فيحرمون بركاتهم.
فَاعْتَبِرُوا يا أُولِي الْأَبْصارِ [٢] فَإِنَّ اللَّهَ يُضِلُّ مَنْ يَشاءُ [فاطر: ٨] بالخذلان وَيَهْدِي مَنْ يَشاءُ [فاطر: ٨] بالمعونة، وليس لكم من الأمر شيء.

<span data-type="title" id=toc-543>[سورة الحشر (٥٩) : آية ٧]</span>
ما أَفاءَ اللَّهُ عَلى رَسُولِهِ مِنْ أَهْلِ الْقُرى فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبى وَالْيَتامى وَالْمَساكِينِ وَابْنِ السَّبِيلِ كَيْ لا يَكُونَ دُولَةً بَيْنَ الْأَغْنِياءِ مِنْكُمْ وَما آتاكُمُ الرَّسُولُ فَخُذُوهُ وَما نَهاكُمْ عَنْهُ فَانْتَهُوا وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقابِ (٧)
قوله تعالى: وَما آتاكُمُ الرَّسُولُ فَخُذُوهُ وَما نَهاكُمْ عَنْهُ فَانْتَهُوا [٧] قال: أصول مذهبنا ثلاث: أكل الحلال، والاقتداء بالرسول صلّى الله عليه وسلّم في الأخلاق والأفعال، وإخلاص النية في جميع الأعمال «١» .
وقال: ألزموا أنفسكم ثلاثة أشياء، فإن خير الدنيا والآخرة فيها: صحبتها بالأمر والنهي بالسنة، وإقامة التوحيد فيها وهو اليقين، وعلماً فيه اتصال الروح، وصاحب هذه الثلاثة أعلم بما في بطن الأرض مما على ظهرها، ونظره في الآخرة أكثر من نظره في الدنيا، وهو في السماوات أشهر بين الملائكة منه في الأرض بين أهله وقرابته. فقيل: ما العلم الذي فيه إيصال الروح؟ قال:
علم قيام الله عليه والرضا.

<span data-type="title" id=toc-544>[سورة الحشر (٥٩) : آية ٩]</span>
وَالَّذِينَ تَبَوَّؤُا الدَّارَ وَالْإِيمانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هاجَرَ إِلَيْهِمْ وَلا يَجِدُونَ فِي صُدُورِهِمْ حاجَةً مِمَّا أُوتُوا وَيُؤْثِرُونَ عَلى أَنْفُسِهِمْ وَلَوْ كانَ بِهِمْ خَصاصَةٌ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولئِكَ هُمُ الْمُفْلِحُونَ (٩)
قوله تعالى: وَيُؤْثِرُونَ عَلى أَنْفُسِهِمْ وَلَوْ كانَ بِهِمْ خَصاصَةٌ [٩] قال: يعني مجاعة وفقراً. تقول العرب: فلان مخصوص إذا كان فقيراً، فيؤثرون رضا الله على هواهم، والإيثار شاهد الحب.
وقد حكي عن وهيب بن الورد «٢» أنه قال: يقول الله تعالى: «وعزتي وعظمتي وجلالي، ما من عبد آثر هواي على هواه إلا قللت همومه وجمعت عليه ضيعته، ونزعت الفقر من قلبه،
_________
(١) تقدم في تفسير الآية (٧١) من سورة التوبة أنه قال: (أصولنا مذهبنا ستة أشياء) وانظر أيضا طبقات الصوفية ١/ ١٧٠.
(٢) وهيب بن الورد بن أبي الورد المخزومي، بالولاء، ( ... - ١٥٣ هـ) : من العباد الحكماء. من أهل مكة.
ووفاته بها. (الحلية ٨/ ١٤٠) .',NULL,'165',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (163,'وجعلت الغنى بين عينيه، واتجرت له من وراء كل تاجر. وعزتي وجلالي، ما من عبد آثر هواه على هواي إلا كثرت همومه، وفرقت عليه ضيعته، ونزعت الغنى من قلبه، وجعلت الفقر بين عينيه، ثم لا أبالي في أي واد هلك» «١» .
قوله تعالى: وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولئِكَ هُمُ الْمُفْلِحُونَ [٩] قال: أي ومن يوق حرص نفسه وبخلها على شيء هو غير الله وغير ذكره، فأولئك هم الباقون مع الله حياة طيبة بحياة طيبة.

<span data-type="title" id=toc-545>[سورة الحشر (٥٩) : آية ١٤]</span>
لا يُقاتِلُونَكُمْ جَمِيعاً إِلاَّ فِي قُرىً مُحَصَّنَةٍ أَوْ مِنْ وَراءِ جُدُرٍ بَأْسُهُمْ بَيْنَهُمْ شَدِيدٌ تَحْسَبُهُمْ جَمِيعاً وَقُلُوبُهُمْ شَتَّى ذلِكَ بِأَنَّهُمْ قَوْمٌ لا يَعْقِلُونَ (١٤)
قوله تعالى: تَحْسَبُهُمْ جَمِيعاً وَقُلُوبُهُمْ شَتَّى [١٤] قال: أهل الحق مجتمعون، وأهل الباطل متفرقون أبداً، وإن اجتمعوا في أبدانهم وتوافقوا في الظاهر، فإن الله تعالى يقول في كتابه العزيز:
تَحْسَبُهُمْ جَمِيعاً وَقُلُوبُهُمْ شَتَّى [١٤] .

<span data-type="title" id=toc-546>[سورة الحشر (٥٩) : الآيات ١٨ الى ١٩]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَلْتَنْظُرْ نَفْسٌ ما قَدَّمَتْ لِغَدٍ وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ خَبِيرٌ بِما تَعْمَلُونَ (١٨) وَلا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنْساهُمْ أَنْفُسَهُمْ أُولئِكَ هُمُ الْفاسِقُونَ (١٩)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَلْتَنْظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍ [١٨] قال:
يسأل الله تعالى العبد عن حق نفسه، وحق العلم الذي بينه وبين ربه، وحق العقل، فمن كان له فليؤدِّ حق نفسه وحق العلم الذي بينه وبين ربه بحسن النظر لنفسه في عاقبة أمره.
وحكي عن الحسن أنه قال: إذا مات ابن آدم قالت بنو آدم: ما ترك، وقالت الملائكة: ما قدم؟.
قوله: وَلا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ [١٩] عند الذنوب فَأَنْساهُمْ [١٩] الله الاعتذار وطلب التوبة.
قال: ما من عبد أذنب ذنبا ولم يتب إلا جره ذلك الذنب إلى ذنب آخر وأنساه الذنب الأول، وما من عبد عمل حسنة إلا جرته تلك الحسنة إلى حسنة أخرى، وبصره عقله تقصيره في الحسنة الأولى، لكي يتوب من تقصيره في حسناته الماضية، وإن كانت خالية صحيحة.

<span data-type="title" id=toc-547>[سورة الحشر (٥٩) : آية ٢٢]</span>
هُوَ اللَّهُ الَّذِي لا إِلهَ إِلاَّ هُوَ عالِمُ الْغَيْبِ وَالشَّهادَةِ هُوَ الرَّحْمنُ الرَّحِيمُ (٢٢)
قوله تعالى: عالِمُ الْغَيْبِ وَالشَّهادَةِ [٢٢] قال: الغيب السر، والشهادة العلانية.
وقال تعالى أيضاً: عالِمُ الْغَيْبِ وَالشَّهادَةِ [٢٢] عالم بالدنيا والآخرة.
والله سبحانه وتعالى أعلم.
_________
(١) نوادر الأصول ٤/ ٢٥- ٢٦ وصفوة الصفوة ٢/ ٢٢٠ والحلية ٨/ ١٤٧.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (164,'<span data-type="title" id=toc-548>السورة التي يذكر فيها الممتحنة</span>

<span data-type="title" id=toc-549>[سورة الممتحنة (٦٠) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِياءَ تُلْقُونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِما جاءَكُمْ مِنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَإِيَّاكُمْ أَنْ تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِنْ كُنْتُمْ خَرَجْتُمْ جِهاداً فِي سَبِيلِي وَابْتِغاءَ مَرْضاتِي تُسِرُّونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَأَنَا أَعْلَمُ بِما أَخْفَيْتُمْ وَما أَعْلَنْتُمْ وَمَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَواءَ السَّبِيلِ (١)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِياءَ [١] قال: حذر الله تعالى المؤمنين من التولي بغير من تولاه الله ورسوله، فإن الله تعالى لم يرض منه أن يسكن إلى وليه، فكيف إلى عدوه ومن شغل قلبه بما لا يعنيه من أمر آخرته نال منه العدو، فكيف بغيره ومن طمع في الآخرة مع إرادة شيء من الدنيا حلالاً كان مخدوعاً، فكيف بالحرام ومن لم يكن فعله مخالفة أو مكابدة أو إيثاراً فهو رياء. قيل: وما معناها؟ قال: المخالفة في ترك النهي ولتركُ ذرة مما نهى الله عنه أفضل من أن تعبد الله عمر الدنيا. والمكابدة في أداء الأوامر والإيثار أن يؤثر الله تعالى على ما دونه، ففي المخالفة فقدوا أنفسهم، وفي المكابدة فقدوا أهواءهم، فصارت شهواتهم في الطاعات، وبالإيثار نالوا محبته ورضاه.

<span data-type="title" id=toc-550>[سورة الممتحنة (٦٠) : آية ٧]</span>
عَسَى اللَّهُ أَنْ يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِينَ عادَيْتُمْ مِنْهُمْ مَوَدَّةً وَاللَّهُ قَدِيرٌ وَاللَّهُ غَفُورٌ رَحِيمٌ (٧)
قوله تعالى: وَاللَّهُ غَفُورٌ رَحِيمٌ [٧] قال: غفور لذنوبكم الماضية بالتوبة، رحيم يعصمكم فيما بقي لكم من عمركم من مثل هذه المعصية.

<span data-type="title" id=toc-551>[سورة الممتحنة (٦٠) : آية ١٠]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا إِذا جاءَكُمُ الْمُؤْمِناتُ مُهاجِراتٍ فَامْتَحِنُوهُنَّ اللَّهُ أَعْلَمُ بِإِيمانِهِنَّ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِناتٍ فَلا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ لا هُنَّ حِلٌّ لَهُمْ وَلا هُمْ يَحِلُّونَ لَهُنَّ وَآتُوهُمْ ما أَنْفَقُوا وَلا جُناحَ عَلَيْكُمْ أَنْ تَنْكِحُوهُنَّ إِذا آتَيْتُمُوهُنَّ أُجُورَهُنَّ وَلا تُمْسِكُوا بِعِصَمِ الْكَوافِرِ وَسْئَلُوا ما أَنْفَقْتُمْ وَلْيَسْئَلُوا ما أَنْفَقُوا ذلِكُمْ حُكْمُ اللَّهِ يَحْكُمُ بَيْنَكُمْ وَاللَّهُ عَلِيمٌ حَكِيمٌ (١٠)
قوله تعالى: وَلا تُمْسِكُوا بِعِصَمِ الْكَوافِرِ [١٠] قال: لا توافقوا أهل البدع على شيء من أهوائهم وآرائهم، والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-552>السورة التي يذكر فيها الصف</span>

<span data-type="title" id=toc-553>[سورة الصف (٦١) : آية ٢]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ مَا لاَ تَفْعَلُونَ (٢)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ مَا لاَ تَفْعَلُونَ [٢] قال: إن الله هدد عباده على دعواهم من غير تحقيق، والدعوى أن يلزمه اليوم حق من حقوق الله براءة وتوبة من كل ذنب ارتكبه، فيقول غداً أعمل، وما من أحد ادعى إلا وقد ضيع حق الله من وجهين، ظاهر وباطن، ولا يكون المدعي خائفاً، ومن لم يكن خائفاً لم يكن آمناً، ومن لم يكن آمناً لم يكن يطلع على الجزاء. وقال: طلاب الآخرة كثيرة، والذي يتولى الله كفايته عبدان، عبد ساذج غير أنه صادق في طلبه، متوكل على الله، فيصدقه فيكفيه مولاه، ويتولى جميع أموره وعبد عالم بالله وبأيامه وأمره ونهيه، كفاه الله كل شيء من هذه الدنيا، فإذا صار إلى الآخرة ما سوى هذين لا يعبأ الله بهم، لأنهم يدعون ما ليس لهم. وقال ابن عيينة في هذه الآية: لم تقولون ما ليس الأمر فيه لكم، لا تدرون تفعلون ذلك أم لا تفعلون.

<span data-type="title" id=toc-554>[سورة الصف (٦١) : آية ٨]</span>
يُرِيدُونَ لِيُطْفِؤُا نُورَ اللَّهِ بِأَفْواهِهِمْ وَاللَّهُ مُتِمُّ نُورِهِ وَلَوْ كَرِهَ الْكافِرُونَ (٨)
قوله تعالى: يُرِيدُونَ لِيُطْفِؤُا نُورَ اللَّهِ بِأَفْواهِهِمْ [٨] يعني جحدوا ما ظهر لهم من حجة النبي صلى الله عليه وسلّم بألسنتهم، وأعرضوا عنه بنفوسهم، فقيض الله لقبوله أنفساً أوجدها على حكم السعادة، وقلوباً زينها بأنوار معرفته، وأسرار نورها بالتصديق، فبذلوا له المهج والأموال كالصدّيق والفاروق وأجلة الصحابة رضي الله عنهم.

<span data-type="title" id=toc-555>[سورة الصف (٦١) : آية ١٤]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا أَنْصارَ اللَّهِ كَما قالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوارِيِّينَ مَنْ أَنْصارِي إِلَى اللَّهِ قالَ الْحَوارِيُّونَ نَحْنُ أَنْصارُ اللَّهِ فَآمَنَتْ طائِفَةٌ مِنْ بَنِي إِسْرائِيلَ وَكَفَرَتْ طائِفَةٌ فَأَيَّدْنَا الَّذِينَ آمَنُوا عَلى عَدُوِّهِمْ فَأَصْبَحُوا ظاهِرِينَ (١٤)
قوله تعالى: كُونُوا أَنْصارَ اللَّهِ [١٤] قال: يعني بالقبول منه، والاستماع إليه بطاعته فيما يأمركم به وينهاكم عنه، والله سبحانه وتعالى أعلم.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (165,'<span data-type="title" id=toc-556>السورة التي يذكر فيها الجمعة</span>

<span data-type="title" id=toc-557>[سورة الجمعة (٦٢) : الآيات ٢ الى ٣]</span>
هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ آياتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتابَ وَالْحِكْمَةَ وَإِنْ كانُوا مِنْ قَبْلُ لَفِي ضَلالٍ مُبِينٍ (٢) وَآخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ وَهُوَ الْعَزِيزُ الْحَكِيمُ (٣)
قوله تعالى: هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولًا مِنْهُمْ [٢] قال: الأميون هم الذين صدقوا محمد صلّى الله عليه وسلّم، نسبوا إليه لاتباعهم إياه واقتدائهم به، ومن لم يقتد به فليس من أمته.
قوله تعالى: وَآخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ [٣] يعني الذين جاؤوا من بعده فآمنوا به واتبعوه يلحقهم الله بأولهم.

<span data-type="title" id=toc-558>[سورة الجمعة (٦٢) : آية ١١]</span>
وَإِذا رَأَوْا تِجارَةً أَوْ لَهْواً انْفَضُّوا إِلَيْها وَتَرَكُوكَ قائِماً قُلْ مَا عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجارَةِ وَاللَّهُ خَيْرُ الرَّازِقِينَ (١١)
قوله تعالى: وَإِذا رَأَوْا تِجارَةً أَوْ لَهْواً انْفَضُّوا إِلَيْها [١١] قال: من شغله عن ربه شيء من الدنيا والآخرة فقد أخبر عن خسّة طبعه ونذالة همته، لأن الله قد فتح له الطريق، وأذن له في مناجاته، فاشتغل بما يفنى، ولم يكن عالماً بمن لم يزل، ولا يزال.
قوله تعالى: قُلْ مَا عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجارَةِ [١١] قال: يعني ما ادخر لكم في الآخرة من جزيل العطايا واللذة الباقية، خير مما أعطاكم من الدنيا.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-559>السورة التي يذكر فيها المنافقون</span>

<span data-type="title" id=toc-560>[سورة المنافقون (٦٣) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
إِذا جاءَكَ الْمُنافِقُونَ قالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَاللَّهُ يَشْهَدُ إِنَّ الْمُنافِقِينَ لَكاذِبُونَ (١)
قوله تعالى: وَاللَّهُ يَشْهَدُ إِنَّ الْمُنافِقِينَ لَكاذِبُونَ [١] قال: لأنهم أقروا بألسنتهم ولم يعرفوا بقلوبهم، فلذلك سماهم منافقين. ومن عرف بقلبه، وأقر بلسانه، ولم يعمل بأركانه ما فرض الله عليه من غير عذر، كان كإبليس لعنه الله، عرفه وأقربه ولم يعمل بأمره. قال: والنفاق على ضربين، عقد بالقلب وإظهار خلافه باللسان، كما قال تعالى: يَقُولُونَ بِأَلْسِنَتِهِمْ مَّا لَيْسَ فِي قُلُوبِهِمْ [الفتح: ١١] والضرب الآخر نفاق نفس الطبع مع صاحبها، وهو الذي قال النبي صلى الله عليه وسلّم:
«الشرك الخفي في أمتي أخفى من دبيب النمل على الصفا في الليلة الظلماء» «١» .

<span data-type="title" id=toc-561>[سورة المنافقون (٦٣) : آية ٩]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تُلْهِكُمْ أَمْوالُكُمْ وَلا أَوْلادُكُمْ عَنْ ذِكْرِ اللَّهِ وَمَنْ يَفْعَلْ ذلِكَ فَأُولئِكَ هُمُ الْخاسِرُونَ (٩)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا لاَ تُلْهِكُمْ أَمْوالُكُمْ وَلا أَوْلادُكُمْ [٩] عن أداء الفرائض في مواقيتها، فإن من شغله عن ذكر الله وخدمته عرض من عروض الدنيا شيئاً لشهوته، ووجد في عبادته نشاطاً فهو مخدوع، إلا الذي يأخذها الله عزَّ وجلَّ.
وقد حكي أن سلمان دخل عليه سعد بن أبي وقاص «٢» رضي الله عنه يعوده فبكى سلمان فقال: ما يبكيك يا أبا عبد الله، توفي سيدنا رسول الله صلّى الله عليه وسلّم وهو عنك راض، وتلقى أصحابك وترد حوضه. فقال سلمان: أما إني لست أبكي جزعاً على الموت، ولا حرصاً على الدنيا، ولكن
_________
(١) تقدم الحديث مع تفسير الآية (١٠٦) من سورة يوسف.
(٢) سعد بن أبي وقاص مالك بن أهيب القرشي الزهري (٢٣ ق. هـ- ٥٥ هـ) : الصحابي الأمير، فاتح العراق ومدائن كسرى. أحد العشرة المبشرين بالجنة. (الحلية ١/ ٩٢) .',NULL,'168',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (166,'رسول الله صلّى الله عليه وسلّم عهد إلينا عهداً فقال: «ليكن بلغة أحدكم من الدنيا مثل زاد الراكب» «١» ، وحولي هذه الأوساد- جمع وسادة- وإنما كان حوله لحافه ومطهرته وجفنته. فقال سعد: يا أبا عبد الله، اعهد إلينا عهداً نأخذه بعدك. فقال: يا سعد، اذكر الله تعالى عند همك إذا هممت، وعند حكمك إذا حكمت، وعند يدك إذا أقسمت «٢» .
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-562>السورة التي يذكر فيها التغابن</span>

<span data-type="title" id=toc-563>[سورة التغابن (٦٤) : آية ٢]</span>
هُوَ الَّذِي خَلَقَكُمْ فَمِنْكُمْ كافِرٌ وَمِنْكُمْ مُؤْمِنٌ وَاللَّهُ بِما تَعْمَلُونَ بَصِيرٌ (٢)
قوله تعالى: وَاللَّهُ بِما تَعْمَلُونَ بَصِيرٌ [٢] هل وافق العمل الطبع والخلقة.

<span data-type="title" id=toc-564>[سورة التغابن (٦٤) : الآيات ١٤ الى ١٥]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْواجِكُمْ وَأَوْلادِكُمْ عَدُوًّا لَكُمْ فَاحْذَرُوهُمْ وَإِنْ تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ (١٤) إِنَّما أَمْوالُكُمْ وَأَوْلادُكُمْ فِتْنَةٌ وَاللَّهُ عِنْدَهُ أَجْرٌ عَظِيمٌ (١٥)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْواجِكُمْ وَأَوْلادِكُمْ عَدُوًّا لَكُمْ فَاحْذَرُوهُمْ [١٤] قال: من حملك من أزواجك وأولادك على جمع الدنيا والركون إليها فهو عدو لك، ومن حثك على بذلها وإنفاقها، وذلك على القناعة والتوكل فليس بعدو لك.
وحكي عن الحسن أنه قال: يا ابن آدم، لا يغرنك من حولك من السباع الضارية ابنك وحليلتك وكلالتك وخادمك، أما ابنك فمثل الأسد في الشدة والصولة، ينازعك فيما في يدك وأما حليلتك، فمثل الكلبة في الهرير والبصبصة، تهر أحياناً وتبصبص أحياناً وأما كلالتك، فو الله لدرهم يقع في ميراث أحدهم، أحب إليه من أن لو كنت أعتقت رقبة وأما خادمك، فمثل الثعلب في الحيل والسرقة.
وأقول لك يا ابن آدم، اتق الله، فلا توقر ظهرك بصلاحهم، فإنما لك خطوات إلى منزلك القابل لأربعة أذرع في ذراعين، فإذا وضعوك هناك انصرفوا عنك وصرفوا النيات، وضربوا الدفوف، وضحكوا بالقهقهة، وأنت تحاسب بما في أيديهم.
قوله تعالى: نَّما أَمْوالُكُمْ وَأَوْلادُكُمْ فِتْنَةٌ
[١٥] قال: إن أعطاك الله المال تشاغلت بحفظه، وإن لم يعطك تشاغلت بطلبه، فمتى تتفرغ له.
والله سبحانه وتعالى أعلم بالصواب.
_________
(١) سنن ابن ماجة ٢/ ١٣٧٤ (رقم ١٤٠٤) والمستدرك على الصحيحين ٤/ ٣٥٣.
(٢) شعب الإيمان ٧/ ٣٠٥- ٣٠٦ والترغيب والترهيب ٤/ ٧٩، ١١٢.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (167,'<span data-type="title" id=toc-565>السورة التي يذكر فيها الطلاق</span>

<span data-type="title" id=toc-566>[سورة الطلاق (٦٥) : الآيات ٢ الى ٣]</span>
فَإِذا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَأَقِيمُوا الشَّهادَةَ لِلَّهِ ذلِكُمْ يُوعَظُ بِهِ مَنْ كانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجاً (٢) وَيَرْزُقْهُ مِنْ حَيْثُ لا يَحْتَسِبُ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ إِنَّ اللَّهَ بالِغُ أَمْرِهِ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْءٍ قَدْراً (٣)
قوله تعالى: ذلِكُمْ يُوعَظُ بِهِ مَنْ كانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ [٢] قال: لا يقبل الموعظة إلا مؤمن، والموعظة ما خرجت إلا من قلب سليم، لا يكون فيه غل ولا حقد ولا حسد، ولا يكون فيه حظ.
قوله تعالى: وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجاً (٢) وَيَرْزُقْهُ مِنْ حَيْثُ لاَ يَحْتَسِبُ [٢- ٣] قال: التقوى التبري من الحول والقوة، والأسباب كلها دونه بالرجوع إليه يجعل له مخرجاً مما كلفه بالمعونة والعصمة من الطواف فيها «١» . ولا يصح التوكل إلا للمتقين، ولا تصح التقوى إلا بالتوكل «٢» ، لذلك قال الله تعالى: وَيَرْزُقْهُ مِنْ حَيْثُ لاَ يَحْتَسِبُ [٣] .
قوله تعالى: وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ [٣] قال: يعني من يكل أموره إلى ربه فإن الله تعالى يكفيه مهم الدارين أجمع. وقال أبو الحسن عمر بن واصل العنبري: سمعت سهلاً يقول: دخلت البادية سبعة عشر مرة بلا زاد من طعام ولا شراب ولا هميان ولا ركوة ولا عصي فلم أحتج إلى شيء آكله إلا وهو معدّ لي، فقربت من البادية ذات كرة، فدفع إلي رجل درهمين صحيحين، فوضعتهما في جيبي ومضيت، فسرت مدة فلم أجد شيئاً، فضعفت وجعلت أقول في نفسي: ما الذي أحدثت حتى حبس عنك معلومك؟ فسمعت صوتاً من الهوى يقول: اطرح ما في الجيب يأتك ما في الغيب. فتذكرت أن في جيبي درهمين، فأخرجتهما ورميت بهما، فلم أسر هنيهة حتى أبصرت رغيفين بينهما عسل، كأنهما أخرجا من التنور ساعة، وعدت إلى ما كنت عليه.
والله سبحانه وتعالى أعلم.
_________
(١) في تفسير القرطبي ١٨/ ١٦٠ ورد أن التستري قال في تفسير هذه الآية: (ومن يتق الله في اتباع السنة يجعل له مخرجا من عقوبة أهل البدع، ويرزقه الجنة مِنْ حَيْثُ لاَ يَحْتَسِبُ) . [.....]
(٢) الحلية ١٠/ ١٩٢ وتقدم هذا القول في تفسير الآية (٨١) من سورة النساء.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (168,'<span data-type="title" id=toc-567>السورة التي يذكر فيها التحريم</span>

<span data-type="title" id=toc-568>[سورة التحريم (٦٦) : آية ٦]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنْفُسَكُمْ وَأَهْلِيكُمْ نَاراً وَقُودُهَا النَّاسُ وَالْحِجارَةُ عَلَيْها مَلائِكَةٌ غِلاظٌ شِدادٌ لا يَعْصُونَ اللَّهَ ما أَمَرَهُمْ وَيَفْعَلُونَ ما يُؤْمَرُونَ (٦)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنْفُسَكُمْ وَأَهْلِيكُمْ نَاراً [٦] قال: يعني بطاعة الله واتباع السنن.

<span data-type="title" id=toc-569>[سورة التحريم (٦٦) : آية ٨]</span>
يَا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحاً عَسى رَبُّكُمْ أَنْ يُكَفِّرَ عَنْكُمْ سَيِّئاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ يَوْمَ لاَ يُخْزِي اللَّهُ النَّبِيَّ وَالَّذِينَ آمَنُوا مَعَهُ نُورُهُمْ يَسْعى بَيْنَ أَيْدِيهِمْ وَبِأَيْمانِهِمْ يَقُولُونَ رَبَّنا أَتْمِمْ لَنا نُورَنا وَاغْفِرْ لَنا إِنَّكَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (٨)
قوله تعالى: يَا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحاً [٨] قال: التوبة النصوح أن لا يرجع، لأنه صار من جملة الأحبة، والمحب لا يدخل في شيء لا يحبه الحبيب «١» . وقال:
علامة التائب أن لا تقله أرض ولا تظله سماء إلا هو متعلق بالعرش وصاحب العرش، حتى يفارق الدنيا، ولا أعرف في هذا الزمان أقل من التوبة، إذ ليس منا أحد أتاه ملك الموت إلا ويقول:
دعني أفعل كذا وكذا، دعني أتنفس ساعة. ثم قال: إن التائب المخلص ولو مقدار ساعة، ولو مقدار نفس واحد قبل موته، يقال له: ما أسرع ما جئت به صحيحاً، وجئنا حيث جئت.
قوله: يَوْمَ لاَ يُخْزِي اللَّهُ النَّبِيَّ [٨] قال: لا يخزيه في أمته، ولا يرد شفاعته. ولقد أوحى الله تعالى إلى النبي صلّى الله عليه وسلّم فقال: إن أحببت جعلت أمر أمتك إليك. فقال: يا رب أنت خير لهم مني. فقال الله تعالى: إذاً لا أخزيك فيهم «٢» .
قوله عزَّ وجلَّ: يَقُولُونَ رَبَّنا أَتْمِمْ لَنا نُورَنا [٨] فقال: لا يسقط الافتقار إلى الله عزَّ وجلَّ عن المؤمنين في الدنيا ولا في العقبى، هم في الجنة أشد افتقاراً إليه، وإن كانوا في دار العز والأمن والغنى لشوقهم إلى لقائه، يَقُولُونَ رَبَّنا أَتْمِمْ لَنا نُورَنا [٨] وارزقنا لقاءك، فإنه منوّر الأنوار وغاية الطلاب.
والله سبحانه وتعالى أعلم.
_________
(١) في تفسير القرطبي ١٨/ ١٩٩ ورد أن التستري قال في تفسير هذه الآية: (هي التوبة لأهل السنة والجماعة، لأن المبتدع لا توبة له، بدليل قوله صلّى الله عليه وسلّم: أحجب الله على كل صاحب بدعة أن يتوب) .
(٢) قوت القلوب ١/ ٣٧٦.',NULL,'171',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (169,'<span data-type="title" id=toc-570>السورة التي يذكر فيها الملك</span>

<span data-type="title" id=toc-571>[سورة الملك (٦٧) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
تَبارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَهُوَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (١) الَّذِي خَلَقَ الْمَوْتَ وَالْحَياةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاً وَهُوَ الْعَزِيزُ الْغَفُورُ (٢)
قوله تعالى: تَبارَكَ الَّذِي بِيَدِهِ الْمُلْكُ [١] قال: أي تعالى الله وتعاظم عن الأشباه والأولاد والأضداد، الذي بيده الملك يقلبه بحوله وقوته، يؤتيه من يشاء، وينزعه ممن يشاء، وهو القادر عليه.
قوله تعالى: هُوَ عَلى كُلِّ شَيْءٍ قَدِيرٌ (١) الَّذِي خَلَقَ الْمَوْتَ وَالْحَياةَ
[١- ٢] قال:
الموت في الدنيا بالمعصية، والحياة في الآخرة بالطاعة. ولهذا قال الله تعالى لموسى عليه السلام فيما أوحى إليه: يا موسى، إن أول من مات من خلقي إبليس لعنه الله لأنه عصاني، وإني أعد من عصاني في الموتى. وقال: إن الموت خلق في صورة كبش أملح لا يمر بشيء فيجد ريحه إلا حيي.
وقد روي في الخبر أن أهل الجنة ليخافون الموت، وأهل النار يتمنون الموت، فيؤتى به في صورة كبش أملح، ثم يقال: هذا الموت فانظروا ما الله صانع فيه، ثم يضجع هناك فيذبح، ثم يجعله الله تعالى في صورة فرس يسرح في الجنة، لا يراه أحد من أهل الجنة إلا أنس به ولا يعلم أنه الموت «١» .
قوله تعالى: لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا [٢] قال: أي أصوبه وأخلصه، فإذا كان صواباً ولم يكن خالصاً لم يقبل، حتى يكون صواباً خالصاً. والخالص الذي يكون لله تعالى بإرادة القلب، والصواب الذي يكون على سبيل السنة وموافقة الكتاب. وقال مرة أخرى:
لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا [٢] أي توكلاً ورضاً علينا، وسياحة بعد الزهد في الدنيا. وإن مثل التقوى واليقين كمثل كفتي الميزان، والتوكل لسانه، يعرف به الزيادة من النقصان «٢» . فقيل:
وما التوكل؟ قال: الفرار من التوكل «٣» ، يعني من دعوى التوكل.
_________
(١) الترغيب والترهيب ٤/ ٣١٦- ٣١٨.
(٢) قوت القلوب ٢/ ٤.
(٣) نسب هذا القول إلى بعض المقربين في قوت القلوب ٢/ ٩، وفي الحلية ١٠/ ١٩٨ أن سهل التستري (سئل عن حقيقة التوكل، فقال: نسيان التوكل) .',NULL,'172',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (170,'قوله تعالى: وَهُوَ الْعَزِيزُ الْغَفُورُ [٢] قال: يعني المنيع في حكمه، الحكيم في تدبيره بخلقه، الغفور للنقصان والخلل الذي يظهر في طاعات عباده.

<span data-type="title" id=toc-572>[سورة الملك (٦٧) : آية ١٢]</span>
إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ (١٢)
قوله تعالى: الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ [١٢] أي يخافون ربهم في سرهم، فيحفظون سرهم من غيره.

<span data-type="title" id=toc-573>[سورة الملك (٦٧) : الآيات ١٤ الى ١٥]</span>
أَلا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اللَّطِيفُ الْخَبِيرُ (١٤) هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولاً فَامْشُوا فِي مَناكِبِها وَكُلُوا مِنْ رِزْقِهِ وَإِلَيْهِ النُّشُورُ (١٥)
قوله تعالى: أَلا يَعْلَمُ مَنْ خَلَقَ [١٤] ألا يعلم من خلق القلب، بما أودعه من التوحيد والجحود. وَهُوَ اللَّطِيفُ [١٤] بعلمه بما في لب القلوب من الأسرار المكنونة فيها، كما قال النبي صلى الله عليه وسلّم: «إن من العلم سراً مكنوناً لله تعالى في القلوب» . الْخَبِيرُ [١٤] يخبرك بما في غيبك.
قوله تعالى: هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولًا [١٥] قال: خلق الله تعالى الأنفس ذلولاً، فمن أذلها بمخالفتها فقد نجاها من الفتن والبلايا والمحن، ومن أذلها واتبعها فقد أذلته نفسه وأهلكته.

<span data-type="title" id=toc-574>[سورة الملك (٦٧) : آية ٢٢]</span>
أَفَمَنْ يَمْشِي مُكِبًّا عَلى وَجْهِهِ أَهْدى أَمَّنْ يَمْشِي سَوِيًّا عَلى صِراطٍ مُسْتَقِيمٍ (٢٢)
قوله عزَّ وجلَّ: أَفَمَنْ يَمْشِي مُكِبًّا عَلى وَجْهِهِ أَهْدى [٢٢] قال: أفمن يكون مطرقاً إلى هوى نفسه بجبلة طبعه بغير هدى من ربه أهدى أَمَّنْ يَمْشِي سَوِيًّا عَلى صِراطٍ مُسْتَقِيمٍ [٢٢] قال: يعني أم من يكون متبعاً شرائع الإسلام مقتدياً بالنبيين.
والله سبحانه وتعالى أعلم.',NULL,'173',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (171,'<span data-type="title" id=toc-575>السورة التي يذكر فيها القلم</span>

<span data-type="title" id=toc-576>[سورة القلم (٦٨) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
ن وَالْقَلَمِ وَما يَسْطُرُونَ (١)
قوله تعالى: ن وَالْقَلَمِ وَما يَسْطُرُونَ [١] قال: النون اسم من أسماء الله تعالى، إذا جمعت بين أوائل السور: «الر» و «حم» و «ن» فهو اسم الرحمن «١» . وقال ابن عباس رضي الله عنهما: النون الدواة التي كتب الذكر منها، والقلم الذي كتب به الذكر الحكيم. وَما يَسْطُرُونَ [١] ما تكتبه الحفظة من أعمال بني آدم. وقال عمر بن واصل: وما يسطرون، أي: وما تولى الله لعباده من الكتابة التي فيها منافع الخلق ومصالح العباد والبلاد.

<span data-type="title" id=toc-577>[سورة القلم (٦٨) : الآيات ٣ الى ٤]</span>
وَإِنَّ لَكَ لَأَجْراً غَيْرَ مَمْنُونٍ (٣) وَإِنَّكَ لَعَلى خُلُقٍ عَظِيمٍ (٤)
قوله تعالى: وَإِنَّ لَكَ لَأَجْراً غَيْرَ مَمْنُونٍ [٣] قال: أي محدود مقطوع ومحسوب عليك.
قوله تعالى: وَإِنَّكَ لَعَلى خُلُقٍ عَظِيمٍ [٤] قال: تأدبتَ بأدب القرآن، فلم تتجاوز حدوده وهو قوله تعالى: إِنَّ اللَّهَ يَأْمُرُ بِالْعَدْلِ وَالْإِحْسانِ [النحل: ٩٠] . وقال: فَبِما رَحْمَةٍ مِنَ اللَّهِ لِنْتَ لَهُمْ [آل عمران: ١٥٩] ثم قال: إن الغضب والحدة من سكون العبد إلى قوته، فإذا خرج من سكونه إلى قوته سكن الضعف في نفسه، فتتولد منه الرحمة واللطف، وهو التخلق بأخلاق الرب جل جلاله. وقد أوحى الله تعالى إلى داود عليه السلام فقال: «تخلق بأخلاقي فإني أنا الصبور» «٢» فمن أوتي الخلق الحسن فقد أوتي أعظم المقامات، لأن ما دونه من المقامات ارتباط بالعامة، والخلق الحسن ارتباط بالصفات والنعوت. وسئل سهل يوماً عن الكرامات، فقال: وما الكرامات، إن الكرامات شيء ينقضي لوقته، ولكن أكبر الكرامات أن تبدل خلقاً مذموماً من أخلاقك بخلق محمود.

<span data-type="title" id=toc-578>[سورة القلم (٦٨) : آية ٤٤]</span>
فَذَرْنِي وَمَنْ يُكَذِّبُ بِهذَا الْحَدِيثِ سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لاَ يَعْلَمُونَ (٤٤)
قوله تعالى: فَذَرْنِي وَمَنْ يُكَذِّبُ بِهذَا الْحَدِيثِ [٤٤] قال: يعني كِلْهُ إلي، فإني أكفيك أمره، ولا تشغل به قلبك.
قوله تعالى: سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لاَ يَعْلَمُونَ [٤٤] قال: سنمدهم إطراقاً إليهم، مشتغلين به عما لنا عليهم من الواجبات، فينسون شكرنا، فنأخذهم من حيث لا يعلمون.

<span data-type="title" id=toc-579>[سورة القلم (٦٨) : آية ٤٩]</span>
لَوْلا أَنْ تَدارَكَهُ نِعْمَةٌ مِنْ رَبِّهِ لَنُبِذَ بِالْعَراءِ وَهُوَ مَذْمُومٌ (٤٩)
قوله عزَّ وجلَّ: لَوْلا أَنْ تَدارَكَهُ نِعْمَةٌ مِنْ رَبِّهِ [٤٩] قال: يعني لولا ما حفظ الله له ما سلف من عمله الصالح، بما جرى به من اجتبائه في الأزل، فاستنقذه به وتداركه. لَنُبِذَ بِالْعَراءِ وَهُوَ مَذْمُومٌ [٤٩] والعراء أرض القيامة، إذ لا زرع فيها، ولا نبت، ولم يكن له ذنب سوى أنه شغل قلبه بتدبير ما لم يكن تدبيره إليه، كما فعل آدم عليه السلام، والله سبحانه وتعالى أعلم.
_________
(١) الإتقان ٣/ ٢٤.
(٢) فيض القدير ١/ ٤٦٥، ٥/ ٣٦٣.',NULL,'174',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (172,'<span data-type="title" id=toc-580>السورة التي يذكر فيها الحاقة</span>

<span data-type="title" id=toc-581>[سورة الحاقة (٦٩) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الْحَاقَّةُ (١) مَا الْحَاقَّةُ (٢)
قوله تعالى: الْحَاقَّةُ (١) مَا الْحَاقَّةُ [١- ٢] قال: إن الله تعالى عظَّم حال يوم القيامة بما فيها من الشدة بإدخال الهاء فيها، ومعناها اليوم الذي يلحق كل أحد فيه بعمله من خير أو شر.
وقال عمر بن واصل: معناها: يحق فيه جزاء الأعمال لكل طائفة.

<span data-type="title" id=toc-582>[سورة الحاقة (٦٩) : الآيات ١٧ الى ١٩]</span>
وَالْمَلَكُ عَلى أَرْجائِها وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمانِيَةٌ (١٧) يَوْمَئِذٍ تُعْرَضُونَ لاَ تَخْفى مِنْكُمْ خافِيَةٌ (١٨) فَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ فَيَقُولُ هاؤُمُ اقْرَؤُا كِتابِيَهْ (١٩)
قوله عزَّ وجلَّ: وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمانِيَةٌ [١٧] قال: يعني ثمانية أجزاء من الكروبيين لا يعلم عدتهم إلا الله. وقال النبي صلى الله عليه وسلّم: «إن الله أذن لي أن أحدث عن ملك من حملة العرش رجلاه في الأرض السفلى وعلى قرنه العرش بين شحمة أذنيه إلى عاتقه خفقان الطائر سبعمائة سنة يقول ذلك الملك سبحان الله حيث كنت» «١» .
قوله تعالى: يَوْمَئِذٍ تُعْرَضُونَ لاَ تَخْفى مِنْكُمْ خافِيَةٌ [١٨] قال: أي تعرضون على الحق عزَّ وجلَّ، فيحاسبكم بأعمالكم، لا يخفى عليه من أعمالكم شيء، كل ذلك معروف محصي عليكم في علمه السابق، فيسأله عن جميع ذلك، يعني يسأله فيقول له: ألم تكن عارفاً بالساعات من أجلي؟ ألم يوسع لك حتى في المجالس من أجلي؟ ألم تسألني أن أزوجك فلانة أمَتي أحسن منك فزوجناكها؟ فهذا سؤال نعمه عليك فكيف سؤاله عن معصيته. وقد حكي عن عتبة الغلام «٢» أنه قال: إن العبد المؤمن ليوقف بين يدي الله تعالى بالذنب الواحد مائة عام.
قوله تعالى: فَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ فَيَقُولُ هاؤُمُ اقْرَؤُا كِتابِيَهْ [١٩] أي فيقول:
هاكم اقرؤوا كتابي بما فيه من أنواع الطاعات.

<span data-type="title" id=toc-583>[سورة الحاقة (٦٩) : الآيات ٢٤ الى ٢٥]</span>
كُلُوا وَاشْرَبُوا هَنِيئاً بِما أَسْلَفْتُمْ فِي الْأَيَّامِ الْخالِيَةِ (٢٤) وَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِشِمالِهِ فَيَقُولُ يا لَيْتَنِي لَمْ أُوتَ كِتابِيَهْ (٢٥)
ويقال لهم: كُلُوا وَاشْرَبُوا هَنِيئاً بِما أَسْلَفْتُمْ فِي الْأَيَّامِ الْخالِيَةِ [٢٤] يعني صوم رمضان وأيام البيض من كل شهر. وقد جاء في الحديث:
«أنه يوضع للصوام يوم القيامة موائد يأكلون عليها والناس في الحساب، فيقال: يا رب، الناس في الحساب وهم لا يأكلون. فيقال لهم: إنهم طالما صاموا في الدنيا وأفطرتم، وقاموا ونمتم» .
_________
(١) مجمع الزوائد ١/ ٨٠ والمعجم الأوسط ٢/ ١٩٩، ٦/ ٣١٤.
(٢) عتبة الغلام: عتبة بن أبان بن صمعة الأنصاري، من زهاد أهل البصرة وعبادهم. جالس الحسن البصري، وأخذ عنه هديه في التقشف. وسمي بالغلام لجده واجتهاده، لا لصغر سنه. (مشاهير علماء الأمصار ص ١٥٢ وصفوة الصفوة ٣/ ٣٧٠) .',NULL,'175',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (173,'وَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِشِمالِهِ فَيَقُولُ يا لَيْتَنِي لَمْ أُوتَ كِتابِيَهْ [٢٥] أي بما فيه من الأعمال الخبيثة والكفر، فيتمنى أن يكون غير مبعوث،

<span data-type="title" id=toc-584>[سورة الحاقة (٦٩) : الآيات ٢٧ الى ٣٠]</span>
يا لَيْتَها كانَتِ الْقاضِيَةَ (٢٧) مَآ أَغْنى عَنِّي مالِيَهْ (٢٨) هَلَكَ عَنِّي سُلْطانِيَهْ (٢٩) خُذُوهُ فَغُلُّوهُ (٣٠)
فيقول: يا لَيْتَها كانَتِ الْقاضِيَةَ [٢٧] يعني:
يا ليت الموتة الأولى كانت عليّ فلم أبعث. مَآ أَغْنى عَنِّي مالِيَهْ [٢٨] كثرة مالي، حيث لم أؤد منه حق الله، ولم أصل به القرابة. هَلَكَ عَنِّي سُلْطانِيَهْ [٢٩] يعني حجتي وعذري، فيقول الله تعالى: خُذُوهُ فَغُلُّوهُ [٣٠] فإذا قال ذلك ابتدره مائة ألف ملك، لو أن ملكاً منهم أخذ الدنيا بما فيها من جبالها وبحارها بقبضته لقوي عليه فتغل يداه إلى عنقه ثم يدخل في الجحيم.

<span data-type="title" id=toc-585>[سورة الحاقة (٦٩) : آية ٣٢]</span>
ثُمَّ فِي سِلْسِلَةٍ ذَرْعُها سَبْعُونَ ذِراعاً فَاسْلُكُوهُ (٣٢)
ثُمَّ فِي سِلْسِلَةٍ ذَرْعُها سَبْعُونَ ذِراعاً [٣٢] كل ذراع سبعون باعاً، كل باع أبعد مما بين الكوفة ومكة، لو وضعت حلقة منها على ذروة جبل لذاب كما يذوب الرصاص، كذا حكي عن ابن عباس رضي الله عنهما. وحكي أن عمر رضي الله عنه قال لكعب: خوفنا يا أبا إسحاق. قال: يا أمير المؤمنين، لو أنك عملت حتى تعود كالعود المقضوب من العبادة، وكان لك عمل سبعين نبياً لظننت أن لا تنجو من أمر ربك وحملة العرش، وجيء باللوح المحفوظ الذي قد حفظ فيه الأعمال وبرزت الجحيم وأزلفت الجنة، وقام الناس لرب العالمين، وزفرت جهنم زفرة لا يبقى ملك مقرب ولا نبي مرسل إلا جثا على ركبتيه، حتى يقول إبراهيم: نفسي نفسي، فيدعى على رؤوس الخلائق بالرجل العادل والرجل الجائر، فإذا جيء بالرجل العادل رفع إليه كتابه بيمينه، فلا سرور ولا فرح ولا غبطة نزل يومئذ بعبد أفضل مما نزل به، فيقول على رؤوس الخلائق ما حكاه الله تعالى، ثم يؤتى بالرجل الجائر، فيدفع إليه كتابه بشماله، فلا حزن ولا ذل ولا حسرة أشد مما نزل بالرجل، فيقول على رؤوس الخلائق ما حكى الله تعالى، فيؤخذ ويسحب على وجهه إلى النار، فينتثر لحمه وعظامه ومخه. فقال عمر رضي الله عنه: حسبي حسبي «١» . قال سهل: إن السلاسل والأغلال ليست للاعتقال، وإنما هي لتجذبهم سفلاً بعد أبداً ما داموا فيها.

<span data-type="title" id=toc-586>[سورة الحاقة (٦٩) : الآيات ٤٤ الى ٤٦]</span>
وَلَوْ تَقَوَّلَ عَلَيْنا بَعْضَ الْأَقاوِيلِ (٤٤) لَأَخَذْنا مِنْهُ بِالْيَمِينِ (٤٥) ثُمَّ لَقَطَعْنا مِنْهُ الْوَتِينَ (٤٦)
قوله عزَّ وجلَّ: وَلَوْ تَقَوَّلَ عَلَيْنا بَعْضَ الْأَقاوِيلِ [٤٤] قال: يعني لو تكلم بما لم تأذن له فيه. لَأَخَذْنا مِنْهُ بِالْيَمِينِ [٤٥] يعني أمرنا بأخذ يده كما تفعل الملوك. ثُمَّ لَقَطَعْنا مِنْهُ الْوَتِينَ [٤٦] وهو نياط القلب، وهو العرق الذي يتعلق القلب به، إذا انقطع مات صاحبه، فنقطع ذلك السبب بمخالفته إيانا.

<span data-type="title" id=toc-587>[سورة الحاقة (٦٩) : آية ٤٨]</span>
وَإِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّقِينَ (٤٨)
قوله تعالى: وَإِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّقِينَ [٤٨] قال: يعني القرآن رحمة للمطيعين.

<span data-type="title" id=toc-588>[سورة الحاقة (٦٩) : آية ٥٠]</span>
وَإِنَّهُ لَحَسْرَةٌ عَلَى الْكافِرِينَ (٥٠)
قوله تعالى: وَإِنَّهُ لَحَسْرَةٌ عَلَى الْكافِرِينَ [٥٠] قال: يعني ما يرون من ثواب أهل التوحيد ومنازلهم وكريم مقاماتهم، والله سبحانه وتعالى أعلم.
_________
(١) المستدرك على الصحيحين ٤/ ٦٣٤ ومجمع الزوائد ١٠/ ٣٤٢ والمعجم الكبير ٩/ ٣٦٠ والحلية ٥/ ٣٧١، ٣٩٠.',NULL,'176',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (174,'<span data-type="title" id=toc-589>السورة التي يذكر فيها المعارج</span>

<span data-type="title" id=toc-590>[سورة المعارج (٧٠) : الآيات ٤ الى ٧]</span>
تَعْرُجُ الْمَلائِكَةُ وَالرُّوحُ إِلَيْهِ فِي يَوْمٍ كانَ مِقْدارُهُ خَمْسِينَ أَلْفَ سَنَةٍ (٤) فَاصْبِرْ صَبْراً جَمِيلاً (٥) إِنَّهُمْ يَرَوْنَهُ بَعِيداً (٦) وَنَراهُ قَرِيباً (٧)
قوله تعالى: تَعْرُجُ الْمَلائِكَةُ وَالرُّوحُ إِلَيْهِ [٤] قال: تعرج الملائكة بأعمال بني آدم والروح وهو دهن النفس، وتعرج إلى الله تعالى مشاهدة بالإخلاص في أعماله، فيقطع هذه المسافة إلى العرش التي مقدارها خمسون ألف سنة بطرفة عين، هذا باطن الآية.
قوله تعالى: فَاصْبِرْ صَبْراً جَمِيلًا [٥] أي رضاً من غير شكوى، فإن الشكوى بلوى، ودعوى الصبر معه دعوى، وإن لله تعالى عباداً شكوا به منه إليه حجة تمسك النفس الطبع عن التفات إلى شيء غير الذي من أجله صبر الصابر.
قوله تعالى: إِنَّهُمْ يَرَوْنَهُ بَعِيداً (٦) وَنَراهُ قَرِيباً [٦- ٧] قال: يعني أنهم يرون المقضي عليهم من الموت والبعث والحساب بعيداً لبعد آمالهم، ونراه قريباً، فإن كل كائن قريب، والبعيد ما لا يكون. ثم قال: إن العلماء طلبوا الوسوسة في الكتاب والسنة، فلم يجدوا لها أصلاً إلا فضول الحلال وفضول الحلال أن يرى العبد وقتاً غير وقته الذي هو فيه وهو الأمل. وقد روي عن حبيش عن ابن عباس رضي الله عنهما: «أن النبي صلى الله عليه وسلّم كان يريق الماء فيتمسح بالتراب فقلت: يا رسول الله إن الماء منك لقريب. فقال: لا أدري لعلي لا أبلغه» «١» . وقد قال أسامة:
قرباننا إلى شهرين. إن أسامة لطويل الأمل. وسئل سهل: بم ترحل الدنيا من القلب؟ فقال:
بقصر الأمل. فقيل: وما قصر الأمل؟ فقال: قطع الهموم بالمضمون، والسكون إلى الضامن.

<span data-type="title" id=toc-591>[سورة المعارج (٧٠) : الآيات ١٩ الى ٢٢]</span>
إِنَّ الْإِنْسانَ خُلِقَ هَلُوعاً (١٩) إِذا مَسَّهُ الشَّرُّ جَزُوعاً (٢٠) وَإِذا مَسَّهُ الْخَيْرُ مَنُوعاً (٢١) إِلاَّ الْمُصَلِّينَ (٢٢)
قوله تعالى: إِنَّ الْإِنْسانَ خُلِقَ هَلُوعاً [١٩] قال: يعني متقلباً في حركات الشهوات واتباع الهوى. إِذا مَسَّهُ الشَّرُّ جَزُوعاً (٢٠) وَإِذا مَسَّهُ الْخَيْرُ مَنُوعاً [٢٠- ٢١] قال: إذا افتقر حزن، وإذا أثرى منع. إِلَّا الْمُصَلِّينَ [٢٢] أي العارفين بمقادير الأشياء، فلا يكون لهم بغير الله فرح، ولا إلى غيره سكون، ولا من غيره فرح، فراقه جزع، كما قال: وَالَّذِينَ هُمْ مِنْ عَذابِ رَبِّهِمْ مُشْفِقُونَ [٢٧] .
وقد حكي عن النبي صلّى الله عليه وسلّم أنه قال: «من خيار أمتي فيما نبأني الملأ الأعلى في الدرجات العلى قوم يضحكون جهراً من سعة رحمة ربهم، ويبكون سراً من خوف شدة عذاب ربهم،
_________
(١) قوت القلوب ٢/ ٣٣.',NULL,'177',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (175,'ويذكرون ربهم بالغداة والعشي في بيوته الطيبة، ويدعونه بألسنتهم رغباً ورهباً ويسألونه بأيديهم خفضاً ورفعاً، ويشتاقون إليه بقلوبهم عوداً وبدءاً، مؤونتهم على الناس خفيفة وعلى أنفسهم ثقيلة، يدبون على الأرض بأقدامهم دبيب النمل بغير فرح ولا بذخ ولا ميل» «١» ، الحديث بطوله.

<span data-type="title" id=toc-592>[سورة المعارج (٧٠) : آية ٢٩]</span>
وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حافِظُونَ (٢٩)
قوله تعالى: وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حافِظُونَ [٢٩] قال: باطن الآية جميع الجوارح الظاهرة والباطنة يحفظونها عن ظهور آثار نفس الطبع عليها.

<span data-type="title" id=toc-593>[سورة المعارج (٧٠) : الآيات ٣٢ الى ٣٣]</span>
وَالَّذِينَ هُمْ لِأَماناتِهِمْ وَعَهْدِهِمْ راعُونَ (٣٢) وَالَّذِينَ هُمْ بِشَهاداتِهِمْ قائِمُونَ (٣٣)
وَالَّذِينَ هُمْ لِأَماناتِهِمْ وَعَهْدِهِمْ راعُونَ [٣٢] قال: باطنها أمانة النفس، لأنها سر الله عند عباده، يسارّهم بمعلومه فيها خواطراً وهمماً، ويسارّونه بالافتقار واللجأ إليه، فإذا سكن القلب إلى ما خطر عليه من وسوسة العبد وبأدنى شيء ظهر إلى الصدر، ومن الصدر إلى الجسد، فيكون قد خان في أمانة الله، وعهده والإيمان.
وَالَّذِينَ هُمْ بِشَهاداتِهِمْ قائِمُونَ [٣٣] قال: قائمون بحفظ ما شهدوا به من شهادة أن لا إله إلا الله، فلا يقعدون عنها في شيء من الأفعال والأقوال والأحوال ولا يفترون.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-594>السورة التي يذكر فيها نوح عليه السلام</span>

<span data-type="title" id=toc-595>[سورة نوح (٧١) : آية ٧]</span>
وَإِنِّي كُلَّما دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوا أَصابِعَهُمْ فِي آذانِهِمْ وَاسْتَغْشَوْا ثِيابَهُمْ وَأَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْباراً (٧)
قوله تعالى: وَأَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْباراً [٧] قال: الإصرار على الذنب يورث الجهل، والجهل يورث التخطي في الباطل، والتخطي في الباطل يورث النفاق، والنفاق يورث الكفر.
قيل: وما علامة المنافق؟ قال: يبصر الشيء عند مذاكرته، فإذا قام من عنده كأنه لم يخطر على قلبه، قال الله تعالى: كُلَّما أَضاءَ لَهُمْ مَشَوْا فِيهِ وَإِذا أَظْلَمَ عَلَيْهِمْ قامُوا [البقرة: ٢٠] .

<span data-type="title" id=toc-596>[سورة نوح (٧١) : آية ٢٥]</span>
مِمَّا خَطِيئاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا ناراً فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللَّهِ أَنْصاراً (٢٥)
قوله تعالى: أُغْرِقُوا فَأُدْخِلُوا نَاراً [٢٥] قال: أغرقوا في الحيرة عن الهدى، فأدخلوا ناراً، فأوجب الله عليهم الهوان، وأنزلهم دار الشقاء.
والله سبحانه وتعالى أعلم.
_________
(١) المستدرك على الصحيحين ٣/ ١٩ وشعب الإيمان ١/ ٤٧٨.',NULL,'178',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (176,'<span data-type="title" id=toc-597>السورة التي يذكر فيها الجن</span>

<span data-type="title" id=toc-598>[سورة الجن (٧٢) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقالُوا إِنَّا سَمِعْنا قُرْآناً عَجَباً (١) يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ وَلَنْ نُشْرِكَ بِرَبِّنا أَحَداً (٢)
قوله تعالى: قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ [١] قال: كان تسعة نفر من نصيبين اليمن، والنفر اسم يقع على الثلاثة إلى العشرة، جاؤوا النبي صلى الله عليه وسلّم وهو يقرأ القرآن في الصلاة، وكانوا من أمثل قومهم في دينهم، فلما سمعوه رقوا له فآمنوا به، ورجعوا إلى قومهم منذرين. فَقالُوا إِنَّا سَمِعْنا قُرْآناً عَجَباً (١) يَهْدِي إِلَى الرُّشْدِ [١- ٢] يعني يدل على اتباع سنن المصطفى صلّى الله عليه وسلّم. وقال سهل: رأيت في دار عاد الأولى مدينة مبنية من حجر، فيها قصر عظيم منقور من حجر يأويه الجن، فدخلت القصر معتبراً، فرأيت شخصاً عظيماً قائماً يصلي نحو الكعبة، عليه جبة صوف بيضاء بها طراوة، فعجبت لطراوة جبته، وانتظرت حتى فرغ من صلاته، فقلت: السلام عليك. فقال: وعليك السلام يا أبا محمد، عجبت لطراوة جبتي وهي علي منذ تسعمائة سنة؟ فيها لقيت عيسى ابن مريم ومحمدا صلّى الله عليه وسلّم فآمنت بهما، واعلم يا أبا محمد أن الأبدان لا تخلق الثياب، وإنما يخلقهما مطاعم السحت والإصرار على الذنوب.
فقلت: ومن أنت؟ فقال: أنا من الذين قال الله تعالى في حقهم: قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ [١] .
وسئل سهل: هل يدخل الجن الجنة؟ فقال: بلغني أن في الجنة براري يسكنها الجن، ويأكلون فيها ويشربون، وفي القرآن دليل عليه، قال تعالى: لَمْ يَطْمِثْهُنَّ إِنْسٌ قَبْلَهُمْ وَلا جَانٌّ [الرحمن: ٧٤] .

<span data-type="title" id=toc-599>[سورة الجن (٧٢) : آية ١٨]</span>
وَأَنَّ الْمَساجِدَ لِلَّهِ فَلا تَدْعُوا مَعَ اللَّهِ أَحَداً (١٨)
قوله تعالى: وَأَنَّ الْمَساجِدَ لِلَّهِ فَلا تَدْعُوا مَعَ اللَّهِ أَحَداً [١٨] قال: أي لا تدعوا مع الله شريكاً، أي ليس لأحد معي شريك في شيء يمنع عبادي من ذكري، كذلك ما كان لله تعالى فهو على هذه الجهة، ليس لأحد فيه سبيل المنع والزجر.

<span data-type="title" id=toc-600>[سورة الجن (٧٢) : آية ٢٢]</span>
قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَداً (٢٢)
قوله تعالى: قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَداً [٢٢] قال:
أمره بالافتقار واللجوء إليه، ثم بإظهارهما بقوله، ليزيد بذلك للكافرين ضلالاً وللمؤمنين إرشاداً، وهي كلمة الإخلاص في التوحيد. إذ حقيقة التوحيد هو النظر للحق لا غير، والإقبال عليه، والاعتماد، ولا يتم ذلك إلا بالإعراض عما سواه، وبإظهار الافتقار واللجوء إليه.
والله سبحانه وتعالى أعلم.',NULL,'179',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (177,'<span data-type="title" id=toc-601>السورة التي يذكر فيها المزمل صلّى الله عليه وسلّم</span>

<span data-type="title" id=toc-602>[سورة المزمل (٧٣) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
يا أَيُّهَا الْمُزَّمِّلُ (١)
قوله تعالى: يا أَيُّهَا الْمُزَّمِّلُ [١] قال: المزمل الذي تزمل في الثياب وضمها عليه، وهو في الباطن اسم له معناه: يا أيها الجامع نفسه ونفس الله عنده.

<span data-type="title" id=toc-603>[سورة المزمل (٧٣) : آية ٦]</span>
إِنَّ ناشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئاً وَأَقْوَمُ قِيلاً (٦)
قوله تعالى: إِنَّ ناشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئاً [٦] قال: يعني الليل كله وما ينشئه العبد من عباده الليل هي أشد مواطأة على السمع والقلب من الإصغاء والفهم. وَأَقْوَمُ قِيلًا [٦] أي وأثبت رتبة، وقيل: وأصوب قيلاً، لأنه أبعد من الرياء. قال الحسن رحمة الله عليه: لقد أدركت أقواماً يقدرون على أن يعملوا في السر، فأرادوا أن يعملوه علانية، ولقد أدركت أقواماً إن أحدهم ليأتيه الزوار فيقوم من الليل فيصلي، وما يشعر به الزوار. وكان لقمان يقول لابنه: يا بني لا تكن أعجز من هذا الديك يصوت بالليل «١» .

<span data-type="title" id=toc-604>[سورة المزمل (٧٣) : الآيات ٨ الى ٩]</span>
وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلاً (٨) رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لا إِلهَ إِلاَّ هُوَ فَاتَّخِذْهُ وَكِيلاً (٩)
قوله تعالى: وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا [٨] قال: اقرأ بسم الله الرحمن الرحيم في افتتاح صلاتك توصلك بركة قراءتها إلى ربك، وتقطعك عن كل ما سواه.
قوله تعالى: لاَ إِلهَ إِلَّا هُوَ فَاتَّخِذْهُ وَكِيلًا [٩] أي كفيلاً بما وعدك من المعونة على الأمر، والعصمة عن النهي، والتوفيق للشكر، والصبر في البلوى، والخاتمة المحمودة. ثم قال:
في الدنيا الجنة والنار، فالجنة والعافية أن تولي الله أمرك، والنار البلوى، والبلوى أن يكلك إلى نفسك. قيل: فما الفرج؟ قال: لا تطمع في الفرج وأنت ترى مخلوقاً، وما من عبد أراد الله بعزم صحيح إلا زال عنه كل شيء دونه، وما من عبد زال عنه كل شيء دونه إلا حق عليه أن يقوم بأمره، وليس في الدنيا مطيع لله وهو يطيع نفسه، ولا يتباعد أحد عن الله إلا بالاشتغال بغير الله، وإنما تدخل الأشياء على الفارغ، وأما من كان مشغول القلب بالله لم تصل إليه الوسوسة وهو في المزيد أبداً، واحفظ نفسك بالأصل. قيل له: ما هو؟ قال: التسليم لأمر الله، والتبري ممن سواه.
والله سبحانه وتعالى أعلم.
_________
(١) شعب الإيمان ٥/ ٤١ (رقم ٥٦٩٨) . [.....]',NULL,'180',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (178,'<span data-type="title" id=toc-605>السورة التي يذكر فيها المدثر صلّى الله عليه وسلّم</span>

<span data-type="title" id=toc-606>[سورة المدثر (٧٤) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
يا أَيُّهَا الْمُدَّثِّرُ (١) قُمْ فَأَنْذِرْ (٢)
قوله تعالى: يا أَيُّهَا الْمُدَّثِّرُ (١) قُمْ فَأَنْذِرْ [١- ٢] قال: يا أيها المستغيث من إعانة نفسك على صدرك وقلبك، قم بنا وأسقط عنك ما سوانا، وأنذر عبادنا لأنا قد هيأناك لأشرف المواقف وأعظم المقامات.

<span data-type="title" id=toc-607>[سورة المدثر (٧٤) : آية ٤]</span>
وَثِيابَكَ فَطَهِّرْ (٤)
وَثِيابَكَ فَطَهِّرْ [٤] قال: أي لا تلبس ثيابك على معصية، فطهره عن حظوظك واشتمل به، كما حكت عائشة رضي الله عنها أنها قالت: (كان لرسول الله صلّى الله عليه وسلّم خميصة، فأعطاها أبا الجهم وأخذ إنبجانيته. فقيل: يا رسول الله، إن الخميصة خير من الإنبجانية.
فقال: «إني كنت أنظر إليها في الصلاة» ) «١» .

<span data-type="title" id=toc-608>[سورة المدثر (٧٤) : آية ١٢]</span>
وَجَعَلْتُ لَهُ مَالاً مَمْدُوداً (١٢)
قوله تعالى: وَجَعَلْتُ لَهُ مَالاً مَمْدُوداً [١٢] قال: يعني الوليد بن المغيرة «٢» ، جعلت له الحرص وطول الأمل.

<span data-type="title" id=toc-609>[سورة المدثر (٧٤) : آية ٥٦]</span>
وَما يَذْكُرُونَ إِلاَّ أَنْ يَشاءَ اللَّهُ هُوَ أَهْلُ التَّقْوى وَأَهْلُ الْمَغْفِرَةِ (٥٦)
قوله تعالى: هُوَ أَهْلُ التَّقْوى وَأَهْلُ الْمَغْفِرَةِ [٥٦] قال: يعني هو أهل أن يتقى فلا يعصى، وأهل المغفرة لمن يتوب. والتقوى هو ترك كل شيء مذموم، فهو في الأمر ترك التسويف، وفي النهي ترك الفكرة، وفي الآداب مكارم الأخلاق، وفي الترغيب كتمان السر، وفي الترهيب اتقاء الوقوف عند الجهل. والتقوى هو التبري من كل شيء سوى الله، فمن لزم هذه الآداب في التقوى فهو أهل المغفرة. وقد حكي أن رجلاً أتى عيسى ابن مريم عليه السلام فقال: يا معلم الخير كيف أكون تقياً كما ينبغي؟ قال: بيسير من الأمر، تحب الله بقلبك كله، وتعمل بكدحك وقوتك ما استطعت، وترحم ابن جنسك كما ترحم نفسك. قال: من جنسي يا معلم الخير؟
قال: ولد آدم، فما لا تحب أن يؤتى إليك فلا تأته إلى أحد «٣» .
والله سبحانه وتعالى أعلم.
_________
(١) مسند أحمد ٦/ ٤٦ ومسند إسحاق بن راهويه ٢/ ١٣٧.
(٢) الوليد بن المغيرة بن عبد الله المخزومي (٩٥ ق. هـ- ١ هـ) : من قضاة العرب في الجاهلية، ومن زعماء قريش، ومن زنادقتها. أدرك الإسلام ولم يسلم. (الأعلام ٨/ ١٢٢) .
(٣) جامع العلوم والحكم ص ١٨١.',NULL,'181',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (179,'<span data-type="title" id=toc-610>السورة التي يذكر فيها القيامة</span>
قرئ على سهل فأقر به، قال عمر بن الخطاب رضي الله عنه: من شاء أن يبصر يوم القيامة فليقرأ سورة القيامة، وإنما قيامة أحدكم موته.

<span data-type="title" id=toc-611>[سورة القيامة (٧٥) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
لاَ أُقْسِمُ بِيَوْمِ الْقِيامَةِ (١) وَلا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ (٢)
قوله تعالى: لاَ أُقْسِمُ بِيَوْمِ الْقِيامَةِ (١) وَلا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ [١- ٢] النفس الأمارة بالسوء هي النفس اللوامة، وهي قرينة الحرص وطول الأمل. ثم قال: إنما نهاكم الله عن القبول وعن الاغترار بالدنيا وعن مخادعة النفس، فقال تعالى: إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ [يوسف: ٥٣] وقال: فَلا تَغُرَّنَّكُمُ الْحَياةُ الدُّنْيا [لقمان: ٣٣] وقال: إِنَّ الشَّيْطانَ لَكُمْ عَدُوٌّ [فاطر: ٦] .

<span data-type="title" id=toc-612>[سورة القيامة (٧٥) : الآيات ٩ الى ١٠]</span>
وَجُمِعَ الشَّمْسُ وَالْقَمَرُ (٩) يَقُولُ الْإِنْسانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ (١٠)
قوله تعالى: وَجُمِعَ الشَّمْسُ وَالْقَمَرُ [٩] قال: باطنها القمر نور بصر عين الرأس الذي لنفس الطبع، والشمس نور بصر القلب الذي لنفس الروح والعقل، ألا تراه كيف قال:
يَقُولُ الْإِنْسانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ [١٠] أي المكذب بيوم القيامة يقول عند جمع النورين: أين المخلص من عذاب الله.

<span data-type="title" id=toc-613>[سورة القيامة (٧٥) : الآيات ٢٢ الى ٢٣]</span>
وُجُوهٌ يَوْمَئِذٍ ناضِرَةٌ (٢٢) إِلى رَبِّها ناظِرَةٌ (٢٣)
قوله تعالى: وُجُوهٌ يَوْمَئِذٍ ناضِرَةٌ (٢٢) إِلى رَبِّها ناظِرَةٌ [٢٢- ٢٣] قال: من قتله حبه فديته رؤيته. ثم قال: جزاء الأعمال الجنة، وجزاء التوحيد النظر إلى الحق عزَّ وجلَّ. وحكي عن أبي الدرداء رضي الله عنه أنه قال: سيروا للبلاء وتجهزوا للفناء واستعدوا للقاء. وكانت رابعة رضي الله عنها تقول: إلهي، إني أحب الدنيا لأذكرك فيها، وأحب الآخرة لأراك فيها.
إلهي، كل ساعة تمر علي لا يكون لساني فيها رطباً بذكرك فهي مشؤومة. إلهي، لا تجمع عليّ أمرين، فإني لا أطيقهما، الإحراق بالنار والفراق منك.

<span data-type="title" id=toc-614>[سورة القيامة (٧٥) : الآيات ٢٦ الى ٢٩]</span>
كَلاَّ إِذا بَلَغَتِ التَّراقِيَ (٢٦) وَقِيلَ مَنْ راقٍ (٢٧) وَظَنَّ أَنَّهُ الْفِراقُ (٢٨) وَالْتَفَّتِ السَّاقُ بِالسَّاقِ (٢٩)
قوله تعالى: كَلَّا إِذا بَلَغَتِ التَّراقِيَ [٢٦] يعني الحلقوم. وَقِيلَ مَنْ راقٍ
[٢٧] أي:
هل من طبيب يداوي؟ وقيل: من يصعد بروح الكافر إلى السماء. وَظَنَّ أَنَّهُ الْفِراقُ [٢٨] يقول: وعلم أنه الفراق للدنيا. وَالْتَفَّتِ السَّاقُ بِالسَّاقِ [٢٩] يقول: أمر الدنيا والآخرة. وقيل:
هما ساقاك إذا التفتا في الكفن. وقد حكي أن يعقوب عليه السلام لما أتاه البشير قال: ما أذن لي ما آتيتك اليوم، إلا أن أقول هون الله عليك سكرة الموت.
وقيل للأسود بن يزيد «١» حين احتضر: أبشر بالمغفرة. قال: فأين الحياء ممن كانت المغفرة منه؟.
_________
(١) الأسود بن يزيد بن قيس النخعي ( ... - ٧٥ هـ) : تابعي، فقيه، من الحفاظ. كان عالم الكوفة في عصره.
(الحلية ٢/ ١٠٢) .',NULL,'182',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (180,'وحكي أن أبا بكر الصديق رضي الله عنه لما احتضر جاءت عائشة رضي الله عنها فتمثلت بهذا البيت: [الطويل]
لَعمرُك ما يغني الثَّراء عن الفتى ... إذا حشرجت يوماً وضاق بها الصدرُ «١»
فكشف عن وجهه فقال: ليس كذلك، ولكن قولي: وَجاءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ [ق: ١٩] انظروا ثوبي هذين وكفنوني فيهما، فإن الحي أحوج إلى الجديد من الميت «٢» .
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-615>السورة التي يذكر فيها الإنسان</span>

<span data-type="title" id=toc-616>[سورة الإنسان (٧٦) : آية ٥]</span>
إِنَّ الْأَبْرارَ يَشْرَبُونَ مِنْ كَأْسٍ كانَ مِزاجُها كافُوراً (٥)
قوله تعالى: إِنَّ الْأَبْرارَ يَشْرَبُونَ [٥] الآية. قال: الأبرار الذين تخلقوا بخلق من أخلاق العشرة الذين شهد لهم رسول الله صلّى الله عليه وسلّم بالجنة. قيل: فما أول شيء ينبغي من الأخلاق؟
فقال: احتمال المئونة، والرفق في كل شيء، والحذر أن لا يميل في رفعه إلى هواه في هذه الخصال اكتساب العقل. ثم لا بد من ثلاثة أخرى فيها اكتساب المعرفة واستعمال العلم والحلم والتواضع، ثم لا بد من ثلاثة أخرى فيها أحكام التعبد السكينة والوقار والإنصاف. وقال: من كان فيه ثلاث خصال لم يأكل التراب جسده، كف الأذى عن الناس، ثم احتمال أذاهم، ثم اصطناع المعروف معهم.

<span data-type="title" id=toc-617>[سورة الإنسان (٧٦) : آية ٧]</span>
يُوفُونَ بِالنَّذْرِ وَيَخافُونَ يَوْماً كانَ شَرُّهُ مُسْتَطِيراً (٧)
وَيَخافُونَ يَوْماً كانَ شَرُّهُ مُسْتَطِيراً [٧] قال: البلايا والشدائد في الآخرة عامة، والسلامة منها خاص الخاص.

<span data-type="title" id=toc-618>[سورة الإنسان (٧٦) : آية ١١]</span>
فَوَقاهُمُ اللَّهُ شَرَّ ذلِكَ الْيَوْمِ وَلَقَّاهُمْ نَضْرَةً وَسُرُوراً (١١)
قوله تعالى: وَلَقَّاهُمْ نَضْرَةً وَسُرُوراً [١١] قال: نضرة في الوجوه وسروراً في القلب.

<span data-type="title" id=toc-619>[سورة الإنسان (٧٦) : آية ١٨]</span>
عَيْناً فِيها تُسَمَّى سَلْسَبِيلاً (١٨)
قوله تعالى: عَيْناً فِيها تُسَمَّى سَلْسَبِيلًا [١٨] وقال: حكي عن المسيب أنه قال: هي عين يمين العرش من قصب من ياقوت. قال سهل: نبه الله به عباده المؤمنين، ثم قال: سلوا ربكم السبيل إلى هذه العين.

<span data-type="title" id=toc-620>[سورة الإنسان (٧٦) : آية ٢١]</span>
عالِيَهُمْ ثِيابُ سُندُسٍ خُضْرٌ وَإِسْتَبْرَقٌ وَحُلُّوا أَساوِرَ مِنْ فِضَّةٍ وَسَقاهُمْ رَبُّهُمْ شَراباً طَهُوراً (٢١)
قوله تعالى: وَسَقاهُمْ رَبُّهُمْ شَراباً طَهُوراً [٢١] قال سهل: نهى الله عباده عن نجاسة خمور الدنيا بما فرق بين الطاهر والطهور، وبين خمور الجنة وخمور الدنيا نجاسة، فإن خمور الدنيا نجسة تنجس شاربها بالآثام، وخمور الجنة طهور تطهر شاربها من كل دنس، وتصلحه لمجلس القدس ومشهد العز. وصلى سهل صلاة العتمة فقرأ قوله تعالى: وَسَقاهُمْ رَبُّهُمْ شَراباً طَهُوراً [٢١] فجعل يحرك فاه كأنما يمص شيئاً، فلما فرغ من صلاته قيل له: أتشرب في الصلاة؟ فقال: والله لو لم أجد لذته عند قراءته كأني عند شربه به ما فعلت ذلك.
والله سبحانه وتعالى أعلم.
_________
(١) البيت لحاتم الطائي في ديوانه ص ١٩٩.
(٢) تفسير القرطبي ١٧/ ١٢- ١٣ والكامل في التاريخ ٢/ ٢٧٠.',NULL,'183',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (181,'<span data-type="title" id=toc-621>السورة التي يذكر فيها المرسلات</span>

<span data-type="title" id=toc-622>[سورة المرسلات (٧٧) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَالْمُرْسَلاتِ عُرْفاً (١)
قوله تعالى: وَالْمُرْسَلاتِ عُرْفاً [١] يعني الملائكة أرسلوا بالمعروف من أمره. قال:
وباطنها أرواح المؤمنين ترسل إلهاماً موافقا للكتاب والسنة.

<span data-type="title" id=toc-623>[سورة المرسلات (٧٧) : الآيات ٣ الى ٦]</span>
وَالنَّاشِراتِ نَشْراً (٣) فَالْفارِقاتِ فَرْقاً (٤) فَالْمُلْقِياتِ ذِكْراً (٥) عُذْراً أَوْ نُذْراً (٦)
وَالنَّاشِراتِ نَشْراً [٣] ما يطهر الأعمال الصالحة منها.
فَالْفارِقاتِ فَرْقاً [٤] بين الحق والباطل والسنة والبدعة.
فَالْمُلْقِياتِ ذِكْراً [٥] وهو الوحي إلهاماً يلقيه نفس الروح والعقل والقلب على نفس الطبع وهو الذكر الخفي.
عُذْراً أَوْ نُذْراً [٦] عذر الله تعالى من الظلم على ما خالف به الكتاب والسنة أو نذراً لخلقه من عذابه فأقسم الله تعالى بها على كون القيامة.

<span data-type="title" id=toc-624>[سورة المرسلات (٧٧) : آية ١٥]</span>
وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ (١٥)
قوله تعالى: وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ [١٥] قال: الويل يومئذ لمن ادعى من غير حقيقة، فكذبته دعواه على رؤوس الأشهاد، وذلك حين الافتضاح.

<span data-type="title" id=toc-625>[سورة المرسلات (٧٧) : آية ٣٥]</span>
هذا يَوْمُ لاَ يَنْطِقُونَ (٣٥)
قوله تعالى: هذا يَوْمُ لاَ يَنْطِقُونَ [٣٥] قال: لا ينطق أحد عن نفسه بحجة، إلا بإظهار العجز والعبودية والتزام المخالفات والجرائم.

<span data-type="title" id=toc-626>[سورة المرسلات (٧٧) : آية ٤٦]</span>
كُلُوا وَتَمَتَّعُوا قَلِيلاً إِنَّكُمْ مُجْرِمُونَ (٤٦)
قوله تعالى: كُلُوا وَتَمَتَّعُوا قَلِيلًا إِنَّكُمْ مُجْرِمُونَ [٤٦] قال: من كانت همته بطنه وفرجه فقد أظهر خسارته، قال الله تعالى: كُلُوا وَتَمَتَّعُوا قَلِيلًا إِنَّكُمْ مُجْرِمُونَ [٤٦] .
والله سبحانه وتعالى أعلم.',NULL,'184',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (182,'<span data-type="title" id=toc-627>السورة التي يذكر فيها النبأ</span>

<span data-type="title" id=toc-628>[سورة النبإ (٧٨) : آية ١١]</span>
وَجَعَلْنَا النَّهارَ مَعاشاً (١١)
قوله تعالى: وَجَعَلْنَا النَّهارَ مَعاشاً [١١] أي أنوار القلوب وتنويرها بذكرنا معاشاً لنفس الروح والعقل، مثل عيش الملائكة، فأما العيش الآخر فهو طريق العوام. ثم قال: ليس من أخلاق المؤمن التذلل عند الفاقة، وقبيح بالفقراء يلبسون الخلقان وهموم الأرزاق في قلوبهم، وإنما أصل هذه الأمور ثلاث: السكون إلى الله جل وعز، والهرب من الخلق، وقلة الأذى. ولقد كان عامر بن عبد قيس يقول إذا أصبح: اللهم إن الناس قد انتشروا لحوائجهم، وإن حاجتي أن تغفر لي «١» .

<span data-type="title" id=toc-629>[سورة النبإ (٧٨) : آية ٢٦]</span>
جَزاءً وِفاقاً (٢٦)
قوله عزَّ وجلَّ: جَزاءً وِفاقاً [٢٦] قال: وافق عذاب النار الشرك لأنهما عظيمان، فلا عذاب أعظم من الشرك.

<span data-type="title" id=toc-630>[سورة النبإ (٧٨) : الآيات ٣٣ الى ٣٤]</span>
وَكَواعِبَ أَتْراباً (٣٣) وَكَأْساً دِهاقاً (٣٤)
قوله تعالى: وَكَواعِبَ أَتْراباً [٣٣] قال: يعني الجواري القينات أتراباً مستويات على ميلاد واحد.
قوله تعالى: وَكَأْساً دِهاقاً [٣٤] أي مملوءه متتابعة. ولقي حكيماً حكيمٌ بالموصل فقال: تشتاق إلى الحور العين؟ فقال: ألا أشتاق إليهن، فإن نور وجوههن من نور الله تعالى عزَّ وجلَّ، فغشي عليه، فحمل إلى منزله، فكان الناس يعودونه شهراً. وقد قال ابن عباس رضي الله عنهما: لو أن جارية منهن بصقت في سبعة أبحر، لكانت الأبحر أحلى من العسل «٢» .
والله سبحانه وتعالى أعلم.
_________
(١) كتاب الزهد لابن أبي عاصم ص ٢٢٥.
(٢) الترغيب والترهيب ٤/ ٢٩٩ (رقم ٥٧١٧) .',NULL,'185',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (183,'<span data-type="title" id=toc-631>السورة التي يذكر فيها الحافرة (النازعات)</span>

<span data-type="title" id=toc-632>[سورة النازعات (٧٩) : آية ٤]</span>
فَالسَّابِقاتِ سَبْقاً (٤)
قوله تعالى: فَالسَّابِقاتِ سَبْقاً [٤] قال: يعني أرواح المؤمنين سبقت بالخير والموافقة فسبقت إلى ملك الموت بالإجابة، شوقاً إلى ربها فخرجت في أطيب ريح وأكمل سرور.

<span data-type="title" id=toc-633>[سورة النازعات (٧٩) : آية ١٦]</span>
إِذْ ناداهُ رَبُّهُ بِالْوادِ الْمُقَدَّسِ طُوىً (١٦)
قوله تعالى: إِذْ ناداهُ رَبُّهُ بِالْوادِ الْمُقَدَّسِ طُوىً [١٦] قال: جوّع موسى نفسه طاوياً عابداً لله تعالى، ثم ناداه ربه ليكون إليه أبلغ.

<span data-type="title" id=toc-634>[سورة النازعات (٧٩) : الآيات ٣٧ الى ٣٨]</span>
فَأَمَّا مَنْ طَغى (٣٧) وَآثَرَ الْحَياةَ الدُّنْيا (٣٨)
قوله تعالى: فَأَمَّا مَنْ طَغى (٣٧) وَآثَرَ الْحَياةَ الدُّنْيا [٣٧- ٣٨] أي قال: جحد حقوق الله وكفر نعمته، وآثر الحياة الدنيا اتباعاً في طلب الشهوات ومتابعة المراد، ثم قال: ما طلعت شمس ولا غربت على أحد إلا وهو جاهل، إلا من يؤثر الله تعالى على نفسه وروحه ودنياه وآخرته. قيل: ما علامة بغض الدنيا؟ قال: أن تهون عليه المصائب، حتى نفسه وولده، كما قال مسلم بن يسار «١» حين مات ولده: يا بني، شغلني الحزن لك عن الحزن عليك، اللهم إني قد جعلت ثوابك لي عليه له «٢» ، والثاني يهون عليه نعيم الدنيا ولو روحه، والثالث لا يكون شيء أقرب إليه من الله عزَّ وجلَّ، كقول عامر بن عبد القيس: ما نظرت إلى شيء إلا رأيت الله أقرب إليه مني «٣» .

<span data-type="title" id=toc-635>[سورة النازعات (٧٩) : آية ٤٠]</span>
وَأَمَّا مَنْ خافَ مَقامَ رَبِّهِ وَنَهَى النَّفْسَ عَنِ الْهَوى (٤٠)
قوله تعالى: وَأَمَّا مَنْ خافَ مَقامَ رَبِّهِ وَنَهَى النَّفْسَ عَنِ الْهَوى [٤٠] قال: لا يسلم من الهوى إلا نبي وبعض الصديقين ليس كلهم، وإنما يسلم من الهوى من ألزم نفسه الأدب، وليس يصفو الأدب إلا للأنبياء عليهم الصلاة والسلام وبعض الصديقين، وكذلك الأخلاق.
وخرج ابن السماك «٤» يوماً إلى أصحابه وقد اجتمعوا إليه فقال لهم: قد كثرت عظاتي لكم، تريدون دوائي لكم، قالوا: نعم. قال: خالفوا أهواءكم، والله سبحانه وتعالى أعلم.
_________
(١) مسلم بن يسار الأموي بالولاء ( ... - ١٠٨ هـ) : فقيه، ناسك، من رجال الحديث. أصله من مكة.
سكن البصرة، فكان مفتيها، وتوفي فيها. (الحلية ٢/ ٢٩٠) .
(٢) نسب هذا القول إلى عمر بن ذر لما دفن ابنه ذر بن عمر، انظر قوله في الحلية ٥/ ١٠٨- ١٠٩ وتهذيب الكمال ٢١/ ٣٣٨.
(٣) نوادر الأصول ٤/ ٧٤، وفيه (منه) مكان (إليه مني) ، وفيه أيضا أن محمد بن واسع قال: (ما نظرت إلى شيء إلا رأيت الله فيه) .
(٤) محمد بن صبيح بن السماك ( ... - ١٨٣ هـ) : الواعظ، الكوفي. كان في زمن هارون الرشيد. روى عنه الأعمش، وروى عنه الإمام أحمد. (الحلية ٨/ ٢٠٣ وسير أعلام النبلاء ٨/ ٣٢٨) .',NULL,'186',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (184,'<span data-type="title" id=toc-636>السورة التي يذكر فيها عبس</span>

<span data-type="title" id=toc-637>[سورة عبس (٨٠) : الآيات ٢١ الى ٢٢]</span>
ثُمَّ أَماتَهُ فَأَقْبَرَهُ (٢١) ثُمَّ إِذا شاءَ أَنْشَرَهُ (٢٢)
قوله تعالى: ثُمَّ أَماتَهُ فَأَقْبَرَهُ [٢١] قال: باطنها أمات منه حظوظ نفسه من الشهوة، فأقبره في نفسه، ثُمَّ إِذا شاءَ أَنْشَرَهُ [٢٢] قريناً بالحكمة، مشاهداً لله منقطعا عمن سواه.

<span data-type="title" id=toc-638>[سورة عبس (٨٠) : الآيات ٢٥ الى ٢٦]</span>
أَنَّا صَبَبْنَا الْماءَ صَبًّا (٢٥) ثُمَّ شَقَقْنَا الْأَرْضَ شَقًّا (٢٦)
قوله تعالى: أَنَّا صَبَبْنَا الْماءَ صَبًّا [٢٥] قال: صب من لطف معانيه ماء ثُمَّ شَقَقْنَا الْأَرْضَ [٢٦] وهو القلب شَقًّا [٢٦] ، فأنبت فيها من ألوان الزهرة روحاً وعقلاً وإيماناً ومعرفة، كما قال رسول الله صلّى الله عليه وسلّم: «ألا إن القرآن زهرة في القلوب، ألا وإن الإيمان يزرع في القلب الغنى، كما يزرع المطر الزهرات، ألا وإن الشح يزرع في القلب النفاق، كما يزرع الندى العشب» .

<span data-type="title" id=toc-639>[سورة عبس (٨٠) : آية ٣٤]</span>
يَوْمَ يَفِرُّ الْمَرْءُ مِنْ أَخِيهِ (٣٤)
قوله عزَّ وجلَّ: يَوْمَ يَفِرُّ الْمَرْءُ مِنْ أَخِيهِ [٣٤] هابيل من قابيل، وسيدنا محمد صلّى الله عليه وسلّم من عمه، وإبراهيم من أبيه، ولوط عليه السلام من امرأته، ونوح من ولده.

<span data-type="title" id=toc-640>[سورة عبس (٨٠) : آية ٣٧]</span>
لِكُلِّ امْرِئٍ مِنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ (٣٧)
لِكُلِّ امْرِئٍ مِنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ [٣٧] يشغله عن الناس كافة، إلا عن نفسه. والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-641>السورة التي يذكر فيها التكوير</span>
قال سهل: حكى محمد بن سوار عن ابن عمر رضي الله عنهم عن النبي صلى الله عليه وسلّم أنه قال:
«من أراد أن ينظر إلى القيامة رأي العين فليقرأ: إِذَا الشَّمْسُ كُوِّرَتْ [١] ، وإِذَا السَّماءُ انْفَطَرَتْ [الانفطار: ١] ، وإِذَا السَّماءُ انْشَقَّتْ [الانشقاق: ١] » .

<span data-type="title" id=toc-642>[سورة التكوير (٨١) : آية ١٤]</span>
عَلِمَتْ نَفْسٌ مَّآ أَحْضَرَتْ (١٤)
قوله تعالى: عَلِمَتْ نَفْسٌ مَّآ أَحْضَرَتْ [١٤] أيقنت كل نفس أن ما اجتهدت فيه لا يصلح لذلك المشهد، وأن من أكرم بخلع الفضل نجا، ومن قرن بجزاء أعماله خاب.
قوله تعالى: وَإِذَا النُّفُوسُ زُوِّجَتْ [٧] قيل: زوجت نفوس المؤمنين بالحور العين، وزوجت نفوس الكفار بالشياطين، قد قرن بين الكافر والشيطان في سلسلة واحدة. وفي الآية تحذير من قرناء السوء. قال سهل: قرن بين نفس الطبع ونفس الروح، فامتزجا في نعيم الجنة، كما كانا في الدنيا مؤتلفين على إدامة الذكر وإقامة الشكر.

<span data-type="title" id=toc-643>[سورة التكوير (٨١) : الآيات ٢٦ الى ٢٨]</span>
فَأَيْنَ تَذْهَبُونَ (٢٦) إِنْ هُوَ إِلاَّ ذِكْرٌ لِلْعالَمِينَ (٢٧) لِمَنْ شاءَ مِنْكُمْ أَنْ يَسْتَقِيمَ (٢٨)
قوله تعالى: فَأَيْنَ تَذْهَبُونَ [٢٦] عن كتابه بعد البيان الذي أتاكم. إِنْ هُوَ إِلَّا ذِكْرٌ لِلْعالَمِينَ [٢٧] قال: ذكر هذا خصوص لمن كان من العالمين عالماً بالذكر منقاداً للشريعة، ألا ترى كيف قال الله تعالى: لِمَنْ شاءَ مِنْكُمْ أَنْ يَسْتَقِيمَ [٢٨] على الطرق إليه بالإيمان به، ولا تصح لكم تلك الاستقامة في الأصل والفرع إلا بمشيئتي السابقة فيكم.
والله سبحانه وتعالى أعلم.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (185,'<span data-type="title" id=toc-644>السورة التي يذكر فيها الانفطار</span>

<span data-type="title" id=toc-645>[سورة الانفطار (٨٢) : الآيات ٥ الى ٦]</span>
عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَأَخَّرَتْ (٥) يا أَيُّهَا الْإِنْسانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيمِ (٦)
قوله تعالى: عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَأَخَّرَتْ [٥] أي ما قدمت من خير أو شر، وأخرت من سيئة سنتها واقتدى بها فيها.
قوله تعالى: يا أَيُّهَا الْإِنْسانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيمِ [٦] قال: أي ما غرك بدونه فقطعك عنه مع لطفه وكرمه.
قيل له: ما القاطع؟ قال: العبد لله والله لعبده، وليس شيء أقرب إليه من قلب المؤمن، فإذا حضر الغير فيه فهو الحجاب، ومن نظر إلى الله بقلبه بَعُدَ عن كل شيء دونه، ومن طلب مرضاته أرضاه بحلمه، ومن أسلم إلى الله تعالى قلبه تولى جوارحه فاستقامت، وإنما شهدت قلوبهم على قدر ما حفظوا من الجوارح.
ثم قال: ألزموا قلوبكم، نحن مخلوقون وخالقنا معنا، ولا تملوا من أعمالكم، فإن الله شاهدكم حيثما كنتم، وأنزلوا به حاجاتكم، وموتوا على بابه، وقولوا: نحن جهال وعالمنا معنا، ونحن ضعفاء ومقوينا معنا، ونحن عاجزون وقادرنا معنا، فإن من لزمها كان الهواء والفضاء والأرض والسماء عنده سواء.
وقال عمر بن واصل تلميذ سهل: إذا قرأ هذه الآية قال: غرني الجهل بترك العصمة منك.

<span data-type="title" id=toc-646>[سورة الانفطار (٨٢) : آية ١٣]</span>
إِنَّ الْأَبْرارَ لَفِي نَعِيمٍ (١٣)
قوله عزَّ وجلَّ: إِنَّ الْأَبْرارَ لَفِي نَعِيمٍ [١٣] قال: نعيم الخاص من عباده، وهم الأبرار، لقاؤه ومشاهدته، كما كان نعيمهم في الدنيا مشاهدته وقربه.
والله سبحانه وتعالى أعلم.',NULL,'188',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (186,'<span data-type="title" id=toc-647>السورة التي يذكر فيها المطففون</span>

<span data-type="title" id=toc-648>[سورة المطففين (٨٣) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَيْلٌ لِلْمُطَفِّفِينَ (١)
قوله تعالى: وَيْلٌ لِلْمُطَفِّفِينَ [١] قال: هم المنافقون ومن تخلق بأخلاقهم، يطففون في صلاتهم، كما قال سليمان رضي الله عنه: الصلاة مكيال، فمن وفى وفي له، ومن طفف فقد علمتم ما قال الله تعالى في حق المطففين: أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ أَنْفُسَكُمْ [البقرة: ٤٤] وتغمزونهم على ما عثروا عليه من عيوب الناس، وترتكبون مثلها وأفظع منها «١» . ولا يطلع على عثرات الخلق إلا مخطئ جاهل، ولا يهتك سر ما اطلع عليه إلا ملعون «٢» . ولقد حكي أن الله تعالى أوحى إلى داود عليه السلام: أشكو إليك عبادي يا داود. فقال: ولم يا رب؟ قال: لأنهم يذنبون في السر ويتوبون في العلانية، وإني لا أريد أن يطلع غيري على ذنب عبدي.

<span data-type="title" id=toc-649>[سورة المطففين (٨٣) : آية ١٥]</span>
كَلاَّ إِنَّهُمْ عَنْ رَبِّهِمْ يَوْمَئِذٍ لَمَحْجُوبُونَ (١٥)
وقال عمر ابن واصل: سألت سهلاً عن قوله تعالى: كَلَّا إِنَّهُمْ عَنْ رَبِّهِمْ يَوْمَئِذٍ لَمَحْجُوبُونَ [١٥] قال:
هم في الدنيا محجوبون عن الآمر والزاجر، كما روي في الخبر: طوبى لمن كان له من قلبه واعظ، ومن قلبه زاجر، فإذا أراد الله فيه أمراً غيب معناه عنه، وهم في الآخرة محجوبون عن الرحمة، والنظر إلى الله عزَّ وجلَّ، وعن نظره إليهم بالرضا والرضوان عند مناقشته إياهم، كما قال:
وَقِفُوهُمْ إِنَّهُمْ مَسْؤُلُونَ [الصافات: ٢٤] عن الدنيا فتلزمهم الحجة فيدخلهم النار، ثم يفتح للمؤمنين مناظر إليهم فينظرون إليهم وهم يحرقون بالنار، ويعذبون بألوان عذابها، فتقر أعينهم فيضحكون منهم، كما ضحكوا في الدنيا من المؤمنين، ثم تسد المناظر، وتطبق عليهم، فعند ذلك بمحو الله أسماءهم، ويخرج ذكرهم من قلوب المؤمنين ويقول: هَلْ ثُوِّبَ الْكُفَّارُ مَا كانُوا يَفْعَلُونَ [٣٦] وفيها دلالة بينة على إثبات الرؤية للمؤمنين خاصة.

<span data-type="title" id=toc-650>[سورة المطففين (٨٣) : آية ١٨]</span>
كَلاَّ إِنَّ كِتابَ الْأَبْرارِ لَفِي عِلِّيِّينَ (١٨)
قوله تعالى: كَلَّا إِنَّ كِتابَ الْأَبْرارِ لَفِي عِلِّيِّينَ [١٨] قال: الكتاب ظاهره في الآيتين جميعاً أعمال الخير والشر، وباطنه أرواح المؤمنين وأرواح الكافرين، تجمع أرواح المؤمنين عند سدرة المنتهى، في حواصل طير خضر ترتع في الجنة إلى يوم القيامة، مرقوم بالرضا والرضوان، وتجمع أرواح الكفار في سجّين تحت الأرض السفلى، تحت خد إبليس لعنه الله، مرقوم بالعداوة والبغضاء.
والله سبحانه وتعالى أعلم.
_________
(١) سنن البيهقي الكبرى ٢/ ٢٩١ (رقم ٣٤٠١) ومصنف ابن أبي شيبة ١/ ٢٥٩ (رقم ٢٩٧٩) وشعب الإيمان ٣/ ١٤٧ (٣١٥٠) .
(٢) الحلية ١٠/ ١٩٩. [.....]',NULL,'189',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (187,'<span data-type="title" id=toc-651>السورة التي يذكر فيها الانشقاق</span>

<span data-type="title" id=toc-652>[سورة الانشقاق (٨٤) : آية ٢]</span>
وَأَذِنَتْ لِرَبِّها وَحُقَّتْ (٢)
قوله تعالى: وَأَذِنَتْ لِرَبِّها وَحُقَّتْ [٢] أي سمعت لربها وأجابت بالامتثال بأمره وحق لها أن تفعل.

<span data-type="title" id=toc-653>[سورة الانشقاق (٨٤) : الآيات ٦ الى ٩]</span>
يا أَيُّهَا الْإِنْسانُ إِنَّكَ كادِحٌ إِلى رَبِّكَ كَدْحاً فَمُلاقِيهِ (٦) فَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ (٧) فَسَوْفَ يُحاسَبُ حِساباً يَسِيراً (٨) وَيَنْقَلِبُ إِلى أَهْلِهِ مَسْرُوراً (٩)
يا أَيُّهَا الْإِنْسانُ إِنَّكَ كادِحٌ إِلى رَبِّكَ كَدْحاً [٦] أي ساعٍ بعملك إلى ربك سعياً فَمُلاقِيهِ [٦] بسعيك فانظر في سعيك يصلح للجنة ولقربه أم للنار وبعده. وقد قال عمارة ابن زاذان «١» : قال لي كهمس: يا أبا سلمة أذنبت ذنباً فأنا أبكي عليه منذ أربعين سنة. قلت: ما هو يا أبا عبد الله؟ قال: زارني أخ لي فاشتريت له سمكاً مشوياً بدانق، فلما أكل قمت إلى حائط جاري، فأخذت منه قطعة، فغسل بها يده، فأنا أبكي عليه منذ أربعين سنة «٢» .
قوله تعالى: فَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ (٧) فَسَوْفَ يُحاسَبُ حِساباً يَسِيراً [٧- ٨] أي نغفر ذنوبه فلا نحاسبه بها، كما روي في الخبر أن الله تعالى إذا أراد أن يستر على عبد يوم القيامة أراه ذنوبه فيما بينه وبينه، ثم غفرها له.
وَيَنْقَلِبُ إِلى أَهْلِهِ مَسْرُوراً [٩] في الجنة بتحقيق ميعاد اللقاء، وبما نال من الرضا.
واعلم أن الله له عباد لا يوقفون مواقفة، ولا يحسون بهول من أهوال يوم القيامة من الحساب والسؤال والصراط، لأنهم له وبه، لا يعرفون شيئاً سواه، ولا لهم دونه اختيار.

<span data-type="title" id=toc-654>[سورة الانشقاق (٨٤) : آية ١٩]</span>
لَتَرْكَبُنَّ طَبَقاً عَنْ طَبَقٍ (١٩)
قوله عزَّ وجلَّ: لَتَرْكَبُنَّ طَبَقاً عَنْ طَبَقٍ [١٩] قال: باطنها لترفعن درجة فوق درجة في الجنة، ولتحولن من حال إلى حال أشرف منها وأسر، كما كنتم في الدنيا ترفعون من درجة إلى درجة أعلى منها، من طمع وخوف وشوق ومحبة.
والله سبحانه وتعالى أعلم.
_________
(١) عمارة بن زاذان الصيدلاني، أبو سلمة البصري: روى عن الحسن البصري ومكحول. (تهذيب التهذيب ٧/ ٣٦٥) .
(٢) الحلية ٦/ ٢١١.',NULL,'190',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (188,'<span data-type="title" id=toc-655>السورة التي يذكر فيها البروج</span>

<span data-type="title" id=toc-656>[سورة البروج (٨٥) : آية ٣]</span>
وَشاهِدٍ وَمَشْهُودٍ (٣)
قوله تعالى: وَشاهِدٍ وَمَشْهُودٍ [٣] قال: قيل الشاهد الملك، كما قال: سائِقٌ وَشَهِيدٌ [ق: ٢١] ، والمشهود يوم القيامة، وذلك يوم القيامة، فقال ابن عباس رضي الله عنهما: الشاهد محمد صلّى الله عليه وسلّم، والمشهود القرآن. وقيل: المشهود الإنسان. وقال سهل: الشاهد نفس الروح، والمشهود نفس الطبع، لأن نفس الطبع مع فهم العقل وفطنة القلب على كل واحد منهما شاهد، والله على الكل شهيد.

<span data-type="title" id=toc-657>[سورة البروج (٨٥) : آية ١٤]</span>
وَهُوَ الْغَفُورُ الْوَدُودُ (١٤)
قوله عزَّ وجلَّ: وَهُوَ الْغَفُورُ الْوَدُودُ [١٤] يعني الغفور للمذنبين، الودود للمغفرة، المتودد المتحبب إلى عباده، بما أولاهم من سابغ نعمه، وجميل آلائه وإحسانه.

<span data-type="title" id=toc-658>[سورة البروج (٨٥) : آية ٢٢]</span>
فِي لَوْحٍ مَحْفُوظٍ (٢٢)
قوله تعالى: فِي لَوْحٍ مَحْفُوظٍ [٢٢] قال: المحفوظ صدر المؤمن، محفوظ عليه أن يناله غير أهله، لأن أهل القرآن هم أهل الله وخاصته، والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-659>السورة التي يذكر فيها الطارق</span>

<span data-type="title" id=toc-660>[سورة الطارق (٨٦) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَالسَّماءِ وَالطَّارِقِ (١)
قوله تعالى: وَالسَّماءِ وَالطَّارِقِ [١] قال: السماء في اللغة السمو والعلو، فباطنها روح محمد صلّى الله عليه وسلّم قائم عند رب العزة والطارق.

<span data-type="title" id=toc-661>[سورة الطارق (٨٦) : الآيات ٣ الى ٤]</span>
النَّجْمُ الثَّاقِبُ (٣) إِنْ كُلُّ نَفْسٍ لَمَّا عَلَيْها حافِظٌ (٤)
النَّجْمُ الثَّاقِبُ [٣] وهو قلبه، يعني مشرق بتوحيد الله وتنزيهه ومداومة الأذكار ومشاهدة الجبار. وقال مرة أخرى: الثاقب قلب المؤمن، يعني مشرق مطهر عن كل شك وريب جرت عليه من وساوس العدو ونفس الطبع.
قوله تعالى: إِنْ كُلُّ نَفْسٍ لَمَّا عَلَيْها حافِظٌ [٤] أي على نفس الطبع حافظ من عصمة الله.

<span data-type="title" id=toc-662>[سورة الطارق (٨٦) : الآيات ٩ الى ١٢]</span>
يَوْمَ تُبْلَى السَّرائِرُ (٩) فَما لَهُ مِنْ قُوَّةٍ وَلا ناصِرٍ (١٠) وَالسَّماءِ ذاتِ الرَّجْعِ (١١) وَالْأَرْضِ ذاتِ الصَّدْعِ (١٢)
قوله تعالى: يَوْمَ تُبْلَى السَّرائِرُ (٩) فَما لَهُ مِنْ قُوَّةٍ وَلا ناصِرٍ [٩- ١٠] قال: أي يكشف عن النيات التي بها تعبدهم الله فيما فرض عليهم ونهاهم عنه، فإن أعمال العباد يوم القيامة موقوفة على مقاصدهم. ولقد كان الربيع يقول: السرائر التي تخفى على الناس، وهي لله بواد، التمسوا دواءهن. ثم يقول: وما دواؤهن؟ هو أن يتوب ثم لا يعود «١» . ثم قال سهل: آلة الفقير ثلاثة أشياء: أداء فرضه وصيانة فقره وحفظ سره.
قوله تعالى: وَالسَّماءِ ذاتِ الرَّجْعِ [١١] قال: ظاهرها ذات الرجع بالمطر بعد المطر.
وَالْأَرْضِ ذاتِ الصَّدْعِ [١٢] بالنبات، وباطنها القلب يرجع بالندم بعد الذنب، وَالْأَرْضِ ذاتِ الصَّدْعِ [١٢] الأرض تنصدع من الموافقات بالأفعال والأقوال.

<span data-type="title" id=toc-663>[سورة الطارق (٨٦) : آية ١٦]</span>
وَأَكِيدُ كَيْداً (١٦)
قوله تعالى: وَأَكِيدُ كَيْداً [١٦] قال: كيده بهم في الدنيا الاستدراج والاغترار، وبالآخرة الحسرة عند نظرهم إلى إكرام الموحدين وإعزازهم، والله سبحانه وتعالى أعلم.
_________
(١) شعب الإيمان ٥/ ٤٥٩ (رقم ٧٢٨٢) وسير أعلام النبلاء ٤/ ٢٥٩.',NULL,'191',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (189,'<span data-type="title" id=toc-664>السورة التي يذكر فيها الأعلى جلّ وعلا</span>

<span data-type="title" id=toc-665>[سورة الأعلى (٨٧) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
سَبِّحِ اسْمَ رَبِّكَ الْأَعْلَى (١)
قوله تعالى: سَبِّحِ اسْمَ رَبِّكَ الْأَعْلَى [١] قال: هو تنزيهه عن الأضداد والأنداد في الظاهر، وفي الباطن مشاهدته بالذكر في الصلاة دون مشاهدة غيره.

<span data-type="title" id=toc-666>[سورة الأعلى (٨٧) : آية ٣]</span>
وَالَّذِي قَدَّرَ فَهَدى (٣)
قوله تعالى: قَدَّرَ فَهَدى [٣] قال: قدر عليهم الشقاوة والسعادة، ثم تولى أهل السعادة، ووكل أهل الشقاوة إلى أنفسهم، قال: والهدى هدايان: أحدهما البيان، والآخر التولي من الله تعالى، ألا ترون كيف يهتدي إلى سبب معاشه إلى ثدي أمه لتولي الله إياه وإلهامه إياه.

<span data-type="title" id=toc-667>[سورة الأعلى (٨٧) : آية ١٤]</span>
قَدْ أَفْلَحَ مَنْ تَزَكَّى (١٤)
قوله تعالى: قَدْ أَفْلَحَ مَنْ تَزَكَّى [١٤] قال: أي فاز وسعد من اتقى الله في السر والعلانية.

<span data-type="title" id=toc-668>[سورة الأعلى (٨٧) : آية ١٦]</span>
بَلْ تُؤْثِرُونَ الْحَياةَ الدُّنْيا (١٦)
قوله تعالى: بَلْ تُؤْثِرُونَ الْحَياةَ الدُّنْيا [١٦] قال: ما ينبغي للمؤمن أن يكون في الدنيا إلا كمثل رجل ركب خشبة في البحر، وهو يقول: يا رب يا رب، لعل أن ينجيه منها، وما من عبد مؤمن زهد في الدنيا إلا وكّل الله به ملكاً حكيماً يغرس في قلبه أنواع الحكم، كما يغرس أهل الدنيا في بساتينهم من طرف الأشجار، والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-669>السورة التي يذكر فيها الغاشية</span>

<span data-type="title" id=toc-670>[سورة الغاشية (٨٨) : الآيات ٢ الى ٣]</span>
وُجُوهٌ يَوْمَئِذٍ خاشِعَةٌ (٢) عامِلَةٌ ناصِبَةٌ (٣)
قوله تعالى: وُجُوهٌ يَوْمَئِذٍ خاشِعَةٌ [٢] أي ذليلة، لأن الله تعالى أمرها أن تخشع وتذل وتفتقر إليه في الدنيا، فلم تفعل، فأذلها في الآخرة بالذلة الباقية.
قوله تعالى: عامِلَةٌ ناصِبَةٌ [٣] أي عاملة في الدنيا بأنواع البدع والضلالات، ناصبة في الآخرة بالعذاب في الدركات.

<span data-type="title" id=toc-671>[سورة الغاشية (٨٨) : آية ٥]</span>
تُسْقى مِنْ عَيْنٍ آنِيَةٍ (٥)
تُسْقى مِنْ عَيْنٍ آنِيَةٍ [٥] أي من عين صديد قد تناهى حرها كما قال: «حميم» أي قد بلغ في الحر منتهاه.

<span data-type="title" id=toc-672>[سورة الغاشية (٨٨) : الآيات ٨ الى ٩]</span>
وُجُوهٌ يَوْمَئِذٍ ناعِمَةٌ (٨) لِسَعْيِها راضِيَةٌ (٩)
قوله تعالى: وُجُوهٌ يَوْمَئِذٍ ناعِمَةٌ [٨] أي نعمة وكرامة. لِسَعْيِها راضِيَةٌ [٩] في الآخرة.

<span data-type="title" id=toc-673>[سورة الغاشية (٨٨) : الآيات ١٢ الى ١٣]</span>
فِيها عَيْنٌ جارِيَةٌ (١٢) فِيها سُرُرٌ مَرْفُوعَةٌ (١٣)
قوله تعالى: فِيها عَيْنٌ جارِيَةٌ [١٢] أي مطردة في عين أخدود. فِيها سُرُرٌ مَرْفُوعَةٌ [١٣] يعني الفرش مرفوعة، على كل سرير سبعون فراشاً، كل فراش في ارتفاع غرفة من غرف الدنيا. قال سهل: ذكر الله تعالى هذه النعم ليرغبهم فيها، ويحذرهم عقوبته على قدر سلطانه، وكرامته على قدر عظيم شأنه وسلطانه، فلم ينجع ذلك في قلوب كفار مكة فذكر قدرته كي يعتبروا،

<span data-type="title" id=toc-674>[سورة الغاشية (٨٨) : آية ١٧]</span>
أَفَلا يَنْظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ (١٧)
فقال تعالى: أَفَلا يَنْظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ [١٧] وهو في الباطن أمر للمؤمنين بالتذلل والافتقار إليه، فقال: انظروا إلى الإبل كيف خلقت، مع خلقتها وقوتها كيف تنقاد لصبي يقودها فلا يكون لها تحير ولا لها دونها اختيار، فلا تعجز أن تكون لربك كالإبل لصاحبها، ولهذا قال الرسول صلّى الله عليه وسلّم: «كن لربك كالجمل الأنف» «١» ، يعني المطاوع، والله سبحانه وتعالى أعلم.
_________
(١) في المستدرك على الصحيحين ١/ ١٣٠: (إن المؤمن كالجمل الأنف، حيثما انقيد انقاد) .',NULL,'192',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (190,'<span data-type="title" id=toc-675>السورة التي يذكر فيها الفجر</span>

<span data-type="title" id=toc-676>[سورة الفجر (٨٩) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَالْفَجْرِ (١) وَلَيالٍ عَشْرٍ (٢) وَالشَّفْعِ وَالْوَتْرِ (٣)
قوله تعالى: وَالْفَجْرِ [١] قال: ظاهرها الفجر الصبح.
وَلَيالٍ عَشْرٍ [٢] قال: يعني عشر ذي الحجة وهي الأيام المعلومات.
وَالشَّفْعِ [٣] آدم وحواء وقيل جميع ما خلق الله من الأضداد، الليل والنهار والنور والظلمة والموت والحياة.

<span data-type="title" id=toc-677>[سورة الفجر (٨٩) : الآيات ٣ الى ٤]</span>
وَالشَّفْعِ وَالْوَتْرِ (٣) وَاللَّيْلِ إِذا يَسْرِ (٤)
وَالْوَتْرِ [٣] هو الله تعالى.
وَاللَّيْلِ إِذا يَسْرِ [٤] ليلة الجمع تذهب بما فيها قال: باطنها والفجر محمد صلّى الله عليه وسلّم منه تفجرت أنوار الإيمان وأنوار الطاعات وأنوار الكونين.
وَلَيالٍ عَشْرٍ [٢] العشرة من أصحابه الذين شهد لهم بالجنة. وَالشَّفْعِ [٣] الفرض والسنة. وَالْوَتْرِ [٣] نية الإخلاص لله تعالى في الطاعات دون رؤية غيره فيهان.
وَاللَّيْلِ إِذا يَسْرِ [٤] أمته وذلك السواد الأعظم كما قال صلّى الله عليه وسلّم: «ليلة أسري بي رأيت سواداً عظيماً ما بين السماء والأرض فقلت: ما هذا السواد يا جبريل؟ قال: هذه أمتك ولك سبعون ألفاً يدخلون الجنة بغير حساب، لم تكلمهم الخطايا، ولم يدنسوا بالدنيا لا يعرفون إلا الله» «١» ، فأقسم الله به وبأصحابه وبأمته.

<span data-type="title" id=toc-678>[سورة الفجر (٨٩) : الآيات ١٤ الى ١٦]</span>
إِنَّ رَبَّكَ لَبِالْمِرْصادِ (١٤) فَأَمَّا الْإِنْسانُ إِذا مَا ابْتَلاهُ رَبُّهُ فَأَكْرَمَهُ وَنَعَّمَهُ فَيَقُولُ رَبِّي أَكْرَمَنِ (١٥) وَأَمَّا إِذا مَا ابْتَلاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبِّي أَهانَنِ (١٦)
وجواب القسم: إِنَّ رَبَّكَ لَبِالْمِرْصادِ [١٤] يعني طريق الكل عليه يجازيهم بأعمالهم فأما سالم أو غيره يقول: يجعل رصداً من الملائكة على جسر جهنم معهم الحسك يسألون الخلق عن الفرائض.
فَأَمَّا الْإِنْسانُ إِذا مَا ابْتَلاهُ رَبُّهُ فَأَكْرَمَهُ وَنَعَّمَهُ [١٥] قال: يعني بعض المؤمنين إذا اختبره ربه بالنعمة فَيَقُولُ رَبِّي أَكْرَمَنِ [١٥] بما أعطاني من السعة والرزق وذلك له استدراج واغترار. وقد قال الحسن رضي الله عنه: لا يزال العبد بخير ما علم ما الذي يفسد عمله. ومنهم من يزين له ما هو فيه ومنهم من تغلبه الشهوة.
وَأَمَّا إِذا مَا ابْتَلاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ [١٦] أي قتر عليه رزقه. فَيَقُولُ رَبِّي أَهانَنِ [١٦] بالفقر، يقول الله: كلا لم أبتله بالغنى لكرامته، ولم أبتله بالفقر لهوانه علي.
ولقد حكي أن فتح الموصلي رجع إلى أهله بعد صلاة العتمة وكان صائماً فقال: عشوني فقال: ما عندنا شيء نعشيك به: قال: فما لكم جلوس في الظلمة؟ قالوا: ما عندنا زيت نسرج به.
_________
(١) صحيح البخاري: كتاب الطب، باب من اكتوى، رقم ٥٣٧٨، وباب من لم يرق، رقم ٥٤٢٠ وكتاب الرقاق، باب يدخل الجنة، رقم ٦١٧٥.',NULL,'193',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (191,'قال: فقعد يبكي من الفرح إلى الصباح وقال: إلهي مثلي يترك بلا عشاء بلا سراج، بأي يد كانت مني يا مولاي «١» .

<span data-type="title" id=toc-679>[سورة الفجر (٨٩) : الآيات ٢٧ الى ٣٠]</span>
يا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ (٢٧) ارْجِعِي إِلى رَبِّكِ راضِيَةً مَرْضِيَّةً (٢٨) فَادْخُلِي فِي عِبادِي (٢٩) وَادْخُلِي جَنَّتِي (٣٠)
قوله تعالى: يا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ [٢٧] قال: هذا خطاب لنفس الروح الذي به حياة نفس الطبع والمطمئنة المصدقة بثواب الله وعقابه.
ارْجِعِي إِلى رَبِّكِ [٢٨] بطريق الآخرة راضِيَةً [٢٨] عن الله بالله مَرْضِيَّةً [٢٨] عنها لسكونها إلى الله عزَّ وجلَّ.
فَادْخُلِي فِي عِبادِي [٢٩] أي في جملة أوليائي الذين هم عبادي حقاً وَادْخُلِي جَنَّتِي [٣٠] قال سهل: الجنة جنتان: أحدهما الجنة نفسها، والأخرى حياة بحياة وبقاء ببقاء.
كما روي في الخبر: يقول الملائكة للمنفردين يوم القيامة: امضوا إلى منازلكم في الجنة، فيقولون: ما الجنة عندنا، وإنما انفردنا لمعنى منه إلينا، لا نريد سواه حياة طيبة.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-680>السورة التي يذكر فيها البلد</span>

<span data-type="title" id=toc-681>[سورة البلد (٩٠) : الآيات ١ الى ٤]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
لاَ أُقْسِمُ بِهذَا الْبَلَدِ (١) وَأَنْتَ حِلٌّ بِهذَا الْبَلَدِ (٢) وَوالِدٍ وَما وَلَدَ (٣) لَقَدْ خَلَقْنَا الْإِنْسانَ فِي كَبَدٍ (٤)
قوله تعالى: لاَ أُقْسِمُ بِهذَا الْبَلَدِ [١] قال: يعني مكة. وَأَنْتَ حِلٌّ بِهذَا الْبَلَدِ [٢] يعني يوم فتح مكة جعلناها لك حلالاً تقتل فيها من شئت من الكفار كما قال النبي صلى الله عليه وسلّم: «إنها لم تحل لأحد قبلي ولا تحل لأحد بعدي وإنما أحلت لي ساعة من نهار» «٢» ، فأقسم الله تعالى بمكة لحلول نبيه فيها إعزازاً له وإذلالاً لأعدائه.
وَوالِدٍ وَما وَلَدَ [٣] قال: الوالد: آدم، وما ولد: محمد صلّى الله عليه وسلّم. لَقَدْ خَلَقْنَا الْإِنْسانَ فِي كَبَدٍ [٤] أي في مشقة وشدة. قال: الكبد الانتصاب، أي لقد خلقناه في بطن أنه منتصباً. كما قال مجاهد: إن الولد يكون في بطن أمه منتصباً كانتصاب الأم، وملك موكل به، إذا اضجعت الأم رفع رأسه، ولولا ذلك لغرق في الدم.

<span data-type="title" id=toc-682>[سورة البلد (٩٠) : الآيات ١٠ الى ١١]</span>
وَهَدَيْناهُ النَّجْدَيْنِ (١٠) فَلا اقْتَحَمَ الْعَقَبَةَ (١١)
قوله تعالى: وَهَدَيْناهُ النَّجْدَيْنِ [١٠] قال: بيّنّا له طريق الخير ليتبعه، وطريق الشر ليجتنبه، كما قال: إِنَّا هَدَيْناهُ السَّبِيلَ إِمَّا شاكِراً وَإِمَّا كَفُوراً [الإنسان: ٣] . وقيل: يعني التدبير.
قوله تعالى: فَلَا اقْتَحَمَ الْعَقَبَةَ [١١] قال: أي فهلا جاوز الصراط والعقبة دونها، وفي الباطن عقبتان، إحداهما: الذنوب التي اجترحها، يعني بين يديه كالجبل يجاوزها بعتق رقبة، أو إطعام في يوم ذي مجاعة وشدة مسكيناً قد لزق بالتراب من الجهد والفاقة، ويتيماً بينه
_________
(١) شعب الإيمان ٧/ ٢٣٠.
(٢) صحيح البخاري: كتاب الجنائز، رقم ١٢٨٤.',NULL,'194',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (192,'وبينه قرابة، والعقبة الأخرى: المعرفة لا يقدر العارف عليها إلا بحول الله وقوته على عتق رقبة نفسه عن الهوى،

<span data-type="title" id=toc-683>[سورة البلد (٩٠) : الآيات ١٤ الى ١٥]</span>
أَوْ إِطْعامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ (١٤) يَتِيماً ذا مَقْرَبَةٍ (١٥)
أَوْ إِطْعامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ [١٤] ضرورة الإيمان قواماً، لا ظلماً وطغياناً بلذة نفس الطبع.
يَتِيماً ذَا مَقْرَبَةٍ [١٥] فاليتيم هاهنا القلب، طعامه الوفاء، والمسكين العارف المتحير، فطعامه ألطافه ذا مقربة عند الله وعند الخلق ذَا مَتْرَبَةٍ [١٦] .

<span data-type="title" id=toc-684>[سورة البلد (٩٠) : الآيات ١٧ الى ١٨]</span>
ثُمَّ كانَ مِنَ الَّذِينَ آمَنُوا وَتَواصَوْا بِالصَّبْرِ وَتَواصَوْا بِالْمَرْحَمَةِ (١٧) أُولئِكَ أَصْحابُ الْمَيْمَنَةِ (١٨)
قوله تعالى: وَتَواصَوْا بِالصَّبْرِ وَتَواصَوْا بِالْمَرْحَمَةِ [١٧] قال: يعني بالصبر على أمر الله، والتراحم بين الخلق.
وقد سئل رسول الله صلّى الله عليه وسلّم ما الإسلام؟ فقال: «الصبر والسماح. فقيل: ما الإيمان؟ فقال:
طيب الكلام وإطعام الطعام» «١» . قال سهل: وأطيب الكلام ذكر الله تعالى.
أُولئِكَ أَصْحابُ الْمَيْمَنَةِ [١٨] قال: يعني الميامنين على أنفسهم من أهوال ذلك اليوم، لا يحسون بدونه، كما كانوا في الدنيا حياة بحياة، وأزلية بأزلية، وسراً بسر.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-685>السورة التي يذكر فيها الشمس</span>

<span data-type="title" id=toc-686>[سورة الشمس (٩١) : الآيات ٣ الى ٤]</span>
وَالنَّهارِ إِذا جَلاَّها (٣) وَاللَّيْلِ إِذا يَغْشاها (٤)
قوله تعالى: وَالنَّهارِ إِذا جَلَّاها [٣] قال: يعني نور الإيمان يجلي ظلمة الجهل، ويطفئ لهيب النار.
وَاللَّيْلِ إِذا يَغْشاها [٤] قال: يعني الذنوب والإصرار عليها يغشى نور الإيمان، فلا يشرق في القلب، ولا يظهر أثره على الصفات، كما قال النبي صلى الله عليه وسلّم: «إن الهوى والشهوة يغلبان العلم والعقل» «٢» والبيان، لسابق القدرة من الله عزَّ وجلّ.

<span data-type="title" id=toc-687>[سورة الشمس (٩١) : الآيات ٩ الى ١٠]</span>
قَدْ أَفْلَحَ مَنْ زَكَّاها (٩) وَقَدْ خابَ مَنْ دَسَّاها (١٠)
قوله تعالى: قَدْ أَفْلَحَ مَنْ زَكَّاها [٩] قال: أفلح من رزق النظر في أمر معاده.
وَقَدْ خابَ مَنْ دَسَّاها [١٠] قال: خسرت نفس أغواها الله عزَّ وجلَّ، فلم تنظر في أمر معاده.
والله سبحانه وتعالى أعلم.
_________
(١) في اعتقاد أهل السنة ٤/ ٨٤٦: (قيل للحسن: ما الإيمان؟ قال: الصبر والسماح. قال: الصبر عن محارم الله، بفرائض الله) .
(٢) تقدم الحديث في تفسير سورة البقرة، والآية (٢٦) من سورة ص، وهو من قول الحارث بن أسد في الحلية ١٠/ ٨٨.',NULL,'195',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (193,'<span data-type="title" id=toc-688>السورة التي يذكر فيها الليل</span>

<span data-type="title" id=toc-689>[سورة الليل (٩٢) : الآيات ١ الى ٨]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَاللَّيْلِ إِذا يَغْشى (١) وَالنَّهارِ إِذا تَجَلَّى (٢) وَما خَلَقَ الذَّكَرَ وَالْأُنْثى (٣) إِنَّ سَعْيَكُمْ لَشَتَّى (٤)
فَأَمَّا مَنْ أَعْطى وَاتَّقى (٥) وَصَدَّقَ بِالْحُسْنى (٦) فَسَنُيَسِّرُهُ لِلْيُسْرى (٧) وَأَمَّا مَنْ بَخِلَ وَاسْتَغْنى (٨)
قوله تعالى: وَاللَّيْلِ إِذا يَغْشى [١] قال: باطنها نفس الطبع.
وَالنَّهارِ إِذا تَجَلَّى [٢] نفس الروح.
وَما خَلَقَ الذَّكَرَ وَالْأُنْثى [٣] أي ومن خلق الخوف والرجاء، فالخوف ذكر والرجاء أنثى.
إِنَّ سَعْيَكُمْ لَشَتَّى [٤] فمنه ما هو خالص ومنه ما هو مشوب بالأحداث.
فَأَمَّا مَنْ أَعْطى وَاتَّقى (٥) وَصَدَّقَ بِالْحُسْنى [٥- ٦] أبو بكر الصديق رضي الله عنه أعطى من نفسه وماله مجهوده، واتقى سكونه إلى نفس الطبع، وصدق بالحسنى كلمة التوحيد.
وقيل: بالجزاء، ويقال: هو الإخلاص.
فَسَنُيَسِّرُهُ لِلْيُسْرى [٧] هو العود إلى الخير.
وَأَمَّا مَنْ بَخِلَ وَاسْتَغْنى [٨] أبو جهل بخل بطاعته لله ورسوله، واستغنى: أظهر من نفسه الاستغناء عنهما.

<span data-type="title" id=toc-690>[سورة الليل (٩٢) : الآيات ١٠ الى ١١]</span>
فَسَنُيَسِّرُهُ لِلْعُسْرى (١٠) وَما يُغْنِي عَنْهُ مالُهُ إِذا تَرَدَّى (١١)
فَسَنُيَسِّرُهُ لِلْعُسْرى [١٠] أي نسهل عليه العمل، بعمل أهل النار، ألا تراه كيف قال عقبه: وَما يُغْنِي عَنْهُ مالُهُ إِذا تَرَدَّى [١١] في النار.

<span data-type="title" id=toc-691>[سورة الليل (٩٢) : آية ١٣]</span>
وَإِنَّ لَنا لَلْآخِرَةَ وَالْأُولى (١٣)
وَإِنَّ لَنا لَلْآخِرَةَ وَالْأُولى [١٣] فالآخرة نفس الروح، والأولى نفس الطبع، يهدي واحد إلى نفس الروح، وآخر إلى نفس الطبع.

<span data-type="title" id=toc-692>[سورة الليل (٩٢) : آية ١٧]</span>
وَسَيُجَنَّبُهَا الْأَتْقَى (١٧)
قوله تعالى: وَسَيُجَنَّبُهَا الْأَتْقَى (١٧) الَّذِي يُؤْتِي مالَهُ يَتَزَكَّى [١٧- ١٨] قال: الأتقى هو الصديق هو أتقى الناس فإن الناس أعطوا واتقوا وهو لم ير الفاني وأبقى لنفسه الباقي كما قال رسول الله صلّى الله عليه وسلّم: «ماذا أبقيت لنفسك؟ قال: الله ورسوله» «١» .

<span data-type="title" id=toc-693>[سورة الليل (٩٢) : آية ٢١]</span>
وَلَسَوْفَ يَرْضى (٢١)
قوله تعالى: وَلَسَوْفَ يَرْضى [٢١] قال: يعني بما له عندنا، وهو محل الفضل، لا محل الثواب، سراً بسر، وحياة بحياة، وأزلية بأزلية.
والله سبحانه وتعالى أعلم.
_________
(١) شعب الإيمان ٢/ ١٠٦ (رقم ١٢٩٨) والحديث قاله صلّى الله عليه وسلّم مخاطبا به أبا بكر الصديق.',NULL,'196',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (194,'<span data-type="title" id=toc-694>السورة التي يذكر فيها الضحى</span>

<span data-type="title" id=toc-695>[سورة الضحى (٩٣) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَالضُّحى (١) وَاللَّيْلِ إِذا سَجى (٢)
قوله تعالى: وَالضُّحى [١] قال: هو نفس الروح في الباطن.
وَاللَّيْلِ إِذا سَجى [٢] يعني نفس الطبع إذا سكن إلى نفس الروح في إدامة الذكر إلى الله تعالى.

<span data-type="title" id=toc-696>[سورة الضحى (٩٣) : آية ٤]</span>
وَلَلْآخِرَةُ خَيْرٌ لَكَ مِنَ الْأُولى (٤)
قوله تعالى: وَلَلْآخِرَةُ خَيْرٌ لَكَ مِنَ الْأُولى [٤] قال: ادخرت لك من المقام المحمود ومحل الشفاعة خيراً مما أعطيتك في الدنيا من النبوة والرسالة.

<span data-type="title" id=toc-697>[سورة الضحى (٩٣) : الآيات ٦ الى ٩]</span>
أَلَمْ يَجِدْكَ يَتِيماً فَآوى (٦) وَوَجَدَكَ ضَالاًّ فَهَدى (٧) وَوَجَدَكَ عائِلاً فَأَغْنى (٨) فَأَمَّا الْيَتِيمَ فَلا تَقْهَرْ (٩)
قوله تعالى: أَلَمْ يَجِدْكَ يَتِيماً فَآوى [٦] قال: يعني ألم يجدك فرداً فآواك إلى أصحابك.
وَوَجَدَكَ ضَالًّا فَهَدى [٧] قال: أي وجدك لا تعرف قدر نفسك فعرفك قدرك، ووجدك ضالاً عن معاني محض مودتك فسقاك من شراب مودته بكأس محبته، فهداك إلى معرفته، وخلع عليك خلع نبوته ورسالته ليدل بهما على قربه ووحدانيته. قال: وفيها وجه آخر: ووجدك نفسك نفس الطبع فقير إلى سبيل المعرفة.
وَوَجَدَكَ عائِلًا فَأَغْنى [٨] قال: وجد نفسك حيرانة والهة إلى المعرفة بنا، فقيرة إليها، فقوى نفس روحك فأغناها بالقرآن والحكمة. وقد قال رسول الله صلّى الله عليه وسلّم: «ليس الغنى كثرة العرض، إنَّما الغنى غنى النفس» «١» .
فَأَمَّا الْيَتِيمَ فَلا تَقْهَرْ [٩] فقد ذقت طعم اليتيم. قال: ووجه آخر: فقد علمت موقع اللطف من قلب اليتيم.
والله سبحانه وتعالى أعلم.
_________
(١) مسند إسحاق بن راهويه ١/ ٣٣٢.',NULL,'197',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (195,'<span data-type="title" id=toc-698>السورة التي يذكر فيها الانشراح</span>

<span data-type="title" id=toc-699>[سورة الشرح (٩٤) : الآيات ١ الى ٢]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ (١) وَوَضَعْنا عَنْكَ وِزْرَكَ (٢)
قوله تعالى: أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ [١] قال: ألم نوسع لك صدرك بنور الرسالة فجعلناه معدناً للحقائق. قال: وأول الشرح بنور الإسلام كما قال الله تعالى: فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلامِ [الأنعام: ١٢٥] ثم قال: يزداد المنازل بعده، فيكون الأنوار على قدر المواهب من البصائر.
وَوَضَعْنا عَنْكَ وِزْرَكَ [٢] قال: يعني أزلنا عنك السكون إلى غيرنا من همة نفس الطبع، فجعلناك ساكناً إلينا قابلاً عنا بنا.

<span data-type="title" id=toc-700>[سورة الشرح (٩٤) : الآيات ٤ الى ٥]</span>
وَرَفَعْنا لَكَ ذِكْرَكَ (٤) فَإِنَّ مَعَ الْعُسْرِ يُسْراً (٥)
قوله تعالى: وَرَفَعْنا لَكَ ذِكْرَكَ [٤] قال: وصلنا اسمك باسمنا في الأذان والتوحيد، فلا يقبل إيمان العبد حتى يؤمن بك.
قوله تعالى: فَإِنَّ مَعَ الْعُسْرِ يُسْراً [٥] قال: عظم الله تعالى حال الرجاء في هذه الآية بكرمه وخفي لطفه، فذكر اليسر مرتين، وقد قال النبي صلى الله عليه وسلّم: «لن يغلب عسر يسرين» «١» ، يعني فطنة القلب والعقل يسران يغلبان نفس الطبع، فيعيدانه إلى الإخلاص، وهو معنى الآية في الباطن، أي فإن مع شدة نفس الطبع في افتقاره إلى ذات الحق عزَّ وجلَّ إلى نفس الروح والعقل وفطنة القلب وهو في الباطن تسكين قلب محمد صلّى الله عليه وسلّم على الإعانة خوفاً، فقال: إنا سلطنا على نفس الطبع الكثيف منك لطائف نفس الروح والعقل والقلب والفهم التي سبقت بالموهبة الجليلة قبل بدوِّ الخلق بألف عام، فغلبت نفس الطبع.

<span data-type="title" id=toc-701>[سورة الشرح (٩٤) : آية ٧]</span>
فَإِذا فَرَغْتَ فَانْصَبْ (٧)
فَإِذا فَرَغْتَ [٧] من صلاتك المكتوبة وأنت جالس فَانْصَبْ [٧] إلى ربك وارجع إليه، كما كنت قبل نفس الطبع، قبل بدوّ الخلق، فرداً بفرد، وسراً بسر، فوهب الله له مثل منزلته السابقة في الدنيا، كما قال النبي صلى الله عليه وسلّم: «إن لي مع الله وقتاً لا يسعني غيره» «٢» ، هذا باطن الآية، وظاهرها ما عليه الظاهر.
_________
(١) المستدرك على الصحيحين ٢/ ٣٢٩، ٥٧٥.
(٢) فيض القدير ٤/ ٦.',NULL,'198',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (196,'وحكى أبو عمرو بن العلاء فقال: «هربنا من الحجاج «١» فدخلنا البادية فأقمنا بها دهراً نتردد من حي إلى حي، فبينا أنا خارج في بعض الأحياء ذات غداة متوزع الخاطر مبهم القلب ضيق الصدر، إذ سمعت شيخاً من الأعراب مجتازاً يقول «٢» : [من الخفيف]
صَبِّرِ النَّفسَ ينجلي كُلّ هَمٍّ ... إنّ في الصبر حيلةَ المُحتالِ
ربّما تكره النفوسُ من الشيء ... له فَرجَةٌ كحلِّ العِقالِ
فلم يستتم الشيخ إنشاد البيتين حتى رأيت فارساً من بعيد ينادي: قد مات الحجاج. قال: فسألت الشيخ عن الفرجة، فقال: الفرجة بضم الفاء: في الحائط والعود ونحوهما، والفرجة بفتح الفاء:
في الأمر من الشدة والنوائب. قال أبو عمرو: فلم أدر بأيهما كنت أشد سروراً، بموت الحجاج أم بهذه الفائدة «٣» .
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-702>السورة التي يذكر فيها التين</span>

<span data-type="title" id=toc-703>[سورة التين (٩٥) : الآيات ٤ الى ٦]</span>
لَقَدْ خَلَقْنَا الْإِنْسانَ فِي أَحْسَنِ تَقْوِيمٍ (٤) ثُمَّ رَدَدْناهُ أَسْفَلَ سافِلِينَ (٥) إِلاَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ (٦)
قوله تعالى: لَقَدْ خَلَقْنَا الْإِنْسانَ فِي أَحْسَنِ تَقْوِيمٍ [٤] قال: أي في أحسن قامة وأحسن صورة.
ثُمَّ رَدَدْناهُ أَسْفَلَ سافِلِينَ [٥] يعني نقلناه من حال إلى حال حتى أدركه الهرم.
إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ [٦] في شبابهم، فإنهم إذا ضعفوا وشاخوا أمرنا الملائكة تكتب لهم الأعمال التي كانت تكتب لهم حال شبابهم.
فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ [٦] أي لا ينقطع عنهم أجور أعمالهم وإن ضعفوا عنها.
والله سبحانه وتعالى أعلم.
_________
(١) الحجاج بن يوسف بن الحكم الثقفي (٤٠- ٩٥ هـ) : قائد، داهية، خطيب. ثبتت له إمارة العراق عشرين سنة. (الأعلام ٢/ ١٦٨) . [.....]
(٢) البيتان لعبيد بن الأبرص في ديوانه ص ١١١- ١١٢ ولأمية بن أبي الصلت في ديوانه ص ٤٤٤ وله أو لحنيف بن عمير أو لنهار ابن أخت مسيلمة الكذاب في شرح شواهد المغني ٢/ ٧٠٧.
(٣) شرح شواهد المغني ٢/ ٧٠٧ وخزانة الأدب ٢/ ٥٤٤.',NULL,'199',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (197,'<span data-type="title" id=toc-704>السورة التي يذكر فيها العلق</span>

<span data-type="title" id=toc-705>[سورة العلق (٩٦) : آية ٦]</span>
كَلاَّ إِنَّ الْإِنْسانَ لَيَطْغى (٦)
قوله تعالى: كَلَّا إِنَّ الْإِنْسانَ لَيَطْغى [٦] قال: أي رؤية الغنى تورث الاستغناء، والاستغناء يورث الطغيان. وقد قال الحسن رحمة الله عليه: لقد قصر نظر عبد زويت عنه الدنيا، ثم لم يعلم أن ذلك نظر من الله، لقد قصر علم عبد بسطت له الدنيا، فلم يخش أن يكون ذلك مكراً من الله تعالى يمكر به «١» . ثم قال: والله ما بسطت الدنيا لعبد إلا طغى كائناً من كان، ثم تلا قوله تعالى: كَلَّا إِنَّ الْإِنْسانَ لَيَطْغى (٦) أَنْ رَآهُ اسْتَغْنى [٦- ٧] .

<span data-type="title" id=toc-706>[سورة العلق (٩٦) : آية ١٤]</span>
أَلَمْ يَعْلَمْ بِأَنَّ اللَّهَ يَرى (١٤)
قوله تعالى: أَلَمْ يَعْلَمْ بِأَنَّ اللَّهَ يَرى [١٤] قال: ليس له وراء، وهو وراء كل وراء.

<span data-type="title" id=toc-707>[سورة العلق (٩٦) : الآيات ١٧ الى ١٨]</span>
فَلْيَدْعُ نادِيَهُ (١٧) سَنَدْعُ الزَّبانِيَةَ (١٨)
قوله تعالى: فَلْيَدْعُ نادِيَهُ [١٧] قال: يعني عشيرته.
سَنَدْعُ الزَّبانِيَةَ [١٨] يعني خزنة جهنم، أرجلهم في الأرض ورؤوسهم في السماء الدنيا. وإنما سموا الزبانية من الزبن وهو الدفع، يدفعون الجهنميين في قفاهم بأيديهم وأرجلهم.
فلما سمع أبو جهل ذكر الزبانية هرب إلى قومه، فقالوا له: أخِفْتَه؟ فقال: لا، ولكن خفت الزبانية، لا أدري من هم.
والله سبحانه وتعالى أعلم بالصواب.

<span data-type="title" id=toc-708>السورة التي يذكر فيها القدر</span>

<span data-type="title" id=toc-709>[سورة القدر (٩٧) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
إِنَّا أَنْزَلْناهُ فِي لَيْلَةِ الْقَدْرِ (١)
قوله تعالى: إِنَّا أَنْزَلْناهُ فِي لَيْلَةِ الْقَدْرِ [١] قال: ليلة القدر قدرت فيها الرحمة على عباده.

<span data-type="title" id=toc-710>[سورة القدر (٩٧) : الآيات ٤ الى ٥]</span>
تَنَزَّلُ الْمَلائِكَةُ وَالرُّوحُ فِيها بِإِذْنِ رَبِّهِمْ مِنْ كُلِّ أَمْرٍ (٤) سَلامٌ هِيَ حَتَّى مَطْلَعِ الْفَجْرِ (٥)
قوله تعالى: مِنْ كُلِّ أَمْرٍ (٤) سَلامٌ [٤- ٥] أي سلام من الظلمة أوقات العارفين به، والقائمين معه على حدود الأحكام في الأوامر والنواهي.
والله سبحانه وتعالى أعلم.
_________
(١) في الحلية ٧/ ٦٨ أن سفيان الثوري قال: (ما بسطت الدنيا على أحد إلا اغترارا، وما زويت عنه إلا اختبارا) .',NULL,'200',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (198,'<span data-type="title" id=toc-711>السورة التي يذكر فيها البينة</span>

<span data-type="title" id=toc-712>[سورة البينة (٩٨) : آية ٥]</span>
وَما أُمِرُوا إِلاَّ لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ حُنَفاءَ وَيُقِيمُوا الصَّلاةَ وَيُؤْتُوا الزَّكاةَ وَذلِكَ دِينُ الْقَيِّمَةِ (٥)
قوله تعالى: وَما أُمِرُوا إِلَّا لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ [٥] قال: العلم كله في الحركات حتى يصير إلى الإخلاص، فإذا بلغ إلى الإخلاص صار طمأنينة، فمن كان علمه يقيناً وعمله إخلاصاً أذهب الله عنه ثلاثة أشياء، الجزع والجهل والعمل، وأعطاه بدل الجزع الصبر، وبدل الجهل العلم، وبدل العلم ترك الاختيار، ولا يكون هذا إلا للمتقين.
قيل: وما الإخلاص؟ قال: الإجابة، فمن لم تكن له الإجابة فلا إخلاص له.
وقال: الإخلاص على ثلاث معان: إخلاص العبادة لله، وإخلاص العمل له، وإخلاص القلب له.

<span data-type="title" id=toc-713>[سورة البينة (٩٨) : آية ٨]</span>
جَزاؤُهُمْ عِنْدَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ذلِكَ لِمَنْ خَشِيَ رَبَّهُ (٨)
قوله تعالى: ذلِكَ لِمَنْ خَشِيَ رَبَّهُ [٨] قال: الخشية سر، والخشوع علانية، من خشعت جوارحه لم يقربه الشيطان. قيل: فما الخشوع؟ قال: الوقوف بين يدي الله، والصبر على ذلك. قال: وكمال الخشية ترك الآثام في السر والعلانية.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-714>السورة التي يذكر فيها الزلزلة</span>

<span data-type="title" id=toc-715>[سورة الزلزلة (٩٩) : الآيات ٦ الى ٨]</span>
يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتاتاً لِيُرَوْا أَعْمالَهُمْ (٦) فَمَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ خَيْراً يَرَهُ (٧) وَمَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ شَرًّا يَرَهُ (٨)
قوله تعالى: يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتاتاً [٦] قال: يتبع كل أحد ما كان يعتمده، فمن اعتمد فضل الله اتبع فضله، ومن اعتمد عمله اتبع عمله، ومن اعتمد الشفاعة اتبع الشفاعة.
قوله تعالى: فَمَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ خَيْراً يَرَهُ [٧] قال: لما نزلت هذه الآية خطب رسول الله صلّى الله عليه وسلّم فقال في خطبته: «ألا وإن الدنيا عرض حاضر، يأكل منه البر والفاجر، ألا وإن الآخرة أجل صادق، يقضي فيها ملك قادر، ألا وإن الخير كله بحذافيره في الجنة، ألا وإن الشر كله بحذافيره في النار، ألا فاعلموا وأنتم من الله على حذر، واعلموا أنكم معروضون على أعمالكم فَمَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ خَيْراً يَرَهُ (٧) وَمَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ شَرًّا يَرَهُ [٧- ٨] » «١» .
قال أبو الدرداء رضي الله عنه: إتمام التقوى أن يتقي الله عبده، حتى يتقيه في مثقال ذرة، حتى يترك بعض ما يرى أنه حلال، خشية أن يكون حراماً يكون حجاباً بينه وبين الحرام «٢» .
_________
(١) الحلية ١/ ٢٦٤- ٢٦٥ وكتاب الأم ١/ ٢٠٢.
(٢) الزهد لابن مبارك ١/ ١٨.',NULL,'201',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (199,'قال سهل: لا تستصغر شيئاً من الذنوب وإن قل، فإنهم قالوا: أربعة بعد الذنب أشد من الذنب: الإصرار والاستبشار والاستصغار والافتخار.
وقد قال ابن مسعود رضي الله عنهما: إن المؤمن يرى ذنوبه كأنه في أصل جبل يخاف أن يقع عليه، وإن الكافر يرى ذنوبه كذبابة وقعت على أنفه فقال هكذا بيده فطارت «١» .
ثم قال سهل: معشر المسلمين لقد أعقبتم الإقرار باللسان واليقين في القلب، أن الله واحد ليس كمثله شيء، وإن لكم يوماً يبعثكم فيه ويسألكم فيه عن مثاقيل الذر من أعمالكم، فإن كان خيراً أثابكم فيه، وإن كان شراً عاقبكم عليه إن شاء، فحققوه بالفعل.
قيل له: وكيف لنا أن نحققه بالفعل؟
قال: بخمسة أشياء لا بد لكم منها: أكل الحلال، ولبس الحلال، وحفظ الجوارح، وأداء الحقوق كما أمرتم به، وكف الأذى عن المسلمين، كيلا يذهب بأعمالكم قصاصاً في القيامة، ثم استعينوا على ذلك كله بالله حتى يتمها لكم.
قيل له: فكيف تصح للعبد هذه الأحوال؟
قال: لا بد له من عشرة أشياء يدع منها خمساً ويتمسك بخمس ويدع وساوس العدو، ويتبع العقل فيما يزجره، ويدع اهتمامه لأمر الدنيا ويتركها لأهلها، ويهتم بالآخرة ويعين أهلها ويدع اتباعه الهوى ويتقي الله على كل حال، ويترك المعصية ويشتغل بالطاعة، ويدع الجهل والإقامة عليه حتى يحكم عمله، ويطلب العلم ويعمل به.
قيل له: وكيف لنا أن نقيمها ونعمل بها؟
قال: لا بد من أربعة أشياء: لا يتعب نفسه فيما كان مصيره إلى التراب، ولا يرغب فيه، ولا يتخذ إخواناً مصيرهم إلى التراب، ولا يرغب فيهم.
قيل: كيف ذلك؟
قال: يعلم أنه عبد، مولاه عالم بحاله، شاهد، قادر على فرحه وترحه، رحيم به «٢» .
والله سبحانه وتعالى أعلم.
_________
(١) سنن الترمذي ٤/ ٦٥٨ (رقم ٢٤٩٢) وسنن البيهقي الكبرى ١٠/ ١٨٨ ومسند أحمد ١/ ٣٨٣ وجامع العلوم والحكم ١/ ١٧٤ والزهد لابن مبارك ١/ ٢٣ والزهد لهناد ٢/ ٤٤٨ وفيض القدير ٢/ ٣٧٢.
(٢) وردت أقوال التستري وما سئل عنه في الحلية ١٠/ ٢٠٨- ٢٠٩.',NULL,'202',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (200,'<span data-type="title" id=toc-716>السورة التي يذكر فيها العاديات</span>

<span data-type="title" id=toc-717>[سورة العاديات (١٠٠) : الآيات ٦ الى ٨]</span>
إِنَّ الْإِنْسانَ لِرَبِّهِ لَكَنُودٌ (٦) وَإِنَّهُ عَلى ذلِكَ لَشَهِيدٌ (٧) وَإِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ (٨)
قوله تعالى: إِنَّ الْإِنْسانَ لِرَبِّهِ لَكَنُودٌ [٦] قال: الكنود الكفور، وهو الذي خالف العهد وجانب الصدق وألف الهوى، فحينئذ يؤيسه الله من كل بر وتقوى.
وَإِنَّهُ عَلى ذلِكَ لَشَهِيدٌ [٧] يعني الله شهيد على أفعاله وأحواله وأسراره.
وَإِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ [٨] قال: الخير المراد هاهنا ثلاث: حب النفس وحب الدنيا وحب الهوى، فسماها خيراً لتعارف أهلها، وإنما الخير ثلاث: الاستغناء عن الخلق والافتقار إلى الله عزَّ وجلَّ وأداء الأمر. والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-718>السورة التي يذكر فيها القارعة</span>

<span data-type="title" id=toc-719>[سورة القارعة (١٠١) : الآيات ١ الى ٤]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
الْقارِعَةُ (١) مَا الْقارِعَةُ (٢) وَما أَدْراكَ مَا الْقارِعَةُ (٣) يَوْمَ يَكُونُ النَّاسُ كَالْفَراشِ الْمَبْثُوثِ (٤)
قوله تعالى: الْقارِعَةُ (١) مَا الْقارِعَةُ [١- ٢] قال: يقرع الله أعداءه بالعذاب.
وَما أَدْراكَ مَا الْقارِعَةُ [٣] تعظيم لها ولشدتها وكل شيء في القرآن، وما أدراك فإنه لم يخبر به، كما قال: وَما يُدْرِيكَ لَعَلَّ السَّاعَةَ تَكُونُ قَرِيباً [الأحزاب: ٦٣] ولم يخبره بها إلا قوله تعالى: وَما أَدْراكَ مَا الْقارِعَةُ [٣] ثم أخبره عنها.
قوله تعالى: يَوْمَ يَكُونُ النَّاسُ كَالْفَراشِ الْمَبْثُوثِ [٤] يعني يجول بعضهم في بعض من هيبة الله عزَّ وجلَّ. وقيل: القرع ثلاث، القرع للأبدان بسهام الموت، وقرع الأعمال بسؤال الله إياهم، وقرع القلوب بخوف القطيعة.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-720>السورة التي يذكر فيها التكاثر</span>

<span data-type="title" id=toc-721>[سورة التكاثر (١٠٢) : آية ٣]</span>
كَلاَّ سَوْفَ تَعْلَمُونَ (٣)
قوله تعالى: كَلَّا سَوْفَ تَعْلَمُونَ [٣] قال سهل: سيعلم من أعرض عني أنه لا يجد مثلي، وأنشد: [من الوافر]
ستذكرني إذا جرَّبتَ غيري ... وتعلم أنني كنت لك كنزا

<span data-type="title" id=toc-722>[سورة التكاثر (١٠٢) : آية ٥]</span>
كَلاَّ لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ (٥)
قوله تعالى: كَلَّا لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ [٥] قال: اليقين النار، والإقرار باللسان فتيلة، والعمل زيته، وابتداء اليقين بالمكاشفة، ثم المعاينة، والمشاهدة.

<span data-type="title" id=toc-723>[سورة التكاثر (١٠٢) : الآيات ٧ الى ٨]</span>
ثُمَّ لَتَرَوُنَّها عَيْنَ الْيَقِينِ (٧) ثُمَّ لَتُسْئَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ (٨)
قوله تعالى: لَتَرَوُنَّها عَيْنَ الْيَقِينِ [٧] قال: عين اليقين ليس هو من اليقين، لكنه نفس الشيء وكليته. ثُمَّ لَتُسْئَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ [٨] قال: لا تأتي على الخلق من الجن والإنس ساعة من ساعات الليل والنهار إلا ولله عليهم فيها حق واجب، عرفه من عرفه، وجهله من جهله فيتثبت أحوالهم يوم القيامة، ثم قرأ: لَتُسْئَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ [٨] .
والله سبحانه وتعالى أعلم.',NULL,'203',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (201,'<span data-type="title" id=toc-724>السورة التي يذكر فيها العصر</span>

<span data-type="title" id=toc-725>[سورة العصر (١٠٣) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَالْعَصْرِ (١) إِنَّ الْإِنْسانَ لَفِي خُسْرٍ (٢) إِلاَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ وَتَواصَوْا بِالْحَقِّ وَتَواصَوْا بِالصَّبْرِ (٣)
قوله تعالى: وَالْعَصْرِ [١] قيل: أي ورب الدهر. وقيل: أراد به والعصر.
إِنَّ الْإِنْسانَ لَفِي خُسْرٍ [٢] يعني أبا لهب خسر أيامه كلها.
إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحاتِ [٣] يعني أدوا الفرائض كما فرضت عليهم.
وَتَواصَوْا بِالْحَقِّ [٣] أي بالله عزَّ وجلَّ.
وَتَواصَوْا بِالصَّبْرِ [٣] على أمره.
قيل: ما الصبر؟ قال: لا عمل أفضل من الصبر، ولا ثواب أكبر من ثواب الصبر ولا زاد إلا التقوى، ولا تقوى إلا بالصبر، ولا معين على الصبر لله إلا الله عزَّ وجلَّ «١» .
قيل: الصبر من الأعمال؟ قال: نعم الصبر من العمل بمنزلة الرأس من الجسد، لا يصلح أحدهما إلا بصاحبه.
قيل: ما أجل الصبر؟ قال: أجله انتظار الفرج من الحق.
قيل: فما أصل الصبر؟ قال: مجاهدة النفس على إقامة الطاعات وأدائها بأحكامها وحدودها ومكابدتها على اجتناب المعاصي صغيرها وكبيرها.
قيل: والناس في الصبر كيف هم؟ قال: الناس في الصبر صنفان: فصنف يصبرون للدنيا حتى ينالوا منها ما تشتهي أنفسهم، فهو الصبر المذموم، وصنف يصبرون للآخرة طلباً لثواب الآخرة وخوفاً من عذابها.
قيل: فالصبر للآخرة هو على نوع واحد أو على أنواع؟ قال: الصبر للآخرة له أربع مقامات:
فثلاث منها فرض، والرابع فضيلة: صبر على طاعة الله عزَّ وجلَّ وصبر على معصيته وصبر على المصائب من عنده «٢» . أو قال: صبر على أمر الله عزَّ وجلَّ، وصبر على نهيه، وصبر على أفعال الله عزَّ وجلَّ، فهذه ثلاث مقامات منه، وهي فرض، والمقام الرابع فضيلة وهو الصبر على أفعال المخلوقين. قال الله تعالى: وَإِنْ عاقَبْتُمْ فَعاقِبُوا بِمِثْلِ مَا عُوقِبْتُمْ بِهِ [النحل: ١٢٦] الآية، كم بالمثل وفضل الصبر، ثم قال: وَاصْبِرْ وَما صَبْرُكَ إِلَّا بِاللَّهِ [النحل: ١٢٧] ولا يعين عليه إلا هو.
ولقد لحق رجل بأويس القرني رحمه الله فسمعه يقول: اللهم إني أعتذر إليك اليوم من كل كبد جائعة وبدن عاري، فإنه ليس في بيتي من الطعام إلا ما في بطني، وليس شيء من الدنيا إلا ما على ظهري. قال: وعلى ظهره خريقة قد تردى بها «٣» .
_________
(١) الحلية ١٠/ ١٩٨ وطبقات الصوفية ١/ ١٧١ وسير أعلام النبلاء ١٣/ ٣٣١.
(٢) قوت القلوب ١/ ٣٣١، ٣٥١- ٣٥٢، وهو قول الحسن البصري.
(٣) شعب الإيمان ١/ ٥٢٤ وصفوة الصفوة ٣/ ٥٣- ٥٤ والحلية ٢/ ٨٧ وسير أعلام النبلاء ٤/ ٣٠.',NULL,'204',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (202,'قال: وأتاه رجل فقال له: يا أويس كيف أصبحت؟ أو قال: وكيف أمسيت؟ قال: أحمد الله على كل حال، وما تسأل عن حال رجل إذا هو أصبح ظن أنه لا يمسي، وإذا أمسى ظن أنه لا يصبح، إن الموت وذكره لم يدع لمؤمن فرحاً، وإن حق الله عزَّ وجلَّ في مال المسلم لم يدع له في ماله فضة ولا ذهباً، وإن الأمر بالمعروف والنهي عن المنكر لم يدع لمؤمن صديقاً، نأمر بالمعروف فيشتمون أعراضنا، ويجدون على ذلك من الفاسقين أعواناً، حتى والله لقد قذفوني بالعظائم، وأيم الله لا أدع أن أقوم لله فيهم بحقه، ثم أخذ الطريق «١» . فهذا أويس قد بلغ هذا المقام في الصبر. والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-726>السورة التي يذكر فيها الهمزة</span>

<span data-type="title" id=toc-727>[سورة الهمزة (١٠٤) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
وَيْلٌ لِكُلِّ هُمَزَةٍ لُمَزَةٍ (١) الَّذِي جَمَعَ مَالاً وَعَدَّدَهُ (٢) يَحْسَبُ أَنَّ مالَهُ أَخْلَدَهُ (٣)
قوله تعالى: وَيْلٌ لِكُلِّ هُمَزَةٍ [١] يعني المغتاب إذا غاب الرجل عنه اغتابه.
لُمَزَةٍ [١] يعني الطاعن إذا رآه طعن فيه، نزلت في الوليد بن المغيرة.
الَّذِي جَمَعَ مَالاً وَعَدَّدَهُ [٢] قال: استعبد ماله لدنياه.
يَحْسَبُ أَنَّ مالَهُ أَخْلَدَهُ [٣] قال: أي أخلده لدار البقاء. وقيل: أخلده من الموت.

<span data-type="title" id=toc-728>[سورة الهمزة (١٠٤) : آية ٦]</span>
نارُ اللَّهِ الْمُوقَدَةُ (٦)
قوله تعالى: نارُ اللَّهِ الْمُوقَدَةُ [٦] أي لا تخمد بأكل الجلد واللحم، حتى يخلص حرها إلى القلوب. والنيران أربعة: نار الشهوة ونار الشقاوة ونار القطيعة ونار المحبة. فنار الشهوة تحرق الطاعات، ونار الشقاوة تحرق التوحيد، ونار القطيعة تحرق القلوب، ونار المحبة تحرق النيران كلها.
ولقد حكي أن علي بن الحسين «٢» رضي الله عنه دخل مغارة مع أصحاب له، فرأى امرأة في المغارة وحدها، فقال لها: من أنت؟ قالت: أمة من إماء الله، إليك عني لا يذهب الحب. فقال لها علي رضي الله عنه: وما الحب؟ قالت: أخفى من أن يرى، وأبين من أن يخفى، كمونه في الحشاء ككمون النار في الحجر، إن قدحته أورى وإن تركته توارى «٣» ، ثم أنشأت تقول:
[من البسيط]
إنَّ المحبينَ في شغلٍ لسيِّدهم ... كَفِتيَة الكهفِ لا يدرون كم لبثوا
_________
(١) كتاب الزهد الكبير ٢/ ٢١٩ وصفوة الصفوة ٣/ ٥٣ وسير أعلام النبلاء ٤/ ٣٠، ٣٣.
(٢) علي بن الحسين بن علي بن أبي طالب القرشي الهاشمي (٣٨- ٩٤ هـ) : يلقب بزين العابدين. أحد من كان يضرب بهم المثل في الحلم والورع. كان يقوت مائة بيت سرا. (الأعلام ٤/ ٢٧٧) .
(٣) ذم الهوى ص ٣٤٦ وربيع الأبرار ٤/ ٢٣ وتزيين الأسواق ص ٣٠ ومصارع العشاق ١/ ١٧٥، ٢/ ٢١٧ والظرف والظرفاء ص ١٥٩.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (203,'<span data-type="title" id=toc-729>السورة التي يذكر فيها الفيل</span>

<span data-type="title" id=toc-730>[سورة الفيل (١٠٥) : آية ١]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحابِ الْفِيلِ (١)
قوله تعالى: أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحابِ الْفِيلِ [١] قال: ألم تعلم كيف فعل ربك بأعدائك وأنت بعد لم تظهر في الدنيا، كذلك يفعل بأعدائك وأنت بين ظهرانيهم ويرفع عنك مكرهم.

<span data-type="title" id=toc-731>[سورة الشرح (٩٤) : آية ٣]</span>
الَّذِي أَنْقَضَ ظَهْرَكَ (٣)
قال عكرمة: قوله تعالى: طَيْراً أَبابِيلَ [٣] قال: طير نشأت من قبل البحر، لها رؤوس كرؤوس الأفاعي «١» . وقيل: كرؤوس السباع، لم تر قبل يومئذ ولا بعده، فجعلت ترميهم بالحجارة لتجدر جلودهم، وكان أول يوم رئي فيه الجدري.

<span data-type="title" id=toc-732>السورة التي يذكر فيها قريش</span>

<span data-type="title" id=toc-733>[سورة قريش (١٠٦) : الآيات ١ الى ٤]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
لِإِيلافِ قُرَيْشٍ (١) إِيلافِهِمْ رِحْلَةَ الشِّتاءِ وَالصَّيْفِ (٢) فَلْيَعْبُدُوا رَبَّ هذَا الْبَيْتِ (٣) الَّذِي أَطْعَمَهُمْ مِنْ جُوعٍ وَآمَنَهُمْ مِنْ خَوْفٍ (٤)
قوله تعالى: لِإِيلافِ قُرَيْشٍ [١] قال: يعني لتألف قريش الرحلتين.
رِحْلَةَ الشِّتاءِ [٢] إلى الشام. وَرحلة الصَّيْفِ [٢] إلى اليمن أهلكنا أصحاب اليمن كذلك، كأنه يقول للنبي صلّى الله عليه وسلّم: ذكّر قريشاً نعمتي عليهم بك قبل إرسالك إليهم.
ْيَعْبُدُوا رَبَّ هذَا الْبَيْتِ
[٣] يعني مكة.
الَّذِي أَطْعَمَهُمْ مِنْ جُوعٍ [٤] السنين، وَآمَنَهُمْ مِنْ خَوْفٍ [٤] النجاشي.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-734>السورة التي يذكر فيها الدين (الماعون)</span>

<span data-type="title" id=toc-735>[سورة الماعون (١٠٧) : الآيات ١ الى ٧]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
أَرَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ (١) فَذلِكَ الَّذِي يَدُعُّ الْيَتِيمَ (٢) وَلا يَحُضُّ عَلى طَعامِ الْمِسْكِينِ (٣) فَوَيْلٌ لِلْمُصَلِّينَ (٤)
الَّذِينَ هُمْ عَنْ صَلاتِهِمْ ساهُونَ (٥) الَّذِينَ هُمْ يُراؤُنَ (٦) وَيَمْنَعُونَ الْماعُونَ (٧)
قوله تعالى: أَرَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ [١] قال: أي بالحساب يوم يدان الناس.
فَذلِكَ الَّذِي يَدُعُّ الْيَتِيمَ [٢] أي يدفعه عن حقه.
وَلا يَحُضُّ عَلى طَعامِ الْمِسْكِينِ [٣] أي لا يطعم مسكيناً، نزلت في العاص بن وائل «٢» .
فَوَيْلٌ لِلْمُصَلِّينَ (٤) الَّذِينَ هُمْ عَنْ صَلاتِهِمْ ساهُونَ [٤- ٥] قال: هم المنافقون، غافلون عن مراعاة أوقات الصلاة ومراعاة حقوقها، وهذا وعيد شديد، إذ ليس كل من كان في صورة المطيعين واقفاً مع العابدين، كان مطيعاً مقبول العمل. وفي زبور داود عليه السلام: قل للذين يحضرون الكنائس بأبدانهم، ويقفون مواقف العباد وقلوبهم في الدنيا: أبي يستخفون؟ أم إياي يخدعون؟. وفي الخبر: ليس لأحد من صلاته إلا ما عقل «٣» .
_________
(١) في تفسير ابن كثير ٤/ ٥٩٠: (كرؤوس السباع) . [.....]
(٢) العاص بن وائل بن هاشم السهمي القرشي ( ... - نحو ٣ ق. هـ) : أحد الحكام في الجاهلية. أدرك الإسلام، وظل على الشرك. وهو والد عمرو بن العاص الصحابي فاتح مصر. (الأعلام ٣/ ٢٤٧) .
(٣) قوت القلوب ٢/ ١٦٩ وفيض القدير ٢/ ٣٣٤.',NULL,'206',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (204,'قوله تعالى: الَّذِينَ هُمْ يُراؤُنَ [٦] قال: هو الشرك الخفي، لأن المنافقين كانوا يحسنون الصلاة في المساجد، فإذا غابوا عن أعين المسلمين تكاسلوا عنها، ألا ترى كيف أثبتهم أولاً مصلين، ثم أوعدهم بالوعيد. واعلموا أن الشرك شركان: شرك في ذات الله عزَّ وجلَّ، وشرك في معاملته، فالشرك في ذاته غير مغفور، وأما الشرك في معاملته قال: نحو أن يحج ويصلي ويعلم الناس، فيثنون عليه، هذا هو الشرك الخفي. وفي الخبر: أخلصوا أعمالكم لله فإن الله لا يقبل من العمل إلا ما خلص، ولا تقولوا هذا لله، وللرحم إذا وصلتموه فإنه للرحم، وليس منه شيء «١» . وقد قال النبي صلى الله عليه وسلّم لمعاذ حين قال له: أوصني يا رسول الله، قال: «أخلص لله يكفيك القليل من العمل» «٢» .
قوله تعالى: وَيَمْنَعُونَ الْماعُونَ [٧] قال: الماعون متاع البيت. وقيل: هو الزكاة، وهو المال بلغة الحبش، والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-736>السورة التي يذكر فيها الكوثر</span>

<span data-type="title" id=toc-737>[سورة الكوثر (١٠٨) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
إِنَّا أَعْطَيْناكَ الْكَوْثَرَ (١) فَصَلِّ لِرَبِّكَ وَانْحَرْ (٢) إِنَّ شانِئَكَ هُوَ الْأَبْتَرُ (٣)
قوله تعالى: إِنَّا أَعْطَيْناكَ الْكَوْثَرَ [١] قال: لما مات القاسم بمكة وإبراهيم بالمدينة قالت قريش: أصبح محمد صلّى الله عليه وسلّم أبتر، فغاظه ذلك، فنزلت: إِنَّا أَعْطَيْناكَ الْكَوْثَرَ [١] نعزيه ونعوضه الكوثر، وهو الحوض، تسقي من شئت بإذني، وتمنع من شئت بإذني. فَصَلِّ لِرَبِّكَ وَانْحَرْ (٢) إِنَّ شانِئَكَ هُوَ الْأَبْتَرُ [٢- ٣] عن خير الدارين أجمع.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-738>السورة التي يذكر فيها الكافرون</span>

<span data-type="title" id=toc-739>[سورة الكافرون (١٠٩) : الآيات ١ الى ٦]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
قُلْ يا أَيُّهَا الْكافِرُونَ (١) لاَ أَعْبُدُ مَا تَعْبُدُونَ (٢) وَلا أَنْتُمْ عابِدُونَ مَآ أَعْبُدُ (٣) وَلا أَنا عابِدٌ مَّا عَبَدْتُّمْ (٤)
وَلا أَنْتُمْ عابِدُونَ مَآ أَعْبُدُ (٥) لَكُمْ دِينُكُمْ وَلِيَ دِينِ (٦)
قوله تعالى: قُلْ يا أَيُّهَا الْكافِرُونَ [١] قال: إنَّما ذكر: قُلْ [١] جواباً عن سؤال الكفار إياه: «اعبد إلهنا شهراً فنعبد إلهك سنة» . فأنزل الله تعالى هذه السورة عند قولهم ذلك، يا أَيُّهَا الْكافِرُونَ [١] قالوا: ما لك يا محمد. قال: لاَ أَعْبُدُ مَا تَعْبُدُونَ [٢] اليوم.
وَلا أَنْتُمْ عابِدُونَ مَآ أَعْبُدُ [٣] اليوم. وَلا أَنا عابِدٌ مَّا عَبَدْتُّمْ [٤] في المستقبل. وَلا أَنْتُمْ عابِدُونَ مَآ أَعْبُدُ [٥] في المستقبل. لَكُمْ [٦] اختياركم ل دِينُكُمْ وَلِيَ [٦] اختياري ل دِينِ [٦] ، ثم نسختها آية السيف «٣» ، والله سبحانه وتعالى أعلم.
_________
(١) شعب الإيمان ٥/ ٣٣٦ والترغيب والترهيب ١/ ٢٣ والفردوس بمأثور الخطاب ٥/ ٢٧١ وفيض القدير ١/ ٢١٧ وجامع العلوم والحكم ص ١٦ ومجمع الزوائد ١٠/ ٢٢١.
(٢) نوادر الأصول ١/ ٩١ وشعب الإيمان ٥/ ٣٤٢- ٣٤٣ والفردوس بمأثور الخطاب ١/ ٤٣٥.
(٣) آية السيف هي الآية الخامسة من سورة التوبة. وهذه الآية قال فيها الضحاك بن مزاحم: (إنها نسخت كل عهد بين النبي صلى الله عليه وسلّم وبين أحد من المشركين، وكل عقد، وكل مدة) . تفسير ابن كثير ٢/ ٣٥٠.',NULL,'207',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (205,'<span data-type="title" id=toc-740>السورة التي يذكر فيها النصر</span>

<span data-type="title" id=toc-741>[سورة النصر (١١٠) : الآيات ١ الى ٣]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
إِذا جاءَ نَصْرُ اللَّهِ وَالْفَتْحُ (١) وَرَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْواجاً (٢) فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُ إِنَّهُ كانَ تَوَّاباً (٣)
قوله تعالى: إِذا جاءَ نَصْرُ اللَّهِ [١] قال: إذا جاء نصر الله لدينك والفتح لدينك.
وَرَأَيْتَ النَّاسَ [٢] وهم أهل اليمن. يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْواجاً [٢] زمراً، القبيلة بأسرها، والقوم بأجمعهم، فانصر روحك على نفسك بالتهيؤ للآخرة لأنه منها، فالنفس تريد الدنيا لأنها منها، والروح تريد الآخرة لأنه منها، فانصر على النفس وافتح له باب الآخرة بالتسبيح والاستغفار لأمتك. وكان يستغفر بعد ذلك ويسبح بالغداة مائة مرة، وبالعشي مائة مرة، واجتهدَ في العبادة ليلاً ونهاراً حتى تورمت قدماه، واحمرت عيناه، واصفرت وجنتاه، وقلّ تبسمه، وكثر بكاؤه وفكرته.
وقد حكي عن ابن عباس رضي الله عنهما أنه قال: لما نزلت هذه السورة واستبشر بها أصحاب النبي صلى الله عليه وسلّم بكى أبو بكر «١» رضي الله عنه بكاء شديداً فقال له رسول الله صلّى الله عليه وسلّم: «ما يبكيك؟ قال: نعيت لك نفسك يا رسول الله. فقال له النبي صلّى الله عليه وسلّم: صدقت» ، ثم قال: «اللهم فقهه في الدين وعلمه التأويل» «٢» ، وهذا تعليم لأمته بالدين والتسبيح. وقد قال الربيع بن خيثم رحمه الله تعالى: أقلوا الكلام إلا من تسع: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر، وقراءة القرآن، وأمر بالمعروف، ونهي عن المنكر، ومسألة خير، وتعوذ من شر «٣» .
إِنَّهُ كانَ تَوَّاباً [٣] أي رجاعاً يقبل التوبة، كلما تاب العبد إليه. واعلم أن إلهنا أكرم من أن يكون معك على نفسك، فإنه قال: إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ [البقرة: ٢٢٢] فإن كنت عليها كان معها بالعفو، وإن كنت معها على أمر الله ونهيه كان عليك، فمن وافق أمر الله على هواه كان ناجياً، ومن وافق هواه على أمر الله كان هالكاً، وإنّ أمر الله تعالى مرّ وهوى النفس حلو، فما مثالها إلا كالأطعمة اللذيذة قد يحصل فيها الصبر، والدواء يشرب مع مرارته لما جعل فيه من المنافع. وكان بعض الصالحين يقول: وا سوأتاه، وإن عفوت. فمنهم من يحذر الرد، ومنهم من يبكي خجلاً، وإن عفي عنه. والله سبحانه وتعالى أعلم.
_________
(١) ليس في كتب الحديث ما يشير إلى أن أبا بكر هو من عرف أن في هذه السورة نعي النبي صلى الله عليه وسلّم، بل هو ابن عباس. انظر: صحيح البخاري: باب تفسير سورة النصر، رقم ٤٦٨٥- ٤٦٨٦، وباب المناقب، رقم ٣٤٢٨ وتحفة الأحوذي ٩/ ٢٠٨.
(٢) صحيح البخاري: باب وضع الماء عند الخلاء، رقم ١٤٢ والمستدرك على الصحيحين، رقم ٢٦٨٠ ومسند أحمد ١/ ٣٣٥ ومصنف ابن أبي شيبة ٦/ ٣٨٣ ومجمع الزوائد ٩/ ٢٧٦ والمعجم الصغير ١/ ٣٢٧ والمعجم الكبير ١٠/ ٢٦٣، ١١/ ١١٠، ١٢/ ٧٠ والمعجم الأوسط ٤/ ١١٣، ٢٧٣.
(٣) الحلية ٢/ ١٠٩.',NULL,'208',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (206,'<span data-type="title" id=toc-742>السورة التي يذكر فيها المسد</span>

<span data-type="title" id=toc-743>[سورة المسد (١١١) : الآيات ١ الى ٥]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
تَبَّتْ يَدا أَبِي لَهَبٍ وَتَبَّ (١) مَآ أَغْنى عَنْهُ مالُهُ وَما كَسَبَ (٢) سَيَصْلى ناراً ذاتَ لَهَبٍ (٣) وَامْرَأَتُهُ حَمَّالَةَ الْحَطَبِ (٤)
فِي جِيدِها حَبْلٌ مِنْ مَسَدٍ (٥)
قوله تعالى: تَبَّتْ يَدا أَبِي لَهَبٍ وَتَبَّ [١] قال: أي خسرت يداه، وَتَبَّ [١] أي خسر، فالخسران الأول خسران المال، والخسران الآخر خسران النفس، ومعنى الخسران ما ذكر بعد ذلك، فقال: مَآ أَغْنى عَنْهُ مالُهُ [٢] في الآخرة، إذ صار إلى النار وَما كَسَبَ [٢] يعني ولده عتبة وعتيبة ومعتب.
وفيها وجه آخر: أن يكون التباب الأول كالدعاء عليه، والثاني كالإخبار عن وقوع الخسران في سابق التقدير، وهو جواب عن قول أبي لهب للنبي صلّى الله عليه وسلّم: «تباً لك» «١» حين جمعهم ودعاهم إلى التوحيد، وأنذرهم العذاب بقوله تعالى: وَأَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ [الشعراء: ٢١٤] .
قوله تعالى: سَيَصْلى نَاراً [٣] سيغشى أبو لهب ناراً في الآخرة. ذاتَ لَهَبٍ [٣] أي ليس لها دخان. وَامْرَأَتُهُ [٤] أم جميل. حَمَّالَةَ الْحَطَبِ [٤] قيل النمامة. وقال عكرمة:
إنها كانت تحمل الشوك تلقيه على طريق النبي صلّى الله عليه وسلّم. فِي جِيدِها حَبْلٌ مِنْ مَسَدٍ [٥] أي سلسلة من حديد في النار كحديد البكرة التي تجري فيه، شهرها بهذه العلامة في جهنم، كما كانت مشهورة بعداوة النبي صلى الله عليه وسلّم.
والله سبحانه وتعالى أعلم.

<span data-type="title" id=toc-744>السورة التي يذكر فيها الإخلاص</span>
سئل سهل عن الإخلاص، فقال: هو الإفلاس، يعني من علم أنه مفلس فهو محق. قال:
وأبطل الله جميع الكفر والأهواء بهذه الأربع آيات. وإنما سميت سورة الإخلاص لأنها تنزيه الله تعالى عن كل ما لا يليق به.

<span data-type="title" id=toc-745>[سورة الإخلاص (١١٢) : الآيات ١ الى ٤]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
قُلْ هُوَ اللَّهُ أَحَدٌ (١) اللَّهُ الصَّمَدُ (٢) لَمْ يَلِدْ وَلَمْ يُولَدْ (٣) وَلَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ (٤)
قوله تعالى: قُلْ هُوَ اللَّهُ أَحَدٌ [١] ليس له كفء ولا مثل.
اللَّهُ الصَّمَدُ [٢] قال: الصمد السيد الذي صمد إليه في الحوائج والعوارض، ومعناه المصمود إليه. وقال: الصمد الذي لا يحتاج إلى الطعام والشراب.
لَمْ يَلِدْ [٣] فيورث. وَلَمْ يُولَدْ [٣] فيكون ملكه محدثاً. وهو أيضاً إثبات الفردانية، ونفي الأسباب عنه، رداً على الكفار.
وَلَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ [٤] معناه: ولم يكن له أحد كفئاً على جهة التقديم.
والله سبحانه وتعالى أعلم.
_________
(١) صحيح البخاري: كتاب التفسير، رقم ٤٤٩٢.',NULL,'209',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (207,'<span data-type="title" id=toc-746>السورة التي يذكر فيها الفلق</span>

<span data-type="title" id=toc-747>[سورة الفلق (١١٣) : الآيات ١ الى ٥]</span>
بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ
قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ (١) مِنْ شَرِّ مَا خَلَقَ (٢) وَمِنْ شَرِّ غاسِقٍ إِذا وَقَبَ (٣) وَمِنْ شَرِّ النَّفَّاثاتِ فِي الْعُقَدِ (٤)
وَمِنْ شَرِّ حاسِدٍ إِذا حَسَدَ (٥)
قوله تعالى: قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ [١] قال: إن الله تعالى أمره في هاتين السورتين بالاعتصام والاستعانة به، وإظهار الفقر إليه. قيل: ما إظهار الفقر؟ قال: هو الحال بالحال، لأن الطبع ميت وإظهاره حياته.
وقال: أفضل الطهارة أن يطهر العبد من حوله وقوته، وكل فعل أو قول لا يقارنه «لا حول ولا قوة إلا بالله» لا يتولاه الله عزَّ وجلَّ، وكل قول لا يقارنه استثناء عوقب عليه، وإن كان براً، وكل مصيبة لا يقارنها استرجاع لم يثبت عليها صاحبها يوم القيامة.
قال: والفلق: الصبح عند ابن عباس رضي الله عنه، وهو عند الضحاك: وادٍ في النار، وعند وهب: بيت في النار، وعند الحسن: جب في النار.
وقيل: أراد به جميع الخلق، وقيل: هو الصخور تنفلق عن المياه.
مِنْ شَرِّ مَا خَلَقَ [٢] من الإنس والجن، وذلك أن لبيد بن أعصم اليهودي سحر النبي صلى الله عليه وسلّم في بئر بني بياضة، وكان يسد إليها فاسد إليها فدب فيه السحر، فاشتد عليه ذلك، فأنزل الله تعالى المعوذتين، وأخبره جبريل عليه السلام بالسحر، وأخرج إليها رجلين من أصحابه فأخرجاه من البئر، وجاءا به إلى النبي صلّى الله عليه وسلّم، فجعل يحل عقدة ويقرأ آية، حتى برىء رسول الله صلّى الله عليه وسلّم بعد ما ختم السورتين بلا مهلة، فكان لبيد بعد ذلك يأتي إلى النبي صلى الله عليه وسلّم فما رأى في وجه النبي صلى الله عليه وسلّم من ذلك شيئاً، ولا ذاكره ذلك «١» .
وَمِنْ شَرِّ غاسِقٍ إِذا وَقَبَ [٣] يعني إذا دخل الليل. وقيل: إذا اشتدت ظلمته. وقيل:
وقوب الليل في النهار أول الليل ترسل فيه عفاريت الجن فلا يشفى مصاب تلك الساعة.
قال سهل: وَمِنْ شَرِّ غاسِقٍ إِذا وَقَبَ [٣] باطنها الذكر إذا دخله رؤية النفس، فستر عن الإخلاص لله بالذكر فيه.
وَمِنْ شَرِّ النَّفَّاثاتِ فِي الْعُقَدِ [٤] أي السواحر تنفث في العقد.
وَمِنْ شَرِّ حاسِدٍ إِذا حَسَدَ [٥] يعني اليهود حسدوا النبي صلى الله عليه وسلّم حتى سحروه.
وقال ابن عباس رضي الله عنهما: في هذه الآية هو نفس ابن آدم.
والله سبحانه وتعالى أعلم.
_________
(١) انظر خبر السحر في: صحيح البخاري: باب هل يستخرج السحر، رقم ٥٤٣٢، وباب إِنَّ الله يَأْمُرُ بالعدل، رقم ٥٧١٦، ودلائل النبوة للأصبهاني ١/ ١٧٠ والسيرة ٣/ ٤٨.',NULL,'210',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (208,'<span data-type="title" id=toc-748>السورة التي يذكر فيها الناس</span>

<span data-type="title" id=toc-749>[سورة الناس (١١٤) : الآيات ٤ الى ٦]</span>
مِنْ شَرِّ الْوَسْواسِ الْخَنَّاسِ (٤) الَّذِي يُوَسْوِسُ فِي صُدُورِ النَّاسِ (٥) مِنَ الْجِنَّةِ وَالنَّاسِ (٦)
قوله تعالى: مِنْ شَرِّ الْوَسْواسِ الْخَنَّاسِ [٤] قيل لسهل: ما الوسوسة؟ فقال: كل شيء دون الله تعالى فهو وسوسة، وإن القلب إذا كان مع الله تعالى فهو قائل عن الله تعالى، وإذا كان مع غيره فهو قائل مع غيره.
ثم قال: من أراد الدنيا لم ينج من الوسوسة، ومقام الوسوسة من العبد مقام النفس الأمارة بالسوء، وهو ذكر الطبع، فوسوسة العدو في الصدور، كما قال: يُوَسْوِسُ فِي صُدُورِ النَّاسِ (٥) مِنَ الْجِنَّةِ وَالنَّاسِ [٥- ٦] يعني في صدور الجن والإنس جميعاً، ووسوسة النفس في القلب.
قال الله تعالى: وَنَعْلَمُ مَا تُوَسْوِسُ بِهِ نَفْسُهُ وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ الْوَرِيدِ [ق: ١٦] وإن معرفة النفس أخفى من معرفة العدو، ومعرفة العدو أجلى من معرفة الدنيا، وأسر العدو معرفته، فإذا عرفته فقد أسرته، وإن لم تعرف أنه العدو وأسرك فإنما مثل العبد والعدو والدنيا كمثل الصياد والطير والحبوب، فالصياد إبليس، والطير العبد، والحبوب الدنيا، وما من نظرة إلا وللشيطان فيها مطمع، فإن كنت صائماً فأردت أن تفطر قال لك: ما يقول الناس، أنت قد عُرفت بالصوم تركت الصيام. فإن قلت: مالي وللناس. قال لك: صدقت أفطر، فإنهم سيضعون أمرك على الحسبة والإخلاص في فطرك، وإن كنت عرفت بالعزلة فخرجت. قال: ما يقول الناس، تركت العزلة.
فإن قلت: مالي وللناس. قال: صدقت اخرج فإنهم سيضعون أمرك على الإخلاص والحسبة.
وكذلك في كل شيء من أمرك، يردك إلى الناس حتى كأنه ليأمرك بالتواضع للشهرة عند الناس.
ولقد حكي أن رجلاً من العباد كان لا يغضب، فأتاه الشيطان وقال: إنك إن تغضب وتصبر كان أعظم لأجرك. ففطن به العابد فقال: وكيف يجيء الغضب؟ قال: آتيك بشيء فأقول: لمن هو؟
فقل: هو لي، فأقول: بل هو لي. فأتاه بشيء وقال العابد: هو لي، فقال الشيطان: لا بل هو لي.
فقال العابد: إن كان لك فاذهب به، ولم يغضب، فرجع الشيطان خائباً حزيناً، أراد أن يشغل قلبه حتى يصيب منه حاجته، فعرفه واتقى غرورة.
ثم قال سهل: عليك بالإخلاص تسلم من الوسوسة، وإياك والتدبير فإنه داء النفس، وعليك بالاقتداء فإنه أساس العمل، وإياك والعجب فإن أدنى باب منه لم تستتمه حتى تدخل النار، وعليك بالقنوع والرضا، فإن العيش فيهما، وإياك والائتمار على غيرك، فإنه لينسيك نفسك، وعليك بالصمت، فأنت تعرف الأحوال فيه، وعليك بترك الشهوات تنقطع به عن الدنيا، وعليك بسهر الليل تموت نفسك من ميلة طبعك وتحيي قلبك، وإذا صليت فاجعلها',NULL,'211',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (209,'وداعاً، وخف الله يؤمنك، وارجهُ يؤملك، واتكل عليه يَكفِك، وعليك بالخلوة تنقطع الآفات عنك. ولقد قال ابن عباس رضي الله عنهما: لولا مخافة الوسواس لرحلت إلى بلاد لا أنيس بها، وهل يفسد الناس إلا الناس.
ثم قال سهل: مخالطة الولي بالناس ذل، وتفرده عزّ، وما رأيت أولياء الله تعالى إلا منفردين، إن عبد الله بن صالح رحمه الله كان رجلاً له سابقة جليلة وموهبة جزيلة، وكان يفرّ من بلد إلى بلد، حتى يأتي مكة، فطال بها مقامه، فقلت له: لقد طال مقامك بها. فقال: ولم لا أقيم بها، ولم أر بقعة ينزل فيها من الرحمة والبركة مثلها يطوف الملائكة حول البيت غدواً وعشية على صور شتى، لا يقطعون ذلك، وإن فيها عجائب كثيرة، ولو قلت كلما رأيت لصغرت عنه قلوب أقوام ليسوا بمؤمنين. فقلت: أسألك بحق الحق أن تخبرني بشيء من ذلك. فقال: ما من ولي لله تعالى صحّت ولايته إلا وهو يحضر في هذه البلد في كل ليلة جمعة، ولقد رأيت رجلاً يقال له مالك بن القاسم الجبلي رحمه الله تعالى ليلة هاهنا ورايت على يده غمراً فقلت: إنك لقريب العهد بالأكل. فقال: أستغفر الله فإني منذ أسبوع لم أطعم شيئاً، ولكني أطعمت والدتي وأسرعت لأدرك صلاة الفجر هاهنا جماعة، وبين مكة وبين الموضع الذي جاء منه سبعمائة فرسخ، فهل أنت مؤمن بذلك؟ فقلت: بلى. فقال: الحمد لله الذي أراني مؤمناً مؤمناً «١» .
وقال ابن سالم: كنت عند سهل رحمه الله تعالى، فأتاه رجلان بعد صلاة العصر، وجعلا يحدثانه، فقلت في نفسي: لقد أبطأا عنده، وما أراهما يرجعان في هذا الوقت، وذهبت إلى منزلي لأهيىء لهما عشاء، فلما رجعت إليه لم أر عنده أحداً، فسألت عن حالهما، فقال: إن أحدهما يصلي المغرب بالمشرق، والآخر بالمغرب، وإنما أتياني زائرين.
ولقد دخل سهل على رجل من عباد البصرة، فرأى عنده بلبلة في قفص، فقال: لمن هذه البلبلة؟ فقال: لهذا الصبي، كان ابناً له، قال: فأخرج سهل من كمه دينار فقال: بني أيما أحب إليك الدينار أم البلبلة؟ فقال: الدينار. فدفع إليه الدينار وأطلق البلبلة. قال: فقعد البلبل على حائط الدار حتى خرج سهل، فجعل يرفرف فوق رأسه، حتى دخل سهل داره، وكان في داره سدرة فسكنت البلبلة السدرة، فلم تزل فيها حتى مات، فلما رفعوا جنازته جعلت ترفرف فوق جنازته والناس يبكون، حتى جاؤوا بها إلى قبره، فوقفت في ناحية حتى دفن وتفرق الناس عن قبره، فلم تزل تضطرب على قبره حتى ماتت، فدفنت بجنبه.
والله سبحانه وتعالى أعلم.
_________
(١) صفوة الصفوة ٤/ ٢٥٤ (رقم ٧٨٨) .',NULL,'212',NULL,NULL,'0');
INSERT INTO [b23600] VALUES (210,'الفهارس الفنية
فهرس الآيات الشواهد فهرس الأحاديث القدسية/ ٢١٨ فهرس الأحاديث النبوية/ ٢١٩ فهرس الأقوال والآثار/ ٢٢٢ فهرس الأعلام/ ٢٢٣ فهرس الأشعار/ ٢٢٦ فهرس المواضع والبلدان/ ٢٢٧ فهرس المصادر والمراجع/ ٢٢٨ فهرس المحتويات/ ٢٣٥',NULL,'213',NULL,NULL,'0');
