INSERT INTO [b23630] VALUES (911,'وسق: ٢/ ٢٩١، اتسق ٢/ ١٩١ وسل: الوسيلة ١/ ١٦٤ وسم: المتوسمين ١/ ٣٥٤ وسن: سنة ١/ ٧٨ وصب: واصبا ١/ ٢٦١، واصب ٢/ ١٦٦ وصد: بالوصيد ١/ ٣٩٧ وصل: وصيلة ١/ ١٧٨، وصلنا ٢/ ١٠٨ وصى: تواصوا ٢/ ٢٢٧ وضع: لا وضعوا ١/ ٢٦١ وضن: موضونة ٢/ ٢٤٨، الوضين ٢/ ٢٤٨ وطأ: يواطئوا ١/ ٢٥٩، وطئا ٢/ ٢٧٣ وطر: وطرا ٢/ ١٣٨ وعد: وعد ١/ ٢٧٣، ٢/ ١٨٩، الوعد ٢/ ١٤٩ وعظ: موعظة ١/ ٨٣، يوعظون ١/ ١٣١ وعل: الوعل ١/ ١٤٠ وعى: واعية ٢/ ٢٦٧، يوعون ٢/ ٢٩٢ وفد: وفدا ٢/ ١١ وفق: وفاقا ٢/ ٢٨٢ وفى: الموفون ١/ ٦٥، وفيت وأوفيت ١/ ١٤٥، يتوفاكم ٢/ ١٣١ وقت: موقوتا ١/ ١٣٩ وقد: وقودها ١/ ٣٤، أوقدوا ١/ ١٧١ وقذ: الموقوذة ١/ ١٥١ وقر: وقرا ١/ ١٨٩، ٣٨٠ وقع: الوقيع ١/ ٣٤٣، وقعت الواقعة ٢/ ٢٤٧ وقل: وقل ٢/ ٧٢ وقى: تقاة ١/ ٩٠ وكأ: متكئا ١/ ٣٠٩ وكز: فوكزه ٢/ ٩٩','2','451',NULL,NULL,'0');
INSERT INTO [b23630] VALUES (912,'وكل: وكلنا ١/ ٢٠٠، وك<span data-type="title" id=toc-2478>ي</span>ل ١/ ٢٠٣، وكيلا ٢/ ٧٥ ولج: تولج ١/ ٩٠، وليجة ١/ ٢٥٤، يلج ٢/ ١٤٢ ولد: بالوالدين ١/ ١٢٦، ولدا ٢/ ١٢ ولى: أولى ١/ ١٢، ٢/ ٢٧٨، موليها ١/ ٦٠، فان تولوا ١/ ٩٠، ٩٦، موالى ١/ ١٢٤، مولى ١/ ١٢٤، ٢/ ٢٠٩، ٢١٥، الأوليان ١/ ١٨١ مولاهم ١/ ١٩٤، ولا تولوا ١/ ٢٤٥، ولايتهم ١/ ٢٥١، مولانا ١/ ٢٦٢ الولاية ١/ ٤٠٥، وليا ٢/ ١، الموالي ٢/ ١، المولى ٢/ ٥٤، موليكم ٢/ ١٣٤ فتول ٢/ ٢٢٨، مولاكم ٢/ ٢٥٤ وهب: وهبت ٢/ ١٣٩، يهب ٢/ ٢٠١ وهج: وهاجا ٢/ ٢٨٢ وهن: ولا تهنوا ١/ ١٠٤، وهن ٢/ ١٢٦، فلا تهنوا ٢/ ٢١٦

(ى)
يئس: ليؤس ١/ ٢٨٦، استيئسوا ١/ ٣١٥، ييئس ١/ ٣٣٢، يئوسا ١/ ٣٨٩، يئوس ٢/ ١٩٨ يدى: أيديهما ١/ ١٠، لما بين يديه ١/ ٤٨، يد الله ١/ ١٧٠ يرع: اليراعة ١/ ٣٤٤ يس: يس ٢/ ١٥٧ يسر: الميسر ١/ ٧٣، ١٧٥، ميسورا ١/ ٣٧٥، يسرا ٢/ ٣٠٣ يعف: اليعافير ٢/ ٧٨، ٢٣٧ يقط: يقطين ٢/ ١٧٥ يقظ: أيقاظا ١/ ٣٩٦ يقن: اليقين ٢/ ٢٥٣ يمم: ولا تيمموا، ١/ ٨٢، اليم ١/ ٢٢٧، ٢/ ١٩، ١٠٦ يمن: أيمانهم ١/ ٢٥٣، ذات اليمين ١/ ٣٩٧ ينع: ينعه ١/ ٢٠٢ يوم: يومئذ ٢/ ٢٦٢','2','452',NULL,NULL,'0');
INSERT INTO [b23630] VALUES (913,'<span data-type="title" id=toc-2479>فهرس الأحاديث والأمثال</span>
أرأيت من لا شرب ... الحديث ١: ٦٤، ١٥٠ اللهم اشدد وطأتك ... الحديث ٢: ٢٠٨ إن الزمان قد استدار ... الحديث ١: ٢٥٩ أولئك الملأ من قريش ... الحديث ١: ٧٧ تحسبها حمقاء ... المثل ١: ٨٣، ٢١٩ جذها جذ العير ... المثل ١: ٢٩٩ دخلت مع النبي عليه الصلاة والسلام بيت ميمونة فأتى بضب ... الحديث ١: ٢٩٢ ح خير المال سكة مأبورة ... الحديث ١: ٣٧٤ ح ردوا على أبى ... الحديث ١: ٥٧ رهبوت خير من رحموت ... المثل ١: ١٩٨ شر ما أجاءني إلى مخة ... المثل ٢: ٤ العجماء جبار لا تؤدى ... الحديث ٢: ٩١ كل مولود يولد ... الحديث ٢: ١٢٢ كما تدين تدان ... المثل ١: ٢٣ كيف إذا بقيت فى حثالة ... الحديث ٢: ٢٢٢ القيد والرتعة ... المثل ١: ٣٠٣ ليت من العشب خوصية ... ١: ١٥٧، ٢: ٥٨
مرجت عهودهم ... الحديث ٢: ٧٧ مليت بيوتهم حبرة ... المثل ٢: ١٢٠ نحن بنو النضر بن كنانة ... الحديث ١: ٣٧٩ وحسبك من شر ... المثل ٢: ٢٧٦ وعثرت على الغزل ... المثل ١: ١٨١ وكان النبي صلى الله عليه وسلم أملكهم لإربه ... الحديث ٢: ٦٥ يبعث زيد بن عمر بن نفيلة أمة وحده ... الحديث ١: ١٠٠.','2','453',NULL,NULL,'0');
