INSERT INTO [b23696] VALUES (32,'جبّان [١] طبّاخ بقّال فامىّ تاجر، ميزاب مرزاب مزراب مثعب، باقلّى [٢] فول، قدر [٣] برمة، موقدة أثافىّ، زنبيل مكتل قفّة، سفل مركن [٤] إجّانة تغار، قنطار بهار، من رطل، حبّة طسّوج، خادم قيّم مغرّك، بلّان [٥] ، شمشك صندل، حصن قلعة قهندز كلات، صاحب ربع مصلحة [٦] مسلحة صاحب الطريق، عشّار مكلّس مرصدىّ [٧] ، مخاصم خصيم [٨] ، حاكم قاض، وكيل جريّ [٩] ، شيرج سليط، زجّاج قواريرىّ، صفع صكّ، بقعة موضع [١٠] ، قطّة سنّور دمّة هرّة، معلّم خادم استاذ شيخ خصىّ [١١] ، دبّاغ صرّام [١٢] أدمىّ سختيانىّ جلودىّ، فاعل روزكارى، قرياتىّ [١٣] رستاقىّ سواديّ، زرّاع فلّاح حرّاث، فندق خان تيم دار التجار [١٤] ، مرزبة أكلة، حبل قلس، وتد كنورا [١٥] ، هدّنها كركرها، لصّ مشوشا، جنحت ولجت [١٦] ، أنقض زور [١٧] ، قف هلى، هيارا جماعة، لكيشا كثير [١٨] ، زرنوق دولاب حنّانة [١٩] ، دالية كرمة [٢٠] ، مسحاة مجرفة، معول فاس، صاعدا زقافا [٢١] ، منحدرا شبالا، طاروس شرته، سكّان رجل، ربّان راس [٢٠] ، ملّاح نوتىّ، ساحل شطّ، رقعة بطاقة [٢٢] ، روحة نفسة، سفينة جاسوس [٢٣] زورق رقيّة تلوّى عرداس [٢٤] طيّار زبزب كاروانيّة مثلّثة واسطيّة [٢٠] ملقوطة شنكولية [٢٥] براكيّة
