INSERT INTO [b23696] VALUES (116,'هارونيّة جلولا باجسرى [١] باقبة [٢] إسكاف بوهرز [٣] كلواذى درزيجان [٤] المدائن كيل [٥] سيب دير العاقول النّعمانيّة جرجرايا [٦] جبّل نهر سابس عبرتا بابل عبدس [٧] قصر هبيرة واما حلوان فمن مدنها خانقين زبوجان شلاشان [٨] الجامد الحرّ السّيروان بندنيجان [٩] واما سامرّا فمن مدنها الكرخ [١٠] عكبرا الدّور الجامعين [١١] بتّ راذانان [١٢] قصر الجصّ جوى [١٣] أيوانا بريقا [١٤] سنديّة ٥ راقفروبة [١٥] دممّا الأنبار هيت تكريت السّنّ فان قال قائل لم جعلت بابل في الجند واليها كان [١٦] ينسب الإقليم في القديم الا ترى ان الجيهانىّ ابتدأ بذكر هذه النواحي وسمّاها [١٧] إقليم بابل وكذلك سمّاها وهب [١٨] في المبتدإ وغيره من العلماء [١٩] قيل له قد تحرّزنا من هذا السؤال ونظائر بأنّا أجرينا علمنا [٢٠] على التعارف كالايمان [٢١] الا ترى ان رجلا لو حلف ان لا يأكل رءوسا ١٠
