INSERT INTO [b23696] VALUES (297,'جا [١] مذر برواز [٢] ، ومن النواحي طخارستان وهي القصبة أيضا ومن مدنها ولوالج [٣] الطّلقان خلم غربنك [٤] سمنجان اسكلكند [٥] رؤب بغلان السفلى بغلان العليا [٦] اسكيمشت راون [٧] آرهن أندراب خست [٨] سراى عاصم، والباميان ومدنها بسغورفند [٩] سكاوند لخراب، ولبلخ أيضا من الرستاق بدخشان بنجهير جاربايه [١٠] بروان [١١] جميعهنّ مدن جليلة واعمال واسعة واما غزنين فإنها كورة جليلة [١٢] وهي اسم القصبة أيضا ومن مدنها كرديس [١٣] سكاوند نوه بردن دمراخى [١٤] حشّ بارى [١٥] فرمل سرهون [١٦] لجرا خواست [١٧]
