INSERT INTO [b23696] VALUES (469,'جنزروذ [١] فرزين وبين جيرفت والسيرجان ناجت خير [٢] وبين السيرجان وفارس [٣] كشيستان جيروقان [٤] مرزقان السورقان مغون [٥] لم أدخلهنّ ولم أر عاقلا أدبّر معه في بابهنّ والى اين وجب [٦] ان يضفن [٧] واما إسبيذ [٨] فقد جعلناها نظير تيماء
<span data-type="title" id=toc-49>جمل شئون هذا الإقليم</span>
