INSERT INTO [b2547] VALUES (1,'<span data-type="title" id=toc-1>المقدمة</span>
INSERT INTO [b2547] VALUES (2,'{بسم الله الرحمن الرحيم} أَيْ: ابدؤوا أوِ افتتحوا بتسمية الله تيمُّناً وتبرُّكاً و {الله} : اسمٌ تفرَّد الباري به سبحانه يجري فِي وصفه مجرى أسماء الأعلام لا يُعرف له اشتقاق وقيل: معناه: ذو العبادة التي بها يُقصد {الرَّحمن الرَّحيم} : صفتان لله تعالى معناهما: ذو الرَّحمة أَي: الرَّحمة لازمةٌ له وهي إرادة الخير ولا فرق بينهما مثل: ندمانٍ ونديم',NULL,'88',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3,'{الحمدُ لله} هو الثَّناء لله والشُّكرُ له بإنعامه {ربِّ العالمين} : مالك المخلوقات كلها',NULL,'88',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4,'قوله تعالى {الرحمن الرحيم}',NULL,'88',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5,'{مالك يوم الدِّين} مأخوذٌ من المِلْك والمِلْك مأخوذٌ من المُلْك أَيْ: قاضي يوم الجزاء والحساب لأنَّه متفرِّدٌ في ذلك اليوم بالحكم',NULL,'88',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6,'{إيَّاك نعبدُ} أَيْ: نخصُّك ونقصدك بالعبادة وهي الطَّاعة مع الخضوع {وإيَّاك نستعين} : ومنك نطلب المعونة',NULL,'89',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (7,'{اهدنا الصراط المستقيم} أَيْ: دُلَّنا عليه واسلكْ بنا فيه وثبِّتنا عليه',NULL,'89',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (8,'{صراط الذين أنعمتَ عليهم} بالهداية وهم قومُ موسى وعيسى عليهما السَّلام قبل أن يُغيِّروا نعمَ الله عز وجل وقيل: هم الذين ذكرهم الله عز وجل في قوله تعالى {فأولئك مع الذين أنعم الله عليهم} الآية {غير المغضوب عليهم} أَيْ: غير الذين غضبتَ عليهم وهم اليهود ومعنى الغضب من الله تعالى: إرادةُ العقوبة {ولا الضَّالين} أَيْ: ولا الذين ضلُّوا وهم النَّصارى فكأنَّ المسلمين سألوا الله تعالى أن يهديهم طريق الذين أنعم عليهم ولم يغضب عليهم كما غضب على اليهود ولم يضلُّوا عن الحقِّ كما ضلَّت النَّصارى',NULL,'89',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (9,'{الم} أنا الله أعلم',NULL,'90',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (10,'{ذلك الكتاب} أَيْ: هذا الكتاب يعني: القرآن {لا ريب فيه} أي: لاشك فيه أَيْ: إنَّه صدقٌ وحقٌّ وقيل لفظه لفظ خبرٍ ويُراد به النهي عن الارتياب قال: {فلا رفث ولا فسوق} ولا ريب فيه أنَّه {هدىً} : بيانٌ ودلالةٌ {للمتقين} : للمؤمنين الذين يتَّقون الشِّرْك في تخصيصه كتابه بالهدى للمتقين دلالةٌ على أنَّه ليس بهدىً لغيرهم وقد قال: {والذين لا يؤمنون في آذانهم وقر}',NULL,'90',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (11,'{الذين يؤمنون:} يُصدِّقون {بالغيب} : بما غاب عنهم من الجنَّة والنَّار والبعث {ويقيمون الصَّلاة} : يُديمونها ويحافظون عليها {وممَّا رزقناهم} : أعطيناهم ممّا ينتفعون به {ينفقون} : يُخرجونه في طاعة الله تعالى',NULL,'90',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (12,'{والذين يؤمنون بما أُنزل إليك} نزلت في (مؤمني) أهل الكتاب يؤمنون بالقرآن {وما أنزل من قبلك} يعني: التَّوراة {وبالآخرة} يعني: وبالدَّار الآخرة {هم يوقنون} : يعلمونها علماً باستدلالٍ',NULL,'91',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (13,'{أولئك} يعني: الموصوفين بهذه الصِّفات {على هدىً} : بيانٍ وبصيرةٍ {من ربِّهم} أَيْ: من عند ربِّهم {وأولئك هم المفلحون} : الباقون في النَّعيم المقيم',NULL,'91',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (14,'{إنَّ الذين كفروا} : ستروا ما أنعم الله عزَّ وجل به عليهم من الهدى والآيات فجحدوها وتركوا توحيد الله تعالى {سواء عليهم} : معتدلٌ ومتساوٍ عندهم {أأنذرتهم} : أعلمتهم وخوَّفتهم {أم لم تنذرهم} أم تركت ذلك {لا يؤمنون} نزلت فِي أبي جهلٍ وخمسةٍ من أهل بيته ثمَّ ذكر سبب تركهم الإيمان',NULL,'91',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (15,'{ختم الله على قلوبهم} أَيْ: طبع الله على قلوبهم واستوثق منها حتى لا يدخلها الإيمان {وعلى سمعهم} : أَيْ: مسامعهم حتى لا ينتفعوا بما يسمعون {وعلى أبصارهم} : على أعينهم {غشاوة} غطاءٌ فلا يبصرون الحقَّ {ولهم عذابٌ عظيمٌ} مُتواصل لا تتخلَّله فُرجةٌ',NULL,'91',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (16,'{وَمِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الآخر} نزلت فِي المنافقين حين أظهروا كلمة الإيمان وأسرُّوا الكفر فنفى الله سبحانه عنهم الإِيمان بقوله: {وما هم بمؤمنين} فدلَّ أنَّ حقيقة الإيمان ليس الإِقرار فقط',NULL,'92',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (17,'{يخادعون الله والذين آمنوا} أَيْ: يعملون عمل المخادع بإظهار غير ما هم عليه ليدفعوا عنهم أحكام الكفر {وما يخدعون إلاَّ أنفسهم} لأنَّ وبال خداعهم عاد عليهم بإطلاع الله تعالى نبيَّه عليه السَّلام والمؤمنين على أسرارهم وافتضاحهم {وما يشعرون} : وما يعلمون ذلك',NULL,'92',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (18,'{في قلوبهم مرض} شك ونفاقٌ {فزادهم الله مرضاً} أَيْ: بما أنزل من القرآن فشكُّوا فِيهِ كما شكُّوا في الذي قبله {ولهم عذابٌ أليم} : مؤلمٌ {بما كانوا يكذبون} بتكذيبهم آيات الله عز وجل ونبيه صلى الله عليه وسلم (ومَنْ قرأ يُكذِّبون فمعناه: بكذبهم في ادّعائهم الإيمان)',NULL,'92',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (19,'{وإذا قيل لهم} (لهؤلاء) المنافقين: {لا تفسدوا فِي الأرض} بالكفر وتعويق النَّاس عن الإيمان {قالوا إنما نحن مصلحون} أَي: الذين نحن عليه هو صلاحٌ عند أنفسنا فردَّ الله تعالى عليهم ذلك فقال',NULL,'92',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (20,'{أَلا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلَكِنْ لا يَشْعُرُونَ} : لا يعلمون أنَّهم مُفسدون',NULL,'92',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (21,'{وَإِذَا قِيلَ لَهُمْ آمِنُوا كَمَا آمَنَ النَّاسُ} هم أصحاب محمد صلى الله عليه وسلم {قالوا أنؤمن كما آمن السفهاء} أَيْ: لا نفعل كما فعلوا وهذا القول كانوا يقولونه فيما بينهم فأخبر الله تعالى به عنهم',NULL,'93',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (22,'{وإذا لقوا الذين آمنوا} إذا اجتمعوا مع المؤمنين ورأوهم {قالوا آمنَّا} {وإذا خلوا} من المؤمنين وانصرفوا {إلى شياطينهم} : كبرائِهم وقادتهم {قالوا إنَّا معكم} (أَيْ: على دينكم) {إنَّما نحن مستهزئون} : مُظهرون غير ما نضمره',NULL,'93',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (23,'{الله يستهزئ بهم} : يجازيهم جزاء استهزائهم {ويمدُّهم} : يُمهلهم ويطوِّل أعمارهم {في طغيانهم} : في إسرافهم ومجاوزتهم القدر في الكفر {يعمهون} يتردَّدون مُتحيِّرين',NULL,'93',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (24,'{أولئك الذين اشتروا الضلالة بالهدى} : أخذوا الضَّلالة وتركوا الهدى {فما ربحت تجارتُهم} فما ربحوا في تجارتهم (وإضافة الرِّبح إلى التجارة على طريق الاتساع كإضافة الإيضاء إلى النار) {وما كانوا مهتدين} فيما فعلوا',NULL,'93',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (25,'{مثلُهم كمثل الذي استوقد ناراً} أَيْ: حالهم في نفاقهم وإبطانهم الكفر كحالِ مَنْ أَوقد ناراً فاستضاء بها وأضاءت النَّار ما حوله ممَّا يخاف ويحذر وأمن فبينما هو كذلك إذ طُفئت ناره فبقي مُظلماً خائفاً مُتحيِّراً فذلك قوله تعالى: {ذهب الله بنورهم} الآية كذلك المنافقون لمَّا أظهروا كلمة الإيمان اغترُّوا بها وأَمِنُوا فلمَّا ماتوا عادوا إلى الخوف والعذاب',NULL,'93',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (26,'{صمٌّ} لتركهم قبول ما يسمعون {بُكْمٌ} لتركهم القول بالخير {عُمْيٌ} لتركهم ما يُبصرون من الهداية {فهم لا يرجعون} عن الجهل والعمى إلى الإسلام ثمَّ ذكر تمثيلاً آخر فقال',NULL,'94',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (27,'{أو كصيِّبٍ} أو كأصحاب مطرٍ شديدٍ {من السَّماء} : من السَّحاب {فيه} : في ذلك السَّحاب {ظلماتٌ ورعدٌ} وهو صوت مَلَكٍ مُوكَّلٍ بالسَّحاب {وبرق} وهي النَّار التي تخرج منه {يجعلون أصابعهم في آذانهم} يعني: أهل هذا المطر {من الصواعق} من شدَّة صوت الرَّعد يسدُّون آذانهم بأصابعهم كيلا يموتوا بشدَّة ما يسمعون من الصَّوت فالمطر مَثَلٌ للقرآن لما فيه من حياة القلوب والظُّلماتُ مثل لما في القرآن من ذكر الكفر والشرك وبيان الفتن والأهوال والرعد مَثَلٌ لما خُوِّفوا به من الوعيد وذكر النَّار والبرقُ مثلٌ لحجج القرآن وما فيه من البيان وجعل الأصابع في الآذان حذر الموت مثَلٌ لجعل المنافقين أصابعهم في آذانهم كيلا يسمعوا القرآن مخافةَ ميل القلب إلى القرآن فيؤدِّي ذلك إلى الإيمان بمحمد صلى الله عليه وسلم وذلك عندهم كفرٌ والكفر موتٌ {واللَّهُ محيطٌ بالكافرين} مُهلكهم وجامعهم في النَّار',NULL,'94',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (28,'{يكاد البرقُ يخطف أبصارهم} هذا تمثيلٌ آخر يقول: يكاد ما في القرآن من الحجج يخطف قلوبهم من شدَّة إزعاجها إلى النَّظر في أمر دينهم {كلما أضاءَ لهم مشوا فيه} : كُلَّما سمعوا شيئاً ممَّا يُحبّون صدَّقوا وإذا سمعوا ما يكرهون وقفوا وذلك قوله عز وجل: {وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُوا وَلَوْ شَاءَ اللَّهُ لذهب بسمعهم وأبصارهم} أَيْ: بأسماعهم الظَّاهرة وأبصارهم الظَّاهرة كما ذهب بأسماعهم وأبصارهم الباطنة حتى صاروا صُمَّاً عُمياً فليحذروا عاجل عقوبة الله سبحانه وآجلها ف {إنَّ الله على كل شيء قديرٌ} من ذلك',NULL,'94',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (29,'{يا أيها الناس} يعني: أهل مكَّة {اعبدوا ربَّكم} : اخضعوا له بالطَّاعة {الذي خلقكم} : ابتدأكم ولم تكونوا شيئاً {والذين من قبلكم} (آبائكم) (وخاق الذين من قبلكم) أي: إنَّ عبادة الخالق أولى من عبادة المخلوق وهو الصَّنم {لعلَّكم تتقون} لكي تتقوا بعبادته عقوبته أن تحلَّ بكم',NULL,'95',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (30,'{الذي جعل لكم الأرض فراشاً} بساطاً لم يجعلها حَزْنةً غليظةً لا يمكن الاستقرار عليها {والسماء بناءً} سقفاً {وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثمرات} يعني: حمل الأشجار وجميع ما ينتفع به ممَّا يخرج من الأرض {فلا تجعلوا لله أنداداً} : أمثالاً من الأصنام التي تعبدونها {وأنتم تعلمون} أنَّهم لا يخلقون والله هو الخالق وهذا احتجاجٌ عليهم في إثبات التَّوحيد ثمَّ احتجَّ عليهم فِي إثبات نبوَّة محمد صَلَّى اللَّهُ عَلَيْهِ وسلم بما قطع عذرهم به فقال:',NULL,'95',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (31,'{وَإِنْ كُنْتُمْ فِي رَيْبٍ مِمَّا نَزَّلْنَا} أي: وإن كنتم فِي شك من صدق هذا الكتاب الذي أنزلناه على محمد صلى الله عليه وسلم وقلتم: لا ندري هل هو من عند الله أم لا {فأتوا بسورة} من مثل هذا القرآن فِي الإِعجاز وحسن النَّظم والإِخبار عمَّا كان وما يكون {وادعوا شهداءكم} واستعينوا بآلهتكم التي تدعونها {من دون الله إن كنتم صادقين} أنَّ محمداً تقوَّله من نفسه',NULL,'95',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (32,'{فإنْ لم تفعلوا} هذا فيما مضى {ولن تفعلوا} هُ أيضاً فيما يُستقبل أبداً {فاتقوا} : فاحذروا أن تصلوا {النَّار التي وقودها} ما يُوقد به {الناسُ والحجارة} يعني حجارة الكبريت وهي أشدُّ لاتِّقادها {أعدَّت} (خُلقت وهُيِّئت) جزاءً {للكافرين} بتكذيبهم ثمَّ ذكر جزاء المؤمنين فقال:',NULL,'96',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (33,'{وبشر الذين آمنوا} أي: أخبرهم خبراً يظهر به أثر السُّرور على بشرتهم {وعملوا الصالحات} أَي: الأعمال الصَّالحات يعني الطَّاعات فيما بينهم وبين ربِّهم {أنَّ لهم} : بأنَّ لهم {جناتٍ} : حدائق ذات الشِّجر {تجري من تحتها} من تحت أشجارها ومساكنها {الأنهار} {كلما رزقوا} : أُطعموا من تلك الجنَّات ثمرةً {قَالُوا هَذَا الَّذِي رُزِقْنَا مِنْ قَبْلُ} لتشابه ما يُؤتون به وأرادوا: هذا من نوع ما رُزقنا من قبل {وأتوا به متشابهاً} في اللَّون والصُّورة مختلفاً في الطَّعم وذلك أبلغ في باب الإِعجاب {ولهم فيها أزواجٌ} : من الحور العين والآدميات {مطهرةٌ} عن كلِّ أذىً وقذرٍ ممَّا في نساء الدُّنيا ومن مساوئ الأخلاق وآفات الشَّيب والهرم {وهم فِيها خالدون} لأنَّ تمام النِّعمة بالخلود',NULL,'96',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (34,'{إنَّ الله لا يَسْتَحْيِ} الآية لمَّا ضرب الله سبحانه المَثل للمشركين بالذُّباب والعنكبوت في كتابه ضحكت اليهود وقالوا: ما يشبه هذا كلام الله سبحانه فأنزل الله تعالى: {إنَّ الله لا يَسْتَحْيِ} لا يترك ولا يخشى {أن يضرب مثلاً} أَنْ يُبيِّنَ شبهاً {ما بعوضةً} ما زائدة مؤكِّدة والبعوض: صغار البق الواحدة: بعوضة {فما فوقها} يعني: فما هو أكبر منها والمعنى: إنَّ الله تعالى لا يترك ضرب المثل ببعوضةٍ فما فوقها إذا علم أنَّ فِيهِ عبرةُ لمن اعتبر وحجَّةً على مَنْ جحد (واستكبر) {فأمَّا الذين آمنوا فيعلمون} أنَّ المثل وقع في حقِّه {وَأَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مَاذَا أَرَادَ اللَّهُ بهذا مثلاً} أَيْ: أَيُّ شيء أرادالله بهذا من الأمثال؟ والمعنى ئئئئانهم يقولون: أَيُّ فائدةٍ في ضرب الله المثل بهذا؟ فأجابهم الله سبحانه فقال {يضلُّ به كثيراًً} أَيْ: أراد الله بهذا المثل أن يضلَّ به كثيراً من الكافرين وذلك أنَّهم يُنكرونه ويُكذِّبونه {ويهدي به كثيراً} من المؤمنين لأنَّهم يعرفونه ويصدِّقونه {وما يضلُّ به إلاَّ الفاسقين} الكافرين الخارجين عن طاعته',NULL,'96',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (35,'{الذين ينقضون} يهدمون ويفسدون {عهدَ الله} : وصيته وأمره في الكتب المتقدِّمة بالإِيمان بمحمد صلى الله عليه وسلم {من بعد ميثاقه} من بعد توكيده عليهم بإيجابه ذلك {وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ} يعني: الرَّحم وذلك أنَّ قريشاً قطعوا رحم النَّبيِّ صلي الله عليه وسلم بالمعاداة معه {ويفسدون فِي الأرض} بالمعاصي وتعويق النَّاس عن الإيمان بمحمد صلى الله عليه وسلم {أولئك هم الخاسرون} (مغبونون) بفوت المثوبة والمصيرِ إلى العقوبة',NULL,'97',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (36,'{كيف تكفرون بالله} معنى كيف ها هنا استفهامٌ في معنى التَّعجُّب للخلقِ أَي: اعجبوا من هؤلاء كيف يكفرون بالله وحالُهم أنَّهم كانوا تراباً فأحياهم بأَنْ خلق فيهم الحياة فالخطاب للكفَّار والتَّعجب للمؤمنين وقوله تعالى: {ثم يميتكم} أَيْ: في الدُّنيا {ثمَّ يُحييكم} في الآخرة للبعث {ثمَّ إليه ترجعون} تردُّون فيفعل بكم ما يشاء فاستعظم المشركون أمر البعث والإعادة فاحتجَّ الله سبحانه عليهم بخلق السماوات والأرض فقال:',NULL,'98',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (37,'{هو الذي خلق لكم} لأجلكم {ما في الأرض جميعاً} بعضها للانتفاع وبعضها للاعتبار {ثمَّ استوى إلى السَّماء} : أقبل على خلقها وقصد إليها {فسوَّاهنَّ سبع سماوات} فجعلهن سبع سماوات مُستوياتٍ لا شقوق فيها ولا فطور ولا تفاوت {وهو بكلِّ شيءٍ عليم} إذ بالعلم يصحُّ الفعل المحكم',NULL,'98',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (38,'{وإذ قال ربك} واذكر لهم يا محمَّدُ إذ قَالَ رَبُّكَ {لِلْمَلائِكَةِ إِنِّي جَاعِلٌ فِي الأَرْضِ خليفة} يعني: آدم جعله خليفةً عن الملائكة الذين كانوا سكَّان الأرض بعد الجنِّ والمراد بذكر هذه القصَّة ذكرُ بدءِ خلق النَّاس {قالوا أتجعل فِيها مَنْ يفسد فِيها} كما فعل بنو الجانِّ قاسوا (الشَّاهد) على الغائب {ونحن نسبح بحمدك} نُبرِّئُك من كلِّ سوءٍ ونقول: سبحان الله وبحمده {ونقدِّسُ لك} ونُنزِّهك عمَّا لا يليق بك {قال إني أعلم ما لا تعلمون} من إضمار إبليس العزم على المعصية فلمَّا قال الله تعالى هذا للملائكة قالوا فيما بينهم: لن يخلق ربُّنا خلقاً هو أعلمُ منَّا ففضَّل الله تعالى عليهم آدم بالعلم وعلَّمه اسم كلِّ شيء حتى القصعة (والقصيعة) والمِغْرفة وذلك قوله تعالى:',NULL,'98',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (39,'{وعلَّم آدم الأسماءَ كلَّها} أَيْ: خلق فِي قلبه علماً بالأسماء على سبيل الابتداء {ثمَّ عرَضهم} أَيْ: عرض المسمَّيات بالأسماء من الحيوان والجماد وغير ذلك {على الملائكة فقال أنبئوني} أخبروني {بأسماء هؤلاء} وهذا أمرُ تعجيزٍ أراد الله تعالى أن يُبيِّن عجزهم عن علم مايرون ويُعاينون {إن كنتم صادقين} أنِّي لا أخلق خلقاً أعلمَ منكم فقالت الملائكة إقراراً بالعجز واعتذاراً:',NULL,'99',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (40,'{سبحانك} تنزيهاً لك عن الاعتراض عليك فِي حكمك {لا علم لنا إلاَّ ما علمتنا} اعترفوا العجز عن علم ما لم يُعلَّموه {إنَّك أنت العليم} العالم {الحكيم} الحاكم تحكم بالحق وتقضي به فلمَّا ظهر عجز الملائكة قال الله تعالى لآدم:',NULL,'99',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (41,'{يا آدم أنبئهم بأسمائهم} أخبرهم بتسمياتهم فسمَّى كلَّ شيءٍ باسمه وألحق كلَّ شيءٍ بجنسه {فلما أنبأهم بأسمائهم} : أخبرهم بمسمَّياتهم {قال} الله تعالى للملائكة: {ألم أقل لكم} وهذا استفهامٌ يتضمَّن التَّوبيخ لهم على قولهم: {أتجعل فيها مَنْ يفسد فيها} {إني أعلم غيب السماوات والأرض} أَيْ: ما غاب فيهما عنكم {وأعلم ما تبدون} : علانيتكم {وما كنتم تكتمون} : سرَّكم لا يخفى عليَّ شيءٌ من أموركم',NULL,'99',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (42,'{وإذ قلنا للملائكة اسجدوا لآدم} سجود تعظيمٍ وتسليمٍ وتحيَّةٍ وكان ذلك انحناءاً يدلُّ على التَّواضع ولم يكن وضعَ الوجه على الأرض {فسجدوا إلاَّ إبليس أبى} امتنع {واستكبر وكان من الكافرين} في سابق علم الله عز وجل',NULL,'100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (43,'{وَقُلْنَا يَا آدَمُ اسْكُنْ أَنْتَ وَزَوْجُكَ الْجَنَّةَ} اتَّخذاها مأوىً ومنزلاً {وكلا منها رغداً} واسعاً {حيث شئتما} ما شئتما إذا شئتما (كيف شئتما) {ولا تقربا هذه الشجرة} لاتحوما حولها بالأكل منها يعني السُّنبلة {فتكونا} فتصيرا {من الظالمين} : العاصين الذين وضعوا أمر الله عز وجل غير موضعه',NULL,'100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (44,'{فأزلهما الشيطان} نحاهما وعدهما {عنها فأخرجهما ممَّا كانا فيه} من الرتبة وليس العيش {وقلنا} لآدم وحواء وإبليس والحيَّة: {اهبطوا} أي: انزلوا إلى الأرض {بعضكم لبعض عدو} يعني: العداوة التي بين آدم وحواء والحيَّة وبين ذرية آدم عليه السَّلام من المؤمنين وبين إبليس لعنه الله {ولكم في الأرض مستقر} موضع قرارٍ {ومتاع إلى حين} ما تتمتَّعون به ممَّا تُنبته الأرض إلى حين االموت',NULL,'100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (45,'{فتلقى آدم من ربه} أخذ وتلقَّن {كلماتٍ} وهو أنَّ الله تعالى ألهم آدم عليه السَّلام حين اعترف بذنبه وقال: {ربنا ظلمنا أنفسنا} الآية {فتاب عليه} فعاد عليه بالمغفرة حين اعترف بالذَّنب واعتذر {إنَّه هو التواب} يتوب على عبده بفضله إذا تاب إليه من ذنبه',NULL,'100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (46,'{قُلْنَا اهْبِطُوا مِنْهَا جَمِيعًا} كرَّر الأمر بالهبوط للتَّأكيد {فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدًى} أَيْ: فإنْ يأتكم مني شريعةٌ ورسولٌ وبيانٌ ودعوةٌ {فَمَنْ تبع هداي} أَيْ: قَبِل أمري واتَّبع ما آمره به {فَلا خَوْفٌ عَلَيْهِمْ} فِي الآخرة وَلا حزن والخطاب لآدم وحوَّاء وذرِّيتهما أعلمهم الله تعالى أنَّه يبتليهم بالطَّاعة ويجازيهم بالجنَّة عليها ويعاقبهم بالنَّار على تركها وهو قوله تعالى:',NULL,'101',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (47,'{والذين كفروا وكذبوا بآياتنا} أَيْ: بأدلتنا وكتبنا {أولئك أصحاب النار هم فيها خالدون}',NULL,'101',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (48,'{يا بني إسرائيل} أولاد يعقوب عليه السَّلام {اذكروا} اشكروا وذكر النِّعمة هو شكرها {نعمتي} يعني: نعمي {التي أنعمت عليكم} يعني: فلق البحر والإِنجاء من فرعون وتظليل الغمام إلى سائر ما أنعم الله تعالى به عليهم والمراد بقوله تعالى: {عليكم} أَيْ: على آبائكم والنِّعمة على آبائهم نعمةٌ عليهم وشكر هذه النِّعم طاعتُه في الإيمان بمحمد صلى الله عليه وسلم ثمَّ صرَّح بذلك فقال {وأوفوا بعهدي} أَيْ: في محمد صلى الله عليه وسلم {أُوف بعهدكم} أدخلكم الجنَّة {وإيَّاي فارهبون} فخافوني في نقض العهد',NULL,'101',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (49,'{وآمنوا بما أنزلت} يعني: القرآن {مصدقاً لما معكم} موافقاً للتَّوراة فِي التَّوحيد والنُّبوَّة {وَلا تكونوا أوَّل كافر به} أَيْ: أوَّل مَنْ يكفر به من أهل الكتاب لأنَّكم إذا كفرتم كفر أتباعكم فتكونوا أئمةً في الضَّلالة والخطابُ لعلماء اليهود {ولا تشتروا} ولا تستبدلوا {بآياتي} ببيان صفة محمد صلى الله عليه وسلم ونعته {ثمناً قليلاً} عوضاً يسيراً من الدُّنيا يعني: ما كانوا يصيبونه من سفلتهم فخافوا إنْ هم بينوا صفة محمد صَلَّى الله عليه وسلم أَنْ تفوتهم تلك المآكل والرِّياسة {وإيايَّ فاتقون} فاخشوني في أمر محمد صلى الله عليه وسلم لا ما يفوتكم من الرِّياسة',NULL,'102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (50,'{ولا تلبسوا الحق بالباطل} أَيْ: لا تخلطوا الحقَّ الذي أنزلتُ عليكم من صفة محمد عليه السلام بالباطل الذي تكتبونه بأيديكم من تغيير صفته وتبديل نعته {وتكتموا الحق} أَيْ: ولا تكتموا الحقَّ فهو جزمٌ عُطِفَ على النَّهي {وأنتم تعلمون} أنَّه نبيٌّ مرسلٌ قد أُنزل عليكم في كتابكم فجحدتم نبوَّته مع العلم به',NULL,'102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (51,'{وأقيموا الصلاة} المفروضة {وآتوا الزكاة} الواجبة في المال {واركعوا مع الراكعين} وصلُّوا مع المصلين محمد صَلَّى اللَّهُ عَلَيْهِ وسلم وأصحابه في جماعةٍ',NULL,'102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (52,'{أتأمرون الناس بالبرِّ} كانت اليهود تقول لأقربائهم من المسلمين: اثبتوا على ما أنتم عليه ولا يؤمنون به فأنزل الله تعالى توبيخاً لهم: {أتأمرون الناس بالبر} بالإيمان بمحمد صلى الله عليه وسلم {وتنسون} وتتركون {أنفسكم} فلا تأمرونها بذلك {وأنتم تتلون الكتاب} تقرؤون التَّوراة وفيها صفة محمد صَلَّى اللَّهُ عَلَيْهِ وسلم ونعته {أفلا تعقلون} أنَّه حقٌّ فتتَّبعونه؟ ! ثمَّ أمرهم الله تعالى بالصَّوم والصَّلاة لأنَّهم إنَّما كان يمنعهم عن الإسلام الشَّره وخوف ذَهاب مأكلتهم وحب الرِّياسة فأُمروا بالصَّوم الذي يُذهب الشَّرَه وبالصًَّلاة التي تُورث الخشوع وتَنفي الكبر وأُريدَ بالصَّلاةِ الصَّلاةُ التي معها الإيمان بمحمد صلى الله عليه وسلم فقال',NULL,'102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (53,'{واستعينوا بالصبر} يعني بالصَّوم {والصلاة} لأنَّها تنهى عن الفحشاء والمنكر {وإنها لكبيرةٌ} لثقيلةٌ (يعني: وإنَّ الإستعانةَ بالصبر والصلاة لثقيلةٌ) {إلاَّ على الخاشعين} السَّاكنين إلى الطَّاعة وقال بعضهم: رجع بهذا القول إلى خطاب المسلمين فأمرهم أَنْ يستعينوا على ما يطلبونه من رضاءِ الله تعالى ونيل جنَّتِهِ بالصَّبر على أداء فرائضه (وهو الصَّوم) والصَّلاة',NULL,'103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (54,'{الذين يظنون} يستيقنون {أنهم ملاقوا ربِّهم} أنَّهم مبعوثون وأنَّهم محاسبون وأنَّهم راجعون إلى الله تعالى أَيْ: يُصدِّقون بالبعث والحساب',NULL,'103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (55,'{يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عليكم} مضى تفسيره {وأني فضلتكم} أعطيتكم الزِّيادة {على العالمين} : على عالمي زمانكم وهو ما ذكره فِي قوله تعالى: {إِذْ جَعَلَ فِيكُمْ أَنْبِيَاءَ} والمراد بهذا التَّفضيل سلفهم ولكن تفضيل الآباء شرف الأبناء',NULL,'103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (56,'{واتقوا يوماً} واحذروا واجتنبوا عقاب يومٍ {لا تجزي} لا تقضي ولا تُغني {نَفْسٌ عَنْ نَفْسٍ شَيْئًا وَلا يُقْبَلُ مِنْهَا شفاعة} أَيْ: لا يكون شفاعةٌ فيكون لها قبول وذلك أنَّ اليهود كانوا يقولون: يشفع لنا آباؤنا الأنبياء فآيسهم الله تعالى عن ذلك {ولا يؤخذ منها عدل} فِداءٌ {ولا هم ينصرون} يُمنعون من عذاب الله تعالى',NULL,'104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (57,'{وإذ نجيناكم} واذكروا ذلك {من آل فرعون} أتباعه ومَنْ كان على دينه {يسومونكم} : يُكلِّفونكم {سوء العذاب} شديد العذاب وهو قوله تعالى: {يذبحون} : ويقتلون {أبناءكم ويستحيون نساءكم} يستبقوهن أحياءً (لقول بعض الكهنة له: إنَّ مولوداً يُولد في بني إسرائيل يكون سببا له ذهاب ملكه) {وفي ذلكم} الذي كانوا يفعلونه بكم {بلاءٌ} : ابتلاءٌ واختبارٌ وامتحانٌ {من ربكم عظيم} وقيل: وفي تنجيتكم من هذه المحن نعمةٌ عظيمة والبلاء: النِّعمة والبلاء: الشِّدَّة',NULL,'104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (58,'{وإذ فرقنا بكم البحر} فجعلناه اثنى عسر طريقاً حتى خاض فيه بنو إسرائيل {فأنجيناكم وأغرقنا آل فرعون وأنتم تنظرون} إلى انطباق البحر عليهم وإنجائكم منهم',NULL,'104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (59,'{وإذ واعدنا موسى أربعين ليلة} أَي: انقضاءَها وتمامَها للتَّكلُّم معه {ثمَّ اتخذتم العجل} معبودا وإلاها {من بعده} من بعد خروجه عنكم للميقات {وأنتم ظالمون} واضعون العبادةَ في غير موضعها وهذا تنبيه على أنَّ كفرهم بمحمَّدٍ صَلَّى اللَّهُ عَلَيْهِ وسلم ليس بأعجب من كفرهم وعبادتهم العجل في زمن موسى عليه السَّلام',NULL,'105',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (60,'{ثمَّ عفونا} محونا ذنوبكم {عنكم من بعد ذلك} من بعد عبادة العجل {لعلكم تشكرون} لكي تشكروا نعمتي بالعفو',NULL,'105',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (61,'{وإذ آتينا موسى الكتاب والفرقان} (عطف تفسيري) يعني: التَّوراة الفارق بين (الحق والباطل) والحلال والحرام {لعلكم تهتدون} لكي تهتدوا بذلك الكتاب (من الضلال)',NULL,'105',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (62,'{وإذ قال موسى لقومه} الذين عبدوا العجل {يَا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ} إلاها {فتوبوا إلى بارئكم} يعني: خالقكم قالوا: كيف نتوب؟ قال {فاقتلوا أنفسكم} أَيْ: ليقتلِ البريءُ منكم المجرمَ {ذلكم} أَي: التَّوبة {خيرٌ لكم عند بارئكم} من إقامتكم على عبادة العجل ثم فعلتم ما أُمرتم به {فتاب عليكم} قبل توبتكم {إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ}',NULL,'105',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (63,'{وَإِذْ قُلْتُمْ يَا مُوسَى لَنْ نُؤْمِنَ لَكَ} يعني: الذين اختارهم موسى عليه السَّلام ليعتذروا إلى الله سبحانه من عبادة العجل فلمَّا سمعوا كلام الله تعالى وفرغ موسى من مناجاة الله عز وجل قالوا له: {لن نؤمن لك} لن نصدِّقك {حتى نرى الله جهرةً} أَيْ: عِياناً لا يستره عنا شيءٌ {فأخذتكم الصاعقة} وهي نارٌ جاءت من السَّماء فأحرقتهم جميعاً {وأنتم تنظرون} إليها حين نزلت وإنَّما أخذتهم الصَّاعقة لأنَّهم امتنعوا من الإِيمان بموسى عليه السَّلام بعد ظهور معجزته حتى يُريهم ربَّهم جهرةً والإيمانُ بالأنبياء واجبٌ بعد ظهور معجزتهم ولا يجوز اقتراح المعجزات عليه فلهذا عاقبهم الله تعالى وهذه الآية توبيخٌ لهم على مخالفة الرسول صَلَّى اللَّهُ عَلَيْهِ وسلم مع قيام معجزته كما خالف أسلافهم موسى مع ما أتى به من الآيات الباهرة',NULL,'106',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (64,'{ثم بعثناكم} نشرناكم وأَعدْناكم أَحياءً {من بعد موتكم لعلكم تشكرون} نعمة البعث',NULL,'106',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (65,'{وظللنا عليكم الغمام} سترناكم عن الشَّمس في التِّيه بالسَّحاب الرَّقيق {وأنزلنا عليكم المنَّ} الطُّرَنْجبين كان يقع على أشجارهم بالأسحار {والسَّلوى} وهي طير أمثال السُّمانى وقلنا لهم: {كلوا من طيبات} من حلالات {ما رزقناكم وما ظلمونا} بإبائهم على موسى عليه السَّلام دخول قرية الجبَّارين ولكنَّهم ظلموا أنفسهم حين تركوا أمرنا فحبسناهم في التِّيه فلمَّا انقضت مدَّة حبسهم وخرجوا من التِّيه قال الله تعالى لهم:',NULL,'107',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (66,'{ادخلوا هذه القرية} وهي أريحا {وادخلوا الباب} يعني: باباً من أبوابها {سجداً} منحنين متواضعين {وقولوا حطة} وذلك أنَّهم أصابوا خطيئةً بإبائهم على موسى عليه السَّلام دخول القرية فأراد الله تعالى أَنْ يغفرها لهم فقال لهم: قولوا حطَّةٌ أَيْ: مسألتنا حطَّةٌ وهو أن تحط عنا ذنوبنا {وسنزيد المحسنين} الذين لم يكونوا من أهل تلك الخطيئة إحساناً وثواباً',NULL,'107',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (67,'{فبدَّل الذين ظلموا قولاً} منهم {غير الذي قيل لهم} أَيْ: غيَّروا تلك الكلمة التي أُمروا بها وقالوا: حنطةٌ {فأنزلنا على الذين ظلموا رجزاً} : ظلمةً وطاعوناً فهلك منهم فِي ساعة واحدة سبعون ألفاً جزاءً لفسقهم بتبديل ما أُمروا به من الكلمة',NULL,'108',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (68,'{وإذ استسقى موسى لقومه} في التِّيه {فقلنا اضرب بعصاك الحجر} وكان حجراً خفيفاً مربَّعاً مثل رأس الرَّجل {فانفجرت} أيْ: فضربَ فانفجرت يعني: فانشقَّت {منه اثنتا عشرة عيناً} فكان يأتي كلُّ سبط عينَهم التي كانوا يشربون منها فذلك قوله تعالى: {قَدْ عَلِمَ كُلُّ أُنَاسٍ مشربهم} وقلنا لهم: {كلوا} من المنِّ والسَّلوى {واشربوا} من الماء فهذا كلُّه {من رزق الله} {ولا تعثوا في الأرض مفسدين} أَيْ: لا تسعوا فيها بالفساد فَمَلُّوا ذلك العيش وذكروا عيشاً كان لهم بمصر فقالوا:',NULL,'108',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (69,'{يَا مُوسَى لَنْ نَصْبِرَ عَلَى طَعَامٍ وَاحِدٍ} يعني: المنَّ الذي كانوا يأكلونه والسَّلوى فكانا طعاماً واحداً {فادع لنا ربك} سله وقل له: أَخرِجْ {يُخْرِجْ لَنَا مِمَّا تُنْبِتُ الأَرْضُ مِنْ بَقْلِهَا} وهو كلُّ نباتٍ لا يبقى له ساقٌ {وقثائها} وهو نوعٌ من الخضراوات {وفومها} وهو الحنطة فقال لهم موسى عليه السَّلام: {أتستبدلون الذي هو أدنى} أَيْ: أخسُّ وأوضع {بالذي هو خيرٌ} أَي: أرفع وأجلُّ؟ فدعا موسى عليه السَّلام فاستجبنا له وقلنا لهم: {اهبطوا مصراً} : أنزلوا بلدةً من البلدان {فإنَّ لكم ما سألتم} أَيْ: فإنَّ الذي سألتم لا يكون إلاَّ فِي القرى والأمصار {وضُربت عليهم} أَيْ: على اليهود الذين كانوا في عصر النبي صلى الله عليه وسلم {الذلَّة} يعني: الجزيةَ وزيَّ اليهوديَّة ومعنى ضرب الذِّلة: إلزامهم إيَّاها إلزاماً لا يبرح {والمسكنة} زي الفقر وأثر البؤس {وباءوا} احتملوا وانصرفوا {بغضب من الله ذلك} أَيْ: ذلك الضَّرب والغضب {بأنّهم كانوا يكفرون بآيات الله} التي أنزلت على محمد صَلَّى اللَّهُ عَلَيْهِ وسلم {ويقتلون النَّبيين} أَيْ: يتولَّون أولئك الذين فعلوا ذلك {بغير حق} أَيْ: قتلاً بغير حقٍّ يعني: بالظُّلم {ذلك} الكفر والقتل بشؤم ركوبهم المعاصي وتجاوزهم أمر الله تعالى',NULL,'109',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (70,'{إن الذين آمنوا} أي: بالأنبياء الماضين ولم يؤمنوا بك {والذين هادوا} دخلوا في دين اليهوديَّة {والنصارى والصابئين} الخارجين من دين إلى دين وهم قومٌ يعبدون النُّجوم {مَنْ آمن} من هؤلاء {بالله واليوم الآخر وعمل صالحاً} بالإيمان بمحمَّدٍ عليه السَّلام لأنَّ الدليل قد قام أنَّ مَنْ لم يؤمن به لا يكون عمله صَالِحًا {فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلا خَوْفٌ عليهم ولا هم يحزنون}',NULL,'110',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (71,'{وإذ أخذنا ميثاقكم} بالطَّاعة لله تعالى والإيمان بمحمَّدٍ عليه السَّلام في حال رفع الطُّور فوقكم يعني: الجبل وذلك لأنَّهم أبوا قبول شريعة التَّوراة فأمر الله سبحانه جبلاً فانقلع من اصله حتى قام على رؤوسهم فقبلوا خوفاً من أن يُرضخوا على رؤوسهم بالجبل وقلنا لكم: {خذوا ما آتيناكم} اعملوا بما أُمرتم به {بقوَّةٍ} بجدٍّ ومواظبةٍ على طاعة الله عز وجل {واذكروا ما فِيهِ} من الثَّواب والعقاب {لعلكم تتقون}',NULL,'110',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (72,'{ثم توليتم من بعد ذلك} أعرضتم عن أمر الله تعالى وطاعته من بعد أخذ الميثاق {فَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ} بتأخير العذاب عنكم {لَكُنْتُمْ مِنَ الخاسرين} الهالكين في العذاب',NULL,'110',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (73,'{ولقد علمتم} عرفتم حال {الذين اعتدوا} جاوزوا ما حُدَّ لهم من ترك الصَّيد في السَّبت {فقلنا لهم كونوا} بتكويننا إيَّاكم {قردةً خاسئين} مطرودين مبعدين',NULL,'111',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (74,'{فجعلناها} أَيْ: تلك العقوبة والمسخة {نكالاً} عبرةً {لما بين يديها} للأمم التي ترى الفرقة الممسوخة {وما خلفها} من الأمم التي تأتي بعدها {وموعظة} عبرةً {للمتقين} للمؤمنين الذين يتقون من هذه الأمَّة',NULL,'111',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (75,'{وَإِذْ قَالَ مُوسَى لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أن تذبحوا بقرة} وذلك أنَّه وُجد قتيلٌ في بني إسرائيل ولم يدروا قاتله فسألوا موسى عليه السَّلام أن يدعو الله تعالى ليبيِّن لهم ذلك فسأل موسى ربَّه فأمرهم بذبح بقرةٍ فقال لهم موسى عليه السَّلام: إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تَذْبَحُوا بَقَرَةً {قَالُوا أتتخذنا هزوا} أتستهزئ بنا حين نسألك عن القتيل فتأمرنا بذبح البقرة؟ ! {قال أعوذ بالله} أمتنع به أن أكون من المستهزئين بالمؤمنين فلمَّا علموا أنَّ ذلك عزمٌ من الله عز وجل سألوه الوصف فقالوا:',NULL,'111',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (76,'{ادع لنا ربك} أَيْ: سله بدعائك إيَّأه {يبين لنا ما هي} ما تلك البقرة وكيف هي وكم سنُّها؟ وهذا تشديدٌ منهم على أنفسهم {قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لا فَارِضٌ} مُسِنَّةٌ كبيرةٌ {ولا بكرٌ} فتيةٌ صغيرةٌ {عوانٌ} نَصَفٌ بين السِّنَّينِ {فافعلوا ما تؤمرون} (فيه تنبيهٌ على منعهم) وقوله تعالى:',NULL,'111',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (77,'{فاقعٌ لونها} أَيْ: شديد الصُّفرة {تسرُّ الناظرين} تعجبهم بحسنها',NULL,'112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (78,'{قالوا ادع لنا ربك يبين لنا ما هي} أَسائمةٌ أم عاملةٌ؟ {إنَّ البقر} جنس البقر {تشابه} اشتبه وأشكل {علينا وإنَّا إنْ شاء الله لمهتدون} إلى وصفها [قال رسول الله صلى الله عليه وسلم: وايمُ اللَّهِ لو لم يستثنوا لما بُيِّنت لهم آخر الأبد]',NULL,'112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (79,'{قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لا ذَلُولٌ} مُذلَّلةٌ بالعمل {تثير الأرض} تُقلبها للزراعة أَيْ: ليستْ تقلِّب لأنَّها ليست ذلولاً {ولا تسقي الحرث} الأرض المهيَّأة للزِّراعة {مسلَّمة} من العيوب وآثار العمل {لا شية فيها} لا لون فيها يُفارق سائر لونها {قالوا الآن جئت بالحقّ} بالوصف التّام الذي تتميَّز به من أجناسها فطلبوها فوجدوها {فذبحوها وما كادوا يفعلون} لغلاء ثمنها',NULL,'112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (80,'{وإذ قتلتم نفساً} هذا أوَّل القصَّة ولكنَّه مؤخرَّ في الكلام {فادَّارأتم} فاختلفتم وتدافعتم {والله مخرجٌ} مُظهرٌ {ما كنتم تكتمون} من أمر القتيل',NULL,'112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (81,'{فقلنا اضربوه ببعضها} بلسانها فيحيى فضرب فيحيى {كَذَلِكَ يُحْيِي اللَّهُ الْمَوْتَى} أَيْ: كما أحيا هذا القتيل {ويريكم آياته} آيات قدرته في خلق الحياة في الأموات (كما خلق في عاميل)',NULL,'112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (82,'{ثم قست قلوبكم} يا معشر اليهود أَي: اشتدَّت وصلبت {من بعد ذلك} من بعد هذه الآيات التي تقدَّمت من المسخ ورفع الجبل فوقهم وانبجاس الماء من الحجرِ وإحياء الميت بضرب عضوٍ وهذه الآيات ممَّا يصدِّقون بها {فهي كالحجارة} في القسوة وعدم المنفعة بل {أشد قسوة} وإنَّما عنى بهذه القسوة تركهم الإِيمان بمحمَّدٍ صلى الله عليه وسلم بعد ما عرفوا صدقه وقدرةَ الله تعالى على عقابهم بتكذيبهم إيَّاه ثمَّ عذر الحجارة وفضَّلها على قلوبهم فقال: {وَإِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الأَنْهَارُ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الماء وإنَّ منها لما يهبط} ينزل من علوٍ إلى سفل {مِنْ خَشْيَةِ اللَّهِ} قال مجاهدٌ: كلُّ حجرٍ تفجَّر منه الماء أو تشقَّق عن ماء أو تردَّى من رأس جبلٍ فهو من خشية الله تعالى نزل به القرآن ثمَّ أوعدهم فقال: {وما الله بغافلٍ عمَّا تعملون} ثمَّ خاطب النبي صلى الله عليه وسلم والمؤمنين فقطع طمعهم عن إيمانهم فقال:',NULL,'113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (83,'{أفتطمعون أن يؤمنوا لكم} وحالهم أنَّ طائفةً منهم كانوا {يسمعون كلام الله} يعني التَّوراة {ثم يحرفونه} يُغيِّرونه عن وجهه يعني: الذين غيَّروا أحكام التَّوراة وغيَّروا آية الرَّجم وصفة محمد صلى الله عليه وسلم {من بعد ما عقلوه} أَيْ: لم يفعلوا ذلك عن نسيانٍ وخطأٍ بل فعلوه عن تعمُّدٍ {وهم يعلمون} أنَّ ذلك مَكْسَبةٌ للأوزار',NULL,'113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (84,'{وإذا لقوا الذين آمنوا} يعني: منافقي اليهود {قالوا آمنا} بمحمَّدٍ وهو نبيٌّ صادقٌ نجده في كتبنا {وإذا خلا بعضهم إلى بعض} يعني: إذا رجع هؤلاء المنافقون إلى رؤساهم لاموهم فقالوا: {أتحدثونهم} أتخبرون أصحاب محمد - صلى الله عليه وسلم - {بما فتح الله عليكم} من صفة النَّبيِّ المُبشَّر به {ليحاجُّوكم} ليجادلوكم ويخاصموكم {به} بما قلتم لهم {عند ربكم} في الآخرة يقولون: كفرتم به بعد أن وقفتم على صدقة {أفلا تعقلون} أفليس لكم ذهن الإنسانيَّة؟ فقال الله تعالى:',NULL,'113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (85,'{أو لا يعلمون أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ} من التَّكذيب يعني: هؤلاء المنافقين {وما يعلنون} من التَّصديق',NULL,'114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (86,'{ومنهم} ومن اليهود {أميُّون} لا يكتبون ولا يقرؤون {لا يعلمون الكتاب إلاَّ أمانيّ} إلاَّ أكاذيب وأحاديثَ مُفتعلةً يسمعونها من كبرائهم {وإن هم إلاَّ يظنون} أَيْ: إلاَّ ظانِّين ظنَّاً وتوهُّماً فيجحدون نُبُوَّتَكَ بالظَّنِّ',NULL,'114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (87,'{فويلٌ} فشدَّةُ عذابٍ {للذين يكتبون الكتاب بأيديهم} أَيْ: من قِبَلِ أنفسهم من غير أن يكون قد أُنزل {ثم يقولون هذا من عند الله} يعني اليهود عمدوا إلى صفة محمد صلى الله عليه وسلم وكتبوا صفته على غير ما كانت فِي التَّوراة وأخذوا عليه الأموال فذلك قوله تعالى: {وويلٌ لهم ممَّا يكسبون} (من حُطَام الدُّنيا) فلمَّا أوعدهم رسول الله صلى الله عليه وسلم بالنَّار عند تكذيبهم إيَّاه قالوا:',NULL,'114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (88,'{لن تمسنا النار إلا أياماً معدودةً} قليلةً ويعنون الأيَّام التي عبد آباؤهم فيها العجل فكذَّبهم الله سبحانه فقال: قل لهم يا محمَّدُ: {أَتَّخَذْتُمْ عِنْدَ اللَّهِ عهداً} أخذتم بما تقولون من الله ميثاقاً؟ {فلن يخلف الله عهده} والله لا ينقض ميثاقه {أَمْ تَقُولُونَ عَلَى اللَّهِ} الباطلَ جهلاً منكم ثمَّ ردَّ على اليهود قولهم: لن تمسَّنا النَّار فقال {بلى} أُعذِّب',NULL,'115',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (89,'{مَنْ كسب سيئة} وهي الشِّرك {وأحاطت به خطيئته} : سدَّت عليه مسالك النَّجاة وهو أّنْ يموت على الشِّرك {فأولئك أصحاب النار هم فيها خالدون} الذين يُخلَّدون في النَّار ثمَّ أخبر عن أخذ الميثاق عليهم بتبيين نعت محمَّدٍ صلى الله عليه وسلم فقال:',NULL,'115',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (90,'قوله تعالى {وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَئِكَ أَصْحَابُ الْجَنَّةِ هُمْ فِيهَا خَالِدُونَ}',NULL,'115',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (91,'{وإذ أخذنا ميثاق بني إسرائيل} أَيْ: في التَّوراة {لا تعبدون} أَيْ: بأن لا تعبدوا {إلاَّ الله وبالوالدين إحساناً} أَيْ: ووصَّيناهم بالوالدين إحساناً {وذي القربى} أَي: القرابة في الرَّحم {واليتامى} يعني: الذين مات أبوهم قبل البلوغ {وقولوا للناس حسناً} أَيْ: صدقاً وحقَّاً في شأن محمَّدٍ عليه السَّلام وهو خطابٌ لليهود {ثم توليتم} أعرضتم عن العهد والميثاق يعني: أوائلهم {إلاَّ قليلاً منكم} يعني: مَنْ كان ثابتاً على دينه ثمَّ آمن بمحمد صَلَّى اللَّهُ عليه وسلم {وأنتم معرضون} عمَّا عُهد إليكم كأوائلكم',NULL,'115',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (92,'{وإذ أخذنا ميثاقكم لا تسفكون دماءكم} بأن لا يقتل بعضكم بعضاً ولا يُخرج بعضكم بعضاً من داره ولا يغلبه عليها {ثم أقررتم} أَيْ: قبلتم ذلك {وأنتم} اليوم {تشهدون} على إقرار أوائلكم ثمَّ أخبر أنَّهم نقضوا هذا الميثاق فقال:',NULL,'116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (93,'{ثم أنتم هؤلاء} أراد: يا هؤلاء {تقتلون أنفسكم} يقتل بعضكم بعضاً {وَتُخْرِجُونَ فَرِيقًا منكم من ديارهم تظاهرون عليهم} تتعاونون على أهل ملَّتكم {بالإثم والعدوان} : بالمعصية والظُّلم {وإن يأتوكم أسارى} مأسورين يطلبون الفداء فديتموهم {وهو محرَّم عليكم إخراجهم} أَيْ: وإخراجهم عن ديارهم محَّرمٌ عليكم {أفتؤمنون ببعض الكتاب} يعني: فداء الأسير {وتكفرون ببعض} يعني: القتل والإخراج والمظاهرة على وجه الإباحة؟ قال السُّدِّيُّ: أحذ الله تعالى عليهم أربعة عهودٍ: تركَ القتل وترك الإِخراج وترك المظاهرة وفداء أُسرائهم فأعرضوا عن كلِّ ما أُمروا به إلاَّ الفداء {فَمَا جَزَاءُ مَنْ يَفْعَلُ ذَلِكَ مِنْكُمْ إِلا خزيٌ} فضيحةٌ وهوانٌ {في الحياة الدنيا} وقوله:',NULL,'116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (94,'{فلا يخفف عنهم العذاب} معناه: في الدُّنيا والآخرة وقيل: هذه الحالة مختصَّةٌ بالآخرة',NULL,'116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (95,'{وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِنْ بَعْدِهِ بالرسل} أَيْ: وأرسلنا رسولاً بعد رسول {وآتينا عيسى ابن مريم البينات} يعني: ما أُوتي من المعجزة {وأيدناه} وقوَّيناه {بِرُوحِ القدس} بجبريل عليه السَّلام وذلك أنَّه كان قرينه يسير معه حيث سار يقول: فعلنا بكم كلَّ هذا فما استقمتم لأنَّكم {كلما جَاءَكُمْ رَسُولٌ بِمَا لا تَهْوَى أَنْفُسُكُمُ اسْتَكْبَرْتُمْ} ثمَّ تعظَّمتم عن الإِيمان به {ففريقاً كذَّبتم} مثل عيسى ومحمَّدٍ عليهما السَّلام {وفريقاً تقتلون} مثل يحيى وزكريا عليهما السَّلام',NULL,'117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (96,'{وقالوا قلوبنا غلفٌ} هو أنَّ اليهود قالوا استهزاءً وإنكاراً لما أتى به محمد عليه السَّلام: قلوبنا غلفٌ عليها غشاوةٌ فهي لا تعي ولا تفقه ما تقول وكلُّ شيءٍ في غلافٍ فهو أغلف وجمعه: غُلْف ثمَّ أكذبهم الله تعالى فقال: {بل لعنهم الله} أَيْ: أبعدهم من رحمته فطردهم {فقليلاً ما يؤمنون} أَيْ: فبقليلٍ يؤمنون بما في أيديهم وقال قتادة: فقليلاً ما يؤمنون أَيْ: ما يؤمن منهم إلاَّ قليلٌ كعبد الله بن سلام',NULL,'117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (97,'{ولما جاءهم كتاب} يعني: القرآن {مصدِّق} موافقٌ {لما معهم} {وكانوا} يعني: اليهود {من قبل} نزول الكتاب {يستفتحون} يستنصرون {على الذين كفروا} بمحمد عليه السَّلام وكتابه ويقولون: اللَّهم انصرنا بالنَّبيِّ المبعوث في آخر الزَّمان {فلما جاءهم ما عرفوا} يعني: الكتاب وبعثة النبيّ {كفروا} ثمَّ ذمَّ صنيعهم فقال:',NULL,'117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (98,'{بئسما اشتروا به أنفسهم} أَيْ: بئس ما باعوا به حظَّ أنفسهم من الثَّواب بالكفر بالقرآن {بغياً} أَيْ: حسداً {أن ينزل الله} أَيْ: إنزال اللَّهُ {مِنْ فَضْلِهِ عَلَى مَنْ يَشَاءُ مِنْ عباده} وذلك أنَّ كفر اليهود لم يكن من شك ولا اشتباهٍ وإنَّما كان حسداً حيث صارت النُّبوَّة في ولد إسماعيل عليه السَّلام {فباءوا} فانصرفوا واحتملوا {بغضب} من الله عليهم لأجل تضييعهم التَّوراة {على غضب} لكفرهم بالنبي محمد صلى الله عليه وسلم والقرآن',NULL,'117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (99,'{وإذا قيل} لليهود {آمنوا بما أنزل الله} بالقرآن {قالوا نؤمن بما أنزل علينا} يعني: التَّوراة {ويكفرون بما وراءه} بما سواه {وهو الحقُّ} يعني: القرآن {مصدِّقاً لما معهم} موافقاً للتَّوراة ثمَّ كذَّبَهم الله تعالى في قولهم: نؤمن بما أنزل علينا بقوله: {فلمَ تقتلون أنبياء الله} أَيْ: أيُّ كتابٍ جُوِّز فِيهِ قتلُ نبيٍّ؟ ! {إن كنتم مؤمنين} شرطٌ وجوابه ما قبله ثمَّ ذكر أنَّهم كفروا بالله تعالى مع وضوح الآيات في زمن موسى عليه السَّلام فقال:',NULL,'118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (100,'{وَلَقَدْ جَاءَكُمْ مُوسَى بِالْبَيِّنَاتِ} يعني: العصا واليد وفلق البحر {ثمَّ اتخذتم العجل من بعده} إلهاً {وأنتم ظالمون}',NULL,'118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (101,'{وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا ما آتيناكم بقوَّة واسمعوا} مضى تفسيره ومعنى: واسمعوا أَيْ: اقبلوا ما فيه من حلاله وحرامه وأطيعوا {قالوا سمعنا} ما فِيهِ {وعصينا} ما أُمرنا به {وأُشربوا في قلوبهم العجل} وسُقوا حبَّ العجل وخُلطوا بحبِّ العجل حتى اختلط بهم والمعنى: حُبَّب إليهم العجل {بكفرهم} باعتقادهم التَّشبيه لأنَّهم طلبوا ما يُتَصَوَّرُ فِي نفوسهم {قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهِ إِيمَانُكُمْ إِنْ كنتم مؤمنين} هذا تكذيبٌ لهم في قولهم: نؤمن بما أنزل علينا وذلك أن آبائهم ادَّعوا الإِيمان ثمَّ عبدوا العجل فقيل لهم: بئس الإيمان إيمانٌ يأمركم بالكفر والمعنى: لو كنتم مؤمنين ما عبدتم العجل يعني: آبائهم كذلك أنتم لو كنتم مؤمنين بما أُنزل عليكم ما كذَّبتم محمَّداً',NULL,'118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (102,'{قُلْ إِنْ كَانَتْ لَكُمُ الدَّارُ الآخِرَةُ عِنْدَ اللَّهِ خَالِصَةً مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إن كنتم صادقين} كانت اليهود تقول: لَنْ يَدْخُلَ الْجَنَّةَ إِلا مَنْ كَانَ هُودًا فقيل لهم: إن كنتم صادقين فتمنَّوا الموت فإنَّ مَنْ كان لا يشكُّ فِي أنَّه صائر إلى الجنَّةِ فالجنَّةُ آثرُ عنده',NULL,'119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (103,'{ولن يتمنوه أبداً} لأنَّهم عرفوا أنَّهم كفرةٌ ولا نصيب لهم فِي الجنَّة وهو قوله تعالى: {بما قدَّمت أيديهم} أيْ: بما عملوا من كتمان أمر محمد صلى الله عليه وسلم وتغيير نعته {واللَّهُ عليم بالظالمين} فيه معنى التَّهديد',NULL,'119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (104,'{ولتجدنهم} يا محمَّدُ يعني: علماءَ اليهود {أَحْرَصَ النَّاسِ عَلَى حَيَاةٍ} لأنَّهم علموا أنَّهم صائرون إلى النَّار إذا ماتوا لما أتوا به في أمر محمَّدٍ صلى الله عليه وسلم {ومن الذين أشركوا} أَيْ: وأحرص من منكري البعث ومَنْ أنكر البعث أحبَّ طول العمر لأنَّه لا يرجو بعثاً فاليهود أحرص منهم لأنَّهم علموا ما جنوا فهم يخافون النَّار {يود أحدكم} أَيْ: أحد اليهود {لو يعمَّرُ ألف سنة} لأنَّه يعلم أنَّ آخرته قد فَسَدَتْ عليه {وما هو} أَيْ: وما أحدهم {بِمُزَحْزِحِهِ} بِمُبْعِدِهِ من {العذاب أن يعمَّر} تعميره',NULL,'119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (105,'{قل من كان عدوا لجبريل} سألت اليهود نبيَّ الله صلى الله عليه وسلم عن مَنْ يأتيه من الملائكة؟ فقال: جبريل فقالوا: هو عدوُّنا ولو أتاك ميكائيل آمنَّا بك فأنزل الله هذه الآية والمعنى: قل من كان عدوا لجبريل فليمت غيظاً {فإنه نزله} أَيْ: نزَّل القرآن {على قلبك بإذن الله} بأمر الله {مصدقاً} موافقاً لما قبله من الكتب {وَهُدًى وَبُشْرَى لِلْمُؤْمِنِينَ} ردٌّ على اليهود حين قالوا: إنَّ جبريل ينزل بالحرب والشِّدَّة فقيل إنَّه - وإنْ كان ينزل بالحرب والشدَّة على الكفرين - فإنه ينزل بالهدى والبشرى للمؤمنين',NULL,'120',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (106,'{مَنْ كَانَ عَدُوًّا لِلَّهِ وَمَلائِكَتِهِ وَرُسُلِهِ وَجِبْرِيلَ وميكال فإن الله عدو للكافرين} أَيْ: مَنْ كان عدوّاً لأحد هؤلاء فإن اللَّهَ عدوٌّ له لأن عدوَّ الواحدِ عدوُّ الجميع وعدوُّ محمَّدٍ عدوُّ الله والواو ها هنا بمعنى أو كقوله: {ومَن يكفر بالله وملائكته وكتبه ورسله} لأنَّ الكافر بالواحد كافرٌ بالكلِّ وقوله: {فإنَّ الله عدوٌ للكافرين} أَيْ: إنَّه تولَّى تلك العداوة بنفسه وكفى ملائكته ورسله أمر مَنْ عاداهم',NULL,'120',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (107,'{ولقد أنزلنا إليك آيات بيّنات} دلالاتٍ واضحاتٍ وهذا جوابٌ لابن صوريا حين قال: يا محمد ما أُنزل عليك من آيةٍ بيِّنةٍ فَنَتَّبعكَ بها {وما يكفر بها إلاَّ الفاسقون} الخارجون عن أديانهم واليهود خرجت بالكفر بمحمد صلى الله عليه وسلم عن شريعة موسى عليه السَّلام ولمَّا ذكر محمدٌ صلى الله عليه وسلم لهم ما أخذ الله تعالى عليهم من العهد فيه قال مالك بن الصَّيف: والله ما عُهد إلينا في محمدٍ عهدٌ ولا ميثاق فأنزل الله تعالى:',NULL,'120',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (108,'{أو كلما عاهدوا عهداً} الآية وقوله {نبذة فريق منهم} يعني: الذين نقضوه من علمائهم {بَلْ أَكْثَرُهُمْ لا يُؤْمِنُونَ} لأنهم من بين ناقضٍ للعهد وجاحدٍ لنبوَّته معاندٍ له وقوله:',NULL,'121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (109,'{نبذ فريق من الذين أوتوا الكتاب} يعني: علماء اليهود {كتاب الله} يعني التَّوراة {وراء ظهورهم} أَيْ: تركوا العمل به حين كفروا بمحمد صلى الله عليه وسلم والقرآن {كأنهم لا يعلمون} أنَّه حقٌّ وأنَّ ما أتى به صدقٌ وهذا إخبارٌ عن عنادهم ثمَّ أخبر أنَّهم رفضوا كتابة واتَّبعوا السِّحر فقال: {واتبعوا} يعني: علماء اليهود',NULL,'121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (110,'{ما تتلوا الشياطين} أَيْ: ما كانت الشَّياطين تُحدِّث وتقصُّ من السِّحر {على ملك سليمان} في عهده وزمان مُلْكه وذلك أنَّ سليمان عليه السلام لما نُزع ملكه دفنت الشَّياطين في خزانته سحراً ونيرنجات فلمَّأ مات سليمان دلَّت الشياطين عليها النَّاس حتى استخرجوها وقالوا للنَّاس: إنَّما مَلَكَكُم سليمان بهذا فتعلَّموه فأقبل بنو إسرائيل على تعلُّمها ورفضوا كتب أنبيائهم فبرَّأ الله سليمان عليه السَّلام فقال: {وَمَا كَفَرَ سُلَيْمَانُ} أَيْ: لم يكن كافراً ساحراً يسحر {ولكنَّ الشياطين كفروا} بالله {يعلمون الناس السحر} يريد: ما كتب لهم الشَّياطين من كُتب السِّحر {وما أنزل على الملكين} أَيْ: ويُعلِّمونهم ما أُنزل عليهما أَيْ: ما علِّما وأُلْهِمَا وقُذِف فِي قلوبهما من علم التَّفرقة وهو رقيةٌ وليس بسحرٍ وقوله: {وما يعلِّمان} يعني: المَلَكَيْن السِّحر {من أحدٍ} أحداً {حتى يقولا إنما نحن فتنة} ابتلاءٌ واختبارٌ {فلا تكفر} وذلك أن الله عز وجل امتحن النَّاس بالملكين فِي ذلك الوقت وجعل المحنة في الكفر والإيمان أن يقبل القابلُ تعلُّم السِّحر فيكفر بتعلُّمه ويؤمن بتركه ولله تعالى أن يمتحن عباده بما شاء وهذا معنى قوله: {إِنَّمَا نَحْنُ فِتْنَةٌ فَلا تَكْفُرْ} أَيْ: محنة من الله نحبرك أنَّ عمل السِّحر كفرٌ بالله وننهاك عنه فإنْ أطعتنا نجوت وإن عصيتنا هلكت وقوله تعالى {فيتعلمون} أَيْ: فيأتون فيتعلَّمون من الملكين {مَا يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْجِهِ} وهو أن يؤخذ كلُّ واحدٍ منهما عن صاحبه ويُبغَّض كلُّ واحدٍ منهما إلى الآخر {وما هم} أَيْ: السَّحَرة الذين يتعلَّمون السِّحر {بضارين به} بالسِّحر {من أحدٍ} أحداً {إلاَّ بإذن الله} بإرادته كون ذلك أَيْ: لا يضرُّون بالسِّحر إلاَّ مَنْ أراد الله أن يلحقه ذلك الضَّرر {ويتعلمون ما يضرُّهم} في الآخرة {ولا ينفعهم} (في الدُّنيا) {ولقد علموا} يعني: اليهود {لمن اشتراه} من اختار السِّحر {مَا لَهُ فِي الآخِرَةِ مِنْ خَلاقٍ} من نصيب في الجنة ثمَّ ذمَّ صنيعهم فقال: {وَلَبِئْسَ مَا شَرَوْا بِهِ أَنْفُسَهُمْ} أَيْ: بئس شيءٌ باعوا به حظَّ أنفسهم حيث اختاروا السِّحر ونبذوا كتاب الله {لو كانوا يعلمون} كُنه ما يصير إليه مَنْ يخسر الآخرة من العقاب',NULL,'121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (111,'{ولو أنَّهم آمنوا} بمحمَّدٍ عليه السَّلام والقرآن {واتقوا} اليهوديَّة والسِّحر لأثيبوا ما هو خيرٌ لهم من الكسب بالسِّحر وهو قوله تعالى: {لَمَثُوبَةٌ مِنْ عِنْدِ اللَّهِ خَيْرٌ لَوْ كَانُوا يعلمون}',NULL,'123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (112,'{يا أيها الذين آمنوا لا تقولوا راعنا} كان المسلمون يقولون للنبي صَلَّى اللَّهُ عَلَيْهِ وسلم: راعنا سمعك وكان هذا بلسان اليهوديَّة سبَّاً قبيحاً فلمَّا سمعوا هذه الكلمة يقولونها لرسول الله صلى الله عليه وسلم أعجبتهم فكانوا يأتونه ويقولون ذلك ويضحكون فيما بينهم فنهى الله تعالى المؤمنين عن ذلك وأنزل هذه الآية وأمرهم أن يقولوا بدل راعنا {انظرنا} أَيْ: انظر إلينا حتى نُفهمك ما نقول {واسمعوا} أيْ: أطيعوا واتركوا هذه الكلمة لأنَّ الطَّاعة تجب بالسَّمع {مَا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَلا الْمُشْرِكِينَ أَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ من ربكم} أَيْ: خيرٌ من عند ربكم',NULL,'123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (113,'{والله يختص برحمته} يخصُّ بنبوَّته {مَنْ يشاء والله ذو الفضل العظيم}',NULL,'123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (114,'{ما نَنْسَخْ من آية أو ننسها} أي: مانرفع آيةً من جهة النَّسخ بأن نُبطل حكمها أو بالإِنساءِ لها بأنْ نمحوها عن القلوب {نأت بخير منها} أَيْ: أصلح لمن تُعبِّد بها وأنفع لهم وأسهل عليهم وأكثر لأجرهم {أو مثلها} في المنفعة والمثوبة {أَلَمْ تَعْلَمْ أَنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ} من النِّسخِ والتَّبديل وغيرهما {قدير} نزلت هذه الآية حين قال المشركون: إنَّ محمداً يأمر أصحابه بأمرٍ ثمَّ ينهاهم عنه ويأمرهم بخلافه ويقول اليوم قولاً ويرجع عنه غداً ما هذا القرآن إلاَّ كلام محمد فأنزل الله تعالى هذه الآية وقولَهُ: {وإذا بدَّلنا آية مكان آيةٍ} الآية',NULL,'123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (115,'{ألم تعلم أن الله له ملك السماوات والأرض} يعمل فيهما ما يشاء وهو أعلم بوجه الصَّلاح فيما يتعبَّدهم به من ناسخ ومنسوخ {وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ} أَيْ: والٍ يلي أمركم ويقوم به {ولا نصير} ينصركم وفي هذا تحذيرٌ من عذابه إذ لا مانع منه',NULL,'124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (116,'{أم تريدون} أَيْ: بل أتريدون {أن تسألوا رسولكم} محمدا صلى الله عليه وسلم {كما سئل موسى من قبل} وذلك أنَّ قريشاً قالوا: يا محمَّدُ اجعل لنا الصَّفا ذهباً ووسِّعْ لنا أرض مكَّة فَنُهوا أن يقترحوا عليه الآيات كما اقترح قوم موسى عليه السَّلام حين قالوا: {أرنا الله جهرة} وذلك أنَّ السُّؤال بعد قيام البراهين كفرٌ ولذلك قال: {وَمَنْ يَتَبَدَّلِ الْكُفْرَ بِالإِيمَانِ فَقَدْ ضَلَّ سواء السبيل} قصده ووسطه',NULL,'124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (117,'{ودَّ كثيرٌ من أهل الكتاب} نزلت حين قالت اليهود للمسلمين بعد وقعة أُحدٍ: ألم تروا إلى ما أصابكم ولو كنتم على الحق ما هزمتم فارجعوا إلى ديننا فذلك قوله تعالى: {لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ إِيمَانِكُمْ كُفَّارًا حَسَدًا من عند أنفسهم} أَيْ: فِي حكمهم وتديّنهم ما لم يؤمروا به {مِنْ بَعْدِ مَا تَبَيَّنَ لهم الحق} فِي التَّوراة أنَّ قول محمَّدٍ صدقٌ ودينه حقٌّ {فاعفوا واصفحوا} وأعرضوا عن مساوئ أخلقهم وكلامهم وغلِّ قلوبهم {حتى يأتي الله بأمره} بالقتال',NULL,'125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (118,'قال تعالى {وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكَاةَ وَمَا تُقَدِّمُوا لأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ إِنَّ اللَّهَ بما تعملون بصير}',NULL,'125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (119,'{وقالوا لن يدخل الجنة} أَيْ: قالت اليهود: لن يدخل الجنَّة {إلاَّ مَنْ كان هوداً} وقالت النَّصارى: لن يدخلها إلاَّ النَّصارى {تلك أمانيهم} التي تمنَّوها على الله سبحانه باطلاً {قل هاتوا برهانكم} قرِّبوا حجَّتكم على ما تقولون ثمَّ بيَّن مَنْ يدخلها فقال:',NULL,'125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (120,'{بلى} يدخلها {مَنْ أسلم وجهه لله} انقاد لأمره وبذل له وجهه في السُّجود {وهو محسن} مؤمنٌ مصدقٌ بالقرآن',NULL,'125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (121,'{وقالت اليهود ليست النصارى على شيء} لمَّا قدم وفد نجران فتنازعوا مع اليهود وكفَّر كلُّ واحدٍ من الفريقين الآخر وقوله تعالى: {وهم يتلون الكتاب} يعني: إنَّ الفريقين يتلون التَّوراة وقد وقع بينهما هذا الاختلاف وكتابهم واحد فدلَّ بهذا على ضلالتهم {كذلك قال الذين لا يعلمون} يعني: كفَّار الأمم الماضية وكفَّار هذه الأمَّة {مثل قولهم} في تكذيب الأنبياء والاختلاف عليهم فسبيل هؤلاء الذين يتلون الكتاب كسبيل مَنْ لا يعلم الكتاب من المشركين في الإنكار لدين الله سبحانه {فالله يحكم بينهم} أَيْ يُريهم عياناً مَنْ يدخل الجنَّة ومَنْ يدخل النَّار',NULL,'125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (122,'{ومن أظلم ممن منع مساجد الله} يعني: بيت المقدس ومحاربيه نزلت في أهل الرُّوم حين خرَّبوا بيت المقدس {أولئك} يعني: أهل الرُّوم {مَا كَانَ لَهُمْ أَنْ يَدْخُلُوهَا إِلا خَائِفِينَ} لم يدخل بيت المقدس بعد أن عمره المسلمون روميٌّ إلاَّ خائفاً لو عُلم به قُتل {لهم في الدنيا خزي} يعني: القتل للحربيِّ والجزية للذميِّ',NULL,'126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (123,'{ولله المشرقُ والمغرب} أَيْ: إنَّه خالقهما نزلت في قوم من الصَّحابة سافروا فأصابهم الضَّباب فتحرَّوا القِبلة وصلَّوا إلى أنحاءٍ مختلفةٍ فلمَّا ذهب الضَّباب استبان أنَّهم لم يصيبوا فلمَّا قدموا سألوا رسول الله صلى الله عليه وسلم عن ذلك وقوله تعالى: {فأينما تولوا} أَيْ: تصرفوا وجوهكم {فثمَّ وجه الله} أَيْ: فهناك قِبلة الله وجهته التي تعبَّدكم الله بالتوجُّه إليها {إنَّ الله واسعٌ عليم} أَيْ: واسع الشَّريعة يُوسِّع على عباده في دينهم (اختلف العلماء في حكم هذه الآية فمنهم مَنْ قال: هي منسوخة الحكم بقوله: {فولِّ وجهك شطر المسجد الحرام} ومنهم مَنْ قال: حكمها ثابت غير أنها مخصوصة بالنَّوافل في السفر وقيل: إنها نزلت في شأن النجاشي حين صلَّى عليه النبي صلى الله عليه وسلم مع أصحابه وقولهم له: كيف تُصلِّي على رجل صلَّى إلى غير قبلتنا فأنزل الله تعالى هذه الآية وبيَّن أنَّ النجاشي وإنْ صلَّى إلى المشرق أو المغرب فإنَّما قصد بذلك وجه الله وعبادته ومعنى {فثمَّ وجه الله} أَيْ: فَثَمَّ رضا الله وأمره كما قال: {إنَّما نُطعمكم لوجه الله} والوجهُ والجِهةُ والوِجهةُ: القِبلةُ)',NULL,'126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (124,'{وقالوا اتخذ الله ولداً} يعني: اليهود في قولهم: {عزير ابنُ الله} والنصارى في قولهم: {المسيح ابنُ الله} والمشركين في قولهم: الملائكة بنات الله ثمَّ نزَّه نفسه عن الولد فقال {سبحانه بل} ليس الأمر كذلك {له ما في السماوات والأرض} عبيداً وملكاً {كلٌّ له قانتون} مطيعون: يعني: أهل طاعته دون النَّاس أجمعين',NULL,'128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (125,'{بديع السماوات والأرض} خالقهما وموجدهما لا على مثالٍ سبق {وإذا قضى أمراً} قدَّره وأراد خلقه {فإنما يقول له كن فيكون} أَيْ: إنما يُكوِّنه فيكون وشرطه أن يتعلَّق به أمره (وقال الأستاذ أبو الحسن: يُكوِّنه بقدرته فيكون على ما أراد)',NULL,'128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (126,'{وقال الذين لا يعلمون} يعني: مشركي العرب قالوا لمحمَّدٍ: لن نؤمن لك حتى {يكلّمنا الله} أنَّك رسوله {أو تأتينا آية} يعني: ما سألوا من الآيات الأربع في قوله تعالى: {وَقَالُوا لَنْ نُؤْمِنَ لَكَ حَتَّى تَفْجُرَ لَنَا} ومعنى {لولا يكلِّمنا الله} أَيْ: هلاَّ يُكلِّمنا الله أنَّك رسوله {كذلك قال الذين من قبلهم} يعني: كفَّار الأمم الماضية كفروا بالتَّعنُّتِ بطلب الآيات كهؤلاء {تشابهت قلوبهم} أشبه بعضها بعضاً فِي الكفر والقسوة ومسألة المحال {قد بيَّنا الآيات لقوم يوقنون} أَيْ: مَنْ أيقن وطلب الحقَّ فقد أتته الآيات لأنَّ القرآن برهانٌ شافٍ',NULL,'128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (127,'{إنا أرسلناك بالحق} بالقرآن والإسلام أَيْ: مع الحقِّ {بشيراً} مُبشِّراً للمؤمنين {ونذيراً} مُخوِّفاً ومُحذِّراً للكافرين {ولا تُسأل عن أصحاب الجحيم} أَيْ: لست بمسؤولٍ عنهم وذلك أن النبي صلى الله عليه وسلم قال: لو أنَّ الله عز وجل أنزل بأسه باليهود لآمنوا فأنزل الله تعالى هذه الآية أَيْ: ليس عليك من شأنهم عُهدةٌ ولا تبعة',NULL,'129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (128,'{ولن ترضى عنك اليهود} الآية نزلت في تحويل القبلة وذلك أنَّ اليهود والنَّصارى كانوا يرجون أنَّ محمدا صلى الله عليه وسلم يرجع إلى دينهم فلمَّا صرف الله تعالى القِبلة إلى الكعبة شقَّ عليهم وأيسوا منه أن يوافقهم على دينهم فأنزل الله تعالى: {وَلَنْ تَرْضَى عَنْكَ الْيَهُودُ وَلا النَّصَارَى حَتَّى تتبع ملتهم} يعني: دينهم وتصلِّي إلى قبلتهم {قل إنَّ هدى الله هو الهدى} أَي: الصِّراط الذي دعا إليه وهدى إليه هو طريق الحقِّ {ولَئِنِ اتبعت أهواءهم} يعني: ما كانوا يدعونه إليه من المهادنة والإِمهال {بعد الذي جاءك من العلم} أَي: البيان بأنَّ دين الله عز وجل هو الإسلام وأنَّهم على الضلالة {مَا لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَلا نصير}',NULL,'129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (129,'{الذين آتيناهم الكتاب} يعني: مؤمني اليهود {يتلونه حق تلاوته} يقرؤونه كما أُنزل ولا يُحرِّفونه ويتَّبعونه حقَّ اتباعه',NULL,'129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (130,'قال تعالى {يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أنعمت عليكم وأني فضلتكم على العالمين}',NULL,'130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (131,'قال تعالى {وَاتَّقُوا يَوْمًا لا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئًا وَلا يُقْبَلُ مِنْهَا عَدْلٌ وَلا تَنْفَعُهَا شفاعة ولا هم ينصرون}',NULL,'130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (132,'{وإذ ابتلى إبراهيم ربُّه} اختبره أَيْ: عامله معاملة المُختبِر {بكلماتٍ} هي عشر خصالٍ: خمسٌ في الرأس وهي الفرق والمضمضة والاستنشاق والسِّواك وقصُّ الشَّارب وخمسٌ في الجسد وهي: تقليم الأظفار وحلق العانة والختان والاستنجاء ونتف الرفغنين {فأتمهنَّ} أدَّاهنَّ تامَّاتٍ غير ناقصات {قال} الله تعالى: {إني جاعلك للناس إماماً} يقتدي بك الصَّالحون فقال إبراهيم: {ومِنْ ذريتي} أَيْ: ومن أولادي أيضاً فاجعل أئمةً يُقتدى بهم فقال الله عز وجل {لا ينال عهدي الظالمين} يريد: مَنْ كان من ولدك ظالماً لا يكون إماماً ومعنى: {عهدي} أَيْ: نُبوَّتي',NULL,'130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (133,'{وإذ جعلنا البيت} يعني: الكعبة {مثابةً للناس} معاداً يعودون إليه لا يقضون منه وطراً كلَّما انصرفوا اشتاقوا إليه {وأَمْناً} أَيْ ك مؤمناً وكانت العرب يرى الرَّجل منهم قاتل أبيه فِي الحرم فلا يتعرَّض له وأمَّا اليوم فلا يُهاج الجاني إذا التجأ إليه عند أهل العراق وعند الشافعيِّ: الأولى أن لا يُهاج فإنْ أُخيف بإقامة الحدِّ عليه جاز وقد قال كثيرٌ من المفسرين: مَنْ شاء آمن ومَنْ شاء لم يُؤمن كما أنَّه لمَّا جعله مثابةً مَنْ شاء ثاب ومَنْ شاء لم يثب {واتَّخذوا} أَيْ: النَّاس {من مقام إبراهيم} وهو الحجر الذي يُعرف بمقام إبراهيم وهو موضع قدميه {مصلَّى} وهو أنَّه تُسنُّ الصَّلاة خلف المقام قرئ على هذا الوجه على الخبر وقرئ بالكسر على الأمر {وعهدنا إلى إبراهيم وإسماعيل} أمرناهما وأوصينا إليهما {أنْ طهِّرا بيتي} من الأوثان والرِّيَب ( {للطائفين} حوله وهم النزائع إليه من آفاق الأرض {والعاكفين} أي: المقيمين فيه وهم سكان الحرم {والركع} جمع راكع و {السجود} جمع ساجد مثله: قاعد وقعود)',NULL,'130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (134,'{وإذ قال إبراهيم رب اجعل هذا} أَيْ: هذا المكان وهذا الموضع {بلداً} مسكناً {آمناً} أَيْ: ذا أمنٍ لا يُصاد طيره ولا يُقطع شجره ولا يُقتل فيه أهله {وارزق أهله من الثمرات} أنواع حمل الشَّجر {مَنْ آمن منهم بالله واليوم الآخر} خَصَّ إبراهيم عليه السلام بطلب الرزق المؤمنين قال تعالى: {وَمَنْ كفر فأمتعه قليلا} فسأرزفه إلى منهى أجله {ثمَّ أضطره} أُلجئه فِي الآخرة {إِلَى عَذَابِ النَّارِ وَبِئْسَ المصير} هي',NULL,'131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (135,'{وإذ يرفع إبراهيم القواعد} أصول الأساس {من البيت وإسماعيل} ويقولان: {ربنا تقبلْ منَّا} تقرُّبنا إليك ببناء هذا البيت {إنك أنت السميع} لدعائنا {العليمُ} بما في قلوبنا',NULL,'131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (136,'{ربنا واجعلنا مسلمين لك} مطيعي مُنقادين لحكمك {ومن ذريتنا أمة} جماعةً {مسلمة لك} وهم المهاجرون والأنصار والتَّابعون بإحسان {وأرنا مناسكنا} عرّفنا مُتَعبَّداتنا',NULL,'131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (137,'{ربنا وابعث فيهم} في الأمَّة المسلمة {رسولاً منهم} يريد: محمدا صلى الله عليه وسلم {يتلو عليهم آياتك ويعلمهم الكتاب والحكمة} أَي: القرآن {ويزكيهم} ويُطهِّرهم من الشِّرك {إنك أنت العزيز} الغالب القويُّ الذي لا يعجزه شيءٌ ومضى تفسير الحكيم',NULL,'132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (138,'{ومَنْ يرغب عن ملة إبراهيم} أَيْ: وما يرغب عنها ولا يتركها {إلاَّ مَنْ سفه نفسه} أَيْ: جهلها بأَنْ لم يعلم أنَّها مخلوقةٌ لله تعالى يجب عليها عبادة خالقها {ولقد اصطفيناه في الدُّنيا} اخترناه للرِّسالة {وإنه في الآخرة لمن الصالحين} أَيْ: من الأنبياء',NULL,'132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (139,'{إذ قال له ربه أسلم} أخلص دينك لله سبحانه بالتَّوحيد وقيل: أسلم نفسك إلى الله {قال أسلمت} بقلبي ولساني وجوارحي {لرب العالمين}',NULL,'132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (140,'{ووصَّى بها} أَيْ: أمر بالملَّة وقيل: بكلمة الإِخلاص {إبراهيم بنيه ويعقوب يا بنيَّ} أراد: أَنْ يَا بَنِيَّ {إِنَّ اللَّهَ اصْطَفَى لَكُمُ الدِّينَ} أَي: الإِسلام دين الحَنيِفيَّة {فلا تموتن إلا وأنتم مسلمون} أي: الزموا الإِسلام حتى إذا أدرككم الموت صادفكم عليه',NULL,'132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (141,'{أم كنتم شهداء} ترك الكلام الأوَّل وعاد إلى مُخاطبة اليهود المعنى: بل أكنتم شهداء أَيْ: حضوراً {إذ حضر يعقوب الموت} وذلك أنَّ اليهود قالت النبي صلى الله عليه وسلم: ألستَ تعلم أنَّ يعقوب يوم مات أوصى بنيه باليهوديَّة؟ فأكذبهم الله تعالى وقال: أكنتم حاضرين وصيته {إِذْ قَالَ لِبَنِيهِ مَا تَعْبُدُونَ مِنْ بَعْدِي}',NULL,'133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (142,'{تِلْكَ أُمَّةٌ} يعني: إبراهيم وبنيه ويعقوب وبنيه {قد خلت} قد مضت {لَهَا مَا كَسَبَتْ} من العمل {ولكم} يا معشر اليهود {ما كسبتم} أَيْ: حسابهم عليهم وإنَّما تُسألون عن أعمالكم',NULL,'133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (143,'{وقالوا كونوا هوداً أو نصارى} نزلت فِي يهود المدينة ونصارى نجران قال كلُّ واحدٍ من الفريقين للمؤمنين: كونوا على ديننا فلا دين إلاَّ ذلك فقال الله تعالى: {قل بل ملَّة إبراهيم حنيفاً} يعني: بل نتبع ملَّة إبراهيم حنيفاً مائلاً عن الأديان كلِّها إلى دين الإسلام ثمَّ أمر المؤمنين أن يقولوا:',NULL,'133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (144,'{آمنا بالله وما أنزل إلينا} يعني: القرآن {وَمَا أُنْزِلَ إِلَى إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ والأسباط} وهم أولاد يعقوب وكان فيهم أنبياء لذلك قال: وما أنزل إليهم وقوله تعالى: {لا نفرق بين أحد منهم} أَيْ: لا نكفر ببعضٍ ونؤمن ببعضٍ كما فعلت اليهود والنَّصارى',NULL,'133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (145,'{فإن آمنوا بمثل ما آمنتم به} أَيْ: إِنْ أتوا بتصديقٍ مثلِ تصديقكم وكان إيمانُهم كإيمانكم {فقد اهتدوا} فقد صاروا مسلمين {وإن تولوا} أعرضوا {فَإِنَّمَا هُمْ فِي شِقَاقٍ} فِي خلافٍ وعداوةٍ {فَسَيَكْفِيْكَهُمُ الله} ثمَّ فعل ذلك فكفاه أمر اليهود بالقتل والسَّبي فِي قريظة والجلاء والنَّفي فِي بني النضير والجزية والذَّلَّة فِي نصارى نجران',NULL,'134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (146,'{صبغة الله} أَي: الزموا دين الله {وَمَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً} أي: ومن أحسن من الله ديناُ؟',NULL,'134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (147,'{قل} يا محمَّدُ لليهود والنَّصارى: {أتحاجوننا في الله} أَتُخاصموننا في دين الله؟ وذلك أنهم قالوا: إن ديننا هو الأقدم وكتابنا هو الأسبق ولو كنتَ نبيّاً لكنتَ منَّا {ولنا أعمالنا} نُجازى بحسنها وسيِّئها وأنتم في أعمالكم على مثل سبيلنا {ونحن له مخلصون} مُوحِّدون',NULL,'134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (148,'{أم تقولون} إنَّ الأنبياء من قبل أن تنزل التَّوراة والإِنجيل {كَانُوا هُودًا أَوْ نَصَارَى} {قُلْ أَأَنْتُمْ أَعْلَمُ أم الله} أَيْ: قد أخبرنا الله سبحانه أنَّ الأنبياء كان دينهم الإِسلام ولا أحدٌ أعلم منه {وَمَنْ أَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهُ مِنَ الله} هذا توبيخٌ لهم وهو أنَّ الله تعالى أشهدهم فِي التِّوراة والإِنجيل أنَّه باعثٌ فيهم محمداً صلى الله عليه وسلم من ذريَّة إبراهيم عليه السَّلام وأخذ مواثيقهم أَنْ يُبيِّنوه ولا يكتموه ثمَّ ذكر قصَّة تحويل القبلة فقال:',NULL,'134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (149,'{تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَهَا مَا كَسَبَتْ وَلَكُمْ مَا كَسَبْتُمْ وَلا تُسْأَلُونَ عَمَّا كَانُوا يعملون}',NULL,'135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (150,'{سيقول السفهاء من الناس} يعني: مشركي مكَّة ويهود المدينة {ما ولاَّهم} ما صرفهم؟ يعنون النبي صلى الله عليه وسلم والمؤمنين {عن قبلتهم التي كانوا عليها} وهي الصَّخرة {قل لله المشرق والمغرب} يأمر بالتَّوجُّه إلى أيٍّ جهةٍ شاء {يَهْدِي من يشاء إلى صراط مستقيم} دينٍ مستقيمٍ يريد: إنِّي رضيتُ هذه القِبلة لمحمَّدٍ صلى الله عليه وسلم ثمَّ مدح أمَّته فقال:',NULL,'135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (151,'{وكذلك} أي: وكما هديناكم صراطاً مستقيماً {جعلناكم أمة وسطاً} عدولاً خياراً {لتكونوا شهداء على الناس} لتشهدوا على الأمم بتبليغ الأنبياء {ويكون الرسول عليكم} على صدقكم {شهيداً} وذلك أنَّ الله تعالى يسأل الأمم يوم القيامة فيقول: هل بلَّغكم الرُّسل الرِّسالة؟ فيقولون: ما بلَّغنا أحدٌ عنك شيئاً فيسأل الرُّسل فيقولون: بلَّغناهم رسالتك فعصوا فيقول: هل لكم شهيدٌ؟ فيقولون: نعم أمة محمد صلى الله عليه وسلم فيشهدون لهم بالتِّبليغ وتكذيب قومهم إيَّاهم فتقول الأمم: يارب بمَ عرفوا ذلك وكانوا بعدنا؟ فيقولون: أخبرنا بذلك نبيُّنا في كتابه ثمَّ يزكيهم محمد صلى الله عليه وسلم {وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنْتَ عَلَيْهَا} أَي: التي أنتَ عليها اليوم وهي الكعبةُ قِبلةً {إلاَّ لنعلم} لنرى وقيل: معناه: لنميّز {مَنْ يتبع الرسول} في تصديقه بنسخ القِبلة {ممن ينقلب على عقبيه} يرتدُّ ويرجع إلى الكفر وذلك أنَّ الله تعالى جعل نسخ القبلة عن الصَّخرة إلى الكعبة ابتلاءً لعباده المؤمنين فمَنْ عصمه صدَّق الرَّسول في ذلك ومَنْ لم يعصمه شكَّ في دينه وتردَّد عليه أمره وظنَّ أنَّ محمداً صلى الله عليه وسلم في حيرةٍ من أمره فارتدَّ عن الإِسلام وهذا معنى قوله: {وَإِنْ كَانَتْ لَكَبِيرَةً} أَيْ: وقد كانت التَّولية إلى الكعبة لثقيلةً إِلا {عَلَى الَّذِينَ هَدَى الله} عصمهم الله بالهداية فلمَّا حوِّلت القبلة قالت اليهود: فكيف بمَنْ مات منكم وهو يصلِّي على القبلة الأولى؟ لقد مات على الضَّلالة فَأَنْزَلَ اللَّهُ تَعَالَى: {وَمَا كَانَ اللَّهُ لِيُضِيعَ إيمانكم} أَيْ: صلاتكم التي صليتم وتصديقكم بالقِبلة الأولى {إنَّ الله بالناس} يعني: بالمؤمنين {لرؤوف رحيم} والرَّأفة أشدُّ الرَّحمة',NULL,'135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (152,'{قد نرى تقلب وجهك} كانت الكعبة أحبَّ القبلتين إلى رسول الله ورأى أنَّ الصَّلاة إليها أدعى لقومه إلى الإسلام فقال لجبريل عليه السَّلام: وددتُ أنَّ الله صرفني عن قِبلة اليهود إلى غيرها فقال جبريل عليه السَّلام: إنَّما أنا عبدٌ مثلك وأنت كريم على ربِّك فسله ثمَّ ارتفع جبريل عليه السَّلام وجعل رسول الله صلى الله عليه وسلم يُديم النَّظر إلى السَّماء رجاء أَنْ يأتيه جبريل عليه السَّلام بالذي سأل فأنزل الله تعالى: {قَدْ نَرَى تَقَلُّبَ وَجْهِكَ فِي السَّمَاءِ} أَيْ: فِي النَّظر إلى السَّماء {فَلَنُوَلِّيَنَّكَ} فلنُصَيِّرَنَّك تستقبل {قبلة ترضاها} تحبُّها وتواها {فَوَلِّ وجهك} أَيْ: أَقبل بوجهك {شطر المسجد الحرام} نحوه وتلقاءه {وَحَيْثُ مَا كُنْتُمْ} فِي برٍّ أو بحرٍ وأردتم الصَّلاة {فولوا وجوهكم شطره} فلمَّا تحوَّلت القِبلة إِلى الكعبة قالت اليهود: يا محمد ما أُمرتَ بهذا وإنَّما هو شيءٌ تبتدعه من تلقاء نفسك فأنزل الله تعالى: {وَإِنَّ الَّذِينَ أُوتُوا الْكِتَابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ} أنَّ المسجد الحرام قِبلة إبراهيم وأنَّه لحقٌّ {وما اللَّهُ بغافل عما تعملون} يا معشر المؤمنين مِنْ طلب مرضاتي',NULL,'136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (153,'{ولئن أتيت الذين أوتوا الكتاب} يعني: اليهود والنَّصارى {بكلِّ آية} (دلالةٍ ومعجزةٍ) {ما تبعوا قبلتك} لأنَّهم مُعاندون جاحدون نبوَّتك مع العلم بها {وَمَا أَنْتَ بِتَابِعٍ قِبْلَتَهُمْ} حسمَ بهذا أطماع اليهود في رجوع النبي صلى الله عليه وسلم إلى قبلتهم لأنَّهم كانوا يطمعون في ذلك {وما بعضهم بِتَابِعٍ قِبْلَةَ بَعْضٍ} أخبر أنَّهم - وإنِ اتَّفقوا في التَّظاهر على النَّبيِّ صلى الله عليه وسلم - مُختلفون فيما بينهم فلا اليهود تتبع قِبلة النَّصارى ولا النَّصارى تتبع قِبلة اليهود {وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُمْ} أَيْ: صلَّيت إلى قِبلتهم {بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ} أنَّ قِبلة الله الكعبة {إِنَّكَ إِذًا لَمِنَ الظَّالِمِينَ} أيْ: إِنَّك إذاً مثلهم والخطاب للنبي صلى الله عليه وسلم فِي الظَّاهر وهو فِي المعنى لأُمَّته',NULL,'137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (154,'{الذين آتيناهم الكتاب يعرفونه} يعرفون محمدا صلى الله عليه وسلم بنعته وصفته {كَمَا يَعْرِفُونَ أَبْنَاءَهُمْ وَإِنَّ فَرِيقًا مِنْهُمْ لَيَكْتُمُونَ الحق} من صفته في التَّوراة {وهم يعلمون} لأنَّ الله بيَّن ذلك في كتابهم',NULL,'137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (155,'{الْحَقُّ مِنْ رَبِّكَ} أي: هذا الْحَقُّ مِنْ رَبِّكَ {فَلا تَكُونَنَّ مِنَ الْمُمْتَرِينَ} الشَّاكِّين في الجملة التي أخبرتك بها من أمر القِبلة وعناد اليهود وامتناعهم عن الإِيمان بك',NULL,'138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (156,'{ولكلٍّ} أَيْ: ولكلِّ أهل دينٍ {وجهةٌ} قِبلةٌ ومُتوجَّةٌ إليها في الصَّلاة {هو مُوَلِّيْها} وجهَه أَيْ: مستقبلها {فاستبقوا الخيرات} فبادروا إلى القبول من الله عزَّ وجل ووَلُّوا وجوهكم حيث أمركم الله تعالى {أَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللَّهُ جَمِيعًا} يجمعكم الله تعالى للحساب فيجزيكم بأعمالكم ثم أَكَّد استقبال القبلة أينما كان بآيتين وهما قوله تعالى:',NULL,'138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (157,'{وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ وَإِنَّهُ لَلْحَقُّ مِنْ رَبِّكَ وَمَا اللَّهُ بغافل عما تعملون}',NULL,'138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (158,'{ومن حيث خرجت} الآية وقوله: {وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ لئلا يكون للناس عليكم حجَّةٌ} يعني: اليهود وذلك أنَّ اليهود كانوا يقولون: ما درى محمَّدٌ أين قِبلته حتى هديناه ويقولون: يخالفنا محمَّدٌ فِي ديننا ويتَّبِع قِبلتنا فهذا كان حجِّتهم التي كانوا يحتجُّون بها تمويهاً على الجُهَّال فلمَّا صُرفت القِبلة إلى الكعبة بطلت هذه الحجَّة ثمَّ قال تعالى: {إلاَّ الذين ظلموا منهم} من النَّاس وهم المشركون فإنَّهم قالوا: توجَّه محمدٌ إلى قِبلتنا وعلم أنَّا أهدى سبيلاً منه فهؤلاء يحتجُّون بالباطلِ ثمَّ قال: {فلا تخشوهم} يعني: المشركين في تظاهرهم عليكم فِي المُحاجَّة والمحاربة {واخشوني} فِي ترك القِبلة ومخالفتها {ولأُتمَّ نعمتي عليكم} أَيْ: ولكي أَتمَّ - عطفٌ على {لئلا يكون} - نِعْمَتِي عَلَيْكُمْ بهدايتي إيّاكم إلى قِبلة إبراهيم فَتَتِمُّ لكم الملَّة الحنيفيَّة {ولعلكم تهتدون} ولكي تهتدوا إلى قِبلة إبراهيم',NULL,'138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (159,'{كما أرسلنا فيكم} المعنى: ولأتمَّ نعمتي عليكم كإرسالي إليكم رسولاً أَيْ: أتمُّ هذه كما أتممت تلك بإرسالي {رسولاً منكم} تعرفون صدقه ونسبه {يتلو عليكم آياتنا} يعني: القرآن وهذا احتجاجٌ عليهم لأنَّهم عرفوا أنَّه أميٌّ لا يقرأ ولا يكتب فلمَّا قرأ عليهم تبيَّن لهم صدقه في النُّبوَّة {ويزكيكم} أَيْ: يُعرِّضكم لما تكونوا به أزكياء من الأمر بطاعة الله تعالى',NULL,'139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (160,'{فاذكروني} بالطَّاعة {أذكركم} بالمغفرة {واشكروا لي} نعمتي {ولا تكفرون} أَيْ: لا تكفروا نعمتي',NULL,'139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (161,'{يا أيها الذين آمنوا استعينوا} على طلب الآخرة {بالصبر} على الفرائض {والصلاة} وبالصَّلوات الخمس على تمحيص الذُّنوب {إِنَّ اللَّهَ مَعَ الصَّابِرِينَ} أَيْ: إنِّي معكم أنصركم ولا أخذلكم',NULL,'139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (162,'{وَلا تَقُولُوا لِمَنْ يُقْتَلُ فِي سَبِيلِ اللَّهِ أموات} نزلت في قتلى بدر من المسلمين وذلك أنَّهم كانوا يقولون لمَنْ يُقتل فِي سبيل الله: مات فلانٌ وذهب عنه نعيم الدُّنيا فقال الله تعالى: ولا تقولوا للمقتولين في سبيلي هم أمواتٌ {بل} هم {أحياء} لأنَّ أرواح الشُّهداء فِي أجواف طيرٍ خضرٍ تسرح في الجنَّة {ولكن لا تشعرون} بما هم فيه من النَّعيم والكرامة',NULL,'139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (163,'{ولنبلونكم} ولنعاملنَّكم مُعاملة المبتلي {بشيء من الخوف} يعني: خوف العدوِّ {والجوع} يعني: القحط {وَنَقْصٍ مِنَ الأَمْوَالِ} يعني: الخسران والنُّقصان فِي المال وهلاك المواشي {والأنفس} يعني: الموت والقتل في الجهاد والمرض والشَّيب {والثمرات} يعني: الجوائح وموت الأولاد فمَنْ صبر على هذه الأشياء استحقَّ الثَّواب ومَنْ لم يصبر لم يستحق يدلُّ على هذا قوله تعالى: {وبشر الصابرين}',NULL,'140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (164,'{الذين إذا أصابتهم مصيبة} ممَّا ذُكر {قالوا إنا لله وإنا إليه راجعون} أَيْ: أموالنا لله ونحن عبيدة يصنع بنا ما يشاء ثمَّ وعدهم على هذا القول المغفرة',NULL,'140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (165,'{أولئك عليهم صلوات من ربهم} أَيْ: مغفرةٌ {ورحمة} ونعمةٌ {وأولئك هم المهتدون} إلى الجنَّة والثَّواب والحقِّ والصَّواب وقيل: زيادة الهدى وقيل: هم المنتفعون بالهداية',NULL,'140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (166,'{إنَّ الصفا والمروة} وهما جبلان معروفان بمكَّة {من شعائر الله} أَيْ: مُتعبَّداته {فمن حجَّ البيت} زاره معظِّماً له {أو اعتمر} قصد البيت للزِّيارة {فلا جناح عليه} فلا إثم عليه {إن يطوَّف بهما} بالجبلين وذلك أنَّ أهل الجاهليَّة كانوا يطوفون بينهما وعليهما صنمان يمسحونهما فكره المسلمون الطَّواف بينهما فأنزل الله تعالى هذه الآية {ومن تطوَّع خيراً} فعل غير المفترض عليه من طوافٍ وصلاةٍ وزكاةٍ وطاعةٍ {فإنَّ الله شاكر} مجازٍ له بعمله {عليم} بنيَّته',NULL,'140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (167,'{إنَّ الذين يكتمون ما أنزلنا} يعني: علماء اليهود {من البينات} من الرَّجم والحدود والأحكام {والهدى} أمر محمد صلى الله عليه وسلم ونعته {من بعد ما بيناه للناس} لبني إسرائيل {فِي الكتاب} فِي التَّوراة {أولئك يلعنهم الله ويلعنهم اللاعنون} كلُّ شيءٍ إلاَّ الجنَّ والإِنس',NULL,'141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (168,'{إلاَّ الذين تابوا} رجعوا من بعد الكتمان {وأصلحوا} السَّريرة {وبيَّنوا} صفة محمَّدٍ صلى الله عليه وسلم {فأولئك أتوب عليهم} أعود عليهم بالمغفرة',NULL,'141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (169,'{إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ أُولَئِكَ عليهم لعنة الله والملائكة والناس أجمعين} يعني: المؤمنين',NULL,'141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (170,'{خَالِدِينَ فِيهَا لا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلا هم ينظرون} أَيْ: ولا هم يُمهلون للرَّجعة والتَّوبة والمعذرة إذ قد زال التَّكليف',NULL,'141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (171,'{وإلهكم إله واحدٌ} كان للمشركين ثلاثمائة وستون صنما يعبدونها من دون الله سبحانه وتعالى فبيَّن الله سبحانه أنَّه إِلههم وأّنَّه واحدٌ فقال: {وإلهكم إله واحدٌ} أَيْ: ليس له في الإِلهيَّة شريكٌ ولا له في ذاته نظيرٌ {لا إله إلا هو الرحمن الرحيم} كذَّبهم الله عز وجل في إشراكهم معه آلهةً فعجب المشركون من ذلك وقالوا: إنَّ محمداً يقول: {وَإِلَهُكُمْ إِلَهٌ وَاحِدٌ} فلأتنا بآيةٍ إن كان من الصَّادقين فأنزل الله تعالى:',NULL,'142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (172,'{إن في خلق السماوات والأرض} مع عظمهما وكثرة أجزائهما {واختلاف الليل والنهار} ذهابهما ومجيئهما {والفلك} السُّفن {الَّتِي تَجْرِي فِي الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ} من التِّجارات {وَمَا أَنْزَلَ اللَّهُ مِنَ السَّمَاءِ مِنْ مَاءٍ} من مطرٍ {فأحيا به الأرض} أخصبها بعد جدوبتها {وبثَّ} وفرَّق {فيها من كلِّ دابة وتصريف الرياح} تقليبها مرَّة جنوباً ومرَّة شمالاً وباردةً وحارَّة {والسحاب المسخَّر} المُذلَّل لأمر الله {بين السماء والأرض لآياتٍ} لدلالاتٍ على وحدانية الله {لقوم يعقلون} فعلمهم الله عز وجل بهذه الآية كيفية الاستدلال على الصَّانع وعلى توحيده وردَّهم إلى التًّفكُّر فِي آياته والنَّظر فِي مصنوعاته ثمَّ أعلم أنَّ قوماً بعد هذه الآيات والبيِّنات يتَّخذون الأنداد مع علمهم أنَّهم لا يأتون بشيءٍ مما ذكر فقال:',NULL,'142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (173,'{وَمِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللَّهِ أنداداً} يعني: الأصنام التي هي أندادٌ بعضها لبعضٍ أَيْ: امثال {يحبونهم كحب الله} أي: كحبِّ المؤمنين الله {والذين آمنوا أشد حباً لله} لأنَّ الكافر يُعرِضُ عن معبوده في وقت البلاء والمؤمن لا يُعرض عن الله في السِّراء والضَّراء والشِّدَّة والرَّخاء {ولو يرى الذين ظلموا} كفروا {إذ يرون العذاب} شدَّة عذاب الله تعالى وقوّته لعلموا مضرَّة اتِّخاذ الأنداد وجواب (لو) محذوفٌ وهو ما ذكرنا',NULL,'143',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (174,'{إذ تبرَّأ الذين اتُّبعوا} هذه الآية تتصل بما قبلها لأنَّ المعنى: وإنَّ الله شديد العذاب حين تبرَّأ المُتَّبَعُون في الشِّرك من أتباعهم عند رؤية العذاب يقولون: لم ندعُكم إلى الضَّلالة وإلى ما كنتم {وتقطعت بهم} عنهم {الأسباب} الوصلات التي كانت بينهم في الدُّنيا من الأرحام والموَّدة وصارت مُخالَّتهم عداوةً',NULL,'143',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (175,'{وَقَالَ الَّذِينَ اتَّبَعُوا} وهم الأتباع {لَوْ أَنَّ لنا كرة} وجعة إلى الدُّنيا تبرَّأنا منهم {كما تبرَّؤوا منا كذلك} أي: كتبرئ بعضهم من بعضٍ {يُرِيهِمُ اللَّهُ أَعْمَالَهُمْ حَسَرَاتٍ عليهم} يعني: عبادتهم الأوثان رجاءَ أن تُقرِّبهم إلى الله تعالى فلمَّا عُذِّبوا على ما كانوا يرجون ثوابه تحسَّروا',NULL,'143',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (176,'{يا أيها النَّاسُ كُلُوا مِمَّا فِي الأَرْضِ حَلالا طَيِّبًا} نزلت هذه الآية فِي الذين حرَّموا على أنفسهم السَّوائب والوصائل والبحائر فأَعلمَ الله سبحانه أنَّها يَحلُّ أكْلُها وأنَّ تحريمها من عمل الشَّيطان فقال: {ولا تتبعوا خطوات الشيطان} أَيْ: سُبله وطرقه ثمَّ بيَّن عداوة الشَّيطان فقال:',NULL,'143',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (177,'{إنما يأمركم بالسوء} بالمعاصي {والفحشاء} البخل وقيل: كلُّ ذنبٍ فيه حدٌّ {وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لا تَعْلَمُونَ} من تحريم الأنعام والحرث',NULL,'144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (178,'{وإذا قيل لهم} أي: لهؤلاء الذين حرَّموا من الحرث والأنعام أشياء: {اتَّبِعُوا مَا أَنْزَلَ اللَّهُ قَالُوا بَلْ نَتَّبِعُ ما ألفينا} ما وجدنا {عليه آباءنا} فقال الله تعالى مُنكراً عليهم: {أو لو كَانَ آبَاؤُهُمْ لا يَعْقِلُونَ شَيْئًا وَلا يَهْتَدُونَ} يتَّبعونهم؟ والمعنى: أيتبعون آباءهم وإنْ كانوا جهَّالاً؟ ! ثمَّ ضرب للكفَّار مثلاً فقال:',NULL,'144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (179,'{ومثل الذين كفروا} فِي وعظهم ودعائهم إلى الله عز وجل {كمثل} الرَّاعي {الذي ينعق} يصيح بالغنم وهي لا تعقل شيئاً ومعنى يَنْعِق: يصيح وأراد {بما لا يسمع إلاَّ دعاءً ونداءً} البهائم التي لا تعقل ولا تفهم ما يقول الرَّاعي إنَّما تسمع صوتاً لا تدري ما تحته كذلك الذين كفروا يسمعون كلام النبي صلى الله عليه وسلم وهم كالغنم إذ كانوا لا يستعملون ما أمره به ومضى تفسير قوله: {صم بكم عمي} ثمَّ ذكر أنَّ ما حرَّمه المشركون حلال فقال:',NULL,'144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (180,'{يا أيها الَّذِينَ آمَنُوا كُلُوا مِنْ طَيِّبَاتِ مَا رَزَقْنَاكُمْ} أَيْ: حلالات ما رزقناكم من الحرث والنَّعم وما حرَّمه المشركون على أنفسهم منهما {واشكروا لله إنْ كنتم إياه تعبدون} أَيْ: إنْ كانت العبادة لله واجبةً عليكم بأنَّه إلهكم فالشُّكر له واجبٌ بأنه منعمٌ عليكم ثمَّ بيَّن المُحرَّم ما هو فقال:',NULL,'144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (181,'{إنما حرَّم عليكم الميتة} وهي كلُّ ما فارقه الرُّوح من غير ذكاةٍ ممَّا يذبح {والدم} يعني: الدَّم السَّائل لقوله في كوضع آخر: {أو دماً مسفوحاً} وقد دخل هذين الجنسين الخصوصُ بالسُّنَّةِ وهو [قوله صلى الله عليه وسلم: أُحلَّت لنا ميتتان ودمان] وقوله تعالى: {ولحم الخنزير} يعني: الخنزير بجميع أجزائه وخصَّ اللَّحم لأنَّه المقصود بالأكل {وما أُهِلَّ به لغير الله} يعني: ما ذُبح للأصنام فذكر عليه غير اسم الله تعالى {فمن اضطر} أَيْ: أُحوج وألجئ في حال الضَّرورة وقيل: مَنْ أكره على تناوله وأُجبر على تناوله كما يُجبر على التَّلفُّظ بالباطل {غير باغٍ} أَيْ: غير قاطعٍ للطَّريق مفارقٍ للأئمة مُشاقًّ للأمَّة {ولا عادٍ} ولا ظالم متعدٍّ فأكلَ {فلا إثم عليه} وهذا يدلُّ على أنَّ العاصي بسفره لا يستبيح أكل الميتة عند الضَّرورة {إنَّ الله غفور} للمعصية فلا يأخذ بما جعل فِيهِ الرُّخصة {رحيمٌ} حيث رخَّص للمضطر',NULL,'145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (182,'{إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنْزَلَ اللَّهُ من الكتاب} يعني: رؤساء اليهود {ويشترون به} بما أنزل الله من نعت محمد صلى الله عليه وسلم في كتابهم {ثمناً قليلاً} يعني: ما يأخذون من الرُّشى على كتمان نعته {أُولَئِكَ مَا يَأْكُلُونَ فِي بُطُونِهِمْ إِلا النَّارَ} إلاَّ ما هو عاقبته النَّار {ولا يكلمهم الله يوم القيامة} أَيْ: كلاماً يسرُّهم {ولا يزكيهم} ولا يُطهِّرهم من دنس ذنوبهم',NULL,'145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (183,'{أولئك الذين اشتروا الضلالة} استبدلوها {بالهدى والعذاب بالمغفرة} حين جحدوا أمر محمَّدٍ صلى الله عليه وسلم وكتموا نعته {فما أصبرهم} أَيْ: فأيُّ شيءٍ صبَّرهم على النَّار ودعاهم إليها حين تركوا الحقَّ واتبعوا الباطل؟ ! وهذا استفهامٌ معناه التَّوبيخ لهم (وقيل: ما أجرأهم على النار)',NULL,'146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (184,'{ذلك} أَيْ: ذلك العذاب لهم {بِأَنَّ اللَّهَ نَزَّلَ الكتاب بالحق} يعني: القرآن فاختلفوا فيه {وإنَّ الذين اختلفوا في الكتاب} فقالوا: إنَّه رَجَزٌ وشِعرٌ وكهانةٌ وسحرٌ {لفي شقاق بعيد} لفي خلافٍ للحقِّ طويلٍ',NULL,'146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (185,'{ليس البر} كان الرَّجل فِي ابتداء الإِسلام إذا شهد الشَّهادتين وصلَّى إلى أَيٍّ ناحيةٍ كانت ثمَّ مات على ذلك وجبت له الجنَّة فلمَّا هاجر رسول الله صلى الله عليه وسلم ونزلت الفرائض وصُرفت القِبلة إلى الكعبة أنزل الله تعالى هذه الآية فقال: {ليس البر} كلَّه أن تُصلُّوا ولا تعملوا غير ذلك {ولكنَّ البرَّ} أَيْ: ذا الْبِرَّ {مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَالْمَلائِكَةِ والكتاب والنَّبيين وَآتَى الْمَالَ عَلَى حُبِّهِ} أَيْ: على حبِّ المال (وقيل: الضميرُ راجعٌ إلى الإِيتاء) {ذوي القربى} قيل: عنى به قرابة النبي صلى الله عليه وسلم (وقيل: أراد به قرابة الميت) {وابن السبيل} هو المنقطع يمرُّ بك والضَّيف ينزل بك {وفي الرِّقاب} أَيْ: وفي ثمنها يعني: المكاتبين {والموفون بعهدهم إذا عاهدوا} اللَّهَ أو النَّاسَ {والصابرين في البأساء} الفقر {والضراء} المرض {وحين البأس} وقت القتال في سبيل الله {أولئك} أهل هذه الصفة هم {الذين صدقوا} في إيمانهم',NULL,'146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (186,'{يا أيها الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلَى} نزلت في حَيَّينِ من العرب أحدهما أشرف من الآخر فقتل الأوضع من الأشرف قتلى فقال الأشرف: لنقتلنَّ الحرَّ بالعبد والذَّكر بالأنثى ولَنُضاعِفَنَّ الجراح فأنزل الله تعالى هذه الآية وقوله: {كُتب} : أُوجب وفُرض {عليكم القصاص} اعتبار المماثلة والتَّساوي بين القتلى حتى لا يجوز أن يقتل حرٌّ بعبدٍ أو مسلمٌ بكافرٍ فاعتبارُ المماثلةِ واجبٌ وهو قوله: {الحرُّ بالحرِّ والعبدُ بالعبدِ والأنثى بالأنثى} ودلَّ قوله في سورة المائدة: {أنَّ النَّفس بالنَّفس} على أنَّ الذَّكر يُقتل بالأنثى فيقتل الحرُّ بالحرَّة {فمن عفي له} أَيْ: تُرك له {من} دم {أخيه} المقتول {شيءٌ} وهو أن يعفو بعض الأولياء فيسقط القود {فاتباع بالمعروف} أَيْ: فعلى العافي الذي هو ولي الدَّم أن يتبع القاتل بالمعروف وهو أن يطالبه بالمال من غير تشدُّد وأذىً وعلى المطلوب منه المال {أداءٌ} تأدية المال إلى العافي {بإحسانٍ} وهو ترك المطل والتَّسويف {ذلك تخفيفٌ من ربكم ورحمة} هو أنَّ الله تعالى خَيَّرَ هذه الأمَّة بين القصاص والدية والعفو ولم يكن ذلك إلاَّ لهذه الأُمَّة {فمن اعتدى} أَيْ: ظلم بقتل القاتل بعد أخذ الدية {فَلَهُ عَذَابٌ أَلِيمٌ}',NULL,'147',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (187,'{ولكم في القصاص حياة} أَيْ: في إثباته حياةٌ وذلك أنَّ القاتل إذا قُتل ارتدع عن القتل كلُّ مَنْ يهمُّ بالقتل فكان القصاص سبباً لحياة الذي يُهَمُّ بقتله ولحياة الهامِّ أيضاً لأنه إنْ قَتلَ قُتل {يا أولي الألباب} يا ذوي العقول {لعلكم تتقون} إراقة الدِّماء مخافة القصاص',NULL,'148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (188,'{كتب عليكم} كان أهل الجاهليَّة يُوصون بمالهم للبعداء رياءً وسُمعةً ويتركون أقاربهم فقراء فأنزل الله تعالى هذه الآية {كتب عليكم} فُرض عليكم وأُوجب {إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ} أَيْ: أسبابه ومُقدِّماته {إنْ ترك خيراً} مالاً {الوصية للوالدين والأقربين بالمعروف} يعني: لا يزيد على الثلث {حقاً} أي: حقَّ ذلك حقَّاً {على المتقين} الذين يتَّقون الشِّرك وهذه الآية منسوخة بآية المواريث ولا تجب الوصية على أحدٍ (ولا تجوز الوصية للوارث)',NULL,'148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (189,'{فَمَنْ بدَّله بعد ما سمعه} أَيْ: بدَّل الإِيصاء وغيَّره من وصيٍّ ووليٍّ وشاهدٍ بعد ما سمعه عن الميت {فإنما إثمه} إثم التَّبديل {عَلَى الَّذِينَ يبدلونه} وبرئ الميِّت {إن الله سميع} سمع ما قاله المُوصي {عليم} بنيَّته وما أراد فكانت الأولياء والأوصياء يمضون وصيه الميت بعد نزول هذه الآية وإن استغرقت المال فأنزل الله تعالى:',NULL,'149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (190,'{فمن خاف} أَيْ: علم {من موصٍ جنفاً} خطأً في التوصية من غير عمدٍ وهو أن يُوصي لبعض ورثته أو يوصي بماله كلِّه خطأً {أو إثماً} أَيْ: قصدا للميل فخاف من الوصية وفعل ما لا يجوز مُتعمِّداً {فأصلح} بعد موته بين ورثته وبين المُوصى لهم {فلا إثم عليه} أَيْ: إِنَّه ليس بمبدلٍ يأثم بل هو متوسطٌ للإِصلاح وليس عليه إثمٌ',NULL,'149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (191,'{يا أيها الذين آمنوا كتب عليكم الصيام} يعني صيام شهر رمضان {كما كتب} يعني: كما أُوجب {على الذين من قبلكم} أَيْ: أنتم مُتَعَبِّدون بالصَّيام كما تُعبِّد مَنْ قبلكم {لعلكم تتقون} لكي تتقوا الأكل والشُّرب والجماع في وقت وجوب الصَّوم',NULL,'149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (192,'{أياماً معدودات} يعني: شهر رمضان {فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ عَلَى سَفَرٍ} فأفطر {فعدَّةٌ} أَيْ: فعليه عدَّةٌ أَيْ: صوم عدَّةٍ يعني: بعدد ما أفطر {مِنْ أَيَّامٍ أُخَرَ} سوى أيَّام مرضه وسفره {وعلى الذين يطيقونه فدية طعام مسكين} هذا كان في ابتداء الإسلام مَنْ أطاق الصوم جاز له أن يُفطر ويُطعم لكلِّ يومٍ مسكيناً مُدَّاً من طعام فَنُسِخ بقوله: {فمن شهد منكم الشهر فليصمه} {فمن تطوع خيراً} زاد في الفدية على مُدٍّ واحدٍ {فَهُوَ خَيْرٌ لَهُ وَأَنْ تَصُومُوا خَيْرٌ لَكُمْ} أيْ: والصَّوم خيرٌ لكم من الإِفطار والفدية وهذا (إنَّما) كان قبل النَّسخ',NULL,'150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (193,'{شهر رمضان} أَيْ: هي شهر رمضان يعني: تلك الأيام المعدودات شهر رمضان {الذي أُنزل فيه القرآن} أنزل جُملةً واحدةً من اللَّوح المحفوظ في ليلة القدر من شهر رمضان فوضع في بيت العزَّة في سماء الدُّنيا ثمَّ نزل به جبريل عليه السَّلام على محمد صلى الله عليه وسلم نجوماً نجوماً عشرين سنةً {هدىً للناس} هادياً للنَّاس {وبينات من الهدى} وآياتٍ واضحاتٍ من الحلال والحرام والحدود والأحكام {والفرقان} الفرق بين الحقِّ والباطل {فمن شهد منكم الشهر} فمَنْ حضر منكم بلده في الشَّهْرَ {فَلْيَصُمْهُ} {وَمَنْ كَانَ مَرِيضًا أَوْ عَلَى سفرٍ فعدَّةٌ من أيام أخر} أعاد هاهنا تخيير المريض والمسافر لأنَّ الآية الأولى وردت في التَّخيير للمريض والمسافر والمقيم وفي هذه الآية نُسخ تخيير المقيم فأُعيد ذكر تخيير المريض والمسافر ليعلم أنَّه باقٍ على ما كان {يريد الله بكم اليسر} بالرُّخصة للمسافر والمريض {وَلا يُرِيدُ بِكُمُ الْعُسْرَ} لأنَّه لم يشدِّد ولم يُضيِّق عليكم {ولتكملوا} (عطف على محذوف) والمعنى: يُرِيدُ اللَّهُ بِكُمُ الْيُسْرَ وَلا يُرِيدُ بِكُمُ العسر لِيَسْهُلَ عليكم {ولتكملوا العدَّة} أَيْ: ولتكملوا عدَّة ما أفطرتم بالقضاء إذا أقمتم وبرأتم {ولتكبروا الله} يعني التَّكبير ليلة الفطر إذا رُئي هلال شوال {على ما هداكم} أرشدكم من شرائع الدِّين',NULL,'150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (194,'{وإذا سألك عبادي عني} الآية سأل بعض الصَّحابة النبي صَلَّى اللَّهُ عَلَيْهِ وسلم: أقريبٌ ربُّنا فنناجيَه أم بعيدٌ فنناديَه؟ فأنزل الله تعالى هذه الآية وقوله تعالى {فإني قريبٌ} يعني: قربه بالعلم {أجيب} أسمع {دعوة الداع إذا دعان فليستجيبوا لي} أَيْ: فليجيبوني بالطَّاعة وتصديق الرُّسل {وليؤمنوا بي لعلهم يرشدون} ليكونوا على رجاءٍ من إصابة الرُّشد',NULL,'151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (195,'{أحلَّ لكم ليلة الصيام} الآية كان في ابتداء الإسلام لا تحلُّ المجامعة في ليالي الصَّوم ولا الأكل ولا الشُّرب بعد العشاء الآخرة فأحلَّ الله تعالى ذلك كلَّه إلى طلوع الفجر وقوله: {الرفث إلى نسائكم} يعني: الإِفضاء إليهنَّ بالجماع {هنَّ لباسٌ لكم} أَيْ: فراشٌ {وأنتم لباس} لحافٌ {لهنَّ} عند الجماع {علم الله أنكم كنتم تختانون أنفسكم} تخونون أنفسكم بالجماع ليالي رمضان وذلك أَنَّ عُمَرَ بْنَ الْخَطَّابِ رَضِيَ اللَّهُ عَنْهُ وغيره فعلوا ذلك ثمَّ أتوا رسول الله صلى الله عليه وسلم يسألونه فنزلت الرُّخصة {فتاب عليكم} فعاد عليكم بالترخيص {وعفا عنكم} ما فعلتم قبل الرُّخصة {فالآن باشروهنَّ} جامعوهنَّ {وابتغوا} واطلبوا {ما كتب الله لكم} ما قضى الله سبحانه لكم من الولد {وكلوا واشربوا} اللَّيل كلَّه {حتى يتبين لكم الخيط الأبيض} يعني: بياض الصُّبح {من الخيط الأسود} من سواد اللَّيل {من الفجر} بيانُ أنَّ هذا الخيط الأبيض من الفجر لا من غيره {ثمَّ أتموا الصيام إلى الليل} بالامتناع من هذه الأشياء {ولا تباشروهنَّ وأنتم عاكفون في المساجد} نهيٌ للمعتكف عن الجماع لأنه يُفسده {تلك} أَيْ: هذه الأحكام التي ذكرها {حدود الله} ممنوعاته {فلا تقربوها} فلا تأتوها {كذلك} أَيْ: مثل هذا البيان {يبين الله آياته للناس لعلهم يتقون} المحارم',NULL,'152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (196,'{وَلا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ} أَيْ: لا يأكل بعضكم مال بعضٍ بما لا يحلُّ في الشَّرع من الخيانة والغضب والسَّرقة والقمار وغير ذلك {وتُدْلُوا بها إلى الحكام} ولا تصانعوا (أي: لاترشوا) بأموالكم الحكَّام لِتقتطعوا حقَّاً لغيركم {لتأكلوا فريقاً} طائفةً {من أموال الناس بالإِثم} بأن ترشوا الحاكم ليقضي لكم {وأنتم تعلمون} أنَّكم مُبطلون وأنَّه لا يحلُّ لكم والأصل في الإِدلاء: الإِرسال من قولهم: أدليتُ الدَّلو',NULL,'153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (197,'{يسألونك عن الأهلة} سأل معاذ بن جبلٍ رسولَ الله صلى الله عليه وسلم عن زيادة القمر ونقصانه فأنزل الله تعالى: {يسألونك عن الأهلة} وهي جمع هلال {قل هي مواقيت للناس والحج} أخبر الله عنه أنَّ الحكمة في زيادته ونقصانه زوال الالتباس عن أوقات النَّاس فِي حجِّهم ومَحِلِّ دُيونِهم وعِدَدِ نسائهم وأجور أُجرائهم ومُدَد حواملهم وغير ذلك {وَلَيْسَ الْبِرُّ بِأَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِهَا} كان الرَّجل في الجاهلية إذا إذا أحرم نقب من بيته نقباً من مؤخره يدخل فيه ويخرج فأمرهم الله بتر ك سنَّة الجاهليَّة وأعلمهم أنَّ ذلك ليس ببرٍّ {ولكن البرَّ} برُّ {من اتقى} مخالفةَ الله {وأتوا البيوت من أبوابها} الآية',NULL,'153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (198,'{وقاتلوا في سبيل الله} الآية نزلت هذه الآية في صلح الحديبية وذلك أن رسول الله صلى الله عليه وسلم لمَّا انصرف من الحديبية إلى المدينة المنورة حين صدَّه المشركون عن البيت صالحهم على أن يرجع عامة القابل ويُخَلُّوا له مكَّة ثلاثة أيَّام فلمَّا كان العام القابل تجهزَّ رسول الله صلى الله عليه وسلم وأصحابه لعمرة القضاء وخافوا أن لا تفي لهم قريشٌ وأن يصدُّوهم عن البيت ويقاتلوهم وكره أصحاب رسول الله صلى الله عليه وسلم قتالهم في الشَّهر الحرام في الحرم فأنزل الله تعالى: {وقاتلوا في سبيل الله} أَيْ: في دين الله وطاعته {الذين يقاتلونكم} يعني: قريشاً {ولا تعتدوا} ولا تظلموا فتبدؤوا في الحرم بالقتال',NULL,'154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (199,'{واقتلوهم حيث ثقفتموهم} وجدتموه وأخذتموهم {وأخرجوهم من حيث أخرجوكم} يعني: من مكَّة {والفتنة أشدّ من القتل} يعني: وشركُهم بالله تعالى أعظمُ من قتلكم إيَّاهم في الحرم {وَلا تُقَاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتَّى يُقَاتِلُوكُمْ فيه} نُهوا عن ابتدائهم بقتلٍ أو قتالٍ حتى يبتدىء المشركون {فإن قاتلوكم فاقتلوهم} أَيْ: إن ابتدؤوا بقتالكم عند المسجد الحرام فلكم القتال على سبيل المكافأة ثم بيَّن أنهم إن انتهوا أَيْ: كفُّوا عن الشِّرك والكفر والقتال وأسلموا {فإنَّ الله غفور رحيم} أَيْ: يغفر لهم كفرهم وقتالهم من قبل وهو منعمٌ عليهم بقبول توبتهم وإيمانهم بعد كفرهم وقتالهم',NULL,'154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (200,'قال تعالى {فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ}',NULL,'155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (201,'{وَقَاتِلُوهُمْ حَتَّى لا تَكُونَ فِتْنَةٌ} أَيْ: شركٌ يعني: قاتلوهم حتى يُسلموا وليس يُقبل من المشرك الوثنيِّ جزيةٌ {ويكون الدين} أَيْ: الطَّاعة والعبادة {لله} وحده فلا يُعبد دونه شيءٌ {فإن انتهوا} عن الكفر {فلا عدوان} أَيْ: فلا قتل ولا نهب {إلاَّ على الظالمين} والكافرين',NULL,'155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (202,'{الشهر الحرام بالشهر الحرام} أَيْ: إن قاتلوكم في الشَّهر الحرام فقاتلوهم في مثله {والحرمات قصاص} أَي: إن انتهكوا لكم حرمةً فانتهكوا منهم مثل ذلك أَعلمَ الله سبحانه أنَّه لا يكون للمسلمين أنْ ينتهكوها على سبيل الابتداء ولكن على سبيل القصاص وهو معنى قوله: {فمن اعتدى عليكم} الآية',NULL,'155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (203,'{وأنفقوا في سبيل الله} في طاعة الله تعالى من الجهاد وغيره {ولا تلقوا بأيديكم إلى التهلكة} ولا تُمسكوا عن الإِنفاق في الجهاد {وأحسنوا} أَيْ: الظنَّ بالله تعالى في الثَّواب والإِخلاف عليكم',NULL,'155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (204,'{وأتموا الحج والعمرة لله} بمناسكها وحدودها وسننها وتأدية كلِّ ما فيهما {فإن أحصرتم} حُبستم ومُنعتم دون تمامهما {فما استيسر} فواجبٌ عليكم ما تيسَّر {من الهدي} وهو ما يُهدى إلى بيت الله سبحانه أعلاه بدنةٌ وأوسطه بقرة وأدناه شاةٌ فعليه ما تيسَّر من هذه الأجناس {ولا تحلقوا رؤوسكم} أَيْ: لا تَحِلُّوا من إحرامكم {حتى يبلغ الهدي محلَّه} حتى يُنحر الهدي بمكَّة في بعض الأقوال وهو مذهب أهل العراق وفي قول غيرهم: مَحِلُّه حيث يَحِلُّ ذبحه ونحره وهو حيث أُحصر وهو مذهب الشَّافعي {فَمَنْ كَانَ مِنْكُمْ مَرِيضًا أَوْ بِهِ أَذًى من رأسه} يعني الهوام تقع في الشَّعر وتكثر فحلق {فَفِدْيَةٌ مِنْ صِيَامٍ} وهو صيام ثلاثة أيَّام {أو صدقة} وهي إطعام ستة مساكين لكلِّ مسكينٍ مُدَّان {أو نسك} ذبيحةٍ {فإذا أمنتم} أَيْ: من العدوِّ أو كان حجٌّ ليس فيه خوفٌ من عدوٍّ {فمن تمتع بالعمرة إلى الحج} أيْ: قدم مكَّة مُحرماً واعتمر في أشهر الحجِّ وأقام حلالا بمكة حتى ينشئ منها الحجَّ عامَه ذلك واستمتع بمحظورات الإحرام لأنَّه حلَّ بالعمرة فمن فعل هذا {ف} عليه {ما استيسر من الهدي فمن لم يجد} ثمن الهدي {فصيام ثلاثة أيام في} أشهر {الحج وسبعة إذا رجعتم} أَيْ: بعد الفراغ من الحجِّ {تلك عشرة كاملة ذَلِكَ} أَيْ: ذلك الفرض الذي أُمرنا به من الهدي أو الصِّيام {لِمَنْ لَمْ يَكُنْ أَهْلُهُ حَاضِرِي الْمَسْجِدِ الْحَرَامِ} أَيْ: لمَنْ لم يكن من أهل مكَّة',NULL,'156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (205,'{الحج أشهر} أًيْ: أشهرُ الحجِّ أشهرٌ {معلوماتٌ} مُوقَّتةٌ معيَّنةٌ وهي شوال وذو القعدة وتسعُ من ذي الحجَّة {فمن فرض} أوجب على نفسه {فيهنَّ الحجَّ} بالإحرام والتَّلبية {فلا رفث} فلا جِماعَ {ولا فسوق} ولا معاصي {ولا جدال} وهو أَنْ يُجادلَ صاحبه حتى يُغضبه والمعنى: لا ترفثوا ولا تفسقوا ولا تجادلوا {فِي الْحَجِّ وَمَا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ الله} أَيْ: يُجازيكم به الله العالم {وتزوَّدوا} نزلت في قومٍ كانوا يحجُّون بلا زادٍ ويقولون: نحن متوكِّلون ثمَّ كانوا يسألون النَّاس وربَّما ظلموهم وغصبوهم فأمرهم الله أَنْ يتزوَّدوا فقال {وتزوَّدوا} ما تتبلَّغون به {فَإِنَّ خَيْرَ الزَّادِ التَّقْوَى} يعني: ما تكفُّون به وجوهكم عن السُّؤال وأنفسكم عن الظلم',NULL,'157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (206,'{وليس عليكم جناح} كان قومٌ يزعمون أنَّه لا حَجَّ لتاجرٍ ولا جَمَّالٍ فأعلمَ اللَّهُ تعالى أنه لا حرج في ابتغاء الرِّزق بقوله: {لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَبْتَغُوا فَضْلا مِنْ ربكم} أَيْ: رزقاً بالتجِّارة في الحجِّ {فإذا أفضتم} أَيْ: دفعتم وانصرفتم من {من عرفات فَاذْكُرُوا اللَّهَ} بالدُّعاء والتَّلبية {عِنْدَ الْمَشْعَرِ الْحَرَامِ واذكروه كما هداكم} أَيْ: ذكراً مثلَ هدايته إيَّاكم أَيْ: يكون جزاءً لهدايته إيَّاكم {وإن كنتم من قبله} أَيْ: وما كنتم من قبل هُدَاه إلاَّ ضالِّين',NULL,'157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (207,'{ثمَّ أفيضوا من حيث أفاض الناس} يعني: العرب وعامِّة النَّاس إلاَّ قريشاً وذلك أنَّهم كانوا لا يقفون بعرفات وإنَّما يقفون بالمزدلفة ويقولون نحن أهل حرم الله فلا نخرج منه فأمرهم الله أن يقفوا بعرفاتٍ كما يقف سائر النَّاس حتى تكون الإفاضة معهم منها {فإذا قضيتم مناسككم} أَيْ: فرغتم من عبادتكم التي أمرتم بها في الحجِّ {فَاذْكُرُوا اللَّهَ كذكركم آباءَكم} كانت العرب إذا فرغوا من حجِّهم ذكروا مفاخر آبائهم فأمرهم الله عز وجل بذكره {أو أشدَّ ذكراً} يعني: وأشدَّ ذكراً {فمن الناس} وهم المشركون كانوا يسألون المال والإبل والغنم ولا يسألون حظَّاً في الآخرة لأَنهم لم يكونوا مؤمنين بها والمسلمون يسألون الحظَّ في الدُّنيا والآخرة وهو قوله:',NULL,'157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (208,'قال تعالى {فَإِذَا قَضَيْتُمْ مَنَاسِكَكُمْ فَاذْكُرُوا اللَّهَ كَذِكْرِكُمْ آبَاءَكُمْ أَوْ أَشَدَّ ذِكْرًا فَمِنَ النَّاسِ مَنْ يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الآخِرَةِ مِنْ خَلاقٍ}',NULL,'158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (209,'{وَمِنْهُمْ مَنْ يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا حسنة} ومعنى {في الدنيا حسنة} : العمل بما يرضي الله {وفي الآخرة حسنة} : الجنة',NULL,'158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (210,'{أولئك لهم نصيبٌ مما كسبوا} أَيْ: ثوابُ ما عملوا {والله سريعُ الحساب} مع هؤلاء لأنَّه يغفر سيئاتهم ويضاعف حسناتهم',NULL,'158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (211,'{واذكروا الله في أيام معدودات} يعني: التَّكبير أدبار الصَّلوات في أيام التَّشريق {فمن تعجَّل في يومين} من أيام التَّشريق فنفر في اليوم الثّاني من مِنىً {فلا إثم عليه} في تعجُّله {ومن تأخر} عن النَّفر إلى اليوم الثالث {فلا إثم عليه} في تأخُّره {لمن اتقى} أَيْ: طرحُ المأثم يكون لمن اتَّقى في حجِّه تضييعَ شيءٍ ممَّا حدَّه الله تعالى',NULL,'158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (212,'{ومن الناس مَن يعجبك قوله} يعني: الأخنس بن شريق وكان منافقاً حلو الكلام حسن العلانية سيئ السَّريرة وقوله {في الحياة الدنيا} لأنَّ قوله إنَّما يعجب النَّاس في الحياة الدُّنيا ولا ثواب له عليه في الآخرة {ويشهد الله على ما في قلبه} لأنَّه كان يقول للنبي صلى الله عليه وسلم: واللَّهِ إنِّي بك لمؤمنٌ ولك محبٌّ {وهو ألدُّ الخصام} أَيْ: شديد الخصومة وكان جَدِلاً بالباطل',NULL,'159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (213,'{وإذا تولى سعى في الأرض} الآية وذلك أنَّه رجع إلى مكَّة فمرَّ بزرعٍ وحُمُرٍ للمسلمين فأحرق الزَّرع وعقر الحُمُر فهو قوله: {ويهلك الحرث والنسل} أَيْ: نسل الدَّوابِّ',NULL,'159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (214,'{وإذا قيل له اتق الله} وإذا قيل له: مهلاً مهلاً {أخذته العزَّةُ بالإِثم} حملته الأنفة وحميَّة الجاهليَّة على الفعل بالإِثم {فحسبه جهنم} كافيه الجحيم جزاءً له {ولبئس المهاد} ولبئس المقرُّ جهنَّم',NULL,'159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (215,'{ومن الناس مَنْ يشري} أَيْ: يبيع {نفسه} يعني: يبذلها لأوامر الله تعالى {ابتغاء مرضاة الله} لطلب رضا الله نزلت في صهيب الرُّوميِّ',NULL,'160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (216,'{يا أيها الذين آمنوا ادخلوا في السِّلْمِ} أَيْ: في الإسلام {كافة} أيْ: جميعاً أيْ: في جميع شرائعه نزلت في عبد الله بن سلام وأصحابه وذلك أنَّهم بعدما دخلوا في الإِسلام عظَّموا السَّبت وكرهوا لُحمان الإِبل فأُمروا بترك ذلك وإنَّه ليس من شرائع الإِسلام تحريم السَّبت وكراهة لحوم الإبل {ولا تتبعوا خطوات الشيطان} أيْ: آثاره ونزغاته {إنه لكم عدوٌّ مبين}',NULL,'160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (217,'{فإن زللتم} تنحَّيتم عن القصد بتحريم السَّبْت ولحوم الإِبل {مِنْ بَعْدِ مَا جَاءَتْكُمُ البينات} أَيْ: القرآن {فاعلموا أنَّ الله عزيز} في نقمته لاتعجزونه ولا يُعجزه شيءٌ {حكيم} فيما شرع لكم من دينه',NULL,'160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (218,'{هل ينظرون} أَيْ: هل ينتظرون يعني: التَّاركين الدُّخول في الإِسلام و (هل) استفهامٌ معناه النَّفي أيْ: ما ينتظر هؤلاء في الآخرة {إلاَّ أن يأتيهم} عذاب {اللَّهُ في ظلل من الغمام} والظُّلَل جمع: ظُلَّة وهي كلُّ ما أظلَّك والمعنى: إنَّ العذاب يأتي فيها ويكون أهول {والملائكة} أَيْ: الملائكةُ الذين وُكِّلوا بتعذيبهم {وقضي الأمر} فُرغ لهم ممَّا يوعدون بأنْ قُدِّر ذلك عليهم {وإلى الله ترجع الأمور} يعني: في الجزاء من الثَّواب والعقاب',NULL,'160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (219,'{سل بني إسرائيل} سؤال توبيخ وتبكيتٍ وتقريعٍ (كما يُقال: سله كم وعظته فلم يقبل) {كم آتيناهم من آية بينةً} من فلق البحر وإنجائهم من عدوِّهم وإنزال المنِّ والسًّلوى وغير ذلك {وَمَنْ يُبَدِّلْ نِعْمَةَ اللَّهِ مِنْ بَعْدِ مَا جاءته} يعني: ما أنعم الله به عليهم من العلم بشأن محمِّدٍ عليه السَّلام فبدَّلوه وغيَّروه',NULL,'161',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (220,'{زين للذين كفروا} أَيْ: رؤساء اليهود {الحياة الدُّنيا} فهي هِمَّتهم وطِلبتهم فهم لا يريدون غيرها {ويسخرون من الذين آمنوا} أَيْ: فقراء المهاجرين {وَالَّذِينَ اتَّقَوْا} الشِّرك وهم هؤلاء الفقراء {فَوْقَهُمْ يَوْمَ الْقِيَامَةِ} لأنَّهم في الجنَّة وهي عاليةٌ والكافرين في النَّار وهي هاويةٌ {وَاللَّهُ يَرْزُقُ مَنْ يشاء بغير حساب} يريد: إِنَّ أموال قريظة والنَّضير تصيرُ إليهم بلا حسابٍ ولا قتالٍ بل بأسهل شيءٍ وأيسره',NULL,'161',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (221,'{كان الناس} على عهد إبراهيم عليه السَّلام {أمة واحدة} كفاراً كلَّهم {فبعث الله النبيين} إبراهيم وغيره {وأنزل معهم الكتاب} والكتابُ اسم الجنس {بالحق} بالعدل والصِّدق {ليحكم بين الناس} أَيْ: الكتابُ {فِيمَا اخْتَلَفُوا فِيهِ وَمَا اخْتَلَفَ فِيهِ إِلا الَّذِينَ أُوتُوهُ مِنْ بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ بغياً} أَيْ: وما اختلفَ في أمر محمَّدٍ بعد وضوح الدّلالات لهم بغياً وحسداً إلاَّ اليهودُ الذين أوتوا الكتاب لأنَّ المشركين - وإن اختلفوا في أمر محمَّد عليه السَّلام - فإنَّهم لم يفعلوا ذلك للبغي والحسد ولم تأتهم البيِّنات في شأن محمد عليه السَّلام كما أتت اليهود فاليهود مخصوصون من هذا الوجه {فهدى الله الذين آمنوا} {ل} معرفة {ما اختلفوا فيه من الحق بإذنه} بعلمه وإرادته فيهم',NULL,'161',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (222,'{أم حسبتم أن تدخلوا الجنة} نزلت في فقراء المهاجرين حين اشتدَّ الضُّرُّ عليهم لأنَّهم خرجوا بلا مالٍ فقال الله لهم - أَيْ لهؤلاء المهاجرين -: أم حسبتم أن تدخلوا الجنَّة من غير بلاءٍ ولا مكروهٍ {ولما يأتكم} أَيْ: ولم يأتكم {مثل الذين خلوا} أَيْ: مثل محنة الذين مضوا {من قبلكم} أَيْ: ولم يُصبكم مثل الذي أصابهم فتصبروا كما صبروا {مَسَّتْهُم البأساء} الشدَّة {والضرَّاء} المرض والجوع {وزلزلوا} أَيْ: حُرِّكوا بأنواع البلاء {حَتَّى يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ مَتَى نصر الله} أَيْ: حين استبطؤوا النَّصر فقال الله: {أَلا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ} أَيْ: أنا ناصر أوليائي لا محالة',NULL,'162',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (223,'{يسألونك ماذا ينفقون} نزلت في عمرو بن الجموح وكان شيخاً كبيراً وعنده مالٌ عظيمٌ فسأل رسول الله صلى الله عليه وسلم: ماذا ننفق من أموالنا؟ وأين نضعُها؟ فنزلت هذه الآية قال كثيرٌ من المفسرين: هذا كان قبل فرض الزكاة فلمَّا فُرضت الزَّكاة نسخت الزَّكاة هذه الآية',NULL,'162',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (224,'{كتب عليكم القتال} فُرض وأوجب عليكم الجهاد {وهو كرهٌ لكم} أَيْ: مشقَّةٌ عليكم لما يدخل منه على النَّفس والمال {وَعَسَى أَنْ تَكْرَهُوا شَيْئًا وَهُوَ خَيْرٌ لَكُمْ} لأنَّ في الغزو إحدى الحسنيين إمَّا الظفر والغنيمة وإمَّا الشَّهادة والجنَّة {وَعَسَى أَنْ تُحِبُّوا شيئاً} أَيْ: القعود عن الغزو {وَهُوَ شَرٌّ لَكُمْ} لما فيه من الذُّل والفقر وحرمان الغنيمة والأجر {والله يعلم} ما فيه مصالحكم فبادروا إلى ما يأمركم به وإنْ شقَّ عليكم',NULL,'163',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (225,'{يسألونك عن الشهر الحرام} نزلت في سريةٍ بعثها رسول الله صلى الله عليه وسلم فقاتلوا المشركين وقد أهلَّ رجب وهم لا يعلمون ذلك فاستعظم المشركون سفك الدِّماء في رجب فأنزل الله تعالى: {يسألونك} يعني: المشركين وقيل: هم المسلمون {عن الشهر الحرام قتالٍ فيه} أَيْ: وعن قتالٍ فيه {قل قتالٌ فيه كبير} ثمَّ ابتدأ فقال: {وصد} ومنعٌ {عن سبيل الله} أَيْ: طاعته يعني: صدَّ المشركين رسولَ الله صَلَّى اللَّهُ عليه وسلم وأصحابه عن البيت الحرام عام الحديبية {وكفر به} بالله {والمسجد الحرام} أَيْ: وصدٌّ عن المسجد الحرام {وإخراج أهله} أَيْ: أهل المسجد يعني: رسول الله صلى الله عليه وسلم وأصحابه حين أُخرجوا من مكَّة {منه أكبرُ} وأعظم وِزْراً {عند الله والفتنة} أَيْ: والشِّرك {أكبر من القتل} يعني: قتل السِّرية المشركين في رجب {ولا يزالون} يعني: المشركين {يقاتلونكم حتى يردوكم عن دينكم} إلى الكفر {إِنِ اسْتَطَاعُوا وَمَنْ يَرْتَدِدْ مِنْكُمْ عَنْ دِينِهِ} الإِسلام أَيْ: يرجع فيموت على الكفر {فأولئك حبطت أعمالهم} (بطلت أعمالهم) فقال هؤلاء السَّرية لرسول الله صلى الله عليه وسلم: أصبنا القوم في رجب أنرجو أن يكون لنا أجر المجاهدين في سبيل الله؟ فأنزل الله تعالى:',NULL,'163',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (226,'{إنَّ الذين آمنوا والذين هاجروا} فارقوا عشائرهم وأوطانهم {وجاهدوا} المشركين {فِي سَبِيلِ اللَّهِ} في نصرة دين الله {أولئك يرجون رحمة الله والله غفور رحيم} غفر لهؤلاء السِّرية ما لم يعلموا ورحمهم والإِجماعُ اليوم منعقدٌ على أنَّ قتال المشركين يجوز في جميع الأشهر حلالها وحرامها',NULL,'164',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (227,'{يسألونك عن الخمر والميسر} نزلت في عُمَر ومعاذٍ وسعد بن أبي وقاص رضي الله عنهم أتوا رسول الله صلى الله عليه وسلم فقالوا: أفتنا في الخمر والميسر فإنَّهما مَذْهَبةٌ للعقل مسلبة للمال فنزل قوله عز وجل {يسألونك عن الخمر} وهو كلُّ مسكرٍ مخالطٍ للعقل مُغطٍّ عليه {والميسر} : القمار {قل فيهما إثم كبير} يعني: الإِثم بسببهما لما فيهما من المخاصمة والمشاتمة وقول الفحش والزُّور وغير ذلك {ومنافع للناس} ما كانوا يصيبونه من المال في بيع الخمر والتِّجارة فيها واللَّذَّة عند شربها ومنفعةُ الميسر ما يُصاب من القمار ويرتفق به الفقراء ثمَّ بيَّن أنَّ ما يحصل بسببهما من الإِثم أكبر من نفعهما فقال {وإثمهما أكبر من نفعهما} وليست هذه الآيةُ المُحرِّمةَ للخمر والميسر إنَّما المُحرِّمةُ التي في سورة المائدة وهذه الآية نزلت قبل تحريمها {ويسألونك ماذا ينفقون} نزلت في سؤال عمرو بن الجموح لمَّا نزل قوله: {فللوالدين والأقربين} في سؤاله أعاد السّؤال وسأل عن مقدار ما ينفق؟ فنزل قوله: {قل العفو} أَيْ: ما فضل من المال عن العيال وكان الرَّجل بعد نزول هذه الآية يأخذ من كسبه ما يكفيه وينفق باقيه إلى أن فُرضت الزَّكاة فنسخت آية الزَّكاة التي في براءة هذه الآية وكلَّ صدقةٍ أُمروا بها قبل الزَّكاة {كذلك} أَيْ: كبيانه في الخمر والميسر أو في الإِنفاق {يبين الله لكم الآيات} لتتفكَّروا في أمر الدُّنيا والآخرة فتعرفوا فضل الآخرة على الدُّنيا',NULL,'164',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (228,'{ويسألونك عن اليتامى} كانت العرب في الجاهليَّة يُشدِّدون في أمر اليتيم ولا يُؤاكلونه وكانوا يتشاءمون بملابسة أموالهم فلمَّا جاء الإِسلام سألوا رسول الله صلى الله عليه وسلم عن ذلك؟ فأنزل الله تعالى هذه الآية وقوله: {قل إصلاح لهم خير} يعني: الإِصلاح لأموالهم من غير أجرةٍ خيرٌ وأعظم أجراً {وإن تخالطوهم} تشاركوهم في أموالهم وتخلطوها بأموالكم فتصيبوا من أموالهم عوضاً عن قيامكم بأمورهم {فإخوانكم} أَيْ: فهم إخوانكم والإِخوانُ يُعين بعضهم بعضاً ويُصيب بعضهم من مال بعضٍ {والله يعلم المفسد} لأموالهم {من المصلح} لها فاتقَّوا الله في مال اليتيم ولا تجعلوا مخالطتكم إيَّاهم ذريعةً إلى إفساد أموالهم وأكلها بغير حقٍّ {ولو شاء الله لأعنتكم} لضيَّق عليكم وآثمكم في مخالطتكم ومعناه: التَّذكير بالنِّعمة في التَّوسعة {إِنَّ اللَّهَ عزيزٌ} في ملكه {حكيم} فيما أمر به',NULL,'166',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (229,'{وَلا تَنْكِحُوا الْمُشْرِكَاتِ حَتَّى يُؤْمِنَّ} نزلت في أبي مرثد الغنويِّ كانت له خليلةٌ مشركةٌ فلمَّا أسلم سأل رسول الله صلى الله عليه وسلم: أيحلُّ له أن يتزوَّج بها؟ فأنزل الله تعالى هذه الآية والمشركات ها هنا عامَّة في كلِّ من كفرت بالنبي صلى الله عليه وسلم حرَّم الله تعالى بهذه الآية نكاحهنَّ ثمَّ استثنى الحرائر الكتابيات بالآية التي في المائدة فبقي نكاح الأَمَة الكتابية على التَّحريم {ولأَمةٌ مؤمنةٌ} نزلت في عبد الله بن رواحة كانت له أَمَةٌ مؤمنةٌ فأعتقها وتزوَّجها فطعن عليه ناسٌ وعرضوا عليه حُرَّةً مشركةً فنزلت هذه الآية وقوله: {ولو أعجبتكم} المشركة بمالها وجمالها {وَلا تُنْكِحُوا الْمُشْرِكِينَ حَتَّى يُؤْمِنُوا} لا يجوز تزويج المسلمة من المشرك بحالٍ {أولئك} أَي: المشركون {يدعون إلى النَّار} أَي: الأعمال الموجبة للنَّار {وَاللَّهُ يَدْعُوَ إِلَى الْجَنَّةِ والمغفرة} أَيْ: العمل الموجب للجنَّة والمغفرة {بإذنه} بأمره يعني: إنَّه بأوامره يدعوكم',NULL,'166',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (230,'{ويسألونك عن المحيض} (ذكر المفسرون أنَّ العرب كانت إذا حاضت المرأة لم يؤاكلوها ولم يشاربوها ولم يَسَّاكَنُوا معها في بيت كفعل المجوس) فسأل أبو الدحداح رسول الله صلى الله عليه وسلم فقال يا رسول الله كيف نصنع بالنِّساء إذا حضن؟ فنزلت هذه الآية والمحيض: الحيض {قل هو أذىً} أَيْ: قذرٌ ودمٌ {فاعتزلوا النساء في المحيض} أَيْ: مجامعتهنَّ إذا حضن {ولا تقربوهنَّ} أَيْ: ولا تجامعوهنَّ {حتى يَطَّهَّرْنَ} أي: يغتسلن ومَنْ قرأ {يَطْهُرْنَ} بالتَّخفيف أَيْ: ينقطع عنهنَّ الدم أي: توجد طهارة وهي الغسل {فإذا تطهَّرن} اغتسلن {فأتوهنَّ} أَيْ: جامعوهنَّ {مِنْ حَيْثُ أَمَرَكُمُ اللَّهُ} بتجنُّبه في الحيض - وهو الفرج - {إنَّ الله يحب التوابين} من الذُّنوب و {المتطهرين} بالماء من الأحداث والجنابات',NULL,'167',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (231,'{نساؤكم حرثٌ لكم} أَيْ: مزرع ومنب للولد {فأتوا حرثكم أنى شئتم} أَيْ: كيف شئتم ومن أين شئتم بعد أن يكون في صِمام واحدٍ فنزلت هذه الآية تكذيباً لليهود وذلك أنَّ المسلمين قالوا: إِنَّا نأتي النِّساء باركاتٍ وقائماتٍ ومستلقياتٍ ومن بين أيديهم ومن خلفهنَّ بعد أن يكون المأتي واحداً فقالت اليهود: ما أنتم إلاَّ أمثال البهائم لكنَّا نأتيهنَّ على هيئةٍ واحدةٍ وإنَّا لنجد في التَّوراة أنَّ كلَّ إِتيانٍ يؤتى النِّساء غير الاستلقاء دنسٌ عند الله فأكذب الله تعالى اليهود {وقدموا لأنفسكم} أَي: العمل لله بما يحبُّ ويرضى {واتقوا الله} فيما حدَّ لكم من الجماع وأمرِ الحائض {واعلموا أنكم ملاقوه} أَيْ: راجعون إليه {وبشر المؤمنين} الذين خافوه وحذروا معصيته',NULL,'168',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (232,'{ولا تجعلوا الله عرضةٌ لأيمانكم} أَيْ: لا تجعلوا اليمين بالله سبحانه علَّةً مانعةً من البرِّ والتَّقوى من حيث تتعمَّدون اليمين لتعتلُّوا بها نزلت في عبد الله بن رواحة حلف أن لا يُكلِّم ختنه ولا يدخل بينه وبين خصم له وجعل يقول: قد حلفتُ أَنْ لا أفعل فلا يحلُّ لي وقوله {أن تبروا} أَي: في أَنْ لا تبرُّوا أو لدفع أن تبرُّوا ويجوز أن يكون قوله: {أن تبروا} ابتداءً وخبره محذوف على تقدير: أن تبرُّوا وتتقوا وتصلحوا بين النَّاس أولى أَي: البرُّ والتُّقى أولى {والله سميع عليمٌ} يسمع أيمانكم ويعلم ما تقصدون بها',NULL,'168',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (233,'{لا يؤاخذكم الله باللغو في أيمانكم} أَيْ: ما يسبق به اللِّسان من غير عقدٍ ولا قصدٍ ويكون كالصِّلة للكلام وهو مِثلُ قول القائل: لا والله وبلى واللَّهِ وقيل: لغو اليمين: اليمينُ المكفَّرة سمِّيت لغواً لأنَّ الكفَّارة تُسقط الإِثم منه {وَلَكِنْ يُؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوبُكُمْ} أَيْ: عزمتم وقصدتم وعلى القول الثاني في لغو اليمين معناه: ولكن يؤاخذكم بعزمكم على ألا تبرُّوا وتعتلُّوا في ذلك بأيمانكم بأنَّكم حلفتم {والله غفورٌ حليم} يؤخِّر العقوبة عن الكفَّار والعُصاة',NULL,'169',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (234,'{للذين يؤلون من نسائهم} أَيْ: يحلفون أن لا يطؤوهنَّ {تربص أربعة أشهر} جعل الله تعالى الأجل في ذلك أربعة أشهر فإذا مضت هذه المدَّة فإمَّا أن يُطلِّق أو يطأ فإن أباهما جميعاً طلَّق عليه الحاكم {فإن فاؤوا} رجعوا عمَّا حلفوا عليه أَيْ: بالجماع {فإنَّ الله غفورٌ رحيم} يغفر له ما قد فعل (ولزمته كفَّارة اليمين)',NULL,'169',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (235,'{وإن عزموا الطلاق} أَيْ: طلَّقوا ولم يفيؤوا بالوطء {فإنَّ الله سميع} لما يقوله {عليمٌ} بما يفعله',NULL,'169',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (236,'{والمطلقات} أَيْ: المُخلَّيات من حبال الأزواج يعني: البالغات المدخول بهنَّ غير الحوامل لأنَّ في الآية بيان عدتهنَّ {يتربصن بأنفسهن ثلاثة قروء} أَيْ: ثلاثة أطهار يعني: ينتظرون انقضاء مدة ثلاثة أطهارٍ حتى تمرَّ عليهن ثلاثة أطهارٍ وقيل: ثلاث حيضٍ {وَلا يَحِلُّ لَهُنَّ أَنْ يَكْتُمْنَ مَا خَلَقَ الله في أرحامهنَّ} يعني: الولد ليبطلن حقَّ الزوج من الرَّجعة {إن كنَّ يؤمنَّ بالله واليوم الآخر} وهذا تغليظٌ عليهنَّ في إِظهار ذلك {وبعولتهن} أَيْ: أزواجهنَّ {أحقُّ بردهنَّ} بمراجعتهنَّ {في ذلك} في الأجل الذي أُمرْنَ أن يتربصن فيه {إن أرادوا إصلاحاً} لا إضراراً {وَلَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوفِ} أَيْ: للنِّساء على الرَّجال مثلُ الذي للرِّجال عليهنَّ من الحقِّ بالمعروف أَيْ: بما أمر الله من حقِّ الرَّجل على المرأة {وللرجال عليهن درجة} يعني: بما ساقوا من المهر وأنفقوا من المال {والله عزيز حكيم} يأمر كما أراد ويمتحن كما أحبَّ',NULL,'169',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (237,'{الطلاق مرتان} كان طلاقُ الجاهلية غير محصورٍ بعددٍ فحصر الله الطلاق بثلاثٍ فذكر في هذه الآية طلقتين وذكر الثَّالثة في الآية الأخرى وهي قوله:
INSERT INTO [b2547] VALUES (238,'{فإن طلقها} يعني: الزوج المُطلِّق اثنتين {فلا تحلُّ له} المطلَّقة ثلاثاً {من بعد} أَيْ: من بعد التَّطليقة الثَّالثة {حتى تنكح زوجاً غيره} غير المُطلِّق (ويجامعها) {فإن طلقها} أَيْ: الزَّوج الثَّاني {فلا جناح عليهما أن يتراجعا} بنكاحٍ جديدٍ {إن ظنا} أَيْ: علما وأيقنا {أَنْ يقيما حدود الله} ما بيَّن الله من حقِّ أحدهما على الآخر',NULL,'171',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (239,'{وإذا طلقتم النساء فبلغن أجلهنَّ} أَيْ: قاربن انقضاء عدتهنَّ {فأمسكوهنَّ بمعروف} أَيْ: راجعوهنّ بإشهادٍ على الرَّجعة وعقد لها لا بالوَطْء كما يقول أبو حنيفة {أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍ} أَي: اتركوهنَّ حتى تنقضي عدتهنَّ ويكنَّ أملك بأنفسهنَّ {وَلا تُمْسِكُوهُنَّ ضراراً} أَيْ: لا تُراجعوهنَّ مضارَّةً وأنتم لا حاجة بكم إليهنَّ {لتعتدوا} عليهنَّ بتطويل العِدَّة {وَمَنْ يَفْعَلْ ذَلِكَ} الاعتداء {فقد ظلم نفسه} ضرَّها وأثم بينه وبين الله عزَّ وجل {ولا تتخذوا آيات الله هزواً} كان الرَّجل يُطلِّق في الجاهليَّة ويقول: إنَّما طلَّقت وأنا لاعبٌ فيرجع فيها فأنزل الله تعالى هذه الآية {واذكروا نعمة الله عليكم} بالإِسلام {وَمَا أَنْزَلَ عَلَيْكُمْ مِنَ الْكِتَابِ} يعني: القرآن {والحكمة} مواعظ القرآن',NULL,'171',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (240,'{وإذا طلقتم النساء فبلغن أجلهن} انقضت عدتهن {فلا تعضلوهنَّ} لا تمنعوهنَّ {أن ينكحن أزواجهنَّ} بنكاحٍ جديدٍ أَي: الذين كانوا أزواجاً لهنَّ نزلت في أخت معقل بن يسار طلَّقها زوجها فلمَّا انقضت عدَّتها جاء يخاطبها فأبى معقلٌ أن يُزوِّجها ومنعها بحقِّ الولاية {إذا تراضوا بينهم بالمعروف} بعقدٍ حلالٍ ومهرٍ جائزٍ {ذلك} أَيْ: أَمْرُ اللَّهِ بتَرْكِ العضل {يوعظ به مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بالله واليوم الآخر ذلكم أزكى} أَيْ: ترك العضل خير {لكم} وأفضلُ {وأطهر} لقلوبكم من الرِّيبة وذلك أنَّهما إذا كان في قلب كلِّ واحدٍ منهما علاقةُ حبِّ لم يُؤمن عليهما {والله يعلم} ما لكم فيه من الصَّلاح',NULL,'172',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (241,'{والوالداتُ يرضعن أولادهن} لفظه لفظ الخبر ومعناه الأمر وهو أمر استحبابٍ لا أمر إيجابٍ يريد: إنهنَّ أحقُّ بالإِرضاع من غيرهنَّ إذا أردن ذلك {حولين} سنتين {كاملين} تامين وهذا تحديدٌ لقطع التَّنازع بين الزَّوجين إذا اشتجرا في مدَّة الرَّضاع يدلُّ على هذا قوله {لمن أراد} أي: هذا التقرير والبيان {لِمَنْ أَرَادَ أَنْ يُتِمَّ الرَّضَاعَةَ} {وَعَلَى الْمَوْلُودِ له} أَي: الأب {رزقهن وكسوتهنَّ} رزق الوالدات ولباسهنَّ قال المفسرون: وعلى الزَّوج رزق المرأة المُطلَّقة وكسوتها إذا أرضعت الولد {بالمعروف} بما يعرفون أنَّه عدلٌ على قدر الإِمكان وهو معنى قوله: {لا تكلف نفس إلاَّ وسعها} لا تلزم نفسٌ إلاَّ ما يسعها {لا تضار والدة بولدها} لا ينزع الولد منها إلى غيرها بعد أَنْ رضيت بإرضاعه وألفها الصَّبيُّ ولا تُلقيه هي إلى أبيه بعدما عرفها تُضَارُّه بذلك وهو قوله: {وَلا مَوْلُودٌ لَهُ بِوَلَدِهِ} {وَعَلَى الْوَارِثِ مِثْلُ ذلك} هذا نسقٌ على قوله: {وَعَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَكِسْوَتُهُنَّ} بمعنى: على وارث الصبيِّ - الذي لو مات الصبيُّ وله مالٌ ورثه - مثل الذي كان على أبيه في حياته وأراد بالوارث مَنْ كان من عصبته كائناً من كان من الرِّجال {فإن أرادا} يعني: الأبوين {فصالاً} فطاماً للولد {عن تراضٍ منهما} قبل الحولين {وتشاور} بينهما {فَلا جُنَاحَ عَلَيْهِمَا وَإِنْ أَرَدْتُمْ أَنْ تَسْتَرْضِعُوا أولادكم} مراضع غير الوالدة {فَلا جُنَاحَ عَلَيْكُمْ} فلا إثم عليكم {إِذَا سَلَّمْتُمْ مَا آتَيْتُمْ بِالْمَعْرُوفِ} أَيْ: إذا سلَّمتم إلى الأُمِّ أجرتها بمقدار ما أرضعت',NULL,'172',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (242,'{والذين يتوفون منكم} أَيْ: يموتون {ويذرون} ويتركون (ويُخَلِّفُون) {أزواجاً} نساءً {يتربصن بأنفسهنَّ} خبرٌ في معنى الأمر {أربعة أشهر وعشرا} هذه المدَّة عدَّة المُتوفَّى عنها زوجها إلاَّ أن تكون حاملاً {فإذا بلغن أجلهنَّ} انقضت عدَّتهنَّ {فلا جناح عليكم} أيُّها الأولياء {فيما فعلن في أنفسهنَّ بالمعروف} أَيْ: مِنْ تزوُّج الأكفاء بإذن الأولياء هذا تفسير المعروف ها هنا لأنَّ التي تُزَوِّج نفسها سمَّاها النبي صَلَّى الله عليه وسلم زانية وهذه الآية ناسخةٌ لقوله تعالى: {مَتَاعًا إِلَى الْحَوْلِ غَيْرَ إِخْرَاجٍ} الآية',NULL,'173',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (243,'{ولا جناح عليكم فيما عرَّضتم به} أَيْ: تكلَّمتم به من غير تصريح وهو أن يُضمِّن الكلام دلالةً على ما يريد {من خطبة النساء} أَي: التماس نكاحهنَّ في العدَّة يعني: المتوفَّى عنها الزَّوج يجوز التعريض بخطبتها في العدَّة وهو أن يقول لها وهي في العدَّة: إنَّك لجميلةٌ وإنَّك لنافقةٌ وإنَّك لصالحةٌ وإنَّ من عزمي أَنْ أتزوَّج وما اشبه ذلك {أو أَكْنَنْتُمْ} أسررتم وأضمرتم {في أنفسكم} من خطبتهنَّ ونكاحهنَّ {عَلِمَ اللَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ} يعني: الخطبة {وَلَكِنْ لا تواعدوهن سراً} أَيْ: لا تأخذوا ميثاقهنَّ أن لا ينكحن غيركم {إلاَّ أن تقولوا قولا معروفا} أي: التعريض بالخطبة كما ذكرنا {وَلا تَعْزِمُوا عُقْدَةَ النِّكَاحِ} أيْ: لا تصححوا عقدة النِّكاح {حتى يبلغ الكتاب أجله} حتى تنقضي العدَّة المفروضة {وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنْفُسِكُمْ} أَيْ: مُطَّلعٌ على ما في ضمائركم {فاحذروه} فخافوه',NULL,'174',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (244,'{لا جُنَاحَ عَلَيْكُمْ إِنْ طَلَّقْتُمُ النِّسَاءَ مَا لم تمسوهنَّ} نزلت في رجلٍ من الأنصار تزوَّج امرأة ولم يسمِّ لها مهراً ثمَّ طلَّقها قبل أن يمسَّها فأعلم الله تعالى أنَّ عقد التَّزويج بغير مهرٍ جائز ومعناه: لا سبيل للنِّساء عليكم إنْ طلقتموهنَّ من قبل المسيس والفرض بصداقٍ ولا نفقة وقوله: {أو تفرضوا لهنَّ فريضة} أَيْ: تُوجبوا لهنَّ صداقاً {ومتعوهنَّ} أَيْ: زوِّدوهنَّ وأعطوهنَّ من ما لكم ما يتمتَّعْنَ به فالمرأة إذا طُلِّقت قبل تسمية المهر وقبل المسيس فإنَّها تستحق المتعة بإجماع العلماء ولا مهرَ لها و {على الموسع} أَي: الغنيِّ الذي يكون في سعةٍ من غناه {قدره} أَيْ: قدر إمكانه {وعلى المقتر} الذي في ضيق من فقره قدر إمكانه أعلاها خادم وأوسطها ثوب وأقلُّها أقلُّ ماله ثمن قال الشافعيُّ: وحسنٌ ثلاثون درهماً {متاعاً} أَيْ: متعوهنَّ متاعاً {بالمعروف} بما تعرفون أنَّه القصد وقدر الإِمكان {حقاً} واجباً {على المحسنين}',NULL,'175',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (245,'{وإن طلقتموهن من قبل أن تمسوهنَّ} هذا في المُطلَّقة بعد التَّسمية وقبل الدُّخول حكم الله تعالى بنصف المهر وهو قوله: {فنصف ما فرضتم} أَيْ: فالواجبُ نصف ما فرضتم {إلاَّ أن يعفون} أَي: النِّساء أَيْ: إلاَّ أَنْ يتركن ذلك النِّصف فلا يُطالبن الأزواج به {أو يعفو الذي بيده عقده النكاح} أَي: الزَّوج لا يرجع في شيءٍ من المهر فيدع لها المهر الذي وفَّاه عملاً {وأن تعفو} خطابٌ للرِّجال والنِّساء {أقرب للتقوى} أَيْ: أدعى إلى اتِّقاء معاصي الله لأنَّ هذا العفو ندبٌ فإذا انتدب المرء له عُلم إنَّه - لما كان فرضاً - أشدُّ استعمالاً {ولا تنسوا الفضل بينكم} لا تتركوا أن يتفضَّل بعضكم على بعض هذا أمرٌ للزَّوج والمرأة بالفضل والإِحسان',NULL,'175',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (246,'{حافظوا على الصلوات} بأدائها في أوقاتها {والصلاة الوسطى} أَيْ: صلاة الفجر لأنَّها بين صلاتي ليلٍ وصلاتي نهارٍ أفردها بالذِّكر تخصيصاً {وقوموا لله قانتين} مُطيعين',NULL,'176',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (247,'{فإن خفتم فرجالاً} أَيْ: إن لم يمكنكم أن تصلُّوا موفِّين للصَّلاة حقًَّها فصلُّوا مُشاةً على أرجلكم {أو ركباناً} على ظهور دوابِّكم وهذا في المطاردة والمسايفة {فإذا أمنتم فاذكروا الله} أَيْ: فصلُّوا الصَّلوات الخمس تامَّةً بحقوقها {كَمَا عَلَّمَكُمْ ما لم تكونوا تعلمون} كما افترض عليكم في مواقيتها',NULL,'176',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (248,'{والذين يتوفون منكم ويذرون أزواجاً وصية} فعليهم وصيةٌ {لأزواجهم} لنسائهم وهذا كان في ابتداء الإِسلام لم يكن للمرأة ميراثٌ من زوجها وكان على الزَّوج أن يُوصي لها بنفقة حولٍ فكان الورثة ينفقون عليها حولاً وكان الحول عزيمةً عليها في الصَّبر عن التَّزوُّج وكانت مُخيَّرة في أن تعتدَّ إن شاءت في بيت الزَّوج وإن شاءت خرجت قبل الحول وتسقط نفقتها فذلك قوله: {متاعاً إلى الحول} أَيْ: متعوهنَّ متاعاً يعني: النَّفقة {غير إخراجٍ} أَيْ: من غير إخراج الورثة إيَّاها {فإن خرجن فَلا جُنَاحَ عَلَيْكُمْ} يا أولياء الميِّت فِي قطع النَّفقة عنهنَّ وترك منعها عن التَّشوف للنَّكاح والتَّصنُّع للأزواج وذلك قوله: {فِي مَا فَعَلْنَ فِي أَنْفُسِهِنَّ مِنْ مَعْرُوفٍ} وهذا كلُّه منسوخٌ بآية المواريث وعدَّةِ المتوفى عنها زوجها',NULL,'176',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (249,'{وللمطلقات متاعٌ بالمعروف حقاً على المتقين} لمَّا ذكر الله تعالى متعة المُطلَّقة في قوله: {حَقًّا عَلَى الْمُحْسِنِينَ} قال رجلٌ من المسلمين: إنْ أحسنتُ فعلتُ وإن لم أُرد ذلك لم أفعل فأوجبها الله تعالى على المتقين الذين يتَّقون الشِّرك',NULL,'177',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (250,'{كذلك يبين الله لكم آياته} شبّه اللَّهُ البيانَ الذي يأتي بالبيان الذي مضى في الأحكام التي ذكرها',NULL,'177',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (251,'{أَلَمْ تَرَ إِلَى الَّذِينَ خَرَجُوا مِنْ ديارهم} ألم تعلم ألم ينته علمك إلى هؤلاء وهم قومٌ من بني إسرائيل خرجوا من بلدتهم هاربين من الطاعون حتى نزلوا واديا فأماتهم الله جميعاً فذلك قوله: {حذر الموت} أَيْ: لحذر الْمَوْتِ {فَقَالَ لَهُمُ اللَّهُ مُوتُوا ثُمَّ أَحْيَاهُمْ} مقتهم الله على فرارهم من الموت فأماتهم عقوبةً لهم ثمَّ بعثهم ليستوفوا بقيَّة آجالهم {إنَّ الله لذو فضل على الناس} أَيْ: تفضُّلٍ عليهم بأَنْ أحياهم بعد موتهم',NULL,'177',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (252,'{وقاتلوا في سبيل الله} يحرِّض المؤمنين على القتال {وَاعْلَمُوا أَنَّ اللَّهَ سَمِيعٌ} لما يقوله المُتعلِّل {عليمٌ} بما يضمره فإيَّاكم والتَّعلُّلَ',NULL,'178',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (253,'{مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا} أَيْ: مَنْ ذا الذي يعمل عمل المُقرض بأن يقدِّم من ماله فيأخذ أضعاف ما قدَّم وهذا استدعاءٌ من الله تعالى إلى أعمال البرِّ {والله يقبض} أَيْ: يُمسك الرِّزق على مَنْ يشاء {ويبسط} أي: ويوسِّع على من يشاء',NULL,'178',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (254,'{أَلَمْ تَرَ إِلَى الْمَلإِ مِنْ بَنِي إِسْرَائِيلَ} أَي: إلى الجماعة {إِذْ قَالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنَا مَلِكًا} سألوا نبيَّهم أشمويل عليه السَّلام ملكاً تنتظم به كلمتهم ويستقيم حالهم في جهاد عدوِّهم وهو قوله: {نقاتل في سبيل الله} {فقال} لهم ذلك النَّبيُّ: {هَلْ عَسَيْتُمْ إِنْ كتب عليكم القتال أن لا تقاتلوا} أَيْ: لعلَّكم أَنْ تجبنوا عن القتال {قالوا وما لنا أن لا نقاتل في سبيل الله} أَيْ: وما يمنعنا عن ذلك؟ {وقد أخرجنا من ديارنا} {و} أُفردنا من {أبنائنا} بالسبي والقتل إذا بلغ الأمر منَّا هذا فلا بدَّ من الجهاد قال الله تعالى: {فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا إِلا قَلِيلا منهم} وهم الذين عبروا النَّهر ويأتي ذكرهم',NULL,'178',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (255,'{وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ اللَّهَ قَدْ بَعَثَ لكم طالوت ملكاً} أَيْ: قد أجابكم إلى ما سألتم من بعث الملك {قالوا} : كيف يملك علينا؟ وكان من أدنى بيوت بني إسرائيل ولم يكن من سبط المملكة فأنكروا ملكه وقالوا: {وَنَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً من المال} أَيْ: لم يُؤت ما يتملَّك به الملوك {قال} النبيُّ: {إنَّ الله اصطفاه عليكم} (اختاره) بالملك {وزاده بسطة في العلم والجسم} كان طالوت يومئذٍ أعلم رجلٍ في بني إسرائيل وأجمله وأتمَّه والبسطة: الزِّيادة في كلِّ شيء {والله يؤتي ملكه من يشاء} ليس بالوراثة {والله واسع} أَيْ: واسع الفضل والرِّزق والرَّحمة فسألوا نبيَّهم على تمليك طالوت آية فـ',NULL,'179',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (256,'{وَقَالَ لَهُمْ نِبِيُّهُمْ إِنَّ آيَةَ مُلْكِهِ أَنْ يأتيكم التابوت} وكان تابوتاً أنزله الله تعالى على آدم عليه السَّلام فيه صور الأنبياء عليهم السَّلام كانت بنو إسرائيل يستفتحون به على عدوِّهم فغلبتهم العمالقة على التَّابوت فلمَّا سألوا نبيَّهم البيِّنة على ملك طالوت قال: إنَّ آية ملكه أن يردَّ الله تعالى التَّابوت عليكم فحملت الملائكة التَّابوت حتى وضعته في دار طالوت وقوله: {فيه سكينة من ربكم} أَيْ: طمأنينةٌ كانت قلوبهم تطمئنُّ بذلك ففي أيِّ مكانٍ كان التَّابوت سكنوا هناك وكان ذلك من أمر الله تعالى {وَبَقِيَّةٌ مِمَّا تَرَكَ آلُ مُوسَى وَآلُ هَارُونَ} أَيْ: تركاه هما وكانت البقيَّة نعلي موسى وعصاه وعمامة هارون وقفيزاً من المنِّ الذي كان ينزل عليهم {تحمله الملائكة} أَي: التَّابوت {إنَّ في ذلك لآية} أَيْ: في رجوع التَّابوت إليكم علامة أنَّ الله قد ملَّك طالوت عليكم {إِنْ كُنْتُمْ مؤمنين} أَيْ: مصدِّقين',NULL,'179',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (257,'{فلما فصل طالوت بالجنود} أَيْ: خرج بهم من الموضع الذي كانوا فيه إلى جهاد العدوِّ {قال} لهم طالوت: {إنَّ الله مبتليكم} أَيْ: مُختبركم ومُعاملكم مُعاملة المختبر {بنهرٍ} أَيْ: بنهر فلسطين ليتميِّز المحقِّق ومَنْ له نيَّةٌ في الجهاد من المُعذِّر {فمن شرب منه} أَيْ: من مائه {فليس مني} أَيْ: من أهل ديني {ومن لم يطعمه} لم يذقه {فَإِنَّهُ مِنِّي إِلا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ} أَيْ: مرَّةً واحدةً أَيْ: أخذ منه بجرَّةٍ أو قِربةٍ وما أشبه ذلك مرَّةً واحدةً قال لهم طالوت: مَنْ شرب من النَّهر وأكثر فقد عصى الله ومن اغترف غرفة بيده أقنعته فهجموا على النَّهر بعد عطش شديد فوقع أكثرهم في النَّهر وأكثروا الشُّرب فهؤلاء جَبُنوا عن لقاء العدو وأطاع قومٌ قليلٌ عددهم فلم يزيدوا على الاغتراف فقويت قلوبهم وعبروا النَّهر فذلك قوله: {فشربوا منه إلاَّ قليلاً منه} وكانوا ثلثمائة وبضعة عشر رجلاً {فلما جاوزه} أَي: النَّهر {هو والذين آمنوا معه قالوا} يعني: الذين شربوا وخالفوا أمر الله تعالى: {لا طاقة لنا اليوم بجالوت وجنوده قال} يعني: القليل الذين اغترفوا وهم {الذين يظنون} أَيْ: يعلمون {أنهم ملاقوا الله} أَيْ: راجعون إليه: {كم مِنْ فئة قليلة} أَيْ: جماعةٍ قَلِيلَةٍ {غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللَّهِ وَاللَّهُ مع الصابرين} بالمعونة والنَّصر',NULL,'180',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (258,'{ولما برزوا} أَيْ: خرجوا {لجالوت وجنوده} أَيْ: لقتالهم {قالوا ربنا أفرغ} أصببْ {علينا صبراً وثبت أقدامنا} بتقوية قلوبنا',NULL,'181',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (259,'{فهزموهم} فردُّوهم وكسروهم {بإذن الله} بقضائه وقدره {وقتل داود} النَّبيُّ وكان في عسكر بني إسرائيل {جالوت} الكافر {وآتاه الله الملك} (أعطى الله داود ملك بني إسرائيل) {والحكمة} أَيْ: جمع له الملك والنُّبوَّة {وعلَّمه مما يشاء} صنعة الدروع ومنطق الطيور {ولولا دفع الله الناس بعضهم ببعض} لولا دفع الله بجنود المسلين لغلب المشركون على الأرض فقتلوا المؤمنين وخرَّبوا البلاد والمساجد',NULL,'181',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (260,'{تلك آيات الله} أي: هذه الآيات التي أخبرتك بها آيات الله أَيْ: علامات توحيده {وإنك لمن المرسلين} أَيْ: أنت من هؤلاء الذين قصصتُ عليك آياتهم',NULL,'181',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (261,'````` {تلك الرسل} أَيْ: جماعة الرُّسل {فضلنا بعضهم على بعض} أَيْ: لم نجعلهم سواءً في الفضيلة وإن استووا في القيام بالرِّسالة {منهم مَنْ كلَّم الله} وهو موسى عليه السَّلام {ورفع بعضهم درجات} يعني محمدا صلى الله عليه وسلم أُرسل إلى النَّاس كافَّةً {وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ القدس} مضى تفسيره {وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلَ الَّذِينَ مِنْ بعدهم} أَيْ: من بعد الرُّسل {مِنْ بَعْدِ مَا جَاءَتْهُمُ البينات} من بعد ما وضحت لهم البراهين {وَلَكِنِ اختلفوا فمنهم مَنْ آمن} ثبت على إيمانه {ومنهم مَنْ كفر} كالنَّصارى بعد المسيح اختلفوا فصاروا فِرقاً ثمَّ تحاربوا {ولو شاء الله ما اقتتلوا} كرَّر ذكر المشيئة باقتتالهم تكذيباً لمن زعم أنَّهم فعلوا ذلك من عند أنفسهم لم يجري به قضاءٌ من الله {ولكنَّ الله يفعل ما يريد} فيوفِّقُ مَنْ يشاء فضلاً ويخذل من يشاء عدلاً',NULL,'182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (262,'{يا أيها الذين آمنوا أنفقوا ممَّا رزقناكم} أَي: الزَّكاة المفروضة وقيل: أراد النَّفقة في الجهاد {مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا بَيْعٌ فيه} يعني: يوم القيامة يعني: لا يؤخذ في ذلك اليوم بدَلٌ ولا فداءٌ {ولا خلة} ولا صداقةٌ {ولا شفاعة} عمَّ نفي الشَّفاعة لأنَّه عنى الكافرين بأنَّ هذه الأشياء لا تنفعهم ألا ترى أنَّه قال: {والكافرون هم الظالمون} أَيْ: هم الذين وضعوا أمر الله فِي غير موضعه',NULL,'182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (263,'{الله لا إله إلاَّ هو الحي} الدَّائم البقاء {القيوم} القائم بتدبير أمر الخلق في إنشائهم وأرزاقهم {لا تأخذه سنة} وهي أوَّل النُّعاس {ولا نوم} وهو الغشية الثَّقيلة {له ما في السماوات وما في الأرض} ملكا وخلقاً {مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلا بِإِذْنِهِ} أَيْ: لا يشفع عنده أحدٌ إلاَّ بأمره إبطالاً لزعم الكفَّار أنَّ الأصنام تشفع لهم {يعلم ما بين أيديهم} من أمر الدُّنيا {وما خلفهم} من أمر الآخرة {ولا يحيطون بشيء من علمه} أَيْ: لا يعلمون شيئاً من معلوم الله تعالى: {إلاَّ بما شاء} إلاَّ بما أنبأ الله به الأنبياء وأطلعهم عليه {وسع كرسيه السماوات والأرض} أي: احتملهما وأطاقهما يعني: ملكه وسلطانه وقيل: هو الكرسيُّ بعينه وهو مشتمل بعظمته على السماوات والأرض وروي عن ابن عباس أن الكرسي علمه {ولا يَؤُوْدُهُ} أَيْ: لا يُجهده ولا يُثقله {حفظهما} أي: حفظ السماوات والأرض {وهو العليُّ} بالقدرة ونفوذ السُّلطان عن الأشباه والأمثال {العظيم} عظيم الشَّأن',NULL,'182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (264,'{لا إكراه في الدِّين} بعد إسلام العرب لأنهم أُكرهوا على الإِسلام فلم يُقبل منهم الجزية لأنَّهم كانوا مشركين فلمَّا أسلموا أنزل الله تعالى هذه الآية
INSERT INTO [b2547] VALUES (265,'{الله وليُّ الذين آمنوا} أَيْ: ناصرهم ومتولي أمرهم {يخرجهم من الظلمات} من الكفر والضَّلالة إلى الإِيمان والهداية {والذين كفروا} أي: اليهود {أولياؤهم الطاغوت} يعني: رؤساءهم كعب بن الأشرف وحُيي بن أخطب {يُخْرِجُونَهُمْ من النور} يعني: ممَّا كانوا عليه من الإِيمان بمحمد عليه السلام قبل بعثه {إلى الظلمات} إلى الكفر به بعد بعثه',NULL,'184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (266,'{ألم تر إلى الذي حاجَّ} جادل وخاصم {إبراهيم في ربه} حين قال له: مَنْ ربُّك؟ {أن آتاه الله الملك} أي: الملك الذي آتاه الله يريد: بطرُ الملك حمله على ذلك وهو نمروذ بن كنعان {إِذْ قَالَ إِبْرَاهِيمُ رَبِّيَ الَّذِي يُحْيِي وَيُمِيتُ} فقال عدو الله: {أنا أحيي وأميت} فعارضه بالاشتراك في العبارة من غير فعل حياةٍ ولا موتٍ فلما لبَّس في الحجَّة بأنْ قال: أنا أفعل ذلك احتجَّ إبراهيم عليه بحجَّةٍ لا يمكنه فيها أن يقول: أنا أفعل ذلك وهو قوله: {قَالَ إِبْرَاهِيمُ فَإِنَّ اللَّهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِي كفر} أي: انقطع وسكت',NULL,'184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (267,'{أو كالذي مرَّ على قرية} عطفٌ على المعنى لا على اللفظ كأنه قال: أرأيت الذي حاجَّ أو كالذي مرَّ وهو عزيز {على قرية} وهي إيليا {وهي خاوية} ساقطةٌ مُتهدِّمةٌ {على عروشها} أي: سقوفها {قال: أنى يحيي هذه الله} أَيْ: من أين يُحيي هذه الله {بعد موتها} يعمرها بعد خرابها؟ ! استعبد أَنْ يفعل الله ذلك فأحبَّ الله أن يُريه آيةً في نفسه في إحياء القرية {فأماته الله مائة عام} وذلك أنه مرَّ بهذه القرية على حمارٍ ومعه ركوة عصيرٍ وسلةُ تينٍ فربط حماره وألقى الله عز وجل عليه النَّوم فلمَّا نام نزع الله عز وجل روحه مائة سنةٍ فلمَّا مضت مائة سنةٍ أحياه الله تعالى وذلك قوله: {ثمَّ بعثه} {قال كم لبثت} كم أقمت ومكثت ها هنا؟ {قَالَ: لَبِثْتُ يَوْمًا أَوْ بَعْضَ يَوْمٍ قَالَ بَلْ لَبِثْتَ مِائَةَ عَامٍ فَانْظُرْ إِلَى طَعَامِكَ} أَي: التِّين {و} إلى {شرابك} أي: العصير {لم يتسنَّه} أَيْ: لم يتغيَّر ولم ينتن بعد مائة سنةٍ وأراه علامة مكثه مائة سنةٍ ببلى عظام حماره فقال: {وانظر إلى حمارك} فرأى حماره ميتاً عظامه بيضٌ تلوح {ولنجعلك آية للناس} الواو زائدة والمعنى: لبثتَ مائة عامٍ لنجعلك آيةً للنَّاس وكونه آيةً أَنْ بعثه شابّاً أسود الرَّأس واللِّحية وبنو بنيه شِيبٌ {وانظر إلى العظام} أَيْ: عظام حماره {كيف نُنْشِزُها} أَيْ: نحييها يقال: أَنشرَ اللَّهُ الموتى وقرىء: {ننشزها} أَيْ: نرفعها من الأرض ونشوز كلِّ شيءٍ: ارتفاعه {ثم نكسوها لحماً فلما تبيَّن له} شاهدَ ذلك {قَالَ: أَعْلَمُ أَنَّ اللَّهَ عَلَى كُلِّ شَيْءٍ قدير} أَيْ: أعلم العلم الذي لا يعترض عليه الإِشكال وتأويله: إنِّي قد علمت مُشاهدةً ما كنت أعلمه غيباً',NULL,'185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (268,'{وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِي الموتى} وذلك أنَّه رأى جيفةً بساحل البحر يتناولها سباع الطير والوحش ودوابُّ البحر ففكَّر كيف يجتمع ما قد تفرَّق منها وأحبَّ أن يرى ذلك فسأل الله تعالى أن يُريه إحياء الموتى فقال الله تعالى: {أولم تؤمن} ألست آمنت بذلك؟ {قال بلى ولكن ليطمئن قلبي} بالمُعاينة بعد الإِيمان بالغيب {قال: فخذ أربعة من الطير} طاوُساً ونسراً وغراباً وديكاً {فصرهنَّ إليك} أَيْ: قطِّعهنَّ كأنَّه قال: خذ إليك أربعة من الطَّير فقطعهنَّ {ثُمَّ اجْعَلْ عَلَى كُلِّ جَبَلٍ مِنْهُنَّ جُزْءًا} ثمَّ أُمر أن يخلط ريشها ولحومها ثمَّ يفرِّق أجزاءها بأن يجعلها على أربعة أجبلٍ ففعل ذلك إبراهيم وأمسك رؤوسهنَّ عنده ثمَّ دعاهنَّ فقال: تعالين بإذن الله فجعلت أجزاء الطُّيور يطير بعضها إلى بعض حتى تكاملت أجزاؤها ثمَّ أقبلن على رؤوسهنّ فذلك قوله: {ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْيًا وَاعْلَمْ أَنَّ اللَّهَ عَزِيزٌ} لا يمتنع عليه ما يريد {حكيم} فيما يدبِّر فلمَّا ذكر الدَّلالة على توحيده بما أتى الرُّسل من البيِّنات حثٍّ على الجهاد والإنفاق فيه فقال:',NULL,'186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (269,'{مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ} أَيْ: مَثلُ صدقاتهم وإنفاقهم {كمثل حبَّةٍ أنبتت سبع سنابل} يريد أنَّه يضاعف الواحد بسبع مائةٍ وجعله كالحبَّة تنبت سبع مائة حبَّةٍ ولا يشترط وجود هذا على ضرب المثل',NULL,'187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (270,'{الَّذِينَ يُنْفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ ثُمَّ لا يتبعون ما أنفقوا منَّاً} الآية وهو أن يقول: أحسنتُ إلى فلانٍ ونعشته وجبرت خلله يمنُّ بما فعل {ولا أذىً} وهو أن يذكر إحسانه لمن لا يحبُّ الذي أُحسن إليه وقوفه عليه',NULL,'187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (271,'{قول معروفٌ} كلامٌ حسنٌ وردٌّ على السَّائل جميل {ومغفرة} أَيْ: تجاوزٌ عن السَّائل إذا استطال عليه عند ردِّه {خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُهَا أَذًى} أَيْ: مَنٌّ وتعييرٌ للسَّائل بالسُّؤال {والله غنيٌّ} عن صدقة العباد {حليم} إذ لم يعجِّل بالعقوبة على مَنْ يمنُّ',NULL,'187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (272,'{يا أيها الذين آمنوا لا تبطلوا صدقاتكم} أَيْ: ثوابها {بالمنِّ} وهو أنْ يمنَّ بما أعطى {والأذى} وهو أن يوبِّخ المُعطي المُعطى له {كالذي ينفق} أَيْ: كإبطاله رياء النَّاس وهو المُنافق يعطي ليوهم أنَّه مؤمنٌ {فمثله} أَيْ: مَثلُ هذا المنافق {كَمَثَلِ صَفْوَانٍ} وهو الحجر الأملس {عَلَيْهِ تُرَابٌ فأصابه وابل} مطرٌ شديدٌ {فتركه صلداً} برَّاقاً أملس وهذا مَثلٌ ضربه الله تعالى للمانِّ والمنافق يعني: إنَّ النَّاس يرون في الظَّاهر أنَّ لهؤلاء أعمالاً كما يُرى التُّراب على هذا الحجر فإذا كان يوم القيامة اضمحلَّ كلُّه وبطل كما أذهب الوابل ما كان على الصفوان فلا يقدر أحدٌ من الخلق على ذلك التُّراب كذلك هؤلاء إذا قدموا على ربِّهم لم يجدوا شيئاً وهو قوله جلَّ وعزَّ {: لا يَقْدِرُونَ عَلَى شَيْءٍ} أَيْ: على ثواب شَيْءٍ {مِمَّا كَسَبُوا وَاللَّهُ لا يَهْدِي الْقَوْمَ الكافرين} لا يجعل جزاءهم على كفرهم أن يهديهم (ثمَّ ضرب مثلاً لمن ينفق يريد ما عند الله ولا يمنُّ ولا يؤذي فقال) :',NULL,'187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (273,'{ومَثل الذين ينفقون أموالهم ابتغاء مرضاة الله وتثبيتاً} أَيْ: يقيناً وتصديقاً {من أنفسهم} بالثَّواب لا كالمنافق الذي لا يؤمن بالثَّواب {كمثل جنة بربوةٍ} وهي ما ارتفع من الأرض وهي أكثر ريعاً من المستفل {أصابها وابلٌ} وهو أشدُّ المطر {فآتت} أعطت {أكلها} ما يؤكل منها {ضِعْفَيْن} أَيْ: حملت في سنة من الرَّيع ما يحمل غيرها في سنتين {فإن لم يصبها وابلٌ} وهو أشدُّ المطر وأصابها طلٌّ وهو المطر الضعيف فتلك حالها في البركة يقول: كما أنَّ هذه الجنَّة تُثمر في كلِّ حالٍ ولا يخيب صاحبها قلَّ المطر أو كَثُر كذلك يضعف الله ثواب صدقة المؤمن قلَّت نفقته أم كثرت ثمَّ قرَّر مَثَل المُرائي في النَّفقة والمُفرِّط في الطَّاعة إلى أَنْ يموت بقوله:',NULL,'188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (274,'{أيود أحدكم} يقول: مثلُهم كمثل رجلٍ كانت له جنَّةٌ فِيهَا مِنْ كُلِّ الثَّمَرَاتِ {وَأَصَابَهُ الْكِبَرُ} فضعف عن الكسب وله أطفال لا يجدون عليه ولا ينفعونه {فأصابها إعصار} وهي ريحٌ شديدةٌ {فيه نارٌ فاحترقت} ففقدها أحوج ما كان إليها عند كبر السِّنِّ وكثرة العيال وطفولة الولد فبقي هو وأولاده عجزةً مُتحيِّرين {لا يقدرون على} حيلةٍ كذلك يُبطل الله عمل المنافق والمرائي حتى لا توبة لهما ولا إقالة من ذنوبهما {كذلك يبين الله} كمثل بيان هذه الأقاصيص {يبين الله لكم الآيات} في أمر توحيده',NULL,'188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (275,'{يا أيها الَّذِينَ آمَنُوا أَنْفِقُوا مِنْ طَيِّبَاتِ مَا كَسَبْتُمْ} نزلت في قومٍ كانوا يتصدَّقون بشرار ثمارهم ورذالة أموالهم والمراد بالطَّيِّبات هاهنا الجياد الخيار ممَّا كسبتم أَيْ: التِّجارة {وممَّا أخرجنا لكم من الأرض} يعني: الحبوب التي يجب فيها الزَّكاة {ولا تيمموا} أَيْ: لاتقصدوا {الخبيث منه تنفقون} أَيْ: تنفقونه {ولستم بآخذيه إلاَّ أن تغمضوا} أَيْ: بآخذي ذلك الخبيث لو أُعطيتم في حقٍّ لكم إلاَّ بالإِغماض والتَّساهل وفي هذا بيانُ أنَّ الفقراء شركاء ربِّ المال والشَّريك لا يأخذ الرَّديء من الجيِّد إلاَّ بالتَّساهل',NULL,'189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (276,'{الشيطانُ يعدكم الفقر} أَيْ: يُخوِّفكم به يقول: أَمسك مالك فإنَّك إنْ تصدَّقت افتقرت {ويأمركم بالفحشاء} بالبخل ومنع الزَّكاة {والله يعدكم} أَنْ يجازيكم على صدقتكم {مغفرة} لذنوبكم وأَنْ يُخلف عليكم',NULL,'189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (277,'{يؤتي الحكمة} علم القرآن والفهم فيه وقيل: هي النُّبوَّة {من يشاء} {وما يذكر إلا أولو الألباب} أَيْ: وما يتَّعظ إلاَّ ذوو العقول',NULL,'189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (278,'{وما أنفقتم من نفقة} أدَّيتم من زكاة {أو نذرتم من نذر} في صدقة التَّطوُّع أَيْ: نويتم أن تصَّدَّقوا بصدقة {فإن الله يعلمه} يجازي عليه {وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ} وعيدٌ لمَنْ أنفق في غير الوجه الذي يجوز له من رياءٍ أو معصيةٍ أو من مال مغصوبٍ',NULL,'190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (279,'{إن تبدوا الصدقات} سألوا رسول الله صلى الله عليه وسلم فقالوا: صدقة السرِّ أفضلُ أم صدقة العلانيَة؟ فنزلت هذه الآية والمفسرون على أنَّ هذه الآية في التَّطوُّع لا في الفرض فإِنَّ الفرضَ إظهاره أفضل وعند بعضهم الآية عامَّةٌ في كلِّ صدقةٍ وقوله: {ويكفر عنكم من سيئاتكم} أي يغفرها لكم ومن للصلة والتأكيد',NULL,'190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (280,'{ليس عليك هداهم} نزلت حين سألت قُتيلة أمُّ أسماء بنت أبي بكر ابنتها أن تعطيها شيئاً وهي مشركةٌ فأبت وقالت: حتى أستأمر رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم فمزلت هذه الآية والمعنى: ليس عليك هُدى مَن خالفك فمنعهم الصَّدقة ليدخلوا في الإسلام {وما تنفقوا من خيرٍ} أَيْ: مالٍ {فلأنفسكم} ثوابه {وما تنفقون إلاَّ ابتغاء وجه الله} خبرٌ والمراد به الأمر وقيل: هو خاصٌّ في المؤمنين أَي: قد علم الله ذلك منكم {وما تنفقوا من خيرٍ} من مالٍ على فقراء أصحاب الصُّفَّة {يوف إليكم} أًيْ: يوفَّر لكم جزاؤه {وأنتم لا تظلمون} أَيْ: لا تنقصون من ثواب أعمالكم شيئاً',NULL,'190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (281,'{للفقراء} أَيْ: هذه الصَّدقات والإِنفاق التي تقدَّم ذكرها {للفقراء الذين أحصروا} أَيْ: حُبسوا أَيْ: هم فعلوا ذلك حبسوا أنفسهم {في سبيل الله} في الجهاد يعني: فقراء المهاجرين {لا يستطيعون ضرباً} أَيْ: سيراً {في الأرض} لا يتفرَّغُون إلى طلب المعاش لأنهم قد ألزموا أنفسهم أمر الجهاد فمنعهم ذلك من التَّصرُّف حثَّ الله تعالى المؤمنين على الإِنفاق عليهم {يحسبهم الجاهل} يخالهم {أغنياء من التعفف} عن السُّؤال {تعرفهم بسيماهم} بعلامتهم التَّخشُّع والتَّواضع وأثر الجهد {لا يسألون الناس إلحافاً} أَيْ: إلحاحاًً إذا كان عندهم غداءٌ لم يسألوا عشاءً وإذا كان عندهم عَشاءٌ لم يسألوا غداءً',NULL,'191',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (282,'{الذين ينفقون أموالهم بالليل والنهار} نَزَلَتْ فِي عَلِيِّ بْنِ أَبِي طَالِبٍ رَضِيَ الله عنه كان عنده أربعة دراهم لا يملك غيرها فَتَصدَّق بدرهمٍ سرَّاً ودرهمٍ علانيةً ودرهمٍ ليلاً ودرهمٍ نهاراً',NULL,'191',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (283,'{الذين يأكلون الربا} أيْ: يُعاملون به فَنَبَّه بالأكل على غيره {لا يقومون} من قبورهم يوم القيامة {إلاَّ كما يقوم الذي يتخبَّطه الشيطان} يصيبه بجنونٍ {من المس} من الجنون وذلك أنَّ آكل الرِّبا يُبعث يوم القيامة مجنوناً {ذلك} أَيْ: ذلك الذي نزل بهم {بأنهم قالوا إنما البيع مثل الربا} وهو أنَّ المشركين قالوا: الزِّيادة على رأس المال بعد مَحِلِّ الدَّين كالزِّيادة بالرِّبح في أوَّل البيع فكذَّبهم الله تعالى فقال: {وَأَحَلَّ اللَّهُ الْبَيْعَ وَحَرَّمَ الرِّبَا فَمَنْ جاءه موعظة من ربه} أَيْ: وُعظ {فانتهى} عن أكل الرِّبا {فَلَهُ مَا سَلَفَ} أَّيْ: ما أكل من الرِّبا ليس عليه ردُّ ما أخذ قبل النَّهي {وأمره إلى الله} والله وليُّ أمره {ومَنْ عاد} إلى استحلال الرِّبا {فَأُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}',NULL,'192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (284,'{يَمْحَقُ اللَّهُ الرِّبَا} أَيْ: ينقصه ويذهب بركته وإن كان كثيراً كما يمحق القمر {ويربي الصدقات} يربيها لصاحبها كما يُربي أحدكم فصيله {والله لا يحبُّ كل كفار} بتحريم الرِّبا مستحلٍّ له {أثيم} فاجر بأكله (مصر عليه)',NULL,'192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (285,'قال تعالى {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَقَامُوا الصَّلاةَ وَآتَوُا الزَّكَاةَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَلا خوفٌ عليهم ولا هم يحزنون}',NULL,'192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (286,'{يا أيها الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَذَرُوا مَا بَقِيَ من الربا} نزلت في العباس وعثمان رضي الله عنهما طلباً رباً لهما كانا قد أسلفنا قبل نزول التَّحريم فلمَّا نزلت هذه الآية سمعها وأطاعا وأخذا رؤوس أموالهما ومعنى الآية: تحريم ما بقي ديناً من الرِّبا وإيجاب أخذ رأس المال دون الزِّيادة على جهة الرِّبا وقوله: {إن كنتم مؤمنين} أَيْ: إنَّ مَنْ كان مؤمناً فهذا حكمه',NULL,'193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (287,'{فإن لم تفعلوا} فإن لم تذروا ما بقي من الرِّبا {فأذنوا} فاعلموا {بحرب من الله ورسوله} أَيْ: فأيقنوا أنَّكم في امتناعكم من وضع ذلك حربٌ لله ورسوله {وإن تبتم} عن الرِّبا {فلكم رؤوس أموالكم لا تظلمون} بطلب الزِّيادة {ولا تُظلمون} بالنُّقصان عن رأس المال',NULL,'193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (288,'{وإنْ كان ذو عسرة} أَيْ: وإن وقع غريم ذو عسرة {فنظرةٌ} أَيْ: فعليكم نظرةٌ أَيْ: تأخيرٌ {إلى ميسرة} إلى غنىً ووجود المال {وأن تصدقوا} على المعسرين برأس المال {خيرٌ لكم}',NULL,'193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (289,'{واتقوا يوماً ترجعون فيه إلى الله} يعني: يوم القيامة تُرَدُّون فِيهِ إِلَى اللَّهِ {ثُمَّ تُوَفَّى كُلُّ نَفْسٍ ما كسبت} أي: جزاء ما كسبت من الأعمال {وهم لا يظلمون} لا ينقصون شيئاً فلمَّا حرَّم الله تعالى الرِّبا أباح السِّلَم فقال:',NULL,'193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (290,'{يا أيها الَّذِينَ آمَنُوا إِذَا تَدَايَنْتُمْ بِدَيْنٍ إِلَى أَجَلٍ مسمى} أي: تباعيتم بدين {فاكتبوه} أمرَ الله تعالى في الحقوق المؤجَّلة بالكتابة والإِشهاد في قوله: {واشهدوا إذا تبايعتم} حفظاً منه للأموال ثمَّ نسخ ذلك بقوله: {فإن أمن بعضكم بعضاً} الآية {وليكتب بينكم} بين المُستدين والمدين {كاتب بالعدل} بالحقِّ والإِنصاف ولا يزيد في المال والأجل ولا ينقص منهما: {وَلا يَأْبَ كَاتِبٌ أَنْ يَكْتُبَ} أي: لا يمتنع من ذلك إذا أُمر وكانت هذه عزيمةً من الله واجبة على الكاتب والشَّاهد فنسخها قوله: {وَلا يضار كاتب ولا شهيد} ثمَّ قال {كما علَّمه الله فليكتب} أَيْ: كما فضَّله الله بالكتابة {وليملل الذي عليه الحق} أي: الذي عليه الدِّين يملي لأنَّه المشهود عليه فيقرُّ على نفسه بلسانه ليعلم ما عليه {ولا يَبْخَسْ منه شيئاً} أُمِرَ أَنْ يُقِرَّ بمبلغ المال من غير نقصان {فإن كان الذي عليه الحق} (أي: الدَّين) {سفيهاً} طفلاً {أو ضعيفاً} عاجزاً أحمق {أَوْ لا يَسْتَطِيعُ أَنْ يُمِلَّ هو} لخرسٍ أو لعيٍّ {فليملل وليه} وارثه أو مَنْ يقوم مقامه {بالعدل} بالصدق والحقِّ {واستشهدوا} وأشهدوا {شهيدين من رجالكم} أَيْ: من أهل ملَّتكم من الأحرار البالغين وقوله: {ممن ترضون من الشهداء} أَيْ: من أهل الفضل والدِّين {أن تضلّ أحداهما} تنسى إحداهما {فتذكر إحداهما الأخرى} الشَّهادة {ولا يأب الشهداء إذا ما دعوا} لتحمُّل الشَّهادة وأدائها {ولا تسأموا أن تكتبوه} لا يمنعكم الضَّجر والملالة أن تكتبوا ما أشهدتم عليه من الحقِّ {صغيراً أو كبيراً إلى أجله} إلى أجل الحقِّ {ذلكم} أَيْ: الكتابة {أقسط} أعدل {عند الله} في حكمه {وأقوم} أبلغ في الاستقامة {للشهادة} لأنَّ الكتاب يُذكِّر الشُّهود فتكون شهادتهم أقوم {وأدنى أن لا تَرْتَابُوا} أيْ: أقرب إلى أن لا تشكُّوا في مبلغ الحقِّ والأجل {إلاَّ أن تكون} تقع {تجارة حاضرة} أَيْ: متجرٌ فيه حاضر من العروض وغيرها ممَّا يتقابض وهو معنى قوله: {تديرونها بينكم} وذلك أنَّ ما يُخاف في النَّساء والتأجيل يؤمن في البيع يداً بيدٍ وذلك قوله: {فليس عليكم جناحٌ أن لا تكتبوها وأَشْهِدوا إذا تبايعتم} قد ذكرنا أنَّ هذا منسوخ الحكم فلا يجب ذلك {وَلا يُضَارَّ كَاتِبٌ وَلا شَهِيدٌ} نهى الله تعالى الكاتب والشَّاهد عن الضِّرار وهو أن يزيد الكاتب أو ينقص أو يحرِّف وأن يشهد الشَّاهد بما لم يُستشهد عليه أو يمتنع من إقامة الشَّهادة {وإنْ تفعلوا} شيئاً من هذا {فإنه فسوق بكم}',NULL,'193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (291,'{وَإِنْ كُنْتُمْ عَلَى سَفَرٍ وَلَمْ تَجِدُوا كَاتِبًا} الآية أمر الله تعالى عند عدم الكاتب بأخذ الرَّهن ليكون وثيقةً بالأموال وذلك قوله: {فَرِهَانٌ مقبوضة} أَيْ: فالوثيقةُ رهنٌ مقبوضةٌ {فَإِنْ أَمِنَ بَعْضُكُمْ بَعْضًا} أَيْ: لم يخف خيانته وجحوده الحقَّ {فليؤدّ الذي اؤتمن} أَيْ: أُمن عليه {أمانته وليتق الله ربه} بأداء الأمانة {ولا تكتموا الشهادة} إذا دُعيتم لإِقامتها {ومن يكتمها فإنه آثمٌ} فاجرٌ {قلبه}',NULL,'195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (292,'{لله ما في السماوات وما في الأرض} ملكا فهو مالك أعيانه {وَإِنْ تُبْدُوا مَا فِي أَنْفُسِكُمْ أَوْ تُخْفُوهُ يحاسبكم به الله} لمَّا نزل هذا جاء ناس من الصَّحابة إلى النبي صلى الله عليه وسلم فقالوا: كُلِّفنا من العمل ما لا نطيق إن أحدنا ليحدِّث نفسه بما لا يحبُّ أن يثبت في قلبه فنحن نحاسب بذلك؟ فقال النبيُّ: فلعلَّكم تقولون كما قالت بنو إسرائيل: سمعنا وعصينا وقولوا: سمعنا وأطعنا فقالوا: سمعنا وأطعنا فأنزل الله تعالى الفرج بقوله: {لا يُكَلِّفُ اللَّهُ نَفْسًا إِلا وُسْعَهَا} فنسخت هذه الآية ما قبلها وقيل: إنَّ هذا في كتمان الشَّهادة وإقامتها ومعنى قوله: {يحاسبكم به الله} يخبركم به ويُعرِّفكم إيَّاه',NULL,'196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (293,'{آمن الرسول} الآية لمَّا ذكر الله تعالى في هذه السُّورة الأحكام والحدود وقصص الأنبياء وآيات قدرته ختم السورة بذكر تصديق نبيه عليه السلام والمؤمنين بجميع ذلك {لا نفرق بين أحد} أَيْ: يقولون: لا نفرق بَيْنَ أَحَدٍ مِنْ رسله كما فعل أهل الكتاب آمنوا ببعض الرُّسل وكفروا ببعض بل نجمع بينهم في الإِيمان بهم {وقالوا سمعنا} قوله {وأطعنا} أمره {غفرانك} أَيْ: اغفر غفرانك',NULL,'196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (294,'{لا يكلف الله نفسا إلاَّ وسعها} ذكرنا أنَّ هذه الآية نسخت ما شكاه المؤمنين من المحاسبة بالوسوسة وحديث النَّفس {لَهَا مَا كَسَبَتْ} (من العمل بالطاعة) {وَعَلَيْهَا ما اكتسبت} (من العمل بالإثم) أَيْ: لا يُؤَاخَذ أحدٌ بذنب غيره {ربنا لا تؤاخذنا} أَيْ: قولوا ذلك على التَّعليم للدُّعاء ومعناه: لا تعاقبنا إن نسينا كانت بنو إسرائيل إذا نسوا شيئاً ممَّا شرع لهم عُجِّلت لهم العقوبة بذلك فأمر الله نبيَّه والمؤمنين أن يسألوه ترك مؤاخذتهم بذلك {أو أَخْطأْنا} تركنا الصَّواب: {ربنا ولا تحمل علينا إصراً} أَيْ: ثقلاً والمعنى: لا تحمل علينا أمراً يثقل {كما حملته على الذين من قبلنا} نحو ما أُمر به بنو إسرائيل من الأثقال التي كانت عليهم {رَبَّنَا وَلا تُحَمِّلْنَا مَا لا طَاقَةَ لَنَا به} أَيْ: لا تعذِّبنا بالنَّار {أنت مولانا} ناصرنا والذي تلي علينا أمورنا {فَانْصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ} في إقامة حجَّتنا وغلبتنا إيَّاهم في حربه وسائر أمورهم حتى يظهر ديننا على الدِّين كلِّه كما وعدتنا',NULL,'197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (295,'{الم}',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (296,'{اللَّهُ لا إِلَهَ إِلا هُوَ الْحَيُّ الْقَيُّومُ}',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (297,'{نزل عليك الكتابَ} أي: القرآن {بالحق} بالصِّدق في إخباره {مصدقاً لما بين يديه} مُوافقاً لما تقدَّم الخبر به في سائر الكتب {وَأَنْزَلَ التَّوْرَاةَ وَالإِنْجِيلَ}',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (298,'{من قبل هدىً للناس وأنزل الفرقان} ما فرق به بين الحقِّ والباطل يعني: جميع الكتب التي أنزلها {إِنَّ الَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ لَهُمْ عَذَابٌ شديدٌ والله عزيز ذو انتقام} ذو عقوبة',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (299,'قال تعالى {إِنَّ اللَّهَ لا يَخْفَى عَلَيْهِ شَيْءٌ فِي الأَرْضِ وَلا فِي السَّمَاءِ}',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (300,'{هو الذي يصوركم} يجعلكم على صورٍ في أرحام الأُمَّهات {كيف يشاء} ذكراً وأنثى قصيراً وطويلاً وأسود وأبيض',NULL,'198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (301,'{هُوَ الَّذِي أَنْزَلَ عَلَيْكَ الْكِتَابَ مِنْهُ آيَاتٌ محكماتٌ} وهنَّ الثَّلاث الآيات في آخر سورة الأنعام: {قُلْ تَعَالَوْا أَتْلُ} إلى آخر الآيات الثَّلاث {هنَّ أمُّ الكتاب} هنَّ أمُّ كلِّ كتاب أنزله الله تعالى على كلِّ نبيٍّ فيهنَّ كلُّ ما أحلَّ وحرَّم ومعناه: أنهنَّ أصل الكتاب الذي يُعمل عليه {وأخر} أَيْ: آياتٌ أُخر {متشابهات} يريد: التي تشابهت على اليهود وهي حروف التَّهجِّي في أوائل السُّور وذلك أنَّهم أوَّلوها على حساب الجُمَّل وطلبوا أن يستخرجوا منها مدَّة بقاء هذه الأُمَّة فاختلط عليهم واشتبه {فأمَّا الذين في قلوبهم زيغٌ} وهم اليهود الذين طلبوا علمَ أجل هذه الأمَّة من الحروف المقطَّعة {فيتبعون ما تشابه منه} من الكتاب يعني: حروف التَّهجِّي {ابتغاء الفتنة} طلب اللَّبس ليضلُّوا به جُهَّالهم {وَابْتِغَاءَ تَأْوِيلِهِ} طلب أجل أمَّة محمد صلى الله عليه وسلم {وما يعلم تأويله إلاَّ الله} يريد: ما يعلم انقضاء ملك أمَّة محمَّدٍ صلى الله عليه وسلم إلاَّ الله لأنَّ انقضاء ملكهم مع قيامِ السَّاعة ولا يعلم ذلك أحد إلاَّ الله ثمَّ ابتدأ فقال: {والراسخون في العلم} أَي: الثَّابتون فيه يعني: علماء مؤمني أهل الكتاب {يقولون آمنا به} أَيْ: بالمتشابه {كُلٌّ مِنْ عِنْدِ ربنا} المحكم والمتشابه وما علمناه وما لم نعلمه {وما يذكر إلا أولو الألباب} ما يتعَّظ بالقرآن إلاَّ ذوو العقول',NULL,'199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (302,'{ربنا} أي: ويقول الرَّاسخون في العلم {رَبَّنَا لا تُزِغْ قُلُوبَنَا} لا تُملها عن الهدى والقصد كما أزغت قلوب الذين في قلوبهم زيغ {بَعْدَ إِذْ هَدَيْتَنَا} للإِيمان بالمحكم والمتشابه من كتابك',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (303,'{ربنا إنك جامع الناس} حاشرهم {ليوم} الجزاء في يوم {لا رَيْبَ فِيهِ إِنَّ اللَّهَ لا يُخْلِفُ الميعاد} للبعث والجزاء',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (304,'{إنَّ الذين كفروا} يعني: يهود قريظة والنَّضير {لن تغني عنهم} أي: لن تنفع ولن تدفع عنهم {أموالهم} {وَلا أولادهم} يعني: التي يتفاخرون بها {من الله} من عذاب الله {شَيْئًا وَأُولَئِكَ هُمْ وَقُودُ النار} هم الذين تُوقد بهم النَّار',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (305,'{كدأب آل فرعون} كصنيع آل فرعون وفعلهم في الكفر والتَّكذيب كفرت اليهود بمحمد صلى الله عليه وسلم',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (306,'{قل للذين كفروا} يعني: يهود المدينة ومشركي مكَّة {ستغلبون وتحشرون إلى جهنم وبئس المهاد} بئس ما مُهِّد لكم',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (307,'{قد كان لكم آية} علامة تدلُّ على صدق محمَّدٍ عليه السَّلام {في فئتين} يعني: المسلمين والمشركين {التقتا} اجتمعتا يوم بدرٍ للقتال {فئةٌ تقاتل في سبيل الله} وهم المسلمون {وأخرى كافرة يرونهم مثليهم} وهم كانوا ثلاثة أمثالهم ولكنَّ الله تعالى قلَّلهم في أعينهم وأراهم على قدر ما أعلمهم أنَّهم يغلبونهم لتقوى قلوبهم وذلك أن الله عز وجل كان قد أعلم المسلمين أنَّ المائة منهم تغلب المائتين من الكفَّار {رأي العين} أَيْ: من حيث يقع عليهم البصر {والله يؤيد} يقوّي {بنصره} بالغلبة والحجَّة مَنْ يشاء {إنَّ في ذلك لعبرة} وهي الآية التي يُعبر بها من منزلة الجهل إلى العلم {لأولي الأبصار} لذوي العقول',NULL,'200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (308,'{زُيِّن للناس حبُّ الشهوات} جمع الشَّهوة وهي تَوَقَانُ النَّفس إلى الشَّيء {والقناطير المقنطرة} الأموال الكثيرة المجموعة {والخيل المسوَّمة} الرَّاعية وقيل: المُعلَّمة كالبلق وذوات الشِّياتِ وقيل: الحسان والخيل: الأفراس {والأنعام} الإِبل والبقر والغنم {والحرث} وهو ما يُزرع ويغرس ثمَّ بيَّن أنَّ هذه الأشياء متاع الدُّنيا وهي فانيةٌ زائلةٌ {واللَّهُ عنده حسن المآب} المرجع ثمَّ أعلم أنَّ خيراً من ذلك كلِّه ما أعده لأوليائه فقال',NULL,'201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (309,'{قل أؤنبئكم بِخَيْرٍ مِنْ ذَلِكُمْ} الذي ذكرت {لِلَّذِينَ اتَّقَوْا} الشِّرك {جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وَأَزْوَاجٌ مُطَهَّرَةٌ وَرِضْوَانٌ مِنَ اللَّهِ وَاللَّهُ بَصِيرٌ بالعباد}',NULL,'201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (310,'قال تعالى {الَّذِينَ يَقُولُونَ رَبَّنَا إِنَّنَا آمَنَّا فَاغْفِرْ لَنَا ذنوبنا وقنا عذاب النار}',NULL,'202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (311,'{الصابرين} على دينهم وعلى ما أصابهم {والصادقين} في نيَّاتهم {والقانتين} المطيعين لله {والمنفقين} من الحلال في طاعة الله {والمستغفرين بالأسحار} المُصلِّين صلاة الصُّبح قيل: نزلت في المهاجرين والأنصار',NULL,'202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (312,'{شهد الله} بيَّن وأظهر بما نصب من الأدلَّة على توحيده {أنَّه لا إله إلاَّ هو والملائكة} أَيْ: وشهدت الملائكة بمعنى: أقرت بتوحيد الله {وأولو العلم} هم الأنبياء والعلماء من مؤمني أهل الكتاب والمسلمين {قائماً بالقسط} أَيْ: قائماً بالعدل يُجري التَّدبير على الاستقامة في جميع الأمور',NULL,'202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (313,'{إنَّ الدين عند الله الإِسلام} افتخر المشركون بأديانهم فقال كلُّ فريقٍ: لا دين إلاَّ ديننا وهو دين الله فنزلت هذه الآية وكذَّبهم الله تعالى فقال: {إنَّ الدين عند الله الإِسلام} الذي جاء به محمد عليه السلام {وما اختلف الذين أوتوا الكتاب} أَي: اليهود لم يختلفوا في صدق نبوَّة محمد صَلَّى اللَّهُ عَلَيْهِ وسلم لما كانوا يجدونه في كتابهم {إِلا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ} يعني: النبي صلى الله عليه وسلم سمِّي علماً لأنَّه كان معلوماً عندهم بنعته وصفته قبل بعثه فلمَّا جاءهم اختلفوا فيه فآمن به بعضهم وكفر الآخرون {بغياً بينهم} طلباً للرِّياسة وحسداً له على النُّبوَّة {وَمَنْ يَكْفُرْ بِآيَاتِ اللَّهِ فَإِنَّ اللَّهَ سَرِيعُ الحساب} أَي: المجازاة له على كفره',NULL,'202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (314,'{فإن حاجوك} أَيْ: جادلوك {فقل أسلمتُ وجهي لله} أَيْ: أخلصت عملي لله وانقدت له {ومن اتبعني} يعني: المهاجرين والأنصار {وقل للذين أوتوا الكتاب والأميين} يعني: العرب {أأسلمتم} استفهامٌ معناه الأمر أَيْ: أسلموا وقوله: {عليك البلاغ} أَي: التَّبليغ وليس عليك هداهم {والله بصيرٌ بالعباد} أيْ: بمَنْ آمن بك وصدَّقك ومَنْ كفر بك وكذَّبك وكان هذا قبل أنْ أُمر بالقتال',NULL,'203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (315,'{إِنَّ الَّذِينَ يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بغير حق} قد مضى تفسيره في سورة البقرة وقوله: {وَيَقْتُلُونَ الَّذِينَ يأمرون بالقسط من الناس} قال [رسول الله صلى الله عليه وسلم: قتلت بنو إسرائيل ثلاثةً وأربعين نبياً من أوَّل النَّهار في ساعةٍ واحدةٍ فقام مائةٌ واثنا عشر رجلاً من عبَّاد بني إسرائيل فأمروا مَنْ قتلهم بالمعروف ونهوهم عن المنكر فَقُتلوا جميعاً من آخر النَّهار في ذلك اليوم فهم الذين ذكرهم الله في هذه الآية] وهؤلاء الذين كانوا في عصر النبي صلى الله عليه وسلم كانوا يتولَّونهم فهم داخلون في جملتهم',NULL,'203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (316,'{أولئك الذين حبطت أعمالهم} بطلت أعمالهم التي يدَّعونها من التَّمسُّك بالتَّوراة وإقامة شرع موسى عليه السَّلام {في الدنيا} لأنَّها لم تحقن دماءَهم وأموالهم {و} في {الآخرة} لأنهم لم يستحقوا بها ثواباً',NULL,'203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (317,'{أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الكتاب} يعني: اليهود {يدعون إلى كتاب الله ليحكم بينهم} وذلك أنَّهم أنكروا آية الرَّجم من التَّوراة وسألوا رسول الله صلى الله عليه وسلم عن حدِّ المحصنين إذا زنيا فحكم بالرَّجم فقالوا: جُرْتَ يا محمد فقال: بيني وبينكم التَّوراة ثمَّ أتوا بابن صوريا الأعور فقرأ التَّوراة فلمَّا أتى على آية الرَّجم سترها بكفِّه فقام ابن سلاَّم فرفع كفَه عنها وقرأها على رسول الله صلى الله عليه وسلم وعلى اليهود فغضب اليهود لذلك غضباً شديداً وانصرفوا فأنزل الله تعالى هذه الآية {ثمَّ يتولى فريق منهم} يعني: العلماء والرؤساء {وهم معرضون}',NULL,'204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (318,'{ذلك} أَْيْ: ذلك الإِعراض عن حكمك بسبب اغترارهم حيث قَالُوا: {لَنْ تَمَسَّنَا النَّارُ إِلا أَيَّامًا مَعْدُودَاتٍ وَغَرَّهُمْ فِي دِينِهِمْ مَا كَانُوا يَفْتَرُونَ} افتراؤهم وهو قوله: {لن تمسنا النار} وقد مضى هذا في سورة البقرة',NULL,'204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (319,'{فكيف إذا جمعناهم} أَيْ: فكيف يكون حالهم إذا جمعناهم {ل} جزاء {يومٍ لا ريبَ فيه ووفيت كلُّ نفس} جزاء {ما كسبت وهم لا يظلمون} بنقصان حسناتهم أو زيادة سيئاتهم',NULL,'204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (320,'{قل اللهم مالك الملك} لمَّا فتح رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم مكَّة ووعد أُمَّته ملك فارس والروم قالت المنافقون واليهود: هيهات هيهات (الفارس والروم أعزُّ وأمنع من أن يُغْلَبَ على بلادهم) فأنزل الله تعالى هذه الآية وقوله: {تُؤْتِي الْمُلْكَ مَنْ تَشَاءُ} محمداً وأصحابه {وَتَنْزِعُ الملك ممن تشاء} أبي جهلٍ وصناديد قريشٍ {وتعزُّ من تشاء} المهاجرين والأنصار {وتذلُّ مَنْ تشاء} أبا جهلٍ وأصحابه حتى حُزَّت رؤوسهم وأُلْقُوا في القليب {بيدك الخير} أَيْ: عزُّ الدُّنيا والآخرة وأراد: الخير والشَّرَّ فاكتفى بذكر الخير لأنَّ الرغبة إليه في فعل الخير بالعبد دون الشَّر',NULL,'205',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (321,'{تولج الليل في النهار} تُدخل اللَّيل في النَّهار أَيْ: تجعل ما نقص من أحدهما زيادةً في الآخر {وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الحيِّ} تخرج الحيوان من النُّطفة وتخرج النُّطفة من الحيوان وتخرج المؤمن من الكافر والكافر من المؤمن {وَتَرْزُقُ من تشاء بغير حساب} بغير تقتيرٍ وتضييقٍ',NULL,'205',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (322,'{لا يَتَّخِذِ الْمُؤْمِنُونَ الْكَافِرِينَ أَوْلِيَاءَ مِنْ دُونِ المؤمنين} أَيْ: أنصاراً وأعواناً من غير المؤمنين وسواهم نزلت في قوم من المؤمنين كانوا يُباطنون اليهود (أي: يألفونهم) ويوالونهم {ومَنْ يفعل ذلك} الاتخِّاذ {فليس من الله في شيء} أَيْ: من دين الله أَيْ: قد برئ من الله وفارق دينه ثمَّ استثنى فقال: {إِلا أَنْ تتقوا منهم تقاة} أَيْ: تقيَّة هذا في المؤمن إذا كان في قومٍ كفَّارٍ وخافهم على ماله ونفسه فله أن يُخالفهم ويُداريهم باللِّسان وقلبُه مطمئنٌّ بالإِيمان دفعاً عن نفسه قال ابنُ عبَّاسٍ: يريد مدارةً ظاهرةً {ويحذركم الله نفسه} أَيْ: يُخَوِّفكم الله على موالاة الكفار عذاب نفسه (يريد: عذابه وخصَّصه بنفسه تعظيماً له) فلمَّا نهى عن ذلك خوَّف وحذَّر عن إبطان موالاتهم فقال:',NULL,'205',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (323,'{قُلْ إِنْ تُخْفُوا مَا فِي صُدُورِكُمْ أَوْ تبدوه} من ضمائركم في موالاتهم وتركها {يعلمه الله ويعلم ما في السماوات وما في الأرض} إتمامٌ للتَّحذير لأنَّه إذا كان لا يخفى عليه شيء فيهما فكيف يخفى عليه الضَّمير؟ {وَاللَّهُ عَلَى كُلِّ شَيْءٍ قَدِيرٌ} تحذيرٌ من عقاب مَنْ لا يعجزه شيء',NULL,'206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (324,'{يوم تجد كلُّ نفسٍ} أَيْ: ويحذّركم الله عذاب نفسه يوم تجد أَيْ: ذلك اليوم وقوله: {ما عملت من خير محضراً} أَيْ: جزاء ما عملت بما ترى من الثَّواب {وَمَا عَمِلَتْ مِنْ سُوَءٍ تَوَدُّ لَوْ أَنَّ بينها وبينه أمداً بعيداً} غاية بعيدةً كما بين المشرق والمغرب',NULL,'207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (325,'{قل} أي: للكفَّار {إن كنتم تحبون الله} [وقف النبي صلى الله عليه وسلم على قريشٍ وهم يسجدون للأصنام فقال: يا معشر قريش واللَّهِ لقد خالفتم ملَّة أبيكم إبراهيم فقالت قريش: إنَّما نعبد هذه حبّاً لله ليقرِّبونا إلى الله] فأنزل الله تعالى: {قل} يا محمد {إِنْ كُنْتُمْ تُحِبُّونَ اللَّهَ} وتعبدون الأصنام لتقرِّبكم إليه {فَاتَّبِعُونِي يُحْبِبْكُمُ اللَّهُ} فأنا رسوله إليكم وحجَّته عليكم ومعنى محبَّة العبد لله سبحانه إرادته طاعته وإيثاره أمره ومعنى محبَّة الله العبد إرادته لثوابه وعفوه عنه وإنعامه عليه',NULL,'207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (326,'{قل أطيعوا الله والرسول فإن تولوا} عن الطَّاعة {فإنَّ الله لا يحب الكافرين} لا يغفر لهم ولا يثني عليهم',NULL,'207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (327,'{إنَّ الله اصطفى آدم} بالنُّبوَّة والرِّسالة {ونوحاً وآل إبراهيم} يعني: إسماعيل وإسحاق ويعقوب والأسباط {وآل عمران} موسى وهارون {على العالمين} على عالمي زمانهم',NULL,'207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (328,'{ذريةً} أَي: اصطفى ذريَّةً {بعضها من بعض} أَيْ: من ولد بعض لأنَّ الجميع ذريَّة آدم ثمَّ ذريَّة نوح {والله سميع} لما تقوله الذُّريَّة المصطفاة {عليمٌ} بما تضمره فلذلك فضَّلها على غيرها',NULL,'208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (329,'{إذ قالت امرأة عمران} وهي حنَّة أمُّ مريم: {إني نذرت لك ما في بطني} أيْ: أوجبتُ على نفسي أن أجعل ما في بطني {محرَّراً} عتيقاً خالصاً لله خادماً للكنيسة مفرَّغاً للعبادة ولخدمة الكنيسة وكان على أولادهم فرضاً أن يُطيعوهم في نذرهم فتصدَّقت بولدها على بيت المقدس',NULL,'208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (330,'{فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ إِنِّي وَضَعْتُهَا أُنْثَى} اعتذرت ممَّا فعلت من النَّذر لمَّا ولدت أنثى {وليس الذكر كالأنثى} في خدمة الكنيسة لما يلحقها من الحيض والنِّفاس {وَإِنِّي أُعِيذُهَا بِكَ} أَيْ: أمنعها وأجيرها {من الشيطان الرجيم} الملعون المطرود',NULL,'208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (331,'{فتقبلها ربُّها بقبول حسن} أَيْ: رضيها مكان المحرَّر الذي نذرته {وأنبتها نباتاً حسناً} في صلاحٍ وعفَّةٍ ومعرفةٍ بالله وطاعةٍ له {وكفلها زكريا} ضمن القيام بأمرها فبنى لها محراباً في المسجد لا يرتقى إليه إلاَّ بسلَّم والمحراب: الغرفة وهو قوله: {كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَ وَجَدَ عِنْدَهَا رزقاً} أَيْ: فاكهة الشِّتاء في الصَّيف وفاكهة الصَّيف في الشِّتاء تأتيها به الملائكة من الجنَّة فلمَّا رأى زكريا ما أُوتيت مريم من (فاكهةالصيف في الشتاء وفاكهةالشتاء في الصيف) على خلاف مجرى العادة طمع في رزق الولد من العاقر على خلاف العادة وذلك قوله:',NULL,'208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (332,'{هنالك} أَيْ: عند ذلك {دَعَا زَكَرِيَّا رَبَّهُ قال رب هب لي من لدنك} أَيْ: من عندك {ذريةً طيبةً} أَيْ: نسلاً مباركاً تقيّاً فأجاب الله دعوته وبعث إليه الملائكة مبشرين وهو قوله:',NULL,'209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (333,'{فَنَادَتْهُ الْمَلائِكَةُ وَهُوَ قَائِمٌ يُصَلِّي فِي الْمِحْرَابِ أَنَّ اللَّهَ يُبَشِّرُكَ بِيَحْيَى مُصَدِّقًا بِكَلِمَةٍ مِنَ الله} أَيْ: مُصدِّقاً بعيسى أنَّه روح الله وكلمته وسُمِّي عيسى كلمة الله لأنَّه حدث عند قوله: {كُنْ} فوقع عليه اسم الكلمة لأنَّه بها كان {وسيداً} وكريماً على ربِّه {وحصوراً} وهو الذي لا يأتي النِّساء ولا أرب له فيهنَّ',NULL,'209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (334,'{قال} زكريا لمَّا بُشِّر بالولد: {ربِّ أنى يكون لي غلامٌ} أَيْ: على أيِّ حالٍ يكون ذلك؟ أَتردُّني إلى حال الشَّباب وامرأتي أَمْ مع حال الكبر؟ {وقد بلغني الكبر} أَيْ: بَلغْتُه لأنَّه كان ذلك اليوم ابن عشرين ومائة سنةٍ {وامرأتي عاقر} لا تلد وكانت بنتَ ثمانٍ وتسعين سنةً قيل له: {كذلك} أَيْ: مثل ذلك من الأمر وهو هبة الولد على الكبر يفعل الله ما يشاء فسبحان مَنْ لا يعجزه شيء فلمَّا بُشِّر بالولد سأل الله علامةً يعرف بها وقت حمل امرأته وذلك قوله',NULL,'209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (335,'{قال رب اجعل لي آية} فقال الله تعالى: {آيَتُكَ أن لا تكلم الناس ثلاثة أيام} جعل الله تعالى علامة حمل امرأته أَنْ يُمسك لسانه فلا يقدر أنْ يُكلِّم النَّاس ثلاثة أيَّامٍ {إلاَّ رمزاً} أَيْ: إيماءً بالشَّفتين والحاجبين والعينين وكان مع ذلك يقدر على التَّسبيح وذكر الله وهو قوله: {وَاذْكُرْ رَبَّكَ كثيراً وسبِّح} أَيْ: وصَلِّ {بالعشي} وهو آخر النَّهار {والإِبكار} ما بين طلوع الفجر إلى الضُّحى',NULL,'209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (336,'{وإذ قالت الملائكة} أَيْ: جبريل عليه السَّلام وحده: {يا مريم إن الله اصطفاك} أَيْ: بما لطف لك حتى انقطعت إلى طاعته {وطهرك} من ملامسة الرِّجال والحيض {واصطفاك على نساء العالمين} على عالمي زمانك',NULL,'210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (337,'{يا مريم اقنتي لربك} قومي للصَّلاة بين يدي ربكِّ فقامت حتى سالت قدماها قيحاً {واسجدي واركعي} أَي: ائتي بالرُّكوع والسُّجود والواو لا تقتضي الترتيب {مع الراكعين} أَي: افعلي كفعلهم وقال: {مع الراكعين} ولم يقل: مع الرَّاكعات لأنَّه أعمُّ',NULL,'210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (338,'{ذلك} أَيْ: ما قصصنا عليك من حديث زكريا ومريم {من أنباء الغيب} أَيْ: من أخباره {نوحيه إليك} أَيْ: نلقيه {وما كنت لديهم} فتعرف ذلك {إذ يلقون أقلامهم} وذلك أنَّ حنَّة لمَّا ولدت مريم أتت بها سدنة بيت المقدس وقالت لهم: دونكم هذه النَّذيرة فتنافس فيها الأحبار حتى اقترعوا عليها فخرجت القرعة لزكريا فذلك قوله: {إذ يلقون أقلامهم} أَيْ: قداحهم التي كانوا يقترعون بها لينظروا أيُّهم تجب له كفالة مريم',NULL,'210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (339,'{إذ قالت الملائكة} يعني: جبريل عليه السَّلام: {يَا مَرْيَمُ إِنَّ اللَّهَ يُبَشِّرُكِ بِكَلِمَةٍ مِنْهُ} يعني: عيسى عليه السَّلام لأنَّه في ابتداء أمره كان كلمة من الله وكُوِّن بكلمة منه أَيْ: من الله {اسمه المسيح} وهو معرَّب من مشيحا بالسِّريانية لقبٌ لعيسى ثمَّ فَسَّر وبيَّن من هو فقال: {عيسى ابن مريم وجيهاً} أَيْ: ذا جاهٍ وشرفٍ وقدرٍ {في الدنيا والآخرة ومن المقربين} إلى ثواب الله وكرامته',NULL,'210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (340,'{ويكلم الناس في المهد} صغيراً {وكهلاً} أَيْ: يتكلَّم بالنُّبوَّة كهلاً وقيل: بعد نزوله من السَّماء {ومن الصالحين} يريد: مثل موسى ويعقوب وإسحاق وإبراهيم عليهم السِّلام',NULL,'211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (341,'{قالت} مريم مُتعجِّبةً: {أنى يكون لي ولد} من غير مسيس بَشَرٌ؟ {قَالَ كَذَلِكِ اللَّهُ يَخْلُقُ مَا يَشَاءُ} مثل ذلك من الأمر وهو خلق الولد من غير مسيس بشرٍ أَي: الأمر كما تقولين ولكنَّ الله {إذا قضى أمراً} ذُكر في سورة البقرة (إلى آخرها)',NULL,'211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (342,'{ويعلمه الكتاب} أراد: الكتابة والخطَّ',NULL,'211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (343,'وقوله: {ورسولاً إلى بني إسرائيل} أَيْ: ويجعله رسولاً إلى بني إسرائيل {أني} أَيْ: بأنِّي {قد جئتكم بآية من ربكم} وهو {أني أخلق} أَيْ: أُقدِّر وأصور {كهيئة الطير} كصورته {وأبرئ الأكمه} وهو الذي وُلد أعمى {والأبرص} أَيْ: الذي به وَضَحٌ أي: بياضٌ {وأُنبئكم بما تأكلون} في غدوكم {وما} كم {تدخرون} لباقي يومكم',NULL,'211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (344,'{ومصدِّقاً} أَيْ: وجئتكم مُصدِّقاً {لِمَا بَيْنَ يَدَيَّ} أَي: الكتاب الذي أنزل من قبلي {ولأُحلَّ لكم بعض الذي حرّم عليكم} أحلَّ لهم على لسان المسيح لحوم الإِبل والثُّروب وأشياء من الطَّير والحيتان ممَّا كان محرَّماً في شريعة موسى عليه السَّلام {وجئتكم بآية من ربكم} أَيْ: ما كان معه من المعجزات الدَّالَّة على رسالته ووحَّدَ لأنَّها كلَّها جنسٌ واحد في الدلالة',NULL,'212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (345,'قال تعالى {إِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ هَذَا صِرَاطٌ مُسْتَقِيمٌ}',NULL,'212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (346,'{فلما أَحسَّ عيسى} علم ورأى {منهم الكفر} وذلك أنَّهم أرادوا قتله حين دعاهم إلى الله تعالى فاستنصر عليهم و {قَالَ مَنْ أَنْصَارِي إِلَى الله} أَيْ: مع الله وفي ذات الله {قال الحواريون} وكانوا قصَّارين يحوّرون الثِّياب أَيْ: يُبيِّضونها آمَنوا بعيسى واتَّبعوه: {نحن أنصار الله} أنصار دينه {آمنا بالله واشهد} يا عيسى {بأنا مسلمون} وقوله:',NULL,'212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (347,'{فَاكْتُبْنَا مَعَ الشَّاهِدِينَ} مع الذين شهدوا للأنبياء بالصِّدق والمعنى: أَثْبِتْ أسماءنا مع أسمائهم لنفوز بمثل ما فازوا',NULL,'212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (348,'{ومكروا} سعوا في قتله بالمكر {ومكر اللَّهُ} جازاهم على مكرهم بإلقاء شبه عيسى على مَنْ دلَّ عليه حتى أحذ وصُلب {والله خير الماكرين} أفضل المجازين بالسيئة العقوبة لأنَّه لا أحد أقدر على ذلك منه',NULL,'212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (349,'{إذ قال الله يا عيسى} والمعنى: ومكر الله إِذْ قَالَ اللَّهُ يَا عِيسَى: {إِنِّي مُتَوَفِّيكَ} أَيْ: قابضك من غير موتٍ وافياً تاماً أَيْ: لم ينالوا منك شيئاً {ورافعك إليَّ} أَيْ: إلى سمائي ومحل كرامتي فجعل ذلك رفعاً إليه للتَّفخيم والتَّعظيم كقوله: {إني ذاهبٌ إلى ربي} وإنَّما ذهب إلى الشَّام والمعنى: إلى أمر ربِّي {ومطهِّرك من الذين كفروا} أَيْ: مُخرجك من بينهم {وجاعل الذين اتبعوك} وهم أهل الإِسلام من هذه الأمَّة اتَّبعوا دين المسيح وصدَّقوه بأنَّه رسول الله فواللَّهِ ما اتَّبعه مَنْ دعاه ربّاً {فوق الذين كفروا} بالبرهان والحُجَّة والعز والغلبة',NULL,'213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (350,'قال تعالى {فَأَمَّا الَّذِينَ كَفَرُوا فَأُعَذِّبُهُمْ عَذَابًا شَدِيدًا فِي الدنيا والآخرة وما لهم من ناصرين}',NULL,'213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (351,'قال تعالى {وَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ والله لا يحبُّ الظالمين}',NULL,'213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (352,'{ذلك} أَيْ: ما تقدَّم من النَّبأ عن عيسى ومريم عليهما السَّلام {نتلوه عليك} نخبرك به {من الآيات} أَي: العلامات الدَّالَّة على رسالتك لأنَّها أخبارٌ من أمورٍ لم يشاهدها ولم يقرأها من كتاب {والذكر الحكيم} أَي: القرآن المحكم من الباطل وقيل: الحكيم: الحاكم بمعنى المانع من الكفر والفساد',NULL,'213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (353,'{إنَّ مثل عيسى} الآية نزلت في وفد نجران حين قالوا للنبي صلى الله عليه وسلم: هل رأيت ولداً من غير ذَكَرٍ؟ فاحتجَّ الله تعالى عليهم بآدم عليه السَّلام أَيْ: إنَّ قياس خلق عيسى عليه السَّلام من غير ذَكَرٍ كقياس خلق آدم عليه السَّلام بل الشأن فيه عجب لأنَّه خُلق من غير ذكر ولا أنثى وقوله: {عند الله} أَيْ: في الإنشاء والخلق وتَمَّ الكلام عند قوله: {كمثل آدم} ثمَّ استأنف خبراً آخر من قصَّة آدم عليه السَّلام فقال: {خلقه من تراب} أَيْ: قالباً من تراب {ثُمَّ قَالَ لَهُ كُنْ} بشراً {فَيَكُونُ} بمعنى فكان',NULL,'213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (354,'{الحق من ربك} أي: الذي أَنْبَأْتُكَ من خبر عيسى الْحَقُّ مِنْ رَبِّكَ {فَلا تَكُنْ مِنَ الْمُمْتَرِينَ} أي: من الشاكين ز الخطاب للنبيِّ عليه السَّلام والمرادُ به نهيُ غيره عن الشَّكِّ',NULL,'214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (355,'{فمن حاجَّك} خاصمك {فيه} في عيسى {من بعد ما جاءك من العلم} بأنَّ عيسى عبد الله ورسوله {فقل تعالوا} هلمُّوا {ندع أبناءَنا وأبناءَكم} لمَّا احتجَّ الله تعالى على النَّصارى من طريق القياس بقوله: {إنّ مثل عيسى عند الله} الآية أمر النبي صلى الله عليه وسلم أن يحتجَّ عليهم من طريق الإِعجاز فلمَّا نزلت هذه الآية دعا رسولُ الله صلى الله عليه وسلم وفد نجران إلى المباهلة وهي الدُّعاء على الظَّالم من الفريقين وخرج رسول الله صلى الله عليه وسلم ومعه الحسن والحسين وعليٌّ وفاطمة عليهم السَّلام وهو يقول لهم: إذا أنا دعوتُ فأمِّنوا فذلك قوله: {ندع أبناءَنا} الآية وقوله: {وأنفسنا وأنفسكم} يعني: بني العمِّ {ثمَّ نبتهل} نتضرع في الدُّعاء وقيل: ندعو بالبهلة وهي اللعنة فندعو الله باللَّعنةِ على الكاذبين فلم تُجبه النَّصارى إلى المباهلة خوفاً من اللَّعنه وقَبِلوا الجزية',NULL,'214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (356,'{إن هذا} الذي أوحيناه إليك {لهو القصص الحق} الخبر الصِّدق',NULL,'215',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (357,'{فإن تولوا} أعرضوا عمَّا أتيت به من البيان {فإنَّ الله} يعلمُ مَنْ يفسد من خلقه فيجازيه على ذلك',NULL,'215',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (358,'{قل يا أهل الكتاب} يعني: يهود المدينة ونصارى نجران {تعالوا إلى كلمة سواءٍ} معنى الكلمة: كلامٌ فيه شرحُ قصَّةٍ {سواءٍ} عدلٍ {بيننا وبينكم} ثمَّ فسر الكلمة فقال: {أن لا نَعْبُدَ إِلا اللَّهَ وَلا نُشْرِكَ بِهِ شَيْئًا} أَيْ: لا نعبد معه غيره {وَلا يَتَّخِذَ بَعْضُنَا بعضاً أرباباً من دون الله} كما اتَّخذت النَّصارى عيسى وبنو إسرائيل عزيزا وقيل: لا نطيع أحداً في معصية الله كما قال الله في صفتهم لمَّا أطاعوا في معصيته علماءهم: {اتخذوا أحبارهم} الآية {فإن تولوا} أعرضوا عن الإِجابة {فقولوا اشهدوا بأنا مسلمون} مُقِرِّون بالتَّوحيد',NULL,'215',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (359,'{يا أهل الكتاب لم تحاجون في إبراهيم} نزلت لمَّا تنازعت اليهود والنَّصارى مع رسول الله صلى الله عليه وسلم في إبراهيم عليه السَّلام فقالت اليهود: ما كان إبراهيم إلاَّ يهوديَّاً وقالت النَّصارى: ما كان إلاَّ نصرانيَّاً وقوله: {وَمَا أُنْزِلَتِ التَّوْرَاةُ وَالإِنْجِيلُ إِلا مِنْ بعده} أَيْ: إِنَّ اليهوديَّة والنَّصرانيَّة حدثتا بعد نزول الكتابين وإنَّما نزلا بعد موته بزمانٍ طويلٍ {أفلا تعقلون} فساد هذه الدَّعوى',NULL,'216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (360,'{ها أنتم} أَيْ: أنتم {هؤلاء} أًيْ: يا هؤلاء {حاججتم} جادلتم وخاصمتم {فيما لكم به علم} يعني: ما وجدوه في كتبهم وأُنزل عليهم بيانه وقصَّته {فَلِمَ تُحَاجُّونَ فِيمَا ليس لكم به علم} من شأن إبراهيم عليه السَّلام وليس في كتابكم أنَّه كان يهوديَّاً أو نصرانيَّاً {والله يعلمُ} شأن إبراهيم {وَأَنْتُمْ لا تعلمون} ثمَّ بيَّن حاله فقال:',NULL,'216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (361,'{مَا كَانَ إِبْرَاهِيمُ يَهُودِيًّا وَلا نَصْرَانِيًّا وَلَكِنْ كان حنيفاً مسلماً} الآية ثمَّ جعل المسلمين أحقَّ النَّاس به فقال:',NULL,'216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (362,'{إنَّ أولى الناس بإبراهيم} أَيْ: أقربهم إليه وأحقَّهم به {للذين اتبعوه} على دينه وملَّته {وهذا النبيُّ} محمد صلى الله عليه وسلم {والذين آمنوا} أَيْ: فهم الذين ينبغي أن يقولوا: إنَّا على دين إبراهيم عليه السَّلام',NULL,'216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (363,'{وَدَّتْ طَائِفَةٌ مِنْ أَهْلِ الْكِتَابِ لَوْ يُضِلُّونَكُمْ} أراد اليهود أن يستزلُّوا المسلمين عن دينهم ويردُّوهم إلى الكفر فنزلت هذه الآية {وَمَا يُضِلُّونَ إِلا أَنْفُسَهُمْ} لأنَّ المؤمنين لا يقبلون قولهم فيحصل الإِثم عليهم بتمنِّيهم إِضلال المؤمنين {وما يشعرون} أَنَّ هذا يضرُّهم ولا يضرُّ المؤمنين',NULL,'217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (364,'{يا أهل الكتاب لم تكفرون بآيات الله} أَيْ: القرآن {وأنتم تشهدون} بما يدلُّ على صحَّته من كتابكم لأنَّ فيه نعتَ محمَّدٍ عليه السَّلام وذكره',NULL,'217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (365,'{يا أهل الكتاب لم تلبسون} ذُكر في سورة البقرة',NULL,'217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (366,'{وقالت طائفة من أهل الكتاب} أي: وذلك أنَّ جماعةً من اليهود قال بعضهم لعبض: أظهروا الإِيمان بمحمَّدٍ والقرآنِ في أوَّل النَّهار وارجعوا عنه في آخر النهار فإنَّه أحرى أن ينقلب أصحابه عن دينه ويشكُّوا إذا قلتم: نظرنا في كتابكم فوجدنا محمَّداً ليس بذاك فأطلع الله نبيَّه عليه السَّلام على سرِّ اليهود ومكرهم بهذه الآية',NULL,'217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (367,'{ولا تؤمنوا} هذا حكايةٌ من كلام اليهود بعضهم لبعض قالوا: لا تُصدِّقوا ولا تُقِرّوا ب {أَنْ يُؤْتَى أَحَدٌ مِثْلَ مَا أُوتِيتُمْ} من العلم والحكمة والكتاب والحجَّة والمنِّ والسَّلوى والفضائل والكرامات {إلاَّ لمن تبع دينكم} اليهوديَّة وقام بشرائعه وقوله: {قل إنَّ الهدى هدى الله} اعتراضٌ بين المفعول وفعله وهو من كلام الله تعالى وليس من كلام اليهود ومعناه: إنَّ الدِّين دين الله وقوله: {أو يحاجُّوكم} عطف على قوله: {أَنْ يُؤْتَى} والمعنى: ولا تؤمنوا بأن يحاجُّوكم عند ربكم لأنَّكم أصحُّ ديناً منهم فلا يكون لهم الحجَّة عليكم فقال الله تعالى: {قل إنَّ الفضل بيد الله} أَيْ: ما تفضَّل الله به عليك وعلى أُمتِّك',NULL,'217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (368,'{يختصُّ برحمته} بدينه الإِسلام {من يشاء والله ذو الفضل} على أوليائه {العظيم} لأنَّه لا شيءَ أعظمُ عند الله من الإِسلام ثمَّ أخبر عن اختلاف أحوالهم في الأمانة والخيانة بقوله:',NULL,'218',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (369,'{وَمِنْ أَهْلِ الْكِتَابِ مَنْ إِنْ تَأْمَنْهُ بقنطارٍ يؤدِّه إليك} يعني: عبد الله بن سلام أُودع ألفاً ومائتي أوقية من ذهب فأدَّى الأمانة فيه إلى مَنْ ائتمنه {وَمِنْهُمْ مَنْ إِنْ تَأْمَنْهُ بِدِينَارٍ لا يُؤَدِّهِ إليك} يعني: فنحاص بن عازوراء أودع ديناراً فخانه {إلاَّ ما دمت عليه قائماً} على رأسه بالاجتماع معه فإن أنظرته وأخَّرته أنكر {ذلك} أَيْ: الاستحلال والخيانة {بأنَّهم} يقولون: {ليس علينا} فيما أصبنا من أموال العرب شيءٌ لأنَّهم مشركون فالأميُّون في هذه الآية العرب كلُّهم ثم كذبهم الله تعالى في هذا فقال {ويقولون على الله الكذب} لأنَّهم ادَّعوا أنَّ ذلك في كتابهم وكذبوا فإنَّ الأمانة مؤدَّاة في كلِّ شريعة {وهم يعلمون} أَنَّهم يكذبون ثمَّ ردَّ عليهم قولهم: {ليس علينا في الأُمييِّن سبيل} بقوله:',NULL,'218',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (370,'{بلى} أَيْ: بلى عليهم سبيل (في ذلك) ثمَّ ابتدأ فقال: {مَنْ أوفى بعهده} أَيْ: بعهد الله الذي عهد إليه في التَّوراة من الإِيمان بمحمدٍ عليه السَّلام والقرآن وأَدَّى الأمانة واتَّقى الكفر والخيانة ونَقْضَ العهد {فَإِنَّ الله يحب المتقين} أََيْ: مَنْ كان بهذه الصفة',NULL,'219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (371,'{إنَّ الذين يشترون بعهد الله} نزلت في رجلين اختصما إلى النبي صَلَّى الله عليه وسلم في ضَيعةٍ فهمَّ المدَّعَى عليه أن يحلف فنزلت هذه الآية فنكل (المُدَّعى عليه) عن اليمين وأقرَّ بالحقِّ ومعنى {يشترون} يستبدلون {بعهد الله} بوصيته للمؤمنين أن لا يحلفوا كاذبين باسمه {وأيمانهم} جميع اليمين وهو الحلف {ثمناً قليلأً} من الدُّنيا {أولئك لا خلاق لهم في الآخرة} أَيْ: لا نصيب لهم فيها {ولا يكلِّمهم الله} بكلامٍ يسرُّهم {ولا ينظر إليهم} بالرَّحمة وأكثر المفسرين على أنَّ الآية نزلت في اليهود وكتمانهم أمر محمد صلى الله عليه وسلم وإيمانهم الذي بدَّلوه من صفة محمد عليه السلام هو الحقُّ في التَّوراة والدَّليل على صحَّة هذا قوله:',NULL,'219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (372,'{وإنَّ منهم} أَيْ: من اليهود {لفريقاً يلوون ألسنتهم بالكتاب} يحرِّفونه بالتَّغيير والتَّبديل والمعنى: يلوون ألسنتهم عن سنن الصَّواب بما يأتونه به من عند أنفسهم {لتحسبوه} أَيْ: لتحسبوا ما لووا ألسنتهم به {من الكتاب}',NULL,'219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (373,'{ما كان لبشرٍ} الآية لمَّا ادَّعت اليهود أنَّهم على دين إبراهيم عليه السَّلام وكذَّبهم الله تعالى غضبوا وقالوا: ما يرضيك منَّا يا محمد إلاَّ أَنْ نتَّخذك ربّاً [فقال رسول الله صلى الله عليه وسلم: معاذَ الله أَنْ نأمر بعبادة غير الله] ونزلت هذه الآية {ما كان لبشر} أن يجمع بين هذين: بين النبوَّة وبين دُعاء الخلق إلى عبادة غير الله {ولكن} يقول: {كونوا ربانيين} الآية أَيْ: يقول: كونوا معلِّمي الناس بعلمكم ودرسكم علِّموا النَّاس وبيِّنوا لهم وكذا كان يقول النبي صلى الله عليه وسلم لليهود لأنَّهم كانوا أهل كتاب يعلمون ما لا تعلمه العرب',NULL,'220',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (374,'{وَلا يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلائِكَةَ وَالنِّبِيِّينَ أَرْبَابًا} كما فعلت النَّصارى والصَّابئون {أَيَأْمُرُكُمْ بِالْكُفْرِ} استفهامٌ معناه الإِنكار أَيْ: لا يفعل ذلك {بعد إذ أنتم مسلمون} بعد إسلامكم',NULL,'220',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (375,'{وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ النَّبِيِّينَ لَمَا آتَيْتُكُمْ من كتاب} ما ها هنا للشرط والمعنى: لئن آتيتكم شيئاً من كتاب وحكمة ومهما آتيتكم {ثُمَّ جَاءَكُمْ رَسُولٌ مُصَدِّقٌ لِمَا مَعَكُمْ لَتُؤْمِنُنَّ به} ويريد بميثاق النَّبييِّن عهدهم ليشهدوا لمحمد عليه السَّلام أنَّه رسول الله صلى الله عليه وسلم وهو قوله: {ثمَّ جاءكم رسول مصدق لما معكم} يريد محمداً {لتؤمننَّ به ولتنصرنَّه} أَيْ: إن أدركتموه ولم يبعث الله بينا إلاَّ أخذ عليه العهد في محمد عليه السلام وأمره بأنْ يأخذ العهد على قومه لَيُؤمننَّ به ولئنْ بُعث وهم أحياءٌ لينصرنَّه وهذا احتجاجٌ على اليهود قوله: {أأقررتم} أَيْ: قال الله للنَّبييِّن: أقررتم بالإِيمان به والنُّصرة له {وَأَخَذْتُمْ عَلَى ذَلِكُمْ إِصْرِي} أَيْ: قبلتم عهدي؟ {قالوا أقررنا قال فاشهدوا} أَي: على أنفسكم وعلى أتباعكم {وَأَنَا مَعَكُمْ مِنَ الشاهدين} عليكم وعليهم',NULL,'220',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (376,'{فَمَن تولى} أعرض من {بعد ذلك} بعد أخذ الميثاق وظهور آيات النبيِّ صَلَّى الله عليه وسلم {فأولئك هم الفاسقون} الخارجون عن الإيمان',NULL,'221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (377,'{أفغير دين الله يبغون} بعد أخذ الميثاق عليهم بالتَّصديق بمحمَّد عليه السَّلام {وله أسلم مَنْ في السماوات والأرض طوعا} الملائكة والمسلون {وكرهاً} الكفَّار في وقت البأس {وإليه يُرجعون} وعيدٌ لهم أَيْ: أيبغون غير دين الله مع أنَّ مرجعهم إليه؟',NULL,'221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (378,'{قل آمنا بالله} أُمِرَ النبي صلى الله عليه وسلم أن يقول: آمنَّا بالله وبجميع الرُّسل من غير تفريقٍ بينهم في الإِيمان كما فعلت اليهود والنَّصارى ونظير هذه الآية قد مضى في سورة البقرة',NULL,'221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (379,'قال تعالى {وَمَنْ يَبْتَغِ غَيْرَ الإِسْلامِ دِينًا فَلَنْ يُقْبَلَ منه وهو في الآخرة من الخاسرين}',NULL,'221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (380,'{كيف يهدي الله} هذا استفهام معنا الإِنكار أَيْ: لا يهدي الله {قوماً كفروا بعد إيمانهم} أَي: اليهود كانوا مؤمنين بمحمَّدٍ عليه السَّلام قبل مبعثه فلمَّا بُعث كفروا به وقوله: {وشهدوا} أَيْ: وبعد أنْ شهدوا {أنَّ الرسول حقٌّ وجاءهم البينات} ما بيِّن في التَّوراة {والله لا يهدي القوم الظالمين} أَيْ: لا يرشد مَنْ نقض عهود الله بظلم نفسه',NULL,'222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (381,'{أولئك جزاؤهم أنَّ عليهم لعنة الله} مثل هذه الآية ذُكر في سورة البقرة',NULL,'222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (382,'قال تعالى {خَالِدِينَ فِيهَا لا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلا هم ينظرون}',NULL,'222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (383,'{إلاَّ الذين تابوا من بعد ذلك} أَيْ: راجعوا الإِيمان بالله وتصديق نبيِّه {وأصلحوا} أعمالهم',NULL,'222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (384,'{إنَّ الذين كفروا بعد إيمانهم} وهم اليهود {ثم ازدادوا كفراً} بالإِقامة على كفرهم {لَنْ تُقْبَلَ تَوْبَتُهُمْ} لأنهم لا يتوبون إلاَّ عند حضور الموت وتلك التَّوبة لا تُقبل',NULL,'222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (385,'{إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ فَلَنْ يقبل من أحدهم ملء الأرض ذهباً} وهو القدر الذي يملؤها يقول: لو افتدى من العذاب بملء الأرض ذهباً لم يُقبل منه',NULL,'222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (386,'{لن تنالوا البر} التقوى وقيل: أَي: الجنَّة {حتى تنفقوا مما تحبون} أَيْ: تُخرجوا زكاة أموالكم',NULL,'223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (387,'{كلُّ الطعام كان حلاً لبني إسرائيل} أَيْ: حلالاً {إِلا مَا حَرَّمَ إِسْرَائِيلُ عَلَى نَفْسِهِ مِنْ قبل أن تنزل التوراة} وذلك أنَّ يعقوب عليه السَّلام مرض مرضاً شديداً فنذر لئن عافاه الله تعالى لَيُحرِّمنَّ أحبَّ الطَّعام والشَّراب إليه وكان أحبَّ الطَّعام والشَّراب إليه لحمانُ الإِبل وألبانها فلمَّا ادَّّعى النَّبيُّ صلى الله عليه وسلم أنَّه علم دين إبراهيم عليه السَّلام قالت اليهود: كيف وأنت تأكل لحوم الإِبل وألبانها؟ فقال النَّبيُّ عليه السَّلام: كان ذلك حلالاً لإِبراهيم عليه السَّلام فادَّعت اليهود أنَّ ذلك كان حراماً عليه فأنزل الله تعالى تكذيباً لهم وبيَّن أنَّ ابتداء هذا التَّحريم لم يكن في التَّوراة إنَّما كان قبل نزولها وهو قوله: {مِنْ قَبْلِ أَنْ تُنَزَّلَ التَّوْرَاةُ قُلْ فأتوا بالتَّوراة} الآية',NULL,'223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (388,'{فمن افترى على الله الكذب} أَيْ: بإضافة هذا التَّحريم إلى الله عزَّ وجل على إبراهيم في التَّوراة {مِنْ بَعْدِ ذَلِكَ} من بعد ظهور الحجَّة بأنَّ التَّحريم إنَّما كان من جهة يعقوب عليه السَّلام {فأولئك هم الظالمون} أنفسهم',NULL,'223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (389,'{قل صدق الله} في هذا وفي جميع ما أخبر به',NULL,'224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (390,'{إنَّ أوَّل بيتٍ وُضع للناس} يُحَجُّ إليه {للذي ببكة} مكَّة {مباركاً} كثير الخير بأن جُعل فيه وعنده البركة {وهدىً} وذا هدىً {للعالمين} لأنَّه قِبلة صلاتهم ودلالةٌ على الله بما جعل عنده من الآيات',NULL,'224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (391,'{فيه آياتٌ بيناتٌ} أَيْ: المشاعر والمناسك كلُّها ثمَّ ذكر بعضها فقال: {مقام إبراهيم} أَيْ: منها مقام إبراهيم {ومَنْ دخله كان آمناً} أَيْ: مَنْ حجَّه فدخله كان آمناً من الذُّنوب التي اكتسبها قبل ذلك وقيل: من النَّار {ولله على الناس حج البيت} عمَّم الإِيجاب ثمَّ خصَّ وأبدل من النَّاس فقال {من استطاع إليه سبيلاً} يعني: مَنْ قوي في نفسه فلا تلحقه المشقَّة في الكون على الراحة فمَنْ كان بهذه الصِّفة وملك الزَّاد والرَّاحلة وجب عليه الحج {ومَنْ كفر} جحد فرض الحجِّ {فإنَّ الله غنيٌّ عن العالمين}',NULL,'224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (392,'قال تعالى {قل يا أهل الْكِتَابِ لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَاللَّهُ شَهِيدٌ على ما تعملون}',NULL,'224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (393,'{قل يا أهل الْكِتَابِ لِمَ تَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمن} كان صدُّهم عن سبيل الله بالتكذيب بالنبي صلى الله عليه وسلم وأنَّ صفته ليست في كتابهم {تبغونها عوجاً} تطلبون لها عوجاً بالشُّبَه التي تلبسونها على سفلتكم {وأنتم شهداء} بما في التَّوراة أنَّ دين الله الإِسلام',NULL,'224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (394,'{يا أيها الذين آمنوا إنْ تطيعوا فريقاً} الآية نزلت في الأوس والخزرج حين أغرى قومٌ من اليهود بينهم ليفتنوهم عن دينهم ثمَّ خاطبهم فقال:',NULL,'224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (395,'{وكيف تكفرون} أَيْ: على أيِّ حالٍ يقع منكم الكفر وآياتُ الله التي تدلُّ على توحيده تُتلى عليكم {وفيكم رسوله ومَنْ يعتصم بالله} يؤمن بالله',NULL,'225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (396,'{يا أيها الذين آمنوا اتقوا الله حق تقاته} وهو أَنْ يُطاع فلا يُعصى ويُذكر فلا يُنسى ويشكر فلا بكفر فلما نزل هذا قال أصحاب النبي صلى الله عليه وسلم: ومَنْ يقوى على هذا؟ وشقَّ عليهم فأنزل الله تعالى: {فاتَّقوا الله ما استطعتم} فنسخت الأولى {ولا تموتن إلا وأنتم مسلمون} أَيْ: كونوا على الإِسلام حتى إذا أتاكم الموت صادفكم عليه وهو في الحقيقةِ نهيٌ عن ترك الإِسلام',NULL,'225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (397,'{واعتصموا بحبل الله جميعاً} أَيْ: تمسَّكوا بدين الله والخطاب للأوس والخزرج {ولا تفرقوا} كما كنتم في الجاهليَّة مُقتتلين على غير دين الله {واذكروا نعمة الله عليكم} بالإِسلام {إِذْ كُنْتُمْ أَعْدَاءً} يعني: ما كان بين الأوس والخزرج من الحرب إلى أن ألَّفَ الله بين قلوبهم بالإِسلام فزالت تلك الأحقاد وصاروا إخواناً مُتوادِّين فذلك قوله: {فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُمْ بِنِعْمَتِهِ إِخْوَانًا وَكُنْتُمْ على شفا حفرة من النار} أَيْ: طرف حفرةٍ من النَّار لو متم على ما كنتم عليه {فأنقذكم} فنجَّاكم {منها} بالإِسلام وبمحمد عليه السَّلام {كذلك} أَيْ: مثل هذا البيان الذي تُلي عليكم {يبين الله لكم آياته لعلكم تهتدون}',NULL,'225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (398,'{ولتكن منكم أمة} الآية أَيْ: وليكن كلُّكم كذلك ودخلت مِنْ لتخصيص المخاطبين من غيرهم',NULL,'226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (399,'{ولا تكونوا كالذين تفرَّقوا} أَي: اليهود والنَّصارى {واختلفوا من بعد ما جاءهم البينات} أَيْ: إنَّ اليهود اختلفوا بعد موسى فصاروا فرقاً وكذلك النَّصارى',NULL,'226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (400,'{يومَ تبيض وجوه} أَيْ: وجوه المهاجرين والأنصار ومَنْ آمنَ بمحمدٍ عليه السَّلام {وتسودّ وجوه} اليهود والنَّصارى ومَنْ كفر به {فأمَّا الذين اسودَّت وجوههم} فيقال لهم: {أكفرتم بعد إيمانكم} لأنَّهم شهدوا لمحمدٍ عليه السَّلام بالنُّبوَّة فلمَّا قدم عليهم كذَّبوه وكفروا به',NULL,'226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (401,'{وَأَمَّا الَّذِينَ ابْيَضَّتْ وُجُوهُهُمْ فَفِي رَحْمَةِ اللَّهِ} أَيْ: جنَّته',NULL,'226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (402,'{تلك آيات الله} أي: القرآن {نتلوها عليك} نُبيِّنها {بالحقِّ} بالصِّدق {وَمَا اللَّهُ يُرِيدُ ظُلْمًا لِلْعَالَمِينَ} فيعاقبهم بلا جرم',NULL,'226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (403,'قال تعالى {ولله ما في السماوات وَمَا فِي الأَرْضِ وَإِلَى اللَّهِ تُرْجَعُ الأُمُورُ}',NULL,'227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (404,'{كنتم خير أمة} عند الله في اللَّوح المحفوظ يعني أمة محمد صلى الله عليه وسلم {أُخرجت للناس} أُظهرت لهم وما أَخرج الله تعالى للنَّاس أُمَّة خيراً من أمة محمد عليه السلام ثمَّ مدحهم بما فيهم من الخصال فقال: {تأمرون بالمعروف} الآية',NULL,'227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (405,'{لن يضرُّوكم} أَي: اليهود {إلاَّ أذىً} إلاَّ ضرراً يسيراً باللِّسان مثل الوعيد والبهت {وَإِنْ يقاتلوكم يولوكم الأدبار} منهزمين وعد الله نبيَّه والمؤمنين النُّصرة على اليهود فصدق وعده فلم يقاتل يهود المدينة رسول الله صَلَّى الله عليه وسلم إلاًّ انهزموا',NULL,'227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (406,'{ضربت عليهم الذلة} ذكرناه {أينما ثقفوا} وُجدوا وصُودفوا {إلاَّ بحبل من الله} أَيْ: لكن قد يعتصمون بالعهد (إذا أعطوه والمعنى: أنَّهم إذلاء في كلِّ مكان إلاَّ أنَّهم يعتصمون بالعهد) والمراد {بحبلٍ من الله وحبلٍ من الناس} العهد والذِّمَّة والأمان الذي يأخذونه من المؤمنين بإذن الله وباقي الآية ذُكر في سورة البقرة ثمَّ أخبر أنَّهم غير متساوين في دينهم فقال:',NULL,'227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (407,'{ليسوا سواء} وأخبر أنَّ منهم المؤمنين فقال: {من أهل الكتاب أمة قائمة} أَيْ: على الحقِّ {يتلون} يقرؤون {آيات الله} كتاب الله {آناء الليل} ساعاته يعني: عبد الله بن سلام ومَنْ آمن معه من أهل الكتاب {وهم يسجدون} أي: يصلون',NULL,'227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (408,'قال تعالى {يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُونَ فِي الْخَيْرَاتِ وَأُولَئِكَ مِنَ الصالحين}',NULL,'228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (409,'{وَمَا يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُ} لن تُجحدوا جزاءه',NULL,'228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (410,'{إنَّ الذين كفروا} الآية سبقت في أوَّل هذه السورة',NULL,'228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (411,'{مَثَلُ مَا يُنْفِقُونَ فِي هَذِهِ الْحَيَاةِ الدُّنْيَا} يعني: نفقة سفلة اليهود على علمائهم {كمثل ريح فيها صرٌّ} بردٌ شديدٌ {أَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوا أَنْفُسَهُمْ} بالكفر والمعصية أعلم الله تعالى أنَّ ضرر نفقتهم عليهم كضرر هذه الرِّيح على هذا الزَّرع {وَمَا ظَلَمَهُمُ اللَّهُ} لأنَّ كلَّ ما فعله بخلقه فهو عدلٌ منه {ولكن أنفسهم يظلمون} بالكفر والعصيان ثمَّ نهى المؤمنين عن مباطنتهم فقال:',NULL,'228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (412,'{يا أيها الذين آمنوا لا تتخذوا بطانة} أَيْ: دخلاً وخواصَّ {من دونكم} من غير أهل ملَّتكم {لا يألونكم خبالاً} أَيْ: لا يدعون جهدهم في مضرَّتكم وفسادكم {ودُّوا ما عنتم} تمنَّوا ضلالكم عن دينكم {قد بدت البغضاء} أَيْ: ظهرت العداوة {من أفواههم} بالشَّتيمة والوقيعة في المسلمين {وما تخفي صدورهم} من العداوة والخيانة {أكبر قد بينا لكم الآيات} أَيْ: علامات اليهود في عداوتهم {إن كنتم تعقلون} موقع نفع البيان',NULL,'228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (413,'{ها أنتم} ها تنبيهٌ دخل على أنتم {أولاء} بمعنى: الذين كأنَّه قيل: الذين {تحبُّونهم ولا يحبُّونكم} أَيْ: تريدون لهم الإِسلام وهم يريدونكم على الكفر {وتؤمنون بالكتاب كلِّه} أَيْ: بالكتب وهو اسم جنس {وإذا خلوا عضُّوا عليكم الأنامل} أَيْ: أطراف الأصابع {من الغيظ} التَّقدير: عضُّوا الأنامل من الغيظ عليكم وذلك لما يرون من ائتلاف المؤمنين واجتماع كلمتهم {قل موتوا بغيظكم} أمر الله تعالى نبيَّه أن يدعو عليهم بدوام غيظهم إلى أن يموتوا {إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ} بما فيها من خيرٍ وشرٍّ',NULL,'229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (414,'{إن تمسسكم حسنةٌ} نصرٌ وغنيمةٌ {تسؤهم} تحزنهم {وإنْ تصبكم سيئة} ضد ذلك وهو كسرٌ وهزيمةٌ {يَفْرَحُوا بِهَا وَإِنْ تَصْبِرُوا} على ما تسمعون من آذاهم {وتتقوا} مقاربتهم ومخالطتهم {لا يضرُّكم كيدهم} عداوتهم {شيئاً إنَّ الله بما يعملون محيط} عالمٌ به فلن تعدموا جزاءه',NULL,'229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (415,'{وإذ غدوت} يعني: يوم أُحدٍ {من أهلك} من منزل عائشة رضي الله عنها {تبوء} تهيئ للمؤمنين {مقاعد} مراكز ومثابت {للقتال والله سميع} لقولكم {عليم} بما في قلوبكم',NULL,'229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (416,'{إذ همَّت طائفتان منكم} بنو سَلِمة وبنو حارثة {أن تفشلا} أَنْ تجبنا وذلك أنَّ هؤلاء همُّوا بالانصراف عن الحرب فعصمهم الله {والله وليُّهما} ناصرهما وموالٍ لهما {وعلى الله فليتوكل} فليعتمد في الكفاية {المؤمنون}',NULL,'229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (417,'{ولقد نصركم الله ببدرٍ وأنتم أذلَّةٌ} بقلَّة العدد وقلَّة السِّلاح {فاتقوا الله لعلكم تشكرون} أَيْ: فاتقونِ فإنه شكر نعمتي',NULL,'230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (418,'{إذ تقول للمؤمنين} يوم بدرٍ: {أَلَنْ يَكْفِيَكُمْ أَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلاثَةِ آلافٍ من الملائكة منزلين}',NULL,'230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (419,'{بلى} تصديقٌ لوعد الله {إن تصبروا} على لقاء العدوِّ {وتتقوا} معصية الله ومخالفة النبيِّ عليه السَّلام ( {ويأتوكم من فورهم} قيل: من وجههم وقيل: من غيظهم) {يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ آلافٍ مِنَ الْمَلائِكَةِ مُسَوِّمِينَ} مُعلَّمين وكانت الملائكة قد سوِّمت يوم بدر بالصُّوف الأبيض في نواصي الخيل وأذنابها ثمَّ صبر المؤمنون يوم بدر فأُمدّوا بخمسة آلاف من الملائكة',NULL,'230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (420,'{وما جعله الله} أَيْ: ذلك الإِمداد {إلاَّ بشرى} أَيْ: بشارةً لكم {وَلِتَطْمَئِنَّ قُلُوبُكُمْ بِهِ} فلا تجزع من كثرة العدو {وما النصر إلاَّ مِنْ عند الله} لأنَّ مَن لم ينصره الله فهو مخذولٌ وإنْ كثرت أنصاره',NULL,'230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (421,'{ليقطع طرفاً} أَيْ: نصركم ببدرٍ (ليقطع طرفاً أي:) ليهدم ركناً من أركان الشِّرك بالقتل والأسر {أو يكبتهم} أَيْ: يخزيهم ويُذلَّهم يعني: الذين انهزموا قوله:',NULL,'231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (422,'{ليس لك من الأمر شيء} الآية لمَّا كان يوم أُحدٍ من المشركين ما كان من كسر رباعية النبي صلى الله عليه وسلم وشجِّه فقال: كيف يفلح قومٌ خضبوا وجه نبيِّهم وَهُوَ يَدْعُوهُمْ إِلَى رَبِّهِمْ؟ ! فَأَنْزَلَ اللَّهُ تَعَالَى هذه الآية يُعلمه أنَّ كثيراً منهم سيؤمنون والمعنى: ليس لك من الأمر في عذابهم أو استصلاحهم شيءٌ حتى يقع إنابتهم أو تعذيبهم وهو قوله: {أو يتوب عليهم أو يعذبهم} فلمَّا نفى الأمر عن نبيِّه عليه السَّلام ذكر أنَّ جميع الأمر له فمَنْ شاء عذَّبه ومن شاء غفر له وهو قوله:',NULL,'231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (423,'{ولله ما في السماوات وما في الأرض يغفر لمن يشاء} أَي: الذَّنب العظيم للموحِّدين {ويعذّب من يشاء} يريد: المشركين على الذَّنب الصَّغير {والله غفورٌ} لأوليائه {رحيمٌ} بهم',NULL,'231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (424,'{يا أيها الذين آمنوا لا تأكلوا الربا} الآية هو أنَّهم كانوا يزيدون على المال ويؤخِّرون الأجل كلَّما أُخرِّ أجلٌ إلى غيره زِيد في المال زيادةٌ {لعلكم تفلحون} لكي تسعدوا وتبقوا في الجنة',NULL,'231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (425,'{واتقوا النَّار} بتحريم الرِّبا وترك الاستحلال له {التي أعدَّت للكافرين} دون المؤمنين',NULL,'232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (426,'قال تعالى {وأطيعوا الله والرسول لعلكم ترحمون}',NULL,'232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (427,'{وسارعوا إلى مغفرةٍ من ربكم} أَي: الإِسلامِ الذي يوجب المغفرة وقيل: إلى التَّوبة وقيل: إلى أداء الفرائض {وجنة عرضها السماوات والأرض أُعدّت} لكلِّ واحدٍ من أولياء الله',NULL,'232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (428,'{الذين ينفقون في السراء والضرَّاء} في اليسر والعسر وكثرة المال وقلَّته {والكاظمين الغيظ} الكافيِّن غضبهم عن إمضائه {والعافين عن الناس} أيْ: المماليك وعمَّنْ ظلمهم وأساء إليهم {والله يحبُّ المحسنين} الموحِّدين الذين فيهم هذه الخصال',NULL,'232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (429,'{والذين إذا فعلوا فاحشة} أَي: الزِّنا نزلت في نبهان التَّمَّار أتته امراةٌ حسناء تبتاع منه التمر فضمَّها إلى نفسه وقبَّلها ثمَّ ندم على ذلك فأتى النبي صلى الله عليه وسلم وذكر ذلك له فنزلت هذه الآية وقوله: {أو ظلموا أنفسهم} يعني: ما دون الزِّنا من قُبلةٍ أو لمسةٍ أو نظرٍ {ذكروا الله} أيْ: ذكروا عقاب الله {ولم يُصرُّوا} أَيْ: لم يقيموا ولم يدوموا {على ما فعلوا} بل أقرُّوا واستغفروا {وهم يعلمون} أن الذي أتوه حرام ومعصية',NULL,'232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (430,'قال تعالى {أُولَئِكَ جَزَاؤُهُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَجَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وَنِعْمَ أَجْرُ العاملين}',NULL,'232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (431,'{قد خلت من قبلكم سننٌ} قد مضت مني فيمن كان قبلكم من الأمم الكافرة سننٌ بإمهالي إيَّاهم حتى يبلغوا الأجل الذي أجَّلته في إهلاكهم وبقيت لهم آثارٌ في الدنيا فيها أعظم الاعتبار {فَسِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ} آخرُ أمرِ {المُكذِّبين} منهم نزلت في قصَّة يوم أُحدٍ يقول الله: فأنا أُمهلهم حتَّى يبلغ أجلي الذي أجَّلْتُ في نصرة النبيِّ عليه السَّلام وأوليائه وإهلاك أعدائه',NULL,'233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (432,'{هذا بيانٌ للناس} أي: القرآن بيانٌ للنَّاس عامَّة {وهدىً وموعظة للمتقين} خاصَّة وهم الذين هداهم الله بفضله',NULL,'233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (433,'{ولا تهنوا} ولا تضعفوا عن جهاد عدوِّكم بما نالكم من الهزيمة {ولا تحزنوا} أَيْ: على ما فاتكم من الغنيمة {وأنتم الأعلون} أَيْ: لكم تكون العاقبة بالنَّصر والظَّفر {إن كنتم مؤمنين} أي: إن الإِيمان يُوجب ما ذكر من ترك الوهن والحزن',NULL,'233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (434,'{إن يمسسكم} يصبكم {قرحٌ} جراحٌ وألمها يوم أُحدٍ {فقد مسَّ القوم} المشركين {قرحٌ مثله} يوم بدرٍ {وتلك الأيام} أَيْ: أيَّام الدُّنيا {نداولها} نُصرِّفها {بين الناس} مرَّةً لفرقةٍ ومرَّةً عليها {وليعلم الله الذين آمنوا} مُميَّزين بالإٍيمان عن غيرهم أَيْ: إِنَّما نجعل الدَّولة للكفَّار على المؤمنين ليميَّز المؤمن المخلص ممَّن يرتدُّ عن الدِّين إذا أصابته نكبة والمعنى: ليعلمهم مشاهدةً كما علمهم غيباً {ويتخذ منكم شهداء} أَيْ: ليكرم قوماً بالشَّهادة {والله لا يحبُّ الظالمين} أَي: المشركين أَيْ: إنَّه إنما يُديل المشركين على المؤمنين لما ذُكر لا لأنَّه يحبُّهم',NULL,'234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (435,'{وليمحص الله الذين آمنوا} أَيْ: ليخلِّصهم من ذنوبهم بما يقع عليهم من قتلٍِ وجرحٍ وذهاب مال {ويمحق الكافرين} يستأصلهم إذا أدال عليهم يعني: أنه يُديل على المؤمنين لما ذُكر ويُديل على الكافرين لإِهلاكهم بذنوبهم',NULL,'234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (436,'{أم حسبتم} بل أحسبتم أَي: لا تحسبوا {أن تدخلوا الجنة ولما يعلم الله} الآية أَيْ: ولمَّا يقع العلم بالجهاد مع العلم بصبر الصَّابرين والآية خطابٌ للذين انهزموا يوم أُحدٍ قيل لهم: أحسبتم أن تدخلوا الجنَّة كما دخل الذين قُتلوا وثبتوا على ألم الجرح والضَّرب من غير أن تسلكوا طريقهم وتصبروا صبرهم؟ !',NULL,'234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (437,'{ولقد كنتم تمنون الموت} كانوا يتمنَّون يوماً مع النبي صلى الله عليه وسلم ويقولون: لنفعلنَّ ولنفعلنَّ ثمَّ انهزموا يوم أُحدٍ فاستحقُّوا العقاب وقوله: {من قبل أن تلقوه} أَيْ: من قبل يوم أحدٍ {فقد رأيتموه} رأيتم ما كنتم تتمنُّون من الموت أَيْ: رأيتم أسبابه ولم تثبتوا مع نبيّكم نزلت في معاتبة الرسول إياهم فقالوا: بلغنا أنَّك قد قُتلْتَ لذلك انهزمنا {وأنتم تنظرون} وأنتم بُصراءُ تتأمَّلون الحال في ذلك كيف هي فَلِمَ انهزمتم؟',NULL,'234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (438,'{وَمَا مُحَمَّدٌ إِلا رَسُولٌ قَدْ خَلَتْ مِنْ قبله الرسل} أَيْ: يموت كما ماتت الرُّسل قبله {أَفَإِنْ مَاتَ أَوْ قُتِلَ انْقَلَبْتُمْ عَلَى أعقابكم} ارتددتم كفَّاراً بعد إيمانكم وذلك لمَّا نعي رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم يوم أُحدٍ وأُشيع أنَّه قد قُتل قال ناس من أهل النِّفاق للمؤمنين: إن كان محمد قد قُتل فالحقوا بدينكم الأوَّل فأنزل الله تعالى هذه الآية {وَمَنْ يَنْقَلِبْ عَلَى عَقِبَيْهِ فَلَنْ يَضُرَّ اللَّهَ شيئاً} أَيْ: فإنما يضرُّ نفسه باستحقاق العذاب {وسيجزي الله} بما يستحقون من الثَّواب {الشاكرين} الطَّائعين لله من المهاجرين والأنصار ثمَّ عاتب المنهزمين بقوله:',NULL,'235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (439,'{وما كان لنفس أن تموت} أَيْ: ما كانت نفسٌ لتموت {إلاَّ بإذن الله} بقضائه وقدره كتب الله ذلك {كتاباً مؤجلاً} إلى أجله الذي قدِّر له فلمَ انهزمتم؟ والهزيمة لا تزيد في الحياة {ومَنْ يرد} بعمله وطاعته {ثواب الدنيا} زينتها وزخرفها {نؤته منها} نُعْطه منها ما قدَّرناه له أَيْ: لهؤلاء المنهزمين طلباً للغنيمة {ومن يرد ثواب الآخرة} يعني: الذين ثبتوا حتى قُتلوا {نؤته منها} ثمَّ احتجَّ على المنهزمين بقوله:',NULL,'235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (440,'{وكأين} أَيْ: وكم {من نبي قاتل} في معركةٍ {معه ربيون كثير} جماعاتٌ كثيرةٌ {فما وهنوا لما أصابهم} أَيْ: ما ضعفوا بعد قتل نبيِّهم الآية',NULL,'236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (441,'{وما كان قولهم} أَيْ: قول أصحاب ذلك النبيِّ المقتول عند الحرب بعد قتل نبيِّهم {إِلا أَنْ قَالُوا رَبَّنَا اغْفِرْ لَنَا ذُنُوبَنَا وإسرافنا} تجاوزنا ما حُدَّ لنا {في أمرنا وثَبِّتْ أقدامَنا} بالقوَّة من عندك والنُّصرة',NULL,'236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (442,'{فآتاهم الله ثواب الدنيا} النَّصر والظَّفر {وحسن ثواب الآخرة} الأجر والمغفرة',NULL,'236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (443,'{يا أيها الذين آمنوا إِنْ تطيعوا الذين كفروا} أَيْ: اليهود والمشركين حيث قالوا لكم يوم أُحدٍ: ارجعوا إلى دين آبائكم وهو قوله: {يردوكم على أعقابكم} يرجعونكم إلى أوَّل أمركم من الشِّرك بالله',NULL,'236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (444,'{بل الله مولاكم} أَيْ: فاستغنوا عن موالاة الكفَّار فأنا ناصركم فلا تستنصروهم ولمَّا انصرف المشركون من أحدٍ همُّوا بالرُّجوع لاستئصال المسلمين وخاف المسلمون ذلك فوعدهم الله تعالى خذلان أعدائهم بقوله:',NULL,'236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (445,'{سنلقي في قلوب الذين كفروا الرُّعب} الخوف حتى لا يرجعوا إليكم {بما أشركوا} أيْ: بإشراكهم بالله {ما لم ينزل به سلطاناً} حجَّةً وبرهاناً أيْ: الأصنام التي يَعبدونها مع الله بغير حجَّة {ومأواهم النار} أَيْ: مرجعهم النَّار {وبئس مثوى الظالمين} مقامهم',NULL,'237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (446,'{ولقد صدقكم الله وعده} بالنَّصر والظَّفر {إذْ تحسُّونهم} تقتلون المشركين يوم أُحدٍ في أوَّل الأمر {بإذنه} بعلم الله وإرادته {حتى إذا فشلتم} جَبنْتُم عن عدوِّكم {وتنازعتم} اختلفتم في الأمر يعني: قول بعضهم: ما مقامنا وقد انهزم القوم الكافرون وقول بعضهم: لا نجاوز أمر رسول الله صَلَّى الله عليه وسلم وهذا الاختلاف كان بين الرُّماة الذين كانوا عند المركز {وعصيتم} الرَّسول بترك المركز {مِنْ بَعْدِ مَا أَرَاكُمْ مَا تحبُّون} من الظَّفر والنَّصر على أعدائكم {منكم مَنْ يريد الدنيا} وهم الذين تركوا المركز وأقبلوا إلى الذَّهب {ومنكم مَن يريد الآخرة} أَيْ: الذين ثبتوا في المركز {ثمَّ صرفكم} ردَّكم بالهزيمة {عنهم} عن الكفَّار {ليبتليكم} ليختبركم بما جعل عليكم من الدَّبرة فيتبيَّن الصَّابر من الجازع والمخلص من المنافق {وَلَقَدْ عفا عنكم} ذنبكم بعصيان النبي صلى الله عليه وسلم والهزيمة {والله ذو فضلٍ على المؤمنين} بالمغفرة',NULL,'237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (447,'{إذْ تصعدون} تَبعدون في الهزيمة {ولا تلوون} لا تقيمون {على أحد والرسول يدعوكم في أخراكم} من خلفكم يقول: إليَّ عبادَ الله (إليَّ عباد الله إليَّ عباد الله) وأنتم لا تلتفتون إليه {فأثابكم} أَيْ: جعل مكان ما ترجعون من الثَّواب {غمَّاً} وهو غمُّ الهزيمة وظفر المشركين {بغمٍّ} أَيْ: بغمِّكم رسول الله صلى الله عليه وسلم إذْ عصيتموه {لكيلا تحزنوا} أَيْ: عفا عنكم لكيلا تحزنوا {على ما فاتكم} من الغنيمة {ولا ما أصابكم} من القتل والجراح',NULL,'238',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (448,'{ثُمَّ أَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ أَمَنَةً نعاساً} وذلك أنَّهم خافوا كرَّة المشركين عليهم وكانوا تحت الحَجَف مُتأهِّبين للقتال فأمَّنهم الله تعالى أمناً ينامون معه وكان ذلك خاصا بالمؤمنين وهو قوله {يغشى طائفة منكم وطائفة قد أهمَّتهم} وهم المنافقون كان همَّهم خلاص أنفسهم {يَظُنُّونَ بِاللَّهِ غَيْرَ الْحَقِّ} أَيْ: يظنون أن أمر محمد عليه السلام مضمحل وأنه لا ينصر {ظنّ الجاهلية} أَيْ: كظنِّ أهل الجاهليَّة وهم الكفَّار {يَقُولُونَ: هَلْ لَنَا مِنَ الأَمْرِ مِنْ شَيْءٍ} ليس لنا من النصر والظَّفَر شيء كما وُعدنا يقولون ذلك على جهة التكذيب فقال الله تعالى: {إنَّ الأمر كلّه لله} أَيْ: النصر والشهادة والقدر والقضاء {يخفون في أنفسهم} من الشك والنفاق {مَا لا يُبْدُونَ لَكَ يَقُولُونَ لَوْ كَانَ لنا من الأمر شيء} أَيْ: لو كان الاختيار إلينا {ما قتلنا هاهنا} يعنون: أنَّهم أخرجوا كُرهاً ولو كان الأمر بيدهم ما خرجوا وهذا تكذيبٌ منهم بالقدر فردَّ الله عليهم بقوله: {قُلْ لَوْ كُنْتُمْ فِي بُيُوتِكُمْ لَبَرَزَ الَّذِينَ كتب عليهم القتل إلى مضاجعهم} مصارعهم ولم يكن لِيُنجيهم قعودُهم {وليبتلي الله ما في صدوركم} أيُّها المنافقون فعلَ الله ما فعلَ يوم أُحدٍ {وليمحِّص} ليظهر ويكشف {ما في قلوبكم} أيُّها المؤمنون من الرِّضا بقضاء الله {والله عليمٌ بذات الصدور} بضمائرها',NULL,'238',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (449,'{إنَّ الذين توَّلوا منكم} أيُّها المؤمنون {يوم التقى الجمعان} أَيْ: الذين انهزموا يوم أحد {إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطَانُ} حملهم على الزَّلَّة {ببعض ما كسبوا} يعني: معصيتهم للنبي صلى الله عليه وسلم بترك المركز {ولقد عفا الله عنهم} تلك الخطيئة',NULL,'239',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (450,'{يا أيها الذين آمنوا لا تكونوا كالذين كفروا} أَيْ: المنافقين {وقالوا لإِخوانهم} أَيْ: في شأن إخوانهم في النَّسب {إذا ضربوا في الأرض} أَيْ: سافروا فماتوا وهلكوا {أو كانوا غُزَّىً} جمع غازٍ فقتلوا {لَوْ كَانُوا عِنْدَنَا مَا مَاتُوا وَمَا قتلوا} تكذيباً منهم بالقضاء والقدر {ليجعل الله ذلك حسرة في قلوبهم} أَيْ: ليجعل ظنَّهم أنَّهم لو لم يحضروا الحرب لاندفع عنهم القتل {حسرة في قلوبهم} ينهى المؤمنين أن يكونوا كهؤلاءِ الكفَّار في هذا القول منهم ليجعل اللَّهُ ذلك حسرةً في قلوبهم دون قلوب المؤمنين {واللَّهُ يحيي ويميت} فليس يمنع الإِنسان تحرُّزه من إتيان أجله',NULL,'239',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (451,'{ولئن قتلتم} أي: والله لئن قتلتم {في سبيل الله} في الجهاد أيُّها المؤمنون {أو متم} في سبيل الله ليغفرنَّ لكم وهو {خيرٌ مما يَجمعون} من أعراض الدُّنيا',NULL,'240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (452,'{ولئن متم} مُقيمين على الجهاد {أو قتلْتُم} مجاهدين {لإِلَى اللَّهِ تُحْشَرُونَ} في الحالين',NULL,'240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (453,'{فبما رحمة من الله} أَيْ: فَبِرَحْمةٍ أَيْ: فبنعمةٍ من الله وإحسانٍ منه إليك {لِنت لهم} يا محمد أَيْ: سهلت أخلاقك لهم وكثر احتمالك {ولو كنت فظاً} غليظاً في القول {غليظ القلب} في الفعل {لانْفَضُّوا} لتفرَّقوا {من حولك فاعف عنهم} فيما فعلوا يومَ أُحدٍ {واستغفر لهم} حتى أشفعك فيهم {وشاورهم في الأمر} تطييباً لنفوسهم ورفعاً من أقدارهم ولتصير سنَّةً {فإذا عزمت} على ما تريد إمضاءه {فتوكل على الله} لا على المشاورة',NULL,'240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (454,'{إنْ ينصركم الله فلا غالب لكم} من النَّاس {وإن يخذلكم} (يوم أُحد) لا ينصركم أحدٌ من بعده والمعنى: لا تتركوا أمري للنَّاس وارفضوا النَّاس لأمري',NULL,'240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (455,'{وما كان لنبيٍّ أن يغلَّ} أَيْ: يخون بكتمان شيءٍ من الغنيمة عن أصحابه نزلت في قطيفة حمراء فُقدت يوم بدرٍ فقال النَّاس: لعلَّ النَّبيَّ أخذها فنفى الله تعالى عنه الغلول وبيَّن أنَّه ما غلَّ نبيٌّ والمعنى: ما كان لنبيٍّ غلولٌ {ومن يَغْلُلْ يأت بما غلَّ} حاملاً له على ظهره {يَوْمَ الْقِيَامَةِ ثُمَّ تُوَفَّى كُلُّ نَفْسٍ مَا كسبت} أَيْ: تُجازى ثواب عملها {وهم لا يظلمون} لا ينقصون من ثواب أعمالهم شيئاً',NULL,'240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (456,'{أفمن اتبع رضوان الله} بالإيمان به والعمل بطاعته يعني: المؤمنين {كمَنْ باء بسخطٍ من الله} احتمله بالكفر به والعمل بمعصيته يعني: المنافقين',NULL,'241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (457,'{هم درجاتٌ عند الله} أَيْ: أهل درجات عند الله يريد أنَّهم مختلفو المنازل فَلِمَن اتِّبع رضوان الله الكرامة والثَّواب ولِمَنْ باء بسخطٍ من الله المهانةُ والعذاب {والله بصيرٌ بما يعملون} فيه حثٌّ على الطَّاعة وتحذيرٌ عن المعصية',NULL,'241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (458,'{لَقَدْ مَنَّ اللَّهُ عَلَى الْمُؤْمِنِينَ إِذْ بَعَثَ فيهم رسولاً من أنفسهم} أَيْ: واحداً منهم عُرِف أمره وخبرُ صدقه وأمانته ليس بمَلَك ولا أحدٍ من غير بني آدم وباقي الآية ذُكر في سورة البقرة {وإن كانوا من قَبْلُ} وقد كانوا من قبل بعثه {لفي ضلالٍ مبين}',NULL,'241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (459,'{أَوَ لَمّا أصابتكم} أَوَ حين أصابتكم مصيبة يعني: ما أصابهم يوم أُحدٍ {قد أصبتم} أنتم {مثليها} يوم بدر وذلك أنَّهم قتلوا سبعين وأسروا سبعين وقُتل منهم يوم أحد سبعون {قلتم أنَّى هذا} من أين أصابنا هذا القتل والهزيمة ونحن مسلمون ورسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم فينا؟ ! {قُلْ هُوَ مِنْ عِنْدِ أَنْفُسِكُمْ} أَيْ: إنَّكم تركتم المركز وطلبتم الغنيمة فَمِنْ قِبَلِكُمْ جاءكم الشَّرُّ {إن الله على كل شيء قدير} من النَّصر مع طاعتكم نبيَّكم وترك النَّصر مع مخالفتكم إيَّاه',NULL,'241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (460,'{وما أصابكم يوم التقى الجمعان} يوم أُحدٍ {فبإذن الله} بقضائه وقدره يُسلِّيهم بذلك {وليعلم المؤمنين} ثابتين صابرين وليعلم المنافقين جازعين ممَّا نزل بهم',NULL,'242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (461,'{وقيل لهم} لعبد الله بن أُبيِّ وأصحابه لمَّا انصرفوا ذلك اليوم عن المؤمنين {تَعَالَوْا قَاتِلُوا فِي سَبِيلِ اللَّهِ أَوِ ادْفَعُوا} عنَّا القوم بتكثيركم سوادنا إنْ لم تقاتلوا {قَالُوا لَوْ نَعْلَمُ قِتَالا لاتَّبَعْنَاكُمْ} أَيْ: لو نعلم أنَّكم تقاتلون اليوم لاتَّبعناكم ولكن لا يكون اليوم قتال ونافقوا بهذا لأنَّهم لو علموا ذلك ما اتَّبعوهم قال الله تعالى: {هم للكفر يومئذٍ} بما أظهروا من خذلان المؤمنين {أقربُ منهم للإِيمان} لأنهم كانوا قبل ذلك أقربَ إلى الإِيمان بظاهر حالهم فلمَّا خذلوا المؤمنين صاروا أقرب إلى الكفر من حيث الظَّاهر',NULL,'242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (462,'{الذين قالوا} يعني: المنافقين {لإِِخوانهم} لأمثالهم من أهل النِّفاق {وقعدوا} عن الجهاد الواو للحال {لو أطاعونا} يعنون: شهداء أُحدٍ في الإنصراف عن النبي صلى الله عليه وسلم والقعود {ما قُتلوا} فردَّ الله تعالى عليهم وقال: {قل} لهم يا محمد {فادْرَؤُوا} فادفعوا {عن أنفسكم الموت إن كنتم صادقين} إنْ صدقتم أنَّ الحذر ينفع من القدر',NULL,'242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (463,'{وَلا تَحْسَبَنَّ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ} يعني: شهداء أُحدٍ {أمواتاً بل أحياء} بل هم أحياءٌ {عند ربهم} في دار كرامته لأنَّ أرواحهم في أجواف طيرٍ خضرٍ {يرزقون} يأكلون',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (464,'{فرحين} مسرورين {بِمَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ وَيَسْتَبْشِرُونَ بِالَّذِينَ لم يلحقوا بهم من خلفهم} ويفرحون بإخوانهم الذين فارقوهم يرجون لهم الشَّهادة فينالون مثلَ ما نالوا {ألاَّ خوفٌ عليهم} أَيْ: بأن لا خوفٌ عليهم يعني: على إخوانهم المؤمنين إذا لحقوا بهم',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (465,'قال تعالى {يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللَّهِ وَفَضْلٍ وَأَنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُؤْمِنِينَ}',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (466,'{الذين استجابوا لله والرسول} أجابوهما {من بعد ما أصابهم القرح} أَيْ: الجراحات {للذين أحسنوا منهم} بطاعة الرَّسول واتَّقوا مخالفته {أجر عظيم} نزلت في الذين أطاعوا الرَّسول حين ندبهم للخروج في طلب أبي سفيان يوم أُحدٍ لمَّا همَّ أبو سفيان بالانصراف إلى محمَّدٍ عليه السَّلام وأصحابه ليستأصلوهم',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (467,'{الذين قال لهم الناس} الآية كان أبو سفيان واعد رسول الله صلى الله عليه وسلم أنْ يوافيه العام المقبل من يوم أُحدٍ بِبَدْرٍ الصُّغرى فلمَّا كان العام المقبل بعث نعيم بن مسعود الأشجعيِّ ليجبِّن المؤمنين عن لقائه وهو قوله: {الذين} يعني: المؤمنين {قال لهم الناس} يعني: نعيم بن مسعود {إنَّ الناس} يعني: أبا سفيان وأصحابه {قد جمعوا} باللطيمة سوق مكة {لكم فاخشوهم} ولا تأتوهم {فزادهم} ذلك القول {إيماناً} أَيْ: ثبوتاً في دينهم وإقامةً على نصرة نبيِّهم {وَقَالُوا حَسْبُنَا اللَّهُ} أَيْ: الذي يكفينا أمرهم هو الله {ونِعْمَ الوكيل} أَيْ: الموكول إليه الأمر',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (468,'{فانقلبوا بنعمةٍ من الله وفضل} ربحٍ وذلك أنَّ رسول الله صلى الله عليه وسلم خرج لذلك الموعد فلم يلق أحداً من المشركين ووافقوا السُّوق وذلك أنَّه كان موضع سوقٍ لهم فاتَّجروا وربحوا وانصرفوا إلى المدينة سالمين غانمين وهو قوله: {لم يمسسهم سوءٌ} أَيْ: قتل ولا جراح {واتبعوا رضوان الله} إلى بدر الصغرى في طاعته وفي طاعة رسوله قوله:',NULL,'244',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (469,'{إنما ذلكم الشيطان يُخوِّف أولياءَه} أَيْ: يُخوِّفكم بأوليائه يعني: الكفَّار {فلا تخافوهم وخافون} في ترك أمري {إن كنتم مؤمنين} مصدقين لوعدي',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (470,'{ولا يحزنك الذين يسارعون في الكفر} أَيْ: في نصرته وهم المنافقون واليهود والمشركون {إنَّهم لن يضرُّوا الله} أََيْ: أولياءَه ودينه {شيئاً} وإنَّما يعود وبال ذلك عليهم {يريد الله أن لا يجعل لهم حظَّاً} نصيباً {في الآخرة} في الجنَّة',NULL,'243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (471,'{إنَّ الذين اشتروا الكفر بالإِيمان} أَيْ: استبدلوا كرَّر {لن يضروا الله شيئاً} لأنه ذكرن في الأول على طريق العلة لما يجب من التَّسلية عن المسارعة إلى الضَّلالة وذكره في الثاني على طريق العلة لاختصاص المضرة بالعاصي دون المعصي',NULL,'245',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (472,'{وَلا يَحْسَبَنَّ الَّذِينَ كَفَرُوا أَنَّمَا نُمْلِي لَهُمْ} أي: أنَّ إملاءنا - وهو الإِمهال والتأخير - {خيرٌ لأنفسهم إنما نملي لهم} أَيْ: نُطوِّل أعمارهم ليزدادوا إثماً لمعاندتهم الحق وخلافهم الرَّسول نزلت الآية في قومٍ من الكفَّار علم الله تعالى أنَّهم لا يؤمنون أبداً وأنَّ بقاءهم يزيدهم كفراً',NULL,'245',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (473,'{مَا كَانَ اللَّهُ لِيَذَرَ الْمُؤْمِنِينَ عَلَى مَا أنتم عليه} أَيُّها المؤمنون من التباس المنافق بالمؤمن {حتى يميز الخبيث من الطيب} أََيْ: المنافق من المؤمن ففعل ذلك يوم أُحدٍ لأنَّ المنافقين أظهروا النِّفاق بتخلُّفهم {وما كان الله ليطلعكم على الغيب} فتعرفوا المنافق من المؤمن قبل التَّمييز {ولكنَّ الله} يختار لمعرفة ذلك مَن يشاء من الرُّسل وكان محمَّد ممَّن اصطفاه الله بهذا العلم',NULL,'245',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (474,'{ولا يحسبنَّ الذين يبخلون} أَيْ: بخل الَّذِينَ يَبْخَلُونَ {بِمَا آتَاهُمُ اللَّهُ مِنْ فَضْلِهِ} بما يجب فيه من الزَّكاة نزلت في مانعي الزَّكاة {هو خيراً لهم} أَيْ: البخلَ خيراً لهم {بل هو شرٌّ لهم} لأَنَّهم يستحقُّون بذلك عذاب الله {سَيُطَوَّقُونَ مَا بَخِلُوا بِهِ يَوْمَ القيامة} وهو أنَّه يُجعل ما بَخِل به من المال حيَّةً يُطوَّقها في عنقه تنهشه من قرنه إلى قدمه {ولله ميراث السماوات والأرض} أَيْ: إنَّه يُغني أهلهما وتبقى الأملاك والأموال لله ولا مالك لها إلاَّ الله تعالى',NULL,'245',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (475,'{لَقَدْ سَمِعَ اللَّهُ قَوْلَ الَّذِينَ قَالُوا إِنَّ الله فقير ونحن أغنياء} نزلت في اليهود حين قالوا - لمَّا نزل قوله: {مَنْ ذَا الَّذِي يقرض الله قرضا} -: إنَّ الله فقيرٌ يستقرضنا ونحن أغنياء ولو كان غنيَّاً ما استقرضنا أموالنا {سَنَكْتُبُ مَا قَالُوا} أَيْ: نأمر الحفظة بإثبات ذلك في صحائف أعمالهم الآية',NULL,'246',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (476,'{ذلك} أَيْ: ذلك العذاب {بما قدَّمت أيديكم} بما سلفت من إجرامكم {وأنَّ الله} وبأن الله {ليس بظلام للعبيد} فيعاقبهم بغير جرمٍ',NULL,'246',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (477,'{الذين قالوا إنَّ الله عهد إلينا} أَيْ: اليهود وذلك أن أمر بني إسرائيل في التَّوراة ألا يُصدقوا رسولاً جاءهم حتى يأتيهم بقربانٍ تأكله النَّار إلاَّ المسيحَ ومحمداً عليهما السَّلام فكانوا يقولون لمحمَّد عليه السَّلام: لا نُصدِّقك حتى تأتينا بقربان تأكله النَّار لأنَّ الله عهد إلينا ذلك فقال الله تعالى لمحمد عليه السَّلام إقامةً للحجَّة عليهم: {قُلْ قَدْ جَاءَكُمْ رُسُلٌ من قبلي} ثم عزى النبي صلى الله عليه وسلم عن تكذيبهم بقوله:',NULL,'246',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (478,'{فَإِنْ كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جاؤوا بالبينات والزبر} أَيْ: الكتب {والكتاب المنير} أَيْ: الهادي إلى الحقِّ',NULL,'246',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (479,'{كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ وَإِنَّمَا تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ الْقِيَامَةِ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَأُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ} أَيْ: ظفر بالخير ونجا من الشَّرِّ {وما الحياة الدنيا} أَيْ: العيش في هذه الدَّار الفانية {إلاَّ متاع الغرور} لأنَّه يغرُّ الإِنسان بما يُمنِّيه من طول البقاء وهو ينقطع عن قريب',NULL,'247',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (480,'{لتبلونَّ} لتختبرُنَّ أيُّها المؤمنون {في أموالكم} بالفرائض فيها {وأنفسكم} بالصَّلاة والصَّوم والحجِّ والجهاد {ولتسمعنَّ من الذين أوتوا الكتاب} وهم اليهود {ومنَ الذين أشركوا} وهم المشركون {أذىً كثيراً} بالشَّتم والتَّعيير {وإن تصبروا} على ذلك الأذى بترك المعارضة {فَإِنَّ ذَلِكَ مِنْ عَزْمِ الأُمُورِ} من حقيقة الإِيمان',NULL,'247',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (481,'{وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ الَّذِينَ أُوتُوا الْكِتَابَ} أخذ الله ميثاق اليهود في التَّوراة ليبيننَّ شأن محمَّد ونعته ومبعثه ولا يخفونه فنبذوا الميثاق ولم يعملوا به وذلك قوله: {فَنَبَذُوهُ وَرَاءَ ظُهُورِهِمْ وَاشْتَرَوْا بِهِ ثَمَنًا قليلاً} أَيْ: ما كانوا يأخذونه من سفلتهم برئاستهم في العلم {فبئس ما يشترون} قُبِّح شراؤهم وخسروا',NULL,'247',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (482,'{لا تحسبنَّ الذين يفرحون} هم اليهود فرحوا بإضلال النَّاس وبنسبة النَّاس إيَّاهم إلى العلم وليسوا كذلك وأَحبُّوا أن يحمدوا بالتَّمسُّك بالحقِّ وقالوا: نحن أصحاب التَّوراة وأولو العلم القديم {فلا تحسبنَّهم بمفازة} بمنجاةٍ {من العذاب}',NULL,'247',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (483,'{ولله ملك السماوات والأرض} أَيْ: يملك تدبيرهما وتصريفهما على ما يشاء الآية والتي بعدها ذُكرت في سورة البقرة',NULL,'248',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (484,'قول تعالى {إنَّ في خلق السماوات وَالأَرْضِ وَاخْتِلافِ اللَّيْلِ وَالنَّهَارِ لآيَاتٍ لأُولِي الأَلْبَابِ}',NULL,'248',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (485,'{الَّذِينَ يَذْكُرُونَ اللَّهَ قِيَامًا وَقُعُودًا وَعَلَى جُنُوبِهِمْ} أَيْ: يصلُّون على هذه الأحوال على قدر إمكانهم {ويتفكرون في خلق السماوات والأرض} فيكون ذلك أزيد في بصيرتهم {ربنا} أَيْ: ويقولون: {ربنا ما خلقت هذا} أَيْ: هذا الذي نراه من خلق السماوات والأرض {باطلاً} أَيْ: خلقاً باطلاً يعني: خلقته دليلاً على حكمتك وكمال قدرتك',NULL,'248',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (486,'{ربَّنا إنَّك مَنْ تدخل النار} للخلود فيها {فقد أخزيته} : أهلكته وأهنته {وما للظالمين} أَيْ: الكفَّار {من أنصار} يمنعونهم من عذاب الله',NULL,'249',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (487,'{ربنا إننا سمعنا منادياً} أَيْ: محمَّداً عليه السَّلام والقرآن {ينادي للإِيمان} أَيْ: إلى الإِيمان {أَنْ آمِنُوا بِرَبِّكُمْ فَآمَنَّا رَبَّنَا فَاغْفِرْ لَنَا ذنوبنا وكَفِّرْ عنّا سيئاتنا} أَيْ: غطِّ واستر عنا ذنوبنا بقبول الطَّاعات حتى تكون كفَّارةً لها {وتوفنا مع الأبرار} يعني: الأنبياء أَيْ: في جملتهم حتى نصير معهم',NULL,'249',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (488,'{ربنا وآتنا ما وعدتنا على رسلك} أَيْ: على ألسنتهم من النَّصر لنا والخذلان لعدِّونا {ولا تُخْزِنَا يوم القيامة} أَيْ: لا تهلكنا بالعذاب قولة',NULL,'249',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (489,'{بعضكم من بعض} أَيْ: حكمُ جميعكم حكمُ واحدٍ منكم فيما أفعل بكم من مجازاتكم على أعمالكم وترك تضييعها لكم',NULL,'249',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (490,'{لا يَغُرَّنَّكَ تَقَلُّبُ الَّذِينَ كَفَرُوا فِي الْبِلادِ} تصرُّفهم للتِّجارات في البلاد وذلك أنَّهم كانوا يتَّجرون ويتنعَّمون في البلاد فقال بعض المؤمنين: إنَّ أعداء الله فيما نرى من الخير وقد هلكنا من الجوع والجهد فنزلت هذه الآية قواة',NULL,'249',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (491,'{متاعٌ قليل} أَيْ: ذلك الكسب والرِّبح متاعٌ قليل لأنَّه فانٍ منقطعٌ وقوله:',NULL,'250',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (492,'{نزلاً} النُّزُل: ما يُهيَّأ للضَّيف ومعناه هاهنا الجزاء والثَّواب {وما عند الله خيرٌ للأبرار} ممَّا يتقلَّب فيه الكفَّار ثمَّ ذكر مؤمني أهل الكتاب فقال:',NULL,'250',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (493,'{وَإِنَّ مِنْ أَهْلِ الْكِتَابِ لَمَنْ يُؤْمِنُ بِاللَّهِ} الآية',NULL,'250',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (494,'{يا أيها الذين آمنوا اصبروا} أَيْ: اصبروا على دينكم فلا تَدعوه لشدِّةٍ نزلت بكم وقيل: على الجهاد {وصابروا} عدوَّكم فلا يكوننَّ أصبر منكم {ورابطوا} أَيْ: اقيموا على جهاد عدوِّكم بالحرب والحجَّة',NULL,'250',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (495,'{يا أيها الناس} يا أهل مكة {اتَّقُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ} آدم {وخلق منها زوجها} حوَّاء خُلقت من ضلع من أضلاعه {وبث} أَيْ: فرَّق ونشر {منهما} {واتقوا الله} أَيْ: خافوه وأطيعوه {الذي تساءلون به} أَيْ: تتساءلون فيما بينكم حوائجكم وحقوقكم به وتقولون: أسألك بالله وأنشدك الله وقوله: {والأرحام} أيْ: واتَّقوا الأرحام أن تقطعوها {إنَّ الله كان عليكم رقيباً} أَيْ: حافظاً يرقب عليكم أعمالكم فاتَّقوه فيما أمركم به ونهاكم عنه',NULL,'251',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (496,'{وآتوا اليتامى أموالهم} الخطاب للأولياء والأوصياء أَيْ: أعطوهم أموالهم إذا بلغوا {وَلا تَتَبَدَّلُوا الخبيث} من أموالهم الحرام (عليكم) {بالطيب} الحلال من مالكم وهو أنَّه كان وليُّ اليتيم يأخذ الجيد من ماله ويجعل مكانه الرَّديء {ولا تأكلوا أموالهم إلى أموالكم} لا تضيفوها في الأكل إلى أموالكم إذا احتجتم إليها {إنَّه} أَيْ: إنَّ أكل أموالهم {كان حوباً كبيراً} أيْ: إثماً كبيراً',NULL,'251',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (497,'{وإن خفتم أن لا تُقسطوا} : ألا تعدلوا {في اليتامى} (أي: في نكاح اليتامى) وهمَّكم ذلك {فانكحوا ما طاب} أَي: الطَّيِّب {لكم من النساء} يعني: من اللاتي تحلُّ دون المحرَّمات والمعنى: أنَّ الله سبحانه قال لنا: فكما تخافون ألا تعدلوا بين اليتامى إذا كفلتموهم فخافوا أيضاً ألا تعدلوا بين النساء إذا نكحتموهن قانكحوا من النِّساء {مثنى} أَي: اثنتين اثنتين {وثلاث} ثلاثاً ثلاثاً {ورباع} أربعاً أربعاً {فإن خفتم أن لا تعدلوا} أَيْ: في الأربع {فواحدة} أَيْ: فلينكح كلُّ واحدٍ منكم واحدةً و {ذلك} أنَّ نكاح هؤلاء النِّسوة على قلَّة عددهنَّ {أدنى} أَيْ: أقربُ إلى العدل وهو قوله: {أن لا تعدلوا} أَيْ: تميلوا وتجوروا',NULL,'252',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (498,'{وآتوا النساء} أَيُّها الأزواج {صدقاتهنَّ} مهورهنَّ {نحلة} فريضةً وتديُّناً {فإنْ طبن لكم} أَيْ: إنْ طابت لكم أنفسهنَّ {عن شيء} من الصَّداق {فكلوه هنيئاً} في الدُّنيا لا يقضي به عليكم سلطانٌ {مريئاً} في الآخرة لا يؤاخذكم الله به',NULL,'252',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (499,'{ولا تؤتوا السفهاء} أَي: النِّساء والصِّبيان {أموالكم التي جعل الله لكم قياماً} لمعايشتكم وصلاح دنياكم يقول: لا تعمدْ إلى مالك الذي خوَّلك الله وجعله لك معيشةً فتعطيه امرأتك وبنيك فيكونوا هم الذين يقومون عليك ثمَّ تنظر إلى ما في أيديهم ولكن أمسك مالك وأصلحه وكن أنت الذي تنفق عليهم في كسوتهم ورزقهم وهو قوله: {وارزقوهم فيها} (أي: اجعلوا لهم فيها رزقاً) {واكسوهم وَقُولُوا لَهُمْ قَوْلا مَعْرُوفًا} أَيْ: عِدَةً جميلةً من البرِّ والصِّلة',NULL,'252',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (500,'{وابتلوا اليتامى} أَيْ: اختبروهم في عقولهم وأديانهم {حَتَّى إِذَا بَلَغُوا النِّكَاحَ} أَيْ: حال النِّكاح من الاحتلام {فإن آنستم} أبصرتم {منهم رشداً} صلاحاً للعقل وحفظاً للمال {ولا تأكلوها إسرافاً وبداراً أن يكبروا} أَيْ: لا تبادروا بأكل مالهم كبرَهم ورشدهم حذر أن يبلغوا فيلزمكم تسليم المال إليهم {وَمَنْ كان غنيّاً} من الأوصياء {فليستعف} عن مال اليتيم ولا يأكل منه شيئاً {ومَنْ كان فقيراً فليأكل بالمعروف} يقدِّر أجرة عمله {فإذا دفعتم} أَيُّها الأولياء {إليهم} إلى اليتامى {أموالهم فأشهدوا عليهم} لكي إنْ وقع اختلافٌ أمكن الوليِّ أن يقيم البيِّنة على ردِّ المال إليه {وكفى بالله حسيباً} محاسباً ومجازياً للمحسن والمسيء',NULL,'252',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (501,'{للرجال نصيب} الآية كانت العرب في الجاهليَّة لا تورث النِّساء ولا الصِّغار شيئاً فأبطل الله ذلك وأعلم أنَّ حقَّ الميراث على ما ذكر في هذه الآية من الفرض',NULL,'253',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (502,'{وإذا حضر القسمة} أَيْ: قسمة المال بين الورثة {أولو القربى} أَي: الذين يُحجبون ولا يرثون {واليتامى والمساكين فارزقوهم منه} وهذا على النَّدب والاستحباب يستحبُّ للوارث أن يرضخ لهؤلاء إذا حضروا القسمة من الذَّهب والفضَّة وأن يقولوا لهم قولاً معروفاً إذا كان الميراث ممَّا لا يمكن أن يرضخ منه كالأرضين والرَّقيق',NULL,'253',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (503,'{وليخش الذين لو تركوا} الآية أَيْ: وليخش مَنْ كان له وُلدٌ صغارٌ خاف عليهم من بعده الضَّيعة أن يأمر الموصي بالإِسراف فيما يعطيه اليتامى والمساكين وأقاربه الذين لا يرثون فيكون قد أمره بما لم يكن يفعله لو كان هو الميِّت وهذا قبل أن تكون الوصية في الثُّلث وقوله: {ذرية ضعافاً} أَيْ: صغاراً {خافوا عليهم} أي: الفقر {فليتقوا الله} فيما يقولون لمن حضره الموت {وليقولوا قولاً سديداً} عدلاً وهو أن يأمره أن يخلِّف ماله لولده ويتصدَّق بما دون الثُّلث أو الثُّلث ثمَّ ذكر الوعيد على أكل مال اليتيم ظلماً فقال:',NULL,'253',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (504,'{إِنَّ الَّذِينَ يَأْكُلُونَ أَمْوَالَ الْيَتَامَى ظُلْمًا إِنَّمَا يأكلون في بطونهم ناراً} الآية تؤول عاقبته إلى النَّار {وسيصلون سعيراً} ناراً ذات تلهُّب أَيْ: يُقاسون حرَّها وشدَّتها',NULL,'254',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (505,'{يوصيكم الله} أَيْ: يفرض عليكم لأنَّ الوصية من اللَّه فرضٌ {في أولادكم} الذُّكور والإِناث {للذكر مثل حظ الأنثيين فإنْ كُنَّ} أَي: الأولاد {نساءً فوق اثنتين} فوق ها هنا صلة لأ الثِّنتين يرثان الثُّلثين بإجماعٍ اليوم وهو قوله: {فلهن ثلثا ما ترك} ويجوز تسمية الاثنين بالجمع {وإن كانت} المتروكة المُخلًّفة {واحدة فلها النصف} وتمَّ بيان ميراث الأولاد ثمَّ قال {ولأبويه} أَيْ: ولأبوي الميِّت {لِكُلِّ وَاحِدٍ مِنْهُمَا السُّدُسُ مِمَّا تَرَكَ إِنْ كَانَ لَهُ وَلَدٌ فَإِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَوَرِثَهُ أَبَوَاهُ فَلأُمِّهِ الثُّلُثُ فَإِنْ كَانَ له} أي: الميت {إخوة} يعني أخوين لأنَّ الأُمَّة أجمعت أنَّ الأخوين يحجبان الأمَّ من الثُّلث إلى السُّدس وقوله: {من بعد وصية} أَيْ: هذه الأنصباء إِنَّمَا تُقسم بعد قضاء الدَّين وإنفاذ وصية الميت {آبَاؤُكُمْ وَأَبْنَاؤُكُمْ لا تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نفعاً} فِي الدُّنْيَا فتعطونه من الميراث ما يستحقُّ ولكنَّ الله قَدْ فرض الفرائض عَلَى ما هو عِنْده حكمة ولو وكل ذَلِكَ إليكم لَمْ تعلموا أيُّهم أنفع لكم فأفسدتم وضيَّعتم {إنَّ الله كان عليماً} بالأشياء قبل خلقها {حكيماً} فيما دبرَّ من الفرائض وقوله:',NULL,'254',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (506,'{وإن كان رجل يورث كلالة} الكلالة: مَنْ لا ولد له ولا والد وكلُّ وارثٍ ليس بوالدٍ ولا ولد للميِّت فهو كلالة أيضاً والكلالة في هذه الآية الميِّت أَيْ: وإن مات رجلٌ ولا ولد له ولا والد {وله أخٌ أو أخت} يريد: من الأمِّ بإجماع من الأُمَّة {فلكلِّ واحدٍ منهما السدس} وهو فرضُ الواحد من ولد الأمِّ {فَإِن كانوا أكثر من} واحدٍ اشتركوا فِي الثُّلث الذَّكر والأنثى فيه سواءٌ وقوله: {غير مضارٍّ} أَيْ: مُدخلٍ الضَّرر عَلَى الورثة وَهُوَ أَنْ يُوصي بدين لَيْسَ عَلَيْه يريد بِذَلِك ضرر الورثة {والله عليمٌ} فيما دبَّر من هَذِهِ الفرائض {حليمٌ} عمَّن عصاه بتأخير عقوبته',NULL,'255',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (507,'قال تعالى {تِلْكَ حُدُودُ اللَّهِ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وذلك الفوز العظيم}',NULL,'255',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (508,'قال تعالى {وَمَنْ يَعْصِ اللَّهَ وَرَسُولَهُ وَيَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نارا خالدا فيها وَلَهُ عَذَابٌ مُهِينٌ}',NULL,'255',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (509,'{واللاتي يأتين الفاحشة} يفعلن الزِّنا {فَاسْتَشْهِدُوا عَلَيْهِنَّ أَرْبَعَةً مِنْكُمْ} أَيّ: من المسلمين {فإن شهدوا} عليهنَّ بالزِّنا {فأمسكوهنَّ} فاحبسوهنَّ {في البيوت} في السجون وهذا كلام في أوَّل الإِسلام إذا كان الزَّانيان ثَيِّبين حُبسا ومُنعا من مخالطة النَّاس ثمَّ نُسخ ذلك بالرَّجم وهو قوله: {أو يجعل الله لهنَّ سبيلاً} وهو سبيلهنَّ الذي جعله الله لهنَّ',NULL,'255',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (510,'{واللذان يأتيانها} أَي: البكرين يزنيان ويأتيان الفاحشة {فآذوهما} بالتَّعنيف والتَّوبيخ وهو أنْ يقال لهما: انتهكتما حرمات الله وعصيتماه واستوجبتما عقابه {فَإِن تابا} من الفاحشة {وأصلحا} العمل فيما بعد فاتركوا أذاهما وهذا كان في ابتداء الإِسلام ثمَّ نسخه قوله: {الزَّانية والزَّاني فاجلدوا كلَّ واحدٍ} الآية',NULL,'256',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (511,'{إنما التوبةُ على الله} أَي: إنما التوبة التي أوجبت الله على نفسه بفضله قَبولَها {للذين يعملون السوء بجهالة} أي: إنّ ذنبَ المؤمن جهلٌ منه والمعاصي كلُّها جهالة ومَنْ عصى ربَّه فهو جاهل {ثم يتوبون من قريب} أَيْ: من قبل الموت ولو بِفُواق ناقة {فأولئك يتوب الله عليهم} يعود عليهم بالرحمة {وكان اللَّهُ عليماً حكيماً} علم ما في قلوب المؤمنين من التصديق فحكم لهم بالتوبة قبل الموت بقدر فُواق ناقة',NULL,'256',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (512,'{وليست التوبة للذين يعملون السيئات} أَي: المشركين والمنافقين {ولا الذين يموتون وهم كفار} يعني: ولا توبة لهؤلاء إذا ماتوا على كفرهم لأنَّ التَّوبة لَا تُقبل فِي الآخرة {أُولَئِكَ أعتدنا} أَيْ: هيَّأنا وأعددنا',NULL,'257',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (513,'{يا أيها الذين آمنوا لا يحلُّ لكم} الآية كان الرَّجل إذا ماتَ ورث قريبُه من عصبته امرأتَه وصار أحقَّ بها من غيره فأبطل الله ذلك وأعلم أنَّ الرَّجل لايرث المرأة من الميت وقوله {أن ترثوا النساء كرهاً} يريد: عين النِّساء كرهاً أَيْ: نكاح النساء وهنَّ كارهاتٌ {ولا تعضلوهنَّ لتذهبوا ببعض ما آتيتموهن} كان الرجل يمسك المرأة ةليس له فيها حاجةٌ إضراراً بها حتى تفتدي بمهرها فَنُهوا عن ذلك ثمَّ استثنى فقال: {إلاَّ أن يأتين بفاحشة مبينة} أيْ: الزِّنا فإذا رأى الرَّجل من امرأته فاحشةً فلا بأس أن يضارَّها حتى تختلع منه {وعاشروهنَّ بالمعروف} أَيْ: بما يجب لهنَّ من الحقوق وهذا قبل أن يأتين الفاحشة {فإن كرهتموهن} الآية أَيْ: فيما كرهتم ممَّا هو لله رضى خيرٌ كثيرٌ وثوابٌ عظيمٌ والخير الكثير في المرأة المكروهة أن يرزقه الله منها ولداً صالحاً',NULL,'257',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (514,'{وإن أردتم} الآية أي: إذا أراد الرَّجل طلاق امرأته وتزوَّج غيرها لم يكن له أن يرجع فيما آتاها من المهر وهو قوله: {وآتيتم إحداهنَّ قنطاراً} أَيْ: مالاً كثيراً {فلا تأخذوا منه شيئاً أتأخذونه بهتاناً} ظلماً {وإثماً مبيناً} وفي هذا نَهْيٌّ عن الضِّرار في غير حال الفاحشة وهو أنْ يضارَّها لتفتدي منه من غير أَنْ أتت بفاحشة',NULL,'258',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (515,'{وكيف تأخذونه} أَي: المهر أو شيئاً منه {وَقَدْ أَفْضَى بَعْضُكُمْ إِلَى بَعْضٍ} أَيّ: وصل إليه بالمجامعة ولا يجوز الرُّجوع في شيءٍ من المهر بعد الجماع {وأخذن منكم ميثاقاً غليظاً} وهو ما أخذه الله على الرِّجال للنِّساء من إمساكٍ بمعروفٍ أَوْ تسريحٍ بإحسانٍ',NULL,'258',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (516,'{ولا تنكحوا ما نكح آباؤكم} الآية كان الرَّجل من العرب يتزوَّج امرأة أبيه من بعده وكان ذلك نكاحاً جائزاً في العرب فحرَّمه الله تعالى ونهى عنه وقوله: {إلاَّ ما قد سلف} يعني: لكن ما قَدْ سلف فَإِن اللَّه تجاوز عَنْهُ {إنَّه} أَي: إنَّ ذَلِكَ النِّكاح {كان فاحشة} زنا عند الله {ومقتاً} بغضاً شديداً {وساء سبيلاً} وقَبُحَ ذلك الفعل طريقاً ثمَّ ذكر المحرَّمات من النِّساء فقال:',NULL,'258',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (517,'{حُرِّمَتْ عَلَيْكُمْ أُمَّهَاتُكُمْ وَبَنَاتُكُمْ وَأَخَوَاتُكُمْ وَعَمَّاتُكُمْ وَخَالاتُكُمْ وَبَنَاتُ الأَخِ وَبَنَاتُ الأُخْتِ وَأُمَّهَاتُكُمُ اللَّاتِي أَرْضَعْنَكُمْ وأخواتكم من الرضاعة وأمهات نسائكم وربائبكم} جمع الرَّبيبة وهِيَ بِنْت امرأة الرَّجُل من غيره {اللاتي في حجوركم} أيْ: في ضمانكم وتربيتكم {وحلائل} وأزواج {أبنائكم الذين من أصلابكم} لا مَنْ تبنَّيتموه {وأن تجمعوا} أَيْ: الجمع {بين الأختين إلاَّ ما قد سلف} مضى منكم في الجاهلية فلا تُؤاخذون به بعد الإِسلام',NULL,'258',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (518,'{والمحصنات} وذوات الأزواج {من النساء} وهنَّ مُحرَّمات على كلِّ أحدٍ غير أزواجهنَّ إِلَّا ما ملكتموهنَّ بالسَّبي من دار الحرب فإنَّها تحلُّ لمالكها بعد الاستبراء بحيضة {كتاب الله عليكم} كتب تحريم ما ذِكْرُ مَنْ النِّساء عليكم {وأحلَّ لكم ما وراء ذلك} أَيْ: ما سوى ذلكم من النِّساء {أن تبتغوا} أَيّ: تطلبوا بأموالكم إمَّا بنكاحٍ وصداقٍ أو بملكِ يمينٍ {محصنين} ناكحين {غير مسافحين} زانين {فما استمتعتم} فما انتفعتم وتلذَّذتم {به منهنَّ} أي: من النساء بالنِّكاح الصَّحيح {فآتوهنَّ أجورهنَّ} أَيْ: مهورهنَّ {فريضة} فَإِن استمتع بالدُّخول بها آتى المهر تامّاً وإن استمتع بعقد النِّكاح آتى نصف المهر {وَلا جُنَاحَ عَلَيْكُمْ فِيمَا تَرَاضَيْتُمْ بِهِ مِنْ بعد الفريضة} من حطِّ المهر أو إبراءٍ من بعض الصَّداق أو كلِّه {إنَّ الله كان عليماً} بما يصلح أمر العباد {حكيماً} فيما بيَّن لهم من عقد النِّكاح',NULL,'259',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (519,'{ومَنْ لم يستطع منكم طولاً} أَيْ: قدرةً وغنىً {أن ينكح المحصنات} الحرائر {المؤمنات فمن مّا ملكت أيمانكم} أَيْ: فليتزوَّج ممَّا ملكت أيمانكم يعني: جارية غيره {من فتياتكم} أَيْ: مملوكاتكم {المؤمنات والله أعلم بإيمانكم} أَي: اعملوا على الظَّاهر في الإِيمان فإنَّكم مُتعبَّدون بما ظهر والله يتولَّى السَّرائر {بعضكم من بعض} أَيْ: دينكم واحدٌ فأنتم متساوون من هذه الجهة فمتى وقع لأحدكم الضَّرورة جاز له تزوُّج الأَمَة {فانكحوهنَّ بإذن أهلهن} أَي: اخطبوهنَّ إلى ساداتهنَّ {وآتوهنَّ أجورهنَّ} مهورهنَّ {بالمعروف} من غير مطلٍ وضرارٍ {محصنات} عفائفَ {غير مسافحات} غير زوانٍ علانيةً {ولا متخذات أخدان} زوانٍ سرَّاً {فإذا أُحصن} تزوَّجن {فإن أتين بفاحشة} بزنا {فعليهنَّ نصف ما على المحصنات} الأبكار الحرائر {من العذاب} أَي: الحدِّ {ذلك} أَيْ: ذلك النِّكاح نكاح الأَمَة {لمن خشي العنت منكم} أَيْ: خاف أن تحمله شدَّة الغِلمة على الزنا قيلقى العنت أَي: الحدَّ في الدُّنيا والعذاب في الآخرة أَباحَ الله نكاح الأمَة بشرطين: أحدهما: عدم الطَّول الثاني: خوف العَنَت ثُمّ قَالَ: {وأن تصبروا} أَيّ: عن نكاح الإِماء {خيرٌ لَكُمْ} لئلا يصير الولد عبداً',NULL,'259',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (520,'{يريدُ الله ليبيِّن لكم} شرائع دينكم ومصالح أمركم {ويهديكم سنن الذين من قبلكم} دين إبراهيم وإسماعيل عليهما السَّلام وهو دين الحنيفيَّة {ويتوب عليكم} يرجع بكم عن معصيته التي كنتم عليها إلى طاعته',NULL,'260',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (521,'{واللَّهُ يريد أن يتوب عليكم} أَيْ: يُخرجكم من كُلّ ما يكره إلى ما يحبُّ ويرضى {ويريد الذين يتبعون الشهوات} وهم الزُّناة وأهل الباطل في دينهم {أن تميلوا} عن الحقِّ وقصد السِّبيل بالمعصية {ميلاً عظيماً} فتكونوا مثلَهم',NULL,'260',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (522,'{يريد الله أن يخفف عنكم} في كلِّ أحكام الشَّرع {وخلق الإِنسان ضعيفاً} يضعف من الصَّبر عن النِّساء',NULL,'261',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (523,'{يا أيها الَّذِينَ آمَنُوا لا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ} وهو كلُّ ما لا يحلُّ في الشَّرع كالرِّبا والغصب والقمار والسَّرقة والخيانة {إلاَّ أن تكون تجارةً} لكن إن كانت تجارة {عن تراضٍ منكم} برضى البَيِّعْين فهو حلال {ولا تقتلوا أنفسكم} لا يقتل بعضكم بعضاً',NULL,'261',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (524,'{ومَنْ يفعل ذلك} أَيْ: أكل المال بالباطل وقتل النَّفس {عدواناً} وهو أن يعدوَ ما أُمر به {وظُلماً فسوف نصليه} أَيْ: نُدخله ناراً {وكان ذلك على الله يسيراً} أَيْ: هو قادر على ذلك ولا يتعذَّر عليه',NULL,'261',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (525,'{إن تجتنبوا كبائر ما تنهون عنه} وهي كُلّ ذنبٍ ختمه اللَّه بنارٍ أَوْ غضبٍ أو لعنةٍ أو عذابٍ أو وعيدٍ في القرآن {نكفر عنكم سيئاتكم} التي هي دون الكبائر بالصَّلوات الخمس {وندخلكم مدخلاً كريماً} أَيْ: الجنَّة',NULL,'261',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (526,'{وَلا تَتَمَنَّوْا مَا فَضَّلَ اللَّهُ بِهِ بَعْضَكُمْ على بعض} الآية قالت أمُّ سلمة: يا رسول الله ليتنا كُنَّا رجالاً فجاهدنا وغزونا وكان لنا مثل أجر الرِّجال فنزلت هذه الآية {للرجال نصيب} ثواب {مما اكتسبوا} من الجهاد {وللنساء نصيبٌ} (ثوابٌ) {ممَّا اكتسبن} من حفظ فروجهنَّ وطاعة أزواجهنَّ {واسألوا الله من فضله} إن احتجتم إلى مَا لِغَيركم فيعطيكم من فضله',NULL,'261',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (527,'{ولكلٍّ} أَيْ: ولكلِّ شخصٍ من الرِّجال والنِّساء {جعلنا موالي} عصبة وورثة {ممَّا ترك الوالدان والأقربون} أَيْ: ممَّن تركهم والداه وأقربوه أَيْ: تشعَّبت العصبة والورثة عن الوالدين والأقربين ثمَّ ابتدأ فقال: {والذين عقدت أيمانكم} وهو الحلفاء أَيْ: عاقدت حلفَهم أيمانُكم وهي جمع يمين من القَسَم وكان الرَّجُل فِي الْجَاهِلِيَّة يعاقد الرَّجُل وَيَقُولُ له: دمي دمُّك وحربي حربُك وسلمي سلمُك فَلَمَّا قام الْإِسْلَام جعل للحليف السُّدس وَهُوَ قوله: {فَآتُوهُمْ نَصِيبَهُمْ} ثُمّ نسخ ذَلِكَ بقوله: {وَأُولُو الأَرْحَامِ بَعْضُهُمْ أَوْلَى بِبَعْضٍ فِي كِتَابِ اللَّهَ} {إِنَّ اللَّهَ كَانَ عَلَى كُلِّ شَيْءٍ شَهِيدًا} أَيْ: لم يغب عنه علم ما خلق',NULL,'262',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (528,'{الرجال قوَّامون على النساء} على تأديبهنَّ والأخذ فوق أيديهنَّ {بما فضَّل الله} الرِّجال على النِّساء بالعلم والعقل والقوَّة في التَّصرف والجهاد والشَّهادة والميراث {وبما أنفقوا} عليهنَّ {من أموالهم} أَي: المهر والإِنفاق عليهنَّ {فالصالحات} من النِّساء اللواتي هنَّ مطيعاتٌ لأزواجهنَّ وهو قوله: {قانتات حافظاتٌ للغيب} يحفظن فروجهنَّ فِي غيبة أزواجهنَّ {بِمَا حَفِظَ الله} بما حفظهنَّ اللَّه فِي إيجاب المهر والنَّفقة لهنَّ وإيصاء الزَّوج بهنَّ {واللاتي تخافون} تعلمون {نشوزهنَّ} عصيانهنَّ {فعظوهنَّ} بكتاب اللَّه وذكِّروهنَّ اللَّه وما أمرهنَّ به {واهجروهن في المضاجع} فرِّقوا بينكم وبينهم في المضاجع (في الفرش) {واضربوهنَّ} ضرباً غير مبرِّح شديد وللزَّوج أن يتلافى نشوز امرأنه بما أذن الله تعالى فيه يعظها بلسانه فإنْ لم تنتهِ هجر مضجعها فإنْ أبت ضربها فإن أبت أن تتَّعظ بالضرب بُعثَ الحكمان {فإن أطعنكم} فيما يُلتمس منهنَّ {فَلا تَبْغُوا عَلَيْهِنَّ سَبِيلا} لا تتجنَّوا عليهنَّ من العلل',NULL,'262',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (529,'{وإنْ خفتم} (علمتم) {شقاق بينهما} علمتم خلافاً بين الزَّوجين {فابعثوا حكماً} أَيْ: حاكماً وهو المانع من الظُّلم من أقاربه {وحَكَماً من أهلها} حتى يجتهدا وينظرا الظَّالم منهما فيأمراه بالرجوع إلى أمر الله أو يُفرِّقا إنْ رأيا ذلك {إن يريدا} أَي: الحكمان {إصلاحاً يوفق الله بينهما} مِن الزوج والمرأة بالصَّلاح {إنَّ الله كان عليماً خبيراً} بما في قلوب الزوجين والحكمين قوله:',NULL,'263',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (530,'{وبالوالدين أحساناً} أَيْ: أحسنوا بهما إحساناً وهو البرُّ مع لين الجانب {وبذي القربى} وهو ذو القرابة يصله ويتعطَّف عليه {واليتامى} يرفق بهم ويُدنيهم {والمساكين} ببذلٍ يسيرٍ أو ردٍّ جميلٍ {وَالْجَارِ ذي القربى} وهو الذي له مع حقِّ الجوار حقُّ القرابة {والجار الجنب} البعيد عنك في النَّسب {والصاحب بالجنب} وهو الرَّفيق في السَّفر {وابن السبيل} عابر الطَّريق (وقيل الضيف) يؤويه ويطعمه حتى يرحل {وما ملكت أيمانهم} أَيْ: المماليك {إِنَّ اللَّهَ لا يُحِبُّ مَنْ كَانَ مُخْتَالا} عظيماً في نفسه لا يقوم بحقوق الله {فخوراً} على عباده بما خوَّله الله من نعمته',NULL,'264',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (531,'{الذين يبخلون} أي: اليهود بخلوا بأموالهم أن ينفقوها في طاعة الله تعالى {ويأمرون الناس بالبخل} أمروا الأنصار ألا ينفقوا أموالهم على رسول الله صلى الله عليه وسلم وقالوا: إنَّا نخشى عليكم الفقر {ويكتمون ما آتاهم الله من فضله} أَيْ: ما في التَّوراة من أمر محمد صلى الله عليه وسلم ونعته',NULL,'264',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (532,'{والذين ينفقون أموالهم رئاء الناس} أَيْ: المنافقين {ومَنْ يكن الشيطانُ له قريناً} يسوِّل له ويعمل بأمره {فساء قريناً} بئس الصَّاحب الشَّيطان',NULL,'264',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (533,'{وماذا عليهم} أَيْ: على اليهود والمنافقين أَيْ: ما كان يضرُّهم {لَوْ آمَنُوا بِاللَّهِ وَالْيَوْمِ الآخِرِ وَأَنْفَقُوا مِمَّا رزقهم الله وكان الله بهم عليماً} لا يُثيبهم بما ينفقون رئاء الناس',NULL,'264',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (534,'{إنَّ الله لا يظلم} لا ينقص أحداً {مثقال} مقدار {ذرة} إن كان مؤمناً أثابه عليها الرِّزق في الدُّنيا والأجر في الآخرة وإنْ كان كافراً أطعمه بها في الدُّنيا {وإن تك حسنة} من مؤمن {يضاعفها} لعشرة أضعافها {ويؤتِ مِنْ لدنه} من عنده {أجراً عظيماً} وهو الجنَّة',NULL,'265',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (535,'{فكيف} أَيْ: فكيف يكون حال هؤلاء اليهود والمنافقين يوم القيامة؟ وهذا استفهامٌ ومعناه التَّوبيخ {إذا جئنا من كلِّ أُمَّة بشهيدٍ} أَيْ: بِنبيِّ كلِّ أُمَّةٍ يشهد عليها ولها {وجئنا بك} يا محمَّد {على هؤلاء شهيداً} على هؤلاء المنافقين والمشركين شهيداً تشهد عليهم بما فعلوا',NULL,'265',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (536,'{يومئذٍ} أَيْ: في ذلك اليوم {يودُّ الذين كفروا وعصوا الرسول} وقد عصوه في الدُّنيا {لو تسوَّى بهم الأرض} أَيْ: يكونون تراباً فيستوون مع الأرض حتى يصيروا وهي شيئاً واحداً {ولا يكتمون الله حديثاً} لأنَّ ما عملوه ظاهرٌ عند الله لا يقدرون على كتمانه',NULL,'265',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (537,'{يا أيها الذين آمنوا لا تقربوا الصلاة} أَيْ: مواضع الصَّلاة أيْ: المساجد {وأنتم سكارى} نُهوا عن الصَّلاة وعن دخول المسجد في حال السُّكْر وكان هذا قبل نزول تحريم الخمر وكان المسلمون بعد نزول هذه الآية يجتنبون السُّكْر والمُسكر أوقات الصَّلاة والسَّكران: المُختلط العقل الذي يهذي ولا يستمرُّ كلامه ألا ترى أنَّ الله تعالى قال: {حتى تعلموا ما تقولون} فإذا علم ما يقول لم يكن سكران ويجوز له الصَّلاة ودخول المسجد {ولا جُنباً} أَيْ: ولا تقربوها وأنتم جنبٌ {إلاَّ عابري سبيل} إلاَّ إذا عبرتم المسجد فدخلتموه من غير إقامةٍ فيه {حتى تغتسلوا} من الجنابة {وإنْ كنتم مرضى} أَيْ: مرضاً يضرُّه الماء كالقروح والجُدّري والجراحات {أو على سفر} أَيْ: مسافرين {أَوْ جَاءَ أَحَدٌ مِنْكُمْ مِنَ الْغَائِطِ} أَوْ الحدث {أو لامستم النساء} أَيْ: لمستموهنَّ بأيديكم {فلم تجدوا ماءً فَتَيَمَّمُوا صعيداً طيباً} تمسَّحوا بترابٍ طيِّبٍ مُنبتٍ',NULL,'265',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (538,'{أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الكتاب} وهم اليهود {يشترون الضلالة} أَيْ: يختارونها على الهدى بتكذيب محمد عليه السلام {ويريدون أن تضلوا السبيل} أن تضلُّوا أيُّها المؤمنون طريق الهدى',NULL,'266',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (539,'{والله أعلم بأعدائكم} فهو يُعْلِمكم ما هم عليه {وَكَفَى بِاللَّهِ ولياً وكفى بالله نصيراً} أَيْ: إنَّ ولايته ونصرته إيَّاكم تُغنيكم عن غيره من اليهود ومن جرى مجراهم',NULL,'267',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (540,'{من الذين هادوا} أَيْ: قومٌ {يحرِّفون الكلم عن مواضعه} أَيْ: يُغيِّرون صفة محمَّد صلى الله عليه وسلم وزمانه ونبوَّته في كتابهم {ويقولون سمعنا} قولك {وعصينا} أمرك {وَاسْمَعْ غَيْرَ مُسْمَعٍ} كانوا يقولون للنبي صلى الله عليه وسلم: اسمع ويقولون في أنفسهم: لا سمعت {وراعنا ليَّاً بألسنتهم} أَيْ: ويقولون راعنا ويوجِّهونها إلى شتم محمَّد عليه السَّلام بالرُّعونة وذكرنا أنَّ هذا كان سبَّاً بلُغتهم {وَلَوْ أَنَّهُمْ قَالُوا سَمِعْنَا وَأَطَعْنَا} مكان قولهم: سمعنا وعصينا وقالوا {واسمع وانظرنا} أَيْ: انظر إلينا بدل قولهم: راعنا {لكان خيراً لهم} عند الله {ولكن لَعَنَهُمُ الله بكفرهم} فلذلك لا يقولون ما هو خيرٌ لهم {فلا يؤمنون إلاَّ قليلاً} أَيْ: إيماناً قليلاً وهو قولهم: اللَّهُ ربُّنا والجنَّةُ حقٌّ والنَّارُ حقٌّ وهذا القليل ليس بشيءٍ مع كفرهم بمحمد صلى الله عليه وسلم وليس بمدحٍ لهم',NULL,'267',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (541,'{يا أيها الَّذِينَ أُوتُوا الْكِتَابَ آمِنُوا بِمَا نَزَّلْنَا مُصَدِّقًا لِمَا مَعَكُمْ مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهًا} أَيْ: نمحو ما فيها من عينٍ وفم وأنفٍ ومارن وحاجب فنجعلها كخفِّ البعير أو كحافز الدَّابة {فنردها على أدبارها} نُحوِّلها قبل ظهورهم {أو نلعنهم} أو نجعلهم قردة وخنازير كما فعلنا بأوائلهم {وَكَانَ أَمْرُ اللَّهِ مَفْعُولا} لا رادَّ لحكمه ولا ناقض لأمره',NULL,'267',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (542,'{إِنَّ اللَّهَ لا يَغْفِرُ أَنْ يُشْرَكَ بِهِ} الآية وعد الله تعالى في هذه الآية مغفرة ما دون الشِّرك فيعفو عن مَنْ يشاء ويغفر لمِنْ يشاء إلاَّ الشِّرك تكذيباً للقدريَّة وهو قوله: {ويغفر ما دون ذلك} أَيْ: الشِّرك {لِمَنْ يَشَاءُ وَمَنْ يُشْرِكْ بِاللَّهِ فَقَدِ افْتَرَى إِثْمًا عَظِيمًا} أَيْ: اختلق ذنباً غير مغفور',NULL,'268',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (543,'{ألم تر إلى الذين يزكون أنفسهم} أَيْ: اليهود قالوا: نحن أبناء الله وأحباؤه وما عملناه باللَّيل كُفِّر عنَّا بالنَّهار وما عملناه بالنَّهار كُفِّر عنَّا باللَّيل {بل الله يزكِّي من يشاء} أَيْ: يجعل مَنْ يشاء زاكياً طاهراً نامياً في الصَّلاح يعني: أهل التَّوحيد {ولا يُظلمون فتيلاً} لا ينقصون من الثواب قدر الفتيل وهو القشرة الرَّقيقة التي حول النَّواة ثمَّ عجَّب نبيَّه عليه السَّلام من كذبهم فقال:',NULL,'268',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (544,'{انظر كيف يفترون على الله الكذب} يعني: قولهم: يكفِّر عنَّا ذنوبنا {وكفى به} بافترائهم {إثماً مبيناً} أَيْ: كفى ذلك في التَّعظيم',NULL,'268',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (545,'{أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِنَ الكتاب} يعني: علماء اليهود {يؤمنون بالجبت} أَيْ: الأصنام {والطاغوت} سدنتها وتراجمتها وذلك أنَّهم حالفوا قريشاً على حرب رسول الله صلى الله عليه وسلم وسجدوا لأصنام قريش وقالوا لهم: أنتم أهدى من محمَّدٍ عليه السَّلام وأقوم طريقةً وديناً وهو قوله: {ويقولون للذين كفروا} يعني: قريشاً {هؤلاء أهدى من الذين آمنوا سبيلاً} وقوله:',NULL,'268',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (546,'قال تعالى {أُولَئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ وَمَنْ يَلْعَنِ اللَّهُ فَلَنْ تَجِدَ لَهُ نَصِيرًا}',NULL,'269',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (547,'{أم لهم نصيبٌ} أَيْ: بل أَلهم نصيب من الملك؟ يعني: ليس لليهود ملك ولو كان إذاً لهم لم يُؤتوا أحداً شيئاً وهو قوله: {فإذاً لا يؤتون الناس نقيراً} أَيْ: لضنُّوا بالقليل وصفهم الله بالبخل في هذه الآية والنَّقير يُضرب مثلاً للشَّيء القليل وهو نقرةٌ في ظهر النَّواة (منها) تبنت النَّخلة',NULL,'269',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (548,'{أم يحسدون الناس} يعني: محمَّداً عليه السَّلام {على ما آتاهم الله من فضله} حسدت اليهود محمَّداً عليه السَّلام على ما آتاه من النُّبوَّة وما أباح له من النِّساء وقالوا: لو كان نبيَّاً لشغله أمر بالنُّبوَّة عن النِّساء فقال الله تعالى: {فَقَدْ آتَيْنَا آلَ إِبْرَاهِيمَ الْكِتَابَ وَالْحِكْمَةَ} يعني: النُّبوَّة {وآتيناهم ملكاً عظيماً} يعني: ملك داود وسليمان عليهما السَّلام وما أُوتوا من النِّساء فكان لداود تسعٌ وتسعون ولسليمان ألفٌ من بين حُرَّةٍ ومملوكةٍ والمعنى: أيحسدون النَّبيَّ عليه السَّلام على النُّبوَّة وكثرة النِّساء وقد كان ذلك في آله لأنَّه من آل إبراهيم عليه السَّلام',NULL,'269',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (549,'{فمنهم} من أهل الكتاب {من آمن به} بمحمَّدٍ عليه السَّلام {ومنهم مَن صدَّ عنه} أعرض عنه فلم يؤمن {وَكَفَى بِجَهَنَّمَ سَعِيرًا} عذاباً لمَنْ لا يؤمن وقوله:',NULL,'269',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (550,'{كلما نضجت جلودهم بدلناهم جلوداً غيرها} يعني: أنَّ جلودهم إذا نضجت واحترقت جُدِّدت بأن تُردَّ إلى الحال التي كانت عليها غير محترقة {ليذوقوا العذاب} ليقاسوه وينالوه {إنَّ الله كان عزيزاً} قوياً لا يغلبه شيء {حكيماً} فيما دبَّر وقوله:',NULL,'269',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (551,'{وندخلهم ظلاً ظليلاً} يعني: ظلَّ هواء الجنَّة وهو ظليلٌ أَيْ: دائمٌ لا تنسخه الشَّمس',NULL,'270',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (552,'{إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تُؤَدُّوا الأَمَانَاتِ إِلَى أهلها} نزلت في ردِّ مفتاح الكعبة على عثمان بن طلحة الحجبيِّ حين أُخذ منه قسراً يوم فتح مكة فأمره الله تعالى بردِّه عليه ثمَّ هذه الآية عامَّةٌ في ردِّ الأمانات إلى أصحابها كيف ما كانوا {إِنَّ اللَّهَ نِعِمَّا يَعِظُكُمْ بِهِ} أَيْ: نِعمَ شيئاً يعظكم به وهو القرآن {إنَّ الله كان سميعاً} لمقالتكم في الأمانة والحكم {بصيراً} بما تعملون فيها قال أبو مرزوق: قال النبي صلى الله عليه وسلم لعثمان: أعطني المفتاح فقال: هاكَ بأمانة الله ودفعه إليه فأراد عليه السَّلام أن يدفعه إلى العباس فنزلت هذه الآية فقال النبي صلى الله عليه وسلم لعثمان: هاك بأمانة الله خالدةً تالدةً لا ينزعها عنكم إلاَّ ظالم ثمَّ إنَّ عثمان هاجر ودفع إلى أخيه شيبة فهو في ولده إلى اليوم',NULL,'270',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (553,'{يا أيها الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَأُولِي الأمر منكم} وهم العلماء والفقهاء وقيل: الأمراء والسَّلاطين وتجب طاعتهم فيما وافق الحقَّ {فإن تنازعتم} اختلفتم وتجادلتم وقال كلُّ فريق: القولُ قولي فَرُدُّوا الأمر في ذلك إلى كتاب الله وسنَّة رسوله {ذلك خيرٌ} أََيْ: ردُّكُمُ ما اختلفتم فيه إلى الكتاب والسنة وردُّك التجادل {وأحسن تأويلاً} وأحمدُ عاقبةً',NULL,'271',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (554,'{ألم تر إلى الذين يزعمون} الآية وقع نزاعٌ بين يهوديِّ ومنافق فقال اليهوديُّ: بيننا أبو القاسم وقال المنافق: لا بل نُحكِّم بيننا كعب بن الأشرف فنزلت هذه الآية وهو قوله: {يريدون أن يتحاكموا إلى الطاغوت} ومعناه: ذو الطُّغيان {وقد أمروا أن يكفروا به} أَيْ: أُمروا أن لا يوالوا غير أهل دينهم {ويريد الشيطان أن يضلَّهم ضلالاً بعيداً} لا يرجعون عنه إلى دين الله أبداً وهذا تعجيب للنبي صلى الله عليه وسلم مِن جهل مَن يعدل عن حكم الله إلى حكم الطَّاغوت مع زعمه بأنَّه يؤمن بالله ورسوله',NULL,'271',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (555,'{وإذا قيل لهم} أي: للمنافقين {تعالوا إلى ما أنزل الله} أَيْ: في القرآن من الحكم {وإلى الرسول} وإلى حكم الرَّسول {رأيت المنافقين يَصُدُّون عنك صدوداً} يُعرضون عنك إعراضاً إلى غيرك عداوةً للدِّين',NULL,'271',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (556,'{فكيف} أَيْ: فكيف يصنعون ويحتالون {إذا أصابتهم مصيبة} مجازاةً لهم على ما صنعوا وهو قوله: {بِمَا قَدَّمَتْ أيديهم} وتمَّ الكلام ههنا ثمَّ عطف على معنى ما سبق فقال: {ثم جاؤوك يحلفون بالله} أَيْ: تحاكموا إلى الطَّاغوت وصدُّوا عنك ثمَّ جاؤوك يحلفون وذلك أنَّ المنافقين أتوا النبي صلى الله عليه وسلم وحلفوا أنَّهم ما أرادوا بالعدول عنه في المحاكمة إلاَّ توفيقاً بين الخصوم أَيْ: جمعاً وتأليفاً وإحساناً بالتَّقريب في الحكم دون الحمل على مُرِّ الحقِّ وكلُّ ذلك كذبٌ منهم لأنَّ الله تعالى قال:',NULL,'271',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (557,'{أُولَئِكَ الَّذِينَ يَعْلَمُ اللَّهُ مَا فِي قُلُوبِهِمْ} أَيْ: من الشِّرك والنِّفاق {فأعرض عنهم} أيْ: اصفح عنهم {وعظهم} بلسانك {وَقُلْ لَهُمْ فِي أَنْفُسِهِمْ قَوْلا بليغاً} أَيْ: خوِّفهم بالله وازجرهم عمَّا هم عليه بأبلغ الزَّجر كيلا يستسِرُّوا الكفر',NULL,'272',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (558,'{وما أرسلنا من رسول إلاَّ ليطاع} فيما يأمرُ به ويحكم لا ليُعصى ويُطلب الحكم من غيره وقوله: {بإذن الله} أَيْ: لأنَّ الله أذن في ذلك وأمر بطاعته {ولو أنهم} أَيْ: المنافقين {إذ ظلموا أنفسهم} بالتَّحاكم إلى الكفَّار {جاؤوك فاستغفروا الله} فزعوا وتابوا إلى الله وقوله:',NULL,'272',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (559,'{فلا} أَيْ: ليس الأمر كما يزعمون أنَّهم آمنوا وهم يخالفون حكمك {وربك لا يؤمنون} حقيقة الإِيمان {حتَّى يحكموك فيما شجر} اختلف واختلط {بَيْنَهُمْ ثُمَّ لا يَجِدُوا فِي أَنْفُسِهِمْ حَرَجًا} ضيقاً وشكَّاً {ممَّا قضيت} أَيْ: أوجبتَ {ويسلموا} الأمر إلى الله وإلى رسوله من غير معارضةٍ بشيءٍ',NULL,'272',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (560,'{ولو أنَّا كتبنا عليهم} أَيْ: على هؤلاء المنافقين من اليهود {أن اقتلوا أنفسكم} كما كتبنا ذلك على بني إسرائيل {أو اخرجوا من دياركم} كما كتبنا على المهاجرين {ما فعلوه إلاَّ قليلٌ منهم} للمشقَّة فيه مع أنَّه كان ينبغي أن يفعلوه {ولو أنهم فعلوا ما يوعظون} ما يُؤمرون به من أحكام القرآن {لكان خيراً لهم} في معاشرتهم وفي ثوابهم {وأشدَّ تثبيتاً} منهم لأنفسهم في الدِّين وتصديقاً بأمر الله',NULL,'272',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (561,'{وإذاً لآتيناهم من لدنَّا} أَيْ: ممَّا لا يقدر عليه غيرنا {أجراً عظيماً} أَيْ: الجنَّة',NULL,'273',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (562,'{ولهديناهم} أرشدناهم {صراطاً مستقيماً} إلى دينٍ مستقيمٍ وهو دين الحنيفيَّة لا دين اليهوديَّة',NULL,'273',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (563,'{ومَن يطع الله} الآية قال المسلمون للنبي صلى الله عليه وسلم: ما لنا منك إلاَّ الدُّنيا فإذا كانت الآخرة رُفِعَت في الأعلى فحزن وحزنوا فنزلت {ومَنْ يطع الله} في الفرائض {والرسول} في السُّنن {فَأُولَئِكَ مَعَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبيين} أَيْ: إنَّه يستمتع برؤيتهم وزيارتهم فلا يتوهمنَّ أنَّه لا يراهم {والصديقين} أفاضل أصحاب الأنبياء {والشهداء} القتلى في سبيل الله {والصالحين} أَيْ: أهل الجنَّة من سائر المسلمين {وحسن أولئك} الأنبياء وهؤلاء {رفيقاً} أَيْ: أصحاباً ورفقاء',NULL,'273',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (564,'{ذلك} أَيْ: ذلك الثَّواب وهو الكون مع النَّبييِّن {الْفَضْلُ مِنَ اللَّهِ} تفضَّل به على مَنْ أطاعه {وكفى بالله عليماً} بخلقه أي: إنه عالم لا يخفى عليه شيء ولا يضيع عنده عمل ثمَّ حثَّ عباده المؤمنين على الجهاد فقال:',NULL,'273',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (565,'{يا أيها الذين آمنوا خذوا حذركم} سلاحكم عند لقاء العدوِّ {فانفروا} أَيْ: فانهضوا إلى لقاء العدوِّ {ثباتٍ} جماعاتٍ مُتفرِّقين إذا لم يكن معكم الرَّسول {أو انفروا جميعاً} إذا خرج الرَّسول إلى الجهاد',NULL,'274',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (566,'{وإنَّ منكم لَمَنْ ليُبطئنَّ} أَيْ: ليتخلفنَّ ويتثاقلنَّ عن الجهاد وهم المنافقون وجعلهم من المؤمنين من حيث إنَّهم أظهروا كلمة الإِسلام فدخلوا تحت حكمهم في الظَّاهر {فإن أصابتكم مصيبةٌ} من العدوِّ وجهدٌ من العيش {قَالَ قَدْ أَنْعَمَ اللَّهُ عليَّ} بالقعود حيث لم أحضر فيصيبني ما أصابكم',NULL,'274',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (567,'{ولئن أصابكم فضلٌ من الله} فتحٌ وغنيمة {ليقولنَّ} هذا المنافق قولَ نادمٍ حاسدٍ: {يَا لَيْتَنِي كُنْتُ مَعَهُمْ فَأَفُوزَ فَوْزًا عَظِيمًا} أي: لأسعد بمثل ما سعدوا به من الغنيمة وقوله: {كَأَنْ لَمْ تَكُنْ بَيْنَكُمْ وَبَيْنَهُ مَوَدَّةٌ} متصلٌ في المعنى بقوله: {قَدْ أَنْعَمَ اللَّهُ عَلَيَّ إِذْ لَمْ أَكُنْ مَعَهُمْ} {كأن لم تكن بينكم وبينه مودَّة} أَيْ: كأنْ لم يعاقدكم على الإِسلام ويعاضدكم على قتال عدوٍّكم ولم يكن بينكم وبينه مودة في الظَّاهر ثمَّ أمر المؤمنين بالقتال فقال:',NULL,'274',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (568,'{فليقاتل في سبيل الله الذين يشرون} أَيْ: يبيعون {الحياة الدُّنيا بالآخرة} أَيْ: بالجنَّة أَيْ: يختارون الجنَّة على البقاء في الدُّنيا {ومَنْ يُقاتل في سبيل الله فيقتل} فيستشهد {أو يغلب} فيظفر فكلاهما سواءٌ وهو معنى قوله: {فسوف نؤتيه أجراً عظيماً} ثواباً لا صفة له ثمَّ حضَّ المؤمنين على الجهاد في سبيله لاستنقاذ ضعفة المؤمنين من أيدي المشركين فقال:',NULL,'274',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (569,'{وَمَا لَكُمْ لا تُقَاتِلُونَ فِي سَبِيلِ اللَّهِ والمستضعفين من الرجال والنساء والوِلدان} وهم قومٌ بمكَّة استُضعفوا فَحُبسوا وعُذِّبوا {الذين يقولون ربنا أخرجنا} إلى دار الهجرة {من هذه القرية} مكَّة {الظالم أهلها} أَيْ: جعلوا لله شركاء {واجعل لنا من لَدُنْكَ ولياً} أَيْ: ولِّ علينا رجلاً من المؤمنين يوالينا {وَاجْعَلْ لَنَا مِنْ لَدُنْكَ نَصِيرًا} ينصرنا على عدوِّك فاستجاب الله دعاءَهم وولَّى عليهم رسولُ الله صلى الله عليه وسلم عتَّابَ بن أسيد وأعانهم (الله) به فكانوا أعزَّ بها من الظَّلمة قبل ذلك',NULL,'275',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (570,'{الَّذِينَ آمَنُوا يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ} في طاعة اللَّهِ {وَالَّذِينَ كَفَرُوا يُقَاتِلُونَ فِي سَبِيلِ الطَّاغُوتِ} أَيْ: في طاعة الشَّيطان {فقاتلوا أولياء الشيطان} عبدة الأصنام {إنَّ كيد الشيطان كان ضعيفاً} يعني: خذلانه إيَّاهم يوم قُتلوا ببدرٍ',NULL,'275',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (571,'{أَلَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أيديكم} عن قتال المشركين وأَدُّوا ما فُرض عليكم من الصَّلاة والزَّكاة نزلت في قوم من المؤمنين استأذنوا النبي صلى الله عليه وسلم وهم بمكَّة في قتال المشركين فلم يأذن لهم {فلما كتب عليهم القتال} بالمدينة {إذا فريقٌ منهم يخشون الناس} أَيْ: عذاب النَّاس بالقتل {كخشية الله} كما يخشى عذاب الله {أو أشدَّ} أكبرَ {خشية} وهذه الخشية إنَّما كانت لهم من حيث طبع البشريَّة لا على كراهية أمر الله بالقتال {وقالوا} جزعاً من الموت وحرصاً على الحياة: {ربنا لمَ كتبت} فرضتَ {علينا القتال لولا} هلاًّ {أخرتنا إلى أجل قريب} وهو الموت أَيْ: هلاَّ تركتنا حتى نموت بآجالنا وعافيتنا من القتل {قل} لهم يا محمَّدُ: {مَتَاعُ الدُّنْيَا قليل} أجل الدُّنيا قريبٌ وعيشها قليلٌ {والآخرة} الجنَّةُ {خيرٌ لمن اتقى} ولم يُشرك به شيئاً {ولا تظلمون فتيلاً} أَيْ: لا تُنقصون من ثواب أعمالكم مثل فتيل النَّواة ثمَّ أعلمهم أنَّ آجالهم لا تخطئهم ولو تمنَّعوا بأمنع الحصون فقال:',NULL,'275',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (572,'{أَيْنَمَا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِي بروج} حصونٍ وقصور {مشيدة} مطوَّلة مرفوعة وقيل: بروج السَّماء {وإن تصبهم} يعني: المنافقين واليهود {حسنة} خصب ورخص سعر {يَقُولُوا هَذِهِ مِنْ عِنْدِ اللَّهِ وَإِنْ تُصِبْهُمْ سيئة} جدبٌ وغلاءٌ {يقولوا هذه من عندك} من شؤم محمد وذلك أن النبي صلى الله عليه وسلم لمَّا قدم المدينة وكفرت اليهود أمسك الله عنهم ما كان قد بسط عليهم فقالوا: ما رأينا أعظم شؤماً من هذا نقصت ثمارنا وغلت أسعارنا منذ قدم علينا فقال الله تعالى: {قل كلٌّ} أًي: الخصب والجدب {من عند الله} من قِبَل اللَّهِ {فَمَالِ هَؤُلاءِ الْقَوْمِ لا يَكَادُونَ يَفْقَهُونَ حديثاً} لا يفهمون القرآن',NULL,'276',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (573,'{ما أصابك} يا ابن آدم {من حسنة} فتح وغنيمةٍ وخصبٍ فمن تفضُّل الله {وما أصابك من سيئة} من جدبٍ وهزيمةٍ وأمرٍ تكرهه {فمن نفسك} فبذنبك يا ابن آدم {وأرسلناك} يا محمدُ {للناس رسولاً وكفى بالله شهيداً} على رسالتك',NULL,'277',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (574,'{من يطع الرسول فقد أطاع الله} يعني: إنَّ طاعتكم لمحمد طاعةٌ لله {ومَنْ تولى} أعرض عن طاعته {فما أرسلناك عليهم حفيظاً} أَيْ: حافظاً لهم من المعاصي حتى لا تقع أَيْ: ليس عليك بأسٌ لتولِّيه لأنَّك لم ترسل عليهم حفيظاً من المعاصي',NULL,'277',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (575,'{ويقولون} أَي: المنافقون {طاعةٌ} أَيْ: طاعةٌ لأمرك {فإذا برزوا} خرجوا {من عندك بيَّت} قدَّر وأضمر {طائفة منهم غير الذي تقول} لك من الطَّاعة أَيْ: أضمروا خلاف ما أظهروا وقدَّروا ليلاً خلاف ما أعطوك نهاراً {واللَّهُ يكتب ما يبيِّتون} أَيْ: يحفظ عليهم ليُجَازَوا به {فأعرض عنهم} أَيْ: فاصفح عنهم وذلك أنه نُهي عن قتل المنافقين في ابتداء الإِسلام ثمَّ نُسخ ذلك بقوله: {جاهِد الكفَّار والمنافقين}',NULL,'277',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (576,'{أفلا يتدبرون القرآن} أَي: المنافقون (أفلا) يتأمَّلون ويتفكرون فيه {ولو كان} القرآن {مِنْ عِنْدِ غَيْرِ اللَّهِ لَوَجَدُوا فِيهِ اخْتِلافًا كثيراً} بالتَّناقض والكذب والباطل وتفاوت الألفاظ',NULL,'278',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (577,'{وإذا جاءهم أمرٌ من الأمن} نزلت في أصحاب الأراجيف وهم قومٌ من المنافقين كانوا يُرجفون بسرايا رسول الله صلى الله عليه وسلم ويُخبرون بما وقع بها قبل أن يُخبرَ به النبي صلى الله عليه وسلم فَيُضعفون قلوب المؤمنين بذلك ويُؤذون النبيَّ عليه السَّلام بسبقهم إيَّاه بالإِخبار وقوله: {أَمْرٌ مِنَ الأَمْنِ} حديثٌ فيه أمنٌ {أو الخوف} يعني: الهزيمة {أذاعوا به} أَيْ: أفشوه {وَلَوْ رَدُّوهُ إِلَى الرَّسُولِ وَإِلَى أُولِي الأَمْرِ منهم} ولو سكتوا عنه حتى يكون الرَّسول هو الذي يفشيه وأولو الأمر مثل أبي بكر وعمر وعلي رضي الله عنهم وقيل: أمراء السَّرايا {لَعَلِمَهُ الَّذِينَ يَسْتَنْبِطُونَهُ} يتبعونه ويطلبون علمَ ذلك {منهم} من الرسول وأولي الأمر {ولولا فضلُ الله} أي: الإِسلام {ورحمته} القرآن {لاتَّبَعْتُمُ الشَّيْطَانَ إِلا قَلِيلا} ممَّن عصم الله كالذين اهتدوا بعقولهم لترك عبادة الأوثان بغير رسولٍ ولا كتابٍ نحو زيد بن عمرو وورقة بن نوفل وطُلاَّب الدِّين وهذا تذكيرٌ للمؤمنين بنعمة الله عليهم حتى سلموا من النِّفاق وما ذُمَّ به المنافقون',NULL,'278',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (578,'{فَقَاتِلْ فِي سَبِيلِ اللَّهِ لا تُكَلَّفُ إِلا نفسك} أَيْ: إلاَّ فعلَ نفسك على معنى: أنَّه لا ضرر عليك في فعل غيرك فلا تهتمَّ بتخلُّف مَنْ يتخلَّف عن الجهاد {وَحَرِّضِ الْمُؤْمِنِينَ} حُضَّهم على القتال {عَسَى اللَّهُ} واجبٌ من الله {أن يكفَّ} يصرف ويمنع {بأس الذين كفروا} شدَّتهم وشوكتهم {واللَّهُ أشدُّ بأساً} عذاباً {وأشدُّ تنكيلاً} عقوبة',NULL,'278',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (579,'{مَنْ يشفع شفاعة حسنةً} هي كلُّ شفاعة تجوز في الدِّين {يكن له نصيبٌ منها} كان له فيها أجر {ومَنْ يشفع شفاعة سيئة} أَيْ: ما لا يجوز في الدين أن يشفع فيه {يكن له كفلٌ منها} أيْ: نصيبٌ من الوِزر والإِثم {وكان الله على كلِّ شيءٍ مقيتاً} مقتدراً',NULL,'279',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (580,'{وإذا حييتم بتحيَّةٍ} أَيْ: إذا سُلِّم عليكم بسلامٍ {فحيوا بأحسن منها} أَيْ: أجيبوا بزيادةٍ على التحيَّة إذا كان المُسَلِّم من أهل الإِسلام {أو ردُّوها} إذا كان من أهل الكتاب (فقولوا: عليكم ولا تزيدوا على ذلك) {إِنَّ اللَّهَ كَانَ عَلَى كُلِّ شَيْءٍ حَسِيبًا} (حفيظاً) مجازياً',NULL,'279',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (581,'{الله لا إله إلاَّ هو ليجمعنَّكم} أَيْ: واللَّهِ ليجمعنَّكم في القبور {إلى يوم القيامة لا ريبَ فيه} لا شكَّ فيه {ومَنْ أصدقُ من الله حديثاً} أَيْ: قولاً وخبراً يريد: أنَّه لا خُلفَ لوعده',NULL,'279',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (582,'{فما لكم في المنافقين فئتين} نزلت في قومٍ قَدِمُوا عَلَى رَسُولِ اللَّهِ صَلَّى اللَّهُ عَلَيْهِ وسلم المدينة فأقاموا ما شاء الله ثمَّ قالوا: إنَّا اجتوينا المدينة فأذن رسول الله صلى الله عليه وسلم لهم أَنْ يخرجوا فلمَّا خرجوا لم يزالوا يرحلون مرحلةً مرحلةً حتى لحقوا بالمشركين فاختلف المؤمنون فيهم فقال بعضهم: إنَّهم كفار مرتدُّون وقال آخرون: هم مسلمون حتى تعلم أنَّهم بدَّلوا فبيَّن الله كفرهم في هذه الآية والمعنى: ما لكم مختلفين في هؤلاء المنافقين على فئتين على فرقتين {والله أركسهم} ردَّهم إلى حكم الكفَّار من الذُّلِّ والصَّغار والسَّبي والقتل {بما كسبوا} بما أظهروا من الارتداد بعدما كانوا على النِّفاق {أتريدون} أيُّها المؤمنون {أن تهدوا} أَيْ: ترشدوا {مَنْ أضلَّ الله} لم يرشده الله أَيْ: يقولون: هؤلاء مهتدون والله قد أضلَّهم {وَمَنْ يُضْلِلِ الله فلن تجد له سبيلاً} أَيْ: ديناً وطريقاً إلى الحجَّة',NULL,'279',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (583,'{ودُّوا} أَيْ: هؤلاء {لو تكفرون كما كفروا فتكونون} أنتم وهم {سواءً فَلا تَتَّخِذُوا مِنْهُمْ أَوْلِيَاءَ} أَيْ: لا تُوالوهم ولا تُباطنوهم {حَتَّى يُهَاجِرُوا فِي سَبِيلِ اللَّهِ} حتى يرجعوا إلى رسول الله {فإن تولوا} عن الهجرة وأقاموا على ما هم عليه {فخذوهم} بالأسر {وَلا تَتَّخِذُوا مِنْهُمْ وَلِيًّا وَلا نصيراً} أيْ: لا تتولوهم ولا تستنصروا بهم على عدوِّكم',NULL,'280',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (584,'{إلاَّ الذين يصلون} أَيْ: فاقتلوهم حيث وجدتموهم إلاَّ الذين يتصلون ويلتجئون {إلى قوم بينكم وبينهم ميثاق} فيدخلون فيهم بالحلف والجوار {أو جاؤوكم حصرت صدورهم} يعني: أو يتصلون بقوم جاؤوكم وقد ضاقت صدورهم بقتالكم وهم بنو مدلجٍ كانوا صلحا للنبي صلى الله عليه وسلم وهذا بيان أنَّ مَن انضمَّ إلى قومٍ ذوي عهد مع النبي صلى الله عليه وسلم فله مثلُ حكمهم في حقن الدم والمال ثمَّ نُسخ هذا كلُّه بآية السَّيف ثمَّ ذكر الله تعالى مِنَّته بكفِّ بأس المعاهدين فقال: {وَلَوْ شَاءَ اللَّهُ لسلطهم عليكم فلقاتلوكم} يعني: إن ضيق صدوركم عن قتالهم إنَّما هو لقذف الله تعالى الرُّعب في قلوبهم ولو قوَّى الله تعالى قلوبهم على قتالكم لقاتلوكم {فإن اعتزلوكم} أَيْ: في الحرب {وألقوا إليكم السلم} أَي: الصُّلح {فَمَا جَعَلَ اللَّهُ لَكُمْ عَلَيْهِمْ سَبِيلا} في قتالهم وسفك دمائهم ثمَّ أمره بقتال مَنْ لم يكن على مثل سبيل هؤلاء فقال:',NULL,'280',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (585,'{ستجدون آخرين} الآية هؤلاء قومٌ كانوا يظهرون الموافقة لقومهم من الكفَّار ويظهرون الإسلام للنبي صلى الله عليه وسلم والمؤمنين يريدون بذلك الأمن في الفريقين فأطلع الله نبيَّه عليه السَّلام على نفاقهم وهو قوله: {يُرِيدُونَ أَنْ يَأْمَنُوكُمْ وَيَأْمَنُوا قَوْمَهُمْ} وقوله: {كُلَّ مَا رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيهَا} كلَّما دُعوا إلى الشِّرك رجعوا فيه {وأولئكم جعلنا لكم عليهم سلطاناً مبيناً} أَيْ: حجَّة بيِّنةً في قتالهم لأنَّهم غَدَرةٌ لا يُوفون لكم بعهدٍ',NULL,'281',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (586,'{وما كان لمؤمن أن يقتل مؤمناً} ألْبَتَّةَ {إلاَّ خطأ} إلاَّ أنَّه قد يخطئ المؤمن بالقتل {ومَنْ قتل مؤمناً خطأ} مثل أن يقصد بالرَّمي غيره فأصابه {فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَدِيَةٌ مُسَلَّمَةٌ إِلَى أَهْلِهِ} إلى جميع ورثته {إلاَّ أن يصدقوا} أَيْ: يعفوا ويتركوا الدية {فإن كان} المقتول {من قوم} حربٍ لكم وكان مؤمناً {فتحرير رقبة مؤمنة} كفارةً للقتل ولا دية لأنَّ عصبته وأهله كفَّار فلا يرثون ديته {وَإِنْ كَانَ مِنْ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُمْ مِيثَاقٌ} كأهل الذِّمة فتجب فيه الدِّية والكفَّارة {فمن لم يجد} الرَّقبة {فصيام شهرين متتابعين توبة من الله} أَيْ: ليقبل الله توبة القاتل حيث لم يبحث عن المقتول وحاله وحيث لم يجتهد حتى لا يخطئ',NULL,'281',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (587,'{ومَنْ يقتل مؤمناً متعمداً} الآية غلَّظ الله وعيد قاتل المؤمن عمداً للمبالغة في الرَّدع والزَّجر',NULL,'282',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (588,'{يا أيها الذين آمنوا إذا ضربتم} أَيْ: سرتم في الأرض {فتبيَّنوا} أَيْ: تأنَّوا وتثَّبتوا نزلت في رجلٍ كان قد انحاز بغنمٍ له إلى جبلٍ فلقي سريةً من المسلمين عليهم أسامة ين زيد فأتاهم وقال: السَّلام عليكم لا إله إلاَّ الله محمد رسول الله وكان قد أسلم فقتله أسامة واستاقوا غنمه فنزلت نهياً عن سفك دم مَنْ هو على مثل هذه الحالة وذلك أنَّ أسامة قال: إنَّما قالها مُتعوِّذاً فقال الله: {وَلا تَقُولُوا لِمَنْ أَلْقَى إِلَيْكُمُ السَّلامَ} أَيْ: حيَّاكم بهذه التَّحيَّة {لست مؤمناً تبتغون عرض الحياة الدنيا} أَيْ: متاعها من الغنائم {فَعِنْدَ اللَّهِ مَغَانِمُ كَثِيرَةٌ} يعني: ثواباً كبيراً لمَنْ ترك قتل مَنْ ألقى إليه السَّلام {كذلك كنتم من قبل} كُفَّاراً ضُلالاً كما كان هذا المقتول قبل إسلامِهِ ثمَّ منَّ الله عليكم بالإِسلام كما منَّ على المقتول أَيْ: إنَّ كلَّ مَنْ أسلم ممَّن كان كافراً فبمنزلة هذا الذي تعوَّذ بالإِسلام قُبِلَ منه ظاهرُ الإِسلام ثمَّ أعاد الأمر بالتبيُّن فقال: {فَتَبَيَّنُوا إِنَّ الله كان بما تعملون خبيراً} أَي: علم أنَّكم قتلتموه على ماله ثمَّ حمل رسول الله صلى الله عليه وسلم ديته إلى أهله وردَّ عليهم غنمه واستغفر لأسامة وأمره بعتق رقبةٍ',NULL,'282',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (589,'{لا يَسْتَوِي الْقَاعِدُونَ مِنَ الْمُؤْمِنِينَ غَيْرُ أُولِي الضرر} أي: الأصحَّاء الذين لا علَّة بهم تضرُّهم وتقطعهم عن الجهاد لا يستوي هؤلاء {وَالْمُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ فَضَّلَ الله المجاهدين بأموالهم وأنفسهم على القاعدين} من أهل العذر {درجةً} لأنَّ المجاهدين باشروا الطَّاعة والقاعدين من أهل العذر قصدوها وإن كانوا في الهمَّة والنيَّة على قصد الجهاد فمباشرة الطَّاعة فوق قصدها بالنِّيّة {وكلاً} من المجاهدين والقاعدين المعذورين {وعد الله الحسنى} الجنَّة {وفضَّل الله المجاهدين على القاعدين} من غير عذرٍ {أجراً عظيماً}',NULL,'283',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (590,'{درجاتٍ منه} أَيْ: منازلَ بعضُها فوقَ بعضٍ من منازل الكرامة',NULL,'283',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (591,'{إنَّ الذين توفاهم الملائكة} أَيْ: قبضت أرواحهم نزلت في قومٍ كانوا قد أسلموا ولم يهاجروا حتى خرج المشركون إلى بدر فخرجوا معهم فقتلوا يوم بدرٍ فضربت الملائكة وجوههم وأدبارهم وقوله: {ظالمي أنفسهم} بالمقام في دار الشِّرك والخروج مع المشركين لقتال المسلمين {قالوا فيم كنتم} أَيْ: قالت الملائكة لهؤلاء سؤال توبيخٍ وتقريع: أكنتم في المشركين أم كنتم في المسلمين؟ فاعتذروا بالضَّعف عن مقاومة أهل الشِّرك في دارهم فـ {قالوا كنا مستضعفين في الأرض} أَيْ: في مكة فحاجَّتهم الملائكة بالهجرة إلى غير دارهم و {قَالُوا أَلَمْ تَكُنْ أَرْضُ اللَّهِ وَاسِعَةً فَتُهَاجِرُوا فِيهَا فأولئك مأواهم جهنم وساءت مصيراً} أخبر الله تعالى أنَّ هؤلاء من أهل النَّار ثمَّ استثنى من صدق في أنَّه مستضعفٌ فقال:',NULL,'283',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (592,'{إلاَّ المستضعفين} أي: الذين يوجدون ضعفاء {لا يستطيعون حيلة} لا يقدرون على حيلةٍ ولا نفقةٍ ولا قوَّةٍ للخروج {وَلا يَهْتَدُونَ سَبِيلا} لا يعرفون طريقاً إلى المدينة',NULL,'284',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (593,'قال تعالى {فَأُولَئِكَ عَسَى اللَّهُ أَنْ يَعْفُوَ عَنْهُمْ وَكَانَ اللَّهُ عَفُوًّا غَفُورًا}',NULL,'284',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (594,'{وَمَنْ يُهَاجِرْ فِي سَبِيلِ اللَّهِ يَجِدْ فِي الأرض مراغماً} أَيْ: مهاجراً ومتحوَّلاً {كثيراً وسعة} في الرِّزق {وَمَنْ يَخْرُجْ مِنْ بَيْتِهِ مُهَاجِرًا إِلَى اللَّهِ} الآية نزلت في حبيب بن ضمرة اللَّيثي وكان شيخاً كبيراً خرج متوجِّهاً إلى المدينة فمات في الطَّريق فقال أصحاب رسول الله صلى الله عليه وسلم: لو وافى المدينة لكان أتمَّ أجراً فأنزل الله تعالى هذه الآية وأخبر أنَّ مَنْ قصد طاعةً ثمَّ أعجزه العذر عن تمامها كتب الله ثواب تمام تلك الطَّاعة ومعنى {وقع أجره على الله} وجب ذلك بإيجابه',NULL,'284',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (595,'{وَإِذَا ضَرَبْتُمْ فِي الأَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أن تقصروا} الآية نزلت في إباحة قصر الصَّلاة في السَّفر وظاهر القرآن يدل على أنَّ القصر يستباح بالسَّفر والخوف لقوله: {إن خفتم أن يفتنكم الذين كفروا} أَيْ: يقتلكم والإِجماع منعقدٌ على أنَّ القصر يجوز في السَّفر من غير خوف وثبتت السنَّة بهذا عن النبي صلى الله عليه وسلم ولكن ذكر الخوف في الآية على حال غالب أسفارهم في ذلك الوقت ثمَّ ذكر صلاة الخوف فقال:',NULL,'285',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (596,'{وإذا كنت فيهم} أَيْ: إذا كنت أيُّها النبيُّ مع المؤمنين في غزواتهم وخوفهم {فأقمت لهم الصلاة} أَي: ابتدأت بها إماماً لهم {فلتقم طائفة منهم معك} نصفهم يصلُّون معك {وليأخذوا} أَيْ: وليأخذ الباقون أسلحتهم {فإذا سجدوا} فإذا سجدت الطَّائفة التي قامت معك {فَلْيَكُونُوا من ورائكم} أَي: الذين أمروا بأخذ السِّلاح {ولتأت طائفة أخرى} أَي: الذين كانوا من ورائهم يحرسونهم {لم يصلوا} (معك الركعة الأولى) {فليصلوا معك} (الركعة الثانية) {وليأخذوا حذرهم} (من عدوهم) {وأسلحتهم} (سلاحهم معهم) يعني: الذين صلوا أول مرة {وَدَّ الَّذِينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وأمتعتكم} في صلاتكم {فيميلون عليكم ميلة واحدة} بالقتال {وَلا جُنَاحَ عَلَيْكُمْ إِنْ كَانَ بِكُمْ أَذًى مِنْ مَطَرٍ أَوْ كُنْتُمْ مَرْضَى أَنْ تضعوا أسلحتكم} ترخيصٌ لهم في ترك حمل السِّلاح في الصَّلاة وحملُه فرضٌ عند بعضهم وسنة مؤكدة عند بعضهم فرخص الله لهم في تركه لعذر المطر والمرض لأنَّ السِّلاح يثقل على المريض ويفسد في المطر {وخذوا حذركم} أَيْ: كونوا على حذرٍ في الصَّلاة كيلا يتغفَّلكم العدوُّ',NULL,'285',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (597,'{فإذا قضيتُمُ الصلاة} فرغتم من صلاة الخوف {فاذكروا الله} بتوحيده وشكره في جميع أحوالكم {فإذا اطمأننتم} رجعتم إلى أهلكم وأقمتم {فأقيموا الصلاة} أتموها {إِنَّ الصَّلاةَ كَانَتْ عَلَى الْمُؤْمِنِينَ كِتَابًا مَوْقُوتًا} مفروضاً موقَّتاً فرضه',NULL,'286',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (598,'{ولا تهنوا} أَيْ: لا تضعفوا {في ابتغاء القوم} يعني: أبا سفيان ومَنْ معه حين انصرفوا من أُحدٍ أمر الله تعالى نبيه أن يسير في آثارهم بعد الوقعة بأيَّام فاشتكى أصحابه ما بهم من الجراحات فقال الله تعالى: {إِنْ تَكُونُوا تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَ} أَيْ: إنْ ألمتم من جراحكم فهم أيضاً في مثل حالتكم من ألم الجراح {وَتَرْجُونَ مِنَ اللَّهِ} من نصر الله إيَّاكم وإظهار دينكم (في الدنيا) وثوابه في العقبى {مَا لا يَرْجُونَ} هم {وكان الله عليماً} بخلقه {حكيماً} فيما حكم',NULL,'286',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (599,'{إنا أنزلنا إليك الكتاب بالحق} هذه الآية وما بعدها نزلت في قصة طعمة بن أُبيرق سرق درعاً ثمَّ رمى بها يهودياً فلما طُلبت منه الدِّرع أحال على اليهوديِّ ورماه بالسَّرقة فاجتمع قوم طعمة وقوم اليهوديِّ وأتوا رسول الله صلى الله عليه وسلم فسأل قوم طعمة النبيَّ صلى الله عليه وسلم أنْ يجادل عن صاحبهم وأن يُبرِّيَه وقالوا: إنك إنْ لم تفعل افتضح صاحبنا وبرئ اليهودي فهم النبي صلى الله عليه وسلم أن يفعل فنزل فوله تعالى: {إنا أنزلنا إليك الكتاب بالحق} في الحكم لا بالتَّعدي فيه {لتحكم بين الناس بما أراك الله} أَيْ: فيما علَّمك الله {ولا تكن للخائنين} طعمة وقومه {خصيماً} مخاصماً عنهم',NULL,'287',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (600,'{واستغفر الله} من جدالك عن طعمة وهمِّك بقطع اليهوديِّ',NULL,'287',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (601,'{ولا تجادل عن الذين يختانون أنفسهم} يخونونها بالمعصية لأنَّ وبال خيانتهم راجعٌ عليهم يعني: طعمة وقومه {إِنَّ اللَّهَ لا يُحِبُّ مَنْ كَانَ خَوَّانًا أثيماً} أَيْ: طعمة لأنَّه خان في الدِّرع وأَثِم في رميه اليهوديَّ',NULL,'287',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (602,'{يستخفون} يستترون بخيانتهم {مِنَ النَّاسِ وَلا يَسْتَخْفُونَ مِنَ اللَّهِ وَهُوَ معهم} علم بما يخفون {إذ يبيِّتون} يُهيِّئون ويُقدِّرون ليلاً {ما لا يرضى من القول} وهو أنَّ طعمة قال: أرمي اليهوديَّ بأنَّه سارق الدِّرع وأحلف أنِّي لم أسرق فيقبل يميني لأنِّي على دينهم {وكان الله بما يعملون محيطا} علاما ثمَّ خاطب قوم طعمة فقال:',NULL,'288',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (603,'{ها أنتم هؤلاء جادلتم} خاصمتم {عنهم} عن طعمة وذويه {فِي الْحَيَاةِ الدُّنْيَا فَمَنْ يُجَادِلُ اللَّهَ عَنْهُمْ يَوْمَ الْقِيَامَةِ} أَيْ: لا أحد يفعل ذلك ولا يكون في ذلك اليوم عليهم وكيلٌ يقوم بأمرهم ويخاصم عنهم ثمَّ عرض التَّوبة على طعمة وقومه بقوله:',NULL,'288',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (604,'{ومَنْ يعمل سوءاً} معصيةً كما عمل قوم طعمة {أو يظلم نفسه} بذنبٍ كفعل طعمة {ثم يستغفر الله} الآية ثمَّ ذكر أنَّ ضرر المعصية إنَّما يلحق العاصي ولا يلحق الله من معصيته ضررٌ فقال:',NULL,'288',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (605,'{وَمَنْ يَكْسِبْ إِثْمًا فَإِنَّمَا يَكْسِبُهُ عَلَى نَفْسِهِ وَكَانَ اللَّهُ عَلِيمًا} بالسَّارق {حكيماً} حكم بالقطع على طعمة',NULL,'288',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (606,'{ومَنْ يكسب خطيئةً} ذنباً بينه وبين الله تعالى يعني: يمينه الكاذبة أنَّه ما سرق {أو إثماً} ذنباً بينه وبين النَّاس يعني: سرقته {ثمَّ يرمِ به} أَيْ: بإثمه {بريئاً} كما فعل طعمة حين رمى اليهوديَّ بالسَّرقة {فقد احتمل بهتانا} برمي البرئ {وإثماً مبيناً} باليمين الكاذبة والسَّرقة',NULL,'288',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (607,'{ولولا فضلُ الله عليك ورحمته} بالنبوَّة والعصمة {لهمَّت} لقد همَّت {طائفة منهم} من قوم طعمة {أن يضلوك} أَيْ: يُخطِّئوك في الحكم وذلك أنَّهم سألوا رسول الله صلى الله عليه وسلم أن يجادل عنهم ويقطع اليهوديَّ {وما يضلون إلاَّ أنفسهم} بتعاونهم على الإِثم والعدوان وشهادتهم الزُّور والبهتان {وَمَا يَضُرُّونَكَ مِنْ شَيْءٍ} لأنَّ الضَّرر على مَنْ شهد بغير حقٍّ ثمَّ منَّ الله عليه فقال: {وَأَنْزَلَ اللَّهُ عَلَيْكَ الْكِتَابَ وَالْحِكْمَةَ} أَي: القضاء بالوحي وبيَّن لك ما فيه الحكمة فلمَّا بان أنَّ السَّارق طعمة تناجى قومه في شأنه فأنزل الله تعالى:',NULL,'289',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (608,'{لا خير في كثير من نجواهم} أَيْ: مسارَّتهم {إلاَّ مَنْ أمر} أَيْ: إلاَّ في نجوى من أمر {بصدقةٍ} وقال مجاهد: هذه الآية عامَّةٌ للناس يريد أنَّه لا خير فيما يتناجى فيه النَّاس ويخوضون فيه من الحديث إلاَّ ما كان من أعمال البرِّ ثمَّ بيَّن أن ذلك إنما ينفع مَن ابتغى به ما عند الله فقال: {وَمَنْ يَفْعَلْ ذَلِكَ} الآية ثمَّ حكم رسول الله صلى الله عليه وسلم على طعمة بالقطع فخاف على نفسه الفضيحة فهرب إلى مكة ولحق بالمشركين فمزل قوله:',NULL,'289',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (609,'{ومَنْ يشاقق الرسول} أَيْ: يخالفه {من بعد ما تبيَّن له الهدى} الإِيمان بالله ورسوله وذلك أنَّه ظهر له من الآية ما فيه بلاغ بما أطلع الله سبحانه على أمره فعادى النبي صلى الله عليه وسلم بعد وضوح الحجَّة وقيام الدليل {وَيَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ} غير دين الموحِّدين {نُوَلِّهِ مَا تَوَلَّى} ندعه وما اختار لنفسه {ونصله جهنم} ندخله إيَّاها ونلزمه النَّار ثمَّ أشرك بالله طعمة فكان يعبد صنماً إلى أن مات فأنزل الله فيه:',NULL,'289',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (610,'{إِنَّ اللَّهَ لا يَغْفِرُ أَنْ يُشْرَكَ بِهِ} الآية ثمَّ نزل في أهل مكة:',NULL,'290',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (611,'{إن يدعون من دونه} أَيْ: ما يعبدون من دون الله {إلاَّ إناثاً} أَيْ: أصنامهم اللاَّت والعزَّى ومناة {وإن يدعون إلاَّ شيطاناً مريداً} ما يعبدون بعبادتهم لها إلاَّ شيطاناً خارجاً عن طاعة الله تعالى يعني: إبليس لأنَّهم أطاعوه فيما سوَّل لهم من عبادتها',NULL,'290',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (612,'{لعنه الله} دحره وأخرجه من الجنَّة {وقال} يعني إبليس: {لأَتَّخِذَنَّ من عبادك} بإغوائي وإضلالي {نصيباً مفروضاً} معلوماً أَيْ: مَن اتَّبعه وأطاعه',NULL,'290',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (613,'{ولأُضلنَّهم} عن الحقِّ {ولأمنينَّهم} أن لا جنَّة ولا نار وقيل: ركوب الأهواء {ولآمرنَّهم فليبتكنَّ آذان الأنعام} أي: فليقطعنَّها يعني: البحائر وسيأتي بيان ذلك فيما بعد في سورة المائدة {ولآمرنهم فليغيرن خلق الله} أَيْ: دينه يكفرون ويحرِّمون الحلال ويحلون الحرام {وَمَنْ يَتَّخِذِ الشَّيْطَانَ وَلِيًّا مِنْ دُونِ اللَّهِ} أيْ: (مَنْ) يُطعه فيما يدعو إليه من الضَّلال {فَقَدْ خَسِرَ خُسْرَانًا مُبِينًا} خسر الجنَّة ونعيمها',NULL,'290',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (614,'{يعدهم} طول العمر في الدُّنيا {ويمنيهم} نيل المراد منها {وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلا غُرُورًا} أَيْ: إلاَّ ما يغرُّهم من إيهام النفع فيما فيه ضرر',NULL,'291',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (615,'{أولئك} أَي: الذين اتَّخذوا الشَّيطان وليَّاً {مأواهم} مرجعهم ومصيرهم {جهنم ولا يجدون عنها محيصاً} معدلاً',NULL,'291',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (616,'{وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي من تحتها الأنهار} الآية',NULL,'291',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (617,'{ليس بأمانيكم ولا أماني أهل الكتاب} نزلت في كفَّار قريش واليهود قالت قريش: لا نُبعث ولا نُحاسب وقالت اليهود: {لن تمسنا النار إلا أياما معدودةً} فنزلت هذه الآية أَيْ: ليس الأمر بأمانيِّ اليهود والكفَّار {مَنْ يعمل سوءاً} كفراً وشركاً {يُجْزَ بِهِ وَلا يَجِدْ لَهُ مِنْ دُونِ الله ولياً} يمنعه {ولا نصيراً} ينصره ثمَّ بيَّن فضيلة المؤمنين على غيرهم بقوله:',NULL,'291',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (618,'{ومن يعمل من الصالحات} الآية وبقوله:',NULL,'291',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (619,'{ومَنْ أحسن ديناً ممَّن أسلم وجهه} أَيْ: توجَّه بعبادته إلى الله خاضعاً له {وهو محسنٌ} مُوَحِّدٌ {واتَّبع ملَّة إبراهيم حنيفاً} ملَّةُ إبراهيم داخلةٌ في ملَّة محمد عليهما السَّلام فمَنْ أقرَّ بملَّة محمَّدٍ فقد اتَّبع ملَّة إبراهيم عليه السَّلام {واتخذ الله إبراهيم خليلاً} صفيَّاً بالرِّسالة والنُّبوَّة مُحبَّاً له خالص الحب',NULL,'292',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (620,'قال تعالى {ولله ما في السماوات وَمَا فِي الأَرْضِ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ محيطا}',NULL,'292',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (621,'{ويستفتونك} يطلبون منك الفتوى {في النساء} في توريثهنَّ كانت العرب لا تورث النِّساء والصِّبيان شيئاً من الميراث {قُلِ اللَّهُ يُفْتِيكُمْ فِيهِنَّ وَمَا يُتْلَى عَلَيْكُمْ} أَي: القرآن يُفتيكم أيضاً يعني: آية المواريث في أوَّل هذه السورة {في} ميراث {يتامى النساء} لأنَّها في قصَّة أم كجَّة وكانت لها بنات {اللاتي لا تؤتونهن ما كتب لهنَّ} أَيْ: فُرض لهن من الميراث {وترغبون} عن {أن تنكحوهنَّ} لدمامتهنَّ قالت عائشة رضي الله عنها: نزلت في اليتيمة يرغب وليها عن نكاحها ولا ينكحها فيعضلها طعما في ميراثها فنُهي عن ذلك {والمستضعفين من الولدان} أَيْ: يُفتيكم في الصِّغار من الغلمان والجواري أن تعطوهنَّ حقهنَّ {وأن تقوموا} أَيْ: وفي أن تقوموا {لليتامى بالقسط} أَي: بالعدل في مهورهنَّ ومواريثهنَّ {وما تفعلوا من خير} من حسنٍ فيما أمرتكم به {فَإِنَّ اللَّهَ كان به عليماً} يجازيكم عليه',NULL,'292',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (622,'{وإن امرأة خافت} علمت {من بعلها} زوجها {نشوزاً} ترفُّعاً عليها لبغضها وهو أن يترك مجامعتها {أو إعراضاً} بوجهه عنها {فَلا جُنَاحَ عَلَيْهِمَا أَنْ يُصْلِحَا بَيْنَهُمَا صُلْحًا} في القسمة والنَّفقة وهي أن ترضى هي بدون حقِّها أو تترك من مهرها شيئاً ليسوِّي الزَّوج بينها وبين ضرَّتها في القسمة وهذا إذا رضيت بذلك لكراهة فراق زوجها ولا تجبر على هذا لأنَّها إنْ لم ترض بدون حقِّها كان الواجب على الزَّوج أن يوفيها حقَّها من النَّفقة والمبيت {والصلحُ خيرٌ} من النُّشوز والإِعراض أَيْ: إنْ يتصالحا على شيءٍ خيرٌ من أن يُقيما على النُّشوز والكراهة بينهما {وأحضرت الأنفس الشح} أَيْ: شحَّت المرأة بنصيبها من زوجها وشحَّ الرَّجل على المرأة بنفسه إذا كان غيرها أحبَّ إليه منها {وإن تحسنوا} العشرة والصُّحبة {وتتقوا} الجور والميل {فَإِنَّ اللَّهَ كَانَ بِمَا تعملون خبيراً} لا يضيع عنده شيء',NULL,'293',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (623,'{وَلَنْ تَسْتَطِيعُوا أَنْ تَعْدِلُوا بَيْنَ النِّسَاءِ وَلَوْ حرصتم} لن تقدروا على التَّسوية بينهنَّ في المحبَّة ولو اجتهدتم {فَلا تَمِيلُوا كُلَّ الْمَيْلِ} إلى التي تحبُّون في النَّفقة والقسمة {فتذروها كالمعلقة} فتدعوا الأخرى كأنَّها معلَّقةٌ لا أيِّماً ولا ذات بعل {وإن تصلحوا} بالعدل في القسم {وتتقوا} الجور {فَإِنَّ اللَّهَ كَانَ غَفُورًا رَحِيمًا} لما ملت إلى التي تحبُّها بقلبك ولمَّا ذكر جواز الصُّلح بينهما إنْ أحبَّا أن يجتمعا ذكر بعده الافتراق فقال:',NULL,'293',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (624,'{وإن يتفرقا} أَيْ: إنْ أبت المرأة الكبيرة الصُّلْح وأبت إلاَّ التَّسوية بينها وبين الشَّابَّة فتفرَّقا بالطَّلاق فقد وعد الله لهما أن يُغني كلَّ واحدٍ منهما عن صاحبه بعد الطَّلاق من فضله الواسع بقوله: {يُغْنِ اللَّهُ كُلًّا مِنْ سَعَتِهِ وَكَانَ اللَّهُ واسعاً} لجميع خلقه في الرِّزق والفضل {حكيماً} فيما حكم ووعظ',NULL,'294',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (625,'قال تعالى {ولله ما في السماوات وَمَا فِي الأَرْضِ وَلَقَدْ وَصَّيْنَا الَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلِكُمْ وَإِيَّاكُمْ أَنِ اتَّقُوا اللَّهَ وإن تكفروا فإنَّ لله ما في السماوات وَمَا فِي الأَرْضِ وَكَانَ اللَّهُ غَنِيًّا حَمِيدًا}',NULL,'294',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (626,'قال تعالى {ولله ما في السماوات وَمَا فِي الأَرْضِ وَكَفَى بِاللَّهِ وَكِيلا}',NULL,'294',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (627,'{إنْ يشأ يذهبكم أيها الناس} يعني: المشركين والمنافقين {ويأت بآخرين} أمثل وأطوع لله منكم',NULL,'294',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (628,'{مَنْ كان يريد ثواب الدنيا} أَيْ: متاعها {فعند الله ثواب الدنيا والآخرة} أَيْ: خير الدُّنيا والآخرة عنده فليطلب ذلك منه وهذا تعريضٌ بالكفَّار الذين كانوا لا يؤمنون بالبعث وكانوا يقولون: ربنا آتنا في الدنيا وما لهم في الآخرة من خلاق',NULL,'294',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (629,'{يا أيها الذين آمنوا كونوا قوامين بالقسط} قائمين بالعدل {شُهَدَاءَ لِلَّهِ وَلَوْ عَلَى أَنْفُسِكُمْ أَوِ الْوَالِدَيْنِ والأقربين} أَي: اشهدوا لله بالحقِّ وإن كان الحقُّ على نفس الشَّاهد أو على والديه أو أقربيه {إن يكن} المشهود عليه {غنياً أو فقيراً} فلا تحابوا غنياً لغناه ولا تحيفوا على الفقير لفقره {فالله أولى بهما} أَيْ: أعلمُ بهما منكم لأنَّه يتولَّى علم أحوالهما {فلا تتبعوا الهوى} في الشَّهادة واتقوا {إن تعدلوا} أَيْ: تميلوا وتجوروا {وإنْ تلووا} أَيْ: تدّافعوا الشَّهادة {أو تعرضوا} تجحدوها وتكتموها {فَإِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا} فيجازي المحسن بإحسانه والمسيء بإساءته',NULL,'295',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (630,'{يا أيها الذين آمنوا آمِنوا بالله ورسوله} أَي: اثبتوا على الإِيمان {والكتاب الذي نزَّل على رسوله} القرآن {والكتاب الذي أنزل من قبل} أَيْ: كلِّ كتاب أنزل على نبيٍّ قبل القرآن',NULL,'295',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (631,'{إن الذين آمنوا} أي: اليهود آمنوا بالتَّوراة {ثمَّ كفروا} بمخالفتها {ثم آمنوا} بالإِنجيل {ثمَّ كفروا} بمخالفته {ثم ازدادوا كفراً} بمحمدٍ {لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ} ما أقاموا على ما هم عليه {ولا ليهديهم سبيلاً} سبيل هدى ثمَّ ألحق المنافقين بهم لأنهم كانوا يتولونهم فقال:',NULL,'295',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (632,'{بَشِّرِ الْمُنَافِقِينَ بِأَنَّ لَهُمْ عَذَابًا أَلِيمًا}',NULL,'296',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (633,'{الَّذِينَ يتخذون الكافرين أولياء من دون المؤمنين} هذه الآية من صفة المنافقين وكانوا يُوالون اليهود مخالفةً للمسلمين يتوهَّمون أنَّ لهم القوَّة والمنعة وهو معنى قوله: {أَيَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ} أَي: القوَّة بالظهور على محمد صلى الله عليه وسلم {فإنَّ العزة} أَي: الغلبة والقوَّة {لله جميعاً}',NULL,'296',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (634,'{وقد نزل عليكم} أيها المؤمنون {في الكتاب} في القرآن {أنْ إذا سمعتم} الكفر بآيات الله والاستهزاء بها {فَلا تَقْعُدُوا مَعَهُمْ حَتَّى يَخُوضُوا فِي حَدِيثٍ} غير الكفر والاستهزاء يعني: قوله في سورة الأنعام {وإذا رأيت الذين يخوضون في آياتنا} هذه كانت مما نزل عليهم في الكتاب وقوله: {إنكم إذاً مثلهم} يعني: إنْ قعدتم معهم راضين بما يأتون من الكفر بالقرآن والاستهزاء به وذلك أنَّ المنافقين كانوا يجلسون إلى أحبار اليهود فيسخرون من القرآن فنهى الله سبحانه المسلمين عن مجالستهم {إِنَّ اللَّهَ جَامِعُ الْمُنَافِقِينَ وَالْكَافِرِينَ فِي جَهَنَّمَ جميعاً} يريد: أنَّهم كما اجتمعوا على الاستهزاء بالآيات يجتمعون في جهنَّم على العذاب',NULL,'296',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (635,'{الذين يتربصون بكم} يعني: المنافقين ينتظرون بكم الدَّوائر {فإن كان لكم فتحٌ من الله} ظهورٌ على اليهود {قالوا ألم نكن معكم} فأعطونا من الغنيمة {وَإِنْ كَانَ لِلْكَافِرِينَ نَصِيبٌ} من الظَّفر على المسلمين {قالوا} لهم {ألم نستحوذ} نغلب {عليكم} نمنعكم عن الدُّخول في جملة المؤمنين {ونمنعكم من المؤمنين} بتخذيلهم عنكم ومراسلتنا إيَّاكم بأخبارهم {فالله يحكم بينكم} يعني: بين المؤمنين والمنافقين {يوم القيامة} يعني: أنَّه أخَّر عقابهم إلى ذلك اليوم ورفع عنهم السَّيف (في الدُّنيا) {وَلَنْ يَجْعَلَ اللَّهُ لِلْكَافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلا} أَيْ: حجَّةً يوم القيامة لأنَّه يفردهم بالنَّعيم وما يشاركونهم فيه من الكرامات بخلاف الدُّنيا',NULL,'296',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (636,'{إنَّ المنافقين يخادعون الله} أَيْ: يعملون عمل المخادع بما يظهرونه ويبطنون خلافه {وهو خادعهم} مجازيهم جزاءَ خداعهم وذلك أنَّهم يُعطون نوراً كما يُعطى المؤمنون فإذا مضوا قليلاً أطفىء نورهم وبقوا في الظُّلمة {وَإِذَا قَامُوا إِلَى الصلاة} مع النَّاس {قاموا كسالى} متثاقلين {يراؤون الناس} ليرى ذلك النَّاس لا لاتِّباع أمر الله يعني: ليراهم النَّاس مُصلِّين لا يريدون وجه الله {ولا يذكرون الله إلاَّ قليلاً} لأنَّهم يعملونه رياءً وسمعةً ولو أرادوا به وجه الله لكان كثيراً',NULL,'297',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (637,'{مذبذبين بين ذلك} مُردَّدين بين الكفر والإِيمان ليسوا بمؤمنين مخلصين ولا مشركين مصرِّحين بالشِّرك {لا إلى هؤلاء ولا إلى هؤلاء} لا من الأنصار ولا من اليهود {وَمَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلا} من أضلَّه الله فلن تجد له ديناً',NULL,'297',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (638,'{يا أيها الَّذِينَ آمَنُوا لا تَتَّخِذُوا الْكَافِرِينَ أَوْلِيَاءَ مِنْ دون المؤمنين} يعني: الأنصار يقول: لا توالوا اليهود من قريظة والنَّضير {أَتُرِيدُونَ أَنْ تَجْعَلُوا لِلَّهِ عَلَيْكُمْ سُلْطَانًا مُبِينًا} حجَّة بيِّنة في عقابكم بموالاتكم اليهود أيْ: إنَّكم إذا فعلتم ذلك صارت الحجُّة عليكم في العقاب',NULL,'298',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (639,'{إِنَّ الْمُنَافِقِينَ فِي الدَّرْكِ الأَسْفَلِ مِنَ النَّارِ} أَيْ: في أسفل درج النَّار {ولن تجد لهم نصيراً} مانعاً يمنعهم من عذاب الله',NULL,'298',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (640,'{إلاَّ الذين تابوا} من النِّفاق {وأصلحوا} العمل {واعتصموا بالله} التجأوا إليه {وأخلصوا دينهم لله} من شائب الرِّياء {فأولئك مع المؤمنين} أَيْ: هم أدنى منهم بعد هذا كلِّه ثمَّ أوقع أجر المؤمنين في التَّسويف لانضمامهم إليهم فقال: {وَسَوْفَ يُؤْتِ اللَّهُ الْمُؤْمِنِينَ أَجْرًا عظيماً}',NULL,'298',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (641,'{ما يفعل الله بعذابكم} بعذاب خلقه {إن شكرتم} اعترفتم بإحسانه {وآمنتم} بنبيِّه {وكان الله شاكراً} للقليل من أعمالكم {عليماً} بنيَّاتكم',NULL,'298',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (642,'{لا يُحِبُّ اللَّهُ الْجَهْرَ بِالسُّوءِ مِنَ الْقَوْلِ} نزلت ترخيصاً للمظلوم أنْ يجهر بشكوى الظَّالم وذلك أنَّ ضيفاً نزل بقوم فأساؤوا قِراه فاشتكاهم فنزلت هذه الآية رخصةً في أن يشكوا وقوله: {إلاَّ من ظلم} لكن من ظلم فإنَّه يجهر بالسُّوء من القول وله ذلك {وكان الله سميعاً} لقول المظلوم {عليماً} بما يضمره أَيْ: فليقل الحقِّ ولا يتعدَّ ما اُذن له فيه',NULL,'298',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (643,'{إن تبدوا خيراً} من أعمال البرِّ {أو تخفوه أو تعفوا عن سوء} يأتيك من أخيك المسلم {فَإِنَّ اللَّهَ كَانَ عفواً} لمَنْ عفا {قديراً} على ثوابه',NULL,'299',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (644,'{إنَّ الذين يكفرون بالله ورسله} هم اليهود كفروا بعيسى عليه السَّلام والإِنجيل ومحمدٍ عليه السَّلام والقرآن {وَيُرِيدُونَ أَنْ يُفَرِّقُوا بَيْنَ اللَّهِ وَرُسُلِهِ} بأن يؤمنوا بالله ويكفروا بالرُّسل {ويقولون نؤمن ببعض} الرّسل {ونكفر} ببعضهم {وَيُرِيدُونَ أَنْ يَتَّخِذُوا بَيْنَ ذَلِكَ سَبِيلا} بيْن الإِيمان بالبعض والكفر بالبعض ديناً يدينون به',NULL,'299',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (645,'{أولئك هم الكافرون حقاً} أَيْ: إنَّ إيمانهم ببعض الرُّسل لا يُزيل عنهم اسم الكفر ثمَّ نزل في المؤمنين',NULL,'299',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (646,'{والذين آمنوا بالله ورسله} الآية',NULL,'299',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (647,'{يسألك أهل الكتاب} سألت اليهود رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم أَنْ يأتيهم بكتابٍ جُمْلَةً من السَّماء كما أتى به موسى فأنزل الله تعالى هذه الآية وقوله: {فَقَدْ سَأَلُوا مُوسَى أَكْبَرَ من ذلك} يعني: السَّبعين الذين ذكروا في قوله: {وَإِذْ قُلْتُمْ يَا مُوسَى لَنْ نُؤْمِنَ لَكَ} {ثُمَّ اتَّخَذُوا الْعِجْلَ} يعني: الذين خلَّفهم موسى مع هارون {من بعد ما جاءتهم البينات} العصا واليد وفلق البحر {فَعَفَوْنَا عَنْ ذَلِكَ} لم نستأصل عبدة العجل {وَآتَيْنَا مُوسَى سُلْطَانًا مُبِينًا} حجَّةً بيِّنةً قوي بها على مَنْ ناوأه',NULL,'299',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (648,'{ورفعنا فوقهم الطور} حين امتنعوا من قبول شريعة التَّوراة {بميثاقهم} أَيْ: بأخذ ميثاقهم {وقلنا لهم لا تعدوا في السبت} لا تعتدوا باقتناص السَّمك فيه {وأخذنا منهم ميثاقاً غليظاً} عهداً مؤكَّداً في النبيِّ صلى الله عليه وسلم',NULL,'300',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (649,'{فبما نقضهم ميثاقهم} أَيْ: فبنقضهم و (ما) زائدةٌ للتَّوكيد وقوله {بل طبع اللَّهُ عليها بكفرهم} أَيْ: ختم الله على قلوبهم فلا تعي وَعْظاً مجازاةً لهم على كفرهم {فلا يؤمنون إلاَّ قليلاً} يعني: الذين آمنوا',NULL,'300',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (650,'{وبكفرهم} بالمسيح {وَقَوْلِهِمْ عَلَى مَرْيَمَ بُهْتَانًا عَظِيمًا} حين رموها بالزِّنا',NULL,'300',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (651,'{وَقَوْلِهِمْ إِنَّا قَتَلْنَا الْمَسِيحَ عِيسَى ابْنَ مَرْيَمَ رَسُولَ اللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَكِنْ شبه لهم} أَيْ: ألقي لهم شبه عيسى على غيره حتى ظنُّوه لمَّا رأوه أنَّه المسيح {وإنَّ الذين اختلفوا فيه} أَيْ: في قتله وذلك أنَّهم لمَّا قتلوا الشَّخص المشَبَّه به كان الشَّبَه أُلقي على وجهه ولم يُلق على جسده شبهُ جسدِ عيسى فلمَّا قتلوه ونظروا إليه قالوا: الوجه وجه عيسى والجسد جسد غيره فاختلفوا فقال بعضهم: هذا عيسى وقال بعضهم: ليس بعيسى وهذا معنى قوله: {لفي شك منه} أَيْ: مِنْ قتله {ما لهم به} بعيسى {من علم} قُتِل أو لم يقتل {إلاَّ اتباع الظن} لكنَّهم يتَّبعون الظَّنَّ {وما قتلوه يقيناً} وما قتلوا المسيح على يقين من أنَّه المسيح',NULL,'300',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (652,'{بل رفعه الله إليه} أَيْ: إلى الموضع الذي لا يجري لأحدٍ سوى الله فيه حكمٌ وكان رفعُه إلى ذلك الموضع رفعاً إليه لأنَّه رُفع عن أن يجري عليه حكم أحدٍ من العباد {وكان الله عزيزاً} في اقتداره على نجاة مَنْ يشاء من عباده {حكيماً} في تدبيره في النَّجاة',NULL,'301',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (653,'{وَإِنْ مِنْ أَهْلِ الْكِتَابِ إِلا لَيُؤْمِنَنَّ بِهِ} أَيْ: ما مِن أهل الكتاب أحدٌ إلاَّ ليؤمننَّ بعيسى {قبل موته} إذا عاين المَلَك ولا ينفعه حينئذٍ إيمانه ولا يموت يهوديٌّ حتى يؤمن بعيسى {وَيَوْمَ الْقِيَامَةِ يَكُونُ عَلَيْهِمْ شَهِيدًا} على أنْ قد بلَّغ الرِّسالة وأقرَّ بالعبوديَّة على نفسه',NULL,'301',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (654,'{فبظلم من الذين هادوا} عاقب الله اليهود إلى ظلمهم وبغيهم بتحريم اشياء عليهم وهي ما ذُكر في قوله: {وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا كُلَّ ذي ظفر} ثمَّ استثنى مؤمنيهم فقال:',NULL,'301',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (655,'قال تعالى {وَأَخْذِهِمُ الرِّبَا وَقَدْ نُهُوا عَنْهُ وَأَكْلِهِمْ أَمْوَالَ النَّاسِ بِالْبَاطِلِ وَأَعْتَدْنَا لِلْكَافِرِينَ مِنْهُمْ عَذَابًا أَلِيمًا}',NULL,'301',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (656,'{لكن الراسخون} يعني: المبالغين في علم الكتاب منهم كعبد الله بن سلام وأصحابه {والمؤمنون} من أصحاب محمد صلى الله عليه وسلم {يُؤْمِنُونَ بِمَا أُنْزِلَ إِلَيْكَ وَمَا أُنْزِلَ مِنْ قَبْلِكَ وَالْمُقِيمِينَ الصَّلاةَ وَالْمُؤْتُونَ الزَّكَاةَ وَالْمُؤْمِنُونَ بِاللَّهِ واليوم الآخر أولئك سنؤتيهم أجراً عظيماً} ظاهر إلى قوله:',NULL,'302',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (657,'قال تعالى {إِنَّا أَوْحَيْنَا إِلَيْكَ كَمَا أَوْحَيْنَا إِلَى نُوحٍ وَالنَّبِيِّينَ مِنْ بَعْدِهِ وَأَوْحَيْنَا إِلَى إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالأَسْبَاطِ وَعِيسَى وَأَيُّوبَ وَيُونُسَ وَهَارُونَ وسليمان وآتينا داود زبورا}',NULL,'302',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (658,'قال تعالى {وَرُسُلا قَدْ قَصَصْنَاهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلا لَمْ نَقْصُصْهُمْ عَلَيْكَ وَكَلَّمَ اللَّهُ مُوسَى تَكْلِيمًا}',NULL,'302',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (659,'{رسلاً مبشرين} أَيْ: بالثَّواب على الطَّاعة {ومنذرين} بالعقاب على المعصية {لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اللَّهِ حُجَّةٌ بَعْدَ الرسل} فيقولوا: ما أرسلت إلينا رسولاً يعلّمنا دينك فبعثنا الرُّسل قطعاً لعذرهم',NULL,'302',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (660,'{لكن الله يشهد} نزلت حين قالت اليهود - لما سئلوا عن نبؤة محمَّدٍ - ما نشهد له بذلك فقال الله تعالى: {لكن الله يشهد} أَيْ: يبيِّن نبوَّتك {بما أنزل إليك} من القرآن ودلائله {أنزله بعلمه} أَيْ: وهو يعلم أنَّك أهلٌ لإِنزاله عليك لقيامك به {والملائكة يشهدون} لك بالنُّبوَّة إنْ جحدت اليهود وشهادة الملائكة إنَّما تُعرف بقيام المعجزة فمَنْ ظهرت معجزته شهدت الملائكة بصدقه {وكفى بالله شهيداً} أَيْ: كفى الله شهيدا',NULL,'302',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (661,'قال تعالى {إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ قَدْ ضَلُّوا ضَلالا بَعِيدًا}',NULL,'302',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (662,'{إنَّ الذين كفروا} يعني اليهود {وظلموا} محمداً عليه السَّلام بكتمان نعته {لم يكن الله ليغفر لهم} هذا فيمن علم أنَّه يموت على الكفر {ولا ليهديهم طريقاً} ولا ليرشدهم إلى دين الإِسلام',NULL,'303',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (663,'{إلاَّ طريق جهنم} يعني: طريق اليهوديَّة وهو الطَّريق الذي يقودهم إلى جهنَّم {خالدين فيها أبداً وكان ذلك} أَيْ: خلودهم {على الله يسيراً} لأنَّه لا يتعذَّر عليه شيءٌ',NULL,'303',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (664,'{يا أيها الناس} يعني: المشركين {قد جاءكم الرسول بالحق} بالهدى والصِّدق {من ربكم فآمنوا خيراً لكم} أَيْ: ايتوا خيراً لكم من الكفر بالإِيمان به {وإن تكفروا} تكذبوا محمد وتكفروا نعمة الله عليكم به {فإنَّ لله ما في السماوات والأرض} أَيْ: لا تضرُّون إلاَّ أنفسكم لأنَّ الله غنيٌّ عنكم {وكان الله عليماً} بما تصيرون إليه من إيمان أو كفر {حكيماً} في تكليفه مع علمه بما يكون منكم',NULL,'303',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (665,'{يا أهل الكتاب} يريد: النَّصارى {لا تغلوا} لا تتجاوزوا الحدَّ ولا تتشدَّدوا {فِي دِينِكُمْ وَلا تَقُولُوا عَلَى اللَّهِ إِلا الحق} فليس له ولدٌ ولا زوجة ولا شريك وقوله: {وكلمته ألقاها} يعني: أنَّه قال له: كن فيكون {وروحٌ منه} أَيْ: روحٌ مخلوقٌ من عنده {ولا تقولوا ثلاثة} أَيْ: لا تقولوا: آلهتنا ثلاثة يعني قولهم: اللَّهُ وصاحبته وابنه تعالى الله عن ذلك {انتهوا خيراً لكم} أَي: ائتوا بالانتهاء عن هذا خيراً لكم مما أنتم عليه',NULL,'303',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (666,'{لن يستنكف المسيح} لن يأنف الذي تزعمون أنَّه إِلهٌ {أن يكون عبداً لله وَلا الملائكة المقربون} من كرامة الله تعالى وهم أكثرُ من البشر',NULL,'304',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (667,'قال تعالى {فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَيَزِيدُهُمْ مِنْ فَضْلِهِ وَأَمَّا الَّذِينَ اسْتَنْكَفُوا وَاسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذَابًا أَلُيمًا وَلا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا ولا نصيرا}',NULL,'304',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (668,'{يا أيها الناس قد جاءكم برهانٌ من ربكم} يعني: النبيَّ عليه السَّلام {وأنزلنا إليكم نوراً مبيناً} وهو القرآن',NULL,'304',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (669,'{فأمَّا الذين آمنوا بالله واعتصموا به} أَي: امتنعوا بطاعته من زيغ الشَّيطان {فَسَيُدْخِلُهُمْ فِي رَحْمَةٍ مِنْهُ} يعني: الجنَّة {وفضل} يتفضَّل عليهم بما لم يخطر على قلوبهم {ويهديهم إليه صراطاً مستقيماً} ديناً مستقيماً',NULL,'304',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (670,'{يستفتونك قل الله يفتيكم في الكلالة} فيمن مات ولا ولد له ولا والد {إِنِ امْرُؤٌ هَلَكَ لَيْسَ لَهُ وَلَدٌ} أراد: ولا والد فاكتفى بذكر أحدهما لأنَّه الكلالة {وله أختٌ} يعني: من أبٍ وأمٍّ أو أبٍٍ لأنَّ ذكر ولد الأم قد مضى في أوَّل السُّورة {فَلَهَا نِصْفُ مَا تَرَكَ وهو يرثها} الأخ يرث الأخت جميع المال {إِنْ لَمْ يَكُنْ لَهَا وَلَدٌ فَإِنْ كَانَتَا} أَيْ: الأختان ( {فَلَهُمَا الثُّلُثَانِ مِمَّا تَرَكَ وَإِنْ كَانُوا إِخْوَةً رجالاً ونساءً} من أب وأمّ أو من أبٍ {فللذكر مثل حظ الأنثيين} ) وقوله: {يُبَيِّنُ اللَّهُ لَكُمْ أَنْ تَضِلُّوا} أي: أن لا تضلوا أو كراهة أن تضلوا ( {والله بكل شيء عليم} من قسمة المواريث)',NULL,'304',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (671,'{يا أيها الذين آمنوا أوفوا بالعقود} يعني: بالعهود المؤكَّدة التي عاهدتموها مع الله والنَّاس ثمَّ ابتدأ كلاماً آخر فقال: {أُحِلَّتْ لَكُمْ بهيمة الأنعام} قيل: هي الأنعام نفسها وهي الإِبلُ والبقر والغنم وقيل: بهيمة الأنعام: وحشِيُّها كالظِّباء وبقر الوحش وحمر الوحش {إلاَّ ما يتلى عليكم} (أي: ما يقرأ عليكم في القرآن) يعني: قوله {حرمت عليكم الميتة} الآية {غير محلي الصيد} يعني: إلاَّ أن تحلُّوا الصَّيد في حال الإِحرام فإنَّه لا يحلُّ لكم {إنَّ الله يحكم ما يريد} يحلُّ ما يشاء ويحرِّم ما يشاء',NULL,'306',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (672,'{يا أيها الذين آمنوا لا تحلوا شعائر الله} يعني: الهدايا المُعلَمة للذَّبح بمكة نزلت هذه الآية في الحُطَم بن ضبيعة أغار على سرح المدينة فذهب به إلى اليمامة فلمَّا خرج رسول الله صلى الله عليه وسلم عام القضية سمع تلبية حجَّاج اليمامة فقال رسول الله صلى الله عليه وسلم: هذا الحطم فدونكم وكان قد قلَّد ما نهب من سرح المدينة وأهداه إلى الكعبة فلمَّا توجَّهوا في طلبه أنزل الله تعالى: {لا تحلوا شعائر الله} يريد: ما أُشعر لله أَيْ: أُعْلِمَ {ولا الشهر الحرام} بالقتال فيه {ولا الهدي} وهي كلُّ ما أُهدي إلى بيت الله من ناقةٍ وبقرةٍ وشاةٍ {ولا القلائد} يعني: الهدايا المقلَّدة من لحاء شجر الحرم {ولا آمِّين البيت الحرام} قاصديه من المشركين
INSERT INTO [b2547] VALUES (673,'{حرِّمت عليكم الميتة} سبق تفسير هذه الاية في سورة البقرة إلى قوله: {والمنخنقة} وهي التي تختنق فتموت بأس وجهٍ كان {والموقوذة} المقتولة ضرباً {والمتردية} التي تقع من أعلى إلى أسفل فتموت {والنطيحة} التي قُتلت نطحا {وما أكل} منه {السبع} فالباقي منه حرامٌ ثمَّ استثنى ما يُدرك ذكاته من جميع هذه المحرَّمات فقال: {إلا ما ذكيتم} أَيْ: إلاَّ ما ذبحتم {وما ذبح على النصب} أَيْ: على اسم الأصنام فهو حرام {وأن تستقسموا بالأزلام} تطلبوا على ما قُسم لكم من الخير والشَّرِّ من الأزلام: القداح التي كان أهل الجاهليَّة يُجيلونها إذا أرادوا أمراً {ذلكم} أَيْ: الاستقسامُ من الأزلام {فسق} خروجٌ عن الحلال إلى الحرام {اليوم} يعني: يوم عرفة عام حجّ رسول الله صلى الله عليه وسلم بعد الفتح {يئس الذين كفروا} أن ترتدُّوا راجعين إلى دينهم {فلا تخشوهم} في مظاهرة محمد واتِّباع دينه {واخشون} في عبادة الأوثان {اليوم} يعني: يوم عرفة {أكملتُ لكم دينكم} أحكام دينكم فلم ينزل بعد هذه الآية حلالٌ ولا حرامٌ {وأتممت عليكم نعمتي} يعني: بدخول مكَّة آمنين كما وعدتكم {فمن اضطر} إلى ما حُرِّم ممَّا ذُكر في هذه الآية {في مخمصة} مجاعةٍ {غير متجانفٍ لإِثم} غير متعرِّضٍ لمعصيةٍ وهو أن يأكل فوق الشيع أو يكون عاصياً بسفره {فإنَّ الله غفورٌ} له ما أكل ممَّا حرَّم عليه {رحيم} بأوليائه حيث رخَّص لهم',NULL,'308',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (674,'{يسألونك ماذا أحلَّ لهم} سأل عديُّ بن حاتم رسول الله صلى الله عليه وسلم فقال: إنَّا نصيد بالكلاب والبُزاة وقد حرَّم الله الميتة فماذا يحلُّ لنا منها؟ فنزلت هذه الآية {قل أحلَّ لكم الطيبات} يعني: ما تستطيبه العرب وهذا هو الأصل في التَّحليل فكل حيوان استطابه العرب كالضِّباب واليرابيع والأرانب فهو حلال وما استخبثه العرب فهو حرام {وما علَّمتم} يعني: وصيد ما علَّمتم {من الجوارح} وهي الكواسب من الطَّير والكلاب والسِّباع {مكلِّبين} مُعلِّمين إيَّاها الصَّيد {تعلمونهن مما علمكم الله} تؤدبوهنَّ لطلب الصَّيد {فكلوا ممَّا أمسكن عليكم} هذه الجوارح وإنْ قتلن إذا لم يأكلن منه فإذا أكلن فالظَّاهر أنَّه حرام {واذكروا اسم الله عليه} عند إرسال الجوارح',NULL,'309',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (675,'{اليوم أحلَّ لكم الطيبات} التي سألتم عنها {وطعام الذين أوتوا الكتاب} وهو اسمٌ لجميع ما يؤكل {حلٌّ لكم وطعامكم حل لهم} أَيْ: حلٌّ لكم أن تطعموهم {والمحصنات} العفائف {من المؤمنات والمحصنات} الحرائر {من الذين أوتوا الكتاب} من أهل الكتاب {إذا آتيتموهنَّ أجورهنَّ} يعني: مهورهنَّ {محصنين} مُتزوِّجين {غير مسافحين} معالنين بالزِّنا {ولا متخذي أخدان} مُسرّين بالزِّنا بهنَّ {ومَنْ يكفر بالإِيمان} بالله الذي يجب الإِيمان به {فقد حبط عمله} إذا مات على ذلك {وهو في الآخرة من الخاسرين} ممَّنْ خسر الثَّواب',NULL,'309',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (676,'{يا أيها الذين آمنوا إذا قمتم إلى الصلاة} أَيْ: إذا أردتم القيام إليها {فاغسلوا وجوهكم وأيديكم إلى المرافق} يعني: مع المرفقين {وامسحوا برؤوسكم وأرجلكم إلى الكعبين} وهما النَّاشزان من جانبي القدم {وإن كنتم جنباً فاطَّهَروا} فاغتسلوا {وإن كنتم مرضى} مفسَّرٌ في سورة النساء إلى قوله: {مَا يُرِيدُ الله ليجعل عليكم من حرج} من ضيقٍ في الدِّين ولكنْ جعله واسعاً بالرُّخصة في التَّيمُّم {ولكن يريد ليطهركم} من الأحداث والجنابات والذُّنوب لأنَّ الوضوء يكفِّر الذُّنوب {وليتم نعمته عليكم} ببيان الشَّرائع و {لعلكم تشكرون} نعمتي فتطيعوا أمري',NULL,'310',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (677,'{واذكروا نعمة الله عليكم} بلإسلام {وميثاقه الذي واثقكم به} يعني: حين بايعوا رسول الله صلى الله عليه وسلم على السَّمع والطَّاعة في كلِّ ما أمر ونهى وهو قوله: {إذ قلتم} حين قُلْتُمْ {سَمِعْنَا وَأَطَعْنَا وَاتَّقُوا اللَّهَ إِنَّ اللَّهَ عليم بذات الصدور} بخفيَّات القلوب',NULL,'310',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (678,'{يا أيها الذين آمنوا كونوا قوَّامين لله} تقومون لله بكلِّ حقٍّ يلزمكم القيام به {شُهَدَاءَ بِالْقِسْطِ} تشهدون بالعدل {وَلا يَجْرِمَنَّكُمْ شَنَآنُ قوم} لا يحملنَّكم بغض قوم على ترك العدل {اعدلوا} في الوليِّ والعدوِّ {هو} أَيْ: العدل {أقرب للتقوى} أَيْ: لاتِّقاء النار',NULL,'311',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (679,'قال تعالى {وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مغفرة وأجر عظيم}',NULL,'311',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (680,'قال تعالى {وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ الْجَحِيمِ}',NULL,'311',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (681,'{يا أيها الذين آمنوا اذكروا نعمة الله عليكم} الآية يعني: ما أنعم الله على نبيِّه حين أتى اليهودَ هو وجماعة من أصحابه يستعينون بهم في دية فتآمروا بينهم أن يطرحوا عليهم رحىً فأعلمهم الله بذلك على لسان جبرائيل حتى خرجوا ثمَّ أخبر عن نقض بني إسرائيل عهد الله كما نقضت هذه الطَّبقة العهد الذي كان بينهم وبين رسول الله حين هموا بالاغتيال به فقال:',NULL,'311',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (682,'{ولقد أخذ الله ميثاق بني إسرائيل} على أن يعلموا بما في التَّوراة {وبعثنا} وأقمنا بذلك {منهم اثني عشر نقيباً} كفيلاً وضميناً ضمنوا عن قومهم الوفاء بالعهد {وقال الله} لهم: {إني معكم} بالعون والنُّصرة {لَئِنْ أَقَمْتُمُ الصَّلاةَ وَآتَيْتُمُ الزَّكَاةَ وَآمَنْتُمْ بِرُسُلِي وعزرتموهم} أَيْ: وقَّرتموهم {وأقرضتم الله قرضاً حسناً} يريد: الصَّدقات للفقراء والمساكين {فمن كفر بعد ذلك} أَيْ: بعد هذا العهد والميثاق {فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ} أخطأ قصد الطَّريق',NULL,'311',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (683,'{فبما نقضهم} فبنقضهم {ميثاقهم} وهو أنَّهم كذَّبوا الرُّسل بعد موسى فقتلوا الأنبياء وضيَّعوا كتاب الله {لعنَّاهم} أخرجناهم من رحمتنا {وَجَعَلْنَا قُلُوبَهُمْ قَاسِيَةً} يابسة عن الإِيمان {يُحَرِّفُونَ الكلم} يغيِّرون كلام الله {عن مواضعه} من صفة محمد صلى الله عليه وسلم في كتابهم وآية الرَّجم {وَنَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ} وتركوا نصيباً ممَّا أمروا به في كتابهم من اتِّبَاع محمَّدٍ {ولا تزال} يا محمد {تطلع على خائنة} خيانة {منهم} مثل ما خانوك حين همُّوا بقتلك {إلاَّ قليلاً منهم} يعني: مَنْ أسلم {فاعفُ عنهم واصفح} منسوخٌ بآية السَّيف {إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ} المتجاوزين',NULL,'312',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (684,'{وَمِنَ الَّذِينَ قَالُوا إِنَّا نَصَارَى أَخَذْنَا مِيثَاقَهُمْ} كما أخذنا ميثاق اليهود {فنسوا حظا مما ذكروا به} فتركوا ما أُمروا به من الإيمان بمحمد صلى الله عليه وسلم {فأغرينا بينهم} فألقينا بين اليهود والنصارى {الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَى يَوْمِ الْقِيَامَةِ وَسَوْفَ يُنَبِّئُهُمُ الله بما كانوا يصنعون} وعيدٌ لهم ثمَّ دعاهم إلى الإِيمان بمحمَّدٍ عليه السلام فقال:',NULL,'312',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (685,'{يا أهل الكتاب} يعني: اليهود والنَّصارى {قد جاءكم رسولنا} محمَّد {يُبَيِّنُ لَكُمْ كَثِيرًا مِمَّا كُنْتُمْ تُخْفُونَ مِنَ الكتاب} تكتمون ممَّا في التَّوراة والإِنجيل كآية الرَّجم وصفة محمَّد عليه السَّلام {ويعفو عن كثير} يتجاوز عن كثير فلا يخبركم بكتمانه {قد جاءكم من الله نور} يعني: النبيَّ {وكتاب مبين} القرآن فيه بيانٌ لكلِّ ما تختلفون فيه',NULL,'313',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (686,'{يهدي به الله} يعني: بالكتاب المبين {مَنِ اتبع رضوانه} اتَّبع ما رضيه الله من تصديق محمَّد عليه السَّلام {سُبُل السلام} طرق السَّلامة التي مَنْ سلكها سلم في دينه {ويخرجهم من الظُّلمات} الكفر {إلى النور} الإِيمان {بإذنه} بتوفسقه وإرادته {ويهديهم إلى صراط مستقيم} وهو الإِسلام',NULL,'313',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (687,'{لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ المسيح ابن مريم} يعني: الذين اتَّخذوه إِلهاً {قُلْ فَمَنْ يَمْلِكُ مِنَ اللَّهِ شَيْئًا} فمَنْ يقدر أن يدفع من عذاب الله شيئاً {إن أراد أن يهلك المسيح} أَيْ: يُعذِّبه ولو كان إلهاً لقدر على دفع ذلك',NULL,'313',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (688,'{وَقَالَتِ الْيَهُودُ وَالنَّصَارَى نَحْنُ أَبْنَاءُ اللَّهِ وَأَحِبَّاؤُهُ} أَمَّا اليهود فإنّهم قالوا: إنَّ الله من حِنَّتِهِ وعطفه علينا كالأب الشفيق وأمَّا النصارى فإنهم تأولو قول عيسى: إذا صلَّيتم فقولا: يا أبانا الذي في السَّماء تقدَّس اسمه وأراد أنَّه في برِّه ورحمته بعباده الصالحين كالأب الرحيم وقيل: أرادوا نحن أبناء رسل الله وإنما قالوا هذا حين حذَّرهم النبيُّ صلى الله عليه وسلم عقوبة الله فقال الله: {قل فلمَ يعذِّبكم بذنوبكم} أَيْ: فلمَ عذَّب مَنْ قبلكم بذنوبهم كأصحاب السَّبت وغيرهم {بَلْ أَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَ} كسائر بني آدم {يغفر لمن يشاء} لَمنْ تاب من اليهودية {ويعذب من يشاء} مَنْ مات عليها وقوله:',NULL,'314',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (689,'{على فترة من الرسل} على انقطاع من الأنبياء {أن تقولوا} لئلا تقولوا {مَا جَاءَنَا مِنْ بَشِيرٍ وَلا نذير} وقوله:',NULL,'314',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (690,'{وجعلكم ملوكاً} أَيْ: جعل لكم الخدم والحشم وهم أوَّل مَن ملك الخدم والحشم من بني آدم {وَآتَاكُمْ مَا لَمْ يُؤْتِ أَحَدًا مِنَ الْعَالَمِينَ} من فلق البحر لكم وإغراق عدوِّكم والمنِّ والسَّلوى وغير ذلك',NULL,'314',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (691,'{يا قوم ادخلوا الأرض المقدَّسة} المطهَّرة يعني: الشَّام وذلك أنَّها طُهِّرت من الشِّرك وجُعلت مسكناً للأنبياء {التي كتب الله لكم} أمركم الله بدخولها {ولا ترتدوا على أدباركم} لا ترجعوا إلى دينكم الشِّركِ بالله',NULL,'314',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (692,'{قَالُوا يَا مُوسَى إِنَّ فِيهَا قَوْمًا جَبَّارِينَ} طوالاً ذوي قوَّة وكانوا من بقايا عادٍ يقال لهم العمالقة',NULL,'315',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (693,'{قال رجلان} وهما يوشع بن نون وكالب بن يوفنا {من الذين يخافون} اللَّهَ في مخالفة أمره {أَنْعَمَ اللَّهُ عَلَيْهِمَا} بالفضل واليقين: {ادخلوا عليهم الباب} الآية وإنَّما قالا ذلك تيقُّناً بنصر الله وإنجاز وعده لنبيِّه فخالفوا نبيَّهم وعصوا أمر الله وأتوا من القول بما فسقوا به وهو قوله:',NULL,'315',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (694,'{قَالُوا يَا مُوسَى إِنَّا لَنْ نَدْخُلَهَا أَبَدًا مَا دَامُوا فِيهَا فَاذْهَبْ أَنْتَ وَرَبُّكَ فَقَاتِلا إنا هاهنا قاعدون} فقال موسى عند ذلك:',NULL,'315',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (695,'{لا أملك إلاَّ نفسي وأخي} يقول: لم يُطعني منهم إلاَّ نفسي وأخي {فافرق بيننا وبين القوم الفاسقين} فاقض بيننا وبين القوم العاصين فحرَّم الله على الذين عصوا دخول القرية وحبسهم في التِّيه أربعين سنةً حتى ماتوا ولم يدخلها أحدٌ من هؤلاء وإنَّما دخلها أولادهم وهو قوله:',NULL,'315',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (696,'{فإنها محرَّمة عليهم} الآية وقوله: {يتيهون في الأرض} يتحيَّرون فلا يهتدون للخروج منها {فلا تأس على القوم الفاسقين} لا تحزن على عذابهم',NULL,'315',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (697,'{واتل عليهم} يعني: على قومك {نبأ} خبر {ابني آدم} هابيل وقابيل {إذ قرَّبا قرباناً} تقرَّب إلى الله هابيل بخيرِ كبشٍ في غنمه فنزلت من السَّماء نارٌ فاحتملته فهو الكبش الذي فُدي به إسماعيل وتقرَّب إلى الله قابيل بأردأ ما كان عنده من القمح وكان صاحب زرع فلم تحمل النَّار قربانه والقربان: اسمٌ لكلِّ ما يُتقرَّب به إلى الله فقال الذي لم يُتقبَّلْْ منه: {لأقتلنك} حسداً له فقال هابيل: {إنما يتقبل الله من المتقين} للمعاصي (لا من العاصين)',NULL,'316',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (698,'{لئن بَسَطتَ إليَّ يدك} لئن بدأتني بالقتل فما أنا بالذي أبدؤك بالقتل {إني أخاف الله} في قتلك',NULL,'316',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (699,'{إني أريد أن تبوء بإثمي وإثمك} أن تحمل إثم قتلي وإثم الذي كان منك قبل قتلي',NULL,'316',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (700,'{فَطَوَّعَتْ له نفسه قتل أخيه} سهَّلته وزيَّنت له ذلك {فقتله فأصبح من الخاسرين} خسر دنياه بإسخاط والديه وآخرته بسخط الله عليه فلمَّا قتله لم يدرِ ما يصنع به لأنَّه كان أوَّل ميِّت على وجه الأرض من بني آدم فحمله في جرابٍ على ظهره',NULL,'316',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (701,'{فَبَعَثَ اللَّهُ غُرَابًا يَبْحَثُ فِي الأَرْضِ} يثير التُّراب من الأرض على غرابٍ ميِّتٍ {لِيُرِيَهُ كَيْفَ يُوَارِي} يستر {سوآة} جيفة {أخيه} فلمَّا رأى ذلك قال: {يا ويلتى أَعَجَزْتُ أَنْ أَكُونَ مِثْلَ هَذَا الْغُرَابِ فَأُوَارِيَ سوآة أخي فأصبح من النادمين} على حمله والتَّطوف به',NULL,'316',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (702,'{من أجل ذلك} من سبب ذلك الذي فعل قابيل {كتبنا} فرضنا {على بني إسرائيل أَنَّهُ مَنْ قَتَلَ نفساً بغير نفسٍ} بغير قَوَدٍ {أو فسادٍ} شركٍ {في الأرض فكأنما قتل الناس جميعاً} يُقتل كما لو قتلهم جميعاً ويصلى النَّار كما يصلاها لو قتلهم {ومَن أحياها} حرَّمها وتورَّع عن قتلها {فَكَأَنَّمَا أَحْيَا النَّاسَ جَمِيعًا} لسلامتهم منه لأنَّه لا يستحلُّ دماءهم {ولقد جاءتهم} يعني: بني إسرائيل {رسلنا بالبينات} بأنَّ لهم صدق ما جاؤوهم به {ثُمَّ إِنَّ كَثِيرًا مِنْهُمْ بَعْدَ ذَلِكَ فِي الأرض لمسرفون} أَيْ: مجاوزون حدَّ الحقِّ',NULL,'317',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (703,'{إنما جزاء الذين يحاربون الله ورسوله} أَيْ: يعصونهما ولا يطيعونهما يعني: الخارجين على الإِمام وعلى الأمَّة بالسَّيف نزلت هذه الآية في قصة العُرَنيين وهي معروفةٌ تعليماً لرسول الله صلى الله عليه وسلم عقوبة مَن فعل مثل فعلهم وقوله: {ويسعون في الأرض فساداً} بالقتل وأخذ الأموال {أن يقتلوا أو يصلبوا أو تُقطع أيديهم وأرجلهم من خلاف أو ينفوا من الأرض} معنى أو ها هنا الإباحة فللإمام أن يفعل ما أراد من هذه الأشياء ومعنى النَّفي من الأرض الحبسُ في السجن لأن المجون بمنزلة المخرج من الدني {ذلك لهم خزي} هوانٌ وفضيحةٌ {فِي الدُّنْيَا وَلَهُمْ فِي الآخِرَةِ عَذَابٌ عَظِيمٌ} وهذا للكفَّار الذين نزلت فيهم الآية لأنَّ العُرنيين ارتدُّوا عن الدِّين والمسلم إذا عوقب في الدُّنيا بجنايته صارت مكفَّرةً عنه',NULL,'317',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (704,'{إِلا الَّذِينَ تَابُوا مِنْ قَبْلِ أَنْ تَقْدِرُوا عليهم} آمنوا من قبل أن تعاقبوهم فالله غفورٌ رحيمٌ لهم هذا في المشرك المحارب إذا آمن قبل القدرة عليه سقط عنه جميع الحدود فأمَّا المسلم المحارب إذا تاب واستأمن قبل القدرة عليه سقط عنه حدود الله ولا تسقط حقوق بني آدم',NULL,'318',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (705,'{يا أيها الذين آمنوا اتقوا} عقاب {الله} بالطَّاعة {وابتغوا إليه الوسيلة} تقرَّبوا إليه بطاعته {وجاهدوا} العدوَّ {فِي سَبِيلِهِ} في طاعته {لعلكم تفلحون} كي تسعدوا وتبقوا في الجنَّة',NULL,'318',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (706,'{إنَّ الذين كفروا} الآية ظاهرة',NULL,'318',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (707,'{يريدون} يتمنَّون بقلوبهم {أن يخرجوا من النار}',NULL,'318',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (708,'{والسارق والسارقةُ فاقطعوا أيديهما} يمينَ هذا ويمين هذه فجمع {جَزَاءً بما كسبا} أَيْ: بجزاء فعلهما {نكالاً} عقوبةً {من الله والله عزيز} في انتقامه {حكيم} فيما أوجب من القطع',NULL,'318',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (709,'{فمن تاب من بعد ظلمه} النَّاس {وأصلح} العمل بعد السَّرقة {فإنَّ الله يتوب عليه} يعود عليه بالرَّحمة',NULL,'319',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (710,'{ألم تعلم أن الله له ملك السماوات والأرض يعذب مَنْ يشاء} على الذَّنب الصَّغير {ويغفر لمن يشاء} الذَّنب العظيم',NULL,'319',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (711,'{يا أيها الرَّسُولُ لا يَحْزُنْكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ} إذ كنت موعودَ النَّصر عليهم وهم المنافقون وبان لهم ذلك بقوله: {مِنَ الَّذِينَ قَالُوا آمَنَّا بِأَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قلوبهم ومن الذين هادوا سماعون} أَيْ: فريقٌ سمَّاعون {للكذب} يسمعون منك ليكذبوا عليك فيقولون: سمعنا منه كذا وكذا لما لمْ يسمعوا {سماعون لقوم آخرين لم يأتوك} أَيْ: هم عيونٌ لأولئك الغُيَّبِ ينقلون إليهم أخبارك {يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوَاضِعِهِ} من بعد أن وضعه الله مواضعه يعني: آية الرَّجم {يقولون إن أوتيتم هذا فخذوه} يعني: يهود خيبر بالجلد وهم الذين ذكروا في قوله: {لقوم آخرين لم يأتوك} وذلك أنَّهم بعثوا إلى قريظة ليستفتوا محمداً صلى الله عليه وسلم في الزانيين المحصنيين وقالوا لهم: إنْ أفتى بالجلد فاقبلوا وإن أفتى بالرَّجم فلا تقبلوا فذلك قوله: {إِنْ أُوتِيتُمْ هَذَا} يعني: الجلد {فخذوه} فاقبلوه {وإن لم تؤتوه فاحذروا} أن تعملوا به {ومن يرد الله فتنته} ضلالته وكفره {فَلَنْ تَمْلِكَ لَهُ مِنَ اللَّهِ شَيْئًا} لن تدفع عنه عذاب الله {أولئك الذين} أَيْ: مَنْ أراد الله فتنته فهم الَّذِينَ {لَمْ يُرِدِ اللَّهُ أَنْ يُطَهِّرَ قُلُوبَهُمْ} أن يُخلِّص نيَّاتهم {لهم في الدنيا خزيٌ} بهتك ستورهم {ولهم في الآخرة عذاب عظيم} وهو النَّار',NULL,'319',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (712,'{سماعون للكذب أكالون للسحت} وهو الرِّشوة في الحكم يعني: حكَّام اليهود يسمعون الكذب ممَّنْ يأتيهم مُبطلاً ويأخذون الرِّشوة منه فيأكلونها {فإن جاؤوك فاحكم بينهم أو أعرض عنهم} خيَّر الله نبيَّه في الحكم بين أهل الكتاب إذا تحاكموا إليه ثمَّ نسخ ذلك بقوله: {وأن احكم بينهم} الآية',NULL,'320',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (713,'{وكيف يحكمونك} عجَّب الله نبيه عليه السلام من تحكيم اليهود إيَّاه بعد علمهم بما في التَّوراة من حكم الزَّاني وحدِّه وقوله: {فيها حكم الله} يعني: الرَّجم {ثمَّ يتولون من بعد ذلك} التَّحكيم فلا يقبلون حكمك بالرَّجم {وما أولئك} الذين يُعرِضون عن الرَّجم {بالمؤمنين}',NULL,'320',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (714,'{إِنَّا أَنْزَلْنَا التَّوْرَاةَ فِيهَا هُدًى} بيان الحكم الذي جاؤوك يستفتوك فيه {ونور} بيانٌ إنَّ أمرك حَقٌّ {يَحْكُمُ بِهَا النَّبِيُّونَ} من لدن موسى إلى عيسى وهم {الذين أسلموا} أَي: انقادوا لحكم التَّوراة {للذين هادوا} تابوا من الكفر وهم بنو إسرائيل إلى زمن عيسى {والربانيون} العلماء {والأحبار} الفقهاء {بما استحفظوا} استرعوا أَيْ: بما كُلِّفُوا حفظه من كتاب الله وقيل: العمل بما فيه وذلك حفظه {من كتاب الله وَكَانُوا عَلَيْهِ شُهَدَاءَ} أنَّه من عند الله ثمَّ خاطب اليهود فقال: {فَلا تَخْشَوُا النَّاسَ} في إظهار صفة محمد صلى الله عليه وسلم والرَّجم {واخشون} في كتمان ذلك {ولا تشتروا بآياتي} بأحكامي وفرائضي {ثمناً قليلاً} يريد: متاع الدُّنيا {وَمَنْ لَمْ يَحْكُمْ بِمَا أَنْزَلَ اللَّهُ فَأُولَئِكَ هم الكافرون} نزلت في مَنْ غيرَّ حكم الله من اليهود وليس في أهل الإسلام منها ومن اللتين بعدها شيءٌ',NULL,'320',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (715,'{وكتبنا عليهم فيها} وفرضنا عليهم في التَّوراة {أنَّ النفس} تُقتل {بالنفس والعين بالعين} الآية كلُّ شخصين جرى القصاص بينهما في النَّفس جرى القصاص بينهما في جميع الأعضاء والأطراف إذا تماثلا في السَّلامة وقوله: {والجروح قصاص} في كلِّ ما يمكن أن يُقتصَّ فيه مثل الشَّفتين والذَّكَر والأُنثيين والأليتين والقدمين واليدين وهذا تعميمٌ بعد التفصيل بقوله: {والعين بالعين والأنف بالأنف} {فمن تصدَّق به فهو كفارة له} مَنْ عفا وترك القصاص فهو مغفرةٌ له عند الله وثواب عظيم',NULL,'321',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (716,'{وقفينا على آثارهم بعيسى} أَيْ: جعلناه يقفو آثار النَّبيِّين يعني: بعثناه بعدهم على آثارهم {مصدقاً لما بين يديه من التوراة} يُصدِّق أحكامها ويدعو إليها {وَآتَيْنَاهُ الإِنْجِيلَ فِيهِ هُدًى وَنُورٌ وَمُصَدِّقًا لِمَا بين يديه من التوراة وهدىً وموعظة} معناه: وهادياً وواعظاً',NULL,'321',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (717,'{وليحكم أهل الإِنجيل} أَيْ: وقلنا لهم: ليحكموا بهذا الكتاب في ذلك الوقت',NULL,'322',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (718,'{وَأَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِمَا بَيْنَ يديه من الكتاب ومهيمناً عليه} أَيْ: شاهداً وأميناً وحفيظاً ورقيباً على الكتب التي قبله فما أخبر أهل الكتاب بأمرٍ فإنْ كان في القرآن فصقوا وإلأَّ فكذِّبوا {فاحكم بينهم} بين اليهود {بما أنزل الله} بالقرآن والرَّجم {وَلا تَتَّبِعْ أَهْوَاءَهُمْ عَمَّا جَاءَكَ مِنَ الْحَقِّ} يقول: لا تتَّبعهم عمَّا عندك من الحق قتتركه وتتَّبعهم {لكلٍّ جعلنا منكم} من أُمَّة موسى وعيسى ومحمَّد صلَّى الله عليهم أجمعين {شرعة ومنهاجاً} سبيلاً وسنَّة فللتَّوراة شريعة وللإِنجيل شريعة وللقرآن شريعة {وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً} على أمرٍ واحدٍ ملَّة الإِسلام {ولكن ليبلوكم} ليختبركم {فِي مَا آتَاكُمْ} أعطاكم من الكتاب والسُّنن {فاستبقوا الخيرات} سارعوا إلى الأعمال الصَّالحة (الزَّاكية) {إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا} أنتم وأهل الكتاب {فينبئكم بما كنتم فيه تختلفون} من الدِّين والفرائض والسُّنن يعني: إنَّ الأمر سيؤول إلى ما يزول معه الشُّكوك بما يحصل من اليقين',NULL,'322',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (719,'{وَاحْذَرْهُمْ أَنْ يَفْتِنُوكَ عَنْ بَعْضِ مَا أَنْزَلَ الله إليك} أَيْ: يَسْتَزِلُّوكَ عن الحقِّ إلى أهوائهم نزلت حين قال رؤساء اليهود بعضهم لبعض: انطلقوا بنا إلى محمد لعلَّنا نفتنه فنرده عمَّا هو عليه فأتوه وقالوا له: قد علمت أنَّا إن اتَّبعناك اتَّبعك النَّاس ولنا خصومةٌ فاقض لنا على خصومنا إذا تحاكمنا إليك ونحن نؤمن بك فأبى ذلك رسول الله صلى الله عليه وسلم وأنزل الله هذه الآية: {فَإِنْ تَوَلَّوْا فَاعْلَمْ أَنَّمَا يُرِيدُ اللَّهُ أَنْ يصيبهم ببعض ذنوبهم} (أي: فإن أعرضوا عن الإِيمان والحكم بالقرآن فاعلم أنَّ ذلك من أجل أنَّ الله يريد أن يعجِّل لهم العقوبة في الدنيا ببعض ذنوبهم) ويجازيهم في الآخرة بجميعها ثمَّ كان تعذيبهم في الدُّنيا الجلاء والنَّفي {وَإِنَّ كَثِيرًا مِنَ النَّاسِ لَفَاسِقُونَ} يعني: اليهود',NULL,'323',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (720,'{أفحكم الجاهلية يبغون} أَيْ: أيطلب اليهود في الزَّانيين حكماً لم يأمر الله به وهم أهل كتاب كما فعل أهل الجاهليَّة؟ ! {وَمَنْ أَحْسَنُ مِنَ اللَّهِ حُكْمًا لِقَوْمٍ يُوقِنُونَ} أَي: مَنْ أيقن تبيَّن عدل الله في حكمه ثمَّ نهى المؤمنين عن موالاة اليهود وأوعد عليها بقوله:',NULL,'323',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (721,'{يا أيها الَّذِينَ آمَنُوا لا تَتَّخِذُوا الْيَهُودَ وَالنَّصَارَى أَوْلِيَاءَ} الآية',NULL,'323',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (722,'{فَتَرَى الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ} يعني: عبد الله بن أُبيٍّ وأصحابه {يسارعون فيهم} في مودَّة أهل الكتاب ومعاونتهم على المسلمين بإلقاء أخبارهم إليهم {يقولون نخشى أن تصيبنا دائرة} أَيْ: يدور الأمر عن حاله التي يكون عليها يعنون: الجدب فتنقطع عنا الميرة والقرض {فَعَسَى اللَّهُ أَنْ يَأْتِيَ بِالْفَتْحِ} يعني: لمحمدٍ على جميع مَنْ خالفه {أو أمرٍ من عنده} بقتل المنافقين وهتك سترهم {فيصبحوا على ما أسروا في أنفسهم} يعني: أهل النِّفاق على ما أضمروا من ولاية اليهود ودسِّ الأخبار إليهم {نادمين}',NULL,'323',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (723,'{ويقول الذين آمنوا} المؤمنون إذا هتك الله ستر المنافقين: {أهؤلاء} يعنون: المنافقين {الَّذِينَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ} حلفوا بأغلظ الأيمان {إنهم لمعكم} إنَّهم مؤمنون وأعوانكم على مَنْ خالفكم {حبطت أعمالهم} بطل كلُّ خيرٍ عملوه بكفرهم {فأصبحوا خاسرين} صاروا إلى النَّار وورث المؤمنون منازلهم من الجنَّة',NULL,'324',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (724,'{يا أيها الَّذِينَ آمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ دِينِهِ} علم الله تعالى أنَّ قوماً يرجعون عن الإِسلام بعد موت نبيِّهم صلى الله عليه وسلم فأخبرهم تعالى أنَّه س {يأتي اللَّهُ بقوم يحبهم ويحبونه} وهم أبو بكر رضي الله عنه وأصحابه الذين قاتلوا أهل الرِّدة {أذلة على المؤمنين} كالولد لوالده والعبد لسيِّده {أعزة على الكافرين} غلاظٍ عليهم كالسَّبع على فريسته {يُجَاهِدُونَ فِي سَبِيلِ اللَّهِ وَلا يَخَافُونَ لَوْمَةَ لائم} كالمنافقين الذين كانوا يرقبون الكافرين ويخافون لومهم في نصرة الدِّين {ذلك فضل الله} أَيْ: محبَّتهم لله عز وجل ولين جانبهم للمسلمين وشدَّتهم على الكفَّار بفضلٍ من الله عليهم',NULL,'324',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (725,'{إنما وليكم الله ورسوله} نزلت لمَّا هجر اليهود مَنْ أسلم منهم فقال عبد الله بن سلام: يا رسول الله إنَّ قومنا قد هجرونا وأقسموا ألا يجالسونا فنزلت هذه الآية فقال: رضينا بالله وبرسوله وبالمؤمنين أولياء وقوله: {وهم راكعون} يعني: صلاة التَّطوع',NULL,'325',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (726,'{ومن يَتَوَلَّ الله ورسوله} يتولَّى القيام بطاعته ونصرة رسوله والمؤمنين {فإنَّ حزب الله} جند الله وأنصار دينه {هم الغالبون} غلبوا اليهود فأجلوهم من ديارهم وبقي عبد الله بن سلام وأصحابه الذين تولَّوا اللَّهَ ورسوله',NULL,'325',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (727,'{يا أيها الذين آمنوا لا تتخذوا} الآية نزلت في رجالٍ كانوا يوادُّون منافقي اليهود ومعنى قوله: {اتَّخذوا دينكم هزواً ولعباً} إظهارهم ذلك باللِّسان واستبطانهم الكفر تلاعباً واستهزاءً {والكفار} يعني: مشركي العرب وكفَّار مكَّة {واتقوا الله} فلا تتَّخذوا منهم أولياء {إن كنتم مؤمنين} بوعده ووعيده',NULL,'325',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (728,'{وإذا ناديتم إلى الصلاة} دعوتم الناس بالأذان {اتخذوها هزواً ولعباً} تضاحكوا فيما بينهم وتغامزوا على طريق السُّخف والمجون تجهيلاً لأهلها {ذلك بأنهم قوم لا يعقلون} مالهم في إجابتها لو أجابوا إليها وما عليهم في استهزائهم بها',NULL,'325',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (729,'{قل يا أهل الكتاب هل تنقمون منا} الآية (أي: هل تنكرون وتكرهون) [أتي نفرٌ من اليهود رسول الله صلى الله عليه وسلم فسألوه عمَّن يُؤمن به من الرُّسل؟ فقال: أؤمنُ بِاللَّهِ وَمَا أُنْزِلَ عَلَيْنَا وَمَا أُنْزِلَ عَلَى إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالأَسْبَاطِ وَمَا أُوتِيَ مُوسَى وَعِيسَى وَالنَّبِيُّونَ مِنْ رَبِّهِمْ لا نُفَرِّقُ بين أحدٍ منهم وَنَحْنُ لَهُ مُسْلِمُونَ] فلمَّا ذكر عيسى جحدوا نبوَّته وقالوا: ما نعلم ديناً شرَّاً من دينكم فأنزل الله تعالى: {هل تنقمون} أَي: هل تكرهون وتنكرون منا إلا إيمانا وفسقكم أَيْ: إنَّما كرهتم إيماننا وأنتم تعلمون أننا على حقٍّ لأنَّكم قد فسقتم بأن أقمتم على دينكم لمحبَّتكم الرِّئاسة وكسبكم بها الأموال وتقدير قوله: {وأنًّ أكثركم فاسقون} ولأنَّ أكثركم والواو زائدةٌ والمعنى: لفسقكم نقمتم علينا الإِيمان وقوله:',NULL,'325',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (730,'{قل هل أنبئكم} أخبركم جوابٌ لقول اليهود: ما نعرف أهل دين شراً منكم فقال الله: {هل أنبئكم} أخبركم {بشرٍّ من} ذلكم المسلمون الذين طعنتم عليهم {مثوبة} جزاءً وثواباً {عند الله مَنْ لَعَنَهُ اللَّهُ} أَيْ: هو مَنْ لعنه الله: أبعده عن رحمته {وغضب عليه وجعل منهم القردة والخنازير} يعني: أصحاب السَّبت {وعبد الطاغوت} نسقٌ على {لعنه الله} وعبد الطاغوت أطاع الشَّيطان فيما سوَّله له {أولئك شر مكاناً} لأنَّ مكانهم سَقَر {وأضل عن سواء السبيل} قصد الطَّريق وهو دين الحنيفيَّة فلمَّا نزلت هذه الآية عيَّر المسلمون اليهود وقالوا: يا إخوان القردة والخنازير فسكتوا وافتضحوا',NULL,'326',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (731,'{وإذا جاؤوكم قالوا آمنا} يعني: منافقي اليهود {وَقَدْ دَخَلُوا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوا بِهِ} أَيْ: دخلوا وخرجوا كافرين والكفر معهم في كِلْتي حالهم',NULL,'326',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (732,'{وَتَرَى كَثِيرًا مِنْهُمْ يُسَارِعُونَ فِي الإِثْمِ وَالْعُدْوَانِ} يجترئون على الخطأ والظُّلم ويبادرون إليه {وأكلهم السُّحت} ما كانوا يأخذونه من الرَّشا على كتمان الحقِّ ثمَّ ذمَّ فعلهم بقوله: {لَبِئْسَ مَا كَانُوا يعملون}',NULL,'327',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (733,'{لولا} هلاَّ {ينهاهم} عن قبح فعلهم {الربانيون والأحبار} علماؤهم وفقهاؤهم {لبئس ما كانوا يصنعون} حين تركوا النَّكير عليهم',NULL,'327',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (734,'{وقالت اليهود يد الله مغلولة} مقبوضةٌ عن العطاء وإسباغ النِّعم علينا قالوا هذا حين كفَّ الله تعالى عنهم بكفرهم بمحمَّد عليه السَّلام ما كان يسلِّط عليهم من الخِصب والنِّعمة فقالوا - لعنهم الله على جهة الوصف بالبخل -: {يد الله مغلولة} وقوله: {غلت أيديهم} أَيْ: جعلوا بخلاء وأُلزموا البخل فهم أبخل قوم {ولعنوا بما قالوا} عُذِّبوا في الدُّنيا بالجِزية (والذلَّة والصَّغار والقحط والجلاء) وفي الآخرة بالنَّار {بل يداه مبسوطتان} قيل: معناه: الوصف بالمبالغة في الجود والإِنعام وقيل معناه: نعمه مبسوظة ودلَّت التَّثنية على الكثرة كقولهم: (لبيك وسعديك) وقيل: معمتاه: أَيْ: نعمة الدُّنيا ونعمة الآخرة {مبسوطتان ينفق كيف يشاء} يرزق كما يريد إن شاء قتَّر وإنْ شاء وسَّع {وَلَيَزِيدَنَّ كَثِيرًا مِنْهُمْ مَا أُنْزِلَ إِلَيْكَ مِنْ ربك طغياناً وكفراً} كلَّما أنزل عليك شيءٌ من القرآن كفروا به فيزيد كفرهم {وَأَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ} بين طوائف اليهود وجعلهم الله مختلفين متباغضين كما قال {تحسبهم جميعاً وقلوبهم شتَّى} {كلما أَوْقَدُوا ناراً للحرب أطفأها الله} كلَّما أرادوا محاربتك ردَّهم الله وألزمهم الخوف {ويسعون في الأرض فساداً} يعني: يجتهدون في دفع الإِسلام ومحو ذكر النبيِّ صلى الله عليه وسلم من كتبهم',NULL,'327',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (735,'{ولو أنَّ أهل الكتاب آمنوا} بمحمد صلى الله عليه وسلم {واتقوا} اليهوديَّة والنصرانيَّة {لكفَّرنا عنهم سيئاتهم} كلَّ ما صنعوا قبل أن تأتيهم',NULL,'328',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (736,'{ولو أنهم أقاموا التوراة والإِنجيل} عملوا بما فيهما من التَّصديق بك {وما أنزل إليهم} من كتب أنبيائهم {لأكلوا من فوقهم ومن تحت أرجلهم} لأنزلتُ عليهم القطر وأخرجتُ لهم من نبات الأرض كلَّما أرادوا {منهم أمة مقتصدة} مؤمنةٌ',NULL,'328',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (737,'{يا أيها الرَّسُولُ بَلِّغْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ} أَيْ: لا تراقبنَّ أحداً ولا تتركنَّ شيئاً ممَّا أُنزل إليك تخوُّفاً مِنْ أَنْ ينالك مكروهٌ بلِّغ الجميع مجاهراً به {وإن لم تفعل فما بلغت رسالته} إنْ كتمت آية ممَّا أنزلتُ إليك لم تبلِّغ رسالتي يعني: إنَّه إنْ ترك بلاغ البعض كان كمَنْ لم يُبلِّغ {والله يعصمك من الناس} أن ينالوك بسوء قال المفسرون: كان النبيُّ صلى الله عليه وسلم يشفق على نفسه غائلة اليهود والكفَّار وكان لا يُجاهرهم بعيب دينهم وسبِّ آلهتهم فأنزل الله تعالى: {يا أيها الرَّسُولُ بَلِّغْ مَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ} فقال: يارب كيف أصنع وأنا واحدٌ أخاف أن يجتمعوا عليَّ؟ فأنزل الله تعالى: {وَإِنْ لَمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَتَهُ وَاللَّهُ يَعْصِمُكَ مِنَ النَّاسِ إِنَّ اللَّهَ لا يَهْدِي القوم الكافرين} لا يرشد مَنْ كذَّبك',NULL,'328',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (738,'{قل يا أهل الكتاب لستم على شيء} من الدِّين {حتى تُقيموا} حتى تُعلموا بما في الكتابين من الإيمان بمحمد صلى الله عليه وسلم وبيان نعته وباقي الآية مضى تفسيره إلى قوله: {فَلا تَأْسَ عَلَى الْقَوْمِ الْكَافِرِينَ} يقول: لا تحزن على أهل الكتاب إنْ كذَّبوك',NULL,'329',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (739,'{إنَّ الذين آمنوا والذين هادوا} سبق تفسيره في سورة البقرة',NULL,'329',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (740,'قال تعالى {لَقَدْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ وَأَرْسَلْنَا إِلَيْهِمْ رُسُلا كُلَّمَا جَاءَهُمْ رَسُولٌ بِمَا لا تَهْوَى أنفسهم فريقا كذبوا وفريقا يقتلون}',NULL,'329',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (741,'{وحسبوا أن لا تكون فتنة} ظنُّوا وقدَّروا إلا تقع بهم عقوبة وعذابٌ في الإِصرار على الكفر بقتل الأنبياء وتكذيب الرُّسل {فعموا وصموا} عن الهدى فلم يعقلوه {ثمَّ تاب الله عليهم} بإرسال محمدا صلى الله عليه وسلم داعياً إلى الصِّراط المستقيم {ثمَّ عموا وصموا كثيرٌ منهم} بعد تبيُّن الحقِّ لهم بمحمَّد عليه السَّلام {والله بصيرٌ بما يعملون} من قتل الأنبياء وتكذيب الرسل',NULL,'329',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (742,'قال تعالى {لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ وَقَالَ الْمَسِيحُ يَا بَنِي إِسْرَائِيلَ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ إِنَّهُ مَنْ يُشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اللَّهُ عَلَيْهِ الْجَنَّةَ وَمَأْوَاهُ النَّارُ وَمَا لِلظَّالِمِينَ مِنْ أَنْصَارٍ}',NULL,'329',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (743,'{لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ ثَالِثُ ثلاثة} أَيْ: ثالث ثلاثةٍ من الآلهة والمعنى: أنَّهم قالوا: اللَّهُ واحدُ ثلاثةِ آلهة: هو والمسيح ومريم فزعموا أنَّ الإِلهيَّة مشتركة بين هؤلاء الثلاثة فكفروا بذلك',NULL,'330',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (744,'{مَا الْمَسِيحُ ابْنُ مَرْيَمَ إِلا رَسُولٌ قَدْ خلت من قبله الرسل} أَيْ: إنه رسول ليس بآلهة كما أنَّ مَنْ قبله كانوا رسلاً {وأمه صديقة} صدَّقت بكلمات ربِّها وكتبه {كانا يأكلان الطعام} يريد: هما لحمٌ ودمٌ يأكلان ويشربان ويبولان ويتغوَّطان وهذه ليست من أوصاف الإِلهيَّة {انظر كيف نبيِّن لهم الآيات} نفسِّر لهم أمر ربوبيتي {ثُمَّ انْظُرْ أَنَّى يؤفكون} يُصرفون عن الحقِّ الذي يؤدِّي إليه تدبُّر الآيات',NULL,'330',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (745,'قال تعالى {مَا الْمَسِيحُ ابْنُ مَرْيَمَ إِلا رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ وَأُمُّهُ صِدِّيقَةٌ كَانَا يَأْكُلانِ الطَّعَامَ انْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الآيَاتِ ثم انظر أنى يؤفكون}',NULL,'330',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (746,'{قل} للنَّصارى: {أَتَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يملك لكم ضراً ولا نفعاً} يعني: المسيح لأنَّه لا يملك ذلك إلاَّ الله عز وجل {والله هو السميع} لكفركم {العليم} بضميركم',NULL,'330',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (747,'{قل يا أهل الكتاب} يعني: اليهود والنَّصارى {لا تغلوا في دينكم} لا تخرجوا عن الحدِّ في عيسى وغُلوُّ اليهود فيه بتكذيبهم إيَّاه ونسبته إلى أنه لغير رشدة وغلوا النصارى فيه ادِّعاؤهم الإِلهيَّة له وقوله: {غير الحق} أَيْ: مخالفين للحق {وَلا تَتَّبِعُوا أَهْوَاءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قبل} يعني: رؤساهم الذين مضوا من الفريقين أَيْ: لا تتبعوا أسلافكم فيما ابتدعوه بأهوائهم {وضلوا عن سواء السبيل} عن قصد الطَّريق بإضلالهم الكثير',NULL,'330',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (748,'{لعن الذين كفروا من بني إسرائيل} يعني: أصحاب السَّبت وأصحاب المائدة {على لسان داود} لأنَّهم لمَّا اعتدوا قال داود عليه السَّلام: اللَّهم العنهم واجعلهم آيةً لخلقك فمسخوا قردة على لسان داود {وعيسى ابن مريم} عليه السَّلام لأنَّه لعن مَنْ لم يؤمن من أصحاب المائدة فقال: اللهم العنهم كما لعنتَ السَّبت فمسخوا خنازير',NULL,'331',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (749,'{كانوا لا يتناهون} لا ينتهون {عن منكر فعلوه}',NULL,'331',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (750,'{ترى كثيراً منهم} من اليهود {يتولون الذين كفروا} كفَّار مكة {لَبِئْسَ مَا قَدَّمَتْ لَهُمْ أَنْفُسُهُمْ أَنْ سَخِطَ الله عليهم} بئسما قدَّموا من العمل لمعادهم في الآخرة سُخطَ الله عليهم',NULL,'331',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (751,'قال تعالى {وَلَوْ كَانُوا يُؤْمِنُونَ بِاللَّهِ وَالنَّبِيِّ وَمَا أُنْزِلَ إِلَيْهِ مَا اتَّخَذُوهُمْ أَوْلِيَاءَ وَلَكِنَّ كَثِيرًا مِنْهُمْ فاسقون}',NULL,'331',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (752,'{لتجدنَّ} يا محمد {أشد الناس عداوة للذين آمنوا اليهود} وذلك أنَّهم ظاهروا المشركين على المؤمنين حسداً للنبيِّ عليه السَّلام {وَلَتَجِدَنَّ أَقْرَبَهُمْ مَوَدَّةً لِلَّذِينَ آمَنُوا الَّذِينَ قَالُوا إنا نصارى} يعني: النَّجاشي ووفده الذين قدموا من الحبشة على رسول الله صلى الله عليه وسلم وآمنوا به ولم يرد جميع النَّصارى {ذلك} يعني: قرب المودَّة {بأنَّ منهم قسيسين ورهباناً} أَيْ: علماء بوصاة عيسى بالإِيمان بمحمَّد عليه السَّلام {وأنهم لا يستكبرون} عن اتِّباع الحقِّ كما يستكبر اليهود وعبدة الأوثان',NULL,'331',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (753,'{وإذا سمعوا ما أنزل إلى الرسول} يعني: النجاشيَّ وأصحابه قرأ عليهم جعفر بن أبي طالب بالحبشة (كهعيص) فما زالوا يبكون وهو قوله: {ترى أعينهم تَفيضُ من الدمع ممَّا عَرَفُوا مِنَ الْحَقِّ} يريد: الذي نزل على محمَّد وهو الحقُّ {يقولون ربنا آمنا} وصدَّقنا {فاكتبنا مع الشاهدين} مع أمَّة محمَّد صلى الله عليه وسلم الذين يشهدون بالحق',NULL,'332',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (754,'{وما لنا لا نؤمن بالله} أَيْ: أيُّ شيءٍ لنا إذا تركنا الإِيمان بالله {وما جاءنا من الحق} أَيْ: القرآن {و} نحن {نطمع أن يدخلنا ربنا} الحنة مع أمَّة محمَّد عليه السَّلام يعنون: أنَّهم لا شيء لهم إذا لم يؤمنوا بالقرآن ولا يتحقق طمعهم في دخول الجنَّة',NULL,'332',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (755,'{فأثابهم الله بما قالوا} يعني: بما سألوا الله من قولهم: {فاكتبنا مع الشاهدين} وقولهم: {ونطمع أن يدخلنا ربنا} الآية {جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا وذلك} أي: الثَّواب {جزاء المحسنين} الموحِّدين ثمَّ ذكر الوعيد لمَنْ كفر من أهل الكتاب وغيرهم فقال:',NULL,'332',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (756,'{وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ الْجَحِيمِ}',NULL,'333',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (757,'{يا أيها الَّذِينَ آمَنُوا لا تُحَرِّمُوا طَيِّبَاتِ مَا أَحَلَّ الله لكم} هَمَّ قومٌ من أصحاب النبيِّ صلى الله عليه وسلم تعاهدوا أن يحرِّموا على أنفسهم المطاعم الطَّيِّبة وأن يصوموا النَّهار ويقوموا اللَّيل ويُخْصُوا أنفسهم فأنزل الله تعالى هذه الآية وسمَّى الخِصاء اعتداءً فلمَّا نزلت هذه الآية قالوا: يا رسول الله إنَّا كنَّا قد حلفنا على ذلك فنزلت:',NULL,'333',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (758,'قال تعالى {وَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلالا طَيِّبًا وَاتَّقُوا الله الذي أنتم به مؤمنون}',NULL,'333',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (759,'{لا يؤاخذكم الله باللغو في أيمانكم} وفسَّرْنا هذا في سورة البقرة {ولكن يؤاخذكم بما عقدتم الأيمان} وهو أن يقصد الأمر فيحلف بالله ويعقد عليه اليمين بالقلب متعمِّداً {فكفارته} إذا حنثتم {إِطْعَامُ عَشَرَةِ مَسَاكِينَ} لكلِّ مسكين مدٌّ وهو رطلٌ وثلث وهو قوله: {من أوسط ما تطعمون أهليكم} لأنَّ هذا القدر وسط في الشِّبع وقيل: من خير ما تطعمون أهليكم كالحنطة والتمر {أو كسوتهم} وهو أقلُّ ما يقع عليه اسم الكسوة من إزارٍ ورداءٍ وقميصٍ {أو تحرير رقبة} يعني: مؤمنة والمُكفِّر في اليمين مُخيَّر بين هذه الثَّلاث {فمن لم يجد} يعني: لم يفضل من قوته وقوت عياله يومه وليلته ما يطعم عشرة مساكين {ف} عليه {صيام ثلاثة أيام} {واحفظوا أيمانكم} فلا تحلفوا واحفظوها عن الحنث',NULL,'333',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (760,'{يا أيها الذين آمنوا إنما الخمر} يعني: الأشربة التي تخمَّر حتى تشتدَّ وتُسْكِر {والميسر} القمار بجميع أنواعه {والأنصاب} الأوثان {والأزلام} قداح الاستقسام التي ذُكرت في أوَّل السُّورة {رجسٌ} قذرٌ قبيحٌ {من عمل الشيطان} ممَّا يسِّوله الشِّيطان لبني آدم {فاجتنبوه} كونوا جانباً منه',NULL,'334',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (761,'{إِنَّمَا يُرِيدُ الشَّيْطَانُ أَنْ يُوقِعَ بَيْنَكُمُ الْعَدَاوَةَ والبغضاء في الخمر والميسر} وذلك لما يحصل بين أهلها من العداوة والمقابح والإِقدام على ما يمنع منه العقل {ويصدكم عن ذكر الله وعن الصلاة} لأنَّ مَن اشتغل بهما منعاه عن ذكر الله والصَّلاة {فهل أنتم منتهون} (استفهامٌ بمعنى الأمر) قالوا: انتهينا ثم أمر بالطاعة فقال:',NULL,'334',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (762,'{وأطيعوا الله وأطيعوا الرسول واحذروا} المحارم والمناهي {فإن توليتم} عن الطَّاعة {فاعلموا أنما على رسولنا البلاغ المبين} فليس عليه إلاَّ البلاغ فإن أطعتم وإلاَّ استحققتم العقاب فلمَّا نزل تحريم الخمر قالوا: يا رسول الله ما تقول في إخواننا الذين مضوا وهم يشربونها ويأكلون الميسر؟ فنزل:',NULL,'335',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (763,'{لَيْسَ عَلَى الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جُنَاحٌ فيما طعموا} من الخمر والميسر قبل التحريم {إذا ما اتقوا} المعاصي والشِّرك {ثمَّ اتقوا} داموا على تقواهم {ثم اتقوا} ظلم العباد مع ضمِّ الإِحسان إليه',NULL,'335',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (764,'{يا أيها الَّذِينَ آمَنُوا لَيَبْلُوَنَّكُمُ اللَّهُ بِشَيْءٍ مِنَ الصَّيْدِ} كان هذا عام الحديبية كانت الوحش والطيَّر تغشاهم في رحالهم كثيرة وهم مُحْرِمون ابتلاءً من الله تعالى {تناله أيديكم} يعني: الفراخ والصِّغار {ورماحكم} يعني: الكبار {ليعلم الله} ليرى الله {مَنْ يخافه بالغيب} أَيْ: مَنْ يخاف الله ولم يره {فمن اعتدى} ظلم بأخذ الصَّيد {بَعْدَ ذَلِكَ} بعد النَّهي {فَلَهُ عَذَابٌ أَلِيمٌ}',NULL,'335',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (765,'{يا أيها الَّذِينَ آمَنُوا لا تَقْتُلُوا الصَّيْدَ وَأَنْتُمْ حُرُمٌ} حرم الله الصَّيد على المُحْرِم فليس له أن يتعرَّض للصَّيد بوجهٍ من الوجوه ما دام مُحرماً {وَمَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّدًا فَجَزَاءٌ مِثْلُ مَا قَتَلَ مِنَ النَّعَمِ} أَيْ: فعليه جزاءٌ مماثل للمقتول من النَّعم في الخِلقة ففي النَّعامة بدنة وفي حمار الوحش بقرة وفي الضَّبع كبش على هذا التَّقدير {يحكم به ذوا عدل} يحكم في الصيد رجلان صالحان {منكم} من أهل ملَّتكم فينظران إلى أشبه الأشياء به من النَّعم فيحكمان به {هدياً بالغ الكعبة} أَيْ: مثل ذلك {صياماً} والمُحرِم إذا قتل صيداً كان مخيَّراً إن شاء جزاه بمثله من النَّعم وإن شاء قوَّم المثل دراهم ثمَّ الدراهم طعاماً ثمَّ يتصَّدق به وإن شاء صام عن كلِّ مدٍّ يوماً {ليذوق وبال أمره} جزاء ما صنع {عفا الله عما سلف} قبل التَّحريم {ومن عاد فينتقم الله منه} مَنْ عاد إلى قتل الصَّيد مُحرماً حُكم عليه ثانياً وهو بصدد الوعيد {والله عزيز} منيع {ذو انتقام} من أهل معصيته',NULL,'335',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (766,'{أحلَّ لكم صيد البحر} ما أُصيب من داخله وهذا الإِحلالُ عامٌّ لكلِّ أحد مُحرِماً كان أو مُحِلاًّ {وطعامه} وهو ما نضب عنه الماء ولم يُصَد {متاعاً لكم وللسيارة} منفعة للمقيم والمسافر يبيعون ويزودون منه ثمَّ أعاد تحريم الصَّيد في حال الإِحرام فقال: {وَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا واتقوا الله الذي إليه تحشرون} خافوا الله الذي إليه تبعثون',NULL,'336',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (767,'{جعل الله الكعبة البيت الحرام} يعني: البيت الذي حرَّم أن يصاد عنده ويختلى للحجِّ وقضاء النُّسك {والشهر الحرام} يعني: الأشهر الحرم فذكر بلفظ الجنس {والهدي والقلائد} ذكرناه في أولَّ السورة وهذه الجملة ذُكرت بعد ذكر البيت لأنَّها من أسباب الحج فذكرت معه {ذلك} أَيْ: ذلك الذي أنبأتكم به في هذه السُّورة من أخبار الأنبياء وأحوال المنافقين واليهود وغير ذَلِكَ {لِتَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي السماوات} الآية أَيْ: يدلُّكم ذلك على أن لا يخفى عليه شيء',NULL,'336',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (768,'قال تعالى {اعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ وَأَنَّ اللَّهَ غفور رحيم}',NULL,'336',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (769,'قال تعالى {مَا عَلَى الرَّسُولِ إِلا الْبَلاغُ وَاللَّهُ يَعْلَمُ ما تبدون وما تكتمون}',NULL,'336',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (770,'{قل لا يستوي الخبيث والطيب} أَيْ: الحرام والحلال {ولو أعجبك كثرة الخبيث} وذلك عن أهل الدُّنيا يعجبهم كثرة المال وزينة الدُّنيا',NULL,'337',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (771,'{يا أيها الَّذِينَ آمَنُوا لا تَسْأَلُوا عَنْ أَشْيَاءَ إِنْ تُبدَ لكم تسؤكم} نزلت حين سُئل النبيُّ حتى أحفوه بالمسألة فقام مُغضباً خطيباً وقال: لا تسألوني في مقامي هذا عن شيء إلاَّ أخبرتكموه فقام رجلٌ من بني سهم يطعن في نسيه فقال: مَنْ أبي؟ فقال: أبوك حذافة وقام آخر فقال: أين أنا؟ فقال: في النَّار فأنزل الله تعالى هذه الآية ونهاهم أن يسألوه عمَّا يُحزنهم جوابه وإبداؤه كسؤالِ مَنْ سأل عن موضعه فقال: في النَّار {وَإِنْ تَسْأَلُوا عَنْهَا} أيْ عن أشياء {حِينَ ينزل القرآن} فيها {تُبدَ لكم} يعني: ما ينزل فيه القرآن من فرضٍ أو نهيٍ أو حكمٍ ومسَّت الحاجة إلى بيانه فإذا سالتم عنها حينئذٍ تبدى لكم {عفا الله عنها} أَيْ: عن مسألتكم ممَّا كرهه النبيُّ صلى الله عليه وسلم ولا حاجة بكم إلى بيانه نهاهم أن يعودوا إلى مثل ذلك وأخبر أنَّه عفا عمَّا فعلوا {والله غفورٌ حليم} لا يعجل بالعقوبة ثمَّ أخبرهم عن حال مَنْ تكلَّف سؤال ما لم يُكلَّفوا فقال:',NULL,'337',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (772,'{قد سألها} أَي: الآيات {قومٌ من قبلكم} الآية يعني: قوم عيسى سألوا المائدة ثمَّ كفروا بها وقوم صالح سألوا النَّاقة ثمَّ عقروها',NULL,'338',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (773,'{مَا جَعَلَ اللَّهُ مِنْ بَحِيرَةٍ} أَيْ: ما أوجبها ولا أمر بها والبحيرة: النَّاقة إذا نُتجت خمسة أبطن شقُّوا أُذنها وامتنعوا من ركوبها وذبحها {ولا سائبة} هو ما كانوا يُسيبِّونه لآلهتهم في نذرٍ يلزمهم إنْ شفي مريض أو قضيت لهم حاجة {ولا وصيلة} كانت الشَّاة إذا ولدت أنثى فهي لهم وإن ولدت ذكراً جعلوه لآلهتهم وإن ولدت ذكراً وأنثى قالوا: وصلت أخاها فلم يذبحوا الذَّكر لآلهتهم {ولا حامٍ} إذا نُتجت من صلب الفحل عشرة أبطن قالوا: قد حمى ظهره فلم يُركب ولم يُنتفع وسيِّب لأصنامهم فلا يُحمل عليه {وَلَكِنَّ الَّذِينَ كَفَرُوا يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ} يتقوَّلون على الله الأباطيل في تحريم هذه الأنعام وهم جعلوها مُحرَّمة لا الله {وأكثرهم} يعني: أتباع رؤسائهم الذين سنُّوا لهم تحريم هذه الأنعام {لا يعقلون} أنَّ ذلك كذبٌ وافتراءٌ على الله من الرُّؤساء',NULL,'338',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (774,'{وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَى مَا أَنْزَلَ اللَّهُ} في القرآن من تحليل ما حرَّمتم {قالوا حسبنا ما وجدنا عليه آباءنا} من الدين {أو لو كَانَ آبَاؤُهُمْ لا يَعْلَمُونَ شَيْئًا وَلا يَهْتَدُونَ} مفسَّرة في سورة البقرة',NULL,'338',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (775,'{يا أيها الذين آمنوا عليكم أنفسكم} احفظوها من ملابسة المعاصي والإِصرار على الذّنوب {لا يضرُّكم مَنْ ضلَّ} من أهل الكتاب {إذا اهتديتم} أنتم {إلى الله مرجعكم جميعاً} مصيركم ومصير مَنْ خالفكم {فينبئكم بما كنتم تعملون} يُجازيكم بأعمالكم',NULL,'338',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (776,'{يا أيها الذين آمنوا شهادة بينكم} نزلت هذه الآية في قصّة تميمٍ وعديٍّ وبُديلٍ خرجوا تجاراً إلى الشَّام فمرض بُديل ودفع إليهما متاعه وأَوصى إليهما أن يدفعاه إلى أهله إذا رجعا فأخذا من متاعه إناءً من فِضَّة وردَّا الباقي إلى أهله فعلموا بخيانتهما ورفعوها إلى رسول الله صلى الله عليه وسلم فأنزل الله تعالى هذه الآيات ومعنى الآية: ليشهدكم {إذا حضر أحدكم الموت} وأردتم الوصية {اثنان ذوا عدل منكم} من أهل ملَّتكم تشهدونهما على الوصية {أو آخران من غيركم} من غير دينكم إذا {ضربتم} سافرتم {في الأرض فأصابتكم مصيبة الموت} علم الله أنَّ من النَّاس مَنْ يسافر فيصحبُهُ في سفره أهل الكتاب دون المسلمين ويحضره الموت فلا يجد مَنْ يُشهده على وصيته من المسلمين فقال: {أو آخران من غيركم} فالذِّميان في السَّفر (خاصَّة) إذا لم يوجد غيرهما (تُقبل شهادتهما في ذلك) وقوله: {تَحْبِسُونَهُمَا مِنْ بَعْدِ الصَّلاةِ فَيُقْسِمَانِ بِاللَّهِ إِنِ ارتبتُمْ لا نشتري به ثمنا} أي: ارتبتم في شهادتهما وشككتم وخشيتم أن يكونا قد خانا حبستموهما على اليمين بعد صلاة العصر فيحلفان بالله ويقولان في يمينهما: لا نبيع الله بعرضٍ من الدُّنيا ولا نُحابي أحداً في شهادتنا {ولو كان ذا قربى} ولو كان المشهود له ذا قربى {ولا نكتم شهادة الله} أَيْ: الشَّهادة التي أمر الله بإقامتها {إنا إذاً لمن الآثمين} إنْ كتمناها ولمًّا رفعوهما إلى رسول الله صلى الله عليه وسلم ونزلت هذه الآية أمر رسول الله صلى الله عليه وسلم أن يستحلفوهما وذلك أنَّهما كانا نصرانيين وبُديل كان مسلماً فحلفا أنَّهما ما قبضا غير ما دفعا إلى الورثة ولا كتما شيئاً وخلَّى سبيلهما ثمَّ اطُّلِع على الإِناء في أيديهما فقالا: اشتريناه منه فارتفعوا إلى النبي صلى الله عليه وسلم فنزل قوله:',NULL,'339',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (777,'{فإن عثر} أَيْ: ظهر واطلع {على أنهما استحقا إثماً} أَيْ: استوجباه بالخيانة والحنث في اليمين {فآخران يقومان مقامهما} من الورثة وهم الذين {استحق عليهم} أَيْ: استحق عليهم الوصية أو الإِيصاء وذلك أنَّ الوصية تستحق على الورثة {الأوليان} بالميت أَيْ: الأقربان إليه والمعنى: قام في اليمين مقامهما رجلان من قرابة الميت فيحلفان بالله: لقد ظهرنا على خيانة الذِّميِّيْن وكذبهما وتبديلهما وهو قوله: {فيقسمان بالله لشهادتنا أحقٌّ من شهادتهما} أَيْ: يميننا أحقُّ من يمينهما {وما اعتدينا} فيما قلنا فلمَّا نزلت هذه الآية قام اثنان من ورثة الميِّت فحلفا بالله أنَّهما خانا وكذبا فدفع الإناء إلى أولياء الميت',NULL,'340',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (778,'{ذلك} أَيْ: ما حَكم به في هذه القصَّة وبيَّنه من ردِّ اليمين {أدنى} إلى الإِتيان بالشَّهادة على ما كانت {أو يخافوا} أَيْ: أقرب إلى أن يخافوا {أَنْ تُرَدَّ أَيْمَانٌ} على أولياء الميِّت بعد أيمان الأوصياء فيحلفوا على خيانتهم وكذبهم فيفتضحوا {واتقوا الله} أن تحلفوا أيماناً كاذبةً أو تخونوا أمانةً {واسمعوا} الموعظة {وَاللَّهُ لا يَهْدِي القوم الفاسقين} لايرشد مَنْ كان على معصيته',NULL,'340',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (779,'{يوم يجمع الله الرسل} أَيْ: اذكروا ذلك اليوم {فيقول} لهم: {ماذا أُجِبْتُمْ} ما أجابكم قومكم في التَّوحيد؟ {قالوا لا علم لنا} من هول ذلك اليوم يذهلون عن الجواب ثمَّ يجيبون بعدما تثوب إليهم عقولهم فيشهدون لمن صدَّقهم وعلى مَنْ كذَّبهم',NULL,'341',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (780,'{إِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ} مضى تفسير الآية إلى قوله: {وَإِذْ كَفَفْتُ بَنِي إِسْرَائِيلَ عَنْكَ} أَيْ: عن قتلك',NULL,'341',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (781,'{وإذ أوحيت إلى الحواريين} أَيْ: ألهمتهم',NULL,'341',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (782,'{إِذْ قَالَ الْحَوَارِيُّونَ يَا عِيسَى ابْنَ مَرْيَمَ هل يستطيع ربك} لم يشكُّوا في قدرته ولكنْ معناه: هل يقبل ربُّك دعاءَك وهل يسهل لك إنزال مائدة علينا من السَّماء عَلَماً لك ودلالةً على صدقك؟ فقال عيسى: {اتقوا الله} أن تسألوه شيئاً لم تسأله الأمم من قبلكم',NULL,'341',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (783,'{قَالُوا: نُرِيدُ أَنْ نَأْكُلَ مِنْهَا} أَيْ: نريد السُّؤال من أجل هذا {وتطمئن قلوبنا} نزداد يقيناً بصدقك {ونكون عليها من الشاهدين} لله بالتَّوحيد ولك بالنُّبوة وقوله',NULL,'342',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (784,'{تَكُونُ لَنَا عِيدًا لأَوَّلِنَا وَآخِرِنَا} أَيْ: نتّخذ اليوم الذي تنزل فيه عيداً نُعظِّمه نحن ومَنْ يأتي بعدنا {وآيةً منك} دلالةً على توحيدك وصدق نبيِّك {وارزقنا} عليها طعاماً نأكله وقوله:',NULL,'342',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (785,'{فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ} أَيْ: بعد إنزال المائدة {فَإِنِّي أُعَذِّبُهُ عَذَابًا لا أُعَذِّبُهُ أَحَدًا من العالمين} أراد: جنساً من العذاب لا يُعذَّب به غيرهم من علمي زمانهم',NULL,'342',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (786,'{وَإِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ} واذكر يا مُحمَّدُ حين يقول الله تعالى يوم القيامة: {أَأَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَأُمِّيَ إِلَهَيْنِ مِنْ دون الله} هذا استفهامٌ معناه التُّوبيخ لمن ادَّعى ذلك على المسيح ليكِّذبهم المسيح فتقوم عليهم الحجَّة {قال سبحانك} أَيْ: براءتك من السُّوء {تعلم ما في نفسي} أَيْ: ما في سرِّي وما أضمره {وَلا أَعْلَمُ مَا فِي نَفْسِكَ} أَيْ: ما تخفيه أنت وما عندك علمه ولم تُطلعنا عليه وقوله:',NULL,'342',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (787,'{وكنتُ عليهم شهيداً} أَيْ: كنت أشهد على ما يفعلون ما كنتُ مقيماً فيهم {فلما توفيتني} يعني: رفعتني إلى السَّماء {كنت أنت الرقيب} الحفيظ {عليهم وأنت على كلِّ شيء شهيد} أَيْ: شهدت مقالتي فيهم وبعد ما رفعتني شهدتَ ما يقولون من بعدي',NULL,'343',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (788,'{إن تعذبهم} أَيْ: من كفر بك {فإنهم عبادك} وأنت العادل فيهم {وإن تغفر لهم} أَيْ: مَنْ تاب منهم وآمن فأنت عزيز لا يمتنع عليك ما تريد حكيم في ذلك',NULL,'343',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (789,'{قال الله هذا يوم} يعني: يوم القيامة {ينفع الصادقين} في الدُّنيا {صدقهم} لأنَّه يوم الإِثابة والجزاء {رضي الله عنهم} بطاعته {ورضوا عنه} بثوابه {ذلك الفوز العظيم} لأنهم فازوا بالجنَّة',NULL,'343',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (790,'{لله ملك السماوات والأرض} عظَّم نفسه عمَّا قالت النصارى: إنَّ معه إلهاً',NULL,'343',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (791,'{الحمد لله الذي خلق السماوات والأرض وجعل الظلمات والنور} وخلق اللَّيل والنَّهار {ثمًّ الذين كفروا} بعد قيام الدَّليل على وحدانيَّته بما ذكر من خلقه {بربهم يعدلون} الحجارةَ والأصنام فيعبدونها معه ٠',NULL,'344',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (792,'{هو الذي خلقكم من طين} يعني: آدم أبا البشر {ثُمَّ قَضَى أَجَلا} يعني: أجل الحياة إلى الموت {وأجل مسمى عنده} من الممات إلى البعث {ثم أنتم} أيُّها المشركون بعد هذا {تمترون} تشكُّون وتكذِّبون بالبعث يريد إنَّ الذي ابتدأ الخلق قادرٌ على إعادته',NULL,'344',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (793,'{وهو الله} أَي: المعبود المعظَّم المتفرِّد بالتَّدبير {في السماوات وفي الأرض}',NULL,'344',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (794,'{وَمَا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيَاتِ رَبِّهِمْ} الدَّالَّة على وحدانيَّته كما ذكر من خلق آدم وخلق اللَّيل والنَّهار {إلاَّ كانوا عنها معرضين} تاركين التًّفكُّر فيها',NULL,'344',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (795,'{فقد كذبوا} يعني: مشركي أهل مكة {بالحق لما جاءهم} يعني: القرآن {فَسَوْفَ يَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ} أَيْ: أخبار استهزائهم وجزاؤه',NULL,'345',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (796,'{ألم يروا} يعني: هؤلاء الكفَّار {كم أهلكنا من قبلهم من قرن} من جيلٍ وأمَّةٍ {مَكَّنَّاهُمْ فِي الأَرْضِ مَا لَمْ نُمَكِّنْ لَكُمْ} أعطيناهم من المال والعبيد والأنعام ما لم نُعطكم {وأرسلنا السماء} المطر {عليهم مدراراً} كثير الدَّرِّ وهو إقباله ونزوله بكثرة {فأهلكناهم بذنوبهم} بكفرهم {وأنشأنا} أوجدنا {من بعدهم قرناً آخرين} وهذا احتجاجٌ على منكري البعث',NULL,'345',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (797,'{ولو نزلنا عليك} قال مشركو مكَّة: لن نؤمن لك حتى تأتينا بكتاب من السَّماء جملةً واحدة معاينة فقال الله: {ولو نزلنا عليك كتاباً} أَيْ: مكتوباً {في قرطاس} يعني: الصَّحيفة {فلمسوه بأيديهم} فعاينوا ذلك مُعاينةً ومسُّوه بأيديهم {لَقَالَ الَّذِينَ كَفَرُوا إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ} أخبر الله تعالى أنَّهم يدفعون الدَّليل حتى لو رأوا الكتاب ينزل من السَّماء لقالوا: سحر',NULL,'345',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (798,'{وقالوا: لولا أنزل عليه ملك} طلبوا ملكاً يرونه يشهد له بالرِّسالة فقال الله عز وجل: {وَلَوْ أَنْزَلْنَا مَلَكًا لَقُضِيَ الأَمْرُ} لأُهلكوا بعذاب الاستئصال كسُنَّة مَنْ قبلهم ممَّن طلبوا الآيات فلم يؤمنوا {ثم لا ينظرون} لا يُمهلون لتوبةٍ ولا لغير ذلك',NULL,'345',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (799,'{ولو جعلناه ملكاً} أيْ: ولو جعلنا الرَّسول الذي ينزل عليهم ليشهدوا له بالرِّسالة مَلَكاً كما يطلبون {لجعلناه رجلاً} لأنَّهم لا يستطيعون أن يروا المَلَك في صورته لأنَّ أعين الخلق تحار عن رؤية الملائكة ولذلك كان جبريل عليه السَّلام يأتي رسول الله صلى الله عليه وسلم في صورة دحية الكلبيِّ {وللبسنا عليهم ما يلبسون} ولخلطنا عليهم ما يخلطون على أنفسهم حتى يشكُّوا فلا يدروا أمَلَكٌ هو أم آدميٌّ أَيْ: فإنَّما طلبوا حال لبسٍ لا حال بيانٍ ثمَّ عزَّى الله نبيَّه عليه السَّلام بقوله:',NULL,'346',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (800,'{ولقد استهزئ برسل من قبلك} وكُذِّبوا ونُسبوا إلى السّحر {فحاق} فحلَّ ونزل {بالذين سخروا} من الرُّسل {ما كانوا به يستهزئون} من العذاب وينكرون وقوعه',NULL,'346',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (801,'{قل} لهم يا محمد: {سيروا في الأرض} سافروا في الأرض {ثم انظروا} فاعتبروا {كيف كان عاقبة} مُكذِّبي الرُّسل يعني: إذا سافروا رأوا آثار الأمم الخالية المهلكة يحذِّرهم مثلَ ما وقع بهم',NULL,'346',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (802,'{قل لمن ما في السماوات والأرض} فإن أجابوك وإلاَّ {قل لله كتب على نفسه الرحمة} أوجب على نفسه الرَّحمة وهذا تلطُّفٌ في الاستدعاء إلى الإنابة {ليجمعنكم} أي: والله ليجمعنكم {إلى يوم القيامة} أَيْ: ليضمنَّكم إلى هذا اليوم الذي أنكرتموه وليجمعنَّ بينكم وبينه ثمَّ ابتدأ فقال: {الذين خسروا أنفسهم} أهلكوها بالشِّرك {فَهُمْ لا يُؤْمِنُونَ}',NULL,'346',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (803,'{وله ما سكن في الليل والنهار} أَيْ: ما حلَّ فيهما واشتملا عليه يعني: جميع المخلوقات',NULL,'346',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (804,'{قل أغير الله أتخذ وليا فاطر السماوات والأرض} خالقهما ابتداءً {وهو يطعم ولا يطعم} يَرزق ولا يرزق',NULL,'347',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (805,'{قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يوم عظيم}',NULL,'347',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (806,'{من يصرف عنه} أَي: العذاب {يومئذ} يوم القيامة {فقد رحمه} فقد أوجب الله له الرَّحمة لا محالة',NULL,'347',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (807,'{وإن يمسسك الله بضر} أيْ: إنْ جعل الضُّرَّ وهو المرض والفقر يمسُّك',NULL,'347',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (808,'{وهو القاهر} القادر الذي لا يعجزه شيء {فوق عباده} أَيْ: إنَّ قهره قد استعلى عليهم فهم تحت التَّسخير',NULL,'347',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (809,'{قل أي شيء أكبر شهادة} قال أهل مكة للنبي صلى الله عليه وسلم ائتنا بمَنْ يشهد لك بالنُّبوَّة فإنَّ أهل الكتاب ينكرونك فنزلت هذه الآية أمر الله تعالى محمداً عليه السَّلام أن يسألهم ثمَّ أمر أن يخبرهم فيقول: {اللَّهُ شَهِيدٌ بيني وبينكم} أَي: الله الذي اعترفتم بأنه خالق السماوات والأرض والظُّلمات والنُّور يشهد لي بالنُّبوَّة بإقامة البراهين وإنزال القرآن عليَّ {وأوحي إلي هذا القرآن} المُعجز بلفظه ونظمه وأخباره عمَّا كان ويكون {لأنذركم} لأخوِّفكم {به} عقاب الله على الكفر {ومَنْ بلغ} يعني: ومَنْ بلغه القرآن من بعدكم فكلُّ مَنْ بلغه القرآن فكأنما رأى محمد عليه السَّلام قل {أإنكم لتشهدون أنَّ مع الله آلهة أخرى} استفهام معناه الجحد والإِنكار {قل لا أشهد} الآية',NULL,'347',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (810,'{الذين آتيناهم الكتاب} مفسَّرة في سورة البقرة',NULL,'348',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (811,'{وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا} أي: لا أحد أظلم ممَّن اختلق على الله كذباً يعني: الذين ذكرهم في قوله: {وإذا فعلوا فاحشة} الآية {أو كذَّب بآياته} بالقرآن وبمحمد عليه السَّلام {إنه لا يفلح الظالمون} لا يسعد مَنْ جحد ربوبيَّة ربِّه وكذَّب رسله وهم الذين ظلموا أنفسهم بإهلاكها بالعذاب',NULL,'348',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (812,'{ويوم} واذكر يوم {نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا أَيْنَ شركاؤكم} أصنامكم وآلهتكم {الذين كنتم تزعمون} أنَّها تشفع لكم وهذا سؤال توبيخ',NULL,'348',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (813,'{ثم لم تكن فتنتهم} أَيْ: لم تكن عاقبة افتتانهم بالأوثان وحبِّهم لها {إلاَّ أن} تبرَّؤوا منها فـ {قَالُوا وَاللَّهِ رَبِّنَا مَا كُنَّا مُشْرِكِينَ}',NULL,'348',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (814,'{انظر} يا محمد {كَيْفَ كَذَبُوا عَلَى أَنْفُسِهِمْ} بجحد شركهم في الآخرة {وضلَّ} وكيف ضلَّ ذلك: زال وبطل {عنهم ما كانوا يفترون} بعبادته من الأصنام',NULL,'348',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (815,'{ومنهم} ومن الكفَّار {من يستمع إليك} إذا قرأت القرآن {وجعلنا على قلوبهم أكنَّة} أغطيةً {أن يفقهوهُ} لئلا يفهموه ولا يعلموا الحقَّ {وفي آذانهم وقراً} ثِقلاً وصمماً فلا يعون منه شيئاً ولا ينتفعون به {وإن يروا كلَّ آية} علامة تدل على صدقك {لا يؤمنوا بها} هذا حالهم في البعد عن الإِيمان {حتى إذا جاؤوك يجادلونك} مخاصمين معك في الدِّين {يقول الذين كفروا} مَنْ كفر منهم: {إن هذا} ما هذا {إلاَّ أساطير الأوَّلين} أحاديث الأمم المتقدمة التي كانوا يسطرونها في كتبهم',NULL,'348',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (816,'{وهم ينهون} النَّاس عن اتَّباع محمد {وينأون} ويتباعدون {عنه} فلا يؤمنون به {وإنْ} وما {يهلكون إلاَّ أنفسهم} بتماديهم في معصية الله تعالى {وما يشعرون} وما يعلمون ذلك',NULL,'349',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (817,'{ولو ترى} يا محمد {إذ وقفوا على النار} أَيْ: حُبسوا على الصِّراط فوق النَّار {فقالوا يا ليتنا نرد} تمنَّوا أن يردُّوا إلى الدُّنيا فيؤمنوا وهو قوله: {ولا نكذب} أَيْ: ونحن لا نكذِّب {بآيات ربنا} بعد المعاينة {ونكون من المؤمنين} ضمنوا أنْ لا يُكذِّبوا ويؤمنوا فقال الله تعالى:',NULL,'349',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (818,'{بل} ليس الأمر على ما تمنَّوا في الردِّ {بدا لهم ما كانوا يخفون من قبل} وهو أنَّهم أنكروا شركهم فأنطق الله سبحانه جوارحهم حتى شهدت عليهم بالكفر والمعنى: ظهرت فضيحتهم في الآخرة وتهتكت أستارهم {ولو ردوا لعادوا لما نهوا} إلى ما نُهوا {عنه} من الشِّرك للقضاء السَّابق فيهم بذلك وأنَّهم خلقوا للشَّقاوة {وإنهم لكاذبون} في قولهم: {وَلا نُكَذِّبَ بِآيَاتِ ربنا}',NULL,'349',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (819,'{وقالوا} يعني: الكفار {إن هي إلاَّ حياتنا الدنيا} أنكروا البعث',NULL,'350',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (820,'{ولو ترى إذ وقفوا على ربهم} عرفوا ربَّهم ضرورة وقيل: وقفوا على مسألة ربِّهم وتوبيخه إيَّاهم ويؤكِّد هذا قوله: {أليس هذا بالحق} أَيْ: هذا البعث فيقرُّون حين لا ينفعهم ذلك ويقولون: {بلى وربنا} فيقول الله تعالى: {فَذُوقُوا الْعَذَابَ بِمَا كنتم تكفرون} بكفركم',NULL,'350',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (821,'{قد خسر الذين كذبوا بلقاء الله} بالبعث والمصير إلى الله {حتى إذا جاءتهم الساعة بغتة} فجأة {قَالُوا يَا حَسْرَتَنَا عَلَى مَا فَرَّطْنَا فِيهَا} قصَّرنا وضيَّعنا عمل الآخرة في الدُّنيا {وَهُمْ يَحْمِلُونَ أَوْزَارَهُمْ} أثقالهم وآثامهم {على ظهورهم} وذلك أنَّ الكافر إذا خرج من قبره استقبله عمله أقبح شيءٍ صورةً وأخبثه ريحاً فيقول: أنا عملك السَّيِّىء طال ما ركبتني في الدُّنيا فأنا أركبك اليوم {ألا ساء ما يزرون} بئس الحمل ما حملوا',NULL,'350',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (822,'{وما الحياة الدنيا إلاَّ لعبٌ ولهو} لأنَّها تفنى وتنقضي كاللَّهو واللَّعب تكون لذَّةً فانيةً عن قريبٍ {وللدار الآخرة} الجنَّة {خير للذين يتقون} الشِّرك {أفلا تعقلون} أنَّها كذلك فلا تَفْتُروا في العمل لها ثم عزى نبيه صلى الله عليه وسلم على تكذيب قريش إيَّاه فقال:',NULL,'350',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (823,'{قَدْ نَعْلَمُ إِنَّهُ لَيَحْزُنُكَ الَّذِي يَقُولُونَ} في العلانيَة: إنَّك كذَّابٌ ومُفترٍ {فإنهم لا يكذبونك} في السرِّ قد علموا صدقك {وَلَكِنَّ الظَّالِمِينَ بِآيَاتِ اللَّهِ يَجْحَدُونَ} بالقرآن بعد المعرفة نزلت في المعاندين الذين تركوا الانقياد للحقِّ كما قال عز وجل: {وجحدوا بها واستيقنتها أنفسهم} افلآيه',NULL,'351',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (824,'{وَلَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلَى مَا كُذِّبُوا} رجاء ثوابي {وأوذوا} حتى نشروا بالمناشير وحرِّقوا بالنَّار {حتى أتاهم نصرنا} معونتنا إيَّاهم بإهلاكِ مَنْ كذَّبهم {وَلا مُبَدِّلَ لِكَلِمَاتِ اللَّهِ} لا ناقِضَ لحكمه وقد حكم بنصر الأنبياء في قوله: {كتب الله لأغلبنَّ أنا ورسلي} {وَلَقدْ جَاءَكَ مِنْ نَبَإِ الْمُرْسَلِينَ} أَيْ: خبرهم في القرآن كيف أنجيناهم ودَمَّرنا قومهم',NULL,'351',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (825,'{وإن كان كبر} عَظُمَ وثَقُل {عليك إعراضهم} عن الإيمان بك وبالقرآن وذلك أن النبي صلى الله عليه وسلم كان يحرص على إيمان قومه فكانوا إذا سألوه آيةً أحبًّ أن يريهم ذلك طمعاً في إيمانهم فقال الله عز وجل: {فإن استطعت أن تبتغي} تطلب {نفقاً} سرباً {في الأرض أو سلماً} مصعداً {في السماء فتأتيهم بآية} فافعل ذلك والمعنى: أنَّك بشرٌ لا تقدر على الإِتيان بالآيات فلا سبيل لك إلاَّ الصَّبر حتى يحكم الله {ولو شاء الله لجمعهم على الهدى} أَيْ: إنَّما تركوا الإِيمان لسابق قضائي فيهم لو شئت لاجتمعوا على الإِيمان {فلا تكوننَّ من الجاهلين} بأنّه يؤمن بك بعضهم دون بعض وأنَّهم لا يجتمعون على الهدى وغلَّظ الجواب زجراً لهم عن هذه الحال',NULL,'351',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (826,'{إنما يستجيب} أَيْ: يُجيبك إلى الإيمان {الذين يسمعون} وهم المؤمنون الذين يستمعون الذِّكر فيقبلونه وينتفعون به والكافر الذي ختم الله على سمعه كيف يصغى إلى الحقِّ؟ {والموتى} يعني: كفَّار مكة {يبعثهم الله ثمَّ إليه يرجعون} يردُّون فيجازيهم بأعمالهم',NULL,'352',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (827,'{وقالوا} يعني: رؤساء قريش {لولا} هلاَّ {نُزِّلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ} يعنون: نزول ملك يشهد له بالنُّبوَّة {قُلْ إِنَّ اللَّهَ قَادِرٌ عَلَى أَنْ ينزل آية ولكن أكثرهم لا يعلمون} ما عليهم في ذلك من البلاء وهو ما ذكرنا في قوله: {ولو أنزلنا ملكاً لقضي الأمر}',NULL,'352',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (828,'{وَمَا مِنْ دَابَّةٍ فِي الأَرْضِ وَلا طَائِرٍ يطير بجناحيه} يعني: جميع الحيوانات لأنها لا تخلوا من هاتين الحالتين {إلاَّ أمم أمثالكم} أصناف مصنَّفة تُعرف بأسمائها فكلُّ جنس من البهائم أُمَّةٌ كالطَّير والظِّباء والذُّباب والأُسود وكلُّ صنفٍ من الحيوان أُمَّةٌ مثل بني آدم يعرفون بالإِنس {ما فرَّطنا في الكتاب من شيء} ما تركنا في الكتاب من شيءٍ بالعباد إليه حاجةٌ إلاَّ وقد بيَّناه إمَّا نصّاً وإمَّا دلالة وإمَّا مجملاً وإمَّا مفصَّلاً كقوله: {وَنَزَّلْنَا عَلَيْكَ الْكِتَابَ تِبْيَانًا لِكُلِّ شَيْءٍ} أَيْ: لكلِّ شيء يحتاج إليه من أمر الدِّين {ثم إلى ربهم} أَيْ: هذه الأمم {يحشرون} للحساب والجزاء',NULL,'352',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (829,'{والذين كذَّبوا بآياتنا} بما جاء به محمد عليه السَّلام {صمٌّ} عن القرآن لا يسمعونه سماع انتفاع {وبكم} عن القرآن لا ينطقون به ثمَّ أخبر أنَّهم بمشيئته صاروا كذلك فقال: {مَنْ يَشَإِ اللَّهُ يُضْلِلْهُ وَمَنْ يَشَأْ يَجْعَلْهُ على صراطٍ مستقيم}',NULL,'353',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (830,'{قل} يا محمد لهؤلاء المشركين بالله {أرأيتكم} معناه: أخبروني {إِنْ أَتَاكُمْ عَذَابُ اللَّهِ} يريد: الموت {أَوْ أتتكم الساعة} القيامة {أغير الله تدعون} أَيْ: أتدعون هذه الأصنام والأحجار التي عبدتموها من دون الله {إن كنتم صادقين} جوابٌ لقوله: {أرأيتكم} لأنَّه بمعنى أخبروني كأنَّه قيل: إنْ كنتم صادقين أخبروا مَنْ تدعون عند نزول البلاء بكم',NULL,'353',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (831,'{بل} أَيْ: لا تدعون غيره {إياه تدعون فيكشف ما تدعون إليه} أَيْ: يكشف الضُّرَّ الذي من أجله دعوتموه {إِنْ شَاءَ وتنسون} وتتركون {ما تشركون} به من الأصنام فلا تدعونه',NULL,'353',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (832,'{ولقد أرسلنا إلى أمم من قبلك} رسلاً فكفروا بهم {فأخذناهم بالبأساء} وهو شدَّة الفقر {والضرَّاء} الأوجاع والأمراض {لعلهم يتضرعون} لكي يتذللَّوا ويتخشعوا',NULL,'353',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (833,'{فلولا} فهلاَّ {إذ جاءهم بأسنا} عذابنا {تضرعوا} تذلَّلوا والمعنى: لم يتضرعوا {ولكن قست قلوبهم} فأقاموا على كفرهم {وزيَّن لهم الشيطان} الضَّلالة التي هم عليها فأصروا',NULL,'353',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (834,'{فلما نسوا ما ذكروا به} تركوا ما وُعظوا به {فتحنا عليهم أبواب كلِّ شيء} من النِّعمة والسُّرور بعد الضُّرِّ الذي كانوا فيه {حتى إذا فرحوا بما أوتوا أخذناهم} في حال فرحهم ليكون أشدَّ لتحسُّرهم {بغتةً فإذا هم مبلسون} آيسون من كلِّ خير',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (835,'{فقطع دابر القوم الذين ظلموا} أنفسهم أَيْ: غابرهم الذي يتخلَّف في آخر القوم والمعنى: استؤصلوا بالهلاك فلم يبق منهم باقية {والحمد لله رب العالمين} على نصر الرُّسل وإهلاك الظَّالمين',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (836,'{قُلْ أَرَأَيْتُمْ إِنْ أَخَذَ اللَّهُ سَمْعَكُمْ وَأَبْصَارَكُمْ} أَيْ: أصمكم وأعمالكم {وَخَتَمَ عَلَى قُلُوبِكُمْ} حتى لا تعرفوا شيئاً يعني: أذهب هذه الأعضاء عنكم أصلاً {مَنْ إِلَهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِهِ} أَيْ: بما أخذ عنكم {انظر كيف نصرف} نبين لهم في القرآن {الآيات ثم هم يصدفون} يعرضون عمَّا ظهر لهم',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (837,'{قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ بَغْتَةً أو جهرة} ليلاً أو نهاراً {هَلْ يُهْلَكُ إِلا الْقَوْمُ الظالمون} الذين جعلوا لله شركاء',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (838,'{وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلا مُبَشِّرِينَ وَمُنْذِرِينَ فَمَنْ آمَنَ وَأَصْلَحَ فَلا خَوْفٌ عَلَيْهِمْ وَلا هُمْ يحزنون}',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (839,'{وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوا يفسقون}',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (840,'{قُلْ لا أَقُولُ لَكُمْ عِنْدِي خَزَائِنُ اللَّهِ} التي منها يرزق ويعطي {ولا أعلم الغيب} فأخبركم بعاقبة ما تصيرون إليه {ولا أقول لكم إني ملك} أشاهد من أمر الله ما لا يشاهده البشر {إِنْ أَتَّبِعُ إِلا مَا يُوحَى إِلَيَّ} أَيْ: ما أخبركم إلاَّ بما أنزل الله عليَّ {قل هل يستوي الأعمى والبصير} الكافر والمؤمن {أفلا تتفكرون} أَنَّهما لا يستويان',NULL,'354',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (841,'{وأنذر به} خوِّف بالقرآن {الَّذِينَ يَخَافُونَ أَنْ يُحْشَرُوا إِلَى رَبِّهِمْ} يريد: المؤمنين يخافون يوم القيامة وما فيها من أهوال {لَيْسَ لَهُمْ مِنْ دُونِهِ وَلِيٌّ وَلا شَفِيعٌ} يعني: إنَّ الشفاعة إنَّما تكون بإذنه ولا شفيعٌ ولا ناصرٌ لأحدٍ في القيامة إلاَّ بإذن الله {لعلهم يتقون} كي يخافوا في الآخرة وينتهوا عمَّا نهيتهم',NULL,'355',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (842,'{ولا تطرد الذين يدعون ربهم} نزلت في فقراء المؤمنين لمَّا قال رؤساء الكفَّار للنبي صلى الله عليه وسلم: نَحِّ هؤلاء عنك لنجالسك ونؤمن بك ومعنى: {يدعون ربهم بالغداة والعشي} يعبدون الله بالصَّلوات المكتوبة {يريدون وجهه} يطلبون ثواب الله {ما عليك من حسابهم} من رزقهم {من شيء} فَتَمَلُّهم وتطردهم {وَمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِنْ شَيْءٍ} أَيْ: ليس رزقك عليهم ولا رزقهم عليك وإنَّما يرزقك وإياهم الله الرزاق فدعهم يدنوا منك ولا تطردهم {فَتَكُونَ مِنَ الظالمين} لهم بطردهم',NULL,'355',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (843,'{وكذلك فتنا بعضهم ببعض} ابتلينا الغنيّ بالفقير والشَّريف بالوضيع {ليقولوا} يعني: الرُّؤساء {أهؤلاء} الفقراء والضُّعفاء {منَّ الله عليهم من بيننا} أنكروا أن يكونوا سبقوهم بفضيلةٍ أو خصُّوا بنعمةٍ فقال الله تعالى: {أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ} أيْ: إنَّما يهدي إلى دينه مَنْ يعلم أنَّه يشكر',NULL,'355',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (844,'{وإذا جاءك الذين يؤمنون بآياتنا} يعني: الصَّحابة وهؤلاء الفقراء {فقل سلام عليكم} سلّم عليهم بتحيَّة المسلمين {كتب ربكم على نفسه الرحمة} أوجب الله لكم الرَّحمة إيجاباً مُؤكَّداً {أنه من عمل منكم سوءاً بجهالة} يريد: إن ذنوبكم حهل ليس بكفرٍ ولا جحود لأنَّ العاصي جاهلٌ بمقدار العذاب في معصيته {ثم تاب من بعده} رجع عن ذنبه {وأصلح} عمله {فأنَّه غفور رحيم}',NULL,'356',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (845,'{وكذلك} وكما بينَّا لك في هذه السُّورة دلائلنا على المشركين {نفصل} نبيِّن لك حجَّتنا وأدلتنا ليظهر الحقُّ ولتعرف يا محمد سبيل المجرمين في شركهم بالله في الدُّنيا وما يصيرون إليه من الخزي يوم القيامة بإخباري إيَّاك',NULL,'356',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (846,'{قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ من دون الله} الأصنام التي يعبدونها مِنْ دُونِ اللَّهِ {قُلْ لا أَتَّبِعُ أَهْوَاءَكُمْ} أَيْ: إنَّما عبدتموها على طريق الهوى لا على طريق البرهان فلا أتَّبعكم على هواكم {قد ضللت إذاً} إنْ أنا فعلت ذلك {وما أنا من المهتدين} الذين سلكوا سبيل الهدى',NULL,'356',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (847,'{قل إني على بينة} يقينٍ وأمرٍ بيِّنٍ {من ربي} لا مُتَّبع لهوىً {وكذبتم به} أَيْ: بربِّي {ما عندي ما تستعجلون به} يعني: العذاب أو الآيات التي اقترحتموها ثمَّ أعلم أنَّ ذلك عنده فقال: {إن الحكم إلاَّ لله يقص الحق} أَيْ: يقول القصص الحقّ ومَنْ قرأ: {يقص الحق} فمعناه: يقضي القضاء الحق {وهو خير الفاصلين} الذين يفصلون بين الحقِّ والباطل',NULL,'356',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (848,'{قُلْ لَوْ أَنَّ عِنْدِي مَا تَسْتَعْجِلُونَ بِهِ} من العذاب لعجلت لكم ولانفصل ما بيني وبينكم بتعجيل العقوبة وهو معنى قوله: {لَقُضِيَ الأَمْرُ بَيْنِي وَبَيْنَكُمْ وَاللَّهُ أَعْلَمُ بِالظَّالِمِينَ} هو أعلم بوقت عقوبتهم فهو يؤخِّرهم إلى وقته وأنا لا أعلم ذلك قوله:',NULL,'357',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (849,'{وعنده مفاتح الغيب} خزاءن ما غاب عن بني آدم من الرزق ولامطر ونزول العذاب والثَّواب والعقاب {لا يَعْلَمُهَا إِلا هُوَ وَيَعْلَمُ مَا فِي البر} القفار {والبحر} كلُّ قرية فيها ماءٌ لا يحدث فيهما شيء إلاَّ بعلم الله {وما تسقط من ورقة إلاَّ يعلمها} ساقطة وقبل أنْ سقطت {ولا حبة في ظلمات الأرض} في الثرى تحت الأرض {ولا رطب} وهو ما ينبت {ولا يابس} وهو ما لا ينبت {إلاَّ في كتاب مبين} أثبت الله ذلك كلَّه في كتابٍ قبل أن يخلق الخلق',NULL,'357',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (850,'{وهو الذي يتوفاكم بالليل} يقبض أرواحكم في منامكم {وَيَعْلَمُ مَا جَرَحْتُمْ} ما كسبتم من العمل {بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فيه} يردُّ إليكم أرواحكم في النَّهار {ليقضى أجل مسمى} يعني: أجل الحياة إلى الموت أَيْ: لتستوفوا أعماركم المكتوبة',NULL,'358',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (851,'{وهو القاهر فوق عباده} مضى هذا {ويرسل عليكم حفظة} من الملائكة يحصون أعمالكم {حَتَّى إِذَا جَاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا} أعوان ملك الموت {وَهُمْ لا يُفَرِّطُونَ} لا يعجزون ولا يُضيِّعون',NULL,'358',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (852,'{ثم ردوا} يعني: العباد يُردُّون بالموت {إلى الله مولاهم الحق أَلا لَهُ الْحُكْمُ} أَي: القضاء فيهم {وَهُوَ أسرع الحاسبين} أقدر المجازين',NULL,'358',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (853,'{قل من ينجيكم} سؤال توبيخٍ وتقريرٍ أَيْ: إنَّ الله يفعل ذلك {من ظلمات البر والبحر} أهوالهما وشدائدهما {تدعونه تضرعاً وخفية} علانيَةً وسرَّاً {لئن أنجانا من هذه} أَيْ: من هذه الشَّدائد {لنكوننَّ من الشاكرين} من المؤمنين الطَّائعين وكانت قريش تسافر في البر والبحر فإذا ضلُّوا الطَّريق وخافوا الهلاك دعوا الله مخلصين فأنجاهم وهو قوله:',NULL,'358',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (854,'{قل الله ينجيكم منها} الآية أعلم الله سبحانه أنَّ الله الذي دعوه هو ينجِّيهم ثمَّ هم يشركون معه الأصنام التي قد علموا أنَّها من صَنعتهم وأنَّها لا تضرُّ ولا تنفع والكرب أشدُّ الغمِّ ثمَّ أخبر أنَّه قادر على تعذيبهم فقال:',NULL,'358',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (855,'{قُلْ هُوَ الْقَادِرُ عَلَى أَنْ يَبْعَثَ عَلَيْكُمْ عذاباً من فوقكم} كالصَّيحة والحجارة والماء {أو من تحت أرجلكم} كالخسف والزَّلزلة {أو يلبسكم شيعاً} يخلطكم فرقاً بأن يبثَّ فيكم الأهواء المختلفة فتخالفون وتقاتلون وهو معنى قوله: {وَيُذِيقَ بَعْضَكُمْ بَأْسَ بَعْضٍ انظر كيف نصرِّف} نُبيِّن لهم {الآيات} في القرآن {لعلهم يفقهون} لكي يعلموا',NULL,'359',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (856,'{وكذَّب به} بالقرآن {قَوْمُكَ وَهُوَ الْحَقُّ قُلْ لَسْتُ عَلَيْكُمْ بِوَكِيلٍ} بمسلَّط أَيْ: إنَّما أدعوكم إلى الله ولم أُومر بحربكم ولا أَخْذكم بالإِيمان وهذا منسوخٌ بآية القتال',NULL,'359',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (857,'{لكلِّ نبأ مستقر} لكلِّ خبرٍ يخبره الله وقتٌ ومكانٌ يقع فيه من غير خلف {وسوف تعلمون} ما كان منه في الدنيا فتستعرفونه وما كان منه في الآخرة فسوف يبدو لكم يعني: العذاب الذي كان يعدهم في الدُّنيا والآخرة',NULL,'359',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (858,'{وإذا رأيت الذين يخوضون في آياتنا} بالتَّكذيب والاستهزاء {فأعرض عنهم} أمر الله تعالى رسوله عليه السَّلام فقال: إذا رأيت المشركين يُكذِّبون بالقرآن وبك ويستهزئون فاترك مجالستهم {حتى يخوضوا في حديث غيره} حتى يكون خوضهم في غير القرآن {وَإِمَّا ينسينَّك الشيطان} إنْ نسيت فقعدت {فَلا تَقْعُدْ بَعْدَ الذِّكْرَى} فقم إذا ذكرت فقال المسلمون: لئن كنَّا كلَّما استهزأ المشركون بالقرآن وخاضوا فيه قمنا عنهم لم نستطع أن نجلس في المسجد الحرام وأن نطوف بالبيت فرخَّص للمؤمنين في القعود معهن يُذكِّرونهم فقال:',NULL,'360',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (859,'{وما على الذين يتقون} الشِّرك والكبائر {من حسابهم} آثامهم {من شيء ولكن ذكرى} يقول: ذكِّروهم بالقرآن وبمحمَّد فرخَّص لهم بالقعود بشرط التَّذكير والموعظة {لعلَّهم يتقون} لِيُرجى منهم التَّقوى',NULL,'360',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (860,'{وذر الذين اتخذوا دينهم لعباً ولهواً} يعني: الكفَّار الذين إذا سمعوا آيات الله استهزؤوا بها وتلاعبوا عند ذكرها {وذكِّر به} وعِظْ بالقرآن {أَنْ تُبْسَلَ نَفْسٌ بما كسبت} تُسلم للهلكة وتحبس في جهَّنم فلا تقدر على التَّخلص ومعنى الآية: وذكرِّهم بالقرآن إسلام الجانين بجناياتهم لعلَّهم يخافون فيتَّقون {وإن تعدل كل عدل} يعني: النَّفس المُبسلة تفدِ كلَّ فداء يعني: تفدِ بالدُّنيا وما فيها {لا يؤخذ منها أُولَئِكَ الَّذِينَ أُبْسِلُوا بِمَا كَسَبُوا} أُسْلِموا للهلاك {لَهُمْ شَرَابٌ مِنْ حَمِيمٍ} وهو الماء الحار',NULL,'360',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (861,'{قل أندعوا مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُنَا وَلا يضرنا} أنعبد ما لا يملك لنا نفعاً ولا ضرَّاً لأنَّه جماد؟ {وَنُرَدُّ عَلَى أَعْقَابِنَا بَعْدَ إِذْ هَدَانَا اللَّهُ} نردُّ وراءنا إلى الشِّرك بالله فيكون حالنا كحال {كالذي استهوته الشياطين في الأرض} استغوته واستفزَّته الغِيلان في المهانة {حيران} متردِّداً لا يهتدي إلى المحجَّة {له أصحابٌ يدعونه إلى الهدى ائتنا} هذا مثَلُ مَنْ ضلَّ بعد الهدى يجيب الشَّيطان الذي يستهويه في المفازة فيصبح في مضلَّة من الأرض يهلك فيها ويعصي مَنْ يدعوه إلى المحجَّة كذلك مَنْ ضلَّ بعد الهدى {قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَى} ردٌّ على مَنْ دعا إلى عبادة الأصنام أَيْ: لا نفعل ذلك لأنَّ هدى الله هو الهدى لا هدى غيره',NULL,'361',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (862,'{وَأَنْ أَقِيمُوا الصَّلاةَ وَاتَّقُوهُ وَهُوَ الَّذِي إِلَيْهِ تحشرون}',NULL,'361',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (863,'{وهو الذي خلق السماوات والأرض بالحق} أَيْ: بكمال قدرته وشمول علمه وإتقان صنعه وكلُّ ذلك حقٌّ {ويوم يقول} واذكر يا محمَّد يوم يقول للشَّيء {كن فيكون} يعني: يوم القيامة يقول للخلق انتشروا فينتشرون',NULL,'361',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (864,'{وَإِذْ قَالَ إِبْرَاهِيمُ لأَبِيهِ آزَرَ أَتَتَّخِذُ أَصْنَامًا آلِهَةً إِنِّي أَرَاكَ وَقَوْمَكَ فِي ضَلالٍ مُبِينٍ}',NULL,'361',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (865,'{وكذلك نري إبراهيم} أَيْ: وكما أرينا إبراهيم استقباح ما كان عليه أبوه من عبادة الأصنام نريه {ملكوت السماوات والأرض} يعني: ملكهما كالشَّمس والقمر والنجوم والجبال والشجر والبحار أراه الله تعالى هذه الأشياء حتى نظر إليها مُعتبراً مُستدلاًّ بها على خالقها وقوله: {وليكون من الموقنين} عطفٌ على المعنى تقديره: ليستدلَّ بها وليكون من الموقنين',NULL,'361',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (866,'{فلما جنَّ} أَيْ: ستر وأظلم {عَلَيْهِ اللَّيْلُ رَأَى كَوْكَبًا قَالَ هَذَا رَبِّي} أَيْ: في زعمكم أيُّها القائلون بحكم النَّجم وذلك أنَّهم كانوا أصحاب نجومٍ يرون التدبيرفي الخليقة لها {فلما أفل} أَيْ: غاب {قال لا أحبُّ الآفلين} عرَّفهم جهلهم وخطأهم في تعظيم النُّجوم ودلَّ على أنَّ مَنْ غاب بعد الظُّهور كان حادثاً مُسخَّراً وليس بربٍّ',NULL,'362',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (867,'{فلما رأى القمر بازغاً} طالعاً فاحتجَّ عليهم في القمر والشَّمس بمثل ما احتجَّ به عليهم في الكوكب وقوله: {لئن لم يهدني ربي} أَيْ: لئن لم يُثبِّتني على الهدى وقوله للشَّمس:',NULL,'362',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (868,'{هذا ربي} ولم يقل هذه لأنَّ لفظ الشَّمس مذكَّرٌ ولأنَّ الشَّمس بمعنى الضياء والنُّور فحمل الكلام على المعنى {هذا أكبر} أَي: من الكوكب والقمر فلمَّا توجَّهت الحجَّة على قومه قال: {إِنِّي بَرِيءٌ مِمَّا تُشْرِكُونَ}',NULL,'362',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (869,'{إني وجهت وجهي} أَيْ: جعلت قصدي بعبادتي وتوحيدي لله عز وجل وباقي الآية مفسَّر فيما مضى',NULL,'362',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (870,'{وحاجَّة قومه} جادلوه وخاصموه في تركه آلهتهم وعبادة الله وخوَّفوه أن تصيبه آلهتهم بسوء فقال: {أتحاجوني في الله} أَيْ: في عبادته وتوحيده {وقد هدان} بيَّن لي ما به اهتديت {ولا أخاف ما تشركون به} من الأصنام أن تصيبني بسوء {إلاَّ أن يشاء ربي شيئاً} إني لا أخاف إلاَّ مشيئة الله أن يعذِّبني {وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْمًا} علمه علماً تاماً {أفلا تتذكرون} تتعظون وتتركون عبادة الأصنام',NULL,'363',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (871,'{وكيف أخاف ما أشركتم} يعني: الأصنام أنكر أن يخافها {وَلا تَخَافُونَ أَنَّكُمْ أَشْرَكْتُمْ بِاللَّهِ مَا لَمْ ينزل به عليكم سلطاناً} ما ليس لكم في إشراكه بالله حجَّةٌ وبرهانٌ {فإيُّ الفريقين أحق بالأمن} بأن يأمن العذاب الموحِّدُ أم المشرك؟',NULL,'363',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (872,'{الذين آمنوا ولم يلبسوا إيمانهم بظلم} لم يخلطوا إيمانهم بشركٍ {أولئك لهم الأمن} من العذاب {وهم مهتدون} إلى دين الله',NULL,'363',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (873,'{وتلك حجتنا} يعني: ما احتجَّ به عليهم {آتيناها إبراهيم} ألهمنا إبراهيم فأرشدناه إليها {نرفع درجات مَنْ نشاء} مراتبهم بالعلم والفهم ثمَّ ذكر نوحاً ومَنْ هدى من الأنبياء من أولاده إلى قوله:',NULL,'363',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (874,'{وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ كُلَّا هَدَيْنَا وَنُوحًا هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِهِ دَاوُدَ وَسُلَيْمَانَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَى وَهَارُونَ وَكَذَلِكَ نَجْزِي الْمُحْسِنِينَ}',NULL,'363',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (875,'{وَزَكَرِيَّا وَيَحْيَى وَعِيسَى وَإِلْيَاسَ كُلٌّ مِنَ الصَّالِحِينَ}',NULL,'363',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (876,'{وكلاً} أَيْ: من المذكورين هاهنا {فضلنا على العالمين} علمي زمانهم',NULL,'364',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (877,'{ومن آبائهم} أَيْ: وهدينا بعض آبائهم {وذرياتهم وإخوانهم} فـ من هاهنا للتبغيض',NULL,'364',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (878,'{ذلك هدى الله} دين الله الذي هم عليه {يَهْدِي بِهِ مَنْ يشاء} يريد: يرشد إليه مَنْ يشاء {من عباده ولو أشركوا} عبدوا غيري {لحبط} بطل عملهم',NULL,'364',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (879,'{أُولَئِكَ الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ} يعني: الكتب التي أنزلها عليهم {والحكم} العلم والفقه {فإن يكفر بها} أي: بآياتنا {هؤلاء} أهل مكَّة {فقد وكلنا بها} أَيْ: أرصدنا لها {قوماً} وفَّقناهم لها وهم المهاجرون والأنصار',NULL,'364',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (880,'{أُولَئِكَ الَّذِينَ هَدَى اللَّهُ} يعني: النَّبيِّين الذين تقدَّم ذكرهم {فبهداهم اقتده} أَي: اصبر كما صبروا فإنَّ قومهم كذَّبوهم فصبروا {قل لا أسألكم عليه} على القرآن وتبليغ الرِّسالة {أجراً} مالاً تعطونيه {إن هو} يعني: القرآن {إِلا ذِكْرَى لِلْعَالَمِينَ} موعظة للخلق أجمعين',NULL,'364',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (881,'{وما قدروا الله حق قدره} ما عظَّموا الله حقَّ عظمته وما وصفوه حقَّ صفته {إِذْ قَالُوا مَا أَنْزَلَ اللَّهُ عَلَى بَشَرٍ من شيء} وذلك أن ليهود أنكروا إنزال الله عزَّ وجل من السَّماء كتاباً إنكاراً للقرآن {قل} لهم يا محمد: {مَنْ أَنْزَلَ الْكِتَابَ الَّذِي جَاءَ بِهِ مُوسَى} يعني: التَّوراة {تجعلونه قراطيس} مكتوبة وتودعونه إيَّاها {تبدونها} يعني: القراطيس يبدون ما يحبُّون ويكتمون صفة محمَّد صلى الله عليه وسلم {وَعُلِّمْتُمْ مَا لَمْ تَعْلَمُوا أَنْتُمْ وَلا آبَاؤُكُمْ} في التَّوراة فضيَّعتموه ولم تنتفعوا به {قل الله} أي: الله أنزله {ثُمَّ ذَرْهُمْ في خوضهم} إفكهم وحديثهم الباطل {يلعبون} يعملون ما لا يُجدي عليهم',NULL,'364',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (882,'{وهذا كتاب} يعني: القرآن {أنزلناه مبارك} كثيرٌ خيره دائمٌ نفعه يبشِّر بالثواب ويزجر عن القبيح إلى ما لا يحصى من بركاته {مصدق الذي بين يديه} موافقٌ لما قبله من الكتب {ولتنذر أم القرى} أهل مكة {ومن حولها} يعني: أهل سائر الآفاق {والذين يؤمنون بالآخرة} إيماناً حقيقياً {يؤمنون به} بالقرآن',NULL,'365',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (883,'{وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا} نزلت في مسيلمة والأسود العنسي ادَّعيا النُّبوَّة وأنَّ الله قد أوحى إليهما وهذا معنى قوله: {أَوْ قَالَ أُوحِيَ إِلَيَّ وَلَمْ يُوحَ إِلَيْهِ شَيْءٌ وَمَنْ قَالَ سَأُنْزِلُ مِثْلَ مَا أَنْزَلَ الله} يعني: المستهزئون الذين قالوا: {لو نشاء لقلنا مثل هذا} {ولو ترى} يا محمد {إذ الظالمون} يعني: الذين ذكرهم {في غمرات الموت} شدائده وأهواله {والملائكة باسطوا أيديهم} إليهم بالضَّرب والتَّعذيب {أخرجوا أنفسكم} أَيْ: يقولون ذلك ونفس الكافر تخرج بمشقةٍ وكُرهٍ لأنَّها تصير إلى أشدِّ العذاب والملائكة يكرهونهم على نزع الرُّوح ويقولون: {أخرجوا أنفسكم} كرهاً {اليوم تجزون عذاب الهون} أَي: العذاب الذي يقع به الهوان الشَّديد {بِمَا كُنْتُمْ تَقُولُونَ عَلَى اللَّهِ غَيْرَ الْحَقِّ} من أنَّه أوحي إليكم ولم يوح {وكنتم عن آياته تستكبرون} عن الإِيمان بها تتعظَّمون',NULL,'365',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (884,'{ولقد جئتمونا فرادى} يقال للكفَّار في الآخرة: جئتمونا فرادى بلا أهل ولا مالٍ ولا شيءٍ قدَّمتموه {كما خلقناكم أول مرة} كما خرجتم من بطون أُمَّهاتكم {وَتَرَكْتُمْ مَا خوَّلناكم} ملَّكناكم وأعطيناكم من المال والعبيد والمواشي {وَرَاءَ ظُهُورِكُمْ وَمَا نَرَى مَعَكُمْ شُفَعَاءَكُمُ الَّذِينَ زعمتم أنَّهم فيكم شركاء} وذلك أنَّ المشركين كانوا يعبدون الأصنام على أنَّهم شركاء الله وشفعاؤهم عنده {لقد تقطع بينكم} وصلكم ومودتكم {وضلَّ عنكم} ذهب عنكم {ما كنتم تزعمون} تُكذِّبون في الدُّنيا',NULL,'366',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (885,'{إنَّ الله فالق الحبّ} شاقُّة بالنَّبات {والنوى} بالنَّخلة {يخرج الحي من الميت} يخرج النُّطفة بشراً حيّاً {ومُخرج الميت} النُّطفة {من الحيّ} وقيل: يخرج المؤمن من الكافر والكافر من المؤمن {ذلكم الله} الذي فعل هذه الأشياء التي تشاهدونها ربكم {فأنى تؤفكون} فمن أين تُصرفون عن الحقِّ بعد البيان!',NULL,'366',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (886,'{فالق الإِصباح} شاقُّ عمود الصُّبح عن ظلمة اللَّيل وسواده على معنى أنَّه خالقه ومبديه {وجعل الليل سكناً} للخلق يسكنون فيه سكون الرَّاحة {والشمس والقمر حسباناً} وجعل الشَّمس والقمر بحسبانٍ لا يجاوزانه فيما يدوران في حسابٍ {ذلك تقدير العزيز} في ملكه يصنع ما أراد {العليم} بما قدَّر من خلقهما',NULL,'366',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (887,'{وَهُوَ الَّذِي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِهَا فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ قَدْ فَصَّلْنَا الآيَاتِ لقوم يعلمون}',NULL,'367',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (888,'{وهو الذي أنشأكم من نفس واحدة} يعني: آدم {فمستقر} أَيْ: فلكم مستقرٌّ في الأرحام {ومستودع} في الأصلاب',NULL,'367',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (889,'{وَهُوَ الَّذِي أَنْزَلَ مِنَ السَّمَاءِ مَاءً} يعني: المطر {فأخرجنا به نبات كلٍّ شيء} يَنبت {فأخرجنا} من ذلك النَّبات {خضراً} أخضر كالقمح والشَّعير والذُّرة وما كان رطباً أخضر مما ينبت من الحبوب {نخرج منه} من الخضر {حباً متراكباً} بعضه على بعض في سنبلةٍ واحدةٍ {ومن النخل من طلعها} أوَّل ما يطلع منها {قنوان} يعني: العراجين التي قد تدلَّت من الطَّلع {دانية} ممَّن يجتنيها يعني: قصار النَّخل اللاَّحقة عذوقها بالأرض {وجنات} أَيْ: وأخرجنا بالماء جنَّات {من أعناب والزيتون} وشجر الزَّيتون {والرمان} وشجر الرُّمان {مشتبهاً} في اللون يعني: الرُّماني {وغير متشابه} في الطَّعم أي: مختلفة في الطَّعم وقيل: مُشتبهاً ورقها مُختلفاً ثمرها {انظروا إلى ثمره} نظر الاستدلال والعبرة أوَّل ما يعقد {وينعه} نضجه {إنَّ في ذلكم لآيات لقوم يؤمنون} يصدِّقون أنَّ الذي أخرج هذا النَّبات قادرٌ على أن يحيي الموتى',NULL,'367',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (890,'{وجعلوا لله شركاء الجن} أطاعوا الشَّياطين في عبادة الأوثان فجعلوهم شركاء لله {وخرقوا له بنين وبنات} افتعلوا ذلك كذباً وكفراً يعني: الذين قالوا: الملائكة بنات الله واليهود والنَّصارى حين دعوا لله ولداً {بغير علم} لم يذكروه عن علمٍ إنَّما ذكروه تكذُّباً وقوله:',NULL,'368',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (891,'{أَنَّى يَكُونُ لَهُ وَلَدٌ وَلَمْ تَكُنْ لَهُ صاحبة} أَيْ: مِنْ أين يكون له ولدٌ؟ ولا يكون الولد إلاَّ من صاحبةٍ ولا صاحبة له {وخلق كلَّ شيء} أَيْ: وهو خالق كل شيء',NULL,'368',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (892,'{ذَلِكُمُ اللَّهُ رَبُّكُمْ لا إِلَهَ إِلا هُوَ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُ وَهُوَ عَلَى كُلِّ شيء وكيل}',NULL,'368',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (893,'{لا تدركه الأبصار} في الدُّنيا لأنَّه وعد في القيامة الرُّؤية بقوله: {وجوه يومئذ ناضرة * إلى ربها ناظرة} الآية والمُطلق يحمل على المقيد وقيل: لا يحيط بكنهه وحقيقته الأبصار وهي تراه فالأبصار ترى الباري ولا تحيط به {وهو يدرك الأبصار} يراها ويحيط بها علماً لا كالمخلوقين الذين لا يدركون حقيقة البصر وما الشَّيء الذي صار به الإِنسان يبصر من عينيه دون أن يبصر من غيرهما {وهو اللطيف} الرَّفيق بأوليائه {الخبير} بهم',NULL,'368',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (894,'{قد جاءكم بصائر من ربكم} يعني: بيِّنات القرآن {فمن أبصر} اهتدى {فلنفسه} عمل {ومن عمي فعليها} فعلى نفسه جنى العذاب {وما أنا عليكم بحفيظ} برقيب على أعمالكم حتى أجازيكم بها',NULL,'369',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (895,'{وكذلك} وكما بيَّنا في هذه السُّورة {نصرِّف} نبيِّن {الآيات} في القرآن ندعوهم بها ونخوِّفهم {وليقولوا درست} عطف على المضمر في المعنى والتقدير: نصرِّف الآيات لتلزمهم الحجَّة وليقولوا درست أَيْ: تعلَّمت مِن يسار وجبر واليهود ومعنى درس: قرأ على غيره ومعنى هذه اللام في قوله: {وليقولوا} معنى لم العاقبة أَيْ: نصرِّف الآيات ليكون عاقبة أمرهم تكذيباً للشَّقاوة التي لحقتهم {ولنبينه لقوم يعلمون} يعني: أولياءه الذين هداهم والذين سعدوا بتبيين الحق',NULL,'369',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (896,'{اتَّبِعْ مَا أُوحِيَ إِلَيْكَ مِنْ رَبِّكَ لا إله إلا هو وأعرض عن المشركين}',NULL,'369',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (897,'{ولو شاء الله ما أشركوا} أَيْ: ولو شاء الله لجعلهم مؤمنين {وَمَا جَعَلْنَاكَ عَلَيْهِمْ حفيظاً} لم تبعث لتحفظ المشركين من العذاب إنَّما بُعثت مُبَلِّغاً فلا تهتمَّ لشركهم فإنَّ ذلك لمشيئة الله',NULL,'369',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (898,'{وَلا تَسُبُّوا الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ} يعني: أصنامهم ومعبوديهم وذلك أنَّ المسلمين كانوا يسبُّون أصنام الكفَّار فنهاهم الله عزَّ وجل عن ذلك لئلا يسبُّوا {الله عدواً بغير علم} أَيْ: ظُلماً بالجهل {كذلك} أَيْ: كما زيَّنا لهؤلاء عبادة الآوثان وطاعة الشَّيطان بالحرمان والخذلان {زينا لكلِّ أمة عملهم} من الخير والشَّرِّ',NULL,'369',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (899,'{وأقسموا بالله جهد أيمانهم} اجتهدوا في المبالغة في اليمين {لَئِنْ جَاءَتْهُمْ آية ليؤمنن بها} وذلك أنَّه لمَّا نزل: {إن نشأ ننزل عليهم} الآية أقسم المشركون بالله لئن جاءنهم آية ليؤمننَّ بها وسأل المسلمون ذلك وعلم الله سبحانه أنَّهم لا يؤمنون فأنزل الله هذه الآية {قُلْ إِنَّمَا الآيَاتُ عِنْدَ اللَّهِ} هو القادر على الإتيان بها {وما يشعركم} وما يدريكم إيمانهم أَيْ: هم لا يؤمنون مع مجيء الآيات إيَّاهم ثمَّ ابتدأ فقال: {إنها إذا جاءت لا يؤمنون} ومَنْ قرأ أنَّها بفتح الألف كانت بمعنى لعلَّها ويجوز أن تجعل لا زائدة مع فتح أنَّ',NULL,'370',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (900,'{ونقلب أفئدتهم وأبصارهم} نحول بينهم وبين الإيمان لو جاءتهم تلك الآية بتقليب قلوبهم وأبصارهم عن وجهها الذي يجب أن تكونَ عليه فلا يؤمنون {كما لم يؤمنوا به} بالقرآن أو بمحمَّدٍ عليه السَّلام {أوَّل مرَّة} أتتهم الآيات مثل انشقاق القمر وغيره {ونذرهم في طغيانهم يعمهون} أخذلهم وأدعهم في ضلالتهم يتمادون',NULL,'370',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (901,'{ولو أننا نزلنا إليهم الملائكة} فرأوهم عياناً {وكلمهم الموتى} فشهدوا لك بالصِّدق والنُّبوَّة {وحشرنا عليهم} وجمعنا عليهم {كلَّ شيء} في الدُّنيا {قُبلاً} و {قِبَلاً} أَيْ: مُعاينةً ومُواجهةً {ما كانوا ليؤمنوا} لما سبق لهم من الشَّقاء {إلا أن يشاء الله} أن يهديهم {ولكنَّ أكثرهم يجهلون} أنَّهم لو أُوتوا بكلِّ آيةٍ ما آمنوا',NULL,'370',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (902,'{وكذلك جعلنا لكلِّ نبيٍّ عدوّاً} كما ابتليناك بهؤلاء القوم كذلك جعلنا لكلِّ نبيٍّ قبلك أعداءً ليعظم ثوابه والعدو هاهنا يُراد به الجمع ثمَّ بيَّن مَنْ هم فقال: {شياطين الإِنس} يعني: مردة الإِنس والشَّيطان: كلُّ متمرِّدٍ عاتٍ من الجنَّ والإِنس {يُوحِي بَعْضُهُمْ إِلَى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُورًا} يعني: إنَّ شياطين الجنِّ الذين هم من جند إبليس يوحون إلى كفار الإِنس ومردتهم فيغرونهم بالمؤمنين وزخرف القول: باطله الذي زُيِّن ووُشِّي بالكذب والمعنى أنَّهم يُزيِّنون لهم الأعمال القبيحة غروراً {ولو شاء ربك ما فعلوه} لَمَنع الشَّياطين من الوسوسة للإِنس',NULL,'371',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (903,'{ولتصغى إليه} ولتميل إلى ذلك الزُّخرف والغرور {أفئدة الذين لا يؤمنون بالآخرة} قلوب الذين لا يصدِّقون بالبعث {وليرضوه} ليحبُّوه {وليقترفوا} ليعملوا ما هم عاملون',NULL,'371',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (904,'{أفغير الله} أَيْ: قل لأهل مكَّة: أفغير الله {أبتغي حكماً} قاضياً بيني وبينكم {وَهُوَ الَّذِي أَنْزَلَ إِلَيْكُمُ الكتاب} القرآن {مفصلاً} مُبَيِّناً فيه أمره ونهيه {وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ} من اليهود والنَّصارى {يعلمون} أنَّ القرآن {مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ فَلا تكونن من الممترين} من الشَّاكين أنَّهم يعلمون ذلك',NULL,'371',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (905,'{وتمت كلمة ربك} أقضيته وعِداته لأوليائه في أعدائه {صدقاً} فيما وعد {وعدلاً} فيما حكم والمعنى: صادقةً عادلةً {لا مبدل لكلماته} لا مغير لحكمه ولا خلف لوعده {وهو السميع} لتضرُّع أوليائه ولقول أعدائه {العليم} بما في قلوب الفريقين',NULL,'372',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (906,'{وإن تطع أكثر من في الأرض} يعني: المشركين {يُضِلُّوكَ عَنْ سَبِيلِ اللَّهِ} دين الله الذي رضيه لك وذلك أنَّهم جادلوه في أكل الميتة وقالوا: أتأكلون ما قتلتم ولا تأكلون ما قتل ربُّكم؟ {إن يتبعون إلا الظن} في تحليل الميتة {وإن هم إلاَّ يخرصون} يكذبون في تحليل ما حرَّمه الله',NULL,'372',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (907,'{إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَنْ يَضِلُّ عَنْ سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ}',NULL,'372',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (908,'{فكلوا مما ذكر اسم الله عليه} أَيْ: ممَّا ذكِّي على اسم الله {إن كنتم بآياته مؤمنين} تأكيدٌ لاستحلال ما أباحه الشَّرع ثمَّ أبلغَ في إباحة ما ذبح على اسم الله بقوله:',NULL,'372',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (909,'{وما لكم أنْ لا تأكلوا مما ذكر اسم الله عليه} عند الذَّبح {وقد فصَّل} بيَّن {لكم ما حرَّم عليكم} في قوله: {حرمت عليكم الميتة} الآية {إلاَّ ما اضطررتم إليه} دعتكم الضَّرورة إلى أكله ممَّا لا يحلُّ عند الاختيار {وإنَّ كثيراً ليضلون بأهوائهم} أَيْ: الذين يُحلُّون الميتة ويناظرونكم في إحلالها ضلُّوا باتِّباع أهوائهم {بغير علمٍ} إنَّما يتَّبعون فيه الهوى ولا بصيرة عندهم ولا علم {إنَّ ربك هو أعلم بالمعتدين} المتجاوزين الحلال إلى الحرام',NULL,'372',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (910,'{وذروا ظاهر الإِثم وباطنه} سرَّه وعلانيته ثمَّ أوعد بالجزاء فقال: {إِنَّ الَّذِينَ يَكْسِبُونَ الإِثْمَ سَيُجْزَوْنَ بِمَا كَانُوا يَقْتَرِفُونَ}',NULL,'373',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (911,'{وَلا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللَّهِ عليه} ممَّا لم يُذَكَّ ومات {وإنه} وإنَّ أكله {لفسقٌ} خروجٌ عن الحقِّ {وإنَّ الشياطين} يعني: إبليس وجنوده وسوسوا {إلى أوليائهم} من المشركين ليخاصموا محمداً وأصحابه في أكل الميتة {وإن أطعتموهم} في استحلال الميتة {إنكم لمشركون} لأنَّ مَنْ أحلَّ شيئاً ممَّا حرَّمه الله فهو مشركٌ',NULL,'373',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (912,'{أَوْ مَنْ كان ميتاً فأحييناه} ضالاًّ كافراً فهديناه {وجعلنا له نوراً} ديناً وإيماناً {يمشي به في الناس} مع المسلمين مُستضيئاً بما قذف الله في قلبه من نور الحكمة والإِيمان {كمَنْ مثله} كمَن هو {في الظلمات} في ظلمات الكفر والضَّلالة {لَيْسَ بِخَارِجٍ مِنْهَا} ليس بمؤمن أبداً نزلت في أبي جهلٍ وحمزة بن عبد المطلب {كذلك} كما زُيِّن للمؤمنين الإِيمان {زين للكافرين ما كانوا يعملون} من عبادة الأصنام',NULL,'373',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (913,'{وَكَذَلِكَ جَعَلْنَا فِي كُلِّ قَرْيَةٍ أَكَابِرَ مُجْرِمِيهَا} يعني: كما أنَّ فسَّاق مكَّة أكابرها كذلك جعلنا فسَّاق كلِّ قرية أكابرها يعني: رؤساءَها ومترفيها {ليمكروا فيها} بصدِّ النَّاس عن الإِيمان {وَمَا يَمْكُرُونَ إِلا بِأَنْفُسِهِمْ} لأنَّ وبال مكرهم يعود عليهم {وما يشعرون} أنَّهم يمكرون بها',NULL,'374',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (914,'{وإذا جاءتهم آية} ممَّا أطلع الله عليه نبيَّه عليه السَّلام ممَّا يخبرهم به {قَالُوا لَنْ نُؤْمِنَ حَتَّى نُؤْتَى مِثْلَ مَا أوتي رسل الله} حتى يوحى إلينا ويأتينا جبريل فنصدِّق به وذلك أنَّ كلَّ واحدٍ من القوم سأل أن يُخصَّ بالوحي كما قال الله: {بَلْ يُرِيدُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُؤْتَى صحفا مُنشَّرة} فقال الله سبحانه: {اللَّهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسَالَتَهُ} يعني: أنَّهم ليسوا بأهل لها هو أعلم بمَنْ يختصُّ بالرِّسالة {سيصيب الذين أجرموا صغار} مذلَّةٌ وهوانٌ {عند الله} أَيْ: ثابت لهم عند الله ذلك',NULL,'374',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (915,'{فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ للإِسلام} يوسِّعْ قلبه ويفتحه ليقبل الإِسلام {وَمَنْ يُرِدْ أَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقًا حرجاً} شديد الضِّيق {كأنما يصَّعد في السماء} إذا كُلَّف الإِيمان لشدَّته وثقله عليه {كذلك} مثل ما قصصنا عليك {يَجْعَلُ اللَّهُ الرِّجْسَ} العذاب {على الذين لا يؤمنون}',NULL,'374',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (916,'{وهذا صراط ربك} هذا الذي أنت عليه يا محمد دين رَبِّكَ {مُسْتَقِيمًا قَدْ فَصَّلْنَا الآيَاتِ لِقَوْمٍ يَذَّكَّرُونَ} وهم المؤمنون',NULL,'375',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (917,'{لهم دار السلام} الجنة {عند ربهم} مضمونةً لهم حتَّى يُدخلهموها {وهو وليهم} يتولَّى إيصال الكرامات إليهم {بما كانوا يعملون} من الطَّاعات',NULL,'375',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (918,'{ويوم يحشرهم جميعاً} الجنّ والإِنس فيقال لهم: {يَا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ من الإِنس} أَيْ: من إغوائهم وإضلالهم {وقال أولياؤهم} الذين أضلَّهم الجنُّ {مِنَ الإِنْسِ رَبَّنَا اسْتَمْتَعَ بعضنا ببعض} يعني: طاعة الإِنس للجنِّ وقبولهم منهم ما كانوا يغرونهم به من الضَّلالة وتزيين الجنِّ للإِنس ما كانوا يهوونه حتى يسهل عليهم فعله {وبلغنا أجلنا الذي أجلت لنا} يعني: الموت والظَّاهر أنَّه البعث والحشر {قال النار مثواكم} فيها مقامكم {خَالِدِينَ فِيهَا إِلا مَا شَاءَ الله} مَنْ شاء الله وهم مَنْ سبق في علم الله أنَّهم يُسلمون {إنَّ ربك حكيم} حكم للذين استثنى بالتَّوبة والتَّصديق {عليم} علم ما في قلوبهم من البرِّ',NULL,'375',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (919,'{وكذلك نولي بعض الظالمين بعضاً} كما خذلنا عُصاة الجنِّ والإنس نَكِلُ بعض الظَّالمين إلى بعض حتى يضلَّ بعضهم بعضاً',NULL,'375',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (920,'{يَا مَعْشَرَ الْجِنِّ وَالإِنْسِ أَلَمْ يَأْتِكُمْ رُسُلٌ منكم} الرُّسل كانت من الإنس والذين بلَّغوا الجنَّ منهم عن الرُّسل كانوا من الجنِّ وهم النُّذر كالذين استمعوا القرآن من محمد صلى الله عليه وسلم من الجنِّ فأبلغوه قومهم',NULL,'375',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (921,'{ذلك} الذي قصصنا عليك من أمر الرُّسل لأنَّه {لم يكن ربك مُهْلِكَ الْقُرَى بِظُلْمٍ} أَيْ: بذنوبهم ومعاصيهم من قبل أن يأتيهم الرَّسول فينهاهم وهو معنى قوله: {وأهلها غافلون} أَيْ: لكلِّ عاملٍ بطاعة الله درجات في الثَّواب ثمَّ أوعد المشركين فقال: {وما ربك بغافل عما يعملون}',NULL,'376',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (922,'{وَلِكُلٍّ دَرَجَاتٌ مِمَّا عَمِلُوا وَمَا رَبُّكَ بِغَافِلٍ عما يعملون}',NULL,'376',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (923,'{وربك الغني} عن عبادة خلقه {ذو الرحمة} بخلقه فلا يعجل عليهم بالعقوبة {إن يشأ يذهبكم} يعني: أهل مكَّة {ويستخلف من بعدكم} وينشئ من بعدكم خلقاَ آخر {كما أنشأكم} خلقكم ابتداءً {مِنْ ذُرِّيَّةِ قَوْمٍ آخَرِينَ} يعني: آباءهم الماضين',NULL,'376',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (924,'{إنما توعدون لآت وما أنتم بمعجزين}',NULL,'376',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (925,'{قل يا قوم اعملوا على مكانتكم} على حالاتكم التي أنتم عليها {إني عامل} على مكانتي وهذا أمرُ تهديدٍ يقول: اعملوا ما أنتم عاملون إنِّي عاملٌ ما أنا عَامِلٌ {فَسَوْفَ تَعْلَمُونَ مَنْ تَكُونُ لَهُ عَاقِبَةُ الدار} أيُّنا تكون له الجنَّة {إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ} لا يسعد مَنْ كفر بالله وأشرك بالله',NULL,'376',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (926,'{وَجَعَلُوا لِلَّهِ مِمَّا ذَرَأَ مِنَ الْحَرْثِ وَالأَنْعَامِ} كان المشركون يجعلون لله من حروثهم وأنعامهم وثمارهم {نصيباً} وللأوثان نصيباً فما كان للصَّنم أُنْفِقَ عليه وما كان لله أُطعم الضِّيفان والمساكين فما سقط ممَّا جعلوه لله في نصيب الأوثان تركوه وقالوا: إنَّ الله غنيٌّ عن هذا وإن سقط ممَّا جعلوه للأوثان من نصيب الله التقطوه وردُّوه إلى نصيب الصَّنم وقالوا: إنَّه فقير فذلك قوله: {فَمَا كَانَ لِشُرَكَائِهِمْ فَلا يَصِلُ إِلَى اللَّهِ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَى شركائهم} ثمَّ ذمَّ فعلهم فقال: {سَاءَ مَا يَحْكُمُونَ} أَيْ: ساء الحكم حكمهم حيث صرفوا ما جعلوه لله على جهة التَّبرُّز إلى الأوثان',NULL,'376',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (927,'{وكذلك} ومثل ذلك الفعل القبيح {زَيَّنَ لِكَثِيرٍ مِنَ المشركين قتل أولادهم شركاؤهم} يعني: الشَّياطين أمروهم بأن يئدوا أولادهم خشية العَيْلَة {ليردوهم} ليهلكوهم في النَّار {وليلبسوا عليهم دينهم} ليخلطوا ويُدخلوا عليهم الشَّكَّ في دينهم ثمَّ أخبر أنَّ جميع ما فعلوه كان بمشيئته فقال: {وَلَوْ شَاءَ اللَّهُ مَا فعلوه فذرهم وما يفترون} من أنَّ لله شريكاً',NULL,'377',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (928,'{وقالوا هذه أنعام وحرث حجر} حرَّموا أنعاماً وحرثاً وجعلوها لأصنامهم فقالوا: {لا يَطْعَمُهَا إِلا مَنْ نَشَاءُ بزعمهم} أعلم الله سبحانه أنَّ هذا التَّحريم كذبٌ من جهتهم {وأنعام حرّمت ظهورها} كالسَّائبة والبحيرة والحامي {وَأَنْعَامٌ لا يَذْكُرُونَ اسْمَ اللَّهِ عَلَيْهَا} يقتلونها لآلتهم خنقاً أو وقذاً {افتراءً عليه} أَيْ: يفعلون ذلك للافتراء على الله وهو أنَّهم زعموا أنَّ الله أمرهم بذلك',NULL,'377',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (929,'{وقالوا ما في بطون هذه الأنعام} يعني: أجِنَّة ما حرَّموها من البحائر والسَّوائب {خالصةٌ لذكورنا} حلالٌ للرِّجال خاصَّة دون النِّساء هذا إذا خرجت الأجنَّة أحياء وإن كان ميتةً اشترك فيها الرِّجال والنِّساء {سيجزيهم وصفهم} سيجزيهم الله جزاء وصفهم الذي هو كذبٌ أَيْ: سيعذِّبهم الله بما وصفوه من التَّحليل والتَّحريم الذي كلُّه كذبٌ {إنه حكيم عليم} أَيْ: هو أعلم وأحكم من أن يفعل ما يقولون',NULL,'378',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (930,'{قد خسر الذين قتلوا أولادهم} بالوأد {سفهاً} للسَّفه {وحرَّموا ما رزقهم الله} من الأنعام يعني: البحيرة وما ذُكر معها',NULL,'378',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (931,'{وهو الذي أنشأ} أبدع وخلق {جنات معروشات} يعني: الكرم {وغير معروشات} ما قام على ساق ولم يُعرش له كالنَّخل والشَّجر {والنخل والزرع مختلفاً أكله} أُكُلُ كلِّ واحدٍ منهما وكلِّ نوعٍ من الثَّمر له طعمٌ غير طعم النَّوع الآخر وكلُّ حبٍّ من حبوب الزَِّرع له طعمٌ غير طعم الآخر {كُلُوا مِنْ ثَمَرِهِ إِذَا أَثْمَرَ} أمر إباحة {وآتوا حقه يوم حصاده} يعني: العشر ونصف العشر {ولا تسرفوا} فتعطوا كلَّه حتى لا يبقى لعيالكم شيء {إنه لا يحب المسرفين} يعني: المجاوزين أمر الله',NULL,'378',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (932,'{ومن الأنعام} وأنشأ من الأنعام {حمولة} وهي كلُّ ما حمل عليها ممَّا أطاق العمل والحمل {وفرشاً} وهو الصِّغار التي لا يحمل عليها كالغنم والبقر والإِبل الصِّغار {كلوا مما رزقكم الله} أَيْ: أحلَّ لكم ذبحه {ولا تتبعوا خطوات الشيطان} في تحريم شيءٍ ممَّا أحله الله {إنه لكم عدو مبين} بيِّنُ العداوة أخرج أباكم من الجنَّة وقال: لأحتنكنَّ ذريته ثمَّ فسر الحمولة والفرش فقال:',NULL,'378',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (933,'{ثمانية أزواج} الذَّكر زوجٌ والأنثى زوجٌ وهي الضَّأن والمعز وقد ذُكرا في هذه الآية والإِبل والبقر ذُكرا فيما بعد وجعلها ثمانيةً لأنَّه أراد الذَّكر والأُنثى من كلِّ صنفٍ وهو قوله: {من الضَّأن اثنين ومن المعز اثنين} والضَّأن: ذوات الصُّوف من المعز والغنم: ذوات الشَّعر {قل} يا محمد للمشركين الذين يُحرِّمون على أنفسهم ما حرَّموا من النَّعم: {آلذكرين} من الضَّأن والمعز {حرَّم} الله عليكم {أم الأُنثيين} فإن كان حرَّم من الغنم ذكورها فكلُّ ذكورها حرام وإن كان حرَّم الأنثيين فكلُّ الإِناث حرام {أمَّا اشتملت عليه أرحام الأُنثيين} وإن كان حرَّم ما اشتملت عليه أرحام الأنثيين من الضَّأْنِ والمعز فقد حرَّم الأولاد كلَّها وكلُّها أولادٌ فكلُّها حرام {نبئُوني بعلم} أَيْ: فسِّروا ما حرَّمتم بعلمٍ إن كان لكم علمٌ في تحريمه وهو قوله: {إن كنتم صادقين}',NULL,'379',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (934,'{أَمْ كُنْتُمْ شُهَدَاءَ إِذْ وَصَّاكُمُ اللَّهُ بِهَذَا} هل شاهدتم الله قد حرَّم هذا إذْ كنتم لا تؤمنون برسول الله؟ ! فلمَّا لزمتهم الحجَّة بيَّن الله تعالى أنَّهم فعلوا ذلك كذباً على الله فقال: {فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا ليضلَّ الناس بغير علم} الآية يعني: عمرو بن لحي وهو الذي غيَّر دين إسماعيل وسنَّ هذا التَّحريم ثمَّ ذكر المحرَّمات بوحي الله فقال:',NULL,'379',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (935,'{قُلْ لا أَجِدُ فِي مَا أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَى طَاعِمٍ يَطْعَمُهُ إِلا أَنْ يَكُونَ ميتة أو دماً مسفوحاً} يعني: سائلاً {أو فسقاً أهلَّ لغير الله به} يعني: ما ذًبح على النُّصب',NULL,'380',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (936,'{وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا كُلَّ ذِي ظُفُرٍ} يعني: الإِبل والنَّعامة {وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَا إلاَّ ما حملت ظهورهما أو الحوايا} وهي المباعر {أو ما اختلط بعظم} فإنِّي لم أحرّمه يعني: ما تعلَّق من الشَّحم بهذه الأشياء {ذلك} التَّحريم {جزيناهم ببغيهم} عاقبناهم بذنوبهم {وإنا لصادقون} في الإِخبار عن التَّحريم وعن بغيهم فلمَّا ذكر لهم رسول الله صلى الله عليه وسلم ما حُرِّم على المسلمين وما حرِّم على اليهود قالوا له: ما أصبت وكذَّبوه فأنزل الله تعالى:',NULL,'380',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (937,'{فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ وَاسِعَةٍ} ولذلك لا يعجل عليكم بالعقوبة {وَلا يُرَدُّ بَأْسُهُ} عذابه إذا جاء الوقت {عَنِ الْقَوْمِ الْمُجْرِمِينَ} يعني: الذين كذَّبوك بما تقول',NULL,'380',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (938,'{سيقول الذين أشركوا} إذا لزمتهم الحجة وتيقَّنوا باطل ما هم عليه: {لَوْ شَاءَ اللَّهُ مَا أَشْرَكْنَا وَلا آبَاؤُنَا ولا حرَّمنا من شيء كذلك كذَّب} جعلوا قولهم: {لَوْ شَاءَ اللَّهُ مَا أَشْرَكْنَا} حجَّةً لهم على إقامتهم على الشِّرك وقالوا إنَّ الله رضي منَّا ما نحن عليه وأراده منَّا وأمرنا به ولو لم يرضه لحال بيننا وبينه ولا حجة لهم في هذا لأنَّهم تركوا أمر الله وتعلَّقوا بمشيئته وأمرُ الله بمعزلٍ عن إرادته لأنَّه مريدٌ لجميع الكائنات غير آمرٍ بجميع ما يريد فعلى العبد أن يحفظ الأمر ويتَّبعه وليس له أن يتعلَّق بالمشيئة بعد ورود الأمر فقال الله تعالى: {كذلك كذب الذين من قبلهم} أَيْ: كما كذَّبك هؤلاء كذَّب كفَّار الأمم الخالية أنبياءهم ولم يتعرَّض لقولهم: {لو شاء الله} بشيءٍ {قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنَا} من كتابٍ نزل في تحريم ما حرَّمتم {إن تتبعون إلاَّ الظن} ما تتَّبعون فيما أنتم عليه إلاَّ الظَّنَّ لا العلم واليقين {وَإِنْ أَنْتُمْ إِلا تَخْرُصُونَ} وما أنتم إلاَّ كاذبين',NULL,'381',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (939,'{قل فللَّه الحجة البالغة} بالكتاب والرَّسول والبيان {فَلَوْ شَاءَ لهداكم أجمعين} إخبار عن تعلُّق مشيئة الله تعالى بكفرهم وأنَّ ذلك حصل بمشيئته إذ لو شاء الله لهداهم',NULL,'381',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (940,'{قل هلم شهداءكم} أَيْ: هاتوا شهداءكم وقرِّبوهم وباقي الآية ظاهر',NULL,'381',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (941,'{قُلْ تَعَالَوْا أَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ} أَقرأ عليكم الذي حرَّمه الله ثم ذكر فقال: {أن لا تشركوا به شيئاً وبالوالدين إحساناً} وأوصيكم بالوالدين إحساناً {ولا تقتلوا أولادكم} من أولادكم من مخافة الفقر {وَلا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بطن} يعني: سر الزِّنا وعلانيته {وَلا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلا بالحق} يريد القصاص',NULL,'381',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (942,'{وَلا تَقْرَبُوا مَالَ الْيَتِيمِ إِلا بِالَّتِي هِيَ أحسن} وهو أن يصلح ماله ويقوم فيه بما يثمره ثمَّ يأكل بالمعروف إن احتاج إليه {حتى يبلغ أشده} أَي: احفظوه عليه حتى يحتلم {وأوفوا الكيل} أتِمُّوه من غير نقصٍ {والميزان} أَيْ: وزن الميزان {بالقسط} بالعدل لا بخسٍ ولا شططٍ {لا نُكَلِّفُ نَفْسًا إِلا وُسْعَهَا} إلاَّ ما يسعها ولا تضيق عنه وهو أنَّه لو كلَّف المعطي الزِّيادة لضاقت نفسه عنه وكذلك لو كلَّف الآخذ أن يأخذ بالنُّقصان {وإذا قلتم فاعدلوا} إذا شهدتم أو تكلَّمتم فقولوا الحقَّ {وَلَوْ} كان المشهود له أو عليه {ذا قربى}',NULL,'382',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (943,'{وأنًّ هذا} ولأنَّ هذا {صراطي مستقيماً} يريد: ديني دين الحنفية أقومُ الأديان {فاتبعوه ولا تتبعوا السبل} اليهوديَّة والنصرانيَّة والمجوسية وعبادة الأوثان {فتفرَّق بكم عن سبيله} فتضلَّ بكم عن دينه {ذلكم} الذي ذكر {وصَّاكم} أمركم به في الكتاب {لعلكم تتقون} كي تتقوا السُّبل',NULL,'382',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (944,'{ثم آتينا} أَيْ: ثمَّ أُخبركم أنَّا آتَيْنَا {مُوسَى الْكِتَابَ تَمَامًا عَلَى الَّذِي أَحْسَنَ} أَيْ: على الذي أحسنه موسى من العلم والحكمة وكتب الله المتقدِّمة أَيْ: علمه ومعنى: {تماماً} على ذلك أَيْ: زيادة عليه حتى تمَّ له العلم بما آتيناه {وتفصيلاً} أَيْ: آتيناه للتَّمام والتفصيل وهو البيان {لعلهم بلقاء ربهم يؤمنون} لكي يُؤمنوا بالبعث ويُصدِّقوا بالثَّواب والعقاب',NULL,'382',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (945,'{وهذا كتاب} يعني: القرآن {أنزلناه مبارك} مضى تفسيره في هذه السُّورة',NULL,'383',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (946,'{أن تقولوا} لئلا تَقُولُوا: {إِنَّمَا أُنْزِلَ الْكِتَابُ عَلَى طَائِفَتَيْنِ مِنْ قَبْلِنَا} يعني: اليهود والنَّصارى {وَإِنْ كُنَّا عَنْ دراستهم لغافلين} وما كنَّا إلاَّ غافلين عن تلاوة كتبهم والخطابُ لأهل مكَّة والمرادُ: إثبات الحجَّة عليهم بإنزال القرآن على محمَّد عليه السَّلام كيلا يقولوا يوم القيامة: إنَّ التَّوراة والإِنجيل أُنزلا على طائفتين من قبلنا وكنَّا غافلين عمَّا فيهما وقوله:',NULL,'383',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (947,'{وصدف عنها} أَيْ: أعرض',NULL,'383',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (948,'{هل ينظرون} إذا كذَّبوك {إِلا أَنْ تَأْتِيَهُمُ الْمَلائِكَةُ} عند الموت لقبض أرواحهم وذكرنا معنى {ينظرون} في سورة البقرة {أو يأتي ربك} أَيْ: أمره فيهم بالقتل {أو يأتي بعض آيات ربك} يعني: طلوع الشَّمس من مغربها والمعنى: إن هؤلاء الذي كذَّبوك إمَّا أن يموتوا فيقعوا في العذاب أو يؤمر فيهم بالسَّيف أو يمهلون قدر مدَّة الدُّنيا فيتوالدون ويتنعَّمون فيها فإذا ظهرت أمارات القيامة {لا يَنْفَعُ نَفْسًا إِيمَانُهَا لَمْ تَكُنْ آمَنَتْ مِنْ قَبْلُ أَوْ كَسَبَتْ فِي إِيمَانِهَا خَيْرًا} قدَّمت طاعةً وهي مؤمنةٌ {قل انتظروا} أحد هذه الأشياء {إنا منتظرون} بكم أحدها',NULL,'383',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (949,'{إن الذين فرقوا دينهم} يعني: اليهود والنَّصارى أخذوا ببعض ما أُمروا وتركوا بعضه كقوله إخباراً عنهم: {أفتؤمنون ببعض الكتاب وتكفرون ببعض} {وكانوا شيعاً} أحزاباً مختلفة بعضهم يُكفِّر بعضاً {لست منهم في شيء} يقول: لم تؤمر بقتالهم فلمَّا أُمر بقتالهم نُسخ هذا',NULL,'384',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (950,'{من جاء بالحسنة} من عمل من المؤمنين حسنةً {فله عشر أمثالها} كتبت له عشر حسناتٍ {وَمَنْ جَاءَ بِالسَّيِّئَةِ} الخطيئة {فلا يجزى إلاَّ مثلها} أَيْ: جزاءً مثلها لا يكون أكثر منها {وهم لا يظلمون} لا ينقص ثواب أعمالهم',NULL,'384',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (951,'{قُلْ إِنَّنِي هَدَانِي رَبِّي إِلَى صِرَاطٍ مُسْتَقِيمٍ ديناً} أَيْ: عرَّفني ديناً {قيماً} مستقيماً',NULL,'384',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (952,'{قل إنَّ صلاتي ونسكي} عبادتي من حجِّي وقرباني {ومحياي ومماتي لله رب العالمين} أَيْ: هو يحييني وهو يميتني وأنا أتوجَّه بصلاتي وسائر المناسك إلى الله لا إلى غيره وقوله:',NULL,'384',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (953,'{وبذلك أمرت} بذلك أوحي إليَّ {وأنا أول المسلمين} من هذه الأمة',NULL,'385',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (954,'{قل أغير الله أبغي رباً} سيِّداً وإلهاً {وهو ربُّ كلِّ شيء} مالكه وسيِّده {وَلا تَكْسِبُ كُلُّ نَفْسٍ إِلا عَلَيْهَا} لا تجني نفسٌ ذنباً إلاَّ أُخذت به {ولا تزر وازرة وزر أخرى} يعني: الوليد بن المغيرة كان يقول: اتَّبعوا سبيلي أحمل أوزاركم فأنزل الله: {ولا تزر وازرة وزر أخرى} لا يحمل أحدٌ جناية غيره حتى لا يُؤَاخذ بها الجاني',NULL,'385',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (955,'{وهو الذي جعلكم} يا أُمَّةَ محمَّدٍ {خلائف} الأمم الماضية في {الأرض} بأنْ أهلكهم وأورثكم الأرض بعدهم {ورفع بعضكم فوق بعض درجات} بالغنى والرِّزق {ليبلوكم في ما آتاكم} ليختبركم فيما رزقكم {إنَّ ربك سريع العقاب} لأعدائه {وإنه لغفور} لأوليائه {رحيم} بهم',NULL,'385',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (956,'{المص} أنا الله أعلم وأفضل',NULL,'386',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (957,'{كتاب} أَيْ: هذا كتابٌ {أنزل إليك} من ربك {فلا يكن في صدرك حرج منه} فلا يضيقنَّ صدرك بإبلاغ ما أرسلت به {لتنذر به} أَيْ: أُنزل لتنذر به النَّاس {وذكرى للمؤمنين} مواعظ للمصدِّقين',NULL,'386',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (958,'{اتبعوا ما أنزل إليكم من ربكم} يعني: القرآن {ولا تتبعوا من دونه أولياء} لا تتخذوا غير الله أولياء {قليلاً ما تذكرون} قليلاً يا معشر المشركين اتَّعاظكم',NULL,'386',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (959,'{وكم من قرية أهلكناها} يعني: أهلها {فجاءها بأسنا} عذابنا {بياتاً} ليلاً {أو هم قائلون} نائمون نهاراً بعنب: جاءهم بأسنا وهم غير متوقِّعين له',NULL,'386',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (960,'{فما كان دعواهم} دعاؤهم وتضرُّعهم {إذ جاءهم بأسنا إلاَّ أن} أقرُّوا على أنفسهم بالشِّرك و {قَالُوا إِنَّا كُنَّا ظَالِمِينَ}',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (961,'{فلنسألن الذين أرسل إليهم} نسأل الأمم ماذا عملوا فيما جاءت به الرُّسل ونسأل الرُّسل هل بلَّغوا ما أُرسلوا به',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (962,'{فلنقصنَّ عليهم بعلم} لنخبرنَّهم بما عملوا بعلمٍ منَّا {وما كنا غائبين} عن الرُّسل والأمم ما بلَّغت وما ردَّ عليهم قومهم',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (963,'{والوزن يومئذ} يعني: وزن الأعمال يوم السُّؤال الذي ذُكر في قوله: {فلنسألنَّ} {الحق} العدل وذلك أنَّ أعمال المؤمنين تتصوَّر في صورةٍ حسنةٍ وأعمال الكافرين في صورةٍ قبيحة فتوزن تلك الصُّورة فذلك قوله: {فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَئِكَ هُمُ المفلحون} النَّاجون الفائزون وهم المؤمنون',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (964,'{وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ} صاروا إلى العذاب {بِمَا كَانُوا بِآيَاتِنَا يَظْلِمُونَ} يجحدون بما جاء به محمد عليه السَّلام',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (965,'{ولقد مكنَّاكم في الأرض} ملَّكناكم فيما بين مكَّة إلى اليمن وإلى الشَّام يعني: مشركي مكَّة {وجعلنا لكم فيها معايش} ما تعيشون به من الرِّزق والمال والتجارة {قليلاً ما تشكرون} أَيْ: إنَّكم غير شاكرين لما أنعمت عليكم',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (966,'{ولقد خلقناكم} يعني: آدم {ثم صوَّرناكم} في ظهره',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (967,'{قال ما منعك أن لا تسجد} لا زائدة معناها: ما منعك أن تسجد؟ وهو سؤالُ التَّوبيخ والتَّعنيف {قَالَ أَنَا خَيْرٌ مِنْهُ} معناه: منعني من السُّجود له أنِّي خيرٌ منه إذ كنتُ ناريَّاً وكان طينيَّاً فترك الأمر وقاس فعصى',NULL,'387',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (968,'{قال فاهبط منها} فانزل من الجنَّة وقيل: من السَّماء {فَمَا يَكُونُ لَكَ أَنْ تَتَكَبَّرَ فيها} عن أمري وتعصيني {فأخرج إنك من الصاغرين} الأذلاء بترك الطَّاعة',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (969,'{قال انظرني} أمهلني {إلى يوم يبعثون} يريد: النَّفخة الثَّانية',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (970,'{قَالَ إِنَّكَ مِنَ الْمُنْظَرِينَ}',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (971,'{قال: فبما أغويتني} يريد: فبما أضللتني أيْ: بإغوائك إيَّاي {لأَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيمَ} على الطَريق المستقيم الذي يسلكونه إلى الجنَّة بأن أُزيِّن لهم الباطل',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (972,'{ثم لآتينَّهم من بين أيديهم} يعني: آخرتهم التي يردون عليها فَأُشكِّكهم فيها {ومن خلفهم} دنياهم التي يُخَلِّفونها فأُرغِّبهم فيها {وعن أيمانهم} أُشبِّه عليهم أمر دينهم {وعن شمائلهم} أُشهِّي لهم المعاصي',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (973,'{قال اخرج منها} من الجنَّة {مذؤوماً} مذموماً بأبلغ الذَّمِّ {مدحوراً} مطروداً ملعوناً {لمن تبعك منهم} من أولاد آدم {لأَمْلأَنَّ جهنم منكم} يعني: من الكافرين وقرنائهم من الشَّياطين',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (974,'{ويا آدم اسكن} سبق تفسيره في سورة البقرة',NULL,'388',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (975,'{فوسوس لهما الشيطان} أَيْ: حدَّث لهما في أنفسهما {ليبدي لهما} هذه اللام لام العاقبة وذلك أنَّ عاقبة تلك الوسوسة أدَّت إلى أن بدت لهما سوآتهما يعني: فروجهما بتهافت اللَّباس عنهما وهو قوله {ما وري} أَيْ: سُتر {عنهما من سوآتهما} {وَقَالَ مَا نَهَاكُمَا رَبُّكُمَا عَنْ هَذِهِ الشَّجَرَةِ} أَيْ: عن أكلها {إلاَّ أن تكونا} لا هاهنا مضمرة أَيْ: إلاَّ أن لا تكونا {ملكين} يبقيان ولا يموتان كما لا تموت الملائكة يدلُّ على هذا المعنى قوله: {أو تكونا من الخالدين}',NULL,'389',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (976,'{وقاسمهما} حلف لهما {إني لكما لمن الناصحين}',NULL,'389',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (977,'{فدلاهما بغرور} غرَّهما باليمين ومعنى دلاَّهما: جَرَّأَهُما على أكل الشَّجرة بما غرَّهما به من يمينه {فلما ذاقا الشجرة بدت لهما سوآتهما} تهافت لباسهما عنهما فأبصر كلُّ واحدٍ منهما عورة صاحبه فاستحييا {وطفقا يخصفان} أقبلا وجعلا يُرقِّعان الورق كهيئة الثواب ليستترا به {وَنَادَاهُمَا رَبُّهُمَا أَلَمْ أَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَأَقُلْ لَكُمَا إِنَّ الشَّيْطَانَ لَكُمَا عَدُوٌّ مُبِينٌ}',NULL,'389',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (978,'{قَالا رَبَّنَا ظَلَمْنَا أَنْفُسَنَا وَإِنْ لَمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ}',NULL,'389',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (979,'{قَالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَلَكُمْ فِي الأرض مستقر} موضع قرار ثم فسر ذلك بقوله:',NULL,'389',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (980,'{فيها تحيون} ولمَّا ذكر عُريَّ آدم وحواء منَّ علينا بما خلق لنا من اللِّباس فقال:',NULL,'390',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (981,'{يا بني آدم قد أنزلنا عليكم} أَيْ: خلقنا لكم {لباساً يواري سوآتكم} يستر عوراتكم {وريشاً} أَيْ: مالاً وما تتجمَّلون به من الثِّياب الحسنة {ولباس التقوى} أَيْ: ستر العورة لمَنْ يتَّقي الله فيواري عورته {ذلك خيرٌ} لصاحبه إ ذا أخذ به أو خيرٌ من التَّعري وذلك أنَّ جماعةً من المشركين كانوا يتعبَّدون بالتَّعريِّ وخلع الثَّياب في الطواف بالبيت {ذلك من آيات الله} أَيْ: من فرائضه التي أوجبها بآياته يعني: ستر العورة {لعلهم يذكرون} لكي يتَّعظوا',NULL,'390',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (982,'{يا بني آدم لا يفتننَّكم الشيطان} لا يخدعنَّكم ولا يُضلنَّكم {كَمَا أَخْرَجَ أَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لباسهما} أضاف النَّزع إليه وإن لم يتولَّ ذلك لأنَّه كان بسببٍ منه {إنَّه يراكم هو وقبيله} يعني: ومَنْ كان من نسله {إِنَّا جَعَلْنَا الشَّيَاطِينَ أَوْلِيَاءَ لِلَّذِينَ لا يُؤْمِنُونَ} سلَّطناهم عليهم ليزيدوا في غيِّهم كما قال: {أَنَّا أَرْسَلْنَا الشَّيَاطِينَ على الكافرين}',NULL,'390',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (983,'{وإذا فعلوا فاحشة} يعني: طوافهم بالبيت عارين',NULL,'391',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (984,'{قل أمر ربي بالقسط} ردٌّ لقولهم: (والله أمرنا به) والقسط: العدل {وأقيموا وجوهكم عند كلِّ مسجد} وجِّهوا وجوهكم حيث ما كنتم في الصَّلاة إلى الكعبة {وَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ} وحِّدوه ولا تشركوا به شيئاًَ {كما بدأكم} في الخلق شقيا وسعيداً فكذلك {تعودون} سعداء وأشقياء يدلُّ على صحَّة هذا المعنى قوله:',NULL,'391',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (985,'{فريقاً هدى} أرشد إلى دينه وهم أولياؤه {وَفَرِيقًا حَقَّ عليهم الضلالة} أَضَلَّهُمْ وهم أولياء الشيطان {إِنَّهُمُ اتَّخَذُوا الشَّيَاطِينَ أَوْلِيَاءَ مِنْ دُونِ اللَّهِ ويحسبون أنهم مهتدون} ثمَّ أمرهم أن يلبسوا ثيابهم ولا يتعرَّوا فقال:',NULL,'391',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (986,'{يا بني آدم خذوا زينتكم} يعني: ما وارى العورة {عند كلِّ مسجد} لصلاةٍ أو طواف {وكلوا واشربوا} كان أهل الجاهليَّة لا يأكلون أيَّام حجِّهم إلاَّ قوتاً ولا يأكلون دسماً يُعظِّمون بذلك حجِّهم فقال المسلمون: نحن أحقُّ أن نفعل فأنزل الله تعالى: {وكلوا} يعني: اللَّحم والدَّسم {واشربوا} اللَّبن والماء وما أحلَّ لكم {ولا تسرفوا} بحظركم على أنفسكم ما قد أحللته لكم من اللَّحم والدَّسم {إِنَّهُ لا يُحِبُّ} مَنْ فعل ذلك أي: لايثيبه ولا يدخله الجنَّة',NULL,'391',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (987,'{قُلْ مَنْ حَرَّمَ زِينَةَ اللَّهِ الَّتِي أَخْرَجَ لعباده} مَنْ حرَّم أن تلبسوا في طوافكم ما يستركم {والطيبات من الرزق} يعني: ما حرَّموه على أنفسهم أيَّام حجِّهم {قل هي} أَي: الطَّيِّبات من الرِّزق {للذين آمنوا في الحياة الدنيا} مباحةٌ لهم مع اشتراك الكافرين معهم فيها في الدُّنيا ثمَّ هي تخلص للمؤمنين يوم القيامة وليس للكافرين فيها شيء وهو معنى قوله: {خالصة يوم القيامة} {كذلك نفصل الآيات} نُفسِّر ما أحللت وما حرَّمت {لقومٍ يعلمون} أنِّي أنا الله لا شريك لي',NULL,'392',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (988,'{قل إنما حرَّم ربي الفواحش} الكبائر والقبائح {ما ظهر منها وما بطن} سرَّها وعلانيتها {والإِثم} يعني: المعصية التي توجب الإِثم {والبغي} ظلم النَّاس وهو أن يطلب ما ليس له {وأن تشركوا بالله} تعدلوا به في العبادة {ما لم ينزل به سلطاناً} لم ينزل كتاباً فيه حجَّةٌ {وَأَنْ تَقُولُوا عَلَى اللَّهِ مَا لا تَعْلَمُونَ} من أنَّه حرَّم الحرث والأنعام وأنَّ الملائكة بنات الله',NULL,'392',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (989,'{ولكلِّ أمة أجل} وقتٌ مضروبٌ لعذابهم وهلاكهم {فإذا جاء أجلهم} بالعذاب {لا يستأخرون ساعة ولا يستقدمون} لا يتأخَّرون ولا يتقدَّمون حتى يُعذَّبوا',NULL,'392',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (990,'{يَا بَنِي آدَمَ إِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يقصون عليكم آياتي} فرائضي وأحكامي {فمن اتقى} اتَّقاني وخافني {وأصلح} ما بيني وبينه {فَلا خوف عليهم} إذا خاف الخلق في القيامة {ولا هم يحزنون} إذا حزنوا',NULL,'392',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (991,'{وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا أُولَئِكَ أَصْحَابُ النَّارِ هُمْ فِيهَا خَالِدُونَ}',NULL,'392',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (992,'{فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا} فجعل له ولداًَ أو شريكاً {أولئك ينالهم نصيبهم من الكتاب} ما كُتب لهم من العذاب وهو سواد الوجه وزرقة العيون {حتى إذا جاءتهم رسلنا يتوفونهم} يريد: الملائكة يقبضون أرواحهم {قَالُوا أَيْنَ مَا كُنْتُمْ تدعون من دون الله} ؟ سؤال توبيخ وتبكيت وتقريع {قالوا ضلوا عنا} بطلوا وذهبوا {وَشَهِدُوا عَلَى أَنْفُسِهِمْ أَنَّهُمْ كَانُوا كافرين} اعترفوا عند مُعاينة الموت وأقروا على أنفسهم بالكفر',NULL,'393',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (993,'{قال ادخلوا} أَيْ: قال الله تعالى لهم: ادخلوا النَّار {في أمم} أَيْ: مع {أمم قد خلت من قبلكم} {كلما دخلت أمة} النَّار {لعنت أختها} يعني: الأمم التي سبقتها إلى النَّار لأنَّهم ضلوا باتِّباعهم {حتى إذا ادَّاركوا فيها} أَيْ: تداركوا وتلاحقوا جميعاً في النَّار {قالت أخراهم} أَيْ: أُخراهم دخولاً إلى النَّار {لأولاهم} دخولاً يعني: قالت الأتباع للقادة: {ربنا هؤلاء أضلونا} لأَنَّهم شرعوا لنا أن نتَّخذ من دونك إلهاً {فآتهم عذاباً ضعفاً} أَضْعِفْ عليهم العذاب بأشدَّ ممَّا تعذِّبنا به {قال} الله تعالى: {لكلٍّ ضعف} للتَّابع والمتبوع عذابٌ مضاعفٌ {ولكن لا تعلمون} يا أهل الكتاب في الدًّنيا مقدار ذلك وقوله:',NULL,'393',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (994,'{فما كان لكم علينا من فضل} لأنَّكم كفرتم كما كفرنا فنحن وأنتم في الكفر سواءٌ',NULL,'393',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (995,'{إنَّ الذين كذبوا بآياتنا} بحججنا التي تدلُّ على توحيد الله ونبوَّة الأنبياء {واستكبروا عنها} ترفَّعوا عن الإِيمان بها والانقياد لأحكامها {لا تفتح لهم أبواب السماء} لا تصعد أرواحهم ولا أعمالهم ولا شيء ممَّا يريدون الله به إلى السَّماء {ولا يدخلون الجنة حتى يلج} يدخل {الجمل في سم الخياط} ثقب الإِبرة يعني: أبدأً {وكذلك} وكما وصفنا {نجزي المجرمين} أَي: المكذِّبين بآيات الله ثمَّ أخبر عن إحاطة النَّار بهم من كلِّ جانبٍ فقال:',NULL,'394',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (996,'{لَهُمْ مِنْ جَهَنَّمَ مِهَادٌ وَمِنْ فَوْقِهِمْ غَوَاشٍ} يعني: لهم منها غطاءٌ ووطاءٌ وفراشٌ ولحافٌ {وَكَذَلِكَ نَجْزِي الظالمين} يعني: الذين أشركوا بالله',NULL,'394',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (997,'{وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لا نُكَلِّفُ نَفْسًا إلا وسعها} أَيْ: إلاَّ ما تطيقه ولا تعجز عنه والمعنى: لا نكلِّف نفساً منهم إلاَّ وسعها ثمَّ أخبر بباقي الآية عن مآلهم',NULL,'394',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (998,'{ونزعنا ما في صدورهم من غل} أذهبنا الأحقاد التي كانت لبعضهم على بعض في دار الدُّنيا {تجري من تحتهم الأنهار} من تحت منازلهم وقصورهم فإذا استقرُّوا في منازلهم {قالوا الْحَمْدُ لِلَّهِ الَّذِي هَدَانَا لهذا} أَيْ: هدانا لما صيرَّنا إلى هذا الثَّواب من العمل الذي أدَّى إليه وأقرُّوا أنَّ المهتدي مَنْ هدى الله بقوله: {وَمَا كُنَّا لِنَهْتَدِيَ لَوْلا أَنْ هَدَانَا اللَّهُ} فحين رأوا ما وعدهم الرُّسل عياناً قالوا: {لقد جاءت رسل ربنا بالحق ونودوا أن تلكُم الْجَنَّةُ} قيل لهم: هذه تلكمُ الجنَّة التي وُعدتم {أورثتموها} أُورثتم منازل أهل النَّار فيها لو عملوا بطاعة الله {بما كنتم تعملون} توحِّدون الله وتطيعونه',NULL,'394',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (999,'{وَنَادَى أَصْحَابُ الْجَنَّةِ أَصْحَابَ النَّارِ أَنْ قَدْ وجدنا ما وعدنا ربنا} في الدُّنيا من الثَّواب {حقاً فَهَلْ وَجَدْتُمْ مَا وَعَدَ رَبُّكُمْ} من العذاب {حقاً} وهذا سؤال تعييرٍ وتقريرٍ فأجاب أهل النَّار و {قالوا نعم فأذَّن مؤذنٍ بينهم} نادى منادٍ وسطهم نداءً يُسمع الفريقين وهو صاحب الصُّور {أَنْ لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ}',NULL,'395',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1000,'{الذين يصدون} يمنعون {عن سبيل الله} دين الله وطاعته {ويبغونها عوجاً} ويطلبونها بالصَّلاة لغير الله وتعظيم ما لم يعظِّمه الله',NULL,'395',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1001,'{وبينهما} بين أهل الجنَّة وبين أهل النَّار {حجاب} حاجزٌ وهو سور الأعراف {وعلى الأعراف} يريد: سور الجنَّة {رجال} وهم الذين استوت حسناتهم وسيئاتهم {يعرفون كلاً بسيماهم} يعرفون أهل الجنَّة ببياض الوجوه وأهل النَّار بسوادها وذلك لأنَّ موضعهم عالٍ مرتفع فهم يرون الفريقين {وَنَادَوْا أَصْحَابَ الْجَنَّةِ أن سلام عليكم} إذا نظروا إلى الجنَّة سلَّموا على أهلها {لم يدخلوها} يعني: أصحاب الأعراف لم يدخلوا الجنَّة {وهم يطمعون} في دخولها',NULL,'395',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1002,'{وإذا صرفت أبصارهم تلقاء أصحاب النار} أَيْ: جهة لقائهم',NULL,'395',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1003,'{ونادى أصحاب الأعراف رجالاً} من أهل النَّار {يعرفونهم بسيماهم} من رؤساء المشركين فيقولون لهم: {مَا أَغْنَى عَنْكُمْ جمعكم} المال واستكثارهم منه {وما كنتم تستكبرون} عن عبادة الله ثمَّ يقسم أصحاب النَّار أنَّ أصحاب الأعراف داخلون معهم النَّار فتقول الملائكة الذين حبسوا أصحاب الأعراف:',NULL,'396',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1004,'{أهؤلاء الذين أقسمتم} يا أصحاب النَّار {لا ينالهم الله برحمة} يقولون لأصحاب الأعراف: {ادْخُلُوا الْجَنَّةَ لا خَوْفٌ عَلَيْكُمْ ولا أنتم تحزنون}',NULL,'396',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1005,'{وَنَادَى أَصْحَابُ النَّارِ أَصْحَابَ الْجَنَّةِ أَنْ أَفِيضُوا عَلَيْنَا مِنَ الْمَاءِ أَوْ مِمَّا رَزَقَكُمُ اللَّهُ} يعني: الطَّعام وهذا يدلُّ على جوعهم وعطشهم {قالوا إنَّ الله حرمهما على الكافرين} تحريم منع لا تحريم تعبُّدٍ',NULL,'396',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1006,'{الذين اتخذوا دينهم} الذي شُرع لهم {لهواً ولعباً} يعني: المستهزئين المُقتسمين {فاليوم ننساهم} نتركهم في جهنَّم {كَمَا نَسُوا لِقَاءَ يَوْمِهِمْ هذا} كما تركوا العمل لهذا اليوم {وما كانوا بآياتنا يجحدون} أَيْ: وكما جحدوا بآياتنا ولم يُصدِّقوها',NULL,'396',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1007,'{ولقد جئناهم} يعني: المشركين {بكتاب} هو القرآن {فصلناه} بيّناه {على علم} فيه يعني: ما أُودع من العلوم وبيان الأحكام {هدى} هادياً {ورحمة} وذا رحمةٍ {لقوم يؤمنون} لقومٍ أريد به هدايتهم وإيمانهم',NULL,'396',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1008,'{هل ينظرون} ينتظرون أَيْ: كأنَّهم ينتظرون ذلك لأنَّه يأتيهم لا محالة {إلاَّ تأويله} عاقبة ما وعد الله في الكتاب من البعث والنُّشور {يوم يأتي تأويله} وهو يوم القيامة {يَقُولُ الَّذِينَ نَسُوهُ مِنْ قَبْلُ} تركوا الإِيمان به والعمل له من قبل إتيانه: {قد جاءت رسل ربنا بالحق} بالصِّدق والبيان {فهل لنا من شفعاء} هل يشفع لنا شافعٌ؟ {أو} هل {نردُّ} إلى الدُّنيا {فَنَعْمَلَ غَيْرَ الذي كنا نعمل} نوحِّد الله ونترك الشِّرك يقول الله: {قد خسروا أنفسهم} حين صاروا إلى الهلاك {وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ} سقط عنهم ما كانوا يقولونه مِنْ أنَّ مع الله إلهاً آخر',NULL,'396',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1009,'{إنَّ ربكم الله الذي خلق السماوات والأرض في ستة أيام} أَيْ: في مقدار ستة أيَّامٍ من الأحد إلى السَّبت واجتمع الخلق في الجمعة {ثم استوى على العرش} أقبل على خلقه وقصد إلى ذلك بعد خلق السماوات والأرض {يغشي الليل النهار} يُلبسه ويُدخله عليه {يطلبه حثيثاً} يطلب اللَّيل دائباً لا غفلة له {والشمس} وخلق الشَّمس {والقمر والنجوم مسخرات} مُذلَّلاتٍ لما يُراد منها من طلوعٍ وأُفولٍ وسيرٍ ورجوعٍ {بأمره} بإذنه {ألا له الخلق} يعني: إنَّ جميع ما في العالم مخلوق له {و} له {الأمر} فيهم يأمر بما أراد {تبارك الله} تمجد وارتفع وتعالى',NULL,'397',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1010,'{ادعوا ربكم تضرُّعاً} أَيْ: تملُّقاً {وخفية} سرَّاً {إنه لا يحب المعتدين} المجاوزين ما أُمروا به',NULL,'397',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1011,'{ولا تفسدوا في الأرض} بالشِّرك والمعاصي وسفك الدِّماء {بعد} إصلاح الله إياها ببعث الرَّسول {وادعوه خوفاً} من عقابه {وطمعا} في ثوابه {إِنَّ رحمة الله} ثوابَ الله {قريب من المحسنين} وهم الذين يطيعون الله فيما أمر',NULL,'398',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1012,'{وهو الذي يرسل الرياح بشرا} طيِّبة ليِّنة من النَّشر وهو الرَّائحة الطَّيِّبة وقيل: مُتفرِّقةً في كلِّ جانبٍ بمعنى المنتشرة {بين يدي رحمته} قدَّام مطره {حَتَّى إِذَا أَقَلَّتْ} أَيْ: حملت هذه الرِّياح {سحاباً ثقالاً} بما فيها من الماء سُقنا السَّحاب {لبلد ميت} إلى مكان ليس فيه نباتٌ {فأنزلنا به} بذلك البلد {الماء فأخرجنا} بذلك الماء {مِنْ كُلِّ الثَّمَرَاتِ كَذَلِكَ نُخْرِجُ الموتى} أَيْ: نحيي الموتى مثل ذلك الإِحياء الذي وصفناه في البلد الميت {لعلكم تذكرون} لعلَّكم بما بيَّنا تتَّعظون فتستدلُّون على توحيد الله وقدرته على البعث ثمَّ ضرب مثلاً للمؤمن والكافر فقال:',NULL,'398',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1013,'{والبلد الطيب} يعني: العذبُ التُّراب {يخرج نباته بإذن ربه} وهذا مثل المؤمن يسمع القرآن فينتفع به ويحسن أثره غليه {والذي خبث} ترابه وأصله {لا يخرج} نباته {إلاَّ نكداً} عسراً مُبطئاً وهو مثل الكافر يسمع القرآن ولا يُؤثِّر فيه أثراً محموداً كالبلد الخبيث لا يُؤثِّر فيه المطر {كذلك نصرِّف الآيات} نبيِّنها {لقوم يشكرون} نِعَمَ الله ويطيعونه',NULL,'398',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1014,'{ولقد أرسلنا نوحاً إلى قومه} ظاهرٌ إلى قوله:',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1015,'{قَالَ الْمَلأُ مِنْ قَوْمِهِ إِنَّا لَنَرَاكَ فِي ضلال مبين}',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1016,'{قَالَ يَا قَوْمِ لَيْسَ بِي ضَلالَةٌ وَلَكِنِّي رسول من رب العالمين}',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1017,'{وأنصح لكم} أَيْ: أدعوكم إلى ما دعاني الله إليه {وأعلم من الله ما لا تعلمون} من أنَّه غفورٌ لمَنْ رجع عن معاصيه وأنَّ عذابه أليمٌ لمن أصرَّ عليها',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1018,'{أوعجبتم أن جاءكم ذكر من ربكم} موعظةٌ من الله {على رجل} على لسان رجل {منكم} تعرفون نسبه وقوله:',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1019,'{إنهم كانوا قوماً عمين} عميت قلوبهم عن معرفة الله تعالى وقدرته',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1020,'{وإلى عاد أخاهم} وأرسلنا إلى عادٍ أخاهم ابن أبيهم {هوداً قال يا قوم اعبدوا الله} وحِّدوا اللَّهَ {مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ أَفَلا تتقون} أفلا تخافون نقمته',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1021,'{قال الملأ} الرُّؤساء والجماعة {الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ إِنَّا لَنَرَاكَ فِي سفاهة} حمقٍ وجهلٍ {وإنا لنظنك من الكاذبين} فيما جئت به من ادِّعاء النُّبوَّة وقوله:',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1022,'{قَالَ يَا قَوْمِ لَيْسَ بِي سَفَاهَةٌ وَلَكِنِّي رسول من رب العالمين}',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1023,'{ناصح أمين} أَيْ: على الرِّسالة لا أكذب فيها',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1024,'{وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِنْ بَعْدِ قَوْمِ نوحٍ} أَيْ: استخلفكم في الأرض بعد هلاكهم {وزادكم في الخلق بسطة} فضيلةً في الطُّول {فاذكروا آلاء الله} نِعَمَ الله عليكم {لعلكم تفلحون} كي تسعدوا وتبقوا في الجنَّة وقوله:',NULL,'399',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1025,'{فأتنا بما تعدنا} أَيْ: من العذاب {إِنْ كُنْتَ مِنَ الصَّادِقِينَ} أنَّ العذاب نازلٌ بنا',NULL,'400',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1026,'{قال: قد وقع} وجب {عليكم من ربكم رجس وغضب} عذابٌ وسخطٌ {أتجادلونني في أسماء سَمَّيْتُموها} كانت لهم أصنام سمَّوها أسماءً مختلفةً فلمَّا دعاهم الرَّسول إلى التَّوحيد استنكروا عبادة الله وحده {ما نَزَّلَ الله بها من سلطان} من حجةٍ وبرهانٍ لكم في عبادتها {فَانْتَظِرُوا} العذاب {إني معكم من المنتظرين} ذلك في تكذيبهم إياي وقوله:',NULL,'400',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1027,'{فَأَنْجَيْنَاهُ وَالَّذِينَ مَعَهُ بِرَحْمَةٍ مِنَّا وَقَطَعْنَا دَابِرَ الذين كذبوا بآياتنا وما كانوا مؤمنين}',NULL,'400',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1028,'{فذروها تأكل في أرض الله} أَيْ: سهَّل الله عليكم أمرها فليس عليكم رزقها ولا مؤونتها وقوله:',NULL,'400',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1029,'{وبوَّأكم في الأرض} أَيْ: أسكنكم وجعل لكم فيها مساكن {تتخذون من سهولها قصوراً} تبنون القصور بكلِّ موضعٍ {وَتَنْحِتُونَ الْجِبَالَ بُيُوتًا} يريد: بيوتاً في الجبال تُشققونها وكانوا يسكنونها شتاءً ويسكنون القصور بالصَّيف',NULL,'400',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1030,'{قال الملأ} وهم الأشراف {الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ} عن عبادة الله {للذين استضعفوا} يريد المساكين {لِمَنْ آمَنَ مِنْهُمْ} بدلٌ من قوله: {للذين استضعفوا} لأنَّهم المؤمنون',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1031,'{قَالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا بِالَّذِي آمَنْتُمْ بِهِ كافرون}',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1032,'{فعقروا الناقة} نحروها {وعتوا عن أمر ربهم} عصوا الله وتركوا أمره في النَّاقة {وقالوا يَا صَالِحُ ائْتِنَا بِمَا تَعِدُنَا} من العذاب',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1033,'{فأخذتهم الرجفة} وهي الزَّلزلة الشَّديدة {فأصبحوا في دارهم} بلدهم {جاثمين} خامدين مَيِّتين',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1034,'{فتولى عنهم} أعرض عنهم صالحٌ بعد نزول العذاب بهم {وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَةَ رَبِّي ونصحت لكم} خوَّفتكم عقاب الله وهذا كما خاطب رسول الله صلى الله عليه وسلم قتلى بدر',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1035,'{ولوطاً} وأرسلنا لوطاً أَيْ: واذكر لوطاً {إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ} يعني: إتيان الذُّكور {مَا سَبَقَكُمْ بِهَا مِنْ أَحَدٍ مِنَ الْعَالَمِينَ} قالوا: ما نزا ذَكَرٌ على ذَكَرٍ حتى كان قوم لوطٍ',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1036,'{إنكم لتأتون الرجال} الآية',NULL,'401',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1037,'{وَمَا كَانَ جَوَابَ قَوْمِهِ إِلا أَنْ قَالُوا أخرجوهم من قريتكم} يعني: لوطاً وأتباعه {إنهم أناس يتطهرون} عن إتيان الرِّجال في أدبارهم',NULL,'402',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1038,'{فأنجيناه وأهله} ابنتيه {إلاَّ امرأته كَانَتْ مِنَ الْغَابِرِينَ} الباقين في عذاب الله',NULL,'402',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1039,'{وأمطرنا عليهم مطراً} يعني: حجارةً',NULL,'402',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1040,'{وإلى مدين} وهم قبيلةٌ من ولد إبراهيم عليه السَّلام {قد جاءتكم بينة من ربكم} موعظةٌ {فأوفوا الكيل والميزان} أَتِمُّوهُمَا وكانوا أهلَ كفرٍ وبخسٍ للمكيال والميزان {ولا تفسدوا في الأرض} لا تعملوا فيها بالمعاصي بعد أن أصلحها الله ببعثه شعيبٍ والأمر بالعدل',NULL,'402',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1041,'{ولا تقعدوا بكلِّ صراط توعدون} لا تقعدوا على طريق النَّاس فتخوِّفون أهل الإِيمان بشعيبٍ بالقتل ونحو ذلك وتأخذون ثياب من مرَّ بكم من الغرباء {وَتَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ بِهِ} وتصرفون عن الإسلام مَنْ آمن بشعيب {وتبغونها عوجاً} تلتمسون لها الزَّيغ {واذكروا إذْ كنتم قليلاً فكثَّركم} بعد القلَّة وأعزَّكم بعد الذِّلة وذلك أنَّه كان مدين بن إبراهيم وزوجه ريثا بنت لوط فولدت حتى كثر عدد أولادها',NULL,'402',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1042,'{وَإِنْ كَانَ طَائِفَةٌ مِنْكُمْ آمَنُوا بِالَّذِي أُرْسِلْتُ بِهِ وَطَائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتَّى يَحْكُمَ اللَّهُ بَيْنَنَا وَهُوَ خَيْرُ الْحَاكِمِينَ}',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1043,'{قَالَ الْمَلأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لَنُخْرِجَنَّكَ يَا شُعَيْبُ وَالَّذِينَ آمَنُوا مَعَكَ مِنْ قَرْيَتِنَا او لتعودنَّ في ملتنا} معناه أنَّهم قالوا لشعيب وأصحابه: ليكوننَّ أحد الأمرين إمَّا الإِخراج من القرية أو عودكم في ملَّتنا ولا نفارقكم على مخالفتنا فقال شعيب: {أَوَ لَوْ كُنَّا كارهين} أَيْ: تجبروننا على العود في ملَّتكم وإنْ كرهنا ذلك؟ وقوله:',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1044,'{وما يكون لنا أن نعود فيها إلاَّ أن يشاء الله ربنا} أَيْ: إلاَّ أن يكون قد سبق في علم الله وفي مشيئته أن نعود فيها {وَسِعَ ربنا كلَّ شيء علماً} علم ما يكون قبل أن يكون {ربنا افتح} احكم واقضِ {بيننا وبين قومنا بالحق} وقوله:',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1045,'{وَقَالَ الْمَلأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ لَئِنِ اتَّبَعْتُمْ شُعَيْبًا إِنَّكُمْ إِذًا لَخَاسِرُونَ}',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1046,'{فأخذتهم الرجفة فأصبحوا في دارهم جاثمين}',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1047,'{كأن لم يغنوا فيها} أَيْ: لم يقيموا فيها ولم ينزلوا وقوله:',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1048,'{فكيف آسى على قوم كافرين} أَيْ: كيف يشتدُّ حزني عليهم ومعناه: الإِنكار أَيْ: لا آسى',NULL,'403',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1049,'{وما أرسلنا في قرية} في مدينةٍ {من نبي} فكذَّبه أهلها {إلاَّ أخذنا} هم {بالبأساء والضراء} بالفقر والجوع {لعلهم يضَّرَّعون} كي يستكينوا ويرجعوا',NULL,'404',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1050,'{ثمَّ بدلنا مكان السيئة الحسنة} بدل البؤسِ والمرضِ الغنى والصحَّة {حتى عفوا} كثروا وسمنوا وسمنت أموالهم {وقالوا} من غِرَّتهم وجهلهم: {قد مسَّ آباءنا الضراء والسراء} قد أصاب آباءنا في الدَّهر مثل ما أصابنا وتلك عادة الدَّهر ولم يكن ما مسَّنا عقوبة من الله فكونوا على ما أنتم عليه فلمَّا فسدوا على الأمرين جميعاً أخذهم الله بغتة {وهم لا يشعرون} بنزول العذاب وهذا تخويفٌ لمشركي قريش',NULL,'404',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1051,'{ولو أنَّ أهل القرى آمنوا} وحَّدوا الله {واتقوا} الشِّرك {لَفَتَحْنَا عَلَيْهِمْ بَرَكَاتٍ من السماء} بالمطر {و} من {الأرض} بالنَّبات والثِّمار {ولكن كذبوا} الرسلَ {فأخذناهم} بالجدوبة والقحط {بِمَا كانوا يكسبون} من الكفر والمعصية',NULL,'404',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1052,'{أفأمن أهل القرى} يعني: أهل مكَّة وما حولها ومعنى هذه الآية وما بعدها: أنَّه لا يجوز لهم أن يأمنوا ليلاً ولا نهاراً بعد تكذيب محمد صلى الله عليه وسلم وقوله:',NULL,'404',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1053,'{وهم يلعبون} أَيْ: وهم في غير ما يُجدي عليهم',NULL,'404',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1054,'{أفأمنوا مكر الله} عذاب الله أن يأتيهم بغتةً',NULL,'404',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1055,'{أَوَلَمْ يهد} يتبيَّن {للذين يرثون الأرض من بعد أهلها} كفار مكَّة ومَنْ حولهم {أن لو نشاء أصبناهم} عذَّبناهم {بذنوبهم} ثمَّ {ونطبع على قلوبهم} حتى يموتوا على الكفر فيدخلوا النَّار والمعنى: ألم يعلموا أنَّا لو نشاء فعلنا ذلك',NULL,'400',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1056,'{تلك القرى} التي أهلكتُ أهلها {نقص عليك من أنبائها} نتلو عليك من أخبارها كيف أُهلكت {ولقد جاءتهم رسلهم بالبينات} يعني: الذين أُرسلوا إليهم {فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا مِنْ قَبْلُ} فما كان أولئك الكفار ليؤمنوا عندإرسال الرُّسل بما كذَّبوا يوم أخذ ميثاقهم فأقرُّوا بلسانهم وأضمروا التَّكذيب {كذلك} أَيْ: مثل ذلك الذي طبع الله على قلوب كفَّار الأمم {يطبع الله على قلوب الكافرين} الذين كتب عليهم ألاَّ يؤمنوا أبداً',NULL,'405',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1057,'{وما وجدنا لأكثرهم من عهد} يعني: الوفاء بالعهد الذي عاهدهم يوم الميثاق',NULL,'405',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1058,'{ثم بعثنا من بعدهم} الأنبياء الذين جرى ذكرهم {مُوسَى بِآيَاتِنَا إِلَى فِرْعَوْنَ وَمَلَئِهِ فَظَلَمُوا بِهَا} فجحدوا بها وكذَّبوا {فانظر} بعين قلبك {كيف كان} عاقبتهم وكيف فعلنا بهم وقوله:',NULL,'405',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1059,'{وَقَالَ مُوسَى يَا فِرْعَوْنُ إِنِّي رَسُولٌ مِنْ رب العالمين}',NULL,'405',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1060,'{حقيق على أن لا أقول} أَيْ: أنا حقيق بأن لا أقول {على الله إلاَّ} ما هو {الحق} وهو أنَّه واحدٌ لا شريك له {قد جئتكم ببينة من ربكم} أي: بأمرٍ من ربِّكم وهو العصا {فأرسل معي بني إسرائيل} أي: أطلق عليهم وخلِّهم وكان فرعون قد استخدمهم في الأعمال الشاقة وقوله:',NULL,'405',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1061,'{قَالَ إِنْ كُنْتَ جِئْتَ بِآيَةٍ فَأْتِ بِهَا إن كنت من الصادقين}',NULL,'405',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1062,'{فإذا هي} أَيْ: العصا {ثعبان} وهو أعظم ما يكون من الحيَّات {مبين} بيِّنٌ أنَّه حيَّة لا لبس فيه',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1063,'{ونزع يده} أخرجها من جيبه',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1064,'{قَالَ الْمَلأُ مِنْ قَوْمِ فِرْعَوْنَ إِنَّ هَذَا لَسَاحِرٌ عَلِيمٌ}',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1065,'{يريد أن يخرجكم من أرضكم} هذا قول الأشراف من قوم فرعون قالوا: يريد موسى أن يخرجكم معشرَ القبط من أرضكم ويزيل ملككم بتقوية عدوِّكم بني إسرائيل فقال فرعون لهم: {فماذا تأمرون} أَيش تُشيرون به عليَّ؟',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1066,'{قالوا أرجه وأخاه} أَخِّرْ أمره وأمر أخيه ولا تعجل {وأرسل في المدائن} في مدائن صعيد مصر {حاشرين} رجالاً يحشرون إليك مَنْ في الصَعيد من السَّحرة فأرسل {وجاء السحرة فرعون} وطالبوه بالمال والجوائز إنْ غلبوه فأجابهم فرعون إلى ذلك وهو قوله:',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1067,'{يَأْتُوكَ بِكُلِّ سَاحِرٍ عَلِيمٍ}',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1068,'{وَجَاءَ السَّحَرَةُ فِرْعَوْنَ قَالُوا إِنَّ لَنَا لَأَجْرًا إِنْ كُنَّا نَحْنُ الْغَالِبِينَ}',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1069,'{قال نعم وإنكم لمن المقربين} أَيْ: ولكم من الأجر المنزلة الرَّفيعة عندي',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1070,'{قالوا يا موسى إمَّا إن تلقي} عصاك {وإمَّا أن نكون نحن الملقين} ما معنا من الحبال والعصي',NULL,'406',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1071,'{قَالَ أَلْقُوا فَلَمَّا أَلْقَوْا سَحَرُوا أَعْيُنَ النَّاسِ واسترهبوهم} قلبوها عن صحَّة إدراكها حيث رأوها حيَّات {وجاؤوا بسحر عظيم} وذلك أنَّهم ألقوا حبالاً غلاظاً فإذا هي حيَّاتٌ قد ملأت الوادي',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1072,'{وَأَوْحَيْنَا إِلَى مُوسَى أَنْ أَلْقِ عَصَاكَ فَإِذَا هي تلقف} تبتلع {ما يأفكون} يكذبون فيه وذلك أنَّهم زعموا أنَّ عصيَّهم وحبالهم حَيّاتٍ وَكذبوا في ذلك',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1073,'{فوقع الحق} ظهر وغلب',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1074,'{فغلبوا هنالك وانقلبوا} وانصرفوا {صاغرين} ذليلين',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1075,'{وألقي السحرة ساجدين} خرُّوا لله عابدين سامعين مطيعين',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1076,'{قَالُوا آمَنَّا بِرِبِّ الْعَالَمِينَ}',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1077,'{رَبِّ مُوسَى وَهَارُونَ}',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1078,'{قَالَ فِرْعَوْنُ آمَنْتُمْ بِهِ قَبْلَ أَنْ آذَنَ لَكُمْ} أصدَّقتم موسى من قبل أمري إيَّاكم؟ ! {إنَّ هذا لمكر مكرتموه في المدينة} لصنيع صنعتموه فيما بينكم وبين موسى في مصر قبل خروجكم إلى هذا الموضع {لتخرجوا منها أهلها} لتستولوا على مصر فتخرجوا منها أهلها وتتغلبوا عليها بسحركم {فسوف تعلمون} ما يظهر لكم',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1079,'{لأقطعن أيديكم وأرجلكم من خلاف} على مخالفة وهو أن يقطع من كلِّ شقٍّ طرف',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1080,'{قالوا إنا إلى ربنا منقلبون} راجعون بالتَّوحيد والإِخلاص',NULL,'407',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1081,'{وما تنقم منا} وما تطعن علينا ولا تكره منَّا {إلاَّ أن آمنا بآيات ربنا} ما أتى به موسى من العصا واليد {ربنا أفرغ علينا صبراً} اصبب علينا الصَّبر عند الصَّلب والقطع حتى لا نرجع كفَّاراً {وتوفنا مسلمين} على دين موسى ثمَّ أغرى الملأ من قوم فرعون بموسى فقالوا:',NULL,'408',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1082,'{أتذر موسى وقومه ليفسدوا في الأرض} ليدعوا النَّاس إلى مخالفتك وعبادة غيرك {ويذرك وآلهتك} وذلك أنَّ فرعون كان قد صنع لقومه أصناماً صغاراً وأمرهم بعبادتها وقال: أنا ربُّكم ورب هذه الأصنام فلذلك قوله: {أنا ربُّكم الأعلى} فقال فرعون: {سنقتل أَبْناءَهم} وكان قد ترك قتل أبناء بني إسرائيل فلمَّا كان من أمر موسى ما كان أعاد عليهم القتل فذلك قوله: {سنقتل أبناءهم ونَسْتَحْيِيَ نساءهم} للمهنة والخدمة {وَإِنَّا فَوْقَهُمْ قَاهِرُونَ} وإنَّا على ذلك قادرون فشكا بنو إسرائيل إلى موسى إعادة القتل على أبنائهم فقال لهم موسى:',NULL,'408',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1083,'{اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا} على ما يُفعل بكم {إِنَّ الأَرْضَ لِلَّهِ يُورِثُهَا مَنْ يَشَاءُ مِنْ عباده} أطمعهم موسى أن يعطيهم الله ملكهم ومالهم {والعاقبة للمتقين} أَيْ: الجنَّة لمن اتَّقى وقيل النَّصر والظَّفر',NULL,'408',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1084,'{قالوا أوذينا} بالقتل الأوَّل {مِنْ قَبْلِ أَنْ تَأْتِيَنَا} بالرِّسالة {وَمِنْ بَعْدِ مَا جِئْتَنَا} بإعادة القتل علينا والإِتعاب في العمل {قَالَ عَسَى رَبُّكُمْ أَنْ يُهْلِكَ عَدُوَّكُمْ} فرعون وقومه {وَيَسْتَخْلِفَكُمْ فِي الأَرْضِ} يُملِّككم ما كان يملك فرعون {فينظر كيف تعملون} فيرى ذلك لوقوع ذلك منكم',NULL,'408',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1085,'{ولقد آخذنا آل فرعون بالسنين} بالجدوب لأهل البوادي {وَنَقْصٍ مِنَ الثَّمَرَاتِ} لأهل القرى وصرَّفنا الآيات: بيَّناها لهم من كلِّ نوعٍ {لعلهم يذكرون} كي يتَّعظوا',NULL,'409',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1086,'{فإذا جاءتهم الحسنة} الخصب وسعة الرِّزق {قَالُوا لَنَا هَذِهِ} أَيْ: إنَّا مستحقُّوه على العادة التي جرت لنا من النِّعمة ولم يعلموا أنَّه من الله فيشكروا عليه {وإن تصبهم سيئة} قحط وجدب {يطيروا} يتشاءموا {بموسى} وقومه وقالوا: إنَّما أصابنا هذا الشرُّ بشؤمهم {ألا إنَّما طائرهم عند الله} شؤمهم جاءهم بكفرهم بالله {وَلَكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ} أنَّ الذي أصابهم من الله',NULL,'409',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1087,'{وقالوا} بموسى: {مهما تأتنا به} أَيْ: متى ما تَأْتِنَا بِهِ {مِنْ آيَةٍ لِتَسْحَرَنَا بِهَا فَمَا نحن لك بمؤمنين} فدعا عليهم موسى فأرسل الله عليهم السَّماء بالماء حتى امتلأت بيوت القِبْطِ ماءً ولم يدخل بيوت بني إسرائيل من الماء قطرة فذلك قوله:',NULL,'409',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1088,'{فأرسلنا عليهم الطوفان} ودام ذلك سبعة أيَّام فقالوا: {يَا مُوسَى ادْعُ لَنَا رَبَّكَ} يكشف عنا فنؤمن لك فدعا ربَّه فكشف فلم يؤمنوا فبعث الله عليهم الجراد فأكلت عامَّة زروعهم وثمارهم فوعدوه أن يؤمنوا إن كشف عنهم فكشف فلم يؤمنوا فبعث الله عليهم القمَّل وهو الدّباء الصِّغار البق التي لا أجنحة لها فتتبَّعَ ما بقي من حروثهم وأشجارهم فصرخوا فكشف عنهم فلم يؤمنوا فعادوا بكفرهم فأرسل الله عليهم الضَّفادع تدخل في طعامهم وشرابهم فعاهدوا موسى أن يؤمنوا فكشف عنهم فعادوا لكفرهم فأرسل الله عليهم الدَّم فسال النِّيل عليهم دماً وصارت مياههم كلُّها دماً فذلك قوله: {آيات مفصلات} مبيَّنات {فاستكبروا} عن عبادة الله',NULL,'409',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1089,'{ولما وقع عليهم الرجز} أَيْ: العذاب وهو ما كانوا فيه من الجراد وما ذكر بعده {قَالُوا يَا مُوسَى ادع لنا ربك بما عهد عندك} بما أوصاك به وتقدَّم إليك أن تدعوه به {لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لنؤمننَّ لك ولنرسلنَّ معك بني إسرائيل} وقوله:',NULL,'410',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1090,'{إلى أجل هم بالغوه} يعني: إلى الأجل الذي غَرَّقهم فيه {إذا هم ينكثون} ينقضون العهد ولا يوفون',NULL,'410',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1091,'{فانتقمنا منهم} سلبنا نعمتهم بالعذاب {فأغرقناهم في اليم} في البحر {بأنهم كذبوا بآياتنا} جزاء تكذيبهم {وكانوا عنها غافلين} غير معتبرين بها',NULL,'410',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1092,'{وأورثنا القوم} ملَّكناهم {الذين كانوا يستضعفون} بقتل أبنائهم واستخدام نسائهم {مشارق الأرض ومغاربها} جهات شرق أرض الشَّام وجهات غربها {التي باركنا فيها} بإخراج الزُّروع والثِّمار والأنهار والعيون {وتمت كلمة ربك الحسنى} مواعيده التي لا خلف فيها بما كانوا يحبُّون وذلك جزاء صبرهم على صنيع فرعون {ودمرنا ما كان يصنع فرعون وقومه} أهلكنا ما عمل فرعون وقومه في أرض مصر {وما كانوا يعرشون} وما بنوا المنازل والبيوت',NULL,'410',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1093,'{وجاوزنا ببني إسرائيل البحر} عبرنا بهم الْبَحْرَ {فَأَتَوْا عَلَى قَوْمٍ يَعْكُفُونَ عَلَى أَصْنَامٍ لهم} يعبدونها مقيمين عليها {قالوا يا موسى اجعل لنا إلهاً} من دون الله {كَمَا لَهُمْ آلِهَةٌ قَالَ إِنَّكُمْ قَوْمٌ تَجْهَلُونَ} نعمة الله عليكم وما صنع بكم حيث توهَّمتم أنَّه يجوز عبادة غيره',NULL,'411',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1094,'{إنَّ هؤلاء} يعني: الذين عكفوا على أصنامهم {متبَّرٌ ما هم فيه} مهلَكٌ ومدَمَّرٌ {وباطل ما كانوا يعملون} يعني: إنَّ عملهم للشَّيطان ليس لله فيه نصيبٌ',NULL,'411',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1095,'{قال أَغَيْرَ اللَّهِ أَبْغِيكُمْ} أطلب لكم {إِلَهًا} معبوداً {وهو فضلكم على العالمين} على عالمي زمانكم بما أعطاكم من الكرامات',NULL,'411',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1096,'{وَإِذْ أَنْجَيْنَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ يُقَتِّلُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ وَفِي ذَلِكُمْ بَلاءٌ مِنْ ربكم عظيم}',NULL,'411',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1097,'{وواعدنا موسى ثلاثين ليلة} يترقَّب انقضاءها للمناجاة وهي ذو القعدة أمره الله تعالى أن يصوم فيها فلمَّا انسلخ الشَّهر استاك لمناجاة ربِّه يريد إزالة الخلوف فأُمر بصيام عشرةٍ من ذي الحجَّة ليكلِّمه بخُلوفِ فيه فذلك قوله: {وَأَتْمَمْنَاهَا بِعَشْرٍ فَتَمَّ مِيقَاتُ رَبِّهِ} أَيْ: الوقت الذي قدَّره الله لصوم موسى {أربعين ليلة} فلمَّا أراد الانطلاق إلى الجبل استخلف أخاه هارون على قومه وهو معنى قوله: {وَقَالَ مُوسَى لأَخِيهِ هَارُونَ اخْلُفْنِي فِي قَوْمِي وأصلح} أَيْ: وارفق بهم {ولا تتبع سبيل المفسدين} لا تطع مَنْ عصى الله ولا توافقه على أمره',NULL,'411',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1098,'{وَلَمَّا جَاءَ مُوسَى لِمِيقَاتِنَا} أَيْ: في الوقت الذي وقَّتنا له {وكلمه ربُّه} فلمَّا سمع كلام الله {قال ربِّ أرني} نفسك {أنظر إليك} والمعنى: إنَّي قد سمعتُ كلامك فأنا أحبُّ أن أراك {قال لن تراني} في الدُّنيا {ولكن} اجعل بيني وبينك ما هو أقوى منك وهو الجبل {فَإِنِ اسْتَقَرَّ مَكَانَهُ} أَيْ: سكن وثبت {فسوف تراني} وإن لم يستقرَّ مكانه فإنَّك لا تطيق رؤيتي كما أنَّ الجبل لا يطيق رؤيتي {فَلَمَّا تَجَلَّى رَبُّهُ} أَيْ: ظهر وبان {جعله دكاً} أَيْ: مدقوقاً مع الأرض كِسَراً تراباً {وخرَّ} وسقط {موسى صعقاً} مغشياً عليه {فلما أفاق قال سبحانك} تنزيهاً لك من السُّوء {تبتُ إليك} من مسألتي الرُّؤية في الدُّنيا {وأنا أول المؤمنين} أوَّل قومي إيماناً',NULL,'412',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1099,'{قال يا موسى إني اصطفيتك} اتَّخذتك صفوةً {على الناس برسالاتي} أَيْ: بوحيي إليك {وبكلامي} كلَّمتك من غير واسطة {فخذ ما آتيتك} من الشَّرف والفضيلة {وكن من الشاكرين} لأنعمي في الدنيا والآخرة',NULL,'412',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1100,'{وكتبنا له في الألواح} يعني: ألواح التَّوراة {من كل شيء} يحتاج إليه في أمر دينه {موعظة} نهياً عن الجهل {وَتَفْصِيلا لِكُلِّ شَيْءٍ} من الحلال والحرام {فخذها} أَيْ: وقلنا له: فخذها {بقوة} بجدٍّ وصحِّةٍ وعزيمةٍ {وأمر قومك} أن {يأخذوا بأحسنها} أَيْ: بحسنها وكلُّها حسن {سأريكم دار الفاسقين} يعني: جهنَّم أَيْ: ولتكن على ذكرٍ منكم لتحذروا أن تكونوا منهم',NULL,'412',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1101,'{سأصرف عن آياتي} يعني: السماوات والأرض أصرفهم عن الاعتبار بما فيها {الذين يتكبرون في الأرض بغير الحق} يعني: المشركين يقول: أعاقبهم بحرمان الهداية {وإن يروا سبيل الرشد} الهدى والبيان الذي حاء من الله {لا يتخذوه سبيلاً} ديناً {وإن يروا سبيل الغي} طاعة الشَّيطان {يتخذوه سبيلاً} ديناً {ذلك} فعل الله بهم {بأنهم كذبوا بآياتنا} جحدوا الإِيمان بها {وكانوا عنها غافلين} غير ناظرين فيها ولا معتبرين بها',NULL,'413',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1102,'{والذين كذبوا بآياتنا ولقاء الآخرة} يريد: الثَّواب والعقاب {حبطت أعمالهم} ضلَّ سعيهم {هل يجزون إلاَّ ما} أَيْ: جزاء ما {كانوا يعملون}',NULL,'413',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1103,'{واتخذ قوم موسى مِنْ بَعْدِهِ} أَيْ: من بعد انطلاقه إلى الجبل {من حليِّهم} التي بقيت في أيديهم ممَّا استعاروه من القبط {عجلاً جسداً} لحماً ودما {له خوار} صوت {الم يروا} يعني: قوم موسى {أنه} أنَّ العجل {لا يكلَِّمهم ولا يهديهم سبيلاً} لا يرشدهم إلى دينٍ {اتخذوه} أَيْ: إلهاً ومعبوداً {وكانوا ظالمين} مشركين',NULL,'413',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1104,'{ولما سُقط في أيديهم} أَيْ: ندموا على عبادتهم العجل {ورأوا أنهم قد ضلوا} قد ابتلوا بمعصية الله وهذا كان بعد رجوع موسى إليهم',NULL,'414',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1105,'{ولما رجع موسى إلى قومه غضبان} عليهم {أسفاً} حزيناً لأنَّ الله تعالى فتنهم {قال بئسما خلفتموني من بعدي} بئسما عملتم من بعدي حين اتَّخذتم العجل إلهاً وكفرتم بالله {أعجلتم أمر ربكم} أسبقتم باتخاذ العجل ميعاد ربِّكم؟ يعني: الأربعين ليلة وذلك أنَّه كان قد وعدهم أن يأتيهم بعد ثلاثين ليلةَ فلمَّا لم يأتيهم على رأس الثَّلاثين قالوا: إنَّه قد مات {وألقى الألواح} التي فيها التَّوراة {وأخذ برأس أخيه} بذؤابته وشعره {يجرُّه إليه} إنكاراً عليه إذ لم يلحقه فَيُعرِّفه ما فعل بنو إسرائيل كما قال في سورة طه: {قَالَ يَا هَارُونُ مَا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضلوا * أن لا تتبعنِ} الآية فأعلمه هارون أنَّه أَنَّما أقام بين أظهرهم خوفاً على نفسه من القتل وهو قوله: {قال ابن أمَّ} وكان أخاه لأبيه وأُمِّه ولكنَّه قال: يا ابنَ أمَّ ليرقِّقه عليه {إنَّ القوم استضعفوني} استذلُّوني وقهروني {وكادوا} وهمُّوا أن {يقتلونني فَلا تُشْمِتْ بِيَ الأَعْدَاءَ} يعني: أصحاب العجل بضربي وإهانتي {ولا تجعلني} في موجدتك وعقوبتك لي {مع القوم الظالمين} الذين عبدوا العجل فلمَّا عرف براءة هارون ممَّا يوجب العتب عليه إذ بلغ من إنكاره على عبدة العجل ما خاف على نفسه القتل',NULL,'414',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1106,'{قال ربِّ اغفر لي} ما صنعتُ إلى أخي {ولأخي} إن قصَّر في الإِنكار {وأدخلنا في رحمتك} جنَّتك',NULL,'414',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1107,'{إِنَّ الَّذِينَ اتَّخَذُوا الْعِجْلَ} يعني: اليهود الذين كانوا في عصر النبي صلى الله عليه وسلم وهم أبناء الذين اتَّخذوا العجل إلهاً فأضيف إليهم تعييراً لهم بفعل آبائهم {سَيَنَالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ} عذابٌ في الآخرة {وذلة في الحياة الدنيا} وهي الجزية {وكذلك نجزي المفترين} كذلك أعاقب مَن اتَّخذ إلهاً دوني',NULL,'415',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1108,'{والذين عملوا السيئات} الشِّرك {ثم تابوا} رجعوا عنها {وآمنوا} صدَّقوا أنَّه لا إله غيري {إنَّ ربك من بعدها} من بعد التَّوبة {لغفور رحيم}',NULL,'415',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1109,'{ولما سكت} سكن {عن موسى الغضب أخذ الألواح} التي كان ألقاها {وفي نسختها} وفيما كُتب فيها: {هدىً} من الضَّلالة {ورحمة} من العذاب {لِلَّذِينَ هم لربهم يرهبون} للخائفين من ربِّهم',NULL,'415',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1110,'{واختار موسى قومه} من قومه {سبعين رجلاً لميقاتنا} أمره الله تعالى أن يأتيه في ناس من بني إسرائيل يعتذرون إليه من عبادة العجل ووعده لذلك موعداً فاختار موسى سبعين رجلاً ليعتذروا فلمَّا سمعوا كلام الله قالوا لموسى: أرنا الله جهرةٌ فأخذتهم {الرَّجفة} وهي الحركة الشَّديدة فماتوا جميعاً فقال موسى: {رب لو شئت أهلكتهم} وإيَّاي قبل خروجنا للميقات وكان بنو إسرائيل يُعاينون ذلك ولا يتهمونني وظن أنَّهم أهلكوا باتِّخاذ أصحابهم العجل فقال: {أتهلكنا بما فعل السفهاء منا} وإنَّما أُهلكوا لمسألتهم الرُّؤية {إِنْ هِيَ إِلا فِتْنَتُكَ} أَيْ: تلك الفتنة التي وقع فيها السفهاء لو تكن إلاَّ فتنتك أي: اختبارك وابتلاؤك أضللتَ بها قوماً فافتتنوا وعصمتَ آخرين وهذا معنى قوله: {تُضِلُّ بِهَا مَنْ تَشَاءُ وَتَهْدِي مَنْ تشاء}',NULL,'415',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1111,'{واكتب لنا} أوجب لَنَا {فِي هَذِهِ الدُّنْيَا حَسَنَةً وَفِي الآخِرَةِ} أَي: اقبل وفادتنا ورُدَّنا بالمغفرة والرَّحمة {إنا هُدْنا إليك} تبنا ورجعنا إليك بالتَّوبة {قال عذابي أصيب به من أشاء} يعني: إنَّ رحمته في الدُّنيا وسعت البرَّ والفاجر وهي في الآخرة للمؤمنين خاصَّةً وهذا معنى قوله: {فسأكتبها} فسأوجبها في الآخرة {للذين يتقون} يريد: أمَّة محمد صلى الله عليه وسلم {ويؤتون الزكاة} صدقات الأموال عند محلها {والذين هم بآياتنا يؤمنون} يصدِّقون بما أنزل على محمد والنَّبييِّن',NULL,'416',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1112,'{الذين يتبعون الرسول النبيَّ الأميَّ} وهو الذي لا يكتب ولا يقرأ وكانت هذه الخلَّة مؤكِّدة لمعجزته في القرآن {الذي يجدونه} بنعته وصفته {مَكْتُوبًا عِنْدَهُمْ فِي التَّوْرَاةِ وَالإِنْجِيلِ يَأْمُرُهُمْ بِالْمَعْرُوفِ} بالتَّوحيد وشرائع الإِسلام {وينهاهم عن المنكر} عبادة الأوثان وما لا يُعرف في شريعة {ويحلُّ لهم الطيبات} يعني: ما حرَّم عليهم في التَّوراة من لحوم الإِبل وشحوم الضَّأن {وَيُحَرِّمُ عَلَيْهِمُ الْخَبَائِثَ} الميتة والدَّم وما ذُكر في سورة المائدة {ويضع عنهم إصرهم} ويُسقط عنهم ثقل العهد الذي أُخذ عليهم {والأغلال التي كانت عليهم} الشَّدائد التي كانت عليهم كقطع أثر البول وقتل النَّفس في التَّوبة وقطع الأعضاء الخاطئة {فالذين آمنوا به} من اليهود {وعزَّروه} ووقَّروه {ونصروه} على عدوه {وَاتَّبَعُوا النُّورَ الَّذِي أُنْزِلَ مَعَهُ} يعني: القرآن الآيتين',NULL,'416',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1113,'{قل يا أيها النَّاسُ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ جَمِيعًا الَّذِي له ملك السماوات وَالأَرْضِ لا إِلَهَ إِلا هُوَ يُحْيِي وَيُمِيتُ فَآمِنُوا بِاللَّهِ وَرَسُولِهِ النَّبِيِّ الأُمِّيِّ الَّذِي يُؤْمِنُ بالله وكلماته واتبعوه لعلكم تهتدون}',NULL,'417',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1114,'{ومن قوم موسى أمة يهدون بالحق} يدعون إلى الحقِّ {وبه يعدلون} وبالحقِّ يحكمون وهم قوم وراء الصين آمنوا بالنبي صلى الله عليه وسلم لا يصل إلينا منهم أحد ولا منَّا إليهم وقوله:',NULL,'417',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1115,'{فانبجست} أَي: انفجرت وهذه الآية مفسرة في سورة اليقرة إلى قوله:',NULL,'417',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1116,'{وَإِذْ قِيلَ لَهُمُ اسْكُنُوا هَذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ وَقُولُوا حِطَّةٌ وَادْخُلُوا الْبَابَ سُجَّدًا نَغْفِرْ لَكُمْ خَطِيئَاتِكُمْ سَنَزِيدُ الْمُحْسِنِينَ}',NULL,'418',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1117,'{فَبَدَّلَ الَّذِينَ ظَلَمُوا مِنْهُمْ قَوْلا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِنَ السَّمَاءِ بما كانوا يظلمون}',NULL,'418',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1118,'{واسألهم} يعني: سؤال توبيخٍ وتقريرٍ {عن القرية} وهي أيلة {التي كانت حاضرة البحر} مُجاورته {إذ يعدون في السبت} يظلمون فيه بصيد السَّمك {إِذْ تَأْتِيهِمْ حِيتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا} ظاهرة على الماء {ويوم لا يسبتون} لا يفعلون ما يفعل في السَّبت يعني: سائر الأيام {لا تأتيهم} الحيتان {كذلك} مثل هذا الاختبار الشَّديد {نبلوهم} نختبرهم {بما كانوا يفسقون} بعصيانهم الله أَيْ: شدَّدتُ عليهم المحنة لفسقهم ولمَّا فعلوا ذلك صار أهل القرية ثلاث فلاق: فرقةٌ صادت وأكلت وفرقةٌ نهت وزجرت وفرقةٌ أمسكت عن الصَّيد وهم الذين قال الله تعالى:',NULL,'418',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1119,'{وإذ قالت أمة منهم} قالوا للفرقة النَّاهية: {لِمَ تَعِظُونَ قَوْمًا اللَّهُ مُهْلِكُهُمْ} لاموهم على موعظة قوم يعلمون أنَّهم غير مُقلعين فقالت الفرقة النَّاهية للذين لاموهم: {معذرة إلى ربكم} أَي: الأمر بالمعروف واجبٌ علينا فعلينا موعظة هؤلاء عذراً إلى الله {ولعلهم يتقون} فيتركون الصَّيد في السَّبت',NULL,'418',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1120,'{فلما نسوا ما ذكروا به} تركوا ما وُعظوا به {أَنْجَيْنَا الَّذِينَ يَنْهَوْنَ عن السوء وأخذنا الذين ظلموا} اعتدوا في السَّبت {بعذاب بئيس} شديدٍ وهو المسخ جزاءً لفسقهم وخروجهم عن أمر الله',NULL,'418',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1121,'{فلما عتوا} أَيْ: طغوا واستكبروا {عن ما نهوا عنه} أَيْ: عن ترك ما نُهوا عنه من صيد الحيتان يوم السَّبت {قلنا لهم} الآية مفسرة في سورة البقرة',NULL,'419',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1122,'{وإذ تأذَّن ربك} قالَ وأعلم ربُّك {ليبعثنَّ} ليرسلنَّ {عليهم} على اليهود {مَنْ يسومهم} أَيْ: يذيقهم {سوء العذاب} إلى يوم القيامة يعني: محمدا صلى الله عليه وسلم وأُمَّته يقاتلونهم أو يعطون الجزية {إنَّ ربك لسريع العقاب} لمن استحقَّ تعجيله',NULL,'419',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1123,'{وقطعناهم في الأرض أمماً} فرَّقناهم في البلاد فلم يجتمع لهم كلمة {منهم الصالحون} وهم الذين آمنوا {ومنهم دون ذلك} الذين كفروا {وبلوناهم} عاملناهم معاملة المختبر {بالحسنات} بالخِصب والعافية {والسيئات} الجدب والشَّدائد {لعلهم يرجعون} كي يتوبوا',NULL,'419',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1124,'{فَخَلَفَ من بعدهم خلف} من بعد هؤلاء الذين قطَّعناهم خلفٌ من اليهود يعني: أولادهم {ورثوا الكتاب} أخذوه عن آبائهم {يأخذون عرض هذا الأدنى} يأخذون ما أشرف لهم من الدُّنيا حلالاً أو حراماً {ويقولون سيغفر لنا} ويتمنَّون على الله المغفرة {وإن يأتهم عرض مثله يأخذوه} وإن أصابوا عرضاً أيْ: متاعاً من الدُّنيا مثل رشوتهم تلك التي أصابوا بالأمس قبلوه وهذا إخبارٌ عن حرصهم على الدُّنيا {أَلَمْ يُؤْخَذْ عَلَيْهِمْ مِيثَاقُ الْكِتَابِ أَنْ لا يقولوا على الله إلاَّ الحق} وأكَّد الله عليهم في التوراة ألا يقولوا على الله إلاَّ الحق فقالوا الباطل وهو قولهم: {سيغفر لنا} وليس في التَّوراة ميعاد المغفرة مع الإِصرار {ودرسوا ما فيه} أَيْ: فهم ذاكرون لما أخذ عليهم من الميثاق لأنهم قد قرؤوه',NULL,'419',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1125,'{والذين يمسكون بالكتاب} يؤمنون به ويحكمون بما فيه يعني: مؤمني أهل الكتاب {وأقاموا الصلاة} التي شرعها محمد صلى الله عليه وسلم {إنا لا نضيع أجر المصلحين} منهم',NULL,'420',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1126,'{وإذ نتقنا الجبل فوقهم} رفعناه باقتلاع له من أصله يعني: ما ذكرنا عند قوله: {ورفعنا فوقكم الطور} الآية {وظنوا} وأيقنوا {أنَّه واقع بهم} إن خالفوا وباقي الآية فيما سبق',NULL,'420',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1127,'{وَإِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظهورهم ذريتهم} أخرج الله تعالى ذريَّة آدم بعضهم من ظهور بعض على نحو ما يتوالد الأبناء من الآباء وجميعُ ذلك أخرجه من صلب آدم مثل الذَّرِّ وأخذ عليهم الميثاق أنَّه خالقهم وأنَّهم مصنوعون فاعترفوا بذلك وقبلوا ذلك بعد أن ركَّب فيهم عقولاً وذلك قوله: {وأشهدهم على أنفسهم ألست بربكم} أي: قال: ألستُ بربكم {قالوا بلى} فأقرُّوا له بالربوبيَّة فقالت الملائكة عند ذلك {شهدنا} أَيْ: على إقراركم {أن} لا {تقولوا} لئلا تقولوا أي: لئلا يقول الكفار {يوم القيامة إنا كنا عن هذا} الميثاق {غافلين} لم نحفظه ولم نذكره ويذكرون الميثاق ذلك اليوم فلا يمكنهم الإنكار مع شهادة الملائكة وهذه الآية تذكيرٌ لجميع المكلَّفين ذلك الميثاق لأنَّها وردت على لسان صاحب المعجزة فقامت في النُّفوس مقام ما هو على ذكرٍ منها',NULL,'420',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1128,'{أو تقولوا} أَيُّها الذُّريَّة محتجِّين يوم القيامة: {إنما أشرك آباؤنا من قبل} أَيْ: قبلنا ونقضوا العهد {وَكُنَّا ذُرِّيَّةً مِنْ بَعْدِهِمْ} صغاراً فاقتدينا بهم {أَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُونَ} أَفَتُعَذَّبنا بما فعل المشركون المكذِّبون بالتَّوحيد وإَّنما اقتدينا بهم وكنا في غفلةٍ عن الميثاق وهذه الآية قطعٌ لمعذرتهم فلا يمكنهم الاحتجاج بكون الآباء على الشِّرك بعد تذكير الله بأخذ الميثاق بالتَّوحيد على كلِّ واحدٍ من الذُّريَّة',NULL,'421',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1129,'{وكذلك} وكما بيَّنا في أمر الميثاق {نفصل الآيات} نبينها ليتدبها العباد {ولعلهم يرجعون} ولكي يرجعوا عمَّا هم عليه من الكفر',NULL,'421',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1130,'{واتل عليهم} واقرأ واقصص يا محمَّد على قومك {نبأ} حبر {الذي آتيناه آياتنا} علَّمناه حجج التَّوحيد {فانسلخ} خرج {منها فأتبعه الشيطان} أدركه {فكأن من الغاوين} الضَّالين يعني: بلعم بن باعوراء أعان أعداء الله على أوليائه بدعائه فَنُزِعَ عنه الإِيمان',NULL,'421',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1131,'{ولو شئنا لرفعناه بها} بالعمل بها يعني: وفَّقناه للعمل بالآيات وكنَّا نرفع بذلك منزلته {ولكنه أخلد إلى الأرض} مال إلى الدُّنيا وسكن إليها وذلك أنَّ قومه أهدوا له رشوة ليدعوَ على قوم موسى فأخذها {واتبع هواه} انقاد لما دعاه إليه الهوى {فمثله كمثل الكلب} أراد أنَّ هذا الكافر إن زجرته لم ينزجر وإن تركته لم يهتدِ فالحالتان عنده سواءٌ كحالتي الكلب اللاهث فإنَّه إنْ حُمل عليه بالطَّرد كان لاهثاً وإن تُرك وربض كان أيضاً لاهثاً كهذا الكافر في الحالتين ضالٌّ وذلك أنَّه زُجر في المنام عن الدُّعاء على موسى فلم ينزجر وتُرك عن الزَّجر فلم يهتد فضرب الله له أخسَّ شيءٍ في أخسّ أحواله وهو حال اللَّهث مثلاً وهو إدلاع اللِّسان من الإِعياء والعطش والطلب يفعل ذلك في حال الكلال وحال الرَّاحة ثمَّ عمَّ بهذا التَّمثيل جميع المكذِّبين بآيات الله فقال: {ذَلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كذبوا بآياتنا} يعني: أهل مكَّة كانوا يتمنَّون هادياً يهديهم فلما جاءهم مَنْ لا يشكُّون في صدقه كذَّبوه فلم يهتدوا لمَّا تُركوا ولم يهتدوا أيضاً لمَّا دُعوا بالرَّسول فكانوا ضالِّين عن الرُّشد في الحالتين {فاقصص القصص} يعني: قصص الذين كذَّبوا بآياتنا {لعلهم يتفكرون} فيتَّعظون ثمَّ ذمَّ مَثلَهم فقال:',NULL,'421',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1132,'{ساء مثلاً القوم الذين كذبوا بآياتنا} أي: بئس مثلُ الْقَوْمُ الَّذِينَ كَذَّبُوا بِآيَاتِنَا {وَأَنْفُسَهُمْ كَانُوا يَظْلِمُونَ} بذلك التَّكذيب يعني: إنما يخسرون حظهم',NULL,'422',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1133,'{مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِي وَمَنْ يُضْلِلْ فأولئك هم الخاسرون}',NULL,'422',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1134,'{ولقد ذَرَأْنا} خلقنا {لجهنم كثيراً من الجن والإِنس} وهم الذين حقَّت عليهم الشَّقاوة {لهم قلوب لا يفقهون بها} لا يعقلون بها الحير والهدى {ولهم أعين لا يبصرون بها} سبل الهدى {ولهم آذان لا يسمعون بها} مواعظ القرآن {أولئك كالأنعام} يأكلون ويشربون ولا يلتفتون إلى الآخرة {بل هم أضلُّ} لأنَّ الأنعام مطيعةٌ لله والكافر غير مطيع {أولئك هم الغافلون} عمَّا في الآخرة من العذاب',NULL,'422',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1135,'{ولله الأسماء الحسنى} يعني: التِّسعة والتِّسعين {فادعوه بها} كقولك: يا اللَّهُ يا قديرُ يا عليمُ {وذروا الذين يلحدون في أسمائه} يميلون عن القصد وهم المشركون عدلوا بأسماء الله عمّا هي عليه فسمُّوا بها أوثانهم وزادوا فيها ونفصوا واشتقوا اللاَّت من الله والعُزَّى من العزيز ومناة من المنَّان {سيجزون ما كانوا يعملون} جزاء ما كانوا يعملون في الآخرة',NULL,'423',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1136,'{وممن خلقنا أمة} الآية يعني: أمة محمد صلى الله عليه وسلم كما قال في قوم موسى عليه السلام: {ومن قوم موسى أمةٌ} الآية',NULL,'423',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1137,'{والذين كذبوا بآياتنا} محمدٍ والقرآن يعني: أهل مكَّة {سنستدرجهم} سنمكر بهم {من حيث لا يعلمون} كلما جدَّدوا لنا معصية جدَّّدنا لهم نعمةً',NULL,'423',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1138,'{وأملي لهم} أُطيل لهم مدَّة عمرهم ليتمادوا في المعاصي {إِنَّ كيدي متين} مكري شديد نزلت في المستهزئين من قريش قتلهم الله في ليلةٍ واحدةٍ بعد أن أمهلهم طويلاً',NULL,'423',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1139,'{أَوَلَم يتفكروا} فيعلموا {ما بصاحبهم} محمَّدٍ {من جنة} من جنون',NULL,'424',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1140,'{أولم ينظروا في ملكوت السماوات والأرض} ليستدلُّوا بها على توحيد الله وفسَّرنا ملكوت السماوات والأرضِ في سورة الأنعام {وما خلق الله من شيء} وفيما خلق الله من الأشياء كلها {وَأَنْ عَسَى أَنْ يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ} وفي أنْ لعلَّ آجالهم قريبة فيهلكوا على الكفر ويصيروا إلى النَّار {فَبِأَيِّ حَدِيثٍ بَعْدَهُ يؤمنون} فبأيِّ قرآنٍ غير ما جاء به محمَّد يُصدِّقون؟ يعني: إنَّه خاتم الرُّسل ولا وحي بعده ثمَّ ذكر علَّة إعراضهم عن الإيمان فقال:',NULL,'424',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1141,'{مَنْ يُضْلِلِ اللَّهُ فَلا هَادِيَ لَهُ وَيَذَرُهُمْ في طغيانهم يعمهون}',NULL,'424',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1142,'{يسألونك عن الساعة} أَي: السَّاعة التي يموت فيها الخلق يعني: القيامة نزلت في قريش قالت لمحمَّدٍ صلى الله عليه وسلم: أسرَّ إلينا متى السَّاعة {أيَّان مرساها} متى وقوعها وثبوتها؟ {قل إنما علمها} العلم بوقتها ووقوعها {عِنْدَ رَبِّي لا يُجَلِّيهَا لوقتها إلاَّ هو} لا يظهرها في وقتها إلاَّ هو {ثقلت في السماوات والأرض} ثقل وقوعها وكَبًُر على أهل السماوات والأرض لما فيها من الأهوال {لا تأتيكم إلاَّ بغتة} فجأة {يسألونك كأنك حَفِيٌّ عنها} عالمٌ بها مسؤول عَنْهَا {قُلْ إِنَّمَا عِلْمُهَا عِنْدَ اللَّهِ وَلَكِنَّ أكثر الناس لا يعلمون} أنَّ علمها عند الله حين سألوا محمداً عن ذلك',NULL,'424',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1143,'{قل لا أملك لنفسي} الآية إنَّ أهل مكة قالوا: يا محمَّد ألا يخبرك ربُّك بالسِّعر الرَّخيص قبل أن يغلو فنستري من الرَّخيص لنربح عليه؟ وبالأرض التي تريد أن تجدب فنرتحل عنها؟ فأنزل الله تعالى هذه الآية ومعنى قوله: {لا أملك لنفسي نفعاً} أي: اجتلاب نفع بأن أربح {ولا ضرَّاً} دفع ضرٍّ بأن أرتحل من الأرض التي تريد أن تجدب {إلاَّ ما شاء الله} أن أملكه بتمليكه {وَلَوْ كُنْتُ أَعْلَمُ الْغَيْبَ} ما يكون قبل أن يكون {لاسْتَكْثَرْتُ مِنَ الْخَيْرِ} لادَّخرت في زمانِ الخِصْبِ لزمن الجدب {وما مسني السوء} وما أصابني الضرُّ والفقر {إن أنا إلاَّ نذير} لمَنْ يصدِّق ما جئت به {وَبَشِيرٌ} لمن اتَّبعني وآمن بي',NULL,'425',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1144,'{هُوَ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ وَاحِدَةٍ} يعني: آدم {وجعل منها زوجها} حوَّاء خلقها من ضلعه {ليسكن إليها} ليأنس بها فيأوي إليها {فلما تغشاها} جامعها {حَمَلَتْ حَمْلا خَفِيفًا} يعني: النُّطفة والمنيِّ {فمرَّت به} استمرَّت بذلك الحمل الخفيف وقامت وقعدت ولم يُثْقِلها {فلما أثقلت} صار إلى حال الثِّقل ودنت ولادتها {دَعَوَا اللَّهَ ربهما} آدم وحواء {لَئِنْ آتَيْتَنَا صَالِحًا} بشراً سويَّاً مثلنا {لَنَكُونَنَّ من الشاكرين} وذلك أنَّ إبليس أتاها في غير صورته التي عرفته وقال لها: ما الذي في بطنك؟ قالت: ما أدري قال: إنِّي أخاف أن يكون بهيمةً أو كلباً أو خنزيراً وذكرت ذلك لآدم فلم يزالا في همٍّ من ذلك ثمَّ أتاها وقال: إن سألتُ الله أن يجعله خلقاً سويَّاً مثلك أَتُسمِّينه عبد الحارث؟ وكان إبليس في الملائكة الحارث ولم يزل بها حتى غرَّها فلمَّا ولدت ولداً سويَّ الخلق سمَّته عبد الحارث فرضي آدم فذلك قوله:',NULL,'425',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1145,'{فلما آتاهما صالحاً} ولداً سويّاً {جعلا له} لله {شركاء} يعني: إبليس فأوقع الواحد موقع الجميع {فيما آتاهما} من الولد إذ سمَّياه عبد الحارث ولا ينبغي أن يكون عبداً إلاَّ لله ولم تعرف حوَّاء أنَّه إبليس ولم يكن هذا شركاً بالله لأنَّهما لم يذهبا إلى أنَّ الحارث ربَّهما لكنهما قصدا إلى أنَّه كان سبب نجاته وتم الكلام عند قوله: فيما {آتاهما} ثمَّ ذكر كفَّار مكة فقال: {فَتَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ}',NULL,'426',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1146,'{أَيُشْرِكُونَ مَا لا يَخْلُقُ شَيْئًا وَهُمْ يُخْلَقُونَ} يريد: أيعبدون ما لا يقدر أن يخلق شيئاً وهم مخلوقون! على الأصنام',NULL,'426',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1147,'{ولا يستطيعون لهم نصراً} لا تنصر مَنْ أطاعها {وَلا أَنْفُسَهُمْ يَنْصُرُونَ} ولا يدفعون عن أنفسهم مكروه مَنْ أرادهم بكسرٍ أو نحوه ثمَّ خاطب المؤمنين فقال:',NULL,'426',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1148,'{وإن تدعوهم} يعني: المشركين {إلى الهدى لا يتبعوكم} الآية',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1149,'{إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ} يعني: الأصنام {عباد} مملوكون مخلوقون {أمثالكم فادعوهم فليستجيبوا لكم} فاعبدوهم هل يثيبونكم أو يجازونكم! ؟ {إِنْ كُنْتُمْ صادقين} أن لكم عند الأصنام منفعةً أو ثواباً أو شفاعةً ثمَّ بيَّن فضل الآدميِّ عليهم فقال:',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1150,'{أَلَهُمْ أَرْجُلٌ يَمْشُونَ بِهَا} مشيَ بني آدم {أَمْ لهم أيدٍ يبطشون بها} يتناولون بها مثل بطش بني آدم {أَمْ لَهُمْ أَعْيُنٌ يُبْصِرُونَ بِهَا أَمْ لَهُمْ آذان يسمعون بها قل ادعوا شركاءكم} الذين تعبدون من دون الله {ثمَّ كيدون} أنتم وشركاؤكم {فلا تنظرون} لا تُمهلون واعجلوا في كيدي',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1151,'{إنَّ وليي الله} الذي يتولَّى حفظي ونصري {الذي نزل الكتاب} القرآن {وهو يتولى الصالحين} الذين لا يعدلون بالله شيئا وقوله:',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1152,'{وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيرًا مِنَ الْجِنِّ وَالإِنْسِ لَهُمْ قُلُوبٌ لا يَفْقَهُونَ بِهَا وَلَهُمْ أَعْيُنٌ لا يُبْصِرُونَ بِهَا وَلَهُمْ آذَانٌ لا يَسْمَعُونَ بِهَا أُولَئِكَ كَالأَنْعَامِ بَلْ هُمْ أَضَلُّ أُولَئِكَ هم الغافلون}',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1153,'{وتراهم ينظرون إليك} تحسبهم يرونك {وهم لا يبصرون} وذلك لأنَّ لها أعيناً مصنوعةً مركَّبةً بالجواهر حتى يحسب الإِنسان أنَّها تنظر إليه',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1154,'{خذ العفو} اقبل الميسور من أخلاق النَّاس ولا تستقصِ عليهم وقيل: هو أن يعفو عمَّنْ ظلمه ويصل مَنْ قطعه {وأمر بالعرف} المعروف الذي يعرف حسنه كلُّ أحدٍ {وأعرض عن الجاهلين} لا تقابل السَّفيه بسفهه فلمَّا نزلت هذه الآية قال رسول الله صلى الله عليه وسلم: كيف يا ربِّ والغضب؟ فنزل:',NULL,'427',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1155,'{وإما ينزغنك من الشيطان نزغ} يعرض لك من الشيطان عارضٌ ونالك منه أدنى وسوسة {فاستعذ بالله} اطلب النَّجاة من تلك البليَّة بالله {إنَّه سميع} لدعائك {عليم} عالمٌ بما عرض لك',NULL,'428',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1156,'{إنَّ الذين اتقوا} يعني: المؤمنين {إذا مسَّهم} أصابهم {طائف من الشيطان} عارضٌ من وسوسته {تذكَّروا} استعاذوا بالله {فإذا هم مبصرون} مواقع خَطَئِهِمْ فينزعون من مخالفة الله',NULL,'428',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1157,'{وإخوانهم} يعني: الكفَّار وهم إخوان الشَّياطين {يمدونهم} أَي: الشَّياطين يطوِّلون لهم الإِغواء والضَّلالة {ثم لا يقصرون} عن الضلالة ولا يبصرونها كما أقصر المُتَّقي عنها حين أبصرها',NULL,'428',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1158,'{وإذا لم تأتهم} يعني: أهل مكَّة {بآية} سألوكها {قالوا لولا اجتبيتها} اختلقتها وأنشأتها من قبل نفسك {قُلْ إِنَّمَا أَتَّبِعُ مَا يُوحَى إِلَيَّ مِنْ ربي} أَيْ: لستُ آتي بالآيات من قبل نفسي {هذا} أَيْ: هذا القرآن الذي أتيتُ به {بصائر من ربكم} حججٌ ودلائلُ تعود إلى الحق',NULL,'428',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1159,'{وإذا قرأ القرآن فاستمعوا له وأنصتوا لعلكم ترحمون} الآيةُ نزلت في تحريم الكلام في الصَّلاة وكانوا يتكلَّمون في الصَّلاة في بدء الأمر وقيل: نزلت في ترك الجهر بالقراءة وراء الإِمام وقيل: نزلت في السُّكوت للخطبة وقوله: {وأنصتوا} أَيْ: عمَّا يحرم من الكلام في الصَّلاة أو عن رفع الصَّوت خلف الإِمام أو اسكتوا لاستماع الخطبة',NULL,'429',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1160,'{واذكر ربك في نفسك} يعني: القراءة في الصَّلاة {تضرُّعاً وخيفة} استكانةًَ لي وخوفاً من عذابي {ودون الجهر} دون الرَّفع {من القول بالغدو والآصال} بالبُكُر والعشيَّات أُمر أن يقرأ في نفسه في صلاة الإِسرار ودون الجهر فيما يرفع به الصَّوت {ولا تكن من الغافلين} الذين لا يقرؤون في صلاتهم',NULL,'429',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1161,'{إنَّ الذين عند ربك} يعني: الملائكة وهم بالقرب من رحمة الله {لا يستكبرون عن عبادته} أَيْ: هم مع منزلتهم ودرجتهم يعبدون الله كأنَّه قيل: مَنْ هو أكبر منك أيُّها الإِنسان لا يستكبر عن عبادة الله {ويسبحونه} يُنزِّهونه عن السُّوء {وله يسجدون}',NULL,'429',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1162,'{يسألونك عن الأنفال} الغنائم لمن هي؟ نزلت حسن اختلفوا في غنائم بدر فقال الشُّبان: هي لنا لأنَّا باشرنا الحرب وقالت الأشياخ: كنا رداءا لكم لأنَّا وقفنا في المصافِّ مع رسول الله صلى الله عليه وسلم ولو انهزمتم لانحزتم إلينا فلا تذهبوا بالغنائم دوننا فأنزل الله تعالى: {قل الأنفال لله والرسول} يضعها حيث يشاء من غير مشاركة فيها فقسمها بينهم على السَّواء {فاتقوا الله} بطاعته واجتناب معاصية {وأصلحوا ذات بينكم} حقيقة وصلكم أَيْ: لا تَخَالفوا {وأطيعوا الله ورسوله} سلِّموا لهما في الأنفال فإنَّهما يحكمان فيها ما أرادا {إن كنتم مؤمنين} ثمَّ وصف المؤمنين فقال:',NULL,'430',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1163,'{إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قلوبهم} أَيْ: المؤمن الذي إذا خُوِّف بالله فرق قلبه وانقاد لأمره {وإذا تليت عليهم آياته زادتهم إيماناً} تصديقاً ويقيناً {وعلى ربهم يتوكلون} بالله يثقون لا يرجون غيره',NULL,'430',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1164,'{الَّذِينَ يُقِيمُونَ الصَّلاةَ وَمِمَّا رَزَقْنَاهُمْ يُنْفِقُونَ}',NULL,'431',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1165,'{أُولَئِكَ هم المؤمنون حقاً} صدقاً من غير شكٍّ لا كإيمان المنافقين {لهم درجات عند ربهم} يعني: درجات الجنَّة {ومغفرة ورزق كريم} وهو رزق الجنَّة',NULL,'431',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1166,'{كما أخرجك} أيْ: امض لأمر الله في الغنائم وإن كره بعضهم ذلك لأنَّ الشُّبان أرادوا أن يستبدُّوا به فقال الله تعالى: أعط مَنْ شئت وإن كرهوا كما مضيت لأمر الله في الخروج وهم له كارهون ومعنى {كما أخرجك ربُّك من بيتك} أمرك بالخروج من المدينة لعير قريش {بالحقِّ} بالوحي الذي أتاك به جبريل {وإنَّ فريقاً من المؤمنين لكارهون} الخروج معك كراهة الطَّبع لاحتمال المشقَّة لأنَّهم علموا أنَّهم لا يظفرون بالعير دون القتال',NULL,'431',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1167,'{يجادلونك في الحق بعد ما تبيَّن} في القتال بعد ما أُمرت به وذلك أنَّهم خرجوا للعير ولم يأخذوا أُهبة الحرب فلمَّا أُمروا بحرب النَّفير شقَّ عليهم ذلك فطلبوا الرُّخصة في ترك ذلك فهو جدالهم {كَأَنَّمَا يُسَاقُونَ إِلَى الْمَوْتِ وَهُمْ يَنْظُرُونَ} أَيْ: لشدَّة كراهيتهم للقاء القوم كأنَّهم يُساقون إلى الموت عياناً',NULL,'431',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1168,'{وإذْ يعدكم الله إحدى الطائفتين} العير أو النَّفير {أَنَّهَا لَكُمْ وَتَوَدُّونَ أَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تكون لكم} أَيْ: العير التي لا سلاح فيها تَكُونُ لَكُمْ {وَيُرِيدُ اللَّهُ أَنْ يُحِقَّ الْحَقَّ} يُظهره ويُعليَه {بكلماته} بِعِدَاتِه التي سبقت بظهور الإِسلام {ويقطع دابر الكافرين} آخر مَنْ بقي منهم يعني: إنَّه إنَّما أمركم بحرب قريشٍ لهذا',NULL,'431',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1169,'{ليحقَّ الحق} أَيْ: ويقطع دابر الكافرين ليُظهر الحقَّ ويُعليَه {ويبطل الباطل} ويُهلك الكفر ويُفنيه {ولو كره المجرمون} ذلك',NULL,'432',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1170,'{إذ تستغيثون ربكم} تطلبون منه المغفرة بالنَّصر على العدوِّ لقلَّتكم {فَاسْتَجَابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلائِكَةِ مردفين} متتابعين جاؤوا بعد المسلمين ومَنْ فتح الدَّال أراد: بألفٍ أردف الله المسلمين بهم',NULL,'432',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1171,'{وما جعله الله} أَيْ: الإِرداف {إلاَّ بشرى} الآية ماضية في سورة آل عمران',NULL,'432',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1172,'{إذ يغشيكم النعاس أمنة منه} وذلك أنَّ الله تعالى أمَّنهم أمناً غشيهم النُّعاس معه وهذا كما كان يوم أُحدٍ وقد ذكرنا ذلك في سورة آل عمران {وَيُنَزِّلُ عَلَيْكُمْ مِنَ السَّمَاءِ مَاءً لِيُطَهِّرَكُمْ بِهِ} وذلك أنَّهم لمَّا بايتوا المشركين ببدرٍ أصابت جماعة منهم جنابات وكان المشركون قد سبقوهم إلى الماء فوسوس إليهم الشَّيطان وقال لهم: كيف ترجون الظَّفر وقد غلبوكم على الماء؟ وأنتم تُصلُّون مُجنِبين ومُحدِثين وتزعمون أنَّكم أولياء الله وفيكم نبيُّه؟ فأنزل الله تعالى مطراً سال منه الوادي حتى اغتسلوا وزالت الوسوسة فذلك قوله: {ليطهركم به} أَيْ: من الأحداث والجنابات {ويذهب عنكم رجز الشيطان} وسوسته التي تكسب عذاب الله {وليربط} به {على قلوبكم} باليقين والنَّصر {ويثبت به الأقدام} وذلك أنَّهم كانوا قد نزلوا على كثيبٍ تغوص فيه أرجلهم فلبَّده المطر حتى ثبتت عليه الأقدام',NULL,'432',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1173,'{إذ يوحي ربك إلى الملائكة} الذين أمدَّ بهم المسلمين {إني معكم} بالعون والنُّصرة {فثبتوا الذين آمنوا} بالتَّبشير بالنَّصر وكان المَلَك أمام الصَّف على صورة رجلٍ ويقول: أبشروا فإنَّ الله ناصركم {سألقي في قلوب الذين كفروا الرعب} الخوف من أوليائي {فاضربوا فوق الأعناق} أَيْ: الرُّؤوس {واضربوا منهم كلَّ بنان} أَيْ: الأطراف من اليدين والرِّجلين',NULL,'433',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1174,'{ذلك} الضَّرب {بأنهم شاقوا الله ورسوله} باينوهما وخالفوهما',NULL,'433',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1175,'{ذلكم} القتل والضَّرب ببدرٍ {فذوقوه وأنَّ للكافرين عذاب النار} بعدما نزل بهم من ضرب الأعناق',NULL,'433',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1176,'{يا أيها الَّذِينَ آمَنُوا إِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفًا} مُجتمعين مُتدانين إليكم للقتال {فلا تولوهم الأدبار} لا تجعلوا ظهوركم ممَّا يليهم',NULL,'433',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1177,'{ومن يُوَلَّهِمْ يومئذٍ} أَيْ: يوم لقاء الكفَّار {دُبُرَهُ إِلا مُتَحَرِّفًا لِقِتَالٍ} مُنعطفاً مُستَطرداً يطلب العودة {أو متحيزاً} مُنضمَّاً {إلى فئة} لجماعةٍ يريدون العود إلى القتال {فقد باء بغضب من الله} الآية وأكثر المفسرين على أنَّ هذا الوعيد إنَّما كان لمَنْ فرَّ يوم بدرٍ وكان هذا خاصَّاً للمنهزم يوم بدرٍ',NULL,'433',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1178,'{فلم تقتلوهم} يعني: يوم بدرٍ {ولكنَّ الله قتلهم} بتسبيبه ذلك من المعونة عليهم وتشجيع القلب {وما رميت إذ رميت} وذلك أنَّ جبريل عليه السَّلام قال للنبيِّ عليه السَّلام يوم بدرٍ: خذ قبضةً من تراب فارمهم بها فأخذ رسول الله صلى الله عليه وسلم قبضةً من حصى الوادي فرمى بها في وجوه القوم فلم يبقَ مشركٌ إلاَّ دخل عينيه منها شيء وكان ذلك شبب هزيمتهم فقال الله تعالى {وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَكِنَّ اللَّهَ رَمَى} أَيْ: إنَّ كفَّاً من حصى لا يملأ عيون ذلك الجيش الكثير برمية بَشرٍ ولكنَّ الله تعالى تولَّى إيصال ذلك إلى أبصارهم {وليبلي المؤمنين منه بلاءً حسناً} وينعم عليهم نعمةً عظيمةً بالنَّصر والغنيمة فعل ذلك {إنَّ الله سميع} لدعائهم {عليم} بنيَّاتهم',NULL,'434',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1179,'{ذلكم وأنَّ الله موهن كيد الكافرين} يُهنِّىء رسوله بإيهانه كيد عدوِّه حتى قُتلت جبابرتهم وأُسِر أشرافهم',NULL,'434',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1180,'{إن تستفتحوا} هذا خطابٌ للمشركين وذلك أنَّ أبا جهلٍ قال يوم بدرٍ: اللَّهم انصر أفضل الدِّينَيْن وأهدى الفئتين فقال الله تعالى: {إن تستفتحوا} تستنصروا لأَهْدى الفئتين {فقد جاءكم الفتح} النَّصر {وإن تنتهوا} عن الشِّرك بالله {فَهُوَ خَيْرٌ لَكُمْ وَإِنْ تعودوا} لقتال محمَّدٍ {نعد} عليكم بالقتل والأسر {ولن تغني عنكم} تدفع عَنْكُمْ {فِئَتُكُمْ} جماعتكم {شَيْئًا وَلَوْ كَثُرَتْ} في العدد {وأنَّ الله مع المؤمنين} فالنَّصر لهم',NULL,'434',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1181,'{يا أيها الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَرَسُولَهُ وَلا تَوَلَّوْا عنه} لا تُعرضوا عنه بمخالفة أمره {وأنتم تسمعون} ما نزل من القرآن',NULL,'435',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1182,'{ولا تكونوا كالذين قالوا سمعنا} سماع قابلٍ وليسوا كذلك يعني: المنافقين وقيل: أراد المشركين لأنَّهم سمعوا ولم يتفكَّروا فيما سمعوا فكانوا بمنزلة مَنْ لم يسمع',NULL,'435',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1183,'{إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الصُّمُّ الْبُكْمُ الذين لا يعقلون} يريد نفراً من المشركين كانوا صمَّاً عن الحقِّ فلا يسمعونه بُكماً عن التَّكلُّم به بيَّن الله تعالى أنَّ هؤلاء شرُّ ما دبَّ على الأرض من الحيوان',NULL,'435',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1184,'{ولو علم الله فيهم خيراً} لو علم أنَّهم يصلحون بما يُورده عليهم من حججه وآياته {لأسمعهم} إيَّاها سماع تفهمٍ {ولو أسمعهم} بعد أن علم أن لا خير فيهم ما انتفعوا بذلك و {لَتَوَلَّوْا وهم معرضون}',NULL,'435',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1185,'{يا أيها الذين آمنوا استجيبوا لله وللرسول} أجيبوا لهما بالطَّاعة {إذا دعاكم لما يحييكم} يعني: لأنَّ به يحيا أمرهم ويقوى ولأنَّه سبب الشَّهادة والشُّهداء أحياءٌ عند ربهم ولأنَّه سببٌ للحياة الدَّائمة في الجنَّة {وَاعْلَمُوا أَنَّ اللَّهَ يَحُولُ بَيْنَ الْمَرْءِ وَقَلْبِهِ} يحول بين الإِنسان وقلبه فلا يستطيع أن يؤمن إلاَّ بإذنه ولا أن يكفر فالقلوب بيد الله تعالى يُقلِّبها كيف يشاء {وأنَّه إليه تحشرون} للجزاء على الأعمال',NULL,'435',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1186,'{واتقوا فتنة} الآية أمر الله تعالى المؤمنين ألا يُقرّوا المنكر بين أظهرهم فيعمَّهم الله بالعذاب والفتنة ها هنا: إقرار المنكر وترك التَّغيير له وقوله: {لا تصيبنَّ الذين ظلموا منكم خاصة} أَيْ: تصيب الظَّالم والمظلوم ولا تكون للظَّلمة وحدهم خاصَّة ولكنَّها عامَّة والتَّقدير: واتَّقوا فتنةً إن لا تتقوها لا تصيب الذين ظلموا منكم خاصَّة أَيْ: لا تقع بالظَّالمين دون غيرهم ولكنها تقع بالصَّالحين والطَّالحين {واعلموا إن الله شديد العقاب} حثٌّ على لزوم الاستقامة خوفاً من الفتنة ومن عقاب الله بالمعصية فيها',NULL,'436',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1187,'{واذكروا} يعني: المهاجرين {إذ أنتم قليل} يعني: حين كانوا بمكَّة في عنفوان الإسلام قبل أن يكلموا أربعين {مستضعفون في الأرض} يعني: أرض مكَّة {تخافون أن يتخطفكم الناس} المشركون من العرب لو خرجتم منها {فآواكم} جعل لكم مأوىً ترجعون إليه وضمَّكم إلى الأنصار {وأيَّدكم بنصره} يوم بدرٍ بالملائكة {ورزقكم من الطيبات} يعني: الغنائم أحلَّها لكم {لعلكم تشكرون} كي تطيعوا',NULL,'436',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1188,'{يا أيها الذين آمنوا لا تخونوا الله} بترك فرائضه {والرسول} بترك سنَّته {وتخونوا} أَيْ: ولا تخونوا {أماناتكم} وهي كلُّ ما ائتمن الله عليها العباد وكلُّ أحدٍ مؤتمنٌ على ما افترض الله عليه {وأنتم تعلمون} أنَّها أمانةٌ من غير شبهةٍ وقيل: نزلت هذه الآية في أبي لُبابة حين بعثه رسول الله صلى الله عليه وسلم إلى قُريظة لمَّا حاصرهم وكان أهله وولده فيهم فقالوا له: ما ترى لنا؟ أننزل على حكم سعدٍ فينا؟ فأشار أبو لبابة إلى حلقه أنَّه الذَّبح فلا تفعلوا وكانت منه خيانةً لله ورسوله',NULL,'436',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1189,'{وَاعْلَمُوا أَنَّمَا أَمْوَالُكُمْ وَأَوْلادُكُمْ فِتْنَةٌ} أَيْ: محنةٌ يظهر بها ما في النَّفس من اتِّباع الهوى أو تجنُّبه ولذلك مال أبو لبابة إلى قُريظة في إطلاعهم على حكم سعد لأنَّ ماله وولده كانت فيهم {وإنَّ الله عنده أجر عظيم} لمن أدى الأمانة ولم يخن',NULL,'437',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1190,'{يا أيها الذين آمنوا إن تتقوا الله} باجتناب الخيانة فيما ذُكر {يجعل لكم فرقاناً} يفرق بينكم وبين ما تخافون فتنجون {وَيُكَفِّرْ عَنْكُمْ سَيِّئَاتِكُمْ} يمحو عنكم ما سلف من ذنوبكم {والله ذو الفضل العظيم} لا يمنعكم ما وعدكم على طاعته',NULL,'437',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1191,'{وإذ يمكر بك الذين كفروا} وذلك أنَّ مشركي قريش تآمروا في دارة النَّدوة في شأن محمَّد عليه السًّلام فقال بعضهم: قيدوه نترص به ريب المنون وقال بعضهم: أخرجوه عنكم تستريحوا من أذاه وقال أبو جهل - لعنه الله -: ما هذا برأي ولكن اقتلوه بأن يجتمع عليه من كلِّ بطنٍ رجلٌ فيضربوه ضربة رجلٍ واحدٍ فإذا قتلوه تفرَّق دمه في القبائل فلا يقوى بنو هاشم على حرب قريش كلِّها فأوحى الله تعالى إلى نبيِّه بذلك وأمره بالهجرة فذلك قوله: {ليثبتوك} أَيْ: ليوثقوك ويشدُّوك {أو يقتلوك} بأجمعهم قتلةَ رجلٍ واحدٍ كما قال اللَّعين أبو جهل {أو يخرجوك} من مكَّة إلى طرفٍ من أطراف الأرض {ويمكرون ويمكر الله} أَيْ: يجازيهم جزاء مكرهم بنصر المؤمنين عليهم {والله خير الماكرين} أفضل المجازين بالسيئة العقوبة وذلك أنَّه أهلك هؤلاء الذين دبَّروا لنبيِّه الكيد وخلَّصه منهم',NULL,'437',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1192,'{وإذا تتلى عليهم آياتنا} الآية كان النَّضر بن الحارث خرج إلى الحيرة تاجراً واشترى أحاديث كليلة ودمنة فكان يقعد به مع المستهزئين فيقرأ عليهم فلمَّا قصَّ رسول الله صلى الله عليه وسلم شأن القرون الماضية قال النَّضرُ بن الحارث: لو شئتُ لقلتُ مثل هذا إنْ هذا إلاَّ ما سطَّر الأوَّلون في كتبهم وقال النَّضر أيضاً:',NULL,'438',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1193,'{اللهم إن كان هذا} الذي يقوله محمَّدٌ حقَّاً {من عندك فَأَمْطِرْ عَلَيْنَا حِجَارَةً مِنَ السماء} كما أمطرتا على قوم لوط {أَوِ ائْتِنَا بِعَذَابٍ أَلِيمٍ} أَيْ: ببعض ما عذَّبت به الأمم حمله شدَّة عداوة النبي صلى الله عليه وسلم على إظهار مثل هذا القول ليوهم أنَّه على بصيرةٍ من أمره وغاية الثِّقة في أمر محمَّد أنَّه ليس على حقٍّ',NULL,'438',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1194,'{وَمَا كَانَ اللَّهُ لِيُعَذِّبَهُمْ وَأَنْتَ فِيهِمْ} وَمَا كان الله ليعذِّب المشركين وأنت مقيمٌ بين أظهرهم لأنَّه لم يعذِّب الله قريةً حتى يخرج النبيُّ منها والذين آمنوا معه {وما كان الله} معذِّبَ هؤلاء الكفَّار وفيهم المؤمنون {يستغفرون} يعني: المسلمين ثمَّ قال:',NULL,'439',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1195,'{وما لهم أن لا يعذبهم الله} أي: ولمَ لا يعذِّبهم الله بالشيف بعد خروج مَنْ عنى بقوله: {وهم يستغفرون} من بينِهم {وهم يصدون} يمنعون النبيِّ والمؤمنين {عن المسجد الحرام} أن يطوفوا به {وما كانوا أولياءه} وذلك أنَّهم قالوا: نحن أولياء المسجد فردَّ الله عليهم بقوله: {إن أولياؤه إلاَّ المتقون} يعني: المهاجرين والأنصار {ولكن أكثرهم لا يعلمون} غيبَ علمي وما سبق في قضائي',NULL,'439',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1196,'{وَمَا كَانَ صَلاتُهُمْ عِنْدَ الْبَيْتِ إِلا مُكَاءً وتصديةً} أي: صفيرا وتصفيفا وكانت قريش يطوفون بالبيت عُراةً يُصفِّرون ويُصفِّقون جعلوا ذلك صلاةً لهم فكان تقربهم إلى الله بالتصفير والصَّفيق {فذوقوا العذاب} ببدرٍ {بما كنتم تكفرون} تجحدون توحيد الله تعالى',NULL,'439',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1197,'{إنَّ الذين كفروا} نزلت في المُنفقين على حرب رسول الله صلى الله عليه وسلم أيَّام بدرٍ وكانوا اثني عشر رجلاً قال تعالى: {فسينفقونها ثم تكون عليهم حسرة} بذهاب الأموال وفوات المراد',NULL,'439',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1198,'{ليميز الله الخبيث من الطيب} أي: إنما تحشرون إلى جهنَّم ليميِّز بين أهل الشَّقاوة وأهل السَّعادة {ويجعل الخبيث} أي: الكافر وهو اسم الجنس {بعضه على بعض} يلحق بعضهم ببعض {فيركمه جميعاً} أَيْ: يجمعه حتى يصير كالسَّحاب المركوم ثمَّ {فيجعله في جهنم أولئك هم الخاسرون} لأنَّهم اشتروا بأموالهم عذاب الله في الآخرة',NULL,'440',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1199,'{قل للذين كفروا} أبي سفيان وأصحابه: {إن ينتهوا} عن الشِّرك وقتال المؤمنين {يغفر لهم ما قد سلف} تقدَّم من الزِّنا والشِّرك لأنَّ الحربيَّ إذا أسلم عاد كَمِثْلِهِ يوم ولدته أمه {وإن يعودوا} للقتال {فقد مضت سنَّة الأولين} بنصر اللَّهِ رسلَه ومَنْ آمن على مَنْ كفر',NULL,'440',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1200,'{وقاتلوهم حتى لا تكون فتنة} كفرٌ {ويكون الدين كله لله} لا يكون مع دينكم كفرٌ في جزيرة العرب {فإن انتهوا} عن الشِّرك {فإنَّ الله بما يعملون بصير} يُجازيهم مُجازاة البصير بهم وبأعمالهم',NULL,'440',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1201,'{وإن تولوا} أَبَوا أن يدعوا الشِّرك وقتال محمد {فاعلموا أنَّ الله مولاكم} ناصركم يا معشر المؤمنين',NULL,'441',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1202,'{واعلموا أنما غنمتم من شيء} أخذتموه قسراً من الكفَّار {فَأَنَّ لله خمسه} هذا تزيينٌ لافتتاح الكلام ومصرف الخمس إلى حيث شكر وهو قوله: {وللرسول} كان له خمس الخمس يصنع فيه ما يشاء واليوم يُصرف إلى مصالح المسلمين {ولذي القربى} وهم بنو هاشم وبنو المطلب الذين حُرِّمت عليهم الصَّدقات المفروضة لهم خمس الخمس من الغنيمة {واليتامى} وهم أطفال المسلمين الذين هلك آباؤهم يُنفق عليهم من خُمس الخمس {والمساكين} وهم أهل الحاجة والفاقة من المسلمين لهم أيضاً خمس الخمس {وابن السبيل} المنقطع به في سفره فخمس الغنيمة يقسم على خمسة أخماس كما ذكره الله تعالى وأربعة أخماسها تكون للغانمين وقوله: {إن كنتم آمنتم بالله} أَيْ: فافعلوا ما أُمرتم به في الغنيمة إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ {وَمَا أَنْزَلْنَا عَلَى عبدنا} يعني: هذه السُّورة {يوم الفرقان} اليوم الذي فرَّقت به بين الحقِّ والباطل {يَوْمَ الْتَقَى الْجَمْعَانِ} حزب الله وحزب الشَّيطان {والله على كل شيء قدير} إذ نصركم الله وأنتم قلة أذلَّةٌ',NULL,'441',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1203,'{إذ أنتم بالعدوة الدنيا} نزولٌ بشفير الوادي الأدنى إلى المدينة وعدوكم نزولٌ بشفير الوادي الأقصى إلى مكَّة {والركب} أبو سفيان وأصحابه وهم أصحاب الإِبل يعني: العير {أسفل منكم} إلى ساحل البحر {ولو تواعدتم} للقتال {لاختلفتم في الميعاد} لتأخَّرتم فنقضتم الْمِيعَادِ لكثرتهم وقلَّتكم {ولكن} جمعكم الله من غير ميعاد {لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولا} في علمه وحكمه من نصر النبي صلى الله عليه وسلم والمؤمنين {ليهلك من هلك عن بينة ويحيا من حيَّ عن بيِّنة} أي: فعل ذلك ليضلَّ ويكفر مَنْ كفر من بعد حجَّةٍ قامت عليه وقطعت عذره ويؤمن من آمن على مثل ذلك وأراد بالبيِّنة نصرة المؤمنين مع قلَّتهم على ذلك الجمع الكثير مع كثرتهم وشوكتهم {وإنَّ الله لسميع} لدعائكم {عليمٌ} بنيَّاتكم',NULL,'441',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1204,'{إذ يريكهم الله في منامك} عينك وهو موضع النَّوم {قليلاً} لتحتقروهم وتجترؤوا عليهم {ولو أراكهم كثيراً لفشلتم} لجّبُنْتُم ولَتأخَّرتم عن حربهم {ولتنازعتم في الأمر} واختلفت كلمتكم {ولكنَّ الله سلَّم} عصمكم وسلَّمكم من المخالفة فيما بينكم {إنه عليم بذات الصدور} علم ما في صدوركم من اليقين ثمَّ خاطب المؤمنين جميعأً بهذا المعنى فقال:',NULL,'442',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1205,'{وَإِذْ يُرِيكُمُوهُمْ إِذِ الْتَقَيْتُمْ فِي أَعْيُنِكُمْ قَلِيلا} قال ابن مسعودٍ: لقد قُلَّلوا في أعيننا يوم بدرٍ حتى قلت لرجلٍ إلى جنبي: تراهم سبعين؟ قال: أراهم مائة وأسرنا رجلاً فقلنا: كم كنتم؟ قال: ألفاً {ويقللكم في أعينهم} ليجترئوا عليكم ولا يرجعوا عن قتالكم {ليقضي الله أمراً كان مفعولاً} في علمه بنصر الإسلام وأهله وذلِّ الشِّرك وأهله {وإلى الله ترجع الأمور} وبعد هذا إليَّ مصيركم فأكرم أوليائي وأعاقب أَعدائي',NULL,'442',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1206,'{يا أيها الذين آمنوا إذا لقيتم فئة} جماعةً كافرةً {فاثبتوا} لقتالهم ولا تنهزموا {واذكروا الله كثيراً} ادعوه بالنَّصر عليهم {لعلكم تفلحون} كي تسعدوا وتبقوا في الجنة فإنَّهما خصلتان إمَّا الغنيمة وإمَّا الشَّهادة',NULL,'443',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1207,'{وأطيعوا الله ورسوله ولا تنازعوا} ولا تختلفوا {فتفشلوا} تجبنوا {وتذهب ريحكم} جَلَدكم وجرأتكم ودولتكم',NULL,'443',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1208,'{ولا تكونوا كالذين خرجوا من ديارهم} يعني: النَّفير {بطراً} طُغياناً في النِّعمة للجميل مع إبطان القبيح {ويصدون عن سبيل الله} لمعاداة المؤمنين وقتالهم {والله بما يعملون محيط} عالم فيجازيهم به',NULL,'443',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1209,'{وإذ زين لهم الشيطان أعمالهم} الآية وذلك أنَّ قريشا لما اجتمعت المسير خافت كنانة وبني مدلج لطوائلَ كانت بينهم فتبدَّى لهم إبليس في جنده على صورة سُراقة بن مالك بن جشعم الكنانيِّ ثمَّ المدلجيِّ فقالوا له: نحنُ نريد قتال هذا الرَّجل ونخاف من قومك فقال لهم: أنا جارٌ لكم أَيْ: حافظٌ من قومي فلا غالب لكم اليوم من النَّاس {فلما تراءت الفئتان} التقى الجمعان {نكص على عقبيه} رجع مولياً فقيل له: يا سراقة أفراراً من غير قتال؟ ! فقال: {إني أرى ما لا ترون} وذلك أنَّه رأى جبريل مع الملائكة جاؤوا لنصر المؤمنين {إني أخاف الله} أن يهلكني فيمن يهلك {وَاللَّهُ شَدِيدُ الْعِقَابِ}',NULL,'443',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1210,'{إِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ} وهم قومٌ أسلموا بمكة ولم يهاجروا فلمَّا خرجت قريش لحرب رسول الله صلى الله عليه وسلم حرجوا معهم وقالوا: نكون مع أكثر الفئتين فلمَّا رأوا قلَّة المسلمين قالوا: {غَرَّ هَؤُلاءِ دِينُهُمْ} إذ خرجوا مع قلَّتهم يقاتلون الجمع الكثير ثمَّ قُتلوا جميعاً مع المشركين قال الله تعالى: {وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ} يُسلم أمره إلى الله {فإنَّ الله عزيز} قويٌّ منيع {حكيم} في خلقه',NULL,'444',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1211,'{ولو ترى} يا محمد {إِذْ يَتَوَفَّى الَّذِينَ كفروا الملائكة} يأخذون أرواحهم يعني: مَنْ قُتلوا ببدرٍ {يضربون وجوههم وأدبارهم} مقاديمهم إذا أقبلوا إلى المسلمين ومآخيرهم إذا ولًّوا {وذوقوا} أَيْ: ويقولون لهم بعد الموت: ذوقوا بعد الموت {عذاب الحريق}',NULL,'444',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1212,'{ذلك} أَيْ: هذا العذاب {بما قدَّمت أيديكم} بما كسبتم وجنيتم {وأنَّ الله ليس بظلام للعبيد} لأنَّه حكم فيما يقضي',NULL,'444',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1213,'{كدأب آل فرعون} الآية يريد: عادة هؤلاء في التَّكذيب كعادة آل فرعون فأنزل الله تعالى بهم عقوبته كما أنزل بآل فرعون {إِنَّ اللَّهَ قَوِيٌّ} قادرٌ لا يغلبه شيء {شديد العقاب} لمَنْ كفر به وكذَّب رسله',NULL,'444',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1214,'{ذلك بأنَّ الله} الآية إنَّ الله تعالى أطعم أهل مكَّة من جوعٍ وآمنهم من خوف وبعث إليهم محمداً رسولاً وكان هذا كلُّه ممَّا أنعم عليهم ولم يكن يُغيِّر عليهم لو لم يُغيِّروا هم وتغييرهم كفرهم بها وتركهم شكرها فلمَّا غيَّروا ذلك غيَّر اللهُ ما بهم فسلبهم النِّعمة وأخذهم ثمَّ نزل في يهود قريظة:',NULL,'444',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1215,'{كَدَأْبِ آلِ فِرْعَوْنَ وَالَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآيَاتِ رَبِّهِمْ فَأَهْلَكْنَاهُمْ بِذُنُوبِهِمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ وكل كانوا ظالمين}',NULL,'445',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1216,'{إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الَّذِينَ كَفَرُوا فهم لا يؤمنون}',NULL,'445',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1217,'{الذين عاهدت منهم} الآية وذلك أنَّهم نقضوا عهد رسول الله صلى الله عليه وسلم وأعانوا عليه مشركي مكَّة بالسِّلاح ثمَّ اعتذروا وقالوا: أخطأنا فعاهدهم ثانيةً فنقضوا العهد يوم الخندق وذلك قوله: {ثُمَّ يَنْقُضُونَ عَهْدَهُمْ فِي كُلِّ مَرَّةٍ وَهُمْ لا يتقون} عقاب الله في ذلك',NULL,'445',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1218,'{فإمَّا تثقفنَّهم في الحرب} فإن أدركتهم في القتال وأسرتهم {فَشَرِّدْ بِهِمْ مَنْ خلفهم} فافعل بهم فعلاً من التَّنكيل والعقوبة يفرق به جمعُ كلِّ ناقضِ عهدٍ فيعتبروا بما فعلت بهؤلاء فلا ينقضوا العهد فذلك قوله تعالى: {لعلهم يذكرون}',NULL,'445',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1219,'{وإمَّا تخافنَّ من قوم} تعلمنَّ من قومٍ {خيانة} نقضاً للعهد بدليلٍ يظهر لك {فانبذ إليهم على سواء} أَي: انبذ عهدهم الذي عاهدتهم عليه لتكون أنت وهم سواءً في العداوة فلا يتوهموا أنَّك نقضت العهد بنصب الحرب أَيْ: أعلمهم أنَّك نقضت عهدهم لئلا يتوهَّموا بك الغدر {إنَّ الله لا يحبُّ الخائنين} الذين يخونون في العهود وغيرها',NULL,'445',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1220,'{ولا يحسبنَّ الذين كفروا سبقوا} وذلك أنَّ مَنْ أفلت من حرب بدرٍ من الكفَّار خافوا أن ينزل بهم هلكة في الوقت فلمَّا لم ينزل طغوا وبغوا فقال الله: لا تحسبنَّهم سبقونا بسلامتهم الآن فـ {إنهم لا يعجزون} نا ولا يفوتوننا فيما يستقبلون من الأوقات',NULL,'446',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1221,'{وأعدوا لهم} أَيْ: خذوا العُدَّة لعدوِّكم {ما استطعتم من قوة} ممَّا تتقوون به على حربهم من السِّلاح والقسي وغيرهما {ومن رباط الخيل} ممَّا يرتبط من الفرس في سبيل الله {ترهبون به} تخوِّفون به بما استطعتم {عدو الله وعدوكم} مشركي مكَّة وكفَّار العرب {وَآخَرِينَ مِنْ دُونِهِمْ} وهم المنافقون {لا تعلمونهم الله يعلمهم} لأنَّهم معكم يقولون: لا إله إلاَّ الله ويغزون معكم والمنافق يريبه عدد المسلمين {وما تنفقوا من شيء} من آلةٍ وسلاحٍ وصفراء وبيضاء {في سبيل الله} طاعة الله {يوف إليكم} يخلف لكم في العاجل ويوفَّر لكم أجره في الآخرة {وَأَنْتُمْ لا تُظْلَمُونَ} لا تنقصون من الثَّواب',NULL,'446',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1222,'{وإن جنحوا للسلم} مالوا إلى الصُّلح {فاجنح لها} فملْ إليها يعني: المشركين واليهود ثمَّ نسخ هذا بقوله: {قاتلوا الذين لا يؤمنون بالله} {وتوكَّل على الله} ثق به {إِنَّهُ هُوَ السَّمِيعُ} لقولكم {الْعَلِيمُ} بما في قلوبكم',NULL,'446',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1223,'{وَإِنْ يُرِيدُوا أَنْ يَخْدَعُوكَ} بالصُّلح لتكفَّ عنهم {فإنَّ حسبك الله} أَيْ: فالذي يتولَّى كفايتك الله {هو الذي أيدك} قوَّاك {بِنَصْرِهِ} يوم بدرٍ {وبالمؤمنين} يعني: الأنصار',NULL,'447',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1224,'{وألف بين قلوبهم} بين قلوب الأوس والحزرج وهم الأنصار {لَوْ أَنْفَقْتَ مَا فِي الأَرْضِ جميعاً ما ألَّفت بين قلوبهم} للعداوة التي كانت بينهم {ولكنَّ الله ألف بينهم} لأنَّ قلوبهم بيده يُؤلِّفها كيف يشاء {إنَّه عزيز} لا يمتنع عليه شيء {حكيم} عليمٌ بما يفعله',NULL,'447',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1225,'{يا أيها النبيُّ حسبك الله} الآية أسلم مع النبي صلى الله عليه وسلم ثلاثةٌ وثلاثون رجلاً وستُّ نسوةٍ ثمَّ أسلم عمر رضي الله عنه فنزلت هذه الآية والمعنى: يكفيك الله ويكفي من اتَّبعك من المؤمنين',NULL,'447',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1226,'{يا أيها النبيُّ حرِّض المؤمنين على القتال} حُضَّهم على نصر دين الله {إِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صَابِرُونَ يَغْلِبُوا مِائَتَيْنِ} يريد: الرَّجل منكم بعشرة منهم في الحرب {وَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُوا أَلْفًا مِنَ الذين كفروا بأنَّهم قومٌ لا يفقهون} أَيْ: هم على جهالةٍ فلا يثبتون إذا صدقتموهم القتال خلاف مَنْ يقاتل على بصيرةٍ يرجو ثواب الله وكان الحكم على هذا زماناً يُصابر الواحد من المسلمين العشرة من الكفَّار فتضرَّعوا وشكوا إلى الله عز وجل ضعفهم فنزل:',NULL,'447',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1227,'{الآن خفف الله عنكم} هوَّن عليكم {وَعَلِمَ أَنَّ فِيكُمْ ضَعْفًا فَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ صَابِرَةٌ يَغْلِبُوا مِائَتَيْنِ وَإِنْ يَكُنْ مِنْكُمْ ألف يغلبوا ألفين} فصار الرَّجل من المسلمين برجلين من الكفَّار وقوله: {بإذن الله} أَيْ: بإرادته ذلك',NULL,'448',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1228,'{مَا كَانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرَى} الآية نزلت في فداء أسارى بدر فادوهم بأربعة ألاف ألف فأنكر الله عزَّ وجل على نبيه صلى الله عليه وسلم ذلك بقوله: لم يكن لنبيِّ أن يحبس كافراً قَدَر عليه للفداء فلا يكون له أيضاً حتى يُثخن في الأرض: يُبالغ في قتل أعدائه {تريدون عرض الدنيا} أي: الفِداء {والله يريد الآخرة} يريد لكم الجنة بقتلهم وهذه الآية بيان عمَّا يجب أن يجتنب من اتِّخاذ الأسرى للمنِّ أو الفِداء قبل الإِثخان في الأرض بقتل الأعداء وكان هذا في يوم بدر ولم يكونوا قد أثخنوا فلذلك أنكر الله عليهم ثمَّ نزل بعده: {فإمَّا منَّاً بعدُ وإمَّا فداءً}',NULL,'448',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1229,'{لولا كتاب من الله سبق} يا محمَّد أنَّ الغنائم وفداء الأسرى لك ولأمَّتك حلال {لَمَسَّكُمْ فِيمَا أَخَذْتُمْ} من الفِداء {عذاب عظيم} فلمَّا نزل هذا أمسكوا أيديهم عمَّا أخذوا من الغنائم فنزل:',NULL,'449',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1230,'{فَكُلُوا مِمَّا غَنِمْتُمْ حَلالا طَيِّبًا وَاتَّقُوا اللَّهَ} بطاعته {إنَّ الله غفور} غفر لكم ما أخذتم من الفِداء {رحيم} رحمكم لأنَّكم أولياؤه',NULL,'449',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1231,'{يأ أيها النَّبِيُّ قُلْ لِمَنْ فِي أَيْدِيكُمْ مِنَ الأَسْرَى إن يعلم الله في قلوبكم خيراً} إرادةً للإِسلام {يؤتكم خيراً مما أخذ منكم} من الفِداء يعني: إِنْ أسلمتم وعلم الله إسلام قلوبكم أخلف عليكم خيراً ممَّا أُخذ منكم {ويغفر لكم} ما كان من كفركم وقتالكم رسول الله صلى الله عليه وسلم',NULL,'449',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1232,'{وإن يريدوا خيانتك} وذلك أنهم قالوا للنبي صلى الله عليه وسلم: آمنَّا بك ونشهد أنَّك رسول الله فقال الله تعالى: إن خانوك وكان قولهم هذا خيانة {فقد خانوا الله من قبل} كفروا به {فأمكن منهم} المؤمنين ببدرٍ وهذا تهديدٌ لهم إن عادوا إلى القتال {والله عليم} بخيانةٍ إن خانوها {حكيم} في تدبيره ومجازاته إيَّاهم',NULL,'449',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1233,'{إنَّ الذين آمنوا وهاجروا} الآية نزلت في الميراث كانوا في ابتداء الإسلام يتوارثون بالهجرة والنُّصرة فكان الرَّجل يُسلم ولا يهاجر فلا يرث أخاه فذلك قوله: {الذين آمنوا وهاجروا} هجروا قومهم وديارهم وأموالهم {وَالَّذِينَ آوَوْا وَنَصَرُوا} يعني: الأنصار أسكنوا المهاجرين ديارهم ونصروهم {أولئك بعضهم أولياء بعض} أيْ: هؤلاء الذين يتوارثون بعضهم من بَعْضٍ
INSERT INTO [b2547] VALUES (1234,'{والذين كفروا بعضهم أولياء بعض} أَيْ: لا توارث بينكم وبينهم ولا ولاية والكافر وليُّ الكافر دون المسلم {إلاَّ تفعلوه} إلاَّ تعاونوا وتناصروا وتأخذوا في الميراث بما أمرتكم به {تكن فتنة في الأرض} شركٌ {وفساد كبير} وذلك أنَّ المسلم إذا هجر قريبه الكافر كان ذلك أدعى إلى الإسلام فإن لم يهجره وتوارثه بقي الكافر على كفره وقوله:',NULL,'450',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1235,'{وَالَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوْا وَنَصَرُوا أُولَئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا} أَيْ: هم الذين حققوا إيمانهم بما يقتضيه من الهجرة والنُّصرة خلاف من أقام بدار الشِّرك',NULL,'450',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1236,'{وَالَّذِينَ آمَنُوا مِنْ بَعْدُ وَهَاجَرُوا وَجَاهَدُوا مَعَكُمْ فأولئك منكم} يعني: الذين هاجروا بعد الحديبية وهي الهجرة الثانية {وأولو الأرحام بعضهم أولى ببعض} نسخ الله الميراث بالهجرة والحِلْفِ بعد فتح مكَّة ردَّ الله المواريث إلى ذوي الأرحام: ابن الأخ والعمِّ وغيرهما {في كتاب الله} في حكم الله {إن الله بكل شيء عليم}',NULL,'450',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1237,'{براءة من الله ورسوله} الآية أخذت المشركون ينقضون عهوداً بينهم وبين رسول الله صلى الله عليه وسلم فأمره الله تعالى أن ينقض عهودهم وينبذها إليهم وأنزل هذه الآية والمعنى: قد برئ الله ورسوله من إعطائهم العهود والوفاء بها إذ نكثوا ثمَّ خاطب المشركين فقال:',NULL,'452',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1238,'{فسيحوا في الأرض أربعة أشهر} سيروا فيها آمنين حيث شئتم يعني: شوالاً إلى صفر وهذا تأجيلٌ من الله سبحانه للمشركين فإذا انقضت هذه المدَّة قُتلوا حيثما أُدركوا {واعلموا أنكم غير معجزي الله} لا تفوتونه وإنْ أُجِّلتم هذه المدَّة {وأنَّ الله مُخْزي الكافرين} مذلُّهم في الدُّنيا بالقتل والعذاب في الآخرة',NULL,'452',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1239,'{وأذان من الله} إعلامٌ منه {ورسوله إلى الناس} يعني: العرب {يوم الحج الأكبر} يوم عرفة وقيل: يوم النَّحر والحجُّ الأكبر الحجُّ بجميع أعماله والأصغر العمرة {أنَّ الله بريء من المشركين ورسولُهُ} أمر الله رسوله صلى الله عليه وسلم أن يُعلم مشركي العرب في يوم الحجِّ الأكبر ببراءته من عهودهم فبعث عليا رضي الله عنه حيث قرأ صدر براءة عليهم يوم النَّحر ثمَّ خاطب المشركين فقال: {فإن تبتم} رجعتم عن الشِّرك {فهو خيرٌ لكم} من الإِقامة عليه {وإن توليتم} عن الإِيمان {فَاعْلَمُوا أَنَّكُمْ غَيْرُ معجزي الله} لا تفوتونه بأنفسكم عن العذاب ثمَّ أوعدهم بعذاب الآخرة فقال: {وَبَشِّرِ الَّذِينَ كفروا بعذاب أليم} ثمَّ استثنى قوماً من براءة العهود فقال:',NULL,'452',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1240,'{إِلا الَّذِينَ عَاهَدْتُمْ مِنَ الْمُشْرِكِينَ ثُمَّ لَمْ ينقصوكم} من شروط العهد {شيئاً} وهم بني ضمرة وبنو كنانة {وَلَمْ يُظَاهِرُوا عَلَيْكُمْ أَحَدًا} لم يعاونوا عليكم عدوَّاً {فأتموا إليهم عهدهم إلى مدَّتهم} إلى انقضاء مدَّتهم وكان قد بقي لهم من مدَّتهم تسعة أشهر فأمر النبي صلى الله عليه وسلم بإتمامها لهم {إنَّ الله يحب المتقين} مَنِ اتَّقاه بطاعته',NULL,'453',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1241,'{فإذا انسلخ الأشهر الحرم} يعني: مدَّة التَّأجيل {فاقتلوا المشركين حيث وجدتموهم} في حلٍّ أو حرمٍ {وخذوهم} بالأسر {واحصروهم} إنْ تحصَّنوا {واقعدوا لهم كلَّ مرصد} على كلِّ طريقٍ تأخذون فيه {فإنْ تابوا} رجعوا عن الشرك {وأقاموا الصلاة} المفروضة {وآتوا الزكاة} من العين والثِّمار والمواشي {فخلوا سبيلهم} فدعوهم وما شاؤوا {إن الله غفور رحيم} لمَنْ تاب وآمن',NULL,'454',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1242,'{وَإِنْ أَحَدٌ مِنَ الْمُشْرِكِينَ} الذين أمرتك بقتلهم {استجارك} طلب منك الأمان من القتل {فأجره} فاجعله في أمنٍ {حتى يسمع كلام الله} القرآن فتقيم عليه حجَّةَ الله وتبيِّن له دين الله {ثمَّ أبلغه مأمنه} إذا لم يرجع عن الشِّرك لينظر في أمره {ذلك بأنهم قومٌ لا يعلمون} يفعلونَ كلَّ هذا لأنَّهم قومٌ جهلةٌ لا يعلمون دين الله وتوحيده',NULL,'454',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1243,'{كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِنْدَ اللَّهِ وَعِنْدَ رسوله} مع إضمارهم الغدر ونكثهم العهد {إلاَّ الذين عاهدتم عند المسجد الحرام} يعني: الذين استثناهم من البراءة {فما استقاموا لكم فاستقيموا لهم} ما أقاموا على الوفاء بعهدهم فأقيموا أنتم',NULL,'454',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1244,'{كيف} أَيْ: كيف يكون لهم عهدهم {و} حالُهم أنهم {إن يظهروا عليكم} يظفروا بكم ويقدروا عليكم {لا يَرْقُبُوا فِيكُمْ} لا يحفظوا فيكم {إلاًّ ولا ذمَّةً} قرابةً ولا عهداً {يرضونكم بأفواههم} يقولون بألسنتهم كلاماً حلواً {وتأبى قلوبهم} الوفاء به {وأكثرهم فاسقون} غادرون ناقضون للعهد',NULL,'454',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1245,'{اشْتَرَوْا بِآيَاتِ اللَّهِ ثَمَنًا قَلِيلا} استبدلوا بالقرآن متاع الدُّنيا {فَصَدُّوا عَنْ سَبِيلِهِ} فأعرضوا عن طاعته {إنهم ساء} بئس {مَا كَانُوا يَعْمَلُونَ} من اشترائهم الكفر بالإِيمان',NULL,'455',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1246,'{لا يرقبون} يعني: هؤلاء النَّاقضين للعهد {وَأُولَئِكَ هُمُ الْمُعْتَدُونَ} المجاوزون للحلال إلى الحرام بنقض العهد',NULL,'455',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1247,'{فإن تابوا} عن الشِّرك {وأقاموا الصَّلاة وآتوا الزكاة فإخوانكم} أَيْ: فهم إخوانكم {في الدين ونفصِّل الآيات} نبيِّن آيات القرآن {لقوم يعلمون} أنَّها من عند الله',NULL,'455',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1248,'{وإن نكثوا أيمانهم} نقضوا عهودهم {وطعنوا في دينكم} اغتابوكم وعابوا دينكم {فقاتلوا أئمة الكفر} رؤساء الضَّلالة يعني: صناديد قريش {إنهم لا أيمان لهم} لا عهود لهم {لعلهم ينتهون} كي ينتهوا عن الشِّرك بالله ثمَّ حرَّض المؤمنين عليهم فقال:',NULL,'455',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1249,'{ألا تقاتلون قوماً نكثوا أيمانهم} يعني: كفَّار مكَّة نقضوا العهد وأعانوا بني بكر على خزاعة {وهموا بإخراج الرسول} من مكَّة {وهم بدؤوكم} بالقتال {أول مرة} حين قاتلوا حلفاءكم خزاعة فبدؤوا بنقض العهد {أتخشونهم} أن ينالكم من قتالهم مكروه فتتركون قتالهم {فالله أحق أن تخشوه} فمكروهُ عذابِ الله أحقُّ أن يُخشى في ترك قتالهم {إن كنتم مؤمنين} مصدِّقين بعقاب الله وثوابه',NULL,'455',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1250,'{قاتلوهم يعذبهم الله بأيديكم} يقتلهم بسيوفكم ورماحكم {ويخزهم} يُذلُّهم بالقهر والأسر {ويشف صدور قوم مؤمنين} يعني: بني خزاعة أعانت قريشٌ بني بكر عليهم حتى نكثوا فيهم فشفى الله صدورهم من بني بكر بالنبيِّ والمؤمنين',NULL,'456',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1251,'{ويذهب غيظ قلوبهم} كَرْبَها ووَجْدَها بمعونة قريش بكراً عليهم {وَيَتُوبُ الله على من يشاء} من المشركين كأبي سفيان وعكرمة بن أبي جهل وسهيل بن عمرو هداهم الله للإسلام',NULL,'456',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1252,'{أم حسبتم} أيُّها المنافقون {أن تتركوا} على ما أنتم عليه من التَّلبيس وكتمان النفاق {ولما يعلم الله الذين جاهدوا منكم} بنيَّةٍ صادقةٍ يعني: العلم الذي يتعلَّق بهم بعد الجهاد وذلك أنَّه لما فُرض القتال تبيَّن المنافق من غيره ومَنْ يوالي المؤمنين ممَّن يوالي أعداءهم {ولم يتخذوا} أَيْ: ولمَّا يعلم الله الذين لم يَتَّخِذُوا {مِنْ دُونِ اللَّهِ وَلا رَسُولِهِ وَلا المؤمنين وليجة} أولياء ودُخُلاً',NULL,'456',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1253,'{مَا كَانَ لِلْمُشْرِكِينَ أَنْ يَعْمُرُوا مَسَاجِدَ اللَّهِ} نزلت في العباس بن عبد المطلب حين عير بالكفر لمَّا أُسر فقال: إنَّا لنعمر المسجد الحرام ونحجب الكعبة ونسقي الحاجَّ فردَّ الله ذلك عليه بقوله: {مَا كَانَ لِلْمُشْرِكِينَ أَنْ يَعْمُرُوا مَسَاجِدَ اللَّهِ} بدخوله والتعوُّذ فيه لأنَّهم ممنوعون عن ذلك {شاهدين على أنفسهم بالكفر} بسجودهم للأصنام واتِّخاذها آلهة {أولئك حبطت أعمالهم} لأنَّ كفرهم أذهب ثوابها',NULL,'456',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1254,'{إنما يعمر مساجد الله} بزيارتها والقعود فيها {مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَأَقَامَ الصَّلاةَ وآتى الزكاة} والمعنى: إنَّ مَنْ كان بهذه الصفة فهو من أهل عمارة المسجد {ولم يخش} في باب الدِّين {إلاَّ الله فعسى أولئك} أَيْ: فأولئك هم المهتدون والمتمسكون بطاعة الله التي تؤدِّي إلى الجنَّة',NULL,'457',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1255,'{أجعلتم سقاية الحاج} قال المشركون: عمارة بيت الله وقيامٌ على السِّقاية خيرٌ من الإِيمان والجهاد فأنزل الله تعالى هذه الآية وسقاية الحاج: سقيهم الشَّراب في الموسم وقوله: {وعمارة المسجد الحرام} يريد: تجميره وتخليقه {كمَنْ آمن} أَيْ: كإيمان من آمن {بالله} ؟ {لا يستون عند الله} في الفضل {والله لا يهدي القوم الظالمين} يعني: الذين زعموا أنَّهم أهل العمارة سمَّاهم ظالمين بشركهم',NULL,'457',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1256,'{الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ بأموالهم وأنفسهم أعظم درجة عند الله} من الذين افتخروا بعمارة البيت وسقي الحاجِّ {وأولئك هم الفائزون} الذين ظفروا بأمنيتهم',NULL,'458',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1257,'{يبشرهم ربهم برحمة منه} الآية أَيْ: يعلمهم في الدُّنيا ما لهم في الآخرة',NULL,'458',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1258,'{خَالِدِينَ فِيهَا أَبَدًا إِنَّ اللَّهَ عِنْدَهُ أَجْرٌ عظيم}',NULL,'458',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1259,'{يا أيها الذين آمنوا لا تتخذوا آباءكم} الآية لمَّا أُمر رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم بالهجرة إلى المدينة كان من النَّاس مَنْ يتعلَّق به زوجته وولده وأقاربه ويقولون: ننشدك بالله أن تضيِّعنا فيرقُّ لهم ويدع الهجرة فأنزل الله تعالى: {لا تتخذوا آباءكم وإخوانكم أولياء} أصدقاء تُؤثرون المقام بين أظهرهم على الشجرة {إن استحبوا} اختاروا {الْكُفْرَ عَلَى الإِيمَانِ وَمَنْ يَتَوَلَّهُمْ مِنْكُمْ فَأُولَئِكَ هم الظالمون} أَيْ: مشركون مثلهم فلمَّا نزلت هذه الآية قالوا: يا نبيَّ الله إن نحن اعتزلنا مَنْ خالفنا في الدِّين نقطع آباءنا وعشائرنا وتذهب تجارتنا وتخرب ديارنا فأنزل الله تعالى:',NULL,'458',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1260,'{قُلْ إِنْ كَانَ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ وَإِخْوَانُكُمْ وَأَزْوَاجُكُمْ وعشيرتكم وأموال اقترفتموها} أَيْ: اكتسبتموها {فتربصوا} مقيمين بمكَّة {حتى يأتي الله بأمره} فتح مكَّة فيسقط فرض الهجرة وهذا أمر تهديد {والله لا يهدي القوم الفاسقين} تهديدٌ لهؤلاء بحرمان الهداية',NULL,'458',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1261,'{لَقَدْ نَصَرَكُمُ اللَّهُ فِي مَوَاطِنَ كَثِيرَةٍ وَيَوْمَ حنين} وهو وادٍ بين مكَّة والطَّائف قاتل عليه نبيُّ الله عليه السَّلام هوازن وثقيفاً {إذ أعجبتكم كثرتكم} وذلك أنَّهم قالوا: لن نُغلب اليوم من قلَّةٍ وكانوا اثني عشر ألفاً {فلم تغن} لم تدفع عنكم شيئاً {وَضَاقَتْ عَلَيْكُمُ الأَرْضُ بِمَا رحبت} لشدَّة ما لحقكم من الخوف ضاقت عليكم الأرض على سعتها فلم تجدوا فيها موضعاً يصلح لقراركم {ثم وليتم مدبرين} انهزمتم أعلمهم الله تعالى أنَّهم ليسوا يغلبون بكثرتهم إنَّما يَغلبون بنصر الله',NULL,'459',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1262,'{ثم أنزل الله سكينته} وهو ما يسكن إليه القلب من لطف الله ورحمته {عَلَى رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَنْزَلَ جُنُودًا لَمْ تروها} يريد: الملائكة {وعذب الذين كفروا} بأسيافكم ورماحكم {وَذَلِكَ جَزَاءُ الْكَافِرِينَ}',NULL,'459',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1263,'{ثُمَّ يَتُوبُ اللَّهُ مِنْ بَعْدِ ذَلِكَ عَلَى من يشاء} فيهديهم إلى الإِسلام من الكفَّار {والله غفور رحيم} بمَنْ آمن',NULL,'459',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1264,'{يا أيها الذين آمنوا إنما المشركون نجس} لا يغتسلون من جنابةٍ ولا يتوضؤون من حدثٍ {فلا يقربوا المسجد الحرام} أَيْ: لا يدخلوا الحرم مُنعوا من دخول الحرم فالحرمُ حرامٌ على المشركين {بعد عامهم هذا} يعني: عام الفتح فلمَّا مُنعوا من دخول الحرم قال المسلمون: إنَّهم كانوا يأتون بالميرة فالآن تنقطع عنا المتاجر فأنزل الله تعالى: {وإن خفتم عيلة} فقراً {فسوف يغنيكم الله من فضله} فأسلم أهل جدَّة وصنعاء وجرش وحملوا الطَّعام إلى مكَّة وكفاهم الله ما كانوا يتخوَّفون {إنَّ الله عليم} بما يصلحكم {حكيم} فيما حكم في المشركين ثمَّ نزل في جهاد أهل الكتاب من اليهود والنَّصارى قوله:',NULL,'459',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1265,'{قَاتِلُوا الَّذِينَ لا يُؤْمِنُونَ بِاللَّهِ وَلا بِالْيَوْمِ الآخر} يعني: كإيمان الموحِّدين وإيمانُهم غيرُ إيمانٍ إذا لم يؤمنوا بمحمد {ولا يحرِّمون ما حرم الله ورسوله} يعني: الخمر والميسر {ولا يدينون دين الحق} لا يتدينون بدين الإِسلام {حتى يعطوا الجزية} وهي ما يعطي المعاهِد على عهده {عن يد} يعطونها بأيديهم يمشون بها كارهين ولا يجيئون بها ركباناً ولا يرسلون بها {وهم صاغرون} ذليلون مقهورون يُجَرُّون إلى الموضع الذي تقبض منهم فيه بالعنف حتى يؤدُّوها من يدهم',NULL,'460',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1266,'{وَقَالَتِ الْيَهُودُ عُزَيْرٌ ابْنُ اللَّهِ وَقَالَتِ النَّصَارَى المسيح ابن الله ذلك قولهم بأفواههم} ليس فيه برهانٌ ولا بيانٌ إنَّما هو قولٌ بالفم فقط {يُضاهئون} يتشبَّهون بقول المشركين حين قالوا: الملائكة بنات الله وقد أخبر الله عنهم بقوله: {وخرقوا له بنين وبناتٍ} {قاتلهم الله} لعنهم الله {أنى يؤفكون} كيف يُصرفون عن الحقِّ بعد وضوح الدَّليل حتى يجعلوا لله الولد وهذا تعجيب للنبي صلى الله عليه وسلم والمؤمنين',NULL,'460',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1267,'{اتخذوا أحبارهم ورهبانهم} علماءهم وعُبَّادهم {أرباباً} آلهةً {من دون الله} حيث أطاعوهم في تحليل ما حرَّم الله وتحريم ما أحلَّ الله {والمسيح ابن مريم} اتخذوه ربَّاً {وما أمروا} في التَّوراة والإِنجيل {إلاَّ ليعبدوا إلهاً واحداً} وهو الذي لا إله غيره {سبحانه عمَّا يشركون} تنزيهاً له عن شركهم',NULL,'461',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1268,'{يريدون أن يطفئوا نور الله بأفواههم} يخمدوا دين الإِسلام بتكذيبهم {وَيَأْبَى اللَّهُ إِلا أَنْ يُتِمَّ نُورَهُ} إلاَّ أَنْ يُظهر دينه',NULL,'461',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1269,'{هو الذي أرسل رسوله} محمداً {بالهدى} بالقرآن {ودين الحق} الحنيفيَّة {لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ} ليعليَه على جميع الأديان',NULL,'461',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1270,'{يا أيها الَّذِينَ آمَنُوا إِنَّ كَثِيرًا مِنَ الأَحْبَارِ وَالرُّهْبَانِ} من فقهاء أهل الكتاب وعلمائهم {ليأكلون أموال الناس بالباطل} يعني: ما يأخذونه من الرُّشا في الحكم {وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ} ويصرفون النَّاس عن الإِيمان بمحمد عليه السلام ثم أنزل في مانعي الزَّكاة من أهل القبلة: {وَالَّذِينَ يَكْنِزُونَ الذَّهَبَ وَالْفِضَّةَ وَلا يُنْفِقُونَهَا فِي سبيل الله} لا يُؤَدُّون زكاتها {فبشرهم بعذاب أليم} أخبرهم أنَّ لهم عذاباً أليماً',NULL,'462',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1271,'{يوم يحمى عليها} يوم تدخل كنوزهم النَّار حتى تحمى وتشتدَّ حرارتها {فتكوى بها} أي: فتلصق بجباههم وجنوبهم وظهورهم حتى يلتقي الحرُّ في أجوافهم ويقال لهم: هذا الذي تكوون به ما جمعتم لأنفسكم وبخلتم به عن حقِّ الله {فذوقوا} العذاب بـ {ما كنتم تكنزون}',NULL,'462',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1272,'{إِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللَّهِ اثْنَا عَشَرَ شهراً} عدد شهور المسلمين التي تُعبِّدوا بأن يجعلوها لسنتهم اثنا عشر شهراً على منازل القمر واستهلال الأهلَّة لا كما يعدُّه أهل الرُّوم وفارس {في كتاب الله} في الإِمام الذي عند الله كتبه يوم خلق السماوات والأرض {منها أربعة حرم} رجب وذو القعدة وذو الحجة والمحرم يعظم انتهاك المحارم فيها بأشدَّ ممَّا يعظم في غيرها {ذلك الدين القيم} الحساب المستقيم {فلا تظلموا فيهن أنفسكم} تحفَّظوا من أنفسكم في الحرم فإنَّ الحسنات فيهن تضعف وكذلك السيئات {وقاتلوا المشركين كافة كما يقاتلونكم كافة} قاتلوهم كلَّهم ولا تحابوا بترك القتال كما إنَّهم يستحلُّون قتال جميعكم {واعلموا أنَّ الله مع المتقين} مع أوليائه الذين يخافونه',NULL,'462',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1273,'{إنما النسيء} تأخير حرمةِ شهرٍ حرَّمه الله إلى شهرٍ آخر لم يحرِّمه وذلك أنَّ العرب في الجاهليَّة ربما كانت تستحلُّ المحرم وتحرِّم بدله صفر فأخبر الله تعالى أنَّ ذلك كلَّه {زيادة في الكفر} حيث أحلُّوا ما حرَّم الله وحرَّموا ما أحلَّ الله {يضل به} بذلك التَّأخير {الذين كفروا يحلونه عاماً ويحرمونه عاماً} إذا قاتلوا فيه أحلُّوه وحرَّموا مكانه صفر وإذا لم يقاتلوا فيه حرَّموه {ليواطئوا} ليوافقوا {عدَّة ما حرم الله} وهو أنَّهم لم يُحلُّوا شهراً من الحرم إلاَّ حرَّموا مكانه شهراً من الحلال ولم يحرِّموا شهراً من الحلال إلاَّ أحلُّوا مكانه شهراً من الحرم لئلا يكون الحرم أكثر من الأربعة كما حرَّم الله فيكون موافقة للعدد {زين لهم سوء أعمالهم} زيَّن لهم الشَّيطان ذلك',NULL,'463',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1274,'{يا أيها الذين آمنوا ما لكم} نزلت في حثِّ المؤمنين على غزوة تبوك وذلك أنَّهم دُعوا إليها في زمان عسرةٍ من النَّاس وجدبٍ من البلاد وشدةٍ من الحرِّ فشقَّ عليهم الخروج فأنزل الله تعالى: {مَا لَكُمْ إِذَا قِيلَ لَكُمُ انْفِرُوا فِي سبيل الله} أخرجوا في الجهاد لحرب العدوِّ {اثاقلتم إلى الأرض} أَحْبَبْتُمْ المقام {أرضيتم بالحياة الدنيا} بدلاً {من الآخرة} يعني: الجنَّة {فما متاع الحياة الدنيا في الآخرة} يريد: الدُّنيا كلَّها {إلاَّ قليل} عند شئ من الجنَّة',NULL,'463',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1275,'{إلاَّ تنفروا} تخرجوا مع نبيِّكم إلى الجهاد {يُعَذِّبْكُمْ عَذَابًا أليماً} بالقحط وحبس المطر {ويستبدل قوماً غيركم} يأت بقومٍ آخرين ينصرُ بهم رسوله {ولا تضرّوه شيئاً} لأنَّ الله عصمه عن النَّاس ولا يخذله أَنْ تثاقلتم كما لم يضرَّه قلَّة ناصريه حين كان بمكَّة وهم به الكفَّار فتولَّى الله نصره وهو قوله:',NULL,'464',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1276,'{إِلا تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللَّهُ إِذْ أَخْرَجَهُ الذين كفروا} أَيْ: اضطروه إلى الخروج لمَّا همُّوا بقتله فكانوا سبباً لخروجه من مكَّة هارباً منهم {ثاني اثنين} أَيْ: واحد اثنين هو صلى الله عليه وسلم وأبو بكر رضي الله عنه والمعنى: مصره الله منفرداً إلاَّ من أبي بكر: {إذْ هما في الغار} هو غارٌ في جبل مكة يقال له: ثور {إذْ يقول لصاحبه} أبي بكر: {لا تحزن} وذلك أنَّه خاف على رسول الله صلى الله عليه وسلم الطَّلب فقال رسول الله صلى الله عليه وسلم: {لا تحزن إنَّ الله معنا} يمنعهم منَّا وينصرنا {فأنزل الله سكينته} ألقى في قلب أبي بكر ما سكن به {وأيده} أَيْ: رسوله {بجنود لم تروها} قوَّاه وأعانه بالملائكة يوم بدر أخبر أنَّه صرف عنه كيد أعدائه ثمَّ أظهره: نصره بالملائكة يوم بدر {وجعل كلمة الذين كفروا} وهي كلمة الشِّرك {السفلى وكلمة الله هي العليا} يعني: كلمة التَّوحيد لأنَّها علت وظهرت وكان هذا يوم بدر',NULL,'464',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1277,'{انفروا خفافاً وثقالاً} شباباً وشيوخاً {وَجَاهِدُوا بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ فِي سَبِيلِ اللَّهِ ذَلِكُمْ خير لكم} من التَّثاقل إلى الأرض {إن كنتم تعلمون} ما لكم من الثَّواب والجزاء ثمَّ نزل في المنافقين الذين تخلَّفوا عن هذه الغزوة:',NULL,'465',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1278,'{لو كان عرضاً قريباً} أَيْ: لو كان ما دُعوا إليه غنيمةً قريبةً {وَسَفَرًا قَاصِدًا} قريباً هيِّناً {لاتَّبَعُوكَ} طمعاً في الغنيمة {ولكن بعدت عليهم الشقة} المسافة {وسيحلفون بالله} عندك إذا رجعت إليهم {لو استطعنا لخرجنا معكم} لو قدرنا وكان لنا سعةٌ من المال {يُهْلِكُونَ أَنْفُسَهُمْ} بالكذب والنِّفاق {وَاللَّهُ يَعْلَمُ إنهم لكاذبون} لأنَّهم كانوا يستطيعون الخروج',NULL,'465',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1279,'{عفا الله عنك لم أذنت لهم} كان رسول الله صلى الله عليه وسلم أذن لطائفةٍ في التَّخلُّف عنه من غير مؤامرةٍ ولم يكن له أن يمضي شيئاً إلاَّ بوحي فعاتبه الله سبحانه وقال: لم أَذنت لهم في التَّخلُّف {حَتَّى يَتَبَيَّنَ لَكَ الَّذِينَ صَدَقُوا وَتَعْلَمَ الْكَاذِبِينَ} حتى تعرف مَنْ له العذر منهم ومَنْ لا عذر له فيكون إذنك لمَنْ له العذر',NULL,'465',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1280,'{لا يَسْتَأْذِنُكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ} في القعود والتَّخلُّف عن الجهاد كراهة {أن يجاهدوا} في سبيل الله {بأموالهم وأنفسهم} الآية',NULL,'465',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1281,'{إنما يستأذنك} في التَّخلُّف {الَّذِينَ لا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَارْتَابَتْ قلوبهم} شكُّوا في دينهم {فَهُمْ فِي رَيْبِهِمْ يَتَرَدَّدُونَ} في شكِّهم يتمادون',NULL,'466',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1282,'{ولو أرادوا الخروج لأعدُّوا له عدَّة} من الزَّاد والركوب لأنَّهم كانوا مياسير {ولكن كره الله انبعاثهم} لم يرد خروجهم معك {فثبطهم} فخذلهم وكسَّلهم {وقيل اقعدوا} وحياً إلى قلوبهم يعني: إنَّ الله ألهمهم أسباب الخذلان {مع القاعدين} الزَّمنى وأولي الضَّرر ثمَّ بّيَّنَ لِمَ كره خروجهم فقال:',NULL,'466',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1283,'{لَوْ خَرَجُوا فِيكُمْ مَا زَادُوكُمْ إِلا خَبَالا} يقول: لو خرجوا لأفسدوا عليكم أمركم {ولأوضعوا خلالكم} لأسرعوا بالنَّميمة في إفساد ذاتِ بينكم {يبغونكم الفتنة} يُثبِّطونكم ويفرِّقون كلمتكم حتى تنازعوا فتفتتنوا {وفيكم سماعون لهم} مَنْ يسمع كلامهم ويطيعهم ولو صحبهم هؤلاء المنافقون أفسدوهم عليكم {والله عليم بالظالمين} المنافقين',NULL,'466',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1284,'{لقد ابتغوا الفتنة من قبل} طلبوا لك الشَّرَّ والعنتَ قبل تبوك وهو أنَّ جماعةً منهم أرادوا الفتك به ليلة العقبة {وقلَّبوا لك الأمور} اجتهدوا في الحيلة عليك والكيد بك {حتى جاء الحق} الآية أَيْ: حتى أخزاهم الله بإظهار الحقِّ وإعزاز الدِّين على كُرهٍ منهم',NULL,'466',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1285,'{وَمِنْهُمْ مَنْ يَقُولُ ائْذَنْ لِي} نزلت في جدِّ بن قيس المنافق قال لرسول الله رسول صلى الله عليه وسلم: هل لك في جلاد بني الأصفر تتحذ منهم سراري وُصفاءَ فقال: ائذن لي يا رسول الله في القعود عنك وأُعينك بمالي {ولا تفتني} ببنات بني الأصفر فإني مُسْتَهترٌ بالنِّساء إني أخشى إن رأيتهنَّ ألا أصبر عنهنَّ فقال الله تعالى: {أَلا فِي الْفِتْنَةِ سَقَطُوا} أَيْ: في الشِّرك وقعوا بنفاقهم وخلفهم أمرك {وإنَّ جهنم لمحيطة بالكافرين} لمحدقةٌ بمَنْ كفر جامعةٌ لهم',NULL,'467',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1286,'{إن تصبك حسنة} نصرٌ وغنيمةٌ {تسؤهم وإن تصبك مصيبة} من قتلٍ وهزيمةٍ {يقولوا قد أخذنا أمرنا من قبل} قد أخذنا حذرنا وعملنا بالحزم حين تخلَّفنا {ويتولوا} وينصرفوا {وهم فرحون} معجبون بذلك وبما نالك من السُّوء',NULL,'467',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1287,'{قُلْ لن يصيبنا} خيرٌ ولا شرٌّ {إلاَّ} وهو مقدَّرٌ مكتوبٌ علينا {هو مولانا} ناصرنا {وعلى الله فليتوكل المؤمنون} وإليه فليفوِّض المؤمنون أمورهم على الرِّضا بتدبيره',NULL,'467',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1288,'{قل هل تربصون بنا} هل تنتظرون أن يقع بنا {إِلا إِحْدَى الحسنيين} الغنيمة أو الشَّهادة {ونحن نتربص} ننتظر {بكم أَنْ يُصِيبَكُمُ اللَّهُ بِعَذَابٍ مِنْ عنده} بقارعةٍ من السَّماء {أو بأيدينا} يأذن لنا في قتلكم فنقتلكم {فَتَرَبَّصُوا إِنَّا مَعَكُمْ مُتَرَبِّصُونَ} فانتظروا مواعيد الشَّيطان إنَّا منتظرون مواعيد الله من إظهار دينه وهلاك مَنْ خالفه ثمَّ ذكر في الآية الثَّانية والثَّالثة أنَّه لا يقبل منهم ما أنفقوا في الجهاد لأنَّ منهم مَنْ قال لرسول الله صلى الله عليه وسلم: اقعد وأُعينك بمالي فأخبر الله تعالى أنه لا يقبل ذلك فعلوه طائعين أو مكرهين وبيَّن أنَّ المانع لقبول ذلك كفرهم بالله ورسوله وكسلهم في الصَّلاة لأنَّهم لا يرجون لها ثواباً وكراهتهم الإِنفاق في سبيل الله لأنَّهم يعدونه مغرما',NULL,'467',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1289,'{قُلْ أَنْفِقُوا طَوْعًا أَوْ كَرْهًا لَنْ يُتَقَبَّلَ مِنْكُمْ إِنَّكُمْ كُنْتُمْ قَوْمًا فَاسِقِينَ}',NULL,'468',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1290,'{وَمَا مَنَعَهُمْ أَنْ تُقْبَلَ مِنْهُمْ نَفَقَاتُهُمْ إِلا أَنَّهُمْ كَفَرُوا بِاللَّهِ وَبِرَسُولِهِ وَلا يَأْتُونَ الصَّلاةَ إِلا وَهُمْ كُسَالَى وَلا يُنْفِقُونَ إِلا وَهُمْ كارهون}',NULL,'468',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1291,'{فلا تعجبك أموالهم ولا أولادهم} لا تستحسن ما أنعمنا عليهم من الأموال الكثيرة والأولاد {إِنَّمَا يُرِيدُ اللَّهُ لِيُعَذِّبَهُمْ بِهَا فِي الْحَيَاةِ الدنيا} يعني: بالمصائب فيها فهي لهم عذابٌ وللمؤمن أجر {وتزهق أنفسهم} وتخرج أرواحهم {وهم} على الكفر',NULL,'468',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1292,'{ويحلفون بالله إنهم لمنكم} أَيْ: إنَّهم مؤمنون وليسوا مؤمنين {ولكنهم قوم يفرقون} يخافون فيحلفون تقيَّةً لكم',NULL,'468',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1293,'{لو يجدون ملجأً} مهرباً {أو مغارات} سراديب {أو مدخلاً} وجهاً يدخلونه {لوَلَّوا إليه} لرجعوا إليه {وهم يجمحون} يُسرعون إسراعاً لا يردُّ وجوهَهم شيءٌ أَيْ: لو أمكنهم الفرار من بين المسلمين بأيِّ وجهٍ كان لفروا لوم يُقيموا بينهم',NULL,'468',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1294,'{ومنهم} ومن المنافقون {من يلمزك} يعيبك وطعن عليك {في} أمر {الصدقات} يقول: إنَّما يعطيها محمَّد مَنْ أحبَّ فإنْ أكثرت لهم من ذلك فرحوا وإنْ أعطيتهم قليلاً سخطوا ثمَّ ذكر في الآية الثَّانية أنَّهم لو رضوا بذلك وتوكَّلوا على الله لكان خيراً لهم وهو قوله:',NULL,'469',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1295,'{وَلَوْ أَنَّهُمْ رَضُوا مَا آتَاهُمُ اللَّهُ وَرَسُولُهُ وَقَالُوا حَسْبُنَا اللَّهُ سَيُؤْتِينَا اللَّهُ مِنْ فَضْلِهِ ورسوله إنا إلى الله راغبون} ثمَّ بيَّن لمن الصَّدقات فقال:',NULL,'469',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1296,'{إنما الصدقات للفقراء} وهم المُتعفِّفون عن السُّؤال {والمساكين} الذين يسألون ويطوفون على النَّاس {والعاملين عليها} السُّعاة لجباية الصَّدَقة {والمؤلفة قلوبهم} كانوا قوماً من أشراف العرب استألفهم رسول الله صلى الله عليه وسلم ليردُّوا عنه قومهم ويُعينوه على عدوِّه {وفي الرقاب} المكاتبين {والغارمين} أهل الدِّيْن {وفي سبيل الله} الغزاة والمرابطون {وابن السبيل} المنقطع في سفره {فريضة من الله} افترضها الله على الأغنياء في أموالهم',NULL,'469',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1297,'{ومنهم الذين يؤذون النبيَّ} بنقل حديثه وعيبه {ويقولون هو أذنٌ} أنَّهم قالوا فيما بينهم: نقول ما شئنا ثمَّ نأتيه فَنَحْلِفُ له فيصدِّقنا لأنَّه أُذنٌ والأُذن: الذي يسمع كلَّ ما يُقال له فقال الله تعالى {قُلْ أُذُنُ خَيْرٍ لَكُمْ} أَيْ: مستمعُ خيرٍ وصلاح لا مستمع شرٍّ وفسادٍ ثمَّ أَكَّد هذا وبيَّنه فقال: {يؤمن بالله} أَيْ: يسمع ما ينزله الله عليه فيصدِّق به {ويؤمن للمؤمنين} ويصدِّق المؤمنين فيما يخبرونه لا الكافرين {وَرَحْمَةٌ لِلَّذِينَ آمَنُوا مِنْكُمْ} أَيْ: وهو رحمةٌ لأنَّه كان سبب إيمانهم',NULL,'469',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1298,'{يحلفون بالله لكم ليرضوكم} يحلف هؤلاء المنافقون فيما بلغكم عنهم من أذى الرَّسول والطَّعن عليه أنَّهم ما أتوا ذلك ليرضوكم بيمينهم {والله ورسوله أحقُّ أن يرضوه} فيؤمنوا بهما ويصدِّقوهما إن كانوا على ما يظهرون',NULL,'470',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1299,'{أَلَمْ يَعْلَمُوا أَنَّهُ مَنْ يُحَادِدِ اللَّهَ وَرَسُولَهُ فَأَنَّ لَهُ نَارَ جَهَنَّمَ خَالِدًا فِيهَا ذَلِكَ الخزي العظيم}',NULL,'470',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1300,'{يحذر المنافقون أن تنزل عليهم} على المؤمنين {سورة} تخبرهم {بما في قلوبهم} من الحسد لرسول الله صلى الله عليه وسلم والمؤمنين وذلك أنَّهم كانوا يفرقون من هتكهم وفضيحتهم {قُلِ اسْتَهْزِئُوا} أمرُ وعيدٍ {إِنَّ اللَّهَ مُخْرِجٌ} مظهرٌ {ما تحذرون} ظهوره',NULL,'470',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1301,'{ولئن سَأَلْتَهُمْ} عمَّا كانوا فيه من الاستهزاء {ليقولنَّ إنما كنا نخوض ونلعب} وذلك أنَّ رجلاً من المنافقين قال في غزوة تبوك: ما رأيتُ مثل هؤلاء أرغبَ بطوناً ولا أكذبَ أَلْسُناً ولا أجبنَ عند اللِّقاء يعني: رسول الله صلى الله عليه وسلم والمؤمنين فأُخبر رسولُ الله صلى الله عليه وسلم بذلك فجاء هذا القائل ليعتذر فوجد القرآن قد سبقه فقال: يا رسول الله إنما كنَّا نخوض ونلعب ونتحدَّث بحديث الرَّكب نقطع به عنا الطريق وهو معنى قوله: {إنَّما كنا نخوض} أَيْ: في الباطل من الكلام كما يخوض الرَّكب فَقَالَ لَهُ رَسُولُ اللَّهِ صَلَّى اللَّهُ عَلَيْهِ وسلم: {أَبِاللَّهِ وَآيَاتِهِ وَرَسُولِهِ كُنْتُمْ تَسْتَهْزِئُونَ}',NULL,'470',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1302,'{لا تعتذروا قد كفرتم بعد إيمانكم} أَيْ: ظهر كفركم بعد إظهاركم الإِيمان {إِنْ نَعْفُ عن طائفة منكم نعذب طائفة} وذلك أنَّهم كانوا ثلاثة نفر فهزئ اثنان وضحك واحد وهو المعفو عنه فلمَّا نزلت هذه الآية برئ من النِّفاق',NULL,'471',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1303,'{المنافقون والمنافقات بعضهم من بعض} على دين بعض {يَأْمُرُونَ بِالْمُنْكَرِ} بالكفر بمحمد صلى الله عليه وسلم {وينهون عن المعروف} عن اتِّباعه {وَيَقْبِضُونَ أَيْدِيَهُمْ} عن النَّفقة في سبيل الله {نسوا الله فنسيهم} تركوا أمر الله فتركهم من كلِّ خيرٍ وخذلهم {إنَّ المنافقين هم الفاسقون} الخارجون عمَّا أمر الله',NULL,'471',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1304,'{وعد الله المنافقين} الآية ظاهرة ثم خاطبهم فقال:',NULL,'471',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1305,'{كالذين من قبلكم} أَيْ: فعلتم كأفعال الذين من قبلكم {فاستمتعوا بخلاقهم} رضوا بنصيبهم من الدُّنيا ففعلتم أنتم أيضاً مثل ما فعلوا {وخضتم} في الطَّعن على النبي صلى الله عليه وسلم كما خاضوا في الطَّعن على أنبيائهم {أُولَئِكَ حَبِطَتْ أَعْمَالُهُمْ في الدنيا والآخرة} لأنَّها لا تُقبل منهم ولا يُثابون عليها',NULL,'471',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1306,'{ألم يأتهم نبأ الذين من قبلهم} ألم يأتهم خبر الذين أُهلكوا في الدُّنيا بذنوبهم فيتَّعظوا ثم ذكرهم {قوم نوح وعاد وثمود وقوم إبراهيم} يعني: نمروذ {وأصحاب مدين} قوم شعيب {والمؤتَفِكاتِ} وأصحاب المؤتفكات وهي قرى قوم لوط {فما كان الله ليظلمهم} ليعذِّبهم قبل بعث الرَّسول {ولكن كانوا أنفسهم يظلمون} بتكذيب الرُّسل',NULL,'472',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1307,'{والمؤمنون والمؤمنات بعضهم أولياء بعض} في الرَّحمة والمحبَّة {يأمرون بالمعروف} يدعون إلى الإِسلام {وينهون عن المنكر} الشِّرك بالله الآية',NULL,'472',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1308,'{وَعَدَ اللَّهُ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِنْ تحتها الأنهار خالدين فيها ومساكن طيبة} يريد قصور الزَّبرجد والدُّرِّ والياقوت {في جنات عدن} هي قصبة الجنَّة وسقفُها عرش الرَّحمن {ورضوان من الله أكبر} ممَّا يوصف',NULL,'472',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1309,'{يا أيها النبيُّ جاهد الكفار} بالسَّيف {والمنافقين} باللِّسان والحُجَّة {واغلظ عليهم} يريد شدَّة الانتهار والنَّظر بالبغضة والمقت',NULL,'472',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1310,'{يحلفون بالله ما قالوا} نزلت حين أساء المنافقون القول في رسول الله صلى الله عليه وسلم وطعنوا في الدِّين وقالوا: إذا قدمنا المدينة عقدنا على رأس عبد الله ابن أُبيّ تاجاً يباهي به رسول الله - صلى الله عليه وسلم - فَسُعِي بذلك إلى رسول الله صلى الله عليه وسلم فدعاهم فحلفوا ما قالوا {ولقد قالوا كلمة الكفر} سبَّهم الرَّسول وطعنهم في الدِّين {وهموا بما لم ينالوا} من عقدهم التَّاج على رأس ابن أُبيّ وقيل: من الاغتيال بالرَّسول {وما نقموا} كرهوا {إِلا أَنْ أَغْنَاهُمُ اللَّهُ وَرَسُولُهُ مِنْ فَضْلِهِ} بالغنيمة حتى صارت لهم الأموال أَيْ: إنَّهم عملوا بضدِّ الواجب فجعلوا موضع شكر الغنى أن نقموه ثمَّ عرض عليهم التَّوبة فقال: {فَإِنْ يَتُوبُوا يَكُ خَيْرًا لَهُمْ وَإِنْ يَتَوَلَّوْا} يعرضوا عن الإِيمان {يُعَذِّبْهُمُ اللَّهُ عَذَابًا أَلِيمًا فِي الدُّنْيَا} بالقتل {و} في {الآخِرَةِ} بالنار {وَمَا لَهُمْ فِي الأَرْضِ مِنْ وَلِيٍّ وَلا نَصِيرٍ} لا يتولاَّهم أحدٌ من المسلمين',NULL,'473',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1311,'{ومنهم مَنْ عاهد الله} يعني: ثعلبة بن حاطب عاهد ربَّه لئن وسَّعَ عليه أن يؤتى كلَّ ذي حقٍ حقَّه ففعل الله ذلك فلم يفِ بما عاهد ومنع الزَّكاة فهذا معنى قوله: {لئن آتانا من فضله لنصدقنَّ} لنعطينَّ الصَّدقة {ولنكوننَّ من الصالحين} ولنعملنَّ ما يعمل أهل الصَّلاح في أموالهم',NULL,'473',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1312,'{فلما آتاهم من فضله بخلوا به} الآية',NULL,'474',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1313,'{فأعقبهم نفاقاً} صيَّر عاقبة أمرهم إلى ذلك بحرمان التَّوبة حتى ماتوا على النِّفاق جزاءً لإخلافهم الوعد وكذبهم في العهد وهو قوله: {إلى يوم يلقونه} الآية',NULL,'474',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1314,'{أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ سِرَّهُمْ وَنَجْوَاهُمْ وأن الله علام الغيوب}',NULL,'474',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1315,'{الذين يلمزون} يعيبون ويغتابون {المطوعين} المتطوعين المُتنفلِّين {من المؤمنين في الصدقات} وذلك أن رسول الله صلى الله عليه وسلم حثَّ على الصَّدقة فجاء بعض الصحابة بالمال الكثير وبعضهم - وهم الفقراء - بالقليل فاغتابهم المنافقون وقالوا: مَنْ أكثر رياءً ومَنْ أقلَّ أراد أن يذكر نفسه فأنزل الله تعالى هذه الآية: {والذين لا يجدون إلاَّ جهدهم} وهو القليل الذي يتعيَّش به {فيسخرون منهم سخر الله منهم} جازاهم سخريتهم حيث صاروا إلى النَّار ثمَّ آيس الله رسول من إيمانهم ومغفرتهم فقال:',NULL,'474',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1316,'{استغفر لهم أو لا تستغفر لهم} وهذا تخييرٌ لرسول الله صلى الله عليه وسلم ثمَّ قال: {إن تستغفر لهم سبعين مرة} أَيْ: إن اسكتثرت من الدُّعاء بالاستغفار للمنافقين لن يغفر الله لهم',NULL,'475',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1317,'{فرح المخلفون} يعني: الذين تخلَّفوا عن رسول الله صلى الله عليه وسلم من المنافقين {بمقعدهم} بقعودهم {خلاف رسول الله} مخالفةً له {وقالوا لا تنفروا} مع محمدٍ إلى تبوك {فِي الْحَرِّ قُلْ نَارُ جَهَنَّمَ أَشَدُّ حَرًّا لو كانوا يفقهون} يعلمون أنَّ مصيرهم إليها',NULL,'475',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1318,'{فليضحكوا قليلاً} في الدُّنيا لأنَّها تنقطع عنهم {وليبكوا كثيراً} في النار بكاءً لا ينقطع {جزاءً بما كانوا يكسبون} في الدُّنيا من النِّفاق',NULL,'475',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1319,'{فإن رجعك الله} ردَّك {إلى طائفة منهم} يعني: الذين تخلَّفوا بالمدينة {فاستأذنوك للخروج} إلى الغزو معك {فَقُلْ لَنْ تَخْرُجُوا مَعِيَ أَبَدًا} إلى غزاةٍ {وَلَنْ تُقَاتِلُوا مَعِيَ عَدُوًّا} من أهل الكتاب {إنكم رضيتم بالقعود أَوَّلَ مَرَّةٍ} حين لم تخرجوا إلى تبوك {فاقعدوا مع الخالفين} يعني: النِّساء والصِّبيان والزَّمنى الذين يخلفون الذَّاهبين إلى السَّفر ثمَّ نُهِيّ رسول اللَّه صلى الله عليه وسلم عن الصَّلاة عليهم إذا ماتوا والدُّعاء لهم عند الوقوف على القبر فقال:',NULL,'475',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1320,'{وَلا تُصَلِّ عَلَى أَحَدٍ مِنْهُمْ مَاتَ أَبَدًا ولا تقم على قبره} الآية',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1321,'{ولا تعجبك أموالهم} مضى تفسيره',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1322,'{وَإِذَا أُنْزِلَتْ سُورَةٌ أَنْ آمِنُوا بِاللَّهِ وَجَاهِدُوا مع رسوله استأذنك أولو الطول منهم} يعني: أصحاب الغنى والقدرة يستأذنونك في التَّخلُّف',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1323,'{رضوا بأن يكونوا مع الخوالف} النِّساء اللاتي يخلفن في البيت {وطبع على قلوبهم} بالنِّفاق {فهم لا يفقهون} لا يفهمون الإِيمان وشرائعه وأمر الله',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1324,'{لَكِنِ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ جَاهَدُوا بِأَمْوَالِهِمْ وَأَنْفُسِهِمْ وَأُولَئِكَ لَهُمُ الْخَيْرَاتُ وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1325,'{أَعَدَّ اللَّهُ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فيها ذلك الفوز العظيم}',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1326,'{وجاء المعذِّرون} المعتذرون وهم قوم {من الأعراب} اعتذروا إلى رسول الله صلى الله عليه وسلم في التَّخلُّف فعذرهم وهو قوله {ليؤذن لهم} أَيْ: في القعود {وقعد الذين كذبوا الله ورسوله} لم يُصدِّقوا نبيَّه واتَّخذوا إسلامهم جُنَّة ثمًّ ذكر أهل العذر فقال:',NULL,'476',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1327,'{ليس على الضعفاء} يعني: الزَّمنى والمشايخ والعجزى {وَلا عَلَى الْمَرْضَى وَلا عَلَى الَّذِينَ لا يَجِدُونَ مَا يُنْفِقُونَ حَرَجٌ إِذَا نَصَحُوا لِلَّهِ ورسوله} أخلصوا أعمالهم من الغِشِّ لهما {ما على المحسنين من سبيل} من طريق بالعقابِ لأنَّه قد سُدَّ طريقه بإحسانه {والله غفور رحيم} لمن كان على هذه الخصال',NULL,'477',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1328,'{وَلا عَلَى الَّذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ} نزلت في سبعة نفرٍ سألوا رسول الله صلى الله عليه وسلم أن يحملهم على الدَّوابِّ فقال: {لا أجد ما أحملكم عليه} فانصرفوا باكين شوقاً إلى الجهاد وحزناً لضيق ذات اليد',NULL,'477',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1329,'{إنما السبيل على الذين يستأذنونك وهم أغنياء رضوا بأن يكونوا مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون}',NULL,'477',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1330,'{يَعْتَذِرُونَ إِلَيْكُمْ} بالأباطيل {إِذَا رَجَعْتُمْ إِلَيْهِمْ} من هذه الغزوة {قُلْ لا تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ} لن نصدِّقكم {قد نبأنا الله من أخباركم} قد أخبرنا الله بسرائركم وما تخفي صدوركم {وَسَيَرَى الله عملكم ورسوله} فيما تستأنفون تبتم من النِّفاق أم أقمتم عليه {ثمَّ تردون إِلَى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ} إلى مَنْ يعلم ما غاب عنّا من ضمائركم {فَيُنَبِّئُكُمْ بِمَا كنتم تعملون} فيخبركم بما كنتم تكتمون وتسرون',NULL,'477',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1331,'{سيحلفون بالله لكم إذا انقلبتم} إذا رجعتم {إليهم} من تبوك أنَّهم ما قدروا على الخروج {لِتُعْرِضُوا عَنْهُمْ} إعراض الصَّفح {فَأَعْرِضُوا عَنْهُمْ} اتركوا كلامهم وسلامهم {إِنَّهُمْ رِجْسٌ} إنَّ عملهم قبيح من عمل الشيطان ثمَّ نزل في أعاريب أسد وغطفان:',NULL,'478',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1332,'{يحلفون لكم لترضوا عنهم فإن ترضوا عنهم فَإِنَّ اللَّهَ لا يَرْضَى عَنِ الْقَوْمِ الْفَاسِقِينَ}',NULL,'478',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1333,'{الأعراب أشدُّ كفراً ونفاقاً} من أهل المدر لأنَّهم أجفى وأقسى {وأجدر} وأولى وأحق {أن لا يعلموا حدود ما أنزل الله} من الحلال والحرام',NULL,'478',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1334,'{وَمِنَ الأَعْرَابِ مَنْ يَتَّخِذُ مَا يُنْفِقُ مَغْرَمًا} لأنَّه لا يرجو له ثواباً {وَيَتَرَبَّصُ بِكُمُ الدوائر} وينتظر أن ينقلب الأمر عليكم بموت الرَّسول عليه السَّلام {عليهم دائرة السوء} عليهم يدور البلاء والخزي فلا يرون في محمد ودينه إلاَّ ما يسوءهم ثمَّ نزل في مَنْ أسلم منهم:',NULL,'478',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1335,'{وَمِنَ الأَعْرَابِ مَنْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الآخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبَاتٍ عِنْدَ اللَّهِ} يتقرَّب بذلك إلى الله عز وجل {وصلوات الرسول} يعني: دعاءه بالخير والبركة والمعنى: أنَّه يتقرَّب بصدقته ودعاء الرَّسول إلى الله {ألاَ إنّها قربة لهم} أَيْ: نورٌ ومكرمةٌ عند الله',NULL,'478',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1336,'{والسابقون الأولون} يعني: الذين شهدوا بدراً {من المهاجرين والأنصار} يعني: الذين آمنوا منهم قبل قدوم الرَّسول عليهم فهؤلاء السُّبَّاق من الفريقين وقيل: أراد كلَّ مَنْ أدركه من أصحابه فإنَّهم كلَّهم سبقوا هذه الأمة بصحبة النبي صلى الله عليه وسلم ورؤيته {والذين اتبعوهم بإحسان} يعني: ومن اتبعهم على مناهجهم إلى يوم القيامة ممَّن يُحسن القول فيهم',NULL,'479',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1337,'{وممن حولكم من الأعراب منافقون} يعني: مزينة وجهينة وغفاراً {ومن أهل المدينة} الأوس والخزرج {مردوا على النفاق} لجُّوا فيه وأبوا غيره {سنعذبهم مرتين} بالأمراض والمصائب في الدُّنيا وعذاب القبر {ثم يردون إلى عذاب عظيم} وهو الخلود في النَّار',NULL,'479',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1338,'{وآخرون اعترفوا بذنوبهم} في التخلف عن الغزو {خلطوا عملاً صالحاً} وهو جهادهم مع النبي صلى الله عليه وسلم قبل هذا {وآخر سيئاً} تقاعدهم عن هذه الغزوة {عَسَى الله} واجبٌ من الله {أَنْ يَتُوبَ عليهم إن الله غفور رحيم} ثمَّ تاب على هؤلاء وعذرهم فقالوا: يا رسول الله هذه أموالنا التي خلفَتْنا عنك فخذها منَّا صدقةً وطهِّرنا واستغفر لنا فقال رسول الله صلى الله عليه وسلم: ما أُمرت أن آخذ من أموالكم شيئا فأنزل الله سبحانه:',NULL,'479',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1339,'{خذ من أموالهم صدقة} فأخذ رسول الله صلى الله عليه وسلم ثلث أموالهم وكانت كفَّارةً للذُّنوب التي أصابوها وهو قوله: {تطهرهم} يعني: هذه الصَّدقة تطهِّرهم من الذُّنوب {وتزكيهم بها} أَيْ: ترفعهم أنت يا محمَّدُ بهذه الصَّدقة من منازل المنافقين {وصل عليهم} ادع لهم {إِنَّ صَلاتَكَ سَكَنٌ لَهُمْ} إنَّ دعواتك ممَّا تسكن نفوسهم إليه بأن قد تاب الله عليهم {والله سميع} لقولهم {عليم} بندامتهم فلمَّا نزلت توبة هؤلاء قال الذين لم يتوبوا من المتخلِّفين: هؤلاء كانوا بالأمس معنا لا يُكلَّمون ولا يُجالسون فما لهم؟ وذلك أن النبي صلى الله عليه وسلم لمَّا رجع إلى المدينة نهى المؤمنين عن مكالمة المنافقين ومجالستهم فأنزل الله تعالى سبحانه:',NULL,'480',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1340,'{أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهِ وَيَأْخُذُ الصَّدَقَاتِ} يقبلها {وَأَنَّ اللَّهَ هو التواب الرحيم} يرجع على مَنْ يرجع إليه بالرَّحمة والمغفرة',NULL,'480',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1341,'{وقل اعملوا} يا معشر عبادي المحسن والمسيء {فَسَيَرَى اللَّهُ عملكم ورسوله والمؤمنون} أَيْ: إنَّ الله يُطلعهم على ما في قلوب إخوانهم من الخير والشَّرِّ فيحبون المحسن ويبغضون المسيء بإيقاع الله ذلك في قلوبهم وباقي الآية سبق تفسيره',NULL,'480',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1342,'{وآخرون مُرْجَوْنَ لأَمْرِ اللَّهِ} مُؤخَّرون ليقضي الله فيهم ما هو قاضٍ وهم كعب بن مالك وهلال بن أمية ومرارة بن الربيع كانوا تخلَّفوا من غير عذر ثمَّ لم يبالغوا في الاعتذار كما فعل أولئك الذين تصدَّقوا بأموالهم فوقف رسولُ الله صلى الله عليه وسلم أمرهم وهم مهجورون حتَّى نزل قوله: {وَعَلَى الثَّلاثَةِ الَّذِينَ خُلِّفُوا} الآيات {إمَّا يعذبهم} بعقابه جزاءً لهم {وإمَّا يتوب عليهم} بفضله {والله عليم} بما يؤول إليه حالهم {حَكِيمٌ} فيما يفعله بهم',NULL,'480',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1343,'{والذين اتخذوا} ومنهم الذين اتَّخذوا مسجداً وكانوا اثني عشر رجلاً من المنافقين بنوا مسجداً يضارُّون به مسجد قباء وهو قوله: {ضرارا وكفرا} بالنبي صلى الله عليه وسلم وما جاء به {وتفريقاً بين المؤمنين} يفرِّقون به جماعتهم لنهم كانوا يصلُّون جميعاً في مسجد قباء فبنوا مسجد الضِّرار ليصلِّي فيه بعضهم فيختلفوا بسبب ذلك {وإرصاداً} وانتظاراً {لمن حارب الله ورسوله من قبل} يعني: أبا عامرٍ الرَّاهب كان قد خرج إلى الشَّام ليأتي بجندٍ يحارب بهم رسول الله صلى الله عليه وسلم وأرسل إلى المنافقين أن ابنوا لي مسجداً {وليحلفنَّ إن أردنا} ببنائه {إلاَّ} الفعلة {الحسنى} وهي الرِّفق بالمسلمين والتَّوسعة عليهم فلمَّا بنوا ذلك المسجد سألوا النبي صلى الله عليه وسلم أن يأتيهم فيصلِّي بهم في ذلك المسجد فنهاه الله عز وجل وقال:',NULL,'481',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1344,'{لا تَقُمْ فِيهِ أَبَدًا لَمَسْجِدٌ أُسِّسَ عَلَى التقوى} بُنيت جُدُره ورُفعت قواعده على طاعة الله تعالى {من أول يوم} بُني وحَدث بناؤه وهو مسجد رسول الله صلى الله عليه وسلم وقيل: هو مسجد قباء {أحقُّ أن تقوم فيه} للصَّلاة {فيه رجال} يعني: الأنصار {يحبون أن يتطهروا} يعني: غسل الأدبار بالماء وكان من عادتهم في الاستنجاء استعمال الماء بعد الحجر {والله يحب المطهرين} من الشِّرك والنِّفاق',NULL,'481',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1345,'{أفمن أسس بنيانه} أَيْ: بناءه الذي بناه {على تقوى من الله} مخافة الله ورجاء ثوابه وطلب مرضاته {خَيْرٌ أَمْ مَنْ أَسَّسَ بُنْيَانَهُ عَلَى شَفَا جرف هار} على حرف مهواةٍ {فانهار به} أُوقع بنيانه {في نار جهنم} وهذا مَثَل والمعنى: إنَّ بناء هذا المسجد كبناءٍ على حرفِ جهنَّم يتهوَّر بأهله فيها لأنَّه معصيةٌ وفعلٌ لما كرهه الله من الضِّرار',NULL,'482',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1346,'{لا يَزَالُ بُنْيَانُهُمُ الَّذِي بَنَوْا رِيبَةً فِي قلوبهم} شكَّاً في قلوبهم {إلاَّ أن تقطَّع قلوبهم} بالموت والمعنى: لا يزالون في شكٍّ منه إلى الموت يحسبون أنَّهم كانوا في بنائه محسنين {والله عليم} بخلقه {حكيم} فيما جعل لكلِّ أحدٍ',NULL,'482',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1347,'{إِنَّ اللَّهَ اشْتَرَى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوَالَهُمْ} الآية نزلت في بيعة العقبة لمَّا بايعت الأنصار رسول الله صلى الله عليه وسلم على أن يعبدوا الله ولا يشركوا به شيئاً وأن يمنعوه ممَّا يمنعون أنفسهم قالوا: فإذا فعلنا ذلك يا رسول الله فماذا لنا؟ قال: الجنَّة قالوا: ربح البيع لا نقيل ولا نستقيل فنزلت هذه الآية ومعنى: {اشْتَرَى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَأَمْوَالَهُمْ بِأَنَّ لَهُمُ الجنة} أنَّ المؤمن إذا قاتل في سبيل الله حتى يُقتل وأنفق ماله في سبيل الله أخذ من الجنَّة في الآخرة جزاءً لما فعل وقوله: {وعداً} أَيْ: وعدهم اللَّهُ الجنَّة وعداً {عليه حقاً} لا خلف فيه {في التوراة والإِنجيل والقرآن} أَيْ: إنَّ الله بيَّن في الكتابين أنَّه اشترى من أمة محمَّدٍ أنفسهم وأموالهم بأنَّ لهم الجنَّة كما بيَّن في القرآن {وَمَنْ أَوْفَى بِعَهْدِهِ مِنَ اللَّهِ} أَيْ: لا أحدٌ أوفى بما وعد من الله ثمًّ مدحهم فقال:',NULL,'482',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1348,'{التائبون} أَيْ: هم التَّائبون من الشِّرك {العابدون} يرون عبادة الله واجبةً عليهم {الْحَامِدُونَ} الله على كلِّ حال {السائحون} الصًّائمون {الراكعون الساجدون} في الفرائض {الآمرون بالمعروف} بالإِيمان بالله وفرائضه وحدوده {والناهون عن المنكر} الشِّرك وترك فرائض الله {والحافظون لحدود الله} العاملون بما افترض الله عليهم',NULL,'483',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1349,'{ما كان للنبيِّ} الآية نزلت في استغفار النبيِّ عليه السَّلام لعمِّه أبي طالب وأبيه وأُمِّه واستغفار المسلمين لآبائهم المشركين نُهوا عن ذلك وكان رسول الله صلى الله عليه وسلم قد قال: لأستغفرنَّ لأبي كما استغفر إبراهيم لأبيه فبيَّن الله سبحانه كيف كان ذلك فقال:',NULL,'483',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1350,'{وَمَا كَانَ اسْتِغْفَارُ إِبْرَاهِيمَ لأَبِيهِ إِلا عَنْ موعدة وعدها إياه} وذلك أنَّه كان قد وعده أن يستغفر له رجاء إسلامه وأن ينقله الله باستغفاره إيَّاه من الكفر إلى الإِسلام وهذا ظاهر في قوله: {سأستغفر لك ربي} وقوله: {لأستغفرنَّ لك} فلمَّا مات أبوه مشركاً تبرَّأ منه وقطع الاستغفار {إنَّ إبراهيم لأوَّاهٌ} دَعَّاءٌ كثير البكاء {حليم} لم يعاقب أحداً إلاََ في الله ولم ينتصر من أحدٍ إلاَّ لله فلمَّا حرَّم الاستغفار للمشركين بين أنهم لم يأخذهم بما فعلوا لأنَّه لم يكن قد بيَّن لهم أنَّه لا يجوز ذلك فقال:',NULL,'484',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1351,'{وَمَا كَانَ اللَّهُ لِيُضِلَّ قَوْمًا بَعْدَ إِذْ هداهم} ليوقع الضَّلالة في قلوبهم بعد الهدى {حتى يبيِّن لهم ما يتقون} فلا يتَّقوه فعند ذلك يستحقُّون الإضلال',NULL,'484',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1352,'{أنَّ الله له ملك السماوات وَالأَرْضِ يُحْيِي وَيُمِيتُ وَمَا لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ ولا نصير}',NULL,'485',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1353,'{لقد تاب الله على النبيِّ} مِنْ إذنه للمنافقين في التَّخلُّف عنه وهو ما ذُكر في قوله: {عفا الله عنك} الآية {وَالْمُهَاجِرِينَ وَالأَنْصَارِ الَّذِينَ اتَّبَعُوهُ فِي سَاعَةِ الْعُسْرَةِ} في زمان عسرة الظَّهر وعسرة الماء وعسرة الزَّاد {مِنْ بَعْدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٍ منهم} من بعد ما همَّ بعضهم بالتَّخلُّف عنه والعصيان ثمَّ لحقوا به {ثم تاب عليهم} ازداد عنهم رضا',NULL,'484',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1354,'{وعلى الثلاثة الذين خلفوا} أَي: عن التَّوبة عليهم يعني: مَنْ ذكرناهم في قوله: {وأخرون مرجون لأمر الله} {حتى إذا ضاقت عليهم الأرض} لأَنَّهم كانوا مهجورين لا يُعاملون ولا يُكلَّمون {وضاقت عليهم أنفسهم} بالهمِّ الذي حصل فيها {ظنوا} أيقنوا {أَنْ لا مَلْجَأَ مِنَ اللَّهِ إِلا إِلَيْهِ} أن لا مُعتَصَم من عذاب الله إلاَّ به {ثمَّ تاب عليهم ليتوبوا} أَيْ: لطف بهم في التَّوبة ووفَّقهم لها',NULL,'485',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1355,'{يا أيها الذين آمنوا} يعني: أهل الكتاب {اتقوا الله} بطاعته {وكونوا مع الصادقين} محمدٍ وأصحابه يأمرهم أن يكونوا معهم في الجهاد والشدة والرخاء وقوله:',NULL,'485',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1356,'{ولا يرغبوا بأنفسهم عن نفسه} لا يرضوا لأنفسهم بالخفض والدَّعَة ورسولُ الله صلى الله عليه وسلم في الحرِّ والمشقَّة {ذَلِكَ} أَيْ: ذلك النَّهي عن التَّخلُّف {بِأَنَّهُمْ لا يُصِيبُهُمْ ظَمَأٌ} وهو شدَّة العطش {وَلا نصب} إعياء من التَّعب {ولا مخمصة} مجاعةٌ {ولا يطؤون موطئاً} ولا يقفون موقفاً {يغيظ الكفار} يُغضبهم {وَلا يَنَالُونَ مِنْ عَدُوٍّ نَيْلا} أسراً وقتلاً إلاَّ كان ذلك قُربةً لهم عند الله',NULL,'485',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1357,'{ولا ينفقون نفقة صغيرة ولا كبيرة} تمرةً فما فوقها {ولا يقطعون واديا} يُجاوزونه في سيرهم {إِلا كُتِبَ لَهُمْ} آثارهم وخُطاهم {لِيَجْزِيَهُمُ اللَّهُ أحسن} بأحسن {ما كانوا يعملون} فلمَّا عِيبَ مَنْ تخلَّف عن غزوة تبوك قال المسلمون: والله لا نتخلف من غزوةٍ بعد هذا ولا عن سرية أبداً فلمَّا أمر رسول الله صلى الله عليه وسلم بالسَّرايا إلى العدُّو نفر المسلمون جميعاً إلى الغزو وتركوا رسول الله صلى الله عليه وسلم وحده بالمدينة فأنزل الله عز وجل:',NULL,'486',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1358,'{وما كان المؤمنون لينفروا كافة} ليخرجوا جميعاً إلى الغزو {فَلَوْلا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طَائِفَةٌ} فهلاَّ خرج إلى الغزو من كل قبيلةٍ جماعةٌ {ليتفقهوا في الدين} ليتعلَّموا القرآن والسُّنن والحدود يعني: الفرقة القاعدين {ولينذروا قومهم إذا رجعوا إليهم} وليعلِّموهم ما نزل من القرآن ويخوفوهم يه {لعلهم يحذرون} فلا يعملون بخلاف القرآن',NULL,'486',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1359,'{يا أيها الذين آمنوا قاتلوا الذين يلونكم} يقربون منكم أُمروا بقتال الأدنى فالأدنى من عدوِّهم من المدينة {وليجدوا فيكم غلظة} شدَّةً وعُنفاً',NULL,'486',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1360,'{وإذا ما أنزلت سورة فمنهم} من المنافقين {مَنْ يَقُولُ أَيُّكُمْ زَادَتْهُ هَذِهِ إيماناً} يقوله المنافقون بعضهم لبعض هزؤاً فقال الله تعالى: {فَأَمَّا الَّذِينَ آمَنُوا فَزَادَتْهُمْ إِيمَانًا} تصديقاً لأنَّهم صدَّقوا بالأولى والثَّانية {وَهُمْ يَسْتَبْشِرُونَ} يفرحون بنزول السُّورة',NULL,'487',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1361,'{وأما الذين في قلوبهم مرض} شكٌّ ونفاقٌ {فزادتهم رجساً إلى رجسهم} كفراً إلى كفرهم لأنَّهم كلَّما كفروا بسورةٍ ازداد كفرهم',NULL,'487',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1362,'{أو لا يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِي كُلِّ عَامٍ مَرَّةً أو مرتين} يُمتحنون بالأمراض والأوجاع وهنَّ روائد الموت {ثمَّ لا يتوبون} من النِّفاق ولا يتَّعظون كما يتَّعظ المؤمن بالمرض',NULL,'487',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1363,'{وإذا ما أنزلت سورة} كان إذا نزلت سورةٌ فيها عيبُ المنافقين وتلا عليهم رسول الله صلى الله عليه وسلم شقَّ ذلك عليهم و {نظر بعضهم إلى بعض} يريدون الهرب من عند رسول الله صلى الله عليه وسلم وقال بعضهم لبعض: {هل يراكم من أحد} إنْ قمتم فإن لم يرهم أحدٌ خرجوا من المسجد وإنْ علموا أنَّ أحداً يراهم ثبتوا مكانهم حتى يفرغ من خطبته {ثم انصرفوا} على عزم الكفر والتَّكذيب {صرف الله قلوبهم} عن كلِّ رشدٍ وهدى {بأنهم قوم لا يفقهون} جزاءً على فعلهم وهو أنَّهم لا يفقهون عن الله دينه وما دعاهم الله إليه',NULL,'487',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1364,'{لقد جاءكم رسول من أنفسكم} من العرب من بني إسماعيل ليفهموا منه {عزيز عليه ما عنتم} شديدٌ عليه مشقَّتكم وكلُّ مضرَّة تُصيبكم {حريص عليكم} أن تؤمنوا وهذا خطابٌ للكفَّار ومَنْ لم يؤمن به ثمَّ ذكر أنَّه {بالمؤمنين رؤوف رحيم}',NULL,'488',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1365,'{فإن تولوا} أعرضوا عن الإِيمان يعني: المشركين والمنافقين {فقل حسبي الله} أَيْ: الذي يكفيني اللَّهُ {لا إِلَهَ إِلا هُوَ عَلَيْهِ تَوَكَّلْتُ} وبه وثقت {وهو رب العرش العظيم} خصَّ بالذِّكر لأنه أعظم ما خلق الله عز وجل',NULL,'488',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1366,'{الر} أنا الله أرى {تلك آيات الكتاب} هذه الآيات التي أنزلتها عليك آيات القرآن {الحكيم} الحاكم بين النَّاس',NULL,'489',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1367,'{أكان للناس} أَهلِ مكَّةَ {عجباً أن أوحينا إلى رجل منهم} وذلك أنَّهم قالوا: ما وجدَ الله مَنْ يُرسله إلينا إلاَّ يتيم أبي طالب؟ ! {أن أنذر الناس وبشر الذين آمنوا} أَيْ: بعثناه بشيراً ونذيراً {أنَّ لهم قدم صدق عند ربهم} يعني: الأعمال الصَّالحة {قال الكافرون إنَّ هذا} القرآن {لسحرٌ مبين}',NULL,'489',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1368,'{إنَّ ربكم الله} مفسَّرةٌ في سورة الأعراف وقوله: {يدبِّر الأمر} يقضيه {مَا مِنْ شَفِيعٍ إِلا مِنْ بَعْدِ إِذْنِهِ} ردٌّ لقولهم: الأصنام شفعاؤنا عند الله',NULL,'489',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1369,'{إِلَيْهِ مَرْجِعُكُمْ جَمِيعًا وَعْدَ اللَّهِ حَقًّا إِنَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِ وَالَّذِينَ كَفَرُوا لَهُمْ شَرَابٌ مِنْ حَمِيمٍ وَعَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْفُرُونَ}',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1370,'{هو الذي جعل الشمس ضياءً} ذات ضياءٍ {والقمر نوراً} ذا نورٍ {وقدَّره} وقدَّر له {منازل} على عدد أيام الشَّهر {ما خلق الله ذلك} يعني: ما تقدَّم ذكره {إلاَّ بالحق} بالعدل أَيْ: هو عادلٌ في خلقه لم يخلقه ظلما ولا باطلاً {يفصِّل الآيات} يُبيِّنها {لقوم يعلمون} يستدلُّون بها على قدرة الله',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1371,'{إِنَّ فِي اخْتِلافِ اللَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ الله في السماوات والأرض لآيات لقوم يتقون}',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1372,'{إنَّ الَّذِينَ لا يَرْجُونَ لِقَاءَنَا} لا يخافون البعث {وَرَضُوا بِالْحَيَاةِ الدُّنْيَا} بدلاً من الآخرة {وَاطْمَأَنُّوا بها} وركنوا إليها {والذين هم عن آياتنا} ما أنزلتُ من الحلال والحرام والشرائع {غافلون} وقوله:',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1373,'{أولئك مأواهم النار بما كانوا يكسبون}',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1374,'{يهديهم ربُّهم بإيمانهم} أَيْ: إلى الجنان ثواباً لهم بإيمانهم',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1375,'{دعواهم} دعاؤهم {فيها سبحانك اللهم} وهو أنَّهم كلَّما اشتهوا شيئاً قالوا: سبحانك اللَّهم فجاءهم ما يشتهون فإذا طعموا ممَّا يشتهون قالوا: الحمد لله ربَّ العالمين',NULL,'490',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1376,'{ولو يعجل الله للناس الشرَّ} الآية نزلت في دعاء الرَّجل على نفسه وأهله وولده بما يكره أن يستجاب له والمعنى: لو استجبتُ لهم في الشَّرِّ كما يحبُّون أن يستجاب لهم في الخير {لقضي إليهم أجلهم} لماتوا وفُرغ من هلاكهم نزلت في النَّضر بن الحارث حين قال: {اللَّهُمَّ إِنْ كَانَ هَذَا هُوَ الْحَقَّ مِنْ عندك} الآية يدلُّ على هذا قوله: {فَنَذَرُ الَّذِينَ لا يَرْجُونَ لِقَاءَنَا} يعني: الكفَّار الذين لا يخافون البعث',NULL,'491',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1377,'{وإذا مس الإنسان} يعني: الكافر {الضرُّ} المرض والبلاء {دعانا لجنبه} أَيْ: مضطجعاً {أَوْ قَاعِدًا أَوْ قَائِمًا فَلَمَّا كَشَفْنَا عَنْهُ ضرَّه مرَّ} طاغياً على ترك الشُّكر {كأن لم يدعنا إلى ضرٍّ مسَّه} لنسيانه ما دعا الله فيه وما صنع الله به {كذلك زين} كما زُيِّن لهذا الكافر الدُّعاء عند البلاء والإِعراض عند الرَّخاء {زين للمسرفين} عملهم وهم الذين أسرفوا على أنفسهم إذ عبدوا الوثن',NULL,'491',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1378,'{ولقد أهلكنا القرون من قبلكم} يخوِّف كفار مكَّة بمثل عذاب الأمم الخالية {وما كانوا ليؤمنوا} لأنَّ الله طبع على قلوبهم جزاءً لهم على كفرهم {كذلك نجزي القوم المجرمين} نفعل بمَنْ كذَّب بمحمَّدٍ كما فعلنا بمَنْ قبلهم جزاءً لكفرهم',NULL,'491',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1379,'{ثُمَّ جَعَلْنَاكُمْ خَلائِفَ فِي الأَرْضِ مِنْ بَعْدِهِمْ} يعني: أهل مكَّة {لننظر كيف تعملون} لنختبر أعمالكم',NULL,'492',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1380,'{وإذا تتلى عليهم} على هؤلاء المشركين {آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ لا يَرْجُونَ لِقَاءَنَا} لا يخافون البعث: {ائت بقرآن غير هذا} ليس فيه عيب آلهتنا {أو بدَّله} تكلَّمْ به من ذات نفسك فبدِّل منه ما نكرهه {قُلْ مَا يَكُونُ لِي أَنْ أُبَدِّلَهُ مِنْ تلقاء نفسي} ما ينبغي لي أَنْ أغيِّره من قبل نَفْسِي {إِنْ أَتَّبِعُ إِلا مَا يُوحَى إِلَيَّ} ما أُخبركم إلاَّ ما أخبرني الله به أَي: الذي أتيتُ به من عند الله لا من عندي نفسي فأبدِّله',NULL,'492',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1381,'{قُلْ لَوْ شَاءَ اللَّهُ مَا تَلَوْتُهُ عَلَيْكُمْ} ما قرأتُ عليكم القرآن {وَلا أَدْرَاكُمْ بِهِ} ولا أعلمكم الله بِهِ {فَقَدْ لَبِثْتُ فِيكُمْ عُمُرًا مِنْ قَبْلِهِ} أقمتُ فيكم أربعين سنةً لا أُحدِّثكم شيئاً {أفلا تعقلون} أنَّه ليس من قبلي',NULL,'492',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1382,'{فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا} لا أحد أظلم ممَّن يظلم ظلم الكفر أَيْ: إني لم أفتر على الله ولم أكذب عليه وأنتم فعلتم ذلك حيث زعمتم أنَّ معه شريكاً {إِنَّهُ لا يُفْلِحُ الْمُجْرِمُونَ} لا يسعد مَنْ كذَّب أنبياء الله',NULL,'492',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1383,'{وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَضُرُّهُمْ} إنْ لم يعبدوه {وَلا يَنْفَعُهُمْ} إن عبدوه {ويقولون هؤلاء شفعاؤنا عند الله} في إصلاح معاشهم في الدُّنيا لأنَّهم لا يقرُّون بالبعث {قُلْ أَتُنَبِّئُونَ اللَّهَ بِمَا لا يعلم في السماوات ولا في الأرض} أتخبرون الله أنَّ له شريكاً ولا يعلم الله سبحانه لنفسه شريكا في السماوات ولا في الأرض ثمَّ نزَّه نفسه عمَّا افتروه فقال: {سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ}',NULL,'492',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1384,'{وما كان الناس إلاَّ أمة واحدة} يعني: من لدن عهد إبراهيم عليه السَّلام إلى أن غيَّر الدِّين عمرو بن لُحي {فاختلفوا} واتَّخذوا الأصنام {ولولا كلمة سبقت من ربك} بتأخير عذاب هذه الأُمَّة إلى القيامة {لقضي بينهم} بنزول العذاب',NULL,'493',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1385,'{ويقولون} يعني: أهل مكَّة: {لولا} هلاَّ {أنزل عليه آية من ربه} مثلُ العصا وما جاءت به الأنبياء {فقل إنما الغيب لله} أَيْ: إنَّ قولكم: هلا أنزل عليه آية غيبٌ وإنَّما الغيب لله لا يعلم أحدٌ لمَ لمْ يفعل ذلك {فَانْتَظِرُوا} نزول الآية {إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ}',NULL,'493',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1386,'{وإذا أذقنا الناس} كفار مكَّة {رحمة} مطراً وخَصْباً {من بعد ضرَّاء مستهم} فقرٍ وبؤسٍ {إذا لهم مكر في آياتنا} قولٌ بالتَّكذيب أَيْ: إذا أخصبوا بطروا فاحتالوا لدفع آيات الله {قل الله أسرع مكراً} أسرع نقمةً يعني: إنَّ ما يأتيهم من العقاب أسرعُ في أهلاكهم ممَّا أتوه من المكر في إبطال آيات الله {إنَّ رسلنا} يعني: الحفظة {يكتبون ما تمكرون} للمجازاة به في الآخرة',NULL,'493',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1387,'{هو الذي يسيِّركم في البر} على المراكب والظُّهور {والبحر} على السُّفن {حتى إذا كنتم في الفلك} السُّفن {وجرين بهم} يعني: وجرت السُّفن بمَنْ ركبها في البحر {بريح طيبة} رُخاءٍ ليِّنةٍ {وفرحوا} بتلك الرِّيح للينها واستوائها {جاءتها ريحٌ عاصف} شديدةٌ {وَجَاءَهُمُ الْمَوْجُ} وهو ما ارتفع من الماء {من كلِّ مكان} من البحر {وظنوا أَنَّهُمْ أُحِيطَ بِهِمْ} دنوا من الهلاك {دَعَوُا الله مخلصين له الدين} تركوا الشِّرك وأخلصوا لله الرُّبوبيَّة وقالوا {لَئِنْ أنجيتنا من هذه} الرِّيح العاصفة {لَنَكُونَنَّ مِنَ الشَّاكِرِينَ} الموحِّدين الطَّائعين',NULL,'493',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1388,'{فَلَمَّا أَنْجَاهُمْ إِذَا هُمْ يَبْغُونَ فِي الأَرْضِ بغير الحق} يعملون بالفساد والمعاصي والجرأة على الله {يا أيها النَّاسُ} يعني: أهل مكَّة {إِنَّمَا بَغْيُكُمْ عَلَى أنفسكم} أَيْ: بغي بعضكم على بعضٍ {متاع الحياة الدنيا} أَيْ: ما ينالونه بهذا الفساد والبغي إنَّما يتمتَّعون به في الحياة الدُّنيا {ثم إلينا مرجعكم}',NULL,'494',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1389,'{إنما مثل الحياة الدنيا} يعني: الحياة الفانية في هذه الدَّار {كماءٍ} كمطرٍ {أنزلناه من السماء فاختلط به} بذلك المطر وبسببه {نبات الأرض ممَّا يأكل الناس} من البقول والحبوب والثِّمار {والأنعام} من المراعي والكلأ {حتى إذا أخذت الأرض زخرفها} زينتها وحسنتها {وازَّينت} بنباتها {وظنَّ} أهل تلك الأرض {أنهم قادرون} على حصادها والانتفاع بها {أتاها أمرنا} عذابنا {فجعلناها حصيداً} لا شيء فيها {كأن لم تغن} لم تكن بالأمس {كذلك} الحياةُ في الدُّنيا سببٌ لاجتماع المال وزهرة الدُّنيا حتى إذا كثر ذلك عند صاحبه وظنَّ أنَّه ممتَّعٌ به سُلِب ذلك عنه بموته أو بحادثةٍ تهلكه {كذلك نفصل الآيات} كما بيَّنا هذا المثل للحياة الدُّنيا كذلك يُبيِّن الله آيات القرآن {لقوم يتفكرون} في المعاد',NULL,'494',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1390,'{والله يدعو إلى دار السلام} وهي الجنَّة ببعث الرَّسول ونصب الأدلة {ويهدي من يشاء} عمَّ بالدَّعوة وخصَّ بالهداية مَنْ يشاء',NULL,'495',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1391,'{للذين أحسنوا} قالوا: لا إله إلاَّ الله {الحسنى} الجنَّة {وزيادة} النَّظر إلى وجه الله الكريم عزَّ وجل {ولا يرهق} يغشى {وجوههم قترٌ} سوادٌ من الكآبة {ولا ذلة} كما يصيب أهل جهنَّم وهذا بعد نظرهم إلى ربِّهم تبارك وتعالى',NULL,'495',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1392,'{والذين كسبوا السيئات} عملوا الشِّرك {جزاء سيئة} أَيْ: فلهم جزاء سيئةٍ {بمثلها وترهقهم ذلة} يُصيبهم ذلٌّ وخزيٌ وهوانٌ {ما لَهُمْ مِنَ اللَّهِ} من عذاب الله {من عاصم} من مانعٍ يمنعهم {كَأَنَّمَا أُغْشِيَتْ} أُلبست {وُجُوهُهُمْ قطعاً} طائفةً {من الليل} وهو مظلم',NULL,'496',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1393,'{ويوم نحشرهم جميعاً} نجمعهم جميعاً: الكفَّارَ وآلهتَهم {ثمَّ نقول للذين أشركوا مكانكم} قفوا والزموا مكانكم {أنتم وشركاؤكم فزيلنا} فرَّقنا وميَّزنا {بينهم} بين المشركين وبين شركائهم وانقطع ما كان بينهم من التَّواصل في الدُّنيا {وقال شركاؤهم} وهي الأوثان: {ما كنتم إيانا تعبدون} أنكروا عبادتهم وقالوا: ما كنَّا نشعر بأنَّكم إيَّانا تعبدون والله يُنطقها بهذا',NULL,'496',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1394,'{فكفى بالله شهيداً} الآية هذا من كلام الشُّركاء قالوا: شهد الله على علمه فينا ما {كنا عن عبادتكم} إلاَّ غافلين لأنَّا كنَّا جماداً لم يكن فينا روحٌ',NULL,'496',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1395,'{هنالك} في ذلك الوقت {تَبْلُو} تختبر {كُلُّ نَفْسٍ ما أسلفت} جزاء ما قدَّمَتْ من خيرٍ أو شرٍّ {ورُدُّوا إلى الله مولاهم الحق} أَي: الذي يملك تولِّي أمرهم ويجازيهم بالحقِّ {وضلَّ عنهم} زال وبطل {مَا كَانُوا يَفْتَرُونَ} في الدُّنيا من التَّكذيب',NULL,'496',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1396,'{قل من يرزقكم من السماء والأرض} مَنْ يُنزِّل من السماء المكر ويُخرج النَّبات من الأرض؟ {أَمَّنْ يَمْلِكُ السَّمْعَ والأبصار} مَنْ جعلها وخلقها لكم؟ على معنى: مَنْ يملك خلقها {ومن يخرج الحيَّ من الميت} المؤمن من الكافر والنَّبات من الأرض والإِنسان من النُّطفة وعلى الضدِّ من ذلك {وَيُخْرِجُ الْمَيِّتَ مِنَ الحيِّ ومَنْ يدبر} أمر الدُّنيا والآخرة {فَسَيَقُولُونَ الله} أَي: الله الذي يفعل هذه الأشياء فإذا أقرُّوا بعد الاحتجاج عليهم {فقل أفلا تتقون} أفلا تخافون الله فلا تشركوا به شيئاً',NULL,'496',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1397,'{فذلكم الله ربكم الحق} أَي: الذي هذا كلُّه فِعْلُه هو الحقُّ ليس هؤلاء الذين جعلتم معه شركاء {فماذا بعد الحق} بعد عبادة الله {إلاَّ الضلال} يعني: عبادة الشَّيطان {فأنى تصرفون} يريد: كيف تُصرف عقولكم إلى عبادة من لا يرزق ولا يحيي ولا يميت',NULL,'497',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1398,'{كذلك} هكذا {حقت} صدَّقت {كلمة ربك} بالشَّقاوة والخذلان {على الذين فسقوا} تمرَّدوا في الكفر {أَنَّهُمْ لا يُؤْمِنُونَ}',NULL,'497',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1399,'{قُلْ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ قُلِ اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ فَأَنَّى تُؤْفَكُونَ}',NULL,'497',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1400,'{قل هل من شركائكم} يعني: آلهتكم {من يهدي} يرشد {إلى الحق} إلى دين الإسلام {قُلِ اللَّهُ يَهْدِي لِلْحَقِّ} أَيْ: إلى الحقِّ {أَفَمَنْ يَهْدِي إِلَى الْحَقِّ أحق أن يتبع أمن لا يهدي} أَي: الله الذي يهدي ويرشد إلى الحقِّ أهلَ الحقِّ أحقُّ أن يتَّبع أمره أم الأصنام التي لا تهدي أحداً {إلاَّ أن يُهدى} يُرشد وهي - وإنْ هُديت - لم تهتد ولكنَّ الكلام نزل على أنَّها إِن هُديت اهتدت لأنَّهم لمَّا اتخذوها آلهة عبر عنها كما يُعبَّر عمَّن يعلم {فما لكم} أيُّ شيءٍ لكم في عبادة الأوثان وهذا كلامٌ تامٌّ {كيف تحكمون} يعني: كيف تقضون حين زعمتم أنَّ مع الله شريكاً',NULL,'497',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1401,'{وما يتبع أكثرهم} يعني: الرُّؤساء لأنَّ السَّفلة يتَّبعون قولهم {إلاَّ ظناً} يظنون أنَّها آلهةٌ {إِنَّ الظَّنَّ لا يُغْنِي مِنَ الْحَقِّ شَيْئًا} ليس الظنُّ كاليقين يعني: إنَّ الظَّنَّ لا يقوم مقام العلم {إنَّ الله عليم بما يفعلون} من كفرهم',NULL,'498',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1402,'{وما كان هذا القرآن أن يفترى من دون الله} هذا جوابٌ لقولهم: {ائت بقرآن غير هذا} يقول: ما كان هذا القرآن افراء من دون الله {ولكن تصديق} ولكن كان تصديق {الذي بين يديه} من الكتب {وتفصيل الكتاب} يعني: تفصيل المكتوب من الوعد لمَنْ آمن والوعيد لمَنْ عصى {لا ريب فيه} لا شكَّ في نزوله من عند ربِّ العالمين',NULL,'498',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1403,'{أم يقولون افتراه} بل أتقولون: افتراه محمد {قل فأتوا بسورة مثله} إن كان مفترىً {وادعوا} إلى معاونتكم على المعارضة كلَّ مَنْ تقدرون عليه {إِنْ كُنْتُمْ صادقين} في أنَّ محمَّداً اختلقه من عند نفسه ونظيرُ هذه الآية في سورة البقرة: {وَإِنْ كُنْتُمْ فِي رَيْبٍ} الآية',NULL,'498',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1404,'{بل كذبوا بما لم يحيطوا بعلمه} أَيْ: بما في القرآن من الحنة والنَّار والبعث والقيامة {ولما يأتهم تأويله} ولم يأتهم بعدُ حقيقة ما وُعدوا في الكتاب {كذلك كذَّب الذين من قبلهم} بالبعث والقيامة',NULL,'498',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1405,'{ومنهم} ومن كفَّار مكَّة {مَنْ يؤمن به} يعني: قوماً علم أنَّهم يؤمنون {وَمِنْهُمْ مَنْ لا يُؤْمِنُ بِهِ وَرَبُّكَ أَعْلَمُ بالمفسدين} يريد: المكذِّبين وهذا تهديدٌ لهم',NULL,'499',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1406,'{وإن كذبوك فقل لي عملي} الآية نسختها آية الجهاد',NULL,'499',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1407,'{ومنهم مَنْ يستمعون إليك} نزلت في المستهزئين كانوا يستمعون الاستهزاء والتَّكذيب فقال الله تعالى: {أفأنت تُسمع الصمَّ} يريد أنَّهم بمنزلة الصُّمِّ لشدَّة عداوتهم {ولو كانوا لا يعقلون} أَيْ: ولو كانوا مع كونهم صمَّاً جهَّالاً! أخبر الله سبحانه أنَّهم بمنزلة الصُّمِّ الجُهَّال إذْ لم ينتفعوا بما سمعوا',NULL,'499',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1408,'{ومنهم مَنْ ينظر إليك} مُتعجِّباً منك غير منتفعٍ بنظره {أَفَأَنْتَ تَهْدِي الْعُمْيَ وَلَوْ كَانُوا لا يُبْصِرُونَ} يريد: إنَّ الله أعمى قلوبهم فلا يبصرون شيئاً من الهدى',NULL,'499',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1409,'{إِنَّ اللَّهَ لا يَظْلِمُ النَّاسَ شَيْئًا} لمَّا ذكر أهل الشَّقاوة ذكر أنَّه لم يظلمهم بتقدير الشَّقاوة عليهم لأنَّه يتصرَّف في ملكه {ولكنَّ الناس أنفسهم يظلمون} بكسبهم المعاصي',NULL,'499',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1410,'{وَيَوْمَ يَحْشُرُهُمْ كَأَنْ لَمْ يَلْبَثُوا إِلا سَاعَةً من النهار} كأن لم يلبثوا في قبورهم إلاَّ قدر ساعة من النَّهار استقصروا تلك المدَّة من هول ما استُقبلوا من أمر البعث والقيامة {يَتَعَارَفُونَ بينهم} يعرف بعضهم بعضا تعارف تعارف توبيخٍ لأنَّ كلَّ فريق يقول للآخر: أنت أضللتني وما يشبه هذا {قد خسر} ثواب الجنَّة {الذين كذَّبوا} بالبعث',NULL,'499',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1411,'{وإما نرينك بعض الذي نعدهم} يريد: ما ابتُلوا به يوم بدرٍ {أو نتوفينك} قبل ذلك {فإلينا مرجعهم} أَيْ: فنعذِّبهم في الآخرة {ثمَّ الله شهيد على ما يفعلون} من محاربتك وتكذيبك فيجزيهم بها ومعنى الآية: إنْ لم ينتقم منهم في العاجل ينتقم منهم في الآجل',NULL,'500',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1412,'{ولكلِّ أمة رسول} يُرسل إليهم {فإذا جاء رسولهم قضي بينهم بالقسط} وهو هلاك مَنْ كذَّبه ونجاة من تبعه {وهم لا يظلمون} لا ينقص ثواب المُصدِّق ويُجازى المكذِّب بتكذيبه',NULL,'500',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1413,'{ويقولون متى هذا الوعد} قالوا ذلك حين قيل لهم: {وإمَّا نرينك بعض الذي نعدهم} الآية فقالوا: متى هذا العذاب الذي تعدنا يا محمَّد؟ {إن كنتم} أنت يا محمَّد وأتباعك صادقين',NULL,'500',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1414,'{قُلْ لا أَمْلِكُ لِنَفْسِي ضَرًّا وَلا نَفْعًا إلاَّ ما شاء الله} الآية مفسَّرةٌ في آيتين من سورة الأعراف فلمَّا استعجلوا العذاب قيل للنبي صلى الله عليه وسلم:',NULL,'500',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1415,'{قل أرأيتم} أعلمتم {إن أتاكم عذابُهُ بياتاً} ليلاً {أو نهاراً مَاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ} أَيٌّ شيءٍ يستعجل المجرمون من العذاب؟ وهذا استفهام معناه التَّهويل والتًّفظيع أَيْ: ما أعظم ما يلتمسون ويستعجلون! كما تقول: أعلمت ماذا تجني على نفسك؟ ! فلمَّا قال لهم النبيُّ عليه السَّلام هذا قالوا: نكذِّب بالعذاب ونستعجله فإذا وقع آمنَّا به فقال الله تعالى:',NULL,'501',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1416,'{أثمَّ إذا ما وقع} وحلَّ بكم {آمنتم به} بعد نزوله فلا يقبل منكم الإِيمان ويقال لكم: {الآنَ} تؤمنون به {وقد كنتم به تستعجلون} في الدنيا مستهزئين',NULL,'501',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1417,'{ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ الْخُلْدِ هل تجزون إلا بما كنتم تكسبون}',NULL,'501',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1418,'{ويستنبئونك} يستخبرونك {أحقٌّ} ما أخبرتنا به من العذاب والبعث؟ {قل إي} نعم {وربي إنه لحق} يعني: العذاب نازلٌ بكم {وَمَا أَنْتُمْ بِمُعْجِزِينَ} بعد الموت أَيْ: فتجازون بكفركم',NULL,'501',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1419,'{وَلَوْ أَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ} أشركت {مَا فِي الأَرْضِ لافْتَدَتْ بِهِ} لبذلته لدفعِ العذاب عنها {وأسروا} أخفوا وكتموا {الندامة} يعني: الرُّؤساء من السَّفلة الذين أضلُّوهم {وقضي بينهم} بين السَّفلة والرُّؤساء {بالقسط} بالعدل فيجازي كلٌّ على صنيعه',NULL,'501',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1420,'{ألاَ إنَّ وعد الله حقٌّ} ما وعد لأوليائه وأعدائه {ولكنَّ أكثرهم لا يعلمون} يعني: المشركين',NULL,'501',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1421,'{هو يحيي ويميت وإليه ترجعون}',NULL,'502',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1422,'{يا أيها الناس} يعني: قريشاً {قَدْ جَاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ} القرآن {وشفاءٌ لما في الصدور} ودواءٌ لداء الجهل {وهدىً} وبيانٌ من الضَّلالة {ورحمةٌ للمؤمنين} ونعمةٌ من الله سبحانه لأصحاب محمَّدٍ',NULL,'502',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1423,'{قل بفضل الله} الإِسلام {وبرحمته} القرآن {فبذلك} الفضل والرَّحمة {فليفرحوا هو خيرٌ} أَيْ: ما آتاهم الله من الإِسلام والقرآن خيرٌ ممَّا يجمع غيرهم من الدُّنيا',NULL,'502',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1424,'{قل} لكفَّار مكَّة: {أرأيتم ما أنزل الله} خلقه وأنشأه لَكُمْ {مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَرَامًا وَحَلالا} يعني: ما حرَّموه ممَّا هو حلالٌ لهم من البحيرة وأمثالها وأحلُّوه ممَّا هو حرامٌ من الميتة وأمثالها {قل الله أذن لكم} في ذلك التَّحريم والتَّحليل {أم} بل {عَلَى اللَّهِ تَفْتَرُونَ}',NULL,'502',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1425,'{وَمَا ظَنُّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ يوم القيامة} أَيْ: ما ظنُّهم ذلك اليوم بالله وقد افتروا عليه؟ {إنَّ الله لذو فضل على الناس} أهل مكَّة حين جعلهم في أمنٍ وحرمٍ إلى سائر ما أنعم به عليهم {ولكنَّ أكثرهم لا يشكرون} لا يُوحدِّون ولا يُطيعون',NULL,'502',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1426,'{وما تكون} يا محمَّد {في شأن} أمرٍ من أمورك {وما تتلوا منه} من الله {من قرآنٍ} أنزله عليك {ولا تعملون من عمل} خاطبه وأمَّته {إلاَّ كُنَّا عليكم شهوداً} نشاهد ما تعلمون {إذ تفيضون} تأخذون {فيه وما يعزب} يغيب ويبعد {عن ربك من مثقال ذرة} وزن ذرَّة {إلاَّ في كتاب مبين} يريد: اللَّوح المحفوظ الذي أثبت الله سبحانه فيه الكائنات',NULL,'502',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1427,'{ألا إنَّ أولياء الله} هم الذين تولَّى الله سبحانه هداهم',NULL,'503',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1428,'{الذين آمنوا} صدَّقوا النبيَّ {وكانوا يتقون} خافوا مقامهم بين يدي الله سبحانه',NULL,'503',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1429,'{لهم البشرى في الحياة الدنيا} عند الموت تأتيهم الملائكة بالبشرى من الله {وفي الآخرة} يُبشَّرون بثواب الله وجنَّته {لا تبديل لكلمات الله} لا خلف لمواعيده',NULL,'503',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1430,'{ولا يحزنك قولهم} تكذيبهم إيَّاك {إنَّ العزة لله} القوَّة لله والقدرة لله {جميعاً} وهو ناصرك {وهو السميع} يسمع قولهم {العليم} بما في ضميركم فيجازيهم بما يقتضيه حالهم',NULL,'503',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1431,'{ألاَّ إن لله مَنْ في السماوات ومَنْ في الأرض} يعني: يفعل بهم وفيهم ما يشاء {وَمَا يَتَّبِعُ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ شُرَكَاءَ} أيْ: ليسوا يتَّبعون شركاء على الحقيقة لأنَّهم يعدُّونها شركاء شفعاء لهم وليست على ما يظنُّون {إِنْ يتبعون إلا الظن} ما يتَّبعون إلاَّ ظنَّهم أنَّها تشفع لهم {وإن هم إلا يخرصون} يقولون ما لا يكون',NULL,'503',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1432,'{هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ والنهار مُبْصِرًا} مُضيئاً لتهتدوا به في حوائجكم {إِنَّ في ذلك لآيات لقومٍ يسمعون} سَمعَ اعتبار',NULL,'503',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1433,'{قالوا اتخذ الله ولدا} يعني: قولهم: الملائكة بنات الله {سبحانه} تنزيهاً له عمل قالوه {هو الغنيُّ} أن يكون له زوجةٌ أو ولدٌ {إِنْ عِنْدَكُمْ مِنْ سُلْطَانٍ بِهَذَا} ما عندكم من حجة بهذا وقوله:',NULL,'504',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1434,'{قُلْ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لا يفلحون}',NULL,'504',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1435,'{متاع في الدنيا} أيْ: لهم متاعٌ في الدُّنيا يتمتَّعون به أيَّاماً يسيراً وقوله:',NULL,'504',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1436,'{إن كان كَبُرَ عليكم مقامي} أَيْ: عَظُم وشقَّ عليكم مكثي ولبثي فيكم {وتذكيري بآيات الله} وعظي وتخويفي إيَّاكم عقوبة الله {فَعَلَى اللَّهِ توكلت} فافعلوا ما شئتم وهو قوله: {فأجمعوا أمركم وشركاءَكم} أَيْ: اعزموا على أمرٍ مُحكمٍ تجتمعون عليه {وشركاءكم} مع شركاءكم وقيل: معناه: وادعوا شركاءكم يعني: آلهتكم {ثُمَّ لا يَكُنْ أَمْرُكُمْ عَلَيْكُمْ غُمَّةً} أَيْ: ليكن أمركم ظاهراً منكشفاً تتمكنون فيه ممَّا شئتم لا كمَنْ يكتم أمراً ويخفيه فلا يقدر أن يفعل ما يريد {ثمَّ اقضوا إليَّ} افعلوا ما تريدون وامضوا إليَّ بمكروهكم {ولا تنظرون} ولا تُؤخِّروا أمري والمعنى: ولا تألوا في الجمع والقوَّة فإنَّكم لا تقدرون على مساءتي لأنَّ لي إلهاً يمنعني وفي هذا تقويةٌ لقلب محمد صلى الله عليه وسلم لأنَّ سبيله مع قومه كسبيل الأنبياء من قبله',NULL,'504',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1437,'{فَإِنْ تَوَلَّيْتُمْ} أعرضتم عن الإِيمان {فَمَا سَأَلْتُكُمْ من أجر} مالٍ تعطونيه وهذا من قول نوح عليه السلام لقومه وقوله:',NULL,'504',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1438,'{فَكَذَّبُوهُ فَنَجَّيْنَاهُ وَمَنْ مَعَهُ فِي الْفُلْكِ وَجَعَلْنَاهُمْ خَلائِفَ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَانْظُرْ كَيْفَ كان عاقبة المنذرين}',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1439,'{فما كانوا ليؤمنوا} يعني: أمم الأنبياء والرُّسل {بما} كذَّب به قوم نوح أَيْ: هؤلاء الآخرون لم يؤمنوا بما كذَّب به أوَّلُوهم وقد علموا أنَّ الله سبحانه أغرقهم بتكذيبهم ثم قال: {كذلك} كما طبعنا على قلوبهم {نطبع على قلوب المعتدين} المُجاوزين الحق إلى الباطل وقوله:',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1440,'{ثُمَّ بَعَثْنَا مِنْ بَعْدِهِمْ مُوسَى وَهَارُونَ إِلَى فِرْعَوْنَ وَمَلَئِهِ بِآيَاتِنَا فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا مُجْرِمِينَ}',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1441,'{فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوا إِنَّ هَذَا لَسِحْرٌ مُبِينٌ}',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1442,'{قَالَ مُوسَى أَتقُولُونَ لِلْحَقِّ لَمَّا جَاءَكُمْ أَسِحْرٌ هذا ولا يفلح الساحرون}',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1443,'{قالوا أجئتنا لتلفتنا} لتردَّنا {عَمَّا وَجَدْنَا عَلَيْهِ آبَاءَنَا وَتَكُونَ لَكُمَا الْكِبْرِيَاءُ} الملك والعزُّ {في الأرض} في أرض مصر وقوله:',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1444,'{وَقَالَ فِرْعَوْنُ ائْتُونِي بِكُلِّ سَاحِرٍ عَلِيمٍ}',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1445,'{فَلَمَّا جَاءَ السَّحَرَةُ قَالَ لَهُمْ مُوسَى أَلْقُوا مَا أنتم ملقون}',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1446,'{إنَّ الله سيبطله} سيهلكه {إنَّ الله لا يصلح عمل المفسدين} لا يجعله ينفعهم',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1447,'{ويحق الله الحق} ويظهره بالدَّلائل الواضحة {بكلماته} بوعده',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1448,'{فَمَا آمَنَ لِمُوسَى إِلا ذُرِّيَّةٌ مِنْ قَوْمِهِ} يعني: من آمن به من بني إسرائيل وكانوا ذرية أولاد يعقوب {على خوفٍ من فرعون ومَلَئِهِمْ} ورؤسائهم {أن يفتنهم} يصرفهم عن دينهم بمحنةٍ وبليَّةٍ يوقعهم فيها {وإنَّ فرعون لعالٍ} متطاولٌ {في الأرض} في أرض مصر {وإنه لمن المسرفين} حيث كان عبداً فادَّعى الربوبية وقوله:',NULL,'505',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1449,'{وَقَالَ مُوسَى يَا قَوْمِ إِنْ كُنْتُمْ آمَنْتُمْ بالله فعليه توكلوا إن كنتم مسلمين}',NULL,'506',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1450,'{لا تجعلنا فتنة للقوم الظالمين} أَيْ: لا تُظهرهم علينا فيروا أنَّهم خيرٌ منا فيزدادوا طغياناً ويقولوا: لو كانوا على حقٍّ ما سُلِّطنا عليهم فَيُفتنوا',NULL,'506',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1451,'{وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكَافِرِينَ}',NULL,'506',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1452,'{وأوحينا إلى موسى وأخيه} الآية لمَّا أُرسل موسى صلوات الله عليه إلى فرعون أمر فرعون بمساجد بني إسرائيل فَخُرِّبت كلُّها ومُنعوا من الصَّلاة فأُمروا أن يتَّخذوا مساجد في بيوتهم ويصلُّوا فيها خوفاً من فرعون فذلك قوله: {تبوءا لقومكما} أي: اتخذ لهم {بمصر بيوتاً} في دورهم {واجعلوا بيوتكم قبلة} أَيْ: صلُّوا في بيوتكم لتأمنوا من الخوف وقوله:',NULL,'506',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1453,'{ربنا ليضلوا عن سبيلك} أَيْ: جعلت هذه الأموال سبباً لضلالهم لأنَّهم بطروا فاستكبروا عن الإِيمان {ربنا اطمس على أموالهم} امسخها وأذهبها عن صورتها فصارت دراهمهم ودنانيرهم حجارةً منقوشةً صحاحاً وأنصافاً وكذلك سائر أموالهم {واشدُدْ على قلوبهم} اطبع عليها حتى لا تلين ولا تنشرح للإِيمان {فلا يؤمنوا} دعاءٌ عليهم {حتى يروا العذاب الأليم} يعني: الغرق فاستجيب في ذلك فلم يؤمن فرعون حتى أدركه الغرق',NULL,'506',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1454,'{قال قد أجيبت دعوتكما} وذلك أنَّ موسى دعا وأمَّن هارون {فاستقيما} على الرِّسالة والدًّعوة {وَلا تَتَّبِعَانِّ سَبِيلَ الَّذِينَ لا يَعْلَمُونَ} لا تسلكا طريق الذين يجهلون حقيقة وعدي فتستعجلا قضائي وقوله:',NULL,'507',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1455,'{فأتبعهم فرعون وجنوده} طلبوا أن يلحقوا بهم {بغياً} طلباً للاستعلاء بغير حقٍّ {وعدواً} ظلماً {حتى إذا أدركه الغرق} تلفظ لما أخبر الله عنه حين لم ينفعه ذلك لأنَّه رأى اليأس وعاينه فقيل له: {آلآن وقد عصيت قبل} أَيْ: آلآن تؤمن أو تتوب؟ فلمَّا أغرقه الله جحد بعض بني إسرائيل غَرَقَةُ وقالوا: هو أعظم شأناً من أن يغرق فأخرجه الله سبحانه من الماء حتى رأوه فذلك قوله:',NULL,'507',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1456,'{آلآن وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِينَ}',NULL,'507',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1457,'{فاليوم ننجيك} نخرجك من البحر بعد الغرق {ببدنك} بجسدك الذي لا روح فيه {لتكون لمَنْ خلفك آية} نكالاً وعبرةً {وإنَّ كثيراً من الناس} يريد: أهل مكَّة {عن آياتنا} عمَّا يراد بهم {لغافلون}',NULL,'507',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1458,'{ولقد بوَّأنا بني إسرائيل مبوَّأ صدق} أنزلنا قريظة والنضير منزل صدقٍ أَيْ: محموداً مختاراً يريد: من أرض يثرب ما بين المدينة والشّام {ورزقناهم من الطيبات} من النَّخل والثِّمار ووسَّعنا عليهم الرِّزق {فَمَا اخْتَلَفُوا} في تصديق النبيِّ صلى الله عليه وسلم وأنَّه رسولٌ مبعوثٌ {حتى جاءهم العلم} حقيقةُ ما كانوا يعلمونه وهو محمَّد عليه السَّلام بنعته وصفته والقرآن وذلك أنَّهم كانوا يُخبرون عن زمانه ونبوَّته ويؤمنون به فلمَّّا أتاهم اختلفوا فكفر به أكثرهم',NULL,'508',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1459,'{فإن كنت في شك} هذا في الظَّاهر خطابٌ للنبي صلى الله عليه وسلم والمراد به غيره من الشَّاكِّين في الدِّين وقوله: {فَاسْأَلِ الذين يقرؤون الكتاب من قبلك} يعني: مَنْ آمن من أهل الكتاب كعبد الله بن سلام وأصحابه فيشهدون على صدق محمد ويخبرون بنبوَّته وباقي الآية والتي تليها خطاب النبي صلى الله عليه وسلم والمراد به غيره',NULL,'508',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1460,'{وَلا تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ فتكون من الخاسرين}',NULL,'508',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1461,'{إنَّ الذين حقت عليهم كلمة ربك} وجبت عليهم كلمة العذاب',NULL,'508',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1462,'{لا يؤمنون * ولو جاءتهم كلُّ آية} وذلك أنَّهم كانوا يسألون رسول الله صلى الله عليه وسلم أن يأتيهم بالآيات حتى يؤمنوا فقال الله تعالى: {لا يُؤْمِنُونَ * وَلَوْ جَاءَتْهُمْ كُلُّ آية حتى يروا العذاب الأليم} فلا ينفعهم حينئذٍ الإِيمان كما لم ينفع فرعون',NULL,'508',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1463,'{فلولا كانت قرية} أَيْ: فما كانت قريةٌ {آمنت فنفعها إيمانها} عند نزول العذاب {إلاَّ قوم يونس لما آمنوا} عند نزول العذاب {كشفنا عنهم عذاب الخزي} يعني: سخط الله سبحانه {ومتعناهم إلى حين} يريد: حين آجالهم وذلك أنَّهم لمَّا رأوا الآيات التي تدلُّ على قرب العذاب أخلصوا التَّوبة وترادُّوا المظالم وتضرَّعوا إلى الله تعالى فكشف عنهم العذاب',NULL,'508',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1464,'{وَلَوْ شَاءَ رَبُّكَ لآمَنَ مَنْ فِي الأَرْضِ كلهم جميعاً} الآية كان رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم حريصاً على أن يؤمن جميع الناي فأخبره الله سبحانه أنَّه لا يؤمن إلا من سبق له من الله السَّعادة وهو قوله:',NULL,'509',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1465,'{وَمَا كَانَ لِنَفْسٍ أَنْ تُؤْمِنَ إِلا بِإِذْنِ الله} أَيْ: إلاَّ بما سبق لها في قضاء الله وقدره {ويجعل الرجس} العذاب {على الذين لا يعقلون} عن الله تعالى أمره ونهيه وما يدعوهم إليه',NULL,'509',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1466,'{قل} للمشركين الذين يسألونك الآيات: {انظروا ماذا} أي: الذي أعظم منها {في السماوات والأرض} من الآيات والعبر التي تدلُّ على وحدانيَّة الله سبحانه فيعلموا أنَّ ذلك كلَّه يقتضي صانعاً لا يشبه الأشياء ولا تشبهه ثمَّ بيَّن أنَّ الآيات لا تُغني عمَّن سبق في علم الله سبحانه أنَّه لا يؤمن فقال: {وما تغني الآيات والنذر} جمع نذير {عن قومٍ لا يؤمنون} يقول: الإنذار غير نافعٍ لهؤلاء',NULL,'509',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1467,'{فهل ينتظرون} أَيْ: يجب ألا ينتظروا بعد تكذيبك {إلاَّ مثل أيام الذين خلوا من قبلهم} إلاَّ مثل وقائع الله سبحانه فيمَنْ سلف قبلهم من الكفَّار',NULL,'509',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1468,'{ثمَّ ننجي رسلنا والذين آمنوا} هذا إخبارٌ عن ما كان الله سبحانه يفعل في الأمم الماضية من إنجاء الرُّسل والمُصدِّقين لهم عما يعذِّب به مَنْ كفر {كذلك} أَيْ: مثل هذا الإِنجاء {ننج المؤمنين} بمحمد صلى الله عليه وسلم من عذابي',NULL,'510',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1469,'{قل يا أيها الناس} يريد: أهل مكَّة {إِنْ كُنْتُمْ فِي شَكٍّ من ديني} الذي جئت به {فلا أعبد الذين تعبدون من دون الله} أي: بشكِّكم في ديني لا أعبد غير الله {ولكن أعبد الله الذي يتوفاكم} يأخذ أرواحكم وفي هذا تهديدٌ لهم لأنَّ وفاة المشركين ميعاد عذابهم وقوله:',NULL,'510',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1470,'{وأن أقم وجهك للدين حنيفاً} استقم بإقبالك على ما أُمرت به بوجهك',NULL,'510',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1471,'{وَلا تَدْعُ مِنْ دُونِ اللَّهِ مَا لا ينفعك ولا يضرُّك} أَيْ: شيئاً ما لأنَّه لا يتحقق النَّفع والضَّرُّ إلاَّ من الله فكأنَّه قال: ولا تدع من دون الله شيئا',NULL,'510',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1472,'{وإن يمسسك الله بضر} بمرضٍ وفقرٍ {فلا كاشف له} لا مزيل له {إلاَّ هو} {وإن يردك بخيرٍ} يرد بك الخير {فلا رادَّ لفضله} لا مانع لما تفضَّل به عليك من رخاءٍ ونعمةٍ {يُصِيبُ بِهِ} بكلَّ واحدٍ ممَّا ذُكر {من يشاء من عباده}',NULL,'510',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1473,'{قل يا أيها النَّاسُ} يعني: أهل مكَّة {قَدْ جَاءَكُمُ الْحَقُّ} القرآن {من ربكم} وفيه البيان والشِّفاء {فمن اهتدى} من الضَّلالة {فإنما يهتدي لنفسه} يريد: مَنْ صدَّق محمَّداً عليه السَّلام فإنَّما يحتاط لنفسه {ومَنْ ضلَّ} بتكذيبه {فإنما يضلُّ عليها} إنَّما يكون وبال ضلاله على نفسه {وَمَا أنا عليكم بوكيلٍ} بحفيظٍ من الهلاك حتى لا تهلكوا',NULL,'510',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1474,'{وَاتَّبِعْ مَا يُوحَى إِلَيْكَ وَاصْبِرْ حَتَّى يَحْكُمَ الله} نسخته آية السَّيف لأنَّ الله سبحانه حكم بقتل المشركين والجزية على أهل الكتاب',NULL,'511',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1475,'{الر} أنا الله الرَّحمن {كتاب} هذا كتابٌ {أُحْكمتْ آياته} بعجيب النَّظم وبديع المعاني ورصين اللَّفظ {ثُمَّ فُصِّلَتْ} بُيِّنت بالأحكام من الحلال والحرام وجميع ما يحتاج إليه من {لدن حكيم} في خلقه {خبير} بمَنْ يُصدِّق نبيَّه وبمَنْ يكذبه',NULL,'512',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1476,'{أن لا تعبدوا} أَيْ: بأن والتَّقدير: هذا كتابٌ بأن لا تعبدوا {إلاَّ الله}',NULL,'512',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1477,'{و} بـ {أن استغفروا ربكم} أَيْ: من ذنوبكم السَّالفة {ثُمَّ تُوبُوا إِلَيْهِ} من المستأنفة متى وقعت {يمتعكم متاعاً حسناً} يتفضَّل عليكم بالرِّزق والسِّعة {إلى أجل مسمى} أجل الموت {وَيُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ} يؤت كلَّ مَنْ فَضُلَت حسناته على سيئاته فضله يعني: الجنَّة وهي فضل الله سبحانه {وإن تولوا} تتولَّوا عن الإِيمان {فإني أخاف عليكم عذاب يوم كبير} وهو يوم القيامة',NULL,'512',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1478,'{إِلَى اللَّهِ مَرْجِعُكُمْ وَهُوَ عَلَى كُلِّ شَيْءٍ قدير}',NULL,'512',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1479,'{ألا إنهم يثنون صدورهم} نزلت في طائفةٍ من المشركين قالوا: إذا أغلقنا أبوابنا وأرخينا ستورنا واستغشينا ثيابنا وطوينا صدورنا على عداوة محمد صلى الله عليه وسلم كيف يعلم ربُّنا؟ فأنزل الله تعالى {ألا إنهم يثنون صدورهم} أَيْ: يعطفونها ويطوونها على عداوة محمد صلى الله عليه وسلم {ليستخفوا منه} ليتواروا عنه ويكتموا عداوته {ألا حين يستغشون ثيابهم} يتدثَّرون بها {يعلم ما يسرون وما يعلنون} أعلم الله سبحانه أنَّ سرائرهم يعلمها كما يعلم مظهرهم {إنه عليم بذات الصدور} بما في النُّفوس من الخير والشَّرِّ',NULL,'513',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1480,'{وما من دابة} حيوانٍ يدبُّ {في الأرض إلاَّ على الله رزقها} فضلاً لا وجوباً {ويعلم مستقرها} حيث تأوي إليه {ومستودعها} حيث تموت {كلٌّ في كتاب مبين} يريد: اللَّوح المحفوظ والمعنى: أنَّ ذلك ثابتٌ في علم الله',NULL,'513',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1481,'{وهو الذي خلق السماوات والأرض في ستة أيام} ذكرنا تفسيره في سورة الأعراف {وكان عرشه على الماء} يعني: قبل خلق السماوات والأرض {ليبلوكم} أَيْ: خلقها لكم لكي يختبركم بالمصنوعات فيها من آياته ليعلم إحسان المحسن وإساءة المسيء وهو قوله تعالى {أيكم أحسن عملاً} أَيْ أَعملُ بطاعة الله تعالى {ولئن قلت} للكفَّار بعد خلق الله السماوات والأرض وبيان قدرته {إنكم مبعوثون من بعد الموت} كذَّبوا بذلك وقالوا: {إن هذا إلاَّ سحر مبين} أَيْ: باطلٌ وخداعٌ',NULL,'513',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1482,'{وَلَئِنْ أَخَّرْنَا عَنْهُمُ الْعَذَابَ إِلَى أُمَّةٍ مَعْدُودَةٍ} إلى أجلٍ وحينٍ معلومٍ {ليقولَّن ما يحبسه} ما يحبس العذاب عنا؟ تكذيباً واستهزاء فقال الله تعالى: {أَلا يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفًا عنهم} إذا أخذتهم سيوف المسلمين لم تغمد عنهم حتى يُباد الكفر وتعلوَ كلمة الإِخلاص {وحاق} نزل وأحاط {بهم} جزاء {ما كانوا به يستهزئون} وهو العذاب والقتل',NULL,'514',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1483,'{ولئن أذقنا الإِنسان} يعني: الوليد بن مغيرة {منَّا رحمة} رزقاً {ثمَّ نزعناها منه إنه ليؤوس} مُؤْيَسٌ قانطٌ {كَفُور} كافرٌ بالنِّعمة يريد: إنَّه لجهله بسعة رحمة الله يستشعر القنوط واليأس عند نزول الشِّدَّة',NULL,'514',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1484,'{ولئن أذقناه نعماء} الآية معناه: إنَّه يبطر فينسى حال الشِّدَّة ويترك حمد الله ما صرف عنه وهو قوله: {ليقولنَّ ذهب السيئات عني} فارقني الضُّرُّ والفقر {إنه لفرحٌ فخورٌ} يُفاخر المؤمنين بما وسَّعَ الله عليه ثمَّ ذكر المؤمنين فقال:',NULL,'514',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1485,'{إلاَّ الذين صبروا} والمعنى: لكن الذبن صبروا على الشِّدَّة والمكاره {وعملوا الصالحات} في السَّراء والضراء',NULL,'514',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1486,'{فلعلك تاركٌ} الآية قال المشركون لرسول الله صلى الله عليه وسلم: ائتنا بكتابٍ ليس فيه سبُّ آلهتنا حتى نتَّبعك وقال بعضهم: هلاَّ اُنزل عليك مَلَكٌ يشهد لك بالنُّبوَّة والصِّدق أو تُعطى كنزاً تستغني به أنت وأتباعك فهمَّ رسولُ الله صلى الله عليه وسلم أن يدع سبَّ آلهتهم فأنزل الله تعالى: {فَلَعَلَّكَ تَارِكٌ بَعْضَ مَا يُوحَى إِلَيْكَ} أَيْ: لعظيم ما يرد على قابك من تخليطهم تتوهَّم أنَّهم يُزيلونك عن بعض ما أنت عليه من أمر ربِّك {وَضَائِقٌ به صدرك أن يقولوا} أَيْ: ضائق صدرك بأن يقولوا {لَوْلا أُنْزِلَ عَلَيْهِ كَنْزٌ أَوْ جَاءَ مَعَهُ مَلَكٌ إِنَّمَا أَنْتَ نَذِيرٌ} عليك أن تنذرهم وليس أن تأتيهم بما يقترحون {والله على كل شيء وكيل} حافظٌ لكلِّ شيءٍ',NULL,'514',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1487,'{أم يقولون} بل أيقولون {افتراه} افترى القرآن وأتى به من قبل نفسه {قل فأتوا بعشر سورٍ مثله} مثل القرآن في البلاغة {مُفترياتٍ} بزعمكم {وادعوا من استطعتم من دون الله} إلى المعاونة على المعارضة {إِنْ كُنْتُمْ صَادِقِينَ} أنه افتراء',NULL,'515',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1488,'{فإلم يستجيبوا لكم} فإن لم يستجب لكم مَنْ تدعونهم إلى المعاونة ولم يتهيَّأ لكم المعارضة فقد قامت عليكم الحجَّة {فَاعْلَمُوا أَنَّمَا أُنْزِلَ بعلم الله} أَيْ: أُنزل والله عالمٌ بإنزاله وعالمٌ أنَّه من عنده {فهل أنتم مسلمون} استفهامٌ معناه الأمر كقوله: {فهل أنتم منتهون}',NULL,'515',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1489,'{من كان يريد الحياة الدنيا} أَيْ: مَنْ كان يريدها من الكفَّار ولا يؤمن بالبعث ولا بالثَّواب والعقاب {نوف إليهم أعمالهم} جزاء أعمالهم في الدُّنيا يعني: إنَّ مَنْ أتى من الكافرين فِعلاً حسناً من إطعام جائعٍ وكسوة عارٍ ونصرة مظلومٍ من المسلمين عُجِّل له ثواب ذلك في دنياه بالزِّيادة في ماله {وهم فيها} في الدُّنيا {لا يُبخسون} لا يُنقصون ثواب ما يستحقُّون فإذا وردوا الآخرة وردوا على عاجل الحسرة إذ لا حسنة لهم هناك وهو قوله تعالى:',NULL,'515',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1490,'{أُولَئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الآخِرَةِ إِلا النار} الآية',NULL,'516',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1491,'{أفمن كان} يعني: النَّبيَّ صلى الله عليه وسلم {على بينة من ربه} بيانٍ من ربِّه وهو القرآن {ويتلوه شاهد} وهو جبريل عليه السَّلام {منه} من الله عز وجل يريد أنَّه يتَّبعه ويؤيِّده ويشهده {ومن قبله} ومن قبل القرآن {كتاب موسى} التَّوراة يتلوه أيضاً في التَّصديق لأنّ موسى عليه السلام بشر في التوراة فالتوراة تتلو النبي صلى الله عليه وسلم في التصديق وقوله: {إماماً ورحمة} يعني أنَّ كتاب موسى كان إماماً لقومه ورحمة وتقدير الآية: أفمَنْ كان بهذه الصِّفة كمَنْ ليس يشهد بهذه الصِّفة؟ فترك ذكر المضادِّ له {أولئك يؤمنون به} يعني: من آمن به من أهل الكتاب {ومن يكفر به من الأحزاب} أصنافِ الكفَّار {فَالنَّارُ مَوْعِدُهُ فَلا تَكُ فِي مِرْيَةٍ مِنْهُ} من هذا الوعد {إِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَلَكِنَّ أَكْثَرَ النَّاسِ لا يؤمنون} يعني: أهل مكَّة',NULL,'516',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1492,'{وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا} فزعم أنَّ له ولداً وشريكاً {أُولَئِكَ يُعْرَضُونَ على ربهم} يوم القيامة {ويقول الأشهاد} وهم الأنبياء والملائكة والمؤمنون {هؤلاء الذين كَذَبوا على ربهم ألا لعنةُ اللَّهِ} إبعاده من رحمته {على الظالمين} المشركين',NULL,'516',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1493,'{الذين يصدون عن سبيل الله} تقدَّم تفسير هذه الآية',NULL,'517',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1494,'{أولئك لم يكونوا معجزين في الأرض} أَيْ: سابقين فائتين لم يعجزونا أن نعذِّبهم في الدُّنيا ولكن أخَّرْنا عقوبتهم {وَمَا كَانَ لَهُمْ مِنْ دُونِ اللَّهِ مِنْ أولياء} يمنعونهم من عذاب الله {يضاعف لهم العذاب} لإِضلالهم الأتباع {ما كانوا يستطيعون السمع} لأني حُلْتُ بينهم وبين الإِيمان فكانوا صُمَّاً عن الحقِّ فلا يسمعونه وعمياً عنه فلا يبصرونه ولا يهتدون',NULL,'517',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1495,'{أولئك الَّذِينَ خَسِرُوا أَنْفُسَهُمْ} بأن صاروا إلى النَّار {وَضَلَّ عَنْهُمْ مَا كَانُوا يَفْتَرُونَ} بطل افتراؤهم في الدنيا فلم يبفعهم شيئاً',NULL,'517',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1496,'{لا جرم} حقَّاً {أَنَّهُمْ فِي الآخِرَةِ هُمُ الأَخْسَرُونَ}',NULL,'517',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1497,'{إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَخْبَتُوا إِلَى ربهم} اطمأنُّوا وسكنوا وقيل: تابوا',NULL,'517',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1498,'{مثل الفريقين} فريق الكافرين وفريق المسلمين {كالأعمى والأصم} وهو الكافر {والبصيرِ والسميع} وهو المؤمن {هل يستويان مثلاً} أَيْ: في المَثل أَيْ: هل يتشابهان؟ {أَفَلا تَذَكَّرُونَ} أفلا تتعظون يا أهل مكَّة',NULL,'517',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1499,'{ولقد أرسلنا نوحاً إلى قومه} فقال لهم: يا قومي {إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ}',NULL,'518',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1500,'{أن لا تعبدوا إلاَّ الله} أَيْ: أُنذركم لِتُوحِّدوا الله وتتركوا عبادة غيره {إني أخاف عليكم} بكفركم {عذاب يومٍ أليم} مؤلمٍ',NULL,'518',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1501,'{فقال الملأ الذين كفروا من قومه} وهم الأشراف والرُّؤساء: {ما نراك إلاَّ بشراً مثلنا} إنساناً مثلنا لا فضل لك علينا {وَمَا نراك اتبعك إلاَّ الذين هم أراذلنا} أخسَّاؤنا يعنون: مَنْ لا شرفَ لهم ولا مال {بادي الرأي} اتَّبعوك في ظاهر الرَّأي وباطنهم على خلاف ذلك {وما نرى لكم} يعنون لنوحٍ وقومه {علينا من فضل} وهذا تكذيبٌ منهم لأنَّ الفضل كلَّه في النُّبوَّة {بل نظنُّكم كاذبين} ليس ما أتيتنا به من الله',NULL,'518',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1502,'{قال يا قوم أرأيتم} أَيْ: أعلمتم {إنْ كنت على بينة من ربي} يقينٍ وبرهانٍ {وآتاني رحمة من عنده} نُبوَّةً {فعميت عليكم} فخفيت عليكم لأنَّ الله تعالى سلبكم علمها ومنعكم معرفتها لعنادكم الحقَّ {أنلزمكموها} أَنُلزمكم قبولها ونضطركم إلى معرفتها إذا كرهتم؟',NULL,'518',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1503,'{وَيَا قَوْمِ لا أَسْأَلُكُمْ عَلَيْهِ} على تبليغ الرِّسالة {مَالا إِنْ أَجْرِيَ إِلا عَلَى اللَّهِ وما أنا بطارد الذين آمنوا} سألوه طرد المؤمنين عنه ليؤمنوا به أنفةً من أن يكونوا معهم على سواء فقال: لا يجوز لي طردهم إذ كانوا يلقون الله فيجزيهم بإيمانهم ويأخذ لهم ممَّن ظلمهم وصغَّر شؤونهم وهو قوله: {إنهم ملاقوا ربهم ولكني أراكم قوماً تجهلون} أنَّ هؤلاء خيرٌ منكم لإِيمانهم وكفركم',NULL,'518',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1504,'{وَيَا قَوْمِ مَنْ يَنْصُرُنِي مِنَ اللَّهِ} مَنْ يمنعني من عذاب الله {إن طردتهم} ؟',NULL,'519',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1505,'{ولا أقول لكم عندي خزائن الله} يعني: مفاتيح الغيب وهذا جوابٌ لقولهم: اتَّبعوك في ظاهرِ ما نرى منهم وهم في الباطن على خلافك فقال مجيباً لهم: {وَلا أَقُولُ لَكُمْ عِنْدِي خَزَائِنُ الله} غيوب الله {ولا أعلم الغيب} ما يغيب عني ممَّا يسترونه في نفوسهم فسبيلي قبول ما ظهر منهم {ولا أقول إني مَلَك} جوابٌ لقولهم: {مَا نَرَاكَ إِلا بَشَرًا مِثْلَنَا} {ولا أقول للذين تزدري} تستصغر وتستحقر {أعينكم} يعني: المؤمنين: {لَنْ يُؤْتِيَهُمُ اللَّهُ خَيْرًا اللَّهُ أَعْلَمُ بِمَا في أنفسهم} أَيْ: بضمائرهم وليس عليَّ أن أطَّلع على ما في نفوسهم {إِنِّي إِذًا لَمِنَ الظَّالِمِينَ} إن طردتهم تكذيباً لهم بعد ما ظهر لي منهم الإيمان وقوله:',NULL,'519',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1506,'{قَالُوا يَا نُوحُ قَدْ جَادَلْتَنَا فَأَكْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ}',NULL,'519',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1507,'{قَالَ إِنَّمَا يَأْتِيكُمْ بِهِ اللَّهُ إِنْ شَاءَ وما أنتم بمعجزين}',NULL,'519',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1508,'{إن كان الله يريد أن يغويكم} أَيْ: يُضِلَّكم ويوقع الغيَّ في قلوبكم لما سبق لكم من الشَّقاء {هو ربكم} خالقكم وسيِّدكم وله أن يتصرَّف فيكم كما يشاء',NULL,'519',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1509,'{أم يقولون} بل يقولون {افتراه} اختلف ما أتى به من الوحي {قل إن افتريته فعليَّ إجرامي} عقوبة جرمي {وأنا بريء مما تجرمون} من الكفر والتكذيب وقوله:',NULL,'519',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1510,'{فلا تبتئس} أَيْ: لا تحزن ولا تغتم',NULL,'520',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1511,'{واصنع الفلك بأعيننا} بمرأى منا وتأويله: بحفظنا إيَّاك حفظ مَنْ يراك ويملك دفع السُّوء عنك {ووحينا} وذلك أنَّه لم يعلم صنعة الفلك حتى أوحى الله إليه كيف يصنعها {ولا تخاطبني} لا تراجعني ولا تحاورني {في الذين ظلموا} في إمهالهم وتأخير العذاب عنهم وقوله:',NULL,'520',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1512,'{إن تسخروا منا} أَيْ: لما يرون من صنعه الفلك {فإنا نسخر منكم} ونعجب من غفلتكم عمَّا قد أظلَّكم من العذاب',NULL,'520',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1513,'{فسوف تعلمون مَنْ يأتيه عذابٌ يخزيه} أَيْ: فسوف تعلمون مَنْ أخسر عاقبةً',NULL,'520',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1514,'{حتى إذا جاء أمرنا} بعذابهم وهلاكهم {وفار التنور} بالماء يعني: تنُّور الخابز وكان ذلك علامةً لنوح عليه السَّلام فركب السَّفينة {قلنا احمل فيها} في الفلك {من كلّ زوجين} من كلِّ شيءٍ له زوج {اثنين} ذكراً وأنثى {وأهلك} واحمل أهلك يعني: ولده وعياله {إِلا مَنْ سَبَقَ عَلَيْهِ القول} يعني: مَنْ كان في علم الله أنَّه يغرق بكفره وهو امرأته واغلة وابنه كنعان {وَمَنْ آمَنَ} واحمل مَنْ صدَّقك {وما آمن معه إلاَّ قليل} ثمانون إنساناً',NULL,'520',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1515,'{وَقَالَ} نوحٌ لقومه الذين أُمر بحملهم: {ارْكَبُوا} يعني: الماء {فيها} في الفلك {بسم الله مجريها ومرساها} يريد: تجري باسم الله وترسي باسم الله فكان إذا أراد أن تجري السفينة قال: بسم الله فجرت وإذا أراد أن ترسي قال: بسم الله فَرَسَتْ أَيْ: ثبتت {إن ربي لغفور} لأصحاب السَّفينة {رحيم} بهم',NULL,'521',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1516,'{وهي تجري بهم في موج} جمع موجةٍ وهي ما يرتفع من الماء {كالجبال} في العِظَم {ونادى نوح ابنه} كنعان وكان كافراً {وكان في معزل} من السَّفينة أَيْ: في ناحيةٍ بعيدة عنها',NULL,'521',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1517,'{قال سآوي إلى جبل} أنضمُّ إلى جبلٍ {يعصمني} يمنعني {من الماء} فلا أغرق {قال} نوح: {لا عَاصِمَ الْيَوْمَ مِنْ أَمْرِ اللَّهِ} لا مانع اليوم من عذاب الله {إِلا من رحم} لكن من رحم الله فإنَّه معصوم {وحال بينهما} بين ابن نوحٍ وبين الجبل {الموج} ما ارتفع من الماء',NULL,'521',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1518,'{وقيل يا أرض ابلعي ماءك} اشربي ماءك {ويا سماء أقلعي} أمسكي عن إنزال الماء {وغيض الماء} نقص {وَقُضِيَ الأَمْرُ} أُهلك قوم نوحٍ وفُرِغ من ذلك {واستوت} السَّفينة {على الجودي} وهو جبل بالجزيرة {وقيل بعداً} من رحمة الله {للقوم الظالمين} المتَّخذين من دون الله آلهاً',NULL,'521',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1519,'{وَنَادَى نُوحٌ رَبَّهُ فَقَالَ رَبِّ إِنَّ ابْنِي} كنعان {من أهلي وإنَّ وعدك الحق} وعدتني أن تنجيني وأهلي أَيْ: فأنجه من الغرق {وأنت أحكم الحاكمين} أعدل العادلين',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1520,'{قَالَ يَا نُوحُ إِنَّهُ لَيْسَ مِنْ أَهْلِكَ} الذين وعدتك أن أُنجيهم {إنه عمل غير صالح} أيْ: سؤالك إيَّاي أن أنجي كافراً عملٌ غير صالح وقيل: معناه: إنَّ ابنك ذو عَمَلٌ غَيْرُ صَالِحٍ {فَلا تَسْأَلْنِ مَا لَيْسَ لك به علم} وذلك أنَّ نوحاً لم يعلم أنَّ سؤاله ربَّه نجاةَ ولدِه محظورٌ عليه مع إصراره على الكفر حتى أعلمه الله سبحانه ذلك والمعنى: فلا تسألني ما ليس لك به علمٌ بجواز مسألته {إِنِّي أَعِظُكَ} أنهاك {أَنْ تَكُونَ مِنَ الْجَاهِلِينَ} من الآثمين فاعتذر نوحٌ عليه السَّلام لمَّا أعلمه الله سبحانه أنَّه لا يجوز له أن يسأل ذلك وقال:',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1521,'{رَبِّ إِنِّي أَعُوذُ بِكَ أَنْ أَسْأَلَكَ مَا لَيْسَ لِي بِهِ عِلْمٌ وَإِلا تَغْفِرْ لِي} جهلي {وَتَرْحَمْنِي أَكُنْ مِنَ الْخَاسِرِينَ}',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1522,'{قيل يا نوح اهبط} من السَّفينة إلى الأرض {بسلامٍ} بسلامةٍ وقيل: بتحيَّةٍ {منا وبركات عليك} وذلك أنَّه صار أبا البشر لأنَّ جميع مَن بقي كانوا من نسله {وعلى أمم ممن معك} أَيْ: من أولادهم وذرايتهم وهم المؤمنون وأهل السَّعادة إلى يوم القيامة {وأمم سنمتعهم} في الدُّنيا يعني: الأمم الكافرة من ذريَّته إلى يوم القيامة',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1523,'{تلك} القصَّة التي أخبرتك بها {مِنْ أَنْبَاءِ الْغَيْبِ} أخبار ما غاب عنك وعن قومك {فاصبر} كما صبر نوح على أذى قومه {إِنَّ العاقبة للمتقين} آخر الأمر بالظَّفر لك ولقومك كما كان لمؤمني قوم نوحٍ وقوله:',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1524,'{إن أنتم إلاَّ مفترون} ما أنتم إلاَّ كاذبون في إشراككم الأوثان وقوله:',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1525,'{يَا قَوْمِ لا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا إِنْ أَجْرِيَ إِلا عَلَى الذي فطرني أفلا تعقلون}',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1526,'{يرسل السماء عليكم مدراراً} كثير الدَّرِّ يعني: المطر {ويزدكم قوة إلى قوتكم} يعني: المال والولد وكان الله سبحانه قد حبس عنهم المطر ثلاث سنين وأعقم أرحام نسائهم فقال لهم هود: إن آمنتم أحيا الله سبحانه بلادكم ورزقكم المال والولد',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1527,'{قالوا} مُنكرين لنبوَّته: {يا هود ما جئتنا ببينة} بحجَّةٍ واضحةٍ وقوله:',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1528,'{اعتراك} أصابك ومسَّك {بعض آلهتنا بسوء} بجنونٍ فأفسد عقلك فالذي يظهر مِنْ عيبها لما لحق عقلك من التَّغيير {قال} نبيُّ الله عليه السَّلام عند ذلك: {إِنِّي أُشْهِدُ اللَّهِ وَاشْهَدُوا أَنِّي بَرِيءٌ مِمَّا تشركون} أَيْ: إن كانت عندكم الأصنام أنَّها عاقبتني لطعني عليها فإني أزيد الآن في الطَّعن عليها وقوله:',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1529,'{فكيدوني جميعاً} احتالوا أنتم وأوثانكم في عداوتي {ثم لا تنظرون} لا تؤجلون وقوله:',NULL,'523',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1530,'{مَا مِنْ دَابَّةٍ إِلا هُوَ آخِذٌ بِنَاصِيَتِهَا} أَيْ: هي في قبضته وتنالها بما شاء قدرته {إنَّ ربي على صراط مستقيم} أَيْ: إنَّ الذي بعثني الله به دينٌ مستقيمٌ',NULL,'524',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1531,'{فإن تولوا} تتولَّوا بمعنى: تُعرضوا عمَّا دعوتكم إليه من الإِيمان {فقد أبلغتكم ما أرسلت به إليكم} فقد ثبتت الحُجَّة عليكم بإبلاغي {ويستخلف ربي قوماً غيركم} أَيْ: ويخلف بعدكم مَنْ هو أطوعُ له منكم {ولا تضرونه} بإعراضكم {شيئاً} إنَّما تضرُّون أنفسكم {إنَّ ربي على كل شيء} من أعمال العباد {حَفِيظٌ} حتى يجازيهم عليها',NULL,'524',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1532,'{ولما جاء أمرنا} بهلاك عادٍ {نَجَّيْنَا هُودًا وَالَّذِينَ آمَنُوا مَعَهُ برحمةٍ منا} حيث هديناهم إلى الإِيمان وعصمناهم من الكفر {ونجيناهم من عذاب غليظ} يعني: ما عُذِّب به الذين كفروا',NULL,'524',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1533,'{وَتِلْكَ عَادٌ} يعني: القبيلة {جَحَدُوا بِآيَاتِ رَبِّهِمْ} كذَّبوها فلم يُقِرّوا بها {وعصوا رسله} يعني: هوداً عليه السَّلام لأنَّ مَنْ كذَّب رسولاً واحداً فقد كفر بجميع الرُّسل {وَاتَّبَعُوا أَمْرَ كُلِّ جَبَّارٍ عَنِيدٍ} واتَّبع السَّفلةُ الرُّؤساءَ والعنيد: المعارضُ لك بالخلاف',NULL,'524',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1534,'{وأتبعوا في هذه الدنيا لعنةً} أُردفوا لعنةً تلحقهم وتنصرف معهم {وَيَوْمَ الْقِيَامَةِ} أَيْ: وفي يوم القيامة كما قال: {لعنوا في الدنيا والآخرة} {ألاَ إنَّ عاداً كفروا ربهم} قيل: بربِّهم وقيل: كفروا نعمة ربِّهم {ألا بعداً لعاد} يريد: بعدوا من رحمة الله تعالى وقوله:',NULL,'524',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1535,'{هو أنشأكم} أَيْ: خلقكم {من الأرض} من آدم وآدم خُلق من تراب الأرض {واستعمركم فيها} جعلكم عمَّاراً لها',NULL,'525',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1536,'{قَالُوا يَا صَالِحُ قَدْ كُنْتَ فِينَا مَرْجُوًّا قبل هذا} وذلك أنَّ صالحاً عليه السَّلام كان يعدل عن دينه ويشنأ أصنامهم وكانوا يرجون رجوعه إلى دين غشيرته فلمَّا أظهر دعاءهم إلى الله تعالى زعموا أنَّ رجاءهم انقطع منه وقوله {مريب} موقعٍ في الرِّيبة',NULL,'525',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1537,'{قال يا قوم أرأيتم} الآية يقول: أعلمتم مَنْ ينصرني من الله أَيْ: مَنْ يمنعني من عذاب الله إِنْ عَصَيْتُهُ بعد بيِّنةٍ من ربِّي ونعمةٍ {فما تزيدونني غير تخسير} أَيْ: ما تزيدونني باحتجاجكم بعبادة آبائكم الأصنام وقلكم: {أتنهانا أن نعبدَ ما يعبدُ آباؤُنا} إلاَّ بنسبتي إيَّاكم إلى الخسارة أَيْ: كلَّما اعتذرتم بشيءٍ زادكم تخسيراً وقيل معنى الآية: ما تزيدونني غير تخسيرٍ لي إن كنتم أنصاري وعنى التَّخسير: التَّضليل والإِبعاد من الخير وقوله:',NULL,'525',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1538,'{وَيَا قَوْمِ هَذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ وَلا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عذاب قريب}',NULL,'525',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1539,'{تمتعوا في داركم} أَيْ: عيشوا في بلادكم {ثلاثة أيام ذلك وعدٌ} للعذاب {غير مكذوب} غير كذبٍ وقوله:',NULL,'526',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1540,'{ومن خزي يومئذٍ} أَيْ: نجَّيناهم من العذاب الذي أهلك قومه ومن الخزي الذي لزمهم وبقي العارُ فيهم مأثوراً عنهم فالواوُ في {ومِنْ} نسقٌ على محذوف وهو العذاب',NULL,'526',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1541,'{وأخذ الذين ظلموا الصيحة} لمَّا أصبحوا اليوم الرَّابع أتتهم صيحةٌ من السَّماء فيها صوت كلِّ صاعقةٍ وصوت كلِّ شيء في الأرض فتقطَّعت قلوبهم في صدورهم',NULL,'526',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1542,'{كَأَنْ لَمْ يَغْنَوْا فِيهَا أَلا إِنَّ ثَمُودَ كفروا ربهم ألا بعدا لثمود}',NULL,'526',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1543,'{ولقد جاءت رسلنا} يعني: الملائكة الذين أتوا {إبراهيم} عليه السَّلام على صورة من الأضياف {بالبشرى} بالبشارة بالولد {قَالُوا سَلامًا} أَيْ: سلِّموا سلاماً {قَالَ سَلامٌ} أَيْ: عليكم السلام {فما لبث أن جاء بعجل حنيذٍ} مشويٍّ',NULL,'526',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1544,'{فلما رأى أيديهم لا تصل إليه} إلى العجل {نكرهم} أنكرهم {وأوجس منهم خيفة} أضمر منهم خوفاً ولم يأمن أن يكونوا جاؤوا لبلاءٍ لمَّا لم يتحرَّموا بطعامه فلمَّا رأوا علامة الخوف في وجهه {قَالُوا لا تَخَفْ إِنَّا أُرْسِلْنَا إِلَى قَوْمِ لوط} بالعذاب',NULL,'526',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1545,'{وامرأته} سارة {قائمة} وراء السِّتر تتسمَّع إلى الرُّسل {فضحكت} سرورا بالأمن قالوا: {إنا أرسلنا إلى قوم لوط} وذلك أنَّها خافت كما خاف إبراهيم عليه السَّلام فقيل لها: يا أيتها الضَّاحكة ستلدين غلاماً فذلك قوله: {فبشرناها بإسحاق ومن وراء إسحاق} أَيْ: بعده {يعقوب} عليهما السَّلام وذلك أنَّهم بشَّروها بأنَّها تعيش إلى أن ترى ولد ولدها',NULL,'527',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1546,'{قالت يا ويلتي أألد وأنا عجوز} وكانت بنت تسع وتسعين سنةً {وهذا بعلي شيخاً} وكان ابن مائة سنة واثنتي عشرة سنة {إنَّ هذا} الذي تذكرون من ولادتي على كبر سنِّي وسنِّ بعلي {لشيء عجيب} معجب',NULL,'527',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1547,'{قالوا أتعجبين من أمر الله} قضاء الله وقدره {رحمة الله وبركاته عليكم أَهْلَ الْبَيْتِ} يعني: بيت إبراهيم عليه السَّلام فكان من تلك البركات أنَّ الأسباط وجميع الأنبياء كانوا من إبراهيم وسارة وكان هذا دعاءً من الملائكة لهم وقوله: {إنّه حميدٌ} أي: محمود {مجيد} كريمٌ',NULL,'527',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1548,'{فلما ذهب عن إبراهيم الروع} الفزع {وجاءته البشرى} بالولد {يجادلنا} أَيْ: أقبل وأخذ يجادل رسلنا {في قوم لوط} وذلك أنَّهم لما قالوا لإِبراهيم عليه السلام: {إنا مهلكوا أهلِ هذه القرية} قال لهم: أرأيتم إن كان فيها خمسون من المسلمين أتهلكونهم؟ قالوا: لا قال: فأربعون؟ قالوا: لا فما زال ينقص حتى قال: فواحدٌ؟ قالوا: لا فاحتجَّ عليهم بلوط و {قَالَ: إِنَّ فِيهَا لُوطًا قَالُوا: نَحْنُ أَعْلَمُ} الآية فهذا معنى جداله وعند ذلك قالت الملائكة:',NULL,'527',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1549,'{إِنَّ إِبْرَاهِيمَ لَحَلِيمٌ أَوَّاهٌ مُنِيبٌ}',NULL,'528',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1550,'{يا إبراهيم أعرض عن هذا} الجدال وخرجوا من عنده فأتوا قرية قوم لوطٍ وذلك قوله:',NULL,'528',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1551,'{ولما جاءت رسلنا لوطاً سِيءَ بهم} حزن بمجيئهم لأنَّه رآهم في أحسن صورةٍ فخاف عليهم قومه وعلم أنَّه يحتاج إلى المدافعة عنهم وكانوا قد أتوه في صورة الأضياف {وضاق بهم ذرعاً} أَيْ: صدراً {وقال هذا يوم عصيب} شديدٌ ولمَّا علم قومه بمجيء قومٍ حسانِ الوجوه أضيافاً للوط قصدوا داره وذلك قوله:',NULL,'528',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1552,'{وجاءه قومه يهرعون إليه} أَيْ: يُسرعون إليه {وَمِنْ قَبْلُ} أَيْ: ومِنْ قبل مجيئهم إلى لوطٍ {كَانُوا يَعْمَلُونَ السَّيِّئَاتِ} يعني: فعلهم المنكر {قال يا قومِ هؤلاء بناتي} أُزوِّجكموهنَّ فـ {هنَّ أَطْهَرُ لكم} من نكاح الرِّجال أراد أن يقي أضيافه ببناته {فاتقوا الله ولا تخزون في ضيفي} لا تفضحوني فيهم لأنَّهم إذا هجموا إلى أضيافه بالمكروه لحقته الفضيحة {أَلَيْسَ مِنْكُمْ رَجُلٌ رشيد} يأمر بالمعروف وينهى عن المنكر',NULL,'528',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1553,'{قَالُوا لَقَدْ عَلِمْتَ مَا لَنَا فِي بَنَاتِكَ من حق} لَسْنَ لنا بأزواجٍ فنستحقهنَّ {وَإِنَّكَ لَتَعْلَمُ مَا نريد} أَيْ: إنَّا نريد الرِّجال لا النِّساء',NULL,'528',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1554,'{قال لو أنَّ لي بكم قوَّة} لو أنَّ معي جماعةً أقوى بها عليكم {أو آوي} أنضمُّ {إلى ركن شديد} عشيرةٍ تمنعني وتنصرني لَحُلْتُ بينكم وبين المعصية فلمَّا رأت الملائكة ذلك',NULL,'529',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1555,'{قَالُوا يَا لُوطُ إِنَّا رُسُلُ رَبِّكَ لَنْ يصلوا إليك} بسوءٍ فإنَّا نحولُ بينهم وبين ذلك {فأسرِ بأهلك بقطع من الليل} في ظلمة اللَّيل {ولا يلتفت منكم أحد} لا ينظر أحدٌ إلى ورائه إذا خرج من قريته {إلاَّ امرأتك} فلا تسرِ بها وخلِّفها مع قومها فإنَّ هواها إليهم و {إنَّه مصيبها ما أصابهم} من العذاب {إنَّ موعدهم الصبح} للعذاب فقال لوط: أريد أعجلَ من ذلك بل السَّاعةَ يا جبريل فقالوا له: أَلَيْسَ الصُّبْحُ بِقَرِيبٍ',NULL,'529',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1556,'{فلما جاء أمرنا} عذابنا {جعلنا عاليها سافلها} وذلك أنَّ جبريل عليه السَّلام أدخل جناحه تحتها حتى قلعها وصعد بها إلى السَّماء ثمَّ قلبها إلى الأرض {وأمطرنا عليها حجارة} قبل قلبهاإلى الأرض {من سجيل} من طينٍ مطبوخٍ طُبخ حتى صار كالآجر فهو سنك كل بالفارسية فَعُرِّب {منضود} يتلو بعضه بعضاً',NULL,'529',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1557,'{مسومة} معلمة بعلامة بها أنَّها ليست من حجارة أهل الدُّنيا {عند ربك} في خزائنه التي لا يُتصرَّف في شيءٍ منها إلاَّ بإرادته {وما هي من الظالمين ببعيد} يعني: كفَّار قريش يُرهبهم بها',NULL,'529',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1558,'{وإلى مدين} ذكرنا تفسير هذه الاية في سورة الأعراف وقوله: {إني أراكم بخير} يعني: النِّعمة والخصب يقول: أيُّ حاجةٍ بكم إلى التَّطفيف مع ما أنعم الله سبحانه به عليكم من المال ورخص السِّعر {وإني أخاف عليكم عذاب يومٍ محيط} يُوعدهم بعذابٍ يُحيط بهم فلا يفلت منهم أحدٌ',NULL,'530',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1559,'{ويا قوم أوفوا المكيال والميزان بالقسط} أَتِمّوهما بالعدل',NULL,'530',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1560,'{بقية الله} أَيْ: ما أبقى الله لكم بعد إيفاء الكيل والوزن {خير لكم} من البخس يعني: من تعجيل النَّفع به {إن كنتم مؤمنين} مصدقين في نعمه شَرَطَ الإِيمانَ لأنَّهم إنَّما يعرفون صحَّة ما يقول إذا كانوا مؤمنين {وَمَا أَنَا عَلَيْكُمْ بِحَفِيظٍ} أَيْ: لم أُؤمر بقتالكم وإكراهكم على الإِيمان',NULL,'530',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1561,'{قَالُوا يَا شُعَيْبُ أَصَلاتُكَ تَأْمُرُكَ أَنْ نَتْرُكَ ما يعبد آباؤنا} يريدون: دينُك يأمرك أَيْ: أفي دينك الأمر بذا؟ {أَوْ أَنْ نَفْعَلَ فِي أَمْوَالِنَا مَا نَشَاءُ} من البخس والظُّلم ونقص المكيال والميزان {إِنَّكَ لَأَنْتَ الْحَلِيمُ الرَّشِيدُ} أيْ: السَّفيه الجاهل وقالوا: الحليم الرَّشيد على طريق الاستهزاء',NULL,'530',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1562,'{قال يا قوم أرأيتم} أعلمتم {إن كنت على بينة من ربي} بيانٍ وحجَّةٍ من ربي {ورزقني منه رزقاً حسناً} حلالاً وذلك أنَّه كان كثير المال وجواب إنْ محذوف على معنى: إنْ كنت على بينة من ربي ورزقني المال الحلال أتَّبع الضَّلال فأبخس وأُطفف؟ يريد: إنَّ الله تعالى قد أغناه بالمال الحلال {وَمَا أُرِيدُ أَنْ أخالفكم إلى ما أنهاكم عنه} أَيْ: لست أنهاكم عن شيءٍ وأدخل فيه وإنَّما أختار لكم ما أختار لنفسي {إن أريد} ما أريد {إلاَّ الإصلاح} فيما بيني وبينكم بأن تعبدوا الله وحده وأَنْ تفعلوا ما يفعل مَنْ يخاف الله {ما استطعت} أَيْ: بقدر طاقتي وطاقة الإِبلاغ والإِنذار ثمَّ أخبر أنَّه لا يقدر هو ولا غيره على الطَّاعة إلاَّ بتوفيق الله سبحانه فقال: {وَمَا تَوْفِيقِي إِلا بِاللَّهِ عَلَيْهِ تَوَكَّلْتُ وإليه أنيب} أرجع في الميعاد',NULL,'530',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1563,'{ويا قوم لا يجرمنكم شقاقي} لا يكسبنَّكم خلافي وعداوتي {أَنْ يُصِيبَكُمْ} عذاب العاجلة {مِثْلُ مَا أَصَابَ قوم نوحٍ} من الغرق {أو قوم هود} من الرِّيح العقيم {أو قوم صالح} من الرَّجفة والصِّيحة {وما قوم لوط منكم ببعيد} في الزَّمان الذي بينكم وبينهم وكان إهلاكهم أقربَ الإِهلاكات التي عرفوها',NULL,'531',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1564,'{واستغفروا ربكم} اطلبوا منه المغفرة {ثمَّ توبوا إليه} توصَّلوا إليه بالتَّوبة {إنَّ ربي رحيم} بأوليائه {ودودٌ} محبٌّ لهم',NULL,'531',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1565,'{قَالُوا يَا شُعَيْبُ مَا نَفْقَهُ} ما نفهم {كثيراً مما تقول} أَيْ: صحَّته يعنون: ما يذكر من التَّوحيد والبعث والنُّشور {وإنا لنراك فينا ضعيفاً} لأنَّه كان أعمى {ولولا رهطك} عشيرتك {لرجمناك} قتلناك {وما أنت علينا بعزيز} بمنيعٍ',NULL,'531',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1566,'{قَالَ يَا قَوْمِ أَرَهْطِي أَعَزُّ عَلَيْكُمْ مِنَ الله} يريد: أمنع عليكم من الله كأنَّه يقول: حفظكم إيَّاي في الله أولى منه في رهطي {واتَّخذتموه وراءكم ظهرياً} ألقيتموه خلف ظهوركم وامتنعتم من قتلي مخافة قومي والله أعزُّ وأكبر من جميع خلقه {إنَّ ربي بما تعملون محيط} خبيرٌ بأعمال العباد حتى يجازيهم بها ثمَّ هدَّدهم فقال:',NULL,'532',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1567,'{ويا قوم اعملوا} الآية يقول: اعملوا على ما أنتم عليه {إني عاملٌ} على ما أنا عليه من طاعة الله وسترون منزلتكم من منزلتي وهو قوله: {سوف تعلمون مَنْ يأتيه عذاب يخزيه} يفضحه ويذله {ومَنْ هو كاذب} منَّا {وارتقبوا إني معكم رقيب} ارتقبوا العذاب من الله سبحانه إنِّي مرتقب من الله سبحانه الرَّحمة وقوله:',NULL,'532',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1568,'{أخذت الَّذِينَ ظَلَمُوا الصَّيْحَةُ} صاح بهم جبريل صيحةً فماتوا في أمكنتهم',NULL,'532',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1569,'{ألا بعداً لمدين} أَيْ: قد بعدوا من رحمة الله سبحانه',NULL,'532',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1570,'{ولقد أرسلنا موسى بآياتنا} يريد: التَّوراة وما أنزل الله فيها من الأحكام {وسلطان مبين} وحجَّةٍ بيِّنةٍ وهي العصا',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1571,'{وَمَا أَمْرُ فِرْعَوْنَ بِرَشِيدٍ} بمرشدٍ إلى خيرٍ',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1572,'{يقدم قومه} يتقدَّمهم إلى النَّار وهو قوله: {فأوردهم النار} أدخلهم النار {وبئس الورد المورود} المدخل المدخول',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1573,'{وأتبعوا في هذه} الدنيا {لعنة} يعني: الغرق {وَيَوْمَ الْقِيَامَةِ} يعني: ولعنة يوم القيامة وهو عذاب جهنَّم {بئس الرفد المرفود} يعني: اللَّعنة بعد اللَّعنة وقوله:',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1574,'{منها قائمٌ وحصيد} أَيْ: من القرى التي أُهلكت قائمٌ بقيت حيطانه وحصيدٌ مخسوفٌ به قد مُحي أثره',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1575,'{وَمَا ظَلَمْنَاهُمْ} بالعذاب والإِهلاك {وَلَكِنْ ظَلَمُوا أَنْفُسَهُمْ} بالكفر والمعصية {فما أغنت عنهم} ما نفعتهم وما دفعت عنهم {آلهتهم التي يدعون} يعبدون {من دون الله} سوى الله {وما زادوهم} وما زادتهم عبادتها {غير تتبيب} بلاءٍ وهلاكٍ وخسارةٍ',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1576,'{وكذلك} وكما ذكرنا من أهلاك الأمم {أخذ ربك} بالعقوبة {إذا أخذ القرى وهي ظالمة} يعني: أهلها',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1577,'{إِنَّ فِي ذَلِكَ} يعني: ما ذكر من عذاب الأمم الخالية {لآية} لعبرةً {لِمَنْ خَافَ عَذَابَ الآخِرَةِ ذَلِكَ يَوْمٌ مَجْمُوعٌ له الناس} لأنَّ الخلق كلهم يحشرون ويجمعون لذلك اليوم {وذلك يوم مشهود} يشهد البرُّ والفاجر',NULL,'533',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1578,'{وما نؤخره} وما نؤخِّر ذلك اليوم فلا نُقيمه عليكم {إلاَّ لأجلٍ معدود} لوقتٍ معلومٍ ولا يعلمه أحدٌ غير الله سبحانه',NULL,'534',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1579,'{يوم يأتِ} ذلك اليوم {لا تَكَلَّمُ نَفْسٌ إِلا بِإِذْنِهِ فَمِنْهُمْ شَقِيٌّ وسعيد} فمن الأنفس في ذلك اليوم شقيٌّ وسعيدٌ',NULL,'534',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1580,'{فَأَمَّا الَّذِينَ شَقُوا فَفِي النَّارِ لَهُمْ فِيهَا زفير وشهيق} وهما من أصوات المكروبين والمحزونين والزَّفير مثل أوَّل نهيق الحمار والشَّهيق آخره إذا ردَّده في الجوف',NULL,'534',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1581,'{خالدين فيها ما دامت السماوات والأرض} أبداً وهذا من ألفاظ التأبيد {إلاَّ ما شاء ربك} أن يحرجهم ولكنَّه لا يشاء ذلك والمعنى: لو شاء أن لا يخلِّدهم لقدر وقيل: إلاَّ ما شاء ربك يعني: إلاَّ مقدار مكثهم في الدُّنيا والبرزخ والوقوف للحساب ثمَّ يصيرون إلى النَّار أبداً وقوله:',NULL,'534',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1582,'{عطاء غير مجذوذ} أَيْ: مقطوعٍ',NULL,'534',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1583,'{فلا تك} يا محمَّدُ {في مرية} شكٍّ {ممَّا يعبد هؤلاء} أيْ: مِن حال ما يعبدون في أنَّها لا تضرُّ ولا تنفع {مَا يَعْبُدُونَ إِلا كَمَا يَعْبُدُ آبَاؤُهُمْ مِنْ قَبْلُ} أَيْ: كعبادة آبائهم يريد: إنَّهم على طريق التَّقليد يعبدون الأوثان كعبادة آبائهم {وَإِنَّا لَمُوَفُّوهُمْ نَصِيبَهُمْ} من العذاب {غير منقوص}',NULL,'534',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1584,'{ولقد آتينا موسى الكتاب فاختلف فيه} هذه الآية تعزية للنبي صَلَّى اللَّهُ عَلَيْهِ وسلم وتسليةٌ له باختلاف قوم موسى في كتابه {ولولا كلمة سبقت من ربك} بتأخير العذاب عن قومك {لَقُضي بينهم} لَعُجِّل عقابهم وفُرِغَ من ذلك {وإنهم لفي شك منه} من القرآن {مريب} موقعٍ للرِّيبة',NULL,'535',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1585,'{وإنَّ كلاًّ} من البرِّ والفاجر والمؤمن والكافر {لما} يعني: لمَنْ في قول الفرَّاء وفي قول البصريين ما زائدة والمعنى: وإنَّ كلاً {ليوفينهم ربك أعمالهم} أَيْ: ليتمنَّ لهم جزاء أعمالهم',NULL,'535',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1586,'{فاستقم} على العمل بأمر ربك والدُّعاء إليه {كما أمرت} في القرآن {ومن تاب معك} يعني: أصحابه أَيْ: وليستقيموا هم أيضاً على ما أُمروا به {ولا تَطْغَوا} تواضعوا لله ولا تتجبَّروا على أحدٍ {إنه بما تعملون بصير} لا تخفى عليه أعمال بني آدم',NULL,'535',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1587,'{ولا تركنوا إلى الذين ظلموا} لا تُداهنوهم ولا ترضوا بأعمالهم يعني: الكفَّار {فتمسكم النار} فيصيبكم لفحها {وَمَا لَكُمْ مِنْ دون الله من أولياء} من مانع يمنعكم من عذاب الله {ثُمَّ لا تنصرون} استئنافٌ',NULL,'535',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1588,'{وأقم الصلاة طرفي النهار} بالصبح والمغرب {وزلفاً من الليل} صلاة العشاء قرب أوَّل الليل والزُّلف: أوَّل ساعات اللَّيل وقيل: صلاة طرفي النَّهار: الفجر والظُّهر والعصر وأمَّا المغرب والعشاء فإنَّهما من صلاة زلف اللَّيل {إن الحسنات يذهبن السيئات} إنَّ الصَّلوات الخمس تكفر ما بينهما من الذنوب إذا اجتنبت الكبائر {ذلك ذكرى} أَيْ: هذه موعظةٌ {للذاكرين}',NULL,'536',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1589,'{واصبر} على الصَّلاة {فإنَّ الله لا يضيع أجر المحسنين} يعني:
INSERT INTO [b2547] VALUES (1590,'{فلولا كان من القرون من قبلكم} أَيْ: ما كان منهم {أولو بقية} دينٍ وتميزٍ وفضلٍ {ينهون عن الفساد في الأرض} عن الشِّرك والاعتداء في حقوق الله والمعصية {إلاَّ قليلاً} لكن قليلاً {ممن أنجينا منهم} وهم أتباع الأنبياء وأهل الحقِّ نهوا عن الفساد {واتَّبع الذين ظلموا ما أترفوا فيه} آثروا الَّلذات على أمر الآخرة وركنوا إلى الدُّنيا والأموال وما أُعطوا من نعيمها',NULL,'536',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1591,'{وما كان ربك ليهلك القرى} أَيْ: أهلها {بظلمٍ} بشركٍ {وأهلها مصلحون} فيما بينهم أَيْ: ليس من سبيل الكفَّار إذا قصدوا الحقَّ في المعاملة أن يُنزِّل اللَّهُ بهم عذاب الاستئصال كقوم لوطٍ عُذِّبوا باللِّواط وقوم شعيب عُذِّبوا ببخس المكيال',NULL,'536',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1592,'{وَلَوْ شَاءَ رَبُّكَ لَجَعَلَ النَّاسَ أُمَّةً وَاحِدَةً} مسلمين كلَّهم {ولا يزالون مختلفين} في الأديان',NULL,'536',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1593,'{إِلا مَنْ رَحِمَ رَبُّكَ} يعني: أهل الحقِّ {ولذلك خلقهم} أَيْ: خلق أهل الاختلاف للاختلاف وأهل الرَّحمة للرَّحمة',NULL,'537',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1594,'{وكلاًّ نقصُّ عليك} أَيْ: كلَّ الذي تحتاج إليه {من أنباء الرسل} نقصُّ عليك {ما نثبت به فؤادك} ليزيدك يقيناً {وجاءك في هذه} أَيْ: في هذه السُّورة {الحق} يعني: ما ذُكر من أقاصيص الأنبياء ومواعظهم وذكر السَّعادة والشَّقاوة وهذا تشريفٌ لهذه السُّورة لأنَّ غيرها من السُّور قد جاء فيها الحقُّ {وموعظة وذكرى للمؤمنين} يتَّعظون إذا سمعوا هذه السُّورة وما نزل بالأمم لمَّا كذَّبوا أنبياءهم',NULL,'537',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1595,'{وَقُلْ لِلَّذِينَ لا يُؤْمِنُونَ اعْمَلُوا عَلَى مَكَانَتِكُمْ} أمر تهديد أَيْ: اعملوا ما أنتم عاملون',NULL,'537',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1596,'{وَانْتَظِرُوا} ما يعدكم الشَّيطان {إِنَّا مُنْتَظِرُونَ} ما يعدنا ربُّنا من النَّصر',NULL,'537',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1597,'{ولله غيب السماوات وَالأَرْضِ} أَيْ: علم ما غاب عن العباد فيهما {وَإِلَيْهِ يُرْجَعُ الأَمْرُ كُلُّهُ} في المعاد حتى لا يكون لأحدٍ سواه أمرٌ {وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ} أَيْ: إنَّه يجزي المحسن بإحسانه والمسيء بإساءَته',NULL,'537',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1598,'{الر} أنا الله الرَّحمن {تلك} هذه {آيات الكتاب المبين} للحلال والحرام والأحكام يعني: القرآن',NULL,'538',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1599,'{إنا أنزلناه} يعني: الكتاب {قرآنا عربيا} بلغة العرب {لعلكم تعقلون} كي تفهموا',NULL,'538',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1600,'{نحن نقصُّ عليك أحسن القصص} نبيِّن لك أحسن البيان {بما أوحينا} بإيماننا {إليك هذا القرآن وإن كنت من قبله لمن الغافلين} وما كنتَ من قبل أن يُوحى إليك إلاَّ من الغافلين',NULL,'538',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1601,'{إذ قال} اذكر إِذْ قَالَ {يُوسُفُ لأَبِيهِ يَا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَبًا وَالشَّمْسَ وَالْقَمَرَ رَأَيْتُهُمْ} الآية رأى يوسف عليه السَّلام هذه الرُّؤيا فلمَّا قصَّها على أبيه أشفق عليه من حسد إخوته له فقال:',NULL,'538',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1602,'{يَا بُنَيَّ لا تَقْصُصْ رُؤْيَاكَ عَلَى إِخْوَتِكَ فيكيدوا لك كيداً} يحتالوا في هلاكك لأنهم لا يعلمون تأويلها',NULL,'539',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1603,'{وكذلك} ومثل ما رأيت {يجتبيك ربك} يصطفيك ويختارك {ويعلمك من تأويل الأحاديث} تعبير الأحلام {وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ} بالنبُّوَّة {وَعَلَى آلِ يَعْقُوبَ} يعني: المُختَصِّين منهم بالنُّبوَّة {عَلَى أَبَوَيْكَ مِنْ قَبْلُ إِبْرَاهِيمَ وَإِسْحَاقَ إِنَّ رَبَّكَ عَلِيمٌ} حيث يضع النبوَّة {حَكِيمٌ} في خلقه',NULL,'539',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1604,'{لَقَدْ كَانَ فِي يُوسُفَ وَإِخْوَتِهِ} أَيْ: في خبرهم وقصصهم {آيات} عبرٌ وعجائبُ {للسائلين} الذين سألوا رسول الله صلى الله عليه وسلم عن ذلك فأخبرهم بها وهو غافلٌ عنها لم يقرأ كتاباً فكان في ذلك أوضح دلالةٍ على صدقه',NULL,'539',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1605,'{إذ قالوا} يعني: إخوة يوسف: {ليوسفُ وأخوه} لأبيه وأُمِّه {أحبُّ إلى أبينا منا ونحن عصبة} جماعةٌ {إنَّ أبانا لفي ضلالٍ مبين} ضلَّ بإيثاره يوسف وأخاه علينا ضلالٍ: خطأ',NULL,'539',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1606,'{اقْتُلُوا يُوسُفَ أَوِ اطْرَحُوهُ أَرْضًا} في أرضٍ يبعد فيها عن أبيه {يَخْلُ لَكُمْ وَجْهُ أبيكم} يُقبل بكليته عليكم {وتكونوا من بعده قوماً صالحين} تُحدثوا توبةً بعد ذلك يقبلها الله سبحانه منكم',NULL,'539',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1607,'{قال قائل منهم} وهو يهوذا أكبر إخوته: {لا تقتلوا يوسف وألقوه في غيابة الجب} في موضعٍ مظلمٍ من البئر لا يلحقه نظر النَّاظرين {يلتقطه بعض السيارة} مارَّة الطَّريق {إن كنتم فاعلين} ما قصدتم من التًّفريق بينه وبين أبيه فلمَّا تآمروا بينهم ذلك وعزموا على طرحه في البئر',NULL,'540',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1608,'{قَالُوا} لأبيهم {مَا لَكَ لا تَأْمَنَّا عَلَى يوسف} لِمَ تخافنا عليه؟ {وإنا له لناصحون} في الرَّحمة والبرِّ والشَّفقة',NULL,'540',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1609,'{أرسله معنا غداً} إلى الصحراء {يرتع ويلعب} نسعى وننشط {وإنا له لحافظون} من كلِّ ما تخافه عليه',NULL,'540',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1610,'{قال إني ليحزنني أن تذهبوا به} ذهابكم به يحززني لأنَّه يفارقني فلا أراه {وَأَخَافُ أَنْ يَأْكُلَهُ الذئب} وذلك أنَّ أرضهم كانت مذأبة {وأنتم عنه غافلون} مشتغلون برعيتكم',NULL,'540',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1611,'{قالوا لئن أكله الذئب ونحن عصبة} جماعةٌ بحضرته {إنا إذاً لخاسرون} لعاجزون',NULL,'540',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1612,'{فَلَمَّا ذَهَبُوا بِهِ وَأَجْمَعُوا أَنْ يَجْعَلُوهُ فِي غيابة الجب} وعزموا على ذلك أوحينا إلى يوسف في البئر تقويةً لقلبه: لتصدقنَّ رؤياك ولَتُخبِرِنَّ إخوتك بصنيعهم هذا بعد هذا اليوم {وَهُمْ لا يَشْعُرُونَ} بأنَّك يوسف في وقت إخبارك إياهم',NULL,'540',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1613,'{وجاؤوا أَبَاهُمْ عِشَاءً يَبْكُونَ}',NULL,'541',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1614,'{قالوا يا أبانا إنا ذهبنا نستبق} نشتدُّ ونعدو ليتبيَّن أيُّنا أسرع عَدْواً {وَتَرَكْنَا يوسف عند متاعنا} ثيابنا {فَأَكَلَهُ الذِّئْبُ وَمَا أَنْتَ بِمُؤْمِنٍ لَنَا} بمصدِّق لنا {ولو كنَّا صادقين} في كلِّ الأشياء لأنَّك اتَّهمتنا في هذه القصَّة',NULL,'541',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1615,'{وجاؤوا على قميصه بدم كذب} لأنَّه لم يكن دمه إنَّما كان دم سخلةٍ {قال} يعقوب عليه السَّلام: {بل} أَيْ: ليس كما تقولون {سوَّلت لكم} زيَّنت لكم {أنفسكم} في شأنه {أمْراً} غير ما تصفون {فصبر} أَيْ: فشأني صبرٌ {جميل} وهو الذي لا جزع فيه ولا شكوى {وَاللَّهُ الْمُسْتَعَانُ عَلَى ما تصفون} أَيْ: به أستعين في مكابدة هذا الأمر',NULL,'541',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1616,'{وجاءت سيارة} رفقةٌ تسير للسَّفر {فأرسلوا واردهم} وهو الذي يرد الماء ليستقي للقوم {فَأَدْلَى دلوه} أرسلها في البئر فتشبت يوسف عليه السَّلام بالرِّشاء فأخبره الوارد فلمَّا رآه {قال يا بشرى} أَيْ: يا فرحتا {هذا غلام وَأَسَرُّوهُ بِضَاعَةً} أسرَّه الوارد ومَنْ كان معه من التُّجار من غيرهم وقالوا: هذه بضاعةٌ استبضعها بعض أهل الماء {والله عليم بما يعملون} بيوسف فلمَّا علم إخوته ذلك أتوهم وقالوا: هذا عبدٌ آبقٌ منَّا فقالوا لهم: فبيعوناه فباعوه منهم وذلك قوله: {وشروه بثمن بخسٍ} حرامٍ لأنَّ ثمن الحُرِّ حرامٌ {دراهم معدودة} باثنين وعشرين درهماً {وكانوا} يعني: إخوته {فيه} في يوسف {من الزاهدين} لم يعرفوا موضعه من الله سبحانه وكرامته عليه',NULL,'541',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1617,'{وَشَرَوْهُ بِثَمَنٍ بَخْسٍ دَرَاهِمَ مَعْدُودَةٍ وَكَانُوا فِيهِ من الزاهدين}',NULL,'542',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1618,'{وقال الذي اشتراه من مصر لامرأته} وهو العزيز صاحب ملك مصر: {أكرمي مثواه} أحسني إليه طول مقامه عندنا {عسى أن ينفعنا} أَيْ: يكفينا - إذا بلغ وفهم الأمور - بعض شؤوننا {أو نتخذه ولداً} وكان حصوراً لا يولد له {وكذلك} وكما نجَّيناه من القتل والبئر {مكَّنا ليوسف في الأرض} يعني: أرض مصر حتى بلغ ما بلغ {ولنعلمه من تأويل الأحاديث} فعلنا ذلك تصديقاً لقوله {ويُعلِّمك مِنْ تَأْوِيلِ الأَحَادِيثِ} {وَاللَّهُ غَالِبٌ عَلَى أَمْرِهِ} على ما أراد من قضائه لا يغلب غالبٌ على أمره ولا يُبطل إرادته منازعٌ {ولكنَّ أكثر الناس} هم المشركون ومَنْ لا يؤمن بالقدر {لا يعلمون} أَنَّ قدرة الله غالبةٌ ومشيئته نافذةٌ',NULL,'542',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1619,'{ولما بلغ أشده} ثلاثين سنةً {آتيناه حكماً وعلماً} عقلاً وفهماً {وَكَذَلِكَ} ومثل ما وصفنا من تعليم يوسف {نجزي المحسنين} الصَّابرين على النوائب كما يوسف عليه السَّلام',NULL,'542',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1620,'{وَرَاوَدَتْهُ الَّتِي هُوَ فِي بَيْتِهَا عَنْ نَفْسِهِ} يعني: امرأة العزيز طلبت منه أن يُواقعها {وغلقت الأبواب} أَيْ: أغلقتها {وقالت هيت لك} أَيْ: هلمَّ وتعال {قال مَعَاذَ الله} أعوذ بالله أن أفعل هذا {إِنَّهُ رَبِّي} إنَّ الذي اشتراني هو سيِّدي {أحسن مثواي} أنعم عليَّ بإكرامي فلا أخونه في حرمته {إنه لا يفلح الظالمون} لا يسعد الزُّناة',NULL,'542',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1621,'{ولقد همت به وهمَّ بها} طمعت فيه وطمع فيها {لولا أن رأى برهان ربِّه} وهو أنَّه مُثِّل له يعقوب عليه السَّلام عاضَّاً على أصابعه يقول: أتعمل عمل الفجَّار وأنت مكتوبٌ في الأنبياء فاستحيا منه وجواب لولا محذوف على معنى: لولا أن لاأى برهان ربِّه لأمضى ما همَّ به {كذلك} أَيْ: أريناه البرهان {لنصرف عنه السوء} وهو حيانة صاحبه {والفحشاء} ركوب الفاحشة {إنَّه من عبادنا المخلصين} الذين أخلصوا دينهم لله سبحانه',NULL,'543',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1622,'{واستبقا الباب} وذلك أنَّ يوسف عليه السَّلام لمَّا رأى البرهان قام مُبادراً إلى الباب واتَّبعته المرأة تبغي التَّشبُّث به فلم تصل إلاَّ إلى دُبر قميصه فقدَّته ووجدا زوج المرأة عند الباب فحضرها في الوقت كيدٌ فأوهمت زوجها أنَّ الذي تسمع من العدو والمبادرة إلى الباب كان منها لا من يوسف فـ {قَالَتْ مَا جَزَاءُ مَنْ أَرَادَ بِأَهْلِكَ سُوءًا} تريد الزِّنا {إلاَّ أن يسجن} يحبس في الحبس {أو عذاب أليم} بالضَّرب فلمَّا قالت ذلك غضب يوسف و {قَالَ هِيَ رَاوَدَتْنِي عَنْ نَفْسِي وَشَهِدَ شَاهِدٌ} وحكم حاكمٌ وبيَّن مبيِّنٌ {من أهلها} وهو ابنُ عمِّ المرأة فقال: {إِنْ كَانَ قَمِيصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وهو من الكاذبين}',NULL,'543',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1623,'{قَالَ هِيَ رَاوَدَتْنِي عَنْ نَفْسِي وَشَهِدَ شَاهِدٌ مِنْ أَهْلِهَا إِنْ كَانَ قَمِيصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكَاذِبِينَ}',NULL,'544',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1624,'{وَإِنْ كَانَ قَمِيصُهُ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصَّادِقِينَ}',NULL,'544',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1625,'{فلما رأى قميصه قُدَّ من دبرٍ} من حكم الشَّاهد وبيانِه ما يُوجب الاستدلال على تمييز الكاذب من الصَّادق فلمَّا رأى زوج المرأة قميص يوسف قُدَّ مِنْ دُبُرٍ {قال إنَّه من كيدكنَّ} أَيْ: قولِك: {ما جزاء مَنْ أراد بأهلك سوءاً} الآية',NULL,'544',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1626,'{يوسف} يا يوسف {أعرض عن هذا} اترك هذا الأمر فلا تذكره {واستغفري لذنبك إنك كنت من الخاطئين} الآثمين ثمَّ شاع ما جرى بينهما في مدينة مصر حتى تحدَّثت بذلك النِّساء وخضن فيه وهو قوله:',NULL,'544',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1627,'{وَقَالَ نِسْوَةٌ فِي الْمَدِينَةِ امْرَأَةُ الْعَزِيزِ تُرَاوِدُ فَتَاهَا} غلامها {عَنْ نَفْسِهِ قَدْ شَغَفَهَا حُبًّا} قد دخل حبُّه في شغاف قلبها وهو موضع الدَّم الذي يكون داخل القلب {إنا لنراها في ضلالٍ} عن طريق الرُّشد بحبِّها إيَّاه',NULL,'544',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1628,'{فلما سمعت} امرأة العزيز {بمكرهن} مقالتهن وشميت مكراً لأنهنَّ قصدْنَ بهذه الماقلة أن تُريهنَّ يوسف ليقوم لها العذر في حبِّه إذا رأين جماله وكنَّ مشتهين ذلك لأنَّ يوسف وُصف لهنَّ بالجمال {أرسلت إليهن} تدعوهنَّ {وأعتدت} وأعدَّت {لهن متكئا} طعاماً يقطع بالسِّكين قيل: هو الأترج {وآتت} وناولت {كلَّ واحدةٍ منهن سكيناً وقالت} ليوسف: {اخْرُجْ عَلَيْهِنَّ فَلَمَّا رَأَيْنَهُ أَكْبَرْنَهُ} أعظمنه وهَالَهُنَّ أمره وبُهتن {وقطعن أيديهنَّ} حَززْنَها بالسَّكاكين ولم يجدن الألم لشغل قلوبهنَّ بيوسف {وَقُلْنَ حاشَ لِلَّه} بَعُدَ يوسف عن أن يكون بشراً {إنْ هذا} ما هذا {إلاَّ ملك كريم} فلمَّا رأت امرأة العزيز ذلك قالت:',NULL,'544',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1629,'{فذلكنَّ الذي لُمْتُنَّنِي فيه} في حبِّه والشَّغف فيه ثم أقرَّّت عندهنَّ بما فعلت فقالت: {ولقد راودته عن نفسه فاستعصم} فامتنع وأبى وتوعَّدته بالسِّجن فقالت: {ولئن لم يفعل} الآية فأمرنه بطاعتها وقلن له: إنَّك الظَّالم وهي المظلومة فقال يوسف:',NULL,'545',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1630,'{رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِي إِلَيْهِ} من معصيتك {وإلاَّ تصرف عني كيدهنَّ} كيد جميع النِّساء {أصبُ إليهنَّ} أمل إليهنَّ {وأكن من الجاهلين} المذنبين',NULL,'545',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1631,'{فاستجاب له ربُّه فصرف عنه كيدهنَّ} حتى لم يقع في شيءٍ ممَّا يطالبنه به {إنَّه هو السميع} لدعائه {العليم} بما يخاف من الإثم',NULL,'546',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1632,'{ثم بدا لهم} للعزيز وأصحابه {من بعد ما رأوا الآيات} آيات براءة يوسف {ليسجننه حتى حين} وذلك أنَّ المرأة قالت: إنَّ هذا العبد فضحني في النَّاس يُخبرهم أنِّي راودته عن نفسه فاحبسه حتى تنقطع هذه المقالة فذلك قوله: {حتى حين} أَيْ: إلى انقطاع اللائمة',NULL,'546',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1633,'{ودخل معه السجن فتيان} غلامان للملك الأكبر رُفع إليه أنَّ صاحب طعامه يريد أن يَسُمَّه وصاحب شرابه مَالأَهُ على ذلك فأدخلهما السِّجن ورأيا يوسف يُعبِّر الرُّؤيا فقالا: لنجرِّب هذا العبد العبرانيّ فتحالما من غير أن يكونا رأيا شيئاً وهو قوله {قال أحدهما} وهو السَّاقي: {إني أراني أعصر خمراً} أَيْ: عنباً وقال صاحب الطَّعام: {إني أراني أحمل فوق رأسي خبزاً} رأيتُ كأنَّ فوقَ رأسي خبزاً {تأكل الطير منه} فإذا سباعُ الطير يَنْهَشْنَ منه {نبئنا بتأويله} أَيْ: خبرنا بتفسير الرُّؤيا {إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ} تُؤثر الإِحسان وتأتي جميل الأفعال فعدلَ يوسف عليه السَّلام عن جواب مسألتهما ودَلَّهما أولاً على أنَّه عالمٌ بتفسير الرُّؤيا فقال:',NULL,'546',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1634,'{لا يأتيكما طعام ترزقانه} تأكلان منه في منامكما {إِلا نَبَّأْتُكُمَا بِتَأْوِيلِهِ} في اليقظة {قبل أن يأتيكما} التَّأويل {ذلكما مما علمني ربّي} أَيْ: لست أخبركما على جهة التَّكهُّن والتَّنجُّم إنَّما ذلك بوحي من الله عز وجل وعلمٍ ثمَّ أخبر عن إيمانه واجتنابه الكفر بباقي الآية وقوله:',NULL,'546',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1635,'{مَا كَانَ لَنَا أَنْ نُشْرِكَ بِاللَّهِ مِنْ شيء} يريد: إن الله سبحانه عصمنا من أن نشرك به {ذَلِكَ مِنْ فضل الله علينا} أَيْ: اتِّباعنا للإيمان يتوفيق الله تعالى وتفضُّله علينا {وعلى الناس} وعلى مَنْ عصمه الله من الشِّرك حتى اتَّبع دينه {ولكنَّ أكثر الناس لا يشكرون} نعمة الله بتوحيده والإِيمان برسله ثمَّ دعاهما إلى الإِيمان فقال:',NULL,'547',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1636,'{يا صاحبي السجن} يعني: يا ساكنيه: {أأرباب متفرِّقون} يعني: الأصنام {خَيْرٌ} أعظم في صفة المدح {أَمِ اللَّهُ الواحد القهار} الذي يقهر كلَّ شيءٍ',NULL,'547',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1637,'{ما تعبدون من دونه} أنتما ومَنْ على مثل حالكما من دون الله {إلاَّ أسماءً} لا معانيَ وراءها {سميتموها أنتم} {إن الحكم إلاَّ لله} ما الفضل بالأمر والنَّهي إلاَّ لله {ذَلِكَ الدِّينُ القيم} المستقيم {ولكن أكثر الناس لا يعلمون} مل للمطيعين من الثَّواب وللعاصين من العقاب ثمَّ ذكر تأويل رؤياهما بقوله:',NULL,'547',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1638,'{يَا صَاحِبَيِ السِّجْنِ أَمَّا أَحَدُكُمَا فَيَسْقِي رَبَّهُ خَمْرًا وَأَمَّا الآخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رأسه} فقالا: ما رأينا شيئاً فقال: {قُضِيَ الأمر الذي فيه تستفتيان} يعني: سيقع بكما ما عبَّرت لكما صدقْتُما أم كذبتما',NULL,'547',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1639,'{وقال} يوسف {للذي ظنَّ} علم {إنَّه ناج منهما} وهو السَّاقي: {اذكرني عند ربك} عند الملك صاحبك وقل له: إنَّ في السِّجن غلاماً محبوساً ظلماً {فَأَنْسَاهُ الشَّيْطَانُ ذِكْرَ ربه} أنسى الشَّيطان يوسف الاستغاثة بربِّه وأوقع في قلبه الاستغاثة بالملك فعوقب بأن {لبث في السجن بضع سنين} سبع سنين فلمَّا دنا فرجه وأراد الله خلاصه رأى الملك رؤيا وهو قوله:',NULL,'547',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1640,'{وقال الملك إني أرى} الآية فلمَّا استفتاهم فيها',NULL,'548',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1641,'{قالوا أضغاث أحلام} أحلامٌ مختلطةٌ لا تأويل لها عندنا {وما نحن بتأويل الأحلام بعالمي} أقرُّوا بالعجز عن تأويلها',NULL,'548',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1642,'{وقال الذي نجا منهما} وهو السَّاقي {وادَّكر بعد أمةٍ} وتذكَّر أمر يوسف بعد حين من الدَّهر: {أنا أنبئكم بتأويله فأرسلون} فأُرسل فأتى يوسف فقال:',NULL,'548',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1643,'{يوسفُ} أَيْ: يا يوسف {أيها الصديق} الكثير الصِّدق وقوله {لعلي أرجع إلى الناس} يعني: أصحاب الملك {لعلهم يعلمون} تأويل رؤيا الملك من جهتك',NULL,'548',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1644,'{قال تزرعون} أَيْ: ازرعوا {سبع سنين دأباً} متتابعةً وهذه السَّبع تأويل البقرات السِّمان {فَمَا حَصَدْتُمْ} ممَّا زرعتم {فَذَرُوهُ فِي سُنْبُلِهِ} لأنَّه أبقى له وأبعد من الفساد {إلا قليلاً ممًّا تأكلون} فإنَّكم تدوسونه',NULL,'548',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1645,'{ثُمَّ يَأْتِي مِنْ بَعْدِ ذَلِكَ سَبْعٌ شِدَادٌ} مُجدباتٌ صعابٌ وهذه تأويل البقرات العجاف {يأكلن} يُفنين ويُذهبن {مَا قدَّمتم لهن} من الحَبِّ {إلاَّ قليلاً ممَّا تحصنون} تحرزون وتدَّخرون',NULL,'549',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1646,'{ثُمَّ يَأْتِي مِنْ بَعْدِ ذَلِكَ عَامٌ فِيهِ يغاث الناس وفيه يعصرون} يمطرون ويخصبون حتى يعصروا من السِّمسم الدُّهن ومن العنب الخمر ومن الزَّيتون الزَّيت فرجع الرَّسول بتأويل الرُّؤيا إلى الملك فعرف الملك أنَّ ذلك تأويلٌ صحيحٌ فقال:',NULL,'549',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1647,'{ائتوني} بالذي عبَّر رؤياي فجاء الرَّسول يوسف وقال: أجب الملك فقال للرسول: {ارْجِعْ إِلَى رَبِّكَ} يعني: الملك {فاسأله} أن يسأل {ما بال النسوة} ما حالهنَّ وشأنهنَّ ليعلم صحَّة براءتي ممَّا قُذفت به وذلك أنَّ النِّسوة كنَّ قد عرفن براءته بإقرار امرأة العزيز عندهنَّ وهو قولها: {ولقد راودته عن نفسه فاستعصم} فأحبَّ يوسف عليه السَّلام أن يُعلم الملك أنَّه حبس ظلما وأنه برئ ممَّا قُذِف به فسأله أن يستعلم النِّسوة عن ذلك {إن ربي بكيدهنَّ} ما فعلن في شأني حين رأينني وما قلن لي {عليم} فدعا الملك النسوة فقال:',NULL,'549',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1648,'{ما خطبكنَّ} ما قصتكنَّ وما شأنكنَّ {إذ راودتنَّ يوسف عن نفسه} جمعهنَّ في المراودة لأنه يعلم مَنْ كانت المُراوِدة {قلن حاشَ لله} بَعُدَ يوسف عمَّا يُتَّهم به {مَا عَلِمْنَا عَلَيْهِ مِنْ سُوءٍ} من زنا فلمَّا برَّأْنَهُ أقرَّت امرأة العزيز فقالت: {الآن حصحص الحق} أَيْ: بان ووضح وذلك أنَّها خافت إنْ كذَّبت شهدت عليها النِّسوة فقالت: {أَنَا رَاوَدْتُهُ عَنْ نَفْسِهِ وَإِنَّهُ لَمِنَ الصَّادِقِينَ} في قوله: {هي راودتني عن نفسي}',NULL,'549',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1649,'{ذلك} أَيْ: ما فعله يوسف من ردِّ الرَّسول إلى الملك {ليعلم} وزير الملك ـ وهو الذي اشتراه ـ {إني لم أخنه} في زوجته {بالغيب وَأَنَّ اللَّهَ لا يَهْدِي كَيْدَ الْخَائِنِينَ} لا يرشد مَنْ خان أمانته أَيْ: إنَّه يفتضح في العاقبة بحرمان الهداية من الله عز وجل فلمَّا قال يوسف عليه السَّلام: {ذَلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ} قال جبريل عليه السَّلام: ولا حين هممت بها يوسف فقال:',NULL,'550',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1650,'{وما أبرئ نفسي} وما أُزكِّي نفسي {إنَّ النفس لأمَّارة بالسوء} بالقبيح وما لا يحبُّ الله {إلاَّ ما} مَنْ {رحم ربي} فعصمه',NULL,'550',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1651,'{وقال الملك ائتوني به} بيوسف {أستخلصه لنفسي} أجعله خالصاً لي لا يشركني فيه أحدٌ {فلمَّا كلَّمه} يوسف {قال إنك اليوم لدينا مكين} وجيهٌ ذو مكانةٍ {أمين} قد عرفنا أمانتك وبراءتك ثمَّ سأله الملك أن يُعبِّر رؤياه شفاهاً فأجابه يوسف بذلك فقال له: ما ترى أن تصنع؟ قال: تجمع الطَّعام في السِّنين المخصبة ليأتيك الخلق فيمتارون منك بحكمك فقال: مَنْ لي بهذا ومَنْ يجمعه؟ فقال يوسف:',NULL,'550',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1652,'{قال اجعلني على خزائن الأرض} على حفظها وأراد بالأرض أرض مصر {إني حفيظٌ عليمٌ} كاتبٌ حاسبٌ',NULL,'551',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1653,'{وكذلك} وكما أنعمنا عليه بالخلاص من السِّجن {مكنَّا ليوسف} أقدرناه على ما يريد {في الأرض} أرض مصر {يَتَبَوَّأُ مِنْهَا حَيْثُ يَشَاءُ} هذا تفسير التَّمكين في الأرض {نصيب برحمتنا مَنْ نشاء} أتفضَّل على مَنْ أشاء برحمتي {وَلا نُضِيعُ أَجْرَ الْمُحْسِنِينَ} ثواب المُوحِّدين',NULL,'551',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1654,'{ولأجر الآخرة خير} الآية أَيْ: ما يعطي الله من ثواب الآخرة خيرٌ للمؤمنين والمعنى: إنَّ ما يعطي الله تعالى يوسف في الآخرة خيرٌ ممَّا أعطاه في الدُّنيا ثمَّ دخل أعوام القحط على النَّاس فأصاب إخوة يوسف المجاعة فأتوه مُمتارين فذلك قوله:',NULL,'551',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1655,'{وَجَاءَ إِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ له منكرون} لأنهم رأوه على زيِّ الملوك وكان قد تقرَّر في أنفسهم هلاك يوسف وقيل: لأنَّهم رأوه من وراء سترٍ',NULL,'551',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1656,'{ولما جهزهم بجهازهم} يعني: حمل لكلِّ رجلٍ منهم بعيراً {قال ائتوني بأخٍ لكم من أبيكم} يعني: بنيامين وذلك أنَّه سألهم عن عددهم فأخبروه وقالوا: خلَّفنا أحدنا عند أبينا فقال يوسف: فأتوني بأخيكم الذي مِنْ أَبِيكُمْ {أَلا تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ} أُتمُّه من غير بخسٍ {وأنا خير المنزلين} وذلك لأن حين أنزلهم أحسن ضيافتهم ثمَّ أوعدهم على ترك الإِتيان بالأخ بقوله:',NULL,'552',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1657,'{فَإِنْ لَمْ تَأْتُونِي بِهِ فَلا كَيْلَ لَكُمْ عِنْدِي وَلا تَقْرَبُونِ}',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1658,'{قَالُوا سَنُرَاوِدُ عَنْهُ أَبَاهُ} نطلب منه ونسأله أن يرسله معنا {وإنا لفاعلون} ما وعدناك من المراودة',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1659,'{وقال} يوسف {لفتيانه} لغلمانه: {اجعلوا بضاعتهم} التي أتوا بها لثمن الميرة وكانت دراهم {في رحالهم} أو عيتهم {لعلَّهم يعرفونها} عساهم يعرفون أنَّها بضاعتهم بعينها {إذا انقلبوا إلى أهلهم} وفتحوا أوعيتهم {لعلهم يرجعون} عساهم يرجعون إذا عرفوا ذلك لأنَّّهم لا يستحلُّون إمساكها',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1660,'{فَلَمَّا رَجَعُوا إِلَى أَبِيهِمْ قَالُوا يَا أَبَانَا منع منّا الكيل} حُكم علينا بمنع الكيل بعد هذا إن لم نذهب بأخينا يعنون قوله: {فلا كيل لكم عندي ولا تقربون} {فأرسل معنا أخانا نكتل} نأخذ كيلنا',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1661,'{قال هل آمنكم عليه} الآية يقول: لا آمنكم على بنيامين إلاَّ كأمني على يوسف يريد: إنَّه لم ينفعه ذلك الأمن فإنَّهم خانوه فهو - وإن أَمِنَهم في هذا - خاف خيانتهم أيضاً ثمَّ قال: {فَاللَّهُ خَيْرٌ حَافِظًا}',NULL,'522',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1662,'{ولما فتحوا متاعهم} ما حملوه من مصر {وَجَدُوا بِضَاعَتَهُمْ رُدَّتْ إِلَيْهِمْ قَالُوا يَا أَبَانَا ما نبغي} منك شيئاً تردُّنا به وتصرفنا إلى مصر {هذه بضاعتنا ردت إلينا} فنتصرَّف بها {ونميرُ أهلنا} نجلب إليهم الطَّعام {ونزداد كيل بعير} نزيد حِمْل بعيرٍ من الطَّعام لأنَّه كان يُكال لكلِّ رجلٍ وِقْر بعير {ذلك كيلٌ يسير} متيسر على من يكبل لنا لسخائه',NULL,'553',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1663,'{قَالَ لَنْ أُرْسِلَهُ مَعَكُمْ حَتَّى تُؤْتُونِ مَوْثِقًا من الله} حتى تحلفوا بالله {لَتَأْتُنَّني به إلاَّ أن يحاط بكم} إلا أن تموتوا كلُّكم {فلما آتَوْهُ موثقهم} عهدهم ويمينهم {قال} يعقوب عليه السَّلام: {الله على ما نقول وكيل} شهيد فلمَّا أرادوا الخروج من عنده قال:',NULL,'553',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1664,'{يا بني لا تدخلوا} مصر {مِنْ بَابٍ وَاحِدٍ وَادْخُلُوا مِنْ أَبْوَابٍ متفرقة} خاف عليهم العين فأمرهم بالتَّفرقة {وَمَا أُغْنِي عَنْكُمْ مِنَ اللَّهِ مِنْ شَيْءٍ} يعني: إنَّ الحذر لا يُغني ولا ينفع من القدر',NULL,'553',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1665,'{ولما دخلوا من حيث أمرهم أبوهم} وذلك أنَّهم دخلوا مصر متفرِّقين من أربعة أبواب {مَا كَانَ يُغْنِي عَنْهُمْ مِنَ اللَّهِ مِنْ شيء} ما كان ذلك ليردَّ قضاءً قضاه الله سبحانه {إلاَّ حاجةً} لكن حاجةً يعني: إنَّ ذلك الدّخول قضى حاجةً في نفس يعقوب عليه السَّلام وهي إرادته أن يكون دخولهم من أبوابٍ متفرِّقةٍ شفقةً عليهم {وإنه لذو علم لما علمناه} لذو يقينٍ ومعرفةٍ بالله سبحانه {ولكن أكثر الناس لا يعلمون} أنَّ يعقوب عليه السَّلام بهذه الصِّفة',NULL,'553',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1666,'{وَلَمَّا دَخَلُوا عَلَى يُوسُفَ آوَى إِلَيْهِ أَخَاهُ} ضمَّه إليه وأنزله عند نفسه {قال إني أنا أخوك} اعترف له بالنِّسب وقال: لا تخبرهم بما ألقيت إليك {فلا تبتئس} فلا تحزن ولا تغتم {بما كانوا يعملون} من الحسد لنا وصرف وجه أبينا عنا',NULL,'554',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1667,'{فلما جهزهم بجهازهم جعل السقاية} وهو إناءٌ من ذهبٍ مرصَّعٌ بالجواهر {في رحل أخيه} بنيامين {ثمَّ أذَّنَ مؤذنٌ} نادى منادٍ {أيتها العير} الرُّفقة {إنكم لسارقون}',NULL,'554',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1668,'{قَالُوا وَأَقْبَلُوا عَلَيْهِمْ مَاذَا تَفْقِدُونَ} ؟',NULL,'554',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1669,'{قالوا نفقد صواع الملك} يعني: السِّقاية {ولمن جاء به حمل بعير} أَيْ: من الطَّعام {وأنا به زعيم} كفيل',NULL,'554',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1670,'{قالوا تالله لقد علمتم} حلفوا على أنَّهم يعلمون صلاحهم وتجنُّبهم الفساد وذلك أنَّهم كانوا معروفين بأنَّهم لا يظلمون أحداً ولا يرزأون شيئاً لأحد',NULL,'554',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1671,'{قالوا فما جزاؤه} أَيْ: ما جزاء السَّارق {إن كنتم كاذبين} في قولكم: ما كنا سارقين',NULL,'554',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1672,'{قالوا جزاؤه مَنْ وجد في رحله} وكانوا يستعبدون كلَّ سارقٍ بسرقته فلذلك قَالُوا: جَزَاؤُهُ مَنْ وُجِدَ فِي رَحْلِهِ أَيْ: جزاء السَّرق مَنْ وجد في رحله المسروق {فهو جزاؤه} أَيْ: فالسَّرق جزاء السَّارق {كذلك نجزي الظالمين} أَيْ: إذا سرق سارقٌ اسْتُرِقَّ فلمَّا أقرُّوا بهذا الحكم صُرف بهم إلى يوسف عليه السَّلام ليفتِّش أمتعتهم',NULL,'555',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1673,'{فبدأ} يوسف {بأوعيتهم} وهي كلُّ ما استودع شيئاً من جرابٍ وجوالق ومِخْلاةٍ {قبل وعاء أخيه} نفياً للتُّهمة {ثُمَّ اسْتَخْرَجَهَا} يعني: السِّقاية {مِنْ وِعَاءِ أَخِيهِ كذلك كدنا} ألهمنا {ليوسف} أي: ألهمناه مثل ذلك الكيد حتى ضممنا أخاه إليه {ما كان ليأخذ أخاه} ويستوجب ضمَّه إليه {في دين الملك} في حكمه وسيرته وعادته {إلاَّ} بمشيئة الله تعالى وذلك أنَّ حكم الملك في السَّارق أن يضرب ويغرم ضعفي ما سرق فلم يكن يوسف يتمكَّن من حبس أخيه في حكم الملك لولا ما كاد الله له تلطُّفاً حتى وجد السَّبيل إلى ذلك وهو ما أجري على ألسنة إخوته أنَّ جزاء السَّارق الاسترقاق {نرفع درجات مَنْ نشاء} بضروب الكرامات وأبواب العلم كما رفعنا درجة يوسف على إخوته في كلِّ شيء {وفوق كلِّ ذي علم عليم} يكون هذا أعلم من هذا وهذا أعلم من هذا حتى ينتهي العلم إلى الله سبحانه فلمَّا خرج الصُّواع من رحل بنيامين',NULL,'555',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1674,'{قالوا} ليوسف {إن يسرق} الصُّواع {فَقَدْ سَرَقَ أَخٌ لَهُ مِنْ قَبْلُ} يعنون: يوسف عليه السَّلام وذلك أنَّه كان يأخذ الطعام من مائدة أبيه سرَّاً منهم فيتصدَّق به في المجاعة حتى فطن به إخوته {فأسرَّها يوسف في نفسه} أَيْ: أسرَّ الكلمة التي كانت جواب قولهم هذا {ولم يُبدها لهم} وهو أنَّه قال في نفسه: {أنتم شرٌّ مكاناً} عند الله بما صنعتم من ظلم أخيكم وعقوق أبيكم {والله أعلم بما تصفون} أَيْ: قد علم أنَّ الذي تذكرونه كذبٌ',NULL,'555',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1675,'{قالوا يا أيها العزيز إنَّ له أباً شيخاً كبيراً} في السِّنِّ {فخذ أحدنا مكانه} واحداً منَّا تستعبده بدله {إِنَّا نَرَاكَ مِنَ المحسنين} إذا فعلت ذلك فقد أحسنت إلينا',NULL,'556',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1676,'{قَالَ مَعَاذَ اللَّهِ أَنْ نَأْخُذَ إِلا مَنْ وجدنا متاعنا عنده إنا إذا لظالمون}',NULL,'556',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1677,'{فلما استيأسوا} يئسوا {منه خلصوا نجياً} انفردوا متناجين في ذهابهم إلى أبيهم من غير أخيهم {قال كبيرهم} وهو روبيل وكان أكبرهم سنَّاً: {أَلَمْ تَعْلَمُوا أَنَّ أَبَاكُمْ قَدْ أَخَذَ عَلَيْكُمْ موثقاً من الله} في خفظ الأخ وردِّه إليه {وَمِنْ قَبْلُ مَا فَرَّطْتُمْ في يوسف} ما زائدة أَيْ: قصَّرتم في أمر يوسف وخنتموه فيه {فلن أبرح الأرض} لن أخرج من أرض مصر {حَتَّى يَأْذَنَ لي أبي} يبعث إليَّ أنَْ آتيه {أَوْ يَحْكُمَ اللَّهُ لِي} يقضي في أمري شيئاً {وَهُوَ خَيْرُ الْحَاكِمِينَ} أعدلهم وقال لإخوته:',NULL,'556',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1678,'{ارْجِعُوا إِلَى أَبِيكُمْ فَقُولُوا يَا أَبَانَا إِنَّ ابنك سرق} يعنون في ظاهر الأمر {وما شهدنا إلاَّ بما علمنا} لأنَّه وُجدت السَّرقة في رحله ونحن ننظر {وَمَا كُنَّا لِلْغَيْبِ حافظين} ما كنا نحفظه إذا غاب عنا',NULL,'556',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1679,'{واسأل القرية التي كنَّا فيها} أَيْ: أهل مصر {والعير التي أقبلنا فيها} يريد: أهل الرُّفقة فلمَّا رجعوا إلى أبيهم يعقوب عليه السَّلام قالوا له هذا فقال:',NULL,'557',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1680,'{بل سوَّلت لكم أنفسكم أمراً} زيَّنته لكم حتى أخرجتم بنيامين من عندي رجاء منفعة فعاد من ذلك شرٌّ وضررٌ',NULL,'557',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1681,'{وتولى عنهم} أعرض عن بنيه وتجدَّد وَجْدُه بيوسف {وقال يا أسفى على يوسف} يا طول حزني عليه {وابيضت عيناه} انقلبت إلى حال البياض فلم يبصر بهما {من الحزن} من البكاء {فهو كظيم} مغمومٌ مكروبٌ لا يُظهر حزنه بجزعٍ أو شكوى',NULL,'557',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1682,'{قالوا تالله تفتأ} لا تزال {تذكر يوسف} لا تَفْتُر من ذكره {حتى تكون حرضاً} فاسداً دنفاً {أو تكون من الهالكين} الميِّتين والمعنى: لا تزال تذكره بالحزن والبكاء عليه حتى تصير بذلك إلى مرض لا تنتفع بنفسك معه أو تموت بغمِّه فلمَّا أغلظوا له في القول',NULL,'557',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1683,'{قال إنما أشكو بثِّي} ما بي من البثِّ وهو الهمُّ الذي تفضي به إلى صاحبك {وحزني إلى الله} لا إليكم {وأعلم من الله ما لا تعلمون} وهو أنَّه علم أنَّ يوسف حيٌّ أخبره بذلك مَلَكُ الموت وقال له: اطلبه من هاهنا وأشار له إلى ناحية مصر ولذلك قال:',NULL,'557',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1684,'{يا بنيَّ اذهبوا فتحسسوا من يوسف} تَبَحَّثوا عنه {ولا تَيْأَسُوا من روح الله} من الفرج الذي يأتي به {إِنَّهُ لا يَيْأَسُ مِنْ رَوْحِ اللَّهِ إِلا القوم الكافرون} يريد: إنَّ المؤمن يرجو الله تعالى في الشدائد والكافر ليس كذلك فخرجوا إلى مصر',NULL,'558',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1685,'{فلما دخلوا عليه قالوا يا أيها العزيز مسَّنا وأهلنا الضر} أصابنا ومَنْ يختصُّ بنا الجوع {وجئنا ببضاعة مزجاة} ندافع بها الأيام ونتفوت وليس ممَّا يتشبَّع به وكانت دراهم زيوفاً {فأوف لنا الكيل} سألوه مساهلتهم في النقد وإعطائهم بدراهمهم مثل ما يعطي بغيرها من الجياد {وتصدَّق علينا} بما بين القيمتين {إن الله يجزي} يتولَّى جزاء {المتصدقين} فلمَّا قالوا هذا أدركته الرِّقَّة ودمعت عيناه وقال توبيخاً لهم وتعظيما لما فعلوا:',NULL,'558',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1686,'{قَالَ هَلْ عَلِمْتُمْ مَا فَعَلْتُمْ بِيُوسُفَ وَأَخِيهِ} بإدخال الغمِّ عليه بإفراده من يوسف {إذ أنتم جاهلون} آثمون بيعقوب أبيكم وقطع رحم أخيكم منكم ولمَّا قال لهم هذه المقالة رفع الحجاب فقالوا:',NULL,'558',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1687,'{أإنك لأنت يوسف قال أنا يوسف} الذي فعلتم به ما فعلتم {وهذا} المظلوم من جهتكم {قد منَّ الله علينا} بالجمع بيننا بعد ما فرَّقتم {إنه مَن يتق} الله {ويصبر} على المصائب {فإنَّ الله لا يضيع أجر المحسنين} أجر مَنْ كان هذا حاله',NULL,'559',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1688,'{قالوا تالله لقد آثرك الله علينا} فضَّلك الله علينا بالعقل والعلم والفضل الحسن {وإنْ كنا لخاطئين} آثمين في أمرك',NULL,'559',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1689,'{قال لا تثريب عليكم اليوم} لا تأنيب ولا تعيير عليكم بعد هذا اليوم ثمَّ جعلهم في حلِّ وسأل لهم المغفرة فقال: {يغفر الله لكم} الآية ثمَّ سألهم عن أبيه فقالوا: ذهبت عيناه فقال:',NULL,'559',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1690,'{اذهبوا بقميصي هذا} وكان قد نزل به جبريل عليه السَّلام على إبراهيم عليه السَّلام لمَّا أُلقي في النَّار وكان فيه ريح الجنَّة لا يقع على مبتلى ولا سقيم إلاَّ صحَّ فذلك قوله: {فألقوه على وجه أبي يأت بصيراً} يرجعْ ويَعُدْ بصيراً',NULL,'559',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1691,'{ولما فصلت العير} خرجت من مصر مُتوجِّهةً إلى كنعان {قَالَ أَبُوهُمْ} لمن حضره: {إني لأجد ريح يوسف} وذلك أنَّه هاجت الرِّيح فحملت ريح القميص واتَّصلت بيعقوب فوجد ريح الجنَّة فعلم أنَّه ليس في الدُّنيا من ريح الجنَّة إلاَّ ما كان من ذلك القميص {ولا أن تفندون} تُسفِّهوني وتُجهِّلوني',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1692,'{قالوا تالله إنك لفي ضلالك القديم} شقائك القديم ممَّا تكابد من الأحزان على يوسف وخطئك في النزاع إليه على عهده منك وكان عندهم أنَّه قد مات وقوله:',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1693,'{فارتدَّ بصيراً} أَيْ: عاد ورجع بصيرا وقوله:',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1694,'{قَالُوا يَا أَبَانَا اسْتَغْفِرْ لَنَا ذُنُوبَنَا إِنَّا كنا خاطئين}',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1695,'{سوف أستغفر لكم ربي} أخَّر ذلك إلى السَّحَر ليكون أقرب إلى الإجابة وكان قد بعث يوسف عليه السًّلام مع البشير إلى يعقوب عليه السَّلام عُدَّة المسير إليه فتهيَّأ يعقوب وخرج مع أهله إليه فذلك قوله:',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1696,'{فلما دخلوا على يوسف آوى إليه} أَيْ: ضمَّ إليه {أبويه} أباه وخالته وكانت أمُّه قد ماتت {وقال ادخلوا مصر} وذلك أنَّه كان قد استقبلهم فقال لهم قبل دخول مصر: ادخلوا مصر آمنين إن شاء الله وكانوا قبل ذلك يخافون دخول مصر إلاَّ بجوازٍ من ملوكهم',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1697,'{ورفع أبويه على العرش} أجلسهما على السَّرير {وخرُّوا له سجداً} سجدوا ليوسف سجدة التَّحيَّة وهو الانحناء {وقد أحسن بي} إليَّ {إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَجَاءَ بِكُمْ مِنَ البدو} وهو البسيط من الأرض وكان يعقوب وولده بأرض كنعان أهل مواشٍ وبريَّة {من بعد أن نزغ الشيطان} أفسد {بيني وبين إخوتي} بالحسد {إنَّ ربي لطيف لما يشاء} عالم بدقائق الأمور {إِنَّهُ هُوَ الْعَلِيمُ} بخلقه {الحكيم} فيهم بما شاء ثمَّ دعا ربَّه وشكره فقال:',NULL,'560',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1698,'{رب قد آتيتني من الملك} ملك مصر {وعلمتني من تأويل الأحاديث} يريد: تفسير الأحلام {فاطر السماوات والأرض} خالقهما ابتداءً {توفني مسلماً} اقبضني على الإِسلام {وألحقني بالصالحين} من آبائي إبراهيم وإسماعيل وإسحاق عليهم السَّلام يريد: ارفعني إلى درجاتهم',NULL,'561',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1699,'{ذلك} الذي قصصنا عليك من أمر يوسف من الأخبار التي كانت غائبة عنك وهو قوله: {مِنْ أَنْبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَمَا كُنْتَ لديهم} لدى إخوة يوسف {إذ أجمعوا أمرهم} عزموا على أَمْرَهُمْ {وَهُمْ يَمْكُرُونَ}',NULL,'561',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1700,'{وما أكثر الناس ولو حرصت بمؤمنين} كان رسول الله صلى الله عليه وسلم يرجو أن تؤمن به قريش واليهود لمَّا سألوه عن قصَّة يوسف فشرحها لهم فخالفوا ظنَّه فقال الله: {وما أكثر الناس ولو حرصت} على إيمانهم {بمؤمنين} لأنَّك لا تهدي مَنْ أحببت لكنَّ الله يهدي مَنْ يشاء',NULL,'561',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1701,'{وما تسألهم عليه} على القرآن {من أجرٍ} مالٍ يعطونك {إِنْ هُوَ} ما هو {إِلا ذكر للعالمين} تذكرةٌ لهم بما هو صلاحهم يريد: إنَّا أزحنا العلَّة في التَّكذيب حيث بعثناك مُبلِّغاً بلا أجرٍ غير أنَّه لا يؤمن إلا من شاء الله سبحانه وإنْ حرص النبيُّ صلى الله عليه وسلم على ذلك',NULL,'561',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1702,'{وكأين} وكم {من آية} دلالةٍ تدلُّ على التَّوحيد {في السماوات والأرض} من الشَّمس والقمر والنُّجوم والجبال وغيرها {يمرُّون عليها} يتجاوزونها غير مُتفكِّرين ولا معتبرين فقال المشركون: فإنَّا نؤمن بالله الذي خلق هذه الأشياء فقال: {وما يؤمن أكثرهم بالله} في إقراره بأنَّ الله خلقه وخلق السماوات والأرض إلاَّ وهو مشركٌ بعبادة الوثن',NULL,'562',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1703,'{أَفَأَمِنُوا} يعني: المشركين {أَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِنْ عذاب الله} عقوبة تغشاهم وتنبسط عليهم',NULL,'562',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1704,'{قل} لهم {هذه} الطَّريقة التي أنا عليها {سبيلي} سنتي ومنهاجي {أدعو إلى الله} وتمَّ الكلام ثمَّ قال: {على بصيرة أنا} أَيْ: على دينٍ ويقينٍ {ومن اتبعني} يعني: أصحابه وكانوا على أحسن طريقة {وسبحان الله} أَيْ: وقل: سبحان الله تنزيهاً لله تعالى عمَّا أشركوا {وَمَا أَنَا مِنَ الْمُشْرِكِينَ} الذين اتَّخذوا مع الله ندا',NULL,'562',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1705,'{قُلْ هَذِهِ سَبِيلِي أَدْعُو إِلَى اللَّهِ عَلَى بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي وَسُبْحَانَ اللَّهِ وَمَا أَنَا مِنَ الْمُشْرِكِينَ}',NULL,'562',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1706,'{وَمَا أَرْسَلْنَا مِنْ قَبْلِكَ إِلا رِجَالا نُوحِي إليهم من أهل القرى} يريد: لم نبعث قبلك نبيَّاً إلاَّ رجالاً غير امرأةٍ وكانوا من أهل الأمصار ولم نبعث من باديةٍ وهذا ردٌّ لإِنكارهم نبوَّته يريد: إنَّ الرُّسل من قبلك كانوا على مثل حالك ومَنْ قبلهم من الأمم كانوا على مثل حالهم فأهلكناهم فذلك قوله: {أفلم يسيروا في الأرض فينظروا} إلى مصارع الأمم المُكذِّبة فيعتبروا بهم {وَلَدَارُ الآخِرَةِ} يعني: الجنَّة {خَيْرٌ لِلَّذِينَ اتَّقَوْا} الشِّرك في الدُّنيا {أفلا تعقلون} هذا حتى تُؤمنوا؟ !',NULL,'562',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1707,'{حتى إذا استيأس الرسل} يئسوا من قومهم أن يؤمنوا {وَظَنُّوا أَنَّهُمْ قَدْ كذبوا} أيقنوا أنَّ قومهم قد كذَّبوهم {جاءهم نصرنا فنجِّي مَنْ نشاء} وهم المؤمنون أتباع الأنبياء {ولا يردُّ بأسنا} عذابنا',NULL,'563',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1708,'{لَقَدْ كَانَ فِي قَصَصِهِمْ} يعني: إخوة يوسف {عبرة} فكرةٌ وتدبُّرٌ {لأولي الألباب} وذلك أنَّ مَنْ قدر على إعزاز يوسف وتمليكه مصر بعد ما كان عبداً لبعض أهلها قادرٌ على أن يعز محمد عليه السَّلام وينصره {ما كان} القرآن {حديثاً يفترى} يتقولَّه بشر {ولكن تصديق الذي بين يديه} ولكن كان تصديق ما قبله من الكتب {وتفصيل كل شيء} يحتاج إليه من أمور الدِّين {وهدىً} وبياناً {ورحمةً لقوم يؤمنون} يصدِّقون بما جاء به محمد صلى الله عليه وسلم',NULL,'563',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1709,'{المر} أنا الله أعلم وأرى {تلك} يعني: ما ذُكر من الأحكام والأخبار قبل هذه الآية {آيات الكتاب} القرآن {والذي أُنزل إليك من ربك الحق} ليس كما يقوله المشركون أنك تأتي به من قبل نفسك باطلاً {ولكنَّ أكثر الناس} يعني: أهل مكة {لا يؤمنون}',NULL,'564',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1710,'{الله الذي رفع السماوات بغير عمدٍ} جمع عماد وهي الأساطين {ترونها} أنتم كذلك مرفوعة بغير عمادٍ {ثم استوى على العرش} بالاستيلاء والاقتدار وأصله: استواء التَّدبير كما أنَّ أصل القيام الانتصاب ثمَّ يقال: قام بالتدبير ثُمَّ يدلُّ على حدوث العرش المستولى عليه (لا على حدوث الاستيلاء بعد خلق العرش المستولى عليه) {وسخر الشمس والقمر} ذلَّلهما لما يراد منهم {كُلٌّ يَجْرِي لأَجَلٍ مُسَمًّى} إلى وقتٍ معلومٍ وهو فناء الدُّنيا {يُدبِّر الأمر} يُصرِّفه بحكمته {يُفصِّل الآيات} يبيِّن الدلائل التي تدلُّ على التَّوحيد والبعث {لعلَّكم بلقاء ربِّكم توقنون} لكي تُوقنوا يا أهل مكَّة بالبعث',NULL,'564',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1711,'{وهو الذي مدَّ الأرض} بسطها ووسًّعها {وجعل فيها رواسي} أوتدها بالجبال {وَأَنْهَارًا وَمِنْ كُلِّ الثَّمَرَاتِ جَعَلَ فِيهَا زَوْجَيْنِ اثنين} حلواً وحامضاً وباقي الآية مضى تفسيره',NULL,'565',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1712,'{وفي الأرض قطعٌ متجاورات} قُرىً بعضُها قريبٌ من بعضٍ {وجنات} بساتين {من أعناب} وقوله: {صنوان} وهو أن يكون الأصل واحداً ثمَّ يتفرَّع فيصير نخيلاً يحملن وأصلهنَّ واحد {وغير صنوان} وهي المتفرِّقة واحدةً واحدةً {تسقى} هذه القطع والجنَّات والنَّخيل {بماء واحدٍ ونُفضِّل بعضها على بعض} يعني: اختلاف الطُّعوم {في الأكل} وهو الثَّمر فمن حلوٍ وحامضٍ وجيِّدٍ ورديءٍ {إن في ذلك لآيات} لدلالاتٍ {لقوم يعقلون} أهل الإِيمان الذين عقلوا عن الله تعالى',NULL,'565',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1713,'{وإن تعجب} يا محمد من عبادتهم ما لا يضرُّ ولا ينفع وتكذيبك بعد البيان فتعجَّبْ أيضاً من إنكارهم البعث وهو معنى قوله: {فعجب قولهم أإذا كنا تراباً} الآية {وأولئك الأغلال} جمع غُلٍّ وهو طوقٌ تقيَّد به اليد إلى العنق',NULL,'565',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1714,'{ويستعجلونك بالسيئة قبل الحسنة} يعني: مشركي مكَّة حين سألوا رسول الله صلى الله عليه وسلم أن يأتيهم بالعذاب استهزاءً يقول: ويستعجلونك بالعذاب الذي لم أُعاجلهم به وهو قوله: {قبل الحسنة} يعني: إحسانه إليهم في تأخير العقوبة عنهم إلى يوم القيامة {وقد خلت من قبلهم المَثُلاتُ} وقد مضت من قبلهم العقوبات في الأمم المُكذِّبة فلم يعتبروا بها {وَإِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنَّاسِ عَلَى ظُلْمِهِمْ} بالتَّوبة يعني: يتجاوز عن المشركين إذا آمنوا {وإنَّ ربك لشديد العقاب} يعني: لمَنْ أصرَّ على الكفر',NULL,'566',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1715,'{وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلا أُنْزِلَ عَلَيْهِ آيَةٌ من ربِّه} هلاَّ أتانا بآيةٍ كما أتى به موسى من العصا واليد {إنما أنت منذر} بالنَّار لمَنْ عصى وليس إليك من الآيات شيءٌ {وَلِكُلِّ قَوْمٍ هاد} نبيٌّ وَدَاعٍ إلى الله عز وجل يدعوهم لما يُعطَى من الآيات لا بما يريدون ويتحكَّمون',NULL,'566',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1716,'{الله يعلم ما تحملُ كلُّ أنثى} من علقةٍ ومضغةٍ وزائدٍ وناقصٍ وذَكَرٍ وأنثى {وما تَغِيضُ الأرحام} تنقصه من مدَّة الحمل التي هي تسعة أشهر {وما تزداد} على ذلك {وكلُّ شيءٍ عنده بمقدار} علم كلَّ شيءٍ فقدَّره تقديراً',NULL,'566',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1717,'{عالم الغيب} ما غاب عن جميع خلقه {والشهادة} وما شهده الخلق {الكبير} العظيم القدر {المتعال} عمّا يقوله المشركون',NULL,'566',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1718,'{سواء منكم} الآية يقول: الجاهر بنطقه والمُضمر في نفسه والظَّاهر في الطُُّرقات والمستخفي في الظُّلمات علمُ الله سبحانه فيهم جميعاً سواءٌ والمستخفي معناه: المختفي والسَّارب: الظَّاهر المارُّ على وجهه',NULL,'567',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1719,'{له} لله سبحانه {معقبات} مَلائِكَةٌ حَفَظَةٌ تَتَعَاقَبُ فِي النُّزُولِ إِلَى الأَرْضِ بعضهم باللَّيل وبعضهم بالنَّهار {من بين يديه} يدي الإِنسان {ومن خلفه يحفظونه من أمر الله} أَيْ: بأمره سبحانه ممَّا لم يُقدَّر فإذا جاء القدر خلَّوا بينه وبينه {إِنَّ اللَّهَ لا يُغَيِّرُ مَا بِقَوْمٍ حَتَّى يغيروا ما بأنفسهم} لا يسلب قوماً نعمةً حتى يعملوا بمعاصيه {وَإِذَا أَرَادَ اللَّهُ بِقَوْمٍ سُوءًا} عذاباً {فَلا مردَّ له} فلا ردَّ لَهُ {وَمَا لَهُمْ مِنْ دُونِهِ مِنْ وَالٍ} يلي أمرهم ويمنع العذاب عنهم',NULL,'567',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1720,'{هو الذي يريكم البرق خوفاً} للمسافر {وطمعاً} للحاضر في المطر {وينشئ} ويخلق {السحاب الثقال} بالماء',NULL,'567',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1721,'{ويسبح الرعد} وهو الملك المُوكَّل بالسَّحاب {بحمده} وهو ما يسمع من صوته وذلك تسبيحٌ لله تعالى {والملائكة من خيفته} أَيْ: وتُسبِّح الملائكة من خيفة الله تعالى وخشيته {ويرسل الصواعق} وهي التي تَحْرِق من برق السَّحاب وينتشر على الأرض ضوؤُه {فيصيب بها من يشاء} كما أصاب أربد حين جادل النبي صلى الله عليه وسلم وهو قوله: {وهم يجادلون في الله} والواو للحال وكان أربد جادل النبيَّ صلى الله عليه وسلم فقال: أخبرني عن ربِّنا أمن نحاسٍ أم حديد؟ فأحرقته الصَّاعقة {وهو شديد المحال} العقوبة أَي: القوَّة',NULL,'567',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1722,'{له دعوة الحق} لله من خلقه الدعوة الحقُّ وهي كلمة التَّوحيد لا إله إلاَّ الله {والذين يدعون} يعني: المشركون يدعون {من دونه} الأصنام {لا يستجيبون لهم بشيء إلاَّ كباسط} إلاَّ كما يستجاب للذي يبسط كفيه يشير إلى الماء ويدعوه إلى فيه {إلاَّ في ضلال} هلاكٍ وبطلانٍ',NULL,'568',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1723,'{ولله يسجد مَنْ في السماوات والأرض طوعاً} يعني: الملائكة والمؤمنين {وكرهاً} وهم مَنْ أُكرهوا على السُّجود فسجدوا لله سبحانه من خوف السَّيف واللَّفظ عامٌّ والمراد به الخصوص {وظلالهم بالغدو والآصال} كلُّ شخصٍ مؤمنٍ أو كافرٍ فإنَّ ظلَّه يسجد لله ونحن لا نقف على كيفية ذلك',NULL,'568',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1724,'{قل} يا محمد للمشركين: {من رب السماوات والأرض} ؟ ثمَّ أخبرهم فقل: {الله} لأنَّهم لا ينكرون ذلك ثمَّ ألزمْهم الحجَّة فقلْ: {أفاتخذتم من دونه أولياء} تولَّيتم غير ربِّ السَّماء والأرض أصناماً {لا يملكون لأنفسهم نفعاً ولا ضرَّاً} ثمَّ ضرب مثلاً للذين يعبدها والذي يعبد الله سبحانه فقال: {قل هل يستوي الأعمى} المشرك {والبصير} المؤمن {أم هل تستوي الظلمات} الشِّرك {والنور} الإِيمان {أم جعلوا لله شركاء} الآية يعني: أجعلوا لله شركاء خلقوا مثل ما خلق الله فتشابه خلق الشُّركاء بخلق الله عندهم؟ وهذه استفهامُ إنكارٍ أَيْ: ليس الأمرُ على هذا حتى يشتبه الأمر بل الله سبحانه هو المتفرِّد بالخلق وهو قوله: {قل الله خالق كلِّ شيء}',NULL,'568',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1725,'{أنزل من السماء ماء} يعني: المطر {فسالت أودية} جمع وادٍ {بقدرها} بقدر ما يملأها أراد بالماء القرآن وبالأودية القلوب والمعنى: أنزل قرآناً فقبلته القلوب بأقدارها منها ما رُزق الكثير ومنها ما رُزق القليل ومنها ما لم يُرزق شيئاً {فاحتمل السيل زبداً} وهو ما يعلو الماء {رابياً} عالياً فوقه والزَّبَد مَثلُ الكفر يريد: إنَّ الباطل ـ وإنْ ظهر الحقِّ في بعض الأحوال ـ فإنَّ الله سيمحقه ويُبطله ويجعل العاقبة للحقِّ وأهله وهو معنى قوله: {فأمَّا الزبد فيذهب جفاء} وهو ما رمى به الوادي {وأمَّا ما ينفع الناس} ممَّا ينبت المرعى {فيمكث} يبقى {في الأرض} ثمَّ ضرب مثلاً آخر وهو قوله: {وممَّا يوقدون عليه في النَّار} يعني: جواهر الأرض من الذَّهب والفضَّة والنُّحاس وغيرها ممَّا يدخل النَّار فتوقد عليها وتتخذ منها الحُلِيُّ وهو الذَّهب والفضَّة والأمتعة وهي للأواني يعني: النُّحاس والرَّصاص وغيرهما وهذا معنى قوله: {ابتغاء حلية أو متاعٍ زبدٌ مثله} أَيْ: مثل زبد الماء يريد: إنَّ من هذه الجواهر بعضها خبث ينفيه الكير {كَذَلِكَ} كما ذُكر من هذه الأشياء {يضرب الله} مثل الحقِّ والباطل وهذه الآية فيها تقديمٌ وتأخير في اللَّفظ والمعنى ما أخبرتك به',NULL,'569',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1726,'{للذين استجابوا لربهم} أجابوه إلى ما دعاهم إليه {الحسنى} الجنَّة {والذين لم يستجيبوا له} وهم الكفَّار {لَوْ أَنَّ لَهُمْ مَا فِي الأَرْضِ جَمِيعًا ومثله معه لافتدوا به} جعلوه فداء أنفسهم من العذاب {أولئك لهم سوء الحساب} وهو أن لا تُقبل منهم حسنة ولا يتجاوز عن سيئة',NULL,'570',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1727,'{أَفَمَنْ يَعْلَمُ أَنَّمَا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الحق كمن هو أعمى} نزلت في أبي جهل لعنه الله وحمزة رضي الله عنه {إنما يتذكر} يتَّعظ ويرتدع عن المعاصي {أولو الألباب} يعني: المهاجرين والأنصار',NULL,'570',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1728,'{الَّذِينَ يُوفُونَ بِعَهْدِ اللَّهِ وَلا يَنْقُضُونَ الْمِيثَاقَ} يعني: العهد الذي عاهدهم عليه وهم في صلب آدم',NULL,'570',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1729,'{وَالَّذِينَ يَصِلُونَ مَا أَمَرَ اللَّهُ بِهِ أَنْ يوصل} وهو الإِيمان بجميع الرُّسل',NULL,'570',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1730,'{والذين صبروا} على دينهم وما أُمروا به {ابتغاء وجه ربِّهم} طلب تعظيم الله تعالى {ويدرؤون} يدفعون {بالحسنة} بالتَّوبة {السيئة} المعصية وهو أنَّهم كلَّما أذنبوا تابوا {أولئك لهم عقبى الدار} يريد: عقابهم الجنَّة',NULL,'570',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1731,'{جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا وَمَنْ صَلَحَ مِنْ آبَائِهِمْ} ومَنْ صدَّق بما صدَّقوا به ـ وإن لم يعملْ مثل أعمالهم ـ يلحق بهم كرامةً لهم {والملائكة يدخلون عليهم من كلِّ باب} بالتَّحيَّة من الله سبحانه والهدايا',NULL,'571',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1732,'{سلامٌ عليكم} يقولون: سلامٌ عليكم والمعنى: سلَّمكم الله من العذاب {بما صبرتم} بصبركم في دار الدُّنيا عمَّا لا يحلُّ {فنعم عقبى الدار} فنعم العقبى عقبى داركم التي عملتم فيها ما أعقبكم الذي أنتم فيه',NULL,'571',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1733,'{والذين ينقضون} الآية مُفسَّرة في سورة البقرة',NULL,'571',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1734,'{الله يبسط الرزق} يُوسِّعه {لمن يشاء ويقدر} ويضيِّق {وفرحوا} يعني: مشركي مكة بما نالوا من الدُّنيا وبطروا {وما الحياة الدنيا في الآخرة} في حياة الآخرة أَيْ: بالقياس إليها {إلاَّ متاع} قليلٌ ذاهبٌ يُتمتَّع به ثمَّ يفنى',NULL,'571',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1735,'{ويقول الذين كفروا لولا} هلا {أنزل عليه آية من ربه} نزلت في مشركي مكَّة حين طالبوا رسول الله صلى الله عليه وسلم بالآيات {قُلْ إِنَّ اللَّهَ يُضِلُّ مَنْ يَشَاءُ} عن دينه كما أضلَّكم بعدما أنزل من الآيات وحرمكم الاستدلال بها {ويهدي إليه} يرشد إلى دينه {مَنْ أناب} رجع إلى الحقِّ',NULL,'571',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1736,'{الذين آمنوا} بدلٌ من قوله: {مَنْ أناب} {وتطمئن قلوبهم بذكر الله} إذا سمعوا ذكر الله سبحانه وتعالى أحبُّوه واستأنسوا به {ألا بذكر الله تطمئن القلوب} يريد: قلوب المؤمنين',NULL,'572',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1737,'{الذين آمنوا وعملوا الصالحات طوبى لهم} وهي شجرةٌ غرسها الله سبحانه بيده وقيل: فرحٌ لهم وقُرَّة أعينٍ',NULL,'572',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1738,'{كذلك} كما أرسلنا الأنبياء قبلك {أرسلناك في أمة} في قرنٍ {قد خلت} قد مضت {من قبلها أمم} قرونٌ {لِتَتْلُوَ عَلَيْهِمُ الَّذِي أَوْحَيْنَا إِلَيْكَ} يعني: القرآن {وهم يكفرون بالرحمن} وذلك أنَّهم قالوا: ما نعرف الرحمان إلاَّ صاحب اليمامة {قل هو ربي} أَي: الرَّحمن الذي أنكرتم معرفته هو إلهي وسيِّدي {لا إله إلاَّ هو}',NULL,'572',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1739,'{ولو أنَّ قرآناً} الآية نزلت حين قالوا للنبيِّ صلى الله عليه وسلم: إنْ كنت نبيَّاً كما تقول فسيِّر عنا جبال مكة فإنَّها ضيِّقةٌ واجعل لنا فيها عيوناً وأنهاراً حتى نزرع ونغرس وابعث لنا آباءنا من الموتى يكلِّمونا أنَّك نبيٌّ فقال الله سبحانه: {ولو أنَّ قرآناً سيرت به الجبال} يريد: لو قضيت على أن لا يقرأ القرآن على الجبال إلاَّ سارت ولا على الأرض إلاَّ تخرَّقت بالعيون والأنهار وعلى الموتى أن لا يُكلَّموا ما آمنوا لما سبق عليهم في علمي وهذا جواب لو وهو محذوف {بل} دع ذلك الذي قالوا من تسيير الجبال وغيره فالأمر لله جميعاً لو شاء أن يؤمنوا لآمنوا وإذا لم يشأ لم ينفع ما اقترحوا من الآيات وكان المسلمون قد أرادوا أن يُظهر رسول الله صلى الله عليه وسلم لهم آيةً ليجتمعوا على الإِيمان فقال الله: {أفلم ييأس الذين آمنوا} يعلم الذين آمنوا {أن لو يشاء الله} لهداهم من غير ظهور الآيات {وَلا يَزَالُ الَّذِينَ كَفَرُوا تُصِيبُهُمْ بِمَا صَنَعُوا} من كفرهم وأعمالهم الخبيثة {قارعة} داهيةٌ تقرعهم من القتل والأسر والحرب والجدب {أو تحلُّ} يا محمد أنت {قريباً من دارهم حتى يأتي وعد الله} يعني: القيامة وقيل: فتح مكة',NULL,'572',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1740,'{ولقد استهزئ برسل من قبلك} أُوذي وكُذِّب {فَأَمْلَيْتُ لِلَّذِينَ كَفَرُوا} أطلتُ لهم المدَّة بتأخير العقوبة ليتمادوا في المعصية {ثمَّ أخذتهم} بالعقوبة {فكيف كان عقاب} كيف رأيت ما صنعتُ بمن استهزأ برسلي كذلك أصنع بمشركي قومك',NULL,'573',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1741,'{أفمن هو قائم على كل نفس بما كسبت} أَيْ: بجرائه يعني: متولٍّ لذلك كما يقال: قام فلان بأمر كذا: إذا كفاه وتولاَّه والقائم على كلِّ نفس هو الله تعالى والمعنى: أفمن هو بهذه الصِّفة كمَنْ ليس بهذه الصِّفة من الأصنام التي لا تضرُّ ولا تنفع؟ وجواب هذا الاستفهام في قوله: {وجعلوا لله شركاء قل سموهم} بإضافة أفعالهم إليهم إن كانوا شركاء لله تعالى كما يضاف إلى الله أفعاله بأسمائه الحسنى نحو: الخالق والرَّازق فإن سمَّوهم قل أتنبئونه {أَمْ تُنَبِّئُونَهُ بِمَا لا يَعْلَمُ فِي الأَرْضِ} أَيْ: أتخبرون الله بشريكٍ له في الأرض وهو لا يعلمه بمعنى: أنَّه ليس له شريك {أم بظاهرٍ من القول} يعني: أم تقولون مجازاً من القول وباطلاً لا حقيقة له وهو كلامٌ في الظَّاهر ولا حقيقة له في الباطن ثمَّ قال: {بل} أَيْ: دع ذكر ما كنَّا فيه {زُيِّنَ لِلَّذِينَ كفروا مكرهم} زيَّن الشَّيطان لهم الكفر {وصدوا عن السبيل} وصدَّهم الله سبحانه عن سبيل الهدى {لهم عذاب في الحياة الدنيا} بالقتل والأسر {وَلَعَذَابُ الآخِرَةِ أَشَقُّ} أشدُّ وأغلظ {وَمَا لَهُمْ مِنَ اللَّهِ} من عذاب الله {من واق} حاجز ومانع',NULL,'573',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1742,'{لَهُمْ عَذَابٌ فِي الْحَيَاةِ الدُّنْيَا وَلَعَذَابُ الآخِرَةِ أَشَقُّ وَمَا لَهُمْ مِنَ اللَّهِ مِنْ وَاقٍ}',NULL,'574',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1743,'{مثل الجنة} صفة الجنَّة {التي وعد المتقون} وقوله: {أكلها دائم} يريد: إنَّ ثمارها لا تنقطع كثمار الدُّنيا {وظلها} لا يزول ولا تنسخه الشَّمس',NULL,'574',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1744,'{والذين آتيناهم الكتاب} يعني: مؤمني أهل الكتاب {يفرحون بما أنزل إليك} وذلك أنَّهم ساءهم قلَّة ذكر الرحمن في القرآن مع كثرة ذكره في التَّوراة فلما أنزل الله تعالى: {قُلِ ادْعُوا اللَّهَ أَوِ ادْعُوا الرحمن} فرح بذلك مؤمنو أهل الكتاب وكفر المشركون بالرَّحمن وقالوا: ما نعرف الرَّحمن إلاَّ رحمان اليمامة وذلك قوله: {ومن الأحزاب} يعني: الكفَّار الذين تحزَّبوا على رسول الله صلى الله عليه وسلم {مَنْ ينكر بعضه} يعني: ذكر الرَّحمن',NULL,'574',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1745,'{وكذلك} وكما أنزلنا الكتاب على الأنبياء بلسانهم {أنزلناه حُكْماً عربياً} يعني: القرآن لأنَّه به يحكم ويفصل بين الحقِّ والباطل وهو بلغة العرب {ولئن اتبعت أهواءهم} وذلك أنَّ المشركين دعوه إلى ملَّة آبائه فتوعَّده الله سبحانه على ذلك بقوله: {مَا لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَلا واق}',NULL,'575',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1746,'{وَلَقَدْ أَرْسَلْنَا رُسُلا مِنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ أزواجاً} ينكحونهنَّ {وذرية} وأولاداً أنسلوهم وذلك أنَّ اليهود عيَّرت رسول الله صلى الله عليه وسلم بكثرة النِّساء وقالوا: ما له همَّةٌ إلاَّ النِّساء والنِّكاح {وَمَا كَانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلا بإذن الله} أَيْ: بإطلاقه له الآية وهذا جوابٌ للذين سألوه أن يوسِّع لهم مكَّة {لِكُلِّ أَجَلٍ كِتَابٌ} لكلِّ أجلٍ قدَّره الله ولكلِّ أمرٍ قضاه كتابٌ أثبت فيه فلا تكون آيةٌ إلاَّ بأجلٍ قد قضاه الله تعالى في كتابٍ',NULL,'575',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1747,'{يَمْحُو اللَّهُ مَا يَشَاءُ وَيُثْبِتُ وَعِنْدَهُ أُمُّ الكتاب} اللَّوح المحفوظ يمحو منه ما يشاء ويثبت ما يشاء وظاهر هذه الآية على العموم وقال قوم: إلاَّ السَّعادة والشَّقاوة والموت والرزق والخلق والخلق',NULL,'575',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1748,'{وإنما نرينك بعض الذي نعدهم} من العذاب {أو نتوفينك} قبل ذلك {فإنما عليك البلاغ} يريد: قد بلَّغت {وعلينا الحساب} إليَّ مصيرهم فأجازيهم أَيْ: ليس عليك إلاَّ البلاغ كيف ما صارت حالهم',NULL,'576',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1749,'{أَوَلَمْ يروا} يعني: مشركي مكَّة {أَنَّا نَأْتِي الأَرْضَ} نقصد أرض مكَّة {ننقصها من أطرافها} بالفتوح على المسلمين يقول: أولم ير أهل مكَّة أنَّا نفتح لمحمد صلى الله عليه وسلم ما حولها من القرى أفلا يخافون أن تنالهم يا محمد {والله يحكم} بما يشاء {لا معقب لحكمه} لا أحدٌ يتتبع ما حكم به فيغيِّره والمعنى: لا ناقص لحكمه ولا رادَّ له {وهو سريع الحساب} أَي: المجازاة',NULL,'576',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1750,'{وَقَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ} يعني: كفَّار الأمم الخالية مكروا بأنبيائهم {فَلِلَّهِ الْمَكْرُ جَمِيعًا} يعني: إنَّ مكر الماكرين له أَيْ: هو من خلقه فالمكر جميعاً مخلوق له ليس يضرُّ منه شيءٌ إلاَّ بإذنه {يعلم ما تكسب كلُّ نفس} جميع الأكساب معلوم له {وسيعلم الكفار} وهو اسم الجنس {لمن} العاقبة بالحنة وقوله تعالى:',NULL,'576',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1751,'{ومن عنده علم الكتاب} هم مؤمنو أهل الكتابين وكانت شهادتهم قاطعةً لقول أهل الخصوم',NULL,'576',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1752,'{الر} أنا الله أرى هذا {كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمَاتِ إلى النور} من الشِّرك إلى الإِيمان {بإذن ربهم} بقضاء ربِّهم لأنَّه لا يهتدي مهتدٍ إلاَّ بإذن الله سبحانه ثمَّ بيَّن ما ذلك النُّور فقال: {إِلَى صِرَاطِ الْعَزِيزِ الْحَمِيدِ}',NULL,'577',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1753,'{الله الذي له ما في السماوات وَمَا فِي الأَرْضِ وَوَيْلٌ لِلْكَافِرِينَ مِنْ عَذَابٍ شديد}',NULL,'577',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1754,'{الذين يستحبون} يُؤثرون ويختارون {الْحَيَاةَ الدُّنْيَا عَلَى الآخِرَةِ وَيَصُدُّونَ عَنْ سَبِيلِ الله} ويمنعون النَّاس عن دين الله {ويبغونها عوجاً} مضى تفسيره {أولئك في ضلال} في خطأ {بعيد} عن الحقِّ',NULL,'577',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1755,'{وَمَا أَرْسَلْنَا مِنْ رَسُولٍ إِلا بِلِسَانِ قَوْمِهِ} بلغة قومه ليفهموا عنه وهو معنى قوله: {لِيُبَيِّنَ لَهُمْ فَيُضِلُّ اللَّهُ مَنْ يَشَاءُ} بعد التَّبيين بإيثاره الباطل {ويهدي مَنْ يشاء} باتباع الحقِّ',NULL,'577',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1756,'{ولقد أرسلنا موسى بآياتنا} بالبراهين التي دلَّت على صحَّة نبوَّته {أَنْ أَخْرِجْ قَوْمَكَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ} من الشِّرك إلى الإِيمان {وذكرهم} وَعِظهم {بأيام الله} بنعمه أَي: بالتَّرغيب والتَّرهيب والوعد والوعيد {إِنَّ فِي ذَلِكَ} التَّذكير بأيَّام الله {لآيات} لدلالاتٍ {لكلِّ صبَّار} على طاعة الله {شكور} لأنعمه والآية الثانية مفسرة في سورة البقرة وقوله:',NULL,'578',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1757,'{وَإِذْ قَالَ مُوسَى لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ أَنْجَاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ وَيُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ وَفِي ذَلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ}',NULL,'578',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1758,'{وإذ تأذَّن} معطوفٌ على قوله: {إذ أنجاكم} والمعنى: وإذ أعلم ربُّكم {لئن شكرتم} وحَّدْتم وأطعتم {لأزيدنَّكم} ممَّا يجب الشُّكر عليه وهو النِّعمة {ولئن كفرتم} جحدتم حقِّي وحقَّ نعمتي {إِنَّ عذابي لشديد} تهديدٌ بالعذاب على كفران النِّعمة',NULL,'578',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1759,'{وَقَالَ مُوسَى إِنْ تَكْفُرُوا أَنْتُمْ وَمَنْ فِي الأرض جميعاً فإن الله لغني حميد}',NULL,'578',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1760,'{أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ مِنْ قَبْلِكُمْ قَوْمِ نوح وعاد وثمود والذين من بعدهم} يعني: من بعد هؤلاء الذين أهلكم الله {لا يعلمهم إلاَّ الله} لكثرتهم ولا يعلم عدد تلك الأمم وتعيينها إلاَّ الله {جاءتهم رسلهم بالبينات فردوا أيديهم} أيدي أنفسهم {في أفواههم} أَيْ: ثقل عليهم مكانهم فعضُّوا على أصابعهم من شدَّة الغيظ',NULL,'578',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1761,'{قالت رسلهم أفي الله شكٌّ} أفي توحيد الله سبحانه شكٌّ؟ وهذا استفهامٌ معناه الإنكار أي: لا شكَّ في ذلك ثمَّ وصف نفسه بما يدلُّ على وحدانيته وهو قوله: {فاطر السماوات والأرض يدعوكم} إلى طاعته بالرُّسل والكتب {لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ إِلَى أَجَلٍ مسمَّى} لا يعاجلكم بالعقوبة والمعنى: إن لم تجيبوا عوجلتم وباقي الآية وما بعدها إلى قوله:',NULL,'579',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1762,'{قَالَتْ لَهُمْ رُسُلُهُمْ إِنْ نَحْنُ إِلا بَشَرٌ مِثْلُكُمْ وَلَكِنَّ اللَّهَ يَمُنُّ عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ وَمَا كَانَ لَنَا أَنْ نَأْتِيَكُمْ بِسُلْطَانٍ إِلا بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ المؤمنون}',NULL,'579',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1763,'{وما لنا أن لا نَتَوَكَّلَ عَلَى اللَّهِ وَقَدْ هَدَانَا سُبُلَنَا وَلَنَصْبِرَنَّ عَلَى مَا آذَيْتُمُونَا وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ}',NULL,'579',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1764,'{وَقَالَ الَّذِينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ أَرْضِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا فَأَوْحَى إِلَيْهِمْ رَبُّهُمْ لنهلكن الظالمين}',NULL,'579',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1765,'{ذلك لمن خاف مقامي وخاف وعيد} ظاهر ومعنى: {خاف مقامي} معناه: خاف مقامه بين يدي {وخاف وعيدِ} : ما أوعدت من العذاب',NULL,'579',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1766,'{واستفتحوا} واستنصروا الله سبحانه على قومهم ففازوا بالنَّصر {وَخَابَ كُلُّ جَبَّارٍ} متكبِّرٍ عن طاعة الله سبحانه {عنيدٍ} مجانب للحقَّ',NULL,'579',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1767,'{من ورائه جهنم} أَيْ: أمامه جهنَّم فهو يردها {ويُسقى من ماء صديد} وهو ما يسيل من الجرح مُختلطاً بالدَّم والقيح',NULL,'580',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1768,'{يتجرَّعه} يتحسَّاه بالجرع لا بمرَّةٍ لمرارته {ولا يكاد يسيغه} لا يجيزه في الحلق إلاَّ بعد إبطاءٍ {ويأتيه الموت} أَيْ: أسباب الموت من البلايا التي تصيب الكافر في النَّار {من كلِّ مكان} من كلِّ شعرةٍ في جسده {وَمَا هُوَ بِمَيِّتٍ} موتاً تنقطع معه الحياة {ومن ورائه} ومن بعد ذلك العذاب {عذاب غليظ} متَّصل الآلام ثمَّ ضرب مثلاً لأعمال الكافر فقال:',NULL,'580',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1769,'{مَثَلُ الَّذِينَ كَفَرُوا بِرَبِّهِمْ أَعْمَالُهُمْ كَرَمَادٍ اشْتَدَّتْ به الريح في يوم عاصف} أَيْ: شديد هبوب الرِّيح ومعنى الآية: إنَّ كلَّ ما تقرَّب به الكافر إلى الله تعالى فَمُحْبَطٌ غيرُ منتفعٍ به لأنَّهم أشركوا فيها غير الله سبحانه وتعالى كالرَّماد الذي ذّرَّتْهُ الرِّيح وصار هباءً لا يُنتفع به فذلك قوله: {لا يقدرون مما كسبوا على شيء} أَيْ: لا يجدون ثواب ما عملوا {ذَلِكَ هُوَ الضلال البعيد} يعني: ضلال أعمالهم وذهابها والمعنى: ذلك الخسران الكبير',NULL,'580',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1770,'{ألم تر} يا محمد {أن الله خلق السماوات والأرض بالحق} أَيْ: بقدرته وصنعه وعلمه وإرادته وكلُّ ذلك حقٌّ {إن يشأ يذهبكم} يُمتكم أيُّها الكفَّار {ويأت بخلق جديد} خيرٍ منكم وأطوع',NULL,'580',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1771,'{وما ذلك على الله بعزيز} بممتنعٍ شديدٍ',NULL,'580',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1772,'{وبرزوا لله جميعاً} خرجوا من قبورهم إلى المحشر {فقال الضعفاء} وهم الأتباع لأكابرهم الذين {استكبروا} عن عبادة الله: {إنَّا كنَّا} في الدُّنيا {لكم تبعاً فهل أنتم مغنون} دافعون {عَنَّا مِنْ عَذَابِ اللَّهِ مِنْ شَيْءٍ قَالُوا لو هدانا الله لهديناكم} أَيْ: إنَّما دعوناكم إلى الضَّلال لأنَّا كنَّا عليه ولو أرشدنا الله لأرشدناكم',NULL,'580',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1773,'{وقال الشيطان} يعني: إبليس {لما قضي الأمر} فصار أهل الجنَّة في الجنَّة وأهل النَّار في النَّار وذلك أنَّ أهل النَّار حينئذٍ يجتمعون باللائمة على إبليس فيقوم خطيباً ويقول: {إِنَّ اللَّهَ وَعَدَكُمْ وَعْدَ الْحَقِّ} يعني: كون هذا اليوم فصدقكم وعده {ووعدتكم} أنَّه غير كائنٍ {فَأَخْلَفْتُكُمْ وَمَا كَانَ لِي عَلَيْكُمْ مِنْ سُلْطَانٍ} أَيْ: ما أظهرت لكم حجَّةً على ما وعدتكم {إلاَّ أن دعوتكم} لكن دعوتكم {فاستجبتم لي} فصدَّقتموني {فلا تلوموني ولوموا أنفسكم} حيث أجبتموني من غير برهانٍ {مَا أَنَا بِمُصْرِخِكُمْ} بمغيثكم {وَمَا أَنْتُمْ بِمُصْرِخِيَّ إِنِّي كَفَرْتُ بِمَا أَشْرَكْتُمُونِ من قبل} بإشراككم إيَّاي مع الله سبحانه في الطَّاعة إنَّي جحدت أن أكون شريكاً لله فيما أشركتموني {إنَّ الظالمين} يريد: المشركين وقوله:',NULL,'581',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1774,'{تحيتهم فيها سلام} يحييهم الله سبحانه بالسَّلام ويحيي بعضهم بعضاً بالسَّلام',NULL,'581',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1775,'{أَلَمْ تَرَ كَيْفَ ضَرَبَ اللَّهُ مَثَلا} بيَّن شبهاً ثمَّ فسَّره فقال: {كلمة طيبة} يريد: لا إله إلاَّ الله {كشجرة طيبة} يعني: النَّخلة {أصلها} أصل هذه الشَّجرة الطَّيِّبة {ثابت} في الأرض {وفرعها} أعلاها عالٍ {في السماء}',NULL,'581',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1776,'{تؤتي} هذه الشَّجرة {أكلها} ثمرها {كلَّ حين} كلَّ وقتٍ في جميع السَّنة ستة أشهرٍ طلعٌ رخص وستة أشهرٍ رطبٌ طيِّبٌ فالانتفاع بالنَّخلة دائمٌ في جميع السَّنة كذلك الإِيمان ثابتٌ في قلب المؤمن وعمله وقوله وتسبيحه عالٍ مرتفع إلى السَّماء ارتفاع فروع النَّخلة وما يكتسبه من بركة الإِيمان وثوابه كما ينال من ثمرة النَّخلة في أوقات السَّنة كُلِّها من الرُّطَب والبسر والتَّمر {ويضرب الله الأمثال للناس} يريد: أهل مكَّة {لَعَلَّهُمْ يَتَذَكَّرُونَ} لكي يتَّعظوا',NULL,'582',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1777,'{ومثل كلمة خبيثة} يعني: الشِّرك بالله سبحانه {ك} مثل {شجرة خبيثة} وهي الكشوت {اجتثت} انتزعت واستؤصلت والكشوت كذلك {من فوق الأرض} لم يرسخ فيها ولم يضرب فيها بعرق {ما لها من قرار} مستقرٌّ في الأرض يريد: إنَّ الشكر لا ينتفع به صاحبه وليس له حجَّةٌ ولا ثباتٌ كهذه الشَّجرة',NULL,'582',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1778,'{يثبت الله الذين آمنوا بالقول الثابت} وهو قول لا إله إلا الله {فِي الْحَيَاةِ الدُّنْيَا} على الحقَّ {وَفِي الآخِرَةِ} يعني: في القبر يُلقِّنهم كلمة الحقِّ عند سؤال الملكين {ويضل الله الظالمين} لا يُلقِّن المشركين ذلك حتى إذا سُئلوا في قبورهم قالوا: لا ندري {ويفعل الله ما يشاء} من تلقين المؤمنين الصَّواب وإضلال الكافرين',NULL,'582',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1779,'{ألم تر إلى الذين بدَّلوا نعمة الله كفراً} بدَّلوا ما أنعم الله سبحانه عليهم به من الإيمان ببعث الرسول صلى الله عليه وسلم كفراً حيث كفروا به {وأحلوا قومهم} الذين اتَّبعوهم {دار البوار} الهلاك ثمَّ فسَّرها فقال:',NULL,'583',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1780,'{جهنم يصلونها وبئس القرار} أَي: المقرُّ',NULL,'583',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1781,'{وجعلوا لله أنداداً} يعني: الأصنام {ليضلوا عن سبيله} النَّاس عن دين الله {قل تمتعوا} بدنياكم {فإنَّ مصيركم إلى النار} وقوله:',NULL,'583',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1782,'{لا بيع فيه} لا فداء فيه {ولا خلال} مخالة يعني: يوم القيامة وهو يوم لا بيعٌ فيه ولا شراءٌ ولا مُخالَّةٌ ولا قرابةٌ إنَّما هي أعمالٌ يُثاب بها قومٌ ويعاقب عليها آخرون',NULL,'583',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1783,'{الله الذي خلق السماوات وَالأَرْضَ وَأَنْزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَكُمْ وَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِأَمْرِهِ وَسَخَّرَ لَكُمُ الأَنْهَارَ}',NULL,'583',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1784,'{وسخر لكم الشمس والقمر} ذلَّلهما لما يُراد منهما {دائبين} مقيمين على طاعة الله سبحانه وتعالى في الجري {وسخر لكم الليل} لتسكنوا فيه {والنهار} لتبتغوا من فضله ومعنى لكم في هذه الآية لأجلكم ليس أنَّها مسخَّرة لنا هي مسخَّرةٌ لله سبحانه لأجلنا ويجوز أنَّها مسخَّرة لنا لانتفاعنا بها على الوجه الذي نريد وقوله:',NULL,'583',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1785,'{وإن تعدوا نعمة الله} إنعام الله عليكم {لا تحصوها} لا تطيقوا عدَّها {إن الإنسان} يعني: الكافر {لظلوم} لنفسه {كفَّار} نعمة ربِّه وقوله:',NULL,'584',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1786,'{واجنبني} أَيْ: بعِّدني واجعلني من على جانبٍ بعيدٍ',NULL,'584',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1787,'{رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيرًا مِنَ النَّاسِ} أَيْ: ضلُّوا بسببها {فمن تبعني} على ديني {فإنه مني} من المتدينين بديني {ومن عصاني} فيما دون الشِّرك {فإنك غفور رحيم}',NULL,'584',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1788,'{ربنا إني أسكنت من ذريتي} يعني: إسماعيل عليه السَّلام {بوادٍ غير ذي زرعٍ} مكَّة حرسها الله {عند بيتك المحرَّم} الذي مضى في علمك أنَّه يحدث في هذا الوادي {ربنا ليقيموا الصلاة} ليعبدوك {فَاجْعَلْ أَفْئِدَةً مِنَ النَّاسِ تَهْوِي إِلَيْهِمْ} تريدهم وتحنُّ إليهم لزيارة بيتك {وارزقهم من الثمرات} ذُكر تفسيره في سورة البقرة {لعلَّهم يشكرون} كي يوحدونك ويعظموك',NULL,'584',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1789,'{رَبَّنَا إِنَّكَ تَعْلَمُ مَا نُخْفِي وَمَا نُعْلِنُ وَمَا يَخْفَى عَلَى اللَّهِ مِنْ شَيْءٍ فِي الأَرْضِ وَلا فِي السَّمَاءِ}',NULL,'584',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1790,'{الحمد لله الذي وهب لي} أعطاني {على الكبر إسماعيل} لأنَّه وُلد له وهو ابن تسع وتسعين {وإسحاق} وُلد له وهو ابن مائة سنة واثنتي عشرة سنة وقوله:',NULL,'584',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1791,'{ومن ذريتي} أَيْ: واجعل منهم مَنْ يقيم الصَّلاة وقوله:',NULL,'585',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1792,'{ولوالدي} استغفر لهما بشرط الإِيمان',NULL,'585',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1793,'{وَلا تَحْسَبَنَّ اللَّهَ غَافِلا عَمَّا يَعْمَلُ الظَّالِمُونَ} يريد: المشركين من أهل مكَّة {إنما يؤخرهم} فلا يعاقبهم في الدُّنيا {ليوم تشخص} تذهب فيه أبصار الخلائق إلى الهواء حيرةً ودهشةً',NULL,'585',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1794,'{مهطعين} مسرعين منطلقين إلى الداعي {مقنعي} رافعي {رؤوسهم} إلى السماء لا ينظر أحدٌ إلى أحدٍ {لا يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ} لا ترجع إليهم أبصارهم من شدَّة النَّظر فهي شاخصةٌ {وأفئدتهم هواء} وقلوبهم خاليةٌ عن العقول بما ذهلوا من الفزع وقوله:',NULL,'585',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1795,'{فيقول الذين ظلموا} أَيْ: أشركوا {ربنا أخرنا إلى أجل قريب} استمهلوا مدَّةً يسيرةً كي يجيبوا الدَّعوة فيقال لهم: {أَوَلَمْ تَكُونُوا أَقْسَمْتُمْ مِنْ قَبْلُ ما لكم من زوال} حلفتم في الدُّنيا أنَّكم لا تُبعثون ولا تنتقلون إلى الآخرة وهو قوله: {وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لا يَبْعَثُ الله من يموت} الآية',NULL,'585',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1796,'{وسكنتم} في الدُّنيا {في مساكن الذين ظلموا أنفسهم} يعني: الأمم الكافرة {وتبيَّن لكم كيف فعلنا بهم} فلم تنزجروا {وضربنا لكم الأمثال} في القرآن فلم تعتبروا',NULL,'586',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1797,'{وَقَدْ مَكَرُوا مَكْرَهُمْ} يعني: مكرهم بالنبي صلى الله عليه وسلم وما همُّوا به من قتله أو نفيه {وعند الله مكرهم} هو عالمٌ به لا يخفى عليه ما فعلوا فهو يجازيهم عليه {وإن كان} وما كان {مكرهم لِتَزُولَ مِنْهُ الْجِبَالُ} يعني: أمر النبيِّ صلى الله عليه وسلم أَيْ: ما كان مكرهم ليبطل أمراً هو في ثبوته وقوَّته كالجبال',NULL,'586',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1798,'{فلا تحسبن الله} يا محمد {مخلف وعده رسله} ما وعدهم من الفتح والنَّصر {إِنَّ اللَّهَ عَزِيزٌ} منيع {ذُو انْتِقَامٍ} من الكفَّار يجازيهم بما كان من سيئاتهم',NULL,'586',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1799,'{يوم تبدل الأرض غير الأرض والسماوات} تُبدَّل الأرض بأرضٍ كالفضَّة بيضاء نقيَّة يُحشر النَّاس عليها والسَّماء من ذهبٍ {وبرزوا} وخرجوا من القبور كقوله تعالى: {وبرزوا لله جميعاً}',NULL,'586',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1800,'{وترى المجرمين} الذين زعموا أنَّ لله شريكاً وولداً يوم القيامة {مقرنين} موصولين بشياطينهم كلُّ كافرٍ مع شيطانٍ في غلٍّ والأصفاد: سلاسل الحديد والأغلال',NULL,'586',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1801,'{سرابيلهم} قُمصهم {من قطران} وهو الهِناء الذي يُطلى به الإِبل وذلك أبلغ لا شتعال النَّار فيهم {وتغشى وجوههم} وتعلو وجوههم {النار}',NULL,'587',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1802,'{ليجزي الله كلَّ نفس} من الكفَّار {ما كسبت} أَيْ: ليقع لهم الجزاء من الله سبحانه بما كسبوا',NULL,'587',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1803,'{هذا} القرآن {بلاغ للناس} أَيْ: أنزلناه إليك لتبلِّغهم {ولينذروا به} ولتنذرهم أنت يا محمد {وليعلموا} بما ذُكر فيه من الحجج {أنما هو إله واحدٌ وليذكر} وليتعظ {أولو الألباب} أهل اللُّبِّ والعقل والبصائر',NULL,'587',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1804,'{الر} أنا الله أرى {تلك آيات} هذه آيات {الكتاب} الذي هو قرآن مبين للأحكام',NULL,'588',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1805,'{ربما يودُّ} الآية نزلت في تمنِّي الكفَّار الإِسلام عند خرةج مَنْ يخرج من النَّار',NULL,'588',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1806,'{ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا} يقول: دع الكفَّار يأخذوا حظوظهم من دنياهم {ويلههم الأمل} يشغلهم عن الأخذ بحظِّهم من الإِيمان والطَّاعة {فسوف يعلمون} إذا وردوا القيامة وبال ما صنعوا',NULL,'588',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1807,'{وما أهلكنا من قرية} يعني: أهلها {إِلا وَلَهَا كِتَابٌ مَعْلُومٌ} أجلٌ ينتهون إليه يعني: إنَّ لأهل كلِّ قرية أجلاً مؤقَّتاً لا يُهلكهم حتى يبلغوه',NULL,'588',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1808,'{ما تسبق من أمة أجلها} أيْ: ما تتقدَّم الوقت الذي وُقَّت لها {وما يستأخرون} لا يتأخَّرون عنه',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1809,'{وقالوا يا أيها الذي نُزِّل عليه الذكر} أَي: القرآن قالوا هذا استهزاءً',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1810,'{لو ما} هلا {تأتينا بالملائكة إن كنت من الصادقين} أنَّك نبيٌّ فقال الله عز وجل:',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1811,'{ما ننزل الملائكة إلاَّ بالحق} أَيْ: بالعذاب {وما كانوا إذاً منظرين} أَيْ: لو نزلت الملائكة لم يُنظروا ولم يُمهلوا',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1812,'{إنا نحن نزلنا الذِّكر} القرآن {وإنا له لحافظون} من أن يُزاد فيه أو يُنقص',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1813,'{ولقد أرسلنا من قبلك} أَيْ: رسلاً {في شيع الأوَّلين} أَيْ: فِرَقِهم',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1814,'{وَمَا يَأْتِيهِمْ مِنْ رَسُولٍ إِلا كَانُوا بِهِ يستهزئون} تعزية للنبي صلى الله عليه وسلم',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1815,'{كذلك} أَيْ: كما فعلوا {نسلكه} ندخل الاستهزاء والشِّرك والضَّلال {في قلوب المجرمين} ثمَّ بيَّن أَيَّ شيء الذي أدخل في قلوبهم فقال:',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1816,'{لا يؤمنون به} أَيْ: بالرَّسول {وقد خلت} مضت {سنَّة الأولين} بتكذيب الرُّسل فهؤلاء المشركون يقتفون آثارهم في الكفر',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1817,'{ولو فتحنا عليهم} على هؤلاء المشركين {باباً من السماء فظلوا فيه يعرجون} فطفقوا فيه يصعدون لجحدوا ذلك وقالوا:',NULL,'589',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1818,'{إنما سكِّرت أبصارنا} أَيْ: سُدَّت بالسِّحر فتتخايل لأبصارنا غير ما نرى {بل نحن قوم مسحورون} سحرنا محمد - صلى الله عليه وسلم - فلا نبصر',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1819,'{ولقد جعلنا في السماء بروجاً} يعني: منازل الشَّمس والقمر {وزيناها} بالنُّجوم للمعتبرين والمستدلِّين على توحيد صانعها',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1820,'{وحفظناها من كلّ شيطان رجيم} مرميٍّ بالنُّجوم',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1821,'{إلاَّ من استرق السمع} يعني: الخطفة اليسيرة {فأتبعه} لحقه {شهاب} نارٌ {مبين} ظاهرٌ لأهل الأرض',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1822,'{والأرض مددناها} بسطناها على وجه الماء {وَأَلْقَيْنَا فيها رواسي} جبالاً ثوابت لئلا تتحرَّك بأهلها {وأنبتنا فيها} في الجبال {من كلِّ شيء موزون} كالذَّهب والفضَّة والجواهر',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1823,'{وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ} من الثِّمار والحبوب {ومَنْ لستم له برازقين} العبيد والدَّوابَّ والأنعام تقديره: وجعلنا لكم فيها معايش وعبيداً وإماءً ودوابَّ نرزقهم ولا ترزقوهم',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1824,'{وإن من شيء} يعني: من المطر {إلاَّ عندنا خزائنه} أَيْ: في حكمنا وأمرنا {وما ننزله إلاَّ بقدر معلوم} لا ننقصه ولا نزيده غير أنَّه يصرفه إلى مَنْ يشاء حيث شاء كما شاء',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1825,'{وأرسلنا الرياح لواقح} السَّحاب تَمُجُّ الماء فيه فهي لواقح بمعنى: ملقحاتٌ وقيل: لواقح: حوامل لأنَّها تحملُ الماء والتُّراب والسَّحاب {فأسقيناكموه} جعلناه سقياً لكم {وَمَا أَنْتُمْ لَهُ} لذلك الماء المنزل من السَّماء {بخازنين} بحافظين أَيْ: ليست خزائنه بأيديكم',NULL,'590',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1826,'{وَإِنَّا لَنَحْنُ نُحْيِي وَنُمِيتُ وَنَحْنُ الْوَارِثُونَ} إذا مات جميع الخلائق',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1827,'{ولقد علمنا المستقدمين} الآية خص رسول الله صلى الله عليه وسلم عَلَى الصَّفِّ الأَوَّلِ فِي الصَّلاةِ فَازْدَحَمَ النَّاسُ عليه فأنزل الله سبحانه هذه الآية يقول: قد علمنا جميعهم وإنَّما نجزيهم على نياتهم',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1828,'{وَإِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1829,'{ولقد خلقنا الإنسان} آدم {من صلصال} طينٍ منتنٍ {من حمأ} طينٍ أسود {مسنون} متغيِّر الرَّائحة',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1830,'{والجانَّ} أبا الجنِّ {خلقناه من قبل} خَلْقِ آدم {مِنْ نَارِ السَّمُومِ} وهي نارٌ لا دخان لها',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1831,'{وَإِذْ قَالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي خَالِقٌ بَشَرًا مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1832,'{فإذا سويته} عدَّلت صورته {ونفخت فيه} وأجريت فيه {من روحي} المخلوقة لي {فقعوا} فخرُّوا {له ساجدين} سجود تحية وقوله:',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1833,'{فَسَجَدَ الْمَلائِكَةُ كُلُّهُمْ أَجْمَعُونَ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1834,'{إِلا إِبْلِيسَ أَبَى أَنْ يَكُونَ مَعَ السَّاجِدِينَ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1835,'{قال يا إبليس ما لك أن لا تَكُونَ مَعَ السَّاجِدِينَ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1836,'{قَالَ لَمْ أَكُنْ لأَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصَالٍ مِنْ حَمَإٍ مَسْنُونٍ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1837,'{قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ}',NULL,'591',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1838,'{وإنَّ عليك اللعنة} الآية يقول: يلعنك أهل السَّماء وأهل الأرض إلى يوم الجزاء فتحصل حينئذٍ من عذاب النار وقوله:',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1839,'{قَالَ رَبِّ فَأَنْظِرْنِي إِلَى يَوْمِ يُبْعَثُونَ}',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1840,'{قَالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ}',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1841,'{إلى يوم الوقت المعلوم} يعني: النَّفخة الأولى حين يموت الخلائق',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1842,'{قَالَ رَبِّ بِمَا أغويتني} أَيْ: بسبب إغوائك إيَّاي {لأُزَيِّنَنَّ لهم} لأولاد آدم الباطل حتى يقعوا فيه',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1843,'{إلاَّ عبادك منهم المخلصين} أَيْ: المُوحِّدين المؤمنين الذي أخلصوا دينهم عن الشِّرك',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1844,'{قَالَ هَذَا صِرَاطٌ عليّ} هذا طريق عليَّ {مستقيم} مرجعه إليَّ فأجازي كلاً بأعمالهم يعني: طريق العبوديَّة',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1845,'{إنَّ عبادي} يعني: الذين هداهم واجتباهم {ليس لك عليهم سلطانٌ} قوَّةٌ وحجَّةٌ في إغوائهم ودعائهم إلى الشِّرك والضَّلال',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1846,'{وإنًّ جهنم لموعدهم أجمعين} يريد: إبليس ومَنْ تبعه من الغاوين',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1847,'{لها} لجهنم {سبعة أبواب} سبعة أطباقٍ طبقٌ فوق طبقٍ {لِكُلِّ بَابٍ مِنْهُمْ} من أتباع إبليس {جزء مقسوم}',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1848,'{إنَّ المتقين} للفواحش والكبائر {فِي جَنَّاتٍ وَعُيُونٍ} يعني: عيون الماء والخمر يقال لهم:',NULL,'592',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1849,'{ادخلوها بسلامٍ} بسلامةٍ {آمنين} من سخط الله سبحانه وعذابه',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1850,'{وَنَزَعْنَا مَا فِي صُدُورِهِمْ مِنْ غِلٍّ} ذكرناه في سورة الأعراف {إخواناً} متآخين {على سرر} جمع سرير {متقابلين} لا يرى بعضهم قفا بعض',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1851,'{لا يمسهم} لا يصيبهم {فيها نصب} إعياء',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1852,'{نبئ عبادي} أخبر أوليائي {أَنِّي أَنَا الْغَفُورُ} لأوليائي {الرحيم} بهم',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1853,'{وأنَّ عذابي هو العذاب الأليم} لأعدائي',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1854,'{ونبئهم عن ضيف إبراهيم} يعني: الملائكة الذين أتوه في صورة الأضياف',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1855,'{إِذْ دَخَلُوا عَلَيْهِ فقالوا سلاماً} سلَّموا سلاماً فـ {قال} إبراهيم: {إنَّا منكم وجلون} فَزِعُون',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1856,'{قالوا لا توجل} : لاتفزع وقوله:',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1857,'{عَلَى أَنْ مَسَّنِيَ الْكِبَرُ} أَيْ: على حالة الكبر {فبم تبشرون} استفهامُ تعجُّبٍ كأنَّه عجب من الولد على كبره',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1858,'{قالوا بشرناك بالحق} بما قضاه الله أن يكون {فلا تكن من القانطين} الآيسين',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1859,'{قال ومَنْ يقنط} ييئس {من رحمة ربِّه إلاَّ الضالون} المكذِّبون',NULL,'593',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1860,'{قال فما خطبكم} ما شأنكم وما الذي جئتم له؟',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1861,'{قالوا إنا أرسلنا إلى قوم مجرمين} يعني: قوم لوط',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1862,'{إلاَّ آل لوط} أتباعه الذين كانوا على دينه وقوله:',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1863,'{قدَّرنا} قضينا ودبَّرنا أنَّها تتخلَّف وتبقى مع مَنْ بقي حتى تهلك وقوله:',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1864,'{فلما جاء آل لوط المرسلون}',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1865,'{منكرون} أَيْ: غير معروفين',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1866,'{قَالُوا بَلْ جِئْنَاكَ بِمَا كَانُوا فِيهِ يَمْتَرُونَ} بالعذاب الذي كانوا يشكُّون في نزوله',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1867,'{وأتيناك بالحق} بالأمر الثَّابت الذي لا شكَّ فيه من عذاب قومك',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1868,'{فأسر بأهلك} مُفسَّرٌ في سورة هود {واتبع أدبارهم} امش على آثارهم ببناتك وأهلك لئلا يتخلَّف منهم أحدٌ {ولا يلتفت منكم أحد} لئلا يرى عظيم ما ينزل بهم من العذاب {وامضوا حيث تؤمرون} حيث يقول لكم جبريل عليه السَّلام',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1869,'{وقضينا إليه} أوحينا إليه وأخبرناه {ذلك الأمر} الذي أخبرته الملائكة إبراهيم من عذاب قومه وهو {أنَّ دابر هؤلاء} أَيْ: أواخر مَنْ تبقَّى منهم {مقطوع} مُهلَكٌ {مصبحين} داخلين في وقت الصُّبح يريد: إنَّهم مهلكون هلاك الاستئصال في ذلك الوقت',NULL,'594',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1870,'{وجاء أهل المدينة} مدينة قوم لوط وهي سذوم {يستبشرون} يفرحون طمعاً منهم في ركوب المعاصي والفاحشة حيث أُخبروا أنَّ في بيت لوطٍ مُرداً حساناً فقال لهم لوط:',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1871,'{إن هؤلاء ضيفي فلا تفضحون} عندهم بقصدكم إيَّاهم فيعلموا أنَّه ليس لي عندكم قدرٌ',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1872,'{واتقوا الله ولا تخزون} مذكورٌ في سورة هود',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1873,'{قَالُوا أَوَلَمْ نَنْهَكَ عن العالمين} عن ضيافتهم لأنَّا نريد منهم الفاحشة وكانوا يقصدون بفعلهم الغرباء',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1874,'{قال هؤلاء بناتي إن كنتم فاعلين} هذا الشَّأن يعني: اللَّذة وقضاء الوطر يقول: عليكم بتزويجهن أراد أن يقي أضيافه ببناته',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1875,'{لعمرك} بحياتك يا محمد {إنهم} إنَّ قومك {لفي سكرتهم يعمهون} في ضلالتهم يتمادون وقيل: يعني: قوم لوط',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1876,'{فأخذتهم الصيحة} صاح بهم جبريل عليه السَّلام صيحةً أهلكتهم {مشرقين} داخلين في وقت شروق الشَّمس وذلك أنَّ تمام الهلاك كان مع الإشراق وقوله:',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1877,'{فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِنْ سجيل}',NULL,'595',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1878,'{للمتوسمين} أَي: المُتفرِّسين المُتثبِّتين في النَّظر حتى يعرفوا حقيقة سمة الشَّيء',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1879,'{وإنها} يعني: مدينة قوم لوط {لبسبيل مقيم} على طريق قومك إلى الشَّام وهو طريقٌ لا يندرس ولا يخفى',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1880,'{إِنَّ فِي ذَلِكَ لآيَةً لِلْمُؤْمِنِينَ} لعبرةً للمصدِّقين يعني: إنَّ المؤمنين اعتبروا بها',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1881,'{وإن كان أصحاب الأيكة} قوم شعيب وكانوا أصحاب غياضٍ وأشجار',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1882,'{فانتقمنا منهم} بالعذاب أخذهم الحرُّ أيَّاماً ثمَّ اضطرم عليهم المكان ناراً فهلكوا {وإنَّهما} يعني: الأيكة ومدينة قوم لوطٍ {لبإمامٍ مبين} لبطريقٍ واضحٍ',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1883,'{ولقد كذَّب أصحاب الحجر} يعني: قوم ثمود والحِجر اسم واديهم {المرسلين} يعني: صالحاً وذلك أنَّ مَنْ كذَّب نبيَّاً فقد كذَّب جميع الرُّسل',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1884,'{وآتيناهم آياتنا} يعني: ما أظهر لهم من الآيات في النَّاقة',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1885,'{وكانوا ينحتون من الجبال بيوتاً} لطول عمرهم كان لا يبقى معهم السُّقوف فاتَّخذوا كهوفاً من الجبال بيوتاً {آمنين} من أن يقع عليهم',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1886,'{فأخذتهم الصيحة} صيحة العذاب {مصبحين} حين دخلوا في وقت الصُّبح',NULL,'596',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1887,'{فما أغنى عنهم} ما دفع العذاب {مَا كَانُوا يَكْسِبُونَ} من الأموال والأنعام',NULL,'597',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1888,'{وما خلقنا السماوات والأرض وما بينهما إلاَّ بالحق} أي: للثَّواب والعقاب أُثيب مَنْ آمن بي وصدَّق رسلي وأعاقب مَنْ كفر بي والموعد لذلك السَّاعة وهو قوله تعالى: {وإنَّ الساعة لآتية} أَيْ: إنَّ القيامة تأتي فيجازى المشركون بقبيح أعمالهم {فاصفح} عنهم {الصفح الجميل} أَيْ: أعرض إعراضاً بغير فحشٍ ولا جزعٍ',NULL,'597',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1889,'{إن ربك هو الخلاق العليم} بما خلق',NULL,'597',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1890,'{ولقد آتيناك سبعاً من المثاني} يعني: الفاتحة وهي سبع آيات وتثنى في كلِّ صلاةٍ امتنَّ الله على رسوله صلى الله عليه وسلم بهذه السُّورة كما امتنَّ عليه بجميع القرآن حين قال: {والقرآن العظيم} أي: العظيم القدر',NULL,'597',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1891,'{لا تَمُدَّنَّ عَيْنَيْكَ إِلَى مَا مَتَّعْنَا بِهِ} نُهي رسول الله صلى الله عليه وسلم عن الرَّغبة في الدُّنيا فخظر عليه أن يمدَّ عينيه إليها رغبةً فيها وقوله: {أزواجاً منهم} أَيْ: أصنافاً من الكفَّار كالمشركين واليهود وغيرهم يقول: لا تنظر إلى ما متَّعناهم به في الدُّنيا {ولا تحزن عليهم} إن لم يؤمنوا {واخفض جَناحَكَ للمؤمنين} ليِّن جانبك وارفق بهم',NULL,'597',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1892,'{وقل إني أنا النذير المبين} أنذركم عذاب الله سبحانه وأُبيِّن لكم ما يقرِّبكم إليه',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1893,'{كما أنزلنا} أَيْ: عذابنا {على المقتسمين} وهم الذين اقتسموا طرق مكة يصدُّون الناس عَنْ الإيمان بمحمد صلى الله عليه وسلم فأنزل الله تعالى بهم خزياً فماتوا شرَّ ميتةٍ',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1894,'{الذين جعلوا القرآن عضين} جزَّؤوه أجزاءً فقالوا: سحرٌ وقالوا: أساطير الأولين وقالوا: مفترى',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1895,'{فَوَرَبِّكَ لَنَسْأَلَنَّهُمْ أَجْمَعِينَ}',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1896,'{عما كانوا يعملون} أَيْ: يفترون من القول في القرآن يريد: لنسألنَّهم سؤال توبيخٍ وتقريعٍ',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1897,'{فاصدع بما تؤمر} يقول: أَظهرْ ما تؤمر واجهر بأمرك {وأعرض عن المشركين} لا تُبالِ بهم ولم يزل النبي صلى الله عليه وسلم مستخفياً حتى نزلت هذه الآية',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1898,'{إنا كفيناك المستهزئين} وكانوا خمسة نفرٍ: الوليد بن المغيرة والعاص بن وائل وعدي بن قيس والأسود بن المطلب والأسود بن عبد يغوث سلَّط الله سبحانه عليهم جبريل عليه السَّلام حتى قتل كلَّ واحدٍ منهم بآفةٍ وكفى نبيه عليه السَّلام شرَّهم',NULL,'598',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1899,'{الَّذِينَ يَجْعَلُونَ مَعَ اللَّهِ إِلهًا آخَرَ فَسَوْفَ يعلمون}',NULL,'599',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1900,'{وَلَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِمَا يَقُولُونَ}',NULL,'599',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1901,'{فسبح بحمد ربك} قل: سبحان الله وبحمده {وَكُنْ مِنَ السَّاجِدِينَ} المصلِّين',NULL,'599',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1902,'{واعبد ربك حتى يأتيك اليقين} أَي: الموت',NULL,'599',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1903,'{أَتَى أَمْرُ اللَّهِ} أَيْ: عذابه لمَنْ أقام على الشرك أي: قد فرب ذلك {فلا تستعجلوه} فإنَّه نازلٌ بكم لا محالة {سبحانه} براءةٌ له من السُّوء {وتعالى} ارتفع بصفاته {عما يشركون} عن إشراكهم',NULL,'600',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1904,'{ينزل الملائكة} يعني: جبريل عليه السَّلام وحده {بالروح} بالوحي {من أمره} والوَحْيُ من أمر الله سبحانه {عَلَى مَنْ يَشَاءُ مِنْ عِبَادِهِ} يريد: النَّبيِّين الذين يختصُّهم بالرِّسالة {أن أنذروا} بدلٌ من الرُّوح أَيْ: أعلموا أهل الكفر {أنه لا إله إلاَّ أنا} مع تخويفهم إنْ لم يقرُّوا {فاتقون} بالتَّوحيد والطَّاعة ثمَّ ذكر ما يدلُّ على توحيده فقال:',NULL,'600',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1905,'{خلق السماوات} الآية',NULL,'600',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1906,'{خَلَقَ الإِنْسَانَ مِنْ نُطْفَةٍ} يعني: أُبيَّ بن خلف {فَإِذَا هُوَ خَصِيمٌ} مخاصمٌ {مبين} ظاهرُ الخصومة وذلك أنَّه خاصم النبيَّ صلى الله علي وسلم في إنكاره البعث وقوله:',NULL,'600',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1907,'{لكم فيها دفء} يعني: ما تستدفئون به من الأكسية والأبنية من أشعارها وأصوافها وأوبارها {ومنافع} من النَّسل والدَّرِّ والرُّكوب',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1908,'{وَلَكُمْ فِيهَا جَمَالٌ} زينةٌ {حِينَ تُرِيحُونَ} تردُّونها إلى مَراحها بالعشايا {وحين تسرحون} تخرجونها إلى المرعى بالغداة',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1909,'{وتحمل أثقالكم} أمتعتكم {إلى بلد} لو تكلَّفتم بلوغه على غير الإِبل لشقَّ عليكم والشِّقِّ: المشقَّة {إنَّ ربكم لرؤوف رحيم} حيث منَّ عليكم بهذه المرافق وقوله:',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1910,'{ويخلق ما لا تعلمون} لم يُسمِّه فالله أعلم به',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1911,'{وعلى الله قصد السبيل} أَي: الإِسلام والطَّريق المستقيم يُؤدِّي إلى رضا الله تعالى كقوله: {هذا صراط مستقيم} {ومنها} ومن السَّبيل {جائر} عادلٌ مائل كاليهوديَّة والنَّصرانية {ولو شاء لهداكم} أرشدكم {أجمعين} حتى لا تختلفوا في الدِّين وقوله:',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1912,'{ومنه شجر} يعني: ما ينبت بالمطر وكلُّ ما ينبت على الأرض فهو شجر {فيه تسيمون} ترعون مواشيكم وقوله:',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1913,'{يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخِيلَ وَالأَعْنَابَ وَمِنْ كُلِّ الثَّمَرَاتِ إِنَّ فِي ذَلِكَ لآيَةً لقوم يتفكرون}',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1914,'{وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومُ مُسَخَّرَاتٌ بِأَمْرِهِ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يعقلون}',NULL,'601',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1915,'{وما ذرأ لكم} أَيْ: وسخَّر لكم ما خلق في الأرض {مختلفاً ألوانه} أَيْ: هيئته ومناظره يعني: الدَّوابَّ والأشجار وغيرهما',NULL,'602',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1916,'{وهو الذي سخر البحر} ذلَّله للرُّكوب والغوص {لتأكلوا منه لحماً طرياً} السَّمك والحيتان {وتستخرجوا منه حلية تلبسونها} الدُّرَّ والجواهرَ {وترى الفلك} السُّفن {مواخر فيه} شواقّ للماء تدفعه بِجُؤْجُئِها بصدرها {ولتبتغوا من فضله} لتركبوه للتِّجارة فتطلبوا الرِّبح من فضل الله',NULL,'602',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1917,'{وألقى في الأرض رواسي} جبالاً ثابتةً {أن تميد} لئلا تميد أَيْ: لا تتحرَّك {بكم وأنهاراً} وجعل فيها أنهاراً كالنِّيل والفرات ودجلة {وسبلاً} وطرقاً إلى كلِّ بلدةٍ {لعلكم تهتدون} إلى مقاصدكم من البلاد فلا تضلُّوا',NULL,'602',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1918,'{وعلامات} يعني: الجبال وهي علاماتُ الطُّرق بالنَّهار {وبالنجم} يعني: جميع النُّجوم {هم يهتدون} إلى الطُّرق والقِبلة في البرِّ والبحر',NULL,'602',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1919,'{أفمن يخلق} يعني: ما ذُكر في هذه السُّورة وهو الله تعالى {كمَنْ لا يخلق} يعني: الأوثان يقول: أَهما سواءٌ حتى يسوَّى بينهما في العبادة؟ {أفلا تذكرون} أفلا تتعظون كما اتَّعظ المؤمنون',NULL,'602',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1920,'{وإن تعدوا نعمة الله لا تحصوها} مرَّ تفسيره {إنَّ الله لغفور} لتقصيركم في شكر نعمه {رحيم} بكم حيث لم يقطعها عنكم بتقصيركم وقوله:',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1921,'{وَاللَّهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ}',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1922,'{وَالَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ لا يَخْلُقُونَ شَيْئًا وهم يخلقون}',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1923,'{أموات} أَيْ: هي أمواتٌ لا روح فيها يعني: الأصنام {غير أحياء} تأكيد {وما يشعرون أيان يبعثون} وذلك أنَّ الله سبحانه يبعث الأصنام لها أرواحٌ فيتبرَّؤون من عابديهم وهي في الدُّنيا جماد لا تعلم متى تُبعث وقوله:',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1924,'{إلهكم} ذكر الله سبحانه دلائل وحدانيته ثمَّ أخبر أنَّه واحد ثمَّ أتبع هذا إنكار الكفَّار وحدانيَّته بقوله: {فالذين لا يؤمنون بالآخرة قلوبهم منكرة} جاحدةٌ غير عارفة {وهم مستكبرون} ممتنعون عن قبول الحقِّ',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1925,'{لا جرم} حقا {أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ} الآية أَيْ: يُجازيهم بذلك {إنه لا يحب المستكبرين} لا يمدحهم ولا يُثيبهم',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1926,'{وَإِذَا قِيلَ لَهُمْ مَاذَا أَنْزَلَ رَبُّكُمْ قَالُوا أساطير الأولين} الآية نزلت في النَّضر بن الحارث وذكرنا قصَّته',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1927,'{ليحملوا أوزارهم} هذه لام العاقبة لأنَّ قولهم للقرآن: أساطير الأولين أدَّاهم إلى أن حملوا أوزارهم كاملة لم يُكفَّر منها شيء بنكبةٍ أصابتهم في الدُّنيا لكفرهم {ومن أوزار الذين يضلونهم} لأنَّهم كانوا دعاةَ الضَّلالة فعليهم مثل أوزار من اتَّبعهم وقوله: {بغير علم} أَيْ: يضلُّونهم جهلاً منهم بما كانوا يكسبون من الإثم ثم صنيعهم فقال: {ألا ساء ما يزرون} أَيْ: يحملون',NULL,'603',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1928,'{قد مكر الذين من قبلهم} وهو نمروذ بنى صرحاً طويلاً ليصعد منه إلى السَّماء فيقاتل أهلها {فأتى الله} فأتى أمر الله وهو الرِّيح وخَلْقُ الزَّلزلة {بنيانهم} بناءهم {من القواعد} من أساطين البناء التي يعمده وذلك أنَّ الزَّلزلة خُلقت فيها حتى تحرَّكت بالبناء فهدمته وهو قوله: {فخرَّ عليهم السقف من فوقهم} يعني: وهم تحته {وَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لا يَشْعُرُونَ} من حيث ظنُّوا أنَّهم في أمانٍ منه',NULL,'604',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1929,'{ثم يوم القيامة يخزيهم} يُذلُّهم {ويقول أين شركائي} أَي: الذين في دعواكم أنَّهم شركائي أين هم ليدفعوا العذاب عنكم {الذين كنتم تشاقون} تخالفون المؤمنين {فيهم قال الذين أوتوا العلم} وهم المؤمنون يقولون حين يرون خزي الكفَّار في القيامة: {إنَّ الخزي اليوم والسوء} عليهم لا علينا',NULL,'604',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1930,'{الذين تتوفاهم الملائكة} مرَّ تفسيره في سورة النِّساء وقوله: {فألقوا السلم} أَي: انقادوا واستسلموا عند الموت وقالوا: {ما كنا نعمل من سوء} شرك {بَلَى إِنَّ اللَّهَ عَلِيمٌ بِمَا كُنْتُمْ تَعْمَلُونَ} من الشرك والتكذيب ثم قيل لهم:',NULL,'604',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1931,'{فادخلوا أبواب جهنم} الآية وقوله: {فلبئس مثوى} مقام {المتكبرين} عن التَّوحيد وعبادة الله سبحانه',NULL,'605',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1932,'{وقيل للذين اتقوا ماذا أنزل ربكم} هذا كان في أيَّام الموسم يأتي الرَّجل مكَّة فيسأل المشركين عمَّا أنزل على محمد صلى الله عليه وسلم؟ فيقولون: أساطير الأولين ويسأل المؤمنين عن ذلك فيقولون: {خيراً} أَيْ: ثواباً لمَنْ آمن بالله ثمَّ فسَّر ذلك الخير فقال: {لِلَّذِينَ أَحْسَنُوا في هذه الدنيا حسنة} قالوا: لا إله إلاَّ الله ثوابٌ مضاعف {ولدار الآخرة} وهي الجنَّة {خير} من الدُّنيا وما فيها',NULL,'605',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1933,'{جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ لهم فيها ما يشاؤون كَذَلِكَ يَجْزِي اللَّهُ الْمُتَّقِينَ}',NULL,'605',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1934,'{الذين تتوفاهم الملائكة طيبين} طاهرين من الشِّرك',NULL,'605',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1935,'{هل ينظرون إلاَّ أن تأتيهم الملائكة} لقبض أرواحهم {أو يأتي أمر ربك} بالقتل والمعنى: هل يكون مدَّة إقامتهم على الكفر إلاَّ مقدار حياتهم إلى أن يموتوا أو يُقتلوا {كذلك فعل الذين من قبلهم} وهو التَّكذيب يعني: كفَّار الأمم الخالية {وَمَا ظَلَمَهُمُ اللَّهُ} بتعذيبهم {وَلكِنْ كَانُوا أَنْفُسَهُمْ يظلمون} بإقامتهم على الشِّرك',NULL,'605',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1936,'{فأصابهم} هذا مؤخَّر في اللَّفظ ومعناه التَّقديم لأنَّ التَّقدير: كذلك فعل الذين من قبلهم فأصابهم الآية ثمَّ يقول: {وما ظلمهم الله} الآية ومعنى: أصابهم {سيئات ما عملوا} أَيْ: جزاؤها {وحاق} أحاط {بهم مَا كَانُوا بِهِ يَسْتَهْزِئُونَ} من العذاب',NULL,'605',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1937,'{وقال الذين أشركوا} يعني: أهل مكَّة: {لَوْ شَاءَ اللَّهُ مَا عبدنا من دونه من شيء} أَيْ: ما أشركنا ولكنَّه شاءه لنا {وَلا حَرَّمْنَا من دونه من شيء} أَيْ: من السَّائبة والبحيرة وإنَّما قالوا هذا استهزاءً قال الله تعالى: {كذلك فعل الذين من قبلهم} أي: من تكذيب الرُّسل وتحريم ما أحلَّ الله {فَهَلْ عَلَى الرُّسُلِ إِلا الْبَلاغُ الْمُبِينُ} أَيْ: ليس عليهم إلاَّ التَّبليغ وقد بلَّغتَ يا محمَّدُ وبلَّغوا فأمَّا الهداية فهي إلى الله سبحانه وتعالى وقد حقَّق هذا فيما بعد وهو قوله:',NULL,'606',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1938,'{ولقد بعثنا في كلِّ أمة رسولاً} كما بعثناك في هؤلاء {أَنِ اعْبُدُوا اللَّهَ} بأن اعبدوا الله {واجتنبوا الطاغوت} الشيطان وكلَّ من يدعو إلى الضلاَّلة {فمنهم مَنْ هدى الله} أرشده {ومنهم مَنْ حقَّت} وجبت {عليه الضلالة} الكفر بالقضاء السابق {فسيروا في الأرض} معتبرين بآثار الأمم المكذِّبة ثمَّ أكَّد أنَّ مَنْ حقَّت عليه الضَّلالة لا يهتدي وهوقوله:',NULL,'606',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1939,'{إن تحرص على هداهم} أَيْ: تطلبها بجهدك {فإنَّ الله لا يهدي مَنْ يضل} كقوله: {مَنْ يُضْلِلِ اللَّهُ فَلا هَادِيَ لَهُ}',NULL,'606',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1940,'{وأقسموا بالله جهد أيمانهم} أغلظوا في الأيمان تكذيباً منهم بقدرة الله على البعث فقال الله تعالى: {بلى} ليبعثنَّهم {وعداً عليه حقاً}',NULL,'606',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1941,'{ليبيِّن لهم} بالبعث ما اختلفوا فيه من أمره وهو أنَّهم ذهبوا إلى خلاف ما ذهب إليه المؤمنون {وَلِيَعْلَمَ الذين كفروا أنهم كانوا كاذبين} ثمَّ أعلمهم سهولة خلق الأشياء عليه بقوله:',NULL,'607',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1942,'{إنما قولنا لشيء} الآية',NULL,'607',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1943,'{والذين هاجروا} نزلت في قومٍ عذَّبهم المشركون بمكَّة إلى أن هاجروا وقوله: {في الله} في رضا الله {لنبوئنهم في الدنيا حسنة} داراً وبلدةً حسنةً وهي المدينة {ولأجر الآخرة} يعني: الجنَّة',NULL,'607',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1944,'{الذين صبروا} على أذى المشركين وهم في ذلك واثقون بالله تعالى مُتوكِّلون عليه',NULL,'607',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1945,'{وما أرسلنا من قبلك} ذكرنا تفسيره في آخر سورة يوسف وقوله: {فاسألوا أهل الذكر} يعني: أهل التَّوراة فيخبرونكم أنَّ الأنبياء كلَّهم كانوا بشراً',NULL,'607',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1946,'{بالبينات} أَيْ: أرسلناهم بالبيِّنات بالحجج الواضحة {والزبر} الكتب {وأنزلنا إليك الذكر} القرآن {لتبين للناس ما نزل إليهم} في هذا الكتاب من الحلال والحرام والوعد والوعيد {ولعلهم يتفكرون} في ذلك فيعتبرون',NULL,'607',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1947,'{أفأمن الذين مكروا السيئات} عملوا بالفساد يعني: عبادة الأوثان وهم مشركو مكَّة {أن يخسف الله بهم الأرض} كما خسف بقارون {أَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لا يَشْعُرُونَ} أَيْ: من حيث يأمنون فكان كذلك لأنَّهم أُهلكوا يوم بدر وما كانوا يُقدِّرون ذلك',NULL,'608',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1948,'{أو يأخذهم في تقلبهم} للسَّفر والتِّجارة {فما هم بمعجزين} بممتنعين على الله',NULL,'608',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1949,'{أو يأخذهم على تخوّف} على تنقض وهو أن يأخذ الأوَّل حتى يأتي الأخذ على الجميع {فإنَّ ربكم لرؤوف رحيم} إذ لم يعجل عليهم بالعقوبة',NULL,'608',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1950,'{أو لم يَرَوْا إِلَى مَا خَلَقَ اللَّهُ مِنْ شَيْءٍ} له ظلٌّ من جبلٍ وشجرٍ وبناءٍ {يتفيَّأ} يتميَّل {ظلاله عن اليمين والشمائل} في أوَّل النَّهار عن اليمين وفي آخره عن الشِّمال إذا كنت مُتوجِّهاً إلى القبلة {سجداً لله} قال المُفسِّرون: ميلانها سجودها وهذا كقوله: {وظلالهم بالغدو والآصال} وقد مرَّ {وهم داخرون} صاغرون يفعلون ما يُراد منهم يعني: هذه الأشياء التي ذكرها أنَّها تسجد لله',NULL,'608',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1951,'{ولله يسجد} أَيْ: يخضع وينقاد بالتَّسخير {ما في السماوات وما في الأرض من دابة} يريد: كلَّ ما دبَّ على الأرض {والملائكة} خصَّهم بالذِّكر تفضيلاً {وهم لا يستكبرون} عن عبادة الله تعالى يعني: الملائكة',NULL,'608',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1952,'{يخافون ربهم من فوقهم} يعني: الملائكة هم فوق ما في الأرض من دابَّة ومع ذلك يخافون الله فلأَنْ يخافَ مَنْ دونهم أولى {ويفعلون ما يؤمرون} يعني: الملائكة وقوله:',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1953,'{وَقَالَ اللَّهُ لا تَتَّخِذُوا إِلهَيْنِ اثْنَيْنِ إِنَّمَا هو إله واحدٌ فإياي فارهبون}',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1954,'{وله الدين واصباً} دائماً أَيْ: طاعته واجبةٌ أبداً {أفغير الله} الذي خلق كلَّ شيء وأمر أن لا تتَّخذوا معه إلهاً {تتقون}',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1955,'{وما بكم من نعمة} من صحَّة جسمٍ أو سعة رزق أو متاع بمالٍ وولدٍ فكلُّ ذلك من الله {ثمَّ إذا مسكم الضرُّ} الأسقام والحاجة {فإليه تجأرون} ترتفعون أصواتكم بالاستغاثة',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1956,'{ثمَّ إذا كشف الضر عنكم} يعني: مَنْ كفر بالله وأئرك بعد كشف الضُّرَّ عنه',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1957,'{ليكفروا بما آتيناهم} ليجحدوا نعمة الله فيما فعل بهم {فتمتعوا} أمر تهديد {فسوف تعلمون} عاقبة أمركم',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1958,'{ويجعلون} يعني: المشركين {لما لا يعلمون} أَي: الأوثان التي لا علم لها {نصيباً مما رزقناهم} يعني: ما ذُكر في قوله: {وهذا لشركائنا} {تالله لتسألنَّ} سؤال توبيخٍ {عمَّا كنتم تفترون} على الله من أنَّه أمركم بذلك',NULL,'609',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1959,'{ويجعلون لله البنات} يعني: خزاعة وكنانة زعموا أنَّ الملائكة بنات الله ثم نزه نفشه فقال تعالى: {سبحانه} تنزيهاً له عمَّا زعموا {وَلَهُمْ مَا يشتهون} يعني: البنين وهذا كقولهم: {أم له البنات} الآية',NULL,'610',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1960,'{وإذا بشر أحدهم بالأنثى} أُخبر بولادة ابنةٍ {ظلَّ} صار {وجهه مسودّاً} متغيِّراً تغيُّرَ مغتمٍّ {وهو كظيم} ممتلئ غمّاً',NULL,'610',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1961,'{يتوارى} يختفي ويتغيب مقدّراً مع نفسه {أيمسكه على هون} أيستحييها على هوانٍ منه لها {أم يدسُّه} يخفيه {في التراب} فعل الجاهليَّة من الوأد {ألا ساء} بئس {ما يحكمون} أَيْ: يجعلون لمن يعترفون بأنَّه خالقهم البناتِ اللاتي محلهنَّ منهم هذا المحل ونسبوه إلى اتِّخاذ الأولاد وجعلوا لأنفسهم البنين',NULL,'610',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1962,'{للذين لا يؤمنون بالآخرة مثل السوء} العذاب والنَّار {ولله المثل الأعلى} الإِخلاص والتَّوحيد وهو شهادة أن لا إله إلا الله',NULL,'610',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1963,'{ولو يؤاخذ الله الناس} المشركين {بظلمهم} بافترائهم على الله تعالى {ما ترك عليها من دابة} يعني: أحداً من المشركين {ولكن يؤخرهم إلى أجل مسمى} وهو انقضاء عمرهم',NULL,'610',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1964,'{ويجعلون لله ما يكرهون} لأنفسهم وذلك هو البنات أَيْ: يحكمون له به {وتصف ألسنتهم الكذب} ثمَّ فسَّر ذلك الكذب بقوله: {أنَّ لهم الحسنى} أَي: الجنَّة والمعنى: يصفون أنَّ لهم مع قبح قولهم الجنَّة إن كان البعث حقّاً فقال الله تعالى: {لا} أَيْ: ليس الأمر كما وصفوه {جرم} كسب قولهم هذا {أنَّ لهم النار وأنَّهم مُفرْطون} متروكون فيها وقيل: مُقدَّمون إليها وقوله:',NULL,'611',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1965,'{فهو وليُّهم اليوم} يعني: يوم القيامة وأُطلق اسم اليوم عليه لشهرته وقوله:',NULL,'611',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1966,'{لتبين لهم الذي اختلفوا فيه} أَيْ: تُبيِّن للمشركين ما ذهبوا فيه إلى خلاف ما يذهب إليه المسلمون فتقوم الحجَّة عليهم ببيانك وقوله: {وهدى} أَيْ: والهداية والرَّحمة للمؤمنين وقوله:',NULL,'611',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1967,'{والله أنزل} ظاهرٌ إلى قوله: {يسمعون} أَيْ: سماع اعتبار يريد: إنَّ في ذلك دلالة على البعث',NULL,'611',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1968,'{وإنَّ لكم في الأنعام لعبرة} لدلالةً على قدرة الله تعالى ووحدانيَّته {نُسْقِيكُمْ مِمَّا فِي بُطُونِهِ مِنْ بَيْنِ فَرْثٍ} وهو سرجين الكرش {ودمٍ لبناً خالصاً سائغاً للشاربين} جائزاً في حلوقهم',NULL,'611',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1969,'{ومن ثمرات} أَيْ: ولكم منها ما {تتخذون منه سكراً} وهو الخمر نزل هذا قبل تحريم الخمر {ورزقاً حسناً} وهو الخلُّ والزَّبيب والتَّمرُ {إنَّ في ذلك لآية لقومٍ يعقلون} يريد: عقلوا عن الله تعالى ما فيه قدرته',NULL,'611',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1970,'{وأوحى ربك إلى النحل} ألهمها وقذف في أنفسها {أَنِ اتَّخِذِي مِنَ الْجِبَالِ بُيُوتًا وَمِنَ الشَّجَرِ} هي تتَّخذ لأنفسها بيوتاً إذا كانت لا أصحاب لها فإذا كانت لها أرباب اتِّخذت بيوتها ممَّا تبني لها أربابها وهو قوله: {ومما يعرشون} أَيْ: يبنون ويسقفون لها من الخلايا',NULL,'612',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1971,'{ثُمَّ كُلِي مِنْ كُلِّ الثَّمَرَاتِ فَاسْلُكِي سُبُلَ ربك} طرق ربِّك تطلب فيها الرَّعي {ذللاً} منقادة مُسخَّرة مطيعة {يخرج من بطونها شراب} وهو العسل {مختلف ألوانه} منه أحمر وأبيض وأصفر {فيه} في ذلك الشَّراب {شفاء للناس} من الأوجاع التي شفاؤها فيه',NULL,'612',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1972,'{والله خلقكم} ولم تكونوا شيئاً {ثمَّ يتوفاكم} عند انقضاء آجالكم {وَمِنْكُمْ مَنْ يُرَدُّ إِلَى أَرْذَلِ العمر} وهو أردؤه يعني: الهرم {لكي لا يعلم بعد علم شيئاً} يصير كالصبيِّ الذي لا عقل له قالوا: وهذا لا يكون للمؤمنين لأنَّ المؤمن لا ينزع عنه علمه وإن كبر {إنّ الله عليم} بما يصنع {قدير} على ما يريد',NULL,'612',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1973,'{وَاللَّهُ فَضَّلَ بَعْضَكُمْ عَلَى بَعْضٍ فِي الرِّزْقِ} حيث جعل بعضكم يملك العبيد وبعضكم مملوكاً {فما الذين فضلوا} وهم المالكون {برادي رزقهم} بجاعلي رزقهم لعبيدهم حتى يكونوا عبيدهم معهم {فيه سواء} وهذا مَثَلٌ ضربه الله تعالى للمشركين في تصييرهم عباد الله شركاء له فقال: إذا لم يكن عبيدكم معكم سواء في الملك فكيف تجعلون عبيدي معي سواء؟ {أفبنعمة الله يجحدون} حيث يتَّخذون معه شركاء',NULL,'612',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1974,'{والله جعل لكم من أنفسكم أزواجاً} يعني: النِّساء {وجعل لكم من أزواجكم بنين وحفدة} يعني: ولد الولد {ورزقكم من الطيبات} من أنواع الثِّمار والحبوب والحيوان {أَفَبِالْبَاطِلِ يُؤْمِنُونَ} يعني: الأصنام {وبنعمة الله هم يكفرون} يعني: التَّوحيد',NULL,'613',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1975,'{وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَمْلِكُ لهم رزقا من السماوات} يعني: الغيث الذي يأتي من جهتها {والأرض} يعني: النَّبات والثِّمار {شيئاً} أَيْ: قليلاً ولا كثيراً {ولا يستطيعون} لا يقدرون على شيء',NULL,'613',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1976,'{فلا تضربوا لله الأمثال} لا تشبِّهوه بخلقه وذلك أنَّ ضرب المثل إنَّما هو تشبيه ذاتٍ بذاتٍ أو وصفٍ بوصفٍ والله تعالى منزَّه عن ذلك {أن الله يعلم} ما يكون قبل أن يكون {وأنتم لا تعلمون} قدر عظمته حيث أشركتم به',NULL,'613',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1977,'{ضرب الله مثلاً} بيَّن شبهاً فيه بيانٌ للمقصود ثمَّ ذكر ذلك فقال: {عبداً مملوكاً لا يقدر على شيء} لأنه عاجز مملوكٌ لا يملك شيئاً وهذا مثل ضربه الله لنفسه ولمَنْ عُبِدَ دونه يقول: العاجز الذي لا يقدر أن ينفق والمالك المقتدر على الإِنفاق لا يستويان فكيف يُسوَّى بين الحجارة التي لا تتحرَّك وبين الله الذي هو على كل شيء قدير وهو رازقُ جميع خلقه ثمَّ بيَّن أنَّه المستحقُّ للحمد دون ما يعبدون من دونه فقال: {الحمد لله} لأنَّه المنعم {بل أكثرهم لا يعلمون} يقول: هؤلاء المشركون لا يعلمون أنَّ الحمد لي لأنَّ جميع النِّعم مني والمراد بالأكثر ها هنا الجميع ثمَّ ضرب مثلاً للمؤمن والكافر فقال:',NULL,'613',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1978,'{وَضَرَبَ اللَّهُ مَثَلا رَجُلَيْنِ أَحَدُهُمَا أَبْكَمُ لا يَقْدِرُ عَلَى شَيْءٍ} من الكلام لأنَّه لا يَفْهم ولا يُفهم عنه {وهو كَلٌّ} ثِقْلٌ ووبالٌ {على مولاه} صاحبه وقريبه {أينما يوجهه} يرسله {لا يَأْتِ بِخَيْرٍ} لأنَّه عاجزٌ لا يَفهم ما يقال له ولا يُفهم عنه {هَلْ يستوي هو} أَيْ: هذا الأبكم {ومَنْ يأمر بالعدل} وهو المؤمن يأمر بتوجيد الله سبحانه {وهو على صراط مستقيم} دينٍ مستقيمٍ يعني: بالأبكم أُبيَّ بن خلف وكان كلاًّ على قومه لأنَّه كان يؤذيهم ومَن يأمر بالعدل حمزة بن عبد المطلب',NULL,'614',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1979,'{ولله غيب السماوات} أَيْ: علم ما غاب فيهما عن العباد {وما أمر الساعة} يعني: القيامة {إلاَّ كلمح البصر} كالنَّظر بسرعةٍ {أو هو أقرب} من ذلك إذا أردناه يريد: إنه يأتي بها في أسرع من لمح البصر إذا أراده',NULL,'614',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1980,'{وَاللَّهُ أَخْرَجَكُمْ مِنْ بُطُونِ أُمَّهَاتِكُمْ لا تَعْلَمُونَ شيئاً} أَيْ: غير عالمين {وجعل لكم السَّمْعَ والأبصار} أَيْ: خلق لكم الحواسَّ التي بها يعلمون ويقفون على ما يجهلون',NULL,'614',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1981,'{أَلَمْ يَرَوْا إِلَى الطَّيْرِ مُسَخَّرَاتٍ} مذلَّلاتٍ {فِي جوِّ السماء} يعني: الهواء وذلك يدلُّ على مُسخِّرٍ سخَّرها ومدبِّرٍ مكَّنها من التَّصرُّف {ما يُمسكهنَّ إلاَّ الله} في حال القبض والبسط والاصطفاف',NULL,'614',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1982,'{وَاللَّهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَنًا} موضعاً تسكنون فيه ويستر عوراتكم وحرمكم وذلك أنَّه خلق الخشب والمدر والآلة التي يمكن بها تسقيف البيوت {وَجَعَلَ لَكُمْ مِنْ جُلُودِ الأَنْعَامِ} يعني: الأنطاع والأدم {بيوتاً} وهي القباب والخيام {تَسْتَخِفُّونَهَا يَوْمَ ظَعْنِكُمْ} يخفُّ عليكم حملها في أسفاركم {ويوم إقامتكم} لا يثقل عليكم في الحالتي {ومن أصوافها} يعني: الضَّأن {وأوبارها} يعني: الإِبل {وأشعارها} وهي المعز {أثاثاً} طنافس وأكسية وبُسطاً {ومتاعاً} تتمتَّعون به {إلى حين} البلى',NULL,'615',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1983,'{والله جعل لكم مما خلق} من البيوت والشَّجر والغمام {ظلالاً وَجَعَلَ لَكُمْ مِنَ الْجِبَالِ أَكْنَانًا} يعني: الغِيران والأسراب {وجعل لكم سرابيل} قمصاً {تقيكم الحر} تمنعكم الحرَّ والبرد فترك ذكر البرد لأنَّ ما وقى الحرَّ وقى البرد فهو معلوم {وسرابيل} يعني: دروع الحديد {تقيكم} تمنعكم {بأسكم} شدَّة الطَّعْن والضَّرب والرَّمي {كَذَلِكَ} مثل ما خلق هذه الأشياء لكم {يتمُّ نعمته عليكم} يريد: نعمة الدُّنيا والخطاب لأهل مكَّة {لعلَّكم تسلمون} تنقادون لربوبيته فتوحِّدونه',NULL,'615',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1984,'{فإن تولوا} أعرضوا عن الإِيمان بعد البيان {فإنما عليك البلاغ المبين} وليس عليك من كفرهم وجحودهم شيء',NULL,'615',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1985,'{يعرفون نعمة الله ثمَّ ينكرونها} يعني: الكفَّار يُقرُّون بأنَّها كلَّها من الله تعالى ثمَّ يقولون بشفاعة آلهتنا فذلك إنكارهم {وأكثرهم} جميعهم {الكافرون}',NULL,'616',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1986,'{ويوم} أَيْ: وأنذرهم يوم {نبعث} وهو يوم القيامة {من كلِّ أمة شهيداً} يعني: الأنبياء عليهم السَّلام يشهدون على الأمم بما فعلوا {ثم لا يؤذن للذين كفروا} في الكلام والاعتذار {ولا هم يستعتبون} ولا يُطلب منهم أن يرجعوا إلى ما يرضي الله تعالى',NULL,'616',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1987,'{وإذا رأى الذين ظلموا} أشركوا {العذاب} النَّار {فلا يخفف عنهم} العذاب {ولا هم ينظرون} يمهلون',NULL,'616',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1988,'{وإذا رأى الذين أشركوا شركاءهم} أوثانهم التي عبدوها من دون الله {قَالُوا رَبَّنَا هَؤُلاءِ شُرَكَاؤُنَا} وذلك أنَّ الله يبعثها حتى تُوردهم النَّار فإذا رأوها عرفوها فقالوا: {ربنا هؤلاء شركاؤنا الذين كنا ندعوا من دونك فألقوا إليهم القول} أَيْ: أجابوهم فقالوا لهم: {إنكم لكاذبون} وذلك أنَّها كانت جماداً ما تعرف عبادة عابديها فيظهر عند ذلك فضيحتهم حيث عبدوا مَن لم يشعر بالعبادة وهذا كقوله تعالى: {سيكفرون بعبادتهم}',NULL,'616',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1989,'{وألقوا إلى الله يومئذ السلم} استسلموا لحكم الله تعالى {وضلَّ عنهم ما كانوا يفترون} بطل ما كانوا يأملون من أنَّ آلهتهم تشفع لهم',NULL,'616',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1990,'{الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ زِدْنَاهُمْ عذابا فوق العذاب بما كانوا يفسدون}',NULL,'617',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1991,'{ويوم نبعث في كلِّ أمَّة شهيداً} وهو يوم القيامة يبعث الله فِي كُلِّ أُمَّةٍ شَهِيدًا {عَلَيْهِمْ مِنْ أَنْفُسِهِمْ} وهو نبيُّهم لأنَّ كلَّ نبيٍّ بُعث من قومه {وجئنا بك شهيداً على هؤلاء} على قومك وتمَّ الكلام ها هنا ثمَّ قال: {ونزلنا عليك الكتاب تبياناً} بياناً {لكلِّ شيء} ممَّا أُمر به ونهي عنه',NULL,'617',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1992,'{إنَّ الله يأمر بالعدل} شهادة أن لا إله إلاَّ الله {والإِحسان} وأداء الفرائض وقيل: بالعدل في الأفعال والإِحسان في الأقوال {وإيتاء ذي القربى} صلة الرَّحم فتؤتي ذا قرابتك من فضل ما رزقك الله {وينهى عن الفحشاء} الزِّنا {والمنكر} الشِّرك {والبغي} الاستطالة على النَّاس بالظُّلم {يعظكم} ينهاكم عن هذا كلِّه ويأمركم بما أمركم به في هذه الآية {لعلكم تذكرون} لكي تتَّعظوا',NULL,'617',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1993,'{وأوفوا بعهد الله إذا عاهدتم} يعني: كلَّ عهدٍ يحسن في الشريعة الوفاء به {ولا تنقضوا الأيمان بعد توكيدها} لا تحنثوا فيها بعد ما وكَّدتموه بالعزم {وقد جعلتم الله عليكم كفيلاً} بالوفاء حيث حلفتم والواو للحال',NULL,'617',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1994,'{ولا تكونوا كالتي نقضت} أفسدت {غزلها} وهي امرأة حمقاء كانت تغزل طول يومها ثمَّ تنقضه وتفسده {من بعد قوة} الغزل بإمراره وفتله {أنكاثاً} قطعاً وتم الكلام هاهنا ثمَّ قال: {تتخذون أيمانكم دخلاً بينكم} أَيْ: غشَّاً وخديعةً {أن تكون} بأن تكون أو لأن تَكُونَ {أُمَّةٌ هِيَ أَرْبَى مِنْ أُمَّةٍ} أَيْ: قوم أغنى وأعلى من قوم وذلك أنهم كانوا يحالفون قوماً فيجدون أكثر منهم وأعزَّّ فينقضون حلف أولئك ويحالفون هؤلاء الذين هم أعزُّ فنُهوا عن ذلك {إنما يبلوكم الله به} أَيْ: بما أمر ونهى {وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيَامَةِ مَا كُنْتُمْ فِيهِ تختلفون} في الدنيا عن أيمان الخديعة فقال:',NULL,'617',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1995,'{وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَكِنْ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ وَلَتُسْأَلُنَّ عما كنتم تعملون}',NULL,'618',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1996,'{وَلا تَتَّخِذُوا أَيْمَانَكُمْ دَخَلا بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بعد ثبوتها} تزلّ عن الإِيمان بعد المعرفة بالله تعالى وهذا إنَّما يستحقُّ في نقض معاهدة رسول الله صلى الله عليه وسلم على نصرة الدِّين {وتذوقوا السوء} العذاب {بما صَدَدتُم عن سبيل الله} وذلك أنَّهم إذا نقضوا العهد لم يدخل غيرهم في الإِسلام فيصير كأنهم صدُّوا عن سبيل الله وعن دين الله',NULL,'618',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1997,'{ولا تشتروا بعهد الله ثمناً قليلاً} لا تنقضوا عهودكم تطلبون بنقضها عرضاً من الدنيا {إنما عند الله} أَيْ: ما عند الله من الثَّواب على الوفاء {هو خير لكم إن كنتم تعلمون} ذلك',NULL,'618',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1998,'{ما عندكم ينفد} يفنى وينقطع يعني: في الدُّنيا {وَمَا عِنْدَ اللَّهِ} من الثَّواب والكرامة {باق} دائمٌ لا ينقطع {وَلَنَجْزِيَنَّ الَّذِينَ صَبَرُوا} على دينهم وعمَّا نهاهم الله تعالى {أجرهم بأحسن ما كانوا يعملون} يعني: الطَّاعات وقوله:',NULL,'619',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (1999,'{فلنحيينه حياة طيبة} قيل هي القناعة وقيل: هي حياة الجنَّة',NULL,'619',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2000,'{فإذا قرأت القرآن} أَيْ: إذا أردت أن تقرأ القرآن {فاستعذ بالله} فاسأل الله أن يعيذك ويمنعك {مِنَ الشَّيْطَانِ الرَّجِيمِ}',NULL,'619',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2001,'{إِنَّهُ لَيْسَ لَهُ سُلْطَانٌ عَلَى الَّذِينَ آمَنُوا} أَيْ: حجَّةٌ في إغوائهم ودعائهم إلى الضَّلالة والمعنى: ليس له عليهم سلطان الإِغواء',NULL,'619',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2002,'{إنما سلطانه على الذين يتولونه} يُطيعونه {والذين هم به} بسببه وطاعته فيما يدعوهم إليه {مشركون} بالله',NULL,'619',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2003,'{وإذا بدلنا آية} أَيْ: رفعناها وأنزلنا غيرها لنوعٍ من المصلحة {والله أعلم} بمصالح العباد في {بما ينزَّل} من النَّاسخ والمنسوخ {قالوا} يعني: الكفَّار {إنما أنت مفترٍ} كذَّابٌ تقوله من عندك {بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ} حقيقةَ القرآن وفائدةَ النَّسخ والتَّبديل',NULL,'619',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2004,'{قل نزله روح القدس} جبريل عليه السَّلام {من ربك} من كلام ربِّك {بالحق} بالأمر الحقِّ {لِيُثَبِّتَ الَّذِينَ آمَنُوا} بما فيه من الحجج والآيات {وهدىً} وهو هدىً',NULL,'620',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2005,'{ولقد نعلم أنَّهم يقولون إنما يُعلِّمه} القرآنَ {بشرٌ} يعنون عبداً لبني الحضرمي كان يقرأ الكتب {لسان الذي يلحدون إليه} لغةُ الذي يميلون القول إليه ويزعمون أنَّه يُعلِّمك {أعجميّ} لا يُفصح ولا يتكلَّم بالعربية {وهذا} يعني القرآن {لسان} لغى {عربيّ مبين} أفصح ما يكون من العربيَّة وأبينه ثم أخير أن الكاذبين هم فقال:',NULL,'620',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2006,'{إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِآيَاتِ اللَّهِ لا يَهْدِيهِمُ اللَّهُ وَلَهُمْ عَذَابٌ أَلِيمٌ}',NULL,'620',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2007,'{إِنَّمَا يَفْتَرِي الْكَذِبَ الَّذِينَ لا يُؤْمِنُونَ بِآيَاتِ الله} لأنَّهم يقولون لما لا يقدر عليه إلاَّ الله هذا من قول البشر ثمَّ سمَّاهم كاذبين بقوله: {وأولئك هم الكاذبون}',NULL,'620',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2008,'{مَنْ كفر بالله من بعد إيمانه} هذا ابتداء كلام وخبره في قوله: {فعليهم غضب من الله} ثمَّ استثنى المُكره على الكفر فقال: {إلاَّ مَنْ أكره} أَيْ: على التَّلفظ بكلمة الكفر {وَقَلْبُهُ مُطْمَئِنٌّ بِالإِيمَانِ وَلَكِنْ مَنْ شَرَحَ بِالْكُفْرِ صدراً} أَيْ: فتحه ووسَّعه لقبوله',NULL,'620',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2009,'{ذلك} الكفر {بأنهم استحبوا الحياة الدنيا} اختاروها {على الآخرة وأنَّ الله} لا يهديهم ولا يريد هدايتهم ثمَّ وصفهم بأنَّهم مطبوعٌ على قلوبهم وسمعهم وأبصارهم وأنَّهم غافلون عمَّا يُراد بهم ثمَّ حكم عليهم بالخسار وأكد ذلك بقوله:',NULL,'620',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2010,'{أُولَئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلَى قُلُوبِهِمْ وَسَمْعِهِمْ وأبصارهم وأولئك هم الغافلون}',NULL,'621',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2011,'{لا جرم} أَيْ: حقَّاً {أنهم في الآخرة هم الخاسرون} المغبونون',NULL,'621',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2012,'{ثم إن ربك للذين هاجروا} يعني: المُستضعفين الذين كانوا بمكَّة {من بعد ما فتنوا} أَيْ: عُذِّبوا وأُوذوا حتى يلفظوا بما يرضيهم {ثمَّ جاهدوا} مع النبي صلى الله عليه وسلم {وصبروا} على الدِّين والجهاد {إِنَّ رَبَّكَ مِنْ بعدها} أَيْ: من بعد تلك الفتنة التي أصابتهم {لغفور رحيم} يغفر لهم ما تلفَّظوا به من الكفر تقيَّة',NULL,'621',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2013,'{يوم تأتي} أَيْ: اذكر لهم ذلك اليوم وذكِّرهم وهو يوم القيامة {كلُّ نفس} كلُّ أحدٍ لا تهمُّه إلاَّ نفسه فهو مخاصمٌ ومحتجٌ عن نفسه حتى إنَّ إبراهيم عليه السَّلام ليدلي بالخلَّة {وَتُوَفَّى كُلُّ نَفْسٍ مَا عَمِلَتْ} أَيْ: جَزَاءُ مَا عَمِلَتْ {وَهُمْ لا يُظْلَمُونَ} لا يُنْقَصُونَ ثمَّ أنزل الله تعالى في أهل مكَّة وما امتُحنوا به من القحط والجوع قوله تعالى:',NULL,'621',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2014,'{وضرب الله مثلاً قرية كانت آمنة} ذات أمنٍ لا يُغار على أهلها {مطمئنة} قارَّةً بأهلها لا يحتاجون إلى الانتقال عنها لخوفٍ أو ضيقٍ {يأتيها رزقها رغداً من كلِّ مكان} يُجلب إليها من كلِّ بلدٍ كما قال: {يُجبى إليه ثمراتُ كلِّ شيء} {فكفرت بأنعم الله} حين كذَّبوا رسوله {فأذاقها الله لباس الجوع} عذَّبهم الله بالجوع سبع سنين {والخوف} من سرايا النَّبيِّ صلى الله عليه وسلم التي كان يبعثهم إليهم فيطوفون بهم {بما كانوا يصنعون} من تكذيب النبيِّ صلى الله عليه وسلم وإخراجه من مكَّة',NULL,'621',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2015,'{ولقد جاءهم} يعني: أهل مكَّة {رسول منهم} من نسبهم يعرفونه بأصله ونسبه {فكذبوه فأخذهم العذاب} يعني: الجوع',NULL,'622',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2016,'{فكلوا} يا معشر المؤمنين {مِمَّا رَزَقَكُمُ اللَّهُ} من الغنائم وهذه الآية والتي بعدها سبق تفسيرهما في سورة البقرة',NULL,'622',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2017,'{إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيرِ وَمَا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلا عَادٍ فَإِنَّ اللَّهَ غَفُورٌ رحيم}',NULL,'622',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2018,'{وَلا تَقُولُوا لِمَا تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ} أَيْ: لوصف ألسنتكم الكذب والمعنى: لا تقولوا لأجل الكذب وسببه لا لغيره: {هذا حلال وهذا حرام} يعني: ما كانوا يحلونه ويحرمونه إليه ثمَّ أوعد المفترين فقال: {إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لا يفلحون}',NULL,'622',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2019,'{متاع قليل} أَيْ: لهم في الدُّنيا متاعٌ قليلٌ ثم يردون إلى عذاب أليمٍ',NULL,'622',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2020,'{وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ من قبل} يعني: في سورة الأنعام: {وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا كُلَّ ذِي ظُفُرٍ} الآية {وما ظلمناهم} بتحريم ما حرَّمنا عليهم {ولكن كانوا أنفسهم يظلمون} بأنواع المعاصي',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2021,'{ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهَالَةٍ} أَيْ: الشِّرك {ثم تابوا من بعد ذلك} آمنوا وصدَّقوا {وأصلحوا} قاموا بفرائض الله وانتهوا عن معاصيه {إن ربك من بعدها} من بعد تلك الجهالة {لغفور رحيم}',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2022,'{إنَّ إبراهيم كان أمة} مؤمناً وحده والنَّاس كلُّهم كفَّارٌ {قانتاً} مُطيعاً {لله حنيفاً} لأنَّه اختتن وقام بمناسك الحج وقوله:',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2023,'{شَاكِرًا لأَنْعُمِهِ اجْتَبَاهُ وَهَدَاهُ إِلَى صِرَاطٍ مُسْتَقِيمٍ}',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2024,'{وآتيناه في الدنيا حسنة} يعني: الذِّكر والثَّناء الحسن في النَّاس كلِّهم {وإنَّه في الآخرة لمن الصالحين} هذا ترغيبٌ في الصَّلاح ليصير صاحبه من جملة مَنْ منهم إبراهيم عليه السَّلام مع شرفه',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2025,'{ثُمَّ أَوْحَيْنَا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْرَاهِيمَ حنيفاً} أمر باتِّباعه فِي مناسك الحجِّ كما علَّم جبريل عليه السَّلام إبراهيم عليه السَّلام',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2026,'{إِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِينَ اخْتَلَفُوا فِيهِ} وهم اليهود أمروا أن يتفرَّغوا للعبادة في يوم الجمعة فقالوا لا نريده ونريد اليوم الذي فرغ الله سبحانه فيه من الخلق واختاروا السَّبْتَ ومعنى اختلفوا فيه أَيْ: على نبيِّهم حيث لم يطيعوه في أخذ الجمعة فجعل السَّبْتَ عليهم أَيْ: غَلَّظَ وضيَّق الأمر فيه عليهم',NULL,'623',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2027,'{ادع إلى سبيل ربك} دين ربِّك {بالحكمة} بالنُّبوَّة {والموعظة الحسنة} يعني: مواعظ القرآن {وجادلهم} افتلهم عمَّا هم عليه {بالتي هي أحسن} بالكلمة اللَّيِّنة وكان هذا قبل الأمر بالقتال {إن ربك هو أعلم} الآية يقول: هو أعلم بالفريقين فهو يأمرك فيهما بما هو الصَّلاح',NULL,'624',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2028,'{وإن عاقبتم} الآية نزلت حين نظر النبي صلى الله عليه وسلم إلى حمزة وقد مُثِّل به فقال: واللَّهِ لأُمَثِلنَّ بسبعين منهم مكانك فنزل جبريل عليه السَّلام بهذه الآيات فصبر النبي صلى الله عليه وسلم وكفَّر عن يمينه وأمسك عمَّا أراد وقوله سبحانه: {ولئن صبرتم} أَيْ: عن المجازاة بالمثلة {لهو} أَيْ: الصَّبر {خير للصابرين} ثمَّ أمره بالصَّبر عزماً فقال:',NULL,'624',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2029,'{وَاصْبِرْ وَمَا صَبْرُكَ إِلا بِاللَّهِ} أَيْ: بتوفيقه ومعونته {ولا تحزن عليهم} على المشركين بإعراضهم عنك {وَلا تَكُ فِي ضيق مما يمكرون} لا يضيق صدرك من مكرهم',NULL,'624',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2030,'{إِنَّ اللَّهَ مَعَ الَّذِينَ اتَّقَوْا} الفواحش والكبائر {والذين هم محسنون} في العمل بالنَّصرة والمعونة',NULL,'625',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2031,'{سبحان الذي} براءة من السُّوء {أسرى بعبده} سيَّر محمَّداً عليه السَّلام {من المسجد الحرام} يعني: مكَّة ومكَّةُ كلُّها مسجد {إلى المسجد الأقصى} وهو بيت المقدس وقيل له الأقصى لبعد المسافة بينه وبين المسجد الحرام {الَّذِي بَارَكْنَا حَوْلَهُ} بالثِّمار والأنهار {لنريه من آياتنا} وهو ما أُري في تلك اللَّيلة من الآيات التي تدلُّ على قدرة الله سبحانه ثمَّ ذكر أنَّه سبحانه أكرم موسى عليه السَّلام أيضاً قبله بالكتاب فقال:',NULL,'627',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2032,'{وآتينا موسى الكتاب} التَّوراة {وجعلناه هدىً لبني إسرائيل} دللناهم به على الهدى {أن لا تتخذوا} فقلنا: لا تتخذوا وأن زائدة والمعنى: لا تتوكَّلوا على غيري ولا تتَّخذوا من دوني ربَّاً',NULL,'627',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2033,'{ذرية} يا ذريَّةَ {مَنْ حملنا مع نوح} يعني: بني إسرائيل وكانوا ذرية ن كان في سفينة نوح عليه السَّلام وفي هذا تذكيرٌ بالنِّعمة إذْ أنجى آباءهم من الغرق ثمَّ أثنى على نوحٍ فقال: {إِنَّهُ كَانَ عبداً شكوراً} كان إذا أكل حمد الله وإذا لبس ثوباً حمد الله',NULL,'628',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2034,'{وقضينا إلى بني إسرائيل} أوحينا إليهم وأعلمناهم في كتابهم {لتفسدنَّ في الأرض مرتين} بالمعاصي وخلاف أحكام التَّوراة {ولتعلن علواً كبيراً} لتتعظمنَّ ولتبغُنَّ',NULL,'628',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2035,'{فإذا جاء وعد أولاهما} يعني: أوَّل مرَّة في الفساد {بعثنا عليكم} أرسلنا عليكم وسلَّطنا {عباداً لنا} يعني: جالوت وقومه {أولي بأسٍ شديد} ذوي قوَّةٍ شديدةٍ {فجاسوا خلال الديار} تردَّدوا وطافوا وسط منازلهم ليطلبوا مَنْ يقتلونهم {وكان وعداً مفعولاً} قضاءً قضاه الله تعالى عليهم',NULL,'628',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2036,'{ثمَّ رددنا لكم الكرَّة عليهم} نصرناكم ورددنا الدَّولة لكم عليهم بقتل جالوت {وأمددناكم بأموالٍ وبنين} حتى عاد أمركم كما كان {وجعلناكم أكثر نفيراً} أكثر عدداً من عدوِّكم',NULL,'628',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2037,'{إن أحسنتم} أَيْ: وقلنا: إن أحسنتم {أحسنتم لأنفسكم} إن أطعتم الله فيما بقي عفا عنكم المساوئ {وإنْ أسأتم} بالفساد وعصيان الأنبياء وقتلهم {فلها} فعليها يقع الوبال {فإذا جاء وعد الآخرة} المرَّة الأخيرة من إفسادكم وجواب (إذا) محذوف على تقدير: بعثناهم {لِيَسُوْءُوْا وجوهكم} وهو أنَّه عليهم بختنصر فسبى وقتل وخرب ومعنى لِيَسُوْءُوْا وجوهكم: ليخزوكم خزياً يظهر أثره في وجوهكم كبسي ذراريكم وإخراب مساجدكم {وليتبروا ما علوا} وليدمِّروا ويُخرِّبوا ما غلبوا عليه',NULL,'628',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2038,'{عسى ربكم} وهذا أيضاً ممَّا أُخبروا به فِي كتابهم والمعنى: لعلَّ ربكم {أن يرحمكم} ويعفو عنكم بعد انتقامه منكم يا بني إسرائيل {وإن عدتم} بالمعصية {عدنا} بالعقوبة هذا في الدنيا وأما في الآخرة فقد {جعلنا جهنم للكافرين حصيراً} أَيْ: سجناً ومحبساً',NULL,'629',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2039,'{إنَّ هذا القرآن يهدي للتي هي أقوم} يرشد إلى الحالة التي هي أعدل وأصوب وهي توحيد الله تعالى والإِيمان برسله {ويبشر المؤمنين} بأنَّ {لهم أجراً كبيراً} وأنَّ أعداءهم معذَّبون في الآخرة',NULL,'629',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2040,'{وأَنَّ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ أَعْتَدْنَا لَهُمْ عذابا أليما}',NULL,'629',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2041,'{ويدع الإِنسان} الآية ربَّما يدعو الإنسان على نفسه عند الغضب والضَّجر وعلى ولده وأهله بما لا يحبُّ أن يستجاب له كما يدعو لنفسه بالخير {وَكَانَ الإِنْسَانُ عَجُولا} يعجل في الدُّعاء بالشَّرِّ كعجلته في الدُّعاء بالخير',NULL,'629',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2042,'{وَجَعَلْنَا اللَّيْلَ وَالنَّهَارَ آيَتَيْنِ} علامتين تدلاَّن على قدرة خالقهما {فمحونا} طمسنا {آية الليل} نورها بما جعلنا فيها من السَّواد {وَجَعَلْنَا آيَةَ النَّهَارِ مُبْصِرَةً} مُضيئةً يُبصر فِيها {لِتَبْتَغُوا فَضْلا مِنْ رَبِّكُمْ} لتبصروا كيف تتصرَّفون في أعمالكم {وَلِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ} بمحو آية اللَّيل ولولا ذلك ما كان يُعرف اللَّيل من النَّهار وكان لا يتبيَّن العدد {وكل شيء} ممَّا يُحتاج إليه {فصلناه تفصيلاً} بينَّاه تبييناً لا يلتبس معه بغيره',NULL,'629',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2043,'{وكلَّ إنسان ألزمناه طائره في عنقه} كتبنا عليه ما يعمل من خيرٍ وشرٍّ {ونخرج له} ونُظهر له {يوم القيامة} صحيفة عمله منشورةً',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2044,'{اقرأ كتابك} أَيْ: يُقال له: اقْرَأْ كِتَابَكَ {كَفَى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيبًا} مُحاسباً يقول: كفيتَ أنت في محاسبة نفسك',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2045,'{من اهتدى فإنما يهتدي لنفسه} ثواب اهتدائه لنفسه {وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا} على نفسه عقوبة ضلاله {وَلا تَزِرُ وازرة وزر أخرى} وذلك أنَّ الوليد بن المغيرة قال: اتَّبعوني وأنا أحمل أوزاركم فقال الله تعالى: {وَلا تَزِرُ وازرة وزر أخرى} أي: لا تحمل نفس ذنب غيرها {وما كنا معذبين} أحداً {حتى نبعث رسولاً} يُبيِّن له ما يجب عليه إقامةً للحجَّة',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2046,'{وَإِذَا أَرَدْنَا أَنْ نُهْلِكَ قَرْيَةً أَمَرْنَا مُتْرَفِيهَا} أمرناهم على لسان رسولٍ بالطَّاعة وعنى بالمترفين: الجبَّارين والمُسلَّطين والملوك وخصَّهم بالأمر لأنَّ غيرهم تبعٌ لهم
INSERT INTO [b2547] VALUES (2047,'{وَكَمْ أَهْلَكْنَا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍ وَكَفَى بِرَبِّكَ بِذُنُوبِ عِبَادِهِ خَبِيرًا بَصِيرًا}',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2048,'{من كان يريد العاجلة} بعلمه وطاعته وإسلامه الدُّنيا {عجلنا له فيها ما نشاء} القدر الذي نشاء {لمن نريد} أن نعجِّل له شيئاً ثمَّ يدخل النَّار في الآخرة {مذموماً} ملوماً {مدحوراً} مطروداً لأنَّه لم يرد الله سبحانه بعمله',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2049,'{ومن أراد الآخرة} الجنَّة {وَسَعَى لَهَا سَعْيَهَا} عمل بفرائض الله {وهو مؤمن} لأنَّ الله سبحانه لا يقبل حسنةً إلاَّ من مؤمنٍِ {فأولئك كان سعيهم مشكوراً} تُضاعف لهم الحسنات',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2050,'{كلاً} من الفريقين {نمدُّ} نزيد ثمَّ ذكرهما فقال: {هؤلاء وهؤلاء من عطاء ربك} يعني: الدُّنيا وهي مقسومةٌ بين البرِّ والفاجر {وما كان عطاء ربك محظوراً} ممنوعاً في الدُّنيا من المؤمنين والكافرين ثمَّ يختصُّ المؤمنين في الآخرة',NULL,'630',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2051,'{انظر كيف فضلنا بعضهم على بعض} في الرِّزق فمن مُقلٍّ ومُكثرٍ {وللآخرة أكبر درجات وأكبر تفضيلاً} من الدُّنيا لأنَّ درجات الجنَّة يقتسمونها على قدر أعمالهم',NULL,'631',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2052,'{ولا تجعل} أَيُّها الإِنسان المخاطب {مع الله إلهاً آخر فتقعد مذموماً} ملوماً {مخذولاً} لا ناصر لك',NULL,'631',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2053,'{وقضى} وأمر {وقضى ربك أن لا تعبدوا إلاَّ إيَّاه وبالوالدين إحساناً} وأمرَ إحساناً بالوالدين {إِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ أَحَدُهُمَا أَوْ كِلاهُمَا} يقول: إن عاش أحد والديك حتى يشيب ويكبر أو هما جميعاً {فلا تقل لهما أف} (لا تقل لهما رديئاً) من الكلام ولا تستثقلنَّ شيئاً من أمرهما {ولا تنهرهما} لا تواجههما بكلامٍ تزجرهما به {وقل لهما قولاً كريماً} ليِّناً لطيفاً',NULL,'631',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2054,'{واخفض لهما جناح الذل} ألن لهما جانبك واخضع لهما {من الرحمة} أَيْ: من رقَّتك عليهما وشفقتك {وقل ربِّ ارحمهما كما ربياني} مثل رحمتهما إيَّاي فِي صغري حتى ربَّياني {صغيراً}',NULL,'632',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2055,'{ربكم أعلم بما في نفوسكم} بما تُضمرون من البِرِّ والعقوق {إن تكونوا صالحين} طائعين لله {فإنَّه كان للأوابين} الرَّاجعين عن معاصي الله تعالى {غفوراً} يغفر لهم ما بدر منهم وهذا فيمن بدرت منه بادرةٌ وهو لا يُضمر عقوقاً فإذا رجع عن ذلك غفر الله له ثمَّ أنزل في برِّ الأقارب وصلة ارحامهم بالإِحسان إليهم قوله:',NULL,'632',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2056,'{وَآتِ ذَا الْقُرْبَى حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ} ممَّا جعل الله لهما من الحقِّ في المال {ولا تبذر تبذيراً} يقول: لا تنفق في غير الحقِّ',NULL,'632',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2057,'{إنَّ المبذرين} المنفقين فِي غير طاعة الله {كَانُوا إِخْوَانَ الشياطين} لأنهم يوافقهم فيما يأمرهم به ثمَّ ذمَّ الشَّيطان بقوله: {وكان الشيطان لربه كفوراً} جاحداً لنعم الله وهذا يتضمنَّ أنَّ المُنفق فِي السَّرف كفور',NULL,'632',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2058,'{وإمَّا تعرضنَّ عنهم} الآية كان النبي صلى الله عليه وسلم إذا سأله فقراء الصَّحابة ولم يكن عنده ما يعطيهم أعرض عنهم حياءً منهم وسكت وهو قوله: {وَإِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاءَ رَحْمَةٍ مِنْ رَبِّكَ} انتظار الرِّزق من الله تعالى يأتيك {فقل لهم قولاَ ميسوراً} ليِّناً سهلاً وكان إذا سُئل ولم يكن عنده ما يُعطي قال: يرزقنا الله وإيَّاكم من فضله',NULL,'633',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2059,'{ولا تجعل يدك مغلولة إلى عنقك} لا تُمسكها عن البذل كلَّ الإِمساك حتى كأنَّها مقبوضة إلى عنقك لا تنبسط بخيرٍ {ولا تبسطها كلَّ البسط} في النفقة والعطيَّة {فتقعد ملوماً} تلوم نفسك وتُلام {محسوراً} ليس عندك شيء من قولهم: حسرتُ الرَّجل بالمسألة: إذا أفنيتَ جميع ما عنده نزلت هذه الآية حين وهب رسول الله صلى الله عليه وسلم قميصه ولم يجد ما يلبسه للخروج فبقي في البيت',NULL,'633',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2060,'{إِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ} يوسع على مَنْ يشاء ويُضيِّق على مَنْ يشاء {إنَّه كان بعباده خبيراً بصيراً} حيث أجرى رزقهم على ما علم فِيهِ صلاحهم',NULL,'633',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2061,'{وَلا تَقْتُلُوا أَوْلادَكُمْ خَشْيَةَ إِمْلاقٍ نَحْنُ نَرْزُقُهُمْ} سبق تفسيره في سورة الأنعام وقوله: {خطأ} أي: إثما',NULL,'633',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2062,'{وَلا تَقْرَبُوا الزِّنَى إِنَّهُ كَانَ فَاحِشَةً وَسَاءَ سبيلا}',NULL,'634',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2063,'{وَلا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلا بالحق} بكفرٍ بعد إسلام أو زنا بعد إحصانٍ أو قتل نفسٍ بتعمُّدٍ {ومَنْ قتل مظلوماً} أَيْ: بغير إحدى هذه الخصال {فَقَدْ جَعَلْنَا لِوَلِيِّهِ} وارثه {سلطاناً} حجَّةً في قتل القاتل إن شاء أو أخذ الدِّية أو العفو {فلا يسرف في القتل} فلا يتجاوز ما حدَّ له وهو أن يقتل بالواحد اثنين أو غير القاتل ممَّنْ هو من قبيلة القاتل كفعل العرب في الجاهليَّة {إنَّه} إنَّ الوليَّ {كَانَ مَنْصُورًا} بقتل قاتل وليِّه والاقتصاص منه وقيل: {إنَّه} إنَّ المقتول ظلماً {كان منصوراً} في الدُّنيا بقتل قاتله وفي الآخرة بالثَّواب',NULL,'634',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2064,'{وَلا تَقْرَبُوا مَالَ الْيَتِيمِ إِلا بِالَّتِي هِيَ أحسن} يعني: الأكل بالمعروف وذكرنا هذا في سورة الأنعام {وأوفوا بالعهد} وهو كلُّ ما أمر ونهى عنه {إنَّ العهد كان مسؤولاً} عنه',NULL,'634',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2065,'{وأوفوا الكيل} أتمُّوه {إذا كلتم وزنوا بالقسطاس المستقيم} بأقوم الموازين {ذلك خيرٌ} أقرب إلى الله تعالى {وأحسن تأويلاً} عاقبةً',NULL,'634',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2066,'{وَلا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ} لا تقولنَّ في شيءٍ بما لا تعلم {إِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ أولئك كان عنه مسؤولاً} أَيْ: يسأل الله العباد فيم استعملوا هذه الحواس',NULL,'634',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2067,'{ولا تمش في الأرض مرحاً} أَيْ: بالكبر والفخر {إنَّك لن تخرق الأرض} لن تثقبها حتى تبلغ آخرها ولا تطاول الجبال والمعنى: إنَّ قدرتك لا تبلغ هذا المبلغ فيكون ذلك صلة إلى الاختيال يريد: إنَّه ليس ينبغي للعاجز أن يذبخ ويستكبر',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2068,'{كُلُّ ذَلِكَ} إشارةٌ إلى جميع ما تقدَّم ذكره مما به ونهى عنه {كان سَيِّئُهُ} وهو ما حرَّم الله سبحانه ونهى عنه',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2069,'{ذلك} يعني: ما تقدَّم ذكره {ممَّا أوحى إليك ربك من الحكمة} من القرآن ومواعظه وباقي الآية مفسَّر في هذه السُّورة ثمَّ نزل فيمن قال من المشركين: الملائكة بنات الله:',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2070,'{أفأصفاكم ربكم بالبنين} أَيْ: آثركم وأخلص لكم البنين دونه وجعل لنفسه البنات {إنكم لتقولون قولاً عظيماً}',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2071,'{ولقد صرَّفنا} بيَّنَّا {في هذا القرآن من كلِّ مثل} يوجب الاعتبار به والتَّفكُّر فيه {ليذكروا} ليتَّعظوا ويتدبَّروا {وما يزيدهم} ذلك البيان والتَّصريف {إلاَّ نفوراً} من الحقِّ وذلك أنَّهم اعتقدوا أنَّها شُبَهٌ وحيلٌ فنفروا منها أشدَّ النُّفور',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2072,'{قل} للمشركين: {لو كان معه} مع الله {آلِهَةٌ كَمَا يَقُولُونَ إِذًا لابْتَغَوْا إِلَى ذِي العرش سبيلاً} إذا لا بتغت الآلهة أن تزيل ملك صاحب العرش',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2073,'{سُبْحَانَهُ وَتَعَالَى عَمَّا يَقُولُونَ عُلُوًّا كَبِيرًا}',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2074,'{تُسَبِّحُ له السماوات} الآية المراد بالتَّسبيح في هذه الآية الدلالة عل أنَّ الله سبحانه خالقٌ حكيمٌ مبرَّأٌ من الأسواء والمخلوقون والمخلوقاتُ كلُّها تدلُّ على هذا وقوله: {وَلَكِنْ لا تَفْقَهُونَ تَسْبِيحَهُمْ} مخاطبة للكفَّار لأنَّهم لا يستدلُّون ولا يعتبرون',NULL,'635',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2075,'{وإذا قرأت القرآن} الاية نزلت في قومٍ كانوا يؤذون النبي صلى الله عليه إذا قرأ القرآن فحجبه الله تعالى عن أعينهم عند قراءة القرآن حتى كانوا يمرُّون به ولا يرونه وقوله: {مستوراً} معناه: ساتراً',NULL,'636',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2076,'{وجعلنا على قلوبهم أكنة} سبق تفسيره في سورة الأنعام {وإذا ذكرت ربك في القرآن وحده} قلت: لا إله إلا الله وأنت تتلو القرآن {ولوا على أدبارهم نفور} أعرضوا عنك نافرين',NULL,'636',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2077,'{نحن أعلم بما يستمعون به} نزلت حين دعا علي رضي الله عنه أشراف قريش إلى طعام اتَّخذه لهم ودخل عليهم النبي صلى الله عليه وسلم وقرأ عليهم القرآن ودعاهم إلى الله سبحانه وهم يقولون فيما بينهم متناجين: هو ساحرٌ وهو مسحورٌ فأنزل الله تعالى: {نحن أعلم بما يستمعون به} أَيْ: يستمعونه أخبر الله سبحانه أنَّه عالمٌ بتلك الحال وبذلك الذين كان يستمعونه {إذ يستمعون} إلى الرَّسول {وَإِذْ هُمْ نجوى} يتناجون بينهم بالتَّكذيب والاستهزاء {إذ يقول الظالمون} المشركون: {إن تتبعون} ما تتبعون {إِلا رَجُلا مسحوراً} مخدوعاً أن اتَّبعتموه',NULL,'636',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2078,'{انْظُرْ كَيْفَ ضَرَبُوا لَكَ الأَمْثَالَ} بَيَّنوا لك الأشباه حين شبَّهوك بالسَّاحر والكاهن والشَّاعر {فضلوا} بذلك عن طريق الحق {فلا يستطيعون سبيلاً} مخرجاً',NULL,'637',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2079,'{وقالوا أإذا كنا عظاماً} بعد الموت {ورفاتاً} وتراباً أَنُبعث ونخلق خلقاً جديداً؟',NULL,'637',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2080,'{قل كونوا حجارة أو حديداً} الآية معناها يقول: قدِّروا أنَّكم لو خُلقتم من حجارةٍ أو حديدٍ أو كنتم الموت الذي هو أكبر الأشياء في صدورهم لأماتكم الله ثمَّ أحياكم لأنَّ القدرة التي بها أنشأكم بها يُعيدكم وهذا معنى قوله: {فَسَيَقُولُونَ مَنْ يُعِيدُنَا قُلِ الَّذِي فطركم} خلقكم {أول مرَّة فسينغضون إليك رؤوسهم} يُحرِّكونها تكذيباً لهذا القول {ويقولون متى هو} ؟ أَي: الإِعادة والبعث {قُلْ عَسَى أَنْ يَكُونَ قَرِيبًا} يعني: هو قريب',NULL,'637',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2081,'{أَوْ خَلْقًا مِمَّا يَكْبُرُ فِي صُدُورِكُمْ فَسَيَقُولُونَ مَنْ يُعِيدُنَا قُلِ الَّذِي فَطَرَكُمْ أَوَّلَ مَرَّةٍ فسينغضون إليك رؤوسهم وَيَقُولُونَ مَتَى هُوَ قُلْ عَسَى أَنْ يَكُونَ قريبا}',NULL,'637',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2082,'{يوم يدعوكم} بالنداء الذي يسمعكم هو النَّفخة الأخيرة {فتستجيبون} تجيبون {بحمده} وهو أنَّهم يخرجون من القبور يقولون: سبحانك وبحمدك حمدوا حين لا ينفعهم الحمد {وَتَظُنُّونَ إِنْ لبثتم إلاَّ قليلاً} استقصروا مدَّة لبثهم في الدُّنيا أو في البرزخ مع ما يعلمون من طول لبثهم فِي الآخرة',NULL,'637',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2083,'{وقل لعبادي} المؤمنين: {يقولوا التي هي أحسن} نزلت حين شكا أصحاب النَّبيِّ صلى الله عليه وسلم إليه أذى المشركين واستأذنوه في قتالهم فقيل له: قل لهم: يقولوا للكفَّار الكلمة التي هي أحسن وهو أن يقولوا: يهديكم الله {إن الشيطان} هو الذي يفسد بينهم',NULL,'637',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2084,'{ربكم أعلم بكم إن يشأ يرحمكم} يوقفكم فتؤمنوا {أو إن يشأ يعذبكم} بأن يميتكم على الكفر {وما أرسلناك عليهم وكيلاً} ما وكل إليك إيمانهم فليس عليك إلاَّ التَّبليغ',NULL,'638',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2085,'{وربك أعلم بمَنْ في السماوات والأرض} لأنَّه هو خالقهم {ولقد فضلنا بعض النبيين على بعض} عن علمٍ بشأنهم ومعنى تفضيل بعضهم على بعض: تخصيص كلِّ واحد منهم بفضيلة دون الآخر {وآتينا داود زبوراً} أَيْ: فلا تنكروا تفضيل محمد عليه السَّلام وإعطاءه القرآن فقد جرت بهذا في النَّبيين',NULL,'638',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2086,'{قل ادعوا الذين زعمتم} الآية ابتلى الله سبحانه قريشاً بالقحط سنين فشكوا ذلك إلى النبي صلى الله عليه وسلم فأنزل الله تعالى: {قل ادعوا الذين زعمتم} ادَّعيتم أنَّهم آلهةٌ {من دونه} ثمَّ أخبر عن الآلهة فقال: {فَلا يَمْلِكُونَ كشف الضر} يعني: البؤس والشِّدة {عنكم ولا تحويلاً} من السَّقم والفقر إلى الصَّحة والغنى ثمَّ ذكر أولياءَه فقال:',NULL,'638',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2087,'{أُولَئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلَى رَبِّهِمُ الْوَسِيلَةَ} يتضرَّعون إلى الله تعالى في طلب الجنَّة {أيُّهم} هو {أقرب} إلى رحمة الله سبحانه يبتغي الوسيلة إليه بصالح الأعمال',NULL,'638',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2088,'{وإن من قرية} الآية أَيْ: وما من أهل قريةٍ إلاَّ ستهلك إمَّا بموت وإمَّا بعذاب يستأصلهم أمَّا الصَّالحة فبالموت وأمَّا الطَّالحة فبالعذاب {كان ذلك في الكتاب مسطوراً} مكتوباً في اللَّوح المحفوظ',NULL,'639',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2089,'{وما منعنا أن نرسل بالآيات} لمَّا سأل المشركون النبي صلى الله عليه وسلم أن يوسع لهم مكة ويجعل الصَّفا ذهباً أتاه جبريل عليه السَّلام فقال: إن شئت كان ما سألوا ولكنَّهم إن لم يؤمنوا لم يُنظروا وإن شئت استأنيت بهم فأنزل الله تعالى هذه الآية ومعناها: أنَّا لم نرسل بالآيات لئلا يُكذِّب بها هؤلاء كما كذب الذين من قبلهم فيستحقُّوا المعاجلة بالعقوبة {وآتينا ثمود الناقة مبصرة} آيةً مُضيئةً بيِّنةً {فظلموا بها} جحدوا أنَّها من الله سبحانه {وما نرسل بالآيات} أَي: العبر والدِّلالات {إلاَّ تخويفاً} للعباد لعلَّهم يخافون القادر على ما يشاء',NULL,'639',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2090,'{وَإِذْ قُلْنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِالنَّاسِ} أَيْ: فهم في قبضته وقدرته يمنعك منهم حتى تبلِّغ الرِّسالة ويحول بينك وبينهم أن يقتلوك {وما جعلنا الرؤيا التي أريناك} يعني: ما أُري ليلة أُسري به وكانت رؤيا يقظة {والشجرة الملعونة في القرآن} وهي شجرة الزَّقوم {إلاَّ فتنةً للناس} فكانت الفتنة في الرُّؤيا أنَّ بعضهم ارتدَّ حين أعلمهم بقصَّة الإسراء وازداد الكفَّار تكذيباً وكانت الفتنة في الزَّقوم أنَّهم قالوا: إنَّ محمداً يزعم أنَّ فِي النار شجراً والنَّار تأكل الشَّجر وقالوا: لا نعلم الزَّقوم إلاَّ التَّمر والزُّبد فأنزل الله تعالى في ذلك: {إنا جعلناها فتنة للظالمين} الآيات {ونخوفهم} بالزَّقوم فما يزدادون إلاَّ كبراً وعتوَّاً',NULL,'639',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2091,'{وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إِبْلِيسَ قَالَ أَأَسْجُدُ لِمَنْ خَلَقْتَ طِينًا}',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2092,'{قال} يعني: إبليس {أرأيتك} أَيْ: أرأيت والكاف توكيدٌ للمخاطبة {هذا الذي كرَّمت عليّ} فضَّلته يعني: آدم عليه السَّلام {لَئِنْ أَخَّرْتَنِ إِلَى يَوْمِ الْقِيَامَةِ لأَحْتَنِكَنَّ ذُرِّيَّتَهُ} لأستأصلنَّهم بالإغواء ولأستولينَّ عليهم {إلاًّ قليلاً} يعني: ممَّن عصمه الله تعالى',NULL,'640',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2093,'{قال} الله: {اذهب} إنِّي أنظرتك إلى يوم القيامة {فمن تبعك} أطاعك {منهم} من ذُرِّيَّتِهِ {فإنَّ جهنم جزاؤكم جزاءً موفوراً} وافراً',NULL,'640',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2094,'{واستفزز من استطعت منهم} أَيْ: أزعجه واستخفَّه إلى إجابتك {بصوتك} وهو الغناء والمزامير {وأجلب عليهم} وصحْ {بخيلك ورجلك} واحثثهم عليهم بالإِغواء وخيلُه: كلُّ راكبٍ في معصية الله سبحانه وتعالى وَرَجِلُه: كلُّ ماشٍ على رجليه في معصية الله تعالى {وشاركهم في الأموال} وهو كلُّ ما أُخذ بغير حقٍّ {والأولاد} وهو كلُّ ولد زنا {وعدهم} أن لا جنَّة ولا نار ولا بعث ولا حساب وهذه الأنواع من الأمر كلُّها أمر تهديد قال الله تعالى: {وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلا غُرُورًا}',NULL,'640',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2095,'{إنَّ عبادي} يعني: المؤمنين {ليس لك عليهم سلطانٌ} حجَّةٌ في الشِّرك {وَكَفَى بِرَبِّكَ وَكِيلا} لأوليائه يعصمهم من القبول مِن إبليس',NULL,'641',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2096,'{ربكم الذي يزجي} يسيِّر {لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِهِ} في طلب التِّجَارة {إنه كان بكم} بالمؤمنين {رحيماً}',NULL,'641',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2097,'{وإذا مسَّكم الضرُّ} خوف الغرق {في البحر ضلَّ} زال وبطل {من تدعون} من الآلهة {إلاَّ إياه} إلاَّ الله {فلما نجاكم} من الغرق وأخرجكم {إلى البر أعرضتم} عن الإيمان والتَّوحيد {وكان الإِنسان} الكافر لربِّه {كفوراً} لنعمة ربِّه جاحداً ثمًّ بيَّن أنَّه قادر أن يهلكهم فِي البرِّ فقال:',NULL,'641',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2098,'{أفأمنتم} يريد: حيث أعرضتم حين سلمتم من هول البحر {أن يخسف بكم} يُغيِّبكم ويذهبكم فِي {جانب البَرِّ} وهو الأرض {أو يرسل عليكم حاصباً} عذاباً يحصبهم أَيْ: يرميهم بحجارةٍ {ثمَّ لا تجدوا لكم وكيلاً} مانعاً ولا ناصراً',NULL,'641',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2099,'{أم أمنتم أن يعيدكم} فِي البحر {تارةً} مرةً {أخرى فَيُرْسِلَ عَلَيْكُمْ قاصفاً} ريحاً شديدةً تقصف الفلك وتكسره {فيغرقكم بما كفرتم} بكفركم حيث سلمتم المرة الأولى {ثُمَّ لا تَجِدُوا لَكُمْ عَلَيْنَا بِهِ تَبِيعًا} ثائراً ولا ناصرا والمعنى: لا تجدوا مَنْ يتًّبعنا بإنكار ما نزل بكم',NULL,'641',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2100,'{ولقد كرَّمنا} فضَّلنا {بني آدم} بالعقل والنُّطق والتَّمييز {وَحَمَلْنَاهُمْ فِي الْبَرِّ} على الإِبل والخيل والبغال والحمير {و} في {البحر} على السُّفن {ورزقناهم من الطيبات} الثِّمار والحبوب والمواشي والسَّمن والزُّبد والحلاوى {وَفَضَّلْنَاهُمْ على كثير ممن خلقنا} يعني: البهائم والدَّوابَّ والوحوش',NULL,'642',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2101,'{يوم ندعو} يعني: يوم القيامة {كلَّ أناسٍ بأمامهم} بنبيِّهم وهو أن يقال: هاتوا مُتَّبعي إبراهيم عليه السَّلام هاتوا مُتبَّعي موسى عليه السلام هاتوا متبعي محمد عليه السلام فيقول أهل الحقِّ فيأخذون كتبهم بأيمانهم ثمَّ يقال: هاتوا مُتَّبِعي الشَّيطان هاتوا مُتَّبعي رؤساء الضَّلالة وهذا معنى قول ابن عباس: إمام هدى وإمام ضلالة {ولا يظلمون} ولا ينقصون {فتيلاً} من الثَّواب وهي القشرة التي في شقِّ النَّواة',NULL,'642',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2102,'{من كان في هذه أعمى} في الدُّنيا أعمى القلب عمَّا يرى من قدرتي في خلق السَّماء والأرض والشَّمس والقمر وغيرهما {فهو في الآخرة} في أمر الآخرة ممَّا يغيب عنه {أعمى} أشدُّ عمىً {وأضلُّ سبيلاً} وأبعد حجَّةً',NULL,'642',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2103,'{وإن كادوا} الآية نزلت فِي وفد ثقيف أتوا رسول الله صلى الله عليه وقالوا: متِّعنا باللاَّت سنةً وحرِّمْ وادينا كما حرَّمت مكَّة فإنَّا نحبُّ أن تعرف العربُ فضلنا عليهم فإنْ خشيت أن تقول العرب: أعطيتهم ما لم تعطنا فقل: الله أمرني بذلك وأقبلوا يلحُّون على النبي صلى الله عليه وسلم فأمسك رسول الله صلى الله عليه وسلم عنهم وقد همَّ أنْ يعطيهم ذلك فأنزل الله: {وإن كادوا} همُّوا وقاربوا {ليفتنونك} ليستزلُّونك {عَنِ الَّذِي أَوْحَيْنَا إِلَيْكَ} يعني: القرآن والمعنى: عن حكمه وذلك أنَّ فِي إعطائهم ما سألوا مخالفةً لحكم القرآن {لتفتري علينا غيره} أَيْ: لتختلق علينا أشياء غير ما أوحينا إليك وهو قولهم: قل الله أمرني بذلك {وإذاً} لو فعلت ما أرادوا {لاتخذوك خليلاً}',NULL,'642',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2104,'{وَلَوْلا أَنْ ثَبَّتْنَاكَ} على الحقِّ بعصمتنا إيَّاك {لقد كدت تركن} تميل {إليهم شيئاً} ركوناً {قليلاً} ثمَّ توعَّد على ذلك لو فعله فقال:',NULL,'643',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2105,'{إذاً لأذقناك ضعف الحياة} ضِعْفَ عذاب الدُّنيا {وضعف الممات} وضعف عذاب الآخرة يعني: ضعف ما يعذِّب به غيره',NULL,'643',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2106,'{وإن كادوا لَيَسْتَفزٌّونَكَ} يعني: اليهود قالوا للنبي صلى الله عليه وسلم: إنَّ الأنبياء بُعثوا بالشَّام فإنْ كنت نبيَّاً فالحق بها فإنَّك إنْ خرجتَ إليها آمنَّا بك فوقع ذلك في قلبه لحبِّ إيمانهم فأنزل الله سبحانه وتعالى هذه الآية ومعنى ليستفزونك: ليزعجونك {من الأرض} يعني: المدينة {وإذا لا يلبثون خلافك إلاَّ قليلاً} أعلم الله سبحانه أنَّهم لو فعلوا ذلك لم يلبثوا حتى يستأصلوا كسنَّتنا فيمن قبلهم وهو قوله:',NULL,'643',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2107,'{سنة من قد أرسلنا قبلك} الآية يقول: لم نرسل قبلك رسولاً فأخرجه قومه إلاَّ أهلكوا {ولا تجد لسنتنا تحويلاً} لا خُلف لسنَّتي ولا يقدر أحدٌ أن يقلبها',NULL,'644',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2108,'{أقم الصلاة} أَيْ: أدمها {لدلوك الشمس} من وقت زوالها {إلى غسق الليل} إقباله بظلامه فيدخل في هذا صلاة الظُّهر والعصر والعشاءين {وقرآن الفجر} يعني: صلاة الفجر سمَّاها قرآناً لأنَّ الصَّلاة لا تصحُّ إلاَّ بقراءة القرآن {إنَّ قرآن الفجر كان مشهوداً} تشهده ملائكة اللَّيل وملائكة النَّهار',NULL,'644',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2109,'{ومن الليل فتهجد} فصلِّ {به} بالقرآن {نافلة لك} زيادةً لك في الدَّرجات لأنه غُفر له ما تقدَّم من ذنبه وما تأخَّر فما عمل من عملٍ سوى المكتوبة فهو نافلة له من أجل أنَّه لا يعمل ذلك في كفَّارة الذُّنوب {عسى أن يبعثك ربك} عسى من الله واجبٌ ومعنى يبعثك ربُّك: يقيمك ربُّك في مقامٍ محمودٍ وهو مقام الشَّفاعة يحمده فيه الخلق',NULL,'644',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2110,'{وقل ربِّ أدخلني} لمَّا أمر النبي صلى الله عليه وسلم بالهجرة أُنزلت عليه هذه الآية ومعناها: أدخلني المدينة إدخال صدق أَيْ: إدخالاً حسناً لا أرى فيه ما أكره {وأخرجني} من مكة إخراج صدق لا ألتفت إليها بقلبي {واجعل لي من لَدُنْكَ سلطاناً نصيراً} قوَّة القدرة والحجَّة حتى أُقيم بهما دينك',NULL,'644',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2111,'{وقل جاء الحق} الإِسلام {وزهق الباطل} واضمحلَّ الشِّرك {إن الباطل} الشِّرك {كان زهوقاً} مضمحلاً زائلاً أُمر النبي صلى الله عليه وسلم أن يقول هذا عند دخول مكَّة يوم الفتح',NULL,'645',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2112,'{وننزل من القرآن} أَيْ: من الجنس الذي هو قرآن {ما هو شفاء} من كلِّ داءٍ لأنَّ الله تعالى يدفع به كثيراً من المكاره {ورحمةٌ للمؤمنين} ثوابٌ لا انقطاع له في تلاوته {ولا يزيد} القرآن {الظالمين} المشركين {إلاَّ خساراً} لأنَّهم يكفرون به ولا ينتفعون بمواعظه',NULL,'645',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2113,'{وإذا أنعمنا على الإنسان} يريد: الوليد بن المغيرة {أعرض} عن الدُّعاء والابتهال فلا يبتهل كابتهاله في البلاء والمحنة {ونأى بجانبه} بعد نفسه عن القيام بحقوق نعم الله تعالى {وإذا مسه الشر} أصابه المرض والفقر {كان يؤوسا} يائساً عن الخير ومن رحمة الله سبحانه لأنَّه لا يثق بفضل الله تعالى على عباده',NULL,'645',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2114,'{قل كلٌّ يعمل على شاكلته} على مذهبه وطريقته فالكافر يعمل ما يشبه طريقته من الإِعراض عند الإِنعام واليأس عند الشدَّة والمؤمن يفعل ما يشبه طريقته من الشكر عند الرَّخاء والصَّبر والاحتساب عند البلاء ألا ترى أنَّه قال: {فربكم أعلم بمن هو أهدى سبيلاً} أَيْ: بالمؤمن الذي لا يُعرض عند النِّعمة ولا ييئس عند المحنة',NULL,'646',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2115,'{ويسألونك} يعني: اليهود {عن الروح} والرُّوح: ما يحيا به البدن سألوه عن ذلك وحقيقته وكيفيَّته وموضعه من البدن وذلك ما لم يُخبر الله سبحانه به أحداً ولم يُعط علمه أحداً من عبادِه فقال {قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي} أَيْ: من علم ربِّي أَيْ: إنَّكم لا تعلمونه وقيل: من خلق ربِّي أيْ: إنَّه مخلوقٌ له {وَمَا أُوتِيتُمْ مِنَ الْعِلْمِ إِلا قَلِيلا} وكانت اليهود تدَّعي علم كل شيء بما في كتابهم فقيل لهم: وما أوتيتم من العلم إلاَّ قليلاً بالإِضافة إلى علم الله تعالى',NULL,'646',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2116,'{ولئن شئنا لنذهبنَّ بالذي أوحينا إليك} لنمحونَّه من القلوب ومنت الكتب حتى لا يوجد له أثر {ثُمَّ لا تَجِدُ لَكَ بِهِ عَلَيْنَا وَكِيلا} لا تجد مَنْ نتوكل عليه فِي ردِّ شيءٍ منه',NULL,'646',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2117,'{إِلا رَحْمَةً مِنْ رَبِّكَ} لكنَّ الله رحمك فأثبت ذلك فِي قلبك وقلوب المؤمنين {إِنَّ فضله كان عليك كبيراً} حيث جعلك سيِّد وَلدِ آدم وأعطاك المقام المحمود',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2118,'{قل لئن اجتمعت الإنس والجن} الآية لمَّا تحدَّاهم رسول الله صلى الله عليه وسلم بالقرآن وعجزوا عن معارضته أنزل الله: {قُلْ لَئِنِ اجْتَمَعَتِ الإِنْسُ وَالْجِنُّ عَلَى أَنْ يأتوا بمثل هذا القرآن} في نظمه وبلاغته {لا يَأْتُونَ بِمِثْلِهِ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظهيراً} مُعيناً مثل ما يتعاون الشعراء على بيت شعرٍ فيقيمونه',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2119,'{ولقد صرَّفنا} بَيَّنّا {للناس في هذا القرآن} لأهل مكَّة {من كلِّ مثل} من الأمثال التي يجب بها الاعتبار {فَأَبَى أكثر الناس} أكثر أهل مكَّة {إلاَّ كفوراً} جحوداً للحقِّ واقترحوا من الآيات ما ليس لهم وهو قوله تعالى:',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2120,'{وقالوا لَنْ نُؤْمِنَ لَكَ} لن نصدِّقك {حَتَّى تَفْجُرَ} تشقق {لنا من الأرض ينبوعاً} عيناً من الماء وذلك أنَّهم سألوه أَن يجريَ لهم نهراً كأنهار الشَّام والعراق',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2121,'{أو تكون لك جنَّة} الآية هذا أيضاً كان فيما اقترحوا عليه',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2122,'{أو تسقط السماء كما زعمت} أنَّ ربَّك إن شاء فعل ذلك {كسفاً} أَيْ: قطعاً {أو تأتي بالله والملائكة قبيلاً} تأتي بهم حتى نراهم مقابلةً وعياناً',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2123,'{أو يكون لك بيتٌ من زخرف} من ذهبٍ فكان فيما اقترحوا عليه أن يكون له جنَّاتٌ وكنوزٌ وقصورٌ من ذهبٍ {أو ترقى في السماء} وذلك أن عبد الله بن أبي أُميَّة قال: لا أؤمن بك يا محمَّد أبداً حتى تتَّخذ سلماً إلى السماء ثمَّ ترقى فِيهِ وأنا أنظر حتى تأتيها وتأتي بنسخةٍ منشورةٍ معك ونفر من الملائكة يشهدون لك أنَّك كما تقول فقال الله سبحانه: {قُلْ سُبْحَانَ رَبِّي هَلْ كُنْتُ إِلا بَشَرًا رسولاً} أَيْ: إنَّ هذه الأشياء ليس في قوى البشر',NULL,'647',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2124,'{وما منع الناس} يعني: أهل مكَّة {أن يؤمنوا} أَيْ: الإِيمان {إذ جاءهم الهدى} البيان وهو القرآن {إلاَّ أن قالوا} إلاَّ قولهم فِي التَّعجب والإِنكار: {أَبَعَثَ اللَّهُ بشراً رسولاً} أَيْ: هلاَّ بعث مَلَكاً فقال الله تعالى:',NULL,'648',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2125,'{قل لو كان في الأرض} بدل الآدميين {ملائكة يمشون مطمئنين} مستوطنين الأرض {لنزلنا عليهم من السماء ملكاً رسولاً} يريد: إنَّ الأبلغ في الأداء إليهم بشر مثلهم وقوله تعالى:',NULL,'648',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2126,'{قُلْ كَفَى بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ إِنَّهُ كان بعباده خبيراً بصيرا}',NULL,'648',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2127,'{ونحشرهم يوم القيامة على وجوههم عمياً} يمشيهم الله سبحانه على وجوههم عُمياً لا يرون شيئاً يسرُّهم {وَبُكْمًا} لا ينطقون بحجَّةٍ {وصماً} لا يسمعون شيئاً يسرُّهم {كلما خبت} أَيْ: سكن لهبها {زدناهم سعيراً} نادرا تتسعر',NULL,'648',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2128,'{ذلك جزاؤهم} هذه الآية مفسَّرة في هذه السُّورة',NULL,'649',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2129,'{أَوَلَمْ يروا} أَوَلَمْ يعلموا {أنَّ الله الذي خلق السماوات والأرض قادرٌ على أن يخلق مثلهم} أَيْ: يخلقهم ثانياً وأراد ب {مثلهم} إيَّاهم وتمَّ الكلام ثمَّ قال: {وَجَعَلَ لَهُمْ أَجَلا لا ريب فيه} يعني: أجل الموت وأجل القيامة {فأبى الظالمون} المشركون {إلاَّ كفوراً} جحوداً بذلك الأجل وهو البعث والقيامة',NULL,'649',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2130,'{قُلْ لَوْ أَنْتُمْ تَمْلِكُونَ خَزَائِنَ رَحْمَةِ رَبِّي} خزائن الرِّزق {إذاً لأمسكتم} لبخلتم {خشية الإنفاق} أن تتفقوا فتفقروا {وكان الإِنسان قتوراً} بخيلاً ثمَّ ذكر قصَّة موسى عليه السَّلام وما آتاه من الآيات وإنكار فرعون ذلك فقال:',NULL,'649',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2131,'{ولقد آتينا موسى تسع آيات بينات} وهي العصا واليد وفلق البحر والطمسة وهي قوله: {ربَّنا اطمسْ على أموالِهم} والطُّوفان والجراد والقُمَّل والضفادع والدَّم {فاسأل} يا محمد {بني إسرائيل} المؤمنين من قريظة والنَّضير {إذا جاءهم} يعني: جاء آباءَهم وهذا سؤال استشهاد ليعرف اليهود صحَّة ما يقول محمَّد عليه السَّلام بقول علمائهم {فَقَالَ لَهُ فِرْعَوْنُ: إِنِّي لأَظُنُّكَ يَا مُوسَى مسحوراً} ساحراً فقال موسى عليه السَّلام:',NULL,'649',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2132,'{لقد علمت ما أنزل هؤلاء} الآيات {إلاَّ رب السماوات والأرض بصائر} عبراً ودلائل {وإني لأظنك} لأعلمك {يا فرعون مثبوراً} ملعوناً مطروداً',NULL,'649',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2133,'{فأراد} فرعون {أن يستفزهم} يخرجهم يعني: موسى وقومه {من الأرض} أرض مصر وقوله:',NULL,'650',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2134,'{فإذا جاء وعد الآخرة} يريد: يوم القيامة {جئنا بكم لفيفاً} مُجتمعين مُختلطين',NULL,'650',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2135,'{وبالحق أنزلناه} أَيْ: أنزلنا القرآن بالدِّين القائم والأمر الثَّابت {وبالحق نزل} وبمحمَّد نزل القرآن أَيْ: عليه نزل كما تقول: نزلتُ بزيدٍ',NULL,'650',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2136,'{وقرآناً فرقناه} قطعناه آيةً وسورةً سورةً في عشرين سنة {لتقرأه على الناس على مكث} تودة وَتَرسُّلٍ ليفهموه {وَنَزَّلْنَاهُ تَنْزِيلا} نجوماً بعد نجومٍ وشيئاً بعد شيءٍ',NULL,'650',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2137,'{قل} لأهل مكَّة: {آمَنُوا} بالقرآن {أَوْ لا تؤمنوا} به وهذا تهديد أَيْ: فقد أنذر الله وبلَّغ رسوله {إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ} من قبل القرآن يعني: ناساً من أهل الكتاب حين سمعوا ما أنزل على النبي صلى الله عليه وسلم خرُّوا سُجَّداً وقوله:',NULL,'650',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2138,'{إن كان وعد ربنا لمفعولاً} أَيْ: وعده بإنزال القرآن وبعث محمِّد عليه السَّلام لمفعولاً',NULL,'650',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2139,'{ويخرون للأذقان يبكون} كرَّر القول لتكرُّر الفعل منهم {ويزيدهم} القرآن {خشوعاً}',NULL,'651',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2140,'{قل ادعوا الله} الآية كان رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم يقول: يا الله يا رحمان فسمع ذلك أبو جهل فقال: إنَّ محمداً ينهاناً أن نعبد إلهين وهو يدعو إلهاً آخر مع الله يقال له الرَّحمن فأنزل الله سبحانه: {قل} يا محمد {ادعوا الله} يا معشر المؤمنين {أو ادعوا الرحمن} إن شئتم قولوا: يا الله وإن شئتم قولوا: يا رحمان {أياً ما تدعوا} أَيَّ أسماءِ اللَّهِ تَدْعُوا {فَلَهُ الأَسْمَاءُ الْحُسْنَى} {وَلا تَجْهَرْ بِصَلاتِكَ} بقراءتك فيسمعها المشركون فيسبُّوا القرآن {ولا تخافت بها} ولا تُخفها عن أصحابك فلا تسمعهم {وابتغِ بين ذلك سبيلاً} اسلك طريقاً بين الجهر والمخافتة وقوله:',NULL,'651',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2141,'{ولم يكن له وليٌّ من الذل} لم يكن وليٌّ ينصره ممَّن استّذلَّه من البشر {وكبره تكبيراً} عظمه عظمةً تامَّةً',NULL,'652',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2142,'{الْحَمْدُ لِلَّهِ الَّذِي أَنْزَلَ عَلَى عَبْدِهِ الْكِتَابَ ولم يجعل له عوجاً} اختلافاً والتباساً',NULL,'653',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2143,'{قيماً} مستقيماً يريد: أنزل على عبده الكتاب قيِّماً ولم يجعل له عوجاً {لينذر} الكافرين {بأساً} عذاباً {شديداً من لدنه} من قِبَلِه وقوله: {أجراً حسناً} يعني: الجنة',NULL,'653',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2144,'{لينذر} بعذابِ الله {الَّذِينَ قَالُوا اتَّخَذَ اللَّهُ وَلَدًا} وهو اليهود والنَّصارى',NULL,'653',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2145,'{ما لهم به} بذلك القول {من علمٍ} لأنَّهم قالوه جهلاً وافتراءً على الله {ولا لآبائهم} الذين قالوا ذلك {كبرت كلمة} مقالتهم تلك كلمة',NULL,'653',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2146,'{مَا لَهُمْ بِهِ مِنْ عِلْمٍ وَلا لآبَائِهِمْ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ أَفْوَاهِهِمْ إِنْ يَقُولُونَ إلا كذبا}',NULL,'653',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2147,'{فلعلك باخع نفسك} قاتلها {على آثارهم} على أثر تولِّيهم وإعراضهم عنك لشدَّة حرصك على إيمانهم {إن لم يؤمنوا بهذا الحديث} يعني: القرآن {أسفاً} غيظاً وحزناً',NULL,'653',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2148,'{إنا جعلنا ما على الأرض} يعني: ما خلق في الدُّنيا من الأشجار والنبات والماء ولك ذي روح على الأرض {زينة لها} زيَّناها بما خلقنا فيها {لنبلوهم أيهم أحسن عملاَ} أزهد فيها وأترك لها ثمَّ أعلم أنَّه يُفني ذلك كلَّه فقال:',NULL,'654',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2149,'{وإنا لجاعلون ما عليها صعيداً جرزاً} بلاقع ليس فيها نبات',NULL,'658',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2150,'{أم حسبت} بل أحسبت {أنَّ أصحاب الكهف} وهو المغارة في الجبل {والرقيم} وهو اللَّوح الذي كُتبت فيه أسماؤهم وأنسابهم {كانوا من آياتنا عجباً} أَيْ: لم يكونوا بأعجب آياتنا ولم يكونوا العجب من آياتنا فقط فإنَّ آياتنا كلَّها عجب وكانت قريش سألوا محمدا صلى الله عليه وسلم عن خبر فتيةٍ فُقدوا في الزمان الأوَّل بتلقين اليهود قريشاً ذلك فأنزل الله سبحانه على نبيِّه عليه السَّلام خبرهم فقال:',NULL,'654',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2151,'{إذ أوى} اذكر {الفتية إلى الكهف} هربوا إليه ممَّن يطلبهم فاشتغلوا بالدُّعاء والتَّضرُّع {فقالوا ربنا آتنا من لدنك رحمة} أعطنا من عندك مغفرةً ورزقا {وهيئ} أصلح {لنا من أمرنا رشداً} أَيْ: أرشدنا إلى ما يُقرِّب منك',NULL,'654',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2152,'{فضربنا على آذانهم} سددنا آذانهم بالنَّوم {في الكهف سنين عدداً} معدودةً',NULL,'654',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2153,'{ثم بعثناهم} ايقظناهم من نومهم {لنعلم} لنرى {أيّ الحزبين} من المؤمنين والكافرين {أحصى} أعدُّ {لما لبثوا} للبثهم في الكهف نائمين {أمداً} غايةً وكان وقع اختلافٌ بين فريقين من المؤمنين والكافرين في قدر مدَّة فقدهم ومنذ كم فقدوهم فبعثهم الله سبحانه من نومهم ليتبيَّن ذلك',NULL,'654',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2154,'{نحن نقصُّ عليك نبأهم} خبرهم {بالحق} بالصِّدق {إنهم فتية} شُبَّانٌ وأحداثٌ {آمنوا بربهم وزدناهم هدى} ثبَّتناهم على ذلك',NULL,'655',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2155,'{وربطنا على قلوبهم} ثبتناها بالصَّبر واليقين {إذ قاموا} بين يديّ ملكهم الذي كان يفتن أهل الأديان عن دينهم {فقالوا ربنا رب السماوات والأرض لن ندعوا مِنْ دُونِهِ إِلَهًا لَقَدْ قُلْنَا إِذًا شَطَطًا} كذباً وجوراً إنْ دعونا غيره',NULL,'655',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2156,'{هؤلاء قومنا اتخذوا من دونه آلهة} يعنون: الذين عبدوا الأصنام في زمانهم {لولا} هلاَّ {يأتون عليهم} على عبادتهم {بسلطانٍ بيِّن} بحجَّةٍ بيِّنةٍ {فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا} فزعم أنَّ معه إلهاً فقال لهم تمليخا - وهو رئيسهم -:',NULL,'655',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2157,'{وإذ اعتزلتموهم} فارقتموهم {وما يعبدون} من الأصنام {إلاَّ الله} فإنكم لن تتركوا عبادته {فأووا إلى الكهف} صيروا إليه {ينشر لكم ربكم من رحمته} يبسطها عليكم {وَيُهَيِّئْ لَكُمْ مِنْ أَمْرِكُمْ مِرْفَقًا} يُسهَّل لكم غذاءً تأكلونه',NULL,'655',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2158,'{وترى الشمس إذا طلعت تزاور} تميل عن كهفهم {ذات اليمين} في ناحية اليمين {وإذا غربت تقرضهم} تتركهم وتتجاوز عنهم {ذات الشمال} في ناحية الشِّمال فلا تصيبهم الشَّمس ألبتةَ لأنَّها تميل عنهم طالعةَ غرابة فتكون صورهم محفوظة {وهم في فجوة منه} مُتَّسعٍ من الكهف ينالهم برد الرِّيح ونسيم الهواء {ذلك} التزاور والقرض {من آيات الله} دلائل قدرته ولطفه بأصحاب الكهف {مَنْ يَهْدِ اللَّهُ فَهُوَ المهتد} أشار إلى أنَّه هو الذي تولَّى هدايتهم ولولا ذلك لم يهتدوا',NULL,'655',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2159,'{وتحسبهم أيقاظاً} لأنَّ أعينهم مُفتَّحة {وهم رقود} نيامٌ {ونقلبهم ذات اليمين وذات الشمال} لئلا تأكل الأرض لحومهم {وكلبهم باسط ذراعيه} يديه {بالوصيد} بفناء الكهف {لو اطلعت} أشرفت {عليهم لوليت} أعرضت {منهم فراراً ولملئت منهم رعباً} خوفاً وذلك أن الله تعالى منعهم بالرُّعب لئلا يراهم أحد',NULL,'656',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2160,'{وكذلك} وكما فعلنا بهم هذه الأشياء {بعثناهم} أيقظناهم من تلك النَّومة التي تشبه الموت {ليتساءلوا بينهم} ليكون بينهم تساؤلٌ عن مدَّة لبثهم {قَالَ قَائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْ} كم مرَّ علينا منذ دخلنا الكهف؟ {قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ} وذلك أنَّهم دخلوا الكهف غدوةً وبعثهم الله في آخر النَّهار لذلك قالوا: يوماً فلمَّا رأوا الشمس قالوا: أَوْ بعض يوم وكان قد بقيت من النَّهار بقيةٌ فقال تمليخا: {ربكم أعلم بما لبثتم} ردَّ علم ذلك إلى الله سبحانه {فابعثوا أحدكم بورقكم} بدراهمكم {هذه إلى المدينة فلينظر أيها} أَيُّ أهلها {أزكى طعاماً} أحلّ من جهةِ أنَّه ذبيحةُ مؤمن أو من جهة أنَّه غير مغصوب وقوله: {وليتلطف} في دخوله المدينة وشراء الطَّعام حتى لا يَطَّلِع عليه أحدٌ {ولا يشعرنَّ بكم} ولا يخبرنَّ بكم ولا بمكانكم {أحداً}',NULL,'656',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2161,'{أنهم إن يظهروا عليكم} يطَّلعوا ويُشرفوا عليكم {يرجموكم} يقتلوكم {أو يعيدوكم في ملتهم} يردُّوكم إلى دينهم {وَلَنْ تُفْلِحُوا إِذًا أَبَدًا} لن تسعدوا في الدُّنيا ولا في الآخرة إن رجعتم إلى دينهم',NULL,'657',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2162,'{وكذلك} وكما بعثناهم وأنمناهم {أَعْثرنا} أطلعنا {عليهم ليعلموا} ليعلم القوم الذين كانوا في ذلك الوقت {أنَّ وعد الله} بالثَّواب والعقاب {حقٌّ وأنَّ الساعة} القيامة {لا ريب فيها} لا شكَّ فيها وذلك أنَّهم يستدلُّون بقصَّتهم على صحَّة أمر البعث {إذ يتنازعون} أي: اذكر يا محمد إذ يتنازع أهلُ ذلك الزَّمان أمرَ أصحاب الكهف {بينهم} وذلك أنَّهم كانوا يختلفون في مدَّة مكثهم وفي عددهم وقيل: تنازعوا فقال المؤمنون: نبني عندهم مسجداً وقال الكافرون: نُحوِّط عليهم حائطاً يدلُّ على هذاقوله: {ابنوا عليهم بنياناً} استروهم عن النَّاس ببناءٍ حولهم وقوله: {ربُّهم أعلم بهم} يدلُّ على أنَّه وقع تنازعٌ في عدَّتهم {قَالَ الَّذِينَ غَلَبُوا عَلَى أَمْرِهِمْ} وهم المؤمنون وكانوا غالبين في ذلك الوقت {لنتخذنَّ عليهم مسجداً} فذكر في القصَّة أنّه جعل على باب الكهف مسجد يصلَّى فيه',NULL,'657',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2163,'{سيقولون ثلاثة} الآية أخبر الله تعالى عن تنازعٍ يجري في عدَّة أصحاب الكهف فجرى ذلك بالمدينة حين قدم وفد نصارى نجران فجرى ذكر أصحاب الكهف فقالت اليعقوبيَّة منهم: كانوا ثلاثةً رابعُهم كلبهم وقالت النِّسطورية: كانوا خمسةً سادسهم كلبهم وقال المسلمون: كانوا سبعةً وثامنهم كلبهم فقال الله تعالى: {قُلْ رَبِّي أَعْلَمُ بِعِدَّتِهِمْ مَا يَعْلَمُهُمْ إِلا قليل} من النَّاس قال ابن عباس: أنا في ذلك القليل ثمَّ ذكرهم بأسمائهم فذكر سبعة {فلا تمار} فلا تجادل في أصحاب الكهف {إلاَّ مراءً ظاهراً} بما أنزل عليك أي: أَيْ أَفتِ في قصَّتهم بالظَّاهر الذي أنزل إليك وقل: لا يعلمهم إلاَّ قليل كما أنزل الله {ما يعلمهم إلاَّ قليل} {ولا تستفت فيهم} في أصحاب الكهف {منهم} من أهل الكتاب {أحداً}',NULL,'657',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2164,'{ولا تقولنَّ لشيء إني فاعل ذلك غداً}',NULL,'658',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2165,'{إلا أن يشاء الله} هذا تأديبٌ من الله سبحانه لنبيه صلى الله عليه وسلم وأمرٌ له بالاستثناء بمشيئة الله سبحانه فيما يعزم يقول: إذا قلت لشيءٍ: إني فاعله غداً فقل: إن شاء الله {واذكر ربك إذا نسيت} أراد: إذا نسيت الاستثناء بمشيئة الله سبحانه فاذكره وقله إذا تذكَّرت {وقل عسى أن يهدين ربي} أَيْ: يعطيني ربِّي من الآيات والدّلالات على النُّبوَّة ما يكون أقرب في الرُّشد وأدلَّ من صحَّة قصَّة أصحاب الكهف ثمَّ فعل الله به ذلك حيث أتاه علم غيوب المرسلين وخبرهم ثمَّ أخبر عن قدر مدَّة لبثهم في الكهف بقوله:',NULL,'658',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2166,'{ولبثوا في كهفهم} منذ دخلوه إلى أن بعثهم الله {ثلاث مائة سنين وازدادوا} بعدها تسع سنين',NULL,'658',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2167,'{قل} يا محمد: {الله أعلم بما لبثوا} ممَّن يختلف في ذلك {له غيب السماوات والأرض} علم ما غاب فيهما عن العباد {أَبْصِرْ به وأسمع} ما أبصرَ الله تعالى بكلِّ موجودٍ وأسمعَه تعالى لكلِّ مسموعٍ {ما لهم} لأهل السماوات والأرض {من} دون الله {من ولي} ناصرٍ {ولا يشرك} الله {فِي حُكْمِهِ أَحَدًا} فليس لأحدٍ أن يحكم بحكمٍ لم يحكمْ به الله',NULL,'659',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2168,'{وَاتْلُ مَا أُوحِيَ إِلَيْكَ مِنْ كِتَابِ رَبِّكَ} اتَّبع القرآن {لا مبدِّل لكلماته} لا مغيِّر للقرآن {ولن تجد من دونه ملتحداً} أَيْ: ملجأ',NULL,'659',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2169,'{وَاصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدَاةِ والعشي} مفسر فِي سورة الأنعام إلى قوله: {وَلا تَعْدُ عيناك عنهم} أَيْ: لا تصرف بصرك إلى غيرهم من ذوي الهيئات والرُّتبة {تريد زينة الحياة الدنيا} تريد مجالسة الأشراف {ولا تطع} في تنحية الفقراء عنك {من إغفلنا قلبه عن ذكرنا} جعلناه غافلاً {وكان أمره فرطاً} أَيْ: ضَياعاً هلاكاً لأنَّه ترك الإِيمان والاستدلال بآيات الله تعالى واتَّبع هواه',NULL,'659',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2170,'{وقل} يا محمَّد لمن جاءك من النَّاس: {الحق من ربكم} يعني: ما آتيتكم به من الإِسلام والقرآن {فمن شاء فليؤمن ومَنْ شاء فليكفر} تخييرٌ معناه التَّهديد {إنا أعتدنا} هيَّأنا {للظالمين} الذين عبدوا غير الله تعالى {ناراً أحاط بهم سرادقها} وهو دخان يحيط بالكفَّار يوم القيامة {وَإِنْ يَسْتَغِيثُوا} مِمَّا هُمْ فِيهِ مِنَ الْعَذَابِ والعطش {يُغاثوا بماءٍ كالمهل} كمذاب الحديد والرَّصاص في الحرارة {يشوي الوجوه} حتى يسقط لحمها ثمَّ ذمَّه فقال: {بئس الشراب} هو {وساءت} النَّار {مرتفقاً} منزلاً ثمَّ ذكر ما وعد المؤمنين فقال:',NULL,'659',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2171,'{إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ إِنَّا لا نضيع أجر مَنْ أحسن عملاً} وقوله:',NULL,'660',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2172,'{يحلون فيها من أساور من ذهب} يحلى كلك مؤمنٍ واحدٍ بسوارين من ذهبٍ وكانت الأساورة من زينة الملوك في الدُّنيا وقوله: {وَيَلْبَسُونَ ثِيَابًا خُضْرًا مِنْ سُنْدُسٍ وَإِسْتَبْرَقٍ} وهما نوعان من الحرير والسُّندس: ما رقَّ والاستبرق: ما غلط {متكئين فيها على الأرائك} وهي السُّرر في الحجال {نعم الثواب} طاب ثوابهم {وحسنت} الأرائك {مرتفقاً} موضع ارتفاق أَيْ: اتِّكاءً على المرفق فيه',NULL,'660',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2173,'{واضرب لهم مثلاً رجلين} يعني: ابني ملكٍ كان فِي بني إسرائيل تُوفِّي وتركهما فاتخذ أحدهما القصور والأجنَّة والآخر كان زاهداً فِي الدُّنيا راغباً فِي الآخرة فكان إذاعمل أحوه شيئاً من زينة الدُّنيا أخذ الزَّاهد مثل ذلك فقدَّمه لآخرته واتَّخذ به عند الله الأجنة والقصور حتى نقد ماله فضربهما الله مثلاً للمؤمن والكافر الذي أبطرته النِّعمة وهو قوله: {جَعَلْنَا لأَحَدِهِمَا جَنَّتَيْنِ مِنْ أَعْنَابٍ وَحَفَفْنَاهُمَا بنخل} وجعلنا النَّخل مُطبقاً بهما {وجعلنا بينهما} بين الجنتين {زرعاً}',NULL,'660',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2174,'{كلتا الجنتين آتت أكلها} أدَّت ريعها تامَّاً {ولم تظلم منه شيئاً} لم تنقص {وفجرنا خلالهما} أخرجنا وسط الجنتين {نهراً}',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2175,'{وكان له ثمر} وكان للأخ الكافر أموال كثيرة {فقال لصاحبه} لأخيه {وهو يحاوره} يراجعه في الكلام ويُجاذبه وذلك أنَّه سأله عن ماله فيما أنفقه؟ فقال: قدَّمته بين يدي لأقدم عليه فقال: {أنا أكثر منك مالاً وأعزُّ نفراً} رهطاً وعشيرةً',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2176,'{ودخل جنته} وذلك أنَّه أخذ بيد أخيه المسلم فأخله جنَّته يطوف به فيها وقوله: {وهو ظالم لنفسه} أَيْ: بالكفر بالله تعالى {قال: ما أظنُّ أن تبيد} تهلك {هذه أبداً} أنكر أنَّ الله سبحانه يفني الدُّنيا وأنَّ القيامة تقوم فقال: {وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِنْ رُدِدْتُ إِلَى ربي} يريد: إن كان البعث حقَّاً {لأَجِدَنَّ خَيْرًا منها منقلباً} كما أعطاني هذا فِي الدُّنيا سيعطيني فِي الآخرة أفضل منه فقال له أخوه المسلم:',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2177,'{وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِنْ رُدِدْتُ إِلَى ربي لأجدنَّ خيراً منها منقلباً}',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2178,'{أَكَفَرْتَ بِالَّذِي خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نطفة} في رحم أُمِّك {ثم سوَّاك رجلاً} جعلك معتدل الخلق والقامة',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2179,'{لكنا} لكن أنا {هو الله ربي} الآية',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2180,'{ولولا} وهلاَّ {إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاءَ اللَّهُ} أي: الأمر ما شاء الله أَيْ: بمشيئة الله تعالى: {لا قوة إلاَّ بالله} لا يقوى أحدٌ على ما فِي يديه من ملكٍ ونعمةٍ إلا بالله وهذه توبيخٌ من المسلم للكافر على مقالته وتعليمٌ له ما يجب أن يقول ثم رجع إلى نفسه فقال: {إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مَالا وَوَلَدًا}',NULL,'661',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2181,'{فعسى ربي أن يؤتين} في الآخرة أو في الدُّنيا {خيراً من جنتك ويرسل عليها} على جنَّتك {حسباناً من السماء} عذاباً يرميها به من بَرَدٍ أو صاعقةٍ {فتصبح صعيداً زلقاً} أرضاً لا نبات فيها',NULL,'662',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2182,'{أو يصبح ماؤها} يعني: النهر خلالهما {غوراً} غائراً ذاهباً فِي الأرض {فَلَنْ تَسْتَطِيعَ} لا تقوى {له طلباً} لا يبقى له أثرٌ تطلبه',NULL,'662',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2183,'{وأحيط بثمره} وأهلكت أشجاره المثمرة {فأصبح يقلب كفيه} يضرب يديه واحدةً على الأخرى ندامةً {على ما أنفق فيها وهي خاوية} ساقطةٌ {على عروشها} سقوفها وما عرش للكروم {وَيَقُولُ يَا لَيْتَنِي لَمْ أُشْرِكْ بِرَبِّي أَحَدًا} تنمى أنَّه كان مُوحِّداً غير مشركٍ حين لم ينفعه التَّمني',NULL,'662',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2184,'{وَلَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ الله} لم ينصره النَّفر الذين افتخر بهم حين قال: {وأعزُّ نفراً} {وما كان منتصراً} بأن يستردَّ بدل ما ذهب منه ثمَّ عاد الكلام إلى ما قبل القصة فقال:',NULL,'662',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2185,'{هنالك} عند ذلك يعني: يوم القيامة {الولاية لله الحق} يتولَّون الله ويؤمنون يه ويتبرَّؤون ممَّا كانوا يعبدون {هو خير ثواباً} أفضل ثواباً ممَّن يُرجى ثوابه {وخير عقباً} أَيْ: عاقبةُ طاعته خيرٌ من عاقبة طاعة غيره',NULL,'662',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2186,'{واضرب لهم} لقومك {مثل الحياة الدنيا كماء} أَيْ: هو كَمَاءٍ {أَنْزَلْنَاهُ مِنَ السَّمَاءِ فَاخْتَلَطَ بِهِ نَبَاتُ الأرض} أَيْ: شرب منه فبدا فيه الرِّيّ {فأصبح} أي: النَّبات {هشيماً} كسيراً مُتفتِّتاً {تذروه الرياح} تحمله وتفرِّقه وهذه الآية مختصرةٌ من قوله تعالى {إنما مثل الحياة الدنيا} الآية {وَكَانَ اللَّهُ عَلَى كُلِّ شَيْءٍ} من الإنشاء والإِفناء {مقتدراً} قادراً أنشأ النَّبات ولم يكن ثمَّ أفناه',NULL,'662',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2187,'{المال والبنون زينة الحياة الدنيا} هذا ردٌّ على الرؤساء الذي كانوا يفتخرون بالمال والأبناء أخبر الله سبحانه أنَّ ذلك ممَّا يُتزيَّن به فِي الحياة الدُّنيا ولا ينفع في الآخرة {والباقيات الصالحات} ما يأتي به سلمان وصهيب وفقراء المسلمين من الصَّلوات والأذكار والأعمال الصالحة {خير عند ربك ثواباً} أفضل ثواباً وأفضل أملاً من المال والبنين',NULL,'663',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2188,'{ويوم} واذكر يوم {نسيّر الجبال} عن وجه الأرض كما نُسيِّر السَّحاب {وترى الأرض بارزة} ظاهرةً ليس عليها شيءٌ {وحشرناهم} المؤمنين والكافرين {فلم نغادر} ونترك {منهم أحداً}',NULL,'663',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2189,'{وعرضوا على ربك} يعني: المحشورين {صفاً} مصفوفين كلُّ زمرةٍ وأمَّةٍ صفٌّ ويقال لهم: {لقد جئتمونا كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ} حُفاةً عُراةً فرادى {بل زعمتم} خطاب لنكري البعث {ألن نجعل لكم موعداً} للبعث والجزاء',NULL,'663',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2190,'{وضع الكتاب} وُضع كتاب كلِّ امرئ فِي يمينه أو شماله {فترى المجرمين} المشركين {مشفقين ممَّا فيه} خائفين ممَّا فيه من الأعمال السيئة {وَيَقُولُونَ} لوقوعهم في الهلكة: {يَا وَيْلَتَنَا مَالِ هَذَا الْكِتَابِ لا يُغَادِرُ} لا يترك {صغيرة} من أعمالنا {ولا كبيرة إلاَّ أحصاها} أثبتها وكتبها {ووجدوا ما عملوا حاضراً} في الكتاب مكتوباً {ولا يظلم ربك أحداً} لا يعاقب أحداً بغير جرمٍ ثمَّ أمر نبيَّه عليه السَّلام أن يذكر لهؤلاء المُتكبِّرين عن مجالسة الفقراء قصَّة إبليس وما أورثه الكبر فقال:',NULL,'664',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2191,'{وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إبليس كان من الجن} أَيْ: من قبيلٍ من الملائكة يُقال لهم: الجنُّ {ففسق} خرج {عن أمر ربه} إلى معصيته في ترك السُّجود {أفتتخذونه وذريته} أولاده وهو الشَّياطين {أولياء من دوني} تطيعونهم في معصيتي {وهم لكم عدوٌّ} كما كان لأبيكم عدواً {بئس للظالمين بدلاً} بئس ما استبدلوا بعبادة الرَّحمن طاعة الشَّيطان',NULL,'664',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2192,'{ما أشهدتهم} ما أحضرتهم يعني: إبليس وذريَّته {خلق السماوات والأرض ولا خلق أنفسهم} أخبر عن كمال قدرته واستغنائه عن الأنصار والأعوان فيما خلق {وما كنت متخذ المضلين عضداً} أنصاراً وأعواناً لاستغنائي بقدرتي عن الأنصار',NULL,'664',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2193,'{ويوم يقول نادوا شركائي الذين زعمتم} الآية يقول الله تعالى يوم القيامة: ادعوا الذين أشركتم بي ليمنعوكم من عذابي {فدعوهم فلم يستجيبوا لهم وجعلنا بينهم} بين المشركين وأهل لا إله إلا الله {موبقاً} حاجزاً',NULL,'665',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2194,'{ورأى المجرمون} المشركون {النار فظنوا} أيقنوا {أنهم مواقعوها} واردوها وداخلوها {ولم يجدوا عنها مصرفاً} مهرباً لإحاطتها بهم من كلِّ جانبٍ وقوله:',NULL,'665',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2195,'{وكان الإِنسان} الكافر {أكثر شيء جدلاً} قيل: هو أبي بن خل وقيل: النَّضر بن الحارث',NULL,'665',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2196,'{وما منع الناس} أهل مكَّة {أن يؤمنوا} الإِيمان {إذ جاءهم الهدى} يعني: محمدا صلى الله عليه وسلم والقرآن {إلاَّ أن تأتيهم سنة الأولين} العذاب يعني: إنَّ الله تعالى قدَّر عليهم العذاب فذلك الذي منعهم من الإِيمان {أو يأتيهم العذاب قبلاً} عياناً يعني: القتل يوم غدر وقوله:',NULL,'665',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2197,'{ويجادل الذين كفروا بالباطل} يريد المُستهزئين والمقتسمين جادلوا في القرآن {ليدحضوا} ليبطلوا {به} بجدالهم {الحق} القرآن {واتخذوا آياتي} القرآن {وما أنذروا} به من النَّار {هزواً}',NULL,'665',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2198,'{ومن أظلم ممن ذكّر} وُعظ {بآيات ربه فأعرض عنها} فتهاون بها {ونسي ما قدَّمت يداه} ما سلف من ذنوبه وباقي الآية سبق تفسيره وقوله:',NULL,'666',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2199,'{بل لهم موعد} يعني: البعث والحساب {لن يجدوا من دونه مَوْئِلاً} ملجأ',NULL,'666',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2200,'{وتلك القرى} يريد: القرى التي أهلكها بالعذاب {أهلكناهم} أهلكنا أهلها {لما ظلموا} أشركوا وكذَّبوا الرُّسل {وجعلنا لمهلكهم} لإِهلاكهم {موعداً}',NULL,'666',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2201,'{وإذ قال موسى} واذكر إذ قال موسى لما في قصَّته من العبرة {لفتاه} يوشع بن نون: {لا أبرح} لا أزال أسير {حتى أبلغ مجمع البحرين} حيث يلتقي بحر الروم وبحر فارس {أو أمضي} إلى أن أمضي {حقباً} دهراً طويلاَ وذلك أنَّ رجلاً أتى إلى موسى عليه السَّلام فقال: هل تعلم أحداً أعلم منك؟ فقال: لا فأوحى الله تعالى إليه: بلى عبدنا خضر فسأل موسى عليه السَّلام السبيل إلى لُقيِّه فجعل الله تعالى له الحوت آيةً وقيل له: إذا فقدت الحوت فارجع فإنَّك ستلقاه فانطلق هو وفتاه حتى أتيا الصَّخرة التي عند مجمع البحرين فقال لفتاه: امكث حتى آتيك وانطلق موسى لحاجته فجرى الحوت حتى وقع فِي البحر فقال فتاه: إذا جاء نبيُّ الله حدَّثته فأنساه الشَّيطان فذلك قوله:',NULL,'666',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2202,'{فلما بلغا مجمع بينهما نسيا حُوْتَهُما} أراد: نسي أحدهما: وهو يوشع ابن نون {فاتخذ سبيله} اتَّخذ الحوت سبيله {في البحر سرباً} ذهاباً والمعنى: سرب سريا والآية على التّقديم والتَّأخير لأنَّ ذهاب الحوت كان قد تقدَّم على النِّسيان',NULL,'667',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2203,'{فلما جاوزا} ذلك المكان الذي ذهب الحوت عنه {قال لفتاه آتنا غداءنا} ما نأكله بالغداة {لقد لقينا من سفرنا هذا نصباً} عناءً وتعباً ولم يجد النَّصب في جميع سفره حتى جاوز الموضع الذي يريده فقال الفتى:',NULL,'667',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2204,'{أرأيت إذْ أوينا إلى الصخرة} يعني: حيث نزلا {فإني نسيت الحوت} نسيت قصَّة الحوت أن أُحدِّثكها ثمَّ اعتذر بإنساء الشَّيطان إيَّاه لأنَّه لو ذكر ذلك لموسى عليه السَّلام ما جاوز ذلك الموضع وما ناله النَّصب ثمَّ ذكر قصَّته فقال: {واتخذ سبيله في البحر عجباً} أَي: أعجب عجباً أخبر عن تعجُّبه من ذلك فقال موسى عليه السَّلام:',NULL,'667',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2205,'{ذلك ما كنا نبغ} نطلب ونريد من العلامة {فَارْتَدَّا عَلَى آثَارِهِمَا} رجعا من حيث جاءا {قصصاً} يقصَّان آثارهما حتى انتهيا إلى الصَّخرة التي فعل الحوت عندها ما فعل',NULL,'667',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2206,'{فوجدا عبداً من عبادنا} يعني: الخضر عليه السَّلام {آتيناه رحمة من عندنا} نبوَّة {وعلمناه من لدنا علماً} أعطيناه علماً من علم الغيب وقوله:',NULL,'667',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2207,'{رشداً} أَيْ: علماً ذا رشدٍ والتَّقدير: على أن تعلِّمني علماً ذا رشدٍ مما علمته',NULL,'667',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2208,'{إنك لن تستطيع معي صبراً} لن تصبر على صنيعي لأنِّي عُلِّمت غيب ربِّي ثمَّ أعلمه العلَّة فِي ترك الصَّبر فقال:',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2209,'{وَكَيْفَ تَصْبِرُ عَلَى مَا لَمْ تُحِطْ بِهِ خبراً} أَيْ: على ما لم تعلمه من أمرٍ ظاهره منكرٌ',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2210,'{قال} له موسى: {سَتَجِدُنِي إِنْ شَاءَ اللَّهُ صابراً} لا أسألك عن شيءٍ حتى تكون أنت تحدِّثني به {ولا أعصي لك أمراً} ولا أخالفك في شيء',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2211,'{قال} له الخضر عليه السَّلام: {فإن اتبعتني} صحبتني {فلا تسألني عن شيء} ممَّا أفعله {حتى أحدث لك منه ذكراً} حتى أكون أنا الذي أُفسِّره لك',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2212,'{فانطلقا} ذهبا يمشيان {حتى إذا ركبا} البحر {في السفينة خرقها} شقَّها الخضر وقلع لوحين ممَّا يلي الماء فـ {قال} موسى منكراً عليه: {أَخَرَقْتَهَا لِتُغْرِقَ أَهْلَهَا لَقَدْ جِئْتَ شيئاً إمراً} أَيْ: عظيماً منكراً',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2213,'فـ {قال} الخضر: {أَلَمْ أَقُلْ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صبراً} فقال موسى:',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2214,'{لا تؤاخذني بما نسيت} أَيْ: تركت من وصيتك {ولا ترهقني من أمري عسرا} لا تضيق عليَّ الأمر في صحبتي إيَّاك',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2215,'{فانطلقا حتى إذا لقيا غلاماً فقتله} أَيْ: ضربه فقضى عليه وقوله: {نفساً زكية} أَيْ: طاهرةً لم تبلغ حدَّ التَّكليف {بغير نفس} بغير قود وقوله:',NULL,'668',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2216,'{قَالَ أَلَمْ أَقُلْ لَكَ إِنَّكَ لَنْ تَسْتَطِيعَ معي صبرا}',NULL,'669',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2217,'{إن سألتك} سؤال توبيخٍ وإنكارٍ {عن شيء بعدها} بعد النَّفس المقتولة {فَلا تُصَاحِبْنِي قَدْ بلغت من لدني عذراً} أعذرت فيما بيني وبينك حيث أخبرتني أنِّي لا أستطيع معك صبراً',NULL,'669',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2218,'{فانطلقا حتى إذا أتيا أهل قرية} وهي أنطاكية {استطعما أهلها} سألاهم الطَّعام {فَأَبَوْا أَنْ يُضَيِّفُوهُمَا} فلم يطعموهما {فوجدا فيها جداراً يريد أن ينقض} قَرُبَ أَن يسقط لميلانه {فأقامه} فسوَّاه فقال موسى: {لو شئت لاتخذت} على إقامته {أجراً} جُعلاً حيث أبوا أن يطعمونا',NULL,'669',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2219,'{قال} الخضر: {هذا} وقت {فراق بيني وبينك} إنِّي لا أصحبك بهد هذا وأخبرك بتفسير ما لم تصبر عليه وأنكرته عليَّ',NULL,'669',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2220,'{أَمَّا السَّفِينَةُ فَكَانَتْ لِمَسَاكِينَ يَعْمَلُونَ فِي الْبَحْرِ فأردت أن أعيبها} أجعلها ذات عيب {وكان وراءهم} أمامهم {ملك يأخذ كلَّ سفينة} صالحة {غضبا}',NULL,'669',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2221,'{وأما الغلام فكان أبواه مؤمنين فخشينا} فكرهنا {أن يرهقهما} يُكلِّفهما {طغياناً وكفراً} ويحملهما حبُّه على أن يتَّبعاه ويدينا بدينه وكان الغلام كافراً',NULL,'669',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2222,'{فَأَرَدْنَا أَنْ يُبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِنْهُ زَكَاةً} صلاحاً {وأقرب رحماً} وأبرَّ بوالديه وأوصل للرَّحم',NULL,'670',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2223,'{وَأَمَّا الْجِدَارُ فَكَانَ لِغُلامَيْنِ يَتِيمَيْنِ فِي الْمَدِينَةِ} يعني: في تلك القرية {وكان تحته كنز لهما} من ذهبٍ وفضَّةٍ ولو سقط الجدار أُخذ الكنز {فأراد ربك أن يبلغا أشدهما} أراد الله سبحانه أن يبقى ذلك الكنز إلى بلوغ الغلامين حتى يستخرجاه {وما فعلته عن أمري} أي: انكشف لي من الله سبحانه علمٌ فعملت به ولم أعلم من عند نفسي',NULL,'670',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2224,'{ويسألونك} يعني: اليهود وذلك أنَّهم سألوه عن رجلٍ طوَّافٍ بلغ شرق الأرض وغربها',NULL,'670',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2225,'{إنا مكنا له في الأرض} سهلنا على السَّير فيها وذلَّلنا له طرقها {وآتيناه من كلِّ شيء} يحتاج إليه {سبباً} علماً يتسبَّب به إلى ما يريد',NULL,'670',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2226,'{فأتبع سبباً} طريقاً يوصله إلى مغيب الشَّمس',NULL,'670',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2227,'{حَتَّى إِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ في عين حمئة} ذات حمأةٍ وهو الطِّين الأسود {ووجد عندها} عند العين {قَوْمًا قُلْنَا يَا ذَا الْقَرْنَيْنِ إِمَّا أَنْ تعذب} إمَّا أن تقتلهم إن أبوا ما تدعوهم إليه {وإمَّا أن تتخذ فيهم حسناً} تأسرهم فتعلِّمهم الهدى خيَّره الله تعالى بين القتل والأسر فقال:',NULL,'670',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2228,'{أما من ظلم} أشرك {فسوف نعذبه} نقتله إذا لم يرجع عن الشِّرك {ثُمَّ يرد إلى ربه} بعد القتل {فيعذبه عذاباً نكراً} يعني: فِي النَّار',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2229,'{وَأَمَّا مَنْ آمَنَ وَعَمِلَ صَالِحًا فَلَهُ جَزَاءً الحسنى} الجنَّة {وسنقول له من أمرنا يسراً} نقول له قولاً جميلاً',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2230,'{ثم أتبع سبباً} سلك طريقاً آخر يوصله إلى المشرق',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2231,'{حَتَّى إِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ على قوم} عُراةٍ {لم نجعل لهم من} دون الشمس {ستراً} سقفاً ولا لباساً',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2232,'{كذلك} القبيل الذين كانوا عند مغرب الشَّمس في الكفر {وقد أحطنا بما لديه} من الجنود والعدَّة {خبراً} علماً لأنَّا أعطيناه ذلك',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2233,'{ثم أتبع سبباً} ثالثاً يُبلِّغه قطراً من أقطار الأرض',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2234,'{حتى إذا بلغ بين السدين} وهما جبلان سدَّ بينهما ذو القرنين {وجد من دونهما} عندهما {قوماً لا يكادون يفقهون قولاً} لا يفهمون كلاماً فاشتكوا إليه فساد يأجوج ومأجوج وأذارهم إياهم وهو قوله:',NULL,'671',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2235,'{إنَّ يأجوج ومأجوج مفسدون في الأرض} بالنَّهب والبغي {فهل نجعل لك خرجاً} جعلاً {عَلَى أَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا}',NULL,'672',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2236,'{قال: ما مكني فيه ربي خير} أي: كالذي أعطاني وملكني أفضل من عطيتكم {فأعينوني بقوة} بعملٍ تعملون معي {أجعل بينكم وبينهم ردماً} سدَّاً حاجزاً',NULL,'672',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2237,'{آتوني} أعطوني {زبر} قطع {الحديد} فأتوه بها فبناه {حَتَّى إِذَا سَاوَى بَيْنَ الصدفين} جانبي الجبلين {قال انفخوا} على زُبر الحديد قطع الحديد بالكير والنَّار {حتى إذا جعله ناراً} جعل الحديد ناراً أَيْ: كنارٍ {قال آتوني} قطراً: وهو النُّحاس الذَّائب {أفرغ عليه} أصبُّ عليه فأفرغ النُّحاس المذاب على الحديد المحمى حتى التصق بعضه ببعض',NULL,'672',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2238,'{فما اسطاعوا أن يظهروه} ما قدروا أن يعلوا عليه لارتفاعه وملاسته {وما استطاعوا} أن ينقبوه من أسفله لصلابته',NULL,'672',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2239,'{قال} ذو القرنين لمَّا فرغ منه: {هذا رحمة من ربي} يعني: التَّمكين من ذلك البناء والتَّقوية عليه {فإذا جاء وعد ربي} أجل ربي بخروج يأجوج مأجوج {جعله دكاً} كِسَراً {وكان وعد ربي} بخروجهم {حقاً} كائناً',NULL,'672',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2240,'{وتركنا بعضهم} يعني: الخلق من الإِنس والجنِّ {يومئذ} يوم القيامة {يموج في بعض} يدخل ويختلط {ونفخ في الصور} وهو القرن الذي يُنفخ فيه للبعث {فجمعناهم} في صعيدٍ واحدٍ',NULL,'672',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2241,'{وعرضنا} أظهرنا {جَهَنَّمَ يَوْمَئِذٍ لِلْكَافِرِينَ عَرْضًا}',NULL,'673',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2242,'{الَّذِينَ كَانَتْ أَعْيُنُهُمْ في غطاء} في غشاوةٍ {عن ذكري} أَيْ: كانوا لا يعتبرون بآياتي فيذكرونني بالتَّوحيد {وكانوا لا يستطيعون سمعاً} لعدواتهم النبي صلى الله عليه وسلم لا يقدرون أن يسمعوا ما يتلوا عليهم',NULL,'673',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2243,'{أفحسب} أفظنّ {الذين كفروا أن يتخذوا عبادي} الشَّياطين {من دوني أولياء} نفعهم ذلك ودفعوا عنهم كلا {إنا أعتدنا جهنم للكافرين نزلاً} منزلاً',NULL,'673',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2244,'{قل هل ننبئكم} نخبركم {بالأخسرين أعمالاً} بالذين هم أشدُّ الخلق وأعظمهم خسراناً فيما عملوا',NULL,'673',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2245,'{الذين ضل سعيهم} حبط عملهم {فِي الْحَيَاةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صنعاً} يظنُّون أنَّهم بعملهم مُطيعون ثمَّ بيَّن مَنْ هم فقال:',NULL,'673',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2246,'{أولئك الذين كفروا بآيات ربهم} بدلائل توحيده من القرآن وغيره {ولقائه} يعني: البعث {فحبطت أعمالهم} بطل اجتهادهم {فَلا نُقِيمُ لَهُمْ يَوْمَ الْقِيَامَةِ وزناً} أَيْ: نهينهم النَّار ولا نعبأ بهم شيئا وقوله:',NULL,'673',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2247,'{ذَلِكَ جَزَاؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوا وَاتَّخَذُوا آيَاتِي ورسلي هزوا}',NULL,'674',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2248,'{جنات الفردوس} وهو وسط الجنَّة وأعلاها درجةً وقوله:',NULL,'674',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2249,'{لا يبغون عنها حولاً} لا يريدون أن يتحوَّلوا عنها',NULL,'674',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2250,'{قل لو كان البحر مداداً} وهو ما يكتب به {لكلمات ربي} أَيْ: لكتابتها وهي حِكَمُه وعجائبه والكلمات: هي العبارات عنها {لنفد البحر قبل أن تنفد كلمات ربي ولو جئنا بمثله} بمثل البحر {مداد} زيادة على البحر',NULL,'674',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2251,'{قل إنما أنا بشر مثلكم} آدميٌّ مِثْلُكُمْ {يُوحَى إِلَيَّ أَنَّمَا إِلَهُكُمْ إِلَهٌ وَاحِدٌ فمن كان يرجو لقاء ربه} ثواب ربه {فليعمل عملاً صالحاً} خالصاً {ولا يشرك} ولا يراءِ {بعبادة ربه أحداً} نزلت هذه الآية في النَّهي عن الرِّياء بالأعمال',NULL,'674',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2252,'{كهيعص} معناه: الله كَافٌّ لِخَلْقِهِ هَادٍ لِعِبَادِهِ يَدُهُ فَوْقَ أَيْدِيهِمْ عالمٌ ببريَّته صادقٌ في وعده',NULL,'675',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2253,'{ذكر} هذا ذكر {رحمة ربك عبده زكريا} أَيْ: هذا القول الذي أنزلت عليك ذكر رحمة الله سبحانه عبده بإجابة دعائه لمَّا دعاه وهو قوله:',NULL,'675',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2254,'{إذ نادى ربه} دعا ربه {نداء خفياً} سرَّاً لم يطَّلعْ عليه غير الله',NULL,'675',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2255,'{قال رب إني وهن} ضعف {العظم مني} أَيْ: عظمي {واشتعل الرأس شيباً} وكثر شيب رأسي جداً {ولم أكن بدعائك} بدعائي إيَّاك {ربي شقياً} أَيْ: كنت مستجاب الدَّعوة قد عوَّدتني الإِجابة',NULL,'675',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2256,'{وإني خفت الموالي} الأقارب وبني العمِّ والعصبة {من ورائي} من بعدي ألاَّ يحسنوا الخلافة لي في دينك {وكانت امرأتي} فيما مضى من الزَّمان {عاقراً} لم تلد {فَهَبْ لِي مِنْ لَدُنْكَ وَلِيًّا} ابناً صالحاً',NULL,'676',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2257,'{يرثني ويرث من آل يعقوب} العلم والنُّبوَّة {واجعله ربِّ رَضِيّاً} مرضياً فاستجاب الله تعالى دعاءه وقال:',NULL,'676',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2258,'{يا زكريا إنا نبشرك بغلام} ولدٍ ذكرٍ {اسمه يحيى} لأنَّه يحيا بالعلم والطَّاعة {لم نجعل له من قبل سمياً} لم يُسمَّ أحدٌ قبله بهذا الاسم فأحبَّ زكريا أن يعلم من أيِّ جهةٍ يكون له الولد ومثلُ امرأته لا تلد ومثله لا يولد له فقال: {رب أنى يكون لي غلام} ولدٌ',NULL,'676',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2259,'{وَكَانَتِ امْرَأَتِي عَاقِرًا وَقَدْ بَلَغْتُ مِنَ الْكِبَرِ عتياً} أَيْ: يُبوساً وانتهاءً في السِّنِّ',NULL,'676',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2260,'{قال} جبريل عليه السَّلام: {كذلك} أَيْ: الأمر كما قيل لك {قال ربك هو عليَّ هيَّنٌ} أردُّ عليك قوَّتك حتى تقوى على الجماع وأفتق رحم امرأتك بالولد {وَقَدْ خَلَقْتُكَ مِنْ قبل} يعني: من قبل يحيى {ولم تك شيئاً}',NULL,'676',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2261,'{قال رب اجعل لي آية} على حمل امرأتي {قال آيَتُكَ أن لا تكلم الناس ثلاث ليالٍ سوياً} أَيْ: تمنع الكلام وأنت سويٌّ صحيحٌ سليمٌ فتعلم بذلك أنَّ الله قد وهب لك الولد',NULL,'676',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2262,'{فخرج على قومه} وذلك أنَّهم كانوا ينتظرونه فخرج عليهم ولم يقدر أن يتكلَّم {فأوحى إليهم} أشار إليهم {أن سبحوا} صلُّوا لله تعالى {بكرة وعشياً} فوهبنا له يحيى وقلنا:',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2263,'{يا يحيى خذ الكتاب} التوارة {بقوة} أعطيتكها وقوَّيتك على حفظها والعمل بما فيها {وآتيناه الحكم صبياً} النُّبوَّة في صباه',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2264,'{وحناناً} وآتيناه حناناً: رحمةً {من لدنا وزكاةً} تطهيراً وقوله:',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2265,'{جباراً} أيْ قتَّالاً مُتكبِّراً {عصياً} عاصياً لربِّه',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2266,'{وسلامٌ عليه} سلامةٌ له منَّا في الأحوال التي ذكرها يريد أنَّ الله سبحانه سلَّمه في هذه الأحوال',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2267,'{واذكر} يا محمَّد {في الكتاب مريم إِذِ انتبذت} تنحَّت من أهلها {مكاناً شرقياً} من جانب الشَّرق وذلك أنَّها أرادت الغسل من الحيض فاعتزلت في ناحيةٍ شرقيةٍ من الدَّار',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2268,'{فاتخذت من دونهم حجاباً} تتستَّر به عنهم {فأرسلنا إليها روحنا} جبريل عليه السَّلام {فتمثَّل} فتصوَّر {لها بشراً} آدمياً {سويَّاً} تامَّ الخلق',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2269,'{قالت إني أعوذ بالرحمن منك} أيُّها البشر {إن كنت تقيّاً} مُؤمناً مُطيعاً فستنتهي عني بتعوُّذي بالله سبحانه منك',NULL,'677',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2270,'{قال} جبريل عليه السَّلام: {إِنَّمَا أَنَا رَسُولُ رَبِّكِ لأَهَبَ لَكِ غُلامًا زكياً} ولداً صالحاً نبيَّاً',NULL,'678',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2271,'{قَالَتْ أَنَّى يَكُونُ لِي غُلامٌ وَلَمْ يَمْسَسْنِي بشر} ليس لي زوجٌ {ولم أك بغيا} وليس بزانيةٍ',NULL,'678',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2272,'{قال كذلك} أَيْ: الأمر كما وصفت لك {قال ربك هو علي هين} أن أهب لكِ غلاماً من غير أبٍ {ولنجعله آية} علامةً للنَّاس على قدرة الله تعالى {ورحمةً منا} لمّنْ تبعه على دينه {وكان} ذلك {أمراً مقضيّاً} قضيت به في سابق علمي فرفع جبريل عليه السَّلام جانب درعها فنفخ في جيبها فحملت بعيسى عليه السَّلام وذلك قوله سبحانه:',NULL,'678',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2273,'{فحملته فانتبذت به} تباعدت بالحمل {مكاناً قصياً} بعيداً من أهلها في أقصى وادي بيت لحم وذلك أنَّها لمَّا أحسَّت بالحمل هربت من قومها مخالفة اللائمة',NULL,'678',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2274,'{فَأَجاءَها المخاض} وجع الولادة {إلى جذع النخلة} وذلك أنَّها حين أخذها الطَّلق صعدت أكمة فإذا عليها جذع نخلةٍ وهو ساقها ولم يكن لها سعفٌ فسارت إليها وقالت جزعاً ممَّا أصابها: {يَا لَيْتَنِي مِتُّ قَبْلَ هَذَا} اليوم وهذا الأمر {وكنت نسياً منسياً} شيئاً متروكاً لا يُعرف ولا يُذكر فلمَّأ رأى جبريل عليه السَّلام وسمع جزعها وناداها من تحت الأكمة وهو قوله:',NULL,'678',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2275,'{فناداها من تحتها أن لا تحزني قد جعل ربك تحتك سرياً} نهر ماءٍ جارٍ وكان تحت الأكمة نهرٌ قد انقطع الماء منه فأرسل الله سبحانه الماء فيه لمريم',NULL,'679',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2276,'{وهزي} وحرِّكي {إليك} إلى نفسك {بجذع النخلة تُسَاقط} النَّخلة {عليك رطباً جنياً} غضَّاً ساعةَ جُني وذلك أنَّ الله تعالى أحيا لها تلك النَّخلة بعد يبسها فأورقت وأثمرت وأرطبت',NULL,'679',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2277,'{فكلي} من الرُّطب {واشربي} من الماء السَّري {وقري عيناً} بولدك {فإمَّا ترينَّ مِنَ الْبَشَرِ أَحَدًا} فسألك عن ولدك ولامَك عليه {فقولي إني نذرت للرحمن صوماً} صمتاً أَيْ: قولي له: إني أوجبت على نفسي لله سبحانه أن لا أتكلَّم وذلك أنَّ الله تعالى أراد أن يظهر براءتها من جهة عيسى عليه السَّلام يتكلَّم ببراءة أمِّه وهو في المهد فذلك قوله: {فلن أكلم اليوم إنسياً}',NULL,'679',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2278,'{فأتت به} بعيسى بعد ما طهرت من نفاسها {قَوْمَهَا تَحْمِلُهُ قَالُوا يَا مَرْيَمُ لَقَدْ جِئْتِ شيئاً فرياً} عظيماً منكراً ولداً من غير أبٍ!',NULL,'679',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2279,'{يا أخت هارون} كان لها أخٌ صالحٌ من جهة أبيها يسمَّى هارون وقيل: هارون رجلٌ صالحٌ كان من أمثل بني إسرائيل فقيل لمريم: يا شبيهته في العفاف {ما كان أبوك} عمران {امْرَأَ سوء} زانٍ {وما كانت أمك} حنَّة {بغياً} زانيةً فمن أين لك هذا الولد من غير زوجٍ؟',NULL,'679',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2280,'{فأشارت} إلى عيس بأن يجعلوا الكلام معه فتعجَّبوا من ذلك وقالوا: {كَيْفَ نُكَلِّمُ مَنْ كَانَ فِي الْمَهْدِ صَبِيًّا} يعني: رضيعاً في الحِجْر',NULL,'679',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2281,'{قال} عيسى عند ذلك: {إني عبد الله} أقرَّ عل نفسه بالعبوديَّة لله سبحانه وتعالى {آتاني الكتاب} علَّمني التَّوراة وقيل: الخطَّ',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2282,'{وجعلني نبياً * وجعلني مباركاً} معلِّماً للخير أدعو إلى الله تعالى {أين ما كنت وأوصاني بالصلاة} أمرني بالصلاة {والزَّكاة} الطَّهارة {ما دمت حيَّاً}',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2283,'{وبرَّاً} لطيفاً {بوالدتي}',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2284,'{والسلام عليَّ يوم ولدت} الآية أَيْ: السَّلامة عليَّ من الله تعالى في هذه الأحوال',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2285,'{ذلك عيسى ابنُ مريم} أَيْ: الذي قَالَ: {إِنِّي عَبْدُ اللَّهِ آتَانِيَ الْكِتَابَ} الآية هو عيسى ابن مريم لا ما يقول النَّصارى مِنْ أنَّه إله وإنَّه ابن الله {قول الحق} أيْ: هذا الكلام قول الحقِّ والحقُّ: هو الله سبحانه وقيل: معنى قوله الحقِّ: أنَّه كلمةُ الله {الذي فيه يمترون} يشكُّون يعني: اليهود يقولون: إنَّه لِزَنيةٍ وإنَّه كذَّاب ساحر ويقول النَّصارى: إنَّه ابن الله',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2286,'{وما كان لله} ما ينبغي له سبحانه {أن يتخذ من ولد} أَيْ: ولداً {سبحانه} تنزيهاً له عن ذلك {فإذا قضى أمراً} أراد كونه {فإنما يقول له كن فيكون} كما قال لعيسى: كن فكان من غير أبٍ',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2287,'{وإنَّ الله ربي وربكم} هذا راجعٌ إلى قوله تعالى: {وأوصاني بالصَّلاة} وأوصاني بأنَّ الله ربِّي وربُّكم {فاعبدوه} {هذا} الذي ذكرت {صراط مستقيم}',NULL,'680',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2288,'{فاختلف الأحزاب} يعني: فرق النَّصارى {من بينهم} فيما بينهم وهم النّسطورية واليعقوبيَّة والملكانية {فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظِيمٍ} يريد: مشهدهم يوم القيامة',NULL,'681',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2289,'{أسمع بهم وأبصر} ما أبصرهم بالهدى يوم القيامة وأطوعهم أنَّ عيسى ليس الله ولا ابن الله سبحانه ولا ثالث ثلاثة ولكن لا ينفعهم ذلك مع ضلالتهم في الدُّنيا وهو قوله: {لكن الظالمون اليوم في ضلال مبين} من أمر عيسى والقول فيه',NULL,'681',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2290,'{وأنذرهم} خوِّفهم يا محمَّد {يوم الحسرة} يوم القيامة حين يُذبح الموت بين الفريقين {إذْ قضي الأمر} أُحكم وفرغ منه {وهم في غفلة} في الدُّنيا من ذلك اليوم {وهم لا يؤمنون} لا يُصدِّقون به',NULL,'681',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2291,'{إنا نحن نرث الأرض} لأنَّا نُميت سُكَّانها {و} نرث {مَنْ عليها} لأنَّا نميتهم {وإلينا يرجعون} للثَّواب والعقاب',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2292,'{واذكر} لقومك {في الكتاب إبراهيم إنه كان صديقاً} مؤمناً مُؤقناً {نبياً} رسولاً رفيعا',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2293,'{إِذْ قَالَ لأَبِيهِ: يَا أَبَتِ لِمَ تَعْبُدُ ما لا يسمع} الدُّعاء {ولا يبصر} العبادة {ولا يغني} ولا يدفع {عنك} من عذاب الله {شيئاً}',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2294,'{يَا أَبَتِ إِنِّي قَدْ جَاءَنِي مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِي أَهْدِكَ صِرَاطًا سَوِيًّا}',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2295,'{يا أبت لا تعبد الشيطان} لا تُعطه {إِنَّ الشَّيْطَانَ كَانَ لِلرَّحْمَنِ عَصِيًّا} عاصياً',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2296,'{يا أبت إني أخاف} إن متَّ على ما أنت عليه أن يصيبك {عذابٌ من الرحمن فتكون للشيطان ولياً} قريناً في النار',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2297,'{قال} أبوه محببا له: {أراغب أنت عن آلهتي} أَزاهدٌ فيها وتارك لعبادتها؟ ! {لئن لم تنته} لئن لم يرجع عن مقالتك في عيبها {لأرجمنك} لأشتمنَّك {واهجرني ملياً} زماناً طويلاً من الدَّهر',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2298,'{قال} إبراهيم: {سلام عليك} أَيْ: سلمتَ مني لا أصيبك بمكروه وهذا جواب الجاهل كقوله: {وإذا خاطبهم الجاهلون قالوا سلاماً} {سأستغفر لك ربي} كان هذا قبل أن نُهي عن استغفاره وعده ذلك رجاء أن يُجاب فيه {إنه كان بي حفياً} بارَّاً لطيفاً',NULL,'682',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2299,'{وأعتزلكم وما تدعون} أُفارقكم وأُفارق ما تعبدون من أصنامكم {وأدعو ربي} أعبده {عسى أن لا أكون بدعاء ربي شقيا} بعبادته {شقياً} كما شقيتم أنتم بعبادة الأصنام يريد: إنَّه يتقبَّل عبادتي ويُثيبني عليها',NULL,'683',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2300,'{فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ} وذهب مهاجراً إلى الشَّام {وهبنا له} بعد الهجرة {إسحاق ويعقوب وكلاً} منهما {جعلنا} هُ {نبياً}',NULL,'683',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2301,'{ووهبنا لهم من رحمتنا} يعني: النُّبوَّة والكتاب {وجعلنا لهم لسان صدق علياً} ثناءً حسناً رفيعاً في كلِّ أهل الأديان',NULL,'683',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2302,'{واذكر في الكتاب موسى إنه كان مخلصاً} مُوحِّداً قد أخلص دينه لله',NULL,'683',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2303,'{وناديناه من جانب الطور الأيمن} حيث أقبل من مدين يريد مصر فنودي من الشَّجرة وكانت في جانب الجبل على يمين موسى {وقرَّبناه نجيَّاً} قرَّبة الله تعالى من السماوات للمناجاة حتى سمع صرير القلم يكتب له في الألواح',NULL,'683',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2304,'{ووهبنا له من رحمتنا} من نعمتنا عليه {أخاه هارون نبيَّاً} حين سأل ربَّه فقال: {وَاجْعَلْ لِي وَزِيرًا مِنْ أَهْلِي * هَارُونَ أَخِي}',NULL,'683',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2305,'{وَاذْكُرْ فِي الْكِتَابِ إِسْمَاعِيلَ إِنَّهُ كَانَ صَادِقَ الوعد} إذا وعد وفَّى وانتظر إنساناً في مكانٍ وعده عنده حتى حال الحول عليه {وكان رسولاً نبيَّاً} قد بعث إلى جرحهم',NULL,'684',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2306,'{وكان يأمر أهله} يعني: قومه {بالصلاة والزكاة} المفروضة عليهم {وكان عند ربِّه مرضياً} لأنَّه قام بطاعته',NULL,'684',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2307,'{واذكر في الكتاب} القرآن {إدريس} وقصَّته {إِنَّهُ كَانَ صِدِّيقًا نَبِيًّا}',NULL,'684',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2308,'{ورفعناه مكاناً علياً} رُفع إلى السَّماء الرَّابعة وقيل: إلى الجنَّة',NULL,'684',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2309,'{أولئك الذين} يعني: الذين ذكرهم من الأنبياء كانوا {مِنْ ذُرِّيَّةِ آدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوحٍ} ومن ذرية من حملنا مع نوح في سفينته {ومن ذرية إبراهيم} يعني: إسحاق وإسماعيل ويعقوب {وإسرائيل} يعني: موسى وهارون {وممَّن هدينا} أرشدنا {واجتبينا} اصطفينا {إِذَا تُتْلَى عَلَيْهِمْ آيَاتُ الرَّحْمَنِ خَرُّوا سُجَّدًا وبكياً} جمع باكيا أخبر الله سبحانه أنَّ هؤلاء الأنبياء كانوا إذا سمعوا بآيات الله سبحانه ويكوا من خشية الله تعالى',NULL,'684',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2310,'{فَخَلفَ من بعدهم} قفا بعد هؤلاء {خلف} قوم سوء يعني: اليهود والنَّصارى والمجوس {أضاعوا الصلاة} تركوا الصَّلاة المفروضة {واتبعوا الشهوات} اللَّذات من شرب الخمر والزِّنا {فسوف يلقون غياً} وهو وادٍ في جهنم',NULL,'685',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2311,'{إلاَّ من تاب} من الشِّرك {وآمن} وصدَّق النَّبيِّين {وعمل صالحاً} أدَّى الفرائض {فأولئك يدخلون الجنَّة ولا يظلمون شيئاً} لا يُنقصون من ثواب أعمالهم شيئاً',NULL,'685',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2312,'{جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمَنُ عِبَادَهُ بِالْغَيْبِ} بالمغيب عنهم ولم يروها {إنَّه كان وعده مأتياً} يؤتي ما وعده لا محالة تأتيه أنت كما يأتيك هو',NULL,'685',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2313,'{لا يسمعون فيها لغواً} قبيحاً من القول {إلاَّ} لكن {سلاماً} قولاً حسناً يسلمون منه والسَّلام: اسمٌ جامعٌ للخير {ولهم رزقهم فيها بكرة وعشياً} على قدر ما يعرفون في الدُّنيا من الغداء والعشاء',NULL,'685',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2314,'{تلك الجنة التي نورث} نعطي وننزل {من عبادنا مَنْ كان تقياً} يتَّقي الله بطاعته واجتناب معاصيه',NULL,'685',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2315,'{وما نتنزل} كان جبريل عليه السَّلام قد احتبس عن النبي صلى الله عليه وسلم أيَّاماً فلمَّا نزل قال له: ألاَّ زرتنا فأنزل الله سبحانه: {وَمَا نَتَنَزَّلُ إِلا بِأَمْرِ رَبِّكَ لَهُ مَا بين أيدينا} من أمر الآخرة {وما خلقنا} ما مضى من أمر الدُّنيا {وما بين ذلك} ما يكون من هذا الوقت إلى قيام السَّاعة وقيل: {له ما بين أيدينا} : يعني: الدُّنيا {وما خلفنا} يعني: السماوات {وما بين ذلك} : الهواء {وما كان ربك نسياً} تاركاً لك منذ أبطأ عنك الوحي وقوله:',NULL,'685',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2316,'{هل تعلم له سمياً} ت هل تعلم أحداً يُسمَّى الله غيره؟',NULL,'686',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2317,'{ويقول الإِنسان} يعني: أُبيَّ بن خلف {أإذا ما متُّ لسوف أخرج حياً} يقول: هذا استهزاءً وتكذيباً بالبعث يقول: لسوف أخرج حيَّاً من قبري بعد ما مت! ؟',NULL,'686',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2318,'{أو لا يذكر} يتذكَّر ويتفكَّر هذا {الإِنْسَانُ أَنَّا خَلَقْنَاهُ مِنْ قَبْلُ وَلَمْ يَكُ شيئاً} فيعلم أنَّ مَنْ قدر على الابتداء قدر على الإِعادة ثمَّ أقسم بنفسه أنَّه يبعثهم فقال:',NULL,'686',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2319,'{فوربك لنحشرنَّهم} يعني: منكري البعث {والشياطين} قرناءهم الذين أضلوا {ثمَّ لنحضرنَّهم حول جهنم جثياً} جماعات جمع: جُثوة',NULL,'686',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2320,'{ثمَّ لننزعنَّ} لنخرجنَّ {من كلِّ شيعة} أُمَّةٍ وفرقةٍ {أيُّهم أشدُّ على الرحمن عتياً} الأعتى فالأعتى منهم وذلك أنَّه يبدأ في التعذيب بأشدهم عتيَّا ثمَّ الذي يليه',NULL,'686',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2321,'{ثُمَّ لَنَحْنُ أَعْلَمُ بِالَّذِينَ هُمْ أَوْلَى بِهَا صلياً} أحقُّ بدخول النَّار',NULL,'687',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2322,'{وإنْ منكم} وما منكم من أحدٍ {إلاَّ واردُها} إلاَّ وهو يرد النَّار {كان على ربك} كان الورود على ربِّك {حتماً مقضياً} حتم بذلك وقضى',NULL,'687',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2323,'{ثمَّ نُنَجِّي} من النَّار {الذين اتقوا} الشِّرك {ونذر الظالمين} المشركين {فيها جثياً} أَيْ: جميعاً',NULL,'687',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2324,'{وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ} يعني: القرآن وما بيَّن الله فيه {قال الذين كفروا} يعني: مشركي قريش {للذين آمنوا أَيُّ الفريقين} منَّا ومنكم {خيرٌ مقاماً} منزلاً ومسكناً {وأحسن ندياً} مجلساً وذلك أنَّهم كانوا أصحاب مالٍ وزينةٍ من الدُّنيا وكان المؤمنون أصحاب فقر ورثاثة فقال لهم: نحن أعظم شأناً وأعزُّ مجلساً وأكرم منزلاً أم أنتم؟ فقال الله تعالى:',NULL,'687',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2325,'{وَكَمْ أَهْلَكْنَا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَحْسَنُ أثاثاً} متاعاً {ورئياً} منظراً من هؤلاء الكفَّار فلم يُغن ذلك عنهم شيئاً',NULL,'687',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2326,'{قل مَنْ كان في الضلالة} الشِّرك والجهالة {فليمدد له الرحمن مدَّاً} فإنَّ الله تعالى يمدُّ له فيها ويمهله في كفره وهذا لفظ أمرٍ معناه الخبر {حَتَّى إِذَا رَأَوْا مَا يُوعَدُونَ إِمَّا الْعَذَابَ} في الدُّنيا {وَإِمَّا السَّاعَةَ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكَانًا وأضعف جنداً} أَهم أم المؤمنون؟ وذلك أنَّهم إن قُتلوا ونُصر المؤمنون عليهم علموا أنَّهم أضعف جنداً وإن ماتوا فدخلوا النَّار علموا أنَّهم شرٌّ مكاناً',NULL,'687',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2327,'{ويزيد الله الذين اهتدوا هدىً} يزيدهم في يقينهم ورشدهم {والباقيات الصالحات} الأعمال الصالحة {خير عند ربك ثواباً} ممَّا يملك الكفَّار من المال {وخيرٌ مردَّاً} أَيْ: في المرَدِّ وهو الآخرة',NULL,'688',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2328,'{أفرأيت الذي كفر بآياتنا} يعني: العاص بن وائل {وقال لأوتين مالاً وولداً} وذلك أن خبايا اقتضى ديناً له عليه فقال: ألستم تزعمون أنَّ في الجنة ذهبا وفضة؟ ولئن كان ما تقولون حقَّاً فإنِّي لأفضلُ نصيباً منك فأَخِّرني حتى أقضيك في الجنَّة استهزاءً فذلك قوله: {لأوتين مالاً وولداً} يعني: في الجنَّة فقال الله تعالى:',NULL,'688',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2329,'{أطلع الغيب} أعلمَ علم الغيب حتى عرف أنَّه في الجنَّة {أم اتخذ عند الرحمن عهداً} أم قال: لا إله إلاَّ الله حتى يستحقَّ دخول الجنَّة؟',NULL,'688',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2330,'{كلا} ليس الأمر كما يقول: {سنكتب ما يقول} سيحفظ عليه ما يقول من الكفر والاستهزاء لنجاريه به {وَنَمُدُّ لَهُ مِنَ الْعَذَابِ مَدًّا} نزيده عذاباً فوق العذاب',NULL,'688',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2331,'{ونرثه ما يقول} من أنَّ في الجنَّة ذهباً وفضةً فنجعله لغيره من المسلمين {ويأتينا فرداً} خالياً من ماله وولده وخدمه',NULL,'688',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2332,'{واتخذوا من دون الله} يعني: أهل مكَّة {آلهة} وهي الأصنام {ليكونوا لهم عزَّاً} أعواناً يمنعونهم مني',NULL,'688',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2333,'{كلا} ليس الأمر على ما ظنُّوا {سيكفرون بعبادتهم} لأنَّهم كانوا جماداً لم يعرفوا أنَّهم يُعبدون {ويكونون عليهم ضداً} أعواناً وذلك أنَّ الله تعالى يحشر آلهتهم فينطقهم ويركِّب فيهم العقول فتقول: يا ربِّ عذّب هؤلاء الذين عبدونا من دونك',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2334,'{ألم تر} يا محمد {إنا أرسلنا الشياطين على الكافرين} سلَّطناهم عليهم بالإِغواء {تؤزهم أزَّاً} تُزعجهم من الطَّاعة إلى المعصية',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2335,'{فلا تعجل عليهم} بالعذاب {إنما نعدُّ لهم} الأيَّام واللَّيالي والأنفاس {عدَّاً} إلى انتهاء أجل العذاب',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2336,'{يوم نحشر المتقين إلى الرحمن وفداً} ركباناً مُكرمين',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2337,'{ونسوق المجرمين إلى جهنم ورداً} عطاشاً',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2338,'{لا يملكون الشفاعة إلاَّ من اتَّخذ} لكم {عند الرحمن عهداً} اعتقد التَّوحيد وقال: لا إله إلاَّ الله فإنه يملك الشَّفاعة والمعنى: لا يشفع إلاَّ مَنْ شَهِدَ أَنْ لا إِلَهَ إِلَّا اللَّهُ',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2339,'{وقالوا اتخذ الرحمن ولدا} يعني: اليهود والنَّصارى ومَنْ زعم أنَّ الملائكة بنات الله',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2340,'{لَقَدْ جِئْتُمْ شَيْئًا إدَّاً} عظيما فظيعا',NULL,'689',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2341,'{تكاد السماوات} تقرب من أن {يتفطرن} يتشقَّقْن {منه} من هذا القول {وتخرُّ} وتسقط {الجبال هدَّاً} سقوطاً',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2342,'{أن دعوا} لأنْ دعوا {للرحمن ولداً}',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2343,'{وما ينبغي للرحمن أن يتَّخذ ولداً} لأنَّه لا يليق به الولد ولا مجانسة بينه وبين أحد',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2344,'{إن كل} ما كل {من في السماوات والأرض إلاَّ} وهو يأتي الله سبحانه يوم القيامة مُقرَّاً له بالعبوديَّة',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2345,'{لقد أحصاهم وعدَّهم عدَّاً} أَيْ: علمهم كلَّهم فلا يخفى عليه أحدٌ ولا يفوته',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2346,'{وَكُلُّهُمْ آتِيهِ يَوْمَ القيامة فرداً} من ماله وولده وليس معه أحدٌ',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2347,'{إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَيَجْعَلُ لَهُمُ الرحمن وداً} محبَّةً في قلوب المؤمنين قيل: نزلت في عليّ بن أبي طالب وقيل: في عبد الرَّحمن بن عوف',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2348,'{فإنما يسرناه} سهَّلنا القرآن {بلسانك} بلغتك {لتبشر به المتقين} الذين صدَّقوا وتركوا الشرك {وتنذر به قوماً لداً} شداد الخصومة',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2349,'{وكم أهلكنا قبلهم} قبل قومك {من قرن} جماعةٍ {هل تحس} تجد {مِنْهُمْ مِنْ أَحَدٍ أَوْ تَسْمَعُ لَهُمْ رِكْزًا} صوتاً',NULL,'690',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2350,'{طه} يا رجل',NULL,'691',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2351,'{ما أنزلنا عليك القرآن لتشقى} لتتعب بكثرة الجهد وذلك أنَّه كان يُصلِّي اللَّيل كلَّه بمكَّة حتى تورَّمت قدماه وقال له الكفَّار: إنَّك لتشقى بترك ديننا فأنزل الله تعالى هذه الآية',NULL,'691',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2352,'{إلاَّ تذكرة} أي: ما أنزلناه إلاَّ تذكرةً موعظةً {لمن يخشى} يخاف الله عز وجل',NULL,'691',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2353,'{تنزيلاَ ممَّن خلق الأرض والسماوات العلى} جمع العليا',NULL,'691',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2354,'{الرحمن على العرش} من أنَّه أعظم المخلوقات {استوى} أي: أقبل على خلقه كقوله: {ثم استوى إلى السماء} مع أنه أعظم المخلوقات أي: استولى وقوله:',NULL,'691',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2355,'{وما تحت الثرى} ما تحت الأرض والثَّرى: التُّراب النَّدي',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2356,'{وإن تجهر بالقول فإنه يعلم السر} وهو ما أسررت في نفسك {وأخفى} وهو ما ستحدِّث به نفسك ممَّا لم يكن بعد والمعنى: إنَّه يعلم هذا فكيف ما جهر به؟',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2357,'{اللَّهُ لا إِلَهَ إِلا هُوَ لَهُ الأَسْمَاءُ الحسنى}',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2358,'{وهل أتاك} يا محمَّد {حديث موسى} خبره وقصَّته',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2359,'{إذ رأى ناراً} في طريقه إلى مصر لمَّا أخذ امرأته الطَّلْقُ {فقال لأهله} لامرأته {امكثوا} أقيموا مكانكم {إني آنست} أبصرت {ناراً لعلي آتيكم منها بقبس} شعلة نارٍ {أو أجد على النار هدى} مَنْ يهديني ويدلُّني على الطَّريق وكان قد ضلَّ عن الطَّريق',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2360,'{فلما أتاها} أي: النَّار',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2361,'{نُودِيَ يَا مُوسَى * إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نعليك} وكانت من جلد حمارٍ ميِّتٍ غيرِ مدبوغٍ لذلك أُمر بخلعها {إنك بالواد المقدس} المُطهَّر {طوى} اسم ذلك الوادي',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2362,'{وأنا اخترتك} اصطفيتك للنُّبوَّة {فاستمع لما يوحى} إليك مني',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2363,'{وأقم الصلاة لذكري} لتذكرني فيها',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2364,'{إنَّ الساعة} القيامة {آتية أكاد أخفيها} أسترها للتَّهويل والتعظيم وأكاد صلةٌ {لتجزى} في ذلك اليوم {كل نفس بما تسعى} تعمل',NULL,'692',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2365,'{فلا يصدنك} يمنعك {عنها} عن الإِيمان بالسَّاعة {مَنْ لا يؤمن بها واتبع هواه} مراده {فتردى} فتهلك',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2366,'{وما تلك} وما التي {بيمينك} في يدك اليمنى؟',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2367,'{قال هي عصاي أتوكأ عليها} أتحامل عليها عند المشي والإِعياء {وأهش} أخبط الورق عن الشَّجر {بِهَا عَلَى غَنَمِي وَلِيَ فِيهَا مَآرِبُ أُخْرَى} حاجاتٌ أخرى سوى التوكؤ والهش وقوله:',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2368,'{قَالَ أَلْقِهَا يَا مُوسَى}',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2369,'{فألقاها فإذا هي حية تسعى}',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2370,'{سنعيدها سيرتها الأولى} أَيْ: نردُّها عصاً كما كانت',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2371,'{واضمم يدك إلى جناحك} جناح الإنسان: عضه إلى أصل إبطه يريد: أدخلها تحت جناحك {تخرج بيضاء من غير سوء} برصٍ أو داءٍ {آية أخرى} لك سوى العصا',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2372,'{لنريك من آياتنا الكبرى} وكانت يده أكبر آياته',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2373,'{اذْهَبْ إِلَى فِرْعَوْنَ إنه طغى} كفر بأنعمي وتكبَّر عن عبادتي فعند ذلك',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2374,'{قال} موسى: {رب اشرح لي صدري} وسِّعْ ولَيِّنْ لي قلبي بالإِيمان والنُّبوَّة',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2375,'{ويسِّر لي أمري} وسهِّلْ عليَّ ما أمرتني به من تبليغ الرِّسالة',NULL,'693',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2376,'{واحلل} افتح {عقدة من لساني} وكانت في لسانه رُتَّة للجمرة التي وضعها على لسانه في صباه',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2377,'{يفقهوا قولي} كي يفهموا كلامي',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2378,'{واجعل لي وزيراً} معيناً {من أهلي} وهو',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2379,'{هارون}',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2380,'{اشدد به أزري} قوِّ به ظهري',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2381,'{وأشركه في أمري} اجعل ما أمرتني به من النُّبوَّة بيني وبينه',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2382,'{كي نسبحك} نصلِّي لك {كثيراً}',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2383,'{ونذكرك كثيراً} باللسان على كلِّ حالٍ',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2384,'{إنك كنت بنا بصيراً} عالماً فاستجاب الله له وقال تعالى:',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2385,'{قد أوتيت سؤلك يا موسى} أُعطيت مرادك ثمَّ ذكر منَّته السالفة عليه بقوله تعالى:',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2386,'{ولقد مننا عليك مرَّة أخرى} قبل هذه وهي:',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2387,'{إذ أوحينا إلى أمك ما يوحى} أَيْ: ألهمناها ما يلهم الإِنسان من الصَّواب وهو إلهام الله تعالى إيَّاها:',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2388,'{أن اقذفيه} اجعليه {في التابوت فاقذفيه} فاطرحيه {في اليم} يعني: نهر النِّيل {فليلقه اليمُّ بالساحل} فيردُّه الماء إلى الشَّطِّ {يأخذه عدوٌّ لي وعدوٌّ له} وهو فرعون {وألقيت عليك محبة مني} حتى لم يقتلك عدوُّك الذي أخذك من الماء وهو أنَّه حبَّبه إلى الخلق كلِّهم فلا يراه مؤمنٌ ولا كافرٌ إلاَّ أحبَّه {ولتصنع} ولتربى وتغذَّى {على عيني} على محبَّتي ومرادي يعني: إذ ردَّه إلى أُمِّه حتى غدته وهو قوله:',NULL,'694',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2389,'{إذ تمشي أختك} مُتعرِّفةً خبرك وما يكون من أمرك بعد الطَّرح في الماء {فتقول} لكم: {هل أدلُّكم على مَنْ يكفله} يرضعه ويضمُّه إليه وذلك حين أبى موسى عليه السَّلام أن يقبل ثدي امرأة فلما قال لهم ذلك قولوا: نعم فجاءت بالأُمِّ فَدُفع إليها فذلك قوله: {فرجعناك إلى أمك كي تقرَّ عينها} بلقائك وبقائك {ولا تحزن} على فقدك {وقتلت نفساً} يعني: القبطي الذي قتله {فنجيناك من الغم} من غمِّ أن تُقتل به {وفتناك فتوناً} اختبرناك اختباراً بأشياء قبل النَّبوَّة {فلبثت} مكثت {سنين في أهل مدين} عشر سنين في منزل شعيب {ثم جئت على قدر} على رأس أربعين سنة وهو القدر الذي يوحى فيه إلى الأنبياء عليهم السَّلام',NULL,'695',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2390,'{واصطنعتك لنفسي} اخترتك بالرِّسالة لكي تحبَّني وتقوم بأمري',NULL,'695',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2391,'{اذهب أنت وأخوك بآياتي} يعني: بما أعطاهما من المعجزة {ولا تنيا} لا تَفتُرا',NULL,'695',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2392,'{اذهبا إلى فرعون إنَّه طغى} علا وتكبَّر',NULL,'695',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2393,'{فقولا له قولاً ليّناً} كنِّياه وعِداه على الإِيمان نعيماً وعمراً طويلاً في صحَّة ومصيراً إلى الجنَّة {لعله يتذكر} يتَّعظ {أو يخشى} يخاف الله تعالى ومعنى (لعلَّ) ها هنا يعود إلى حال موسى وهارون أَي: اذهبا أنتما على رجائكما وطعمكما وقد علم الله تعالى ما يكون منه',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2394,'{قَالا رَبَّنَا إِنَّنَا نَخَافُ أَنْ يَفْرُطَ عَلَيْنَا} يجعل علينا بالقتل والعقوبة {أو أن يطغى} يتكبَّر ويستعصي',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2395,'{قال لا تخافا إنني معكما} بالعون والنُّصرة {أسمع} ما يقول {وأرى} ما يفعل وقوله:',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2396,'{فَأَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ} أَيْ: خلِّ عنهم ولا تستخرهم {ولا تعذبهم} ولا تتعبهم في العمل {قد جئناك بآية من ربك} يعني: اليد البيضاء والعصا {والسلام على من اتبع الهدى} سَلِمَ مَنْ أسلم',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2397,'{إِنَّا قَدْ أُوحِيَ إِلَيْنَا أَنَّ الْعَذَابَ عَلَى مَنْ كذَّب} أنبياء الله {وتولى} أعرض عن الإيمان وقوله:',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2398,'{قال فمن ربكما يا موسى}',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2399,'{ربنا الذي أعطى كلَّ شيء خلقه} أَيْ: أتقن كلَّ شيءٍ ممَّا خلق وخلقه على الهيئة التي بها يُنتفع والتي هي أصلح وأحكم لما يُراد منه {ثم هدى} أي: هداه لمعيشته ثمَّ سأله فرعون عن أعمال الأمم الماضية وهو قوله:',NULL,'696',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2400,'{فما بال القرون الأولى} الماضية؟ فأجابه موسى عليه السَّلام بأنَّ أعمالهم محفوظةٌ عند الله يُجازون بها وهو قوله:',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2401,'{علمها عند ربي في كتاب} وهو اللَّوح المحفوظ {لا يضل ربي} لا يخطئ ومعناه: لا يترك مَنْ كفر به حتى ينتقم منه {ولا ينسى} مَنْ وحَّده حتى يجازيه',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2402,'{الذي جعل لكم الأرض مهدا} فراشاً {وسلك لكم فيها سبلاً} وسهَّل لكم فيها طُرُقاً {وأنزل من السماء ماء} يريد: المطر وتمَّ ها هنا جواب موسى ثمَّ تلوَّن الخطاب وقال الله تعالى: {فأخرجنا به أزواجاً} أصنافاً {من نبات شتى} مختلفة الألوان والطُّعوم',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2403,'{كلوا} منها {وارعوا أنعامكم} فيها أَيْ: أسيموها واسرحوها في نبات الأرض {إنَّ في ذلك} الذي ذكرت {لآيات} لعبرة {لأولي النهي} لذوي العقول',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2404,'{منها خلقناكم} يعني: آدم عليه السَّلام {وفيها نعيدكم} عند الموت {ومنها نخرجكم} عند البعث {تارة} مرَّةً {أخرى}',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2405,'{ولقد أريناه} يعني: فرعون {آياتنا كلَّها} الآيات التِّسع {فكذَّب} بها وزعم أنَّها سحرٌ {وأبى} أن يُسلم',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2406,'{قال} لموسى: {أجئتنا لتخرجنا من أرضنا} من أرض مصر',NULL,'697',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2407,'{بسحرك يا موسى * فلنأتينَّك بسحر مثله} فلنعارضنَّ سحرك بسحرٍ مثله {فاجعل بيننا وبينك موعداً} لمعارضتنا إيَّاك لا نُخلف ذلك الموعد {نحن ولا أنت} وأراد بالموعدت ها هنا موضعاً يتواعدون للاجتماع هناك وهو قوله: {مكاناً سوى} أَيْ: يكون النَّصف فيما بيننا وبينك',NULL,'698',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2408,'{قال موعدكم يوم الزينة} أَيْ: وقتُ موعدكم يوم الزِّينة وهو يوم عيدٍ كان لهم {وأن يحشر الناس ضحى} يريد: يجمع أهل مصر في ذلك اليوم نهاراً أراد موسى صلوات الله عليه أن يكون أبلغ في الحجَّة وأشهر ذكراً في الجمع',NULL,'698',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2409,'{فتولى} فأدبر {فرعون فجمع كيده} حِيَله وسحرته {ثم أتى} الميعاد',NULL,'698',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2410,'{قال لهم موسى} للسَّحرة: {لا تفتروا على الله كذباً} لا تشركوا مع الله أحداً {فيسحتكم} فيستأصلكم {بعذاب وقد خاب من افترى} خسر مَن ادَّعى مع الله تعالى إلهاً آخر',NULL,'698',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2411,'{فتنازعوا أمرهم بينهم} فتشاوروا بينهم يعني: السَّحرة {وأسروا النجوى} تكلَّموا فيما بينهم سرَّاً من فرعون فقالوا: إنْ غلَبَنا موسى اتَّبعناه',NULL,'698',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2412,'{قالوا إن هذان لساحران} يعنون: موسى وهارون عليهما السَّلام {يريدان أن يخرجاكم من أرضكم} من مصر ويغلبا عليها {بسحرهما ويذهبا بطريقتكم المثلى} بجماعتكم الأشراف أَيْ: يصرفا وجوههم إليهما',NULL,'698',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2413,'{فأجمعوا كيدكم} أي: اعزموا على الكيد من غير اختلافٍ بينكم فيه {ثم ائتوا صفاً} مُجتمعين مصطفِّين ليكون أشدَّ لهيبتكم {وقد أفلح اليوم من استعلى} أَيْ: قد سعد اليوم مَنْ غلب',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2414,'{قالوا يا موسى إمَّا أن تلقي} عصاك من يدك إلى الأرض {وَإِمَّا أَنْ نَكُونَ أَوَّلَ مَنْ أَلْقَى}',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2415,'{قال بل ألقوا} أنتم فألقوا {فإذا حبالهم وعصيهم} جمع العصا {يخيل إليه} يُشبَّه لموسى {أنها تسعى} وذلك أنَّها تحرَّكت بنوع حيلةٍ وتمويهٍ وظن موسى أنَّها تسعى نحوه',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2416,'{فأوجس} فأضمر {في نفسه خيفة} خوفاً خاف أن لا يفوز ولا يغلب فلا يُصدَّق حتى قال الله تعالى له:',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2417,'{لا تخف إنك أنت الأعلى} الغالب',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2418,'{وألق ما في يمينك تلقف} تبتلع {ما صنعوا إنما صنعوا} أي: الذين صنعوه {كَيْدُ سَاحِرٍ وَلا يُفْلِحُ السَّاحِرُ حَيْثُ أَتَى} ولا يسعد الساحر ما كان فألقى موسى عصاه فتلقَّفت كلَّ الذي صنعوه وعند ذلك أُلقي',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2419,'{السحرة سجداً} خرُّوا ساجدين لله تعالى {قالوا آمنا برب هارون وموسى}',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2420,'{قال آمنتم له} صدّقتموه {قبل أن آذن لكم إنه لكبيركم} معلّمكم {الَّذِي عَلَّمَكُمُ السِّحْرَ فَلأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُمْ مِنْ خلاف} اليد اليمنى والرِّجل اليسرى {ولأصلبنكم في جذوع النخل} عل رؤوس النَّخل {ولتعلمن أينا أشد عذاباً} أنا أو ربُّ موسى {وأبقى} وأدوم',NULL,'699',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2421,'{قالوا لن نؤثرك} لن نختار دينك {على ما جاءنا من البينات} اليقين والهدى {والذي فطرنا} ولا نختارك على الذي خلقنا {فَاقْضِ مَا أَنْتَ قَاضٍ} فاصنع ما أنت صانعٌ منت القطع والصَّلب {إنما تقضي هذه الحياة الدنيا} إنَّما سلطانك وملكك في هَذِهِ الْحَيَاةَ الدُّنْيَا',NULL,'700',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2422,'{إنَّا آمنا بربنا ليغفر لنا خطايانا} الشرك الذي كنَّا فِيهِ {وَمَا أَكْرَهْتَنَا عَلَيْهِ من السحر} وإكراهك إيانا على تعلُّم السِّحر {والله خير} لنا منك {وأبقى} لأنَّك فانٍ هالكٌ',NULL,'700',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2423,'{إنَّه مَنْ يأت ربَّه مجرماً} مات على الشِّرك {فإنَّ له جهنم لا يموت فيها} فيستريح بالموت {ولا يحيا} حياةً تنفعه',NULL,'700',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2424,'{ومن يأته مؤمنا} ما على الإيمان {قد عمل الصالحات} قد أدَّى الفرائض {فَأُولَئِكَ لَهُمُ الدَّرَجَاتُ الْعُلَى} في الجنَّة وقوله:',NULL,'700',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2425,'{جزاء من تزكى} تطهَّر من الشِّرك بقول: لا إله إلاَّ الله',NULL,'700',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2426,'{وَلَقَدْ أَوْحَيْنَا إِلَى مُوسَى أَنْ أَسْرِ بِعِبَادِي} سِرْ بهم ليلاً من أرض مصر {فَاضْرِبْ لهم} بعصاك {طريقاً في البحر يبساً} يابساً {لا تخاف دركاً} من فرعون خلقك {ولا تخشى} غرقاً في البحر',NULL,'700',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2427,'{فأتبعهم} فلحقهم {فرعون بجنوده فغشيهم من اليم} فعلاهم من البحر {ما غشيهم} ما غرَّقَهم',NULL,'701',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2428,'{وأضل فرعون قومه وما هدى} ردَّ عليه حيث قال: {وَمَا أَهْدِيكُمْ إِلا سَبِيلَ الرَّشَادِ} ثمَّ ذكر مِننه على بني إسرائيل فقال:',NULL,'701',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2429,'{قد أنجيناكم من عدوكم} فرعون {وواعدناكم} لإِيتاء الكتاب {جانب الطور الأيمن} وذلك أنَّ الله سبحانه وعد موسى أن يأتي هذا المكان فيؤتيه كتاباً فيه الحلال والحرام والأحكام ووعدهم موسى أن يأتي هذا المكان عند ذهابه عنهم {ونزلنا عليكم المنَّ والسلوى} يعني: في التِّيه',NULL,'701',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2430,'{كلوا} أيْ: وقلنا لهم: كلوا {مِنْ طَيِّبَاتِ} حلالات {ما رزقناكم ولا تطغوا} ولا تكفروا النِّعمة {فيه فيحلَّ} فيجب {عليكم غضبي ومن يحلل} يجب {عليه غضبي فقد هوى} هلك وصار إلى الهاوية',NULL,'701',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2431,'{وإني لغفار لمن تاب} من الشِّرك {وآمن} وصدَّق بالله {وعمل صالحاً} بطاعة الله {ثمَّ اهتدى} أقام على ذلك حتى مات عليه',NULL,'701',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2432,'{وما أعجلك عن قومك} يعني: السَّبعين الذين اختارهم وذلك أنَّه سبقهم شوقاً إلى ميعاد الله وأمرهم أن يتَّبعوه فذلك قوله:',NULL,'702',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2433,'{قال: هم أولاء على أثري} يجيئون بعدي {وعجلت إليك} بسبقي إيَّاهم {لترضى} لتزداد عن رضى',NULL,'702',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2434,'{قال فإنا قد فتنا قومك} أَيْ: ألقيناهم في الفتنة واختبرناهم {من بعدك} من بعد خروجك من بينهم {وأضلهم السامريُّ} بدعائهم إلى عبادة العجل',NULL,'702',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2435,'{فرجع موسى إلى قومه غضبان أسفاً} شديد الحزن {قَالَ: يَا قَوْمِ أَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حسناً} أنَّه يعطيكم التَّوراة صدقاً لذلك الموعد {أفطال عليكم العهد} مدَّة مفارقتي إيَّاكم {أَمْ أَرَدْتُمْ أَنْ يَحِلَّ} أن يجب {عليكم غضب من ربكم فأخلفتم موعدي} باتِّخاذ العجل ولم تنظروا رجوعي إليكم',NULL,'702',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2436,'{قالوا: ما أخلفنا موعدك بملكنا} باختيارنا ونحن نملك من أمرنا شيئاً ولكنَّ السامري استغلونا وهو معنى قوله: {ولكنا حملنا أوزاراً} أثقالاَ {من زينة القوم} من خلي آل فرعون {فقذفناها} ألقيناها في النَّار بأمر السَّامِرِيَّ وذلك أنَّه قال: اجمعوها وألقوها في النَّار ليرجع موسى فيرى فيها رأيه {فكذلك ألقى السامري} ما معه من الحُلِّي في النَّار وهو قوله: {فكذلك ألقى السامري} ثمَّ صاغ لهم عجلا وهو قوله:',NULL,'702',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2437,'{فأخرج لهم عجلاً جسداً} لحماً ودماً {له خوار} صوت فسجدوا له وافتتنوا به وقالوا: {هذا إلهكم وإله موسى فنسي} فتركه ها هنا وخرج يطلبه قال الله تعالى احتجاجاً عليهم',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2438,'{أفلا يرون ألا يرجع} أنَّه لا يرجع {إليهم قولاً} لا يُكلِّمهم العجل ولا يجيبهم {ولا يملك لهم ضراً ولا نفعاً}',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2439,'{ولقد قال لهم هارون من قبل} من قبل رجوع موسى: {يا قوم إنما فتنتم به} ابتليتم بالعجل {وإنَّ ربكم الرحمن} لا العجل {فاتبعوني} على ديني {وأطيعوا أمري}',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2440,'{قالوا لن نبرح عليه عاكفين} على عبادته مقيمين {حَتَّى يَرْجِعَ إِلَيْنَا مُوسَى} فلمَّا رجع موسى',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2441,'{قَالَ يَا هَارُونُ مَا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضلوا} ت أخطأوا الطَّريق بعبادة العجل {أن لا تتبعن} أن تتبعني وتلحق بي وتخبرني؟ {أفعصيت أمري} حيث أقمتَ فيما بينهم وهم يعبدون غير الله! ؟ ثمَّ أخذ شعر رأسه بيمينه ولحيته بشماله غضباً وإنكاراً عليه فقال:',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2442,'{أن لا تتبعن أفعصيت أمري}',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2443,'{يا ابن أُمَّ لا تَأْخُذْ بِلِحْيَتِي وَلا بِرَأْسِي إِنِّي خَشِيتُ أَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَنِي إِسْرَائِيلَ} خشيت إن فارقتهم واتبتعك أن يصيروا حزبين يقتل بعضهم بعضاً فتقول: أوقعتَ الفرقة فيما بينهم {ولم ترقب قولي} لم تحفظ وصيتي في حسن الخلافة عليهم ثمَّ أقبل موسى على السَّامري فقال:',NULL,'703',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2444,'{فما خطبك} فما قصَّتك وما الذي تخاطب به فيما صنعت؟',NULL,'704',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2445,'{قال: بصرت بما لم يبصروا به} علمت ما لم يعلمه بنو إسرائيل قال موسى: وما ذلك؟ قال: رأيت جبريل عليه السَّلام على فرس الحياة فأُلقي فِي نفسي أن أقبض من أثرها فما ألقيته على شيءٍ إلاَّ صار له روحٌ ولحمٌ ودمٌ فحين رأيتُ قومك سألوك أن تجعل لهم إلهاً زيَّنت لي نفسي ذلك فذلك قوله: {فقبضت قبضة من أثر الرسول فنبذتها} طرحتها في العجل {وكذلك سولت لي نفسي} حدثني نفسي',NULL,'704',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2446,'{قال} له موسى صلوات الله عليه: {فاذهب فإنَّ لك في الحياة} يعني: ما دمت حيَّاً {أَنْ تَقُولَ لا مِسَاسَ} لا تخالط أحداً ولا يخالطك وأمر موسى بني إسرائيل ألا يخالطوه وصار السَّامريُّ بحيث لو مسَّه أحدٌ أو مسَّ هو أحداً حُمَّ كلاهما {وإن لك موعداً} لعذابك {لن تخلفه} لن يُخلفكه الله {وانظر إلى إلهك} معبودك {الذي ظلت عليه عاكفاً} دمتَ عليه مقيماً تعبده {لنحرقنَّه} بالنَّار {ثمَّ لننسفنَّه} لنذرينَّه في البحر',NULL,'704',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2447,'{إِنَّمَا إِلَهُكُمُ اللَّهُ الَّذِي لا إِلَهَ إِلا هو} لا العجل {وسع كلَّ شيء علماً} علم كلَّ شيء علماً',NULL,'704',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2448,'{كذلك} كما قصصنا عليك هذه القصَّة {نَقُصُّ عَلَيْكَ مِنْ أَنْبَاءِ مَا قَدْ سَبَقَ} من الأمور {وَقَدْ آتَيْنَاكَ مِنْ لَدُنَّا ذِكْرًا} يعني: القرآن',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2449,'{من أعرض عنه} فلم يؤمن به {فَإِنَّهُ يَحْمِلُ يَوْمَ الْقِيَامَةِ وِزْرًا} حملاً ثقيلاً من الكفر',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2450,'{خالدين فيه} لا يغفر ربك لهم ذلك ولا يكفِّر عنهم شيء {وساء لهم يوم القيامة حملاً} بئس ما حملوا على أنفسهم من المآثم كفراً بالقرآن',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2451,'{يوم ينفخ في الصور ونحشر المجرمين} الذين اتَّخذوا مع الله إلهاً آخر {يومئذ زرقاً} زرق العيون سود الوجوه',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2452,'{يتخافتون} يتساررون {بينهم إن لبثتم} في قبوركم إلاَّ عشر ليالٍ يريدون ما بين النَّفختين وهو أربعون سنة يُرفع العذاب في تلك المدَّة عن الكفَّار فيستقصرون تلك المدَّة إذا عاينوا هول القيامة قال الله تعالى:',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2453,'{نَحْنُ أَعْلَمُ بِمَا يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طريقة} أعدلهم قولاً {إن لبثتم إلاَّ يوماً}',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2454,'{ويسألونك عن الجبا} سألوا النبي صلى الله عليه وسلم كيف تكون الجبال يوم القيامة؟ {فقل ينسفها ربي نسفاً} يصيِّرها كالهباء المنثور حتى تستوي مع الأرض وهو قوله:',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2455,'{فيذرها قاعاً صفصفاً} مكاناً مستوياً',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2456,'{لا ترى فيها عوجاً ولا أمتا} انخفاضا وارتفاعا',NULL,'705',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2457,'{يومئذ يتبعون الداعي} الذي يدعوهم إلى موقف القيامة ولا يقدرون ألا يتَّبعوا {وخشعت} سكنت {الأصوات للرحمن فلا تسمع إلاَّ همساً} وطء الأقدام نقلها إلى المحشر',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2458,'{يومئذ} يوم القيامة {لا تنفع الشفاعة} أحداً {إلاَّ من أذن له الرحمن} في أن يُشفَع له وهم المسلمون الذين رضي الله قولهم لأنَّهم قالوا: لا إله إلا الله وهذا معنى قوله: {وَرَضِيَ لَهُ قَوْلا}',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2459,'{يعلم ما بين أيديهم} من أمر الآخرة {وما خلفهم} من أمر الدُّنيا وقيل ما قدَّموا وما خلَّفوا من خيرٍ وشرٍّ {ولا يحيطون به علماً} وهم لا يعلمون ذلك',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2460,'{وعنت الوجوه} خضعت وذلَّت {لِلْحَيِّ الْقَيُّومِ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا} خسر مَنْ أشرك بالله',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2461,'{ومن يعمل من الصالحات} الطَّاعات لله {وهو مؤمن} مصدِّق بما جاء به محمد صلى الله عليه وسلم {فلا يخاف ظلماً ولا هضماً} لا يخاف أن يزاد في سيئاته ولا ينقص من حسناته',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2462,'{وكذلك} وهكذا {أنزلناه قرآناً عربياً وصرَّفنا} بيَّنا {فِيهِ مِنَ الْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لهم} القرآن {ذكراً} وموعظة',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2463,'{ولا تعجل بالقرآن} كان إذا نزل جبريل عليه السَّلام بالوحي يقرؤه مع جبريل عليه السَّلام مخافة النِّسيان فأنزل الله سبحانه: {ولا تعجل بالقرآن} أَيْ: بقراءته {مِنْ قَبْلِ أَنْ يُقْضَى إِلَيْكَ وحيه} من قبل أن يفرغ جبريل ممَّا يريد من التَّلاوة {وقل رب زدني علماً} بالقرآن وكان كلما نزل عليه شيء من القرآن ازداد به علماً',NULL,'706',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2464,'{ولقد عهدنا إلى آدم} أمرناه وأوصينا إليه {من قبل} هؤلاء الذين تركوا أمري ونفضوا عهدي في تكذيبك {فنسي} فترك ما أمر به {ولم نجد له عزماً} حفظاً لما أُمر به وقوله:',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2465,'{وَإِذْ قُلْنَا لِلْمَلائِكَةِ اسْجُدُوا لآدَمَ فَسَجَدُوا إِلا إبليس أبى}',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2466,'{فَقُلْنَا يَا آدَمُ إِنَّ هَذَا عَدُوٌّ لَكَ وَلِزَوْجِكَ فَلا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقَى}',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2467,'{إن لك أن لا تجوع فيها ولا تعرى}',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2468,'{ولا تضحى} أَيْ: لا يؤذيك حرُّ الشَّمس وقوله:',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2469,'{شجرة الخلد} يعني: مَنْ أكل منها لم يمت وقوله:',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2470,'{فغوى} فأخطأ ولم ينل مراده ممَّا أكل ويقال: لم يرشد',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2471,'{ثم اجتباه} اختاره {ربه فتاب عليه} عاد عليه بالرَّحمة والمغفرة {وهدى} أي: هداه إلى التوبة وقوله:',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2472,'{قَالَ اهْبِطَا مِنْهَا جَمِيعًا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمَّا يَأْتِيَنَّكُمْ مِنْي هُدًى فَمَنِ اتَّبَعَ هُدَايَ فلا يضل ولا يشقى}',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2473,'{من أعرض عن ذكري} موعظتي وهي القرآن {فإنَّ له معيشة ضنكاً} ضيقى يعني: في جهنَّم وقيل: يعني عذاب القبر {ونحشره يوم القيامة أعمى} البصر',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2474,'{قَالَ رَبِّ لِمَ حَشَرْتَنِي أَعْمَى وَقَدْ كُنْتُ بصيراً}',NULL,'707',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2475,'{قال كذلك أتتك آياتنا} يقول: كما أتتك آياتي {فَنَسِيتَهَا} فتركتها ولم تؤمن بها {وَكَذَلِكَ الْيَوْمَ تنسى} تُترك في جهنَّم',NULL,'708',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2476,'{وكذلك} وكما تجزي مَنْ أعرض عن القرآن {نجزي مَنْ أسرف} أشرك {ولعذاب الآخرة أشدُّ} ممَّا يُعذِّبهم به في الدُّنيا والقبر {وأبقى} وأدوم',NULL,'708',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2477,'{أفلم يهد لهم} أفلم يتبيَّن لهم بياناً يهتدون به {كم أهلكنا قبلهم من القرون يمشون} هؤلاء إذا سافروا في مساكن أولئك الذين أهلكناهم بتكذيب الأنبياء {إن في ذلك لآيات} لعبراً {لأولي النهى} لذوي العقول',NULL,'708',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2478,'{ولولا كلمة سبقت من ربك} في تأخير العذاب عنهم {لكان لزاماً} لكان العذاب لازماً لهم في الدُّنيا {وأجل مسمى} وهو القيامة وقوله:',NULL,'708',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2479,'{وسبح بحمد ربك} صل لربِّك {قبل طلوع الشمس} صلاة الفجر {وقبل غروبها} صلاة العصر {ومن آناء الليل فسبح} فصلِّ المغرب والعشاء الآخرة {وأطراف النهار} صلِّ صلاة الظُّهر في طرف النِّصف الثاني وسمَّى الواحد باسم الجمع {لعلك ترضى} لكي ترضى من الثَّواب في المعاد',NULL,'708',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2480,'{ولا تمدنَّ عينيك} مُفسَّر في سورة الحجر وقوله: {زهرة الحياة الدنيا} أَي: زينتها وبهجتها {لنفتنهم فيه} لنجعل ذلك فتنةً لهم {ورزق ربك} لك في المعاد {خير وأبقى} أكثر وأدوم',NULL,'708',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2481,'{وأمر أهلك بالصلاة} يعني: قريشاً وقيل: أهل بيته {لا نسألك رزقاً} لخلقنا ولا لنفسك {نحن نرزقك والعاقبة} الجنَّة {للتقوى} لأهل التَّقوى يعني: لك ولمن صدَّقك ونزلت هذه الآيات لمَّا استسلف رسول الله صلى الله عليه وسلم من يهوديٍّ وأبى أن يعطيه إلاَّ برهنٍ وحزن لذلك رسول الله صلى الله عليه وسلم',NULL,'709',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2482,'{وقالوا} يعني: المشركين {لولا} هلاَّ {يأتينا} محمَّد عليه السَّلام {بآية من ربه} ممَّا كانوا يقترحون من الآيات قال الله: {أَوَلَمْ تأتهم بيِّنة} بيان {ما في الصحف الأولى} يعني: في القرآن بيان ما في التَّوراة والإِنجيل والزَّبور',NULL,'709',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2483,'{ولو أنا أهلكناهم بعذاب من قبله} من قبل نزول القرآن وقوله: {من قبل أن نذل} بالعذاب {ونخزى} في جهنَّم',NULL,'709',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2484,'{قل} يا محمد لهم: {كلٌّ متربص} منتظرٌ دوائر الزَّمان ولمَنْ يكون النَّصر {فتربصوا فستعلمون} في القيامة {من أصحاب الصراط السويّ} المستقيم {ومن اهتدى} من الضَّلالة نحن أم أنتم',NULL,'709',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2485,'{اقترب للناس} يعني: أهل مكَّة {حسابهم} وقت محاسبة الله إيَّاهم على أعمالهم يعني: القيامة {وهم في غفلة} عن التَّأهُّب لذلك {معرضون} عن الإِيمان',NULL,'710',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2486,'{مَا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ} يعني: ما يحدث الله تعالى من تنزيل شيءٍ من القرآن يذكرهم ويعظمهم به {إلاَّ استمعوه وهم يلعبون} يستهزئون به',NULL,'710',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2487,'{لاهية} غافلةً {قلوبهم وأسروا النجوى} قالوا سرّاً فيما بينهم {الذين ظلموا} أشركوا وهو أنَّهم قالوا: {هل هذا} يعنون محمَّداً {إلاَّ بشرٌ مثلكم} لحمٌ ودمٌ {أفتأتون السحر} يريدون: إنَّ القرآن سحرٌ {وأنتم تبصرون} أنَّه سحر فلمَّا أطلع الله سبحانه نبيه صلى الله عليه وسلم على هذا السِّرِّ الذي قالوه أخبر أنَّه يعلم القول في السَّماء والأرض بقوله:',NULL,'710',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2488,'{قال ربي يعلم القول} أَيْ: ما يقال {في السماء والأرض وهو السميع} للأقوال {العليم} بالأفعال ثمَّ أخبر انَّ المشركين اقتسموا القول في القرآن وأخذوا ينقضون أقوالهم بعضها ببعض فيقولون مرَّةً:',NULL,'711',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2489,'{أضغاث أحلام} أَيْ: أباطيلها يعنون أنَّه يرى ما يأتي به في النَّوم رؤيا باطلة ومرَّةً هو مفترىً ومرَّةً هو شعرٌ ومحمَّد شاعرٌ {فليأتنا بآية كما أرسل الأولون} بالآيات مثل: النَّاقة والعصا واليد فاقترحوا الآيات التي لا يقع معها إمهالٌ إذا كُذِّب بها فقال الله تعالى:',NULL,'711',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2490,'{ما آمنت قبلهم من قرية أهلكناها} بالآيات التي اقترحوها {أفهم يؤمنون} يريد: إنَّ اقتراح الآيات كان سبباً للعذاب والاستئصال للقرون الماضية وكذلك يكون لهؤلاء',NULL,'711',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2491,'{وَمَا أَرْسَلْنَا قَبْلَكَ إِلا رِجَالا نُوحِي إِلَيْهِمْ} ردّاً لقولهم {هل هذا إلاَّ بشر مثلكم} {فاسألوا} يا أهل مكَّة {أهل الذكر} مَنْ آمن من أهل الكتاب {إن كنتم لا تعلمون} أنَّ الرُّسل بشر',NULL,'711',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2492,'{وما جعلناهم} أي: الرُّسل {جسداً} أَيْ: أجساداً {لا يأكلون الطعام} وهذا ردٌّ لقولهم: {مال هذا الرسول يأكل الطعام} فأعملوا أنَّ الرُّسل جميعاً كانوا يأكلون الطَّعام وأنَّهم يموتون وهو قوله: {وما كانوا خالدين}',NULL,'711',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2493,'{ثم صدقناهم الوعد} ما وعدناهم من عذاب مَنْ كفر بهم وإنجائهم مع مَنْ تابعهم وهو قوله: {فأنجيناهم ومَنْ نشاء وأهلكنا المسرفين} المشركين',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2494,'{لَقَدْ أَنْزَلْنَا إِلَيْكُمْ} يا معشر قريش {كِتَابًا فيه ذكركم} شرفكم {أفلا تعقلون} ما فضلتكم به على غيركم؟',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2495,'{وكم قصمنا} أهلكنا {من قرية كانت ظالمة} يعني: إنَّ أهلها كانوا كفَّاراً {وأنشأنا} أحدثنا {بعدها} بعد إهلاك أهلها {قوماً آخرين} نزلت في أهل قرىً باليمن كذَّبوا نبيَّهم وقتلوه فسلَّط الله سبحانه عليهم بختنصَّر حتى أهلكهم بالسَّيف فذلك قوله:',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2496,'{فلما أحسوا بأسنا} رأوا عذابنا {إذا هم منها} من قريتهم {يركضون} يسرعون هاربين وتقول لهم الملائكة:',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2497,'{لا تركضوا وارجعوا إلى ما أترفتم فيه} نَعِمْتُم فيه {لعلكم تسألون} من دنياكم شيئاً قالت الملائكة لهم هذا على سبيل الاستهزاء بهم كأنَّهم قيل لهم: ارجعوا إلى ما كنتم فيه من المال والنِّعمة لعلكم تُسألون فإنَّكم أغنياء تملكون المال فلمَّا رأَوا ذلك أقرُّوا على أنفسهم حيث لم ينفعهم فقالوا:',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2498,'{يا ويلنا إنا كنا ظالمين} لأنفسنا بتكذيب الرُّسل',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2499,'{فما زالت} هذه المقالة {دعواهم} يدعون بها ويقولون: يا ويلنا {حتى جعلناهم حصيداً} بالسُّيوف كما يحصد الزَّرع {خامدين} ميِّتين',NULL,'712',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2500,'{وَمَا خَلَقْنَا السَّمَاءَ وَالأَرْضَ وَمَا بَيْنَهُمَا لاعِبِينَ} عبئا وباطلاً أَيْ: ما خلقتهما إلاَّ لأُجازي أوليائي وأُعذِّب أعدائي',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2501,'{لو أردنا أن نتخذ لهواً} امرأةً وقيل: ولداً {لاتخذناه من لدنا} بحيث لا يظهر لكم ولا تطَّلعون عليه {إن كنا فاعلين} ما كنَّا فاعلين ولسنا من يفعله',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2502,'{بل نقذف بالحق على الباطل} نُلقي القرآن على باطلهم {فيدمغه} فيذهبه ويكسره {فإذا هو زاهقٌ} ذاهبٌ {ولكم الويل} ما معشر الكفَّار {مما تصفون} الله تعالى بما لا يليق به',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2503,'{وله من في السماوات وَالأَرْضِ} عبيداً وملكاً {ومَنْ عنده} يعني: الملائكة {لا يستكبرون عن عبادته ولا يستحسرون} لا يملُّون ولا يعيون',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2504,'{يسبحون الليل والنهار لا يفترون} لا يضعفون',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2505,'{أم اتخذوا آلهةً من الأرض} يعني: الأصنام {هم ينشرون} يحيون الأموات والمعنى: أنتشر آلهتهم التي اتَّخذوها؟',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2506,'{لو كان فيهما} في السَّماء والأرض {آلهةٌ إلاَّ اللَّهُ} غير الله {لفسدتا} لخربتا وهلك مَنْ فيهما بوقوع التَّنازع بين الآلهة',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2507,'{لا يُسأل عما يفعل} عن حكمه في عباده {وهم يُسألون} عمَّا عملوا سؤال توبيخ',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2508,'{أَمِ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً قُلْ هَاتُوا برهانكم} حجَّتكم على أن مع الله تعالى معبوداً غيره {هذا ذكر مَنْ معي} يعني: القرآن {وذكر مَنْ قبلي} يعني: التَّوراة والإِنجيل فهل في واحدٍ من هذه الكتب إلاَّ توحيد سبحانه وتعالى؟ {بل أكثرهم لا يعلمون الحق} فلا يتأمَّلون حجَّة التَّوحيد وهو قوله: {فهم معرضون}',NULL,'713',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2509,'{وما أرسلنا من قبلك من رسولٍ} الآية يريد: لم يُبعثْ رسولٌ إلاَّ بتوحيد الله سبحانه ولم يأتِ رسولٌ أُمّته بأنَّ لهم إلهاً غير الله',NULL,'714',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2510,'{وقالوا اتخذ الرحمن ولدا} يعني: الذين قالوا: الملائكة بنات الله والمعنى: وقالوا: اتَّخذ الرحمن ولداً من الملائكة {سبحانه} ثمَّ نزَّه نفسه عمَّا يقولون {بل} هم {عباد مكرمون} يعني: الملائكة مكرمون بإكرام الله إيَّاهم',NULL,'714',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2511,'{لا يَسْبِقُونَهُ بِالْقَوْلِ} لا يتكلَّمون إلاَّ بما يأمرهم به {وَهُمْ بِأَمْرِهِ يَعْمَلُونَ}',NULL,'714',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2512,'{يعلم ما بين أيديهم وما خلفهم} ما علموا وما هم عاملون {وَلا يَشْفَعُونَ إِلا لِمَنِ ارتضى} لمن قال: لا إله إلاَّ الله {وهم من خشيته مشفقون} خائفون لأنَّهم لا يأمنون مكر الله',NULL,'714',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2513,'{ومَنْ يقل منهم} من الملائكة {إني إلهٌ من دونه} من دون الله تعالى {فذلك نجزيه جهنم} يعني: إبليس حيث ادَّعى الشِّركة في العبادة ودعا إلى عبادة نفسه {كذلك نجزي الظالمين} المشركين الذين يعبدون غير الله تعالى',NULL,'714',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2514,'{أَوَلَمْ يَر} أولم يعلم {الذين كفروا أن السماوات والأرض كانتا رتقاً} مسدودةً {ففتقناهما} بالماء والنَّبات كانت السَّماء لا تُمطر والأرض لا تُنبت ففتحهما الله سبحانه بالمطر والنَّبات {وجعلنا من الماء} وخلقنا من الماء {كلَّ شيء حي} يعني: إنَّ جميع الحيوانات مخلوقةٌ من الماء كقوله تعالى: {والله خلق كلَّ دابَّةٍ من ماءٍ} ثمَّ بكَّتهم على ترك الإيمان فقال: {أفلا يؤمنون} وقوله:',NULL,'714',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2515,'{وجعلنا فيها} في الرَّواسي {فجاجاً سبلاً} طرقاً مسلوكةً يهتدوا',NULL,'715',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2516,'{وجعلنا السماء سقفاً محفوظاً} بالنُّجوم من الشَّياطين {وهم عن آياتها} شمسها وقمرها ونجومها {معرضون} لا يتفكَّرون فيها وقوله:',NULL,'715',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2517,'{كلٌّ في فلك يسبحون} يجرون ويسيرون والفَلَكُ: مدار النُّجوم',NULL,'715',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2518,'{وما جَعَلْنا لبشر من قبلك الخلد} دوام البقاء {أفإن مت فهم الخالدون} نزل حين قالوا: {نتربَّصُ به ريَب المنون} وقوله:',NULL,'715',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2519,'{ونبلوكم} نختبركم {بالشر} بالبلايا والفقر {والخير} المال والصِّحة {فتنة} ابتلاءً لننظر كيف شكركم وصبركم',NULL,'715',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2520,'{وإذا رآك الذين كفروا} يعني: المستهزئين {إن يتخذونك} ما يتَّخذونك {إلاَّ هزواً} مهزوءاً به قالوا: {أهذا الذي يذكر آلهتكم} يعيب أصنامكم {وهم بذكر الرحمن هم كافرون} جاحدون إلهيَّته يريد أنَّهم يعيبون مَنْ جحد إليهة أصنامهم وهم جاحدون إليهة الرَّحمن وهذا غاية الجهل',NULL,'715',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2521,'{خلق الإنسانُ من عجل} يريد: إنَّ خلقته على العجلة وعليها طُبع {سَأُرِيكم آياتي} يعني: ما توعدون به من العذاب {فلا تستعجلون}',NULL,'617',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2522,'{ويقولون متى هذا الوعد} وعد القيامة',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2523,'{لو يعلم الذين كفروا} الآية وجواب لو محذوف على تقدير: لآمنوا ولما أقاموا على الكفر',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2524,'{بل تأتيهم} القيامة {بغتة} فجأة {فتبهتهم} تحيرهم',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2525,'{وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحَاقَ بِالَّذِينَ سخروا منهم ما كانوا به يستهزئون}',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2526,'{قل مَنْ يَكْلَؤُكُمْ} يحفظكم {بالليل والنهار من الرحمن} إن أنزل بكم عذابه {بل هم عن ذكر ربهم} كتاب ربِّهم {معرضون}',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2527,'{أَمْ لَهُمْ آلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنَا لا يستطيعون نصر أنفسهم} فكيف تنصرهم وتمنعهم؟ ! {ولا هم منا يصحبون} لا يُجارون من عذابنا',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2528,'{بل متَّعنا هؤلاء} الكفَّار {وآباءهم حتى طال عليهم العمر} أَيْ: متَّعناهم بما أعطيناهم من الدُّنيا زماناً طويلاً فقست قلوبهم {أَفَلا يَرَوْنَ أَنَّا نَأْتِي الأَرْضَ نَنْقُصُهَا مِنْ أطرافها} بالفتح على محمد صلى الله عليه وسلم {أفهم الغالبون} أم النبيُّ صلى الله عليه وسلم وأصحابه؟',NULL,'716',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2529,'{قل إنما أنذركم} أُخوِّفكم {بالوحي} بالقرآن الذي أوحي إليَّ وأُمرت فيه بإنذاركم {وَلا يَسْمَعُ الصُّمُّ الدُّعَاءَ إِذَا مَا يُنْذَرُونَ} كذلك أنتم يا معشر المشركين',NULL,'717',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2530,'{ولئن مستهم} أصابتهم {نفحة من عذاب ربك} قليلٌ وأدنى شيءٍ لأقرّوا على أنفسهم بسوء صنيعهم وهو قوله: {لَيَقُولُنَّ يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ}',NULL,'717',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2531,'{ونضع الموازين القسط} ذوات القسط أي: العدل {فلا تظلم نفسٌ شيئاً} لا يزاد على سيئاته ولا ينقص من ثواب حسناته {وإن كان} ذلك الشَّيء {مثقال حبة} وزن حبَّةٍ {من خردل أتينا بها} جئنا بها {وكفى بنا حاسبين} مُجازين وفي هذا تهديد',NULL,'717',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2532,'{ولقد آتينا موسى وهارون الفرقان} البرهان الذي فرَّق به بين حقّه وباطل فرعون {وضياء} يعني: التَّوراة الذي كان ضياءً يُضيء هدى ونوراً {وذكراً} وعِظَةً {للمتقين} من قومه',NULL,'717',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2533,'{الذين يخشون ربهم بالغيب} يخافونه ولم يروه',NULL,'717',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2534,'{وهذا ذكر مبارك} يعني: القرآن {أفأنتم له منكرون} جاحدون',NULL,'717',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2535,'{ولقد آتينا إبراهيم رشده} هُداه وتوفيقه {من قبل} من قبل موسى وهارون {وكنا به عالمين} أنَّه أهلٌ لما آتيناه',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2536,'{إِذْ قَالَ لأَبِيهِ وَقَوْمِهِ مَا هَذِهِ التَّمَاثِيلُ} الأصنام {التي أنتم لها عاكفون} على عبادتها مقيمون!',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2537,'{قالوا وجدنا آباءنا لها عابدين} فاقتدينا بهم',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2538,'{قَالَ لَقَدْ كُنْتُمْ أَنْتُمْ وَآبَاؤُكُمْ فِي ضَلالٍ مبين}',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2539,'{قالوا أجئتنا بالحق} يعنون: أَجادٌّ أنت فيما تقول أم لاعبٌ؟',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2540,'{قال بل ربكم رب السماوات وَالأَرْضِ الَّذِي فَطَرَهُنَّ وَأَنَا عَلَى ذَلِكُمْ مِنَ الشاهدين} أي: أشهد على أنَّه خالقها',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2541,'{وتالله لأكيدن أصنامكم} لأمركن بها {بعد أن تولوا مدبرين} قال ذلك في يوم عيدٍ لهم وهم يذهبون إلى الموضع الذي يجتمعون فيه',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2542,'{فجعلهم جذاذاً} حطاماً ودقاقاً {إلاَّ كبيراً لهم} عظيم الآلهة فإنَّه لم يكسره {لعلهم إليه} إلى إبراهيم ودينه {يرجعون} إذا قامت الحُجَّة عليهم فلمَّا انصرفوا',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2543,'{قالوا من فعل هذا بآلهتنا} ت الآية قال الذين سمعوا قوله: {لأكيدن أصنامكم}',NULL,'718',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2544,'{سمعنا فتى يذكرهم} يعيبهم {قال له إبراهيم}',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2545,'{قالوا فأتوا به على أعين الناس} على رؤوس النَّاس بمرأىً منهم {لعلَّهم يشهدون} عليه أنَّه الذي فعل ذلك وكرهوا أن يأخذوه بغير بيِّنةٍ فلمَّا أتوا به',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2546,'{قَالُوا أَأَنْتَ فَعَلْتَ هَذَا بِآلِهَتِنَا يَا إِبْرَاهِيمُ}',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2547,'{قال بل فعله كبيرهم هذا} غضب من أن يعبدوا معه الصِّغار وأراد إقامه الحجَّة عليهم {فاسألوهم} مَنْ فعل بهم هذا {إن كانوا ينطقون} ت إن قدروا على النُّطق',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2548,'{فرجعوا إلى أنفسهم} تفكَّروا ورجعوا إلى عقولهم {فقالوا إنكم أنتم الظالمون} هذا الرجل بسؤالكم إيَّاه وهذه آلهتكم حاضرةٌ فاسألوها',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2549,'{ثم نكسوا على رؤوسهم} أطرقوا لما لحقهم من الخجل وأقرُّوا بالحجَّة عليهم فقالوا: {لقد علمت ما هؤلاء ينطقون} فلمَّا اتَّجهت الحجَّة عليهم قال إبراهيم:',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2550,'{أَفَتَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لا يَنْفَعُكُمْ شيئاً ولا يضركم}',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2551,'{أفٍ لكم} أَيْ: نتناً لكم فلما عجوا عن الجواب',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2552,'{قالوا حرّقوه} بالنَّار {وانصروا آلهتكم} بإهلاك مَنْ يعيبها {إن كنتم فاعلين} أمراً في أهلاكه فلمَّا ألقوه في النَّار',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2553,'{قُلْنَا يَا نَارُ كُونِي بَرْدًا وَسَلامًا عَلَى إبراهيم} ذات بردٍ وسلامةٍ لا يكون فيها بردٌ مضرٌّ ولا حرٌّ مُؤذٍ',NULL,'719',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2554,'{وأرادوا به} بإبراهيم {كيداً} مكراً في إهلاكه {فجعلناهم الأخسرين} حين لم يرتفع مرادهم في الدُّنيا ووقعوا في العذاب في الآخرة',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2555,'{ونجيناه} من نمروذ وقومِه {ولوطاً} ابن أخيه {إلى الأرض التي باركنا فيها للعالمين} وهي الشَّام وذلك أنَّه خرج مهاجراً من أرض العراق إلى الشَّام',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2556,'{ووهبنا له إسحاقَ} ولداً لصلبه {ويعقوب نافلة} ولد الولد {وكلاً جعلنا صالحين} يعني: هؤلاء الثَّلاثة',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2557,'{وجعلناهم أئمة} يُقتدى بهم في الخير {يهدون} يدعون النَّاس إلى ديننا {بأمرنا وأوحينا إليهم فعل الخيرات} أن يفعلوا الطَّاعات ويقيموا الصَّلاة ويؤتوا الزَّكاة',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2558,'{ولوطاً آتيناه حكماً} فصلاً بين الخصوم بالحقِّ {وَنَجَّيْنَاهُ مِنَ الْقَرْيَةِ الَّتِي كَانَتْ تَعْمَلُ الْخَبَائِثَ} يعني: أهلها كانوا يأتون الذُّكران في أدبارهم',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2559,'{وأدخلناه في رحمتنا إنه من الصالحين}',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2560,'{ونوحاً إذ نادى من قبل} من قبل إبراهيم {فنجيناه وأهله من الكرب العظيم} الغمِّ الذي كان فيه من أذى قومه',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2561,'{ونصرناه} منعناه من أن يصلوا إليه بسوء وقوله:',NULL,'720',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2562,'{إذ يحكمان في الحرث} قيل: كان ذلك زرعاً وقيل: كان كرماً {إذ نفشت} رعت ليلاً {فيه غنم القوم} بلا راعٍ {وكنا لحكمهم شاهدين} لم يغبْ عن علمنا',NULL,'721',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2563,'{ففهمناها سليمان} ففهمنا القضيَّة سليمان دون داود عليهما السَّلام وذلك أنَّ داود حكم لأهل الحرث برقاب الغنم وحكم سليمان بمنافعهم إلى أن يعود الحرث كما كان {وسخرنا مع داود الجبال يسبحن} يجاوبنه بالتَّسبيح {و} كذلك {الطير وكنا فاعلين} ذلك',NULL,'721',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2564,'{وعلمناه صنعة لبوس لكم} علم ما يلبسونه من الذروع {لتحصنكم} لتحرزكم {من بأسكم} من حربكم {فهل أنتم شاكرون} نعمتنا عليكم؟',NULL,'721',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2565,'{ولسليمان الريح} وسخرنا له الرِّيح {عاصفة} شديدة الهبوب {تَجْرِي بِأَمْرِهِ إِلَى الأَرْضِ الَّتِي بَارَكْنَا فِيهَا} يعني: الشَّام وكان منزل سليمان عليه السَّلام بها',NULL,'721',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2566,'{ومن الشياطين} وسخَّرنا له من الشَّياطين {من يغوصون له} يدخلون تحت الماء لاستخراج جواهر البحر {ويعملون عملاً دون ذلك} سوى الغوص {وكنا لهم حافظين} من أن يُفسدوا ما عملوا وليبصروا تحت أمره',NULL,'721',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2567,'{وأيوب إذ نادى ربه} دعا ربَّه {أني مسني الضرُّ} أصابني الجهد وقوله:',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2568,'{وآتيناه أهله ومثلهم معهم} وهو أنَّ الله تعالى أحيا مَنْ أمات من بنيه وبناته ورزقه مثلهم من الولد {رحمة} نعمةً {من عندنا وذكرى للعابدين} عظةً لهم ليعلموا بذلك كما قدرتنا وقوله:',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2569,'{وذا الكفل} هو رجلٌ من بني إسررئيل تكفَّل بخلافه نبيٍّ في أمته فقام بذلك',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2570,'قال تعالى {وأدخلناهم في رحمتنا إنهم من الصالحين}',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2571,'{وذا النون} وذاكر صاحب الحوت وهو يونس عليه السَّلام {إذ ذهب} من بين قومه {مغاضباً} لهم قبل أمرنا له بذلك {فظن أن لن نقدر عليه} أن لن نقضي عليه ما قضينا من حبسه في بطن الحوت {فنادى في الظلمات} ظلمة بطن الحوت وظلمة البحر وظلمة اللَّيل {أَنْ لا إِلَهَ إِلا أَنْتَ سُبْحَانَكَ إِنِّي كنت من الظالمين} حيث غاضبت قومي وخرجت من بينهم قبل الإذن',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2572,'{وكذلك} وكما نجيناه {ننجي المؤمنين} من كربهم إذا استغاثوا بنا ودعونا وقوله:',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2573,'{لا تذرني فرداً} أَيْ: وحيداً لا ولد لي ولا عقب {وأنت خير الوارثين} خير من يبقى بعد من يموت وقوله:',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2574,'{وأصلحنا له زوجه} بأن جعلناها ولوداً بعد أن صارت عقيماً {إنهم كانوا يسارعون في الخيرات} يُبادرون في عمل الطَّاعات {ويدعوننا رغباً} في رحمتنا {ورهباً} من عذابنا {وكانوا لنا خاشعين} عابدين في تواضع',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2575,'{والتي أحصنت} واذكر التي منعت {فرجها} من الحرام {فنفخنا فيها من روحنا} أمرنا جبريل عليه السَّلام حتى نفخ في جيب درعها والمعنى: أجرينا فيها روح المسيح المخلوقة لنا {وجعلناها وابنها آية للعالمين} دلالةً لهم على كمال قدرتنا وكانت الآيةُ فيهما جميعاً واحدةً لذلك وُحِّدت',NULL,'723',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2576,'{إنَّ هذه أمتكم} دينكم وملَّتكم {أمة واحدة} ملَّة واحة وهي الإِسلام',NULL,'723',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2577,'{وتقطعوا أمرهم بينهم} اختلفوا في الدّين فصاروا فرقاً {كلٌّ إلينا راجعون} فنجزيهم بأعمالهم',NULL,'723',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2578,'{فمن يعمل من الصالحات} الطَّاعات {وهو مؤمنٌ} مصدِّق بمحمَّدٍ عليه السَّلام {فلا كفران لسعيه} لا نُبطل عمله بل نُثيبه {وإنا له كاتبون} ما عمل حتى نجازيه',NULL,'723',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2579,'{وحرام على قرية} يعني: قريةً كافرةً {أهلكناها} أهلكناها بعذاب الاستئصال أن يرجعوا إلى الدُّنيا ولا زائدةٌ في الآية ومعنى حرامٌ عليهم أنَّهم ممنوعون من ذلك لأنَّ الله تعالى قضى على مَنْ أُهلك أن يبقى في البرزخ إلى يوم القيامة',NULL,'723',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2580,'{حتى إذا فتحت يأجوج ومأجوج} من سدِّها {وهم من كل حدب} نشر وتلٍّ {ينسلون} ينزلون مسرعين',NULL,'723',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2581,'{واقترب الوعد الحق} يعني: القيامة والواو زائدة لأنَّ اقترب جواب حتى {فإذا هي شاخصة} ذاهبةٌ لا تكاد تطرف من هول ذلك اليوم يقولون {يَا وَيْلَنَا قَدْ كُنَّا فِي غَفْلَةٍ مِنْ هذا} في الدُّنيا عن هذا اليوم {بل كنا ظالمين} بالشِّرك وتكذيب الرُّسل',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2582,'{إنكم} أيُّها المشركون {وما تعبدون من دون الله} يعني: الأصنام {حصب جهنم} وقودها {أنتم لها واردون} فيها داخلون',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2583,'{لو كان هؤلاء} الأصنام {آلهة} على الحقيقة ما دخلوا النَّار {وكلٌّ} من العابدين والمعبودين في النَّار {خالدون}',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2584,'{إن الذين سبقت لهم منا الحسنى} السَّعادة والرَّحمة {أولئك عنها} عن النَّار {مبعدون}',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2585,'{إِنَّ الَّذِينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنَى أُولَئِكَ عنها مبعدون}',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2586,'{لا يسمعون حسيسها} صوتها',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2587,'{لا يحزنهم الفزع الأكبر} يعني: الإِطباق على النَّار وقيل: ذبح الموت بمرأىً من الفريقين {وتتلقاهم الملائكة} تستقبلهم فيقولون لهم {هذا يومكم الذي كنتم توعدون} للثواب ودخلوا الجنَّة',NULL,'724',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2588,'{يوم نطوي السماء كطي السجل للكتب} وهو مَلَكٌ يطوي بني آدم وقيل: السِّجلُّ: الصَّحيفة والمعنى: كطيِّ السِّجل على ما فيه من المكتوب {كما بدأنا أوَّل خلق نعيده} كما خلقناكم ابتداءً حُفاةً عُراةً غُرلاً كذلك نعيدهم يوم القيامة {وعداً علينا} أَيْ: وعدناه وعداً {إنا كنَّا فاعلين} يعني: الإِعادة والبعث',NULL,'725',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2589,'{وَلَقَدْ كَتَبْنَا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ} قيل: في الكتب المنزلة بعد التَّوراة وقيل: أراد بالذِّكر اللَّوح المحفوظ {أنَّ الأرض} يعني: أرض الجنَّة {يرثها عبادي الصالحون} وقيل: أرض الدُّنيا تصير للمؤمنين من أمة محمد صلى الله عليه وسلم',NULL,'725',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2590,'{إنَّ في هذا} القرآن {لبلاغاً} لوصولاً إلى البغية {لقوم عابدين} مُطيعين لله تعالى',NULL,'725',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2591,'{وما أرسلناك إلاَّ رحمة للعالمين} للبَرِّ والفاجر فمن أطاعه عُجِّلت له الرَّحمة ومَنْ عصاه وكذَّبه لم يحلقه العذاب في الدُّنيا كما لحق الأمم المكذبة',NULL,'725',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2592,'{قُلْ إِنَّمَا يُوحَى إِلَيَّ أَنَّمَا إِلَهُكُمْ إِلَهٌ وَاحِدٌ فَهَلْ أَنْتُمْ مُسْلِمُونَ}',NULL,'725',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2593,'{فإن تولوا} عن الإسلام {فقل آذنتكم على سواءٍ} أعلمتكم بما يوحى إليَّ على سواءٍ لتستووا في ذلك يريد: لم أُظهر لبعضكم شيئاً كتمته عن غيره {وإن أدري} ما أعلم {أقريب أم بعيد ما توعدون} يعني: القيامة',NULL,'725',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2594,'{إِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تكتمون}',NULL,'726',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2595,'{وإن أدري لعله} ت لعلَّ تأخير العذاب عنكم {فتنة} اختبارٌ لكم {ومتاعٌ إلى حين} إلى حين الموت',NULL,'726',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2596,'{قال رب احكم بالحق} اقض بيني وبين أهل مكَّة بالحقِّ أُمر أن يقول كما قالت الرُّسل قبله من قولهم: {ربَّنا افتحْ بيننا وبينَ قومِنا بالحقِّ} {وربنا} أَيْ: وقل ربُّنا {الرحمن المستعان على ما تصفون} من كذبكم وباطلكم',NULL,'726',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2597,'{يا أيها الناس} يا أهل مكة {اتقوا ربكم} أطيعوه {إن زلزلة الساعة شيء عظيم} وهي زلزلةٌ يكون بعدها طلوع الشَّمس من مغربها',NULL,'727',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2598,'{يوم ترونها} يعني: الزَّلزلة {تذهل كلُّ مرضعة عمَّأ أرضعت} تتلاك كلُّ امرأةٍ تُرضع ولدها الرضيع اشتعالا بنفسها وخوفاً {وتَضَعُ كلُّ ذات حملٍ حملها} تُسقط ولدها من هول ذلك اليوم {وَتَرَى النَّاسَ سُكَارَى} من شدَّة الخوف {وَمَا هم بسكارى} من الشَّراب {ولكنَّ عذاب الله شديد} فهم يخافونه',NULL,'727',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2599,'{وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ علم} نزلت في النَّضر بن الحارث وجماعةٍ من قريش كانوا يُنكرون البعث ويقولون: القرآن أساطير الأولين ويجادلون النبي صلى الله عليه وسلم {ويتبع} في جداله ذلك {كلَّ شيطان مريد} متمرِّدٍ عاتٍ',NULL,'727',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2600,'{كُتب عليه} قُضي على الشَّيطان {أنَّه مَنْ تولاَّه} اتَّبعه {فإنه يضله ويهديه إلى عذاب السعير} يدعوه إلى النَّار بما يُزيِّن له من الباطل',NULL,'728',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2601,'{يا أيها الناس} يعني: كفار مكَّة {إِنْ كُنْتُمْ فِي رَيْبٍ مِنَ الْبَعْثِ} شكٍّ من الإِعادة {فإنا خلقناكم} خلقنا أباكم الذي هو أصل البشر {من تراب ثمَّ} خلقنا ذريَّته {من نطفة ثمَّ من علقة} وهي الدَّم الجامد {ثمَّ من مضغة} وهي لحمةٌ قليلةٌ قدر ما يُمضغ {مُخَلَّقَةٍ} مصوَّرةٍ تامَّة الخلق {وغير مخلقة} وهي ما تمجُّه الأرحام دماً يعني: السِّقط {لنبيِّن لكم} كمال قدرتنا بتصريفنا أطوار خلقكم {ونقرُّ في الأرحام ما نشاء} ننزل فيها ما لا يكون سقطاً {إلى أجل مسمى} إلى وقت خروجه {ثم نخرجكم} من بطون الأمهات {طفلاً} صغاراً {ثمَّ لتبلغوا أَشدكم} عقولكم ونهاية قوَّتكم {ومنكم من يُتوفَّى} يموت قَبْلُ بلوغ الأشدِّ {وَمِنْكُمْ مَنْ يُرَدُّ إِلَى أرذل العمر} وهو الهرم والخرف حتى لا يعقل شيئاً وهو قوله: {لكيلا يعلم من بعد علم شيئاً} ثمَّ ذكر دلالةً أخرى على البعث فقال: {وترى الأرض هامدة} جافَّةً ذات ترابٍ {فإذا أنزلنا عليها الماء} المطر {اهتزت} تحركت بالنبات {وربت} زادت {وأنبتت من كلِّ زوج بهيج} من كلِّ صنفٍ حسنٍ من النَّبات',NULL,'728',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2602,'{ذلك} الذي تقدَّم ذكره من اختلاف أحوال خلق الإِنسان وإحياء الأرض بالمطر {بأن الله هو الحق} الدَّائم الثَّابت الموجود {وَأَنَّهُ يُحْيِي الْمَوْتَى وَأَنَّهُ عَلَى كُلِّ شَيْءٍ قدير}',NULL,'728',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2603,'{وَأَنَّ السَّاعَةَ آتِيَةٌ لا رَيْبَ فِيهَا وَأَنَّ اللَّهَ يَبْعَثُ مَنْ فِي الْقُبُورِ}',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2604,'{وَمِنَ النَّاسِ مَنْ يُجَادِلُ فِي اللَّهِ بِغَيْرِ علم} نزلت في أبي جهل {ولا هدىً} ليس معه من ربِّه رشادٌ ولا بيانٌ {ولا كتابٍ} له نورٌ',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2605,'{ثاني عطفه} لاوي عنقه تكبُّراً {ليضل} الناس عن طاعة الله سبحانه باتِّباع محمَّد عليه السَّلام {له في الدنيا خزي} يعني: القتل ببدرٍ',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2606,'{ذلك بما قدَّمت يداك} هذا العذاب بما كسبْتَ {وأن الله ليس بظلام للعبيد} لا يعاقب بغير جرمٍ',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2607,'{وَمِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلَى حَرْفٍ} على جانبٍ لا يدخل فيه دخول مُتمكِّنٍ {فإن أصابه خير} خِصبٌ وكثرةُ مالٍ {اطمأنَّ به} في الدِّين بذلك الخصب {وإن أصابته فتنة} اختبارٌ بجدبٍ وقلَّة مالٍ {انقلب على وجهه} رجع عن دينه إلى الكفر',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2608,'{يَدْعُو مِنْ دُونِ اللَّهِ مَا لا يَضُرُّهُ} إن عصاه {ولا ينفعه} إن أطاعه {ذلك هو الضلال البعيد} الذَّهاب عن الحقِّ',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2609,'{يدعو لمَنْ ضرُّه أقرب من نفعه} ضرره بعبادته أقرب من نفعه ولا ينفع عنده والعرب تقول لما لا يكون: هو بعيدٌ والمعنى في هذا أنَّه يضرُّ ولا ينفع {لبئس المولى} الناصر {ولبئس العشير} الصاحب والخليط',NULL,'729',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2610,'{إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ إِنَّ اللَّهَ يَفْعَلُ مَا يُرِيدُ}',NULL,'730',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2611,'{مَنْ كَانَ يَظُنُّ أَنْ لَنْ يَنْصُرَهُ اللَّهُ} محمدا صلى الله عليه وسلم حتى يُظهره على الدِّين كلِّه فليمت غيظاً وهو تفسير قوله: {فَلْيَمْدُدْ بِسَبَبٍ إِلَى السَّمَاءِ} أَيْ: فليشدد حبلاً في سقفه {ثمَّ ليقطع} أَيْ: ليَمُدَّ الحبل حتى ينقطع فيموت مختلفا {فلينظر هل يُذْهِبَنَّ كيده ما يغيظ} غيظه وقوله:',NULL,'730',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2612,'{وَكَذَلِكَ أَنْزَلْنَاهُ آيَاتٍ بَيِّنَاتٍ وَأَنَّ اللَّهَ يَهْدِي من يريد}',NULL,'730',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2613,'{إنَّ الله يفصل بينهم يوم القيامة} أَيْ: يحكم ويقضي بأن يدخل المؤمنين الجنَّة وغيرهم من هؤلاء الفرق النَّار {إنَّ الله على كل شيء شهيد} يريد: إنَّ اللَّهَ عالمٌ بما في قلوبهم',NULL,'730',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2614,'{ألم تر أنَّ الله يسجد له} يذلُّ له وينقاد له {من في السماوات وَمَنْ فِي الأَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُومُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَابُّ وَكَثِيرٌ مِنَ النَّاسِ وَكَثِيرٌ حَقَّ عليه العذاب} وذلك أنَّ كلَّ شيءٍ منقاد لله عز وجل على ما خلقه وعلى ما رزقه وعلى ما أصحَّه وعلى ما أسقمه فالبر والفاجر والمؤمن والكافر في هذا سواءٌ {ومَنْ يهن الله} يذلَّه بالكفر {فما له من مكرم} أحدٌ يكرمه {إنَّ الله يفعل ما يشاء} يُهين من يشاء بالكفر ويكرم من يشاء بالإيمان',NULL,'730',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2615,'{هذان خصمان} يعني: المؤمنين والكافرين {اختصموا في ربهم} في دينه {فَالَّذِينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيَابٌ مِنْ نَارٍ} يلبسون مقطعات النيران {يصبُّ من فوقِ رؤوسهم الحميم} ماءٌ حارٌّ لو سقطت نقطٌ على جبال الدُّنيا أذابتها',NULL,'731',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2616,'{يصهر} يُذاب {به} بذلك الماء {ما في بطونهم} من الأمعاء {والجلود} وتنشوي جلودهم فتتساقط',NULL,'731',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2617,'{ولهم مقامع} سياطٌ {من حديد}',NULL,'731',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2618,'{كلما أرادوا أن يخرجوا منها} من جهنَّم {من غمّ} يصيبهم {أعيدوا فيها} ردوا إليها بالمقاطع {و} تقول لهم الخزنة: {ذوقوا عذاب الحريق} النَّار وقال في الخصم الذين هم المؤمنون:',NULL,'731',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2619,'{إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جنات} الآية وهي مفسَّرةٌ في سورة الكهف',NULL,'731',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2620,'{وهدوا} أُرشدوا في الدُّنيا {إلى الطيب من القول} ت وهو شهادة أن لا إله إلا الله {وهدوا إلى صراط الحميد} دين اللَّهِ المحمود في أفعاله',NULL,'731',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2621,'{إِنَّ الَّذِينَ كَفَرُوا وَيَصُدُّونَ عَنْ سَبِيلِ اللَّهِ} يمنعون عن طاعة الله تعالى {والمسجد الحرام} يمنعون المؤمنين عنه {الذي جعلناه للناس} خلقناه وبنيناه للنَّاس كلِّهم لم تخص به بعضاً دون بعض {سواءً العاكف فيه والباد} سواءٌ في تعظيم حرمته وقضاء النُّسك به الحاضر والذي يأتيه من البلاد فليس أهل مكَّة بأحقَّ به من النَّازع إليه {ومَن يرد فيه بإلحادٍ بظلمٍ} أَيْ: إلحاداً بظلمٍ وهو أن يميل إلى الظُّلم ومعناه: صيد حمامة وقطع شجرة ودخوله غير مُحرمٍ وجميع المعاصي لأنَّ السَّيئات تُضاعف بمكَّة كما تضاعف الحسنات',NULL,'732',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2622,'{وإذ بوأنا لإبراهيم مكان البيت} بيَّنا له أين يُبنى {أن لا تشرك} يعني: وأمرناه أَنْ لا تُشْرِكْ {بِي شَيْئًا وَطَهِّرْ بَيْتِيَ} مفسَّرٌ في سورة البقرة',NULL,'732',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2623,'{وأذن في الناس} نادِ فيهم {بالحج يأتوك رجالاً} مُشاةً على أرجلهم {و} ركباناً {على كلّ ضامر} وهو البعير المهزول {يأتين من كلِّ فج عميق} طريق بعيدٍ',NULL,'732',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2624,'{ليشهدوا} ليحضروا {منافع لهم} من أمر الدُّنيا والآخرة {وَيَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَعْلُومَاتٍ عَلَى ما رزقهم من بهيمة الأنعام} يعني: التَّسمية على ما ينجر في يوم النَّحر وأيَّامِ التشويق {فكلوا منها} أمر إباحةٍ وكان أهل الجاهليَّة لا يأكلون من نسائكهم فأمر المسلمون أن يأكلوا {وأطعموا البائس الفقير} الشَّديد الفقر',NULL,'732',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2625,'{ثم ليقضوا تفثهم} يعني: ما يخرجون به من الإحرام وهو أخذ الشَّارب وتقليم الظُّفر وحلق العانة ولبس الثَّوب {وليوفوا نذورهم} يعني: ما نذروه من ير وهدي في أيَّام الحجِّ {وليطوفوا بالبيت العتيق} القديم: وقيل: المُعتق من أن يتسلَّط عليه جبَّار يعني: الكعبة',NULL,'733',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2626,'{ذلك} أَيْ: الأمر ذلك الذي ذكرت {ومن يعظم حرمات الله} فرائض الله وسننه {وأحلت لكم الأنعام} أن تأكلوها {إلاَّ ما يتلى عليكم} في قوله: {حرمت عليكم الميتة} الآية ومعنى هذا النَّهي تحريمُ ما حرَّمه أهل الجاهليَّة من البحيرة والسَّائبة وغير ذلك {فاجتنبوا الرجس من الأوثان} يعني: عبادتها {واجتنبوا قول الزور} يعني: الشِّرك بالله',NULL,'733',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2627,'{حنفاء لله} مسلمين عادلين عن كلِّ دينٍ سواه {ومن يشرك بالله فكأنما خرَّ} سقط {من السماء} فاختطفته الطَّير من الهواء أو ألفته الرِّيح في {مكان سحيق} بعيدٍ يعني: إنَّ مَنْ أشرك فقد هلك وبَعُدَ عن الحقِّ',NULL,'733',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2628,'{ذلك ومن يعظم شعائر الله} يستسمن البُدن {فإنَّ ذلك من} علامات التَّقوى',NULL,'733',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2629,'{لكم فيها منافع} الرُّكوب والدَّرُّ والنَّسل {إلى أجل مسمى} وهو أن يُسمِّيها هدياً {ثمَّ محلها} حيث يحلُّ نحرها عند {البيت العتيق} يعني: الحرم كلَّه',NULL,'734',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2630,'{ولكلِّ أمة} جماعةٍ سلفت قبلكم {جعلنا منسكاً} ذبحاً للقرابين {ليذكروا اسم الله} عند الذَّبح {على ما رزقهم من بهيمة الأنعام} يعني: الأنعام {فإلهكم إله واحد} أَيْ: لا تذكروا على ذبائحكم إلاَّ الله وحده {فله أسلموا} أخلصوا العبادة {وبشر المخبتين} المتواضعين',NULL,'734',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2631,'{الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَالصَّابِرِينَ عَلَى مَا أَصَابَهُمْ وَالْمُقِيمِي الصَّلاةِ وَمِمَّا رَزَقْنَاهُمْ ينفقون}',NULL,'734',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2632,'{والبدن} الإبل والبقر {جعلناها لكم من شعائر الله} أعلام دينه {لكم فيها خيرٌ} النَّفع في الدُّنيا والأجر في العقبى {فاذكروا اسم الله} وهو أن يقول عند نحرها: اللَّهُ أَكْبَرُ لا إِلَهَ إِلا اللَّهُ وَاللَّهُ أكبر {صواف} قائمةً معقولة اليد اليسرى {فإذا وجبت جنوبها} سقطت على الأرض {فكلوا منها وأطعموا القانع} الذي يسألك {والمعتر} الذي يتعرَّض لك ولا يسألك {كذلك} الذي وفصفنا {سخرناها لكم} يعني: البدن {لعلَّكم تشكرون} لكي تطيعوني',NULL,'734',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2633,'{لن ينال الله لحومها ولا دماؤها} كان المشركون يُلطِّخون جدار الكعبة بدماء القرابين فقال الله تعالى: {لن ينال الله لحومها ولا دماؤها} أَيْ: لن يصل إلى اللَّهَ لُحُومُهَا وَلا دِمَاؤُهَا {وَلَكِنْ يَنَالُهُ التَّقْوَى منكم} أَيْ: النِّيَّةُ والإِخلاص وما أريد به وجه الله تعالى {لتكبروا الله على ما هداكم} إلى معالم دينه {وبشر المحسنين} المُوحِّدين',NULL,'734',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2634,'{إن الله يدافع} غائلة المشركين عن المؤمنين {إن الله لا يحب كل خوَّانٍ} في أمانته {كفورا} لنعمته وهم الذين تقرَّبوا إلى الأصنام بذبائحهم',NULL,'735',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2635,'{أُذِنَ للذين يقاتلون} يعني: المؤمنين وهذه أوَّلُ آيةٍ نزلت في الجهاد والمعنى: أُذن لهم أن يُقاتلوا {بأنهم ظلموا} بظلم الكافرين إيَّاهم {وإنَّ الله على نصرهم لقدير} وعدٌ من الله تعالى بالنَّصر',NULL,'735',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2636,'{الذين أخرجوا من ديارهم بغير حق} يعني: المهاجرين {إلاَّ أن يقولوا ربنا الله} أَيْ: لم يُخرجوا إلاَّ بأن وحَّدوا الله تعالى {ولولا دفع الله الناس بعضهم ببعض} لولا أن دفع الله بعض النَّاس ببعض {لهدِّمت صوامع وبيعٌ} في زمان عيسى عليه السَّلام {وصلوات} في أيَّام شريعة موسى عليه السَّلام يعني: كنائسهم وهي بالعبرانيَّة صلوتا {ومساجد} في أيام شريعة محمد صلى الله عليه وسلم {ولينصرنَّ الله من ينصره} يعني: مَنْ نصر دين الله نصره الله على ذلك {إِنَّ اللَّهَ لَقَوِيٌّ} على خلقه {عزيز} منيعٌ في سلطانه',NULL,'735',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2637,'{الذين إن مكناهم في الأرض} يعني: هذه الأمَّة إذا فتح الله عليهم الأَرْضِ {أَقَامُوا الصَّلاةَ وَآتَوُا الزَّكَاةَ وَأَمَرُوا بِالْمَعْرُوفِ ونهوا عن المنكر ولله عاقبة الأمور} أَيْ: آخر أمور الخلق ومصيرهم إليه ثمَّ عزَّى نبيَّه فقال:',NULL,'735',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2638,'{وَإِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلِهِمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ}',NULL,'736',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2639,'{وَقَوْمُ إِبْرَاهِيمَ وَقَوْمُ لُوطٍ}',NULL,'736',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2640,'{وأصحاب مدين وكُذِّب موسى فأمليت للكافرين} أَيْ: أمهلتهم {ثم أخذتهم} عاقبتهم {فكيف كان نكير} إنكاري عليهم ما فعلوا بالعذاب',NULL,'736',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2641,'{فكأين من قرية} وكم من قريةٍ {أهلكناها وهي ظالمة} بالكفر {فهي خاوية} ساقطةٌ {على عروشها} سقوفها {وبئر مُعَطَّلَةٍ} متروكةٍ بموت أهلها {وقصر مشيد} رفيعٍ طويلٍ',NULL,'736',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2642,'{أفلم يسيروا في الأرض} يعني: كفَّار مكَّة {فينظروا} إلى مصارع الأمم المكذبة وهو قوله: {فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهَا أَوْ آذَانٌ يسمعون بها} فيتفكَّروا ويعتبروا ثم ذكر أن الأبصار لا تعمى عن رؤية الآيات ولكن القلوب تعمى فلا يتفكروا ولا يعتبروا',NULL,'736',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2643,'{ويستعجلونك بالعذاب} كانوا يقولون له: {فَأْتِنَا بِمَا تَعِدُنَا إِنْ كُنْتَ مِنَ الصَّادِقِينَ} فقال الله تعالى: {ولن يخلف الله وعده} الذي وعدك من نصرك وإهلاكهم ثمَّ ذكر أنَّ لهم مع عذاب الدُّنيا في الآخرة عذاباً طويلاً وهو قوله تعالى: {وإنَّ يوماً عند ربك} أَيْ: من أيَّام عذابهم {كألف سنة مما تعدون} وذلك أن يوماً من أيَّام الآخرة كألفِ سنةٍ في الدُّنيا ثمَّ ذكر سبحانه أنَّه قد أخذ قوماً بعد الإِمهال فقال:',NULL,'736',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2644,'{وَكَأَيِّنْ مِنْ قَرْيَةٍ أَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثمَّ أخذتها وإلي المصير}',NULL,'737',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2645,'{قل يا أيها النَّاسُ إِنَّمَا أَنَا لَكُمْ نَذِيرٌ مُبِينٌ}',NULL,'737',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2646,'{فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كريم}',NULL,'737',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2647,'{والذين سعوا في آياتنا} عملوا في إبطالها {معاجزين} مقدرين أنهم يعجزوننا ويفوتوتنا',NULL,'737',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2648,'{وما أرسلنا من قبلك من رسول} وهو الذي يأتيه جبريل عليه السلام بالوحي عياناً {ولا نبيّ} وهو الذي تكون نبوَّته إلهاماً ومناماً {إلاَّ إذا تمنى} قرأ {ألقى الشيطان} في قراءته ما ليس ممَّا يقرأ يعني: ما جرى على لسان النبيِّ صلى الله عليه وسلم حين قرأ سورة والنجم في مجلس من قريش فلما بلغ قوله تعالى: {ومناة الثَّالثة الأخرى} جرى على لسانه: تلك الغرانيق العلى وإنَّ شفاعتهنَّ لترتجى ثمَّ نبَّهه جبريل عليه السَّلام على ذلك فرجع وأخبرهم أنَّ ذلك كان من جهة الشَّيطان فذلك قوله: {فَيَنْسَخُ اللَّهُ مَا يُلْقِي الشَّيْطَانُ ثُمَّ يُحْكِمُ الله آياته} يُبيِّنها حتى لا يجد أحدٌ سبيلاً إلى إبطالها {والله عليم} بما أوحى إلى نبيِّه محمد صلى الله عليه وسلم {حكيم} في خلقه ثمَّ ذكر أنَّ ذلك ليفتن الله به قوماً فقال:',NULL,'737',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2649,'{ليجعل ما يلقي الشيطان فتنة} ضلالةً {للذين في قلوبهم مرض} وهم أهل النِّفاق {والقاسية قلوبهم} المشركين {وإنَّ الظالمين} الكافرين {لفي شقاق بعيد} خلافٍ طويلٍ مع النبي صلى الله عليه وسلم والمؤمنين',NULL,'738',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2650,'{وليعلم الذين أوتوا العلم} التَّوحيد والقرآن {أنه الحق} أَيْ: الذي أحكم الله سبحانه من آيات القرآن وهو الحقُّ {فتخبت له قلوبهم} فتخشع',NULL,'738',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2651,'{ولا يزال الذين كفروا في مرية} في شكٍّ {منه} ممَّا أُلقي على لسان الرسول صلى الله عليه وسلم {حتى تأتيهم الساعة} القيامة {بغتة} فجأة {أو يأتيهم عذاب يوم عقيم} يعني: يوم بدرٍ وكان عقيماً عن أن يكون للكافرين فيه فرحٌ أو راحةٌ والعقيم معناه: التي لا تلد',NULL,'738',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2652,'{الملك يومئذ} يعني: يوم القيامة {لله} وحده من غير مُنازعٍ ولا مُدَّعٍ {يحكم بينهم} ثمَّ بيَّن حكمه فقال: {فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فِي جَنَّاتِ النَّعِيمِ}',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2653,'{وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا فَأُولَئِكَ لَهُمْ عَذَابٌ مهين}',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2654,'{والذين هاجروا} فارقوا أوطانهم وعشائرهم {في سبيل الله} في طاعة اللَّهِ {ثُمَّ قُتِلُوا أَوْ مَاتُوا لَيَرْزُقَنَّهُمُ اللَّهُ رزقاً حسناً} في الجنَّة',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2655,'{لَيُدْخِلَنَّهُمْ مدخلاً} أَيْ: إدخالاً وموضعاً {يرضونه} وهو الجنة',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2656,'{ذلك} أَيْ: الأمر ذلك الذي قصصنا عليك {ومَنْ عاقب بمثل ما عوقب به} أَيْ: جازى العقوبة بمثلها {ثم بغي عليه} ظُلم {لينصرنَّه الله} يعني: المظلوم',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2657,'{وذلك} أَيْ: ذلك النَّصر للمظلوم بأنَّه القادر على ما يشاء فمن قدرته أن {يولج الليل في النهار} يزيد من هذا في ذلك ومن ذلك في هذا والباقي ظاهرٌ إلى قوله:',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2658,'{ذَلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِنْ دُونِهِ هُوَ الْبَاطِلُ وَأَنَّ اللَّهَ هو العلي الكبير}',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2659,'{أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ مَاءً فَتُصْبِحُ الأَرْضُ مُخْضَرَّةً إِنَّ اللَّهَ لَطِيفٌ خبير}',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2660,'{له ما في السماوات وَمَا فِي الأَرْضِ وَإِنَّ اللَّهَ لَهُوَ الْغَنِيُّ الحميد}',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2661,'{أَلَمْ تَرَ أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَا فِي الأَرْضِ وَالْفُلْكَ تَجْرِي فِي الْبَحْرِ بِأَمْرِهِ وَيُمْسِكُ السَّمَاءَ أَنْ تَقَعَ عَلَى الأَرْضِ إِلا بإذنه إن الله بالناس لرؤوف رحيم}',NULL,'739',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2662,'{إنَّ الإنسان لكفور} يعني: إنَّ الكافر لجاحدٌ لآيات الله تعالى الدَّالة على توحيده وقوله:',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2663,'{لكلّ أمة جعلنا منسكاً هم ناسكوه} شريعةً هم عاملون بها {فلا يُنازِعُنَّكَ} يُجادِلُنَّكَ {في الأمر} نزلت في الذين جادلوا المؤمنين فقالوا: ما لكم تأكلون ما تقتلون ولا تأكلون ممَّا قتله الله؟',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2664,'{وإن جادلوك} بباطلهم مِراءً وتعنُّتاً فادفعهم بقولك: {الله أعلم بما تعملون} من التكذيب والكفر',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2665,'قال تعالى {اللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ فِيمَا كُنْتُمْ فيه تختلفون}',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2666,'{أَلَمْ تَعْلَمْ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السماء والأرض إنَّ ذلك} كلَّه {في كتاب} يعني: اللَّوح المحفوظ {إن ذلك} يعني: علمه بجميع ذلك {على الله يسير}',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2667,'{وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَمْ يُنَزِّلْ به} بعبادته {سلطاناً} حجَّةً وبرهاناً {وما ليس لهم به علم} لم يأتهم به كتابٌ ولا نبيٌّ {وما للظالمين} المشركين {من نصير} مانعٍ من عذاب الله تعالى',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2668,'{وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ} يعني: القرآن {تعرف في وجوه الذين كفروا المنكر} الإِنكار بالعبوس والكراهة {يكادون يسطون} يقعون ويبشطون {بِالَّذِينَ يَتْلُونَ عَلَيْهِمْ آيَاتِنَا قُلْ أَفَأُنَبِّئُكُمْ بِشَرٍّ مِنْ ذَلِكُمُ} بِشَرٍ لكم وأكره إليكم من هذا القرآن الذي تسمعون {النار} أَيْ: هي النَّار',NULL,'740',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2669,'{يا أيها الناس} يعني: يا أهل مكَّة {ضرب مثل} بُيِّن لكم ولمعبودكم شَبَهٌ {فَاسْتَمِعُوا لَهُ إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ الله} من الأصنام {لن يخلقوا ذباباً ولو اجتمعوا} كلُّهم لخلقه {وإن يسلبهم الذباب شيئاً} ممَّا عليهم من الطَّيب {لا يستنقذوه منه} لا يستردُّوه منه لعجزهم {ضعف الطالبُ والمطلوب} يعني: العابد والمعبود والطَّالب: الذُّباب يطلب من الصَّنم ما لطِّخ به من الزَّعفران والطِّيب وهو مَثَلٌ لعابده يطلب منه الشَّفاعة والنُّصرة والمطلوب: الصنم',NULL,'741',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2670,'{ما قدروا الله حق قدره} ما عظَّموه حقَّ تعظيمه إذ أشركوا به ما لا يمتنع من الذُّباب ولا ينتصر منه',NULL,'741',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2671,'{الله يصطفي من الملائكة رسلاً} مثل جبريل وميكائيل وإسرافيل عليهم السَّلام {ومن الناس} يعني: النبيِّين عليهم السَّلام {إنَّ الله سميع} لقول عباد {بصير} بمَنْ يختاره',NULL,'741',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2672,'{يعلم ما بين أيديهم} ما عملوه {وما خلفهم} وما هم عاملون ممَّا لم يعلموه',NULL,'741',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2673,'قال تعالى {يا أيها الَّذِينَ آمَنُوا ارْكَعُوا وَاسْجُدُوا وَاعْبُدُوا رَبَّكُمْ وَافْعَلُوا الخير لعلكم تفلحون}',NULL,'741',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2674,'{وجاهدوا في الله} في سبيل الله {حق جهاده} بينة صادقة {هو اجتباكم} اختاركم لدينه {وَمَا جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حرج} ضيقٍ لأنَّه سهَّل الشَّريعة بالتَّرخيص {ملَّة أبيكم} اتبعوا ملَّة أبيكم {إبراهيم} كان هو في الحرمة كالأب صلى الله عليه وسلم ولذلك جُعل أبا المسلمين {هو سماكم} أَيْ: الله تعالى سمَّاكم {المسلمين من قبل} أي: من قبل القرآن في سائر الكتب {وفي هذا} يعني: القرآن {ليكون الرسول شهيداً عليكم} وذلك أنَّه يشهد لمَنْ صدَّقه وعلى مَنْ كذَّبه {وتكونوا شهداء على الناس} تشهدون عليهم أنَّ رسلهم قد بلَّغتهم وقوله: {واعتصموا بالله} أَيْ: تمسَّكوا بدينه {هو مولاكم} ناصركم ومتولي أموركم {فنعم المولى ونعم النصير}',NULL,'742',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2675,'{قد أفلح المؤمنون} سعد المصدِّقون ونالوا البقاء في الجنَّة',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2676,'{الذين هم في صلاتهم خاشعون} ساكنون لا يرفعون ابصارهم عن مواضع سجودهم',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2677,'{والذين هم عن اللغو معرضون} عن كلِّ ما لا يجمل في الشَّرع من قولٍ وفعلٍ',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2678,'{والذين هم للزكاة فاعلون} للصدقة الواجبة مودون',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2679,'{وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حافظون} يحفظونها عن المعاصي',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2680,'{إِلا عَلَى أَزْوَاجِهِمْ} من زوجاتهم {أو ما ملكت أيمانهم} من الإماء {فإنهم غير ملومين} لا يلامون في وطئهنَّ',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2681,'{فمن ابتغى} طلب ما {وراء ذلك} ما بعد الزَّوجة والأَمَة {فأولئك هم العادون} المتعدُّون عن الحلال إلى الحرام',NULL,'743',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2682,'{والذين هم لأماناتهم} ما ائتمنوا عليه من أمر الدِّين والدُّنيا {وعهدهم راعون} وحلفهم الذي يُوجد عليهم راعون يرعون ذلك ويقومون بإتمامه',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2683,'{والذين هم على صلواتهم يحافظون} بإدائها في مواقيتها',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2684,'{أولئك هم الوارثون} ثمَّ ذكر ما يرثون فقال:',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2685,'{الذين يرثون الفردوس} وذلك أنَّ الله تعالى جعل لكل امرئ بيتاً في الجنَّة فمَنْ عمل عمل أهل الجنَّة ورث بيته في الجنَّة والفردوس خير الجنان',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2686,'{ولقد خلقنا الإنسان} ابن آدم {من سلالة} من ماءٍ سُلَّ واستُخرِجَ من ظهر آدم وكان آدم عليه السَّلام خُلق من طينٍ',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2687,'{ثمَّ جعلناه} جعلنا الإنسان {نطفة} في أوَّل بُدوِّ خلقه {في قرار مكين} يعني: الرَّحم وقوله',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2688,'{ثم أنشأناه خلقاً آخر} قيل: يريد الذُّكورية والأُنوثيَّة وقيل: يعني: نفخ الرُّوح وقيل: نبات الشَّعر والأسنان {فتبارك الله} استحقَّ التَّعظيم والثَّناء بدوام بقائه {أحسن الخالقين} المُصوِّرين والمقدرين',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2689,'{ثُمَّ إِنَّكُمْ بَعْدَ ذَلِكَ لَمَيِّتُونَ}',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2690,'{ثمَّ إنكم يوم القيامة تبعثون}',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2691,'{ولقد خلقنا فوقكم سبع طرائق} سبع سماوات كلٌّ سماءٍ طريقةٌ {وما كنَّا عن الخلق غافلين} عمَّن خلقنا من الخلق كلِّهم',NULL,'744',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2692,'{وأنزلنا من السماء ماء بقدر} بمقدارٍ معلومٍ عند الله تعالى {فأسكناه} أثبتناه {في الأرض} قيل: هو النِّيل ودجلة والفرات وسيحان وجيحان وقيل: هو جميع المياه فِي الأَرْضِ {وَإِنَّا عَلَى ذَهَابٍ بِهِ لَقَادِرُونَ} حتى تهلكوا أنتم ومواشيكم عطشا وقوله:',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2693,'{فَأَنْشَأْنَا لَكُمْ بِهِ جَنَّاتٍ مِنْ نَخِيلٍ وَأَعْنَابٍ لَكُمْ فِيهَا فَوَاكِهُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ}',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2694,'{وشجرة تخرج} يعني: الزَّيتون {من طور سيناء} يعني: جبلاً معروفاُ أوَّل ما ينبت الزَّيتون ينبت هناك {تنبت بالدهن} لأنَّه يتَّخذ الدُّهن من الزَّيتون {وصبغ} إدام {للآكلين} وقوله:',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2695,'{وَإِنَّ لَكُمْ فِي الأَنْعَامِ لَعِبْرَةً نُسْقِيكُمْ مِمَّا فِي بُطُونِهَا وَلَكُمْ فِيهَا مَنَافِعُ كَثِيرَةٌ وَمِنْهَا تأكلون}',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2696,'{وعليها وعلى الفلك تحملون}',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2697,'{وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَى قَوْمِهِ فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُمْ مِنْ إِلَهٍ غَيْرُهُ}',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2698,'{يريد أن يتفضل عليكم} يتشَّرف عليكم فيكون أفضل منكم بأن يكون متبوعاً وتكونوا له تبعاً {ولو شاء الله لأنزل ملائكة} تُبلِّغنا عنه {ما سمعنا بهذا} الذي يدعو إليه نوحٌ {في آبائنا الأولين}',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2699,'{إن هو} ما هو {إلاَّ رجلٌ به جنة} جنونٌ {فتربصوا به حتى حين} انتظروا موته حتى يموت',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2700,'{قال رب انصرني} بإهلاكهم {بما كذبون} بتكذيبهم إياي وقوله:',NULL,'745',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2701,'{فأوحينا إليه} الآية مُفسَّرة في سورة هود {فاسلك فيها} أَيْ اُدخل في السَّفينة والباقي مفسَّر في سورة هود',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2702,'{فإذا استويت} اعتدلت في السَّفينة راكباً الآية',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2703,'{وقل رب أنزلني} منها {منزلاً} إنزالاً {مباركاً} فاستجاب الله تعالى دعاءَه حيث قال: {اهبط بسلام منا وبركات عليك} وبارك فيهم بعد إنزالهم من السَّفينة حتى كان جميع الخلق من نسل نوحٍ ومَنْ كان معه في السَّفينة',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2704,'{إن في ذلك} الذي ذكرت {لآيات} لدلالاتٍ على قدرتنا {وإن كنا لمبتلين} مُختبرين طاعتهم بإرسال نوحٍ إليهم',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2705,'{ثم أنشأنا من بعدهم} أحدثنا {قرناً آخرين} يعني: عاداً',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2706,'{فأرسلنا فيهم رسولاً منهم} وهو هود وقوله:',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2707,'{وأترفناهم} أَيْ: نعَّمناهم ووسَّعنا عليهم وقوله:',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2708,'{وَلَئِنْ أَطَعْتُمْ بَشَرًا مِثْلَكُمْ إِنَّكُمْ إِذًا لَخَاسِرُونَ}',NULL,'746',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2709,'{أنكم مخرجون} أَيْ: من قبوركم أحياء',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2710,'{هيهات هيهات} بُعْداً {لما توعدون} من البعث',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2711,'{إن هي} ما هي {إلاَّ حياتنا الدنيا} يعني: الحياة الدَّانية في هذه الدَّار {نموت ونحيا} يموت الآباء ويحيا الأولاد',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2712,'{إِنْ هِيَ إِلا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وما نحن بمبعوثين}',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2713,'{قال رب انصرني} عليهم {بما كذبون} بتكذيبهم إياي',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2714,'{قال عمَّا قليل} عن قريبٍ {ليصبحنَّ نادمين} يندمون إذا نزل بهم العذاب على التَّكذيب',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2715,'{فأخذتهم الصيحة} صيحة العذاب {بالحق} بالأمر من الله تعالى {فجعلناهم غثاء} هلكى هامدين كغثاء السَّيل وهو ما يحمله من بالي الشَّجر {فبعداً} فهلاكاً {للقوم الظالمين} المشركين',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2716,'{ثُمَّ أَنْشَأْنَا مِنْ بَعْدِهِمْ قُرُونًا آخَرِينَ}',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2717,'{مَا تسبق من أمة أجلها} لا تموت قبل أجلها {وما يستأخرون} بعد الأجل طرفة عين وقوله:',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2718,'{تترا} أَيْ: متتابعةً {وجعلناهم أحاديث} أَيْ: لمَنْ بعدهم يتحدثون بهم وقوله:',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2719,'قال تعالى {ثُمَّ أَرْسَلْنَا مُوسَى وَأَخَاهُ هَارُونَ بِآيَاتِنَا وَسُلْطَانٍ مبين}',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2720,'{وكانوا قوماً عالين} مستكبرين قاهرين غيرهم بالظُّلم',NULL,'747',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2721,'{وقومهما لنا عابدون} أَيْ: مطيعون متذللون',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2722,'{فَكَذَّبُوهُمَا فَكَانُوا مِنَ الْمُهْلَكِينَ}',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2723,'{ولقد آتينا موسى الكتاب لعلهم يهتدون} لكي يهتدي به قومه',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2724,'{وجعلنا ابن مريم وأُمَّه آية} دلالة على قدرتنا {وآويناهما إلى ربوة} يعني: بيت المقدس وهو أقرب الأرض إلى السَّماء {ذات قرار} أرضٍ مستويةٍ وساحةٍ واسعةٍ {ومعين} ماءٍ ظاهرٍ وقيل: هي دمشق',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2725,'{يا أيها الرسل كلوا من الطيبات} هذا خطابٌ للنبي صلى الله عليه وسلم والمراد به أنَّ الله تبارك وتعالى كأنه أخبر أنَّه قد قال لجميع الرُّسل قبله هذا القول وأمرهم بهذا والمعنى: كلوا من الحلال',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2726,'{وإنَّ هذه أمتكم أمة واحدة} أَيْ: ملَّتكم أيُّها الرُّسل ملَّةٌ واحدةٌ وهي الإِسلام {وأنا ربكم} شرعتها لكم وبيَّنتها لكم {فاتقون} فخافون',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2727,'{فتقطعوا أمرهم بينهم} يعني: المشركين واليهود والنصارى {وزيرا} فرقاً {كلُّ حزب} جماعةٍ {بما لديهم} بما عندهم من الدِّين {فرحون} مُعجبون مسرورون',NULL,'748',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2728,'{فذرهم في غمرتهم} حيرتهم وضلالتهم {حتى حين} يريد: حتى حينِ الهلاكِ بالسَّيف أو الموت',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2729,'{أيحسبون أنما نمدُّهم به} ما نبسط عليهم {من مال وبنين} من المال والأولاد في هذه الدُّنيا',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2730,'{نسارع لهم في الخيرات} نُعطيهم ذلك ثواباً لهم {بل لا يشعرون} أنَّ ذلك استدراجٌ ثمَّ رجع إلى ذكر أوليائه فقال:',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2731,'{إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ} خائفون عذابه ومكره',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2732,'{وَالَّذِينَ هُمْ بِآيَاتِ رَبِّهِمْ يُؤْمِنُونَ}',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2733,'{وَالَّذِينَ هُمْ بِرَبِّهِمْ لا يُشْرِكُونَ}',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2734,'{والذين يؤتون ما آتوا} يُعطون ما يُعطون {وقلوبهم وجلة} خائفةٌ أنَّ ذلك لا يُقبل منهم وقد أيقنوا أنَّهم إلى ربِّهم صائرون بالموت وقوله:',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2735,'{وهم لها سابقون} أَيْ: إليها ثمَّ ذكر أنَّه لم يُكلِّف العبد إلاَّ ما يسعه فقال:',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2736,'{ولا نكلف نفساً إلاَّ وسعها} فمَنْ لم يستطع أن يصلي قائماً فليصلِّ جالساً {ولدينا كتاب} يعني: اللَّوح المحفوظ {ينطق بالحق} يُبيِّن بالصِّدق {وهم لا يظلمون} لا ينقصون من ثواب أعمالهم ثمَّ عاد إلى ذكر المشركين فقال:',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2737,'{بل قلوبهم في غمرة} في جهالةٍ وغفلةٍ {من هذا} الكتاب الذي ينطق بالحقِّ {ولهم أعمال من دون ذلك} وللمشركين أعمالٌ حبيثة دون أعمال المؤمنين الذين ذكرهم {هُمْ لَهَا عَامِلُونَ}',NULL,'749',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2738,'{حتى إذا أخذنا مترفيهم} رؤساءَهم وأغنياءَهم {بالعذاب} بالقحط والجوع سبع سنين {إذا هم يجأرون} يضجُّون ويجزعون ونقول لهم:',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2739,'{لا تَجْأَرُوا الْيَوْمَ إِنَّكُمْ مِنَّا لا تُنْصَرُونَ} لا تُمنعون ولا ينفعكم جزعكم',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2740,'{قَدْ كَانَتْ آيَاتِي تُتْلَى عَلَيْكُمْ} يعني: القرآن {فكنتم على أعقابكم} على أدياركم {تنكصون} ترجعون القهقرى مُكذِّبين به',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2741,'{مستكبرين به} أي: بالحرم تقولون: لا يظهر علينا أحدٌ لأنَّا أهل الحرم {سامراً} سُمَّاراً باللَّيل {تُهْجِرُون} تهذون وتقولون الهُجر من سب النبي صلى الله عليه وسلم',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2742,'{أفلم يدبروا القول} يتدبَّروا القرآن فيقفوا على صدقك {أم جاءهم} بل أَجاءهم {ما لم يأت آباءهم الأولين} يريد: إنَّ إنزال الكتاب قد كان قبل هذا فليس إنزال الكتاب عليك ببديعٍ ينكرونه',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2743,'{أم لم يعرفوا رسولهم} الذي نشأ فيما بينهم وعرفوه بالصِّدق',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2744,'{أم يقولون} بل أيقولون {به جنة} جنونٌ {بل جاءهم} ليس الأمر كما يقولون بل جاءهم الرَّسول {بالحق} بالقرآن من عند الله',NULL,'750',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2745,'{ولو اتبع الحق} القرآن الذي يدعو إلى المحاسن {أهواءَهم} التي تدعو إلى المقابح أي: لو كان التَّنزيل بما يُحبُّون {لفسدت السماوات والأرض} وذلك أنها خلفت دلالةً على توحيد الله فلو كان القرآن على مرادهم لكن يدعو إلى الشِّرك وذلك يُؤدِّي إلى إفساد أدلة التَّوحيد وقوله: {ومَنْ فيهنَّ} لأنَّهم حينئذٍ يُشركون بالله تعالى {بل أتيناهم بذكرهم} بشرهم في الدُّنيا والآخرة',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2746,'{أم تسألهم} أنت يا محمَّد على ما جئت به {خرجاً} جُعلاً وأجراً {فخراج ربك} فعطاه ربك وثوابه {خير} وقوله:',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2747,'{وإنك لتدعوهم إلى صراط مستقيم}',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2748,'{لناكبون} أَيْ: عادلون مائلون',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2749,'{وَلَوْ رَحِمْنَاهُمْ وَكَشَفْنَا مَا بِهِمْ مِنْ ضُرٍّ} جذب وقحطٍ {للجوا} لتمادوا {في طغيانهم يعمهون} نزلت هذه الآية حين شكوا إلى النبي صلى الله عليه وسلم وقالوا: قتلْتَ الآباء بالسَّيف والأبناء بالجوع',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2750,'{ولقد أخذناهم بالعذاب} بالجوع {فما استكانوا لربهم} ما تواضعوا',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2751,'{حَتَّى إِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شديد} يوم بدرٍ وقيل: عذاب الآخرة {إِذَا هُمْ فِيهِ مُبْلِسُونَ} آيسون من كلِّ خير وقوله:',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2752,'قال تعالى {وَهُوَ الَّذِي أَنْشَأَ لَكُمُ السَّمْعَ وَالأَبْصَارَ وَالأَفْئِدَةَ قليلا ما تشكرون}',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2753,'قال تعالى {وَهُوَ الَّذِي ذَرَأَكُمْ فِي الأَرْضِ وَإِلَيْهِ تُحْشَرُونَ}',NULL,'751',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2754,'{وله اختلاف الليل والنهار} أي: هو الذي جعلنهما مختلفين وقوله:',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2755,'{بَلْ قَالُوا مِثْلَ مَا قَالَ الأَوَّلُونَ}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2756,'{قَالُوا أإذا متنا وكنا تراباً وعظاما أإنا لمبعوثون}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2757,'{لَقَدْ وُعِدْنَا نَحْنُ وَآبَاؤُنَا هَذَا مِنْ قَبْلُ إن هذا إلاَّ أساطير الأوَّلين}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2758,'{قُلْ لِمَنِ الأَرْضُ وَمَنْ فِيهَا إِنْ كُنْتُمْ تعلمون}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2759,'{سَيَقُولُونَ لِلَّهِ قُلْ أَفَلا تَتَّقُونَ}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2760,'{قل من رب السماوات السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيمِ}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2761,'{سيقولون لله قل أفلا تتقون}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2762,'{ملكوت كل شيء} أي: ملكه يعني: مَنْ يملك كلَّ شيء؟ {وهو يجير} يُؤمن من يشاء {ولا يجار عليه} لا يُؤمَنُ مَنْ أخافه وقوله:',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2763,'{فأنى تسحرون} تُخدعون وتُصرفون عن توحيده وطاعته',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2764,'{بل أتيناهم بالحق} يعني: القرآن {وإنهم لكاذبون} أنَّ الملائكة بنات الله',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2765,'{مَا اتَّخَذَ اللَّهُ مِنْ وَلَدٍ وَمَا كَانَ مَعَهُ مِنْ إِلَهٍ إِذًا لَذَهَبَ كُلُّ إِلَهٍ بما خلق} ينفرد بمخلوقاته فيمنع الإله الآخر عن الاستيلاء عليها {ولعلا بعضهم على بعض} بالقهر والمزاحمة كالعادة بين الملوك {سبحان الله} تنزيهاً له {عما يصفون} من الكذب',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2766,'{عالم الغيب والشهادة فتعالى عما يشركون}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2767,'{قل رب إما تريني ما يوعدون} ما يُوعَدُ المشركون من العذاب',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2768,'{فلا تجعلني} معهم أَيْ: إنْ أنزلت بهم النِّقمة فاجعلني خارجا منهم',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2769,'{وَإِنَّا عَلَى أَنْ نُرِيَكَ مَا نَعِدُهُمْ لَقَادِرُونَ}',NULL,'752',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2770,'{ادفع بالتي هي أحسن} من الحلم والصَّفح {السيئة} التي تأتيك منهم من الأذى والمكروه {نحن أعلم بما يصفون} فنجازيهم به وهذا كان قبل الأمر بالقتال',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2771,'{وَقُلْ رَبِّ أَعُوذُ بِكَ مِنْ هَمَزَاتِ الشَّيَاطِينِ} نزعاتها ووساوسها',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2772,'{وأعوذ بك رب أن يحضرون} في شيءٍ من أموري وقوله:',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2773,'{رب ارجعون} أي: ارددني إلى الدُّنيا',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2774,'{لعلي أعمل صالحاً} أَيْ: بالتَّوحيد {فيما تركت} حين كنت في الدُّنيا {كلا} لا يرجع إلى الدُّنيا {إنها كلمة هو قائلها} عند الموت ولا يُجاب إلى ذلك {ومن ورائهم} أمامهم {برزخ} حاجزٌ بينهم وبين الرُّجوع إلى الدُّنيا',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2775,'{فإذا نفخ في الصور} النَّفخة الأخيرة {فلا أنساب بينهم يومئذ} لا يفتخرون بالأنساب {ولا يتساءلون} كما يتساءلون في الدُّنيا من أيِّ قبيلةٍ ونَسبٍ أنت',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2776,'{فَمَنْ ثَقُلَتْ مَوَازِينُهُ فَأُولَئِكَ هُمُ الْمُفْلِحُونَ}',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2777,'{وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فِي جهنم خالدون}',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2778,'{تفلح} تحرق {وهم فيها كالحون} عابسون لتقلُّص شفاههم بالانشواء فيقال لهم:',NULL,'753',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2779,'{أَلَمْ تَكُنْ آيَاتِي تُتْلَى عَلَيْكُمْ فَكُنْتُمْ بِهَا تكذبون}',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2780,'{قالوا ربنا غلبت علينا شقوتنا} التي قضيتَ علينا {وكنا قوماً ضالين} أقرُّوا على أنفسهم بالضلال وقوله:',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2781,'{رَبَّنَا أَخْرِجْنَا مِنْهَا فَإِنْ عُدْنَا فَإِنَّا ظَالِمُونَ}',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2782,'{اخسؤوا} أي: تباعدوا تباعد سخط عليكم وقوله:',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2783,'{إِنَّهُ كَانَ فَرِيقٌ مِنْ عِبَادِي يَقُولُونَ رَبَّنَا آمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَأَنْتَ خَيْرُ الرَّاحِمِينَ}',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2784,'{فاتخذتموهم سخرياً} أَيْ: سخرتم منهم واسهزأتم {حتى أنسوكم ذكري} لاشتغالكم بالاستهزاء',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2785,'{إني جزيتهم اليوم} قابلتُ عملهم بما يستحقُّون من الثَّواب {بما صبروا} على أذاكم {أنهم هم الفائزون} النَّاجون من العذاب والنَّار',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2786,'{قَالَ كَمْ لَبِثْتُمْ فِي الأَرْضِ عَدَدَ سِنِينَ} قال الله تعالى لمنكري البعث إذا بعثهم من قبورهم: كم لبثتم في قبوركم؟ وهذا سؤال توبيخٍ لهم لأنَّهم كانوا يُنكرون أن يُبعثوا من قبورهم',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2787,'{قالوا لبثنا يوماً أو بعض يوم} وذلك أنَّ العذاب رُفع عنهم فيما بين النَّفختين ونسوا ما كانوا فيه من العذاب فاستقصروا مدَّة لبثهم فلذلك قَالُوا: {لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ فَاسْأَلِ العادين} أي: فاسأل الملائكة الذين يحفظون عدد ما لبثنا',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2788,'{قال إن لبثتم} ما لبثتم {إلاَّ قليلاً} وإن طال لبثكم في طول لبثكم في النَّار {لو أنكم كنتم تعلمون} مقدار لبثكم في القبر وذلك أنَّهم لم يعلموا ذلك حيث قالوا: {لبثنا يوماً أو بعض يوم} فقيل: لهم: لو كنتم تعلمون ذلك كان قليلاً عند طول لبثكم في النَّار',NULL,'754',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2789,'{أفحسبتم أنَّما خلقناكم عبثاً} أَيْ: للعبث لا لحكمة من ثوابٍ للمطيع وعقابٍ للعاصي وقيل: عبثاً للعبث حتى تعبثوا وتغفلوا وتلهوا',NULL,'755',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2790,'{رب العرش الكريم} أي: السَّرير الحسن',NULL,'755',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2791,'{وَمَنْ يَدْعُ مَعَ اللَّهِ إِلَهًا آخَرَ لا برهان له به} لا حجَّة له بما يفعل من عبادته غير الله {فإنما حسابه عند ربه} جزاؤه عند الله تعالى فهو يجازيه لما يستحقُّه {إنه لا يفلح الكافرون} لا يسعد المُكذِّبون ثم أمر رسوله أن يستغفر للمؤمنين ويسأل لهم الرَّحمة فقال:',NULL,'755',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2792,'{وَقُلْ رَبِّ اغْفِرْ وَارْحَمْ وَأَنْتَ خَيْرُ الرَّاحِمِينَ}',NULL,'755',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2793,'{سورة أنزلناها} أَيْ: هذه سُورةٌ أنزلناها {وفرضناها} ألزمنا العمل بما فُرض فيها',NULL,'756',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2794,'{الزانية والزاني} إذا كانا حُرَّين بالغين غير محصنين {فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍ وَلا تأخذكم بهما رأفة} رقَّةٌ ورحمةٌ فَتُعطِّلوا الحدود وتخفِّفوا الضَّرب حتَّى لا يُؤلم وقوله: {في دين الله} أَيْ: في حكم الله {وليشهد} وليحضر {عذابهما} جلدهما {طائفة} نفرٌ {من المؤمنين}',NULL,'756',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2795,'{الزاني لا ينكح إلاَّ زانية} الآية نزلت في قومٍ من فقراء المهاجرين همُّوا أن يتزوَّجوا بغايا كنَّ بالمدينة لِعَيْلَتِهم فأنزل الله تعالى تحريم ذلك لأنهنَّ كنَّ زانياتٍ مشركاتٍ وبيَّن أنَّه لا يتزوَّج بهنَّ إلاَّ زانٍ أو مشركٌ وأنَّ ذلك حرامٌ على المؤمنين',NULL,'756',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2796,'{والذين يرمون} بالزِّنا {المحصنات} الحرائر العفائف {ثمَّ لم يأتوا} على ما رموهنَّ به {بأربعة شهداء} أَيْ: يشهدون عليهنَّ بذلك {فاجلدوهم} أَي: الرَّامين {ثمانين جلدة} يعني: كلَّ واحدٍ منهم {ولا تقبلوا لهم شهادةً أبداً} لا تُقبل شهادتهم إذا شهدوا لأنَّهم فسقوا برمي المحصنات إلاَّ أن يرجعوا ويُكذِّبوا أنفسهم ويتركوا القذف فينئذ تُقبل شهادتهم لقوله تعالى:',NULL,'757',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2797,'{إلاَّ الذين تابوا من بعد ذلك}',NULL,'757',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2798,'{والذين يرمون أزواجهم} يقذفوهن بالزِّنا {ولم يكن لهم شهداء إلاَّ أنفسهم} يشهدون على صحَّة ما قالوا إلاَّ هم {فشهادة أحدهم أربع شَهاداتٍ بالله} أربع مرات أنَّه صادقٌ فيما قذفها به يُسقط عنه الحدَّ ثم يقول في الخامسة: لَعْنَةَ اللَّهِ عَلَيْهِ إِنْ كَانَ مِنَ الْكَاذِبِينَ فإذا فعل الزَّوج هذا وجب الحدُّ على المرأة ويسقط ذلك عنها بأن تقول: أشهد بالله إنَّه لمن الكاذبين فيما قذفني به أربع مرات وذلك لقوله تعالى:',NULL,'757',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2799,'{وَالْخَامِسَةُ أَنَّ لَعْنَةَ اللَّهِ عَلَيْهِ إِنْ كَانَ من الكاذبين}',NULL,'757',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2800,'{ويدرأ عنها العذاب} أَيْ: يدفع عنها عقوبة الحدِّ والخامسة تقول: عليَّ غضب الله إن كان الصادقين',NULL,'757',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2801,'{وَالْخَامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إِنْ كَانَ من الصادقين}',NULL,'757',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2802,'{ولولا فضل الله عليكم ورحمته} جواب لولا محذوفٌ عل تقدير: لفضحكم بارتكاب الفاحشة ولعاجلكم بالعقوبة ولكنَّه {توابٌ} يقبل التَّوبة ويرحم مَنْ رجع عن السَّيئة {حَكِيمٌ} فيما فرض من الحدود',NULL,'758',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2803,'{إنَّ الذين جاؤوا بالإِفك} بالكذب على عائشة رضوان الله عليها وصفوان {عصبة} جماعة {منكم} يعني: حسَّان بن ثابت ومسطحاً وعبد الله ابن أُبيّ المنافق وحمنة بنت جحش {لا تحسبوه} لا تحسبوا ذلك الإفك {شرّاً لكم بل هو خيرٌ لكم} لأنَّ الله تعالى يأجركم على ذلك ويُظهر براءتكم {لكلِّ امرىء منهم مَا اكْتَسَبَ مِنَ الإِثْمِ} جزاء ما اجترح من الذَّنب {والذي تولَّى كبره} تحمَّل معظمه فبدأ بالخوض فيه وهو عبد الله ابن أبي',NULL,'758',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2804,'{لولا} هلاَّ {إذ سمعتموه} يعني: الإِفك {ظنَّ المؤمنون والمؤمنات} رجع من الخطاب إلى الخبر والمعنى: ظننتم أيُّها المؤمنون بالذين هم كأنفسهم {خيراً} والمؤمنون كلُّهم كالنَّفس الواحدة وقلتم: {هذا إفك مبين} كذبٌ ظاهرٌ',NULL,'758',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2805,'{لولا جاؤوا عَلَيْهِ بِأَرْبَعَةِ شُهَدَاءَ فَإِذْ لَمْ يَأْتُوا بِالشُّهَدَاءِ فأولئك عند الله هم الكاذبون}',NULL,'758',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2806,'{وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا والآخرة لمسكم} لأصابكم {في ما أفضتم} خضتم {فيه} من الإفك {عذاب عظيم}',NULL,'758',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2807,'{إذ تلقونه بألسنتكم} تأخذونه ويرويه بعضكم عن بعض {وتحسبونه هيناً} وتظنُّونه سهلاً وهو كبيرٌ عند الله سبحانه',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2808,'{ولولا} هلاَّ {إذ سمعتموه} سمعتم هذا الكذب {قُلْتُمْ مَا يَكُونُ لَنَا أَنْ نَتَكَلَّمَ بِهَذَا سبحانك} تعجُّباً من هذا الكتاب {هذا بهتان} كذبٌ نتحيَّر من عظمه والمعنى: هلا أنكرتموه وصنتم ألسنتكم عن الخوض فيه؟',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2809,'{يعظكم الله أن تعودوا} كراهة أن تعودوا لمثل هذا الإِفك أبداً',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2810,'{إنَّ الذين يحبون أن تشيع الفاحشة} يفشوَ الزِّنا {في الذين آمنوا لهم عذاب أليمٌ} وهم المنافقون كانوا يشيعون هذا الكذب ويطلبون العيب للمؤمنين وأن يكثر فيهم الزنا',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2811,'{إِنَّ الَّذِينَ يُحِبُّونَ أَنْ تَشِيعَ الْفَاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذَابٌ أَلِيمٌ فِي الدُّنْيَا والآخرة والله يعلم وأنتم لا تعلمون}',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2812,'{ولولا فضل الله عليكم ورحمته} لعجَّل لكم الذي تستحقُّونه من العقوبة',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2813,'{وَلَوْلا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكَا} ما صلح وطهر من هذا الذَّنب أحد {منكم} يعني: من الذين خاضوا فيه {ولكنَّ الله يزكي مَنْ يشاء} يُطهِّر مَنْ يشاء من الإِثم والذَّنب بالرَّحمة والمغفرة',NULL,'759',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2814,'{ولا يأتل} ولا يحلف {أولو الفضل منكم والسعة} يعني أبا بكر الصديق رضي الله عنه {أَنْ يُؤْتُوا أُولِي الْقُرْبَى وَالْمَسَاكِينَ وَالْمُهَاجِرِينَ فِي سبيل الله} يعني: مسطحاً وكان مسكيناً مهاجراً وكان ابن خالة أبي بكر وكان قد حلف أن لا ينفق عليه ولا يُؤتيه شيئاً {وليعفوا وليصفحوا} عنهم لخوضهم في حديث عائشة {ألا تحبون أن يغفر الله لكم} فلمَّا نزلت هذه الآية قال أبو بكر الصديق بلى أنا أحبُّ أن يغفر الله لي ورَجَع إلى مسطح بنفقته التي كان ينفق عليه',NULL,'760',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2815,'{إنَّ الذين يرمون المحصنات الغافلات} عن الفواحش كغفلة عائشة رضي الله عنها عمَّا قذفت به {لعنوا} عُذِّبوا {في الدنيا} بالجلد {و} في {الآخرة} بالنار',NULL,'760',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2816,'{يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَأَيْدِيهِمْ وَأَرْجُلُهُمْ بِمَا كانوا يعملون} وقوله:',NULL,'760',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2817,'{يوفيهم الله دينهم الحق} أي: جزاءهم الواجب {وَيَعْلَمُونَ أَنَّ اللَّهَ هُوَ الْحَقُّ المبين} لأنَّه قد بيَّن لهم حقيقة ما كان بعدهم به في الدُّنيا',NULL,'760',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2818,'{الخبيثات} من القول وقيل: من النِّساء {للخبيثين} من الرِّجال {والخبيثون} من النَّاس {للخبيثات} من القول وقيل: من النِّساء {والطيبات} من القول وقيل: من النِّساء {للطيبين} من النَّاس {والطيبون} من النَّاس {للطيبات} من القول وقيل: من النَّاس {أولئك} يعني: عائشة وصفوان {مبرَّؤون مما يقولون} يقوله أهل الخبث والقاذفون',NULL,'760',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2819,'{يا أيها الَّذِينَ آمَنُوا لا تَدْخُلُوا بُيُوتًا غَيْرَ بُيُوتِكُمْ حتى تستأنسوا} تستأذنوا {وتسلموا على أهلها} وهو أن يقول: السَّلام عليكم أَأَدخلُ',NULL,'761',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2820,'{فإن لم تجدوا فيها} في البيوت {أحداً} يأذن لكم في دخولها {فَلا تَدْخُلُوهَا حَتَّى يُؤْذَنَ لَكُمْ وَإِنْ قِيلَ لكم ارجعوا} انصرفوا {فارجعوا} ولا تقفوا على أبوابهم {هو} أي: الرُّجوع {أزكى لكم} أطهر لكم وأصلح فلمَّا نزلت هذا الآية قيل: يا رسول الله أفرأيت الخانات والمساكن في الطَّريق ليس فيها ساكن؟ فأنزل الله سبحانه',NULL,'761',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2821,'{لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَدْخُلُوا بُيُوتًا غَيْرَ مسكونة} بغير استئذانٍ {فيها متاع} منفعةٌ {لكم} في قضاء حاجةٍ أو نزولٍ وغيره',NULL,'761',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2822,'{قل للمؤمنين يغضوا من أبصارهم} يكفُّوها عن النَّظر إلى ما لا يحلُّ {ويحفظوا فروجهم} عن مَنْ لا يحلُّ وقيل: يستروها حتى لا تظهر وقوله:',NULL,'761',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2823,'{ولا يبدين زينتهن} يعني: الخلخالين والقُرطين والقلائد والدَّماليج ونحوها ممَّا يخفى {إلاَّ ما ظهر منها} وهو الثِّياب والكحل والخاتم والخضاب والسِّوار فلا يجوز للمرأة أن تظهر إلاَّ وجهها ويديها إلى نصف الذِّراع {وليضربن بخمرهنَّ} وليلقين مقانعهنَّ {على جيوبهن} ليسترون بذلك شعورهنَّ وقرطهنَّ وأعناقهنَّ {ولا يبدين زينتهن} يعن: الزينة الخفيَّة لا الظَّاهرة {إلاَّ لبعولتهن} أزواجهنَّ وقوله: {أو نسائهنَّ} يعني: النِّساء المؤمنات فلا يحلُّ لامرأةٍ مسلمةٍ أن تتجرَّد بين يدي امرأةٍ مشركةٍ إلاَّ إذا كانت المشركة مملوكةً لها وهو قوله: {أَوْ مَا مَلَكَتْ أَيْمَانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أولي الإربة من الرجال} يعني: الذين يتَّبعون النِّساء يخدمونهنَّ ليصيبوا شيئاً ولا حاجة لهم فيهنَّ كالخصيِّ والخنثى والشَّيخ الهَرِم والأحمق العنِّين {أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلَى عَوْرَاتِ النساء} لم يقووا عليها {وَلا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِينَ مِنْ زينتهنَّ} أَيْ: لا يضربن بإحدى الرِّجلين على الأخرى ليصيب الخلخالُ الخلخالَ فيعلم أنَّ عليها خلخالين فإنَّ ذلك يحرِّك من الشَّهوة {وتوبوا إلى الله جميعاً} راجعوا طاعة الله سبحانه فيما أمركم ونهاكم عنه من الآداب المذكورة في هذه السُّورة',NULL,'761',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2824,'{وأنكحوا الأيامى منكم} الذين لا أزواج لهم من الرِّجال والنِّساء {والصالحين من عبادكم} عبيدكم {وإمائكم} جورابكم {إِنْ يَكُونُوا فُقَرَاءَ يُغْنِهِمُ اللَّهُ مِنْ فَضْلِهِ} هذا وعدٌ من الله تعالى بالغنى على النِّكاح وإعلامٌ أنَّه سببٌ لنفي الفقر',NULL,'763',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2825,'{وليستعفف} وليعفَّ عن الحرام مَنْ لا يقدر على تزوُّج امرأةٍ بأن لا يملك المهر والنًّفقة {حَتَّى يُغْنِيَهُمُ اللَّهُ مِنْ فَضْلِهِ وَالَّذِينَ يَبْتَغُونَ} يطلبون {الكتاب} المكاتبة {مما ملكت أيمانكم} من عبيدكم وهو أن يطلب من مولاه أن يبيعه منه بمالٍ معلومٍ يُؤدِّيه إليه في مدَّةٍ معلومةٍ فإذا أدَّى ذلك عتق {فكاتبوهم} فأعطوهم ما يطلبون من الكتابة {إن علمتم فيهم خيراً} اكتساباً للمال يقدرون على أداء مال الكتابة {وآتوهم من مال الله الذي آتاكم} يعني: حطُّوا عنهم من المال الذي كاتبتموهم عليه ويستحبُّ ذلك للسيِّد وهو أن يحطَّ عنه ربع المال وقيل: المراد بهذا أن يُؤتوا سهمهم من الزَّكاة {ولا تكرهوا فتياتكم} إماءكم {على البغاء} الزِّنا نزلت في عبد الله ابن أُبيِّ وكانت له جوارٍ يكرههنَّ على الزِّنا ويأخذ منهنَّ أجراً معلوماً {إن أردن تحصناً} قيل: إنَّ هذا راجعٌ إلى قوله: {وَأَنْكِحُوا الأَيَامَى مِنْكُمْ وَالصَّالِحِينَ مِنْ عِبَادِكُمْ وَإِمَائِكُمْ} إن أردن تحصُّناً وقيل: إنْ بمعنى: إذ والمعنى: لا تكرهوهنَّ على الزِّنا إذ أردن التًّعفُّف عنه {لتبتغوا عرض الحياة الدنيا} يعني: ما يؤخذ من أجورهنَّ {ومن يكرههنَّ} على الزِّنا {فإنَّ الله من بعد إكراههنَّ} لهنَّ {غفور رحيم} والوزر على المُكْرِه',NULL,'763',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2826,'{ولقد أنزلنا إليكم آيات مبينات} يعني: القرآن {ومثلاً} وخبراً وعبرةً {من الذين خَلَوا} مضوا {من قبلكم} يعني: ما ذكر من قصص القرون الماضية',NULL,'764',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2827,'{الله نور السماوات والأرض} أَيْ: بنوره وهداه يهتدي من في السماوات والأرض ثمَّ ضرب مثلاً لذلك النُّور الذي يقذفه في قلب المؤمن حتى يهتدي به فقال: {مثل نوره كمشكاة} وهي الكوَّة غير النَّافذة والمراد بها ها هنا الذي وسط القنديل كالكوَّة يُوضع فيها الذُّبالة وهو قوله: {فيها مصباح} يعني: السِّراج {المصباح في زجاجة} لأنَّ النُّور في الزُّجاج وضوء النَّار أبين منه في كلِّ شيءٍ {الزجاجة كأنها كوكب} لبياضه وصفائه {دريّ} منسوبٌ إلى أنه كالدر {يوقد} أي: الزُّجاجة والمعنى للمصباح ولكنه حذف المضاف مَنْ قرأ بالياء أراد: يُوقد المصباح {من شجرة} أَيْ: من زيت شجرةٍ {مباركة زيتونة لا شرقية} ليست ممَّا يطلع عليها الشَّمس في وقت شروقها فقط {ولا غربية} أو عند الغروب والمعنى: ليس يسترها عن الشَّمس في وقتٍ من النَّهار شيءٌ فهو أنضر لها وأجود لزيتها {يكاد زيتها يضيء} لصفائه دون السراج وهو قوله: {وَلَوْ لَمْ تَمْسَسْهُ نَارٌ نُورٌ عَلَى نُورٍ} يعني: نور السراج ونور الزيت ثم قال عزَّ مِنْ قائلٍ: {يهدي الله لنوره مَنْ يشاء} الآية',NULL,'764',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2828,'{في بيوت} أَي: المصباح يوقد فِي بُيُوتٍ يعني: المساجد {أَذِنَ اللَّهُ أَنْ ترفع} تبنى وقوله تعالى:',NULL,'765',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2829,'{تتقلب فيه القلوب} بين الطَّمع في النَّجاة والحذر من الهلاك {والأبصار} تنقلب في أي ناحية نؤخذ بهم أذات اليمين أم ذات الشِّمال؟ ومن أيِّ جهةٍ يُؤتون كُتبهم من جهة اليمين أم من جهة الشِّمال؟',NULL,'765',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2830,'{ليجزيهم الله أحسن} بأحسن {ما عملوا وَيَزِيدَهُمْ مِنْ فَضْلِهِ} ما لم يستحقُّوه بأعمالهم ثمَّ ضرب مثلاً لأعمال الكافرين فقال:',NULL,'765',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2831,'{والذين كفروا أعمالهم كسراب} وهو يا يرى في الفلوات عند شدَّة الحرِّ كأنَّه ماءٌ {بقيعة} جمع قاعٍ وهو المنبسط من الأرض {يحسبه الظمآن} يظنُّه العطشان {ماءً حتى إذا جاءه} جاء موضعه {لم يجده شيئاً} كذلك الكافر يحسب أنَّ عمله مُغنٍ عنه أو نافعه شيئاً فإذا أتاه الموت واحتاج إلى عمله لم يجد عمله أغنى عنه شيئاً {ووجد الله عنده} ووجد الله بالمرصاد عند ذلك {فوفَّاه حسابه} تحمَّل جزاء عمله',NULL,'765',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2832,'{أو كظلمات} وهذا مثلٌ آخرُ ضربه الله لأعمال الكافر {في بحر لجيٍّ} وهو البعيد القعر الكثير الماء {يغشاه} يعلوه {موجٌ} وهو ما ارتفع من الماء {من فوقه موج} متراكمٌ بعضه على بعض {مِن} فوق الموج {سحاب} وهذه كلُّها {ظلمات بعضها فوق بعض} ظلمة السَّحاب وظلمة الموج وظلمة البحر {إذا أخرج} النَّاظر {يده} بين هذه الظُّلمات {لم يكد يراها} لم يرها لشدَّة الظُّلمة وأراد بالظُّلمات أعمال الكفار وبالبحر اللُّجيِّ قلبه وبالموج فوق الموج ما يغشى قلبه من الجهل والشَّكِّ والحيرة وبالسَّحاب الرِّين والختم على قلبه ثمَّ قال: {وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُورًا فَمَا له من نور} أَيْ: مَنْ لم يهده الله للإِسلام لم يهتد',NULL,'766',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2833,'{ألم تر أنَّ الله يسبح له} يصلّي له {من السماوات والأرض} المطيع يُسبِّح له والعاصي يذلُّ أيضاً بخلق الله تعالى إيَّاه على ما يشاء على أنَّ الله بريءٌ من السُّوء {والطير صافات} أجنحتهنَّ في الهواء تسبِّح الله {كلٌّ قد علم صلاته} وهي لبني آدم {وتسبيحه} وهو عامٌّ لغيرهم من الخلق',NULL,'766',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2834,'{ولله ملك السماوات والأرض وإلى الله المصير}',NULL,'766',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2835,'{ألم تر أنَّ الله يزجي} يسوق {سحاباً} إلى حيث يريد {ثمَّ يؤلف بينه} يجمع بين قطع ذلك السَّحاب {ثم يجعله ركاماً} بعضه فوق بعض {فترى الودق} المطر {يخرج من خلاله} فُرَجِه {وينزل من السماء من جبالٍ} في السَّماء {من بَردٍ فيصيب} بذلك البرد {مَنْ يَشَاءُ وَيَصْرِفُهُ عَنْ مَنْ يَشَاءُ يَكَادُ سنا برقه} ضوء برق السَّحاب {يذهب بالأبصار} من شدَّة توقُّده',NULL,'766',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2836,'{يقلب الله الليل والنهار} يُصرِّفهما في اختلافهما وتعاقبهما {إن في ذلك} الذي ذكرت من هذه الأشياء {لعبرة لأولي الأبصار} لذوي العقول',NULL,'767',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2837,'{وَاللَّهُ خَلَقَ كُلَّ دَابَّةٍ مِنْ مَاءٍ} أَيْ: من نطفةٍ {فَمِنْهُمْ مَنْ يَمْشِي على بطنه} كالحيًّات والحيتان {ومنهم من يمشي على رجلين} كالإنس والجنِّ والطَّير {ومنهم من يمشي على أربع} كالبقر والجمال وغيرهما',NULL,'767',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2838,'{لَقَدْ أَنْزَلْنَا آيَاتٍ مُبَيِّنَاتٍ وَاللَّهُ يَهْدِي مَنْ يَشَاءُ إِلَى صِرَاطٍ مُسْتَقِيمٍ}',NULL,'767',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2839,'{ويقولون آمنا بالله} يعني: المنافقين {ثمَّ يتولى} يعرض عن قبول حكم الرَّسول صلى الله عليه وسلم {فريق منهم من بعد ذلك} الإقرار {وَمَا أُولَئِكَ بِالْمُؤْمِنِينَ}',NULL,'767',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2840,'{وَإِذَا دُعُوا إِلَى اللَّهِ} إلى كتاب الله {ورسوله ليحكم بينهم} نزلت في بشر المنافق وخصمه اليهوديّ كان اليهوديُّ يجرُّه إلى رسول الله صلى الله عليه وسلم ليحكم بينهما وجعل المنافق يجرُّه إلى كعب بن الأشرف وهذا إذا كان الحقُّ على المنافقين أعرضوا عن حكم رسول الله صلى الله عليه وسلم لأنَّه كان لا يقبل الرُّشا وإن كان لهم الحقُّ على غيرهم أسرعوا إلى حكمه وهو قوله تعالى:',NULL,'767',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2841,'{وَإِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ} مُطيعين منقادين قال الله تعالى:',NULL,'767',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2842,'{أفي قلوبهم مرض} فجاء بلفظ التَّوبيخ ليكون أبلغ في ذمِّهم {أم ارتابوا} شكُّوا {أَمْ يَخَافُونَ أَنْ يَحِيفَ اللَّهُ عَلَيْهِمْ وَرَسُولُهُ} أي: يظلم {بل أولئك هم الظالمون} لأنفسهم بكفرهم ونفاقهم',NULL,'768',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2843,'{إِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِينَ إِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَنْ يَقُولُوا سَمِعْنَا وَأَطَعْنَا وَأُولَئِكَ هُمُ الْمُفْلِحُونَ}',NULL,'768',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2844,'{وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ وَيَخْشَ اللَّهَ وَيَتَّقْهِ فأولئك هم الفائزون}',NULL,'768',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2845,'{وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ} وذلك أنَّ المنافقين حلفوا أنَّهم يخرجون إلى حيث يأمرهم الرسول صلى الله عليه وسلم للغزو والجهاد فقال الله تعالى: {قل لا تقسموا طاعة معروفة} خيرٌ وأمثلُ من يمينٍ تحنثون فيها',NULL,'768',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2846,'{قُلْ أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّوْا فإنما عليه ما حمَّل} من تبليغ الرِّسالة {وعليكم ما حملتم} من طاعته الآية',NULL,'768',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2847,'{وَعَدَ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَعَمِلُوا الصَّالِحَاتِ ليستخلفنَّهم في الأرض} ليورثنَّهم أرض الكفَّار من العرب والعجم {كما استخلف الذين من قبلهم} يعني: بني إسرائيل {وليمكنَّن لهم دينهم الذي ارتضى لهم} حتى يتمكَّنوا منه من غير خوفٍ {وليبدلنَّهم من بعد خوفهم} من العدوِّ {أمناً} لا يخافون معه العدوَّ {ومن كفر} بهذه النِّعمة فعصى الله ورسوله وسفك الدِّماء {فأولئك هم الفاسقون} فكان أوَّل مَنْ كفر بهذه النِّعمة بعد ما أنجز الله وعده الذين قتلوا عثمان بن عفان رضي الله عنه فعادوا في الخوف وظهر الشر والخلاف',NULL,'768',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2848,'{وَأَقِيمُوا الصَّلاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا الرَّسُولَ لَعَلَّكُمْ ترحمون}',NULL,'769',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2849,'{لا تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الأَرْضِ ومأواهم النار ولبئس المصير}',NULL,'769',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2850,'{يا أيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم} من العبيد والإماء {والذين لم يبلغوا الحلم منكم} من الأحرار {ثلاث مرَّات} ثمَّ بيَّنهنَّ فقال: {من قبل صلاة الفجر} وهو حين يخرج الإنسان من ثياب النَّوم {وحين تضعون ثيابكم من الظهيرة} للقائلة {ومن بعد صلاة العشاء} الآخرة {ثلاث عورات لكم} يعني: هذه الأوقات لأنَّها أوقات التَّجرُّد وظهور العورة {ليس عليكم ولا عليهم جناح} ألا يستأذنوا بعد هذه الأوقات {طوافون} أَيْ: هم طَوَّافُونَ {عَلَيْكُمْ} يريد أنَّهم خدمكم فلا بأس عليهم أن يدخلوا في غير هذه الأوقات الثَّلاثة بغير إذنٍ وهذه الآية منسوخةٌ عند قومٍ وعند قومٍ لم تُنسخ ويجب العمل بها',NULL,'769',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2851,'{وإذا بلغ الأطفال منكم} من أحراركم {الحلم فليستأذنوا} في كلِّ وقتٍ {كما استأذن الذين من قبلهم} يعني: الكبار من الأحرار',NULL,'770',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2852,'{وَالْقَوَاعِدُ مِنَ النِّسَاءِ اللَّاتِي لا يَرْجُونَ نِكَاحًا} يعني: العجائز اللاتي أيسن من البعولة {فليس عليهن جناح أن يضعن ثيابهن} جلابيبهنَّ {غير متبرجات بزينة} غير مُظهراتٍ زينتهنَّ وهو أن لا تريد بوضع الجلباب أن تُري زينتها {وأن يستعففن} فلا يضعن الجلباب {خيرٌ لهن}',NULL,'770',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2853,'{ليس على الأعمى حرج} الآية كان المسلمون يخرجون للغزو ويدفعون مفاتيح بيوتهنَّ إلى الزَّمنى الذين لا يخرجون ويقولون لهم: قد أحللنا لكم أن تأكلوا مما فيها فكانوا يتوفون ذلك حتى نزلت هذه الآية وقوله: {ولا على أنفسكم} أراد: ولا عليكم أنفسكم {أن تأكلوا من بيوتكم} أَيْ: بيوت أولادكم فجعل بيوت أولادهم بيوتهم لأنَّ ولد الرَّجل من كسبه ومالَه كمالِه وقوله: {أو ما ملكتم مفاتحه} يريد: الزَّمنى الذين كانوا يخزنون للغزاة {ليس عليكم جناح أن تأكلوا} من منازل هؤلاءٍ إذا دخلتموها وإن لم يحضروا ولم يعلموا من غير أن يحملوا وهذه رخصةٌ من الله تعالى لطفاً بعباده ورغبة بهم عن دناءة الأخلاق وضيق النَّظر وقوله: {أَوْ صديقكم} يجوز للرَّجل أن يدخل بيت صديقه فيتحرَّم بطعامه من غير استئذانٍ بهذه الآية وقوله: {أن تأكلوا جميعاً أو أشتاتاً} يقول: لا جناح عليكم إن اجتمعتم في الأكل أو أكلتم فرادى وإن اختلفتم فكان فيكم الزَّهيد والرغيب والعليل والصَّحيح وذلك أنَّ المسلمين تركوا مؤاكلة المرضى والزَّمنى بعد نزول قوله تعالى: {ولا تأكلوا أموالكم بينكم بالباطل} فقالوا: إنَّهم لا يستوفون من الأكل فلا تحلُّ لنا مؤاكلتهم فنزلت الرُّخصة في هذه الآية {فإذا دخلتم بيوتاً فسلموا على أنفسكم} فليسلِّم بعضكم على بعض وقيل: إذا دخلتم بيوتاً خالية فليقل الدَّاخل: السَّلام علينا وعلى عباد الله الصالحين وقوله تعالى:',NULL,'770',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2854,'{وإذا كانوا معه على أمر جامع} يجمعهم في حربٍ حضرت أو صلاةٍ في جمعةٍ أو تشاورٍ في أمرٍ {لم يذهبوا} لم يتفرقوا عن النبيِّ صلى الله عليه وسلم {حتى يستأذنوه} نزلت في حفر الخندق كان المنافقون ينصرفون بغير امر رسول الله صلى الله عليه وسلم وقوله:',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2855,'{اتَجْعَلُوا دُعَاءَ الرَّسُولِ بَيْنَكُمْ كَدُعَاءِ بَعْضِكُمْ بَعْضًا} أَيْ: لا تقولوا إذا دعوتموه: يا محمد كما يقول أحدكم لصاحبه ولكن قولوا: يا رسول الله يا نبيَّ الله {قد يعلم الله الذين يتسللون} يخرجون في خُفيةٍ من بين النَّاس {لواذاً} يستتر بغيره فيخرج مُختفياً {فليحذر الذين يخالفون عن أمره} أَيْ: يخافون أمر الرسول صلى الله عليه وسلم وينصرمن بغير إذنه {أن تصيبهم فتنة} بلية يظهر نفاقهم {أو يصيبهم عذاب أليم} عاجلٌ في الدُّنيا',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2856,'{ألا إنَّ لله ما في السماوات والأرض} عبيداً وملكاً وخلقاً',NULL,'722',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2857,'{تبارك} ثبت ودام {الذي نزل الفرقان} القرآن الذي فرق بين الحقِّ والباطل {على عبده} محمد صلى الله عليه وسلم {ليكون للعالمين} الجنِّ والإِنس {نذيراً} مخوِّفاً من العذاب',NULL,'773',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2858,'{وخلق كلَّ شيء} ممَّا يُطلق في صفة المخلوق {فقدَّره تقديراً} جعله على مقدارٍ وقوله:',NULL,'773',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2859,'{نشوراً} أَيْ: حياةً بعد الموت',NULL,'773',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2860,'{وقال الذين كفروا إن هذا} ما هذا القرآن {إلاَّ إفك} كذبٌ {افتراه} اختلقه {وأعانه عليه قوم آخرون} يعنون: اليهود {فقد جاؤوا} بهذا القول {ظلماً وزوراً} كذباً',NULL,'733',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2861,'{وقالوا أساطير الأولين} أَيْ: هو ما سطره الأوَّلون {اكتتبها} كتبها {فهي تملى عليه بكرة وأصيلاً} يعنون أنَّه يختلف إلى مَنْ يعلَّمه بالغداة والعشيِّ',NULL,'774',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2862,'{قل} يا محمد لهم: {أنزله} أنزل القرآن {الذي يعلم السر في السماوات والأرض} يعلم بواطن الأمور فقد أنزله على ما يقتضيه علمه',NULL,'774',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2863,'{وقالوا مال هذا الرسول} يعنون محمداً عليه السَّلام {يأكل الطعام} أنكروا أن يكون الرَّسول بصفة البشر {ويمشي في الأسواق} طلباً للمعاش يعنون أنَّه ليس بملك ولام مَلَكٍ {لولا} هلاَّ {أنزل إليه ملك} يُصدِّقه {فيكون معه نذيراً} داعياً إلى الله يشاركه في النُّبوَّة',NULL,'774',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2864,'{أو يلقى إليه كنز} يستغني به عن طلب المعاش {وقال الظالمون} المشركون: {إن تتبعون} ما تتبعون {إِلا رَجُلا مسحوراً} مخدوعاً',NULL,'774',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2865,'{انْظُرْ} يا محمد {كَيْفَ ضَرَبُوا لَكَ الأَمْثَالَ} إذ مثَّلوك بالمسحور والفقير الذي لا يصلح أن يكون رسولاً والناقص عن القيام بالأمور إذ طلبوا أن يكون معك مَلَك {فضلوا} بهذا القول عن الدَّين والإِيمان {فلا يستطيعون سبيلاً} إلى الهدى ومخرجاً من ضلالتهم',NULL,'774',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2866,'{تَبَارَكَ الَّذِي إِنْ شَاءَ جَعَلَ لَكَ خَيْرًا من ذلك} الذي قالوه من إلقاء الكنز وجعل الجنَّة ثمَّ بيَّن ذلك فقال: {جنات تجري من تحتها الأنهار} يعني: في الدُّنيا لأنَّه قد شاء أن يعطيه ذلك في الآخرة وقوله:',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2867,'{سمعوا لها تغيظاً} أَيْ: صوتاً بغيظٍ وهو التَّغضُّب {وزفيرا} صوتا شديدا',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2868,'{إِذَا رَأَتْهُمْ مِنْ مَكَانٍ بَعِيدٍ سَمِعُوا لَهَا تغيظا وزفيرا}',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2869,'{وإذا ألقوا منها مكاناً ضيقاً} وذلك أنَّهم يُدفعون في النَّار كما يُدفع الوتد في الحائط {مقرَّنين} مقرونين مع الشيطان {دعوا هنالك ثبوراً} ويلاً وهلاكاً فيقال لهم:',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2870,'{لا تَدْعُوا الْيَوْمَ ثُبُورًا وَاحِدًا وَادْعُوا ثُبُورًا كثيراً}',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2871,'{قل أذلك} الذي ذكرتُ من موضع أهل النَّار ومصيرهم {خيرٌ أم جنة الخلد} الآية وقوله:',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2872,'{وعداً مسؤولاً} لأنَّ الملائكة سألت ذلك لهم في قوله تعالى: {ربَّنا وأَدْخِلْهم جنَّاتِ عدنٍ التي وَعَدْتَهُم ومن صلح من آبائهم وأزوَاجِهم وذُرِّيَّاتهم}',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2873,'{وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ} الأصنام والملائكة والمسيح وعزيراً {فيقول أأنتم أضللتم عبادي هؤلاء} هذا توبيخ للكفَّار كقوله لعيسى عليه السلام: {أَأَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَأُمِّيَ إِلَهَيْنِ مِنْ دون الله} ؟ !',NULL,'775',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2874,'{قَالُوا سُبْحَانَكَ مَا كَانَ يَنْبَغِي لَنَا أَنْ نتَّخذ من دونك من أولياء} أن نوالي أعداءك وفي هذا براءةُ معبوديهم منهم {ولكن متعتهم وآباءهم} في الدُّنيا بالصَّحة والنِّعمة {حتى نسوا الذكر} تركوا ما وُعظوا به {وكانوا قوماً بوراً} هلكى بكفرهم',NULL,'776',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2875,'{فقد كذبوكم بما تقولون} بقولكم: إنَّهم كانوا آلهة {فما تستطيعون} يعني الآلهة {صرفاً} للعذاب عنكم {ولا نصراً} لكم {ومن يظلم} أَيْ: يشرك {منكم نذقه عذاباً كبيراً}',NULL,'776',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2876,'{وما أرسلنا قبلك} الآية هذا جواب لقولهم: {مال هذا الرسول} الآية أخبر الله سبحان أنَّ كلَّ مَنْ خلا من الرُّسل كان بهذه الصِّفة {وجعلنا بعضكم لبعض فتنة} الصَّحيح للمريض والغنيّ للفقير فيقول الفقير: لو شاء الله لأغناني كما أغنى فلاناً ويقول المريض لو شاء الله لعافاني كما عافى فلاناً وكذلك كلُّ النَّاس مبتلى بعضهم ببعض فقال الله تعالى: {أتصبرون} على البلاء؟ فقد عرفتم ما وُعد الصَّابرون {وكان ربك بصيراً} بمَنْ يصبر وبمَنْ يجزع',NULL,'776',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2877,'{وقال الَّذِينَ لا يَرْجُونَ لِقَاءَنَا} لا يخافون البعث: {لولا} هلاَّ {أنزل علينا الملائكة} فتخبرنا أنَّ محمداً صادقٌ {أو نرى ربنا} فيخبرنا بذلك {لقد استكبروا في أنفسهم} حين طلبوا من الآيات ما لم يطلبه أُمَّة {وعتوا عتوّاً كبيراً} وغلوا في كفرهم أشدَّ الغلوِّ',NULL,'777',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2878,'{يوم يرون الملائكة} يعني: إنَّ ذلك اليوم الذي يرون فيه الملائكة هو يوم القيامة وإنَّ الله سبحانه حرمهم البشرى في ذلك اليوم وتقول لهم الملائكة: {حجراً محجوراً} أَيْ: حراماً محرَّماً عليهم البشرى',NULL,'777',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2879,'{وقدمنا} وقصدنا {إلى ما عملوا من عمل} ممَّا كانوا يقصدون به التقرُّب إلى الله سبحانه {فجعلناه هباءً منثوراً} باطلاً لا ثواب له لأنَّهم عملوه للشَّيطان والهباء: دقاق التُّراب والمنثور: المتفرِّق',NULL,'777',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2880,'{أصحاب الجنة يومئذ خيرٌ مستقراً} موضع قرار {وأحسن مقيلاً} موضع قيلولة',NULL,'777',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2881,'{ويوم تشقق السماء بالغمام} عن الغمام وهو السَّحاب الأبيض الرَّقيق {ونزل الملائكة تنزيلاً} لإكرام المؤمنين',NULL,'777',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2882,'{الملك يومئذ الحق} أَيْ: الملك الذي هو الملك حقَّاً ملك الرَّحمن يومئذ',NULL,'777',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2883,'{ويوم يعض الظالم} الكافر يعني: عُقبة بن أبي مُعَيط كان قد آمن ثمَّ ارتدَّ لرضى أُبيّ بن خلف {على يديه} ندماً وتحسُّراً {يَقُولُ: يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلا} طريقاً إلى الجنة بالإسلام',NULL,'778',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2884,'{يا ويلتى ليتني لم أتخذ فلاناً} يعني: أُبيَّاً {خليلاً}',NULL,'778',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2885,'{لقد أضلني عن الذكر} القرآن {بَعْدَ إِذْ جَاءَنِي وَكَانَ الشَّيْطَانُ لِلإِنْسَانِ خَذُولا} عند البلاء يعني: إنًّ قبوله قول أُبيِّ بن خلف في الكفر كان من عمل الشيطان',NULL,'778',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2886,'{وقال الرسول} في ذلك اليوم: يا {ربِّ إنَّ قومي اتخذوا هذا القرآن مهجوراً} متروكاً أعرضوا عنه',NULL,'778',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2887,'{وكذلك} وكما جعلنا لك أعداءً من المشركين {جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِنَ الْمُجْرِمِينَ وَكَفَى بربك هادياً} يهديك وينصرك فلا تُبالِ بِمَنْ يعاديك',NULL,'778',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2888,'{وقال الذين كفروا لولا نزل عليه القرآن جملةً واحدة} أَيْ: لم نزل عليه متفرِّقاً؟ وهلاَّ كان دفعةً واحدةُ كالتَّوراة والإِنجيل؟ قال الله تعالى: {كذلك} فرَّقنا تنزيله {لنثبت به فؤادك} لِنُقوِّيَ به قلبك وذلك أنَّه كلَّما نزل عليه وحيٌ جديدٌ ازداد هو قوَّة قلبٍ {ورتلناه ترتيلا} بيناه تبينا في تثبُّتٍ ومهلةٍ',NULL,'778',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2889,'{ولا يأتونك} يعني: المشركين {بمثل} يضربونهن في إبطال أمرك {إلاَّ جئناك بالحق} بما يردُّ ما جاؤوا به من المثل {وأحسن تفسيراً} بياناً وتفصيلاً ممَّا ذكروا',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2890,'{الذين} أَيْ: هم الذين {يحشرون على وجوههم} يُمشيهم الله عليها فهم يُساقون عَلَى وُجُوهِهِمْ {إِلَى جَهَنَّمَ أُولَئِكَ شَرٌّ مَكَانًا وأضلُّ سبيلاً} من كلِّ أحدٍ',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2891,'{وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُ أَخَاهُ هارون وزيراً} أَيْ: مُعيناً وملجأ',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2892,'{فَقُلْنَا اذْهَبَا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا} وهم القبط فكذَّبوهما {فدمَّرناهم تدميراً} أهلكناهم إهلاكاً',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2893,'{وقوم نوحٍ لمَّا كذَّبوا الرسل} مَنْ كذَّب نبيَّاً فقد كذَّب الرُّسل كلَّهم لأنَّهم لا يفرِّقون بينهم في الإيمان بهم {أغرقناهم وجعلناهم للناس آية} عبرة {وأعتدنا للظالمين} في الآخرة {عذاباً اليماً} سوى ما ينزل بهم من عاجل العذاب وقوله:',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2894,'{وأصحاب الرَّسِّ} كانوا أهل بئرٍ قعودٍ عليها وأصحاب مواشٍ يعبدون الأصنام فأُهلكوا بتكذيب نبيِّهم {وقروناً} وجماعاتٍ {بين ذلك} الذين ذكرناهم {كثيراً}',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2895,'{وكلاً ضربنا له الأمثال} بيَّنا لهم الأشباه في إقامة الحجَّة عليهم {وكلاًّ تبرنا تتبيراً} أهلكنا إهلاكاً',NULL,'779',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2896,'{ولقد أتوا} يعني: مشركي مكَّة {على القرية التي أمطرت مطر السوء} يعني: الحجارة وهي قرية قوم لوطٍ {أفلم يكونوا يرونها} إذا مرُّوا بها مسافرين فيعتبروا {بل كانوا لا يرجون نشوراً} لا يخافون بعثاً',NULL,'780',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2897,'{وإذا رأوك إن يتخذونك إلاَّ هزواً} ما يتَّخذونك إلاَّ مهزوءاً به ويقولون: {أهذا الذي بعث الله رسولاً} إلينا؟',NULL,'780',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2898,'{إن كاد} إنَّه كاد {ليضلنا عن آلهتنا} فيصدُّنا عن عبادتها {لولا أن صبرنا عليها} لصرفنا عنها',NULL,'780',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2899,'{أرأيت من اتخذ إلهه هواه} وهو أنَّهم كانوا يعبدون شيئاً حجراً أو ما كان فإذا رأوا حجراً أحسن طرحوا الأوَّل وعبدوا الأحسن فهم يعبدون ما تهواه أنفسهم {أفأنت تكون عليه وكيلا} حفيفا حتى تردَّه إلى الإِيمان أَيْ: ليس عليك إلاَّ التبليغ وقيل: إنَّ هذا ممَّا نسخته آية السَّيف',NULL,'780',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2900,'{أم تحسب أنَّ أكثرهم يسمعون} سماع تفهيم {أو يعقلون} بقلوبهم ما تقول لهم: {إن هم} ما هم {إلاَّ كالأنعام} في جهل الآيات وما جعل لهم من الدَّليل {بل هم أضلُّ سبيلاً} لأنَّ النَّعم تنقاد لمن يتعهده وهم لا يطيعون مولاهم الذي أنعم عليهم',NULL,'780',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2901,'{ألم تر} ألم تعلم {إلى ربك كيف مدَّ الظلَّ} وقت الإِسفار إلى وقت طلوع الشَّمس {ولو شاء لجعله} لجعل الظلَّ {ساكناً} ثابتاً دائماً {ثمَّ جعلنا الشمس عليه دليلاً} لأنَّ بالشَّمس يُعرف الظِّلُّ',NULL,'780',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2902,'{ثم قبضناه} قبضنا الظِّلَّ إلينا بارتفاع الشَّمس {قبضاً يسيراً} قيل: خفيَّاً وقيل: سهلاً',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2903,'{وهو الذي جعل لكم الليل لباساً} يستركم {والنوم سباتاً} راحةً لأبدانكم {وجعل النهار نشوراً} حياة تنتشرون فيه من النَّوم وقوله:',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2904,'{طهوراً} هو الطَّاهر المُطهِّر',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2905,'{لنحيي به} بالماء الذي أنزلناه من السَّماء {بلدة ميتا} بالجدوية {ونسقيه مما خلقنا أنعاماً وأناسيَّ كثيراً} جمع إنسيٍّ وهم الذين سقيناهم المطر',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2906,'{ولقد صرفناه} أَيْ: المطر {بينهم} بأنواعه وابلاً وطشَّاً ورُهَاماً ورذاذاً {ليذكروا} ليتذكَّروا به نعمة الله تعالى {فأبى أكثر الناس إلاَّ كفوراً} جُحوداً حين قالوا: سُقينا بِنَوء كذا',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2907,'{وَلَوْ شِئْنَا لَبَعَثْنَا فِي كُلِّ قَرْيَةٍ نَذِيرًا} لنخفِّف عليك أعباء النبوَّة ولكن لم نفعل ذلك ليعظم أجرك',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2908,'{فلا تطع الكافرين} في هواهم ولا تداهنهم {وجاهدهم به} وجاهد بالقرآن {جهاداً كبيراً} لا يُخالطه فتورٌ',NULL,'781',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2909,'{وهو الذي مرج البحرين} خلطهما {هذا عذب فرات} شديد العذوبة {وهذا ملح أجاج} شديد الملوحة {وجعل بينهما} بين العذب والمالح {برزخاً} حاجزاً من قدرته حتى لا يختلط أحدهما بالآخر {وحجراً محجوراً} حراماً محرَّماً أن يغلب أحدهما صاحبه',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2910,'{وهو الذي خلق من الماء} النُّطفة {بشراً} آدمياً {فجعله نسباً} لا يحلُّ تزوُّجه {وصهراً} يحلُّ تزوُّجه كابنة العمِّ والخال وابنهما {وكان ربك قديراً} قادراً على ما يشاء وقوله:',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2911,'{وكان الكافر على ربه ظهيراً} معيناً للشَّيطان على معصية الله سبحانه',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2912,'{وَمَا أَرْسَلْنَاكَ إِلا مُبَشِّرًا وَنَذِيرًا}',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2913,'{قل ما أسألكم عليه} على تبليغ الرِّسالة والوحي {من أجر} فيقولون: إنَّه يطلب أموالنا {إلاَّ من شاء} لكن مَنْ شاء {أَنْ يَتَّخِذَ إِلَى رَبِّهِ سبيلا} بإنفاق ماله وقوله:',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2914,'{وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِي لا يَمُوتُ وَسَبِّحْ بحمده وكفى به بذنوب عباده خبيرا}',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2915,'{فاسأل به خبيراً} فاسأل أيُّها الإنسان الذي لا تعلم صفته خبيراً يخبرك بصفاته',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2916,'{وإذا قيل لهم} لهؤلاء المشركين: {اسجدوا للرحمن} وهو اسم الله سبحانه كانوا لا يعرفونه لذلك قالوا: {وما الرحمن أنسجد لما تأمرنا} أنت يا محمد {وزادهم} قول القاتل لهم: اسجدوا للرَّحمن {نفوراً} عن الإِيمان',NULL,'782',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2917,'{تبارك الذي جعل في السماء بروجاً} أَيْ: منازل الكواكب السَّبعة {وجعل فيها سراجاً} وهو الشَّمس',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2918,'{وهو الذي جعل الليل والنهار خلفة} إذا وهب هذا أتى هذا فأحدهما يخلف الآخر فمَنْ فاته عملٌ بالليل فله مُسْتَدْرَكٌ بالنَّهار وهو قوله: {لِمَنْ أَرَادَ أَنْ يَذَّكَّرَ} يذكر الله بصلاةٍ وتسبيحٍ وقراءةٍ {أو أراد شكوراً} شكراًَ لنعمته وطاعته',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2919,'{وعباد الرحمن} يعني: خواصَّ عباده {الذين يمشون على الأرض هوناً} بالسَّكينة والوقار {وإذا خاطبهم الجاهلون} بما يكرهونه {قالوا سلاماً} سداداً من القول يسلمون فيه من الإثم وقوله:',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2920,'{والذين يبيتون لربهم سجدا وقياما}',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2921,'{غراماً} أيْ: شرَّاً لازماً',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2922,'{إِنَّهَا سَاءَتْ مُسْتَقَرًّا وَمُقَامًا}',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2923,'{والذين إذا أنفقوا لم يسرفوا} لم يكن إنفاقهم في معصية الله تعالى {ولم يقتروا} لم يمنعوا حقَّ الله سبحانه {وكان} إنفاقهم بين الإِسراف والإِقتار {قواما} قائماً قوله:',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2924,'{يلق أثاماً} أَيْ: عقوبةً وقيل: جزاء الآثام وقوله:',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2925,'{يُضَاعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيَامَةِ وَيَخْلُدْ فِيهِ مهانا}',NULL,'783',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2926,'{يبدّل الله سيئاتهم حسنات} يُبدِّلهم الله بقبائح أعمالهم في الشِّرك محاسن الأعمال في الإسلام بالشِّرك إيماناً وبالزِّنا عفَّة وإحصاناً وبقتل المؤمنين قتل المشركين',NULL,'784',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2927,'{ومن تاب} أَيْ: عزم على التَّوبة {فإنه يتوب إلى الله متاباً} فينبغي أن يبادر إليها ويتوجَّه بها إلى الله',NULL,'784',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2928,'{والذين لا يشهدون الزور} لا يشهدون بالكذب {وإذا مرُّوا باللغو مروا كراماً} سمعوا من الكفار الشَّتم والأذى صفحوا وأعرضوا وهو منسوخ بالقتال على هذا التَّفسير',NULL,'784',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2929,'{والذين إذا ذكروا} وُعظوا {بآيات ربهم} بالقرآن {لم يخروا عليها صماً وعمياناً} لم يتغافلوا عنها كأنَّهم صمٌّ لم يسمعوها وعميٌّ لم يروها',NULL,'784',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2930,'{وَالَّذِينَ يَقُولُونَ رَبَّنَا هَبْ لَنَا مِنْ أَزْوَاجِنَا وذرياتنا قرة أعين} بأَنْ نراهم مطيعين لك صالحين {واجعلنا للمتقين إماماً} أَيْ: اجعلنا ممَّن يهتدي به المُتَّقون ويهتدي بالمتَّقين',NULL,'784',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2931,'{أولئك يجزون} يثابون {الغرفة} الدرجة في الجنة {بما صبروا} على طاعة الله سبحانه {ويلقون} ويُستقبلون {فيها} في الغرفة بالتحية والسلام',NULL,'785',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2932,'{خَالِدِينَ فِيهَا حَسُنَتْ مُسْتَقَرًّا وَمُقَامًا}',NULL,'785',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2933,'{قل ما يعبأ بكم} أَيْ: ما يفعل ويصنع وأيُّ وزنٍ لكم عنده {لولا دعاؤكم} توحيدكم وعبادتكم إياه {فقد كذبتم} يا أهل مكَّة فخرجتم عن أن يكون لكم عنده مقدار {فسوف يكون} العذاب لازماً لكم',NULL,'785',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2934,'{طسم} أقسم الله بطَوله وسنانه وملكه',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2935,'{تلك} هذه {آيات الكتاب المبين} يعني: القرآن',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2936,'{لعلَّك باخعٌ نفسك} قاتلٌ نفسك {أن لا يكونوا مؤمنين} لتركهم الإِيمان وذلك أنَّه لما كذَّبه أهل مكًّة شقَّ عليه ذلك فأعلمه الله سبحانه أنَّه لو شاء لاضطرهم إلى الإِيمان فقال:',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2937,'{إِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّمَاءِ آيَةً فظلت أعناقهم لها خاضعين} يذلُّون بها فلا يلوي أحدٌ منهم عنقه إلى معصية الله تعالى',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2938,'{وما يأتيهم من ذكرٍ} من وعظٍ {من الرحمن محدث} في الوحي والتَّنزيل',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2939,'{فسيأتيهم أنباء ما كانوا به يستهزئون} فسيعلمون نبأ ذلك وهو وعيدٌ لهم وقوله:',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2940,'{كَمْ أَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ} من كلِّ نوع محمودٍ ممَّا يحتاج إليه النَّاس',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2941,'{إنَّ في ذلك لآية} لدلالةً على توحيد الله سبحانه وقدرته {وما كان أكثرهم مؤمنين} لما سبق في علمي وقضائي',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2942,'{وإن ربك لهو العزيز الرحيم}',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2943,'{و} اذكر يا محمَّدُ {إذ نادى ربك موسى} ليلة رأى الشَّجرة والنَّار {أن ائت القوم الظالمين} لأنفسهم بالكفر',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2944,'{قوم فرعون ألا يتقون} ألا يخافون الله سبحانه فيؤمنوا به',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2945,'{قَالَ رَبِّ إِنِّي أخاف أن يكذبون}',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2946,'{وَيَضِيقُ صدري} من تكذيبهم إيَّاي {ولا ينطلق لساني} بأداء الرِّسالة للعقدة التي في فيه {فأرسل إلى هارون} ليظاهرني على التَّبليغ',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2947,'{ولهم عليَّ ذنب} بقتل القبطيِّ',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2948,'{قال كلا} لا يقتلونك {إنَّا معكم} بالنُّصرة {مستمعون} نسمع ما تقول ويقال لك',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2949,'{فأتيا فرعون فقولا إنا رسول} ذوا رسالة {ربّ العالمين}',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2950,'{أن أرسل معنا بني إسرائيل} مفسَّرٌ في سورة طه فلمَّا أتاه بالرِّسالة عرفه فرعون فقال:',NULL,'787',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2951,'{ألم نُربِّك فينا وليداً} صبيّاً {ولبثت فينا من عمرك سنين} ثلاثين سنةً',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2952,'{وَفَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ} يعني: قتل القبطيِّ {وأنت من الكافرين} الجاحدين لنعمتي عليك',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2953,'{قَالَ} موسى: {فَعَلْتُهَا إِذًا وَأَنَا مِنَ الضَّالِّينَ} الجاهلين لم يأتني من الله شيء',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2954,'{فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِي رَبِّي حُكْمًا وَجَعَلَنِي مِنَ الْمُرْسَلِينَ}',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2955,'{وتلك نعمة تمنُّها عليَّ} أقرَّ بإنعامه عليه فقال: هي نعمةٌ إذ ربَّيتني ولم تستعبدني كاستعبادك بني إسرائيل {عبَّدت} معناه: اتَّخذت عبيداً',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2956,'{قال فرعون وما ربُّ العالمين} أَيُّ شيءٍ ربُّ العالمين الذي تزعم أنَّك رسوله؟',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2957,'{قال رب السماوات وَالأَرْضِ وَمَا بَيْنَهُمَا إِنْ كُنْتُمْ مُوقِنِينَ} أنَّه خالقهما',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2958,'{قال} فرعون {لمن حوله} من أشراف قومه مُعجبِّاً لهم: {ألا تستمعون} إلى ما يقوله: موسى؟ ! فقال موسى',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2959,'{ربكم وربُّ آبائكم الأولين}',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2960,'{قال} فرعون: {إن رسولكم الذي أرسل إليكم لمجنون} يتكلَّم بكلام لا تعرف صحَّته',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2961,'{قال} موسى: {رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَا إِنْ كُنْتُمْ تعقلون} فقال فرعون حين لزمته الحجَّة',NULL,'788',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2962,'{لَئِنِ اتَّخَذْتَ إِلَهًا غَيْرِي لأَجْعَلَنَّكَ مِنَ الْمَسْجُونِينَ} من المحبوسين في السِّجن',NULL,'789',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2963,'{قال} موسى: {أو لو جِئِتُكَ بشيء مبين} يعني: أو نفعل ذلك وإن أتيتُك على ما أقول بحجَّةٍ بيِّنةٍ؟',NULL,'789',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2964,'{قال فأت به} مفسَّر أكثره إلى قوله تعالى:',NULL,'789',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2965,'{فَأَلْقَى عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُبِينٌ}',NULL,'789',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2966,'{وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ}',NULL,'789',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (2967,'{قَالَ لِلْمَلإِ حَوْلَهُ إِنَّ هَذَا لَسَاحِرٌ عَلِيمٌ}',NULL,'789',NULL,NULL,'0');
