INSERT INTO [b2547] VALUES (3070,'{إن هذا} ما هذا الذي تدعونا إليه {إلا خَلْق الأوَّلين} كذبهم وافتراؤهم ومَنْ قرأ {خُلق الأولين} فمعناه: عادة الأوَّلين أَيْ: الذي نحن فيه عادة الأوَّلين يعيشون ما عاشوا ثمَّ يموتون ولا بعثٌ ولا حساب وقوله:',NULL,'793',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3071,'{وَمَا نَحْنُ بِمُعَذَّبِينَ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3072,'{فَكَذَّبُوهُ فَأَهْلَكْنَاهُمْ إِنَّ فِي ذَلِكَ لآيَةً وَمَا كان أكثرهم مؤمنين}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3073,'{إن رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3074,'{كَذَّبَتْ ثَمُودُ الْمُرْسَلِينَ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3075,'{إِذْ قَالَ لَهُمْ أَخُوهُمْ صَالِحٌ أَلا تَتَّقُونَ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3076,'{إِنِّي لَكُمْ رَسُولٌ أَمِينٌ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3077,'{فَاتَّقُوا اللَّهَ وَأَطِيعُونِ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3078,'{وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إلاَّ على رب العالمين}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3079,'{أتتركون في ما هاهنا} أَيْ: في الدُّنيا {آمنين} من الموت والعذاب وقوله:',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3080,'{في جنات وعيون}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3081,'{ونخل طلعها} أَيْ: ثمرها {هضيم} أَيْ: ليِّنٌ نضيجٌ',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3082,'{وَتَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتًا فَارِهِينَ} حاذقين بنحتها و {فارهين} أشرين بطرين وكانوا مُعمَّرين لا يبقى البناء مع عمرهم فنحتوا في الجبال بيوتا وقوله:',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3083,'{فَاتَّقُوا اللَّهَ وَأَطِيعُونِ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3084,'{وَلا تُطِيعُوا أَمْرَ الْمُسْرِفِينَ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3085,'{الذين يفسدون في الأرض ولا يصلحون}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3086,'{إنما أنت من المسحرين} أَيْ: من الذين سُحروا مرَّةً بعد أخرى: وقيل ممَّن له سَحر وهو الرِّئة أَيْ: إنَّما أنت بشر مثلنا وقوله:',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3087,'{مَا أَنْتَ إِلا بَشَرٌ مِثْلُنَا فَأْتِ بِآيَةٍ إِنْ كُنْتَ مِنَ الصادقين}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3088,'{لها شربٌ} أَيْ: حظٌّ ونصيب من الماء',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3089,'{ولا تمسوها بسوء} بعقر وقوله:',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3090,'{فَعَقَرُوهَا فَأَصْبَحُوا نَادِمِينَ}',NULL,'794',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3091,'{فَأَخَذَهُمُ الْعَذَابُ إِنَّ فِي ذَلِكَ لآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3092,'{وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3093,'{كَذَّبَتْ قَوْمُ لُوطٍ الْمُرْسَلِينَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3094,'{إِذْ قَالَ لَهُمْ أَخُوهُمْ لُوطٌ أَلا تَتَّقُونَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3095,'{إِنِّي لَكُمْ رَسُولٌ أَمِينٌ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3096,'{فَاتَّقُوا اللَّهَ وَأَطِيعُونِ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3097,'{وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إلاَّ على رب العالمين}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3098,'{أتأتون الذكران من العالمين} يريد: ما كان من فعل قوم لوطٍ مِنْ إتيان الرِّجال في أدبارهم',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3099,'{وَتَذَرُونَ مَا خَلَقَ لكم ربكم من أزواجكم} وتدَعون أن تأتوا نسائكم {بل أنتم قوم عادون} ظالمون غاية الظُّلم',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3100,'{قَالُوا: لَئِنْ لَمْ تَنْتَهِ يَا لُوطُ لَتَكُونَنَّ من المخرجين} عن بلدنا',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3101,'{قال: إني لعملكم} يعني: اللِّواط {من القالين} من المبغضين وقوله:',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3102,'{رَبِّ نَجِّنِي وَأَهْلِي مِمَّا يَعْمَلُونَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3103,'{فنجيناه وأهله أجمعين}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3104,'{إلاَّ عجوزاً} يعني: امرأته {في الغابرين} في الباقين في العذاب',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3105,'{ثم دمرنا} أهلكنا',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3106,'{وَأَمْطَرْنَا عَلَيْهِمْ مَطَرًا فَسَاءَ مَطَرُ الْمُنذَرِينَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3107,'{إِنَّ فِي ذَلِكَ لآيَةً وَمَا كَانَ أَكْثَرُهُمْ مُؤْمِنِينَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3108,'{وإن ربك لهو العزيز الرحيم}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3109,'{كذَّب أصحاب الأيكة} وهي الغيضة وهم قوم شعيب',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3110,'{إِذْ قَالَ لَهُمْ شُعَيْبٌ أَلا تَتَّقُونَ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3111,'{إِنِّي لَكُمْ رَسُولٌ أَمِينٌ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3112,'{فَاتَّقُوا اللَّهَ وَأَطِيعُونِ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3113,'{وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إلاَّ على رب العالمين}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3114,'{أوفوا الكيل} أتمُّوه {ولا تكونوا من المخسرين} النَّاقصين للكيل والوزن وقوله:',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3115,'{وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ}',NULL,'795',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3116,'{وَلا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلا تَعْثَوْا فِي الأرض مفسدين}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3117,'{والجبلَّة الأولين} أَيْ: الخليقة السابقين',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3118,'{قَالُوا إِنَّمَا أَنْتَ مِنَ الْمُسَحَّرِينَ}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3119,'{وَمَا أَنْتَ إِلا بَشَرٌ مِثْلُنَا وَإِنْ نَظُنُّكَ لمن الكاذبين}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3120,'{فأسقط علينا كسفاً من السماء} أَيْ: قطعةً',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3121,'{قال ربي أعلم بما تعملون} فيجازيكم به وما عليَّ إلاَّ الدَّعوة',NULL,'786',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3122,'{فكذبوه فأخذهم عذاب يوم الظلة} وذلك أنَّ الحرَّ اخذهم فلم ينفعهم ماءٌ ولا كَنٌّ فخرجوا إلى البرِّيَّة وأظلَّتهم سحابةٌ وجدوا لها برداً واجتمعوا تحتها فأمطرت عليهم ناراً فاحترقوا به وقوله:',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3123,'{إِنَّ فِي ذَلِكَ لآيَةً وَمَا كَانَ أَكْثَرُهُمْ مؤمنين}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3124,'{وإن ربك لهو العزيز الرحيم}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3125,'{وإنه} يعني: القرآن {لَتَنْزِيلُ رَبِّ الْعَالَمِينَ}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3126,'{نَزَلَ بِهِ الرُّوحُ الأَمِينُ} جبريل عليه السَّلام',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3127,'{على قلبك} حتى وعيته',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3128,'{بلسان عربي مبين}',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3129,'{وإنه} وإنَّ ذكر محمَّدٍ صلى الله عليه وسلم {لفي زبر الأولين} لفي كتب الأوَّلين',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3130,'{أو لم يكن لهم} للمشركين {آية} دلالةً على صدقه {أن يعلمه علماء بني إسرائيل} يعلمون محمدا صلى الله عليه وسلم بالنُّبوَّة والرِّسالة',NULL,'796',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3131,'{ولو نزلناه} يعني: القرآن {على بعض الأعجمين} جمع الأعجم وهو الذي لا يحسن العربيَّة',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3132,'{فقرأه عليهم ما كانوا به مؤمنين} أنفةً من اتَّباعه',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3133,'{كذلك سلكناه} أدخلنا التَّكذيب {في قلوب المجرمين} فذلك الذي منعهم عن الإِيمان',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3134,'{لا يُؤْمِنُونَ بِهِ حَتَّى يَرَوُا الْعَذَابَ الأَلِيمَ}',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3135,'{فَيَأْتِيَهُمْ بَغْتَةً وَهُمْ لا يَشْعُرُونَ}',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3136,'{فيقولوا هل نحن منظرون} فلمَّا نزلت هذه الآيات قالوا: إلى متى توعدنا بالعذاب؟ فأنزل الله سبحانه',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3137,'{أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ}',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3138,'{أفرأيت إن متعناهم} بالدُّنيا وأبقيناهم فيها {سنين}',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3139,'{ثمَّ جاءهم} العذاب لم ينفعهم إمتاعهم بالدُّنيا فيما قبل',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3140,'{مَا أَغْنَى عَنْهُمْ مَا كَانُوا يُمَتَّعُونَ}',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3141,'{وَمَا أهلكنا من قرية إلاَّ لها منذرون} رسلٌ ينذرهم',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3142,'{ذكرى} إنذاراً للموعظة {وما كنا ظالمين} في إهلاكهم بعد قيام الحُجَّة عليهم',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3143,'{وما تَنَزَّلَتْ به} بالقرآن {الشياطين}',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3144,'{وما ينبغي لهم} ذلك {وما يستطيعون} ذلك',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3145,'{إنهم} عن استراق من السَّماء {لمعزولون} بالشُّهب',NULL,'797',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3146,'{فَلا تَدْعُ مَعَ اللَّهِ إِلَهًا آخَرَ فَتَكُونَ من المعذبين}',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3147,'{وأنذر} خوِّف {عشيرتك الأقربين} أدنى أهلك وأقاربك',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3148,'{واخفض جناحك} ليِّن جانبك وقوله تعالى:',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3149,'{فَإِنْ عَصَوْكَ فَقُلْ إِنِّي بَرِيءٌ مِمَّا تَعْمَلُونَ}',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3150,'{وَتَوَكَّلْ عَلَى الْعَزِيزِ الرَّحِيمِ}',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3151,'{الذي يراك حين تقوم} أَيْ: إلى صلاتك',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3152,'{وتقلبك} تصرُّفك في أركان الصَّلاة قائماً وقاعداً وراكعاَ وساجداً {في الساجدين} في المصلين',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3153,'{إنه هو السميع العليم}',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3154,'{هل أنبئكم} أخبركم {عَلَى مَنْ تَنَزَّلُ الشَّيَاطِينُ}',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3155,'{تنزَّل على كلِّ أفاك} كذَّاب {أثيم} فاجرٍ مثل مسيلمة وغيره من الكهنة',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3156,'{يلقون} إليهم ما سمعوا ويخلطون بذلك كذباً كثيراً وهذا كان قبل أن حجبوا عن السَّماء',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3157,'{والشعراء يتبعهم الغاوون} يعني: شعراء الكفَّار كانوا يهجون رسول الله صلى الله عليه وسلم فيتَّبعهم الكفَّار',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3158,'{أَلَمْ تَرَ أَنَّهُمْ فِي كُلِّ وَادٍ يَهِيمُونَ} في كلِّ لغوٍ يخوضون يمدحون بباطلٍ ويشتمون بباطلٍ ثمَّ استثنى شعراء المؤمنين فقال:',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3159,'{وَأَنَّهُمْ يَقُولُونَ مَا لا يَفْعَلُونَ}',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3160,'{إِلا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللَّهَ كثيراً وانتصروا من بعد ما ظلموا} ردُّوا على مَنْ هجا رسول الله صلى الله عليه وسلم والمسلمين {وسيعلم الذين ظلموا أَيَّ منقلب ينقلبون} أَيَّ مرجعٍ يرجعون إليه بعد مماتهم',NULL,'798',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3161,'{طس تلك آيات القرآن} هذه الآيات التي وُعدتم بها وذلك أنَّهم وُعدوا بالقرآن في كتبهم {وكتاب} أَيْ: وآياتِ كتابٍ {مبين}',NULL,'799',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3162,'{هدىً} أَيْ: هو هدىً {وبشرى للمؤمنين}',NULL,'799',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3163,'{إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ زَيَّنَّا لَهُمْ أعمالهم} جعلنا جزاءهم على كفرهم أن زيَّنا لهم أعمالهم القبيحة حتى رأوها حسنةً {فهم يعمهون} يتحيَّرون',NULL,'799',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3164,'{أولئك الذين لهم سوء العذاب} في الدُّنيا القتل ببدرٍ {وهم في الآخرة هم الأخسرون} بحرمان النَّجاة والمنع من الجنان',NULL,'799',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3165,'{وإنك لتلقى القرآن} الآية أَيْ: يلقى إليك القرآن وحياً من الله سبحانه',NULL,'799',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3166,'{وإنك لتلقى القرآن من لدن حكيم عليم}',NULL,'799',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3167,'{إذ قال موسى} اذكر يا محمَّد قصَّة موسى حين قال {لأهله} في مسيرة من مدين إلى مصر وقد ضلَّ الطَّريق وأصلد زنده: {إني آنست ناراً} أبصرتها من بعيد {سآتيكم منها بخبر} عن الطَّريق أين هو {أو آتيكم بشهاب قبسٍ} شعلة نار أقتبسها لكم {لعلكم تصطلون} تستدفئون من البرد',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3168,'{فَلَمَّا جَاءَهَا نُودِيَ أَنْ بُورِكَ مَنْ فِي النار} أَيْ: مَنْ في طلب النَّار وقصدها والمعنى: بورك فيك يا موسى يقال: بورك فلانٌ وبورك له وبورك فيه {ومَنْ حولها} وفيمن حولها من الملائكة وهذا تحيَّةٌ من الله سبحانه لموسى وتكرمةٌ له {وسبحان الله ربِّ العالمين} تنزيهاً لله من السُّوء وقوله:',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3169,'{يَا مُوسَى إِنَّهُ أَنَا اللَّهُ الْعَزِيزُ الْحَكِيمُ}',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3170,'{تهتزُّ} أَيْ: تتحرَّك {كأنَّها جانّ} حيَّةٌ خفيفةٌ {ولى مدبراً ولم يعقب} ولم يرجع ولم يلتفت قلنا: {يا موسى لا تخف}',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3171,'{إلا من ظلم} لكن مَنْ ظلم نفسه {ثمَّ بدَّل حسناً بعد سوء} أَيْ: تاب {فإني غفورٌ رحيم} وقوله:',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3172,'{في تسع آيات} أَيْ: من تسع آيات أنت مرسلٌ بها {إلى فرعون وقومه} وقوله:',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3173,'{مبصرة} أَيْ: مضيئةً واضحةً',NULL,'800',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3174,'{وجحدوا بها واستيقنتها أنفسهم} الآية معناها: وجحدوا بها ظلماً وترفُّعاً عن أن يؤمنوا بما جاء به موسى وهم يعلمون أنَّها من عند الله عزَّ وجل',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3175,'{وَلَقَدْ آتَيْنَا دَاوُدَ وَسُلَيْمَانَ عِلْمًا وَقَالا الْحَمْدُ لِلَّهِ الَّذِي فَضَّلَنَا عَلَى كَثِيرٍ مِنْ عِبَادِهِ المؤمنين}',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3176,'{وورث سليمان داود} نبوَّته وعلمه دون سائر أولاده {وقال: يا أيُّها الناس علِّمنا منطق الطير} فهمنا ما يقوله الطَّير',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3177,'{وحشر} وجُمع {لسليمان جنوده} في مسيرٍ له {فهم يوزعون} يحبس أولهم على آخرهم حتى يجتمعوا',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3178,'{حتى إذا أتوا على واد النمل} كان هذا الوادي بالشَّام وكانت نملة كأمثال الذُّباب {لا يحطمنَّكم سليمان وجنوده} لا يكسرنَّكم بأن يطؤوكم',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3179,'{فتبسَّم} سليمان عليه السلام لمَّا سمع قولها وتذكَّر ما أنعم الله به عليه فقال: {ربِّ أوزعني} ألهمني {أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَى وَالِدَيَّ وَأَنْ أَعْمَلَ صَالِحًا تَرْضَاهُ وَأَدْخِلْنِي برحمتك في عبادك الصالحين}',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3180,'{وتفقد الطير} طلبها وبحث عنها {فَقَالَ: مَا لِي لا أَرَى الْهُدْهُدَ أَمْ كان} بل أَكان {من الغائبين} لذلك لم يره',NULL,'801',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3181,'{لأعذبنه عذاباً شديداً} لأنتفنَّ ريشه وألقينَّه في الشَّمس {أو ليأتيني بسلطان مبين} حجَّةٍ واضحةٍ في غيبته',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3182,'{فمكث غير بعيدٍ} لم يطل الوقت حتى جاء الهدهد وقال لسليمان: {أحطتُ بما لم تحط به} علمتُ ما لم تعلمه {وجئتك من سبأ} وهي مدينةٌ باليمن {بنبأ يقين} بخبرٍ لا شكَّ فيه وقوله:',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3183,'{وأوتيت من كلِّ شيء} أَيْ: ممَّا يُعطى الملوك {ولها عرش} سريرٌ {عظيم} وقوله:',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3184,'{وَجَدْتُهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِنْ دُونِ اللَّهِ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ فهم لا يهتدون}',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3185,'{أن لا يسجدوا} أَيْ: لأنْ لا يسجدوا لله {الذي يخرج الخبء في السماوات والأرض} القطر من السَّماء والنبات من الأرض وقوله:',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3186,'{اللَّهُ لا إِلَهَ إِلا هُوَ رَبُّ الْعَرْشِ العظيم}',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3187,'{قَالَ سَنَنْظُرُ أَصَدَقْتَ أَمْ كُنْتَ مِنَ الْكَاذِبِينَ}',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3188,'{ثمَّ تولَّ عنهم} أَيْ: استأخر غير بعيدٍ {فانظر ماذا يرجعون} ما يردُّون من الجواب فمضى الهدهد وألقى إليها الكتاب ف',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3189,'{قالت يا أيها الملأ إني ألقي إليَّ كتاب كريم} حسنٌ ما فيه ثمَّ بيَّنت ما فيه فقالت:',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3190,'{إِنَّهُ مِنْ سُلَيْمَانَ وَإِنَّهُ بِسْمِ اللَّهِ الرَّحْمَنِ الرحيم}',NULL,'802',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3191,'{أن لا تعلوا علي} أَيْ: لا تترفَّعوا عليَّ وإن كنتم ملوكاً {وأتوني مسلمين} طائعين مُنقادين',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3192,'{قالت يا أيها الملأ أفتوني في أمري} بيِّنوا لي ما أعمل {ما كنت قاطعة} قاضية وفاضلة {أمرا حتى تشهدون} حتى تحضرون أَيْ: لا أقطع أمراً دونكم',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3193,'{قالوا} مُجيبين لها: {نحن أولو قوَّة} في القتال {وأولو بأس شديد} عند الحرب {والأمر إليك} أيَّتها الملكة {فانظري ماذا تأمرين} نُطِعْك',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3194,'{قالت إنَّ الملوك إذا دخلوا قرية} عنوةً وغلبةً {أفسدوها} خرَّبوها {وجعلوا أعزَّة أهلها أذلة} أهانوا أشرافها بها ليستقيم لهم الأمر أشارت إلى أنَّها لو جاءت سليمان محاربة اجتاحت إلى التَّخريب والإِفساد وصدَّقها الله سبحانه في قولها فقال: {وكذلك يفعلون}',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3195,'{وإني مرسلة إليهم بهدية} أُصانعه بها وأختبره أملكٌ هو أم نبيٌّ؟ فإن كان ملكاً قبلها وإن كان نبيا لهم يقبلها {فناظرة بِمَ} بأيِّ شيءٍ {يرجع المرسلون} من عنده',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3196,'{فلما جاء} البريد أو الرسول {سُلَيْمَانَ قَالَ أَتُمِدُّونَنِ بِمَالٍ فَمَا آتَانِيَ اللَّهُ} من الدِّين والنُّبوَّة والحكمة {خيرٌ مما آتاكم} من الدُّنيا {بل أنتم بهديتكم تفرحون} لأنَّهم أهل مكاثرة بالدُّنيا ثمَّ قال للرَّسول:',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3197,'{ارْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُودٍ لا قِبَلَ لَهُمْ} لا طاقة لهم {بها ولنخرجنَّهم منها} من أرضهم {أذلة} فجاءها الرَّسول وأخبرها بما رأى وشاهد فتجهَّزت للمسير إلى سليمان فلمَّا علم سليمان عليه السَّلام بمسيرها إليه',NULL,'803',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3198,'{قال يا أيها الملأ أيكم يأتيني بعرشها} سريرها {قبل أن يأتوني مسلمين} لأنَّه حينئذٍ لا يحلُّ أخذ ما في أيديهم',NULL,'804',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3199,'{قَالَ عِفْرِيتٌ مِنَ الْجِنِّ} وهو المارد القويُّ: {أَنَا آتِيكَ بِهِ قَبْلَ أَنْ تَقُومَ مِنْ مقامك} من مجلسك الذي جلستَ فيه للحكم {وإني عليه} على حمله {لقويٌّ أمين} على ما فيه من الجواهر فقال سليمان عليه السَّلام: أريد أسرع من هذا ف',NULL,'804',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3200,'{قَالَ الَّذِي عِنْدَهُ عِلْمٌ مِنَ الْكِتَابِ} وهو آصف بن برخيا وكان قد قرأ كتب الله سبحانه {أَنَا آتِيكَ بِهِ قَبْلَ أَنْ يَرْتَدَّ إِلَيْكَ طرفك} قبل أن يرجع إليك الشَّخَص من منتهى طرفك {فلما رآه} رأى سليمان عليه السَّلام العرش {مُسْتَقِرًّا عِنْدَهُ قَالَ هَذَا مِنْ فَضْلِ رَبِّي ليبلوني أأشكر} نعمته {أم أكفر} ها {ومَنْ شكر فإنما يشكر لنفسه} لأَنَّ نفع ذلك يعود إليه حيث يستوجب المزيد {وَمَنْ كَفَرَ فَإِنَّ رَبِّي غَنِيٌّ} عن شكره {كريم} بالإٍفضال على مَنْ يكفر النِّعمة',NULL,'804',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3201,'{قال نكروا} غيِّروا لها {عرشها} بتغيير صورته {ننظر أتهتدي} أتعلم أنَّه عرشها فتعرفه',NULL,'804',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3202,'{فَلَمَّا جَاءَتْ قِيلَ: أَهَكَذَا عَرْشُكِ قَالَتْ كَأَنَّهُ هو} شبَّهته به لأنَّه كان مُغيَّراً وأراد سليمان أن يختبر عقلها لأنَّه قيل له: إنَّ في عقلها شيئاً ثمَّ قالت: {وأوتينا العلم} بصحَّة نبوَّة سليمان {من قبلها} من قبل هذه الآية التي رأيتُها في إحضار العرش {وكنا مسلمين} مناقدين له قبل مجيئنا',NULL,'804',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3203,'{وصدها} ومنعها عن الإيمان {مَا كَانَتْ تَعْبُدُ مِنْ دُونِ اللَّهِ إِنَّهَا كانت من قوم كافرين} فنشأت فيهم ولم تعرف إلاَّ قوماً يعبدون الشَّمس',NULL,'805',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3204,'{قيل لها ادخلي الصرح} وذلك أنَّه قيل لسليمان عليه السَّلام: إنَّ قدميها كحافر الحمار فأراد سليمان أن يرى قدميها فاتَّخذ له ساحةً من زجاجٍ تحته الماء والسَّمك وجلس سليمان في صدر الصَّرح وقيل لَهَا: ادْخُلِي الصَّرْحَ {فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةً} ماءً وهي معظمه {وكشفت عن ساقيها} لدخول الماء فرأى سليمان قدمها وإذا هي أحسن النَّاس ساقاً وقدماً و {قال} لها: {إنَّه صرح ممرَّد} أملس {من قوارير} ثمَّ إنَّ سليمان عليه السَّلام دعاها إلى الإِسلام فأجابت و {قالت: رب إني ظلمت نفسي} بالكفر {وأسلمت مع سليمان لله ربّ العالمين} وقوله:',NULL,'805',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3205,'{فإذا هم فريقان} فإذا قوم صالحٍ فريقان مؤمنٌ وكافرٌ {يختصمون} يقول كلُّ فريقٍ: الحقُّ معي وطلبت الفرقة الكافرة على تصديق صالح عليه السَّلام العذاب فقال:',NULL,'805',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3206,'{يَا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ} أَي: لمَ قلتم: إنْ كان ما أتيت به حقاً فأنتا بالعذاب {لولا} هلاَّ {تستغفرون الله} بالتَّوبة من الكفر {لعلكم ترحمون} لكي ترحموا',NULL,'805',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3207,'{قالوا اطيرنا بك} تشاءمنا بك {وبمن معك} وذلك إنَّهم قُحطوا بتكذيبهم فقالوا: أصابنا القحط بشؤمك وشؤم أصحابك فقال صالح عليه السَّلام: {طائركم عند الله} أَيْ: ما أصابكم من خيرٍ وشرٍّ فمن الله {بَلْ أَنْتُمْ قَوْمٌ تُفْتَنُونَ} تختبرون بالخير والشرِّ',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3208,'{وكان في المدينة} مدينة ثمود {تسعة رهط} كانو عتادة قومِ صالحٍ',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3209,'{قالوا: تقاسموا} احلفوا {بالله لنبيتنَّه وأهله} لنأتينَّ صالحاً ليلا ولنقتلته وأهله {ثم لنقولنَّ} لوليِّ دمه: {ما شهدنا مهلك أهله} ما حضرنا إهلاكهم {وإنا لصادقون} في قولنا',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3210,'{ومكروا مكراً} لتبييت صالحٍ {ومكرنا مكراً} جازيناهم على ذلك وقوله:',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3211,'{إنَّا دمرناهم} وذلك أنَّهم لمَّا خرجوا ليلاً لإِهلاك صالحٍ دَمَغتهم الملائكة بالحجارة من حيث لا يرونهم فقتلوهم وقوله: {وقومهم أجمعين} إهلاك قوم ثمود بالصَّيحة',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3212,'{فتلك بيوتهم} مساكنهم {خاوية} ساقطةً خاليةً {بما ظلموا} بكفرهم بالله سبحانه وقوله:',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3213,'{وأنجينا الذين آمنوا وكانوا يتقون}',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3214,'{أتأتون الفاحشة وأنتم تبصرون} تعلمون أنَّها فاحشة فهو أعظم لذنوبكم وقوله:',NULL,'806',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3215,'{أإنكم لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِنْ دُونِ النِّسَاءِ بَلْ أنتم قوم تجهلون}',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3216,'{إنهم أناس يتطهرون} يتنزهن عن أدبار الرِّجال يقولونه استهزاءً وقوله:',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3217,'{قدرناها من الغابرين} أَيْ: قضينا عليها أنَّها من الباقين في العذاب',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3218,'{وأمطرنا عليهم} على شُذَّاذهم ومَنْ كان منهم في الأسفار {مطراً} وهو الحجارة',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3219,'{قل} لهم يا محمد: {الحمد لله} أَيْ: على إهلاك الكفَّار من الأمم الخالية {وَسَلامٌ عَلَى عِبَادِهِ الَّذِينَ اصْطَفَى} اصطفاهم لرسالته {آلله خير أما يشركون} به من الأصنام وقوله:',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3220,'{حدائق ذات بهجة} أَيْ: بساتين ذات حسنٍ {ما كان لكم أن تنبتوا شجرها} أَيْ: ما قدرتم عليه {بل هم قومٌ يعدلون} يشركون',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3221,'{أمن جعل الأرض قراراً} لا تتحرَّك {وجعل خلالها أنهاراً} وسطها أنهاراً جاريةً {وجعل لها رواسي} جبالاً ثوابت {وجعل بين البحرين} العذب والمالح {حاجزاً} مانعاً من قدرته حتى لا يختلطا',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3222,'{أمن يجيب المضطر} المجهود ذا الضرورة {ويكشف السوء} الضُّرَّ {ويجعلكم خلفاء الأرض} سكَّانها بإهلاك من قلبكم',NULL,'807',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3223,'{أَمَّنْ يَهْدِيكُمْ فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ أَإِلَهٌ مع الله تعالى الله عما يشركون}',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3224,'{ومن يرزقكم من السماء} المطر {و} من {الأرض} النبات وقوله:',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3225,'{قل لا يعلم من في السماوات وَالأَرْضِ الْغَيْبَ إِلا اللَّهُ وَمَا يَشْعُرُونَ أَيَّانَ يبعثون}',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3226,'{بل ادارك علمهم في الآخرة} أَيْ: لحقهم علمهم بأنَّ السَّاعة والبعث حقٌّ في الآخرة حين لا ينفعهم ذلك ومن قرأ: (إدراك) فمعناه: تدارك أَيْ: تكامل عملهم يوم القيامة لأنَّهم يبعثون ويشاهدون ما وعدوا {بل هم في شك منها} في الدُّنيا {بل هم منها} من علمها {عمون} جاهلون وقوله:',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3227,'{وقال الذين كفروا أإذا كنا ترابا وآباؤنا أإنا لمخرجون}',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3228,'{لَقَدْ وُعِدْنَا هَذَا نَحْنُ وَآبَاؤُنَا مِنْ قَبْلُ إِنْ هَذَا إِلا أَسَاطِيرُ الأَوَّلِينَ}',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3229,'{قُلْ سِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عاقبة المجرمين}',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3230,'{ولا تحزن عليهم} أَيْ: على تكذيبهم وإعراضهم عنك {ولا تكن في ضيق مما يمكرون} ولا تضيّق قلبك بمكرهم',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3231,'{ويقولون متى هذا الوعد} أَيْ: وعد العذاب {إن كنتم صادقين} أنَّ العذاب نازلٌ بالمكذِّب',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3232,'{قل عسى أن يكون ردف لكم} أَي: ردفكم والمعنى: تبعكم ودنا منكم {بعض الذي تستعجلون} من العذاب وكان ذلك يوم بدر',NULL,'808',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3233,'{وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَكِنَّ أَكْثَرَهُمْ لا يَشْكُرُونَ}',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3234,'{وَإِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يعلنون}',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3235,'{وما من غائبة} أَيْ: جملةٍ غائبةٍ عن الخلق {إلاَّ في كتاب مبين} وهو اللَّوح المحفوظ',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3236,'{إنَّ هذا القرآن يَقُصُّ عَلَى بَنِي إِسْرَائِيلَ أَكْثَرَ الَّذِي هُمْ فيه يختلفون} وذلك أنَّ بني إسرائيل اختلفوا حتى لعن بعضهم بعضاً فقال الله سبحانه: إنَّ هذا القرآن ليقصُّ عليهم الهدى ممَّا اختلفوا فيه لو أخذوا به',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3237,'{وَإِنَّهُ لَهُدًى وَرَحْمَةٌ لِلْمُؤْمِنِينَ}',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3238,'{إنَّ ربك يقضي بينهم} بين المختلفين في الدِّين {بحكمه} يوم القيامة {وهو العزيز} القويُّ فلا يردُّ له أمرٌ {العليم} بأحوالهم',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3239,'{فَتَوَكَّلْ عَلَى اللَّهِ إِنَّكَ عَلَى الْحَقِّ الْمُبِينِ}',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3240,'{إنك لا تسمع الموتى} الكفَّار {وَلا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ} يعني: الكفَّار الذين هم بمنزلة الصم ولا يسمعون النِّداء إذا أعرضوا',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3241,'{وما أنت بهادي العمي عن ضلالتهم} يريد: إنَّه أعمالهم حتى لا يهتدوا فكيف يهدي النبي صلى الله عليه وسلم عن ضلالتهم قوماً عمياً {إن تُسمع} ما تُسمع سماع إفهام {إِلا مَنْ يؤمن بآياتنا} بأدلَّتنا {فهم مسلمون} في علم الله سبحانه',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3242,'{وإذا وقع القول عليهم} وجب العذاب والسُّخط عليهم وذلك حين لا يقبل الله سبحانه من كافرٍ إيمانه ولم يبق إلاَّ مَنْ يموت كافراً في علم الله سبحانه {أخرجنا لهم دابة من الأرض} وخروجها من أوَّل أشراط القيامة {تكلمهم} تُحدِّثهم بما يسوءهم {أَنَّ النَّاسَ كَانُوا بِآيَاتِنَا لا يُوقِنُونَ} تخبر الدَّابَّة مَنْ رآها أنَّ أهل مكة كانوا بمحمد صلى الله عليه وسلم وبالقرآن لا يوقنون ومَنْ كسر: {إنَّ النَّاس} كان المعنى: تقول لهم: إنَّ الناس',NULL,'809',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3243,'{ويوم نحشر} نجمع {من كلِّ أمة فوجاً} جماعةً {ممن يكذب بآياتنا فهم يوزعون} يحبس أولهم على آخرهم ليجتمعوا',NULL,'810',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3244,'{حتى إذا جاؤوا قال} الله تعالى لهم: {أكذَّبتم بآياتي ولم تحيطوا بها علماً} ولم تعرفوها حقَّ معرفتها وهذا توبيخٌ لهم {أماذا كنتم تعملون} حين لم تتفكَّروا فيها',NULL,'810',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3245,'{ووقع القول} وجبت الحُجَّة {عليهم بما ظلموا} بإشراكهم {فهم لا ينطقون} بحجَّةٍ وعذرٍ ثمَّ ذكر الدَّليل على قدرته وإلهيتَّه سبحانه وتعالى فقال:',NULL,'810',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3246,'{أَلَمْ يَرَوْا أَنَّا جَعَلْنَا اللَّيْلَ لِيَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يؤمنون} وقوله:',NULL,'810',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3247,'{إلا من شاء الله} يعني: الشّهداء {وكلٌّ أتوه} يأتون الله سبحانه {داخرين} صاغرين',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3248,'{وترى الجبال تحسبها جامدة} واقفةً مُستقرَّةً {وهي تمرُّ مرَّ السحاب} وذلك أنَّ كلَّ شيءٍ عظيمٌ وكلَّ جمع كثيرٌ يقصر عنه الطَّرف لكثرته فهو في حسبان النَّاظر واقفٌ وهو يسير {صنع الله} أَيْ: صنع الله ذلك صنعه {الذي أتقن} أحكم {كلَّ شيء}',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3249,'{من جاء بالحسنة} وهي كلمة لا إله إلاَّ الله {فله خيرٌ منها} فمنها يصل إليه الخير {ومَنْ جاء بالسيئة} الشِّرك {فَكُبَّت} أُلقيت وطُرحت {وجوههم في النار} وقيل لهم: {هل تجزون إلاَّ ما كنتم} بما كنتم {تعملون}',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3250,'قل يا محمَّد: {إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَذِهِ الْبَلْدَةِ} يعني: مكَّة {الذي حرَّمها} جعلها حرماً آمناً {وله كلُّ شيء} ملكا وخلقا وقوله:',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3251,'{إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَذِهِ الْبَلْدَةِ الَّذِي حَرَّمَهَا وَلَهُ كُلُّ شَيْءٍ وَأُمِرْتُ أَنْ أكون من المسلمين}',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3252,'{وَمَنْ ضَلَّ فَقُلْ إِنَّمَا أَنَا مِنَ الْمُنْذِرِينَ} أَيْ: ليس عليَّ إلاَّ البلاغ',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3253,'{وقل الحمد لله سيريكم آياته} أيُّها المشركون يعني: يوم بدر {فتعرفونها وما ربك بغافل عما تعملون}',NULL,'811',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3254,'{طسم}',NULL,'812',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3255,'{تلك آيات الكتاب المبين} يعني: القرآن وهو مبينٌ للأحكام',NULL,'812',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3256,'{نتلو} نقصُّ {عليك من نبأ موسى} خبر موسى {وفرعون بالحق} بالصِّدق الذي لا شكَّ فيه {لقوم يؤمنون} يُصدِّقون أنَّ ما يأتيهم به صدقٌ',NULL,'812',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3257,'{إنَّ فرعون علا} استكبر وتعظَّم {في الأرض} أرض مصر {وجعل أهلها شيعاً} فرقاً تتبع بعض تلك الفرق بعضاً في خدمته {يستضعف طائفة منهم} وهم بنو إسرائيل',NULL,'812',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3258,'{وَنُرِيدُ أَنْ نَمُنَّ عَلَى الَّذِينَ اسْتُضْعِفُوا فِي الأرض} ننعم على بني إسرائيل {ونجعلهم أئمّة} قادةً في الخير {ونجعلهم الوارثين} يرثون ملك فرعون وقومه وقوله:',NULL,'812',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3259,'{ونمكن لهم في الأرض} أرض مصر والشَّام حتى يغلبوا عليها من غير مُنازعٍ {وَنُرِيَ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا مِنْهُمْ مَا كَانُوا يحذرون} وذلك أنَّهم كانوا قد أُخبروا أنَّ هلاكهم على يدي رجل من بني إسرائيل فكانوا على وجلٍ منهم',NULL,'813',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3260,'{وأوحينا إلى أم موسى} قيل: إنَّه وحي إلهام وقيل: وحي إعلام',NULL,'813',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3261,'{فالتقطه} أخذه {آل فرعون} عن الماء {ليكون لهم عدوا وحزنا} أي: ليصبر الأمر إلى ذلك {إنَّ فرعون وهامان وجنودهما كانوا خاطئين} أَيْ: عاصين آثمين',NULL,'813',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3262,'{وقالت امرأة فرعون قرة عين} أَيْ: هو قرَّة عين لي {ولك لا تقتلوه} فإنَّه أتانا به الماء من أرضٍ أخرى وليس هو من بني إسرائيل {وهم لا يشعرون} بما هو كائنٌ من أمرهم وأمره',NULL,'813',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3263,'{وأصبح فؤاد أم موسى فارغاً} خالياً عن كلِّ شيء إلاَّ عن ذكر موسى وهمِّه {إن كادت لتبدي به} بأنَّه ابنها {لولا أن ربطنا على قلبها} قوينا وألهمناها الصَّبر {لتكون من المؤمنين} المُصدِّقين بوعد الله سبحانه',NULL,'813',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3264,'{وقالت لأخته} لأخت موسى {قصيه} اتَّبعي أثره فاتَّبعته {فبصرت به عن جنب} أبصرته من بعيدٍ {وهم لا يشعرون} أنَّها أخته',NULL,'813',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3265,'{وحرمنا عليه المراضع} منعتا موسى أن يقبل ثدي مرضعةٍ {من قبل} أن نردَّه على أُمَّه {فقالت} أخته حين تعذَّر عليهم رضاعة: {هَلْ أَدُلُّكُمْ عَلَى أَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ} يضمُّونه إليهم {وهم له ناصحون} مخلصون شفقته',NULL,'814',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3266,'{فرددناه إلى أمه} وذلك أنَّها دلَّتهم على أمِّ موسى فَدُفِعَ إليها تُربِّيه لهم وقوله: {ولكن أكثرهم لا يعلمون} آل فرعون كانوا لا يعملون أنَّ الله وعدها ردَّه عليها',NULL,'814',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3267,'{ولما بلغ أشدَّه} منتهى قوَّته وهو ما فوق الثَّلاثين {واستوى} وبلغ أربعين سنةً {آتيناه حكماً} عقلاً وفهماً {وعلماً} قبل النُّبوَّة',NULL,'814',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3268,'{ودخل المدينة} يعني: مدينةً بأرض مصر {على حين غفلة من أهلها} فيما بين المغرب والعشاء {فوجد فيها رجلين يقتتلان} أحدهما إسرائيليٌّ وهو الذي من شيعته والآخر قطبي وهو الذي من عدوه {فاستغاثه} الإِسرائيلي على الفرعونيِّ {فوكزه موسى} ضربه بجميع كفِّه {فقضى عليه} فقتله ولم يتعمَّد قتله فندم على ذلك لأنَّه لم يُؤمر بقتله فـ {قَالَ هَذَا مِنْ عَمَلِ الشَّيْطَانِ إِنَّهُ عَدُوٌّ مضلٌّ مبين} ثمَّ استغفر فقال:',NULL,'814',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3269,'{رَبِّ إِنِّي ظَلَمْتُ نَفْسِي فَاغْفِرْ لِي فَغَفَرَ لَهُ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ}',NULL,'814',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3270,'{قال رب بما أنعمت عليَّ} بالمغفرة {فلن أكون ظهيراً للمجرمين} لن أُعين بعدها على خطيئة',NULL,'815',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3271,'{فأصبح في} تلك {المدينة خائفا} من قتله القطبي {يترقب} ينتظر الأخبار {فإذا} الإسرائيليُّ {الذي استنصره بالأمس يستصرخه} يستغيثه {قال له موسى: إنك لغويٌّ مبين} ظاهر الغواية قد قلت بك بالأمس رجلاً وتدعوني إلى آخر وأقبل إليهما {فَلَمَّا أَنْ أَرَادَ أَنْ يَبْطِشَ بِالَّذِي هُوَ عدوٌّ لهما} أَيْ: بالقبطِيِّ فظنَّ الذي من شيعته أنَّه يريده فقال:',NULL,'815',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3272,'{أَتُرِيدُ أَنْ تَقْتُلَنِي كَمَا قَتَلْتَ نَفْسًا بِالأَمْسِ إِنْ تُرِيدُ إِلا أَنْ تَكُونَ جَبَّارًا فِي الأرض} تقتل ظلماً فلمَّا قال الإِسرائيلي هذا علم القبطيِّ بالأمس فأتى فرعون فأخبره بذلك فأمر فرعون بقتل موسى فأتاه رجلٌ فأخبره بذلك وهو قوله:',NULL,'815',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3273,'{وجاء رجل من أقصى المدينة يسعى} وهو مؤمن آل فرعون {قَالَ يَا مُوسَى إِنَّ الملأ يأتمرون بك} يأمر بعضهم بعضاً ويتشاورون {ليقتلوك فاخرج} من هذه المدينة {إِنِّي لَكَ مِنَ النَّاصِحِينَ}',NULL,'815',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3274,'{فخرج منها خائفاً يترقب} ينتظر الطَّلب {قال: ربّ نجني من القوم الظالمين} قوم فرعون',NULL,'815',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3275,'{ولما توجَّه} قصد بوجهه {تلقاء مدين} نحوها {قَالَ عَسَى رَبِّي أَنْ يَهْدِيَنِي سَوَاءَ السَّبِيلِ} قصد الطَّريق وذلك أنَّه لم يكن يعرف الطَّريق',NULL,'816',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3276,'{ولما ورد ماء مدين} وهو بئر كانت لهم {وجد عليه أمة} جماعةً {من الناس يسقون} مواشيهم {ووجد من دُونِهمُ امرأتين تذودان} تحبسان غنمهما عن الماء حتى يصدر مواشي النَّاس {قال} موسى لهما: {ما خطبكما} ؟ ما شأنكما لا تسقيان مع النَّاس؟ {قالتا لا نسقي} مواشينا {حتى يصدر الرعاء} عن الماء لأنا لا نطيق أن نستقي وأن نُزاحم الرِّجال فإذا صدروا سقينا من فضل مواشيهم {وأبونا شيخ كبير} لا يمكنه أن يرد وأن يستقي',NULL,'816',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3277,'{فسقى لهما} أغنامهما من بئرٍ أخرى رفع عنها حجراً كان لا يرفعه إلاَّ عشرة أنفس {ثمَّ تولى إلى الظلّ} أَيْ: إلى ظلِّ شجرةٍ {فَقَالَ رَبِّ إِنِّي لِمَا أَنْزَلْتَ إِلَيَّ مِنْ خير} طعامٍ {فقير} محتاجٌ وكان قد جاع فسأل الله تعالى ما يأكل فلما رجعتا إلى أبيهما أخبرتاه بما فعل موسى فقال لإحداهما: اذهبي فادعيه فذلك قوله:',NULL,'816',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3278,'{فجاءته إحداهما} أخذت {تمشي على استحياء} مُستترةً بكُمِّ درعها {قَالَتْ: إِنَّ أَبِي يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ مَا سَقَيْتَ لَنَا فَلَمَّا جَاءَهُ وَقَصَّ عَلَيْهِ الْقَصَصَ} أخبره بأمره والسَّبب الذي أخرجه من أرضه {قَالَ لا تَخَفْ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِمِينَ} يعني: من فرعون وقومه فإنَّه لا سلطان له بأرضنا',NULL,'816',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3279,'{قالت إحداهما يا أبت استأجره} ليرعى أغنامنا {إنَّ خير من استأجرت القويُّ الأمين} وإنَّما قالت ذلك لأنَّها عرفت قوَّته برفع الحجر من رأس البئر وأمانته بأنَّ موسى قال لها لمَّا دعته إلى أبيها: امشي خلفي فإنَّا بني يعقوب لا ننظر إلى أعجاز النِّساء',NULL,'817',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3280,'{قال} عند ذلك الشِّيخ لموسى: {إني أريد أن أنكحك} أزوِّجك {إحدى ابنتيَّ هاتين على أن تأجرني} تكون أجيراً لي {ثماني حجج} سنين {فإن أتممت عشراً فمن عندك} وليس بواجبٍ عليك {وَمَا أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ} بأن اشترط العشر {سَتَجِدُنِي إِنْ شَاءَ اللَّهُ مِنَ الصَّالِحِينَ} الوافين بالعهد',NULL,'817',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3281,'{قال} موسى: {ذلك} الذي وصفت {بيني وبينك} أَيْ: لك ما شرطتَ عليَّ ولي ما شرطتُ من تزويج إحداهما {أيما الأجلين قضيت فلا عدوان عليَّ} لا علم عليَّ بأن أُطالب بأكثر منه {والله على ما نقول وكيل} والله شاهدنا على ما عقدنا',NULL,'817',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3282,'{فلما قضى موسى الأجل} مفسَّر فيما مضى إلى قوله: {أو جذوة من النار} قطعةٍ وشعلةٍ من النَّار',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3283,'{فلما أتاها نودي من شاطئ} جانب {الواد الأيمن} من يمين موسى {في البقعة} في القطعة من الأرض {المباركة} بتكليم الله سبحانه فيها موسى عليه السَّلام وإتيانه النُّبوَّة {من الشجرة} من جانب الشَّجَرَةِ {أَنْ يَا مُوسَى إِنِّي أَنَا اللَّهُ ربُّ العالمين} والباقي مفسَّرٌ فيما سبق إلى قوله:',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3284,'{وَأَنْ أَلْقِ عَصَاكَ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّى مُدْبِرًا وَلَمْ يُعَقِّبْ يَا مُوسَى أقبل ولا تخف إنك من الآمنين}',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3285,'{واضمم إليك جناحك} أَيْ: يدك {من الرهب} من الخوف والمعنى: سكِّن روعك واخفض عليك جنبيك وذلك أنه كان يرتعد خوفاً {فذانك} اليد والعصا {برهانان من ربك} الآية وقوله:',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3286,'{قَالَ رَبِّ إِنِّي قَتَلْتُ مِنْهُمْ نَفْسًا فَأَخَافُ أن يقتلون}',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3287,'{ردءاً} أَيْ: مُعيناً',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3288,'{قال: سنشدُّ عضدك} أَيْ: نُقوِّيك {بأخيك ونجعل لكما سلطاناً} حُجَّةً بيِّنة {فلا يصلون إليكما} بسوءٍ {بآياتنا} العصا واليد وسائر ما أُعطيا',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3289,'{فَلَمَّا جَاءَهُمْ مُوسَى بِآيَاتِنَا بَيِّنَاتٍ قَالُوا مَا هَذَا إِلا سِحْرٌ مُفْتَرًى وَمَا سَمِعْنَا بِهَذَا في آبائنا الأولين}',NULL,'818',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3290,'{وقال موسى} لمَّا كُذِّب ونُسب إلى السِّحر: {رَبِّي أَعْلَمُ بِمَنْ جَاءَ بِالْهُدَى مِنْ عِنْدِهِ} يعني: نفسه أَيْ: ربِّي أعلم بي أنَّ الذي جئتُ به مِنْ عِنْدِهِ {وَمَنْ تَكُونُ لَهُ عَاقِبَةُ الدَّارِ} أَيْ: العقبى المحمودة في الدَّار الآخرة وقوله:',NULL,'819',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3291,'{فأوقد لي يا هامان على الطين} أَيْ: اطبخ لي الآجر {فاجعل لي صرحاً} بناء طويلاً مشرفاً {لعلي أطلع إلى إله موسى} أنظر إليه وأقف عليه وقوله:',NULL,'819',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3292,'{وَاسْتَكْبَرَ هُوَ وَجُنُودُهُ فِي الأَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوا أَنَّهُمْ إِلَيْنَا لا يُرْجَعُونَ}',NULL,'819',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3293,'{فَأَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ فَانْظُرْ كَيْفَ كان عاقبة الظالمين}',NULL,'819',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3294,'{وجعلناهم أئمة} قادةً ورؤساء {يدعون إلى النار} أي: إلى الضَّلالة التي عاقبتها النَّار',NULL,'819',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3295,'{وأتبعناهم في هذه الدنيا لعنةً} وذلك أنَّهم لمَّا هلكوا لُعنوا فهم يُعرضون على النار غدوةً وعشيةً إلى يوم القيامة {ويوم القيامة هم من المقبوحين} الممقوتين المهلكين',NULL,'819',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3296,'{وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ مِنْ بَعْدِ مَا أَهْلَكْنَا الْقُرُونَ الأُولَى بَصَائِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لعلهم يتذكرون} أَيْ: مبيِّناً لهم',NULL,'820',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3297,'{وما كنت بجانب الغربيّ} أَيْ: الجبل الغربيّ الذي هو في جانب الغرب {إذ قضينا إلى موسى الأمر} أحكمناه معه وعهدنا إليه بأمرنا ونهينا {وما كنت من الشاهدين} الحاضرين هناك',NULL,'820',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3298,'{ولكنا أنشأنا} أحدثنا وخلقنا {قروناً} أمماً {فتطاول عليهم العمر} فنسوا عهد الله وتركوا أمره {وما كنت ثاوياً} مُقيماً {فِي أَهْلِ مَدْيَنَ تَتْلُو عَلَيْهِمْ آيَاتِنَا وَلَكِنَّا كنا مرسلين} أرسلناك رسولاً وأنزلنا عليك هذه الأخبار ولولا ذلك ما عملتها',NULL,'820',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3299,'{وما كنت بجانب الطور إذ نادينا} موسى {ولكن} أوحينا إليك هذه القصص {رحمة من ربك}',NULL,'820',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3300,'{ولولا أن تصيبهم مصيبة} عقوبةٌ ونقمةٌ {بما قدَّمت أيديهم} وجواب لولا محذوف وتقديره: لعاجلناهم بالعقوبة',NULL,'820',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3301,'{فلما جاءهم الحق} محمد صلى الله عليه وسلم {من عندنا قالوا: لولا أوتي} محمد {مثل ما أوتي موسى} كتاباً جملةً واحدةً {أَوَلَمْ يَكْفُرُوا بِمَا أُوتِيَ مُوسَى مِنْ قَبْلُ} أَيْ: فقد كفروا بآيات موسى كما كفروا بآيات محمد صلى الله عليه وسلم و {قالوا سحران تظاهرا} وذلك حين سألوا اليهود عنه فأخبروهم أنَّهم يجدونه في كتابهم بنعمته وصفته وقالوا: ساحران تظاهرا يعنون: موسى ومحمداً عليهما السَّلام تعاونا على السِّحر {وقالوا إنَّا بكلٍّ} من موسى ومحمدٍ عليهما السَّلام {كافرون}',NULL,'820',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3302,'{قل} لهم: {فَأْتُوا بِكِتَابٍ مِنْ عِنْدِ اللَّهِ هُوَ أَهْدَى منهما} من كتابيهما {أتبعه أن كنتم صادقين} أَنَّهما كانا ساحرين',NULL,'821',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3303,'{فإن لم يستجيبوا لك} أَيْ: لم يجيبوك إلى الإيتان بالكتاب {فاعلم أنَّما يتبعون أهواءهم} أَيْ: يُؤثرون هواهم على الدِّين',NULL,'821',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3304,'{ولقد وصلنا لهم القول} أنزلنا القرآن يتبع بعضه بعضاً {لعلهم يتذكرون} يتَّعظون ويعتبرون',NULL,'821',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3305,'{الذين آتيناهم الكتاب من قبله} من قبل محمد صلى الله عليه وسلم {هم به يؤمنون} يعني: مؤمني أهل الكتاب',NULL,'821',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3306,'{وإذا يُتلى عليهم} القرآن {قالوا آمنا به} صدَّقنا به {إنَّه الحقُّ من ربنا} ذلك أنَّهم عرفوا بما ذُكر في كتبهم من نعت النبي صلى الله عليه وسلم وكتابه {إنَّا كنا من قبله} من قبل القرآن أو من قبل محمد صلى الله عليه وسلم {مسلمين} لأنَّا كنَّا نؤمن به وبكتابه',NULL,'821',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3307,'{أولئك يؤتون أجرهم مرتين} مرَّةً بإيمانهم بكتابهم ومرَّةً بإيمانهم بالقرآن {بما صبروا} بصبرهم على ما أُوذوا {ويدرؤون بالحسنة السيئةَ} ويدفعون بما يعملون من الحسنات ما تقدَّم لهم من السَّيئات {ومما رزقناهم ينفقون} يتصدَّقون',NULL,'822',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3308,'{وإذا سمعوا اللغو} القبيح من القول {أعرضوا عنه} لم يلتفتوا إليه يعني: إذا شتمهم الكفَّار لم يشتغلوا بمعارضتهم بالشَّتم {وَقَالُوا: لَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ سَلامٌ عَلَيْكُمْ} ليس هذا تسليم التحيَّة وإنَّما هو تسليم المتاركة أي: بينا وبينكم المتاركة والتَّسليم وهذا قبل أن يُؤمر المسلمون بالقتال {لا نبتغي الجاهلين} لا نصحبهم',NULL,'822',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3309,'{إنك لا تهدي مَنْ أحببت} نزلت حين حرص النبي صلى الله عليه وسلم على إيمان عمِّه عند موته فلم يؤمن فأنزل الله تعالى هذه الآية والمعنى: لا تهدي مَنْ أحببت هدايته {ولكنَّ الله يهدي من يشاء} هدايته {وهو أعلم بالمهتدين} بمن يهتدي في معلومه',NULL,'822',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3310,'{وقالوا} يعني: مشركي مكَّة: {إن نتبع الهدى معك} بالإِيمان بك {نُتخطف} نُسلب ونوخذ {من أرضنا} لإِجماع العرب على خلافنا فقال الله تعالى: {أَوَلَمْ نُمَكِّنْ لَهُمْ حَرَمًا آمِنًا} أخبر سبحانه أنَّه آمنهم بحرمة البيت ومنع منهم العدوَّ فكيف يخافون أن تستحل العرب قتالهم فيه؟ {يجبى} يُجمع {ولكن أكثرهم لا يعلمون} أنَّ ذلك ممَّا تفضَّل الله به سبحانه عليهم',NULL,'822',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3311,'{وكم أهلكنا من قرية بطرت معيشتها} عاشوا في البطر وكفران النِّعمة {فتلك مساكنهم} خاويةً {لم تسكن من بعدهم إلاَّ قليلاً} لا يسكنها إلاَّ المسافر والمارُّ يوماً أو ساعةً',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3312,'{وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرَى حَتَّى يَبْعَثَ في أمها} أعظمها والآية',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3313,'{وَمَا أُوتِيتُمْ مِنْ شَيْءٍ فَمَتَاعُ الْحَيَاةِ الدُّنْيَا وَزِينَتُهَا وَمَا عِنْدَ اللَّهِ خَيْرٌ وَأَبْقَى أَفَلا تعقلون}',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3314,'{أفمن وعدناه وعداً حسناً} يعني: الجنَّة {فهو لاقيه} مُدركه ومُصيبه {كَمَنْ مَتَّعْنَاهُ مَتَاعَ الْحَيَاةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيَامَةِ مِنَ المحضرين} في النَّار نزلت في رسول الله صلى الله عليه وسلم وأبي جهل',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3315,'{ويوم يناديهم} أَيْ: المشركين {فَيَقُولُ: أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ} في الدُّنيا أنَّهم شركائي',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3316,'{قال الذين حقَّ عليهم القول} وجب عليهم العذاب يعني: الشَّياطين {رَبَّنَا هَؤُلاءِ الَّذِينَ أَغْوَيْنَا أَغْوَيْنَاهُمْ كَمَا غَوَيْنَا تبرأنا إليك ما كانوا إيانا يعبدون} كعادة الشَّيطان في التَّبَرُّؤِ ممَّن يطيعه إذا أورده الهلكة',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3317,'{وقيل} للكفَّار: {ادعوا شركاءَكم} مَنْ كنتم تعبدون من دون الله {فدعوهم فلم يستجيبوا لهم} لم يجيبوهم بشيءٍ ينفعهم {ورأوا العذاب لو أنهم كانوا يهتدون} لما اتَّبعوهم ولما رأوا العذاب',NULL,'823',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3318,'{وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ: مَاذَا أَجَبْتُمُ الْمُرْسَلِينَ}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3319,'{فَعَمِيَتْ عليهم الأنباء} عميت عليهم الحجج لأنَّ الله تعالى قد أعذر إليهم في الدُّنيا فلا تكون لهم حجة يؤمئذ فسكتوا فذلك قوله: {فهم لا يتساءلون} أَيْ: لا يسأل بعضهم بعضا عم يحتجُّون به',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3320,'{وربك يخلق ما يشاء} كما يشاء {ويختار} ممَّا يشاء فاختار من كلِّ ما خلق شيئاً {ما كان لهم الخيرة} ليس لهم أن يختاروا على الله تعالى وليس لهم الاختيار والمعنى: لا يرسل الرُّسل إليهم على اختيارهم والباقي ظاهرٌ إلى قوله:',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3321,'{وَرَبُّكَ يَخْلُقُ مَا يَشَاءُ وَيَخْتَارُ مَا كَانَ لَهُمُ الْخِيَرَةُ سُبْحَانَ اللَّهِ وَتَعَالَى عَمَّا يُشْرِكُونَ}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3322,'{وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3323,'{وَهُوَ اللَّهُ لا إِلَهَ إِلا هُوَ لَهُ الْحَمْدُ فِي الأُولَى وَالآخِرَةِ وَلَهُ الْحُكْمُ وَإِلَيْهِ ترجعون}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3324,'{قُلْ أَرَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ اللَّيْلَ سَرْمَدًا إِلَى يَوْمِ الْقِيَامَةِ مَنْ إِلَهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِضِيَاءٍ أَفَلا تَسْمَعُونَ}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3325,'{قُلْ أَرَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا إِلَى يَوْمِ الْقِيَامَةِ مَنْ إِلَهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِلَيْلٍ تَسْكُنُونَ فِيهِ أَفَلا تُبْصِرُونَ}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3326,'{وَمِنْ رَحْمَتِهِ جَعَلَ لَكُمُ اللَّيْلَ وَالنَّهَارَ لِتَسْكُنُوا فيه ولتبتغوا من فضله ولعلكم تشكرون}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3327,'{وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنْتُمْ تزعمون}',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3328,'{ونزعنا من كلِّ أمة} أَيْ: أخرجنا {شهيداً} يعني: رسولهم الذي أُرسل إليهم {فقلنا هاتوا برهانكم} أَيْ: اعتقدتم به أنّه برهانٌ لم في أنَّكم كنتم على الحقِّ {فعلموا أنَّ الحق لله} أنَّ الحقَّ ما دعا إليه الله سبحانه وأتاهم به الرسول صلى الله عليه وسلم {وضلَّ عنهم ما كانوا يفترون} لم ينتفعوا بما عبدوه من دون الله سبحانه',NULL,'824',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3329,'{إنَّ قارون كان من قوم موسى} كان ابن عمِّه {فبغى عليهم} بالكبر والتجبُّر والبذخ وكثرة المال {وآتيناه من الكنوز ما إنَّ مفاتحه} جمع المفتح وهو ما يُفتح به {لتنوء بالعصبة} تُثقل الجماعة {أُولِي الْقُوَّةِ} {إِذْ قَالَ لَهُ قَوْمُهُ: لا تفرح} بكثرة الماء ولا تأشر {إنَّ الله لا يحبُّ الفرحين} الأشرين البطرين',NULL,'825',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3330,'{وابتغ فيما آتاك الله الدار الآخرة} أَيْ: اطلبها بإنفاق مالك في رضا الله تعالى {ولا تنس نصيبك من الدنيا} لا تترك أن تعمل في دنياك لآخرتك {وأحسن} إلى الناس {كَمَا أَحْسَنَ اللَّهُ إِلَيْكَ وَلا تَبْغِ الْفَسَادَ في الأرض} العمل بالمعاصي',NULL,'825',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3331,'{قال إنما أوتيته على علم عندي} عل فضل علمٍ عندي وكنت بذلك العلم مستحقا لفضل المال وكان أقرأ بني إسرائيل للتَّوراة قال الله تعالى: {أَوَلَمْ يَعْلَمْ أَنَّ اللَّهَ قَدْ أَهْلَكَ مِنْ قَبْلِهِ مِنَ الْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قوة وأكثر جمعاً} للمال منه {ولا يسأل عن ذنوبهم المجرمون} لأنَّهم يدخلون النَّار بغير حسابٍ',NULL,'825',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3332,'{فخرج على قومه في زينته} في ثيابٍ حمرٍ عليه وعلى دوابِّه والرُّكبان الذين معه {قال الذين يريدون الحياة الدنيا} ظاهرٌ إلى قوله:',NULL,'825',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3333,'{ولا يلقاها} أَيْ: ولا يُلقَّن ولا يُوفَّق لهذه الكلمة {إلاَّ الصابرون} عن زينة الدنيا',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3334,'{فَخَسَفْنَا بِهِ وَبِدَارِهِ الأَرْضَ فَمَا كَانَ لَهُ مِنْ فِئَةٍ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَمَا كَانَ مِنَ الْمُنْتَصِرِينَ}',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3335,'{وأصبح الذين تمنوا مكانه بالأمس} صار الذين كانوا يقولون: يَا لَيْتَ لَنَا مِثْلَ مَا أُوتِيَ قَارُونُ {يقولون ويكأنَّ الله} ألم تر ألم تعلم أن {الله يبسط الرزق لمن يشاء ويقدر} يوسع لمن يشاء ويُضيِّق {لولا أنْ منَّ الله علينا} عصمنا عن مثل ما كان عليه قارون من البطر والبغي {لخسف بنا} كما خُسف به',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3336,'{تلك الدار الآخرة} يعني: الجنَّة {نَجْعَلُهَا لِلَّذِينَ لا يُرِيدُونَ عُلُوًّا في الأرض} تكبُّراً وتجبُّراً فيها {ولا فساد} عملاً بالمعاصي وأخذاً للمال بغير حقٍّ {والعاقبة} المحمودة {للمتقين}',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3337,'{مَنْ جَاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْهَا وَمَنْ جَاءَ بِالسَّيِّئَةِ فَلا يُجْزَى الَّذِينَ عَمِلُوا السَّيِّئَاتِ إلا ما كانوا يعملون}',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3338,'{إن الذي فرض عليك القرآن} أنزله وقيل: فرض عليك العمل بما في القرآن {لرادُّك إلى معاد} إلى مكَّة ظاهراً عليها وذلك حين اشتاق رسول الله صلى الله عليه وسلم إلى مولده',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3339,'{وَمَا كُنْتَ تَرْجُو أَنْ يُلْقَى إِلَيْكَ الْكِتَابُ إلاَّ رحمة من ربك} لكن رحمك ربُّك فاختارك للنُّبوَّة وأنزل عليك الوحي',NULL,'827',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3340,'{وَلا يَصُدُّنَّكَ عَنْ آيَاتِ اللَّهِ بَعْدَ إِذْ أنزلت إليك} وهذا حين دُعي إلى دين آبائه وقوله:',NULL,'827',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3341,'{كل شيء هالك إلاَّ وجهه} أَيْ: إلاَّ إيَّاه {له الحكم} يحكم بما يريد {وإليه ترجعون}',NULL,'826',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3342,'{الم}',NULL,'828',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3343,'{أحسب الناس أن يتركوا} الآية نزلت في الذين جزعوا من أصحاب النبي صلى الله عليه وسلم من أذى المشركين معناه أحسبوا أن يُقنع منهم بأن يقولوا: إنَّا مؤمنون فقط ولا يُمتحنون بما يُبيِّن حقيقة إيمانهم',NULL,'828',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3344,'{ولقد فتنا الذين من قبلهم} اختبرنا وابتلينا {فليعلمنَّ الله} صِدقَ {الذين صدقوا} في قولهم: آمنَّا بوقوعه منهم وهو الصَّبر على البلاء {وليعلمنَّ} كذب {الكاذبين} في قولهم: آمنَّا بارتدادهم إلى الكفر عن الدِّين عند البلاء ومعنى العلم ها هنا العلم به موجوداً كائناً',NULL,'828',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3345,'{أم حسب الذين يعملون السيئات} الشِّرك {أن يسبقونا} يفوتونا {ساء ما يحكمون} بئس حكما يحكمون لأنفسهم بهذا الظَّنِّ',NULL,'828',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3346,'{من كان يرجو لقاء الله} يخشى البعث {فإنَّ أجل الله} وعده بالثَّواب والعقاب {لات} لكائن وقوله:',NULL,'829',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3347,'{وَمَنْ جَاهَدَ فَإِنَّمَا يُجَاهِدُ لِنَفْسِهِ إِنَّ اللَّهَ لغني عن العالمين}',NULL,'829',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3348,'{ولنجزينَّهم أحسن الذي كانوا يعملون} أَيْ: بأحسن أعمالهم وهو الطَّاعة',NULL,'829',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3349,'{ووصينا الإِنسان بوالديه حسناً} أمرناه أن يُحسن إليهما {وإن جاهداك} اجتهدا عليك {لِتُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ} أنَّه لي شريك {فلا تطعهما} أُنزلت في سعد بن أبي وقَّاص لمَّا أسلم حلفت أمُّه أن لا تأكل ولا تشرب ولا يظلُّها سقف بيت حتى يكفر بمحمد صلى الله عليه وسلم ويرجع إلى ما كان عليه فأُمر أن يترضَّاها ويُحسن إليها ولا يُطيعها في الشِّرك وقوله:',NULL,'829',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3350,'{لندخلنَّهم في الصالحين} أَيْ: في زمرتهم وجملتهم ومعناه: لنحشرنَّهم معهم وقوله:',NULL,'829',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3351,'{جعل فتنة الناس} أَيْ: أذاهم وعذابهم {كعذاب الله} جزع من ذلك كما يجزع من عذاب الله ولا يصبر على الأذيَّة في الله {ولئن جاء} المؤمنين {نصرٌ من ربك ليقولنَّ} هؤلاء الذين ارتدُّوا حين أُوذوا: {إنا كنَّا معكم} وهم كاذبون فقال الله تعالى: {أو ليس الله بأعلم بما في صدور العالمين} يعني: إنَّه عالم بإيمان المؤمن وكفر الكافر',NULL,'829',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3352,'{وليعلمنَّ الله الذين آمنوا وليعلمنَّ المنافقين} هذا إخبارٌ عن الله تعالى أنَّه يعلم إيمان المؤمن وكفر المنافق',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3353,'{وقال الذين كفروا} من أهل مكَّة {للذين آمنوا اتبعوا سبيلنا} الطَّريق الذي نسلكه ي ديننا {ولنحمل خطاياكم} أَيْ: إن كان فيه إثمٌ فنحن نحمله قال الله تعالى: {وَمَا هُمْ بِحَامِلِينَ مِنْ خطاياهم من شيء} يخفِّف عنهم العذاب {إنهم لكاذبون} في قولهم لأنَّهم في القيامة لا يحملون عنهم خطاياهم ثمَّ أعلم الله عز وجل أنَّهم يحملون أوزار أنفسهم وأثقالاً أخرى بسبب إضلالهم مع أثقال أنفسهم لأنَّ مَنْ دعا إلى ضلالةٍ فاتُّبع فعليه مثل أوزار الذين اتَّبعوه ثمَّ ذكر أنَّه يُوبِّخهم على ما قالوا فقال: {وليسألنَّ يوم القيامة عما كانوا يفترون} أي: سؤال توبيخ وقوله:',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3354,'{وَلَيَحْمِلُنَّ أَثْقَالَهُمْ وَأَثْقَالا مَعَ أَثْقَالِهِمْ وَلَيُسْأَلُنَّ يَوْمَ القيامة عما كانوا يفترون}',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3355,'{وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَى قَوْمِهِ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلا خَمْسِينَ عَامًا فَأَخَذَهُمُ الطُّوفَانُ وَهُمْ ظَالِمُونَ}',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3356,'{فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين}',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3357,'{وَإِبْرَاهِيمَ إِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ ذلكم خير لكم إن كنتم تعلمون}',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3358,'{وتخلقون إفكاً} أَيْ: تقولون كذبا: إن الأوثان شركاه الله وقوله:',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3359,'{وَإِنْ تُكَذِّبُوا فَقَدْ كَذَّبَ أُمَمٌ مِنْ قَبْلِكُمْ وما على الرسول إلا البلاغ المبين}',NULL,'830',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3360,'{أَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللَّهُ الْخَلْقَ ثُمَّ يعيده} كما بدأ وليس المعنى: على أو لم يروا كيف يعيده لأنَّهم لم يروا الإِعادة',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3361,'{قُلْ سِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ بَدَأَ الخلق} يعني: الأمم الماضية كيف قدر الله سبحانه على خلقهم ابتداءً {ثمَّ الله ينشئ النشأة الآخرة} أيْ: يبعثهم ثانية بإنشائه إيلهم',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3362,'{يُعَذِّبُ مَنْ يَشَاءُ وَيَرْحَمُ مَنْ يَشَاءُ وَإِلَيْهِ تقلبون}',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3363,'{وَمَا أَنْتُمْ بِمُعْجِزِينَ فِي الأَرْضِ وَلا فِي السماء} لو كنتم فيها ثمَّ عاد الكلام إلى قصَّة إبراهيم عليه السَّلام فقال:',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3364,'{وَالَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ وَلِقَائِهِ أُولَئِكَ يَئِسُوا من رحمتي وأولئك لهم عذاب أليم}',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3365,'{فما كان جواب قومه} حين دعاهم إلى الله سبحانه {إلاَّ أن قالوا اقتلوه أو حرّقوه} الآية',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3366,'{وقال} لهم إبراهيم: {إِنَّمَا اتَّخَذْتُمْ مِنْ دُونِ اللَّهِ أَوْثَانًا مَوَدَّةَ بينكم} أَيْ: ليتوادُّوا بها فهي مودَّة بينكم ما دمتم في هذه الدنيا تنقطع ولا تنفع في الآخرة وهو قوله تعالى: {ثم يوم القيامة يكفر بعضكم ببعض} تتبرَّأُ الأوثان من عابديها وقوله تعالى:',NULL,'831',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3367,'{فآمن له لوط} هو أوَّل مَنْ آمن بإبراهيم عليه السَّلام {وقال إني مهاجر إلى ربي} هاجر من سواد الكوفة إلى الشَّام',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3368,'{وآتيناه أجره في الدنيا} قيل: هو الذِّكر الحسن وقيل: هو الوالد الصالح',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3369,'{وَلُوطًا إِذْ قَالَ لِقَوْمِهِ إِنَّكُمْ لَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ أَحَدٍ مِنَ الْعَالَمِينَ}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3370,'{وتقطعون السبيل} أَيْ: سبيل الولد وقيل: يأخذون النَّاس من الطُّرق لطلب الفاحشة {وتأتون في ناديكم} مجلسكم {المنكر} كان بعضهم يُجامع بعضاً في مجالسهم {فَمَا كَانَ جَوَابَ قَوْمِهِ إِلا أَنْ قَالُوا ائْتِنَا بِعَذَابِ اللَّهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ} أنًّه نازلٌ بنا وقوله:',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3371,'{قال رب انصرني على القوم المفسدين}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3372,'{وَلَمَّا جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَى قَالُوا إِنَّا مهلكوا أَهْلِ هَذِهِ الْقَرْيَةِ إِنَّ أَهْلَهَا كَانُوا ظَالِمِينَ}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3373,'{قَالَ إِنَّ فِيهَا لُوطًا قَالُوا نَحْنُ أَعْلَمُ بِمَنْ فِيهَا لَنُنَجِّيَنَّهُ وَأَهْلَهُ إِلا امْرَأَتَهُ كَانَتْ من الغابرين}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3374,'{وَلَمَّا أَنْ جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالُوا لا تَخَفْ وَلا تَحْزَنْ إِنَّا مُنَجُّوكَ وَأَهْلَكَ إِلا امْرَأَتَكَ كَانَتْ من الغابرين}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3375,'{إِنَّا مُنْزِلُونَ عَلَى أَهْلِ هَذِهِ الْقَرْيَةِ رِجْزًا مِنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3376,'{ولقد تركنا منها} من قرية قوم لوط {آية بينة} عبرةً ظاهرةً وهي خرابها وآثارها وقوله:',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3377,'{وَإِلَى مَدْيَنَ أَخَاهُمْ شُعَيْبًا فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ وَارْجُوا الْيَوْمَ الآخِرَ وَلا تَعْثَوْا فِي الأَرْضِ مُفْسِدِينَ}',NULL,'832',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3378,'{فَكَذَّبُوهُ فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ}',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3379,'{وكانوا مستبصرين} أَيْ: في ضلالتهم معجبين بها وقيل: حسبوا أنَّهم على الهدى وهم على الباطل وقيل: أتوا ما أتوه وقد بيِّن لهم أنَّ عاقبته العذاب',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3380,'{وَقَارُونَ وَفِرْعَوْنَ وَهَامَانَ وَلَقَدْ جَاءَهُمْ مُوسَى بِالْبَيِّنَاتِ فَاسْتَكْبَرُوا فِي الأَرْضِ وَمَا كَانُوا سَابِقِينَ}',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3381,'{فكلاً} من الكفَّار {أخذنا} عاقبنا {بذنبه فمنهم من أرسلنا عليه حاصباً} وهم قوم لوط {ومنهم من أخذته الصيحة} قوم ثمود {ومنهم مَنْ خسفنا به الأرض} قارون وقومه {ومنهم من أغرقنا} قوم نوح وفرعون {وَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ} لأنَّه قد بيَّن لهم بإرسال الرَّسول {ولكن كانوا أنفسهم يظلمون} بكفرهم',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3382,'{مَثَلُ الَّذِينَ اتَّخَذُوا مِنْ دُونِ اللَّهِ أَوْلِيَاءَ} يعني: الأصنام في قلَّة غنائها عنهم {كمثل العنكبوت اتخذت بيتاً} لا يدفع عنها حراً ولا برداً {وإنَّ أوهن البيوت لبيت العنكبوت} وذلك أنَّه لا بيت أضعف منه فيما يتَّخذه الهوامُّ {لو كانوا يعلمون} موضعَه عند قوله: مثلُ الذين اتخذوا من دونه أولياء لو كانوا يعملون كمثل العنكبوت فهو مؤخَّر معناه التقديم وقوله:',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3383,'{إِنَّ اللَّهَ يَعْلَمُ مَا يَدْعُونَ مِنْ دُونِهِ مِنْ شَيْءٍ وَهُوَ الْعَزِيزُ الْحَكِيمُ}',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3384,'{وَتِلْكَ الأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ وَمَا يَعْقِلُهَا إِلا العالمون}',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3385,'{خلق الله السماوات وَالأَرْضَ بِالْحَقِّ إِنَّ فِي ذَلِكَ لآيَةً لِلْمُؤْمِنِينَ}',NULL,'833',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3386,'{إنَّ الصلاة تنهى عن الفحشاء والمنكر} يعني: إنَّ في الصَّلاة منهاةً ومزدجراً عن معاصي الله تعالى فمن لم تنهه صلاته عن المنكر فليست صلاته بصلاةٍ {ولذكر الله أكبر} من كلِّ شيء في الدُّنيا وأفضل',NULL,'834',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3387,'{وَلا تُجَادِلُوا أَهْلَ الْكِتَابِ إِلا بِالَّتِي هِيَ أحسن} وهو الجميل من القول بالدُّعاء إلى الله عزَّ وجل والتَّنبيه على الحجج {إلاَّ الذين ظلموا منهم} أَيْ: إلاَّ الذين ظلموكم بالقتال ومنع الجزية',NULL,'834',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3388,'{وكذلك} أَيْ: وكما آتيناهم الكتاب {أنزلنا إليك الكتاب فَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يؤمنون به} بمحمد صلى الله عليه وسلم يعني: مَنْ كانوا قبل عصره كانوا يؤمنون به لما يجدونه من نعته في كتابهم {ومن هؤلاء} الذين هو بين ظهرانيهم {مَنْ يؤمن به}',NULL,'834',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3389,'{وما كنت تتلو من قبله} من قبل الكتاب الذي أنزلناه إليك {من كتابٍ ولا تخطُّه} ولا تكتبه {بيمينك إذاً لارتاب المبطلون} لشكُّوا فيك واتَّهموك لو كنت تكتب وأراد بالمبطلين كفَّار قريش يعني: لقالوا: إنَّه كتبه وتعلَّمه من كتاب',NULL,'834',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3390,'{بل هو} يعني: محمداً صلى الله عليه وسلم والعلم بأنَّه أُمِّيٌّ {آيات بينات في صدور الذين أوتوا العلم} من أهل الكتاب قرؤوها من التَّوراة وحفظوها',NULL,'834',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3391,'{وَقَالُوا لَوْلا أُنْزِلَ عَلَيْهِ آيَاتٌ مِنْ رَبِّهِ} كما أُنزل على مَنْ قبله من الأنبياء {قل إنما الآيات عند الله} إذا شاء أرسلها وليست بيدي',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3392,'{قل كفى بالله بيني وبينكم شهيداً} يشهد على صدقي وعلى تكذيبكم وقوله:',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3393,'{قُلْ كَفَى بِاللَّهِ بَيْنِي وَبَيْنَكُمْ شَهِيدًا يَعْلَمُ ما في السماوات وَالأَرْضِ وَالَّذِينَ آمَنُوا بِالْبَاطِلِ وَكَفَرُوا بِاللَّهِ أُولَئِكَ هم الخاسرون}',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3394,'{ويستعجلونك بالعذاب ولولا أجل مسمى لَجَاءَهُمُ الْعَذَابُ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَهُمْ لا يَشْعُرُونَ}',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3395,'{يستعجلونك بالعذاب وإنَّ جهنم لمحيطة بالكافرين}',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3396,'{ويقول: ذوقوا ما كنتم تعملون} أَيْ: جزاءه من العذاب',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3397,'{يَا عِبَادِيَ الَّذِينَ آمَنُوا إِنَّ أَرْضِي وَاسِعَةٌ} نزلت في حثِّ مَنْ كانوا بمكَّة لا يقدرون على إظهار دينهم على الهجرة',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3398,'{كل نفس ذائقة الموت} أينما كانت فلا تُقيموا بدار الشِّرك وقوله:',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3399,'{لَنُبَوِّئَنَّهُمْ من الجنة غرفاً} أَيْ: ولننزلنَّهم منها قصوراً',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3400,'{الذين صبروا وعلى ربهم يتوكلون}',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3401,'{وكأين} وكم {من دابَّة لا تحمل رزقها} فتخبئه لغدٍ {الله يرزقها} يوماً بيوم {وإياكم} وذلك أنَّ الذين كانوا بمكَّة من المؤمنين إذا قيل لهم اخرجوا إلى المدينة قالوا: فمَنْ يُطعمنا بها ولا مال لنا هناك فأنزل الله تعالى: {الله يرزقها وإياكم}',NULL,'835',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3402,'{ولئن سالتهم من خلق السماوات وَالأَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُولُنَّ اللَّهُ فَأَنَّى يؤفكون}',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3403,'{الله يبسط الرزق لمن يشاء من عباده وَيَقْدِرُ لَهُ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ}',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3404,'{وَلَئِنْ سَأَلْتَهُمْ مَنْ نَزَّلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الأَرْضَ مِنْ بَعْدِ مَوْتِهَا لَيَقُولُنَّ الله قل الحمد لله} على إنزاله الماء لإِحياء الأرض {بل أكثرهم لا يعقلون} العقل الذي يعرفون به الحقَّ من الباطل',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3405,'{وَمَا هَذِهِ الْحَيَاةُ الدُّنْيَا إِلا لَهْوٌ وَلَعِبٌ} لنفادها عن قريب {وإنَّ الدار الآخرة لهي الحيوان} الحياة الدَّائمة {لو كانوا يعلمون} أنَّها كذلك ولكنَّهم لا يعلمون',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3406,'{فإذا ركبوا في الفلك} وخافوا الغرق {دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ إِذَا هُمْ يُشْرِكُونَ}',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3407,'{ليكفروا بما آتيناهم} أَيْ: ليجحدوا بما أنعمنا عليهم من إنجائهم والظَّاهر أنَّ هذا لام الأمر أمر التَّهديد ويدلُّ عليه قوله تعالى: {وليتمتعوا فسوف يعلمون}',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3408,'{أَوَلَمْ يروا} يعني: أهل مكَّة {أنا جعلنا حرماً آمناً} ذا أمنٍ لا يُغار على أهله {ويتخطف الناس من حولهم} بالقتل والنَّهب والسَّبي {أفبالباطل يؤمنون} يعني: الأصنام {وبنعمة الله} يعني: محمدا صلى الله عليه وسلم والقرآن {يكفرون}',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3409,'{وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاءَهُ أَلَيْسَ فِي جهنَّم مثوى للكافرين}',NULL,'836',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3410,'{والذين جاهدوا فينا} أعداء الدِّين والكفَّار {لنهدينَّهم سبلنا} سبل الشًّهادة والمغفرة: وقيل: من اجتهد في عملٍ لله زاده الله تعالى هدىً على هدايته {وإنَّ الله لمع المحسنين} بنصره إيَّاهم',NULL,'837',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3411,'{الم}',NULL,'838',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3412,'{غلبت الروم} غلبتها فارس {في أدنى الأرض} أدنى أرض الشَّام من أرض العرب وفارس وهي أذرعات وعسكر {وهم} والرُّوم {من بعد غلبهم} غلبة فارس إيَّاهم {سيغلبون} فارس',NULL,'838',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3413,'{في بضع سنين} البضع: ما بين الثّلاث إلى التِّسع {لله الأمر من قبل} من قبل أن تغلب الرُّوم {ومن بعد} ما غلبت {ويومئذٍ يفرح المؤمنون} يوم تغلب الرُّومُ فارسَ يَفْرَحُ الْمُؤْمِنُونَ {بِنَصْرِ اللَّهِ} الرُّوم لأنَّهم أهل كتاب فهم أقرب إلى المؤمنين وفارس مجوس فكانوا أقرب إلى المشركين فالمؤمنون يفرحون بنصر الله الرُّوم على فارس والمشركون يحزنون لذلك',NULL,'838',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3414,'{فِي بِضْعِ سِنِينَ لِلَّهِ الأَمْرُ مِنْ قَبْلُ وَمِنْ بَعْدُ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ}',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3415,'{بنصر الله ينصر من يشاء وهو العزيز الرحيم}',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3416,'{وعد الله} وعد ذلك وعداً {ولكنَّ أكثر الناس} يعني: مشركي مكَّة {لا يعلمون} ذلك ثمَّ بيَّن مقدار ما يعلمون فقال:',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3417,'{يعلمون ظاهراً من الحياة الدنيا} يعني: أمر معاشهم وذلك أنَّهم كانوا أهل تجارة وتكسب بها',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3418,'{أولم يتفكروا في أنفسهم} فيعلموا {ما خلق الله السماوات والأرض وما بينهما إلاَّ بالحق} أَيْ: للحقِّ وهو الدّلالة على توحيده وقدرته {وأجل مسمى} ووقتٍ معلومٍ تفنى عنده يعني: يوم القيامة وقوله:',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3419,'{وأثاروا الأرض} أَيْ: قلبوها للزِّراعة {وعمروها أكثر مما عمروها} يعني: إنَّ الذين أُهلكوا من الأمم الخالية كانوا أكثر حرثاَ وعمارةً من أهل مكَّة',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3420,'{ثم كان عاقبة الذين أساؤوا} أشركوا {السوأى} النَّار {أن كذَّبوا} بأن كذَّبوا وقوله:',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3421,'{اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ثُمَّ إِلَيْهِ ترجعون}',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3422,'{يبلس المجرمون} أَيْ: يسكتون لانقطاع حجَّتهم وليأسهم من الرَّحمة',NULL,'839',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3423,'{ولم يكن لهم من شركائهم} أوثانهم التي عبدوها رجاء الشَّفاعة {شفعاء وكانوا بشركائهم كافرين} قالوا: ما عبدتمونا وقوله:',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3424,'{يومئذ يتفرَّقون} يعني: المؤمنين والكافرين ثمَّ بيَّن كيف ذلك التفرق فقال:',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3425,'{فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ فِي روضة يحبرون} أَي: يسمعون في الجنَّة',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3426,'{وَأَمَّا الَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الآخِرَةِ فأولئك في العذاب محضرون}',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3427,'{فسبحان الله} فصلُّوا لله سبحانه {حين تمسون} يعني: صلاة المغرب والعشاء الآخرة {وحين تصبحون} صلاة الفجر {وعشياً} يعني: صلاة العصر {وحين تظهرون} يعني: صلاة الظهر',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3428,'{وله الحمد في السماوات وَالأَرْضِ وَعَشِيًّا وَحِينَ تُظْهِرُونَ}',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3429,'{يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِي الأَرْضَ بَعْدَ مَوْتِهَا وَكَذَلِكَ تُخْرَجُونَ}',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3430,'{ومن آياته أن خلقكم من تراب} يعني: أباكم آدم {ثم إذا أنتم بشر تنتشرون} يعني: ذريته',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3431,'{وَمِنْ آيَاتِهِ أَنْ خَلَقَ لَكُمْ مِنْ أَنْفُسِكُمْ} من جنسكم {أَزْوَاجًا لِتَسْكُنُوا إِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَوَدَّةً وَرَحْمَةً} يعني: الأُلفة بين الزَّوجين',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3432,'{ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم} وأنتم بنو رجلٍ واحدٍ وامرأةٍ واحدةٍ',NULL,'840',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3433,'{وَمِنْ آيَاتِهِ مَنَامُكُمْ بِاللَّيْلِ وَالنَّهَارِ وَابْتِغَاؤُكُمْ مِنْ فضله} أَي: اللَّيل لتناموا فيه والنَّهار لتبتغوا فيه من فضله',NULL,'841',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3434,'{ومن آياته يريكم البرق خوفاً} للمسافر {وطمعاً} للحاضر وقوله:',NULL,'841',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3435,'{ثُمَّ إِذَا دَعَاكُمْ دَعْوَةً مِنَ الأَرْضِ إِذَا أنتم تخرجون} ثم إذا دعاكم دعوة إذا أنتم تخرجون من الأرض هكذا تقدير الآية على التَّقديم والتأخير وقوله:',NULL,'841',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3436,'{كلٌّ له قانتون} أَيْ: مُطيعون لا طاعة العبادة ولكن طاعة الإرادة خلقهم على ما أراد فكانوا على ما أراد لا يقدر أحدٌ أن يتغيَّر عمَّا خُلق عليه وقوله:',NULL,'841',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3437,'{وهو أهون عليه} أَيْ: هيِّنٌ عليه وقيل: هو أهون عليه عندكم وفيما بينكم لأَنَّ الإِعادة عندنا أيسر من الابتداء {وله المثل الأعلى} الصِّفة العليا وهو أنَّه لا إله إلاَّ هو ولا ربَّ غيره',NULL,'841',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3438,'{ضرب لكم مثلاً} بيَّن لكم شبهاً في اتِّخاذكم الأصنام شركاء مع الله سبحانه {من أنفسكم} ثمَّ بيَّن ذلك فقال: {هَلْ لَكُمْ مِنْ مَا مَلَكَتْ أَيْمَانُكُمْ} من العبيد والإماء {مِنْ شُرَكَاءَ فِي مَا رَزَقْنَاكُمْ} من المال والولد أّيْ: هل يشاركونكم فيما أعطاكم الله سبحانه حتى تكونوا أنتم وهم {فيه سواء تخافونهم} أن يرثوكم كما يخاف بعضكم بعضاً أن يرثه ماله والمعنى: كما لا يكون هذا فكيف يكون ما هو مخلوقٌ لله تعالى مثلَه حتى يُعبد كعبادته؟ فلمَّا لزمتهم الحجَّة بهذا ذكر أنَّهم يعبدونها باتَّباع الهوى فقال: {بل اتبع الذين ظلموا أهواءهم} في عبادة الأصنام',NULL,'841',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3439,'{بَلِ اتَّبَعَ الَّذِينَ ظَلَمُوا أَهْوَاءَهُمْ بِغَيْرِ عِلْمٍ فَمَنْ يَهْدِي مَنْ أَضَلَّ اللَّهُ وَمَا لَهُمْ من ناصرين}',NULL,'842',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3440,'{فأقم وجهك للدين حنيفاً} أَيْ: أقبل عليه ولا تُعرض عنه {فطرة الله} أي: اتَّبع فطرة الله أَيْ: خِلقة الله التي خلق النَّاس عليها وذلك أنَّ كلَّ مولودٍ يُولد على ما فطره الله عليه من أنَّه لا ربَّ له غيره كما أقرَّ له لمَّا أُخرج من ظهر آدم عليه السَّلام {لا تبديل لخلق الله} لم يبدَّلِ الله سبحانه دينه فدينُه أنَّه لا ربَّ غيره {ذلك الدين القيم} المستقيم',NULL,'842',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3441,'{منيبين إليه} راجعين إلى ما أمر به وهو حالٌ من قوله: {فأقم وجهك} والمعنى: فأقيموا وجوهكم لأنَّ أمره أمرٌ لأمته وقوله:',NULL,'842',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3442,'{من الذين فرقوا دينهم وكانوا شيعاً} مفسَّرٌ في سورة الأنعام {كلُّ حزب} كلُّ جماعةٍ من الذين فارقوا دينهم {بما لديهم فرحون} أَيْ: يظنون أنَّهم على الهدى ثمَّ ذكر أنَّهم مع شركهم لا يلتجئون في الشَّدائد إلى الأصنام فقال:',NULL,'842',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3443,'{وَإِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُنِيبِينَ إليه} الآية وقوله:',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3444,'{ليكفروا بما آتيناهم} مفسَّرٌ في سورة العنكبوت إلى قوله:',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3445,'{أم أنزلنا} أَيْ: أَأنزلنا {عليهم سلطاناً} كتاباً {فهو يتكلَّم بما كانوا به يشركون} ينطق بعذرهم في الإِشراك',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3446,'{وإذا أذقْنا الناس رحمة فرحوا بها} الآية هذا من صفة الكافر يبطر عند النِّعمة ويقنط عند الشدة ولا يشكر في الأُولى ولا يحتسب في الثانية',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3447,'{أَوَلَمْ يَرَوْا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يؤمنون}',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3448,'{فَآتِ ذَا الْقُرْبَى حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ ذَلِكَ خَيْرٌ لِلَّذِينَ يُرِيدُونَ وَجْهَ اللَّهِ وَأُولَئِكَ هم المفلحون}',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3449,'{وَمَا آتَيْتُمْ مِنْ رِبًا لِيَرْبُوَ فِي أَمْوَالِ الناس} يعني: ما يعطونه من الهدية ليأخذوا أكثر منها وهو من الرِّبا الحلال {فلا يربو عند الله} لأنَّكم لم تريدوا بذلك وجه الله وقوله: {فأولئك هم المضعفون} أصحاب الإِضعاف يُضَاعِفُ لهم بالواحدة عشراً',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3450,'{اللَّهُ الَّذِي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ هَلْ مِنْ شُرَكَائِكُمْ مَنْ يَفْعَلُ مِنْ ذَلِكُمْ مِنْ شَيْءٍ سُبْحَانَهُ وَتَعَالَى عَمَّا يشركون}',NULL,'843',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3451,'{ظهر الفساد} القحط وذهاب البركة {في البر} القفار {والبحر} القرى والرِّيف {بما كسبت أيدي الناس} بشؤم ذنوبهم {ليذيقهم بعض الذي عملوا} كان ذلك لِيُذَاقوا الشِّدَّة بذنوبهم في العاجل',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3452,'{قُلْ سِيرُوا فِي الأَرْضِ فَانْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلُ كَانَ أَكْثَرُهُمْ مُشْرِكِينَ}',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3453,'{فَأَقِمْ وَجْهَكَ لِلدِّينِ الْقَيِّمِ مِنْ قَبْلِ أَنْ يأتي يومٌ} القيامةُ فلا ينفع نفساً إيمانها {يومئذٍ يصدَّعون} يتفرَّقون فريقٌ في الجنَّة وفريقٌ في السَّعير',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3454,'{مَنْ كفر فعليه كفره} أي: وبال كفره وعذابه {ومَنْ عمل صالحاً فلأنفسهم يمهدون} يفرشون ويسؤون المضاجع والمعنى: لأنفسهم يبغون الخير',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3455,'{ومن آياته أن يرسل الرياح مبشرات} بالمطر {وليذيقكم من رحمته} نعمته بالمطر يُرسلها {ولتجري الفلك بأمره} وذلك أنَّها تجري بالرِّياح {ولتبتغوا من فضله} بالتِّجارة في البحر وقوله:',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3456,'{وَمِنْ آيَاتِهِ أَنْ يُرْسِلَ الرِّيَاحَ مُبَشِّرَاتٍ وَلِيُذِيقَكُمْ مِنْ رَحْمَتِهِ وَلِتَجْرِيَ الْفُلْكُ بِأَمْرِهِ وَلِتَبْتَغُوا مِنْ فضله ولعلكم تشكرون}',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3457,'{فانتقمنا من الذين أجرموا} أَيْ: عاقبنا الذين اشركوا {وكان حقاً عليناً نصر المؤمنين} في العاقبة وكذلك ننصرك في العاقبة على مَنْ عاداك',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3458,'{الله الذي يرسل الرياح فتثير سحاباً} تُزعجها وتُخرجها من أماكنها {فيبسطه} الله {في السماء كيف يشاء ويجعله كسفاً} قطعاً يريد أنَّه مرَّةً يبسطه ومرَّةً يقطعه {فترى الودق} المطر {يخرج من خلاله} وسطه وشقوقه {فَإِذَا أَصَابَ بِهِ} بالودق {مَنْ يَشَاءُ مِنْ عباده إذا هم يستبشرون} يفرحون',NULL,'844',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3459,'{وَإِنْ كَانُوا مِنْ قَبْلِ أَنْ يُنَزَّلَ عَلَيْهِمْ} المطر {من قبله} كرَّر من قبل التأكيد {لمبلسين} آيسين',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3460,'{فانظر إلى آثار رحمة الله} يعني: آثار المطر الذي هو رحمة الله تعالى {كيف يحيي الأرض} جعلها تنبت {بعد موتها} يبسطها {إنَّ ذلك} الذي فعل ذلك وهو الله عزَّ وجل {لمحيي الموتى}',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3461,'{ولئن أرسلنا ريحاً فرأوه مصفراً} رأوا النَّبت قد اصفرَّ وجفَّ {لظلُّوا من بعده يكفرون} يريد: إنَّ الكفَّار يستبشرون بالغيث فإذا جفَّ النَّبت ولم يحتاجوا إلى الغيث ظلوا يكفوا بنعمة الله عز وجل فلم يؤمنوا ولم يشكروا إنعامه بالمطر',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3462,'{فإنك لا تسمع الموتى} مضت الاية في سورة الأنبياء والتي بعدها في سورة النمل',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3463,'{وما أنت بهاد الْعُمْيِ عَنْ ضَلالَتِهِمْ إِنْ تُسْمِعُ إِلا مَنْ يؤمن بآياتنا فهم مسلمون}',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3464,'{الله الذي خلقكم من ضعف} من نطفةٍ الآية',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3465,'{ويوم تقوم الساعة يقسم المجرمون} يحلف الكافرون {ما لبثوا} في قبورهم {غير ساعة كذلك كانوا يؤفكون} أَيْ: كذَّبوا في هذا الوقت كما كانوا يُكذِّبون في الدُّنيا',NULL,'845',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3466,'{وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَالإِيمَانَ لَقَدْ لَبِثْتُمْ في كتاب الله} أَيْ: فيما بيَّن في كتابه وهو اللوح المحفوظ {يوم البعث ولكنكم كنتم لا تعلمون} أنَّه يكون وقوله:',NULL,'846',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3467,'{ولا هم يستعتبون} أَيْ: لا يُطلب منهم أن يرجعوا إلى ما يرضي الله سبحانه',NULL,'846',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3468,'{ولقد ضربنا للناس في هذا القرآن من كلِّ مثل} بيَّنا لهم الأمثال للاعتبار {ولئن جئتهم بآية} لهم فيها بيانٌ واعتبارٌ {لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ أَنْتُمْ إِلا مُبْطِلُونَ} ما أنتم إلاَّ أصحاب الأباطيل',NULL,'846',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3469,'{كذلك} كما طبع الله على قلوبهم حتى لم يفهموا {يَطْبَعُ اللَّهُ عَلَى قُلُوبِ الَّذِينَ لا يَعْلَمُونَ} أدلَّة التَّوحيد',NULL,'846',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3470,'{فاصبر إنَّ وعد الله} في نصرك وتمكينك {حق ولا يستخفنَّك} لا يستفزنَّك عن دينك {الذين لا يوقنون} أي: الضُّلال الشَّاكُّون',NULL,'846',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3471,'{الم}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3472,'{تِلْكَ آيَاتُ الْكِتَابِ الْحَكِيمِ}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3473,'{هُدًى وَرَحْمَةً لِلْمُحْسِنِينَ}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3474,'{الَّذِينَ يُقِيمُونَ الصَّلاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ بِالآخِرَةِ هم يوقنون}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3475,'{أُولَئِكَ عَلَى هُدًى مِنْ رَبِّهِمْ وَأُولَئِكَ هُمُ المفلحون}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3476,'{ومن الناس من يشتري لهو الحديث} يعني: النَّضر بن الحارث كان يخرج تاجراً إلى فارس فيشتري أخبار الأعاجم ثمَّ يأتي بها فيقرؤها في أندية قريش فيستملحونها ويتركون استماع القرآن وقوله: {ويتخذها هزواً} أَيْ: يتَّخذ آيات الكتاب هزوا وقوله:',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3477,'{وَإِذَا تُتْلَى عَلَيْهِ آيَاتُنَا وَلَّى مُسْتَكْبِرًا كَأَنْ لَمْ يَسْمَعْهَا كَأَنَّ فِي أُذُنَيْهِ وَقْرًا فَبَشِّرْهُ بعذاب أليم}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3478,'{إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ النعيم}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3479,'{خَالِدِينَ فِيهَا وَعْدَ اللَّهِ حَقًّا وَهُوَ الْعَزِيزُ الحكيم}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3480,'{خلق السماوات بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَأَلْقَى فِي الأَرْضِ رَوَاسِيَ أَنْ تَمِيدَ بِكُمْ وَبَثَّ فِيهَا مِنْ كُلِّ دَابَّةٍ وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَنْبَتْنَا فِيهَا مِنْ كُلِّ زَوْجٍ كَرِيمٍ}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3481,'{هَذَا خَلْقُ اللَّهِ فَأَرُونِي مَاذَا خَلَقَ الَّذِينَ مِنْ دُونِهِ بَلِ الظَّالِمُونَ فِي ضَلالٍ مُبِينٍ}',NULL,'847',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3482,'{وَلَقَدْ آتَيْنَا لُقْمَانَ الْحِكْمَةَ أَنِ اشْكُرْ لِلَّهِ} أَيْ: وقلنا له: أن اشكر الله وقوله:',NULL,'848',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3483,'{وَإِذْ قَالَ لُقْمَانُ لابْنِهِ وَهُوَ يَعِظُهُ يَا بُنَيَّ لا تُشْرِكْ بِاللَّهِ إِنَّ الشِّرْكَ لَظُلْمٌ عظيم}',NULL,'848',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3484,'{حملته أمه وهناً على وهن} أَيْ: لزمها بحملها إيَّاه أن تضعف مرَّةً بعد مرَّةً {وفصاله} وفطامه {في عامين} لأنَّها ترضع الولد عامين {أن اشكر لي ولوالديك} المعنى: وصَّينا الإِنسان أن اشكر لي ولوالديك',NULL,'848',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3485,'{وإن جاهداك} مُفسَّرٌ فيما مضى وقوله: {وصاحبهما في الدنيا معروفاً} أَيْ: مُصَاحَباً معروفاً وهو المستحسن {واتبع سبيل من أناب} رجع {إليَّ} يعني: اسلك سبيل محمد صلى الله عليه وسلم وأصحابه نزلت في سعد بن أبي وقاص وقد مرَّ',NULL,'848',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3486,'{يا بني إنها إن تك مثقال} رُوي أنَّ ابنه قال له: إنْ علمت بالخطيئة حيث لا يراني أحدٌ كيف يعلمها الله عزوجل؟ فقال: {إنها} أي: الخطيئة {إن تك مثقال حبة من خردل} أو: السَّيِّئة ثمَّ كانت {في صخرة} أي: ي أخفى مكان {أو في السماوات أو في الأرض} أينما كانت أتى الله بها ولن تخفى عليه ومعنى {يأت بها الله} أَيْ: للجزاء عليها {إِنَّ اللَّهَ لَطِيفٌ} باستخراجها {خبير} بمكانها وقوله:',NULL,'848',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3487,'{إنَّ ذلك من عزم الأمور} أي: الأمور الواجبة',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3488,'{ولا تصعر خدِّك للناس} لا تُعرض عنهم تكبُّراً {ولا تمش في الأرض مرحاً} مُتَبختراً مختالاً',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3489,'{واقصد في مشيك} ليكن مشيك قصداً لا بِخُيلاء ولا بإسراع {واغضض} واخفض {من صوتك إنَّ أنكر الأصوات} أقبحها {لصوت الحمير}',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3490,'{أَلَمْ تَرَوْا أَنَّ اللَّهَ سَخَّرَ لَكُمْ مَا في السماوات} من الشَّمس والقمر والنُّجوم لتنتفعوا بها {وما في الأرض} ومن البحار والأنهار والدَّوابِّ {وأسبغ} وأوسعَ وأتمَّ {عليكم نعمة ظاهرة} وهي حسن الصُّورة وامتداد القامة {وباطنة} وهي المعرفة والباقي قد مضى تفسيره إلى قوله تعالى:',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3491,'{أو لو كان الشيطان يدعوهم إلى عذاب السعير} أَيْ: موجباته فيتَّبعونه',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3492,'{ومن يسلم وجهه إلى الله} يُقبل على طاعته وأوامره {وهو محسن} مؤمنٌ موحِّدٌ {فقد استمسك بالعروة الوثقى} بالطَّرفِ الأوثق الذي لا يخاف انقطاعه {وإلى الله عاقبة الأمور} مرجعها',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3493,'{وَمَنْ كَفَرَ فَلا يَحْزُنْكَ كُفْرُهُ إِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوا إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصدور}',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3494,'{نمتعهم قليلاً} بالدُّنيا {ثمَّ نضطرهم} نُلجئهم {إِلَى عَذَابٍ غَلِيظٍ}',NULL,'849',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3495,'{ولئن سالتهم من خلق السماوات والأرض ليقولن الله قل الحمد لله} الذي خلقها {بل أكثرهم لا يعلمون} إذ أشركوا به بعد إقرارهم بأنه خالقها',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3496,'{لله ما في السماوات والأرض إن الله هو الغني الحميد}',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3497,'{وَلَوْ أَنَّمَا فِي الأَرْضِ مِنْ شَجَرَةٍ أَقْلامٌ} الآية وذلك أنَّ المشركين قالوا في القرآن: هذا كلام سيفذ وينقطع فأعلم الله سبحانه أن كلامه لا ينفذ {والبحر يمده} أَيْ: يزيد فيه ثمَّ كتبت به كلمات الله {ما نفدت}',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3498,'{مَا خَلْقُكُمْ وَلا بَعْثُكُمْ إِلا كَنَفْسٍ وَاحِدَةٍ} أَيْ: كخلق وكبعث نفسٍ واحدةٍ لأنَّ قدرة الله سبحانه على بعث الخلق كقدرته على بعث نفسٍ واحدةٍ وقوله:',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3499,'{أَلَمْ تَرَ أَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي إِلَى أَجَلٍ مُسَمًّى وَأَنَّ الله بما تعملون خبير}',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3500,'{ذلك} أَيْ: فعل الله ذلك لتعلموا {بأن الله هو الحق} الذي لا إله إلا غيره وقوله:',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3501,'{إِنَّ فِي ذَلِكَ لآيَاتٍ لِكُلِّ صَبَّارٍ شكور} أَيْ: لكلِّ مؤمنٍ بهذه الصِّفة',NULL,'850',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3502,'{وإذا غشيهم} علاهم {موج كالظلل} كالجبال وقيل: كالسَّحاب وقوله: {فمنهم مقتصد} أَيْ: مؤمنٌ مُوفٍ بما عاهد الله في البحر وقوله: {كل ختار} عذار {كفور} جحودٍ وقوله:',NULL,'851',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3503,'{لا يجزي والد عن ولده} لا يكفي ولا يُغني عنه شيئاً و {الغَرُور} الشَّيطان',NULL,'851',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3504,'{إنَّ الله عنده علم الساعة} متى تقوم {وينزل الغيث} المطر {ويعلم ما في الأرحام} ذكرا أو أنثى',NULL,'851',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3505,'{الم}',NULL,'852',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3506,'{تَنْزِيلُ الْكِتَابِ لا رَيْبَ فِيهِ مِنْ رَبِّ العالمين}',NULL,'852',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3507,'{أَمْ يَقُولُونَ افْتَرَاهُ بَلْ هُوَ الْحَقُّ مِنْ رَبِّكَ لِتُنْذِرَ قَوْمًا مَا أَتَاهُمْ مِنْ نَذِيرٍ من قبلك لعلهم يهتدون}',NULL,'852',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3508,'{الله الذي خلق السماوات وَالأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَى عَلَى الْعَرْشِ مَا لَكُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَلا شَفِيعٍ أَفَلا تَتَذَكَّرُونَ}',NULL,'852',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3509,'{يدبر الأمر من السماء إلى الأرض} يعني: القضاء من السَّماء فينزله إلى الأرض مدَّة أيام الدُّنيا {ثمَّ يعرج إليه} أَيْ: يرجع الأمر والتَّدبير إلى السَّماء ويعود إليه بعد انقضاء الدُّنيا وفنائها {فِي يَوْمٍ كَانَ مِقْدَارُهُ أَلْفَ سَنَةٍ مِمَّا تعدون} وهو يوم القيامة وذلك اليوم يطول على قومٍ ويشتدُّ حتى يكو كخمسين ألف سنة ويقصر على قوم فلا آخر له معلوم وقوله',NULL,'852',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3510,'{ذلك عالم الغيب والشهادة العزيز الرحيم}',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3511,'{الذي أحسن كلَّ شيء خلقه} أَيْ: أتقنه وأحكمه {وبدأ خلق الإنسان من طين} آدم عليه السَّلام',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3512,'{ثم جعل نسله} ذريَّته {من سلالةٍ} نطفةٍ {من ماء مهين} ضعيفٍ حقيرٍ',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3513,'{وقالوا} يعني: منكري البعث {أإذا ضللنا في الأرض} صرنا تراباً وبطلنا {أإنا لفي خلق جديد} نُخلق بعد ذلك خلقاً جديداً',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3514,'{وقالوا أإذا ضللنا في الأرض أإنا لَفِي خَلْقٍ جَدِيدٍ بَلْ هُمْ بِلِقَاءِ رَبِّهِمْ كافرون}',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3515,'{قل يتوفاكم} يقبض أرواحكم',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3516,'{ولو ترى} يا محمد {إذ المجرمون} المشركون {ناكسوا رؤوسهم} مُطأطئوها حياءً من ربهم عز وجل ويقولون: {ربنا أبصرنا} ما كنا به مُكذِّبين {وسمعنا} منك صدق ما أتت به الرُّسل {فارجعنا} فارددنا إلى الدُّنيا {نعمل صالحاً}',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3517,'{ولو شئنا لآتينا كلَّ نفس هداها} رشدها الآية ويقال لأهل النَّار:',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3518,'{فذوقوا بما نسيتم لقاء يومكم هذا} أَيْ: تركتم الإِيمان به {إنا نسيناكم} تركناكم في النَّار',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3519,'{إِنَّمَا يُؤْمِنُ بِآيَاتِنَا الَّذِينَ إِذَا ذُكِّرُوا بِهَا} أي: وُعظوا {خرُّوا سجداً} لله سبحانه خوفاً منه {وسبحوا بحمد ربهم} نزَّهوا الله تعالى بالحمد لله {وهم لا يستكبرون} عن الإيمان به والسُّجود له',NULL,'853',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3520,'{تتجافى جنوبهم} ترتفع أضلاعهم {عن المضاجع} الفرش ومواضع النَّوم {يدعون ربهم خوفاً} من النَّار {وطمعاً} في الجنَّة {وَمِمَّا رزقناهم ينفقون} يصَّدَّقون',NULL,'854',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3521,'{فلا تعلم نفس} من هؤلاء {ما أخفي لهم} ما أعد لهم {من قرة أعين} ممَّا تقرُّ به عينه إذ رآه',NULL,'854',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3522,'{أَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًا} نزلت في أمير المؤمنين عليّ بن أبي طالب رضي الله عنه والوليد بن عقبة بن أبي معيط',NULL,'854',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3523,'{أَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ جَنَّاتُ الْمَأْوَى نُزُلا بِمَا كَانُوا يَعْمَلُونَ}',NULL,'854',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3524,'{وَأَمَّا الَّذِينَ فَسَقُوا فَمَأْوَاهُمُ النَّارُ كُلَّمَا أَرَادُوا أَنْ يَخْرُجُوا مِنْهَا أُعِيدُوا فِيهَا وَقِيلَ لَهُمْ ذُوقُوا عَذَابَ النَّارِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ}',NULL,'854',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3525,'{ولنذيقنهم من العذاب الأدنى} قيل: المصيبات في الدُّنيا وقيل: القتل ببدر وقيل: عذاب القبر وقيل: الجوع سبع سنين والأولى المُصيبات والجوع لقوله: {لعلهم يرجعون} وقوله:',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3526,'{وَمَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآيَاتِ رَبِّهِ ثُمَّ أعرض عنها إنا من المجرمين منتقمون}',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3527,'{فلا تكن في مرية من لقائه} أَيْ: من لقاء موسى عليه السَّلام ليلة المعراج وعده الله تعالى أن يريه موسى عليه السَّلام ليلة الإِسراء به',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3528,'{وجعلنا منهم} من بني إسرائيل {إئمة} قادة {يهدون} يدعون الخلق {بأمرنا لما صبروا} حين صبروا على الحقِّ',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3529,'{إنَّ ربك هو يفصل} يحكم {بينهم يوم القيامة} بين المُكذِّبين بك {فيما كانوا فيه يختلفون} من أمرك',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3530,'{أولم يهد لهم} يتبيَّن لهم صدقك {كم أهلكنا} إهلاكنا مَنْ كذَّب الرُّسل منهم وهم {يمشون في مساكنهم} إذا سافروا فيرون خرابَ منازلهم {إنَّ في ذلك لآيات أفلا يسمعون} آيات الله وعظاته',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3531,'{أَوَلَمْ يَرَوْا أَنَّا نَسُوقُ الْمَاءَ إِلَى الأَرْضِ الجرز} الغليظة التي لا نبأت فيها {فَنُخْرِجُ بِهِ زَرْعًا تَأْكُلُ مِنْهُ أَنْعَامُهُمْ وَأَنْفُسُهُمْ أفلا يبصرون} هذا فيلعموا أنَّا نقدر على إعادتهم',NULL,'855',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3532,'{وَيَقُولُونَ مَتَى هَذَا الْفَتْحُ إِنْ كُنْتُمْ صَادِقِينَ} وذلك أنَّ المؤمنين قالوا للكفَّار: إنَّ لنا يوماً يحكم الله بيننا وبينكم فيه يريدون يوم القيامة فقالوا: متى هذا الفتح؟ فقال الله تعالى: {قُلْ يَوْمَ الْفَتْحِ لا يَنْفَعُ الَّذِينَ كَفَرُوا إيمانهم ولا هم ينظرون} يمهلون للتوبة',NULL,'856',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3533,'{قُلْ يَوْمَ الْفَتْحِ لا يَنْفَعُ الَّذِينَ كَفَرُوا إيمانهم ولا هم ينظرون}',NULL,'856',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3534,'{فأعرض عنهم} منسوخٌ بآية السَّيف {وانتظر} عذابهم {إنهم منتظرون} هلاكك في زعمهم الكاذب',NULL,'856',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3535,'{يا أيها النَّبِيُّ اتَّقِ اللَّهَ} اثبت على تقوى الله ودُمْ عليه {ولا تطع الكافرين والمنافقين} وذلك أنَّ الكافرين قالوا له: ارفض ذكر آلهتنا وقل: إنَّ لها شفاعةً ومنفعةً لمن عبدها ووازَرَهم المنافقون على ذلك {إِنَّ اللَّهَ كَانَ عَلِيمًا} بما يكون قبل كونه {حكيماً} فيما يخلق',NULL,'857',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3536,'{وَاتَّبِعْ مَا يُوحَى إِلَيْكَ مِنْ رَبِّكَ إِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا}',NULL,'857',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3537,'{وتوكل على الله وكفى بالله وكيلا}',NULL,'857',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3538,'{مَا جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جوفه} هذا تكذيبٌ لبعض مَنْ قال من الكافرين: إنَّ لي قلبين أفهم بكلِّ واحدٍ منهما أكثر ممَّا يفهم محمد فأكذبه الله تعالى قيل: إنَّه ابن خطل {وَمَا جَعَلَ أَزْوَاجَكُمُ اللَّائِي تُظَاهِرُونَ مِنْهُنَّ أُمَّهَاتِكُمْ} لم يجعل نسائكم اللائي تقولون: هنَّ علينا كظهور أمهاتنا في الحرام كما تقولون وكان هذا من طلاق الجاهليَّة فجعل الله في ذلك كفَّارة {وما جعل أدعياءكم} مَنْ تبنَّيتموه {أبناءكم} في الحقيقة كما تقولون {ذلكم قولكم بأفواهكم} قولٌ بالفم لا حقيقة له {وَاللَّهُ يَقُولُ الحق} وهو أنَّ غير الابن لا يكون ابناً {وهو يهدي السبيل} أَيْ: السَّبيل المستقيم',NULL,'857',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3539,'{ادعوهم لآبائهم} أَيْ: انسبوهم إلى الذين ولدوهم {هو أقسط عند الله} أعدل عند الله {فإن لم تعلموا آباءهم} مَنْ هم {فَإِخْوَانُكُمْ فِي الدِّينِ} أي: فهم إخوانكم في الدين {ومواليكم} وبنو عمّكم وقيل: أولياؤكم في الدِّين {وَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيمَا أَخْطَأْتُمْ به} وهو أن يقول لغير ابنه: يا بنيَّ من غير تَعَمُّدٍ أن يجريه مجرى الولد في الميراث وهو قوله: {ولكن ما تعمَّدت قلوبكم} يعني: ولكنَّ الجُناح في الذي تعمَّدت قلوبكم',NULL,'858',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3540,'{النبيُّ أولى بالمؤمنين من أنفسهم} إذا دعاهم النبيُّ صلى الله عليه وسلم إلى شيءٍ ودعتهم أنفسهم إلى شيءٍ كانت طاعة النبي صلى الله عليه وسلم أولى {وأزواجه أمهاتهم} في حرمة نكاحهن عليهم {وأولو الأرحام} والأقارب {بعضهم أولى ببعض} في الميراث {في كتاب الله} في حكمه {من المؤمنين والمهاجرين} وذلك أنَّهم كانوا في ابتداء الإِسلام يرثون بالإِيمان والهجرة {إلا أن تفعلوا إلى أوليائكم معروفاً} لكن إن يوصوا بهم بشيءٍ من الثُّلث فهو جائزٌ {كان ذلك في الكتاب مسطوراً} كان هذا الحكم في اللَّوح المحفوظ مكتوبا',NULL,'858',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3541,'{وإذ أخذنا} واذكر إذ أخذنا {من النبيّين ميثاقهم} على الوفاء بما حملوا وأن يُصدِّق بعضهم بعضاً',NULL,'859',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3542,'{ليسأل الصادقين عن صدقهم} المُبلِّغين من الرُّسل عن تبليغهم وفي تلك المسألة تبكيتٌ للكفَّار {وأعدَّ للكافرين} بالرُّسل {عذاباً أليماً}',NULL,'859',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3543,'{يا أيها الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جاءتكم جنود} يعني: الأحزاب وهم قريش وغطفان قريظة والنَّضير حاصروا المسلمين أيَّام الخندق {فأرسلنا عليهم ريحاً} وهي الصَّبا كفأت قدورهم وقلعت فساطيطهم {وجنوداً لم تروها} وهم الملائكة {وكان الله بما يعملون} من حفر الخندق {بصيراً}',NULL,'859',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3544,'{إذ جاءوكم من فوقكم} من قبل المشرق يعني: قُريظة والنَّضير {ومن أسفل منكم} قريشٌ من ناحية مكَّة {وإذ زاغت الأبصار} مالت وشخصت وتحيَّرت لشدَّة الأمر وصعوبته عليكم {وبلغت القلوب الحناجر} ارتفعت إلى الحلوق لشدَّة الخوف {وتظنون بالله الظنونا} ظنَّ المنافقون أنَّ محمدا صلى الله عليه وسلم وأصحابه يُستأصلون وأيقن المؤمنون بنصر اللَّهِ',NULL,'859',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3545,'{هنالك} في تلك الحال {ابتلي المؤمنون} اختبروا ليتبيَّن المخلص من المنافق {وزلزلوا} وحرِّكوا وخُوِّفوا',NULL,'860',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3546,'{وَإِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ} شك ونفاقٌ: {ما وعدنا الله ورسوله إلاَّ غرورا} إذْ وعدنا أنَّ فارس والرُّوم يُفتحان علينا',NULL,'860',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3547,'{وإذ قالت طائفة منهم} من المنافقين: {يا أهل يثرب} يعني: المدينة {لا مُقَامَ لَكُمْ} لا مكان لكم تُقيمون فيه {فارجعوا} إلى منازلكم بالمدينة أمروهم بترك رسول الله صلى الله عليه وسلم وخذلانه وذلك أنَّ النبيَّ صلى الله كان قد خرج من المدينة إلى سلع لقتال القوم {ويستأذن فريقٌ منهم} من المنافقين {النبيَّ} في الرُّجوع إلى منازلهم {يقولون: إنَّ بيوتنا عورة} ليست بحصينةٍ نخاف عليها العدوِّ قال الله تعالى: {وَمَا هِيَ بِعَوْرَةٍ إِنْ يُرِيدُونَ إِلا فراراً} من القتال',NULL,'860',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3548,'{ولو دخلت عليهم} لو دخل عليهم هؤلاء الذين يريدون قتالهم المدينة {من أقطارها} جوانبها {ثمَّ سئلوا الفتنة} سألتهم الشِّرك بالله {لأتوها} لأعطوا مرادهم {وما تلبثوا بها إلاَّ يسيراً} وما احتبسوا عن الشِّرك إلا يسيراً أَيْ لأسرعوا الإجابة إليه',NULL,'860',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3549,'{ولقد كانوا عاهدوا الله من قبل} عاهدوا رسول الله صلى الله عليه وسلم قبل غزوة الخندق {لا يولون الأدبار} لا ينهزمون عن العدوِّ {وكان عهد الله مسؤولاً} والله تعالى يسألهم عن ذلك العهد يوم القيامة',NULL,'860',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3550,'{قُلْ لَنْ يَنْفَعَكُمُ الْفِرَارُ إِنْ فَرَرْتُمْ مِنَ الموت أو القتل} الذي كُتب عليكم {وإذاً لا تمتعون إلاَّ قليلاً} لا تبقون في الدُّنيا إلاَّ إلى آجالكم',NULL,'861',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3551,'{قُلْ مَنْ ذَا الَّذِي يَعْصِمُكُمْ مِنَ اللَّهِ إِنْ أَرَادَ بِكُمْ سُوءًا أَوْ أَرَادَ بِكُمْ رَحْمَةً وَلا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا ولا نصيرا}',NULL,'861',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3552,'{قد يعلم الله المعوقين منكم} الذين يُعوِّقون النَّاس عن نصرة محمَّد عليه السَّلام {والقائلين لإِخوانهم هلمَّ إلينا} يقولون لهم: خلُّوا محمدا صلى الله عليه وسلم فإنَّه مغرورٌ وتعالوا إلينا {ولا يأتون البأس إلاَّ قليلاً} لا يحضرون الحرب مع أصحاب النبي صلى الله عليه وسلم إلاّ تعذيراً وتقصيراً يرى أنَّ له عذراً ولا عذر له يوهمونهم أنَّهم معهم',NULL,'861',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3553,'{أشحة عليكم} بخلاء عليكم بالخير والنَّفقة {فَإِذَا جَاءَ الْخَوْفُ رَأَيْتَهُمْ يَنْظُرُونَ إِلَيْكَ تَدُورُ أعينهم} في رؤوسهم من الخوف كدوران عين الذي {يُغشى عليه من الموت} قَرُبَ أن يموت فانقلبت عيناه {فإذا ذهب الخوف سلقوكم بألسنة حداد} آذوكم بالكلام وجادلوكم في الغنيمة {أشحة} بخلاء {على الخير} الغنيمة',NULL,'861',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3554,'{يحسبون الأحزاب لم يذهبوا} لجبنهم وشدَّة خوفهم يظنون أنَّهم بعد انهزامهم لم ينصرفوا بعد {وإن يأت الأحزاب} يرجعوا كرَّةً ثانية {يودوا لو أنَّهم بادون في الأعراب} خارجون من المدينة إلى البادية في الأعراب {يسألون عن أنبائكم} أَيْ: يودوا لو أنَّهم غائبون عنكم يسمعون أخباركم بسؤالهم عنها من غير مشاهدة قال الله تعالى: {وَلَوْ كَانُوا فِيكُمْ مَا قَاتَلُوا إِلا قَلِيلا} رياءً من غير حِسْبَةٍ ولمَّا وصف الله تعالى حال المنافقين في الحرب وصف حال المؤمنين فقال:',NULL,'861',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3555,'{لقد كان لكم} أيُّها المؤمنون {في رسول الله أسوة حسنة} سنَّةٌ صالحةٌ واقتداءٌ حسنٌ حيث لم يخذلوه ولم يتولَّوا عنه كما فعل هو صلى الله عليه وسلم يوم أُحدٍ شُجَّ حاجبه وكسرت رباعيته فوقف صلى الله عليه وسلم ولم يمهزم ثمَّ بيَّن لمَنْ كان هذا الاقتداء برسول الله صلى الله عليه وسلم فقال: {لمن كان يرجو الله واليوم الآخر} أَيْ: يخافهما',NULL,'862',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3556,'{ولما رأى المؤمنون الأحزاب قالوا} تصديقاً لوعد الله تعالى: {هَذَا مَا وَعَدَنَا اللَّهُ وَرَسُولُهُ وَصَدَقَ اللَّهُ ورسوله} ووعدُ الله تعالى إيَّاهم في قوله: {أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَثَلُ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ مَسَّتْهُمُ الْبَأْسَاءُ وَالضَّرَّاءُ وَزُلْزِلُوا حَتَّى يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ: مَتَى نَصْرُ اللَّهِ؟ أَلا إِنَّ نَصْرَ الله قريبٌ} فعلموا بهذه الآية أنَّهم يُبتلون فلمَّا ابتلوا بالأحزاب علموا أنَّ الجنَّة والنَّصر قد وجبا لهم إن سلَّموا وصبروا وذلك قوله: {وما زادهم إلاَّ إيماناً} وتصديقاً بالله ورسوله {وتسليماً} لله أمره',NULL,'862',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3557,'{مِنَ الْمُؤْمِنِينَ رِجَالٌ صَدَقُوا مَا عَاهَدُوا اللَّهَ} كانوا صادقين في عهودهم بنصرة النبي صلى الله عليه وسلم {فمنهم من قضى نحبه} فرغٍ من نذره واستُشهد يعني: الذين قُتلوا بأُحدٍ {ومنهم مَنْ ينتظر} أن يقتل شهيداً {وما بدلوا تبديلاً} عهدهم ثمَّ ذكر جزاء الفريقين فقال:',NULL,'862',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3558,'{ليجزي الله الصادقين بصدقهم} الآية',NULL,'863',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3559,'{وردَّ الله الذين كفروا} قريشاً والأحزاب {بغيظهم} على ما فيهم من الغيظ {لم ينالوا خيراً} لم يظفروا بالمسلمين {وكفى الله المؤمنين القتال} بالرِّيح والملائكة',NULL,'863',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3560,'{وأنزل الذين ظاهروهم من أهل الكتاب} الذين عاونوا الأحزاب من يقظة {من صياصيهم} حصونهم وذلك أن النبي صلى الله عليه وسلم حاصرهم واشتدَّ ذلك عليهم حتى نزلوا على حكمه وذلك قوله تعالى: {وقذف في قلوبهم الرعب فريقاً تقتلون} يعني: الرِّجال {وتأسرون فريقاً} يعني: النِّساء والذُّريَّة وقوله:',NULL,'863',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3561,'{وأرضا لم تطئوها} يعني: خيبر ولم يكونوا نالوها فوعدهم الله تعالى إيَّاها',NULL,'863',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3562,'{يأ أيها النبي قل لأزواجك} الآية نزلت حين سألت نساء رسول الله صلى الله عليه وسلم شيئاً من عرض الدُّنيا وآذينه بزيارة النَّفقة فأنزل الله سبحانه هذه الآيات وأمره أن يخبرهن بين الإِقامة معه على طلب ما عند الله أو السِّراح إن أردْنَ الدُّنيا وهو قوله: {إِنْ كُنْتُنَّ تُرِدْنَ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أمتعكنَّ} متعة الطَّلاق فقرأ عليهنَّ رسول الله صلى الله عليه وسلم هذه الآيات فاخترن الآخرة على الدُّنيا والجنَّة على الزِّينة فرفع الله سبحانه درجتهنَّ على سائر النِّساء بقوله:',NULL,'864',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3563,'{وَإِنْ كُنْتُنَّ تُرِدْنَ اللَّهَ وَرَسُولَهُ وَالدَّارَ الآخِرَةَ فَإِنَّ اللَّهَ أَعَدَّ لِلْمُحْسِنَاتِ مِنْكُنَّ أَجْرًا عَظِيمًا}',NULL,'864',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3564,'{يَا نِسَاءَ النَّبِيِّ مَنْ يَأْتِ مِنْكُنَّ بِفَاحِشَةٍ مبيِّنة} بمعصيةٍ ظاهرةٍ {يضاعف لها العذاب ضعفين} ضعفي عذاب غيرها من النِّساء',NULL,'864',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3565,'{ومن يقنت} يطع {نؤتها أجرها مرَّتين} مثلي ثواب غيرها من النِّساء {وأَعتدنا لها رزقاً كريماً} يعني: الجنَّة وقوله:',NULL,'864',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3566,'{فَلا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِي فِي قَلْبِهِ مرض} أَيْ: لا تقلن قولاَ يجد منافقٌ به سبيلاً إلى أن يطمع في موافقتكنَّ له وقوله: {وقلن قولا معروفا} أي: فلن بما يوجبه الدِّين والإِسلام بغير خضوعٍ فيه بل بتصريحٍ',NULL,'864',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3567,'{وقرن في بيوتكن} أمرٌ لهنَّ من الوقار والقرار جميعاً {ولا تبرجن} ولا تُظهرن المحاسن كما كان يفعله أهل الجاهليَّة وهو ما بين عيسى ومحمد صلوات الله عليهما {إنما يريد الله ليذهب عنكم الرجس} وهو كلُّ مُستَنكرٍ ومُستقذَرٍ من عملٍ {أهل البيت} يعني: نساء النبيِّ صلى الله عليه وسلم ورجال أهل بيته',NULL,'865',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3568,'{وَاذْكُرْنَ مَا يُتْلَى فِي بُيُوتِكُنَّ مِنْ آيَاتِ الله} يعني: القرآن {والحكمة} يعني: السُّنَّة',NULL,'865',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3569,'{إنَّ المسلمين والمسلمات} الآية قالت النِّساء: ذكر الله تعالى الرِّجال بخيرٍ في القرآن ولم يذكر النِّساء بخيرٍ فما فينا خيرٌ يُذكر فأنزل الله تعالى هذه الآية',NULL,'865',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3570,'{وَمَا كَانَ لِمُؤْمِنٍ وَلا مُؤْمِنَةٍ} الآية نزلت في عبد الله بن جحش وأخته زينب خطبها رسول الله صلى الله عليه وسلم على مولاه زيد بن حارثة وظنَّت أنَّه خطبها لنفسه فلمَّا علمت أنًّه يريدها لزيدٍ كرهت ذلك فأنزل الله تعالى: {وما كان لمؤمن} يعني: عبد الله بن جحش {ولا مؤمنة} يعني: أخته زينب {إِذَا قَضَى اللَّهُ وَرَسُولُهُ أَمْرًا أَنْ يَكُونَ لهم الخيرة من أمرهم} أَيْ: الاختيار فأعلم أنَّه لا اختيار على ما قضاه الله ورسوله وزوَّجها من زيدٍ ومكثت عنده حيناً ثمَّ إن رسول الله صلى الله عليه وسلم أتى زيداً ذات يومٍ لحاجة فأبصرها قائمةً في درع وخمار فأعجبه وكأنَّها وقعت في نفسه وقال: سبحان الله مُقلِّب القلوب فلمَّا جاء زيدٌ أخبرته بذلك وأُلقي في نفس زيدٍ كراهتها فأراد فراقها فأتى رسول الله صلى الله عليه وسلم فقال: إني أريد أن أفارق صاحبتي فإنَّها تؤذيني بلسانها فذلك قوله:',NULL,'865',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3571,'{وإذ تقول للذي أنعم الله عليه} بالإسلام يعني: زيداً {وأنعمت عليه} بالإِعتاق: {أمسك عليك زوجك واتق الله} فيها وكان صلى الله عليه وسلم يحبُّ أن يتزوَّج بها إلا أنَّه آثر ما يجب من الأمر بالمعروف وقوله: {وتخفي في نفسك ما الله مبديه} أن لو فارقها تزوجها وذلك أن الله تعالى كان قضى ذلك وأعلمه أنَّها ستكون من أزواجه وأن زيداً يُطلِّقها {وتخشى الناس} تكره قالة النَّاس لو قلت: طَلِّقْها فيقال أمر رجلاً بطلاق امرأته ثمَّ تزوَّجها {والله أحقُّ أن تخشاه} في كلِّ الأحوال ليس أنَّه لم يَخْشَ الله في شيءٍ من هذه القضيَّة ولكن ذكر الكلام ها هنا على الجملة وقيل والله أحقُّ أن تستحيي منه فلا تأمر زيداً بإمساك زوجته بعد إعلام الله سبحانه إياك أنها ستكون زوجتك وأنت تستحيي من النَّاس وتقول: أمسك عليك زوجك {فلما قضى زيد منها وطراً} حاجته من نكاحها {زَوَّجْنَاكَهَا لِكَيْ لا يَكُونَ عَلَى الْمُؤْمِنِينَ حَرَجٌ} الآية لكيلا يظنَّ ظانٌّ أنَّ امرأة المتبنَّى لا تحلُّ للمتبنِّي وكانت العرب تظنُّ ذلك وقوله: {وكان أمر الله مفعولاً} كائناً لا محالة وكان قد قضى في زينب أن يتزوَّجها رسول الله صلى الله عليه وسلم',NULL,'866',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3572,'{مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيمَا فرض الله له} فيما أحلَّ له من النِّساء {سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ} يقول: هذه السُّنَّة قد مضت أيضاً لغيرك يعني: كثرة أزواج داود وسليمان عليهما السَّلام والمعنى: سنَّ الله له سنَّةٌ واسعةً لا حرج عليه فيها {وَكَانَ أَمْرُ اللَّهِ قَدَرًا مَقْدُورًا} قضاءً مقضياً',NULL,'867',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3573,'{الذين يبلغون رسالات الله} الذين نعت قوله: {في الذين خلوا من قبل} {ويخشونه وَلا يَخْشَوْنَ أَحَدًا إِلا اللَّهَ} لا يخشون قالة النَّاس ولائمتهم فيما أحلَّ الله لهم {وكفى بالله حسيباً} حافظاً لأعمال خلقه',NULL,'867',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3574,'{مَا كَانَ مُحَمَّدٌ أَبَا أَحَدٍ مِنْ رِجَالِكُمْ} فتقولوا: إنَّه تزوجَّ امرأة ابنه يعني: زيداً ليس له بابنٍ وإن كان قد تبنَّاه {ولكن} كان {رسول الله وخاتم النبيين} لا نبيَّ بعده',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3575,'{يا أيها الذين آمنوا اذكروا الله ذكراً كثيراً} وهو أن لا يُنسى على حالٍ',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3576,'{وسبحوه} صلُّوا له {بكرة} صلاة الفجر {وأصيلاً} صلاة العصر والعشاءين',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3577,'{هو الذي يصلي عليكم} يغفر لكم ويرحمكم {وملائكته} يستغفرون لكم {لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ} من ظلمات الجهل والكفر إلى نور اليقين والإسلام',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3578,'{تحيتهم} تحيَّةُ الله للمؤمنين {يوم يلقونه} يرونه {سلام} يسلِّم عليهم {وأعدَّ لهم أجراً كريماً} وهو الجنَّة',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3579,'{يا أيها النبي إنا أرسلناك شاهداً} على أُمَّتك بإبلاغ الرِّسالة',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3580,'{وداعياً إلى الله} إلى ما يُقرب منه من الطَّاعة والتَّوحيد {بإذنه} بأمره أَيْ: إنَّه أمرك بهذا لا أنَّك تفعله من قبلك {وسراجاً منيراً} يُستضاء به من ظلمات الكفر وقوله:',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3581,'{وَبَشِّرِ الْمُؤْمِنِينَ بِأَنَّ لَهُمْ مِنَ اللَّهِ فَضْلا كبيرا}',NULL,'868',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3582,'{ودع أذاهم} لا تُجازهم عليه إلى أن تُؤمر فيهم بأمرنا',NULL,'869',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3583,'{يا أيها الذين آمنوا إذا نكحتم المؤمنات} تزوجتموهنَّ {ثمَّ طلقتموهنَّ من قبل أن تمسوهن} تجامعوهن {فما لكم عليهن من عدَّة تعتدونها} تحصونها عليهن بالأفراد والأشهر لأنَّ المُطلَّقة قبل الجماع لا عدَّة عليها {فمتعوهنَّ} أعطوهنَّ ما يستمتعن به وهذا أمر ندب لأنَّ الواجب لها نصف الصَّداق {وسرحوهن سَراحاً جميلاً} بالمعروف كما أمر الله تعالى ثمَّ ذكر ما يحلُّ من النِّساء للنبيِّ صلى الله عليه وسلم فقال:',NULL,'869',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3584,'{يا أيها النَّبِيُّ إِنَّا أَحْلَلْنَا لَكَ أَزْوَاجَكَ اللَّاتِي آتَيْتَ أجورهن} مهورهنَّ {وما ملكت يمينك} من الإِماء {ممَّا أفاء الله عليك} جعلهنَّ غنيمة تُسبى وتُسترقُّ بحكم الشَّرع {وبنات عمك وبنات عماتك} أن يتزوجهنَّ يعني: نساء بني عبد المطلب {وبنات خالك وبنات خالاتك} يعني: نساء بني زُهرة {اللَّاتِي هَاجَرْنَ مَعَكَ} فمن لمن يهاجر منهنَّ لم يحلَّ له نكاحها {وامرأة} وأحللنا لك امرأةً {مُؤْمِنَةً إِنْ وَهَبَتْ نَفْسَهَا لِلنَّبِيِّ إِنْ أَرَادَ النبيُّ أن يستنكحها} فله ذلك {خالصة لك من دون المؤمنين} فليس لغير النبي صلى الله عليه وسلم أن يستبيح وطء امرأةٍ بلفظ الهبة من غير ولي ولا مهر ولا شاهدٍ {قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِي أَزْوَاجِهِمْ} وهو أن لا نكاح إلاَّ بوليٍّ وشاهدين {وما ملكت أيمانهم} يريد أنَّه لا يحلُّ لغير النبي صلى الله عليه وسلم إلا أربع ولي وشاهدين وإلا ملك اليمين والنبي صلى الله عليه وسلم يحلُّ له ما ذكر في هذه الآية {لكيلا يكون عليك حرج} في النِّكاح',NULL,'869',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3585,'{ترجي من تشاء منهن} تُؤخِّر {وتؤوي} وتضمُّ {إليك مَنْ تشاء} أباح الله سبحانه له أن يترك القسمة والتَّسوية بين أزواجه حتى إنَّه ليؤخِّر مَنْ شاء منهنَّ عن وقت نوبتها ويطأ مَنْ يشاء من غير نوبتها ويكون الاختيار في ذلك إليه يفعل فيه ما يشاء وهذا من خصائصه {ومن ابتغيت} طلبتَ وأردتَ إصابتها {ممن عزلت} هجرتَ وأخَّرت نوبتها {فلا جناح عليك} في ذلك كلِّه {ذلك أدنى أن تقرَّ أعينهنَّ} الآية إذا كانت هذه الرُّخصة مُنزَّلة من الله سبحانه عليك كان أقرب إلى أن {وَيَرْضَيْنَ بِمَا آتَيْتَهُنَّ كُلُّهُنَّ وَاللَّهُ يَعْلَمُ مَا فِي قُلُوبِكُمْ} من أمر النِّساء والميل إلى بعضهنَّ ولمَّا خيَّر النبي صلى الله عليه وسلم نساءه فاخترنه ورضين به قصره الله سبحانه عليهنَّ وحرَّم عليه طلاقهنَّ والتَّزوُّج بسواهنَّ وجعلهنَّ أُمَّهات المؤمنين وهو قوله:',NULL,'870',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3586,'{لا يحلُّ لك النساء من بعد} أَيْ: من بعد هؤلاء التِّسع {وَلا أَنْ تَبَدَّلَ بِهِنَّ مِنْ أَزْوَاجٍ وَلَوْ أعجبك حسنهنَّ} ليس لك أن تطلِّق واحدةً من هؤلاء ولا تتزوَّج بدلها أخرى أعجبتك بجمالها {إلاَّ ما ملكت يمينك} من الإِماء فإنهنَّ حلالٌ لك',NULL,'871',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3587,'{يا أَيُّها الذين آمنوا لا تدخلوا بيوت النبي} الآية نزلت في ناسٍ من المؤمنين كانوا يتحينون طعام النبي صلى الله عليه وسلم فيدخلون عليه قبل الطَّعام إلى أن يدرك ثمَّ يأكلون ولا يخرجون فكان النبي صلى الله عليه وسلم يتأذَّى بهم وهو قوله: {غير ناظرين إناه} أيْ: منتظرين إدراكه {ولا مُسْتأنِسِين لحديث} طالبين الأنس {وَاللَّهُ لا يَسْتَحْيِي مِنَ الْحَقِّ} لا يترك تأديبكم وحملكم على الحقِّ {وإذا سألتموهنَّ متاعاً فاسألوهنَّ من وراء حجاب} إذا أردتم أن تخاطبوا أزواج النبي صلى الله عليه وسلم في أمرٍ فخاطبوهنَّ من وراء حجابٍ وكانت النِّساء قبل نزول هذه الآية يبرزن للرِّجال فلمَّا نزلت هذه الآية ضرب عليهنَّ الحجاب فكانت هذا آية الحجاب بينهنَّ وبين الرِّجال {ذلكم} أَيْ: الحجاب {أطهر لقلوبكم وقلوبهن} فإنَّ كلَّ واحدٍ من الرَّجل والمرأة إذا لم ير الآخر لم يقع في قبله {وَمَا كَانَ لَكُمْ أَنْ تُؤْذُوا رَسُولَ اللَّهِ} أَيْ: ما كان لكم أذاه في شيءٍ من الأشياء {وَلا أن تنكحوا أزواجه من بعده أبداً} وذلك أنَّ رَجُلًا مِنْ أَصْحَابِ النَّبِيّ صَلَّى اللَّهُ عَلَيْهِ قال: لئن قبض رسول الله صلى عليه وسلم لأنكحن عائشة رضي الله عنها وعن أبيها فأعلم الله سبحانه أنَّ ذلك محرَّمٌ بقوله: {إِنَّ ذَلِكُمْ كَانَ عند الله عظيماً} أَيْ: ذنباً عظيماً',NULL,'871',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3588,'{إن تبدوا شيئاً أو تخفوه} الآية نزلت في هذا الرَّجل الذي قال: لأنكحنَّ عائشة أخبر الله أنَّه عالمٌ بما يُظهر ويُكتم فلمَّا نزلت آية الحجاب قالت الآباء والأنباء لرسول الله صلى عليه وسلم: ونحن أيضاً نُكلِّمهنَّ من وراء الحجاب؟ فأنزل الله سبحانه:',NULL,'872',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3589,'{لا جُنَاحَ عَلَيْهِنَّ فِي آبَائِهِنَّ وَلا أَبْنَائِهِنَّ وَلا إِخْوَانِهِنَّ وَلا أَبْنَاءِ إِخْوَانِهِنَّ وَلا أَبْنَاءِ أَخَوَاتِهِنَّ وَلا نِسَائِهِنَّ وَلا مَا مَلَكَتْ أَيْمَانُهُنَّ} أَيْ: في ترك الاحتجاب من هؤلاء',NULL,'872',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3590,'{إنَّ الله وملائكته يصلُّون على النبيِّ} الله تعالى يثني على النبيِّ ويرحمه والملائكة يدعون له {يا أيها الذين آمنوا صلوا عليه وسلموا تسليماً} قولوا: اللهم صلى على محمدٍ وسلِّمْ',NULL,'873',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3591,'{إن الذين يؤذون الله ورسوله} يعني: اليهود والنَّصارى والمشركين في قولهم: {يد الله مغلولةٌ} و {إنَّ الله فقيرٌ} و {المسيحُ ابنُ الله} والملائكة بنات الله وشجُّوا وجه رسول الله صلى الله عليه وسلم وقالوا له: ساحرٌ وشاعرٌ',NULL,'873',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3592,'{وَالَّذِينَ يُؤْذُونَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا} يرمونهم بغير ما عملوا',NULL,'873',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3593,'{يأ أيها النبي قل لأزواجك} الآية كان قومٌ من الزُّناة يتَّبعون النِّساء إذا خرجن ليلاً ولم يكونوا يطلبون إلاَّ الإِماء ولم يكن يومئذ تُعرفْ الحرَّة من الأمة لأنَّ زِيَّهُنَّ كان واحداً إنما يخرجون في درع وخمار فنهيى الله سبحانه الحرائر أن يتشبَّهنَّ بالإماء وأنزل قوله تعالى: {يدنين عليهنَّ من جلابيبهنَّ} أَيْ: يرخين أرديتهنَّ وملاحفهنَّ ليعلم أنهنَّ حرائر فلا يتعرض لهنَّ وهو قوله: {ذَلِكَ أَدْنَى أَنْ يُعْرَفْنَ فَلا يؤذين وكان الله غفوراً} لما سلف من ترك السِّتر {رحيماً} بهنَّ إذ يسترهنَّ',NULL,'873',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3594,'{لَئِنْ لَمْ يَنْتَهِ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِمْ مرض} يعني: الزُّناة {والمرجفون في المدينة} الذين يوقعون أخبار السَّرايا بأنهم هُزموا بالكذب والباطل {لنغرينَّك بهم} لنسلطنَّك عليهم {ثم لا يجاورونك فيها} لا يساكنونك في المدينة {إلاَّ قليلاً} حتى خرجوا منها',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3595,'{ملعونين} مطرودين {أينما ثقفوا} وُجدوا {أُخِذُوا وَقُتِّلُوا تَقْتِيلا}',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3596,'{سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ} سنَّ الله في الذين ينافقون الأنبياء ويرجفون بهم أن يُقتلوا حيث ما ثقفوا وقوله:',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3597,'{يَسْأَلُكَ النَّاسُ عَنِ السَّاعَةِ قُلْ إِنَّمَا عِلْمُهَا عِنْدَ اللَّهِ وَمَا يُدْرِيكَ لَعَلَّ السَّاعَةَ تَكُونُ قريبا}',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3598,'{إِنَّ اللَّهَ لَعَنَ الْكَافِرِينَ وَأَعَدَّ لَهُمْ سَعِيرًا}',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3599,'{خَالِدِينَ فِيهَا أَبَدًا لا يَجِدُونَ وَلِيًّا وَلا نصيرا}',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3600,'{يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يَا ليتنا أطعنا الله وأطعنا الرسولا}',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3601,'{إنا أطعنا سادتنا} أَيْ: قادتنا ورؤساءنا في الشِّرك والضَّلالة',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3602,'{ربنا آتهم ضعفين من العذاب} مثلي عذابنا',NULL,'874',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3603,'{يا أيها الَّذِينَ آمَنُوا لا تَكُونُوا كَالَّذِينَ آذَوْا مُوسَى} لا تؤذوا نبيَّكم كما آذَوا هم موسى عليه السَّلام وذلك أنَّهم رموه بالبرص والأدرة حتى برَّأه الله مما رموه به بآيةٍ معجزةٍ {وكان عند الله وجيهاً} ذا جاهٍ ومنزلةٍ وقوله:',NULL,'875',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3604,'{وقولوا قولاً سديداً} أَيْ: حقَّاً وصواباً قيل: هو لا إله إلا الله',NULL,'875',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3605,'{يُصْلِحْ لَكُمْ أَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ فَقَدْ فَازَ فَوْزًا عَظِيمًا}',NULL,'875',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3606,'{إنا عرضنا الأمانة} الفرائض التي افترض الله سبحانه على العباد وشرط عليهم أنَّ مَنْ أدَّاها جُوزي بالإِحسان ومَنْ خان فيها عوقب {على السماوات والأرض والجبال} أفهمهنَّ الله سبحانه خطابه وأنطقهنَّ {فأبين أن يحملْنَها} مخافةً وخشيةً لا معصيةً ومخالفةً وهو قوله: {وأشفقن منها} أَيْ: خشين منها {وحملها الإِنسان} آدم عليه السَّلام {إنَّه كان ظلوماً} لنفسه {جهولاً} غِرَّاً بأمر الله سبحانه وما احتمل من الأمانة ثمَّ بيَّن أنَّ حمل آدم عليه السَّلام هذه الأمانة كان سبباً لتعذيب المنافقين والمشركين في قوله:',NULL,'875',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3607,'{لِيُعَذِّبَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَاتِ وَيَتُوبَ الله على المؤمنين والمؤمنات} يعني: إذا خانوا في الأمانة بمعصية أمر الله سبحانه تاب عليهم بفضله {وكان الله غفوراً رحيماً}',NULL,'876',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3608,'{الحمد لله} على جهة التَّعظيم {الذي له ما في السماوات وَمَا فِي الأَرْضِ وَلَهُ الْحَمْدُ فِي الآخِرَةِ} لأنَّ أهل الجنَّة يحمدونه',NULL,'877',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3609,'{يعلم ما يلج في الأرض} يدخل فيها من الماء والأموات {وما يخرج منها} من النَّبات {وما ينزل من السماء} من الأمطار {وما يعرج} يصعد {فيها} من الملائكة',NULL,'877',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3610,'{وقال الذين كفروا} يعني: منكري البعث: {لا تأتينا الساعة} أَيْ: لا نبعث {قل} لهم يا محمد: {بلى وربي لتأتينكم عالمِ الغيب} بالخفض من نعت قوله: {وربي} وبالرَّفع على معنى: هو عالم الغيب وقوله: {لا يعزب} مفسَّرٌ في سورة يونس وقوله:',NULL,'877',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3611,'{ليجزي} يعود إلى قوله: {لتأتينكم} معناه: لتأتينَّكم السَّاعة {ليجزي الذين آمنوا} الآية',NULL,'878',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3612,'{والذين سعوا في آياتنا} مفسَّر في سورة الحج',NULL,'878',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3613,'{ويرى الذين أوتوا العلم} يعني: مؤمني أهل الكتاب {الذي أنزل إليك من ربك} وهو القرآن {هو الحقَّ ويهدي إلى صراط العزيز} القرآن',NULL,'878',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3614,'{وقال الذين كفروا} إنكاراً للبعث وتعجُّباً منه: {هل ندلكم على رجل} وهو محمد صلى الله عليه وسلم {ينبئكم إذا مزقتم كلَّ ممزق} أَيْ: فُرِّقتم وصرتم رُفاتاً {إنكم لفي خلق جديد} أَيْ: تُبعثون',NULL,'878',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3615,'{افترى على الله كذبا} فيما يُخبر به من البعث {أم به جنة} حالةُ جنونٍ قال الله تعالى: {بَلِ الَّذِينَ لا يُؤْمِنُونَ بِالآخِرَةِ فِي الْعَذَابِ والضلال البعيد}',NULL,'878',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3616,'{أَفَلَمْ يَرَوْا إِلَى مَا بَيْنَ أَيْدِيهِمْ وَمَا خلفهم من السماء والأرض} يقول: أما يعلمون أنَّهم حيث ما كانوا فهم يرون ما بين أيديهم من الأرض والسَّماء مثل الذي خلقهم وأنَّهم لا يخرجون منها فكيف يأمنون؟ ! {إِنْ نَشَأْ نَخْسِفْ بِهِمُ الأَرْضَ أَوْ نُسْقِطْ عليهم كسفاً من السماء} عذاباً {إِنَّ فِي ذَلِكَ لآيَةً لِكُلِّ عَبْدٍ مُنِيبٍ} لعلامةً تدلُّ على قدرة الله سبحانه على إحياء الموتى لكلِّ مَنْ أناب إلى الله تعالى وتأمَّل ما خلق الله سبحانه',NULL,'879',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3617,'{ولقد آتينا داود منَّا فضلاً} ثمَّ بيَّن ذلك فقال: {يا جبال} أَيْ: قلنا يا جبال {أوّبي معه} سبِّحي معه {والطير} كان إذا سبَّح جاوبته الجبال بالتَّسبيح وعكفت عليه الطَّير من فوقه تسعده على ذلك {وألنا له الحديد} جعلناه ليِّناً في يده كالطِّين المبلول والعجين وقلنا له:',NULL,'879',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3618,'{أن اعمل سابغات} دروعاً كوامل {وقدِّر في السرد} لا تعجل مسمار الدِّرع دقيقاً فيفلق ولا غيظا فيفصم الحلق اجعله على قدر الحاجة والسَّرْد: نسج الدُّروع {واعملوا} يعني: داود وآله {صالحاً} عملاً صالحاً من طاعة الله تعالى',NULL,'879',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3619,'{ولسليمان الريح} وسخرنا له الرِّيح {غدوها شهر} مسيرها إلى انتصاف النَّهار مسيرة شهر ومن انتصاف النَّهار إلى اللَّيل مسيرة شهر وهو قوله: {ورواحها شهر وَأَسَلْنَا لَهُ عَيْنَ الْقِطْرِ} أذبنا له عين النُّحاس فسالت له كما يسيل الماء {ومن الجنِّ} أَيْ: سخَّرنا له من الْجِنِّ {مَنْ يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِ} بأمر ربه {ومَنْ يزغ} يمل ويعدل {منهم عن أمرنا} الذي أمرناه به من طاعة سليمان {نذقه من عذاب السعير} وذلك أنَّ الله تعالى وكَّل بهم ملكاً بيده سوطٌ من نار فمن زاغ عن أمر سليمان ضربةً أحرقته',NULL,'879',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3620,'{يعملون له ما يشاء من محاريب} مجالس ومساكن ومساجد {وتماثيل} صورة الأنبياء إذ كانت تصوَّر في المساجد ليراها النَّاس ويزدادوا عبادة {وجفانٍ} قصاعٍ كبارٍ {كالجوابِ} كالحياض التي تجمع الماء {وقدور راسيات} ثوابت لا تحرَّكن عن مكانها لعظمها وقلنا: {اعملوا} بطاعة الله يا {آل داود شكراً} له على نعمه',NULL,'880',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3621,'{فلما قضينا عليه الموت ما دلَّهم} الآية كان سليمان عليه السَّلام يقول: اللَّهم عمِّ على الجنِّ موتي ليعلم الإِنس أنَّ الجنَّ لا يعلمون الغيب فمات سليمان عليه السَّلام مُتوكِّئاً على عصاه سنةً ولم تعلم الجنُّ ذلك حتى أكلت الأرضةُ عصاه فسقط ميِّتاً وهو قوله: {مَا دَلَّهُمْ عَلَى مَوْتِهِ إِلا دَابَّةُ الأَرْضِ تأكل منسأته} عصاه {فلما خرَّ} سقط {تبينت الجن} علمت {أَنْ لَوْ كَانُوا يَعْلَمُونَ الْغَيْبَ مَا لَبِثُوا} بعد موت سليمان {في العذاب المهين} فيما سخَّرهم فيه سليمان عليه السَّلام واستعملهم',NULL,'880',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3622,'{لقد كان لسبأ} وهو اسم قبيلةٍ {في مساكنهم} باليمن {آية} دلالةٌ على قدرتنا {جنتان} أَيْ: هي جنَّتان {عن يمين وشمال} بستانٌ يمنةً وبستانٌ يسرةً وقيل لهم: {كلوا من رزق ربكم واشكروا له} على ما أنعم عليكم {بلدة طيبة} أَيْ: بلدتكم بلدةٌ طيِّبةٌ ليست بسبخةٍ {و} الله {ربٌّ غفور} والمعنى: تمتَّعوا ببلدتكم الطَّيِّبة واعبدوا ربَّاً يغفر ذنوبكم',NULL,'881',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3623,'{فأعرضوا} عن أمر الله تعالى بتكذيب الرُّسل {فأرسلنا عليهم سيل العرم} وهو السِّكْر الذي يحبس الماء وكان لهم سِكْرٌ يحبس الماء عن جنَّتيهم فأرسل الله تعالى فيه جرذاناً ثقبته فانبثق الماء عليهم فغرق جنَّاتهم {وبدلناهم بجنَّتيهم جنتين ذواتي أكل خمط} أَيْ: ثمرٍ مُرٍّ {وأثل} وهو الطَّرفاء {وشيء من سدر قليل} وذلك أنَّ الله تعالى أهلك أشجارهم المثمرة وأنبت بدلها الأراك والطرفاء والسدر',NULL,'881',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3624,'{ذَلِكَ جَزَيْنَاهُمْ بِمَا كَفَرُوا} أَيْ: جزيناهم ذلك الجزاء بكفرهم {وهل نجازي إلاَّ الكفور} بسوء عمله وذلك أنَّ المؤمن تُكفَّر عنه سيئاته والكافر يُجازى بكلِّ سوءٍ يعمله',NULL,'882',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3625,'{وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِي بَارَكْنَا فِيهَا} يعني: قرى الشَّام {قرى ظاهرة} متواصلةً يُرى من هذه القرية القرية الأخرى فكانوا يخرجون من سبأ إلى الشَّام فيمرُّون على القرى العامرة {وقدرنا فيها السير} جعلنا سيرَهم بمقدارٍ إذا غدا أحدهم من قريةٍ قال في أخرى وإذا راح من قريةٍ أوى إلى أخرى وقلنا لهم: {سيروا فيها} في تلك القرى {ليالي وأياماً} أَيَّ وقت شئتم من ليلٍ أو نهارٍ {آمنين} لا تخافون عدوَّاً ولا جوعاً ولا عطشاً',NULL,'882',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3626,'{فقالوا ربنا باعد بين أسفارنا} وذلك أنَّهم سئموا الرَّاحة وبطروا النِّعمة فتمنَّوا أن تتباعد قراهم ليبعد سفرهم بينها {وظلموا أنفسهم} بالكفر والبطر {فجعلناهم أحاديث} لمَنْ بعدهم يتحدَّثون بقصَّتهم {ومزَّقناهم كلَّ ممزق} وفرَّقناهم في البلاد فصاروا يُتمثَّل بهم في الفُرقة وذلك أنَّهم ارتحلوا عن أماكنهم وتفرَّقوا في البلاد {إنَّ في ذلك} الذي فعلنا {لآيات لكل صبار شكور} أَيْ: لكلِّ مؤمنٍ لأنَّ المؤمن هو الذي إذا ابتُليَ صبر وإذا أُعطيَ شكر',NULL,'882',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3627,'{ولقد صدَّق عليهم إبليس ظنه} الذي ظن من إغوائهم {فاتبعوه إلاَّ فريقاً من المؤمنين} أَيْ: وجدهم كما ظنَّ بهم إلاَّ المؤمنين',NULL,'883',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3628,'{وما كان له عليهم من سلطان} من حجَّةٍ يستتبعهم بها {إلاَّ لنعلم} المعنى: لكن امتحانهم بإبليس لِنَعْلَمَ {مَنْ يُؤْمِنُ بِالآخِرَةِ مِمَّنْ هُوَ مِنْهَا في شك} عُلِمَ وقوعه منه',NULL,'883',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3629,'{قل} يا محمد لمشركي قومك: {ادعوا الذين زعمتم} أنَّهم آلهةٌ {من دون الله} وهذا أمرُ تهديدٍ ثمَّ وصفهم فقال: {لا يملكون مثقال ذرة في السماوات ولا في الأرض وما لهم فيهما} في السماوات ولا في الأرض {من شرك} شركةٍ {وما له} لله {منهم من ظهير} عونٍ يريد: لم يُعنِ الله على خلق السماوات والأرض آلهتُهم فكيف يكونون شركاء له؟ ثمَّ أبطل قولهم أنَّهم شفعاؤنا عند الله فقال:',NULL,'883',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3630,'{وَلا تَنْفَعُ الشَّفَاعَةُ عِنْدَهُ إِلا لِمَنْ أَذِنَ له} أَيْ: أذن الله له أن يشفع {حتى إذا فزّع} أذهب الفزع {عن قلوبهم} يعني: كشف الفزع عن قلوب المشركين بعد الموت إقامةً للحجَّة عليهم وتقول لهم الملائكة: {ماذا قال ربكم} ؟ فيما أوحى إلى أنبيائه {قالوا الحق} فأقرُّوا حين لا ينفعهم الإِقرار',NULL,'883',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3631,'{قل من يرزقكم من السماوات} المطر {و} من {الأرض} النَّبات ثمَّ أمره أن يخبرهم فقال: {قل الله} أَيْ: الذي يفعل ذلك الله وهذا احتجاجٌ عليهم ثمَّ أمره بعد إقامة الحجَّة عليهم أن يُعرَّض بكونهم على الضَّلال فقال: {وَإِنَّا أَوْ إِيَّاكُمْ لَعَلَى هُدًى أَوْ فِي ضلال مبين} أَيْ: نحن أو أنتم إمَّا على هدىً أو ضلالٍ والمعنى: أنتم الضَّالون حيث أشركتم بالذي يرزقكم من السماء والأرض وهذا كما تقول لصاحبك إذا كذب: أحدنا كاذبٌ وتعنيه ثمَّ بيَّن براءته منهم ومن أعمالهم فقال:',NULL,'884',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3632,'{قل لا تسألون عما أجرمنا} الآية وهذا كقوله تعالى: {لكم دينكم ولي دين} ثمَّ أخبر أنَّه يجمعهم في القيامة ثمَّ يحكم بينهم وهو قوله تعالى:',NULL,'884',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3633,'{قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّ وَهُوَ الْفَتَّاحُ الْعَلِيمُ}',NULL,'884',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3634,'{قل أروني الذين ألحقتم به شركاء} ألحقتموهم بالله تعالى في العبادة يعني: الأصنام أَيْ: أرونيهم هل خلقوا شيئاً وهذه الآية مختصرةٌ تفسيرها قوله تعالى: {قُلْ أَرَأَيْتُمْ شُرَكَاءَكُمُ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا مِنَ الأَرْضِ أَمْ لهم شرك في السماوات} ثمَّ قال: {كلا} أيْ: لبس الأمر على ما يزعمون {بل هو الله العزيز الحكيم}',NULL,'884',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3635,'{وما أرسلناك إلاَّ كافَّة للناس} جامعا صلهم كلَّهم بالإِنذار والتَّبشير {ولكنَّ أكثر الناس لا يعلمون} ذلك وقوله تعالى:',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3636,'{وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ}',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3637,'{قُلْ لَكُمْ مِيعَادُ يَوْمٍ لا تَسْتَأْخِرُونَ عَنْهُ ساعة ولا تستقدمون}',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3638,'{ولا بالذي بين يديه} أَيْ: من الكتب المُتقدِّمة وقوله: {يرجع بعضهم إلى بعض القول} أَيْ: في التَّلاوم ثمَّ ذكر إيش يرجعون فقال: {يَقُولُ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا لَوْلا أَنْتُمْ لكنا مؤمنين}',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3639,'{قَالَ الَّذِينَ اسْتَكْبَرُوا لِلَّذِينَ اسْتُضْعِفُوا: أَنَحْنُ صَدَدْنَاكُمْ عَنِ الْهُدَى بَعْدَ إِذْ جَاءَكُمْ بَلْ كُنْتُمْ مجرمين}',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3640,'{وَقَالَ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا: بَلْ مَكْرُ الليل والنهار} أَيْ: مكركم بنا فيهما {إذ تأمروننا أن نكفر بالله} {وأسروا} : وأظهروا',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3641,'{وما أرسلنا في قرية من نذير} نبيٍّ يُنذرهم {إلاَّ قال مترفوها} رؤساؤها وأغنياؤها {إنَّا بما أُرسلتم به كافرون}',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3642,'{وقالوا} للرُّسل: {نحن أكثر أموالاً وأولاداً} منكم يعنون أنَّ الله سبحانه رضي منَّا حيث أعطانا المال {وما نحن بمعذبين} كما تقولون',NULL,'885',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3643,'{قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ ويقدر} وليس ذلك ممَّا يدلُّ على العواقب {ولكن أكثر الناس لا يعلمون} ذلك',NULL,'886',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3644,'{وَمَا أَمْوَالُكُمْ وَلا أَوْلادُكُمْ بِالَّتِي تُقَرِّبُكُمْ عِنْدَنَا زلفى} أَيْ: قُربى يعني: تقريباً {إلاَّ من آمن} لكنْ مَنْ آمَنَ {وَعَمِلَ صَالِحًا فَأُولَئِكَ لَهُمْ جَزَاءُ الضعف} من الثَّواب بالواحد عشرة {وهم في الغرفات آمنون} قصور الجنة',NULL,'886',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3645,'{وَالَّذِينَ يَسْعَوْنَ فِي آيَاتِنَا مُعَاجِزِينَ أُولَئِكَ فِي العذاب محضرون}',NULL,'886',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3646,'{وما أنفقتم من شيء} ما تصدَّقتم من صدقةٍ {فهو يخلفه} يعطي خلقه إمَّا عاجلاً في الدُّنيا وإمَّا آجلاً في الآخرة',NULL,'886',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3647,'{ويوم نحشرهم جميعاً} العابدين والمعبودين {ثم يقول للملائكة} توبيخاً للكفَّار: {أهؤلاء إياكم كانوا يعبدون}',NULL,'886',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3648,'{قالوا سبحانك} تنزيهاً لك {أنت ولينا} الذي نتولاَّه ويتولاَّنا {من دونهم بل كانوا يعبدون الجن} يُطيعون إبليس وأعوانه {أكثرهم بهم مؤمنون} مُصدِّقون ما يمنُّونهم ويعدونهم وقوله تعالى:',NULL,'886',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3649,'{فَالْيَوْمَ لا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَفْعًا وَلا ضَرًّا وَنَقُولُ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ النَّارِ التي كنتم بها تكذبون}',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3650,'{وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالُوا مَا هَذَا إِلا رَجُلٌ يُرِيدُ أَنْ يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ آبَاؤُكُمْ وَقَالُوا مَا هَذَا إِلا إِفْكٌ مُفْتَرًى وَقَالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جَاءَهُمْ إِنْ هَذَا إِلا سِحْرٌ مُبِينٌ}',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3651,'{وَمَا آتَيْنَاهُمْ مِنْ كُتُبٍ يَدْرُسُونَهَا وَمَا أَرْسَلْنَا إليهم قبلك من نذير} يعني: مشركي مكَّة لم يكونوا أهل كتابٍ ولا بُعث إليهم نبيٌّ قبل محمد صلى الله عليه وسلم',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3652,'{وكذب الذين من قبلهم} من الأمم {وما بلغوا} يعني: مشركي مكَّة {معشار} عشر {ما آتيناهم} من القُوَّة والنِّعمة {فكذبوا رسلي فكيف كان نكير} إنكاري عليهم ما فعلوا بالإِهلاك والعقوبة؟',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3653,'{قل إنما أعظكم بواحدة} بخصلةٍ واحدةٍ وهي الطَّاعة لله تعالى {أن تقوموا} لأن تقوموا {لله مثنى وفرادى} مُجتمعين ومُنفردين {ثم تتفكروا} فتعلموا {ما بصاحبكم} محمد {من جنة} من جنونٍ {إنْ هو إلاَّ نذير لكم} ما هُوَ إِلا نَذِيرٌ لَكُمْ {بَيْنَ يَدَيْ عَذَابٍ شديد} إنْ عصيتموه',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3654,'{قل ما سألتكم من أجر} على تبليغ الرِّسالة {فَهُوَ لَكُمْ إِنْ أَجْرِيَ إِلا عَلَى اللَّهِ} يعني: إنَّما أطلب الثَّواب من الله لا عَرضاً من الدُّنيا',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3655,'{قل إنَّ ربي يقذف بالحق} يُلقيه إلى أنبيائه',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3656,'{قل جاء الحق} جاء أمر الله الذي هو الحق {وما يبدئ الباطل وما يعيد} أَيْ: ما يخلق إبليس أحداً ولا يبعثه إنَّما يفعل ذلك الله تعالى',NULL,'887',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3657,'{قُلْ إِنْ ضَلَلْتُ فَإِنَّمَا أَضِلُّ عَلَى نَفْسِي} أَيْ: على نفسي يكون وبال ضلالي وهذا إخبارٌ أنَّ مَنْ ضلَّ فإنما يضرُّ نفسه {وإن اهتديت فبما يوحي إليَّ ربي} يعني: لولا الوحيُ ما كنت أهتدي',NULL,'888',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3658,'{ولو ترى} يا محمد {إذ فزعوا} عن البعث {فلا فوت} لهم منَّا {وأخذوا من مكان قريب} على الله وهو القبور',NULL,'888',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3659,'{وقالوا} حين عاينوا العذاب {آمنا به} بالله {وأنى لهم التناوش} أَيْ: كيف يتناولون التَّوبة وقيل: الرَّجعة وقد بعدت عنهم يريد إنَّ التَّوبة كانت تُقبل عنهم في الدُّنيا وقد ذهبت الدُّنيا وبعدت عن الآخرة',NULL,'888',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3660,'{وقد كفروا به} بمحمد صلى الله عليه وسلم والقرآن {من قبل} أَيْ: في الدُّنيا {ويقذفون بالغيب} يرمون محمدا صلى الله عليه وسلم بالكذب والبهتان ظنَّاً لا يقيناً {من مكان بعيد} وهو أنَّ الله تعالى أبعدهم قبل أن يعلموا صدق محمد صلى الله عليه وسلم',NULL,'888',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3661,'{وحيل بينهم} مُنعوا ممَّا يشتهون من التَّوبة والإِيمان والرُّجوع إلى الدنيا {كما فُعل بأشياعهم} ممَّن كانوا على مثل دأبهم من تكذيب الرُّسل قبلهم حين لم يقبل منهم الإِيمان والتَّوبة {إنهم كانوا في شك} من أمر الرُّسل والبعث {مريب} موقعٍ للرِّيبة والتُّهمة',NULL,'888',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3662,'{الحمد لله فاطر السماوات والأرض} خالقهما على ابتداء {جاعل الملائكة رسلاً أولي} أصحاب {أَجْنِحَةٍ مَثْنَى وَثُلاثَ وَرُبَاعَ يَزِيدُ فِي الْخَلْقِ} في خلق الملائكة وأجنحتها {ما يشاء}',NULL,'889',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3663,'{ما يفتح الله للناس من رحمة} رزقٍ ومطرٍ فلا يقدر أحدٌ أن يمسكه والذي يمسك لا يرسله أحد',NULL,'889',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3664,'{يا أيها الناس} خطاب أهل مكَّة {اذكروا نعمة الله عليكم} بالرِّزق والمطر وسائر ذلك {هل من خالق غير الله} هل يخلق أحدٌ سواه ثُمَّ {يرزقكم من السماء} المطر {و} من {الأرض} النَّبات {لا إِلَهَ إِلا هُوَ فَأَنَّى تؤفكون} من أين يقع لكم الإِفك والكذب بتوحيد الله؟ ! ثمَّ عزَّى نبيَّه عليه السَّلام بقوله:',NULL,'889',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3665,'{وَإِنْ يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ وإلى الله ترجع الأمور}',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3666,'{يا أيها النَّاسُ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَلا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا وَلا يَغُرَّنَّكُمْ بِاللَّهِ الْغَرُورُ}',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3667,'{إِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا إِنَّمَا يَدْعُو حِزْبَهُ لِيَكُونُوا مِنْ أَصْحَابِ السَّعِيرِ}',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3668,'{الَّذِينَ كَفَرُوا لَهُمْ عَذَابٌ شَدِيدٌ وَالَّذِينَ آمَنُوا وعملوا الصالحات لهم مغفرة وأجر كبير}',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3669,'{أفمن زين له سوء عمله} بإضلال الله تعالى إيَّاه فرأى قبيح ما يعمله حَسَنًا {فَإِنَّ اللَّهَ يُضِلُّ مَنْ يَشَاءُ وَيَهْدِي مَنْ يَشَاءُ فَلا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَاتٍ} لا تغتمَّ لكفرهم ولا تتحسَّر على تركهم الإيمان',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3670,'{وَاللَّهُ الَّذِي أَرْسَلَ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَسُقْنَاهُ إِلَى بَلَدٍ مَيِّتٍ فَأَحْيَيْنَا بِهِ الأَرْضَ بَعْدَ مَوْتِهَا كَذَلِكَ النُّشُورُ}',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3671,'{مَنْ كان يريد العزة} أَيْ: عِلْمَ العزَّةِ لمَنْ هي {فَلِلَّهِ الْعِزَّةُ جَمِيعًا إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ} إليه يصل الكلام الذي هو توحيده وهو قول لا إله إلا الله {والعمل الصالح} يرفع ذلك الكلم الطَّيَّب والكلم الطَّيَّب: ذكر الله تعالى والعمل الصَّالح: أداء فرائضه فمن كان حسناً وعمل صالحاً رفعه العمل ومعنى الرَّفع رفعه إلى محل القبول {والذين يمكرون السيئات} يعني: الذين مكروا برسول الله صلى الله عليه وسلم في دار النَّدوة {ومكر أولئك هو يبور} أي: يفسد ويبطل وقوله تعالى:',NULL,'890',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3672,'{وما يعمَّر من معمَّر} أي: يُطوَّل عُمر أحدِ {وَلا يُنْقَصُ مِنْ عُمُرِهِ} ولا يكون أحدٌ ناقص العمر وهو مُحصىً في الكتاب يعني: عدد عمر الطَّويل العمر وعمر القصير العمر',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3673,'{وما يستوي البحران هذا عذب فرات} شديد العذوبة {وهذا ملح أجاج} شديد المرارة {ومن كلٍّ} من الملح والعذب {تأكلون لحماً طرياً} من السَّمك {وتستخرجون} منه من الملح {حلية تلبسونها} يعني: المرجان وإنَّما ذكر هذا للدَّلالة على قدرته وقوله:',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3674,'{من قطمير} يعني: لفافة النَّواة',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3675,'{ويوم القيامة يكفرون بشرككم} أَيْ: يقولون: ما كنتم إيَّانا تعبدون {ولا ينبئك مثل خبير} وهو الله عز وجل وقوله:',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3676,'{يا أيها النَّاسُ أَنْتُمُ الْفُقَرَاءُ إِلَى اللَّهِ وَاللَّهُ هُوَ الْغَنِيُّ الْحَمِيدُ}',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3677,'{إِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ}',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3678,'{وَمَا ذَلِكَ عَلَى اللَّهِ بِعَزِيزٍ}',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3679,'{ولا تزرُ وازِرَةٌ} أَيْ: لا تحمل نفسٌ حاملةٌ {وزِرْ أخرى} حِمل نفسٍ أخرى {وإن تدع مثقلة} نفس بالذنوب {إلى حملها} ذبوبها {لا يحمل منه شيء ولو كان} المدعو {ذا قربى} مثل الأب والابن {إنما تنذر الذين يخشون ربَّهم بالغيب} إنَّما ينفع إنذارك الذين يخافون الله تعالى ولم يروه {ومَنْ تزكَّى} عمل خيراً',NULL,'891',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3680,'{وما يستوي الأعمى} عن الحقِّ وهو الكافر {والبصير} الذي يبصر رشده وهو المؤمن',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3681,'{ولا الظلمات ولا النور} يعني: الكفر والإيمان',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3682,'{وَلا الظِّلُّ وَلا الحرور} يعني: الجنَّة التي فيها ظلٌّ دائمٌ والنَّار التي لها حرارةٌ شديدةً',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3683,'{وَمَا يَسْتَوِي الأَحْيَاءُ ولا الأموات} يعني: المؤمنين والكفَّار {إنَّ الله يُسمع من يشاء} فينتفع بذلك {وما أنت بمسمع مَنْ في القبور} يعني: الكفَّار شبَّههم بالأموات أيْ: كما لا يسمع أصحاب القبور كذلك لا يسمع الكفار وقوله:',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3684,'{إِنْ أَنْتَ إِلا نَذِيرٌ}',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3685,'{إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا وَإِنَّ مِنْ أُمَّةٍ إِلا خلا فِيهَا نَذِيرٌ}',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3686,'{وَإِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ جَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُنِيرِ}',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3687,'{ثُمَّ أَخَذْتُ الَّذِينَ كَفَرُوا فَكَيْفَ كَانَ نَكِيرِ}',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3688,'{ومن الجبال جدد بيض وحمر} أَيْ: طرائق تكون في الجبال كالعروق بيض وحمر {وغرابيب سود} وهي الجبال ذات الصُّخور السُّود',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3689,'{وَمِنَ النَّاسِ وَالدَّوَابِّ وَالأَنْعَامِ مُخْتَلِفٌ أَلْوَانُهُ كَذَلِكَ} أَيْ: كاختلاف الجبال والثَّمرات في اختلاف الألوان {إنما يخشى الله من عباده العلماء} أَيْ: مَنْ كان عالماً بالله اشتدَّت خشيته وقوله:',NULL,'892',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3690,'{يرجون تجارة لن تبور} يعني: لن تكسد ولن تفسد',NULL,'893',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3691,'{إنه غفور} لذنوبهم {شكور} لحسناتهم',NULL,'893',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3692,'{وَالَّذِي أَوْحَيْنَا إِلَيْكَ مِنَ الْكِتَابِ هُوَ الْحَقُّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ إِنَّ اللَّهَ بِعِبَادِهِ لخبير بصير}',NULL,'893',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3693,'{ثمَّ أورثنا} أعطينا بعد هلاك الأمم {الكتاب} القرآن لـ {الذين اصطفينا من عبادنا} وهم أمَّة محمد صلى الله عليه وسلم ثمَّ ذكر أصنافهم فقال: {فمنهم ظالم لنفسه} وهو الذي زادت سيئاته على حسناته {ومنهم مقتصد} وهو الذي استوت حسناته وسيِّئاته {ومنهم سابق بالخيرات} وهو الذي رجحت حسناته {بإذن الله} بقضائه وإرادته {ذلك هو الفضل الكبير} يعني: إيتاء الكتاب: وقوله تعالى:',NULL,'893',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3694,'{جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ من ذهب ولؤلؤا ولباسهم فيها حرير}',NULL,'893',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3695,'{الحمد لله الذي أذهب عنا الحَزَنَ} يعني: كلَّ ما يحزن له الإنسان من أمر المعاش والمعاد',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3696,'{الذي أحلنا} أنزلنا {دار المقامة} دار الخلود {من فضله} أَيْ: ذلك بتفضُّله لا بأعمالنا {لا يمسنا فيها نصب} تعبٌ {ولا يمسنا فيها لغوب} إعياءٌ',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3697,'{وَالَّذِينَ كَفَرُوا لَهُمْ نَارُ جَهَنَّمَ لا يُقْضَى عليهم فيموتوا}',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3698,'{وهم يصطرخون} يستغيثون وقوله: {أَوَلَمْ نُعَمِّرْكُمْ مَا يَتَذَكَّرُ فِيهِ مَنْ تَذَكَّرَ} أَيْ: العمر الذي يتَّعظ فيه ويرجع فيه إلى الله مَنْ يتَّعظ وهو ستون سنةً {وجاءكم النذير} يعني: الرَّسول: وقيل: الشيب',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3699,'{إن الله عالم غيب السماوات والأرض إنه عليم بذات الصدور}',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3700,'{هُوَ الَّذِي جَعَلَكُمْ خَلائِفَ فِي الأَرْضِ} أَيْ: جعلكم أمة خلفت مَنْ قبلها من الأمم',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3701,'{قُلْ أَرَأَيْتُمْ شُرَكَاءَكُمُ الَّذِينَ تَدْعُونَ مِنْ دُونِ الله أروني} أخبروني عنهم {ماذا خلقوا من الأرض} أَيْ: بأيِّ شيءٍ أوجبتم لهم الشِّركة مع الله أَلخلقٍ خلقوه من الأرض {أَمْ لَهُمْ شِرْكٌ فِي} خلق {السماوات أم آتيناهم} أعطينا المشركين {كتاباً} بما يدَّعونه من الشِّرك {فهم على بيَّنةٍ} من ذلك الكتاب {بل إن يعد الظالمون} ما بعد بعض الظالمين بعضاً {إلاَّ غروراً} أباطيل',NULL,'894',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3702,'{إن الله يمسك السماوات والأرض أن تزولا} لئلا تزولا وتتحرَّكا {ولئن زالتا} ولو زالتا {إن أمسكهما} ما أمسكهما {من أحدٍ من بعده} سوى الله تعالى',NULL,'895',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3703,'{وأقسموا بالله جهد أيمانهم} يعني: المشركين كانوا يقولون قبل بعثة محمد صلى الله عليه وسلم لئن أتانا رسولٌ {ليكونن أهدى من إحدى الأمم} أَيْ: من اليهود والنَّصارى والمجوس {فلما جاءهم نذير} هو النبي صلى الله عليه وسلم {ما زادهم} مجيئه {إلاَّ نفوراً} عن الحقِّ',NULL,'895',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3704,'{استكباراً في الأرض} أَيْ: استكبروا عن الإيمان استكباراً {ومكر السيئ} ومكروا المكر السيئ وهو مكرهم بالنبي صلى الله عليه وسلم ليقتلوه {ولا يحيق} أَيْ: يحيط {المكر السيئ إلاَّ بأهله} فحاق بهم مكرهم يوم بدرٍ {فهل ينظرون} بعد تكذيبك {إلاَّ سنة الأولين} يعني: العذاب',NULL,'895',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3705,'{أَوَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَكَانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَمَا كَانَ اللَّهُ لِيُعْجِزَهُ مِنْ شَيْءٍ في السماوات وَلا فِي الأَرْضِ إِنَّهُ كَانَ عَلِيمًا قَدِيرًا}',NULL,'895',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3706,'{ولو يؤاخذ الله الناس بما كسبوا} من الجرائم {ما ترك على ظهرها} على ظهر الأرض {من دابة} من الإنس والجنِّ وكلِّ ما يعقل {وَلَكِنْ يُؤَخِّرُهُمْ إِلَى أَجَلٍ مُسَمًّى فَإِذَا جَاءَ أجلهم فإنَّ الله كان بعباده بصيراً}',NULL,'895',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3707,'{يس} يا إنسان',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3708,'{والقرآن الحكيم} أقسم الله تعالى بالقرآن المحكم أنَّ محمداً صلى الله عليه وسلم من المرسلين وهو قوله:',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3709,'{إنك لمن المرسلين}',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3710,'{على صراط مستقيم} على طريق الأنبياء الذين تقدَّموك',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3711,'{تنزيل} أَيْ: القرآن تَنْزِيلَ {الْعَزِيزِ الرَّحِيمِ}',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3712,'{لِتُنْذِرَ قَوْمًا مَا أُنْذِرَ آباؤهم} في الفترة {فهم غافلون} عن الإيمان والرُّشد',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3713,'{لقد حقَّ القول} وجبت عليهم كلمة العذاب {فهم لا يؤمنون} ثمَّ بيَّن سبب تركهم الإيمان فقال:',NULL,'896',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3714,'{إنا جعلنا في أعناقهم أغلالاً} أراد: في أعناقهم وأيديهم لأنَّ الغلَّ لا يكون في العنق دون اليد {فهي إلى الأذقان} أَيْ: فأيديهم مجموعةٌ إلى أذقانهم لأنَّ الغلَّ يجعل في اليد ممَّا يلي الذقن {فهم مقمحون} رافعوا رؤوسهم لا يستطيعون الإطراق لأن من علت يده إلى ذقنه ارتفع رأسه وهذا مَثَلٌ معناه: أمسكنا أيديهم عن النَّفقة في سبيل الله بموانعَ كالأغلال',NULL,'897',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3715,'{وَجَعَلْنَا مِنْ بَيْنِ أَيْدِيهِمْ سَدًّا وَمِنْ خَلْفِهِمْ سداً} هذا وصف إضلال الله تعالى إياهم فهم بمنزلة مَنْ سُدَّ طريقه من بين يديه ومن خلقه يريد: إنَّهم لا يستطيعون أن يخرجوا من ضلالهم {فأغشيناهم} فأعميناهم عن الهدى {فهم لا يُبصرون} هـ ثم ذكر أنَّ هؤلاء لا ينفعهم الإِنذار فقال:',NULL,'897',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3716,'{وَسَوَاءٌ عَلَيْهِمْ أَأَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لا يُؤْمِنُونَ}',NULL,'897',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3717,'{إنما تنذر من اتبع الذكر} إنما إنذارك من ابتع القرآن فعمل به {وخشي الرحمن بالغيب} خاف اله تعالى ولم يره',NULL,'897',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3718,'{إنا نحن نحيي الموتى} عند البعث {ونكتب ما قدَّموا} من الأعمال {وآثارهم} ما استُنَّ به بعدهم وقيل: خطاهم إلى المساجد {وكلَّ شيء أحصيناه} عددناه وبيَّناه {في إمام مبين} وهو اللَّوح المحفوظ',NULL,'897',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3719,'{واضرب لهم مثلاً أصحاب القرية} وهي أنطاكية {إذ جاءها المرسلون} رسل عيس عليه السَّلام',NULL,'897',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3720,'{إذْ أرسلنا إليهم اثنين} من الحوارييِّن {فكذبوهما فَعَزَّزْنا بثالث} قوَّينا الرِّسالة برسولٍ ثالث وقوله:',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3721,'{قَالُوا مَا أَنْتُمْ إِلا بَشَرٌ مِثْلُنَا وَمَا أَنْزَلَ الرَّحْمنُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلا تَكْذِبُونَ}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3722,'{قَالُوا رَبُّنَا يَعْلَمُ إِنَّا إِلَيْكُمْ لَمُرْسَلُونَ}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3723,'{وَمَا علينا إلا البلاغ المبين}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3724,'{إنّا تطيرنا بكم} أَيْ: تشاءمنا وذلك أنَّهم حُبس عنهم المطر فقالوا: هذا بشؤمكم {لئن لم تنتهوا لَنَرْجُمَنَّكم} لنقتلنَّكم رجماً بالحجارة',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3725,'{قالوا طائركم معكم} شؤمكم معكم بكفركم {أَإِنْ ذكرتم} وُعظتم وخُوِّفتم تطيَّرتم {بل أنتم قوم مسرفون} مُجاوزون الحدّ بشرككم',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3726,'{وجاء من أقصى المدينة رجل} وهو حبيب النَّجار كان قد آمن بالرُّسل وكان منزله في أقصى البلد فلمَّا سمع أنَّ القوم كذَّبوهم وهمُّوا بقتلهم أتاهم يأمرهم بالإيمان فقال: {يَا قَوْمِ اتَّبِعُوا الْمُرْسَلِينَ}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3727,'{اتبعوا مَنْ لا يسألكم أَجْراً} على أداء النُّصح وتبليغ الرِّسالة {وهم مهتدون} يعني: الرُّسل فقيل له: أنت على دين هؤلاء؟ فقال:',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3728,'{وَمَا لِي لا أَعْبُدُ الَّذِي فَطَرَنِي وَإِلَيْهِ ترجعون}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3729,'{أَأَتَّخِذُ مِنْ دُونِهِ آلِهَةً إِنْ يُرِدْنِ الرَّحْمَنَ بِضُرٍّ لا تُغْنِ عَنِّي شَفَاعَتُهُمْ شَيْئًا وَلا ينقذون}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3730,'{إِنِّي إِذًا لَفِي ضَلالٍ مُبِينٍ}',NULL,'898',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3731,'{إني آمنت بربكم فاسمعون} فلمَّا قال ذلك وثبوا إليه فقتلوه فأدخله الله تعالى الجنَّة فذلك قوله تعالى:',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3732,'{قيل ادخل الجنَّة} فلمَّا شاهدها قَالَ: {يَا لَيْتَ قَوْمِي يَعْلَمُونَ}',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3733,'{بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ الْمُكْرَمِينَ} أَيْ: بمغفرة ربي',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3734,'الجزء الثالث والعشرون:
INSERT INTO [b2547] VALUES (3735,'{إن كانت} ما كانت عقوبتهم {إلاَّ صيحة واحدة} صاح بهم جبريل عليه السَّلام فماتوا عن آخرهم وهو قوله: {فإذا هم خامدون} ساكنون قد ماتوا',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3736,'{يا حسرة على العباد} يعني: هؤلاء حين استهزؤوا بالرُّسل فتحسَّروا عند العقوبة',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3737,'{ألم يروا} يعني: أهل مكَّة {كَمْ أَهْلَكْنَا قَبْلَهُمْ مِنَ الْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لا يرجعون} يعني: ألم يروا أنَّ الذين أهلكناهم قبلهم لا يرجعون إليهم',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3738,'{وإن كل} وما كلُّ مَنْ خُلق مِن الخلق إلاَّ {جميع لدينا محضرون} عند البعث يوم القيامة يحضرهم ليقفوا على ما عملوا',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3739,'{وآية لهم} على البعث {الأرض الميتة أحييناها} وقوله:',NULL,'899',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3740,'{وَجَعَلْنَا فِيهَا جَنَّاتٍ مِنْ نَخِيلٍ وَأَعْنَابٍ وَفَجَّرْنَا فيها من العيون}',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3741,'{وما عملته أيديهم} أَيْ: لم تعمله ولا صنع لهم في ذلك',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3742,'{سبحان الذي خلق الأزواج كلها} أَيْ: الأجناس من النَّبات والحيوان {وممَّا لا يعلمون} ممَّا خلق الله سبحانه من جميع الأنواع والأشباه',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3743,'{وآية لهم} ودلالةٌ لهم على توحيد الله سبحانه وقدرته {الليل نسلخ} نُخرج {منه النهار} إخراجاً لا يبقى معه شيء من ضوء النَّهار والمعنى: ننزع النَّهار فنذهب به ونأتي باللَّيل {فإذا هم مظلمون} داخلون في الظَّلام',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3744,'{والشمس} أَيْ: وآيةٌ لهم الشَّمس {تجري لمستقرٍ لها} عند انقضاء الدُّنيا',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3745,'{والقمر قدرناه منازل} ذا منازلٍ {حتى عاد} في آخر منزله {كالعرجون القديم} وهو عود الشِّمراخ إذا يبس اعوجَّ',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3746,'{لا الشَّمْسُ يَنْبَغِي لَهَا أَنْ تُدْرِكَ الْقَمَرَ} فيجتمعا معاً {ولا الليل سابق النهار} يسبقه فيأتي قبل انقضاء النَّهار {وكلٌّ} من الشَّمس والقمر والنُّجوم {فِي فَلَكٍ يسبحون} يسيرون',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3747,'{وآية لهم أنا حملنا ذريتهم} أباهم {في الفلك المشحون} يعني: سفينة نوحٍ عليه السَّلام',NULL,'900',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3748,'{وخلقنا لهم من مثله} من مثل جنس سفينة نوح {ما يركبون} في البحر',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3749,'{وإن نشأ نغرقهم فلا صريخ لهم} فلا مُغيث لهم {ولا هم يُنقذون} ينجون',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3750,'{رحمةً منا ومتاعاً إلى حين} أَيْ: إلا أن نَرحمهم ونُمتِّعهم إلى انقضاء آجالهم',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3751,'{وَإِذَا قِيلَ لَهُمُ اتَّقُوا مَا بَيْنَ أَيْدِيكُمْ} العذاب الذي عُذِّب به الأمم قبلكم {وما خلفكم} يعني: عذاب الآخرة {لعلكم ترحمون} لكي تكونوا على رجاء الرَّحمة وجواب {إذا} محذوف تقديره: وإذا قيل لهم هذا أعرضوا ودلَّ على هذا قوله تعالى:',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3752,'{وَمَا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيَاتِ رَبِّهِمْ إِلا كَانُوا عَنْهَا مُعْرِضِينَ}',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3753,'{وإذا قيل لهم أنفقوا مما رزقكم ال} كان فقراء أصحاب رسول الله صلى الله عليه وسلم يقولون للمشركين: أعطونا من أموالكم ما زعمتم أنَّها لله تعالى فكانوا يقولون استهزاءً: {أنطعم مَن لو يشاء الله أطعمه} فقال الله تعالى: {إن أنتم إلاَّ في ضلال مبين}',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3754,'{وَيَقُولُونَ مَتَى هَذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ} أنَّا نُبعث',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3755,'{ما ينظرون} ما ينتظرون {إلاَّ صيحة واحدة} وهي نفخة إسرافيل {تأخذهم وهم يخصمون} يختصمون يُخاصم بعضهم بعضاً يعني: يوم تقوم الساعة وهم في غفلةٍ عنها',NULL,'901',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3756,'{فلا يستطيعون} بعد ذلك أن يُوصوا في أمورهم بشيءٍ {ولا إلى أهلهم يرجعون} لا ينقلبون إلى أهليهم من الأسواق ويموتون في مكانهم',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3757,'{ونفخ في الصور} يعني: نفخة البعث {فإذا هم من الأجداث} القبور {إلى ربهم ينسلون} يخرجون بسرعة',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3758,'{قَالُوا: يَا وَيْلَنَا مَنْ بَعَثَنَا مِنْ مَرْقَدِنَا} أَيْ: منامنا وذلك أنَّهم كانوا قد رفع عنه العذاب فيا بين النَّفختين فيرقدون ثم يقولون: {هذا ما وعد الرحمن وصدق المرسلون} أقرُّوا حين لم ينفعهم',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3759,'{إِنْ كَانَتْ إِلا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ جميع لدينا محضرون} يريد: إنَّ بعثهم وإحياءهم كان بصيحة تُصاح بهم وهو قول إسرافيل عليه السَّلام: أيَّتها العظام البالية',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3760,'{فَالْيَوْمَ لا تُظْلَمُ نَفْسٌ شَيْئًا وَلا تُجْزَوْنَ إلاَّ ما كنتم تعملون}',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3761,'{إن أصحاب الجنة اليوم في شغل} بافتضاض الأبكار {فاكهون} ناعمون فرحون مُعجبون',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3762,'{هُمْ وَأَزْوَاجُهُمْ فِي ظِلالٍ عَلَى الأَرَائِكِ مُتَّكِئُونَ}',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3763,'{ولهم ما يدعون} يتمنَّون',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3764,'{سلام} أَيْ: لهم سلامٌ {قولاً} يقوله الله عزَّ وجل قولاً',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3765,'{وامتازوا اليوم أيها المجرمون} أَيْ: انفردوا عن المؤمنين',NULL,'902',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3766,'{أَلَمْ أَعْهَدْ إِلَيْكُمْ} ألم آمركم {يَا بَنِي آدَمَ أَنْ لا تَعْبُدُوا الشَّيْطَانَ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ}',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3767,'{وَأَنِ اعْبُدُونِي هَذَا صِرَاطٌ مُسْتَقِيمٌ}',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3768,'{ولقد أضل منكم جبلاً} خلقاً {كثيراً أفلم تكونوا تعقلون} عدوانه وإضلاله',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3769,'{هَذِهِ جَهَنَّمُ الَّتِي كُنْتُمْ تُوعَدُونَ}',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3770,'{اصلوها اليوم} اُدخلوها وقاسوا حرَّها {بما كنتم تكفرون} بكفركم',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3771,'{الْيَوْمَ نَخْتِمُ عَلَى أَفْوَاهِهِمْ وَتُكَلِّمُنَا أَيْدِيهِمْ وَتَشْهَدُ أَرْجُلُهُمْ بِمَا كَانُوا يَكْسِبُونَ}',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3772,'{ولو نشاء لطمسنا على أعينهم} لأعميناهم وأذهبنا أبصارهم {فاستبقوا الصراط} فتبادروا إلى الطَّريق {فأنى} يبصرون حينئذٍ وقد طمسنا أعنيهم؟',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3773,'{ولو نشاء لمسخناهم} حجارةً وقردةً وخنازير {على مكانتهم} في منازلهم {فما استطاعوا مُضيّاً ولا يرجعون} أَيْ: لم يقدروا على ذهابٍ ولا مجيءٍ',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3774,'{ومَنْ نعمره ننكسه في الخلق} مَنْ أطلنا عمره نكَّسنا خلقه فصار بدل القوة ضعفا وبدل الشَّباب هرماً {أفلا يعقلون} أنَّا نفعل ذلك',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3775,'{وما علمناه الشعر} لم نعلم محمدا صلى الله عليه وسلم قول الشِّعر {وما ينبغي له} وما يتسهَّل له ذلك {إن هو} أَيْ: ليس الذي أتى به {إلاَّ ذكرٌ وقرآن مبين}',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3776,'{لينذر مَنْ كان حيَّاً} عاقلاً فلا يغفل ما يُخاطب به لأنَّ الكافر كالميِّت {ويحق القول على الكافرين} تجب الحُجَّة عليهم',NULL,'903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3777,'{أَوَلَمْ يَرَوْا أَنَّا خَلَقْنَا لَهُمْ مِمَّا عَمِلَتْ أيدينا أنعاماً} أَيْ: عملناه من غير واسطةٍ ولا توكيلٍ ولا شريكٍ أعاننا {أنعاماً فهم لها مالكون} ضابطون',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3778,'{وذللناها} سخَّرناها {لهم فمنها ركوبهم} ما يركبون',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3779,'{ولهم فيها منافع ومشارب أفلا يشكرون}',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3780,'{وَاتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لَعَلَّهُمْ يُنْصَرُونَ} يُمنعون من عذاب الله تعالى',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3781,'{لا يستطيعون نصرهم} لا تنصرهم آلهتهم {وهم لهم جند محضرون} في النار لأنَّ أوثانهم معهم فيها',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3782,'{فلا يحزنك قولهم} فيك بالسُّوء والقبيح {إنا نعلم ما يسرون وما يعلنون} فنجازيهم بذلك',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3783,'{أَوَلَمْ يَرَ الإِنْسَانُ أَنَّا خَلَقْنَاهُ مِنْ نُطْفَةٍ} يعني: العاص بن وائل وقيل: أبي بن خلف {فإذا هو خصيم مبين} جَدِلٌ بالباطل خاصم النبي صلى الله عليه وسلم في إنكار البعث وهو قوله:',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3784,'{وضرب لنا مثلاً ونسي خلقه} وهو أنَّه قال: متى يحيي الله العظم البالي المتفتِّت؟ ونسي ابتداء خلقه لأنَّه لو علم ذلك ما أنكر الإِعادة وهذا معنى قوله: {قال من يحيي العظام وهي رميم} أَيْ: باليةٌ',NULL,'904',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3785,'{قل يحييها الذي أنشأها} خلقها {أَوَّلَ مَرَّةٍ وَهُوَ بِكُلِّ خَلْقٍ} من الابتداء والإِعادة {عليم}',NULL,'905',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3786,'{الَّذِي جَعَلَ لَكُمْ مِنَ الشَّجَرِ الأَخْضَرِ نَارًا} يعني: المرخ والعفار ومنهما زنود الأعراب {فإذا أنتم منه توقدون} تورون النَّار ثمَّ احتج عليهم بخلق السماوات والأرض فقال:',NULL,'905',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3787,'{وليس الذي خلق السماوات وَالأَرْضَ بِقَادِرٍ عَلَى أَنْ يَخْلُقَ مِثْلَهُمْ بَلَى وهو الخلاق العليم} ثمَّ ذكر كمال قدرته فقال:',NULL,'905',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3788,'{إنما أمره إذا أراد شيئاً} أَيْ: خلق شيءٍ {أن يقول له كن فيكون} ذلك الشَّىء',NULL,'905',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3789,'{فسبحان} تنزيهاً لله سبحانه من أن يُوصف بغير القدرة على الإعادة {الذي بيده ملكوت كلِّ شيء} أَيْ: القدرة على كلِّ شيء {وإليه ترجعون} تُردُّون في الآخرة',NULL,'905',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3790,'{والصافات صفاً} يعني: صفوف الملائكة في السَّماء',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3791,'{فالزاجرات زجراً} يعني: الملائكة تزجر السَّحاب وتسوقه',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3792,'{فالتاليات ذكراً} جماعة قرَّاء القرآن',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3793,'{إن إلهكم لواحد} أقسم الله سبحانه بهؤلاء أنَّ إلهكم لواحد',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3794,'{وَرَبُّ الْمَشَارِقِ} مطالع الشمس',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3795,'{إنا زينا السماء الدنيا بزينة الكواكب} بضوئها {و} حفظناها',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3796,'{حفظاً من كلِّ شيطان مارد} متمرِّدٍ خبيثٍ',NULL,'906',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3797,'{لا يَسَّمَّعون إلى الملأ الأعلى} يعني: الملائكة {ويقذفون من كلّ جانب} ويُرمون',NULL,'907',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3798,'{دحوراً} يُدحرون دحوراً أَيْ: يُباعدون {ولهم عذاب واصب} دائم',NULL,'907',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3799,'{إلاَّ من خطف الخطفة} سمع الكلمة من الملائكة فأخذها بسرعة {فأتبعه} لحقه {شهاب ثاقب} كوكبٌ مضيءٌ',NULL,'907',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3800,'{فاستفتهم} فسلهم يعني: أهل مكَّة {أهم أشد خلقاً أم من خلقنا} من الأمم السَّالفة قبلهم وغيرهم من السماوات والأرض {إنا خلقناهم من طين لازب} لاصقٍ لازمٍ',NULL,'907',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3801,'{بل عجبت} يا محمَّد من تكذيبهم إيَّاك {و} هم {يسخرون} من تعجُّبك',NULL,'907',NULL,NULL,'0');
