INSERT INTO [b2547] VALUES (3877,'{فنظر نظرة في النجوم} وذلك أنَّه كان لقومه من الغد عيدٌ يخرجون إليه ويضعون أطعمتهم بين يدي أصنامهم لتبرِّك عليها زعموا فقالوا لإِبراهيم: ألا تخرج معنا إلى عيدنا؟ فنظر إلى نجم وقال:',NULL,'911',NULL,NULL,'0');
