INSERT INTO [b2547] VALUES (3891,'{فلما بلغ} ذلك الغلام {معه السعي} أَيْ: أدرك معه العمل {قَالَ: يَا بُنَيَّ إِنِّي أَرَى فِي الْمَنَامِ أني أذبحك} وذلك أنَّه أُمر في المنام بذبح ولده {فانظر ماذا ترى} ما الذي تراه فيما أقول لك هل تستسلم له؟ فاستسلم الغلام و {قَالَ يَا أَبَتِ افْعَلْ مَا تُؤْمَرُ سَتَجِدُنِي إِنْ شَاءَ اللَّهُ مِنَ الصَّابِرِينَ}',NULL,'912',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3892,'{فلما أسلما} انقادا لأمر الله {وتلَّه للجبين} صرعه على أحد جنبيه',NULL,'913',NULL,NULL,'0');
