INSERT INTO [b2547] VALUES (3900,'{إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3901,'{وَبَشَّرْنَاهُ بِإِسْحَاقَ نَبِيًّا مِنَ الصَّالِحِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3902,'{وَبَارَكْنَا عَلَيْهِ وَعَلَى إِسْحَاقَ وَمِنْ ذُرِّيَّتِهِمَا مُحْسِنٌ وظالم لنفسه مبين}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3903,'{ولقد مننا على موسى وهارون} بالنُّبوَّة',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3904,'{ونجيناهما وقومهما من الكرب العظيم} يعني: الغرق وقوله:',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3905,'{وَنَصَرْنَاهُمْ فَكَانُوا هُمُ الْغَالِبِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3906,'{وَآتَيْنَاهُمَا الْكِتَابَ الْمُسْتَبِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3907,'{وَهَدَيْنَاهُمَا الصِّرَاطَ الْمُسْتَقِيمَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3908,'{وَتَرَكْنَا عَلَيْهِمَا فِي الآخِرِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3909,'{سَلامٌ عَلَى مُوسَى وَهَارُونَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3910,'{إِنَّا كَذَلِكَ نَجْزِي الْمُحْسِنِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3911,'{إنهما من عبادنا المؤمنين}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3912,'{وَإِنَّ إِلْيَاسَ لَمِنَ الْمُرْسَلِينَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3913,'{إِذْ قَالَ لِقَوْمِهِ أَلا تَتَّقُونَ}',NULL,'913',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3914,'{أتدعون بعلاً} يعني: صنماً كان لهم',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3915,'{الله رَبَّكُمْ وَرَبَّ آبَائِكُمُ الأَوَّلِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3916,'{فكذبوه فإنهم لمحضرون} في النَّار',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3917,'{إلاَّ عباد الله المخلصين} من قومه',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3918,'{وَتَرَكْنَا عَلَيْهِ فِي الآخِرِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3919,'{سلام على إِلْ ياسين} يعني: إلياس عليه السَّلام وقيل: يعني قومه ممَّن ينتسب إلى إتباعه',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3920,'{إِنَّا كَذَلِكَ نَجْزِي المحسنين} ز',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3921,'{إنه من عبادنا المؤمنين}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3922,'{وَإِنَّ لُوطًا لَمِنَ الْمُرْسَلِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3923,'{إِذْ نَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3924,'{إِلا عَجُوزًا فِي الْغَابِرِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3925,'{ثُمَّ دَمَّرْنَا الآخَرِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3926,'{وَإِنَّكُمْ لَتَمُرُّونَ عَلَيْهِمْ مُصْبِحِينَ}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3927,'{وبالليل أفلا تعقلون}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3928,'{وإن يونس لمن المرسلين}',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3929,'{إذ أبق} هرب {إلى الفلك المشحون} السَّفينة المملوءة حين ذهب مغاضبا فوقعت السَّفينة ولم تجرِ فقارعه أهل السَّفينة فخرجت القرعة عليه فخرج منها وألقى نفسه في البحر فذلك قوله:',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3930,'{فساهم} فقارع {فكان من المدحضين} المغلوبين بالقرعة',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3931,'{فالتقمه} فابتلعه {الحوت وهو مليم} أتى بما يُلام عليه',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3932,'{فلولا أنَّه كان من المسبحين} من المُصلِّين قبل ذلك',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3933,'{للبث في بطنه} في بطن الحوت إلى يوم القيامة',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3934,'{فنبذناه} طرحناه {بالعراء} وجه الأرض {وهو سقيم} عليلٌ كالفرخ الممعَّط',NULL,'914',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3935,'{وأنبتنا عليه} عنده {شجرة من يقطين} وهو القرع ليستظلَّ بها',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3936,'{وأرسلناه إلى مائة ألف أو يزيدون} بل يزيدون',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3937,'{فآمنوا فمتعناهم إلى حين} إلى انقضاء آجالهم',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3938,'{فاستفتهم} فسل يا محمَّد أهلَ مكَّة {ألربك البنات ولهم البنون} وذلك أنَّهم كانوا يزعمون أنَّ الملائكة بنات الله',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3939,'{أم خلقنا الملائكة إناثاً وهم شاهدون} حاضرون خلقنا إياهم',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3940,'{أَلا إِنَّهُمْ مِنْ إِفْكِهِمْ لَيَقُولُونَ}',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3941,'{وَلَدَ اللَّهُ وَإِنَّهُمْ لَكَاذِبُونَ}',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3942,'{أَصطفى البنات على البنين} أتَّخذ البنات دون البنين فاصطفاها وجعل لكم البنين؟ كقوله: {أَفَأَصْفَاكُمْ رَبُّكُمْ بِالْبَنِينَ وَاتَّخَذَ مِنَ الْمَلائِكَةِ إِنَاثًا}',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3943,'{مَا لَكُمْ كَيْفَ تحكمون}',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3944,'{أفلا تتذكرون}',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3945,'{أم لكم سلطان} برهانٌ {مبين} على أنَّ لله ولداً',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3946,'{فأتوا بكتابكم} الذي فيه حُجَّتكم {إِنْ كُنْتُمْ صَادِقِينَ}',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3947,'{وجعلوا بينه وبين الجنة} يعني: الملائكة {نسباً} حين قالوا: إنَّهم بنات الله {ولقد علمت الجنة} الملائكة {إنهم لمحضرون} أنَّ الذين قالوا هذا القول محضرون في النار',NULL,'915',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3948,'{سُبْحَانَ اللَّهِ عَمَّا يَصِفُونَ}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3949,'{إلاَّ عباد الله المخلصين} فإنهم ناجون من النَّار',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3950,'{فإنكم وما تعبدون} من الأصنام',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3951,'{ما أنتم عليه بفاتنين} لا تفتنون أحداً على ما يعبدون ولا تضلونه',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3952,'{إِلا مَنْ هُوَ صال الجحيم} أَيْ: إلا مَنْ هو في معلوم الله أنَّه يدخل النَّار',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3953,'{وما منا إلاَّ له} هذا من قول الملائكة والمعنى: ما منَّا مَلَكٌ إلاَّ له {مقام معلوم} من السَّماء يعبد الله سبحانه هناك',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3954,'{وإنا لنحن الصافون} في الصَّلاة',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3955,'{وإنا لنحن المسبحون} المُصلُّون',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3956,'{وإن كانوا ليقولون} كان كفار مكَّة يقولون: لو جاءنا كتابٌ كما جاء غيرنا من الأوَّلين لأخلصنا عبادة الله سبحانه فلمَّا جاءهم كفروا به',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3957,'{لَوْ أَنَّ عِنْدَنَا ذِكْرًا مِنَ الأَوَّلِينَ}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3958,'{لَكُنَّا عباد الله المخلصين}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3959,'{فسوف يعلمون} عاقبة كفرهم',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3960,'{وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَلِينَ}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3961,'{إِنَّهُمْ لَهُمُ الْمَنْصُورُونَ}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3962,'{وإن جندنا لهم الغالبون} أَيْ: تقدَّم الوعد بنصرتهم وهو قوله: {كتب الله لأغلبن أنا ورسلي}',NULL,'916',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3963,'{فَتَوَلَّ عَنْهُمْ حَتَّى حين} حتى تنقضي المدة التي أمهلوا فيها',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3964,'{وأَبْصرْهم} انظر إليهم إذا عذِّبوا {فسوف يبصرون} ما أنكروا',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3965,'{أفبعذابنا يستعجلون} وذلك أنَّهم كانوا يقولون: متى هذا الوعد؟',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3966,'{فإذا نزل} العذاب {بساحتهم} بِفِنائهم {فَسَاءَ صَبَاحُ الْمُنذَرِينَ}',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3967,'{وتول عنهم حتى حين}',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3968,'{وأبصر} انظر فبئس ما يصبحون عند ذلك',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3969,'{سُبْحَانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَ}',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3970,'{وَسَلامٌ على المرسلين}',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3971,'{والحمد لله رب العالمين}',NULL,'917',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3972,'{ص} صدق الله {والقرآن ذي الذكر} ذي الشَّرف',NULL,'918',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3973,'{بل الذين كفروا في عزَّة} امتناعٍ من الدِّين {وشقاق} خلافٍ وعداوةٍ',NULL,'918',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3974,'{كم أهلكنا} هذا جواب القسم واعترض بينهما قوله: {بل الذين كفروا} {فنادوا} بالاستغاثة عند الهلاك {ولات حين مناص} وليس حين منجىً وفوت',NULL,'918',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3975,'{وعجبوا} يعني: أهل مكَّة {أن جاءهم منذر منهم} محمد صلى الله عليه وسلم',NULL,'918',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3976,'{أجعل الآلهة إلهاً واحداً} وذلك أنَّهم اجتمعوا عند أبي طالب يشكون إليه النبي صلى الله عليه وسلم: إني أدعوكم إلى كلمة التَّوحيد لا إله إلاَّ الله فقالوا: كيف يسع الخلق كلَّهم إلهٌ واحد؟ {إنَّ هذا} الذي يقوله {لشيء عجاب} عجب',NULL,'918',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3977,'{وانطلق الملأ منهم} نهضوا من مجلسهم ذلك يقول بعضهم لبعض: {امْشُوا وَاصْبِرُوا عَلَى آلِهَتِكُمْ إنَّ هذا} الذي يقوله محمَّد {لشيءٌ يراد} أَيْ: لأَمرٌ يُراد بنا ومكرٌ يمكر علينا',NULL,'919',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3978,'{ما سمعنا بهذا} الذي يقوله {في الملَّة الآخرة} فيما أدركنا عليه آباءنا {إن هذا إلاَّ اختلاق} زورٌ وكذب',NULL,'919',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3979,'{أَأنزل عليه الذكر من بيننا} كيف خُصَّ بالوحي من جملتنا؟ قالوا هذا حسداً له على النُّبوَّة قال الله تعالى: {بَلْ هُمْ فِي شَكٍّ مِنْ ذكري} أَيْ: وَحْيِي أَيْ: حين قالوا: اختلاق {بل لما يذوقوا عذاب} ولو ذاقوه لأيقنوا وصدَّقوا',NULL,'919',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3980,'{أم عندهم خزائن رحمة ربك} أَيْ: مفاتيح النُّبوَّة حتى يعطوا النُّبوَّة مَن اختاروا',NULL,'919',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3981,'{أم لهم ملك السماوات والأرض وما بينهما} يعني: إنَّ ذلك لله عزَّ وجل فيصطفي مَنْ يشاء {فليرتقوا في الأسباب} أَيْ: إن ادَّعوا شيئاً من ذلك فليصعدوا فيما يوصلهم إلى السَّماء وليأتوا منها بالوحي إلى مَنْ يختارون ثمَّ وعد نبيَّه النَّصر فقال:',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3982,'{جند ما هنالك} أَيْ: هم جندٌ هنالك {مهزوم} مغلوبٌ {من الأحزاب} كالقرون الماضية الذين قُهروا وأُهلكوا وهذا إخبارٌ عن هزيمتهم ببدرٍ ثمَّ عزَّى نبيَّه عليه السَّلام فقال:',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3983,'{كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَفِرْعَوْنُ ذُو الأوتاد}',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3984,'{كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَفِرْعَوْنُ ذُو الأوتاد} ذو الملك الشَّديد',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3985,'{إن كل} ما كل من هؤلاء {إلاَّ كذَّب الرسل فحقَّ} فوجب {عقاب}',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3986,'{وما ينظر هؤلاء} أَيْ: ما ينتظر هؤلاء كفار مكَّة {إلاَّ صيحة واحدة} وهي نفخة القيامة {ما لها من فواق} رجوعٌ ومردٌّ',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3987,'{وقالوا ربنا عجِّلْ لنا قطنا} كتابنا وصحيفة أعمالنا {قبل يوم الحساب} وذلك لمَّا نزل قوله: {فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ} {وَأَمَّا مَنْ أوتي كتابه بشماله} سألوا ذلك فنزلت هذه الآية وقوله:',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3988,'{داود ذا الأيد} أي: ذا القوة في العبادة {إنَّه أوَّابٌ} رجَّاع إلى الله سبحانه',NULL,'920',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3989,'{إنا سخرنا الجبال معه يسبحن} يجاوبنه بالتَّسبيح {بالعشي والإِشراق} يعني: الضُّحى',NULL,'921',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3990,'{والطير} أَيْ: وسخَّرنا الطَّير {محشورة} مجموعةً {كلٌّ له} لداود {أواب} مطيعٌ يأتيه ويسبِّح معه',NULL,'921',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3991,'{وشددنا ملكه} بالحرس وكانوا ثلاثةً وثلاثين ألف رجلٍ يحرسون كلَّ ليلةٍ محرابه {وآتيناه الحكمة} الإِصابة في الأمور {وفصل الخطاب} بيان الكلام والبصر في القضاء وهو الفصل بين الحقِّ والباطل',NULL,'921',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3992,'{وهل أتاك نبأ الخصم} يعني: الملكين اللذين تصوَّرا في صورة خصمين من بني آدم {إذ تسوروا المحراب} علوا غرفة داود عليه السَّلام',NULL,'921',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3993,'{إذ دخلوا على داود ففزع منهم} لأنَّهما دخلا بغير إذنٍ في غير وقت دخول الخصوم {قالوا لا تخف خصمان} أَيْ: نحن خَصْمَانِ {بَغَى بَعْضُنَا عَلَى بَعْضٍ} أَيْ: ظلم بعضنا بعضاً {فاحكم بيننا بالحق ولا تشطط} ولا تَجُرْ {واهدنا إلى سواء الصراط} إلى طريق الحقِّ',NULL,'921',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3994,'{إِنَّ هَذَا أَخِي لَهُ تِسْعٌ وَتِسْعُونَ نَعْجَةً} يعني: امرأة {ولي نعجة واحدة} أَي: امرآةٌ {فقال أكفلنيها} أَي: انزل عنها واجعلني أنا أكلفها {وعزَّني في الخطاب} غلبني في الاحتجاج لأنَّه أقوى مني وأقدر على النُّطق وهذا القول من الملكين على التّمثيل لا على التَّحقيق كأنَّ القائل منهما قال: نحن كخصمين هذه حالهما فلمَّا قال هذا أحد الخصمين اعترف له الآخر',NULL,'921',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3995,'{قال} داود عليه السَّلام: {لقد ظلمك بسؤال نعجتك} أَيْ: بسؤاله إيَّاك نعجتك: امرأتك أن يضمَّها {إلى نعاجه وإن كثيراً من الخلطاء} الشُّركاء {لَيَبْغِي بَعْضُهُمْ عَلَى بَعْضٍ إِلا الَّذِينَ آمَنُوا وعملوا الصالحات وقليلٌ ما هم} وقليلٌ هم {وظنَّ داود} علم عند ذلك {إنّما فتناه} ابتليناه بتلك المرأة التي أحبَّ أن يتزوَّجها ثمَّ تزوَّجها {فاستغفر ربه} ممَّا فعل وهو محبته أن يتزوَّج امرأةَ مَنْ له امرأةٌ واحدةٌ وله تسع وتسعون امرأةً {وخرَّ راكعاً} سقط للسُّجود بعد ما كان راكعاً {وأناب} رجع إلى الله سبحانه بالتَّوبة',NULL,'922',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3996,'{فغفرنا له ذلك وإنَّ له عندنا} بعد المغفرة {لزلفى} قربةً {وحسن مآب} مرجع',NULL,'922',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3997,'{يَا دَاوُدُ إِنَّا جَعَلْنَاكَ خَلِيفَةً فِي الأَرْضِ} أَيْ: عن قبلك من الأنبياء وقوله: {بما نسوا يوم الحساب} أَيْ: تركوا الإِيمان به والعمل له',NULL,'922',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3998,'{وَمَا خَلَقْنَا السَّمَاءَ وَالأَرْضَ وَمَا بَيْنَهُمَا بَاطِلا} إلاَّ لأمرٍ صحيحٍ وهو الدَّلالة على قدرة خالقهما وتوحيده وعبادته وقوله:',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (3999,'{أَمْ نَجْعَلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَالْمُفْسِدِينَ فِي الأَرْضِ أَمْ نَجْعَلُ الْمُتَّقِينَ كَالْفُجَّارِ}',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4000,'{كِتَابٌ أَنْزَلْنَاهُ إِلَيْكَ مُبَارَكٌ لِيَدَّبَّرُوا آيَاتِهِ وَلِيَتَذَكَّرَ أولو الألباب}',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4001,'{وَوَهَبْنَا لِدَاوُدَ سُلَيْمَانَ نِعْمَ الْعَبْدُ إِنَّهُ أَوَّابٌ}',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4002,'{الصافناتُ الجياد} أي: الخيل القائمة',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4003,'{فَقَالَ: إِنِّي أَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ ربي} آثرت حبَّ الخير أي: الخيل على ذكر الله حتى فاتني في وقته {حتى توارت} الشَّمس {بالحجاب} أَيْ: غربت وقوله:',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4004,'{فطفق مسحاً بالسوق والأعناق} أَيْ: أقبل يقطع سوقها وأعناقها ولم يفعل ذلك إلاَّ لإِباحة الله عزَّ وجل له ذلك وقوله:',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4005,'{ولقد فتنا سليمان} ابتليناه {وألقينا على كرسيِّه جسداً} شيطاناً تصوَّر في صورته وذلك أنَّه تزوَّج امرأة وهويها وعبدت الصَّنم في داره بغير علمه فنزع الله ملكه أيَّاماً وسلَّط شيطاناً على مملكته ثمَّ تاب سليمان وأعاد الله عليه ملكه فسأل الله أن يهب له ملكاً يدلُّ على أنَّه غفر له وردَّ عليه ما نزع منه وهو قوله:',NULL,'923',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4006,'{وَهَبْ لِي مُلْكًا لا يَنْبَغِي لأَحَدٍ مِنْ بعدي} وقوله:',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4007,'{رخاءً} أي: ليِّنةً مُطيعةً سريعةً {حيث أصاب} أراد وقصد سليمان عليه السَّلام',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4008,'{والشياطين} أَيْ: وسخَّرنا له {كلَّ بناء} من الشَّياطين مَنْ يبنون له {وغوَّاص} يغوصون في البحر فيستخرجون ما يريد',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4009,'{وآخرين مقرنين في الأصفاد} وسخَّرنا له مردة الشَّياطين حتى قرنهم في السَّلاسل من الحديد وقلنا له:',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4010,'{هذا} الذي أعطيناك {عطاؤنا فامنن} أَيْ: أعطِ {أو أمسك بغير حساب} عليك في إعطائه ولا إمساكه وهذا مما خصَّ به وقوله:',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4011,'{وإن له عندنا لزلفى وحسن مآب}',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4012,'{بنصب} أَيْ: بتعبٍ ومشقَّةٍ في بدني {وعذاب} في أهلي ومالي فقلنا له:',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4013,'{اركض برجلك} أَيْ: دُسْ وحرِّك برجلك في الأرض فداس فنبعت عين ماءٍ فاغتسل به حتى ذهب الدّاء من ظاهره ثمَّ شرب منه فذهب الدَّاء من باطنه',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4014,'{وَوَهَبْنَا لَهُ أَهْلَهُ وَمِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنَّا وذكرى لأولي الألباب} مُفسَّرةٌ في سورة الأنبياء عليهم السَّلام',NULL,'924',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4015,'{وخذ بيدك ضغثاً} حزمةً من الحشيش {فاضرب به} امرأتك {ولا تحنث} في يمينك وقوله:',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4016,'{أولي الأيدي} أَيْ: ذوي القوَّة في العبادة {والأبصار} البصائر في الدِّين',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4017,'{إنا أخلصناهم بخالصة ذكرى الدار} أَيْ: جعلناهم يُكثرون ذكر الدَّار الآخرة والرُّجوع إلى الله تعالى وقوله:',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4018,'{وإنهم عندنا لمن المصطفين الأخيار}',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4019,'{من الأخيار} جمع خيِّر',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4020,'{هذا ذكر} شرفٌ وذكرٌ جميلٌ يُذكرون به أبداً {وإنَّ للمتقين} مع ذلك {لحسن مآب} مرجعٍ في الآخرة ثمَّ بيَّن ذلك المرجع فقال:',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4021,'{جنات عدن} وقوله:',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4022,'{مُتَّكِئِينَ فِيهَا يَدْعُونَ فِيهَا بِفَاكِهَةٍ كَثِيرَةٍ وَشَرَابٍ}',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4023,'{أتراب} أقرانٌ وأمثالٌ أسنانهنَّ واحدة',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4024,'{هَذَا مَا تُوعَدُونَ لِيَوْمِ الْحِسَابِ}',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4025,'{إِنَّ هَذَا لَرِزْقُنَا مَا لَهُ مِنْ نَفَادٍ}',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4026,'{هذا وإنَّ للطاغين} أي: الأمر هذا الذي ذكرت وقوله:',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4027,'{جَهَنَّمَ يَصْلَوْنَهَا فَبِئْسَ الْمِهَادُ}',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4028,'{هذا فليذوقوه حميمٌ وغساق} أي: حميمٌ وغسَّاقٌ فليذوقوه والغسَّاق: ما سال من جلود أهل النَّار',NULL,'925',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4029,'{وآخر} أَيْ: وعذابٌ آخر {من شكله} من مثل ذلك الأوَّل {أزواج} أنواع فإذا دخلت الرُّؤساء النَّار ثمَّ دخل بعدهم الأتباع قالت الملائكة:',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4030,'{هذا فوج} جماعةٌ {مقتحمٌ معكم} داخلوا النَّار فقال الرُّؤساء: {لا مرحباً بهم إنهم صالوا النار} كما صليناها فقال الأتباع',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4031,'{بَلْ أَنْتُمْ لا مَرْحَبًا بِكُمْ أَنْتُمْ قَدَّمْتُمُوهُ لنا} شرعتم وسننتم الكفر لنا {فبئس القرار} قرارنا وقراركم',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4032,'{قالوا} أي: الأتباع {ربنا مَنْ قدَّم لنا هذا} شرعه وسنَّه {فزده عذاباً ضعفاً في النار} كقوله: {ربنا آتهم ضعفين من العذاب}',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4033,'{وقالوا} يعني: صناديد قريش: {مَا لَنَا لا نَرَى رِجَالا كُنَّا نَعُدُّهُمْ من الأشرار} أي: فقراء المسلمين',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4034,'{أتخذناهم سخرياً} كنَّا نسخر بهم في الدَّنيا أَمفقودون هم؟ {أم زاغت عنهم الأبصار} فلا نراهم ها هنا',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4035,'{إن ذلك} الذي ذكرنا عن أهل النَّار {لحق} ثمَّ بيَّن ما هو فقال: {تَخَاصُمُ أهل النار}',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4036,'{قُلْ إِنَّمَا أَنَا مُنْذِرٌ وَمَا مِنْ إِلَهٍ إلا الله الواحد القهار}',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4037,'{رب السماوات وَالأَرْضِ وَمَا بَيْنَهُمَا الْعَزِيزُ الْغَفَّارُ}',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4038,'{قل هو نبأٌ عظيم} أي: القرآن الذي أنباتكم به وجئتكم فيه بما لا يُعلم إلاَّ بوحي وهو قوله:',NULL,'926',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4039,'{أَنْتُمْ عَنْهُ مُعْرِضُونَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4040,'{مَا كَانَ لِي مِنْ عِلْمٍ بِالْمَلإِ الأَعْلَى} وهم الملائكة {إذ يختصمون} في شأن آدم عليه السَّلام يعني: قولهم: {أتجعل فيها مَنْ يفسد فيها} الآية وقوله:',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4041,'{إِنْ يُوحَى إِلَيَّ إِلا أَنَّمَا أَنَا نَذِيرٌ مبين}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4042,'{إِذْ قَالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي خَالِقٌ بَشَرًا من طين}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4043,'{فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا له ساجدين}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4044,'{فَسَجَدَ الْمَلائِكَةُ كُلُّهُمْ أَجْمَعُونَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4045,'{إلا إبليس استكبر وكان من الكافرين}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4046,'{لما خلقت بيدي} أَيْ: تولَّيت خلقه وهذا اللَّفظ ذُكر تخصيصاً وتشريفاً لآدم عليه السَّلام وإن كان كلُّ شيءٍ يتولَّى الله خلقه دون غيره وقوله:',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4047,'{قَالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نَارٍ وخلقته من طين}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4048,'{قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4049,'{وَإِنَّ عَلَيْكَ لَعْنَتِي إِلَى يَوْمِ الدِّينِ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4050,'{قَالَ رَبِّ فَأَنْظِرْنِي إِلَى يَوْمِ يُبْعَثُونَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4051,'{قَالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4052,'{إِلَى يَوْمِ الْوَقْتِ الْمَعْلُومِ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4053,'{قَالَ فَبِعِزَّتِكَ لأُغْوِيَنَّهُمْ أَجْمَعِينَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4054,'{إِلا عِبَادَكَ مِنْهُمُ الْمُخْلَصِينَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4055,'{قال فالحقُّ والحقَّ أقول} أَيْ: فبالحقِّ أقول وأقول الحقَّ قَسمٌ جوابه: {لأَمْلأَنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ أَجْمَعِينَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4056,'{لأَمْلأَنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ أَجْمَعِينَ}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4057,'{قل ما أسألكم عليه} على تبليغ الرِّسالة {من أجر وما أنا من المتكلفين} المنقولين للقرآن من تلقاء نفسي',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4058,'{إن هو} ليس القرآن {إلا ذكر} عظة {للعالمين}',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4059,'{ولتعلمنَّ} أنتم أيُّها المشركون {نبأه} ما أخبرتكم فيه من البعث والقيامة {بعد حين} بعد الموت',NULL,'927',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4060,'{تنزيل الكتاب} ابتداء وخبره قوله: {من الله العزيز الحكيم} وقوله:',NULL,'928',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4061,'{مخلصا له الدين} أي: الطَّاعة والمعنى: اعبده مُوحِّداً لا إله إلا هو',NULL,'928',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4062,'{ألا لله الدين الخالص} أَيْ: الطَّاعة لا يستحقُّها إلاَّ الله تعالى ثمَّ ذكر الذين يعبدون غيره فقال: {وَالَّذِينَ اتَّخَذُوا مِنْ دُونِهِ أَوْلِيَاءَ مَا نَعْبُدُهُمْ} أَيْ: ويقولون: {مَا نَعْبُدُهُمْ إِلا لِيُقَرِّبُونَا إِلَى اللَّهِ زُلْفَى} أَيْ: قربى {إِنَّ اللَّهَ يَحْكُمُ بَيْنَهُمْ فِي مَا هُمْ فيه يختلفون} من أمر الدين ثم أنَّه لا يهدي هؤلاء فقال {إِنَّ اللَّهَ لا يَهْدِي مَنْ هُوَ كاذب} في إضافة الولد إلى الله تعالى {كفار} يكفر نعمته بعبادة غيره ثمَّ ذكر براءته عن الولد فقال:',NULL,'928',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4063,'{لو أراد الله أن يتخذ ولداً} كما يزعم هؤلاء {لاصطفى} لاختار {ممَّا يخلق ما يشاء سبحانه} تنزيهاً له عن الولد وقوله:',NULL,'929',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4064,'{يكور الليل على النهار} أَيْ: يدخل أحدهما على الآخر',NULL,'929',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4065,'{خلقكم من نفس واحدة} يعني: آدم عليه السَّلام {ثمَّ جعل منها زوجها} حوَّاء {وأنزل لكم من الأنعام ثمانية أزواج} مشروحٌ في سورة الأنعام وقوله: {خلقاً من بعد خلق} أَيْ: نطفةً ثمَّ علقةً ثمَّ مضغةً {في ظلمات ثلاث} ظلمة البطن وظلمة الرَّحم وظلمة المشيمة {فأنى تُصرفون} عن عبادته إلى عبادة غيره بعد هذا البيان! وقوله:',NULL,'929',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4066,'{ولا يرضى لعباده الكفر} أَيْ: المؤمنين المخلصين منهم كقوله: {عيناً يشرب بها عباد الله} {وإن تشكروا} أَيْ: إن تطيعوا ربَّكم {يرضه لكم} يرض الشُّكر لكم ويُثبكم عليه',NULL,'929',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4067,'{وإذا مس الإنسان} يعني: الكافر {ضرٌّ دعا ربَّه منيباً إليه} راجعاً {ثمَّ إذا خَوَّلَهُ} أعطاه {نِعْمَةً مِنْهُ نَسِيَ مَا كَانَ يَدْعُو إِلَيْهِ من قبل} نسي الله الذي كان يتضرَّع إليه من قبل النِّعمة وترك عبادته {قل} يا محمَّد عليه السَّلام لمن يفعل ذلك: {تَمَتَّعْ بِكُفْرِكَ قَلِيلا إِنَّكَ مِنْ أَصْحَابِ النَّارِ} وهذا تهديدٌ',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4068,'{أمن هو قانت} قائمٌ مطيعٌ لله {آناء الليل} أوقاته {يحذر} عذاب {الآخرة} كمَنْ هو عاص؟ ثمَّ ضرب لهما مثلاً فقال: {هَلْ يَسْتَوِي الَّذِينَ يَعْلَمُونَ وَالَّذِينَ لا يَعْلَمُونَ} أَيْ: هل يستوي العالم والجاهل؟ كذلك لا يستوي المطيع والعاصي {إنما يتذكر أولو الألباب} إنَّما يتَّعظ بوعظ الله ذوو العقول وقوله:',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4069,'{للذين أحسنوا في هذه الدنيا} وحَّدوا الله تعالى وعملوا بطاعته {حسنة} وهي الجنَّة {وأرض الله واسعة} فهاجروا فيها واخرجوا من بين الكفَّار {إنما يوفى الصابرون} على طاعة الله تعالى وما يبتليهم به {أَجْرَهُمْ بغير حساب} بغير مكيالٍ ولا ميزان',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4070,'{قُلْ إِنِّي أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ مُخْلِصًا له الدين} أَيْ: مُوحِّداً',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4071,'{وأمرت لأَنْ أَكُونَ أَوَّلَ الْمُسْلِمِينَ} من هذه الأمة',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4072,'{قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ}',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4073,'{قل الله أعبد مخلصا له ديني}',NULL,'930',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4074,'{قل إن الخاسرين الذين خسروا أنفسهم} بالتَّخليد في النَّار {وأهليهم} لأنَّهم لم يدخلوا مدخلِ المؤمنين الذين لهم أهل في الجنَّة',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4075,'{لهم من فوقهم ظللٌ} هذا كقوله {يوم يغشاهم العذاب من فوقهم} الآية وكقوله: {لَهُمْ مِنْ جَهَنَّمَ مِهَادٌ وَمِنْ فَوْقِهِمْ غَوَاشٍ} {ذلك} الذي وصفت من العذاب {يخوِّف الله به عباده}',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4076,'{والذين اجتنبوا الطاغوت} أَيْ: الأوثان {أن يعبدوها وأنابوا إلى الله} رجعوا إليه بالَّطاعة {لهم البشرى} بالجنَّة {فَبَشِّرْ عباد}',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4077,'{الذين يستمعون القول} القرآن وغيره {فيتبعون أحسنة} وهو القرآن',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4078,'{أفمن حقَّ عليه كلمةُ العذاب أفأنت} يا محمَّدُ {تنقذ} هـ أَيْ تُخرجه من النَّار أيْ: إنَّه لا يقدر على هدايته وقوله:',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4079,'{لهم غرفٌ من فوقها غرفٌ مبنية} أَيْ: لهم منازل في الجنَّة رفيعةٌ وفوقها منازل أرفع منها',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4080,'{أَلَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّمَاءِ ماء فسلكه} أدخل ذلك الماء {ينابيع في الأرض} وهي المواضع التي ينبع منها الماء وكلُّ ماءٍ في الأرض فن السَّماء نزل {ثم يخرج به} بذلك الماء {زرعاً مختلفا ألوانه} خضرة وصفرةً {ثمَّ يهيج} ييبس {فتراه مصفراً ثم يجعله حطاماً} دُقاقاً فتاتاً {إنَّ في ذلك لذكرى لأولي الألباب} يذكرون ما لهم من الدَّلالة في هذا على توحيد الله تعالى وقدرته',NULL,'931',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4081,'{أفمن شرح الله صدره} وسَّعه {للإِسلام فهو على نور من ربه} أَيْ: فاهتدى إلى دين الإِسلام كمَنْ طبع على قلبه ويدل على هذا المحذوف قوله: {فَوَيْلٌ لِلْقَاسِيَةِ قُلُوبُهُمْ من ذكر الله}',NULL,'932',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4082,'{اللَّهُ نزَّل أَحسنَ الحَديثِ} أي: القرآن {كتاباً متشابهاً} يشبه بعضه بعضاً من غير اختلاف ولا تناقص {مثاني} يثني فيه الأخبار والقصص وذكر الثَّواب والعقاب {تقشعر} تضطرب وتتحرَّك بالخوف {منه جلود الذين يخشون ربهم} يعني: عند ذكر آية العذاب {ثُمَّ تَلِينُ جُلُودُهُمْ وَقُلُوبُهُمْ إِلَى ذِكْرِ اللَّهِ} أَيْ: من آية الرَّحمة {ذلك هدى الله} أَيْ: ذلك الخشية من العذاب ورجاء الرَّحمة هدى الله',NULL,'932',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4083,'{أفمن يتقي بوجهه سوء العذاب} وهو الكافر يُلقي في النَّار مغلولاً فلا يتهيَّأ له أن يتَّقي النَّار إلاَّ بوجهه ومعنى الآية: أفمن هذا حاله كمَن يدخل الجنَّة؟ وقوله:',NULL,'932',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4084,'{كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لا يَشْعُرُونَ}',NULL,'932',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4085,'{فَأَذَاقَهُمُ اللَّهُ الْخِزْيَ فِي الْحَيَاةِ الدُّنْيَا وَلَعَذَابُ الآخرة أكبر لو كانوا يعلمون}',NULL,'932',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4086,'{ولقد ضربنا للناس في هذا القرآن من كلِّ مثل لعلهم يتذكرون}',NULL,'932',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4087,'{غير ذي عوج} أَيْ: ليس فيه اختلافٌ وتضادٌّ ثمَّ ضرب مثلاً للموحِّد والمشرك فقال:',NULL,'933',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4088,'{ضَرَبَ اللَّهُ مَثَلا رَجُلا فِيهِ شُرَكَاءُ مُتَشَاكِسُونَ} متنازعون سيِّئةٌ أخلاقهم وكلُّ واحدٍ يستخدمه بقدر نصيبه وهذا مَثَلُ المشرك الذي يعبد آلهة شتى {ورجلا سلما} خالصاً لرجل وهو الذي يعيد اله وحده {هل يستويان مثلاً} أَيْ: هل يستوي مَثَل الموحِّد ومَثَل المشرك؟ {الحمد لله} وحده دون غيره من المعبودين {بل أكثرهم لا يعلمون} مفسَّر في سورة النَّحل ثمَّ ذكر أنهم يموتون ويرجعون إلى الله فيختصمون عنده فقال:',NULL,'933',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4089,'{إِنَّكَ مَيِّتٌ وَإِنَّهُمْ مَيِّتُونَ} {ثُمَّ إِنَّكُمْ يَوْمَ القيامة عند ربكم تختصمون} يعني: المؤمن والكافر والمظلوم والظَّالم',NULL,'933',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4090,'{ثُمَّ إِنَّكُمْ يَوْمَ القيامة عند ربكم تختصمون}',NULL,'933',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4091,'{فمن أظلم ممن كذب على الله} وزعم أنَّ له ولداً وشريكاً {وكذَّب بالصدق} بالقرآن {إذ جاءه} على لسان الرَّسول {أليس في جهنَّم مثوى} مقامٌ ومنزلٌ لهؤلاء',NULL,'933',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4092,'{والذي جاء بالصدق} يعني: محمدا صلى الله عليه وسلم بالقرآن {وصدَّق} أبو بكر رضي الله عنه ثم المؤمنين بعده وقوله:',NULL,'933',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4093,'{لهم ما يشاؤون عِنْدَ رَبِّهِمْ ذَلِكَ جَزَاءُ الْمُحْسِنِينَ}',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4094,'{لِيُكَفِّرَ اللَّهُ عَنْهُمْ أَسْوَأَ الَّذِي عَمِلُوا وَيَجْزِيَهُمْ أجرهم بأحسن الذي كانوا يعملون}',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4095,'{أليس الله بكافٍ عبده} يعني: محمدا صلوات الله عليه ينصره ويكفيه أمر مَنْ يُعاديه {ويخوفونك بالذين من دون} أَيْ: يُخوِّفونك بأوثانهم يقولون: إنَّك لتعيبها وإنَّها لتصيبنَّك بسوء ثمَّ بيَّن أنَّهم مع عبادتهم الأوثان يُقرُّون بأنَّ الخالق هو الله فقال:',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4096,'{وَمَنْ يَهْدِ اللَّهُ فَمَا لَهُ مِنْ مُضِلٍّ أليس الله بعزيز ذي انتقام}',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4097,'{ولئن سالتهم من خلق السماوات وَالأَرْضَ لَيَقُولُنَّ اللَّهُ قُلْ أَفَرَأَيْتُمْ مَا تَدْعُونَ من دون الله} الأوثان {إن أرادني الله بضرٍّ} بلاءٍ وشدَّةٍ هل يكشفنَ ذلك عني {أو أرادني برحمة} نعمةٍ هل يمسكن ذلك عني؟ وهذا بيان أنَّها لا تنفع ولا تدفع',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4098,'{قُلْ يَا قَوْمِ اعْمَلُوا عَلَى مَكَانَتِكُمْ إِنِّي عَامِلٌ فَسَوْفَ تَعْلَمُونَ}',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4099,'{مَنْ يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مقيم}',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4100,'{إِنَّا أَنْزَلْنَا عَلَيْكَ الْكِتَابَ لِلنَّاسِ بِالْحَقِّ فَمَنِ اهْتَدَى فَلِنَفْسِهِ وَمَنْ ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا وَمَا أَنْتَ عَلَيْهِمْ بِوَكِيلٍ}',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4101,'{الله يتوفى الأنفس} يقبض الأرواح {حين} عند {موتها والتي لم تمت} أَيْ: ويقبض روح التي لَمْ تَمُتْ {فِي مَنَامِهَا فَيُمْسِكُ الَّتِي قَضَى عليها الموت} أَيْ: يمسك أنفس الأموات عنده {ويرسل الأخرى} أنفس الأحياء إذا انتهبوا من منامهم يردُّ عليهم أرواحهم {إلى أجل مسمى} وهو أجل الموت',NULL,'934',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4102,'{أم اتخذوا من دون الله شفعاء} أَيْ: الأوثان التي عبدوها لتشفع لهم {قل أو لو كانوا لا يَمْلِكُونَ شَيْئًا} من الشَّفاعة {ولا يعقلون} أنَّهم يعبدونهم لا يتركون عبادتهم',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4103,'{قل لله الشفاعة جميعاً} فليس يشفع أحدٌ إلاَّ بإذنه',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4104,'{وَإِذَا ذُكِرَ اللَّهُ وَحْدَهُ اشْمَأَزَّتْ قُلُوبُ الَّذِينَ لا يؤمنون بالآخرة} كان المشركون إذا سمعوا قول لا إله إلاَّ الله وحده لا شريك له نفروا من ذلك وإذا ذكر الأوثان فرحوا و {اشمأزَّت} : نفرت وقوله:',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4105,'{قل اللهم فاطر السماوات وَالأَرْضِ عَالِمَ الْغَيْبِ وَالشَّهَادَةِ أَنْتَ تَحْكُمُ بَيْنَ عبادك في ما كانوا فيه يختلفون}',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4106,'{وَبَدَا لَهُمْ مِنَ اللَّهِ مَا لَمْ يَكُونُوا يحتسبون} في الدُّنيا أنَّه نازلٌ بهم في الآخرة وقوله:',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4107,'{وَبَدَا لَهُمْ سَيِّئَاتُ مَا كَسَبُوا وَحَاقَ بِهِمْ ما كانوا به يستهزئون}',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4108,'{إنما أوتيته على علم} أُعطيته على شرفٍ وفضلٍ وكنت علمتُ أنِّي سأُعطى هذا باستحقاقي {بل هي فتنة} أي: تلكم العطيَّة فتنةٌ من الله تعالى يبتلي به العبد ليشكر أو يكفر',NULL,'935',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4109,'{قد قالها الذين من قبلهم} يعني: قارون حين قال: {إنما أوتيته على علم عندي}',NULL,'936',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4110,'{فَأَصَابَهُمْ سَيِّئَاتُ مَا كَسَبُوا وَالَّذِينَ ظَلَمُوا مِنْ هَؤُلاءِ سَيُصِيبُهُمْ سَيِّئَاتُ مَا كَسَبُوا وَمَا هُمْ بمعجزين}',NULL,'936',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4111,'{أَوَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشَاءُ وَيَقْدِرُ إِنَّ فِي ذَلِكَ لآيَاتٍ لِقَوْمٍ يؤمنون}',NULL,'936',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4112,'{قُلْ يَا عِبَادِيَ الَّذِينَ أَسْرَفُوا عَلَى أَنْفُسِهِمْ} بارتكاب الكبائر والفواحش نزلت في قومٍ من أهل مكَّة همُّوا بالإسلام ثمَّ قالوا: إنَّ محمداً يقول: إنَّ مَنْ عبد الأوثان واتَّخذ مع الله آلهةً وقتل النَّفس لا يُغفر له وقد فعلنا كلَّ هذا فأعلم الله تعالى أنَّ مَنْ تاب وآمن غفر اله له كلَّ ذنب فقال: {لا تقنطوا من رحمة الله} اللآيه',NULL,'936',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4113,'{وأنيبوا إلى ربكم} أَيْ: ارجعوا إليه بالطاعة {وأسلموا} وأطيعوا {له}',NULL,'936',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4114,'{واتبعوا أحسن ما أنزل إليكم} أَيْ: القرآن كقوله: {اللَّهُ نزَّل أَحسنَ الحَديثِ} وقوله:',NULL,'936',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4115,'{أن تقول نفس يا حسرتى} أَيْ: افعلوا ما أمرتكم به من الإنابة وابتاع القرآن خوفَ أن تصيروا إلى حالةٍ تقولون فيها هذا القول وقوله: {على ما فرطت في جنب الله} أَيْ: قصَّرت في طاعة الله وسلوك طريقة {إن كنت لمن الساخرين} أَيْ: ما كنت إلاَّ من المستهزئين بدين الله تعالى وكتابه',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4116,'{أَوْ تَقُولَ لَوْ أَنَّ اللَّهَ هَدَانِي لَكُنْتُ من المتقين}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4117,'{أَوْ تَقُولَ حِينَ تَرَى الْعَذَابَ لَوْ أَنَّ لِي كَرَّةً فَأَكُونَ مِنَ الْمُحْسِنِينَ}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4118,'{بَلَى قَدْ جَاءَتْكَ آيَاتِي فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وكنت من الكافرين}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4119,'{وَيَوْمَ الْقِيَامَةِ تَرَى الَّذِينَ كَذَبُوا عَلَى اللَّهِ وُجُوهُهُمْ مُسْوَدَّةٌ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِلْمُتَكَبِّرِينَ}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4120,'{وينجي الله الذين اتقوا بمفازتهم} بمناجتهم من العذاب والمفازة ها هنا بمعنى الفوز وقوله:',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4121,'{اللَّهُ خَالِقُ كُلِّ شَيْءٍ وَهُوَ عَلَى كُلِّ شيء وكيل}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4122,'{له مقاليد السماوات والأرض} أَيْ: مفاتيح خزائنها فكل شيء في السماوات والأرض الله فاتحُ بابه',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4123,'{قُلْ أَفَغَيْرَ اللَّهِ تَأْمُرُونِّي أَعْبُدُ أَيُّهَا الْجَاهِلُونَ} هذا جواب الذين دعوه إلى دين آبائه وقوله:',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4124,'{وَلَقَدْ أُوحِيَ إِلَيْكَ وَإِلَى الَّذِينَ مِنْ قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ الْخَاسِرِينَ}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4125,'{بل الله فاعبد وكن من الشاكرين}',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4126,'{والأرض جميعاً قبضته يوم القيامة} أَيْ: ملكه من غير منازعٍ كما يقال: هو في قبضة فلان: إذا ملك التَّصرُّف فيه وإن لم يقبض عليه بيده {والسماوات مطويات} كقوله: {يوم نطوي السَّماء} {بيمينه} أَيْ: بقوَّته وقيل: بقسمه لأنَّه حلف أنَّه يطويها',NULL,'937',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4127,'{ونفخ في الصور فصعق} أَيْ: مات {مَنْ في السماوات وَمَنْ فِي الأَرْضِ إِلا مَنْ شَاءَ اللَّهُ} قيل: هم الشُّهداء وهم أحياءٌ عند ربِّهم وقيل: جبريل وميكائيل وإسرافيل وملك الموت وحملة العرش عليهم السَّلام {ثُمَّ نُفِخَ فِيهِ أُخْرَى فَإِذَا هُمْ قِيَامٌ ينظرون} ينتظرون أمر الله فيهم',NULL,'938',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4128,'{وأشرقت الأرض} أُلبست الإِشراق عَرَصَاتُ القيامة {بنور ربها} وهو نورٌ يخلقه الله في القيامة يلبسه وجه الأرض {ووضع الكتاب} أَيْ: الكتب التي فيها أعمال بني آدم {وجيء بالنبيين والشهداء} الذين يشهدون للرُّسل بالتبليغ',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4129,'{وَوُفِّيَتْ كُلُّ نَفْسٍ مَا عَمِلَتْ وَهُوَ أَعْلَمُ بما يفعلون}',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4130,'{وسيق الذين كفروا إلى جهنم زمراً} جماعاتٍ وأفواجاً وقوله:',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4131,'{قِيلَ ادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا فَبِئْسَ مثوى المتكبرين}',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4132,'{طبتم} أَيْ: كنتم طيِّبين في الدُّنيا وقوله:',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4133,'{وأورثنا الأرض} أَيْ: أرض الجنَّةَ {نتبوَّأ من الجنة} نتَّخذ منها منازل {حيث نشاء فنعم أجر العاملين} ثواب المطيعين',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4134,'{وترى الملائكة حافين من حول العرش} محيطين به {وقضي بينهم} أيْ: حُكم بين أهل الجنَّة والنَّار {وقيل الحمد لله رب العالمين}',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4135,'{حم} قُضي ما هو كائن',NULL,'940',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4136,'{تنزيل الكتاب} ابتداء وخبره: {من الله العزيز العليم}',NULL,'940',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4137,'{غافر الذنب} لمن قال لا إله إلا الله {وقابل التوب} ممن قال: لا إله إلا الله {شديد العقاب} لمَنْ لم يقل لا إله إلاَّ الله {ذي الطول} الغنى والسَّعة',NULL,'940',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4138,'{ما يجادل في آيات الله} أي: في دفعها وإبطالها {فلا يغررك تقلبهم} تصرفهم {في البلاد} للتجارات أَيْ: سلامتهم بعد كفرهم حتى إنَّهم يتصرَّفون حيث شاؤوا فإنَّ عاقبتهم كعاقبة من الكفَّار وهو قوله:',NULL,'940',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4139,'{كذبت قبلهم قوم نوح والأحزاب من بعدهم} أي: الذين تحزَّبوا على انبيائهم بالمخافة والعداوة كعادٍ وثمود {وهمَّت كلُّ أمة برسولهم ليأخذوه} أَيْ: قصدت كلُّ أمة رسولها ليتمكَّنوا منه فيقتلوه {وجادلوا} بباطلهم {ليدحضوا} ليدفعوا {به الحق فأخذتهم} فعاقبتهم {فكيف كان عقاب} استفهام تقرير',NULL,'940',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4140,'{وكذلك} ومثل ما ذكرنا {حقت كلمة ربك على الذين كفروا أنهم أصحاب النار} يعني: قوله: {لأملأنَّ جهنم منك وممن تبعك} الآية ثمَّ أخبر بفضل المؤمنين وأنَّ الملائكة يستغفرون لهم فقال:',NULL,'941',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4141,'{الذين يحملون العرش ومَنْ حوله} من الملائكة وقوله: {ربنا وسعت كلَّ شيءٍ رحمةً وعلماً} أَيْ: وسعت رحمتك كلَّ شيء وعلمتَ كل شيء',NULL,'941',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4142,'{ربَّنا وأَدْخِلْهم جنَّاتِ عدنٍ التي وَعَدْتَهُم ومَنْ صلَحَ من آبائِهم وأزوَاجِهم وذُرِّيَّاتهم إنك أنت العزيز الحكيم}',NULL,'941',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4143,'{وقهم السيئات ومن تق السيئات يومئذ فقد رحمته وذلك هو الفوز العظيم}',NULL,'941',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4144,'{إن الذين كفروا ينادون} وهم في النَّار وقد مقتوا أنفسهم حين وقعوا في العذاب: {لمقت الله} إيَّاكم في الدُّنيا إذ تُدعون إلى الإيمان فتكفرون أكبرُ من مقتكم أنفسكم',NULL,'941',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4145,'{قالوا ربنا أمتَّنا اثنتين وأحييتنا اثنتين} وذلك أنَّهم كانوا أمواتاً نُطفاً فأُحيوا ثمَّ أُميتوا في الدُّنيا ثمَّ أُحيوا للبعث {فاعترفنا بذنوبنا} أي: أريتنا من الآيات ما أوجب علينا الإقرار بذنوبنا {فهل إلى خروج} من الدُّنيا {من سبيل} فقيل لهم:',NULL,'941',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4146,'{ذلكم} العذاب {بأنَّه إذا دعي الله وحده كفرتم} نكرتم وحدانيته {وإن يشرك به تؤمنوا} تُصدِّقوا ذلك الشِّرك {فالحكم لله} في إنزال العذاب بكم لا يمنعه عن ذلك مانع',NULL,'942',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4147,'{هو الذي يريكم آياته} دلائل توحيده {وينزل لكم من السماء رزقاً} بالمطر {وما يتذكر} وما يتَّعظ بآيات الله {إلاَّ مَنْ ينيب} يرجع إلى الله بالإيمان',NULL,'942',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4148,'{فادعوا الله مخلصين له الدين} الطَّاعة',NULL,'942',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4149,'{رفيع الدرجات} رافعها لأهل الثَّواب في الجنَّة {ذو العرش} مالكه وخالقه {يلقي الروح} الوحي الذي تحيا به القلوب من موت الكفر {من أمره} من قوله {على مَنْ يشاء من عباده} على مَنْ يختصه بالرِّسالة {لينذر يوم التلاق} ليخوِّف الخلق يوم يلتقي أهل الأرض وأهل السَّماء أَيْ: يوم القيامة',NULL,'942',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4150,'{يوم هم بارزون} خارجون من قبورهم {لا يخفى على الله} من أعمالهم وأموالهم {شيء} يقول الله في ذلك اليوم: {لمن الملك اليوم} ثمَّ يجيب نفسه {لله الواحد القهار}',NULL,'942',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4151,'{اليوم تجزى كل نفس بما كسبت لا ظلم اليوم إن الله سريع الحساب}',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4152,'{وأنذرهم يوم الآزفة} خوّفهم بيوم القيامة والآزقة: القريبة {إذ القلوب لدى الحناجر} وذلك أنَّ القلوب ترتفع من الفزع إلى الحناجر {كاظمين} ممتلئين غمّاً وخوفاً وحزناً {ما للظالمين} أي: الكافرين {من حميم} قريبٍ {ولا شفيع يطاع} فيشفع فيهم',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4153,'{يعلم خائنة الأعين} خيانة الأعين وهي مسارقتها النَّظر إلى ما لا يحلُّ',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4154,'{والله يقضي بالحق والذين يدعون من دونه لا يقضون بشيء إن الله هو السميع البصير}',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4155,'{أَوَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عاقبة الذين كانوا من قبلهم كانوا هم أشد منهم قوة وآثارا في الأرض فأخذهم الله بذنوبهم وما كان لهم من الله من واق}',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4156,'{ذلك بأنهم كانت تأتيهم رسلهم بالبينات فكفروا فأخذهم الله إنه قوي شديد العقاب}',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4157,'{ولقد أرسلنا موسى بآياتنا} بعلاماتنا التي تدلُّ على صحة نبوّته {وَسُلْطَانٍ مبين} أي: حجة ظاهرة',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4158,'{إلى فرعون وهامان وقارون فقالوا ساحر كذاب}',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4159,'{فلما جاءهم بالحق من عندنا قالوا اقتلوا أبناء الذين آمنوا معه} وذلك أَنَّ فرعون أمر بإعادة القتل على الذُّكور من أولاد بني إسرائيل لمَّا أتاه موسى عليه السَّلام ليصدَّهم بذلك عن متابعة موسى {وما كيد الكافرين} مكر فرعون وسوء صنيعه {إلاَّ في ضلال} زوالٍ وبطلانٍ وذهابٍ',NULL,'943',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4160,'{وقال فرعون} لملَئهِ: {ذروني أقتل موسى وليدع ربه} الذي أرسله إلينا فمنعه {إني أخاف أن يبدل دينكم} الذي أنتم عليه ويبطله {أو أن يظهر في الأرض الفساد} أو يفسد عليكم دينكم إن لم يبطله فلمَّا توعَّده بالقتل قال موسى:',NULL,'944',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4161,'{إني عذت بربي وربكم من كلِّ متكبر لا يؤمن بيوم الحساب} وقوله:',NULL,'944',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4162,'{يصبكم بعض الذي يعدكم} قيل: كلُّ الذي يعدكم',NULL,'944',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4163,'{يا قوم لكم الملك اليوم ظاهرين في الأرض} هذا من قول مؤمن آل فرعون أعلمهم أنَّ لهم الملك ظاهرين عالين عل بني إسرائيل في أرض مصر ثمَّ أعلمهم أنَّ عذاب الله لا يدفعه دافع فقال: {فمن ينصرنا من بأس الله} أَيْ: مَنْ يمنعنا من عذابه {إن جاءنا} ؟ فـ {قال فرعون} حين منع قتله: {ما أُريكُمْ} من الرَّأي والنَّصيحة {إلاَّ ما أرى} لنفسي',NULL,'944',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4164,'{وقال الذي آمن} يعني: مؤمن آل فرعون: {يا قوم إني أخاف عليكم مثل يوم الأحزاب} ثمَّ فسَّر ذلك فقال:',NULL,'944',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4165,'{مثل دأب قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَالَّذِينَ مِنْ بَعْدِهِمْ} خوَّفهم إن أقاموا على كفرهم مثل حال هؤلاء حين عذِّبوا ثمَّ خوَّفهم بيوم القيامة وهو قوله:',NULL,'944',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4166,'{إني أخاف عليكم يوم التناد} وذلك أنَّه يكثر النِّداء في ذلك اليوم يُنادى بالسَّعادة والشَّقاوة ويُنادى فيُدعى كلُّ أناسٍ بإمامهم',NULL,'945',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4167,'{يوم تولون مدبرين} مُنصرفين عن موقف الحساب إلى النَّار {ما لكم من الله} من عذاب الله {من عاصم} مانعٍ يمنعكم من عذاب الله',NULL,'945',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4168,'{ولقد جاءكم يوسف من قبل} أي: من قبل موسى {بالبينات} بالآيات المعجزات {كذلك} مثل ذلك الضَّلال {يضل الله مَنْ هو مسرف} مشركٌ {مرتاب} شاكٌّ فيما أتى به الأنبياء',NULL,'945',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4169,'{الذين يجادلون في آيات الله} أَيْ: في إبطالها ودفعها {بغير سلطان} أَيْ: حُجَّةٍ {أتاهم كبر} ذلك الجدال {مقتاً} بغضاً',NULL,'945',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4170,'{وقال فرعون يا هامان ابن لي صرحاً} قصراً طويلاً {لعلي أبلغ الأسباب} أبواب السماوات وأطرافها التي تُوصلني إليها',NULL,'945',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4171,'{وإني لأظنه كاذباً} في ادِّعائه إلهاً دوني {وكذلك} مثل ما وصفنا {زين لفرعون سوء عمله وصدَّ عن السبيل} ومُنع عن الإيمان {وما كيد فرعون إلاَّ في تباب} خسارٍ يريد: أنه خسر كيده ولم ينفعه ذلك',NULL,'945',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4172,'{وقال الذي آمن} من قوم فرعون: {يا قوم اتَّبِعُون أهدكم سبيل الرشاد} طريق الصَّواب',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4173,'{يا قوم إنما هذه الحياة الدنيا متاع} متعةٌ ينتفعون بها مدَّة ولا تبقي وقوله:',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4174,'{من عمل سيئة فلا يجزى إلاَّ مثلها ومن عَمِلَ صَالِحًا مِنْ ذَكَرٍ أَوْ أُنْثَى وَهُوَ مُؤْمِنٌ فَأُولَئِكَ يدخلون الجنة يرزقون فيها بغير حساب}',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4175,'{ويا قوم ما لي أدعوكم إلى النجاة وتدعونني إلى النار}',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4176,'{وأشرك به ما ليس لي به علم} أيْ: أشرك بالله شيئاً لا علم لي به أنَّه شريك له',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4177,'{لا جرم} حقا {أنما تدعونني إليه ليس له دعوة} إجابة دعوة أَيْ: لا يستجيب لأحدٍ {في الدنيا ولا في الآخرة وأنَّ مردَّنا} مرجعنا {إلى الله}',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4178,'{فستذكرون} إذا عاينتم العذاب {ما أقول لكم وأفوض أمري إلى الله} وذلك أنهم تدعوه لمخالفته دينهم',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4179,'{فوقاه الله سيئات ما مكروا وحاق بآل فرعون سوء العذاب}',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4180,'{النار يعرضون عليها غدوّاً وعشياً} وذلك أنَّهم يُعرضون على النَّار صباحاً ومساءً ويقال لهم: هذه منازلكم إذا بعثتم',NULL,'946',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4181,'{وإذ يتحاجون في النار فيقول الضُّعَفَاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعًا فهل أنتم مغنون عنا نصيبا من النار}',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4182,'{قال الذين استكبروا إنا كل فيها إن الله قد حكم بين العباد}',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4183,'{وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوماً من العذاب}',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4184,'{قالوا: أَوَلَمْ تك تأتيكم رسلكم بالبينات قالوا: بلى قالوا: فادعوا} أَيْ: فادعوا أنتم إذاً فإنَّا لن ندعو الله لكم {وما دعاء الكافرين إلاَّ في ضلال} هلاكٍ وبطلانٍ لأنَّه لا ينفعهم',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4185,'{إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا} بظهور حجتهم والانتصار ممَّن عاداهم بالعذاب في الدُّنيا والآخرة {ويوم يقوم الاشهاد} الملائكةُ الذين يكتبون أعمال بني آدم',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4186,'{يوم لا ينفع الظالمين معذرتهم ولهم اللعنة ولهم سوء الدار}',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4187,'{ولقد آتينا موسى الهدى وأورثنا بني إسرائيل الكتاب}',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4188,'{هدى وذكرى لأولي الألباب}',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4189,'{فاصبر} يا محمَّدُ {إنَّ وعد الله} في نصرتك وإهلاك أعدائك {وسبح بحمد ربك} صل بالشُّكر منك لربِّك {بالعشي والإِبكار} أَيْ: طرفي النَّهار وقوله:',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4190,'{إن في صدورهم إلاَّ كبر ما هم ببالغيه} أَيْ: تكبُّرٌ وطمعٌ أن يعلوا على محمدٍ عليه السلام وما هم ببالغي ذلك {فاستعذ بالله} أي: فامتنع بالله من شرِّهم',NULL,'947',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4191,'{لخلق السماوات والأرض أكبر من خلق الناس} أي: في القدرة من إعادة الناس للبعث',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4192,'{وما يستوي الأعمى والبصير والذين آمنوا وعملوا الصالحات ولا المسيء قليلا ما تتذكرون}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4193,'{إن الساعة لآتية لا ريب فيها ولكن أكثر الناس لا يؤمنون}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4194,'{وقال ربكم ادعوني أستجب لكم} اعبدوني أُثبكم وأغفر لكم وقوله: {داخرين} أي: صاغرين وقوله:',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4195,'{الله الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَالنَّهَارَ مبصراً إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَكِنَّ أكثر الناس لا يشكرون}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4196,'{ذلكم الله ربكم خالق كلِّ شيء لا إله إلا هو فأنى تؤفكون}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4197,'{كذلك يؤفك} أَيْ: كما صُرفتم عن الحقِّ مع قيام الدلائل يُصرف عن الحقِّ {الذين كانوا بآيات الله يجحدون} وقوله:',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4198,'{الله الذي جعل لكم الأرض قرارا والسماء بناء وصوركم فأحسن صوركم ورزقكم من الطيبات ذلكم الله ربكم فتبارك الله رب العالمين}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4199,'{هو الحي لا إله إلا هو فادعوه مخلصين له الدين الحمد لله رب العالمين}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4200,'{قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ من دون الله لما جاءني البينات من ربي وأمرت أن أسلم لرب العالمين}',NULL,'948',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4201,'{ولتبلغوا أجلاً مسمَّى} أَيْ: وقتاً محدوداً لا تجاوزونه {ولعلكم تعقلون} ولكي تعقلوا أنَّ الذي فعل ذلك لا إله غيره',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4202,'{هو الذي يحيي ويميت فَإِذَا قَضَى أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُنْ فَيَكُونُ}',NULL,'939',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4203,'{ألم تر إلى الذين يجادلون في آيات الله} أي: في دفعها وإبطالها {أنى يصرفون} عن الحقِّ وقوله:',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4204,'{الذين كذبوا بالكتاب وبما أرسلنا به رسلنا فسوف يعلمون}',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4205,'{والسلاسل يسحبون} يُجَرُّون',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4206,'{في الحميم ثم في النار يسجرون} يُصيَّرون وقوداً للنَّار',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4207,'{ثم قيل لهم أين ما كنتم تشركون}',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4208,'{من دون الله} أي: الأصنام {قالوا ضلوا عنا} زالوا عنَّا وبطلوا فلا نراهم {بل لم نكن ندعوا من قبل شيئاً} أَيْ: ضاعت عبادتنا فلم تكن تصنع شيئاً {كذلك} كما أضلَّهم {يضل الله الكافرين}',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4209,'{ذلكم} العذاب الذي نزل بكم {بما كنتم تفرحون} بالباطل وتبطرون',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4210,'{ادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا فَبِئْسَ مَثْوَى المتكبرين}',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4211,'{فإمَّا نرينك بعض الذي نعدهم} من العذاب في حياتك {أو نتوفينك} قبل أن ينزل بهم ذلك {فإلينا يرجعون} وقوله:',NULL,'949',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4212,'{فإذا جاء أمر الله} بعذاب الأمم المُكذِّبة {قضي بالحق وخسر هنالك المبطلون} أَيْ: تبيَّن خسران أصحاب الباطل فقوله:',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4213,'{الله الذي جعل لكم الأنعام لتركبوا منها ومنها تأكلون}',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4214,'{ولكم فيها منافع} من الصُّوف والوبر والدَّرِّ والنَّسل {ولتبلغوا عليها حاجة في صدوركم} من حلم أثقالكم إلى البلاد وقوله:',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4215,'{ويريكم آياته فأي آيات الله تنكرون}',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4216,'{أَفَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عاقبة الذين من قبلهم كانوا أكثر منهم وأشد قوة وآثارا في الأرض فما أغنى عنهم ما كانوا يكسبون}',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4217,'{فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم} رضوا بما عندهم من العلم وقالوا: نخن أعلم منهم لن نُبعث ولن نعذب قوله:',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4218,'{فلما رأوا بأسنا قالوا آمنا بالله وحده وكفرنا بما كنا به مشركين}',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4219,'{سنة الله} أَيْ: سنَّ الله هذه السُّنَّةَ في الأمم كلِّها أن لا ينفعهم الإِيمان إذا رأوا العذاب {وخسر هنالك الكافرون} تبيَّن لهم الخسران',NULL,'950',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4220,'{حم}',NULL,'951',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4221,'{تنزيلُ من الرحمن الرحيم} ابتداءٌ وخبره قوله:',NULL,'951',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4222,'{كتابٌ فصلت آياته} بُيِّنت {لقوم يعلمون} لمَنْ يعلم ذلك ممَّن يعلم العربيَّة',NULL,'951',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4223,'{بشيرا ونذيرا فأعرض أكثرهم فهم لا يسمعون}',NULL,'951',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4224,'{وقالوا قلوبنا في أكنَّة} أغطيةٍ {وفي آذاننا وقر} صممٌ أَيْ: نحن في ترك القبول منك بمنزلة مَن لا يفقه ولا يسمع {ومن بيننا وبينك حجاب} خلافٌ في الدِّين فلا نجتمع معك ولا وافقك {فاعمل} على دينك فـ {إننا عاملون} على ديننا وقوله:',NULL,'951',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4225,'{فاستقيموا إليه} وجِّهوا إليه وجوهكم بالطَّاعة {وويلٌ للمشركين}',NULL,'952',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4226,'{الذين لا يؤتون الزكاة} لا يؤمنون بوجوبها فلا يؤدونها',NULL,'952',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4227,'{إنَّ الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون}',NULL,'952',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4228,'{قل أإنكم لتكفرون بالذي خلق الأرض في يومين} الأحد والأثنين',NULL,'952',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4229,'{وبارك فيها} بما خلق فيها من المنافع {وقدَّر فيها أقواتها} أرزاق أهلها وما يصلح لمعاشهم في البحار والأنهار والأشجار والدَّوابِّ {في أربعة أيام} في تتمة أربعة أَيَّامٍ وهو يوم الثلاثاء والأربعاء فصارت الجملة أربعة أيَّام خلق الله الأرض وما فيها من سبب الأقوات والمنافع والتجارات فتمَّ أمرها في أربعة أيَّام {سواء} أَيْ: استوت استواء وسواءً {للسائلين} عن ذلك أَيْ: لمَنْ سأل في كم خُلقت السماوات والأرض؟ فيقال: في أربعة أيام',NULL,'952',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4230,'{ثم استوى} قصد وعمد {إلى} خلق {السماء وهي دخان} بخارٌ مرتفعٌ عن الماء {فقال لها وللأرض ائتيا طوعاً أو كرهاً} بما خلقت فيكما من المنافع وأَخْرِجاها لمنافع خلقي قال للسماوات: أطلعي شمسك وقمرك ونجومك وقال للأرض: أخرجي ماءك وثمارك طائعةً أو كارهةً ففعلتا ما أمرهما طوعاً وهو قوله: {قالتا أتينا طائعين}',NULL,'952',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4231,'{فقضاهن} صنعهنَّ وأحكمهنَّ {سبع سماوات في يومين وأوحى في كلِّ سماءٍ أمرها} أوحى في أهل كلِّ سماءٍ بما أراد من الأمر والنَّهي وقوله: {وحفظاً} أَيْ: حفظناها من استماع الشَّياطين بالكواكب حفظاً',NULL,'953',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4232,'{فإن أعرضوا} عن الإِيمان بعد هذا البيان {فقل أنذرتكم} خوَّفتكم {صاعقة} مهلكةً تنزل بكم كما نزلت بمن قبلكم {إذ جاءتهم الرسل من بين أيديهم} أتت الرُّسل إيَّاهم ومَنْ كان قبلهم {ومن خلفهم} ومن بعد الرُّسل الذين أُرسلوا إلى آبائهم جاءتهم الرسل أنفسهم وقوله:',NULL,'953',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4233,'{إذ جاءتهم الرسل من بين أيديهم ومن خلفهم أن لا تعبدوا إلاَّ الله قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون}',NULL,'953',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4234,'{فأما عاد فاستكبروا في الأرض بغير الحق وقالوا من أشد منا قوة أولم يروا أن الله الذي خلقهم هو أشد منهم قوة وكانوا بآياتنا يجحدون}',NULL,'953',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4235,'{ريحاً صرصراً} أي: لها صوتٌ شديدٌ {في أيام نحسات} مشؤومات عليهم وقوله:',NULL,'953',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4236,'{وأما ثمود فهديناهم} دعوناهم ودللناهم {فاستحبوا العمى على الهدى} فاختاروا الكفر على الإيمان {فأخذتهم صاعقة} مهلكةُ {العذاب} ذي {الهون} وهو الهوان أي: العذاب الذي يهينهم وقوله:',NULL,'953',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4237,'{ونجينا الذين آمنوا وكانوا يتقون}',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4238,'{ويوم يحشر أعداء الله إلى النار فهم يوزعون}',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4239,'{حتى إذا ما جاؤوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون}',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4240,'{وهو خلقكم أوَّل مرة} ابتداءُ إخبارٍ عن الله تعالى وليس من كلام الجلود',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4241,'{وما كنتم تستترون أن يشهد عليكم سمعكم} أي مِنْ أن يشهد عليكم سمعكم أي: لم تكونوا تخافون أن يشهد عليكم جوارحكم فتستتروا منها {ولكن ظننتم أنَّ الله} أي: ظننتم أنَّ ما تُخفون {لا يعلم} اللَّهُ ذلك ولا يطَّلع عليه وذلك الظَّنُّ منكم بربِّكم',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4242,'{أرداكم} أهلككم',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4243,'{فإن يصبروا} في جهنَّم {فالنار مثوىً لهم} أي: مقامهم لا يخرجون منها {وإن يستعتبوا} يطلبوا الصلح {فما هم من المعتبين} أَيْ: ممَّن يُصالح ويرضى',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4244,'{وقيضنا لهم} أيْ: سبَّبنا لهم {قرناء} من الشَّياطين {فزينوا لهم ما بين أيديهم} من أمر الدُّنيا حتى آثروه {وما خلفهم} من أمر الآخرة فدعوهم إلى التَّكذيب به وأن لا جنَّة ولا نار ولا بعث ولا حساب {وحقَّ عليهم القول في أمم} مع أممٍ بالخسران والهلاك وقوله:',NULL,'954',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4245,'{والغوا فيه} أَيْ: عارضوه بكلامٍ لا يُفهم من المُكاء والصَّفير وباطل الكلام {لعلكم تغلبون} هـ على قراءته فيترك القراءة وقوله:',NULL,'955',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4246,'{فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم أسوأ الذي كانوا يعملون}',NULL,'955',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4247,'{ذلك جزاء أعداء الله النار لهم فيها دار الخلد جزاء بما كانوا بآياتنا يجحدون}',NULL,'955',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4248,'{ربنا أرنا الذين أضلانا من الجن والإنس} يعنون: إبليس وقابيل لأنًّهما أوَّل مَنْ سنَّ الضَّلالة {نجعلهما تحت أقدامنا ليكونا} في الدرك الأسفل من النَّار',NULL,'955',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4249,'{إنَّ الذين قالوا ربنا الله} أَيْ: وحَّدوه {ثمَّ استقاموا} على التَّوحيد فلم يشركوا به شيئاً {تتنزل عليهم الملائكة} عند الموت {أن لا تخافوا} ذنوبكم {ولا تحزنوا} عليها فإنَّ الله يغفرها لكم',NULL,'955',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4250,'{نحن أولياؤكم في الحياة الدنيا وفي الآخرة} أَيْ: أنصاركم وأحباؤكم وهم قرناؤهم الذين كانوا معهم في الدُّنيا من الحفظة يقولون لهم: لن نُفارقكم في القيامة حتى ندخلكم الجنَّة {ولكم فيها ما تدَّعون} تمنون وتسألون',NULL,'955',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4251,'{نزلاً} أيْ: جعل الله ذلك رزقا لهم مُهيَّئاً',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4252,'{ومَنْ أحسن قولاً ممن دعا إلى الله} الآية قيل: هو رسول الله صلى الله عليه وسلم لأنَّه دعا إلى توحيد الله وقيل: إنَّها نزلت في المُؤّذِّنين',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4253,'{ولا تستوي الحسنة ولا السيئة} لا زائدة {ادفع} السَّيئة {بالتي هي أحسن} كالغضب يُدفع بالصَّبر والجهل والحلم والإِساءة بالعفو {فإذا الذي بينك وبينه عداوة} يصير لك كأنًّه صديقٌ قريبٌ إذا فعلت ذلك',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4254,'{وما يلقاها} أي: ما يُلقَّى هذه الخصلة {إلاَّ الذين صبروا} بكظم الغيظ واحتمال الأذى {وما يلقَّاها إلاَّ ذو حظ عظيم} وهو الجنَّة',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4255,'{وإما ينزغنك من الشيطان نزغ} أَيْ: إنْ صرفك عن الاحتمال نَزْغُ الشَّيطان {فاستعذ بالله} من شرِّه وامض على حلمك',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4256,'{ومن آياته} علاماته التي تدلُّ على أنَّه واحدٌ {الليل والنهار والشمس والقمر} الآية',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4257,'{فإن استكبروا} أي: الكفَّار يقول: إن استكبروا عن السُّجود لله {فالذين عند ربك} وهم الملائكة {يسبحون له} يُصلُّون له {بالليل والنهار وهم لا يسأمون} لا يملُّون',NULL,'956',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4258,'{ومن آياته أنك ترى الأرض خاشعة} مُغبَّرةً لا نبات فيها {فَإِذَا أَنْزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ} تحرَّكت بالنًّبات {وربت} انتفخت وعلت ثمَّ تصدَّعت عن النَّبات',NULL,'957',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4259,'{إنَّ الذين يلحدون في آياتنا} يجعلون الكلام فيها على غير جهته بأن ينسبوها إلى الكذب والسِّحر {لا يخفون علينا} بل نعلمهم ونجازيهم بذلك',NULL,'957',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4260,'{إنَّ الذين كفروا بالذكر} أَيْ: بالقرآن {لما جاءهم وإنَّه لكتاب عزيز} منيعٌ من الشَّيطان والباطل',NULL,'957',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4261,'{لا يأتيه الباطل من بين يديه ولا من خلفه} أي: الكُتب التي تقدَّمت لا تبطله ولا يأتي كتابٌ بعده يبطله وقيل: إنَّه محفوظٌ من أن ينقص منه فيأتيه الباطل من بين يديه أو يزاد فيه فيأتيه الباطل من خلقه',NULL,'957',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4262,'{ما يقال لك إلاَّ ما قد قيل للرسل من قبلك} أَيْ: إنْ كذَّبك قومك فقد كذَّب الذين من قبلك',NULL,'957',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4263,'{ولو جعلناه قرآناً أعجمياً} لا بلسان العرب {لقالوا لولا فصلت} بُيِّنت {آياته} بلغتنا حتى نعرفها {أأعجمي وعربي} أي: القرآنُ أعجميٌّ ونبيٌّ عربيٌّ {قل هو} أي: القرآن {للذين آمنوا هدى} من الضَّلالة {وشفاء} من الجهل {والذين لا يؤمنون} في ترك قبوله بمنزلة مَنْ {في آذانهم وقرٌ وهو} أي: القرآن {عليهم} ذو {عمى} لأنَّهم لا يفقهونه {أولئك ينادون من مكان بعيد} أَيْ: كأنهم لقلَّة استماعهم وانتفاعهم يُنادون إلى الإيمان بالقرآن من حيث لا يسمعونه لبعد المسافة',NULL,'957',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4264,'{ولقد آتينا موسى الكتاب فاختلف فيه} بالتَّكذيب والتَّصديق والإِيمان به والكفر كما فعل قومك {ولولا كلمة سبقت من ربك} بتأخير العذاب عن قومك {لقضي بينهم} لفرغ من هلاكهم {وإنهم لفي شك منه} من القرآن {مريب}',NULL,'958',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4265,'{من عمل صالحا فلنفسه ومن أساء فعليها وما ربك بظلام للعبيد}',NULL,'958',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4266,'{إليه يردُّ علم الساعة} لأنَّه لا يعلمه غيره {وما تخرج من ثمرات من أكمامها} أوعيتها {ويوم يناديهم أين شركائي} الذين كنتم تزعمون {قالوا آذناك} أعلمناك {ما منا من شهيد} شاهدٍ أنَّ لك شريكاً لمَّا عاينوا القيامة تبرَّؤوا من معبوديهم',NULL,'958',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4267,'{وضلَّ عنهم} زال وبطل {ما كانوا يدعون من قبل} يثقون به ويعبدون قبل يوم القيامة {وظنوا} علموا {ما لهم من محيص} من مهربٍ',NULL,'958',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4268,'{لا يسأم الإنسان من دعاء الخير} لا يَمَلُّ الكافر من الدُّعاء بالصحَّة والمال {وإن مسَّه الشرُّ} الفقر والضرُّ {فيؤوس} من روح الله {قنوط} من رحمته وقوله:',NULL,'958',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4269,'{ليقولنَّ هذا لي} أَيْ: هذا واجبٌ لي بعملي استحققه {وما أظن الساعة قائمة ولئن رجعت إلى ربي إن لي عنده للحسنى} أَيْ: لستُ أوقن بالبعث وقيام السَّاعة فإن كان الأمر على ذلك إنَّ لي عنده لثواباً',NULL,'959',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4270,'{وإذا أنعمنا على الإنسان} الآية يقول: إذا كان الكافر في نعمةٍ تباعد عن ذكر الله وإذا مسَّته الحاجة أكثر الدُّعاء',NULL,'959',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4271,'{قل أرأيتم إن كان} القرآن {من عند الله ثم كفرتم به من أضلُّ} منكم لأنَّهم في {شقاق بعيد} أيْ: في خلافٍ بعيدٍ عن الحقّ بكفرهم بالقرآن',NULL,'959',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4272,'{سنريهم آياتنا في الآفاق} ما يفتح على محمَّد صلى الله عليه وسلم من القرى {وفي أنفسهم} فتح مكة {حتى يتبين لهم} أنَّ القرآن حقٌّ وصدقٌ منزلٌ من عند الله تعالى {أَوَلَمْ يكف بربك أنَّه على كل شيء شهيد} وهو يشهد لمحمَّد عليه السَّلام ولكتابه بالصِّدق',NULL,'959',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4273,'{ألا إنهم في مرية} شكٍّ {من لقاء ربهم} من البعث والمصير إليه {ألا إنَّه بكلِّ شيء محيط} عالمٌ',NULL,'959',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4274,'{حم} ح: حكم الله م: مجده',NULL,'960',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4275,'{عسق} ع: علمه س: سناؤه ق: قدرته أقسم الله تعالى بها',NULL,'960',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4276,'{كذلك يوحي إليك} ما من نبيٍّ صاحب كتابٍ إلاَّ وقد أوحى الله إليه: حم عسق فهو معنى قوله: {كذلك يوحي إليك وإلى الذين من قبلك}',NULL,'960',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4277,'{له ما في السماوات وما في الأرض وهو العلي العظيم}',NULL,'960',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4278,'{تكاد السماوات يتفطرن من فوقهن} تكاد كلُّ واحدة منها تتفطَّر فوق التي تليها من قول المشركين: اتَّخذ الله ولداً {والملائكة يسبحون بحمد ربهم} يُنزِّهون الله تعالى عن السُّوء {ويستغفرون} اللَّهَ {لمن في الأرض} من المؤمنين',NULL,'960',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4279,'{والذين اتخذوا من دونه أولياء} أَيْ: آلهةً {الله حفيظ عليهم} يحفظ أعمالهم ليجازيهم بها {وما أنت عليهم بوكيل} لم تُوكَّل عليهم وما عليك إلاَّ البلاغ',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4280,'{وكذلك} وهكذا {أوحينا إليك قرآناً عربياً} بلفظ العرب {لتنذر أمَّ القرى} أهل مكَّة {ومَنْ حولها} سائر النَّاس {وتنذر يوم الجمع} تخوِّفهم بيوم القيامة الذي يجمع فيه الخلق {لا ريب فيه} كما يرتاب الكافرون {فريق في الجنة وفريق في السعير} إخبارٌ عن اختلاف حال النَّاس في ذلك اليوم',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4281,'{ولو شاء الله لجعلهم أمة واحدة} لجعل الفريقين فريقاً واحداً {ولكن يدخل من يشاء في رحمته} بيَّن أنَّه إنَّما يُدخل الجنة مَنْ يشاء فهو فضلٌ منه {والظالمون} والكافرون {ما لهم من وليٍّ ولا نصير} ناصرٍ يمنعهم من العذاب',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4282,'{أم اتخذوا} بل اتَّخذوا {من دونه أولياء فاللَّهُ هو الوليُّ} لا ما اتَّخذوه من دونه',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4283,'{وما اختلفتم فيه من شيء} من أمر الدِّين {فحكمه إلى الله} لا إليكم وقد حكم أنَّ الدِّين هو الإِسلام لا غيره وقوله:',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4284,'{جعل لكم من أنفسكم أزواجاً} حلائل {ومن الأنعام أزواجاً} أَيْ: خلق الذَّكر والأنثى {يذرؤكم فيه} أيْ: يكثركم بجعله لم حلائل لأنهنَّ سبب النَّسل وفيه بمعنى: به {ليس كمثله شيء} الكافُ زائدةٌ أَيْ: ليس مثله شيء',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4285,'{له مقاليد السماوات والأرض يبسط الرزق لمن يشاء ويقدر إنه بكل شيء عليم}',NULL,'961',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4286,'{شرع لكم} بيَّن وأظهر لكم {من الدين ما وصى به} أمر {زوجا} ثمَّ بيَّن ذلك فقال: {أن أقيموا الدين ولا تتفرقوا فيه} والله يبعث الأنبياء كلَّهم بإقامة الدِّين وترك الفرقة {كبر} عَظُمَ وشقَّ {على المشركين ما تدعوهم إليه} من التَّوحيد وترك الأوثان {الله يجتبي إليه مَنْ يشاء} يصطفي مَنْ يشاء لدينه فيهديه إليه',NULL,'962',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4287,'{وما تفرَّقوا إِلا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بينهم} ما تفرَّق أهل الكتاب إلاَّ عن علمٍ بأنَّ الفرقة ضلالةٌ ولكنَّهم فعلوا ذلك للبغي {ولولا كلمة سبقت من ربك} في تأخيرهم إلى السَّاعة {لقضي بينهم} لجوزوا بأعمالهم {وإنَّ الذين أورثوا الكتاب من بعدهم} يعني: هذه الأمَّة أعطوا الكتاب من بعد اليهود والنَّصارى {لفي شك منه مريب} يعني: كفَّار هذه الأمَّة ومشركيها',NULL,'962',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4288,'{فلذلك فادع} أَيْ: إلى ذلك يعني: إلى إقامة الدِّين فادع النَّاس {واستقم كما أمرت} اثبت على الدِّين الذي أُمرتَ به {وقل آمنت بما أنزل الله من كتاب} أَيْ: بجميع كتب الله المنزلة {وأمرت لأعدل بينكم} لأسوِّي بينكم في الإيمان بكتبكم وقيل: لأعدل بينكم في القضية وقوله: {لا حجة} أَيْ: لا خصومة {بيننا وبينكم} وهذا منسوخٌ بآية القتال',NULL,'962',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4289,'{والذين يحاجون في الله} يُخاصمون في دين الله نبيه عليه السلام {من بعد ما استجيب له} أجيب النبي صلى الله عليه وسلم إلى الدِّين فأسلموا ودخلوا في دينه {حجتهم داحضةٌ عند ربهم} أَيْ: باطلةٌ زائلةٌ لأنَّهم يخاصمون صادقاً في خبره قد ظهرت معجزته',NULL,'963',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4290,'{الله الذي أنزل الكتاب بالحق والميزان} أَيْ: العدل والمعنى: إنَّ الله تعالى أمر أن يقتدي بكتابه في أوامره ونواهيه وأن يعامل بالنَّصفة والسَّويَّة وآلةُ ذلك الميزان ثمَّ قال: {وما يدريك لعلَّ الساعة قريب} أَيْ: فاعمل بالعدل والكتاب فلعلَّ السَّاعة قد قربت منك وأنت لا تدري',NULL,'963',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4291,'{يستعجل بها الذين لا يؤمنون بها} ظنَّاً منهم أنَّها غير كائنة {والذين آمنوا مشفقون} خائفون منها لأنَّهم يعلمون أنَّهم مبعوثون ومحاسبون {ألا إنَّ الذين يمارون} تدخلهم المِرية والشَّكُّ {في الساعة لفي ضلال بعيد} لأنَّهم لو فكَّروا لعلموا أنَّ الذي أنشأهم أوَّلاً قادرٌ على إعادتهم',NULL,'963',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4292,'{الله لطيف بعباده} حفيٌّ بارٌّ بهم بَرِّهم وفاجرِهم حيث لم يقتلهم جُوعاًَ بمعاصيهم',NULL,'963',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4293,'{مَنْ كان يريد حرث الآخرة} من أراد بعمله الآخرة {نزد له في حرثه} أَيْ: كسبه بالتَّضعيف بالواحدة عشراً {ومَنْ كان يريد حرث الدنيا} بعمله الدنيا {نؤته منها وما له في الآخرة من نصيب} أَيْ: مَنْ آثر دنياه على آخرته لم نجعل له نصيباً في الآخرة',NULL,'964',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4294,'{أم لهم} بل أَلهم {شركاء} آلهةٌ {شرعوا لهم من الدين ما لم يأذن به الله ولولا كلمة الفصل} أَيْ: القَدَر السَّابق بأنَّ القضاء والجزاء يوم القيامة {لقضي بينهم} في الدُّنيا',NULL,'964',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4295,'{ترى الظالمين} المشركين يوم القيامة {مشفقين} خائفين {ممَّا كسبوا} أَيْ: من جزائه {وهو واقع بهم} لا محالة وقوله:',NULL,'964',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4296,'{قُلْ لا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا} أَيْ: على تبليغ الرِّسالة {أجراً إلاَّ المودَّة في القربى} أيْ: إلاَ أن تحفظوا قرابتي وتَوَدُّوني وتصلوا رحمي وذلك أنَّه لم يكن حيٌّ من قريش إلا وللنبي صلى الله عليه وسلم فيهم قرابةٌ فكأنًّه يقول: إذا لم تؤمنوا بي فاحفظوا قرابتي ولا تُؤذوني وقيل: معناه: إلاَّ أَنْ تتودَّدُوا إلى الله عزَّ وجل بما يُقرِّبكم منه وقوله: {إلاَّ المودة} استثناءٌ ليس من الأوَّل {ومن يقترف} يعمل {حسنة نزدْ له فيها حسناً} نضاعفها له',NULL,'964',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4297,'{أم يقولون} بل أيقولون يعني: أهل مكَّة {افترى على الله كذباً} تقوَّل القرآن من قبل نفسه {فإنْ يشأ اللَّهُ يختم على قلبك} يربط على قلبك بالصَّبر على أذاهم ثم ابتدأ فقال {ويمح الله الباطل} أَيْ: الشِّرك {ويحق الحق بكلماته} بما أنزله الله من كتابه على لسان نبيِّه عليه السَّلام وهو القرآن',NULL,'964',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4298,'{وهو الذي يقبل التوبة عن عباده} إذا رجع العبد عن معصية الله تعالى إلى طاعته قَبِلَ ذلك الرُّجوع وعفا عنه ما سلف وهو قوله: {ويعفو عن السيئات}',NULL,'965',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4299,'{ويستجيب الذين آمنوا} أَيْ: يُجيبهم إلى ما يسألون',NULL,'965',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4300,'{ولو بسط الله الرزق لعباده} أَيْ: وسَّع عليهم الرِّزق {لبغوا في الأرض} لطغوا وعصوا {ولكن ينزِّل بقدر ما يشاء} فيجعل واحداً فقيراً وآخر غنيَّاً {إنَّه بعباده خبيرٌ بصير}',NULL,'965',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4301,'{وهو الذي ينزل الغيث} المطر {من بعد ما قنطوا} من بعدِ يأسِ العباد من نزوله {وينشر رحمته} ويبسط مطره',NULL,'965',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4302,'{ومن آياته} دلائل قدرته {خلق السماوات والأرض وما بثَّ} فرَّق ونشر {فيهما من دابة وهو على جمعهم} للحشر {إذا يشاء قدير}',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4303,'{وما أصابكم من مصيبة} بليَّةٍ وشدَّةٍ {فبما كسبت أيديكم} فهي جزاء ما اكتسبتم من الإِجرام {ويعفو عن كثير} فلا يُجازي عليه',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4304,'{وما أنتم بمعجزين في الأرض} هرباَ اَيْ: إنِْ هربتم لم تعجزوا الله في أخذكم',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4305,'{ومن آياته الجوار} السُّفن التي تجري {في البحر كالأعلام} كالجبال في العظم',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4306,'{إن يشأ يسكن الريح فيظللن} فيصرن {رواكد} ثوابت على ظهر البحر لا تجري {إِنَّ فِي ذَلِكَ لآيَاتٍ لِكُلِّ صَبَّارٍ شَكُورٍ} لكلِّ مؤمنٍ',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4307,'{أو يوبقهن} يُهلكهنَّ يعني: أهلها {بما كسبوا} من الذُّنوب {ويعف عن كثير} فلا يعاقب عليها',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4308,'{ويعلم الذين يجادلون في آياتنا} أَيْ: في دفعها وإبطالها {ما لهم من محيص} مهربٍ من عذاب الله',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4309,'{فما أوتيتم من شيء} من أثاثِ الدُّنيا {فمتاع الحياة الدنيا} يتمتَّع به في هذه الدَّار {وَمَا عِنْدَ اللَّهِ} من الثَّواب {خير وأبقى للذين آمنوا} نزلت في أبي بكر الصديق رضي الله عنه حين أنفق جميع ماله وتصدَّق به فلامه النَّاس',NULL,'966',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4310,'{والذين يجتنبون} عطفٌ على قوله: {للذين آمنوا} {كبائر الإِثم والفواحش} الشِّرك وموجبات الحدود {وإذا ما غضبوا هم يغفرون} يتجاوزون ويحملون',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4311,'{والذين استجابوا لربهم} أجابوه بالإِيمان والطَّاعة {وأمرهم شورى بينهم} لا ينفردون برأيهم بل يتشاورون',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4312,'{والذين إذا اصابهم البغي} الظُّلم {هم ينتصرون} ينتقمون ممَّن ظلمهم ثمَّ بيَّن حدَّ الانتصار فقال:',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4313,'{وجزاء سيئة سيئة مثلها} أَيْ: إنما يُجازى السُّوء بمثله فيقتصُّ من الجاني بمقدار جنايته {فمن عفا} ترك الانتقام {وأصلح} بينه وبين الظَّالم عليه بالعفو {فأجره على الله} أَيْ: إنَّ الله يأجره على ذلك {إنَّه لا يحب الظالمين} الذين يبدؤون بالظُّلم',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4314,'{ولمن انتصر بعد ظلمه} أَيْ: بعد أنْ ظُلم {فأولئك ما عليهم من سبيل} باللَّوم ولا القصاص لأنه أخذ حقه',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4315,'{إنما السبيل على الذين يظلمون الناس ويبغون في الأرض بغير الحق أولئك لهم عذاب أليم}',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4316,'{ولمن صبر} على الأذى {وغفر} ولم يكافئ {إن ذلك} أَيْ: الصَّبر والغفران {لمن عزم الأمور} لأنَّه يوجب الثَّواب فهو أتم عزم وقوله:',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4317,'{ومن يضلل الله فما له من ولي من بعده وترى الظالمين لما رأوا العذاب يقولون هل إلى مرد من سبيل}',NULL,'967',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4318,'{وتراهم يعرضون عليها} على النَّار {خاشعين من الذل} مُتواضعين ساكنين {ينظرون} إلى النَّار {من طرف خفي} مسارقة',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4319,'{وما كان لهم من أولياء ينصرونهم من دون الله ومن يضلل الله فما له من سبيل}',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4320,'{استجيبوا لربكم} بالإيمان والطَّاعة {مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا مَرَدَّ له من الله} أَيْ: إنَّ الله تعالى إذا أتى به لم يرده {ما لكم من ملجأ يومئذٍ} مهربٍ من العذاب {وما لكم من نكير} إنكارٌ على ما ينزل بكم من العذاب لا تقدرون أن تنكروه فتغيروه وقوله:',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4321,'{فإن أعرضوا فما أرسلناك عليهم حفيظاً إن عليك إلا البلاغ وإنا إذا أذقنا الإنسان منا رحمة فرح بِهَا وَإِنْ تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ فإن الإنسان كفور}',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4322,'{لله ملك السماوات والأرض يخلق ما يشاء يهب لمن يشاء إناثا ويهب لمن يشاء الذكور}',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4323,'{أو يزوجهم ذكراناً وإناثاً} أَيْ: يجعل ما يهب من الولد بعضه ذكوراً وبعضه إناثاً {ويجعل من يشاء عقيماً} لا يُولد له',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4324,'{وما كان لبشر أن يكلمه الله إلاَّ وحياً} بأن يوحي إليه في منامه {أو من وراء حجاب} كما كلَّم موسى عليه السَّلام {أو يرسل رسولاً} مَلَكاً {فيوحيَ بإذنه ما يشاء} فيكلِّمه عنه بما يشاء',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4325,'{وكذلك} وكما أوحينا إلى سائر الرُّسل {أوحينا إليك روحاً} ما يحيا به الخلق أَيْ: يهتدون به وهو القرآن {من أمرنا} : أَيْ: فِعْلِنا في الوحي إليك {ما كنت تدري ما الكتاب ولا الإيمان} قبل الوحي ويعني بالإيمان شرائعه ومعالمه {ولكن جعلناه} جعلنا الكتاب {نوراً} وقوله: {وإنك لتهدي} بوحينا إليك {إلى صراط مستقيم} يعني الإسلام',NULL,'968',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4326,'{صراط الله الذي له ما في السماوات وما في الأرض ألا إلى الله تصير الأمور}',NULL,'969',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4327,'{حم}',NULL,'970',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4328,'{والكتاب المبين} الذي أبان الهدى وما تحتاج إليه الأُمَّة {إنا جعلناه} بيَّناه {قرآنا عربيا} بلغة العرب {لعلكم تعقلون} تعرفون أحكامه ومعانيه',NULL,'970',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4329,'{إنا جعلناه قرآنا عربيا لعلكم تعقلون}',NULL,'970',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4330,'{وإنه} أَيْ: القرآن {في أمِّ الكتاب} أَيْ: اللَّوح المحفوظ {لدينا لعليٌّ حكيم} يريد: إنَّه مثبتٌ عند الله تعالى في اللَّوح المحفوظ بهذه الصِّفة',NULL,'970',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4331,'{أفنضرب عنكم الذكر صفحاً} أَفنمسك عن إنزال القرآن ونتركه من أجل أنَّكم لا تؤمنون به وهو قوله: {أن كنتم قوماً مسرفين} أَيْ: لأن كنتم قوماً مُشركين مُجاوزين أمر الله قال قتادة رضي الله عنه: واللَّهِ لو أنَّ هذا القرآنَ رُفع حين ردَّه أوائل هذا الأمة لهلكوا',NULL,'970',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4332,'{وكم أرسلنا من نبي في الأولين}',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4333,'{وما يأتيهم من نبي إلاَّ كانوا به يستهزئون}',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4334,'{فأهلكنا أشد منهم} من قومك {بطشاً} قوَّة {ومضى مثل الأولين} سنَّتهم في العقوبة',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4335,'{ولئن سالتهم من خلق السماوات والأرض ليقولن خلقهن العزيز العليم}',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4336,'{الذي جعل لكم الأرض مهدا وجعل لكم فيها سبلا لعلكم تهتدون}',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4337,'{والذي نزل من السماء ماء بقدر} بمقدار معلوم عند الله {فأنشرنا} فأحيينا {به} بذلك الماء {بلدة ميتاً كذلك تخرجون} من قبوركم أحياء',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4338,'{والذي خلق الأزواج} الأصناف {كلها} وقوله:',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4339,'{وما كنَّا له مقرنين} أي: مطيقين',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4340,'{وإنا إلى ربنا لمنقلبون}',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4341,'{وجعلوا له من عباده جزءاً} أَيْ: الذين جعلوا الملاكة بنات الله',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4342,'{أم اتخذ ممَّا يخلق بنات وأصفاكم} أخلصكم وخصَّكم {بالبنين} كقوله: {أفأصفاكم ربُّكم بالبنين} الآية',NULL,'971',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4343,'{وإذا بشر أحدهم بما ضرب للرحمن مثلاَ} بما وصفه به من اتِّخاذ البنات',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4344,'{أو مَنْ يُنشَّأ في الحلية} أَيْ: أَنسبوا إليه مَنْ يُنشَّأ في الحلية؟ يعني: البنات {وهو في الخصام غير مبين} وذلك أنَّ المرأة لا تكاد تقوم بحجَّةٍ في الخصومة',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4345,'{وجعلوا الملائكة الذين هم عباد الرحمن إناثاً} أَيْ: حكموا بأنَّهم إناثٌ حين قالوا: إنَّهم بنات الله {أشهدوا} أَحضروا {خلقهم} حين خُلقوا؟ {ستكتب شهادتهم} على الملائكة بأنَّهم بنات الله {ويسألون} عنها',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4346,'{وقالوا: لو شاء الرحمن ما عبدناهم} أَيْ: الملائكة وذلك أنَّهم قالوا: لو لم يرض منَّا بعبادتنا إيَّاها لعجَّل عقوبتنا {ما لهم بذلك من علم} ما لهم بقولهم: الملائكة بناتُ الله من علمٍ {إن هم إلاَّ يخرصون} يكذبون',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4347,'{أم آتيناهم كتاباً من قبله} من قبل القرآن فيه عبادة غير الله {فهم به مستمسكون} بذلك الكتاب ثمَّ بيَّن أنَّهم اتَّبعوا ضلالة آبائهم فقال:',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4348,'{بل قالوا إنا وجدنا آباءنا على أمة} دينٍ',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4349,'{وكذلك ما أرسلنا من قبلك فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلا قَالَ مُتْرَفُوهَا إنا وجدنا آباءنا على أمة وإنا على آثارهم مقتدون}',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4350,'{قال أو لو جئتكم بأهدى} بدينٍ أهدى {ممَّا وجدتم عليه آباءكم} أَتتبعونهم؟ {قالوا} أَيْ: الأمم للرُّسل: {إنا بما أرسلتم به كافرون}',NULL,'972',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4351,'{فانتقمنا منهم} بالعقوبة',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4352,'{وإذ قال إبراهيم لأبيه وقومه: إنني براء} أي: بريء',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4353,'{إلا الذي فطرني فإنه سيهدين}',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4354,'{وجعلها كلمة} أَيْ: كلمة التَّوحيد {باقية في عقبه} عقب إبراهيم عليه والسلام لا يزال من ولده مَنْ يوحِّدُ الله عزَّ وجل {لعلهم يرجعون} كي يرجعوا بها من الكفر إلى الإيمان',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4355,'{بل متعتُ هؤلاء وآباءهم} في الدُّنيا ولم أهلكهم {حتى جاءهم الحق} القرآن',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4356,'{ولما جاءهم الحق قالوا هذا سحر وإنا به كافرون}',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4357,'{وقالوا لولا نزل هذا القرآن على رجل من} إحدى {القريتين} مكَّة والطَّائف {عظيم} أَيْ: الوليد بن المغيرة من أهل مكَّة وعروة بن مسعود الثقفيِّ من الطَّائف قال الله تعالى:',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4358,'{أهم يقسمون رحمة ربك} نبوَّته وكرامته فيجعلونها لمن يشاؤون؟ {نحن قسمنا بينهم معيشتهم في الحياة الدنيا} فجعلنا بعضهم غنيَّاً وبعضهم فقيراً {ورفعنا بعضهم فوق بعض درجات} بالمال {ليتخذ بعضهم بعضاً سخرياً} ليُسخِّر الأغنياء بأموالهم الفقراء ويستخدموهم فيكون بعضهم لبعض سببَ المعاش في الدُّنيا هذا بماله وهذا بأعماله فكما قسمنا هذه القسمة كذلك اصطفينا للرِّسالة مَنْ نشاء ثمَّ بيَّن أنَّ الآخرة أفضل من الدُّنيا فقال: {ورحمة ربك} أَيْ: الجنَّة {خيرٌ ممَّا يجمعون} في الدُّنيا ثمَّ ذكر قلَّة خطر الدُّنيا عنده فقال:',NULL,'973',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4359,'{ولولا أن يكون الناس أمة واحدة} مجتمعين على الكفر وقوله: و {معارج} : مراقي {عليها يظهرون} يعلون يصعدون',NULL,'974',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4360,'{ولبيوتهم أبواباً وسرراً} من فضَّةٍ {عليها يتكئون}',NULL,'974',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4361,'{وزخرفاً} أَيْ: ومن زخرفٍ وهو الذَّهب {وإن كلُّ ذلك لما متاع الحياة الدنيا} لمتاع الحياة الدُّنيا',NULL,'974',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4362,'{ومَنْ يعش} يُعرض {عن ذكر الرحمن نقيض له شيطاناً} نسبِّب له شيطاناً {فهو له قرين} لا يُفارقه',NULL,'974',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4363,'{وإنهم} أَيْ: الشَّياطين {ليصدونهم} يمنعون الكافرين {ويحسبون} الكفَّار {أنهم مهتدون}',NULL,'974',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4364,'{حتى إذا جاءنا} يعني: الكافر {قال} لقرينه: {يا ليت بيني وبينك بعد المشرقين} أَيْ: بُعد ما بين المشرق والمغرب {فبئس القرين} أنت ثم لا يفارقه حتى يصيروا إلى النار وقال الله تعالى:',NULL,'974',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4365,'{ولن ينفعكم اليوم إذ ظلمتم} أشركتم في الدُّنيا {أنكم في العذاب مشتركون} إشراككم في العذاب لأنَّ لكلِّ واحدٍ نصيبَه الأوفرَ منه',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4366,'{أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين}',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4367,'{فإما نذهبن بك} تميتك قبل أن نعذِّبهم {فإنا منهم منتقمون} بعد موتك',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4368,'{أو نرينك} في حياتك {الذي وعدناهم} من العذاب',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4369,'{فاستمسك بالذي أوحي إليك إنك على صراط مستقيم}',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4370,'{وإنه} أَيْ: القرآن {لذكر} لَشرفٌ {لك ولقومك} إذ نزل بلغتهم ونزل عليك وأنت منهم {وسوف تسألون} عن شكر ما جعلنا لكم من الشَّرف',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4371,'{واسأل من أرسلنا} أَيْ: أمم مَنْ أرسلنا {من قبلك} يعني: أهل الكتابين هل في كتاب أحدٍ الأمرُ بعبادة غير الله تعالى؟ ومعنى هذا السُّؤال التَّقرير لعبدة الأوثان أنَّهم على الباطل',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4372,'{ولقد أرسلنا موسى بآياتنا إلى فرعون وملئه فقال إني رسول رب العالمين}',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4373,'{فلما جاءهم بآياتنا إذا هم منها يضحكون}',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4374,'{وما نريهم من آية إلاَّ هي أكبرُ من أختها} قرينتها وصاحبتها التي كانت قبلها {وأخذناهم بالعذاب} بالسِّنين والطُّوفان والجراد {لعلهم يرجعون} عن كفرهم',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4375,'{وقالوا يا أيها الساحر} خاطبوه بما تقدَّم له عندهم من التَّسمية بالسَّاحر: {ادع لنا ربك بما عهد عندك} فيمن آمن به مِنْ كشف العذاب عنه {إننا لمهتدون} أَيْ: مؤمنون',NULL,'975',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4376,'{فلما كشفنا عنهم العذاب إذا هم ينكثون} ينقصون عهدهم وقوله:',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4377,'{وهذه الأنهار تجري من تحتي} بأمري: وقيل: من تحت قصوري',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4378,'{أم أنا} بل أنا {خير من هذا الذي هو مهين} حقيرٌ ضعيفٌ يعني: موسى {ولا يكاد يبين} يُفصح بكلامه لِعِيِّه',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4379,'{فلولا} فهلاًّ {ألقي عليه أسورة من ذهب} حليٌّ بأساور الذَّهب إن كان رئيساً مُطاعاً؟ والطَّوق والسِّوار من الذَّهب كان من علامة الرِّئاسة عندهم {أو جاء معه الملائكة مقترنين} مُتتابعين يشهدون له',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4380,'{فاستخف قومه} وجد قومه القبط جُهَّالاً',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4381,'{فلما آسفونا} أغضبونا بكفرهم {انتقمنا منهم}',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4382,'{فجعلناهم سلفاً} مُتقدِّمين في الهلاك ليتَّعظ بهم مَنْ بعدهم {ومثلاً للآخرين} عبرةً لن يجيء بعدهم',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4383,'{ولما ضرب ابن مريم مثلاً} نزلت هذه الآية حين خاصمه الكفَّار لما نزل قوله تعالى: {إنَّكم وما تعبدون من دون الله} الآية فقالوا: رضينا أن يكون آلهتنا بمنزلة عيسى فجعلوا عيسى عليه السَّلام مثلاً لآلهتهم فقال الله تعالى: {ولما ضرب ابن مريم مثلاً إذا قومك منه يَصِدًّون} أَيْ: يضجُّون وذلك أنَّ المسلمين ضجُّوا من هذا حتى نزل قوله تعالى: {إِنَّ الَّذِينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنَى أُولَئِكَ عنها مُبعدون} وذكر الله تعالى في هذه السُّورة تلك القصَّة وهو قوله:',NULL,'976',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4384,'{وقالوا أآلهتنا خيرٌ أم هو} يعني: عيسى عليه السَّلام {ما ضربوه لك إلاَّ جدلاً} أَيْ: إلاَّ الإرادة للمجادلة لأنَّهم علموا أنَّ المراد بحصب جهنم ما اتَّخذوه من الموات {بل هم قوم خصمون} يجادلون بالباطل ثمَّ بيَّن حال عيسى عليه السَّلام فقال:',NULL,'977',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4385,'{إن هو إلاَّ عبد أنعمنا عليه وجعلناه مثلاً لبني إسرائيل} آيةً تدلُّ على قدرة الله',NULL,'977',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4386,'{ولو نشاء لجعلنا منكم} بدلكم {ملائكة في الأرض يخلفون} بأن نهلككم ونأتي بهم بدلاً منكم يكونون خلفاء منكم',NULL,'977',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4387,'{وإنه} أَي: وإنَّ عيسى {لعلم للساعة} بنزوله يُعلم قيام السَّاعة {فلا تمترنَّ بها} لا تشكُّوا فيها',NULL,'977',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4388,'{ولا يصدنكم الشيطان إنَّه لكم عدوٌ مبين}',NULL,'977',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4389,'{ولما جاء عيسى} إلى بني إسرائيل {بالبينات} بالآيات التي يعجز عنها المخلوقون {قال قد جئتكم بالحكمة} أَيْ: الإنجيل {ولأبين لكم بعض الذي تختلفون فيه} أي: كله',NULL,'977',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4390,'{إن الله هو رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ هَذَا صِرَاطٌ مُسْتَقِيمٌ}',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4391,'{فاختلف الأحزاب} الآية مفسَّرةٌ في سورة مريم',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4392,'{هل ينظرون} أَيْ: يجب ألا ينتظروا بعد تكذيبك {إلاَّ} أن يَفْجَأَهُمْ قيام {السَّاعة} ثمَّ ذكر أنَّ مخالَّتهم في الدُّنيا تبطل في ذلك اليوم وتنقلب عداوة فقال:',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4393,'{الأَخِلاءُ يَوْمَئِذٍ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلا الْمُتَّقِينَ} وهم المؤمنون وقوله:',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4394,'{يا عباد لا خوف عليكم اليوم ولا أنتم تحزنون}',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4395,'{الذين آمنوا بآياتنا وكانوا مسلمين}',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4396,'{تحبرون} تُكرمون وتسرُّون',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4397,'{يطاف عليهم بصحاف} بقصاع وأكوابٍ وهي الأواني التي لا عُرى لها {وفيها ما تشتهيه الأنفس وتلذ الأعين} أَيْ: تستلذًّ وهذا وصفٌ لجميع ما في الجنَّة من الطيبات وقوله:',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4398,'{وتلك الجنة التي أورثتموها بما كنتم تعملون}',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4399,'{لكم فيها فاكهة كثيرة منها تأكلون}',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4400,'{إن المجرمين في عذاب جهنم خالدون}',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4401,'{لا يفتر عنهم} أَيْ: لا يخفف عنهم العذاب {وهم فيه مبلسون} ساكنون سكوت يأس',NULL,'978',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4402,'{وما ظلمناهم ولكن كانوا هم الظالمين}',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4403,'{ونادوا يا مالك ليقضِ علينا ربك} ليمتنا فنستريح {قال: إنكم ماكثون} مُقيمون في العذاب',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4404,'{لقد جئناكم بالحق ولكن أكثركم للحق كارهون}',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4405,'{أم أبرموا أمراً} أحكموا الأمر في المكر بمحمَّد عليه السَّلام {فإنا مبرمون} مُحكمون أمراً في مجازاتهم',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4406,'{أم يحسبون أنا لا نسمع سرَّهم ونجواهم بلى ورسلنا لديهم يكتبون}',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4407,'{قل: إن كان للرحمن ولد} الآية معناها: إنْ كنتم تزعمون أنَّ للرحمن ولداً فأنا أوَّل الموحِّدين لأنَّ مَنْ عبد الله واعترف بأنَّه إلهه فقد دفع أن يكون له ولد وقيل: {فأنا أول العابدين} الآنفين من هذا القول',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4408,'{سبحان رب السماوات والأرض رب العرش عما يصفون}',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4409,'{فذرهم يخوضوا ويلعبوا حتى يُلاقوا يومهم الذي يوعدون}',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4410,'{وهو الذي في السماء إله} يُعبد {وفي الأرض إله} يُعبد أيْ: هو المعبود فيهما {وهو الحكيم} في تدبير خلقه {العليم} بصلاحهم',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4411,'{وتبارك الذي له ملك السماوات والأرض وما بينهما وعنده علم الساعة وإليه ترجعون}',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4412,'{ولا يملك الذين يدعون من دونه الشفاعة} أَيْ: الأوثان لا يشفعون لعابديها {إلاَّ مَنْ شهد بالحق} يعني: عيسى وعزيزا والملائكة فلهم الشَّفاعة في المؤمنين لا في الكفَّار وهم يشهدون بالحقِّ بالوحدانيَّة لله {وهم يعلمون} حقيقة ما شهدوا به',NULL,'979',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4413,'{ولئن سألتهم من خلقهم ليقولن الله فأنى يؤفكون}',NULL,'980',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4414,'{وقيله} أَيْ: ويسمع قول محمَّد عليه السَّلام شاكياًَ إلى ربِّه وهو راجعٌ إلى قوله {أنا لا نسمع سرَّهم ونجواهم}',NULL,'980',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4415,'{فاصفح عنهم} أَيْ: أعرض عنهم وهذا قبل أن يؤمر بقتالهم {وقل سلام} أيْ: سلامةٌ لنا منكم {فسوف تعلمون} تهديدٌ لهم',NULL,'980',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4416,'{حم}',NULL,'981',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4417,'{والكتاب المبين}',NULL,'981',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4418,'{إنا أنزلناه} أَيْ: القرآن {في ليلة مباركة} قيل: هي ليلة القدر في رمضان أنزل الله القرآن فيها من أمِّ الكتاب إلى سماء الدُّنيا ثمَّ أنزله على نبيِّه عليه السَّلام نجوماَ وقيل: ليلة النِّصف من شعبان {إنا كنا منذرين} مُحذِّرين عبادنا العقوبة بإنزال الكتاب',NULL,'981',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4419,'{فيها يفرق} يُفصل {كلُّ أمر حكيم} مُحكمٍ من أرزاق العباد وآجالهم وذلك أنَّه يُدبِّر في تلك الليلة أمر السَّنة',NULL,'981',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4420,'{أمراً من عندنا} معناه: يُفْرق كلُّ أمرٍ حكيمٍ فرقاً من عندنا فوضع الأمر موضع الفرق لأنَّه أمرٌ {إنا كنا مرسلين} محمَّداً إلى قومه',NULL,'981',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4421,'{رحمةً} أَيْ: للرًّحمة وقوله:',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4422,'{إن كنتم موقنين} أَيْ: إن أيقنتم بأنَّه ربُّ السماوات والأرض فأيقنوا أنَّ محمداً رسوله لأنه أرسله',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4423,'{لا إله إلا هو يحيي ويميت ربكم وربُّ آبائكم الأولين}',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4424,'{بل هم في شك} من البعث والنَّشر {يلعبون} مُشتغلين بالدُّنيا',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4425,'{فارتقب} فانتظر {يوم تأتي السماء بدخان مبين} وذلك حين دعا رسول الله صلى الله عليه وسلم على قومه بالقحط فمنع القطر وأجدبت الأرض وانجرَّت الآفاق وصار بين السَّماء والأرض كالدُّخان',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4426,'{يغشى الناس} ذلك الدخان وهم يقولون: {هذا عذاب أليم}',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4427,'{ربنا اكشف عنا العذاب إنا مؤمنون} مُصدِّقون بنبيِّك قال الله تعالى:',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4428,'{أنى لهم الذكرى} من أين لهم التَّذكُّر والاتِّعاظ {و} حالهم أنَّهم {قد جاءهم رسول مبين} يبيِّن لهم أحكام الدِّين يعني: محمدا صلى الله عليه وسلم',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4429,'{ثمَّ تولوا} أعرضوا {عنه وقالوا معلَّم} أَيْ: إنَّه معلَّم يُعلِّمه ما يأتي به بشر',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4430,'{إنا كاشفوا العذاب قليلاً} أَيْ: يكشف عنكم عذاب الجوع في الدُّنيا ثمَّ تعودون في العذاب وهو قوله: {إنكم عائدون}',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4431,'{يوم نبطش البطشة الكبرى} أَيْ: يوم القيامة وقيل: يوم بدرٍ',NULL,'982',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4432,'{ولقد فتنا} بلونا {قبلهم قوم فرعون وجاءهم رسول كريم} على الله تعالى يعني: موسى عليه السَّلام',NULL,'983',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4433,'{أن أدوا إليَّ عباد الله} أَيْ: سلِّموهم إليَّ ولا تُعذِّبوهم يعني: بني إسرائيل كما قال: {فأرسل معي بني إسرائيل} {إني لكم رسول أمين} على وحي الله عز وجل',NULL,'983',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4434,'{وأن لا تعلوا على الله} لا تعصوه ولا تخالفوا أمره {إني آتيكم بسلطان مبين} بحجة واضحة تدلُّ على أنَّني نبيٌّ',NULL,'983',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4435,'{وإني عذت بربي وربكم أن ترجمون} أَن تقتلون وذلك أنَّهم توعَّدوه بالقتل',NULL,'983',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4436,'{وإن لم تؤمنوا لي فاعتزلون} أَيْ: لا تكونوا عليَّ ولا لي وخلُّوا عني',NULL,'983',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4437,'{فدعا ربَّه أنَّ} أَيْ: بأنَّ {هؤلاء} أَيْ: يا ربِّ هؤلاء {قوم مجرمون} مشركون فقال الله تعالى:',NULL,'983',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4438,'{فأسرِ بعبادي} بني إسرائيل {ليلاً إنكم متبعون} يتَّبعكم فرعون وقومه',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4439,'{واترك البحر رهواً} خلِّفه وراءك ساكناً غير مضطربٍ وذلك أنّ الماء وقف له كالطود العظيم حين جاوز البحر {إنهم جندٌ مغرقون} نغرقهم في ذلك البحر الذي تجاوزوه رهواً',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4440,'{كم تركوا} بعد هلاكهم {من جنات وعُيُون} الآية مُفسَّرةٌ في سورة الشُّعراء',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4441,'{وزروع ومقام كريم}',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4442,'{ونعمة كانوا فيها فاكهين}',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4443,'{كذلك} أَيْ: الأمر كما وصفنا {وأورثناها} أعطيناها {قوماً آخرين} يعني: بني إسرائيل',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4444,'{فما بكت عليهم السماء والأرض} لأنَّهم ماتوا كفَّاراً والمؤمن يبكي عليه مصعد عمله ومُصلاَّه من الأرض {وما كانوا منظرين} مؤخَّرين حين أخذناهم بالعذاب',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4445,'{ولقد نجينا بني إسرائيل} بإهلاك فرعون وقومه {من العذاب المهين} يعني: قتل الأبناء واستخدام النِّساء',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4446,'{من فرعون إنه كان عالياً} مستكبراً مُتعظِّماً {من المسرفين} الكافرين المُتجاوزين حدِّهم',NULL,'984',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4447,'{ولقد اخترناهم} بني إسرائيل {على علمٍ} منَّا بهم {على العالمين} عالمي زمانهم',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4448,'{وآتيناهم من الآيات ما فيه بلاء مبين} نعمةٌ ظاهرةٌ من فلق البحر وإنزال المنِّ والسَّلوى',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4449,'{إنَّ هؤلاء} أَيْ: مشركي مكَّة {ليقولون:}',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4450,'{إن هي إلاَّ موتتنا الأولى} أَيْ: ليس إلاَّ الموت ولا نشر بعده وهو قوله: {وما نحن بمنشرين}',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4451,'{فأتوا بآبائنا} الذين ماتوا {إن كنتم صادقين} أنَّا نُبعث بعد الموت',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4452,'{أهم خير} أَيْ: أقوى وأشدُّ {أم قوم تبع} الحِميريِّ {والذين من قبلهم} من الكفَّار {أهلكناهم}',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4453,'{وما خلقنا السماوات والأرض وما بينهما لاعبين} ونحن نلعب في خلقهما أَيْ: إنَّما خلقناهما لأمرٍ عظيم وهو قوله: {ما خلقناهما إلاَّ بالحق} أَيْ: لإقامة الحقِّ وإظهاره من توحيد الله وإلزام طاعته',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4454,'{ما خلقناهما إلاَّ بالحق ولكن أكثرهم لا يعلمون}',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4455,'{إنَّ يوم الفصل} وهو يوم القيامة يفصل الله تعالى فيه بين العباد {ميقاتهم} الذي وقَّتنا لعذابهم {أجمعين}',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4456,'{يوم لا يغني مولى عن مولى شيئاً} قريبٌ عن قريبٍ {ولا هم ينصرون} يُمنعون من عذاب الله',NULL,'985',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4457,'{إلا من رحم} لكن مَنْ رحم الله فإنَّه يُنصر',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4458,'{إنَّ شجرة الزَّقوم}',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4459,'{طعام الأثيم} أَيْ: صاحب الإثم وهو أبو جهل',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4460,'{كالمهل} أَيْ: كالذَّائب من الفضَّة والنُّحاس في الحرارة {يَغلي في البطون} في بطون آكليه',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4461,'{كغلي الحميم} وهو الماء الحارُّ',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4462,'{خذوه} يعني: الأثيم {فاعتلوه} سوقوه سوقاً بالعنف {إلى سواء الجحيم} وسط الجحيم',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4463,'{ثمَّ صبوا فوق رأسه من عذاب الحميم} كما قال: {يصبُّ من فوقِ رؤوسهم الحميم} ويقال له:',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4464,'{ذق إنك أنت العزيز الكريم} بزعمك وعلى قولك وذلك أنَّه قال: ما بين جبليها أعزُّ ولا أكرم مني',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4465,'{إنَّ هذا} الذي ترون من العذاب {ما كنتم به تمترون} فيه تشكُّون',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4466,'{إنَّ المتقين في مقام أمين} أمنوا فيه من الغير',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4467,'{في جنات وعيون}',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4468,'{يلبسون من سندس} وهو ما رقَّ من الثّياب {وإستبرق} وهو ما غلظ منه {متقابلين} متواجهين',NULL,'986',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4469,'{كذلك} كما وصفنا {وزوجناهم بحور} وهنَّ النِّساء النَّقيات البياض {عين} واسعة الأعين',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4470,'{يدعون فيها بكلِّ فاكهة آمنين} من الموت',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4471,'{لا يذوقون فيها الموت إلاَّ} سوى {الموتة الأولى} الموتة التي ذاقوها في الدنيا',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4472,'{فضلا من ربك ذلك هو الفوز العظيم}',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4473,'{فإنما يسرناه} سهَّلنا القرآن {بلسانك لعلهم يتذكرون} يتَّعظون',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4474,'{فارتقب} فانتظر الفتح والنَّصر {إنهم مرتقبون} مُنتظرون قهرك وهلاكك',NULL,'987',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4475,'{حم}',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4476,'{تنزيل الكتاب من الله العزيز الحكيم}',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4477,'{إن في السماوات والأرض} أَيْ: إنَّ في خلقهما {لآيات} لدلالاتٍ على قدرة الله وتوحيده وقوله:',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4478,'{وفي خلقكم وما يبث من دابة آيات لقوم يوقنون}',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4479,'{واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون}',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4480,'{فبأيِّ حديث بعد الله} أَيْ: بعد حديث الله وكتابه {يؤمنون}',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4481,'{ويلٌ لكلِّ أفاك أثيم} كذَّاب صاحب إثمٍ',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4482,'{يسمع آيات الله تتلى عليه ثمَّ يصرُّ} يُقيم على كفره {مستكبراً} مُتعظِّماً عن الإيمان به',NULL,'988',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4483,'{وإذا علم من آياتنا شيئاً اتخذها هزواً} استهزأ بها',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4484,'{ومن ورائهم} أمامهم {جهنم ولا يغني عنهم ما كسبوا} من الأموال {شيئاً}',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4485,'{هذا هدى} أَيْ: هذا القرآن هدىً {والذين كفروا بآيات ربهم لهم عذابٌ من رجز أليم} مُؤلمٌ مرجع وقوله:',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4486,'{الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون}',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4487,'{جميعاً منه} أيْ: كلُّ ذلك تفضُّلٌ منه وإحسانٌ',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4488,'{قل للذين آمنوا يغفروا للذين لا يرجون أيام الله} نزلت قبل الأمر بالقتال يقول: قل لهم يصفحوا عن المشركين الذين لا يخافون عقوبة الله وعذابه {ليجزي قوماً} أَيْ: ليجزيهم {بما كانوا يكسبون} من سوء أعمالهم وقوله:',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4489,'{من عمل صالحا فلنفسه ومن أساء فعليها ثم إلى ربكم ترجعون}',NULL,'989',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4490,'{ورزقناهم من الطيبات} أَيْ: المنِّ والسَّلوى',NULL,'990',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4491,'{وآتيناهم بينات من الأمر} يعني: أحكام التَّوراة وبيان أمر النبيِّ عليه السَّلام {فما اختلفوا} في نبوَّته {إِلا مِنْ بَعْدِ مَا جَاءَهُمُ الْعِلْمُ} يعني: ما عملوه من شأنه {بغياً بينهم} حسداً منهم له',NULL,'990',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4492,'{ثم جعلناك على شريعة} مذهبٍ وملَّةٍ {من الأمر} من الدِّين {فاتبعها ولا تتبع أهواء الذين لا يعلمون} مراد الكافرين',NULL,'990',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4493,'{إنهم لن يغنوا عنك من الله شيئاً} لن يدفعوا عنك عذاب الله إن اتَّبعت أهواءهم',NULL,'990',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4494,'{هذا} إشارةٌ إلى القرآن {بصائر} معالم {للناس} في الحدود والأحكام يبصرون بها',NULL,'990',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4495,'{أم حسب الذين اجترحوا السيئات} اكتسبوا الكفر والمعاصي {أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم} مُستوياً حياتهم وموتهم أَيْ: المؤمنُ مؤمنٌ حياً وميتاً والكافر كافرٌ حياً وميتاً فلا يستويان {ساء ما يحكمون} بئس ما يقضون إذ حسبوا أنَّهم كالمؤمنين: نزلت هذه الآية حين قال المشركون: لئن كان ما تقولون حقا لنفضلنَّ عليكم في الآخرة كما فضلنا عليكم في الدنيا',NULL,'990',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4496,'{وخلق الله السماوات والأرض بالحق ولتجزى كل نفس بما كسبت وهم لا يظلمون}',NULL,'991',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4497,'{أفرأيت من اتخذ إلهه هواه} أَيْ: الكافر اتَّخذ دينه ما يهواه فلا يهوى شيئاً إلاَّ ركبه {وأضله الله على علم} على ما سبق في علمه قبل أن يخلقه أنَّه ضالٌّ وباقي الآية مُفسَّر في أوَّل سورة البقرة',NULL,'991',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4498,'{وقالوا} يعني: منكري البعث: {ما هي إلاَّ حياتنا الدنيا} أَيْ: ما الحياة إلاَّ هذه الحياة في دار الدُّنيا {نموت} نحن {ونحيا} أولادنا {وما يهلكنا إلاَّ الدهر} أَيْ: ما يفنينا إلاَّ مرُّ الزَّمان {وما لهم بذلك من علم} أَيْ: الذين يقولون {إن هم إلاَّ يظنون} ما هم إلاَّ ظانِّين ما يقولون',NULL,'991',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4499,'{وإذا تتلى عليهم آياتنا} أَدلَّتنا في قدرتنا على البعث {بينات} واضحات {ما كان حجَّتهم إلاَّ أن قالوا ائتوا بآبائنا إن كنتم صادقين} أن نُبعث بعد الموت وقوله:',NULL,'991',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4500,'{ثمَّ يجمعكم إلى يوم القيامة} أَيْ: مع ذلك اليوم',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4501,'{ولله ملك السماوات والأرض ويوم تقوم الساعة يومئذ يخسر المبطلون}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4502,'{وترى كل أمة} كلم أهلِ دينٍ {جاثية} مُجتمعةً للحساب وقيل: جالسةً على الرُّكَب من هول ذلك اليوم',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4503,'{هذا كتابنا ينطق} أَيْ: ديوان الحفظة {إنا كنا نَستنسِخُ} نأمر بنسخ {ما كنتم تعملون}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4504,'{فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ذلك هو الفوز المبين}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4505,'{وأما الذين كفروا أفلم تكن آياتي تتلى عليكم فاستكبرتم وكنتم قوما مجرمين}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4506,'{وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4507,'{وبدا لهم سَيِّئَاتُ مَا عَمِلُوا وَحَاقَ بِهِمْ مَا كَانُوا به يستهزئون}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4508,'{وقيل اليوم ننساكم} نترككم في العذاب كما تركتم الإيمان والعمل ليومكم هذا وقوله:',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4509,'{ولا هم يستعتبون} أَيْ: لا يُلتمس منهم عمل ولا طاعة',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4510,'{فلله الحمد رب السماوات ورب الأرض رب العالمين}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4511,'{وله الكبرياء} العظمة {في السماوات والأرض} أَيْ: إنَّه يُعظَّم بالعبادة في السموات والأرض {وهو العزيز الحكيم}',NULL,'992',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4512,'{حم}',NULL,'993',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4513,'{تنزيل الكتاب من الله العزيز الحكيم}',NULL,'993',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4514,'{ما خلقنا السماوات والأرض وما بينهما إلاَّ بالحق} أَيْ: للحقِّ ولإِقامة الحقِّ {وأجل مسمَّى} تفنى عند انقضاء ذلك الأجل {والذين كفروا عما أنذروا معرضون} أعرضوا بعدما قامت عليهم الحجَّة بخلق الله السماوات والأرض ثمَّ طالبهم بالدَّليل على عبادة الأوثان فقال:',NULL,'993',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4515,'{قل أرأيتم ما تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا من الأرض أم لهم شرك في السماوات} أَيْ: مشاركةٌ مع الله في خلقهما لذلك أشركتموهم في عبادته {ائتوني بكتاب من قبل هذا} أَيْ: من قبل القرآن فهي بيان ما تقولون {أو أثارة من علم} روايةٍ عن الأنبياء أنَّهم أَمروا بعبادة غير الله فلمَّا قامت عليهم الحجَّة جعلهم أضلَّ الخلق فقال:',NULL,'993',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4516,'{ومَنْ أضلُّ ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة} أَيْ: أبداً الآية',NULL,'994',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4517,'{وإذا حشر الناس كانوا لهم أعداءً} عادوا معبوديهم لأنهم بسبهم وقعوا في الهلكة وجحد المعبودون عبادتهم وهو قوله: {وكانوا بعبادتهم كافرين} كقوله: {تبرأنا إليك ما كانوا إيانا يعبدون} وقوله:',NULL,'994',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4518,'{وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ كفروا للحق لما جاءهم هذا سحر مبين}',NULL,'994',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4519,'{قل إن افتريته فلا تملكون لي من الله شيئا} أي: إن عذَّبني على افترائي لم تملكوا دفعه وإذا كنتم كذلك لم أفتر على الله من أجلكم {هو أعلم بما تفيضون فيه} تخوضون فيه من الإفك {وهو الغفور} لمَنْ تاب {الرحيم} به',NULL,'994',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4520,'{قل ما كنت بدعاً} بديعاً {من الرسل} أَيْ: لستُ بأوَّل مرسل فتنكروا نبوَّتي {وما أدري ما يفعل بي} إلى إيش يصير أمري معكم أتقتلونني أم تخرجونني {ولا بكم} أَتُعذِّبون بالخسف أم الحجارة والمعنى: ما أدري إلى ماذا يصير أمري وأمركم في الدُّنيا',NULL,'994',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4521,'{قل أرأيتم إن كان} القرآن {من عند الله وكفرتم به وشهد شاهدٌ من بني إسرائيل} يعني: عبد الله بن سلام {على مثله} على مثل ما شهد عليه القرآن من تصديق محمَّد عليه السَّلام {فآمن} ذلك الرَّجل {واستكبرتم} عن الإيمان',NULL,'995',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4522,'{وقال الذين كفروا} من اليهود: {لو كان} دين محمَّدٍ {خيراً ما سبقونا إليه} يعنون: عبد الله بن سلام وأصحابه {وإذ لم يهتدوا به} بالقرآن كما اهتدى به أهل الإيمان {فسيقولون هذا إفكٌ قديم} كما قالوا: أساطير الأوَّلين',NULL,'995',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4523,'{ومن قبله} ومن قبل القرآن {كتاب موسى} التَّوراة {إماماً ورحمة وهذا كتاب} أَيْ: القرآن {مصدق} أَيْ: مصدِّقٌ لما بين يديه لما تقدَّم من الكتب {لساناً عربياً} نصب على الحال وقوله:',NULL,'995',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4524,'{إنَّ الذين قالوا ربنا الله ثم استقاموا فلا خوفٌ عليهم ولا هم يحزنون}',NULL,'995',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4525,'{أولئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون}',NULL,'995',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4526,'{حملته أمه كرهاً} على مشقَّةٍ {ووضعته كرهاً} أَيْ: على مشقَّةٍ {وحمله وفصاله ثلاثون شهراً} أقلُّ الحمل ستة أشهر والفِصال: الفِطام ويكون ذلك بعد حولين {حتى إذا بلغ أشده} غاية شبابه وهي ثلاثٌ وثلاثون سنةً {وبلغ أربعين سنةً قال: ربِّ أوزعني} الآية نزلت في أبي بكر رضي الله عنه وذلك أنَّه لمَّا بلغ أربعين سنةً آمن بالنبيِّ صلى الله عليه وسلم وآمن أبوه فذلك قوله: {أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَى والدي} أَيْ: بالإِيمان {وأصلح لي في ذريتي} بأن تجعلهم مؤمنين فاستجاب الله له في أولاده فأسلموا ولم يكن أحدٌ من الصَّحابة أسلم هو وأبوه وبنوه وبناته إلاَّ أبو بكر رضي الله عنه',NULL,'995',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4527,'{أولئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة وعد الصدق الذي كانوا يوعدون}',NULL,'996',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4528,'{والذي قال لوالديه} نزلت في كافرٍ عاقٍّ قال لوالديه: {أَتَعِدانِني أن أخرج} من قبري حيَّاً {وقد خلت القرون من قبلي} فلم يُبعث منهم أحدٌ {وهما يستغيثان الله} يعني: والديه يستغيثان بالله على إيمان ولدهما ويقولان له: {ويلك آمن إنَّ وعد الله حق فيقول ما هذا} الذي تتدعونني إليه {إلاَّ أساطير الأولين}',NULL,'996',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4529,'{أولئك الذين} أَيْ: مَنْ كان بهذه الصِّفة فهم الذين {حق عليهم القول} وجب عليهم العذاب {في أمم} كافرةٍ {من الجن والإِنس}',NULL,'996',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4530,'{ولكلٍّ} من المؤمنين والكافرين {درجات} منازل ومراتب من الثَّواب والعقاب {ممَّا عملوا}',NULL,'996',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4531,'{ويوم يعرض الذين كفروا على النار} فيقال لهم: {أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها} وذلك أنَّهم يفعلون ما يشتهون لا يتوفون حراما ولا يجتنبون مأئما {فاليوم تجزون عذاب الهون} الآية',NULL,'996',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4532,'{واذكر أخا عاد} يعني: هوداً {إذ أنذر قومه بالأحقاف} أَيْ: منازلهم {وقد خلت النذر من بين يديه ومن خلفه} أَيْ: قد أُنذروا بالعذاب أنْ عَبَدوا غيرَ الله قبل إنذار هو وبعده',NULL,'997',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4533,'{قالوا أجئتنا لتأفكنا} لتصرفنا {عن آلهتنا فَأْتِنَا بِمَا تَعِدُنَا} من العذاب {إِنْ كُنْتَ مِنَ الصادقين}',NULL,'997',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4534,'{قال: إنما العلم عند الله} هو يعلم متى يأتيكم العذاب {و} إنما أنا مُبلِّغٌ {أبلغكم ما أرسلت به ولكني أراكم قوماً تجهلون} مراشدكم حين أدلُّكم على الرَّشاد وأنتم تُعرضون',NULL,'997',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4535,'{فلما رأوه} أَيْ: السَّحاب {عارضاً} قد عرض في السماء {مستقبل أوديتهم} يأتي من قبلها {قالوا هذا عارض ممطرنا} سحابٌ يمطر علينا قال الله تعالى: {بل هو ما أستعجلتم به} من العذاب',NULL,'997',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4536,'{تدمّر} تُهلك {كلَّ شيء} مرَّت به من الرِّجال والدَّوابِّ {فأصبحوا لا يُرى} أشخاصهم {إلاَّ مساكنهم} لأنَّ الرِّيح أهلكتهم وفرَّقتهم وبقيت مساكنهم خاليةً',NULL,'997',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4537,'{ولقد مكَّناهم} من القوَّة والعمر والمال {فيما إن مكَّناكم فيه} في الذي ما مكَّنَّاكم فيه',NULL,'997',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4538,'{ولقد أهلكنا ما حولكم} يا أهل مكَّة {من القرى} كحجر ثمود وقرى قوم لوط {وصرَّفنا الآيات} بيَّنا الدَّلالات {لعلهم يرجعون} عن كفرهم يعني: الأمم المهلكة',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4539,'{فلولا نصرهم الذين اتخذوا من دون الله قرباناً آلهة} يعني: أوثانهم الذين اتَّخذوها آلهةَ يتقرَّبون بها إلى الله {بل ضلوا عنهم} بطلوا عند نزول العذاب {وذلك إفكهم} أَيْ: كذبهم وكفرهم يعني: قولهم: إنَّها تُقرِّبنا إلى الله',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4540,'{وإذ صرفنا إليك نفرا من الجن} كانوا تسعة نفرٍ من الجنِّ من نينوى من أرض الموصل وذلك أنَّه عليه السَّلام أُمر أن يُنذر الجنَّ فصرف إليه نفرٌ منهم ليتسمعوا ويبلِّغوا قومهم {فلما حضروه} قال بعضهم لبعض: {أنصتوا} أَيْ: اسكتوا {فلما قضي} أَيْ: فرغ من تلاوة القرآن رجعوا {إلى قومهم منذرين} وقالوا لهم ما قصَّ الله في كتابه وقوله:',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4541,'{قالوا يا قومنا إنا سمعنا كتابا أنزل من بعد موسى مصدقاً لما بين يديه يهدي إلى الحق وإلى طريق مستقيم}',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4542,'{يا قومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم}',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4543,'{ومن لا يجب داعي الله فليس بمعجز في الأرض وليس له من دونه أولياء أولئك في ضلال مبين}',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4544,'{ولم يعي بخلقهن} أَيْ: لمن يضعف عن إبداعهن',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4545,'{ويوم يعرض الذين كفروا على النار أَلَيْسَ هَذَا بِالْحَقِّ قَالُوا بَلَى وَرَبِّنَا قَالَ فذوقوا العذاب بما كنتم تكفرون}',NULL,'998',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4546,'{فاصبر كما صبر أولو العزم من الرسل} أَيْ: ذوو الرَّأي والجدِّ وكلّهم أولو العزم إلاَّ يونس وقيل: هم أصحاب الشَّرائع نوح وإبراهيم وموسى وعيسى ومحمد منهم صلى الله عليه وسلم أجمعين {ولا تستعجل لهم} العذاب {كأنهم يوم يرون ما يوعدون} من العذاب في الآخرة {لم يلبثوا} في الدُّنيا {إلاَّ ساعة من نهار} لهولِ ما عاينوا ونسوا قدره مكثهم في الدُّنيا {بلاغ} أَيْ: هذا القرآن بلاغٌ أَيْ: تبْليغٌ من الله تعالى إليكم على لسان محمَّد عليه السَّلام {فهل يهلك إلاَّ القوم الفاسقون} أَيْ: لا يُهلك مع رحمة الله وتفضُّله إلاَّ الكافرون',NULL,'999',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4547,'{الذين كفروا} أهل مكَّة {وصدوا عن سبيل الله} ومنعوا النَّاس عن الإيمان بمحمد صلى الله عليه وسلم {أضلَّ أعمالهم} أحبطها فلا يرون في ألآخرة لها جزاءً وقوله:',NULL,'1000',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4548,'{كفَّر عنهم سيئاتهم} أَيْ: سترها وغفرها لهم {وأصلح بالهم} أمرهم وحالهم',NULL,'1000',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4549,'{ذلك} الإِضلال والتَّكفير لاتِّباع الكافرين الباطل وهو الشَّيطان واتِّباع المؤمنين الحقَّ وهو القرآن {كذلك يضرب الله للناس أمثالهم} أَيْ: كالبيان الذي ذُكر يُبيِّن الله للنَّاس أمثال سيئات الكافرين وحسنات المؤمنين',NULL,'1000',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4550,'{فإذا لقيتم الذين كفروا فضرب الرقاب} فاضربوا رقابهم أَيْ: فاقتلوهم {حتى إذا أثخنتموهم} أكثرتم فيهم القتل {فشدوا} وثاق الأسارى حتى لا يفلتوا منكم {فإمَّا منَّاً بعد} أَيْ: بعد أن تأسروهم إما منتم عليهم فأطلقتموهم وإمَّا أن تُفادوهم بمالٍ {حتى تَضَعَ الحرب أوزارها} أَيْ: اقتلوهم وأسروهم حتى لا يبقى كافر يقاتلكم فتسكن الحر وتنقطع وهو معنى قوله: {تضع الحرب أوزارها} أي: يضع أهلها آلة الحر من السِّلاح وغيره ويدخلوا في الإِسلام أو الذِّمَّة {ذلك} أَيْ: افعلوا ذلك الذي ذكرت {ولو يشاء الله لانتصر منهم} أهلكهم بغير قتالٍ {ولكن ليبلو بعضكم ببعض} يمحِّص المؤمنين بالجهاد ويمحق الكافرين {والذين قتلوا في سبيل الله} وهم أهل الجهاد',NULL,'1000',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4551,'{سيهديهم} في الدُّنيا إلى الطَّاعات وفي الآخرة إلى الدَّرجات {ويصلح بالهم} أمر معاشهم',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4552,'{ويدخلهم الجنة عرَّفها لهم} بيَّن لهم مساكنهم فيها وعرَّفهم منازلهم',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4553,'{يا أيها الذين آمنوا إن تنصروا الله} أَيْ: رسوله ودينه {ينصركم ويثبت أقدامكم} في مواطن القتال',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4554,'{والذين كفروا فتعساً لهم} أَيْ: سقوطاً وهلاكاً {وأضلَّ أعمالهم} أبطلها لأنَّها كانت للشَّيطان ثمَّ توعَّدهم فقال:',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4555,'{ذلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم}',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4556,'{أَفَلَمْ يَسِيرُوا فِي الأَرْضِ فَيَنْظُرُوا كَيْفَ كَانَ عاقبة الذين من قبلهم دمَّر الله عليهم وللكافرين أمثالها} أَيْ: أمثال تلك العاقبة التي كانت لمَنْ قبلهم',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4557,'{ذلك} أَيْ: ذلك النَّصر للمؤمنين والهلاك للكافرين {بأنَّ الله مولى الذين آمنوا} وليُّهم وناصرهم {وأن الكافرين لا مولى لهم} لا ولي ينصرهم من الله',NULL,'1001',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4558,'{والذين كفروا يتمتعون} في الدُّنيا {ويأكلون كما تأكل الأنعام} ليس لهم همَّةٌ إلاَّ بطونهم وفروجهم ثمَّ يصيرون إلى النَّار',NULL,'1002',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4559,'{وكأين من قرية هي أشدُّ قوة من قريتك التي أخرجتك} يعني: مكَّة أخرجك أهلها {أهلكناهم} بتكذيبهم الرُّسل {فلا ناصر لهم}',NULL,'1002',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4560,'{أفمن كان على بينة من ربه} وهم النبيُّ صلى الله عليه وسلم والمؤمنون {كمَنْ زين له سوء عمله واتبعوا أهواءهم} وهم أبو جهل والكفَّار',NULL,'1002',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4561,'{مثل} صفة {الجنَّة التي وعد المتقون فيها أنهار من ماءٍ غير آسن} غير متغيِّرِ الرَّائحة {وأنهار من خمر لذة للشاربين} لذيذة',NULL,'1002',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4562,'{ومنهم مَنْ يستمع إليك} يعني: المنافقين {حتى إذا خرجوا من عندك} كانوا يستمعون خطبة رسول الله صلى الله عليه وسلم وإذاخرجوا سألوا أصحاب رسول الله صلى الله عليه وسلم استهزاءً وإعلاماً أنَّهم لم يلتفتوا إلى ما قال يقولون: {ماذا قال آنفاً} أَيْ: الآن وقوله:',NULL,'1002',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4563,'{وآتاهم تقواهم} أَيْ: ثواب تقواهم ويجوز أن يكون المعنى: وألهمهم تقواهم ووفَّقهم لها',NULL,'1002',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4564,'{فهل ينظرون} ينتظرون {إلاَّ الساعة} القيامة {أن تأتيهم بغتة} أَيْ: هم في الحقيقة كذلك لأنَّه ليس الأمر إلاَّ أن تقوم عليهم السَّاعة بغتةً {فقد جاء أشراطها} علاماتها من بعث محمد صلى الله عليه وسلم وغيره {فأنى لهم إذا جاءتهم} السَّاعة {ذكراهم} أَيْ: فمن أين لهم أن يتذكَّروا أو يتوبوا بعد مجيء السَّاعة',NULL,'1003',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4565,'{فأعلم أنه لا إله إلاَّ الله} أَيْ: فاثبت على ذلك من علمك {والله يعلم متقلبكم} مُتصرِّفكم في أعمالكم وأشغالكم وقيل: مُتقلَّبكم من الأصلاب إلى الأرحام {ومثواكم} مرجعكم في الدُّنيا والآخرة',NULL,'1003',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4566,'{ويقول الذين آمنوا} حرصاً منهم على الوحي إذا استبطؤوه: {لولا نزلت سورة فإذا أنزلت سورة محكمة} غير منسوخةٍ {وذكر فيها} فُرِضَ {القتال رأيت الذين في قلوبهم مرض} أَيْ: المنافقين {ينظرون إليك} شررا {نظر المغشي عليه من الموت} كنظر مَنْ وقع في سكرات الموت كراهة منهم للقتال {فأولى لهم}',NULL,'1003',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4567,'{طاعة وقول معروف} أَيْ: لو أطاعوا وقالوا لك قولاً حسناً كان ذلك أولى {فإذا عزم الأمر} أَيْ: جدَّ الأمرُ ولزم فرض القتال {فلو صدقوا الله} في الإِيمان والطَّاعة {لكان خيراً لهم}',NULL,'1003',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4568,'{فهل عسيتم إن توليتم} أَيْ: لعلَّكم إن أعرضتم عمَّا جاء به محمد عليه السَّلام أن تعودوا إلى أمر الجاهليَّة فيقتل بعضكم بعضاً وهو قوله: {أن تفسدوا في الأرض وتقطعوا أرحامكم} أَيْ: بالبغي والظلم والقتل',NULL,'1003',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4569,'{أولئك الذين لعنهم الله فأصمهم وأعمى أبصارهم}',NULL,'1003',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4570,'{أفلا يتدبرون القرآن} فيتَّعظوا بمواعظه {أم على قلوبٍ أقفالها} فليس تفهمها',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4571,'{إنَّ الذين ارتدوا على أدبارهم من بعد ما تبين لهم الهدى} يعني: كفَّار أهل الكتاب كفروا بمحمد صلى الله عليه وسلم وهم يعرفونه {الشيطان سول لهم} زيَّن لهم {وأملى لهم} أطال لهم الأمل',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4572,'{ذلك بأنهم قالوا للذين كرهوا ما نزَّل الله} يعني: المشركين {سنطيعكم في بعض الأمر} في التَّظاهر على عداوة محمد صلى الله عليه وسلم',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4573,'{فكيف} أَيْ: فكيف يكون حالهم {إذا توفتهم الملائكة}',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4574,'{ذلك بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه فأحبط أعمالهم}',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4575,'{أم حسب الذين في قلوبهم مرض} وهم المنافقون {أن لن يخرج الله أضغانهم} لن يظهر الله أحقادهم على النبي صلى الله عليه وسلم والمؤمنين',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4576,'{ولو نشاء لأريناكهم} لعرفناكم {فلعرفتهم بسيماهم} بعلامتهم {ولتعرفنهم في لحن القول} في معنى كلامهم إذا تكلَّموا معك',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4577,'{ولنبلونكم} بالجهاد {حتى نعلم المجاهدين منكم والصابرين} العلم الذين يقع به الجزاء {ونبلو أخباركم} أَيْ: ونكشف ما تُسرُّون',NULL,'1004',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4578,'{إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئاً وسيحبط أعمالهم}',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4579,'{ولا تبطلوا أعمالكم} أَيْ: بالمنِّ على رسول الله صلى الله عليه وسلم بإسلامكم',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4580,'{إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَنْ سَبِيلِ اللَّهِ ثم ماتوا وهم كفار فلن يغفر الله لهم}',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4581,'{وتدعوا إلى السلم} أَيْ: لا توادعوهم ولا تتركوا قتالهم حتى يُسلموا لأنَّكم الأعلون ولا ضعف بكم فتدعوا إلى الصُّلح {والله معكم} بالنُّصرة {ولن يتركم أعمالكم} لن ينقصكم شيئاً من ثواب أعمالكم وقوله:',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4582,'{ولا يسألكم أموالكم} أَيْ: لا يسألكم محمَّد عليه السَّلام أموالكم أجراً على تبليغ الرِّسالة',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4583,'{إن يسألكموها فيحفكم} يجهدكم بالمسألة {تبخلوا ويخرج أضغانكم} ويظهر عداوتكم لأنَّ في مسألة المال ظهور العداوة والحق',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4584,'{ها أنتم هؤلاء} يا هؤلاء {تدعون لتنفقوا في سبيل الله فمنكم مَنْ يبخل} بالصدفة {ومَنْ يبخل فإنما يبخل عن نفسه} لأنَّ له ثواب ما أعطي فإذا لم يعط لم يستحقَّ الثَّواب {والله الغنيُّ} عن صدقاتكم {وأنتم الفقراء} إليها في الآخرة {وإن تتولوا} عن الرَّسول {يستبدل قوماً غيركم} أطوع منكم وهم فارس {ثم لا يكونوا} في الطَّاعة {أمثالكم} بل يكونوا أطوع منكم وهذا الخطاب للعرب',NULL,'1005',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4585,'{إنا فتحنا لك فتحاً مبيناً} حكمنا لك بإظهار دينك والنُّصرة على عدوِّك وفتحنا لكم أمر الدِّين',NULL,'1007',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4586,'{لِيَغْفِرَ لَكَ اللَّهُ مَا تَقَدَّمَ مِنْ ذَنْبِكَ} ما علمت في الجاهليَّة {وما تأخَّر} ممَّا لم تعمله وقيل: ما تقدَّم من ذنبك يعني: ذنب أبويك آدم وحوَّاء ببركتك وما تأخَّر من ذنوب أُمَّتك بدعوتك {ويتم نعمته عليك} بالنبوة والحكمة {ويهديك صراطاً مستقيماً} أَيْ: يُثبِّتك عليه',NULL,'1007',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4587,'{وينصرك الله نصرا عزيز} ذا عزٍّ لا يقع معه ذلٌّ',NULL,'1007',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4588,'{هو الذي أنزل السكينة في قلوب المؤمنين} اليقين والطُّمأنينة {ليزدادوا إيماناً} بشرائع الدِّين {مع إيمانهم} تصديقهم بالله وبرسله وقوله:',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4589,'{ليدخل الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خالدين فيها ويكفر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما}',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4590,'{الظانين بالله ظنَّ السوء} يظنُّون أن لن ينصر الله محمَّداً والمؤمنين {عليهم دائرة السوء} بالذُّلَّ والعذاب أَيْ: عليهم يدور الهلاك والخزي',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4591,'{ولله جنود السماوات والأرض وكان الله عزيزا حكيما}',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4592,'{إنا أرسلناك شاهدا} على أُمَّتك يوم القيامة {ومبشراً} بالجنَّة مَنْ عمل خيراً {ونذيراً} منذراً بالنَّار مَنْ عمل سوءً',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4593,'{وتعزروه} أَيْ: تنصروه {وتوقروه} وتعظِّموه',NULL,'1009',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4594,'{إنَّ الذين يبايعونك} بالحديبية {إنما يبايعون الله} أَيْ: أخذك عليهم البيعة عقدُ الله عليهم {يد الله فوق أيديهم} نعمة الله علهيم فوق ما صنعوا من البيعة {فمن نكث} نقض البيعة {فإنما ينكث على نفسه} فإنما يضرُّ نفسه بذلك النَّكث',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4595,'{سيقول لك المخلفون من الأعراب} الآية لمَّا أراد رسول الله صلى الله عليه وسلم المسير إلى مكَّة عام الحديبية استنفر مَنْ حول المدينة من الأعراب حذراً من قريش أن يعرضوا بحرب فتثاقلوا عنه وخافوا قريشاً على رسول الله صلى الله عليه وسلم وعلى أنفسهم فأنزل الله تعالى: {سيقول لك المخلفون} الذين خلَّفهم الله عن صحبتك إذا انصرفت إليهم فعاتبتهم عن التَّخلُّف: {شغلتنا} عن الخروج معك {أموالنا وأهلونا} أَيْ: ليس لنا مَنْ يقوم فيها إذا خرجنا {فاستغفر لنا} تركنا الخروج معك ثمَّ كذَّبهم الله تعالى في ذلك العذر فقال: {يقولون بألسنتهم ما ليس في قلوبهم} الآية',NULL,'1008',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4596,'{بل ظننتم أن لن ينقلب الرسول والمؤمنون إلى أهليهم أبداً} وذلك أنَّهم قالوا: إنَّ محمداً وأصحابه أكلة رأس أًيْ: قليلو العدد وأنهم لا لا يرجعون من هذا الوجه أبداً فقال الله تعالى: {وظننتم ظنَّ السوء وكنتم قوماً بُوْراً} هالكين عند الله تعالى بهذا الظن',NULL,'1009',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4597,'{ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا}',NULL,'1009',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4598,'{ولله ملك السماوات والأرض يغفر لمن يشاء ويعذب من يشاء وكان الله غفوراً رحيماً}',NULL,'1009',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4599,'{سيقول المخلفون} يعني: هؤلاء: {إذا انطلقتم إلى مغانم} يعني: عنائم خيبر {ذرونا نتبعكم} إلى خيبر فنشهد معكم {يريدون أن يبدلوا كلام الله} يغيّروا وعد الله الذي وعد أهل الحديبية وذلك أنَّ الله تعالى حكم لهم بغنائم خيبر دون غيرهم {قل لن تتبعونا} إلى خيبر {كذلكم قال الله من قبل} أَيْ: من قبل مرجعنا إليكم إنَّ غنيمة خيبر لمّنْ شهد الحديبية دون غيرهم {فسيقولون بل تحسدوننا} أن نصيب معكم من الغنائم',NULL,'1009',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4600,'{قل للمخلفين من الأعراب ستدعون إلى قوم} إلى قتال قوم {أولي بأس شديد} وهم فارس والرُّوم وقيل: بنو حنيفة أصحاب اليمامة {تقاتلونهم أو يسلمون} يعني: أو هم يسلمون أصحاب مسيلمة الكذاب فيترك قتالهم {فإن تطيعوا} مَنْ دعاكم إلى قتالهم {يؤتكم الله أجراً حسناً وإن تتولوا كما توليتم من قبل} عام الحديبية يعني: نافقتم وتركتم الجهاد {يعذِّبكم عذاباً أليماً} ثم ذكر أهل العُذر في التَّخلُّف عن الجهاد فقال:',NULL,'1010',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4601,'{لَيْسَ عَلَى الأَعْمَى حَرَجٌ وَلا عَلَى الأَعْرَجِ حرج ولا على المريض حرج} الآية ثمَّ ذكر خبر مَنْ أخلص نيَّته فقال:',NULL,'1010',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4602,'{لقد رضي الله عن المؤمنين} وكانوا ألفاً وأربعمائة {إذ يبايعونك} بالحديبية على أن يناجزوا قريشاً ولا يفرُّوا {تحت الشجرة} يعني: سمرة كانت هنالك وهذه البيعة تسمَّى بيعة الرِّضوان {فعلم ما في قلوبهم} من الإِخلاص والوفاء {فأنزل} الله {السكينة عليهم} وهي الطُّمأنينة وثلج الصدر بالنُّصرة من الله تعالى لرسوله {وأثابهم فتحاً قريباً} أَيْ: فتح خيبر',NULL,'1010',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4603,'{ومغانم كثيرة يأخذونها} يعني: عقار خيبر وأموالها',NULL,'1011',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4604,'{وعدكم الله مغانم كثيرة تأخذونها} وهي الفتوح التي تفتح لهم إلى يوم القيامة {فعجَّل لكم هذه} يعني: خيبر {وكفَّ أيدي الناس عنكم} لما خرجوا وخلفوا عيالهم بالمدينة حفظ الله عليهم عيالهم وقد همَّت اليهود بهم فقذف الله في قلوبهم الرُّعب فانصرفوا {ولتكون} هزيمتهم وسلامتكم {آية للمؤمنين ويهديكم صراطاً مستقيماً} يعني: طريق التَّوكُّل وتفويض الأمر إلى الله سبحانه في كلِّ شيء',NULL,'1011',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4605,'{وأخرى} أَيْ: ومغانم أخرى {لم تقدروا عليها} يعني: فارس والرُّوم {قد أحاط الله بها} علم أنَّه يفتحها لكم',NULL,'1011',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4606,'{ولو قاتلكم الذين كفروا} أَيْ: أهل مكَّة لو قاتلوكم عام الحديبية {لولوا الأدبار} لانهزموا عنك ولنصرت عليهم',NULL,'1011',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4607,'{سنة الله} كسنَّة الله في النُّصرة لأوليائه',NULL,'1011',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4608,'{وهو الذي كفَّ أيديهم عنكم وأيديكم عنهم ببطن مكة} مَنَّ الله سبحانه على المؤمنين بما أوقع من صلح الحديبية فكفَّهم عن القتال بمكَّة وذكر حُسن عاقبة ذلك في الآية الثَّانية وقوله: {من بعد أن أظفركم عليهم} وذلك أنَّ رجالاً من قريش طافوا بعسكر رسول الله صلى الله عليه وسلم ذلك العام ليصيبوا منهم فأُخذوا وأُتي بهم رسول الله صلى الله عليه وسلم فعفا عنهم وخلَّى سبيلهم وكان ذلك سبب الصُّلح بينهم',NULL,'1011',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4609,'{هم الذين كفروا} يعني: أهل مكَّة {وصدوكم عن المسجد الحرام} منعوكم من زيارة البيت {والهدي} ومنعوا الهدي {معكوفاً} محبوساً {أن يبلغ محله} منحره وكانت سبعين بدنةً {ولولا رجال مؤمنون ونساء مؤمنات} بمكَّة {لم تعلموهم أن تطئوهم} أَيْ: لولا أن تطؤوهم في القتال لأنَّكم لم تعلموهم مؤمنين وهو قوله: {بغير علم} {فتصيبكم منهم معرَّة} كفَّارةٌ وعارٌ وعيبٌ من الكافرين يقولون: قتلوا أهل دينهم {ليدخل الله في رحمته} دينه الإِسلام {مَنْ يشاء} من أهل مكَّة قبل أن يدخلوها {لو تزيلوا} تميَّز عنهم هؤلاء المؤمنين {لعذَّبنا الذين كفروا منهم عذاباً أليماً} لأنزلنا بهم ما يكون عذاباً لهم أليماً بأيديكم',NULL,'1012',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4610,'{إذْ جعل الذين كفروا في قلوبهم الحمية حَمِيَّةَ الجاهلية} حين صدُّوا رسول الله صلى الله عليه وسلم وأصحابه عن البيت {فأنزل الله سكينته على رسوله وعلى المؤمنين} أَيْ: الوقار حين صالحوهم ولم تأخذهم من الحمية ما أخذهم فيلجُّوا ويقاتلوا {وألزمهم كلمة التقوى} توحيد الله والإيمان به وبرسوله: لا إله إلا الله محمد رسول الله وقيل: يعني: بسم الله الرحمن الرحيم أبى المشركون أن يقبلوا هذا لمَّا أراد رسول الله صلى الله عليه وسلم أن يكتب كتاب الصُّلح بينهم وقالوا: اكتب باسمك اللَّهم فقال الله تعالى: {وكانوا أحقَّ بها وأهلها} أي: المؤمنين لأنَّ الله اختارهم للإيمان وكانوا أحقَّ بكلمة التَّقوى من غيرهم',NULL,'1012',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4611,'{لقد صدق الله رسوله الرؤيا بالحق} الآية كان رسول الله صَلَّى اللَّهُ عَلَيْهِ وسلم رأى في منامه قبل خروجه عام الحديبية كأنَّه وأصحابه يدخلون مكَّة مُحلِّقين ومُقصِّرين غير خائفين فلمَّا خرج عام الحديبية كانوا قد وطنوا أنفسهم على دخول مكَّة لرؤيا رسول الله صلى الله عليه وسلم فلمَّا صدُّوا عن البيت راب بعضهم ذلك فأخبر الله تعالى أنَّ تلك الرُّؤيا صادقةٌ وأنَّهم يدخلونها إٍن شاء الله آمنين وقوله: {فعلم ما لم تعلموا} علم الله تعالى أنًّ الصَّلاح كان في ذاك الصُّلح ولم تعلموا ذلك {فجعل من دون ذلك} أَيْ: من دون دخولكم المسجد {فتحاً قريباً} وهو صلح الحديبية ولم يكن فتحٌ في الإسلام كان أعظم من ذلك لأنَّه دخل في الإسلام في تلك السِّنين مثل من كان في الإسلام قبل ذلك أو أكثر وقيل: يعني: فتح خيبر',NULL,'1013',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4612,'{هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَى وَدِينِ الْحَقِّ ليظهره على الدين كله} ليجعل دين الحقِّ ظاهراً على سائر الأديان عالياً عليها {وكفى بالله شهيداً} أنَّك مرسلٌ بالحقِّ ثمَّ حقَّق الله تلك الشَّهادة وبيَّنها فقال:',NULL,'1013',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4613,'{محمد رسول الله والذين معه} من المؤمنين {أشداء} غلاظٌ {على الكفار رحماء بينهم} متوادُّون متعاطفون {تراهم ركعاً سجداً} في صلواتهم {يبتغون فضلاً من الله} أن يدخلهم الجنَّة {ورضواناً} أن يرضى عنه {سيماهم} علامتهم {في وجوههم من أثر السجود} يعني: نوراً وبياضاً في وجوههم يوم القيامة يُعرفون بذلك النُّور أنَّهم سجدوا في دار الدُّنيا لله تعالى {ذلك مثلهم} صفة محمَّد صلى الله عليه وسلم وأصحابه {في التوراة ومثلهم في الإنجيل كزرع أخرج شطأه} فراخه ونباته {فآزره} قوَّاه وأعانه أيْ: قوَّى الشَّطأ الزَّرع كما قوَّى أمر محمَّد وأصحابه والمعنى: أنَّهم يكونون قليلاً ثمَّ يكثرون وهذا مثل ضربه الله تعالى لنبيِّه عليه السَّلام إذ خرج وحده فأيَّده بأصحابه كما قوَّى الطَّاقة من الزرع بما ينبت حوله {فاستغلظ} فَغَلُظَ وقوِيَ {فاستوى} ثمَّ تلاحق نباته وقام على {سوقه} جمع ساق {يعجب الزراع} بحسن نباته واستوائه {ليغيظ بهم الكفار} فعل الله تعالى ذلك بمحمَّد وأصحابه ليغيظ بهم أهل الكفر {وعد الله الذين آمنوا وعملوا الصالحات منهم} أَيْ: من أصحاب محمد عليه السَّلام {مغفرة وأجراً عظيماً}',NULL,'1014',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4614,'{يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله} أَيْ: لا تُقدِّموا خلاف الكتاب والسُّنَّة وقيل: لا تذبحوا قبل أن يذبح النبيُّ عليه السَّلام في الأضحى وقيل: لا تصوموا قبل صومه نزلت في النَّهي عن صوم يوم الشَّكِّ والمعنى: لا تسبقوا رسول الله صلى الله عليه وسلم بشيءٍ حتى يكون هو الذي يأمركم به {واتقوا الله} في مخالفة أمره {إنَّ الله سميع} لأقوالكم {عليم} بأحوالكم',NULL,'1015',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4615,'{يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبيّ} نزلت في ثابت ابن قيس بن شماس وكان جهوريَّ الصَّوت وربَّما كان يُكلِّم رسول الله صلى الله عليه وسلم فينادي بصوته فأمره بغضِّ الصَّوت عند مخاطبته {ولا تجهروا له بالقول كجهر بعضكم لبعض} لا تُنزِّلوه منزلة بعضكم من بعضٍ فتقولوا: يا محمد ولكن خاطبوه بالنبوَّة والسَّكينة والإِعظام {أن تحبط أعمالكم} كي لا تبطل حسناتكم {وأنتم لا تشعرون} أنَّ خطابه بالجهر ورفع الصَّوت فوق صوته يُحبط العمل فلمَّا نزلت هذه الآية خفض أبو بكر وعمر رضي الله عنهما صوتهما فما كلَّما النبيَّ صلى الله عليه وسلم إلاَّ كأخي السِّرار فأنزل الله تعالى:',NULL,'1015',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4616,'{إنَّ الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن الله قلوبهم للتقوى} أَيْ: اختبرها وأخلصها للتَّقوى',NULL,'1016',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4617,'{إن الذين ينادونك من وراء الحجرات} نزلت في وفد تميمٍ أتوا رسول الله صلى الله عليه وسلم ليفاخروه فنادوا على الباب: يا محمَّد اخرج إلينا فإنَّ مدحنا زينٌ وإنَّ ذمنا شينٌ فقال الله تعالى: {أكثرهم لا يعقلون} أَيْ: إنَّهم جهَّال ولو عقلوا لما فاخر رسول الله صلى الله عليه وسلم',NULL,'1016',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4618,'{ولو أنَّهم صبروا حتى تخرج إليهم لكان خيراً لهم} من إيذائهم إيَّاك بالنِّداء على بابك {والله غفور رحيم} لمن تاب منهم',NULL,'1016',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4619,'{يا أيها الذين آمنوا إن جاءكم فاسق بنبأ} نزلت في الوليد بن عقبة بعثه رسول الله صلى الله عليه وسلم مُصَّدِّقاً إلى قومٍ كانت بينه وبينهم تِرةٌ في الجاهليَّة فخاف أن يأتيهم وانصرف من الطَّريق إلى رسول الله صلى الله عليه وسلم وقال: إنَّهم منعوا الصَّدقة وقصدوا قتلي ذلك قوله: {إن جاءكم فاسق بنبأ فتبينوا} أَيْ: فاعلموا صدقه من كذبه {أن تصيبوا} لئلا تصيبوا {قوماً بجهالة} وذلك رسول الله صلى الله عليه وسلم همَّ أن يغزوهم حتى تبيَّن له طاعتهم',NULL,'1016',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4620,'{واعلموا أنَّ فيكم رسول الله} فلا تقولوا الباطل فإنَّ الله يخبره {لو يطيعكم في كثير من الأمر} لو أطاع مثل هذا المخبر الذي أخبره بما لا أصل له {لعنتم} لائمتم ولهلكتم {ولكنَّ الله حبب إليكم الإيمان} فأنتم تطيعون الله ورسوله فلا تقعون في العنت يعني بهذا: المؤمنين المخلصين ثمَّ أثنى عليهم فقال: {أولئك هم الراشدون}',NULL,'1017',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4621,'{فضلاً من الله} أَيْ: الفضل من الله عليهم',NULL,'1017',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4622,'{وإن طائفتان من المؤمنين اقتتلوا} نزلت في جمعين من الأنصار كان بينهما قتالٌ بالأيدي والنِّعال {فأصلحوا بينهما} بالدُّعاءِ إلى حكم كتاب الله فإن بغت إحداهما على الأخرى أَيْ: تعدَّت إحداهما على الأخرى وعدلت عن الحقِّ {فقاتلوا} الباغية حتى ترجع إلى أمر الله في كتابه {فإن فاءت} رجعت إلى الحقِّ {فأصلحوا بينهما} بحملهما على الإِنصاف {وأقسطوا} وأعدلوا {إنَّ الله يحب المقسطين}',NULL,'1017',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4623,'{إنما المؤمنون أخوة} في الدين والولاية {فأصلحوا بين أخويكم} إذا اختلفا واقتتلا {واتقوا الله} في إصلاح ذات البين {لعلكم ترحمون} كي ترحموا به',NULL,'1018',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4624,'{يا أيها الذين آمنوا لا يسخر قوم من قوم} الآية نهى الله تعالى المؤمنين والمؤمنات أن يسخر بعضهم من بعض {عسى أن يكونوا} أَيْ: المسخور منه {خيراً منهم} من السَّاخر ومعنى السُّخرية ها هنا الازدارء والاحتقار {ولا تلمزوا أنفسكم} لا يعب بعضكم بعضاً {ولا تنابزوا بالألقاب} وهو أن يُدعى الرَّجل بلقبٍ يكرهه نهى الله تعالى عن ذلك {بئس الاسم الفسوق بعد الإِيمان} يعني: إنَّ السُّخرية واللَّمز والتَّنابز فسوقٌ بالمؤمنين وبئس ذلك بعد الإِيمان',NULL,'1018',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4625,'{يا أيها الذين آمنوا اجتنبوا كثيراً من الظن إنَّ بعض الظن إثم} وهو أنْ يظنَّ السُّوء بأهل الخير وبمن لا يُعلم منه فسقٌ {ولا تجسسوا} لا تطلبوا عورات المسلمين ولا بتحثوا عن معايبهم {ولا يغتب بعضكم بعضاً} لا تذكروا أحدكم بشيءٍ يكرهه وإن كان فيه ذلك الشَّيء {أيحب أحدكم أن يأكل لحم أخيه ميتاً} يعني: إن ذكراك أخاك على غيبةٍ بسوءٍ كأكل لحمه وهو ميِّت لا يحسُّ بذلك {فكرهتموه} إنْ كرهتم أكل لحمه ميتاً فاكرهوا ذكره بسوءٍ',NULL,'1018',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4626,'{يا أيها الناس إنَّا خلقناكم من ذكر وأنثى} أَيْ: كلُّكم بنو أبٍ واحدٍ وأمٍّ واحدةٍ فلا تفاضل بينكم في النَّسب {وجعلناكم شعوباً} وهي رؤوس القبائل كربيعة ومضر {وقبائل} وهي دون الشُّعوب كبكر من ربيعة وتميم من مضر {لتعارفوا} ليعرف بعضكم بعضاً في قرب النَّسب وبعده لا لتتفاخروا بها ثمَّ أعلم أنَّ أرفعهم عنده منزلةً أتقاهم فقال: {إن أكرمكم عند الله أتقاكم} الآية',NULL,'1019',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4627,'{قالت الأعراب آمنا} نزلت في نفرٍ من بني أسدٍ قدموا المدينة في سنة جدبة بذرايهم وأظهروا كلمة الشَّهادة ولم يكونوا مؤمنين في السِّرِّ فقال الله تعالى: {قل لم تؤمنوا ولكن قولوا أسلمنا} أَيْ: لم تُصدِّقوا الله ورسوله بقلوبكم ولكن أظهرتم الطَّاعة مخافة القتل والسَّبي {ولما يدخل الإِيمان في قلوبكم وإن تطيعوا الله ورسوله} ظاهراً وباطناً {لا يلتكم} لا ينقصكم {من} ثواب {أعمالكم شيئاً} الآية ثمَّ بيَّن حقيقة الإيمان والمؤمن فقال:',NULL,'1019',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4628,'{إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا وجاهدوا بأموالهم وأنفسهم في سبيل الله أولئك هم الصادقون} أَيْ هؤلاء هم الذين صدقوا في إيمانهم لا مَنْ أسلم خوف السَّيف ورجاء المنفعة فلمَّا نزلت الآيتان جاءت الأعراب رسول الله صلى الله عليه وسلم وحلفوا بالله أنَّهم مؤمنون وعلم الله غير ذلك منهم فأنزل الله تعالى:',NULL,'1019',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4629,'{قل أتعلمون الله بدينكم} الآية أيْ: أَتُعَلِّمونه بما أنتم عليه وهو يعلم ذلك',NULL,'1020',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4630,'{يمنون عليك أن أسلموا} وذلك أنَّهم كانوا يقولون لنبي الله صلى الله عليه وسلم: أتيناك بالعيال والأثقال طوعاً ولم نقاتلك كما قاتلك بنو فلانٍ فأعطنا فقال الله تعالى: {قل لا تمنوا عليَّ} وقوله: {إن كنتم صادقين} أنَّكم مؤمنون أَيْ: لله المنَّةُ إن صدقتم في إيمانكم لا لكم',NULL,'1020',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4631,'{إن الله يعلم غيب السماوات والأرض والله بصير بما تعملون}',NULL,'1020',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4632,'{ق} قُضي ما هو كائنٌ إلى يوم القيامة {والقرآن المجيد} الكبير القدر والكثير الخير',NULL,'1021',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4633,'{بل عجبوا} يعني: كفَّار مكَّة {أن جاءهم منذر منهم} محمدٌ عليه السَّلام وهم يعرفون نسبه وأمانته {فقال الكافرون هذا شيء عجيب} يعني: هذا الإنكار الذي ينذرنا',NULL,'1021',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4634,'{أإذا متنا وكنا تراباً} نُبعث؟ وهذا استفهامُ إنكارٍ وجوابه محذوفٌ ثمَّ انكروا ذلك أصلاً فقالوا: {ذلك} أَيْ: البعث {رجع بعيد} ردٌّ لا يكون قال الله تعالى:',NULL,'1021',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4635,'{قد علمنا ما تنقص الأرض منهم} ما تأكل من لحومهم {وعندنا كتاب حفيظ} أي: اللَّوح المحفوظ من أن يدرس ويتغيَّر وفيه جميع الأشياء المقدَّرة',NULL,'1021',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4636,'{بل كذَّبوا بالحق} أَيْ: بالقرآن {لما جاءهم فهم في أمرٍ مريج} مُلتبسٍ عليهم مرَّةً يقولون للنبيِّ صلى الله عليه وسلم ساحرٌ ومرَّةً: شاعرٌ ومرَّةً: مُعلَّمٌ ثمَّ دلَّهم على قدرته فقال:',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4637,'{أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج} شقوق وقوله:',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4638,'{من كلِّ زوج بهيج} أَيْ: من كلِّ لونٍ حسنٍ',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4639,'{تبصرة} فعلنا ذلك تبصيراً ودلالةً على قدرتنا {لكلِّ عبد منيب} يرجع إلى الله تعالى فيتفكَّر في قدرته وقوله:',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4640,'{وحبَّ الحصيد} أَيْ: ما يُقتات من الحبوب',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4641,'{والنخل باسقات} طوالاً {لها طلع نضيد} ثمرٌ متراكبٌ',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4642,'{رزقاً للعباد} أَيْ: آتينا هذا الأشياء للرِّزق {وأحيينا به} بذلك الماء {بلدة ميتاً كذلك الخروج} من القبور وقوله:',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4643,'{كذبت قبلهم قوم نوح وأصحاب الرس وثمود}',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4644,'{وعاد وفرعون وإخوان لوط}',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4645,'{وقوم تبع} وهو ملكٌ كان باليمن أسلم ودعا قومه إلى الإِسلام فكذَّبوه وقوله: {فحقَّ وعيد} وجب عليهم العذاب',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4646,'{أفعيينا بالخلق الأول} أَيْ: أعجزنا عنه حتى تعيى بالإعادة {بل هم في لبس} شكٍّ {من خلق جديد} أَيْ: البعث',NULL,'1022',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4647,'{ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه} يحدثه قلبه {ونحن أقرب إليه} بالعلم {من حبل الوريد} وهو عرقٌ في العنق',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4648,'{إذ يتلقى المتلقيان} أَيْ: المَلكان الحافظان يتلقَّيان ويأخذان ما يعمله الإنسان فيثبتانه {عن اليمين وعن الشمال قعيد} قاعدان على جانبيه',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4649,'{ما يلفظ} يتكلَّم {من قول إلاَّ لديه رقيب} حافظٌ {عتيد} حاضر',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4650,'{وجاءت سكرة الموت} أَيْ: غمرته وشدَّته {بالحق} أَيْ: من أمر الآخرة حتى يراه الإنسان عياناً {ذلك ما كنت منه تحيد} أي: تهرب وتروغ يعني: الموت',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4651,'{ونفخ في الصور} أَيْ: نفخة البعث {ذلك يوم الوعيد} الذي يُوعد الله به الكفَّار',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4652,'{وجاءت كلُّ نفس} إلى المحشر {معها سائق} من الملائكة يسوقها {وشهيد} شاهدٌ عليها بعملها وهو الأيدي والأرجل فيقول الله تعالى:',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4653,'{لقد كنت في غفلة من هذا} اليوم {فكشفنا عنك غطاءك} فخلينا عنك سترك حتى عاينته {فبصرك اليوم حديد} فعلمك بما أنت فيه نافذٌ',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4654,'{وقال قرينه} أَيْ: المَلك الموَّكل به: {هذا ما لديَّ عتيد} هذا الذي وكَّلتني به قد أحضرته فأحضرت ديوان أعماله فيقول الله للملَكين الموكَّلين بالإِنسان:',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4655,'{ألقيا في جهنم كلَّ كفار عنيد} عاصٍ مُعرضٍ عن الحقِّ',NULL,'1023',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4656,'{مناع للخير} للزَّكاة المفروضة وكلِّ حقٍّ في ماله {معتد} ظالمٍ {مريب} شاكٍّ',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4657,'{الذي جعل مع الله إلهاً آخر فألقياه في العذاب الشديد}',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4658,'{قال قرينه} من الشَّياطين: {ربنا ما أطغيته} ما أظللته {ولكن كان في ضلال بعيد} أَيْ: إنَّما طغى هو بضلاله وإنَّما دعوته فاستجاب لي كما قال في الإخبار عن الشَّيطان: {إلاَّ أنْ دعوتُكم فاستجبتُمْ لي} فحينئذٍ يقول الله:',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4659,'{لا تختصموا لدي وقد قدَّمت إليكم بالوعيد} حذَّرتكم العقوبة في الدُّنيا على لسان الرُّسل',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4660,'{ما يبدل القول لدي} لا تبديل لقولي ولا خلف لوعدي {وما أنا بظلام للعبيد} فأعاقب بغير جرم',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4661,'{يوم نقول لجهنم هل امتلأت} وهذا استفهامُ تحقيقٍ وذلك أنَّ الله عزَّ وجل وعدها أن يملأها فلمَّا ملأها قال لها: {هل امتلأت وتقول هل من مزيد} أَيْ: هل بقي فيَّ موضعٌ لم يمتلىء أيْ قد امتلأت',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4662,'{وأزلفت الجنة} أُدنيت الجنَّة {للمتقين} حتى يروها {غير بعيد} منهم ويقال لهم:',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4663,'{هذا ما توعدون لكلِّ أواب} رجاع إلى الله بالطًّاعة {حفيظ} حافظ لأمر الله',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4664,'{من خشي الرحمن بالغيب} خاف الله ولم يره {وجاء بقلب منيب} مقبلٍ إلى طاعة الله يقال لهم:',NULL,'1024',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4665,'{ادخلوها بسلام} بسلامةٍ من العذاب {ذلك يوم الخلود} لأهل الجنَّة فيها',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4666,'{لهم ما يشاؤون فيها ولدينا مزيد} زيادةٌ ممَّا لم يخطر ببالهم وقيل: هو الرُّؤية',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4667,'{وكم أهلكنا قبلهم} قبل أهل مكَّة {من قرنٍ} جماعةٍ من النَّاس {هم أشدُّ منهم بطشاً فَنَقَّبوا} طوفوا في البلاد وفتشوا فلم يريوا محيصاً من الموت',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4668,'{إن في ذلك} الذي ذكرت {لذكرى} لعظةً وتذكيراً {لمن كان له قلب} أَيْ: عقلٌ {أو ألقى السمع} أَيْ: استمع القرآن {وهو شهيد} حاضر القلب وقوله:',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4669,'{وما مسنا من لغوب} أَيْ: وما أصابنا تعبٌ وإعياءٌ وهذا ردٌّ على اليهود في قولهم: إنَّ الله تعالى استراح يوم السَّبت',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4670,'{فَاصْبِرْ عَلَى مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ} صل لله {قبل طلوع الشمس} أَيْ: صلاة الفجر {وقبل الغروب} صلاة الظهر والعصر',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4671,'{ومن الليل فسبحه} أَيْ: صلاتي العشاء {وأدبار السجود} أَيْ: الرَّكعتين بعد المغرب',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4672,'{واستمع} يا محمد {يوم يناد المناد} وهو إسرافيل عليه السَّلام يقول: أيَّتها العظام البالية واللُّحوم المُتمزِّقة إنَّ الله يأمركن أن تتجمعن لفصل القضاء {من مكان قريب} من السِّماء وهو صخرة بيت المقدس أقرب موضعٍ من الأرض إلى السَّماء',NULL,'1025',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4673,'{يوم يسمعون الصيحة بالحق} أَيْ: نفخة البعث {ذلك يوم الخروج} من القبور',NULL,'1026',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4674,'{إنا نحن نحيي ونميت وإلينا المصير}',NULL,'1026',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4675,'{يوم تشقق الأرض عنهم} فيخرجون {سراعاً}',NULL,'1026',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4676,'{وما أنت عليهم بجبار} بمسلِّطٍ يجبرهم على الإسلام وهذا قبل أن يوم بالقتال {فذكِّر} فعظ {بالقرآن مَنْ يخاف وعيد}',NULL,'1026',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4677,'{والذاريات ذرواً} أي: الرِّياح التي تذرو التُّراب',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4678,'{فالحاملات وقراً} وهي السَّحاب تحمل الماء',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4679,'{فالجاريات يسراً} السُّفن تجري في البحر بيسرٍ {فالمقسمات أمراً} الملائكة تأتي بأمرٍ مختلف من الخصب والخصب والجدب والمطر والحوادث',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4680,'{فالمقسمات أمرا}',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4681,'{إنما توعدون} من الخير والشَّرِّ والثَّواب والعقاب {لصادق} أقسم الله بهذه الأشياء على صدق وعده',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4682,'{وإنَّ الدين} الجزاء على الأعمال {لواقع} لكائنٌ',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4683,'{والسماء ذات الحبك} الخَلْق الحسن',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4684,'{إنكم} يا أهل مكَّة {لفي قول مختلف} في أمر النبي صلى الله عليه وسلم',NULL,'1027',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4685,'{يؤفك عنه} يُصرف عن الإيمان به {مَنْ أفك} صُرف عن الخير',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4686,'{قتل الخراصون} لُعن الكذَّابون يعني: المُقتسمين',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4687,'{الذين هم في غمرة} غفلةٍ {ساهون} لاهون',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4688,'{يسألون أيان يوم الدين} متى يوم الجزاء؟ استهزاءً منهم قال الله تعالى:',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4689,'{يوم هم على النار يفتنون} أي: يقع الجزاء يوم هم على النار يُفتنون يُحرَّقون ويُعذَّبون وتقول لهم الخزنة:',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4690,'{ذوقوا فتنتكم} عذابكم {هذا الذي كنتم به تستعجلون} في الدُّنيا',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4691,'{إنَّ المتقين في جنات وعيون}',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4692,'{آخذين ما آتاهم ربهم} من الثَّواب والكرامة {إنهم كانوا قبل ذلك} قبل دخولهم الجنَّة {محسنين}',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4693,'{كانوا قليلاً من الليل ما يهجعون} كانوا ينامون قليلا من الليل',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4694,'{وبالأسحار هم يستغفرون}',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4695,'{وفي أموالهم حق للسائل والمحروم} وهو الذي لا يسأل الناس ولا يكسب',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4696,'{وفي الأرض آيات} دلالاتٌ على قدرة الله تعالى ووحدانية {للموقنين}',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4697,'{وفي أنفسكم} أيضاً آياتٌ من تركيب الخلق وعجائب ما في الآدمي من خلقه {أفلا تبصرون} ذلك',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4698,'{وفي السماء رزقكم} أَي: الثَّلج والمطر الذي هو سبب الرِّزق والنَّبات من الأرض {وما توعدون} ما ابتداء وخبره محذوف على تقدير: وما توعدون من البعث والثَّواب والعقاب حقٌّ ودلَّ على هذا المحذوف قوله:',NULL,'1028',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4699,'{فوربِّ السماء والأرض إنَّه لحقٌّ مثل ما أنكم تنطقون} أَيْ: كما أنَّكم تتكلَّمون أي: إنَّه معلومٌ بالدَّليل كما إِنَّ كلامكم إذا تكلّمتم معلومٌ لكم ضرورة أنكم تتكلمون ومثل رفع لأنَّه صفةٌ لقوله: لحق ومَنْ نصب أراد: إنَّه لحقّ حقاً مثلَ ما أنّكم تنطقون',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4700,'{هل أتاك حديث ضيف إبراهيم المكرمين} بأن خدمهم بنفسه',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4701,'{إذ دخلوا عليه فقالوا سلاماً} سلَّموا سلاماً {قال سلامٌ} عليكم {قوم منكرون} أي: أنتم قوم لا نعرفكم',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4702,'{فراغ} فعدل ومال {إلى أهله} وقوله:',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4703,'{فقربه إليهم قال ألا تأكلون}',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4704,'{فأوجس منهم خيفة} أَيْ: وقع في نفسه الخوف منهم وقوله:',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4705,'{فأقبلت امرأته في صرَّة} أَيْ: أخذت تصيح بشدَّةٍ {فَصَكَّتْ} لطمت {وجهها وقالت} : أنا {عجوز عقيم} فكيف ألد؟',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4706,'{قالوا كذلك} كما اخبرناك {قال ربك} أي: نخبرك عن الله لا عن أنفسنا {إنَّه هو الحكيم العليم} يقدر أن يجعل العقيم ولوداً فلمَّا قالوا ذلك علم إبراهيم أنَّهم رسلٌ وأنَّهم ملائكة صلوات الله عليهم',NULL,'1029',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4707,'الجزء السابع والعشرين:
INSERT INTO [b2547] VALUES (4708,'{قَالُوا إِنَّا أُرْسِلْنَا إِلَى قَوْمٍ مُجْرِمِينَ} يعنون قوم لوط',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4709,'{لنرسل عليهم حجارة من طين} يعني: السِّجيل',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4710,'{مسوَّمة عند ربك للمسرفين} مًعلَّمة على كلِّ حجرٍ منها اسم مَنْ يهلك به',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4711,'{فأخرجنا مَنْ كان فيها} يعني: من قرى قوم لوط {من المؤمنين}',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4712,'{فما وجدنا فيها غير بيت من المسلمين} يعني: بيت لوطٍ عليه السَّلام',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4713,'{وتركنا فيها} بأهلاكهم {آية} علامة للخائفين تدلُّ على أنَّ الله أهلكهم',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4714,'{وفي موسى} عطفٌ على قوله: وفي الأرض {إذ أرسلناه إلى فرعون بسلطان مبين} بحجَّةٍ واضحةٍ',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4715,'{فتولى} فأعرض عن الإيمان {بركنه} مع جنوده وما كان يتقوَّى به وقوله:',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4716,'{وهو مليم} أَيْ: أتى ما يُلام عليه',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4717,'{وفي عاد} أيضاً آيةٌ {إذ أرسلنا عليهم الريح العقيم} وهي التي لا بركة فيها ولا تأتي بخيرٍ',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4718,'{ما تذر من شيء أتت عليه إلاَّ جعلته كالرميم} كالنَّبت الذي قد تحطَّم',NULL,'1030',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4719,'{وفي ثمود إذ قيل لهم تمتعوا حتى حين} إلى فناء آجالكم',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4720,'{فعتوا عن أمر ربهم} عصوه {فأخذتهم الصاعقة} العذاب المهلك',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4721,'{فما استطاعوا من قيام} أي: أن يقوموا بعذاب الله {وما كانوا منتصرين} أي: لم ينصرهم أحدٌ علينا',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4722,'{وقوم نوح} وأهلكنا قوم نوحٍ قبل هؤلاء',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4723,'{والسماء بنيناها بأيدٍ} بقوَّةٍ {وإنا لموسعون} لقادرون وقيل: جاعلون بين السَّماء والأرض سعةً',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4724,'{والأرض فرشناها} مهَّدناها لكم {فنعم الماهدون} نحن',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4725,'{ومن كل شيء خلقنا زوجين} صنفين كالذَّكر والأنثى والحلو والحامض والنُّور والظُّلمة {لعلكم تذكرون} فتعلموا أنَّ خالق الأزواج فردٌ',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4726,'{ففروا} من عذاب الله إلى طاعته',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4727,'{ولا تجعلوا مع الله إلهاً آخر إني لكم منه نذير مبين}',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4728,'{كذلك} كما أخبرناك {ما أتى الذين من قبلهم} من قبل أهل مكَّة {من رسول إلاَّ قالوا ساحرٌ أو مجنون}',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4729,'{أتواصوا به} أوصى بعضهم بعضاً بالتَّكذيب والألف للتَّوبيخ {بل هم قوم طاغون} عاصون',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4730,'{فتولًّ عنهم فما أنت بملوم} لأنَّك بلغت الرِّسالة',NULL,'1031',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4731,'{وذكر} ذكِّرهم بأيَّام الله {فإنَّ الذكرى تنفع المؤمنين}',NULL,'1032',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4732,'{وما خلقت الجن والإِنس إلاَّ ليعبدون} أي: إلاَّ لآمرهم بعبادتي وأدعوهم إليها وقيل: أراد المؤمنين منهم وكذا هو في قراءة ابن عباس: وما خلقت الجن والإِنس من المؤمنين إلاَّ ليعبدون {ما أريد منهم من رزق} أن يرزقوا أنفسهم أو أحداً من عبادي {وما أريد أن يطعمون} لأنِّي أنا الرَّزَّاق والمُطعم وقوله:',NULL,'1032',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4733,'{ما أريد منهم من رزق وما أريد أن يطعمون}',NULL,'1032',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4734,'{المتين} أي: المُبالغ في القُوَّة',NULL,'1032',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4735,'{فإنَّ للذين ظلموا} أَيْ: أهل مكَّة {ذنوباً} نصيباً من العذاب {مثل ذنوب} نصيب {أصحابهم} الذين هلكوا {فلا يستعجلون} إنْ أخَّرتهم إلى يوم القيامة',NULL,'1032',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4736,'{فويلٌ للذين كفروا من يومهم الذي يوعدون} من يوم القيامة',NULL,'1032',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4737,'{والطور} أقسم الله تعالى بالجبل الذي كلَّم عليه موسى وهو جبلٌ بمدين اسمه زبير',NULL,'1033',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4738,'{وكتاب مسطور} مكتوبٍ',NULL,'1033',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4739,'{في رقّ} وهو الجلد الذي يكتب فيه {منشور} مبسوطٍ أَيْ: دواوين الحفظة التي أثبتت فيها أعمال بني آدم',NULL,'1033',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4740,'{والبيت المعمور} وهو بيتٌ في السَّماء بإزاء الكعبة تزوره الملائكة',NULL,'1033',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4741,'{والسقف المرفوع} أي: السَّماء',NULL,'1033',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4742,'{والبحر المسجور} المملوء',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4743,'{إنَّ عذاب ربك لواقع} لنازلٌ كائن',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4744,'{ما له من دافع}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4745,'{يوم تمور السماء موراً} تتحرَّك وتضطرب وتدور يعني: يوم القيامة',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4746,'{وتسير الجبال سيرا}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4747,'{فويل يومئذ للمكذبين}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4748,'{الذين هم في خوض} باطلٍ {يلعبون} أي: تشاغلهم بكفرهم',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4749,'{يوم يدعون إلى نار جهنم دعاً} يُدفعون إليها دفعاً عنيفاً ويقال لهم:',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4750,'{هذه النار التي كنتم بها تكذبون}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4751,'{أفسحر هذا} الذي ترون {أم أنتم لا تبصرون} وهذا توبيخٌ لهم والمعنى: أتصدِّقون الآن عذاب الله وقوله:',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4752,'{اصلوها فاصبروا أو لا تصبروا سواء عليكم إنما تجزون ما كنتم تعملون}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4753,'{إنَّ المتقين في جنات ونعيم}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4754,'{فاكهين بما آتاهم ربهم} أي: معجبين به',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4755,'{كلوا واشربوا هنيئاً بما كنتم تعملون}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4756,'{متكئين على سرر مصفوفة وزوجناهم بحور عين}',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4757,'{والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم} يريد: إنَّه يلحق الأولاد بدرجة الآباء في الجنَّة إذا كانوا على مراتب وكذلك الآباء بدرجة الأبناء لتقرَّ بذلك أعينهم فيلحق بعضهم بعضاً إذا اجتمعوا في الإيمان من غير أن ينقص من أجر مَنْ هو أحسن عملاً شيئا بزيارته في درجة الأنقص عملاً وهو قوله: {وما ألتناهم} أَيْ: وما نقصناهم {من عملهم من شيء كلُّ امرئ بما كسب} بما عمل من خيرٍ أو شرٍّ {رهين} مرهونٌ يُؤخذ به',NULL,'1034',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4758,'{وأمددناهم بفاكهة ولحم} أَيْ: زدناهم',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4759,'{يتنازعون} يتناولون ويأخذ بعضهم من بعض {فيها كأساً لا لغوٌ فيها ولا تأثيم} لا يجري بينهم فيها باطلٌ ولا إثمٌ كما يجري بين شرب الخمر في الدُّنيا',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4760,'{ويطوف عليهم} بالخدمة {غلمان لهم كأنهم} في بياضهم وصفائهم {لؤلؤ مكنون} مخزونٌ مصونٌ',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4761,'{وأقبل بعضهم على بعض} في الجنَّة {يتساءلون} عن أحوالهم التي كانت في الدُّنيا',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4762,'{قالوا إنا كنا قبل في أهلنا مشفقين} خائفين من عذاب الله',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4763,'{فمنَّ الله علينا} بالجنَّة {ووقانا عذاب السموم} عذاب سموم جهنم وهو نارها وحرارتها',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4764,'{إنا كنا من قبل ندعوه إنه هو البر الرحيم}',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4765,'{فذكر} فذكِّرهم يا محمَّد الجنَّة والنَّار {فما أنت بنعمة ربك} برحمة ربّك وإكرامه إيَّاك بالنُّبوَّة {بكاهنٍ} تخبر بما في غدٍ من غير وحيٍ {ولا مجنون} كما تقولون',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4766,'{أم يقولون} بل أيقولون: هو {شاعرٌ نتربَّص به ريب المنون} ننتظر به الموت فيهلك',NULL,'1035',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4767,'{قل تربصوا فإني معكم من المتربصين} حتى يأتي أمر الله فيكم',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4768,'{أم تأمرهم أحلامهم} عقولهم {بهذا} أَيْ: بترك قبول الحقِّ من صاحب المعجزة {أم هم قوم طاغون} أَيْ: أم يكفرون طغياناً بعد ظهور الحقِّ',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4769,'{أم يقولون تقوَّله} أَي: القرآن من قبل نفسه ليس كما يقولون {بل لا يؤمنون} استكباراً',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4770,'{فليأتوا بحديث مثله إن كانوا صادقين} أنَّ محمداً يقوله من قبل نفسه',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4771,'{أم خلقوا من غير شيء} أَيْ: لغير شيءٍ يعني: أَخُلقوا عبثاً وسُدىً {أم هم الخالقون} أنفسهم',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4772,'{أم خلقوا السماوات والأرض بل لا يوقنون}',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4773,'{أم عندهم خزائن ربك} ما في خزائن ربِّك من العلم بما يكون في غدٍ {أم هم المصيطرون} المُسلَّطون الجبَّارون',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4774,'{أم لهم سلَّم} مرقىً إلى السَّماء {يستمعون فيه} أنَّ الذي هم عليه حقٌّ {فليأت مستمعهم} إن ادَّعوا ذلك {بسلطانٍ مبين} بحجَّةٍ واضحةٍ ثمَّ سفَّه أحلامهم في جعلهم البنات لله فقال:',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4775,'{أم له البنات ولكم البنون}',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4776,'{أم تسألهم أجراً} على ما جئتهم به {فهم من مغرم} غُرمٍ {مثقلون} مجهدون والمعنى: إنَّ الحجَّة واجبةٌ عليهم من كلِّ جهةٍ',NULL,'1036',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4777,'{أم عندهم الغيب} علم ما يؤول إليه أمر محمد صلى الله عليه وسلم {فهم يكتبون} يحكمون بأنَّه يموت فتسريح منه',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4778,'{أم يريدون كيداً} مكراً بكم في دار النَّدوة {فالذين كفروا هم المكيدون} المجزيون بكيدهم لأنَّ الله تعالى حفظ نبيَّه عليه السَلام من مكرهم وقُتلوا هم ببدر',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4779,'{أم لهم إله غير الله سبحان الله عما يشركون}',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4780,'{وإن يروا كسفاً} قطعاً {من السماء ساقطا يقولوا} لعنادهم وفرط شقاوتهم: {سحاب مركوم} بعضه على بعض وهذا جوابٌ لقولهم: {فأسقط علينا كسفاً من السماء} أخبر الله تعالى أنَّه لو فعل ذلك لم يؤمنوا',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4781,'{فذرهم حتى يُلاقوا يومهم الذي فيه يصعقون} يموتون ثمَّ أخبر أنَّه يعجِّل لهم العذاب في الدنيا فقال:',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4782,'{يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون}',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4783,'{وإنَّ للذين ظلموا} كفروا {عذاباً دون ذلك} قبل موتهم وهو الجوع والقحط سبع سنين ثمَّ أمره بالصَّبر فقال:',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4784,'{واصبر لحكم ربك فإنك بأعيننا} بحيث نراك ونحفظك ونرعاك {وسبح بحمد ربك حين تقوم} من مجلسك قل: سبحانك اللهم وبحمدك',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4785,'{ومن الليل} فسبحه أَيْ: صلِّ له صلاتي العشاء {وإدبار النجوم} أَيْ: ركعتي الفجر',NULL,'1037',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4786,'{والنجم إذا هوى} أي: والثُّريا إذا سقطت وقيل: القرآن إذا نزل مُتفرِّقاً نجوماً',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4787,'{ما ضلَّ صاحبكم} محمد عليه السَّلام {وما غوى}',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4788,'{وما ينطق عن الهوى} ما الذي يتكلَّم به ممَّا قاله بهواه',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4789,'{إن هو} ما هو {إلاَّ وحيٌ يوحى} إليه',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4790,'{علمه شديد القوى} أَيْ: جبريل عليه السَّلام',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4791,'{ذو مرَّة} قوَّةٍ شديدةٍ {فاستوى} جبريل عليه السَّلام في صورته التي خلقه الله عز وجل عليها',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4792,'{وهو بالأفق الأعلى} وذلك أن رسول الله صلى الله عليه وسلم سأله أن يريه نفسه على صورته فواعده ذلك بحراء فطلع له جبريل عليه السلام من الشرق فسدَّ الأفق إلى المغرب',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4793,'{ثم دنا فتدلى} هذا من المقلوب أَيْ: ثمَّ تدلى أَيْ: نزل من السَّماء فدنا من محمَّد عليه السَّلام',NULL,'1038',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4794,'{فكان} منه في القرب على قدر {قوسين أو أدنى} والمعنى: أنَّه بعد ما رأى رسول الله صلى الله عليه وسلم من عظمه وهاله ذلك ردَّه الله تعالى إلى صورة آدميٍّ حتى قرب من النبي صلى الله عليه وسلم للوحي وذلك قوله:',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4795,'{فأوحى إلى عبده} محمد صلى الله عليه وسلم {ما أوحى} الله عزَّ وجل إلى جبريل عليه السَّلام',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4796,'{ما كذب الفؤاد ما رأى} أَيْ: لم يكذب قلب محمَّد عليه السَّلام فيما رأى ليلة المعراج وذلك أنَّ الله جعل بصره في فؤاده حتى رآه وحقَّق الله تعالى تلك الرُّؤية وقال: إنها كانت رؤية حقيقة ولم تكن كذباً',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4797,'{أفتمارونه على ما يرى} أفتجادلونه في أنه رأى الله عز وجل',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4798,'{ولقد رآه} ربَّه وقيل: رأى جبريل على صورته التي خلق عليها {نزلة أخرى} مرَّة أخرى',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4799,'{عند سدرة المنتهى} وهي شجرةٌ إليها ينتهي علم الخلق وما وراءها غيبٌ لا يعلمه إلاَّ الله عز وجل',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4800,'{عندها جنة المأوى} وهي جنَّةٌ تصير إليها أرواح الشُّهداء',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4801,'{إذ يغشى السدرة ما يغشى} قيل: يغشاها فراش من ذهب وقيل: الملائكة أمثال الغربان',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4802,'{ما زاغ البصر وما طغى} هذا وصفٌ أدبِ النبي صلى الله عليه وسلم ليلة المعراج أَيْ: لم يمل بصره عما قصده له ولا جاوز إلى ما أُمر به',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4803,'{لقد رأى من آيات ربه الكبرى} أَيْ: ما رأى من الآيات العظام تلك اللَّيلة',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4804,'{أفرأيتم اللات والعُزَّى}',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4805,'{ومناة الثالثة الأخرى} هذه أصنامٌ من حجارةٍ كانت في جوف الكعبة والمعنى أخبرنا عن هذه الإناث التي تعبدونها وتزعمون أنَّها بنات الله أللَّهِ هي وأنتم تختارون الذُّكران وذلك قوله:',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4806,'{أَلَكُمُ الذَّكَرُ وَلَهُ الأُنْثَى}',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4807,'{تلك إذاً قسمة ضيزى} جائرةٌ ناقصةٌ',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4808,'{إن هي} ما هذه الأوثان {إلاَّ أسماء} لا حقيقة لها {سَمَّيْتُمُوهَا أَنْتُمْ وَآبَآؤُكُمْ مَا أَنْزَلَ اللَّهُ بِهَا} بعبادتها {من سلطان} حجَّةٍ وبرهانٍ {إن يتبعون} ما يتَّبعون في عبادتها وأنَّها شفعاء لهم {إلاَّ الظن وما تهوى الأنفس} يعني: إنَّ ذلك شيء ظنُّوه وأمرٌ سوَّلت لهم أنفسهم {ولقد جاءهم من ربهم الهدى} البيان على لسان محمد صلى الله عليه وسلم',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4809,'{أم للإِنسان ما تمنى} أَيظنُّون أنَّ لهم ما تمنَّوا من شفاعة الأصنام؟ ليس كما تمنَّوا بل',NULL,'1040',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4810,'{فللَّه الآخرة والأولى} فلا يجري في الدَّارين إلاَّ ما يريد',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4811,'{وكم من ملك في السماوات} هو أكرم على الله من هذه الأصنام {لا تغني شفاعتهم} عن أحدٍ {شيئاً إلاَّ من بعد أن يأذن الله} لهم في ذلك {لمن يشاء ويرضى} كقوله: {ولا يشفعون إلاَّ لمن ارتضى}',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4812,'{إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثى} يقولون: إنَّهم بنات الله',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4813,'{وما لهم به من علم إن يتبعون إلاَّ الظن وإنَّ الظَّنَّ لا يُغْنِي مِنَ الْحَقِّ شَيْئًا} إِنَّ ظنَّهم لا يدفع عنهم من العذاب شيئاً',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4814,'{فأعرض} يا محمَّد {عن من تولَّى عن ذكرنا} أعرضَ عن القرآن {ولم يرد إلاَّ الحياة الدنيا}',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4815,'{ذلك مبلغهم من العلم} يقول: ذلك نهاية علمهم أَنْ آثروا الدُّنيا على الآخرة وقوله:',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4816,'{ولله ما في السماوات وما في الأرض ليجزي الذين أساؤوا بما عملوا ويجزي الذين أحسنوا بالحسنى}',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4817,'{إلاَّ اللمم} يعني: صغار الذُّنوب كالنَّظرة والقُبلة وقوله: {إذ أنشأكم من الأرض} يعني: خلق أباكم من التُّراب {وإذ أنتم أجنَّة} جمع جنين {فلا تزكوا أنفسكم} لا تمدحوها {هو أعلم بمن اتقى} عمل حسنةً',NULL,'1041',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4818,'{أفرأيت الذي تولى} أعرض عن الإيمان يعني: الوليد بن المغيرة وكان قد ابتع رسول الله صلى الله عليه وسلم فعيَّره بعض المشركين على ذلك فقال: إنّي أخشى عذاب الله فضمن له إن هو أعطاه شيئاً من ماله إلى شركه أنْ يتحمَّل عنه عذاب الله فرجع في الشِّرك وأعطى صاحبه الضَّامن من بعض ما كان ضمن له ومنعه الباقي وذلك قوله:',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4819,'{وأعطى قليلاً وأكدى} أَيْ: قطع ذلك ومنعه',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4820,'{أعنده علم الغيب فهو يرى} ما غاب من أمر الآخرة حتى علم أنَّ غيره يحمل عنه العذاب',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4821,'{أم لم ينبأ بما في صحف موسى} أسفار التَّوراة',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4822,'{و} صحف {إبراهيم الذي وفَّى} أكمل ما أُمر به أتمه ثمَّ بيَّن ذلك فقال:',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4823,'{ألا تزر وازرةٌ وزر أخرى} أَيْ: لا تؤخذ نفسٌ بمأثم غيرها',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4824,'{وإن ليس للإِنسان إلاَّ ما سعى} عمل لآخرته',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4825,'{وإنَّ سعيه} عمله {سوف يرى} في ميزانه من خيرٍ وشرٍّ',NULL,'1042',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4826,'{ثم يجزاه} يجزى عليه {الجزاء الأوفى} الأتمَّ',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4827,'{وأنَّ إلى ربك المنتهى} المصير والمرجع',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4828,'{وأنه هو أضحك} مَنْ شاء من خلقه {وأبكى} نم شاء منهم',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4829,'{وأنه هو أمات} في الدنيا {وأحيا} للبعث وقوله:',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4830,'{وأنه خلق الزوجين الذكر والأنثى}',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4831,'{إذا تمنى} أي: نصب في الرَّحم',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4832,'{وأنَّ عليه النشأة الأخرى} الخلق الآخر بعد الموت',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4833,'{وأنه هو أغنى} بالمال {وأقنى} أرضى بما أعطى وقيل: أقنى: أعطى أصول الأموال وما يتَّخذ فيه قنيةً',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4834,'{وأنَّه هو رب الشعرى} وهي كوكب خلف الجوزاء كانت تُعبد في الجاهليَّة',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4835,'{وأنه أهلك عاداً الأولى} قوم هود',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4836,'{وثمود فما أبقى}',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4837,'{وقوم نوح من قبل إنهم كانوا هم أظلم وأطغى}',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4838,'{والمؤتفكة} قرى قوم لوط {أهوى} أسقطها إلى الأرض بعد رفعها',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4839,'{فغشَّاها ما غشَّى} ألبسها العذاب والحجارة',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4840,'{فبأي آلاء ربك تتمارى} بأيِّ نِعَم ربِّك التي تدلُّ على توحيده وقدرته تتشكَّكُ أيُّها الإنسان؟',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4841,'{هذا} محمَّدٌ {نذير من النذر الأولى} أَيْ: هو رسولٌ أُرسل إليكم كما أُرسل مَنْ قبله من الرُّسل',NULL,'1043',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4842,'{أزفت الآزفة} قربت القيامة',NULL,'1044',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4843,'{ليس لها من دون الله كاشفة} لا يكشف عنها إلاَّ الله تعالى كقوله: {لا يجلِّيها لوقتها إلا هو}',NULL,'1044',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4844,'{أفمن هذا الحديث} أي: القرآن {تعجبون}',NULL,'1044',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4845,'{وتضحكون ولا تبكون}',NULL,'1044',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4846,'{وأنتم سامدون} لاهون غافلون',NULL,'1044',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4847,'{فاسجدوا لله واعبدوا} معناه: فاسجدوا لله واعبدوا الذي خلق السماوات والأرض ولا تسجدوا للأصنام التي ذكرت في هذه السُّورة',NULL,'1044',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4848,'{اقتربت الساعة} دنت القيامة {وانشقَّ القمر} انفلق بنصفين عَلَى عَهْدِ رَسُولِ اللَّهِ صَلَّى اللَّهُ عَلَيْهِ وسلم وذلك أنَّ أهل مكَّة سألوه آيةً فأراهم القمر فلقتين حتى رأوا حراءً بنيهما فأخبر الله تعالى أنَّ ذلك من علامات قرب السَّاعة',NULL,'1045',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4849,'{وإن يروا} يعني: أهل مكَّة {آية} تدلُّ على صدق محمد صَلَّى اللَّهُ عَلَيْهِ وسلم {يعرضوا ويقولوا سحرٌ مستمر} ذاهب باطلٌ يذهب وقيل: محكمٌ شديدٌ وقوله:',NULL,'1045',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4850,'{وكلُّ أمر مستقر} أَيْ: يستقرُّ قرار تكذيبهم وقرار تصديق المؤمنين يعني: عند ظهور الثَّواب والعقاب',NULL,'1045',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4851,'{ولقد جاءهم} جاء أهل مكَّة {من الأنباء} أخبار إهلاك الأمم المُكذِّبة {ما فيه مزدجر} متناهى منتهى',NULL,'1045',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4852,'{حكمة بالغة} أَيْ: ما أتاهم من أخبار مَنْ قبلهم حكمةٌ بالغةٌ تامَّةٌ ليس فيها نقصانٌ أي: القرآن وذلك أنَّ تلك الأخبار قُصَّت عليهم في القرآن {فما تغن النذر} جمع نذير أَيْ: فليست تغني عن التَّكذيب',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4853,'{فتولَّ عنهم} وتمَّ الكلام ثمَّ قال: {يوم يدع الداع إلى شيء نكر} مُنكرٍ وهو النَّار',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4854,'{خشعاً} ذليلةً {أبصارهم يخرجون من الأجداث} القبور {كأنهم جراد منتشر} كقوله: {كالفراش المبثوث}',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4855,'{مهطعين} مُقبلين ناظرين {إلى الداع} إلى مَنْ يدعوهم إلى المحشر {يقول الكافرون هذا يوم عسر} شديدٌ',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4856,'{كذبت قبلهم} قبل أهل مكَّة {قوم نوح فكذَّبوا عبدنا} نوحاً {وقالوا: مجنون وازدجر} زُجر ونُهِرَ ونُهي عن دعوته ومقالته',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4857,'{فدعا ربَّه أني مغلوب} مقهورٌ {فانتصر} فانتقم لي منهم',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4858,'{ففتحنا أبواب السماء بماء منهمر} سائلٍ',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4859,'{وفجرنا الأرض عيوناً} فتحناها بعيون الماء {فالتقى الماء} ماءُ السَّماء وماءُ الأرض {على أمر قد قدر} قُضي عليهم في أمِّ الكتاب',NULL,'1046',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4860,'{وحملناه} أَيْ: نوحاً {على ذات ألواح} وهي السَّفينة {ودسر} يعني: ما تُشدَّ به السَّفينة من المسامير والشرط',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4861,'{تجري بأعيننا} بمرأى من وحفظٍ {جزاءً لمن كان كفر} يعني: نوحاً أَيْ: فعلنا ذلك ثواباً له إذ كُفر به وكُذِّب',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4862,'{ولقد تركناها آية} تركنا تلك القِصَّة آيةً: علامةً ليعتبر بها {فهل من مدَّكر} مُتَّعظٍ بها',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4863,'{فكيف كان عذابي} استفهام معناه التَّقرير {ونذر} أي: إنذاري',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4864,'{ولقد يسرنا القرآن للذكر} سهَّلناه للحفظ فليس يحفظ كتابٌ من كتب الله ظاهراً إلاَّ القرآن {فهل من مدكر} مُتَّعظ بمواعظه',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4865,'{كذبت عاد فكيف كان عذابي ونذر}',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4866,'{إنا أرسلنا عليهم ريحاً صرصراً} شديدةً ذات صوتٍ {في يوم نحس} شؤمٍ {مستمر} دائم الشُّؤم',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4867,'{تنزع الناس} تقلعهم من مواضعهم {كأنهم أعجاز نخل} أصول نخلٍ {منقعر} مُنقطعٍ ساقطٍ شُبِّهوا وقد كبَّتهم الرِّيح على وجوههم بنخيل سقطت على الأرض',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4868,'{فكيف كان عذابي ونذر}',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4869,'{ولقد يسرنا القرآن للذكر فهل من مدكر}',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4870,'{كذبت ثمود بالنذر} جمع نذير وقوله:',NULL,'1047',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4871,'{إنا إذاً لفي ضلال} ذهابٍ عن الصَّواب {وسعر} جنون',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4872,'{أألقي الذكر عليه من بيننا} أنكروا أن يكون مخصوما بالوحي من بينهم {بل هو كذَّاب أشر} بَطِرٌ يريد أن يتعظَّم علينا',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4873,'{سيعلمون غداً} عند نزول العذاب بهم {من الكذاب الأشر}',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4874,'{إنا مرسلو الناقة} مخرجوها من الهضبة كما سألوا {فتنة لهم} محنةً لهم لنختبرهم {فارتقبهم} انتظر ما هم صانعون {واصطبر}',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4875,'{ونبئهم أنَّ الماء قسمة بينهم} بين ثمود والناقة غِبَّاً لهم يومٌ ولها يومٌ {كلُّ شرب} نصيبٍ من الماء {محتضر} يحضره القوم يوماً والنَّاقة يوماً',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4876,'{فنادوا صاحبهم} قُدَاراً عاقر الناقة {فتعاطى} تناول النَّاقة بالعقر فعقرها وقوله:',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4877,'{فكيف كان عذابي ونذر}',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4878,'{كهشيم المحتظر} هو الرَّجل يجعل لغنمه حظيرةً بالشَّجر والشَّوك دون السِّباع مما سقط من ذلك فداسته الغنم فهو الهشيم وقوله:',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4879,'{ولقد يسرنا القرآن للذكر فهل من مدكر}',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4880,'{كذبت قوم لوط بالنذر}',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4881,'{إلاَّ آل لوط} أَي: أتباعه على دينه من أهله وأُمَّته {نجيناهم} من العذاب {بسحر} من الأسحار كقوله: {فأسر بأهلك} الآية؟',NULL,'1048',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4882,'{نعمة من عندنا} عليهم بالإنجاء {كذلك} كما جزينا لوطاً وآله {نجزي مَنْ شكر} آمن بالله وأطاعه',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4883,'{ولقد أنذرهم} خوَّفهم لوط {بطشتنا} أخذنا إيَّاهم بالعقوبة {فتماروا بالنذر} كذَّبوا بإنكاره شكَّاً منهم',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4884,'{ولقد راودوه عن ضيفه} سألوه أن يُخلِّي بينهم وبين القوم الذين أتوه في صورة الأضياف وكانوا ملائكةً {فطمسنا أعينهم} أعميناها وصيّرناها كسائر الوجه وقلنا لهم: {فذوقوا عذابي ونذر}',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4885,'{ولقد صبحهم بكرةً} جاءهم صباحاً {عذابٌ مستقر} ثابتٌ لأنَّه أفضى بهم إلى عذاب الآخرة',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4886,'{فذوقوا عذابي ونذر}',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4887,'{ولقد يسرنا القرآن للذكر فهل من مدكر}',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4888,'{ولقد جاء آل فرعون النذر} الإنذار على لسان موسى وهارون عليهما السَّلام',NULL,'1039',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4889,'{كذبوا بآياتنا} التِّسع {كلها فأخذناهم} بالعذاب {أخذ عزيز} قويٍّ {مقتدر} قادرٍ لا يعجزه شيء ثمَّ خاطب العرب فقال:',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4890,'{أكفاركم خيرٌ من أولئكم} الذين ذكرنا قصَّتهم {أم لكم براءة} من العذاب {في الزبر} الكتب تأمنون بها من العذاب',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4891,'{أم يقولون} كفَّار مكَّة: {نحن جميع منتصر} جماعةٌ منصورون',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4892,'{سيهزم الجمع} أَي: جمعهم {ويولون الدبر} ينهزمون فيرجعون على أدباره وكان هذا يوم بدر',NULL,'1049',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4893,'{بل الساعة موعدهم} للعذاب {والساعة أدهى وأمر} أشدُّ أمراً وأشدُّ مرارةً ممَّا يلحقهم في الدُّنيا',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4894,'{إنَّ المجرمين في ضلال} في الدُّنيا {وسعر} نارٍ في الآخرة',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4895,'{يوم يسحبون} يجرُّون {في النار على وجوههم} ويقال لهم: {ذوقوا مسَّ سقر} إصابة جهنَّم إياكم بالعذاب',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4896,'{إنا كلَّ شيء خلقناه بقدر} أَيْ: كلُّ ما خلقناه فمقدورٌ مكتوبٌ في اللَّوح المحفوظ وهذه الآيات نزلت في القدرية الذين يُكذِّبون بالقدر',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4897,'{وما أمرنا} لشيءٍ إذا أردنا تكوينه {إلاَّ واحدة} كلمةٌ واحدةٌ وهي كن {كلمح بالبصر} في السُّرعة كخطفة البصر',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4898,'{ولقد أهلكنا أشياعكم} أشباهكم في الكفر من الأمم الماضية',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4899,'{وكل شيء فعلوه في الزبر} في كتب الحفظة',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4900,'{وكلُّ صغير وكبير} من أعمالهم {مستطر} مكتوبٌ',NULL,'1050',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4901,'{إنَّ المتقين في جنات ونهر} ضياءٍ وسعةٍ وقيل: أراد أنهاراً فوحَّد لوفاق الفواصل',NULL,'1051',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4902,'{في مقعد صدق} في مجلس لا لغوٌ فيه ولا تأثيمٌ {عند مليك مقتدر} وهو الله تعالى وعند إشارةٌ إلى الرُّتبة والقربة من فضل الله ورحمته',NULL,'1051',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4903,'{الرحمن}',NULL,'1052',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4904,'{علم القرآن} علَّم نبيَّه عليه السَّلام القرآن ليس كما يقول المشركون: إنَّما يُعلِّمه بَشرٌ وقيل: معناه: يسَّر القرآن لأَنْ يُذكر فعلَّمه هذه الأُمَّة حتى حفظوه',NULL,'1052',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4905,'{خلق الإنسان} يعني: النبيَّ صلى الله عليه وسلم',NULL,'1052',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4906,'{علمه البيان} القرآن الذين فيه بيان كلِّ شيءٍ وقيل: {خلق الإنسان} يعني: ابن آدم فعلَّمه النُّطق وفضَّله به على سائر الحيوان',NULL,'1052',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4907,'{الشمس والقمر} يجريان {بحسبان} بحسابٍ لا يجاوزانه',NULL,'1052',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4908,'{والنجم} كلُّ نبتٍ لا يقوم على ساق ولا يبقى على الشَّتاء {والشجر يسجدان} يخضعان لله تعالى بما يريد منهما',NULL,'1052',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (4909,'{والسماء رفعها} فوق الأرض {ووضع الميزان} العدل والإنصاف',NULL,'1053',NULL,NULL,'0');
