INSERT INTO [b2547] VALUES (4914,'{والحب ذو العصف} أَيْ: ورق الزَّرع وقيل: هو التِّبن {والريحان} الرِّزق ثمَّ خاطب الجن والإنس فقال:',NULL,'1053',NULL,NULL,'0');
