INSERT INTO [b2547] VALUES (4916,'{خلق الإنسان} آدم {من صلصال} طينٍ يابسٍ يُسمع له صلصلةٌ {كالفخار} وهو ما طبخ من الطِّين',NULL,'1053',NULL,NULL,'0');
