INSERT INTO [b2547] VALUES (5014,'{وفرش مرفوعة} على السررظ',NULL,'1060',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5015,'{إنا أنشأناهن} خلقناهنَّ أَيْ: الحور العين {إنشاء} خلقاً من غير ولادةٍ',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5016,'{فجعلناهنَّ أبكاراً} عذارى',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5017,'{عُرباً} مُتحبِّبات إلى الأزواج عواشق لهم {أتراباً} مُستوياتٍ في السنِّ',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5018,'{لأصحاب اليمين}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5019,'{ثلة من الأولين} من الأمم الماضية',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5020,'{وثلة من الآخرين} من هذه الأُمَّة يعني: إنَّ أصحاب الجنَّة نصفان: نصفٌ من الأمم الماضية ونصفٌ من هذه الأمَّة ثمَّ ذكر منازل أصحاب الشِّمال فقال:',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5021,'{وأصحاب الشمال ما أصحاب الشمال}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5022,'{في سموم} ريحٍ حارَّةٍ {وحميم}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5023,'{وظلٍّ من يحموم} دخانٍ شديد السَّواد {لا بارد} المنزل {ولا كريم} المنظر',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5024,'{إنهم كانوا قبل ذلك} في الدُّنيا {مترفين} مُنعَّمين لا يتعبون في طاعة الله',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5025,'{وكانوا يصرون على الحنث العظيم} يُقيمون على الذَّنب العظيم وهو الشِّرك وكانوا يُنكرون البعث {وكانوا يقولون أإذا متنا وكنا تراباً وعظاماً أَإِنّا لمبعوثون} فقال الله تعالى:',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5026,'{وكانوا يصرون على الحنث}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5027,'{أإذا متنا وكنا تراباً وعظاما أإنا لمبعوثون}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5028,'{أو آباؤنا الأولون}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5029,'{قل إنَّ الأولين والآخرين} {لمجموعون إلى ميقات يوم معلوم} وهو يوم القيامة ومعنى {إلى ميقات} لميقات يوم وقوله:',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5030,'{لمجموعون إلى ميقات يوم معلوم}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5031,'{ثم إنكم أيها الضالون المكذبون}',NULL,'1061',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5032,'{لآكلون من شجر من زقوم}',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5033,'{فمالئون منها البطون}',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5034,'{فشاربون عليه من الحميم}',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5035,'{شرب الهيم} أَيْ: الإِبل العطاش',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5036,'{هذا نزلهم} ما أعدَّ لهم من الرِّزق {يوم الدين} المجازاة',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5037,'{نحن خلقناكم} ابتداءً {فلولا} فهلاَّ {تصدّقون} بالخلق الثَّاني وهو البعث',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5038,'{أفرأيتم ما تمنون} تصبُّون في الأرحام من المنيّ',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5039,'{أأنتم تخلقونه} بشراً {أم نحن الخالقون}',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5040,'{نحن قدَّرنا} قضينا {بينكم الموت وما نحن بمسبوقين}',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5041,'{على أن نبدِّل أمثالكم} أَيْ: إن أردنا أن نخلق خلقاً غيركم لم نُسبق ولا فاتنا ذلك {وننشئكم} نخلقكم {في ما لا تعلمون} من الصُّور أَيْ: نجعلكم قردةً وخنازير والمعنى: لسنا عاجزين عن خلق أمثالكم بدلاً منكم ومسخكم من صوركم إلى غيرها',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5042,'{ولقد علمتم النشأة الأولى} الخلقة الأولى أَيْ: أقررتم بأنَّ الله خلقكم في بطون أُمَّهاتكم {فلولا تذكرون} أنِّي قادرٌ على إعادتكم',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5043,'{أفرأيتم ما تحرثون} تقلبون من الأرض وتلقون فيه من البذر',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5044,'{أأنتم تزرعونه} تنبتونه {أم نحن الزارعون}',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5045,'{لو نشاء لجعلناه حطاماً} تبناً يابساً لا حَبَّ فيه {فظلتم تفكهون} تعجبون وتندمون ممَّا نزل بكم وممَّا علمتم من الحرث وتقولون:',NULL,'1062',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5046,'{إنا لمغرمون} صار ما أنفقنا على الحرث غُرْماً علينا',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5047,'{بل نحن محرومون} ممنوعون منعنا رزقنا وقوله:',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5048,'{أفرأيتم الماء الذي تشربون}',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5049,'{أأنتم أنزلتموه من المزن أم نحن المنزلون}',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5050,'{أجاجاً} أَيْ: مِلحاً لا يمكن شربه',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5051,'{أفرأيتم النار التي تورون} تقدحون',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5052,'{أأنتم أنشأتم} خلقتم {شجرتها} التي تخرج منها',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5053,'{نحن جعلناها تذكرة} يتذكَّر بها نار جهنَّم {ومتاعاً} ومنفعةً {للمقوين} للمسافرين',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5054,'{فسبح باسم ربك العظيم} أَيْ: نَزِّه الله ممَّا يقول المشركون',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5055,'{فلا أقسم} لا زائدة {بمواقع النجوم} مساقطها ومغاربها وقيل: أراد نجوم القرآن',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5056,'{وإنه لقسم لو تعلمون عظيم}',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5057,'{إنه لقرآن كريم} حسنٌ عزيزٌ',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5058,'{في كتاب مكنون} مصونٍ عند الله',NULL,'1063',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5059,'{لا يمسه} باليد أَيْ: المصحف {إلاَّ المطهرون} من الجنابات والأحداث',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5060,'{تنزيل من رب العالمين}',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5061,'{أفبهذا الحديث} أَيْ: القرآن {أنتم مدهنون} مُكِّذبون',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5062,'{وتجعلون رزقكم} شكر رزقكم فحذف الشُّكر {أنكم تكذبون} بسقيا الله إذا مُطرتم وتقولون: مطرنا بنوء كذا',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5063,'{فلولا} فهلاَّ {إذا بلغت} الرُّوح {الحلقوم}',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5064,'{وأنتم} يا أصحاب الميت {حينئذٍ تنظرون} إليه وهو في النَّزع',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5065,'{ونحن أقرب إليه منكم} بالعلم والقدرة {ولكن لا تبصرون} لا تعلمون ذلك',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5066,'{فلولا إن كنتم غير مَدِينين} مملوكين ومجزيين',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5067,'{ترجعونها} أَيْ: تردُّون الرُّوح إلى الميِّت {إن كنتم صادقين} أنَّكم غير مملوكين وغير مُدْبِرين وقوله: {ترجعونها} جوابٌ واحدٌ لشيئين وقوله: {فلولا إذا بلغت الحلقوم} وقوله: {فلولا إن كنتم} ثمَّ ذكر مآل الخلق بعد الموت فقال:',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5068,'{فأما إن كان من المقربين} {فروح} فلهم روحٌ أَيْ: استراحةٌ وبردٌ {وريحان} ورزقٌ حسنٌ',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5069,'{وأما إن كان من أصحاب اليمين} {فسلام لك من أصحاب اليمين} أَيْ: إنَّك ترى فيهم ما تحبُّ من السَّلامة وقد علمت ما أعدَّ لهم من الجزاء لأنَّه قد بُيِّن لك في قوله: {في سدر مخضود} الآيات',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5070,'{وأما إن كان من أصحاب اليمين} ز',NULL,'1064',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5071,'{فسلام لك من أصحاب اليمين}',NULL,'1065',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5072,'{وأما إن كان من المكذبين الضالين} وهم أصحاب المشأمة',NULL,'1065',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5073,'{فنزل من حميم} فلهم نزلٌ أعدَّ لهم من شراب جهنَّم',NULL,'1065',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5074,'{وتصلية جحيم} إدخال النَّار',NULL,'1065',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5075,'{إن هذا} الذي ذكرت {لهو حق اليقين}',NULL,'1065',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5076,'{فسبح باسم ربك العظيم} أَيْ: نزِّه الله من السُّوء',NULL,'1065',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5077,'{سبح لله ما في السماوات والأرض وهو العزيز الحكيم} ذُكر تفسيرها في قوله: {إن مِنْ شيءٍ إلاَّ يُسَبِّحُ بحمده}',NULL,'1066',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5078,'{له ملك السماوات والأرض يحيي ويميت وهو على كل شيء قدير}',NULL,'1066',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5079,'{هو الأوَّل} قبل كلِّ شيءٍ فكلُّ شيءٍ دونه {والباطن} العالم بكلِّ شيءٍ',NULL,'1066',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5080,'{يَعْلَمُ مَا يَلِجُ فِي الأَرْضِ} ما يدخل فيها من مطرٍِ وغيره {وما يخرج منها} من نباتٍ وشجرٍ {وما ينزل من السماء} من رزقٍ ومطرٍ ومَلكٍ وأمرٍ {وما يعرج فيها} يصعد إليها من عملٍ {وهو معكم} بالعلم والقدرة {أين ما كنتم}',NULL,'1066',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5081,'{له ملك السماوات والأرض وإلى الله ترجع الأمور}',NULL,'1066',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5082,'{يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي الليل وهو عليم بذات الصدور}',NULL,'1066',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5083,'{آمنوا بالله ورسوله} صدِّقوا بأنَّ الله تعالى واحدٌ وأنَّ محمداً رسول الله {وأنفقوا} من المال الذي {جعلكم مستخلفين فيه} أَيْ: كان لغيركم فملكتموه وقوله:',NULL,'1067',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5084,'{وقد أخذ ميثاقكم} أَيْ: حين أخرجكم من ظهر آدم عليه السَّلام بأنَّ الله ربُّكم لا إله لكم سواه {إن كنتم مؤمنين} أي: إِن كنتم على أن تؤمنوا يوماً من الأيام',NULL,'1067',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5085,'{هو الذي ينزل على عبده آيات بينات ليخرجكم من الظلمات إلى النور وإن الله بكم لرؤوف رحيم}',NULL,'1067',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5086,'{وما لكم أنْ لا تنفقوا في سبيل الله ولله ميراث السماوات والأرض} أَيْ: أَيُّ شيءٍ لكم في ترك الإِنفاق في طاعة الله وأنتم ميِّتون تاركون أموالكم ثمَّ بيَّن فضل السَّابقين في الإنفاق والجهاد فقال {لا يستوي منكم مَنْ أنفق من قبل الفتح} يعني: فتح مكَّة {وقاتل} جاهد مع رسول الله صلى الله عليه وسلم أعداء الله {أولئك أعظم درجة} يعني: عند الله {من الذين أنفقوا من بعد} الفتح {وقاتلوا وكلاً} من الفريقين {وعد الله الحسنى} الجنَّة',NULL,'1067',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5087,'{مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا} ذُكر تفسيره في سورة البقرة',NULL,'1067',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5088,'{يوم ترى المؤمنين والمؤمنات} وهو يوم القيامة {يسعى نورهم} على الصِّراط {بين أيديهم وبأيمانهم} وتقول لهم الملائكة: {بشراكم اليوم جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا ذلك هو الفوز العظيم}',NULL,'1067',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5089,'{يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا نقتبس من نوركم} انتظرونا وقفوا لنا تستضيء بنوركم {قيل} لهم {ارجعوا وراءكم} من حيث جئتم {فالتمسوا نوراً} فلا نور لكم عندنا {فضرب بينهم} بين المؤمنين والمنافقين {بسور} وهو حاجزٌ بين الجنَّة والنار وقيل: هو سور الأعراف {له باب} في ذلك السُّور بابٌ {باطنه فيه الرحمة} لأنَّ ذلك الباب يُفضي إلى الجنَّة {وظاهره من قبله} أَيْ: من قبل ذلك الظَّاهر {العذاب} وهو النَّار',NULL,'1068',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5090,'{ينادونهم} ينادي المنافقون المؤمنين: {ألم نكن معكم} في الدُّنيا نناكحكم ونوارثكم {قالوا بلى ولكنكم فتنتم أنفسكم} آثمتموها بالنِّفاق {وتربصتم} بمحمدَّ عليه السَّلام الموت {وارتبتم} شككتم في الإيمان {وغرَّتكم الأمانيّ} ما كنتم تمنَّون من نزول الدَّوابر بالمؤمنين {حتى جاء أمر الله} الموت {وغرَّكم بالله} أَيْ: بحلمه وإمهاله {الغرور} الشَّيطان',NULL,'1068',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5091,'{فاليوم لا يؤخذ منكم فدية} بدلٌ {ولا من الذين كفروا} وهم المشركون {مأواكم النار} منزلكم النَّار {هي مولاكم} أولى بكم {وبئس المصير} هي',NULL,'1068',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5092,'{ألم يأن للذين آمنوا} ألم يحن {أن تخشع قلوبهم} ترقَّ وتلين {لذكر الله وما نزل من الحق} وهو القرآن وهذا حثٌّ من الله تعالى لقومٍ من المؤمنين على الرِّقة والخشوع {ولا يكونوا كالذين أوتوا الكتاب من قبل} أي: اليهود والنَّصارى {فطال عليهم الأمد} الزَّمان بينهم وبين أنبيائهم {فقست قلوبهم} لم تَلِنْ لذكر الله ونسوا ما عهد الله سبحانه إليهم في كتابهم {وكثير منهم فاسقون} وهم الذين تركوا الإِيمان بمحمد صلى الله عليه وسلم',NULL,'1068',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5093,'{اعلموا أنَّ الله يحيي الأرض بعد موتها قد بينا لكم الآيات} أي: إنَّ إحياء الأرض بعد موتها دليلٌ على توحيد الله تعالى وقدرته',NULL,'1069',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5094,'{إن المصدقين والمصدقات} الذي يتصدَّقون وينفقون في سبيل الله {وأقرضوا الله قرضاً حسناً} بالنَّفقة في سبيله {يضاعف لهم} ما عملوا {ولهم أجرٌ كريم} وهو الجنَّة',NULL,'1069',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5095,'{وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ أُولَئِكَ هُمُ الصِّدِّيقُونَ} المُبالغون في الصِّدق {والشهداء عند ربهم} أَي: الأنبياء عليهم السَّلام {لهم أجرهم ونورهم} في ظلمة القبر وقيل: هم جميع المؤمنين',NULL,'1069',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5096,'{اعلموا أنما الحياة الدنيا لعب ولهو} في انقضائها وقلَّة حاصلها {وزينة} يتزيَّنون بها {وتفاخرٌ بينكم} يفخر بها بعضكم على بعض {وتكاثر في الأموال والأولاد} مباهاةٌ بكثرتها ثمَّ ضرب لها مثلاً فقال: {كمثل غيث} مطرٍ {أعجب الكفار} أي: الزُّراع {نباتُه} ما أنبته ذلك الغيث {ثم يهيج} ييبس {فتراه مصفراً} بعد يبسه {ثمَّ يكون حطاما} هشيما متفتتا كذلك الإِنسان يهرم ثمَّ يموت ويبلى {وفي الآخرة عذاب شديد} للكفَّار {ومغفرة من الله ورضوان} لأوليائه',NULL,'1069',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5097,'{سابقوا إلى مغفرة من ربكم} ذُكر في سورة آل عمران عند قوله: {وسارعوا إلى مغفرةٍ من ربِّكم} الآية',NULL,'1070',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5098,'{ما أصاب من مصيبة في الأرض} بالجدبِ {ولا في أنفسكم} بالمرض والموت والخسران {إلاَّ في كتاب} أي: اللَّوح المحفوظ {من قبل أن نبرأها} نخلق تلك المصيبة {إنَّ ذلك على الله يسير} أَيْ: خلقها في وقتها بعد أَنْ كتبها في اللَّوح المحفوظ',NULL,'1070',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5099,'{لكي لا تأسوا على ما فاتكم} من الدُّنيا {ولا تفرحوا بما آتاكم} أعطاكم منها أَيْ: لكيلا تحزنوا حزناً يُطغيكم ولا تبطروا بالفرح بعد أَنْ علمتم أنَّ ما يصيبكم من خيرٍ وشرٍّ فمكتوب لا يخطئكم {والله لا يحب كل مختال} مُتكبِّرٍ بما أُوتي من الدُّنيا {فخور} به على النَّاس',NULL,'1070',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5100,'{الذين يبخلون ويأمرون الناس بالبخل} ذُكر في سورة النِّساء',NULL,'1071',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5101,'{لقد أرسلنا رسلنا بالبينات} بالدّلالات الواضحات {وأنزلنا معهم الكتاب والميزان} العدل {ليقوم الناس بالقسط} ليتعامل النَّاس بينهم بالعدل {وأنزلنا الحديد} وذلك أنَّ آدم عليه السَّلام نزل إلى الأرض بالعلاة والمطرقة وآلة الحدَّادين {فيه بأس شديد} قوَّةٌ وشدَّةٌ يُمتنع بها ويُحارب {ومنافع للناس} يستعملونه في أدواتهم {وليعلم الله} أَيْ: أرسلنا الرُّسل ومعهم هذه الأشياء ليتعامل النَّاس بالحقِّ وليرى الله مَنْ ينصر دينه {ورسله بالغيب} في الدنيا وقوله:',NULL,'1071',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5102,'{ولقد أرسلنا نوحا وإبراهيم وجعلنا في ذريتهما النبوة والكتاب فمنهم مهتد وكثير منهم فاسقون}',NULL,'1071',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5103,'{ورهبانية ابتدعوها} أَي: ابتدعوا من قبل أنفسهم رهبانيَّةً أَي: التَّرهُّب في الصَّوامع {ما كتبناها عليهم} ما أمرناهم بها {إلاَّ ابتغاء رضوان الله} لكنَّهم ابتغوا بتلك الرَّهبانيَّة رضوان الله {فما رعوها حق رعايتها} أي: قصورا في تلك الرَّهبانيَّة حين لم يؤمنوا بمحمد عليه السَّلام {فآتينا الذين آمنوا منهم} بمحمَّدٍ عليه السَّلام {أجرهم وكثير منهم فاسقون} وهم الذين لم يؤمنوا به',NULL,'1071',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5104,'{يا أيها الذين آمنوا} بالتَّوراة والإنجيل {اتقوا الله وآمنوا برسوله} محمد عليه السَّلام {يؤتكم كفلين} نصيبين {من رحمته} نصيباً بإيمانكم الأوَّل ونصيباً بإيمانكم بمحمَّد عليه السَّلام وكتابه {ويجعل لكم نوراً تمشون به} في الآخرة على الصِّراط {ويغفر لكم} وعدهم الله هذه الأشياء كلَّها على الإيمان بمحمد عليه السلام ثمَّ قال:',NULL,'1072',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5105,'{لئلا يعلم} أي: ليعلم ولا زائدة {أهل الكتاب} اليهود والنَّصارى {ألا يقدرون على شيء} أنَّهم لا يقدرون على شيءٍ {من فضل الله} يعني: إِنْ لم يؤمنوا لم يُؤتهم الله شيئاً ممَّا ذُكر {وأنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذو الفضل العظيم}',NULL,'1072',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5106,'{قد سمع الله قول التي تجادلك في زوجها} نزلت في سبب خولة بنت ثعلبة وزوجها أوس بن الصَّامت ظاهر منها وكان ذلك أوَّل ظهارٍ في الإِسلام وكان الظِّهار من طلاق الجاهليَّة فأتت رسول الله صلى الله عليه وسلم وذكرت أنَّ زوجها ظاهر منها فقال رسول الله صلى الله عليه وسلم: حَرُمْتِ عليه فقالت: أشكو إلى الله فاقتي ووحدتي وصبيةً صغاراً وجعلت تُراجع رسول الله صلى الله عليه وسلم فإذا قال لها: حَرُمْتِ عليه هتفت وشكت إلى الله وقوله: {والله يسمع تحاوركما} أَيْ: تخاطبكما ومراجعتكما الكلام ثمَّ ذمَّ الظَّهار فقال:',NULL,'1073',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5107,'{الذين يظاهرون منكم من نسائهم ما هنَّ أمهاتهم} أَيْ: ما اللواتي يجعلن من الزَّوجات كالأمهات بأمهاتٍ {إن أمهاتهم إلاَّ اللائي ولدنهم} ما أُمهاتهم إلاَّ الوالدات {وإنهم ليقولون} بلفظ الظِّهار {منكراً من القول} لا تُعرف صحَّته {وزوراً} وكذباً فإنَّ المرأة لا تكون كالأمِّ {وإنَّ الله لعفو غفور} عفا وغفر للمُظاهِر بجعل الكفَّارة عليه ثمَّ ذكر حكم الظِّهار فقال:',NULL,'1074',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5108,'{والذين يظاهرون من نسائهم ثم يعودون لما قالوا} في الآية تقديمٌ وتأخيرٌ تقديرها: والذين يُظاهرون من نسائهم فتحرر رقبةٍ لما قالوا ثمَّ يعودون أي: عل المُظاهر عتق رقبةٍ لقوله لامرأته: أنتِ عليَّ كظهر أُمِّي ثمَّ يعود إلى استباحة الوطء ولا تحلُّ له قبل الكفَّارة وهو قوله: {من قبل أن يتماسا} أي: يَجَّامعا {ذلكم توعظون به} أي: ذلك التَّغليظ في الكفَّارة وعظٌ لكم كي تنزجروا به عن الظِّهار فلا تُظاهروا',NULL,'1074',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5109,'{فمن لم يجد} الرَّقبة لفقره {فصيام شهرين متتابعين} لو أفطر فيما بين ذلك ابطل التَّتابع ويجب عليه الاستئناف {فمن لم يستطع} ذلك لمرضٍ أو لخوفِ مشقَّةٍ عظيمةٍ {فإطعام ستين مسكيناً} لكلِّ مسكينٍ مدٌّ من غالب القوت {ذلك} أي: الفرض الذين وصفنا {لتؤمنوا بالله ورسوله} لتصدقوا ما أتى به الرَّسول عليه السَّلام وتُصدِّقوا أنَّ الله تعالى به أمر {وتلك حدود الله} يعني: ما وصف في الظِّهار والكفَّارة {وللكافرين} لمن لم يُصدِّق به {عذاب أليم}',NULL,'1074',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5110,'{إنَّ الذين يحادون الله} يُخالفون الله {ورسوله كُبِتوا} أُذِلُّوا وأُخزوا {كما كُبِتَ الذين من قبلهم} ممَّن خالف الله ورسوله {وقد أنزلنا آيات بينات وللكافرين} بها {عذاب مهين}',NULL,'1074',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5111,'{يوم يبعثهم الله جميعاً فينبئهم بما عملوا} يخبرهم بذلك ليعلموا وجوب الحجَّة عليهم {أحصاه الله} علمه الله وأحاط بعدده {ونسوه} هم وقوله:',NULL,'1075',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5112,'{ما يكون من نجوى ثلاثة} أَيْ: مناجاة ثلاثةٍ وإن شئت قلتَ: من متناجين ثلاثة {إلاَّ هو رابعهم} بالعلم يسمع نجواهم',NULL,'1075',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5113,'{ألم تر إلى الذين نهوا عن النجوى} نزلت في المنافقين واليهود كانوا يتناجون فيما بينهم دون المؤمنين وينظرون إلى المؤمنين ليُواقعوا في قلوبهم ريبةً وتهمةً ويظنُّون أنَّ ذلك لشيءٍ بلغهم ممَّا يهمُّهم فشكوا ذلك إلى رسول الله صَلَّى اللَّهُ عليه وسلم فنهاهم عن ذلك فعادوا لما نُهوا عنه فأنزل الله: {ألم تر إلى الذين نهوا عن النجوى ثم يعودون لما} أَي: إلى {ما نُهوا عنه ويتناجون بالإِثم والعدوان ومعصية الرسول} أي: يعصي بعضهم بعضاً سرَّاً بالظُّلم والإِثم وترك طاعة الرَّسول عليه السَّلام {وإذا جاؤوك حيوك بما لم يُحَيِّكَ به الله} يعني: قولهم: السَّام عليك {ويقولون في أنفسهم: لولا يعذِّبنا الله بما نقول} وذلك أنَّهم قالوا: لو كان نبيَّاً لعذَّبنا بهذا قال الله: {حسبهم جهنم يصلونها فبئس المصير} ثمَّ نهى المؤمنين عن مثل ذلك فقال:',NULL,'1075',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5114,'{يا أيها الذين آمنوا إذا تناجيتم فلا تتناجوا بالإِثم والعدوان ومعصية الرسول}',NULL,'1076',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5115,'{إنما النجوى من الشيطان} أَيْ: النَّجوى بالإِثم والعدوان ما يزيِّن الشَّيطان لهم {ليحزن الذين آمنوا وليس بضارِّهم} وليس الشَّيطان بضارِّهم {شيئاً إِلا بِإِذْنِ اللَّهِ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ} أَيْ: وإِليه فَلْيَكِلُوا أمورهم',NULL,'1076',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5116,'{يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس} توسَّعوا في مجلس رسول الله صلى الله عليه وسلم {فافسحوا} أوسعوا المجلس {يفسح الله لكم} يُوسِّعه عليكم نزلت في قومٍ كانوا يبكون إلى مجلس رسول الله صلى الله عليه وسلم ويأخذون مجالسهم بالقرب منه فإذا دخل غيرهم ضنُّوا بمجالسهم وكان رسول الله صلى الله عليه وسلم يحبُّ أن يُكرم أهل بدرٍ فدخلوا يوماً فقاموا بين يديه ولم يجدوا عنده مجلساً ولم يقم لهم أحدٌ من هؤلاء الذين أخذوا مجالسهم فكره النبيُّ عليه السَّلام ذلك فنزلت هذه الآية وأمرهم أن يُوسِّعوا في المجلس لمن أراد النبي صلى الله عليه وسلم {وإذا قيل انشزوا فانشزوا} وإذا قيل لكم: قوموا إلى صلاةٍ أو جهادٍ أو عمل خيرٍ فانهضوا {يرفع الله الذين آمنوا منكم} بطاعة الله {والذين أوتوا العلم درجات} في الجنَّة',NULL,'1076',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5117,'{يا أيها الذين آمنوا إذا ناجيتم الرسول فقدموا بين يدي نجواكم} أمام مناجاتكم {صدقة} نزلت حين غلب أهلُ الجدة الفقراءَ على مجالسة رسول الله صلى الله عليه وسلم ومناجاته فكره الرَّسول ذلك فأمرهم الله بالصَّدقة عند المناجاة ووضع ذلك عن الفقراء فقال: {فإن لم تجدوا فإنَّ الله غفور رحيم} ثمَّ نسخ الله ذلك فقال:',NULL,'1077',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5118,'{أأشفقتم} بخلتم وخفتم بالصَّدقة الفقر {فإذْ لم تفعلوا وتاب الله عليكم} عاد عليكم بالتَّخفيف {فأقيموا الصلاة وآتوا الزكاة} المفروضة',NULL,'1077',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5119,'{ألم تر إلى الذين تولوا قوماً غضب الله عليهم} أَي: المنافقين تولَّوا اليهود وناصحوهم ونقلوا إليهم أسرار المؤمنين {ما هم منكم} أيُّها المؤمنون {ولا منهم} من اليهود {ويحلفون} أنَّهم لا يخونون المؤمنين {وهم يعلمون} أنَّهم كاذبون في حلفهم',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5120,'{أعد الله لهم عذابا شديدا إنهم ساء ما كانوا يعملون}',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5121,'{اتخذوا أيمانهم} الكاذبة {جنة} يستجنُّون بها من القتل',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5122,'{لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلا أَوْلادُهُمْ مِنَ الله شيئا أولئك أصحاب النار هم فيها خالدون}',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5123,'{يوم يبعثهم الله جميعاً فيحلفون له} كاذبين ما كانوا مشركين {كما يحلفون لكم} كاذبين {ويحسبون أنهم على شيء} من نفاقهم يأتونكم بوجهٍ ويأتون الكفَّار بوجهٍ ويظنُّون أنَّهم يسلمون فيما بينكم وبينهم {ألا إنهم هم الكاذبون}',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5124,'{استحوذ عليهم الشيطان} أي: استولى عليهم',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5125,'{إنَّ الذين يحادون الله ورسوله} يخالفونهما {أولئك في الأذلين} المغلوبين',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5126,'{كتب الله} قضى الله {لأغلبنَّ أنا ورسلي} إمَّا بالظفَّر والقهر وإمَّا بظهور الحجَّة',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5127,'{لا تجد قوماً يؤمنون بالله واليوم الآخر يوادون من حادَّ الله ورسوله} الآية أخبر الله في هذه الآية أنَّ المؤمن لا يوالي الكافر وإنْ كان أباه أو أخاه أو قريبه وذلك أنَّ المؤمنين عادوا آباءَهم الكفَّار وعشائرهم وأقاربهم فمدحهم الله على ذلك فقال: {أولئك كتب في قلوبهم الإيمان} أي: أثبته {وأيدهم بروحٍ منه} أيْ: بنور الإِيمان وقيل: بالقرآن ثمَّ وعدهم الإِدخال في الجنَّة فقال: {ويدخلهم جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا رضي الله عنهم ورضوا عنه أولئك حزب الله أَلاَ إنَّ حزب الله هم المفلحون}',NULL,'1078',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5128,'{سبح لله ما في السماوات وما في الأرض وهو العزيز الحكيم}',NULL,'1080',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5129,'{هو الذي أخرج الذين كفروا من أهل الكتاب} يعني: النَّضير {من ديارهم} مساكنهم بالمدينة وذلك أنهم نقضوا العهد بينهم وبين رسول الله صلى الله عليه وسلم فأمر رسول الله صلى الله عليه وسلم بقتل كعب بن الأشرف سيدهم فقتل غلية وحاصر بني النَّضير ثمَّ صالحهم على أن يخرجوا إلى الشَّام فخرجوا وتركوا رباعهم وضياعهم وقوله: {لأوَّل الحشر} كانوا أوَّل مَنْ حُشر إلى الشَّام من اليهود من الجزيرة العرب وقيل إنه أوَّل حشرٍ إلى الشَّام والحشر الثَّاني حشر القيامة والشَّام أرض المحشر {ما ظننتم} أَيُّها المؤمنون {أن يخرجوا} لعدَّتهم ومنَعتهم {وظنوا أنهم مانعتهم حصونهم من الله} وذلك أنَّهم كانوا أهل حلقةٍ وحصونٍ فظنُّوا أنَّها تحفظهم من ظهور المسلمين عليهم {فأتاهم الله} أي: أمر الله {من حيث لم يحتسبوا} من جهة المؤمنين وما كانوا يحسبون أنَّهم يغلبونهم ويظهرون عليهم {وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ} ألقى في قلوبهم الخوف بقتل سيِّدهم {يخربون بيوتهم بأيديهم} وذلك أنَّ النبي صلى الله عليه وسلم صالحهم على أنَّ لهم ما أقلَّت الإِبل وكانوا ينظرون إلى الخشبة والشَّيء في منازلهم ممَّا يستحسنونه فيقلعونه وينتزعونه ويهدمون البيوت لأجله فذلك إخراجهم بأيديهم ويخرِّب المؤمنون باقيها وهو قوله: {وأيدي المؤمنين} وأضاف الإخراب بأيدي المؤمنين إليهم لأنهم عضوا منازلهم للخراب بنقض العهد {فاعتبروا} فاتَّعظوا {يا أولي الأبصار} يا ذوي العقول فلا تفعلوا فعل بني النير فينزل بكم ما نزل بهم',NULL,'1080',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5130,'{ولولا أن كتب الله} قضى الله {عليهم الجلاء} الخروج عن الوطن {لعذَّبهم في الدنيا} بالقتل والسَّبى كما فعل بقريظة',NULL,'1081',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5131,'{ذلك بأنهم شاقوا الله ورسوله ومن يشاق الله فإن الله شديد العقاب}',NULL,'1081',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5132,'{ما قطعتم من لينة} من نخلةٍ من نخيلهم {أو تركتموها قائمة} فلم تقطعوها {فبإذن الله} أي: إنَّه أذن في ذلك إِنْ شئتم قطعتم وإنْ شئتم تركتم وذلك أنَّهم لمَّا تحصَّنوا بحصونهم أمر رسول الله صلى الله عليه وسلم بقطع نخيلهم وإحراقها فجزعوا من ذلك وقالوا: من أين لك يا محمَّد عقر الشَّجر المثمر؟ واختلف المسلمون في ذلك فمنهم مَنْ قطع غيظاً لهم ومنهم من ترك القطع وقالوا: هو مالنا: أفاء الله علينا به فأخبر الله أنَّ كلَّ ذلك من القطع والتَّرك بإذنه {وليخزي الفاسقين} وليذلَّ اليهود وليغيظهم',NULL,'1081',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5133,'{وما أفاء الله على رسوله} ردَّ الله على رسوله ورجع إليه {منهم} من بني النَّضير من الأموال {فما أوجفتم عليه من خيل ولا ركاب} أَيْ: ما حملتم خيلكم ولا إبلكم على الوجيف إليه وهو السَّير السَّريع والمعنى: لم تركبوا إليه خيلاً ولا إبلاً ولا قطعتم إليه شفة فهو خالصٌ لرسول الله صلى الله عليه وسلم يعمل فيه ما أحبَّ وليس كالغنيمة التي تكون للغانمين وهذا معنى قوله: {ولكنَّ الله يسلط رسله على مَنْ يشاء} الآية',NULL,'1081',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5134,'{ما أفاء الله على رسوله من أهل القرى} من أموال القرى الكافرة {فللَّه وَلِلرَّسُولِ وَلِذِي الْقُرْبَى وَالْيَتَامَى وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ} وكان الفيء يُخَمَّسُ خمسةَ أخماسٍ فكانت أربعةُ أخماسه لرسول الله صلى الله عليه وسلم يفعل فيها ما يشاء والخمس الباقي للمذكورين في هذه الآية وأمَّا اليوم فما كان للنبي صلى الله عليه وسلم من الفيء يُصرف إلى أهل الثُّغور المُترصِّدين للقتال في أحد قولي الشَّافعي رحمه الله والفيء: كلُّ مالٍ رجع إلى المسلمين من أيدي الكفَّار عفواً من غير قتال مثل: مال الصُّلح والجزية والخراج أو هربوا فتركوا ديارهم وأموالهم كفعل بني النَّضير وقوله: {كيلا يكون} يعني: الفيء {دولة} متداولاً {بين الأغنياء} الرُّؤساء والأقوياء {منكم وما آتاكم الرسول} أعطاكم من الفيء {فخذوه وما نهاكم عنه} عن أخذه {فانتهوا}',NULL,'1082',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5135,'{للفقراء المهاجرين} يعني: خمس الفيء للذين هاجروا إلى المدينة وتركوا ديارهم وأموالهم حُبَّاً لله ولرسوله ونصرةً لدينه وهو قوله: {وينصرون الله} أي: دينه {ورسوله أولئك هم الصادقون} في إيمانهم',NULL,'1083',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5136,'{والذين تبوؤوا الدار والإِيمان} نزلوا المدينة وقبلوا الإيمان {من قبلهم} من قبل المهاجرين وهم الأنصار {يحبون من هاجر إليهم} من المسلمين {ولا يجدون في صدورهم حاجة} غيظاً وحسداً {مما أوتوا} ممَّا أُوتي المهاجرون من الفيء وذلك أنَّ رسول الله صلى الله عليه وسلم قسم أموال بني النَّضير بين المهاجرين ولم يعط الأنصار منها شيئاً إلاَّ ثلاثة نفرٍ كانت بهم حاجة فطابت أنفس الأنصار بذلك فذلك قوله: {ويؤثرون على أنفسهم} أَي: يختارون إخوانهم المهاجرين بالمال على أنفسهم {ولو كان بهم خصاصة} حاجةٌ وفاقةٌ إلى المال {ومَنْ يوق شح نفسه} مَنْ حُفظ من الحرص المهلك على المال وهو حرصٌ يحمله على إمساك المال عن الحقوق والحسد {فأولئك هم المفلحون}',NULL,'1083',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5137,'{والذين جاؤوا من بعدهم} أي: والذين يجيئون من بعد المهاجرين والأنصار إلى يوم القيامة {يقولون ربنا اغفر لنا ولإخواننا الذين سبقونا بالإيمان} أي: المهاجرين والأنصار {ولا تجعل في قلوبنا غلاً} حقداً {للذين آمنوا} الآية فمن ترحَّم عَلَى أَصْحَابِ رَسُولِ اللَّهِ صَلَّى اللَّهُ عَلَيْهِ وسلم ولم يكن في قلبه غِلٌّ لهم فهو من أهل هذه الآية ومَنْ يشتم واحداً منهم ولم يترحَّم عليه لم يكن له حظٌّ في الفيء وكان خارجاً من جملة أقسام المؤمنين وهم ثلاثةٌ: المهاجرون والأنصار والذين جاؤوا من بعدهم بهذه الصِّفة التي ذكرها الله تعالى',NULL,'1083',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5138,'{ألم تر إلى الذين نافقوا} الآية وذلك أنَّ المنافقين ذهبوا إلى بني النَّضير لمَّا حاصرهم رسول الله صلى الله عليه وسلم وقالوا: لا تخرجوا من دياركم فإن قاتلكم محمدٌ كنَّا معكم وإن أخرجكم خرجنا معكم وذلك قوله: {لئن أخرجتم لنخرجنَّ معكم ولا نطيع فيكم أحداً} سألنا خذلانكم {أبداً} فكذَّبهم الله تعالى فيما قالوا بقوله: {والله يشهد إنهم لكاذبون} والآية الثَّانية وذكر أنَّهم إن نصروهم انهزموا ولم ينتصروا وهو قوله:',NULL,'1084',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5139,'{ولئن نصروهم ليولنَّ الأدبار ثمَّ لا ينصرون}',NULL,'1084',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5140,'{لأنتم} أيُّها المؤمنون {أشد رهبة في صدورهم} صدور المنافقين من الله يقول: أنتم أهيبُ في صدورهم من الله تعالى لأنهم يخفون منكم موافقة اليهود خوفاً منكم ولا يخافون الله فيتركون ذلك',NULL,'1084',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5141,'{لا يقاتلونكم جميعاً} أي: اليهود {إلاَّ في قرىً محصنة أو من وراء جدر} أي: لِمَا ألقى الله في قلوبهم من الرعب لا يقاتلونكم إلاَّ محصنين بالقرى والجدران ولا يبرزون لقتالكم {بأسهم بينهم شديد} خلافهم بينهم عظيم {تحسبهم جميعاً} مُجتمعين مُتَّفقين {وقلوبهم شتى} مُختلفةٌ مُتفرِّقةٌ و {ذلك بأنهم قوم لا يعقلون} عن الله أمره',NULL,'1084',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5142,'{كمثل الذين من قبلهم} أي: المشركين يقول: هم في تركهم الإيمان وغفلتهم عن عذاب الله كالذين من قبلهم {قريباً ذاقوا وبال أمرهم} يعني: أهل بدرٍ ذاقوا العذاب بمدَّةٍ قليلةٍ من قبل ما حلَّ بالنَّضير من الجلاء والنَّفي وكان ذلك بعد مرجعه من أُحدٍ وقوله:',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5143,'{كمثل الشيطان} يعني: إنَّ المنافقين في نصرتهم لليهود كمثل الشَّيطان {إذ قال للإِنسان اكفر} يعني: عابداً في بني إسرائيل فتنه الشَّيطان حتى كفر ثمَّ خذله كذلك المنافقون منَّوا بني النَّضير نصرتهم ثمَّ خذلوهم وتبرَّؤوا منهم',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5144,'{فكان عاقبتهما} عاقبة الشَّيطان والكافر {أنهما في النار خالدين فيها وذلك جزاء الظالمين}',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5145,'{يا أيها الذين آمنوا اتقوا الله} بأداء فرائضه واجتناب معاصيه {ولتنظر نفسٌ ما قدَّمت لغد} يوم القيامة من طاعةٍ وعملٍ صالحٍ',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5146,'{ولا تكونوا كالذين نسوا الله} تركوا طاعة اللَّه وأمره {فأنساهم أنفسهم} حظَّ أنفسهم أن يُقدِّموا لها خيرا',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5147,'{لا يستوي أصحاب النار وأصحاب الجنة أصحاب الجنة هم الفائزون}',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5148,'{لو أنزلنا هذا القرآن على جبل لرأيته خاشعاً متصدعاً من خشية الله} أخبر الله تعالى أنَّ من شأن القرآن وعظمته أنَّه لو جُعل في الجبل تمييزٌ - كما جعل في الإنسان - وأُنزل عليه القرآن لخشع وتصدَّع أَيْ: تشقَّق من خشية الله قوله:',NULL,'1085',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5149,'{عالم الغيب والشهادة} السِّرِّ والعلانيَة وقوله:',NULL,'1086',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5150,'{الملك} : ذو الملك {القدوس} الطَّاهر عمَّا لا يليق به {السلام} ذو السَّلامة من الآفات والنقائض {المؤمن} المُصدِّق رسله بخلق المعجزة لهم وقيل: الذي آمن خلقه من ظلمه {المهيمن} الشَّهيد {العزيز} القويُّ {الجبار} الذي جبر الخلق على ما أراد من أمره {المتكبر} عمَّا لا يليق به',NULL,'1086',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5151,'{هو الله الخالق البارئ المصور له الأسماء الحسنى يسبح له ما في السماوات والأرض وهو العزيز الحكيم}',NULL,'1086',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5152,'{يا أيها الذين آمنوا لا تتخذوا عدوي وعدوكم أولياء} نزلت في حاطب ابن أبي بلتعة لمَّا كتب إلى مشركي مكَّة ينذرهم برسول الله صلى الله عليه وسلم حين أراد الخروج إليهم {تلقون إليهم بالمودة} أَيْ: تُلقون إليهم أخبار النَّبيِّ صلى الله عليه وسلم وسرَّه بالمودة التي بينكم وبينهم {وقد كفروا} أَي: وحالهم أنَّهم كافرون {بما جاءكم من الحق} دين الإِسلام والقرآن {يخرجون الرسول وإياكم} أيُّها المؤمنون من مكَّة {أن تؤمنوا} لأن آمنتم {بالله ربكم إن كنتم خرجتم} من مكَّة {جهاداً} للجهاد {في سبيلي وابتغاء مرضاتي} وجواب هذا الشَّرط متقدِّم وهو قوله: {لا تتخذوا عدوي} أي: لا تتَّخذوهم أولياء إن كنتم تبتغون مرضاتي وقوله: {تسرون إليهم بالمودة} كقوله: {تُلقون إليهم بالمودَّة} {وأنا أعلم بما أخفيتم وما أعلنتم} وذلك أنَّ الله أطلع نبيَّه عليه السَّلام على مكاتبة حاطبٍ للمشركين حتى استردَّ الكتاب ممَّن دفعه إليه ليوصله إليهم {ومن يفعله منكم} أي: الإسرار إليهم {فقد ضلَّ سواء السبيل} أخطأ طريق الدِّين ثمَّ أعلم أنَّه ليس ينفعهم ذلك عند المشركين فقال:',NULL,'1087',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5153,'{إن يثقفوكم} أَيْ: يلقوكم ويظفروا بكم {يكونوا لكم أعداءً ويبسطوا إليكم أيديهم} بالضَّرب والقتل {وألسنتهم بالسّوء} أي: الشَّتم {وودوا لو تكفرون} فلا تُناصِحوهم فإنَّهم معكم على هذه الحالة ثمَّ أخبر أنَّ أهلهم وأولادهم الذين لأجلهم يُناصحون المشركين لا ينفعونهم شيئاً في القيامة فقال:',NULL,'1088',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5154,'{لن تنفعكم أرحامكم ولا أولادكم} المشركون {يوم القيامة يفصل بينكم} فيدخل المؤمنون الجنَّة والكافرون النَّار ثمَّ أمرَ أصحاب رسول الله صلى الله عليه وسلم بالاقتداء بأصحاب إبراهيم عليه السَّلام فقال:',NULL,'1088',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5155,'{قد كانت لكم أسوة حسنة} ائتمامٌ واقتداءٌ وطريقةٌ حسنةٌ {في إبراهيم والذين معه} من أصحابه إذ تبرَّؤوا من قومهم الكفَّار وعادوهم وقالوا لهم: {كفرنا بكم} أَيْ: أنكرناكم وقطعنا محبتكم وقوله: {إلاَّ قول إبراهيم لأبيه} أَيْ: كانت لكم أسوةٌ فيهم ما خلا هذا فإنَه لا يجوز الاستغفار للمشركين ثمَّ أخبرنا أنَّهم قالوا يعني قوم إبراهيم: {ربنا عليك توكلنا وإليك أنبنا وإليك المصير}',NULL,'1088',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5156,'{ربنا لا تجعلنا فتنة للذين كفروا} أَيْ: لا تُظهرهم علينا فيظنوا أنَّهم على حق فيقتتنوا بذلك',NULL,'1089',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5157,'{لقد كان لكم فيهم} في إبراهيم والذين معه {أسوة حسنة} تقتدون بهم فتفعلون من البراءة من الكفَّار كما فعلوا وتقولون كما قال ممَّا أخبر عنهم ثمَّ بيَّن أنَّ هذا الاقتداء بهم {لمن كان يرجو الله واليوم الآخر} {ومن يتول} عن الحقِّ ووالى الكفَّار {فإنَّ الله هو الغني الحميد}',NULL,'1089',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5158,'{عسى الله أن يجعل بينكم وبين الذين عاديتم منهم} من مشركي مكَّة {مودَّة} بأن يهديهم للدِّين فيصبروا لكم أولياء وإخواناً ثمَّ فعل ذلك بعد فتح مكَّة فتزوَّج رسول الله صلى الله عليه وسلم أم حبيبة بنت أبي سفيان ولأن أبي سفيان للمؤمنين وترك ما كان عليه من العداوة ثمَّ رخص في صلة الذين لم يقاتلوهم من الكفار فقال:',NULL,'1089',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5159,'{لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم أن تبروهم} أَيْ: لا ينهاكم عن برِّ هؤلاء {وتقسطوا إليهم} أَيْ: تعدلوا فيهم بالإحسان ثمَّ ذكر أنَّه إنَّما ينهاهم عن أن يتولَّوا مشركي مكَّة الذين قاتلوهم فقال:',NULL,'1089',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5160,'{إنما ينهاكم الله عن الذين قاتلوكم في الدين وأخرجوكم من دياركم وظاهروا على إخراجكم أن تولوهم}',NULL,'1089',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5161,'{يا أيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات} الآية نزلت بعد صلح الحديبية وكان الصُّلح قد وقع على أن يردَّ إلى أهل مكَّة مَنْ جاء من المؤمنين منهم فأنزل الله في النِّساء إذا جئن مهاجراتٍ أَنْ يمتحن وهو قوله: {فامتحنوهن} وهو أنّْ تُستحلف ما خرجت بُغضاً لزوجها ولا عشقاً لرجلٍ من المسلمين وما خرجت إلاَّ رغبةً في الإسلام فإذا حلفت لم تردَّ إلى الكفَّار وهو قوله: {فإن علمتموهنَّ مؤمنات فلا ترجعوهنَّ إلى الكفار} لأنَّ المسلمةَ لا تحلُّ للكافر وقوله: {وآتوهم} يعني: أزواجهم الكفَّار ما أنفقوا عليهنَّ من المهر {ولا جناح عليكم أن تنكحوهن إذا آتيتموهن أجورهنَّ} أي: مهورهنَّ وإن كان لهنَّ أزواجٌ كفَّارٌ في دار الإِسلام لأنَّ الإِسلام أبطل تلك الزَّوجية {ولا تمسكوا بعصم الكوافر} أَيْ: لا تمسكوا بنكاحهنَّ فإنَّ العصمة لا تبقى بين المشركة والمؤمن والمعنى: إن لحقت بالمشركين واحدةٌ من نسائكم فلا تتمسكوا بنكاحها {واسألوا ما أنفقتم} عليهنَّ من المهر مَنْ يتزوجهنَّ من الكفَّار {وليسألوا} يعني: المشركين {ما أنفقوا} من المهر فلمَّا نزلت هذه الآية أدَّى المؤمنون ما أُمروا به من نفقات المشركين على نسائهم وأبى المشركون ذلك فنزلت:',NULL,'1090',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5162,'{وإن فاتكم شيء من أزواجكم إلى الكفار} أَيْ: لحقتْ واحدةٌ من نسائكم مرتدَّةً بالكفَّار {فعاقبتم} فغزوتموهم وكانت العقبى لكم {فآتوا الذين ذهبت أزواجهم} إلى الكفَّار {مثل ما أنفقوا} عليهنَّ من الغنائم ثمَّ نزل في بيعة النِّساء:',NULL,'1090',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5163,'{يا أيها النبيُّ إذا جاءك المؤمنات يبايعنك على أن لا يشركن بالله شيئاً ولا يسرقن ولا يزنين ولا يقتلن أولادهنَّ ولا يأتين ببهتان يفترينه بين أيديهن وأرجلهن} أَيْ: لا يأتين بولدٍ ينسبنه إلى الزَّوج فإن ذلك بهتانٌ وفِريةٌ {ولا يعصينك في معروف} أيْ: فيما وافق طاعة الله تعالى {فبايعهنَّ} أمره أن يُبايعهنَّ على الشَّرائط التي ذكرها في هذه الآية ثمَّ نهى المؤمنين عن موالاة اليهود فقال:',NULL,'1091',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5164,'{يا أيها الذين آمنوا لا تتولوا قوماً غضب الله عليهم قد يئسوا من الآخرة} أن يكون لهم فيها ثوابٌ {كما يئس الكفار} الذين لا يوقنون بالبعث {من أصحاب القبور} أن يُبعثوا وقيل: كما يئس الكفار الذين في القبور مَنْ أَنْ يكون لهم في الآخرة خيرٌ',NULL,'1091',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5165,'{سبح لله ما في السماوات والأرض وهو العزيز الحكيم}',NULL,'1092',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5166,'{يا أيها الذين آمنوا لِمَ تقولون ما لا تفعلون} كان المؤمنون يقولون: لو علمنا أحبَّ الأعمال إلى الله لبذلنا فيه أموالنا وأنفسنا فأُخبروا بذلك في قوله: {إنَّ الله يُحِبُّ الذين يقاتلون} الآية',NULL,'1092',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5167,'وقوله: {كَبُر مقتاً عند الله} أَيْ: عَظُم ذلك في البغض {أن تقولوا ما لا تفعلون} وقوله:',NULL,'1092',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5168,'{إن الله يحب الذين يقاتلون في سبيله صفاً} وأُعلموا أنَّ أحب الأعمال إلى الله الجهاد فلم يَفوا بما قالوا وانهزموا يوم أُحدٍ فَعُيِّروا بهذه الآية وقوله: {كأنهم بنيان مرصوص} لاصق بعضه ببعض لا يزولون عن أماكنهم',NULL,'1092',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5169,'{وإذ قال موسى} أَيْ: اذكر يا محمَّد لقومك قصَّة موسى إذ قال لقومه: {يا قوم لم تؤذونني} وذلك حين رموه بالأذرة {وقد تعلمون أني رسول الله إليكم} والرَّسول يُعظَّم ولا يُؤذى {فلما زاغوا} عدلوا عن الحقِّ {أزاغ الله قلوبهم} أضلَّهم الله وصرف قلوبهم عن الحقِّ {والله لا يهدي القوم الفاسقين} أَيْ: مَنْ سبق في علمه أنَّه فاسقٌ وقوله:',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5170,'{وإذ قال عيسى ابن مريم يا بني إسرائيل أني رسول الله إليكم مُصدِّقاً لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد فلما جاءهم بالبينات قالوا هذا سحر مبين}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5171,'{ومن أظلم ممن افترى على الله الكذب وهو يدعى إلى الإسلام والله لا يهدي القوم الظالمين}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5172,'{يريدون ليطفئوا نور الله بأفواههم والله متم نُورَهُ وَلَوْ كَرِهَ الْكَافِرُونَ}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5173,'{هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَى وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5174,'{يا أيها الذين آمنوا هل أدلكم على تجارة تنجيكم من عذاب أليم}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5175,'{تؤمنون بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم ذلكم خير لكم إن كنتم تعلمون}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5176,'{يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار ومساكن طيبة في جنات عدن ذلك الفوز العظيم}',NULL,'1903',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5177,'{وأخرى تحبونها} أَيْ: ولكم أخرى تحبُّونها في العاجل مع ثواب الآجل ثمَّ بيَّن ما هي فقال: {نصرٌ من الله وفتح قريب}',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5178,'{يا أيها الذين آمنوا كونوا أنصار الله} أعواناً بالسَّيف على أعدائه {كما قال عيسى ابن مريم للحواريين مَنْ أنصاري إلى الله} أَيْ: مع الله {قال الحواريون نحن أنصار الله فآمنت طائفة من بني إسرائيل} بعيسى {وكفرت طائفة فأيدنا الذين آمنوا} قوّيناهم {على عدوهم فأصبحوا ظاهرين} غالبين',NULL,'1093',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5179,'{يسبح لله ما في السماوات وما في الأرض الملك القدوس العزيز الحكيم}',NULL,'1095',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5180,'{هو الذي بعث في الأميين} يعني: العرب {رسولاً منهم} محمداً عليه السَّلام',NULL,'1095',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5181,'{وآخرين منهم} أَيْ: وفي آخرين منهم {لما يلحقوا بهم} وهم التَّابعون وجميعُ مَنْ يدخل في الإسلام والنبي صلى الله عليه وسلم مبعوثٌ إلى كلِّ مَنْ شاهده وإِلى كلِّ مَنْ كان بعدهم من العرب والعجم',NULL,'1095',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5182,'{ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ ذو الفضل العظيم}',NULL,'1095',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5183,'{مثل الذين حملوا التوراة} كُلِّفوا العمل بها {ثمَّ لم يحملوها} لم يعملوا بما فيها {كمثل الحمار يحمل أسفاراً} كتباً أَيْ: اليهود شبَّههم في قلَّة انتفاعهم بما في أيديهم من التَّوراة إذ لم يؤمنوا بمحمد عليه السَّلام بالحمار يحمل كتباً ثمَّ قال: {بئس مثلُ القوم الذين كذبوا بآيات الله والله لا يهدي القوم الظالمين}',NULL,'1095',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5184,'{قل يا أيها الذين هادوا إن زعمتم أنكم أولياء لله مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صادقين} فسِّر في سورة البقرة عند قوله: {قُلْ إِنْ كَانَتْ لَكُمُ الدَّارُ الآخِرَةُ} الآية',NULL,'1096',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5185,'{ولا يتمنونه أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ}',NULL,'1096',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5186,'{قل إنَّ الموت الذي تفِرُّون منه} وذلك أنَّهم علموا أنَّ عاقبتهم النَّار بتكذيب محمد عليه السلام فكرهوا الموت قال الله: {فإنَّه ملاقيكم} أَيْ: لا بدَّ لكم منه يلقاكم وتلقونه',NULL,'1096',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5187,'{يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله} أي: اعملوا على المشي إليه {وذروا البيع} اتركوه بعد النِّداء',NULL,'1096',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5188,'{فإذا قضيت الصَّلاة} فُرغ منها {فانتشروا في الأرض} أَمرُ إباحةٍ {وابتغوا من فضل الله} الرِّزق',NULL,'1096',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5189,'{وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْوًا انْفَضُّوا إِلَيْهَا} أَيْ: تفرَّقوا عنك إلى التِّجارة وكان النبي صلى الله عليه وسلم في خطبته يوم الجمعة فقدمت عيرٌ وضرب لقدومها الطبل وكان ذلك في زمان غلاءٍ بالمدينة فتفرَّق الناس عن النبي صلى الله عليه وسلم إلى التِّجارة وصوت الطبل ولم يبق معه إلاَّ اثنا عشر نفساً وقوله: {وتركوك قائماً} أَيْ: في الخطبة {قل ما عند الله} للمؤمنين {خير من اللهو ومن التجارة والله خير الرازقين} فإيَّاه فاسألوا ولا تنفضوا عن رسول الله صلى الله عليه وسلم لطلب الزرق',NULL,'1097',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5190,'{إذا جاءك المنافقون قالوا نشهد إنك لرسول الله والله يعلم إنك لرسوله والله يشهد إنَّ المنافقين لكاذبون} لإِضمارهم خلاف ما أظهروا',NULL,'1098',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5191,'{اتخذوا أيمانهم} جمع يمينٍ {جنَّة} سترةً يستترون بها من القتل يعني: قولهم: {ويحلفون بالله إنهم لمنكم} وقوله: {يحلفون بالله ما قالوا} {فصدوا عن سبيل الله} منعوا النَّاس عن الإيمان بمحمد صلى الله عليه وسلم {إنهم ساء ما كانوا يعملون} بئس العملُ عملهم',NULL,'1098',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5192,'{ذلك بأنهم آمنوا} في الظَّاهر {ثمَّ كفروا} بالاعتقاد',NULL,'1098',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5193,'{وإذا رأيتهم تعجبك أجسامهم} في طولها واستواء خلقها وكان عبد الله ابن أبي جسيماً صبيحاً فصيحاً إذا تكلم يسمع النبي صلى الله عليه وسلم قوله وهو قوله: {وإن يقولوا تسمع لقولهم} ثمَّ أعلم أنَّهم في ترك التَّفهُّم بمنزلة الخشب فقال: {كأنهم خشب مسندة} أَيْ: ممالة إلى الجدار {يسبحون} من جُبنهم وسوء ظنِّهم {كلَّ صيحة عليهم} أَيْ: إنْ نادى منادٍ في العسكر أو ارتفع صوتٌ ظنُّوا أنَّهم يُرادون بذلك لما في قلوبهم من الرُّعب {هم العدو} وإن كانوا معك {فاحذرهم} ولا تأمنهم {قاتلهم الله} لعنهم الله {أنى يؤفكون} من أين يُصرفون عن الحقِّ بالباطل؟ !',NULL,'1098',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5194,'{وإذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رؤوسهم} وذلك أنَّه لما نزلت هذا الآيات قيل لعبد الله بن أبيّ: لقد نزلت فيك آيٌ شدادٌ فاذهب إلى رسول الله صلى الله عليه وسلم يستغفر لك فلوى رأسه وأعرض بوجهه إظهاراً للكراهة {ورأيتهم يصدون} يُعرضون عمَّا دُعوا إليه {وهم مستكبرون} لا يستغفرون ثمَّ أخبر أنَّ استغفار الرَّسول عليه السَّلام لا ينفعهم لفسقهم وكفرهم فقال:',NULL,'1099',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5195,'{سَوَاءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لهم لن يغفر الله لهم}',NULL,'1099',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5196,'{هم الذين يقولون: لا تنفقوا على من عند رسول الله} وذلك أن عبد الله ابن أبي قال لقومه وذويه: لا تنفقوا على أصحاب محمَّد - صلى الله عليه وسلم عنهم - حتى يفضوا أَيْ: يتفرَّقوا {ولله خزائن السماوات والأرض} أَيْ: إنَّه يرزق الخلق كلَّهم وهو يرزق المؤمنين والمنافقين جميعاً',NULL,'1099',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5197,'{يقولون لئن رجعنا إلى المدينة} يعني: عبد الله بن أبيّ وكان قد خرج مع رسول الله صلى الله عليه وسلم إلى غزوة بني المصطلق وجرى بينه وبين واحدٍ من المؤمنين جدال فأفرط عليه المؤمن فقال عبد الله بن أبيّ: {لئن رجعنا إلى المدينة ليخرجن الأعزُّ منها الأذل} يعني: الأعز نفسه والأذل رسول الله صلى الله عليه وسلم فقال الله تعالى: {ولله العزَّة} القوَّة والغلبة {ولرسوله} بعلوِّ كلمته وإظهار دينه {وللمؤمنين} بنصر الله إيَّاهم على مَنْ ناوأهم',NULL,'1100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5198,'{يا أيها الذين آمنوا لا تلهكم} لا تشغلكم {أموالكم ولا أولادكم عن ذكر الله} أَيْ: الصَّلوات الخمس {ومَنْ يفعل ذلك} يشتغل بشيءٍ عن الصَّلوات {فأولئك هم الخاسرون}',NULL,'1100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5199,'{وأنفقوا من ما رزقناكم} يعني: أذوا الزَّكاة {من قبل أن يأتي أحدكم الموت فيقول: ربِّ لولا أخرتني إلى أجل قريب} هلاَّ أخرتني إلى أجلٍ قريبٍ يسأل الرجعة وما قصَّر أحدٌ في الزَّكاة والحجِّ إلاَّ سأل الرَّجعة عند الموت {فأصدَّق} أَيْ: أتصدَّق وأُزكِّي {وأكن من الصالحين} أَيْ: أحج قال الله تعالى:',NULL,'1100',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5200,'{ولن يؤخر الله نفساً إذا جاء أجلها والله خبيرٌ بما تعملون}',NULL,'1101',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5201,'{يسبح لله ما في السماوات وما في الأرض له الملك وله الحمد وهو على كل شيء قدير}',NULL,'1102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5202,'{هو الذي خلقكم} أَيْ: في بطون أمهاتكم {فمنكم كافر ومنكم مؤمن} أَيْ: خلقكم كُفَّاراً ومؤمنين وقوله:',NULL,'1102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5203,'{فأحسن صوركم} أَيْ: خلقكم أحسن الحيوان',NULL,'1102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5204,'{يعلم ما في السماوات والأرض ويعلم ما تسرون وما تعلنون والله عليمٌ بذات الصدور}',NULL,'1102',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5205,'{ألم يأتكم} يا أهلَ مكَّة {نبأ الذين كفروا من قبل} أَيْ: خبر الأمم الكافرة قبلكم {فذاقوا وبال أمرهم} ذاقوا في الدُّنيا العقوبة بكفرهم {ولهم} في الآخرة {عذاب أليم}',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5206,'{ذلك} أَيْ: ذلك الذي نزل بهم {بأنَّه كانت تأتيهم رسلهم بالبينات فقالوا أبشرٌ يهدوننا} استبعدوا أن يكون الدَّاعي إلى الحقِّ بشراً والمراد بالبشر ههنا الجمع لذلك قال: {يهدوننا فكفروا وتولوا} عن الإِيمان {واستغنى الله} أَيْ: عن إيمانهم {والله غنيٌّ} عن خلقه {حميد} في أفعاله وقوله:',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5207,'{زعم الذين كفروا أن لن يبعثوا قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير}',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5208,'{فآمنوا بالله ورسوله والنور الذي أنزلنا والله بما تعملون خبير}',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5209,'{يوم التغابن} يغبن فيه أهلُ الجنَّة أهلَ النَّار بأخذ منازلهم التي كانت لهم في الجنَّة لو آمنوا ويغبن مَنْ ارتفعت منزلته في الجنَّة مَنْ كان دون منزلته فيظهر في ذلك اليوم غبن كلِّ كافرٍ بترك الإِيمان وغبن كلِّ مؤمنٍ بتقصيره',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5210,'{وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَئِكَ أَصْحَابُ النَّارِ خالدين فيها وبئس المصير}',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5211,'{ما أصاب من مصيبة إلاَّ بإذن الله} بعلمه وإرادته {ومَنْ يؤمن بالله} يُصدِّق بأنَّه لا تصيبه مصيبة إلا بإذن الله {يهد قلبه} يجعله مهتدياً حتى يشكر عند النِّعمة ويصبر عند الشدة',NULL,'1103',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5212,'{وأطيعوا الله وأطيعوا الرسول فإن توليتم فإنما على رسولنا البلاغ المبين}',NULL,'1104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5213,'{الله لا إله إلاَّ هو وعلى الله فليتوكل المؤمنون}',NULL,'1104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5214,'{يا أيها الذين آمنوا إنَّ من أزواجكم وأولادكم عدواً لكم} نزلت في قومٍ آمنوا وأرادوا الهجرة فثبَّطهم أهلهم وأولادهم وقالوا: لا نصبر على مفارفتكم فأخبر الله تعالى أنَّهم أعداءٌ لهم بحملهم إيَّاهم على المعصية وترك الطَّاعة {فاحذروهم} أن تقبلوا منهم ولا تطيعوهم ثمَّ إذا هاجر هذا الذي ثبَّطه أهله عن الهجرة رأى النَّاس قد تعلَّموا القرآن وتفقَّهوا في الدِّين فيهمُّ أن يعاقب أهله فقال الله تعالى: {وإن تعفوا وتصفحوا وتغفروا فإن الله غفور رحيم}',NULL,'1104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5215,'{أنما أموالكم وأولادكم فتنة} ابتلاء واختبارٌ لكم فمَنْ كسب الحرام لأجل الأولاد ومنع ماله عن الحقوق فهو مفتونٌ بالمال والولد {والله عنده أجر عظيم} لمن صبر عن الحرام وأنفق المال في حقِّه',NULL,'1104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5216,'{فاتقوا الله ما استطعتم} يعني: إذا أمكنكم الجهاد والهجرة فلا يفتننكم الميل إلى الأموال والأولاد عن ذلك وهذه الآية ناسخةٌ لقوله تعالى: {اتَّقوا اللَّهَ حقَّ تُقاتِه} وقوله: {وأنفقوا خيراً لأنفسكم} أَيْ: قدِّموا خيرا لأنفسكم من أموالكم {ومَنْ يُوقَ شُحَّ نفسه} بخلها وحرصها حتى ينفق المال {فأولئك هم المفلحون} الفائزون بالخير',NULL,'1104',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5217,'{إن تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم والله شكور حليم}',NULL,'1105',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5218,'{عالم الغيب والشهادة العزيز الحكيم}',NULL,'1105',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5219,'{يا أيها النبيُّ إذا طلقتم النساء} هذا خطابٌ للنبي صلى الله عليه وسلم والمؤمنون داخلون معه في الخطاب ومعنى قوله: {إذا طلقتم} : إذا أردتم طلاق النِّساء {فطلقوهنَّ لعدتهنَّّ} أَيْ: لطهرهنَّ الذي يحصينه من عدتهنَّ وهذا سنَّةُ الطَّلاق ولا تُطلقوهنَّ لحيضتهنَّ التي لا يعتدون بها من زمان العِدَّة {وأحصوا العدة} أَيْ: عدد أقرائها واحفظوها لتعلموا وقت الرَّجعة إن أردتم أن تُراجعوهنَّ وذلك أنَّ الرجعة إنَّما تجوز في زمان العِدَّة {واتقوا الله ربكم} وأطيعوه فيما يأمركم وينهاكم {لا تخرجوهنَّ من بيوتهن} حتى تنقضي عدَّتهنَّ {ولا يخرجن} من البيوت في زمان العِدَّة {إلاَّ أن يأتين بفاحشة مبينة} وهي الزِّنا فيخرجن حينئذٍ لإِقامة الحدِّ عليهنَّ {وتلك حدود الله} يعني: ما ذكر من طلاق السُّنَّة {ومَنْ يتعدَّ حدود الله} ما حدَّ الله له من الطَّلاق وغيره {فقد ظلم نفسه لا تدري لعلَّ الله يحدث بعد ذلك أمراً} بعد الطَّلاق مراجعةً وهذا يدلُّ على كراهية التَّطليق ثلاثاً بمرَّةٍ واحدةٍ لأنَّ إحداث الرَّجعة لا يكون بعد الثَّلاث',NULL,'1106',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5220,'{فإذا بلغن أجلهنَّ} قاربن انقضاء العدَّة {فأمسكوهنَّ} برجعةٍ تراجعونهنَّ بها {بمعروف} وهو أن لا يريد بالرَّجعة ضرارها {أو فارقوهنَّ بمعروف} أي: اتركوهن حتى تنقضي عدتهن فتبين ولا تضروهن بمراجعتهنَّ {وأشهدوا ذوي عدل منكم} على الرَّجعة أو الفراق {ومَنْ يتَّق الله} يُعطه فيما يأمره وينهاه {يجعل له مخرجاً} من الشدَّة إلى الرَّخاء ومن الحرام إلى الحلال ومن النَّار إلى الجنَّة يعني: من صبر على الضِّيق واتَّقى الحرام جعل الله له مخرجاً من الضِّيق',NULL,'1107',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5221,'{ويرزقه من حيث لا يحتسب} ويروى أنَّ هذا نزل في عوف بن مالك الأشجعيِّ أتى رسول الله صلى الله عليه وسلم فقال: إنَّ العدو أسر ابني وشكا إليه الفاقة فَقَالَ لَهُ رَسُولُ اللَّهِ صَلَّى اللَّهُ عَلَيْهِ وسلم اتَّق الله واصبر وأَكْثِرْ من قول: لا حول ولا قوة إلاَّ بالله ففعل الرَّجل ذلك فبينا هو في بيته إذ أتاه ابنه وقد غفل عنه العدو وأصابَ إبلاً لهم وغنماً فساقها إلى أبيه {ومَن يتوكل على الله} ما أهمَّه يتوثق به ويسكن قلبه إليه {فهو حسبه} كافيه {إنَّ الله بالغ أمره} يبلغ أمره فيما يريد وينفذه {قد جعل الله لكل شيء قدراً} ميقاتاً وأجلاً',NULL,'1107',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5222,'{واللائي يئسن من المحيض من نسائكم} أَيْ: القواعد من النِّساء اللاتي قعدن عن الحيض {إن ارتبتم} إنْ شككتم في حكمهنَّ ولم تعلموا عدَّتهنَّ وذلك أنَّهم سألوا فقالوا: قد عرفنا عدَّة التي تحيض فما عِدَّة التي لا تحيض والتي لم تحض بعده؟ فبيَّن الله تعالى ذلك فقال: {فعدتهنَّ ثلاثة أشهر واللائي لم يحضن} يعني: الصِّغار {وأولات الأحمال} ذوات الحمل من النِّساء {أجلهنَّ} عدتهنَّ {أن يضعن حملهنَّ} فإذا وضعت الحامل انقضت عدَّتها مُطلَّقةً كانت أو مُتوفَّى عنها زوجها {ومن يتق الله} بطاعته في أوامره ونواهيه {يجعل له من أمره يسراً} أتاه باليسر في أموره',NULL,'1108',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5223,'{ذلك} يعني: ما ذكر من أحكام العِدَّة {أمر الله أنزله إليكم} الآية',NULL,'1108',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5224,'{أسكنوهنَّ} أَيْ: المطلَّقات {من حيث سكنتم} أَيْ: من منازلكم وبيوتكم {من وُجدكم} : من سعتكم وطاقتكم {ولا تضاروهنَّ} لا تؤذوهن {لتضيقوا عليهن} مساكنهن فيحتجن إلى الخروج {وإن كنّ} أي المطلقات {أولات حَمْلٍ فأنفقوا عليهن حتى يضعن حملهن فإن أرضعن لكم} أولادكم منهنَّ {فآتوهن أجورهنّ} على إرضاعهن {وأتمروا بينكم بمعروف} أَيْ: ليقبل بعضكم من بعضٍ إذا أمره بمعروف {وإن تعاسرتم} تضايقتم ولم تتوافقوا على إرضاع الأمِّ {فسترضع} الصَّبيَّ {له} لوالده مرضعةٌ أخرى سوى الأُمَّ ولا تُكرَهُ الأمُّ على الإِرضاع',NULL,'1108',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5225,'{لينفق ذو سعة من سعته} أمر أهل التَّوسعة أن يُوسِّعوا على نسائهم المرضعات أولادهنَّ {ومَنْ قدر عليه رزقه} مَنْ كان رزقه بمقدار القوت {فلينفق} على قدر ذلك {لا يكلف الله نفسا إلا ما آتاها} أعطاها {سيجعل الله بعد عسرٍ يُسْراً} أعلم الله تعالى المؤمنين أنَّهم - وإن كانوا في حالٍ ضيقةٍ - سَيُوَسِّرهم ويفتح عليهم وكان الغالب عليهم في ذلك الوقت الفقر والفاقة ثمَّ فتح الله عليهم وجاءهم باليسر',NULL,'1109',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5226,'{وكأين} وكم {من قرية عتت عن أمر ربها ورسله} عتا أهلها عمَّا أمر الله تعالى به ورسله {فحاسبناها} في الآخرة {حساباً شديداً وعذَّبناها عذاباً نكراً} فظيعاً يعني: عذاب النَّار',NULL,'1109',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5227,'{فذاقت وبال أمرها} ثقل عاقبة أمرها {وكان عاقبة أمرها خسراً} خساراً وهلاكاً وقوله:',NULL,'1109',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5228,'{قد أنزل الله إليكم ذكراً} أَيْ: القرآن',NULL,'1109',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5229,'{رسولاً} أَيْ: وأرسل رسولاً {يتلو عليكم آياتِ الله مبينات ليخرج الذين آمنوا وعملوا الصالحات من الظلمات إلى النور} من ظلمات الكفر إلى نور الإيمان وقوله: {قد أحسن الله له رزقاً} أَيْ: رزقهُ الجنَّة التي لا ينقطع نعيمُها وقوله:',NULL,'1109',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5230,'{يتنزل الأمر بينهنَّ} يعني: إن من كلِّ سماء وكلِّ أرض خلقاً من خلقه وأمراً نافذاً من أمره {لتعلموا} أَيْ: أعلمكم ذلك وبيَّنه لتعلموا قدرته على كلّ شيء وأنَّه علم كلَّ شيءٍ',NULL,'1110',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5231,'{يا أيها النبيُّ لم تحرم ما أحل الله لك} [رُوي أنَّ النبيَّ صلى الله عليه وسلم دخل حفصة في يوم نوبتها فخرجت هي لبعض شأنها فأرسل رسول الله صلى الله عليه وسلم إلى مارية جاريته وأدخلها بيت حفصة وواقعها فلمَّا رجعت حفصة علمت بذلك فغضبت وبكت وقالت: أَما لي حرمةٌ عندك وحقٌّ؟ ! فقال رسول الله صلى الله عليه وسلم: اسكتي فهي حرامٌ عليَّ أبتغي بذلك رضاك وحلف أن لا يقربها وبشَّرها بأنَّ الخليفة من بعده أبوها وأبو عائشة رضي الله عنهم أجمعين ذكوراً وإناثاً وقال لها: لا تخبرني أحداً بما أسررتُ إليك من أمر الجارية وأمر الخلافة من بعدي فلمَّا خرج رسول الله صلى الله عليه وسلم من عندها أخبرت عائشة رضي الله عنها وعن أبيها بذلك وقالت: قد أراحنا الله من مارية فإنَّ رسول الله صلى الله عليه وسلم حرَّمها على نفسه] وقصَّت عليها القصَّة فنزل: {لم تحرم ما أحل الله لك} أَيْ: الجارية {تبتغي} بتحريمها {مرضاة أزواجك والله غفور رحيم} غفر لك ما فعلت من التَّحريم ثمَّ أمره بأن يكفِّر عن يمنيه فقال:',NULL,'1111',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5232,'{قد فرض الله لكم} أَيْ: بيَّن الله لكم {تحلَّة أيمانكم} ما تستحلُّ به المحذوف عليه من الكفَّار يعني: في سورة المائدة',NULL,'1112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5233,'{وإذ أسرَّ النبيُّ إلى بعض أزواجه} يعني: حفصة {حديثاً} تحريم الجارية وأمر الخلافة {فلما نبأت به} أخبرت به عائشة رضوان الله عليهما وعلى أبيهما {وأظهره الله عليه} أطلع نبيَّه عليه السَّلام على إفشائها السِّرَّ {عرَّف بعضه} أخبر حفصه ببعض ما قالت لعائشة {وأعرض عن بعض} فلم يُعرِّفها إيَّأه على وجه التَّكرُّم والإِغضاء {فلما نبأها به} أخبر حفصة بما فعلت {قالت من أنبأك هذا} من أخبرك بما فعلت؟ {قال نبأني العليم الخبير}',NULL,'1112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5234,'{إن تتوبا إلى الله} يعني: عائشة وحفصة {فقد صغت قلوبكما} عذلت وزاغت عن الحقِّ وذلك أنَّهما أحبَّتا ما كره رسول الله صلى الله عليه وسلم من اجتناب جاريته {وإن تظاهرا عليه} تتعاونا على أذى رسول الله صلى الله عليه وسلم {فإنَّ الله هو مولاه} وليُّه وحافظه فلا يضرُّه تظاهُرُكُما عليه وقوله: {وصالح المؤمنين} قيل: أبو بكر وعمر رضي الله عنهما وهو تفسير النبي صلى الله عليه وسلم {والملائكة بعد ذلك ظهير} أَيْ: الملائكة بعد هؤلاء أعوانٌ',NULL,'1112',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5235,'{عَسَى رَبُّهُ إِنْ طَلَّقَكُنَّ أَنْ يُبْدِلَهُ أَزْوَاجًا خيراً منكن} هذا إخبارٌ عن قدرة الله تعالى على أن يبذله لو طلَّق أزواجه خيراً منهنَّ وتخويفٌ لنسائه وقوله: {قانتات} مطيعاتٍ {سائحات} صائماتٍ',NULL,'1113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5236,'{يا أيها الذين آمنوا قُوا أنفسكم وأهليكم ناراً} أَيْ: خذوا أنفسكم وأهليكم بما يُقرِّب من الله تعالى وجَنِّبوا أنفسكم وأهليكم المعاصي {وقودها الناس والحجارة} أَيْ: توقد بهذين الجنسين {عليها ملائكة غلاظ شداد لا يعصون الله ما أمرهم ويفعلون ما يؤمرون} يعني: خزنة جهنم وقوله:',NULL,'1113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5237,'{يا أيها الذين كفروا لا تعتذروا اليوم إنما تجزون ما كنتم تعملون}',NULL,'1113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5238,'{توبة نصوحاً} هي التَّوبة التي تنصح صاحبها حتى لا يعود إلى ما تاب منه ونصوحاً معناه بالغةً في النُّصْح وقوله: {لا يخزي الله النبيَّ والذين آمنوا معه} أَيْ: لا يفضحكم ولا يهلكهم {نورهم} على الصِّراط {يسعى بين أيديهم وبأيمانهم يقولون ربنا أتمم لنا نورنا} إذا طُفىء نور المنافقين دعوا الله وسألوه أن يتمَّ لهم النُّور ثمَّ ضرب مثلاً للنِّساء الصَّالحات والطَّالحات فقال:',NULL,'1113',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5239,'{يا أيها النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ وَمَأْوَاهُمْ جهنم وبئس المصير}',NULL,'1114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5240,'{ضرب الله مثلاً للذين كفروا امرأة نوح وامرأة لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما} أَيْ: في الدِّين فكانت امرأةُ نوحٍ تخبر قومه أنَّه مجنونٌ وامرأة لوط دلَّت على أضيافه {فلم يُغْنيا} يعني: نوحاً ولوطاً {عنهما من} عذاب {الله شيئاً} من شيءٍ وهذا تخويفٌ لعائشة وحفصة وإخبار أنَّ الأنبياء لا يُغنون عن مَنْ عمل بالمعاصي شيئاً وقطعٌ لطمع من ركب المعصية رجاء أن ينفعه صلاح غيره وقوله:',NULL,'1114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5241,'{ربِّ ابنِ لي عندك بيتاً في الجنة} قيل: إنَّ فرعون لما تبيَّن له إسلامها وَتَدَها على الأرض بأربعة أوتاد على يديها ورجليها فقالت وهي تعذَّب: {ربِّ ابنِ لي عندك بيتاً في الجنة ونجني من فرعون وعمله} أَيْ: تعذيبه إيَّاي وفي هذا بيانٌ أنَّها لم تمل إلى معصيته مع شدَّة ما قاست من العذاب وكذا فليكن صوالح النِّساء وأمرٌ لعائشة وحفصة أن يكونا كآسيةَ وكمريم بنت عمران وقوله:',NULL,'1114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5242,'{ومريم ابنة عمران} هو عطفٌ على قوله: امرأة فرعون {التي أحصنت فرجها} أَيْ: عفَّت وحفظت {فنفخنا فيه من} جيب درعها من {روحنا} فُسِّر في سورة الأنبياء {وصدَّقت بكلمات ربِّها وكتبه} آمنت بما أنزل الله على الأنبياء {وكانت من القانتين} أَيْ: من القوم المُطيعين لله أَيْ: إِنَّها أطاعت فدخلت في جملة المطيعين لله من الرِّجال والنِّساء',NULL,'1114',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5243,'{تبارك} أَيْ: تعالى وتعظَّم {الذي بيده الملك} يُؤتيه مَنْ يشاء وينزعه عمَّن يشاء',NULL,'1116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5244,'{الذي خلق الموت والحياة ليبلوكم} في الحياة {أيكم أحسن عملاً} أَيْ: أطوع لله وأروع عن محارمه ثمَّ يُجازيكم بعد الموت',NULL,'1116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5245,'{الذي خلق سبع سماوات طباقا} بعضها فوق بعض {ما ترى في خلق الرحمن} أَيْ: خلقه السَّماء {من تفاوت} اضطرب واختلافٍ بل هي مستويةٌ مستقيمةٌ {فارجع البصر} أعد فيها النَّظر {هل ترى من فطور} صدوعٍ وشقوقٍ {ثم ارجع البصر} كرِّر النظر {كرَّتين} مرَّتين',NULL,'1116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5246,'{ينقلب إليك البصر} ينصرف ويرجع {خاسئاً} صاغراً ذليلاً {وهو حسير} أيْ: وقد أعيا من قبل أن يرى في السَّماء خللاً',NULL,'1116',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5247,'{ولقد زيَّنا السماء الدنيا} التي تدنو منكم {بمصابيح} بكواكب {وجعلناها رجوماً} مرامي {للشياطين} إذا استرقوا السَّمع {وأعتدنا لهم} في الآخرة {عذاب السعير}',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5248,'{وللذين كفروا بربهم عذاب جهنم وبئس المصير}',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5249,'{إذا ألقوا فيها سمعوا لها} لجهنَّم {شهيقاً} صوتاً كصوت الحمار {وهي تفور} تغلي',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5250,'{تكاد تميز من الغيظ} تنقطع غضباً على الكفَّار {كلما ألقي فيها فوج سألهم خزنتها} سؤال توبيخ: {ألم يأتكم نذير} رسولٌ في الدُّنيا ينذركم عذاب الله؟ فقالوا:',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5251,'{قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء إن أنتم إلاَّ في ضلال كبير}',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5252,'{لو كنا نسمع} من الرسل مَنْ يفهم ويتفكَّر {أو نعقل} عقل مَن ينظر {ما كنا في أصحاب السعير} وقوله:',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5253,'{فاعترفوا بذنبهم} بتكذيب الرُّسل ثمَّ اعترفوا بجهلهم {فسحقاً لأصحاب السعير} أَيْ: أسحقهم الله سحقاً أَيْ: باعدهم من رحمته مُباعدةً',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5254,'{إن الذين يخشون ربهم بالغيب} قبل مُعاينة العذاب وأحكام الآخرة',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5255,'{وأسروا قولكم أو اجهروا به} نزلت في المشركين الذين كانوا ينالون من رسول الله صلى الله عليه وسلم بألسنتهم فيخبره الله تعالى فقالوا: فيما بينهم: أَسرّوا قولكم كيلا يسمع إله محمد فقال الله تعالى:',NULL,'1117',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5256,'{ألا يعلم من خلق} أَيْ: ألا يعلم ما في صدوركم وما تُسرّون به مَنْ خلقكم؟',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5257,'{هو الذي جعل لكم الأرض ذلولاً} سهلاً مُسخَّرةً {فامشوا في مناكبها} جوانبها {وإليه النشور} إليه يبعث الخلق',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5258,'{أأمنتم من في السماء} قدرته وسلطانه وعرشه {أن يخسف بكم الأرض} تغور بكم {فإذا هي تمور} تتحرَّك بكم وترتفع فوقكم وقوله:',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5259,'{فستعلمون} أَيْ: عند مُعاينة العذاب {كيف نذير} أَيْ: إنذاري بالعذاب',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5260,'{ولقد كذَّب الذين من قبلهم فكيف كان نكير} إنكاري إذ أهلكتهم',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5261,'{أولم يروا إلى الطير فوقهم صافات} باسطاتٍ أجنحتها {ويقبضن} يضربن بها جنوبهنَّ {ما يمسكهنَّ} في حال القبض والبسط {إلاَّ الرحمن} بقدرته',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5262,'{أمن هذا الذي هو جند لكم ينصركم مِنْ دون الرحمن} يدفع عنكم عذابه',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5263,'{بل لجُّوا} تمادوا {في عتوّ} عصيانٍ وضلالٍ {ونفور} تباعدٍ عن الحقِّ',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5264,'{أفمن يمشي مكباً على وجهه} أَيْ: الكافر يُحشر يوم القيامة وهو يمشي على وجهه يقال: كببْتُ فلاناً على وجهه فأكبَّ هو يقول هذا {أهدى أمن يمشي سوياً} مستوياً مستقيماً {على صراط مستقيم} وهو المؤمن',NULL,'1118',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5265,'{قل هو الذي أنشأكم} خلقكم {وَجَعَلَ لَكُمُ السَّمْعَ وَالأَبْصَارَ وَالأَفْئِدَةَ قَلِيلا مَا تشكرون} أَيْ: لا تشكرون خالقكم وخالق هذه الأعضاء لكم إذ أشركتم به غيره',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5266,'{قل هو الذي ذرأكم} خلقكم {في الأرض وإليه تحشرون}',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5267,'{ويقولون متى هذا الوعد} أَيْ: وعد الحشر',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5268,'{قل إنما العلم} بوقوعه ومجيئه {عند الله وإنما أنا نذير} مُخوِّفٌ {مبين} أُبيِّن لكم الشَّريعة',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5269,'{فلمَّا رأوه} أَيْ: العذاب في الآخرة {زلفة} قريباً {سيئت وجوه الذين كفروا} تبيَّن في وجوههم السُّوء وعلتها الكآبة {وقيل هذا} العذاب {الذي كنتم به تَدَّعُون} تفتعلون من الدُّعاء أَيْ: تدعون الله به إذ تقولون: {اللَّهُمَّ إِنْ كَانَ هَذَا هُوَ الْحَقَّ مِنْ عندك} الآية',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5270,'{قل أرأيتم إن أهلكني الله} فعذَّبني {ومَنْ معي أو رحمنا} غفر لنا {فمن يجير الكافرين من عذاب أليم} يعني: نحن مع إيماننا خائفون نخاف عذاب الله ونرجو رحمته فمن يمنعكم من عذابه وأنتم كافرون؟',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5271,'{قل هو الرحمن آمنا به وعليه توكلنا فستعلمون من هو في ضلال مبين}',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5272,'{قُلْ أرأيتم إن أصبح ماؤكم غوراً} غائراً ذاهباً في الأرض {فمن يأتيكم بماء معين} ظاهر تناله الأيدي والدِّلاء',NULL,'1119',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5273,'{ن} أقسم الله بالحوت الذي على ظهره الأرضُ {والقلم} يعني: القلم الذي خلقه الله تعالى فجرى بالكائنات إلى يوم القيامة {وما يسطرون} أَيْ: وما تكتب الملائكة',NULL,'1120',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5274,'{ما أنت بنعمة ربك} بإنعامه عليك بالنُّبوِّة {بمجنون} أَيْ: إِنَّك لا تكون مجنوناً وقد أنعم الله عليك بالنُّبوَّة وهذا جوابٌ لقولهم: {وقالوا يا أيها الذي نزل عليه الذكر إنَّك لمجنونٌ}',NULL,'1120',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5275,'{وإنَّ لك لأجراً غير ممنون} غير مقطوعٍ ولا منقوصٍ',NULL,'1120',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5276,'{وإنك لعلى خلق عظيم} أَيْ: أنت على الخُلُقِ الذي أمرك الله به في القرآن',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5277,'{فستبصر} يا محمد {ويبصرون} أي: المشركون الذين رموه بالجنون',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5278,'{بأيكم المفتون} الفتنة أَبِكَ أم بهم',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5279,'{هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَهُوَ أعلم بالمهتدين}',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5280,'{فلا تطع المكذبين} فيما دعوك إليه من دينهم',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5281,'{وَدُّوا لو تدهن فيدهنون} تلين فلينون لك',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5282,'{ولا تطع كلَّ حلاَّفٍ} كثير الحلف بالباطل أَيْ: الوليد بن المغيرة {مهين} حقير',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5283,'{همَّاز} عيَّابٍ {مشَّاء بنميم} سَاعٍ بين النَّاسِ بالنَّميمة',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5284,'{مناع للخير} بخيلٍ بالمال عن الحقوق {معتد} مجاوزٍ في الظُّلم {أثيم} آثمٍ',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5285,'{عتل} جافٍ غليظٍ {بعد ذلك} مع ذكرنا من أوصافه {زنيم} مُلحَقٍ بقومه وليس منهم',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5286,'{إن كان} لأن كان {ذا مال وبنين} يُكذِّب بالقرآن وهو قوله:',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5287,'{إذا تتلى عليه آياتنا قال أساطير الأولين} والمعنى: أَيجعل مُجازاة نعمة الله عليه بالمال والبنين الكفر بآياتنا؟',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5288,'{سنسمه على الخرطوم} سنجعل على أنفه علامةً باقيةً ما عاش نخطم أنفه بالسَّيف يوم بدرٍ',NULL,'1121',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5289,'{إنا بلوناهم} امتحنا أهل مكَّة بالقحط والجوع {كما بلونا أصحاب الجنة} كما امتحنَّا أصحاب البستان بإحراقها وذهاب قوتهم منها وكانوا قوماً بناحية اليمن وكان لهم أبٌ وله جنَّةٌ كان يتصدَّق فيها على المساكين فلمَّا مات قال بنوه: نحن جماعةٌ وإنْ فعلنا ما كان يفعل أبونا ضاق علينا الأمر فخلقوا ليقطعنَّ ثمرها بسدفةٍ من اللَّيل كيلا يشعر المساكين فيأتوهم وهو قوله: {إذْ أقسموا ليصرمنها مصبحين}',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5290,'{ولا يستثنون} ولا يقولون إن شاء الله',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5291,'{فطاف عليها طائف من ربك وهم نائمون} أَيْ: أنزل الله عليها ناراً أحرقتها',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5292,'{فأصبحت كالصريم} كاللَّيل المُظلم سوداء',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5293,'{فتنادوا مصبحين} نادى بعضهم بعضاً لمَّا أصبحوا ليخرجوا إلى الصِّرام وهو قوله:',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5294,'{أن اغدوا على حرثكم إن كنتم صارمين} قاطعين الثَّمر',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5295,'{فانطلقوا} ذهبوا إليها {وهم يتخافتون} يسارون الكلام بينهم',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5296,'بـ {أن لا يدخلنها اليوم عليكم مسكين}',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5297,'{وغدوا على حرد} قصدٍ وجدٍّ {قادرين} عند أنفسهم على ثمر الجنَّة',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5298,'{فلما رأوها} سواد محترقةً {قالوا إنَّا لضالون} مُخطئون طريقنا وليست هذه جنتنا ثم عملوا أنَّها عقوبةٌ من الله تعالى فقالوا:',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5299,'{بل نحن محرومون} حُرمنا ثمر جنَّتنا بمنعنا المساكين',NULL,'1122',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5300,'{قال أوسطهم} أعدلهم وأفضلهم: {ألم أقل لكم لولا تسبحون} هلاَّ تستثنون ومعنى التَّسبيح ها هنا الاستثناء بإن الله لأنَّه تعظيمٌ لله وكلُّ تعظيمٍ لله فهو تسبيحٌ له',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5301,'{قالوا سبحان ربنا} نزَّهوه عن أن يكون ظالماً وأقرُّوا على أنفسهم بالظُّلم فقالوا: {إنا كنا ظالمين}',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5302,'{فأقبل بعضهم على بعض يتلاومون} يلوم بعضهم بعضاً بما فعلوا من الهرب من المساكين ومنع حقهم',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5303,'{قالوا يا ويلنا إنا كنا طاغين} بمنع حقِّ الفقراء وترك الاستثناء',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5304,'{عسى ربنا أن يُبْدِلَنا خيراً منها} من هذه الجنَّة {إنا إلى ربنا راغبون}',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5305,'{كذلك العذاب} كما فعلنا بهم نفعل بمَنْ خالف أمرنا ثمَّ بيَّن ما عند الله للمؤمنين فقال تعالى:',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5306,'{إنَّ للمتقين عند ربهم جنات النعيم} فلمَّا نزلت قال بعض قريش: إنْ كان ما تذكرون حقَّاً فإنَّ لنا في الآخرة أكثرَ ممَّا لكم فنزل:',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5307,'{أفنجعل المسلمين كالمجرمين} {ما لكم كيف تحكمون}',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5308,'{ما لكم كيف تحكمون}',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5309,'{أم لكم كتاب} نزل من عند الله {فيه} ما تقولون {تدرسون} تُقرُّون ما فيه',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5310,'{إنَّ لكم فيه} في ذلك الكتاب {لما تخيرون} تختارون',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5311,'{أم لكم أيمان} عهودٌ ومواثيق {علينا بالغة} محكمةٌ لا ينقطع عهدها {إلى يوم القيامة إنَّ لكم لما تحكمون} تقضون وكسرت إنَّ في الآيتين لمكان اللازم في جوابها وحقُّها الفتح لو لم تكن اللام',NULL,'1123',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5312,'فـ {سلهم} يا محمد {أيهم بذلك} الذين يقولون من أنَّ لهم في الآخرة حظّاَ {زعيم} كفيلٌ لهم',NULL,'1124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5313,'{أم لهم شركاء} آلهةٌ تكفل لهم بما يقولون {فليأتوا بشركائهم} لتكفل لهم {إن كانوا صادقين} فيما يقولون',NULL,'1124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5314,'{يوم يكشف عن ساق} عن شدَّةٍ من الأمر وهو يوم القيامة قال ابن عباس رضي الله عنه: أشدُّ ساعةٍ في القيامة فصار كشف السَّاق عبارةً عن شدَّة الأمر {ويدعون إلى السجود} أَيْ: الكافرون والمنافقون {فلا يستطيعون} يصير ظهرهم طبقاً واحداً كلَّما أراد أن يسجد واحدٌ منهم خرَّ على قفاه',NULL,'1124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5315,'{خاشعة أبصارهم} ذليلةً لا يرفعونها {ترهقهم} تغشاهم {ذلَّة وقد كانوا يدعون إلى السجود} في الدُّنيا {وهم سالمون} فيأبون ولا يسجدون لله',NULL,'1124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5316,'{فذرني ومن يكذب بهذا الحديث} دعني والمُكذِّبين بهذا القرآن أَيْ: كِلْهُمْ إليَّ ولا تشغل قلبك بهم فإنِّي أكفيك أمرهم {سنستدرجهم من حيث لا يعلمون} أَيْ: نأخذهم قليلاً قليلاً ولا نباغتهم',NULL,'1124',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5317,'{وأملي لهم} أُمهلهم كي يزدادوا تمادياً في الشِّرك {إنَّ كيدي متين} شديدٌ لا يطاق',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5318,'{أم تسألهم} بل أتسألهم على ما آتيتهم به من الرِّسالة {أجراً فهم من مغرم} ممَّا يعطونك {مُثقلون}',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5319,'{أم عندهم الغيب} علم ما في غدٍ {فهم يكتبون} يحكمون وقوله:',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5320,'{ولا تكن كصاحب الحوت} كيونس في الضَّجر والعجلة {إذ نادى} دعا ربَّه {وهو مكظوم} مملوءٌ غمَّاً',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5321,'{لولا أن تداركه} أدركه {نعمة} رحمةٌ {من ربه لنبذ} لطرح حين ألقاه الحوت {بالعراء} بالأرض الفضاء الواسعة لأنَّها خاليةٌ من البناء والإِنسان والأشجار {وهو مذموم} مجرم',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5322,'{فاجتباه ربه} فاختاره {فجعله من الصالحين} بأن رحمه وتاب عليه',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5323,'{وإن يكاد الذين كفروا ليزلقونك بأبصارهم لما سمعوا الذكر} أَيْ: إنَّهم لشدَّة إبغاضهم وعداوتهم لك إذا قرأت القرآن ينظرون إليك نظراً شديداً يكاد يصرعك ويسقطك عن مكانك {ويقولون إنه لمجنون}',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5324,'{وما هو} أَيْ: القرآن {إلا ذكر} عظمة {للعالمين}',NULL,'1125',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5325,'{الحاقة} أَيْ: القيامة لأنَّها حقَّت فلا كاذبة لها',NULL,'1126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5326,'{ما الحاقة} استفهامٌ معناه التَّعظيم لشأنها كقولك: زيدٌ ما هو؟',NULL,'1126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5327,'{وما أدراك ما الحاقة} أَيٌّ شيء أعلمك ما ذلك اليوم؟ ثمَّ ذكر أمر مَنْ كذَّب بالقيامة فقال:',NULL,'1126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5328,'{كذبت ثمود وعادٌ بالقارعة} بالقيامة التي تقرع القلوب',NULL,'1126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5329,'{فأمَّا ثمود فأهلكوا بالطاغية} أَيْ: بالصَّيحة الطَّاغية وهي التي جاوزت المقدار',NULL,'1126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5330,'{وأمَّا عادٌ فأهلكوا بريح صرصر عاتية} عتت على خُزَّانها فلم تُطعهم',NULL,'1126',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5331,'{سخرها عليهم} استعملها عليهم كما شاء وقوله: {حسوماً} أَيْ: دائمةً مُتتابعةً والمعنى: تحسمهم حسوماً أَيْ: تذهبهم وتفنيهم {فترى القوم} أيْ: أهل القرى {فيها} أَيْ: في تلك الأيَّام {صرعى} جمع صريعٍ {كأنهم أعجاز} أصول {نخل خاوية} ساقطةٍ',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5332,'{فهل ترى لهم من باقية} أَيْ: هل ترى منهم باقياً',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5333,'{وجاء فرعون ومِنْ قِبَلِه} أَيْ: تُبَّاعه ومَنْ قرأ {ومَنْ قَبْلَه} فمعناه: مَنْ تقدَّمه من الأمم {والمؤتفكات} أَيْ: أهل قرى قوم لوط {بالخاطئة} بالخطأ العظيم وهو الكفر',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5334,'{فعصوا رسول ربهم فأخذهم أخذة رابية} زائدةً تزيد على الأخذات',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5335,'{إنَّا لما طغى الماء} جاوز حدَّه يعني: أيَّام الطُّوفان {حملناكم} أَيْ: حملنا آباءكم {في الجارية} وهي السَّفينة',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5336,'{لنجعلها} لنجعل تلك الفعلة التي فعلنا من إغراق قوم نوحٍ وإنجاء مَنْ معه {لكم تذكرة} تتذكَّرونها فتتَّعظون بها {وتعيها أذن واعية} لتحفظها كلُّ أذنٍ تحفظ ما سمعت',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5337,'{فإذا نفخ في الصور نفخة واحدة} أَيْ: النِّفخة الأولى لقيام السَّاعة',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5338,'{وحملت الأرض والجبال فدكتا} كُسرتا {دكَّة واحدة} فصارت هباءً منبثاً',NULL,'1127',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5339,'{فيومئذٍ وقعت الواقعة} قامت القيامة',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5340,'{وانشقت السماء فهي يومئذٍ واهية} أَيْ: مُتَشَقِّقةٌ',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5341,'{والملك} يعني: الملائكة {على أرجائها} نواحيها {ويحمل عرش ربك فوقهم} فوق الملائكة {يومئذٍ ثمانية} أملاك',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5342,'{يومئذٍ تعرضون} على ربِّكم {لا تخفى منكم خافية} كقوله: {لا يخفى على الله منهم شيءٌ}',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5343,'{فأمَّا مَنْ أوتي كتابه بيمينه فيقول هاؤم اقرؤوا كتابيه} خذوا فاقرؤوا كتابي وذلك لما يرى فيه الحسنات',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5344,'{إني ظننت أني ملاق حسابيه} أَيْ: أيقنت أنِّي أُحاسب',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5345,'{فهو في عيشة راضية} ذات رضىً أَيْ: يرضى بها صاحبها',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5346,'{في جنة عالية}',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5347,'{قطوفها دانية} ثمارها قريبةٌ من مريدها على أيِّ حالٍ كان يقال لهم:',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5348,'{كلوا واشربوا هنيئاً بما أسلفتم} قدمتم لآخرتم من الأعمال الصَّالحة {في الأيام الخالية} الماضية في الدنيا وقوله:',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5349,'{وأمَّا مَنْ أوتي كتابه بشماله فيقول يا ليتني لم أوت كتابيه}',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5350,'{ولم أدر ما حسابيه}',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5351,'{يا ليتها كانت القاضية} يقول: ليت الموتة التي مُتها لم أَحْيَ بعدها',NULL,'1128',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5352,'{ما أغنى عني ماليه}',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5353,'{هلك عني سلطانية} ذهب عني حجتي وزال عن ملكي وقوَّتي فيقول الله لخزنة جهنَّم:',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5354,'{خذوه فغلُّوه} {ثم الجحيم صلوه} أدخلوه',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5355,'{ثم الجحيم صلوه}',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5356,'{ثمَّ في سلسلة ذرعها سبعون ذراعاً فاسلكوه} أَيْ: أدخلوه في تلك السلسة فتدخل في دبره وتخرج من فيه وهي سلسة لو جُمع حديد الدُّنيا ما وزن حلقةً منها',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5357,'{إنه كان لا يؤمن بالله العظيم}',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5358,'{ولا يحض على طعام المسكين} لا يأمر بالصَّدقة على الفقراء',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5359,'{فليس له اليوم هاهنا حميم} قريبٌ ينفعه',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5360,'{ولا طعام إلا من غسلين} وهو صديد أهل النَّار',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5361,'{لا يأكله إلاَّ الخاطئون} وهم الكافرون',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5362,'{فلا أقسم} {لا} زائدة {بما تبصرون} ما ترون من المخلوقات',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5363,'{وما لا تبصرون} ما ترون منها',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5364,'{إنه} إنَّ القرآن {لقول} لتلاوةُ {رسول كريم} على الله يعني: محمَّداً صلوات الله عليه',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5365,'{وما هو بقول شاعر} أَيْ: ليس هو شاعراً {قليلاً ما تؤمنون} {ما} لغوٌ مؤكِّدة',NULL,'1129',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5366,'{ولا بقول كاهن} وهو الذي يُخبر عن المُغيَّبات من جهة النُّجوم كذباً وباطلاً ثمَّ بيَّن أنَّ ما يتلوه من الله تعالى فقال:',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5367,'{تنزيل من رب العالمين}',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5368,'{ولو تقول علينا بعض الأقاويل} يعني: النبي صلى الله عليه وسلم لو قال ما لم يُؤمر به وأتى بشيءٍ مِنْ قِبَل نفسه {لأخذنا منه باليمين} {مِنْ} صلةٌ والمعنى: لأخذناه بالقوَّة والقدرة',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5369,'{لأخذنا منه باليمين}',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5370,'{ثمَّ لقطعنا منه الوتين} وهو نياط القلب أَيْ: لأهلكناه',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5371,'{فما منكم من أحد عنه حاجزين} أَيْ: لم يحجزنا عنه أحدٌ منكم',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5372,'{إنه لتذكرة للمتقين}',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5373,'{وإنا لنعلم أن منكم مكذبين}',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5374,'{وإنَّه} أَيْ: القرآن {لحسرة على الكافرين} يوم القيامة إذا رأوا ثواب متابعيه',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5375,'{وإنه لحق اليقين} أَيْ: وإنَّه اليقين حقُّ اليقين',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5376,'{فسبح باسم ربك العظيم} نزِّهه عن السُّوء',NULL,'1130',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5377,'{سأل سائل} دعا داعٍ {بعذاب واقع}',NULL,'1131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5378,'{للكافرين} على الكافرين وهو النَّضر بن الحارث حين قال: {اللَّهُمَّ إِنْ كَانَ هَذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ} الآية {ليس له دافع} ليس لذلك العذاب الذي يقع بهم دافعٌ',NULL,'1131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5379,'{من الله} أَيْ: ذلك العذاب يقع بهم من الله {ذي المعارج} ذي السماوات',NULL,'1131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5380,'{تعرج الملائكة والروح} يعني: جبريل عليه السَّلام {إليه} إلى محل قربته وكرامته وهو السَّماء {في يوم} {في} صلةُ واقعٍ أَيْ: عذابٌ واقعٌ فِي يَوْمٍ {كَانَ مِقْدَارُهُ خَمْسِينَ أَلْفَ سَنَةٍ} وهو يوم القيامة',NULL,'1131',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5381,'{فاصبر صبراً جميلاً} وهذا قبل أن أمر بالقتال',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5382,'{إنهم} يعني: المشركين {يرونه} يرون ذلك اليوم {بعيداً} مُحالاً لا يكون',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5383,'{ونراه قريباً} لأنَّ ما هو آتٍ قريبٌ ثمَّ ذكر متى يكون ذلك اليوم فقال:',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5384,'{يوم تكون السماء كالمهل} كدرديِّ الزَّيت وقيل: كالقار المُذاب وقد مَّر هذا',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5385,'{وتكون الجبال} : الجواهر وقيل: الذَّهب والفضَّة والنُّحاس {كالعهن} كالصُّوف المصبوغ',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5386,'{ولا يسأل حميم حميماً} لا يسأل قريبٌ عن قريبٍ لاشتغاله بما هو فيه',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5387,'{يبصرونهم} يُعرَّف بعضهم بعضاً أَيْ: إنَّ الحميم يرى حميمه ويعرفه ولا يسأل عن شأنه {يودُّ المجرم} يتمنَّى الكافر {لو يفتدي من عذاب يومئذٍ ببنيه}',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5388,'{وصاحبته} وزوجته {وأخيه}',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5389,'{وفصيلته} عشيرته التي فُصِلَ منها {التي تؤويه} تضمُّه إليها في النَّسب',NULL,'1132',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5390,'{ومَنْ في الأرض جميعاً ثم ينجيه} ذلك الافتداء',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5391,'{كلا} ليس الأمر كذلك لا ينجيه شيءٌ {إنها لظى} وهي من أسماء جهنَّم',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5392,'{نزاعة للشوى} يعني: جلود الرأس تقشرها عنه',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5393,'{تدعو} الكافر باسمه والمنافق فتقول: إليَّ إليَّ يا {مَنْ أدبر} عن الإِيمان {وتولى} أعرض',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5394,'{وجمع} المال {فأوعى} فأمسكه في وعائه ولم يُؤدِّ حقَّ الله منه',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5395,'{إنَّ الإِنسان خُلق هلوعاً} وتفسير الهلوع ما ذكره في قوله: {إذا مسَّه الشر جزوعاً} يجزع من الشر ولا يستمسك',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5396,'{إذا مسَّه الشر جزوعاً}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5397,'{وإذا مسَّه الخير منوعاً} إذا أصاب المال منع حقَّ الله',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5398,'{إلاَّ المصلين} أَيْ: المؤمنين',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5399,'{الذين هم على صلاتهم دائمون} لا يلتفتون في الصَّلاة عن سمت القبلة',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5400,'{والذين في أموالهم حق معلوم}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5401,'{للسائل والمحروم}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5402,'{والذين يصدقون بيوم الدين}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5403,'{والذين هم من عذاب ربهم مشفقون}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5404,'{إن عذاب ربهم غير مأمون}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5405,'{وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5406,'{إِلا عَلَى أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5407,'{فَمَنِ ابْتَغَى وَرَاءَ ذَلِكَ فَأُولَئِكَ هُمُ الْعَادُونَ}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5408,'{وَالَّذِينَ هُمْ لأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5409,'{والذين هم بشهاداتهم قائمون} يقيمونها ولا يكتمونها',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5410,'{والذين هم على صلاتهم يحافظون}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5411,'{أولئك في جنات مكرمون}',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5412,'{فمال الذين كفروا} ما بالهم {قبلك مهطعين} يُديمون النَّظر إليك ويتطلّعون نحوك',NULL,'1133',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5413,'{عن اليمين وعن الشمال} عن جوانبك {عزين} جماعاتٍ حلقاً حلقاً وذلك أنَّهم كانوا يجتمعون عنده ويستهزئون به وبأصحابه ويقولون: لئن دخل هؤلاء الجنَّة فلندخلنَّها قبلهم قال الله تعالى:',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5414,'{أيطمع كل امرئ منهم أن يدخل جنة نعيم * كلا} لا يدخلونها {إنا خلقناهم مما يعلمون} من ترابٍ ومن نطفةٍ فلا يستوجب أحدٌ الجنة بشرفه وماله لأنَّ الخلق كلَّهم من أصلٍ واحدٍ بل يستوجبونها بالطاعة',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5415,'{كلا إنا خلقناهم مما يعلمون}',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5416,'{فلا أقسم} لا صلة يعني: أُقسم وقوله:',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5417,'{وما نحن بمسبوقين} أَيْ: بمغلوبين نظيره قد تقدَّم في سورة الواقعة',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5418,'{فذرهم يخوضوا} في باطلهم {ويلعبوا} في دنياهم {حتى يُلاقوا يومهم الذي يوعدون} نسختها آية القتال',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5419,'{يوم يخرجون من الأجداث} القبور {سراعاً كأنهم إلى نصب} إلى شيءٍ منصوبٍ من علمٍ أو رايةٍ {يوفضون} يُسرعون',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5420,'{خاشعة أبصارهم} ذليلةً خاضعةً لا يرفعونها لذلَّتهم {ترهقهم ذلة} يغشاهم هوان {ذلك اليوم الذي كانوا يوعدون} يعني: يوم القيامة',NULL,'1134',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5421,'{إنا أرسلنا نوحاً إلى قومه أن أنذر قومك} أَيْ: بأن خوِّفهم عذاب الله {من قبل أن يأتيهم عذابٌ أليم}',NULL,'1135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5422,'{قال يا قوم إني لكم نذير مبين} {أن اعبدوا الله واتقوه وأطيعون}',NULL,'1135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5423,'{أن اعبدوا الله واتقوه وأطيعون}',NULL,'1135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5424,'{يغفر لكم من ذنوبكم} {مِنْ} صلة {ويؤخركم} عن العذاب {إلى أجل مسمىً} وهو أجل الموت فتموتوا غيرَ ميتة مَنْ يهلك بالعذاب {إنَّ أجل الله إذا جاء لا يؤخر} إذا جاء الأجل في الموت لا يُؤخَّر {لو كنتم تعلمون} ذلك وقوله:',NULL,'1135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5425,'{قال رب إني دعوت قومي ليلا ونهارا}',NULL,'1135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5426,'{إلاَّ فراراً} أَيْ: نفاراً عن طاعتك وإدباراً عني',NULL,'1135',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5427,'{وإني كلما دعوتهم} إلى الإِيمان بك {لتغفر لهم} ما قد سلف من ذنوبهم {جعلوا أصابعهم في آذانهم} لئلا يسمعوا صوتي {واستغشوا ثيابهم} غطُّوا بها وجوههم مبالغةً في الإِعراض عني كيلا يروني {وأصروا} أَقاموا على كفرهم {واستكبروا} عن اتِّباعي {استكباراً} لأنَّهم قالوا: {أَنؤمنُ لك واتَّبعكَ الأَرْذَلون}',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5428,'{ثم إني دعوتهم جهاراً} أظهرتُ لهم الدَّعوة',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5429,'{ثم إني أعلنت لهم وأسررت لهم إسراراً} أَيْ: خلطتُ دعاءَهم العلانيَة بدعاءِ السِّرِّ',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5430,'{فقلت استغفروا ربكم إنه كان غفاراً} {يرسل السماء عليكم مدرارا}',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5431,'{يرسل السماء عليكم مدراراً}',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5432,'{ويمددكم بأموال وبنين ويجعل لكم جنات ويجعل لكم أنهاراً} وذلك أنَّهم لما كذَّبوه حبس الله عنهم المطر وأعقم نساءَهم فهلكت أموالهم ومواشيهم فوعدهم نوحٌ إنْ آمنوا أَنْ يردَّ الله عليهم ذلك فقال: {يرسل السماء عليكم مدراراً} كثيرة الدر أي: كثرة المطر {ويمددكم بأموالٍ وبنين} : يعطكم زينة الدُّنيا وهي المال والبنون',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5433,'{ما لكم لا تَرْجُون لله وقاراً} لا تخافون لله عظمةً',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5434,'{وقد خلقكم أطواراً} حالاً بعد حالٍ نطفةً ثمَّ علقةً ثمَّ مضغةً إلى تمام الخلق',NULL,'1136',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5435,'{ألم تروا كيف خلق الله سبع سماوات طباقا} بعضها فوق بعض',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5436,'{وجعل القمر فيهن نوراً} أي: في إحدهن {وجعل الشمس سراجاً} تُضِيءُ لأهل الأرض',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5437,'{والله أنبتكم من الأرض نباتاً} جعلكم تنبتون من الأرض نباتاً وذلك أنَّه خلق آدم من الأرض وأولاده أحياءً منه',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5438,'{ثم يعيدكم فيها} أمواتاً {ويخرجكم} منها إخراجاً وقوله:',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5439,'{والله جعل لكم الأرض بساطا}',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5440,'{سبلاً فجاجاً} أَيْ: طرقاً بيِّنةً وقوله:',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5441,'{واتبعوا مَنْ لم يزده ماله وولده إلاَّ خساراً} أَيْ: اتَّبعوا أشرافهم الذين لا يزيدون بإنعام الله تعالى عليهم بالمال والولد إلاَّ طغياناً وكفراً',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5442,'{ومكروا مكراً كباراً} أفسدوا في الأرض فساداً عظيماً بالكفر وتكذيب الرسل',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5443,'{وقالوا} لسفلتهم: {لا تذرنَّ آلهتكم ولا تَذَرُنَّ ودَّاً ولا سواعاً ولا يغوث ويعوق ونسرا} وهي أسماء أوثانهم',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5444,'{وقد أضلوا كثيراً} أَيْ: ضلَّ كثيرٌ من النَّاس بسببها كقوله: {إنهنَّ أضللْنَ كثيراً من النَّاس} {ولا تزد الظالمين إلاَّ ضلالاً} دعاءٌ من نوحٍ عليهم بأن يزيدهم الله ضلالاً وذلك أن الله تعالى أخبره لن يؤمن من قومه إلاَّ من قد آمن فلما أيس نوح من إيمانهم دعا عليهم بالضَّلال والهلاك قال الله تعالى',NULL,'1137',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5445,'{ممَّا خطيئاتهم} {ما} صلة أَيْ: مِن خطيئاتهم التي ارتكبوها {أغرقوا} بالطُّوفان {فأدخلوا ناراً} بعد الغرق أَيْ: أُدخلوا جهنَّم {فلم يجدوا لهم من دون الله أنصاراً} لم يجدوا مَنْ يمنعهم من عذاب الله',NULL,'1138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5446,'{وقال نوحٌ رَبِّ لا تَذَرْ عَلَى الأَرْضِ مِنَ الْكَافِرِينَ ديَّاراً} أَيْ: نازل دار أَيْ: أحداً',NULL,'1138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5447,'{إنك إن تذرهم} فلا تهلكهم {يضلوا عبادك} بدعوتهم إلى الضَّلال {ولا يلدوا إلاَّ فاجراً كفاراً} إلاَّ مَنْ يفجر ويكفر وذلك أنَّ الله أخبره أنَّهم لا يلدون مؤمناً',NULL,'1138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5448,'{ربِّ اغفر لي ولوالدي} وكانا مؤمنين {ولمن دخل بيتي} مسجدي {مؤمنا وللمؤمنين والمؤمنات} إلى يوم القيامة {ولا تزد الظالمين إلاَّ تباراً} هلاكاً ودماراً',NULL,'1138',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5449,'{قل أوحي إليَّ} أَيْ: أُخبرت بالوحي من الله إليَّ {أنَّه استمع نفرٌ من الجن} وذلك أنَّ الله تعالى بعث نفراً من الجنِّ ليَستمعوا قراءة النبي صلى الله عليه وسلم وهو يُصلِّي الصُّبح ببطن نخلة وهؤلاء الذين ذكرهم الله في سورة الأحقاف في قوله: {وإذ صرفنا إليك} الآية فلما رجعوا إلى قومهم قالوا: {إنا سمعنا قرآناً عجباً} في فصاحته وبيانه وصدق إخباره',NULL,'1139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5450,'{يهدي إلى الرشد فآمنا به ولن نشرك بربنا أحدا}',NULL,'1139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5451,'{وأنَّه تعالى جدُّ ربنا} أي: جلاله وعظمته عن أن يتخذ ولدا أوصاحبة',NULL,'1139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5452,'{وأنَّه كان يقول سفيهنا} جاهلنا {على الله شططاً} غلوَّاً في الكذب حتى يصفه بالوالد والصاحبة',NULL,'1139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5453,'{وأنا ظننا أن لن تقول الإنس والجن على الله كذباً} أي: كنَّا نظنُّهم صادقين في أنَّ لله صاحبةً وولداً حتى سمعنا القرآن وكنَّا نظنُّ أنَّ أحداً لا يكذب على الله انقطع ها هنا قول الجن قال الله تعالى:',NULL,'1139',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5454,'{وأنه كان رجال من الإنس يعوذون برجال من الجن} وذلك أنَّ الرَّجل في الجاهليَّة كان إذا سافر فأمسى في الأرض الفقر قال: أعوذ بسيِّد هذا الوادي من شرِّ سفهاء قومه أَي: الجنِّ يقول الله: {فزادوهم رهقاً} أَيْ: فزادهم بهذا التَّعوُّذ طغياناً وذلك أنَّهم قالوا: سُدْنا الجنَّ والإِنس',NULL,'1140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5455,'{وأنهم ظنوا كما ظننتم أن لن يبعث الله أحداً} يقول: ظنَّ الجنُّ كما ظننتم أيُّها الإِنس أن لا بعث يوم القيامة وقالت الجنُّ:',NULL,'1140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5456,'{وأنا لمسنا السماء} أَي: رُمْنَا استراق السَّمع فيها {فوجدناها ملئت حرساً شديداً} من الملائكة {وشهباً} من النُّجوم يريدون: حُرست بالنُّجوم من استماعنا',NULL,'1140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5457,'{وأنا كنَّا} قبل ذلك {نقعد منها مقاعد للسمع فمن يستمع الآن يجد له شهاباً رصداً} أي: كواكب حفظةً تمنع من الاستماع',NULL,'1140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5458,'{وأنَّا لا ندري أشرٌّ أريد بمن في الأرض} بحدوث رجم الكواكب {أم أراد بهم ربهم رشداً} أَيْ: خيراً',NULL,'1140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5459,'{وأنا منا الصالحون} بعد استماع القرآن أَيْ: بررةٌ أتقياءُ {ومنا دون ذلك} دون البررة {كنا طرائق قدداً} أَيْ: أصنافاً مختلفين',NULL,'1140',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5460,'{وأنا ظننا أن لن نعجز الله في الأرض} علمنا أن لا نفوته إِنْ أراد بنا أمراً {ولن نعجزه هرباً} إِنْ طلبنا وقوله:',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5461,'{فلا يخاف بَخْساً} أَيْ: نقصاً {ولا رهقاً} أَيْ: ظلماً والمعنى: لا نخاف أن ينقص من حسناته ولا أن يُزاد في سيئاته',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5462,'{وأنا منا المسلمون ومنا القاسطون} الجائرون عن الحقّ {فمن أسلم فأولئك تحروا رشداً} قصدوا طريق الحقّ قال الله تعالى:',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5463,'{وأما القاسطون فكانوا لجهنم حطبا}',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5464,'{وألو استقاموا على الطريقة} لو آمنوا جميعاً أَي: الخلق كلُّهم أجمعون الجنُّ والإِنس {لأسقيناهم ماءً غدقاً} لوسَّعنا عليهم في الدُّنيا وضرب المثل بالماء لأنَّ الخير كلَّه والرِّزق بالمطر وهذا كقوله تعالى: {ولو أنَّ أهل القرى آمنوا واتقوا} الآية',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5465,'{لِنَفْتنهم فيه} لنختبرهم فنرى كيف شكرهم {ومن يعرض عن ذكر ربه يسلكه} يدخله {عذاباً صعداً} شاقاً',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5466,'{وأنَّ المساجد لله} يعني: المواضع التي يُصلَّى فيها وقيل: الأعضاء التي يسجد عليها وقيل: يعني: إنَّ السَّجدات لله جمع مسجد بمعنى السُّجود {فلا تدعوا مع الله أحداً} أمرٌ بالتَّوحيد لله تعالى في الصَّلاة',NULL,'1141',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5467,'{وإنه لما قام عبد الله يدعوه} أي: النَّبيُّ صلى الله عليه وسلم لمَّا قام ببطن نخلة يدعوا الله {كادوا يكونون عليه} كاد الجنُّ يتراكبون ويزدحمون حرصاً على ما يسمعون ورغبة فيه وقوله:',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5468,'{قل إنما أدعو ربي ولا أشرك به أحدا}',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5469,'{قل إني لا أملك لكم ضرّاً ولا رشدا}',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5470,'{ولن أجد من دونه ملتحداً} أَيْ: ملجأً',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5471,'{إلاَّ بلاغاً من الله ورسالاته} لكن أُبلِّغ عن الله ما أُرسلت به ولا أملك الكفر والإِيمان وهو قوله: {لا أملك لكم ضرّاً ولا رشداً} وقوله:',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5472,'{حتى إذا رأوا} أي: الكفَّار {ما يوعدون} من العذاب والنَّار {فسيعلمون} حينئذٍ {مَنْ أضعف ناصراً} أنا أو هم {وأقل عدداً}',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5473,'{قل إن أدري} ما أدري {أقريب ما توعدون} من العذاب {أم يجعل له ربي أمداً} أجلاً وغايةً',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5474,'{عالم الغيب} أي: هو عالم الغيب {فلا يظهر} فلا يطع على ما غيبه من العباد {أحداً}',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5475,'{إلاَّ من ارتضى} اصطفى {من رسول} فإنَّه يُطلعه على ما يشاء من الغيب معجزةً له {فإنَّه يسلك من بين يديه ومن خلفه رصداً} أي: يجعل من جميع جوانبه رصداً من الملائكة يحفظون الوحي من أن يسترقه الشَّياطين فتلقيه إلى الكهنة فيساوون الأنبياء',NULL,'1142',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5476,'{ليعلم} الله {أن قد أبلغوا رسالات ربهم} أي: ليُبلِّغوا رسالات ربِّهم فإذا بلَّغوا علم الله ذلك فصار كقوله: {وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنْكُمْ} أي: ولمَّا يجاهدوا {وأحاط بما لديهم} علم الله ما عندهم {وأحصى كلَّ شيء عدداً} أي: علم عدد كلِّ شيء فلم يخف عليه شيءٌ',NULL,'1143',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5477,'{يا أيها المزمل} أي: المُتَلفِّف بثيابه نزل هذا على النبي صلى الله عليه وسلم وهو مُتلَفِّفٌ بقطيفةٍ',NULL,'1144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5478,'{قم الليل إلاَّ قليلاً} أي: صلِّ كلَّ اللَّيلِ إلاَّ شيئاً يسيراً تنام فيه وهو الثُّلث ثمَّ قال:',NULL,'1144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5479,'{نصفه} أَيْ: قم نصفه {أو انقص منه} من النِّصف {قليلاً} إلى الثُّلث',NULL,'1144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5480,'{أو زد عليه} على النِّصف إلى الثُّلثين جعل له سعةً في مدَّة قيامه في اللَّيل فكأنَّه قال: قم ثلثي اللَّيل أو نصفه أو ثلثه فلمَّا نزلت هذه الآية أخذ المسلمون أنفسهم بالقيام على هذا المقادير وشقَّ ذلك عليهم لأنهم لكم يمكنهم أن يحفظوا هذه المقادير وكانوا يقومون اللَّيل كله حتى انتفخت أقدامهم ثمَّ خفَّف الله عنهم بآخر هذه السُّورة وهو قوله: {إنَّ ربك يعلم أنك تقوم} الآية ثمَّ نسخ قيام اللَّيل بالصَّلوات الخمس وكان هذا في صدر الإِسلام وقوله: {ورتل القرآن ترتيلاً} أَي: بيِّنه تبييناً بعضُه على إثر بعضٍ في تودة',NULL,'1144',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5481,'{قولا ثقيلا} رصينا زرينا ليس بالسفساف والخفيف لأنَّه كلام الله',NULL,'1145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5482,'{إنَّ ناشئة الليل} ساعاته {هي أشد وطئا} أثقلُ على المُصلِّين من ساعات النَّهار ومَنْ قرأ: وِطاء فمعناه: أشدُّ موافقةً بين القلب والسَّمع والبصر واللِّسان لأنَّ اللَّيل تهدأ فيه الصلوات وتنقطع الحركات ولا تحول دون تسمُّعه وتفهُّمه شيءٌ {وأقوم قيلاً} وأصوب قراءةً',NULL,'1145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5483,'{إنَّ لك في النهار سبحاً طويلاً} أَيْ: تصرُّفاً في حوائجك إقبالاً وإدباراً وهذا حثٌ على القيام باللَّيل لقراءة القرآن',NULL,'1145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5484,'{واذكر اسم ربك} بالتَّعظيم والتَّنزيه {وتبتل إليه تبتيلاً} وانقطع إليه في العبادة وقوله:',NULL,'1145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5485,'{فاتخذه وكيلاً} أَيْ: قيِّماً بأمورك مُفوَّضاً إليه',NULL,'1145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5486,'{واصبر على ما يقولون واهجرهم هجراً جميلاً} وهو أن لا تتعرَّض لهم ولا تشتغل بمكافآتهم وهذه الآية نسختها آية القتال',NULL,'1145',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5487,'{وذرني والمكذبين} لا تعنهم لشأنهم فإني أكفيكم يعني: رؤساء المشركين كقوله: {فذرني ومن يكذب بهذا الحديث} وقد مرَّ {أولي النعمة} ذوي التنغم والتًَّرفُّه {ومهِّلهم قليلاًَ} يعني: إلى مدَّة آجالهم',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5488,'{إنَّ لدينا} يعني: في الآخرة {أنكالاً} قيوداً {وجحيماً} ناراً عظيمةً',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5489,'{وطعاما ذا غصة} يغض في الحلوق ولا يسوغ وهو الغِسلين والضَّريع والزَّقُّوم',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5490,'{يوم ترجف الأرض والجبال} تضطرب وتتحرَّك {وكانت الجبال كثيباً مهيلاً} رملاً سائلاً',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5491,'{إنا أرسلنا إليكم رسولاً} محمدا صلى الله عليه وسلم {شاهداً عليكم} يشهد عليكم يوم القيامة بما فعلتم وقوله:',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5492,'{فأخذناه أخذاً وبيلاً} ثقيلاً غليظاً',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5493,'{فكيف تتقون إن كفرتم يوماً يجعل الولدان شيباً} أَيْ: فكيف تتحصَّنون من عذاب يومٍ يشيب الطِّفل لهوله وشدَّته إن كفرتم اليوم في الدُّنيا',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5494,'{السماء منفطر به} متشقِّق في ذلك اليوم',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5495,'{إنَّ هذه} الآيات {تذكرة} تذكيرٌ للخلق {فمن شاء اتخذ إلى ربه سبيلاً} بالطَّاعة والإِيمان',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5496,'{إن ربك يعلم أنك تقوم} للصَّلاة والقراءة {أدنى} أقلَّ {من ثلثي الليل ونصفه وثلثه} أي: وتقم نصفه ثلثه {وطائفة من الذين معك والله يقدِّر الليل والنهار} فيعلم مقادير أوقاتهما {علم أن لن تحصوه} لن تُطيقوا قيام اللَّيل {فتاب عليكم} رجع لكم إلى التَّخفيف {فاقرؤوا ما تيسر من القرآن} رخَّص لهم أن يقوموا فيقرؤوا ما أمكن وخفَّ بغير مقدارٍ معلومٍ من القراءة والمُدَّة {علم أن سيكون منكم مرضى} فيثقل عليهم قيام اللَّيل وكذلك المسافرون للتِّجارة والجهاد وهو قوله: {وآخرون يضربون في الأرض يبتغون من فضل الله وآخرون يقاتلون في سبيل الله} يريد: أنَّه خفف قيام اللَّيل لما علم من ثقله على هؤلاء {فاقرؤوا ما تيسر منه} قال المُفسِّرون: وكان هذا في صدر الإِسلام ثمَّ نُسخ بالصَّلوات الخمس وقوله: {وَمَا تُقَدِّمُوا لأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ الله هو خيراً وأعظم أجراً} مما خلقتم وتركتم {واستغفروا الله إن الله غفور} لذنوب المؤمنين {رحيم} بهم',NULL,'1146',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5497,'{يا أيها المدثر} أي: المتدثر في ثوبه',NULL,'1148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5498,'{قم فأنذر} النَّاس',NULL,'1148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5499,'{وربك فكبر} فصفه بالتَّعظيم',NULL,'1148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5500,'{وثيابك فطهر} لا تلبسها على معصيةٍ ولا على غدر فإنَّ الغادر والفاجر يُسمَّى دنس الثِّياب',NULL,'1148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5501,'{والرجز فاهجر} أي: الأوثان فاهجر عبادتها وكذلك كلَّ ما يؤدي إلى العذاب',NULL,'1148',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5502,'{ولا تمنن تستكثر} لا تعظ شيئاً لتأخذَ أكثر منه وهذا خاصة للنبي صلى الله عليه وسلم لأنَّه مأمورٌ بأجلِّ الأخلاق وأشرفِ الآداب',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5503,'{ولربك فاصبر} اصبر لله على أوامره ونواهيه وما يمتحنك به حتى يَكون هو الذي يُثيبك عليها',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5504,'{فإذا نقر في الناقور} نُفخ في الصُّور الآية وقوله:',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5505,'{فذلك يومئذ يوم عسير}',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5506,'{على الكافرين غير يسير}',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5507,'{ذرني ومن خلقت وحيداً} أَيْ: لا تهتمَّ لشأنه فإني أكفيك أمره أَي: الوليد بن المغيرة يقول: خلقته وحيداً لا ولد له ولا مال',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5508,'{وجعلت له مالاً ممدوداً} دائماً لا ينقطع عنه من الزَّرع والضَّرع والتّجارة',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5509,'{وبنين شهوداً} حضوراً معه بمكَّة وكانوا عشرةً',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5510,'{ومهدت له تمهيداً} بسطت له في العيش والمال بسطاً',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5511,'{ثم يطمع أن أزيد} يرجو أن أزيده مالاً وولداً',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5512,'{كلا} قطعٌ لرجائه {إنَّه كان لآياتنا عنيداً} للقرآنِ معانداُ غير مطيعٍ',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5513,'{سأرهقه صعوداً} سأغشيه مشقَّةً من العذاب',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5514,'{إنَّه فكر وقدَّر} وذلك أنَّ قريشاً سألته ما تقول في محمَّد؟ فتفكَّر في نفسه وقدَّر القول في محمد عليه السلام والقرآن ماذا يمكنه أن يقول فيهما',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5515,'{فقتل} لُعن وعُذِّب {كيف قدَّر} ؟ استفهامٌ على طريق التعجب',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5516,'{ثم قتل كيف قدر}',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5517,'{ثم نظر} {ثم عبس وبسر} كلح وجهه',NULL,'1149',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5518,'{ثم عبس وبسر}',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5519,'{ثمَّ أدبر واستكبر} عن الإِيمان',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5520,'{فقال إن هذا} ما هذا الذي يقرؤه محمد {إلاَّ سحرٌ يؤثر} يُروى عن السَّحرة',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5521,'{إن هذا إلاَّ قول البشر} كما قالوا: {إنَّما يُعلِّمه بشرٌ} قال الله تعالى',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5522,'{سأصليه سقر} سأُدخله جهنَّم ثمَّ أعلم عظم شأن سقر من العذاب فقال:',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5523,'{وما أدراك ما سقر} ما أعلمك أيُّ شيءٍ سقر!',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5524,'{لا تبقي ولا تذر}',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5525,'{لواحة للبشر} محرّقةٌ للجلد حتى تُسوِّده',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5526,'{عليها تسعة عشر} من الخزنة الواحد منهم يدفع بالدُّفعة الواحدة في جهنَّم أكثر من ربيعة ومضر فلمَّا نزلت هذه الآية قال بعض المشركين: أنا أكفيكم منهم سبعة عشر فاكفوني اثنين فأنزل الله:',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5527,'{وما جعلنا أصحاب النار إلاَّ ملائكة} لا رجالاً فمن ذا يغلب الملائكة؟ {وما جعلنا عدتهم} عددهم في القلَّة {إلاَّ فتنة للذين كفروا} لأنَّهم قالوا: ما أعون محمَّدٍ إلاَّ تسعة عشر {ليستيقن الذين أوتوا الكتاب} ليعلموا أنَّ ما أتى به النبي صَلَّى اللَّهُ عَلَيْهِ وسلم موافقٌ لما في كتبهم {ويزداد الذين آمنوا} لأنَّهم يُصدِّقون بما أتى به الرَّسول عليه السَّلام وبعدد خزنة النَّار {ولا يرتاب الذين أوتوا الكتاب والمؤمنون} أَيْ: لا يشكُّون في أنَّ عددهم على ما أخبر به محمد عليه السَّلام {وليقول الذين في قلوبهم مرض} شكٌّ {والكافرون: ماذا أراد الله بهذا مثلاً} أيُّ شيءٍ أراد الله بهذا العدد وتخصيصه؟ {كذلك} كما أضلَّهم الله بتكذيبهم {يضلُّ الله مَنْ يشاء ويهدي من يشاء وما يعلم جنود ربك إلاَّ هو} هذا جوابٌ لقولهم: ما أعوانه إلاَّ تسعة عشر {وما هي} أي: النَّار {إلاَّ ذكرى للبشر} أَيْ: إنَّها تُذكِّرهم في الدُّنيا النّار في الآخرة',NULL,'1150',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5528,'{كلا} ليس الأمر على ما ذكروا من التَّكذيب له {والقمرِ} قسمٌ',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5529,'{والليل إذ أدبر} جاء بعد النَّهار',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5530,'{والصبح إذا أسفر} أضاء',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5531,'{إنها لإِحدى الكبر} إنَّ سقر لإِِحدى الأمور العظام',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5532,'{نذيراً} إنذاراً {للبشر}',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5533,'{لمن شاء منكم أن يتقدَّم} فيما أُمِرَ به {أو يتأخر} عنه فقد أُنذرتم',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5534,'{كل نفس بما كسبت رهينةٌ} مأخوذةٌ بعملها',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5535,'{إلاَّ أصحاب اليمين} يعني: أهل الجنَّة فهم لا يزتهنون بذنوبهم ولكن الله يغفر لهم وقيل: أصحاب اليمين ها هنا أطفال المسلمين وقوله:',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5536,'{في جنات يتساءلون}',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5537,'{عن المجرمين}',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5538,'{ما سلككم في سقر} أَيْ: ما أدخلكم جهنَّم؟',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5539,'{قالوا لم نك من المصلين}',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5540,'{ولم نك نطعم المسكين}',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5541,'{وكنا نخوض مع الخائضين} ندخل الباطل مع مَنْ دخله',NULL,'1151',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5542,'{وكنا نكذب بيوم الدين} بيوم الجزاء',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5543,'{حتى أتانا اليقين} الموت',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5544,'{فما تنفعهم شفاعة الشافعين}',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5545,'{فما لهم عن التذكرة معرضين} ما لهم يُعرضون عن تذكيرك إيَّاهم',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5546,'{كأنهم حمر مستنفرة}',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5547,'{فرَّت من قسورة} أي: الأسد وقيل: الرُّماة الصَّيَّادون',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5548,'{بَلْ يُرِيدُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُؤْتَى صحفا منشرة} وذلك أنهم قالوا: إن سرَّك أن نتَّبعك فأت كلَّ واحدٍ منا بكتابٍ من ربِّ العالمين نؤمر فيه بابتاعك كما قالوا: {لن نُؤْمِنَ لِرُقِيِّكَ حَتَّى تُنَزِّلَ عَلَيْنَا كِتَابًا نَقْرَؤُهُ} الآية',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5549,'{كلا} ردٌّ لما قالوا {بل لا يخافون الآخرة} حيث يقترحون أن يُؤتوا صحفاً منشرة',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5550,'{كلا إنه تذكرة} إنَّ القرآن تذكيرٌ للخلق وليس بسحرٍ',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5551,'{فمن شاء ذكره}',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5552,'{وما يذكرون إلاَّ أن يشاء الله هو أهل التقوى} أهلٌ أن يُتَّقى عقابه {وأهل المغفرة} أهلٌ أنْ يعمل بما يُؤدِّي إلى مغفرته',NULL,'1152',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5553,'{لا أقسم} لا صلةٌ معناه: أقسم وقيل: لا ردٌّ لإِنكار المشركين البعث ثمّ قال: أقسم {بيوم القيامة}',NULL,'1153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5554,'{ولا أقسم بالنفس اللوامة} وهي نفس ابن آدم تلومه يوم القيامة إنْ كان عمل شرَّاً لِمَ عمله وإنْ كان عمل خيراً لأمته على ترك الاستكثار منه وجواب هذا القسم مضمرٌ على تقدير: إنَّكم مبعوثون ودلَّ عليه ما بعده من الكلام وهو قوله:',NULL,'1153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5555,'{أيحسب الإِنسان} أي: الكافر {ألن نجمع عظامه} للبعث والإِحياء بعد التَّفرقة والبلى!',NULL,'1153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5556,'{بلى قادرين} بلى نقدر على جمعها و {على أن نسوي بنانه} نجعله كخف البعير فلا يمكن أن يعمل بها شيئاً وقيل: نسوي بنائه على ما كانت وإنْ دقَّت عظامها وصغرت',NULL,'1153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5557,'{بل يريد الإِنسان ليفجر أمامه} يُؤخِّر التَّوبة ويمضي في معاصي الله تعالى قُدُماً قُدُماً فيقدّم الأعمال السَّيِّئة وقيل: معناه ليكفر بما قدَّامه يدلُّ على هذا قوله:',NULL,'1153',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5558,'{يسأل أيان} متى {يوم القيامة} تكذيباً به واستبعاداً لوقوعه',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5559,'{فإذا برق البصر} فزع وتحيَّر',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5560,'{وخسف القمر} أظلم وذهب ضوءه',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5561,'{وجمع الشمس والقمر} أَيْ: جُمعا في ذهاب نورهما',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5562,'{يقول الإنسان يومئذٍ أين المفر} أَي: الفرار؟',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5563,'{كلا} لا مفرَّ ذلك اليوم و {لا وزر} ولا ملجأ ولا حِرز',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5564,'{إلى ربك يومئذٍ المستقر} المنتهى والمصير',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5565,'{ينبأ الإِنسان} يُخبر {بما قدَّم وأخر} بأوَّل عمله وآخره',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5566,'{بل الإِنسان على نفسه بصيرة} أَيْ: شاهدٌ عليها بعملها يشهد عليه جوارحه وأُدخلت الهاء في البصيرة للمبالغة وقيل: لأنَّه أراد بالإِنسان الجوارح',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5567,'{ولو ألقى معاذيره} ولو اعتذر وجادل فعليه من نفسه من يُكذِّب عذره وقيل: معناه: ولو أرخى السُّتور وأغلق الأبواب والمِعذار: الستر بلغة اليمن',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5568,'{لا تحرّك به} بالوحي {لسانك لتعجل به} كان جبريل عليه السَّلام إذا نزل بالقرآن تلاه النبيُّ صلى الله عليه وسلم قبل فراغ جبريل كراهيةَ أن ينفلت منه فأعلم الله تعالى أنه لا ينسبه إيَّاه وأنَّه يجمعه في قلبه فقال:',NULL,'1154',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5569,'{إنَّ علينا جمعه وقرآنه} قراءته عليك حتى تعيه',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5570,'{فإذا قرأناه فاتبع قرآنه} أَي: لا تعجل بالتِّلاوة إلى أن يقرأ عليك',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5571,'{ثم إنَّ علينا بيانه} أَيْ: علينا أن ننزِّله قرآناً فيه بيانٌ للنَّاس',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5572,'{كلا} زجرٌ وتنبيهٌ {بل تحبون العاجلة}',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5573,'{وتذرون الآخرة} أي: تختارون الدُّنيا على العقبى',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5574,'{وجوهٌ يومئذٍ} يوم القيامة {ناضرة} مُضِيئةٌ حسنةٌ',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5575,'{إلى ربها ناظرة} تنظر إلى خالقها عياناً',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5576,'{ووجوه يومئذ باسرة} كالحةٌ',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5577,'{تظن} توقن {أن يفعل بها فاقرة} داهيةٌ عظيمةٌ من العذاب',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5578,'{كلا إذا بلغت التراقي} يعني: النَّفس بلغت عظام الحلق',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5579,'{وقيل من راق} قال مَنْ حضر ذلك الذي قارب الموت: هل من طبيبٍ يداويه وراقٍ يرقيه فيشفى برقيته؟',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5580,'{وظن} أيقن الذي نزل به الموت {أنَّه الفراق} من الدُّنيا والأهل والمال',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5581,'{والتفت الساق بالساق} التفَّت ساقاه لشدَّة النَّزع وقيل: تتابعت عليه الشَّدائد',NULL,'1155',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5582,'{إلى ربك يومئذ المساق} المنتهى والمرجع بسوق الملائكة الرُّوح إلى حيث أمر الله سبحانه',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5583,'{فلا صدَّق ولا صلى} يعني: أبا جهلٍ لعنه الله',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5584,'{ولكن كذب وتولى} عن الإِيمان',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5585,'{ثمَّ ذهب إلى أهله يتمطى} يتبختر',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5586,'{أولى لك فأولى} {ثم أولى لك فأولى} هذا تهديدٌ ووعيدٌ له والمعنى: وليك المكروه يا أبا جهل أي: لومك المكروه',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5587,'{ثم أولى لك فأولى}',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5588,'{أيحسب الإنسان أن يترك سدى} مُهملاً غير مأمورٍ ولا منهيٍّ',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5589,'{ألم يك نطفة من مني يمنى} يصبُّ في الرَّحم',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5590,'{ثمَّ كان علقة فخلق فسوى} فخلقه الله فسوَّى خلقه حتى صار إنساناً بعد أن كان علقةً',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5591,'{فجعل منه الزوجين الذكر والأنثى} فخلق من الإِنسان صنفين الرَّجل والمرأة',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5592,'{أليس ذلك} الذي فعل هذا {بقادر على أن يحيي الموتى} ؟ بلى وهو على كل شيء قدير',NULL,'1156',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5593,'{هل أتى على الإنسان} قد أتى على آدم {حين من الدَّهر} أربعون سنةً {لم يكن شيئاً مذكوراً} لأنَّه كان جسداً مُصوَّراً من طينٍ لا يُذكر ولا يُعرف ويجوز أن يريد جميع النَّاس لأنَّ كلَّ أحدٍ يكون عدماً إلى أَنْ يصير شيئاً مذكوراً',NULL,'1257',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5594,'{إنا خلقنا الإِنسان} يعني: ابن آدم {من نطفة أمشاج} أخلاطٍ يعني: ماء الرَّجل وماء المرأة واختلاف ألوانهما {نبتليه فجعلناه سميعاً بصيرا} أي: خلقناه كلذلك لنختبره بالتَّكليف والأمر والنَّهي',NULL,'1157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5595,'{إنَّا هديناه السبيل} بيَّنا له الطَّريق {إمَّا شاكراً وإمَّا كفوراً} إنْ شكر أو كفر يعني: أعذرنا إليه في بيان الطَّريق يبعث الرَّسول آمن أو كفر',NULL,'1157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5596,'{إنا أعتدنا للكافرين سلاسل وأغلالا وسعيرا}',NULL,'1157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5597,'{إنَّ الأبرار} المُطيعين لربِّهم {يشربون من كأس} إناءٍ فيه شرابٌ {كان مزاجها كافوراً} يُمزج لهم بالكافور',NULL,'1157',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5598,'{عيناً} من عينٍ {يشرب بها} بتلك العين {عباد الله يفجرونها تفجيراً} يقودونها حيث شاؤوا من منازلهم',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5599,'{يوفون بالنذر} إذا نذروا في طاعة الله وفوا به {ويخافون يوماً كان شرُّه مستطيراً} منتشراً فاشياً',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5600,'{ويطعمون الطعام على حبّه} على قلَّته وحبِّهم إيَّاه {مسكيناً} فقيراً {ويتيماً} لا أب له {وأسيراً} أي: المملوك والمحبوس في حقٍّ من المسلمين ويقولون لهم:',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5601,'{إنما نطعمكم لوجه الله} لطلب ثواب الله {لا نريد منكم} بما نُطعمكم {جزاء} مكافأة منكم {ولا شكوراً} شكراً',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5602,'{إنا نخاف من ربنا يوماً عبوساً} كريه المنظر لشدَّته {قمطريراً} صعباً شديداً طويل الشَّر',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5603,'{فوقاهم الله شرَّ ذلك اليوم} الذي يخافون {ولقَّاهم نضرة} ضياءً في وجوههم {وسروراً} في قلوبهم',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5604,'{وجزاهم بما صبروا} على طاعة الله وعن معصيته {جنة وحريراً}',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5605,'{متكئين فيها على الأرائك لا يرون فيها شمساً ولا زمهريراً} حرَّاً ولا برداً صيفاً ولا شتاءً',NULL,'1158',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5606,'{ودانية عليهم ظلالُها} أَيْ: قريبة منهم ضلال أشجارها {وذللت قطوفها تذليلاً} أُدنيت منهم ثمارها فهم ينالونها قعوداً كانوا أو قياماً',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5607,'{ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا} أَيْ: لها بياض الفضَّة وصفاء القوارير وهو قوله:',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5608,'{قوارير من فضة قدروها تقديراً} أي: جُعلت الأكواب على قدر رِيِّهِمْ وهو ألد الشَّراب',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5609,'{ويسقون فيها كأساً كان مزاجها زنجبيلاً} والزَّنجبيل: شيءٌ تستلذُّه العرب فوعدهم الله ذلك في الجنَّة',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5610,'{عيناً} من عينٍ {فيها} في الجنَّة {تسمى} تلك العين {سلسبيلاً}',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5611,'{ويطوف عليهم ولدان} أي: غلمانٌ {مخلَّدون} لا يشيبون {إذا رأيتهم حسبتهم} في بياضهم وصفاء ألوانهم {لؤلؤاً منثوراً}',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5612,'{وإذا رأيت ثمَّ} إذا رميت ببصرك في الجنَّة {رأيت نعيماً وملكاً كبيراً} وهو أنَّ أدناهم منزلاً ينظر في ملكه في مسيرة ألف عامٍ',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5613,'{عاليهم} فوقهم {ثياب سندس} أي: الحرير وقوله: {شراباً طهوراً} طاهراً من الأقذاء والأقذار وليس بنجس كخمر الدنيا وقوله:',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5614,'{إن هذا كان لكم جزاء وكان سعيكم مشكورا}',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5615,'{إنا نحن نزلنا عليك القرآن تنزيلا}',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5616,'{ولا تطع منهم آثماً} يعني: عتبة بن ربيعة {أو كفوراً} يعني: الوليد بن المغيرة وذلك أنَّهما ضمنا للنبي صلى الله عليه وسلم المال والتَّزويج إِنْ ترك دعوتهم إلى الإسلام',NULL,'1159',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5617,'{واذكر اسم ربك بكرة وأصيلا}',NULL,'1160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5618,'{ومن الليل فاسجد له وسبحه ليلا طويلا}',NULL,'1160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5619,'{إنَّ هؤلاء يحبُّون العاجلة} يعني: الدُّنيا {ويذرون وراءهم يوماً ثقيلاً} ويتركون العمل ليومٍ شديدٍ أمامهم وهو يوم القيامة',NULL,'1160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5620,'{نحن خلقناهم وشددنا أسرهم} خلقهم وخلق مفاصلهم',NULL,'1160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5621,'{إنَّ هذه} السُّورة {تذكرة} تذكيرٌ للخلق {فمن شاء اتخذ إلى ربه سبيلاً} وسيلةً بالطَّاعة',NULL,'1160',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5622,'{وما تشاؤون إلاَّ أن يشاء الله} أَيْ: لستم تشاؤون شيئاً إلاَّ بمشيئة الله تعالى لأنَّ الأمر إليه',NULL,'1160',NULL,NULL,'0');
