INSERT INTO [b2547] VALUES (5711,'{يوم يقوم الروح} قيل: هو جبريل عليه السَّلام وقيل: هو مَلَكٌ يقوم صفاً وقيل: الرُّوح جندٌ من جنود الله ليسوا من الملائكة ولا من النَّاس يقومون {والملائكة صفاً} صفوفاً {لا يتكلمون إلاَّ من أذن له الرحمن وقال صواباً} حقاً في الدُّنيا يعني: لا إله إلاَّ الله',NULL,'1168',NULL,NULL,'0');
