INSERT INTO [b2547] VALUES (5848,'{ثم ما أدراك ما يوم الدين}',NULL,'1181',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5849,'{يوم لا تملك نفس لنفس شيئاً} لا تملك أن تُنجيها من العذاب {والأمر يومئذٍ لله} وحده لم يملك أحدٌ أمراً في ذلك اليوم كما ملك في الدُّنيا',NULL,'1181',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5850,'{ويل للمطففين} يعني: الذين يبخسون حقوق النَّاس في الكيل والوزن',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5851,'{الذين إذا اكتالوا} أخذوا بالكيل {على الناس} من النَّاس {يستوفون} يأخذون حقوقهم تامَّة وافيةً',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5852,'{وإذا كالوهم} كالوا لهم {أو وزنوهم} وزنوا لهم {يخسرون} ينقصون',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5853,'{ألا يظن أولئك} ألا يستيقن أولئك الذين يفعلون ذلك {أنهم مبعوثون}',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5854,'{ليوم عظيم} يعني: يوم القيامة',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5855,'{يوم يقوم الناس} من قبورهم {لربِّ العالمين} والمعنى أنَّهم لو أيقنوا بالبعث ما فعلوا ذلك',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5856,'{كلا} ردع وزجر أي: ليس الأمر على ما هم عليه فليرتدعوا {إنَّ كتاب الفجار} الذي فيه أعمالهم مرقومٌ مكتوبٌ مثبتٌ عليهم في {سجين} في أسفل سبع أرضين وهو محل إبليس وجنده',NULL,'1182',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5857,'{وما أدراك ما سجين} أي: ليس ذلك ممَّا كنتَ تعلمه أنت ولا قومُك وقوله:',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5858,'{كتاب مرقوم} فمؤخَّرٌ معناه التَّقديم لأنَّ التَّقدير كما ذكرنا: إنَّ كتاب الفجَّار كتابٌ مرقومٌ في سجِّين وقوله:',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5859,'{ويل يومئذ للمكذبين}',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5860,'{الذين يكذبون بيوم الدين}',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5861,'{وما يكذب به إلا كل معتد أثيم}',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5862,'{إذا تتلى عليه آياتنا قال أساطير الأولين}',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5863,'{كلا بل ران على قلوبهم} أي: غلب عليها حتى غمرها وغشيها {ما كانوا يكسبون} من المعاصي وهو كالصَّدأ يغشى القلب',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5864,'{كلا إنهم عن ربهم يومئذ لمحجوبون} يحجبون عن الله تعالى فلا يرونه',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5865,'{ثم إنهم لصالوا الجحيم} لداخلوا النَّار',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5866,'{ثمَّ يقال هذا} العذاب {الذي كنتم به تكذبون} في الدُّنيا',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5867,'{كلا إنَّ كتاب الأبرار لفي عليين} في السَّماء السَّابعة تحت العرش',NULL,'1183',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5868,'{وما أدراك} وما الذي أعلمك يا محمد {ما عليون} كيف هي وأيُّ شيءٍ صفتها',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5869,'{كتاب مرقوم} يعني: كتاب الأبرار كتابٌ مرقومٌ',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5870,'{يشهده المقربون} تحضره الملائكة لأنَّ عليين محلُّ الملائكة وقوله:',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5871,'{إن الأبرار لفي نعيم}',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5872,'{على الأرائك ينظرون} أَيْ: إلى ما أعطاهم الله سبحانه من النَّعيم والكرامة',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5873,'{تعرف في وجوههم نَضْرَةَ النعيم} أي: غضارته وبريقه',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5874,'{يسقون من رحيق} وهو الخمر الصَّافية {مختوم}',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5875,'{ختامه مسك} يعني: إذا فني ما في الكأس وانقطع الشَّراب يختم ذلك الشَّراب برائحة المسك {وفي ذلك فليتنافس المتنافسون} فليرغب الرَّاغبون بالمبادرة إلى طاعة الله عزَّ وجل',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5876,'{ومزاجه} ومزاج ذلك الشَّراب {من تسنيم} وهو عينُ ماءٍ تجري في جنَّة عدنٍ وهي أعلى الجنَّات ثمَّ فسَّره فقال:',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5877,'{عيناً يشرب بها المقربون} أَيْ: يشربها المُقرَّبون',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5878,'{إنَّ الذين أجرموا} أشركوا يعني: أبا جهلٍ وأصحابه {كانوا من الذين آمنوا} من فقراء المؤمنين {يضحكون} استهزاءً بهم',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5879,'{وإذا مروا بهم يتغامزون} يغمز بعضهم بعضاً ويشيرون إليهم',NULL,'1184',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5880,'{وإذا انقلبوا} رجعوا {إلى أهلهم} أصحابهم وذويهم {انقلبوا فكهين} مُعجبين بما هم فيه يتفكَّهون بذكر المؤمنين',NULL,'1185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5881,'{وإذا رأوهم} رأوا المؤمنين {قالوا إنَّ هؤلاء لضالون}',NULL,'1185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5882,'{وما أرسلوا} يعني: الكفَّار {عليهم} على المؤمنين {حافظين} لأعمالهم موكلين بأموالهم',NULL,'1185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5883,'{فاليوم} يعني: يوم القيامة {الذين آمنوا من الكفار يضحكون} كما ضحكوا منهم في الدُّنيا',NULL,'1185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5884,'{على الأرائك ينظرون} إليهم كيف يُعذَّبون',NULL,'1185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5885,'{هل ثوِّب الكفار ما كانوا يفعلون} أي: هل جُوزوا بسخريتهم بالمؤمنين في الدُّنيا؟',NULL,'1185',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5886,'{إذا السماء انشقت} تنشقُّ السَّماء يوم القيامة',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5887,'{وأذنت لربها} سمعت أمر ربِّها بالانشقاق {وحقت} وحقَّ لها أن تطيع',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5888,'{وإذا الأرض مدَّت} من أطرافها فَزِيد فيها كما يمدُّ الأديم',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5889,'{وألقت ما فيها} ما في بطنها من الموتى والكنوز {وتخلَّت} وخَلَتْ منها',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5890,'{وأذنت لربها وحقت}',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5891,'{يا أيها الإنسان إنك كادح إلى ربك كدحاً} عاملٌ لربِّك عملاً {فملاقيه} فملاقٍ عملك والمعنى: إذا كان يوم القيامة لقي الإنسان عمله',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5892,'{فأمَّا مَنْ أوتي كتابه بيمينه}',NULL,'1186',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5893,'{فسوف يحاسب حساباً يسيراً} وهو العرض على الله عز وجل لأنَّ مَنْ نُوقش الحساب عذاب',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5894,'{وينقلب إلى أهله} في الجنَّة {مسروراً}',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5895,'{وأمَّا مَنْ أوتي كتابه وراء ظهره} وذلك أنَّ يديه غُلَّتا إلى عنقه فيُؤتى كتابه بشماله من وراء ظهره',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5896,'{فسوف يدعو ثبوراً} فينادي بالهلاك على نفسه',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5897,'{ويصلى سعيراً} ويدخل النَّار',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5898,'{إنَّه كان في أهله} في الدُّنيا {مسروراً} متابعاً لهواه',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5899,'{إنَّه ظنَّ أن لن يحور} لن يرجع إلى ربِّه',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5900,'{بلى} أيْ: ليس الأمر كما ظنَّ يرجع إلى ربِّه',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5901,'{فلا أقسم} معناه فأقسم {بالشفق} وهو الحمرة التي تُرى بعد سقوط الشَّمس وقيل: يعني: اللَّيل والنَّهار',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5902,'{والليل وما وسق} جمع وحمل وضمَّ وآوى من الدواب والحشرات والهرام والسباع وكلّ شيء دخل عليه اللَّيل',NULL,'1187',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5903,'{والقمر إذا اتسق} اجتمع واسترى',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5904,'{لتركبنَّ طبقاً عن طبق} حالا بعد حال ومن النُّطفة وإلى العلقة وإلى الهرم والموت حتى يصيروا إلى الله تعالى وقوله:',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5905,'{فما لهم لا يؤمنون}',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5906,'{وإذا قرئ عليهم القرآن لا يسجدون}',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5907,'{بل الذين كفروا يكذبون}',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5908,'{والله أعلم بما يوعون} أي: يحملون في قلوبهم ويُضمرون',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5909,'{فبشرهم} أخبرهم {بعذاب أليم} وقوله:',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5910,'{غير ممنون} أي: غير منقوصٍ ولا مقطوعٍ',NULL,'1188',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5911,'{والسماء ذات البروج} يعني: بروج الكواكب وهي اثنا عشر برجاً',NULL,'1189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5912,'{واليوم الموعود} يوم القيامة',NULL,'1189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5913,'{وشاهد} يوم الجمعة {ومشهود} يعني: يوم عرفة',NULL,'1189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5914,'{قتل} لُعن {أصحاب الأخدود} وهو الشَّقُّ يحفر في الأرض طولاً وهم قومٌ كفرةٌ كانوا يعبدون الصنم وكان قومٌ من المؤمنين بين أظهرهم يكتمون إيمانهم فاطَّلعوا على ذلك منهم فشقُّوا أخدوداً في الأرض وملؤوه ناراً وعرضوهم على النَّار فمن لم يرجع عن دينه قذفوه فيها',NULL,'1189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5915,'{النار ذات الوقود} ذات الالتهاب',NULL,'1189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5916,'{إذ هم عليها قعود} وذلك أنَّهم قعدوا عند تلك النَّار',NULL,'1189',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5917,'{وهم على ما يفعلون بالمؤمنين} من التَّعذيب والصَّدِّ عن الإِيمان {شهود} حاضرون أخبر الله تعالى عن قصَّة قومٍ بلغت بصيرتهم في إيمانهم إلى أن صبروا على أَنْ أُحرقوا بالنَّار في الله',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5918,'{وما نقموا منهم إلاَّ أن يؤمنوا بالله العزيز الحميد} أَيْ: ما أنكروا عليهم ذنباً إلاَّ إيمانهم',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5919,'{الذي له ملك السماوات والأرض والله على كل شيء شهيد}',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5920,'{إنَّ الذين فتنوا} أَيْ: أحرقوا {المؤمنين والمؤمنات ثمَّ لم يتوبوا} لم يرجعوا عن كفرهم {فلهم عذاب جهنم} بكفرهم {ولهم عذاب الحريق} بما أحرقوا المؤمنين',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5921,'{إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ تجري من تحتها الأنهار ذلك الفوز الكبير}',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5922,'{إنَّ بطش ربك} أخذه بالعذاب {لشديد}',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5923,'{إنه هو يبدئ} الخلق يخلقهم ابتداءً ثمَّ يُعيدهم عند البعث',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5924,'{وهو الغفور الودود} المحبُّ أولياءه',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5925,'{ذو العرش} خالقه ومالكه {المجيد} المستحقُّ لكمال صفات العلو والمدح',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5926,'{فعال لما يريد}',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5927,'{هل أتاك حديث الجنود} خبر الجموع الكافرة ثمَّ بيَّن مَنْ هم فقال:',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5928,'{فرعون وثمود}',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5929,'{بل الذين كفروا} من قومك {في تكذيب} كذبٍ لك',NULL,'1190',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5930,'{والله من ورائهم محيط} قدرته مشتملةٌ عليهم فلا يعجزه عنهم أحدٌ',NULL,'1191',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5931,'{بل هو قرآن مجيد} كثير الخير وليس كما زعم المشركون',NULL,'1191',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5932,'{في لوح محفوظ} من أن يبدِّل ما فيه أو يُغيِّر',NULL,'1191',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5933,'{والسماء والطارق} يعني: النُّجوم كلَّها لأنَّ طلوعها باللَّيل وكلُّ ما أتى ليلاً فهو طارق فقد فسَّر الله تعالى ذلك بقوله:',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5934,'{وما أدراك ما الطارق}',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5935,'{النجم الثاقب} المضيء النَّيِّرُ',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5936,'{إن كلُّ نفسٍ لما عليها} لَعَليها و {ما} صلة {حافظ} من ربِّها يحفظ عملها',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5937,'{فَلْيَنْظُرِ الإِنسان ممَّ خلق} من أي شيء خلقه ربُّه ثمَّ بيَّن فقال:',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5938,'{خلق من ماءٍ دافق} مدفوقٍ مصبوبٍ في الرَّحم يعني: النُّطفة',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5939,'{يخرج من بين الصلب} وهو ماء الرَّجل {والترائب} عظام الصَّدر وهو ماء المرأة',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5940,'{إنَّه} إنَّ الله {على رجعه} على بعث الإِنسان وإِعادته بعد الموت {لقادر}',NULL,'1192',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5941,'{يوم تبلى السرائر} يعني: يوم القيامة وفي ذلك اليوم تختبر السَّرائر وهي الفرائضُ التي هي سرائر بين العبد وربِّه كالصَّلاة والصَّوم وغسل الجنابة ولو شاء العبد أن يقول: فعلت ذلك ولم يفعله أمكنه فهي سرائر عند العبد وإنما تبين وتظهر صحَّتها وأمانة العبد فيها يوم القيامة',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5942,'{فما له} يعني: الإِنسان الكافر {من قوة ولا ناصر}',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5943,'{والسماء ذات الرجع} أَيْ: المطر',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5944,'{والأرض ذات الصدع} تتشقَّق عن النبات',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5945,'{وإنه} أَيْ: القرآن {لقول فصل} يفصل بين الحقّ والباطل',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5946,'{وما هو بالهزل} أَيْ: باللَّعب والباطل',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5947,'{إنهم} يعني: مشركي مكَّة {يكيدون كيداً} يُظهرون للنبيِّ صلى الله عليه وسلم على ما هم على خلافه',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5948,'{وأكيد كيدا} وهو استدراج الله تعالى إيَّاهم من حيث لا يعلمون {فمهِّل الكافرين أمهلهم رويداً} يقول: أخِّرهم قليلاً فإني آخذهم بالعذاب فأُخذوا يوم بدرٍ وذلك أنَّه كان يدعو الله تعالى عليهم فقال الله تعالى: {أمهلهم رويداً} أي: قليلا',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5949,'{فمهِّل الكافرين أمهلهم رويداً}',NULL,'1193',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5950,'{سبِّح اسم ربك الأعلى} نزِّه ذات ربِّك من السُّوء وقيل: معناه: قل: سبحان ربِّي الأعلى',NULL,'1194',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5951,'{الذي خلق فسوَّى} خلق الإنسان مُستوي الخلق',NULL,'1194',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5952,'{والذي قدَّر فهدى} قدَّر الأرزاق ثمَّ هدى لطلبها',NULL,'1194',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5953,'{والذي أخرج} من الأرض {المرعى} النَّبات',NULL,'1194',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5954,'{فجعله غثاء} يابساً وهو ما يحمله السَّيل ممَّا يجف من النَّبات {أحوى} أسود بالياً',NULL,'1194',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5955,'{سنقرئك} سنجعلك قارئاً لما يأتيك به جبريل عليه السَّلام من الوحي {فلا تنسى} شيئاً وهذا وعدٌ من الله سبحانه لنبيِّه عليه السَّلام أن يحفظ عليه الوحي حتى لا ينفلت منه شيءٌ',NULL,'1194',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5956,'{إلاَّ ما شاء الله} أن ينسخه وقيل: إلاَّ ما شاء الله وهو لا يشاء أن تنسى {إنَّه يعلم الجهر} من القول والفعل {وما يخفى}',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5957,'{ونيسِّرك لليسرى} أَيْ: نُهوِّن عليك الشَّريعة اليسرى وهي الحنيفيَّة السَّمحة',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5958,'{فذكر} فَعِظْ بالقرآن {إن نفعت الذكرى} التَّذكير',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5959,'{سيذكر} سيتَّعظ {من يخشى} الله',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5960,'{ويتجنبها} ويتجنب الذكرى ويباعد عنها {الأشقى} في علم الله',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5961,'{الذي يصلى النار الكبرى} الذي يدخل جهنَّم',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5962,'{ثمَّ لا يموت فيها ولا يحيا} لا يموت فيها موتاً يستريح به من العذاب ولا يحيا حياةً يجد فيها روح الحياة',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5963,'{قد أفلح} صادف البقاء في الجنَّة {مَنْ تزكَّى} أكثر من العمل الصالح',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5964,'{وذكر اسم ربه فصلى} أَيْ: الصَّلوات الخمس',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5965,'{بل تؤثرون} تختارون {الحياة الدنيا}',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5966,'{والآخرة خير وأبقى} من الدُّنيا',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5967,'{إن هذا} الذي ذكرت من فلاح المُتزكِّي وكون الآخرة خيراً من الدُّنيا {لفي الصحف الأولى} مذكورٌ في الكتب المتقدِّمة',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5968,'{صحف إبراهيم وموسى} يعني: ما أنزل الله عليهما من الكتب',NULL,'1195',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5969,'{هل أتاك حديث الغاشية} يعني: القيامة لأنَّها تغشى الخلق ومعنى: {هل أتاك} أَيْ: إنَّ هذا لم يكن من علمك ولا من علم قومك',NULL,'1196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5970,'{وجوه يومئذٍ خاشعة} ذليلةٌ',NULL,'1196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5971,'{عاملة} في النار تعالج حرَّها وعذابها {ناصبة} ذات نصبٍ وتعبٍ',NULL,'1196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5972,'{تصلى ناراً} تقاسي حرَّها {حامية} حارَّةً',NULL,'1196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5973,'{تسقى من عين آنية} متناهيةٍ في الحرارة',NULL,'1196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5974,'{ليس لهم} في جهنم {طعام إلاَّ من ضريع} وهو يبيس الشِّبْرِقِ وهو نوعٌ من الشَّوك لا تقربه دابَّةٌ ولا ترعاه وصفته ما ذكر الله: {لا يسمن ولا يغني من جوع}',NULL,'1196',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5975,'{لا يسمن ولا يغني من جوع}',NULL,'1196',NULL,NULL,'0');
