INSERT INTO [b2547] VALUES (5983,'{ونمارق مصفوفة} أَيْ: وسائد بعضها بجنب بعضٍ',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5984,'{وزرابيُّ} وهي البسط والطَّنافس {مبثوثة} مفرَّقة في المجالس ثمَّ نبَّههم على عظيمٍ من خلقه قد ذلَّله لصغير ليدلَّهم بذلك على توحيده فقال:',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5985,'{أفلا ينظرون إلى الإِبل كيف خلقت} وقوله:',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5986,'{وإلى السماء كيف رفعت}',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5987,'{وإلى الجبال كيف نصبت}',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5988,'{سطحت} أَيْ: بُسطت',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5989,'{فذكِّر إنما أنت مذكِّر} ذكِّرهم نعم الله ودلائل توحيده فإنَّك مبعوثٌ بذلك',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5990,'{لست عليهم بمصيطر} بمسلِّط تُكرههم على الإِيمان وهذا قبل أن أمر بالحرب',NULL,'1197',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5991,'{إلاَّ من تولى} لكنْ من تولَّى عن الإيمان {وكفر}',NULL,'1198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5992,'{فيعذِّبه الله العذاب الأكبر} عذاب جهنم',NULL,'1198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5993,'{إنَّ إلينا إيابهم} رجوعهم',NULL,'1198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5994,'{ثمَّ إنَّ علينا حسابهم}',NULL,'1198',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5995,'{والفجر} يعني: فجر كلِّ يومٍ',NULL,'1199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5996,'{وليالٍ عشر} عشر ذي الحجَّة',NULL,'1199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5997,'{والشفع} يعني: يوم النَّحر لأنَّه يوم العاشر {والوتر} يوم عرفة لأنَّه يوم التَّاسع',NULL,'1199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5998,'{والليل إذا يسر} يعني: ليل المزدلفة إذا مضى وذهب وقيل: إذا جاء وأقبل',NULL,'1199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (5999,'{هل في ذلك} الذي ذكرت {قَسَمٌ لذي حجر} أَيْ: مقنعٌ ومكتفى في القسم لذي عقلٍ ثمَّ ذكر الأمم التي كذَّبت الرُّسل كيف أهلكهم فقال:',NULL,'1199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6000,'{ألم تر كيف فعل ربك بعاد}',NULL,'1199',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6001,'{إرم} يعني: عاداً الأولى وهو عاد بن عوص بن إرم وإرم: اسم القبيلة {ذات العماد} أَيْ: ذات الطُّول وقيل: ذات البناء الرفيع وقيل: ذات العمد السيَّارة وذلك أنَّهم كانوا أهل عمدٍ سيَّارة ينتجعون الغيث',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6002,'{التي لم يخلق مثلها في البلاد} في بطشهم وقوَّتهم وطول قامتهم',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6003,'{وثمود الذين جابوا} قطعوا {الصخر} فاتَّخذوا منها البيوت {بالواد} يعني: وادي القرى وكانت مساكنهم هناك',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6004,'{وفرعون ذي الأوتاد} ذي الجنود والجموع الكثيرة وكانت لهم مضارب كثيرةٌ يوتدونها في أسفارهم وقوله:',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6005,'{الذين طغوا في البلاد}',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6006,'{فأكثروا فيها الفساد}',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6007,'{فصبَّ عليهم ربك سوط عذاب} أَيْ: جعل سوطه الذي ضربهم به العذاب',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6008,'{إنَّ ربك} جواب القسم الذي في أوَّل السُّورة {لبالمرصاد} بحيث يرى ويسمع ويرصد أعمال بني آدم',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6009,'{فأمَّا الإنسان} يعني: الكافر {إذا ما ابتلاه ربُّه} امتحنه بالنِّعمة والسَّعة {فأكرمه} بالمال {ونعَّمة} بما وسَّع عليه {فيقول ربي أكرمنِ} لا يرى الكرامة من الله إلاَّ بكثرة الحظِّ من الدُّنيا',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6010,'{وإمَّا إذا ما ابتلاه فقدر} فضيَّق {عليه رزقه فيقول: ربي أهانن} يرى الهوان في قلَّة حظِّه من الدنيا وهذا صفة الكافر فأما المؤمن فالكرامة عنده أن يُكرمه الله بطاعته والهوان أن يُهينه بمعصيته ثم رَدَّ هذا على الكافر فقال:',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6011,'{كلا} أي: ليس الأمر كما يظنُّ هذا الكافر {بل لا تكرمون اليتيم} إخبارٌ عمَّا كانوا يفعلونه من ترك اليتيم وحرمانه ما يستحقُّ من الميراث',NULL,'1200',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6012,'{ولا تَحَاضُّون على طعام المسكين} لا تأمرون به ولاتعينون عليه',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6013,'{وتأكلون التراث} يعني: ميراث اليتامى {أكلاً لمّاً} شديداً تجمعون المال كلَّه في الأكل فلا تُعطون اليتيم نصبه',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6014,'{وتحبون المال حباً جماً} كثيراً',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6015,'{كلا} ما هكذا ينبغي أن يكون الأمر {إذا دكت الأرض دكاً دكاً} إذا زُلزلت الأرض فكَسر بعضها بعضاً',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6016,'{وجاء ربك} أَيْ: أمر ربِّك وقضاؤه {والملك} أَيْ: الملائكةُ {صفاً صفاً} صفوفاً',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6017,'{وجيء يومئذٍ بجهنم} تُقاد بسبعين أَلْفِ زمامٍ كلُّ زمامٍ بأيدي سبعين ألف مَلَكٍ {يومئذٍ يتذكَّر الإنسان} يُظهر الكافر التَّوبة {وأنى له الذكرى} ومن أين له التَّوبة؟',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6018,'{يقول يا ليتني قدمت لحياتي} أَيْ: للدَّار الآخرة التي لا موت فيها',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6019,'{فيومئذٍ لا يعذِّب عذابه أحد} لا يتولَّى عذاب الله تعالى يومئذٍ أحدٌ والأمر يومئذ أمره ولا أمر غيره',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6020,'{ولا يوثق وثاقه} يعني: بالوثاق الإِسار والسًَّلاسل والأغلال والمعنى: لا يبلغ أحدٌ من الخلق كبلاغ الله سبحانه في التَّعذيب والإِيثاق',NULL,'1201',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6021,'{يا أيتها النفس المطمئنة} إلى ما وعد الله سبحانه المصدِّقة بذاك',NULL,'1202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6022,'{ارجعي إلى ربك} يقال لها ذلك عند الموت {راضية} بما آتاها الله {مرضية} رضي الله عنها ربُّها هذا عند خروجها من الدُّنيا فإذا كان يوم القيامة قيل:',NULL,'1202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6023,'{فادخلي في عبادي} أَيْ: في جملة عبادي الصَّالحين',NULL,'1202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6024,'{وادخلي جنتي}',NULL,'1202',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6025,'{لا أقسم} المعنى: أقسم و {لا} توكيدٌ {بهذا البلد} يعني: مكَّة',NULL,'1203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6026,'{وأنت} يا محمَّدُ {حلٌّ بهذا البلد} تصنع فيه ما تريد ما القتل والأسر وأحلت له مكَّةُ ساعةً من النَّهار يوم الفتح حتى قاتل وقتل من شاء',NULL,'1203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6027,'{ووالدٍ} أقسم بآدم عليه السَّلام {وما ولد} وولده و {ما} بمعنى مَنْ',NULL,'1203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6028,'{لقد خلقنا الإِنسان في كبد} أَيْ: مشقَّةٍ يكابد أمر الدنيا والآخرة وشدائدها وقيل: مُنتصباً معتدلاً',NULL,'1203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6029,'{أيحسب أن لن يقدر عليه أحد} نزلت في رجلٍ من بني جمح يُكنى أبا الأشدين كان يوصف بالقوَّة فقال الله تعالى: أيحسب بقوَّته أن لن يقدر عليه أحد والله قادر عليه',NULL,'1203',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6030,'{يقول أهلكت مالاً} على عداوة محمد صلى الله عليه وسلم {لبداً} كثيراً بعضه على بعض كاذبٌ في ذلك قال الله تعالى:',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6031,'{أيحسب أن لم يره أحد} في إنفاقه فيعلم مقدار نفقته ثمَّ ذكر ما يستدلُّ به على أنَّ الله تعالى قادرٌ عليه وأَنْ يحصي عليه ما يعمله فقال:',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6032,'{ألم نجعل له عينين}',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6033,'{ولساناً وشفتين}',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6034,'{وهديناه النجدين} يقول: ألم نُعرِّفه طريق الخير وطريق الشَّرِّ',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6035,'{فلا اقتحم العقبة} أَيْ: لم يدخل العقبة وهذا مثل ضربه الله تعالى للمنفق في طاعة الله يحتاج أن يتحمَّل الكُلفة كمَنْ يتكلَّف صعود العقبة يقول: لم ينفق هذا الإنسان في طاعة الله شيئاً',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6036,'{وما أدراك ما العقبة} أَيْ: ما اقتحام العقبة ثمَّ فسَّره فقال:',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6037,'{فك رقبة} وهو إخراجها من الرِّقِّ بالعون في ثمنها',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6038,'{أو إطعامٌ في يومٍ ذي مسغبة} مجاعةٍ',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6039,'{يتيماً ذا مقربة} ذا قرابةٍ',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6040,'{أو مسكيناً ذا متربة} أَيْ: ذا فقرٍ قد لصق من فقره بالتُّراب',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6041,'{ثم كان من الذين آمنوا} أَيْ: كان مقتحم العقبة وفاكُّ الرَّقبة والمُطعم من الذين آمنوا فإنَّه إنْ لم يكن منهم لم ينفعه قربةٌ {وتواصوا} أوصى بعضهم بعضاً {بالصبر} على طاعة الله تعالى {وتواصوا بالمرحمة} بالرَّحمة على الخلق',NULL,'1204',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6042,'{أولئك أصحاب الميمنة} مَنْ كان بهذه الصفة فهو من جملة أصحاب اليمين',NULL,'1205',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6043,'{والذين كفروا بآياتنا هم أصحاب المشأمة} أصحاب الشِّمال وقيل في أصحاب اليمين: إنَّهم الميامين على أنفسهم وفي أصحاب المشأمة: إنَّهم المشائيم على أنفسهم',NULL,'1205',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6044,'{عليهم نار مؤصدة} مُطَبقةٌ',NULL,'1205',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6045,'{والشمس وضحاها} وضيائها',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6046,'{والقمر إذا تلاها} تبعها في الضِّياء والنُّور وذلك في النِّصف الأوَّل من الشَّهر يخلف الشمس والقمر في النُّور',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6047,'{والنهار إذا جلاَّها} جلَّى الظُّلمة وكشفها وقيل: جلَّى الشَّمس وبيَّنها لأنها تبين إذا انبسط النَّهار',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6048,'{والليل إذا يغشاها} يستر الشَّمس',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6049,'{والسماء وما بناها} أَيْ: وبنائها',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6050,'{والأرض وما طحاها} وطحوها أَيْ: بسطها',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6051,'{ونفس وما سوَّاها} وتسوية خلقها',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6052,'{فألهمها فجورها وتقواها} علَّمها الطَّاعة والمعصية',NULL,'1206',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6053,'{قد أفلح} سعد {مَنْ زكاها} أصلح الله نفسه وطهَّرها من الذُّنوب',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6054,'{وقد خاب مَنْ دسَّاها} جعلها الله ذليلةً خسيسةً حتى عملت بالفجور ومعنى دسَّاها: أخفى محلها ووضع منها وأحملها وخذلها',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6055,'{كذبت ثمود بطغواها} بطغيانها كذَّبت الرُّسل',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6056,'{إذِ انبعث} قام {أشقاها} عاقر النَّاقة',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6057,'{فقال لهم رسول الله} صالحٌ {ناقة الله} ذَروا ناقة الله {وسقياها} وشربها في يومها',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6058,'{فكذَّبوه فعقروها} فقتلوا النَّاقة {فدمدم عليهم ربهم} أهلكهم هلاك استئصال {بذنبهم فسوَّاها} سوى الدمدمة عليهم فعمَّهم بها وقيل: سوَّى ثمود بالهلاك فأنزله بصغيرها وكبيرها',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6059,'{ولا يخاف عقباها} لا يخاف اللَّهُ من أحدٍ تبعةَ ما أنزل بهم وقيل: لا يخاف أشقاها عاقبة جنايته',NULL,'1207',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6060,'{والليل إذا يغشى} أيْ: يغشى الأُفق بظلمته',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6061,'{والنهار إذا تجلى} بان وظهر',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6062,'{وما خلق} ومَنْ خلق {الذكر والأنثى} وهو الله تعالى وجواب القسم وهو قوله:',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6063,'{إنَّ سعيكم لشتى} إنَّ عملكم لمختلفٌ يريد: بينهما بُعدٌ يعني: عمل المؤمن وعمل الكافر نزلت في أبي بكر الصِّديق وأبي سفيان بن حرب',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6064,'{فأمَّا مَنْ أعطى} ماله {واتقى} ربَّه واجتنب محارمه',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6065,'{وصدَّق بالحسنى} أيقن بأنَّ الله سبحانه سيخلف عليه وقيل: صدَّق بـ لا إله إلاَّ الله',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6066,'{فسنيسره} فسنهيِّئه {لليسرى} للخلَّة اليسرى أَي: الأمر السَّهل من العمل بما يُرضي الله تعالى وكان أبو بكر الصديق رضي الله عنه اشترى جماعةً يُعذِّبُهم المشركون ليرتدُّوا عن الإِسلام فوصفه الله تعالى بأنَّه أعطى وصدَّق بالمُجازاة من الله له',NULL,'1208',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6067,'{وأمَّا مَنْ بخل} بالنَّفقة في الخير {واستغنى} عن الله فلم يرغب في ثوابه',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6068,'{وكذب بالحسنى}',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6069,'{فسنيسره للعسرى} أَيْ: نخذله حتى يعمل بما يُؤدِّيه إلى العذاب والأمر العسير',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6070,'{وما يغني عنه ماله إذا تردَّى} أَيْ: مات وهلك وقيل: سقط في جهنَّم',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6071,'{إنَّ علينا للهدى} أَي: إِنَّ علينا أَّنْ نبيِّن طريق الهدى من طريق الضَّلال',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6072,'{وإن لنا للآخرة والأولى} فمن طلبهما من غير مالكهما فقد أخطأ',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6073,'{فأنذرتكم} خوَّفتكم {ناراَ تلظى} تتوقَّد',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6074,'{لا يصلاها إلاَّ الأشقى} لا يدخلها إلاَّ الكافر {الذي كذب وتولى}',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6075,'{الذي كذَّب وتولَّى}',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6076,'{وسيجنبها} أَيْ: يبعد منها {الأتقى} يعني: أبا بكر رضوان الله عليه',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6077,'{الذي يؤتي ماله يتزكى} يطلب أن يكون عند الله زاكياً ولا يطلب رياءً ولا سمعةً',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6078,'{وما لأحد عنده من نعمة تجزى} وذلك أنَّ الكفَّار قالوا لمَّا اشترى أبو بكر رضي الله عنه بلالاً فأعتقه: ما فعل أبو بكر ذلك إلاَّ ليدٍ كانت عنده لبلال فقال الله تعالى: وما لأحد عنده من نعمة تُجزى أَيْ: لم يفعل ذلك مجازاة ليدٍ أُسديت إليه',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6079,'{إلاَّ ابتغاء وجه ربه الأعلى} أي: لكن طلب ثواب الله',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6080,'{ولسوف يرضى} سيدخل الجنَّة',NULL,'1209',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6081,'{والضحى} أيْ: النَّهارِ كلِّه',NULL,'1210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6082,'{والليل إذا سجى} سكن بالخلق واستقرَّ بظلامه',NULL,'1210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6083,'{ما ودَّعك ربك وما قلى} وما تركك منذ اختارك وما أبغضك منذ أحبَّك وهذا جواب القسم وقد كان تأخَّر الوحي عن النبي صلى الله عليه وسلم خمسة عشر يوماً فقال ناس: إنَّ محمداً ودَّعه ربُّه وقلاه فأنزل الله هذه السورة',NULL,'1210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6084,'{وللآخرة خير لك من الأولى} لأّنَّ الله يعطيك فيها الكرامات والدَّرجات',NULL,'1210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6085,'{ولسوف يعطيك ربك} في الآخرة من الثَّواب وفي مقام الشَّفاعة {فترضى} يروى أنَّه قال عليه السَّلام لمَّا نزلت هذه الآية: إذن لا أرضى وواحدٌ من أُمَّتي في النَّار ثمَّ أخبر عن حاله قبل الوحي وذكَّره نعمه عليه فقال:',NULL,'1210',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6086,'{ألم يجدك يتيماً} حين مات أبوك ولم يُخلِّفا لك مالاً ولا مأوى {فآوى} فآواك إلى عمِّك أبي طالب وضمَّك إليه حتى كفلك وربَّاك',NULL,'1211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6087,'{ووجدك ضالاً} عمَّا أنت عليه اليوم من معالم النُّبوَّة وأحكام القرآن والشَّريعة فهداك إليها كقوله: {ما كنت تدري ما الكتاب ولا الإيمانُ} الآية',NULL,'1211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6088,'{ووجدك عائلا} فقيرا لا مال لك فأغناك بمال خديجة رضي الله عنه ثمَّ بالغنائم',NULL,'1211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6089,'{فأما اليتيم فلا تقهر} على ماله واذكر يُتمك',NULL,'1211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6090,'{وأما السائل فلا تنهر} فلا تزجره ولكن بذلٌ يسير أو رد جميل واذكر فقرك',NULL,'1211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6091,'{وأمَّا بنعمة ربك} أَيْ: النُّبوَّة والقرآن {فحدِّث} أخبر بها',NULL,'1211',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6092,'{ألم نشرح لك صدرك} أَلم نفتحْ ونوسِّع ونليِّن لك قلبك بالإِيمان والنُّبوَّة والعلم والحكمة؟ هذا استفهامٌ معناه التَّقرير',NULL,'1212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6093,'{ووضعنا} حططنا {عنك وزرك} ما سلف منك في الجاهليَّة وقيل: يعني: الخطأ والسَّهو وقيل: معناه: خفَّفنا عليك أعباء النُّبوَّة والوِزر في اللُّغة: الحِمل الثقيل',NULL,'1212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6094,'{الذي أنقض} أثقل {ظهرك}',NULL,'1212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6095,'{ورفعنا لك ذكرك} أي: إذا ذُكرت ذكرتَ معي',NULL,'1212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6096,'{فإنَّ مع العسر يسراً} أي: مع الشِّدَّة التي أنتَ فيها من مقاساة بلاء المشركين يُسراً بإظهاري إيَّاك عليهم حتى تغلبهم وينقادوا لك طوعاً أو كرهاً',NULL,'1212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6097,'{إنَّ مع العسر يسراً} تكرارٌ للتَّأكيد وقيل: إنَّ هذا عامٌّ في كلِّ عسرٍ أصاب المؤمن وهو من الله تعالى على وعد اليسر إمَّا في الدنيا وأما في الآخرة فالعسر واحدٌ واليسر اثنان',NULL,'1212',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6098,'{فإذا فرغت} من صلاتك {فانصب} أَي: اتعب في الدُّعاء وسله حاجتك وارغب إلى الله تعالى به',NULL,'1213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6099,'{وإلى ربك فارغب}',NULL,'1213',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6100,'{والتين والزيتون} هما جبلان بالشَّام طور تينا وطور زيتا بالسِّريانية سمِّيا بالتِّين والزَّيتون لأنَّهما يُنبتانهما',NULL,'1214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6101,'{وطور سينين} جبل موسى عليه السَّلام وسينين: المبارك بالسِّريانية',NULL,'1214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6102,'{وهذا البلد الأمين} الآمن يعني: مكَّة سمَّاه أميناً لأنه آمنٌ لا يُهاج أهله',NULL,'1214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6103,'{لقد خلقنا الإِنسان في أحسن تقويم} صورةٍ لأنَّه معتدل القامة يتناول مأكوله بيده',NULL,'1214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6104,'{ثم رددناه أسفل سافلين} إلى أرذل العمر والسَّافلون: هم الهرمى والزَّمنى والضَّعفى',NULL,'1214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6105,'{إِلا الَّذِينَ آمَنُوا وعملوا الصالحات فلهم أجرٌ غير ممنون} يعني: إن المؤمن ردَّ إلى أرذل العمر كُتب له مثل أجره إذا كان يعمل بخلاف الكافر فذلك قوله: {فلهم أجرٌ غير ممنون} أي: غير مقطوعٍ وقيل: معنى: {ثم رددناه أسفل سافلين} : إلى النَّار يعني: الكافر ثمَّ استثنى المؤمنين فقال: {إلاَّ الذين آمنوا} وهذا القول أظهر ثمَّ قال توبيخاً للكافر:',NULL,'1214',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6106,'{فما يكذبك} أيُّها الإِنسان {بعد} هذه الحُجَّة {بالدين} بالحساب والجزاء ومعنى: ما يُكذِّبك: ما الذي يجعلك مكذِّباً بالدِّين وقيل: إنَّ هذا خطاب للنبي صلى الله عليه وسلم فما الذي يكذِّبك يا محمد بعد ما تبيَّن متن قدرتنا على خلق الإِنسان وظهر من حجَّتنا كأنَّه قال: فمَنْ يقدر على تكذيبك بالثَّواب والعقاب',NULL,'1215',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6107,'{أليس الله بأحكم الحاكمين} في جميع ما خلق وصنع وكلُّ ذلك دالٌّ على علمه وحكمته جلَّ جلاله وتقدَّست أسماؤه ولا إله غيره',NULL,'1215',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6108,'{أقرأ باسم ربك} يعني: اقرأ القرآن باسم ربك وهو أن تذكر التَّسمية في ابتداء كلِّ سورةٍ {الذي خلق} الأشياء والمخلوقات',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6109,'{خلق الإنسان} يعني: ابن آدم {من علق} جمع عَلَقةٍ',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6110,'{اقرأ وربك الأكرم} يعني: الحليم عن جهل العباد فلا يعجل عليهم بالعقوبة',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6111,'{الذي علَّم بالقلم} ثمَّ بيَّن ما علَّم فقال:',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6112,'{علَّم الإنسان ما لم يعلم} وهو الخطُّ والكتابة',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6113,'{كلا} حقَّاً {إنَّ الإِنسان ليطغى} ليتجاوز حده ويستكبر على ربِّه',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6114,'{أن رآه} رأى نفسه {استغنى}',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6115,'{إنَّ إلى ربك الرجعى} المرجع في الآخرة فيجازي الطَّاغي بما يستحقُّه',NULL,'1216',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6116,'{أرأيت الذي ينهى} يعني: أبا جهلٍ',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6117,'{عبداً إذا صلى} وذلك أنَّه قال: لئن رأيتُ محمدا لأطأنَّ على رقبته ومعنى: أرأيتَ ها هنا تعجُّبٌ وكذلك قوله:',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6118,'{أرأيت إن كان على الهدى} {أو أمر بالتقوى}',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6119,'{أو أمر بالتقوى}',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6120,'{أرأيت إن كذب وتولى} والمعنى: أرأيت الذي ينهى عبداً إذا صلَّى وهو على الهدى آمرٌ بالتَّقوى والنَّاهي كاذبٌ مُتولٍّ عن الذِّكرى أَيْ: فما أعجب من ذا!',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6121,'{ألم يعلم} أبو جهلٍ {بأنَّ الله يرى} أَيْ: يراه ويعلم ما يفعله',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6122,'{كلا} ردعٌ وزجرٌ {لئن لم ينته} عمَّا هو عليه من الكفر ومعاداة النبي صلى الله عليه وسلم {لنسفعا بالناصية} لنجرَّن بناصيته إلى النَّار ثمَّ وصف ناصيته فقال:',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6123,'{ناصيةٍ كاذبة خاطئة} وتأويلها: صاحبها كاذب خاطئ',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6124,'{فليدع ناديه} فليستعن بأهل مجلسه وذلك أنَّه قال لرسول الله صلى الله عليه وسلم: لأملأنَّ عليك هذا الوادي خيلا جرداء ورجالاً مُرداً فقال الله تعالى: {فليدع ناديه}',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6125,'{سندع الزبانية} وهم الملائكة الغلاط الشِّداد قال رسول الله صلى الله عليه وسلم: لو دعا ناديه لأخذته الزَّبانية عياناً',NULL,'1217',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6126,'{كلا} ليس الأمر على ما عليه أبو جهلٍ {لا تطعه واسجد} وصلِّ {واقترب} تقرّب إلى ربِّك بطاعته',NULL,'1218',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6127,'{إنا أنزلناه} أي: أنزلنا القرآن {في ليلة القدر} ليلة الحكم والفصل يقضي الله فيها قضاء السَّنة والقَدْر: بمعنى التَّقدير أنزل الله تعالى القرآن كلَّه في ليلة القدر جُملةً واحدةً من اللَّوح المحفوظ إلى سماء الدُّنيا ثمَّ به جبريل عليه السَّلام على النبي صلى الله عليه والسلام في عشرين سنةً',NULL,'1219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6128,'{وما أدراك} يا محمَّد عليه السَّلام {ما ليلة القدر} على التَّعظيم لشأنها والتعجب منها ثمَّ أخبر عنها فقال:',NULL,'1219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6129,'{ليلة القدر خير من ألف شهر} أي: من ألف شهرٍ ليس فيها ليلة القدر',NULL,'1219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6130,'{تنزل الملائكة والروح} يعني: جبريل عليه السَّلام {فيها} في تلك اللَّيلة {بإذن ربهم من كل أمر} أي: بكلِّ أمرٍ قضاه الله تعالى في تلك اللَّيلة للسَّنة وتمَّ الكلام ها هنا ثمَّ قال:',NULL,'1219',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6131,'{سلام هي} أَيْ: تلك اللَّيلة كلها سلامةٌ وخيرٌ لا داء فيها ولا يستطيع الشَّيطان أن يصنع فيها شيئاً وقيل: يعني: تسليم الملائكة في تلك اللَّيلة على أهل المساجد {حتى مطلع الفجر} إلى وقت طلوع الفجر',NULL,'1220',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6132,'{لم يكن الذين كفروا} بمحمد صلى الله عليه وسلم {من أهل الكتاب} أي: اليهود والنَّصارى {والمشركين} يعني: كفَّار العرب {منفكين} مُنتهين زائلين عن كفرهم {حتى تأتيهم البينة} يعني: أتتم البينة أَي: البيان والبصيرة وهو محمد عليه السلام والقرآن يقول: لم يتركوا كفرهم حتى بُعث إليهم محمَّدٌ عليه السَّلام وهذا فيمَنْ آمن من الفريقين ثمَّ فسَّر البيِّنة فقال:',NULL,'1221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6133,'{رسول من الله يتلو صحفاً} كتباً {مطهرة} من الباطل',NULL,'1221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6134,'{فيها كتب} أحكامٌ {قيِّمة} مستقيمةٌ عادلةٌ ثمَّ ذكر كفَّار أهل الكتاب فقال:',NULL,'1221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6135,'{وما تفرَّق الذين أوتوا الكتاب} أي: ما اختلفوا في كون محمَّدٍ عليه السَّلام حقاً لما يجدون من نعته في كتابهم {إلا من بعد ما جاءتهم البينة} إلاَّ من بعد ما بيَّنوا أنَّه النبيُّ الذي وُعدوا به في التَّوراة والإِنجيل يريد: أنَّهم كانوا مجتمعين على صحَّة نبوَّته فلمَّا بُعث جحدوا نبوَّته وتفرَّقوا فمنهم مَنْ كفر بغياً وحسداً ومنهم مَنْ آمن وهذا كقوله تعالى: {وَمَا اخْتَلَفَ الَّذِينَ أُوتُوا الْكِتَابَ إِلا مِنْ بعد ما جاءهم العلم بغياً بينهم} الآية',NULL,'1221',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6136,'{وما أمروا} يعني: كفَّار الذين أوتوا الكتاب {إلا ليعبدوا الله} إلاَّ أنْ يعبدوا الله {مخلصين له الدين} الطَّاعة أَيْ: مُوحِّدين له لا يعبدون معه غيره {حنفاء} على دين إبراهيم عليه السَّلام ودين محمد صلى الله عليه وسلم وقوله: {وذلك دين القيمة} أي: دين الملَّة القيِّمة وهي المستقيمة وباقي الآية ظاهر',NULL,'1222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6137,'{إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها أولئك هم شر البرية}',NULL,'1222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6138,'{إنَّ الذين آمنوا وعملوا الصالحات أولئك هم خير البرية}',NULL,'1222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6139,'{جزاؤهم عند ربهم جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا رضي الله عنهم ورضوا عنه ذلك لمن خشي ربه}',NULL,'1222',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6140,'{إذا زلزلت الأرض زلزالها} أي: حُرِّكت حركةً شديدةً لقيام السَّاعة',NULL,'1223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6141,'{وأخرجت الأرض أثقالها} كنوزها وموتاها فألقتها على ظهرها',NULL,'1223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6142,'{وقال الإِنسان} يعني: الكافر الذي لا يؤمن بالبعث: {ما لها} إنكاراً لتلك الحالة',NULL,'1223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6143,'{يومئذ تحدّث أخبارها} أَيْ: تُخبر بما عُمل عليها من خيرٍ وشرٍّ',NULL,'1223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6144,'{بأنَّ ربك أوحى لها} أي: أمرها بالكلام وأذن لها فيه',NULL,'1223',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6145,'{يومئذ يصدر الناس} ينصرف النَّاس {أشتاتاً} متفرِّقين عن موقف الحساب فآخذٌ ذات اليمين وآخذٌ ذات الشِّمال {ليروا أعمالهم} أَيْ: ثوابها',NULL,'1224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6146,'{فمن يعمل مثقال ذرة خيراً يره} يرى المؤمن ثوابه في الآخرة والكافر في الدُّنيا يراه في نفسه وأهله وماله',NULL,'1224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6147,'{ومَنْ يعمل مثقال ذرة شراً يره} جزاء المؤمن في الدُّنيا بالأحزان والمصائب والكافر في الآخرة',NULL,'1224',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6148,'{والعاديات} يعني: الخيل في الغزو {ضبحاً} تضبح ضبحاً وهو صوت أجوافها إذا عدت',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6149,'{فالموريات} وهي الخيل التي تُوري النَّار {قدحاً} بحوافرها إذا عدت في الأرض ذات الحجارة باللَّيل',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6150,'{فالمغيرات صبحاً} يعني: الخيل تُغير على العدوِّ وقت الصبح وإنما يُغير أصحابها ولكن جرى الكلام على الخيل',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6151,'{فأثرن} هجين {به} بمكان عدوها {نقعاً} غباراً',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6152,'{فوسطن} توسطن {به} بالمكان الذي هي به {جمعاً} من النَّاس أغارت عليهم يريد: صارت في وسط قومٍ من العدوِّ تُغير عليهم',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6153,'{إن الإنسان} جواب القسم {لربه لكنود} لكفورٌ يعني: الكافر يجحد نعم الله تعالى',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6154,'{وإنه} وإنَّ الله تعالى {على ذلك} على كنوده {لشهيد}',NULL,'1225',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6155,'{وإنّه لحب الخير} لأجل حبِّ المال {لشديد} لبخيلٌ',NULL,'1226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6156,'{أفلا يعلم} هذا الإنسان {إذا بعثر} قُلب فَأُثير {ما في القبور} يعني: إذا بُعث الموتى',NULL,'1226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6157,'{وحصِّل} بيِّن وأُبرز {ما في الصدور} من الكفر والإيمان',NULL,'1226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6158,'{إنَّ ربهم بهم يومئذٍ لخبير} عالمٌ فيجازيهم على كفرهم في ذلك اليوم وإنَّما قال بهم لأنَّ الإِنسان اسم الجنس',NULL,'1226',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6159,'{القارعة} يعني: القيامة لأنَّها تقرع القلوب بأهوالها',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6160,'{ما القارعة} تفخيمٌ لشأنها وتهويلٌ كما قلنا في الحاقة',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6161,'{وما أدراك ما القارعة}',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6162,'{يوم يكون الناس كالفراش} كغوغاء الجراد لا يتَّجه إلى جهةٍ واحدةٍ كذلك النَّاس إذا بُعثوا ماج بعضهم في بعضٍ للحيرة {المبثوث} المفرَّق',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6163,'{وتكون الجبال كالعهن} كالصُّوف {المنفوش} المندوف لخفَّة سيرها',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6164,'{فأمَّا مَنْ ثقلت موازينه} بالحسنات',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6165,'{فهو في عيشة راضية} يرضاها',NULL,'1227',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6166,'{وأما من خفت موازينه} {فأمه هاوية} فمسكنه النَّار',NULL,'1228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6167,'{فأمه هاوية} فمسكنه النَّار',NULL,'1228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6168,'{وما أدراك ما هيه} ثمَّ فسرها فقال:',NULL,'1228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6169,'{نار حامية} شديدة الحرارة',NULL,'1228',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6170,'{ألهاكم التكاثر}',NULL,'1229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6171,'{حتى زرتم المقابر} شغلكم التَّكاثر بالأموال والأولاد والعدد عن طاعة الله تعالى: {حتى زرتم المقابر} : حتى أدرككم الموت على تلك الحالة نزلت في اليهود قالوا: نحن أكثرُ من بني فلانٍ وبنو فلان أكثرُ من بني فلانٍ ألهاهم ذلك حتى ماتوا ضُلالاً',NULL,'1229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6172,'{كلا} ليس الأمر الذي ينبغي أَنْ تكونوا عليه التَّكاثر {سوف تعلمون} عند النَّزع سوء عاقبة ما كنتم عليه',NULL,'1229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6173,'{ثمَّ كلا سوف تعلمون} سوء عاقبة ما كنتم عليه في القبر والتكوير لتأكيد التَّهديد',NULL,'1229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6174,'{كلا لو تعلمون علم اليقين} أَيْ: لو علمتم الأمرَ حقَّ علمه لشغلكم ذلك عمَّا أنتم فيه وجواب {لو} محذوف ثمَّ ابتدأ فقال:',NULL,'1229',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6175,'{لترون الجحيم}',NULL,'1230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6176,'{ثم لترونها} تأكيدٌ أيضاً {عَيْنَ اليقين} عياناً لستم عنها بغائبين',NULL,'1230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6177,'{ثمَّ لتسألنَّ يومئذ عن النعيم} عن الأمن والصَّحة فيما أفنيتموها',NULL,'1230',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6178,'{والعصر} هو الدَّهر أَقسم الله به',NULL,'1231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6179,'{إن الإنسان} يعني: الكافر العامل لغير طاعة الله {لفي خسر} خسرانٍ يعني: إنَّه يخسر أهله ومحله ومنزلته في الجنَّة',NULL,'1231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6180,'{إلاَّ الذين آمنوا} فإنَّهم ليسوا في خسرٍ {وتواصوا بالحق} وصى بعضهم الإقامة على التَّوحيد والإِيمان {وتواصوا بالصبر} على طاعة الله والجهاد في سبيله ويروى مرفوعاً: {إن الإنسان لفي خسرٍ} يعني: أبا جهلٍ {إلاَّ الذين آمنوا} يعني: أبا بكر {وعملوا الصالحات} يعني: عمر بن الخطاب {وتواصوا بالحقِّ} يعني: عثمان {وتواصوا بالصَّبر} يعني: علياً رضي الله عنهم أجمعين',NULL,'1231',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6181,'{ويلٌ لكلِّ همزة لمزة} يعني: الإنسان الذي يغتاب النَّاس ويعيبهم نزلت في أُميَّة بن خلف وقيل: في الوليد بن المغيرة وكان يغتاب النبي صلى الله عليه وسلم',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6182,'{الذي جمع مالاً وعدده} أعدَّه للدَّهر وقيل: أكثر عدده',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6183,'{يحسب أنَّ ماله أخلده} في الدُنيا حتى لا يموت',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6184,'{كلا} ليس الأمر على ما يحسب {لينبذنَّ في الحطمة} ليطرحنَّ في النار وقوله:',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6185,'{وما أدراك ما الحطمة}',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6186,'{نار الله الموقدة}',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6187,'{التي تطلع على الأفئدة} أَيْ: يبلغ ألمها وإحراقها إلى الأفئدة',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6188,'{إنها عليهم مؤصدة} مطبقةٌ',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6189,'{في عمد} جمع عمودٍ {ممددة} قيل: يعني: أوتاد الأطباق التي تطبق عليهم ومعنى {في عمدٍ} : بعمدٍ وقيل: إنَّها عمدٌ يُعذَّبون بها في النَّار',NULL,'1232',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6190,'{ألم تر} ألم تعلم وقيل: ألم تخبر {كيف فعل ربك بأصحاب الفيل}',NULL,'1233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6191,'{ألم يجعل كيدهم في تضليل} أضلَّ كيدهم عمَّا أرادوا من تخريب الكعبة',NULL,'1233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6192,'{وأرسل عليهم طيراً أبابيل} جماعاتٍ جماعاتٍ',NULL,'1233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6193,'{ترميهم بحجارة من سجيل} من آجرٍ',NULL,'1233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6194,'{فجعلهم كعصف مأكول} كزرعٍ أكلته الدَّوابُّ فداسته وفتَّتته والعصف: ورق الزرع',NULL,'1233',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6195,'{لإِيلاف قريش} قيل: هذه اللام تتَّصل بما قبلها على معنى: أهلك الله أصحاب الفيل لتبقى قريش وتألف رحلتيها وقيل: معنى اللام التَّأخير على معنى: ليعبدوا ربَّ هذا البيت {لإِيلاف قريش} أَيْ: ليجعلوا عبادتهم شكراً لهذه النِّعم واعترافاً بها يقال: ألف الشَّيء وآلفه بمعنىً واحد والمعنى: لإيلاف قريش رحلتيها وذلك انَّه كانت لهم رحلتان رحلةٌ في الشتاء إلى اليمن ورحلة في الصَّيف إلى الشَّام وبهما كانت تقوم معايشهم وتجاراتهم وكان لا يتعرَّض لهم في تجارتهم أحدٌ يقول: هم سكَّان حرم الله وولاة بيته فمنَّ الله عليهم بذلك وقال:',NULL,'1234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6196,'{إيلافهم رحلة الشتاء والصيف}',NULL,'1234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6197,'{فليعبدوا ربَّ هذا البيت}',NULL,'1234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6198,'{الذي أطعمهم من جوع} أَيْ: بعد جوعٍ وكانوا قد أصابتهم شدة حتى أكلوا الميتة والجيف ثمَّ كشف الله ذلك عنهم {وآمنهم من خوف} فلا يخافون في الحرم الغارة ولا يخافون في رحلتهم',NULL,'1234',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6199,'{أرأيت الذي يكذب بالدين} نزلت في العاص بن وائل وقيل: نزلت في الوليد بن المغيرة وقيل: في أبي سفيان وذلك أنه جزوراً فأتاه يتيمٌ يسأله فقرعه بعصاه فذلك قوله تعالى: {يدعُّ اليتيم} أَيْ: يدفعه بجفوةٍ من حقِّه',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6200,'{فذلك الذي يدع اليتيم}',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6201,'{ولا يحض على طعام المسكين} لا يُطعم المسكين ولا يأمر بإطعامه',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6202,'{فويل للمصلين}',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6203,'{الذين هم عن صلاتهم ساهون} غافلون يُؤخِّرونها عن وقتها',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6204,'{الذين هم يراؤون} يعني: المنافقين يُصلُّون في العلانيَة ويتركون الصَّلاة في السِّرِّ',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6205,'{ويمنعون الماعون} الزَّكاة وما فيه منفعةٌ من الفأس والقِدر والماء والملح',NULL,'1235',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6206,'{إنَّا أعطيناك الكوثر} قيل: هو نهرٌ في الجنَّة حافتاه الدُّرُّ وقيل: هو الخير الكثير',NULL,'1236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6207,'{فصلِّ لربك} صلاة العيد يعني: يوم النَّحر {وانحر} نُسكك وقيل: {فصلِّ} : فضع يدك على نحرك في صلاتك',NULL,'1236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6208,'{إنَّ شانئك} مُبغضك {هو الأبتر} المُنقطع العقب وقيل: المنقطع عن كلِّ خير نزلت في العاص بن وائل سمى النبي صلى الله عليه وسلم أبتر عند موت ابنه القاسم',NULL,'1236',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6209,'{قل يا أيها الكافرون} نزلت في رهطٍ من قريش قالوا للنبي صلى الله عليه وسلم تعبد آلهتنا سنةً ونعبد إلهك سنةً فأنزل الله هذه السُّورة',NULL,'1237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6210,'{لا أعبد ما تعبدون} في الحال',NULL,'1237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6211,'{ولا أنتم عابدون ما أعبد} في الحال ما أعبده',NULL,'1237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6212,'{ولا أنا عابد} في الاستقبال {ما عبدتم}',NULL,'1237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6213,'{ولا أنتم عابدون} في الاستقبال {ما أعبد} فنفى عنهم عبادة الله في الحال وفيما يستقبل وهذا في قومٍ أعلمه الله أنَّهم لا يؤمنون ونفى أيضاً عن نفسه عبادة الأصنام في الحال وفيما يستقبل ولييئسوا في ذلك',NULL,'1237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6214,'{لكم دينكم} الشِّرك {ولي دين} الإسلام وهذا قبل أن يُؤمر بالحرب',NULL,'1237',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6215,'{إذا جاء نصر الله} إيَّاك على مَنْ ناوأك من اليهود والعرب {والفتح} يعني: فتح مكة',NULL,'1238',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6216,'{ورأيت الناس يدخلون في دين الله أفواجا} جماعاتٍ جماعاتٍ بعد ما كان يدخل واحدٌ فواحدٌ وكان رسول الله صلى الله عليه وسلم لمَّا نزلت هذه السورة قال: قد نُعِيَتْ إليَّ نفسي',NULL,'1238',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6217,'{فسبح بحمد ربك} أمره الله عز وجل أن يُكثر التَّسبيح والاستغفار ليختم له في آخر عمره بالزِّيادة في العمل الصّالح',NULL,'1238',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6218,'{تبت يدا أبي لهب وتب} لمَّا نزل قوله: {وَأَنْذِرْ عَشِيرَتَكَ الأَقْرَبِينَ} صَعِدَ رَسُولُ اللَّهِ صَلَّى الله عليه وسلم الصَّفا ونادى بأعلى صوته يدعو قومه فاجتمعوا إليه فأنذرهم النَّار وقال: إنِّي نَذِيرٌ لَكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيدٍ فَقَالَ أبو لهب: تبَّاً لك ما دعوتنا إلاَّ لهذا فأنزل الله: {تبت يدا أبي لهب} أَيْ: خابت وخسرت {وتب} وخسر هو ولمَّا خوفه النبي صلى الله عليه وسلم بالعذاب قال: إنَّه إنْ كان ما يقوله ابن أخي حقاً فإني أفتدي منه بمالي وولدي فقال الله تعالى',NULL,'1239',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6219,'{ما أغنى عنه ماله وما كسب} يعني: ولده',NULL,'1239',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6220,'{سيصلى ناراً ذات لهب}',NULL,'1239',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6221,'{وامرأته حَمّالةَ الحطب} نقَّالة الحديث الماشية بالنَّميمة وهي أمُّ جميلٍ أخت أبي سفيان',NULL,'1240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6222,'{في جيدها} في عنقها {حبل من مسد} سلسلةٌ من حديدٍ ذرعها سبعون ذراعاً تدخل في فيها وتخرج من دبرها ويلوى سائرها في عنقها والمسد: كلُّ ما أُحكم به الحبل',NULL,'1240',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6223,'روي أنَّ قوماً من المشركين قال لرسول الله صلى عليه وسلم: انسب لنا ربَّك فأنزل الله عز وجل:
INSERT INTO [b2547] VALUES (6224,'{الله الصمد} السَّيِّد الذي قد انتهى إليه السودد وقيل: الصَّمد: الذي لا جوف لَه ولا يأكل ولا يشرب وقيل: هو المقصود إليه في الرَّغائب',NULL,'1241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6225,'{لم يلد ولم يولد}',NULL,'1241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6226,'{ولم يكن له كفواً أحد} لم يكن أحدٌ مثلاً له',NULL,'1241',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6227,'{قل أعوذ برب الفلق} نزلت هذا السُّورة والتي بعدها لمَّا سحر لبيدُ بن الأعصم اليهودي رسول الله صلى الله عليه وسلم فاشتكى شكوى شديدةً فأعلمه الله بما سحر به وأين هو فبعث مَنْ أتى به وكان وَتَراً فيه إحدى عشرة عقدةً فجعلوا كلما حلُّوا عقدةً وجد راحة حتى حلو العقد كلَّها وأمره الله أن يتعوَّذ بهاتين السُّورتين وهما إحدى عشرة آية على عدد العقد قوله: {برب الفلق} يعني: الصُّبح',NULL,'1242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6228,'{من شر ما خلق}',NULL,'1242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6229,'{ومن شر غاسق} يعني: اللَّيل {إذا وقب} دخل',NULL,'1242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6230,'{ومن شر النفاثات} يعني: السَّواحر تنفث {في العقد} كأنَّها تنفخ فيها بشيءٍ تقرؤه',NULL,'1242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6231,'{ومن شرِّ حاسد إذا حسد} يعني: لبيداً الذي سحره',NULL,'1242',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6232,'{قل أعوذ برب الناس}',NULL,'1243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6233,'{ملك الناس}',NULL,'1243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6234,'{إله الناس}',NULL,'1243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6235,'{من شرِّ الوسواس} يعني: ذا الوسواس وهو الشيطان {الخناس} وهو الذي يخنس ويرجع إذا ذُكر الله والشَّيطان جاثمٌ على قلب الإِنسان فإذا ذَكر الله تنحَّى وخنس وإذا غفل التقم قبله فحدثه ومناه وهو وقوله:',NULL,'1243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6236,'{الذي يوسوس في صدور الناس}',NULL,'1243',NULL,NULL,'0');
INSERT INTO [b2547] VALUES (6237,'{من الجِنَّة} أَيْ: الشيطان الذي هو من الجنِّ {والناس} عطف على قوله: الوسواس والمعنى: من شرِّ ذي الوسواس ومن شر النَّاس كأنَّه أُمر أن يستعيذه من شرِّ الجنِّ ومن شر الناس',NULL,'1243',NULL,NULL,'0');
