INSERT INTO [b25803] VALUES (16,'إسماعيل بن زكريا عن عاصم عن محمد بن سيرين قال
INSERT INTO [b25803] VALUES (17,'حدثنا أشهل بن حاتم عن ابن عون عن محمد قال إن هذا العلم دين فلينظر الرجل عمن يأخذ دينه',NULL,'20',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (18,'حدثنا أحمد بن يونس حدثنا زائدة عن هشام بن حسان عن الحسن قال لا تجالسوا أهل الأهواء ولا تسمعوا منهم حدثنا إسحاق بن منصور حدثنا عفان حدثني',NULL,'21',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (19,'يحيى بن سعيد قال سألت شعبة وسفيان ومالكا وابن
INSERT INTO [b25803] VALUES (20,'سمعت عليا يقول سمعت يحيى يقول ينبغي في صاحب الحديث خصال يكون ثبت الأخذ ويفهم ما يقال له ويبصر الرجال ثم يتعاهد ذاك',NULL,'23',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (21,'سمعت أبا قدامة يقول سمعت يحيى بن سعيد يقول رب صالح لو لم يحدث كان خيرا له إنما هو أمانة إنما هو تأدية الأمانة في الذهب والفضة أيسر منه في الحديث.
INSERT INTO [b25803] VALUES (22,'ثم المختارية من أهل الكوفة حين تنبأ فيهم في قديم الدهر وأصحاب علي وعبد الله متوافرون فغلب عليها بمن تابعه من
INSERT INTO [b25803] VALUES (23,'حدثنا أبو نعيم ثنا شريك عن أبي إسحاق قال سمعت خزيمة بن نصر العبسي أيام المختار وهو يقولون ما يقولون من الكذب وكان من أصحاب علي قاتلهم الله أي عصابة تشانوا وأي حديث أفسدوا',NULL,'26',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (24,'حدثنا النفيلي ثنا عيسى بن يونس عن أبيه عن أبي إسحاق عن صلة بن زفر العبسي قال قاتل الله المختار أي شعية أفسد وأي حديث شان حدثني أحمد بن حنبل حدثنا أبو بكر بن عياش حدثنا',NULL,'27',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (25,'مغيرة قال لم يكن يصدق عن علي في الحديث عنه إلا أصحاب عبد الله حدثنا شبابة بن سوار حدثنا شعبة عن عمرو بن مرة عن ابن أبي ليلى قال صحبت عليا في السفر والحضر فكل ما يحدثون عنه باطل
INSERT INTO [b25803] VALUES (26,'اتهم كان يقول تعلمت القرآن في سنتين والوحي في ثلاث سنين وابن عباس يقول لا وحي إلا ما بين اللوحين وأجمع على ذلك المسلمون وقد قال رسول الله صلى الله عليه وسلم ستة لعنهم',NULL,'29',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (27,'الله وكل نبي مجاب منهم الزائد في كتاب الله',NULL,'30',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (28,'وأمر الحارث في حديثه بين عند من لم يعم الله قلبه وقد روى عن علي تشهدا خالف فيه الأمة قال كان يقول بسم الله خير الأسماء التحيات لله ما طاب فلله وما خبث فلغيره أشهد أن لا إله إلا الله وأن محمدا عبده ورسوله أشهد أنه نعم الرب ونعم الرسول محمد السلام على نبي الله السلام على أنبياء الله السلام على المؤمنين والمؤمنات من غاب منهم ومن شهد ونحو هذا',NULL,'31',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (29,'والتشهد عن ابن مسعود وأبي موسى وابن عباس كأنهم تكلموا بلسان واحد عن النبي صلى الله عليه وسلم محفوظ مشهور.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (30,'حدثني عبد الله بن الربيع حدثنا يحيى بن آدم حدثنا
INSERT INTO [b25803] VALUES (31,'أو أربعة سمعت ابن حنبل يقول كان أبو إسحاق تزوج امرأة الحارث فوقع حديثه إليه ويقولون لم يسمع من الحارث إلا ثلاثة أو أربعة سمعت أبا بكر بن عياش يقول قال أحمد كلاما هذا معناه
INSERT INTO [b25803] VALUES (32,'حكي عن سفيان قال كنا نعرف فضل حديث عاصم على حديث الحارث روى عنه أبو إسحاق حديثا في تطوع النبي صلى الله عليه
INSERT INTO [b25803] VALUES (33,'فيا لعباد الله أما كان ينبغي لأحد من أصحاب النبي صلى الله عليه وسلم وأزواجه يحكي هذه الركعات إذ هم معه في دهرهم والحكاية عن',NULL,'36',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (34,'عائشة رضي الله عنها في الاثنتي عشرة ركعة من السنة وابن عمر عشر ركعات والعامة من الأمة أو من
INSERT INTO [b25803] VALUES (35,'ثم خالف رواية الأمة واتفاقها حين روى أن في خمس وعشرين من الإبل خمسا من الغنم',NULL,'38',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (36,'وهذا حماد بن سلمة عن ثمامة بن عبد الله عن أنس أن أبا بكر كتب له الصدقة التي فرض رسول الله صلى الله عليه وسلم فيما دون خمس وعشرين من الإبل في كل خمس شاة فإذا بلغت خمسا وعشرين ففيها ابنة مخاض',NULL,'39',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (37,'وكذلك حكاية الزهري عن عبد الله بن عمر وما حكى سفيان بن',NULL,'40',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (38,'عيينة عن الزهري أيضا كذلك',NULL,'41',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (39,'وسألت عليا يعني ابن المديني عن عاصم والحارث فقال لي يا أبا إسحاق مثلك يسأل عن ذا الحارث كذاب قال علي وسمعت يحيى بن سعيد يقول قال سفيان كنا نعرف فضل حديث عاصم على حديث الحارث
