INSERT INTO [b25803] VALUES (62,'ثم قال علي من يتركه جرير أي شيء هو فقال له الحلواني وهو معنا ذكر لي عن حسين الجعفي أنه قال كان طويل اللحية أحمقها سمعته يقول لبيك قاتل نعثل فقال علي حينئذ هذا والله الجهل والغلو',NULL,'65',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (63,'<span data-type="title" id=toc-37>٣٧ </span>- الكلبي محمد بن السائب كذاب ساقط',NULL,'66',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (64,'حدثت عن المعتمر بن سليمان عن أبيه قال كان بالكوفة كذابان فمات أحدهما السدي والكلبي حدثت عن علي بن الحسين بن واقد حدثني',NULL,'67',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (65,'أبي قال قدمت الكوفة ومنيتي لقي السدي فأتيته فسألته عن تفسير سبعين آية من كتاب الله تعالى فحدثني بها فلم أقم من مجلسي حتى سمعته يشتم أبا بكر وعمر رضي الله عنهما فلم أعد إليه وأما الكلبي فالأمر فيه أطم وأعظم سمعت سليمان بن معبد يقول حدثنا الأصمعي قال سمعت قرة بن خالد يقول كانوا يرون أن الكلبي يذرف قلت للأصمعي وما التذريف قال الزيادة',NULL,'68',NULL,NULL,'0');
