INSERT INTO [b25803] VALUES (293,'أما إسماعيل بن عياش فقلت لأبي اليمان ما أشبه حديثه بثياب سابور يرقم على الثوب المئة ولعل شراءه دون عشرة قال كان من أروى الناس عن الكذابين وهو في حديث الثقات من الشاميين أحمد منه في حديث غيرهم',NULL,'297',NULL,NULL,'0');
INSERT INTO [b25803] VALUES (294,'وأما أبو يحمد فرحمه الله وغفر له ما كان يبالي إذا وجد خرافة عمن يأخذه فأما حديثه عن الثقات فلا بأس به
