INSERT INTO [b25803] VALUES (327,'وفلان عن الشعبي ويزيد بن أبي حبيب عن مكحول فقالوا له يا أبا داود يزيد بن أبي حبيب أين كنت رأيته فقال يا أحمق تراني قلته ولم أعد له جوابا رأيته بالباب والأبواب ثم يقول أحمد يزيد ما كان يصنع بالباب والأبواب فانظر إلى جسارته وجرأته وتهاونه ببليته',NULL,'331',NULL,NULL,'0');
