-- Insert data for b26209
INSERT INTO [b26209] VALUES (1,'أخبرنا الشيخ الإمام أبو الفضل جعفر بن علي بن هبة الله الهمداني قراءة عليه وأنا أسمع في ثالث عشري رجب سنة خمس وثلاثين وستمائة بدمشق قيل له
أخبركم الشيخ الامام شيخ الاسلام أوحد الأنام الفقيه الحافظ أبو طاهر أحمد بن محمد بن أحمد بن محمد بن ابراهيم بن سلفه السلفي الأصبهاني رضي الله عنة قراءة عليه بالاسكندريه وأنا أسمع في المحرم سنة ثلاث وسبعين وخمسمائة قال
سألت الشيخ الأوحد أبا الكرم خميس بن علي بن أحمد بن علي بن ابراهيم بن الحسن بن سلامويه الحوزي الحافظ بواسط سنة خمسمائة
<span data-type="title" id=toc-1>١ </span>- عن أبي القاسم عمر بن علي بن أحمد الميموني فقال هو من قرية ميمون وهي على مقدار نصف فرسخ من واسط كان غلاما لأبي طالب بن مخلد جد أبي المفضل رباه مع ابنه أبي الحسن وسمعه',NULL,'44',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (2,'من القاضي أبي الفرج الخيوطي وأبي عبد الله العلوي صاحب ابن مبشر وغيرهما مات بعد الخمسين وأربعمائة وكان مكثرا
<span data-type="title" id=toc-2>٢ </span>- وسألته عن أبي محمد الغندجاني فقال سمع بافادة أبيه وعمه من المخلص والكتاني والصرصري وابن الصلت والفرضي وابن',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (3,'المهدي وابن بشران وابن رزقويه ومن في درجتهم نبيل جليل صحيح الأصول صدوق فارق بغداد بعيد الثلاثين وأربعمائة وأقام بواسط متديرا لها وسمع منه أهلها والناس الى أن توفي في أواخر سنة سبع وستين وجاز الثمانين وكان بغدادي المولد ثقه
<span data-type="title" id=toc-3>٣ </span>- وسألته عن أبي البركات احمد بن عثمان بن نفيس فقال هو ابن أبي عمرو عثمان بن أحمد بن نفيس بن سعيد المؤدب كان يقول أبي مضري الأصل من مضر بن نزار وجدنا سماعه في الأصول مع أبيه من ابن التباني وابن خزفه وغيرهما وكان يقول قد أجاز لي',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (4,'الجاذري وسمعت أنا لم نر سماعه الأصول وكان لا بأس به مولده سنة اثنتين وأربعمائة ومات ببغداد بعد الثمانين وأربعمائة وكان عنده أبو الفضل التميمي سمع منه لما قدم واسط مجتازا الى مهذب الدوله أبي الحسن علي بن نصر أمير البطائح
<span data-type="title" id=toc-4>٤ </span>- وسألته عن أبي عبد الله العلوي فقال هو الحسين بن محمد العلوي والد أبي محمد عبد الله المقرئ الصدر في الجامع بواسط كان شاهدا عدلا ورواية ثقه عن أبي الحسن بن مبشر وأبي بكر بن رزق الله الحداد وأبي بكر الخليل بن أبي رافع الطحان حدث عن ابن مبشر',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (5,'عن أحمد بن سنان بمسنده كله آخر من حدث عنه بواسط أبو الحسن بن مخلد والد أبي الفضل
<span data-type="title" id=toc-5>٥ </span>- وسألته عن أبي طالب الصيرفي فقال هو محمد بن أحمد بن عثمان الصيرفي سمع بافادة أخيه أبي القاسم عبيد الله بن أحمد الذي يقال له الأزهري كان يتهم بالرفض خرج عن بغداد في أول ما وردت اليها دعاة الإسماعيليه وأقام بواسط مستخفيا فحدث بها الى أن مات وكان صحيح الأصول جيد السماع أكثرها بخط أخيه
<span data-type="title" id=toc-6>٦ </span>- وسألته عن كاتب ابن قنطر البيع فقال هو أبو القاسم عبيد',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (6,'الله بن هارون بن محمد القطان توفي سنة أربع وعشرين آخر من حدث عنه شيخنا أبو الحسن بن أبي الصقر العدل الأديب سمع الحضيني أبا الطيب والمفيد أبا بكر وغيرهما وكان جيد السماع مستقيم الطريقه
<span data-type="title" id=toc-7>٧ </span>- وسألته عن أبي الحسن علي بن محمد بن علي بن عبيد الله كاتب الوقف بواسط فقال كان اسمه صدقه فيكره ان يسمى به كان معلما في الأصل بالحوز ثم انتقل عنها مع دخول الأتراك العراق فخدم في الوقف وقربه اسماعيل',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (7,'القاضي وأدناه ثم سمع بعد ذلك قوله ابنه أبو المفضل محمد بن اسماعيل وكان عنده عن أبي الحسن العجمي وأبي الحسن بن سمنان المؤدب والقاضي أبي الحسين بن الرؤاسي صاحب ابن الباقلاني الأشعري وأبي بكر أحمد بن محمد بن طاوان السمسار المعروف بشراره وغيرهم وكان عنده القراءات عن أبي علي بن علان ورأينا معه خطه وكان لا بأس به رحمه الله
<span data-type="title" id=toc-8>٨ </span>- وسألته عن أبي علي بن المعلى فقال هو محمد بن العلاء بن المعلى بصري الأصل أقام بواسط وحدث بها عن البصريين الى أن مات وروى عنه ابن التباني وغيره لا أخبره جيدا',NULL,'50',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (8,'<span data-type="title" id=toc-9>٩ </span>- وسألته عن القاضي أبي تمام فقال هو علي بن محمد بن الحسن بن يزداد العبدي وأبوه أبو خازم قاضي القادر أمير المؤمنين على واسط وأعمالها كان غاليا في التسنن فقبض عليه أبو محمد بن سهلان وزير سلطان الدوله وبعث به إلى ابن أبي الشوك فقتله في',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (9,'نواحي الدينور واستقضى بعد أبيه فلم تستقم طريقته حتى عزل بالقاضي أبي الطيب بن كماري وكان أحد شهوده فبقي معزولا إلى أن قتل أبو الطيب قتله اللصوص في داره سنة اثنتين وعشرين السنه التي مات فيها القادر فرد أبو تمام فبقي قاضيا إلى شوال سنة أربع وثلاثين فنقم عليه الملك العزيز أبو منصور بن جلال الدوله فقبض عليه وأخرج من داره الخمور وآلاتها وقال هذا كان يخفي هذا المنكر فقوم قالوا كان يفعله وقوم قالوا لا بل أدخل إلى داره مع الأجناد وقت دخولهم اليها وخرجوا به طلبا لسوء السمعه الا أنه كان قد سمع أبا الحسين بن المظفر وأبا الفضل وأبو الفضل الزهري وبواسط أبا الفرج الخيوطي',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (10,'صاحب الزعفراني وأبا عبد الله العلوي وغيرهما وأقام ببغداد بعد عزله وكان رافضيا يتظاهر به ويقول بخلق القرآن ويدعو إليه إلا أنه كان صحيح السماع رحل إليه الناس وسمع منه أهل الآفاق إلى أن مات في شوال من سنة تسع وخمسين
<span data-type="title" id=toc-10>١٠ </span>- وسألته عن أبي الفتح بن المختار فقال هو محمد بن محمد بن المختار كان نحويا فاضلا جالس أبا القاسم بن كردان وسمع منه وجالس أبا الحسين بن دينار ولم يثبت له عنه رواية إلا أنه سمع من أبي الحسن عبد السلام بن عبد الملك البزاز وأبي عبد الله محمد بن أحمد',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (11,'السقطي صاحب أبي بكر النقاش وغيرهما وكان حسن الإيراد جيد المحفوظ متيقظا في الشهاده بلغ تسعين إلا شهورا ومات سنة أربع وسبعين وأربعمائة
<span data-type="title" id=toc-11>١١ </span>- وسألته عن ابن كردان فقال هو أبو القاسم علي بن طلحة بن كردان النحوي صاحب أبي وعلي بن عيسى الرماني قرأ عليهما كتاب سيبويه والواسطيون يفضلونه على ابن جني والربعي صنف كتابا كبيرا في اعراب القرآن قال لي شيخنا أبو الفتح كان يقارب خمسة عشر مجلدا ثم بد ١ له فيه فغسله قبل موته مات سنة أربع وعشرين وكان
متنزها متصونا ركب اليه فخر',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (12,'الملك أبو غالب محمد بن علي بن خلف وزير بهاء الدوله وهو سلطان الوقت وبذل له فلم يقبل وكان قد جرت بينه وبين القاضي أبي تغلب أحمد بن عبيد الله العاقولي صديق الوزير المغربي وخليفه السلطان والحكام على واسط في وقته وكان معظما مفخما خصومه فقال له ابن كردان ان صلت علينا بمالك صلنا عليك بقناعتنا حكى ذلك لنا عنه أبو نعيم أحمد بن علي ابن أخي سكرة المقرئ في الجامع بواسط آخر من حدث عنه أبو المعالي محمد بن عبد السلام بن شانده مات سنة أربع وعشرين
<span data-type="title" id=toc-12>١٢ </span>- وسألته عن ابن شانده فقال هو أبو المعالي محمد بن عبد السلام بن عبيد الله بن احموله الأصبهاني المعروف بابن شانده كان رئيسا محتشما وثقة صدوقا قال لي ولدت في سنة ست وتسعين',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (13,'وثلاثمائة في السنه التي مات فيها أبو بكر احمد بن عبيد بن بيري وكان قد سمع ابن خزفه ما تاريخ ابن أبي خيثمه كان يقول ذلك ووجدنا الأصول بعد وفاته رحمه الله وكان عنده عن عمه أبي محمد التلعكبري مصنف الرافضه كتب من علمهم لا يسمعها أحدا ومددت يدي اليها يوما فاستلبها من يدي وقال هذا لا يصلح لك وكان يتظاهر بالسنه
<span data-type="title" id=toc-13>١٣ </span>- وسألته عن ابن بيري فقال هو أبو بكر أحمد بن عبيد بن الفضل بن سهل بن بيري سمع البغوي وأبا بكر بن أبي داود وابن',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (14,'صاعد والصولي وابن مبشر الواسطي كان ثقة صدوقا كف بأخره آخر من حدث عنه بواسط أبو الحسن بن مخلد والد أبي المفضل
<span data-type="title" id=toc-14>١٤ </span>- وسألته عن أبي الحسن العجمي فقال هو طرسوسي الأصل كان صالحا مسندا ثقة عنده عن أبي بكر بن مهدي خال أبي عبد الله السقطي وأبي بكر الشمشاطي الخطيب كان بواسط وغيرهما وولده أبو بكر الذي يعرف بالهرمزان كان عنده حديث كثير وقراءات عوال عن الكتاني وغيره وكان صدرا في الجامع بواسط للقراء مشهورا بالصلاح والحفظ للقرآن
<span data-type="title" id=toc-15>١٥ </span>- وسألته عن الشمشاطي فقال هو أبو بكر محمد بن جعفر بن',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (15,'أحمد بن عبد الوهاب سمع في صدر الثلاثمائة من أبي عبد الله أحمد بن الحسن بن عبد الجبار وغيره وكان ثقة صدوقا مات بعد الخمسين والثلاثمائة سمعت أبا الحسن علي بن محمد بن الطيب الشاهد المغازلي يقول سمعت أبا غالب محمد بن أحمد بن سهل النحوي يقول سمعت أبا طاهر الريان بن سليمان الفرضي يقول سئل أبو بكر محمد بن جعفر بن أحمد الشمشاطي ما بال الناس تفرقوا فطائفة اشتغلت بالفقه ودقائقه وأخرى بالحديث وطرقه وأخرى بالكلام ومعانيه وأخرى بالأدب ومحاسنه فقال تلك الطرق الى الله فكل طائفة سلكت منها طريقة
<span data-type="title" id=toc-16>١٦ </span>- وسألته عن أبي غالب النحوي فقال هو محمد بن أحمد بن سهل يعرف بابن الخاله أصله من نهر',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (16,'سابس ينسب الى خاله ابن بشران وكان أحد الأعيان قدم واسطا فجالس ابن الجلاب وابن دينار وتخصص بابن كردان وقرأ عليه كتاب سيبويه ولازم حلقة أبي اسحاق الرفاعي صاحب السيرافي وكان يقول قرأت عليه من أشعار العرب ألف ديوان',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (17,'وكان مكثرا حسن المحاضره مليح العارضه الا أنه لم ينتفع به أحد بواسط ولم يبرع به أحد في الأدب وكان
جيد الشعر مع ذلك رأينا في كتبه بعده خطوط أشياخ عدة بكتب كثيره في الأدب وغيره الا أنه كان معتزليا وشهد ثم اسماعيل قاضي واسط في آخر شوطه ومات بعد الخمسين منتصف رجب سنة اثنتين وستين
<span data-type="title" id=toc-17>١٧ </span>- وسألته عن ابن خزفه فقال هو أبو الحسن علي بن أبي بكر محمد بن الحسن بن خزفه الصيدلاني سمع أباه وأبا عبد الله محمد بن الحسين بن سعيد الزعفراني المعدل وروى عنه عن أبي بكر أحمد بن زهير بن حرب تاريخه الجامع الكبير وكان مكثرا صدوقا أملى بعد الأربعمائة الى أن مات في سنة تسع وأربعمائة وكان مداخلا لفخر الملك ومعه كالنديم وأبو القاسم اللالكائي يدلس به فيقول حدثنا',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (18,'علي بن محمد النديم بواسط حدثنا عنه جماعة
<span data-type="title" id=toc-18>١٨ </span>- وسألته عن ابن دينار فقال هو أبو الحسين علي بن محمد بن عبد الرحيم بن دينار الكاتب بصري الأصل واسطي الأخير سمع أبا بكر بن مقسم ولقي المتنبي وسمع منه ديوانه ومدحه بقصيدة هي عندنا قوما في ديوانه أولها رب القريض اليك الحل والرحل ضاقت الى العلم إلا نحوك السبل
تضائل الشعراء اليوم ثم فتى صعاب كل قريض عنده ذلل
وكان شاعرا مجيدا شارك المتنبي في أكثر ممدوحيه كسيف الدوله بن حمدان وابن العميد وغيرهما وكان حسن الخط يقال على طريقة ابن مقله توفي سنة تسع وأربعمائة حمل الناس عنه الأدب وأكثروا بواسط وغيرها وكان سهل الخلائق حميد الطريقه سأله الناس',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (19,'بواسط بعد موت أبي محمد عبد الله بن أبي عبد الله العلوي أن يجلس لهم صدرا فيقرئهم فامتنع وقال أنا أتعمم مدورة وكمي ضيق وليست هذه حلية أهل القرآن أظنني سمعت ذلك من أبي الحسن المغازلي الشاهد
<span data-type="title" id=toc-19>١٩ </span>- وسألته عن أبي الحسن محمد بن محمد بن مخلد الأزدي البزاز فقال سمع بإفادة أبيه أبي طالب من أبي بكر بن بيري وأبي عبد الله العلوي وأبي علي بن معاذ وأبي الحسين بن دينار وابن خزفة والناس وكان جيد الأصول ثقة فيما يرويه ويقول جيد الخط توفي في سنة ثمان وستين
<span data-type="title" id=toc-20>٢٠ </span>- وسألته عن أبي الحسن عبد السلام بن عبد الملك بن حبيب البزاز فقال لم أر له سماعا إلا من أبي غانم سهل بن اسماعيل بن بلبل',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (20,'<span data-type="title" id=toc-21>٢١ </span>- الفقيه الخصاصي وكان أبو غانم ثقة صدوقا صحيح السماع شفعوي المذهب سمع من أبي جعفر محمد بن علي بن مهران الزيتوني عن أحمد بن عبد الجبار العطاردي وكان عنده المغازي بهذا الإسناد
<span data-type="title" id=toc-22>٢٢ </span>- وسألته عن أبي عبد الله الحسين بن أحمد بن التباني البيع فقال سمع أبا محمد بن السقاء وغيره أملى بواسط بعد أبي الحسين بن كماري والد اسماعيل وكان ثقة جيد التحفظ آخر من حدث عنه الرئيس أبو الحسن هبة الله بن محمد بن موسى المعروف بابن الصفار الكاتب المقرئ
<span data-type="title" id=toc-23>٢٣ </span>- وسألته عن أبي علي بن علان فقال هو أحمد بن أبي الحسن محمد بن علان الشاهد قديم الشهاده شهد ثم أبي ابراهيم العلوي',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (21,'قاضي واسط فمن بعده وكان يترفض ويكتمه وجلس صدرا للقراء في جامع واسط ثم عجز عن ذلك فلزم بيته وكان عنده عن أبي ابراهيم القاضي أحاديث فيها تشيع وسمع من أبي الطيب عبد الغفار بن عبيد الله
بن السري الحضيني وقرأ عليه وتخصص به ومات بعد الأربعين وأربعمائة آخر من حدث عنه أبو الفضل بن السوادي الشيخ الصالح
<span data-type="title" id=toc-24>٢٤ </span>- وسألته عن أبي الحسن علي بن عبيد الله بن علي المعروف بابن القصاب البيع فقال رحل به أبوه الى أبي بكر المفيد الجرجرائي فسمع منه وكان ثقة موسرا حسن المواساة لأهل العلم حدثني سبطة أبو عبد الله بن السوادي أنه مات فجاءه بعد عوده من صلاة العصر وكان صلاها في الجامع فاتكأ فمات وأصحابنا قد قالوا سمع ابن السقاء وما أحق ذلك',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (22,'<span data-type="title" id=toc-25>٢٥ </span>- وسألته عن الحضيني فقال هو أبو الطيب عبد الغفار بن عبيد الله كوفي الأصل تدير واسطا وتصدر بجامعها للقراء الى ان مات سمع ابن مجاهد ومن هو أقدم منه أظن أنه توفي سنة سبع وستين وثلاثمائة وكان ثقة
<span data-type="title" id=toc-26>٢٦ </span>- وسألته عن أبي نعيم بن خصية فقال هو محمد بن عبد الواحد بن عبد العزيز كان عدلا مستقيما سمع ابن خزفه ورأينا سماعه في الأصول
<span data-type="title" id=toc-27>٢٧ </span>- وسألته عن أبي البركات ابراهيم بن محمد بن خلف الجماري ويعرف بالبني فقال كان سقطيا سمع ابن خزفه والناس',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (23,'<span data-type="title" id=toc-28>٢٨ </span>- وولده أبو نعيم محمد بن ابراهيم الذي حدث بمسند مسدد وكان سمعه بإفادته وكلاهما ثقه
<span data-type="title" id=toc-29>٢٩ </span>- وسألته عن أبي غالب محمد بن الحسين بن أبي صالح المقرئ فقال كان شيخا صالحا جيد الحفظ للقرآن وله بواسط مسجد يعرف به وعقب من جهة ابنته حدث عن أبي الحسين بن دينار وابن خزفه وسمع ببغداد من ابن مهدي وشهد بأخرة
فبلغه عن ابن فضلان اليهودي الناظر كان بواسط من جهة السلطان أنه قال ترى هذا الشيخ يشهد ثم منكر ونكير فترك الشهادة ولم يعد فيها حتى مات وكانت شهادته ثم اسماعيل قاضي واسط وكان متقشفا
<span data-type="title" id=toc-30>٣٠ </span>- وسألته عن القاضي اسماعيل فقال هو أبو علي اسماعيل بن محمد',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (24,'ابن أحمد بن كماري سمع من ابن بيري وحدث عنه بتاريخ بحشل عن أبي بكر محمد بن عثمان بن سمعان المعدل عن بحشل وسمع من أبيه وابن خزفه وقرأ الأدب على ابن دينار وكان كاتبا مترسلا فصيحا حسن العقل والتثبت فقيها على مذهب أبي حنيفه وأصحابه قرأ على أبيه أبي الحسين محمد بن أحمد وكان أبوه قرأ على أبي بكر الرازي وهم بيت معروف بالصون والعلم والمعرفه بالقضاء
<span data-type="title" id=toc-31>٣١ </span>- والأحكام وكان ابنه أبو المفضل محمد بن اسماعيل قاضيا بعده على واسط وكان لين الجانب كيس الأخلاق إلا أنه كان يزعم أنهم من ولد',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (25,'علي بن محمد صاحب الزنج بالبصره ولم يثبت ذلك ورأيت بخطه بعد موته أشياء تدل على رفضه والله يرحمه
<span data-type="title" id=toc-32>٣٢ </span>- وسألته عن أبي الحسن علي بن محمد بن محمد بن الطيب المغازلي فقال كان مالكي المذهب شهد ثم أبي المفضل محمد بن اسماعيل وكان عارفا بالفقه والشروط والسجلات وسمع الحديث الكثير عن عالم من الناس من أهل
واسط وغيرهم وجمع التاريخ المجدد التالي لتاريخ بحشل وأصحاب شعبة وأصحاب يزيد بن هارون وأصحاب مالك وكان مكثرا خطيبا على المنبر يخلف صاحب الصلاه بواسط وكان مطلعا على كل علم من علوم الشريعه غرق ببغداد بعد الثمانين وأحدر الى واسط فدفن بها وكان يومه مشهودا
<span data-type="title" id=toc-33>٣٣ </span>- وسألته عن أبي طالب سعد بن محمد الوحيد فقال ما أعرفه',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (26,'في رجال واسط إلا أن أبا الحسن المغازلي أنشدنا قال أنشدنا أبو غالب النحوي قال أنشدنا أبو طالب الوحيد لنفسه
لو تخليت للزمان للاقى مسمعاه مني عتابا المطلوب
انما تكثر النوائب في الدنيا لأن الكرام فيها قليل
<span data-type="title" id=toc-34>٣٤ </span>- وسألته عن أبي البركات محمد بن الحسن الهاشمي فقال هذا يعرف بابن ملوك خرج عن واسط وهو صبي فأقام بمكه وسمع بها من كريمة وغيرها وأقام بالقدس وسمع من اسماعيل الأستراباذي ثم قدم واسط وقد أسن فلازمته وعولت على أن أسمع منه مغازي الواقدي فنهاني أبو المفضل عنه وقال هذا كان قيما في الحمام في مبتدئه فرغبت عنه
<span data-type="title" id=toc-35>٣٥ </span>- وسألته عن ابن الصقر فقال هو أبو الحسن محمد بن علي',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (27,'وعلي يكنى أبا طالب بن أبي الصقر واسم أبي الصقر الحسن كان يقول أنا من ولد أبي الصقر اسماعيل بن بلبل الوزير قدم جدي مع القاضي يوسف بن يعقوب الى واسط وكيلا بين يديه فتديرها وكان شاعرامجيدا وكاتبا سديدا حسن الخط والعقل والمروءه وكان قد سمع من أبي القاسم كاتب ابن قنطر وسمعته يقول كان زوج خالتي وكان قد رحل الى بغداد ولازم الشيخ أبا اسحاق الشيرازي وعلق عنه كتبه كلها ولما وقعت الفتنه بين الحنابله والأشعريه كان قائما فيها قاعدا وعمل في ذلك أشعارا سماها الشافعيات رويت عنه وهي مدونه في شعره وبلغ تسعين سنه إلا شهورا قال لي ولدت يوم الأحد ثالث عشر ذي القعده من سنة تسع وأربعماية وقال خميس توفي ابن أبي الصقر في جمادى الأولى سنة ثمان وتسعين
<span data-type="title" id=toc-36>٣٦ </span>- وسألته عن أبي الفرج أحمد بن علي بن جعفر بن المعلى الخيوطي فقال كان مكثرا عن الزعفراني أبي عبد الله محمد بن الحسين',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (28,'ابن سعيد صاحب ابن أبي خيثمه قال لي أبو الحسن المغازلي قرأ على الزعفراني تاريخ ابن أبي خيثمه لنفسه وللناس نيفا وثمانين مره وأراد الانحدار الى البصره الى ابن داسه ليسمع منه سنن أبي داود فكتب له الزعفراني اليه قد انحدر أبو الفرج اليك وهو عيني مذ ذهبت عيني فاصغ اليه واقض حاجته ورده الي مسرعا أو ما هو نحوه وكان حسن الخط صحيح النقل والواسطيون يقولون كان حافظا متقنا وقد كان فيه
<span data-type="title" id=toc-37>٣٧ </span>- وسألته عن أبي طاهر الريان بن سليمان الفرضي فقال كان من أهل باب الزاب قيما بالفرائض اماما في القراءة لا أعرف ذلك
<span data-type="title" id=toc-38>٣٨ </span>- وسألته عن أبي طاهر محمد بن عبد الله بن حامد الناقد',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (29,'الكاتب في دار البطيخ فقال قد سمع من ابي التباني شيئا من أماليه سمعناها منه وكان شيخا له رواء صالحا من شيوخ أهل القرآن
<span data-type="title" id=toc-39>٣٩ </span>- وسألته عن أبي طاهر محمد بن علي البيع فقال بغدادي قدم واسطا ثم احتلال بغداد وخروج الخليفه عنها وكان يذم الأجزاء المفاريد ويقول لا يعجبني الجزء العاشر والجزء الثلاثون والجزء الثاني والتسعون وما أشبه ذلك
وكان يروي المصنفات الصغار مما هو جزء الى اثنين الى ثلاثه لا يزيد على ذلك وكان عنده من هذا كثير حدثني بذلك كله أبو الحسن المغازلي
<span data-type="title" id=toc-40>٤٠ </span>- وسألته عن أبي نعيم بن زبزب فقال مولده سنة ثمان',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (30,'عشرة على ما قال سمع ابن عبد الرحمن العلوي بالكوفه وأبا القاسم الميموني بواسط وأبا غالب بن أبي صالح كتاب الأشربه لأبي ثور ابراهيم بن خالد واستوعب جميع ما ثم أبي تمام بن أبي خازم لأنه جاره وكان كلما دخل قوم صحبهم صحيح السماع ثقة لا بأس به إلا أنه يتهم بالتشيع وما سمعنا منه ذلك
<span data-type="title" id=toc-41>٤١ </span>- وسألته عن أبي الفضل بن السوادي فقال جاز الثمانين وكف بأخرة وصام أزيد من ستين سنه كثير الدرس للقرآن كثير المجاهده سمع أبا علي بن علان وأبا غالب بن أبي صالح وأبا تمام بن أبي خازم وشهد ثم أبي المفضل القاضي هو وأخوه أبو عبد الله وتقدما الشهود كافة يوم شهدا
<span data-type="title" id=toc-42>٤٢ </span>- وسألته عن معاذ بن عبد الله بن رجاء الطحان فقال سمع',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (31,'من أبي طالب البغداذي الأزهري وكان قد نزل عليهم بواسط
<span data-type="title" id=toc-43>٤٣ </span>- وسألته عن أبي السعادات الخطيب فقال كثير الشيوخ خطب على المنبر الشرقي من واسط وشهد ثم أبي علي بن برهون قاضي واسط وكتب الوقف بعد أبي الحسن المعروف بصدقة وله شعر جيد ومعرفة بالأسانيد وهو من ولد أبي اسحاق سعد بن أبي وقاص رضي الله عنه إلا أنه يكتم ذلك مشتهر به وذلك معدود من عقله وقرأ القرآن على أبي علي غلام الهراس ومعه خطه أظن بالسبعه
<span data-type="title" id=toc-44>٤٤ </span>- وسألته عن بدر بن عبد الله المقرئ فقال شيخ صالح من أهل الورع والزهد يعرف بذلك وهو غسل شيخنا أبا المفضل بوصية منه وله مسجد يقرئ فيه وقد ختم خلقا من عباد الله القرآن
<span data-type="title" id=toc-45>٤٥ </span>- وسألته عن أبي نعيم البخاري فقال شيخ زاهد صاحب زاويه',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (32,'ومسجد يكتب المصاحف وما يحسن ذكره من أبي المفضل وكان متخصصا به وطريقته حسنه
<span data-type="title" id=toc-46>٤٦ </span>- وسألته عن نجا بن أبي كريمه فقال شيخ من شيوخ أهل القرآن من أبي المفضل وابن نفيس ورأيت سماعه بخط أبي الحسن العكبري
<span data-type="title" id=toc-47>٤٧ </span>- وسألته عن ابن طيلون فقال سمع الغندجاني من أبي نعيم ابن أخي سكرة ولازمه وهو شيخ مرضي الطريقه
<span data-type="title" id=toc-48>٤٨ </span>- وسألته عن أبي الفضل بن العجمي فقال سمع أبا الحسن بن مخلد والغندجاني وغيرهما وببغداد ابن المسلمه وطبقته ولازم أبا اسحاق وعلق عنه كتبه وهو مكثر ثقة يفهم ما يقرأ عليه',NULL,'75',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (33,'<span data-type="title" id=toc-49>٤٩ </span>- وسألته عن أبي المجد بن جهور فقال هو ابن أخي القاضي أبي تغلب الذي كان قاضي واسط قرأ على عمه القرآن وعلى غلام الهراس ومعه خطه بالقراءات السبع وسمع من أبي تمام ومن أبي غالب بن بشران وهو أحد المعدلين ويقوم على البيمارستان بواسط وله فيه آثار جميله
<span data-type="title" id=toc-50>٥٠ </span>- وسألته عن أبي البركات فضل الله بن محمد بن مخلد',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (34,'الأزدي فقال هو أخو شيخنا أبي المفضل سمع أبا طالب البغدادي وغيره وفيه صلاح وديانه لم يرض لأخيه أبي المفضل الشهادة وهجرة عليها وهو صلى عليه لما مات ٥١ وسألته عن أخيه أبي الكرم نصر الله فقال سمع أباه وأبا تمام وسماعه في الأصول واضح جيد
<span data-type="title" id=toc-51>٥٢ </span>- وسألته عن أبي تغلب بن عجيف فقال حميد الطريقة سمع أبا تمام وأكثر عن الغندجاني وكتب أكثر أصوله بخطه وهو جيد الصون يفهم ما يقرأ عليه',NULL,'77',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (35,'<span data-type="title" id=toc-52>٥٣ </span>- وسألته عن القاضي أبي الأزهر فقال سمع قاضي بغداد أبا عبد الله الدامغاني بها ومن أبي الحسن كاتب الوقف بواسط كثيرا مجالس أبي المفضل وولي الحسبه بالبلد وشهد ثم أبي المفضل وهو اليوم أحد رؤساء واسط وأعيانها وذوي اليسار فيها
<span data-type="title" id=toc-53>٥٤ </span>- وسألته عن أبي علي بن برهون قاضي واسط فقال متقدم',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (36,'في الفقه من أصحاب الشيخين أبي اسحاق الشيرازي وأبي نصر بن الصباغ قضى بواسط بعد أبي تغلب فظهر من عقله وعدله وحسن سيرته ما زاد على الظن وسمع الخطيب وابن النقور والصريفيني وابن حمدوية وابن الغريق وطبقتهم واصوله حسنه وسماعاته صحيحة ٠
<span data-type="title" id=toc-54>٥٥ </span>- وسألته عن أبي محمد الآمدي فقال سبط ابي تغلب بن',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (37,'الاغلاقي الشاهد احد غلمان ابي المفضل والمتشبهين بطريقه من ابي المفضل وابن شاندة وابن نفيس وغيرهم ورحل الى بغداد فسمع هناك من جماعة وقرأ على أبي الخطاب بن الجراح القرآن وهو متحقق بالسنه صاحب مسجد لا يعاب بشئ
<span data-type="title" id=toc-55>٥٦ </span>- وسألته عن ابن شيران فقال قد من ابي نعيم ابن اخي سكرة وأبي الحسن المغازلي وسمع الغندجاني وغيره وقرأ على غلام الهراس العشرة وخطه معه بها وهو الآن متصدر بالجامع للإقراء وله معرفة بفقه أبي حنيفه',NULL,'80',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (38,'<span data-type="title" id=toc-56>٥٧ </span>- وسألته عن أبي الغنائم السدري الأشناني فقال شيخ صالح من أهل القرآن قديم وهو لقن أبا العز محمد بن الحسين بن بندار القرآن وسماعاته على اصول الغندجاني رأيتها مع أبي المفضل وغيره
<span data-type="title" id=toc-57>٥٨ </span>- وسألته عن ابي العز بن بندار فقال هو احد الأئمة الأعيان في علوم القرآن قرأ على غلام الهراس وغيره واستوعب القراءات وطرقها وبرع في المعرفة بها وسمع الحديث ببغداد وبواسط من الغندجاني وابن مخلد وإسماعيل',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (39,'القاضي وابن خصية وغيرهم من البغداديين ابن المسلمة والصريفيني وابن النقور وابن البسري وطبقتهم وهو حسن الخط جيد النقل ذو فهم بما يقوله ويرويه
<span data-type="title" id=toc-58>٥٩ </span>- وسألته عن أبي تغلب بن جهور فقال متقدم في الفقه لازم رضوان وإلياس الحنيفيين وعلق عنهما كتبه واستوعب علمه ثم انحدر إلى واسط فدرس بها',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (40,'زمانا فلما ولي أبو بكر الشامي قضاء القضاه ولاه واسطا وعزل أبا المفضل فظهر من شهامته وعنايته بعمارة الوقوف ما زاد على الظن وأقام حشمة القضاء وجعل له أبهة ونورا بعد أن كان إسماعيل وابنه أبو المفضل قد وضعا منه وتهاونا به ولم يزل على طريقه مرضية إلى أن عزله عميد الدولة أبو منصور بن جهير لسبب كان في نفسه منه ولم يعن بالحديث سمع قليلا سمعته يقول
سمعت رضوان الحنيفي يقول وسئل أيجوز الترحم على الفاسقين وأهل المعصية فقال ومن أحق بذلك منهم
<span data-type="title" id=toc-59>٦٠ </span>- وسألته عن ابن أخي سلم فقال هو الصيرفي سماعاته على أصول الغندجاني واضحة وهو صين دين لا بأس به',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (41,'<span data-type="title" id=toc-60>٦١ </span>- وسألته عن صدقه فقال صالح من غلمان أبي المفضل عليه
<span data-type="title" id=toc-61>٦٢ </span>- وسألته عن ابن التكين فقال كثير السماع من البغداديين ومعه خطوطهم كالشمس وضوحا إلا أنه أقام بواسط وتديرها فهي وطنه وهو صالح متحقق بالسنه
<span data-type="title" id=toc-62>٦٣ </span>- وسألته عن أبي علي بن المختار فقال أحمد بن محمد بن',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (42,'جعفر بن المختار العدل ابن بنت أبي الفتح قرأ الأدب على جده وسمع من جماعة من أصحابنا وسمع ببغداد من عاصم وغيره وشهد ثم أبي المفضل محمد بن إسماعيل وله شعر جيد وترسل سديد وموضع من النزاهة معروف
<span data-type="title" id=toc-63>٦٤ </span>- وسألته عن أبي الفضل بن جهور فقال هو محمد بن محمد بن الحسين بن عيسى بن جهور من أعيان الرؤساء وفضلاء الأدباء لم يعرض للحديث لتشاغله بالأدب تارة وبالتصرف أخرى قرأ الأدب على أبي علي الحسن بن عبد العزيز التونسي مغربي قدم واسطا وأقام بها إلى أن مات وجالس أبا غالب وسمع منه كثيرا وقال لي قرئ عليه',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (43,'كتاب الأصول لابن السراج وأنا أسمع سمعنا منه أدبا كثيرا وأشعارا حسانا له ولغيره وهو أخو أبي تغلب القاضي وآل جهور كلهم أعيان أماثل
<span data-type="title" id=toc-64>٦٥ </span>- وسألته عن بركة الحوزي فقال بركة بن حسان بن عيسى الحوزي أبو طاهر رجل صالح من أعيان أهل الخير ومن تلاميذ أبي المفضل بن مخلد قرأ عليه القرآن وله معرفة بالكلام وطريقة حسنه في التصرف سمع ابن مخلد والغندجاني وأبا غالب بن أبي صالح وأبا عبد الله أحمد بن أحمد بن سليمان
<span data-type="title" id=toc-65>٦٦ </span>- وسألته عن أبي منصور هبة الله بن الفضل بن سليمان الواسطي فقال هو أحد رؤسائها المتقدمين فيها سمع أبا تمام ولم',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (44,'يكن الحديث من همه ولكن وقع له حضور فأدركته السن لا بأس به ومن المتقدمين
<span data-type="title" id=toc-66>٦٧ </span>- وسألته عن أبي الحسن محمد بن محمد بن عيسى النحوي المعروف بالخيشي فقال بصري الأصل قرأ الأدب على أبي عبد الله الحسين بن علي النمري وكان يحفظ كتاب المقتضب ظاهرا كذا قال لي ابن أبي الصقر وكان قرأ عليه وانتفع به وإنما صار إلى واسط لأن الملك العزيز أبا منصور بن جلال الدولة استقدمه إليها ليقرأ عليه فأقام في دار بنى عمرون وتردد اليه الناس وكان يتردد الى دار الملك ثم اصعد إلى بغداد فأقام بها مدة ومات وكان له ابن يلقب البلصوص يكتب خطا حسنا وقع إلى مصر فخرجت والدته بعد موت أبيه في طلبه وكان معها مال له قدر فهلكت بنواحي الأنبار وتلف المال
<span data-type="title" id=toc-67>٦٨ </span>- وسألته عن عبد الملك بن مروان الكاتب أبي منصور فقال هذا كان في قديمه نصرانيا الراوي والله أعلم به لم يعرض للحديث ولم يكن من شأنه',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (45,'غير أنه كان شاعرا مجيدا وأديبا بارعا رأيت له قصيدة في وصف رمي البندق تزيد على خمسمائة بيت لم يقل أحد مثلها أجاد فيه أوصاف المياه والصحارى والرياض والشجر والغياض والسماء والأفلاك والنجوم وصنوف الأطيار أنشدناها أبو السعادات بن بختيار تلميذه عنه
<span data-type="title" id=toc-68>٦٩ </span>- وسألته عن أبي علي الحسن بن القاسم بن علي المقرئ المعروف بغلام الهراس فقال نشأ في بلده وطلب القرآن وقرأ على أبي محمد عبد لله بن أبي عبد الله العلوي ورحل الى بغداد فقرأ على النهراوني أبي الفرج عبد الملك بن بكران والحمامي والسوسنجردي ورحل الى مكه فقرأ على',NULL,'88',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (46,'الكارزيني ورحل الى مصر فقرأ على ابن نفيس الأنصاري وقرأ بحران على العلوي السني وقرأ بدمشق على الرهاوي وعلى أبي علي الأهوازي وسمع منه مصنفاته وكان يقرئ معه في جامع دمشق ثم عاد الى واسط وقد كف وكان في قديمه أعور والحاصل يقرئ الناس في الجامع فرحل اليه الناس من الآفاق وقرؤوا عليه ورأيته وقبلت يده وجلست بين يديه كثيرا إلا أنني لم أقرأ عليه وتوفي في أواخر سنة سبع وستين وكان يلقب امام الحرمين والبغداديون لهم فيه',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (47,'كلام وسمعت من أصحابنا من يقول سمعت أبا الفضل بن خيرون وقيل له أبو علي غلام الهراس عن أبي علي الأهوازي فقال مطرز معلم كذاب عن كذاب وروى الحديث عن ابن خزفه وكان اشتغاله بالقرآن أكثر
<span data-type="title" id=toc-69>٧٠ </span>- وسألته عن أبي الحسن العطار فقال هو أحمد بن المظفر بن أحمد بن يزداد الشافعي صاحب أبي محمد بن السقاء الحافظ روى عنه مسند مسدد وحدث به عنه أبو نعيم الجماري وكان عنده الأصل بخطه والسماع عليه بخط مسعود بن ناصر السجزي الحافظ أضوأ من',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (48,'الشمس وسماع أبي الحسن من أبي محمد صحيح محقق ثم أصحابنا الواسطيين
<span data-type="title" id=toc-70>٧١ </span>- وسألته عن أبي عمرو عثمان بن أحمد بن نفيس المؤدب فقال هو والد أبي البركات أحمد شيخنا سمع أبا بكر بن لال الهمذاني وحدث بواسط ومات بها قبل الثلاثين وسماع أبي نعيم الجماري منه في سنة ثمان وعشرين املاء بخطه وهو آخر من حدث عنه
<span data-type="title" id=toc-71>٧٢ </span>- وسألته عن أبي احمد بن شوذب فقال عمر بن أبي محمد عبد الله بن شوذب المقرئ سمع أباه عبد الله بن عمر والخلق حدث عنه ابناه أبو عمرو عثمان وأبو الحسين علي وكان ثقة ثبتا معتقدا للسنه أمارا بالمعروف نهاء عن المنكر أنكر عن أبي ابراهيم العلوي القاضي بعض',NULL,'91',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (49,'أمره وأراد اخراجه من البلد فراسله عضد الدوله بالكف عنه من جانب السؤال فكف وابناه هذان رحلا الى المفيد وسمعا منه فأكثرا وحدثا عنه بواسط آخر من حدثنا عن أبي الحسين شيخنا أبو عبد الله بن السوادي
<span data-type="title" id=toc-72>٧٣ </span>- وسألته عن أبي المفضل بن الجلخت فقال شيخنا أبو المفضل هبة الله بن محمود بن مخلد الأزدي يقصر الوصف عما كان عليه من خشونة الطريقه وحسنها وما كان ينطوي عليه من الزهد والاجتهاد في العبادة صام وقته كله ولازم المسجد الجامع معتكفا يقرئ',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (50,'القرآن ويملي الحديث روى عن أبي الحسن العجمي والميموني وكان كثير المشيخه حسن المعرفه بالحديث والفقه والفرائض وطرق القراءات والحساب جماعة لخلال الخير وقرأ القرآن على أبي المرجى بن ورقاء البزاز وأبي علي بن علان وغيرهما لم يبلغ الستين وكان ذا جاه عظيم ثم السلطان وفي أعين العوام توفي يوم الأحد رابع عشر المحرم سنة احدى وثمانين ودفن بداره وقبره الآن يغشى ويزار ويتبرك به
<span data-type="title" id=toc-73>٧٤ </span>- وسألته عن أبي بكر محمد بن الحسن بن خزفه والد أبي الحسن فقال ثقة صدوق شارك ولده في أكثر أشياخه لم يسمع في حداثته وانما سمع بأخرة
<span data-type="title" id=toc-74>٧٥ </span>- وسألته عن أبي الحسين محمد بن أحمد بن الطيب بن جعفر بن كماري والد اسماعيل القاضي فقال سمع أبا الحسين عبد الحميد بن موسى القناد وطبقته وأملى في الجامع بواسط وكان يتكلم على الأحاديث لا من طريق الصحيح والسقيم ولا الجرح والتعديل ولكن من طريق الوعظ والفقه فانه كان فقيها حنيفا من أصحاب الرازي أبي',NULL,'93',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (51,'بكر أحمد بن علي توفي سنة سبع عشرة آخر من حدث عنه شيخنا أبو تمام علي بن محمد ينوي
<span data-type="title" id=toc-75>٧٦ </span>- وسألته عن أبي عبد الله السقطي فقال هو محمد بن علي يعرف بابن أخت مهدي وكان الذي أفاده خاله أبو بكر بن مهدي سمع أبا بكر النقاش وروى عنه الصحيح عن الفربري عن البخاري اختل بأخرة فترك حديثه آخر من حدث عنه شيخنا أبو الفتح بن المختار توفي قبل العشر والأربعمائة وسمع من الزعفراني كتاب',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (52,'الموطأ وكان يرويه عن تمتام عن مالك
<span data-type="title" id=toc-76>٧٧ </span>- وسألته عن الزعفراني فقال أبو عبد الله محمد بن الحسين بن سعيد العدل سمع التمتام وابن أبي الدنيا وابن أبي خيثمه وسمع منه تاريخه الكبير الجامع وكان ذا حال نزل به صاحب الزنج على ما يقول الواسطيون في منحدره الى البصره فلما ملك الزنج واسطا نهبوها نهبا ذريعا إلا محلته في الجانب الشرقي فانه حماها وتركوها تكرمه له بوصية من صاحبهم لهم في ذلك كف بأخرة وتوفي سنة خمس وثلاثين وثلاثمائة
<span data-type="title" id=toc-77>٧٨ </span>- وسألته عن أبي الحسن بن الصفار الكاتب فقال هو هبة الله',NULL,'95',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (53,'ابن أبي الحسين محمد بن موسى أصلهم من النعمانيه سكن أبوه واسطا وتزوج الى آل العرمرم فرزق منهم ولده أبا الحسن هذا ونشأ نشوءا حسنا قرأ القرآن على ابن علان وعلى ابن الصواف وأخذ عنه القراءات ثم بعدهما على الهرمزان أبي بكر أحمد بن علي بن عبد الله العجمي وأسن وكبر وكان اماما في النجوم قوم لثلاثين سنه آتيه قرأت عليه القرآن وهو آخر من حدث عن ابن التباني مات في السابع والعشرين من شهر رمضان سنة ست وثمانين وأربعمائة
<span data-type="title" id=toc-78>٧٩ </span>- وسألته عن أبي منصور بن عبد العزيز العكبري فقال محمد بن محمد بن احمد بن الحسين بن عبد العزيز بن مهران سمع أباه وعمه والعكبريين وببغداد ابن الصلت',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (54,'وابن مهدي ياكلوا وطبقتهم وبالكوفه أبا عبد الله الجعفي وكان كثير المحاسن عزيز الحفظ للحكايات وكان يقول قرأت الأدب على عبد السلام البصري قدم علينا سنة ثمان وستين فسمعنا منه كثيرا لا أعلم من حاله الا أن أبا علي بن البرداني كتب الي بما فيه عليه غميزه ولعله علم من الذي علمت وقد كان أبو علي أحد الحفاظ الأئمه الذين يعلمون ما يقولون
<span data-type="title" id=toc-79>٨٠ </span>- وسألته عن أبي علي الحسن بن عيينه المحدث فقال واسطي نبيل ثقة حدث عنه الميموني سمعت شيخنا أبا المفضل يقول سمعت أبا القاسم عمر بن علي بن أحمد الميموني يقول رأيت أبا علي الحسن ابن عيينه المحدث في المنام بعد وفاته وكأن على أصابع يديه شيئا مكتوبا',NULL,'97',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (55,'بلون الذهب أو لون الزعفران فقلت يا أستاذ أرى على يديك شيئا مليحا فما هو فقال يا بني هذا من كتبتي لحديث رسول الله صلى الله عليه وسلم رحمه الله
<span data-type="title" id=toc-80>٨١ </span>- وسألته عن
أبي تمام ينوي فقال علي بن محمد يعرف بابن بنت الحراني حدث عن أبي الحسين بن كماري وأبي بكر أحمد بن العباس الدوبنائي البزاز ودوبنايا محله من شرقي واسط تجاور قبر يزيد بن هارون لم يكن به بأس إلا أني لا أحدث عنه لا لسوء رأيته به ولا أنه كان يفهم التخليط ةولكن كان سماعه مضطربا القدماء فلم يعجبني هذا
<span data-type="title" id=toc-81>٨٢ </span>- وسألته عن أبي الحسين عبد الله بن أحمد بن شبح فقال كان قارئا صالحا وشروطيا عالما وكان له مسجد وزاويه ينتابه فيها الناس ويقرؤون عليه القرآن وقد سمعت أستاذنا أبا علي الحسن بن علي ابن غراب المقرئ يقول تلقنت القرآن من أبي الحسن وكان يثني عليه
<span data-type="title" id=toc-82>٨٣ </span>- ولأبي الحسن هذا أخ الغرماء أبا علي رأيته أنا شيخا مسنا وما رأيت',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (56,'بالحساب أعلم منه وكان اسماعيل القاضي ينتفع بحسابه في الفرائض ويعول عليه في قسمة التركات
<span data-type="title" id=toc-83>٨٤ </span>- وسألته عن أبي علي بن غراب فقال أستاذنا وعليه تلقنت القرآن وكان ولد قبل الأربعمائة وكان يقول أحمد الله أني ولدت قبلها وكان حسن الحفظ للقرآن كثير الخشوع ختم به جماعة كبيره كتاب الله سمعته يقول سمعت أبا بكر بن القنبائي لصاحب يقول ظهر لي ابليس فسألني أن أقرأ له سورة يس فقرأتها فلما بلغت الى قوله تعالى يا حسرة على العباد ما يأتيهم من رسول إلا كانوا به يستهزئون بكى بكاءا شديدا فقلت ما يبكيك منها يا عدو الله فقال يا أبا بكر وعزة الحق الخالق لقد سمعت الهكم سبحانه وهو يقول يا حسرة على العباد قبل ان أصحهما أباكم آدم بألفي عام
<span data-type="title" id=toc-84>٨٥ </span>- وسألته عن أبي بكر القنبائي فقال ما أعرف اسمه ولا اسم أنه كان زاهدا منقطعا عن الناس له حانوت طحين ربما كلمه في الأحايين الناس من وراء شباكه وكان لا يشهد الجمعه ولا الجماعه ولا يهنئ أحدا ولا يعزيه وكان ابن بحتر المفرئ يلومه على ذلك ولا يرضى عنه ويسبه ويقول ترك الفرض لغير فرض حدثني بكل',NULL,'99',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (57,'ذلك شيخنا أبو علي بن غراب
<span data-type="title" id=toc-85>٨٦ </span>- وسألته عن ابن بحتر فقال كان شيخا حسن الحفظ القرآن وكان وحيدا حدثني شيخنا أبو علي بن غراب قال كان يتهم بأن معه مالا وله ذخيره وكان ينكر ذلك فقرأت عليه يوما سورة براءة فلما بلغت الى قوله تعالى والذين يكنزون الذهب والفضه ولا ينفقونها في سبيل الله فبشرهم بعذاب أليم يوم يحمى عليها في نار جهنم فتكوى بها جباههم وجنوبهم وظهورهم الآيه قال وي وي وي وجعل يلطم على وجهه قال فتحققت ما يقول الناس فيه فلما مات دخل أصحاب السلطان داره فنبشوها فوجدوا جره خضراء مملوءة دنانير فأخذوها وانصرفوا فعرفت أنه كان يولول من أجلها
<span data-type="title" id=toc-86>٨٧ </span>- وسألته عن أبي الحسين بن الرؤاسي فقال هو محمد بن علي بن الحسن الفقيه الشافعي الإمام علق عن أبي حامد تعليقه الكبير وسمع من أبي بكر بن الباقلاني الأشعري جل تصانيفه وسمع أبا بكر بن بيري وطبقته الحديث وولى القضاء على البطائح والأعمال السفلى',NULL,'100',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (58,'من واسط وأملى في الجامع بعد الأربعين حدثنا عنه صدقة كاتب الوقف وكان صاحبه ومتخصصا به ومات في أعماله التي كان يتولاها وكتبه هناك معه فتفرقت وضاعت وكان ابنه أبو عبد الله صحب أبا اسحاق الفيروزابادي وعلق عنه ومضى الى نيسابور ليرى أبا المعالي الجويني فمات هناك وانقرض عقبه
<span data-type="title" id=toc-87>٨٨ </span>- وسألته عن أبي الطيب بن كماري فقال كان قاضيا بواسط عزل به أبو تمام بن أبي خازم وكان شيخا صالحا متدينا موسرا هجم عليه اللصوص فقتلوه في داره سنة اثنتين وعشرين وأربعمائة ولم يحدثنا عنه أحد بمسند غير أن أصحابنا
كلهم لم يختلفوا في حسن صونه قال لي أبو الحسن بن الصفار شيخنا كنت في حجرة بعد موت أبي فباع لي من كتب أبي ومن أدواته بأربعمائة دينار واشترى لي بها ضيعة هي لورثته الى اليوم',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (59,'<span data-type="title" id=toc-88>٨٩ </span>- وسألته عن أبي جعفر بن بمفسدة النعماني فقال مرضي الصون سمع أبا بكر المفيد وحدث عنه بأحاديث وعثمان وسمع أبا محمد بن السقاء بواسط وكان ابن ابنه حسن الحفظ للقرآن يؤم بنور الدوله دبيس بن مزيد بالتراويح في شهر رمضان وكانت له عندهم منزلة رحمه الله',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (60,'<span data-type="title" id=toc-89>٩٠ </span>- وسألته عن أبي بكر بن طاوان فقال أحمد بن محمد بن عبد الوهاب بن طاوان السمسار يعرف بشراره كان يستملي على الشيوخ قديما بواسط سمعت أبي وأبا الغنائم بن بالهيثم وأستاذنا أبا علي بن غراب يقولون رأينا شراره جالسا على حجر عال بين يدي أبي الحسين بن كماري وهو يصيح بأعلى صوته بعد صلاة الجمعه اللهم صل على محمد المختار وعلى أبي بكر صاحب الغار وعلى عمر ممصر الأمصار وعلى عثمان شهيد الدار وعلى علي قاتل الكفار وعلى جميع الصحابه من المهاجرين والأنصار خذوا الإملاء رحمكم الله فيكتب الناس حينئذ سمع أبا الفرج الخيوطي وأبا بكر بن بيري والناس إلا أنه كان لا يميز يسأله الإنسان اخراج حديث فيترك أن يحدثه عن الخيوطي وهو متقدم الإسناد فيه ويحدثه عن ابن القصاب وهو حاضر معه أكثر عنه شيخنا أبو الحسن بن الصفار مات بعد الأربعين وأربعمائة',NULL,'103',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (61,'<span data-type="title" id=toc-90>٩١ </span>- وسألته عن أبي الحسن أحمد بن محمد بن سمنان المؤدب فقال أملى في الجامع بعد أبي الحسين بن كماري سمع أبا الحسن البكائي الكوفي ونظراءه ومات قبل الثلاثين وأربعمائة حدثنا عنه صدقة كاتب الوقف وغيره
<span data-type="title" id=toc-91>٩٢ </span>- وسألته عن أبي بكر الهرمزان فقال أحمد بن علي بن عبد الله الطرسوسي من أبناء المحدثين جلس صدرا للقراء في جامع واسط وكان حسن الحفظ للقرآن قرأ عليه شيخنا أبو الحسن بن الصفار وغيره وكان قرأ على أبي الحسن بن علان والد أبي علي وعلى أبي حفص الكتاني ببغداد وغيرهما وهو صاحب ليلة الصافات وانما قيل ذلك لأنه قام بالقرآن في ليلة نصف شعبان بصوت جهير يسمع على بعد من الأرض واجتمع اليه الناس في مسجد موسى وهو الجامع الشرقي بواسط وواسط حينئذ فيها القراء والأئمه فأخذوه بأعينهم ورصدوا عليه الغلط وهو يمر مر السحاب الى آخر سورة الصافات في ركعة ثم قام في الثانيه',NULL,'104',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (62,'فأخذ بقية الختمه لم يخف منها حرف واحدا عن أسماع الناس وما سمعنا بمثل هذا عن أحد من أهل واسط ولا غيرها رحمة الله عليه وسمعت شيخنا أبا الحسن بن الصفار يقول كنت في تلك الليله حاضرا في المسجد الجامع ومعنا أبو علي غلام الهراس الذي صار بعده صدرا للقراء في الجامع وهو يتسمع عليه الى أن بلغ الى قوله تعالى من سورة الأعراف الذين يتبعون الرسول النبي الأمي الذي يجدونه مكتوبا عندهم في التوراة والإنجيل يأمرهم بالمعروف وينهاهم عن المنكر الآية فصاح أبو علي بأعلى صوته جعله الله شفيعك يا أستاذ لم يشتهر بالحديث اشتهاره بالقرآن سمعت أبا الحسن علي بن محمد بن الطيب المالكي يقول لشيخنا أبي منصور بن عبد العزيز العكبري ونحن نكتب بين يديه يا شيخ دعنا من الحديث فانا سكارى منه هات الملح الانشادات والحكايات
<span data-type="title" id=toc-92>٩٣ </span>- وسألته عن أبي اسحاق الرفاعي فقال هو ابراهيم بن',NULL,'105',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (63,'سعيد كان ضريرا أصله من عبد أمي قدم صبيا ذا فاقة الى واسط فدخل الجامع الى حلقه عبد الغفار الحضيني فتلقن القرآن وكان معاشه من أهل الحلقه ثم أصعد الى بغداد فصحب أبا سعيد السيرافي وقرأ عليه كتاب شرح سيبويه وسمع منه كتب اللغه والدواوين وعاد الى واسط وقد مات عبد الغفار والحاصل صدرا يقرئ الناس في الجامع ونزل الزيديه من واسط وهناك تكون الرافضه والعلويون فنسب الى مذهبهم ومقت على ذلك وجفاه الناس',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (64,'وكان شاعرا حسن الشعر جيده ومن شعره وجدته في كتاب أبي غالب محمد بن احمد بن سهل النحوي
وأحبه ما كنت أحسب أنني أبلى ببينهم فبنت وبانوا
نأت المسافة فالتذكر حظهم مني وحظي منهم النسيان
توفي سنة أحدى عشرة وأربعمائة سمعت أبا نعيم أحمد بن علي ابن أخي سكرة المقرئ الإمام يقول رأيت جنازة أبي اسحاق الرفاعي مع غروب الشمس تخرج الى الجبانه وخلفها رجلان فحدثت بهذا شيخنا أبا الفتح بن المختار النحوي فقال سمى لك الرجلين فقلت لا',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (65,'فقال كنت أنا أحدهما وأبو غالب بن بشران الآخر وما صدقنا أننا نسلم خوفا أن نقتل ومن عجائب ما اتفق ان هذا الرجل توفي وكان على هذا الوصف من الفضل وكانت هذه حاله وتوفي في غد وفاته رجل من حشو العامة يعرف بدبا كان سواديا فأغلق البلد لأجله وصلى عليه الناس كافة ولم يوصل الى جنازته من كثرة الزحام
<span data-type="title" id=toc-93>٩٤ </span>- وسألته عن أبي بكر محمد بن علي بن مهدي فقال هو خال أبي عبد الله السقطي سمع الزعفراني وأبا عيسى جبير بن محمد السمسار وأبا الطيب عبد الله بن فرخ الجذوعي وكان ثقة ثبتا حدث عنه علي العجمي الطرسوسي والد الهرمزان وغيره
<span data-type="title" id=toc-94>٩٥ </span>- وسألته عن أبي محمد بن السقاء فقال عبد الله بن محمد بن',NULL,'108',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (66,'عثمان المزني مزينة مضر لم يكن سقاء وانما هذا لقب نبز به من وجوه الواسطيين وذوي الثروه منهم والحفظ والإسناد والتقدم فيه رحل به أبوه الى بغداد فسمع أبا بكر بن أبي داود والبغوي وابن صاعد وغيرهم ثم رحل به الى الموصل فسمع أبا يعلى ودخل به الكوفه فسمع ابن المجدر وابن زيدان وأصحاب أبي كريب وحج به فسمع أبا سعيد الفضل بن محمد الجندي وعاد به الى البصره فسمع أبا خليفه وخرج به الى تستر فسمعه هناك من قوم كان عندهم حديث نصر الجهضمي وعاد الى واسط وبارك الله له في سنه وعلمه وأملى بواسط
واتفق أنه أملى حديث الطائر فلم تحتمله أنفس العوام فوثبوا به',NULL,'109',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (67,'وأقاموه وغسلوا موضعه فمضى ولزم بيته وكان لا يحدث أحدا من الواسطيين فلهذا قل حديثه عندهم واذا جاءه الرجل الغريب أحلفه بالله الذي لا اله إلا هو ما وضعك أحد من أهل واسط ولا تعط حديثي أحدا منهم فاذا حلف له حدثه توفي سنة احدى وسبعين وصلى عليه ابو مصعب البزاز وصلي عليه مرة أخرى صلى عليه ابن أخيه ودفن خلف مسجده في طرف شارع الب ٤ صريين وقبره الآن معروف يزار حدثني بكل ذلك شيخنا أبو الحسن المغازلي
<span data-type="title" id=toc-95>٩٦ </span>- وسألته عن الخليل بن أبي رافع الطحان فقال الغرماء أبا بكر سمع تميم بن المنتصر وشارك بحشلا في أكثر شيوخه آخر من حدث عنه أبو عبد الله الحسين بن محمد العلوي صاحب ابن مبشر توفي أظن سنة ثلاث عشرة وثلاثمائه',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (68,'<span data-type="title" id=toc-96>٩٧ </span>- وسألته عن أبي بكر بن رزق الله الحداد فقال اسمه أحمد ويعرف ببكير شارك بحشلا في أكثر شيوخه آخر من حدث عنه أبو عبد الله العلوي لا أعلم من حاله إلا السلامه
<span data-type="title" id=toc-97>٩٨ </span>- وسألته عن بحشل فقال أبو الحسن أسلم بن سهل بن أسلم بن زياد بن حبيب الرزاز منسوب الى الرزازين المحله السفلى من واسط ومسجده هناك وداره ثقة ثبت امام جامع يصلح للصحيح وجده لأمه أبو محمد وهب ويقال وهبان بن بقيه جمع تاريخ الواسطيين وضبط أسماءهم ورتب طبقاتهم وكان لا مزيد عليه في الحفظ والإتقان توفي سنة ثمانين ومائتين قبلها أو بعدها بقليل',NULL,'111',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (69,'<span data-type="title" id=toc-98>٩٩ </span>- وحدث عنه بامتناعهم أبو بكر محمد بن عثمان بن سمعان المعدل وكان يضاهيه في الحفظ والإتقان وقد شركه في أكثر شيوخه مات قبل الثلاثين وثلاثمائة وحدث عنه بتاريخ بحشل أبو بكر بن بيري وأبو الحسن
<span data-type="title" id=toc-99>١٠٠ </span>- علي بن الحسن الجاذري الصلحي أصله من فم الصلح وكان ثقة ثبتا مستقيم الروايه آخر من حدث عنه أبو الحسن بن مخلد وكان شيخنا أبو البركات بن نفيس يقول سمعت منه وما صح عندي ذلك
<span data-type="title" id=toc-100>١٠١ </span>- وسألته عن أحمد بن سنان القطان فقال أبو جعفر أحمد بن سنان بن أسد بن حبان القطان توفي سنة أربع وخمسين أو ثلاث',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (70,'وخمسين ومائتين رأيت ذلك بخط أبي المفضل بن مخلد جمع المسند وكان من الحفظ والعداله الى حد لا مزيد عليه وقد أخرج عنه البخاري في كتابه الصحيح حديثا واحدا لم يخرج عنه غيره وهو حديث زيد بن أسلم رأيت عمر يقبل الحجر
<span data-type="title" id=toc-101>١٠٢ </span>- وابنه أبو محمد جعفر يضاهيه في الجلاله والثقه حدث عنه أبو محمد ابن السقاء وغيره
<span data-type="title" id=toc-102>١٠٣ </span>- وسألته عن أبي الطيب بن فرخ فقال اسمه عبد الله الجذوعي سمع أبا بكر بن أبي الدنيا وأكثر عنه وحدث عنه أبو بكر بن',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (71,'مهدي ثم علي العجمي عن ابن مهدي وروايته مستقيمه ولا أعلم عليه إلا الخير توفي بعد العشرين والثلاثمائة
<span data-type="title" id=toc-103>١٠٤ </span>- وسألته عن أبي عيسى جبير بن محمد السمسار فقال سمع أبا علي الحسن بن منصور الشعيري وغيره حدث عنه أبو بكر بن مهدي واشتهر بالرواية عنه وعندنا كثير من حديثه حدثنا به صدقة كاتب الوقف عن علي العجمي والد الهرمزان عن ابن مهدي عنه وأبو علي الشعيري هذا أحد مشيخة يعقوب بن سفيان
<span data-type="title" id=toc-104>١٠٥ </span>- وسألته عن جابر بن الكردي فقال ابو العباس حدث عن يزيد بن هارون وحدث عنه بحشل وغيره ولم يقل فيه سوءا
<span data-type="title" id=toc-105>١٠٦ </span>- وسألته عن أبي بكر محمد بن موسى البابسيري فقال هو',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (72,'منسوب الى محلة من شرقي واسط حدث عنه علي العجمي وغيره وكان لا بأس به
<span data-type="title" id=toc-106>١٠٧ </span>- قلت له يروي عن محمود بن محمد عن عمرو بن أبي عاصم النبيل فقال محمود هذا هو ابن محمد العدل أبو عبد الله أحد شيوخ ابن السقاء معروف بالثقه
<span data-type="title" id=toc-107>١٠٨ </span>- وسألته عن أبي البركات التمار فقال اسمه محمد بن علي سمع أبا الحسن بن خزفه وطبقته حديثه الآن ثم أبي السعادات الخطيب وأبي العز القلانسي وغيرهما
<span data-type="title" id=toc-108>١٠٩ </span>- وسألته عن أبي نعيم أحمد بن علي ابن أخي سكرة المقرئ',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (73,'فقال شيخنا كان صدرا في الجامع سمع أبا بكر أحمد بن العباس الدوبنائي وأبا القاسم عبيد الله بن طاهر العلوي ابن عم أبي عبد الله وكان أبو القاسم العلوي هذا سمع أبا محمد بن شوذب وأكثر عنه
<span data-type="title" id=toc-109>١١٠ </span>- والدوبنائي سمع أبا محمد بن ماسي فأكثر عنه وكان متحققا بالسنه واحد من أصحابنا يقول لما نزل الوزير المقرئ بواسط في درب الواسطيين مكث أياما لم يحضر مسجدهم فدخل عليه أبو بكر هذا فقال يا شيخ يا أستاذ يا وزير مهما شئت كن ان كنت تحضر مسجدنا هذا في الصلوات الخمس وإلا والطاعه أيها الشيخ ثم انتقل عنهم من يومه وسمعت أبا نعيم يقول سألنا أبا بكر أن التابعين لنا فقال قد أجزت لكم ولحبل الحبله وهذا طريف من كلام أصحابنا',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (74,'وكان أبو نعيم صالحا يصوم وقته وعرضت عليه الشهادة فرغب عنها وكان حسن الأخذ للقرآن رحمه الله
<span data-type="title" id=toc-110>١١١ </span>- وسألته عن علي بن محمد بن عيسى بن موسى الحصري شيخ الغندجاني فقال سمع منه ببغداد وكان يحدث عن المصري
<span data-type="title" id=toc-111>١١٢ </span>- وسألته عن حمد بن محمد بن عثمان الأصبهاني فقال قدم علينا واسطا وسمعنا منه وكنت رأيته ببغداد واجتمعنا هناك في مجالس وكان قد سمع من الأصبهانيين كابن منده وغيره وكان له حفظ ومعرفه فتدير البصره ومات بها
<span data-type="title" id=toc-112>١١٣ </span>- وسألته عن أبي مسلم الليثي البخاري فقال قدم علينا واسطا في سنة تسع وخمسين وقال كتبت وكتب لي عشر رواحل وسألت عنه أبا بكر الدقاق ابن الخاضبه ببغداد فأثنى عليه وقال كان له أنس بالصحيح',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (75,'وبلدينا أبو طاهر بركه بن سنان الحوزي يقول ناظرت أبا الحسن المغازلي في التفضيل بين مالك والشافعي ففضلت الشافعي لأني أنتحل مذهبه وفضل مالكا لأنه كان ينتحل مذهبه فاحتكمنا الى أبي مسلم الليثي البخاري ففضل الشافعي فغضب أبو الحسن وقال لعلك على مذهبه فقال نحن أصحاب الحديث الناس على مذاهبنا فلسنا على مذهب أحد ولو كنا ننتسب الى مذهب أحد لقيل أنتم تضعون له الأحاديث
ووعده أبو طاهر بلدينا هذا بأرز يطعمه اياه فتمادى الأمر فيه يومين أو ثلاثة فقال يا أبا طاهر في الحديث الصحيح عن رسول الله صلى الله عليه وسلم
آية المنافق ثلاث اذا حدث كذب واذا وعد أخلف واذا ائتمن المطلوب قال فطبخت له الأرز وأطعمته اياه وانحدر من عندنا الى البصره وتوجه منها الى الأهواز فبلغنا وفاته
<span data-type="title" id=toc-113>١١٤ </span>- وسألته عن عبد الله بن عطاء الهروي فقال رأيته ببغداد ملتحفا بأصحابنا ومتخصصا بالحنابله يخرج لهم الأحاديث المتعلقه',NULL,'118',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (76,'بالصفات ويرويها لهم وأضداده من الأشعريه يقولون هو يضعها وما علمت فيه ذلك
<span data-type="title" id=toc-114>١١٥ </span>- وسألته عن أبي محمد الطبسي فقال رأيته ببغداد وقال لي في عزمي أن لا أحدث إلا بالصحيح ففيه غنى عن غيره وكان له رواء وأبهه ومعرفه صالحه وسمعت منه
<span data-type="title" id=toc-115>١١٦ </span>- وسألته عن الحميدي فقال أبو عبد الله محمد بن أبي نصر واسم أبي نصر فتوح بن عبد الله قدم واسطا وسمع من أبي غالب وأبي تمام والجماعه في الوقت وأقام بها مدة حتى نسخ الكامل للمبرد وقرأه على أبي غالب وكان يرويه عن أبي الحسين بن دينار عن ابي علي الطوماري عن المبرد
وأصعد الى بغداد ولقيته هناك وجالسته وسمعت منه وكان أكثر الناس فضلا وعلما وحفظا ودراية خرج تاريخ المغاربه وكان له',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (77,'شعر حسن حدث عنه أبو بكر الخطيب
<span data-type="title" id=toc-116>١١٧ </span>- وسألته عن أبي بكر محمد بن أحمد بن عبد الباقي الدقاق فقال كان علامه في الأدب قدوة في الحديث جيد اللسان جامعا لخلال الخير ما رأيت ببغداد من أهلها أحسن منه قراءة للحديث ولا أعرف بما يقوله
<span data-type="title" id=toc-117>١١٨ </span>- وسألته عن ابي الحسن علي بن أحمد الأنصاري الأندلسي النحوي فقال قدم علينا وكان فاضلا في النحو متقدما في العربيه وكان يتتبع أسماء من يحضر السماع فيكتبها عن آخرها ولا يخل بأحد فقيل له في ذلك فقال هذا عاجل ثوابه وإلا فمن أين لنا علم بطول العمر حتى نرويه وانحدر من عندنا الى البصره فسمع بها من أصحاب أبي عمرو قال لي ابن وتتداخل أبو الحسن وكان اماما في الخير بارعا في العلوم غاية في الصلاح سمعت أبا الحسن الأنصاري هذا يقول للشاكر أبي عمر الحسن بن علي بن غسان وقد أنشده شعرا له هذا شعر فيه روح',NULL,'120',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (78,'وخرج الى مكة فمات في طريقها وكانت له معرفة بالحديث حسنه وكان على وجهه أثر العباده
<span data-type="title" id=toc-118>١١٩ </span>- وسألته عن أبي زيد الأصبهاني فقال عبد الله بن عبد الملك قدم واسطا سنة أربع وستين وسمعته يقرأ الغندجاني وسألني عن اسمي ليكتبه فلم أعلمه به جهلا مني بما في ذلك من الفضيله وكان حافظا متقنا تدل انتقاءاته على علمه
<span data-type="title" id=toc-119>١٢٠ </span>- وسألته عن رضوان الأهوازي فقال لا أعرف اسم أنه كان فقيها زاهدا أقام بواسط مدة طويله وقرأ عليه من أهلها جمع وانتفعوا به ثم بدا له فرحل عنها الى الطيب فمات هناك وسمعت أبا المفضل بن مخلد يقول مرض رضوان الأهوازي بواسط ثم أبل فدخلت عليه وقد وصف له الطبيب الفروج فجئته به فلما رآه في يدي قال ما هذا فقلت وصفه الطبيب فأنا أذبحه لك',NULL,'121',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (79,'فقال لالالا فقلت ولم فقال لا أريد أن تتلف نفس في حقي فقلت هذا لا بد منه فقال ان كان لا بد أن تفعل فخذ لي رطل لحم وأصلحه فقلت وما الفرق بين هذا وذاك وكلاهما حيوان فقال ذاك لم أقصد به أنا
<span data-type="title" id=toc-120>١٢١ </span>- وسألته عن أبي الفتح محمد بن أحمد بن خلدون الأخباري وقد أنشدنا شيئا من شعره فقال بصري الأصل جوال في الآفاق يقص علىالطريق الوعظ والأخبار قدم علينا
<span data-type="title" id=toc-121>١٢٢ </span>- وسألته عن أبي علي الحسن بن النجم بن بنان الموصلي فقال قدم واسطا وكان متقدم الميلاد قال ابن أبي الصقر وكان نزل عليه بواسط انه ولد سنة ثمانين وثلاثمائة وكان شاعرا هجاء يقطع أعراض الناس وله في أناس من واسط رؤساء وغيرهم أهاج قبيحه ولما تجدد ببغداد بأمر الله أمير المؤمنين ما تجدد من خروجه عن داره بسط لسانه فشتم العباس بن عبد المطلب وولده قاطبة فلما عاد الإمام الى داره طلبه الهاشميون فهرب الى البصره وأخفى شخصه في المطلوب بها فعرف ذلك ابن راويه الهاشمي فدخل عليه فقتله ولقي عمله',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (80,'<span data-type="title" id=toc-122>١٢٣ </span>- وسألته عن أبي عبد الله محمد بن علي بن عيسى القارئ فقال أحد القراء سمع ابن بيري وغيره حدث عنه أبو الحسن المغازلي
<span data-type="title" id=toc-123>١٢٤ </span>- وسألته عن عثمان بن علي بن كامخ الصوفي فقال شيخ محتشم من شيوخ الصوفيه وكان يعرف طرفا من الكلام على مذهب الأشعري قرأ على أبي الحسين الرؤاسي وكان صاحب زاويه ورباط بقرية عبد الله بن عبيد الله بن طاهر أسفل واسط بفرسخين سمع أبا الحسن العطار سمعنا منه وكان لا بأس به ناهز التسعين سنه
<span data-type="title" id=toc-124>١٢٥ </span>- وسألته عن أبي طالب الرامي فقال كان من أولاد الرؤساء نبغ في الشعر وتقدم في رمي أن الحرفه كانت غالبة عليه خرج الى صريفين الآس فأقام بها ثم انتقل عنها الى القاوسان فاقام بها ومكث هناك يتردد في الفقر الى أن جاءه أجله',NULL,'123',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (81,'وكان يشعر شعرا حسنا منه ما أنشدنا لنفسه
لما خلت واسط ممن ألوذ به ولم أجد من يراعي حرمة الأدب
خرجت عنها الى الرستاق منتقلا تنقل الشيخ من ضعف الى عطب
وايضا من طول ادباري وغدر الزمان قعدت للتعليم في القاوسان
فليت شعري بعد كوني بها أقعد للتعليم في البوزدان
سبحان من صيرني مدبرا أفضح نفسي في مكان مكان
القاوسان والبوزدان قريتان في شرقي واسط من سوادها
<span data-type="title" id=toc-125>١٢٦ </span>- وسألته عن أبي تغلب محمد بن الحسن بن شاذان الكاتب فقال كان كاتبا للملك العزيز أيام مقامه بواسط فلما خرج عنها الى بلاد بكر خرج معه الى هناك فهلك وكان حسن الشعر أنشدونا عنه
آخر السؤالات والحمد لله رب العالمين وصلواته على سيدنا محمد وعلى آله وصحبه أجمعين
أنشدنا الشيخ الإمام الحافظ أبو طاهر أحمد بن محمد بن أحمد بن محمد',NULL,'124',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (82,'السلفي الأصبهاني قال أنشدنا الشيخ أبو علي أحمد بن محمد بن جعفر بن المختار المعدل بواسط لنفسه وأفادينه خميس بن علي الحافظ
كم جاهل متواضع ستر التواضع جهله
ومبرز في علمه هدم التكبر فضله
فدع التكبر ما حييت ولا تصاحب أهله
فالكبر عيب للفتى أبدا يقبح فعله
وله ما هذه الدنيا بدار مسرة
فتخوفي مكرا لها وخداعا بينا الفتى فيها يسر بنفسه
وبماله يستمتع استمتاعا حتى سقته من المنية شربة
وحمته منها بعد ذاك رضاعا فغدا بما كسبت يداه رهينة
لا يستطيع لما اعتراه دفاعا لوكان ينطق قال من تحت الثرى
ليحسن العمل الفتى ما اسطاعا
أخبرنا أبو الكرم نصر الله بن محمد بن محمد بن مخلد الأزدي بقراءتي عليه بواسط في شهر ربيع الآخر سنة خمسمائة أنا أبو تمام علي بن محمد بن',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26209] VALUES (83,'الحسن العبدي أنا عبيد الله بن عبد الرحمن بن محمد الزهري ثنا عبد الرحمن بن الحسن بن منصور بن شهريار الذهبي ثنا ابراهيم بن هانئ ثنا عثمان بن صالح ثنا ابن وهب أخبرني مالك قال سمعت زيد بن أسلم يقول في هذه الآيه نرفع درجات من نشاء قال بالعلم
آخر الجزء والحمد لله رب العالمين وصلواته على سيدنا محمد خاتم النبيين وعلى آله وصحبه أجمعين
كتبه أحمد بن رضوان بن اسماعيل المقدسي الشافعي وذلك يوم الخميس لأربع ان بقين من رجب سنة خمس وثلاثين وستمائه',NULL,'126',NULL,NULL,'0');
