-- Insert data for b26230
INSERT INTO [b26230] VALUES (1,'بسم الله الرحمن الرحيم
قرأت على الشيخ الامام ابي القاسم اسماعيل بن احمد السمرقندي في داره في عشر ذي الحجة سنة عشرين وخمس مئة قلت له اخبركم ابو القاسم يوسف بن الحسن بن محمد التفكري الزنجاني قراءة عليه في ذي القعدة سنة سبعين واربع مائة قال اخبرنا ابونعيم احمد بن عبد الله بن احمد بن اسحاق بن مهران قراءة عليه من اصله قال حدثنا ابو علي محمد بن احمد بن الحسين بن الصواف حدثنا ابو جعفر محد بن عثمان بن ابي شيبة قال:
<span data-type="title" id=toc-1>١</span>- سمعت أبي يقول: سمعت قران بن تمام يقول سمعت عثمان بن الاسود يقول كنا نجالس محمد بن عبد الرحمن بن ابي بكر فلا نقوم من عنده الا وقد نفع ـ ان شاء الله ـ احدنا علما حسنا.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (2,'<span data-type="title" id=toc-2>٢</span>- وسمعت أبي يقول قلت لابي اسامة اخبرني عن ابي العوام عمران القطان كان يذهب الى شيء من القدر قال كان عمران القطان من اخص الناس بقتادة وكانوا يقولون انه يميل اليه الا انهم لم يثبتوا عليه شيئا
<span data-type="title" id=toc-3>٣</span>- وسمعت أبي يقول لما حدثنا وكيع عن يزيد بن زياد الدمشقي سالت وكيعا عنه فقال كان هذا رفيعا في اهل الشام في الفقه والصلاح.
<span data-type="title" id=toc-4>٤</span>- وسمعت ابي يقول كنا في مسجد وكيع جماعة فتناظرنا في عبد الرحمن بن عجلان حين حدثنا وكيع عنه فقمت اليه',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (3,'<span data-type="title" id=toc-5>٥</span>- وسمعت ابن نمير يقول وسئل عن صالح الفزاري فقال كان ثقة وقد كتبنا عن ابي معاوية عنه وكان ابو معاوية يذكره باحسن الذكر
<span data-type="title" id=toc-6>٦</span>- وسمعت أبي يقول تذاكرنا انا وابو نعيم مسافرا الجصاص فقلت له كنتم تتهمونه في في شيء من رايه فقال لي كان مسافر يذهب مذهب الحارث بن حصيرة وما سمعت منه شيئا اعتد به الا شيئا واحدا سمعته يقول اجد قلبي لا يحب عثمان.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (4,'قال ابو جعفر وكان الحارث بن حصيرة محترقا.
<span data-type="title" id=toc-7>٧</span>- وسمعت أبي يقول سألت ابا نعيم عن مسلم الحنفي الذي حدث عنه سفيان فقال كان مسلم احد الثقات المأمونين
<span data-type="title" id=toc-8>٨</span>- وسمعت أبي يقول قلت لابي نعيم هذا الشيخ الذي حدث عنه سفيان يقال له عيسى بن عمر بن عبد العزيز فقال اذا ازداد النبيذ على طول الترك جودة فلا خير فيه فقال هذا شيخ ولم ينسبه سفيان',NULL,'100',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (5,'قال ابو علي بن الصواف في كتابي عيسى بن عمر واظنه عيسى عن عمر
<span data-type="title" id=toc-9>٩</span>- وسمعت أبي يقول قلت لابي نعيم حسن بن عياش كيف كان عندكم فقال لي كان ضعيفا لم يزل
<span data-type="title" id=toc-10>١٠</span>- وسمعت ابراهيم بن ابي بكر بن عياش وسالته عن موت حسن بن عياش فقال مات قبل ابي بكر بعشر سنين.
<span data-type="title" id=toc-11>١١</span>- وسمعت ابراهيم بن ابي بكر يقول ما بقي احد يطلب حديثه ما عند الله الا احمد بن يونس
<span data-type="title" id=toc-12>١٢</span>- قال ابو جعفر ورايت سالم بن ابراهيم بن ابي بكر بن',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (6,'عياش وكان رجلا فاتكا ومعه ابن ابي يوسف القاضي فقالا نتعرض للمتوكل فتعرضا له فقال له سالم يا امير المؤمنين انا سالم بن ابراهيم بن ابي بكر بن عياش وليك وولي آبائك وقال له ابن ابي يوسف انا ابن ابي يوسف خادمك وخادم آبائك فبعث الى ابي فسأله عن صحة نسب سالم فاخبره فأعطاه مئتي دينار واما ابن ابي يوسف فقال له علي خمسون ألف درهم دين فلم يعط شيئا.
<span data-type="title" id=toc-13>١٣</span>- وسمعت أبي يقول لما حدثنا زيد بن الحباب عن ربيع بن ابراهيم الحنفي وسالته عن فقال كان والله من الصادقين.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (7,'<span data-type="title" id=toc-14>١٤</span>- وسمعت أبي يقول قلت لمحمد بن بشر العبدي الحسن العبدي الذي حدث عنه علي بن المبارك فقال لي كان سيدا من سادات قومنا وكان ابن عم زيد بن صوحان.
<span data-type="title" id=toc-15>١٥</span>- وسمعت علي بن المديني يقول قال عبد الرحمن بن مهدي عن ابي سليمان بن جعفر بن سليمان قال قال لنا محمد بن واسع عليكم بمالك نعم الرجل مالك وثابت وان ابا عمران الجوني لحسن الحديث.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (8,'<span data-type="title" id=toc-16>١٦</span>- وسمعت علي بن المديني يقول سمعت عبد الرحمن بن مهدي قال سمعت سفيان وذكر منصورا فقال ربما حدث عنه رجلين عن ابراهيم كان لا يرسل شيئا.
<span data-type="title" id=toc-17>١٧</span>- قال وسمعت علي بن المديني يقول سمعت عبد الرحمن بن مهدي يقول حدث سفيان بحديث زهير عن ابي اسحاق عن حارثة بن مضرب عن عمر للفارس سهمان فانكره.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (9,'<span data-type="title" id=toc-18>١٨</span>- وسمعت يحيى بن مطيع النسائي يقول سمعت يحيى بن عبد الملك بن ابي غنية يقول سمعت مالك بن مغول يقول كانت بنو عجل تفخر بمقاتل بن بشير العجلي وكنت اغدوا اليه فاجد مسجد بني عجل الاكابر قد بكروا اليه فكان اذا تكلم انصتوا لكلامه وحفظوا عنه ما يقول وتدارسه بعضهم على بعض وكنت اليوم الذي اتيه فيه واجالسه تصغر الدنيا في عيني وقلبي.
<span data-type="title" id=toc-19>١٩</span>- وسمعت عبد الله بن براد يقول سمعت ابن ادريس يقول سمعت الحسن بن الفرات يقول اتينا ابن ابي مليكة وجالسناه فخرجنا ونحن نقول ما راينا بالحجاز رجلا يعدله عندنا
<span data-type="title" id=toc-20>٢٠</span>- وسمعت عمي ابا بكر يقول سالت مالك بن اسماعيل عن حديث فقال حدثنيه الشيخ الصالح ابراهيم بن',NULL,'105',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (10,'الزبرقان عن ابي روق عن محمد بن جحادة عن ابيه عن عائشة قال كان رسول الله صلى الله عليه وسلم يقرا انه عمل غير صالح
<span data-type="title" id=toc-21>٢١</span>- وسمعت أبي يقول قلت لجرير اخبرني عن عمران',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (11,'الخياط الذي حدث عنه مغيرة فقال كان عمران هذا مولى لبني ضبة وكان احد الصالحين
<span data-type="title" id=toc-22>٢٢</span>- وسمعت أبي يقول سمعت ابا اسامة يقول قلت لمهدي بن ميمون اخبرني عن عمران القصير كان يذهب الى شيء من القدر فقال لقد برأ الله عمران من ذلك ان يكون يذهب هذا المذهب ولقد قلت هجرا فاستغفر الله منه
<span data-type="title" id=toc-23>٢٣</span>- حدثنا منجاب بن الحارث قال سمعت ابا عامر الاسدي يقول سمعت سفيان الثوري يقول سمعت عبد الملك بن ابي بشير يقول كنت اذا لقيت عبد الله بن المساور فجر لي علم ابن عباس',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (12,'<span data-type="title" id=toc-24>٢٤</span>- وسمعت ابراهيم بن ابي معاوية يقول كنت مع ابي ذات ليلة فذكر يوسف بن ميمون فقال لي يا ابراهيم كان يوسف بن ميمون ممن رفعه الله بالصدق
<span data-type="title" id=toc-25>٢٥</span>- وسمعت عبد الله بن براد يقول سمعت اسحاق بن ابي اسحاق الشيباني يقول سمعت ابي يقول كان ابن علاقة من المؤمنين الصادقين.
<span data-type="title" id=toc-26>٢٦</span>- سمعت علي بن المديني يقول سمعت عبد الرحمن بن مهدي وذكر عنده ابو جعفر الخطمي قال كان ابو جعفر الخطمي وأبوه وجده حبيب بن خماشة قوما توارثوا الصدق بعض عن بعض.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (13,'<span data-type="title" id=toc-27>٢٧</span>- سمعت الحسن بن سهل يقول سمعت حفص بن عمر يقول سمعت ابن ادريس الاودي يقول رحمه الله على الحر بن صياح فقد كنت القاه شبيها بالمهيم بما هو عليه من العمل والعبادة
<span data-type="title" id=toc-28>٢٨</span>- وسمعت مليح بن وكيع يقول سمعت الوليد بن مسلم يقول سمعت الاوزاعي يقول حدثني الثقة المطعم بن المقدام.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (14,'أن رسول الله صلى الله عليه وسلم قال ما خلف عبد على اهله افضل من ركعتين يركعهما عندهم حين يريد سفرا
<span data-type="title" id=toc-29>٢٩</span>- سمعت محمد بن يزيد يقول سمعت ابا عامر العقدي يقول حدثني الثقة عثمان بن نسطاس قال سمعت عطاء بن يسار يقول في هذه الأية (ان الحسنات يذهبن السيئات) قال ان من الحسنات سبحان الله والحمد لله ولا اله الا الله والله اكبر ولا حول ولا قوة الا بالله وهن الباقيات الصالحات.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (15,'<span data-type="title" id=toc-30>٣٠</span>- سمعت ابراهيم بن ابي معاوية يقول سمعت ابي يقول حدثني الشيخ الصالح شعيب بن كيسان عن ثابت بن الضحاك ولا يزالون مختلفين الا من رحم ربك ولذلك خلقهم قال الرحمة
<span data-type="title" id=toc-31>٣١</span>- وسمعت منجاب بن الحارث قال سمعت علي بن مسهر يقول كان يحيى بن ايوب البجلي عندنا من الصالحين',NULL,'111',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (16,'كان ممن ادبه عون بن عبد الله
<span data-type="title" id=toc-32>٣٢</span>- سمعت زكريا بن يحيى الكسائي يقول سمعت يحيى بن عيسى الرملي يقول حدثني الثقة بدر بن خليل عن ابي جعفر حتى اذا استيئس الرسل استيئس الرسل من قومهم ان يؤمنوا وظنوا انهم قد كذبوا قال وظن قوم الرسل ان الرسل قد كذبوا
<span data-type="title" id=toc-33>٣٣</span>- سمعت ابن نمير يقول كان حميد بن سلمانثقة
<span data-type="title" id=toc-34>٣٤</span>- وسمعت عبد الله بن عمر بن ابان يقول سمعت حسينا الجعفي يوما وحدث عن ابي منير فقال كان والله ابو منير عندنا من الاخيار الصالحين.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (17,'<span data-type="title" id=toc-35>٣٥</span>- سمعت عبد الله بن مروان بن معاوية يقول سمعت ابي يقول وحدث يوما عن ابي بسطام فقال رحمه الله كان ممن يتحرى الصدق.
<span data-type="title" id=toc-36>٣٦</span>- وسمعت ابراهيم بن محمد بن ميمون يقول سمعت مطلب بن زياد يقول سمعت ليث بن ابي سليم يقول ما مات عون بن عبد الله تركت مجالسة الناس زمانا حزنا عليه
<span data-type="title" id=toc-37>٣٧</span>- وسمعت الحسن بن سهل يقول سمعت محمد بن بشر يقول سمعت نافع بن عمر يقول كان ابن ابي مليكة اذا ارادنا ان نساله لم نساله حتى نساره.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (18,'<span data-type="title" id=toc-38>٣٨</span>- وسمعت طاهر بن ابي احمد يقول سمعت ابي يقول لما بلغ سفيان ان جراح بن مليح حدث عن ابيه سعيد بن مسروق ان الربيع بن خثيم قال لرجل هل لك في صحيفة عليها خاتم محمد فتلا الربيع هؤلاء قل تعالوا اتل ما حرم ربكم عليكم فانكره سفيان انكارا شديدا
<span data-type="title" id=toc-39>٣٩</span>- سمعت منجابا قال سمعت ابا عامر الاسدي يقول سمعت سفيان يقول كان ابن شوذب عندنا',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (19,'ونحن نعده من ثقات مشايخنا
<span data-type="title" id=toc-40>٤٠</span>- سمعت أبي يقول سالت ابا نعيم عن ابي سلمة علي بن سلمة فقال كان ابو سلمة ثقة
<span data-type="title" id=toc-41>٤١</span>- وسمعت أبي يقول حدثنا يحيى بن يمان عن محمد بن مسلم البصري فقلت من هذا الشيخ فقال شيخ من اهل البصرة ثقة. قال ابي فسالت عبد الرحمن بن مهدي عنه فقال كان من القدرية',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (20,'<span data-type="title" id=toc-42>٤٢</span>- وسمعت أبي يقول قلت لابي نعيم شيخ حدث عن المنهال بن خليفة يقال له العلاء بن نجيح فقال ابو نعيم كان اصحابنا يذكرونه بخير
<span data-type="title" id=toc-43>٤٣</span>- وسمعت أبي يقول قلت لابي نعيم هذا الشيخ الذي حدث عنه سفيان يكنى بابي رباح فقال من حدثك عنه فقلت حدثني يحيى بن يمان عن سفيان عن ابي رباح فقال قد كان سفيان حدث عنه، فقلت عندك معرفته فقال ما اقف عليها الساعة.
<span data-type="title" id=toc-44>٤٤</span>- سمعت أبي يقول كنا عند ابي نعيم فذكرنا سفيان بن عيينة وعمران بن عيينة وابراهيم بن عيينة ومحمد بن عيينة فقال ابو نعيم سفيان بن عيينة الحنظة اللازوردية',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (21,'وسائر القوم شعير البط
<span data-type="title" id=toc-45>٤٥</span>- وسمعت أبي يقول ذكرت لابي نعيم يعقوب ابا المتئد خال سفيان بن عيينة فقال كان ضعيفا قلت يا ابا نعيم تجعله من شعير البط قال هو منهم.
<span data-type="title" id=toc-46>٤٦</span>- وسمعت أبي يقول وذكرنا عند ابي نعيم الفضل بن يزيد الثمالي فقال ابو نعيم كان ابن عم لابي حمزة الثمالي وكانا جميعا ضعيفين.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (22,'<span data-type="title" id=toc-47>٤٧</span>- وسمعت أبي يقول سالت محمد بن الحسن الواسطي عن بشير بن ميمون الواسطي فقال مثل بشير لا يذكر في العلم
<span data-type="title" id=toc-48>٤٨</span>- سمعت أبي يقول سمعت ابا نعيم يقول عمر بن صهبان الذي حدث عنه مندل كان ضعيفا
<span data-type="title" id=toc-49>٤٩</span>- وسمعت ابي يقول سالت ابا نعيم عن ابي جابر الذي حدث عنه عبد الله بن نمير فقال كان ابو جابر من الضعفاء.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (23,'<span data-type="title" id=toc-50>٥٠</span>- وسمعت ابي يقول سالت عبد الرحمن بن مهدي عن عبد الرحمن بن عبد المؤمن الرام فقال ما اعرف منه الا خيرا
<span data-type="title" id=toc-51>٥١</span>- سمعت ابي يقول سالت عبد الرحمن بن مهدي عن عبد الرحمن بن علقمة الذي حدث عنه سفيان فقال عبد الرحمن كان هذا من الاثبات الثقات.
<span data-type="title" id=toc-52>٥٢</span>- سمعت ابي يقول سالت ابا نعيم عن عبد الملك بن ايوب حدث عنه ابو المحياة فقال كان ينسب الى خير
<span data-type="title" id=toc-53>٥٣</span>- قال وسالت عبد الرحمن بن مهدي عن خالد بن قيس اخي نوح بن قيس فقال كنا نعده من خيار مشايخنا.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (24,'<span data-type="title" id=toc-54>٥٤</span>- وسمعت ابي يقول سالت اسحاق بن منصور عن رجل حدث عنه ابو خالد الدالاني يقال له خارجة بن هلال فقال كان هذا حجازيا من اصحاب ابي سعيد الخدري
<span data-type="title" id=toc-55>٥٥</span>- سمعت ابي يقول سالت عبد الرحمن عن مرزوق بن ميمون فقال كان هذا رجلا من التجار ليس من اهل الحديث
<span data-type="title" id=toc-56>٥٦</span>- سمعت ابي يقول سالت محمد بن بشر العبدي عن بكير بن عثمان العبدي فقال كان هذا رجلا منا ولم يكن بالمحمود في قومه.
<span data-type="title" id=toc-57>٥٧</span>- سمعت ابي يقول ذكرت لابي نعيم عبد الرحمن بن محمد بن عبيد الله العرزمي فقال كان هؤلاء اهل بيت يتوارثون الضعف قرنا بعد قرن.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (25,'<span data-type="title" id=toc-58>٥٨</span>- قال ابي قلت لابي نعيم فحسن بن محمد بن عبيد الله العرزمي فقال كان حسن خير القوم ولم يكن عندنا به باس
<span data-type="title" id=toc-59>٥٩</span>- سمعت ابي يقول سالت ابا نعيم عن مهلل العبدي فقال لي هذا من اصحاب ابي الجارود قلت فان عبيد الله حدث عنه قال حسبك قد قلت لك
<span data-type="title" id=toc-60>٦٠</span>- سمعت ابي يقول سالت ابا نعيم عن ابي المضاء فقال هذا من شيوخ عبيد الله بن موسى الشيعة
<span data-type="title" id=toc-61>٦١</span>- قلت لابي نعيم وان ابا المضاء حدث عن ابراهيم بن عبد الله بن صبح فقال ابو نعيم هذا كان اصحابنا يذكرونه بخير.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (26,'<span data-type="title" id=toc-62>٦٢</span>- سمعت ابي يقول سالت ابا نعيم عن قطري الخشاب فقال كان قطري رجلا من اهل الخير
<span data-type="title" id=toc-63>٦٣</span>- سمعت ابي يقول سالت ابا نعيم عن عبد الله بن عبد الله بن الاسود الحارثي فقال كان عندنا لا باس به من الاخيار
<span data-type="title" id=toc-64>٦٤</span>- سمعت ابي يقول سالت يحيى بن ادم عن صدقة بن سهل فقال هذا شيخ من القراء ولم يكن من اصحاب الحديث
<span data-type="title" id=toc-65>٦٥</span>- سمعت ابي يقول كنت يوما عند عمر بن زرعة وكان رجلا من اصبر الناس على فقر واحسنهم عملا فجاءوا اليه قوم',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (27,'من ناحية حمير فقالوا يا ابا حفص ان فلانة توفيت وتركت دارا ومتاعا وكساء فيه الف درهم وقد اوصت انك وارثها وانك مولاها قال فسكت ساعة ثم قال قد كانت هذه المرأة تاتينا وتدعي ما تقول من الولاء فكان يمنعنا الحياء ان نرد عليها فاما اذا كان هذا فليست لنا بمولاة ولست لها بوارث فانصرفوا فما اخذ منهم شيئا.
<span data-type="title" id=toc-66>٦٦</span>- وسمعت ابي يقول سمعت ابا نعيم وساله عن عمر بن عبد الله عن جارود بن السري السعدي فقال كان شيخا من اهل السلامة ان شاء الله
<span data-type="title" id=toc-67>٦٧</span>- وسمعت ابي يقول سالت ابا نعيم عن مستورد الغزال فقال كان شيخا من اهل السوق لا باس به ولم يكن يعرف الحديث
<span data-type="title" id=toc-68>٦٨</span>- وسمعت ابي يقول سمعت ابا نعيم وذكر عنده اسماعيل بن عبد الاعلى العرني فقال كان هذا شيخا من عرينه وكان ابوه عبد الاعلى احد الصالحين.',NULL,'123',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (28,'<span data-type="title" id=toc-69>٦٩</span>- وسمعت ابي يقول سمعت ابا نعيم وساله عن محمد بن عبد الله بن نمير عن عمر بن حمزة فقال كان هذا من اصحاب ابي الجارود
<span data-type="title" id=toc-70>٧٠</span>- وسمعت ابي يقول سمعت ابا احمد الزبيري وساله علي بن المغيرة الازدي فقال كان جارنا وجليسنا وما علمنا الا خيرا
<span data-type="title" id=toc-71>٧١</span>- سمعت ابي يقول سالت ابا نعيم عن شيخ حدثنا عبد الله بن المبارك عنه يقال له عوف بن ابي دلهامة فقال كان هذا شيخا من اهل الحجاز وكانوا يصفونه بخير',NULL,'124',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (29,'<span data-type="title" id=toc-72>٧٢</span>- وسمعت ابي يقول سالت ابا نعيم عن محمد بن الفضل بن عطية فقال كان ضعيفا
<span data-type="title" id=toc-73>٧٣</span>- وسمعت ابي يقول سمعت ابا نعيم وذكر عبد الله بن معاوية القرشي فقال كان ضعيفا
<span data-type="title" id=toc-74>٧٤</span>- وسمعت ابي يقول سعت ابا نعيم وذكر عنده عبد الله بن طفيل فقال لم يكن في الحديث بشيء وكان بلغني ترفضه
<span data-type="title" id=toc-75>٧٥</span>- وسمعت ابي يقول سالت ابا نعيم عن شيخ كتبت عنه يقال له عبد الرحمن بن حفص التغلبي فقال ما كان به باس
<span data-type="title" id=toc-76>٧٦</span>- وسمعت ابي يقول سالت ابا نعيم عن القعقاع بن عمرو فقال ابو نعيم روى عنه قيس وكان كالخير',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (30,'<span data-type="title" id=toc-77>٧٧</span>- وسمعت ابي يقول سمعت ابا نعيم يقول وذكرنا عنده عبد الحميد بن حريث فقال ابو نعيم قد روى عنه قيس وكان اصحابنا يصفونه بالثقة والخير
<span data-type="title" id=toc-78>٧٨</span>- وسمعت ابي يقول سالت ابا نعيم عن شيخ حدث عنه جابر الجعفي يقال له يزيد بن مرة فقال كان هذا يعتبر من اصحاب حذيفة
<span data-type="title" id=toc-79>٧٩</span>- وسمعت ابي يقول سمعت ابا نعيم يقول وذكر نصير بن ابي الاشعث فقال كان عثمانيا وكان يقال انه كان عابدا',NULL,'126',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (31,'<span data-type="title" id=toc-80>٨٠</span>- وسمعت ابي يقول سالت ابا نعيم عن القاسم حدث عنه ابو شهاب فقال كان عثمانيا ولم يكن من اهل الحديث
<span data-type="title" id=toc-81>٨١</span>- وسمعت ابي يقول ذكرت انا وابو نعيم الحكم بن ظهير فقال لي ابو نعيم اليس قد رايته وكتبت عنه قال فكيف رايته قلت كان رجلا نبيلا عند اهل الكوفة قال كذلك كان. قلت يا ابا نعيم فاخبرني من اين جاءنا هذا الضعف قال اولا تدري قلت فاخبرني قال حدث عن السدي احاديث منكرة ولم يخبر بها احد غيره وحدث عن علقمة بن مرثد باحاديث',NULL,'127',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (32,'منكرة وحدث عن عاصم بن ابي النجود باحاديث منكرة لم يجيء بها احد غيره ولم يحدث عن شيخ الا جاء بشيء لم يعرف فمن ثم جاءه الضعف
<span data-type="title" id=toc-82>٨٢</span>- وسمعت ابي يقول سالت ابا نعيم يا ابا نعيم من هؤلاء الذين تركتهم من اهل الكوفة كانو يرون السيف والخروج على السلطان فقال على رأسهم ابو حنيفة',NULL,'128',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (33,'وكان مرجئا يرى السيف ثم قال ابو نعيم حدثني عمار بن رزيق قال كان ابو حنيفة يكتب الى ابراهيم بن عبد الله بالبصرة يساله القدوم الى الكوفة ويوعده نصره.',NULL,'129',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (34,'<span data-type="title" id=toc-83>٨٣</span>- ثم قال ابي ذكرت انا وابو نعيم ابا معاوية فقلت يا ابا نعيم كان يرى رايا فسكت عليه فقال ما عرفت ابا معاوية الا وهو يرى الارجاء ويدعو اليه
<span data-type="title" id=toc-84>٨٤</span>- ثم قال كان ابو الجارود زياد بن المنذر الجعفي وعمرو بن شمر وابو يعفور عبد الكريم بن يعفور الجعفي وعبد الله بن بكير الغنوي وكادح بن جعفر والحكم بن زهير وحنش ومختار بن غسان',NULL,'130',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (35,'وابو حنيش الهمداني وحلو بن السري وعمر بن حمزة وعبد اله بن الطفيل وعباد بن جعفر وقد بقي خلق لم يسمهم
<span data-type="title" id=toc-85>٨٥</span>- حدثنا المنجاب اخبرنا طلق بن غنام قال كنت عند شريك فقال اخبرني عن قيس بن الربيع يحدث عن ابي داود الاعمى قال قلت لا ولكن معلى بن هلال يكثر عنه وانما ذكرت معلى قلت لعله يقول فيه شيئا فلم يذكر معلى بشيء',NULL,'131',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (36,'ثم قال شريك دخلت على ابي داود الاعمى فجعل يقول سمعت ابا سعيد وسمعت ابن عباس وسمعت ابن عمر ثم اعادها في ذلك المجلس فجعل حديث هذا لهذا وحديث هذا لهذا ولو شاء ان يقول سمعت عبد الله بن مسعود لقال.
<span data-type="title" id=toc-86>٨٦</span>- حدثنا عبد الله بن محمد بن سالم حدثنا طلق بن غنام قال سمعت قيسا يقول سمعت الاعمش يقول لما حدث اسماعيل بن رجاء عن ابيه بحديث النعل قلت له اما انت فقد عرفناك فاسالك بالله كيف كان ابوك فقال اللهم اني لا اعلمه الا خيرا',NULL,'132',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (37,'<span data-type="title" id=toc-87>٨٧</span>- حدثنا عبد الله بن محمد بن سالم حدثنا علي بن كليب الفساطيطي عن احمد بن عبد الله عن الحسن بن عياش قال سالت سفيان عن تزويج المولى العربية فقال ما ارى بذلك باسا
<span data-type="title" id=toc-88>٨٨</span>- حدثنا عبد الله بن محمد بن سالم قال سمعت ابا يحيى الخيوطي قال سمعت حسنا يقول لو ظننت انه اقرب الى الله لما امسيت حتى افعله يعني تزويج المولى العربية
<span data-type="title" id=toc-89>٨٩</span>- حدثنا عبد الله بن محمد بن سالم قال سمعت محمد بن سعيد بن زائدة او عبد الرحمن القطان او غيرهما قد شككت فيه ولا اعلمه الا عبد الرحمن قال جاء محمد بن الصباح الاشعثي يخاصم الى شريك فقال له محمد بن الصباح',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26230] VALUES (38,'أصلحك الله انه نبطي مستعرب فقال شريك الا ادلك على من هو شر من ذلك عربي مستنبط
<span data-type="title" id=toc-90>٩٠</span>- حدثنا عبد الله بن محمد بن سالم حدثنا محمد بن سعيد بن زائدة قال جار رجل الى شريك يخاصم اليه فقال له يا ابا عبد الله ان هذا نبطي حسب يعني خصمه فقال لرب نبطي خير من الف عربي وما عسى ان يفخر بن العرب هل هي الا عظام باليه او ارواح في النار. قلت يا ابا جعفر سمعت هذا من شريك أأرويه عنك قال نعم اروه عني
آخر مسائل ابن ابي شيبة',NULL,'134',NULL,NULL,'0');
