INSERT INTO [b26353] VALUES (1,'جزء فيه
INSERT INTO [b26353] VALUES (2,'جزء فيه
INSERT INTO [b26353] VALUES (3,'بسم الله الرحمن الرحيم
INSERT INTO [b26353] VALUES (4,'٢ - حدثنا أبو سعيد نا يحيى بن اليمان العجلي عن ابن أبي ذئب عن سعيد بن سمعان عن أبي هريرة قال كان رسول الله صلى الله عليه وسلم ينشر أصابعه في الصلاة نشرا',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (5,'٣ - نا يحيى بن يمان عن سفيان عن عبيد الله بن عمر عن نافع عن ابن عمر أن النبي صلى الله عليه وسلم اشترى هديه من قديد',NULL,'44',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (6,'٤ - حدثنا منصور بن وردان أبو محمد إمام مسجد الأنصار نا علي بن عبد الأعلى عن أبيه عن أبي البختري عن علي قال لما نزلت {وللهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ إِلَيْهِ سَبِيلاً} قال المؤمنون يا رسول الله أفي كل عام مرتين فسكت ثم قالوا يا رسول الله أفي كل عام مرتين قال لا ولو قلت نعم لوجبت فأنزل الله عز وجل {يأَيُّهَا الَّذِينَ آمَنُواْ لاَ تَسْأَلُواْ عَنْ أَشْيَاءَ إِن تُبْدَ لَكُمْ تَسُؤْكُمْ} الآية',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (7,'٥ - نا عبد الله بن خراش الشيباني عن العوام بن حوشب عن مجاهد عن ابن عباس قال رسول الله صلى الله عليه وسلم الناس شركاء في ثلاث الماء والنار والكلأ وثمنه حرام',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (8,'٦ - حدثنا زياد بن الحسن بن فرات عن أبيه عن جده عن أبي حازم عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم ما في الجنة شجرة إلا ساقها من ذهب',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (9,'٧ - حدثنا أبو عبد الرحمن بن منصور الفزاري سألت رجلاً من قومه عن اسمه فقال نضر نا عقبة بن علقمة اليشكري قال سمعت عليا عليه السلام يقول سمعت أذناني من في رسول الله صلى الله عليه وسلم وهو يقول طلحة والزبير جاراي في الجنة',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (10,'٨ - حدثنا أشعث بن زبيد بن الحارث قال نا مجالد عن عامر عن جابر بن عبد الله وعن الحارث عن علي قالا إن رسول الله صلى الله عليه وسلم لعن عشرة آكل الربا وموكله وكاتبه وشاهديه والواشمة والموشومة ومانع الصدقة والمحل والمحلل له',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (11,'٩ - حدثنا عقبة بن خالد نا عبيد الله بن عمر حدثني نافع عن ابن عمر أن النبي صلى الله عليه وسلم حرق نخل بني النضير',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (12,'١٠ - حدثنا عقبة بن خالد نا عبيد الله بن عمر حدثني سعيد بن أبي سعيد عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم إن الله عز وجل حرم على لساني ما بين لابتي المدينة',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (13,'١١ - حدثنا عقبة بن خالد نا عبيد الله بن عمر عن خبيب بن عبد الرحمن عن جده حفص بن عاصم عن أبي هريرة ⦗٦٢⦘ قال قال رسول الله صلى الله عليه وسلم يوشك الفرات أن يحسر عن كنز من ذهب فمن حضره فلا يأخذ منه شيئاً',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (14,'١٢ - حدثنا عقبة نا عبيد الله حدثني أبو الزناد عن الأعرج عن أبي هريرة عن النبي صلى الله عليه وسلم مثله إلا أنه قال يحسر عن جبل من ذهب',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (15,'١٣ - حدثنا عقبة نا عبيد الله حدثني نافع عن ابن عمر أن النبي صلى الله عليه وسلم كانت تركز له العنزة يصلي إليها',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (16,'١٤ - حدثنا أبو خالد الأحمر عن عبيد الله عن نافع عن ابن عمر أن النبي صلى الله عليه وسلم كانت تركز له الحربة يصلي إليها يوم العيد',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (17,'١٥ - حدثنا عبد الله بن نمير عن عبيد الله عن نافع عن ابن عمر أن النبي صلى الله عليه وسلم كان يخرج ومعه الحربة يوم العيد يصلي إليها فمن أجل ذلك اتخذها الأمراء',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (18,'١٦ - حدثنا عقبة نا ابن أبي ليلى عن عمرو بن مرة عن عبد الرحمن بن أبي ليلى عن عبد الله بن زيد قال كان أذان رسول الله صلى الله عليه وسلم شفعاً شفعاً في الأذان والإقامة',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (19,'١٧ - حدثنا عقبة نا ابن أبي ليلى عن عطية العوفي عن أبي سعيد الخدري قال جاء حسين إلى النبي صلى الله عليه وسلم وهو يصلي فأخذ بعنقه وهو جالس فنهض به وإنه لممسك بيديه حتى ركع',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (20,'١٨ - حدثنا وكيع نا ابن أبي ليلى عن أبي الزبير عن جابر قال نهى رسول الله صلى الله عليه وسلم عن بيع الطعام حتى يجري فيه الصاعان صاع البائع وصاع المشتري',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (21,'١٩ - حدثنا عقبة نا ابن أبي ليلى عن أبي الزبير عن جابر قال نهى رسول الله صلى الله عليه وسلم عن بيع الطعام حتى يقبضه صاحبه أو يبع في صاعه',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (22,'٢٠ - حدثني عقبة حدثني ابن أبي ليلى عن أبي أو ابن أبي نجيح عن أبي سلمة بن عبد الرحمن عن أبيه عن النبي صلى الله عليه وسلم أنه كان يقول في الصلاة على الميت اللهم اغفر لحينا وميتنا وذكرنا وأنثانا وصغيرنا وكبيرنا وشاهدنا وغائبنا اللهم من أحييته منا فأحيه على الإسلام ومن توفيته منا فتوفه على الإيمان',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (23,'٢١ - حدثنا عقبة بن خالد عن الأعمش عن أبي صالح عن أم سلمة قالت نعم اليوم يوم ينزل الله عز وجل فيه إلى السماء الدنيا قالوا يا أم المؤمنين وأي يوم هو قالت يوم عرفة',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (24,'٢٢ - حدثنا عقبة نا موسى بن محمد بن إبراهيم التيمي عن أبيه عن أبي سعيد الخدري قال نهى رسول الله صلى الله عليه وسلم أن يمثل بالبهائم',NULL,'80',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (25,'٢٣ - حدثنا عقبة نا موسى بن محمد بن إبراهيم التيمي عن أبيه عن جابر قال قال رسول الله صلى الله عليه وسلم غبوا في العيادة وأربعوا إلا أن يكون مغلوباً',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (26,'٢٤ - حدثنا عقبة بن خالد حدثني سعد بن سعيد أخو يحيى بن سعيد حدثني سعيد بن مرجانة قال سمعت أبا هريرة يقول قال رسول الله صلى الله عليه وسلم ينزل الله عز وجل إلى السماء الدنيا لشطر الليل أو لثلث الليل فيقول من يدعوني فأستجيب له ومن يسألني فأعطيه ومن يقرض غير عديم ولا ظلوم',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (27,'٢٥ - حدثنا عقبة حدثني موسى بن محمد بن إبراهيم عن أبيه عن السلولي عن معاذ قال قال رسول الله صلى الله عليه وسلم إن أتخذ منبراً فقد اتخذه أبي إبراهيم وإن أتخذ العصا فقد اتخذها أبي إبراهيم',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (28,'٢٦ - حدثني عقبة حدثني أسامة بن زيد حدثني محمد بن كعب القرظي قال سمعت كعب بن عجرة في مسجد الكوفة يقول أمرني رسول الله صلى الله عليه وسلم أن أحلق رأسي وأفدي شاة',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (29,'٢٧ - حدثني عقبة حدثني عنبسة قال حنظلة عن أنس قال قال رسول الله صلى الله عليه وسلم عمر الذباب أربعون يوماً والذباب في النار',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (30,'٢٨ - حدثني عقبة عن أبي سعد سعيد بن المرزبان مولى حذيفة بن اليمان عن أبي سلمة بن عبد الرحمن بن عوف عن ثوبان قال قال رسول الله صلى الله عليه وسلم من قال حين يمسي رضيت بالله رباً، وبالإسلام ديناً وبمحمد نبياً كان حقاً على الله عز وجل أن يرضيه',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (31,'٢٩ - حدثني عقبة بن خالد حدثني مسعر عن أبي حصين قال كان عبيدة يرى رأي علي وكان الأسود يرى غير ذلك فأوصى عبيدة إذا مات أن يصلي عليه الأسود',NULL,'99',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (32,'٣٠ - حدثنا عقبة بن خالد نا الصباح حدثني خالد بن أبي أمية عن أبي عبيدة بن حذيفة عن أبيه وعن عمرو بن حريث قال قال رسول الله صلى الله عليه وسلم من باع داراً ثم لم يجعل ثمنها في مثلها لم يبارك له فيها',NULL,'99',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (33,'٣١ - حدثني عقبة بن خالد نا موسى بن محمد عن أبيه عن أبي سعيد الخدري قال قال رسول الله صلى الله عليه وسلم إذا دخلتم على المريض فنفسوا له في أجله فإن ذلك لا يرد شيئاً وهو يطيب بنفسه',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (34,'٣٢ - حدثني عقبة بن خالد، نا موسى بن محمد عن أبيه عن أبي سعيد الخدري قال مر رجل على النبي صلى الله عليه وسلم بشاة يجر بأذنها فقال دع أذنها وخذ بسالفتها',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (35,'٣٣ - حدثني عقبة نا موسى بن محمد عن أبيه عن سلمة بن الأكوع قال سئل النبي صلى الله عليه وسلم عن الصلاة في القوس والقرن فقال اطرح القرن وصل في القوس',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (36,'٣٤ - حدثنا عثام بن علي هو أبو علي العامري عن الأعمش عن حبيب بن أبي ثابت عن سعيد بن جبير عن ابن عباس أن رسول الله صلى الله عليه وسلم كان يقوم من الليل فيصلي ركعتين ثم يستاك',NULL,'108',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (37,'٣٥ - حدثنا السيد بن عيسى الهمداني عن أبي إسحاق عن هبيرة بن يريم عن عبد الله بن مسعود قال من أتى عرافاً أو ساحراً أو كاهناً فصدقه بما يقول فقد كفر بما أنزل على محمد صلى الله عليه وسلم',NULL,'109',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (38,'٣٦ - حدثنا أبو خالد عن عمرو بن قيس عن أبي إسحاق عن هبيرة عن عبد الله قال من أتى ساحراً أو عرافاً فصدقه بما يقول فقد كفر بما أنزل على محمد صلى الله عليه وسلم',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (39,'٣٧ - حدثنا عقبة نا موسى بن محمد بن إبراهيم عن أبيه عن أنس قال: نهى رسول الله صلى الله عليه وسلم أن يفترش على أبواب البيوت شيئاً وقال اكتموها (١) عن الباب شيئاً
INSERT INTO [b26353] VALUES (40,'٣٨ - حدثني عقبة نا موسى بن محمد عن أبيه عن أنس بن مالك قال قال رسول الله صلى الله عليه وسلم إذا أكلتم فاخلعوا نعالكم فإنه أروح لأقدامكم',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (41,'٣٩ - حدثني عقبة حدثني أبو أمية الثقفي عن أبي الزناد قال رأيت في يد أبي بردة خاتماً من فضة فصه عقيقة حمراء فيه تمثال رجل بين أسدين يلحسانه فقال تدرون خاتم من ذا فقلنا لا قال هذا خاتم دانيال أخذه أبو موسى فقومه عليه من الخمس وكان يتختم به',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (42,'٤٠ - حدثني عقبة حدثنا شعبة حدثني سعيد الجريري عن أبي نضرة عن أبي سعيد قال قال أبو بكر ألست أحق الناس بها ألست أول من أسلم ألست صاحب كذا',NULL,'118',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (43,'٤١ - حدثني إسماعيل بن الوليد الراسبي أبو يونس عن هشام بن حسان عن ابن سيرين قال أول من أسلم من الرجال أبو بكر وأول من أسلم من النساء خديجة',NULL,'120',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (44,'٤٢ - حدثنا عبد الله بن إدريس عن شعبة عن عمرو بن مرة عن أبي حمزة الأنصاري عن زيد بن أرقم قال علي عليه السلام أول من أسلم قال عمرو فذكرته لإبراهيم فقال أول من أسلم أبو بكر',NULL,'121',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (45,'٤٣ - حدثنا أبو أسامة عن شعبة عن عمرو بن مرة عن إبراهيم قال أبو بكر أول من أسلم',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (46,'٤٤ - حدثنا إسحاق بن سليمان الرازي أبو يحيى عن أبي سنان سعيد بن سنان عن أبي حصين عن شقيق عن حذيفة قال كنا نؤمر بالسواك إذا قمنا من الليل',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (47,'٤٥ - حدثنا إسحاق بن سليمان الرازي قال سمعت جراح بن الضحاك الكندي يذكر عن كريب بن سليم الكندي عن أمة امرأة الزبير بن العوام قالت كان رسول الله صلى الله عليه وسلم يأمرنا إذا حم الزبير أن نبرد له الماء ونحدره عليه',NULL,'123',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (48,'٤٦ - حدثنا إسحاق بن سليمان الرازي عن حنظلة قال سمعت نافعاً يقول سمعت ابن عمر يقول سمعت رسول الله صلى الله عليه وسلم يقول من الفطرة تقليم الأظفار وحلق العانة والأخذ من الشارب',NULL,'124',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (49,'٤٧ - حدثنا إسحاق بن سليمان عن معاوية بن يحيى عن يونس بن ميسرة عن أبي إدريس عن أبي الدرداء قال قال رسول الله صلى الله عليه وسلم الطهارات أربع قص الشارب وحلق العانة وتقليم الأظفار والسواك',NULL,'124',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (50,'٤٨ - حدثنا إسحاق بن سليمان عن معاوية بن يحيى عن الزهري قال السنة في السواك أن تستاك للصبح والظهر',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (51,'٤٩ - حدثنا إسحاق بن سليمان عن عبد الله بن جابر أن عمر بن عبد العزيز كان إذا بلغه عن رجل نسك واجتهاد سأل عن عقله',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (52,'٥٠ - حدثنا إسحاق بن سليمان قال سمعت عثمان بن زائدة يقول قال لقمان لابنه لا تؤخر التوبة فإن الموت قد يأتي بغتة',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (53,'٥١ - حدثنا أبو أحمد محمد بن عبد الله قال كان سفيان يصلي بنا المغرب فيجهر بالتشهد كأنه يريد أن يعلمنا وكذلك إذا بدأ بالتشهد قال الحمد لله وإذا فرغ من آخره قال الحمد لله',NULL,'126',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (54,'٥٢ - حدثنا عقبة بن خالد حدثني عنبسة البصري عن عمرو بن ميمون عن الزهري عن عروة عن عائشة قالت قال رسول الله صلى الله عليه وسلم الزنجي إذا شبع زنا وإذا جاع سرق وإن فيهم لسماحةً ونجدةً',NULL,'126',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (55,'٥٣ - حدثنا يونس بن بكير الشيباني عن الأعمش عن طلحة بن مصرف عن عمرو بن شرحبيل عن عبد الله قال قال رسول الله صلى الله عليه وسلم من كذب علي متعمداً فليتبوأ مقعده من النار',NULL,'128',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (56,'٥٤ - حدثنا عبد الله بن الأجلح عن الأعمش عن حبيب بن أبي ثابت عن ثعلبة بن يزيد الحماني عن علي عن النبي صلى الله عليه وسلم قال من كذب علي متعمداً فليتبوأ مقعده من النار',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (57,'٥٥ - نا أسباط بن محمد عن مطرف عن عطية عن أبي سعيد قال قال رسول الله صلى الله عليه وسلم من كذب علي متعمداً فليتبوأ مقعده من النار',NULL,'135',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (58,'٥٦ - نا أبو خالد قال سمعت يزيد بن أبي عبيد مولى سلمة بن الأكوع قال سمعت سلمة قال سمعت رسول الله صلى الله عليه وسلم يقول من تقول علي غير الحق فليتبوأ مقعده من النار',NULL,'136',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (59,'٥٧ - حدثنا أبو خالد قال سمعت سليمان التيمي يقول عن أنس قال قال رسول الله صلى الله عليه وسلم من كذب علي متعمداً فليتبوأ مقعده من النار',NULL,'137',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (60,'٥٨ - حدثنا يونس بن بكير عن محمد بن إسحاق عن عبد الرحمن بن الأسود عن أبيه عن عبد الله قال من السنة أن تخفي التشهد',NULL,'138',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (61,'٥٩ - حدثنا يونس بن بكير عن محمد بن إسحاق عن يحيى بن عباد بن عبد الله بن الزبير عن أبيه عن جده عبد الله بن الزبير عن الزبير بن العوام قال كان على النبي صلى الله عليه وسلم يوم أحد درعان فنهض إلى الصخرة فلم يستطع فقعد طلحة تحته حتى استوى على الصخرة قال الزبير فسمعت النبي صلى الله عليه وسلم يقول أوجب طلحة',NULL,'140',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (62,'٦٠ - حدثنا يونس بن بكير عن محمد بن إسحاق عن الزهري عن المسور بن مخرمة ومروان بن الحكم في قوله {وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوَى} قال لا إله إلا الله وحده لا شريك له',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (63,'٦١ - حدثنا عبد الله بن الأجلح عن محمد بن إسحاق عن الزهري عن عروة في قوله عز وجل {وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوَى} قال لا إله إلا الله وحده لا شريك له',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (64,'٦٢ - حدثنا يونس بن بكير عن الأعمش عن مجاهد في قوله عز وجل {إِلَى رَبِّهِمْ يُحْشَرُونَ} قال حشرهم الموت',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (65,'٦٣ - حدثنا يونس بن بكير عن محمد بن إسحاق عن عبد الله بن أبي نجيح عن مجاهد قال إن كان لعلى بعض أصحاب الكهف لوضح الورق يعني الحلي من خزائنه',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (66,'٦٤ - حدثنا أبو خالد وآخر نا عمرو بن قيس عن عاصم عن شقيق عن عبد الله قال قال رسول الله صلى الله عليه وسلم تابعوا بين الحج والعمرة فإنهما ينفيان الفقر والذنوب كما ينفي الكير خبث الحديد والذهب والفضة وليس للحجة المبرورة ثواب دون الجنة',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (67,'٦٥ - ثنا أبو خالد الأحمر عن عمرو بن قيس عن أبي إسحاق قال حدثت عن صلة بن زفر العبسي قال كنا عند عمار فأتي بشاة مصلية فقال كلوا فتنحى بعض القوم فقال إني صائم فقال عمار من صام اليوم الذي يشك فيه فقد عصى أبا القاسم صلى الله عليه وسلم',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (68,'٦٦ - حدثنا أبو خالد عن عيسى بن ميسرة عن أبي الزناد عن أنس قال قال رسول الله صلى الله عليه وسلم الصلاة نور المؤمن',NULL,'146',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (69,'٦٧ - حدثنا أبو خالد عن ابن عجلان عن أبيه عن أبي هريرة قال جاء رجل إلى النبي صلى الله عليه وسلم فشكا إليه جاراً له فقال له النبي صلى الله عليه وسلم ثلاث مرات صبراً ثم قال له في الثالثة أو الرابعة اطرح متاعك في الطريق قال فجعل الناس يمرون عليه ويقولون مالك فيقول آذاه جاره فجعلوا يقولون لعنه الله فجاءه جاره فقال رد متاعك لا والله لا أوذيك',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (70,'٦٨ - حدثنا أبو خالد عن ابن عجلان عن سعيد عن أبي هريرة أن النبي صلى الله عليه وسلم كان يقول اللهم إني أعوذ بك من جار السوء في دار المقامة فإن جار البادية يتحول',NULL,'148',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (71,'٦٩ - حدثنا أبو خالد عن ابن عجلان عن سعيد بن أبي سعيد قال كان من دعاء داود عليه السلام اللهم إني أعوذ بك من جار السوء ومن زوج تشيبني قبل المشيب ومن ولد يكون علي ربا (١) ومن مال يكون علي عذاباً ومن خليل ماكرٍ عيناه ترياني وقلبه يرعاني إذا رأى حسنة دفنها وإن رأى سيئة أذاعها
INSERT INTO [b26353] VALUES (72,'٧٠ - حدثنا أبو خالد عن الأعمش عن أبي صالح عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم إذا زنت أمة أحدكم فليجلدها ثلاثاً بكتاب الله عز وجل فإن عادت فليبعها ولو بحبل من شعر',NULL,'151',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (73,'٧١ - حدثنا أبو خالد عن ابن عجلان عن سعيد عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم إذا زنت أمة أحدكم فليجلدها بكتاب الله عز وجل فإن عادت في الرابعة فليبعها ولو بحبل من شعر',NULL,'153',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (74,'٧٢ - حدثنا أبو خالد عن أشعث بن سوار عن عدي بن ثابت عن يزيد بن البراء عن خاله أن رجلاً تزوج امرأة أبيه أو امرأة ابنه فأرسل إليه النبي صلى الله عليه وسلم فقتله',NULL,'156',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (75,'٧٣ - حدثنا حفص بن غياث عن أشعث عن عدي بن ثابت عن البراء قال مر بي خالي أبو بردة بن نيار ومعه لواء فقلت أين تريد قال بعثني رسول الله صلى الله عليه وسلم إلى رجل تزوج امرأة أبيه [أن] آتيه برأسه',NULL,'157',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (76,'٧٤ - حدثنا أبو خالد عن يزيد بن سنان عن أبي المبارك عن عطاء بن أبي رباح عن أبي سعيد الخدري قال قال رسول الله صلى الله عليه وسلم ما آمن بالقرآن من استحل محارمه',NULL,'161',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (77,'٧٥ - نا أبو خالد عن يزيد بن سنان عن أبي المبارك عن عطاء بن أبي رباح عن أبي سعيد الخدري قال أحبوا المساكين فإني سمعت رسول الله صلى الله عليه وسلم يقول في دعائه اللهم أحيني مسكيناً واحشرني في زمرة المساكين',NULL,'164',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (78,'٧٦ - حدثنا عبدة بن سليمان الكلابي عن الأعمش عن خيثمة بن عبد الرحمن قال ما تقرؤون في القرآن يا أيها الذين آمنوا فإنه في التوراة يا أيها المساكين',NULL,'170',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (79,'٧٧ - حدثنا أبو خالد عن حجاج عن قتادة عن ابن سيرين عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم من أكل ناسياً أو شرب ناسياً فلا يفطر فإنما هو رزق رزقه الله عز وجل',NULL,'170',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (80,'٧٨ - حدثنا أبو أسامة عن عوف عن ابن سيرين وخلاس بن عمرو عن أبي هريرة عن النبي صلى الله عليه وسلم مثله أو نحوه',NULL,'171',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (81,'٧٩ - حدثنا أبو خالد عن حجاج عن أبي بكر بن حفص عن ابن حنين عن ابن عباس قال قال رسول الله صلى الله عليه وسلم لا يقرأ أحدكم وهو راكع ولا ساجد ولا يلبس ثوباً أحمر مورداً',NULL,'172',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (82,'٨٠ - حدثنا أبو خالد عن حجاج عن الحسن بن سعد عن عبد الله بن شداد عن أم سلمة قالت بكت أسماء على حمزة ثلاثة أيام وتسلبت فدعاها رسول الله صلى الله عليه وسلم فأمرها أن توضأ وأن تكتحل',NULL,'173',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (83,'٨١ - حدثنا أبو خالد وعبدة بن سليمان جميعاً عن محمد بن إسحاق عن نافع عن ابن عمر قال قال رسول الله صلى الله عليه وسلم إذا نعس أحدكم يوم الجمعة في مجلسه فليتحول عن مجلسه ذلك',NULL,'177',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (84,'٨٢ - حدثنا أبو خالد عن ابن جريج عن عطاء وطاووس في الرجل ينعس قال أحدهما فليتزحزح وقال الآخر فليقم ولا يتخطى',NULL,'181',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (85,'٨٣ - حدثنا أبو خالد عن الضحاك بن عثمان عن مخرمة بن سليمان عن كريب عن ابن عباس قال قال رسول الله صلى الله عليه وسلم لا ينظر الله عز وجل إلى رجل أتى رجلاً أو امرأة في الدبر',NULL,'181',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (86,'٨٤ - حدثنا أبو خالد عن حميد عن أنس قال قال رسول الله صلى الله عليه وسلم لرجل يا خال (١) أسلم أسلم قال أجدني كارهاً قال أسلم وإن كنت كارهاً
INSERT INTO [b26353] VALUES (87,'٨٥ - حدثنا أبو خالد عن الأعمش عن الحكم ومسلم البطين وسلمة بن كهيل عن سعيد بن جبير وعطاء ومجاهد عن ابن عباس قال جاءت امرأة إلى النبي صلى الله عليه وسلم فقالت يا رسول الله إن أختي ماتت وعليها صيام شهرين متتابعين قال أرأيت لو كان على أختك دين أكنت تقضينه قالت نعم قال فحق الله عز وجل أحق أن تقضيه',NULL,'185',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (88,'٨٦ - حدثنا أبو خالد وعقبة بن خالد جميعاً عن سعيد بن أبي عروبة عن قتادة عن شهر بن حوشب عن أبي أمامة قال أبو خالد قال رسول الله صلى الله عليه وسلم الوضوء يكفر ما بين ذلك وتصير الصلاة وقال عقبة بن خالد قال رسول الله صلى الله عليه وسلم يكفر ما قبل ذلك وتصير الصلاة نافلة قيل سمعته من رسول الله صلى الله عليه وسلم قال غير مرة ولا ثلاث ولا أربع',NULL,'186',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (89,'٨٧ - حدثنا أبو خالد عن مجالد عن الشعبي عن جابر بن عبد الله قال كنا عند النبي صلى الله عليه وسلم فخط خطا وخط خطين عن يمينه وخط خطين عن يساره ثم وضع يده في الخط الأوسط فقال هذا سبيل الله ثم تلا هذه الآية {وَأَنَّ هَذَا صِرَاطِي مُسْتَقِيماً فَاتَّبِعُوهُ وَلاَ تَتَّبِعُواْ السُّبُلَ فَتَفَرَّقَ بِكُمْ عَن سَبِيلِهِ}',NULL,'188',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (90,'٨٨ - حدثنا أبو خالد الأحمر قال سمعت شعبة يقول قلت لأيوب رويت عن الحسن ألفاً قال نعم ألف وألف',NULL,'191',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (91,'٨٩ - حدثنا حفص بن غياث وأبو خالد جميعاً عن حجاج عن رياح بن عبيدة قال حفص عن ابن أخي أبي سعيد عن أبي سعيد وقال أبو خالد عن مولى لأبي سعيد عن أبي سعيد أن رسول الله صلى الله عليه وسلم كان إذا أكل أو شرب قال الحمد لله الذي أطعمنا وسقانا وجعلنا مسلمين',NULL,'191',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (92,'٩٠ - حدثنا أبو خالد عن محمد بن إسحاق عن عبد الرحمن بن القاسم عن أبيه عن عائشة قالت أفاض رسول الله صلى الله عليه وسلم من آخر يوم (١) حين صلى الظهر ثم رجع فمكث بمنى ليالي أيام التشريق يرمي الجمرة فإذا زالت الشمس رمى كل جمرة بسبع حصيات يكبر مع كل حصاة ويقف عند الأولى وعند الثانية فيطيل القيام ويتضرع ثم يرمي الثالثة ولا يقف عندها
INSERT INTO [b26353] VALUES (93,'٩١ - حدثنا أبو خالد عن ابن عجلان عن أبي حازم عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم من وقاه الله شر ما بين لحييه وشر ما بين رجليه دخل الجنة',NULL,'197',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (94,'٩٢ - حدثنا أبو خالد عن ابن جريج عن عبد الكريم بن مالك عن عكرمة عن أنس أن النبي صلى الله عليه وسلم رأى رجلاً يسوق بدنة فقال اركبها فقال إنها بدنة فقال اركبها غير مفدوحة',NULL,'198',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (95,'٩٣ - حدثنا أبو خالد عن حجاج بن أرطأة عن أبي بكر بن حزم عن عمرة عن عائشة قالت قال رسول الله صلى الله عليه وسلم إذا رميتم وحلقتم حل لكم كل شيء إلا النساء وحل لكم الثياب والطيب',NULL,'199',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (96,'٩٤ - حدثنا أبو خالد عن محمد بن إسحاق عن الزهري عن السائب بن يزيد قال كان رسول الله صلى الله عليه وسلم يخطب خطبتين يفصل بينهما بقعود وأبو بكر وعمر كذلك',NULL,'200',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (97,'٩٥ - حدثنا أبو خالد عن سعد بن طارق أبي مالك، عن ربعي بن حراش عن حذيفة قال أتي الله بعبد من عبيده آتاه مالاً فقال ماذا عملت في الدنيا قال {وَلاَ يَكْتُمُونَ اللهَ حَدِيثاً} قال أي رب آتيتني مالاً وكنت أبايع الناس وكان من خلقي الجواز وكنت أتيسر على الموسر وأنظر المعسر فقال الله عز وجل أنا أحق بذا منك تجاوزوا عن عبدي فقال عقبة بن عامر وأبو مسعود الأنصاري وهكذا سمعنا من رسول الله صلى الله عليه وسلم',NULL,'201',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (98,'٩٦ - حدثنا أبو خالد عن ليث عن مجاهد عن عائشة قالت كان رسول الله صلى الله عليه وسلم يطلب الغذاء فنقول ليس فيقول إني صائم',NULL,'204',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (99,'٩٧ - حدثنا أبو خالد عن حجاج عن نافع عن ابن عمر أن رسول الله صلى الله عليه وسلم كان إذا أدخل الميت وقال مرة إذا وضع الميت في لحده قال مرة بسم الله وبالله وعلى ملة رسول الله وقال مرة بسم الله وبالله وعلى سنة رسول الله صلى الله عليه وسلم',NULL,'205',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (100,'٩٨ - وحدثنا أبو خالد عن حجاج عن الحكم عن مقسم عن ابن عباس قال قال رسول الله صلى الله عليه وسلم غدوة أو روحة في سبيل الله خير من الدنيا وما فيها',NULL,'210',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (101,'٩٩ - حدثنا أبو خالد عن مجالد عن عامر عن جابر بن عبد الله قال جاء ماعز إلى النبي صلى الله عليه وسلم فأقر بالزنا فرده ثم جاء فرده ثم جاء فرده فلما جاء في الرابعة قال ارجموه فرجمه رسول الله صلى الله عليه وسلم ورجمناه ففر فاتبعناه حتى قتلناه قال عامر قال لي جابر ها هنا قتلناه',NULL,'211',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (102,'١٠٠ - حدثنا أبو خالد قال سمعت سفيان يقول إنه ليمر بين يدي المسكين وأنا أصلي فأدعه ويمر أحدهم عليه الثياب يتمشى فلا أدعه',NULL,'212',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (103,'١٠١ - حدثنا أبو بكر بن عياش نا أبو إسحاق عن أبي الأحوص عن عبد الله قال علمنا رسول الله صلى الله عليه وسلم التشهد التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين أشهد أن لا إله إلا الله وأشهد أن محمداً عبده ورسوله قال ثم يتخير',NULL,'212',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (104,'١٠٢ - حدثنا أبو بكر نا عاصم والأعمش عن أبي وائل مثله',NULL,'213',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (105,'١٠٣ - حدثنا ابن فضيل عن خصيف عن أبي عبيدة عن عبد الله عن النبي صلى الله عليه وسلم بمثله',NULL,'213',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (106,'١٠٤ - حدثنا أبو خالد عن عثمان بن الأسود عن مجاهد عن عبد الله بن مسعود بمثله',NULL,'214',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (107,'١٠٥ - حدثنا أبو خالد عن أيمن بن نابل عن أبي الزبير عن جابر قال كان رسول الله صلى الله عليه وسلم يعلمنا التشهد بسم الله وبالله التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين أشهد أن لا إله إلا الله وأشهد أن محمداً عبده ورسوله نسأل الله الجنة ونعوذ بالله من النار',NULL,'216',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (108,'١٠٦ - حدثنا عبد الله بن إدريس قال سمعت عبيد الله بن عمر يذكر عن نافع عن ابن عمر أن أبا بكر ضرب وغرب وأن عمر ضرب وغرب',NULL,'220',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (109,'١٠٧ - حدثنا أبو خالد قال سئل الأعمش عن حديث فقال لابن المختار ترى أحداً من أصحاب الحديث فغمض عينه وقال يا أبا محمد ما أرى أحداً قال فحدث [به] (١)
INSERT INTO [b26353] VALUES (110,'١٠٨ - حدثنا أبو خالد عن عمرو بن قيس قال قذف المحصنة يحبط عمل تسعين سنة',NULL,'222',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (111,'١٠٩ - حدثنا أبو خالد عن حجاج عن الزهري عن عبد الرحمن بن هنيدة عن ابن عمر قال قال رسول الله صلى الله عليه وسلم إن البلاء إذا نزل أصاب من بين أظهرهم ثم يبعثون على أعمالهم',NULL,'223',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (112,'١١٠ - سمعت أبا خالد الأحمر قال سمعت سعد بن طارق يقول سمعت أبي قال سمعت النبي صلى الله عليه وسلم يقول من أقر بتوحيد الله وكفر بما يعبد من دونه حرم الله ماله ودمه وحسابه على الله عز وجل',NULL,'224',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (113,'١١١ - نا أبو خالد عن يزيد بن كيسان عن أبي حازم عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم لقنوا أمواتكم لا إله إلا الله',NULL,'224',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (114,'١١٢ - حدثنا أبو خالد الأحمر قال أكل سفيان ليلة فشبع فقال إن الحمار إذا زيد في علفه زيد في عمله فقام حتى أصبح قال أبو سعيد فحدثت [به] (١) أبا زكريا المراوحي فحدثني أبو زكريا المراوحي عن أبي خالد قال صحبنا سفيان في طريق مكة فكان يقرأ في المصحف كل يوم فإن لم يقرأ فيه فتحه ونظر فيه وأطبقه
INSERT INTO [b26353] VALUES (115,'١١٣ - حدثنا أبو خالد عن عبد الحميد بن جعفر الأنصاري عن سعيد بن أبي سعيد عن أبي شريح الخزاعي قال: خرج علينا رسول الله صلى الله عليه وسلم فقال أليس تشهدون أن لا إله إلا الله وأني رسول الله قلنا بلى قال فإن هذا القرآن سبب طرفه بيد الله عز وجل وطرفه بأيديكم فتمسكوا به فإنكم لن تضلوا ولن تهلكوا بعده أبداً',NULL,'229',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (116,'١١٤ - حدثني أبو خالد حدثني شيخ منذ أربعين سنة عن الضحاك قال يأتي على الناس زمان تكثر فيه الأحاديث حتى يبقى المصحف معلقاً ويقع عليه الغبار ما ينظر فيه',NULL,'231',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (117,'١١٥ - حدثنا أبو خالد عن كثير بن زيد عن ربيح بن عبد الرحمن بن أبي سعيد الخدري عن أبيه عن أبي سعيد قال خرج علينا رسول الله صلى الله عليه وسلم ونحن نتذاكر المسيح الدجال فقال ألا أخبركم بما هو أخوف عليكم عندي من المسيح الدجال قلنا بلى يا رسول الله قال الشرك الخفي أن يقوم الرجل فيزين صلاته لما يرى من نظر الرجل',NULL,'231',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (118,'١١٦ - حدثنا أبو خالد عن سعد بن إسحاق بن كعب بن عجرة الأنصاري عن عاصم بن عمر بن قتادة عن محمود بن لبيد قال قال رسول الله صلى الله عليه وسلم إياكم وشرك السرائر قالوا يا رسول الله وما شرك السرائر قال الرجل يقوم يصلي فيزين صلاته جاهداً لما يرى من نظر الناس إليه فذلك شرك السرائر',NULL,'232',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (119,'١١٧ - حدثنا أبو خالد عن داود بن أبي هند عن أبي نضرة عن أبي سعيد قال لما رجع النبي صلى الله عليه وسلم من تبوك سئل عن الساعة فقال لا يأتي على الناس مائة سنة وعلى ظهر الأرض نفس منفوسة اليوم',NULL,'233',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (120,'١١٨ - حدثنا أبو خالد عن كثير بن زيد عن المطلب بن حنطب قال أحسبه عن مصعب عن سعد قال قال رسول الله صلى الله عليه وسلم من دعا بدعاء يونس أستجيب له قال يريد {وَكَذَلِكَ نُنْجِي المُؤْمِنِينَ}',NULL,'234',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (121,'١١٩ - نا أبو خالد عن داود بن أبي هند عن العباس بن عبد الرحمن عن عمران بن حصين أن الحصين أتى النبي صلى الله عليه وسلم فقال أرأيت رجلاً كان يقري الضيف ويصل الرحم مات قبلك وهو أبوك قال إن أبي وأباك وأنت في النار قال فمات حصين مشركاً',NULL,'236',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (122,'١٢٠ - حدثنا أبو معاوية عن هشام بن عروة عن أبيه عن عائشة قالت قال رسول الله صلى الله عليه وسلم لا تسبوا ورقة بن نوفل فإني رأيت له جنة أو جنتين',NULL,'237',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (123,'١٢١ - حدثنا عبد الله بن الأجلح عن عبد الله بن العمري عن ابن شبرمة قال سمعت شقيقاً أو قال شقيق جاء مصدق رسول الله صلى الله عليه وسلم فنزل على الماء فأخذت بأذن شاة ما لنا شاة غيرها فأتيته فقلت يا مصدق رسول الله صلى الله عليه وسلم ما لنا غير هذه الشاة فقال ما لكم شاة غيرها ليس عليها شيء',NULL,'238',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (124,'١٢٢ - حدثنا أبو خالد عن داود بن أبي هند عن عكرمة عن ابن عباس قال كان رسول الله صلى الله عليه وسلم يصلي فجاء أبو جهل فقال ألم أنهك عن هذا فانصرف إليه صلى الله عليه وسلم فزبره فقال والله إنك لتعلم ما بها نادٍ أكثر مني فأنزل الله {فَلْيَدْعُ نَادِيه سَنَدْعُ الزَّبَانِيّة} قال ابن عباس فوالله لو دعا ناديه لأخذته زبانية الله عز وجل',NULL,'239',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (125,'١٢٣ - حدثنا أبو خالد عن حجاج عن الوليد بن أبي مالك عن القاسم عن أبي أمامة قال قال رسول الله صلى الله عليه وسلم يجير على المسلمين بعضهم',NULL,'240',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (126,'١٢٤ - حدثنا أبو خالد عن الحجاج بن أرطاة عن الوليد بن أبي مالك عن عبد الرحمن بن سلمة أن رجلاً أجار رجلاً وهو مع أبي عبيدة بن الجراح وعمرو بن العاص وخالد بن الوليد فقال عمرو وخالد لا نجير من أجاره فقال أبو عبيدة بل سمعت رسول الله صلى الله عليه وسلم يقول يجير على المسلمين بعضهم',NULL,'242',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (127,'١٢٥ - حدثنا أبو خالد عن حجاج عن عمرو بن شعيب عن أبيه عن جده عن عمر قال سمعت رسول الله صلى الله عليه وسلم يقول لا يقتل بالولد الوالد',NULL,'243',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (128,'١٢٦ - حدثنا يونس بن بكير عن محمد بن إسحاق عن الزهري قال إن للعلم غوائل فمن غوائله أن يترك العلم حتى يذهب بعضه ومن غوائله النسيان ومن غوائله الكذب فيه وهو أشد غوائله',NULL,'245',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (129,'١٢٧ - حدثنا يونس بن بكير عن محمد بن إسحاق عن الزهري عن عروة عن المسور بن مخرمة ومروان بن الحكم أنهما حدثاه جميعاً أن النبي صلى الله عليه وسلم ساق يوم الحديبية سبعين بدنة عن سبعمائة رجل',NULL,'245',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (130,'١٢٨ - حدثنا عبد الله بن الأجلح عن الأعمش عن أبي سفيان عن جابر قال نحر عنا رسول الله صلى الله عليه وسلم يوم الحديبية عن كل سبعة بدنة وأكثرنا لم ينحر',NULL,'247',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (131,'١٢٩ - حدثنا يونس بن بكير عن أبي حنيفة قال لو أعطيت في صدقة الفطر هليلج أجزأك',NULL,'248',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (132,'١٣٠ - حدثنا أبو أسامة عن مجالد عن الشعبي عن جابر بن عبد الله قال كنا عند النبي صلى الله عليه وسلم فأقبل سعد فقال النبي صلى الله عليه وسلم هذا خالي فليرني امرؤ خاله',NULL,'248',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (133,'١٣١ - حدثنا أبو أسامة عن إسحاق بن أبي إسحاق الشيباني عن أبيه قال لما حدث الشعبي بحديث المغيرة بن شعبة أنه رأى النبي صلى الله عليه وسلم توضأ ومسح على خفيه قال قلت يا أبا عمرو من أين كان لرسول الله صلى الله عليه وسلم خفين قال أهداه له دحية الكلبي',NULL,'250',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (134,'١٣٢ - حدثنا أبو أسامة حدثني محمد بن عمرو عن أبي سلمة عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم أربعة أنهار فجرت من الجنة الفرات والنيل نيل مصر وسيحان وجيحان',NULL,'251',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (135,'١٣٣ - حدثنا أبو أسامة حدثني محمد بن عمرو عن أبي سلمة عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم إن منبري هذا لعلى ترعة من ترع الجنة',NULL,'252',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (136,'١٣٤ - حدثنا أبو أسامة عن محمد بن عمرو عن سالم عن أبيه قال قال رسول الله صلى الله عليه وسلم لا تباع الثمرة حتى يبدو صلاحها',NULL,'253',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (137,'١٣٥ - نا المحاربي قال كان ضرار بن مرة ومحمد بن سوقة إذا كان يوم الجمعة طلب كل واحد منهما صاحبه فإذا اجتمعا جلسا يبكيان',NULL,'253',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (138,'١٣٦ - حدثنا عبد الله بن الأجلح عن ضرار بن مرة أبي سنان الشيباني قال لا تجيئوني جماعة يجيء أحدكم وحده فإنكم إذا اجتمعتم تحدثتم فإذا كان الرجل وحده لم يخل من أن يدرس جزؤه أو يذكر ربه عز وجل',NULL,'254',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (139,'١٣٧ - حدثنا أبو أسامة عن شعبة عن قتادة عن مطرف بن عبد الله بن الشخير قال ذكر سمرة عند عمران بن حصين فقال رجل هلك سمرة فقال عمران بن حصين كلاماً ذب به عن الإسلام أفضل',NULL,'254',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (140,'١٣٨ - حدثنا أبو أسامة عن عبد الرحمن بن يزيد عن مكحول عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم امسحوا على الخفين والخمار فإنه حق',NULL,'254',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (141,'١٣٩ - حدثنا عبد الله بن نمير نا الأعمش عن الحكم عن عبد الرحمن بن أبي ليلى عن كعب بن عجرة عن بلال قال رأيت رسول الله صلى الله عليه وسلم توضأ ومسح على الخفين والخمار',NULL,'256',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (142,'١٤٠ - نا أبو أسامة نا زائدة عن الأعمش عن الحكم عن عبد الرحمن بن أبي ليلى عن البراء بن عازب عن بلال أن النبي صلى الله عليه وسلم كان يمسح على الخفين',NULL,'260',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (143,'١٤١ - حدثنا أبو أسامة قال سمعت الأعمش يقول أما تعجب من كثير النواء وسؤاله أبا جعفر عن أبي بكر وعمر ولو كان علي ها هنا ما سألته عن أبي بكر وعمر ولو كان علي ها هنا ما سألته عن أبي بكر وعمر',NULL,'260',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (144,'١٤٢ - حدثنا إبراهيم بن أعين قال سألت شريك بن عبد الله فقلت يا أبا عبد الله أرأيت من قال لا أفضل أحداً على أحد قال هذا أحمق أليس قد فضل أبو بكر وعمر قال قلت فأدركت أحداً يفضل عليهما قال لا إلا يفتضح قال وسمعت سعيد بن سعيد يقول من فضل على أبي بكر وعمر فقد عابهما قال قلت له: وعاب من فضل عليهما',NULL,'261',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (145,'١٤٣ - حدثنا عبد الله بن إدريس نا أبي عن سماك بن حرب عن علقمة بن وائل عن المغيرة بن شعبة قال بعثني رسول الله صلى الله عليه وسلم إلى أهل نجران قال ألستم تقرؤن {يَا أُخْتَ هَارُونَ} وقد علمتم ما كان بين موسى وعيسى فلم أدر ما أجيبهم فرجعت إلى رسول الله صلى الله عليه وسلم فأخبرته فقال ألا أخبرتهم أنهم كانوا يسمون بأنبيائهم والصالحين قبلهم',NULL,'262',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (146,'١٤٤ - حدثنا عبد الله بن إدريس عن الأعمش عن أبي الضحى قال رأيت في مفرق ابن الزبير عشية عرفة من الطيب ما لو كان لرجل كان رأس مال',NULL,'262',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (147,'١٤٥ - حدثنا حفص بن غياث عن أشعث عن عطاء قال أكثر الحيض خمس عشرة',NULL,'263',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (148,'١٤٦ - حدثنا المحاربي عن سلام بن سلم عن حميد عن أنس قال قال رسول الله صلى الله عليه وسلم وقت النفساء أربعون يوماً إلا أن ترى الطهر قبل ذلك',NULL,'263',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (149,'١٤٧ - حدثنا أبو خالد قال سمعت سفيان يقول لا تجاوز النفساء أربعين يوماً فإذا طهرت صامت وصلت ويأتيها زوجها',NULL,'264',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (150,'١٤٨ - حدثنا أبو خالد سمعت القاسم بن الوليد الهمداني يقول وقت النفساء ستون يوماً',NULL,'265',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (151,'١٤٩ - حدثنا شجاع بن الوليد بن قيس عن علي بن عبد الأعلى عن أبي سهل عن مسة الأسدية عن أم سلمة قالت كانت النفساء تجلس على عهد رسول الله صلى الله عليه وسلم أربعين يوماً وكنا نطلي وجوهنا بالورس من الكلف',NULL,'265',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (152,'١٥٠ - نا منصور بن وردان عن علي بن عبد الأعلى عن أبيه قال قال عمر وقت النفساء أربعون',NULL,'267',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (153,'١٥١ - حدثنا يحيى بن اليمان عن إسرائيل عن جابر عن عبد الله بن يسار عن سعيد بن المسيب عن عثمان قال وقت النفساء أربعون يوماً',NULL,'267',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (154,'١٥٢ - حدثنا ابن إدريس عن عبد الرحمن بن زياد عمن سمع عبد الله بن عمرو يقول قال رسول الله صلى الله عليه وسلم ما شد سليمان طرفه إلى السماء تخشعاً حيث أعطاه الله عز وجل ما أعطاه',NULL,'268',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (155,'١٥٣ - حدثنا ابن إدريس عن ليث عن حرمي مولى لزينب عن زينب عن رسول الله صلى الله عليه وسلم قال يغسل بول الجارية و [ينضح] (١) على بول الغلام
INSERT INTO [b26353] VALUES (156,'١٥٤ - حدثنا عبد السلام بن حرب نا الجلد بن أيوب عن معاوية بن قرة عن أنس قال الحيض ثلاث وأربع وخمس وست وسبع وثمان وتسع وعشر',NULL,'272',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (157,'١٥٥ - حدثنا عبد السلام عن الربيع بن صبيح عمن سمع أنساً يقول لا يكون الحيض أكثر من عشرة',NULL,'272',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (158,'١٥٦ - حدثنا ابن علية نا الجلد بن أيوب عن معاوية بن قرة عن أنس قال القرء ثلاث وأربع وخمس وست وسبع وثمان وتسع وعشر',NULL,'273',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (159,'١٥٧ - حدثنا خالد بن حيان الرقي عن هارون بن زياد القشيري عن الأعمش عن علقمة قال الحيض ثلاث وأربع وخمس وست وسبع وثمان وتسع وعشر فما زاد فهي مستحاضة',NULL,'273',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (160,'١٥٨ - حدثنا ابن إدريس عن عبيد الله عن الزهري عن أبي سلمة عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم من أدرك من الصلاة ركعة فقد أدرك الصلاة كلها',NULL,'274',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (161,'١٥٩ - حدثنا ابن إدريس عن محمد بن إسحاق عن عبد الله بن الفضل عن سليمان بن يسار قال سمعت ابن عمر يقول قتل مسيلمة عبدٌ أسود',NULL,'276',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (162,'١٦٠ - حدثنا ابن إدريس عن ليث عن مجاهد في قوله تعالى {فَسَوْفَ يَأْتِي اللهُ بِقَوْمٍ يُحِبُهُمْ وَيُحِبُونَهُ} هم قوم من سبأ',NULL,'276',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (163,'١٦١ - حدثنا ابن إدريس عن شعبة عن سماك بن حرب عن عياض الأشعري قال قال رسول الله صلى الله عليه وسلم هم قوم هذا لأبي موسى',NULL,'277',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (164,'١٦٢ - حدثنا عبد الله بن الأجلح عن محمد بن عمرو عن سالم الأفطس عن سعيد بن جبير عن ابن عباس في قوله تعالى {فَسَوْفَ يَأْتِي اللهُ بِقَوْمٍ يُحِبُهُمْ وَيُحِبُونَهُ} قال قوم من أهل اليمن ثم من كندة ثم من السكون',NULL,'280',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (165,'١٦٣ - حدثنا ابن إدريس عن شعبة قال قلت لأشعث بن أبي الشعثاء أكان أبوك يشرب النبيذ قال نعم حتى لقي عبد الله بن عمر فنهاه عنه وأحسبه عن رسول الله صلى الله عليه وسلم',NULL,'281',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (166,'١٦٤ - حدثنا ابن إدريس عن ليث عن مجاهد عن عائشة قالت قال رسول الله صلى الله عليه وسلم أول من يكسى خليل الله إبراهيم',NULL,'282',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (167,'١٦٥ - حدثنا ابن إدريس عن شعبة عن أبي بشر عن يوسف المكي عن محمد بن حاطب قال سمعت عليا يقول في قول الله عز وجل {إنَّ الّذِينَ سَبَقَتْ لَهُمْ مِنَا الحُسْنَى أُولَئِكَ عَنْهَا مُبْعَدُونَ} قال عثمان وأصحابه',NULL,'283',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (168,'١٦٦ - حدثنا عبد الله بن إدريس نا عاصم بن كليب عن أبيه عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم من رآني فقد رأى الحق فإن الشيطان لا يتمثل بي',NULL,'284',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (169,'١٦٧ - حدثنا ابن إدريس عن زكريا بن أبي زائدة قال كان الشعبي يمر بأبي صالح فيأخذ بأذنه فيمدها ويقول ويلك تفسر القرآن وأنت لا تحفظه',NULL,'285',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (170,'١٦٨ - نا سفيان (١) عن أبي إسحاق عن أبي الأحوص عن عبد الله ح
INSERT INTO [b26353] VALUES (171,'١٦٩ - ثنا وكيع نا سفيان عن أبي حصين عن أبي صالح عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم من رآني في المنام فقد رآني في اليقظة فإن الشيطان لا يتمثل علي',NULL,'287',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (172,'١٧٠ - حدثنا أبو أسامة عن سعيد بن أبي عروبة عن خليل بن حيان عن ابن أخي مطرف عن مطرف قال لقيت علياً بالحزين (١) يعني المبرد وما حوله فلما رآني أسرع نحوي فقلت أنا أحق بالإسراع إليك فقال ما منعك أن تأتينا فاعتذرت قال ما شغلك ولا منعك إلا حب عثمان قال فلما تنفس عن أصحابه قال إن تحبه فقد والله كان خيرنا وأبرنا وأوصلَنَا
INSERT INTO [b26353] VALUES (173,'١٧١ - حدثنا ابن إدريس قال سئل الأعمش عن حديث فامتنع فما زالوا به حتى استخرجوه منه فلما حدث به ضرب مثلاً فقال جاء قفاف إلى صيرفي بدراهم يريه إياها فلما ذهب [يزنها] (١) وجدها تنقص سبعين فقال
INSERT INTO [b26353] VALUES (174,'١٧٢ - حدثنا حفص عن الأعمش قال رأيت شقيقاً ومجاهداً والأزر منهما في نصف الساق والقميص فوق الإزار والرداء فوق القميص',NULL,'292',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (175,'١٧٣ - حدثنا أبو خالد عن يحيى بن سعيد قال سمعت علي بن حسين يقول يا أهل العراق أحبونا بحب الإسلام فوالله إن زال حبكم بنا حتى صار شيناً',NULL,'292',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (176,'١٧٤ - حدثنا أبو أسامة عن الأعمش قال قال رسول الله صلى الله عليه وسلم آفة العلم النسيان وإضاعته أن يحدث به غير أهله',NULL,'293',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (177,'١٧٥ - حدثنا حميد بن عبد الرحمن الرؤاسي قال سمعت الأعمش يقول انظروا هذه الدنانير فلا تنثروها على الكنايس يعني الحديث',NULL,'294',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (178,'١٧٦ - نا حميد بن عبد الرحمن قال سمعت أبي يقول سمعت الأعمش يقول انظروا لا تنثروا اللؤلؤ على الخنازير يعني الحديث',NULL,'295',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (179,'١٧٧ - حدثنا أبو خالد قال سمعت الأعمش يقول لولا أني أخاف أن تفوتني التكبيرة الأولى لأكلت حتى يقيم قال أبو سعيد يعني السحور',NULL,'295',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (180,'١٧٨ - حدثنا ابن إدريس قال سمعت الأعمش قرأ عنده رجل بلحن من هذه الألحان فقال الأعمش قرأ رجل عند أنس بهذه القراءة فكرهها',NULL,'296',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (181,'١٧٩ - حدثنا ابن إدريس عن عبد الله بن أبي السفر عن الشعبي قال ما [علمت] (١) أحداً انتصف من شريح إلا أعرابي أتاه في خصومة فجعل يكلمة ويمسه بيده فقال له شريح إن لسانك أطول من يدك فقال الأعرابي أسامري فلا تمس قال فلما أراد أن يقوم قال له شريح إني لم أرد بهذا شراً قال له الأعرابي ولا أجرمت إليك قال ابن إدريس وكانت القضاة تكره أن يقوم الخصم غضبان
INSERT INTO [b26353] VALUES (182,'١٨٠ - حدثنا أبو تميلة يحيى بن واضح المروزي نا شيخ كان يختلف معنا إلى محمد بن إسحاق قال لما أصيب عمر سمع صوت الجن
INSERT INTO [b26353] VALUES (183,'١٨١ - حدثنا ابن إدريس عن ليث عن معروف بن أبي معروف قال لما أصيب عمر سمع صوت
INSERT INTO [b26353] VALUES (184,'١٨٢ - حدثنا أبو معمر سعيد بن خثيم الهلالي حدثني ابن المعتمر البصري قال حدثني ابن الأعمش قال كان بالبصرة شيخ قد عمر وكان إذا قيل له كيف أصبحت أو كيف أمسيت قال
INSERT INTO [b26353] VALUES (185,'١٨٣ - حدثنا عبد الله بن إدريس سمعت الأعمش يرويه عن عبد الله بن مرة عن مسروق عن عبد الله قال كان النبي صلى الله عليه وسلم في نخل متوكياً على عسيب فعرض له يهودي فسأله عن الروح فجعل يقول برأسه فظننت أنه يوحى إليه فقال {يَسْأَلُونَكَ عَنِ الرُّوحِ قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي وَمَا أُوتِيْتُمْ مِنَ العِلْمِ إلاَّ قَلِيلاً}',NULL,'299',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (186,'١٨٤ - حدثنا وكيع نا الأعمش عن إبراهيم عن علقمة عن عبد الله قال كنت مع النبي صلى الله عليه وسلم في حرث بالمدينة فمر بقوم من اليهود فقال بعضهم لبعض سلوه وقال بعضهم لا تسألوه فقالوا يا محمد ما الروح فقام متوكياً على عسيبة فظننا أنه يوحى إليه فقال {يَسْأَلُونَكَ عَنِ الرُّوح قُلْ الرُوحُ مِنْ أمْرِ رَبِّي} الآية قال بعضهم لبعض قد قلنا لكم لا تسألوه',NULL,'302',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (187,'١٨٥ - حدثنا ابن إدريس عن عقبة بن إسحاق بن معاوية السلمي عن ليث عن مجاهد قال كنت إذا رأيت العرب استجفيتها فإذا .. .. رأيتها من .. .. دينها فإذا دخلوا في الصلاة فكأنها أجساد .. منها أرواح',NULL,'302',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (188,'١٨٦ - حدثنا الحماني عن عثمان بن واقد العمري عن أبي نصيرة عن مولى لأبي بكر عن أبي بكر رضي الله عنه قال قال رسول الله صلى الله عليه وسلم لم يصر من استغفر الله عز وجل ولو عاد في اليوم سبعين مرة',NULL,'303',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (189,'١٨٧ - حدثنا عبد الله بن إدريس عن ليث عن مجاهد قال أصاب كل امرأة من نساء عبد الرحمن بن عوف ربع الثمن ثمانون ألفاً',NULL,'305',NULL,NULL,'0');
INSERT INTO [b26353] VALUES (190,'قال الشيخ أبو القاسم عبيد الله هذا آخر ما عند يزداد بن عبد الرحمن بن محمد بن يزداد الكاتب أبي محمد الشيخ الفقيه الصالح من حديث أبي سعيد الأشج
