INSERT INTO [b26440] VALUES (1,'<span data-type="title" id=toc-1>الجزء الخامس [من] مسند مالك</span>
INSERT INTO [b26440] VALUES (2,'<span data-type="title" id=toc-3>حديث هشام بن عروة بن الزبير</span>
INSERT INTO [b26440] VALUES (3,'٢- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي صلى الله عليه وسلم أن رسول الله صلى الله عليه وسلم سئل عن الرقاب أيها أفضل فقال: أغلاها ثمنا وأنفسها عند أهلها.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (4,'٣- حدثنا أبو مصعب عن مالك عن هشام بن عروة عن أبيه عن النبي صلى الله عليه وسلم بمثله ولم يذكر أبو مصعب في إسناده عائشة.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (5,'٤- حدثنا ابن أبي أويس قال حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أنها قالت: جاءت بريرة فقالت: إني كاتبت أهلي على تسع أواق في كل عام أوقية فأعينيني فقالت: عائشة إن أحب أهلك أن أعدها لهم عنك عددتها ويكون ⦗٦⦘ ولاؤك لي فعلت فذهبت بريرة إلى أهلها فقالت لهم ذلك فأبوا عليها فجاءت من عند أهلها ورسول الله صلى الله عليه وسلم جالس فقالت: إني قد عرضت عليهم ذلك فأبوا إلا أن يكون الولاء لهم فسمع ذلك رسول الله عليه السلام فسألها فأخبرت عائشة رسول الله صلى الله عليه وسلم فقال: خذيها واشترطي لهم الولاء فإنما الولاء لمن أعتق ففعلت عائشة ثم قام رسول الله صلى الله عليه وسلم في الناس فحمد الله فأثنى عليه ثم قال: ما بال رجال يشترطون شروطا ليست في كتاب الله ما كان من شرط ليس في كتاب الله فهو باطل وإن كان مائة شرط قضاء الله أحق وشرط الله أوثق إنما الولاء لمن أعتق.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (6,'٥- حدثنا عبد الله بن مسلمة عن مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أنها قالت: لما قدم رسول الله عليه السلام المدينة وعك أبو بكر وبلال قالت: فدخلت عليهما فقلت: ياأبت كيف تجدك ويابلال كيف تجدك قالت: فكان أبو بكر إذا أخذته الحمى ⦗٧⦘ يقول:
INSERT INTO [b26440] VALUES (7,'٦- حدثنا؟ به أبو مصعب عن مالك وزاد فيه قال مالك: قال يحيى بن سعيد: قالت عائشة: وكان عامر بن فهيرة يقول:
INSERT INTO [b26440] VALUES (8,'٧- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عائشة ⦗٨⦘ أنها قالت كانت عندي امرأة من بني أسد فدخل النبي عليه السلام فقال: من هذه؟ فقلت له: هذه امرأة لا تنام الليل تذكر من صلاتها فقال رسول الله صلى الله عليه وسلم: مه عليكم من الأعمال بما تطيقون فوالله لا يمل الله حتى تملوا.',NULL,'7',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (9,'٨- حدثنا عبد الله قال حدثنا مالك عن هشام بن عروة أو ابن شهاب عن عروة عن عائشة قالت: كنت أغتسل أنا ورسول الله عليه السلام من إناء قال: أحسبه واحد.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (10,'٩- وحدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة أنها قالت: كنت أغتسل أنا ورسول الله من إناء واحد نغترف ⦗٩⦘ فيه جميعا.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (11,'١٠- حدثنا عبد الله قال حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة عن فاطمة بنت أبي حبيش قالت: يارسول الله إني لا أطهر أفأدع الصلاة؟ قال رسول الله صلى الله عليه وسلم: إنما ذلك عرق وليس بالحيضة.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (12,'١١- حدثنا عبد الله عن مالك عن هشام بن عروة عن عروة عن عائشة زوج النبي عليه السلام أن رسول الله صلى الله عليه وسلم كان إذا اغتسل من الجنابة بدأ بغسل يديه ثم توضأ كما يتوضأ للصلاة ثم يدخل أصابعه في الماء فيخلل أصول شعره ثم يصب على رأسه ثلاث غرف بيديه ثم يفيض الماء على جلده كله.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (13,'١٢- حدثنا عبد الله بن مسلمة عن مالك عن هشام بن عروة عن ⦗١٠⦘ أبيه عن عائشة أنها قالت: كنت أرجل رأس رسول الله عليه السلام وأنا حائض.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (14,'١٣- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عائشة أنها قالت: قالت فاطمة بنت أبي حبيش لرسول الله عليه السلام: يارسول الله إني لا أطهر أفأدع الصلاة؟ قالت: فقال رسول الله عليه السلام: إنما ذلك عرق وليس بالحيضة فإذا أقبلت فاتركي الصلاة فإذا ذهب قدرها واغسلي الدم عنك وصلي.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (15,'١٤- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أن الحارث بن هشام سأل رسول الله عليه السلام كيف يأتيك الوحي فقال رسول الله صلى الله عليه وسلم أحيانا يأتيني في مثل صلصلة الجرس وهو أشده علي فيفصم عني وقد وعيت ما قال وأحيانا يتمثل لي الملك رجلا فيكلمني فأعي ما يقول قالت عائشة ولقد رأيته ينزل عليه في اليوم الشديد البرد فيفصم عنه وإن جبينه ليتفصد عرقا.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (16,'١٥- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن ⦗١٢⦘ عائشة زوج النبي عليه السلام أن رسول الله صلى الله عليه وسلم قال: إذا نعس أحدكم في الصلاة فليرقد حتى يذهب عنه النوم فإن أحدكم إذا صلى وهو ناعس لعله يذهب يستغفر فيسب نفسه.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (17,'١٦- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عائشة أنها قالت: كان رسول الله صلى الله عليه وسلم يصلي بالليل ثلاث عشرة ركعة ثم يصلي إذا سمع النداء للصبح ركعتين خفيفتين.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (18,'١٧- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن ⦗١٣⦘ عائشة زوج النبي عليه السلام أنها قالت: صلى رسول الله عليه السلام في بيته وهو جالس فصلى وراءه قوم قياما فأشار إليهم أن اجلسوا فلما انصرف قال: إنما جعل الإمام ليؤتم به فإذا ركع فاركعوا وإذا رفع فارفعوا وإذا صلى جالسا فصلوا جلوسا.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (19,'١٨- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أنها أخبرته أنها لم تر رسول الله عليه السلام يصلي صلاة الليل قاعدا قط حتى أسن فكان يقرأ قاعدا حتى إذا أراد أن يركع قام فقرأ نحوا من ثلاثين أو أربعين آية ثم يركع.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (20,'١٩- حدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أنها قالت: أتي رسول الله عليه السلام ⦗١٤⦘ بصبي فبال على ثوبه فدعا بماء فأتبعه إياه.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (21,'٢٠- حدثنا أبو مصعب قال: حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة أن رسول الله صلى الله عليه وسلم رأى في جدار القبلة بصاقا أو مخاطا أو نخامة فحكه.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (22,'٢١- حدثنا أبو مصعب قال حدثنا مالك عن هشام عن أبيه عن عائشة أن النبي صلى الله عليه وسلم قال: مروا أبا بكر فليصل بالناس فقالت عائشة: يارسول الله إن أبا بكر إذا قام في مقامك لم يسمع الناس من البكاء فمر عمر ⦗١٥⦘ فليصل بالناس قالت عائشة: فقلت لحفصة: قولي له إن أبا بكر إذا قام في مقامك لم يسمع الناس من البكاء فمر عمر فليصل بالناس ففعلت حفصة فقال رسول الله صلى الله عليه وسلم: مه إنكن لأنتن صواحب يوسف مروا أبا بكر فليصل بالناس فقالت حفصة لعائشة: ما كنت لأصيب منك خيرا.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (23,'٢٢- حدثنا أبو مصعب قال: حدثنا مالك عن هشام بن عروة عن أبيه عائشة أنها قالت: خسفت الشمس على عهد رسول الله صلى الله عليه وسلم فصلى رسول الله صلى الله عليه وسلم بالناس فقام فأطال القيام ثم ركع فأطال الركوع ثم قام فأطال القيام وهو دون القيام الأول ثم ركع فأطال الركوع وهو دون الركوع الأول ثم رفع ثم سجد ثم فعل في الركعة الأخرى مثل ذلك ثم انصرف وقد تجلت الشمس فخطب الناس فحمد الله وأثنى عليه ثم قال: إن الشمس والقمر آيتان من آيات الله لا يخسفان لموت أحد ولا لحياته فإذا رأيتم ذلك فادعوا ⦗١٦⦘ الله وكبروا وتصدقوا وقال: ياأمة محمد والله ما من أحد أغير من الله أن يزني عبده أو تزني أمته ياأمة محمد لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (24,'٢٣- حدثنا عبد الله وأبو مصعب عن مالك عن هشام بن عروة عن أبيه عن عائشة أم المؤمنين أنها قالت جاء عمي من الرضاعة فاستأذن علي فأبيت أن آذن له حتى أسأل رسول الله صلى الله عليه وسلم قالت: فجاء رسول الله عليه السلام فسألته عن ذلك فقال: إنه عمك فأذني له قالت: قلت يارسول الله إنما أرضعتني المرأة ولم يرضعني الرجل فقال رسول الله صلى الله عليه وسلم: إنه عمك فليلج عليك قالت عائشة: وذلك بعد أن ضرب علينا الحجاب فقالت عائشة: يحرم من الرضاعة ما يحرم من الولادة.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (25,'٢٤- حدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن ⦗١٧⦘ أبيه عن عائشة أن رجلا قال للنبي عليه السلام: إن أمي افتلتت نفسها وأراها لو تكلمت تصدقت أفأتصدق عنها؟ فقال رسول الله صلى الله عليه وسلم: نعم.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (26,'٢٥- حدثنا أبو مصعب عن مالك عن هشام بن عروة عن أبيه أنه قال: قلت لعائشة زوج النبي عليه السلام وأنا يومئذ حديث السن: أرأيت قول الله تبارك وتعالى (إن الصفا والمروة من شعائر الله فمن حج البيت أو اعتمر فلا جناح عليه أن يطوف بهما) فما أرى على أحد شيئا ألا يطوف بهما فقالت عائشة: كلا لو كان كما تقول لكانت: فلا جناح عليه أن لا يطوف بهما إنما أنزلت هذه الآية في الأنصار كانوا يهلون لمناة وكانت مناة حذو قديد وكانوا يتحرجون أن يطوفوا بين الصفا والمروة فلما جاء الإسلام سألوا رسول الله صلى الله عليه وسلم ⦗١٨⦘ عن ذلك فأنزل الله تبارك وتعالى (إن الصفا والمروة من شعائر الله فمن حج البيت أو اعتمر فلا جناح عليه أن يطوف بهما) .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (27,'٢٦- حدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أن رسول الله صلى الله عليه وسلم ذكر صفية بنت حيي فقيل إنها قد حاضت فقال رسول الله صلى الله عليه وسلم: لعلها حابستنا فقالوا: يا رسول الله إنها قد أفاضت قال: فلا إذا.
INSERT INTO [b26440] VALUES (28,'٢٧- حدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن ⦗١٩⦘ أبيه عن عائشة زوج النبي عليه السلام أنها كانت تقول إن كان رسول الله ليقبل بعض نسائه وهو صائم ثم تضحك.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (29,'٢٨- حدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن أبيه عن عائشة أن حمزة بن عمرو الأسلمي قال لرسول الله عليه السلام: أصوم في السفر وكان كثير الصيام فقال له رسول الله عليه السلام: إن شئت فصم وإن شئت فأفطر.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (30,'٢٩- حدثنا.. .. .. ..؟ .. .. .. .. عن مالك عن هشام بن عروة عن أبيه عن عائشة زوج النبي عليه السلام أنها قالت: كان يوم عاشوراء يوما تصومه ⦗٢٠⦘ قريش في الجاهلية وكان النبي عليه السلام يصومه في الجاهلية فلما قدم النبي عليه السلام المدينة صامه وأمر بصيامه فلما فرض رمضان كان هو الفريضة وترك يوم عاشوراء فمن شاء صامه ومن شاء تركه.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (31,'٣٠- حدثنا أبو مصعب قال حدثني مالك عن هشام بن عروة عن أبيه عن عائشة أن رسول الله عليه السلام كفن في ثلاثة أثواب بيض سحولية ليس فيها قميص ولا عمامة.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (32,'٣١- حدثنا ابن أبي أويس قال حدثنا مالك عن هشام بن عروة عن أبيه عن عبد الله بن عمرو بن العاص قال: سمعت رسول الله صلى الله عليه وسلم يقول: إن ⦗٢١⦘ الله لا يقبض العلم انتزاعا ينتزعه من العباد ولكن يقبض العلم بقبض العلماء حتى إذا لم يبق عالما اتخذ الناس رؤوسا جهالا فسئلوا فأفتوا بغير علم فضلوا وأضلوا.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (33,'٣٢- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن عمر بن أبي سلمة أنه رأى رسول الله يصلي في ثوب واحد في بيت أم سلمة واضعا طرفيه على عاتقيه.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (34,'٣٣- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه أن عبد الله بن الأرقم كان يؤم أصحابه فحضرت الصلاة يوما فذهب لحاجته ثم رجع فقال: سمعت رسول الله صلى الله عليه وسلم يقول: إذا وجد أحدكم الغائط فليبدأ به قبل الصلاة.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (35,'حدثنا محمد بن أبي المنظور قال حدثنا إسماعيل بن إسحاق قال:
INSERT INTO [b26440] VALUES (36,'٣٥- حدثنا عبد الله عن مالك عن هشام عن أبيه عن عبد الله بن ⦗٢٣⦘ الزبير عن سفيان بن أبي زهير أنه قال: سمعت رسول الله عليه السلام يقول: تفتح اليمن فيأتي قوم يبسون فيتحملون بأهليهم ومن أطاعهم والمدينة خير لهم لو كانوا يعلمون ثم تفتح العراق فيأتي قوم يبسون فيتحملون بأهليهم ومن أطاعهم والمدينة خير لهم لو كانوا يعلمون ثم تفتح الشأم فيأتي قوم يبسون فيتحملون بأهليهم ومن أطاعهم والمدينة خير لهم لو كانوا يعلمون.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (37,'٣٦- حدثنا إبراهيم بن حمزة قال حدثنا عبد العزيز بن محمد عن هشام بن عروة عن أبيه عن عبد الله بن الزبير عن سفيان بن أبي زهير أنه سمع النبي صلى الله عليه وسلم يقول، ثم ذكر مثله.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (38,'٣٧- حدثنا أبو مصعب قال حدثنا مالك عن هشام عن أبيه عن ⦗٢٤⦘ المسور بن مخرمة أن سبيعة الأسلمية نفست بعد وفاة زوجها بليال فجاءت رسول الله صلى الله عليه وسلم فاستأذنته أن تنكح فأذن لها فنكحت.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (39,'٣٨- حدثنا عبد الله عن مالك عن هشام عن أبيه عن حمران أن عثمان بن عفان جلس على المقاعد فجاء المؤذن فآذنه بصلاة العصر فدعا بماء فتوضأ ثم قال: والله لأحدثنكم حديثا لولا آية في كتاب الله ما حدثتكموه ثم قال: سمعت رسول الله صلى الله عليه وسلم يقول: ما من امرىء يتوضأ فيحسن وضوءه ثم يصلي الصلاة إلا غفر له ما بينه وبين الصلاة الأخرى حتى يصليها،
INSERT INTO [b26440] VALUES (40,'٣٩- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن المسور بن مخرمة أنه أخبره أنه دخل على عمر بن الخطاب بعد أن صلى الصبح من الليلة التي طعن فيها عمر فأوقظ عمر فقيل له: الصلاة، لصلاة الصبح، فقال عمر: نعم ولا حظ في الإسلام لمن ترك الصلاة فصلى عمر وجرحه يثعب دما.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (41,'٤٠- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن زبيد بن الصلت أنه قال: خرجت مع عمر بن الخطاب إلى الجرف فنظر فإذا هو قد احتلم وصلى ولم يغتسل فقال: والله ما أراني إلا وقد احتلمت وما شعرت وصليت وما اغتسلت قال: فاغتسل وغسل ما رأى في ثوبه ونضح ما لم ير وأذن وأقام ثم صلى الصبح بعد ارتفاع الضحى متمكنا.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (42,'٤١- حدثنا عبد الله عن مالك عن هشام عن أبيه عن يحيى بن عبد ⦗٢٦⦘ الرحمان بن حاطب أنه اعتمر مع عمر بن الخطاب في ركب فيهم عمرو بن العاص وأن عمر عرس ببعض الطريق من بعض المياه فاحتلم عمر وقد كاد أن يصبح فلم يجد مع الركب ماء فركب حتى جاء الماء فجعل يغسل ما رأى في ثوبه من احتلام حتى أسفر فقال له عمرو بن العاص: قد أصبحت ومعنا ثياب فدع ثوبك يغسل فقال عمر: واعجبا لك ياابن العاص إن كنت تجد ثيابا أفكل الناس يجد ثيابا فوالله لو فعلتها لكانت سنة بل أغسل ما رأيت وأنضح ما لم أر.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (43,'٤٢- حدثنا عبد الله عن مالك عن هشام بن عروة عن أبيه عن زينب بنت أبي سلمة أنها قالت: جاءت أم سليم امرأة أبي طلحة إلى رسول الله عليه السلام فقالت: يارسول الله إن الله لا يستحيي من الحق هل على المرأة من غسل إذا هي احتلمت فقال: نعم إذا رأت الماء.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (44,'٤٣- حدثنا أبو مصعب قال: حدثنا مالك عن هشام بن عروة عن أبيه عن زينب بنت أبي سلمة عن أم سلمة زوج النبي عليه السلام عن النبي ⦗٢٧⦘ صلى الله عليه وسلم بمثله.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (45,'٤٤- حدثنا أبو مصعب قال حدثنا مالك عن هشام بن عروة عن زينب بنت أبي سلمة عن أم سلمة زوج النبي عليه السلام أن رسول الله صلى الله عليه وسلم قال: إنما أنا بشر مثلكم وإنكم تختصمون إلي فلعل بعضكم أن يكون ألحن بحجته من بعض فأقضي له على نحو ما أسمع منه فمن قضيت له بشيء من حق أخيه فلا يأخذ منه شيئا فإنما أقطع له قطعة من النار.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (46,'٤٥- حدثنا أبو مصعب قال حدثنا مالك عن هشام عن أبيه أن مخنثا كان عند أم سلمة زوج النبي عليه السلام فقال لعبد الله بن أبي أمية ورسول الله يسمع: ياعبد الله إن فتح الله لكم الطائف غدا فأنا أدلك على بنت غيلان فإنها تقبل بأربع وتدبر بثمان فقال رسول الله صلى الله عليه وسلم ⦗٢٨⦘: لا يدخلن هؤلاء عليكم.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (47,'٤٦- حدثنا أبو مصعب الزهري عن مالك بن أنس عن هشام بن عروة عن عباد بن عبد الله بن الزبير عن عائشة زوج النبي عليه السلام أنها أخبرته أنها سمعت النبي عليه السلام يقول قبل أن يموت وهو مستند إلى صدرها وأصغت إليه وهو يقول: اللهم اغفر لي وارحمني وألحقني بالرفيق.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (48,'٤٧- حدثنا عبد الله بن مسلمة عن مالك عن هشام بن عروة عن فاطمة بنت المنذر أن أسماء بنت أبي بكر كانت إذا أتيت بالمرأة قد حمت تدعو لها أخذت الماء فصبته بينها وبين جيبها وقالت: إن رسول الله صلى الله عليه وسلم كان يأمرنا أن نبردها بالماء.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (49,'٤٨- حدثنا عبد الله بن مسلمة عن مالك عن هشام بن عروة عن فاطمة بنت المنذر عن أسماء بنت أبي بكر أنها قالت: سألت امرأة رسول الله عليه السلام فقالت: يارسول الله أرأيت إحدانا إذ أصاب ثوبها الدم من الحيضة كيف تصنع؟ قال: إذا أصاب ثوب إحداكن الدم من الحيضة فلتقرصه ثم لتنضحه بالماء ثم لتصل.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (50,'٤٩- حدثنا أبو مصعب عن مالك عن عشام بن عروة عن فاطمة بنت المنذر عن أسماء بنت أبي بكر الصديق أنها قالت: أتيت عائشة زوج النبي عليه السلام حين خسفت الشمس فإذا الناس قيام يصلون وإذا هي قائمة تصلي فقلت: ما للناس فأشارت بيدها] إلى ⦗٣٠⦘ السماء فقالت: سبحان الله فقلت: آية فأشارت أن نعم قالت: فقمت حتى تجلاني الغشي فجعلت أصب الماء فوق رأسي فحمد الله رسول الله وأثنى عليه ثم قال: ما من شيء كنت لم أره إلا قد رأيته في مقامي هذا حتى الجنة والنار ولقد أوحي إلي أنكم تفتنون في القبور مثل أو قريب من فتنة الدجال لا أدري أيتهما قالت أسماء يؤتى أحدكم فيقال له: ما علمك بهذا الرجل فأما المؤمن أو الموقن لا أدري أي ذلك قالت أسماء فيقول: محمد رسول الله جاءنا بالبينات والهدى فأجبنا وآمنا واتبعنا فيقال: نم صالحا فقد علمنا إن كنت لمؤمنا وأما المنافق أو المرتاب لا أدري أيتهما قالت أسماء فيقول: لا أدري سمعت الناس يقولون شيئا فقلته.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (51,'<span data-type="title" id=toc-4>حديث مالك عن أبي الزبير محمد بن مسلم</span>
INSERT INTO [b26440] VALUES (52,'٥١- حدثنا عبد الله بن مسلمة عن مالك عن أبي الزبير المكي عن سعيد بن جبير عن عبد الله بن عباس أنه قال: صلى رسول الله صلى الله عليه وسلم الظهر والعصر جميعا والمغرب والعشاء جميعا في غير خوف ولا سفر.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (53,'٥٢- حدثنا عبد الله بن مسلمة قال مالك: أرى ذلك كان في مطر.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (54,'٥٣- حدثنا عبد الله بن مسلمة عن مالك عن أبي الزبير المكي عن طاوس اليماني عن عبد الله بن عباس أن رسول الله صلى الله عليه وسلم كان يعلمهم هذا الدعاء كما يعلمهم السورة من القرآن يقول: اللهم إني أعوذ بك من عذاب جهنم وأعوذ بك من عذاب القبر وأعوذ بك من فتنة المسيح الدجال وأعوذ بك من فتنة المحيا والممات.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (55,'٥٤- حدثنا عبد الله بن مسلمة عن مالك عن أبي الزبير عن طاوس عن عبد الله بن عباس أن رسول الله صلى الله عليه وسلم كان إذا قام في الصلاة من جوف الليل يقول: اللهم لك الحمد وأنت نور السموات والأرض ولك الحمد أنت قيام السموات والأرض ولك الحمد أنت رب السموات ⦗٣٤⦘ والأرض ومن فيهن أنت الحق وقولك الحق ووعدك حق ولقاؤك حق والجنة حق والنار حق والساعة حق اللهم لك أسلمت وبك آمنت وعليك توكلت وإليك أنبت وبك خاصمت وإليك حاكمت فاغفر لي ما قدمت وأخرت وأسررت وأعلنت أنت إلهي لا إله إلا أنت.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (56,'٥٥- حدثنا أبو مصعب قال حدثني مالك عن أبي الزبير المكي عن جابر بن عبد الله قال نحرنا مع رسول الله عليه السلام بالحديبية البدنة عن سبعة والبقرة عن سبعة.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (57,'٥٦- حدثنا أبو مصعب عن مالك بن أنس عن أبي الزبير المكي ⦗٣٥⦘ عن جابر بن عبد الله أنه أخبره أن رسول الله صلى الله عليه وسلم نهى عن أكل لحوم الضحايا بعد ثلاث ثم قال بعد: كلوا وتزودوا وادخروا.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (58,'٥٧- حدثنا عبد الله عن مالك عن أبي الزبير عن جابر بن عبد الله أن رسول الله صلى الله عليه وسلم نهى أن يأكل الرجل بشماله أو أن يمشي في نعل واحدة أو أن يشتمل الصماء وأن يحتبي في ثوب واحد كاشفا عن فرجه.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (59,'٥٨- حدثنا عبد الله بن مسلمة عن مالك عن أبي الزبير المكي عن جابر بن عبد الله أن رسول الله عليه السلام نهى أن يأكل الرجل بشماله أو أن يمشي في نعل واحدة وأن يشتمل الصماء وأن يحتبي في ثوب واحد كاشفا عن فرجه.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (60,'٥٩- حدثنا عبد الله بن مسلمة عن مالك عن أبي الزبير المكي عن ⦗٣٦⦘ جابر بن عبد الله أن رسول الله صلى الله عليه وسلم قال: أغلقوا الباب وأوكوا السقاء وأكفئوا الإناء أو خمروا الإناء وأطفئوا المصباح فإن الشيطان لا يفتح غلقا ولا يحل وكاء ولا يكشف إناء وإن الفويسق تضرم على الناس بيتهم أو بيوتهم.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (61,'<span data-type="title" id=toc-5>حديث حميد بن قيس المكي</span>
INSERT INTO [b26440] VALUES (62,'٦١- حدثنا.. .. ..؟ .. .. .. عن مالك عن حميد بن قيس عن مجاهد عن عبد الرحمان بن أبي ليلى عن كعب بن عجرة أن رسول الله عليه السلام قال له: لعله آذاك هوامك فقلت: نعم فقال رسول الله صلى الله عليه وسلم: احلق رأسك وصم ثلاثة أيام أو أطعم ستة مساكين أو انسك بشاة.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (63,'<span data-type="title" id=toc-6>حديث عطاء الخراساني</span>
INSERT INTO [b26440] VALUES (64,'<span data-type="title" id=toc-7>حديث أيوب بن أبي تميمة</span>
INSERT INTO [b26440] VALUES (65,'٦٤- حدثنا عبد الله بن مسلمة عن مالك عن أيوب السختياني عن محمد عن أبي هريرة أن رسول الله صلى الله عليه وسلم انصرف من اثنتين فقال له ذو ⦗٤٠⦘ اليدين: أقصرت الصلاة أم نسيت يارسول الله فقال رسول الله صلى الله عليه وسلم: صدق ذو اليدين، فقالوا: نعم فقام رسول الله فصلى اثنتين أخريين ثم سلم ثم كبر ثم سجد مثل سجوده أو أطول ثم رفع ثم كبر فسجد مثل سجوده أو أطول ثم رفع.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (66,'٦٥- حدثنا أبو مصعب قال حدثني مالك عن أيوب بن أبي تميمة السختياني عن محمد بن سيرين عن أم عطية الأنصارية قالت: دخل علينا رسول الله عليه السلام حين توفيت ابنته فقال: اغسلنها ثلاثا او خمسا أو أكثر من ذلك إن رأيتن بماء وسدر واجعلن في الآخرة كافورا أو شيئا من كافور فإذا فرغتن فآذنني قالت: فلما فرغنا آذناه فأعطانا حقوه فقال: أشعرنها إياه يعني إزاره.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (67,'<span data-type="title" id=toc-8>حديث حميد الطويل</span>
INSERT INTO [b26440] VALUES (68,'٦٧- حدثنا.. .. ..؟ .. .. .. عن مالك عن حميد الطويل عن أنس ⦗٤٢⦘ بن مالك أنه قال: احتجم رسول الله عليه السلام حجمه أبو طيبة فأمر له رسول الله صلى الله عليه وسلم بصاع من تمر وأمر أهله أن يخففوا عنه من خراجه.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (69,'٦٨- حدثنا.. ..؟ .. .. .. عن مالك عن حميد الطويل عن أنس بن مالك أن رسول الله صلى الله عليه وسلم نهى عن بيع الثمار حتى تزهي فقيل له: يارسول الله وما تزهي قال: تحمر، وقال رسول الله أرأيت إذا منع الله الثمرة فبم يأخذ أحدكم مال أخيه.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (70,'٦٩- حدثنا أبو مصعب قال حدثنا مالك عن حميد الطويل عن أنس بن مالك أن رسول الله صلى الله عليه وسلم حين خرج إلى خيبر أتاها ليلا وكان إذا جاء قوما بليل لم يغر حتى يصبح قال: فلما أصبح خرجت يهود ⦗٤٣⦘ بمساحيها ومكاتلها فلما رأوه قالوا: محمد والله محمد والخميس، فقال رسول الله عليه السلام: الله أكبر خربت خيبر إنا إذا نزلنا بساحة قوم فساء صباح المنذرين.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (71,'٧٠- حدثنا أبو مصعب قال حدثنا مالك عن حميد الطويل عن أنس بن مالك أنه قال سافرنا مع رسول الله عليه السلام في رمضان فلم يعب الصائم على المفطر ولا المفطر على الصائم.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (72,'٧١- حدثنا أبو مصعب عن مالك عن حميد الطويل عن أنس بن مالك أنه قال: خرج علينا رسول الله عليه السلام في رمضان فقال: إني رأيت هذه الليلة حتى تلاحى رجلان فرفعت فالتمسوها في التاسعة والسابعة والخامسة.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (73,'<span data-type="title" id=toc-9>حديث يحيى بن سعيد الأنصاري</span>
INSERT INTO [b26440] VALUES (74,'٧٣- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد أنه سمع سعيد بن المسيب يقول: لما صدر عمر بن الخطاب من منى أناخ بالأبطح ثم كوم كومة بطحاء ثم طرح عليها رداءه ثم استلقى ومد يده ⦗٤٥⦘ إلى السماء فقال: اللهم كبرت سني وضعفت قوتي وانتشرت رعيتي فاقبضني إليك غير مضيع ولا مفرط ثم قدم المدينة في عقب ذي الحجة فخطب الناس وقال: أيها الناس قد سنت لكم السنن وفرضت لكم الفرائض وتركتم على الواضحة إلا أن تضلوا بالناس يمينا وشمالا وصفق بإحدى يديه على الأخرى ثم قال: إياكم أن تهلكوا عن آية الرجم أن يقول قائل لا نجد حدين في كتاب الله فقد رجم رسول الله عليه السلام ورجمنا فوالذي نفسي بيده لولا أن يقول الناس: زاد عمر في كتاب الله لكتبتها " الشيخ والشيخة فارجموهما البتة فإنا قد قرأناها.
INSERT INTO [b26440] VALUES (75,'٧٤- حدثنا عبد الله قال حدثنا مالك عن يحيى بن سعيد ⦗٤٦⦘ قال: أخبرنا عبادة بن الوليد عن أبيه عن جده قال: بايعنا رسول الله على السمع والطاعة في اليسر والعسر والمنشط والمكره وألا ننازع الأمر أهله وأن نقول أو نقوم بالحق حيثما كنا لا نخاف في الله لومة لائم.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (76,'٧٥- حدثنا أبو مصعب الزهري قال حدثنا مالك عن يحيى بن سعيد أنه قال أخبرني عبادة بن الوليد بن عبادة بن الصامت أن أباه أخبره عن عبادة بن الصامت قال: بايعنا رسول الله عليه السلام على السمع والطاعة في العسر واليسر ثم ذكر معنى حديث القعنبي.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (77,'٧٦- حدثنا عبد الله عن مالك عن يحيى بن سعيد عن القاسم بن محمد أن عائشة كانت تقول إذا تشهدت: التحيات الطيبات الصلوات والزاكيات لله أشهد أن لا إله إلا الله وأن محمدا عبد الله ورسوله السلام عليك أيها النبي ورحمة الله وبركاته السلام علينا وعلى عباد الله الصالحين السلام عليكم.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (78,'٧٧- حدثنا عبد الله عن مالك عن يحيى بن سعيد عن أبي سلمة بن عبد الرحمان قال سمعت أبا قتادة بن ربعي يقول: سمعت رسول الله صلى الله عليه وسلم يقول: الرؤيا من الله والحلم من الشيطان فإذا رأى أحدكم الشيء يكرهه فلينفت عن يساره ثلاث مرات إذا استيقظ ثم ليتعوذ من شرها فإنها لن تضره إن شاء الله.
INSERT INTO [b26440] VALUES (79,'٧٨- حدثنا أبو مصعب قال: حدثنا مالك عن يحيى بن سعيد عن سليمان بن يسار أن عبد الله بن عباس وأبا سلمة بن عبد الرحمان اختلفا في المرأة تنفس بعد وفاة زوجها بليال فقال عبد الله: آخر الأجلين وقال أبو سلمة: إذا نفست فقد حلت وجاء أبو هريرة فقال: أنا مع ابن أخي ⦗٤٨⦘ يعني أبا سلمة فبعثوا كريبا مولى ابن عباس إلى أم سلمة زوج النبي عليه السلام يسألها عن ذلك فجاء فأخبرهم أنها قالت: ولدت سبيعة الأسلمية بعد وفاة زوجها بليال فذكرت ذلك لرسول الله عليه السلام فقال: قد حللت فانكحي.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (80,'٧٩- وحدثنا عبد الله عن مالك عن يحيى بن سعيد أنه سمع أبا الحباب سعيد بن يسار يقول: سمعت أبا هريرة يقول: قال رسول الله صلى الله عليه وسلم: أمرت بقرية تأكل القرى يقولون يثرب وهي المدينة تنفي الناس كما تنفي الكير خبث الحديد.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (81,'٨٠- وحدثنا ابن أبي أويس قال حدثنا مالك عن يحيى بن سعيد ⦗٤٩⦘ عن سعيد بن يسار عن أبي هريرة أنه قال: ما من أمير عشرة إلا وهو يؤتى به يوم القيامة مغلولا أطلقه الحق أو أوبقه.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (82,'٨١- وحدثنا أبو مصعب قال: حدثنا مالك عن يحيى بن سعيد عن عطاء بن أبي رباح أن مولى لأسماء بنت أبي بكر أخبره أنه جاء مع أسماء بنت أبي بكر منى بغلس قال: فقلت لها: لقد جئنا منى بغلس فقالت: قد كنا نأتيها هاهنا مع من هو خير منك.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (83,'٨٢- وحدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن عمرة بنت عبد الرحمان عن عائشة أن يهودية جاءت تسألها فقالت لها: أعاذك الله من عذاب القبر فسألت عائشة رسول الله عليه السلام: أيعذب الناس في قبورهم فقال رسول الله صلى الله عليه وسلم عائذا بالله من ذلك ثم ركب رسول الله عليه السلام ذات غداة مركبا فخسفت الشمس فرجع ضحى فمر بين ظهراني الحجر ثم قام يصلي وقام الناس وراءه ⦗٥٠⦘ فقام قياما طويلا ثم ركع ركوعا طويلا ثم رفع فقام قياما طويلا وهو دون القيام الأول ثم ركع ركوعا طويلا وهو دون الركوع الأول ثم رفع فسجد ثم قام قياما طويلا وهو دون القيام الأول ثم ركع ركوعا طويلا وهو دون الركوع الأول ثم قام قياما طويلا وهو دون القيام الأول ثم ركع ركوعا طويلا وهو دون الركوع الأول ثم رفع فسجد ثم انصرف فقال رسول الله صلى الله عليه وسلم ما شاء الله أن يقول ثم أمرهم أن يتعوذوا من عذاب القبر.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (84,'٨٣- حدثنا ابن أبي أويس قال حدثنا مالك عن يحيى بن سعيد عن عمرة بنت عبد الرحمان أن بريرة جاءت تستعين عائشة أم المؤمنين فقالت لها عائشة: إن أحب أهلك أن أصب لهم ثمنك صبة واحدة وأعتقك فعلت فذكرت ذلك بريرة لأهلها فقالوا: لا إلا أن يكون ولاؤك لنا.
INSERT INTO [b26440] VALUES (85,'٨٤- وحدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن عمرة بنت عبد الرحمان أن عائشة زوج النبي عليه السلام قالت: ما طال علي وما نسيت: القطع في ربع دينار فصاعدا.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (86,'٨٥- وحدثنا عبد الله بن مسلمة وأبو مصعب عن مالك عن يحيى بن سعيد عن عمرة بنت عبد الرحمان بن أسعد بن زرارة الأنصاري أنها أخبرته عن حبيبة بنت سهل الأنصارية أنها كانت تحت ثابت بن قيس بن شماس وأن رسول الله صلى الله عليه وسلم خرج إلى صلاة الصبح فوجد حبيبة بنت سهل عند بابه في الغلس فقال رسول الله صلى الله عليه وسلم: من هذه؟ قالت: أنا حبيبة بنت سهل قال: ما شأنك؟ قالت: لا أنا ولا ثابت بن قيس لزوجها فلما جاء ثابت بن قيس قال له رسول الله عليه السلام: هذه حبيبة بنت سهل قد ⦗٥٢⦘ ذكرت ما شاء الله أن تذكر فقالت حبيبة: يارسول الله كل ما أعطاني عندي فقال رسول الله صلى الله عليه وسلم لثابت بن قيس: خذ منها فأخذ منها وجلست في أهلها.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (87,'٨٦- حدثنا عبد الله بن مسلمة عن مالك عن يحيى بن سعيد عن عمرة بنت عبد الرحمان عن عائشة أنها قالت: إن كان رسول الله عليه السلام ليصلي الصبح فينصرف النساء متلفعات بمروطهن ما يعرفن من الغلس.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (88,'٨٧- وحدثنا أبو مصعب قال حدثني مالك عن يحيى بن سعيد ⦗٥٣⦘ عن عمرة بنت عبد الرحمن أنها سمعت عائشة تقول: خرجنا مع رسول الله عليه السلام لخمس ليال بقين من ذي القعدة ولا نراه إلا أنه الحج فلما دنونا من مكة أمر رسول الله صلى الله عليه وسلم من لم يكن معه هدي إذا طاف بالبيت وسعى بين الصفا والمروة أن يحل قالت عائشة: فدخل علينا يوم النحر بلحم بقر فقلت: ما هذا؟ فقالوا: نحر رسول الله صلى الله عليه وسلم [عن أزواجـ]ـه.
INSERT INTO [b26440] VALUES (89,'٨٨- وحدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن عمرة بنت عبد الرحمان أنها أخبرته أن عائشة زوج النبي عليه السلام قالت: لو أدرك رسول الله صلى الله عليه وسلم ما أحدث النساء لمنعهن المسجد كما منعته نساء بني إسرائيل.
INSERT INTO [b26440] VALUES (90,'٨٩- حدثنا أبو مصعب عن مالك عن يحيى بن سعيد عن عمر بن كثير بن أفلح عن أبي محمد مولى أبي قتادة عن أبي قتادة الأنصاري ثم السلمي أنه قال: خرجنا مع رسول الله صلى الله عليه وسلم عام حنين فلما التقينا كانت للمسلمين جولة قال: فرأيت رجلا من المشركين قد على رجلا من المسلمين فاستدرت له حتى أتيته من ورائه فضربته على حبل عاتقه ضربة فقطعت الدرع قال: وأقبل علي فضمني ضمة وجدت منها ريح الموت ⦗٥٥⦘ ثم أدركه الموت فأرسلني فلحقت عمر بن الخطاب فقلت له: ما بال الناس فقال: أمر الله قال: ثم إن الناس رجعوا فقال رسول الله صلى الله عليه وسلم: من قتل قتيلا له عليه بينة فله سلبه قال أبو قتادة: فقمت ثم قلت: من يشهد لي؟ ثم جلست، ثم قال ذلك الثالثة فقال رسول الله صلى الله عليه وسلم: ما لك ياأبا قتادة، فاقتصصت عليه القصة فقال رجل من القوم: صدق يارسول الله وسلب ذلك القتيل عندي فأرضه منه قال أبو بكر الصديق: لا هاء الله إذا لا يعمد إلى أسد من أسد الله يقاتل عن الله وعن رسوله فيعطيك سلبه فقال رسول الله صلى الله عليه وسلم: صدق فأعطه إياه، قال أبو قتادة: فأعطانيه فبعت الدرع فابتعت به مخرفا في بني سلمة فإنه لأول مال تأثلته في الإسلام.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (91,'٩٠- حدثنا عمرو بن مرزوق قال أخبرنا مالك بن أنس عن يحيى بن سعيد عن عمر بن كثير بن أفلح عن أبي محمد مولى أبي قتادة عن أبي قتادة أن رسول الله صلى الله عليه وسلم قال يوم حنين: من قتل قتيلا فله سلبه.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (92,'٩١- حدثنا ابن أبي أويس قال: حدثنا مالك عن يحيى بن سعيد ⦗٥٦⦘ قال: أخبرني أبو بكر بن محمد بن عمر بن حزم عن عمرة عن عائشة زوج النبي عليه السلام أنها قالت: سمعت رسول الله صلى الله عليه وسلم يقول: ما زال جبريل يوصيني بالجار حتى ظننت أنه ليورثنه.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (93,'٩٢- حدثنا أبو مصعب قال حدثني مالك عن يحيى بن سعيد عن أبي بكر بن محمد بن عمرو بن حزم عن عمر بن عبد العزيز عن أبي بكر بن عبد الرحمان عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال: أيما رجل أفلس فأدرك رجل ماله بعينه فهو أحق به من غيره.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (94,'٩٣- حدثنا عبد الله بن مسلمة قال حدثنا مالك عن يحيى بن ⦗٥٧⦘ سعيد عن محمد بن إبراهيم عن علقمة بن وقاص عن عمر بن الخطاب قال رسول الله صلى الله عليه وسلم: إنما الأعمال بالنية وإنما لامرىء ما نوى فمن كانت هجرته إلى الله ورسوله فهجرته إلى الله ورسوله ومن كانت هجرته لدنيا يصيبها وامرأة يتزوجها فهجرته إلى ما هاجر إليه.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (95,'٩٤- حدثنا عبد الله عن مالك عن يحيى بن سعيد عن محمد بن إبراهيم عن أبي حازم التمار عن البياضي أن رسول الله صلى الله عليه وسلم خرج على الناس وهم يصلون وقد علت أصواتهم بالقرآن فقال: إن المصلى يناجي ربه فلينظر ما يناجيه به ولا يجهر بعضكم على بعض بالقرآن.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (96,'٩٥- حدثنا عبد الله عن مالك عن يحيى بن سعيد عن محمد بن ⦗٥٨⦘ إبراهيم عن أبي سلمة بن عبد الرحمان عن أبي سعيد الخدري أنه قال: سمعت رسول الله عليه السلام يقول: يخرج فيكم قوم تحقرون صلاتكم مع صلاتهم وأعمالكم مع أعمالهم يقرؤون القرآن لا يجاوز حناجرهم يمرقون من الدين مروق السهم من الرمية تنظر في النصل فلا ترى شيئا ثم تنظر في القدح فلا ترى شيئا ثم تنظر في الريش فلا ترى شيئا وتتمارى في الفوق.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (97,'٩٦- حدثنا عبد الله بن مسلمة عن مالك عن يحيى بن سعيد عن محمد بن إبراهيم بن الحارث أن عائشة زوج النبي عليه السلام قالت: كنت نائمة إلى جنب رسول الله عليه السلام ففقدته من الليل فالتمسته بيدي فوضعت يدي على قدميه وهو ساجد وهو يقول: أعوذ برضاك من سخطك وبمعافاتك من عقوبتك وبك منك لا أحصي ثناء عليك أنت كما أثنيت على نفسك.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (98,'٩٧- حدثنا أبو مصعب عن مالك عن يحيى بن سعيد أنه قال: ⦗٥٩⦘ أخبرني محمد بن إبراهيم بن الحارث التيمي عن عيسى بن طلحة بن عبيد الله عن عمير بن سلمة الضمري أنه أخبره عن البهزي أن رسول الله صلى الله عليه وسلم خرج يريد مكة حتى إذا كان بالروحاء إذا حمار وحشي عقير وذكر لرسول الله عليه السلام فقال دعوه فإنه يوشك أن يأتي صاحبه فجاء البهزي وهو صاحبه إلى رسول الله عليه السلام فقال: يارسول الله شأنكم بهذا الحمار فأمر به رسول الله عليه السلام أبا بكر بقسمه بين الرفاق ثم مضى حتى إذا كان بالأثابة بين الرويثة والعرج إذا ظبي حاقف في ظل وفيه سهم فزعم أن رسول الله عليه السلام أمر رجلا يقف عنده لا يريبه أحد من الناس حتى يجاوزوه.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (99,'٩٨- حدثنا عبد الله بن مسلمة عن مالك عن يحيى بن سعيد عن محمد بن يحيى بن حبان عن ابن محيرز أن رجلا من بني كنانة يدعى المخدجي سمع رجلا بالشأم يدعى أبا محمد يقول: إن الوتر واجب فقال ⦗٦٠⦘ المخدجي فرحت إلى عبادة بن الصامت فاعترضت له وهو رائح إلى المسجد فأخبرته بالذي قال أبو محمد فقال عبادة: كذب أبو محمد سمعت رسول الله صلى الله عليه وسلم يقول: خمس صلوات كتبهن الله على العباد فمن جاء بهن لم يضيع منهن شيئا استخفافا بحقهن كان له عند الله عهد أن يدخله الجنة ومن لم يأت بهن فليس له عند الله عهد إن شاء عذبه فإن شاء أدخله الجنة.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (100,'٩٩- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن محمد بن يحيى بن حبان عن أبي عمرة الأنصاري عن زيد بن خالد الجهني أنه قال: توفي رجل يوم خيبر فذكروه لرسول الله عليه السلام فزعم زيد أنه قال: صلوا على صاحبكم فتغيرت وجوه الناس لذلك فزعم زيد أن رسول الله صلى الله عليه وسلم قال: إن صاحبكم قد غل في سبيل الله قال ففتحنا متاعه فوجدنا خرزات من خرز يهود ما تساوين درهمين.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (101,'١٠٠- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن ⦗٦١⦘ محمد بن يحيى بن حبان عن القاسم بن محمد عن عائشة زوج النبي عليه السلام أنها قالت: مر على عمر بن الخطاب بغنم من الصدقة فرأى فيها شاة حافلا ذات ضرع عظيم فقال عمر: ما هذه الشاة فقالوا: شاة من الصدقة فقال عمر: ما أعطى هذه أهلها وهم طائعون لا تفتنوا الناس لا تأخذوا حزرات المسلمين نكبوا عن الطعام.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (102,'١٠١- حدثنا عبد الله عن مالك عن يحيى بن سعيد عن محمد بن يحيى بن حبان عن عمه واسع بن حبان عن عبد الله بن عمر أنه كان يقول: إن أناسا يقولون إذا قعدت على حاجتك فلا تستقبل القبلة ولا بيت المقدس قال عبد الله بن عمر: لقد ارتقيت على ظهر البيت فرأيت رسول الله على لبنتين مستقبلا بيت المقدس لحاجته وقال: لعلك من الذين يصلون على أوراكهم فقلت: لا أدري والله.
INSERT INTO [b26440] VALUES (103,'١٠٢- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن محمد بن يحيى بن حبان أن عبدا سرق وديا من حائط رجل فغرسه في حائط سيده فخرج صاحب الودي يلتمس وديه فوجده فاستدعى على العبد مروان بن الحكم فسجن العبد وأراد قطع يده فانطلق صاحب العبد إلى رافع بن خديج فسأله عن ذلك فأخبره رافع أنه سمع رسول الله يقول: لا قطع في ثمر ولا كثر والكثر الجُمَّار فقال الرجل: فإن مروان بن الحكم أخذ غلاما لي وهو يريد قطع يده وأنا أحب أن تمشي معي إليه فتخبره بالذي سمعت من رسول الله فمشى معه رافع بن خديج حتى أتيا مروان بن الحكم فقال: أخذت غلاما لهذا؟ فقال: نعم قال: ما أنت صانع به؟ قال: أردت قطع يده قال له رافع: سمعت رسول الله عليه السلام يقول: لا قطع في ثمر ولا كثر فأمر مروان بالعبد فأرسل.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (104,'١٠٣- حدثنا عبد الله بن مسلمة عن مالك عن يحيى بن سعيد عن بشير بن يسار أن سويد بن النعمان أخبره أنه خرج مع رسول الله عليه السلام عام خيبرحتى إذا كانوا بالصهباء وهي من أدنى خيبر صلى العصر ثم دعا بالأزواد فلم يؤت إلا بالسويق فأمر به فثري فأكل رسول الله عليه السلام وأكلنا ثم قام إلى المغرب فمضمض ومضمضنا ثم صلى ولم يتوضأ.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (105,'١٠٤- حدثنا أبو مصعب عن مالك عن يحيى بن سعيد عن بشير ⦗٦٤⦘ ابن يسار أن أبا بردة بن نيار ذبح قبل أن يذبح رسول الله عليه السلام يوم الأضحى فزعم أن رسول الله عليه السلام أمره أن يعود بضحية أخرى فقال أبو بردة: لا أجد إلا جذعا قال رسول الله عليه السلام: وإن لم تجد إلا جذعا فاذبحه.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (106,'١٠٥- حدثنا علي بن عبد الله قال حدثنا معن بن عيسى عن مالك عن يحيى بن سعيد عن بشير بن يسار عن أبي بردة بن نيار أنه ذبح قبل أن يذبح رسول الله يوم الأضحى فزعم أن رسول الله صلى الله عليه وسلم أمره أن يعود بأضحية أخرى فقال أبو بردة: لا أجد إلا جذعا قال: وإن لم تجد إلا جذعا فاذبحه.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (107,'١٠٦- قال علي فأما الذين قالوا عن بشير بن يسار أن أبا بردة ذبح فحدثنا سفيان وحدثنا حماد بن زيد وحدثنا عبد الوهاب وذكر علي حديث كل واحد منهم.
INSERT INTO [b26440] VALUES (108,'١٠٧- حدثنا.. .. ..؟ .. .. .. عن مالك عن يحيى بن سعيد عن بشير بن يسار أنه أخبره أن عبد الله بن سهل الأنصاري ومحيصة بن مسعود خرجا إلى خيبر فتفرقا في حوائجهما فقتل عبد الله بن سهل فقدم محيصة فأتى هو وأخوه حويصة وعبد الرحمان بن سهل وهو أخو المقتول إلى رسول الله عليه السلام فذهب عبد الرحمان بن سهل ليتكلم لمكانه من أخيه فقال رسول الله عليه السلام: كبركبر فتكلم حويصة ومحيصة فذكرا له شأن عبد الله بن سهل فقال لهم رسول الله: أتحلفون خمسين يمينا وتستحقون دم صاحبكم أو قاتلكم قالوا: يارسول الله لم نشهد ولم نحضر فقال رسول الله صلى الله عليه وسلم: فتبرئكم يهود بخمسين يمينا فقالوا: يارسول الله كيف نقبل أيمان قوم كفار.
INSERT INTO [b26440] VALUES (109,'١٠٨- حدثنا عبد الله بن مسلمة عن مالك عن يحيى بن سعيد عن ⦗٦٦⦘ عدي بن ثابت الأنصاري عن عبد الله بن يزيد الخطمي أن أبا أيوب الأنصاري أخبره أنه صلى مع رسول الله في حجة الوداع المغرب والعشاء بالمزدلفة جميعا.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (110,'١٠٩- حدثنا عبد الله بن مسلمة عن مالك عن يحيى بن سعيد عن عدي بن ثابت الأنصاري عن البراء بن عازب أنه قال: صليت مع رسول الله عليه السلام العتمة فقرأ فيها (بالتين والزيتون) .',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (111,'١١٠- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن عمرو بن شعيب أن رجلا من بني مدلج يقال له قتادة حذف ابنه بسيف ⦗٦٧⦘ فأصاب ساقه فنزي في جرحه فمات فقدم سراقه بن جعشم على عمر بن الخطاب فذكر ذلك له فقال له عمر: اعدد لرحل قديد عشرين ومائة بعير حتى أقدم عليك فلما قدم عليه عمر أخذ من تلك الإبل ثلاثين حقة وثلاثين جذعة وأربعين خلفة ثم قال: أين أخو المقتول؟ فقال: هأنذا، فقال: خذها فإن رسول الله صلى الله عليه وسلم قال: ليس للقاتل شيء.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (112,'١١١- حدثنا عبد الله عن مالك عن يحيى بن سعيد عن عبد الرحمان الأعرج عن عبد الله بن بحينة أنه قال: إن رسول الله قام من اثنتين من الظهر ولم يجلس فيهما فلما قضى صلاته سجد سجدتين ثم سلم بعد ذلك.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (113,'١١٢- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن ⦗٦٨⦘ أبي صالح السمان عن أبي هريرة أن رسول الله عليه السلام قال: لولا أن أشق على أمتي لأحببت ألا أتخلف خلف سرية تخرج في سبيل الله ولكني لا أجد ما أحملهم عليه ولا يجدون ما يتحملون عليه ويشق عليهم أن يتخلفوا بعدي فوددت أني أقاتل في سبيل الله فأقتل ثم أحيا فأقتل ثم أحيا فأقتل.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (114,'١١٣- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن سعيد بن أبي سعيد المقبري عن عبد الله بن أبي قتادة عن أبيه أنه قال: جاء رجل إلى رسول الله عليه السلام فقال: يارسول الله أرأيت إن قتلت في سبيل الله صابرا محتسبا مقبلا غير مدبر يكفر الله عني خطاياي؟ فقال رسول الله صلى الله عليه وسلم: نعم فلما أدبر ناداه رسول الله أو أمر به فنودي له فقال رسول الله عليه السلام: كيف قلت؟ فأعاد قوله فقال النبي عليه السلام: نعم إلا الدين كذلك قال لي جبريل.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (115,'١١٤- حدثنا أبو مصعب قال حدثنا مالك عن يحيى بن سعيد عن واقد بن سعد بن معاذ الأنصاري عن نافع بن جبير بن مطعم عن مسعود بن الحكم عن علي بن أبي طالب أن رسول الله عليه السلام كان يقوم في الجنائز ثم جلس بعد.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (116,'١١٥- حدثنا أبو ثابت قال حدثني عبد الله بن وهب قال حدثني مالك عن يحيى بن سعيد عن عباد بن تميم أن عويمر بن أشقر ذبح أضحيته قبل أن يغدو يوم الأضحى وأنه ذكر ذلك لرسول الله عليه السلام فأمره أن يعود بضحية أخرى.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (117,'<span data-type="title" id=toc-10>حديث الفضيل بن أبي عبد الله</span>
INSERT INTO [b26440] VALUES (118,'١١٧- حدثنا عبد الله عن مالك عن مخرمة بن سليمان عن كريب مولى ابن عباس أن عبد الله بن عباس أخبره أنه بات عند ميمونة زوج النبي عليه السلام وهي خالته فاضطجعت في عرض الوسادة واضطجع رسول الله وأهله في طولها فنام رسول الله عليه السلام حتى إذا انتصف الليل أو قبله بقليل أو بعده بقليل استيقظ رسول الله عليه السلام فجلس يمسح النوم ⦗٧٢⦘ عن وجهه بيده ثم قرأ رسول الله صلى الله عليه وسلم العشر الآيات الخواتم من سورة آل عمران ثم قام إلى شنة معلقة فتوضأ منها وأحسن وضوءه ثم قام فصلى قال عبد الله: فقمت فصنعت مثل ما صنع ثم ذهبت فقمت إلى جنبه فوضع رسول الله يده اليمنى على رأسي وأخذ بأذني يفتلها فصلى ركعتين ثم ركعتين ثم ركعتين ثم ركعتين ثم ركعتين ثم ركعتين ثم أوتر ثم اضطجع حتى جاء المؤذن فقام فصلى ركعتين خفيفتين ثم خرج فصلى الصبح.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (119,'١١٨- حدثنا عبد الله عن مالك عن قطن بن وهب أن يحنس مولى آل الزبير أخبره أنه كان جالسا عند عبد الله بن عمر في الفتنة فجاءته مولاة له فسلمت عليه فقالت: ياأبا عبد الرحمان إني أردت الخروج واشتد علينا ⦗٧٣⦘ الزمان فقال لها عبد الله: اقعدي لكاع فإني سمعت رسول الله عليه السلام يقول: لا يصبر على لأوائها وشدتها أحد إلا كنت له شفيعا أو شهيدا يوم القيامة.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (120,'١١٩- حدثنا أبو مصعب عن مالك عن يونس بن يوسف بن حماس عن عمه عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال: لتتركن المدينة على أحسن ما كانت حتى يدخل الكلب أو الذئب فيغذي على بعض سواري المسجد أو على المنبر فقالوا: يارسول الله فلمن تكون الثمار ذلك الزمان؟ قال: للعوافي الطير والسباع.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (121,'١٢٠- حدثنا عبد الله عن مالك عن يونس بن يوسف عن عطاء بن يسار عن أبي أيوب الأنصاري أنه وجد غلمانا قد ألجؤوا ثعلبا إلى زاوية فطردهم عنه.
INSERT INTO [b26440] VALUES (122,'١٢١- حدثنا عبد الله عن مالك عن زيد بن رباح عن أبي عبد الله الأغر عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: صلاة في مسجدي هذا خير من ألف صلاة فيما سواه إلا المسجد الحرام.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (123,'١٢٢- وحدثنا عبد الله عن مالك عن عبيد الله بن سلمان الأغر عن أبيه عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: صلاة في مسجدي هذا خير من ألف صلاة فيما سواه إلا المسجد الحرام.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (124,'١٢٣- حدثنا عبد الله عن مالك عن زيد بن أبي أنيسة أن عبد ⦗٧٥⦘ الحميد بن عبد الرحمان بن زيد بن الخطاب أخبره عن مسلم بن يسار الجهني أن عمر بن الخطاب سئل عن هذه الآية {وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم وأشهدهم على أنفسهم ألست بربكم قالوا بلى شهدنا أن تقولوا يوم القيامة إنا كنا عن هذا غافلين} فقال عمر بن الخطاب: سمعت رسول الله عليه السلام سئل عنها فقال رسول الله عليه السلام: إن الله خلق آدم ثم مسح ظهره بيمينه فاستخرج منه ذرية فقال: خلقت هؤلاء للجنة وبعمل أهل الجنة يعملون ثم مسح ظهره فاستخرج منه ذرية فقال: خلقت هؤلاء للنار وبعمل أهل النار يعملون فقال رجل: يارسول الله ففيما العمل؟ فقال رسول الله عليه السلام: إن الله إذا خلق العبد للجنة استعمله بعمل أهل الجنة حتى يموت على عمل من أعمال أهل الجنة فيدخله به الجنة وإذا خلق العبد للنار استعمله بعمل النار حتى يموت على عمل من أعمال أهل النار فيدخله به النار.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (125,'١٢٤- حدثنا عبد الله عن مالك عن أيوب بن حبيب مولى سعد بن أبي وقاص قال القاضي: وقد حدثنا عبد الله في سماعه من مالك فقال عن أبي المثنى الجهني أنه قال: كنت عند مروان بن الحكم فدخل عليه أبو سعيد الخدري فقال له مروان: أسمعت رسول الله عليه السلام ينهى عن النفخ في الشراب؟ فقال أبو سعيد: نعم، فقال له رجل: يارسول الله إني لا أروى من نفس واحد فقال له رسول الله صلى الله عليه وسلم: فأبن القدح عن فيك ثم تنفس قال: فإني أرى القذاة قال: فأهرقها.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (126,'١٢٥- حدثنا ابن أبي أويس قال حدثني مالك عن هلال بن أسامة ⦗٧٧⦘ عن عطاء بن يسار عن عمر بن الحكم أنه قال: أتيت رسول الله عليه السلام فقلت: يارسول الله إن جارية لي كانت ترعى غنما لي فجئتها ففقدت شاة وكنت من بني آدم فلطمت وجهها وعلي رقبة أفأعتقها؟ فقال لها رسول الله عليه السلام: أين الله؟ قالت: في السماء، قال: من أنا؟ قالت: أنت رسول الله قال: أعتقها.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (127,'١٢٦- حدثنا أبو مصعب قال حدثنا مالك عن صيفي مولى ابن أفلح عن أبي السائب مولى هشام بن زهرة قال: دخلت على أبي سعيد الخدري في بيته قال: فوجدته يصلي فجلسنا ننتظره حتى قضى صلاته قال: فسمعت تحريكا تحت سرير في بيته فإذا حية فقمت لأقتلها فأشار ⦗٧٨⦘
INSERT INTO [b26440] VALUES (128,'١٢٧- وحدثنا أبو مصعب قال حدثنا مالك عن هاشم بن هاشم بن ⦗٧٩⦘ عتبة بن أبي وقاص عن عبد الله بن نسطاس عن جابر بن عبد الله السلمي أن النبي عليه السلام قال: من حلف على منبري هذا بيمين آثمة تبوأ مقعده من النار.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (129,'١٢٨- حدثنا ابو مصعب قال حدثنا مالك عن سعيد بن عمرو بن شرحبيل بن سعيد بن سعد بن عبادة عن أبيه عن جده أنه قال: خرج سعد بن عبادة مع النبي عليه السلام في بعض مغازيه وحضرت أمه الوفاة بالمدينة فقيل لها: أوصي فقالت: فيم أوصي إنما المال مال سعد فتوفيت قبل أن يقدم سعد فلما قدم سعد ذكر ذلك له فقال سعد: يا ⦗٨٠⦘ رسول الله هل ينفعها أن أتصدق عنها؟ فقال النبي عليه السلام: نعم فقال سعد: حائط كذا وكذا صدقة عنها الحائط سماه.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (130,'١٢٩- حدثنا أبو مصعب قال حدثني مالك عن أبي ليلى بن عبد الله بن عبد الرحمان بن سهل عن سهل بن أبي حثمة أنه أخبره هو ورجال من كبراء قومه أن عبد الله بن سهل ومحيصة خرجا إلى خيبر من جهد أصابهما فأتي محيصة فأخبر أن عبد الله بن سهل قد قتل وطرح في فقير أو عين فأتى يهود فقال: أنتم والله قتلتموه قالوا: والله ما قتلناه فأقبل حتى قدم على قومه فذكر لهم ذلك ثم أقبل هو وأخوه حويصة وهو أكبر منه وعبد الرحمان بن سهل أخو المقتول فذهب محيصة ليتكلم وهو الذي كان بخيبر فقال رسول الله صلى الله عليه وسلم لمحيصة: كبر كبر يريد السن ⦗٨١⦘ فتكلم حويصة ثم تكلم محيصة فقال رسول الله عليه السلام: إما أن تدوا صاحبكم وإما أن تؤذنوا بحرب فكتب إليهم رسول الله صلى الله عليه وسلم في ذلك فكتبوا: إنا والله ما قتلناه فقال رسول الله صلى الله عليه وسلم لحويصة ومحيصة وعبد الرحمان: أتحلفون وتستحقون دم صاحبكم؟ فقالوا: لا قال: فيحلف لكم يهود قالوا: ليسوا بمسلمين فوداه رسول الله صلى الله عليه وسلم من عنده فبعث إليهم بمائة ناقة حتى أدخلت عليهم في الدار قال سهل: لقد ركضتني منها ناقة حمراء.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (131,'١٣٠- وحدثنا عبد الله عن مالك عن محمد بن أبي أمامة بن سهل بن حنيف أنه سمع أباه يقول: اغتسل سهل بن حنيف بالخرار فنزع جبة كانت عليه وعامر بن ربيعة ينظر قال: وكان سهل رجلا أبيض حسن الجلد قال: فقال له عامر: ما رأيت كاليوم ولا جلد عذراء فوعك سهل مكانه واشتد وعكه فأتى رسول الله صلى الله عليه وسلم فأخبر أن سهلا قد وعك وأنه غير رائح معك فأتاه رسول الله عليه السلام فأخبره ⦗٨٢⦘ سهل الذي كان من شأن عامر فقال رسول الله عليه السلام: علام يقتل أحدكم أخاه ألا بركت إن العين حق توضأ له فتوضأ له عامر فراح سهل مع رسول الله صلى الله عليه وسلم ليس به بأس.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (132,'١٣١- حدثنا عبد الله عن مالك عن أبي بكر بن عمر عن سعيد بن يسار أنه قال: كنت أسير مع عبد الله بن عمر بطريق مكة قال سعيد: فلما خشيت الصبح نزلت فأوترت ثم أدركته فقال لي عبد الله: أين كنت فقلت له: خشيت الفجر فنزلت فأوترت فقال: أليس لك في رسول الله عليه السلام أسوة فقلت: بلى والله قال: فإن رسول الله صلى الله عليه وسلم كان يوتر على البعير.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (133,'١٣٢- وحدثنا عبد الله عن مالك عن إسماعيل بن محمد عن مولى لعمرو بن العاص أو لعبد الله بن عمرو بن العاص عن عبد الله بن عمرو بن العاص أن رسول الله صلى الله عليه وسلم قال: صلاة أحدكم وهو قاعد مثل نصف صلاته وهو قائم.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (134,'١٣٣- حدثنا أبو مصعب قال حدثنا مالك عن عمرو بن الحارث عن عبيد بن فيروز عن البراء بن عازب أن رسول الله صلى الله عليه وسلم سئل: ماذا يتقى من الضحايا فأشار بيده وقال: أربعا وكان البراء يشير بيده ويقول: يدي أقصر من يد رسول الله عليه السلام العرجاء البين ظلعها والعوراء البين عورها والمريضة البين مرضها والعجفاء التي لا تنقي.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (135,'١٣٤- حدثنا أبو مصعب عن مالك عن أبي بكر بن عثمان أنه سمع أبا أمامة بن سهل يقول: كنا نشهد الجنائز فما يجلس آخر الناس حتى يؤذنوا.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (136,'١٣٥- حدثنا أبو مصعب عن مالك عن طلحة بن عبد الملك الأيلي عن القاسم بن محمد عن عائشة زوج النبي صلى الله عليه وسلم أن رسول الله ⦗٨٥⦘ صلى الله عليه وسلم قال: من نذر أن يطيع الله فليطعه ومن نذر أن يعصي الله فلا يعصه.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (137,'١٣٦- حدثنا القعنبي عن مالك عن المسور بن رفاعة القرظي عن الزبير بن عبد الرحمان بن الزبير أن رفاعة بن سموال طلق امرأته تميمة بنت وهب في عهد رسول الله صلى الله عليه وسلم ثلاثا فنكحها عبد الرحمان بن الزبير فاعترض عنها ولم يستطع أن يغشاها ففارقها فأراد رفاعة أن ينكحها وهو زوجها الأول الذي كان طلقها فبلغ ذلك رسول الله صلى الله عليه وسلم فنهاه عن تزويجها وقال: لا تحل لك حتى تذوق العسيلة.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (138,'١٣٧- حدثنا القعنبي عن مالك عن سعيد بن إسحاق بن كعب بن ⦗٨٦⦘ عجرة عن عمته زينب بنت كعب أن الفريعة بنت مالك بن سنان وهي أخت أبي سعيد الخدري أخبرتها أنها جاءت رسول الله صلى الله عليه وسلم تسأله أن ترجع إلى أهلها في بني خدرة فإن زوجها خرج في طلب أعبد له أبقوا حتى إذا كانوا بطرف القدوم لحقهم فقتلوه فسألت رسول الله صلى الله عليه وسلم أن أرجع إلى أهلي فإن زوجي لم يتركني في مسكن يملكه ولا نفقة فقال رسول الله صلى الله عليه وسلم: نعم قالت: فخرجت حتى إذا كنت في الحجرة أو في المسجد دعاني أو أمر بي فدعيت له فقال: كيف قلت فرددت عليه القضية التي ذكرت له من شأن زوجي قالت: فقال امكثي في بيتك حتى يبلغ الكتاب أجله قالت: فاعتددت فيه أربعة أشهر وعشرا فلما كان عثمان بن عفان أرسل إلي فسألني عن ذلك فأخبرته فاتبعه وقضى به.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26440] VALUES (139,'١٣٨- حدثنا أبو مصعب قال حدثني مالك عن عبد الكريم بن مالك الجزري عن عبد الرحمان بن أبي ليلى عن كعب بن ⦗٨٧⦘ عجرة أنه كان مع رسول الله صلى الله عليه وسلم فآذاه القمل في رأسه فأمره رسول الله صلى الله عليه وسلم أن يحلق رأسه وقال: صم ثلاثة أيام أو أطعم ستة مساكين مدين مدين لكل إنسان أو انسك بشاة أي ذلك فعلت أجزأ عنك.
