INSERT INTO [b26510] VALUES (1,'مكتبة أبي عبيد القاسم بن سلام (١)

الخطب والمواعظ
لأبي عبيد القاسم بن سلام
(٢٢٤ هـ)

تحقيق
الدكتور رمضان عبد التواب

الناشر
مكتبة الثقافة الدينية',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b26510] VALUES (2,'بسم الله الرحمن الرحيم

<span data-type="title" id=toc-2>الخطب والمواعظ والحض على أعمال البر وطلب الخير</span>',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (3,'<span data-type="title" id=toc-3>خطب النبي محمد صلى الله عليه وسلم ووصاياه وعهده إلى الناس في الخطب وغيرها</span>
١ - حدثنا أبو إسحاق إبراهيم بن محمد بن عبد الرحمن بن أبي بكرة عن أبيه حدثنا أبو عبيد قال حدثنا إسماعيل بن عياش عن شرحبيل بن مسلم الخولاني قال سمعت أبا أمامة الباهلي يقول:
⦗٨٨⦘ قال رسول الله صلى الله عليه وسلم في خطبته عام حجة الوداع إن الله قد أعطى كل ذي حق حقه فلا وصية لوارث والولد للفراش وللعاهر الحجر وحسابهم على الله من ادعى إلى غير أبيه أو انتمى إلى غير مواليه فعليه لعنة الله البالغة إلى يوم القيامة لا تنفق امرأة من بيتها شيئاً إلا بإذن زوجها قيل يا رسول الله ولا الطعام قال ذاك أفضل أموالنا ثم قال: العارية مؤداة والمنحة مردودة والدين مقضي والزعيم غارم',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (4,'٢ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح وقال حدثنا معاوية بن صالح أن ضمرة بن حبيب حدثه أن عبد الرحمن بن عمرو السلمي حدثه أنه سمع العرباض بن سارية يقول
⦗٩٠⦘ قال رسول الله صلى الله عليه وسلم لقد تركتكم على البيضاء ليلها كنهارها فلا يزيغ عنها بعدي إلا هالك ومن يعش منكم فسيرى اختلافاً كثيراً فعليكم بما عرفتم من سنتي وسنة الخلفاء الراشدين المهديين وعضوا عليها بالنواجذ',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (5,'٣ - حدثنا أبو عبيد قال حدثنا عباد بن عباد المهلبي عن هشام بن زياد عن أبي المقدام عن محمد ⦗٩١⦘ ابن كعب أنه [حدثه] (١) ميمون بن أبي شبيب عن أبي ذر قال:
قال لي رسول الله صلى الله عليه وسلم اتق الله حيث كنت وأتبع السيئة الحسنة تمحها وخالق الناس بخلق حسن
_________
(١) ما بين المعقوفين زيادة لازمة لإقامة النص.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (6,'٤ - حدثنا أبو عبيد قال حدثنا عمار بن محمد الثوري ⦗٩٢⦘ عن ليث بن أبي سليم عن حبيب بن أبي ثابت عن ميمون بن أبي شبيب عن معاذ بن جبل عن النبي صلى الله عليه وسلم مثله ولم يذكره عن أبي ذر',NULL,'91',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (7,'٥ - حدثنا أبو عبيد قال حدثنا إسماعيل بن جعفر عن ⦗٩٣⦘ شريك بن عبد الله بن أبي نمر عن عطاء بن يسار قال:
بعث رسول الله صلى الله عليه وسلم معاذاً إلى اليمن فقال معاذ يا رسول الله أوصني فقال اتق الله ما استطعت واذكر الله عند كل شجرة وحجر وإذا عملت سيئة فأحدث عندها قربة السر بالسر والعلانية بالعلانية',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (8,'٦ - حدثنا أبو عبيد قال حدثنا علي بن عاصم عن ⦗٩٤⦘[أبي] علي الرحبي عن عكرمة عن ابن عباس قال:
قال رسول الله صلى الله عليه وسلم لا تقفن عند رجل يقتل مظلوما فإن اللعنة تنزل على من حضره وحين لم يدفعوا عنه ولا تقفن عند رجل يضرب مظلوما فإن اللعنة تنزل على من حضره
قال: وقال رسول الله صلى الله عليه وسلم لا ينبغي لامرئ شهد مقاما فيه مقال حق إلا تكلم به فإنه لن يقدم أجله ولن يحرمه رزقا هو له',NULL,'93',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (9,'٧ - حدثنا أبو عبيد قال حدثنا أبو النضر عن سليمان ابن المغيرة عن حميد بن هلال قال حدثنا أبو قتادة وأبو الدهماء قالا:
أتينا على رجل من أهل البادية فقال البدوي: أخذ بيدي رسول الله صلى الله عليه وسلم فجعل يعلمني مما علمه الله فكان مما حفظت عنه ⦗٩٦⦘ أنه قال إنك لن تدع شيئا اتقاء الله إلا أعطاك الله خير منه',NULL,'95',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (10,'٨ - حدثنا أبو عبيد قال: حدثنا عبد الوهاب بن عبد المجيد الثقفي عن أيوب عن حميد بن هلال قال:
أوصى رسول الله صلى الله عليه وسلم رجلا فقال إنك لن تدع شيئا اتقاء الله إلا أعطاك خيرا منه',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (11,'٩ - حدثنا أبو عبيد قال حدثنا الفضل بن دكين عن عيسى بن عبد الرحمن السلمي قال حدثنا طلحة بن ⦗٩٧⦘ مصرف عن عبد الرحمن بن عوسجة عن البراء بن عازب قال
جاء رجل إلى رسول الله صلى الله عليه وسلم فقال علمني عملا يدخلني الجنة قال فقال لئن كنت أقصرت الخطبة لقد أعرضت المسألة أعتق النسمة وفك الرقبة قال أوليسا واحدا قال لا عتق النسمة أن تفرد عتقها وفك الرقبة أن تعين في ثمنها.
والمنحة الوكوف والفيء على ذي الرحم الظالم فإن لم تطق ذلك فأطعم الجائع واسق الظمآن ومر بالمعروف وانه عن المنكر فإن لم تطق ذلك فكف لسانك إلا من خير',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (12,'١٠ - حدثنا أبو عبيد قال حدثنا هشيم ومروان ابن معاوية عن عوف بن أبي جميلة عن زرارة ابن أوفى عن عبد الله بن سلام ⦗٩٩⦘ قال:
لما قدم رسول الله صلى الله عليه وسلم المدينة انجفل الناس وقيل: قدم رسول الله قال فذهبت أنظر إليه فلما تبينت وجهه عرفت أنه ليس بوجه كذاب فسمعته يقول أيها الناس أطعموا الطعام وأفشوا السلام وصلوا الأرحام وصلوا والناس نيام تدخلوا الجنة بسلام',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (13,'١١ - حدثنا أبو عبيد قال حدثنا سعيد بن أبي مريم عن يحيى بن أيوب عن عبيد الله بن زحر عن علي ⦗١٠٠⦘ بن يزيد عن القاسم أبي عبد الرحمن عن أبي أمامة عن عقبة بن عامر قال
لقيت رسول الله صلى الله عليه وسلم يوما فقلت ما النجاة فقال يا عقبة املك عليك لسانك وليسعك بيتك وابك على خطيئتك',NULL,'99',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (14,'١٢ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح عن معاوية بن صالح عن ضمرة بن حبيب قال
⦗١٠١⦘ قال رسول الله صلى الله عليه وسلم من فتح له باب خير فلينتهزه فإنه لا يدري متى يغلق عنه',NULL,'100',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (15,'١٣ - حدثنا أبو عبيد قال حدثنا الأشجعي عن يحيى ابن عبيد الله المديني عن أبيه عن أبي هريرة عن النبي صلى الله عليه وسلم أنه قال
⦗١٠٢⦘ ما رأيت مثل النار نام هاربها ولا مثل الجنة نام طالبها',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (16,'١٤ - حدثنا أبو عبيد قال حدثنا مروان بن معاوية الفزاري والنضر بن إسماعيل البجلي عن موسى الصغير عن عمرو بن مرة عن أبي جعفر عبد الله بن المسور الهاشمي قال:
قال رسول الله صلى الله عليه وسلم يا عجبا كل العجب للشاك في قدرة الله وهو يرى خلقه ويا عجبا كل العجب للمكذب بالنشأة الآخرة وهو يرى النشأة الأولى ويا عجبا كل العجب ⦗١٠٣⦘ للمكذب بنشور الموتى وهو يموت كل ليلة ويحيا ويا عجبا كل العجب للمصدق بدار الخلود وهو يسعى لدار الغرور ويا عجبا كل العجب للمختال الفخور وإنما خلق من نطفة ثم يعود جيفة وهو بين ذلك لا يدري ما يفعل به',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (17,'١٥ - حدثنا أبو عبيد قال حدثنا إسماعيل بن إبراهيم عن سعيد بن إياس الجريري عن أبي تميمة الهجيمي عن رجل من قومه قال
⦗١٠٤⦘ لقيت رسول الله صلى الله عليه وسلم في بعض طرق المدينة وعليه إزار من قطن منثر الحاشية فقلت عليك السلام يا رسول الله فقال [هذه] (١) تحية الميت إن عليك السلام تحية [الموتى] (٢) سلام عليكم سلام عليكم أي هكذا قل.
قال قلت أين أتزر قال فأقنع ظهره وأخذ بعظم ساقه فقال ها هنا ائتزر فإن أبيت فها هنا أسفل من ذلك فإن أبيت فها هنا فوق الكعبين فإن أبيت فإن الله لا يحب كل مختال فخور.
قال وسألته عن المعروف فقال لا تحقرن منه شيئا ولو أن تعطي صلة الحبل ولو أن تعطي شسع النعل ولو أن تفرغ من دلوك في إناء المستسقي ولو أن تنحي الشيء عن طريق الناس يؤذيهم ولو أن تلقى أخاك ووجهك له منبسط ولو أن تؤنس الوحشان بنفسك وإن رجل سبك بأمر يعلمه فيك وأنت ⦗١٠٥⦘ تعرف منه نحوه فلا تسبه يكن لك أجر ذلك وعليه وزره وما سر أذنك أن تسمعه فاعمل به وما ساء أذنك أن تسمعه فاجتنبه
_________
(١) ما بين المعقوفين زيادة لازمة لتمام المعنى، ففي جامع الأصول: لا تقل عليك السلام، فإن ذلك تحية الموتى. قل: السلام عليك. ومثله في سنن أبي داود.
(٢) ما بين المعقوفين زيادة لازمة لتمام المعنى.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (18,'١٦ - حدثنا أبو عبيد قال حدثنا إسماعيل بن جعفر عن عمرو بن أبي عمرو عن المطلب بن حنطب عن عبادة ابن الصامت قال:
قال رسول الله صلى الله عليه وسلم: اضمنوا لي ستا من أنفسكم أضمن لكم الجنة: اصدقوا إذا حدثتم وأوفوا إذا وعدتم وأدوا إذا ائتمنتم واحفظوا فروجكم وغضوا أبصاركم وكفوا أيديكم',NULL,'105',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (19,'١٧ - حدثنا أبو عبيد قال حدثنا عبد الله بن جعفر عن عمرو بن أبي عمرو عن المطلب بن عبد الله بن حنطب قال:
وعظ رسول الله صلى الله عليه وسلم رجلا فقال لا تشرك بالله شيئا وإن قتلت وإن قطعت ولا تشرب الخمر فإنها مفتاح الشرور وأطع والديك وإن أمراك أن تخرج من مالك فاخرج منه ولا تنازع الأمر أهله وإن رأيت أن لك',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (20,'١٨ - حدثنا أبو عبيد قال حدثنا عمر بن عبد الرحمن الأبار عن منصور عن أبي وائل عن أبي موسى.
وحدثنا أبو عبيد قال حدثنا أبو معاوية عن ⦗١٠٨⦘ الأعمش عن أبي وائل عن أبي موسى أو أحدهما بإسناده
أن رسول الله صلى الله عليه وسلم قال عودوا المريض وأطعموا الجائع وفكوا العاني',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (21,'<span data-type="title" id=toc-4>مواعظ إبراهيم الخليل خليل الرحمن صلى الله عليه وسلم التي وعظه الله بها وذكر فضائله وطاعته</span>
١٩ - حدثنا أبو عبيد قال حدثنا عباد بن عباد قال حدثنا واصل مولى أبي عيينة عن موسى بن عبيدة قال
نزلت على صيفي بن هلال فذكر عبادته واجتهاده وكان صاحب كتب وكانت له سراويلان إحداهما عليه والأخرى معلقة فإذا رابه من هذه شيء أنزل تلك ثم غسل هذه قال فسألته عن ذلك فقال
⦗١١٠⦘ إني وجدت في بعض ما أنزل الله من الكتب أن الله أوحى إلى إبراهيم أن يا إبراهيم أنت خليلي وأطوع من في الأرض لي فإذا قمت تصلي لي وتعبدني فإن استطعت ألا ترى الأرض عورتك فافعل',NULL,'109',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (22,'٢٠ - حدثنا أبو عبيد قال حدثنا مروان بن معاوية عن عابد بن ناجية عن سليمان بن أبي سليمان عن وهب بن منبه قال:
كان في صحف إبراهيم أو قال فيما أنزل الله على إبراهيم أيها الملك المبتلى إني لم أبعثك لتجمع الدنيا بعضها إلى بعض ولا لتبني البنيان ولكني بعثتك لترد عني دعوة المظلوم فإني لا أردها وإن كانت من كافر',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (23,'٢١ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح عن الليث بن سعد عن يونس بن يزيد عن ابن شهاب عن عمرو (١) بن أبي سفيان قال قال كعب الأحبار لأبي هريرة ألا أخبرك عن إسحاق بن إبراهيم النبي صلى الله عليه فقال ⦗١١٢⦘ أبو هريرة بلى فقال كعب:
لما أري إبراهيم ذبح ابنه إسحاق قال الشيطان: والله لئن لم أفتن عند هذه آل إبراهيم لا أفتنهم أبدا فتمثل الشيطان لهم رجلا يعرفونه فأقبل حين خرج إبراهيم غاديا بإسحاق ليذبحه حتى دخل على سارة امرأة إبراهيم فقال: أين أصبح إبراهيم غاديا بإسحاق فقالت: غدا به لبعض حاجته فقال الشيطان: لا والله ما لذلك غدا به قالت سارة: فأين غدا به قال: غدا به ليذبحه قالت سارة: لم يكن ليذبح ابنه قال الشيطان: بلى والله قالت: ولم يذبحه قال: زعم أن ربه أمره بذلك قالت: فقد أحسن بأن يطيع الله ربه إن كان أمره بذلك. قال: فخرج الشيطان من عند سارة حتى أدرك إسحاق وهو يمشي على إثر أبيه فقال: أين أصبح أبوك غاديا بك فقال: غدا بي لبعض حاجته فقال: لا والله ولكنه غدا بك ليذبحك فقال إسحاق: ما كان أبي ليذبحني قال: بلى قال: ولم قال: زعم أن ربه أمره بذلك قال إسحاق: فوالله لئن أمره بذلك ليطيعنه قال: فتركه وأسرع إلى إبراهيم فقال: أين أصبحت غاديا بابنك قال: غدوت به لبعض حاجتي قال: والله ما غدوت به إلا لتذبحه قال: ولم أذبحه قال: زعمت أن ربك أمرك بذلك فقال والله لئن كان أمرني به ربي لأفعلن
_________
(١) في المخطوط: عمر وهو خطأ، انظر تاريخ الطبري ١/ ٢٦٥ ومصادر الترجمة.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (24,'٢٢ - حدثنا أبو عبيد قال حدثنا إسماعيل بن إبراهيم عن ⦗١١٣⦘ أيوب وهشام عن ابن سيرين عن أبي هريرة قال:
إن إبراهيم لم يكذب إلا ثلاث كذبات اثنتين في ذات الله وواحدة في ذات نفسه فأما اللتان في ذات الله فقوله {إني سقيم} وقوله {بل فعله كبيرهم هذا فاسألوهم إن كانوا ينطقون} وأما التي في ذات نفسه فإنه أقبل يسير ومعه سارة في ملك جبار فبلغ الجبار خبر سارة فأرسل إلى إبراهيم فقال إنها أختي فلما رجع إبراهيم إلى سارة قال لها إن هذا سألني فقلت إنها أختي فأخبريه أنك أختي.
فأرسل إلى سارة وقام إبراهيم يصلي فلما أتته أرادها فأمسك عنها فقال لها دعيني ولك علي ألا أضرك فخلي عنه فدعا الذي جاء بها فقال إنك جئتني بشيطان ولم تأتني بإنسان وأمر بها بهاجر فانقلبت إلى إبراهيم فقال لها مهيم ⦗١١٤⦘ فقالت رد الله كيد الكافر الفاجر وأخدم وليدة',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (25,'٢٣ - حدثنا أبو عبيد قال حدثنا يزيد بن هارون عن سليمان التيمي عن أبي عثمان عن سلمان قال
⦗١١٥⦘ أرسل على إبراهيم أسدان جائعان فجعلا يلحسانه وسجدا له',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (26,'٢٤ - حدثنا أبو عبيد قال حدثنا يزيد بن هارون عن زكريا بن أبي زائدة عن الشعبي عن عبد الله ابن عمرو قال:
لما ألقي إبراهيم في النار قال حسبي الله ونعم الوكيل',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (27,'٢٥ - حدثنا أبو عبيد قال حدثنا عبد الرحمن ⦗١١٦⦘ وإسماعيل بن عمر عن سفيان عن المختار بن فلفل قال سمعت أنس بن مالك يقول
قال رجل للنبي صلى الله عليه وسلم يا خير البرية فقال ذاك إبراهيم',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (28,'٢٦ - حدثنا أبو عبيد قال حدثنا إسماعيل بن إبراهيم عن
⦗١١٧⦘ أبي رجاء عن الحسن في قول الله {وإذ ابتلى إبراهيم ربه بكلمات فأتمهن} قال ابتلاه بالكواكب فرضي عنه وابتلاه بالقمر فرضي عنه وابتلاه بالشمس فرضي عنه وابتلاه بالنار فرضي عنه وابتلاه بابنه فرضي عنه وابتلاه بالهجرة وابتلاه بالختان',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (29,'٢٧ - حدثنا أبو عبيد قال حدثنا أبو الأسود عن ابن ⦗١١٨⦘ لهيعة عن عبد الله بن هبيرة عن حنش الصنعاني عن ابن عباس في قوله عز وجل {وإذ ابتلى إبراهيم ربه بكلمات} قال
هي عشر ست في الإنسان وأربع في المشاعر فالتي في الإنسان حلق العانة والختان ونتف الإبط قال وكان ابن هبيرة يجعل هؤلاء الثلاث واحدة وتقليم الأظفار وقص الشارب والسواك والغسل يوم الجمعة والغسل من الجنابة والأربع التي في المشاعر الطواف بالبيت والسعي بين الصفا والمروة ورمي الجمار والإفاضة',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (30,'٢٨ - حدثنا أبو عبيد قال حدثنا أبو معاوية ويزيد عن حجاج عن عطاء قال
قال رسول الله صلى الله عليه وسلم عشر فطر عليهن أبوكم إبراهيم خمس في الرأس وخمس في الجسد فأما التي في الرأس فالمضمضة والاستنشاق والسواك وقص الشارب وإبقاء اللحية وأما التي في الجسد فنتف الإبط وقص الأظافر والختان والاستحداد والاستنجاء بالحجارة',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (31,'٢٩ - حدثنا أبو عبيد قال حدثنا يزيد عن حماد ⦗١٢٠⦘ ابن سلمة عن علي بن زيد عن سلمة بن محمد بن عمار بن ياسر عن عمار بن ياسر قال قال رسول الله صلى الله عليه وسلم عشر من الفطرة المضمضة والاستنشاق والسواك وقص الشارب وتقليم الأظفار ونتف ⦗١٢١⦘ الإبط وحلق العانة وغسل البراجم والانتضاح بالماء والختان',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (32,'٣٠ - حدثنا أبو عبيد قال حدثنا حجاج عن ابن جريج عن مجاهد في قول الله {إني جاعلك للناس إماما} قال:
قال الله لإبراهيم إني مبتليك بأمر فما هو قال تجعلني للناس إماما قال نعم قال وتجعل البيت مثابة للناس وأمنا قال نعم قال وتجعلنا مسلمين لك ومن ذريتنا أمة ⦗١٢٢⦘ مسلمة لك. قال نعم قال وترينا مناسكنا وتتوب علينا إنك أنت التواب الرحيم',NULL,'121',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (33,'٣١ - حدثنا أبو عبيد قال حدثنا حسان بن عبد الله عن ابن لهيعة عن موسى بن أيوب عن ابن عباس
أنه سئل عن قول الله {إن إبراهيم كان أمة} قال كان إماما',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (34,'٣٢ - حدثنا أبو عبيد قال حدثنا عبد الرحمن عن سفيان عن فراس عن الشعبي عن مسروق عن ⦗١٢٣⦘ عبد الله أنه قال
أتدرون ما الأمة الذي يعلم الناس قال أبو عبيد أحسبه قال الخير والقانت المطيع لله',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (35,'٣٣ - حدثنا أبو عبيد قال حدثنا حجاج عن ابن جريج عن مجاهد في قوله {كان أمة} قال
أمة على حدة قال وقوله قانتا مطيعا لله',NULL,'123',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (36,'٣٤ - حدثنا أبو عبيد قال حدثنا حجاج عن ابن جريج قال أخبرني القاسم بن أبي بزة عن عكرمة في قول الله ⦗١٢٤⦘ تعالى {وآتيناه أجره في الدنيا} قال
هو لسان الصدق الذي جعل الله له قال فالأمم كلها تتولى إبراهيم اليهود والنصارى والناس أجمعون قال ويشهدون له بالعدل فذلك لسان الصدق وهو الأجر الذي آتاه الله في الدنيا',NULL,'123',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (37,'٣٥ - حدثنا أبو عبيد قال حدثنا حجاج عن ابن جريج عن مجاهد في قول الله وإبراهيم الذي وفى قال
وفى بما فرض الله عليه',NULL,'124',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (38,'٣٦ - حدثنا أبو عبيد قال حدثنا محمد بن كثير عن حماد بن سلمة عن عطاء عن عكرمة قال
وفى {ألا تزر وازرة وزر أخرى وأن ليس للإنسان إلا ⦗١٢٥⦘ ما سعى وأن سعيه سوف يرى}',NULL,'124',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (39,'٣٧ - حدثنا أبو عبيد قال حدثنا حجاج عن ابن جريج قال
هذا في صحف إبراهيم وموسى أن ليس للإنسان إلا ما سعى وأن سعيه سوف يرى وكذا وكذا كل هذا في صحف إبراهيم وموسى',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (40,'٣٨ - حدثنا أبو عبيد قال حدثنا أبو الأسود عن ابن لهيعة عن زبان بن فائد الحمراوي عن سهل بن معاذ بن أنس عن أبيه (١) رفعه قال
⦗١٢٦⦘ ألا أخبركم لم سمي الله خليله الذي وفى قال لأنه كان يقول إذا أصبح وأمسى {فسبحان الله حين تمسون وحين تصبحون} حتى ختم الآية
_________
(١) في المخطوطة عن أخيه وهو تحريف. وأبوه هو معاذ بن أنس الجهني صحابي نزل مصر. وله ثلاثون حديثاً. وعنه ابن سهل. انظر: خلاصة تهذيب الكمال ٣٢٤/ ٢٩.',NULL,'125',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (41,'<span data-type="title" id=toc-5>مواعظ موسى صلى الله عليه عند مساءلته الله جل ثناؤه وإجابته إياه وما كتب الله له في الألواح</span>
٣٩ - حدثنا أبو عبيد قال حدثنا جرير بن عبد الحميد عن قابوس بن أبي ظبيان عن أبيه عن ابن عباس قال
⦗١٢٨⦘ قال موسى صلى الله عليه رب أي عبادك أحب إليك قال أكثرهم لي ذكرا قال رب فأي عبادك أغنى قال الراضي بما أعطيته قال رب فأي عبادك أعدل قال الذي يحكم على نفسه كما يحكم على الناس',NULL,'127',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (42,'٤٠ - حدثنا أبو عبيد قال حدثنا أبو معاوية عن العوام ابن جويرية عن الحسن قال
سأل موسى صلى الله عليه ربه فقال أي رب ما لعائد المريض من الأجر قال أدعه من ذنوبه كيوم ولدته أمه قال يا رب فما لمشيع الموتى من الأجر قال أبعث الملائكة معهم بآبائهم يشيعونه من قبره إلى محشره قال يا رب فما لمعزي الثكلى من الأجر قال أظله في ظلي يوم لا ظل إلا ظلي',NULL,'128',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (43,'٤١ - حدثنا أبو عبيد قال حدثنا عبد الرحمن عن سفيان عن عطاء بن أبي مروان عن أبيه عن كعب ⦗١٢٩⦘ قال
قال موسى صلى الله عليه يا رب أقريب أنت فأناجيك أم بعيد فأناديك قال يا موسى أنا جليس من ذكرني قال فإنها تكون حال أجلك أن أذكرك فيها من جنابة أو غائط فقال اذكرني على كل حال',NULL,'128',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (44,'٤٢ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح عن معاوية بن صالح عن عبد الوهاب بن بخت
إن موسى صلى الله عليه وسلم سأل ربه من أهلك الذين هم أهلك قال الذين يذكرون بذكري وأذكر بذكرهم والذين يأوون إلى ذكري كما تأوي النسور إلى وكورها والذين يكلفون بحبي كما يكلف الصبي بحب الناس والذي يغضبون لمحارمي كما يغضب النمر إذا حرب',NULL,'129',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (45,'٤٣ - حدثنا أبو عبيد قال وحدثني أبو مسهر الدمشقي عن سعيد بن عبد العزيز التنوخي (١) عن ثابت بن معبد المحاربي
قال وكان من كبراء أهل الشام وولي هو وأخوه الساحل أربعين سنة قال
قال موسى رب أي الناس أتقى قال الذي يذكرني ولا ينسى قال رب أي الناس أغنى قال الذي يقنع بما يؤتى قال رب أي الناس أعز قال الذي يغفر بعدما يقدر قال رب أي الناس أعلم قال الذي يأخذ من علم الناس إلى علمه وإن كان عالما
_________
(١) في المخطوطة: الشوخبي وهو تصحيف. وهو سعيد بن عبد العزيز بن أبي يحيى التنوخي، أبومحمد الدمشقي الفقيه. روى عن مكحول، ونافع وغيرهما. وعنه شعبة، والثوري، وأبومسهر وخلق. وثقه ابن معين وأبوحاتم والنسائي. وتوفي سنة ١٦٧هـ. انظر ترجمته في تهذيب التهذيب ٤/ ٥٩ وحلية الأولياء ٨/ ٢٧٤ وخلاصة تهذيب الكمال ١٨٧/ ٧.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (46,'٤٤ - حدثنا أبو عبيد قال حدثنا أبو معاوية عن ⦗١٣١⦘ الأعمش عن سالم بن أبي الجعد عن يزيد بن بشر قال حدثني رجل من أهل الكتاب يقال له يسى (١) وكان قد أسلم وحسن إسلامه قال أوحى الله إلى موسى إن جاءك الموت وأنت على غير وضوء فلا تلومن إلا نفسك قال وأوحى الله إلى موسى يا موسى إن الله يدفع بالصدقة سبعين بابا من السوء منها الغرق والحرق وذات الجنب
_________
(١) في الأصل المخطوط: نسى وهو تحريف.',NULL,'130',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (47,'٤٥ - حدثنا أبو عبيد قال حدثنا كثير بن هشام عن جعفر بن برقان عن ميمون بن مهران ⦗١٣٢⦘ قال بلغني أن مما كتب الله لموسى في الألواح أن لا تمن مال صاحبك ولا امرأة صاحبك',NULL,'131',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (48,'٤٦ - حدثنا أبو عبيد قال حدثنا عباد بن العوام عن العلاء بن المسيب عن خيثمة بن عبد الرحمن قال إن في التوراة مكتوبا يا ابن آدم تفرغ لعبادتي أملأ قلبك غنى وأسد فقرك وإن لا تفعل أملأ قلبك شغلا ولا أسد فقرك',NULL,'132',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (49,'٤٧ - حدثنا أبو عبيد قال حدثنا يزيد عن هشام بن حسان عن خالد الربعي قال
قرأت في التوراة اتق الله وإذا شبعت فاذكر الجائع',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (50,'٤٨ - حدثنا قال حدثنا ابن مهدي عن محمد بن أبي الوضاح عن خصيف عن مجاهد أو سعيد بن جبير ⦗١٣٤⦘ هكذا قال عبد الرحمن قال
كانت الألواح من زمرد فلما ألقاها موسى ذهب التفصيل وبقي الهدى والرحمة ثم قرأ عبد الرحمن {وكتبنا له في الألواح من كل شيء موعظة وتفصيلاً لكل شيء} وقرأ {ولما سكت عن موسى الغضب أخذ الألواح وفي نسختها هدى ورحمة} قال ولم يذكر التفصيل ها هنا',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (51,'٤٩ - حدثنا أبو عبيد قال حدثنا يزيد ومحمد بن جعفر عن شعبة عن عمرو بن مرة عن عبد الله بن ⦗١٣٥⦘ سلمة عن صفوان بن عسال
أن يهوديين قال أحدهما لصاحبه انطلق بنا إلى هذا النبي فقال لا تقل نبي فإنه إن سمعك كانت له أربعة أعين فانطلقا إلى رسول الله صلى الله عليه وسلم فسألاه عن قول الله {ولقد آتينا موسى تسع آيات بينات} فقال رسول الله صلى الله عليه وسلم لا تشركوا بالله شيئا ولا تقتلوا النفس التي حرم الله إلا بالحق ولا تزنوا ⦗١٣٦⦘ ولا تسرقوا ولا تسحروا ولا تمشوا ببريء إلى ذي سلطان فيقتله ولا تأكلوا الربا ولا تقذفوا المحصنة أو قال لا تفروا من الزحف شك شعبة في هاتين وعليكم خاصة يهود ألا تعدوا في السبت
قال: فقبلا يديه ورجليه وقالا نشهد أنك نبي الله قال فما يمنعكما أن تسلما فقالا إن داود دعا الله أن لا يزال في ذريته نبي وإنا نخاف إن أسلمنا أن تقتلنا يهود',NULL,'134',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (52,'<span data-type="title" id=toc-6>مواعظ أيوب النبي صلى الله عليه وسلم التي وعظ بها وما كان من كلامه في ابتلائه</span>
٥٠ - حدثنا أبو عبيد قال حدثني أبو عبيد الله الأزدي عن سيار العابد عن عبد الواحد بن أبي عون المدني قال
وقف رجال على أيوب وهو في مزبلة وتحته فروة فأمسكوا على أنفهم من ريحه وقالوا يا أيوب والله لقد كنت تعمل أعمالا لو كانت لله ما أنزل بك هذا البلاء!
فقال أيوب: قاتل الله الغنى ما أعزه لأهله! وقاتل الله الفقر ما أذله لأهله! أي رب بأي ذنوبي أخذتني فوعزتك إنك لتعلم أنه ما عرى لي جار وعندي فضل ثوب وإن كنت لأسمع العبد من عبيدك يحنث باسم من أسمائك فأكفر عنه إجلالا لك',NULL,'137',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (53,'٥١ - حدثنا أبو عبيد قال حدثنا محمد بن كثير وغيره بإسناد لا أحفظه دخل كلام أحدهما في كلام الآخر عن وهب بن منبه أن ابن عباس قال له كيف قال الفتى لأيوب فقال وهب قال له
لقد كان في عظمة الله وجلاله وذكر الموت ما يقطع لسانك وينسيك حججك ألم تعلم يا أيوب أن لله عباداً أسكتتهم الخشية من غير عي ولا بكم وإنهم لهم الفصحاء الطلقاء الألباء العالمون بالله وبأيامه ولكنهم إذا ذكروا عظمة الله انقطعت ألسنتهم واقشعرت جلودهم وطاشت عقولهم إعظاما لله وإجلالا فإذا استفاقوا من ذلك استبقوا إلى الله بالأعمال الزاكية يعدون أنفسهم مع الظالمين الخاطئين وإنهم لأنزاه بررة ومع المقصرين المفرطين وإنهم لأكياس أقوياء ولكنهم لا يستكثرون لله الكثير ولا يرضون له بالقليل ولا يدلون عليه بالأعمال',NULL,'138',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (54,'٥٢ - قال أبو عبيد وفي غير حديث محمد بن كثير أن هذا الفتى الذي كلم أيوب هو جبريل بعثه الله في صورة رجل شاب وكان أيوب قبل هذا قد تكلم في بلائه بكلام عوتب عليه بهذا القول',NULL,'138',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (55,'<span data-type="title" id=toc-7>مواعظ داود النبي صلى الله عليه وسلم</span>
٥٣ - حدثنا أبو عبيد قال حدثنا إسماعيل بن مجالد عن أبي إسحاق السبيعي (١) عن عبد الرحمن بن أبزى أن داود النبي صلى الله عليه وسلم أوصى ابنه سليمان فقال
يا بني كن لليتيم كالأب الرحيم واعلم أنك كما تزرع كذلك تحصد واعلم أن خطبة الأحمق في نادي القوم كالمتغني عند رأس الميت
⦗١٤٠⦘ واعلم أن المرأة الصالحة لبعلها كالملك المتوج بالتاج المخوص بالذهب واعلم أن المرأة السوء لبعلها كالشيخ الكبير على ظهره الحمل الثقيل
ما أقبح الفقر بعد الغنى وأقبح منه الضلالة بعد الهدى وإن وعدت أخاك شيئا فأنجز له ما وعدته وإن لا تفعل تورث بينك وبينه عداوة
_________
(١) في المخطوطة: الشسعي وهو تحريف.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (56,'٥٤ - حدثنا أبو عبيد قال حدثنا عمار بن محمد عن خاله سفيان بن سعيد عن أبي إسحاق عن عبد الرحمن بن أبزى عن داود مثل ذلك وزاد فيه قال
ونعوذ بالله من صاحب السوء الذي إذا ذكرت له يعيبك وإذا نسيت لم يذكرك',NULL,'140',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (57,'٥٥ - حدثنا أبو عبيد قال حدثنا أحمد بن عثمان عن ⦗١٤١⦘ عبد الله بن المبارك عن سفيان عن رجل عن وهب بن منبه قال
في حكمة آل داود حق على العاقل ألا يغفل عن أربع ساعات ساعة يتخلى فيها لربه وساعة يحاسب فيها نفسه وساعة يفضي فيها إلى إخوانه الذين يخبرونه بعيوبه ويصدقونه عن نفسه وساعة يخلي بين نفسه وبين لذاتها فيما يحل ويجمل فإن هذه الساعة عون له على هذه الساعات وإجمام (١) للقلوب
وحق على العاقل أن يعرف زمانه ويحفظ لسانه ويقبل على شأنه وحق على العاقل ألا يظعن في إحدى ثلاث زاد لمعاده ⦗١٤٢⦘ ومرمة لمعاشه ولذة في غير محرم
_________
(١) في المخطوط: وإخماد وهو تحريف، وما أثبتناه من المصادر. والإجمام الإراحة، يقال أجم نفسك يوما أو يومين: أرحها. انظر لسان العرب (جمم) ١٤/ ٣٧٢.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (58,'٥٦ - حدثنا أبو عبيد قال حدثنا ابن مهدي عن جابر بن يزيد بن رفاعة عن المغيرة بن عيينة قال:
أنزل الله على داود {اعملوا آل داود شكرا} فقال داود يا رب كيف أشكرك وأنت الذي تنعم علي ثم ترزقني على النعمة والشكر فالنعم منك والشكر منك فكيف أطيق شكرك فقال يا داود الآن عرفتني حق معرفتي',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (59,'٥٧ - حدثنا أبو عبيد قال حدثنا أبو الأسود عن ابن لهيعة عن زهرة بن معبد أنه سمع أبا عبد الرحمن الحبلي يقول في ⦗١٤٣⦘ قول الله {اعملوا آل داود شكرا} قال:
الصلاة شكر والصوم شكر وكل عمل يعمل لله شكر وأفضل الشكر الحمد',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (60,'٥٨ - حدثنا أبو عبيد قال حدثنا ابن أبي مريم عن عبد الله بن المنيب قال أخبرني أبي المنيب وجدي جميعاً عن عطاء بن يسار قال:
قال رسول الله صلى الله عليه وسلم وهو يخطب الناس على المنبر وقرأ هذه الآية {اعملوا آل داود شكرا} فقال: ثلاث من أوتيهن فقد أوتي ما أوتي آل داود فقيل له وما هن يا رسول الله فقال العدل في الغضب والرضا والقصد في الفقر والغنى وذكر الله في السر والعلانية',NULL,'143',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (61,'٥٩ - حدثنا أبو عبيد قال حدثنا ابن أبي مريم عن ابن لهيعة عن الحارث بن يزيد عن علي بن رباح قال ⦗١٤٤⦘ سمعت وهبا الذماري يحدث [عن] (١) فضالة بن عبيد أن داود سأل ربه أن يخبره بأحب الأعمال إليه فقال عشر يا داود لا تذكرن أحداً من خلقي إلا بخير ولا تغتابن أحدا من خلقي ولا تحسدن أحدا من خلقي فقال داود يا رب هؤلاء الثلاث لا أستطيع أن أعملهن فاحبس عني السبع.
ولكن يا رب أخبرني بأحبائك من خلقك أحبهم لك فقال ذو سلطان يرحم الناس ويحكم للناس كما يحكم لنفسه ورجل أعطاه الله مالا فهو ينفق منه ابتغاء وجه الله ورجل يفني شبابه وقوته في طاعة الله، ورجل كان قلبه معلقاً بالمساجد من حبه إياها ورجل لقي امرأة حسناء وأمكنته من نفسها فتركها من خشية الله ورجل حيثما كان علم أن الله معه ورجل فاضت عيناه من خشية الله
_________
(١) ما بين المعقوفين ساقطة من المخطوط، وهو في الزهد لابن المبارك.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (62,'٦٠ - حدثنا إبراهيم بن محمد بن يعقوب قال حدثنا أبو عبيد ⦗١٤٥⦘ القاسم بن سلام قال حدثنا عبد الرحمن بن مهدي عن سفيان بن عيينة عن عطاء بن السائب قال سمعت أبا عبد الله الجدلي يقول
أوحى الله إلى داود أن يا داود أحببني وأحبب من يحبني [وحببني] (١) إلى خلقي فقال يا رب ها أنذا (٢) أحبك وأحب من يحبك فكيف أحببك إلى خلقك قال أن تذكرني فلا تذكر إلا حسنا
_________
(١) ما بين المعقوفين زيادة من الزهد لابن حنبل، وبدونها لا يستقيم الكلام.
(٢) في المخطوطة: هذا وهو تحريف.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (63,'٦١ - حدثنا أبو عبيد قال حدثنا أبو مسهر عن خالد بن يزيد بن صالح بن صبيح عن ابن حلبس قال
⦗١٤٦⦘ قال داود رب علمني عملا إذا أنا عملته بلغت وقارك فأوحى الله إليه يا داود اعمل لي كأنك تراني وأحب المؤمن من أجلي ولا يزل لسانك رطبا من ذكري',NULL,'145',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (64,'٦٢ - حدثنا أبو عبيد قال حدثنا أبو الأسود عن ابن لهيعة عن خالد بن يزيد عن سعيد بن أبي هلال أن داود النبي صلى الله عليه وسلم كان يقول
اللهم نامت العيون وغارت النجوم وأنت حي قيوم لا تأخذك سنة ولا نوم فاغفر لي ذنبي العظيم',NULL,'146',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (65,'٦٣ - حدثنا أبو عبيد قال حدثنا علي بن عاصم عن عطاء بن السائب عن أبي عبد الله الجدلي أن نبي الله صلى الله عليه وسلم داود كان يقول
اللهم إني أعوذ بك من جار عينه تراني وقلبه يرعاني إن رأى حسنة أطفأها وإن رأى سيئة أشاعها',NULL,'146',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (66,'٦٤ - حدثنا أبو عبيد قال حدثنا حجاج عن حماد بن سلمة عن ثابت أن داود قال
أي رب أنت حكم عدل فكيف أصنع بأوريا قال يا داود إني أستوهبك منه فيهبك لي فقال داود أي رب الآن أعلم أنك قد غفرت لي',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (67,'٦٥ - حدثنا أبو عبيد قال حدثنا يزيد عن الجريري عن أبي السليل قال
كان داود النبي صلى الله عليه وسلم يدخل المسجد فينظر أغمص خلقه من بني إسرائيل فيجلس إليهم ثم يقول مسكين بين ظهري مساكين',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (68,'<span data-type="title" id=toc-8>مواعظ سليمان بن داود وفضائله</span>
٦٦ - حدثنا أبو عبيد قال حدثنا علي بن عاصم عن سعيد بن إياس الجريري عن عبد الله بن شقيق عن كعب
قال أوحى الله إلى داود أن ابن بيت المقدس قال فعارضه ببناء (١) له قال فأوحى الله إليه أن يا داود عارضت مسجدي بنائك ليس لك أن تبنيه قال يا رب ففي عقبي فقال في عقبك
فلما ولى سليمان أوحى الله إليه أن يبنيه فبناه فلما دخله خر ساجدا شكرا لله وقال يا رب من دخله من تائب فتب عليه أو مستغفر فاغفر له أو سائل فأعطه
قال وذبح أربعة آلاف بقرة وسبعة آلاف شاة وصنع طعاما فدعا إليه بني إسرائيل
_________
(١) في المخطوطة: بيتا تصحيف.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (69,'٦٧ - حدثنا أبو عبيد قال حدثنا علي بن ثابت عن ⦗١٤٩⦘ جعفر بن برقان عن صالح بن مسمار قال
لما مات نبي الله داود أوحى الله إلى سليمان أن سلني حاجتك فقال أسألك أن تجعل قلبي يخشاك كما كان قلب أبي وأن تجعل قلبي يحبك كما كان قلب أبي فقال الله أرسلت إلى عبدي أسأله حاجته فكانت حاجته أن أجعل قلبه يخشاني وأن أجعل قلبه يحبني لأهبن له ملكا لا ينبغي لأحد من بعده قال الله {فسخرنا له الريح تجري بأمره رخاء حيث أصاب والشياطين كل بناء وغواص وآخرين مقرنين في الأصفاد هذا عطاؤنا فامنن أو أمسك بغير حساب} قال فأعطاه الله ما أعطاه وفي الآخرة لا حساب عليه',NULL,'148',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (70,'٦٨ - حدثنا أبو عبيد قال حدثنا أبو الأسود عن ابن لهيعة عن عبد الرحمن بن زياد بن أنعم عن سلامان ⦗١٥٠⦘ بن عامر قال
قال رسول الله صلى الله عليه وسلم أرأيتم سليمان وما أعطي في ملكه فإنه لم يرفع رأسه إلى السماء تخشعا حتى قبضه الله',NULL,'149',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (71,'٦٩ - قال أبو عبيد وفي غير حديث ابن أنعم
أن نملة قالت لسليمان أنا على قدري أشكر لله منك وكان على فرس ذنوب فخر عنه ساجداً ثم قال لولا أن أبخلك لسألتك أن تنزع مني ما أعطيتني',NULL,'150',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (72,'٧٠ - حدثنا أبو عبيد قال حدثنا أبو مسهر عن سعيد بن عبد العزيز قال قال سليمان بن داود
نظرت في الحكمة فكثر همي ونظرت في العلم فكثر شيبي وذهبت أنظر في الأمر فإذا مع الشباب كبر وإذا مع الغنى فقر وإذا مع الصحة سقم وإذا مع الحياة موت وإذا تربتي وتربة السفيه الأحمق تصيران إلى أن تكونا سواء إلا أن أفضله يوم القيامة ⦗١٥١⦘ بعمل صالح فكيف يهنيني مع هذا طعام أو شراب',NULL,'150',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (73,'٧١ - حدثنا أبو عبيد قال حدثنا أبو مسهر عن سعيد بن عبد العزيز عن ربيعة بن يزيد قال
قالت أم سليمان بن داود لسليمان يا بني لا تكثر الجماع فإنه يوهن ظهرك حين تحتاج الناس إلى القوة
وإياك وكثرة النوم فإنه يفقرك حين تحتاج الناس إلى أعمالهم',NULL,'151',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (74,'٧٢ - حدثنا يعقوب بن سنيد بن داود المصيصي حدثنا أبي حدثنا يوسف بن محمد بن المنكدر عن أبيه عن ⦗١٥٢⦘ جابر بن عبد الله عن النبي صلى الله عليه وسلم قال
قالت أم سليمان لسليمان يا بني لا تكثر النوم بالليل فإن كثرة النوم بالليل تترك العبد فقيرا يوم القيامة',NULL,'151',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (75,'<span data-type="title" id=toc-9>مواعظ المسيح عيسى بن مريم صلى الله عليه وزهده ومناقبه</span>
٧٣ - حدثنا أبو عبيد قال حدثنا يزيد بن هارون عن أبي معشر عن سعيد بن أبي سعيد المقبري قال
جاء رجل إلى عيسى بن مريم فقال يا معلم الخير علمني شيئا تعلمه وأجهله ينفعني ولا يضرك قال وما هو قال كيف يكون العبد تقيا لله قال بيسير من الأمر تحب الله حقا من قلبك وتعمل لله بكدحك وقوتك ما استطعت وترحم بني جنسك رحمتك نفسك
فقال يا معلم الخير من بنو جنسي قال ولد آدم كلهم وما تحب ألا تراه فلا تأته إلى غيرك وأنت تقي لله حقا',NULL,'153',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (76,'٧٤ - حدثنا أبو عبيد قال حدثنا عبد الرحمن عن سفيان عن عبد العزيز بن رفيع عن أبي ثمامة العابدي قال
قال الحواريون لعيسى بن مريم يا نبي الله ما المخلص قال الذي يعمل العمل لله لا يحب أن يحمده الناس عليه قالوا فمن الناصح لله فقال الذي يبدأ بحق الله ويؤثر حق الله على حق الناس
وإذا عرض لك أمران أمر دنيا وأمر آخرة فابدأ بأمر الآخرة ثم تفرغ لأمر الدنيا
قال قال سفيان حدثنيه عنه منصور ثم لقيته [فسألته عنه]',NULL,'154',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (77,'٧٥ - حدثنا أبو عبيد قال حدثني أبو اليمان عن ⦗١٥٥⦘ صفوان بن عمرو عن شريح بن عبيد عن يزيد بن ميسرة قال
قال المسيح إن أحببتم أن تكونوا أصفياء الله ونور بني آدم فاعفوا عمن ظلمكم وعودوا من لا يعودكم (١) وأحسنوا إلى من لا يحسن إليكم وأقرضوا من لا يجزيكم
_________
(١) في المخطوطة: وعودوا على من لا يعود عليكم. والصواب ما أثبتاه وهو في حلية الأولياء والزهد لابن حنبل، لأن عاد المريض بمعنى زاره تتعدى بنفسها!',NULL,'154',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (78,'٧٦ - حدثنا أبو عبيد قال حدثنا هشيم قال أخبرنا ⦗١٥٦⦘ مغيرة عن الشعبي قال
قال عيسى بن مريم: ليس الإحسان أن تحسن إلى من أحسن إليك تلك مكافأة بالمعروف إنما الإحسان أن تحسن إلى من أساء إليك',NULL,'155',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (79,'٧٧ - حدثنا أبو عبيد قال حدثنا أبو مسهر عن خالد بن يزيد بن صالح بن صبيح عن ابن حلبس قال
قال عيسى لحوارييه ذروا الناس واكفوهم أنفسكم ولتسعكم بيوتكم وابكوا على خطاياكم',NULL,'156',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (80,'٧٨ - حدثنا أبو عبيد قال حدثنا يزيد بن هارون عن شريك بن عبد الله عن عاصم عن أبي صالح عن أبي ⦗١٥٧⦘ هريرة قال
كان عيسى يقول يا معشر الحواريين اتخذوا بيوتكم منازل واتخذوا المساجد مساكن وكلوا من بقل البرية واخرجوا من الدنيا بسلام',NULL,'156',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (81,'٧٩ - حدثنا أبو عبيد قال حدثنا عمرو بن طارق عن السري بن يحيى عن سليمان التيمي أن عيسى بن مريم قال
إني أقول الحق ما لكم في العالم من بيت إن أنتم في الدنيا إلا عابرو سبيل ألا فاتخذوا مساجد الله بيوتا واتخذوا بيوتكم كمنازل الأضياف',NULL,'157',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (82,'٨٠ - حدثنا أبو عبيد قال حدثنا ابن أبي مريم عن يحيى بن أيوب عن خالد بن يزيد عن سعيد بن أبي هلال أن عيسى ⦗١٥٨⦘ ابن مريم كان يقول
من كان يظن أن حرصه يزيد في رزقه فليزد في طوله أو في عرضه أو في عدد بنانه أو ليغير لونه
ألا فإن الله تبارك وتعالى خلق الخلق فمضى الخلق لما خلق ثم قسم الرزق فمضى الرزق لما قسم فليست الدنيا بمعطية أحدا شيئا ليس له ولا بمانعة أحدا شيئا هو له فعليكم بعبادة ربكم فإنكم خلقتم لها',NULL,'157',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (83,'٨١ - حدثنا أبو عبيد قال حدثنا ابن أبي مريم قال حدثني عبد الملك بن قدامة بن محمد بن حاطب بن معمر بن حبيب الجمحي قال حدثني أبي أن عيسى بن مريم كان يقول
يا أيها الناس لا تكثروا الكلام بغير ذكر الله فتقسو قلوبكم فإن القلب القاسي بعيد من الله ولا تنظروا في أعمال الناس كأنكم أرباب ولكن انظروا في أعمالكم كأنكم عبيد واعلموا أنما الناس مبتلى ومعافى فارحموا أهل البلاء واحمدوا الله على العافية',NULL,'158',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (84,'٨٢ - حدثنا أبو عبيد قال حدثنا خالد بن خداش عن صالح المري عن أبي عمران الجوني عن أبي الجلد قال
قال عيسى بن مريم بحق أقول لكم ما الدنيا تريدون ولا الآخرة قالوا فبين لنا يا نبي الله فقد كنا نرى أنا نريد إحداهما فقال لو أطعتم رب الدنيا الذي بيده مفاتيح خزائنها لأعطاكموها ولو أطعتم رب الآخرة لأعطاكموها ولكن لا هذه تريدون ولا تلك',NULL,'159',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (85,'٨٣ - حدثنا أبو عبيد قال حدثنا أحمد بن عثمان عن ابن المبارك عن سفيان بن عيينة عن عمران الكوفي قال
قال عيسى بن مريم للحواريين لا تأخذوا ممن تعلمون من الأجر إلا مثلما أعطيتموني ويا ملح الأرض لا تفسدوا فإن كل شيء يفسد فإنما يداوى بالملح وإن الملح إذا فسد فليس له دواء واعلموا أن فيكم خصلتين من الجهل الضحك من غير عجب والصحة من غير سهر',NULL,'160',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (86,'٨٤ - وعن مالك بن مغول قال
بلغنا أن عيسى بن مريم قال يا معشر الحواريين تحببوا إلى الله ببغض أهل المعاصي وتقربوا إليه بمباعدتكم منهم والتمسوا رضاه ⦗١٦١⦘ بما يسخطهم قال لا أدري بأيتهن أبدأ
قالوا يا روح الله الله فمن نجالس قال جالسوا من يذكركم الله رؤيته ومن يزيد في عملكم منطقه ومن يرغبكم في الآخرة عمله',NULL,'160',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (87,'٨٥ - حدثنا أبو عبيد قال حدثنا عفان عن معاوية بن عبد الكريم قال حدثنا بكر بن عبد الله المزني
أن عيسى بن مريم كان يقول إذا أصبح إلهي أصبحت لا أملك ما أرجو ولا أستطيع دفع ما أخاف أصبحت والأمر بيد ⦗١٦٢⦘ غيري بيدك يا إلهي فلا فقير فهو أفقر مني',NULL,'161',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (88,'٨٦ - حدثنا أبو عبيد قال حدثنا هشيم عن حصين عن هلال بن يسار قال
كان عيسى بن مريم إذا ذكر الساعة ملأ فاه خوارا',NULL,'162',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (89,'٨٧ - حدثنا أبو عبيد قال حدثنا أبو مسهر عن سعيد بن عبد العزيز قال
نظر عيسى بن مريم إلى إبليس فقال هذا أركون الدنيا منها خرج وإياها سأل لا أشركه في شيء منها ولا حجر أضعه تحت رأسي ولا أكشر فيها ضاحكا حتى أخرج منها
قال أبو عبيد الأركون العظيم من النصارى',NULL,'162',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (90,'٨٨ - حدثنا أبو عبيد قال حدثنا ابن أبي مريم عن يحيى بن أيوب عن يحيى بن سعيد ⦗١٦٣⦘ قال ما ابتنى عيسى بن مريم بيتا ولا وضع حجرا على حجر',NULL,'162',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (91,'٨٩ - حدثنا أبو عبيد قال حدثنا هشيم قال أخبرنا مغيرة عن الشعبي قال
كان عيسى بن مريم يلبس الشعر ويأكل الشجر ويبيت حيث أمسى لم يكن له ولد يموت ولا بيت يخرب',NULL,'163',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (92,'٩٠ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح عن الليث عن يونس عن ابن شهاب قال
ما لبس عيسى بن مريم إلا الصوف حتى توفاه الله',NULL,'163',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (93,'٩١ - حدثنا أبو عبيد قال حدثنا المبارك أبو الحسن عن سفيان الثوري قال
⦗١٦٤⦘ قال عيسى بن مريم أربع هن أعجب لا يدركن إلا بحب الصمت وهو أول العبادة والتواضع لله وذكر الله على كل حال وقلة الشيء',NULL,'163',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (94,'٩٢ - حدثنا أبو عبيد قال حدثنا عمر بن عبد الرحمن عن منصور قال
قال يحيى بن زكريا لعيسى بن مريم أوصني فقال لا تغضب فقال لا أستطيع قال فلا تقتن قال هذا عسى',NULL,'164',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (95,'٩٣ - حدثنا أبو عبيد قال حدثنا حجاج عن ابن جريج في قول الله {مصدقا بكلمة من الله} قال
كان عيسى ويحيى ابني خالة فكانت أم يحيى تقول لمريم إني أجد الذي في بطني يسجد للذي في بطنك
⦗١٦٥⦘ قال فذلك تصديقه وهو أول من صدق بعيسى
قال وكان يحيى أكبر من عيسى',NULL,'164',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (96,'٩٤ - حدثنا أبو عبيد قال حدثنا ابن أبي مريم عن يحيى بن أيوب قال حدثني عمارة بن غزية عن يحيى بن سعيد
أن عيسى كان يسيح فقيل له إن السياحة قد شقت عليك فلو اتخذت حمارا تركبه فقال أعوذ بالله أن أجعل لحمار من الحمير شعبة من قلبي',NULL,'165',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (97,'<span data-type="title" id=toc-10>مواعظ يحيى بن زكريا صلى الله عليه وسلم وفضائله وذكر مقتله</span>
٩٥ - حدثنا أبو عبيد قال حدثنا عفان قال حدثنا موسى بن خلف قال حدثنا يحيى بن أبي كثير عن زيد بن سلام عن جده ممطور عن الحارث ⦗١٦٧⦘ الأشعري أن نبي الله صلى الله عليه قال:
إن الله أمر يحيى بن زكريا بخمس كلمات أن يعمل بهن وأن يأمر بني إسرائيل أن يعملوا بهن فكاد يبطئ فقال له عيسى صلى الله عليه إنك قد أمرت بخمس كلمات أن تعمل بهن وأن تأمر بني إسرائيل أن يعملوا بهن فإما أن تبلغهم وإما أن أبلغهم فقال يا أخي إني أخشى إن سبقتني أن أعذب أو يخسف بي فجمع يحيى بني إسرائيل في بيت المقدس حتى امتلأ المسجد وقعدوا على الشرف فحمد الله وأثنى عليه ثم قال
إن الله أمرني بخمس كلمات أن تعملوا بهن أولهن أن تعبدوا الله ولا تشركوا به شيئا فإن مثل ذلك مثل رجل اشترى عبدا من خالص ماله بورق أو ذهب فقال هذه داري وهذا عملي فاعمل وأد إلي عملك فجعل يعمل ويؤدي عمله إلى غير سيده فأيكم يسره أن يكون عبده كذلك؟.
⦗١٦٨⦘ وإن الله خلقكم ورزقكم فاعبدوه ولا تشركوا به شيئا وأمركم بالصلاة فإن الله ينصب وجهه لعبده ما لم يلتفت فإذا صليتم فلا تلتفوا وأمركم بالصيام فإن مثل الصيام مثل من معه صرر من مسك في عصابة كلهم يحب أن يجد ريح المسك وأمركم بالصدقة فإن مثل ذلك كمثل رجل أسره العدو فشدوا يديه إلى عنقه فقال هل لكم أن أفتدي نفسي فجعل يفتدي نفسه منهم بالقليل والكثير حتى فدى نفسه وأمركم بذكر الله كثيرا فإن مثل ذلك كمثل رجل طلبه العدو سراعا في أثره فأتى حصنا حصينا فتحصن فيه وإن العبد أحصن ما يكون من الشيطان إذا كان في ذكر الله.
قال وقال رسول الله صلى الله عليه وسلم وأنا آمركم بخمس أمرني الله بهن بالجماعة والسمع والطاعة والهجرة والجهاد في سبيل الله وإنه من خرج عن الجماعة قيد شبر فقد خلع ربقة الإسلام من عنقه إلا أن يرجع (١) ومن دعا بدعوى جاهلية فهو من جثى جهنم قالوا يا رسول الله وإن صام وصلى فقال وإن صام وصلى وزعم أنه مسلم فادعوا المسلمين بما سماهم الله المسلمين المؤمنين عباد الله
_________
(١) في المخطوطة: يراجع. وما أثبتناه عن المعجم الكبير للطبراني. وفي الفتح الرباني في الموضعين: إلى أن يرجع.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (98,'٩٦ - حدثنا أبو عبيد قال حدثنا أبو النضر عن سليمان بن المغيرة عن علي بن زيد بن جدعان قال حدثنا علي بن حسين قال أقبلنا مع الحسين بن علي فكان قلما نزلنا منزلا إلا حدثنا بحديث يحيى بن زكرياء حيث قتل قال:
كان ملك من هذه الملوك مات وترك امرأته وابنته فورث ملكه أخوه فأراد أن يتزوج امرأة أخيه فاستأمر يحيى بن زكريا في أن يتزوجها وكانت الملوك في ذلك الزمان يعملون بأمر الأنبياء فقال له لا تزوجها فإنها بغي فعرفت المرأة أنه قد ذكرها وصرف عنها فقالت من أين هذا حتى بلغها أنه من قبل يحيى فقالت ليقتلن يحيى أو ليخرجن من ملكه!
فعمدت إلى ابنتها فصنعتها ثم قالت اذهبي إلى عمك عند الملأ فإنه إذا رآك سيدعوك ويجلسك في حجره ويقول ⦗١٧٠⦘ سليني ما شئت فإنك لن تسألي شيئا إلا أعطيتك فإذا قال لك ذلك فقولي لا أسألك شيئا إلا رأس يحيى!
قال وكانت الملوك إذا تكلم أحدهم بشيء على رءوس الملأ ثم لم يمض له نزع من ملكه ففعلت ذلك قال فجعل يأتيه الموت من قتله يحيى ويأتيه الموت من خروجه من ملكه قال فاختار ملكه فقتله قال فساخت بأمها الأرض.
قال ابن جدعان: فحدثت بهذا الحديث سعيد بن المسيب قال فما أخبرك كيف كان قتل زكريا قلت لا قال
زكرياء حيث قتل ابنه انطلق هاربا منهم فاتبعوه حتى أتى على شجرة ذات ساق فدعته إليها فانطوت عليه وبقي من ثوبه هدبة تكفتها الريح فانطلقوا إلى الشجرة فلم يجدوا أثره ثم بصروا بتلك الهدبة فدعوا بمنشار فقطعوا الشجرة فقطعوه فيها',NULL,'169',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (99,'٩٧ - حدثنا أبو عبيد قال حدثنا أبو مسهر عن سعيد بن عبد العزيز عن قسيم مولى عمر بن فلان قال:
كان ملك هذه المدينة يعني دمشق له بنت فتزوجها ⦗١٧١⦘ ابن أخيه فطلقها فأفتاه يحيى بن زكريا أنها لا تحل لها حتى تنكح زوجاً غيره فقال لها أمها: إذا كنت بين يدي الملك فقال ما حاجتك فقولي رأس يحيى فقالت له ذلك فأعظمه. فقال جلساؤه اضمن لها ما جعلت لها فأتى يحيى بن زكريا وهو قائم يصلي في جيرون فقطع رأسه ثم ذهبت البنت تحمله في طبق حتى إذا بلغت إلى موضع الفِسْقِيَّة خسف بها فخرجت أمها فقيل لها أدركي ابنتك فجاءت ولم يبق إلا رأسها فقالت اقطعوا رأسها فقطعوا رأسها وأخزى الله ذلك الملك',NULL,'170',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (100,'٩٨ - حدثنا أبو عبيد قال حدثنا يزيد بن سليمان التيمي عن أسلم العجلي عن أبي هريرة أو ابن عمر هكذا الحديث قال
إن امرأة ورثت من أبيها ملك أربع سنين يقال لها رنة قتلت يحيى بن زكريا فأتيت برأسه في طست فأمرت الأرض فأخذتها.
⦗١٧٢⦘ قال قال سليمان فلا أدري أفي هذا الحديث أم في حديث ذكره عن أبي أيوب عن عبد الله بن عمر أنها قتلت في يوم سبعين نبيا وأنها مكتوبة في التوراة مقتلة الأنبياء وأنها على منبر من حديد في النار يسمع صراخها أقصى أهل النار',NULL,'171',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (101,'٩٩ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح عن الليث عن يحيى بن سعيد عن سعيد بن المسيب قال
قدم بختنصر دمشق فإذا هو بدم يحيى بن زكريا يغلي فسأل عنه فأخبروه فقتل على دمه سبعين ألفا فسكن الدم',NULL,'172',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (102,'١٠٠ - حدثنا أبو عبيد قال حدثنا عفان بن مسلم عن حماد بن سلمة عن علي بن زيد عن يوسف بن مهران عن ابن عباس قال
قال رسول الله صلى الله عليه وسلم ما من ولد آدم أحد إلا قد خطىء أو هم بخطيئة ليس يحيى بن زكريا',NULL,'172',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (103,'<span data-type="title" id=toc-11>مواعظ لقمان وذكر ما كان من حكمته ووصيته ابنه</span>
١٠١ - حدثنا أبو عبيد قال: حدثنا هشيم قال أخبرنا أبو بشر عن مجاهد في قول الله {ولقد آتينا لقمان الحكمة} قال الصواب قال هشيم وقال غيره الصواب في غير نبوة',NULL,'173',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (104,'١٠٢ - حدثنا أبو عبيد قال حدثنا هشيم ومحمد بن جعفر عن شعبة عن الحكم عن مجاهد قال
كان لقمان رجلا صالحا ولم يكن نبيا',NULL,'173',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (105,'١٠٣ - حدثنا أبو عبيد قال حدثنا عبد الله بن صالح عن معاوية بن صالح عن يحيى بن سعيد عن سعيد بن المسيب
أن لقمان كان أسود من سودان مصر أعطاه الله الحكمة ومنعه النبوة',NULL,'174',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (106,'١٠٤ - حدثنا أبو عبيد قال حدثنا حكام الرازي عن سعيد بن عبد الرحمن الزبيدي عن مجاهد قال
كان لقمان عبدا حبشيا غليظ الشفتين مصفح القدمين قاضيا لبني إسرائيل',NULL,'174',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (107,'١٠٥ - حدثنا أبو عبيد قال حدثنا حجاج عن ⦗١٧٥⦘ المسعودي عن عون بن عبد الله قال
قال لقمان لابنه يا بني ارج الله رجاء لا تأمن فيه مكره وخف الله خوفا لا تأيس فيه من رحمته.
فقال: كيف أستطيع ذلك يا أبه وإنما لي قلب واحد فقال يا بني إن المؤمن كذي قلبين قلب يرجو به وقلب يخاف به',NULL,'174',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (108,'١٠٦ - حدثنا أبو عبيد قال حدثنا أبو الأسود عن ابن لهيعة عن خالد بن يزيد عن سعيد بن أبي هلال أنه بلغه أن لقمان الحكيم قال لابنه
يا بني إذا نذرت نذرا فأعد له وفاء وإذا تحملت حمالة فأعد لها غرامة وإذا أخطأت خطيئة فأعد لها ⦗١٧٦⦘ صدقة',NULL,'175',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (109,'١٠٧ - حدثنا أبو عبيد قال حدثنا أبو اليمان عن شعيب بن أبي حمزة عن ابن أبي حسين قال
بلغني أن لقمان الحكيم كان يقول يا بني لا تعلم العلم لتباهي به العلماء وتماري به السفهاء وترائي به في المجالس ولا تدع العلم زهدا فيه ورغبة في الجهالة
يا بني اختر المجالس على عينيك فإذا رأيت قوما يذكرون الله فاجلس معهم فإن تك عالما ينفعك علمك وإن تك جاهلا يعلموك وإذا رأيت قوما لا يذكرون الله فلا تجلس معهم فإن تك عالما لا ينفعك علمك وإن تك جاهلا يزيدوك غيا ولعل الله أن يطلع عليهم بعذاب فيصيبك معهم',NULL,'176',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (110,'١٠٨ - حدثنا أبو عبيد قال حدثنا يزيد عن نافع بن عمر الجمحي عن ابن أبي مليكة عن عبيد بن عمير أن لقمان قال لابنه
يا بني تخير المجالس على عينيك
ثم ذكر مثل حديث أبي اليمان إلى آخره وزاد فيه
ولا يعجبنك رحب الذراعين سفاك دماء الناس فإن له قاتلا لا يموت
وقال يزيد أحسبه قال بيده كصخرة يرضخ بها رأسه كلما رضخه أعيد له رأس جديد',NULL,'177',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (111,'١٠٩ - حدثنا أبو عبيد قال حدثنا أحمد بن عثمان عن ⦗١٧٨⦘ ابن المبارك عن جعفر بن حيان عن محمد ابن واسع قال
قال لقمان لابنه يا بني اتق الله ولا تر الناس أنك تخشاه ليكرموك وقلبك فاجر',NULL,'177',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (112,'١١٠ - قال وحدثونا عن سفيان بن عيينة قال
قيل للقمان: أي الناس خير فقال المؤمن العالم الغني قيل له أغني من المال قال لا ولكن الذي إذا احتيج إليه نفع وإذا استغنى عنه اكتفى
قال فأي الناس شر قال الذي لا يبالي أن يراه الناس مسيئا',NULL,'178',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (113,'<span data-type="title" id=toc-12>مواعظ الحكمة من سائر الكتب</span>
١١١ - حدثنا أبو عبيد قال حدثنا أبو معاوية عن هشام بن عروة عن أبيه قال
مكتوب في الحكمة بني لتكن كلمتك طيبة وليكن وجهك بسطا تكن أحب إلى الناس ممن يعطيهم العطاء
قال عروة ومكتوب في الحكمة أحبب خليلك وخليل أبيك',NULL,'179',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (114,'١١٢ - حدثنا أبو عبيد قال حدثنا ابن مهدي عن هشام الدستوائي عن قتادة قال
⦗١٨٠⦘ مكتوب في الحكمة اشرب تشبع وابتغ تجد واتق توقه',NULL,'179',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (115,'١١٣ - حدثنا أبو عبيد قال حدثنا إسحاق بن سليمان الرازي عن أبي جعفر الرازي عن الربيع بن أنس قال
مكتوب في الحكمة من يصحب صاحب السوء لا يسلم ومن يدخل مدخل السوء يتهم ومن لا يملك لسانه يندم',NULL,'180',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (116,'١١٤ - حدثنا أبو عبيد قال حدثنا خالد بن خداش عن صالح المري عن أبي عمران الجوني عن أبي الجلد قال
⦗١٨١⦘ قرأت في الحكمة أن الله تعالى يقول يا عبادي الصديقين تنعموا بعبادتي في الدنيا وإن بها تنعمون في الجنة',NULL,'180',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (117,'١١٥ - حدثنا أبو عبيد قال حدثنا مروان بن معاوية عن منصور بن حيان عن القاسم بن أبي بزة عن وهب بن منبه قال
مكتوب في الكتب إنك يا ابن آدم ما عبدتني ورجوتني فإني غافر لك على ما كان وحق عليّ ألا أضل عبدي وهو حريص على الهدى وأنا الحكم',NULL,'181',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (118,'١١٦ - حدثنا أبو عبيد قال حدثنا المغيرة ابن مطرف عن أبي عمر الصنعاني عن ابن بنت وهب ابن منبه عن وهب بن منبه قال
⦗١٨٢⦘ قرأت في بعض الكتب أن الله لم يخلق العباد للفناء ولكن خلقهم للبقاء إنما يتحولون من دار إلى دار',NULL,'181',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (119,'١١٧ - حدثنا أبو عبيد قال حدثنا أحمد بن عثمان عن ابن المبارك عن محمد بن مطرف عن الحجاج بن فرافصة قال
بلغني أن في بعض الكتب من عمل بغير مشورة فذلك باطل يتعنى ومن لم ينتصر من ظالمه بيد ولا لسان ولا حقد فذلك علمه كذا وكذا قال أبو عبيد لا أدري قال بيقين أم كلمة أخرى ومن استغفر لظالمه فقد هزم الشيطان',NULL,'182',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (120,'بسم الله الرحمن الرحيم

<span data-type="title" id=toc-13>جماع مواعظ أصحاب رسول الله صلى الله عليه وسلم ومن بعدهم </span>من التابعين وغيرهم',NULL,'183',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (121,'<span data-type="title" id=toc-14><span data-type="title" id=toc-15>مواعظ أبي بكر الصديق في خطبته ووصاياه</span></span>
١١٨ - حدثنا أبو عبيد قال حدثنا أبو النضر عن شعبة قال أخبرني يزيد بن خمير قال سمعت سليم ابن عامر يحدث عن أوسط البجلي أنه سمع أبا بكر حين توفي رسول الله صلى الله عليه وسلم يقول:
قام فينا رسول الله صلى الله عليه وسلم عام أول مقامي هذا ثم بكى ثم ⦗١٨٦⦘ قال عليكم بالصدق فإنه مع البر وهما في الجنة وإياكم والكذب فإنه مع الفجور وهما في النار وسلوا الله المعافاة فإنه لم يؤت رجل بعد اليقين شيئا خير له من المعافاة ثم قال لا تقاطعوا ولا تدابروا ولا تباغضوا ولا تحاسدوا وكونوا عباد الله إخوانا',NULL,'185',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (122,'١١٩ - حدثنا أبو عبيد قال حدثنا علي بن هاشم عن هشام بن عروة عن أبيه قال
خطب أبو بكر فحمد الله وأثنى عليه ثم قال أما بعد فإني وليت أمركم ولست بخيركم ولكنه نزل القرآن وسن النبي صلى الله عليه وعلمنا فعلمنا واعلمن أيها الناس أن أكيس الكيس التقى أو قال الهدى وأعجز العجز الفجور وأن أقواكم عندي الضعيف حتى آخذ له بحقه وأن أضعفكم عندي القوي حتى آخذ منه الحق
⦗١٨٧⦘ أيها الناس إنما أنا متبع ولست بمبتدع فإن أنا أحسنت فأعينوني وإن أنا زغت فقوموني أقول قولي هذا وأستغفر الله لي ولكم',NULL,'186',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (123,'١٢٠ - حدثنا أبو عبيد قال حدثنا علي بن هاشم عن إسماعيل بن أبي خالد عن قيس بن أبي حازم أو غيره عن أبي بكر نحو ذلك',NULL,'187',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (124,'١٢١ - حدثنا أبو عبيد قال حدثنا أزهر بن عمير وكان بالثغر قال حدثني أبو الهذيل عن عمرو بن دينار ⦗١٨٨⦘ قال
خطب أبو بكر الصديق فقال أوصيكم بتقوى الله لفقركم وفاقتكم أن تتقوه وأن تثنوا عليه بما هو أهله وأن تستغفروه إنه كان غفارا وأن تخلطوا الرغبة بالرهبة وأن تخلصوا لله اليقين فيما بلغكم من كتابه فإنه أثنى على زكرياء وأهل بيته فقال {إنهم كانوا يسارعون في الخيرات ويدعوننا رغبا ورهبا وكانوا لنا خاشعين}
واعلموا أن الله قد ارتهن بحقه أنفسكم وأخذ على ذلك مواثيقكم واشترى منكم القليل الفاني بالكثير الباقي وهذا كتاب الله لا تفنى عجائبه فصدقوا قول الله وانتصحوا كتابه واستضيئوا به ليوم الظلمة فإنما خلقكم لعبادته وأمركم بطاعته
واعلموا أنكم تغدون وتروحون في آجال قد غيب عنكم علمها فإن استطعتم أن ينقضي الأجل وأنتم في طاعة الله فافعلوا ولن تستطيعوا ذلك إلا بالله وسابقوا في مهل من آجالكم من قبل أن تنقضي فيكون قد أسلمتم إلى سوء أعمالكم فإن قوماً جعلوا آجالهم لغيرهم ونسوا أنفسهم فأنهاكم أن تكونوا أمثالهم فالوحا الوحا ثم النجاء النجاء فإن من ورائكم طالباً حثيثا ممره سريع.
واعلموا أنكم ما أخلصتم لله فربكم أطعتم وحقكم ⦗١٨٩⦘ حفظتم فأعطوا ضرائبكم في أيام سلفكم واجعلوها نوافل بين أيديكم كي تستوفوا سلفكم وضرائبكم حين فقركم وحاجتكم
ثم تفكروا عباد الله فيمن كان قبلكم أين كانوا أمس وأين هم اليوم وأين الجبارون الذين كان لهم ذكر القتال وأين الذين كانوا يعطون الغلبة في مواطن الحروب قد ضعضع بهم الدهر وصاروا رميما وأين الملوك الذين أثاروا الأرض وعمروها قد نسوا ونسي ذكرهم فهم اليوم كلا شيء وأين الملوك الذين بنوا المدائن العظام وحصنوها وجعلوا فيها الأعاجيب فتلك بيوتهم خاوية وهم في ظلمات القبور {هل تحس منهم من أحد أو تسمع لهم ركزا} أين الوضاء (١) الحسنة وجوههم المعجبون بشبابهم قد صاروا رميما وأين من تعرفون من أصحابكم وإخوانكم قد وردوا على ما قدموا فحلوا في الشقوة والسعادة.
إن الله تبارك وتعالى ليس بينه وبين أحد من خلقه نسب يعطيه به خيرا ولا يصرف عنه به شرا إلا بطاعته واتباع أمره وإنه لا خير بخير بعده النار ولا شر بشر بعده الجنة أقول قولي هذا وأستغفر الله لي ولكم
_________
(١) في المخطوطة: الوضاة. والتصحيح من نهاية الأرب. والوضاء: جمع وضيئ وهو الحسن النظيف. انظر لسان العرب (وضأ) ١/ ١٩٠.',NULL,'187',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (125,'١٢٢ - حدثنا أبو عبيد قال حدثنا نعيم عن بقية ⦗١٩٠⦘ بن الوليد عن مكنف بن حاجب أن أبا بكر الصديق رضي الله عنه لما حضرته الوفاة دعا كاتبا فقال اكتب
بسم الله الرحمن الرحيم
هذا ما أوصى به أبو بكر عند آخر عهده بالدنيا خارجا منها وأول عهده بالآخرة داخلا فيها حيث يصدق الكاذب ويتقي الفاجر أو قال ويتوب الفاجر ويؤمن الكافر.
إني وليت عليكم عمر بن الخطاب فإن يعدل فذاك ظني به ورجائي فيه وإن غير فالخير أردت ولا يعلم الغيب إلا الله {وسيعلم الذين ظلموا أي منقلب ينقلبون}',NULL,'189',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (126,'١٢٣ - حدثنا أبو عبيد قال حدثنا حجاج عن وطر بن خليفة عن عبد الرحمن بن عبد الله قال لعمر بن عبد العزيز حدثنا ابن عباس قال
قال رسول الله صلى الله عليه وسلم إن لكل شيء شرفا وإن شرف المجلس ما استقبل به القبلة وإنما تجالسون والأمانة ولا تصلوا خلف النائم ولا المحدث ولا تستروا الجدر واقتلوا الحية والعقرب وإن كنتم في الصلاة.
ومن نظر في كتاب أخيه بغير إذنه فإنما ينظر في النار. ومن أحب أن يكون أكرم الناس فليتق الله ومن أحب ⦗١٩٢⦘ أن يكون أقوى الناس فليتوكل على الله ومن أحب أن يكون أغنى الناس فليكن بما في يد الله أوثق منه بما في يديه
ألا أنبئكم بشراركم قالوا بلى يا رسول الله قال من نزل وحده ومنع رفده وجلد عبده ألا أنبئكم بشر من هذا قالوا بلى يا رسول الله قال الذي يبغض الناس ويبغضونه ألا أنبئكم بشر من هذا قالوا بلى يا رسول الله قال الذين لا يقيلون عثرة ولا يقبلون معذرة ألا أنبئكم بشر من هذا قالوا بلى يا رسول الله قال الذي لا يرجى خيره ولا يؤمن شره.
إن عيسى بن مريم قال لقومه يا بني إسرائيل لا تكلموا بالحكمة عند الجهال فتظلموها ولا تمنعوها أهلها فتظلموهم ولا تظلموا ولا تعاقبوا ظالما فيبطل فضلكم.
يا بني إسرائيل الأمر ثلاثة أمر تبين رشده فاتبعه وأمر تبين غيه فاجتنبه وأمر اختلف فيه فرده إلى الله',NULL,'191',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (127,'١٢٤ - حدثنا أبو عبيد قال حدثنا عباد بن عباد قال حدثني الحجاج بن فرافصة عن رجلين قال عباد قد سماهما ⦗١٩٣⦘ الحجاج عن الزهري عن عبيد الله بن عبد الله بن عتبة عن ابن عباس قال:
قال لي رسول الله صلى الله عليه وسلم يا غلام ألا أعلمك كلمات ينفعك الله بهن احفظ الله يحفظك احفظ الله تجده أمامك تعرف إلى الله في الرخاء يعرفك في الشدة إذا سألت فاسأل الله وإذا استعنت فاستعن بالله جف القلم بما هو كائن فلو اجتمع الخلق أن يعطوك شيئا لم يكتبه الله لك لم يقدروا عليه وعلى أن يمنعوك شيئا كتبه الله لك لم يقدروا عليه فاعمل لله بالرضى في اليقين واعلم أن في الصبر على ما تكره خيرا كثيرا وأن الفرج مع الكرب وأن مع العسر يسرا',NULL,'192',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (128,'١٢٥ - حدثنا أبو عبيد قال حدثنا إسماعيل بن عياش عن عمر مولى غفرة عن ابن عباس مثل ذلك أو نحوه',NULL,'193',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (129,'١٢٦ - حدثنا أبو عبيد قال حدثنا أبو معاوية وعمر ⦗١٩٤⦘ بن عبد الرحمن الأبار كلاهما عن ليث بن أبي سليم عن مجاهد عن ابن عمر قال
أخذ رسول الله صلى الله عليه وسلم بثوبي أو ببعض جسدي ثم قال يا عبد الله كن في الدنيا غريبا أو عابر سبيل وعد نفسك في أهل القبور
قال قال مجاهد وقال لي عبد الله بن عمر يا مجاهد إذا أصبحت فلا تحدث نفسك بالمساء وإذا أمسيت فلا تحدث نفسك بالصباح وخذ من حياتك قبل موتك ومن صحتك قبل سقمك فإنك يا عبد الله لا تدري ما اسمك غدا',NULL,'193',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (130,'١٢٧ - حدثنا أبو عبيد قال حدثنا كثير بن هشام عن جعفر بن برقان عن زياد بن الجراح عن عمرو بن ميمون الأودي ⦗١٩٥⦘ قال
بلغني أن رسول الله صلى الله عليه وسلم قال لرجل وهو يعظه اغتنم خمسا قبل خمس شبابك قبل هرمك وصحتك قبل سقمك وغناك قبل فقرك وفراغك قبل شغلك وحياتك قبل موتك',NULL,'194',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (131,'١٢٨ - حدثنا أبو عبيد قال حدثنا إسماعيل بن جعفر عن العلاء بن عبد الرحمن عن أبيه عن أبي هريرة قال
قال رسول الله صلى الله عليه وسلم بادروا بالأعمال ستا طلوع الشمس من مغربها أو الدجال أو الدخان أو الدابة أو خاصة أحدكم أو [أمر] العامة',NULL,'195',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (132,'١٢٩ - حدثنا أبو عبيد قال حدثنا أبو معاوية عن أبي رجاء الجزري عن برد بن سنان عن واثلة عن أبي هريرة قال
قال رسول الله صلى الله عليه وسلم يا أبا هريرة كن ورعا تكن أعبد الناس وكن قنعا تكن أشكر الناس وأحب للناس ما تحب لنفسك تكن مؤمنا وأحسن جوار من جاورك تكن مسلما وأقلل الضحك فإن كثرة الضحك تميت القلب',NULL,'196',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (133,'١٣٠ - حدثنا أبو عبيد قال حدثنا علي بن عاصم عن عبد الله بن عثمان بن خثيم عن عثمان بن جبير عن أبيه أو ⦗١٩٧⦘ جده عن أبي أيوب الأنصاري قال
أتى النبي صلى الله عليه وسلم رجل فقال عظني وأوجز فقال إذا قمت في صلاتك فصل صلاة مودع ولا تكلمن بكلام يعتذر منه غدا واجمع اليأس مما في أيدي الناس',NULL,'196',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (134,'١٣١ - حدثنا أبو عبيد قال حدثنا خالد بن عمرو عن سفيان عن أبي حازم عن سهل بن سعد ⦗١٩٨⦘ قال
قال رجل يا رسول الله دلني على عمل إذا أنا عملته أحبني الله وأحبني الناس فقال رسول الله صلى الله عليه وسلم ازهد في الدنيا يحبك الله وازهد فيما في أيدي الناس يحبك الناس
قال أبو عبيد قد كنت منكرا لهذا الحديث فحدثني هذا الشيخ عن وكيع أنه سأله عنه وعن حديث آخر قد ذكرناه في كتاب النكاح ولولا مقالته هذه لتركتها',NULL,'197',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (135,'١٣٢ - حدثنا أبو عبيد قال حدثنا عبد الرحمن بن مهدي عن سفيان عن حبيب بن أبي ثابت عن ابن سابط قال
⦗١٩٩⦘
لما حضر أبا بكر الموت أرسل إلى عمر فقال يا عمر إن وليت على الناس فاتق الله واعلم أن لله عملا بالليل لا يقبله بالنهار وأن لله عملا بالنهار لا يقبله بالليل وأنه لا يقبل نافلة حتى تؤدى الفريضة وإنما ثقلت موازين من ثقلت موازينه يوم القيامة باتباعهم الحق في الدنيا وثقله عليهم وحق لميزان إذا وضع فيه الحق غدا بأن يكون ثقيلا وإنما خفت موازين من خفت موازينه يوم القيامة باتباعهم الباطل في الدنيا وخفته عليهم وحق لميزان إذا وضع فيه الباطل غدا أن يكون خفيفا
وأن الله ذكر أهل الجنة فذكرهم بأحسن أعمالهم وتجاوز عن سيئها فإذا ذكرتهم قلت إني لخائف ألا ألحق بهم وأن الله ذكر أهل النار فذكرهم بأسوأ أعمالهم ورد عليهم أحسنها فإذا ذكرتهم قلت إني لخائف أن أكون من هؤلاء.
وأن الله ذكر آية الرحمة مع آية العذاب ليكون المؤمن راغبا راهبا لا يتمنى على الله ولا يقنط من رحمته
⦗٢٠٠⦘ فإن أنت حفظت وصيتي فلا يكونن غائب أحب إليك من الموت وهو آتيك وإن أنت ضيعت وصيتي فلا يكونن غائب أبغض إليك من الموت ولست بمعجزه',NULL,'198',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (136,'<span data-type="title" id=toc-16>مواعظ عمر بن الخطاب رضي الله عنه في الخطب والوصايا والكتب وغير ذلك</span>
١٣٣ - حدثنا أبو عبيد قال حدثنا النضر بن إسماعيل قال حدثنا محمد بن سوقة عن عبد الله بن دينار عن ابن عمر قال
خطبنا عمر بالجابية فقال إني قمت فيكم كمقام رسول الله صلى الله عليه وسلم فينا فقال
أوصيكم أصحابي ثم الذين يلونهم ثم يفشو الكذب حتى يحلف الرجل ولا يستحلف وحتى يشهد الشاهد ولا يستشهد وعليكم بالجماعة وإياكم والفرقة فإن الشيطان مع الواحد وهو من الاثنين أبعد
لا يخلون رجل بامرأة لا يخلون رجل بامرأة لا يخلون ⦗٢٠٢⦘ رجل بامرأة لا يخلون رجل بامرأة إلا كان ثالثهما الشيطان.
من أراد بحبوحة الجنة فليلزم الجماعة ومن سرته حسنته وساءته سيئته فذلك المؤمن',NULL,'201',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (137,'١٣٤ - حدثنا أبو عبيد قال حدثنا علي بن معبد عن عبيد الله بن عمرو عن عبد الملك بن عمير عن رجل عن عبد الله بن الزبير عن عمر نحو ذلك',NULL,'202',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (138,'١٣٥ - حدثنا أبو عبيد قال حدثنا هشام بن عمار عن عمرو بن واقع قال حدثنا عروة بن رويم عن الصالحي قال
كتب عمر بن الخطاب إلى أبي عبيدة بن الجراح فأتاه كتابه بالجابية في مثل أذن الفأرة بكلام ما سمعت أحسن منه من أحد من الناس
أما بعد فإنه لا يقيم أمر الله في الناس إلا حصيف العقدة بعيد الغرة لا يطلع الناس منه على عروة ولا يحنق في الحق ⦗٢٠٤⦘ على الجرة ولا يخاف في الله لومة لائم',NULL,'203',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (139,'١٣٦ - حدثنا أبو عبيد قال حدثنا يزيد عن هشام بن حسان عن الحسن قال
كتب عمر إلى أبي موسى أما بعد فإن القوة في العمل ألا تؤخر عمل اليوم لغد فإنكم إذا فعلتم ذلك تداركت عليكم الأعمال فلم تدروا بأيها تأخذون فأضعتم.
وإن الأعمال مؤداه إلى الأمير ما أدى الأمير إلى الله فإذا رتع الأمير رتعوا.
وإن للناس نفرة عن سلطانهم وأعوذ بالله أن تدركني أو قال تدركنا فإنها ضغائن محمولة ودنيا موثرة وأهواء متبعة فأقيموا الحق ولو ساعة من نهار',NULL,'204',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (140,'١٣٧ - حدثنا أبو عبيد قال حدثنا المبارك بن سعيد بن مسروق عن يحيى بن عبد الله قال
كتب عمر بن الخطاب إلى عبد الله بن عمر أما بعد فإني أوصيك بتقوى الله فإنه من اتقاه وقاه ومن أقرضه جزاه ومن شكره زاده
⦗٢٠٥⦘ اجعل التقوى نصب عينيك وجلاء قلبك واعلم أنه لا عمل لمن لا نية له ولا أجر لمن لا خشية له ولا مال لمن لا رفق له ولا جديد لمن لا خلق له',NULL,'204',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (141,'١٣٨ - حدثنا أبو عبيد قال حدثنا علي بن ثابت عن جعفر بن برقان قال
قال عمر بن الخطاب لا تعرضن فيما لا يعنيك واعتزل عدوك واحتفظ من خليلك إلا الأمين فإن الأمين من القوم لا يعدله شيء وشاور في أمرك الذين يخشون الله فإنما يخشى الله من عباده العلماء',NULL,'205',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (142,'١٣٩ - حدثنا أبو عبيد قال حدثنا أحمد بن يعقوب قال (١)
_________
(١) هكذا في المخطوطة، ويبدو أن هنا سقطاً أو أن الحديث التالي روي بطريقين.',NULL,'205',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (143,'١٤٠ - حدثنا علي بن عبد العزيز قال حدثنا أبو عبيد القاسم بن سلام قال حدثنا حجاج بن محمد قال حدثنا شعبة بن الحجاج عن عمر بن سليمان عن عبد الرحمن ⦗٢٠٦⦘ بن أبان عن أبيه أبان بن عثمان عن زيد بن ثابت أنه خرج من عند مروان بنصف النهار قال
فقلنا ما خرج من عنده هذه الساعة إلا لشيء سأله عنه فأتيته فسألته فقال أجل سألنا عن أشياء سمعتها من رسول الله صلى الله عليه وسلم
سمعت رسول الله صلى الله عليه وسلم يقول نضر الله امرأ سمع منا حديثا فحمله حتى يبلغه غيره فرب حامل فقه لأفقه منه ورب حامل فقه ليس بفقيه
ثلاث لا يغل عليهن قلب مسلم إخلاص العمل لله ⦗٢٠٧⦘ ومناصحة ولاة الأمر ولزوم الجماعة فإن دعوتهم تحيط من ورائهم
قال ومن كانت نيته الآخرة جمع الله شمله وجعل غناه في قلبه وأتته الدنيا وهي راغمة ومن كانت نيته الدنيا فرق الله عليه أمره وجعل فقره بين عينيه ولم يأته من الدنيا إلا ما كتب له
قال وسألته عن {الصلاة الوسطى} فقال هي الظهر',NULL,'205',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (144,'١٤١ - حدثنا أبو عبيد قال حدثنا أحمد بن خالد الحمصي عن محمد بن إسحاق عن الزهري عن محمد ⦗٢٠٨⦘ بن جبير بن مطعم عن أبيه قال:
قام رسول الله صلى الله عليه وسلم بالخيف من منى فقال نضر الله عبدا سمع مقالتي فوعاها ثم أداها إلى من لم يسمعها فرب حامل فقه إلى من هو أفقه منه.
ثلاث لا يغل عليهن قلب المؤمن إخلاص العمل وطاعة ذوي الأمر ولزوم الجماعة فإن دعوتهم تحيط من ورائهم',NULL,'207',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (145,'١٤٢ - حدثنا أبو عبيد حدثنا حجاج عن حماد بن سلمة عن سماك بن حرب عن عبد الرحمن ⦗٢٠٩⦘ بن يزيد عن ابن مسعود قال:
قال رسول الله صلى الله عليه وسلم نضر الله رجلا سمع منا كلمة فبلغها عنا كما سمعها فإنه رب مبلغ أوعى من سامع
قال حجاج وكنت سمعته من المسعودي عن سماك عن عبد الرحمن بن مسعود عن أبيه فذكرت ذلك لحماد فقال هكذا حفظته عن سماك',NULL,'208',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (146,'١٤٣ - حدثنا أبو عبيد قال حدثنا إسماعيل بن إبراهيم عن أيوب عن ابن سيرين عن أبي بكرة أن رسول الله صلى الله عليه وسلم خطب في حجته فقال
إن الزمان قد استدار كهيئته يوم خلق الله السموات والأرض السنة اثنا عشر شهراً منها أربعةٌ حرم ثلاثة متواليات ذو القعدة ⦗٢١٠⦘ وذو الحجة والمحرم ورجب مضر الذي بين جمادى وشعبان.
ثم قال أي يوم هذا قال فقلنا الله ورسوله أعلم فسكت حتى ظننا أنه سيسميه بغير اسمه فقال أليس يوم النحر قلنا بلى ثم قال أي شهر هذا قلنا الله ورسوله أعلم قال فسكت حتى ظننا أنه سيسميه بغير اسمه فقال أليس ذا الحجة قلنا بلى قال فإن دماءكم وأموالكم قال وأحسبه قال وأعراضكم عليكم حرام كحرمة يومكم هذا في شهركم هذا في بلدكم هذا وستلقون ربكم فيسألكم عن أعمالكم
ألا لا ترجعن بعدي ضلالا يضرب بعضكم رقاب بعض ألا هل بلغت ألا ليبلغ الشاهد منكم الغائب فلعل بعض من يبلغه أن يكون أوعى له من بعض من سمعه.
قال قال ابن سيرين قد كان ذاك كان بعض من بلغه أوعى من بعض من سمعه',NULL,'209',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (147,'١٤٤ - قال أبو عبيد وكان غير أيوب يزيد في إسناده فيقول عن ابن سيرين عن عثمان بن عفان عن عبد الله بن المبارك عن مالك بن مغول أنه بلغه أن عمر بن الخطاب قال
حاسبوا أنفسكم قبل أن تحاسبوا وزنوها قبل أن توزنوا وتجهزوا للعرض الأرض {يومئذ تعرضون لا تخفى منكم خافية}',NULL,'210',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (148,'<span data-type="title" id=toc-17>مواعظ عمر التي أشار بها عليه أصحابه في الكتب وغيرها</span>
١٤٥ - حدثنا أبو عبيد قال حدثنا مروان بن معاوية قال حدثنا محمد بن سوقة قال أخرج إلي نعيم بن أبي هند صحيفة فأتى فيها
من أبي عبيدة ومعاذ إلى عمر أما بعد فإنا عهدناك وشأن نفسك لك مهم فأصبحت قد وليت أمر هذه الأمة أحمرها وأسودها يجلس بين يديك الشريف والوضيع والصديق والعدو ولكل حصته من العدل فانظر كيف أنت عند ذلك يا عمر؟
وإنا نحذرك ما حذرت الأمم قبلك يوما تعنو فيه الوجوه ⦗٢١٢⦘ وتجب فيه القلوب وتقطع فيه الحجج لعزة ملك قهرتهم جبروته والخلق داخرون له ينتظرون قضاءه ويخافون عقوبته
وإنه ذكر لنا أنه سيأتي على الناس زمان فيه إخوان العلانية أعداء السريرة وإنا نعوذ بالله أن ينزل كتابنا من قلبك سوى المنزل الذي نزل من قلوبنا وإنا كتبنا إليك نصيحة والسلام عليك!
فكتب إليهما عمر جاءني كتابكما تذكران أنكما عهدتماني وشأن نفسي إلي مهم فأصبحت قد وليت أمر هذه الأمة أحمرها وأسودها يجلس بين يدي الشريف والوضيع والصديق والعدو ولكل حصته من العدل فانظر كيف أنت عند ذلك يا عمر فإنه لا حول ولا قوة إلا بالله
وكتبتما تحذراني ما حذرت الأمم قبل ليوم تعنو فيه الوجوه وتجب فيه القلوب وتقطع فيه الحجج لعزة ملك قهرتهم ⦗٢١٣⦘ جبروته والخلق له داخرون ينتظرون قضاءه ويخافون عقوبته وقديما كان اختلاف الليل والنهار بآجال الناس يبليان كل جديد ويقربان كل بعيد ثم يصير الناس إلى الجنة وإلى النار
كتبتما تذكران أن سيأتي على الناس زمان يكون فيه إخوان العلانية أعداء السريرة ولستما بأولئك ولا هذا ذاك الزمان [إنما ذلك في آخر الزمان] إذا ظهرت الرغبة والرهبة وكانت رغبة الناس بعضهم إلى بعض لصلاح ديناهم
وكتبتما تعوذان بالله أن ينزل كتابكما من قلبي سوى المنزل الذي نزل به من قلوبكما وأنكما كتبتما إلي نصيحة وقد صدقتما فتعهداني منكما بكتاب فإني لا غنى بي عنكما والسلام عليكما',NULL,'211',NULL,NULL,'0');
INSERT INTO [b26510] VALUES (149,'تم الكتاب في المواعظ
⦗٢١٤⦘
الحمد لله وحده
وصلى الله على سيدنا محمد وآله الطيبين الطاهرين

نسخت هذه النسخة
[من] (١) كتاب تاريخه سنة مائتين وست وثمانين
من الهجرة النبوية

وكان الفراغ منه في ربيع الأول
سنة تسع وأربعين وسبعمائة
_________
(١) ما بين المعقوفين ساقطة من المخطوطة.',NULL,'213',NULL,NULL,'0');
