INSERT INTO [b26548] VALUES (1,'من حديث سفيان بن سعيد الثوري
INSERT INTO [b26548] VALUES (2,'<span data-type="title" id=toc-2>[من حديث سفيان عن أبي الزبير]</span>
INSERT INTO [b26548] VALUES (3,'٢- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن أبي الزبير , عن جابر رضي الله عنه قال نهى رسول الله صلى الله عليه وسلم أن يشرب من فم السقاء.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (4,'٣- حدثنا يعلى، قال: حدثنا سفيان، عن أبي الزبير، عن جابر رضي الله عنه، قال: جاء أبو حميد إلى النبي صلى الله عليه وسلم، بقدح من لبن، بالبقيع بالنقيع نهارا، فقال: ألا خمرته، ولو بعود تعرضه عليه.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (5,'٤- حدثنا قَبِيصَة حدثنا سفيان عن أبي الزبير , عن جابر رضي الله عنه قال مروا على رسول الله صلى الله عليه وسلم بحمار قد وسم في وجهه يدخن منخراه فقال لعن الله من فعل هذا ألست قد نهيت عن هذا ألا لا تسموا وجه حمار ولا تضلابوا تضربوا وجهه.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (6,'٥- حدثنا قَبِيصَة , حدثنا سفيان عن أبي الزبير , عن جابر رضي الله عنه قال: قال النبي صلى الله عليه وسلم أيما رجل كان له شرك في أرض أو ربعة ⦗٤٧⦘ فليس له أن يبيع حتى يستأذن صاحبه فإن شاء أخذ وإن شاء أذن له.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (7,'٦- حدثنا يعلى وقَبِيصَة وأبو نُعَيْم قالوا حدثنا سفيان عن أبي الزبير , عن جابر قال قال رسول الله صلى الله عليه وسلم أمسكوا عليكم أموالكم لا تعمروها أحدا فمن أعمر شيئا فهو له.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (8,'٧- حدثنا أبو نُعَيْم وقَبِيصَة حدثنا سفيان عن أبي الزبير , عن جابر قال: قال رسول الله صلى الله عليه وسلم أمرت أن أقاتل الناس حتى يقولوا: لا إله إلا الله، فإذا قالوها عصموا مني دماءهم وأموالهم إلا بحقها، وحسابهم على الله، ثم قرأ: {فذكر إنما أنت مذكر لست عليهم بمصيطر} .',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (9,'٨- حدثنا أبو نُعَيْم، حدثنا سفيان، عن أبي الزبير، عن جابر رضي الله عنه، قال: قال رسول الله صلى الله عليه وسلم: إن عرش إبليس على البحر، فيبعث سراياه، فأغبطهم عنده منزلة، أعظمهم فتنة.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (10,'٩- حدثنا أبو نُعَيْم، حدثنا سفيان، عن أبي الزبير، عن جابر رضي الله عنه، قال: قال رسول الله صلى الله عليه وسلم: إن إبليس قد يئس أن يعبده المصلون، ولكن في التحريش بينهم.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (11,'١٠- حدثنا أبو نُعَيْم، حدثنا سفيان، عن أبي الزبير، عن جابر رضي الله عنه، قال: كان النبي صلى الله عليه وسلم يقرأ {الم تنزيل} و {تبارك} .',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (12,'١١- حدثنا أبو نُعَيْم وقَبِيصَة حدثنا سفيان عن أبي الزبير , عن جابر قال كوى النبي صلى الله عليه وسلم سعدا في أكحله [مرتين] .',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (13,'<span data-type="title" id=toc-3>من حديث سفيان عن عبد الله بن عثمان بن خثيم</span>
INSERT INTO [b26548] VALUES (14,'١٣- حدثنا قَبِيصَة حدثنا سفيان عن عبد الله عن ⦗٥٠⦘ عبد الرحمن بن بهمان عن عبد الرحمن بن حسان بن ثابت , عن أبيه قال لعن رسول الله صلى الله عليه وسلم زوارات القبور.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (15,'١٤- حدثنا قَبِيصَة حدثنا سفيان عن عبد الله بن عثمان عن إسماعيل بن عبيد بن رفاعة الزرقي عن أبيه , عن جده قال: قال رسول الله صلى الله عليه وسلم إن قريشا أهل أمانة [وصدق فمن] ، بغاها العوائر أكبه الله لمنخريه من بغاها العوائر أكبه الله لمنخريه من بغاها العوائر أكبه الله لمنخريه.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (16,'١٥- حدثنا أبو نُعَيْم حدثنا سفيان عن عبد الله بن عثمان عن سعيد بن جُبَيْر , عن ابن عباس رضي الله عنهما أن رسول الله صلى الله عليه وسلم تزوج وهو محرم واحتجم وهو محرم.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (17,'١٦- حدثنا يعلى وقَبِيصَة حدثنا سفيان عن عبد الله ؤبن ابن عثمان عن سعيد بن جُبَيْر , عن ابن عباس رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم خير أكحالكم الإثمد يجلو البصر وينبت الشعر.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (18,'١٧- حدثنا يعلى حدثنا سفيان عن عبد الله بن عثمان عن إسماعيل بن عبيد بن رفاعة عن أبيه , عن جده قال خرج رسول الله صلى الله عليه وسلم إلى البقيع، فقال: يا معشر التجار حتى إذا اشرأبوا قال: إن التجار يحشرون يوم القيامة فجارا إلا من اتقى وبر وصدق.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (19,'١٨- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن عثمان بن خثيم عن ابن سابط عن حفصة ابنة [عبد الرحمن بن] أبي بكر على عن أم سلمة قالت لما قدم المهاجرون المدينة تزوجوا في الأنصار فكانوا يجبونهم، وكانت الأنصار لا تفعل ذلك , [فأراد رجل من المهاجرين ⦗٥٢⦘ امرأته على ذلك] فقالت امرأة رجل منهم منهن لزوجها: حتى أسأل رسول الله صلى الله عليه وسلم فاستحيت أن تسأله فسألته؟ فدعا بها فقرأ عليها: {نساؤكم حرث لكم فأتوا حرثكم أنى شئتم} صمام واحد صمام واحد صمام واحد. .',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (20,'١٩- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن عثمان عن شهر بن حوشب عن أسماء ابنة يزيد قالت قال رسول الله صلى الله عليه وسلم لا يصلح الكذب إلا في ثلاثة الرجل يكذب امرأته لترضى عنه والكذب ليصلح بين الناس والكذب في الحرب.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (21,'<span data-type="title" id=toc-4>من حديث سفيان عن عبد الله بن محمد بن عَقِيل</span>.
INSERT INTO [b26548] VALUES (22,'٢١- حدثنا يعلى وأبو نُعَيْم وقَبِيصَة حدثنا سفيان عن عبد الله [بن محمد بن عقيل] عن جابر بن عبد الله رضي الله عنه قال كنا نصلي مع رسول الله صلى الله عليه وسلم المغرب ومنازلنا [على قدر] ميل ثم نرجع إلى منازلنا ونحن نبصر مواقع النبل.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (23,'٢٢- حدثنا قَبِيصَة عن سفيان عن عبد الله عن جابر رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم خير صفوف الرجال المقدم وشرها المؤخر وخير صفوف النساء المؤخر وشرها المقدم.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (24,'٢٣- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن محمد بن عَقِيل عن جابر بن عبد الله رضي الله عنه قال كنا مع رسول الله صلى الله عليه وسلم في صير بالمدينة فقال يطلع عليكم رسول الله صلى الله عليه وسلم ⦗٥٤⦘ مرجل رجل من أهل الجنة قال فجاء أبو بكر رضي الله عنه فهنيناه، ثُم قال: يطلع عليكم رجل من أهل الجنة قال فجاء عمر رضي الله عنه فهنيناه، ثُم قال: يطلع عليكم رجل من أهل الجنة قال فرأيت رسول الله صلى الله عليه وسلم يصغي رأسه من تحت السعف ويقول اللهم إن شئت جعلته عليا فجاء علي رضي الله عنه فهنيناه.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (25,'٢٤- حدثنا قَبِيصَة , وأبرن وأبو نعيم قالا حدثنا سفيان عن عبد الله عن الطفيل بن أبي بن كعب عن أبي بن كعب قال قلت يا رسول الله صلى الله عليه وسلم إني أكثر الصلاة عليك، فكم أجعل لك من صلاتي؟ قال: ما شئت، [قلت: الربع؟ قال: ما شئت، وإن زدت فهو خير لك، قلت النصف؟ قال: ما شئت، فإن زدت فهو خير لك، قلت الثلثين؟ قال: ما شئت] فإن زدت فهو خير لك، قلت: أجعل صلاتي كلها؟ قال: إذن تكفى همك ويغفر ذنبك.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (26,'٢٥- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن محمد بن عَقِيل عن جابر بن عبد الله أو أنس قال: قال رسول الله صلى الله عليه وسلم لصوت أبي طلحة في الجيش خير من ألف رجل.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (27,'٢٦- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن محمد بن عقيل، عن أبي سلمة، عن أبي هريرة، أو عائشة قالا: كان رسول الله صلى الله عليه وسلم إذا ضحى اشترى كبشين عظيمين، سمينين أقرنين، أملحين موجؤين يذبح أحدهما عن أمته من شهد منهم بالتوحيد، وشهد له بالبلاغ، ويذبح الآخر عن محمد صلى الله عليه وسلم، وآل محمد صلى الله عليه وسلم.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (28,'٢٧- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن محمد بن عَقِيل عن الطفيل بن أبي بن كعب، عن أبي بن كعب رضي الله عنه، قال: كان رسول الله صلى الله عليه وسلم: إذا جاء ربع الليل قام فقال أيها الناس اذكروا الله اذكروا الله جاءت الراجفة تتبعها الرادفة تتبعها الرادفة، جاء الموت بما فيه جاء الموت بما فيه.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (29,'<span data-type="title" id=toc-5>سفيان بن سعيد عن أبي إسحاق</span>.
INSERT INTO [b26548] VALUES (30,'٢٩- حدثنا قَبِيصَة عن سفيان عن أبي إسحاق عن أبي حبة بن قيس عن علي رضي الله عنه أنه بدأ فغسل يديه ثلاثا ثم تمضمض واستنشق ثلاثا ثم غسل وجهه ثلاثا، ثم غسل قدميه ثلاثا ثم مسح برأسه ثلاثا ثم غسل يديه ثلاثا، ثم قام قائما فشرب فضل الإناء , ثم قال: هكذا رأيت رسول الله صلى الله عليه وسلم يتوضأ.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (31,'٣٠- حدثنا قَبِيصَة عن سفيان عن أبي إسحاق عن ؤعاصم بن عمرو قال جاء نفر إلى عمر رضي الله عنه فسألوه عن الحائض ما لزوجها منها وعن الغسل من الجنابرة الجنابة وعن صلاة الرجل تطوعا في بيته ⦗٥٧⦘ فقال أسحار أنتم سألت رسول الله صلى الله عليه وسلم عن الحائض ما لزوجها منها قال ما وراء الإزار وعن الغسل من الجنابة فقال يتوضأ وضوئه وضوءه للصلاة ثم يفرغه على رأسه ثلاثا ثم يفرغ على جسده وعن صلاة الرجل في بيته قال نور فنور بيتك.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (32,'٣١- وعن أبي إسحاق عن الأسود عن عائشة رضي الله عنها قالت كان رسول الله صلى الله عليه وسلم يجنب ثم ينام ثم لا يمس ماء.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (33,'٣٢- حدثنا وكيع عن سفيان عن أبي إسحاق عن الحارث عن علي رضي الله عنه قال في مس الذكر قال لا بأس به ما لم يعمد ذلك.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (34,'٣٣- عن وكيع عن سفيان عن أبي إسحاق عن يحيى بن وثاب عن ابن عمر عن النبي صلى الله عليه وسلم قال: من جاء إلى الجمعة فليغتسل.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (35,'٣٤- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن عاصم بن ضمرة عن علي رضي الله عنه قال: كان يدعو فيقول: تم نورك فهديت فلك الحمد، وعظم حلمك فعفوت فلك الحمد، وبسطت يدك فأعطيت فلك الحمد ربنا وجهك أكرم الوجوه، وجاهك خير الجاه، وعطيتك أفضل العطية وأهنؤها، تطاع ربنا فتشكر، وتعصى ربنا فتغفر لمن شئت، تجيب المضطر، وتكشف الضر، وتشفي السقيم، وتنجي من الكرب وتقبل التوبة، وتغفر الذنوب، لا يجزئ بآلائك أحد تجزى بآلائك، ولا يحصي نعماءك قول قائل.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (36,'٣٥- حدثنا وكيع عن سفيان عن أبي إسحاق عن عبد الله بن أبي بصير عن أبي بن كعب قال: قال رسول الله صلى الله عليه وسلم إن الصف الأول على مثل صف الملائكة ولو تعلمون فضيلته لابتدرتموه.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (37,'٣٦- حدثنا قَبِيصَة عن سفيان عن أبي إسحاق عن التميمي واسمه [أربدة] عن ابن عباس رضي الله عنهما قال كان رسول الله صلى الله عليه وسلم إذا سجد رؤي بياض إبطيه.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (38,'٣٧- حدثنا أبرن أبو نعيم عن سفيان عن أبي إسحاق عن عبد الله بن يزيد قال: حدثني البراء وكان غير كذوب قال كنا إذا صلينا خلف النبي صلى الله عليه وسلم لم يحن أحد منا ظهره حتى يضع النبي صلى الله عليه وسلم جبهته.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (39,'٣٨- حدثنا قَبِيصَة , حدثنا سفيان عن منصور والأعمش , وحماد وحصين عن أبي وائل عن عبد الله رضي الله عنه وعن أبي إسحاق عن أبي عبيدة والأسود وأبي الأحوص عن عبد الله عن النبي صلى الله عليه وسلم أنه كان يعلمهم التشهد التحيات لله والصلوات والطيبات السلام عليك أيها النبي ورحمة الله وبركاته ⦗٦٠⦘ السلام علينا وعلى عباد الله الصالحين أشهد أن لا إله إلا الله وأشهد أن محمدا عبده ورسوله.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (40,'٣٩- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن أبي الأحوص عن عبد الله رضي الله عنه قال صلاة الرجل في جماعة تزيد على صلاة الرجل وحده بضع وعشرون درجة.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (41,'٤٠- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن أبي إسحاق قال بعث ابن الزبير إلى عبد الله بن يزيد الخطمي أن استسقي بالناس وخرج الناس معه فيهم زيد بن أرقم والبراء.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (42,'٤١- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن السائب بن مالك أن النبي صلى الله عليه وسلم صلى في كسوف الشمس ركعتين.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (43,'٤٢- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن البراء رضي الله عنه قال أن النبي صلى الله عليه وسلم إذا قفل من سفر قال آيبون تائبون عابدون لربنا حامدون.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (44,'٤٣- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن هاني بن هاني عن علي رضي الله عنه قال استأذن عمار على النبي صلى الله عليه وسلم فقال ائذنوا للطيب المطيب.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (45,'٤٤- عن وكيع عن سفيان عن أبي إسحاق عن الحارث عن علي رضي الله عنه قال ⦗٦٢⦘ قال رسول الله صلى الله عليه وسلم قد عفوت لكم عن صدقة الخيل والرقيق ولكن هاتوا من كل أربعين درهما درهم.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (46,'٤٥- حدثنا قَبِيصَة , عن سفيان عن أبي إسحاق عن وهب بن جابر عن عبد الله بن عمرو رضي الله عنهما عن النبي صلى الله عليه وسلم قال: كفى بالمرء إثما أن يضيع من يقوت.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (47,'٤٦- حدثنا قَبِيصَة , عن سفيان عن أبي إسحاق عن أبي الأحوص {وسبح بحمد ربك حين تقوم} قال تقول سبحان الله وبحمده.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (48,'٤٧- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن البراء رضي الله عنه قال كان النبي صلى الله عليه وسلم إذا أراد أن ينام إذا اضطجع وضع خده على يده اليمنى ثم يقول رب قني عذابك يوم تبعث عبادك.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (49,'٤٨- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق قال: أخبرني جري النهدي قال بعثني رجل من بني سليم بالكناسة فقال عبد الله بن مسعود رضي الله عنه , عد رسول الله صلى الله عليه وسلم في يدي أو في يده خمسا التسبيح نصف الميزان التسبيح نصف الميزان , والحمد لله يملؤه , والتكبير يملأ ما بين السماوات السماء والأرض , والصوم نصف الصبر , والطهور نصف الإيمان.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (50,'٤٩- حدثنا قبيصة وأبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن أبي الأحوص عن عبد الله قال كان من دعاء النبي صلى الله عليه وسلم اللهم إني أسألك الهدى والتقى والعفة والغنى.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (51,'٥٠- حدثنا أبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن هبيرة بن يريم عن علي رضي الله عنه قال كان رسول الله صلى الله عليه وسلم يوقظ أهله في العشر الأواخر ويشمر.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (52,'٥١- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن سليمان بن صرد قال: قال رسول الله صلى الله عليه وسلم يوم الأحزاب الآن نغزوهم ولا يغزونا.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (53,'٥٢- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن رجل من جهينة أو مزينة قال: سمعت النبي صلى الله عليه وسلم وهو ينادي ينادى بشعارهم: يا حرام يا حرام فقال يا حلال يا حلال.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (54,'٥٣- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن المهلب بن أبي صفرة قال: أخبرني من سمع النبي صلى الله عليه وسلم يقول عن إن بيتم الليلة فليكن شعاركم {حم لا ينصرون} .',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (55,'٥٤- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن رجل رفعه ⦗٦٥⦘ قال لا تسبوا الأموات فتؤذوا الأحياء.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (56,'٥٥- حدثنا أبو نُعَيْم قال: حدثنا سفيان عن أبي إسحاق عن ناجية بن كعب عن علي رضي الله عنه قال أتيت النبي صلى الله عليه وسلم فقلت إن عمك الضال قد مات يعني أباه قد مات قال انطلق فواره ولا تحدثن شيئا حتى تأتيني فأتيته فأمرني فاغتسلت ودعا لي بدعوات ما يسرني بهن ما عرض من شيء.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (57,'٥٦- حدثنا أبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن مسلم بن نذرير السعدي عن حذيفة رضي الله عنه قال أخذ رسول الله صلى الله عليه وسلم بعضلة ساقي أو ساقه فقال هذا موضع الإزار فإن أبيت فأسفل من ذلك فإن أبيت فأسفل من ذلك فإن أبيت فلا حق للإزار في بالكعبين.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (58,'٥٧- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن أبي الأحوص عن عبد الله رضي الله عنه ⦗٦٦⦘ قال كان رسول الله صلى الله عليه وسلم يسلم عن يمينه وعن يساره السلام عليكم ورحمة الله حتى يرى بياض خده.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (59,'٥٨- حدثنا قَبِيصَة , عن سفيان عن أبي إسحاق عن الحارث عن علي رضي الله عنه قال انصرف حيث كان صاحبك.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (60,'٥٩- حدثنا قَبِيصَة , عن سفيان عن أبي إسحاق عن حارثة بن وهب الخزاعي قال صلينا مع رسول الله صلى الله عليه وسلم بمنى أكثر ما كان الناس وآمنه ركعتين في حجة الوداع.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (61,'٦٠- حدثنا قَبِيصَة حدثنا سفيان عن أبي إسحاق عن عبد الرحمن بن يزيد عن عبد الله أنه كره أنَّ أن يبزق عن يمينه في غير الصلاة.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (62,'٦١- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن أبي إسحاق عن عاصم بن ضمرة عن علي رضي الله عنه قال ⦗٦٧⦘ كان رسول الله صلى الله عليه وسلم يصلي على أثر كل صلاة ركعتين إلا في الفجر والعصر.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (63,'٦٢- عن وكيع عن سفيان عن أبي إسحاق عن عاصم بن ضمرة عن علي رضي الله عنه أن النبي صلى الله عليه وسلم كان يصلي أربعا قبل الظهر.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (64,'٦٣- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن عاصم بن ضمرة عن علي رضي الله عنه قال ليس الوتر بحتم كهيئة المكتوبة ولكنها سنة سنها رسول الله صلى الله عليه وسلم.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (65,'٦٤- حدثنا وكيع عن سفيان عن أبي إسحاق عن عاصم بن ضمرة قال سألنا عليا عن تطوع رسول الله صلى الله عليه وسلم بالنهار فقال إنكم لا تطيقونه فقلنا أخبرنا به نأخذ منه ما أطقنا فقال كان رسول الله صلى الله عليه وسلم إذا صلى الفجر أمهل , حتى إذا كانت ⦗٦٨⦘ الشمس من هاهنا من قبل المشرق مقدارها من صلاة العصر من هاهنا يعني من قبل المغرب قام فصلى ركعتين ثم أمهل حتى إذا كانت الشمس من هاهنا يعني من قبل المشرق مقدارها من صلاة الظهر من هاهنا يعني من قبل المغرب قام فصلى أربعا وأربعا قبل الظهر إذا زالت الشمس وركعتين بعد الظهر وأربعا قبل العصر يفصل بين كل ركعيتن بالتسليم على الملائكة المقربين والنبيين ومن تبعهم من المء=ؤمنينالمؤمنين والمسلمين.
INSERT INTO [b26548] VALUES (66,'٦٥- حدثنا أبو نُعَيْم وقَبِيصَة حدثنا سفيان عن أبي إسحاق عن نمير بن عريب عن عامر بن مسعود قال: قال رسول الله صلى الله عليه وسلم الصوم في الشتاء الغنيمة الباردة.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (67,'٦٦- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن عبد الله بن مالك عن ابن عمر رضي الله عنهما قال صلى النبي صلى الله عليه وسلم الصلاتين بجمع بإقامة.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (68,'٦٧- حدثنا قَبِيصَة عن سفيان عن أبي إسحاق عن وهب عن جابر عن عبد الله بن عمرو رضي الله عنهما عن النبي صلى الله عليه وسلم قال: كفى بالمرء إثما أن يضيع من يقوت.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (69,'٦٨- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي إسحاق عن سعيد بن وهب عن خباب [قال شكونا إلى رسول الله صلى الله عليه وسلم حر الرمضاء فلم] يشكنا.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (70,'٦٩- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن عمرو بن ميمون الأودي عن عمر رضي الله عنه قال كان المشركون لا يفيضون من جمع حتى يرون الشمس على ثبير فخالفهم النبي صلى الله عليه وسلم فأفاض قبل أن تطلع الشمس.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (71,'٧٠- حدثنا يعلى وأبو نُعَيْم وقَبِيصَة قالوا حدثنا سفيان عن أبي إسحاق عن النجراني قال أتي رسول الله صلى الله عليه وسلم برجل سكران فقال أي شيء شربت ⦗٧٠⦘ قال نبيذ قال تمر وزبيب قال لا تخلطوهما كل واحد منهما يكفي وحده ثم ضربه.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (72,'٧١- حدثنا قَبِيصَة , عن سفيان عن أبي إسحاق عن أبي عبيدة عن عبد الله قال أشد الناس عذابا يوم القيامة إمام جائر يضل الناس بغير علم ورجل قتل نبيا أو قتله نبي ومصور.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (73,'٧٢- حدثنا قَبِيصَة عن سفيان عن أبي إسحاق عن عمرو بن غالب قال دخل الأشتر على عائشة رضي الله عنها فقالت أنت الذي حرضت عليا قتل ابن أختي قال قد حرصت على قتله وحرص على قتلي قالت أما سمعت ما قال رسول الله صلى الله عليه وسلم لا يحل دم امرىء مسلم إلا في ثلاثة النفس بالنفس والثيب الزاني والتارك للإسلام.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (74,'٧٣- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن أبي الأحوص عن عبد الله فقال اشتكى رجل من الأنصار فاشتد وجعه فنعت له الكي , وأتوا رسول الله صلى الله عليه وسلم فسألوه فسكت فسألوه ثم فسكت ثم سألوه فسكت ثم سألوه فسكت فسألوه فقال: إن شئتم فاكووه، وإن شئتم فارضفوه بالرضف.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (75,'٧٤- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن أبي إسحاق عن أبي الأحوص عن عبد الله رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم من رآني في المنام فقد رآني فإن الشيطان لا يتمثل مثلي.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (76,'٧٥- حدثنا يعلى وأبو نُعَيْم قالا حدثنا سفيان عن أبي إسحاق عن هبيرة بن يريم عن عبد الله رضي الله عنه قال محمد بن نأتى من أتى كاهنا أو سادنا أو عرافا فصدقه بما يقول فقد كفر بما أنزل على محمد النبي صلى الله عليه وسلم.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (77,'٧٦- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن عبد الله بن حلام عن عبد الله بن مسعود رضي الله عنه , قال رأى رسول الله صلى الله عليه وسلم امرأة فأعجبته، فأتى سودة وهي تصنع طيبا وعندها نساء، فأخلينه، فقضى حاجته ثم مضى، ثم قال: أيما رجل رأى امرأة تعجبه فليقم إلى أهله، فإن معها مثل الذي معها.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (78,'٧٧- حدثنا أبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن هبيرة بن يريم عن علي رضي الله عنه قال كان رسول الله صلى الله عليه وسلم يوقظ أهله في العشر الأواخر ويشمر.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (79,'٧٨- حدثنا قَبِيصَة , حدثنا سفيان عن أبي إسحاق عن خمير بن مالك قال: قال عبد الله لقد قرأت من في رسول الله صلى الله عليه وسلم سبعين سورة وزيد بن ثابت رضي الله عنه له [ذؤابتان] يلعب مع الصبيان.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (80,'٧٩- حدثنا أبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن أبي حبيبة الطائي قال أتيت أبا الدرداء رضي الله عنه فقلت إن أخا لي مات , وأوصى لي بطائفة من ماله , فأين أضعه؟ في الفقراء في المجاهدين في الرقاب؟ فقال: أما أنا فلو كنت لم أعدل بالمجاهدين , إن رسول الله صلى الله عليه وسلم قال: مثل الذي يعتق عند الموت مثل الذي يهدي بعد ماعندما يشبع.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (81,'٨٠- حدثنا أبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن البراء رضي الله عنه قال كان رسول الله صلى الله عليه وسلم إذا قفل من سفر قال آيبون تائبون لربينا حامدون.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (82,'٨١- حدثنا وكيع عن سفيان عن أبي إسحاق عن سعيد بن ذي حدان عمن سمع عليا يقول إنَّ الله عز وجل سمى الحرب على لسان نبيه خدعة.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (83,'٨٢- حدثنا أبو نُعَيْم حدثنا سفيان عن أبي إسحاق عن أصحابه عن علي رضي الله عنه قال ما أرى أحد أدركه عقله وبايعه الإسلام ينام حتى يقرأ خواتيم سورة البقرة فإنهن من كنز تحت العرش.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (84,'<span data-type="title" id=toc-6>سفيان بن سعيد عن منصور</span>.
INSERT INTO [b26548] VALUES (85,'٨٤- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن منصور عن إبراهيم قال كان يقال بلوا الشعر وأنقوا البشرة.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (86,'٨٥- عن وكيع عن سفيان عن منصور عن هلال بن يساف عن أبي يحيى عن عبد الله بن عمرو رضي الله عنهما قال ⦗٧٥⦘ قال رسول الله صلى الله عليه وسلم أسبغوا الوضوء.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (87,'٨٦- حدثنا قَبِيصَة , وأبو نُعَيْم قالا حدثنا سفيان عن منصور عن سالم بن أبي الجعد عن علي رضي الله عنه في الجنب إذا أراد أن ينام يتوضأ وضوئه وضوءه للصلاة.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (88,'٨٧- حدثنا قَبِيصَة عن سفيان عن منصور عن إبراهيم مثل حديث عائشة رضي الله عنها: كان رسول الله صلى الله عليه وسلم يتوضا يتوضأ وضوئه وضوءه للصلاة يعني وهو جنب.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (89,'٨٨- حدثنا قَبِيصَة , حدثنا سفيان عن منصور عن إبراهيم عن الأسود عن عائشة رضي الله عنها قالت كنت أغتسل أنا ورسول الله صلى الله عليه وسلم من إناء واحد وأنا جنب وكان ⦗٧٦⦘ يأمرني أتزر قيباشرني فيباشرني وأنا حائض وكان يخرج رأسه من المسجد وهو معتكف وأغسله وأنا حائض.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (90,'٨٩- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور والأعمش , عن أبي وائل عن حذيفة رضي الله عنه أن النبي صلى الله عليه وسلم أتى سباطة قوم فبال قائما.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (91,'٩٠- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن هلال بن يساف عن أبي يحيى عن عبد الله بن عمرو رضي الله عنهما قال رأى رسول الله صلى الله عليه وسلم قوما تلوح أعقابهم فقال ويل للأعقاب من النار أسبغوا الوضوء.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (92,'٩١- حدثنا قَبِيصَة ويعلى عن سفيان , عن مَنْصُور عن مجاهد عن الحكم بن سفيان أو سفيان بن الحكم الثقفي عن النبي صلى الله عليه وسلم أنه توضأ فنضح فرجه قال فرأيت البلل من وراء الثوب.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (93,'٩٢- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور والأعمش , عن أبي وائل عن حذيفة رضي الله عنه أن النبي صلى الله عليه وسلم أتى سباطة قوم فبال قائما.
INSERT INTO [b26548] VALUES (94,'٩٣- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن خيثمة عن أبي عبيدة قال سافر عبد الله إلى المدينة فلم ينزع خفيه ثلاثة أيام.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (95,'٩٤- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور والأعمش , عن إبراهيم عن الأسود قال كان آخر أذان بلال اللهُ أَكْبَر اللهُ أَكْبَر لا إله إلا الله.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (96,'٩٥- حدثنا قَبِيصَة عن سفيان , عن مَنْصُور عن هلال بن يساف عن وهب بن الأجدع عن علي رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم لا تصلوا بعد العصر إلا أن تصلوا والشمس مرتفعة.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (97,'٩٦- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم قال كانوا ربما عجلوا العصر إذا كانت جنازة.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (98,'٩٧- حدثنا أبو نُعَيْم عن سفيان , عن مَنْصُور عن خيثمة عمن سمع عبد الله يقول لا سمر بعد الصلاة إلا لمصل أو مسافر.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (99,'٩٨- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور والأعمش , عن طلحة بن مصرف عن عبد الرحمن بن عوسجة عن البراء بن عازب رضي الله عنه عن النبي صلى الله عليه وسلم قال: إن لله ملائكة يصلون على الصفوف الأول.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (100,'٩٩- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن مجاهد عن ابن عمر رضي الله عنهما قال كره أن يجهر بالقراءة بالنهار في الصلاة.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (101,'١٠٠- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن أبي الضحى عن مسروق عن عائشة رضي الله عنها قالت كان النبي صلى الله عليه وسلم يكثر أن يقول في ركوعه وسجوده سبحانك اللهم ربنا وبحمدك اللهم اغفر لي يتأولون القرآن {فسبح بحمد ربك واستغفره إنه كان توابا} .',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (102,'١٠١- حدثنا قَبِيصَة وأبو نُعَيْم قالا حدثنا سفيان , عن مَنْصُور عن عدي عن عائشة رضي الله عنها قالت من سمع النداء فلم يجبه لم يرد خيرا ولم يراد به خيرا.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (103,'١٠٢- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن مجاهد عن أبي عياش الزرقي قال صلى رسول الله صلى الله عليه وسلم الظهر بعسفان والمشركون بينه وبين القبلة , معهم، أو عليهم , خالد بن الوليد , فقال المشركون: كانوا في صلاة لو أصبنا منهم كانت هي الغنيمة، فقال المشركون: إنها ستجيء ⦗٨٠⦘ صلاة هي أحب إليهم من أموالهم وآبائهم وأبنائهم قال: فنزل جبريل عليه السلام بالآيات فيما بين الظهر والعصر , فصلى رسول الله صلى الله عليه وسلم, وصف الناس خلفه صفين , [وكبر وكبروا] معه , ثم ركع وركعوا جميعا ثم رفع ورفعوا جميعا , ثم سجد وسجد معه الصف الذي يلونه , وقام الصف المؤخر يحرسونهم بسلاحهم , ثم رفع ورفعوا , ثم سجد الصف المؤخر ورفعوا فتأخر الصف المقدم وتقدم الصف المؤخر , فكبر وكبروا معه , ثم ركع وركعوا معه جميعا , ثم رفع ورفعوا جميعا , ثم سجد وسجدوا الصف الذي يلونه وقام الصف المؤخر يحرسونهم بسلاحهم يحرسونهم, ثم رفع ورفعوا , ثم سجد الصف المؤخر ثم سلم عليهم وصلاها من آخرىن في أرض بني سليم.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (104,'١٠٣- حدثنا قَبِيصَة , حدثنا سفيان [عن مَنْصُور] عن هلال بن يساف عن سعيد بن زيد أن النبي صلى الله عليه وسلم قال: اثبت حراء , فإنه ليس فما عليك إلا نبي أو صديق أو شهيد وعليه النبي صلى الله عليه وسلم وأبو بكر , وعمر , وعثمان , وعلي , وطلحة , والزبير , وعبد الرحمن بن عوف , وسعد بن مالك، رضوان الله عليهم.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (105,'١٠٤- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن أبي وائل عن أبي موسى رضي الله عنه عن النبي صلى الله عليه وسلم قال: أطعموا الجائع وعودوا المريض وفكوا العاني.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (106,'١٠٥- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن الحكم عن عبد الرحمن ابن أبي ليلى عن كعب بن عجرة رضي الله عنه عن النبي صلى الله عليه وسلم قال: معقبات لا يخيب قائلهن أو فاعلهن: تسبح الله ثلاثا وثلاثين، وتحمده ثلاثا وثلاثين، وتكيبر أربعا وثلاثين في دبر كل صلاة مكتوبة.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (107,'١٠٦- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن إبراهيم قال ليس شيء من الكلام يضاعف الحمد الحمد أكثر الكلام تضعيفا.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (108,'١٠٧- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن مجاهد {والباقيات الصالحات} قال سبحان الله والحمد لله ولا إله إلا الله واللهُ أَكْبَر.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (109,'١٠٨- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن هلال بن يساف عن الأغر , عن أبي هريرة رضي الله عنه , قال من قال لا إله إلا الله أنجته يوما من دهره أصابه قبل ذلك ما أصابه.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (110,'١٠٩- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان , عن مَنْصُور عن فضيل بن عمرو عن زياد بن الحصين عن أبي العالية قال كان رسول الله صلى الله عليه وسلم بأخرة إذا قام من مجلسه قال سبحانك اللهم وبحمدك أشهد أن لا إله إلا أنت أستغفرك وأتوب إليك فقيل يا رسول الله كلمات تقولهن بآخرة بأخرة قال كلمات علمنيهن جبريل كفارات لما يكون في المجلس.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (111,'١١٠- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم قال كان يعلمونهن عند المنام التسبيح والتكبير والتحميد ويقرأون ويقرؤون المعوذتين.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (112,'١١١- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن هلال بن يساف عن أبي عبيدة قال ما دام قلب رجل يذكر الله فهو في صلاة وإن يحرك به شفتيه أفضل.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (113,'١١٢- حدثنا قَبِيصَة وأبو نُعَيْم جميعا قالا حدثنا سفيان , عن مَنْصُور عن ذر عن يسيع عن النعمان بن بشير رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم إن الدعاء هو العبادة ثم قرأ {وقال ربكم ادعوني أستجب لكم إن الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين} .',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (114,'١١٣- حدثنا أبو نُعَيْم وقَبِيصَة حدثنا سفيان , عن مَنْصُور عن هلال بن يساف عن رجل من ثقيف عن رجل من جهينة عن النبي صلى الله عليه وسلم قال إنكم لعلكم تقاتلون قوما فتظهرون عليهم فيتقونكم بأموالهم دون أنفسهم وأبنائهم ويصالحونكم على صلح ⦗٨٤⦘ فلا تصيبوا تصيبون منهم فوق ذلك فإنه لا يحل لكم.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (115,'١١٤- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن ربعي بن حراش قال كنا كنت في جنازة حُذَيفة، فسمعت رجلا يقول: سمعت هذا يقول: ما بي بأس مما سمعت عن رسول الله صلى الله عليه وسلم: لئن اقتتلتم لأنظرن إلى أقصى بيت في داري، فلأدخلنَّه، فلئن دخل عَليّ لأقولن: بؤ بإثمي وإثمك، فأكون كابني آدم.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (116,'١١٥- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن ربعي بن حراش عن البراء بن ناجية المحاربي عن عبد الله قال: قال رسول الله صلى الله عليه وسلم تدور رحى الإسلام على خمس ة ثلاثين أو ست وثلاثين فإن هلكوا فسبيل من هلك وإن يبقى لهم دينهم فسبعين عاما قال عمر رضي الله عنه يا رسول الله مما مضى أو مما بقي قال مما بقي.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (117,'١١٦- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن إبراهيم عن علقمة قال جاء ابنا مليكة الجعفيان إلى رسول الله صلى الله عليه وسلم فقالا يا رسول الله إن أمنا وأدت في الجاهلية فهل ينفعها صلاة مع صلاتنا أو صيام مع صيامنا فقال رسول الله صلى الله عليه وسلم الوائدة والموؤدة في النار فوليا وهما يبكيان فدعاهما فقال وأمي مع أمكما.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (118,'١١٧- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن ربعي بن حراش عن امرأته عن أخت حذيفة رضي الله عنه قالت خطبنا رسول الله صلى الله عليه وسلم فقال يا معشر النساء إن لكن في الفضة ما تحلين ما من امرأة تحلى بذهب تظهره إلا عذبت به يوم القيامة.
INSERT INTO [b26548] VALUES (119,'١١٨- حدثنا قَبِيصَة عن سفيان , عن مَنْصُور عن إبراهيم عن علقمة قال: قال عبد الله لعن الله الواشمات و [المستوشمات] والمتنمصات والمتفلجات للحسن [المغيرات خلق الله] .. , وذكر الحديث.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (120,'١١٩- حدثنا وكيع عن سفيان , عن مَنْصُور عن مجاهد عن يوسف عن عبد الله بن الزبير قال جاء رجل إلى النبي صلى الله عليه وسلم فقال يا رسول الله إن أبي مات ولم يحج أفحج عنه قال أنت أكبر ولده قال نعم قال فحج عن أبيك أفرأيت لو كان على أبيك دين فقضيته.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (121,'١٢٠- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن أبي حازم , عن أبي هريرة رضي الله عنه , عن النبي صلى الله عليه وسلم قال: من حج هذا البيت ولم يرفث ولم يفسق فرجع كان كيوم ولدته [أمه] .',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (122,'١٢١- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن أبي وائل عن الصبي بن معبد أنه أهل بالحج والعمرة فقال له [سلمان] بن ربيعة وزيد بن صوحان أنت أضل من بعيرك فسأل عمر رضي الله عنه عن ذلك فقال هديت لسنة نبيك صلى الله عليه وسلم.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (123,'١٢٢- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن سعيد بن جُبَيْر , عن ابن عمر رضي الله عنهما كان يدهن بالزيت قبل أن يحرم.
INSERT INTO [b26548] VALUES (124,'١٢٣- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن هلال بن يساف عن أبي المثنى الحمصي عن أبي أُبَي ابن امرأة عبادة بن الصامت رضي الله عنه قال كنا جلوسا عند النبي صلى الله عليه وسلم فقال إنه سيجيء أمراء يؤخرون الصلاة تغشاهم أشياء حتى لا يصلوا الصلاة لوقتها فصلوا الصلاة لوقتها فقال رجل يا رسول الله ثم لا أصلي معهم قال نعم.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (125,'١٢٤- حدثنا أبو نُعَيْم حدثنا سفيان , عن مَنْصُور عن هلال بن يساف عن أبي يحيى عن عبد الله بن عمرو رضي الله عنهما قال أتيت رسول الله صلى الله عليه وسلم وهو يصلي جالسا فقلت يا رسول الله حدثت أنك قلت إن صلاة الرجل جالسا على النصف من صلاته قائما قال أجل ولكني لست كأحدكم.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (126,'١٢٥- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن رِبْعِي بن حِرَاش عن طارق بن عبد الله المحاربي قال: قال رسول الله صلى الله عليه وسلم إذا صليت فلا تبزقن عن يمينك ولكن تلقاء شمالك إن كان فارغا أو تحت قدميك ثم قل به هكذا , يعني [ادلكه] .',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (127,'١٢٦- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم قال طول القنوت في الصلاة أحب إلي يعني القيام.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (128,'١٢٧- حدثنا قَبِيصَة عن سفيان , عن مَنْصُور عن سالم بن أبي الجعد عن رجل من أهل الشام عن كعب بن مرة قال سئل النبي صلى الله عليه وسلم أي الليل أسمع قال جوف الليل الآخر ثم الصلاة مقبولة محضورة حتى يطلع الفجر ثم لا صلاة بعد الفجر حتى تكون الشمس قيد الرمح، أَو رمحين ثم الصلاة مقبولة محضورة حتى يقوم الظل قيام الرمح ثم لا صلاة حتى تزول الشمس ثم الصلاة مقبولة محضورة حتى تصلي تصلى العصر ثم لا صلاة حتى تغرب الشمس.
INSERT INTO [b26548] VALUES (129,'١٢٨- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم عن علقمة عن عبد الله رضي الله عنه أنه كان يوتر حين يبقى من الليل نحو مما مضى من صلاة المغرب.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (130,'١٢٩- حدثنا قَبِيصَة حدثنا سفيان , عن مَنْصُور عن رِبْعِي عن بعض أصحاب النبي صلى الله عليه وسلم قال: قال رسول الله صلى الله عليه وسلم لا تتقدموا هذا الشهر حتى تروا الهلال وتكملوا العدة قبله ثم صوموا حتى تروا الهلال وأكملوا العدة قبله.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (131,'١٣٠- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور [عن مجاهد ⦗٩١⦘ عن حرملة بن إياس] عن أبي الخليل عن مولى لأبي قتادة عن أبي قتادة رضي الله عنه عن النبي صلى الله عليه وسلم قال: صيام يوم عرفة كفارة سنتين سنة قبله وسنة بعده وصيام يوم عاشوراء كفارة سنة.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (132,'١٣١- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن ذر عن عبد الله بن شداد عن ابن عباس رضي الله عنهما قال: قال رجل يا رسول الله إني أحدث نفسي بالشيء لأن أكزن أكون حممة أحب إلي من أن أتكلم به فقال رسول الله صلى الله عليه وسلم اللهُ أَكْبَر اللهُ أَكْبَر رضي بالوسوسة.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (133,'١٣٢- حدثنا قَبِيصَة , عن سفيان , [عن مَنْصُور بن حيان عن ابن بجاد عن جدته قالت: ⦗٩٢⦘ قال رسول الله صلى الله عليه وسلم ردوا السائل ولو بظلف شاة محرق] .',NULL,'91',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (134,'١٣٣- حدثنا يعلى وأبو نُعَيْم قالا حدثنا سفيان , عن مَنْصُور عن رِبْعِي بن حِرَاش عن رجل عن علي رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم لا يؤمن رجل حتى يؤمن بالبعث بعد الموت والإيمان بالقدر.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (135,'١٣٤- حدثنا قَبِيصَة وأبو نُعَيْم حدثنا سفيان عن منصور عن عبيد بن نسطاس عن أبي عبيدة عن عبد الله بن مسعود رضي الله عنه , قال من أتى الجنازة فليأخذ بجوانبها الأربع فإنها من السنة ثم تطوع بعد إن شئت أو دع.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (136,'١٣٥- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم عن عبد الرحمن بن يزيد قال: حدثني علقمة عن أبي مسعود فلقيت أبا مسعود فحدثني ⦗٩٣⦘ عن رسول الله صلى الله عليه وسلم قال: من قرأ بالايتين بالآيتين من آخر سورة البقرة في ليلة كفتاه.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (137,'١٣٦- حدثنا يعلى حدثنا سفيان [, عن مَنْصُور] حدثنا مقسم عن ابن عباس رضي الله عنهما قال ساق رسول الله صلى الله عليه وسلم مئة بدنة فيها جمل لأبي جهل.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (138,'١٣٧- حدثنا أبو نُعَيْم عن سفيان , عن مَنْصُور عن إبراهيم عن الأسود عن عائشة رضي الله عنها أنها كانت تفتل القلائد لهدي رسول الله صلى الله عليه وسلم.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (139,'١٣٨- حدثنا وكيع عن سفيان , عن مَنْصُور والأعمش , عن إبراهيم عن الأسود عن عائشة رضي الله عنها أن رسول الله صلى الله عليه وسلم أهدى غنما مقلدة.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (140,'١٣٩- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم عن همام بن الحارث عن عدي بن حاتم رضي الله عنه قال: قلت يا رسول الله إنا نرسل كلابنا معلمة قال كل ما أمسكن عليك قال: قلت يا رسول الله وإن قتلن قال وإن قتلن ما لم يشركها كلب من غيرها قال: قلت يا رسول الله ما ترى بالمعراض قال ما خزق فكل وما أصاب بعرض فلا تأكل.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (141,'١٤٠- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان , عن مَنْصُور عن مجاهد عن مورق العجلي عن أبي ذر رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم من لاءمكم من خدمكم فأطعموهم مما تأكلون واكسوهم مما تلبسون ومن لم يلائمكم فبيعوه ولا تعذبوا عباد الله.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (142,'١٤١- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن يونس بن خباب عن طلق بن حبيب ⦗٩٥⦘ عن رجل كان يأتيه اليسر فرحل إلى الشام والمدينة قال فصليت إلى جنب شيخ فقال لي ما أقدمك فقلت أصاب أبي اليسر فخرجت أطلب له فقال ما أعلم له إلا كلمات سمعت رسول الله صلى الله عليه وسلم يقولهن تعلمهن لعل الله يشفيه أو ينفعه: ربنا الله الذي في السماء، تقدس اسمك، أمرك في السماء والأرض، كما رحمتك في السماء اجعل رحمتك في الأرض، اغفر لنا ذنوبنا وخطأنا، رب الطيبين، أنزل رحمة من رحمتك، [وشفاء من شفائك] على هذا الوجع قال ففعله فبرأ.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (143,'١٤٢- حدثنا يعلى حدثنا سفيان , عن مَنْصُور عن المنهال عن سعيد بن جُبَيْر , عن ابن عباس قال كان رسول الله صلى الله عليه وسلم يعوذ الحسن والحسين، يقول: أعيذكما بكلمات الله التامة التامات، من كل شيطان وهامة، ومن كل عين لامة، ويقول، هكذا كان أبي إبراهيم صلى الله عليه وسلم يعوذ ابنيه إسماعيل وإسحاق.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (144,'١٤٣- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور وليث عن مجاهد عن عقار بن المغيرة عن المغيرة بن شعبة رضي الله عنه عن النبي صلى الله عليه وسلم قال: من اكتوى أو استرقى فلم يتوكل.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (145,'١٤٤- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم عن عمارة بن عمير عن عمته عن عائشة رضي الله عنها قالت قال رسول الله صلى الله عليه وسلم إن أحق ما أكل الرجل من أطيب كسبه وإن ولد الرجل من أطيب كسبه.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (146,'١٤٥- حدثنا أبو نُعَيْم حدثنا سفيان , عن مَنْصُور عن إبراهيم عن الأسود عن عائشة رضي الله عنها أنها كانت توقظ أهلها ليلة ثلاث وعشرين.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (147,'١٤٦- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن مجاهد عن أسيد بن ظهير قال كان أحدنا إذا استغنى عن أرضه أعطاها بالنصف والثلث، والربع،، ويشترط ثلاث جداول، والقصارة، قال والقصارة وما سقط من السنبل والجداول وما سقط من سقى الربيع، قال فكنا نعمل فيها بالحديد، قال وبما شاء الله، ويصيب منها حتى جاءنا رافع بن خديج، فقال: إن رسول الله صلى الله عليه وسلم ينهاكم [عن أمر كان لكم نافعا، وطاعة الله، وطاعة رسوله أنفع لكم، إن رسول الله صلى الله عليه وسلم ينهاكم] عن الحقل، من استغنى عن أرضه، فليمنحها أخاه، وينهاكم عن المزابنة.',NULL,'97',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (148,'١٤٧- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن طلحة بن مصرف عن أنس بن مالك رضي الله عنه قال مر رسول الله صلى الله عليه وسلم بتمرة مطروحة في الطريق قال لولا أني أخشى أن تكون من الصدقة لأكلتها.
INSERT INTO [b26548] VALUES (149,'١٤٨- حدثنا قَبِيصَة حدثنا سفيان , عن مَنْصُور عن أبي الضحى عن مسروق عن عائشة رضي الله عنها قالت لما نزلت آيات الخمر قام رسول الله صلى الله عليه وسلم فتلاهن ثم حرم تجارة في الخمر.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (150,'١٤٩- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم عن الأسود عن عائشة رضي الله عنها قالت اشتريت جارية يقال لها بريرة فاشترط أهلها أن الولاء لهم فسألت النبي صلى الله عليه وسلم فقال اشتريها فإن الولاء لولي النعمة لمن أعطاها الثمن.
INSERT INTO [b26548] VALUES (151,'١٥٠- حدثنا قَبِيصَة حدثنا سفيان , عن مَنْصُور عن سالم بن أبي الجعد عن كُرَيْب عن ابن عَبَّاس رضي الله عنهما , قال: قال رسول الله صلى الله عليه وسلم لو أن أحدكم قال حين يأتي أهله قال ⦗٩٩⦘ منصور أراه قال بسم الله اللهم جنبنا الشيطان وجنب الشيطان ما رزقتنا ويولد بينهما ولد لم يضره أبدا.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (152,'١٥١- حدثنا أبو نُعَيْم حدثنا سفيان , عن مَنْصُور عن موسى بن عبد الله بن يزيد عن مولاة لعائشة عن عائشة رضي الله عنها قالت ما نظرت أو ما رأيت فرج رسول الله صلى الله عليه وسلم قط.',NULL,'99',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (153,'١٥٢- حدثنا قَبِيصَة , حدثنا سفيان , عن مَنْصُور عن إبراهيم عن علقمة قال اختلف إلى عبد الله في امرأة شهرا توفي عنها زوجها لم يدخل بها ولم يفرض لها صداقا قال لها مثل صداق نسائها وعليها العدة ولها الميراث.
INSERT INTO [b26548] VALUES (154,'١٥٣- حدثنا قَبِيصَة حدثنا سفيان , عن مَنْصُور عن إبراهيم عن علقمة قال: قال عبد الله لعن الله الواشمات، [والمستوشمات] ، والمتنمصات، والمتفلجات للحسن المغيرات خلق الله قال: فبلغ ذلك امرأة من بني أسد يقال لها: أم يعقوب، فأتته فقالت: بلغني أنك لعنت ذيت وذيت فقال: نعم وما يمنعني أن ألعن من لعن رسول الله صلى الله عليه وسلم وفي كتاب الله.',NULL,'100',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (155,'١٥٤- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن منصور والأعمش , عن سعد بن عبيدة عن أبي عبد الرحمن السلمي عن علي قال أبو نُعَيْم قال كنا مع رسول الله صلى الله عليه وسلم في بقيع الغرقد , ولم يقله قَبِيصَة , قال: قال رسول الله صلى الله عليه وسلم: ما منكم من أحد إلا وقد كتب مقعده في الجنة, ومقعده في النار , قالوا: يا رسول الله , أفلا نتكل؟ فقال: اعملوا؛ فكل ميسر , ثم قرأ {فأما من أعطى واتقى. وصدق بالحسنى. فسنيسره ⦗١٠١⦘ لليسرى. وأما من بخل واستغنى. وكذب بالحسنى. فسنيسره للعسرى} .',NULL,'100',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (156,'١٥٥- حدثنا أبو نُعَيْم حدثنا سفيان , عن مَنْصُور عن أبي وائل عن عبد الله رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم بئس ما لأحدهم يقول نسيت آية كيت وكيت بل هو نُسِّي.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (157,'١٥٦- حدثنا قَبِيصَة , عن سفيان , عن مَنْصُور عن سالم بن أبي الجَعْد , عن أبيه عن عبد الله رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم ما منكم منكم من أحد إلا وقد وكل به قرينه من الجن وقرينه من الملائكة قالوا وإياك يا رسول الله قال وإياي ولكن الله أعانني عليه فأسلم فلا يأمرني إلا بخير.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (158,'١٥٧- حدثنا قَبِيصَة وأبو نُعَيْم حدثنا سفيان , عن مَنْصُور عن عبد الله بن مرة عن عبد الله بن عمر رضي الله عنه قال نهى رسول الله صلى الله عليه وسلم عن النذر فقال إن النذر لا يرد شيئا إنما يستخرج به من الشحيح.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (159,'١٥٨- حدثنا أبو نُعَيْم قال: حدثنا سفيان , عن مَنْصُور عن هلال بن يساف قال آمين اسم من أسماء الله عز وجل.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (160,'<span data-type="title" id=toc-7>سفيان بن سعيد عن عَطَاء بن السَّائب</span>.
INSERT INTO [b26548] VALUES (161,'١٦٠- حدثنا يعلى وأبو نُعَيْم وقَبِيصَة قالوا حدثنا سفيان عن عَطَاء قال رأيت عبد الله بن [أبي] أوفى يبزق دما ثم لم يتوضأ.
INSERT INTO [b26548] VALUES (162,'١٦١- حدثنا أبو نُعَيْم حدثنا سفيان عن عَطَاء عن قيس بن سكن قال كان النبي صلى الله عليه وسلم إذا سجد قال سجد وجهي للذي خلقه وشق سمعه وبصره وكان إذا سجد قال سجد وجهي متعفرا في التراب لخالقه وحق له.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (163,'١٦٢- حدثنا قَبِيصَة , حدثنا سفيان عن عَطَاء , عن أبيه عن عبد الله بن عمرو رضي الله عنهما عن النبي صلى الله عليه وسلم أنه صلى في كسوف الشمس ركعتين.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (164,'١٦٣- حدثنا قَبِيصَة , حدثنا سفيان عن عَطَاء , عن أبيه [عن عبد الله] قال: قال رسول الله صلى الله عليه وسلم أوصي امرءا [بأمه] وأوصي امرءا [بأمه] وأوصي امرءا [بأمه] [وأصي امرءا بأبيه وأوصي امرءا بأبيه وأصي امرءا] وأوصي امرءا بمولاه الذي يليه وإن كان عليه منه أذى يؤذيه.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (165,'١٦٤- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن عَطَاء بن السائب عن حرب بن عبيد الله عن خال له من بكر بن وائل قال أتى النبي صلى الله عليه وسلم فسأله عن زكاة البقر والإبل والغنم فأخبره فلما ولى قال يا رسول الله أعشرهم قال إنما العشور على اليهود ⦗١٠٥⦘ والنصارى وليس على المسلم عشور.',NULL,'104',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (166,'١٦٥- حدثنا أبو نُعَيْم وعبيد الله وقَبِيصَة عن سفيان عن عَطَاء , عن أبيه عن عبد الله بن عمرو قال: قال رسول الله صلى الله عليه وسلم خصلتان لا يحصيهما رجل مسلم، إلا دخل الجنة وهما يسير ومن يعمل بهما قليل قالوا: وما هما يا رسول الله قال: يسبح الله في دبر كل صلاة مكتوبة عشرا، ويحمده عشرا، ويكبره عشرا، فتلك خمسون ومئة على اللسان، وألف وخمسمئة في الميزان، وإذا أوى إلى فراشه سبحه وحمده وكبره، مئة فتلك مئة باللسان على اللسان وألف في الميزان فأيكم يعمل في اليوم، والليلة ألفين وخمسمئة سيئة قالوا: يا رسول الله قالوا: يا رسول الله، وكيف لا يحصيهما قال: يأتي أحدكم الشيطان فيحدثه حتى ينومه ويأتيه فيقول فيقول: اذكر حاجة كذا وكذ احتى ينصرف، ولا يذكرها.',NULL,'105',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (167,'١٦٦- حدثنا قَبِيصَة , عن سفيان عن حبيب بن أبي ثابت وعَطَاء بن السَّائب عن أبي وائل عن سليمان بن ربيعة قال جدب عمر السمر بعد النوم , يعني كره.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (168,'١٦٧- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن عَطَاء بن السَّائب , عن أبيه عن عبد الله بن عمرو قال جاء رجل إلى النبي صلى الله عليه وسلم يبايعه عىل على الهجرة قال يا رسول الله أتيتك أبايعك على الهجرة وتركت أبواي يبكيان قال فارجع إليهما فأضحكهما كما أبكيتهما.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (169,'١٦٨- حدثنا قَبِيصَة , عن سفيان عن عَطَاء بن السَّائب عن عرفجة عن عتبة بن فرقد عن رجل من أصحاب النبي صلى الله عليه وسلم قال: إذا جاء رمضان صفدت فيه الشياطين وغلقت فيه أبواب النار وفتحت فيه أبواب الجنة ونادى مناد يا باغي الخير هلم يا باغي الشر أقصر.',NULL,'106',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (170,'١٦٩- حدثنا قَبِيصَة , حدثنا سفيان عن عَطَاء بن السَّائب عن عبد الله بن عبيد بن عمير قال أراه , عن أبيه عن ابن عمر رضي الله عنهما قال قيل له نستلمها الحجر والركن اليماني فقال سمعت رسول الله صلى الله عليه وسلم يقول مسحهما يحطان الخطايا.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (171,'١٧٠- حدثنا قَبِيصَة , حدثنا سفيان عن عَطَاء بن السَّائب عن سعيد بن جُبَيْر , عن ابن عباس رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم إن البركة وسط القصعة فكلوا من نواحيها ولا تأكلوا من رأسها.',NULL,'107',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (172,'١٧١- حدثنا قَبِيصَة , عن سفيان عن عَطَاء بن السَّائب عن أبي عبد الرحمن السلمي قال جمعت مع حذيفة رضي الله عنه بالمدائن فسمعته يقول إنَّ الله عز وجل تعالى يقول {اقتربت الساعة وانشق القمر} ألا إن القمر قد انشق على عهد رسول الله صلى الله عليه وسلم ألا وإن الساعة قد اقتربت ألا وإن المضمار اليوم والسبق غدا.
INSERT INTO [b26548] VALUES (173,'١٧٢- حدثنا قَبِيصَة وأبو نُعَيْم عن سفيان [عن عَطَاء بن السَّائب عن أم كلثوم ابنة علي قال أتيتها بشيء من الصدقة فقالت احذر شبابنا فإن ميمون أو مهران مولى النبي صلى الله عليه وسلم أخبرني أن النبي صلى الله عليه وسلم قال: إنا أهل بيت نهينا عن الصدقة وإن موالينا من أنفسنا فلا تأكلوا الصدقة] .',NULL,'108',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (174,'<span data-type="title" id=toc-8>حديث سفيان بن سعيد عن المقدام بن شريح</span>.
INSERT INTO [b26548] VALUES (175,'١٧٤- حدثنا قَبِيصَة , عن سفيان عن المقدام بن شريح , عن أبيه عن عائشة رضي الله عنها قالت إن كنت لأوتى بالإناء وأنا حائض وأشرب منه فيتناوله رسول الله صلى الله عليه وسلم فيضع فاه من حيث شربت فيشرب وأوتى بالعرق فأنتهس منه فيتناوله رسول الله صلى الله عليه وسلم فينتهس من حيث نهسته.',NULL,'109',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (176,'١٧٥- حدثنا قَبِيصَة وأبو نُعَيْم عن سفيان عن المقدام بن شريح , عن أبيه عن عائشة رضي الله عنها قالت ما بال (١) رسول الله صلى الله عليه وسلم قائما منذ أنزل الله عليه القرآن.
INSERT INTO [b26548] VALUES (177,'<span data-type="title" id=toc-9>من حديث سفيان عن أبي علي بياع الأنماط</span>
INSERT INTO [b26548] VALUES (178,'١٧٧- حدثنا قَبِيصَة حدثنا سفيان عن أبي علي بياع الأنماط عن أبي عثمان , عن أبي هريرة رضي الله عنه , قال أمرني رسول الله صلى الله عليه وسلم أن أنادي لا صلاة إلا بقراءة فاتحة الكتاب فما زاد.
INSERT INTO [b26548] VALUES (179,'<span data-type="title" id=toc-10>سفيان عن عبد الله بن دينار</span>.
INSERT INTO [b26548] VALUES (180,'١٧٩- حدثنا أبو نُعَيْم وقَبِيصَة عن سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما أن النبي صلى الله عليه وسلم أمر عمر رضي الله عنه أن يغسل ذكره ويتوضأ ويرقد.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (181,'١٨٠- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن دينار عن نافع عن ابن عمر رضي الله عنهما أنه كان يمسح رأسه مرة.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (182,'١٨١- حدثنا قَبِيصَة , عن سفيان عن عبد الله بن دينار عن سليمان بن يَسَار عن عراك بن مالك , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم ليس على المسلم في فرسه ولا عبده صدقة.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (183,'١٨٢- حدثنا أبو نُعَيْم حدثنا سفيان [عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم لكل غادر لواء يعرف به يوم القيامة] .',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (184,'١٨٣- حدثنا أبو نُعَيْم حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال اتخذ رسول الله صلى الله عليه وسلم خاتما من ذهب، فاتخذ الناس خواتيمهم من ذهب، فقال رسول الله صلى الله عليه وسلم: إني اتخذت خاتما من ذهب فاتخذ الناس خواتيم من ذهب، فنبذه، وقال: إني لست ألبسه أبدا فنبذ الناس خواتيمهم.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (185,'١٨٤- حدثنا عبيد الله بن موسى وقَبِيصَة عن سفيان عن عبد الله بن دينار عن عبد الله بن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم وأشار إلى المشرق وإن الفتنة من هاهنا وإن الفتنة من هاهنا ثلاثا من حيث يطلع قرنا شيطان.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (186,'١٨٥- حدثنا أبو نُعَيْم عن سفيان عن عبد الله بن دينار سمعت ابن عمر رضي الله عنه يقول وقت رسول الله صلى الله عليه وسلم لأهل الشام الجحفة ولأهل المدينة ذا الحليفة ولأهل نجد قرنا.
INSERT INTO [b26548] VALUES (187,'١٨٦- حدثنا قَبِيصَة حدثنا سفيان عن عبد الله بن دينار قال: سمعت ابن عمر يقول قال رسول الله صلى الله عليه وسلم إن اليهود إذا سلموا عليكم يقولون السام عليكم فردوا عليهم وعليكم.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (188,'١٨٧- حدثنا أبو نُعَيْم حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم أسلم سالمها الله وغفار غفر الله لها وعصية عصت الله ورسوله.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (189,'١٨٨- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن دينار قال رأى ابن عمر رضي الله عنه شيئا وهو في الصلاة فظن أنها عقرب فأهوى إليها برجله , فإذا هي قطنة.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (190,'١٨٩- حدثنا أبو نُعَيْم وقَبِيصَة حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما أن النبي صلى الله عليه وسلم كان يصلي على راحلته أينما توجهت به.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (191,'١٩٠- حدثنا عبيد الله وأبو نُعَيْم وقَبِيصَة عن سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال ⦗١١٥⦘ كان رسول الله صلى الله عليه وسلم يأتي قباء ماشيا وراكبا.',NULL,'114',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (192,'١٩١- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال نهى رسول الله صلى الله عليه وسلم أن يلبس المحرم ثوبا مصبوغا بورس أو بزعفران.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (193,'١٩٢- حدثنا يعلى حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم إذا لم يجد المحرم نعلين فليلبس خفين وليقطع أسفل من الكعبين.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (194,'١٩٣- حدثنا يعلى وقَبِيصَة قالا حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال سئل رسول الله صلى الله عليه وسلم عن الضب قال لا آكله ولا أحرمه.',NULL,'115',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (195,'١٩٤- حدثنا يعلى وأبو نُعَيْم وقَبِيصَة حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم من اقتنى كلبا إلا كلب صيد أو ماشية انتقص من عمله كل يوم قيراطان.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (196,'١٩٥- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما عن النبي صلى الله عليه وسلم قال: من اشترى طعاما فلا يبعه حتى يقبضه.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (197,'١٩٦- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن دينار عن عبد الله بن عمر رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم من ابتاع عبدا وله مال فماله للبائع إلا أن يشترط المبتاع.',NULL,'116',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (198,'١٩٧- حدثنا أبو نُعَيْم قال: حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال: قال رجل للنبي صلى الله عليه وسلم إني رجل أخدع في البيع قال إذا بعت فقل لا خلابة فكان الرجل يقوله.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (199,'١٩٨- حدثنا يعلى وأبو نُعَيْم وقَبِيصَة عن سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال نهى رسول الله صلى الله عليه وسلم عن بيع الولاء وعن هبته.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (200,'١٩٩- حدثنا أبو نُعَيْم وقبيصة قالا حدثنا سفيان عن عبد الله بن دينار عن عبد الله بن عمر قال كنا نتقي الكلام عند الانبساط إلى النساء مخافة أن ينزل فينا القرآن فلما توفي رسول الله صلى الله عليه وسلم تكلمنا.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (201,'٢٠٠- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن دينار عن عبد الله بن عمر رضي الله عنهما قال نهى رسول الله صلى الله عليه وسلم أن يتناجى اثنان دون الثالث.',NULL,'117',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (202,'٢٠١- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله بن دينار عن ابن عمر رضي الله عنهما قال كان النَّبِيُّ صلى الله عليه وسلم يبايعنا على السمع والطاعة ثم يلقنهم فيما استطعتم.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (203,'٢٠٢- حدثنا قَبِيصَة , حدثنا سفيان حدثنا عبد الله بن دينار قال لما اجتمع الناس على عبد الملك بن مروان كتب إليه ابن عمر أما بعد , فإني أقر بالسمع والطاعة لعبد الله لعبد الملك أمير المؤمنين على سنة الله وسنة رسوله فيما استطعت استطعنا , وقد أقر بني بمثل ذلك , والسلام.',NULL,'118',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (204,'<span data-type="title" id=toc-11>من حديث سفيان عن عبد الله بن أبي نجيح</span>
INSERT INTO [b26548] VALUES (205,'٢٠٤- حدثنا قَبِيصَة , عن سفيان عن ابن أبي نجيح عن مجاهد {إنا انزلناه أنزلناه في ليلة القدر} قال ليلة الحكم.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (206,'٢٠٥- حدثنا قَبِيصَة , حدثنا سفيان عن عبد الله ابن أبي نجيح وأيوب عن مجاهد عن عبد الرحمن ابن أبي ليلى عن كعب بن عجرة قال مر بي رسول الله صلى الله عليه وسلم، وأنا أوقد تحت قدر لي، فقال: أيؤذيك هوام رأسك؟ قلت: نعم. قال: فدعا حجاما فحلقه، , ثم قال: صم ثلاثة أيام، أو أطعم فرقا في ستة مساكين، أو انسك شاة.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (207,'٢٠٦- حدثنا قَبِيصَة , حدثنا سفيان عن أيوب وابن أبي نجيح عن مجاهد عن عبد الرحمن ابن أبي ليلى عن علي رضي الله عنه قال ⦗١٢٠⦘ أمرني النبي صلى الله عليه وسلم أن أصدق بجلال البدن التي نحرت وبجلودها.',NULL,'119',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (208,'٢٠٧- حدثنا يعلى حدثنا سفيان عن ابن أبي نجيح عن جابر رضي الله عنه قال كنا نبيع أمهات الأولاد على عهد رسول الله صلى الله عليه وسلم.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (209,'٢٠٨- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان حدثنا ابن أبي نجيح ابن أبي ليلى عن عبد الله بن كثير عن أبي المنهال عن ابن عباس رضي الله عنهما قال: قدم رسول الله صلى الله عليه وسلم المدينة وهم يسلفون في الثمار السنة والسنتين، فقال: رسول الله صلى الله عليه وسلم: من أسلف فليسلف في كيل معلوم ووزن معلوم.',NULL,'120',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (210,'<span data-type="title" id=toc-12>سفيان عن عثمان بن المغيرة الثقفي</span>.
INSERT INTO [b26548] VALUES (211,'٢١٠- حدثنا قَبِيصَة , حدثنا سفيان عن عثمان الثقفي عن سالم بن أبي الجَعْد عن ابن الحنفية قال: قال رسول الله صلى الله عليه وسلم لبلال أرحنا بالصلاة.',NULL,'121',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (212,'٢١١- حدثنا أبو نُعَيْم وقَبِيصَة قالا حدثنا سفيان عن أبي اليقظان عن زاذان عن جرير بن عبد الله.',NULL,'122',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (213,'<span data-type="title" id=toc-13>القسم الثاني: رواية محمد بن يوسف الفريابي عن الثوري</span>',NULL,'123',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (214,'بسم الله الرحمن الرحيم
INSERT INTO [b26548] VALUES (215,'٢١٣- حدثنا عبيد الله عن الزهري عن سعيد بن المسيب , عن أبي هريرة رضي الله عنه , قال كبر رسول الله صلى الله عليه وسلم على النجاشي أربع تكبيرات.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (216,'٢١٤- عن الأسود بن قيس عن نبيح عن جابر بن عبد الله رضي الله عنه قال لما كان يوم أحد حمل القتلى ليدفنوا في البقيع فنادى منادي رسول الله صلى الله عليه وسلم إن رسول الله صلى الله عليه وسلم يأمركم أن تدفنوا القتلى في مضاجعهم بعدما حملت أبي وخالي لندفنهم فردوا.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (217,'٢١٥- عن سالم قال: أخبرني أبو حازم قال رأيت الحسين بن علي رضي الله عنهما يقول لسعيد بن العاص حين مات الحسن تقدم فلولا أنها سنة لتقدمت ما تقدمت.',NULL,'132',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (218,'٢١٦- عن خالد الحذاء عن أم الهذيل عن أم عطية قالت نهينا عن اتباع الجنائز ولم يعزم علينا.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (219,'٢١٧- عن عثمان عن زاذان عن جرير أن النبي صلى الله عليه وسلم قال: اللحد لنا والشق لغيرنا.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (220,'٢١٨- عن حبيب بن أبي ثابت عن أبي وائل عن أبي هياج الأسدي قال: قال لي علي رضي الله عنهعليه السلام أبعثك على ما بعثني رسول الله صلى الله عليه وسلم ألا تترك قبرا مشرفا إلا سويته ولا تمثالا إلا طمسته.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (221,'٢١٩- عن سليمان الشيباني عن الشعبي عن ابن عباس رضي الله عنهما أن النبي صلى الله عليه وسلم صلى على قبر بعد ما دفن.',NULL,'133',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (222,'٢٢٠- عن علقمة بن مرثد عن سليمان بن بريدة , عن أبيه قال أتى النبي صلى الله عليه وسلم قبرا فجلس عليه، وجلس الناس حوله، فجعل يحرك أصبعه ورأسه كالمخاطب. فقام فجعل يبكي فاستقبله عمر رضي الله عنه، فقال: ما يبكيك؟ قال إني استأذنت ربي عزَّ وجلَّ ربي في قبر أم محمد فأذن لي وسألته الاستغفار فأبى علي، فأدركتني رحمتها فبكيت.
INSERT INTO [b26548] VALUES (223,'٢٢١- حدثنا عن عبد الله بن عثمان عن عبد الرحمن بن بهمان عن عبد الرحمن بن حسان بن ثابت , عن أبيه قال لعن رسول الله صلى الله عليه وسلم زوارات القبور.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (224,'٢٢٢- عن علقمة بن مرثد عن ابن بريدة , عن أبيه قال كان رسول الله صلى الله عليه وسلم يعلمهم إذا خرجوا إلى المقابر أن يقولوا سلام عليرك عليكم مأهل الديار من المؤمنين والمسلمين وإنا إن شاء الله بكم ⦗١٣٥⦘ لاحقون أنتم لنا فرطا ونحن لكم تبعا نسأل الله لنا ولكم العافية.',NULL,'134',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (225,'٢٢٣- عن شريك بن عبد الله بن أبي نمر قال: سمعت عَطَاء بن يسار قال كان رسول الله صلى الله عليه وسلم يخرج ليلة عائشة رضي الله عنها إلى البقيع فيقول السلام عليكم أهل دار قوم مؤمنين وإنا بكم إن شاء الله لاحقون وإنا وإياكم فيما توعدون غدا مؤجلون اللهم اغفر لأهل البقيع مرتين إو أو ثلاثا.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (226,'٢٢٤- عن هشام بن عروة , عن أبيه عن عائشة رضي الله عنها قالت بعث لي أبي أي يوم توفي النبي صلى الله عليه وسلم فقلت يوم الاثنين قال إني أرجوا أن أموت فيه فمات من عشيته ودفن ليلا رضي الله عنه.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (227,'٢٢٥- عن الأعمش عبد اللهعن أبي سفيان عن جابر قال: قال رسول الله صلى الله عليه وسلم يبعث كل عبد على ما مات عليه.',NULL,'135',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (228,'٢٢٦- عن سليمان التيمي عن أنس بن مالك رضي الله عنه عن النبي صلى الله عليه وسلم قال: مررت بموسى وهو قائم يصلي في قبره.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (229,'٢٢٧- عن عاصم عن القاسم بن محمد عن عائشة رضي الله عنها قالت رأيت رسول الله صلى الله عليه وسلم يقبل عثمان بن مظعون وهو ميت والدمع يسيل على لحيته.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (230,'٢٢٨- عن أبي إسحاق عن ناجية بن كعب عن علي بن أبي طالب رضي الله عنه قال: لما مات أبو طالب أتيت النبي صلى الله عليه وسلم فقلت: إن عمك الشيخ الضال قد مات، فمن يواريه؟ قال اذهب فوار أباك ولا تحدثن شيئا حتى تأتيني قال فأتيته فأمرني فاغتسلت ثم دعا لي بدعوات ما يسرني أن لي بهن ما على الأرض من شيء.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (231,'٢٢٩- عن إسماعيل بن أبي خالد عن أبي بردة عن عائشة رضي الله عنها قالت أغمي على النبي صلى الله عليه وسلم وهو في حجري فجغلت فجعلت أمسحه وأدعوا له بالشفاء فلما أفاق قال بل أسأل الله الرفيق الأعلى ⦗١٣٧⦘ الأسعد مع جبريل عليه السلام وإسرافيل وميكائيل عليهم السلام.',NULL,'136',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (232,'٢٣٠- عن مَنْصُور عن أبي وائل قال دخل معاوية على أبي هاشم بن عتبة بن ربيعة فقال يا خال ما يبكيك أوجع يشئزك أم حرص على الدنيا قال كل لا ولكن عهد إلي رسول الله صلى الله عليه وسلم عهدا لم آخذ به قال يا أبا هاشم إنها ستدركك أموال يأتي يؤتى بها أقوام وإنما يكفيك من جمع المال خادم ومركب في سبيل الله عز وجل.',NULL,'137',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (233,'٢٣١- عن موسى بن عبيدة عن إياس بن سلمة , عن أبيه قال كنا مع رسول الله صلى الله عليه وسلم في جنازة كنا مع رسول الله صلى الله عليه وسلم في جنازة، فأثنى فأثني عليها، فقال رسول الله صلى الله عليه وسلم: وجبت، قال فسئل يا رسول الله، ما وجبت؟ قال: الملائكة شهداء الله في السماء وأنتم شهداء الله في الأرض، فإذا شهدتم وجبت، ثم قرأ {وقل اعملوا فسيرى الله عملكم ورسوله والمؤمنون} .
INSERT INTO [b26548] VALUES (234,'٢٣٢- عن زبيد عن إبراهيم عن مسروق عن عبد الله بن مسعود رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم ليس منا من ضرب الخدود وشق الجيوب ودعا دعوى الجاهلية.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (235,'٢٣٣- عن محمد بن عبد الرحمن عن المقبري عن عبد الرحمن بن مهران , عن أبي هريرة رضي الله عنه , قال لا تضربوا على علي فسطاطا ولا تتبعوني بمجمرة تمشوا علي بمجمر وأسرعوا بي المشي فإني سمعت رسول اللهِ صَلَّى الله عَلَيه وسَلَّم يقول إذا وضع الرجل الصالح على سريره قال قدموني قدموني وإذا وضع الرجل السوء على سريره قال يا ويله أين تذهبون بي.',NULL,'138',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (236,'٢٣٤- عن عبد الرحمن بن زياد عن رجل قد سماه قال: قال رسول الله صلى الله عليه وسلم من بث لم يصبر ثم قرأ {إنما أشكوا بثي وحزني إلى الله} .',NULL,'139',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (237,'٢٣٥- عن مَنْصُور عن هلال بن يساف عن أبي يحيى عن عبد الله بن عمرو رضي الله عنهما قال أتيت النبي صلى الله عليه وسلم وهو يصلي جالسا فقلت حدثت يا رسول الله أنك قلت إن صلاة الرجل قاعدا على النصف من صلاته قائما قال أجل ولكني لست كأحدكم.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (238,'٢٣٦- عن أبي إسحاق , عن أبي سلمة بن عبد الرحمن عن أم سلمة قالت والذي توفى نفسه تعني النبي صلى الله عليه وسلم ما توفي رسول الله صلى الله عليه وسلم حتى كان أكثر صلاته قاعدا إلا المكتوبة وكان أعجب الأعمال ⦗١٤٠⦘ إليه الذي به يداوم عليه العبد وإن كان يسيرا.',NULL,'139',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (239,'٢٣٧- عن أيوب عن محمد بن سيرين , عن عبد الله بن شقيق عن عائشة رضي الله عنها قالت كان رسول الله صلى الله عليه وسلم يصلي من الليل طويلا قاعدا وليلا طويلا قائما قلت كيف كان يصنع قالت كان إذا قرأ قائما ركع قائما , وإذا قرأ قاعدا ركع قاعدا.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (240,'٢٣٨- عن أيوب بن موسى عن عطاء بن ميناء , عن أبي هريرة رضي الله عنه , قال سجدنا مع النبي صلى الله عليه وسلم في {إذا السماء انشقت} و {اقرأ باسم ربك الذي خلق} .',NULL,'140',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (241,'٢٣٩- عن الأسود بن قيس عن ثعلبة بن عباد عن سمرة بن جندب أن رسول الله صلى الله عليه وسلم قام فصلى بنا في كسوف الشمس ⦗١٤١⦘ وما نسمع له صوتا.',NULL,'140',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (242,'٢٤٠- عن الأعمش عن إبراهيم التيمي , عن أبيه عن أبي ذر رضي الله عنه , قال: قلت يا رسول الله أي مسجد وضع في الأرض أول قال المسجد الحرام قلت ثم أي قال ثم المسجد الأقصى قلت كم بينهما قال أربعين سنة ثم أينما أدركتك الصلاة فصل فإنها مسجد.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (243,'٢٤١- عن أبي هارون العبدي عن أبي سعيد الخدري رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم لا تشد المطايا إلا إلى ثلاثة مساجد المسجد الحرام ومسجد المدينة والمسجد الأقضصى.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (244,'٢٤٢- عن أبي صالح مولى التوأمة , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم صلاة في مسجدي هذا أفضل من ألف صلاة فيما سواه إلا المسجد الحرام.',NULL,'141',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (245,'٢٤٣- عن موسى بن عبيدة عن أخيه عن سهل بن سعد رضي الله عنه قال خرج علينا رسول الله صلى الله عليه وسلم ونحن نقترىء , يقري بعضنا بعضا، فقال: الحمد لله كتاب الله واحد، وفيكم الأخيار وفيكم الأحمر والأسود، اقرأوا، اقرأوا قبل أن يأتي قوم أقوام يقيمون حروفه، أقامة إقامة السهم، لا يجاوز حناجرهم يتعجلون أجره، ولا يتأجلونه.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (246,'٢٤٤- عن الأعمش عن خيثمة عن الحسن عن عمران بن حصين أنه مر بقاص يقص فسأل فبكى عمران بن حصين وقال إني سمعت رسول الله صلى الله عليه وسلم يقول اقرؤوا القرآن وسلوا الله به فأنه فإنه سيجيء قوم يقرؤون القرآن يسألون الناس.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (247,'٢٤٥- عن موسى بن عبيدة عن عبد الله بن يزيد [مولى] الأسود بن سفيان عن محمد بن عبد الرحمن بن ثوبان عن عبد الله بن عمرو رضي الله عنهما قال ⦗١٤٣⦘ خرجت , فإذا نفر جلوس بفناء باب رسول الله صلى الله عليه وسلم فتذاكروا آية فقال بعضهم فيما أنزلت آية كذا وكذا؟ وقال بعضهم في كذا وكذا حتى اختلفوا وارتفعت أصواتهم فخرج رسول الله صلى الله عليه وسلم فقام على الباب كالمغضب فقال يا قوم دعوا المراء في القرآن ثلاثا فإن الأمم قبلكم لم يلعنوا حتى اختلفوا وكذب بعضهم بعضا والمراء في القرآن كفر.',NULL,'142',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (248,'٢٤٦- عن ليث عن عمرو بن شعيب عن عبد الله بن عمرو رضي الله عنهما قال جلست إلى رسول الله صلى الله عليه وسلم مجلسا ما جلست أغبط عندي منه خرج رسول الله صلى الله عليه وسلم وقوم يتجادلون في القرآن على باب حجرة حجره فخرج محمرا مجهه وجهه كأن وجهه حبة رمان أو قطر دم فقال عن هذا ضلت الأمم قبلكم جادلوا الكتاب بعضه ببعض وضربوا بعضه ببعض إن القرآن لم ينزل يكذب بعضه بعضا ولكن ينزل يصدق بعضه بعضا فما كان من حلال فأحلوه وما كان من حرام فاجتنبوه واتركوه وما كان من متشابهه فآمنوا به.',NULL,'143',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (249,'٢٤٧- عن سعد بن إبراهيم عن عمر بن أبي سلمة عن أبي سلمة , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم جدال في القرآن كفر.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (250,'٢٤٨- عن أبي إسحاق قال: سمعت خمير بن مالك يقول سمعت ابن مسعود رضي الله عنه يقول قرأت من في رسول الله صلى الله عليه وسلم سبعين سورة وإن زيدا ذو ذؤابتين يلعب.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (251,'٢٤٩- عن الأعمش عن أبي وائل قال: قال عبد الله إنس إني سمعت القراء فسمعتهم متقاربين فاقرأوا كما علمتم وإياكم والتنطع والاختلاف إنما هو كقول أحدكم هلم وتعال.',NULL,'144',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (252,'٢٥٠- عن حبيب بن أبي ثابت عن سعيد بن جُبَيْر , عن ابن عباس رضي الله عنهما قال: قال عمر رضي الله عنه علي أقضانا وأبيّ أقرؤنا وإنا اندعا لندع بعض ما يقول وأبيّ يقول إني لا أدع شيئا سمعته من رسول الله صلى الله عليه وسلم ويقول الله عزَّ وجلَّ {ما ننسخ من آية أو ننسها نأتي نأت بخير منها أو مثلها} .',NULL,'145',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (253,'٢٥١- عن الأعمش عن أبي وائل عن مسروق عن عبد الله بن عمرو رضي الله عنهما قال ذاك رجل لم أزل أحبه يعني عبد الله بن مسعود رضي الله عنه , إني سمعت رسول الله صلى الله عليه وسلم يقول اقرؤوا القرآن من أربعة: ابن أم عبد وأبيّ بن كعب ومعاذ بن جبل وسالم مولى أبي حذيفة.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (254,'٢٥٢- عن مَنْصُور عن أبي وائل عن ابن مسعود رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم بئسما لأحدهم أن يقول نسيت آية كيت وكيت ولكن بل هو نُسِّي.',NULL,'145',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (255,'٢٥٣- عن الأعمش عن إبراهيم عن عبيدة عن عبد الله بن مسعود رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم اقرأ قلت أقرأ عليك وعليك أنزل؟ قال فقرأت سورة النساء حتى إذا بلغت {فَكَيْفَ إِذَا جِئْنَا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَجِئْنَا بِكَ عَلَى هَؤُلَاءِ شَهِيدًا} قال حسبك فالتفت إليه , فإذا عيناه تذرفان صلى الله عليه وسلم.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (256,'٢٥٤- عن إبراهيم بن المهاجر [عن ربعي بن حراش] قال حدثني من لم يكذبني يعني حذيفة رضي الله عنه أن رسول الله صلى الله عليه وسلم لقيه عند أحجار المراء فقال إن من أمتك الضعيف فمن قرأ القرآن على خلاف حرف فلا يتحولن إلى غيره رغبة عنه.',NULL,'146',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (257,'٢٥٥- عن أبي قيس عن عمرو بن ميمون عن أبي مسعود رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم أيعجز أحدكم أن يقرأ ثلث القرآن في ليلة قال فكبر ذلك في أنفسهم فقال الله الواحد ثلث القرآن.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (258,'٢٥٦- , عن مَنْصُور عن ذر عن يسيع عن النعمان بن بشير رضي الله عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول إن الدعاء هو العبادة ثم قرأ {ادعوني أستجب لكم} .',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (259,'٢٥٧- حدثني من سمع أنس بن مالك رضي الله عنه يقول مر رسول الله صلى الله عليه وسلم على سعد وهو يدعو ويشير بإصبعه فقال أَحِّد يا سعد.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (260,'٢٥٨- عن سلمة بن وردان قال: سمعت أنس بن مالك رضي الله عنه قال: قال رجل يا رسول الله أي الدعاء أفضل قال سل الله ⦗١٤٨⦘ العفو والعافيه والعافية في الدنيا والآخرة فإنك إن أعطيت ذلك أفلحت.',NULL,'147',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (261,'٢٥٩-[عن أبي مسعود] عن أبي الورد عن اللجلاج عن معاذ بن جبل رضي الله عنه , قال مر رسول الله صلى الله عليه وسلم برجل وهو يقول اللهم إني أسألك الصبر فقال سألت الله البلاء فسله العافيه العافية.
INSERT INTO [b26548] VALUES (262,'٢٦٠- عن عبد الرحمن بن زياد عن عبد الله بن يزيد عن عبد الله بن عمرو رضي الله عنهما عن النبي صلى الله عليه وسلم أنه كان يقول اللهم إني أسألك الصحة ⦗١٤٩⦘ والعفة والأمانة وحسن الخلق والرضا بالقدر.',NULL,'148',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (263,'٢٦١- عن الأعمش عن أبي صالح عن أبي سعيد رضي الله عنه , قال لله عزَّ وجلَّ تسعة وتسعين اسما منها اسم إذا دعي به أجاب.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (264,'٢٦٢- , عن مَنْصُور عن ربعي عن عبد الله بن شداد عن علي رضي الله عنه , أنه قال لعبد الله بن جعفر أحمد ألا أعلمك كلمتين لم أعلمها الحسن والحسين إذا سألت الله مسألة فأردت أن تنجح فقال لا إله إلا الله , وحده لا شريك له , العلي العظيم لا إله إلا الله , وحده لا شريك له الحليم الكريم.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (265,'٢٦٣- عن الأعمش عن مجاهد عن عبد الله ابن الرحمن بن أبي ليلى عن أبي ذر رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم لاَ حولَ وَلاَ قُوَّةَ إِلاَّ بِاللهِ كنز من كنوز الجنة.',NULL,'149',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (266,'٢٦٤- عن عبد الرحمن بن زياد عن عبد الله بن يزيد عن عبد الله بن عمرو رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم ما من دعوة أسرع إجابة من دعوة الغائب للغائب.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (267,'٢٦٥- عن عبد الله بن عيسى عن [عبد الله] بن أبي الجعد عن ثوبان قال: قال رسول الله صلى الله عليه وسلم لا يرد [القدر إلا] الدعاء ولا يزيد في العمر إلا البر وإن الرجل ليحرم الرزق بالذنب يصيبه.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (268,'٢٦٦- عن مَنْصُور عن هلال بن يساف عن سمرة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم أفضل الكلام سبحان الله ⦗١٥١⦘ والحمد لله ولا إله إلا الله واللهُ أَكْبَر لا يضرك بأيهن بدأت.',NULL,'150',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (269,'٢٦٧- عن عبد الرحمن بن عابس عن عبد الرحمن ابن أبي ليلى عن رجل من أصحاب النبي صلى الله عليه وسلم قال: نهى رسول الله صلى الله عليه وسلم عن الحجامة للصائم والمواصلة إبقاء على أصحابه ولم يحرمها قال قلت إنك تواصل فقال إني أواصل إلى السحر وربي عزَّ وجلَّ يطعمني ويسقيني.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (270,'٢٦٨- عن حبيب بن أبي ثابت عن [ابن] المطوس , عن أبيه , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم من أفطر يوما من شهر رمضان متعمدا من غير رخصة ولا مرض فلن يقضيه صيام الدهر كله وإن صامه.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (271,'٢٦٩- عن هشام بن عروة , عن أبيه عن عائشة رضي الله عنها قالت كان رسول الله صلى الله عليه وسلم يقبل بعض نسائه وهو صائم.',NULL,'151',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (272,'٢٧٠- عن عاصم عن عبد الله بن عامر , عن أبيه قال رأيت رسول الله صلى الله عليه وسلم يستاك وهو صائم ما لا أحصي.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (273,'٢٧١- عن ابن أبي ليلى عن عَطَاء , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم تسحروا فإن في السحور بركة.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (274,'٢٧٢- عن أسامة بن زيد عن رجل , عن أبيه عن عمرو بن العاص قال: قال رسول الله صلى الله عليه وسلم فرق ما بين صومنا وصوم أهل الكتاب أكلة السحر.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (275,'٢٧٣- , عن مَنْصُور عن ربعي بن حراش عن بعض أصحاب رسول الله صلى الله عليه وسلم قال: ⦗١٥٣⦘ قال رسول الله صلى الله عليه وسلم لا تصوموا هذا الشهر حتى تروا الهلال أو تكملوا العدة.',NULL,'152',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (276,'٢٧٤- عن أبي عباد عن سعيد المقبري , عن أبي هريرة رضي الله عنه , قال نهى النبي صلى الله عليه وسلم عن صيام ثلاثة أيام يوم قبل شهر رمضان ويوم الأضحى ويوم الفطر وأيام التشريق.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (277,'٢٧٥- , عن مَنْصُور عن ربعي بن حراش عن بعض أصحاب رسول الله صلى الله عليه وسلم قال: أصبح الناس صياما لتمام ثلاثين يوما فجاء أعرابيان فشهدا أنهما أهلا بالهلال أمس فأمرهم النبي صلى الله عليه وسلم فأفطروا.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (278,'٢٧٦- عن عبد الله بن عَطَاء عن عبد الله بن بريدة , عن أبيه قال جاءت امراة إلى النبي صلى الله عليه وسلم فقالت إن أمي ماتت وعليها ⦗١٥٤⦘ حج صوم شهر رمضان فقال صومي مكانها.',NULL,'153',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (279,'٢٧٧- عن هشام بن عروة , عن أبيه عن عائشة رضي الله عنها أن حمزة بن عمرو الأسلمي سأل النبي صلى الله عليه وسلم فقال يا رسول الله إني رجل أصوم في السفر فقال إن شئت فصم وإن شئت فأفطر.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (280,'٢٧٨- عن يحيى بن سعيد , عن أبي سلمة عن عائشة رضي الله عنها قالت قد كان تكون علي الأيام من في شهر رمضان أقضيه في شعبان.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (281,'٢٧٩- عن عاصم بن سليمان عن أم الهذيل عن الرباب عن سلمان بن عامر الضبي قال: قال رسول الله صلى الله عليه وسلم إذا أفطر أحدكم فليفطر على التمر فإن ⦗١٥٥⦘ لم يجد فليفطر على الماء فإن الماء له طهور.',NULL,'154',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (282,'٢٨٠- عن الأعمش عن عمارة بن عمير عن أبي عطية الوادعي قال: قلت لعائشة رضي الله عنها عندنا رجلان من أصحاب النبي صلى الله عليه وسلم أحدهما يعجل الصلاة ويعجل الإفطار والآخر يؤخر الصلاة ويؤخر الفطر قالت من الذي يعجل الفطر قلت ابن مسعود قالت هكذا رأيت رسول الله صلى الله عليه وسلم يصنع.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (283,'٢٨١- عن أبي حازم عن سهل بن سعد رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم لا يزال الناس بخير ما عجلوا الفطر.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (284,'٢٨٢- عن طلحة بن يحيى عن عائشة بنت طلحة عن عائشة رضي الله عنها أم المؤمنين قالت كان رسول الله صلى الله عليه وسلم يأتينا فيقول عندكم شيء فأقول لا فيقول أصوم فأتانا يوما وقد أهدي لنا حيس فقال عندكم ⦗١٥٦⦘ شيء فقلت نعم قد أهدي إلينا حيس فقال أما إني أصبحت وأنا صائم فأكل.',NULL,'155',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (285,'٢٨٣- عن الجريري عن أبي السليل عن مجيبة الباهلي [عن أبيه أو عن عمه] أتيت النبي صلى الله عليه وسلم فقال ممن أنت؟ فقلت: أما تعرفني كنت أتيتك عام أول فقال: إني رأيت لك جسما فقلت: إني لم أفطر بعدك نهارا إلا ليلا، فقال: من أمرك أنَّ تعذب نفسك؟ قلت: إني أقوى قال: فصم شهر الصبر، ويوما من الشهر فقلت: إني أقوى قال: صم صوم الشهرشهر الصبر ويومان من الشهر فقلت إني أقوى قال فصم وأفطر.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (286,'٢٨٤- عن حبيب بن أبي ثابت عن أبي العباس عن عبد الله بن عمرو رضي الله عنهما قال: ⦗١٥٧⦘ قال رسول الله صلى الله عليه وسلم أحب الصيام إلى الله صيام داود كان يصوم يوما ويفطر يوما ولا يفر إذا لاقى.',NULL,'156',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (287,'٢٨٥- عن حبيب بن أبي ثابت عن أبي العباس عن عبد الله بن عمرو رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم لا صام من صام الأبد.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (288,'٢٨٦-[عن سعيد الجريري] عن يزيد بن عبد الله بن الشخير عن مطرف بن عبد الله بن الشخير عن عمران بن حصين قال قيل يا رسول الله إن فلانا لا بفطر يفطر الدهر قال لا صام ولا أفطر.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (289,'٢٨٧- عن ثور بن يزيد عن خالد بن معدان عن عائشة رضي الله عنها قالت ⦗١٥٨⦘ كان رسول الله صلى الله عليه وسلم يتحرى الاثنين الإثنين والخميس.',NULL,'157',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (290,'٢٨٨- عن ابن ذكوان عن موسى بن [أبي] عثمان , عن أبيه , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم لا تصوم المرأة إذا كان زوجها شاهدا إلا بإذنه.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (291,'٢٨٩- عن أبي إسحاق عن جري النهدي عن رجل من بني سليم قال: قال رسول الله صلى الله عليه وسلم الصوم نصف الصبر.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (292,'٢٩٠- عن أبي الزبير , عن جابر رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم إذا دعي أحدكم وهو صائم فليجب فإن شاء طعم وإن شاء ترك.',NULL,'158',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (293,'٢٩١- حدثنا الأعمش , عن سعيد بن عبيدة عن أبي عبد الرحمن السلمي عن علي رضي الله عنه قال كنا جلوسا مع رسول الله صلى الله عليه وسلم في جنازة، في بقيع الغرقد، فقال: ما منكم من أحد إلا قد كتب مقعده في النار، ومقعده في الجنة فقال رجل: يا رسول الله، أفلا نتكل؟ فقال: اعملوا فكل ميسر، ثم قرأ: {فأما من أعطى واتقى وصدق بالحسنى فسنيسره لليسرى وأما من بخل واستغنى وكذب بالحسنى فسنيسره للعسرى} .',NULL,'159',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (294,'٢٩٢- حدثنا الأعمش , عن يزيد بن وهب الجهني حدثنا عبد الله بن مسعود رضي الله عنه , قال: حدثنا رسول الله صلى الله عليه وسلم الصادق المصدوق: إن خلق أحدكم [يجمع] في بطن أمه أربعين ليلة نطفة، ثم يكون علقة مثل ذلك، ثم يكون مضغة مثل ذلك، ثم يبعث الله عليه الملك بأربع كلمات فيقال: اكتب أجله ورزقه، وشقي، أو أم سعيد، إن الرجل ليعمل بعمل أهل الجنة حتى ما يكون ما بينه وبين الجنة إلا ذراع، فيغلب عليه الكتاب الذي سبق، فيختم بعمل أهل النار، فيدخل النار وإن الرجل ليعمل بعمل أهل النار حتى ما يكون ما بينه وبين النار بينه الجنة إلا ذراع، فيغلب عليه الكتاب الذي سبق، فيعمل بعمل أهل الجنة ⦗١٦٠⦘ فيدخل الجنة.',NULL,'159',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (295,'٢٩٣- عن طلحة بن يحيى قال: حدثتني عائشة بنت طلحة قالت أخبرتني عائشة رضي الله عنها أم المؤمنين قالت أتي رسول الله النبي صلى الله عليه وسلم بصبي من الأنصار ليصلي عليه، قال: فقلت: يا رسول الله، طوبى لهذا، عصفور من عصافير الجنة، لم يعمل سوءا، ولم يدر به، قال: أو غير ذلك يا عائشة، إن الله خلق الجنة وخلق لها أهلا، خلقها لهم وهم في أصلاب آبائهم وخلق النار وخلق لها أهلا، خلقها لهم وهم في أصلاب آبائهم.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (296,'٢٩٤- عن عمر بن محمد [عن عمر] مولى غفرة عن رجل من الأنصار عن حذيفة رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم لكل أمة مجوس ومدجوس هذه الأمة الذيم الذين يقولون لا قدر إم إن مرضوا فلا تعودوهم وإن ماتوا ⦗١٦١⦘ فلا تشيعوهم هم من شيعة الدجال حق على الله تعالى أن يلحقهم بالدجال.',NULL,'160',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (297,'٢٩٥- , عن مَنْصُور عن ربعي بن حراش عن رجل عن علي عليه السلام قال: قال رسول الله صلى الله عليه وسلم لا يؤمن العبد حتى يؤمن بأربع شهادة أن لا إله إلا الله وأني رسول الله بعثني بالحق ويؤمن بالبعث بعد الموت ويؤمن بالقدر.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (298,'٢٩٦- عن علقمة بن مرثد عن سليمان بن بريدة عن ابن يعمر قال رأيت ابن عمر فقلت إنا نسير في هذه الآفاق فنلقى قوما يقولون لا قدر فقال ابن عمر إذا لقيت أولئك فأخبرهم أن عبد الله بن عمر منهم بريء وأنهم منه براء برآء ثم أنشأ يحدثنا قال يحدثنا قال يحدثنا قال بينا نحن عند رسول الله صلى الله عليه وسلم فجاء فجاء رجل قال رجل قال سفيان ⦗١٦٢⦘ فذكر من هيئتة هيئته ما أدري كيف ذكره فقال يا رسول الله أذن ادن مني فدنا , ثم قال: أدن ادن مني فدنا دنوة حتى كادت ركبته تمس ركبة رسول الله صلى الله عليه وسلم فقال يا رسول الله أخبرنا أخبرني ما الإيمان قال تؤمن بالله وملائكته ورسله وذكره.',NULL,'161',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (299,'٢٩٧- عن الأعمش عن أبي سفيان عن جابر رضي الله عنه قال كان رسول الله صلى الله عليه وسلم يكثر أن يقول يا مقلب القلوب ثبت قلبي على دينك فقال له بعض أصحابه: أتتخاف علينا ونخن قد آمنا بك وبما جئت به قال القلب بين إصبعين أصبعين من أصابع الله عز وجل يقول هكذا , وهكذا ,.
INSERT INTO [b26548] VALUES (300,'٢٩٨- عن زياد بن إسماعيل السهمي عن محمد بن عباد المخزومي , عن أبي هريرة رضي الله عنه , قال جاء مشركو قريش إلى رسول الله صلى الله عليه وسلم فخاصموه في القدر فنزلت {إن المجرمين في ضلال وسعر يوم يسحبون في النار على ⦗١٦٣⦘ وجوههم ذوقوا مس سقر إنا كل شيء خلقناه بقدر} .',NULL,'162',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (301,'٢٩٩- حدثني أبو هارون العبدي عن أبي سعيد الخدري رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم احتج آدم وموسى عليهما السلام، فقال موسى: أنت آدم الذي خلقك الله بيده ونفخ فيك من روحه فبمعصيتك ربك أشقيتهم أشقيتنا وأخرجتنا من الجنة وذكره فحج آدم موسى.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (302,'٣٠٠- عن الأعمش عن مالك بن الحارث عن عبد [الرحمن] بن يزيد عن عبد الله بن مسعود رضي الله عنه , قَصْد في سُنَّة خير من اجتهاد في بدعة.',NULL,'163',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (303,'٣٠١- عن الأعمش عن سالم بن أبي الجَعْد عن يزيد بن بشر السكسكي أنَّ ابن عمر قيل له ألا تجاهد قال بني الإسلام على خمس شهادة أن لا إله إلا الله وإقام الصلاة وإيتاء الزكاة وحج البيت وصوم رمضان هكذا حدثنا رسول الله صلى الله عليه وسلم ثم الجهاد بعد ذلك حسن.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (304,'٣٠٢- عن الأعمش عن عمارة بن عمير عن أبي معمر عن أبي مسعود الأنصاريّ رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم لا تجزىء صلاة لا يقيم الرجل فيها صلبه إذا رفع رأسه من الركوع والسجود.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (305,'٣٠٣- عن أبي الزبير , عن جابر رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم بين العبد والكفر ترك الصلاة.',NULL,'164',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (306,'٣٠٤- عن سهيل بن أبي صالح , عن عبد الله بن دينار عن أبي صالح عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم الإيمان بضع وستون بابا أفضلها لا إله إلا الله وأدناها إماطة الأذى عن الطريق والحياء شعبة من الإيمان.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (307,'٣٠٥- عن أبي إسحاق عن جري النهدي عن رجل من بني سليم عبد الله بن مسعود رضي الله عنه , قال عد رسول الله صلى الله عليه وسلم في يدي أو في يده خمسا التسبيح نصف [الميزان] والحمد لله تملأه تملؤه والتكبير يملأ ما بين السَّماء والأرض والصوم نصف الصبر والطهور نصف الإيمان.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (308,'٣٠٦- عن الأعمش عن عمرو بن مرة عن أبي عبيدة عن عن أبي موسى الأشعري رضي الله عنه قال: قال رسول الله صلى الله عليه وسلم إن الله لا ينام ولا ينبغي له أن ينام يخفض القسط ويرفعه يرفع إليه عمل الليلة قبل النهار وعمل النهار ⦗١٦٦⦘ قبل الليلة حجابه النور ولو كشفها لأحرقت سبحات وجهه كل شيء أدركه بصره.',NULL,'165',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (309,'٣٠٧- عن الأعمش عن ذكوان , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم لما خلق الله عز وجل الخلق كتب في كتاب كتبه على نفسه فهو موضوع فوق عرشه أن رحمتي تغلب غضبي.',NULL,'166',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (310,'٣٠٨- عن جعفر بن برقان عن يزيد بن الأصم , عن أبي هريرة رضي الله عنه , قال: قال رسول الله صلى الله عليه وسلم ليسألكم الناس عن كل شيء حتى يسألونكم هذا الله خلق كل شيء فمن خلق اال الله عزَّ وجلَّ.
INSERT INTO [b26548] VALUES (311,'٣٠٩- عن إسماعيل بن أبي خالد عن الشعبي عنق مسروق عن عائشة رضي الله عنها قالت من قال أن محمدا صلى الله عليه وسلم رأى ربه، فقد كذب، وهو يقول: {لا تدركه الأبصار وهو يدرك الأبصار وهو اللطيف الخبير} ، ومن حدثك أنه يعلم ما في غد، فقد كذب، وهو يقول: {لا يعلم الغيب إلا الله} عزَّ وجلَّ.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (312,'٣١٠- عن عبد العزيز بن رفيع عن عبد الله بن أبي قتادة , عن أبيه قال: قال رسول الله صلى الله عليه وسلم لا تسبوا الدهر فإن الله هو الدهر.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (313,'٣١١- عن أبي الزناد عن الأعرج , عن أبي هريرة رضي الله عنه , أن رسول الله صلى الله عليه وسلم قال: لا يقولن أحدكم يا خيبة الدهر فإن الله هو الدهر.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (314,'٣١٢- عن عبد الله بن دينار عن عبد الله بن عمر رضي الله عنهما أن رسول الله صلى الله عليه وسلم قال: إذا قال الرجل لأخيه يا كافر فقد باء بها أحدهما.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (315,'٣١٣- عن هشام بن عروة , عن أبيه قال: قال رسول الله صلى الله عليه وسلم المتشبع بما لم يعط كلابس ثوبي زور.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (316,'٣١٤- عن الأعمش عن أبي وائل عن ابن مسعود رضي الله عنه قال أول ما يقضى بين الناس يوم القيامة في الدماء.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (317,'٣١٥- عن حنظلة السدوسي عن أنس بن مالك رضي الله عنه قال قلنا يا رسول الله أينحني بعضنا لبعض قال لا قال فيعانق بعضنا بعضا قال نعم.',NULL,'168',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (318,'٣١٦- عن أبي شعيب عن عقبة بن صهبان قال: سمعت الزبير يقول قد قرأتها حينا وما أراني من أهلها [فأصبحنا من أهلها] {واتقوا فتنة لا تصيبن الذين ظلموا منكم خاصة} .',NULL,'169',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (319,'٣١٧- حدثني عبد الله بن الحسين المصيصي حدثنا محمد بن كثير حدثنا سفيان عن عمرو بن عامر عن أنس رضي الله عنه قال كان رسول الله صلى الله عليه وسلم يتوضأ لكل صلاة قال إنا لنحفظ وضوءا واحدا.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (320,'٣١٨- حدثنا إسحاق بن إبراهيم بن عباد بصنعاء حدثنا عبد الرزاق عن الثوري عن ليث عن كعب , عن أبي هريرة رضي الله عنه , أن رسول الله صلى الله عليه وسلم قال: إذا صليتم علي فسلوا الله لي الوسيلة قيل وما الوسيلة يا رسول الله قال أعلى درجة في الجنة لا ينالها إلا رجل واحد وأرجوا أن أكون أنا هو.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (321,'٣١٩- حدثنا إسحاق حدثنا عبد الرزاق حدثنا الثوري ومهمر ومعمر عن أبي إسحاق عن أبي الأحوص عن عبد الله عن النبي صلى الله عليه وسلم أنه كان يسلم عن يمينه السلام عليكم ورحة ورحمة الله حتى يرى بياض خده، وعن يساره السلام عليكم ورحمة الله حتى يرى بياض خده.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (322,'٣٢٠- حدثنا إسحاق حدثنا عبد الرزاق حدثنا الثوري ومعمر عن حماد عن أبي الضُّحَى عن مسروق عن عبد الله رضي الله عنه قال ⦗١٧١⦘ ما نسيت فيما نسيت عن رسول الله صلى الله عليه وسلم أنه كان يسلم عن يمينه وذكره.',NULL,'170',NULL,NULL,'0');
INSERT INTO [b26548] VALUES (323,'٣٢١- حدثنا إسحاق حدثنا عبد الرزاق عن الثوري عن عبد العزيز بن رفيع عن أبي عمر عن أبي الدرداء رضي الله عنه قال: قلت يا رسول الله ذهب أصحاب الأموال بالدنيا والآخرة يصومون كما نصوم ويصلون كما نصلي ويجاهدون كما نجاهد ويتصدقون كما ولا نتصدق قال أفلا أدلك على أمر إذا فعلته أدركت من سبقك ولم يدركك من بعدك إلا من فعل كما فعلت تسبح الله عزَّ وجلَّ ثلاثا وثلاثين دبر كل صلاة مكتوبة وتحمده ثلاثا وثلاثين وتكبره أربعا وثلاثين.
