-- Insert data for b26551
INSERT INTO [b26551] VALUES (1,'<span data-type="title" id=toc-1>بسم الله الرحمن الرحيم</span>
الحمد لله وسلام على عباده الذين اصطفى وبعد فقد وقع الكلام فيمن يؤتي أجره مرتين من ذلك عشرة وردت فى عدة أحاديث، ونظمتها فى أبيات ثم وقفت على عدة أخرى، فأردت جمع ذلك في هذه الكراسة والله الموفق.

<span data-type="title" id=toc-2>حديث القرآن فيمن يؤتي أجره مرتين</span>
قال: الله تعالى مخاطبا أزواج نبيه - صلى الله عليه وسلم: (ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين) .
وقال تعالى:',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (2,'(يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله يؤتكم كفلين من رحمته) .
وقال تعالى: (الذين آتيناهم الكتاب من قبله هم به يؤمنون، وإذا يتلى عليهم قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين أولئك يؤتون أجرهم مرتين) .
وقال تعالى: (وما أموالكم ولا أولادكم بالتى تقربكم عندنا زلفى إلا من آمن وعمل صالحا فأولئك لهم جزاء الضعف بما عملوا) .

<span data-type="title" id=toc-3>ثلاثة لهم أجران</span>
وأخرج الشيخان عن أبى موسى الأشعري قال: قال رسول الله - صلى الله عليه وسلم: (ثلاثة لهم أجران - وفى لفظ يؤتون أجرهم مرتين - رجل من أهل الكتاب آمن بنبيه، وأدرك النبي - الله صلى الله عليه وسلم - فآمن به،',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (3,'واتبعه وصدقه فله أجران، وعبد مملوك أدى حق الله تعالى وحق سيده فله أجران، ورجل كانت له أمة فأدبها فأحسن أدبها ثم اعتقها وتزوجها فله أجران) .

<span data-type="title" id=toc-4>جزاء من أسلم من أهل الكتاب</span>
وأخرج الطبرانى فى الكبير عن أبى أمامة قال: قال رسول الله - صلى الله عليه وسلم -',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (4,'وهو يخطب عام حجة الوداع: (من أسلم من أهل الكتابين فله أجره مرتين، ومن أسلم من المشركين فله أجره) .

<span data-type="title" id=toc-5>أربعة يؤتون أجرهم مرتين</span>
وأخرج أيضا عن أبى أمامة قال: قال رسول الله - صلى الله عليه وسلم: (أربعة يؤتون أجرهم مرتين: أزواج النبي - صلى الله عليه وسلم -، ومن أسلم من أهل الكتاب، ورجل كانت عنده أمة فأعتقها، ثم تزوجها، وعبد مملوك أدى حق الله وحق سادته) .

<span data-type="title" id=toc-6>جزاء العبد إذا نصح لسيده</span>
وأخرج الشيخان عن ابن عمر أن رسول الله - صلى الله عليه وسلم - قال:',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (5,'(إن العبد إذا نصح لسيده، وأحسن عبادة ربه، فله أجره مرتين) .
وأخرجا عن أبى هريرة رضى الله تعالى عنه قال: قال رسول - الله صلى الله عليه وسلم: (للعبد المملوك الصالح أجران) .

<span data-type="title" id=toc-7>جزاء أهل القرآن</span>
وأخرج الشيخان عن عائشة رضى الله تعالى عنها أن رسول - الله صلى الله عليه وسلم - قال: (الماهر بالقرآن مع السفرة الكرام البررة والذى يقرأه وهو عليه شاق له أجران) .
وأخرج الدارمى فى مسنده عن وهب الذمارى قال:',NULL,'23',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (6,'(من آتاه الله القرآن فقام به آناء الليل، وآناء النهار، وعمل بما فيه، ومات على الطاعة بعثه الله يوم القيامة مع السفرة، والأحكام - والسفرة الملائكة والأحكام الأنبياء - قال: ومن كان عليه حريصا وهو يتفلت منه، وهو لا يدعه أوتي أجره مرتين) .

<span data-type="title" id=toc-8>جزاء الحاكم المجتهد</span>
وأخرج البخارى، وأبو داود عن عمرو بن العاص، وأبى هريرة قال: قال رسول الله - صلى الله عليه وسلم: (إذا اجتهد الحاكم فأصاب له أجران، وإذا اجتهد فأخطاء فله أجر) .
وأخرج البيهقى فى الشعب من طريق عبد الرزاق عن معمر عن موسى بن إبراهيم عن رجل من آل أبى ربيعة أنه بلغه أن أبا بكر حين أستخلف قعد في بيته حزينا، فدخل عليه عمر فأقبل على عمر يلومه، فقال: أنت كلفتنى هذا؟',NULL,'24',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (7,'وشكى إليه الحكم بين الناس. فقال عمر: أما علمت أن رسول الله - صلى الله عليه وسلم - قال: (إن الوالى إذا اجتهد فأصاب الحق فله أجران، وإذا اخطأ فله أجر واحد) .

<span data-type="title" id=toc-9>جزاء الصدقة على الأقارب</span>
وأخرج الشيخان عن زينب امرأة عبد الله بن مسعود قالت: جئت إلى النبي - صلى الله عليه وسلم - فإذا امرأة من الأنصار حاجتها حاجة، فخرج علينا بلال، فقلنا له: ائت رسول الله - صلى الله عليه وسلم - فأخبره أن امرأتين بالباب، تسألانك تجزئ الصدقة عنهما على أزواجهما، وعلى أيتام في حجرهما، فدخل بلال فسأله فقال: (لهما أجران أجر القرابة، وأجر الصدقة) .
وأخرج الطبرانى فى الكبير عن أبى أمامة أن رسول الله - صلى الله عليه وسلم -',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (8,'قال: (إن الصدقة على ذى قرابة يضعف أجرها مرتين) .
وأخرج فى الأوسط عن ابن مسعود أن امرأة سألت النبي - صلى الله عليه وسلم - هل لها من الأجر فى زوجها، وأيتام فى حجرها وهم بنو أخيها أن تجعل صدقتها فيهم؟ فقال: (نعم لها أجران، أجر القرابة، وأجر الصدقة) .
وأخرج عن جمرة بنت قحافة قالت: قلت: يا رسول الله زوجي',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (9,'محتاج فهل يجوز أن أعود عليه؟ قال: (نعم لك أجران) .

<span data-type="title" id=toc-10>من فضائل الوضوء وثوابه</span>
وأخرج ابن ماجه عن أبى بن كعب أن رسول الله - صلى الله عليه وسلم - دعا بماء فتوضأ مرة مرة، فقال:',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (10,'(هذا وظيفة الوضوء) ، ثم توضأ مرتين مرتين ثم قال: (هذا وضوء من توضأه أعطاه الله كفلين من الأجر، ثم توضأ ثلاثا ثلاثا فقال: هذا وضوئى ووضوء المرسلين من قبلى) .
وأخرج سعيد بن منصور وأحمد والحاكم عن ابن عمر مثله، ولفظه: (ضاعف الله له الأجر مرتين) .',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (11,'<span data-type="title" id=toc-11>هل للجانب الأيسر من المسجد فضل على الأيمن</span>؟
وأخرج ابن ماجه عن ابن عمر قال: قيل للنبي - صلى الله عليه وسلم - إن ميسرة المسجد تعطلت؟ فقال النبي - صلى الله عليه وسلم: (من عمر ميسرة المسجد كتب الله له كفلين من الأجر) .
وأخرج الطبرانى فى الكبير عن ابن عباس - رضى الله تعالى عنه - قال: قال رسول الله - صلى الله عليه وسلم: (من عمر جانب المسجد الأيسر لقلة أهله فله أجران) .
وأخرج فى الأوسط عن ابن عباس قال: قال رسول الله - صلى الله عليه وسلم:',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (12,'(من ترك الصف الأول مخافة أن يؤذى مسلما، ويصلى فى الصف الثانى أو الثالث ضعف له أجر الصف الأول) .
وأخرج مسلم عن جرير بن عبد الله قال: قال رسول الله - صلى الله عليه وسلم - (من سن سنة حسنة فله أجرها، وأجر من عمل بها من بعده، من غير أن ينقص من أجورهم شئ) .

<span data-type="title" id=toc-12>جزاء الإمام والمؤذن</span>
وأخرج أبو الشيخ ابن حيان عن أبى هريرة قال: قال رسول الله - صلى الله عليه وسلم: (للإمام والمؤذن مثل أجر من صلى معهما) .',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (13,'وأخرج أبو داود عن أبى سعيد الخدرى قال:',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (14,'خرج رجلان فى سفر فحضرت الصلاة، وليس معهم ماء فتيمموا صعيدا طيبا فصليا، ثم وجدا الماء فى الوقت فعاد أحدهما الصلاة والوضوء، ولم يعد الآخر، أتيا النبي - صلى الله عليه وسلم - فذكرا ذلك له فقال للذى لم يعد: (أصبت السنة، وقد أجزأتك صلاتك) وقال للذى توضأ وأعاد: (لك الأجر مرتين) .

<span data-type="title" id=toc-13>طالب العلم يضاعف له الأجر</span>
وأخرج الدارمى فى مسنده والبيهقى في المفصل والطبرانى فى الكبير بسند رجاله موثوقون عن واثلة بن الأسقع قال: قال رسول الله - صلى الله عليه وسلم: (من طلب علما فأدركه كتب الله له كفلين من الأجر، ومن طلب علما فلم يدركه كتب الله له كفلا من الأجر) .
وأخرجه أبو يعلى وزاد في آخره ففسره قال: (من طلب علما فأدركه أعطاه الله أجر ما علم، وأجر ما عمل،',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (15,'ومن طلب علما فلم يدركه أعطاه الله أجر ما عمل، وسقط عنه أجر ما لم يعمل) .
وأخرج الطبرانى فى الأوسط عن على قال: قال رسول الله - صلى الله عليه وسلم: (من أسبغ الوضوء فى البرد الشديد كان له من الأجر كفلان) .

<span data-type="title" id=toc-14>الجنازة لها أجران</span>
وأخرج ابن أبى شيبة فى المصنف: ثنا وكيع ثنا همام عن أبى عمران الجولانى قال: قال رسول الله - صلى الله عليه وسلم: (للجنازة أجران) .
مرسل.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (16,'وأخرج عبد الرزاق فى المصنف عن يحيى بن كثير أن النبي - صلى الله عليه وسلم - قال: (من أدرك الخطبة فقد أدرك الجمعة، ومن أدرك الخطبة فقد أدرك الصلاة، ومن دنا من الإمام فاستمع وأنصت كان له كفلان من الأجر، ومن لم يسمع ولم ينصت كان عليه كفلان من الوزر) .

<span data-type="title" id=toc-15>من فضائل غسل الجمعة</span>
وأخرج الطبرانى فى الكبير عن أبى أمامة قال: قال رسول الله - صلى الله عليه وسلم: (من اغتسل يوم الجمعة وابتكر، ودنا واستمع، وأنصت كان له كفلان من الأجر) .
وأخرج أحمد عن على بن أبى طالب قال: (إذا كان يوم الجمعة خرجت الشياطين يريتون الناس [الى',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (17,'اسواقهم،ومعهم الرايات] ، وتقوم الملائكة على أبواب المساجد يكتبون الناس على قدر منازلهم السابق والمصلى والذى يليه، حتى يخرج الإمام فمن دنا من الإمام فأنصت واستمع ولم يلغ، كان له كفلان من الأجر ومن نأى فاستمع وانصت ولم يلغ كان له كفل من الأجر، ومن دنا من الإمام فلغا ولم ينصت، ولم يستمع كان له كفلان من الوزر) .
وأخرجه أبو داود فى سننه، وصرح فيه بالرفع.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (18,'وأخرج سعيد بن منصور فى سننه عن مكحول قال:
(من أتى الجمعة فقعد قريبا من الإمام، فسمع وأنصت فله أجران اثنان، ومن لم يسمع ولم ينصت فعليه وزران، ومن كان بعيدا من الإمام فلم يسمع ولم ينصت فله أجر واحد، ومن يسمع ولم ينصت فعليه وزران، ومن لم يسمع ولم ينصت فعليه وزر واحد) .

<span data-type="title" id=toc-16>ابنك له أجر شهيدين</span>
وأخرج أبو داود عن قيس بن شماس قال: جاءت امرأة إلى النبي - صلى الله عليه وسلم - يقال لها: أم خلاد وهى منتقبة تسأل عن ابنها وهو مقتول فقال لها بعض أصحاب رسول الله - صلى الله عليه وسلم - جئت تسألين عن ابنك وأنت منتقبة؟ فقالت: إن أرزء ابنى فلن أرزء حيائى، فقال رسول الله - صلى الله عليه وسلم:',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (19,'(ابنك له أجر شهيدين) فقالت: ولم ذلك يا رسول الله؟ قال: (لأنه قتله أهل الكتاب) .

<span data-type="title" id=toc-17>جزاء شهيد البحر</span>
وأخرج الطبرانى فى الكبير عن أبى أمامة سمعت رسول الله - صلى الله عليه وسلم - يقول: (شهيد البحر مثل شهيدى البر) .
وأخرج ابن أبى شيبة فى المصنف ثنا وكيع عن سعيد بن',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (20,'عبد العزيز عن علقمة بن شهاب قال: قال رسول الله - صلى الله عليه وسلم: (من لم يدرك الغزو معى فليغز فى البحر، فإن غزوة فى البحر أفضل من غزوتين فى البر، وإن شهيد البحر له أجر شهيدى البر) .
وأخرج سعيد بن منصور فى سننه عن كعب الأحبار أنه قال: فى',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (21,'غزوة البحر: (إن قتل أو غرق كان له أجر شهيدين) .
وأخرج الطبرانى فى الأوسط عن ابن عباس قال: قال رسول الله - صلى الله عليه وسلم: (إذا تسارعتم إلى الخير فامشوا حفاة فإن الله يضاعف أجره على المنتعل) .
وأخرج سعيد بن منصور فى سننه عن مكحول: (أنه سئل عن الرجل يغتسل من الجنابة يوم الجمعة؟ قال: من فعل ذلك كان له أجران) .
وأخرج البيهقى فى شعب الإيمان عن أبى هريرة قال: قال رسول الله - صلى الله عليه وسلم:',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (22,'(أيعجز أحدكم أن يجامع أهله فى كل جمعة؟! فإن له أجرين اثنين أجر غسله وأجر غسل امرأته) . فى إسناده بقية.

<span data-type="title" id=toc-18>جزاء القارئ والسامع للقرآن</span>
وقال الدارمى فى مسنده: ثنا أبو المغيرة ثنا عبدة عن خالد بن معدان قال: (إن الذى يقرأ القرآن له أجر، وإن الذى يستمع له أجران) .
وقال ابن أبى شيبة فى المصنف: ثنا يحيى بن يونس عن الأوزاعى عن حسان بن عطية عن فروة اللخمى قال: قال رسول الله - صلى الله عليه وسلم: (أيما سرية خرجت فى سبيل الله فرجعت وقد أخفقت فلها أجرها مرتين) قال فى الصحاح: أخفق الرجل إذا غزا ولم يغنم، وأخفق الصائد إذا رجع ولم يصطد.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (23,'<span data-type="title" id=toc-19>من فضائل صلاة العصر</span>
وأخرج عبد الرزاق فى مصنفه عن يزيد بن أبى حبيب أن النبي - صلى الله عليه وسلم - قال: (إن هذه الصلاة التى فرضت على من كان قبلكم - يعنى العصر - فضيعوها فمن حفظها اليوم فله أجرها مرتين، ولا صلاة بعدها حتى يرى الشاهد) .
مرسل أو معضل.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (24,'وأخرج مسلم والنسائى عن أبى بصرة الغفارى قال: صلى بنا رسول الله - صلى الله عليه وسلم - صلاة العصر فقال:',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (25,'(إن هذه الصلاة عرضت على الذين من قبلكم فضيعوها، ألا ومن صلاها ضعف له أجره مرتين) .

<span data-type="title" id=toc-20>جزاء المؤمن التقى</span>
وأخرج ابن أبى حاتم فى تفسيره عن محمد بن كعب القرظى قال: إذا كان المؤمن تقيا غنيا آتاه الله أجره مرتين وتلا هذه الآية: (ومن أموالكم ولا أولادكم) إلى قوله: (فأولئك لهم جزاء الضعف) .
قال: تضعيف الحسنة.
وأخرج الشيخان عن سلمة بن الأكوع قال: خرجنا مع رسول الله - صلى الله عليه وسلم - إلى خيبر، وكان سيف عامر بن الأكوع فيه قصر فتناول به ساق يهودى ليضربه يرجع ذباب سيفه فأصاب ركبة عامر فمات منه، فقلت: يا رسول الله، زعموا أن عامر أحبط عمله؟!',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (26,'قال: (كذاب من قال، إن له لأجرين، إنه لجاهد) . قلت: فهمت من الحديث أن سبب الأجرين كونه استشهد بيده وسلاح نفسه خطأ.

<span data-type="title" id=toc-21>جزاء الوضوء قبل الطعام</span>
وأخرج الحاكم فى تاريخ نيسابور عن عائشة قالت: قال رسول الله - صلى الله عليه وسلم: (الوضوء قبل الطعام حسنة وبعده حسنات) .
قلت: ظهر لى فيه نكتة أن الأول من شرع غيرنا، والثانى من شرعنا كما دل عليه حديث سلمان قلت: يا رسول الله قرأت فى التوراة بركة الطعام الوضوء قبله وبعده',NULL,'44',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (27,'فناسب تضعيف أجر ما شرعه النبي - صلى الله عليه وسلم - على ما شرعه من قبله',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (28,'كم قيل بذلك فى حديث: (صوم عاشوراء كفارة سنة) (وصوم عرفة كفارة سنتين) .
وسبب ذلك أن ذلك سنة موسى، وهذا سنة النبي - صلى الله عليه وسلم - فضعف أجره.
وأخرج الترمذى عن أبى هريرة قال:',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (29,'قال رجل: يا رسول الله الرجل يعمل العمل فيسره فإذا طلع عليه أعجبه؟ قال رسول الله - صلى الله عليه وسلم: (له أجران أجر السر وأجر العلانية) .
وأخرج أبو نعيم فى الحلية من حديث أبى ذر مثله.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (30,'وأخرج الطبرانى فى الكبير عن أبى مسعود الأنصارى قال: جاء رجل إلى النبي - صلى الله عليه وسلم - فقال: إنى أعمل العمل فأسره فيظهر فأفرح به؟ قال: (كتب لك أجران: أجر السر، وأجر العلانية) .
وأخرج ابن أبى شيبة فى المصنف عن حبيب بن أبى ثابت: أن ناسا من أصحاب النبي - صلى الله عليه وسلم - قالوا: يا رسول الله إنا نعمل أعمالا فى السر، فنسمع الناس يتحدثون بها فيعجبنا أن نذكر بخير؟ فقال:',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (31,'(لكم أجران، أجر السر، وأجر العلانية) .
قال الترمذى: فسره بعض أهل العلم بأن يعجبه ثناء الناس عليه بالخير لقوله عليه السلام: أنتم شهداء الله فى الأرض للإكرام والتعظيم.
وقال بعضهم: إذا طلع عليه فأعجبه رجاء أن يعمل بعمله فيكون له مثل أجرهم.
وأخرج سعيد بن منصور فى سننه عن أبى موسى الأشعرى أنه خطب فقال: (أيها الناس إنكم فى زمان لعامل الله فيه أجر واحد وإنه سيكون من بعدكم زمان يكون لعامل الله فيه أجران) .
وقال ثنا إسماعيل بن إبراهيم ثنا سعيد الجريرى عن أبى السليل عن عبد الله بن رياح الأنصارى قال:',NULL,'50',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (32,'(للماشى فى الجنازة قيراطان، وللراكب قيراط) .
وأخرج ابن أبى شيبة فى المصنف عن كعب قال: (الصدقة تضاعف يوم الجمعة) ، وأخرج عنه أيضا قال: (يوم الجمعة تضاعف فيه الحسنة والسيئة) .

<span data-type="title" id=toc-22>تضاعف الحسنات يوم الجمعة</span>
وأخرج الطبرانى فى الأوسط عن أبى هريرة _ رضي الله عنه _',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (33,'قال: قال رسول الله - صلى الله عليه وسلم: (تضاعف الحسنات يوم الجمعة) .
وأخرج عن أبى بكر الصديق وعمران بن حصين قال: قال رسول الله - صلى الله عليه وسلم: (من اغتسل يوم الجمعة كفر عنه ذنوبه وخطاياه فإذا أخذ فى المشى كتب له بكل خطوة عشرون حسنة) .
وأخرج ابن أبى الدنيا فى كتاب ذكر الموت عن يحيى بن عتيق قال: قلت لمحمد بن سيرين: (الرجل يتبع الجنازة لا يتبعها حسنة يتبعها حياء من أهله أله فى ذلك أجر؟',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (34,'قال: اجر واحد؟ قال: بل أجران أجر صلاته على أخيه، وأجر صلته للحى) .

<span data-type="title" id=toc-23>جزاء قراءة الرجل فى المصحف</span>
وأخرج الطبرانى والبيهقى فى الشعب عن أوس الثقفى قال: قال رسول الله - صلى الله عليه وسلم: (قراءة الرجل فى غير المصحف ألف درجة، وقراءته فى المصحف تضاعف ألفى درجة) .
وأخرج البيهقى فى الشعب أيضا عن ابن عمر قال: قال رسول',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (35,'الله - صلى الله عليه وسلم: (من قرأ القرآن فأعربه كان له بكل حرف عشرون حسنة، ومن قرأ بغير إعراب كان له بكل حرف عشر حسنات) .
المراد بإعرابه معرفة معانى ألفاظه، وليس المراد المصطلح عليه فى النحو وهو ما يقابل اللحن، لأن القراءة مع فقده ليست قراءة، ولا ثواب فيها.
وقد صح من حديث ابن مسعود مرفوعا: (من قرأ حرفا من كتاب الله فله به حسنة، والحسنة بعشر أمثالها) .
أخرجه الترمذى.
وحديث ابن عمر الذى أفردناه ظاهر فى التضعيف.
أخرج ابن أبى شيبة فى المصنف عن الأوزاعى قال: ابتعت جارية وشرط على أهلها أن لا أبيع، ولا أهب، ولا أمهرها فإذا مت فهى حرة.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (36,'فسألت الحكم بن عتيبة فقال: لا بأس به، وسألت مكحولا فقال: لا بأس به. قلت: يخاف على منه قال: بل أجرك فيه أجرين.
وأخرج أحمد بسند رجاله ثقات عن ابن عمر سمعت رسول الله - صلى الله عليه وسلم - يقول:',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (37,'(إنى لأعلم أرضا يقال لها: عمان، ينضح بناحيتها البحر الحجة منها أفضل من حجتين من غيرها) .
وأخرج الطبرانى في الكبير عن قيس بن عاصم عن أبيه سمعت رسول الله - صلى الله عليه وسلم - يقول:',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (38,'(إذا كان يوم القيامة أمر بالوالى فيوقف على جسر جهنم، فيأمر الله بالجسر فينتفض انتفاضة فيزول كل عظم منه من مكانه، ثم يسأله فإن كان مطيعا اجتبذه فأعطاه كفلين من الأجر، وإن كان عاصيا خرق به الجسر فهوى فى جهنم سبعين خريفا) .

<span data-type="title" id=toc-24>قصيدة تجمع الذين يؤتون أجرين</span>
وقد اجتمع من هذه الأحاديث والآثار جملة تزيد على الثلاثين وقد نظمتها فى أبيات فقلت:
وجميع آتى فيما رويناه أنهم ... يثنى لهم أجر جود محققاً
فأزواج خير الخلق أولهم ومن ... على زوجها أو للقريب تصدقا
وفاز بجهد ذو اجتهاد أصاب ورد ... وضوء اثنين والكتابى صدقا
وعبد آتى حق الإله وسيد ... وعامر يسوى مع غنى له تقى',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (39,'ومن أمة بسرى فأدب محسناً ... وينكحها من بعده حين اعتقا
ومن سن خيراً أو أعاد صلاته ... كذاك جبان إذ بجاهر ذا شقا
كذاك شهيد فى البحر ومن أتى ... له القتل من أهل الكتاب فألحقا
وطالب علم مدرك ثم مسبغ ... وضوء لدى البرد الشديد فحققا
ومستمع بخطبة قد دنا ومن ... بتأخير صف أول مسلما وقى
وحافظ عصر مع إمام مؤذن ... ومن كان فى وقت الفساد موفقاً
وكامل خير محسفاً ثم إن بدا ... يرى فرحاً مستبشراً بالذى ارتقا
ومغتسل فى جمعة عن جنابة ... ومن فيه حقاً قد غدا متصدقاً',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26551] VALUES (40,'وماش يصلى جمعة ثم من أتى ... بهذا اليوم خيراً ما فضعفه مطلقا
ومن حتفه قد جاء من سلاحه ... ونازع فعل إن الخير تسبقا
وماش لدى تشييع ميت وغاسل ... يداً بعد أكل والمجاهد إذ فقا
ومشيع ميتا حياء من أهله ... ومستمع القرآن فيما روى التقى
وفى مصحف يقرأ وقارئه معربا ... بتفهيم معناه الشريف محققا
تم بحمد الله',NULL,'60',NULL,NULL,'0');
